INSERT INTO [b399] VALUES (193,'١٠٧ - فأتى بني عبد المطلب [وهم: نخ مي مج جا هق] يسقون على زمزم (٩٦) فقال: انزعوا (٩٧) بني عبد المطلب فلولا أن يغلبكم الناس على سقايتكم لنزعت معكم (٩٨)',NULL,'87',NULL,NULL,'0');
