INSERT INTO [b4003] VALUES (1,'فوائد ابن نصر
INSERT INTO [b4003] VALUES (2,'<span data-type="title" id=toc-2>الجزء الأول</span>
INSERT INTO [b4003] VALUES (3,'بسم الله الرحمن الرحيم
INSERT INTO [b4003] VALUES (4,'٢- حدثنا أبو الفوارس قال: حدثنا أبو إبراهيم المزني قال: حدثنا الشافعي قال: أخبرنا مالك بن أنس، عن ابن شهاب، عن حميد بن عبد الرحمن، عن أبي هريرة رضي الله عنه: ((أن رجلاً أفطر في شهر رمضان فأمره النبي صلى الله عليه وسلم بعتق رقبة، أو صيام شهر أو إطعام ستين مسكيناً فقال: إني لا أجد، قال: فأتي النبي صلى الله عليه وسلم بعرق تمر فقال: خذ هذا فتصدق به، فقال: يا رسول الله! ما أجد أحوج مني؛ فضحك رسول الله صلى الله عليه وسلم حتى بدت ثناياه، ثم قال: كله)) .',NULL,'40',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (5,'٣- حدثنا أبو الفوارس قال: حدثنا يونس بن عبد الأعلى قال: حدثنا محمد بن إدريس الشافعي قال: حدثنا محمد بن خالد الجندي، عن أبان بن صالح، عن الحسن، عن أنس قال: قال رسول الله صلى الله عليه وسلم: ((لا يزداد الأمر إلا شدة ولا الدنيا إلا إدباراً، ولا تقوم الساعة إلا على شرار الناس، ولا مهدي إلا عيسى بن مريم)) .',NULL,'40',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (6,'٤- حدثنا أبو علي عبد الواحد بن علي بن محمد بن أبي الخصيب، وأبو بكر بن أبي الموت قالا: حدثنا علي بن عبد العزيز بمكة قال: حدثنا أبو عبيد القاسم بن سلام، عن علي بن هاشم بن البريد، عن الأعمش، عن حبيب عن عروة، عن عائشة: ((أن فاطمة بنت أبي حبيش استحاضت فسألت رسول الله صلى الله عليه وسلم عن ذلك فقال: تدع الصلاة أيام أقرائها، ثم تتوضأ لكل صلاة وتصلي حتى يجيء ذلك الوقت، وإن قطر على الحصير)) .',NULL,'41',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (7,'٥- حدثنا أبو علي بن أبي الخصيب قال: حدثنا أبو علي الحسين بن منصور بن عبد الرحمن الرماني بالمصيصة قال: حدثنا داود بن معاذ بن معاذ ابن أخت مخلد بن الحسين أبو سليمان، قال: حدثنا أبو الشيخ جارية بن الهرم الفقيمي، عن هشام بن عروة، عن أبيه عن عبد الله ابن عمرو قال: سمعت رسول الله صلى الله عليه وسلم يقول: ((إن الله لا يقبض العلم انتزاعاً ينتزعه من الناس، ولكن يقبض العلم بقبض العلماء، حتى إذا لم يترك عالماً اتخذ الناس رءوساً جهالاً، فأخبروا بغير علم فضلوا وأضلوا)) .',NULL,'42',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (8,'٦- حدثنا أبو محمد عبد الرحمن بن جيش الفرغاني قال: حدثنا إبراهيم بن زهير الحلواني بحلوان، قال: حدثني مكي بن إبراهيم قال: حدثنا عبيد الله بن عمر، عن نافع، عن ابن عمر قال: قال رسول الله صلى الله عليه وسلم: ((لا تقدموا بين يدي رمضان بصوم، صوموا لرؤيته وأفطروا لرؤيته، فإن غم عليكم فأكملوا العدة ثلاثين يوماً)) ، وكان ⦗٤٣⦘ ابن عمر إذا كان ذلك اليوم أرسل من ينظر في الهلال، فإن رآه أصبح صائماً، وإن لم يره أصبح مفطراً، وإن كان بينه وبينه سحاب أصبح صائماً.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (9,'٧- حدثنا أبو العباس أحمد بن الحسن بن إسحاق بن عتبة الرازي قال: حدثنا أبو الزنباع روح بن الفرج القطان قال: حدثني يحيى بن عبد الله بن بكير، حدثني الليث ابن سعد، عن عبيد الله بن أبي جعفر، عن بكير بن عبد الله بن الأشج، عن نافع، عن ابن عمر، عن رسول الله صلى الله عليه وسلم أنه قال: ((من أعتق عبداً وله مال، فماله له إلا أن يشترط السيد ماله، فيكون له)) .',NULL,'43',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (10,'٨- حدثنا أبو العباس أحمد بن الحسن الرازي قال: حدثنا أحمد بن داود بن موسى، قال: حدثنا: أبو داود الطيالسي قال: حدثنا شعبة، عن قتادة، عن يونس بن ⦗٤٤⦘ جبير، عن ابن عمر: ((أنه طلق امرأته وهي حائض؟ فقال النبي صلى الله عليه وسلم لعمر: ليراجعها فإذا طهرت فليطلقها، إن شاء. قلت: فتحسب بها؟ قال: فمه؟ أرأيت إن عجز واستحمق؟!)) .',NULL,'43',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (11,'٩- حدثنا أبو الحسن أحمد بن سليمان بن حذلم القاضي قال: حدثنا بكار بن قتيبة البكاروي، قال: حدثنا روح بن عبادة قال: حدثنا الحجاج الصواف عن أبي الزبير عن جابر، أن النبي صلى الله عليه وسلم قال: ((من قال: سبحان الله العظيم غرست له نخلة في الجنة)) .',NULL,'44',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (12,'١٠- حدثنا أبو الحسن علي بن أحمد البغدادي بن المقابري قال: حدثنا مسلم بن إبراهيم بن عبد الله الكجي، قال: حدثنا سليمان بن داود قال: حدثنا عبد الله بن وهب، عن عمرو بن الحارث، عن دراج أبي السمح، عن أبي السائب مولى بني الزهرة، عن أم سلمة أن رسول الله صلى الله عليه وسلم قال: ((خير مصلى النساء قعرهن في بيوتهن)) .',NULL,'44',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (13,'١١- حدثنا أبو بكر أحمد بن القاسم بن معروف بن حبيب قال: حدثنا أبو زرعة عبد الرحمن بن عمرو قال: حدثنا سعيد بن مريم قال: حدثنا نافع بن يزيد أخبرني عقيل، عن ابن شهاب، عن عبيد الله بن عبد الله بن عتبة، عن ابن عباس، عن النبي صلى الله عليه وسلم أنه قال: ((أقرأني جبريل القرآن على حرف، فاستزدته فزادني حتى انتهى على سبعة أحرف)) .',NULL,'45',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (14,'١٢- حدثني أبو القاسم حمزة بن محمد بن علي بن محمد بن العباس الكناني، أملى بمصر في داره قال: حدثنا أبو عبد الله بن أحمد بن موسى بن زياد قال: حدثني ⦗٤٦⦘ عبد الأعلى بن حماد النرسي، قال: حدثنا داود بن عبد الرحمن عن عبد الله بن عثمان ابن خثيم، عن شهر بن حوشب، عن أسماء بنت يزيد، أن رسول الله صلى الله عليه وسلم خطب الناس فقال: ((يا أيها الناس لا يحملكم على أن تتايعوا في الكذب كما يتتايع الفراش في النار، كل كذب يكتب على ابن آدم إلا ثلاث خصال: رجل كذب امرأته ليرضها، ورجل كذب بين امرأين ليصلح بينهما، ورجل كذب في خديعة حرب)) .',NULL,'45',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (15,'١٣- حدثني أبو بكر بن أبي الموت قال: حدثني علي بن عبد العزيز البغوي قال: حدثنا أبو نعيم الفضل بن دكين قال: حدثنا مسعر عن أبي عون الثقفي، عن ابن شداد، عن ابن عباس قال: ((حرمت الخمر بعينها القليل منها والكثير، والسكر كل من كل شراب)) .',NULL,'47',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (16,'١٤- حدثنا علي بن أحمد بن سعيد بن دكين لفظاً من حفظه قال: حدثنا محمد بن أحمد أبو بشر الدولابي، قال: حدثنا يحيى بن عثمان الحمصي قال: حدثنا الفريابي عن سفيان عن ابن أبي ليلى عن الشعبي، عن المغيرة بن شعبة: أنه قام في الركعتين فسبحوا به فلم يجلس فلما فرغ وسلم سجد سجدتين للسهو وهو جالس، ثم قال: ((هكذا رأيت رسول الله صلى الله عليه وسلم يفعله)) .',NULL,'47',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (17,'١٥- أخبرنا أبو القاسم بكير بن الحسن بن عبد الله بن سلمة بن دينار الرازي قال: حدثنا بكار بن قتيبة البكراوي قال: حدثنا مؤمل بن إسماعيل قال: حدثنا سفيان عن الأعمش قال: كان إبراهيم يكره أن يقال: حانت الصلاة.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (18,'١٦- حدثني أبو القاسم بكير الرازي قال: حدثنا صفوان بن عيسى قال: حدثنا محمد بن عجلان، عن القعقاع، عن أبي صالح عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: ((إنما أنا لكم مثل الوالد فإذا أتى أحدكم الغائط فلا يستقبل القبلة ولا يستدبرها، فإذا استطاب فلا يستطب بيمينه، وكان يأمر بثلاث أحجار ونهى عن الروث والرمة)) .',NULL,'48',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (19,'١٧- حدثني أبو الحسن محمد بن علي بن جعفر بن خلاد الجوهري قال: حدثنا أبو غسان مالك بن يحيى بن مالك الدميري قال: حدثنا يزيد بن هارون قال: حدثنا أبو عقيل يحيى بن المتوكل قال: حدثني عبد الله بن نافع، عن أبيه، عن ابن عمر قال: ((قدمت رفقة فنزلوا المصلى فقال عمر لعبد الرحمن بن عوف: هل لك أن تحرسهم الليلة من السرق؟ فباتا يحرسانهم ويصليان ما كتب الله لهما فسمع عمر بكاء صبي فتوجه نحوه وقال لأمه: اتقي الله وأحسني إلى صبيك، ثم عاد إلى مكانه، فسمع بكاه فعاد إلى أمه فقال لها مثل ذلك ثم عاد إلى مكانه، فلما كان في آخر الليل سمع بكاءه ⦗٥٠⦘ فأتى أمه فقال: ويحك إني لأراك أم سوء، ما لي لا أرى ابنك لا يقر منذ الليلة؟ فقالت: يا عبد الله قد أبرمتني منذ الليلة أني أريغه عن الفطام، فيأبى قال: ولم؟ فقال: لأن عمر لا يفرض إلا للفطم قال: وكم له؟ قالت كذا وكذا شهراً، قال: ويحك لا تعجليه، فصلى الفجر وما يستبين الناس قراءته من غلبة بكائه فلما سلم، قال: يا بؤس لعمر كم قتل من أولاد المسلمين؟ ثم أمر منادياً فنادى: لا تعجلوا صبيانكم عن الفطام فإنا نفرض لكل مولود في الإسلام، وكتب ذلك في الآفاق: إنا نفرض لكل مولود في الإسلام.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (20,'١٨- حدثنا أبو بكر أحمد بن محمد بن أبي الموت المكي قال: حدثنا علي بن عبد العزيز قال: حدثنا القعنبي، عن مالك بن أنس عن يحيى بن سعيد، عن عمرة بنت عبد الرحمن أن أبا بكر الصديق دخل على عائشة وهي تشتكي ويهودية ترقيها فقال أبو بكر: ((ارقيها بكتاب الله)) .',NULL,'50',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (21,'١٩- حدثنا أبو بكر بن أبي الموت قال: حدثنا علي، قال: حدثنا القعنبي عن مالك بن أنس، عن خبيب بن عبد الرحمن، عن حفص بن عاصم عن أبي سعيد الخدري، أو عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: ((سبعة يظلهم الله يوم لا ظل إلا ظله، إمام عادل، وشاب نشأ في عبادة الله، ورجل ذكر الله خالياً ففاضت عيناه، ورجل دعته ذات حسب وجمال فقال: إني أخاف الله، ورجل تصدق بصدقة فأخفاها حتى لا تعلم شماله ما تنفق يمينه، ورجل كان قلبه معلق بالمساجد إذا خرج منه حتى يعود إليه، ورجلان تحابا في الله اجتمعا على ذلك وتفرقا عليه)) .',NULL,'51',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (22,'٢٠- حدثني أبو قتيبة سلم بن الفضل البغدادي قال: حدثني محمد بن يونس الكديمي قال: حدثنا محمد بن عبد الله الأنصاري قال: حدثنا محمد بن عون، عن ⦗٥٢⦘ الحسن، عن عبد الرحمن بن سمرة قال: قال رسول الله صلى الله عليه وسلم: ((يا عبد الرحمن، لا تسأل الإمارة فإنك إن أعطيتها عن مسألة وكلت إليها، وإن أعطيتها من غير مسألة أعنت عليها، وإذا حلفت على يمين فرأيت خيراً منها فأت الذي هو خير وكفر عن يمينك)) .',NULL,'51',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (23,'٢١- حدثنا أبو قتيبة السلم ابن الفضل قال: أخبرنا عمر بن أبي شيبة قال: حدثنا محرز بن هشام: أخبرنا عبد الملك بن هارون بن محمد بن عنترة، عن أبيه عن أبي إسحاق، عن مهاجر المكي عن عطية الليثي عن علي بن أبي طالب قال: قال رسول الله صلى الله عليه وسلم: ((أيما عبد قال عند الكرب: لا إله إلا الله الحليم الكريم، سبحان الله رب العرش العظيم، الحمد لله رب العالمين كان حقاً على الله عز وجل أن يحرمه من النار)) .',NULL,'52',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (24,'٢٢- حدثنا أبو محمد عبد الله بن جعفر بن محمد بن زنجويه بن الورد قال: حدثني خير بن موفق، قال: حدثني يحيى بن عبد الله بن بكير قال: حدثنا الليث بن سعد، عن محمد بن غنج، عن نافع أن أصحاب رسول الله صلى الله عليه وسلم أكلوا ثوماً عام غزوة خيبر نياً فيزعمون أن رسول الله صلى الله عليه وسلم قال: ((من أكل منكم هذه الشجرة فلا يأتي المسجد حتى يذهب ريحها)) .',NULL,'53',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (25,'٢٣- حدثنا أبو محمد قال: حدثنا خير بن موفق، قال: حدثنا ابن بكير قال: حدثنا الليث بن سعد بن محمد بن غنج، عن نافع عن ابن عمر، عن رسول الله صلى الله عليه وسلم: ((أنه دفع إلى يهود خيبر [نخل] خيبر وأرضها على أن يعملوها من أموالهم ولرسول الله صلى الله عليه وسلم شطر ثمرتها)) .',NULL,'53',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (26,'٢٤- حدثنا أبو محمد بن الورد قال: حدثنا محمد بن معمر بن أعين قال: حدثنا علي بن جعد قال: حدثنا ابن أبي ذئب، عن الزهري قال: قال أبو بكر الصديق: ((لو رأيت رجلاً على حد ما حددته ولا دعوت إليه أحداً حتى يكون معي غيري)) .',NULL,'54',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (27,'٢٥- حدثنا أبو الفضل العباس بن محمد بن نصر السري بن عبد الله بن سهل بن أيوب الرافقي بمصر قال: حدثنا الهلال بن علاء قال: حدثني أبي قال: حدثنا عيسى ابن يونس عن مجمع بن يحيى بن مجمع بن جارية الأنصاري قال: حدثني رجل من الأنصار أن رسول الله صلى الله عليه وسلم قال: ((بلوا أرحامكم ولو بالسلام)) .',NULL,'54',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (28,'٢٦- حدثنا أبو الفضل الرافقي قال: حدثنا هلال بن العلاء الرقي قال: حدثنا أبي قال: حدثنا يزيد بن زريع، عن سعيد، عن قتادة، عن حنظلة الأسدي أن رسول الله صلى الله عليه وسلم قال: ((من حافظ على الصلوات الخمس مواقيتها وركوعها وسجودها يراها حقاً لله تعالى عليه إلا حرم على النار)) .',NULL,'55',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (29,'٢٧- حدثنا أبو يوسف يعقوب المبارك قال: حدثنا بكر بن سهل قال: حدثنا عبد الله بن يوسف قال: حدثنا مالك بن أنس، عن ابن شهاب، عن أنس بن مالك ((أن رسول الله صلى الله عليه وسلم دخل مكة عام الفتح وعلى رأسه المغفر فلما نزعه، جاءه رجل فقال: ابن خطل متعلق بأستار الكعبة)) .',NULL,'56',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (30,'٢٨- حدثنا أبو يوسف يعقوب بن المبارك قال: حدثنا أبو عبد الرحمن النسائي قال: حدثنا قتيبة بن سعيد قال: حدثنا مالك بن أنس، عن ابن شهاب، عن أنس ((أن النبي صلى الله عليه وسلم دخل مكة يوم الفتح وعلى رأسه المغفر فقيل: إن ابن خطل متعلق بأستار الكعبة، فقال: اقتلوه)) .',NULL,'56',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (31,'٢٩- حدثنا أبو الورد قال: حدثنا أبو يزيد يوسف بن يزيد القراطيسي قال: حدثنا حجاج بن إبراهيم، قال: حدثنا المعتمر، عن الحجاج، عن عمرو بن شعيب، عن أبيه، عن جده قال: قال رسول الله صلى الله عليه وسلم: ((من بنى مسجداً لله يذكر فيه اسم الله، بنى الله عز وجل له بيتاً في الجنة)) .',NULL,'57',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (32,'٣٠- حدثنا إبراهيم بن محمد بن صالح بن سنان قال: حدثنا أبو زرعة قال: حدثنا علي بن عياش الحمصي قال: حدثنا الليث بن سعد: حدثني يزيد بن أبي حبيب، عن سعد بن سنان، عن أنس بن مالك، عن رسول الله صلى الله عليه وسلم قال: ((تكون بين يدي الساعة فتن كقطع الليل المظلم يصبح فيها الرجل مؤمناً ويمسي كافراً، ويمسي كافراً ويصبح مؤمناً؛ يبيع أقوام دينهم بعرض من الدنيا)) .',NULL,'57',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (33,'٣١- حدثنا أبو يعقوب إسحاق بن إبراهيم بن هاشم الأذرعي قال: حدثنا أبو زرعة قال: حدثنا علي بن عياش قال: حدثنا شعيب بن أبي حمزة، عن نافع، عن ابن عمر، أن النبي صلى الله عليه وسلم قال: ((من جاء منكم الجمعة فليغتسل)) .',NULL,'58',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (34,'٣٢- حدثنا أبو الحارث أحمد بن عمارة الخطابي قال: حدثنا علي بن حفص بن ⦗٥٩⦘ عمرو السلمي أبو الحسن الرازي قال: حدثنا محمد بن الجهم السمري، قال: حدثنا يحيى بن زياد الفراء، عن قيس بن الربيع، عن عاصم عن زر، عن عبد الله: ((أن النبي صلى الله عليه وسلم قرأ {طه} بكسر الطاء والهاء)) .',NULL,'58',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (35,'٣٣- حدثنا أبو عبد الله الخليلي وأبو الحسن محمد بن عبد الله بن جعفر الرازي قالا: حدثنا ابن ضريس، قال: حدثنا القعنبي قال: قرأت على مالك بن أنس، عن ابن شهاب، عن أنس بن مالك: ((أن رسول الله صلى الله عليه وسلم دخل مكة وعلى رأسه مغفر، فلما وضعه جاءه رجل فقال: ابن خطل متعلق بأستار الكعبة؛ فقال النبي صلى الله عليه وسلم: اقتلوه)) .',NULL,'59',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (36,'٣٤- حدثنا ابن عمرو محمد بن موسى بن فضالة قال: حدثنا إسماعيل بن قيراط، قال: حدثنا عبد الله بن أحمد بن بشير بن ذكوان، قال: حدثنا سويد بن عبد العزيز قال: ⦗٦٠⦘ ثنا شعبة قال: حدثنا مالك بن أنس، عن عمرو بن مسلم، عن سعيد، عن أم سلمة، عن النبي صلى الله عليه وسلم قال: ((من رأى منكم هلال ذي الحجة فأراد أن يضحي فلا يأخذ من شعره ولا من أظفاره حتى يضحي)) .',NULL,'59',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (37,'٣٥- حدثنا أبو الحسن محمد بن عبد الله الرازي قال: حدثنا أبو العباس سليمان ابن أحمد بن الضحاك الرملي قال: حدثنا عبد الله بن أحمد بن عمير المقدسي قال: حدثنا أبو الأصبغ محمد بن سماعة قال: حدثنا مهدي بن إبراهيم قال: حدثنا مالك بن أنس، عن الزهري قال: سمعت سعيد بن المسيب يقول: ((ليس من شريف ولا عالم ولا ذي سلطان إلا وفيه عيب لا بد، ولكن من الناس من لا يذكر عيوبه، من كان فضله أكثر من نقصه، ذهب نقصه لفضله)) .',NULL,'60',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (38,'٣٦- حدثني محمد بن محمد وأبو علي آدم العدل قالا: حدثنا أحمد بن علي بن سعيد المروزي القاضي قال: حدثنا سريج قال: حدثنا فرج بن فضالة، عن هشام بن عروة، عن أبيه، عن عائشة قالت: ((كنت أغلف لحية رسول الله صلى الله عليه وسلم بالغالية ثم يحرم)) .',NULL,'61',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (39,'٣٧- حدثنا أبو الوليد بكر بن شعيب القرشي قال: حدثني أحمد بن سعيد قال: حدثنا إبراهيم بن يعقوب: حدثني إسماعيل بن سعيد الطبري قال: حدثنا منيع بن عبد الرحمن شيخ بصري كتبنا عنه بمكة، عن هشام، عن ابن سيرين، عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: ((لا تعطي المرأة من مالها شيئاً إلا بإذن زوجها)) .',NULL,'61',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (40,'٣٨- حدثني بكر بن شعيب قال: حدثنا أحمد بن عمرو قال: حدثنا ⦗٦٢⦘ عبد الرحمن بن يحيى بن زكريا الحراني قال: حدثنا يحيى بن عبد الله، عن إبراهيم بن يزيد، عن عمرو بن دينار وأبي الزبير، عن جابر قال: قال رسول الله صلى الله عليه وسلم: ((كل مسكر حرام)) .',NULL,'61',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (41,'٣٩- حدثني أبو زرعة وأبو بكر ابنا أبي دجانة قالا: حدثنا أبو الحسن محمد بن بشر بن يوسف قال: حدثنا هشام بن عمار قال: حدثنا سعيد بن يحيى قال: حدثنا أبو حمزة الثمالي عن أبي إسحاق، عن الحارث، عن علي قال: قال رسول الله صلى الله عليه وسلم: ((من زار أخاه في الله لا لغير التماس موعود وينجز ما عنده، وكل الله به تسعين ألف ملك ينادونه من خلفه حتى يرجع إلى بيته، ألا طبت وطابت لك الجنة، ألا طبت وطابت لك الجنة)) .',NULL,'62',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (42,'٤٠- حدثنا أبو زرعة وأبو بكر قالا: حدثنا أحمد بن صالح التميمي قال: حدثنا محمد قال: حدثنا جرير، عن منصور، عن أبي إسحاق، عن عاصم بن حمزة، عن علي قال: قال رسول الله صلى الله عليه وسلم: ((إن الله وتر يحب الوتر؛ فأوتروا يا أهل القرآن)) .',NULL,'63',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (43,'٤١- حدثني أبو بكر محمد بن إبراهيم بن محمد الحلبي الساكن بتنيس قال: حدثنا محمد بن إسحاق بن بريد الأنطاكي قال: حدثنا الهيثم بن جميل قال: حدثنا جرير بن حازم عن الزبير بن الخريت عن عكرمة عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: ((إذا تشاجرتم في الطريق فاجعلوها سبعة أذرع)) .',NULL,'63',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (44,'٤٢- حدثني أبو بكر بن أحمد بن إبراهيم بن عطية بن الحداد بتنيس قال: حدثنا حامد بن شعيب البلحي قال: حدثنا أبو بكر بن أبي شيبة قال: حدثنا أبو معاوية، عن أبي مالك الأشجعي، عن ابن حدير، عن ابن عباس قال: قال رسول الله صلى الله عليه وسلم: ((من كانت له أنثى لم يئدها ولم يهنها، ولم يؤثر ولده عليها، -يعني: الذكور- أدخله الله الجنة)) .',NULL,'64',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (45,'٤٣- حدثني أبو القاسم إلياس بن محمد بن إلياس التجيبي بمصر قال: حدثنا علي بن أحمد قال: حدثنا أبو بكر بن أبي موسى قال: سمعت موسى بن طريف قال: سمعت أبا معاوية الأسود يقول: ((اللهم اغفر لي أنسي بالآدميين)) .',NULL,'64',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (46,'٤٤- حدثني أبو بكر محمد بن سعيد بن إبراهيم بن القاسم الجحدري بتنيس قال: ⦗٦٥⦘ حدثنا محمد بن يحيى الأنطاكي قال: حدثني محمد بن الحسن الأشرسوني: حدثنا أحمد بن محمد بن صدقة قال: حدثنا وكيع، عن النضر بن عربي، عن عكرمة، عن ابن عباس قال: قال النبي صلى الله عليه وسلم: ((ليت شعري ما فعل أبوي؛ فأنزل الله عليه: {إنا أرسلناك بالحق بشيراً ونذيراً ولا تسأل عن أصحاب الجحيم} )) .',NULL,'64',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (47,'٤٥- حدثنا أبو الحسن علي بن علان الحراني قال: حدثنا أحمد بن علي قال: حدثنا محمد بن بحر في بلهجيم بالبصرة قال: حدثنا سليم بن مسلم الحجبي المكي قال: حدثنا النضر بن العربي، عن عكرمة، عن ابن عباس أن النبي صلى الله عليه وسلم قال: ((الذي يشرب في آنية الذهب والفضة إنما يجرجر في بطنه نار جهنم)) .',NULL,'65',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (48,'٤٦- حدثنا أبو محمد بن الورد قال: حدثنا عمرو بن أحمد عن عمرو بن السرح قال: حدثنا إسحاق بن زبريق الزبيدي قال: حدثنا عمرو بن الحارث قال: حدثنا ⦗٦٦⦘ عبد الله بن سالم، عن الزبيدي، عن أنس بن مالك قال: ((أهدي للنبي صلى الله عليه وسلم إستبرق فجعل الناس يمسونها ويعجبون منها، فقال النبي صلى الله عليه وسلم: أتعجبكم هذه؟ فوالله لمناديل سعد بن معاذ في الجنة أحسن منها)) .',NULL,'65',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (49,'٤٧- حدثنا حمزة بن محمد الكناني قال: حدثنا أبو عبد الله النسائي قال: حدثنا علي بن حجر قال: حدثنا هشيم قال: ((آخر غزاة غزاها النبي صلى الله عليه وسلم غزاة تبوك)) .',NULL,'67',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (50,'٤٨- حدثنا أحمد بن سليمان بن أيوب بن حذلم القاضي قال: حدثنا موسى بن محمد، عن ابن عوف المزني، قال: حدثنا النفيلي قال: قرأت على معقل، قال: سألت عطاء: كم في القرآن سجدة؟ فقال: ((أمسك: الأعراف، الرعد، النحل، وبني إسرائيل، ومريم، والحج منها وليس في الآخرة سجدة، والفرقان، والنمل وتنزيل وليس في آخر سجدة، وحم تنزيل، وليس في المفصل سجدة)) .',NULL,'67',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (51,'٤٩- حدثنا أبو علي محمد بن هارون بن شعيب الأنصاري قال: حدثنا زكريا بن يحيى السجري قال: حدثنا إبراهيم بن الحسن المقسمي قال: حدثنا سلام بن أبي الصهباء قال: حدثنا ثابت البناني، عن أنس بن مالك قال: قال رسول الله صلى الله عليه وسلم: ((طلب العلم فريضة على كل مسلم)) .',NULL,'67',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (52,'٥٠- حدثنا أبو المعافى المسافر بن أحمد بن حاطب بتنيس قال: حدثنا الحسن ابن الحسين الصواف قال: حدثنا أبو حمدون الطيب بن إسماعيل الفصاص، قال: حدثنا يحيى بن آدم عن أبي بكر، عن عاصم عن زر، عن عبد الله قال: قال رسول الله صلى الله عليه وسلم: ((من سره أن يقرأ القرآن رطباً أو غضاً كما نزل فليقرأ على قراءة ابن أم عبد)) .',NULL,'68',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (53,'٥١- حدثني أبو الحسن علي بن هاشم البغدادي قال: سمعت جعفر يقول: حدثني أبو عيسى الأنباري قال: سمعت حميد بن عاصم يقول سمعت حارث يقول: ((لكل شيء جوهر، وجوهر العقل الصبر)) .',NULL,'69',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (54,'٥٢- حدثنا أبو سمعون عبد الرحمن بن عبد الله بن عمر بن راشد قال: حدثنا أبو زرعة قال: حدثنا ابن المبارك قال: حدثنا ابن عياش عن أبي بكر بن مريم قال: كتب عمر بن عبد العزيز إلى والي حمص: ((أن مر لأهل العلم والصلاح من بيت المال ما يغنيهم ويكفيهم لئلا يشغلهم الفقر عن تلاوة القرآن وما حملوه من علم الحديث)) .',NULL,'69',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (55,'٥٣- حدثنا أبو الحسن علي بن أحمد بن محمد بن سلامة الطحاوي بمصر قال: حدثني أبي قال: سمعت أحمد بن عمر يقول: ((كان كثير من هذه الحكايات أحب إلي من كثير من الفقه)) .',NULL,'70',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (56,'٥٤- حدثنا أبو أحمد الحسين بن جعفر الزيات قال: حدثنا محمد بن عيسى قال: حدثنا أبو السكين قال: سمعت الأصمعي يقول: سمعت الرشيد يقول: لما افتتحت مدائن كسرى وجد على بابها مكتوب الكفالة غرامة من لم يصدق)) .
INSERT INTO [b4003] VALUES (57,'<span data-type="title" id=toc-3>أول الجزء الثاني</span>
INSERT INTO [b4003] VALUES (58,'٥٦- حدثنا أبو إسحاق بن إبراهيم بن أبي ثابت البصري العطار قال: حدثنا سعدان بن أبي نضير قال: حدثنا سفيان بن عيينة عن عمرو بن دينار أنه سمع جابر بن عبد الله يقول: ((زار رسول الله صلى الله عليه وسلم قبر عبد الله بن أبي بعدما أدخل حفرته فأمر به فأخرج ووضعه على ركبتيه أو فخده، ونفث عليه من ريقه وألبسه قميصه والله أعلم)) .',NULL,'71',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (59,'٥٧- حدثني إبراهيم بن سنان قال: حدثنا أبو زرعة قال: حدثني أبو نعيم قال: حدثنا سفيان، عن جابر عن عدي بن ثابت، عن عبد الله بن يزيد، عن أبي أيوب قال: ((صلى رسول الله صلى الله عليه وسلم بجمع المغرب ثلاثاً والعشاء ركعتين بإقامة واحدة)) .',NULL,'71',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (60,'٥٨- حدثنا أبو عبد الملك هشام بن محمد الكندي، حدثنا عثمان بن خرزاد قال: حدثنا حسين بن يزيد الأنصاري قال: حدثنا حفص بن غياث، عن أشعث، عن الحسن، عن أنس قال: ((نهى رسول الله صلى الله عليه وسلم أن يصلى بين القبور)) .',NULL,'72',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (61,'٥٩- حدثنا جعفر بن محمد بن هشام الكندي قال: حدثنا أبو بكر أحمد بن محمد القنطوي قال: حدثنا علي بن حرب الموصلي قال: حدثنا سفيان بن عيينة، عن مسعر، عن عمرو بن دينار، عن يحيى بن جعدة، عن أم هانئ قالت: ((كنت أسمع قراءة النبي صلى الله عليه وسلم وأنا على عريش أهلي)) .',NULL,'73',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (62,'٦٠- حدثنا أبو يعقوب الأذرعي وأبو العباس الرازي وأبو محمد بن الورد قالوا: حدثنا أبو يزيد القراطيسي قال: حدثنا العباس بن طالب قال: حدثنا حماد بن زيد قال: حدثنا أيوب، عن حميد بن هلال العدوي، عن هصان، عن عبد الرحمن بن سمرة، عن معاذ بن جبل قال: قال رسول الله صلى الله عليه وسلم: ((بشر الناس أن من قال: لا إله إلا الله مخلصاً بها دخل الجنة)) ، فقلت: أنت سمعته من معاذ؟ قال: أنا سمعته من معاذ يحدث عن رسول الله صلى الله عليه وسلم.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (63,'٦١- حدثنا أبو يعقوب الأذرعي وأبو طاهر بن محمد بن عبد العزيز بن حسنون قالا: حدثنا مقداد قال: حدثنا أسد بن موسى قال: حدثنا سفيان بن عيينة، عن أبي إسحاق، عن الحارث، عن علي بن أبي طالب قال: قال رسول الله صلى الله عليه وسلم: ((قد تجوزنا لكم عن صدقة الخيل والرقيق)) .',NULL,'74',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (64,'٦٢- حدثنا خيثمة قال: حدثنا خلف بن محمد قال: حدثنا إسماعيل بن أبان الوراق قال: حدثنا سلام الطويل، عن زيد العمي، عن محمد بن سيرين عن أبي سعيد الخدري قال: قال رسول الله صلى الله عليه وسلم: ((فاتحة الكتاب شفاء من كل سم)) .',NULL,'75',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (65,'٦٣- حدثنا خيثمة قال: حدثنا جعفر بن عنبسة قال: حدثنا أحمد بن عمر البجلي قال: حدثنا محمد بن فضيل، عن عبد الرحمن بن زيد، عن أبيه زيد، عن جعفر بن محمد، عن أبيه، عن جده قال: قال رسول الله صلى الله عليه وسلم: ((إذا دخلتم بلاداً فكلوا بصلها يطرد عنكم وباءها)) .',NULL,'76',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (66,'٦٤- حدثنا أبو حاتم عدي بن يعقوب الخطيب قال: حدثنا محمد بن يزيد، عن عبد الصمد قال: حدثنا إبراهيم بن سعيد الجوهري قال: حدثنا شاذان، عن هريم، عن ⦗٧٧⦘ سفيان، عن مجالد، عن الشعبي قال: ((من قرأ القرآن لم يخرف)) .',NULL,'76',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (67,'٦٥- حدثنا أبو القاسم علي بن الحسين بن السفر بقراءة أبي سهل صالح بن إدريس المقري قال: حدثنا أحمد بن البرقي قال: حدثنا عمرو بن أبي سلمة قال: حدثنا صدقة ابن عبد الله الدمشقي عن ابن جريج عن محمد بن المنكدر عن جابر بن عبد الله قال: قال رسول الله صلى الله عليه وسلم: ((الحج المبرور ليس لصاحبه عند الله ثواب إلا الجنة، قيل: يا رسول الله وما بره؟ قال: الثج والعج فإن لم يكن فطيب الكلام وإطعام الطعام)) .',NULL,'77',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (68,'٦٦- حدثني أبو الفضل العباس بن عبد الله البغدادي قال: سمعت سهل بن عبد الله التستري يقول: ((حياة هذا العلم على يدي العلماء ثم يذهب على يدي العلماء)) .',NULL,'78',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (69,'٦٧- حدثني الخليلي قال: حدثنا مفضل بن محمد بن الجندي قال: سمعت أبا مصعب يقول: ((كان مالك بن أنس لا يحدث بحديث النبي صلى الله عليه وسلم إلا على وضوء إجلالاً لحديث رسول الله صلى الله عليه وسلم)) .',NULL,'78',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (70,'٦٨- حدثني أبو طاهر محمد بن عبد العزيز بن حسنون لفظاً من كتابه قال: حدثنا محمد بن إسحاق العطار بالمدينة قال: حدثنا أحمد بن أبي بكر، عن حاتم بن إسماعيل، عن عبد الله بن محرز، عن قتادة، عن أنس قال: قال رسول الله صلى الله عليه وسلم: ((لا يجوز النكاح إلا بولي وشاهدي عدل)) .',NULL,'78',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (71,'٦٩- حدثني أبو إسحاق إبراهيم القدوري قال: سمعت أحمد بن محمد الزيادي يقول: كان أبو حاتم العطار الواعظ يقول في قصصه –إذا رأى الصوفية عليهم القوط، والأبراد وثياب الصوف والمرقعات وهم يصلون-: قد نشرتم أعلامكم وضربتم طبولكم وأخرجتم أبواقكم، فيا ليت شعري في اللقاء كيف تكونون؟!',NULL,'78',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (72,'٧٠- سمعت أبا الفوارس الصابوني يقول: سمعت المزني يقول: سمعت الشافعي يقول: رأيت قوماً ببغداد يقال لهم: الصوفية، لهم أفقر في دين الله من وقادي الأتانير.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (73,'٧١- حدثني القاضي أبو الحسن علي بن محمد القزويني قال: حدثنا مأمون بن عبيد الله المروزي قال: سمعت محمد بن علي المروزي يقول: سمعت زيد بن أخرم يقول: سمعت عبد الله بن داود سمعت هشام بن عروة يقول: ((ينبغي للرجل أن يكره ولده على طلب الحديث)) .',NULL,'79',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (74,'٧٢- حدثني أبي وأبو علي بن أبي نصر وحماد بن فطيس قالوا: حدثنا أحمد بن علي بن سعيد القاضي قال: حدثنا يحيى بن معين قال: حدثنا حماد بن خالد الخياط قال: حدثنا معاوية بن صالح عن يونس بن سيف عن غضيف بن الحارث قال: ((ما نسيت من الأشياء فإني لم أنس شيئاً رأيت رسول الله صلى الله عليه وسلم يفعله، رأيته صلى الله عليه وسلم واضعاً يده اليمنى على اليسرى في الصلاة)) .',NULL,'79',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (75,'٧٣- حدثنا أبو بكر العطوفي قال: حدثنا عبد الله بن محمود المروزي قال: حدثنا محمد بن يحيى قال: حدثنا هاشم بن مخلد قال: حدثنا أبو يحيى عن إبراهيم بن ميمون عن أبي إسحاق الهمداني قال: أخبرني عمرو بن شرحبيل أبو ميسرة: ((أن جبريل علم النبي صلى الله عليه وسلم يقول إذا فرغ من فاتحة الكتاب يقول: آمين، فكان النبي صلى الله عليه وسلم إذا فرغ منها قال: آمين. يمد بها صوته)) .',NULL,'80',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (76,'٧٤- حدثني أبو الحسن أحمد بن محمود الشمعي بمصر في مسجد عبد الله قال: حدثنا أبو أحمد محمد بن عبدوس بن كامل السراج قال: حدثنا داود بن عمرو الضبي قال: حدثنا إسماعيل بن عياش قال: حدثني حميد بن أبي سويد بن عطاء عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: ((علموا ولا تعنفوا فإن المعلم خير من المعنف)) .',NULL,'81',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (77,'٧٥- حدثني أبو محمد الحسن بن رشيق العسكري المعدل قال: حدثنا محمد بن أحمد بن جعفر الذهلي قال: حدثنا عبيد بن جناد الحلبي قال: عطاء بن مسلم الخفاف عن خالد الحذاء عن عبد الرحمن بن أبي بكرة، عن أبيه عن النبي صلى الله عليه وسلم قال: ((اغد عالماً ⦗٨٢⦘ أو متعلماً مستمعاً أو محباً ولا تكن الخامسة فتهلك)) ، فقال مسعر بن كدام: يا عطاء هذه خامسة زادنا الله إياها لم تكن في أيدينا، إنما كان في أيدينا: ((اغد عالماً أو متعلماً أو مستمعاً ولا تكن الرابعة فتهلك)) ، يا عطاء، ويل لمن لم تكن فيه واحدة من هذه.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (78,'٧٦- حدثنا أبو الحسن علي بن أحمد القاضي قال: حدثنا القاسم بن عبيد السياري قال: حدثنا عبد الله بن علي الكرماني قال: علي بن الحسن بن شقيق يقول: سمعت أبا حمزة السكري يقول: سمعت أبا حنيفة يقول: ((إذا جاء الحديث عن النبي صلى الله عليه وسلم لم نمل إلى غيره وأخذنا به، وإذا جاء عن الصحابة تخيرنا، وإذا جاء عن التابعين زاحمناهم)) .',NULL,'83',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (79,'٧٧- حدثنا خيثمة قال: حدثنا الحسن بن جرير الصوري قال: حدثنا أبو صفوان القاسم بن يزيد الكلاح قال: حدثنا حسان بن سياه قال: حدثنا الحسن بن ذكوان عن ⦗٨٤⦘ نافع عن ابن عمر قال: قال رسول الله صلى الله عليه وسلم: ((من سئل عن علم فكتمه جيء به يوم القيامة قد ألجم بلجام من نار)) .',NULL,'83',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (80,'٧٨- حدثني أبو عبد الرحمن ضحاك بن يزيد بن أبي كبشة السكسكي قال: حدثنا أبو زرعة قال: حدثنا الحسن بن الصباح قال: حدثنا خالد بن خداش قال: صحبت مالك بن أنس فقلت: يا أبا عبد الله أوصني فقال: ((اتق الله واطلب العلم من عند أهله)) .',NULL,'87',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (81,'٧٩- حدثنا خيثمة قال: حدثنا السري بن يحيى قال: حدثنا قبيصة قال: حدثنا الثوري عن طلحة الحضرمي عن عطاء عن ابن عباس عن النبي صلى الله عليه وسلم أنه قال: ((التمسوا الخير عند حسان الوجوه)) .',NULL,'87',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (82,'٨٠- حدثنا خيثمة قال: حدثنا ابن أبي عزرة قال: حدثنا قبيصة قال: حدثنا الثوري عن جرير عن الحسن عن عمران بن الحصين عن النبي صلى الله عليه وسلم قال: ((لا تقوم الساعة إلا على شرار القوم)) .',NULL,'88',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (83,'٨١- حدثنا أبو يعقوب الأذرعي قال: حدثنا أبو زرعة قال: حدثنا سليمان بن حرب قال: حدثنا حماد بن سلمة، عن عمرو بن دينار، عن نافع بن جبير بن مطعم، عن أبيه: ((أن النبي صلى الله عليه وسلم نام عن صلاة الصبح حتى طلعت الشمس فلما قام صلى ركعتين ثم صلى الصبح)) .',NULL,'89',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (84,'٨٢- حدثنا أبو عمر بن موسى بن رباح قال: سمعت ابن دريد يحدث عن عبيد الله ابن محمد، عن أبيه قال: يقال: ((إن سخاء النفس عما في أيدي الناس أكبر من سخاء النفس بالبذل، ومروءة القنوع والرضى أكثر من مروءة الإعطاء)) .',NULL,'89',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (85,'٨٣- حدثني أبو عمرو سعيد بن عثمان بن الفتح الشافعي قال: حدثنا أبو العباس عمرو بن عصيم إمام جامع صور قال: حدثنا مؤمل بن شهاب قال: حدثنا يزيد بن هارون قال: حدثنا عمران بن حدير عن ابن عباس قال: ((من ملك ثلثمائة درهم فقد حرم عليه نكاح الإماء ووجب عليه الحج)) .',NULL,'90',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (86,'٨٣- حدثني نصر بن علي بن عبد الله مولى أبي القاسم القزويني القاضي قال: حدثنا عبد الله بن محمد النيسابوري قال: حدثنا يوسف بن سعيد بن مسلم قال: حدثنا خالد بن يزيد، قال: حدثنا أبو حمزة الثمالي عن أبي جعفر قال: أدخل معاوية بن أبي سفيان غلاماً له خصياً على امرأته ميسون بنت بحدل فاستترت منه فقال لها معاوية: ما لك تسترين منه؟ إنما هو بمنزلة المرأة، (سمعت رسول الله صلى الله عليه وسلم) يقول: ((إنه قوم بمثل هذا فمن صبر منهم واحتسب كانت له الجنة)) .',NULL,'90',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (87,'٨٤- حدثني أبو عبد الله محمد بن إبراهيم النعمان قال: حدثنا أحمد بن هودة قال: حدثنا إبراهيم بن إسحاق النهاوندي، عن عبد الله بن حماد، عن كثير بن كميلة، عن جعفر بن محمد، عن آبائه قال: ((مر النبي صلى الله عليه وسلم برجل ساجد وهو يقول: يا رب ما عليك أن ترضي كل من له قبلي تبعة وتدخلني الجنة، فإنما عفوك عن الظالمين، قال: فتعجب النبي صلى الله عليه وسلم من دعائه)) .',NULL,'91',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (88,'٨٥- وبإسناده عن ابن كميلة عن جعفر الصادق قال: ما من أحد يظلم مظلمة إلا أخذ الله بها إما في نفسه وإما في ولده وإما في ماله، فأما الظلم بين العبد ومولاه فإنه إن تاب غفر الله له.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (89,'٨٦- وبإسناده عن آبائه، قال: قال رسول الله صلى الله عليه وسلم: ((ما اصطحب اثنان قط إلا كان أعظمهما أجراً أنفعهما لصاحبه)) .',NULL,'91',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (90,'٨٧- حدثنا حامد بن محمد الحضرمي قال: حدثنا أحمد بن محمد بن محمد بن حمزة الحضرمي قال: حدثنا هشام بن عمار قال: حدثنا الوليد بن مسلم قال: حدثنا الأوزاعي عن الزهري: أن عمر بن الخطاب ضرب صبيغاً الكوفي عن حروف القرآن ⦗٩٢⦘ حتى اضطردت الدماء في ظهره.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (91,'٨٨- حدثنا أبو بكر محمد بن عمر بن مسلم القاضي بن الجعابي قال: حدثنا أبي قال: حدثنا عبد الله بن بشر، عن علي بن موسى الرضا قال: حدثنا موسى بن جعفر قال: حدثني جعفر بن محمد قال: حدثني محمد بن علي قال: حدثني أبي، عن أبيه، عن علي بن أبي طالب رضي الله عنه قال: سمعت النبي صلى الله عليه وسلم قال: ((من أفتى بغير علم لعنته ملائكة السماء والأرض)) .',NULL,'92',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (92,'٨٩- حدثنا خيثمة قال: حدثنا السري بن يحيى قال: حدثني قبيصة أنا سفيان عن ابن أبي ليلى عن الحكم عن مقسم عن ابن عباس قال: ((كفن النبي صلى الله عليه وسلم في ثوبين أبيضين وبردة حبرة)) .',NULL,'93',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (93,'٩٠- حدثنا خيثمة قال: حدثني السري بن يحيى قال: حدثنا أبو نعيم وقبيصة قالا: حدثنا سفيان عن هشام بن عروة عن أبيه عن عائشة قالت: ((كفن رسول الله صلى الله عليه وسلم في ثلاث أثواب بيض من كرسف ليس قميص ولا عمامة)) .',NULL,'93',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (94,'٩١- حدثنا أبو الطالب محمد بن جعفر بن محمد بن غندر البغدادي قال: حدثنا بن الحسين قال: حدثنا أحمد بن عبد العزيز قال: حدثنا زكريا بن يحيى قال: حدثنا الأصمعي قال، قال الفضيل بن عياض: النظر إلى وجه الإمام العادل عبادة.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (95,'٩٢- حدثني أبو قتيبة السلم بن الفضل قال: حدثنا يوسف القاضي قال: حدثنا ⦗٩٤⦘ سعيد بن سليمان قال: حدثنا يحيى بن العلاء البجلي قال: حدثنا الضحاك بن عثمان قال: سمعت أبا سلمة بن عبد الرحمن يحدث عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: ((الهدية تأخذ بالسمع والقلب)) .',NULL,'93',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (96,'٩٣- حدثني أبي رحمه الله قال: حدثنا أحمد بن علي بن سعيد المروزي القاضي قال: حدثنا سويد بن سعيد قال: حدثنا سفيان عن سليمان قال: رأيت مع عكرمة ابناً له فقلت: أيحفظ هذا من الحديث أشياء؟ فقال: ((سبحان الله، أزهد الناس في العالم أهله)) .',NULL,'94',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (97,'٩٤- حدثنا خيثمة قال: حدثنا إسحاق بن سيار النصيبي قال: سمعت أبا عاصم النبيل يقول: شهدت سفيان بن عيينة والناس يقرءون عليه وهو متكئ عليه على جنبه نائم فقلت: يا أبا محمد لا تنم فإنهم يقرءون عليك، فقال: أنا أحدثهم؟ إنما يقرءون علي.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (98,'٩٥- أنشدني أبو القاسم بن السفر وضحاك البهتلي قالا: أنشدنا وريزة الغساني:
INSERT INTO [b4003] VALUES (99,'٩٦- حدثنا ابن أبي الموت قال: حدثنا علي بن عبد العزيز قال: حدثنا القعنبي قال: أخبرنا مالك، عن نافع، عن ابن عمر: ((أنه طلق امرأته وهي حائض في زمان رسول الله صلى الله عليه وسلم، قال عمر: فسألت رسول الله صلى الله عليه وسلم فقال: مره فليراجعها ثم ليمسكها ⦗٩٦⦘ حتى تحيض ثم تطهر فإن شاء أمسكها وإن شاء طلقها قبل أن يمس، فتلك العدة التي أمر الله عز وجل أن تطلق بها النساء)) .',NULL,'95',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (100,'٩٧- حدثني هشام بن محمد بن جعفر الكندي قال: حدثنا عثمان بن خرزاد قال: حدثنا سليمان بن عبد الرحمن قال: حدثنا إسماعيل بن عياش، عن مسلم بن أبي عمران عن سعيد بن جبير، عن أبي الأحوص، عن عبد الله أن النبي صلى الله عليه وسلم قال: ((قارئ القرآن بكل حرف عشر حسنات أما إني لا أقول الحرف {الم} ولكن ألف عشر حسنات ولام عشر حسنات وميم عشر حسنات)) .',NULL,'96',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (101,'٩٨- حدثنا أبو يعقوب إسحاق بن إبراهيم الأذرعي قال: حدثنا عثمان بن خرزاد، قال: حدثنا عبد الله بن عبد الجبار الخبائري، قال: حدثني عبد الله بن حميد، عن أبيه حميد بن عبد الله عن أبي كبشة الأنماري قال: من جمع مالاً من مهاوش ألقي في مهابر فقيل له: يا أبا كبشة ما مهاوش؟ قال: من هاهنا وهاهنا، قيل: فما مهابر قال: وادٍ من أودية جهنم.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (102,'٩٩- حدثني أحمد بن سليمان بن أيوب بن حذلم القاضي قال: حدثنا محمد بن هاشم بن شهاب قال: محمد المتوكل قال: حدثني سهل مولى المغيرة عن محمد بن ⦗٩٨⦘ كعب القرظي عن الحسن عن أنس قال: قال رسول الله صلى الله عليه وسلم: ((يقرأ القرآن من ثلاثة: فرجل يقرؤه لا يخطئ فيه واواً، ولا ألفاً فيقول قد قرأ القرآن فما أخطأ فيه واو، ولا ألف، فذاك من شرار أمتي، ورجل قرأه فلهو أزهى فيه من أمير على منبره، فذاك شر من الآخر، ورجل قرأه فعمد إلى دواء القرآن فجعله على داء قلبه فتستر بلون الحزن واختلس الخوف قد كدوا في محاريبهم وجثوا في برانسهم، فبهم يسقي الله الغيث وينصروا على العدو من أمتي أقل من كبريت الأحمر)) .',NULL,'97',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (103,'١٠٠- حدثني أبو عمر محمد بن إبراهيم المادراني الأطروشي قال: حدثنا أبو العباس أحمد بن شجاع الصوفي قال: حدثنا العلاء بن مسلم الرواسي قال: حدثنا إسماعيل بن مغراء قال: حدثنا إسماعيل بن عياش عن برد عن مكحول عن أبي أمامة قال: قال رسول الله صلى الله عليه وسلم: ((أحضروا موائدكم البقل فإنها مطردة للشيطان مع ⦗٩٩⦘ التسمية)) .',NULL,'98',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (104,'١٠١- حدثنا أحمد بن سليمان بن حذلم القاضي وأبو يعقوب الأذرعي وابن سنان قالوا: حدثنا أبو زرعة قال: حدثنا هشام بن عمار (ح) وحدثني سليمان بن عبد الرحمن قال: حدثني سعيد بن يحيى قال: حدثني عبد الحميد بن جعفر، عن عطاء مولى أبي أحمد أنه سمع أبا هريرة يقول: ((إن رسول الله صلى الله عليه وسلم بعث بعثاً فدعاهم فقال: ماذا معك يا فلان من القرآن؟، قال: معي كذا وكذا، فاستقرأهم كذلك، حتى مر على رجل منهم وهو من أحدثهم سناً فقال: ما معك يا فلان من القرآن؟، قال: معي كذا وكذا مع سورة البقرة، فقال له: إذا كان معك سورة البقرة، فأنت أميرهم، فقال رجل منهم: ما منعني من أن أحفظ القرآن إلا خشية ألا أقوم به، فقال النبي صلى الله عليه وسلم: تعلموا القرآن وأقرءوه وارقدوا، فإن مثل القرآن لمن تعلمه فقرأه وقام به كمثل الجراب محشوٌ مسكاً يفوح ريحه من كل مكان، ومثل من تعلمه ورقد وهو في جوفه كمثل جراب أوكي على مسك)) .',NULL,'99',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (105,'١٠٢- حدثنا أبو علي محمد بن هارون بن شعيب الأنصاري قال: حدثنا بكر بن سهل وعمارة بن وثيمة قالا حدثنا عبد الله بن صالح قال: حدثنا معاوية بن صالح عن راشد بن سعد عن أبي أمامة الباهلي عن النبي صلى الله عليه وسلم أنه قال: ((اتقوا فراسة المؤمن فإنه يرى بنور الله)) .',NULL,'100',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (106,'١٠٣- حدثني أبو الحارث بن عمارة الخطابي قال: حدثنا أحمد بن علي بن سعيد ⦗١٠١⦘ القاضي قال: حدثنا سريج قال: حدثنا عمرو بن جميع عن الأعمش عن بشر بن غالب عن أخيه بشر بن غالب عن الحسن بن علي قال: حدثني أبي عن النبي صلى الله عليه وسلم أنه قال: ((ما من مدينة يكثر مؤذنوها إلا قل بردها)) .',NULL,'100',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (107,'١٠٤- حدثني أبو الحسن بن عثمان بن محمد الذهبي قال: حدثنا الحسن بن يوسف الزهري قال: حدثنا الزبير بن بكار قال: حدثني سليمان بن داود قال: لما ولي زياد البصرة صعد المنبر فحمد الله وأثنى عليه وقال: يا أيها الناس إني قد رأيت خلالاً ثلاثاً نبذت إليكم فيهن النصيحة: رأيت إعظام ذوي الشرف وإجلال أهل العلم وتوقير ذوي الأنساب. وإني أعاهد الله عهداً: لا يأتيني وضيع بشريف لا يعرف له حق شرفه على ضعته إلا عاقبته، ولا يأتيني عالم بجاهل لا جاه في علمه ليهجنه إلا عاقبته، ولا يأتيني كاهل بحدثٍ لا يعرف له فضل سنه إلا عاقبته، فإنما الناس بأشرافهم وعلمائهم وذوي أنسابهم.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (108,'١٠٥- حدثني أبو عمران موسى بن رباح البصري قال: حدثنا ابن دريد قال: حدثنا أبو حاتم وعبد الرحمن قالا: سمعنا الأصمعي يقول: قال أسماء بن خارجة: ما أحب أن أرد عن حاجته سألنيها، لأنه لأن يكون كريماً فأصون عرضه، أو لئيماً فأصون عرضي عنه، أو محتاجاً فأسد حاجته.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (109,'١٠٦- حدثني أبو الحسين عثمان بن محمد الذهبي قال: حدثني الحسن بن يوسف قال: حدثنا الزبير بن بكار حدثني سليمان بن داود قال، قيل للحسن البصري: إن فلاناً له عقل وأدب ورزقه مضيق عليه؟ فقال: ألا تدرون لعل الله جعل عقله وأدبه رزقه.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (110,'١٠٧- حدثني إبراهيم بن سنان قال: حدثنا إسماعيل بن قيراط قال: حدثنا سليمان بن عبد الرحمن قال: حدثنا عبد الملك بن مهران قال: حدثنا عبيد بن نجيح، عن هشام بن عروة، عن أبيه، عن عائشة عن النبي صلى الله عليه وسلم أنه قال: ((عاقبوا الناس على قدر عقولهم)) .',NULL,'102',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (111,'١٠٨- حدثني أبو القاسم بن السفر بقراءة أبي سهل صالح بن إدريس قال: حدثنا يزيد بن محمد بن عبد الصمد قال: حدثنا محمد بن عثمان قال: حدثنا سعيد بن بشير عن قتادة عن أبي ميمونة، عن أبي هريرة قال: ((يا نبي الله، إني إذا رأيتك قرت عيني، وطابت نفسي، فأخبرنا عن كل شيء، قال: كل شيء من ماء)) .
INSERT INTO [b4003] VALUES (112,'<span data-type="title" id=toc-4>أول الجزء الثالث</span>
INSERT INTO [b4003] VALUES (113,'١١٠- حدثني أبو الطيب محمد بن جعفر بن زاراد المنبجي البغدادي قال: حدثنا أحمد بن علي قال: حدثنا حفص بن عبد الله الحلواني قال: حدثنا عمر بن عبيد بياع الخمر قال: حدثنا هشام بن عروة، عن أبيه، عن عائشة قالت: قال رسول الله صلى الله عليه وسلم: ((إن الله يحب أن تؤتى رخصه كما يحب أن تؤتى عزائمه، قلت وما عزائمه؟ قال: فرائضه)) .',NULL,'104',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (114,'١١١- حدثنا أبو الطيب قال: حدثنا أحمد بن محمد بن سعيد بن عبد الرحمن قال: حدثنا زكريا بن يحيى بن سنان قال: حدثنا نصر بن مرحوم قال: حدثنا الحارث ابن محمد القرشي، عن أبيه، عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: ((أما يخشى الذي يرفع رأسه قبل الإمام أن يحول رأسه رأس حمار)) .',NULL,'105',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (115,'١١٢- حدثنا حمزة بن محمد بن علي الكناني قال: حدثنا أبو عبد الله ⦗١٠٦⦘ محمد بن عمران بن عبد الغفار المكي قال: حدثنا إبراهيم بن محمد الشافعي قال: أخبرنا الحارث بن عمير عن أيوب عن عكرمة عن ابن عباس قال: ((سجدة {ص} ليست من العزائم وقد سجد فيها رسول الله صلى الله عليه وسلم)) .',NULL,'105',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (116,'١١٣- حدثنا أبو محمد بن الورد قال: حدثنا الحسن بن علي بن ياسر البغدادي قال: حدثنا محمد بن بكار قال: حدثنا يحيى بن عقبة عن أبي العيزار عن ابن أبي ليلى عن أبي إدريس الأودي عن عاصم عن زر عن صفوان بن عسال قال: ((إن رسول الله صلى الله عليه وسلم سجد في {إذا السماء انشقت} )) .',NULL,'106',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (117,'١١٤- حدثنا أبو محمد بن الورد قال: حدثنا الحسن بن علي بن ياسر البغدادي قال: حدثنا محمد بن بكار قال: حدثنا يحيى بن عقبة بن أبي العيزار عن ابن أبي ليلى، عن إدريس الأودي، عن عاصم، عن زر، عن صفوان بن عسال: ((إن رسول الله صلى الله عليه وسلم سجد في {إذا السماء انشقت} )) .',NULL,'107',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (118,'١١٥- حدثنا أبو يعقوب الأذرعي قال: حدثنا الحسين بن حميد العكي قال: حدثنا زهير بن عباد قال: حدثني فضيل بن عياض، عن الأعمش، عن أبي وائل أنه قال للأعمش: يا سليمان نعم الرب ربنا، لو أطعناه ما عصانا.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (119,'١١٦- حدثنا إبراهيم بن ثابت وخيثمة بن سليمان قالا: حدثنا محمد بن عوف قال: حدثنا آدم بن أبي إياس قال: حدثنا شعبة، عن قتادة، عن أنس قال: قلت: يا رسول الله، إن أهل الكتاب يسلمون علينا فكيف نرد عليهم؟، قال: ((قولوا: وعليكم)) .',NULL,'107',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (120,'١١٧- حدثنا أبو يعقوب قال: حدثنا الحسن بن حميد العكي قال: حدثنا زهير بن عباد قال: حدثنا محمد بن فضيل قال: قال رجل لعامر بن قيس: ادع الله لي فقال: يا ابن أخي، إنك استعنت برجل قد عجز عن نفسه، أطع الله يغفر لك.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (121,'١١٨- حدثنا أبو الحسن علي بن أحمد المقابري قال: حدثنا يوسف بن يعقوب المطوعي قال: حدثنا محمد بن بكار قال: حدثنا محمد بن طلحة، عن ميمون بن حمزة، عن إبراهيم النخعي أنه قال: ((قد تكلمت ولو وجدت بداً ما تكلمت، وإن زماناً أكون فيه فقيه أهل الكوفة، لزمان سوء)) .',NULL,'108',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (122,'١١٩- حدثنا أبو عبد الله محمد بن عبد الله القاضي المالكي قال: حدثنا بن أبي الشيخ قال: حدثنا أحمد بن الصقر قال: أحمد بن بديل اليامي قال: حدثنا حسين الجعفي قال: سمعت عبد الله بن أبي رواد يقول: كان زناة أهل الجاهلية أشد حياء من قراء هذا الزمان.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (123,'١٢٠- حدثنا أبو بكر يوسف بن القاسم بن فارس قال: حدثنا الحسن بن محمد البغدادي قال: سمعت محمد بن الحسن يقول سمعت إبراهيم الحربي يقول: ((ليس كل التقاء مودة ولا كل قطيعة فرقة، والناس من خوف الذل في ذل)) .',NULL,'108',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (124,'١٢١- حدثنا أبو علي بن حبيب قال: حدثنا محمد بن إبراهيم الصوري قال: حدثنا ابن كثير قال: حدثنا سفيان الثوري، عن عكرمة، عن ابن عباس قال: لما رفع القواعد من البيت وجد على صخرة مكتوب: ((ما ساد من لم يعف عن ذنب صاحبه وإن كان في إجرامه متعمداً)) .',NULL,'109',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (125,'١٢٢- وبإسناده عن ابن عباس قال: ((أوحى الله إلى يوسف بعفوك عن إخوتك رفعت ذكرك في الذاكرين)) .',NULL,'109',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (126,'١٢٣- حدثنا أبو القاسم بن السفر بقراءة أبي سهل صالح بن إدريس قال: حدثنا يزيد بن عبد الصمد قال: نا سعيد بن منصور، عن هشيم: حدثنا أبو هاشم، عن أبي مجلز، عن قيس بن عباد، عن أبي سعيد الخدري قال: ((من قرأ سورة الكهف يوم الجمعة أضاء له من النور ما بينه وبين البيت العتيق)) .',NULL,'109',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (127,'١٢٤- حدثنا أبو الميمون بن راشد قال: حدثنا أبو زرعة عن سعيد بن منصور بمثله.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (128,'١٢٥- حدثني أبو العباس بن جمح بن القاسم المؤذن قال: حدثنا أبو القاسم عبد الصمد بن سعيد الحمصي قال: حدثنا أحمد بن عبد الرحمن بن خلف الجبلاني قال: حدثنا أبي قال: حدثنا محمد بن حمير عن ابن جريج عن عطاء عن ابن عباس أن النبي صلى الله عليه وسلم قال: ((نهيت أن أصلي على العزاب من أمتي)) .',NULL,'110',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (129,'١٢٦- حدثني خيثمة وأخوه وأبو علي بن حبيب قالوا: أخبرنا العباس بن الوليد ببيروت قال: أخبرني أبي عن الأوزاعي عن قرة بن عبد الرحمن بن حيوئيل، عن الزهري، عن أبي سلمة عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: ((من حسن إسلام المرء تركه ما لا يعنيه)) .',NULL,'110',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (130,'١٢٧- حدثنا عمرو بن أحمد بن رشيد الطبراني أبو سعيد قال: حدثنا عبد الرحمن ابن القاسم بن رواس الدمشقي قال: حدثنا أبو تقي هشام بن عبد الملك اليزني الحمصي قال: حدثنا عبد السلام بن عبد القدوس، عن هشام بن عروة، عن أبيه، عن عائشة قالت: قال رسول الله صلى الله عليه وسلم: ((أربع لا يشبعن من أربع: عين من نظر، وأرض من مطر، وأنثى من ذكر، وعالم من علم)) .',NULL,'111',NULL,NULL,'0');
INSERT INTO [b4003] VALUES (131,'١٢٨- حدثنا خيثمة بن سليمان قال: حدثنا العباس بن الوليد أخبرني محمد بن شعيب قال: أخبرني عمرو بن يزيد النصري، عن العباس بن أبي سلام عن أبي أمامة قال: قال رسول الله صلى الله عليه وسلم: ((لا يدخل الجنة مدمن خمر ولا عاق لوالديه، ولا مكذب بالقدر)) .',NULL,'112',NULL,NULL,'0');
