INSERT INTO [b423] VALUES (124,'{وَقَالُوا مَا هِيَ إِلَّا حَيَاتُنَا الدُّنْيَا} [٢٤] ... ٧/ ٣٥٨ ... ٦/ ٢٨٥ (¬١)
