INSERT INTO [b423] VALUES (127,'<span data-type="title" id=toc-58>سورة الفتح</span>
{إِنَّا فَتَحْنَا لَكَ} [١] ... ٧/ ٦٠٣ ... دفع (٢٧٠) ٢٩٢
