-- Insert data for b435
INSERT INTO [b435] VALUES (1,'<span data-type="title" id=toc-2>آدم الخرسانى توفى سنة ٢٢٠ هجرية</span>

هو آدم بن أبى إياس الخراسانى البغدادى شيخ البخارى نزيل عسقلان سمع شعبة وابن أبى ذئب وروى الكثير، وكان صالحا قانتا.','1','6',NULL,NULL,'0');
INSERT INTO [b435] VALUES (2,'<span data-type="title" id=toc-3>الآمدى توفى سنة ٦٣١ هجرية</span>

" أبو الحسن على بن أبى على محمد الملقب بسيف الدين الآمدى الفقيه الأصولى المتكلم كان حنفيا ثم انتقل إلى مذهب الشافعى، وأشهر مؤلفاته كتاب الإحكام فى أصول الأحكام','1','1',NULL,NULL,'0');
INSERT INTO [b435] VALUES (3,'<span data-type="title" id=toc-4>الأبى سنة ٨٢٧ هجرية</span>

محمد بن خلفة بن عمر الأبى الوشتاتى المالكى عالم بالحديث من أهل تونس ونسبته إلى (أبة) من قرأها كان قاضيا، ومن مؤلفاته (إكمال المعلم لفوائد كتاب مسلم وشرح المدونة وغيرهما)','1','2',NULL,NULL,'0');
INSERT INTO [b435] VALUES (4,'<span data-type="title" id=toc-5>الإبيانى سنة ١٩٣٦ هجرية</span>

هو محمد زيد الإبيانى مدرس الشريعة الإسلامية بمدرسة الحقوق بمصر من آل زيد فى ابيانة بالغربية فى مصر ولد بها وتعلم بالأزهر ثم بدار العلوم فى القاهرة وتولى تدريس الشريعة فى مدرسة الحقوق مدة ثمان وثلاثين سنة توفى بالقاهرة ومن مؤلفاته شرح الأحكام الشرعية فى الأحوال الشخصية','1','7',NULL,NULL,'0');
INSERT INTO [b435] VALUES (5,'<span data-type="title" id=toc-6>أبى بن كعب سنة ٢١ هجرية</span>

أبى بن كعب بن قيس بن عبيد من بنى النجار من الخزرج أبو المنذر صحابى أنصارى، وكان من كتاب الوحى، وشهد بدرا وأحدا والخندق والمشاهد كلها مع رسول الله صلى الله عليه وسلم وكان يفتى على عهد الرسول، واشترك فى جمع القرآن فى عهد عثمان، وفى الخبر (أقرأ أمتى أبى بن كعب) مات بالمدينة','1','9',NULL,NULL,'0');
INSERT INTO [b435] VALUES (6,'<span data-type="title" id=toc-7>الأتقانى سنة ٧٥٨ هجرية</span>

أمير كاتب العميد ابن أمير غازى قوام الدين المكنى بأبى حنيفة الأتقانى الفارابى نسبته إلى فاراب ناحية وراء ن هجريةر سيحون، كان رأسا فى الحنفية بارعا فى الفقه واللغة العربية كثير الإعجاب بنفسه شديد التعصب على من يخالفه، شرح المنتخب الحسامى وسماه التبيين وشرح الهداية للميرغينانى. وسماه غاية البيان ونادرة الأقران تنقل بين بغداد وتولى التدريس بها وقدم دمشق مرتين وقدم مصر.','1','5',NULL,NULL,'0');
INSERT INTO [b435] VALUES (7,'<span data-type="title" id=toc-8>الأثرم سنة ٢٦١ هجرية</span>

أحمد بن محمد بن هانىء الطائى أو الكلبى الاسكافى أبو بكر الأثرم من حفاظ الحديث. أخذ عن الإمام وآخرين له كتاب فى علل الحديث وآ خر فى السنن.','1','4',NULL,NULL,'0');
INSERT INTO [b435] VALUES (8,'<span data-type="title" id=toc-9>ابن الأثير سنة ٢٦١ هجرية</span>

أبو السعادات مجد الدين المبارك بن محمد بن محمد، محدث لغوى أصولى له النهاية فى غريب الحديث، وجامع الأصول وغيرها.','1','8',NULL,NULL,'0');
INSERT INTO [b435] VALUES (9,'<span data-type="title" id=toc-10>أحمد جودت سنة ١٣١٢ هجرية</span>

أحمد جودت (باشا) بن إسماعيل بن على مؤرخ تركى من الوزراء، له اشتغال بالعربية، ولد وتعلم فى مدينة لوفجه التابعة لولاية الطونة وسكن ألاستانة وبها توفى وتولى نظارة العدلية.','1','3',NULL,NULL,'0');
INSERT INTO [b435] VALUES (10,'<span data-type="title" id=toc-11>الأخفش توفى سنة ٢٩٢ هجرية</span>

هارون بن موسى بن شريك التغلبى أبو عبد الله شيخ القراء بدمشق يعرف بالأخفش الدمشقى أو أخفش باب الجابية (من أحياء دمشق) وكان قيما بالقراءات السبع عارفا بالتفسير والنحو والمعانى والغريب والشعر وعنه اشتهر قراءة أهل الشام','1','12',NULL,NULL,'0');
INSERT INTO [b435] VALUES (11,'<span data-type="title" id=toc-12>الأذرعى سنة ٦٧٧ هجرية</span>

سليمان بن أبى العز بن وهيب بن عطاء الأذرعي شيخ الحنفية فى زمانه من أهل أذرعات قرب دمشق أقام فى دمشق يدرس ويفتى ثم انتقل الى القاهرة تولى قضاء القضاة فى أيام الظاهر بيبرس وعاد إلى دمشق فدرس بالظاهرية وولى القضاء وقبيل وفاته ومات بدمشق وله (الوجيز الجامع لمسائل الجامع) فى فقة الحنفية وغيره.','1','10',NULL,NULL,'0');
INSERT INTO [b435] VALUES (12,'<span data-type="title" id=toc-13>الأردبيلى سنة ٧٩٩ هجرية</span>

جمال الدين يوسف الأردبيلى من فقهاء الشافعية، له كتاب الأنوار لأعمال الأبرار مع حاشيه الكمثرى.','1','11',NULL,NULL,'0');
INSERT INTO [b435] VALUES (13,'<span data-type="title" id=toc-14>ابن أرقم</span>

سليمان بن أرقم أبو البصرى مولى قريظة أو النضير، قدم بغداد، وحدث بها عن الحسن البصرى وغيره وروى عنه على بن حمزة الكسائى وغيره.','1','13',NULL,NULL,'0');
INSERT INTO [b435] VALUES (14,'<span data-type="title" id=toc-15>ابن أرقم توفى سنة ٦٨ هجرية</span>

زيد بن أرقم الخزرجى الأنصارى صحابى غزا مع النبى صلى الله عليه وسلم سبع عشرة غزوة وشهد صفين مع على ومات بالكوفة، روى له البخارى ومسلم سبعين حديثا.','1','14',NULL,NULL,'0');
INSERT INTO [b435] VALUES (15,'<span data-type="title" id=toc-16>أسامة بن زيد سنة ٥٤ هجرية</span>

أبو محمد أسامة بن زيد بن حارثة حبيب رسول الله صلى الله عليه وسلم وابن حبيبه. ولد بمكة ونثسأ على الإ سلام لأن أباه كان من أول الناس إسلاما أمَّره رسول الله صلى الله عليه وسلم قبل أن يبلغ العشرين فكان مظفرا مات بالجرف ضاحية المدينة','1','15',NULL,NULL,'0');
INSERT INTO [b435] VALUES (16,'<span data-type="title" id=toc-17>أسد بن الفرات توفى سنة ٢١٤ هجرية</span>

أسد بن الفرات بن سنان مولى بنى سليم أبو عبد الله قاضى القيروان وأحد القادة الفاتحين نشأ بالقيروان ثم بتونس، ورحل إلى الشرق فى طلب الحديث وكان صاحب رأى صنف الأسدية فى الفقه المالكى، وتوفى من جراحات أصابته فى إحدى المعارك.','1','22',NULL,NULL,'0');
INSERT INTO [b435] VALUES (17,'<span data-type="title" id=toc-18>الإسفرايينى سنة ٤٠٦ هجرية</span>

أبو حامد أحمد بن محمد بن أحمد الإسفرايينى من أعلام الشافعية، ولد فى اسفرايين بالقرب من نيسابور ورحل إلى بغداد فتفقه فيها وعظمت مكانته وألف كتبا، منها مطول فى أصول الفقه ومختصر فى الفقه سماه الرونق وتوفى ببغداد.','1','16',NULL,NULL,'0');
INSERT INTO [b435] VALUES (18,'<span data-type="title" id=toc-19>الإسفرايينى سنة ٤١٨ هجرية</span>

أبو اسحق إبراهيم بن محمد بن إبراهيم بن مهران الإسفرايينى الفقيه الشافعى المتكلم الأصولى صاحب أول مدرسة بنيت للفقة والأصول (بنيسابور) وقد بلغ درجة الاجتهاد.','1','17',NULL,NULL,'0');
INSERT INTO [b435] VALUES (19,'<span data-type="title" id=toc-20>الإسفرايينى سنة ٤٢٩ هجرية</span>

عبد القاهر بن طاهر بن محمد بن عبد الله البغدادى التميمى الإسفراييني أبو منصور عالم متفنن من أئمة الأصول كان صدر الإسلام فى عصره ولد ونشا فى بغداد ورحل إلى خراسان واستقر فى نيسابور ومات فى إسفرائين كان يدرس فى سبعة عشر فنا وله تصانيف كثيرة منها التحصيل فى أصول الفقه ومعيار النظر وغيرها.','1','18',NULL,NULL,'0');
INSERT INTO [b435] VALUES (20,'<span data-type="title" id=toc-21>الأسنوى سنة ٧٧٢ هجرية</span>

عبد الرحيم بن الحسن بن على جمال الدين، فقيه أصولى من علماء العربية ولد بإسنا وانتقل إلى القاهرة وبقى بها حتى انتهت إليه رياسة الشافعية له مؤلفات منها الأشباه والنظائر ونهاية السول شرح منهاج الوصول وغيرها.','1','20',NULL,NULL,'0');
INSERT INTO [b435] VALUES (21,'<span data-type="title" id=toc-22>أشهب سنة ٢٠٤ هجرية</span>

أشهب بن عبد العزيز القيسى فقيه مصر ولد سنه ١٤٥ وانتهت إليه رياسة المذهب المالكى بمصر بعد ابن القاسم','1','19',NULL,NULL,'0');
INSERT INTO [b435] VALUES (22,'<span data-type="title" id=toc-23>أصبغ بن الفرج سنة ٢٢٥ هجرية</span>

جده سعيد بن نافع وكان من كبار المالكية بمصر وكان كاتب ابن وهب وله عدة تصانيف.','2','109',NULL,NULL,'0');
INSERT INTO [b435] VALUES (23,'<span data-type="title" id=toc-24>الاصطخرى سنة ٣٢٨</span>

أبو سعيد الحسن بن أحمد بن يزيد فقيه شافعى ولى حبة بغداد واستقضاه المقتدر على سجستان وله كتب منها "أدب القضاء" والفرائض"وغيرها.','1','21',NULL,NULL,'0');
INSERT INTO [b435] VALUES (24,'<span data-type="title" id=toc-25>أطفيش توفى سنة ١٣٣٢ هجرية</span>

محمد ين يوسف بن عيسى أطفيش الحفصى العدوى إباضى المذهب مجتهد كان له أثر بارز فى قضية بلاده السياسية مولده ووفاته فى بلدة يسجن من وأدى ميزا ب فى الجزائر له أكثر من من ثلاثمائة مؤلف شامل الأصل والفرع في علوم الشريعة وشرح النيل فى عشرة أجزاء كبيرة فى الفقه الإباضى.','1','23',NULL,NULL,'0');
INSERT INTO [b435] VALUES (25,'<span data-type="title" id=toc-26>إمام الحرمين سنة ٤٧٨ هجرية</span>

عبد الملك بن محمد الله بن يوسف بن محمد الجوينى إمام الحرمين من أصحاب الشافعى ولد فى جوين من نواحى نيسابور ورحل إلى بغداد فمكة والمدينة وجمع طرق المذاهب ثم عاد الى نيسابور واشتغل بالتدريس بها وله مصنفات كثيرة من أشهرها (البرهان) فى أصول الفقه وتوفى بنيسابور.','1','24',NULL,NULL,'0');
INSERT INTO [b435] VALUES (26,'<span data-type="title" id=toc-27>الأمير توفى سنة ١٢٣٢ هجرية</span>

محمد بن محمد بن أحمد السنباوى الأزهر من فقهاء المالكية وعلماء اللغة العربية ولد فى ناحية سنبو بمصر وتعلم فى الأزهر وتوفى بالقاهرة وكانت لجده أحمد إمرة بالصعيد فلقب بالأمير أكثر كتبه حواش وشروح ومنها الإكليل شرح مختصر خليل، وحاشية على شرح الزرقانى على الغمرية فقه أيضا.','1','25',NULL,NULL,'0');
INSERT INTO [b435] VALUES (27,'<span data-type="title" id=toc-28>أنس بن مالك سنة ٩٣ هجرية</span>

جده النضر بن ضمضم النجارى الخزرجى- خادم رسول الله صلى الله عليه وسلم ولد بالمدينة وتوفى بالبصرة وهو من الستة المكثرين للحديث من الصحابة وهو آخر من مات من الصحابة " با البصرة.','1','26',NULL,NULL,'0');
INSERT INTO [b435] VALUES (28,'<span data-type="title" id=toc-29>ابن إدريس</span>

فقيه إمامى من شيوخه محمد بن محمد بن النعمان الذى تلقى عن شيخه أبى جعفر الصدوق، بن كتبه " السرائر " فى الفقه.','1','27',NULL,NULL,'0');
INSERT INTO [b435] VALUES (29,'<span data-type="title" id=toc-30>ابن إدريس</span>

زكريا بن إدريس بن عبد الله أبو جرير القمى يروى عن الأئمة الصادق والكاظم والرضا، له كتاب رواه عنه أحمد بن محمد بن خالد عن أبيه وروى عنه " كثيرون','1','31',NULL,NULL,'0');
INSERT INTO [b435] VALUES (30,'<span data-type="title" id=toc-31>الأزهرى سنة ٣٧٠ هجرية</span>

أبو منصور بن أحمد بن طلحة الأزهرى بن نوح بن أزهر الهودى اللغوى صاحب التهذيب فى اللغة شافعى المذهب توفى بمدينة هراه.','1','28',NULL,NULL,'0');
INSERT INTO [b435] VALUES (31,'<span data-type="title" id=toc-32>إسحاق بن إبراهيم سنة ٢٧٥ هجرية</span>

إسحاق بن إبراهيم بن هانىء النيسابورى أبو يعقوب. خدم الإمام أحمد، وذكره أبو بكر الخلال فقال أخا دين وورع ونقل عن الإمام أحمد مسائل كثيرة مات ببغداد.','1','29',NULL,NULL,'0');
INSERT INTO [b435] VALUES (32,'<span data-type="title" id=toc-33>إسماعيل بن سعد</span>

إسماعيل بن سعيد الشالنجى أبو إسحاق قال عنه أبو بكر الخلال ما احتسب أحدا من أصحاب أبى عبد الله روى عنه أحسن مما روى ولا أكثر مسائل منه، وكان عالما بالرأى وله كتاب ترجمه بالبيان على ترتيب الفقهاء.','1','30',NULL,NULL,'0');
INSERT INTO [b435] VALUES (33,'<span data-type="title" id=toc-34>الأصم سنة ٦٣١ هجرية</span>

عثمان بن أبى عبد الله بن أحمد أبو عبد الله قاض من فقهاء الإباضية بعمان له تصانيف منها " التاج البصيرة النور " ولم يكن بأصم وإنما لقب بذلك لقصة ذكرت فى اللباب لابن الأثير ج ١ ص ٥٧.','1','32',NULL,NULL,'0');
INSERT INTO [b435] VALUES (34,'<span data-type="title" id=toc-35>الأصم سنة ٣٤٦ هجرية</span>

محمد بن يعقوب بن يوسف بن معقل بن سنان الأموى بالولاء أبو العباس محدث من أهل نيسايور وتوفى بها. أخذ عن رجال الحديث بمكة ومصر ودمشق وغيرها فى رحلة واسعة. وأصيب بالصمم بعد إيابه حدث ستا وسبعين سنة. كان ثقة أمينا.','1','33',NULL,NULL,'0');
INSERT INTO [b435] VALUES (35,'<span data-type="title" id=toc-36>إياس بن معاوية سنة ١٢٢ هجرية</span>

إياس بن معاوية بن قرة المزنى أبو واثلة قاضى البصرة يضرب به المثل فى الفطنة والذكاء قال عنه الجاحظ من مفاخر مضر ومن مقدمى القضاة وللمدائنى كتاب سماه ركن إياس توفى بواسط','1','35',NULL,NULL,'0');
INSERT INTO [b435] VALUES (36,'<span data-type="title" id=toc-37>الآجرى توفى سنة ٣٦٠ هجرية</span>.

محمد بن الحسين بن عبد الله أبو بكر الآجرى، فقيه شافعى محدث، نسبته إلى آجر من قرى بغداد، ولد فيها وحدث ببغداد ثم انتقل إلى مكة فتنسك وتوفى بها، له تصانيف كثيرة منها كتاب الشريعة وأخلاق العلماء، وكتاب الأربعين حديثا.','2','111',NULL,NULL,'0');
INSERT INTO [b435] VALUES (37,'<span data-type="title" id=toc-38>الأجهورى توفى سنة ٩٥٧ هجرية</span>.

أبو زيد عبد الرحمن بن على الأجهورى الفقيه الزاهد، بقية السلف، أثنى عليه الشعرانى فى طبقاته، تخرج به جماعة من الفضلاء نحو المائة، له حاشية على مختصر خليل، توفى فى صفر.','2','110',NULL,NULL,'0');
INSERT INTO [b435] VALUES (38,'<span data-type="title" id=toc-39>الأجهورى توفى سنة ١٠٦٦ هجرية</span>.

أبو الإرشاد نور الدين على بن زين العابدين بن محمد بن الشيخ عبد الرحمن الأجهورى، شيخ المالكية فى عصره، أخذ عن كثيرالأعلام ممن أخذوا عن جده، وألف تآليف كثيرة منها ثلاثة شروح
على مختصر خليل كبير ووسط وصغير، وحاشية على شرح التتائى على الرسالة وتآليف فى الحديث والمنطق والنحو، توفى فى جمادى الأولى.','2','114',NULL,NULL,'0');
INSERT INTO [b435] VALUES (39,'<span data-type="title" id=toc-40>الأجهورى توفى سنة ١١٩٨ هجرية</span>

أبوزيد عبد الرحمن بن حسين بن عمر الأجهورى، أديب متقن للعربية والأصول والقراءات، درس بالأزهر، ومن مؤلفاته، الملتاذ فى الأربعة الشواذ، له تعليقات على الجامع الصغير. و','2','115',NULL,NULL,'0');
INSERT INTO [b435] VALUES (40,'<span data-type="title" id=toc-41>أبو إسحاق المروزى توفى سنة ٣٤٠ هجرية</span>.

إبراهيم بن أحمد المروزى أبوإسحاق فقيه انتهت إليه رياسة الشافعية بالعراق بعد ابن شريح، ولد " بمرو الشاهجان " وأقام ببغداد أكثر أيامه توفى بمصر، له تصانيف منها شرح مختصر المزنى.','2','116',NULL,NULL,'0');
INSERT INTO [b435] VALUES (41,'<span data-type="title" id=toc-42>أسماء" بنت أبى بكر" توفيت سنة ٧٣ هجرية</span>

أسماء بنت أبى بكر الصديق، عبد الله بن أبى قحافة، من قريش، صحابية من الفضليات، آخر المهاجرين والمهاجرات وفاة، وهى أخت أم المؤمنين عائشة لأبيها، وأم عبد الله بن الزبير توفيت بمكة، شهدت اليرموك،وكانت فصيحة تقول الشعر، وسميت " ذات النطاقين" لأنها صنعت للنبى صلى الله عليه وسلم طعاما حين هاجر للمدينة وشقت نطاقها وشدت به الطعام.','2','117',NULL,NULL,'0');
INSERT INTO [b435] VALUES (42,'<span data-type="title" id=toc-43>أسماء بنت عميس توفيت سنة ٤٠ هجرية</span>.

أسماء بنت عميس بن معد بن تيم بن الحارث الخثعمى، صحابية، كان لها شأن أسلمت قبل دخول النبى صلى الله عليه وسلم دار الأرقم بمكة، وهاجرت إلى أرض الحبشة، وقتل زوجها جعفر بن أبى طالب فتزوجها أبو بكر الصديق وصفها أبو نعيم بمهاجره البحرين ومصلية القبلتين.','2','118',NULL,NULL,'0');
INSERT INTO [b435] VALUES (43,'<span data-type="title" id=toc-44>إسماعيل بن محمد بن وقاص أبى توفى سنة١٣٤ هجرية</span>.

إسماعيل بن محمد بن سعد بن أبى وقاص الزهرى، روى عن أبيه وعميه عامر ومصعب، وأنس، وروى عنه صالح بن كيسان والزهرى ومالك وابن عيينة، وثقه ابن معين والعجلى وأبو حاتم والنسائى.','2','119',NULL,NULL,'0');
INSERT INTO [b435] VALUES (44,'<span data-type="title" id=toc-45>الأصفهانى توفى سنة ٣٢٢ هجرية</span>.

محمد بن بحر الأصفهانى، أبو مسلم وال، من أهل أصفهان، معتزلى من كبار الكتاب، كان عالما بالتفسير وبغيره من صنوف العلم، من كتبه جامع التأويل فى التفسير أربعة عشر مجلدا ومجموع رسائله.','1','36',NULL,NULL,'0');
INSERT INTO [b435] VALUES (45,'<span data-type="title" id=toc-46>الأصفهانى توفى سنة ٦٨٨ هجرية</span>.

محمد بن محمود بن عياد السلمانى أو عبد الله شمس الدين الأصفهانى، قاض من فقهاء الشافعية بأصبهان، ولد وتعلم بها، توجه إلى مصر وولى قضاء قوص فقضاء الكرك، واستقر أخيرا فى القاهرة مدرسا، توفى بها، له كتب، منها: شرح المحصول فى أصول الفقه، والقواعد فى الأصول والدين، والمنطق والجدل.','1','43',NULL,NULL,'0');
INSERT INTO [b435] VALUES (46,'<span data-type="title" id=toc-47>الأصمعى توفى سنة٢١٦ هجرية</span>.

عبد الملك بن قريب بن على بن أصمع الباهلى أبو سعيد الأصمعى، راوية العرب وأحد أئمة العلم باللغة والشعر والبلدان، مولده ووفاته بالبصرة، له تصانيف كثيرة فى اللغة والشعر من الأصمعيات.','1','37',NULL,NULL,'0');
INSERT INTO [b435] VALUES (47,'<span data-type="title" id=toc-48>الأعرج توفى سنة١١٧ هجرية</span>.

عبد الرحمن بن هرمز أبو داود، عرف بالأعرج حافظ قارئ، من أهل المدينة، أول من برز فى القرآن والسنن، رابط بثغر الإسكندرية مدة ومات بها.','1','38',NULL,NULL,'0');
INSERT INTO [b435] VALUES (48,'<span data-type="title" id=toc-49>الأعرج توفى سنة٧٢٨ هجرية</span>.

الحسن بن محمد بن الحسين الخراسانى نظام الدين المعروف بالأعرج، فاضل مفسرمن أهل نيسابور، من كتبه ثلاثة تفاسير للقرآن الكريم وتعبيرالتحرير شرح لتحرير المجسطى للطوسى.','1','39',NULL,NULL,'0');
INSERT INTO [b435] VALUES (49,'<span data-type="title" id=toc-50>الأقفهسى توفى سنة ٨٢٣ هجرية</span>.

القاضى الفاضل جمال الدين عبد الله بن مقداد الأقفهسى، الفقيه العالم الإمام، انتهت إليه رياسة المذهب المالكى والفتوى بمصر، أخذ عن خليل وانتفع به وبغيره، له شرح على مختصر شيخه فى ثلاثة مجلدات، وشرح على الرسالة وتفسير، توفى فى رمضان.','1','41',NULL,NULL,'0');
INSERT INTO [b435] VALUES (50,'<span data-type="title" id=toc-51>الألوسى توفى سنة ١٢٧٠ هجرية</span>.

مفتى بغداد شهاب الدين السيد محمود الألوسى البغدادى، له كتاب فى التفسير من ثلاثين جزءا "روح المعانى "','1','44',NULL,NULL,'0');
INSERT INTO [b435] VALUES (51,'<span data-type="title" id=toc-52>ابن أمير حاج توفى سنة ٨٧٩ هجرية</span>.

محمد بن محمد بن محمد المعروف بابن أمير حاج، ويقال ابن الموقت أبو عبد الله شمس الدين، فقيه من علماء الحنفية من أهل حلب من كتبه التقرير والتحبير فى شرح التحرير للكمال بن الهمام فى أصول الفقه.','1','40',NULL,NULL,'0');
INSERT INTO [b435] VALUES (52,'<span data-type="title" id=toc-53>أبو أيوب توفى سنة ٥٢ هجرية</span>.

خالد بن زيد بن كليب بن ثعلبة أبو أيوب الأنصارى من بنى النجار، صحابى شهد العقبة وبدرا وأحدا وسائر المشاهد، عاش إلى أيام بنى أمية وكان يسكن المدينة ورحل إلى الشام روى له البخارى ومسلم.','1','42',NULL,NULL,'0');
INSERT INTO [b435] VALUES (53,'<span data-type="title" id=toc-55>الباترتى توفى سنة ٧٨٦ هجرية</span>

محمد بن محمد بن محمود أكمل الدين علامة بفقة الحنفية وعارف بالأدب نسبته إلى بابرت من أعمال دجيل ببغداد، رحل إلى حلب ثم إلى القاهرة توفى بمصر من كتبه العناية فى شرح الهداية،: التقرير على أصول البزدوى وشرح المنار، وشرح مختصرة ابن الحاجب.','1','45',NULL,NULL,'0');
INSERT INTO [b435] VALUES (54,'<span data-type="title" id=toc-56>الباجورى سنة ١٢٧٧ هجرية</span>

إبراهيم بن محمد بن أحمد الباجورى شيخ الجامع الأزهر من فقهاء الشافعية، ولد ونشأ فى الباجور وتعلم فى الأزهر وولى مشيخته وله كتب وحواش كثيرة منها التحفة الخيرية وحاشيته على الشنشورية فى الفرائض وغيرها.','1','46',NULL,NULL,'0');
INSERT INTO [b435] VALUES (55,'<span data-type="title" id=toc-57>الباجى سنة ٤٧٤ هجرية</span>

سليمان بن خلف بن سعد التجيبى القرطبى فقيه مالكى كبير من رجال الحديث أصله من بطليوس ومولده فى باجة بالأندلس ترحل فى بعض البلاد وعاد إلى الأندلس تولي القضاء فى بعض أنحائها وتوفى بالمرية ومن كتبه " السراج فى علم الحجاج) " وأحكام الأصول وغيرها.','1','47',NULL,NULL,'0');
INSERT INTO [b435] VALUES (56,'<span data-type="title" id=toc-58>الباقلانى سنة ٤٠٣ هجرية</span>

محمد بن الطيب بن محمد بن جعفر ابو بكر قاض من كبار علماء الكلام انتهت اليه الرياسة فى مذهب الأشاعرة، كان جيد الاستنباط سريع الجواب من كتبه إعجاز القرآن والإنصاف وغيره.','1','48',NULL,NULL,'0');
INSERT INTO [b435] VALUES (57,'<span data-type="title" id=toc-59>البجيرمى سنة ١٢٢١ هجرية</span>

سليمان بن محمد بن عمر البجيرمى فقيه مصرى ولد فى بجيرمة وتعلم فى الأزهر ودرس وكف بصره، له التجريد حاشية على شرح المنهج فى فقه الشافعية وله حاشية على شرح الخطيب المسمى بالإقناع.','1','50',NULL,NULL,'0');
INSERT INTO [b435] VALUES (58,'<span data-type="title" id=toc-60>البخارى سنة ٢٥٦ هجرية</span>

أبو عبد الله محمد بن إسماعيل بن إبراهيم بن المغيرة ولد سنة ١٤٩ هجرية ودرس القرآن والحديث منذ حياته له رحلات واسعة فى أقطار الإسلام بحثا فى الحديث حتى صار إماما ومرجعا فيه.','1','51',NULL,NULL,'0');
INSERT INTO [b435] VALUES (59,'<span data-type="title" id=toc-61>البخارى سنة ٧٣٠ هجرية</span>

عبد العزيز بن أحمد بن محمد علاء الدين البخارى فقيه حنفى من علماء الأصول له تصانيف منها شرح أصول البزدوى وشرح المنتخب الحسامى','1','49',NULL,NULL,'0');
INSERT INTO [b435] VALUES (60,'<span data-type="title" id=toc-62>برهان الدين سنة ٦١٦ هجرية</span>

برهان الدين محمود بن أحمد من فقهاء الحنفية له الميحط البرهانى وغيره','1','52',NULL,NULL,'0');
INSERT INTO [b435] VALUES (61,'<span data-type="title" id=toc-63>البراذعى سنة ٤٠٠ هجرية</span>

(ويقال ابن البراذعى) هو خلف بن أبى القاسم الأزدى فقيه من فقهاء المالكية ولد وتعلم فى القيروان وانتقل إلى صقلية فاتصل بأميرها وصنف عنده كتبا منها التهذيب فى أختصار المدونة وتمهيد مسائل المدونة واختصار الواضحة ثم رحل أصبهان فكان به، حتى توفى','1','53',NULL,NULL,'0');
INSERT INTO [b435] VALUES (62,'<span data-type="title" id=toc-64>بروع بنت واشق</span>

هى بروع بنت وأشق الكلابية، وكانت امرأة هلال بن مرة، وهى صحابية','1','54',NULL,NULL,'0');
INSERT INTO [b435] VALUES (63,'<span data-type="title" id=toc-65>بريرة</span>

من موالى الصحابة، وهى مولاة السيدة عائشة ومعتقتها','1','55',NULL,NULL,'0');
INSERT INTO [b435] VALUES (64,'<span data-type="title" id=toc-66>البزدوى ٤٨٢ هجرية</span>

على بن محمد بن الحسين بن عبد الكريم أبو الحسن فخرالاسلام البزدوى فقيه أصولى من أكابر الحنفية من سكان سمرقند نسبته إلى بزدة قلعة بقرب نسف له تصانيف منها المبسوط ’ وكنز الوصول فى أصول الفقه يعرف بأصول البزدوى.','1','56',NULL,NULL,'0');
INSERT INTO [b435] VALUES (65,'<span data-type="title" id=toc-67>البغدادى توفى سنة ٤٦٣ هجرية</span>

الخطيب البغدادى أحمد بن على بن ثابت أبو بكر ولد فى غزية بين الكوفة ومكة نشأ ومات ببغداد له" مؤلفات كثيرة منها تاريخ و (الفقيه والمتفقة)','1','59',NULL,NULL,'0');
INSERT INTO [b435] VALUES (66,'<span data-type="title" id=toc-68>أبو بكر الصديق ١٣ هجرية</span>

عبد الله بن أبى قحافة عثمان بن عامر التيمى القرشى أول الخلفاءالراشدين وأول من آمن برسول الله صلى الله علبه وسلم من الرجال ولد بمكة ونشأ سيد من سادات قريش وغنيا من كبار موسريهم وعالما بأنساب القبائل وأخبارها وسياستها، وكانت العرب تلقبه بعالم قريش شهد الغزوات كلها وبذل الأموال فى سبيل الله وبويع بالخلافة يوم وفاة رسول الله صلي الله عليه وسلم- سنة ١١ هجرية','1','57',NULL,NULL,'0');
INSERT INTO [b435] VALUES (67,'<span data-type="title" id=toc-69>أبو بكر اليمنى ٨٠٠ هجرية</span>

أبو بكر بن على بن محمد الحوازى اليمنى فقية حنفى شرح مختصر القدورى بكتاب أسماه الجوهرة النيرة وله شرح آخر على مختصر القدورى اسمه السراج الوهابى مخطوط بمكتبة','1','58',NULL,NULL,'0');
INSERT INTO [b435] VALUES (68,'<span data-type="title" id=toc-70>البيضاوى سنة ٦٨٥ هجرية</span>

أبو الخير نصر الدين البيضاوى عبد الله بن عمر، قاض مفسر ولد فى البيضاء قرب شيراز بفارس وولى قضاء شيراز ثم صرف عن القضاء فرحل الى تبريز، وبها توفى من تصانيفه التفسير المشهور أنوار التنزيل، وطلائع الأنوار فى التوحيد، ومنهاج الوصول فى الأصول وغيرها.','1','60',NULL,NULL,'0');
INSERT INTO [b435] VALUES (69,'<span data-type="title" id=toc-71>البيهقي سنة ٤٥٨ هجرية</span>

أحمد بن الحسين بن على أبو بكر من ائمة الحديث نشأ فى بيهق ورحل إلى بغداد ثم إلى الكوفة ومكة وغيرهما وطلب إلى نيسابور فلم يزل بها إلى أن مات ونقل جثمانه إلى بلده، له كتب كثيرة فى نصرة مذهب الشافعى وبسط موجزه وتأييد آرائه، صنف كتبا كثيرة منها السنن الصغرى. والأسماء والصفات وغيرها.','1','61',NULL,NULL,'0');
INSERT INTO [b435] VALUES (70,'<span data-type="title" id=toc-72>الباب الشامى</span>

عمر بن عبد الله بن موسى أبو حفص إمام كبير من متقدمى أصحاب الشافعى ومن أئمة أصحاب الوجوه. فقيه جليل ومن كبار المحدثين والرواة وأعيان النقلة. نسب إلى باب الشام وهى إحدى المحال الأربعة المشهورة القديمة بالجانب الغربى من بغداد.','1','62',NULL,NULL,'0');
INSERT INTO [b435] VALUES (71,'<span data-type="title" id=toc-73>البدخشى</span>

محمد بن الحسن البدخشى له كتاب مناهج العقول فى الأصول شرح به منهاج البيضاوى.','1','63',NULL,NULL,'0');
INSERT INTO [b435] VALUES (72,'<span data-type="title" id=toc-74>البراء</span>

أبو عمارة البراء بن عازب بن الحارث بن عدى بن مجدعة بن حارثة الأنصارى الأوسى أبوه عازب صحابى. روى للبراء عن النبى صلى الله عليه وسلم ثلثمائة حديث وخمسة أحاديث اتفق البخارى ومسلم ومنها على اثنين وعشرين، وروى عنه بعض من الصحابة وجماعة من التابعين نزل الكوفة. وتوفى بها شهد أحدا.','1','64',NULL,NULL,'0');
INSERT INTO [b435] VALUES (73,'<span data-type="title" id=toc-75>أبو بردة</span>

عامر بن أبى موسى عبد الله بن قيس الأشعرى قاضى الكوفة.','1','65',NULL,NULL,'0');
INSERT INTO [b435] VALUES (74,'<span data-type="title" id=toc-76>بسرة " بنت غزوان" القرن الأول</span>

بسرة بنت غزوان المازنى أخت عتبة بن غزوان الصحابى المشهور أمير البصرة جاء فى الحلية بسند صحيح أن أبا هريرة كان يعمل عندها أجيرا بطعام بطنه على عهد النبى صلى الله علية وسلم ثم تزوجهما بعد ذلك لما كان مروان يستخلفه فى إمرة المدينة.','1','66',NULL,NULL,'0');
INSERT INTO [b435] VALUES (75,'<span data-type="title" id=toc-77>ابن بطال سنة ٤٠٤ هجرية</span>

سليمان بن محمد بن بطال البطليوسى فقيه باحث تعلم بقرطبة واشتهر بكتابه المقنع فى أصول الأحكام الذى قيل فيه أنه لا يستغنى عنه الحكام.','1','67',NULL,NULL,'0');
INSERT INTO [b435] VALUES (76,'<span data-type="title" id=toc-78>ابن بطال سنة ٤٤٩ هجرية</span>

على بن خلف ين عبد الملك بن بطال عالم بالحديث من أهل قرطبة له شرح البخارى.','1','68',NULL,NULL,'0');
INSERT INTO [b435] VALUES (77,'<span data-type="title" id=toc-79>البغورى سنة ٥١٠ هجرية</span>

الحسين بن مسعود بن محمد الفراء البغوى فقيه محدث مفسر ينسب إلى بغاه من قرى خراسان وله التهذيب فى فقه الشافعية وشرح السنة فى الحديث وسالم التفسير وتوفى بمرو الروز.','1','69',NULL,NULL,'0');
INSERT INTO [b435] VALUES (78,'<span data-type="title" id=toc-80>أبو بكر سنة ٢٧٥ هجرية</span>

أحمد بن محمد بن الحجاج بن عبد العزيز أبو بكر المروذى فقيه حنبلى من المقدمين من أصحاب أحمد ورعا فاضلا يأنس إليه ابن حنبل. وهو الذى تولى إغماض ابن حنبل لما مات وغسله. روى عنه مسائل كثيرة.','1','77',NULL,NULL,'0');
INSERT INTO [b435] VALUES (79,'<span data-type="title" id=toc-81>أبو بكر (القرن الثالث)</span>

أحمد بن محمد بن خالد بن شيرزاد أبو بكر المعروف بالنورانى فقيه حنبلى قاضى تكريت حدث عن أبى عمار المروزى ومحمد بن سليمان وغيرهما وكان من الأصحاب.','1','70',NULL,NULL,'0');
INSERT INTO [b435] VALUES (80,'<span data-type="title" id=toc-82>أبو بكر سنة ٢٧٩ هجرية</span>

أحمد بن خيثمة زهير بن حرب بن شداد أبو بكر فقيه حنبلى اخذ علم الحديث عن الإمام أحمد ويحيى بن معين. وسمع من منصور بن مسلمة الخزاعى وغيره وكان ثقة عالما راوية للأدب وبصيرا بأيام الناس. روى عنه خلق كثير وله كتاب التاريخ.','1','71',NULL,NULL,'0');
INSERT INTO [b435] VALUES (81,'<span data-type="title" id=toc-83>أبو بكر سنة ٢٩٣ هجرية</span>

أحمد بن محمد بن عبد الله بن صدقة أبو بكر من فقهاء الحنابلة نقل عن الإمام مسائل وأشياء كثيرة.','1','72',NULL,NULL,'0');
INSERT INTO [b435] VALUES (82,'<span data-type="title" id=toc-84>أبو بكر بن حزم سنة ١٢٠ هجرية</span>

أبوبكر محمد ين عمرو ين حزم أنصارى مدنى من تابعى التابعين تولى القضاء الإمرة والموسم فى زمن سليمان بن عبد الملك وعمر بن عبد العزيز وكان محدثا ثقة توفى بالمدينة.','1','74',NULL,NULL,'0');
INSERT INTO [b435] VALUES (83,'<span data-type="title" id=toc-85>أبو بكر</span>

أبو بكر عنبر الخراسانى فقيه حنبلى سكن بغداد وحدث عن الإمام أحمد بإشبانة.','1','73',NULL,NULL,'0');
INSERT INTO [b435] VALUES (84,'<span data-type="title" id=toc-86>البلقينى سنة ٨٦٨ هجرية</span>

صالح بن عمر بن رسلان البلقينى شيخ الإسلام قاض من العلماء بالحديث والفقه مصرى تفقه بأخيه عبد الرحمن بالقاهرة وناب عنه فى الحكم ثم تصدر للافتاء والتدريس بعد موته ولى قضاء الديار المصرية وعزل وأعيد ست مرات. وتوفى وهو على القضاء. من كتبه ديوان خطب ست مجلدات. وتتمة التدريب أكمل بة كتاب أبيه. وغير ذلك توفى بالقاهرة.','1','76',NULL,NULL,'0');
INSERT INTO [b435] VALUES (85,'<span data-type="title" id=toc-87>البلقينى سنة ٨٠٥ هجرية</span>

عمر بن رسلان بن نصير بن صالح الكنانى العسقلانى الأصل ثم البرقينى المصرى الشافعى أبو جعفر مجتهد حافظ للحديث من العلماء بالدين ولد فى بلقينه وتعلم بالقاهرة وولى قضاء الشام وتوفى بالقاهرة من كتبه " التدريب، فى فقه الشافعية، وتصحيح المنهاج، وحواش على الروضة وغيرها.','1','78',NULL,NULL,'0');
INSERT INTO [b435] VALUES (86,'<span data-type="title" id=toc-88>البنانى سنة ١١٩٨ هجرية</span>

عبد الرحمن بن جاد الله المغربى فقيه أصولى قدم مصر وجاور بالأزهر له حاشية على شرح المحلى فى أصول الفقه جزءان.','1','75',NULL,NULL,'0');
INSERT INTO [b435] VALUES (87,'<span data-type="title" id=toc-89>البنانى سنة ١٢٤٥ هجرية</span>

محمد بن محمد بن محمد الغربى بن عبد السلام البنانى النفذى المغربى المكى مفتى المالكية بمكة أصله من فاس سكن مكة وتوفى فيها له تصانيف منها شرح البخارى.','1','79',NULL,NULL,'0');
INSERT INTO [b435] VALUES (88,'<span data-type="title" id=toc-90>ابن بيرى سنة ١٠٩٩ هجرية</span>

إبراهيم بن حسين بن أحمد بن بيرى، كان فقيها مفتيا بمكة، له حواش وشروح فى الفقه والحديث، وله فى الأصول " غاية التحقيق فى عدم جواز التلفيق، فى التقليد وله كتاب فى العمرة وجمرة العقبة.','1','80',NULL,NULL,'0');
INSERT INTO [b435] VALUES (89,'<span data-type="title" id=toc-91>ابن برهان توفى سنة٤٥٦ هجرية</span>.

عبد الواحد بن على بن عمر بن إسحاق بن إبراهيم ابن برهان. (بفتح الباء) وهو النحوى صاحب العربية واللغة والتواريخ وأيام العرب، كان حنبليا فتحول حنفيا، من كتبه: الاختيار فى الفقه.','1','81',NULL,NULL,'0');
INSERT INTO [b435] VALUES (90,'<span data-type="title" id=toc-92>ابن برهان توفى سنة ٥١٨ هجرية</span>.

أحمد بن على بن برهان (بفتح الباء) أبو الفتح، فقيه بغداد، غلب عليه علم الأصول، من تصانيفه البسيط والوسيط والوجيز فى الفقه والأصوال مولده ووفاته ببغداد.','1','82',NULL,NULL,'0');
INSERT INTO [b435] VALUES (91,'<span data-type="title" id=toc-93>بريدة توفى سنة ٦٣ هجرية</span>.

بريدة بن الحصيب الأسلمى، أسلم حين مر به النبى صلى الله عليه وسلم مهاجرا بالضميم- مكان فى حدود مكة- وقيل بعد بدر، وسكن البصرة لما فتحت، وفى الصحيحين عنه أنه غزا مع النبى ست عشرة غزوة، مات فى خلافة يزيد بن معاوية، قال ابن سعد سنة ٦٣ هجرية.','1','91',NULL,NULL,'0');
INSERT INTO [b435] VALUES (92,'<span data-type="title" id=toc-94>البزار توفى سنة ٢٩٢ هجرية</span>

أحمد بن عمرو بن عبد الخالق أبو بكر البزار، حافظ من العلماء بالحديث من أهل البصرة، حدث فى آخر عمره بأصبهان وبغداد والشام، وتوفى بالرملة له مسندان أحدهما البحر الزاخر.','1','83',NULL,NULL,'0');
INSERT INTO [b435] VALUES (93,'<span data-type="title" id=toc-95>أبو بصير</span>.

عتبة بن أسيد (بفتح الهمزة وكسر السين) من بنى ثقيف، صحابى، مشهور بكنيته، توفى فى زمن رسول الله صلى الله عليه وسلم، بعد صلح الحديبية وقبل الفتح وله خبر شهير يتعلق بتطبيق نصوص صلح الحديبية.','1','84',NULL,NULL,'0');
INSERT INTO [b435] VALUES (94,'<span data-type="title" id=toc-96>بلال توفى سنة ٢٠ هجرية</span>.

بلال بن رباح الحبشى أبو عبد الله، مؤذن رسول الله صلى الله عليه وسلم وخازنه على بيت ماله، وأحد السابقين للإسلام، شهد المشاهد مع رسول الله، ولما توفى رسول الله أذن بلال، ولم يؤذن بعد ذلك، توفى بدمشق، روى له البخارى ومسلم.','1','85',NULL,NULL,'0');
INSERT INTO [b435] VALUES (95,'<span data-type="title" id=toc-97>ابن البناء توفى سنة ٧٢١ هجرية</span>.

أحمد بن محمد بن عثمان الأزدى العدوى، أبو العباس، رياضى باحث من أهل مراكش مولدا ووفاة كان أبوه بناء، ونشأ هو منصرفا إلى العلم فنبغ فى علوم شتى، له حاشية على الكشاف ومنتهى السول فى علم الأصول.','1','89',NULL,NULL,'0');
INSERT INTO [b435] VALUES (96,'<span data-type="title" id=toc-99>الترمذى توفى سنة ٢٩٥ هجرية</span>

أبو عيسى محمد بن عيسى الترمذى، سمع من البخاري وغيره حتى صار اماما محدثا حجة، وألف كتابه السنن، وكتاب العلل وتوفى بترمذ مدينة على طرف نهر جيحون.','1','86',NULL,NULL,'0');
INSERT INTO [b435] VALUES (97,'<span data-type="title" id=toc-100>التلمسانى سنة ٧٧١ هجرية</span>

"الشريف " أبو عبد الله محمد بن احمد بن على الادريسى باحث من أعلام المالكية "انتهت إليه رياستهم بالمغرب كان من قرية تسمى القلوين من أعمال تلمسان ونشا بتلمسان ورحل عنها فى ظروف مختلفة ثم عاد إليها منها وبقيت له فيها مدرسة صار يدرس فيها الى أن توفى، ومن كتبه المفتاح فى أصول الفقه وشرح (جمل الخونجى) .','1','87',NULL,NULL,'0');
INSERT INTO [b435] VALUES (98,'<span data-type="title" id=toc-101>التونسى سنة ٧٦٣ هجرية</span>

محمد بن محمد بن أبى القاسم بن جميل الربعى التونسى من فضلاء المالكية تونسى لأصول واستقر بمصر.','1','98',NULL,NULL,'0');
INSERT INTO [b435] VALUES (99,'<span data-type="title" id=toc-102>ابن تيمية توفى سنة ٧٢٨ هجرية</span>

أحمد بن عبد الحليم بن عبد السلام بن عبد الله النميرى الحرانى الدمشقى الحنبلى أبو العباسى تقى الدين تيمية الامام شيخ الاسلام ولد فى حران وتحول به أبوه الى دمشق فنبغ واشتهر وطلب إلى مصر فقصدها ثم عاد إلى دمشق ومات معتقلا فى قلعتها وله مؤلفات كثيرة جدا منها (الفتاوى) (وكتاب الإيمان) (ومنهاج ال سنة) .','1','88',NULL,NULL,'0');
INSERT INTO [b435] VALUES (100,'<span data-type="title" id=toc-103>ابن تيمية (مجدالدين) سنة ٦٥٢ هجرية</span>

مجد الدين عبد السلام بن عبد الله بن الخضر بن تيمية الحرانى فقيه حنبلى. محدث مفسر ولد بحران ورحل إلى بغداد فأقام ست سنين ثم عاد إلى حران: وتوفى بها وكان وحيد زمانه فى معرفة المذهب الحنبلى وله كتب كثيرة.','1','90',NULL,NULL,'0');
INSERT INTO [b435] VALUES (101,'<span data-type="title" id=toc-104>التمرتاشى توفى سنة ١٠٠٤ هجرية</span>.

محمد بن عبد الله بن أحمد الخطيب التمرتاشى الحنفى الغزى له كتاب تنوير الأبصار فى الفقه الحنفى.','1','93',NULL,NULL,'0');
INSERT INTO [b435] VALUES (102,'<span data-type="title" id=toc-105>تميم الدارى توفى سنة٤٠ هجرية</span>.

تميم بن أوس بن خارجة الدارى أبو رقية، صحابى، نسبته إلى الدار ابن هانئ من لخم، أسلم سنة تسع هجرية، وكان يسكن المدينة ثم انتقل إلى الشام بعد مقتل عثمان فنزل بيت المقدس وهو أول من أسرج السراج بالمسجد، روى البخارى ومسلم، وكان عابد أهل فلسطين ومات بها.','1','92',NULL,NULL,'0');
INSERT INTO [b435] VALUES (103,'<span data-type="title" id=toc-106>التميمى توفى سنة ٣٦٣ هجرية</span>.

النعمان بن محمد بن جيون، له مؤلفات منها دعائم الإسلام، وذكر الحلال والحرام فى فقه الشيعة','1','94',NULL,NULL,'0');
INSERT INTO [b435] VALUES (104,'<span data-type="title" id=toc-108>الثعالبى توفى سنة ٨٧٥ هجرية</span>

عبد الرحمن بن محمد بن مخلوف الثعالبى الجزائرى- أبو زيد- مفسر، من كتبه الجواهر الحسان فى تفسير القرأن، وجامع الأمهات فى أحكام العبادات وغيرها.','1','95',NULL,NULL,'0');
INSERT INTO [b435] VALUES (105,'<span data-type="title" id=toc-109>أبو ثعلبة سنة ٧٥ هجرية</span>

أبو ثعلبة الخشنى مشهور بكنيته ولهذا اختلف فى اسمه خرج له الستة أربعين حديثا منها أربعة فى الصحيحين.','1','96',NULL,NULL,'0');
INSERT INTO [b435] VALUES (106,'<span data-type="title" id=toc-110>أبو ثور توفي٢٤٠ هجرية</span>

إبراهيم بن خالد بن أبى اليمان الكلبى البغدادى الفقيه صاحب الشافعى صنف الكتب وفرع على السنن، مات ببغداد شيخا. ومن كتبه اختلاف مالك والشافعى.','1','97',NULL,NULL,'0');
INSERT INTO [b435] VALUES (107,'<span data-type="title" id=toc-111>الثورى سنة ١٦١ هجرية</span>

سفيان بن سعيد بن مسروق الثورى من بنى ثور من أعلام المحدثين نشأ فى الكوفة وراوده المنصور على أن يلى الحكم فأبر وخرج من الكوفة هاربا ثم انتقل إلى البصرة ومات بها مستخفيا وله من الكتب " الجامع الكبير " و" الجامع الصغير " فى الحديث وكتاب فى الفرائض','1','99',NULL,NULL,'0');
INSERT INTO [b435] VALUES (108,'<span data-type="title" id=toc-112>جابر بن عبد الله توفى سنة ٧٨ هجرية</span>

جابر بن عبد الله بن عمرو بن حرام الخزرجى الأنصاري السلمى صحابى من المكثرين فى الرواية عن النبى صلى الله عليه وسلم غزا سبع عشرة غزوة','1','101',NULL,NULL,'0');
INSERT INTO [b435] VALUES (109,'<span data-type="title" id=toc-114>جبير بن مطعم سنة ٥٩ هجرية</span>

جبير بن مطعم بن عدى بن نوفل صحابى جليل كان من علماء قريش وسادتهم توفى بالمدينة وكان عليما بأنساب قريش والعرب قاطبة وله فى البخاري ومسلم ستون حديثا','1','100',NULL,NULL,'0');
INSERT INTO [b435] VALUES (110,'<span data-type="title" id=toc-115>الجرجانى سنة ٨١٦ هجرية</span>

السيد الشريف على بن محمد بن على الجرجانى، من كبار علماء العربية. ولد فى تاكو ودرس فى شيراز، واقام بها إلى أن توفى وله نحو خمسين مصنفا منها (التعريفات) ، وشرح المواقف، وشرح السراجية.','1','102',NULL,NULL,'0');
INSERT INTO [b435] VALUES (111,'<span data-type="title" id=toc-116>ابن جريح توفى سنة ٤٨٩ هجرية</span>

عبد الملك بن عبد العزيز بن جريج، أبو الوليد، وأبو خالد فقيه الحرم المكى كان إمام أهل الحجاز فى عصره وهو أول من صنف التصانيف فى العلم بمكة','1','103',NULL,NULL,'0');
INSERT INTO [b435] VALUES (112,'<span data-type="title" id=toc-117>ابن جرير الطبرى توفى سنة ٣١٠ هجرية</span>

محمد بن جرير بن يزيد الطبرى ابو جعفر الفقيه المفسر المؤرخ ولد فى آمل طبرستان واستوطن بغداد وتوفى بها امتنع عن القضاء وولاية المظالم له جامع البيان فى تفسير القرآن له اختلاف الفقهاء وأخبار الرسل والملوك ويعرف بتاريخ الطبرى.','1','105',NULL,NULL,'0');
INSERT INTO [b435] VALUES (113,'<span data-type="title" id=toc-118>جستنيان ٥٦٥ هجرية</span>

بيتروس ساباتيوس جستينانوس ولد سنة ٤٧٨ م فى بلدة تورزيوم على حدود البانيا من أعمال مقدونيا نشأ وتربى فى كنف عمه جستين الأول ثم أصبح امبراطورا سنة ٥١٨ هجرية شكل عدة لجان لتجميع التراث القانونى الرومانى فجمعت ثم نسبت اليه وعرفت بموسوعة جستنيان.','1','104',NULL,NULL,'0');
INSERT INTO [b435] VALUES (114,'<span data-type="title" id=toc-119>الجصاص توفى سنة ٣٧٠ هجرية</span>

أحمد بن على الرازى أبو بكر الجصاص سكن ببغداد ومات فيها انتهت إليه رئاسة الحنفية إمتنع عن تولى القضاء ألف كتاب أحكام القران وكتابا فى أصول الفقه','1','106',NULL,NULL,'0');
INSERT INTO [b435] VALUES (115,'<span data-type="title" id=toc-120>ابو جعفر الهندوانى</span>

- محمد بن عبد الله بن محمد أبو جعفر الهندوانى البلخى الحنفى لقب بأبى حنيفة الصغير توفى ببخارى','1','107',NULL,NULL,'0');
INSERT INTO [b435] VALUES (116,'<span data-type="title" id=toc-121>جلال الدين المحلى سنة ٨٦٤ هجرية</span>

محمد بن أحمد بن محمد بن إبراهيم المحلى الشافعى أصولي مفسر مولده ووفاته بالقاهرة، من كتبه تفسير الجلالين أتمه الجلال السيوطى وكنز الراغبين فى شرح المنهاج والبدر الطالع فى حل جمع الجوامع وغيره.','1','108',NULL,NULL,'0');
INSERT INTO [b435] VALUES (117,'<span data-type="title" id=toc-122>ابن الجنيد توفى سنة ٣٨١ هجرية</span>

محمد بن أحمد بن الجنيد أبو على فاضل إمامى من أهل الرأى له نحو خمسين كتابا منها تهذيب الشيعة لأحكام الشريعة فى نحو عشرين مجلدا.','1','109',NULL,NULL,'0');
INSERT INTO [b435] VALUES (118,'<span data-type="title" id=toc-123>ابن الجوزى سنة ٥٩٧ هجرية</span>

عبد الرحمن بن على بن محمد الجوزى القرشى البغدادى أبو الفرج علامة عصره فى التاريخ والحديث كثير التصانيف مولده ووفاته ببغداد ونسبته الى (مشرعة الجوز) له نحو ثلاثمائة مصنف منها شذور العقود فى تاريخ العهود والمدهش فى المواعظ وصولة العقل على الهوى والناسخ والمنسوخ.','1','110',NULL,NULL,'0');
INSERT INTO [b435] VALUES (119,'<span data-type="title" id=toc-124>جولد تسهير ١٩٢١م</span>

اجناس كولد صهير مستشرق مجرى موسوى تعلم فى بودابست وبرلين ورحل إلى سوريا كما انتقل إلى فلسطين ومصر ولازم بعض علماء الأزهر عين استاذا فى جامعة بودابست وتوفى بها وله تصانيف كثيرة فى الفقه الإسلامى باللغات الأجنبية.','1','113',NULL,NULL,'0');
INSERT INTO [b435] VALUES (120,'<span data-type="title" id=toc-125>جعفر سنة ٦٧٦ هجرية</span>

جعفر بن يحيى أبو القاسم الحلى أحد أئمة الفقه "الشيعى وله كتاب المعتبر فى شرح المختصر وكتاب نكت النهاية وكتاب المسائل المصرية والمسلك فى أصول الدين والمعارج فى أصول الفقه.','1','111',NULL,NULL,'0');
INSERT INTO [b435] VALUES (121,'<span data-type="title" id=toc-126>جعفر بن أبى طالب سنة ٨ هجرية</span>

جعفر بن أبى طالب بن عبد المطلب، صحابى هاشمى يلقب بالطيار وهو أخو على بن أبى طالب، وكان أسن منه بعشر سنين وكان من السابقين إلى الإسلام وهاجر للحبشة فى الهجرة الثانية وحضر واقعة مؤته بالبلقاء وأستشهد بها.','1','112',NULL,NULL,'0');
INSERT INTO [b435] VALUES (122,'<span data-type="title" id=toc-127>جعفر النسائى القرن الثالث</span>

جعفر بن محمد النسائى الشعرانى. أبو محمد كان عالما رفيع القدر ثقة ورعا قتل بمكة فى شىء من الأمر والنهى من تلامذة الإمام أحمد بن حنبل.','1','114',NULL,NULL,'0');
INSERT INTO [b435] VALUES (123,'<span data-type="title" id=toc-128>أبو جعفر سنة ١١٤ هجرية</span>

محمد بن على زين العابدين بن الحسين الطالبى الهاشمى القرشى أبو جعفر الباقر خامس الأئمة الاثنا عشر عند الإمامية وكان ناسكا عابدا له فى العلم وتفسير القرآ ن أراء وأقوال ولد بالمدينة ودفن بها.','1','115',NULL,NULL,'0');
INSERT INTO [b435] VALUES (124,'<span data-type="title" id=toc-129>جابر بن زيد توفى سنة ٩٣ هجرية</span>.

جابر بن زيد الأزدى البصرى أبو الشعثاء، تابعى فقيه من الأئمة من أهل البصرة أصله من عمان، صحب ابن عباس، وصفه الشماخى ـ أحد علماء الإباضية- بأنة أصل المذاهب. قال قتادة لما مات جابر: اليوم مات أعلم أهل العراق.','1','116',NULL,NULL,'0');
INSERT INTO [b435] VALUES (125,'<span data-type="title" id=toc-130>الجرجانى ٣٩٨ هجرية</span>

أبو عبد الله محمد بن يحيى بن مهدى الجرجانى، فقيه حنفى، عده المرغينانى صاحب الهداية من أصحاب التخريج، وتفقه عليه أبو الحسين القدورى، كما تفقه هو على أبى بكر الرازى، دفن إلى جوار قبر أبى حنيفة.','1','117',NULL,NULL,'0');
INSERT INTO [b435] VALUES (126,'<span data-type="title" id=toc-132>ابن الحاجب سنة ٦٤٦ هجرية</span>

أبو عمر عثمان بن عمر بن أبى بكر بن يونس فقيه مالكى من كبار العلماء بالعربية كردى الأصل ولد فى اسنا من صعيد مصر ونشأ فى القاهرة وسكن دمشق، ومات بالإسكندرية وكان أبوه حاجبا فعرف به. من مؤلفاته (مختصر الفقه) استخرجه من ستين كتابا، (منتهى السول والأمل فى علمى الأصول والجدل) وغيرها.','1','118',NULL,NULL,'0');
INSERT INTO [b435] VALUES (127,'<span data-type="title" id=toc-133>الحارثى توفى سنة ٧١١ هجرية</span>

مسعود بن أحمد بن مسعود بن زيد الحارثى سعد الدين، العراقى ثم المصرى، فقيه حنبلى، ولد ونشأ، بمصر وسكن دمشق ثم عاد إلى مصر فدرس بجامع ابن طولون وولى القضاء قبل وفاته بعامين وظل إلى ان توفى من كتبه شرح المقنع لابن قدامة توفى بالقاهرة.','1','119',NULL,NULL,'0');
INSERT INTO [b435] VALUES (128,'<span data-type="title" id=toc-134>الحاكم الشهيد ٣٣٤ هجرية</span>

محمد بن محمد بن أحمد أبو الفضل المروزى الشهير بالحاكم الشهيد قاض وزير كان عالم مرو وأمام الحنفية فى عصره. ولى قضاء بخارى ثم وزارة خراسان قتل شهيدا فى الرى من أشهر كتبه الكافى والمنقى " فى فروع الحنفية.','1','120',NULL,NULL,'0');
INSERT INTO [b435] VALUES (129,'<span data-type="title" id=toc-135>ابن حبيب سنة ٢٣٨ هجرية</span>

عبد الملك بن حبيب بن سليمان بن هارون السلمى القرطبى أبو مروان عالم الأندلس وفقيهها فى عصر زار مصر ثم عاد إلى الأندلس فتوفى بقرطبة كان رأسا فى فقه المالكية. له تصانيف كثيرة منها طبقات الفقهاء. والتابعين، وتفسير موطأ مالك وغيرهما.','1','121',NULL,NULL,'0');
INSERT INTO [b435] VALUES (130,'<span data-type="title" id=toc-136>حبيب بن أبى ثابت سنة ١١٩ هجرية</span>

حبيب بن أبى ثابت الكاهلى مولاهم أبو يحيى الكوفى أخذ عن خلق من الصحابة والتابعين وأخذ عنه مسعر والثورى وشعبة وغيرهم. له نحو مائتى حديث قيل مات سنة ١١٩، وقيل سنة ١٢٢ هجرية','1','122',NULL,NULL,'0');
INSERT INTO [b435] VALUES (131,'<span data-type="title" id=toc-137>ابن حجر العسقلانى سنة ٨٥٢ هجرية</span>

أحمد بن على بن محمد الكنانى العسقلانى من أئمة العلم والتاريخ أصله من عسقلان بفلسطين مولده ووفاته بالقاهرة انكب على الحديث ورحل فى طلبه وولى القضاء مرة وتصانيفه كثيرة منها (الدرر الكامنة فى اعيان المائة الثامنة) وأشهرها الفتح البارى فى شرح صحيح البخارى.','1','123',NULL,NULL,'0');
INSERT INTO [b435] VALUES (132,'<span data-type="title" id=toc-138>ابن حجر الهيثمى توفى سنة ٩٧٤ هجرية</span>

أحمد بن محمد بن على السعدى الأنصاري شيخ الإسلام أبو العباس فقيه " باحث مصرى مولد. فى محله أبو الهيثم غربية وإليها نسبته، تعلم فى الأزهر وله تصانيف كثيرة، منها كتب كثيرة منها تحفة المحتاج لشرح المنهاج.','1','125',NULL,NULL,'0');
INSERT INTO [b435] VALUES (133,'<span data-type="title" id=toc-139>حذيفة بن اليمان توفى سنة ٣٦ هجرية</span>

حذيفة بن اليمان العبسى صاحب وله رسول الله ولد بالمدينة وشهد أحدا والخندق توفى بالمدينة.','1','128',NULL,NULL,'0');
INSERT INTO [b435] VALUES (134,'<span data-type="title" id=toc-140>ابن حزم سنة ٤٥٦ هجرية</span>

على بن أحمد بن سعيد بن حزم الظاهرى أبومحمد. عالم الأندلس فى عصر ولد بقرطبة كانت له ولأبيه من قبله رياسة الوزارة وتدبير المملكة فزهد بها وانصرف إلى العلم والتأليف وكان يستنبط الأحكام من الكتاب والسنه، انتقد-كثيرا من الفقهاء توفى فى بادية لبلة بالأندلس وله فى الفقة كتاب المحلى وفى الأصول الأحكام','1','124',NULL,NULL,'0');
INSERT INTO [b435] VALUES (135,'<span data-type="title" id=toc-141>الحسن البصرى</span>

الحسن بن يسار البصرى أبو سعيد تابعى كان إمام أهل البصرة ولد بالمدينة له مواقف مع الحجاج بن يوسف توفى بالبصرة','1','126',NULL,NULL,'0');
INSERT INTO [b435] VALUES (136,'<span data-type="title" id=toc-142>الحسن بن زياد سنة ٢٠٤ هجرية</span>

الحسن بن زياد اللؤلؤى الكوفى أبو على واشتهر باللؤلؤى نسبة إلى بيع اللؤلؤ وكان أبوه من موالى الانصار قاض فقيه من أصحاب ابى حنيفة ولى القضاء بالكوفة ومن كتبه " ادب القاضى " و " معانى الايمان) و " النفقات ".','1','127',NULL,NULL,'0');
INSERT INTO [b435] VALUES (137,'<span data-type="title" id=toc-143>حسين الجعفى سنة ٢٠٣ هجرية</span>

الحسين بن على الجعفى كان من كبار المحدثين والرواة وهو منسوب إلى جعفى قبيله من ولد جعفر بن سعد العشيرة من "مذحج " وكان رأسا فى الزهد والعبادة مع تقدمه فى العلم.','1','129',NULL,NULL,'0');
INSERT INTO [b435] VALUES (138,'<span data-type="title" id=toc-144>الحصكفى توفى سنة ١٠٨٨ هجرية</span>

محمد بن على بن محمد مفتى الحنفية فى دمشق، ولد وتوفى بها وكان عاكفا على التدريس، من كتبه " الدر المختار فى شرح تنوير الأبصار والدر المنتقى شرح ملتقى الابحر"','1','131',NULL,NULL,'0');
INSERT INTO [b435] VALUES (139,'<span data-type="title" id=toc-145>الحصيرى سنة ٦٣٦ هجرية</span>

محمود بن أحمد بن عبد السيد بن عثمان أبو المجامد انتهت إليه رياسة الحنفية فى زمانه مولده فى بخارى ونسبته إلى محلة كان يعمل فيها الحصيريه من كتبه التحرير فى شرح الجامع الكبير والطريقة الحصيريه فى الخلاف بين الشافعية والحنفية.','1','130',NULL,NULL,'0');
INSERT INTO [b435] VALUES (140,'<span data-type="title" id=toc-146>الحطاب سنة ٩٥٤ هجرية</span>

أبو عبد الله بن محمد بن محمد بن عبد الرحمن الرعينى الفقيه المالكى أصله من المغرب ولد واشتهر بمكة ومات فى طرابلس المغرب ومن كتبه قرة العين شرح ورقات إمام الحرمين','1','132',NULL,NULL,'0');
INSERT INTO [b435] VALUES (141,'<span data-type="title" id=toc-147>الحكم بن عمرو سنة ٥٠ هجرية</span>

- الحكم بن عمرو بن مجدع الغفارى، صحابى له رواية، وحديثه فى البخارى وغيره، صحب النبى صلى الله عليه وسلم إلي أن مات وانتقل إلى البصرة فى أيام معاوية فوجهه زياد إلى خراسان كان صالحا فاضلا مقداما فغزا أقام بمرو ومات بها','1','136',NULL,NULL,'0');
INSERT INTO [b435] VALUES (142,'<span data-type="title" id=toc-148>الحكم بن هشام توفى سنة ٢٠٦ هجرية</span>

الحكم بن هشام بن عبد الرحمن الداخل، الأموى أبو العاص من أفحل ملوك بنى أميه بالأندلس يلقب بالربضى لايقاعه بأهل الربض ولد ونشأ بقرطبة. توفى بقرطبة.','1','133',NULL,NULL,'0');
INSERT INTO [b435] VALUES (143,'<span data-type="title" id=toc-149>الحلى (المحقق) سنة ٦٧٦ هجرية</span>

الحسن بن يحيى بن الحسين بن سعيد نجم الدين أبو القاسم- جعفر بن فقيه إمامى مقدم من أهل الحلة فى العراق كان مرجع الشيعة الامامية فى عصره من تصانيفه شرائع الإسلام والنافع مختصر الشرائع توفى فى الحلة.','1','134',NULL,NULL,'0');
INSERT INTO [b435] VALUES (144,'<span data-type="title" id=toc-150>الحلوانى (شمس الائمة) سنة ٤٤٨ هجرية</span>

عبد العزيز بن أحمد بن نصر بن صالح الحلوانى البخارى الملقب بشمس الأئمة فقيه الحنفية منسوب إلى عمل الحلوان كان إمام أهل الرأى فى وقته ببخارى من كتبه المبسوط فى الفقه والنوادر فى الفروع والفتاوى وشرح أدب القاضى لأبى يوسف ودفن ببخارى.','1','140',NULL,NULL,'0');
INSERT INTO [b435] VALUES (145,'<span data-type="title" id=toc-151>حماد بن أبى سليمان سنة ١٢٠ هجرية</span>

حماد بن أبى سليمان رواية إبراهيم النخعى ويكنى بأبى اسماعيل مولى إبراهيم بن أبى موسى الأشعرى كان استاذا لأبى حنيفة إذ درس عليه ثمانى عشرة سنة','1','135',NULL,NULL,'0');
INSERT INTO [b435] VALUES (146,'<span data-type="title" id=toc-152>حماد بن سلمة توفى سنة ١٦٧ هجرية</span>

أبو سلمة، حماد بن سلمة بن دينار البصرى، الربعى بالولاء مفتى البصرة وأحد رجال الحديث وكان حافظا ثقة مأمونا.','1','139',NULL,NULL,'0');
INSERT INTO [b435] VALUES (147,'<span data-type="title" id=toc-153>حمزة بن حبيب سنة ١٥٦ هجرية</span>

حمزة بن حبيب بن عمارة كان مولى لآل عكرمة التميمى وكان يتجر باليزيت ومات بحلوان بالعراق فى خلافة المنصور وهو أحد القراء المشهورين','1','137',NULL,NULL,'0');
INSERT INTO [b435] VALUES (148,'<span data-type="title" id=toc-154>ابن حنبل سنة ٢٤١ هجرية</span>

أحمد بن محمد بن حنبل أبو عبد الله الثسيبانى الوائلى امام المذهب الحنبلى واحد الأئمة الأربعة أصله من مرر وكان ابوه والى سرخس ولد ببغداد ونشأ مكبا على طلب العلم وسافر فى سبيله كثيرا، وصنف المسند ستة مجلدات تحتوى ثلاثين ألف حديث','1','138',NULL,NULL,'0');
INSERT INTO [b435] VALUES (149,'<span data-type="title" id=toc-155>ابو حنيفة سنة ١٥٠ هجرية</span>

النعمان بن ثابت التيمى بالولاء الكوفى الفقيه المجتهد أحد الأئمة الأربعة ونسب إليه المذهب الحنفى.','1','141',NULL,NULL,'0');
INSERT INTO [b435] VALUES (150,'<span data-type="title" id=toc-156>أبوالحارث القرن الثالث</span>

أحمد بن محمد أبو الحارث الصائغ كان من أصحاب الإمام وكان الامام يأنس به ويقدمه روى عن الإمام مسائل كثيرة.','1','142',NULL,NULL,'0');
INSERT INTO [b435] VALUES (151,'<span data-type="title" id=toc-157>أبو الحارث المروذى القرن الثالث</span>

أحمد بن محمد بن عبد ربه المروذى من أصحاب الإمام أحمد وروى عنه.','1','150',NULL,NULL,'0');
INSERT INTO [b435] VALUES (152,'<span data-type="title" id=toc-158>أبو حازم سنة ١٣٥ هجرية</span>

سلمة بن دينار المدنى الأعرج الزاهد الفقيه وهو مخزومى سمع سهل بن سعد وأكثر الرواية عنه فى الصحيحين وغيرهما وسمع عن سعيد بن المسبب وعطاء وغيرهما.','1','143',NULL,NULL,'0');
INSERT INTO [b435] VALUES (153,'<span data-type="title" id=toc-159>ابن أبى حازم سنة ١٨٤ هجرية</span>

عبد العزبز بن أبى حازم سلمة ين دينار المدنى أبو تمام فقيه محدث قال ابن حنبل لم يكن بالمدينة بعد مالك أفقه من ابن أبى حازم.','1','144',NULL,NULL,'0');
INSERT INTO [b435] VALUES (154,'<span data-type="title" id=toc-160>ابن حامد سنة ٤٠٣ هجرية</span>

الحسن بن حامد بن على بن مروان أبو عبد الله البغدادى إمام الحنبلية فى زمانة ومدرسهم ومفتيهم له المصنفات فى العلوم المختلفة فله الجامع فى المذهب وله شرح الخرقى وشرح أصول الدين وشرح أصول الفقه توفى بقرب واقصة حين رجوعه من مكة.','1','145',NULL,NULL,'0');
INSERT INTO [b435] VALUES (155,'<span data-type="title" id=toc-161>الحدادى سنة ٨٠٠ هجرية</span>

أبو بكر بن على بن محمد الحداد الزبيدى فقيه حنفى يمانى من أهل العبادية " قرية فى تهامة " استقر فى زبيد وتوفى بها وله فى مذهب أبى حنيفة مصنفات جليلة منها " السراج الوهاج " فى ثمانى مجلدات في شرح مختصر القدورى وهو مخطوط وله سراج الظلام مخطوط فى شرح منظومة الهاملى.','1','147',NULL,NULL,'0');
INSERT INTO [b435] VALUES (156,'<span data-type="title" id=toc-162>حرب من الفقهاء القرن الثالث</span>

حرب بن إسماعيل بن خلف الحنظلى الكرمانى أبو محمد ذكر. أبو بكر الخلال فقال رجل جليل أخذ العلم عن أحمد بن حنبل وروى عنة','1','148',NULL,NULL,'0');
INSERT INTO [b435] VALUES (157,'<span data-type="title" id=toc-163>الحسن البصرى سنة ٤٣٨ هجرية</span>

الحسن بن محمد بن إبراهيم أبو على المالكى عالم بالقراءات من أهل بغداد له الروضة فى القراءات الإحدى عشرة.','1','146',NULL,NULL,'0');
INSERT INTO [b435] VALUES (158,'<span data-type="title" id=toc-164>الحسن سنة ١١٠٢ هجرية</span>

الحسن ين مسعود بن محمد أبو على نور الدين اليوسى فقيه مالكى أديب ينعت بغزال عصره تنقل فى الأمصار فأخذ. من علمائها وأستقر بفاس مدرسا وأشتهر حتى قيل عنه. من فاته الحسن البصرى فليصحب الحسن اليوسى يكفيه من كتبه " قانون أحكام العلم، حاشية على شرح السنوسى فى التوحيد الكوكب الساطع فى شرح جمع الجوامع لم يتمه. وغيرها.','1','151',NULL,NULL,'0');
INSERT INTO [b435] VALUES (159,'<span data-type="title" id=toc-165>الحسن سنة ٥٠ هجرية</span>

الحسن بن على بن أبى طالب ال هجريةاشمى القرشى أبو محمد خامس الخلفاء الراشدين وأخرهم وثانى الأئمة الاثنى عشر عند الإمامية ولد فى المدينة المنورة وأمه فاطمة الزهراء بنت الرسول صلى الله عليه وسلم بايعه أهل العراق بالخلافة بعد مقتل أبيه وكاات تقع بين أتباعه وأتباع معاوية حرب لولا أنه حكم عقله وحبه للخير وصالح المسلمين فخلع نفسه من الخلافة وسلم الأمر لمعاوية توفى بالمدينة.','1','149',NULL,NULL,'0');
INSERT INTO [b435] VALUES (160,'<span data-type="title" id=toc-166>أبو الحسن الرضا سنة ٢٠٣ هجرية</span>

على بن موسى الكاظم بن جعفر الصادق ثامن الأئمة الاثناعشر عند الإمامية من أجلاء أهل البيت وفضلائهم، ولد فى المدينة وكان أسود اللون أمه حبشية وأحبه المأمون العباسى فعهد إليه بالخلافة من بعده وزوجة أبنته وضرب أسمه على الدينار والدرهم، مات فى حياة المأمون بطوس فدفنه الى جانب أبيه الرشيد.','1','157',NULL,NULL,'0');
INSERT INTO [b435] VALUES (161,'<span data-type="title" id=toc-167>ابن أبى الحصين سنة ٣٧ هجرية</span>

عبد الله بن أبى الحصين الأسدى فارس ممن كان مع على بن أبى طالب فى حرب صفين قتل فيها.','1','152',NULL,NULL,'0');
INSERT INTO [b435] VALUES (162,'<span data-type="title" id=toc-168>ابن الحكم سنة ٢٥١ هجرية</span>

عبد الوهاب بن الحكم بن نافع أبو الحسن الوراق صحب الإمام أحمد وسمع منه ومن غير.. كان يسكن الجانب الغربى ببغداد وكان من الصالحين اختلف فى وفاته فقيل سنة ٢٥٠، سنة ٢٥١ وهو أثبت دفن بباب البردان ببغداد.','1','154',NULL,NULL,'0');
INSERT INTO [b435] VALUES (163,'<span data-type="title" id=toc-169>الحلى سنة ٦٧٦ هجرية</span>

أبو القاسم الحلى جعفر ين يحيى أحد أئمة الفقة الشيعى " انظر جعفر "','1','153',NULL,NULL,'0');
INSERT INTO [b435] VALUES (164,'<span data-type="title" id=toc-170>حمزة ابن عبد المطلب سنة ٣ هجرية</span>

حمزة بن عبد المطلب بن هاشم يلقب بأسد الرحمن وأسد رسول الله، وهو عم رسول الله صلى الله عليه وسلم وأخوه من الرضاعة، وكان أسن منه بسنتين وآخى رسول الله بينه وبين زيد بن حارثة، أسلم فى السنة الثانية من بعثة الرسول. وأستشهد يوم أحد فى نصف شوال.','1','156',NULL,NULL,'0');
INSERT INTO [b435] VALUES (165,'<span data-type="title" id=toc-171>حمزة بن سنة ٦١ هجرية</span>

حمزة بن عمرو بن عويمر الأسلمى روى له عن رسول الله صلى الله عليه وسلم تسعة أحاديث، وروت عنه أم المؤمنين عائشة وعروة وسليمان بن يسار وغيرهم.','1','155',NULL,NULL,'0');
INSERT INTO [b435] VALUES (166,'<span data-type="title" id=toc-172>ابن حمزة سنة ٨٣٤ هجرية</span>

جعفر بن محمد بن حمزة شرف الدين داعية إسماعيلى من علمائهم له الرسالة الموقظة مخطوط.','1','161',NULL,NULL,'0');
INSERT INTO [b435] VALUES (167,'<span data-type="title" id=toc-173>أبو حمزة</span>

الحسن بن على أبى حمزة البطائنى من وجوه الواقفة له كتب منها كتاب الفتن وهو كتاب الملاحم روى عنه كثيرون','1','158',NULL,NULL,'0');
INSERT INTO [b435] VALUES (168,'<span data-type="title" id=toc-174>الحموى سنة ١٠٩٨ هجرية</span>

العلامة السيد أحمد بن محمد الحنفى المعروف بالحموى نسبة الى حماه، من، مؤلفاته حاشيته على الأشباه والنظائر وكتاب كشف الرمز عن خبايا الكنز.','1','159',NULL,NULL,'0');
INSERT INTO [b435] VALUES (169,'<span data-type="title" id=toc-175>حنبل سنة ٢٧٣ هجرية</span>

حنبل بن إسحاق ين حنبل، أبو على الشيبانى، ابن عم الإمام أحمد بن حنبل سمع كثيرين، منهم أبو نعيم الفضل بن ركين وأحمد بن حنبل وسمع منه المسند ونقل عنه مسائل أجاد فيهما الرواية ومات بواسط.','1','160',NULL,NULL,'0');
INSERT INTO [b435] VALUES (170,'<span data-type="title" id=toc-176>حويصة صحابى</span>

أبو سعيد حويصة بن مسعود بن كعب بن عامر بن عدى الأنصارى الأوسى الحارثى المدنى الصحابى شهد أحدا أو الخندق وسائر المشاهد بعدها مع رسول الله صلى الله عليه وسلم. وقد أسلم على يد أخيه محيصة','1','164',NULL,NULL,'0');
INSERT INTO [b435] VALUES (171,'<span data-type="title" id=toc-177>حاتم الطائى توفى سنة ٤٦ قبل الهجرة</span>.

حاتم بن عبد الله بن سعد بن الحشرج الطائى القحطانى أبو عدى،فارس شاعر، يضرب المثل بجوده، فعد أشهر كرماء العرب، مات فى عوارض
جبل فى بلادطىء - وقبر عليه.','1','162',NULL,NULL,'0');
INSERT INTO [b435] VALUES (172,'<span data-type="title" id=toc-178>الحجاج الثقفى توفى سنة ٩٥ هجرية</span>.

الحجاج بن يوسف بن الحكم الثقفى، أبو محمد، قائد وخطيب، ولد ونشأ بالطائف فى الحجاز وانتقل إلى الشام تولى مكة والمدينة والطائف ثم أضاف إليها العراق، قال أبو عمر بن العلاء، ما رأيت أحد أفصح من الحسن البصرى والحجاج، اتفق أكثر المؤرخين على أنه كان سفاكا سفاحا.','1','163',NULL,NULL,'0');
INSERT INTO [b435] VALUES (173,'<span data-type="title" id=toc-179>حرمى بن عمارة توفى سنة ٢٠١ هجرية</span>.

حرمى (بفتح الحاء المهملة والراء وياء مشددة) ـ بن عمارة ـ بضم العين ـ ابن أبى حفصة البصرى، روىعن قرة بن خالد وشعبة.','1','166',NULL,NULL,'0');
INSERT INTO [b435] VALUES (174,'<span data-type="title" id=toc-180>أبو الحسن الأشعرى توفى سنة ٣٢٤ هجرية</span>.

على بن إسماعيل بن أبى بشر إسحاق بن سالم المكنى بأبى الحسن الأشعرى، ولد بالبصرة وتفقه على أبى إسحاق المروزى وابن سريج وتتلمذ فى العقائد لأبى على الجبائى، وبرع فى علمى الكلام والجدل على طريقة أهل الاعتزال حتى صار رأسا من رءوسهم، ولما كمل نضجه العقلى ترجحت عنده مذاهب أهل السنة وأدلتهم فأعلن خروجه على المعتزلة، بلغت مؤلفاته أكثر من مائة من أهمها مقالات الإسلاميين وإثبات القياس، وتخرج عليه خلق كثير، ويعتبر من مجتهدى الشافعية،أقام ببغداد يرد على أهل البدع وينصر السنة.','1','165',NULL,NULL,'0');
INSERT INTO [b435] VALUES (175,'<span data-type="title" id=toc-181>الحسن بن حى توفى سنة ١٥٤ هجرية</span>.

الحسن بن صالح بن حى الهمدانى الثورى الكوفى، عده الطوسى فى رجاله تارة من أصحاب الباقر وتارة من أصحاب الصادق وقال عنه: إنه زيدى، وإليه تنسب الصالحية منهم وفى الفهرست لابن النديم أنه كان فقيها متكلما وكان من كبار الشيعة الزيدية واختلف فى سنة وفاته بين ١٥٤ و١٩٩ هجرية.','1','172',NULL,NULL,'0');
INSERT INTO [b435] VALUES (176,'<span data-type="title" id=toc-182>الحسن بن سعيد توفى سنة ٥٧٩ هجرية</span>.

الحسن بن سعيد بن عبد الله بن بندار أبو على الشاتانى، فقيه غلب عليه الشعر فأجاده، مولده فى شاتان من نواحى ديار بكر وإليها نسبته، وانتقل إلى الموصل فتوفى فيها.','1','167',NULL,NULL,'0');
INSERT INTO [b435] VALUES (177,'<span data-type="title" id=toc-183>أبو الحسن السهيلى توفى سنة ٥٨١ هجرية</span>.

أبو الحسن وأبو زيد وأبو القاسم عبد الرحمن بن عبد الله بن أحمد السهيلى العلامة الأندلسى النحوى الحافظ العلم برع فى العربية واللغات والأخبار والأثر، عاش اثنتين وسبعين سنة.','1','168',NULL,NULL,'0');
INSERT INTO [b435] VALUES (178,'<span data-type="title" id=toc-184>حسن بن يحيى توفى سنة ١٢٣٨ هجرية</span>.

الحسن بن يحيى بن أحمد الكبسى، قاضى من فضلاء الزيدية باليمن قام بالقضاء فى بلاد خولان، وتوفى بصنعاء، من كتبة: بيع الغبن، إبطال بدعة الحمى والحدود، تحريم الزكاة على بنى هاشم.
أبو الحسين البصرى توفى سنة ٤٣٦ هجرية. محمد بن على الطيب أبو الحسين البصرى، أحد أئمة المعتزلة، من كتبه المعتمد فى أصول الفقه، وشرح الأصول الخمسة، وكتاب فى الإمامة.','1','169',NULL,NULL,'0');
INSERT INTO [b435] VALUES (179,'<span data-type="title" id=toc-185>الحسين بن القاسم بن محمد توفى سنة ١١٣١ هجرية</span>.

الحسين بن القاسم بن الإمام المؤيد بالله محمد بن المنصور القاسم الحسنى الشهارى، من أئمة الزيدية باليمن، توفى فى شهارة.','1','170',NULL,NULL,'0');
INSERT INTO [b435] VALUES (180,'<span data-type="title" id=toc-186>أبو الحسين الخياط حوالى ٣٠٠ هجرية</span>.

عبد الرحيم بن محمد بن عثمان، عالم فاضل، له كتب كثير فى النقض على ابن الراوندى، وكان فقيها صاحب حديث واسع الحفظ لمذاهب المتكلمين، شيخ المعتزلة ببغداد، تنتسب إليه فرقة منهم تدعى " الخياطية ".','1','171',NULL,NULL,'0');
INSERT INTO [b435] VALUES (181,'<span data-type="title" id=toc-187>الحسين بن على توفى سنة ٦١ هجرية</span>.

الحسين بن على بن أبى طالب الهاشمى، أبو عبد الله، سبط رسول الله صلى الله عليه وسلم، وهو وأخوه الحسن، سيدا شباب أهل الجنة، قال رسول الله صلى الله عليه وسلم فيه: " حسين منى وأنا من حسين، أحب الله من أحب حسينا، حسين سبط من الأسباط " وكان الحسين فاضلا كثير الصلاة والصوم والحج والصدقة وأفعال الخير. قتل رضى الله عنه يوم عاشوراء بكربلاء من أرض العراق.','1','173',NULL,NULL,'0');
INSERT INTO [b435] VALUES (182,'<span data-type="title" id=toc-188>أبو الحصين</span>.

أبو الحصين ـ كأمير ـ عثمان بن عاصم، من كبار التابعين وفقهائهم.','1','174',NULL,NULL,'0');
INSERT INTO [b435] VALUES (183,'<span data-type="title" id=toc-189>أم الحصين</span>.

أم الحصين بنت إسحاق الأحمسية، من كبار رواة الصحابة روى عنها يحيى بن الحصين وغيره.','1','175',NULL,NULL,'0');
INSERT INTO [b435] VALUES (184,'<span data-type="title" id=toc-190>حفصة توفيت سنة ٤٥ هجرية</span>.

أم المؤمنين حفصة بنت عمر بن الخطاب تزوجها النبى صلى الله عليه وسلم، سنة أثنتين من الهجرة، وكانت ولادتها قبل البعثة بخمس سنوات، روت عن النبى صلى الله عليه وسلم وروى عنها كثيرون منهم أخوها عبد الله، ماتت فى زمن معاوية.','1','176',NULL,NULL,'0');
INSERT INTO [b435] VALUES (185,'<span data-type="title" id=toc-191>أبو حفص توفى سنة ٣٠٩ هجرية</span>.

عمر بن إسماعيل بن أبى غيلان أبو حفص الثقفى البغدادى، سمع على بن الجعد وجماعة، وثقه الخطيب.','1','177',NULL,NULL,'0');
INSERT INTO [b435] VALUES (186,'<span data-type="title" id=toc-192>الحكم توفى سنة ١٥٤ هجرية</span>.

الحكم بن أبان العدمى، روى عن طاووس وجماعة، وكان شيخ أهل اليمن وعالمهم بعد يعقوب، قال أحمد العجلى عنه ثقة صاحب سنة.','1','178',NULL,NULL,'0');
INSERT INTO [b435] VALUES (187,'<span data-type="title" id=toc-193>الحكم توفى سنة ١٩٩ هجرية</span>.

الحكم بن عبد الله البلخى الفقيه وكنيته أبو مطيع صاحب أبى حنيفة وصاحب كتاب الفقه الأكبر، ولى قضاء بلخ، وحدث عن ابن عوف وجماعة','1','182',NULL,NULL,'0');
INSERT INTO [b435] VALUES (188,'<span data-type="title" id=toc-194>الحكم توفى سنة ٢٢٢ هجرية</span>

أبو اليمان الحكم بن نافع البهرانى الحمصى الحافظ، روى عن جرير بن عبد الحميد وطبقته، وكان ثقة حجة كثير الحديث.','1','179',NULL,NULL,'0');
INSERT INTO [b435] VALUES (189,'<span data-type="title" id=toc-195>الحكم توفى سنة ٢٣٢ هجرية</span>.

الحكم بن موسى أبو صالح القنطرى البغدادى الحافظ، سمع إسماعيل بن عياش وطبقته.','1','180',NULL,NULL,'0');
INSERT INTO [b435] VALUES (190,'<span data-type="title" id=toc-196>الحكم توفى سنة ٢٩٥ هجرية</span>.

الحكم بن معبد الخزاعى الفقيه، مصنف كتاب السنة بأصبهان، روى عن محمد بن حميد الأزدى ومحمد ابن المثنى وطبقتهما، وكان من كبارالحنفية وثقاتهم.','1','183',NULL,NULL,'0');
INSERT INTO [b435] VALUES (191,'<span data-type="title" id=toc-197>الحكم بن عتيبة توفى سنة ١١٤ هجرية</span>.

الحكم بن عتيبة الكوفى الكندى أبو محمد جاء عنه فى تقريب التهذيب أنه ثقة ثبت، فقيه، إلا أنه ربما دلس، وأنه مات سنة ثلاث عشرة بعد المائة أو بعدها، وله نيف وستون سنة.','1','181',NULL,NULL,'0');
INSERT INTO [b435] VALUES (192,'<span data-type="title" id=toc-198>أم حكيم بنت أسيد</span>

أم حكيم بنت أسيد (بفتح الهمزة) ، روت عن أمها عن أم سلمة، وروى عنها المغيرة بن الضحاك','1','184',NULL,NULL,'0');
INSERT INTO [b435] VALUES (193,'<span data-type="title" id=toc-199>حماد توفى سنة ١٧٩ هجرية</span>.

حماد بن زيد بن درهم الأزدى البصرى أبو إسماعيل شيخ العراق فى عصره من حفاظ الحديث، أصله من سبى سجستان، مولده ووفاته بالبصرة.','1','186',NULL,NULL,'0');
INSERT INTO [b435] VALUES (194,'<span data-type="title" id=toc-200>حماد توفى سنة ١٩٠ هجرية</span>.

حماد بن عثمان الغاب، عده الطوسى فى رجاله مرة من أصحاب الصادق، ومرة من أصحاب الكاظم ومرة من أصحاب الرضا رضى الله عنهم، وقال عنهم أنه ثقة جليل القدر.','1','185',NULL,NULL,'0');
INSERT INTO [b435] VALUES (195,'<span data-type="title" id=toc-201>حمزة الأسلمى توفى سنة ٦١ هجرية</span>.

حمزة بن عمرو بن عويمر بن الحارث الأسلمى، صحابى كان كثير العبادة وشهد فتح أفريقيا مع عبد الله بن سعد، وكانت له فيها مقامات محمودة روى له البخارى ومسلم.','1','187',NULL,NULL,'0');
INSERT INTO [b435] VALUES (196,'<span data-type="title" id=toc-202>حميد توفى ١٤٣ هجرية</span>.

حميد الطويل بن أبى حميد طرخان، كنيته أبو عبيدة أحد ثقات التابعين البصريين، سمع أنسا وطائفة،
كان قائما يصلى فسقط ميتا.','1','188',NULL,NULL,'0');
INSERT INTO [b435] VALUES (197,'<span data-type="title" id=toc-203>أبو حميد الساعدى</span>.

عبد الرحمن بن سعد بن المنذر، روى عن النبى صلى الله عليه وسلم عدة أحاديث وله ذكر فى الصحيحين، روى عنه جابر الصحابى وغيره، توفى فى آخر خلافة معاوية.','1','189',NULL,NULL,'0');
INSERT INTO [b435] VALUES (198,'<span data-type="title" id=toc-205>خالد بن سعيد توفى سنة ١٤ هجرية</span>

جده العاصى بن أمية صحابى من الولاة الغزاة قديم الإسلام لزم الرسول صلى الله عليه وسلم يصلى معه فى نواحى مكة خاليا هاجر إلى الحبشة فبقى بها طويلا غزا مع النبى صلى الله عليه وسلم وحضر فتح مكة وتبوك توفى فى وقعة مرج الصغرى فى عهد أبى بكر.','1','190',NULL,NULL,'0');
INSERT INTO [b435] VALUES (199,'<span data-type="title" id=toc-206>الخثعمية</span>

صحابية قال الحفاط إنها لم تسم مشهورة بحديثها فى حجة الوداع الذى سألت فيه النبى صلى الله عليه وسلم عن الحج عن أبيها.','1','191',NULL,NULL,'0');
INSERT INTO [b435] VALUES (200,'<span data-type="title" id=toc-207>الخرفى سنة ٣٣٤ هجرية</span>

أبو القاسم عمر بن الحسين بن عبد الله الخرقى فقيه حنبلى من أهل بغداد نسبته إلى بيع الخرق ووفاته بدمشق له تصانيف احترقت وبقى منها المختصر فى الفقه','1','193',NULL,NULL,'0');
INSERT INTO [b435] VALUES (201,'<span data-type="title" id=toc-208>ابن خزيمة سنة ٣١١</span>

محمد بن إسحق بن خزيمة السلمى أمام نيسابور فى عصر كان فقيها مجتهدا عالما بالحديث مولده ووفاته بنيسابور ورحل إلى العراق والشام والجزيرة ومصر وله مصنفات كثيرة منها كتاب التوحيد واثبات صفات الرب','1','194',NULL,NULL,'0');
INSERT INTO [b435] VALUES (202,'<span data-type="title" id=toc-209>الخصاف توفى سنة ٢٦١ هجرية</span>

أحمد بن عمر بن مهير الشيبانى أبو بكر المعروف بالخصاف فقيه حاسب فرضى كان مقدما عند الخليفة المهتدى بالله وكان ورعا يأكل من كسب يده توفى ببغداد ومن تآليفه- أحكام الأوقاف الحيل الوصايا. أدب القاضى.','1','192',NULL,NULL,'0');
INSERT INTO [b435] VALUES (203,'<span data-type="title" id=toc-210>الخطابى سنة ٣٨٨ هجرية</span>

أحمد بن إبراهيم بن الخطاب الخطابى البوستى كان فقيها أديبا محدثا له تصانيف منها غريب الحديث معالم السنن وأعلام السنن وغير ذلك','1','195',NULL,NULL,'0');
INSERT INTO [b435] VALUES (204,'<span data-type="title" id=toc-211>ابو الخطاب سنة ٥١٠</span>

محفوظ بن أحمد بن الحسن الكلوذانى إمام الحنابلة فى عصره أصله من كلوذاى من ضواحى بغداد ومولده ووفاته ببغداد، من كتبه (التمهيد) فى أصول الفقة والانتصار فى المسائل الكبار وغيرها','1','196',NULL,NULL,'0');
INSERT INTO [b435] VALUES (205,'<span data-type="title" id=toc-212>الخطيب الشربينى سنة ٩٧٧</span>

محمد بن أحمد الشربينى شمس الدين. فقيه شافعى مفسرمن أهل القاهرة له تصانيف كثيرة منها مفتى المحتاج فى شرح منهاج الطالبين، والاقناع فى حل ألفاظ أبى شجاع وغيرها','1','197',NULL,NULL,'0');
INSERT INTO [b435] VALUES (206,'<span data-type="title" id=toc-213>ابن خلدون سنة ٨٠٨</span>

عبد الرحمن بن محمد بن محمد بن خلدون أبو زيد ولى الدين الحضرمى الأشبيلى الفليسوف المؤرخ العالم اشتهر بكتابه: (العبر وديوان المبتدأ والخبر فى تاريخ العرب والعجم والبربر) طبع فى ٧ مجلدات أولها المقدمة التى تعرض أصول علم الاجتماع','1','198',NULL,NULL,'0');
INSERT INTO [b435] VALUES (207,'<span data-type="title" id=toc-214>الخلال سنة ٢١١ هجرية</span>

أبو بكر أحمد بن احمد بن هارون مفسر عالم بالحديث واللغة من كبار الحنابلة، من أهل بغداد قال الذهبى عنه انه جامع علم أحمد ومرتبه له تفسير الغريب " وطبقات اصحاب ابن حنبل " وغيره -','1','199',NULL,NULL,'0');
INSERT INTO [b435] VALUES (208,'<span data-type="title" id=toc-215>خليل بن اسحاق توفى سنة ٧٧٦ هجرية</span>

ضياء الدين خليل بن اسحاق بن موسى الجندى فقيه مالكى من أهل مصر، تعلم فى القاهرة وولى الافتاء على مذهب مالك له " المختصر فى الفقه " ترجم إلى الفرنسية وغيره','1','200',NULL,NULL,'0');
INSERT INTO [b435] VALUES (209,'<span data-type="title" id=toc-216>خنساء بنت خزام</span>

خنساء بنت خزام الانصارية الصحابية وهى التى أنكحها أبوها وهى كارهة فرد الرسول نكاحها، وروى حديثها هذا أبو داود والنسائى','1','201',NULL,NULL,'0');
INSERT INTO [b435] VALUES (210,'<span data-type="title" id=toc-217>خواهر زاده سنة ٤٨٣</span>

محمد بن الحسين بن محمد أبو بكر، البخارى المعروف ببكر خواهر زادة فقيه كان شيخ الأحناف فيما وراء، النهر مولده ووفاته فى بخارى له المبسوط والمختصر والتجنيس فى الفقه','1','210',NULL,NULL,'0');
INSERT INTO [b435] VALUES (211,'<span data-type="title" id=toc-218>الخرشى سنة ١١٠١ هجرية</span>

محمد بن عبد الله الخراشى المالكى أبو عبد الله أول من تولى مشيخة الأزهر كان فقيها فاضلا ورعا من كتبه الشرح الكبير على متن خليل والشرح الصغير على نفس المتن وهما مذهب المالكية وغيرهما أقام وتوفى بالقاهرة.','1','202',NULL,NULL,'0');
INSERT INTO [b435] VALUES (212,'<span data-type="title" id=toc-219>الخير الرملى سنة ١٠٨١ هجرية</span>

خير الدين بن أحمد بن على الأيوبى العليمى فقيه باحث من أهل الرملة بفلسطين ولد ومات فيها ورحل إلى مصر سنة ١٠٠٧ هجرية فمكث فى الأزهر ست سنين وعاد إلى بلده فأفتى بها ودرس الى أن توفى له الفتاوى " الخيرية" ومظهر الحقائق حاشية على البحر الرائق.','1','211',NULL,NULL,'0');
INSERT INTO [b435] VALUES (213,'<span data-type="title" id=toc-220>خالد بن الوليد توفى سنة ١٤ هجرية</span>.

خالد بن الوليد بن المغيرة المخزومى القرشى، كان يلى أعنة الخيل فى قريش، وقد أسلم سنة سبع من الهجرة، وله بطولات نادرة فى حروب الردة وقتال الفرس، توفى بحمص.','1','205',NULL,NULL,'0');
INSERT INTO [b435] VALUES (214,'<span data-type="title" id=toc-221>ابن خديج توفى سنة ٧٤ هجرية</span>.

رافع بن خديج ـ بفتح الخاء ـ ابن رافع بن عدى الأنصارى الأوسى، صحابى، شهد أحدا وأكثر المشاهد مع الرسول صلى الله عليه وسلم توفى بالمدينة.','1','203',NULL,NULL,'0');
INSERT INTO [b435] VALUES (215,'<span data-type="title" id=toc-222>الدار قطنى توفى سنة ٣٨٥ هجرية</span>

على بن عمر بن أحمد بن مهدى أبوه الحسن الدارقطنى الشافعى أول من صنف القراءات. ولد بدار القطن من أحياء بغداد ورحل إلى مصر وعاد إلى بغداد فتوفى بها من تصانيفه كتاب السنن','1','209',NULL,NULL,'0');
INSERT INTO [b435] VALUES (216,'<span data-type="title" id=toc-224>الداراورى توفى سنة ١٨٦ هجرية</span>

أبو محمد عبد العزيز بن محمد بن عبيد الداراوردى الجهنى بالولاء،. المدنى محدث روى عنه خلق كثير منهم سفيان وشعبة نسبته إلى دراورد من قرى خراسان اصله منها ومولد ووفاته بالمدينة','1','204',NULL,NULL,'0');
INSERT INTO [b435] VALUES (217,'<span data-type="title" id=toc-225>أبودواد الظاهرى سنة ٢٧٠ هجرية</span>

داود بن على بن خلف الأصبهانى أبو سليمان الملقب بالظاهرى أحد الأئمة المجتهدين فى الإسلام تنسب إليه طائفة الظاهرية وسميت فى بذلك لأخذها بظاهر الكتاب وال سنة وإعراضها عن القياس وهو من أهل قاشان ومولد بالكوفة سكن بغداد انتهت إليه رياسة العلم وبها توفى','1','206',NULL,NULL,'0');
INSERT INTO [b435] VALUES (218,'<span data-type="title" id=toc-226>الدبوسى سنة ٣٤٠ هجرية</span>

عبد الله بن عمر بن عيسى أبو زيد أول من وضع علم الخلاف كان فقيها باحثا نسبته إلى دبوسية بين بخارى وسمرقند وفاته فى بخارى ومن مؤلفاته- تأسيس النظر فيما اختلف فيه الإمام وصاحباه ومالك والشافعى وكذا الأسرار فى الاصول والفروع عند الحنفية.','1','207',NULL,NULL,'0');
INSERT INTO [b435] VALUES (219,'<span data-type="title" id=toc-227>أبو الدرداء سنة ٣٢ هجرية</span>

عويمر بن مالك بن قيس بن أمية الأنصارى الخزرجى أبو الدرداء صحابى من الحكماء ولاه معاوية قضاء دمشق بأمر عمر بن الخطاب وهو أول قاض بها وهو أحد الذين جمعوا القرآن حفظا على عهد النبى صلى الله عليه وسلم مات بالشام.','1','208',NULL,NULL,'0');
INSERT INTO [b435] VALUES (220,'<span data-type="title" id=toc-228>ابو الدرداء سنة ٢٧٥ هجرية</span>

سليمان بن الأشعث بن اسحاق بن بشير لأسدى السجستانى أبو داود اماه أهل الحديث فى زمانه توفى بالبصرة له كتاب السنن جزءان وهو أحد الكتب الستة.','1','212',NULL,NULL,'0');
INSERT INTO [b435] VALUES (221,'<span data-type="title" id=toc-229>الدردير سنة ١٢٠١ هجرية</span>

أبو البركات أحمد بن محمد بن أحمد العدوى فاضل من فقهاء المالكية ولد فى بنى عدى بمصر وتعلم بالأزهر وتوفى بالقاهرة وكان من كبارالصوفية فى عصر. ومن كتبه أقرب المسالك لمذهب مالك وشرح القدير فى شرح مختصر خليل وغيرها','1','213',NULL,NULL,'0');
INSERT INTO [b435] VALUES (222,'<span data-type="title" id=toc-230>الدسوقى سنة ١٢٠١ هجرية</span>

محمد بن أحمد بن عرفة الدسوقى المالكى من علماء العربية من أهل دسوق تعلم وأقام وتوفى بالقاهرة وكان من المدرسين فى الأزهر، له كتب منها "الحدود الفقهية" فى فقة المالكية، وحواش على مفتى اللبيب والسعد التفتازانى والشرح الكبير على مختصر خليل وشرح السنوسى " لمقدمة أم البراهين.','1','217',NULL,NULL,'0');
INSERT INTO [b435] VALUES (223,'<span data-type="title" id=toc-231>ابن دقيق العيد توفى سنة ٧٠٢ هجرية</span>

محمد بن على بن وهب بن مطيع ابو الفتح تقى الدين القشيرى المعروف كأبيه وجده بابن دقيق العبد قاض من أكابر العلماء بالاصول مجتهد اصل أبيه من منفلوط بمصر وانتقل الى قوص وتعلم بدمشق والاسكنرية ثم بالقاهرة ولى قضاء الديار المصرية سنه ٦٩٥ هجرية توفى بالقاهرة له تصانيف منها أحكام الأحكام فى الحديث.','1','215',NULL,NULL,'0');
INSERT INTO [b435] VALUES (224,'<span data-type="title" id=toc-232>ابن دواد القرن الثالث</span>

محمد بن داود بن صبيح أبو جعفر المصيصى كان من خواص الإمام أحمد ورؤسائهم وله مسائل كثيرة عن الإمام.','1','214',NULL,NULL,'0');
INSERT INTO [b435] VALUES (225,'<span data-type="title" id=toc-233>الدباس</span>

محمد بن محمد بن الحسن بن سفيان أبو طاهر الدباس. كان إمام أهل الرأى بالعراق وكان من أهل السنة والجماعة وكان من أقران عبد الله الكرخى كان يوصف بالحفظ ومعرفة الروايات. ولى القضاء بالشام أخرج منها إلى مكة فمات بها. ذكر الحموى أن الدباس نسبة إلى بيع الدبس الماكول.','1','238',NULL,NULL,'0');
INSERT INTO [b435] VALUES (226,'<span data-type="title" id=toc-235>ابن ذؤيب</span>.

ويقال: ابن أبى ذؤيب، إسماعيل بن عبد الرحمن، روى عن ابن عمر وعطاء: وعنه ابن أبى نجيح وسعيد بن خالد القارظى، وثقه أبو زرعة والدار قطنى.','1','216',NULL,NULL,'0');
INSERT INTO [b435] VALUES (227,'<span data-type="title" id=toc-236>أبو ذر توفى سنة ٣٢ هجرية</span>.

جندب بن جنادة بن سفيان بن عبيد من بنى غفار، صحابى من كبارهم، قديم الإسلام، يضرب به المثل فى الصدق هاجر بعد وفاة النبى صلى الله عليه وسلم إلى بادية الشام وكان ديدنه تعرض الفقراء على مشاركة الأغنياء فى أموالهم ولما مات لم يكن فى داره ما يكفن به، مات بالربذة من قرى المدينة.','1','218',NULL,NULL,'0');
INSERT INTO [b435] VALUES (228,'<span data-type="title" id=toc-237>الذهبى توفى سنة ٧٤٨ هجرية</span>.

محمد بن أحمد بن عثمان بن قايماز الذهبى شمس الدين، حافظ مؤرخ علامة محقق، مولده ووفاته بدمشق رحل إلى القاهرة وطاف كثيرا من البلدان، تصانيفه كثيرة تقارب المائة منها سير أعلام النبلاء خمسة عشر مجلدا وتذكرة الحفاظ والعبر وميزان الاعتدال وغيرها.','1','219',NULL,NULL,'0');
INSERT INTO [b435] VALUES (229,'<span data-type="title" id=toc-239>الرازى توفى سنة ٦٠٦ هجرية</span>

محمد بن عمر أحمد بن الحسين أبو عبد الله فخر الدين الرازى أصله من طبرستان ومولده فى الرى ويقال له ابن خطيب الرى، له مؤلفات كثيرة من أشهرها مفاتيح الغيب فى التفسير وله كتاب المحصول فى علم الأصول توفى فى هراه','1','220',NULL,NULL,'0');
INSERT INTO [b435] VALUES (230,'<span data-type="title" id=toc-240>الراغب سنة ٥٠٢ هجرية</span>

الراغب الأصفهانى أبو القاسم الحسين بن محمد بن المفضل من الحكماء العلماء من أهل اصبهان وسكن بغداد واشتهر حتى كان يقرن بالغزالى وله كتب منها محاضرات الأدباء ومفردات القرآن.','1','221',NULL,NULL,'0');
INSERT INTO [b435] VALUES (231,'<span data-type="title" id=toc-241>أبو رافع</span>

هو مولى رسول الله صلى الله عليه وسلم واسمه أسلم قيل إنه كان مولى للعباس بن عبد المطلب فوهبه للنبى فلما اسلم العباس بشر أبو رافع النبى بإسلامه فاعتقه وزوجه سلمى مولاته','1','223',NULL,NULL,'0');
INSERT INTO [b435] VALUES (232,'<span data-type="title" id=toc-242>الرافعى سنة ٦٢٣ هجرية</span>

- أبو القاسم عبد الكريم بن محمد بن عبد الكريم الرأفعى القزوينى فقيه من كبار الشافعية كان له مجلس بقزوين للتفسير والحديث وتوفى بها له كتاب " المحرر " فى الفقه "فتح العز. بز فى شرح الوجيز " و " شرح مسند الشافعى "','1','222',NULL,NULL,'0');
INSERT INTO [b435] VALUES (233,'<span data-type="title" id=toc-243>الرافعى سنة ١٣٠٨ هجرية</span>

عبد الغنى بن أحمد عبد القادر الرافعى البيسارى الفاروقى قاض من فقهاء الحنفية ولد وتعلم فى طرابلس الشام أخذ الحديث عن علماء دمشق وعين مفتيا لطرابلس ثلاث سنوات.','1','224',NULL,NULL,'0');
INSERT INTO [b435] VALUES (234,'<span data-type="title" id=toc-244>الربيع المؤذن توفى سنة ٢٧٠ هجرية</span>

الربيع بن سليمان بن عبد الجبار بن كامل، أبو محمد، صاحب الشافعى وراوى كتبه، وأول من أملى الحديث بجامع ابن طولون، كان مؤذنا مولده ووفاته بمصر.','1','225',NULL,NULL,'0');
INSERT INTO [b435] VALUES (235,'<span data-type="title" id=toc-245>ربيعة الراى سنة ١٣٦ هجرية</span>

ربيعه بن فروخ التميمى بالولاء المدنى أبو عثمان إمام حافظ فقيه مجتهد لقب بربيعة الرأى لأنه كان بصيرا بالرأى والقياس كان صاحب الفتوى بالمدينة وتفقه عليه مالك، توفى بالهاشمية','1','226',NULL,NULL,'0');
INSERT INTO [b435] VALUES (236,'<span data-type="title" id=toc-246>ابن رجب سنة ٧٩٥ هجرية</span>

عبد الرحمن بن أحمد بن رجب السلامى البغدادى ثم الدمشقى أبو الفرج من العلماء ولد فى بغداد ونشآ وتوفى فى دمشق ومن كتبه- القواعد الفقهية الاستخراج لأحكام الخراج وذيل طبقات الحنابلة لابن أبى يعلى وغيرها.','1','227',NULL,NULL,'0');
INSERT INTO [b435] VALUES (237,'<span data-type="title" id=toc-247>ابن رشد سنة ٥٢٠ هجرية</span>

محمد بن أحمد بن رشد أبو الوليد قاضى الجماعة بقرطبة من أعيان المالكية له تآليف منها "المقدمات الممهدات " فى الأحكام الشرعية "البيان والتحصيل " فقه (وشرح معانى الآثار " وغيرها. مولده ووفاته بقرطبة.','1','228',NULL,NULL,'0');
INSERT INTO [b435] VALUES (238,'<span data-type="title" id=toc-248>ابن رشد الحفيد سنة ٥٩٥ هجرية</span>

محمد بن أحمد بن أحمد بن آحمد بن رشد الأندلسى أبو الوليد الفليسوف ويلقب بالحفيد تمييزا له عن جده المتوفى سنة ٥٢٠ توفى بمراكش ودفن فى قرطبة له كتب كثيرة منها " بداية المجتهد ونهايه المقتصد " فى الفقه قال ابن الأبار، كان يفزع إلى فتواه فى الخطب كما يفزع إلى فتواه فى الفقه','1','242',NULL,NULL,'0');
INSERT INTO [b435] VALUES (239,'<span data-type="title" id=toc-249>الرضى سنة ٢٠٣ هجرية</span>

على بن موسى الكاظم بن جعفر الصادق من اجلاء السادة آهل البيت وفضلائهم ولد فى المدينة عهد اليه المأمون العباسى بالخلافة من بعده ولكن آهل بغداد ثاروا فخلعوا المأمون، من أئمة الأمامية.','1','230',NULL,NULL,'0');
INSERT INTO [b435] VALUES (240,'<span data-type="title" id=toc-250>ابن الرفعة سنة ٧١٠ هجرية</span>

أحمد بن محمد بن على الأنصارى أبو العباس، فقيه شافعى كان محتسب القاهرة وناب فى الحكم وله كتب منها (الكفاية) فى شرح التنيه (والمطلب) فى شرح الوسيط.','1','229',NULL,NULL,'0');
INSERT INTO [b435] VALUES (241,'<span data-type="title" id=toc-251>الرملى سنة ١٠٠٤ هجرية</span>

محمد بن أحمد بن حمزة شمس الدين الرملى فقيه الديار المصرية فى عصره. ومرجعها فئ الفتوى يقال له الشافعى الصغير، نسبة إلى الرملة من قرى المنوفية بمصر مولده ووفاته بالقاهرة ولى افتاء الشافعية وصنف شروحا وحواشى كثيرة منها.. " نهاية المحتاج إلى شرح المنهاج".','1','231',NULL,NULL,'0');
INSERT INTO [b435] VALUES (242,'<span data-type="title" id=toc-252>ابن راهويه</span>

إسحاق بن إبراهيم بن مخلد الحنظلى المروزى المعروف بابن راهويه أحد أعلام نيسابور نقل عنه أنه أملى أحد عشر ألف حديث من حفظة وكان فقيها ومحدثا.','1','232',NULL,NULL,'0');
INSERT INTO [b435] VALUES (243,'<span data-type="title" id=toc-253>الرحمتى سنة ١٢٠٥ هجرية</span>

مصطفى بن محمد بن رحمة الله الأيوبى الأنصارى فقيه دمشقى من علماء الحنفية مات بجهة السيل أثناء الحج ودفن بمكة من كتبه حاشية على مختصر شرح التنوير للعلائى وحاشية كل المنح واختصر شرح الشهاب الخفاجى عل الشفا.','1','237',NULL,NULL,'0');
INSERT INTO [b435] VALUES (244,'<span data-type="title" id=toc-254>الرشيدى سنة ١٠٦٩ هجرية</span>

أحمد بن عبد الرزاق بن محمد الرشيدى مغربى الأصل مولده ووفاته برشيد بمصر وكان شيخ الشافعية فيها له كتب منها حاشية عن شرح المنهاج للرملى.','1','234',NULL,NULL,'0');
INSERT INTO [b435] VALUES (245,'<span data-type="title" id=toc-255>الرويانى سنة ٤٥٠ هجرية</span>

أحمد بن محمد بن أحمد الرويانى أبوالعباس فقيه شافعى من أهل رويان بنواحى طبرستان له الجرجانيات.','1','233',NULL,NULL,'0');
INSERT INTO [b435] VALUES (246,'<span data-type="title" id=toc-256>الرويانى سنة ٥٠٥ هجرية</span>

شريح بن عبد الكريم بن أحمد الرويانى أبو نصر فقيه شافعى ولى القضاء فى آمل طبرستان ومن كتبة روضة الأحكام وزينة الحكام فى أدب القضاء.','1','235',NULL,NULL,'0');
INSERT INTO [b435] VALUES (247,'<span data-type="title" id=toc-257>الرويانى سنة ٥٠٢ هجرية</span>

عبد الواحد بن إسماعيل بن أحمد أبو المحاسن فخر الإسلام الرويانى فقيه شافعى بنى بآمل طبرستان مدرسة وتنقل فى البلاد ثم عاد إلى آمل وقتل بها وبلغ من تمكنه فى الفقه وحفظه له أن قال: لو احترقت كتب الشافعى لأمليتها. له تصانيف منها بحر المذهب من أصول كتب الشافعية وغيره.','1','236',NULL,NULL,'0');
INSERT INTO [b435] VALUES (248,'<span data-type="title" id=toc-258>الربيع</span>.

ابن حبيب بن عمرو الفراهيدى، عالم بالحديث، إباضى من أعيان المائة الثانية للهجرة من أهل البصرة له كتاب فى الحديث سماه الجامع الصحيح من أربعة أجزاء.','1','239',NULL,NULL,'0');
INSERT INTO [b435] VALUES (249,'<span data-type="title" id=toc-259>الزاهدى توفى سنة ٦٥٨ هجرية</span>

مختار بن محمود أبو الرجا نجم الدين الزاهدى القزوينى فقيه من أكابر الحنفية من أهل غزمين بخوارزم رحل إلى بغداد والروم. ومن كتبه الحاوى فى الفتاوى والمجتبى شرح مختصر القدورى فى الفقه.','1','241',NULL,NULL,'0');
INSERT INTO [b435] VALUES (250,'<span data-type="title" id=toc-261>الزبير بن العوام سنة ٣٦ هجرية</span>

هو الزبير بن العوام بن خويلد بن أسد بن عبد العزى بن قصى بن كلاب ابن مرة بن كعب بن لؤى بن غالب بن فهر بن مالك بن النضر بن كنانة وأمه صفية بنت عبد المطلب عمة رسول الله ويكنى أبا عبيد الله. وهو أحد العشرة المبشرين بالجنة وأحد أصحاب الشورى.','1','240',NULL,NULL,'0');
INSERT INTO [b435] VALUES (251,'<span data-type="title" id=toc-262>ابن الزبير توفى سنة ٧٣ هجرية</span>

عبد الله بن الزبير بن العوام القرشى الأسدى أول مولود فى المدينة بعد الهجرة بويع له بالخلافة سنه ٦٤ هجرية وكانت له مع الأمويين وقائع هائلة انتهت بقتله وصلبه، رضى الله عنه','1','245',NULL,NULL,'0');
INSERT INTO [b435] VALUES (252,'<span data-type="title" id=toc-263>ابو زرعه توفى سنة ٣٠٢ هجرية</span>

محمد بن عثمان بن إبراهيم بن زرعه من موالى ثقيف من أهل دمشق ولى القضاء بمصر ثم عاد إلى دمشق فولى قضاءها إلى أن توفى بها','1','244',NULL,NULL,'0');
INSERT INTO [b435] VALUES (253,'<span data-type="title" id=toc-264>الزركشى سنة ٧٩٤ هجرية</span>

محمد بن بهادر بن عبد الله الزركشى،. أبو عبد الله عالم بفقه الشافعية والأصول تركى الأصل- مصرى المولد والوفاة، له تصانيف كثيرة منها " لقطة العجلان، والبحر المحيط، والمنثور " ويعرف بقواعد الزركشى.','1','243',NULL,NULL,'0');
INSERT INTO [b435] VALUES (254,'<span data-type="title" id=toc-265>الزعفرانى سنة ٢٥٩</span>

الحسن بن محمد بن الصباح البزار الزعفرانى البغدادى فقيه من رجا ل الحديث، ثقة كان روايا للإمام الشافعى.','1','246',NULL,NULL,'0');
INSERT INTO [b435] VALUES (255,'<span data-type="title" id=toc-266>زفر توفى سنة ١٥٨ هجرية</span>

زفر بن الهذيل بن قيس العنبرى فقيه كبير من أصحاب أبى حنيفة أصله من أصبهان أقام بالبصرة وولى قضاءها وتوفى بها، كان من أصحاب الحديث فغلب عليه الرأى.','1','247',NULL,NULL,'0');
INSERT INTO [b435] VALUES (256,'<span data-type="title" id=toc-267>زكريا الانصارى سنة ٩٢٦ هجرية</span>

أبو يحيى شيخ الإسلام زكريا بن محمد بن زكريا الأنصارى السفيكى المصرى الشافعى قاض مفسر من حفاظ الحديث ولد فى سفيكة " بشرقية مصر " ولاه السلطان قايتباى القضاء بمصر بعد إلحاح، له مؤلفات كثيرة منها " تنقيح تحرير اللباب فى الفقه. وغاية الوصول فى الأصول ".','1','248',NULL,NULL,'0');
INSERT INTO [b435] VALUES (257,'<span data-type="title" id=toc-268>أبو الزناد</span>

عبد الله بن ذكوان القرشى المدنى محدث كبير قال عنه مصعب الزبيرى كان فقيه أهل المدينة وكان صاحب كتابة وحساب.','1','250',NULL,NULL,'0');
INSERT INTO [b435] VALUES (258,'<span data-type="title" id=toc-269>الزهرى سنة ١٢٤ هجرية</span>

ابو بكر محمد بن مسلم بن عبيد الله بن شهاب الزهرى من بنى زهرة ابن كلاب من قريش " أول من دون الحديث واحد كبار الحفاظ والفقهاء. تابعى من أهل المدينة كان يحفظ كثيرا جدا بالنسبة لعصره من الأحإديث مات بشعب آخر حد الحجاز.','1','249',NULL,NULL,'0');
INSERT INTO [b435] VALUES (259,'<span data-type="title" id=toc-270>(القاضى) زيد</span>

زيد بن محمد الكلاوى الجيلى علامة الزيدية وحافظ أقوالهم وفقيههم قال فى الانتصار: كان من اتبأع المؤيد بالله ولم يعاصره وكان من حفاظ الألفاظ العترة له العناية العظمى فى خدمة المذهب الزيدى.','1','254',NULL,NULL,'0');
INSERT INTO [b435] VALUES (260,'<span data-type="title" id=toc-271>زيد بن أرقم توفى سنة ٦٨ هجرية</span>

زيد بن أرقم الخزرجى الأنصارى صحابى غزا مع النبى صلى الله عليه وسلم سبع عشرة غزوة وشهد صفين مع على ومات بالكوفة روى له البخارى ومسلم سبعين حدبثا.','1','251',NULL,NULL,'0');
INSERT INTO [b435] VALUES (261,'<span data-type="title" id=toc-272>زيد بن ثابت سنة ٤٥ هجرية</span>

زيد بن ثابت بن الضحاك الأنصارى الخزرجىأبو خارجة صحابى من اكابرهم كان أحد كتاب الوحى ولد فى المدينة ونشأ بمكة وهاجر مع النبى صلى الله عليه وسلم وتعلم وتفقه فى الدين وكان رأسا بالمدينة فى القضاء والفتوى والفرائض وكان كاتب مصحف أبى بكر وعثمان حين جهز المصاحف للأمصار.','1','252',NULL,NULL,'0');
INSERT INTO [b435] VALUES (262,'<span data-type="title" id=toc-273>زيد بن على سنة ١٢٢ هجرية</span>

زيد بن على بن الحسينى بن على بن أبى طالب، ويقال له زيد الشهيد كانت إقامته بالكوفه وقرأ على واصل بن عطاء رأس المعتزلة وأنتقل الى الشام فضيق عليه هشام بن عبد الله وحبسه خمسة أشهر وعاد الى العراق ثم الى المدينة ثم رجع الى الكوفة سنة ١٢٠ فبايعه كثير من أهلها على الدعوة الى الكتاب وال سنة وجهاد الظالمين قتل بالكوفة وحمل رأسه الى الشام ثم الى المدينة ثم الى مصر.','1','253',NULL,NULL,'0');
INSERT INTO [b435] VALUES (263,'<span data-type="title" id=toc-274>الزيلعى سنة ٧٤٣ هجرية</span>

عثمان بن على بن محمد، فخر الدين الزيلعى فقيه حنفى، قدم القاهرة سنة ٧٠٥ هجرية فأفتى وتوفى وتوفى فيها له تبيين الحقائق فى شرح كنز الدقائق " و " شرح الجامع الكبير " فى الفقة وغيرهما','1','255',NULL,NULL,'0');
INSERT INTO [b435] VALUES (264,'<span data-type="title" id=toc-275>أبو زكريا</span>

يحيى بن صالح إمام من أئمة الإباضية قرأ على عالم فى جربة اسمه الشيخ يوسف بن محمد وذكره صاحب النيل وأثنى عليه فى مقدمة كتابه وعرض عليه الكتاب ملقبا إياه بالأستاذ: أى المعلم الماهر.','1','262',NULL,NULL,'0');
INSERT INTO [b435] VALUES (265,'<span data-type="title" id=toc-276>ابن زياد القرن الثالث</span>

إبراهيم بن زياد فقية حنبلى معاصر للإمام أحمد ونقل عنه بعض المسائل.','1','256',NULL,NULL,'0');
INSERT INTO [b435] VALUES (266,'<span data-type="title" id=toc-277>زيد بن حارثة</span>

هو أبو أسامة زيد بن حارثة بن شرحيل صحابى هاشمى اشتهر بالولاء وهو أشهر موالى النبى صلى الله عليه وسلم. قيلى أنه أول عن أسلم. وقد هاجر مع النبى وشهد المشاهد المثسهورة وقد زوجه النبى مولاته أم أيمن فولدت له أسامة وتزوج زينب قبل النبى فى القصة المشهورة.','1','257',NULL,NULL,'0');
INSERT INTO [b435] VALUES (267,'<span data-type="title" id=toc-278>زروق توفى سنة ٨٩٩ هجرية</span>.

أحمد بن أحمد بن محمد البرنس الفاسى، فقيه محدث صوفى، من أهل فاس بالمغرب، تفقه بها وقرأ بمصر والمدينة من كتبه: شرح مختصر خليل فى فقه المالكية، وشرح رسالة أبى زيد القيراونى والقواعد فى التصوف وغيرها، توفى فى تكرين من أعمال طرابلس الغرب.','1','258',NULL,NULL,'0');
INSERT INTO [b435] VALUES (268,'<span data-type="title" id=toc-279>ابن الساعاتى سنة ٦٩٤</span>

أحمد بن على بن تغلب مظفر الدين ابن الساعاتى ولد فى بعلبك وأنتقل مع أبيه إلى بغداد فنشأ بها في المدرسة المستنصرية وتولى تدريس الحنفية هناك له مؤلفات منها مجمع البحرين وشرحه وجامع النظام فى أصوال الفقه','1','260',NULL,NULL,'0');
INSERT INTO [b435] VALUES (269,'<span data-type="title" id=toc-280>السبكى توفى سنة ٣٥٦ هجرية</span>

تقى الدين السبكى على بن عبد الكافى بن على بن تمام السبكى الأنصاري الخزرجى شيخ الإسلام فى عصره وهو والد التاج السبكى صاحب الطبقات ولد فى سبك. من أعمال المنوفية بمصر وأنتقل إلى القاهرة ثم إلى الشام وولى قضاه الشام وعاد إلى القاهرة فتوفى بها. وله مختصر طبقات الفقهاء والمسائل الحلبية وأجوبتها فى فقه الشافعية.','1','261',NULL,NULL,'0');
INSERT INTO [b435] VALUES (270,'<span data-type="title" id=toc-282>إبن السبكى سنة ٧٧١ هجرية</span>

عبد الوهاب بن على بن عبد الكافى السبكى أبو نصر قاضى القضاه المؤرخ الباحث ولد فى القاهرة وانتقل إلى دمشق مع والده فسكنها وتوفى بها نسبه الى "سبك" من أعمال المنوفية بمصر، وهو صاحب طبقات الشافعية: جامع الجوامع.','1','259',NULL,NULL,'0');
INSERT INTO [b435] VALUES (271,'<span data-type="title" id=toc-283>سحنون سنة ٢٤٠ هجرية</span>

عبد السلام بن سعيد بن حبيب التنوخى- قاض- فقيه، انتهت إليه رياسة العلم فى المغرب، حمصى الأصل ومولده فى القيروان وولى القضاء ب هجريةا واستمر إلى أن مات','1','263',NULL,NULL,'0');
INSERT INTO [b435] VALUES (272,'<span data-type="title" id=toc-284>السرخسى سنة ٤٨٣ هجرية</span>

محمد بن أحمد بن سهل أبو بكر شمس الأئمة قاض من كبار الأحناف مجتهد من أهل سرخس فى "خرآسان " أشهر كتبه المبسوط فى الفقة وله شرح الجامع الكبير للامام محمد وشرح السير الكبير وشرح مختصر الطحاوى وسكن فرغانه فى آخر حياته حتى توفى بها.','1','264',NULL,NULL,'0');
INSERT INTO [b435] VALUES (273,'<span data-type="title" id=toc-285>ابن سريج سنة ٣٠٦ هجرية</span>

أحمد بن عمر بن سريج فقيه الشافعية فى عصره، مولده ووفاته ببغداد له نحو أربعمائة مصنف ولى القضاء بشيراز.','1','266',NULL,NULL,'0');
INSERT INTO [b435] VALUES (274,'<span data-type="title" id=toc-286>ابو السعود سنة ٩٨٢ هجرية</span>

محمد بن محمد بن مصطفى العمادى من علماء الترك المستعمرين ولد بقسطنطينية ودرس فى بلاد متعددة وتقلد القضاء فى بروسة فالقسطنطينة فالربع وأضيف إليه الإفتاء. له مؤلفات منها التفسير المشهور.','1','268',NULL,NULL,'0');
INSERT INTO [b435] VALUES (275,'<span data-type="title" id=toc-287>سعيد بن جبير سنة ٩٥ هجرية</span>

سعيد بن جبير الأسدى بالولاء تابعى كان أعلمهم على الاطلاق حبشى الأصل من الموالى أخذ عن ابن عباس وا بن عمر قتله الحجاج بواسط.','1','265',NULL,NULL,'0');
INSERT INTO [b435] VALUES (276,'<span data-type="title" id=toc-288>أبو سعيد الخدرى سنة ٥١ هجرية</span>

سعد بن مالك بن سنان الخدرى الأنصارى الخزرجى أبو سعيد صحابى كان من ملازمى النبى صلى الله عليه وسلم وروى عنه أحاديث كثيرة وغزأ اثنتى عثسرة غزوة وله فى الصحيحين أحاديث كثيرة توفى بالمدينة','1','267',NULL,NULL,'0');
INSERT INTO [b435] VALUES (277,'<span data-type="title" id=toc-289>سعيد بن زيد توفى سنة ٥١ هجرية</span>

سعيد بن زيد بن عمرو صحابى من خيار الصحابة شهد المشاهد كلها الا بدرا وهو أحد العشرة المبشرين بالجنة ولد بمكة وتوفى بالمدينة وله فى الصحيحين ٤٨ حديثا.','1','271',NULL,NULL,'0');
INSERT INTO [b435] VALUES (278,'<span data-type="title" id=toc-290>سعيد بن المسيب سنة ٩٤ هجرية</span>

سعيد بن المسيب بن حزن بن آبى وهب المخزومى القرشى أحد فقهاء المدينة السبعة كان يعش من تجارة الزيت وكان أحفظ الناس بأحكام عمر بن الخطاب وأقضيته حتى سمى راويه عمر توفى بالمدينة.','1','269',NULL,NULL,'0');
INSERT INTO [b435] VALUES (279,'<span data-type="title" id=toc-291>سفيان بن عيينة سنة ١٩٨ هجرية</span>

هو سفيان بن عيينة بن ميمون الهلالى الكوفى أبو محمد محد ث الحرم المكى من الموالى ولد بالكوفة وسكن مكة وتوفى بها ما كان حافظة فقيها واسع العلم.','1','270',NULL,NULL,'0');
INSERT INTO [b435] VALUES (280,'<span data-type="title" id=toc-292>أبو سفيان سنة ٣١ هجرية</span>

هو صخر بن حرب بن أمية بن عبد شمس صحابى من سادات قريش فى الجاهلية وهو والد معاوية رأس الدولة الأموية أسلم يوم الفتح وأبلى بعد إسلامه وشهد حنينا والطائف واليرموك وغيرها وتوفى بالمدينة.','1','272',NULL,NULL,'0');
INSERT INTO [b435] VALUES (281,'<span data-type="title" id=toc-293>أم سلمة</span>

هند بنت سهيل بن مغيرة القرشية من زوجات النبى صلى الله عليه وسلم تزوجها فى ال سنة الرابعة وكانت زوجة من قبل لآبى سلمة بن عبد الأسد بن المغيرة وهاجرت معه إلى الحبشة ثم إلى المدينة ولما مات بالمدينة من أثر جرح تزوجها الرسول.','1','273',NULL,NULL,'0');
INSERT INTO [b435] VALUES (282,'<span data-type="title" id=toc-294>سليمان بن شعيب</span>

سليمان بن شعب بن الليث بن سعد المصرى روى عن ابن لهيعه، قال ابن يونس روى مناكير، وقال العقيلى حديثه غير محفوظ.','1','274',NULL,NULL,'0');
INSERT INTO [b435] VALUES (283,'<span data-type="title" id=toc-295>ابن السيد سنة ٥٢١ هجرية</span>

عبد الله بن محمد بن السيد البطليوسى من علماء اللغة والأداب ولد فى بطليوس ونشأ بها وانتقل إلى بلنسية فسكنها وتوفى بها، وكتابه فى أسباب الاختلاف يسمى الانصاف فى التنبيه على الأسباب التى أوجبت الخلاف بين المسلمين وله كتاب الاقتضاب فى اللغة.','1','282',NULL,NULL,'0');
INSERT INTO [b435] VALUES (284,'<span data-type="title" id=toc-296>ابن سيرين توفى سنة ٩١١ هجرية</span>

محمد بن سيرين البصرى الأنصارى بالولاء أبو بكر إمام وقته فى علوم الدين بالبصرة تابع ولد ومات بالبصرة اشتهر بالورع وتعبير الرؤيا.','1','275',NULL,NULL,'0');
INSERT INTO [b435] VALUES (285,'<span data-type="title" id=toc-297>السيوطى توفى سنة ٩١١ هجرية</span>

عبد الرحمن بن ابى بكر بن محمد بن سابق الدين الخصيرى السيوطى جلال الدين إمام حافظ مؤرخ أديب له نحو ستمائة مصنف نشأ فى القاهرة يتيما وبها توفى ومن أشهر مؤلفاته الإتقان فى علوم القرآن والأشباه والنظائر فى العربية، والأشباه والنظائر فى فروع الشافعية والحاوى للفتاوى.','2','120',NULL,NULL,'0');
INSERT INTO [b435] VALUES (286,'<span data-type="title" id=toc-298>سالم سنة ١٠٦،١٠٨ هجرية</span>

سالم بن عبد الله بن عمر بن الخطاب أحد فقهاء المدينة من سادات التابعين وعلمائهم وثقاتهم روى عن أبيه وغيره وروى عنه الزهرى ونافع.','1','276',NULL,NULL,'0');
INSERT INTO [b435] VALUES (287,'<span data-type="title" id=toc-299>سعد بن أبى وقاص</span>

سعد بن مالك بن أهيب أحد العشرة المبشرين بالجنة روى عن النبى صلى الله عليه وسلم كثيرا وروت عنه عائشة وابن عباس وأبن عمر.','2','121',NULL,NULL,'0');
INSERT INTO [b435] VALUES (288,'<span data-type="title" id=toc-300>السكونى</span>

إسماعيل بن مسلم أبى زياد السكونى الشعيرى قاضى الموصل روى عن الصادق مختلف فى مذهبه فذهب جماعة إلى كونه من الإمامية ونقل الإمامية الإجماع على العمل بروايته.','1','278',NULL,NULL,'0');
INSERT INTO [b435] VALUES (289,'<span data-type="title" id=toc-301>السكونى</span>

عبد الله بن عجلان السكونى محدث عن أبى جعفر الصدوق وجاء فى المستدرك أن الصدوق من رواة الأحاديث المرسلة عنه لأنه لم يذكر فى طريقه إليه فى المشيخة.','1','277',NULL,NULL,'0');
INSERT INTO [b435] VALUES (290,'<span data-type="title" id=toc-302>سلمان بن عامر صحابى</span>

سلمان بن عامر بن أوس بن حجر بن عمرو بن الحارث صحابى نزل البصرة روى عنه محمد وحفصة ولدا سيرين وغيرهما، وروى له البخارى حديثا واحدا.','1','279',NULL,NULL,'0');
INSERT INTO [b435] VALUES (291,'<span data-type="title" id=toc-303>ابن سلمون سنة ٧٤١ هجرية</span>

عبد الله بن على بن عبد الله بن على بن سلمون الكنانى أبو محمد ولد بغرناطة وتوفى فى رقعة طريف من كتبه الشافى فى تحرير ماوقع من الخلاف بين التبصرة والكافى فى فروع المالكية.','1','280',NULL,NULL,'0');
INSERT INTO [b435] VALUES (292,'<span data-type="title" id=toc-304>سهيل سنة ١٨ هجرية</span>

سهيل بن عمرو بن عبد شمس القرشى خطيب قريشى وأحد سادتها فى الجاهلية أسره المسلمون يوم بدر حتى أسلم بمكة يوم الفتح وسكنها ثم سكن المدينة وهو الذى تولى أمر الصلح بالحديبية مات بالشام.','1','281',NULL,NULL,'0');
INSERT INTO [b435] VALUES (293,'<span data-type="title" id=toc-305>سودة بنت زمغة سنة ٥٤ هجرية</span>

سودة بنت زمعة بنت قيس بن عبد شمس إحدى زوجات النبى تزوجها النبى بعد وفاة زوجها السكران وبعد وفاة خديجة وتوفيت بالمدينة.','1','283',NULL,NULL,'0');
INSERT INTO [b435] VALUES (294,'<span data-type="title" id=toc-306>سالم بن عبد الله بن عمر توفى سنة ١٠٦ هجرية</span>.

سالم بن عبد الله بن عمر بن الخطاب، أحد فقهاء المدينة، من سادات التابعين وعلمائهم وثقاتهم، روىعن أبيه وغيره، وروى عنه الزهرى ونافع توفى بالمدينة','1','285',NULL,NULL,'0');
INSERT INTO [b435] VALUES (295,'<span data-type="title" id=toc-307>السالمى توفى سنة ١٣٣٢ هجرية</span>.

عبد الله بن حميد سلوم السالمى، أبو محمد، مؤرخ فقيه، من أعيان الإباضية، انتهت إليه رياسة العلم عندهم فى عصره، ولد ومات فى عمان، له تصانيف كثيرة، منها: جوهر النظام فى علمى الأديان والأحكام وتحفة الأعيان فى تاريخ عمان وحاشية الجامع الصحيح للربيع بن حبيب، وطلعة الشمس وشرحها فى أصول الفقه.
سبيعة الأسلمية. سبيعة بنت الحارث الأسلمية، صحابية جليلة روت عن رسول الله صلى الله عليه وسلم أحاديث وروى عنها عبد الله بن عمر وزفر بن أوس ومسروق وغيرهم.','1','304',NULL,NULL,'0');
INSERT INTO [b435] VALUES (296,'<span data-type="title" id=toc-308>سعد بن عبادة توفى سنة ١٦ هجرية</span>.

سعد بن عبادة بن دليم الأنصارى، سيد الخزرج، يكنى أبا سالم، شهد العقبة وكان أحد النقباء وكان مشتهرا بالجود هو وأبوه وجده، وكان يحمل راية الأنصار فى المعارك، ومات ببصرى وهى أول مدينة فتحت بالشام.','1','284',NULL,NULL,'0');
INSERT INTO [b435] VALUES (297,'<span data-type="title" id=toc-309>سعد بن معاذ توفى سنة ٥ هجرية</span>.

سعد بن معاذ بن النعمان الأوسى الأنصارى، صحابى من الأبطال، كان سيد الأوس، وحمل لواءهم يوم بدر ورمى بسهم يوم الخندق فمات من أثر جراحه ودفن بالبقيع.','1','305',NULL,NULL,'0');
INSERT INTO [b435] VALUES (298,'<span data-type="title" id=toc-310>سعيد بن عبد الملك توفى سنة ١٣٢ هجرية</span>.

سعيد بن عبد الملك بن مروان، من أهل دمشق، كان حسن السيرة متعبدا، وقد ولى الغزو فى خلافة أخيه هشام، وكان عاملا على الموصل، وكان يقال له سعيد الخير، قتل قرب الرملة بفلسطين.
سعيد بن منصور توفى سنة ٢٢٧ هجرية. سعيد بن منصور الخرسانى أبو عثمان، الحافظ صاحب السير، روى عن فليح بن يسار بن سليمان وشريك وطبقتهما، وجاور بمكة وفيها مات، فى رمضان، وقد روى البخارى عن رجل عنه.','1','290',NULL,NULL,'0');
INSERT INTO [b435] VALUES (299,'<span data-type="title" id=toc-311>أبو سعيد البردعى توفى سنة ٣١٧ هجرية</span>.

أحمد بن الحسين القاضى أبو سعيد أخذ عن إسماعيل بن حماد بن أبى حنيفة عن أبيه عن جده، وتفقه على أبى على الدقاق وغيرهما، انتهت إليه مشيخة الحنفية ببغداد، وتفقه عليه خلق كثير منهم أبو الحسن الكرخى وأبو عمرو الطبرى وأبوطاهر الدباسى خرج إلى الحج فقتل بمكة فى واقعة القرامطة، وفى العقد الثمين أنه حسن بن أحمد البردعى.','1','286',NULL,NULL,'0');
INSERT INTO [b435] VALUES (300,'<span data-type="title" id=toc-312>سكينة بنت الحسين توفيت سنة ١١٧ هجرية</span>

سكينة بنت الحسين بن على بن أبى طالب، كانت سيدة نساء عصرها، تجالس الأجلة من قريش ويجتمع إليها الشعراء، وكانت إقامتها ووفاتها بالمدينة.','1','288',NULL,NULL,'0');
INSERT INTO [b435] VALUES (301,'<span data-type="title" id=toc-313>سلمان توفى سنة ٣٦ هجرية</span>.

سلمان الفارسى، صحابى من مقدميهم عاش عمرا طويلا، وقد رحل إلى الشام والموصل، وهو المشهور بأنه أشار على الرسول صلى الله عليه وسلم بحفر الخندق فى غزوة الأحزاب، وقد صار أميرا على المدائن فأقام بها وتوفى فيها.','1','289',NULL,NULL,'0');
INSERT INTO [b435] VALUES (302,'<span data-type="title" id=toc-314>أبو سلمة</span>

عبد الله بن عبد الأسد بن هلال بن عمر زوج أم سلمة، صحابى، وكان قديم الإسلام، وهاجر إلى
الحبشة ثم إلى المدينة، وشهد بدرا وأحدا، ثم جرح بها فمات من جراحته.','1','287',NULL,NULL,'0');
INSERT INTO [b435] VALUES (303,'<span data-type="title" id=toc-315>أم سليم توفيت سنة ٣٠ هجرية</span>.

أم سليم بنت ملحان بن خالد بن زيد بن حرام الأنصارية، واختلف فى اسمها، وهى أم أنس بن مالك خادم الرسول صلى الله عليه وسلم، وكانت هى وأختها خالتين لرسول الله من جهة الرضاع.','1','291',NULL,NULL,'0');
INSERT INTO [b435] VALUES (304,'<span data-type="title" id=toc-316>سماعة</span>

سماعة بن مهران الحضرمى الكوفى يكنى أبا محمد، له كتاب، روى عن أبى عبد الله الصادق، وقال
عنه صاحب نتائج التنقيح، ثقة على الأقوى، مات بالمدينة.','1','306',NULL,NULL,'0');
INSERT INTO [b435] VALUES (305,'<span data-type="title" id=toc-317>ابن السمعانى توفى سنة ٥١٠ هجرية</span>.

محمد بن منصور بن عبد الجبار التميمى السمعانى أبو بكر، فقيه محدث، له علم بالتاريخ والأنساب وله كتب فى الحديث والوعظ، مولده ووفاته وهو والد عبد الكريم صاحب الأنساب. بمرو من كتبة الأمالى مائة وأربعون مجلسا،','1','292',NULL,NULL,'0');
INSERT INTO [b435] VALUES (306,'<span data-type="title" id=toc-318>ابن السمعانى توفى سنة ٥٦٢ هجرية</span>.

عبد الكريم بن محمد بن منصور التميمى السمعانى مؤرخ، رحاله من حفاظ الحديث، مولده ووفاته بمرو من كتبة الأنساب وتذييل تاريخ بغداد وتاريخ وفاته للمتأخرين من الرواة، وله كتاب تبيين معادن المعانى فى لطائف القرآن الكريم.','1','293',NULL,NULL,'0');
INSERT INTO [b435] VALUES (307,'<span data-type="title" id=toc-319>سند توفى سنة ٥٤١ هجرية</span>.

سند بن عفان إبراهيم بن حريز الأزدى كنيته أبو على، سمع من شيخه أبى بكر الطرطوشى، وكان من زهاد العلماء، فقيها فاضلا، شرح المدونة فى كتاب أسماه الطراز، يقع فى ثلاثين سفرا، وتوفى قبل إكماله، وقد جلس مجلس الدرس خلفا لشيخه.','1','294',NULL,NULL,'0');
INSERT INTO [b435] VALUES (308,'<span data-type="title" id=toc-320>سهل بن سعد توفى سنة ٩١ هجرية</span>.

سهل بن سعد بن مالك بن خالد بن ثعلبة الخزرجى الأنصارى من بنى ساعدة، صحابى من مشاهيرهم، من أهل المدينة،عاش نحو مائة سنة، له فى الصحيحين مائة وثمانية وثمانون حديثا، وعن قتادة أنه مات بمصر.','1','295',NULL,NULL,'0');
INSERT INTO [b435] VALUES (309,'<span data-type="title" id=toc-321>ابن الشاط سنة ٧٢٣</span>

عمدة المحققين سراج الدين أبو القاسم قاسم بن عبد الله الأنصارى ولد سنة ٦٤٣ هجرية وهو فقيه اصولى.','1','297',NULL,NULL,'0');
INSERT INTO [b435] VALUES (310,'<span data-type="title" id=toc-322>الشاطبى توفى سنة ٧٩٠ هجرية</span>

إبراهيم بن موسى بن محمد اللخمى الغرناطى الشهير بالشاطبى، أصولى حافظ من أهل غرناطة كان من ائمة المالكية وهو صاحب الموافقات فى أصول الفقة، والمجلى شرح كتاب البيوع من صحيح البخارى.','1','298',NULL,NULL,'0');
INSERT INTO [b435] VALUES (311,'<span data-type="title" id=toc-324>الشافعى سنة ٢٠٤ هجرية</span>

محمد بن إدريس بن العباس القرشى أبو عبد الله أحد الأئمة الأربعة ولد فى غزة وحمل منها طفلا إلى مكة ونشأ بالبادية وتعلم الشعر والفروسية: تفقه على مالك بالمدينة وذهب إلى بغداد مرتين تم قصد مصر وتوفى بها','1','296',NULL,NULL,'0');
INSERT INTO [b435] VALUES (312,'<span data-type="title" id=toc-325>الشبراملسى سنة ١٠٨٧ هجرية</span>

على بن على الشبراملسى أبو الضياء نور الدين فقيه شافعى مصرى تعلم وعلم بالأزهر: صنف كتبا منها حاشية على نهاية المحتاج وغيره.','1','300',NULL,NULL,'0');
INSERT INTO [b435] VALUES (313,'<span data-type="title" id=toc-326>ابو شجاع توفى سنة ٥٩٣ هجرية</span>

أحمد بن الحسين بن أحمد الأصفهانى الشهير بأبى شجاع. ولد سنه ٥٣٣ هجرية وتولى الوزارة ومات بالمدينة المنورة وله كتاب الإقناع المشهور فى فقه الشافعية.','1','299',NULL,NULL,'0');
INSERT INTO [b435] VALUES (314,'<span data-type="title" id=toc-327>شداد بن أوس سنة ٥٨ هجرية</span>

شداد بن اوس أبو يعلى الأنصارى وهو ابن أخى حسان بن ثابت نزل- بيت المقدس فعد من أهل الشام وتوفى بها.','1','301',NULL,NULL,'0');
INSERT INTO [b435] VALUES (315,'<span data-type="title" id=toc-328>الشرقاوى سنة ١٢٢٧</span>

عبد الله بن حجازى بن ابراهيم الشرقاوى الأزهرى فقيه من علماء مصر تعلم فى الأزهر وولى مشيخته وصنف كتبا منها فتح المجدى لشرح مختصر الزبيدى وحاشية على شرح التحرير فى فقه الشافعية وغير ذلك.','1','302',NULL,NULL,'0');
INSERT INTO [b435] VALUES (316,'<span data-type="title" id=toc-329>الشرنبلالى سنة ١٠٦٩</span>

حسن بن عمار بن على الشرنبلالى المصرى فقيه حنفى مكثر من التصنيف نسبته إلى شبرا بلو نقله والده منها إلى القاهرة فنشأ بها ودرس فى الأزهر وأصبح المعول عليه فى الفتوى، له نور الإيضاح ومراقى الفلاح وحاشية على درر الحكام لملاخسرو وتوفى بالقاهرة.','1','303',NULL,NULL,'0');
INSERT INTO [b435] VALUES (317,'<span data-type="title" id=toc-330>شريح توفى سنة ٧٨ هجرية</span>

شريح بن الحارث بن قيس الكندى من أشهر القضاة الفقهاء اصلا من اليمن ولى قضاء الكوفة فى زمن عمر وعثمان وعلى ومعاوية، أستعفى زمن الحجاج توفى بالكوفة.','1','310',NULL,NULL,'0');
INSERT INTO [b435] VALUES (318,'<span data-type="title" id=toc-331>شعبة ١٦٠ هجرية</span>

هو أبو بسطام شعبة بن الحجاج بن الورد العتكى من أئمة رجال الحديث حفظا ودراية ولد ونشأ بواسط وسكن البصرة الى أن توفى، له كتاب" الغرائب " فى الحديث','1','307',NULL,NULL,'0');
INSERT INTO [b435] VALUES (319,'<span data-type="title" id=toc-332>الشعبى سنة ١٠٥ هجرية</span>

عامر بن شرحبيل ولد بالكوفة وكان يكره الرأى سمع عليه أبو حنيفة الحديث ولى قضاء الكوفة','1','308',NULL,NULL,'0');
INSERT INTO [b435] VALUES (320,'<span data-type="title" id=toc-333>الشعرانى سنة ٩٧٣ هجرية</span>

عبد الوهاب بن أحمد بن على الحنفى- نسبه إلى محمد بن الحنفية- الشعرانى أبو محمد ولد فى قلقشندة بمصر وتوفى بالقاهرة له تصانيف كثيرة منها الميزان- ومدارك السالكين وإرشاد الطالبين إلى مراتب العلماء العاملين وغيرها.','1','309',NULL,NULL,'0');
INSERT INTO [b435] VALUES (321,'<span data-type="title" id=toc-334>الشلبى توفى سنة ١٠١٠ هجرية</span>

شهاب الدين أحمد بن يونس من علماء الحنفية له حاشية على شرح الكنز مطبوع على هامش تبيين الحقائق.','1','311',NULL,NULL,'0');
INSERT INTO [b435] VALUES (322,'<span data-type="title" id=toc-335>الشوكانى ١٢٥٠ هجرية</span>

محمد بن على بن محمد فقيه مجتهد من كبار علماء اليمن. من صنعاء ولد بهجرة شوكان ونشأ بصنعاء وولى قضاءها سنة ١٢٢٩ ومات حاكما ب هجريةا وكان يرى تحريم التقليد، من مؤلفاته، نيل الأوطار، والفوائد المجموعة فى الأحاديث الموضوعة، والدرر البهية فى المسائل الفقهية، وإرشاد الفحول فى الأصول وغيرها.','1','313',NULL,NULL,'0');
INSERT INTO [b435] VALUES (323,'<span data-type="title" id=toc-336>الشيخ زادة سنة ١٠٨٧ هجرية</span>

عبد الرحمن بن محمد بن سليمان المعررف بشيخى زاده. فقيه حنفى من آهل كليبولى بتركيا من قضاة الجيش له مجمع الأنهر فى شرح ملتقى الأبحر: ونظم الفرائد فى مسائل الخلاف فى التوحيد.','1','312',NULL,NULL,'0');
INSERT INTO [b435] VALUES (324,'<span data-type="title" id=toc-337>الشيرازى توفى سنة ٤٧٦ هجرية</span>

أبو أسحاق إبراهيم بن على بن يوسف ولد فى فيروزالأباد بفارس: وانتقل إلى شيراز فقرأ على علمائها، وانصرف إلى البصرة ثم إلى بغداد ونبغ فى علوم الشريعة وكان مفتى الأمة فى عصره له مؤلفات كثيرة.','1','314',NULL,NULL,'0');
INSERT INTO [b435] VALUES (325,'<span data-type="title" id=toc-338>ابن شاس سنة ٦١٦ هجرية</span>

عبد الله بن محمد بن نجم بن شاس بن نزار الجذامى السعدى المصرى جلال الدين أبو محمد شيخ المالكية فى عصره بمصر. من أهل دمياط مات فيها مجاهدا. من كتبه: الجواهر اليمنية فى فقه المالكية.','2','123',NULL,NULL,'0');
INSERT INTO [b435] VALUES (326,'<span data-type="title" id=toc-339>شرف الدين ابن إسماعيل سنة ١٢٢٣ هجرية</span>

شرف الدين بن إسماعيل بن محمد الحسينى اليمنى زيدى من أهل صنعاء له رسائل وأسئلة وأجوبة فى مجلد ضخم.','2','122',NULL,NULL,'0');
INSERT INTO [b435] VALUES (327,'<span data-type="title" id=toc-340>الشروانى</span>

على بن محمد على الزهرى الشروانى المدنى رئيس علماء الحنفية فى عصره بالمدينة له حاشية على ديباجة الدرر وهوامش على المختصر','2','125',NULL,NULL,'0');
INSERT INTO [b435] VALUES (328,'<span data-type="title" id=toc-341>الشروانى سنة ١١٣٤ هجرية</span>

يوسف بن إبراهيم بن محمد أكمل الدين الزهرى الثسروانى فقيه حنفى ولد بشروان واشتهر وتوفى بالمدينة له كتب فى الفقه منها هدية الصحيح شرح مشكاة المصابيح وشرح ملتقى الأبحر.','2','124',NULL,NULL,'0');
INSERT INTO [b435] VALUES (329,'<span data-type="title" id=toc-342>ابن شمر</span>

عمرو بن شمر بن يزيد أبو عبد الله الجعفى الكوفى، من أصحاب الباقر والصادق، له كتاب رواه عنه إبراهيم بن سليمان الخزاز وروى عنه النضر بن سويد وغيرهما.','2','126',NULL,NULL,'0');
INSERT INTO [b435] VALUES (330,'<span data-type="title" id=toc-343>ابن شهاب توفى سنة ٣٥٠ هجرية</span>.

إبراهيم بن محمد، من علماء الكلام من أهل بغداد، له كتاب مجالس الفقهاء ومناظرتهم نحو ٤٠ورقة.','2','127',NULL,NULL,'0');
INSERT INTO [b435] VALUES (331,'<span data-type="title" id=toc-344>ابن شهاب توفى سنة ٤٢٨ هجرية</span>.

الحسن بن شهاب بن الحسن بن على القلبرى، من العلماء العارفين بالفقه والأدب له مصنفات فى
الفقه والفرائد.','2','128',NULL,NULL,'0');
INSERT INTO [b435] VALUES (332,'<span data-type="title" id=toc-346>الصادق سنة ١٤٨ هجرية</span>

أبو عبد الله جعفر الصادق بن محمد الباقر الهاشمى العلوى أحد ائمة الزيدية روى عنه مالك والثورى وإبن عيينه وقال أبو حنيفة عنه ما رأيت أفقه منه.','2','129',NULL,NULL,'0');
INSERT INTO [b435] VALUES (333,'<span data-type="title" id=toc-347>صالح بن أحمد توفى سنة ٢٥٦ هجرية</span>

هو صالح بن الامام أحمد بن حنبل وكنيته ابو الفضل ولد ببغداد ونشأ بين يدى أبيه الإمام أحمد وأخذ عنه ثم ولى القضاء بأصبهان وتوفى فيها.','1','315',NULL,NULL,'0');
INSERT INTO [b435] VALUES (334,'<span data-type="title" id=toc-348>الصاوى سنة ١٢٤١</span>

أحمد بن محمد الخلوتى الشهير با لصاوى فقيه مالكى نسبته الى"صا الحجر" فى محافظة الغربية وتوفى بالمدينة المنورة من كتبه " حاشيته على تفسير الجلالين وحواش على بعض كتب الشيخ أحمد الدردير فى فقه المالكية.','1','318',NULL,NULL,'0');
INSERT INTO [b435] VALUES (335,'<span data-type="title" id=toc-349>صدر الشريعة ٧٤٧ هجرية</span>

عبد الله بن مسعود بن محمود البخارى الحنفى صدر الشريعة الأصغر من صدر الشريعة الأكبر من علماء أصول الفقه والدين له كتاب التنقيح فى أصول الفقه وشرح التوضيح وكتاب شرح الوقاية فى فقه الأحناف توفى فى بخارى.','1','316',NULL,NULL,'0');
INSERT INTO [b435] VALUES (336,'<span data-type="title" id=toc-350>الصدر الشهيد</span>

عمر بن عبد العزيز بن عمر بن مازة أبو محمد برهان الأئمة حسام الدين المعروف بالصدر الشهيد من أكابر الحنفية من أهل خراسان وله كتب كثيرة منها الفتاوى الصغرى والفتاوى الكبرى وشرح الجامع الصغير قتل بسمرقند ودفن فى بخارى.','1','317',NULL,NULL,'0');
INSERT INTO [b435] VALUES (337,'<span data-type="title" id=toc-351>الصفتى</span>

عبده يوسف بن الشيخ سعيد الصفتى أحد علماء المالكية المتأخرين. له حاشية على شرح العلإمه ابن تركى على العشماوية فى فقة" المالكية','1','320',NULL,NULL,'0');
INSERT INTO [b435] VALUES (338,'<span data-type="title" id=toc-352>الصفدى ٧٦٤ هجرية</span>

صلاح الدين الصفدى خليل بن أيبك بن عبد الله الصفدى ولد فى صفد بفلسطين وتعلم فى دمشق وتوفى بها وله تصانيف كثيرة.','1','319',NULL,NULL,'0');
INSERT INTO [b435] VALUES (339,'<span data-type="title" id=toc-353>ابن الصلاح سنة ٦٤٣ هجرية</span>

عثمان بن عبد الرحمن صلاح الدين، بن موسى الشهرزورى المعروف بابن الصلاح ولد فى شرخان قرب شهرزور: انتقل إلى الموصل تم خراسان فبيت المقدس فدمشق وتوفى بها. له (شرح الوسيط) فى فقه الشافعية وأدب المفتى والمستفتى وطبقات فقهاء الشافعية وغيرها.','1','321',NULL,NULL,'0');
INSERT INTO [b435] VALUES (340,'<span data-type="title" id=toc-354>الصنعانى سنة ١١٨٢ هجرية</span>

محمد بن اسماعيل (الأمير اليمنى الصنعانى صاحب كتاب " سبل السلام " شرح بلوغ المرام لابن حجر الهيثمى وهو من فقهاء الزيدية.','2','130',NULL,NULL,'0');
INSERT INTO [b435] VALUES (341,'<span data-type="title" id=toc-355>الصيرفى سنة ٣٣٠ هجرية</span>

- أبو بكر محمد بن عبد الله المعروف بالصيرفى الفقيه الشافعى البغدادى كان عالما فى أصول الفقه حتى قال عنه القفال: انه كان أعلم الناس بأصول الفقه بعد الشافعى ومن كتبه البيان فى دلائل الأعلام على أصول الأحكام كتاب الفرائض، والصيرفى نسبة مشهورة لمن يصرف الدراهم والدنانير.','2','131',NULL,NULL,'0');
INSERT INTO [b435] VALUES (342,'<span data-type="title" id=toc-356>ابن الصباغ</span>

أبو نصر بن الصباغ الفقيه عبد السيد بن محمد البغدادى الشافعى أحد الأئمة ومؤلف الشامل كان نظيرا لأبى إسحاق وكان حجة ولى القطامية توفى ببغداد.','2','132',NULL,NULL,'0');
INSERT INTO [b435] VALUES (343,'<span data-type="title" id=toc-357>الصدوق سنة ٣٨١ هجرية</span>

محمد بن على بن الحسين بن بايويه القمى أبو جعفر من كبار المحدثين والفقهاء عند الشيعة الإمامية نشأ فى قم وتعلم بها وقد نبغ وأحصى له أكثر من مائتى مؤلف مابين مطبوع ومخطوط ومن كتبه من لايحضره الفقيه توفى بالرى ودفن بها','1','325',NULL,NULL,'0');
INSERT INTO [b435] VALUES (344,'<span data-type="title" id=toc-358>الصفار سنة ٢٦٥ هجرية</span>

يعقوب بن الليث الصفار أبو يوسف من ابطال العالم وأحد الأمراء الدهاة الكبار. تزهد وغلب على سجستان وامتلك كرمان وشيراز واستولى على فارس توفى بجند يسابور.','2','133',NULL,NULL,'0');
INSERT INTO [b435] VALUES (345,'<span data-type="title" id=toc-359>صعصعة بن صوحان توفى سنة ٦٠ هجرية</span>.

صعصة بن صوحان بن حجر بن الحارث العبدى من سادات عبد القيس من أهل الكوفة، كان خطيبا بليغا شهد صفين مع على رضى الله عنه مات فى البحرين، وقيل مات فى الكوفة.','2','134',NULL,NULL,'0');
INSERT INTO [b435] VALUES (346,'<span data-type="title" id=toc-361>ضباعة بنت الزبير</span>

هى ضباعة بنت الزبير بن عبد المطلب الهاشمية، بنت عم النبى عليه الصلاة والسلام كانت زوج المقداد بن الأسود روت عن النبى صلى الله عليه وسلم وعن زوجها المقداد وروت عنها أم عباس وعائشة والزهرى.','1','322',NULL,NULL,'0');
INSERT INTO [b435] VALUES (347,'<span data-type="title" id=toc-363>أبو طالب الأملى سنة ٤٢٤</span>

يحيى بن الحسين بن هارون أبو طالب، له مؤلفات وله تخريجات على مذهب الهادى وكان يرى أن مالا يوجد للهادى فيه فمذهبه كأبى حنيفة بويع بالخلافة بعد موت أخيه المؤبد بالله وتوفى بآمل طبرستان.','1','323',NULL,NULL,'0');
INSERT INTO [b435] VALUES (348,'<span data-type="title" id=toc-364>الطباطبائى</span>

السيد محسن الطباطبائى الحكيم آيه الله العظمى من كبار فقهاء، الإمامية المعاصرين بالنجف الأشرف- له مستمسك العروة الوثقى وغيره','1','324',NULL,NULL,'0');
INSERT INTO [b435] VALUES (349,'<span data-type="title" id=toc-365>الطبرانى ٣٦٠ هجرية</span>

الحافظ سليمان بن أحمد الشامى اللخمى الطبرانى ولد بطبرية بالشام سنة ٢٦٠ هجرية وانتقل الى الحرمين واليمن ومصر وبغداد والكوفة وغيرها طلبا للحديث وأقام فى الرحلة ثلاثة وثلاثين سنة','1','327',NULL,NULL,'0');
INSERT INTO [b435] VALUES (350,'<span data-type="title" id=toc-367>الطبرسى سنة ٥٤٨ هجرية</span>

أمين الدين أبو الفضل بن الحسن بن الفضل مفسر محقق لغوى من أجلاء الإمامية منسوب إلى طبرستان له مجمع البيان فى تفسير القرآن توفى " سيزوار " ونقل الى مشهد الرضوى','1','326',NULL,NULL,'0');
INSERT INTO [b435] VALUES (351,'<span data-type="title" id=toc-368>الطحاوى سنة ٣٢١ هجرية</span>

أحمد بن محمد بن سلامة بن سلمة الأزدى الطحاوى أبو جعفر فقيه انتهت إليه رياسة" الحنفية بمصر، ولد ونشأ فى " طحا " من قرى مصر وتفقه على مذهب الشافعى ثم تحول حنفيا رحل الى الشام وتوفى بالقاهرة له المختصر فى الفقه والاختلاف بين الفقهاء.','1','330',NULL,NULL,'0');
INSERT INTO [b435] VALUES (352,'<span data-type="title" id=toc-369>الطحطاوى توفى سنة ١٢١١ هجرية</span>

أحمد بن محمد بن إسماعيل ففيه حنفى اشتهر بكتابه حاشية الدر المختار ولد بطهطا بالقرب من اسيوط وتعين بالأزهر. ثم تقلد مشيخة الحنفية بمصر وتوفى بالقاهرة','1','328',NULL,NULL,'0');
INSERT INTO [b435] VALUES (353,'<span data-type="title" id=toc-370>الطوسى سنة ٤٦٠ هجرية</span>

"أبو جعفرمحمد بن الحسن بن على الطوسى مفسر، نعته السبكى بفقيه الشيعة ومصنفهم، انتقل من خراسان إلى بغداد وأقام أربعين سنة ورحل إلى الغرى (بالنجف) فاستقر إلى ان توفى له تصانيف كثيرة منها " تهذيب الأحكام " والتبيان الجامع لعلوم القرآن، ويطلق عليه فى كتب الامامية لقب " الشيخ ".','1','332',NULL,NULL,'0');
INSERT INTO [b435] VALUES (354,'<span data-type="title" id=toc-371>أبو طالب سنة ٢٤٤ هجرية</span>

أحمد بن حميد أبو طالب المشكانى المتخصص بصحبة الإمام أحمد بن حنبل وقد روى عنه مسائل كثيرة وكان أحمد يكرمه ويعظمه وقد صحب الإمام إلى أن مات وكان صالحا تعلم من إمامه مذهب القنوع والإحتراف.','1','331',NULL,NULL,'0');
INSERT INTO [b435] VALUES (355,'<span data-type="title" id=toc-372>أبو طالب سنة ٤٤٠ هجرية</span>

أحمد بن عبد الله بن سهل أبو طالب المعروف بابن البقال صاحب الفتيا والنظر سمع أبا العباس الهاشمى وأبا بكر بن شاذان ودرس الفقه على أبى عبد الله بن حامد وكانت له حلقة بجامع المنصور. توفى فى شهر ربيع الأول ودفن فى مقبرة الامام أحمد.','1','329',NULL,NULL,'0');
INSERT INTO [b435] VALUES (356,'<span data-type="title" id=toc-373>أبو طالب سنة ٢٤٠ هجرية</span>

عصمة بن أبى عصمة أبو طالب العكبرى فقيه حنبلى روى عن الامام أحمد أشياء وصحبه إلى أن مات وكان صالحا روى عن الإمام مسائل كثيرة كما ذكر ابن الخلال.','1','333',NULL,NULL,'0');
INSERT INTO [b435] VALUES (357,'<span data-type="title" id=toc-374>طاووس اليمانى سنة ١٠٦ هجرية</span>

أبو عبد الرحمن طاووس بن كيسان اليمانى الحميرى مولاهم وقيل الهدمانى مولاهم. من كبار التايعين والعلماء سمع ابن عباس وأن عمر وجابرا وغيرهم وروى عنه خلائق من التابعين واتفقوا على فضيلته ووفور علمه وحفظه وتثبته توفى بمكة.','1','335',NULL,NULL,'0');
INSERT INTO [b435] VALUES (358,'<span data-type="title" id=toc-375>طلحة بن عبيد الله صحابى</span>

هو طلحة بن عبيد الله بن عثمان بن عمرو بن كعب القرشى، ويكنى بأبى محمد ويعرف بطلحة الخير، وطلحة الفياض ولما قدم طلحة المدينة آخى الرسول بينه وبين كعب بن مالك، لم يشهد بدرا وشهد أحدا وما بعدها، ووقى رسول الله صلى الله عليه وسلم فى أحد وهو أحد العشرة المشهود لهم بالجنة وأحد الستة الذين جعل عمر فيهم الشورى، قتله مروان ابن الحكم يوم الجمل وقد نيف على الستين من عمره.','1','334',NULL,NULL,'0');
INSERT INTO [b435] VALUES (359,'<span data-type="title" id=toc-376>أبو طلحة (صحابى) توفى سنة ٣١ هجرية وقيل ٥١ هجرية</span>.

أبو طلحة الأنصارى، اسمه زيد بن سهل بن الأسود ابن حرام الأنصارى الخزرجى شهد العقبة وبدرا وما بعدها كان من الرماة المذكورين من الصحابة، قتل يوم حنين عشرين رجلا وأخذ أسلابهم، لما قال الرسول صلى الله عليه وسلم:" من قتل قتيلا فله سلبه "، اختلف فى وقت وفاته، فقيل سنة إحدى وثلاثين، وقيل سنة أربع وثلاثين، وقال المدائنى: مات سنة إحدى وخمسين،','1','336',NULL,NULL,'0');
INSERT INTO [b435] VALUES (360,'<span data-type="title" id=toc-377>أبو طيبة (صحابى)</span>

أبو طيبة الحجام، مولى الأنصار من بنى حارثة، حجم النبى صلى الله عليه وسلم واختلف فى صحة اسمة، فقيل اسمه دينار، وقيل اسمه ميسرة، وقيل اسمه نافع.','1','337',NULL,NULL,'0');
INSERT INTO [b435] VALUES (361,'<span data-type="title" id=toc-378>عائشة توفيت سنة ٥٨ هجرية</span>

عائشة بنت أبى بكر الصديق عبد الله بن عثمان من قريش أفقه نساء المسلمين وأعلمهن بالدين والأدب كانت تكنى بأم عبد ألله تزوجها النبى قبل الهجرة وبنى بها فى الثانية بعد الهجرة وكانت احب نسائه إليه وأكثرهن رواية للحديث عنه.','1','340',NULL,NULL,'0');
INSERT INTO [b435] VALUES (362,'<span data-type="title" id=toc-379>ابن عابدين سنة ١٢٥٢ هجرية</span>

محمد أمين بن عمر بن عبد العزيز عابدين الدمشقى فقية الديارالشامية وإمام الحنفية فى عصره مولده ووفاته فى دمشق وله الكتاب المشهور رد المختار على الدرالمختار خمسه مجلدات فى الفقه يعرف بحاشية ابن عابدن وغير ذلك','1','339',NULL,NULL,'0');
INSERT INTO [b435] VALUES (363,'<span data-type="title" id=toc-381>عاصم بن أبى النجود سنة ١٢٧ هجرية</span>

هو عاصم بهدلة المكنى أبى النجود، كوفى، أسدى بالولاء وهو أحد القراء السبعة تابعى مولده ووفاته بالكوفة.','1','338',NULL,NULL,'0');
INSERT INTO [b435] VALUES (364,'<span data-type="title" id=toc-382>العاملى سنة ٩٦٥ هجرية</span>

- الامام زين الدين بن الامام نور الدين على بن أحمد بن محمد بن جمال إبن تقى الدين بن صالح بن مشرف أو أشرف الجبعى العاملى ولد فى جبع وجوَّد القرآن وهو إبن تسع سنين نشأ فى بيت علم له مؤلفات تجاوزت السبعين منها الروضة البهية فى شرح اللمعة الدمشقية فى فقه الشيعة الإمامية','1','342',NULL,NULL,'0');
INSERT INTO [b435] VALUES (365,'<span data-type="title" id=toc-383>العباس بن عبد المطلب سنة ٣٢ هجرية</span>

جده هاشم بن عبد مناف وكنيته أبو الفضل كان من أكابر قريش فى الجاهليه والإسلام وهو عم النبى صلى الله عليه وسلم وجد الخلفاء العباسيين وأبو حبر الأمة عبد الله بن عباس.. أسلم قبل الهجرة وكتم اسلامه وأقام بمكة يكتب لرسول الله صلى الله عليه وسلم أخبار المشركين وله فى الصحيحين ٣٥ حديثا توفى بالمدينة.','1','344',NULL,NULL,'0');
INSERT INTO [b435] VALUES (366,'<span data-type="title" id=toc-384>ابو العباس سنة ٣٥٣</span>

أحمد بن إبراهيم بن الحسن وهو إمام فقيه مناضل كان إماميا ثم رجع إلى الزيدية وعنه أخذت جميع كتب الشيعة فى عصره.','1','341',NULL,NULL,'0');
INSERT INTO [b435] VALUES (367,'<span data-type="title" id=toc-385>ابو العباس الشاعر</span>

هو السائب بن فروخ المكى أبو العباس الشاعر أخذ عن عبد الله بن عمرو وابن عمر وأخذ عنه حبيب بن أبى ثابت وعمرو بن دينار ووثقه أحمد.','1','343',NULL,NULL,'0');
INSERT INTO [b435] VALUES (368,'<span data-type="title" id=toc-386>ابن عبد البر سنة ٤٦٣ هجرية</span>

يوسف بن عبد الله بن محمد بن عبد البر النمرى القرطبى المالكى أبو عمر ومن كبار حفاظ الحديث ومؤرخ أديب بحاثة ولد بقرطبة ورحل رحلات طويلة وولى قضاء لشبونة وغيرها توفى بشاطبة. من كتبه الدرر فى اختصار المغازى والسير والاستيعاب فى تراجم الصحابة وغيرها.','1','345',NULL,NULL,'0');
INSERT INTO [b435] VALUES (369,'<span data-type="title" id=toc-387>ابن عبد الحكم توفى سنة ٢١٤</span>

عبد الله بن الحكم بن أيمن بن ليث بن رافع أبومحمد، فقيه مصرى من العلماء كان من أجلة أصحاب مالك، انتهت اليه الرياسة بمصر بعد أشهب، ولد فى الإسكندرية وتوفى بالقاهرة وله مصنفات فى الفقه وغيره.','1','346',NULL,NULL,'0');
INSERT INTO [b435] VALUES (370,'<span data-type="title" id=toc-388>عبد الرحمن بن عوف توفى سنة ٣٢ هجرية</span>

أبو محمد عبد الرحمن بن عوف بن عوف بن عبد الحارث الزهرى القرشى صحابى من أكابرهم وهو أحد العشرة المبشرين بالجنة وأحد الستة أصحاب الشورى الذين جعل عمر الخلافة فيهم شهد المشاهد كلها توفى بالمدينة.','1','347',NULL,NULL,'0');
INSERT INTO [b435] VALUES (371,'<span data-type="title" id=toc-389>ابن عبد السلام سنة ٧٤٩</span>

محمد بن عبد السلام بن يوسف بن كثير الهوارى بن المنستيرى، أبوعبد الله فقيه مالكى. كان قاضى الجماعة بتونس وولى القضاء بها سنة ٧٣ هجرية له كتب منها شرح جامع الأمهات لابن الحاجب.','1','348',NULL,NULL,'0');
INSERT INTO [b435] VALUES (372,'<span data-type="title" id=toc-390>عبد القاهر بن طاهر سنة ٤٢٩ هجرية</span>

هو عبد القاهربن طاهربن محمد بن عبد الله البغدادى التميمى الاسفرايينى من أئمة الأصول كان صدرالإسلام فى عصره ولد ونشأ فى بغداد ورحل إلى خراسان وكانت وفاته فى اسفرايين ومن مؤلفاته الفرق بين الفرد وكتاب أصول الدين.','1','349',NULL,NULL,'0');
INSERT INTO [b435] VALUES (373,'<span data-type="title" id=toc-391>عبد الكريم سلمان سنة ١٩١٨م</span>

عبد الكريم بن حسين بن سلمان أغا. تعلم فى الأزهر واتصل بجمال الدين الأفغانى ومحمد عبده ورأس تحرير الوقائع المصرية بعد محمد عبده وعين مفتشا بالمحاكم الشرعية وكان من إعضاء مجلس الأزهر ومن الكتاب المشهورين.','1','350',NULL,NULL,'0');
INSERT INTO [b435] VALUES (374,'<span data-type="title" id=toc-392>عبد الله بن إباض سنة ٨٦ هجرية</span>

عبد الله بن اباض المقاعسى المرى التميمى من بنى مرة رأس الإباضية واليه نسبتهم كان معاصرا لمعاوية.','1','353',NULL,NULL,'0');
INSERT INTO [b435] VALUES (375,'<span data-type="title" id=toc-393>عبد الله بن عامر المتوفى سنة ١١٨</span>

هو عبد الله بن عامر اليحصبى، يرجع فى أصله إلى حمير، وهو من التابعين، كان أمام أهل الشام فى القراءة وهو أحد القراء السبعة','1','351',NULL,NULL,'0');
INSERT INTO [b435] VALUES (376,'<span data-type="title" id=toc-394>عبد الله بن عباس سنة ٥٦٨</span>

عبد الله بن عباس بن عبد المطلب القرشى الهاشمى أبو العباس حبر الأمة والصحابى الجليل ولد بمكة وشهد مع على " الجمل وصفين" وينسب إليه كتاب فى تفسير القرآن جمعه بعض أهل العلم من مرويات المفسرين عنه فى كل أية','1','352',NULL,NULL,'0');
INSERT INTO [b435] VALUES (377,'<span data-type="title" id=toc-395>عبد الله بن عمر سنة ٧٣ هجرية</span>

عبد الله بن عمر بن الخطاب العدوى أبو عبد الرحمن صحابى نشا فى الإسلام وهاجر إلى المدينة مع أبيه وشهد فتح مكة أفتى الناس فى الإسلام ستين سنه وغزا أفريقية مرتين وهو أخر من توفى بمكه من الصحابة وكان مولده ووفاته بها','1','355',NULL,NULL,'0');
INSERT INTO [b435] VALUES (378,'<span data-type="title" id=toc-396>عبد الله بن عمرو سنة ٦٥ هجرية</span>

عبد الله بن عمرو بن العاص من قريش صحابى من أهل مكة أسلم قبل أبيه شهد الحروب والغزوات وشهد صفين مع معاوية وولاه معاوية الكوفة وانزوى فى عهد يزيد له فى الصحيحين سبعمائة حديث.','1','354',NULL,NULL,'0');
INSERT INTO [b435] VALUES (379,'<span data-type="title" id=toc-397>عبد الله بن كثير سنة ١٣٠ هجرية</span>

عبد الله بن كثير الدارى المكى أحد القراء السبعة كان قاضى الجماعة بمكة مولده ووفاته بها.','2','135',NULL,NULL,'0');
INSERT INTO [b435] VALUES (380,'<span data-type="title" id=toc-398>عبد الله بن مسعود سنة ٣٢ هجرية</span>

عبد الله بن مسعود بن غافل بن حبيب الهذلى من أكابر الصحابة ومن أقربهم إلى رسول الله صلى الله عليه وسلم وهو من السابقين إلى الإسلام وكان خادم رسول الله وصاحب سره ولى بعد وفاة النبى صلى الله عليه وسلم بيت مال الكوفة ثم قدم المدينة فى خلافة عثمان فتوفى بها.','2','137',NULL,NULL,'0');
INSERT INTO [b435] VALUES (381,'<span data-type="title" id=toc-399>عبد المطلب بن ربيعة سنة ٦٣ هجرية</span>

جده الحارث بن عبد المطلب بن هاشم صحابى سكن المدينة وأنتقل إلى الشام فى خلافة عمر فتوفى فى دمشق له ثمانية أحاديث.','2','138',NULL,NULL,'0');
INSERT INTO [b435] VALUES (382,'<span data-type="title" id=toc-400>عبد الملك بن مروان سنة ٨٦ هجرية</span>

عبد الملك بن مروان بن الحكم الأموى القرشى أبو الوليد نشأ فى المدينة فقيها واسع العلم واستعمله معاوية على المدينة وانتقلت إليه الخلافة بموت أبيه سنه ٦٥ هجرية ونقلت فى أيامه الدواوين من الفارسية والرومية إلى العربية وضبطت الحروف بالنقط والحركات وهو أول من صك الدنانير فى الإسلام توفى فى دمشق.','2','139',NULL,NULL,'0');
INSERT INTO [b435] VALUES (383,'<span data-type="title" id=toc-401>عبد الوهاب"القاضى" سنة ٤٢٢ هجرية</span>

عبد الوهاب بن على بن نصر الثعلبى البغدادى أبو المجد قاض من فقهاء المالكية ولد ببغداد ورحل إلى الشام وتوجه إلى مصر وتوفى بها له كتاب التلقين فى فقه المالكية وشرح المدونة وشرح فصول الأحكام.','1','356',NULL,NULL,'0');
INSERT INTO [b435] VALUES (384,'<span data-type="title" id=toc-402>ابو عبيدة بن الجراح سنة ١٨ هجرية</span>

هو عامر بن الجراح بن هلال الفهرى القرشى فاتح الديار الشامية صحابى لقبه النبى صلى الله عليه وسلم بأمين الأمة وهو أحد العشرة المبشرين بالجنة له فى الصحيحين ١٤حديثا','2','140',NULL,NULL,'0');
INSERT INTO [b435] VALUES (385,'<span data-type="title" id=toc-403>عثمان بن أبى العاص سنة ٥١ هجرية</span>

جد بشر بن عبد بن دهمان من ثقيف صحابى من أهل الطائف أسلم فى وفد ثقيف فاستعمله النبى صلى الله عليه وسلم على الطائف فبقى كذلك إلى عهد عمر فولاه عمان والبحرين وعزله عثمان فسكن البصرة إلى أن توفى','1','359',NULL,NULL,'0');
INSERT INTO [b435] VALUES (386,'<span data-type="title" id=toc-404>عثمان بن الحكم سنة ١٦٣ هجرية</span>

عثمان بن الحكم الحزامى المصرى شيخ لعبد الله. بن وهب، قال عنه أبو حاتم ليس بالمتقن عرض عليه قضاء مصر فأبى','2','155',NULL,NULL,'0');
INSERT INTO [b435] VALUES (387,'<span data-type="title" id=toc-405>عثمان بن عفان سنة ٣٥ هجرية</span>

عثمان بن عفان بن أبى العاص بن أمية من قريش أمير المؤمنين ذوالنورين ثالث الخلفاء الراشدين، وأحد العشرة المبشرين من كبار من أعتزبهم الإسلام فى عهد ظهوره ولد بمكة وأسلم بعد البعثة بقليل وكان غنيا شريفا فى الجاهلية ومن أظهر أعماله في الإسلام جمع القرآن الكريم فى مصحف واحد وتجهيز جيشى العسرة بماله صارت إليه الخلافة بعد وفاة عمرسنه ٢٣ هجرية','1','357',NULL,NULL,'0');
INSERT INTO [b435] VALUES (388,'<span data-type="title" id=toc-406>عدى بن حاتم سنة ٦٧ هجرية</span>

عدى بن حاتم الطائى قدم على النبى سنة سبع من الهجرة ونزل بالكوفة وسكنها، وفقئت عينه يوم الجمل مع على، مات بالكوفة وكان سنه ١٢٠ سنة','1','358',NULL,NULL,'0');
INSERT INTO [b435] VALUES (389,'<span data-type="title" id=toc-407>ابن العربى</span>

محمد بن عبد الله بن محمد بن عبد الله بن أحمد الإمام الحافظ القاضى أبو بكر المعروف بابن العربى ختام علماء الأندلس وآخر أئمتها له أحكام القرآن','1','360',NULL,NULL,'0');
INSERT INTO [b435] VALUES (390,'<span data-type="title" id=toc-408>ابن عرفة سنة ٨٠٣ هجرية</span>

محمد بن محمد بن عرفة الورغنى أبو عبد الله إمام تونس ولد وتوفى بها ومن كتبه المختصر الكبير فى كتب المالكية ومختصر الفرائض والحدود فى التعاربف الفقيهية وهو منسوب إلى (ورغمة) قرية بافريقية.','1','361',NULL,NULL,'0');
INSERT INTO [b435] VALUES (391,'<span data-type="title" id=toc-409>إبن عرفة توفى سنة ٣١٨ هجرية</span>

الحسين بن محمد بن مودود السلمى الحرانى، محدث حران وفقيهها كان حافظا للحديث عارفا برجاله له (تاريخ) وكتاب فى الأمثال.','1','362',NULL,NULL,'0');
INSERT INTO [b435] VALUES (392,'<span data-type="title" id=toc-410>عز الدين بن عبد السلام سنة ٦٦٠ هجرية</span>

عز الدين بن عبد السلام بن أبى القاسم شيخ الإسلام ولد سنه ٥٧٧ هجرية الأصول عن الآمدى والفقه عن ابن عساكر وقد انتهت إليه معرفه مذهب الشافعى بلغ مرتبة الاجتهاد ولقب بسلطان العلماء توفى بمصر.','1','363',NULL,NULL,'0');
INSERT INTO [b435] VALUES (393,'<span data-type="title" id=toc-411>العزيزى سنة ١٠٧٠ هجرية</span>

على بن أحمد محمد بن إبراهيم العزيزى البولاقى الشافعى فقيه مصرى من العلماء بالحديث مولده بالعزيزية شرقية ووفاته ببولاق من كتبه السراج المنير شرح الجامع الصغير.','1','365',NULL,NULL,'0');
INSERT INTO [b435] VALUES (394,'<span data-type="title" id=toc-412>عكرمة سنة ١٠٧ هجرية</span>

أبو عبد الله عكرمة البربرى مولى ابن عباس كان فقيها عالما، بالتفسير وال سنة الا أنه يرى رأى الخوارج، لهذا تجنبه الإمام مالك والأمام مسلم فلم يرويا له، ويقال هو الذى أدخل مذهب الأباضية إلى المغرب توفى بالمدينة','1','364',NULL,NULL,'0');
INSERT INTO [b435] VALUES (395,'<span data-type="title" id=toc-413>علاء الدين الطرابلسى توفى سنة ٨٤٤ هجرية</span>

على بن خليل أبو الحسن، علاء الدين الطرابلسى فقيه حنفى كان قاضيا بالقدس، له معين الحكام فيما يتردى بين الخصمين من الأحكام.','1','368',NULL,NULL,'0');
INSERT INTO [b435] VALUES (396,'<span data-type="title" id=toc-414>علقمة سنة ٦٢ هجرية</span>

علقمة بن قيس بن عبد الله بن مالك النخعى الهمدانى تابعى كان فقيه العراق ولد فى حياة النبى صلى الله عليه وسلم وروى الحديث عن الصحابة وشهد صفين وغزا خراسان وسكن الكوفة وتوفى بها.','1','367',NULL,NULL,'0');
INSERT INTO [b435] VALUES (397,'<span data-type="title" id=toc-415>عليش سنة ١٢٩٩</span>

محمد بن أحمد بن محمد عليش ابو عبد الله فقيه من أعيان المالكية: ولد فى القاهرة وتعلم فى الأزهر وولى مثسيخة المالكية فيه من تصانيفه فتح العلى المالك على مذهب الإمام مالك وهو مجموع فتاويه وشرح على مختصرخليل وغيره.','1','366',NULL,NULL,'0');
INSERT INTO [b435] VALUES (398,'<span data-type="title" id=toc-416>على بن ابى طالب سنة ٤٠ هجرية</span>

على بن أبى طالب بن عبد المطلب أمير المؤمنين أول الناس اسلاما بعد خديجة ولد بمكة وربى فى حجر النبى صلى الله عليه وسلم وكان اللواء فى يده ولى الخلافة بعد مقتل عثمان فأقام بالكوفة وقتل شهيدا فى ١٧ رمضان سنة ٤٠ هجرية','1','395',NULL,NULL,'0');
INSERT INTO [b435] VALUES (399,'<span data-type="title" id=toc-417>عمار بن ياسر</span>

عمار بن ياسر بن عامر الكنانى القحطانى أبو: اليقظان صحابى من الولاة الشجعان ومن السابقين الى الإسلام، هاجر إلى المدينة وشهد بدرأ واحدا وغيرهما، ولاه عمر الكوفة ثم عزله " وشهد الجمل وصفين وقتل فيها','1','370',NULL,NULL,'0');
INSERT INTO [b435] VALUES (400,'<span data-type="title" id=toc-418>عمر بن الخطاب توفى سنة ٢٣ هجرية</span>

عمر بن الخطاب بن نفيل القرشى العدوى أبو حفص ثانى الخلفاء الراشدين وأول من لقب بأمير المؤمنين، مضرب المثل فى العدل، كان فى الجاهلية من ابطال قريش وأشرافهم وله السقاية فيهم أسلم قبل ال هجرية جرة بخمس سنين وشهد الوقائع وكانت له، تجارة بين الشام والحجاز بويع بالخلافة بعد وفاة ابى بكر بعهد منه','1','369',NULL,NULL,'0');
INSERT INTO [b435] VALUES (401,'<span data-type="title" id=toc-419>عمر بن عبد العزيز سنة ١٠١ هجرية</span>

أبوحفص، عمر بن عبد العزبز بن مروان بن الحكم الأموى ولى الحكم بعد سليمان بن عبد الملك فكان خليفة صالحا عادلا وربما لقب بخامس الخلفاء الراشدين','1','371',NULL,NULL,'0');
INSERT INTO [b435] VALUES (402,'<span data-type="title" id=toc-420>عمرو بن حزم سنة ٥١،٥٤ هجرية</span>

ابو الضحاك، عمرو بن حزم بن زيدبن لوزان بن عمرو الأنصارى، الخزرجى المدنى وقيل فى نسبه غيرذلك أول مشاهده مع رسول الله صلى الله علية وسلم الخندق واستعملة الرسول صلى الله عليه وسلم على نجران باليمن وبعث معه كتابا فيه الفرائض والسنن والصدقات والجروح والديات وكتابه هذا مشهورفى كتب ال سنة، توفى بالمدينة إحدى وقيل ثلاث وقيل أربع وخمسين.','1','372',NULL,NULL,'0');
INSERT INTO [b435] VALUES (403,'<span data-type="title" id=toc-421>عمرو بن دينارتوفى سنة ١٢٦ هجرية</span>

أبو محمد عمرو بن دينار المكى الجمحى مولاهم، سمع أبن عمروابن عباس وجابرا وآخرين من الصحابه وخلائق من أئمه التابعين وروى عنه جعفر الصادق وأيوب وقتادة وخلائق من الأئمة واجمعوا على جلاله وإمامته وتوثيقه اختلف فى سنة وفاته ومات وهو ابن ثمانين سنة على التقريب.','1','373',NULL,NULL,'0');
INSERT INTO [b435] VALUES (404,'<span data-type="title" id=toc-422>عمر بن شعيب سنة ٢١٨ هجرية</span>

أبو إبراهيم عمرو بن شعيب بن محمد السهمى من رجا ل الحديث كان يسكن مكة.','1','374',NULL,NULL,'0');
INSERT INTO [b435] VALUES (405,'<span data-type="title" id=toc-423>عمرو بن عبيد سنة ١٤٤ هجرية</span>

عمرو بن عبيد بن باب التيمى بالولاء أبو عثمان البصرى شيخ المعتزلة فى عصره وأحد الزهاد المشهورين اشتهر بعلمه وزهده له رسائل وخطب وكتب منها " التفسير، والرد على القدرية توفى بحران بقرب مكة','1','375',NULL,NULL,'0');
INSERT INTO [b435] VALUES (406,'<span data-type="title" id=toc-424>أبوعمرو بن العلاء المتوفى١٥٤ هجرية</span>

زبان بن العلاء بن عمار التميمى المازنى البصرى، أحد القراء السبعة ليس فيهم أكثر شيوخا منه، سمع أنس بن مالك وغيره، وقرأ على الحسن البصرى وغيره، ومات بالكوفة','1','376',NULL,NULL,'0');
INSERT INTO [b435] VALUES (407,'<span data-type="title" id=toc-425>عميرة</span>

شهاب الدين أحمد البرلس المشهور بعميرة من علماء القرن العاشر الهجرى وله حاشية على شرح جلال الدين المحلى المسمى كنز الراغبين شرح منهاج الطالبين للنووى','1','377',NULL,NULL,'0');
INSERT INTO [b435] VALUES (408,'<span data-type="title" id=toc-426>عيسى بن عمر توفى سنة ١٤٩ هجرية</span>

عيسى بن عمر الثقفى بالولاء أبو سليمان من أئمة اللغة وهو شيخ الخليل وسيبويه وابن العلاء وأول من هذب النحو ورتبه وهو من أهل البصرة','1','380',NULL,NULL,'0');
INSERT INTO [b435] VALUES (409,'<span data-type="title" id=toc-427>العينى سنة ٨٥٥ هجرية</span>

ابو محمد محمود بن أحمد العينى قاضى القضاة ولد بمصر سنه ٧٦٢ ووولى الحسبة بالقاهرة وقضاء الأحناف، من مؤلفاته، شرح صحيح البخارى،وشرح الهداية،وشرح الكنز فى فقه الاحناف','1','379',NULL,NULL,'0');
INSERT INTO [b435] VALUES (410,'<span data-type="title" id=toc-428>ابن عيينة سنة ١٩٨ هجرية</span>

سفيان بن عيينه بن ميمون ال هجريةلالى الكوفى، محدث الحرم المكى، ولد بالكوفة وتوفى بها وكان حافظا ثقة، واسع العلم كببر القدر قال الشافعى: لولا مالك وسفيان لذهب علم الحجاز. له (الجامع) فى الحديث، وكتاب فى التفسير','1','378',NULL,NULL,'0');
INSERT INTO [b435] VALUES (411,'<span data-type="title" id=toc-429>عبادة بن الصامت سنة ٣٤ هجرية</span>

عبادة بن الصامت بن قيسى الأنصارى الخزرجى أبو الوليد، صحابى من الموصوفين بالورع. شهد العقبة، وكان أحد النقباء وشهد بدرا وسائر المواقع، وحضر فتح مصر، مات بفلسطين، وكان من سادات الصحابة.','1','382',NULL,NULL,'0');
INSERT INTO [b435] VALUES (412,'<span data-type="title" id=toc-430>عبد الرحمن ابن سهل</span>

عبد الرحمن بن سهل بن زيد الأنصارى الحارثى المدنى شهد أحدا والخندق وما بعدها من المشاهد مع رسول الله صلى الله عليه وسلم وقد استعمله عمر بن الخطاب على البصرة بعد موت عتبة بن غزوان','1','383',NULL,NULL,'0');
INSERT INTO [b435] VALUES (413,'<span data-type="title" id=toc-431>عبد الرزاق سنة ٢١١ هجرية</span>

عبد الرزاق بن همام بن نافع الحميرى بالولاء من حفاظ الحديث الثقاة من أهل صنعاء له كتاب الجامع الكبير فى الحديث وله كتاب فى تفسير القرآ ن روى عنه أئمة الإسلام منهم سفيان بن عينيه وكان من شيوخه وأحمد بن حنبل ويحيى بن معين وغيرهم توفى باليمن','1','381',NULL,NULL,'0');
INSERT INTO [b435] VALUES (414,'<span data-type="title" id=toc-432>عبد بن زمغة صحابى</span>

عبد بن زمعة بن قيس بن عبد شمس بن عبدون وينتهى نسبه إلى عامر بن لؤى القرشى العامرى أخو سودة أم المؤمنين ثبت خبره فى الصحيحين فى مخاصمة سعد بن أبى وقاص فى ابن وليدة زمعة أسلم يوم الفتح وكان من سادات الصحابة.','1','385',NULL,NULL,'0');
INSERT INTO [b435] VALUES (415,'<span data-type="title" id=toc-433>أبو عبد الله</span>

ابن خواز منداد البصرى أبو بكر المالكى فقية أصولى نقل عنه ابن القيم فى كتابه أعلام الموقعين وغيره.','1','387',NULL,NULL,'0');
INSERT INTO [b435] VALUES (416,'<span data-type="title" id=toc-434>أبو عبد الله بن سهل سنة ٧ هجرية</span>

عبد الله بن سهل بن زيد الأنصارى الحارثى المدنى الصحابى الذى قتله اليهود بخيبر وكان خرج إلى خيبر بعد فتحها مع أصحاب لة يمتارون تمرا فوجد قتيلا بها رضى الله عنة.','1','384',NULL,NULL,'0');
INSERT INTO [b435] VALUES (417,'<span data-type="title" id=toc-435>أبو عبيد سنة ٣١٩ هجرية</span>

أبو عبيد بن حربويه على بن الحسين بن حرب بن عيس البغدادى الشافعي وهي من أئمة أصحاب الشافعى وأصحاب الوجوه قاضى مصر وهو من تلامذة أبى ثور وداود الظاهرى توفى ببغداد وصلى عليه أبو سعيد الاصطخرى','1','389',NULL,NULL,'0');
INSERT INTO [b435] VALUES (418,'<span data-type="title" id=toc-436>أبو عبيد سنة ٢٢٤ هجرية</span>

أبو عبيد بن سلام البغدادى وهو معدود فيمن أخذ الفقه عن الشافعى وكان إماما فى علوم كثيرة منها القراءات والحديث والتفسير والفقه واللغة أقام ببغداد وولى قضاء طرسوس ثم سكن مكة حتى مات بها وقد صنف كثيرا من الكتب فى كل فن','1','386',NULL,NULL,'0');
INSERT INTO [b435] VALUES (419,'<span data-type="title" id=toc-437>ابن عتبة سنة ٩٨ هجرية</span>

عبيد الله بن عبد الله بن عتبة بن مسعود أبو عبد الله مفتى المدينة وأحد الفقها السبعة فيها من أعلام التابعين. قال ابن سعد كان ثقة عالما فقيها كثير الحديث والعلم بالشعر وقد ذهب بصره ومات بالمدينة','1','390',NULL,NULL,'0');
INSERT INTO [b435] VALUES (420,'<span data-type="title" id=toc-438>عتبة ابن أبى وقاص</span>

عتبة بن أبى وقاص بن وهب ويقال أهيب بن زهرة القرشى الزهرى أخو سعد واختلف فى أنه من الصحابة مات مقتولا بالمدينة وقيل فى غزوة أحد بعد أن كسرت رباعية الرسول','1','391',NULL,NULL,'0');
INSERT INTO [b435] VALUES (421,'<span data-type="title" id=toc-439>عزمى زادة سنة ١٠٤٠ هجرية</span>

مصطفى بن محمد المعروف بعزمى زاده فاض تركى مستعرب من فقهاء الحنفية ولى فضاء الشام سنة ١٠١١ هجرية وقضاء مصر سنة ١٠١٣ هجرية ثم ولى القضاء بإستامبول ومن كتبه العربية نتائج الأفكار حاشية على شرح المنار فى أصول الفقه، وحاشيه على درر الحكام فىالفقه.','1','388',NULL,NULL,'0');
INSERT INTO [b435] VALUES (422,'<span data-type="title" id=toc-440>عطاء سنة ١١٤ هجرية</span>

عطاء ابن أبى أسلم بن صنوان تابعى من إجلاء الفقهاء له ولد فى جند باليمن ونشأ بمكة وتوفى فيها وكان مفتى أهلها ومحدثهم.','1','392',NULL,NULL,'0');
INSERT INTO [b435] VALUES (423,'<span data-type="title" id=toc-441>عطاء سنة ١١٥ هجرية</span>

عطاء بن أبى رباح المكى القرشى مولى أبى خيثم وهو من كبار التابعين وله فى خلافة عثمان ونثسأ بمكة وسمع العبادلة وغيرهم الأربعة من الصحابة وروى عنه جماعات من التابعين كا لزهرى وقتادة وهو من مفتى أهل مكة ومشاهيرهم وهو أحد شيوخ الشافعيين فى سلسلة الفقه.','1','393',NULL,NULL,'0');
INSERT INTO [b435] VALUES (424,'<span data-type="title" id=toc-442>عطاء الخراسانى سنة ١٣٥ هجرية</span>

أبو أيوب بن أبى مسلم الخراسانى البلخى، عن التابعين سكن الشام وروى عن معاذ بن جبل وكعب بن عجرة وابن عباس وسمع ابن المسيب وروى عنه عطاء بن أبى رباح توفى بأريحياء ودفن يبيت المقدس','1','416',NULL,NULL,'0');
INSERT INTO [b435] VALUES (425,'<span data-type="title" id=toc-443>عطاء بن يسار سنة ٩٤ هجرية</span>

العطاء بن يسار الهلالى المدنى مولى ميمونة بنت الحارث الهلالية أم المؤمنين أخو سليمان وعبد الملك وعبد الله بن يسار وهو عن كبار التابعين سمع ابن مسعود وابن أبى كعب وعبد الله بن سلام وغيرهم رروى عنه جماعة من التابعين كان ثقة كثير الحديث توفى سنة ثلاث أو أربع ومائة وقيل الأصح أنه توفى سنة ٩٤ هجرية.','1','394',NULL,NULL,'0');
INSERT INTO [b435] VALUES (426,'<span data-type="title" id=toc-444>ابن عقيل سنة ٥١٣ هجرية</span>

على بن عقيل بن محمد بن عقيل البغدادى وكنيته أبو الوفا أصولى حنبلى واعظ متكلم سمع الحديث الكثير من علمائه وتفقه على القاضى أبى يعلى ابن الفراء مال إلى مذهب المعتزلة ولكنه عدل عن هذا والتزم مذهب الحنابلة فى الفقه له كتاب الفنون وله فى الفقه كتاب الفصول وعمدة الأدلة وغيرها. دفن ببغداد قريبا من قبر الإمام أحمد','1','397',NULL,NULL,'0');
INSERT INTO [b435] VALUES (427,'<span data-type="title" id=toc-445>علاء الدين المرداوى سنة ٨٨٥ هجرية</span>

على بن سليمان بن أحمد بن محمد المرداوى الملقب يعلاء الدين المكنى بأبى الحسن فقيه حنبلى أصولى ولد ببلدة "مردا" سنة ٨١٧ هجرية. حفظ بها القرآن ثم غادرها شابا إلى مدينة الخليل ورحل الى دمشق وتوفى بها. واشتغل بالعلم وتفقة على تقى الدين بن قندس شيخ الحنابلة. انتهت إليه رياسة المذهب ومن كتبه الإنصاف فى معرفة الراجح من الخلاف فى أربعة مجلدات، والتنقيح الشيق فى تحرير أحكام المقنع وغيرها.','1','396',NULL,NULL,'0');
INSERT INTO [b435] VALUES (428,'<span data-type="title" id=toc-446>أبو على</span>

الحسين بن أبى العلا خالد بن طهمان الخفاف الزندجى أبو على الأعور، معدود من أصحاب الباقر والصادق، كوفى مولى بنى عامر من بنى أسد، له كتب منها مايعد من الأصول روى عنه خلق كثير','1','398',NULL,NULL,'0');
INSERT INTO [b435] VALUES (429,'<span data-type="title" id=toc-447>ابن علية سنة ١٩٣ هجرية</span>

إسماعيل بن إبراهيم بن مقسم أبو بشر الأسدى مولاهم، ويعرف بابن علية وهو منسوب إلى أمة. من أهل البصرة وأصلة كوفى. سمع من أبي التياح الضبعى حديثا واحدا وروى الكثير عن عبد العزيز بن صهيب وأيوب السختيانى. وقد سمع منه الإمام أحمد وابن جريج وشعبة وغيرهم. وذكره الخلال فيمن روى عن أحمد ولى المظالم ببغداد أيام هارون الرشيد كما ولى صدقات البصرة.','1','399',NULL,NULL,'0');
INSERT INTO [b435] VALUES (430,'<span data-type="title" id=toc-448>أبو عمر</span>

ابن عبد البر يوسف بن عبد الله بن محمد النمرى القرطبى المالكى من كبار حفاظ الحديث يقال له حافظ المغرب صاحب الإستيعاب والدرر، ولة مؤلفات كثيرة بعضها مطبوع وبعضها الآخر مخطوط منها الإنصاف فيما بين العلماء من الاختلاف والكافى فى الفقة توفى بشاطبة.','1','400',NULL,NULL,'0');
INSERT INTO [b435] VALUES (431,'<span data-type="title" id=toc-449>أبو عمرو سنة ٤٤٢ هجرية</span>

عثمان بن أبى بكر حمود الصدفى أبو عمر المعروف بابن الضابط عالم بالحديث والأدب من أهل المغرب قرأ فى القيروان ورحل إلى الشرق بالأندلس وكان المعز بن باديس ينتدبه لبعض المهمات فرحل فى أحداها فانقطع خبره. له عوالى الحديث والاقتصاد فى القراءات السبع.','1','401',NULL,NULL,'0');
INSERT INTO [b435] VALUES (432,'<span data-type="title" id=toc-450>أبو عمرو سنة ٤٤٤ هجرية</span>

عثمان بن سعيد بن عثمان أبو عمرو الدانى أحد حفاظ الحديث ومن الأئمة فى علم القرآن ورواياته وتفسيره من أهل دانية بالأندلس دخل المشرق فحج وزار مصر وعاد فتوفى فى بلده له أكثر من مائة تصنيف منها التيسير فى القراءات السبع والمقنع فى رسم المصاحف ونقطها وطبقات القراء وغير ذلك.','1','402',NULL,NULL,'0');
INSERT INTO [b435] VALUES (433,'<span data-type="title" id=toc-451>عمران بن الحصين ٥٢ هجرية</span>

أبو نجيم عمران بن الحصين بن عبيد بن خلف أسلم هو وأبو هريرة عام خيبر سنة سبع من الهجرة وروى له ١٨٠ حديثا نزل البصرة وكان قاضيها وتوفى بها.','1','403',NULL,NULL,'0');
INSERT INTO [b435] VALUES (434,'<span data-type="title" id=toc-452>عيسى ٢١٢ هجرية</span>

عيسى بن دينار بن واقد الغافقى أبو عبد الله فقيه الأندلس فى عصر. وأحد عائها المشهورين. قام برحلة فى طلب الحديث وعاد فكانت الفتيا تدور عليه بالأندلس لايتقدمه أحد.','1','404',NULL,NULL,'0');
INSERT INTO [b435] VALUES (435,'<span data-type="title" id=toc-453>عيسى ٨٢٣ هجرية</span>

عيسى ين علال الكتانى الصمودى ابو مهدى قاض له تعليق على مختصر ابن عرفة فى فقه المالكية كان إماما لجامع القرويين بفاس وولى القضاء بها والخطابة','1','406',NULL,NULL,'0');
INSERT INTO [b435] VALUES (436,'<span data-type="title" id=toc-454>عيسى سنة ١٠٦٢ هجرية</span>

عيسى بن عبد الرحمن السكتانى مفتى مراكش وقاضيها وعالمها في عصره مولده ووفاته فيها تفوق فى فقه المالكية والتفسير وصنف كتبا منها حاشية على شرح أم البراهين للسنوسى في التوحيد.','1','405',NULL,NULL,'0');
INSERT INTO [b435] VALUES (437,'<span data-type="title" id=toc-455>عبادة بن نسى توفى سنة ١١٨ هجرية</span>.

عبادة بن نسى الكندى، ثقة، توفى فى خلافة هشام بن عبد الملك.','1','407',NULL,NULL,'0');
INSERT INTO [b435] VALUES (438,'<span data-type="title" id=toc-456>العباس توفى سنة١٢٩٨ هجرية</span>.

العباس بن عبد الرحمن بن محمد، من أبناء المتوكل على الله إسماعيل بن القاسم الحسنى الشهارى من أئمة الزيدية فى اليمن، ولى القضاء للمتوكل محمد بن يحيى، ثم سكن صنعاء وبويع بالإمانة ثم حدثت فتن فتنحى بعد خمسة شهور وكان فقيها.','1','409',NULL,NULL,'0');
INSERT INTO [b435] VALUES (439,'<span data-type="title" id=toc-457>عاصم بن عمر توفى سنة ٧٠ هجرية</span>.

عاصم بن عمر بن الخطاب القرشى العدوى، كان من أحسن الناس خلقا وهو جد عمر بن عبد العزيز لأمه، مات بالربذة.','1','410',NULL,NULL,'0');
INSERT INTO [b435] VALUES (440,'<span data-type="title" id=toc-458>عامر بن سعد توفى سنة ١٠٣ هجرية</span>.

عامر بن سعد بن أبى وقاص القرشى المدنى التابعى، سمع أباه وعثمان بن عفان وابن عمر وعائشة وغيرهم من الصحابة رضوان الله عليهم، وروى عنه خلق من التابعين، واتفقوا على توثيقه، توفى بالمدينة قيل سنة أربع ومائة، وقيل غير ذلك.','1','408',NULL,NULL,'0');
INSERT INTO [b435] VALUES (441,'<span data-type="title" id=toc-459>عبد الجبار</span>

عبد الحبار بن قيس المرادى، فقيه إباضى، عينه الحارث بن تليد قاضيا لما تولى الإمامة، وكان
مشيرا له وصديقا، وكانا لا يقترقان، ولما قامت الفتنة تقدم عبد الجبار إلى قيادة الإباضية وأعلن الثورة على " العكى " ثم أرسل عبد الرحمن بن حبيب من اغتاله هو وابن تليد.','1','411',NULL,NULL,'0');
INSERT INTO [b435] VALUES (442,'<span data-type="title" id=toc-460>عبد الرحمن بن أبى بكر</span>

عبد الرحمن بن أبى بكر الصديق وأمه أم رومان بنت الحارث بن الحويرث وهو شقيق أم المؤمنين عائشة رضى الله عنها.','1','414',NULL,NULL,'0');
INSERT INTO [b435] VALUES (443,'<span data-type="title" id=toc-461>عبد العزيز</span>

أبو عبد الرحمن بن عبد العزيز بن أبى رواد، خراسانى ثم مكى أزدى، سمع نافعا وصالحا وعكرمة وغيرهم وروى عنه ابنه عبد الله وآخرون، قيل عنه أنه ثقة صدوق متعبد.
عبد الله بن بحينة قال الواقدى يكنى أبا محمد، وأبوه مالك بن القشب الأزدى، كان حليفا لبنى المطلب بن عبد مناف، وكان عبد الله ناسكا فاضلا صائم الدهر، مات فى أيام معاوية فى ولاية مروان على المدينة.','1','412',NULL,NULL,'0');
INSERT INTO [b435] VALUES (444,'<span data-type="title" id=toc-462>عبد الله بن جعفر توفى سنة ٨٠ هجرية</span>.

عبد الله بن جعفر بن أبى طالب بن عبد المطلب الهاشمى القرشى، صحابى ولد بأرض الحبشة، وهو أول من ولد بها من المسلمين وأتى البصرة والكوفة والشام، كان أحد الأمراء فى جيش على يوم صفين، مات بالمدينة.','1','413',NULL,NULL,'0');
INSERT INTO [b435] VALUES (445,'<span data-type="title" id=toc-463>أبو عبد الله الزبيرى توفى سنة ٣١٧ هجرية</span>.

أحمد بن سليمان البصرى الزبيرى أبو عبد الله، باحث من فقهاء الشافعية من أهل البصرة قد يعرف بصاحب " الكافى " وهو مختصر له فى الفقه كان أعمى، نسبته إلى الزبير بن العوام ومن كتبه: الإمارة ورياضة المتعلم والاستشارة والاستخارة والمسكت.','1','415',NULL,NULL,'0');
INSERT INTO [b435] VALUES (446,'<span data-type="title" id=toc-464>عبد الله بن الفضل توفى سنة ٧٠٥ هجرية</span>.

عبد الله بن الفضل بن محمد بن هيكل البنهانى أبو عيسى أصله كوفى وانتقل إلى مصر
عبد الله بن أبى قتادة تابعى عبد الله بن أبى قتادة بن ربعى ويكنى أبو إبراهيم، ويقال أبو يحيى الأنصارى السلمى المدنى التابعى، سمع أباه وروى عنه إسماعيل بن أبى خالد، ويحيى بن أبى كثير وآخرون من التابعين، واتفقوا على توثيقه، توفى بالمدينة فى خلافة الوليد بن عبد الملك.','1','419',NULL,NULL,'0');
INSERT INTO [b435] VALUES (447,'<span data-type="title" id=toc-465>العضد توفى سنة ٧٥٦ هجرية</span>.

هو عبد الرحمن بن أحمد بن عبد الغفار أبوالفضل عضد الدين الايجى عالم بالأصول والمعانى العربية، من أهل أيج بفارس، ولى القضاء، من تصانيفه: المواقف فى علم الكلام والعقائد العضدية والرسالة العضدية فى علم الوضع وشرح مختصر ابن الحاجب فى الأصول وغيرها، مات مسجونا.','1','420',NULL,NULL,'0');
INSERT INTO [b435] VALUES (448,'<span data-type="title" id=toc-466>عطاء بن يزيد توفى سنة ١٠٧ هجرية</span>.

عطاء بن يزيد الليثى، أو محمد، وهو من كنانة، روى عنه الزهرى ومات وهو ابن٨٢ سنة.','1','417',NULL,NULL,'0');
INSERT INTO [b435] VALUES (449,'<span data-type="title" id=toc-467>ابن العطار توفى سنة ٧٢٤ هجرية</span>.

على بن إبراهيم بن داود بن سليمان، أبو حسن علاء الدين بن العطار، من أهل دمشق، كان أبوه عطارا وجده طبيبا، باشر مشيخة المدرسة النورية ثلاثين سنة، له مصنفات منها الوثائق المجموعة، والاعتقاد الخالص من الشك والانتقاد وغيرهما.','1','418',NULL,NULL,'0');
INSERT INTO [b435] VALUES (450,'<span data-type="title" id=toc-468>أم عطية</span>

أم عطية الصحابية اسمها نسية (بضم النون وفتح السين، وقيل بفتح السين، وقيل بفتح النون
وكسر السين) وقيل بنت كعب، وقيل بنت الحارث، وهى من فاضلات الصحابة، والغازيات منهن مع رسول الله صلى الله عليه وسلم، وكانت تغسل الميتات، وهى التى غسلت بنت رسول الله صلى الله عليه وسلم، روى لها عن رسول الله صلى الله عليه وسلم أربعون حديثا.','1','424',NULL,NULL,'0');
INSERT INTO [b435] VALUES (451,'<span data-type="title" id=toc-469>عقبة بن عامر توفى سنة ٥٨ هجرية</span>.

عقبة بن عامر بن عيس بن مالك الجهنى أمير من الصحابة كان رديف النبى صلى الله عليه وسلم، شهد صفين مع معاوية، وحضر فتح مصر مع عمرو بن العاص، ولى مصر سنة ٤٤ هجرية، وعزل عنها سنة ٤٧ هجرية وهو أحد من جمع القرآن. وله خمسة وخمسون حديثا مات بالقاهرة ودفن بها وله مسجد يعرف باسمه.','1','422',NULL,NULL,'0');
INSERT INTO [b435] VALUES (452,'<span data-type="title" id=toc-470>على بن الحسين توفى سنة ٩٤ هجرية</span>.

على بن الحسين بن على بن أبى طالب الهاشمى القرشى الملقب بزين العابدين رابع الأئمة الأثنى عشر عند الإمامية، كان حليما ورعا كريما، أحصى بعد موته عدد من كان يقوتهم سرا فكانوا نحو مائة بيت وليس للحسين عقب إلا منه ولد ومات بالمدينة.','1','421',NULL,NULL,'0');
INSERT INTO [b435] VALUES (453,'<span data-type="title" id=toc-471>أبو على الفارسى توفى سنة ٣٧٧ هجرية</span>.

هو الحسن بن عبد الغفار الفارسى الأصل أحد الأئمة فى علم العربية، ولد فى ناحية من أعمال فارس، ودخل بغداد سنة ٣٠٧ هجرية وتجول فى كثير من البلدان صنف كتاب الإيضاح والتذكرة فى علوم العربية وغيرهما. كان مهتما بالاعتزال وتوفى ببغداد.','1','423',NULL,NULL,'0');
INSERT INTO [b435] VALUES (454,'<span data-type="title" id=toc-472>ابن العماد توفى سنة ٨٠٨ هجرية</span>.

أحمد بن عماد بن يوسف بن عبد النبى أبو العباس شهاب الدين الأقفهسى، وسمى القاهرى، فقيه شافعى له تصانيف منها التعقبات على المهمات وشرح المنهاج والمعفوات فى الفقه ونسب إلى أقفهس من البهنسا بمصر.','1','425',NULL,NULL,'0');
INSERT INTO [b435] VALUES (455,'<span data-type="title" id=toc-473>عمرو بن العاص توفى سنة ٤٣ هجرية</span>.

هو عمرو بن العاص بن وائل السهمى القرشى، أبو عبد الله فاتح مصر وأحد عظماء العرب، وأسلم فى هدنة الحديبية، ولاه النبى عليه الصلاة والسلام امرة الجيش ثم استعمله على عمان وولاه عمر فلسطين وفتح مصر، وولى عليها من قبل عمر ثم عزله عثمان، وأعيد إلى ولاية مصر من قبل معاوية سنة ٣٨ هجرية وتوفى بالقاهرة.','1','426',NULL,NULL,'0');
INSERT INTO [b435] VALUES (456,'<span data-type="title" id=toc-475>الغزالى توفى سنة ٥٠٥ هجرية</span>

هو الفيلسوف الفقيه الأصولى أبو حامد محمد بن محمد الغزالى الشافعى ولد فى مدينة طوس من أعمال خراسان وتوفى بها تجول فى طلب العلوم الشرعية والعقلية حتى نبغ فيها ثم آثر التصوف وغلب عليه وله اكثر من مائتى كتاب ومقالة ورسالة','1','427',NULL,NULL,'0');
INSERT INTO [b435] VALUES (457,'<span data-type="title" id=toc-476>غلام الخلال سنة ٣٦٣ هجرية</span>

أبو بكر عبد العزيز بن جعفر بن أحمد البغوى مفسر ثقة فى الحديث من أعيان الحنابلة من أهل بغداد كان تلميذا لأبى بكر الخلال فلقب به ومن كتبه الشافعى والمقنع فى الفقه كبيران جدا وتفسير القرآن والخلاف مع الشافعى وزاد المسافروالتنبيه','1','428',NULL,NULL,'0');
INSERT INTO [b435] VALUES (458,'<span data-type="title" id=toc-477>الغامدية</span>

فى اسمها خلاف، قيل أبية، وقيل سبيعة، وفى الإصابة أنها سبيعة القرشية وروى عن عائشة حديث فحواه أنها أقرت على نفسها بالزنا لرسول الله صلى الله عليه وسلم ورجمت بعد أن وضعت حملها وفطمته.','1','429',NULL,NULL,'0');
INSERT INTO [b435] VALUES (459,'<span data-type="title" id=toc-478>ابن الغرس سنة ٨٩٤ هجرية</span>

محمد بن محمد بن خليل البدر بن الغرس أبو اليسر من فقهاء الحنفية مولده ووفاته بالقاهرة كان غاية فى الذكاء له كتب منها الفواكه البدرية فى الأقضية الحكمية يعرف برسالة ابن الغرس فى القضاء وحاشيته عل شرح التفتازانى فى العقائد النسفية.','1','430',NULL,NULL,'0');
INSERT INTO [b435] VALUES (460,'<span data-type="title" id=toc-479>ابن فارس توفى سنة ٣٩٥ هجرية</span>

أحمد بن فارس زكريا القزوينى الرازى من أئمة اللغة والأدب ومن شيوخ البديع الهمذانى والصاحب بن عباد أصله من قزوين اليها ينسب، وله تآليف كثيرة منها كتاب المجمل المشهور فى فقة اللغة','1','433',NULL,NULL,'0');
INSERT INTO [b435] VALUES (461,'<span data-type="title" id=toc-481>فاطمة سنة ١١ هجرية</span>

فاطمة بنت رسول الله محمد (صلى الله عليه وسلم) بن عبد الله بن عبد المطلب الهاشميه القرشية وأمها خديجة بنت خويلد من نابهات قريش وإحدى الفصيحات العاقلات. تزوجها على بن أبى طالب رضى الله عنه ولدت له الحسن والحسين وأم كلثوم زينب لها ١٨ حديثا','1','431',NULL,NULL,'0');
INSERT INTO [b435] VALUES (462,'<span data-type="title" id=toc-482>ابن فرحون سنة ٧٩٩ هجرية</span>

برهان الدين إبراهيم ابن الامام شمس الدين على بن فرحون المالكى كان عالما بالفقه والأصول والرجال رحل إلى مصر والقدس ودمشق ولى القضاء بالمدينة ومن مؤلفاته (تسهيل المهمات فى شرح جامع الأمهات ((والتبصرة فى أصول الأقضية) وكان قاضيا للمالكية بالمدينة ومات بها.','1','432',NULL,NULL,'0');
INSERT INTO [b435] VALUES (463,'<span data-type="title" id=toc-483>الفضل بن العباس توفى سنة ١٣ هجرية</span>

والده العباس بن عبد المطلب كان من شجعان الصحابة ووجوههم وكان أسن اخوته ثبت مع أبيه يوم حنين وأردفه النبى صلى الله عليه وسلم وراءه فى حجة الوداع استشهد فى وقعة اجنادين بفلسطين','1','434',NULL,NULL,'0');
INSERT INTO [b435] VALUES (464,'<span data-type="title" id=toc-484>ابن فورك سنة ٤٠٦</span>

محمد بن الحسن بن فورك الانصارى الأصبهانى أبو بكر واعظ عالم بالأصول والكلام من فقهاء الشافعية سمع بالبصرة وبغداد وحدث بنيسابور وتوفى على مقربة منها ونقلى أليها، له تصانيف كثيرة منها (الحدود فى الأصول) وغيره','1','435',NULL,NULL,'0');
INSERT INTO [b435] VALUES (465,'<span data-type="title" id=toc-485>الفيروزابادى سنة ٨١٧ هجرية</span>

محمد بن يعقوب بن محمد بن إبراهيم بن عمر ابو طاهر مجد الدين الشيرازى من أئمة اللغة والأدب ولد بكازرون وانتقل إلى العراق وجال فى مصر والشام ودخل بلاد الروم والهند اشهر كتبه القاموس المحيط وله كثير غيره','1','437',NULL,NULL,'0');
INSERT INTO [b435] VALUES (466,'<span data-type="title" id=toc-486>ابن فارس سنة ٣٩٥ هجرية</span>

أحمد بن فارس بن زكريا القزوينى الرازى أبو الحسين من ائمة اللغة والأدب أصله من قزوين وأقام مدة فى هزان ثم انتقل الى الرى وتوفى بها. من تصانيفه فى علم العربية. مقاييس اللغة ستة أجزاء. والمجمل. والصاحبى وله جامع التأويل فى تفسير القرآن أربع مجلدات.','1','439',NULL,NULL,'0');
INSERT INTO [b435] VALUES (467,'<span data-type="title" id=toc-487>الفنرى</span>

محمد بن حمزة بن محمد شمس الدين الفنرى الرومى عالم بالمنطق والأصول ولى القضاء.','1','436',NULL,NULL,'0');
INSERT INTO [b435] VALUES (468,'<span data-type="title" id=toc-488>ابن أبى الفوارس</span>

محمد بن أبى الفوارس توران شاه بن خشروشاه الجيلى العلامة الفقيه، يروى مذهب الزيدية وغيره وله مؤلفات منها تعليق الشرح ومنتزع شرح التجريد وله مقالات وعناية بمذهب الزيدية.','1','438',NULL,NULL,'0');
INSERT INTO [b435] VALUES (469,'<span data-type="title" id=toc-490>القاسم سنة ٢٤٤ هجرية</span>

الإمام القاسم بن إبراهيم الرسى الحسنى إمام من أئمة الزيدية ولد سنة ١٧٠ هجرية وتوفى بالرسى','1','440',NULL,NULL,'0');
INSERT INTO [b435] VALUES (470,'<span data-type="title" id=toc-491>قاسم بن أصبغ توفى سنة ٣٤٠ هجرية</span>

قاسم بن أصبغ بن محمد بن يوسف القرطبى سكن قرطبة ومات بها وكان جده من موالى بنى أمية، له مسند مالك وأحكام القرآن والناسخ والمنسوخ','1','441',NULL,NULL,'0');
INSERT INTO [b435] VALUES (471,'<span data-type="title" id=toc-492>ابن القاسم سنة ١٩١ هجرية</span>

عبد الرحمن بن القاسم بن خالد بن جنادة العتقى المصرى أبو عبد الله وعرف بابن القاسم فقيه تفقه بالإمام مالك ونظرائه، ولد ومات بمصر روى المدونة عن مالك','2','156',NULL,NULL,'0');
INSERT INTO [b435] VALUES (472,'<span data-type="title" id=toc-493>قاضى خان سنة ٥٧٢ هجرية</span>

حسن بن منصور بن أبى القاسم محمود بن عبد العزيز فخر الدين المعروف بقاضى خان الاوزدنجى الفرغانى فقيه حنفى من كبارهم له مؤلفات منها الفتاوى فى أربعة أجزاء والأمانى والوقعات والأوزدنجى نسبة إلى أوزدنج بنواحى إصبهان','2','157',NULL,NULL,'0');
INSERT INTO [b435] VALUES (473,'<span data-type="title" id=toc-494>قاضى زاده سنة ٩٩٨ هجرية</span>

شمس الدين أحمد بن تودر المعروف بقاضى زادة، له نتائج الأفكار فى تكمله كتاب فتح القدير لابن الهمام','2','141',NULL,NULL,'0');
INSERT INTO [b435] VALUES (474,'<span data-type="title" id=toc-495>ابن قدامة سنة ٦٢٠ هجرية</span>

عبد الله بن أحمد بن محمد بن قدامة بن مقدام بن نصر بن عبد الله المقدسى ثم الدمشقى الحنبلى موفق الدين رحل إلى بغداد وسمع بها من عبد القادر الجيلانى وغيره ثم عاد إلى دمشق وصنف كتاب المغنى ورحل إلى بغداد بعد ذلك توفى بدمشق ومن كتبه الكافى فى الفقه، مختصر العلل مختصر الهداية وروضة الناطر وجنة المناظر.','2','144',NULL,NULL,'0');
INSERT INTO [b435] VALUES (475,'<span data-type="title" id=toc-496>ابن قدامه (شمس الدين) توفى سنة ٦٨٢ هجرية</span>

عبد الرحمن بن محمد بن أحمد بن قدامة المقدسى الجماعيلى الحنبلى من أعيان الحنابلة ولد وتوفى فى دمشق وهو أول من ولى القضاء بها من الحنابلة، له تصانيف منها الشافى وهو الشرح الكبير للمقنع فى فقه الحنابلة','2','142',NULL,NULL,'0');
INSERT INTO [b435] VALUES (476,'<span data-type="title" id=toc-497>قدامة بن مظعون سنة ٣٦ هجرية</span>

قدامه بن مظعون بن حبيب الجمحى صحابى شهد بدرا وأحدا والخندق وسائر المشاهد مع رسول الله صلى الله عليه وسلم استعمله عمر على البحرين ثم عزله.','2','143',NULL,NULL,'0');
INSERT INTO [b435] VALUES (477,'<span data-type="title" id=toc-498>القدورى توفى سنة ٤٢٨ هجرية</span>

أحمد بن محمد بن أحمد القدورى فقيه حنفى ولد ومات فى بغداد، انت هجرية ت إليه رئاسة الحنفية فى العراق وصنف المختصر المعروف باسمه، "القدورى" فى فقه الحنفية، ومن كتبه التجريد فى سبعه أجزاء يشتمل على الخلاف بين الشافعية والحنفية وكتاب النكاح.','2','145',NULL,NULL,'0');
INSERT INTO [b435] VALUES (478,'<span data-type="title" id=toc-499>القرافى سنة ٦٨٤ هجرية</span>

أحمد بن إدريس بن عبد الرحمن أبو العباس شهاب الدين الصنهاجى القرافى من علماء المالكية والقرافى نسب إلى القرافة محلة مجاورة لقبر الأمام الشافعى بالقاهرة وهو مصرى المولد والمنشأ والوفاة له مصنفات جليلة فى الفقة والأصول.','1','442',NULL,NULL,'0');
INSERT INTO [b435] VALUES (479,'<span data-type="title" id=toc-500>القرطبى سنة ٢٧٦ هجرية</span>

قاسم بن محمد بن قاسم بن محمد بن سيار الأموى مولاهم البيانى الأندلسى القرطبى أبو محمد، من أعلام الفقهاء والمحدثين فى الأندلس وهو أحد المجتهدين له كتاب الإيضاح " ومولده ووفاته بقرطبة رحل إلى مصر رحلتين','1','443',NULL,NULL,'0');
INSERT INTO [b435] VALUES (480,'<span data-type="title" id=toc-501>ابن القشيرى سنة ٤٦٥</span>

عبد الكريم بن هوازن بن عبد الملك بن طلحة النيسابورى القشيرى من بنى قشير شيخ خراسان فى عصره ومن كتبه " التيسير فى التفسير ولطا ئف الإشارات والرسالة" القشيرية.','1','448',NULL,NULL,'0');
INSERT INTO [b435] VALUES (481,'<span data-type="title" id=toc-502>القرطبى ٦٧١ هجرية</span>

أبو عبد الله محمد بن أحمد بن أبى بكر بن نوح الأنصارى الخزرجى كان مقره منية أبن خصيب (محافظة المنيا بمصر) توفى ودفن بها وله كتاب جامع أحكام القرآن فى تفسير القرآن','1','445',NULL,NULL,'0');
INSERT INTO [b435] VALUES (482,'<span data-type="title" id=toc-503>القفال توفى سنة ٤٧١ هجرية</span>

عبد الله احمد المروزى أبو بكر فقيه شافعى كان وحيد زمانه فقها وحفظ، وزهدا كثير الآثار فى مذهب الأمام الشافعى له كتاب شرح فروع محمد بن الحداد المصرى فى الفقه وكانت صناعته عمل الأقفال قبل الاشتغال بالفقه يلقب بالقفال الصغير توفى بسجستان','1','444',NULL,NULL,'0');
INSERT INTO [b435] VALUES (483,'<span data-type="title" id=toc-504>القفال الشاش سنة ٥٠٧ هجرية</span>

محمد بن أحمد بن الحسين بن عمر أبو بكر الشاشى القفال الفارقى رئيس الشافعية بالعراق فى عصره ولد بميافارقين ورحل إلى بغداد فتولى بها التدريس فى المدرسة النظاميه وأستمر إلى أن توفى من كتبه " حلية العلماء " فى معرفة مذاهب الفقهاء " وغيره.','1','446',NULL,NULL,'0');
INSERT INTO [b435] VALUES (484,'<span data-type="title" id=toc-505>أبو قلابة سنة ١٠٤ هجرية</span>

عبد الله بن زيد بن عمرو الجرمى عالم بالقضاء والأحكام ناسك من أهل البصرة رفض القضاء وهرب إلى الشام فمات بها وكان من رجال الحديث الثقات','1','447',NULL,NULL,'0');
INSERT INTO [b435] VALUES (485,'<span data-type="title" id=toc-506>القليوبى سنة ١٠٦٩ هجرية</span>

أحمد بن احمد بن سلامة أبو العباسى شهاب الدين القليوبى، فقيه شافعى من أهل قليوب بمصر له حواش وشروح ورسائل وكتاب فى تراجم جماعة من أهل البيت سماه "تحفة الراغب "','1','449',NULL,NULL,'0');
INSERT INTO [b435] VALUES (486,'<span data-type="title" id=toc-507>القيروانى سنة ٣٨٦ هجرية</span>

ابن أبى زيد عبد الله بن عبد الرحمن النفزاوى القيروانى فقيه من أعيان القيروان مولده ومنشأه ووفاته بها كان إمام المالكية فى عصره يلقب بقطب المذهب وبمالك الأصفر له عدة كتب منها النوادر والزيادات نحو مائة جزء ومختصر المدونة وأشهر كتبه الرسالة فى اعتقاد أهل ال سنة','1','451',NULL,NULL,'0');
INSERT INTO [b435] VALUES (487,'<span data-type="title" id=toc-508>ابن القيم سنة ٧٥١ هجرية</span>

محمد بن أبى بكر بن أيوب بن سعد الزرعى الدمشقى أحد كبار العلماء مولده ووفاته فى دمشق تتلمذ لشيخ الإسلام ابن تيمية وهو الذى هذب كتبه ونشر علمه ألف تصانيف كثيرة منها أعلام الموقعبن الطرق الحكميه فى السياسة الشرعيه وغيرها','1','450',NULL,NULL,'0');
INSERT INTO [b435] VALUES (488,'<span data-type="title" id=toc-509>أبو القاسم سنة ١٩١ هجرية</span>

عبد الرحمن بن القاسم بن خالد بن جنادة العتقى المصرى فقية تفقه بالإمام مالك ونظرائه. جمع الزهد والعلم وله المدونة وهى من أجل كتب المالكية رواه عن الإمام مالك. مولده ووفاته بمصر.','1','452',NULL,NULL,'0');
INSERT INTO [b435] VALUES (489,'<span data-type="title" id=toc-510>أبو القاسم الفورانى سنة ٤١٦ هجرية</span>

عبد الرحمن بن محمد بن أحمد بن فوران أبو القاسم فقيه من علماء الأصول والفروع ومن كتابه الإبانة فى مذهب الشافعية وتتمة الإبانة وكان مقدم الشافعية بمرو.','1','453',NULL,NULL,'0');
INSERT INTO [b435] VALUES (490,'<span data-type="title" id=toc-511>القاسم القرن السادس</span>

محمد بن أحمد القاسانى نسبة إلى قاسان من بلاد الترك وقيل أنها من بلاد خراسان. وقيل القاشانى. وقال صاحب كتاب طبقات الزيدية أنه من أصحاب داوود الظاهرى، أنة خالفه فى مسائل كثيرة فى الأصول والفروع.','1','454',NULL,NULL,'0');
INSERT INTO [b435] VALUES (491,'<span data-type="title" id=toc-512>القاضى ٣٠٦ هجرية</span>

أحمد بن عمر بن سريح القاضى أبو العباسى البغدادى فقيه شافعى كان يقال لة البازالأشهب وكان يفضل على جميع أصحاب الشافعى. ولى القضاء بشيراز برع فى الفقة وعلم الكلام له مصنفات كثيرة منها الرد على ابن داود فى القياس. والرد عليه فى مسائل اعترض بها الشافعى.','1','455',NULL,NULL,'0');
INSERT INTO [b435] VALUES (492,'<span data-type="title" id=toc-513>القاضى ٤٦٢ هجرية</span>

الحسين بن محمد بن أحمد أبو على القاضى المرورذى أمام جليل من أصحاب الشافعى كان فقيها روى الحديت عن عبد الملك الإسفراييني وروى عنه عبد الرزاق المنعى وتلميذه محيى السنة البغوى وغيرهما وتفقه على القفال المروزى وكان يقال له حبر الأمة.','1','457',NULL,NULL,'0');
INSERT INTO [b435] VALUES (493,'<span data-type="title" id=toc-514>القاضى سنة ٤٥٩ هجرية</span>

محمد بن إسماعيل بن محمد بن إسماعيل بن أحمد بن عمرو القاضى أبو على العراقى الطوسى من أهلها ولى القضاء مدة بالطابران ولقب بالعراقى لطول مقامه ببغداد وكان فقيها فاضلا تفقه ببغداد على أبى حامد الإسفرايينى.','1','456',NULL,NULL,'0');
INSERT INTO [b435] VALUES (494,'<span data-type="title" id=toc-515>القاضى سنة ٤٤٩ هجرية</span>

محمد بن على بن عثمان الكراجكى أبو الفتح باحث إمامى من كبار أصحاب الشريف المرتضى له كتب منها " كنز الفوائد " و " النوادر، و " معونة الفارض فى الفرائض وغيرها. توفى بصور','1','458',NULL,NULL,'0');
INSERT INTO [b435] VALUES (495,'<span data-type="title" id=toc-516>القاضى سنة ٤٨٦ هجرية</span>

أبو على يعقوب بن إبراهيم بن سطور البرزينى فقيه حنبلى صنف كتبا فى الأصول والفروع، ولى القضاء ثم رفع يده عنه ثم عاد إليه، وكان متشددا فى السنة متعففا فى القضاء ومات وهو على القضاء.','1','459',NULL,NULL,'0');
INSERT INTO [b435] VALUES (496,'<span data-type="title" id=toc-517>القاضى سنة ٤٧٦ هجرية</span>

أبو الفتح عبد الوهاب بن أحمد بن عبد الوهاب بن حلبة الحرانى فقيه حنبلى كان يلى القضاء بحران، وكان ناشر للمذهب وتولى الإفتاء والخطابة والتدريس، استشهد عند اضطراب أهل حران هرب على ابن قريش.','1','460',NULL,NULL,'0');
INSERT INTO [b435] VALUES (497,'<span data-type="title" id=toc-518>القاضى عبد الجبار ٤١٥ هجرية</span>

عبد الجبار بن أحمد أبو الحسن الهمذانى الاسدابادى المعتزلى صاحب التصانيف.','1','463',NULL,NULL,'0');
INSERT INTO [b435] VALUES (498,'<span data-type="title" id=toc-519>القاضى عبد الوهاب سنة ٤٢٢ هجرية</span>

عبد الوهاب بن على بن نصر الثعلبى البغدادى أبو محمد. من فقهاء المالكية. ولد ببغداد، ولى القضاء ورحل إلى الشام وتوجه إلى مصر وتوفى فيها وله كتب منها التلقين فى فقه المالكية وعيون المسائل وشرح المدونة والنصرة. لمذهب مالك وغيرها.','1','464',NULL,NULL,'0');
INSERT INTO [b435] VALUES (499,'"<span data-type="title" id=toc-520>القاضى "عياض سنة ٥٤٤ هجرية</span>

عياض بن موسى بن عياض بن عمرون اليحصبى السبتى أبو الفضل عالم الغرب وإمام أهل الحديث فى وقته كان من اعلم الناس بأنساب العرب وكلامهم ولى قضاء سبتة ومولده فيها ثم قضاء غرناطة وتوفى بمراكش له تصانيف كثيرة منها الشفا بتعريف حقوق المصطفى وشرح صحيح مسلم.','1','461',NULL,NULL,'0');
INSERT INTO [b435] VALUES (500,'<span data-type="title" id=toc-521>ابو قتادة سنة ٤٠ هجرية</span>

هو ابن ربعى الأنصارى شهر باسم الحارث وأمة كبشة بنت مطهر بن حرام ابن سواد بن غنيم وكان يقال له فارس رسول الله شهد أحدا ومابعدها اختلفوا فى شهوده بدرا توفى بالكوفة.','1','462',NULL,NULL,'0');
INSERT INTO [b435] VALUES (501,'<span data-type="title" id=toc-522>القصار سنة ٢٧١ هجرية</span>

حمدون بن أحمد ين عمارة القصار النيسابورى أبو صالح صوفى وكان عالما فقيها يذهب مذهب الثورى.','1','465',NULL,NULL,'0');
INSERT INTO [b435] VALUES (502,'<span data-type="title" id=toc-523>القهستانى سنة ٩٥٣ هجرية</span>

محمد القهستانى شمس الدين فقيه حنفى كان مفتيا ببخارى له كتب منها فى الفقه جامع الرموز فى شرح النقاية مختصرالوقاية.','1','466',NULL,NULL,'0');
INSERT INTO [b435] VALUES (503,'<span data-type="title" id=toc-524>القيروانى "عبد الرحمن "سنة ٣٨٠ هجرية</span>

عهد الرحمن بن محمد ين رشيق القيروانى مؤرخ فقيه حافظ للحديث صنف كتبا فى فقه المالكية وفى أخبار العلماء والصالحين من كتبه " المستوعب" لزيادات مسائل المبسوط مما ليس فى المدونة. حج وأخذ من جماعة من علماء المشرق.','1','468',NULL,NULL,'0');
INSERT INTO [b435] VALUES (504,'<span data-type="title" id=toc-525>القاسم بن محمد توفى سنة ١٠٧ هجرية</span>.

القاسم بن أبى بكر الصديق أبو محمد أحد الفقهاء السبعة فى المدينة، ولد فيها وتوفى بقديد بين مكة
والمدينة، من سادات التابعين، كان صالحا ثقة، قال ابن عيينة كان القاسم أفضل أهل زمانه.','1','467',NULL,NULL,'0');
INSERT INTO [b435] VALUES (505,'<span data-type="title" id=toc-526>أبو القاسم الانماطى توفى سنة ٢٨٨ هجرية</span>.

أبوالقاسم عثمان بن سعيد بن بشار الأحول الأنماطى الفقية الشافعى كان من كبار فقهائهم، أخذ الفقه
عن المزنى والربيع بن سليمان المرادى وأخذ عنه أبو العباس بن سريج، مات ببغداد ونسب إلى الأنماط وهى البسط التى تفرش وأهل مصر يسمون هذه الآلات بالأنماط وبائعها بالأنماطى.','1','469',NULL,NULL,'0');
INSERT INTO [b435] VALUES (506,'<span data-type="title" id=toc-527>القاضى جعفر من فقهاء القرن السادس الهجرى</span>

القاضى جعفر بن أحمد بن عبد السلام ابن أبى يحيى المعتزلى قدم إلى " أب " وأظهر القول فيها بأن العباد يخلقون أفعالهم وأن القرآن مخلوق، ودعا الناس إلى ذلك، وسأل أهل السنة أن يناظروه فناظره العمرانى ذاكرا الأخبار المروية عن الرسول عليه الصلاه والسلام بالتحذير من القدرية، ورد عليه القاضى جعفر بكتاب سماه " الدافع للباطل من مذاهب الحنابل " وهو أحد قضاة الزيدية.','1','470',NULL,NULL,'0');
INSERT INTO [b435] VALUES (507,'<span data-type="title" id=toc-528>القاضى أبو الطيب الطبرى توفى سنة ٤٥٠ هجرية</span>.

طاهر بن عبد الله بن طاهر الطبرى، أبو الطيب، قاض من أعيان الشافعية ولد فى آمل طبرستان واستوطن بغداد وتوفى بها، له شرح مختصر المزنى.','1','471',NULL,NULL,'0');
INSERT INTO [b435] VALUES (508,'<span data-type="title" id=toc-529>قبيصة بن جابر الأسدى توفى سنة ٦٩ هجرية</span>.

قبيصة بن جابر بن وهب الأسدى الكوفى، تابعى من رجال الحديث الفصحاء، أحد فقهاء الطبقة الأولى من فقهاء أهل الكوفة بعد الصحابة.','1','472',NULL,NULL,'0');
INSERT INTO [b435] VALUES (509,'<span data-type="title" id=toc-530>قبيصة بن ذؤيب توفى سنة ٨٦ هجرية</span>.

قبيصة بن ذؤيب الخزاعى، صحابى من فقهاء الوجوه، ولد فى حياة النبى صلى الله عليه وسلم، كان على خاتم عبد الملك بن مروان بالشام توفى بدمشق.','1','473',NULL,NULL,'0');
INSERT INTO [b435] VALUES (510,'<span data-type="title" id=toc-531>قتادة توفى سنة ٢٣ هجرية</span>.

قتادة بن النعمان بن زيد بن عامر الأنصارى الأوسى، صحابى بدرى، كان من الرماة المشهورين، شهد المشاهد كلها، توفى بالمدينة وهو أخو أبو سعيد الخدرى لأمه.','1','474',NULL,NULL,'0');
INSERT INTO [b435] VALUES (511,'<span data-type="title" id=toc-532>قتادة توفى سنة ١١٨ هجرية</span>.

قتادة بن دعامة بن قتادة بن عزيز البصرى، مفسر حافظ، قال الإمام أحمد قتادة أحفظ أهل البصرة، كان عالما بالحديث ورأسا فى العربية، مات بواسط فى الطاعون.','1','475',NULL,NULL,'0');
INSERT INTO [b435] VALUES (512,'<span data-type="title" id=toc-533>ابن القصار توفى سنة ٣٩٨ هجرية</span>.

على بن أحمد البغدادى القاضى أبو الحسن المعروف بابن القصار، تفقه على الأبهرى، وله كتاب فى مسائل الخلاف، كان أصوليا ولى قضاء بغداد قال أبو ذر هو أفقه من رأيت من المالكيين، وكان ثقة قليل الحديث','1','476',NULL,NULL,'0');
INSERT INTO [b435] VALUES (513,'<span data-type="title" id=toc-534>ابن القطان توفى سنة ٣٥٩ هجرية</span>.

أحمد بن محمد بن أحمد بن القطان، فقيه شافعى من أهل بغداد، وتوفى بها، له مصنفات فى أصول الفقه وفروعه.','1','477',NULL,NULL,'0');
INSERT INTO [b435] VALUES (514,'<span data-type="title" id=toc-536>الكاشانى توفى سنة ٥٨٧ هجرية</span>

أبو بكر بن مسعود بن أحمد الكاشانى علاء الدين فقيه حنفى من أهل حلب له بدائع الصنائع فى ترتيب الشرائع والسلطان المبين فى أصول الدين توفى فى حلب','1','478',NULL,NULL,'0');
INSERT INTO [b435] VALUES (515,'<span data-type="title" id=toc-537>ابن كثير الحافظ سنة ٧٧٤ هجرية</span>

إسماعيل بن عمر بن كثير القرشى الدمشقى أبو الفداء عماد الدين حافط مؤرخ ولد فى قرية من أعمال بصرى الشام وانتقل مع أبيه إلى دمشق ورحل فى طلب العلم حتى صار من الأئمة المشار إليهم وتوفى بدمشق وهو صاحب التفسير المشهور والبداية والنهاية فى التاريخ','1','479',NULL,NULL,'0');
INSERT INTO [b435] VALUES (516,'<span data-type="title" id=toc-538>الكرخى ٣٤٠ هجرية</span>

هو عبيد الله بن الحسين الكرخى أبو الحسن فقيه انتهت اليه رياسة الحنفية بالعراق، مولده فى الكوفة ووفاته ببغداد له رسالة فى أصول الأحناف','1','481',NULL,NULL,'0');
INSERT INTO [b435] VALUES (517,'<span data-type="title" id=toc-539>الكسانى سنة ١٨٩ هجرية</span>

على بن حمزة بن عبد الله الأسدى بالولاء الكوفى إمام فى اللغة والنحو والقراءة ولد فى بعض قرى الكوفة وتعلم بها وتوفى با لرى عن سبعين عاما وله كتاب معانى القرآن وكتاب القراءات','1','480',NULL,NULL,'0');
INSERT INTO [b435] VALUES (518,'<span data-type="title" id=toc-540>الكعبى سنة ٣١٩ هجرية</span>

عبد الله بن محمود الكعبى أبو القاسم أحد أئمه المعتزلة وله آراء ومقالات فى الكلام انفرد بها وهو من أهل بلخ وتوفى بها له كتب منها التفسير وتأييد مقالة أبى الهذيل وغيرها','1','485',NULL,NULL,'0');
INSERT INTO [b435] VALUES (519,'<span data-type="title" id=toc-541>الكمال بن الهمام سنة ٨٦١ هجرية</span>

محمد بن عبد الواحد بن عبد الحميد بن مسعود السيواسى ثم الإسكندرى كمال الدين المعروف بابن الهمام، إمام من علماء الحنفية عارف بأصول الديانات والتفسير والفرائض والفقه. من سيواس، ولد بالإسكندرية ونبغ فى القاهرة وجاور بالحرمين ثم كان شيخ الشيوخ بالخانقاه بالشيخونية بمصر','1','482',NULL,NULL,'0');
INSERT INTO [b435] VALUES (520,'<span data-type="title" id=toc-542>الكرابيسى توفى سنة ٢٤٥ أو ٢٤٨ هجرية</span>.

أبو على الحسين بن على بن يزيد الكرابيسى البغدادى، صاحب الإمام الشافعى رضى الله عنهما وكان أحفظ الناس لمذهبه، له تصانيف كثيرة فى أصول الفقه وفروعه، وكان متكلما، عارفا بالحديث، أخذ عنه الفقه خلق كثير، ونسبته إلى الكرابيس وهى الثياب الغليظة وهو لفظ فارسى عرب وكان يبيعها فنسب إليها، قال ابن خلكان والأشبه بالصواب وفاته سنة ٢٤٨ هجرية.','1','483',NULL,NULL,'0');
INSERT INTO [b435] VALUES (521,'<span data-type="title" id=toc-543>الكرمانى توفى سنة ٥٤٣ هجرية</span>.

عبد الرحمن بن محمد بن أميرويه، أبو الفضل الكرمانى، فقيه حنفى وانتهت إليه رئاسة المذهب بخراسان وله كتب كثيرة، منها التجريد فى الفقه، الإيضاح فى شرح التجريد وشرح الجامع الكبير والفتاوى، ولد بكرمان ومات بمرو.','1','484',NULL,NULL,'0');
INSERT INTO [b435] VALUES (522,'<span data-type="title" id=toc-545>ابن اللبان سنة ٤٧٨ هجرية</span>

أبو الحسين بن اللبان محمد بن عبد الله بن الحسن عالم وقته فى الفرائض والمواريث من أهل البصرة له كتب فى الفرائض، قال السبكى ليس لأحد مثلها.','1','486',NULL,NULL,'0');
INSERT INTO [b435] VALUES (523,'<span data-type="title" id=toc-546>اللخمى توفى سنة ٤٧٨ هجرية</span>

أبو الحسن على بن محمد الربعى فقيه مالكى له معرفة بالأدب والحديث قيروانى الأصل نزل سفاقس وتوفى بها صنف كتبا مفيدة من أحسنها تعليق كبير على المدونة فى فقه المالكية سماه" النصرة "','1','487',NULL,NULL,'0');
INSERT INTO [b435] VALUES (524,'<span data-type="title" id=toc-547>أبو لهب سنة ٢ هجرية</span>

عبد العزى بن عبد المطلب بن هاشم، عم الرسول صلى الله عليه وسلم، وكان من أشد الناس عداوة له بعد ظهور الإسلام، وفيه نزلت سورة " تبت يد أبى لهب وتب "','1','489',NULL,NULL,'0');
INSERT INTO [b435] VALUES (525,'<span data-type="title" id=toc-548>الليث سنة ١٥٧ هجرية</span>

الليث بن سعد بن عبد الرحمن الفهمى بالولاء إمام أهل مصر فى عصره. حديثا وفقها كانت له مكانة عظمى حتى أن القاضى والنائب من تحت أمره ومشورته أصله من خراسان ومولده فى قلقشنده ووفاته بالقاهرة','1','488',NULL,NULL,'0');
INSERT INTO [b435] VALUES (526,'<span data-type="title" id=toc-549>أبوليث</span>

نصر بن محمد بن أحمد بن ابراهيم السمرقندى أبو الليث الملقب بإمام الهدى من ائمة ال الحنفية له تصانيف نفيسة منها خزانة الفقه والمقدمة فى الفقه وشرح الجامع الصغير فى الحديث ومختلف الرواية فى الخلافيات يين أبى حنيفة ومالك والشافعى','1','491',NULL,NULL,'0');
INSERT INTO [b435] VALUES (527,'<span data-type="title" id=toc-550>ابن ابى ليلى توفى سنة ١٤٨ هجرية</span>

محمد بن عبد الرحمن بن أبى ليلى الأنصارى الكوفى الفقيه قاضى الكوفة من أصحا الرأى له أخبار مع الإمام أبى حنيفة وغيره ومات بالكوفة.','1','490',NULL,NULL,'0');
INSERT INTO [b435] VALUES (528,'<span data-type="title" id=toc-551>أبو الليث سنة ٣١٤ هجرية</span>

نصر بن القاسم أبو الليث البغدادى الفرائضى نسبة إلى علم الفرائض الذى هو علم المواريث روى عن شريح بن يونس وأقرانه وكان ثقة من فقهاء أهل الرأى.','1','492',NULL,NULL,'0');
INSERT INTO [b435] VALUES (529,'<span data-type="title" id=toc-552>ابن الماجشون سنة ٢١٢ هجرية</span>

عبد الملك بن عبد العزيز بن عبد الله التيمى بالولاء أبو مروان ابن الماجشون فقيه مالكى فصيح دارت عليه الفتيا فى زمانه وعلى أبيه قبله.','1','493',NULL,NULL,'0');
INSERT INTO [b435] VALUES (530,'<span data-type="title" id=toc-553>ابن لقمان توفى سنة ١٠٣٩ هجرية</span>.

أحمد بن محمد بن لقمان بن أحمد بن شمس الدين بن الإمام المهدى أحمد بن يحيى، فقيه زيدى من علماء اليمن، له شروح وتعاليق منها شرح الكافل فى علم الأصول.','1','494',NULL,NULL,'0');
INSERT INTO [b435] VALUES (531,'<span data-type="title" id=toc-554>اللكنوى توفى سنة ١١٨٠ هجرية</span>.

عبد العلى محمد بن نظام الدين محمد اللكنوى الأنصارى المكنى بأبى العباس الملقب ببحر العلوم فقيه حنفى أصولى منطقى كان من نوابغ القرن الثانى عشر له كتب كثيرة من أشهرها فواتح الرحموت شرح مسلم الثبوت وتنوير المنار وهو شرح على منار الأنوار للنسفى وكلاهما فى الأصول ورسائل الأركان فى الفقه.','1','495',NULL,NULL,'0');
INSERT INTO [b435] VALUES (532,'<span data-type="title" id=toc-555>ابن ماجة سنة ٢٧٣</span>

محمد بن يزيد الربعى القزوينى أبوعبد الله بن ماجه أحد الائمة فى علم الحديث من أهل قزوين، رحل الى البصرة وبغداد والشام ومصروالحجاز والرى فى طلب الحديث وصنف كتابه " سنن ابن ماجة " وهو أحد الكتب الستة المعتمدة وله غيره فى التفسير والتاريخ.','1','498',NULL,NULL,'0');
INSERT INTO [b435] VALUES (533,'<span data-type="title" id=toc-557>الماتريدى سنة ٣٣٣ هجرية</span>

محمد بن محمد بن محمود الماتريدى أمام المتكلمين فى عصره. وسمى بالماتريدى نسبةالى بلدته "ما تريد" محله بسمرقند وله فى الفقه كتاب مأخذ الشرائع وفى الأصول كتاب الجدل','1','496',NULL,NULL,'0');
INSERT INTO [b435] VALUES (534,'<span data-type="title" id=toc-558>المازرى سنة ٥٣٦ هجرية</span>

محمد بن على بن عمر التميمى محدث من فقهاء المالكية منسوب إلى مازر بجزيرة صقليه ووفاته بالمهديه من كتبه " المعلم بفوائد مسلم" والتلقينفى الفروع وغيرهما.','1','497',NULL,NULL,'0');
INSERT INTO [b435] VALUES (535,'<span data-type="title" id=toc-559>مالك سنة ١٧٩</span>

مالك بن أنس بن مالك الأصبحى الحميرى أبو عبد الله، إمام دار الهجرة وأحد الأئمة الأربعه مولده ووفاته فى المدينة، كان صلبا فى دينه بعيدا عن الامراء والملوك له كتاب المؤطأ ورسالة فى الوعظ وكتاب المسائل ورسالة فى الرد على القدرية وتفسير غريب القرآن.','1','499',NULL,NULL,'0');
INSERT INTO [b435] VALUES (536,'<span data-type="title" id=toc-560>الماوردى توفى سنة ٤٥٠ هجرية</span>

أبو الحسن على بن محمد بن حبيب، أقضى قضاة عصره، من العلماء الباحثين أصحاب التصانيف الكثيرة النافعة ولد فى البصرة، وانتقل الى بغداد وكان يميل إلى مذهب الاعتزال نسبته إلى بيع ماء الورد ووفاته ببغداد، من كتبه "أدب الدنيا والدين " الأحكام السلطانية و" الحاوى " فى فقه الشافعية وغيرها.','1','500',NULL,NULL,'0');
INSERT INTO [b435] VALUES (537,'<span data-type="title" id=toc-561>المؤيد بالله سنة توفى سنة ٤١١ هجرية</span>.

أحمد بن الحسين بن هارون الحسنى الآملى مبرزا فى علم النحوواللغة والحديث وغيرها ولد بأمل طبرستان وبويع بالخلافه سنه ٣٨٠ هجرية.','2','1',NULL,NULL,'0');
INSERT INTO [b435] VALUES (538,'<span data-type="title" id=toc-562>محمد بن الحسن سنة ١٨٩ هجرية</span>

محمد بن الحسن بن فرقد من موالى بنى شيبان إمام فى الفقه والأصول وهو الذى نشر علم أبى حنيفة اصله من قرية هوسته فى غوطة دمشق وولد بواسطة نشأ باكوفة فسمع من أبى حنيفة وغلب عليه مذهبه وعرف به وانتقل الى بغداد فولاه الرشيد القضاء بالرقة ثم عزله مات فى الرى.','2','2',NULL,NULL,'0');
INSERT INTO [b435] VALUES (539,'<span data-type="title" id=toc-563>محمد عبده توفى سنة ١٩٠٥م</span>

محمد عبده بن حسن خير الله من آل التركمانى مفتى الديار المصرية ومن كبار رجال الإصلاح والتجديد فى الاسلام ولد فى شنرا من قرى الغربية بمصر ونشأ فى محلة نصر بالبحيرة وتعلم بالجامع الاحمدى بطنطا ثم الأزهر، وقد تقلب فى شتى الشئون والوظانف والبلدان من مؤلفاته الاسلام والنصرانية وهى كثيرة متداوله.','2','3',NULL,NULL,'0');
INSERT INTO [b435] VALUES (540,'<span data-type="title" id=toc-564>محمد بن على سنة ٤٤٩ هجرية</span>

محمد بن على بن عثمان الكراجكى أبو الفتح باحث امامى من كبار أصحاب الشريف المرتضى، له كتب منها "كنز الفوائد ومعونة الفارض " فى الفرائض وغيرها توفى بصور.','2','4',NULL,NULL,'0');
INSERT INTO [b435] VALUES (541,'<span data-type="title" id=toc-565>محمد بن المنصور</span>

محمد بن منصور بن يزيد المرادى الكوفى الزيدى أحد أئمة الزيدية وجامع أقوال الأئمة مؤلفاته كثيرة جمع تفسير الغريب للإمام زيد بن على توفى سنة نيف وتسعين ومائتين.','2','5',NULL,NULL,'0');
INSERT INTO [b435] VALUES (542,'<span data-type="title" id=toc-566>المرتضى سنة ٣١٠ هجرية</span>

محمد بن الامام يحيى الهادى بن الحسين الحسنى الإمام المرتضى، كان عالما بالفقه وأصول الدين له فى الفقه كتابان " الايضاح والنوازل " وغيرهما.','2','6',NULL,NULL,'0');
INSERT INTO [b435] VALUES (543,'<span data-type="title" id=toc-567>المرغينانى سنة ٥٩٣ هجرية</span>

برهان الدين أبو الحسن على بن أبى بكر بن عبد الجليل الرشداني المرغينانى فقيه من فقهاء الأحناف وله كتاب بداية المبتدى وشرحه الهداية','2','8',NULL,NULL,'0');
INSERT INTO [b435] VALUES (544,'<span data-type="title" id=toc-568>مروان بن الحكم توفى سنة ٦٥ هجرية</span>

مروان بن الحكم بن أبى العاص بن أمية أبو عبد الملك الخليفة الأموى هو أول من ملك من بنى الحكم وإليه ينسب بنو مروان ولد بمكه ونشأ بالطائف وسكن المدينة وجعله عثمان فى خاصته وإتخذه كاتبا له، تقلبت به الأحوال حتى توفى فى دمشق بالطاعون','2','7',NULL,NULL,'0');
INSERT INTO [b435] VALUES (545,'<span data-type="title" id=toc-569>المروزى</span>

محمد بن نصر المروزى أبو عبد الله إمام فى الفقه والحديث ولد ببغداد ونشأ بنيسابور ثم استوطن سمرقند وتوفى بها له كتب كثيرة منها القسامة فى الفقة','2','11',NULL,NULL,'0');
INSERT INTO [b435] VALUES (546,'<span data-type="title" id=toc-570>المزنى سنة ٢٦٤ هجرية</span>

إسماعيل بن يحيى بن إسماعيل أبو إبراهيم المزنى نسبة الى مزينة من مضر صاحب الأمام الشافعى من أهل مصر كان زاهدا عالما مجتهدا قوى الحجة له كتاب الجامع الكبير والجامع الصغيرالمختصر','2','9',NULL,NULL,'0');
INSERT INTO [b435] VALUES (547,'<span data-type="title" id=toc-571>مسروق سنة ٦٣ هجرية</span>

مسروق بن الأجدع بن مالك الهمدانى تابعى ثقة من أهل اليمن قدم المدينة فى أيام أبى بكر وشهد حروب على كان أعلم بالفتيا من شريح','2','10',NULL,NULL,'0');
INSERT INTO [b435] VALUES (548,'<span data-type="title" id=toc-572>مسلم توفى سنة ٢٦١ هجرية</span>

مسلم بن الحجاج بن مسلم القشيرى النيسابورى ابو الحسين حافط من أئمة المحدثين ولد بنيسابور ورحل الى الحجاز ومصروالشام والعراق وتوفى بظاهر نيسابور وأشهر كتبه صحيح مسلم جمع فيه اثنى عثسر ألف حديث كتبها فى خمس عشرة سنة وهو أحد الصحيحين المعول عليها عند أهل ال سنة فى الحديث وقد شرحه كثيرون وله كتب اخرى','2','12',NULL,NULL,'0');
INSERT INTO [b435] VALUES (549,'<span data-type="title" id=toc-573>ابن مظعون توفى سنة ٢ هجرية</span>

عثمان بن مظعون بن حبيب بن وهب بن حذافه بن جمح الجمحى، أسلم بعد ثلاثه عشر رجلا وهاجر الى الحبثسه هو وابنه السائب فى الهجرة الأولى وفى الصحيحين عن سعد بن أبى وقاص قال: رد النبى صلى الله عليه وسلم علىعثمان بن مظعون التبتل ولو أذن له توفى بعد شهوده بد را وهو أول من مات بالمدينة من المهاجرين واول من دفن بالبقيع منهم','2','13',NULL,NULL,'0');
INSERT INTO [b435] VALUES (550,'<span data-type="title" id=toc-574>أبوالمظفر</span>

محيى الدين بن محمد أورنك زيب بهادر عالم كبير بادشاه واحد من سلاطين الهند الغازين فى سبيل الله ممن عنوا بالشريعة الإسلامية ونشر أثارها فقرب العلماء واستعان بهم وكان من آثاره كتاب " الفتاوى الهندية " التى حشد لها العلماء وفتح لهم مكتبة وأغدق عليهم العطايا حتى ظهرت وصارت من أهم المراجع فى الفقة الحنفى','2','14',NULL,NULL,'0');
INSERT INTO [b435] VALUES (551,'<span data-type="title" id=toc-575>معاذ بن جبل سنة ١٨ هجرية</span>

- معاذ بن جبل بن عمرو بن آوس الأنصارى الخزرجى أبو عبد الرحمن صحابى جليل وكان أعلم الأئمة بالحلا ل والحرام وهو أحد الستة الذين جمعوا القرآن على عهد النبى صلى الله عليه وسلم ش هجريةد العقبة مع الأنصار السبعين وشهد بدرا واحدا الخندق والمشاهد كلها مع الرسول صلى الله عليه وسلم وبعثه الرسول صلى الله عليه وسلم قاضيا ومرشدا لأهل اليمن توفى بناحية الاردن','2','16',NULL,NULL,'0');
INSERT INTO [b435] VALUES (552,'<span data-type="title" id=toc-576>المعافى النهروانى سنة ٣٩٠ هجرية</span>

المعافى بن زكريا بن يحيى الجريرى النهروانى ابو الفرج بن طرار من الأدباء الفقهاء مولده ووفاته بالنهروان فى العراق ولى القضاء ببغداد له تصانيف كثيرة','2','15',NULL,NULL,'0');
INSERT INTO [b435] VALUES (553,'<span data-type="title" id=toc-577>معاوية بن أبى سفيان سنة ٦٠ هجرية</span>

معاوية بن أبى سفيان صخر بن حرب القرشى الأموى مؤسس الدولة الأموية فى الشام واحد دهاة العرب ولد بمكة قبل الهجرة بعشرين سنة واسلم يوم فتحها سنة ٨ هجرية وكان من كتاب الوحى بويع له بالخلافة بعد الحسن بن على سنة ٤١ هجرية ومات فى دمشق سنة ٦٠ هجرية','2','17',NULL,NULL,'0');
INSERT INTO [b435] VALUES (554,'<span data-type="title" id=toc-578>معاوية بن عمار سنة ١٤٥ هجرية</span>

معاوية، بن عمار العبدى الدهنى من مشايخ الشيعة من أهل الكوفة أخذ عن سعيد بن جبير وروى عنه الثورى له كتاب الحديث','2','18',NULL,NULL,'0');
INSERT INTO [b435] VALUES (555,'<span data-type="title" id=toc-579>أبومعشر الدارمى سنة ٥٨ هجرية</span>

صحابى جليل جاء فيه حديث الرسول صلى الله عليه رسلم "انت ومالك لأيبك"','2','19',NULL,NULL,'0');
INSERT INTO [b435] VALUES (556,'<span data-type="title" id=toc-580>المعلى بن منصور توفى سنة ٢١١ هجرية</span>

المعلى بن منصور الرازى أبو يعلى من رجال الحديث ثقة من أصحاب أبى يوسف ومحمد بن الحسن وأخذ عنه كثيرون، طلب للقضاء غير مرة فأبى، أصله من الرى وسكن بغداد من كتبه " النوادر " و "الأمالى" كلاهما فى الفقه','2','20',NULL,NULL,'0');
INSERT INTO [b435] VALUES (557,'<span data-type="title" id=toc-581>معمر سنة ١٥٣ هجرية</span>

معمر بن راشد بن أبى العمر الازدى ابو عروة فقيه حافظ متقن ثقه، من أهل البصرة ولد واشتهر فيها وسكن اليمن وهو عند مؤرخى رجال الحديث أول من صنف باليمن','2','21',NULL,NULL,'0');
INSERT INTO [b435] VALUES (558,'<span data-type="title" id=toc-582>ابن مفلح سنة ٧٦٣ هجرية</span>

محمد بن مفلح بن محمد بن مفرج آبو عبد الله اعلم أهل عصره بمذهب ألامام أحمد بن حنبل ولد ونشأ فى ييت المقدس وتوفى بصالحية دمشق من مؤلفاته كتاب الفروع والنكت ؤالفوائد السنية على مشكل المحررلابن تيمية وغيرها','2','22',NULL,NULL,'0');
INSERT INTO [b435] VALUES (559,'<span data-type="title" id=toc-583>المقداد سنة ٣٣، ٣٧ هجرية</span>

المقداد بن عمرو ويعرف بابن الأسود الكندى البهرانى الحضرمى أبو معبد أول أبو عمرو، صحابى من الأبطال وهوأحد السبعة الذين كانوا أول من أظهر الاسلام، شهد بدرا وغيرها وسكن المدينة ودفن بها','2','146',NULL,NULL,'0');
INSERT INTO [b435] VALUES (560,'<span data-type="title" id=toc-584>المقرى</span>

محمد بن محمد بن أحمد أبو بكر التلمساني باحث من الفقهاء الأدباء المتصوفين من علماء الحنابلة، ولد وتعلم بتلممسان وبها دفن بعد وفاته بفاس وهو جد الأديب صاحب نفح الطيب ومن مؤلفاته القواعد ويشتمل على ١٢٠٠ قاعدة','2','23',NULL,NULL,'0');
INSERT INTO [b435] VALUES (561,'<span data-type="title" id=toc-585>ابن ملك٨٠١ هجرية</span>

عبد اللطيف بن عبد العزيز بن آمين الدين بن فرشتا الكرمانى المعروف. بابن ملك فقيه حنفى له شرح مجمع البحرين فى الفقة وشرح المنار فى الأصول وغيرهما','2','24',NULL,NULL,'0');
INSERT INTO [b435] VALUES (562,'<span data-type="title" id=toc-586>ابن ابى ملكية توفى سنة ١٧٧ هجرية</span>

ولأه أبن الزبير قضاء الطائف','2','25',NULL,NULL,'0');
INSERT INTO [b435] VALUES (563,'<span data-type="title" id=toc-587>ابن المنذر سنة ٢٧٧ هجرية</span>

محمد بن ادريس بن المنذر بن داود بن مهرأن أبو حاتم الحنظلى الرازى كان أحد الأئمة الحفاظ سمع محمد بن عبد الله الانصارى وأبا زيد النحوى وعثمان بن الهيثم المؤذن والأمام أحمد وآخرين وروى عنه يونس بن عبد الأعلى، والربيع بن سليمان الريان وغيرهما. قدم بغداد وحدث ب هجريةا. وأول كتبه الحديث سنه تسع ومائتين','2','26',NULL,NULL,'0');
INSERT INTO [b435] VALUES (564,'<span data-type="title" id=toc-588>المنصور سنة ١٥٨ هجرية</span>

عبد الله بن محمد بن على بن العباس أبو جعفر المنصور ثانى خلفاء بنى العباس وأول من عنى بالعلوم من ملوك العرب كان عارفا بالفقه والأدب محبا للادباء وهو بانى مدينة بغداد دفن بالحجون بمكه','2','27',NULL,NULL,'0');
INSERT INTO [b435] VALUES (565,'<span data-type="title" id=toc-589>المنصور بالله سنة ٦٤١ هجرية</span>

عبد الله بن حمزة بن سليمان بن على بن حمزة بن أبى هاشم الحسنى القاسمى المنصور بالله أحد ائمة الزيدية وله مؤلفات كثيرة أهمها كتاب الشافى','2','28',NULL,NULL,'0');
INSERT INTO [b435] VALUES (566,'<span data-type="title" id=toc-590>ابو منصور توفى سنة ٤٦٠ هجرية</span>

أبو منصور على بن الحسن القرميسين فقيه حنبلى ودفن بمقبرة الامام أحمد','2','29',NULL,NULL,'0');
INSERT INTO [b435] VALUES (567,'<span data-type="title" id=toc-591>ابن منظور سنة ٧١١ هجرية</span>

محمد بن مكرم بن على جمال الدين بن منظور صاحب لسان العرب الإمام اللغوى الحجة ولد بمصر وخدم فى ديوان الانشاء بالقاهرة وتوفى بمصر ومن مؤلفاته مختصر الأغانى','2','30',NULL,NULL,'0');
INSERT INTO [b435] VALUES (568,'<span data-type="title" id=toc-592>ابو موسى الاشعرى سنة ٤٤ هجرية</span>

عبد الله بن قيس بن سليم بن حضار بن حرب من بنى الأشعر بن قحطان صحابى من الولاة الفاتحين وأحد الحكمين اللذين رضى بهما على ومعاويه ولد فى زبيد باليمن وقدم مكة عند ظهور الاسلام واسلم وهاجر الى أرض الحبشة ثم استعمله الرسول على الزبيد وعدن وولاه عمر البصرة سنة ١٧ هجرية وأقره عثمان عليها وعزله على توفى بالكوفة','2','31',NULL,NULL,'0');
INSERT INTO [b435] VALUES (569,'<span data-type="title" id=toc-593>ابن مودود الموصلى سنة ٦٨٣</span>

- عبد الله بن محمود بن مودود الموصلى، فقيه حنفى، ولد بالوصل ورحل الى دمشق وولى قضاء الكوفة مدة واستمر ببغداد مدرسا وفيها توفى، له كتاب المختار وشرحه الاختيار','2','38',NULL,NULL,'0');
INSERT INTO [b435] VALUES (570,'<span data-type="title" id=toc-594>ابن المواز توفى سنة ٢٨١ هجرية</span>

محمد بن ابراهيم بن زياد المواز أبو عبد الله فقيه مالكى من أهل - الاسكندرية انتهت إليه رياسة المذهب فى عصره','2','32',NULL,NULL,'0');
INSERT INTO [b435] VALUES (571,'<span data-type="title" id=toc-595>الميدانى ١٢٩٨ هجرية</span>

عبد الغنى بن طالب بن حماده الغنيمى الدمشقى من فقهاء الحنفية، وهو صاحب كتاب اللباب شرح القدورى وشروح ورسائل فى الصرف والتوحيد','2','33',NULL,NULL,'0');
INSERT INTO [b435] VALUES (572,'<span data-type="title" id=toc-596>الميراغنانى</span>

برهان الدين محمود بن أحمد صاحب كتاب المحيط البرهانى فى فقه الحنفية','2','34',NULL,NULL,'0');
INSERT INTO [b435] VALUES (573,'<span data-type="title" id=toc-597>ماعز</span>

ماعز بن مالك الأسلمى الصحابى، عده ابن عبد البرفى المدنيين له مع رسول الله صلى الله عليه وسلم واقعة مشهورة وهى اعترافه على نفسه بالزنا فرجم لذلك، كتب له رسول الله صلى الله عليه وسلم بإسلام قومه.','2','36',NULL,NULL,'0');
INSERT INTO [b435] VALUES (574,'<span data-type="title" id=toc-598>مجزر المدلجى صحابى</span>

" مجزر بن الأعور بن جعدة بن معاز عتواره بن مدلج الكنانى مذكور فى الصحيحين من طريق الزهرى سعى مجززا لأنه كان إذا أسر أسيرا جز ناصيته وأطلقه شهد فتح مصر وبعض الفتوح بعد النبى صلى الله عليه وسلم، وكان معروفا بالقيافة.','2','35',NULL,NULL,'0');
INSERT INTO [b435] VALUES (575,'<span data-type="title" id=toc-599>محمد بن سلمة سنة ٢٧٨ هجرية</span>

أبو عبد الله محمد بن سلمة الفقيه البلخى تفقه على شداد بن حكيم ثم على أبى سليمان الجوزانى ولد سنة ١٩٢ هجرية.','2','37',NULL,NULL,'0');
INSERT INTO [b435] VALUES (576,'<span data-type="title" id=toc-600>محيصة صحابى</span>

محيصة بن مسعود بن كعب بن عامر بن عدى الانصارى الأوسى. الحارثى الصحابى بعثه رسول الله صلى الله عليه وسلم إلى أهل فدك يدعوهم إلى الإسلام وشهد أحدا والخندق وما بعدهما من المشاهد وكان إسلامه قبل الهجرة روى عنه ابنه سعد وحفيده مرام ومحمد ين أبي سهل وغيرهم.','2','39',NULL,NULL,'0');
INSERT INTO [b435] VALUES (577,'<span data-type="title" id=toc-601>المرداوى سنة ٨٨٥ هجرية</span>

على بن سليمان بن أحمد المرداوى ثم الدمشقى فقيه حنبلى ولد فى مزدى قرب نابلس انتقل إلى دمشق وتوفى بها من كتبه الإنصاف فى معرفة الراجح من الخلاف وغيره من كتب الفقه وله فى الأصول تحرير المنقول وشرح التحبير فى شرح التحرير.','2','40',NULL,NULL,'0');
INSERT INTO [b435] VALUES (578,'<span data-type="title" id=toc-602>المصيصى القرن الثالث</span>

محمد بن داوود بن صباح أبو جعفر المصيصى. كان من خواص الإمام أحمد وكان يحدثة بأشياء لايحدث بها غيره وله روايات كثيرة عن الإمام أحمد وروى عنه كثيرون.','2','42',NULL,NULL,'0');
INSERT INTO [b435] VALUES (579,'<span data-type="title" id=toc-603>مطرف بن مازن سنة ٢٢ هجرية</span>

مطرف بن مازن راوية الإمام مالك مات بالمدينة.','2','41',NULL,NULL,'0');
INSERT INTO [b435] VALUES (580,'<span data-type="title" id=toc-604>المقداد بن الأسود</span>

المقداد بن الأسود الكندى من السابقين للإسلام وفاجر الهجرتين وشهد بدرا وجميع المشاهد. روى عنه على وأنس توفى فى خلافة عثمان.','2','43',NULL,NULL,'0');
INSERT INTO [b435] VALUES (581,'<span data-type="title" id=toc-605>ابن منصور سنة ٢٥١ هجرية</span>

أحمد بن منصور بن سيار الرمادى أبو بكر فقيه حنبل سمع من عبد الرزاق ابن همام وغيره، وروى عنه جماعة، وروى عن الإمام أحمد.','2','44',NULL,NULL,'0');
INSERT INTO [b435] VALUES (582,'<span data-type="title" id=toc-606>ابن منصور سنة ٢٥٤ هجرية</span>

إسحاق بن منصور بن بهرام أبو يعقوب، فقيه حنبلى ولد بمرو ودخل إلى العراق والحجاز والشام فسمع سفيان بن عيينة ويحيى بن سعيدالقطان، ووكيع وغيرهم، دون عن الإمام أحمد مسائل فى الفقة، وكان ثقة مأمونا.','2','45',NULL,NULL,'0');
INSERT INTO [b435] VALUES (583,'<span data-type="title" id=toc-607>ابن منصور سنة ٢٥٤ هجرية</span>

محمد بن منصور بن داود بن إبراهيم أبو جعفر العابد المعروف بالطوسى فقيه حنبلى تلفى عن الإمام احمد ونقل عنه مسائل لم يروها غيرها وكان مقربا لصلاحه وورعه.','2','46',NULL,NULL,'0');
INSERT INTO [b435] VALUES (584,'<span data-type="title" id=toc-608>منصور بن حازم</span>

أبو أيوب البجلى من جملة أصحاب الشيعة الإمامية وفقهائهم، روى عن الصادق والكاظم، وله كتب منها " "أصول الشرائع" و "كتاب الحج".','2','47',NULL,NULL,'0');
INSERT INTO [b435] VALUES (585,'<span data-type="title" id=toc-609>منصور سنة ١٣٢ هجرية</span>

منصور بن المعتمر بن عبد الله بن ربيعة السلمى أبو عتاب من كبار تابعى التابعين ومن أعلام رجال الحديث بالكوفة ولم يكن فيها أحفظ للحديث منه سمع النخعى والشعبى والزهرى وسعيد بن جبير وروى عنه الثورى وهو أثبت الناس فيه.','2','48',NULL,NULL,'0');
INSERT INTO [b435] VALUES (586,'<span data-type="title" id=toc-610>المهدى١٢٥١ هجرية</span>

عبد الله بن أحمد ين المتوكل بن على من حفدة الهادى من أئمة الزيدية من أهل صنعاء مولدا ووفاة.','2','53',NULL,NULL,'0');
INSERT INTO [b435] VALUES (587,'<span data-type="title" id=toc-611>مهنا القرن الثالث</span>

مهنا بن يحيى الشامى السلمى أبو عبيد الله من كبار أصحاب الإمام وروى عنه مسائل كثيرة وكتب عنه عبد الله بن أحمد عدة مسائل قال مهنا لزمت أبا عبد الله ثلاثا وأربعين سنة.','2','49',NULL,NULL,'0');
INSERT INTO [b435] VALUES (588,'<span data-type="title" id=toc-612>المواق سنة ٧٩٧ هجرية</span>

أحمد بن يوسف بن القاسم بن يوسف العبدلى الغرناطى أبو عبد الله فقيه مالكى كان عالم غرناطة وإمامها فى وقته. له مؤلفات منها " التاج والإكليل فى شرح مختصر خليل. مطبوع مع الخطاب وله أيضا سنن المهتدين فى مقامات الدين.','2','50',NULL,NULL,'0');
INSERT INTO [b435] VALUES (589,'<span data-type="title" id=toc-613>ابن أبى موسى القرن الثالث</span>

محمد بن موسى النهرتيرى البغدادى أبو عبد الله فقيه حنبلى تتلمذ على أحمد ذكره أبو بكر الخلال فقال فلان عنده عن أبى عبد الله جزء مسائل كبار جياد. وذكر. الدارقطنى فقال شيخ لأهل بغداد جليل ٠ وقال عنه الخطيب: كان ثقة فاضلا جليلا وكان مقربا وصحب ابن سعدان وروى عنه جماعة منهم أبو حسين بن النادى.','2','51',NULL,NULL,'0');
INSERT INTO [b435] VALUES (590,'<span data-type="title" id=toc-614>المتولى توفى ٤٧٨ هجرية</span>

عبد الرحمن بن مأمون النيسابورى أبو سعد، المعروف بالمتولى، فقيه مناظر، عالم بالأصول، ولد بنيسابور وتعلم بمرو. وتولى التدريس ببغداد وتوفى بها، له كتب منها تتمة الإبانة للفورانى فى فقه الشافعية وكتاب الفرائض وكتاب فى أصول الدين.','2','54',NULL,NULL,'0');
INSERT INTO [b435] VALUES (591,'<span data-type="title" id=toc-615>مجاهد توفى سنة ١٠٤ هجرية</span>

مجاهد بن جبر أبو الحجاج المكى مولى بنى مخزوم، تابعى مفسر من أهل مكة، كان شيخ القراء والمفسرين أخذ التفسير عن ابن عباس، تنقل فى الأسفار واستقر فى الكوفة، قيل إنه مات وهو ساجد.','2','52',NULL,NULL,'0');
INSERT INTO [b435] VALUES (592,'<span data-type="title" id=toc-616>المحب الطبرى توفى سنة ٦٩٤ هجرية</span>.

أحمد بن عبد الله بن محمد الطبرى، أبوالعباس محب الدين حافظ، فقيه شافعى، ولد ومات بمكة، وكان شيخ الحرام فيها، له تصانيف منها السمت الثمين فى مناقب أمهات المؤمنين، والأحكام، وغيرهما.','2','55',NULL,NULL,'0');
INSERT INTO [b435] VALUES (593,'<span data-type="title" id=toc-617>محمد بن أبى بكر</span>

محمد بن أبى بكر الصديق رضى الله عنه، وأمه أسماء بنت عميس.','2','57',NULL,NULL,'0');
INSERT INTO [b435] VALUES (594,'<span data-type="title" id=toc-618>ابن المرتضى توفى سنة ٨٤٠ هجرية</span>.

محمد بن إبراهيم بن على بن المرتضى بن الفضل الحسنى القاسمى، أبو عبد الله، مجتهد باحث، من أعيان اليمن، وهو أخو الهادى بن إبراهيم وأقبل فى أوأخر أيامه على العبادة تعلم بصنعاء وصعدة ومكة، له كتب نفائس منها إيثار الحق على الخلق، والروض الباسم عن سنة أبى القاسم وحصر آيات الأحكام الشرعية وغيرها.','2','56',NULL,NULL,'0');
INSERT INTO [b435] VALUES (595,'<span data-type="title" id=toc-619>ابن مرزوق توفى سنة ٧٨١ هجرية</span>.

محمد بن أحمد بن محمد بن مرزوق العجيسى أبو عبد الله شمس الدين، فقيه من أعيان تلمسان، رحل إلى المشرق سنة ٧١٨ هجرية مع والده وأقام بمصر مدة وعاد إلى تلمسان، تولى أعمالا علمية وسياسية بها ثم رحل إلى القاهرة واستمر بها إلى أن توفى له كتب منها شرح عمدة الأحكام فى الحديث وشرح الشفا والإمامة وعقيدة أهل التوحيد المخرجة من ظلمات التقليد.','2','58',NULL,NULL,'0');
INSERT INTO [b435] VALUES (596,'<span data-type="title" id=toc-620>مروان توفى سنة ٦٥ هجرية</span>.

مروان بن الحكم بن أبى العاص بن أمية القرشى، الأمرى، وهو ابن عم عثمان بن عفان، ولد على عهد رسول الله صلى الله عليه وسلم، استعمله معاوية على المدينة وأخرجه منها عبد الله بن الزبير، وانتقل إلى الشام مدة ثم رحل إلى الجابية ودعا لنفسه فبايعه أهل الأردن سنة٦٤ هجرية وتوفى بدمشق وهو أول من ضرب الدنانير الشامية وكتب عليها قل هو الله أحد.','2','147',NULL,NULL,'0');
INSERT INTO [b435] VALUES (597,'<span data-type="title" id=toc-621>مسلمة توفى سنة ١٢٠ هجرية</span>.

مسلمة بن عبد الملك بن مروان بن الحكم، أمير قائد من أبطال عصره، من بنى أمية فى دمشق، له فتوحات مشهورة، وبنى مسجد مسلمة بالقسطنطينية سنة ٩٦ هجرية، وولاه أخوه يزيد إمرة العراق ثم أرمينية، مات بالشام.','2','158',NULL,NULL,'0');
INSERT INTO [b435] VALUES (598,'<span data-type="title" id=toc-622>مصعب بن الزبير توفى سنة ٧١ هجرية</span>.

مصعب بن الزبير بن العوام بن خويلد الأسدى القرشى، أبو عبد الله، أحد الولاة الأبطال فى صدر الإسلام وولاه أخوه. يزيد إمرة العراقين ثم أرمينية أضاف إليه الكوفة، قتل فى إحدى الوقائع ونقلت رأسه إلى عبد الملك وبمقتله نقلت بيعة أهل العراق إلى ملوك الشام.','2','148',NULL,NULL,'0');
INSERT INTO [b435] VALUES (599,'<span data-type="title" id=toc-623>معاذ بن أنس</span>

معاذ بن أنس الجهنى حليف الأنصار، صحابى كان بمصر والشام، روى عن النبى صلى الله عليه وسلم أحاديث،وله رواية عن أبى الدرداء وكعب الأحبار وروى عنه ابنه سهل بن معاذ وحده، وذكر أبو أحمد العسكرى ما يدل على أنه بقى إلى خلافة عبد الملك بن مروان.','2','149',NULL,NULL,'0');
INSERT INTO [b435] VALUES (600,'<span data-type="title" id=toc-624>ابن أم مكتوم صحابى</span>

قيل اسمه عبد الله، وقيل اسمه عمرو وهو ابن قيس من بنى عامر بن لؤى وأمه أم مكتوم المخزومية، قدم المدينة مهاجرا بعد بدر وكان الرسول عليه السلام يستخلفه على المدينة، يصلى بالناس فى عامة غزواته، وشهد القادسية، توفى بالمدينة.','2','150',NULL,NULL,'0');
INSERT INTO [b435] VALUES (601,'<span data-type="title" id=toc-625>مكحول توفى سنة ١١٨ هجرية</span>.

أبو عبد الله مكحول بن زيد، ويقال ابن أبى مسلم بن شاذل بن سند بن شروان الكابلى الدمشقى، فقيه تابعى، سمع أنس بن مالك وأبا هند الدارى وواثلة ابن الأسقع وغيرهم من الصحابة وسمع جماعات من التابعين قال عنه ابن يونس: كان فقيها عالما واتفقوا على توثيقه.','2','153',NULL,NULL,'0');
INSERT INTO [b435] VALUES (602,'<span data-type="title" id=toc-626>المغيرة بن شعبة صحابى توفى سنة ٥٠ أو ٥١ هجرية</span>.

أعبد عبد الله، ويقال أبو عيسى المغيرة بن شعبة بن أبى عامر بن مسعود بن معتب ابن مالك الثقفى الكوفى، أسلم عام الخندق، روى له عن الرسول صلى الله عليه وسلم أحاديث كثيرة، وكان المغيرة موصوفا بالدهاء والحلم، شهد الحديبية مع رسول الله، وولاه عمر بن الخطاب البصرة ثم الكوفة، وشهد اليمامة، وفتح الشام والقادسية، توفى بالكوفة.','2','151',NULL,NULL,'0');
INSERT INTO [b435] VALUES (603,'<span data-type="title" id=toc-627>ملا جيون توفى سنة ١١٣٠ هجرية</span>

أحمد المدعو بشيخ " جيون " أو "ملا جيون"، ابن أبى سعيد بن عبد الله بن عبد الرازق الحنفى المكى اللكنوى، له مؤلفات كثيرة منها أشراق الأبصار فى تخريج أحاديث نورالأنوار، والتفسيرات الأحمدية فى بيان الآيات الشرعية، وله فى الأصول نور الأنوار فى شرح المنار توفى بدهلى ودفن بها.','2','152',NULL,NULL,'0');
INSERT INTO [b435] VALUES (604,'<span data-type="title" id=toc-628>المنصور بالله القاسم ابن محمد توفى سنة ١٠٢٩ هجرية</span>

القاسم بن محمد بن على، من سلالة الهادى إلى الحق صاحب اليمن من أئمة الزيدية، ولد ونشأ فى
أطراف صنعاء، بايع له خلق كثير بالإمامة، كان حازما شجاعا له تآليف منها الاعتصام فى الحديث مات قبل إتمامه، والأساس لعقائد الأكياس فى أصول الدين.','2','154',NULL,NULL,'0');
INSERT INTO [b435] VALUES (605,'<span data-type="title" id=toc-629>ميمونة توفيت سنة ٥١ هجرية</span>.

ميمونة بنت الحارث بن حزن الهلالية آخر امرأة تزوجها الرسول عليه الصلاة والسلام، وآخر من مات من زوجاته كان أسمها برة فسماها ميمونة، بايعت بمكة قبل الهجرة، روت عدة أحاديث توفيت بمكان قرب مكة ودفنت به.','2','59',NULL,NULL,'0');
INSERT INTO [b435] VALUES (606,'<span data-type="title" id=toc-631>الناصر توفى سنة ٣٠٤ هجرية</span>

أبو محمد الحسن بن على بن الحسن بن على بن عمر بن على بن الحسين ابن على بن أبى طالب الحسينى الإمام الناصر الكبير كان عالما شجاعا ورعا زاهد إليه تنسب الناصرية','2','60',NULL,NULL,'0');
INSERT INTO [b435] VALUES (607,'<span data-type="title" id=toc-632>الناطفى سنة ٤٤٦</span>

أحمد بن محمد بن عمر أبو العباس الناطفى فقيه حنفى من أهل الرى منسوب الى عمل الناطف من كتبه الأجناس والفروق والأحكام','2','62',NULL,NULL,'0');
INSERT INTO [b435] VALUES (608,'<span data-type="title" id=toc-633>نافع بن عبد الرحمن المدنى توفى سنة ١٦٩ هجرية</span>

هو نافع بن عبد الرحمن بن أبى نعيم ابو رويم، ويقال أبو نعيم الليثى مولاهم. أحد القراء السبعة الأعلام. ثقة صالح، اصله من اصبهان، أخذ القراءة عرضا عن تابعى أهل المدينة عبد الرحمن بن هرمز الأعرج، وابى جعفر القارىء، وشيبه بن نصاح وغيرهم. توفى سنة ١٦٥ هجرية وقيل غير ذلك','2','61',NULL,NULL,'0');
INSERT INTO [b435] VALUES (609,'<span data-type="title" id=toc-634>ابن نافع سنة ٢٠٦ هجرية</span>

أبو محمد عبد الله بن نافع الصائع المدنى القرشى المخزومى مولاهم سمع مالكا وابن أبى ذؤيب، وهشام بن عروه وغيرهم، قال ابن سعد كان قد لزم مالك بن أنس لزوما شديدا، وكان لايقدم عليه أحدا','2','64',NULL,NULL,'0');
INSERT INTO [b435] VALUES (610,'<span data-type="title" id=toc-635>نجم الدين سنة ٧٦٧</span>

عمر بن يوسف نجم الدين من أكابر اليمن في الدولة الرسولية له أثار منها المدرسه العمرية بتعز','2','63',NULL,NULL,'0');
INSERT INTO [b435] VALUES (611,'<span data-type="title" id=toc-636>ابن نجيم سنة ٩٧٠</span>

زين الدين بن إبراهيم بن محمد فقيه حنفى مصرى له تصانيف منها الأشباه والنظائر فى أصول الفقه- البحر الرائق فى شرح كنز الدقائق وغيرها','2','65',NULL,NULL,'0');
INSERT INTO [b435] VALUES (612,'<span data-type="title" id=toc-637>النخعى سنة ٩٦ هجرية</span>

إبراهيم بن يزيد بن قيس بن الأسود أبو عمران النخعى، من أكابرالتابعين صلاحا وحفظا للحديث من أهل الكوفة مات مختفيا من الحجاج','2','66',NULL,NULL,'0');
INSERT INTO [b435] VALUES (613,'<span data-type="title" id=toc-638>ابن النديم سنه٤٣٨ هجرية</span>

محمد بن اسحق بن محمد بن اسحق أبو الفرج بن أبى يعقوب النديم صاحب كتاب الفهرست وهو من أقدم كتب التراجم ومن أفضلها. وهو بغدادى وكان معتزليا متشيعا ألف الفهرست فى شبابه وعاود النظر فيه فى كهولته وله كتاب أخر سماه (التشبيهات)','2','67',NULL,NULL,'0');
INSERT INTO [b435] VALUES (614,'<span data-type="title" id=toc-639>النسائى ٣٠٣ هجرية</span>

أحمد بن على بن شعيب أبو عبد الرحمن صاحب السنن أصله من نساء بخراسان واستوطن مصر ثم خرج الى الرملة فمات بها ودفن ببيت المقدس','2','68',NULL,NULL,'0');
INSERT INTO [b435] VALUES (615,'<span data-type="title" id=toc-640>النسفى٤١٤ هجرية</span>

أبو جعفر محمد بن أحمد بن محمود النسغى له تعليقة فى الخلاف وكان قنوعا أخذ عن ابى بكر الرازى الفقه','2','69',NULL,NULL,'0');
INSERT INTO [b435] VALUES (616,'<span data-type="title" id=toc-641>النسفى ٤٢٤ هجرية</span>

أبو على الحسين بن خضر النسفى تفقه على أبى بكر محمد بن الفضل وأخذ عنه شمس الأئمة عبد العزيز الحلوانى وجعفر بن محمد النسفى وله الفوائد والفتاوى وكان إمام عصره','2','70',NULL,NULL,'0');
INSERT INTO [b435] VALUES (617,'<span data-type="title" id=toc-642>النسفى٥٣٣ هجرية</span>

عبد العزيز بن عثمان بن ابراهيم أمام عصره فى بخارى تفقه على برهان الدين الكبير عن السرخس عن الحلوانى ومن مؤلفاقه المنقذ من الزلل فى مسائل الجدل وكفاية الفحول فى الأصول','2','72',NULL,NULL,'0');
INSERT INTO [b435] VALUES (618,'<span data-type="title" id=toc-643>النظام سنة ٢٣١ هجرية</span>

إبراهيم بن سيار بن هانىء البصرى أبو اسحاق النظام من أئمة المعتزلة تبحر فى علوم الفلسفة وانفرد بأراء خاصة تابعه فيها فرقة من المعتزلة تسمى النظامية نسبة إليه واشتهر بالنظام لاجادته نظم الكلام وكان شاعرا أديبا بليغا.','2','71',NULL,NULL,'0');
INSERT INTO [b435] VALUES (619,'<span data-type="title" id=toc-644>نظام الملك سنة ٤٨٥ هجرية</span>

الحسن بن على بن اسحاق الطوسى ابو على الملقب بقوام الدين وزير حازم أصله من نواحى طوس تآدب بآداب العرب وسمع الحديث الكثير واشتغل بالأعمال السلطانية قال ابن عقيل، كانت أيامه دولةأهل العلم ودفن فى اصبهآن.','2','74',NULL,NULL,'0');
INSERT INTO [b435] VALUES (620,'<span data-type="title" id=toc-645>النووى توفى سنة ٦٧٦ هجرية</span>

يحيى بن شرف بن مرى بن حسين الحزامى الحورانى النووى، الشافعى علامة بالفقة والحديث مولده ووفاته فى نوا (من قرى حوران بسورية) من كتبه فى فقه الشافعية (تصحيح التنبيه) وله كتب كثيرة فى الحديث والتوحيد والفتاوى.','2','73',NULL,NULL,'0');
INSERT INTO [b435] VALUES (621,'<span data-type="title" id=toc-646>النيسابورى سنة ٥٤٨ هجرية</span>

محمد بن يحيى بن منصور أبو سعد محيى الدين النيسابورى رئيس الشافعية فى نيسابور فى عصره تفقه على الإمام الغزالى ودرس بنظامية نيسابور وقتلته الغز، ومن كتبه المحيط فى شرح الوسيط والانتصاف فى مسائل الخلاف وغيرها.','2','75',NULL,NULL,'0');
INSERT INTO [b435] VALUES (622,'<span data-type="title" id=toc-647>نصر بن يحيى سنة ٢٦٨ هجرية</span>

نصر بن يحيى البلخى أخذ الفقه عن أبى سليمان الجوزجانى عن محمد بن الحسن وكان معاصرا لمحمد بن سلمة وزامله فى الدرس على الجوزجانى.','2','76',NULL,NULL,'0');
INSERT INTO [b435] VALUES (623,'<span data-type="title" id=toc-648>النهروانى سنة ٥٥٦ هجرية</span>

إبراهيم بن دينار ين أحمد النهروانى الرزاز أبو حكيم. فرضى. أى من المتخصصين فى التوريث من فقهاء الحنابلة من أهل بغداد له تصانيف فى الفقه والفرائض منا " شرح الهداية وكتب منه تسعة مجلدات ولم يكمله.','2','77',NULL,NULL,'0');
INSERT INTO [b435] VALUES (624,'<span data-type="title" id=toc-649>نافع بن عبد الحارث</span>

نافع بن الحارث بن كلدة الثقفى الطائفى كان من رفيق أهل الطائف، واعترف الحارث بنسبه إليه ولما ظهر الإسلام نزل من الطائف إلى النبى صلى الله عليه وسلم وأسلم وشهد الحروب أستأذن عمر باتخاذ دار له بأرض البصرة فأذن له فكان أول من بنى دارا بها.','2','78',NULL,NULL,'0');
INSERT INTO [b435] VALUES (625,'<span data-type="title" id=toc-650>نافع مولى ابن عمر، توفى سنة ١١٧ هجرية</span>.

أبو عبد الله نافع بن عمر من أهل أبو شهر أصابه عبد الله فى غزواته وكان له من الولد عمر بن نافع وأبو بكر وعبد الله.','2','79',NULL,NULL,'0');
INSERT INTO [b435] VALUES (626,'<span data-type="title" id=toc-651>نعيم بن سلامة صحابى</span>

ويقال له ابن سلامة السلمى، صحابى له ذكر فى حديث أخرجة البزار عن أبى هريرة، قال بينا رسول الله صلى الله عليه وسلم جالس وأبو بكر وعمر ومعاذ وابن مسعود ونعيم بن سلام،إذ قدم يزيد على النبى صلى الله عليه وسلم،فقال أبو بكر: يا رسول الله، ما رأيت نعيما أسرع غيابا ولا أكثر مغنما من هؤلاء. قال: يا أبا بكر، ألا أدلك على ما هو أسرع غيابا وأكثر مغنما؟ من صلى صلاة الغداة فى جماعة " إلخ الحديث.','2','80',NULL,NULL,'0');
INSERT INTO [b435] VALUES (627,'<span data-type="title" id=toc-652>الهادى سنة ٢٩٨ هجرية</span>

يحيى بن الحسن بن القاسم بن إبراهيم بن إسماعيل بن ابراهيم (طباطبا) ابن الحسن بن الحسن بن على بن أبى طالب إمام من أئمة الزيدية كتب فى الفقه وغيره كتبا قيمة منها كتاب الأحكام على نمط كتاب الموطأ للإمام مالك حيث يذكر اجتهاداته وو جوهها ويربط أكثر المسائل بالأدلة وقلده فى أجتهاده كثير من أهل اليمن','2','82',NULL,NULL,'0');
INSERT INTO [b435] VALUES (628,'<span data-type="title" id=toc-654>أبو هاشم الجبائى توفى سنة ٣٢١ هجرية</span>

أبوهاشم عبد السلام بن أبى على محمد الجبائى كان هو وأبوه من كبار المعتزلة وكتب الكلام مشحونة بمذهبهما والجبائى بضم الجيم نسبة إلى قريه من قرى البصرة','2','81',NULL,NULL,'0');
INSERT INTO [b435] VALUES (629,'<span data-type="title" id=toc-655>ابن هبيرة توفى سنة ٥٦٠ هجرية</span>

يحيى بن هبيرة بن محمد الزهرى الشيبانى أبو المظفرمن كبار الوزراء فى الدولة العباسية عالم بالفقه والأدب ولد بقرية بالعراق ودخل بغداد فى صباه وتوفى بها. من تصانيفه الايضاح والتبين فى اختلاف الأئمه المجتهدين والإفصاح عن معانى الصحاح وغيرها','2','84',NULL,NULL,'0');
INSERT INTO [b435] VALUES (630,'<span data-type="title" id=toc-656>أبو هريرة سنة ٥٩</span>

عبد الرحمن بن صخر الدوسى الملقب بأبى هريرة كان اكثر الصحابة حفظا للحديث ورو اية له. نشأ يتيما ثم قدم المدينة ورسول الله صلى الله عليه وسلم بخيبر فأسلم سنة سبع ولزم صحبة النبى فروى عنه ٥٣٤٤ حديثا توفى بالمدينة المنورة','2','83',NULL,NULL,'0');
INSERT INTO [b435] VALUES (631,'<span data-type="title" id=toc-657>هشام بن عبد الحكم توفى سنة ٤٠٢ هجرية</span>

هشام بن الحكم بن عبد الرحمن الناصر أبو الوليد المؤيد الأموى من خلفاء الدولة الآموية بالآندلس ولد بقرطبة وقتل بها','2','85',NULL,NULL,'0');
INSERT INTO [b435] VALUES (632,'<span data-type="title" id=toc-658>هشام بن عروة سنة ١٤٦ هجرية</span>

هشام بن عروة بن الزبير بن العوام، الفقيه أبو المنذر الاسدى المدنى أحد أئمة الحديث أدرك عمه عبد الله بن الزبير، وقال: مسح ابن عمر رأسى ودعا لى','2','88',NULL,NULL,'0');
INSERT INTO [b435] VALUES (633,'<span data-type="title" id=toc-659>هند بنت عتبة توفيت سنة ١٤ هجرية</span>

جدها ربيعة بن عبد شمس صحابية قرشيه عالية الشهرة وهى أم الخليفة معاوية كانت فصيحة جريئة شاعرة اسلمت يوم فتح مكة وقد شهدت اليرموك وحرضت على الروم','2','86',NULL,NULL,'0');
INSERT INTO [b435] VALUES (634,'<span data-type="title" id=toc-660>هشام</span>

هشام بن عبد الله الرازى تفقه على أبى يوسف ومحمد ومات محمد فى منزله بالرى ودفن فى مقبرته، له النوادر وصلاة الأثر، كان صدوقا عظيم القدر ثقة.','2','87',NULL,NULL,'0');
INSERT INTO [b435] VALUES (635,'<span data-type="title" id=toc-661>هشام سنة ١٩٩ هجرية</span>

هشام بن الحكم أبو محمد مولى كندة من أكابر أصحاب الصادق وكان تقيما روى أحاديث كثيرة. ولد بالكوفة ونشأ بواسط وتحول إلى بغداد ثم تحول منها إلى الكوفة وبها مات.','2','89',NULL,NULL,'0');
INSERT INTO [b435] VALUES (636,'<span data-type="title" id=toc-662>هلال بن أمية صحابى</span>

هلال بن أمية بن عامر بن قيس الأنصارى الواقفى. شهد بدرا وما بعدها وهو أحد الثلاثة الذين تاب الله عليهم بعد أن تخلفوا عن غزوة تبوك، له ذكر فى الصحيحين فى قصة اللعان مطولة عاش الى خلاقة معاوية.','2','90',NULL,NULL,'0');
INSERT INTO [b435] VALUES (637,'<span data-type="title" id=toc-663>ابن أبى هريرة توفى سنة ٣٤٥ هجرية</span>.

الحسن بن الحسين المكنى بأبى على المعروف بابن ابن أبى هريرة تتلمذ لأبى العباس بن سريج ثم لأبى إسحاق المروزى ثم درس ببغداد وتخرج عليه خلق كثير، أنتهت إليه رياسة الشافعية بها وله أقوال خاصة فى فروع الشافعية وأقوال خاصة فى الأصوال ألف كتاب المسائل فى الفقة وشرح مختصر المزنى، توفى ببغداد.','2','91',NULL,NULL,'0');
INSERT INTO [b435] VALUES (638,'<span data-type="title" id=toc-664>هشام بن عبد الملك توفى سنة ١٢٥ هجرية</span>.

هشام بن عبد الملك بن مروان، من ملوك الدولة الأموية فى الشام، ولد فى دمشق وبويع فيها بعد وفاة أخيه يزيد اجتمع فى خزانته من المال مالم يجتمع فى خزانة أحد من ملوك بنى أمية فى الشام، كان حسن السياسة، يباشر الأعمال بنفسه.','2','95',NULL,NULL,'0');
INSERT INTO [b435] VALUES (639,'<span data-type="title" id=toc-666>ابن وهب سنة ١٩٧ هجرية</span>

عبد الله بن وهب بن مسلم الفهرى بالولاء البصرى أبو محمد فقيه من الأئمة من أصحاب مالك جمع بين الفقه والحديث والعبادة وله كتب منها الجامع فى الحديث والموطأ فى الحديث وكان حافظا عرض عليه القضاء فخبأ نفسه. مولده ووفاته بمصر.','2','92',NULL,NULL,'0');
INSERT INTO [b435] VALUES (640,'<span data-type="title" id=toc-667>ابن وهبان توفى سنة ٧٦٨ هجرية</span>.

عبد الوهاب بن أحمد بن وهبان الحارثى الدمشقى فقيه حنفى ولى قضاء حماة وتوفى فى نحو الأربعين من عمره له منظومة فى الفقه اسمها قيد الشرائد وشرحها بكتاب عقد القلائد، وله كتاب أحاسن الأخبار من محاسن السبعة الأخيار.','2','93',NULL,NULL,'0');
INSERT INTO [b435] VALUES (641,'<span data-type="title" id=toc-669>يحيى بن حمزة سنة ٧٤٥ هجرية</span>

(الامام) يحيى بن حمزة بن على الحسينى العلوى الطالبى من أكابر ائمة الزيدية وعلمائهم فى اليمن ولد فى صنعاء وتلقب بالمؤيد بالله او المؤيد برب العزة من تصانيفه الشامل فى أصول الدين ونهاية الوصول الى علم الأصول والحاوى فى الفقه وكثير غيرها.','2','94',NULL,NULL,'0');
INSERT INTO [b435] VALUES (642,'<span data-type="title" id=toc-670>يحيى بن يحيى توفى ٢٣٤ هجرية</span>

جده أبو عيسى كثير بن دسلاس الليثى بالولاء عالم الاندلس فى عصره بربرى الأصل من قبيلة قصموده، رحل إلى المشرق فسمع الموطأ من الإمام مالك وأخذ عن علماء مكه ومصر، وعاد إلى الأندلس فنشر فيها مذهب مالك وكان لايولى قاض الأ بأمره توفى بقرطبة.','2','96',NULL,NULL,'0');
INSERT INTO [b435] VALUES (643,'<span data-type="title" id=toc-671>يعقوب سنة ٢٠٥</span>

هو يعقوب بن أسحاق بن زيد، أبو محمد الخضرمى، مولاهم البصرى، احد القراء العشرة، امام أهل البصرة ومقريها. أخذ القراءة عرضا عن سلام الطويل ومهدى بن ميمون وأبى ألاشهب العطاردى وغيرهم، سمع الحروف من الكسائى، وسمع من حمزة حروفا.','2','97',NULL,NULL,'0');
INSERT INTO [b435] VALUES (644,'<span data-type="title" id=toc-672>أبو يعلى الكبير توفى سنة ٤٥٨ هجرية</span>

محمد بن الحسين بن محمد بن خلف عالم عصره فى الآصول والفروع وأنواع الفنون من أهل بغداد ولى قضاء الخلافة والحريم وحران وحلوان تصانيفه كثيرة منها " المجرد " فى الفقه على مذهب أحمد وكان شيخ الحنابلة','2','98',NULL,NULL,'0');
INSERT INTO [b435] VALUES (645,'<span data-type="title" id=toc-673>أبو يعلى الصغير سنة ٥٦٠ هجرية</span>

محمد بن محمد بن محمد الحسين عماد الدين بن القاضى أبى خازن بن أبى يعلى الكبير قاض من كبار الحنابلة ببغداد وتوفى بها من كتبه (التعليقة) فى مسائل الخلاف (النكت والاشارات) فى مسائل المفردات وغيرها','2','99',NULL,NULL,'0');
INSERT INTO [b435] VALUES (646,'<span data-type="title" id=toc-674>يوسف أصاف سنة ١٩٣٨م</span>

يوسف بن همام أصاف، محام، مترجم، فاصل لبنانى المولد تعلم العربية والسريانية وألايطالية ومبادىء العلوم فى لبنان ثم عين مدرسا فى " عكا " ورحل إلى ايطاليا وتركيا ثم أستقر بمصر واستخدم مترجما بالاسكندرية وكانت وفاته بلبنان','2','100',NULL,NULL,'0');
INSERT INTO [b435] VALUES (647,'<span data-type="title" id=toc-675>يوسف شخت</span>

مستشرق ألمانى أستاذ فى العلوم الشرقية له عدة كتب أشهرها كتاب الحيل فى الفقه لأبى حاتم القزوينى','2','101',NULL,NULL,'0');
INSERT INTO [b435] VALUES (648,'<span data-type="title" id=toc-676>أبو يوسف سنة ١٨٢ هجرية</span>

يعقوب بن إبراهيم بن حبيب الأنصارى الكوفى البغدادى أبو يوسف صاحب الإمام آبى حنيفة وتلميذه وأول من نشر مذهبه ولد بالكوفة وولى القضاء ببغداد: كان أول من دعى قاضى القضاه وله كتاب الخراج والآثار وغيره مات ببغداد','2','102',NULL,NULL,'0');
INSERT INTO [b435] VALUES (649,'<span data-type="title" id=toc-677>ابن يونس سنة ٤١٥ هجرية</span>

أبو بكر محمد بن عبد الله بن يونس الصقلى، كان فقيها اماما فرضيا،. وكان ملازما للجهاد ألف كتابا فى الفرائض وكتابا جامعا للمدونة اضاف إليها غيرها من الأمهات','2','103',NULL,NULL,'0');
INSERT INTO [b435] VALUES (650,'<span data-type="title" id=toc-678>ابن يسار سنة ١٠٩ ١٠٣ هجرية</span>

سليمان بن يسار مولى ميمونة زوج النبى صلى الله عليه وسلم كان عالما ثقة، روى عن ابن عباس وأبى هريرة وروى عنه الزهرى وجماعة، وهو من الفقهاء السبعة بالمدينة.','2','104',NULL,NULL,'0');
INSERT INTO [b435] VALUES (651,'<span data-type="title" id=toc-679>يعقوب بن بختان من فقهاء القرن الثالث</span>

يعقوب بن إسحق بن بختان أبو يوسف أحد فقها الحنابلة وأحد الصالحين الثقاة كان جار أبى عبد الله وصديقه وروى عنه مسائل كثيرة فى الورع والسلطان وروى عنه أبو بكر بن أبى الدينار وجعفر الصندلى وغيرهما.','2','105',NULL,NULL,'0');
INSERT INTO [b435] VALUES (652,'<span data-type="title" id=toc-680>يزيد بن أبى سفيان توفى سنة ١٨ هجرية</span>.

أبو خالد يزيد أبى سفيان صخر بن حرب القرشى الأموى الصحابى ابن الصحابى، كان أفضل بنى أبى سفيان، وتوفى ولا عقب له، وكان يقال له يزيد الخير، أسلم يوم الفتح ومات سنة ثمانى عشرة، وقيل سنة تسع عشرة وله رواية عن النبى صلى الله عليه وسلم.','2','106',NULL,NULL,'0');
INSERT INTO [b435] VALUES (653,'<span data-type="title" id=toc-681>يزيد بن يزيد بن جابر توفى سنة ١٣٣ أو ١٣٤ هجرية</span>

يزيد بن يزيد بن جابر الأسدى الدمشق روى عن عبد الرحمن بن أبى عمرة وبشر بن عبيد الله الحضرمى ومكحول ووهب بن منبه، وروى عنه الأوزاعى ثقة عالم حافظ وثقه ابن معين ورجح البخارى أن وفاتة سنة ١٣٤ هجرية)','2','107',NULL,NULL,'0');
INSERT INTO [b435] VALUES (654,'<span data-type="title" id=toc-682>يعلى بن أمية توفى سنة ٣٧ هجرية</span>.

أبو خالد يعلى بن أمية بن أبى عبيدة بن همام التميمى، أسلم يوم فتح مكة وشهد حنينا والطائف وتبوك مع رسول الله صلى الله عليه وسلم، واستعمله عثمان على صنعاء، روى عن الرسول الله صلى الله عليه وسلم ثمانية وعشرين حديثا قتل بصفين مع على كرم الله وجهه.','2','108',NULL,NULL,'0');
