INSERT INTO [b46] VALUES (295,'المَسْبُوكُ عَلي منْحَة السُّلوْك في شرحِ تُحفَة المُلَوْك','2','2',NULL,NULL,'0');
INSERT INTO [b46] VALUES (296,'ح عبد المحسن بن محمد القاسم، ١٤٢٨ هـ
فهرسة مكتبة الملك فهد الوطنية أثناء النشر
القاسم، عبد المحسن بن محمد
المسبوك على منحة السلوك: شرح تحفة الملوك لبدر الدين العيني، والمتن لزين الدين محمد بن أبي بكر الرازي/ عبد المحسن بن محمد القاسم - الرياض: ١٤٢٨ هـ
٤ مج
٣٩٢ ص: ١٧ × ٢٤ سم
ردمك: ٥ - ٣٧٨ - ٥٨ - ٩٩٦٠ - ٩٧٨ (مجموعة)
٨ - ٣٨٠ - ٥٨ - ٩٩٦٠ - ٩٧٨ (ج ٢)
