INSERT INTO [b46] VALUES (680,'<span data-type="title" id=toc-352>كتاب الزكاة</span>
الزكاة تجب على كل حر بالغ عاقل مسلم،
<hr><span data-type="title">منحة السلوك</span>

كتاب الزكاة
وجه المقارنة (¬١) بالصلاة، قد مر في أول الكتاب (¬٢).

<span data-type="title" id=toc-353>[تعريف الزكاة]</span>
وهي لغة: عبارة عن النماء. يقال: زكي الزرع إذا نمي، وقيل: عن الطهارة (¬٣). قال الله تعالى: {قَدْ أَفْلَحَ مَنْ تَزَكَّى} [الأعلى: ١٤] أي: تطهر (¬٤).
وشرعًا: إعطاء شقصٍ من النصاب الحولي، إلى فقير غير هاشمي، ولا مولاه، بطريق التمليك، بشرط قطع المنفعة عنه من كل وجه لله تعالى (¬٥).

<span data-type="title" id=toc-354>[إيجاب الزكاة]</span>
قوله: الزكاة تجب على كل حر، بالغ، عاقل، مسلم (¬٦).
أقول: معنى يجب: يفترض؛ لأن الوجوب يستعمل بمعنى: الفرض،
_________
(¬١) في ق، م "وجه المناسبة والمقارنة".
(¬٢) في ١/ ٦١.
(¬٣) لسان العرب ١٤/ ٣٥٨ مادة زكا، القاموس المحيط ٢/ ٤٦٤ مادة ز ك و، مختار الصحاح ص ١١٥ مادة ز ك ا، المصباح المنير ١/ ٦٥٤ مادة الزكاء.
(¬٤) أي: طهر نفسه من الأخلاق الرذيلة. وقيل: أي: تطهر من الشرك، قاله: ابن عباس.
وقيل: من أعطى صدقة الفطر قاله: أبو سعيد الخدري، وعطاء، وقتادة، وأبو العالية.
وقيل: من كان عمله زاكيًا. قاله: الحسن، والربيع.
تفسير ابن كثير ٤/ ٧٩٠، الجامع لأحكام القرآن ٢٠/ ١٦، زاد المسير في علم التفسير ٨/ ٢٣٠، الدر المنثور في التفسير بالمأثور ٦/ ٥٦٧، الكشاف ٤/ ٢٠٥.
(¬٥) كنز الدقائق ١/ ٢٥١، تنوير الأبصار ٢/ ٢٥٦، الاختيار ١/ ٩٩، تبيين الحقائق ١/ ٢٥١.
(¬٦) الكتاب ١/ ٣١٦، درر البحار (مخطوط) ق ٢٤/ ب، شرح الوقاية ١/ ٩٨، كشف الحقائق ١/ ٩٧، الاختيار ١/ ٩٩، بداية المبتدي ١/ ١٠٣، الهداية ١/ ١٠٣، كنز الدقائق ١/ ٢٥١، حاشية الشلبي ١/ ٢٥١.','3','5',NULL,NULL,'0');
INSERT INTO [b46] VALUES (681,'ملك نصابًا ملكًا تامًا
<hr><span data-type="title">منحة السلوك</span>

توسعًا (¬١). واحترز بقوله: "حر" عن الرقيق، ومعتق البعض (¬٢).
وبقوله: "بالغ، عاقل". عن الصبي، والمجنون (¬٣).
وقال الشافعي: تجب عليهما؛ لعموم النصوص (¬٤).
قلنا: الأهلية معدومة فيهما، فصارت كالصلاة (¬٥).
وبقوله: "مسلم"، احترز عن الكافر؛ لعدم أهليته لأداء العبادات (¬٦).
قوله: ملك نصابًا.
صفة لقوله: "حر، بالغ"؛ لأنه بمُلك النصاب يصير غنيًا، والزكاة إنما تجب على الغني (¬٧).
قوله: ملكًا تامًا.
_________
(¬١) الهداية ١/ ١٠٣، شرح فتح القدير ٢/ ١٥٤، العناية ٢/ ١٥٤.
(¬٢) شرح الوقاية ١/ ٩٨، كشف الحقائق ١/ ٩٧، تبيين الحقائق ١/ ٢٥١، حاشية الشلبي ١/ ٢٥١.
(¬٣) كشف الحقائق ١/ ٩٧، تبيين الحقائق ١/ ٢٥١، شرح الوقاية ١/ ٩٨، العناية ٢/ ١٥٤، شرح فتح القدير ٢/ ١٥٤، الاختيار ١/ ٩٩، حاشية رد المحتار ٢/ ٢٥٨.
(¬٤) وهو مذهب المالكية، والحنابلة.
الكافي لابن عبد البر ص ٨٨، القوانين الفقهية ص ٦٧، إخلاص الناوي ١/ ٢٥٣، رحمة الأمة ١/ ٩١، الروض المربع ص ١٥٠، دليل الطالب ١/ ١٨٢.
(¬٥) رؤوس المسائل ص ٢٠٨، الهداية ١/ ١٠٣، تبيين الحقائق ١/ ٢٥٢، كشف الحقائق ١/ ٢٥٢، شرح فتح القدير ٢/ ١٥٣، العناية ٢/ ١٥٣، بدائع الصنائع ٢/ ٤.
(¬٦) بدائع الصنائع ٢/ ٤، تبيين الحقائق ١/ ٢٥٣، حاشية الشلبي ١/ ٢٥٣، الهداية ١/ ١٠٣، البحر الرائق ٢/ ٢٠٢، حاشية رد المحتار ٢/ ٢٥٩.
(¬٧) تحفة الفقهاء ١/ ٣٠١، المبسوط ٢/ ١٤٩، بدائع الصنائع ٢/ ٤، تبيين الحقائق ١/ ٢٥٣، الهداية ١/ ١٠٣، البحر الرائق ٢/ ٢٠٢.','3','6',NULL,NULL,'0');
INSERT INTO [b46] VALUES (682,'رقبة ويدًا، وتمَّ عليه
<hr><span data-type="title">منحة السلوك</span>

احتراز عن الملك الناقص، حيث لا يجب فيه الزكاة، كالبيع قبل القبض، لا زكاة فيه، وكالدية (¬١) على العاقلة (¬٢)، والمهر إذا كان دينًا، وبدل الصلح عن دم العمد، وبدل الخلع (¬٣) (¬٤).
قوله: رقبةً ويدًا.
أي: من حيث الرقبة، ومن حيث اليد، وبه احترز عن المكاتب، فإنه وإن كان مالكًا لما في يده من حيث اليد، لكنه غير مالك من حيث الرقبة (¬٥).
قوله: وتم عليه.
_________
(¬١) مصدر ودي: القاتل المقتول، إذا أعطى وليه المال الذي هو بدل النفس. وقيل: هي دية، وعقل. وسميت عقلًا؛ لأنها تعقل الدماء عن أن تسفك. وقال قوم: أصل الدية الإبل كانت تجمع، وتعقل بفناء ولي المقتول، فسميت الدية عقلًا وإن كانت دراهم ودنانير.
لسان العرب ١٥/ ٣٨٣ مادة ودي، مختار الصحاح ص ٢٩٨ مادة ود ي، المصباح المنير ٢/ ٦٥٤ مادة وَدَي، القاموس المحيط ٤/ ٥٩٢ مادة ودي، حلية الفقهاء ص ١٩٦، أنيس الفقهاء ص ٢٩٢، طلبة الطلبة ص ٣٣١.
(¬٢) عاقلة الرجل: عصبته، وهم القرابة من قبل الأب، الذين يعطون دية من قتله خطأ. مختار الصحاح ص ١٨٧ مادة ع ق ل، القاموس المحيط ٣/ ٢٧٨ مادة ع ق ل، لسان العرب ١١/ ٤٦٠ مادة عقل، المصباح المنير ٢/ ٤٢٢ مادة عقلتُ.
(¬٣) شرح فتح القدير ٢/ ١٥٤، حاشية رد المحتار ٢/ ٢٥٩، البحر الرائق ٢/ ٢٠٢، تبيين الحقائق ١/ ٢٥٣، حاشية الشلبي ١/ ٢٥٣.
(¬٤) الخلع -بضم الخاء وفتحها- بمعنى: الإزالة مطلقًا. يقال: خالعت المرأة زوجها؛ إذا افتدت منه بمالها.
لغة الفقه ص ٢٦٠، حلية الفقهاء ص ١٧٠، أنيس الفقهاء ص ١٦١، مختار الصحاح ص ٧٨ مادة خ ل ع، القاموس المحيط ٢/ ٩٤ مادة خ ل ع، لسان العرب ٨/ ٧٦ مادة خ ل ع.
(¬٥) العناية ٢/ ١٦٠، تبيين الحقائق ١/ ٢٥٣.','3','7',NULL,NULL,'0');
INSERT INTO [b46] VALUES (683,'الحول، وجوبًا على الفور في قولٍ.
<hr><span data-type="title">منحة السلوك</span>

أي: على النصاب حول (¬١)؛ لقوله -صلى الله عليه وسلم-: "ليس في مالٍ زكاة، حتى يحول عليه الحول" رواه أبو داود (¬٢).
قوله: وجوبًا. مفعول لقوله: "تجب".
قوله: على الفور في قولٍ.
_________
(¬١) الكتاب ١/ ١٣٦، المختار ١/ ٩٩، بداية المبتدي ١/ ١٠٣، الاختيار ١/ ٩٩، الهداية ١/ ١٠٣، شرح الوقاية ١/ ٩٨، كشف الحقائق ١/ ٩٨، ملتقى الأبحر ١/ ١٦٩، مراقي الفلاح ص ٦٥٨.
(¬٢) ٢/ ١٠٠ كتاب الزكاة، باب في زكاة السائمة رقم ١٥٧٣، ورواه الدارقطني ٢/ ٩١ كتاب الزكاة، باب وجوب الزكاة بالحول رقم ٥ من طريق حسان بن سياه عن ثابت ... عن أنس -رضي الله عنه- مرفوعًا: "ليس في مال زكاة، حتى يحول عليه الحول". وحسان ضعيف. ورواه الدارقطني في السنن ٢/ ٩٠ كتاب الزكاة، باب وجوب الزكاة بالحول رقم ١.
من حديث إسماعيل بن عياش، عن عبيد الله بن عمر، عن نافع، عن ابن عمر -رضي الله عنهما- بلفظ: "لا زكاة في مال، حتى يحول عليه الحول"، والبيهقي ٤/ ١٠٤ كتاب الزكاة، باب لا يعد عليهم بما استفاد من غير نتاجها حتى يحول عليه الحول، وقال -أي البيهقي-: وليس بصحيح، ورواه الترمذي ٢/ ٢٠٨ كتاب الزكاة، باب ما جاء لا زكاة على المال المستفاد حتى يحول عليه الحول رقم ٦٣١، والدارقطني في السنن ٢/ ٩٠ كتاب الزكاة، باب وجوب الزكاة بالحول رقم ٢.
من حديث عبد الرحمن بن زيد بن أسلم، عن أبيه، عن عمر -رضي الله عنه- بلفظ: "ليس في مال المستفيد زكاة، حتى يحول عليه الحول"، ولفظ الترمذي: "من استفاد مالًا فلا زكاة عليه، حتى يحول عليه الحول". قال البيهقي ٤/ ١٠٤: وعبد الرحمن ضعيف لا يحتج به، ورواه أبو عبيد في الأموال ص ٥٠٥ كتاب الصدقة وأحكامها وسننها، باب فروض زكاة الذهب والورق وما فيها من السنن، وابن ماجه ١/ ٥٧١ كتاب الزكاة، باب من استفاد مالًا رقم ١٧٩٢، والدارقطني ٢/ ٩١ كتاب الزكاة باب وجوب الزكاة بالحول رقم ٣٠، والبيهقي في السنن ٤/ ٩٥ كتاب الزكاة باب لا زكاة في مال حتى يحول عليه الحول، كلهم من حديث حارثة بن أبي الرجال عن عمرة عن عائشة -رضي الله عنها- بلفظ "لا زكاة في مال حتى يحول عليه الحول" وحارثة ضعيف، قال في الزوائد ٢/ ٥٠: إسناده ضعيف لضعف حارثة بن محمد.','3','8',NULL,NULL,'0');
INSERT INTO [b46] VALUES (684,'وكل دين لآدمي يمنع بقدره حالًّا كان، أو مؤجلًا.
<hr><span data-type="title">منحة السلوك</span>

وهو قول: الكرخي (¬١)، وعامة أهل الحديث (¬٢).
وقيل: على التراخي (¬٣)؛ لأن جميع العمر وقت للأداء (¬٤).
وفائدته: أنه هل يأثم بالتأخير؟ وهل تُردُّ شهادته أم لا؟ (¬٥).

<span data-type="title" id=toc-355>[أثر الدين في الزكاة]</span>
قوله: وكل دين لآدمي يمنع بقدره.
أي: يمنع الزكاة بقدر الدين، سواء كان الدين حالًا، أو مؤجلًا (¬٦).
مثلًا إذا كان له أربعمائة وعليه دين مائتا درهم، فإنه يمنع زكاة المائتين. ولو كان له مائتان أو ثلاثمائة لا زكاة عليه أصلًا. وعلى هذا [فقس] (¬٧).
وقال الشافعي: ديون العباد لا تمنع الزكاة، كفرض الحج (¬٨).
_________
(¬١) العناية ٢/ ١٥٥، شرح فتح القدير ٢/ ١٥٥.
(¬٢) وأبي حنيفة، وأبي يوسف، ومحمد. وإليه ذهب المالكية، والشافعية، والحنابلة، وهو اختيار أبي منصور الماتريدي.
تحفة الفقهاء ١/ ٢٦٣، شرح فتح القدير ٢/ ١٥٦، الاختيار ١/ ٩٩، التفريع ١/ ٢٧٥، الكافي لابن عبد البر ص ٩٩، أسنى المطالب ١/ ٣٣٨، المهذب ١/ ١٤٠، المجموع ٥/ ٣٣٥، زاد المستقنع ص ١٦٤، منار السبيل ١/ ٢٠٤.
(¬٣) وهو قول: عامة مشايخ الحنفية، واختيار الجصاص، وأبي بكر الرازي.
شرح فتح القدير ٢/ ١٥٥، بدائع الصنائع ٢/ ٣، الاختيار ١/ ٩٩، العناية ٢/ ١٥٥.
(¬٤) الهداية ١/ ١٠٣، بدائع الصنائع ٢/ ٣، الاختيار ١/ ٩٩، العناية ٢/ ١٥٥، شرح فتح القدير ٢/ ١٥٥، تحفة الفقهاء ١/ ٢٦٣.
(¬٥) العناية ٢/ ١٥٥، شرح فتح القدير ٢/ ١٥٦.
(¬٦) المختار ١/ ٩٩، الكتاب ١/ ١٣٧، بداية المبتدي ١/ ١٠٤، مراقي الفلاح ص ١٦٩، كنز الدقائق ١/ ٢٥٢، تبيين الحقائق ١/ ٢٥٢، كشف الحقائق ١/ ٩٨، شرح الوقاية ١/ ٩٨.
(¬٧) زيادة يقتضيها السياق.
(¬٨) وبه قال: ربيعة، وحماد بن أبي سليمان.
وذهب المالكية: إلى أن الدين يمنع وجوب الزكاة في الأموال الباطنة، وهو الذهب =','3','9',NULL,NULL,'0');
INSERT INTO [b46] VALUES (685,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

قلنا: إنه مشغول بحاجته، بخلاف الحج؛ لأنه لا مطالب له من العباد (¬١). وإنما أطلق بقوله: كل دين لآدمي؛ ليتناول جميع أنواع الديون، مثل دين استهلاك، ومهر ولو مؤجلًا، وعشر، وخراج، ونفقة قريب، وزوجة قضيت بها، وإذا لم يقض بها لا يمنع (¬٢)، وكذلك دين الزكاة يمنع عندنا (¬٣). خلافًا لزفر (¬٤).
_________
= والفضة، وعروض التجارة، ولا يمنعها في الظاهرة، وهي الزروع، والثمار، والمواشي، والمعادن والمانع دين الآدمي، لا دين كفارة ليمين، أو غيره كظهار، وصوم، ولا دين هدي وجب عليه في حج، أو عمرة، فلا يسقطان زكاة العبد.
والفرق بين الأموال الباطنة، والظاهرة: أن تعلق الزكاة بالظاهرة آكد؛ لظهورها، وتعلق قلوب الفقراء بها، ولأن تعلق الأطماع من الفقراء بها أكثر والحاجة إلى حفظها أوفر، فتكون الزكاة فيها أوكد؛ ولأن الظاهرة نامية بنفسها.
وذهب الحنابلة: إلى أن الدين يمنع وجوب الزكاة في الأموال الباطنة والظاهرة، وحكم دين من كفارة، وزكاة، ونذر مطلق، ودين حج ونحوه، كدين آدمي في منعه وجوب الزكاة في قدره؛ لوجوب قضائه.
الشرح الصغير ١/ ٢٢٧، الكافي لابن عبد البر ص ٩٤، المعونة ١/ ٣٦٨، مختصر المزني ص ١٤٧، المهذب ١/ ١٥٨، المجموع ٥/ ٣٤٤، الإفصاح ١/ ٢١٣، هداية الراغب ص ١٧٥، كشاف القناع ٢/ ١٧٥، الشرح الكبير لابن قدامة ٢/ ٤٥٤، الإنصاف ٣/ ١٥.
(¬١) الهداية ١/ ١٠٤، العناية ٢/ ١٥٦، شرح فتح القدير ٢/ ١٥٦، تبيين الحقائق ١/ ٢٥٤، شرح الوقاية ١/ ٩٨، كشف الحقائق ١/ ٩٨.
(¬٢) العناية ٢/ ١٦٠، تبيين الحقائق ١/ ٢٥٤، شرح فتح القدير ٢/ ١٦١، الهداية ١/ ١٠٤.
(¬٣) تبيين الحقائق ١/ ٢٥٤، رؤوس المسائل ص ٢١٧، الكتاب ١/ ١٣٧، العناية ٢/ ١٦١، بداية المبتدي ١/ ١٠٤، ملتقى الأبحر ١/ ١٧١، الهداية ١/ ١٠٤، كشف الحقائق ١/ ٩٨.
(¬٤) حيث يرى: أن الدين لا يمنع وجوب الزكاة؛ لأنه لا مطالب له من جهة العباد، فصار كدين النذر، والكفارة.
تبيين الحقائق ١/ ٢٥٤، تحفة الفقهاء ١/ ٢٧٥، الهداية ١/ ١٠٤، شرح فتح القدير ٢/ ١٦١، شرح الوقاية ١/ ٩٨، البناية ٢/ ١٦١.','3','10',NULL,NULL,'0');
INSERT INTO [b46] VALUES (686,'ومن مات وعليه زكاة، أو صدقة فطر، أو صوم، أو نذر، أو كفارة سقطت، إلا إن أوصى بها فتنفذ من الثلث.
ولا زكاة في غير الفضة والذهب والسوائم، إلا بنية التجارة.
<hr><span data-type="title">منحة السلوك</span>

وأما ديون النذور. والكفارات. لا يمنع؛ لأنه ليس لها مطالب من جهة العباد (¬١).

<span data-type="title" id=toc-356>[سقوط الزكاة عن الميت]</span>
قوله: ومن مات وعليه زكاة، أو صدقة فطرٍ، أو صوم، أو نذر، أو كفارة سقطت.
لأنها حق الله تعالى، ولا يؤخذ من تركته، إلا إذا أوصى يؤخذ من الثلث؛ لأن تصرفه من الثلث لا غير (¬٢).
وقال الشافعي: يؤخذ من تركته، أوصى بها أو لم يوص (¬٣).

<span data-type="title" id=toc-357>[الأصناف الواجبة فيها الزكاة]</span>
قوله: ولا زكاة في غير الفضة، والذهب، والسوائم (¬٤)، إلا بنية التجارة (¬٥).
_________
(¬١) الهداية ١/ ١٠٤، شرح فتح القدير ٢/ ١٦١، تحفة الفقهاء ١/ ٢٧٥، العناية ٢/ ١٦١، تبيين الحقائق ١/ ٢٥٤.
(¬٢) تحفة الفقهاء ١/ ٣١١، تبيين الحقائق ١/ ٢٥٤ التجريد في الخلاف (مخطوط) لوحة ٦٧/ ب النسخة الأصلية لدى مكتبة فاتح التركي، تحت رقم ٢٠٤٠.
(¬٣) وهو مذهب الحنابلة.
وذهب المالكية: إلى أن من مات وعليه زكاة سقطت، إلا إذا أوصى بها.
القوانين ص ٢٥٣، بداية المجتهد ١/ ٢٤٩، منهج الطلاب ٢/ ٢٩٠، فتح الوهاب ٢/ ٢٩٠، الإفصاح ١/ ٢١٢، دليل الطالب ١/ ١٨٥.
(¬٤) السائمة: هي الراعية. وسامت الماشية: رعت. وأسمتها: أخرجتها إلى الرعي.
مختار الصحاح ص ١٣٥ مادة س وم، القاموس المحيط ٢/ ٦٥١ مادة س وم، معجم مقاييس اللغة ٣/ ١١٨ باب السين والواو وما يثلثهما مادة سوم، الدر النقي ٢/ ٣١٩، لغة الفقه ص ١٠٢، طلبة الطلبة ص ٣٩.
(¬٥) وكذا عند الحنابلة: إلا أن الخارج من الأرض من الحبوب، والثمار، فيه الزكاة ولو =','3','11',NULL,NULL,'0');
INSERT INTO [b46] VALUES (687,'ولا زكاة في مال الضمار، وهو ما لا يقدر عليه بنفسه ولا بنائبه.
<hr><span data-type="title">منحة السلوك</span>

وذلك كالعروض (¬١)، والأمتعة، والسلع، ونحوها، ولا زكاة فيها إلا بنية التجارة؛ لأنها مبادلة المال بالمال، والنية للتمييز، والإخلاص، فلا بد منها، بخلاف النقدين، والسوائم (¬٢).
قوله: ولا زكاة في مال الضمار (¬٣).
وهو. أي: الضمار ما لا يقدر عليه بنفسه، ولا بنائبه، مثل المال الضائع، والساقط في البحر، والمدفون في المفازة، والعبد الآبق (¬٤)،
_________
= لم ينوِ. وعند المالكية: المال الذي تجب فيه الزكاة ثلاثة أصناف: العين، والحرث، والماشية، وما يرجع إلى ذلك بالقيمة، كالتجارة، فلا تجب في الجوهر، والعروض، ولا أصول الأملاك، ولا الخيل، والعبيد، ولا العسل، واللبن، ولا غير ذلك، إلا أن يكون للتجارة.
وعند الشافعية: تجب الزكاة في خمسة أشياء: المواشي، والأثمان، والزروع، والثمار، وعروض التجارة.
القوانين ص ٦٧، التلقين ص ٤٦ متن أبي شجاع ص ٨٠، التذكرة ص ٦٩، مختصر الخرقي ص ٤٦، العمدة ص ٢٦.
(¬١) العروض: جمع عرض -بسكون الراء- وهي: الأمتعة التي لا يدخلها كيل، ولا وزن، ولا تكون حيوانًا، ولا عقارًا.
مختار الصحاح ص ١٧٨ مادة ع ر ض، القاموس المحيط ٣/ ١٩٣ مادة ع ر ض، المصباح المنير ٢/ ٤٠٤ مادة عرض، لغة الفقهاء ص ١١٤، الدر النقي ٢/ ٣٤٠، لسان العرب ٧/ ١٧٠ مادة عرض.
(¬٢) تحفة الفقهاء ١/ ٢٧٢، بدائع الصنائع ٢/ ٢١، الكتاب ١/ ١٣٨، الهداية ١/ ١٠٤، كشف الحقائق ١/ ٩٨، شرح الوقاية ١/ ٩٨، الاختيار ١/ ١٠١، تبيين الحقائق ١/ ٢٥٦.
(¬٣) المال الضمار: هو المال الغائب الذي لا يرجى وصوله. وأصله من الإضمار، وهو: التغييب، والإخفاء. وقيل: هو ما يكون عينه قائمًا، ولا ينتفع به.
العناية ٢/ ١٦٤، شرح فتح القدير ٢/ ١٦٤.
(¬٤) يجوز أبق بفتح الهمزة والباء والقاف، فعل ماض. ويجوز الكسر في لغة، أي: كسر =','3','12',NULL,NULL,'0');
INSERT INTO [b46] VALUES (688,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

والمغصوب، والدين المجحود إذا لم يكن عليه بينة، والمودع عند من لا يعرفه، والذي أخذه السلطان مصادرة (¬١).
وقال زفر: يجب في الضمار الزكاة لإطلاق النصوص (¬٢).
ولنا: قول علي -رضي الله عنه-: "لا زكاة في المال الضمار" موقوفًا، ومرفوعًا (¬٣).
_________
= الباء، ويجوز بالمد آبق صفة للعبد. يقال أبق العبد: إذا هرب من سيده، وهو مملوك فرّ من مالكه قصدًا مُعنِّدًا.
القاموس المحيط ١/ ١٠٤ مادة أب ق، لسان العرب ٣/ ١٠ مادة أبق، المصباح المنير ١/ ٢ مادة أبق، لغة الفقه ص ١٥٧، أنيس الفقهاء ص ١٨٨، المطلع على أبواب المقنع ص ٢٣٠.
(¬١) العناية ٢/ ١٦٤، الهداية ١/ ١٠٤، ملتقى الأبحر ١/ ١٧١، شرح فتح القدير ٢/ ١٦٤، تبيين الحقائق ١/ ٢٥٦، حاشية الشلبي ١/ ٢٥٦، المختار ١/ ١٠١، شرح الوقاية ١/ ٩٨.
(¬٢) والسبب متحقق، وهو الملك، ولا يضره زوال اليد كابن السبيل.
وعند المالكية: يزكيه لكل عام؛ لتفريطه. وقيل: لعام واحد. وقيل: إن كان في الدار زكاه. وإن كان في الصحراء فلا زكاة عليه، كل ذلك صحيح عن مالك.
ويجب عند الشافعية في الأظهر، وهو القول: الجديد؛ لملك النصاب، وتمام الحول. والثاني: وهو القديم، لا تجب؛ لامتناع النماء والتصرف، فأشبه مال المكاتب، لا تجب فيه الزكاة على السيد.
وعند الحنابلة: يزكيه إذا قبضه، لما مضى.
تبيين الحقائق ١/ ٢٥٦، الهداية ١/ ١٠٤، العناية ٢/ ١٦٤، ملتقى الأبحر ١/ ١٧١، الاختيار ١/ ١٠١، شرح فتح القدير ٢/ ١٦٤، الذخيرة ٣/ ٤١، الكافي لابن عبد البر ص ٩٤، مغني المحتاج ١/ ٤٠٩، نهاية المحتاج ٣/ ١٢٩، الإفصاح ١/ ٢٤١، الإقناع للحجاوي ٢/ ١٧٤.
(¬٣) قال في نصب الراية ٢/ ٣٣٤: غريب.
وقال في الدراية ١/ ٢٤٩: لم أجده. =','3','13',NULL,NULL,'0');
INSERT INTO [b46] VALUES (689,'ولا تصح إلا بنية مقارنة للأداء، أو لعزلها،
<hr><span data-type="title">منحة السلوك</span>

وفي المدفون في الأرض، والكرم، اختلاف المشايخ (¬١).

<span data-type="title" id=toc-358>[صحة أداء الزكاة]</span>
قوله: ولا تصح. أي: الزكاة، إلا بنية مقارنة للأداء، أو لعزلها.
لأن النية لا بد منها لأداء العبادات، والزكاة تؤدى متفرقة، فربما يتحرج في النية عند أداء كل دفعة، فاكتفى بها عند العزل؛ تسهيلًا، وتيسيرًا (¬٢).
_________
= وروى أبو عبيد القاسم بن سلام في كتاب الأموال، في باب الصدقة، عن الحسن البصري قال: "إذا حضر الوقت الذي يؤدي فيه الرجل زكاته، أدى عن كل مال، وعن كل دين إلا ما كان منه ضمارًا لا يرجوه"، وروى مالك في الموطأ ١/ ٢٥٣ كتاب الزكاة، باب الزكاة في الدين رقم ١٨، والبيهقي ٤/ ١٥٠ كتاب الزكاة باب زكاة الدين إذا كان على معسر أو جاحد، عن أيوب بن أبي تميمة السختياني، أن عمر بن عبد العزيز، كتب في مال قبضه بعض الولاة ظلمًا يأمر برده إلى أهله، ويؤخذ زكاته لما مضى من السنين، ثم عقب بعد ذلك بكتاب، أن لا يؤخذ منه إلا زكاة واحدة، فإنه كان ضمارًا. قال في نصب الراية ٢/ ٣٣٤: قال في الإمام: فيه انقطاع بين أيوب وعمر.
وروى ابن أبي شيبة، ٣/ ٢٥ وأبو عبيد في كتاب الأموال ص ٣٤٢، عن عمرو بن ميمون، قال: أخذ الوليد بن عبد الملك مال رجل من أهل الرقة، يقال له: أبو عائشة عشرين ألفًا، فألقاها في بيت المال، فلما ولي عمر بن عبد العزيز أتاه ولده، فرفعوا مظلمتهم إليه، فكتب إلى ميمون أن ادفعوا إليهم أموالهم، وخذوا زكاة عامهم هذا، فإنه لولا أنه كان مالًا ضمارًا، أخذنا منه زكاة ما مضى.
(¬١) في بخارى فقيل: يجب؛ لإمكان حفر جميع الأرض، والوصول إليه. وقيل: لا تجب؛ لأن حفر جميعها إذا لم يكن متعذرًا كان متعسرًا، والحرج مدفوع.
تبيين الحقائق ١/ ٢٥٦، العناية ٢/ ١٦٦، الهداية ١/ ١٠٤، حاشية الشلبي ١/ ٢٥٦.
(¬٢) العناية ٢/ ١٧٠، شرح فتح القدير ٢/ ١٧٠، الكتاب ١/ ١٣٧، ملتقى الأبحر ١/ ١٧٢، كشف الحقائق ١/ ٩٩، شرح الوقاية ١/ ٩٩، كنز الدقائق ١/ ٢٥٧، بداية المبتدي ١/ ١٠٥، الهداية ١/ ١٠٥، البحر الرائق ٢/ ٢١٠، تبيين الحقائق ١/ ٢٥٧.','3','14',NULL,NULL,'0');
INSERT INTO [b46] VALUES (690,'إلا إذا تصدق بكل النصاب.
و<span data-type="title" id=toc-359>نصاب الفضة:</span> مائتا درهم، كل عشرة منها وزن سبعة
<hr><span data-type="title">منحة السلوك</span>

قوله: إلا إذا تصدق بكل النصاب.
فإنه لا يحتاج حينئذ إلى النية؛ لأن الزكاة جزء من المال، وكان متعينًا فيه فلم يحتج إلى التعيين (¬١).
وعند زفر (¬٢)، والشافعي (¬٣): لا تسقط.

[نصاب الفضة]
قوله: ونصاب الفضة مائتا درهم (¬٤).
لما فرغ من بيان من تجب عليه الزكاة، ومن لا تجب، شرع في بيان نصب الأموال الزكوية، وقدم زكاة النقدين لأغلبهما، وقدم الفضة على الذهب، لكثرتها بالنسبة إلى الذهب.
قوله: كل عشرة منها وزن سبعةٍ.
_________
(¬١) الكتاب ١/ ١٣٩، المختار ١/ ١٠١، كشف الحقائق ١/ ٩٩، شرح الوقاية ١/ ٩٩، تبيين الحقائق ١/ ٢٥٧، البحر الرائق ٢/ ٢١٠، الاختيار ١/ ١٠١.
(¬٢) العناية ٢/ ١٧٠، الاختيار ١/ ١٠١.
(¬٣) وكذا عند المالكية، فصحة الإخراج عندهم، يفتقر إلى مقارنة النية.
وعند الحنابلة: يستحب مقارنة النية؛ للإخراج، وإن تقدمت النية حال الدفع بزمان يسير جاز، وإن طال لم يجز.
الشرح الصغير ١/ ٢٣٥، بلغة السالك ١/ ٢٣٥، روض الطالب ١/ ٣٥٨، أسنى المطالب ١/ ٣٥٨، دليل الطالب ١/ ٢٠٥، الإفصاح ١/ ٢١٠، الروض المربع ص ١٦٤.
(¬٤) وفاقًا للمالكية، والشافعية، والحنابلة.
بداية المبتدي ١/ ١١١، الجامع الصغير ص ١٢٣، الكتاب ١/ ١٤٦، الهداية ١/ ١١١، المختار ١/ ١١٢، ملتقى الأبحر ١/ ١٧٨، الوقاية ١/ ١٠٤، التلقين ص ٤٦، أقرب المسالك ص ٣٨، التنبيه ص ٨، شرح المحلي على المنهاج ٢/ ٢٢، الإفصاح ١/ ٢٠٦، الروض المربع ص ١٥٨.','3','15',NULL,NULL,'0');
INSERT INTO [b46] VALUES (691,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

أي: العشرة من الدراهم تكون وزن سبعة مثاقيل في الزكاة، ونصاب السرقة، وتقدير الديات، والمهر (¬١) (¬٢).
وأصله: أن الدراهم كانت مختلفةً في زمن عمر -رضي الله عنه-، وكانت على ثلاثة أصناف:
صنف منها: كل عشرة، عشرة مثاقيل، كل درهم عشرون قيراطًا.
وصنف منها: كل عشرة ستة مثاقيل، كل درهم اثنا عشر قيراطًا، وهو ثلاثة أخماس مثقالٍ.
وصنف منها: كل عشرة خمسة مثاقيل، كل درهم نصف مثقالٍ، وهو عشرة قراريط، وكان المثقال نوعًا واحدًا، وهو عشرون قيراطًا، وكان عمر -رضي الله عنه-، يطالب الناس في استيفاء الخراج، بأكبر الدراهم، ويشق عليهم ذلك، فالتمسوا منه التخفيف، فشاور عمر أصحاب رسول الله -صلى الله عليه وسلم-، فاجتمع رأيهم على أن يأخذ عمر من كل نوع ثلاثة، فأخذ فصار الدرهم بوزن أربعة عشر قيراطًا، فاستقر الأمر عليه في ديوان عمر -رضي الله عنه-، وهذا لأن ثلث العشرين قيراطًا، ستة وثلثان، وثلث الاثني عشر أربعة، وثلث العشرة ثلاثة وثلث،
_________
(¬١) وبيان المسألة، كما ذكر الكاساني بقوله: "وإنما اعتبر وزن سبعة، وهو أن يكون العشرة منها وزن سبعة مثاقيل، والمائتان مما يوزن مائة وأربعون مثقالًا؛ لأنه الوزن المجمع عليه للدراهم المضروبة في الإسلام، وذلك أن الدراهم في الجاهلية كان بعضها ثقيلًا مثقالًا، وبعضها خفيفًا طريًا، فلما عزموا على ضرب الدراهم في الإسلام، جمعوا الدرهم الثقيل، والدرهم الخفيف، فجعلوهما درهمين، فكانا درهمين بوزن سبعة، فاجتمعت الأمة على العمل على ذلك".
بدائع الصنائع ٢/ ٨٤١.
(¬٢) شرح فتح القدير ٢/ ٢١١، تبيين الحقائق ١/ ٢٧٨، الاختيار ١/ ١١١.','3','16',NULL,NULL,'0');
INSERT INTO [b46] VALUES (692,'أغلبها فضة، وفيه خمسة دراهم،
<hr><span data-type="title">منحة السلوك</span>

فالمجموع: أربعة عشر قيراطًا، تكون عشرة دراهم، مثل وزن سبعة مثاقيل؛ لأن وزن سبعة مثاقيل، مائة وأربعون قيراطًا، فكذا عشرة دراهم، مائة وأربعون قيراطًا (¬١).
وذكر في "الغاية": أن دراهم مصر: أربعة وستون حبةً، وهو أكبر من درهم الزكاة، فالنصاب مائة وثمانون درهمًا وحبتان (¬٢).
قوله: أغلبها فضة.
اعتبارًا للغالب، حتى لو كان الغش غالبًا على الفضة يكون في حكم العروض (¬٣)، ولم يعتبر القليل للضرورة؛ لأن الفضة لا تنطبع إلا بقليل غشٍّ (¬٤).
قوله: وفيه خمسة دراهم.
_________
(¬١) تبيين الحقائق ١/ ٢٧٨، العناية ٢/ ٢١١، الاختيار ١/ ١١، حاشية الشلبي ١/ ٢٧٨، شرح فتح القدير ٢/ ٢١١، البحر الرائق ٢/ ٢٢٧، منحة الخالق ٢/ ٢٢٧.
(¬٢) الغاية في شرح الهداية؛ لشمس الدين أبي العباس أحمد بن إبراهيم السروجي ق/ ١٢٩/ أالجزء الخامس، النسخة الأصلية لدى جامعة الملك سعود، تحت رقم ١٣٦٦ ونصه: "الدرهم المصري أربعة وستون حبة، وهو أكثر من درهم الزكاة، فإذا أسقطت الزايد، كان النصاب من دراهم مصر مائة وثمانين درهمًا وحبتين فقط".
(¬٣) شرح فتح القدير ٢/ ٢١٣، المختار ١/ ١١٢، الكتاب ١/ ١٤٧، بداية المبتدي ١/ ١١١، الاختيار ١/ ١١٢، الهداية ١/ ١١١.
(¬٤) وفاقًا للثلاثة، فلا يزكي مغشوش حتى يبلغ خالصه نصابًا.
تبيين الحقائق ١/ ٢٧٩، تحفة الفقهاء ١/ ٢٦٥، شرح فتح القدير ٢/ ٢١٣، العناية ٢/ ٢١٣، البحر الرائق ٢/ ٢٢٨، متن الرسالة ص ٧٦، أقرب المسالك ص ٣٨، التنبيه ص ٨، شرح المحلي على المنهاج ٢/ ٢٢، الروض المربع ص ١٥٨، الإنصاف ٣/ ١٣٢.','3','17',NULL,NULL,'0');
INSERT INTO [b46] VALUES (693,'ثم في كل أربعين درهمًا درهم، والناقص عفو.
<hr><span data-type="title">منحة السلوك</span>

أي: وفي مائتي درهم، خمسة دراهم (¬١)؛ لقوله -صلى الله عليه وسلم-: "هاتوا ربع العُشُور، من كل أربعين درهمًا، وليس عليكم شيء، حتى تتم مائتي درهم، فإذا كانت مائتي درهم، ففيها خمسة دراهم، فما زاد فعلى حساب ذلك" رواه أبو داود (¬٢).
قوله: ثم في كل أربعين درهمًا درهم، والناقص عفو (¬٣).
يعني: إذا زاد على المائتين شيء، لا شيء فيه عند أبي حنيفة، حتى يبلغ أربعين درهمًا، فإذا بلغ أربعين درهمًا ففيه درهم (¬٤)، وتكون الجملة: ستة
_________
(¬١) وفاقًا للثلاثة.
المختار ١/ ١١١، بداية المبتدي ١/ ١١١، الاختيار ١/ ١١١، الهداية ١/ ١١١، الوقاية ١/ ١٠٤، كشف الحقائق ١/ ١٠٤، الكتاب ١/ ١٤٦، متن الرسالة ص ٧٦، أقرب المسالك ص ٣٨، التنبيه ص ٨، شرح المحلي على المنهاج ٢/ ٢٢، الإفصاح ١/ ٢٠٦، شرح الزركشي ٢/ ٤٩٥.
(¬٢) ٢/ ٩٩ كتاب الزكاة، باب في زكاة السائمة ١٥٧٢، ورواه الدارمي ١/ ٤١٠ كتاب الزكاة باب في زكاة الورق رقم ١٥٨٦، والترمذي ٢/ ٢٠٠ برقم ٦٢٠ كتاب الزكاة، باب ما جاء في زكاة الذهب والورق، وابن عدي ٧٠٤، وأبو عبيد في الأموال ص ٥٠٠ كتاب الصدقة وأحكامها وسننها، باب فروض زكاة الذهب والورق وما فيهما من السنن، والدارقطني ٢/ ٩٢ كتاب الزكاة، باب وجوب زكاة الذهب والورق والماشية رقم ٣، والبيهقي في السنن ٤/ ١١٨ كتاب الزكاة، باب لا صدقة في الخيل. قال الترمذي ٢/ ٢٠١: وسألت محمد بن إسماعيل عن هذا الحديث، فقال: كلاهما عندي صحيح، يحتمل عن أبي إسحاق، ويحتمل أن يكون روى عنهما جميعًا، وقال ابن حجر في البلوغ ص ١٢٧ رقم ٧: وهو حسن.
(¬٣) بداية المبتدي ١/ ١١١، الكتاب ١/ ١٤٦، الهداية ١/ ١١١، ملتقى الأبحر ١/ ١٧٩، تحفة الفقهاء ١/ ٢٦٦، شرح فتح القدير ٢/ ٢٠٩، الاختيار ١/ ١١١، كشف الحقائق ١/ ١٠٥.
(¬٤) شرح فتح القدير ٢/ ٢١٠، تبيين الحقائق ٢/ ٢٧٧، الهداية ١/ ١١١، شرح الوقاية ١/ ١٠٥، الاختيار ١/ ١١١، تحفة الفقهاء ١/ ٢٦٦.','3','18',NULL,NULL,'0');
INSERT INTO [b46] VALUES (694,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

دراهم خمسة في المائتين، ودرهم في الأربعين، ولا شيء فيما دون الأربعين (¬١).
وقالا: ما زاد على المائتين فبحسابه (¬٢)، حتى إذا زادت عشرة على المائتين مثلًا، يعطي خمسة دراهم وربع، وإذا زادت خمسة عشر، يعطي خمسة دراهم وربع درهم وثمن درهم، وإذا زادت عشرين يعطي خمسة دراهم ونصف درهم، وعلى هذا [فقس] (¬٣) لما مر (¬٤) من قوله: "فما زاد فعلى حساب ذلك".
وله قوله -صلى الله عليه وسلم-: "لا تأخذوا من الكسور شيئًا" رواه أبو بكر الرازي (¬٥)،
_________
(¬١) تحفة الفقهاء ١/ ٢٦٤، الهداية ١/ ١١١، شرح فتح القدير ٢/ ٢١٠، تبيين الحقائق ٢/ ٢٧٧، كشف الحقائق ١/ ١٠٥.
(¬٢) وإليه ذهب المالكية، والشافعية، والحنابلة.
الهداية ١/ ١١١، البحر الرائق ٢/ ٢٢٨، شرح فتح القدير ٢/ ٢١٠، تحفة الفقهاء ١/ ٢٦٤، الاختيار ١/ ١١١، حاشية رد المحتار ٢/ ٢٩٩، ملتقى الأبحر ١/ ١٧٩، تنوير الأبصار ٢/ ٢٩٩، تبيين الحقائق ١/ ٢٧٧، الكافي لابن عبد البر ص ٨٨، التفريع ١/ ٢٧٣، التلقين ص ٤٦، مختصر خليل ص ٦٢، التذكرة ص ٧١، متن الغاية والتقريب ص ٨٨، الإفصاح ١/ ٢٠٦، هداية الراغب ص ١٨٢.
(¬٣) زيادة يقتضيها السياق.
(¬٤) في ٣/ ١٨.
(¬٥) هو أحمد بن علي الرازي أبو بكر، الإمام الكبير الشأن المعروف بالجصاص، من أهل الري، ولد سنة ٣٠٥ هـ انتهت إليه رئاسة الحنفية، سكن بغداد وخوطب في أن يلي القضاء فامتنع توفي سنة ٣٧٠ هـ ببغداد من مصنفاته: أحكام القرآن، شرح الجامع، شرح مختصر الطحاوي.
الجواهر المضية ١/ ٢٢٠، تاريخ بغداد ٤/ ٣١٤، الطبقات السنية رقم ٢٦٨، الفوائد البهية ص ٢٧.','3','19',NULL,NULL,'0');
INSERT INTO [b46] VALUES (695,'و<span data-type="title" id=toc-360>نصاب الذهب </span>عشرون مثقالًا
<hr><span data-type="title">منحة السلوك</span>

في شرح مختصر الطحاوي (¬١).

[نصاب الذهب]
قوله: ونصاب الذهب عشرون مثقالًا (¬٢).
لما روت عائشة -رضي الله عنها- "أن رسول الله -صلى الله عليه وسلم- كان يأخذ من كل عشرين دينارًا فصاعدًا، نصف دينار، ومن الأربعين دينارًا" رواه ابن ماجه (¬٣).
_________
(¬١) جـ ١ لوحة ١٩٢/ ب (مخطوط) النسخة الأصلية لدى متحف الآثار العتيقة بقونية بتركيا، تحت رقم ٣٢٧/ ٤٩٧٩ - ٧ ونصه فيه: "مسألة: قال أبو جعفر: وما زاد على خمس أواقٍ من الورق فلا شيء فيه، حتى يكون أربعين درهمًا، ففيها درهم، ثم يعتبر في كل زيادة أربعين درهمًا، وهو قول: أبي حنيفة، وقال: أبو يوسف، ومحمد: ما زاد فبحسابه. قال أحمد: يروى نحو قول: أبي حنيفة، عن عمر، والحسن، والشعبي، وروي نحو قولهما: عن علي. والحجة للقول الأول: ما روى يونس بن بكير، عن محمد، قال: حدثني المنهال بن الجراح، عن حبيب بن نجيح، عن عبادة بن نُسي، عن معاذ بن جبل -رضي الله عنه- أن رسول الله -صلى الله عليه وسلم- أمره حين وجهه إلى اليمن: أن لا يأخذ من الكسور شيئًا، إذا بلغ الورق مائتي درهم، أخذ منها خمسة دراهم، ولا يأخذ مما زاد، حتى تبلغ أربعين درهمًا، فيأخذ منها درهمًا". ورواه الدارقطني ٢/ ٩٣ كتاب الزكاة، باب ليس في الكسر شيء رقم ١، والبيهقي ٤/ ١٣٥، كتاب الزكاة، باب ذكر الخبر الذي روى في وقص الورق.
من طريق ابن إسحاق، عن المنهال بن الجراح، عن حبيب بن نجيح، عن عبادة بن نسي، عن معاذ -رضي الله عنه- أن رسول الله -صلى الله عليه وسلم- أمره حين وجهه إلى اليمن: أن لا تأخذ من الكسور شيئًا. إذا كانت الورق مائتي درهم فخذ منها خمسة دراهم، ولا تأخذ مما زاد شيئًا حتى تبلغ أربعين درهمًا، فإذا بلغت أربعين فخذ منها درهمًا.
قال الدارقطني في السنن ٢/ ٩٤: "المنهال بن الجراح متروك الحديث، وعبادة بن نسي لم يسمع من معاذ".
وقال البيهقي ٤/ ١٣٦: إسناد هذا الحديث ضعيف جدًا. وقال الحافظ في الدراية ١/ ٢٥٠: إسناده ضعيف جدًا.
(¬٢) المختار ١/ ١١١، الكتاب ١/ ١٤٧، تنوير الأبصار ٢/ ٢٩٥، الدر المختار ٢/ ٢٩٥، الاختيار ١/ ١١١، بداية المبتدي ١/ ١١٢، كشف الحقائق ١/ ١٠٤، كنز الدقائق ١/ ٢٧٦.
(¬٣) ١/ ٥٧١ كتاب الزكاة باب زكاة الورق والذهب رقم ١٧٩١، ورواه الدارقطني ٢/ ٩٢ =','3','20',NULL,NULL,'0');
INSERT INTO [b46] VALUES (696,'أغلبها ذهب، وفيه نصف مثقال،
<hr><span data-type="title">منحة السلوك</span>

والمثقال: ستة دوانق، وهو عشرون قيراطًا، كل قيراط خمس شعيرات (¬١).
قوله: أغلبها ذهب. اعتبارًا للغالب، وقد مر (¬٢).
قوله: وفيه. أي: في عشرين مثقالًا نصف مثقالٍ (¬٣)؛ لما روينا.
_________
= كتاب الزكاة، باب وجوب زكاة الذهب والورق، والماشية والثمار والحبوب رقم ١ "ولم يذكر فصاعدًا".
من طريق عبيد الله بن موسى، عن إبراهيم بن إسماعيل، عن عبد الله بن واقد، عن ابن عمر وعائشة -رضي الله عنهما-، أن النبي -صلى الله عليه وسلم-: "كان يأخذ ... " وإبراهيم بن إسماعيل هو ابن مجمع، قال فيه ابن معين: لا شيء، وقال أبو حاتم: يكتب حديثه ولا يحتج به، فإنه كثير الوهم.
وكذا رواه أبو عبيد في الأموال ص ٥٠٠ كتاب الصدقة وأحكامها وسننها، باب فروض زكاة الذهب الورق وما فيهما من السنن. من طريق الحسن بن عمارة، عن أبي إسحاق، عن عاصم بن صفرة، عن علي -رضي الله عنه- موقوفًا قال: "في كل عشرين دينارًا نصف دينار، وفي كل أربعين دينارًا دينار، وفي كل مائتي درهم خمسة دراهم". والحسن بن عمارة ضعيف جدًا. ورواه أبو داود ٢/ ١٠٠ كتاب الزكاة، باب في الزكاة السائمة رقم ١٥٧٣، عن سليمان بن داود المهري، أخبرنا ابن وهب، أخبرني جرير بن حازم وسمى آخر، عن أبي إسحاق، عن عاصم بن ضمرة، والحارث بن الأعور، عن علي -رضي الله عنه- عن النبي -صلى الله عليه وسلم- وفيه: "وليس عليك شيء، يعني في الذهب، حتى يكون لك عشرون دينارًا، فإذا كان لك عشرون دينارًا، وحال عليها الحول ففيها نصف دينار، فما زاد فبحساب ذلك" قال في الزوائد ٢/ ٤٩: إسناد الحديث ضعيف؛ لضعف إبراهيم بن إسماعيل.
(¬١) والدرهم: أربعة عشرة قيراطًا، فيكون الدرهم الشرعي سبعين شعيرة، والمثقال: مائة شعيرة، فهو درهم وثلاثة أسباع درهم.
الدر المختار ٢/ ٢٩٦، حاشية رد المحتار ٢/ ٢٩٦، شرح فتح القدير ٢/ ٢١٥، حاشية الشلبي ٢/ ٢٧٨، شرح الوقاية ١/ ١٠٤، تبيين الحقائق ١/ ٢٧٨، العناية ٢/ ٢١٥.
(¬٢) في ٣/ ١٧.
(¬٣) بداية المبتدي ١/ ١١٢، الكتاب ١/ ١٤٧، تنوير الأبصار ٢١/ ٢٩٩، الهداية ١/ ١١٢، =','3','21',NULL,NULL,'0');
INSERT INTO [b46] VALUES (697,'ثم في كل أربعة مثاقيل قيراطان، والناقص عفو، والتِّبْر والحُليُّ والآنية نصاب.
<hr><span data-type="title">منحة السلوك</span>

قوله: ثم في كل أربعة مثاقيل قيراطان، والناقص عفو.
يعني إذا زاد على عشرين مثقالًا، لا شيء فيه عند أبي حنيفة، إلى أن يبلغ أربعة مثاقيل، فإذا بلغ ذلك ففيه قيراطان (¬١)، والقيراطان: من أربعة مثاقيل ربع العشر؛ لأن عدد المثاقيل وهي أربعة، إذا ضرب في عدد قراريط المثقال وهو عشرون، يكون ثمانين، وعشر ثمانين ثمانية، وربع الثمانية اثنان، فيكون القيراطان: ربع عشر أربعة مثاقيل (¬٢)، فافهم.
وقالا: ما زاد فبحسابه (¬٣)، وقد مر (¬٤).
قوله: والتِّبْر، والحلي (¬٥)، والآنية، نصاب.
يعني: في وجوب الزكاة (¬٦).
_________
= كنز الدقائق ١/ ٢٧٦، المختار ١/ ١١١. الدر المختار ٢/ ٢٩٩، ملتقى الأبحر ١/ ١٧٨.
(¬١) الكتاب ١/ ١٤٨، المختار ١/ ١١١، الاختيار ١/ ١١١، كنز الدقائق ١/ ٢٧٧، كشف الحقائق ١/ ١٠٤، تبيين الحقائق ١/ ٢٧٧، تنوير الأبصار ٢/ ٢٩٩، الدر المختار ٢/ ٢٩٩.
(¬٢) العناية ٢/ ٢١٥، حاشية رد المحتار ٢/ ٢٩٦، تبيين الحقائق ١/ ٢٧٨، شرح فتح القدير ٢/ ٢١٥، البحر الرائق ٢/ ٢٢٧.
(¬٣) البحر الرائق ٢/ ٢٢٨، شرح فتح القدير ٢/ ٢١٠، الهداية ١/ ١١١، كنز الدقائق ١/ ٢٧٧، تحفة الفقهاء ١/ ٢٦٤، تنوير الأبصار ٢/ ٢٩٩، الدر المختار ٢/ ٢٩٩.
(¬٤) في ٣/ ١٩.
(¬٥) الحُلي: كل حِلْية حَليت بها امرأة، أو سيفًا، ونحوه. والجمع حُلِيُّ، ويكون من الذهب، أو الفضة، أو الجوهر.
لسان العرب ١٤/ ١٩٥ مادة حلا، المغرب ص ١٢٧ مادة الحلي، مختار الصحاح ص ٦٤ مادة ح ل ا، القاموس المحيط ١/ ٧٠١ مادة ح ل ى.
(¬٦) المختار ١/ ١١٠، الكتاب ١/ ١٤٨، بداية المبتدي ١/ ١١٢، كنز الدقائق ١/ ٢٧٧، الوقاية ١/ ١٠٤، الهداية ١/ ١١٢، كشف الحقائق ١/ ١٠٤، تبيين الحقائق ١/ ٢٧٧.','3','22',NULL,NULL,'0');
INSERT INTO [b46] VALUES (698,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

والتبر: القطعة المأخوذة من المعدن (¬١).
وقال الشافعي: لا زكاة في حلي النساء، وخاتم الفضة للرجال (¬٢).
ولنا: ما روي عن أم سلمة -رضي الله عنها- قالت: كنت ألبس أوضاحًا من ذهب، فقلت يا رسول الله: أكنز هو؟ قال: "ما بلغ أن تؤدى زكاته فزكي، فليس بكنزٍ" رواه أبو داود (¬٣). والوضح: الحلى وجمعه أوضاح.
وما رواه الشافعي، من حديث جابرٍ -رضي الله عنه- أنه -صلى الله عليه وسلم- قال: "ليس في الحلي زكاة"، فلا أصل له، رواه البيهقي (¬٤).
_________
(¬١) لسان العرب ٤/ ٨٨ مادة تبر، القاموس المحيط ١/ ٣٥٦ مادة ت ب ر، المغرب ص ٥٨ مادة التبر، مختار الصحاح ص ٣١ مادة ت ب ر.
(¬٢) وإليه ذهب المالكية، والحنابلة.
جواهر الإكليل ١/ ١٢٧، القوانين الفقهية ص ٦٩، متن أبي شجاع ص ٨٨، كفاية الأخيار ١/ ١١٤، المستوعب ٣/ ٢٧٨ الإقناع للحجاوي ٢/ ٢٠٦، العمدة ص ٢٧، مختصر الخرقي ص ٤٧، منتهى الإرادات ١/ ٤٠٥.
(¬٣) ٢/ ٩٥ كتاب الزكاة، باب الكنز ما هو وزكاة الحلى ١٥٦٤، ورواه أيضًا الدارقطني ٢/ ١٠٥ كتاب الزكاة، باب ما أدى زكاته فليس بكنز رقم ١، والحاكم ١/ ٣٩٠ كتاب الزكاة، والبيهقي في السنن الكبرى ٤/ ٨٣ كتاب الزكاة، باب تفسير الكنز الذي ورد الوعيد فيه، من طريق ثابت بن عجلان، عن عطاء، عن أم سلمة ... " قال الحاكم: صحيح على شرط البخاري، وسكت عنه الذهبي. وقال في الدراية ١/ ٢٥٩: قواه ابن دقيق العيد.
وقال في المجموع ٦/ ٣٣: رواه أبو داود بإسناد حسن.
(¬٤) أخرجه ابن الجوزي في التحقيق ٢/ ١٤٢٠، من حديث إبراهيم بن أيوب، عن عافية بن أيوب، عن ليث بن سعد، عن أبي الزبير عن جابر مرفوعًا.
قال ابن عبد الهادي ٢/ ١٤٢١: الصواب وقف هذا الحديث على جابر.
وروي موقوفًا عن جابر رواه الشافعي ١/ ٢٣٩ ترتيب المسند كتاب الزكاة، الباب الأول في الأمر بها والتهديد على تركها وعلى من تجب وفيم تجب رقم ٦٢٩، عن سفيان، =','3','23',NULL,NULL,'0');
INSERT INTO [b46] VALUES (699,'وما غالبه منهما غش فهو كعروض التجارة، إلا أن يخلص منه نصاب. ونصاب العروض أن تبلغ قيمتها نصابًا بالأنفع للفقراء.
<hr><span data-type="title">منحة السلوك</span>

قوله: وما غالبه منهما.
أي: من الذهب والفضة غش، فهو كعروض التجارة، فلا يزكى إلا بنية التجارة، ويقوم عند الزكاة، إلى أن يخلص منه نصاب، فحينئذٍ لا يشترط فيها نية التجارة ولا القيمة (¬١).

<span data-type="title" id=toc-361>[زكاة العروض]</span>
ونصاب العروض (¬٢)، أن تبلغ قيمتها نصابًا بالأنفع للفقراء. وذلك
_________
= عن عمرو بن دينار، قال: سمعت رجلًا يسأل جابر بن عبد الله -رضي الله عنهما- عن الحلي أفيه زكاة؟ فقال جابر: لا، فقال: وإن كان يبلغ ألف دينار، فقال: جابر كثير.
ورواه عبد الرزاق كتاب الزكاة، باب التبر والحلي رقم ٧٠٤٦، وابن أبي شيبة في المصنف ٣/ ١٥٥ كتاب الزكاة، باب من قال ليس في الحلي زكاة، والدارقطني ٢/ ١٠٧ كتاب الزكاة، باب زكاة الحلي رقم ٤ من طريق أبي حمزة ميمون، عن الشعبي، عن جابر موقوفًا.
وقال الدارقطني: أبو حمزة هذا ميمون: ضعيف الحديث، ورواه البيهقي في المعرفة ٦/ ١٣٩ كتاب الزكاة، باب زكاة الحلي رقم ٨٢٧٩ من طريق سفيان، عن عمرو بن دينار، قال: سمعت رجلًا يسأل جابر بن عبد الله -رضي الله عنهما- عن الحلي، أفيه الزكاة؟ قال جابر: لا، فقال: وإن كان يبلغ ألف دينار، فقال جابر: كثير، والبيهقي في السنن الكبرى ٤/ ١٣٨ كتاب الزكاة باب من قال لا زكاة في الحلي، وقد أنكر البيهقي هذا الحديث، حيث قال في المعرفة ٦/ ١٤٤: باطل لا أصل له، إنما يروى عن جابر من قوله غير مرفوع، والذي يروى عن عافية بن أيوب مجهول، فمن احتج به مرفوعًا كان مغررًا بدينه، داخلًا فيما نعيب به المخالفين في الاحتجاج برواية الكذابين، والله يعصمنا من أمثاله.
(¬١) وكذا عند المالكية.
تحفة الفقهاء ١/ ٢٦٥، المختار ١/ ١١٢، سنن الحقائق ١/ ٢٧٩، حاشية الشلبي ١/ ٢٧٩، الكتاب ١/ ١٤٧، كشف الحقائق ١/ ١٠٦، الاختيار ١/ ١١٢، منح الجليل ٢/ ٤١، شرح الزرقاني على خليل ٢/ ١٤٤.
(¬٢) العروض جمع عرض -بفتحتين-: حطام الدنيا، والعرض -بسكون الراء-: المتاع، =','3','24',NULL,NULL,'0');
INSERT INTO [b46] VALUES (700,'وكمال النصاب في طرفي الحول كافٍ.
<hr><span data-type="title">منحة السلوك</span>

لرعاية حق الفقراء (¬١).
وعن أبي يوسف: أن يقوم بما اشترى به، إذا كان الثمن من النقود، وإن اشتراها بغير النقود يقومها بالغالب من النقود (¬٢).
وعن محمد: أنها تقوم بالنقد الغالب على كل حال، ويقوم بالمصر الذي هو فيه، وإن كان في مفازة يقوم في المصر الذي يليه (¬٣).
قوله: وكمال النصاب في طرفي الحول كافٍ (¬٤).
صورته: إذا كان النصاب كاملًا في ابتداء الحول وانتهائه، فنقصانه فيما
_________
= واختلفت عبارات الفقهاء في المراد منها. قال الأصمعي: ما كان من مال غير نقد. وقال أبو عبيد: ما عدا العقار، والحيوان، والمكيل، والموزون، والغالب يطلقونها على الأول.
لسان العرب ٧/ ١٧٠ مادة عرض، الدر النقي في شرح ألفاظ الخرقي ٢/ ٢٤٠، معجم لغة الفقهاء ١/ ١١٤، العناية ٢/ ٢١٧، شرح فتح القدير ٢/ ٢١٧.
(¬١) وإليه ذهب الحنابلة.
بدائع الصنائع ١/ ٢٠، الهداية ١/ ١١٢، تبيين الحقائق ١/ ٢٧٩، حاشية الشلبي ١/ ٢٧٩، ملتقى الأبحر ١/ ١٧٩، العناية ٢/ ٢١٩، شرح الزركشي ٢/ ٥١٩، التسهيل ص ٨٥، الإفصاح ١/ ٢٠٩، المغني ٢/ ٦٢٥.
(¬٢) الهداية ١/ ١١٢، تبيين الحقائق ١/ ٢٨٠، شرح فتح القدير ٢/ ٢٢٠، العناية ٢/ ٢٢٠، البحر الرائق ٢/ ٢٢٩، حاشية الشلبي ١/ ٢٨٠.
(¬٣) وعند الشافعية: يقومها بالثمن الذي اشتراها به، وإن كان قد اشتراها بعوض، قومها بنقد البلد.
شرح فتح القدير ٢/ ٢١٩، تبيين الحقائق ١/ ٢٨٠، العناية ٢/ ٢١٩، البحر الرائق ٢/ ٢٢٩، حاشية الشلبي ١/ ٢٨٠، التلقين ص ٤٧، التفريع ١/ ٢٨٠، مغني المحتاج ١/ ٣٩٩، المجموع ٦/ ٦٤.
(¬٤) الكتاب ١/ ١٤٩، كنز الدقائق ١/ ٢٨٠، البحر الرائق ٢/ ٢٢٩، المختار ١/ ٩٩، الهداية ١/ ١٠٣، الاختيار ١/ ٩٩.','3','25',NULL,NULL,'0');
INSERT INTO [b46] VALUES (701,'ويضم الذهب والفضة والعروض بعضها إلى بعض بالقيمة،
<hr><span data-type="title">منحة السلوك</span>

بين ذلك لا يسقط الزكاة؛ لأن ما بين ذلك ليس بوقت الوجوب، ولا بوقت الانعقاد، فلم يعتبر كمال النصاب فيه (¬١)، خلافًا لزفر (¬٢).

<span data-type="title" id=toc-362>[صفة ضم الذهب والفضة والعروض]</span>
قوله: ويضم الذهب، والفضة، والعروض، بعضها إلى بعض بالقيمة (¬٣).
أما نفس الضم: فليس فيه خلاف عندنا (¬٤)، ولكن الخلاف في كيفية الضم؟.
فعند أبي حنيفة: يضم الذهب إلى الفضة بالقيمة (¬٥).
_________
(¬١) تبيين الحقائق ١/ ٢٨٠، الهداية ١/ ١١٢.
(¬٢) والمالكية، والحنابلة، حيث يرون: أن نقصان النصاب في بعض الحول يمنع وجوب الزكاة.
وذهب الشافعية: إلى أن النصاب يعتبر بآخر الحول، وهو الأظهر عندهم، وفي قول: بطرفيه. وفي قول: بجميعه.
تبيين الحقائق ١/ ٢٨٠، العناية ٢/ ٢٢٠، شرح فتح القدير ٢/ ٢٢١، البحر الرائق ٢/ ٢٢٩، الشرح الصغير ١/ ٢٠٦، جواهر الإكليل ١/ ١٢١، القوانين الفقهية ص ٦٨، مغني المحتاج ١/ ٣٩٧، السراج الوهاج ص ١٢٧، المجموع ٦/ ٥٥، الإفصاح ١/ ٢١١، الروض المربع ص ١٥١، نيل المراد ص ٦٩.
(¬٣) الكتاب ١/ ١٤٩، المبسوط ٢/ ١٩٢، كنز الدقائق ١/ ٢٨٠.
(¬٤) وإلى الضم ذهب المالكية، والحنابلة.
وذهب الشافعية: إلى عدم الضم، واختاره أبو ثور.
بداية المجتهد ١/ ٢٥٧، متن الرسالة ص ٧٦، مختصر خليل ص ٦٢، حلية العلماء ٣/ ٩٠، رحمة الأمة ١/ ١٠٢، الإنصاف ٢/ ١٣٦، المستوعب ٣/ ٢٨٣.
(¬٥) الكتاب ١/ ١٤٩، المبسوط ٢/ ١٩٣، تبيين الحقائق ١/ ٢٨١، الهداية ١/ ١١٣، ملتقى الأبحر ١/ ١٧٩، كشف الحقائق ١/ ١٠٦، شرح الوقاية ١/ ١٠٦.','3','26',NULL,NULL,'0');
INSERT INTO [b46] VALUES (702,'ويضم ما دون الأربعين إلى ما دون أربعة مثاقيل أيضًا.
<hr><span data-type="title">منحة السلوك</span>

وعندهما: بالأجزاء (¬١)، حتى إذا كان النصف من أحدهما، والنصف من الآخر، أو الثلث من أحدهما، والثلثان من الآخر، أو الربع من أحدهما وثلاثة الأرباع من الآخر، يضم بالاتفاق.
أما إذا كان من أحدهما النصف، ومن الآخر ربع يساوي قيمته النصف من الآخر، يضم عند أبي حنيفة، خلافًا لهما، فيؤدي الزكاة من أي النوعين شاء، أو يؤدي من الدراهم حصتها، ومن الدنانير حصتها.
وأما العروض: فعند أبي حنيفة إن شاء قوم العروض، فيضم قيمتها إلى الذهب والفضة، وإن شاء قوم الذهب والفضة، فيضم القيمة إلى قيمة العروض (¬٢).
وعندهما: لا يضم الذهب والفضة بالقيمة، ولكن يقوم العروض، فيضم باعتبار الأجزاء (¬٣).
قوله: ويضم ما دون الأربعين. أي: من الدراهم، إلى ما دون أربعة مثاقيل من الدنانير (¬٤).
_________
(¬١) وهو رواية عن أبي حنيفة، وإليه ذهب المالكية والحنابلة.
تبيين الحقائق ١/ ٢٨١، شرح فتح القدير ٢/ ٢٢٢، كشف الحقائق ١/ ١٠٦، الهداية ١/ ١١٣، الكافي لابن عبد البر ص ٩٠، القوانين الفقهية ص ٦٨، الإفصاح ١/ ٢٠٧، الإنصاف ٢/ ١٣٦، المستوعب ٣/ ٢٨٣.
(¬٢) تبيين الحقائق ١/ ٢٨٢، الهداية ١/ ١١٣، كشف الحقائق ١/ ١٠٦، الوقاية ١/ ١٠٦، المبسوط ٢/ ١٩٣.
(¬٣) تبيين الحقائق ١/ ٢٨٢، العناية ٢/ ٢٢٢، شرح فتح القدير ٢/ ٢٢٢، شرح الوقاية ١/ ١٠٦، البحر الرائق ٢/ ٢٣٠، تحفة الفقهاء ١/ ٢٦٧.
(¬٤) تحفة الفقهاء ١/ ٢٦٧، العناية ٢/ ٢٢١، شرح الوقاية ١/ ١٠٦، كشف الحقائق ١/ ١٠٦، شرح فتح القدير ٢/ ٢٢١.','3','27',NULL,NULL,'0');
INSERT INTO [b46] VALUES (703,'و<span data-type="title" id=toc-363>نصاب الإبل:</span> في كل خمس شاة إلى خمس وعشرين، ثم بنت مخاض إلى ست وثلاثين، ثم بنت لبون إلى ست وأربعين، ثم حقة إلى
<hr><span data-type="title">منحة السلوك</span>

صورته: إذا كان الفاضل على المائتين مثلًا ثلاثين، وعلى عشرين مثقالًا ثلاثة مثاقيل، يضم أحدهما إلى الآخر، على الخلاف المذكور. فافهم.

[نصاب الإبل]
قوله: ونصاب الإبل: في كل خمس شاة، إلى قوله: إلى مائة وعشرين (¬١).
لما روى البخاري في صحيحه (¬٢)، مسندًا إلى ثمامة بن عبد الله بن أنس (¬٣)، أن أنسًا حدثه، أن أبا بكر -رضي الله عنه- كتب له هذا الكتاب، لما وجّهه إلى البحرين: "بسم الله الرحمن الرحيم، هذه فريضة الصدقة التي فرض رسول الله -صلى الله عليه وسلم- على المسلمين، والذي أمر الله به رسوله، فمن سئلها من المسلمين على وجهها فليعطها، ومن سئل فوقها فلا يعط، في أربع وعشرين من الإبل فما دونها من الغنم، في كل خمسٍ شاة، فإذا بلغت خمسًا وعشرين إلى خمس وثلاثين ففيها بنت مخاضٍ أنثى، فإذا بلغت ستًا وثلاثين إلى خمس وأربعين ففيها بنت لبون أنثى، فإذا بلغت ستة وأربعين إلى ستين ففيها حقة
_________
(¬١) المختار ١/ ١٠٥، كنز الدقائق ١/ ٢٦٠، كشف الحقائق ١/ ٩٩، تنوير الأبصار ٢/ ٢٧٧، الاختيار ١/ ١٠٥، تبيين الحقائق ١/ ٢٦٠، بداية المبتدي ١/ ١٠٥، الكتاب ١/ ١٣٨.
(¬٢) قال البخاري في صحيحه ٢/ ٥٢٧: كتاب الزكاة، باب من بلغت عنده صدقة بنت مخاض وليست عنده رقم ١٣٨٦، حدثنا محمد بن عبد الله بن المثنى الأنصاري، قال: حدثني أبي، قال: حدثني ثمامة بن عبد الله بن أنس أن أنسًا حدثه أن أبا بكر كتب له هذا الكتاب ...
(¬٣) هو ثمامة بن عبد الله بن أنس بن مالك الأنصاري، من التابعين، كان من العلماء الصادقين، ولي قضاء البصرة، وكان يقول: صحبت جدي ثلاثين سنة، روى عن جده والبراء، وثقه أحمد والنسائي، توفي بعد مائة وعشرة من الهجرة.
سير أعلام النبلاء ٥/ ٢٠٤، خلاصة تهذيب تهذيب الكمال ص ٥٨، تهذيب الكمال ٤/ ٤٠٥، الكاشف ١/ ١٧٤.','3','28',NULL,NULL,'0');
INSERT INTO [b46] VALUES (704,'إحدى وستين، ثم جذعة إلى ست وسبعين، ثم بنتا لبون إلى إحدى وتسعين، ثم حقتان إلى مائة وعشرين، ثم يبدأ كما مر إلى خمس وعشرين، ثم بنت مخاض إلى مائة وخمسين، ثم ثلاث حِقاق، ثم يبدأ إلى ست وثلاثين، ثم بنت لبون إلى مائة وست
<hr><span data-type="title">منحة السلوك</span>

طروقة الجمل، فإذا بلغت واحدة وستين إلى خمس وسبعين ففيها جذعة، فإذا بلغت يعني ستًا وسبعين إلى تسعين ففيها بنتا لبون، فإذا بلغت إحدى وتسعين إلى عشرين ومائة ففيها حقتان طروقتا الجمل، فإذا زادت على عشرين ومائة ففي كل أربعين بنت لبون، وفي كل خمسين حقة" (¬١).
قوله: ثم يبدأ كما مر، إلى خمس وعشرين.
اعلم أنه لا خلاف بين الفقهاء إلى مائة وعشرين (¬٢)، ولكن اختلفوا في الزيادة عليها:
فقال أصحابنا: يستأنف الفريضة، فيكون في خمس شاة مع الحقتين، وفي العشر شاتان، هكذا إلى مائة وخمس وأربعين، ففيها حقتان وبنت مخاض، إلى مائة وخمسين ففيها ثلاث حقاق، ثم تستأنف الفريضة هكذا، في كل خمس شاة، فإذا بلغت مائة وخمسًا وسبعين ففيها ثلاث حقاق وبنت لبون، إلى مائة وستة وثمانين ففيها ثلاث حقاق وبنت لبونٍ، إلى مائة وست
_________
(¬١) وتمامه: "ومن لم يكن معه إلا أربع من الإبل، فليس فيها صدقة إلا أن يشاء ربها، فإذا بلغت خمسًا من الإبل ففيها شاة، وفي صدقة الغنم في سائمتها إذا كانت أربعين إلى عشرين ومائة شاة، فإذا زادت على عشرين ومائة إلى مائتين شاتان، فإذا زادت على مائتين إلى ثلاثمائة ففيها ثلاث شياه، فإذا زادت على ثلاثمائة ففي كل مائة شاة، فإذا كانت سائمة الرجل ناقصة من أربعين شاة واحدة فليس فيها صدقة إلا أن يشاء ربها، وفي الرقة ربع العشر، فإن لم تكن إلا تسعين ومائة فليس فيها شيء إلا أن يشاء ربها".
وقد سقت الحديث بتمامه؛ لأن المصنف أشار إليه مقطعًا في مواضع من هذا الكتاب.
(¬٢) مراتب الإجماع ص ٣٦، الإجماع لابن المنذر ص ٤٦.','3','29',NULL,NULL,'0');
INSERT INTO [b46] VALUES (705,'وتسعين، ثم أربع حقاق إلى مائتين، ثم يبدأ كما بدأ ثانيًا.
<hr><span data-type="title">منحة السلوك</span>

وتسعين فيجب فيها أربع حقاق، إلى مائتين، ثم يستأنف الفريضة أبدًا، مثل ما استؤنفت من مائة وخمسين إلى مائتين (¬١).
وقال الشافعي: إذا زادت على مائة وعشرين واحدة، ففيها ثلاث بنات لبون، وإذا صارت مائة وثلاثين ففيها حقة وبنتا لبون، ثم يدور الحساب على الأربعينات، والخمسينات، فيجب في كل أربعين بنت لبون، وفي كل خمسين حقة، كما يدور في البقر على الثلاثينات، والأربعينات (¬٢).
ولنا: كتاب رسول الله -صلى الله عليه وسلم- إلى عمرو بن حزم -رضي الله عنه- (¬٣)، فكان فيه: "إذا بلغت إحدى وتسعين ففيها حقتان، إلى أن تبلغ عشرين ومائة، فإذا كانت أكثر من ذلك، ففي كل خمسين حقة، وفي كل أربعين بنت لبون، فما فضل فإنه
_________
(¬١) بداية المبتدي ١/ ١٠٦، كنز الدقائق ١/ ٢٦٠، العناية ٢/ ١٧٢، تبيين الحقائق ١/ ٢٦٠، الهداية ١/ ١٠٦، الكتاب ١/ ١٣٩، ملتقى الأبحر ١/ ١٧٣، كشف الحقائق ١/ ٩٩، شرح الوقاية ١/ ٩٩، المختار ١/ ١٠٦.
(¬٢) وإليه ذهب الحنابلة، وهو مذهب الأوزاعي، وإسحاق.
وعند المالكية: في مائة وإحدى وعشرين إلى تسع وعشرين ومائة حقتان، أو ثلاث بنات لبون، الخيار للسائل، ثم في كل عشر يتغير الواجب، ففي كل أربعين بنت لبون، وفي كل خمسين حقه.
أقرب المسالك ص ٣٦، مواهب الجليل ٢/ ٢٦١، التاج الإكليل ٢/ ٢٦١، شرح الزرقاني على خليل ٢/ ١١٧، التذكرة ص ٧٠، متن الزبد ص ٣٣، المقنع ١/ ٣٠١، الشرح الكبير لابن قدامة ٢/ ٤٨٦، شرح منتهى الإرادات ١/ ٣٧٦.
(¬٣) هو عمرو بن حزم بن زيد بن لوذان، أبو الضحاك الأنصاري، صحابي، شهد الخندق وما بعدها، واستعمله النبي -صلى الله عليه وسلم- على نجران وهو ابن سبع عشرة سنة ليفقههم في الدين، وكتب له عهدًا مطولًا فيه توجيه وتشريع، توفي بالمدينة سنة ٥٣ هـ.
الإصابة ٢/ ٥٣٢، الاستيعاب ٣/ ٥١٧، أسد الغابة ٤/ ٢١٤، تهذيب الكمال ٢١/ ٥٨٥، الكامل لابن الأثير ٣/ ١٩٦.','3','30',NULL,NULL,'0');
INSERT INTO [b46] VALUES (706,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

يعاد إلى أول فرائض الإبل، فما كان أقل من خمس وعشرين ففيه الغنم، في كل خمس ذود شاة" رواه أبو داود، والطحاوي (¬١)، وقال أبو الفرج (¬٢) (¬٣): قال أحمد بن حنبل: حديث ابن حزم في الصدقات: صحيح.
وما تمسك به الشافعي، بما روي في البخاري: "فإذا زادت على عشرين ومائة، ففي كل أربعين بنت لبون، وفي كل خمسين حقة" (¬٤).
فجوابه: أنا نعمل به أيضًا، ألا ترى أن في تسعين ومائة تجب ثلاث حقاق وبنت لبونٍ، وكذا في المائتين أربع حقاق عندنا، فيحمل حديث
_________
(¬١) أبو داود في المراسيل ص ١٢٨ باب ما جاء في صدقة السائمة في الزكاة رقم ١٠٦، والطحاوي في شرح معاني الآثار ٤/ ٣٧٥، باب فرض الزكاة في الإبل السائمة، ورواه أيضًا عبد الرزاق ٤/ ٤٤ كتاب الزكاة، باب الصدقات رقم ٦٧٩٣، والدارمي ١/ ٤٠٨ كتاب الزكاة، باب في زكاة الغنم رقم ١٥٨١، وابن حبان ١٤/ ٥٠١ كتاب التاريخ، باب كتب النبي رقم ٦٥٥٩، والدارقطني ٢/ ١١٧ كتاب الزكاة، باب زكاة الإبل والغنم رقم ٥، والحاكم ١/ ٣٩٥ كتاب الزكاة، والبيهقي ٤/ ٨٩ كتاب الزكاة، باب كيف فرض الصدقة، قال الحاكم: إسناده صحيح، وهو من قواعد الإسلام.
قال في تنقيح التحقيق ١/ ٤١٢: قال يعقوب بن سفيان: لا أعلم في الكتب المنقولة أصح من كتاب عمرو بن حزم فإن الصحابة والتابعين يرجعون إليه ويدعون رأيهم.
(¬٢) في كتابه التحقيق في أحاديث التعليق ٢/ ١٣٦١.
(¬٣) هو أبو الفرج عبد الرحمن بن علي بن محمد الجوزي القرشي البغدادي، علامة عصره في التاريخ والحديث، كثير التصانيف، ولد ببغداد سنة ٥١٠ هـ، وطلب العلم صغيرًا واشتهر بالوعظ، فقيه، لغوي، مفسر، توفي ببغداد سنة ٥٩٧ هـ من مصنفاته: تلبيس إبليس، زاد المسير، المنتظم في تاريخ الأمم والملوك.
وفيات الأعيان ١/ ٢٧٩، البداية والنهاية ١٣/ ٢٨، مفتاح السعادة ١/ ٢٠٧، الأعلام ٣/ ٣١٦.
(¬٤) سبق تخريجه في ٣/ ٢٩.','3','31',NULL,NULL,'0');
INSERT INTO [b46] VALUES (707,'والبخت والعراب سواء.
و<span data-type="title" id=toc-364>نصاب البقر:</span> ثلاثون، وفيه تبيع إلى أربعين،
<hr><span data-type="title">منحة السلوك</span>

الخصم عليه؛ لأن ظاهره يدل على زيادة فيها أربعون، وفيها خمسون، ولكن تخلل الغنم بحديث: عمرو بن حزم (¬١).
قوله: والبخت، والعراب سواء؛ لأن اسم الإبل يتناولهما (¬٢).
والبخت: جمع بختي، وهو منسوب إلى بخت نصر (¬٣).
والعراب: جمع عربيّ (¬٤)، والأناسي عرب.

[نصاب البقر]
قوله: ونصاب البقر: ثلاثون، وفيه تبيع (¬٥) إلى أربعين،
_________
(¬١) تبيين الحقائق ١/ ٢٦١، العناية ٢/ ١٧٧، الهداية ١/ ١٠٦، شرح فتح القدير ٢/ ١٧٧، البحر الرائق ٢/ ٢١٤، حاشية الشلبي ١/ ٢٦١، كشف الحقائق ١/ ٩٩.
(¬٢) وفاقًا للمالكية، والشافعية، والحنابلة.
الهداية ١/ ١٠٦، تحفة الفقهاء ١/ ٢٨٦، كشف الحقائق ١/ ١٠٠، الكتاب ١/ ١٤٠، شرح الوقاية ١/ ١٠٠، الحجة على أهل المدينة ١/ ٤٨١، بداية المبتدي ١/ ١٠٦، كنز الدقائق ١/ ٢٦١، جواهر الإكليل ١/ ١٢٠، منح الجليل ٢/ ١٢، قليوبي على شرح المحلي على المنهاج ٢/ ٨، عميرة ٢/ ٨، كشاف القناع ٢/ ٢٨٣، الإفصاح ١/ ١٩٨.
(¬٣) وهي نوع من الإبل وهي التي لها سنامان، وهي الإبل الخراسانية.
الحيوان ١/ ١٦٧، لغة الفقهاء ص ١٠٤، المصباح المنير ١/ ٣٧، لسان العرب ٢/ ٩ مادة بخت.
(¬٤) هي الإبل التي لها سنام واحد، وهي الإبل العربية.
لسان العرب ١/ ٥٩٠ مادة عرب، لغة الفقه ص ١٠٦، المطلع على أبواب المقنع ص ١٢٥، أساس البلاغة "ع ر ب"، المحيط في اللغة "ع ر ب"، القاموس المحيط "ع ر ب".
(¬٥) التبيع: الذي أتى عليه حول من أولاد البقر، والأنثى تبيعة. وقيل: هو المفطوم من أمه، فهو تبيعها، ويقوى على ذلك.
القاموس المحيط ١/ ٣٥٧ مادة ت ب ع، مختار الصحاح ص ٣١ مادة ت ب ع، الدر النقي ٢/ ٣٢٣، المطلع ص ١٢٥، الحيوان ١/ ٢٣٥.','3','32',NULL,NULL,'0');
INSERT INTO [b46] VALUES (708,'ثم مُسنَّة،
<hr><span data-type="title">منحة السلوك</span>

ثم مسنة (¬١) (¬٢).
لما روي عن معاذ بن جبل -رضي الله عنه-: "أنه -صلى الله عليه وسلم- بعثه إلى اليمن، وأمره أن يأخذ من كل ثلاثين بقرة تبيعًا، أو تبيعةً، ومن كل أربعين مسنة" رواه الترمذي (¬٣).
_________
(¬١) وفاقًا للثلاثة.
الأصل ١/ ٥٥، رؤوس المسائل ص ٢٠٢، الكتاب ١/ ١٤١، بداية المبتدي ١/ ١٠٦، ملتقى الأبحر ١/ ١٧٤، كنز الدقائق ١/ ٢٦١، كشف الحقائق ١/ ١٠٠، متن الرسالة ص ٨١، مختصر خليل ص ٥٩، التذكرة ص ٧٠، متن الغاية والتقريب ص ٨٥، العمدة ص ٢٦، الإقناع لابن المنذر ١/ ١٧٠.
(¬٢) وهي التي صارت ثنية. ويجذع البقر في السنة الثانية، ويثنى في الثالثة، فهو ثني، والأنثى ثنية.
الدر النقي ٢/ ٣٢٤، المطلع ص ١٢٥، لغة الفقه ص ٩٩، معجم لغة الفقهاء ص ٤٢٨.
(¬٣) ٣/ ٢٠٤ كتاب الزكاة، باب ما جاء في زكاة البقر رقم ٦٢٣، ورواه أيضًا مالك ١/ ٢٥٩ كتاب الزكاة، باب ما جاء في صدقة البقر رقم ٢٤، والشافعي في الأم ٢/ ٧، وفي مسند الشافعي ٦/ ١٢٢، وعبد الرزاق ٦٨٤١/ ٤/ ٢١ في الزكاة باب البقر، وابن أبي شيبة ٣/ ١٢٧، وأحمد ٥/ ٢٣٠، والدارمي ١/ ٤٠٨ كتاب في الزكاة، باب زكاة البقر رقم ١٥٨٢، وابن ماجه ١٨٠٣ في زكاة البقر، باب صدقة البقر ١/ ٥٧٦، وأبو داود ٢/ ١٠١ في كتاب الزكاة باب في زكاة السائمة رقم ١٥٧٦، والنسائي ٥/ ٢٥ في كتاب الزكاة، باب زكاة البقر رقم ٢٤٥٠، وابن الجارود رقم ٣٤٣ كتاب الزكاة ص ٩٥، وابن خزيمة ٢٢٦٨ كتاب الزكاة، باب صدقة البقر ٤/ ١٩، وابن حبان كما في الموارد رقم ٧٩٤ ص ٢٠٣ في كتاب الزكاة، باب فرض الزكاة وما تجب فيه، والدارقطني ٢/ ١٠٢ في الزكاة، باب ليس في الخضروات صدقة رقم ٢٩، والبيهقي ٤/ ٩٨ في الزكاة، باب كيف فرض صدقة البقر، والبغوي في الزكاة، باب صدقة البقر السائمة ٦/ ١٩ رقم ١٥٧١، وابن زنجويه ١/ ١٢٥ في كتاب الفيء ووجوهه وسبيله، باب أخذ الجزية من عرب أهل الكتاب، وابن المنذر في الإقناع ١/ ١٦٩ رقم ٤٨ كتاب الزكاة، باب صفة صدقة البقر، والحاكم ١/ ٣٩٨ كتاب الزكاة وقال الحاكم ١/ ٣٩٨: صحيح على شرط الشيخين ولم يخرجاه، وسكت عنه =','3','33',NULL,NULL,'0');
INSERT INTO [b46] VALUES (709,'وما زاد بحسابه إلى ستين،
<hr><span data-type="title">منحة السلوك</span>

قوله: وما زاد بحسابه.
أي: وما زاد على الأربعين يعتبر بحسابه (¬١)، مثلًا في الواحدة الزائدة ربع عشر مسنة (¬٢)، أو ثلث عشر التبيع، وفي الثنتين نصف عشر مسنة، أو ثلثا عشر تبيع، وفي الثلاثة ثلاثة أرباع عشر مسنةٍ، أو عشر تبيعٍ، وهذا عند أبي حنيفة في رواية الأصل (¬٣).
وفي رواية الحسن عنه: أنه لا يجب في الزيادة شيء، إلى أن يبلغ خمسين، ثم فيها مسنة وربع مسنةٍ، أو ثلث تبيع (¬٤).
وقالا: لا شيء في الزيادة، حتى يبلغ ستين، وهو رواية عن أبي حنيفة (¬٥).
_________
= الذهبي، ونقل الحافظ في التلخيص ٨١٤ عن ابن عبد البر قوله: إسناده متصل، صحيح ثابت.
(¬١) الأصل ١/ ٥٥، الكتاب ١/ ١٤١، الهداية ١/ ١٠٦، العناية ٢/ ١٧٩، الوقاية ١/ ١٠٠، كنز الدقائق ١/ ٢٦٢، بداية المبتدي ١/ ١٠٦، كشف الحقائق ١/ ١٠٠، ملتقى الأبحر ١/ ١٧٤.
(¬٢) في ق، م "ربع العشر من المسنة".
(¬٣) كتاب الأصل ١/ ٥٥.
(¬٤) الهداية ١/ ١٠٦، بدائع الصنائع ٢/ ٢٨، تبيين الحقائق ١/ ٢٦٢، البحر الرائق ٢/ ٢١٥، حاشية الشلبي ١/ ٢٦٢، العناية ٢/ ١٧٩.
(¬٥) رواها أسد بن عمرو عنه، وهو قول: أبي يوسف، ومحمد، وهو مذهب المالكية، والشافعية، والحنابلة.
البحر الرائق ٢/ ٢١٥، حاشية الشلبي ١/ ٢٦٢، الهداية ١/ ١٠٧، تبيين الحقائق ١/ ٢٦٢، العناية ٢/ ١٧٩، الأصل ١/ ٥٦، الكتاب ١/ ١٤١، بدائع الصنائع ٢/ ٢٨، متن الرسالة ص ٨١، الكافي لابن عبد البر ص ١٠٦، القوانين ص ٧٣، مختصر خليل ص ٥٩، متن الغاية والتقريب ص ٨٥، التذكرة ص ٧٠، مغني المحتاج ١/ ٣٧٤، المهذب ١/ ١٤٨، المحرر ١/ ٢١٤، الإفصاح ١/ ١٩٩، زاد المستقنع ص ١٥٤، العمدة ص ٢٦.','3','34',NULL,NULL,'0');
INSERT INTO [b46] VALUES (710,'ثم تبيعان إلى سبعين، ثم مسنة وتبيع إلى ثمانين، ثم مسنتان إلى تسعين، ثم ثلاثة أتبعة إلى مائة، ثم تبيعان ومسنة، وهكذا أبدًا كما ذكرنا.
والذكر والأنثى سواء، والجواميس والبقر سواء.
<hr><span data-type="title">منحة السلوك</span>

قوله: ثم تبيعان.
أي: في ستين تبيعان، إلى سبعين ففيها مسنة وتبيع، إلى ثمانين ففيها مسنتان، إلى تسعين ففيها ثلاثة أتبعةٍ، إلى مائة ففيها تبيعان ومسنة (¬١).
قوله: وهكذا أبدًا.
أي: وهكذا يتغير الفرض من التبيع إلى المسنة، ومن المسنة إلى التبيع (¬٢). فليتدبر فإنه ظاهر على الفطن الذكي.
قوله: والجواميس (¬٣)، والبقر سواء (¬٤).
لأنها نوع منه، فيتناولهما النصوص الواردة باسم البقر (¬٥)، بخلاف ما
_________
(¬١) بداية المبتدي ١/ ١٠٧، كنز الدقائق ١/ ٢٦٢، الكتاب ١/ ١٤٢، المختار ١/ ١٠٧، بدائع الصنائع ٢/ ٢٨، الاختيار ١/ ١٠٧، الهداية ١/ ١٠٧.
(¬٢) الكتاب ١/ ١٤٢، المختار ١/ ١٠٧، بداية المبتدي ١/ ١٠٧، الاختيار ١/ ١٠٧، بدائع الصنائع ٢/ ٢٨، الهداية ١/ ١٠٧، تبيين الحقائق ١/ ٢٦٢، كشف الحقائق ١/ ١٠٠.
(¬٣) الجواميس: واحدها جاموس، وهو نوع من البقر، وهي أنبل البقر، وأكثرها ألبانًا، وأعظمها أجسامًا.
حياة الحيوان للدميري ١/ ٢٦٤، الدر النقي ٢/ ٣٢٤، المطلع ١٢٦، لغة الفقه ص ١٠٦.
(¬٤) وفاقًا للمالكية، والشافعية، والحنابلة.
تبيين الحقائق ١/ ٢٦٣، الكتاب ١/ ١٤٢، الهداية ١/ ١٠٦، الحجة على أهل المدينة ١/ ٤٨١، كنز الدقائق ١/ ٢٦٣، ملتقى الأبحر ١/ ١٧٤، كشف الحقائق ١/ ١٠٠، جواهر الإكليل ١/ ١٢٠، منح الجليل ٢/ ١٢، حاشية البيجوري على ابن قاسم ١/ ٢٧٩، حاشية الجمل على شرح المنهاج ٢/ ٢٢٢، مختصر الخرقي ص ٤٤، الكافي لابن قدامة ١/ ٢٩٢.
(¬٥) الحجة على أهل المدينة ١/ ٤٨٢، تبيين الحقائق ١/ ٢٦٣.','3','35',NULL,NULL,'0');
INSERT INTO [b46] VALUES (711,'و<span data-type="title" id=toc-365>نصاب الغنم:</span> أربعون، وفيه شاة إلى مائة وإحدى وعشرين، ثم شاتان إلى مائتين وواحدة، ثم ثلاث شياه إلى أربعمائة، أربع شياه، في كل مائة شاة. والضأن والمعز سواء.
<hr><span data-type="title">منحة السلوك</span>

إذا حلف لا يأكل لحم البقر، حيث لا يحنث بأكل لحم الجاموس؛ لأن مبنى الأيمان على العرف، وفي العادة أوهام الناس لا تسبق إليه (¬١).

[نصاب الغنم]
قوله: ونصاب الغنم أربعون (¬٢) إلى آخره.
لما روى البخاري في صحيحه، في كتاب أبي بكر لأنس "في صدقة الغنم في سائمتها إذا كانت أربعين، إلى عشرين ومائة شاة، فإذا زادت على عشرين ومائة إلى مائتين شاتان، فإذا زادت على مائتين إلى ثلاثمائة ففيها ثلاث شياه، فإذا زادت على ثلاثمائة ففي كل مائة شاة" (¬٣).
قوله: والضأن (¬٤)، والمعز (¬٥) سواء.
_________
(¬١) تبيين الحقائق ١/ ٢٦٣، الهداية ١/ ١٠٧، البحر الرائق ٢/ ٢١٥.
(¬٢) وفاقًا للثلاثة.
الاختيار ١/ ١٠٨، بداية المبتدي ١/ ١٠٧، كنز الدقائق ١/ ٢٦٣، تنوير الأبصار ٢/ ٢٨١، الدر المختار ٢/ ٢٨١، الكتاب ١/ ١٤٢، الأصل ١/ ٣٤، المختار ١/ ١٠٨، القوانين الفقهية ص ٧٣، متن الرسالة ص ٨١، التذكرة ص ٧٠، هداية الغلام ص ٦١، المحرر ١/ ٢١٥، الروض المربع ص ١٥٤.
(¬٣) سبق تخريجه ٣/ ٢٨.
(¬٤) الضأن مهموز، ويجوز تخفيفه بالإسكان كنظائره، وهو جمع واحدُه: ضائن، كراكب ورَكْب، ويقال في الجمع أيضًا: ضأن -بفتح الهمزة- ويجمع أيضًا على ضئين، وهو فعيل بفتح أوله، والأنثى ضائنة بهمزة بعد الألف، ثم نون وجمعها: ضوائن.
المصباح المنير ٢/ ٣٦٥ مادة الضأن، لسان العرب ١٣/ ٢٥١ مادة ضأن، لغة الفقه ص ١٠٣، المطلع ص ١٤٦، الدر النقي ١/ ٣٢٧.
(¬٥) المعز: اسم جنس لا واحد له من لفظه، وهي ذوات الشعر من الغنم، الواحدة شاة، والذكر ماعز، والأنثى ماعِزة. =','3','36',NULL,NULL,'0');
INSERT INTO [b46] VALUES (712,'ويؤخذ الثني منهما، ولا يؤخذ الجذع.
<hr><span data-type="title">منحة السلوك</span>

لأن النص ورد باسم الشاة والغنم، وهو شامل لهما، فكانا جنسًا واحدًا، فيكمل نصاب أحدهما بالآخر (¬١).
قوله: ويؤخذ الثني منهما.
أي: من الضأن والمعز (¬٢)، وهو ما تمت له سنة (¬٣)، ولا يؤخذ الجذع وهو ما أتى عليه أكثرها (¬٤).
وروى الحسن، عن أبي حنيفة: أنه يجوز الجذع من الضأن، وهو قولهما (¬٥) وقول: الشافعي (¬٦).
_________
= لسان العرب ٥/ ٤١٠ مادة معز، لغة الفقه ص ١٠٣، المصباح المنير ٢/ ٥٧٥ مادة المعز، مختار الصحاح ص ٢٦٢ مادة م ع ز.
(¬١) وإليه ذهب المالكية، والشافعية، والحنابلة.
الكتاب ١/ ١٤٣، ملتقى الأبحر ١/ ١٧٥، البحر الرائق ٢/ ٢١٦، كنز الدقائق ١/ ٢٦٣، الحجة ١/ ٤٨٢، الهداية ١/ ١٠٧، تبيين الحقائق ١/ ٢٦٣، كشف الحقائق ١/ ١٠١، متن الرسالة ص ٨١، التاج والإكليل ٢/ ٢٦٢، متن أبي شجاع ص ٨٥، كفاية الأخيار ١/ ١١١، الكافي لابن قدامة ١/ ٢٩٢، شرح منتهى الإرادات ١/ ٣٧٩.
(¬٢) كنز الدقائق ١/ ٢٦٣، المختار ١/ ١٠٨، الهداية ١/ ١٠٧، تبيين الحقائق ١/ ٢٦٣، تحفة الفقهاء ١/ ٢٨٦، ملتقى الأبحر ١/ ١٧٥، شرح فتح القدير ٢/ ١٨٢، العناية ٢/ ١٨٢، الاختيار ١/ ١٠٨.
(¬٣) المختار ١/ ١٠٨، تحفة الفقهاء ١/ ٢٨٦، الهداية ١/ ١٠٨، تبيين الحقائق ١/ ٢٦٣، العناية ٢/ ١٨٢.
(¬٤) وهو ظاهر الرواية، أن الجذع لا تؤخذ من الضأن، والمعز.
كنز الدقائق ١/ ٢٦٣، تحفة الفقهاء ١/ ٢٨٧، العناية ١/ ١٨٢، البحر الرائق ٢/ ٢١٦.
(¬٥) المبسوط ٢/ ١٨٢، تحفة الفقهاء ١/ ١٨٧، الهداية ١/ ١٠٧، العناية ٢/ ١٨٢، البحر الرائق ٢/ ٢١٦، كشف الحقائق ١/ ١٠١.
(¬٦) وأحمد. =','3','37',NULL,NULL,'0');
INSERT INTO [b46] VALUES (713,'وما ينتج بين ظبي وشاة، أو بقرة وحشية وأهلية، يعتبر أمه.
و<span data-type="title" id=toc-367>نصاب الخيل:</span> اثنان ذكر وأنثى،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-366>[النتاج من أصلين مختلفين]</span>
قوله: وما ينتج بين ظبي (¬١) وشاة، أو بقرةٍ وحشية وأهلية، يعتبر أمه (¬٢).
حتى إذا نزى ظبي على شاةٍ، أو بقرة وحشية على أهلية، فولدت شاة وبقرة تلحق بأمها حتى يجوز التضحية بها، ويكمل بها النصاب ونحوهما، وبالعكس لا (¬٣).

[نصاب الخيل]
قوله: ونصاب الخيل: اثنان ذكر وأنثى.
هذا التقدير على قول: أبي حنيفة (¬٤)، وقيل: ثلاثة، وعن الطحاوي:
_________
= وذهب المالكية: إلى أنه يؤخذ الثني من الضأن، ذكرًا أو أنثى، ولا يؤخذ الثني من المعز إلا الأنثى، ويؤخذ الجذع.
مواهب الجليل ٢/ ٢٦٢، التاج والإكليل ٢/ ٢٦٢، الإقناع في حل ألفاظ أبي شجاع ١/ ٢٠١، المحرر ١/ ٢١٥، التسهيل ص ٨٤، الإفصاح ١/ ٢٠٣.
(¬١) الظباء: جمع ظبي، والأنثى ظبيه بالهاء، وجمع الظبي في القلة أظب، وفي الكثرة ظبي وظبيُّ. وقيل: هي الغزلان.
مختار الصحاح ص ١٧٠ مادة ظ ب ي، الحيوان ٢/ ١٤٠، الدر النقي ٣/ ٧٧٣، المطلع ٢٨٣.
(¬٢) المبسوط ٢/ ١٨٣.
(¬٣) وذهب المالكية، والشافعية: إلى أن الزكاة لا تجب في النعم المتولدة من النعم الإنسية ومن الوحشية، أو عكسه.
وذهب الحنابلة: إلى أن الزكاة تجب في المستولدة بين الوحشية والأهلية، سواء كانت الوحشية الفحول، أم الأمهات.
المبسوط ٢/ ١٨٣، جواهر الإكليل ١/ ١١٨، منح الجليل ٢/ ٥، تحفة المحتاج ٣/ ٢١٠، حاشية الشرواني على تحفة المحتاج ٣/ ٢١٠، قليوبي ٢/ ٩، المغني ٢/ ٤٦، الإفصاح ١/ ٢٠٢.
(¬٤) المختار ١/ ١٠٨، الكتاب ١/ ١٤٣.','3','38',NULL,NULL,'0');
INSERT INTO [b46] VALUES (714,'وفيه ديناران، أو زكاة الغنيمة.
<hr><span data-type="title">منحة السلوك</span>

خمس (¬١). والأصح أن لا تقدير؛ لعدم النقل به (¬٢).
قوله: وفيه ديناران.
أي: أن الواجب في نصاب الخيل، وهو اثنان ذكر وأنثى، ديناران، يعطي عن كل فرس دينارًا (¬٣)، أو يقومها ويعطي عن كل مائتي درهم خمسة دراهم (¬٤).
وقال أبو يوسف، ومحمد: لا زكاة في الخيل أصلًا، إذا لم تكن للتجارة (¬٥)، لما روي عن النبي -صلى الله عليه وسلم- أنه قال: "ليس على المسلم صدقة في عبيده، ولا في فرسه" رواه البخاري، ومسلم، وابن ماجه، وغيرهم (¬٦).
_________
(¬١) تبيين الحقائق ١/ ٢٦٦.
(¬٢) تبيين الحقائق ١/ ٢٦٦.
(¬٣) المختار ١/ ١٠٨، الدر المختار ٢/ ٢٨٢، حاشية رد المحتار ٢/ ٢٨٢، تنوير الأبصار ٢/ ٢٨٢، بداية المبتدي ١/ ١٠٨، الهداية ١/ ١٠٨، البحر الرائق ٢/ ٢١٧.
(¬٤) وهذا عند أبي حنيفة، وهو قول: زفر.
المختار ١/ ١٠٨، بداية المبتدي ١/ ١٠٨، الهداية ١/ ١٠٨، البحر الرائق ٢/ ٢١٧، الدر المختار ٢/ ٢٨٢، حاشية رد المحتار ٢/ ٢٨٢، كشف الحقائق ١/ ١٠١.
(¬٥) وإليه ذهب المالكية، والشافعية، والحنابلة.
الهداية ١/ ١٠٨، مسائل في الخلاف للمروزي (مخطوط) لوحة ٤٣/ أالنسخة الأصلية لدى مكتبة جامعة القرويين بالرباط، تحت رقم ٤٨٩، البحر الرائق ٢/ ٢١٦، بدائع الصنائع ٢/ ٣٤، حاشية الشلبي ١/ ٢٦٥، تبيين الحقائق ١/ ٢٦٥، القوانين ص ٦٨، التلقين ص ٤٦، المنهاج ١/ ٤٢٥، زاد المحتاج ١/ ٤٢٦، الفروع ٢/ ٣٧٨، الإقناع لابن المنذر ١/ ١٦١.
(¬٦) البخاري ٢/ ٥٣٢ كتاب الزكاة، باب ليس على المسلم في عبده صدقة رقم ١٣٩٥، ومسلم ٢/ ٦٧٦ كتاب الزكاة، باب لا زكاة على المسلم في عبده وفرسه رقم ٩٨٢، وابن ماجه ١/ ٥٨٩ كتاب الزكاة باب صدقة الخيل والرقيق ١٨١٢، ورواه أيضًا مالك ١/ ٢٧٧ كتاب الزكاة، باب ما جاء في صدقة الرقيق والخيل والعسل رقم ٣٧، والشافعي في المسند =','3','39',NULL,NULL,'0');
INSERT INTO [b46] VALUES (715,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

ولأبي حنيفة ما روى جابر -رضي الله عنه-، عن النبي -صلى الله عليه وسلم- أنه قال: "في كل فرسٍ سائمةٍ دينار، وليس في الرابطة (¬١) شيء" رواه الدارقطني (¬٢)، وأبو بكر
_________
= ص ٩١، وعبد الرزاق في المصنف ٤/ ٣٣ كتاب الزكاة، باب الخيل رقم ٦٨٧٨، والحميدي ٢/ ٤٦٠ رقم ١٠٧٣، وابن أبي شيبة ٢/ ٣٨٠ كتاب الزكاة، باب ما قالوا في زكاة الخيل رقم ١٠١٣٨، وأحمد ٢/ ٢٤٩، وأبو داود ٢/ ١٠٨ كتاب الزكاة، باب صدقة الرقيق رقم ١٥٩٥، والترمذي ٢/ ٢٠٦ كتاب الزكاة، باب ما جاء ليس في الخيل والرقيق صدقة ٦٢٨، والنسائي ٥/ ٣٥ كتاب الزكاة، باب زكاة الخيل رقم ٢٤٦٧، وابن حبان ٨/ ٦٥ كتاب الزكاة، باب ذكر نفي إيجاب الصدقة على المرء في رقيقه ودوابه رقم ٣٢٧١، والدارقطني ٢/ ١٢٧ كتاب الزكاة، باب مال التجارة وسقوطها عن الخيل والرقيق رقم ٥، والبيهقي في السنن ٤/ ١١٧ كتاب الزكاة، باب لا صدقة في الخيل، والبغوي في شرح السنة ٦/ ٢٢ رقم ١٥٧٣، والطيالسي ص ٣٣٠ رقم ٢٥٢٧ والدارمي ١/ ٤١١ كتاب الزكاة، باب ما لا تجب فيه الصدقة من الحيوان رقم ١٥٨٩، وابن حزم في المحلى ٦/ ١٣٣، والطحاوي في شرح معاني الآثار ٢/ ٢٩ كتاب الزكاة، باب الخيل السائمة هل فيها صدقة أم لا؟، وأبو يعلى في المسند ١٠/ ٥٢٢ رقم ٦١٣٨.
(¬١) الرابطة من الخيل: الملازمة لثغر العدو، وأصله أن يربط كل واحد من الفريقين خيله. والرباط في الخيل: الخمسة فما فوقها.
لسان العرب ٧/ ٣٠٢، مختار الصحاح ص ٩٧ مادة ر ب ط، القاموس المحيط ٢/ ٢٨٩ مادة ر ب ط، المغرب ص ١٨١ مادة ربط.
(¬٢) في سننه ٢/ ١٥٢ كتاب الزكاة باب زكاة مال التجارة وسقوطها عن الخيل والرقيق رقم ١. ونصه: وقد روى أبو يوسف عن غورك السَّعْدي، عن جعفر بن محمد، عن أبيه، عن جابر -رضي الله عنه- قال: قال رسول الله -صلى الله عليه وسلم- ... ". ورواه أيضًا البيهقي في السنن الكبرى ٤/ ١١٩ كتاب الزكاة، باب من رأى في الخيل صدقة، وفي معرفة السنن والآثار ٦/ ٩٥ كتاب الزكاة، باب لا صدقة في الخيل رقم ٨١١٩.
قال في مجمع الزوائد ٣/ ٦٩: فيه ليث بن حماد وعراك وكلاهما ضعيف.
وقال الدارقطني في السنن ٢/ ١٢٥: تفرد به غورك، عن جعفر وهو ضعيف جدًّا ومن دونه ضعفاء.','3','40',NULL,NULL,'0');
INSERT INTO [b46] VALUES (716,'ولا يجب شيء في ذكور، أو إناث محضة في الأشهر،
<hr><span data-type="title">منحة السلوك</span>

الرازي، في شرحه لمختصر الطحاوي (¬١).
والجواب عما روى البخاري: أن المراد من الفرس فرس الغازي (¬٢).
قوله: ولا يجب شيء في ذكورٍ، أو إناث محضة في الأشهر.
يعني: إذا كانت الخيل ذكورًا محضة، فالمشهور عن أبي حنيفة: أنه لا يجب فيها شيء (¬٣).
وروي عنه: أنه يجب فيها أيضًا (¬٤).
وإن كانت إناثًا محضةً فكذلك فيه: روايتان (¬٥).
_________
(¬١) جـ ١ لوحة ١٨٩/ أ (مخطوط) النسخة الأصلية "متحف الآثار العتيقة بقونية بتركيا، تحت رقم ٣٢٧/ ٤٩٧٩ - ٤.
(¬٢) تبيين الحقائق ١/ ٢٦٥، العناية ٢/ ١٨٣، حاشية الشلبي ١/ ٢٦٥ البحر الرائق ٢/ ٢١٧، الهداية ١/ ١٠٨ شرح فتح القدير ٢/ ١٨٦.
(¬٣) لعدم النماء.
بداية المبتدي ١/ ١٠٨، تحفة الفقهاء ١/ ٢٩٠، شرح فتح القدير ٢/ ١٨٦، شرح الوقاية ١/ ١٠١، الهداية ١/ ١٠٨، كشف الحقائق ١/ ١٠١ تبيين الحقائق ٢١/ ٢٦٦، حاشية الشلبي ٢/ ٢٦٦ البحر الرائق ٢/ ٢١٧.
(¬٤) بداية المبتدي ١/ ١٠٨، تحفة الفقهاء ١/ ٢٩٠، شرح فتح القدير ٢/ ١٨٦، شرح الوقاية ١/ ١٠١، الهداية ١/ ١٠٨، كشف الحقائق ١/ ١٠١، البحر الرائق ٢/ ٢١٧، حاشية رد المحتار ٢/ ٢٨٢.
(¬٥) في رواية: يجب في الإناث المحضة زكاة؛ لأنها تتناسل بالفحل المستعار.
والرواية الثانية: عدم الوجوب؛ لأنها غير معدة للنماء. والأشهر: الرواية الأولى.
بداية المبتدي ١/ ١٠٨، تحفة الفقهاء ١/ ٢٩٠، البحر الرائق ٢/ ٢١٧، تبيين الحقائق ٢/ ٢٦٥، حاشية الشلبي ٢/ ٢٦٥، حاشية رد المحتار ٢/ ٢٨٢ كشف الحقائق ١/ ١٠١، شرح الوقاية ١/ ١٠١، شرح فتح القدير ٢/ ١٨٦.','3','41',NULL,NULL,'0');
INSERT INTO [b46] VALUES (717,'ولا في البغال والحمير،
<hr><span data-type="title">منحة السلوك</span>

وإن كانت علوفةً، أو سائمةً للحمل والركوب، والجهاد فلا تجب الزكاة فيها (¬١).
وإن كانت للتجارة يجب فيها الزكاة (¬٢)، وسواء كانت تعلف في المصر، أو تسام في البراري. كذا في "التحفة" (¬٣).
قوله: ولا في البغال.
أي: ولا تجب الزكاة أيضًا في البغال، والحمير بالإجماع (¬٤)؛ لقوله -صلى الله عليه وسلم-: "ليس في الجبهة، ولا في الكسعة، ولا في النخة صدقة" (¬٥).
الجبهة: الخيل (¬٦). والكسعة: الحمير (¬٧).
_________
(¬١) بدائع الصنائع ١/ ٣٤، تحفة الفقهاء ١/ ٢٩٠، البحر الرائق ٢/ ٢١٧.
حاشية رد المحتار ٢/ ٢٨٢، الهداية ١/ ١٠٨.
(¬٢) الكتاب ١/ ١٤٤، بداية المبتدي ١/ ١٠٨، البحر الرائق ٢/ ٢١٧، حاشية رد المحتار ٢/ ٢٨٢، الهداية ١/ ١٠٨.
(¬٣) أي: تحفة الفقهاء لعلاء الدين السمرقندي ١/ ٢٩٠.
(¬٤) مراتب الإجماع ص ٣٧، قال ابن هبيرة في الإفصاح ١/ ٢٠١: واتفقوا على أنها -أي البغال والحمير- إذا لم تكن للتجارة، فلا زكاة فيها.
(¬٥) رواه البيهقي في السنن الكبرى ٤/ ١١٨ كتاب الزكاة باب لا صدقة في الخيل، من طريق سليمان بن أرقم عن الحسن عن عبد الرحمن بن سمرة -رضي الله عنه- أن رسول الله -صلى الله عليه وسلم- قال: "لا صدقة في الكسعة والجبهة والنخة". وأخرجه أيضًا أبو داود في المراسيل ص ١٣٢ باب في الزكاة، ما جاء في صدقة السائمة في الزكاة رقم ١١٤. قال البيهقي في السنن ٤/ ١١٨: إسناده ضعيف.
(¬٦) وهو: اسم يقع على الخيل الذي لا يفر.
لسان العرب ١٣/ ٤٨٣، مختار الصحاح ص ٣٩ مادة ج ب هـ، القاموس المحيط ١/ ٤٤٢ مادة ج ب هـ، المغرب ص ٧٤ مادة الجبهة.
(¬٧) لسان العرب ٨/ ٣٠٩ مادة كسع، تاج العروس ٥/ ٤٩٤ مادة كسع، غريب الحديث لأبي =','3','42',NULL,NULL,'0');
INSERT INTO [b46] VALUES (718,'ولا في الصغار إلا تبعًا للكبيرة.
<hr><span data-type="title">منحة السلوك</span>

والنخة: البقر العوامل (¬١).
قوله: ولا في الصغار إلا تبعًا للكبيرة (¬٢).
صورته: إذا اشترى أربعين من الحملان (¬٣)، أو ثلاثين من
_________
= عبيد ١/ ٧ مادة كسع، غريب الحديث لابن الجوزي ٢/ ٢٩٠ مادة كسع.
(¬١) لسان العرب ٣/ ٥٩ مادة نخع، غريب الحديث لأبي عبيد ١/ ٧ مادة نخة، غريب الحديث لابن الجوزي ٢/ ٢٩٧ مادة نخة، طلبة الطلبة ص ٤٣.
(¬٢) هذا عند أبي حنيفة، وهو آخر أقواله، وهو قول: محمد، وكان أبو حنيفة يقول أولًا: يجب فيها ما يجب في المسان، وهو قول: زفر ثم رجع، وقال -أي أبو حنيفة-: فيها واحدة منها، وبها أخذ أبو يوسف.
وجه القول الأول: أن المقادير لا يدخلها القياس، فإذا امتنع إيجاب ما ورد به الشرع، امتنع أصلًا، وإذا كان فيها واحد من المسان، جعل الكل تبعًا له في انعقادها نصابًا دون تأدية الزكاة، والشارع أوجب قليلًا في كثير، وهو أسنان معلومة، فلو أوجبنا الكبار فيها أدى إلى قلب الموضوع، فإنه إيجاب الكثير في القليل، وربما يزيد على جميعها، وإذا كان فيها كبار صارت الصغار تبعًا لها في انعقاد النصاب، لا في جواز الأخذ.
ووجه القول الثاني: أن الشارع أوجب اسم الإبل والبقر والغنم، فيتناول الصغار والكبار، كما في الأيمان، حتى لو حلف لا يأكل الإبل يحنث بأكل الفصيل، ولهذا يعد مع الكبار، لتكميل النصاب، ولولا أنها نصاب واحد لما كمل بها.
ووجه القول الثالث: أنا لو أوجبنا فيها ما يجب في المسان؛ لأضررنا بأربابها، ولو لم نوجب أصلًا؛ لأضررنا بالفقراء فأوجبنا واحدة منها، كما في المهازيل، وهذا لأن الكبر والصغر وصف، ففواته لا يوجب فوات الوجوب، كالسمن والهزال.
الهداية ١/ ١٠٨، الكتاب ١/ ١٤٤، الاختيار ١/ ١٠٩، تبيين الحقائق ١/ ٢٢٦، كنز الدقائق ١/ ٢٢٦، كشف الحقائق ١/ ١٠١، العناية ١/ ١٨٧، شرح فتح القدير ٢/ ١٨٧، حاشية الشلبي ١/ ٢٦٧، البحر الرائق ٢/ ٢١٧.
(¬٣) الحملان: الصغير من الضأن، ويجمع أيضًا على أحمال.
لسان العرب ١١/ ١٨٠ مادة حمل، القاموس المحيط ١/ ٧١٤ مادة ح م ل، مختار الصحاح ص ٦٥ مادة ح م ل.','3','43',NULL,NULL,'0');
INSERT INTO [b46] VALUES (719,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

العجاجيل (¬١)، أو خمسة وعشرين من الفصلان (¬٢)، فهل ينعقد الحول عليها؟.
فعندهما: لا ينعقد (¬٣)، وعند أبي يوسف: ينعقد (¬٤)، حتى لو حال الحول من حين ملكه يجب فيها الزكاة (¬٥)، وأما إذا كانت كبيرة معها ينعقد عليها الحول بالاتفاق، فتجب الزكاة عند تمام حولان الحول (¬٦)، ثم إذا أخذ الساعي منها يأخذ الصغيرة عندنا (¬٧).
_________
(¬١) جمع عجل، وهو الذي فطم عن أمه، فهو يتبعها. وقيل: ولد البقرة حين يوضع.
لسان العرب ١١/ ٤٢٩ مادة عجل، القاموس المحيط ٣/ ١٦٤، مادة ع ج ل، مختار الصحاح ص ١٧٥ مادة ع ج ل.
(¬٢) الفصيل: ولد الناقة إذا أفصل عن أمه فهو فصيل، ويجمع على فصال.
لسان العرب ١١/ ٥٢٢ مادة فصل، القاموس المحيط ٣/ ٤٩٧ مادة ف ص ل، مختار الصحاح ص ٢١١ مادة ف ص ل.
(¬٣) شرح قاضي خان على شرح الزيادات لمحمد بن الحسن (مخطوط) ق ١٤/ أالنسخة الأصلية لدى المكتبة الأحمدية بحلب تحت رقم ٥٣٣، العناية ٢/ ١٨٦، تبيين الحقائق ١/ ٢٦٦، فتح القدير ١/ ١٨٧، كشف الحقائق ١/ ١٠١، الاختيار ١/ ١٠١، الهداية ١/ ١٠٨، البحر الرائق ٢/ ٢١٧.
(¬٤) وإليه ذهب المالكية، الشافعية، والحنابلة.
العناية ٢/ ١٨٦، تبيين الحقائق ١/ ٢٦٦، شرح فتح القدير ٢/ ١٨٧، البحر الرائق ٢/ ٢١٧، الهداية ١/ ١٠٨، الكافي لابن عبد البر ص ١٠٦، مختصر خليل ص ٥٨، مغني المحتاج ١/ ٣٧٨، نهاية المحتاج ٣/ ٦٣، الإنصاف ٣/ ٣١، المغني ٢/ ٤٧٣، الإفصاح ١/ ٢٠٢، شرح منتهى الإرادات ١/ ٣٧١.
(¬٥) الكتاب ١/ ١٤٤، الهداية ١/ ١٠٨، المختار ١/ ١٠١، العناية ٢/ ١٨٦، شرح فتح القدير ٢/ ١٨٧، تبيين الحقائق ١/ ٢٦٦، البحر الرائق ٢/ ٢١٧.
(¬٦) تبيين الحقائق ١/ ٢٦٧، شرح فتح القدير ٢/ ١٨٧، الاختيار ١/ ١٠١، البحر الرائق ٢/ ٢١٧، الهداية ١/ ١٠٨، كشف الحقائق ١/ ١٠١، العناية ٢/ ١٨٦.
(¬٧) الهداية ١/ ١٠٨، شرح فتح القدير ٢/ ١٨٨، تبيين الحقائق ١/ ٢٦٧، حاشية الشلبي ١/ ٢٦٧.','3','44',NULL,NULL,'0');
INSERT INTO [b46] VALUES (720,'وليس في العلوفة، ولا في الحوامل، والعوامل السائمة، زكاة.
<hr><span data-type="title">منحة السلوك</span>

وقال زفر: يأخذ منها ما يأخذ في المسان (¬١).
قوله: وليس في العلوفة، ولا في الحوامل، والعوامل السائمة، زكاة (¬٢).
العلوفة -بفتح العين-: ما يعلف من الغنم، وغيرها. الواحد والجمع سواء (¬٣). والحوامل: جمع حاملةٍ (¬٤). والعوامل: جمع عاملةٍ (¬٥).
_________
(¬١) وإليه ذهب المالكية.
وعند الشافعي في القول القديم: لا تؤخذ إلا الكبيرة، لكن دون الكبيرة المأخوذة من الكبار في القيمة. وفي القول الجديد: يؤخذ في الصغار صغيرة.
وعند الحنابلة: يؤخذ من الصغار صغيرة في غنم دون إبل وبقر، فلا يجزيء إخراج فصلان وعجلان، فيقوم النصاب من الكبار، ويقوم فرضه الواجب فيه، ثم تقوم الصغار، ويؤخذ عنها كبيرة بالقسط، والتعديل بالقيمة، مكان زيادة السن.
تبيين الحقائق ١/ ٢٦٧، العناية ٢/ ١٨٧، الهداية ١/ ١٠٨، الاختيار ١/ ١٠٩، شرح فتح القدير ٢/ ١٨٧، كشف الحقائق ١/ ١٠١، البحر الرائق ٢/ ٢١٧، جواهر الإكليل ١/ ١١٩، القوانين ص ٧٣، مغني المحتاج ١/ ٣٧٦، شرح المحلي على المنهاج ٢/ ١٠، قليوبي ٢/ ١٠، الإقناع للحجاوي ٢/ ١٩٢، حاشية المقنع ١/ ٣٠٤.
(¬٢) بداية المبتدي ١/ ١٠٨، كنز الدقائق ١/ ٢٦٨، الكتاب ١/ ١٤٥، البحر الرائق ٢/ ٢١٨، كشف الحقائق ١/ ١٠٢، تبيين الحقائق ١/ ٢٦٨ المختار ١/ ١٠٩.
(¬٣) مختار الصحاح ص ١٨٩ مادة ع ل ف، القاموس المحيط ٣/ ٢٩٤ مادة ع ل ف، لسان العرب ٩/ ٢٥٦ مادة ع ل ف، المصباح المنير ٢/ ٤٢٥ مادة عَلَفْتُ، المطلع ص ١٨٤، طلبة الطلبة ص ٣٩.
(¬٤) وهي المُعدَّة لحمل الأثقال.
مختار الصحاح ص ٦٥ مادة ح م ل، لسان العرب ١١/ ١٧٩ مادة حمل، القاموس المحيط ١/ ٧١٢ مادة ح م ل، المصباح المنير ١/ ١٥٢ مادة الحميلُ، طلبة الطلبة ص ٣٩.
(¬٥) وهي المعدة للأعمال.
لسان العرب ١١/ ٤٧٧ مادة عمل، القاموس المحيط ٣/ ٣١٤ مادة ع م ل، معجم =','3','45',NULL,NULL,'0');
INSERT INTO [b46] VALUES (721,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وقال مالك: تجب فيها الزكاة (¬١).
ولنا: ما روي عن علي -رضي الله عنه- أن رسول الله -صلى الله عليه وسلم- قال: "ليس في العوامل صدقة" قال أبو الحسن القطان (¬٢): إسناده صحيح (¬٣)، وعن جابرٍ -رضي الله عنه- أنه -صلى الله عليه وسلم- قال: "ليس في المثيرة صدقة" رواه الدارقطني (¬٤).
_________
= مقاييس اللغة ٤/ ١٤٥ باب العين والميم وما يثلثهما مادة عمل، طلبة الطلبة ص ٣٩، لغة الفقهاء ص ٣٢٤.
(¬١) وذهب الشافعية، والحنابلة: إلى أنه ليس في العلوفة، ولا في العوامل زكاة. أما الحوامل: فتجب فيها الزكاة.
القوانين ص ٧٣، منح الجليل ٢/ ٤، أقرب المسالك ص ٣٦، التفريع ١/ ٢٨٩، منهج الطلاب ١/ ٢٣٢، فتح الوهاب ١/ ٢٣٢، روض الطالب ١/ ٣٤٢، أسنى المطالب ١/ ٣٤٢، نهاية المحتاج ٣/ ٦٧، حاشية الشبراملسي ٣/ ٦٧، كشاف القناع ٢/ ١٨٣، شرح منتهى الإرادات ١/ ٣٧٤.
(¬٢) هو أبو الحسن علي بن إبراهيم بن سلمة القزويني القطان، من كبار حفاظ الحديث، ولد بقزوين سنة ٢٥٤ هـ، روى عن ابن ماجه سننه، رحل إلى العراق واليمن، كان عالمًا بالحديث والفقه، والنحو، واللغة، توفي بالأهواز سنة ٣٤٥ هـ.
تذكرة الحفاظ ٢/ ٤٧٠، شذرات الذهب ١/ ٣٧٠، سير أعلام النبلاء ١٥/ ٤٦٣، الأعلام ٤/ ٢٥٠.
(¬٣) رواه أبو داود ٢/ ٩٩ كتاب الزكاة، باب في زكاة السائمة رقم ١٥٧٢، وابن خزيمة ٤/ ٢٠ كتاب الزكاة، باب ذكر الدليل على أن النبي إنما أوجب الصدقة في البقر في سوائمها دون عواملها رقم ٢٢٧٠، والدارقطني ٢/ ١٠٣ كتاب الزكاة، باب ليس في العوامل صدقة رقم ٣، والبيهقي ٤/ ٩٩ كتاب الزكاة، باب كيف فرض صدقة البقر.
عن علي -رضي الله عنه-.
قال الزيلعي في نصب الراية ٢/ ٣٥٣ عن ابن القطان قال: إسناده صحيح وكلهم ثقات.
(¬٤) ٢/ ١٠٤ كتاب الزكاة، باب تفسير الخليطين وما جاء في الزكاة على الخليطين ورواه أيضًا البيهقي في السنن الكبرى ٤/ ١١٦ كتاب الزكاة، باب ما يسقط الزكاة عن الماشية. عن =','3','46',NULL,NULL,'0');
INSERT INTO [b46] VALUES (722,'والسائمة: الراعية أكثر الحول، لا للركوب والعمل.
وبنت مخاض: ما دخل في السنة الثانية.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-368>[تعريف بعض ما وجبت فيه الزكاة من البهائم]</span>
قوله: والسائمة: الراعية أكثر الحول، لا للركوب والعمل.
هذا تفسير السائمة (¬١)، وهي من سامت الماشية سومًا. أي: رعت قيد بقوله الراعية أكثر الحول؛ لأنه لو علفها نصف حول لا تكون سائمةً، حتى لا تجب فيها الزكاة (¬٢)، وقيد بقوله: "لا للركوب والعمل"؛ لأنها إذا كانت للركوب، أو العمل لا زكاة فيها (¬٣).
قوله: وبنت مخاضٍ: ما دخل في السنة الثانية.
أي: بنت مخاضٍ ما تمت لها سنة، ودخلت في السنة الثانية (¬٤)، وإنما سميت بها؛ لأن أمها حملت بعدها وهي ماخض، يقال: مخضت الحامل مخضًا، أي: أخذها وجع الولادة (¬٥).
_________
= ابن جريج، عن زياد بن سعيد، عن أبي الزبير، عن جابر -رضي الله عنه-، أن النبي -صلى الله عليه وسلم- قال: "ليس في المثيرة صدقة".
قال البيهقي في السنن ٤/ ١١٦: في إسناده ضعف، والصحيح موقوف.
ووقفه عبد الرزاق في مصنفه عن جابر ٤/ ١٩ كتاب الزكاة، باب ما لا يؤخذ من الصدقة رقم ٦٨٢٨.
وقال الحافظ في الدراية ١/ ٢٥٦: إسناده حسن، وأخرجه عبد الرزاق وهو أصح.
(¬١) العناية ٢/ ١٩٤، شرح فتح القدير ٢/ ١٩٤.
(¬٢) تبيين الحقائق ١/ ٢٥٩، المختار ١/ ١٠٥، الكتاب ١/ ١٤٥، الاختيار ١/ ١٠٥، الهداية ١/ ١٠٩، شرح الوقاية ١/ ١٠٢.
(¬٣) شرح فتح القدير ٢/ ١٧١، بدائع الصنائع ٢/ ٣٤، الكتاب ١/ ١٤٥، كشف الحقائق ١/ ١٠٢، الهداية ١/ ١٠٩، تبيين الحقائق ١/ ٢٦٨، حاشية الشلبي ١/ ٢٦٨.
(¬٤) الهداية ١/ ١٠٥، شرح فتح القدير ٢/ ١٧٢.
(¬٥) مختار الصحاح ص ٢٥١ مادة م خ ض، لسان العرب ٧/ ٢٢٩ مادة مخض، القاموس =','3','47',NULL,NULL,'0');
INSERT INTO [b46] VALUES (723,'وبنت لبون: في الثالثة. والحِقَّة: في الرابعة. والجذعة: في الخامسة.
<hr><span data-type="title">منحة السلوك</span>

قوله: وبنت لبونٍ: في الثالثة.
أي: بنت لبونٍ ما تمت لها سنتان ودخلت في السنة الثالثة (¬١).
سميت بها: لأن أمها حملت قبلها، وولدت، وهي ذات لبنٍ (¬٢).
قوله: والحقة: في الرابعة.
أي: الحقة ما تمت لها ثلاث سنين، ودخلت في السنة الرابعة (¬٣)، سميت بها: لأنها استحقت أن يحمل عليها (¬٤) (¬٥).
قوله: والجذعة: في الخامسة.
أي: الجذعة ما تمت لها أربع سنين ودخلت في السنة الخامسة (¬٦).
سميت بها: لأنها أطاقت الجذع، يقال جذع الدابة: أي حبسها على غير علفٍ (¬٧) (¬٨).
_________
= المحيط ٤/ ٢١٣ مادة م خ ض، الدر النقي ٢/ ٣٢٠، لغة الفقه ص ١٠٤، المطلع على أبواب المقنع ص ١٢٣، النظم المستعذب ١/ ١٤٣.
(¬١) الهداية ١/ ١٠٥، بدائع الصنائع ٢/ ٣٢.
(¬٢) مختار الصحاح ص ٢٤٦ مادة ل ب ن، لسان العرب ١٣/ ٣٧٣ مادة لبن، الدر النقي ٢/ ٣٢٠، لغة الفقه ص ١٠٤، المطلع على أبوب المقنع ص ١٢٤.
(¬٣) المبسوط ٢/ ١٥١، المختار ١/ ١٠٦.
(¬٤) العناية ٢/ ١٧٣.
(¬٥) مختار الصحاح ص ٦٢ مادة ح ق ق، القاموس المحيط ١/ ٦٨٠ مادة ح ق ق، لسان العرب ١٠/ ٥٥ مادة حقق، لغة الفقه ص ١٠٤، حلية الفقهاء ص ٩٧، المطلع على أبواب المقنع ص ١١٤، النظم المستعذب ١/ ١٤٣.
(¬٦) المختار ١/ ١٠٦، الهداية ١/ ١٠٥.
(¬٧) المبسوط ٢/ ١٥١، العناية ٢/ ١٧٣.
(¬٨) لسان العرب ٨/ ٤٥ مادة جذع، مختار الصحاح ص ٤١ مادة ج ذ ع، القاموس المحيط ١/ ٤٦٢ مادة ج ذ ع، النظم المستعذب ١/ ١٤٤.','3','48',NULL,NULL,'0');
INSERT INTO [b46] VALUES (724,'والتبيع: في الثانية. والمسنة: في الثالثة. وثني الغنم ما بلغ سنة، وجذعها ما بلغ أكثرها.
<hr><span data-type="title">منحة السلوك</span>

قوله: والتبيع: في الثانية.
أي: التبيع ما تمت له سنة، ودخل في السنة الثانية (¬١). سمي بذلك؛ لأنه يتبع أمه (¬٢).
قوله: والمسنة: في الثالثة.
أي: المسنة ما تمت لها سنتان، ودخلت في السنة الثالثة (¬٣).
قوله: وثني الغنم ما بلغ سنةً (¬٤)، وجذعها ما بلغ أكثرها.
أي: جذع الغنم ما بلغ أكثر السنة (¬٥)، والجذع من البقر ابن سنةٍ (¬٦)،
_________
(¬١) المختار ١/ ١٠٨، الهداية ١/ ١٠٦، بدائع الصنائع ١/ ٣٢، شرح فتح القدير ٢/ ١٧٨.
(¬٢) مختار الصحاح ص ٣١ مادة ت ب ع، القاموس المحيط ١/ ٣٥٧ مادة ت ب ع، الدر النقي ٢/ ٣٢٣، المطلع على أبواب المقنع ص ١٢٥، لغة الفقه ص ١٠٦، طلبة الطلبة ص ٤٠، النظم المستعذب ١/ ١٤٤.
(¬٣) القاموس المحيط ٢/ ٦٣٢ مادة س ن ن، المصباح المنير ١/ ٢٩٢ مادة السن، المطلع على أبواب المقنع ص ١٢٥، طلبة الطلبة ص ٤٠، الدر النقي ٢/ ٣٢٣، النظم المستعذب ١/ ١٤٥.
(¬٤) المختار ١/ ١٠٧، تبيين الحقائق ١/ ٢٦٣، مختار الصحاح ص ٣٧ مادة ث ن ي، القاموس المحيط ١/ ٤٢٤ مادة ث ن ي، المصباح المنير ١/ ٨٥ مادة الثنية، حلية الفقهاء ص ١٠٠، طلبة الطلبة ص ٤٠.
(¬٥) المبسوط ٢/ ١٨٢، تبيين الحقائق ١/ ٣٦٣.
(¬٦) لسان العرب ٨/ ٤٥ مادة جذع، مختار الصحاح ص ٤١ مادة ج ذ ع، القاموس المحيط ١/ ٤٦٢ مادة ج ذ ع، النظم المستعذب ١/ ١٤٤، المطلع على أبواب المقنع ص ١٢٥، الدر النقي ٢/ ٣٢٤.','3','49',NULL,NULL,'0');
INSERT INTO [b46] VALUES (725,'ومن وجب عليه سن لا يملكه أعطى أعلى منه، وأخذ الزائد برضا الساعي، أو أعطى أسفل منه مع الزائد مطلقًا.
<hr><span data-type="title">منحة السلوك</span>

والثني ابن سنتين (¬١)، والجذع من الإبل، ابن أربع سنين، والثني ابن خمسة (¬٢).

<span data-type="title" id=toc-369>[عدم وجود السن المفروض]</span>
قوله: ومن وجب عليه سن. أي: ذات سنٍّ، وصاحبه لا يملكه، أعطى أعلى منه، وأخذ الزائد برضا الساعى، يعنى: لا يجبر الساعي على القبول؛ لأن فيه شراء الزيادة، ولا إجبار فيه (¬٣).
قوله: أو أعطى أسفل منه مع الزائد مطلقًا.
_________
(¬١) العناية ٢/ ١٨٢، المبسوط ٢/ ٨٢، مختار الصحاح ص ٣٧ مادة ث ن ي، القاموس المحيط ١/ ٤٢٤ مادة ث ن ي، المصباح المنير ١/ ٨٥ مادة الثنية، المطلع على أبواب المقنع ص ١٢٤، الدر النقي ٢/ ٣٢١، طلبة الطلبة ص ٤٠.
(¬٢) بدائع الصنائع ٢/ ٣٢، المبسوط ٢/ ١٥١.
(¬٣) وعند المالكية: من لم يملك السن الوسط، أجبر على شرائه.
وعند الشافعية: من وجب عليه سن ولم يكن عنده أخذ منه، ورد عليه شاتان، أو عشرون درهمًا، أو سن أسفل منه، ودفع معه شاتان أو عشرون درهمًا. والاختيار في الصعود والنزول إلى المصدق. وفي الشاتين، أو العشرين درهمًا إلى الذي يعطي.
وعند الحنابلة: من وجبت عليه سن فعدمها، خير المالك دون الساعي إلى ما يليها في ملكه، ثم إلى ما يليه إن عدمه، وفي النزول كذلك، فإن شاء أخرج سنًا أفضل منها ومعها شاتان، أو عشرون درهمًا، وإن شاء المالك أخرج أعلى منها، فإن عدم ما يليها انتقل إلى الأخرى، وضاعف الجبران، وهكذا، ولا مدخل للجبران في غير الإبل فمن عدم فريضة البقر، أو الغنم، ووجد دونها حرم إخراجها، ولزمه تحصل الفريضة، وإخراجها، وإن وجد أعلى منها فدفعها بغير جبران قبلت منه، وإن لم يفعل كلف شراؤها.
الكتاب ١/ ١٤٤، بداية المبتدي ١/ ١٠٩، الهداية ١/ ١٠٩ الاختيار ١/ ١١٠، كشف الحقائق ١/ ١٠٢ تبيين الحقائق ١/ ٢٧٠، تحفة الفقهاء ١/ ٢٨٨، التلقين ص ٥٠، الكافي لابن عبد البر ص ١٠٤، التنبيه ص ٥٦، روض الطالب ١/ ٣٤٤، الإقناع للحجاوي ٢/ ١٨٩، شرح منتهى الإرادات ١/ ٣٧٧.','3','50',NULL,NULL,'0');
INSERT INTO [b46] VALUES (726,'ويجوز دفع القيمة في الزكاة، والفطر، والكفارة، والعشر، والخراج، والنذر، لا في الهدايا والضحايا.
<hr><span data-type="title">منحة السلوك</span>

يعني: سواء رضي الساعي، أو لم يرض، حتى إذا لم يرض يجبر على القبول (¬١).

<span data-type="title" id=toc-370>[ما يجوز دفع القيمة فيه من العبادات]</span>
قوله: ويجوز دفع القيمة في الزكاة، والفطر، والكفارة، والعشر، والخراج (¬٢)، والنذر (¬٣) (¬٤).
وقال الشافعي: لا يجوز قياسًا على الهدي، والأضحية (¬٥).
ولنا: ما روى البخاري (¬٦)، من حديث ثمامة، أن أنسًا حدثه، أن
_________
(¬١) بداية المبتدي ١/ ١٠٩، شرح فتح القدير ٢/ ١٩٠، تبيين الحقائق ١/ ٢٧٠، الهداية ١/ ١٠٩، كشف الحقائق ١/ ١٠٢، الاختيار ١/ ١١٠، العناية ١/ ١٩٠.
(¬٢) الخراج: هو ما يؤخذ على الأرض. وقيل ما يخرج من غلة الأرض، ثم سمي ما يأخذه السلطان خراجًا، فيقال: أدى فلان خراج أرضه، وأدى أهل الذمة خراج رؤوسهم، يعني: الجزية، وهو يقع على مال الفيء، والجزية، والغَلَّة.
أنيس الفقهاء س ١٨٥، حلية الفقهاء ص ١٣٣، الدر النقي ٢/ ٣٣٨، لغة الفقه ص ٣٢٢، النظم المستعذب ١/ ١٥٢، مختار الصحاح ص ٧٢ مادة خ ر ج.
(¬٣) النذر: واحد النذور، يقال: نذرت أُنْذر وانذِرُ -بكسر الذال وضمها- إذا أوجبت على نفسك شيئًا تبرعًا. وقيل: إيجاب عين الفعل المباح على نفسه؛ تعظيمًا لله تعالى.
القاموس المحيط ٤/ ٣٥١ مادة ن ذ ر، لسان العرب ٥/ ٢٠٠ مادة نذر، أنيس الفقهاء ص ٣٠١، لغة الفقه ص ١٧٢، الدر النقي ٣/ ٧٩٧، النظم المستعذب ١/ ٢٢١.
(¬٤) الهداية ١/ ١٠٩، العناية ٢/ ١٩١، كشف الحقائق ١/ ١٠٢، الكتاب ١/ ١٤٤، المختار ١/ ١٢٤، ملتقى الأبحر ١/ ١٧٦، تنوير الأبصار ٢/ ٢٨٥، حاشية رد المحتار ٢/ ٢٨٥.
(¬٥) وإليه ذهب المالكية، والحنابلة.
التفريع ١/ ٢٨٩، التلقين ص ٥٠، المهذب ١/ ١٥٠، الحاوي ٣/ ٧٧، الكافي لابن قدامة ١/ ٢٩٥، الإنصاف ٣/ ٦٥.
(¬٦) سبق تخريجه في ٣/ ٢٨.','3','51',NULL,NULL,'0');
INSERT INTO [b46] VALUES (727,'والواجب أخذ الوسط من النصاب.
<hr><span data-type="title">منحة السلوك</span>

أبا بكرٍ كتب له الفريضة التي أمر الله ورسوله: "من بلغت عنده من الإبل صدقة الجذعة، وليست عنده جذعة، وعنده حقة، فإنها تقبل منه الحقة" الحديث.
قوله: والواجب أخذ الوسط من النصاب.
حتى لو وجب عليه بنت لبونٍ مثلًا، لا يؤخذ منه خيار بنت لبونٍ في ماله، ولا أردى بنت لبونٍ فيه، وإنما يؤخذ بنت لبونٍ وسطٍ، وكذا غيرها (¬١)؛ لقوله -صلى الله عليه وسلم-: "إياكم وكرائم أموالهم" رواه الجماعة (¬٢).
_________
(¬١) وفاقًا للثلاثة.
كنز الدقائق ١/ ٢٧٢، تحفة الفقهاء ١/ ٣٠٨، تبيين الحقائق ١/ ٢٧٢، كشف الحقائق ١/ ١٠٣، الهداية ١/ ١٠٩، تنوير الأبصار ٢/ ٢٨٦، الدر المختار ٢/ ٢٨٦، الوقاية ١/ ١٠٣، الشرح الكبير للدردير ١/ ٤٣٥، حاشية الدسوقي ١/ ٤٣٥ المنهاج ١/ ٤٢٩، زاد المحتاج ١/ ٤٣٠ المقنع ١/ ٣٠٦، حاشية المقنع ١/ ٣٠٦.
(¬٢) البخاري ٢/ ٥٤٤ كتاب الزكاة، باب أخذ الصدقة من الأغنياء، وترد في الفتنراء حيث كانوا رقم ١٤٢٥، ومسلم ١/ ٥٠ كتاب الإيمان، باب الدعاء إلى الشهادتين وشرائع الإسلام رقم ١٩ وأبو داود ٢/ ١٠٤ كتاب الزكاة، باب في زكاة السائمة رقم ١٥٨٤، والترمذي ٢/ ٢٠٤ كتاب الزكاة، باب ما جاء في كراهية أخذ خيار المال في الصدقة رقم ٦٢٥، والنسائي ٥/ ٥٥ كتاب الزكاة، باب إخراج الزكاة من بلد إلى بلد رقم ٢٥٢٢، وابن ماجه ١٧٨٣ كتاب الزكاة باب فرض الزكاة ١/ ٥٦٨، وأحمد ١/ ٢٣٣.
من حديث ابن عباس -رضي الله عنهما-، أن رسول الله -صلى الله عليه وسلم- لما بعث معاذًا إلى اليمن قال له: "إنك تأتي قومًا من أهل الكتاب، فادعهم إلى شهادة أن لا إله إلا الله، وأني رسول الله، فإن هم أطاعوك لذلك فأعلمهم أن الله افترض عليهم خمس صلوات في كل يوم وليلة، فإن هم أطاعوك فأعلمهم أن الله افترض عليهم صدقة تؤخذ من أغنيائهم فترد على فقرائهم، فإن هم أطاعوك لذلك فإياك وكرائم أموالهم، واتق دعوة المظلوم، فإنه ليس بينها وبين الله حجاب".','3','52',NULL,NULL,'0');
INSERT INTO [b46] VALUES (728,'ومطلق المستفاد يضم في الحول، إلا أن الربح والولد يضم إلى أصله لا غير،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-371>[ضم المستفاد]</span>
قوله: ومطلق المستفاد يضم في الحول، إلا أن الربح والولد يضم إلى أصله لا غير (¬١).
اعلم أن الفائدة على ضربين ما يكون من جنس الأصل، وما يكون من غير جنس الأصل، والثاني: لا يضم إلى الأصل بالاتفاق، بل يستأنف له حول آخر، كما إذا كانت له إبل فاستفاد بقرًا، أو غنمًا في أثناء الحول.
والأول: لا يخلو: إما أن يكون حاصلًا بسبب الأصل، كالأولاد، والأرباح، وذاك يضم بالإجماع، وإن كان حاصلًا بسبب مقصودٍ في نفسه، كالموروث، والمشتري، والموهوب (¬٢)، ونحوها، يضم عندنا (¬٣). خلافًا للشافعي (¬٤).
_________
(¬١) كنز الدقائق ١/ ٢٧٢، بداية المبتدي ١/ ١٠٩، العناية ٢/ ١٩٥، الهداية ١/ ١٠٩، شرح فتح القدير ٢/ ١٩٥، تبيين الحقائق ١/ ٢٧٢، كشف الحقائق ١/ ١٠٣، الكتاب ١/ ١٤٥.
(¬٢) الهبة: في اللغة: إيصال النفع إلى الغير. وفي الشرع: تمليك العين بلا عوض. وقيل: هي التبرع والتفضل بما ينفع الموهوب له. يقال: وهب له مالًا وهْبًا وَوَهَبًا بالتحريك وهبة، ويسمى الموهوب: هبة وموهبةً، والجمع هبات ومواهب.
مختار الصحاح ص ٣٠٧ هـ مادة وهـ ب، القاموس المحيط ٤/ ٦٦١ مادة وهـ ب، معجم مقاييس اللغة ٦/ ١٤٧ باب الهاء والباء وما يثلثهما مادة وهب، المغرب ص ٤٩٦ مادة الهبة، أنيس الفقهاء ص ٢٥٥، لغة الفقه ص ٢٣٩، طلبة الطلبة ص ٢٢١، التعريفات ص ٢٥٧.
(¬٣) شرح فتح القدير ٢/ ١٩٦، تحفة الفقهاء ١/ ٣٠٩، العناية ٢/ ١٩٥، حاشية الشلبي ١/ ٢٧٢ كشف الحقائق ١/ ١٠٣، البحر الرائق ٢/ ٢٢٢، شرح الوقاية ١/ ١٠٣.
(¬٤) وأحمد، حيث يريان: أن ما استفاده في أثناء الحول بإرث، أو عقد، لا من نماء النصاب استقبل به حولًا، ولم يضمه في الحول إلى ما عنده، سواء كان من جنسه، أو من غير جنسه، وسواء كان مما تجب الزكاة في عينه، كالمواشي، والأثمان، أو في قيمته كعروض التجارة. =','3','53',NULL,NULL,'0');
INSERT INTO [b46] VALUES (729,'وغيرهما يُضمُّ إلى أقرب جنسه حولًا.
والزكاة واجبة في النصاب دون العفو،
<hr><span data-type="title">منحة السلوك</span>

والمراد من الضم: أن تجب الزكاة في الفائدة عند تمام الحول على الأصل (¬١).
قوله: وغيرهما.
أي: غير الربح، والولد، يُضمُّ إلى أقرب جنسه حولًا (¬٢).
صورته: إذا كان له نصاب من الفضة، ونصاب آخر عروض التجارة، ثم وهب له دراهم، يضم الدراهم إلى الفضة، إن كان نصاب الفضة أقرب إلى تمام الحول، ويضم إلى نصاب العروض إن كان أقرب إلى الحول.

<span data-type="title" id=toc-372>[سقوط الزكاة عن العفو]</span>
قول: والزكاة واجبة في النصاب دون العفو (¬٣)، هذا عندهما (¬٤) (¬٥)،
_________
= وذهب المالكية: إلى أنه إن كان حيوانًا ضم ما استفاده إلى ما كان في يديه وزكاه، وإن كان عينًا لم تجب عليه زكاة حتى يحول عليه الحول.
القوانين ص ٦٩، ٧٤، التلقين ص ٤٧، الشرح الصغير ١/ ٢٠٨، منح الجليل ٢/ ٥، جواهر الإكليل ١/ ١١٨، مغني المحتاج ١/ ٣٧٩، شرح المحلي على المنهاج ٢/ ١٤، المستوعب ٣/ ١٩٢، الإفصاح ١/ ٢١٣.
(¬١) الهداية ١/ ١١٠، العناية ٢/ ١٩٦، البحر الرائق ٢/ ٢٢٢، شرح فتح القدير ٢/ ١٩٦، تبيين الحقائق ١/ ٢٧٢، كشف الحقائق ١/ ١٠٣، شرح الوقاية ١/ ١٠٣.
(¬٢) الهداية ١/ ١٠٩، شرح فتح القدير ٢/ ١٩٦، تبيين الحقائق ١/ ٢٧٣، كشف الحقائق ١/ ١٠٣، العناية ٢/ ١٩٦، حاشية الشلبي ١٢/ ٢٧٣.
(¬٣) ما بين الفرضين يسمى العفو، ويسمى الوقص، ويسمى الشنق.
شرح منتهى الإرادات ١/ ٧٧.
(¬٤) لأن الزيادة على النصاب تسمى في الشرع عفوًا، والعفو ما يخلو عن الوجوب، وهو مذهب المالكية، والشافعية، والحنابلة.
الكتاب ١/ ١٤٥، بداية المبتدي ١/ ١١٠، كشف الحقائق ١/ ١٠٢، الاختيار ١/ ١٠٢، الهداية ١/ ١١٠، شرح الوقاية ١/ ١٠٢ تبيين الحقائق ٢١/ ٢٦٨، شرح فتح القدير ٢/ ١٩٧، العناية ٢/ ١٩٧، التلقين ص ٤٩، المعونة ١/ ٤٠٥ روض الطالب ١/ ٣٤١، رحمة الأمة ١/ ٩٧ منتهى الإرادات ١/ ٣٧٧، الإفصاح ١/ ٢٠٠.
(¬٥) في م بزيادة (وهو المعتمد).','3','54',NULL,NULL,'0');
INSERT INTO [b46] VALUES (730,'فلا يسقط شيء بهلاك العفو،
<hr><span data-type="title">منحة السلوك</span>

وعند: محمدٍ، وزفر يجب فيهما (¬١).
قوله: فلا يسقط شيء بهلاك العفو.
[هذا] (¬٢) فائدة الخلاف المذكور، فلهذا أتى بالفاء، أي: لا يسقط شيء من الزكاة، إذا بقي النصاب، وهلك العفو (¬٣).
صورته: إذا كان له تسع من الإبل مثلًا، فحال عليها الحول، فهلك منها أربعة التي هي عفو، تسقط أربعة أتساع شاة عند محمدٍ (¬٤)، ولو كان له مائة وعشرون شاةً فحال عليها الحول، فهلك منها ثمانون، سقط عند محمدٍ (¬٥) ثلثا شاةٍ، وبقي الثلث.
_________
(¬١) لأن الزكاة وجبت شكرًا لنعمة المال، ويحصل به الغنى؛ ولأن النصاب منه غير متعين، فإذا وجد أكثر منه تعلق بالكل، كنصاب السرقة، والمهر، والسفر، والحيض، وكل ما كان مقدرًا شرعًا، وإنما سمي عفوًا؛ لوجوب الزكاة قبل وجوده.
تبيين الحقائق ١/ ٢٦٩، حاشية الشلبي ١/ ٢٦٩، الهداية ١/ ١١٠، الاختيار ١/ ١٠٢ الكتاب ١/ ١٤٥، المختار ١/ ١٠٢، كشف الحقائق ١/ ١٠٢، شرح فتح القدير ٢/ ١٩٧ العناية ٢/ ١٩٧.
(¬٢) زيادة يقتضيها السياق.
(¬٣) تبيين الحقائق ١/ ٢٧٠، كنز الدقائق ١/ ٢٦٩، شرح فتح القدير ٢/ ١٩٧، كشف الحقائق ١/ ١٠٢، العناية ٢/ ١٩٧، الاختيار ١/ ١٠٢، البحر الرائق ٢/ ٢١٨.
(¬٤) وزفر.
تبيين الحقائق ١/ ٢٦٩، حاشية الشلبي ١/ ٢٦٩، كشف الحقائق ١/ ١٠٢، الهداية ١/ ١١٠، الاختيار ١/ ١٠٢، شرح فتح القدير ٢/ ١٩٧، العناية ٢/ ١٩٧.
(¬٥) وزفر. =','3','55',NULL,NULL,'0');
INSERT INTO [b46] VALUES (731,'ولو هلك النصاب بعد وجوب الزكاة سقطت، ولو هلك بعضه سقط بقدره،
<hr><span data-type="title">منحة السلوك</span>

وعندهما: لا يسقط شيء في الفصلين جميعًا (¬١).
قوله: ولو هلك النصاب بعد وجوب الزكاة سقطت (¬٢).
وقال الشافعي: إذا هلكت الأموال الباطنة بعد الوجوب، وبعد التمكن من الأداء لا تسقط (¬٣).
ولنا: أن المال محل الزكاة فيفوت بفوات المحل (¬٤).
قوله: ولو هلك بعضه. أي: بعض النصاب، سقط بقدره، مثل ما إذا هلك مائة وبقي مائة، يجب عليه زكاة المائة. وعلى هذا [فقس] (¬٥) (¬٦).
_________
= تبيين الحقائق ١/ ٢٦٩، حاشية الشلبي ١/ ٢٦٩، الاختيار ١/ ١٠٢، كشف الحقائق ١/ ١٠٢، العناية ٢/ ١٩٧، البحر الرائق ٢/ ٢١٨.
(¬١) تبيين الحقائق ١/ ٢٦٩، البحر الرائق ٢/ ٢١٨، شرح فتح القدير ٢/ ١٩٧، العناية ٢/ ١٩٧، الاختيار ١/ ١٠٢ كشف الحقائق ١/ ١٠٢.
(¬٢) الكتاب ١/ ١٤٦، بداية المبتدي ١/ ١١٠ ملتقى الأبحر ١/ ١٧٧ الهداية ١/ ١١٠، المختار ١/ ١٠٢.
(¬٣) وإليه ذهب الحنابلة.
وعند المالكية: إذا فرَّط في زكاة عليه حتى مات، فإن أوصى لزم الورثة إخراجها عنه، وإن لم يوصِ لم يلزم الورثة، وإن لم يفرِّط فيها حتى مات أُخرجت من رأس المال.
المعونة ٣/ ١٦٤٦، التفريع ٢/ ٣٢٥، المدونة ٤/ ٣٠٩، جواهر الإكليل ٢/ ٣٢٢، روض الطالب ١/ ٣٦٥، أسنى المطالب ١/ ٣٦٥، التسهيل ص ٨٢، الروض المربع ص ١٥٢.
(¬٤) تحفة الفقهاء ١/ ٣٠٦، العناية ٢/ ٢٠١، شرح فتح القدير ٢/ ٢٠١، تبيين الحقائق ١/ ٢٦٩، البحر الرائق ٢/ ٢١٨.
(¬٥) تحفة الفقهاء ١/ ٣٠٦، العناية ٢/ ٢٠٣، شرح فتح القدير ٢/ ٢٠٣، تبيين الحقائق ١/ ٢٦٩، البحر الرائق ٢/ ٢١٩، حاشية الشلبي ١/ ٢٧٠، المختار ١/ ١٠٢، الهداية ١/ ١١٠.
(¬٦) زيادة يقتضيها السياق.','3','56',NULL,NULL,'0');
INSERT INTO [b46] VALUES (732,'ولو أهلكه المالك ضمن، ولو هلك بعد طلب الساعي فقولان.
<hr><span data-type="title">منحة السلوك</span>

قوله: ولو أهلكه المالك ضمن المتعدي (¬١).
قوله: ولو هلك بعد طلب الساعي، فقولان.
في قول مشايخ ما وراء النهر: لا يضمن، وهو اختيار أبي طاهر الدباس (¬٢)، وأبي سهل الزجاجي (¬٣) (¬٤). وهو الصحيح (¬٥).
وفي قول العراقيين: يضمن، وهو اختيار الكرخي (¬٦).
_________
(¬١) الهداية ١/ ١١٠، تحفة الفقهاء ١/ ٣٠٦، تبيين الحقائق ١/ ٢٧٠، حاشية الشبلي ١/ ٢٧٠، البحر الرائق ٢/ ٢١٩، شرح فتح القدير ٢/ ٢٠٢، العناية ٢/ ٢٠٢.
(¬٢) هو محمد بن محمد بن سفيان، أبو طاهر الدباس، الفقيه، إمام أهل الرأي بالعراق في عصره، موصوف بالحفظ ومعرفة الروايات، من أقران أبي الحسن الكرخي، ولي القضاء بالشام وخرج منها إلى مكة فتوفي بها.
الجواهر المضية ٢/ ١١٦، الفوائد البهية ص ١٨٧، الوفيات بالوفيات ١/ ١٦٢، الطبقات السنية رقم ٢٢٥٠، طبقات الفقهاء للشيرازي ١٤٢.
(¬٣) صاحب كتاب الرياضة، درس على أبي الحسن الكرخي، ودرس عليه أبو بكر الرازي، وتفقه به فقهاء نيسابور من أصحاب الإمام أبي حنيفة، كان إذا دخل مجالس النظر تغيرت وجوه المخالفين لقوة نفسه، وحسن جدله، يقال له: أبو سهل الفرضي وأبو سهل الزجاجي، وأبو سهل الغزالي، رجع إلى نيسابور فمات بها.
تاج التراجم ص ٣٣٥، الجواهر المضية ٤/ ٥١، الفوائد البهية ص ٨١، الطبقات السنية رقم ٢٨٨٧١.
(¬٤) تبيين الحقائق ١/ ٢٧٠، العناية ٢/ ٢٠٣.
(¬٥) وعليه عامتهم؛ لأنه لم يفوت بهذا المنع على أحد ملكًا، ولا يدًا، فصار كما لو طلب واحد من الفقراء. فلنا أن نمنع؛ لأنه لا يضمن.
تحفة الفقهاء ١/ ٣٠٧، تبيين الحقائق ١/ ٢٧٠.
(¬٦) لأن حق الأخذ له، ومنعه يوجب الضمان، كالوديعة.
شرح فتح القدير ٢/ ٢٠٣، تبيين الحقائق ١/ ٢٧٠.','3','57',NULL,NULL,'0');
INSERT INTO [b46] VALUES (733,'ويصح التعجيل لسنين، ولنصب أيضًا بعد ما ملك النصاب.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-373>[تعجيل دفع الزكاة]</span>
قوله: ويصح التعجيل [لسنين] (¬١)، ولنصب أيضًا بعد ما ملك النصاب (¬٢).
وقال مالك: لا يصح (¬٣).
ولنا: "أنه -صلى الله عليه وسلم- استسلف من عباسٍ (¬٤) زكاة عامين" رواه الشيخ أبو الحسين القدوري (¬٥)، وروي: "أن العباس سأل رسول الله -صلى الله عليه وسلم- في تعجيل
_________
(¬١) في الأصل بسنتين والمثبت من بقية النسخ.
(¬٢) العناية ٢/ ٢٠٤، الهداية ١/ ١١١، بداية المبتدي ١/ ١١١ شرح فتح القدير ٢/ ٢٠٤، المختار ١/ ١٠٣، تبيين الحقائق ١/ ٢٧٤، الاختيار ١/ ١٠٣.
(¬٣) وذهب الشافعية، والحنابلة: إلى عدم صحة تعجيل النصاب.
وأما تعجيل دفع الزكاة: فذهب الشافعية: إلى عدم تعجيلها لعامين، ولا لأكثر.
وذهب الحنابلة: إلى جواز تعجيل الزكاة عامًا، أو عامين، ولا يزيد على عامين.
القوانين ص ٦٨، بداية المجتهد ١/ ٢٧٤، شرح المحلي على المنهاج ٢/ ٤٤، قليوبي على شرح المحلي ٢/ ٤٤، مغني المحتاج ١/ ٤١٦، أسنى المطالب ١/ ٣٦١، المغني ٢/ ٤٩٦، الشرح الكبير لابن قدامة ٢/ ٦٧٨.
(¬٤) هو العباس بن عبد المطلب بن هاشم بن عبد مناف، أبو الفضل، عم رسول الله -صلى الله عليه وسلم-، وجد الخلفاء العباسيين، كان في قريش مشهورًا بالرأي والحلم، كانت إليه سقاية الحاج وعمارة المسجد الحرام، شهد الفتح وحنينًا، مات سنة ٣٢ هـ.
صفة الصفوة ١/ ٥٠٦، تاريخ الفسوي ١/ ٢٩٥، شذرات الذهب ١/ ٣٨، سير أعلام النبلاء ٢/ ٧٨، أسد الغابة ٣/ ١٦٤.
(¬٥) رواه الترمذي ٣/ ٣٧ كتاب الزكاة، باب ما جاء في تعجيل الزكاة رقم ٦٧٨، والدارقطني ٢/ ١٢٤ كتاب الزكاة، باب تعجيل الصدقة قبل الحول رقم ٥.
من طريق إسحاق بن منصور، عن إسرائيل، عن الحجاج بن دينار، عن الحكم بن جَحْل، عن حجر العدوي، عن علي أنه قال: لعمر "إنا قد أخذنا من العباس زكاة العام عام الأول".
ورواه الدارقطني ٢/ ١٢٤ كتاب الزكاة، باب تعجيل الصدقة قبل الحول رقم ٧ عن ابن =','3','58',NULL,NULL,'0');
INSERT INTO [b46] VALUES (734,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

صدقته قبل أن تحل، فرخص له في ذلك" رواه ابن ماجه (¬١).
_________
= عباس -رضي الله عنهما- بلفظ: بعث رسول الله -صلى الله عليه وسلم- عمر ساعيًا، فأتى العباس يطلب صدقة ماله، فأغلظ له العباس، فخرج إلى النبي -صلى الله عليه وسلم- فأخبره، فقال رسول الله -صلى الله عليه وسلم-: "إن العباس قد أسلفنا زكاة ماله العام والعام المقبل". ومحمد بن عبيد الله العزرمي ضعيف.
ورواه البزار "كشف الأستار" ١/ ٤٢٤ كتاب الزكاة باب تعجيل الزكاة رقم ٨٩٥، وأبو يعلى ٢/ ١٢ رقم ٦٣٨ والدارقطني ٢/ ١٢٤ كتاب الزكاة، باب تعجيل الصدقة قبل الحول رقم ٦، والبيهقي ٤/ ١١١ كتاب الزكاة، باب تعجيل الصدقة. عن موسى بن طلحة أن النبي -صلى الله عليه وسلم- قال: "يا عمر أما علمت أن عم الرجل صنو أبيه، إنا كنا احتجنا إلى مال فتعجلنا من العباس صدقة ماله لسنتين". والحسن بن عمارة متروك قال الهيثمي في مجمع الزوائد ٣/ ٧٩: رواه أبو يعلى والبزار، وفيه الحسن بن عمارة، وفيه كلام.
ورواه الدارقطني ٢/ ١٢٥ كتاب الزكاة، باب تعجيل الصدقة قبل الحول رقم ٩ عن أبي رافع وفيه: "أن العباس أسلفنا صدقة العام عام الأول" وفيه إسماعيل المكي فيه مقال. قال الحافظ في الفتح ٣/ ٢٦٤: وليس بثبوت هذه القصة، في تعجيل صدقة العباس ببعيد في النظر بمجموع هذه الطرق.
(¬١) ١/ ٥٧٢ كتاب الزكاة باب تعجيل الزكاة قبل محلها رقم ١٧٩٥، والدارمي ١/ ٤١٣ كتاب الزكاة، باب في تعجيل الزكاة رقم ١٥٩٣، وأبو داود ١٦٢٤ كتاب الزكاة، باب في تعجيل الزكاة ٢/ ١١٥ والترمذي ٣/ ٣٧ رقم ٦٧٨ كتاب الزكاة، باب ما جاء في تعجيل الزكاة، والدارقطني ٢/ ١٢٣ كتاب الزكاة، باب تعجيل الصدقة قبل الحول رقم ٣، وأبو عبيد في الأموال ١٨٨٥ ص ٧٠٣ كتاب الصدقة وأحكامها وسننها باب تعجيل الصدقة وإخراجها قبل أوانها، وابن الجارود ص ٩٨ رقم ٣٦٠ كتاب الزكاة، والحاكم ٣/ ٣٣٢ كتاب معرفة الصحابة، والبيهقي في معرفة السنن والآثار ٦/ ٨٢ كتاب الزكاة، باب تعجيل الزكاة رقم ٨٠٧٤.
من طريق إسماعيل بن زكريا، عن الحجاج بن دينار، عن الحكم بن عتيبة، عن حجية بن عدي، عن علي، أن العباس: سأل رسول الله -صلى الله عليه وسلم- في تعجيل الصدقة قبل أن تحل، فرخص له في ذلك" قال الحاكم: صحيح الإسناد ولم يخرجه، وسكت عنه الذهبي. قال في المجموع ٦/ ١٤٥: حديث علي: رواه أبو داود، والترمذي، وغيرهما، بإسناد حسن.','3','59',NULL,NULL,'0');
INSERT INTO [b46] VALUES (735,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

ومعنى قوله: "أو لنصبٍ" أن يكون عنده نصاب، فيقدم زكاة نصبٍ كثيرةٍ ليست في ملكه بعد، فإنه يجوز (¬١)، خلافًا لزفر (¬٢).
_________
(¬١) لأنه أدى بعد السبب، وهو المال، والحول الأول وما بعده سواء، بخلاف ما قبل تمام النصاب؛ لأنه أدى قبل السبب فلا يجوز كغيره من العبادات؛ ولأن النصاب الأول سبب لوجود الزكاة فيه وفي غيره من النصب، ألا يرى أنها تضم إليها؟ فكانت تبعًا له.
المبسوط ٢/ ١٧٧، بدائع الصنائع ٢/ ٥١ الاختيار ١/ ١٠٣، شرح فتح القدير ٢/ ٢٠٤ العناية ٢/ ٢٠٤، تبيين الحقائق ١/ ٢٧٦.
(¬٢) حيث يرى: أنه إذا أدى عن نصب لا يجزيه إلا عن النصاب الذي في ملكه؛ لأنه أدى قبل السبب وهو الملك.
الاختيار ١/ ١٠٤، المبسوط ٢/ ١٧٧، بدائع الصنائع ٢/ ٥١ شرح فتح القدير ٢/ ٢٠٤، العناية ٢/ ٢٠٤ تبيين الحقائق ١/ ٢٧٦.','3','60',NULL,NULL,'0');
INSERT INTO [b46] VALUES (736,'<span data-type="title" id=toc-374>باب المعدن والركاز</span>
ومن وجد معدنًا من جوهر ذائب في أرض مباحة،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-375>[زكاة المعدن]</span>
قوله: المعدن والركاز.
أي: هذا بيان أحكام المعدن، والركاز.
والمعدن: اسم لمالٍ خلقه الله تعالى في الأرض يوم خلقها (¬١).
والكنز: اسم لمال دفنه بنو آدم (¬٢).
والرِّكاز: اسم لهما جميعًا (¬٣)، فقد يذكر ويراد به المعدن، وقد يذكر ويراد به الكنز (¬٤).
قوله: ومن وجد معدنًا من جوهرٍ ذائبٍ، كالذهب، والفضة، والنحاس، والرصاص (¬٥)، ونحوها، في أرضٍ مباحةٍ،
_________
(¬١) القاموس المحيط ٣/ ١٧٣ مادة ع د ن، المغرب ص ٣٠٦ مادة عدن، لسان العرب ٣١/ ٢٧٩ مادة عدن، المصباح المنير ٢/ ٣٩٧ مادة عَدَنَ، أنيس الفقهاء ص ١٣٢، لغة الفقه ص ١١٥، حليّة الفقهاء ص ١٠٦، القاموس الفقهي ص ٢٤٥، تحفة الفقهاء ١/ ٣٢٧، العناية ٢/ ٣٣.
(¬٢) القاموس المحيط ٤/ ٨٧ مادة ك ن ز، مختار الصحاح ص ٢٤١ مادة ك ن ز، المغرب ص ٤١٦ مادة كنز، المصباح المنير ٢/ ٥٤٢ مادة كنزت، تحفة الفقهاء ١/ ٣٢٧، العناية ٢/ ٢٣٣.
(¬٣) القاموس المحيط ٢/ ٣٨٢ مادة ر ك ز، لسان العرب ٥/ ٣٥٦ مادة ركز، مختار الصحاح ص ١٠٧ مادة ر ك ز، المغرب ص ١٩٦ مادة "ركز"، المصباح المنير ١/ ٢٣٦ مادة ركَزّتُ، حلية الفقهاء ص ١٠٦، لغة الفقه ص ١١٥، أنيس الفقهاء ص ١٣٢.
(¬٤) بدائع الصنائع ٢/ ٦٥، تحفة الفقهاء ١/ ٣٢٧.
(¬٥) الرصاص -بفتح الراء، وقيل: هو بالكسر-: من المعدنيات، مشتق من ذلك؛ لتداخل أجزائه. وهو عنصر فلزي لين.
تاج العروس ٤/ ٣٩٧ مادة رصص، لسان العرب ٧/ ٤١ مادة رصص، مختار الصحاح =','3','61',NULL,NULL,'0');
INSERT INTO [b46] VALUES (737,'ففيه الخُمُس،
<hr><span data-type="title">منحة السلوك</span>

ففيه الخُمُس (¬١).
وقال الشافعي: لا شيء فيه؛ لأنه مباح سبقت يده إليه، إلا إذا كان ذهبًا أو فضةً، فإنه يجب فيه الزكاة إذا بلغ نصابًا، من غير اشتراط الحول (¬٢).
_________
= ص ١٠٢ مادة ر ص ص، القاموس المحيط ٢/ ٣٤٤ مادة ر ص ص، المعجم الوسيط ١/ ٣٤٨ مادة الرِّصاص.
(¬١) المختار ١/ ١١٧، كنز الدقائق ١/ ٢٨٩، الهداية ١/ ١١٦، تبيين الحقائق ١/ ٢٨٩، ملتقى الأبحر ١/ ١٨٣.
(¬٢) اختلف العلماء في زكاة المعدن، بأي شيء تتعلق؟
فذهب الحنفية: إلى أنها تتعلق بكل ما ينطبع، كالذهب، والفضة والنحاس، ونحوها.
وذهب المالكية، والشافعية: إلى أنها لا تتعلق إلا بالذهب والفضة.
وذهب الحنابلة: إلى أنها تتعلق بكل خارج من الأرض، بكل ما ينطبع، كالذهب، والفضة، والحديد، وبما لا ينطبع، كالدر، والفيروزج، والياقوت، والزبرجد، والنورة، والعنبر، والكحل، والملح، والكبريت، والزفت، والقار، والزئبق، والنفط، وغيرها مما يسمى معدنًا.
واختلفوا هل يعتبر النصاب في المعدن؟.
فذهب الحنفية إلى عدم اعتبار النصاب فيه.
وذهب الأئمة الثلاثة، مالك، والشافعي، وأحمد: إلى اعتبار النصاب في المعدن.
واختلفوا في قدر الواجب في المعدن؟
فقال أبو حنيفة: فيه الخمس.
وقال مالك، وأحمد: فيه ربع العشر.
وعن الشافعي، ثلاثة أقوال: أحدها ربع العشر، والثاني: الخمس، والثالث: إن حصل بتعب فربع عشره، وإن كان بلا تعب فالخمس.
تبيين الحقائق ١/ ٢٨٨ ملتقى الأبحر ١/ ١٨٣، الاختيار ١/ ١١٧، العناية ٢/ ٢٣٣، شرح فتح القدير ٢/ ٢٣٣، تنوير الأبصار ٢/ ٣١٨، الهداية ١/ ١١٦، الشرح الصغير ١/ ٢٢٩، التلقين ص ٤٦، التفريع ١/ ٢٧٨، القوانين الفقهية ص ٧٠، مواهب الجليل ٢/ ٣٣٩، السراج الوهاج ص ١٢٥ تحفة المحتاج ٣/ ٢٨٢، رحمة الأمة ١/ ١٠٥، مغني المحتاج =','3','62',NULL,NULL,'0');
INSERT INTO [b46] VALUES (738,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

ولنا: قوله -صلى الله عليه وسلم-: "العجماء جُبار، والبئر جُبار، والمعدن جُبار وفي الركاز الخُمُس" رواه البخاري، وغيره (¬١)، وعن أبي هريرة -رضي الله عنه- أنه قال: قال رسول الله -صلى الله عليه وسلم-: "في الركاز الخُمُس، قيل: وما الركاز يا رسول الله؟ قال: الذهب الذي خلقه الله تعالى في الأرض يوم خُلِقت" رواه البيهقي (¬٢).
_________
= ١/ ٣٩٤، أسنى المطالب ١/ ٣٨٥، المقنع ١/ ٣٢٥، كشاف القناع ٢/ ٢٢٢، شرح منتهى الإرادات ١/ ٣٩٨ المستوعب ٣/ ٢٧٣.
(¬١) البخاري ٢/ ٥٤٥ كتاب الزكاة باب في الركاز الخمس رقم ١٤٢٨، ومسلم ١٣٣ كتاب في الحدود باب جرح العجماء والمعدن والبئر جبار رقم ١٧١٠، ورواه الطيالسي ص ٣٠٤ رقم ٢٣٠٥، والشافعي "ترتيب المسند" ١/ ٢٤٨ كتاب الزكاة، الباب الرابع في الركاز والمعادن ٦٧١، ٦٧٢، وأبو عبيد في الأموال ص ٤٢٠ كتاب الخمس وأحكامه وسننه، باب الخمس في المعادن والركاز، وعبد الرزاق في المصنف ١٠/ ٦٥ كتاب العقول، باب العجماء رقم ١٨٣٧٣، والحميدي ٢/ ٤٦٢ رقم ١٠٧٩، وأحمد ٢/ ٢٢٨، والدارمي ١/ ٤٢٢ في كتاب الزكاة، باب في الركاز رقم ١٦٢٢، وابن ماجه ٢/ ٨٣٩ كتاب اللقطة، باب من أصاب ركازًا رقم ٢٦٧٣، وأبو داود ٤/ ١٩٦ كتاب الديات، باب العجماء والمعدن والبئر جبار ٤٥٩٣، والترمذي ٣/ ٣ كتاب الزكاة باب ما جاء في أن العجماء جرحها جبار، وفي الركاز الخمس رقم ٦٤٢ والنسائي ٥/ ٤٤ كتاب الزكاة، باب المعدن رقم ٢٤٩٥، وابن الجارود في المنتقى ص ١٠١ كتاب الزكاة رقم ٣٧٢، وابن حبان بترتيب ابن بلبان ١٣/ ٣٥١ كتاب الجنايات، باب القصاص رقم ٦٠٠٥، وأبو يعلى في مسنده ١٠/ ٤٣٧ رقم ٦٠٥٠، والدارقطني ٣/ ١٤٩ كتاب الحدود والديات وغيره رقم ٢٠٤.
(¬٢) ٤/ ١٥٢ كتاب الزكاة، باب من قال: المعدن ركاز فيه الخمس.
من طريق أبي يوسف، عن عبد الله بن سعيد بن أبي سعيد، عن أبيه، عن جده، عن أبي هريرة -رضي الله عنه- قال: قال رسول الله -صلى الله عليه وسلم- ... ".
قال البيهقي ٤/ ١٥٢: تفرد به عبد الله بن سعيد المقبري، وهو ضعيف جدًا، جرحه أحمد بن حنبل، ويحيى بن معين، وجماعة من أئمة الحديث.','3','63',NULL,NULL,'0');
INSERT INTO [b46] VALUES (739,'والباقي له، ولو وجده في داره فلا شيء فيه، بخلاف الكنز،
<hr><span data-type="title">منحة السلوك</span>

قوله: والباقي له.
أي: أربعة أخماسه للواجد (¬١).
قوله: ولو وجد. أي: ولو وجد المعدن في داره، فلا شيء فيه، هذا عند أبي حنيفة (¬٢).
وعندهما: فيه الخمس أيضًا؛ لإطلاق الحديث (¬٣).
وله: أنه مالك الدار بجميع أجزائها، والمعدن من أجزائها، ولا مؤنة في سائر أجزائها، فكذا في هذا الجزء (¬٤).
قوله: بخلاف الكنز.
يعني: إذا وجد كنزًا في داره، ففيه الخمس بالاتفاق (¬٥)؛ لأنه ليس من
_________
(¬١) المختار ١/ ١١٧، كنز الدقائق ١/ ٢٨٩، شرح فتح القدير ٢/ ٢٣٤، العناية ٢/ ٢٣٤، تبيين الحقائق ١/ ٢٨٩، ملتقى الأبحر ١/ ١٨٤، الوقاية ١/ ١٠٨.
(¬٢) المختار ١/ ١١٧، المبسوط ٢/ ٢١٤، الوقاية ١/ ١٠٨، كشف الحقائق ١/ ١٠٨، الهداية ١/ ١١٦، تبيين الحقائق ١/ ٢٨٩، الاختيار ١/ ١١٧.
(¬٣) وإليه ذهب المالكية، والشافعية، والحنابلة.
الهداية ١/ ١١٦، تبيين الحقائق ١/ ٢٨٩، الاختيار ١/ ١١٧ كشف الحقائق ١/ ١٠٨، المختار ١/ ١١٧، المبسوط ٢/ ٢١٤، الشرح الصغير ١/ ٢٢٩، مواهب الجليل ٢/ ٣٣٩، أسنى المطالب ١/ ٣٨٥، السراج الوهاج ص ١٢٥، المبدع ٢/ ٣٥٧، الإقناع للحجاوي ٢/ ٢٢٢، منتهى الإرادات ١/ ٣٩٨.
(¬٤) الهداية ١/ ١١٦، بدائع الصنائع ٢/ ٦٥، تبيين الحقائق ١/ ٢٨٩، حاشية الشلبي ١/ ٢٨٩، كشف الحقائق ١/ ١٠٨، شرح فتح القدير ٢/ ٢٣٦، العناية ٢/ ٢٣٦.
(¬٥) بين أبي حنيفة، وأبي يوسف، ومحمد.
المبسوط ٢/ ٢١٢، تحفة الفقهاء ١/ ٣٢٨ الهداية ١/ ١١٦، كشف الحقائق ١/ ١٠٨، تبيين الحقائق ١/ ٢٨٩، العناية ٢/ ٢٣٦، شرح فتح القدير ٢/ ٢٣٦.','3','64',NULL,NULL,'0');
INSERT INTO [b46] VALUES (740,'ولو وجده في أرضه فروايتان.
ومن وجد كنزًا ففيه الخمس ولو كان متاعًا،
<hr><span data-type="title">منحة السلوك</span>

أجزاء الأرض؛ لأنه ليس بمركبٍ فيها (¬١).
قوله: ولو وجده في أرضه. أي: ولو وجد المعدن في أرضه، فروايتان عن أبي حنيفة: على رواية الأصل: لا شيء فيه (¬٢)، وعلى رواية الجامع الصغير (¬٣): فيه الخمس (¬٤).

<span data-type="title" id=toc-376>[زكاة الكنز]</span>
قوله: ومن وجد كنزًا ففيه الخمس.
هذا بالاتفاق (¬٥)؛ لقوله -صلى الله عليه وسلم-: "في الركاز الخمس" (¬٦).
قوله: ولو كان متاعًا.
أي: ولو كان الكنز متاعًا من الأثاث، والسلاح، وأثاث المنازل، ونحوها (¬٧).
_________
(¬١) تبيين الحقائق ١/ ٢٨٩، الهداية ١/ ١١٦، المبسوط ٢/ ٢١٢، تحفة الفقهاء ١/ ٣٢٨، كشف الحقائق ١/ ١٠٨، حاشية الشلبي ١/ ٢٨٩، شرح فتح القدير ٢/ ٢٣٦، العناية ٢/ ٢٣٦.
(¬٢) كتاب الأصل ٢/ ١١٦.
(¬٣) الجامع الصغير ص ٣٣.
(¬٤) وإليه ذهب المالكية، والشافعية، والحنابلة.
جواهر الإكليل ١/ ١٣٧، مواهب الجليل ٢/ ٣٣٩، روض الطالب ١/ ٣٨٥، مغني المحتاج ١/ ٣٩٤، كشاف القناع ٢/ ٢٢٦، منتهى الإرادات ١/ ٣٩٩.
(¬٥) الإجماع لابن المنذر ص ٤٩.
(¬٦) سبق تخريجه في ٣/ ٦٣.
(¬٧) وإليه ذهب الحنابلة.
وذهب المالكية، والشافعية: إلى أنه لا يجب الخمس في الركاز، إلا في الذهب، والفضة خاصة. =','3','65',NULL,NULL,'0');
INSERT INTO [b46] VALUES (741,'والباقي لُقَطَة في الضرب الإسلامي.
وفي الجاهلي هو للواجد، إن كانت الأرض مباحة،
<hr><span data-type="title">منحة السلوك</span>

قوله: والباقي لُقَطة (¬١) في الضرب الإسلامي.
يعني: إذا وجد كنزًا، وعليه علامة الإسلام، كما إذا كانت عليه كلمة الشهادة (¬٢)، يؤخذ الخمس والباقي حكمه حكم اللقطة، في التعريف، والتصدق على نفسه إن كان فقيرًا، وإلا على غيره إن كان غنيًا (¬٣).
قوله: وفي الجاهلي هو للواجد، إن كانت الأرض مباحةً.
يعني: إذا كانت العلامة عليه جاهلية، كما إذا كان نقشها صليبًا، ففيه
_________
= تبيين الحقائق ١/ ٢٦٠، الهداية ١/ ١١٧، المبسوط ٢/ ٢١٢، تحفة الفقهاء ١٢/ ٣٢٨، المعونة ١/ ٣٧٨ أقرب المسالك ص ٤٠، روض الطالب ص ٣٨٦، الوجيز ١/ ٩٧، الإفصاح ١/ ٢١٧، الروض المربع ص ١٥٨.
(¬١) اللقطة: المال الضائع من ربه، سميت لقطة؛ لأن من وجدها يلتقطها.
مختار الصحاح ص ٢٥١ مادة ل ق ط، القاموس المحيط ٤/ ١٦٠ مادة ل ق ط، طلبة الطلبة ص ١٩٢، أنيس الفقهاء ص ١٨٨، المطلع على أبواب المقنع ص ٢٨٢.
(¬٢) أو شيء من القرآن، أو اسم النبي، أو أحد الخلفاء الراشدين، أو أحد ملوك الإسلام.
مغني المحتاج ١/ ٣٩٦، كشاف القناع / ٢٨٨.
(¬٣) وكذا عند المالكية، والشافعية، والحنابلة: حكمه حكم اللقطة، إلا أن حكم اللقطة بعد الحول والتعريف -عند المالكية، والشافعية، والحنابلة-: أن ملتقطها يتملكها، سواء كان غنيًا أو فقيرًا، مع تخييره عند المالكية بين تملكها، أو تركها في يده أمانة، أو الصدقة بها.
الهداية ١/ ١١٦، العناية / ٢٣٧، الوقاية ١/ ١٠٨، تبيين الحقائق ١/ ٢٩٠، حاشية الشلبي ١/ ٢٩٠، المختار ١/ ١١٧، شرح فتح القدير ٢/ ٢٣٧، القوانين ص ٧٠، ٢٢٥ المعونة ١/ ٣٨١، جواهر الإكليل ١/ ١٣٧، بلغة السالك ٢/ ٣٢٤، أقرب المسالك ص ١٦٠، الوجيز ١/ ٢٥٣، روض الطالب ١/ ٣٨٦، التذكرة ص ١٠٩، السراج الوهاج ص ٣١٣، مطالب أولي النهى ٤/ ٢٢٩، الروض المربع ص ١٥٨ و ٣١٨، هداية الراغب ص ١٨١، ٣١٠، الإفصاح ١/ ٢١٧، ٢/ ٦٣.','3','66',NULL,NULL,'0');
INSERT INTO [b46] VALUES (742,'وإن لم تكن فلمالكها أول الفتح، فإن جهل فلأقصى مالك يعرف في الإسلام،
<hr><span data-type="title">منحة السلوك</span>

الخمس بلا خلافٍ؛ لأنه دفين الكفار، وحكمه حكم الغنيمة، وأربعة أخماسه للواجد إذا كانت الأرض مباحةً، مثل المفاوز، والجبال، سواء كان الواجد حُرًّا، أو عبدًا مسلمًا، أو ذِمِّيًا صغيرًا، أو كبيرًا غنيًا، أو فقيرًا؛ لأنهم من أهل الغنيمة (¬١).
قوله: وإن لم تكن. أي: وإن لم تكن الأرض مباحةً، مثل ما إذا كانت مملوكةً، ففيه الخمس أيضًا، وأربعة أخماسه لمالكها أول الفتح، وهو الذي يسمى المخبط له، وهو الذي خصه الإمام بتمليك هذه البقعة حين فتح أهل الإسلام تلك البلدة، أو لورثته إن عرفوا (¬٢). والمصنف ترك هذا. فإن جهل مالكها أول الفتح، وورثته فلأقصى مالك الأرض يعرف في الإسلام، أو ورثته، وإن لم يعرفوا فلبيت المال (¬٣).
_________
(¬١) وإليه ذهب المالكية، والشافعية، والحنابلة.
بدائع الصنائع ٢/ ٦٥، تحفة الفقهاء ١/ ٣٢٨، تبيين الحقائق ١/ ٢٨٩، الهداية ١/ ١١٦، كشف الحقائق ١/ ١٠٨، شرح فتح القدير ٢/ ٢٣٧، العناية ٢/ ٢٣٧، الاختيار ١/ ١٠٨، القوانين الفقهية ص ٧٠، مواهب الجليل ٢/ ٣٣٩، نهاية المحتاج ٣/ ٩٨، حاشية الشبراملسي ٣/ ٩٨، هداية الراغب ص ١٨١، غاية المنتهى ٢/ ٨١.
(¬٢) شرح فتح القدير ٢/ ٢٣٧، تحفة الفقهاء ١/ ٣٢٨، الاختيار ١/ ١١٨ العناية ٢/ ٢٣٧، الوقاية ١/ ١٠٨، كشف الحقائق ١/ ١٠٨، تبيين الحقائق ١/ ٢٨٩، حاشية الشلبي ١/ ٢٨٩، الهداية ١/ ١١٦.
(¬٣) وذهب المالكية: إلى أن باقيه بعد إخراج الخمس، يكون لمالك الأرض، بإحياء، أو بإرث منه، لا لواجده، ولا لمالكها بشراء، أو هبة، بل للبائع الأصلي، أو الواهب، فإن علم وإلا فلقطة.
وقيل: لمالكها في الحال مطلقًا. والأول: هو الأصوب عندهم.
وعند الشافعية: إن وجد في ملك شخص، فللشخص إن ادعاه يأخذه بلا يمين، وإن لم =','3','67',NULL,NULL,'0');
INSERT INTO [b46] VALUES (743,'فإن خفي (¬١) الضرب جعل جاهليًا. ولا شيء في الفيروزج،
<hr><span data-type="title">منحة السلوك</span>

قوله: فإن خفي الضرب، بأن اشتبه عليهم، جعل جاهليًا في ظاهر المذهب؛ لأنه الأصل (¬٢).
وقيل: يجعل إسلاميًا في زماننا؛ لتقادم العهد (¬٣).

<span data-type="title" id=toc-377>[زكاة الأحجار الكريمة]</span>
قوله: ولا شيء في الفيروزج.
_________
= يدع فلمن مُلك منه، وتقوم ورثته مقامه بعد موته، وهكذا يجري ما تقرر حتى ينتهي الأمر إلى المحيي للأرض، فهو يملكه وإن لم يدعه.
وعند الحنابلة: إن علم مالك الأرض، أو كانت الأرض منتقلة إلى واجد الركاز، فهو لواجده.
شرح فتح القدير ٢/ ٢٣٧، الهداية ١/ ١١٦، الوقاية ١/ ١٠٨، العناية ٢/ ٢٣٧، كشف الحقائق ١/ ١٠٨، تبيين الحقائق ١/ ٢٨٩ حاشية الشلبي ١/ ٢٨٩، المختار ١/ ١١٨، تحفة الفقهاء ١/ ٣٢٨، الشرح الصغير ١/ ٢٣١، الشرح الكبير للدردير ١/ ٤٩٠، جواهر الإكليل ١/ ١٣٧، مغني المحتاج ٣٩٦، أسنى المطالب ١/ ٣٨٧، نهاية المحتاج ٣/ ٩٩، كشاف القناع ٢/ ٢٢٧، منتهى الإرادات ١/ ٤٠٠، مطالب أولي النهى ٢/ ٨١.
(¬١) في د "اشتبه".
(¬٢) تبيين الحقائق ١/ ٢٩٠، الهداية ١/ ١١٧، العناية ٢/ ٢٣٨، شرح فتح القدير ٢/ ٢٣٨، ملتقى الأبحر ١/ ١٨٤، البحر الرائق ٢/ ٢٣٥، الدر المختار ٢/ ٣٢٣.
(¬٣) وذهب المالكية: إلى أنه إن كان هناك شك في جاهلية وغيرها، فركاز، يخمس، والباقي لواجده.
وعند الشافعية، والحنابلة: إن لم يعلم من أي الضربين هو، أو لم تكن عليه علامة، كالتِّبر، والأواني، والحلي، والسبائك، أو كان مثله يضرب في الجاهلية والإسلام، فهو لقطة.
تبيين الحقائق ١/ ٢٩٠ شرح فتح القدير ٢/ ٢٣٨، ملتقى الأبحر ١/ ١٨٤، حاشية رد المحتار ٢/ ٣٢٣، البحر الرائق ٢/ ٢٣٥، العناية ٢/ ٢٣٨، الهداية ١/ ١١٧، الشرح الصغير ١/ ٢٣١، منح الجليل ٢/ ٨١، مغني المحتاج ١/ ٣٩٦، أسنى المطالب ١/ ٣٨٦، منتهى الإرادات ١/ ٣٩٩، الإقناع للحجاوي ٢/ ٢٢٨، المستوعب ٣/ ٣٠٥.','3','68',NULL,NULL,'0');
INSERT INTO [b46] VALUES (744,'والياقوت، واللؤلؤ، والعنبر.
<hr><span data-type="title">منحة السلوك</span>

أي: لا خمس، وهو حجر مضيء يوجد في الجبال (¬١)؛ لقوله -صلى الله عليه وسلم-: "لا خمس في الحجر" (¬٢).
وكذا لا يجب في الياقوت، واللؤلؤ، والعنبر (¬٣)، والزُّمرد (¬٤)، وجميع الجواهر، والفصوص (¬٥) من الحجارة (¬٦).
وعند أبي يوسف: يخمس العنبر، واللؤلؤ، وكل حليةٍ تستخرج من البحر (¬٧).
_________
(¬١) المبسوط ٢/ ٢١٣، كنز الدقائق ١/ ٢٩٠.
(¬٢) أخرجه ابن عدي في الكامل ٥/ ٢٢ من رواية عمر الكلاعي عن عمرو بن شعيب عن أبيه عن جده رفعه "لا زكاة في حجر" قال في الدراية ١/ ٢٦٢: وعمر ضعيف وتابعه العزرمي عن عمرو وهو أضعف منه.
(¬٣) وهو نوع من الطيب، صلب لا طعم له ولا ريح، إلا إذا سحق، أو أحرق. يقال: إنه روث دابة بحرية.
المعجم الوسيط ٢/ ٦٣٠ مادة العنبر، المطلع ص ١٣٣، الدر النقي ٣/ ٦٩٦.
(¬٤) الزُّمرد -بالضم-: واحدته زمردة، حجر أخضر شفاف، شديد الخضرة.
المعجم الوسيط ١/ ٤٠٠ مادة الزمرد، معجم لغة الفقهاء ص ٢٣٣، المغرب ص ٣٤٤ مادة الزمردة.
(¬٥) فص الخاتم: ما يركب فيه من غيره، جمعه فصوص.
القاموس المحيط ٣/ ٤٩٥ مادة ف ص ص، لسان العرب ٧/ ٦٧ مادة فصص، المصباح المنير ٢/ ٤٧٤ مادة فصُّ، مختار الصحاح ص ٢١١ مادة ف ص ص.
(¬٦) العناية ٢/ ٢٤٠، شرح فتح القدير ٢/ ٢٤٠.
(¬٧) وذهب أبو حنيفة، ومحمد، ومالك، والشافعي، وأحمد: إلى أن ما يستخرج من البحر، كالمسك، والعنبر، واللؤلؤ، والمرجان، وغيرها، لا شيء فيها بحال.
العناية ٢/ ٢٤٠، تحفة الفقهاء ١/ ٣٣٢، الهداية ١/ ١١٧، التلقين ص ٤٦، التفريع ١/ ٢٧٨، المنهاج ١/ ٤٦٥، زاد المحتاج ١/ ٤٦٥، الإنصاف ٣/ ١٢٢، الإقناع للحجاوي ٢/ ٢٢٥.','3','69',NULL,NULL,'0');
INSERT INTO [b46] VALUES (745,'وفي الزئبق الخُمُس.
<hr><span data-type="title">منحة السلوك</span>

قوله: وفي الزئبق (¬١): الخُمُس (¬٢).
خلافًا لأبي يوسف، فهو جعله كالقير (¬٣)، والنفط (¬٤)، وهما كالرصاص (¬٥).
_________
(¬١) وهو: عنصر فلزي، سائل في درجة الحرارة العادية. فارسي معرب.
المعجم الوسيط ١/ ٣٨٧ مادة الزئبق، كتاب الجوهرتين العتيقتين المائعتين الصفراء والبيضاء ص ١٦٦، المعرب ص ٣٤٦ مادة الزئبق.
(¬٢) في قول: أبي حنيفة الآخر، وكان يقول أولًا: لا خمس فيه، وهو قول: أبي يوسف الأول، ثم رجع وقال: فيه الخمس، فإن أبا يوسف قال: سألت أبا حنيفة عن الزئبق، فقال: لا خمس فيه، فلم أزل به حتى قال: فيه الخمس، وكنت أظن أنه مثل الرصاص والحديد، ثم بلغني بعد ذلك أنه ليس كذلك، وهو بمنزلة القير والنفط.
المبسوط ٢/ ٢١٣، بدائع الصنائع ٢/ ٦٧، شرح فتح القدير ٢/ ٢٣٩، العناية ٢/ ٢٣٩، الهداية ١/ ١١٧، تبيين الحقائق ١/ ٢٩٠.
(¬٣) القار، والقير: وهو شيء أسود يطلى به السفن. وقيل: هو الزفت. وهو معرب.
القاموس المحيط ٣/ ٧٢٢ مادة ق ي ر، مجمل اللغة ص ٥٨٤ باب القاف والألف وما يثلثها مادة قار، المعجم الوسيط ١/ ٣٩٥ مادة الزفت، المعرب ص ٥١٠ مادة القار.
(¬٤) النفط: القطران، وهو حلابة جبل في قعر بئر توقد فيه النار، ويطلى به الإبل للجرب وغيره. ويحصل عليه بتقطير زيت البترول الخام، أو قطران الفحم الحجري، وهو سريع الاشتعال، من الوقود.
لسان العرب ٧/ ٤١٦ مادة نفط، القاموس المحيط ٤/ ٤١٧ مادة ن ف ط، مختار الصحاح ص ٢٨٠ مادة ن ف ط، المعجم الوسيط ٢/ ٩٤١ مادة النفط.
(¬٥) المبسوط ٢/ ٢١٣، بدائع الصنائع ٢/ ٦٧، تبيين الحقائق ١/ ٢٩٠، الهداية ١/ ١١٧ شرح فتح القدير ٢/ ٢٣٩، العناية ٢/ ٢٣٩.','3','70',NULL,NULL,'0');
INSERT INTO [b46] VALUES (746,'<span data-type="title" id=toc-378>باب زكاة النبات</span>
يجب عُشْر كل نابت (¬١) بماء السماء، أو سيحًا،
<hr><span data-type="title">منحة السلوك</span>

قوله: زكاة النبات.
أي: هذا بيان أحكام زكاة النبات، وهي العشر.

<span data-type="title" id=toc-379>[إخراج العشر في النبات]</span>
قوله يجب عشر كل نابتٍ بماء السماء، أو سيحًا (¬٢).
_________
(¬١) في ب زيادة "سُقي".
(¬٢) اختلف العلماء في الجنس الذي تجب فيه الزكاة من النبات ما هو؟
فذهب الحنفية: إلى أن الزكاة تجب في كل ما أخرجت الأرض، من قليله وكثيره العشر، سواء سقي سيحًا، أو سقته السماء، إلا الحطب، والحشيش، والقصب خاصة.
وذهب المالكية، والشافعية: إلى أن الجنس الذي تجب فيه الزكاة هو ما ادخر، واقتيت، كالحنطة، والشعير، والأرز، وغيره.
وذهب الحنابلة: إلى أن العشر يجب في كل ما يكال ويدخر، من الزروع والثمار.
وفائدة الخلاف بين المالكية، والشافعية، والحنابلة: أن الحنابلة يجب عندهم العشر في السمسم، وبذر الكتان، والكمون، والكراويا، والخردل، واللوز، والفستق.
وعند المالكية، والشافعية: لا تجب فيه الزكاة.
وفائدة الخلاف مع أبي حنيفة: أن الزكاة تجب في الخضروات كلها عند الحنفية.
وعند المالكية، والشافعية، والحنابلة: لا زكاة فيها.
ومقدار الواجب فيما يجب فيه الزكاة من ذلك، عند أبي حنيفة، ومالك، والشافعي، وأحمد، -على اختلافهم فيه كما ذكرنا- العشر، مع كونه يسقى سيحًا بغير مؤنة، أو كان سقيه من السماء، وإن كان يسقى بالنواضح والكلف، فنصف العشر.
المختار ١/ ١١٣، كنز الدقائق ١/ ٢٩١، البحر الرائق ٢/ ٢٣٦ ملتقى الأبحر ١/ ١٨٥، الاختيار ١/ ١١٣، بداية المبتدي ١/ ١١٧، الوقاية ١/ ١٠٩، الهداية ١/ ١١٧، كشف الحقائق ١/ ١٠٩ الكتاب ١/ ١٥٠، التلقين ص ٥٠، الذخيرة ٤/ ٧٣، التذكرة ص ٧١، المهذب ١/ ١٥٣، الروض المربع ص ١٥٥، المحرر ١/ ٢٢٠ الإفصاح ١/ ٢٠٥.','3','71',NULL,NULL,'0');
INSERT INTO [b46] VALUES (747,'إلا الحطب والقصب والحشيش،
<hr><span data-type="title">منحة السلوك</span>

المراد من ماء السماء: المطر، ومن السيح: الماء الجاري (¬١)؛ وذلك لقوله تعالى: {وَآتُوا حَقَّهُ يَوْمَ حَصَادِهِ} [الأنعام: ١٤١]. وانتصاب "سيحًا"، بنزع الخافض، تقديره: أو بسيح.
قوله: إلا الحطب، والقصب (¬٢)، والحشيش (¬٣).
يعني: لا شيء فيها؛ لأن سبب العشر الأرض النامية، وهذه الأشياء إذا غلبت على الأرض أفسدتها، ولا يحصل بها النماء (¬٤).
قال في خلاصة الفتاوى (¬٥): "لا عشر في الطرفاء (¬٦)، وشجر القطن،
_________
(¬١) مختار الصحاح ص ١٣٦ مادة س ي ح، القاموس المحيط ٢/ ٦٥٥ مادة س ي ح، مجمل اللغة ص ٣٦٤ باب السين والياء وما يثلثها مادة سيح، المغرب ص ٢٤١ مادة ساح، فقه اللغة وسر العربية للثعالبي ص ٢٨٥.
(¬٢) القصب هو: كل نبت كان ساقه أنابيب، وكعوبًا. والواحدة قصبة. وقيل: هو الكبير النابت في الغيضة.
المغرب ص ٣٨٤ مادة القصب، مختار الصحاح ص ٢٢٤ مادة ق ص ب، القاموس المحيط ٣/ ٦٢٧ مادة ق ص ب، المصباح المنير ٢/ ٥٠٤ مادة قصبتُ.
(¬٣) الحشيش هو: اليابس من الكلأ.
المغرب ص ١١٦ مادة الحشيش، مختار الصحاح ص ٥٨ مادة ح ش ش، القاموس المحيط ١/ ج ٦٤٨ مادة ح ش ش، لسان العرب ٦/ ٢٨٢ مادة حشش.
(¬٤) شرح فتح القدير ٢/ ٢٤٢، تحفة الفقهاء ١/ ٣٢١، الاختيار ١/ ١١٣، الكتاب ١/ ١١٣ شرح الوقاية ١/ ١٠٩، الهداية ١/ ١١٨، كشف الحقائق ١/ ١٠٩.
(¬٥) لطاهر بن أحمد البخاري ١/ ٢٤٧.
(¬٦) الطرفاء: شجرة، الواحدة طرفة، وهي من العضاة، وهدبه مثل هدب الأثل، وليس له خشب. وقد تتحمض به الإبل إذا لم تجد حمضًا غيره.
مجمل اللغة ص ٤٥٨ باب الطاء والراء وما يثلثهما مادة طرف، لسان العرب ٩/ ٢٢٠ =','3','72',NULL,NULL,'0');
INSERT INTO [b46] VALUES (748,'من غير شرط نصاب، أو حول، أو عقل، أو بلوغ.
<hr><span data-type="title">منحة السلوك</span>

والباذنجان، ولا عشر في الأدوية، كالهليلج (¬١)، ولا في الكندر (¬٢)، والصمغ" (¬٣).
قوله: من غير شرط نصابٍ، أو حولٍ، أو عقل، أو بلوغ.
هذا يتعلق بقوله: يجب عشر كل نابتٍ، وهذا عند أبي حنيفة.
والخلاف ههنا في موضعين: في اشتراط النصاب (¬٤)، وفي اشتراط البقاء. فعند أبي حنيفة: لا يشترط واحد منهما (¬٥)، وعندهما: كلاهما
_________
= مادة طرف، مختار الصحاح ص ١٦٤ مادة ط ر ف، القاموس المحيط ٣/ ٦٨ مادة ط ر ف، المصباح المنير ٢/ ٣٧١ مادة طرف.
(¬١) الهليلج، والإهليلج، والإهليجة، معرب عقر من الأدوية. وهو ثمر، منه أصفر، ومنه أسود، وهو البالغ النضج.
تاج العروس ٢/ ١١٦ مادة هلج، لسان العرب ٢/ ٣٩٢ مادة هلج، القاموس المحيط ١/ ١٩٣ مادة الإهليلج، المغرب ص ٥٠٤ مادة الهليلج، المعرب ص ١٣٣ مادة الإهليلج.
(¬٢) الكندر: اللبان، وقيل: ضرب من العلك، وهو نافع لقطع البلغم جدًا.
تاج العروس ٣/ ٥٣٩ مادة كندر، لسان العرب ٤/ ١٥٣ مادة كندر، المصباح المنير ٢/ ٥٤٨ مادة اللَّبنُ، القاموس المحيط ٤/ ٨٦ مادة ك ن د ر.
(¬٣) الصمغ: هو شيء ينضحه الشجر ويسيل منها، وأنواعه كثيرة، وهو في بلاد العرب.
لسان العرب ٨/ ٤٤١ مادة صمغ، مجمل اللغة ص ٤١٨ باب الصاد والميم وما يثلثها مادة صمغ، مختار الصحاح ص ١٥٥ ص م غ، القاموس المحيط ٢/ ٨٥٢ مادة ص م غ.
(¬٤) وإلى اشتراط النصاب: ذهب المالكية، والشافعية، والحنابلة، ونصابه خمسة أوسق، ببدو صلاح الثمر، واشتداد الحب.
سراج السالك ١/ ١٩٧، أقرب المسالك ص ٣٧، تحفة المحتاج ٣/ ٢٤٤، ٢٥٤ حاشية الشرواني ٣/ ٢٤٤، ٢٥٤، الروض المربع ص ١٥٦، منتهى الإرادات ١/ ٣٨٨.
(¬٥) الهداية ١/ ١١٧، بدائع الصنائع ٢/ ٥٩ الكتاب ١/ ١٥٠، المختار ١/ ١١٣، كشف الحقائق ١/ ١٠٩، شرح الوقاية ١/ ١٠٩ تبيين الحقائق ١/ ٢٩٢، ملتقى الأبحر ١/ ١٨٥.','3','73',NULL,NULL,'0');
INSERT INTO [b46] VALUES (749,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

شرط (¬١).
لهما في الأول قوله -صلى الله عليه وسلم-: "ليس في حبٍ، ولا تمرٍ صدقة، حتى يبلغ خمسة أوسقٍ" رواه مسلم (¬٢). وقوله -صلى الله عليه وسلم-: "ليس فيما دون خمسة أوسقٍ صدقة" رواه أبو داود (¬٣).
وله قوله -صلى الله عليه وسلم-: "فيما سقت السماء والغيم العشر، وفيما سقي بالساقية نصف العشر" رواه مسلم، وغيره (¬٤)، وقوله -صلى الله عليه وسلم-: "فيما سقت السماء، والعيون، أو كان عثريًا (¬٥) العشر، وفيما سقي بالنضح (¬٦) نصف العشر" رواه
_________
(¬١) الهداية ١/ ١١٧، بدائع الصنائع ٢/ ٥٩، تبيين الحقائق ١/ ٢٩٢، ملتقى الأبحر ١/ ١٨٦، الكتاب ١/ ١٥٠، الاختيار ٢/ ١١٣، شرح فتح القدير ٢/ ٢٤٢، العناية ٢/ ٢٤٢.
(¬٢) ٢/ ٦٧٤ في مقدمة كتاب الزكاة رقم ٥ (٩٧٩) عن أبي سعيد الخدري -رضي الله عنه-.
(¬٣) ٢/ ٩٤ كتاب الزكاة باب ما تجب فيه الزكاة رقم ١٥٥٨. ورواه أيضًا البخاري ٢/ ٥٢٤ كتاب الزكاة، باب زكاة الورق رقم ١٣٧٨، ومسلم ٢/ ٦٧٣ أول كتاب الزكاة رقم ٩٧٩ عن أبي سعيد الخدري -رضي الله عنه-، وتمامه: "ليس فيما دون خمس ذود صدقة، وليس فيما دون خمس أواق صدقة، وليس فيما دون خمسة أوسق صدقة".
(¬٤) رواه مسلم ٢/ ٦٧٥ كتاب الزكاة، باب ما فيه العشر أو نصف العشر رقم ٩٨١، ورواه أيضًا أحمد ٣/ ٣٤١، وابن خزيمة ٤/ ٣٨ كتاب الزكاة، باب ذكر مبلغ الواجب من الصدقة في الحبوب والثمار رقم ٢٣٠٩، والطحاوي في الشرح ٢/ ٣٧ كتاب الزكاة، باب زكاة ما يخرج من الأرض، والبيهقي ٤/ ١٣٠ كتاب الزكاة، باب قدر الصدقة فيما أخرجت الأرض كلهم بلفظ: "فيما سقت الأنهار والغيم العشر ... ".
(¬٥) أي: ما سقته السماء.
القاموس المحيط ٣/ ١٥٤ مادة ع ث ر، لسان العرب ٤/ ٥٤١ مادة عثر، مجمل اللغة ص ٥٠٠ باب العين والثاء وما يثلثهما مادة عثر، المصباح المنير ٢/ ٣٩٣ مادة عثر.
(¬٦) أي: سقاها بالسانية. والناضح: البعير يُستسقى عليه.
القاموس المحيط ٤/ ٣٨٦ مادة ن ض ح، مختار الصحاح ص ٢٧٧ مادة ن ض ح، المغرب ص ٤٥٤ مادة النضح، المصباح المنير ٢/ ٦٠٩ مادة نضحتُ.','3','74',NULL,NULL,'0');
INSERT INTO [b46] VALUES (750,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

الجماعة غير مسلمٍ (¬١). كل ذلك بلا فصلٍ بين القليل والكثير، وتأويل ما رويناه زكاة التجارة (¬٢).
ولهما في الثاني قوله -صلى الله عليه وسلم-: "ليس في الخضراوات صدقة" (¬٣) والزكاة ليست بمرادةٍ، فتعين العشر (¬٤).
وله: ما روينا؛ ولأن السبب هي الأرض النامية، وقد سمي بما لا يبقى
_________
(¬١) البخاري ٢/ ٥٤٠ كتاب الزكاة، باب العشر فيما يسقى من ماء السماء، وبالماء الجاري رقم ١٤١٢، وابن ماجه ١/ ٥٨١ كتاب الزكاة، باب صدقة الزروع والثمار رقم ١٨١٧، وأبو داود ٢/ ١٠٨ كتاب الزكاة، باب صدقة الزرع رقم ١٥٩٧، والترمذي ٢/ ٢١٢ كتاب الزكاة، باب ما جاء في الصدقة فيما يسقى بالأنهار وغيره رقم ٦٣٩، والنسائي ٥/ ٤١ كتاب الزكاة، باب ما يوجب العشر، وما يوجب نصف العشر رقم ٢٤٨٨.
(¬٢) الهداية ١/ ١١٨، بدائع الصنائع ٢/ ٥٩، تبيين الحقائق ١/ ٢٩٢، حاشية الشلبي ١/ ٢٩٢ شرح فتح القدير ٢/ ٢٤٣، العناية ٢/ ٢٤٣.
(¬٣) رواه الترمذي ٢/ ٢١١ رقم ٦٣٣ كتاب الزكاة، باب ما جاء في زكاة الخضروات عن الحسن بن عمارة عن محمد بن عبد الرحمن بن عبيد عن عيسى بن طلحة عن معاذ.
قال الترمذي: إسناد هذا الحديث ليس بصحيح، وليس يصح في هذا الباب عن النبي -صلى الله عليه وسلم- شيء، وإنما يروى هذا عن موسى بن طلحة، عن النبي -صلى الله عليه وسلم- مرسلًا، والحسن بن عمارة، ضعيف عند أهل الحديث.
ورواه البزار كما في الكشف ٢/ ٤١٩ كتاب الزكاة، باب ما لا زكاة فيه رقم ٨٨٥، وابن عدي في الكامل ٦١٠، والدارقطني ٢/ ٩٧ كتاب الزكاة باب ليس في الخضروات صدقة رقم ١٠، ويحيى بن آدم في الخراج رقم ٥٠٣.
قال في الدراية ١/ ٢٦٣: وفي الباب عن علي، وعائشة، ومحمد بن جحش عند الدارقطني، وكلها أسانيدها ضعيفة.
وقال في التلخيص الحبير ٢/ ١٦٥: وهو ضعيف.
(¬٤) الهداية ١/ ١١٨، بدائع الصنائع ٢/ ٥٩، تبيين الحقائق ١/ ٢٩٢، حاشية الشلبي ١/ ٢٩٢، شرح فتح القدير ٢/ ٢٤٣، العناية ٢/ ٢٤٣.','3','75',NULL,NULL,'0');
INSERT INTO [b46] VALUES (751,'فإن جعل أرضه محطبة، أو مقصبة، أو محتشًا، وجب فيه العشر.
وما سُقي بغرب، أو دالية، ففيه نصف العشر، وإن سُقي سيحًا وبدالية حُكِم بأكثر الحول،
<hr><span data-type="title">منحة السلوك</span>

فيجب العشر، كالخراج (¬١).
وما روياه، ليس بثابتٍ (¬٢).
قوله: فإن جعل أرضه محطبة، أو مقصبة، أو محتشًا، وجب فيه العشر؛ لأنه حينئذ يقصد بها الاستغلال (¬٣).

<span data-type="title" id=toc-380>[الاختلاف في السقي]</span>
قوله: وما سقي بغرب، أو دالية، ففيه نصف العشر (¬٤)؛ لما روينا.
والغرب: الدلو العظيمة (¬٥)، والدالية: الدولاب، وهي: الناعورة (¬٦).
قوله: وإن سقي سيحًا وبدالية، حكم بأكثر الحول.
_________
(¬١) الهداية ١/ ١١٨، بدائع الصنائع ٢/ ٥٩، شرح فتح القدير ٢/ ٢٤٣، حاشية الشلبي ١/ ٢٩٢، العناية ٢/ ٢٤٣، تبيين الحقائق ١/ ٢٩٢.
(¬٢) ففيه ضعف، كما سبق في الحكم عليه في ٣/ ٧٣.
(¬٣) تببين الحقائق ١/ ٢٩٢، العناية ٢/ ٢٤٥، بدائع الصنائع ٢/ ٥٩، شرح فتح القدير ٢/ ٢٤٣، الهداية ١/ ١١٨، الدر المختار ٢/ ٣٢٧، حاشية رد المحتار ٢/ ٣٢٧.
(¬٤) بداية المبتدي ١/ ١١٨، المختار ١/ ١١٣، الهداية ١/ ١١٨، شرح فتح القدير ٢/ ٢٤٦، العناية ٢/ ٢٤٦.
(¬٥) المغرب ص ٣٣٨ مادة الغرب، مختار الصحاح ص ١٩٧ مادة غ ر ب، طلبة الطلبة ص ٤٨، المصباح المنير ٢/ ٤٤٤ مادة غربت.
(¬٦) والناعورة: دولاب ذو دلاء أو نحوها يدور بدفع الماء أو جر الماشية فيخرج الماء من البئر أو النهر إلى الحقل.
المصباح المنير ١/ ١٩٩ مادة الدلو، القاموس المحيط ٢/ ٢٠٨ مادة د ل و، المغرب ص ١٦٦ مادة الدالية، لسان العرب ١٤/ ٢٦٦ مادة د لا، المطلع على أبواب المقنع ص ١٣١، المعجم الوسيط ص ٩٣٤.','3','76',NULL,NULL,'0');
INSERT INTO [b46] VALUES (752,'وفي العسل العشر،
<hr><span data-type="title">منحة السلوك</span>

يعني: إذا سقيت الأرض بالماء الجاري، والدالية جميعًا، حكم بأكثر الحول (¬١)، فإن كان السقي بالسيح في أكثر الحول ففيها العشر كاملًا، وإن كان بالدالية ففيها نصف العشر (¬٢).

<span data-type="title" id=toc-381>[زكاة العسل]</span>
قوله: وفي العسل العشر.
هذا إذا أخذ من الأرض العشرية، وإن أخذ من أرض الخراج، فلا شيء فيه (¬٣).
_________
(¬١) تحفة الفقهاء ١/ ٣٢٢، بداية المبتدي ١/ ١١٨، تبيين الحقائق ١/ ٢٩٣، الهداية ١/ ١١٨، تنوير الأبصار ٢/ ٣٢٨، الدر المختار ٢/ ٣٢٨.
(¬٢) وذهب المالكية: إلى أنه إن سقي الزرع بهما، فعلى حكميهما، فيؤخذ لما سقي بالعشر، ولما سقي بآلة نصفه. وهل إذا كان أحدهما ثلثين، والآخر ثلثًا، يغلب الأكثر على الأقل، ويزكى كل على حكمه؟ فيه قولان: عندهم، فقيل: يغلب الأكثر، وقيل: يجعل كل على حكمه.
وعند الشافعية: إذا سقي الزرع بماء السماء والدواليب، وجب إخراج الزكاة بالقسط، فإن كان النصف بهذا، أو النصف بهذا وجب ثلاثة أرباع العشر، أو ثلثاه بماء السماء وثلثه بالدولاب، وجب خمسة أسداس العشر، وفي عكسه ثلثا العشر. ولو سقي بهما وجهل المقدار، وجب ثلاثة أرباع العشر.
وذهب الحنابلة: إلى أنه يجب العشر فيما سقي بلا مؤنة، ونصف العشر مع المؤنة، وثلاثة أرباعه بهما، فإن تفاوتا فالاعتبار بأكثرهما نفعًا، ومع الجهل العشر.
بدائع الصنائع ٢/ ٦٢ تحفة الفقهاء ١/ ٣٢٢، الهداية ١/ ١١٨، تنوير الأبصار ٢/ ٣٢٨، الدر المختار ٢/ ٣٢٨، تبيين الحقائق ١/ ٢٩٣، الشرح الكبير للدردير ١/ ٤٤٩، حاشية الدسوقي ١/ ٤٤٩، منح الجليل ٢/ ٣٠، أسنى المطالب ١/ ٣٧١، مغني المحتاج ١/ ٣٨٥، المقنع ١/ ٣١٩، الروض المربع ص ١٥٧.
(¬٣) وذهب المالكية، والشافعي في القول الجديد: إلى أنه لا زكاة في العسل.
وذهب الحنابلة، وهو القول القديم للشافعي: أنه يجب في العسل العشر، سواء أخذه من موات، أو من أرض مملوكة له، أو لغيره، عشرية، أو خراجية. =','3','77',NULL,NULL,'0');
INSERT INTO [b46] VALUES (753,'ولو وُجد في الجبل ففيه العشر، كالثمر،
<hr><span data-type="title">منحة السلوك</span>

قوله: ولو وجد في الجبل، واصلًا بما قبله.
أي: ولو وجد العسل في الجبل، ففيه العشر، كالثمر الموجود فيه؛ لأنه مال (¬١).
وعن أبي يوسف، والحسن: إذا وجد في الجبال، والمفاوز على الأشجار، والكهوف، فلا شيء فيه، وهو بمنزلة الثمار تكون في الجبال، والأودية، ولا خراج فيها، ولا عشر (¬٢)، ثم إذا وجب العشر في العسل في أرض العشر، فعند أبي حنيفة: يجب في قليله، وكثيره (¬٣). وعند أبي يوسف: إذا بلغ قيمة خمسة أوسق ففيه العشر (¬٤)، وعنه: لا شيء فيه حتى يبلغ عشر قرب، كل قربةٍ خمسون مَنًّا (¬٥)، وعنه: لا شيء فيه حتى يبلغ
_________
= الأصل ١/ ١١٦، الجامع الصغير ص ١٣٣، الهداية ١/ ١١٨، تبيين الحقائق ١/ ٢٩١، تنوير الأبصار ٢/ ٣٢٥، الدر المختار ٢/ ٣٢٥، كشف الحقائق ١/ ١٠٩، التلقين ص ٤٦، التفريع ١/ ٢٩٤، حلية العلماء ٣/ ٧٣، رحمة الأمة ١/ ١٠٠، أسنى المطالب ١/ ٣٦٨، الإفصاح ١/ ٢١٥، شرح منتهى الإرادات ١/ ٣٩٦، المبدع ٢/ ٣٥٥.
(¬١) الهداية ١/ ١١٩، الأصل ١/ ١٣٤، كشف الحقائق ١/ ١٠٩، الوقاية ١/ ١٠٩، تنوير الأبصار ٢/ ٣٢٥، شرح فتح القدير ٢/ ٢٤٦، العناية ٢/ ٢٤٦، المختار ١/ ١١٤.
(¬٢) شرح فتح القدير ٢/ ٢٤٩، العناية ٢/ ٢٤٩، الاختيار ١/ ١٤، حاشية رد المحتار ٢/ ٣٢٥، الهداية ١/ ١١٩، تبيين الحقائق ١/ ٢٩٣.
(¬٣) الأصل ١/ ١٣٢، تبيين الحقائق ١/ ٢٩٣، حاشية رد المحتار ٢/ ٣٢٥، العناية ٢/ ٢٤٧، الاختيار ١/ ١١٤.
(¬٤) وهو نصاب عند الشافعي في قوله القديم الموجب فيه الزكاة.
تبيين الحقائق ١/ ٢٩٣، الكتاب ١/ ١٥٢، شرح فتح القدير ٢/ ٢٤٨، العناية ٢/ ٢٤٨، الاختيار ١/ ١١٤، حاشية الشلبي ١/ ٢٩٢، البحر الرائق ٢/ ٢٣٧، نهاية المحتاج ٣/ ٧٢، مغني المحتاج ١/ ٣٨٢.
(¬٥) الهداية ١/ ١١٨، تبيين الحقائق ١/ ٢٩٣، العناية ٢/ ٢٤٩، الاختيار ١/ ١١٤، البحر الرائق ٢/ ٢٣٧.','3','78',NULL,NULL,'0');
INSERT INTO [b46] VALUES (754,'ولا يطرح أجر العمال، ونفقة البقر قبل العشر. ولا شيء في<span data-type="title" id=toc-382> القير، والنفط</span>.
<hr><span data-type="title">منحة السلوك</span>

خمسة أمناء (¬١). وعند محمدٍ: إذا بلغ خمسة أفراق، والفَرَق ستة وثلاثون رطلًا بالعراقي، ففيه العشر (¬٢).
قوله: ولا يطرح أجر العمال، ونفقة البقر قبل العشر؛ لإطلاق ما تلونا، وما روينا (¬٣)، وكذلك في كري الأنهار، وأجرة الحافظ، ونحوهما (¬٤).

[القير والنفط]
قوله: ولا شيء في القير، والنفط. هذا إذا كان في أرض عشر (¬٥)، ولو كان في أرض خراج يجب الخراج (¬٦)، لأنهما ليسا من الأموال، وإنما هما عين فوارةٍ، كعين الماء، غير أنه إن كان حريمه يصلح للزراعة يجب فيها الخراج، وإلا فلا خراج فيه أيضًا (¬٧). والقير: الزفت، ويقال: القار، والنفط
_________
(¬١) الهداية ١/ ١١٨، بدائع الصنائع ٢/ ٦١، العناية ٢/ ٢٤٩، تبيين الحقائق ١/ ٢٩٣ الاختيار ١/ ١١٤، البحر الرائق ٢/ ٢٣٧.
(¬٢) وعند الحنابلة: نصابه مائة وستون رطلًا عراقية، وذلك عشرة أفراق.
الكتاب ١/ ١٥٢، بدائع الصنائع ٢/ ٦١، العناية ٢/ ٢٤٩، الهداية ١/ ١١٩، تبيين الحقائق ١/ ٢٩٣، الاختيار ١/ ١١٤، البحر الرائق ٢/ ٢٣٧، منتهى الإرادات، ١/ ٣٩٧، المقنع ١/ ٣٢٤.
(¬٣) بداية المبتدي ١/ ١١٩، شرح فتح القدير ٢/ ٢٥٠، الهداية ١/ ١١٩ كشف الحقائق ١/ ١٠٩، تبيين الحقائق ١/ ٢٩٤ كنز الدقائق ١/ ٢٩٤، حاشية الشلبي ١/ ٢٩٤، البحر الرائق ٢/ ٢٣٨.
(¬٤) شرح فتح القدير ٢/ ٢٥٠، تبيين الحقائق ١/ ٢٩٤، حاشية الشلبي ١/ ٢٩٤، البحر الرائق ٢/ ٢٣٨.
(¬٥) الأصل ١/ ١١٩، المبسوط ٢/ ٢١٦، بدائع الصنائع ٢/ ٦٢، كنز الرقائق ١/ ٢٩٦، تبيين الحقائق ١/ ٢٩٦، حاشية الشلبي ١/ ٢٩٦، كشف الحقائق ١/ ١١٠، الوقاية ١/ ١١٠.
(¬٦) وعند المالكية: لا زكاة فيه.
الشرح الكبير للدردير ١/ ٤٨٦، حاشية الدسوقي ١/ ٤٨٦.
(¬٧) المبسوط ٢/ ٢١٦، بدائع الصنائع ٢/ ٦٢، الأصل ١/ ١١٩.','3','79',NULL,NULL,'0');
INSERT INTO [b46] VALUES (755,'<span data-type="title" id=toc-383>مصارف الزكاة </span>سبعة:
<hr><span data-type="title">منحة السلوك</span>

بفتح النون دهن يكون على وجه الماء (¬١).

[مصارف الزكاة]
قوله: مصارف الزكاة، والعشر مبتدأ.
وقوله: "سبعة"، خبره، ويجوز أن يكون التقدير: هذا بيان مصارف الزكاة بأن يكون المبتدأ محذوفًا.
وقوله: سبعة: أي: هي سبعة، على حذف المبتدأ أيضًا.
والأصل فيه قوله تعالى: {إِنَّمَا الصَّدَقَاتُ لِلْفُقَرَاءِ} الآية (¬٢)، وهم ثمانية أصناف، وقد سقط منها المؤلفة قلوبهم؛ لأن الله تعالى أعز الإسلام، وأغنى عنهم، وهو من قبيل انتهاء الحكم؛ لانتهاء علته إذ لا نسخ بعده -صلى الله عليه وسلم- (¬٣).
_________
(¬١) شرح فتح القدير ٢/ ٢٥٨، العناية ٢/ ٢٥٨.
(¬٢) وتمامها: {وَالْمَسَاكِينِ وَالْعَامِلِينَ عَلَيْهَا وَالْمُؤَلَّفَةِ قُلُوبُهُمْ وَفِي الرِّقَابِ وَالْغَارِمِينَ وَفِي سَبِيلِ اللَّهِ وَابْنِ السَّبِيلِ فَرِيضَةً مِنَ اللَّهِ وَاللَّهُ عَلِيمٌ حَكِيمٌ} [سورة التوبة، الآية: ٦٠].
(¬٣) اختلف العلماء في المؤلفة قلوبهم هل بقي الآن لهم حكم؟
فذهب الحنفية: إلى سقوط حكمهم. وهو القول: المشهور عن المالكية.
والقول الثاني: عند المالكية: إن احتيج إلى استئلافهم في بعض الأوقات، رد إليهم سهمهم.
وعند الشافعية: المؤلفة قلوبهم ضربان: مؤلفة الكفار، ومؤلفة المسلمين. ومؤلفة الكفار ضربان: من يرجى إسلامه، ومن يخاف شره، وكان الرسول -صلى الله عليه وسلم- يعطيهم. وهل يعطون بعد وفاته؟ قولان. ومؤلفة المسلمين ضربان: ضرب لهم شرف يرجى بعطيتهم إسلام نظرائهم، وقوم يرجى إسلامهم، وكان الرسول -صلى الله عليه وسلم- يعطيهم. وأما بعده ففيه ثلاثة أقوال: أحدها: لا يعطون، والثاني: يعطون من سهم المؤلفة، والثالث: من خمس الخمس.
وضرب في طرف بلاد الإسلام، إن أعطوا دفعوا عن المسلمين، وقوم إن أعطوا جبوا الصدقات ممن يليهم، وفيهم أقوال: أحدها: يعطون من سهم المؤلفة، والثاني: من خمس الخمس، والثالث: من سهم سبيل الله، والرابع: من سهم المؤلفة، وسهم سبيل الله.
وذهب الحنابلة: إلى أن حكمهم باقٍ لم ينسخ، ومتى وجد الإمام قومًا من المشركين، =','3','80',NULL,NULL,'0');
INSERT INTO [b46] VALUES (756,'<span data-type="title" id=toc-384>الفقير:</span> وهو من له أدنى شيء. و<span data-type="title" id=toc-385>المسكين:</span> وهو من لا شيء له، وقيل: بالعكس.
والعامل:
<hr><span data-type="title">منحة السلوك</span>


[الفقير]
قوله: الفقير.
أي: المصرف الأول: الفقير، وهو من له أدنى شيء (¬١).

[المسكين]
قوله: والمسكين.
أي: المصرف الثاني: المسكين، وهو من لا شيء له (¬٢).
وقيل: بالعكس. أي: قيل المسكين: من له أدنى شيء، والفقير: من لا شيء له (¬٣). وهو قول: الشافعي أيضًا (¬٤).

<span data-type="title" id=toc-386>[العامل على الزكاة]</span>
قوله: والعامل.
_________
= فخاف الضرر بهم، ويعلم بإسلامهم مصلحة، جاز أن يتألفهم بمال الزكاة.
تبيين الحقائق ١/ ٢٩٦، الكتاب ١/ ١٥٣، الهداية ١/ ١٢٠، بداية المبتدي ١/ ١٢٠، شرح فتح القدير ٢/ ٢٥٩، العناية ٢/ ٢٥٩، حاشية الدسوقي ١/ ٤٩٥، بلغة السالك ١/ ٢٣٢، مختصر خليل ص ٦٧، أقرب المسالك ص ٤٠، التنبيه ص ٦٣، المهذب ١/ ١٧٢، الروض المربع ص ١٦٦، نيل المآرب ١/ ١٦٣، نيل المراد ص ٧٥.
(¬١) المختار ١/ ١١٨، كنز الدقائق ١/ ٢٩٦، الكتاب ١/ ١٥٣، شرح فتح القدير ٢/ ٢٦١، تبيين الحقائق ١/ ٢٩٦، العناية ٢/ ٢٦١.
(¬٢) وهو مذهب المالكية.
المختار ١/ ١١٨، الكتاب ١/ ١٥٣، شرح فتح القدير ٢/ ٢٦١، الاختيار ١/ ١١٨، العناية ٢/ ٢٦١، الهداية ١/ ١٢٠، أقرب المسالك ص ٤٠، سراج السالك ١/ ٢٠٥.
(¬٣) كنز الدقائق ١/ ٢٩٦، بداية المبتدي ١/ ١٢٠، شرح فتح القدير ٢/ ٢٦١، العناية ٢/ ٢٦١، الهداية ١/ ١٢٠، تبيين الحقائق ١/ ٢٩٦.
(¬٤) وأحمد.
السراج الوهاج ص ٣٥٥، كفاية الأخيار ١/ ١٢١، العمدة في فقه الإمام أحمد ص ٢٩، الكافي لابن قدامة ١/ ٣٣٢.','3','81',NULL,NULL,'0');
INSERT INTO [b46] VALUES (757,'غير الهاشمي ولو كان غنيًا. و<span data-type="title" id=toc-387>المكاتب</span>،
<hr><span data-type="title">منحة السلوك</span>

أي: المصرف الثالث: العامل، يدفع إليه إن عمل بقدر عمله، فيعطيه ما يكفيه، وأعوانه غير مقدر، بشرط أن يكون غير هاشمي، فإن الهاشمي لا يحل له عندنا (¬١). خلافًا للشافعي (¬٢).
وأما إذا كان غنيًا فلا يضر، وتحل له العمالة بالإجماع (¬٣).

[المكاتب]
قوله: والمكاتب.
أي: المصرف الرابع: المكاتب، يعان في حل (¬٤) رقبته (¬٥).
وعند مالك: يبتاع رقبته، فيعتق، فيكون الولاء على مذهبه لجماعة المسلمين دون المعتق (¬٦).
_________
(¬١) وإليه ذهب المالكية، والحنابلة.
تحفة الفقهاء ١/ ٣٠٠، بدائع الصنائع ٢/ ٤٤، بداية المبتدي ١/ ١٢٠، الكتاب ١/ ١٥٤، الاختيار ١/ ١١٩، الهداية ١/ ١٢٠، الشرح الصغير ١/ ٢٣٢، بلغة السالك ١/ ٢٣٢، منتهى الإرادات ١/ ٤٢٥، المقنع ١/ ٣٤٧.
(¬٢) الذي في كتب الشافعية: أنه لا يحل دفع الزكاة لهم، ونقل في المجموع عن صاحب البيان وجزم به ابن الصباغ أنه لو استعملهم الإمام في الحفظ أو النقل فلهم أجرته لكن تعقب هذا الكلام في أسنى المطالب ١/ ٣٩٩ وقال: وهذا إما ضعيف أو مبني على أن ما يعطاه العامل أجرة لا زكاة.
مختصر المزني ص ٢٦٠، متن أبي شجاع ص ٩٣، المجموع ٦/ ٢٢٨.
(¬٣) بدائع الصنائع ٢/ ٤٤. المختار ١/ ١١٩، كنز الدقائق ١/ ٢٩٧.
(¬٤) وفي بقية النسخ "فك".
(¬٥) وإليه ذهب الشافعية، والحنابلة.
بداية المبتدي ١/ ١٢١، الكتاب ١/ ١٥٤، المختار ١/ ١١٩، كنز الدقائق ١/ ٢٩٧، الهداية ١/ ١٢١، الوقاية ١/ ١١٠، حاشية البيجوري ١/ ٢٩٤، التنبيه ص ٦٣، الكافي لابن قدامة ١/ ٣٣٤، حاشية المقنع ١/ ٣٤٨، الروض المربع ص ١٦٦، كشاف القناع ٢/ ٢٨٠.
(¬٦) مواهب الجليل ٢/ ٣٥٠، التاج والإكليل ٢/ ٣٥٠.','3','82',NULL,NULL,'0');
INSERT INTO [b46] VALUES (758,'و<span data-type="title" id=toc-388>المديون</span>، والغازي المنقطع،
<hr><span data-type="title">منحة السلوك</span>


[المديون]
قوله: والمديون، أي: المصرف الخامس: المديون يدفع إليه إذا لم يملك نصابًا فاضلًا عن دينه (¬١).

<span data-type="title" id=toc-389>[في سبيل الله]</span>
قوله: والغازي المنقطع، أي: المصرف السادس: الغازي المنقطع (¬٢)، وهو تفسير قوله تعالى: {وَفِي سَبِيلِ اللَّهِ} [التوبة: ٦٠] (¬٣)، فقال أبو يوسف:
_________
(¬١) وإليه ذهب المالكية.
وذهب الشافعية، والحنابلة: إلى أن الغارم من تدين لنفسه مع الفقر، ومن غرم لإصلاح ذات البين تدفع له الزكاة، وإن كان غنيًا.
الكتاب ١/ ١٥٤، المختار ١/ ١١٩، الهداية ١/ ١٢١، كشف الحقائق ١/ ١١٠، الوقاية ١/ ١١٠، العناية ٢/ ٢٦٣، بداية المبتدي ١/ ١٢١، تبيين الحقائق ١/ ٢٩٨، مختصر خليل ص ٦٧، سراج السالك ١/ ٢٠٦، بلغة السالك ١/ ٢٣٣، متن أبي شجاع ص ٩٢، التذكرة ص ٧٤، السراج الوهاج ص ٣٥٦، السلسبيل ١/ ٣١٢، العمدة ص ٢٩، الكافي لابن قدامة ١/ ٣٣٤، الإقناع للحجاوي ٢/ ٣٧٩.
(¬٢) أي: الفقير منهم.
وذهب المالكية، والشافعية، والحنابلة: إلى أنه يأخذ الغني منهم كما يأخذ الفقير.
وهل الحج والعمرة من السبيل فيجوز صرف الزكاة فيه؟
ذهب الحنفية، والمالكية، والشافعية، ورواية عن الإمام أحمد: إلى عدم جواز ذلك؛ لأن السبيل عندهم محمول على الغزاة، لا غير.
وذهب الحنابلة: إلى أن الحج والعمرة من سبيل الله، فيأخذ إن كان فقيرًا من الزكاة ما يؤدي به الحج، أو العمرة.
الكتاب ١/ ١٥٤، الهداية ١/ ١٢١، شرح فتح القدير ٢/ ٢٦٤، العناية ٢/ ٢٦٤، الوقاية ١/ ١١٠، كشف الحقائق ١/ ١١٠، المختار ١/ ١١٩، تبيين الحقائق ١/ ٢٩٨، الشرح الكبير للدردير ١/ ٤٩٧، الخرشي على خليل ٢/ ٢١٩، السراج الوهاج ص ٣٥٦، نهاية المحتاج ٥/ ١٨٥، المهذب ١/ ١٧٣، مغني المحتاج ٥/ ١١١، كشاف القناع ٢/ ٢٨٣، المستوعب ٣/ ٣٥٥، الفروع ٢/ ٦٢١، منار السبيل ١/ ٢٠٩.
(¬٣) وقيل: هم الغزاة، والمرابطون. وقيل: هم الغزاة الذين لا حق لهم في الديوان. =','3','83',NULL,NULL,'0');
INSERT INTO [b46] VALUES (759,'وقيل: الحاج المنقطع،
<hr><span data-type="title">منحة السلوك</span>

المراد منه، الغازي المنقطع (¬١)؛ لما روى البخاري في صحيحه (¬٢): أن النبي -صلى الله عليه وسلم- قال: "إن خالدًا (¬٣) حبس أدرعه في سبيل الله"، ولا شك أن الدرع للحرب، لا للحج.
وقال محمد: الحاج المنقطع (¬٤)؛ لما روى البخاري أيضًا، عن أبي لاسٍ الخزاعي (¬٥) أنه قال:
_________
= الكشاف للزمخشري ٢/ ١٥٨، جامع البيان في تأويل آي القرآن ٦/ ٤٢، تفسير ابن كثير ٢/ ٥٧٠، زاد المسير ٣/ ٣١٠.
(¬١) أي: الفقير الغازي.
بداية المبتدي ١/ ١٢١، تبيين الحقائق ١/ ٢٩٨، العناية ٢/ ٢٦٤، شرح فتح القدير ٢/ ٢٦٤، الهداية ١/ ١٢١، الاختيار ١/ ١١٩.
(¬٢) ٢/ ٥٣٤ كتاب الزكاة باب قول الله وفي الرقاب ... وفي سبيل الله رقم ١٣٩٩ وتمامه: عن أبي هريرة -رضي الله عنه- قال: بعث رسول الله -صلى الله عليه وسلم- عمر بن الخطاب على الصدقة، فمنع ابن جميل، وخالد بن الوليد، والعباس عم رسول الله، فقال رسول الله -صلى الله عليه وسلم-: "ما ينقم ابن جميل، إلا أن كان فقيرًا فأغناه الله، وأما خالد بن الوليد، فإنكم تظلمون خالدًا، وقد احتبس أدراعه وأعتده في سبيل الله، وأما العباس عم رسول الله، فهي علي ومثلها، ثم قال: يا عمر أما شعرت أن عم الرجل صنو أبيه".
(¬٣) هو خالد بن الوليد بن المغيرة المخزومي القرشي، صحابي جليل، من أشراف قريش، أسلم سنة ٧ هـ، وجهه أبو بكر لقتال مسيلمة ومن ارتد من القبائل، كان شجاعًا، مظفرًا، خطيبًا فصيحًا توفي سنة ٢١ هـ.
الإصابة ١/ ٤١٣، الاستيعاب ١/ ٤١٣، صفة الصفوة ١/ ٢٦٨، الأعلام ٢/ ٣٠٠.
(¬٤) الهداية ١/ ١٢١، تبيين الحقائق ١/ ٢٩٨، العناية ٢/ ٢٦٤، شرح فتح القدير ٢/ ٢٦٤، الوقاية ١/ ١١٠.
(¬٥) هو أبو لاس الخزاعي ويقال: الحارثي، قيل: اسمه عبد الله، وقيل: اسمه زياد بن عنبسة، له صحبة، يعد في أهل المدينة روى عنه جعفر بن عبد الله بن الحكم، وعمر بن الحكم بن ثوبان. =','3','84',NULL,NULL,'0');
INSERT INTO [b46] VALUES (760,'ومَنْ ماله بعيد عنه، وللمالك أن يعم كل المصارف وأن يخص بعضها،
<hr><span data-type="title">منحة السلوك</span>

" حملنا النبي -صلى الله عليه وسلم- على إبل الصدقة للحج" (¬١). فعلم بذلك، أن سبيل الله الحاج المنقطع؛ لأنه -صلى الله عليه وسلم- صرف الصدقة إليه.

<span data-type="title" id=toc-390>[ابن السبيل]</span>
قوله: ومَنْ ماله بعيد عنه.
أي: المصرف السابع: ابن السبيل، وهو من ماله بعيد عنه، فيدفع إليه الزكاة؛ لأنه فقير في الحال، وإن كان غنيًا بالنظر إلى حيث ماله (¬٢).
قوله: وللمالك أن يعم كل المصارف، وأن يخص بعضها (¬٣).
_________
= تهذيب الكمال ١٥/ ٣٩٢، الإصابة ٤/ ١٦٨، الاستيعاب ٤/ ١٧١، تهذيب الكمال ٥/ ٣٤٥، خلاصة تذهيب تهذيب الكمال ص ٢٠٩.
(¬١) رواه البخاري معلقًا ٢/ ٥٣٤ كتاب الزكاة "باب قول الله تعالى: وفي الرقاب ... وفي سبيل الله ... ولفظه ويذكر عن أبي لاس: "حملنا النبي -صلى الله عليه وسلم- على إبل الصدقة للحج".
ورواه أيضًا الإمام أحمد ٤/ ٢٢١، وابن خزيمة في صحيحه ٤/ ٧٣ رقم ٢٣٧٧، والحاكم في المستدرك ١/ ٤٤٤، والطبراني في الكبير ٢٢/ ٣٣٤ برقمي ٨٣٧، ٨٣٨، وابن أبي عاصم في الآحاد والمثاني ٤/ ٣٠٣ برقم ٢٣٢٨.
قال الحاكم ١/ ٤٤: هذا حديث صحيح على شرط مسلم ولم يخرجه وله شاهد صحيح.
وقال ابن حجر في الفتح ٣/ ٣٣٢: ورجاله ثقات، إلا أن فيه عنعنة ابن إسحاق.
(¬٢) اختلف العلماء في صفة ابن السبيل بعد اتفاقهم على سهمه.
فذهب الحنفية، والمالكية، والحنابلة: إلى أن ابن السبيل هو المجتاز دون المنشيء.
وعند الشافعية: ابن السبيل هو المسافر، أو من ينشيء السفر وهو محتاج في سفره، إن كان سفره في غير معصية.
بدائع الصنائع ١/ ٤٦، كنز الدقائق ١/ ٢٩٧، الكتاب ١/ ١٥٤، تبيين الحقائق ١/ ٢٩٧، شرح فتح القدير ٢/ ٢٦٤، الاختيار ١/ ١١٩، العناية ٢/ ٢٦٤، الشرح الصغير ١/ ٢٣٣، جواهر الإكليل ١/ ١٣٩، المجموع ٦/ ٢١٤، مختصر المزني ص ٢٥٨، نيل المآرب ١/ ٢٦٥، المبدع ٢/ ٤٣٠، كشاف القناع ٢/ ٢٨٤، الإفصاح ١/ ٢٢٧، المقنع ١/ ٣٥، الإنصاف ٣/ ٢٣٧.
(¬٣) وإليه ذهب المالكية، والحنابلة. =','3','85',NULL,NULL,'0');
INSERT INTO [b46] VALUES (761,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وهو قول: عمر بن الخطاب، وعلي بن أبي طالب، وابن عباس، ومعاذ بن جبل، وحذيفة بن اليمان -رضي الله عنهم- (¬١) (¬٢)،
_________
= كنز الدقائق ١/ ٢٩٩، المختار ١/ ١١٩، بداية المجتهد ١/ ٢٧٥، مواهب الجليل ٢/ ٣٤٢، السلسبيل ١/ ٣١٣، الروض المربع ص ١٦٧.
(¬١) هو حذيفة بن اليمان بن جابر العبسي، أبو عبد الله، من كبار الصحابة، أسلم هو وأبوه وأرادا شهود بدر فصدهما المشركون، وشهد أحدًا، استعمله عمر على المدائن فلم يزل بها حتى مات سنة ٣٦ هـ.
تهذيب التهذيب ٢/ ٢١٩، شذرات الذهب ١/ ٣٢، سير أعلام النبلاء ٢/ ٣٦١، أسد الغابة ١/ ٤٦٨، الإصابة ١/ ٣١٧.
(¬٢) أثر عمر رواه ابن أبي شيبة ٢/ ٤٠٥ كتاب الزكاة، باب ما قالوا في الرجل إذا وضع الصدقة في صنف واحد رقم ١٠٤٤٨، والبيهقي ٧/ ٧ كتاب الصدقات باب من جعل الصدقة في صنف واحد من هذه الأصناف، وابن جرير الطبري ١/ ١١٥. من طريق ليث بن أبي سليم عن عطاء عن عمر بن الخطاب -رضي الله عنه-.
قال البيهقي في السنن الكبرى ٧/ ٧: وهو منقطع.
وقال في معرفة السنن والآثار ٩/ ٣١٩: وهذا منقطع بين عطاء وعمر وليث غير قوي، وقال في الدراية ١/ ٢٦٦: وإسناده منقطع.
وأثر علي بن أبي طالب رواه البيهقي ٧/ ٨ كتاب الصدقات باب من جعل الصدقة في صنف واحد من هذه الأصناف، من طريق الحسن بن عمارة عن الحكم عن يحيى بن الجزار عن علي قال: لا بأس أن يجعل الرجل الصدقة في صنف واحد.
والحسن بن عمارة قال عنه في التقريب ص ١٦٢: متروك.
وأثر ابن عباس رواه ابن جرير ١٠/ ١١٦، وابن أبي شيبة ٢/ ٤٠٥ كتاب الزكاة، باب ما قالوا في الرجل إذا وضع الصدقة في صنف واحد رقم ١٠٤٥١. بلفظ: عن عطاء قال: سألته أعطي الصدقة في صنف واحد من الأصناف الثمانية، قال: نعم.
وعبد الرزاق ٤/ ١٠٥ كتاب الزكاة، باب إنما الصدقات للفقراء رقم ٧١٣٦ بلفظ: إذا وضعتها في صنف واحد من هذه الأصناف فحسبك، والبيهقي ٧/ ٧ كتاب الصدقات باب من جعل الصدقة في صنف واحد من هذه الأشياء. =','3','86',NULL,NULL,'0');
INSERT INTO [b46] VALUES (762,'ولا يدفع إلى غني، وإن كان نصابه غير تام
<hr><span data-type="title">منحة السلوك</span>

وجماعة أخر (¬١)، ولم يُرو عن غيرهم من الصحابة خلاف ذلك، فكان إجماعًا (¬٢).
وعند الشافعي: لا يجوز إلا إذا دفع إلى ثمانية (¬٣) أصناف من كل صنف ثلاثة أنفس، إلا العامل (¬٤).

<span data-type="title" id=toc-391>[من لا يستحق الزكاة]</span>
قوله: ولا يدفع إلى غني، وإن كان نصابه غير تام (¬٥)؛ لقوله -صلى الله عليه وسلم-: "لا تحل الصدقة لغني" رواه أبو داود، والنسائي، والترمذي (¬٦).
_________
= قال ابن حجر في الدراية ١/ ٢٦٦: وإسناده حسن.
وأثر حذيفة رواه ابن جرير الطبري في التفسير ١٠/ ١١٥، وابن أبي شيبة ٢/ ٤٠٥ كتاب الزكاة، ما قالوا في الرجل إذا وضع الصدقة في صنف واحد رقم ١٠٤٤٥ بلفظ: إذا أعطاها في صنف واحد من الأصناف الثمانية التي سمى الله تعالى أجزأه.
من طريق حجاج عن المنهال عن زر عن حذيفة ضعفه البيهقي، وقال البيهقي ٧/ ٧: الحجاج بن أرطاة لا يحتج به.
وأثر معاذ بن جبل رواه البيهقي ٧/ ٩ كتاب الصدقات، باب من قال لا يخرج صدقة قوم منهم من بلدهم وفي بلدهم من يستحقها من طريق الشافعي عن مطرف بن مازن عن معمر عن عبد الله بن طاووس عن أبيه أن معاذ بن جبل قضى أيما رجل انتقل من مخلاف عشيرته إلى غير مخلاف عشيرته فعشره وصدقته إلى مخلاف عشيرته.
قال البيهقي في معرفة السنن ٩/ ٣٢١: منقطع.
(¬١) كسعيد بن جبير، والحسن، والنخعي، وعطاء، والثوري، وأبي عبيد.
تبيين الحقائق ١/ ٢٩٩، شرح فتح القدير ٢/ ٢٦٦، جامع البيان في تأويل آي القرآن ١/ ٤٠٤، المغني ٢/ ٥٢٨.
(¬٢) تبيين الحقائق ١/ ٢٩٩، شرح فتح القدير ٢/ ٢٦٦.
(¬٣) في ص "جميع الأصناف".
(¬٤) كفاية الأخيار ١/ ١٢٤، هداية الغلام ص ٦٤.
(¬٥) تحفة الفقهاء ١/ ٣٠١، تبيين الحقائق ١/ ٣٠٢، الكتاب ١/ ١٥٥، بداية المبتدي ١/ ١٢١، كشف الحقائق ١/ ١١٢، الهداية ١/ ١٢١.
(¬٦) أبو داود ١٦٣٤ كتاب الزكاة، باب من يعطى من الصدقة وحد الغني ٢/ ١١٨، والترمذي =','3','87',NULL,NULL,'0');
INSERT INTO [b46] VALUES (763,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وقال: مالك، والشافعي: يجوز دفعها إلى غني الغزاة، إذا لم يكن له شيء في الديوان (¬١)، ولم يكن يأخذ من الفيء (¬٢) (¬٣).
_________
= ٣/ ١٣ كتاب الزكاة، باب ما جاء من لا تحل له الصدقة رقم ٦٥٢، والنسائي ٥/ ٩٩ كتاب الزكاة، باب إذا لم يكن له دراهم وكان له عدلها رقم ٢٥٩٧، ورواه أيضًا الطيالسي ص ٣٠٠ رقم ٢٢٧١ كتاب الزكاة، وعبد الرزاق ٤/ ١١٠ كتاب الزكاة، باب كم الكنز ولمن الزكاة رقم ٧١٥٥، وابن أبي شيبة ٣/ ٢٠٧، وأحمد ٢/ ١٩٢، والدارمي ١/ ٤١٤ كتاب الزكاة، باب من تحل له الصدقة رقم ١٥٩٦، وأبو نعيم في الحلية ٨/ ٣٠٨، وأبو يعلى ١١/ ٦٢ رقم ٦١٩٩، وابن الجارود رقم ٣٦٣ ص ٩٩، والطحاوي ٢/ ١٤ كتاب الزكاة، باب ذي المرة السوي الفقير هل يحل له الصدقة أم لا، والدارقطني ٢/ ١١٩ كتاب الزكاة، باب لا تحل الصدقة لغني ولا لذي مرة سوي، والحاكم ١/ ٤٠٧ كتاب الزكاة، والبيهقي ٧/ ١٣ كتاب الزكاة، باب الفقير أو المسكين له كسب، أو حرفة تغنيه وعياله، فلا يعطى بالفقر والمسكنة شيئًا، والبغوي ٦/ ٨٢، كلهم عن عبد الله بن عمرو -رضي الله عنهما-، سوى النسائي، وأبي يعلى فعن أبي هريرة -رضي الله عنه- وتمامه: "لا تحل الصدقة لغني ولا لذي مرة سوي" قال الترمذي: حديث حسن صحيح. وقال في التلخيص ٣/ ١٠٨: بسند صحيح.
(¬١) الديوان: هو موضع لحفظ الحقوق من الأموال، والعمال، ومن يقوم بها من الجيوش، والعمال. وأول من وضعه في الإسلام عمر بن الخطاب -رضي الله عنه-. وقيل: هو اسم الدفتر الذي يكتب فيه أسماء الجيش، وأهل العطاء.
تاج العروس ٩/ ٢٠٤ مادة دوّن، المطلع على أبواب المقنع ص ٢٩٩، لغة الفقه ص ١٢٠، الأحكام السلطانية للماوردي ص ١٩٩.
(¬٢) عند المالكية: يجوز دفعها لغني الغزاة من غير تقييد. والشافعية: كما ذكره المصنف.
وعند الحنابلة: يجوز دفع الزكاة لغني الغزاة، الذين لا حق لهم في الديوان.
الشرح الصغير ١/ ٢٣٣، بلغة السالك ١/ ٢٣٣، الشرح الكبير للدردير ١/ ٤٩٧، المعونة ١/ ٤٤٣، المهذب ١/ ١٧٣، السراج الوهاج ص ٣٥٦، الإقناع للحجاوي ٢/ ٢٨٣، المبدع ٢/ ٤٢٤.
(¬٣) الفيء في الأصل: مصدر فاء يفيء فيئة وفيؤًا: إذا رجع، وهو الخراج، أو الغنيمة.
وفي الاصطلاح: ما أخذ من مال مشرك بعد أن تضع الحرب أوزارها، وتصير الدار دار إسلام. =','3','88',NULL,NULL,'0');
INSERT INTO [b46] VALUES (764,'ولا إلى ذمي، بخلاف غير الزكاة،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-392>[أهل الذمة]</span>
قوله: ولا إلى ذمي.
أي: ولا تدفع الزكاة إلى ذمي أيضًا (¬١)؛ لما روي من حديث معاذ -رضي الله عنه- أنه -صلى الله عليه وسلم- قال: "أعلمهم أن الله فرض عليهم صدقة تؤخذ من أغنيائهم، وترد إلى فقرائهم" متفق عليه (¬٢).
وقال زفر: يجوز (¬٣).
قوله: بخلاف غير الزكاة.
يعني: غير الزكاة، مثل صدقة الفطر، والكفارات، والصدقة المنذورة،
_________
= مختار الصحاح ص ٢١٦ مادة ف ي أ، القاموس المحيط ٣/ ٥٤٠ مادة ف ي أ، الدر النقي ٣/ ٦٠٣، لغة الفقه ص ١٨٣، المطلع على أبواب المقنع ص ٢١٩.
(¬١) وفاقًا للثلاثة.
الكتاب ١/ ١٥٥، المختار ١/ ١٢٥ الهداية ١/ ١٢١، تبيين الحقائق ١/ ٣٠٠، كشف الحقائق ١/ ١١٠، الاختيار ١/ ١٢٥، التلقين ص ٥٢، التفريع ١/ ٢٩٨، متن أبي شجاع ص ٩٠، التذكرة ص ٧٤، المستوعب ٣/ ٣٦٩، الكافي لابن قدامة ١/ ٣٣٧.
(¬٢) البخاري ٢/ ٥٠٥ كتاب الزكاة، باب وجوب الزكاة رقم ١٣٣١، ومسلم ١/ ٥٠ كتاب الإيمان، باب الدعاء إلى الشهادتين وشرائع الإسلام رقم ١٩.
من حديث ابن عباس -رضي الله عنهما-، أن رسول الله -صلى الله عليه وسلم- لما بعث معاذًا إلى اليمن، قال له: "إنك تأتي قومًا من أهل الكتاب، فادعهم إلى شهادة أن لا إله إلا الله، وأني رسول الله، فإن هم أطاعوك لذلك فأعلمهم أن الله افترض عليهم خمس صلوات في كل يوم وليلة، فإن هم أطاعوك لذلك فأعلمهم أن الله افترض عليهم صدقة تؤخذ من أغنيائهم، فترد على فقرائهم، فإن هم أطاعوك لذلك فإياك وكرائم أموالهم، واتق دعوة المظلوم فإنه ليس بينها وبين الله حجاب".
(¬٣) تبيين الحقائق ١/ ٣٠٠، بدائع الصنائع ٢/ ٤٩.','3','89',NULL,NULL,'0');
INSERT INTO [b46] VALUES (765,'ولا يبنى منها مسجد،
<hr><span data-type="title">منحة السلوك</span>

يجوز دفعها إليه عندهما. خلافًا لأبي يوسف (¬١)، والشافعي (¬٢).
وأما التطوع: فدفعه إليه جائز اتفاقًا (¬٣).

<span data-type="title" id=toc-393>[بناء المساجد]</span>
قوله: ولا يبنى منها.
أي: من الزكاة مسجد؛ لأن التمليك شرط فيها؛ فلم يوجد (¬٤). وكذا لا يبنى منها القناطر (¬٥)، والسقايات (¬٦)، وإصلاح الطرقات، وكري الأنهار، والحج، والجهاد، وكل ما لا تمليك فيه (¬٧).
_________
(¬١) تحفة الفقهاء ١/ ٣٠٣، بدائع الصنائع ٢/ ٤٩، تبيين الحقائق ١/ ٣٠٠، البحر الرائق ٢/ ٢٤٢، منحة الخالق ٢/ ٢٤٢.
(¬٢) ومالك، وأحمد.
منح الجليل ٢/ ١٠٧، الخرشي على خليل ٢/ ٢٣٣، المجموع ٦/ ٢٢٨، رحمة الأمة ١/ ١١١، المغني ٢/ ٥٢٠، الشرح الكبير لابن قدامة ٢/ ٧١١.
(¬٣) مراتب الإجماع ص ٣٤.
(¬٤) تبيين الحقائق ١/ ٣٠٠، شرح فتح القدير ٢/ ٢٦٧، البحر الرائق ٢/ ٢٤٣، الوقاية ١/ ١١١، بداية المبتدي ١/ ١٢١، الهداية ١/ ١٢١.
(¬٥) القناطر: جمع قنطرة، وهي: ما يبنى على الماء للعبور. والجسر عام مبني كان، أو غير مبني.
لسان العرب ٥/ ١١٨ مادة قنطر، تاج العروس ٣/ ٥٠٩ مادة قنطر، مختار الصحاح ص ٢٢٦ مادة ق ط ر ب، القاموس المحيط ٣/ ٧٠٠ مادة ق ن ط ر، المغرب ص ٣٨٧ مادة قطر.
(¬٦) السقاية -بالكسر-: الموضع يتخذ لسقي الناس في المواسم، وغيره.
لسان العرب ١٤/ ٣٨٨، مختار الصحاح ص ١٢٨ مادة س ق ي، المغرب ص ٢٢٩، القاموس المحيط ٢/ ٥٨٣ مادة س ق ي.
(¬٧) تبيين الحقائق ١/ ٣٠، الهداية ١/ ١٢١، شرح فتح القدير ٢/ ٢٦٧، العناية ٢/ ٢٦٧، شرح الوقاية ١/ ١١١، البحر الرائق ٢/ ٢٤٣.','3','90',NULL,NULL,'0');
INSERT INTO [b46] VALUES (766,'ولا يُكفَّن بها ميت، ولا يُقضى دينه، ولا يعتق بها عبد، ولا يدفعها المزكي إلى أصوله وفروعه،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-394>[كفن الميت ودينه]</span>
قوله: ولا يكفن بها.
أي: بالزكاة ميت؛ لانعدام التمليك (¬١).
ولا يقضى دينه. أي: دين الميت؛ لانعدام التمليك أيضًا (¬٢).
قوله: ولا يعتق بها عبد (¬٣).
يعني: لا يجوز أن يشتري بها عبد فيعتق (¬٤). خلافًا لمالك، وقد مر (¬٥).

<span data-type="title" id=toc-395>[الأصول والفروع]</span>
قوله: ولا يدفعها المزكي إلى أصوله.
وهم: الآباء، والأجداد، والأمهات، والجدات من قبل الأب والأم، وإن علوا، وفروعه، وهم: الأولاد، وأولاد الأولاد، وإن سفلوا، لعدم تحقق التمليك على الكمال (¬٦).
_________
(¬١) الكتاب ١/ ١٥٥، شرح فتح القدير ٢/ ٢٦٧، كنز الدقائق ١/ ٣٠٠، كشف الحقائق ١/ ١١١، الوقاية ١/ ١١١، بداية المبتدي ١/ ١٢١، الهداية ١/ ١٢١ وانظر التفريع ١/ ٢٩٨، الكافي لابن عبد البر ص ١١٥، منتهى الإرادات ١/ ٤٢٨، شرح منتهى الإرادات ١/ ٢٤٨.
قال ابن هبيرة في الإفصاح ١/ ٢٣١: واتفقوا على أنه لا يجوز أن يخرج الزكاة إلى بناء مسجد، ولا تكفين ميت، وإن كان من القرب؛ لتعين الزكاة لما عينت له.
(¬٢) شرح فتح القدير ٢/ ٢٦٨، تبيين الحقائق ١/ ٣٠٠.
(¬٣) وهو الرقيق.
القاموس المحيط ٣/ ١٣٥ مادة ع ب د، مختار الصحاح ص ١٧٢ مادة ع ب د، المطلع على أبواب المقنع ص ١٣٨، الدر النقي ٢/ ٢٥٥، المصباح المنير ٢/ ٣٨٩ مادة عبدت.
(¬٤) الكتاب ١/ ١٥٥، كنز الرقائق ١/ ٣٠١.
(¬٥) في ٣/ ٨٢.
(¬٦) اتفق الأئمة الأربعة على أنه لا يجوز إخراج الزكاة إلى الوالدين، والمولودين، علوا =','3','91',NULL,NULL,'0');
INSERT INTO [b46] VALUES (767,'وزوجته، وزوجها،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-396>[الزوجان]</span>
قوله: وزوجته.
أي: ولا يدفعها أيضًا إلى زوجته؛ لعدم كمال التمليك، لوجود الاشتراك في المنافع بينهما (¬١).
قوله: وزوجها.
أي: ولا تدفع المرأة زكاتها إلى زوجها أيضًا، هذا عند أبي حنيفة (¬٢).
وعندهما: يجوز (¬٣)؛ لقوله -صلى الله عليه وسلم-: "لها أجران: أجر القرابة، وأجر
_________
= أو سفلوا إلا مالكًا فإنه قال: في الجد والجدة فمن وراءهما يجوز دفعها إليهم، وكذلك إلى بني البنين لسقوط نفقتهم عنده.
الكتاب ١/ ١٥٥، المختار ١/ ١٢٠، بداية المبتدي ١/ ١٢٢، الاختيار ١/ ١٢٠، الهداية ١/ ١٢٢، شرح الوقاية ١/ ١١١، أقرب المسالك ص ٤١، التفريع ١/ ٢٩٨، شرح ابن قاسم على أبي شجاع ١/ ٢٩٧، حاشية البيجوري على ابن قاسم ١/ ٢٩٧، التسهيل ص ٨٧، العمدة ص ٣٠.
(¬١) وفاقًا للثلاثة.
الكتاب ١/ ١٥٥، المختار ١/ ١٢٠، كشف الحقائق ١/ ١١١، الهداية ١/ ١٢٢، البحر الرائق ٢/ ٢٤٣، كنز الدقائق ١/ ٣٠١، الشرح الصغير ١/ ٢٣٥، التفريع ١/ ٢٩٨ التنبيه، ص ٤٣، التذكرة ص ٧٤، الروض المربع ص ١٦٩، الكافي لابن قدامة ١/ ٣٣٩.
(¬٢) وإليه ذهب الحنابلة.
الكتاب ١/ ١٥٥، تحفة الفقهاء ١/ ٣٠٣، ملتقى الأبحر ١/ ١٩١، المختار ١/ ١٢٠، كشف الحقائق ١/ ١١١، شرح الوقاية ١/ ١١١، بداية المبتدي ١/ ١٢٢، شرح فتح القدير ٢/ ٢٧٠، الروض المربع ص ١٦٩، تصحيح الفروع ٢/ ٦٣٥.
(¬٣) وهو مذهب الشافعية.
وذهب المالكية: إلى أنه إن كان يستعين بما يأخذه منها على نفقتها فلا يجوز، وإن كان يصرفه في غير نفقتها لأولادٍ فقراء عنده من غيرها، أو نحو ذلك جاز.
الكتاب ١/ ١٥٥، تحفة الفقهاء ١/ ٣٠٣، الاختيار ١/ ١٢٠، كشف الحقائق ١/ ١١١، =','3','92',NULL,NULL,'0');
INSERT INTO [b46] VALUES (768,'ومكاتبه،
<hr><span data-type="title">منحة السلوك</span>

الصدقة" رواه البخاري، والطحاوي في شرح الآثار (¬١). وذلك حين سئل النبي -صلى الله عليه وسلم- عن جواز إنفاق زينب على زوجها عبد الله، وأيتام لها في حجرها.
وله: أن المنافع بينهما متصلة، فلا يتحقق التمليك على الكمال (¬٢).
والجواب عن الحديث: أن زينب كانت صناع اليدين (¬٣)، ولم يكن لها مال يجب عليها فيه الزكاة، فكانت صدقتها على عبد الله نافلة لا فريضة (¬٤).

<span data-type="title" id=toc-397>[الأرقاء]</span>
قوله: ومكاتبه.
_________
= الهداية ١/ ١٢٢، القوانين ص ٧٤، منح الجليل ٢/ ٩٣، مغني المحتاج ٣/ ١٠٨، نهاية المحتاج ٦/ ١٥٥.
(¬١) البخاري ٢/ ٥٣٣ كتاب الزكاة، باب الزكاة على الزوج والأيتام في الحجر رقم ١٣٩٧، والطحاوي في شرح معاني الآثار ٢/ ٢٣ كتاب الزكاة باب المرأة هل يجوز لها أن تعطي زوجها من زكاة مالها أم لا؟.
وتمامه: عن زينب امرأة عبد الله قالت: كنت في المسجد، فرأيت النبي -صلى الله عليه وسلم- فقال: تصدقن ولو من حليكن، وكانت زينب تنفق على عبد الله، وأيتام في حجرها، قال: فقالت لعبد الله: سل رسول الله أيجزيء عني أن أنفق عليك، وعلى أيتامي في حجري من الصدقة؟ فقال: سلي أنت رسول الله فانطلقت إلى النبي -صلى الله عليه وسلم-، فوجدت امرأة من الأنصار على الباب حاجتها مثل حاجتي، فمر علينا بلال، فقلنا: سل النبي -صلى الله عليه وسلم- أيجزيء عني أن أنفق على زوجي، وأيتام لي في حجري؟ وقلنا: لا تخبر بنا، فدخل، فسأله فقال: من هما؟ قال: زينب، قال: أي الزيانب؟ قال: امرأة عبد الله، قال: "نعم لها أجران، أجر القرابة، وأجر الصدقة".
(¬٢) شرح فتح القدير ٢/ ٢٧٠، العناية ٢/ ٢٧٠.
(¬٣) أي: صنعة اليدين، تعمل للناس، وتتصدق بذلك.
العناية ٢/ ٢٧١، تبيين الحقائق ١/ ٣٠١.
(¬٤) الهداية ١/ ١٢٢، شرح فتح القدير ٢/ ٢٧١، العناية ٢/ ٢٧١، تبيين الحقائق ٢/ ٢٧١، الاختيار ١/ ١٢٠.','3','93',NULL,NULL,'0');
INSERT INTO [b46] VALUES (769,'ومدبره، وأم ولد وعبد أُعتِق بعضه، ولا إلى مملوك غني، وولده الصغير،
<hr><span data-type="title">منحة السلوك</span>

أي: ولا يدفعها المزكي أيضًا إلى مكاتبه، ومدبره (¬١)، وأم ولده، وعبده الذي أعتق بعضه؛ لعدم الإخراج الصحيح؛ لأن كسب المملوك لسيده فصار كأنه دفع إلى نفسه (¬٢). وإذا دفع إلى مكاتب غيره يجوز. وإن كان مولاه غنيًا (¬٣).
قوله: ولا إلى مملوك غني.
لأن المملوك واقع المولى، فلم يجز (¬٤)، ولا إلى ولده الصغير؛ لأنه
_________
(¬١) وهو المعتق عن دبر، أي: بعد الموت. ودبر الشيء: مؤخره.
والمدبر المطلق، هو: الذي قيل له: أنت حر بعد موتي.
والمدبر المقيد: هو الذي قيل له: إن مت من مرض، أو إلى وقت كذا، أو في طريق كذا، فأنت حر.
طلبة الطلبة ص ٦٢ - ١٣٥، المطلع على أبواب المقنع ص ٣١٥، حلية الفقهاء ص ٢٠٨، لسان العرب ٤/ ٢٧٣، مادة دبر، القاموس المحيط ٢/ ١٤٧ مادة د ب ر، المصباح المنير ١/ ١٨٨، مادة الدبر.
(¬٢) وفاقًا للمالكية، والشافعية، والحنابلة.
قال ابن هبيرة في الإفصاح ١/ ٢٣١: واتفقوا على أنه لا يجوز إخراج الزكاة المفروضة إلى مكاتبه، ولا إلى عبده.
الكتاب ١/ ١٥٦، الهداية ١/ ١٢٢، كنز الدقائق ١/ ٣٠١، تبيين الحقائق ١/ ٣٠١، بداية المبتدي ١/ ١٢٢، تحفة الفقهاء ١/ ٣٠٠، شرح فتح القدير ٢/ ٢٧١، حاشية الدسوقي ١/ ٤٩٦، الشرح الصغير ١/ ٢٣٣، مغني المحتاج ٣/ ١٠٩، حاشية البيجوري ١/ ٢٩٦، الإقناع في حل ألفاظ أبي شجاع ١/ ٢١٣، مختصر الخرقي ص ٤٥، دليل الطالب ١/ ٢١١.
(¬٣) تحفة الفقهاء ١/ ٣٠١، الهداية ١/ ١٢٢، تبيين الحقائق ١/ ٣٠١، البحر الرائق ٢/ ٢٤٤، كنز الدقائق ١/ ٣٠١، العناية ٢/ ٢٧١، الكتاب ١/ ١٥٦.
(¬٤) الكتاب ١/ ١٥٦، المختار ١/ ١٢٠، الهداية ١/ ١٢٢، شرح فتح القدير ٢/ ٢٧٢، ملتقى الأبحر ١/ ١٩٠، كشف الحقائق ١/ ١١١.','3','94',NULL,NULL,'0');
INSERT INTO [b46] VALUES (770,'بخلاف امرأته، ولا إلى هاشمي
<hr><span data-type="title">منحة السلوك</span>

يعد غنيًا بيسار أبيه (¬١).
قوله: بخلاف امرأته.
يعني: إذا دفع إلى امرأة الغني يجوز؛ لأنها لا تعد غنية بيسار الزوج، وبقدر النفقة لا تصير موسرة (¬٢)، وكذلك يجوز دفعها إلى البنت الكبيرة الفقيرة لغني (¬٣).

<span data-type="title" id=toc-398>[بنو هاشم ومواليهم]</span>
قوله: ولا إلى هاشمي.
أي: ولا يدفع أيضًا إلى هاشمي (¬٤)؛ لقوله -صلى الله عليه وسلم-: "نحن أهل بيت لا تحل لنا الصدقة" رواه البخاري (¬٥)، وقوله -صلى الله عليه وسلم-: "إن هذه الصدقات، إنما هي أوساخ الناس، وإنها لا تحل لمحمد، ولا لآل محمد" رواه مسلم (¬٦).
_________
(¬١) المختار ١/ ١٢٠، كنز الدقائق ١/ ٣٠٢، الاختيار ١/ ١٢٠، الهداية ١/ ١٢٢، العناية ٢/ ٢٧٢، شرح الوقاية ١/ ١١١، ملتقى الأبحر ١/ ١٩٠، تبيين الحقائق ١/ ٣٠٢.
(¬٢) الهداية ١/ ٢٢٢، العناية ٢/ ٢٧٢، تحفة الفقهاء ١/ ٣٠٠، شرح فتح القدير ٢/ ٢٧٢، تبيين الحقائق ١/ ٣٠٣.
(¬٣) تحفة الفقهاء ١/ ٣٠٠، شرح فتح القدير ٢/ ٢٧٢، تبيين الحقائق ١/ ٣٠٣، العناية ٢/ ٢٧٢، الهداية ١/ ٢٢٢.
(¬٤) وفاقًا للثلاثة.
الكتاب ١/ ١٥٦، المختار ١/ ١٢٠، ملتقى الأبحر ١/ ١٩٠، الوقاية ١/ ١١١، كشف الحقائق ١/ ١١١، الاختيار ١/ ١٢٠، الكافي لابن عبد البر ص ١١٥، مختصر خليل ص ٦٧، الإقناع في حل ألفاظ أبي شجاع ١/ ٢١٤، التذكرة ص ٧٤، منار السبيل ١/ ٢١٢، التسهيل ص ٨٧.
(¬٥) ٣/ ١١١٨ كتاب الجهاد، باب من تكلم بالفارسية والرطانة رقم ٢٩٠٧.
وتمامه: عن أبي هريرة -رضي الله عنه-، أن الحسن بن علي، أخذ تمرة من تمر الصدقة فجعلها في فيه، فقال النبي -صلى الله عليه وسلم- بالفارسية: "كخ، كخ، أما تعرف أنا لا نأكل الصدقة".
(¬٦) ٢/ ٧٥٤ كتاب الزكاة باب ترك استعمال آل النبي على الصدقة رقم ١٠٧٢. عن عبد =','3','95',NULL,NULL,'0');
INSERT INTO [b46] VALUES (771,'ومولاه.
<hr><span data-type="title">منحة السلوك</span>

والهاشمي: آل عباس، وآل علي، وآل جعفر، وآل عقيل، وآل الحارث ابن عبد المطلب. ذكرهم القدوري هكذا (¬١).
وفائدة تخصيصهم بالذكر: جواز الدفع إلى بعض بني هاشم، وهم بنو أبي لهب (¬٢). قال أبو نصر البغدادي (¬٣): وما عدا المذكورين، لا تحرم عليهم الزكاة.
قوله: ومولاه.
أي: ولا يدفع أيضًا إلى مولى الهاشمي (¬٤)؛ لقوله -صلى الله عليه وسلم-: "إن الصدقة لا تحل لنا، وإن مولى القوم من أنفسهم" رواه الجماعة، وصححه الترمذي (¬٥).
_________
= المطلب بن ربيعة بن الحارث في مختصره ١/ ١٥٦ ونصه: "ولا تدفع إلى بني هاشم وهم آل علي، وآل عباس، وآل جعفر، وآل عقيل، وآل حارث بن عبد المطلب، ومواليهم".
(¬١) في مختصره ١/ ١٥٦ ونصه: "ولا تدفع إلى بني هاشم وهم آل علي، وآل عباس، وآل جعفر، وآل عقيل، وآل حارث بن عبد المطلب، ومواليهم".
(¬٢) تبيين الحقائق ١/ ٣٠٣، شرح فتح القدير ٢/ ٢٧٤، بدائع الصنائع ٢/ ٤٩، الهداية ١/ ١٢٢، العناية ٢/ ٢٧٤.
(¬٣) في كتابه شرح القدوري (مخطوط) ج ١ لوحة ٨٦/ أالنسخة الأصلية لدى مكتبة مكة، تحت رقم ٥٧، فقه حنفي، ونصه: "وإذا ثبت أنها حرام عليهم، قلنا: هم البطون التي ذكرها صاحب الكتاب، ولا تحرم على من سواهم" ا. هـ.
(¬٤) وإليه ذهب الشافعية، والحنابلة.
وذهب المالكية: إلى جواز دفعها إلى مواليهم.
الكتاب ١/ ١٥٦، المختار ١/ ١٢١، ملتقى الأبحر ١/ ١٩١، كنز الدقائق ١/ ٣٠٣، كشف الحقائق ١/ ١١١، الوقاية ١/ ١١١، منح الجليل ٢/ ٨٥، الخرشي على خليل ٢/ ٢١٥، السراج الوهاج ص ٣٥٦، التذكرة ص ٧٤، الإقناع في حل ألفاظ أبي شجاع ١/ ٢١٤، كشاف القناع ٢/ ٢٩٠، الإفصاح ١/ ٢٣١.
(¬٥) أحمد ٦/ ٨، وأبو داود ٢/ ١٢٣ كتاب الزكاة، باب الصدقة على بني هاشم رقم ١٦٥٠، =','3','96',NULL,NULL,'0');
INSERT INTO [b46] VALUES (772,'ولو ظنه مصرفًا فأعطاه فأخطأ سقطت عنه،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-399>[الظن والشك في المصرف]</span>
قوله: ولو ظنه مصرفًا.
يعني: دفع إلى رجل يظنه فقيرًا فأعطاه فأخطأ، بأن بان أنه غني، أو هاشمي، أو كافر، أو دفع في ليلة مظلمة فبان أنه أبوه، أو ابنه، سقطت عنه الزكاة، ولا إعادة عليه عندهما (¬١). خلافًا لأبي يوسف؛ لأن خطأه ظهر بيقين (¬٢).
ولهما: ما روي عن معن بن يزيد (¬٣) قال:
_________
= والترمذي ٣/ ١٩ كتاب الزكاة، باب ما جاء في كراهية الصدقة للنبي وأهل بيته ومواليه رقم ٦٥٧، والنسائي ٥/ ١٠٧ كتاب الزكاة، باب الصدقة لا تحل للنبي رقم ٢٦١٣، ورواه أيضًا ابن خزيمة ٢٣٤٤ كتاب الزكاة، باب الزكاة عن استعمال موالي النبي على الصدقة ٤/ ٥٧، والطحاوي ٢/ ٨ في شرح المعاني كتاب الزكاة، باب الصدقة على بني هاشم، وابن حبان ٨/ ٨٨ كتاب الزكاة، باب ذكر الزجر عن أكل الصدقة المفروضة لآل محمد رقم ٣٢٩٣، والطيالسي ص ١٣١ رقم ٩٧٢، والبغوي رقم ١٦٠٧، وابن أبي شيبة ٣/ ٢١٤ وأبو يعلى ٢٧٢٨، والحاكم ١/ ٤٠٤ كتاب الزكاة عن أبي رافع.
قال الحاكم: صحيح على شرط الشيخين ولم يخرجاه وسكت عنه الذهبي.
(¬١) الكتاب ١/ ١٥٦، المختار ١/ ١٢٢، الهداية ١/ ١٢٢، ملتقى الأبحر ١/ ٢٩٢، تبيين الحقائق ١/ ٣٠٤، شرح الوقاية ١/ ١١٢، كشف الحقائق ١/ ١١٢.
(¬٢) وإليه ذهب المالكية، والشافعية.
وذهب الحنابلة: إلى أنه إذا دفعها إلى من يظنه مستحقًا لها، فبان عبدًا، أو كافرًا، لم يجزئه، وإذا دفعها إلى من يظنه فقيرًا فبان غنيًا أجزأه.
الكتاب ١/ ١٥٦، المختار ١/ ١٢٢، الهداية ١/ ١٢٢، ملتقى الأبحر ١/ ١٩٢، شرح الوقاية ١/ ١١٢، كشف الحقائق ١/ ١١٢، تبيين الحقائق ١/ ٣٠٤، الكافي لابن عبد البر ص ١١٥، القوانين الفقهية ض ٧٥، المعونة ١/ ٤٤٥، التفريع ١/ ٢٩٨، روض الطالب ١/ ٤٠٤، أسنى المطالب ١/ ٤٠٤، رحمة الأمة ١/ ١١٢، الإنصاف ٣/ ٢٦٤، منتهى الإرادات ١/ ٤٣٥.
(¬٣) هو معن بن يزيد بن الأخنس بن حبيب السلمي، بايع هو وأبوه وجده النبي -صلى الله عليه وسلم- شهد =','3','97',NULL,NULL,'0');
INSERT INTO [b46] VALUES (773,'إلا في مكاتبه، ولو أعطاه شاكًّا لم تسقط، إلا أن يتحقق أنه مصرف.
<hr><span data-type="title">منحة السلوك</span>

كان أبي يزيد (¬١) أخرج دنانير يتصدق بها، فوضعها عند رجل في المسجد، فجئت فأخذتها، فأتيت بها، فقال: والله ما إياك أردت، فخاصمته إلى رسول الله -صلى الله عليه وسلم- فقال: "لك ما نويت يا يزيد، ولك ما أخذت يا معن" رواه البخاري (¬٢).
قوله: إلا في مكاتبه.
يعني: لا تسقط في هذه الصورة؛ لأنه لم يوجد الإخراج عن ملكه (¬٣)، وكذلك إذا ظهر عبده، أو مدبره، أو أم ولده (¬٤).
قوله: ولو أعطاه شاكًا لم تسقط، إلا أن يتحقق أنه مصرف.
يعني: إذا دفع الزكاة إليه وهو شاك، ولم يتحر، ولم يظهر أنه مصرف، لا تسقط إلا إذا ظهر صوابه يقينًا، أو بأكبر رأيه، فحينئذ يجوز (¬٥).
وكذا إذا تحرى ووقع أكبر رأيه أنه ليس بمصرف، فدفع مع ذلك لا
_________
= بدرًا، ونزل الكوفة، ودخل مصر ثم سكن دمشق واستشهد بمرج راهط سنة ٥٤ هـ.
الإصابة ٣/ ٤٥٠، تهذيب التهذيب ١٠/ ٢٥٣، طبقات ابن سعد ٦/ ٣٦، الأعلام ٧/ ٢٧٤.
(¬١) هو يزيد الجرمي أبو معن، بايع النبي -صلى الله عليه وسلم-، له ولأبيه ولابنه صحبه، يعد في أهل الكوفة، روى عنه ابنه معن.
أسد الغابة ٥/ ٢٠٩.
(¬٢) ٢/ ٥١٧ كتاب الزكاة، باب إذا تصدق على ابنه وهو لا يشعر رقم ١٣٥٦.
(¬٣) الكتاب ١/ ١٥٦، المختار ١/ ١٢٢، تبيين الحقائق ١/ ٣٠٥، الهداية ١/ ١٢٣، شرح الوقاية ١/ ١١٢، كشف الحقائق ١/ ١١٢.
(¬٤) كنز الدقائق ١/ ٣٠٥، تبيين الحقائق ١/ ٣٠٥، كشف الحقائق ١/ ١١٢، شرح الوقاية ١/ ١١٢، الكتاب ١/ ١٥٦، الاختيار ١/ ١٢٢، الهداية ١/ ١٢٣.
(¬٥) الهداية ١/ ١٢٣، شرح فتح القدير ٢/ ٢٧٦، تبيين الحقائق ١/ ٣٠٤، حاشية الشلبي ١/ ٣٠٤، العناية ٢/ ٢٧٦.','3','98',NULL,NULL,'0');
INSERT INTO [b46] VALUES (774,'ويكره إعطاؤه واحدًا من الزكاة نصابًا، ويكره نقلها إلى بلد آخر، إلا إلى قريب، أو أحوج.
<hr><span data-type="title">منحة السلوك</span>

<span data-type="title" id=toc-400>[صرف الزكاة: من صنف واحد]</span>
يجزئه، إلا إذا ظهر أنه فقير أو أجنبي بيقين، أو بدليل غالب (¬١).

<span data-type="title" id=toc-401>[حكم نقل الزكاة إلى بلد آخر]</span>
قوله: ويكره نقلها.
أي: نقل الزكاة إلى بلد آخر؛ لأن فيه ترك رعاية حق الجوار، إلا إذا نقل إلى قريبه، أو قوم هم أحوج من أهل بلده؛ لأن فيه صلة القريب، وزيادة دفع الحاجة (¬٢) (¬٣).
_________
(¬١) الهداية ١/ ١٢٣، شرح فتح القدير ٢/ ٢٧٧، تبيين الحقائق ١/ ٣٠٤، العناية ٢/ ٢٧٧، كشف الحقائق ١/ ١٢٢، البحر الرائق ٢/ ٢٤٨.
(¬٢) الكتاب ١/ ١٥٧، المختار ١/ ١٢٢، منحة الخالق ٢/ ٢٥٠، البحر الرائق ٢/ ٢٥٠، تبيين الحقائق ١/ ٣٠٥، الهداية ١/ ١٢٣.
(¬٣) اختلف العلماء في حكم نقل الزكاة من بلد إلى بلد آخر.
فذهب الحنفية (أ)، والحنابلة في إحدى الروايات: إلى كراهة نقلها، وهو مروي عن الحسن والنخعي (ب).
وذهب المالكية (جـ)، والشافعية على الصحيح من مذهبهم (د)، وهو إحدى الروايتين عن أحمد، وهي المذهب عند الحنابلة (هـ): إلى تحريم نقل الزكاة من بلد المال، إلى بلد آخر، إذا وجد في بلد المال مستحق لها.
وفي قول للشافعية: أن نقلها جائز (و).
وعن أحمد رواية: أنه يجوز نقلها إلى الثغر (ز). =
_________
(أ) القوانين الفقهية لابن جزي ص ١٠٩، وراجع: مواهب الجليل ٢/ ٣٥٧.
(ب) المجموع ٦/ ٢٢١، ٢٢٢، روضة الطالبين ٢/ ٣٣١.
(جـ) المغني ٢/ ٦٧١، الشرح الكبير لابن قدامة ١/ ٦٩٨، الإنصاف ٣/ ٢٠٠.
(د) المبسوط ٢/ ١٨٠، ١٨١، وراجع: اللباب في شرح الكتاب ١/ ١٥٧.
(هـ) المغني ٢/ ٦٧٢، الشرح الكبير لابن قدامة ١/ ٦٩٨، الإنصاف ٣/ ٢٠١.
(و) المجموع ٦/ ٢٢١، ٢٢٢، روضة الطالبين ٢/ ٣٣٢.
(ز) الإنصاف ٣/ ٢٠١.','3','99',NULL,NULL,'0');
INSERT INTO [b46] VALUES (775,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
=  وعنه: يجوز نقلها إلى الثغر، وغيره، مع رجحان الحاجة، وهو اختيار شيخ الإسلام ابن تيمية رحمه الله وقيده بمسيرة يومين (أ).
ثم اختلف القائلون بتحريم نقلها في إجزائها إذا نقلت على قولين:
القول الأول: أنها لا تجزيء: فيما إذا كان في بلد المال من هو أحوج ممن نقلت إليهم، وهو أحد القولين: عند المالكية (ب).
وأحد القولين: عند الشافعية، وهو المذهب عند الشافعية (جـ)، ورواية عند الحنابلة، وهو اختيار الخرقي، وابن حامد، والقاضي وجماعة (د).
القول الثاني:
أنها تجزيء إذا كان من نقلت إليهم أحوج من أهل بلد المال، أو مساوين لهم في الحاجة، وهو القول الآخر: للمالكية (هـ)، والقول الثاني عند الشافعية (و)، ورواية عن أحمد، وهي اختيار أبي الخطاب، وابن قدامة، وهي المذهب عند الحنابلة (ز).
الأدلة:
أدلة القول الأول:
أ - أن فقراء المحل قد ملكوا الزكاة قبل صرفها (حـ).
٢ - ولأن من أخرج الزكاة من بلد المال قد دفع الزكاة، إلى غير من أمر بدفعها، أشبه ما لو دفعها إلى غير الأصناف (ط). =
_________
(أ) الإنصاف ٣/ ٢٠١.
(ب) الخرشي على مختصر خليل ٢/ ٢٢٣.
(جـ) المجموع ٦/ ٢٢١، ٢٢٢، روضة الطالبين ٢/ ٣٣١، ٣٣٢.
(د) المغني ٢/ ٦٧٢، الشرح الكبير لابن قدامة ١/ ٦٩٨، الإنصاف ٣/ ٢٠٢.
(هـ) الخرشي على مختصر خليل ٢/ ٢٢٣.
(و) المجموع ٦/ ٢٢١، ٢٢٢، روضة الطالبين ٢/ ٣٣١، ٣٣٢.
(ز) المغني ٢/ ٦٧٢، الشرح الكبير لابن قدامة ١/ ٦٩٨، الإنصاف ٣/ ٢٠٢.
(حـ) قليوبي وعميرة ٣/ ١٨٩.
(ط) المغني ٢/ ٦٧٢.','3','100',NULL,NULL,'0');
INSERT INTO [b46] VALUES (776,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
= ٣ - ولأن حقوق الله جل وعلا على ضربين: حق على البدن، وحق في المال، ثم ثبت أن من حقوق الأبدان ما يختص بمكان وهو الوقوف، والطواف، والسعي، فجاز أن يكون من حقوق الأموال ما يختص بمكان (أ).
أدلة القول الثاني:
١ - أن الصدقات في عهده -صلى الله عليه وسلم- كانت تنقل إليه من القرى والقبائل (ب)، وكذا كانت تُحمل إلى أبي بكر، وعمر -رضي الله عنهما- من غير المدينة (جـ).
٢ - أن مصرف الزكاة مطلق الفقراء بالنص، وقد دفعها إليهم (د).
المناقشة:
يمكن مناقشة أدلة القول الثاني بما يلي:
أما الدليل الأول فإن ما حمل إلى المدينة من تلك الزكوات محمول على أنه إما فائض عن حاجة أهل البلاد التي جاء منها، أو أنه جُبي لشدة حاجة أهل المدينة، وعلى كلا الاحتمالين لا يستقيم هذا الدليل.
وأما الدليل الثاني: فإن هذا النص مطلق قُيّدَ بقوله -صلى الله عليه وسلم- لمعاذ: "فإن هم أطاعوك لذلك، فأعلمهم أن الله افترض عليهم صدقة، تؤخذ من أغنيائهم فتُرد في فقرائهم" متفق عليه (هـ).
الترجيح:
بناء على ما سبق من الأدلة، والمناقشة، فإن الراجح هو القول الأول: وهو القول: بعدم إجزاء الزكاة المخرجة عن بلد المال الذي أهله أشد حاجة لها، أو مساوون غيرهم في الحاجة إليها، إلى بلد آخر، وذلك لما في إخراجها من مخالفة لأمره -صلى الله عليه وسلم-، وترك للعدل.
ولأن إخراجها عن بلدها إلى بلد آخر، قد يحمل مخرجيها على منعها، أو إخراجها غير طيبة بها نفوسهم؛ لما يرون من حرمان فقرائهم منها.
_________
(أ) انظر: الروايتين والوجهين للقاضي أبي يعلى الفراء لعبد الكريم اللاحم ١/ ٢٣٤.
(ب) المبسوط ٢/ ١٨١.
(جـ) الروايتين والوجهين ١/ ٢٣٥، والأثر أخرجه ابن خزيمة ٤/ ٦٧، ٦٨.
(د) البناية في شرح الهداية ٣/ ٥٦٥، الروايتين والوجهين ١/ ٢٣٥.
(هـ) صحيح البخاري مع فتح الباري ٣/ ٢٦١، صحيح مسلم بشرح النووي ١/ ١٦١.','3','101',NULL,NULL,'0');
INSERT INTO [b46] VALUES (777,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

والحوج -بضم الحاء، وسكون الواو-: جمع أحوج (¬١).
_________
(¬١) مختار الصحاح ص ٦٧ مادة ح وج، القاموس المحيط ١/ ٧٣٣ مادة ح وج، مجمل اللغة ص ١٨٨ باب الحاء والواو وما يثلثهما مادة حوج.','3','102',NULL,NULL,'0');
INSERT INTO [b46] VALUES (778,'<span data-type="title" id=toc-402>باب صدقة الفطر</span>
تجب على كل مسلم حُرٍّ مالك نصابًا، فاضلًا عن حاجته الأصلية،
<hr><span data-type="title">منحة السلوك</span>

قوله: صدقة الفطر.
أي: هذا في بيان أحكام صدقة الفطر، أو تكون صدقة الفطر مبتدأ، وقوله: تجب، خبره.
الأصل في وجوبها: ما قال ابن عباس -رضي الله عنهما-: "فرض رسول الله -صلى الله عليه وسلم- زكاة الفطر طُهرة للصائم، من اللغو، والرفث، وطعمة للمساكين، من أداها قبل الصلاة فهي زكاة مقبولة، ومن أداها بعد الصلاة فهي صدقة من الصدقات" رواه أبو داود (¬١).

<span data-type="title" id=toc-403>[من تجب عليه زكاة الفطر]</span>
قوله: على كل مسلم حر مالك نصابًا، فاضلًا عن حاجته الأصلية (¬٢).
أما اشتراط الإسلام: فلوقوع القربة (¬٣)، وأما اشتراط ملك النصاب:
_________
(¬١) ٢/ ١١١ كتاب الزكاة، باب زكاة الفطر رقم ١٦٠٩، وابن ماجه ١/ ٥٨٥ كتاب الزكاة، باب صدقة الفطر رقم ١٨٢٧، والدارقطني ٢/ ١٣٨ كتاب زكاة الفطر، والحاكم ١/ ٤٠٩ كتاب الزكاة، والبيهقي في السنن الكبرى ٤/ ١٦٣ كتاب الزكاة باب الكافر يكون فيمن يمون فلا يؤدى عنه زكاة الفطر.
قال الدارقطني ٢/ ١٣٨: ليس فيهم مجروح.
وقال الحاكم ١/ ٤٠٩: هذا حديث صحيح على شرط البخاري ولم يخرجاه.
وحسنه النووي في المجموع ٦/ ١٢٦ وابن قدامة في المغني ٢/ ٦٥٠.
(¬٢) بداية المبتدي ١/ ١٢٣، كنز الدقائق ١/ ٣٠٦، الهداية ١/ ١٢٣، تبيين الحقائق ١/ ٣٠٦، البحر الرائق ٢/ ٢٥٢، الكتاب ١/ ١٥٨، الوقاية ١/ ١١٢، كشف الحقائق ١/ ١١٢، تنوير الأبصار ٢/ ٣٥٩.
(¬٣) تحفة الفقهاء ١/ ٣٣٤، بدائع الصنائع ٢/ ٦٩، حاشية رد المحتار ٢/ ٣٥٩، تبيين الحقائق ١/ ٣٠٦، الاختيار ١/ ١٢٣، الهداية ١/ ١٢٤.','3','103',NULL,NULL,'0');
INSERT INTO [b46] VALUES (779,'وإن كان غير نامٍ عنه، وعن ولده الصغير الذي لا شيء له، وعن عبيده للخدمة،
<hr><span data-type="title">منحة السلوك</span>

فلقوله -صلى الله عليه وسلم-: "خير الصدقة ما كان عن ظهر غنى، وابدأ بمن تعول" رواه البخاري (¬١)، وهو أن يكون مالكًا لمقدار النصاب، فاضلًا عن مسكنه، وثيابه، وأثاثه، وفرسه، وسلاحه، وعبيده (¬٢).
وقال الشافعي: يجب على كل من يملك زيادة على قوت يومه، لنفسه وعياله (¬٣).
قوله: وإن كان. أي: النصاب غير نامٍ.
يعني: لا يشترط أن يكون النصاب ناميًا لوجوب صدقة الفطر؛ لأنها تجب بالقدرة الممكنة، دون الميسرة، بخلاف الزكاة (¬٤).
قوله: عنه. أي: عن نفسه، وعن ولده الصغير الذي لا شيء له، وعن عبيده للخدمة؛ لأن السبب بأن يمونه ويلي عليه (¬٥)؛ لما روي أنه -صلى الله عليه وسلم-: "أمر
_________
(¬١) ٢/ ٥١٨ كتاب الزكاة، باب لا صدقة إلا عن ظهر غنى رقم ١٣٦٠ عن أبي هريرة -رضي الله عنه-.
(¬٢) الكتاب ١/ ١٠٩٣، كنز الدقائق ١/ ٣٠٦، البحر الرائق ٢/ ٢٥٢، حاشية رد المحتار ٢/ ٣٦٠، تبيين الحقائق ١/ ٣٠٦.
(¬٣) وإليه ذهب المالكية، والحنابلة: مع اتفاق الأئمة على وجوب زكاة الفطر على الأحرار المسلمين، وخلافهم في صفة من تجب عليه منهم. قال ابن هبيرة في الإفصاح ١/ ٢٢٠: واتفقوا على وجوب زكاة الفطر على الأحرار المسلمين.
منح الجليل ١/ ١٠٢، جواهر الإكليل ١/ ١٤٢ مغني المحتاج ١/ ٤٠٣، متن أبي شجاع ص ٩٠، التذكرة ص ٧٣، عمدة السالك وعدة الناسك ص ٢٠٠، مختصر الخرقي ص ٤٨، العمدة لابن قدامة ص ٢٨.
(¬٤) تبيين الحقائق ١/ ٣٠٦، الاختيار ١/ ١٢٣، الهداية ١/ ١٢٤، شرح الوقاية ١/ ١١٢، العناية ٢/ ٢٨٤، شرح فتح القدير ٢/ ٢٨٤.
(¬٥) بدائع الصنائع ٢/ ٦٩، الهداية ١/ ١٢٤، المختار ١/ ١٢٣، الكتاب ١/ ١٥٩، كشف الحقائق ١/ ١١٣، الاختيار ١/ ١٢٣، شرح الوقاية ١/ ١١٣. =','3','104',NULL,NULL,'0');
INSERT INTO [b46] VALUES (780,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

بصدقة الفطر على الصغير، والكبير، والحر، والعبد بمن يمونون" رواه الدارقطني (¬١). وهؤلاء المذكورون بهذه الصفة على الكمال.
قيد بقوله: "الذي لا شيء له"؛ لأنه إذا كان له مال يجب من ماله عندهما (¬٢)، خلافًا لمحمد (¬٣)، وقيد بقوله: "عبيده للخدمة"؛ لأنه إذا كان
_________
= قال ابن هبيرة: واتفقوا على أن من كان مخاطبًا بزكاة الفطر -على اختلافهم في صفته- أنه يجب عليه زكاة الفطر عن نفسه، وعن غيره من أولاده الصغار، ومماليكه المسلمين الذين ليسوا للتجارة.
الإفصاح ١/ ٢٢٠.
(¬١) ٢/ ١٤١ كتاب زكاة الفطر حديث رقم ١٢، وروراه أيضًا البيهقي ٤/ ١٦١ كتاب الزكاة، باب إخراج زكاة الفطر عن نفسه وغيره.
من طريق القاسم بن عبد الله بن عامر بن زرارة، عن عمير بن عمار الهمداني، عن الأبيض بن الأغر، عن الضحاك بن عثمان، عن نافع، عن ابن عمر -رضي الله عنهما- قال: "أمر رسول الله -صلى الله عليه وسلم- ... ".
قال البيهقي ٤/ ١٦١: إسناده غير قوي.
وقال الدارقطني ٢/ ١٤١: رفعه القاسم وليس بالقوي، والصواب موقوف.
وقد رواه البخاري ٢/ ٥٤٧ كتاب الزكاة، باب صدقة الفطر على العبد وغيره من المسلمين رقم ١٤٣٢، ومسلم ٢/ ٦٧٧ كتاب الزكاة، باب زكاة الفطر على المسلمين من التمر والشعير رقم ٩٨٤ عن ابن عمر -رضي الله عنهما- بلفظ: "فرض رسول الله -صلى الله عليه وسلم- زكاة الفطر، صاعًا من تمر، أو صاعًا من شعير، على العبد والحر، والذكر والأنثى، والصغير والكبير من المسلمين".
(¬٢) لأن الشرع أجراه مجرى المؤنة، حيث وجب على الإنسان من جهة غيره فأشبه النفقة، ونفقة الصغير في ماله إذا كان له مال.
الهداية ١/ ١٢٤، تبيين الحقائق ١/ ٣٠٧، شرح الوقاية ١/ ١١٣، كشف الحقائق ١/ ١١٣، العناية ٢/ ٢٨٥، شرح فتح القدير ٢/ ٢٨٥.
(¬٣) وزفر: حيث يريان أنه يجب على الأب الغني، صدقة ولده الصغير الغني؛ لأنها زكاة في =','3','105',NULL,NULL,'0');
INSERT INTO [b46] VALUES (781,'ولو أنه كافر، بخلاف ولده الكبير وزوجته، ولو أدى عنهما
<hr><span data-type="title">منحة السلوك</span>

للتجارة، لا تجب عليه عنه شيء (¬١).
قوله: ولو أنه كافر.
أي: ولو أن العبد كافر؛ لإطلاق ما رويناه (¬٢).
قوله: بخلاف ولده الكبير.
أي: لا تجب عليه عن ولده الكبير؛ لأنه لا يمونه، ولا يلي عليه، فانعدم السبب (¬٣).
ولا عن زوجته أيضًا؛ لأنه لا يلي عليها، ولا يمونها إلا لضرورة مصالح النكاح، ولهذا لا تجب عليه الرواتب نحو الأدوية (¬٤).
قوله: ولو أدى عنهما.
_________
= الشريعة، كزكاة المال، فلا تجب على الصغير؛ ولأنها عبادة، والصغير ليس بأهل لوجوبها.
الهداية ١/ ١٢٤، تبيين الحقائق ١/ ٣٠٧، كشف الحقائق ١/ ١١٣، شرح الوقاية ١/ ١١٣ العناية ٢/ ٢٨٥، شرح فتح القدير ٢/ ٢٨٥، البحر الرائق ٢/ ٢٥٢.
(¬١) بدائع الصنائع ٢/ ٧٠، بداية المبتدي ١/ ١٢٤، البحر الرائق ٢/ ٢٥٢، كنز الدقائق ١/ ٣٠٧، العناية ٢/ ٢٨٦، شرح فتح القدير ٢/ ٢٨٦، الهداية ١/ ١٢٤.
(¬٢) المختار ١/ ١٢٣، الكتاب ١/ ١٥٩، ملتقى الأبحر ١/ ١٩٤، تنوير الأبصار ٢/ ٣٦٣، الدر المختار ١٢/ ٣٦٣، بدائع الصنائع ٢/ ٧٠، الهداية ١/ ١٢٤.
(¬٣) وكذا عند المالكية: إلا إذا كان فقيرًا فيُخرج عنه والدُه.
الكتاب ١/ ١٥٩، تحفة الفقهاء ١/ ٣٣٦، العناية ٢/ ٢٨٦، الهداية ١/ ١٢٤، الاختيار ١/ ١٢٣، الدر المختار ٢/ ٣٦٣، التلقين ص ٥١، الكافي في فقه المالكية ص ١١١.
(¬٤) العناية ٢/ ٢٨٦، تبيين الحقائق ١/ ٣٠٧، الدر المختار ٢/ ٣٦٣، الهداية ١/ ١٢٤، الاختيار ١/ ١٢٣، حاشية رد المحتار ٢/ ٣٦٣.','3','106',NULL,NULL,'0');
INSERT INTO [b46] VALUES (782,'تبرعًا ولم يعلما أجزأهما.
ولا تجب عن مكاتبه، بخلاف مدبره، وأم ولده، ولا عن عبد، أو عبيد بين اثنين.
<hr><span data-type="title">منحة السلوك</span>

أي: ولو أدى الوالد عن ولده الكبير، أو الزوج عن زوجته على وجه التبرع، وهما لم يعلما ذلك، أجزأهما استحسانًا؛ لأنه مأذون فيه عادة (¬١).

<span data-type="title" id=toc-404>[دفع الزكاة عن الأرقاء]</span>
قوله: ولا تجب عن مكاتبه.
لعدم الولاية عليه (¬٢)، وكذا المستسعي، ذكره صاحب "التحفة" (¬٣).
قوله: بخلاف مدبره، وأم ولده.
يعني: يجب على المولي أن يخرج صدقة الفطر عن مدبره، وأم ولده؛ لأنه يلي عليهما (¬٤).
قوله: ولا عن عبدٍ.
أي: ولا تجب عن عبدٍ، أو عبيدٍ بين موليين؛ [لوجود] (¬٥) الولاية، والمؤونة في حق كل منهما (¬٦).
وقال أبو يوسف ومحمد: يجب على كل واحدٍ منهما ما يخصه من
_________
(¬١) شرح فتح القدير ٢/ ٢٨٦، العناية ٢/ ٢٨٦، الدر المختار ٢/ ٣٦٣، حاشية رد المحتار ٢/ ٣٦٣، تحفة الفقهاء ١/ ٣٣٦، الهداية ١/ ١٢٤.
(¬٢) شرح فتح القدير ٢/ ٢٨٧، العناية ٢/ ٢٨٦.
(¬٣) تحفة الفقهاء ١/ ٣٣٧.
(¬٤) العناية ٢/ ٢٨٥، تحفة الفقهاء ٢/ ٣٣٦، الهداية ١/ ١٢٤، الاختيار ١/ ١٢٣، تنوير الأبصار ٢/ ٣٦٣، حاشية رد المحتار ٢/ ٣٦٣، الكتاب ١/ ١٥٩.
(¬٥) في س، ص، م "لقصور".
(¬٦) تبيين الحقائق ١/ ٣٠٧، الهداية ١/ ١٢٤، كشف الحقائق ١/ ١١٣، الدر المختار ٢/ ٣٦٣، شرح الوقاية ١/ ١١٣، العناية ٢/ ٢٨٧، البحر الرائق ٢/ ٢٥٢.','3','107',NULL,NULL,'0');
INSERT INTO [b46] VALUES (783,'وهي: نصف صاع من بر وزنًا، أو دقيقه، أو صاع من تمر، أو شعير،
<hr><span data-type="title">منحة السلوك</span>

الرؤوس، دون الأشقاص (¬١) ففي الثلاثة، تجب لأجل العبدين، وفي الخمسة تجب لأجل الأربعة (¬٢).

<span data-type="title" id=toc-405>[مقدار الزكاة]</span>
قوله: وهي. أي: صدقة الفطر، نصف صاع من برٍ وزنًا، أو دقيقه، أو صاعٍ من تمرٍ، أو شعيرٍ (¬٣)، وقال الشافعي: من كل نوع صاع (¬٤). لما روي عن أبي سعيدٍ الخدري -رضي الله عنه- قال: "كنا نخرج زكاة الفطر صاعًا من طعام، أو صاعًا من شعيرٍ أو صاعًا من تمرٍ، أو صاعًا من أقطٍ، أو صاعًا من زبيبٍ" رواه البخاري (¬٥).
_________
(¬١) الشقص -بكسر الشين-: هو القطعة من الأرض، والطائفة من الشيء. والشقيص الشريك.
لسان العرب ٧/ ٤٨ مادة شقص، تاج العروس ٤/ ٤٠٢ مادة شقص، معجم مقاييس اللغة ٣/ ٢٠٤ باب الشين والقاف وما يثلثهما مادة شقص، المطلع على أبواب المقنع ص ٢٧٨، الدر النقي ٣/ ٧٩٩، المصباح المنير ١/ ٣١٩ مادة الشِّقْصُ.
(¬٢) تبيين الحقائق ١/ ٣٠٧، الهداية ١/ ١٢٤، شرح الوقاية ١/ ١١٣، شرح فتح القدير ٢/ ٢٨٧، كشف الحقائق ١/ ١١٣، العناية ٢/ ٢٨٧، البحر الرائق ٢/ ٢٥٢، حاشية رد المحتار ٢/ ٣٦٣، التنوير في شرح الجامع الكبير لمحمود بن أحمد الحصيري (مخطوط) لوحة ٨/ أ.
(¬٣) الحجة على أهل المدينة ١/ ٥٣٥، بداية المبتدي ١/ ١٢٥، كشف الحقائق ١/ ١١٣، المختار ١/ ١٢٣، ملتقى الأبحر ١/ ١٩٤، الكتاب ١/ ١٦٠، كنز الدقائق ١/ ٣٠٨، الجامع الصغير، لمحمد بن الحسن ص ١٣٦.
(¬٤) وبه قال: مالك، وأحمد.
بداية المتجهد ١/ ٢٨١، مختصر خليل ص ٦٩، جواهر الإكليل ١/ ١٤٢، شرح ابن قاسم على أبي شجاع ٢/ ٢٩٢، حاشية البيجوري ١/ ٢٩٢، الإقناع للحجاوي ٢/ ٢٤٧، الروض المربع ص ١٦١.
(¬٥) ٢/ ٥٤٨ كتاب الزكاة، باب صدقة الفطر صاع من طعام رقم ١٤٣٥.','3','108',NULL,NULL,'0');
INSERT INTO [b46] VALUES (784,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

ولنا: ما روي أنه -صلى الله عليه وسلم-: "قام خطيبًا، فأمر بصدقة الفطر، صاع تمر، أو صاع شعيرٍ عن كل رأس، أو صاع برٍّ، أو قمحٍ بين اثنين، عن الصغير، والكبير، والحر، والعبد" رواه أبو داود (¬١)، وروي أن رسول الله -صلى الله عليه وسلم- خطب قبل يوم العيد بيومين، فقال: "إن صدقة الفطر مد من بر على كل إنسانٍ، أو صاع مما سواه من الطعام" رواه الدارقطني (¬٢).
_________
(¬١) ٢/ ١١٤ كتاب الزكاة، باب من روى نصف صاع من قمح رقم ١٦٢٠، ورواه أيضًا الإمام أحمد ٥/ ٤٣٢، وابن خزيمة ٤/ ٨٧ كتاب الزكاة، باب إخراج التمر والشعير في صدقة الفطر رقم ٢٤١٠، والطحاوي في شرح المعاني ٢/ ٤٥ كتاب الزكاة، باب مقدار صدقة الفطر، والطبراني في الكبير ٢/ ٨١، والدارقطني ٢/ ١٤٧ كتاب زكاة الفطر رقم ٣٧، والحاكم ٣/ ٢٧٩ كتاب معرفة الصحابة، والبيهقي في السنن ٤/ ١٦٧ كتاب الزكاة، باب من قال يخرج من الحنطة في صدقة الفطر نصف صاع.
من طرق عن الزهري، عن ثعلبة بن عبد الله، أو عبد الله بن ثعلبة، عن النبي -صلى الله عليه وسلم-، أو عن عبد الله بن ثعلبة بن صعير، عن أبيه، قال: "قام رسول الله -صلى الله عليه وسلم- ... ".
قال البيهقي ٤/ ١٧٠: وقد وردت أخبار عن الرسول -صلى الله عليه وسلم- في صاع من بر، ووردت أخبار في نصف صاع، ولا يصح شيء من ذلك.
(¬٢) ٢/ ١٤٨ كتاب زكاة الفطر رقم ٤٥ من طريق يحيى بن جرجة، عن الزهري، عن عبد الله بن ثعلبة بن أبي صعير، "أن رسول الله -صلى الله عليه وسلم- خطب ... ".
قال الدارقطني عن يحيى بن جرجة: ليس بقوي.
ورواه الترمذي ٣/ ٣٤ كتاب الزكاة، باب ما جاء في صدقة الفطر رقم ٦٧٤ عن عمرو بن شعيب، عن أبيه، عن جده، ولفظه: أن رسول الله -صلى الله عليه وسلم- بعث مناديًا في فجاج مكة: "ألا إن صدقة الفطر واجبة على كل مسلم، ذكر أو أنثى، حر أو عبد، صغير أو كبير، مدان من قمح، أو سواه صاع من طعام".
قال الترمذي: هذا حديث حسن غريب.
وقال ابن الجوزي في التحقيق ٢/ ١٤٤٧: الصحيح عن عبد الله بن ثعلبة بن صعير عن أبيه؛ لأن ثعلبة هو الصحابي لا صعير.
وقال ابن عبد الهادي في تنقيح التحقيق ٢/ ١٤٤٨: هذا حديث مضطرب الإسناد =','3','109',NULL,NULL,'0');
INSERT INTO [b46] VALUES (785,'أو دقيقه، أو سويقه،
<hr><span data-type="title">منحة السلوك</span>

والجواب عن حديث أبي سعيدٍ: أنه ليس بحجةٍ علينا؛ لأنه أخبر بفعل نفسه حيث قال: كنا نخرج، وفعل النبي -صلى الله عليه وسلم- ليس بموجب، ففعل الصحابة أولى بأن لا يكون موجبًا. والعجب من الشافعي: أنه لا يرى تقليد الصحابي واجبًا، فكيف قلد أبا سعيد في هذه المسألة؟! (¬١).
قوله: أو دقيقه. أي: دقيق البر، أو سويقه، أو دقيق الشعير، أو سويقه، كله جائز عندنا (¬٢)؛ خلافًا للشافعي (¬٣).
ولنا: ما روى سفيان (¬٤) بإسناده، إلى أبي سعيدٍ قال: "كنا نخرج على
_________
= والمتن. وقال -أي ابن عبد الهادي-: قال مهنا: ذكرت لأحمد حديث ثعلبة بن أبي صعير، فقال: ليس بصحيح، وسألته عن ابن أبي صعير أمعروف هو؟ قال: من يعرف ابن أبي صعير؟! ليس هو بمعروف.
وقال ابن عبد الهادي أيضًا: وذكر أحمد، وعلي بن المديني: ابن أبي صعير فضعفاه جميعًا وقال: ابن عبد البر: ليس دون الزهري من يقوم به حجة.
(¬١) بدائع الصنائع ٢/ ٧٢، شرح فتح القدير ٢/ ٢٩٣.
(¬٢) وعند الحنابلة.
كشف الحقائق ١/ ١١٤، الجامع الصغير للصدر الشهيد (مخطوط) ٢٤/ أالنسخة الأصلية لدى مكتبة الأوقاف بحلب الرفاعية ٧٩، تنوير الأبصار ٢/ ٣٦٤، الهداية ١/ ١٢٥، الاختيار ١/ ١٢٣، الجامع الصغير ص ١٣٦، المختار ١/ ١٢٣، ملتقى الأبحر ١/ ١٩٤، كنز الدقائق ١/ ٣٠٨، كشاف القناع ٢/ ٢٥٣، المبدع ٢/ ٣٩٤، الإقناع للحجاوي ٢/ ٢٥٣.
(¬٣) ومالك حيث لا يجزيء عندهما الخبز، ولا الدقيق ولا السويق، فالواجب هو الحب السليم.
المعونة ١/ ٤٣٨، التلقين ص ٥٢، الذخيرة ٣/ ١٦٨، بلغة السالك ١/ ٢٣٨، مغني المحتاج ١/ ٤٠٧، أسنى المطالب ١/ ٣٩٢، رحمة الأمة ١/ ١٠٧.
(¬٤) هو سفيان بن سعيد بن مسروق الثوري المضري، أبو عبد الله، أمير المؤمنين في الحديث، كان إمام أهل زمانه في علوم الدين، ولد بالكوفة سنة ٩٧ هـ، وراوده المنصور العباسي =','3','110',NULL,NULL,'0');
INSERT INTO [b46] VALUES (786,'وفي الزبيب روايتان،
<hr><span data-type="title">منحة السلوك</span>

عهد رسول الله -صلى الله عليه وسلم- صاع تمرٍ، أو شعيرٍ، أو أقطٍ، أو زبيبٍ، أو صاعًا من دقيق" رواه أبو داود (¬١).
قوله: وفي الزبيب روايتان.
في روايةٍ مشهورةٍ عن أبي حنيفة أو يجب نصف صاع؛ لأن نصف صاع لما وجب من التمر ففي الزبيب أولى؛ لأنه أعلى قيمةً منه (¬٢).
وفي روايةٍ: يجب صاع، كما هو قولهما (¬٣).
_________
= على أنه يلي الحكم فأبى وخرج من الكوفة، سكن مكة والمدينة، وانتقل إلى البصرة وبها توفي سنة ١٦١ هـ.
وفيات الأعيان ١/ ٢١٠، طبقات ابن سعد ٦/ ٢٥٧، حلية الأولياء ٦/ ٣٥٦، تهذيب التهذيب ٤/ ١١.
(¬١) ٢/ ١١٣ كتاب الزكاة، باب كم يؤدي في صدقة الفطر رقم ١٦١٨ من طريق حامد بن يحيى، أخبرنا سفيان، وحدثنا مسدد، ثنا يحيى، عن ابن عجلان، سمع عياضًا قال: سمعت أبا سعيد الخدري -رضي الله عنه- يقول: "لا أخرج أبدًا إلا صاعًا، إنا كنا نخرج على عهد رسول الله -صلى الله عليه وسلم- صاع تمر، أو شعير، أو أقط، أو زبيب" هذا حديث يحيى، زاد سفيان: "أو صاعًا من دقيق" قال حامد: فأنكروا عليه فتركه سفيان، قال أبو داود: "فهذه الزيادة وهم من ابن عيينة" اهـ.
ورواه البخاري ٢/ ٥٤٨ كتاب الزكاة، باب صدقة الفطر صاع من طعام رقم ١٤٣٥، ومسلم ٢/ ٦٧٨ كتاب الزكاة، باب زكاة الفطر على المسلمين من التمر والشعير رقم ٩٨٥ عن أبي سعيد -رضي الله عنه- قال: "كنا نخرج زكاة الفطر صاعًا من طعام، أو صاعًا من شعير، أو صاعًا من تمر، أو صاعًا، من أقط أو صاعًا من زبيب".
(¬٢) بدائع الصنائع ٢/ ٧٢، تحفة الفقهاء ١/ ٣٣٧، الجامع الصغير ص ١٣٦، الحجة على أهل المدينة ١/ ٥٣٩، العناية ٢/ ٢٩٤، شرح فتح القدير ٢/ ٢٩٥.
(¬٣) تحفة الفقهاء ١/ ٣٣٧، الهداية ١/ ١٢٥، بدائع الصنائع ٢/ ٧٢، ملتقى الأبحر ١/ ١٩٥، كشف الحقائق ١/ ١١٤، تبيين الحقائق ١/ ٣٠٨، حاشية الشلبي ١/ ٣٠٨.','3','111',NULL,NULL,'0');
INSERT INTO [b46] VALUES (787,'والدقيق أفضل من البر، والدراهم أفضل منهما، وقيل: البر أفضل منهما.
<hr><span data-type="title">منحة السلوك</span>

قوله: والدقيق أفضل من البر (¬١).
وهو اختيار الفقيه أبي جعفر، ذكره في "الهداية" (¬٢)
قوله: والدراهم أفضل منهما.
أي: من الدقيق، والبر، وهو رواية عن أبي يوسف (¬٣).
قوله: وقيل: البر أفضل منهما.
أي: من الدراهم، والدقيق؛ لأنه أبعد عن الخلاف، وهو رواية أبي بكر الأعمش (¬٤) (¬٥).
_________
(¬١) العناية ٢/ ٢٩٦، بدائع الصنائع ٢/ ٧٢، الهداية ١/ ١٢٦، تبيين الحقائق ١/ ٣٠٨، الاختيار ١/ ١٢٤.
(¬٢) الهداية ١/ ١٢٦.
(¬٣) وذهب المالكية، والشافعية، والحنابلة: إلى أنه لا يجوز إخراج القيمة، بدلًا عن القوت.
التخريج في الخلاف (مخطوط) لوحة ٦٨/ أالنسخة الأصلية لدى مكتبة فاتح بتركيا، تحت رقم ٢٠٤٠، الهداية ١/ ١٢٦، تبيين الحقائق ١/ ٣٠٩، ملتقى الأبحر ١/ ١٩٥، الاختيار ١/ ١٢٤، التفريع ١/ ٢٩٧، التلقين ص ٥٢، مغني المحتاج ١/ ٤٠٧، روضة الطالبين ٢/ ٣٠٣، السلسبيل ٢١/ ٣٠٦، الإقناع لابن المنذر ١/ ١٨٤.
(¬٤) هو أبو بكر محمد بن سعيد بن محمد بن عبد الله، الفقيه، المعروف بالأعمش، تفقه على أبي بكر محمد الإسكاف، وتفقه عليه ولده أبو القاسم عبيد الله، والفقيه أبو جعفر الهندوني توفي سنة ٣٤٠ هـ.
الجواهر المضية ٣/ ١٦٠، الفوائد البهية ص ١٦٠، الطبقات السنية رقم ٢٠١٠، كتاب أعلام الأخيار رقم ١٨٤.
(¬٥) الهداية ١/ ١٢٦.','3','112',NULL,NULL,'0');
INSERT INTO [b46] VALUES (788,'والصاع: ثمانية أرطال بالعراقي. ووقتها: فجر يوم الفطر،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-406>[مقدار الصاع]</span>
قوله: والصاع: ثمانية أرطالٍ بالعراقي. وهذا عندهما (¬١).
وعند أبي يوسف: خمسة أرطالٍ وثلث رطلٍ (¬٢)، وهو مذهب أهل الحجاز (¬٣).
وقيل: لا خلاف بينهم في الصاع، وإنما أبو يوسف لما حرر صاع أهل المدينة، وجده خمسة أرطالٍ وثلث رطل، برطل أهل المدينة، وهو أكبر من رطل أهل بغداد؛ لأنه ثلاثون أستارًا، والرطل البغدادي: عشرون أستارًا، فإذا قابلت ثمانية أرطالٍ بالبغدادي بخمسة أرطالٍ وثلث بالمدني، تجدهما سواء (¬٤).

<span data-type="title" id=toc-407>[وقت الوجوب]</span>
قوله: ووقتها. أي: وقت صدقة الفطر، فجر يوم الفطر (¬٥).
وبه أخذ الشافعي في القديم (¬٦)، وقال في الجديد: وقتها غروب الشمس، من آخر يوم من رمضان (¬٧).
_________
(¬١) تبيين الحقائق ١/ ٣٠٩، بداية المبتدي ١/ ١٢٦، المختار ١/ ١٢٤، الهداية ١/ ١٢٦، تحفة الفقهاء ١/ ٣٣٨، كشف الحقائق ١/ ١١٤، العناية ٢/ ٢٩٧، حاشية رد المحتار ٢/ ٣٦٥.
(¬٢) تبيين الحقائق ١/ ٣٠٩، تحفة الفقهاء ١/ ٣٣٨، الهداية ١/ ١٢٦، الاختيار ١/ ١٢٤، كشف الحقائق ١/ ١١٤، العناية ٢/ ٢٩٧، شرح فتح القدير ٢/ ٢٩٧، حاشية رد المحتار ٢/ ٣٦٥.
(¬٣) ومذهب الشافعية، والحنابلة.
تبيين الحقائق ١/ ٣٠٩، شرح فتح القدير ٢/ ٢٩٧ الشرح الكبير للدردير، حاشية الدسوقي ١/ ٥٠٤، تحفة المحتاج ٣/ ٣٢٠، حاشية الشرواني على تحفة المحتاج ٣/ ٣٢٠، الكافي لأبي محمد عبد الله بن قدامة ١/ ٣٢٤، الشرح الكبير لأبي الفرج ابن قدامة ٢/ ٦٦٠.
(¬٤) تبيين الحقائق ١/ ٣١٠، حاشية رد المحتار ٢/ ٣٦٥، الإيضاح والتبيان ص ٥٧.
(¬٥) بداية المبتدي ١/ ١٢٦، الكتاب ١/ ١٦١، كنز الدقائق ١/ ٣١٠، تنوير الأبصار ٢/ ٣٦٧، المختار ١/ ١٢٤، الهداية ١/ ١٢٦، كشف الحقائق ١/ ١١٥، ملتقى الأبحر ١/ ١٩٤.
(¬٦) التنبيه ص ٦٠، المهذب ١/ ١٦٥.
(¬٧) وإليه ذهب الحنابلة، وهو القول المشهور عند المالكية. =','3','113',NULL,NULL,'0');
INSERT INTO [b46] VALUES (789,'ويستحب دفعها قبل الخروج إلى صلاة العيد.
<hr><span data-type="title">منحة السلوك</span>

ولنا: ما روي ابن عمر -رضي الله عنهما- قال: "فرض رسول الله -صلى الله عليه وسلم- زكاة الفطر من رمضان صاعًا من تمر، أو صاعًا من شعير" رواه أبو داود (¬١).
والفطر من رمضان بطلوع الفجر الثاني من يوم الفطر (¬٢).
وإذا ثبت أن وقت الوجوب يدخل بطلوع الفجر، فمن مات قبل ذلك سقطت فطرته؛ لأنه لم يدرك وقت الوجوب.
ومن أسلم، أو ولد بعد طلوع الفجر، لم تجب فطرته؛ لأنه لم يكن وقت الوجوب من أهل الفطرة (¬٣).
قوله: ويستحب دفعها قبل الخروج إلى صلاة العيد (¬٤).
_________
= والقول الآخر عند المالكية كما ذهب إليه الحنفية، وبه أخذ الشافعي في القديم: أنها من طلوع فجر يوم الفطر.
القوانين الفقهية ص ٧٦، بداية المجتهد ١/ ٢٨٢، التنبيه ص ٦٠، المهذب ١/ ١٦٥، المقنع ١/ ٣٤٠، المغني ٢/ ٦٢٨.
(¬١) ٢/ ١١٢ كتاب الزكاة باب كم يؤدي في صدقة الفطر رقم ١٦١١.
ورواه أيضًا البخاري ٢/ ٥٤٧ كتاب الزكاة باب صدقة الفطر على العبد وغيره من المسلمين رقم ١٤٣٢، ومسلم ٢/ ٦٧٧ كتاب الزكاة، باب زكاة الفطر على المسلمين من التمر، والشعير رقم ٩٨٤.
(¬٢) العناية ٢/ ٢٩٧، بدائع الصنائع ٢/ ٧٤، تبيين الحقائق ١/ ٣١٠، كشف الحقائق ١/ ١١٤، شرح فتح القدير ٢/ ٢٩٧، الهداية ١/ ١٢٦.
(¬٣) تحفة الفقهاء ١/ ٣٣٩، الهداية ١/ ١٢٦، الكتاب ١/ ١٦١، كنز الدقائق ١/ ٣١٠، تنوير الأبصار ٢/ ٣٦٧، الاختيار ١/ ١٢٤ كشف الحقائق ١/ ١١٥، ملتقى الأبحر ١/ ١٩٤، تبيين الحقائق ١/ ٣١٠.
(¬٤) وفاقًا للثلاثة.
الكتاب ١/ ١٦١، المختار ١/ ١٢٤، بداية المبتدي ١/ ١٢٦، ملتقى الأبحر ١/ ١٩٤، =','3','114',NULL,NULL,'0');
INSERT INTO [b46] VALUES (790,'ويصح تعجيلها مطلقًا،
<hr><span data-type="title">منحة السلوك</span>

لما روى ابن عمر -رضي الله عنهما-، قال: "أمرنا رسول الله -صلى الله عليه وسلم- بزكاة الفطر، أن تؤدى قبل خروج الناس إلى الصلاة" رواه أبو داود (¬١).

<span data-type="title" id=toc-408>[تعجيل زكاة الفطر]</span>
قوله: ويصح تعجيلها مطلقًا.
أي: سواء عجل قبل الفطر في رمضان، أو قبل رمضان؛ لوجود أداء المسبب بعد وجود السبب، كالتعجيل في الزكاة (¬٢).
وعند خلف بن أيوب (¬٣): يجوز تعجيلها بعد دخول رمضان، لا قبله (¬٤).
وقيل: يجوز تعجيلها في النصف الأخير من رمضان (¬٥).
_________
= الاختيار ١/ ١٢٤، الهداية ١/ ١٢٤، أقرب المسالك ص ٤١، مختصر خليل ص ٦٩، مغني المحتاج ١/ ٤٠٢، الإقناع في حل ألفاظ أبي شجاع ١/ ٢١٠، زاد المستقنع ص ١٦٣.
(¬١) ٢/ ١١١ كتاب الزكاة، باب متى تؤدى رقم ١٦١٠.
ورواه البخاري ٢/ ٥٤٧ كتاب الزكاة، باب فرض صدقة الفطر رقم ١٤٣٢، ومسلم ٢/ ٦٧٩ كتاب الزكاة، باب الأمر بإخراج زكاة الفطر قبل الصلاة رقم ٩٨٦.
(¬٢) تبيين الحقائق ١/ ٣١١، بدائع الصنائع ٢/ ٧٤، الهداية ١/ ١٢٦، حاشية الشلبي ١/ ٣١١، حاشية رد المحتار ٢/ ٣٦٧، البحر الرائق ٢/ ٢٥٥.
(¬٣) هو خلف بن أيوب العامري البلخي، أبو سعيد، من أصحاب مجد وزفر، وتفقه على أبي يوسف، كان ذا علم وعمل وتأله، موصوف بالصلاح والزهد، إمام محدث، فقيه، مفتي المشرق، توفي سنة ٢٠٥ هـ.
تاج التراجم ص ١٦٦، الفوائد البهية ص ٧١، شذرات الذهب ٢/ ٣٤، سير أعلام النبلاء ٩/ ٥٤١، ميزان الاعتدال ١/ ٦٥٩.
(¬٤) لأنه صدقة الفطر، ولا فطر قبل الشروع في الصوم.
بدائع الصنائع ٢/ ٧٤، شرح فتح القدير ٢/ ٢٩٩، تبيين الحقائق ١/ ٣١١، الاختيار ١/ ١٢٤، حاشية الشلبي ١/ ٣١١، البحر الرائق ٢/ ٢٥٥، العناية ٢/ ٢٩٩.
(¬٥) وهو قول نوح بن أبي مريم. =','3','115',NULL,NULL,'0');
INSERT INTO [b46] VALUES (791,'ولا تسقط بالتأخير بخلاف الأضحية.
<hr><span data-type="title">منحة السلوك</span>

وقيل: في العشر الأخير (¬١).
وعند الحسن بن زياد: لا يجوز (¬٢) تعجيلها أصلًا (¬٣).
والأصح (¬٤) ما ذكره المصنف (¬٥).
قوله: ولا تسقط بالتأخير.
لأنه يقدر على مثلها من عنده قربة، بخلاف الأضحية حيث تسقط إذا فاتت عن وقتها؛ لأنه لا يقدر على الإتيان بمثلها؛ لأنها لم تشرع قربة في سائر الأيام (¬٦).
_________
= الهداية ١/ ١٢٦، تبيين الحقائق ١/ ٣١١، العناية ٢/ ٢٩٩، شرح فتح القدير ٢/ ٢٩٩ البحر الرائق ٢/ ٢٥٥.
(¬١) الهداية ١/ ١٢٦، تبيين الحقائق ١/ ٣١١.
(¬٢) في ص "لا يصح".
(¬٣) وذهب المالكية، والحنابلة: إلى جواز تقديمها قبل العيد بيومين، فإن عجلها لأكثر من ذلك لم يجز. وذهب الشافعية: إلى جواز تقديمها من أول الشهر.
الاختيار ١/ ١٢٤، تبيين الحقائق ١/ ٣١١، البحر الرائق ٢/ ٢٥٥، الهداية ١/ ١٢٦، العناية ٢/ ٢٩٩، شرح فتح القدير ٢/ ٢٩٩ أقرب المسالك ص ٤١، بلغة السالك ١/ ٢٣٩، أسنى المطالب ١/ ٣٦٢، مغني المحتاج ١/ ٤١٦، الكافي لابن قدامة ١/ ٣٢١، المحرر ١/ ٢٢٧.
(¬٤) في ق م "والأولى".
(¬٥) وهو اختيار أبي بكر محمد بن الفضل، وعليه الفتوى، وصححه الكاساني، والمرغيناني، والزيلعي، والبابرتي، والموصلي.
بدائع الصنائع ٢/ ٧٤، الهداية ١/ ١٢٦، شرح فتح القدير ٢/ ٢٩٩، العناية ٢/ ٢٩٩، تبيين الحقائق ١/ ٣١١، المختار ١/ ١٢٤.
(¬٦) وفاقًا للثلاثة.
الهداية ١/ ١٢٦، الكتاب ١/ ١٦١، العناية ٢/ ٢٩٩، تبيين الحقائق ١/ ٣١١، حاشية =','3','116',NULL,NULL,'0');
INSERT INTO [b46] VALUES (792,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وقال الحسن بن زياد: تسقط صدقة الفطر بالتأخير، كالأضحية (¬١). والله أعلم.
_________
= الشلبي ١/ ٣١١، شرح فتح القدير ٢/ ٢٩٩، جواهر الإكليل ١/ ١٤٤، الشرح الصغير ١/ ٢٣٩، حاشية الجمل ٢/ ٢٧٧، رحمة الأمة ١/ ١٠٧، منار السبيل ١/ ٢٠٢، دليل الطالب ١/ ٢٠٢.
(¬١) العناية ٢/ ٣٠٠، تبيين الحقائق ١/ ٣١١، شرح فتح القدير ٢/ ٢٩٩.','3','117',NULL,NULL,'0');
INSERT INTO [b46] VALUES (793,'<span data-type="title" id=toc-409>كتاب الصوم</span>
يصح صوم رمضان من الصحيح المقيم بمطلق النية، ونية النفل، ونية واجب آخر.
<hr><span data-type="title">منحة السلوك</span>

كتاب الصوم
ذكر الصوم عقيب الزكاة وإن كان الوجه تقدم الحج عليه، من حيث إن له مناسبة بالزكاة في المال؛ لأن الحج عبادة مركبة من البدنيّ، والمالي؛ والصوم عبادة بدنية لا تعلق لها بالمال أصلًا، والمفرد قبل المركب.
وهو لغةً: إمساك مطلقًا (¬١).
وشرعًا: إمساك مخصوص، في وقت مخصوص، من شخص مخصوص، بنيةٍ (¬٢).

<span data-type="title" id=toc-410>[من يصح منه الصوم]</span>
قوله: يصح صوم رمضان من الصحيح المقيم، بمطلق النية، مثل ما إذا قال: نويت أن أصوم (¬٣)، ونية النفل مثل ما إذا قال: نويت أن أصوم نفلًا (¬٤)، ونية واجبٍ آخر مثل ما إذا كان عليه رمضان آخر، ونواه في هذا الرمضان
_________
(¬١) مختار الصحاح ص ١٥٧ مادة ص وم، القاموس المحيط ٢/ ٨٧١ مادة ص وم، المغرب ص ٢٧٤ مادة الصوم، المصباح المنير ١/ ٣٥٢ مادة صام، المطلع على أبواب المقنع ص ١٤٥، الدر النقي ٢/ ٣٥٥، لغة الفقه ص ١٢٣، طلبة الطلبة ص ٥١، النظم المستعذب ١/ ١٦٩.
(¬٢) كشف الحقائق ١/ ١١٥، الوقاية ١/ ١١٥، ملتقى الأبحر ١/ ١٩٦، كنز الدقائق ١/ ٣١٢، الاختيار ١/ ١٢٥، تنوير الأبصار ٢/ ٣٧١.
(¬٣) كنز الدقائق ١/ ٣١٢، بداية المبتدي ١/ ١٢٧، الدر المختار ٣٧٧، تبيين الحقائق ١/ ٣١٢، الهداية ١/ ١٢٧، الكتاب ١/ ١٦٢، المختار ١/ ١٢٥.
(¬٤) تنوير الأبصار ٢/ ٣٧٧، الهداية ١/ ١٢٧، الاختيار ١/ ١٢٥، ملتقى الأبحر ١/ ١٩٧، الكتاب ١/ ١٦٢، كشف الحقائق ١/ ١١٥، تبيين الحقائق ١/ ٣١٢.','3','118',NULL,NULL,'0');
INSERT INTO [b46] VALUES (794,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

ففي جميع ذلك تقع نيته عن رمضان؛ لأنه متعين، ولا يحتاج إلى التعيين (¬١).
وقال الشافعي: لا يجوز إلا بالتعيين عن فرض الوقت (¬٢).
وإنما قيد بقوله: "من الصحيح المقيم"؛ لأن المريض إذا نوى واجبًا آخر، فعن أبي حنيفة روايتان: في رواية يقع عما نوى (¬٣)، وفي رواية، وهي قولهما: يقع عن رمضان (¬٤). وهي الأصح (¬٥).
وكذلك المسافر إذا نوى واجبًا آخر، يقع عما نوى عند أبي حنيفة (¬٦)، وعندهما عن فرض الوقت (¬٧)، ولو نوى النفل، ففيه: روايتان (¬٨).
_________
(¬١) كنز الدقائق ١/ ٣١٣، الهداية / ١٩٨، شرح الوقاية ١/ ١١٥، ملتقى الأبحر ١/ ١٩٦.
(¬٢) وإليه ذهب المالكية، والحنابلة.
بداية المجتهد ١/ ٢٩٢، منح الجليل ٢/ ١٢٠، نهاية المحتاج ٣/ ١٥٩، السراج الوهاج ص ١٣٨، الكافي لابن قدامة ١/ ٣٥١، مختصر الخرقي ص ٨٥٠.
(¬٣) تحفة الفقهاء ١/ ٣٤٨، المختار ١/ ١٢٧، تبيين الحقائق ١/ ٣١٦، حاشية الشلبي ١/ ٣١٦، الاختيار ١/ ١٢٧، الهداية ١/ ١٢٨، البحر الرائق ٢/ ٢٦١.
(¬٤) تحفة الفقهاء ١/ ٣٤٨، المختار ١/ ١٢٧، تبيين الحقائق ١/ ٣١٦، الاختيار ١/ ١٢٧، حاشية الشلبي ١/ ٣١٦، الهداية ١/ ١٢٨، البحر الرائق ٢/ ٢٦١.
(¬٥) المبسوط ٣/ ٦١، العناية ٢/ ٣١٠، حاشية الشلبي ١/ ٣١٦، البحر الرائق ٢/ ٢٦١.
(¬٦) تحفة الفقهاء ١/ ٣٤٨، بدائع الصنائع ٢/ ٨٤، منحة الخالق ٢/ ٢٦١ البحر الرائق ٢/ ٢٦١، تبيين الحقائق ١/ ٣١٥، حاشية الشلبي ١/ ٣١٥، شرح الوقاية ١/ ١١٦، كشف الحقائق ١/ ١١٦.
(¬٧) تحفة الفقهاء ١/ ٣٤٨، بدائع الصنائع ٢/ ٨٤، البحر الرائق ٢/ ٢٦١، منحة الخالق ٢/ ٢٦١، تبيين الحقائق ١/ ٣١٥، حاشية الشلبي ١/ ٣١٥، الهداية ١/ ١٢٨.
(¬٨) أصحهما عدم صحة ما نوى، ووقوعه عن فرض الوقت؛ لأن فائدة النفل الثواب، وهو في فرض الوقت أكثر، كما لو أطلق النية.
تحفة الفقهاء ١/ ٣٤٨، بدائع الصنائع ٢/ ٨٤، البحر الرائق ٢/ ٢٦١، تبيين الحقائق ١/ ٣١٥، الهداية ١/ ١٢٨.','3','119',NULL,NULL,'0');
INSERT INTO [b46] VALUES (795,'والنذر المعين يصح بمطلق النية، وبنية النفل لا بنية واجب آخر، وكلاهما يصح بنية من الليل والنهار قبل الضحوة الكبرى،
<hr><span data-type="title">منحة السلوك</span>

قوله: والنذر المعين.
مثل ما إذا نذر العشر الأول من رجب مثلًا، يصح بمطلق النية، مثل ما إذا قال: نويت أن أصوم (¬١)، وبنية النفل مثل ما إذا قال: نويت أن أصوم نفلًا (¬٢).
قوله: لا بنية واجب آخر. أي: لا يصح أداء النذر المعين بنية واجبٍ آخر (¬٣).
والفرق بينه وبين صوم رمضان: حيث يصح صوم رمضان بكل ما نوى، ولا يصح النذر المعين بنية واجب آخر؛ لأن التعيين في رمضان من جهة الشارع، وليس له إبطال هذا وفي النذر التعيين من جهة الناذر وله إبطال هذا فيما له وهو النفل، لا فيما عليه، وهو الواجب الآخر (¬٤) فافهم.
قوله: وكلاهما أي: صوم رمضان، والنذر المعين، يصح بنيةٍ من الليل والنهار، قبل الضحوة الكبرى (¬٥).
_________
(¬١) تبيين الحقائق ١/ ٣١٦، المختار ١/ ١٢٦، بداية المبتدي ١/ ١٢٨، ملتقى الأبحر ١/ ١٩٧، الكتاب ١/ ١٦٢، شرح الوقاية ١/ ١١٥، كشف الحقائق ١/ ١١٥، البحر الرائق ٢/ ٢٦١، الدر المختار ٢/ ٣٧٩.
(¬٢) تبيين الحقائق ١/ ٣١٦، المختار ١/ ١٢٦، حاشية رد المحتار ٢/ ٣٧٨، الدر المختار ٢/ ٣٧٨، الاختيار ١/ ١٢٦، بداية المبتدي ١/ ١٢٨، ملتقى الأبحر ١/ ١٩٧، كشف الحقائق ١/ ١١٥، البحر الرائق ٢/ ٢٦١.
(¬٣) شرح فتح القدير ٢/ ٣٠٨، تبيين الحقائق ١/ ٣١٦، العناية ٢/ ٣٠٨، كشف الحقائق ١/ ١١٥، الاختيار ١/ ١٢٦، الدر المختار ٢/ ٣٧٩، الهداية ١/ ١٢٨.
(¬٤) شرح فتح القدير ٢/ ٣٠٨، تبيين الحقائق ١/ ٣١٦.
(¬٥) المختار ١/ ١٢٦، الكتاب ١/ ١٦٢، الهداية ١/ ١٢٨، الاختيار ١/ ١٢٦، تبيين الحقائق ١/ ٣١٥، الدر المختار ٢/ ٣٧٧، حاشية رد المحتار ٢/ ٣٧٧.','3','120',NULL,NULL,'0');
INSERT INTO [b46] VALUES (796,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وقال الشافعي: الصوم الواجب لا يجوز إلا بنية من الليل (¬١)؛ لقوله -صلى الله عليه وسلم-: "لا صيام لمن لم يُبيِّت النية من الليل" رواه أبو داود، والترمذي وحسنه (¬٢).
_________
(¬١) وفاقًا للمالكية، والحنابلة.
أقرب المسالك ص ٤٢، بلغة السالك ١/ ٢٤٥ حاشية ابن قاسم العبادي على تحفة المحتاج ٣/ ٣٨٦، التذكرة ص ٧٦، نيل المآرب ١/ ٢٧٧، المحرر ١/ ٢٢٨.
(¬٢) أبو داود ٢/ ٣٢٩ كتاب الصوم، باب النية في الصيام رقم ٢٤٥٤، والترمذي ٣/ ٦٨٠ رقم ٧٣٠ كتاب الصوم، باب ما جاء لا صيام لمن لم يعزم من الليل، وابن أبي شيبة ١/ ٢٩٢ كتاب الصيام، باب من قال لا صيام لمن لم يعزم من الليل، رقم ٩١١١، وأحمد ٦/ ٢٨٧، والدارمي ١/ ٤٣١، كتاب الصوم، باب من لم يجمع الصيام من الليل رقم ١٦٥٠، والبخاري في التاريخ الصغير ١/ ١٣٢، وابن ماجه ١/ ٥٢٤ كتاب الصيام، باب ما جاء في فرض الصوم من الليل والخيار في الصوم رقم ١٧٠٠، والدارقطني ٢/ ١٧٢ كتاب الصيام، باب تبييت النية من الليل وغيره رقم ٢، والمروزي في السنة ٣٣، وابن خزيمة ٣/ ٢١٢ كتاب الصيام، باب إيجاب الإجماع على الصوم الواجب قبل طلوع الفجر رقم ١٩٣٣، والطحاوي في الشرح ٢/ ٥٤ كتاب الصيام، باب الرجل ينوي الصيام بعد ما يطلع الفجر، والطبراني في الكبير ٢٣/ ١٩٦ رقم ٣٣٧، والبيهقي ٤/ ٢٠٢ كتاب الصيام، باب الدخول في الصوم بالنية، والخطيب في التاريخ ٣/ ٩٢.
والنسائي ٤/ ١٩٦ كتاب الصيام، باب ذكر اختلاف الناقلين لخبر حفصة في ذلك رقم ٢٣٣١ من حديث حفصة -رضي الله عنها- ورواه عن ابن جريج عن ابن شهاب به.
قال النسائي في السنن الكبرى ٢/ ١١٧: الصواب عندنا موقوف، ولم يصح رفعه والله أعلم. ا. هـ.
وقال الدارقطني ٢/ ١٧٢: كلهم ثقات.
وقال في التلخيص الحبير ٢/ ١٨٨: قال أحمد: ماله عندي ذلك الإسناد. وقال في المجموع ١/ ٢٨٩: وإسناده صحيح في كثير من الطرق فيعتمد عليه، ولا يضر كون بعض طرقه ضعيفًا، أو موقوفًا، فإن الثقة الواصل له مرفوعًا، زيادة علم فيجب قبولها.
وقال البيهقي ٤/ ٢٠٢: قد اختلف على الزهري في إسناده وفي رفعه إلى النبي -صلى الله عليه وسلم-، =','3','121',NULL,NULL,'0');
INSERT INTO [b46] VALUES (797,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

ولنا: ما روى محمد، في كتاب "الاستحسان" (¬١) أن: "أعرابيًا شهد بهلال رمضان بعد الصبح، فقبل رسول الله -صلى الله عليه وسلم- شهادته، وأمر الناس بالصوم"؛ ولأن النية لما جاءت في الليل وهو ليس بوقت للصوم، فلأن يجوز في النهار وهو وقت الصوم أولى.
والحديث محمول على نفي الكمال (¬٢) كقوله -صلى الله عليه وسلم-: "لا صلاة لجار المسجد، إلا في المسجد" (¬٣).
_________
= وعبد الله بن أبي بكر أقام إسناده ورفعه، وهو من الثقات الأثبات.
وقال الدارقطني ٢/ ١٧٢: رفعه عبد الله بن أبي بكر عن الزهري، وهو من الثقات الرفعاء.
(¬١) من كتابه "الأصل" المعروف بـ "المبسوط" ٣/ ٨٣: وتمامه: قال: أخبرنا حازم بن إبراهيم البجلي، عن سماك بن حرب، عن عكرمة مولى ابن عباس، أن النبي -صلى الله عليه وسلم- قبل شهادة أعرابي وحده على رؤية هلال شهر رمضان، قدم المدينة فأخبرهم أنه قد رآه، فأمرهم رسول الله -صلى الله عليه وسلم- أن يصوموا بشهادته.
ورواه أيضًا الدارمي ٢/ ٥، وابن ماجه ١٦٥٢، وأبو داود ٢٣٤٠، والترمذي ٣/ ٣٨٢ رقم ٦٨٧، والنسائي ٤/ ١٣١، وابن الجارود ٣٧٩، وابن خزيمة ١٩٢٣، والطحاوي في المشكل ١/ ٢٠١، وابن حبان كما في الموارد ٨٧٠، والدارقطني ٢/ ١٥٧، والبيهقي ٤/ ٢١٢، قال الحاكم: صحيح عن ابن عباس.
(¬٢) بدائع الصنائع ٢/ ٨٦، الهداية ١/ ١٢٧، المبسوط ٣/ ٨٥، حاشية رد المحتار ٢/ ٣٧٧، البحر الرائق ٢/ ٢٦٠، تبيين الحقائق ١/ ٣١٤.
(¬٣) رواه الدارقطني ١/ ٤١٩ كتاب الصلاة باب الحث لجار المسجد على الصلاة فيه إلا من عذر رقم ١، ٢ عن جابر وأبي هريرة، ورواه الحاكم ١/ ٢٤٦ كتاب الإمامة وصلاة الجماعة عن أبي هريرة، والبيهقي ٣/ ٥٧ كتاب الصلاة، باب فرض الجماعة في غير الجمعة على الكفاية عن أبي هريرة.
قال في كشف الخفاء ٢/ ٤٩١: وأسانيدها ضعيفة ... وبالجملة فهو مأثور عن علي.
وقال ابن حجر في التلخيص ٢/ ٢١: حديث مشهور بين الناس وهو ضعيف ليس له إسناد ثابت.','3','122',NULL,NULL,'0');
INSERT INTO [b46] VALUES (798,'لا بعدها كالنفل.
والأفضل التَّبييت،
<hr><span data-type="title">منحة السلوك</span>

قوله: لا بعدها.
أي: لا يصح كلاهما بنية من النهار بعد الضحوة الكبرى، كالنفل، فإنه يجوز بنية من النهار قبل الضحوة الكبرى، وهي ما قبل نصف النهار (¬١) (¬٢).
وذكر "القدوري" (¬٣): ما بينه وبين الزوال.
وما ذكره المصنف، هو الأصح؛ لأن النية إنما تصح إذا وقعت في الليل، أو في أكثر النهار، والنية الواقعة قبل نصف النهار تكون واقعةً في أكثر النهار (¬٤)، بخلاف ما قال: "القدوري"، حيث قال: لا تقع النية في أكثر النهار لا محالة؛ لأن نصف اليوم من طلوع الفجر الصادق، إلى الضحوة الكبرى، لا وقت الزوال (¬٥).
قوله: والأفضل التبييت.
أي: النية من الليل؛ لتكون أبعد من الخلاف (¬٦).
_________
(¬١) وعند الشافعية: يصح النفل بنية قبل الزوال.
وعند الحنابلة: أي وقت من النهار نوى صوم التطوع، صح صومه.
تبيين الحقائق ١/ ٣١٥، الهداية ١/ ١٢٨، المبسوط ٣/ ٨٥، البحر الرائق ٢/ ٢٦٠، بدائع الصنائع ٢/ ٨٦، شرح المحلي ٢/ ٥٢، عميرة ٢/ ٥٢، الإنصاف ٣/ ٢٩٧، الفروع ٣/ ٤٣.
(¬٢) لسان العرب ١٤/ ٤٥٣ مادة ضحا.
(¬٣) في مصنفه "الكتاب" ١/ ١٦٢.
(¬٤) تبيين الحقائق ١/ ٣١٥، كنز الدقائق ١/ ٣١٣، البحر الرائق ٢/ ٢٦٠، منحة الخالق ٢/ ٢٦٠، حاشية رد المحتار ٢/ ٣٧٧، شرح الوقاية ١/ ١١٦، كشف الحقائق ١/ ١١٦.
(¬٥) تبيين الحقائق ١/ ٣١٥، البحر الرائق ٢/ ٢٦٠، حاشية رد المحتار ٢/ ٣٧٧، كشف الحقائق ١/ ١١٦، شرح الوقاية ١/ ١١٦.
(¬٦) تحفة الفقهاء ١/ ٣٤٩، بدائع الصنائع ٢/ ٨٥.','3','123',NULL,NULL,'0');
INSERT INTO [b46] VALUES (799,'ولو نوى المريض أو المسافر برمضان واجبًا آخر صح، ولو تطوع به ففيه روايتان، والنذر المطلق، والكفارة، وقضاء رمضان ونحوها لا يصح بنية في النهار.
ويستحب طلب الهلال ليلة ثلاثين من شعبان ورمضان،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-411>[نية من لا يجب عليه الصوم]</span>
قوله: ولو نوى المريض، أو المسافر برمضان واجبًا آخر، صح.
هذا عند أبي حنيفة، خلافًا لهما (¬١)، وقد مر (¬٢).

<span data-type="title" id=toc-412>[ما تجب النية فيه من الليل]</span>
قوله: والنذر المطلق، مثل ما إذا نذر عشرة أيامٍ مثلًا، من غير تعيين الأيام، والكفارة، وقضاء رمضان، ونحوها، لا يصح بنية في النهار، إذ ليس لها وقت متعين، فلم يتعين لها إلا بنية من الليل (¬٣).

<span data-type="title" id=toc-413>[طلب رؤية الهلال]</span>
قوله: ويستحب طلب الهلال ليلة ثلاثين من شعبان، ورمضان (¬٤).
لقوله -صلى الله عليه وسلم-: "لا تصوموا حتى تروا الهلال، ولا تفطروا حتى تروه، فإن غُمَّ عليكم فاقدروا له" رواه البخاري (¬٥).
ومعنى فاقدروا له: أي: قدروا عدده باستيفاء عدد الثلاثين (¬٦).
_________
(¬١) تحفة الفقهاء ١/ ٣٤٨، المختار ١/ ١٢٧، الهداية ١/ ١٢٨، الاختيار ١/ ١٢٧.
(¬٢) في ٣/ ١١٩.
(¬٣) الهداية ١/ ١٢٨، شرح فتح القدير ٢/ ٣١١، الكتاب ١/ ١٦٣، بداية المبتدي ١/ ١٢٨، ملتقى الأَبحر ١/ ١٩٧، شرح الوقاية ١/ ١١٥، تنوير الأبصار ٢/ ٣٧٧، الاختيار ١/ ١٢٦.
(¬٤) وكذا عند الحنابلة؛ لأن الشهر قد يكون تسعة وعشرين يومًا.
الكتاب ١/ ١٦٣، بداية المبتدي ١/ ١٢٩، العناية ٢/ ٣١٣، الاختيار ١/ ١٣٠، الهداية ١/ ١٢٩، شرح فتح القدير ٢/ ٣١٣، كشاف القناع ٢/ ٣٠٠، المستوعب ٣/ ٣٩٥.
(¬٥) ٢/ ٦٧٤ كتاب الصوم باب قول النبي إذا رأيتم الهلال فصوموا وإذا رأيتموه فأفطروا رقم ١٨٠٩ من طريق مالك، عن نافع، عن ابن عمر -رضي الله عنهما-، أن النبي -صلى الله عليه وسلم- قال ... ".
(¬٦) وعند الحنابلة: إن لم ير مع الصحو أكملوا عدة شعبان ثلاثين يومًا ثم صاموا، وإن حال دون منظره غيم أو قتر ليلة الثلاثين وجب صيامه في ظاهر المذهب. وعنه: لا يجب وإن صاموا لأصل الغيم لم يفطروا.','3','124',NULL,NULL,'0');
INSERT INTO [b46] VALUES (800,'فإن لم يروا فلا صوم، ولا فطر.
ويكره<span data-type="title" id=toc-414> صوم يوم الشك</span>،
<hr><span data-type="title">منحة السلوك</span>

قوله: فإن لم يروا فلا صوم، ولا فطر.
أي: فإن لم يروا الهلال ليلة الثلاثين من شعبان لا يصومون، وإن لم يروه ليلة ثلاثين من رمضان، لا يفطرون؛ لما روينا (¬١).

[صوم يوم الشك]
قوله: ويكره صوم يوم الشك (¬٢) (¬٣).
ووقوع الشك: بأن يغم عليهم هلال رمضان، أو هلال شعبان (¬٤)، وإنما يكره، لقوله -صلى الله عليه وسلم-: "لا تقدموا الشهر حتى تروا الهلال، أو تكملوا العدة، ثم صوموا حتى تروا الهلال، أو تكملوا العدة" رواه أبو داود، والنسائي (¬٥).
_________
(¬١) وكذا عند المالكية.
بداية المبتدي ١/ ١٢٩، المختار ١/ ١٢٨، الهداية ١/ ١٢٩، البحر الرائق ٢/ ٢٦٥، شرح فتح القدير ٢/ ٣١٣، العناية ٢/ ٣١٣، شرح الزرقاني على خليل ١/ ١٩٥، حاشية البناني ١/ ١٩٥.
(¬٢) بداية المبتدي ١/ ١٢٩، العناية ٢/ ٣١٤، الهداية ١/ ١٢٩، شرح فتح القدير ٢/ ٣١٤، كنز الدقائق ١/ ٣١٧، حاشية الشلبي ١/ ٣١٧.
(¬٣) وهو اليوم الذي يشك فيه، هل هو من شعبان، أو من رمضان.
المطلع على أبواب المقنع ص ١٥٥، طلبة الطلبة ص ٥٧، القاموس الفقهي ص ٢٠٠، معجم لغة الفقهاء ص ٢٦٥.
(¬٤) المبسوط ٣/ ٦٣، تبيين الحقائق ١/ ٣١٧.
(¬٥) أبو داود ٢/ ٢٩٨ كتاب الصوم باب إذا أغمى الشهر رقم ٢٣٢٦، والنسائي ٤/ ١٣٥ في كتاب الصيام، باب ذكر الاختلاف على منصور في حديث ربعي رقم ٢١٢٦، ورواه أيضًا ابن المنذر في الإقناع ١/ ١٩٠ كتاب الصوم، باب ذكر الأمر بالصوم لرؤية الهلال رقم ٦١، وابن خزيمة ٣/ ٢٠٣ كتاب الصيام، باب الزجر عن الصيام لرمضان قبل مضي ثلاثين يومًا لشعبان، إذا لم ير الهلال الجزء الأول منه رقم ١٩١١، وابن حبان ٨/ ٢٣٨ كتاب الصوم، باب ذكر البيان بأن قوله -صلى الله عليه وسلم- فصوموا ثلاثين أراد به إن لم تروا الهلال رقم =','3','125',NULL,NULL,'0');
INSERT INTO [b46] VALUES (801,'إلا أن يوافق وردًا له.
<hr><span data-type="title">منحة السلوك</span>

قوله: إلا أن يوافق وردًا له.
أي: إلا أن يوافق يوم الشك يوم ورده الذي كان من عادته أن يصوم فيه، فحينئذٍ لا يكون مكروهًا (¬١)؛ لقوله -صلى الله عليه وسلم-: "لا تقدموا صوم رمضان بيومٍ، ولا يومين، إلا أن يكون صوم يصومه رجل فليصم ذلك الصوم" رواه أبو داود (¬٢). فعلم بهذا أن المراد من قوله -صلى الله عليه وسلم-: "لا تقدموا الشهر، حتى تروا الهلال" الحديث، غير التطوع، حتى لا يزاد على صوم رمضان، كما زاد أهل الكتاب على صومهم (¬٣).
وقال الشافعي: يكره التطوع (¬٤)؛ لقوله -صلى الله عليه وسلم-: "إذا انتصف شعبان، فلا
_________
= ٣٤٥٨، والبزار ٩٦٩، والدارقطني ٢/ ١٦٠ كتاب الصيام رقم ٢٠، والبيهقي ٤/ ٢٠٨ كتاب الصوم، باب النهي عن استقبال شهر رمضان.
قال في المجموع ٦/ ٢٧٠: رواه أبو داود، والنسائي، والدارقطني، وغيرهم، بإسناد صحيح على شرط البخاري، ومسلم.
من طريق جرير عن منصور عن ربعي بن حراش عن حذيفة -رضي الله عنه-.
(¬١) وإليه ذهب الحنابلة.
وذهب المالكية إلى جواز صومه تطوعًا بلا كراهة.
تبيين الحقائق ١/ ٣١٧، شرح فتح القدير ٢/ ٣١٥، البحر الرائق ٢/ ٢٦٤، الهداية ١/ ١٢٩، كنز الدقائق ١/ ٣١٧، منحة الخالق ٢/ ٢٦٤. مختصر خليل ص ٧٠، التلقين ص ٥٦، الإفصاح ١/ ٢٣٥، مطالب أولي النهى ٢/ ٢١٩.
(¬٢) ٢/ ٣٠٠ كتاب الصوم، باب فيمن يصل شعبان برمضان رقم ٢٣٣٥.
ورواه أيضًا البخاري ٢/ ٦٧٦ كتاب الصوم، باب لا يتقدمن رمضان بصوم يوم، ولا يومين رقم ١٨١٥، ومسلم ٢/ ٧٦٢ كتاب الصيام، باب لا تقدموا رمضان بصوم يوم، ولا يومين رقم ١٠٨٢.
(¬٣) شرح فتح القدير ٢/ ٣١٥، الهداية ١/ ١٢٩، تبيين الحقائق ١/ ٣١٧، البحر الرائق ٢/ ٢٦٤، العناية ٢/ ٣١٥.
(¬٤) المجموع ٦/ ٤٠٠، الحاوي الكبير ٣/ ٤١٠.','3','126',NULL,NULL,'0');
INSERT INTO [b46] VALUES (802,'ومن رأى الهلال وحده فردت شهادته صام. فإن أفطر بعد الرد
<hr><span data-type="title">منحة السلوك</span>

تصوموا" رواه أبو داود (¬١). ولنا: ما روينا (¬٢)، وما رواه غير محفوظٍ. قاله: أحمد.

<span data-type="title" id=toc-415>[رد شهادة من رأى الهلال وحده]</span>
قوله: ومن رأى الهلال.
أي: هلال رمضان وحده، فردت شهادته صام (¬٣)؛ لقوله -صلى الله عليه وسلم-: "صوموا لرؤيته، وأفطروا لرؤيته" رواه البخاري (¬٤).
قوله: فإن أفطر بعد الرد.
_________
(¬١) ٢/ ٣٠٠ كتاب الصوم، باب النهي في كراهية ذلك -أي: فيمن يصل شعبان برمضان رقم ٢٣٣٧ ورواه أيضًا الدارمي ١٢/ ٤٤٣ كتاب الصوم، باب النهي عن الصوم بعد انتصاف شعبان رقم ١٦٩١، وابن ماجه ١/ ٥٢٨ كتاب الصيام، باب ما جاء في النهي أن يتقدم رمضان بصوم إلا من صام صومًا فوافقه رقم ١٦٥١، والترمذي ٣/ ٨٦ كتاب الصوم، باب ما جاء في كراهية الصوم في النصف الثاني من شعبان لحال رمضان رقم ٧٣٨، وقال الترمذي: حسن صحيح من حديث أبي هريرة -رضي الله عنه- بلفظ "إذا انتصف شعبان فلا تصوموا"، والنسائي في الكبرى ٢/ ١٧٢ كتاب الصيام باب صيام شعبان رقم ٢٩١١، والطحاوي في شرح المعاني ٢/ ٨٢ كتاب الصيام، باب الصوم بعد النصف من شعبان إلى رمضان، والبيهقي في السنن ٤/ ٢٠٩ كتاب الصيام، باب الخبر الذي ورد في النهي عن الصيام من حديث أبي هريرة، قال في البلوغ: رواه الخمسة واستنكره أحمد.
وقال الترمذي ٣/ ٨٦: حسن صحيح.
ورمز له السيوطي في الجامع الصغير ص ٣٦ رقم ٤٩٤ بالحسن.
(¬٢) تحفة الفقهاء ١/ ٣٤٢، الهداية ١/ ١٢٩، تبيين الحقائق ١/ ٣١٨.
(¬٣) كنز الدقائق ١/ ٣١٨، الكتاب ١/ ١٦٣، تبيين الحقائق ١/ ٣١٨، البحر الرائق ٢/ ٢٦٥، منحة الخالق ٢/ ٢٦٥، الوقاية ١/ ١١٧، كشف الحقائق ١/ ١١٧، المختار ١/ ١٣٠، فتاوى مؤيد زاده (مخطوط) لوحة ١٩٢/ أ.
(¬٤) ٢/ ٦٧٤ كتاب الصوم، باب قول النبي إذا رأيتم الهلال فصوموا وإذا رأيتموه فأفطروا رقم ١٨١٠. من حديث أبي هريرة -رضي الله عنه-.','3','127',NULL,NULL,'0');
INSERT INTO [b46] VALUES (803,'لزمه القضاء لا غير، وكذا لو أفطر قبله عند البعض، ولو صام ثلاثين يومًا لم يفطر وحده، فإن أفطر فلا كفارة عليه (¬١).
<hr><span data-type="title">منحة السلوك</span>

أي: بعد أن رد القاضي شهادته، لزمه القضاء لا غير. أي: لا الكفارة؛ لأن تفرده بالرؤية يوهم الغلط فيه، فتقع الشبهة، والكفارة تندريء بالشبهات (¬٢).
قوله: وكذا لو أفطر قبله.
أي: وكذا لا تجب الكفارة لو أفطر قبل رد القاضي شهادته عند البعض. وقيل: تجب.
والأول: أصح؛ لما بينا من ثبوت الشبهة (¬٣).
قوله: ولو صام ثلاثين يومًا لم يفطر وحده؛ لأن وجوب الصوم عليه في الابتداء كان للاحتياط، وهنا الاحتياط في تأخير الإفطار؛ لأنه يحتمل أن الهلال اشتبه عليه، ومع هذا لو أفطر لا كفارة عليه للحقيقة التي عنده (¬٤).
_________
(¬١) في ب زيادة "وعليه قضاؤه".
(¬٢) وإليه ذهب الشافعية، والحنابلة.
وذهب المالكية: إلى أنه يلزمه الصيام، فإذا أفطر متعمدًا، فعليه القضاء والكفارة.
كنز الدقائق ١/ ٣١٨، بداية المبتدي ١/ ١٣٠، رؤوس المسائل ص ٢٣٤، الهداية ١/ ١٣٠، المختار ١/ ١٣٠، تبيين الحقائق ١/ ٣١٨ التفريع ١/ ٣٠١، الشرح الكبير للدردير ١/ ٥١١، رحمة الأمة ١/ ١١٥، حلية العلماء ٣/ ١٨٣ العمدة لابن قدامة ص ٣١، منتهى الإرادات ١/ ٤٤٢.
(¬٣) وكذا صححه ابن الهمام، والزيلعي، وغيرهما.
شرح فتح القدير ٢/ ٣٢١، تبيين الحقائق ١/ ٣٩١، العناية ٢/ ٣٢١، الهداية ١/ ١٣٠، البحر الرائق ٢/ ٢٦٦.
(¬٤) فمن نظر إلى أن المورث للشبهة رد القاضي شهادته، قال: بوجوب الكفارة قبل الرد؛ لانتفاء ما يورثها، وتحقق الرمضانية؛ لتيقنه بالرؤية. ومن نظر إلى أن يوم الصوم يوم =','3','128',NULL,NULL,'0');
INSERT INTO [b46] VALUES (804,'ويقبل في هلال رمضان في الغيم شهادة واحد عدل، ولو كان عبدًا، أو أمة، أو محدودًا في قذف.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-416>[الشهادة في دخول الشهر وخروجه]</span>
قوله: ويقبل في هلال رمضان في الغيم شهادة واحدٍ عدلٍ (¬١)؛ لأنه أمر ديني فيقبل فيه خبر الواحد، ذكرًا كان أو أنثى، حرًا كان أو عبدًا، أو أمةً، أو محدودًا في قذفٍ (¬٢).
وعن أبي حنيفة: أنه لا تقبل شهادة المحدود في القذف؛ لأنه شهادة من وجه (¬٣).
والأول: أصح؛ لأنه من باب الإخبار (¬٤).
_________
= يصوم الناس فيه، وليس ما نحن فيه من اليوم يومًا يصوم الناس فيه، قال: بعدم وجوبها.
الهداية ١/ ١٣٠، العناية ٢/ ٣٢١، شرح فتح القدير ٢/ ٣٢١، تبيين الحقائق ١/ ٣١٩، البحر الرائق ٢/ ٢٦٥.
(¬١) وذهب المالكية: إلى أن رؤية الهلال في شهر رمضان تثبت بشهادة شاهدين عدلين، سواء كانت السماء صحوًا، أو غيمًا، وهو قول: عند الشافعية.
وذهب الشافعية، والحنابلة: إلى أنها تثبت بشهادة عدل، سواء كان في السماء علة، أم لم يكن.
تبيين الحقائق ١/ ٣١٩، العناية ٢/ ٣٢٢، بداية المبتدي ١/ ١٣١، الهداية ١/ ١٣١، ملتقى الأبحر ١/ ١٩٨، كشف الحقائق ١/ ١١٧، شرح الوقاية ١/ ١١٧، التلقين ص ٥٥، التفريع ١/ ٣٠١، مختصر خليل ص ٦٩، أقرب المسالك ص ٤١، المهذب ١/ ١٧٩، مغني المحتاج ١/ ٤٢٠، المستوعب ٣/ ٣٩٦، الشرح الكبير لأبي الفرج ابن قدامة ٣/ ٨.
(¬٢) بعد التوبة، وهذا القول: هو ظاهر الرواية.
تحفة الفقهاء ١/ ٣٤٦، العناية ٢/ ٣٢٣، كشف الحقائق ١/ ١١٧، شرح فتح القدير
٢/ ٣٢٣، بدائع الصنائع ٢/ ٨١، شرح الوقاية ١/ ١١٧، ملتقى الأبحر ١/ ١٩٨.
(¬٣) العناية ٢/ ٣٢٣، بدائع الصنائع ٢/ ٨١، شرح فتح القدير ٢/ ٣٢٣، تحفة الفقهاء ١/ ٣٤٦، الاختيار ١/ ١٢٩، تبيين الحقائق ١/ ٣٢٠.
(¬٤) تحفة الفقهاء ١/ ٣٤٦، بدائع الصنائع ٢/ ٨١، العناية ٢/ ٣٢٣، تبيين الحقائق ١/ ٣٢٠، حاشية الشلبي ١/ ٣٢٠.','3','129',NULL,NULL,'0');
INSERT INTO [b46] VALUES (805,'فإذا صاموا ثلاثين يومًا، ولم يروا ففي الفطر خلاف، بخلاف شهادة اثنين،
<hr><span data-type="title">منحة السلوك</span>

قوله: فإذا صاموا ثلاثين يومًا، ولم يروا يعني: إذا صام الناس بشهادة هذا الواحد ثلاثين يومًا ثم لم يروا هلال شوال، ففي الفطر خلاف. ففي رواية الحسن، عن أبي حنيفة: لا يفطرون احتياطًا (¬١)، وفي رواية عن محمدٍ: يفطرون (¬٢).
قوله: بخلاف شهادة اثنين.
يعني: بخلاف ما إذا صام الناس بشهادة اثنين ثلاثين يومًا، ولم يروا
_________
(¬١) ولأن الفطر لا يثبت بشهادة الواحد.
تبيين الحقائق ١/ ٣٢٠، الهداية ١/ ١٣١، العناية ٢/ ٣٢٣، شرح فتح القدير ٢/ ٣٢٣، شرح الوقاية ١/ ١١٧.
(¬٢) ويثبت الفطر بناء على ثبوت الرمضانية بشهادة الواحد.
وذهب المالكية إلى: أن رمضان إن ثبت برؤيتها، ولم ير هلال شوال بعد ثلاثين يومًا لغيرهما حال كون السماء صحوًا، لا غيم بها ليلة الإحدى والثلاثين، كُذِّبا في شهادتهما برؤية رمضان، فيجب تبييت الصوم.
وذهب الشافعية: إلى أنهم إذا صاموا برؤية عدل ولم يروا الهلال بعد الثلاثين، أفطروا في الأصح عندهم، وإن كانت السماء مصحية.
وعند الحنابلة: إن صاموا بشهادة عدلين ثلاثين يومًا، ولم يروا هلال شوال، أفطروا مع الصحو والغيم، ولا يفطرون إن صاموا بشهادة واحد، ولا إن صاموا لغيم ثلاثين ولم يروه، فلا يفطرون.
تبيين الحقائق ١/ ٣٢٠، العناية ٢/ ٣٢٣، شرح الوقاية ١/ ١١٧، شرح فتح القدير ٢/ ٣٢٣، البحر الرائق ٢/ ٢٦٦، الهداية ١/ ١٣١، التفريع ١/ ٣٠١، التلقين ص ٥٧، الشرح الصغير ١/ ٢٤٠، مغني المحتاج ١/ ٤٢٢، نهاية المحتاج ٣/ ١٥٥، الإنصاف ٣/ ٢٧٦، شرح منتهى الإرادات ١/ ٤٤١، المبدع ٣/ ٩، العمدة لابن قدامة ص ٣٠، تصحيح الفروع ٣/ ١٦.','3','130',NULL,NULL,'0');
INSERT INTO [b46] VALUES (806,'وفي الصحو لا بد من أهل محلة، أو خمسين رجلًا. وفي هلال شوال في الغيم لا بد من رجلين حرين، أو رجل وامرأتين كالأضحى.
<hr><span data-type="title">منحة السلوك</span>

الهلال حيث يفطرون بلا خلافٍ (¬١).
قوله: وفي الصحو. يعني: وفيما إذا لم يكن بالسماء علة، من سحاب، أو دخان، لا بد من أهل محلة؛ لأن التفرد في مثل هذه الحالة يوهم الغلط، فوجب التوقف في خبره، حتى يكون جمعًا كثيرًا يقع بهم العلم، أو خمسين رجلًا، مثل القسامة (¬٢).
قوله: وفي هلال شوال في الغيم لا بد من رجلين.
أي: لا بد أن يشهد رجلان، أو رجل وامرأتان، عدولًا أحرارًا غير محدودين، كما في سائر الأحكام؛ لأن فيه منفعة العباد، وهي الإفطار، فأشبهت الشهادة على حقوق الناس (¬٣).
قوله: كالأضحى.
يعني: كما أن هلال الأضحى لا بد له من شهادة رجلين، أو رجلٍ وامرأتين؛ لأن فيه منفعة العباد أيضًا، من نحو التوسع بلحوم الأضاحي،
_________
(¬١) شرح فتح القدير ٢/ ٣٢٣، الإفصاح لابن هبيرة ١/ ٢٤٢.
(¬٢) شرح فتح القدير ٢/ ٣٢٤، تبيين الحقائق ١/ ٣٢١، الهداية ١/ ١٣١، البحر الرائق ٢/ ٢٦٨، منحة الخالق ٢/ ٢٦٨، الهداية ١/ ١٣١، كشف الحقائق ١/ ١١٧، شرح الوقاية ١/ ١١٧، فتاوى مؤيد زاده (مخطوط) لوحة ١٩٢/ أ.
(¬٣) وذهب المالكية، والشافعية، والحنابلة: إلى أنه لا يقبل في سائر الشهور غير رمضان إلا عدلان، سواء كانت السماء مصحية، أم لا.
بداية المبتدي ١/ ١٣١، المختار ١/ ١٣٠، الهداية ١/ ١٣١، تحفة الفقهاء ١/ ٣٤٦، أقرب المسالك ص ٤٢، بداية المجتهد ١/ ٢٨٦، الذخيرة ٢/ ٤٩١، التنبيه ص ٦٥، الوجيز ١/ ١٠٠، غاية المنتهى ٢/ ١٧٤، المستوعب ٣/ ٤٠٤.','3','131',NULL,NULL,'0');
INSERT INTO [b46] VALUES (807,'ولا يلزم أحد المصرين برؤية المصر الآخر، إلا إذا اتحدت المطالع.
<hr><span data-type="title">منحة السلوك</span>

والإحلال في الحج، [فهلال شوال في الغيم كذلك] (¬١) (¬٢).
وعن أبي حنيفة: أن هلال الأضحى، كهلال رمضان. ذكره في "الخلاصة" (¬٣) عن "النوادر".

<span data-type="title" id=toc-417>[تعدد المطالع]</span>
قوله: ولا يلزم أحد المصرين برؤية المصر الآخر.
أي: لا يلزم الصوم ولا الإفطار أحد المصرين برؤية أهل المصر الآخر؛ لأن كل قومٍ مخاطبون بما عندهم، إلا إذا اتحدت المطالع، فحينئذٍ يلزم أحد المصرين برؤية الآخر، حتى إذا صام أهل أحدهما ثلاثين يومًا، وأهل الآخر تسعة وعشرين يومًا، يجب عليهم قضاء يومٍ (¬٤).
_________
(¬١) شرح فتح القدير ٢/ ٣٢٥، العناية ٢/ ٣٢٥، تحفة الفقهاء ١/ ٣٤٦، البحر الرائق ٢/ ٢٦٩، الاختيار ١/ ١٣٠، الوقاية ١/ ١١٧، كشف الحقائق ١/ ١١٧.
(¬٢) زيادة يقتضيها السياق.
(¬٣) خلاصة الفتاوى، لطاهر بن أحمد بن عبد الرشيد البخاري ١/ ٢٤٩.
(¬٤) وذهب المالكية، والحنابلة: إلى أنه إذا رأى الهلال أهل بلد، لزم الناس كلهم الصوم وهو ظاهر المذهب والرواية، وعليه الفتوى عند الحنفية.
والقول الآخر عند الحنفية: ما ذكره المصنف.
وذهب الشافعية: إلى أنه إن كان البلدان متقاربين، وجب الصوم على أهلهما، وإن كانا متباعدين وجب على من رأى، ولم يجب على من لم ير. ووجه التباعد عندهم: اختلاف المطالع، كالعراق، والشام، والحجاز.
شرح فتح القدير ٢/ ٣١٣، البحر الرائق ٢/ ٢٧٠، كشف الحقائق ١/ ١١٧، شرح الوقاية ١/ ١١٧، مقدمة الغزنوي (مخطوط) لوحة ١٩٥/ أالنسخة الأصلية لدى مكتبة مكة، تحت رقم ١٧ فقه حنفي، الواقعات للناطفي (مخطوط) ق ٣٤/ ب النسخة الأصلية لدى المكتبة الأحمدية بحلب، تحت رقم ٥٥٩، "المذهب الحنفي، فقه المذاهب الإسلامية"، بدائع الصنائع ٢/ ٨٣، تبيين الحقائق ١/ ٣٢١، الشرح الصغير ١/ ٢٤٠، الكافي لابن عبد البر ص ١٢٠، القوانين ص ٧٩، المجموع ٦/ ٢٧٣، إخلاص الناوي ١/ ٢٨٩، مغني =','3','132',NULL,NULL,'0');
INSERT INTO [b46] VALUES (808,'ولو أكملوا شعبان ثم صاموا رمضان فكان ثمانية وعشرين، فإن كانوا عدوا شعبان عن رؤية هلاله قضوا يومًا، وإلا قضوا يومين. ولو رئي الهلال قبل الزوال فهو لِليلة الماضية، وإن رئي بعده فهو للَّيلة المستقبلة.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-418>[نقصان رمضان عن تسعة وعشرين يومًا]</span>
قوله: ولو أكملوا شعبان، ثم صاموا رمضان فكان ثمانية وعشرين، فإن كانوا عدوا شعبان عن رؤية هلاله، قضوا يومًا؛ لأنهم لما عدوا أيام شعبان من رؤية الهلال وظهر رمضان ثمانيةً وعشرين يومًا، علم أنهم أكلوا يومًا من رمضان فيقضون يومًا (¬١).
وأما إذا لم يعدوا أيام شعبان من رؤية الهلال، قضوا يومين؛ لاحتمال أن يكون رمضان كاملًا، فيكون أكلهم يومين فيقضون يومين (¬٢).

<span data-type="title" id=toc-419>[رؤية الهلال قبل الزوال وبعده]</span>
قوله: ولو رئي الهلال قبل الزوال، فهو من الليلة الماضية.
يعني: إذا رأوا الهلال يوم الشك، فإن كانوا رأوه قبل الزوال يكون من الليلة الماضية، ويكون ذلك اليوم من شهر رمضان، وإن كانوا رأوه بعد الزوال، فهو لليلة المستقبلة. وهذا التفصيل رواية عن أبي يوسف (¬٣)، وفي ظاهر الرواية هو لليلة المستقبلة، سواء كان قبل الزوال أو بعده، حتى لا
_________
= المحتاج ١/ ٤٢٢، الكافي لابن قدامة ١/ ٣٤٨، التسهيل ص ٨٨، الإفصاح ١/ ٢٣٦.
(¬١) شرح فتح القدير ٢/ ٣٢٤، بدائع الصنائع ٢/ ٨٣، الفتاوى الكبرى "تجنيس الواقعات" للصدر الشهيد (مخطوط) لوحة ٢٥/ ب النسخة الأصلية لدى مكتبة شستربتي، تحت رقم ٣٥٤٥.
(¬٢) وذهب الحنابلة: إلى أنهم إن صاموا ثمانية وعشرين يومًا، ثم رأوا الهلال، قضوا يومًا فقط، وإن صاموا لأجل غيم ونحوه كقتر ودخان، لم يفطروا.
شرح فتح القدير ٢/ ٣٢٤، بدائع الصنائع ٢/ ٨٣، كشاف القناع ٢/ ٣٠٥، منتهى الإرادات ١/ ٤٤١.
(¬٣) تبيين الحقائق ١/ ٣٢١، بدائع الصنائع ٢/ ٨٢، تحفة الفقهاء ١/ ٣٤٧، حاشية الشلبي ١/ ٣٢١، شرح فتح القدير ٢/ ٣١٣.','3','133',NULL,NULL,'0');
INSERT INTO [b46] VALUES (809,'و<span data-type="title" id=toc-420>وقت الصوم:</span> من طلوع الفجر الثاني إلى غروب الشمس.
<hr><span data-type="title">منحة السلوك</span>

يكون ذلك اليوم من شهر رمضان (¬١).
وإذا رأوا هلال الفطر قبل الزوال، قال أبو يوسف: أفطروا، وإن رأواه بعده لم يفطروا (¬٢).
وقال قاضي خان: إن أفطروا لا كفارة عليهم؛ لأنهم أفطروا بتأويل (¬٣)، وقال -صلى الله عليه وسلم-: "أفطروا لرؤيته" (¬٤).
وعندهما: لا تعتبر رؤيته بالنهار (¬٥)، ووقته العشية، ولا يعتبر قبله ولا بعده (¬٦).

[وقت الصوم]
قوله: ووقت الصوم: من طلوع الفجر الثاني، إلى غروب الشمس (¬٧)؛
_________
(¬١) وإليه ذهب المالكية، والشافعية، والحنابلة.
حاشية الشلبي ١/ ٣٢١، بدائع الصنائع ٢/ ٨٢، تحفة الفقهاء ١/ ٣٤٧، تبيين الحقائق ١/ ٣٢١، شرح فتح القدير ٢/ ٣١٣، التلقين ص ٥٦، القوانين ص ٧٩، الذخيرة ٢/ ٤٩٢، المعونة ١/ ٤٦٣، روض الطالب ١/ ٤١١، أسنى المطالب ١/ ٤١١، رحمة الأمة ١/ ١١٥، مختصر الخرقي ص ٥٢، السلسبيل ١/ ٣٢١، شرح منتهى الإرادات ١/ ٤٣٩، المغني ٣/ ١٠٨.
(¬٢) بدائع الصنائع ١/ ٨٢، تحفة الفقهاء ١/ ٣٤٧، حاشية الشلبي ١/ ٣٢١، شرح فتح القدير ٢/ ٣١٣.
(¬٣) تبيين الحقائق ١/ ٣٢٢.
(¬٤) هذا قطعة من الحديث الذي سبق في ٣/ ١٢٧ الذي رواه البخاري ٢/ ٦٧٤ كتاب الصوم، باب قول النبي: إذا رأيتم الهلال فصوموا، وإذا رأيتموه فأفطروا رقم ١٨١٠، ومسلم ٢/ ٧٦٢ كتاب الصيام باب وجوب صوم رمضان لرؤية الهلال رقم ١٠٨١، عن أبي هريرة -رضي الله عنه- بلفظ "صوموا لرؤيته وأفطروا لرؤيته".
(¬٥) شرح فتح القدير ٢/ ٣١٣، بدائع الصنائع ٢/ ٨٢، حاشية الشلبي ١/ ٣٢١.
(¬٦) شرح فتح القدير ٢/ ٣١٣، بدائع الصنائع ٢/ ٨٢.
(¬٧) وفاقًا للثلاثة. =','3','134',NULL,NULL,'0');
INSERT INTO [b46] VALUES (810,'والصوم: هو الكف عن الأكل والشرب والجماع، نهارًا مع النية.
<hr><span data-type="title">منحة السلوك</span>

لقوله تعالى: {وَكُلُوا وَاشْرَبُوا حَتَّى يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْأَبْيَضُ مِنَ الْخَيْطِ الْأَسْوَدِ مِنَ الْفَجْرِ} [البقرة: ١٨٧].
قوله: والصوم: هو الكف عن الأكل، والشرب، والجماع، نهارًا مع النية.
هذا حد الصوم شرعًا (¬١).
قلت: هذا الحد غير مانعٍ؛ لأنه لا يخرج الحائض، والنفساء، والكافر، ولو قال: مع النية من أهله لخرج هؤلاء (¬٢). قيد به الشيخ حافظ الدين النسفي في "مختصره" (¬٣).
_________
= المختار ١/ ١٢٨، الكتاب ١/ ١٦٤، بداية المبتدي ١/ ١٣١، حاشية رد المحتار ٢/ ٣٧١، الاختيار ١/ ١٢٨، ملتقى الأبحر ١/ ١٩٦، كنز الدقائق ١/ ٣١٢، أقرب المسالك ص ٤٢، مختصر خليل ص ٧٣، حاشية البيجوري ١/ ٢٩٨، الإقناع في حل ألفاظ أبي شجاع ١/ ٢١٦، نيل المآرب ١/ ٢٧٤، الإقناع لابن المنذر ١/ ١٩١.
(¬١) الكتاب ١/ ١٦٥، المختار ١/ ١٢٨، تبيين الحقائق ١/ ٣١٢، بداية المبتدي ١/ ١٣١.
(¬٢) تبيين الحقائق ١/ ٣١٢، العناية ٢/ ٣٢٦.
(¬٣) الموسوم بكنز الدقائق ١/ ٣١٢ حيث قال: "هو ترك الأكل، والشرب، والجماع، من الصبح إلى الغروب، بنية من أهله".','3','135',NULL,NULL,'0');
INSERT INTO [b46] VALUES (811,'<span data-type="title" id=toc-421>فصل</span>
ومن أكل، أو شرب، أو جامع ناسيًا لم يفطر،
<hr><span data-type="title">منحة السلوك</span>

فصل
هذا الفصل في بيان ما يفسد الصوم، وما لا يفسده، وما يوجب القضاء، وما لا يوجبه.

<span data-type="title" id=toc-422>[أثر النسيان في الصوم]</span>
قوله: ومن أكل، أو شرب، أو جامع ناسيًا لم يفطر (¬١)؛ لقوله -صلى الله عليه وسلم-: "إذا نسي فأكل وشرب، فليتم صومه، فإنما أطعمه الله وسقاه" رواه البخاري (¬٢).
فإن قلتَ: لِمَ لا يجوز أن يكون المراد بالحديث الإمساك تشبهًا، كالحائض إذا طهرت؟.
قلتُ: أمره بإتمام صومه، وبالإمساك تشبهًا لا يتم صومه، والمأمور به هو الإتمام للصوم، والذي يؤيد هذا المعنى، ما روي أنه -صلى الله عليه وسلم- قال: "إذا أكل الصائم ناسيًا، أو شرب ناسيًا، فإنما هو رزق ساقه الله إليه، فلا قضاء عليه"
_________
(¬١) وإليه ذهب الشافعية.
وعند المالكية: يفطر، ويجب عليه القضاء.
وذهب الحنابلة: إلى أن من أكل، أو شرب ناسيًا، أو مكرهًا لم يفطر، ومن جامع ناسيًا فعليه القضاء والكفارة.
الكتاب ١/ ١٦٥، الهداية ١/ ١٣٢، كنز الدقائق ١/ ٣٢٢، كشف الحقائق ١/ ١١٨، ملتقى الأبحر ١/ ١٩٩، المختار ١/ ١٣١، قنية المنية لتتيم الغنية، لمختار بن محمد الزاهري (مخطوط) لوحة ٣١/ ب النسخة الأصلية لدى جامعة الملك سعود، تحت رقم ٣٣٤٨، بداية المبتدي ١/ ١٣٢، حاشية العدوي ٢/ ٢٥٦، الخرشي على خليل ٢/ ٢٥٦، قليوبي ٢/ ٥٨، السراج الوهاج ص ١٤٠، المقنع في شرح الخرقي ٢/ ٥٥٨، السلسبيل ١/ ٣٣٠، الروض المربع ص ١٧٦.
(¬٢) ٢/ ٦٨٢ كتاب الصوم، باب الصائم إذا أكل أو شرب ناسيًا رقم ١٨٣١ عن أبي هريرة -رضي الله عنه-.','3','136',NULL,NULL,'0');
INSERT INTO [b46] VALUES (812,'بخلاف المكره، والمخطيء.
<hr><span data-type="title">منحة السلوك</span>

رواه الدارقطني، وقال: إسناده صحيح (¬١).
وإذا ثبت في الأكل والشرب، ثبت أيضًا في الجماع دلالة؛ لأنه في معناه (¬٢).
قوله: بخلاف المكره، والمخطيء.
يعني: إذا أكل، أو شرب، أو جامع مكرهًا، أو مخطئًا، أفطر (¬٣). خلافًا للشافعي (¬٤)؛ لأن المفطر وصل إلى جوفه، فيفسد صومه، وهو القياس في الناسي، إلا أنا تركناه بما رويناه (¬٥).
والفرق بين صورة الخطأ والنسيان: أن الخاطيء ذاكر للصوم، لكنه غير
_________
(¬١) رواه الدارقطني في السنن ٢/ ١٧٨ كتاب الصيام، باب تبييت النية من الليل وغيره، رقم ٢٧.
قال الدارقطني: إسناد صحيح، وكلهم ثقات.
(¬٢) شرح فتح القدير ٢/ ٣٢٧، بدائع الصنائع ٢/ ٩٨، تبيين الحقائق ١/ ٣٢٢، حاشية الشلبي ٣٢٢١، البحر الرائق ٢/ ٢٧١، الاختيار ١/ ١٣١.
(¬٣) وإليه ذهب المالكية، وهو قول: الأوزاعي، والليث.
الهداية ١/ ١٣٢، الكتاب ١/ ١٣٢، الاختيار ١/ ١٣١، بدائع الصنائع ٢/ ١٠٠، تبيين الحقائق ١/ ٣٢٢، كشف الحقائق ١/ ١١٨، ملتقى الأبحر ١/ ١٩٩، شرح فتح القدير ٢/ ٣٢٨، القوانين ص ٨٣، التفريع ١/ ٣٠٥.
(¬٤) حيث يرى أنه لا يفطر.
وعند الحنابلة: من أكل، أو شرب مكرهًا لا يفسد صومه، ومن جامع مكرهًا فعليه القضاء والكفارة. وهو قول: عطاء، وابن الماجشون.
الحاوي الكبير ٣/ ٤٢٠، إخلاص الناوي ١/ ٢٩٣، الروض المربع ص ١٧٥، ١٧٦، المقنع ١/ ٣٦٧، ٣٧٠، حاشية المقنع ١/ ٣٧٠.
(¬٥) تبيين الحقائق ١/ ٣٢٢، شرح فتح القدير ٢/ ٣٢٩، العناية ١/ ٣٢٨، كشف الحقائق ١/ ١١٨، شرح الوقاية ١/ ١١٩.','3','137',NULL,NULL,'0');
INSERT INTO [b46] VALUES (813,'ولو أنزل باحتلام، أو فكر، أو نظر، أو أصبح جنبًا من جماع، أو ادهن، أو قبَّل، لم يفطر.
<hr><span data-type="title">منحة السلوك</span>

قاصد للشرب، والناسي قاصد للشرب، لكنه ليس بذاكر للصوم وهما على طرفي نقيض (¬١).
قوله: ولو أنزل باحتلام، أو فكر، أو نظر، أو أصبح جنبًا من جماع، أو ادهن، أو قبَّل، لم يفطر (¬٢). أما الإنزال بالاحتلام فلا يفطر؛ لقوله -صلى الله عليه وسلم-: "لا يفطر من قاء ولا من احتلم ولا من احتجم" رواه أبو داود (¬٣)، وأما
_________
(¬١) تبيين الحقائق ١/ ٣٢٢، الهداية ١/ ١٣٢، العناية ١/ ٣٢٨، شرح فتح القدير ٢/ ٣٢٩، البحر الرائق ٢/ ٢٧٢.
(¬٢) وإليه ذهب الشافعية، والمالكية، إلا أنه عند المالكية يفطر إذا أنزل بفكر، أو نظر.
والحنابلة كالحنفية والشافعية، إلا أنه عند الحنابلة: إذا كرر النظر فأمنى فسد صومه، لا إن أمذى.
بداية المبتدي ١/ ١٣٢، الاختيار ١/ ١٣١، كشف الحقائق ١/ ١١٨، شرح الوقاية ١/ ١١٩، كنز الدقائق ١/ ٣٢٢، البحر الرائق ٢/ ٢٧٢، الهداية ١/ ١٣٢، ملتقى الأبحر ١/ ١٩٩، الكتاب ١/ ١٦٥، الكافي لابن عبد البر ص ١٢٤، التفريع ١/ ٣٠٧، الخرشي على خليل ٢/ ٢٥٣، حاشية العدوي ٢/ ٢٥٣، التلقين ص ٥٧، القوانين الفقهية ص ٨١، الشرح الصغير ١/ ٢٤٤، الوجيز ١/ ١٠١، إرشاد الغاوي ١/ ٢٩١، إخلاص الناوي ١/ ٢٩١، السراج الوهاج ص ١٤٠، زاد المستقنع ص ١٧٦، منتهى الإرادات ١/ ٤٤٩، الروض المربع ص ١٧٥، شرح منتهى الإرادات ١/ ٤٤٨، المستوعب ٣/ ٤٢٧، المبدع ٣/ ٣٢، مطالب أولي النهى ٢/ ٢٠٣، الكافي لابن قدامة ١/ ٣٦٠.
(¬٣) ٢/ ٣١٠ كتاب الصوم، باب في الصائم يحتلم نهارًا في رمضان، من طريق محمد بن كثير، حدثنا سفيان، عن زيد بن أسلم، عن رجل من أصحابه، عن رجل من أصحاب رسول الله -صلى الله عليه وسلم-.
ورواه أيضًا الترمذي ٣/ ٧٠ كتاب الصوم، باب الصائم يذرعه القيء رقم ٧١٩، والبيهقي ٤/ ٢٢٠ كتاب الصيام، باب من ذرعه القيء لم يفطر ومن استقاء أفطر، وأبو يعلى في مسنده ٢/ ٣١٠، والدارقطني ٢/ ١٨٣ كتاب الصيام، باب القبلة للصائم رقم ١٦. عن أبي سعيد الخدري -رضي الله عنه-. =','3','138',NULL,NULL,'0');
INSERT INTO [b46] VALUES (814,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

الإنزال بالفكر، أو بالنظر فكذلك لا يفطر لعدم المباشرة (¬١) وأما إذا أصبح جنبًا من جماع فلأن الطهارة من الجنابة، ليست بشرط لصحة الصوم (¬٢).
وأما الادهان: فلأنه غير منافٍ والداخل من المسام لا من المسالك لا ينافيه، كما لو اغتسل بالماء البارد ووجد برده في كبده (¬٣).
وأما التقبيل: فلما روى أبو سعيد الخدري -رضي الله عنه- "أنه -صلى الله عليه وسلم- رخص في القبلة للصائم، والحجامة" رواه الدارقطني (¬٤). وعن عائشة -رضي الله عنها- قالت: "كان رسول الله -صلى الله عليه وسلم- يقبل وهو صائم، ويباشر وهو صائم، ولكنه كان أملك
_________
= قال الترمذي ٣/ ٧٠ في جامعه: حديث أبي سعيد غير محفوظ، وعبد الرحمن بن زيد بن أسلم، يضعف في الحديث.
وابن أبي حاتم في علله ١/ ٢٤٠.
(¬١) تحفة الفقهاء ١/ ٣٥٣، الهداية ١/ ١٣٢، تبيين الحقائق ١/ ٣٢٢، البحر الرائق ٢/ ٢٧٢، العناية ٢/ ٣٢٩، شرح فتح القدير ٢/ ٣٢٩.
(¬٢) المبسوط ٣/ ٥٦، فتاوى قاضيخان ١/ ٢١٠، العناية ٢/ ٣٢٩، شرح فتح القدير ٢/ ٣٢٩، الهداية ١/ ١٣٢، كشف الحقائق ١/ ١١٨.
(¬٣) بدائع الصنائع ٢/ ١٠٦، تبيين الحقائق ١/ ٣٢٣، البحر الرائق ٢/ ٢٧٣، كشف الحقائق ١/ ١١٨، الهداية ١/ ١٣٢، شرح الوقاية ١/ ١١٨، العناية ٢/ ٣٢٠، الاختيار ١/ ١٣٢، شرح فتح القدير ٢/ ٣٢٠.
(¬٤) ٢/ ١٨٢ كتاب الصيام، باب القبلة للصائم رقم ١٠، ورواه أيضًا النسائي في الكبرى ٦/ ٢٣٦ كتاب الصيام باب ذكر حديث أبي سعيد رقم ٣٢٣٧، وابن حزم في المحلى ٦/ ٢٠٤.
من طريق علي بن شعيب ثنا هاشم بن القاسم، ثنا الأشجعي، عن سفيان، عن خالد الحذاء، عن أبي المتوكل، عن أبي سعيد. وقال الدارقطني في السنن بعد أن ساق إسناده ٢/ ١٨٢: كلهم ثقات.
وقال ابن حجر في الفتح ٤/ ١٥٥: إسناده صحيح.
وقال في الدراية ١/ ٢٨٦: ورجاله ثقات.','3','139',NULL,NULL,'0');
INSERT INTO [b46] VALUES (815,'ولو أنزل بقُبلة، أو لمس، لزمه القضاء لا غير. وتباح القبلة للصائم إن أَمِنَ على نفسه.
<hr><span data-type="title">منحة السلوك</span>

لإربه" (¬١) رواه البخاري، وأبو داود (¬٢).

<span data-type="title" id=toc-423>[ما يفطر وما لا يفطر]</span>
قوله: ولو أنزل بقبلةٍ، أو لمس، لزمه القضاء، لا غير.
يعني: لا الكفارة، لقصور الجنابة (¬٣).
قوله: وتباح القبلة للصائم، إن أَمِنَ على نفسه.
أي: إن أمن من الإنزال، والجماع؛ لما روينا، ويكره إن لم يأمن (¬٤).
والشافعي أباحها في الحالتين (¬٥)، والمس والمباشرة،
_________
(¬١) الإربة -بكسر الهمزة-: الحاجة، وتطلق أيضًا على النكاح.
مختار الصحاح ص ٥ مادة أر ب، القاموس المحيط ١/ ١٢٨ مادة أرب، المصباح المنير ١/ ١٠ مادة الأرب، المطلع على أبواب المقنع ص ٣١٩، طلبة الطلبة ص ٥٦.
(¬٢) البخاري ٢/ ٦٨٠ كتاب الصوم، باب المباشرة للصائم رقم ١٨٢٦، وأبو داود ٢/ ٣١١ كتاب الصوم، باب القبلة للصائم رقم ٢٣٨٢.
(¬٣) وإليه ذهب الشافعية، والحنابلة.
وعند المالكية: عليه القضاء والكفارة.
الهداية ١/ ١٣٢، العناية ٢/ ٣٢٩، المختار ١/ ١٣١، تبيين الحقائق ١/ ٣٢٤، الاختيار ١/ ١٣١، مختصر الطحاوي ص ٥٤، الكتاب ١/ ١٦٥، التفريع ١/ ٣٠٥، حاشية العدوي ٢/ ٢٥٣، شرح المحلي على المنهاج ٢/ ٥٨، عميرة ٢/ ٥٨، مختصر الخرقي ص ٥٠، الكافي لابن قدامة ١/ ٣٥٤.
(¬٤) وعند المالكية: تكره القبلة للشاب، أو الشيخ، إن علمت السلامة من ذلك، وإلا حرم.
وعند الحنابلة: تكره القبلة ممن تحرك شهوته، ولا تكره ممن لا تحرك شهوته.
الكتاب ١/ ١٦٦، المختار ١/ ١٣٤، البحر الرائق ٢/ ٢٨١، الهداية ١/ ١٣٢، بداية المبتدي ١/ ١٣٢، بلغة السالك ١/ ٢٤٤، جواهر الإكليل ١/ ١٤٧، كشاف القناع ٢/ ٣٢٩، الروض المربع ص ١٧٨.
(¬٥) الذي عند الشافعية: أن من حركت القبلة شهوته، كره له أن يقبل، وإن لم تكن تحرك =','3','140',NULL,NULL,'0');
INSERT INTO [b46] VALUES (816,'ولو دخل حلقه ذباب، أو غبار، أو دخان، وهو ذاكر لصومه لم يفطر،
<hr><span data-type="title">منحة السلوك</span>

كالقبلة (¬١)؛ لما روى أبو هريرة -رضي الله عنه-: "أنه -صلى الله عليه وسلم- سأله رجل عن المباشرة للصائم، فرخص له، وأتاه آخر فنهاه، فإذا الذي رخص له شيخ، والذي نهاه شاب" رواه أبو داود بإسناد جيد (¬٢).
قوله: ولو دخل حلقه ذباب، أو غبار، أو دخان، وهو ذاكر لصومه لم يفطر؛ لأنه لا يستطيع الامتناع عنه (¬٣).
_________
= القبلة شهوته، فقال الشافعي: لا بأس به وتركها أولى. وقد سأل رجل الشافعي بقوله:
سل العالم المكي هل في تزوار ... وضمة مشتاق الفؤاد
فأجابه بقوله:
فقلت معاذ الله أن يذهب التقى ... تلاصق أكباد بهن جراح
قال الربيع: فسألت الشافعي كيف أفتى بهذا؟ فقال: هذا رجل قد أعرس في هذا الشهر شهر رمضان، وهو حديث السن، فسأل هل عليه جناح أن يقبل أو يضم من غير وطء؟ فأفتيته بهذه الفتيا.
المهذب ١/ ١٨٦، الحاوي الكبير ٣/ ٤٣٩، مغني المحتاج ١/ ٤٣١.
(¬١) وفاقًا للثلاثة.
النتف في الفتاوى ١/ ١٥١، بداية المبتدي ١/ ١٣٢، الاختيار ١/ ١٣١، البحر الرائق ٢/ ٢٧٣، الهداية ١/ ١٣٢، جواهر الإكليل ١/ ١٤٧، الشرح الصغير ١/ ٢٤٤، مغني المحتاج ١/ ٤٣١، نهاية المحتاج ٣/ ١٧٤، مطالب أولي النهى ٢/ ٢٠٣، الروض المربع ص ١٧٨.
(¬٢) أبو داود ٢/ ٣١٢ كتاب الصوم، باب كراهيته للشاب رقم ٢٣٨٧، وكذا قال النووي في المجموع ٦/ ٣٥٤: رواه أبو داود بإسناد جيد.
(¬٣) وفاقًا للمالكية، والشافعية، والحنابلة.
الدر المختار ٢/ ٣٩٥، بداية المبتدي ١/ ١٣٣، المختار ١/ ١٣٣، حاشية رد المحتار ٢/ ٣٩٥، الهداية ١/ ١٣٣، العناية ٢/ ٣٣٢، منح الجليل ٢/ ١٤٦، جواهر الإكليل ١/ ١٥٢، منهج الطلاب ١/ ٣١٩، فتح الوهاب ١/ ٣١٩، العمدة لابن قدامة ص ٣٢، التسهيل ص ٨٨.','3','141',NULL,NULL,'0');
INSERT INTO [b46] VALUES (817,'بخلاف المطر والثلج.
ولو تنخع وابتلع ما تنخع، أو ابتلع ريقه المغلوب بالدم، لم يفطر.
وإن ابتلع ما بين أسنانه من عَشائه دون حمصة، لم يفطر،
<hr><span data-type="title">منحة السلوك</span>

قوله: بخلاف المطر، والثلج.
يعني: إذا دخل حلقه مطر، أو ثلج يفطر؛ لإمكان الامتناع عنه بأن آواه خيمة، أو سقف (¬١).
قوله: ولو تنخع وابتلع ما تنخع، وهو النخامة، وهي: ما ينزل من الخيشوم (¬٢)، أو ابتلع ريقه المغلوب بالدم، لم يفطر؛ لتعذر الاحتراز عنه (¬٣). وقيل: في الثانية يفطر (¬٤).
قوله: وإن ابتلع ما بين أسنانه من عشائه دون حمصةٍ، لم يفطر.
_________
(¬١) وإليه ذهب الحنابلة.
الهداية ١/ ١٣٣، تبيين الحقائق ١/ ٣٢٤، منحة الخالق ٢/ ٢٧٣، البحر الرائق ٢/ ٢٧٣ العناية ٢/ ٣٣٢، شرح فتح القدير ٢/ ٣٣٢، المقنع شرح الخرقي ٢/ ٥٥٨، المغني ٣/ ٣٧.
(¬٢) مختار الصحاح ص ٢٧١ مادة ن خ م، المصباح المنير ٢/ ٥٩٦ مادة النخاعة، المطلع على أبواب المقنع ص ١٤٨.
(¬٣) وإليه ذهب المالكية.
تبيين الحقائق ١/ ٣٢٥، شرح فتح القدير ٢/ ٣٣٢، العناية ٢/ ٣٣٣، حاشية الدسوقي ١/ ٥٢٥، الشرح الكبير للدردير ١/ ٥٢٥، الشرح الصغير ١/ ٢٤٦.
(¬٤) وعند الشافعية: إن تركها مع القدرة، فوصلت الجوف أفطر في الأصح، وقيل: لا يفطر.
وعند الحنابلة: يحرم على الصائم بلع النخامة، سواء كانت من جوفه، أو صدره، أو دماغه، ويفطر بها إن وصلت إلى فمه؛ لأنها من غير الفم.
شرح فتح القدير ٢/ ٣٣٢، تبيين الحقائق ١/ ٣٢٥، العناية ٢/ ٣٣٣، نهاية المحتاج ٣/ ١٦٥، حاشية الشبراملسي ٣/ ١٦٥، المبدع ٣/ ٤٠، الروض المربع ص ١٧٨.','3','142',NULL,NULL,'0');
INSERT INTO [b46] VALUES (818,'إلا إذا أخرجه ثم رده، وبقدر الحمصة يفطر ولا كفارة عليه. ولو ابتلع سمسمة لزمته الكفارة،
<hr><span data-type="title">منحة السلوك</span>

لعدم إمكان الاحتراز عنه (¬١). والعشاء: بفتح العين العشوة (¬٢).
قوله: إلا إذا أخرجه ثم رده يعني: إذا أخرجه بيده، ثم أكله يفطر؛ لإمكان الاحتراز عنه (¬٣).
قوله: وبقدر الحمصة يفطر.
يعني: إذا ابتلع ما بين أسنانه من عشائه بقدر الحمصة يفطر، ولا كفارة عليه (¬٤). وعند زفر: يفطر في قدر الحمصة وما دونها، وعليه الكفارة أيضًا في قدر الحمصة (¬٥).
قوله: وإن ابتلع سمسمة لزمته الكفارة (¬٦).
_________
(¬١) وعند المالكية: إن ابتلع ما بين أسنانه لا يفطر مطلقًا.
وعند الشافعية، والحنابلة: لو بقي طعام بين أسنانه، فجرى به ريقه لم يفطر إن عجز عن تمييزه ومجه، فإن لم يعجز أفطر، وإذا ابتلعه قصدًا يفطر كذلك، ولو دون الحمصة.
تحفة الفقهاء ١/ ٣٥٣، تبيين الحقائق ١/ ٣٢٥، البحر الرائق ٢/ ٢٧٣، تنوير الأبصار ٢/ ٣٩٦، بداية المبتدي ١/ ١٣٣، الشرح الصغير ١/ ٢٤٥، القوانين الفقهية ص ٨٠، زاد المحتاج ١/ ٥١٤، تحفة المحتاج ٣/ ٤٠٧، الإقناع للحجاوي ٢/ ٣٢١، الفروع ٣/ ٥٦.
(¬٢) مختار الصحاح ص ١٨٣ مادة ع ش ا، القاموس المحيط ٣/ ٢٣٥ مادة ع ش و، المصباح المنير ٢/ ٤١٢ مادة العشيُّ.
(¬٣) المختار ١/ ١٣٣، بداية المبتدي ١/ ١٣٣، الدر المختار ٢/ ٣٩٦، الاختيار ١/ ١٣٣، الهداية ١/ ١٣٣، الوقاية ١/ ١١٩، كشف الحقائق ١/ ١١٨.
(¬٤) تحفة الفقهاء ١/ ٣٥٣، تبيين الحقائق ١/ ٣٢٥، الوقاية ١/ ١١٩، الهداية ١/ ١٣٣، الدر المختار ١٢/ ٢٩٦.
(¬٥) تحفة الفقهاء ١/ ٣٥٣، النتف في الفتاوى ١/ ١٥٧، الهداية ١/ ١٣٣، البحر الرائق ٢/ ٢٧٣، شرح فتح القدير ٢/ ٣٣٣، تبيين الحقائق ١/ ٣٢٥.
(¬٦) لأنها من جنس ما يتغذى به، وهو رواية عن محمد. =','3','143',NULL,NULL,'0');
INSERT INTO [b46] VALUES (819,'وإن مضغها لم يفطر إلا أن يجد طعمها في حلقه. ولو أكل عجينًا، أو دقيقًا، أو ابتلع حصاة، أو نحوها، لزمه القضاء لا غير.
<hr><span data-type="title">منحة السلوك</span>

وقيل: لا تجب الكفارة؛ لأنه ناقص [الجناية] (¬١) (¬٢)، وعليه: فخر الإسلام البزدوي.
وقال الصدر الشهيد: المختار أنه تجب الكفارة؛ لأنها من جنس ما يتغذى بها (¬٣).
قوله: وإن مضغها. أي: وإن مضغ السمسمة لم يفطر؛ لأنها تتلاشى بالمضغ، إلا إذا وجد طعمها في حلقه، فحينئذ يفطر (¬٤).
قوله: ولو أكل عجينًا، أو دقيقًا، أو ابتلع حصاةً، أو نحوها، مثل النواة، والحديد، والرصاص، وغير ذلك، لزمه القضاء؛ لوجود صورة الفطر، ولا كفارة عليه، لقصور الجناية (¬٥)، إلا عند محمد تجب الكفارة في الدقيق، والعجين (¬٦).
_________
= الهداية ١/ ١٣٣، شرح فتح القدير ٢/ ٣٣٣، تبيين الحقائق ١/ ٣٢٥، حاشية الشلبي ١/ ٣٢٥، البحر الرائق ٢/ ٢٧٣.
(¬١) الهداية ١/ ١٣٣، شرح فتح القدير ٢/ ٣٣٣، البحر الرائق ٢/ ٢٧٣، حاشية الشلبي ١/ ٣٢٥، تبيين الحقائق ١/ ٣٢٥.
(¬٢) الزيادة من ص.
(¬٣) الواقعات للصدر الشهيد (مخطوط) ٣٧/ ب النسخة الأصلية لدى المكتبة الوطنية بتونس، تحت رقم ٢٠٢.
(¬٤) الهداية ١/ ٣٣٣، المبسوط ٣/ ١٤٢، شرح فتح القدير ٢/ ٣٣٣، البحر الرائق ٢/ ٢٧٤، شرح الوقاية ١/ ١١٩.
(¬٥) بدائع الصنائع ٢/ ٩٩، تبيين الحقائق ١/ ٣٢٦، البحر الرائق ٢/ ٢٧٥، شرح فتح القدير ٢/ ٣٣٦، الفتاوى التتارخانية ٢/ ٣٧٥.
(¬٦) بدائع الصنائع ٢/ ٩٩، الأصل ٢/ ٢٧٨، تبيين الحقائق ١/ ٣٢٦، البحر الرائق ٢/ ٢٧٥، شرح فتح القدير ٢/ ٣٣٦.','3','144',NULL,NULL,'0');
INSERT INTO [b46] VALUES (820,'ولو أكل مسكًا، أو كافورًا، أو زعفرانًا، أو ترابًا مشوبًا، أو
<hr><span data-type="title">منحة السلوك</span>

وعلى هذا الخلاف: الأرز (¬١).
ولو أكل ملحًا لا كفارة عليه (¬٢). وقيل: تجب إذا اعتاد أكل ذلك وحده (¬٣).
ولو ابتلع جوزةً (¬٤) رطبةً، أو لوزةً (¬٥) رطبةً، أو بطيخةً صغيرةً، فعليه القضاء، لا الكفارة (¬٦).
قوله: ولو أكل مسكًا، أو كافورًا، أو زعفرانًا (¬٧)، أو ترابًا مشوبًا، أو
_________
(¬١) المبسوط ٣/ ١٣٨، تبيين الحقائق ١/ ٣٢٦، البحر الرائق ٢/ ٢٧٥، شرح فتح القدير ٢/ ٣٣٣.
(¬٢) تبيين الحقائق ١/ ٣٢٦، الفتاوى التتارخانية ٢/ ٣٧٦، منحة الخالق ٢/ ٢٧٥، البحر الرائق ٢/ ٢٧٥، شرح فتح القدير ٢/ ٣٣٦.
(¬٣) وقيل: في قليله تجب دون كثيره.
تبيين الحقائق ١/ ٣٢٦، الفتاوى التتارخانية ٢/ ٣٧٧، حاشية الشلبي ١/ ٣٢٦، منحة الخالق ٢/ ٢٧٥، البحر الرائق ٢/ ٢٧٥، شرح فتح القدير ٢/ ٣٣٦.
(¬٤) الجوز: فارسي معرب، وهو نوعان: هندي وشامي، ويقال: جوز الهند النارجيل، وشجرته شبيهة بالنخلة.
لسان العرب ٥/ ٣٢٦ مادة جوز، تاج العروس ٤/ ٢١ مادة جوز، المصباح المنير ١/ ١١٥ مادة جاز، المطلع على أبواب المقنع ص ٢٣٧.
(¬٥) اللوز: من الثمار، عربي يكثر في بلاد العرب.
لسان العرب ٥/ ٤٠٧ مادة لوز، تاج العروس ٤/ ٧٩ مادة لوز، مختار الصحاح ص ٢٥٣ مادة ل وز، القاموس المحيط ٤/ ١٨٢ مادة ل وز.
(¬٦) بدائع الصنائع ٢/ ٩٩، المبسوط ٣/ ١٣٨، حاشية الشلبي ١/ ٣٢٦، البحر الرائق ٢/ ٢٧٥، شرح فتح القدير ٢/ ٣٣٦.
(¬٧) وهو: نبت يتخذ من زهره سحيق أصفر، يصنع به.
تاج العروس ٣/ ٢٣٨ مادة زعفر، لسان العرب ٤/ ٣٢٤ مادة زعفر، المصباح المنير ١/ ٢٥٣ مادة الزعفران، القاموس المحيط ٢/ ٤٥٢ مادة ز ع ف ر.','3','145',NULL,NULL,'0');
INSERT INTO [b46] VALUES (821,'ورق شجر يُعتاد أكلُها لزمته الكفارة.
<hr><span data-type="title">منحة السلوك</span>

ورق شجر يُعتاد أكلُها، لزمته الكفارة (¬١).
أما المسك، والكافور، والزعفران: فلأنها مما يؤكل عادةً، ويتداوى بهما، فكملت الجناية، فتجب الكفارة (¬٢).
وأما التراب: فإنما يوجب الكفارة إذا كان مشوبًا، أي: مختلطًا بشيء (¬٣)، حتى إذا أكل ترابًا خالصًا لا تجب عليه الكفارة؛ لأنه مما لا يتغذى به، ولا يتداوى به عادةً (¬٤)، وكذلك لا تجب الكفارة في الطين، إلا في الطين الأرمني (¬٥)؛ لأنه يتداوى به (¬٦).
وأما ورق الشجر: فكذلك إنما يوجب الكفارة إذا كان مما يعتاد أكله (¬٧)؛ لكمال الجناية (¬٨)،
_________
(¬١) بدائع الصنائع ٢/ ٩٨، المبسوط ٣/ ١٣٨، حاشية الشبلي ١/ ٣٢٦، البحر الرائق ٢/ ٢٧٦ شرح فتح القدير ٢/ ٣٣٦، الفتاوى التتارخانية ٢/ ٣٧٦.
(¬٢) بدائع الصنائع ٢/ ٩٨، المبسوط ٣/ ١٣٨، حاشية الشلبي ١/ ٣٢٦، شرح فتح القدير ٢/ ٣٣٦، البحر الرائق ٢/ ٢٧٦.
(¬٣) بدائع الصنائع ٢/ ٩٨، تبيين الحقائق ١/ ٣٢٦، البحر الرائق ٢/ ٢٧٥، شرح فتح القدير ٢/ ٣٣٦.
(¬٤) شرح فتح القدير ٢/ ٣٣٦، النتف في الفتاوى ١/ ١٥٣، تبيين الحقائق ١/ ٣٢٦، حاشية الشلبي ١/ ٣٢٦، البحر الرائق ٢/ ٢٧٥.
(¬٥) منسوب إلى الأرمن، جيل من الناس، سمي به بلدهم.
المغرب ص ١٩٩ مادة طين أرمني.
(¬٦) النتف في الفتاوى ١/ ١٥٣، المبسوط ٣/ ١٣٨، تبيين الحقائق ١/ ٣٢٦، البحر الرائق ٢/ ٢٧٥، شرح فتح القدير ٢/ ٣٣٦.
(¬٧) كورق الكرم. البحر الرائق ٢/ ٢٧٦.
(¬٨) النتف في الفتاوى ١/ ١٥٣، بدائع الصنائع ٢/ ٩٨، تبيين الحقائق ١/ ٣٢٦، حاشية الشلبي ١/ ٣٢٦، البحر الرائق ٢/ ٢٧٦.','3','146',NULL,NULL,'0');
INSERT INTO [b46] VALUES (822,'ولو مضغ لقمة ناسيًا فذكر فابتلعها وجبت الكفارة، ولو أخرجها ثم ابتلعها لم تجب.
<hr><span data-type="title">منحة السلوك</span>

وأما إذا كان مما لا يعتاد أكله (¬١)، لا تجب الكفارة (¬٢).
وعلى هذا التفصيل: النباتات كلها (¬٣).

<span data-type="title" id=toc-424>[أثر النسيان والتعمد]</span>
قوله: ولو مضغ لقمة ناسيًا فذكر فابتلعها، وجبت الكفارة (¬٤).
وقيل: يجب القضاء، دون الكفارة (¬٥). والأول: أظهر.
قوله: ولو أخرجها. أي: ولو أخرج تلك اللقمة الممضوغة من فيه، ثم ابتلعها لم تجب الكفارة. وقيل: تجب الكفارة.
والأول: أصح. قاله: أبو الليث؛ لأن بعد إخراجها تعافها النفس، وما دامت في فيه يتلذذ بها (¬٦).
_________
(¬١) كورق الكرم إذا عظم.
البحر الرائق ٢/ ٢٧٦.
(¬٢) وعليه القضاء.
تبيين الحقائق ١/ ٣٢٦، البحر الرائق ٢/ ٢٧٦.
(¬٣) حاشية الشلبي ١/ ٣٢٦.
(¬٤) شرح فتح القدير ٢/ ٣٣٦، بدائع الصنائع ٢/ ١٠٠.
(¬٥) وعند الحنابلة: لا يفسد صومه.
مختصر الطحاوي ص ٥٧، شرح فتح القدير ٢/ ٣٣٦، الروض المربع ص ١٧٥، المقنع ١/ ٣٦٦.
(¬٦) عيون المسائل، لأبي الليث السمرقندي ٢/ ٥٥ ونصه: ولو أن رجلًا أخرج من الخبز لقمة؛ ليأكلها وهو ناسٍ، فلما مضغها ذكر أنه صائم فابتلعها وهو ذاكر، فإن في هذه المسألة أربعة أقاويل للمتأخرين: قال بعضهم: لا كفارة عليه. وقال بعضهم: عليه الكفارة. وقال بعضهم: إن ابتلعها فلا كفارة عليه، وإن أخرجها ثم أعادها فابتلعها فعليه الكفارة. وقال بعضهم: إن ابتلعها قبل أن تخرج فعليه الكفارة، وإن أخرجها ثم أعادها فلا كفارة عليه. =','3','147',NULL,NULL,'0');
INSERT INTO [b46] VALUES (823,'ولو أفطر عمدًا، ثم مرض، أو حاضت لم تجب الكفارة. ولو سافر طائعًا وجبت.
<hr><span data-type="title">منحة السلوك</span>

وقيل: إن كانت سخنة بعد، فعليه الكفارة (¬١).
قوله: ولو أفطر عمدًا، ثم مرض، أو حاضت. أي: المرأة، أفطرت عمدًا، ثم حاضت، لم تجب الكفارة عليهما؛ لأنه ظهر بالمرض والحيض أن الفطر في ذلك اليوم مباح لهما، فلا تجب الكفارة (¬٢).
قوله: ولو سافر طائعًا وجبت.
يعني: إذا أفطر عمدًا، ثم سافر طائعًا، يعني: باختياره، وجبت الكفارة؛ لأنه بإفطاره عمدًا وجبت عليه الكفارة، ثم لم يظهر ما يرفعها، بخلاف الصورة الأولى (¬٣).
أما لو سافر مكرهًا: فقد ذكر في "خلاصة الفتاوى" أنه لا تسقط عنه الكفارة في ظاهر الرواية، وفي رواية الحسن، عن أبي حنيفة: تسقط. وعندهما: لا تسقط (¬٤).
_________
= قال الفقيه -رضي الله عنه-: وهذا القول أصح عندي؛ لأنه لما أخرجها صارت بحال يعافه منها، وما دامت في فمه فإنه يتلذذ بها.
(¬١) شرح فتح القدير ٢/ ٣٣٦، بدائع الصنائع ٢/ ١٠٠.
(¬٢) وذهب المالكية: إلى أن من أفطر في نهار رمضان عامدًا فسد صومه، وعليه كفارة الجماع في نهار رمضان.
وذهب الشافعية، والحنابلة: إلى أن من أفطر في نهار رمضان عامدًا، فسد صومه ولا كفارة.
شرح فتح القدير ٢/ ٣٣٧، بدائع الصنائع ٢/ ١٠٠، الهداية ١/ ١٣٩، القوانين الفقهية ص ٨٣، المعونة ١/ ٤٦٨، مغني المحتاج ١/ ٤٤٣، أسنى المطالب ١/ ٤٢٥، زاد المستقنع ص ١٧٥، شرح منتهى الإرادات ١/ ٤٤٧، الإفصاح ١/ ٢٤٠.
(¬٣) شرح فتح القدير ٢/ ٣٣٧، بدائع الصنائع ٢/ ١٠٠، الفتاوى التتارخانية ٢/ ٣٨٣.
(¬٤) خلاصة الفتاوى، لطاهر بن أحمد البخاري ١/ ٢٤٧ ونصه: "ولو أفطر في أول النهار =','3','148',NULL,NULL,'0');
INSERT INTO [b46] VALUES (824,'وللمريض الفطر يوم نوبة حُمّاه، وللمرأة أيضًا يوم عادة حيضها بناء على العادة، فإن أفطر ولم يأت الحمى والحيض، وجبت الكفارة.
فإن غلبه القيء لم يفطر مطلقًا،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-425>[الفطر للمريض والمسافر]</span>
قوله: وللمريض الفطر يوم نوبة حماه (¬١) (¬٢)، وللمرأة أيضًا يوم عادة حيضها، بناء على العادة، لأن الظاهر أن الحمى تأتيه يوم النوبة، والحيض يأتيها يوم العادة (¬٣).
قوله: فإن أفطر. أي: إن أفطر المريض يوم نوبة حُمّاه، أو أفطرت المرأة، يوم عادة حيضها؛ اعتمادًا على مجيء الحمى، والحيض ولم يأت الحمى، ولا الحيض، وجبت عليهما الكفارة؛ لكمال الجناية، وعدم ظهور ما يبيح الإفطار (¬٤).

<span data-type="title" id=toc-426>[أثر القيء في الصوم]</span>
قوله: فإن غلبه القيء (¬٥) لم يفطر مطلقًا.
_________
= متعمدًا، ثم أكرهه السلطان على السفر، لا يسقط عنه الكفارة في ظاهر الرواية. وفي رواية الحسن عن أبي حنيفة رحمهما الله: يسقط. وعندهما: لا يسقط ولو سافر باختياره لا يسقط عنه الكفارة باتفاق الروايات، وكذا لو خرج بنفسه بالإكراه ... ".
(¬١) شرح فتح القدير ٢/ ٣٣٧.
(¬٢) وهي علة يستعر بها الجسم من الحميم. وحُمُوَّة الألم: سورته وشدته ونشاطه.
لسان العرب ١٤/ ٢٠١ مادة حما، مختار الصحاح ص ٦٦، مادة ح م ي، القاموس المحيط ١/ ٧١٩ مادة ح م ي، المصباح المنير ١/ ١٥٣ مادة حميت.
(¬٣) شرح فتح القدير ٢/ ٣٣٧، المبسوط ٣/ ٨٣.
(¬٤) شرح فتح القدير ٢/ ٣٣٧، المبسوط ٣/ ٨٣.
(¬٥) القيء: هو ما يخرج من فم الإنسان من معدته.
لسان العرب ١/ ١٣٥ مادة قيأ، القاموس المحيط ٣/ ٧٢١ مادة قاء، المغرب ص ٣٩٧ مادة قاء ما أكل، المصباح المنير ٢/ ٥٢٢ مادة قاء، أنيس الفقهاء ص ٥٥، طلبة الطلبة ص ٥٦، المطلع على أبواب المقنع ص ١٤٧، الدر النقي ٢/ ٩٧.','3','149',NULL,NULL,'0');
INSERT INTO [b46] VALUES (825,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

يعني: سواء قاء كثيرًا، أو قليلًا (¬١)؛ لقوله -صلى الله عليه وسلم-: "من ذرعه القيء، فليس عليه قضاء، ومن استقاء عمدًا، فليقض" رواه أبو داود، وغيره (¬٢). وقال
_________
(¬١) المختار ١/ ١٣٣، بداية المبتدي ١/ ١٣٣، البحر الرائق ٢/ ٢٧٤، الهداية ١/ ١٣٣، منحة الخالق ٢/ ٢٧٤، الاختيار ١/ ١٣٣، ملتقى الأبحر ١/ ٢٠٠.
(¬٢) أبو داود ٢/ ٣١٠ كتاب الصوم باب الصائم يستقيء عامدًا رقم ٢٣٨٠، ورواه أيضًا ابن أبي شيبة ٢/ ٢٩٧ كتاب الصيام، باب ما جاء في الصائم يتقيأ، أو يبدؤه القيء رقم ٩١٨٩، وعبد الله بن أحمد في مسائله ٦٩٢، وأبو يعلى ١١/ ٤٨٢ رقم ٦٦٠٤، وأحمد ٢/ ٤٩٨، والدارمي ١/ ٤٣٩ كتاب الصوم، باب الرخصة فيه رقم ١٦٨٠، والبخاري في التاريخ ١/ ٩١، وابن ماجه ٥٣٦ كتاب الصيام، باب ما جاء في الصائم يقيء ١٦٧٦، والترمذي ٣/ ٧١ كتاب الصوم باب ما جاء في من استقاء عمدًا رقم ٧٢٠، والنسائي في الكبرى ٢/ ٢١٥ كتاب الصيام، باب الصائم يتقيأ رقم ٣١٣٠، وابن الجارود في المنتقى ص ١٠٤ باب الصيام رقم ٣٨٥، وابن خزيمة ٣/ ٢٢٥ كتاب الصيام، باب ذكر إيجاب قضاء الصوم عن المستقيء عمدًا رقم ١٩٦٠، والطحاوي في الشرح ٢/ ٩٧ كتاب الصيام، باب الصائم يقيء، وفي مشكل الآثار ٢/ ٢٧٦ باب مشكل ما روي عن رسول الله -صلى الله عليه وسلم- أنه قاء فأفطر، وابن حبان ٨/ ٢٨٥ كتاب الصوم، باب قضاء الصوم رقم ٣٥١٨، وابن عدي ١٦٤١، والحاكم في المستدرك ١/ ٤٢٧ كتاب الصوم، والدارقطني ٢/ ١٨٤ كتاب الصيام باب القبلة للصائم رقم ٢٠، والبيهقي ٤/ ٢١٩ كتاب الصيام، باب من ذرعه القيء لم يفطر، ومن استقاء أفطر، والبغوي في شرح السنة ١٧٥٥، والخطيب في الموضح ٢/ ١٩٢.
من طريق عيسى بن يونس وحفص بن غياث، عن هشام بن حسان، عن محمد بن سيرين،
عن أبي هريرة -رضي الله عنه-.
قال الحاكم ١/ ٤٢٧: صحيح على شرط الشيخين ولم يخرجاه، وسكت عنه الذهبي. وقال الترمذي ٣/ ٧٢: حديث حسن غريب، وقال محمد: لا أراه محفوظًا.
وقال شيخ الإسلام في الفتاوى ٢٥/ ٢٢٢: والذين لم يثبتوا هذا الحديث، لم يبلغهم من وجه يعتمدونه، وقد أشاروا إلى علته، وهي انفراد عيسى بن يونس، وقد ثبت أنه لم ينفرد به، بل وافقه عليه حفص بن غياث.','3','150',NULL,NULL,'0');
INSERT INTO [b46] VALUES (826,'وإن تعمد ملء فيه أفطر ولا كفارة.
<hr><span data-type="title">منحة السلوك</span>

الدارقطني (¬١): رواته كلهم ثقات.
قوله: وإن تعمد. أي: وإن تعمد القيء ملء فيه، أفطر، وعليه القضاء، لا الكفارة (¬٢)؛ لما روينا هذا في الطعام، والماء، والمرة (¬٣).
وأما إذا قاء بلغمًا فهو لا يفطر عندهما، خلافًا لأبي يوسف (¬٤).
وإن قاء مرارًا في مجلس واحد ملء فيه، لزمه القضاء، وإن كان في مجالس، أو غدوة، ثم نصف النهار، ثم عشية لا يلزمه القضاء. ذكره في "خزانة الأكمل" (¬٥).
_________
(¬١) في سننه ٢/ ١٨٤ كتاب الصيام باب القبلة للصائم رقم ٢٠.
(¬٢) وفي هذا يقول: محمود بن أبي بكر الفراهي، أبو النصر، بدر الدين، في منظومته الموسومة بـ "لمعة البدر، نظم الجامع الصغير" (مخطوط) لوحة ٢٤/ ب.
وليس القيء يفسده ولكن ... إعادته الكثير تضر فيه
وعود قليله عفو وقالوا ... ينافيه التقيؤ مِلء فيه
النسخة الأصلية لدى مكتبة مكة، تحت رقم ٩٤ "فقه حنفي".
(¬٣) وذهب المالكية، والشافعية، والحنابلة: إلى أن من تقيأ عمدًا أفطر، ومن ذرعه القيء لم يفطر.
ملتقى الأبحر ١/ ٢٠٠، المختار ١/ ١٣٣، بداية المبتدي ١/ ١٣٣، شرح فتح القدير ٢/ ٣٣٥، الهداية ١/ ١٣٣، تبيين الحقائق ١/ ٣٢٦، الدر المختار ٢/ ٤١٤، جواهر الإكليل ١/ ١٤٩، القوانين الفقهية ص ٨١، روضة الطالبين ٢/ ٣٥٦، مغني المحتاج ١/ ٤٢٧، منتهى الإرادات ١/ ٤٤٨، الكافي لابن قدامة ١/ ٣٥٣.
(¬٤) تبيين الحقائق ١/ ٣٢٦، شرح فتح القدير ٢/ ٣٣٥، البحر الرائق ٢/ ٢٧٥، منحة الخالق ٢/ ٢٧٥، الدر المختار ٢/ ٤١٥، حاشية رد المحتار ٢/ ٤١٥.
(¬٥) لوحة ٧٧/ ب، والخزانة لأبي عبد الله يوسف بن محمد الجرجاني (مخطوط) النسخة الأصلية لدى مكتبة أحمد الثالث بتركيا، برقم ١/ ٧٩٩، قال في مقدمته: "هذا الكتاب محيط بكل تصنيفات الأصحاب، واتفقت البداية بكافي الحاكم، ثم بالجامعين، ثم بالزيادات، ثم بمجرد ابن زياد، والمنتقى، والكرخي، وشرح الطحاوي، وعيون المسائل ... ".','3','151',NULL,NULL,'0');
INSERT INTO [b46] VALUES (827,'ومن أكل غذاء، أو شرب دواء، أو جامع عامدًا في أحد السبيلين، لزمته الكفارة.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-427>[ارتكاب المفطرات عمدًا]</span>
قوله: ومن أكل غذاء، أو شرب دواء، أو جامع عامدًا في أحد السبيلين، لزمته الكفارة (¬١).
وعند الشافعي: لا تجب الكفارة إلا بالجماع، وتجب على الزوج دون المرأة (¬٢).
ولنا: قوله -صلى الله عليه وسلم-: "من أفطر في رمضان، فعليه ما على المظاهر" رواه الدارقطني بمعناه (¬٣).
_________
(¬١) والمالكية كالحنفية، فمن أكل، أو شرب، أو جامع عامدًا في أحد السبيلين لزمته الكفارة. وتلزم الزوجة أيضًا، إذا كانت مطاوعة له.
وفي هذا يقول: محمد الكواكبي في منظومته الموسومة بالفرائد السنية في فروع الفقه (مخطوط) ق ٤١ النسخة الأصلية لدى المكتبة الأزهرية، تحت رقم ٣٢٨٣/ ٤٩٣٩.
في واحد من السبيلين إذا ... جامع أو جومع مثله كذا
في أكله أو شربه غذاء ... عمدًا إذا كان كذا دواء
فإنه يقضي كذا يكفر ... كفارة مثل الذي يظاهر
وذاك حيث يفسد الأداء ... وليس حيث يفسد القضاء
المختار ١/ ١٣١، بداية المبتدي ١/ ١٣٤، شرح ملا مسكين على كنز الدقائق ص ٧٧، الاختيار ١/ ١٣١، الهداية ١/ ١٣٤، الوقاية ١/ ١١٩، كشف الحقائق ١/ ١١٩، تنوير الأبصار ٢/ ٤٠٩، حاشية رد المحتار ٢/ ٤٠٩، القوانين ص ٨٣، التلقين ص ٥٨، الشرح الصغير ١/ ٢٤٨.
(¬٢) وعند الحنابلة: لا تجب الكفارة إلا بالجماع، وإذا كانت المرأة المجامعة معذورة بجهل، أو نسيان، أو إكراه، فالقضاء ولا كفارة. وإن طاوعت عامدة عالمة، فالكفارة أيضًا.
روض الطالب ١/ ٤٢٤، شرح المحلي على المنهاج ٢/ ٧١، الروض المربع ص ١٧٦، المقنع ١/ ٣٦٨.
(¬٣) الدارقطني ٢/ ١٩٠ كتاب الصيام، باب القبلة للصائم رقم ٥٢.
ولفظه: حدثنا أبو سهل بن زياد من أصله، ثنا إسماعيل بن إسحاق، ثنا يحيى ابن =','3','152',NULL,NULL,'0');
INSERT INTO [b46] VALUES (828,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وما روي عن أبي هريرة -رضي الله عنه-: "أن رجلًا أفطر في رمضان، فأمره -صلى الله عليه وسلم- أن يعتق رقبة" رواه مسلم، وأبو داود (¬١). ولفظ "أفطر" فيهما يتناول المأكول وغيره، وكلمة "مَن" تطلق على الذكر والأنثى (¬٢)، والإنزال في الجماع ليس بشرط؛ لأنه شبع والتقاء الختانين كافٍ (¬٣).
_________
= الحماني، ثنا هشيم، عن إسماعيل بن سالم، عن مجاهد، عن أبي هريرة -رضي الله عنه- "أن النبي -صلى الله عليه وسلم- أمر الذي أفطر يومًا من رمضان بكفارة الظهار".
ورواه مسلم ٢/ ٧٨٢ كتاب الصيام باب تغليظ تحريم الجماع في نهار رمضان على الصائم رقم (٨٣) ١١١١.
وتمامه: عن أبي هريرة -رضي الله عنه- قال: جاء رجل إلى النبي -صلى الله عليه وسلم-، فقال: هلكت يا رسول الله، قال: وما أهلكك؟ قال: وقعت على امرأتي في رمضان، قال: هل تجد ما تعتق رقبة؟ قال: لا، قال: فهل تستطيع أن تصوم شهرين متتابعين؟ قال: لا، قال: فهل تجد ما تطعم ستين مسكينًا؟ قال: لا، قال: ثم جلس، فأُتي النبي -صلى الله عليه وسلم- بعرق فيه تمر، قال: تصدق بهذا، قال: أفقر منا؟ فما بين لابتيها أهل بيت أَحوج إليه منا، فضحك النبي -صلى الله عليه وسلم- حتى بدت أنيابه، ثم قال: اذهب فأطعمه أهلك".
(¬١) مسلم ٢/ ٧٨١ كتاب الصيام، باب تغليظ تحريم الجماع في نهار رمضان على الصائم، ووجوب الكفارة الكبرى فيه، وبيانها، وأنها تجب على الموسر والمعسر، وتثبت في ذمة المعسر حتى يستطيع رقم ١١١١، وأبو داود ٢٣٩٠ كتاب الصوم، باب كفارة من أتى أهله في رمضان رقم ٢٣٩٠. وتمام السياق في الحديث السابق.
(¬٢) العناية ٢/ ٣٣٨، الهداية ١/ ١٣٤، كشف الحقائق ١/ ١١٩، حاشية رد المحتار ٢/ ٤٠٩، تبيين الحقائق ١/ ٣٢٧، الوقاية ١/ ١١٩.
(¬٣) وفاقًا للثلاثة.
تبيين الحقائق ١/ ٣٢٧، الهداية ١/ ١٣٤، العناية ٢/ ٣٣٧، شرح فتح القدير ٢/ ٣٣٧، كشف الحقائق ١/ ١١٩، حاشية الشلبي ١/ ٣٢٧، الكافي لابن عبد البر ص ١٢٥، القوانين الفقهية ص ٨٠، قليوبي ٢/ ٧٠، حاشية الشرواني ٣/ ٤٤٧، المبدع ٣/ ٣٠، حاشية المقنع ١/ ٣٦٨.','3','153',NULL,NULL,'0');
INSERT INTO [b46] VALUES (829,'ولا كفارة بالجماع فيما دون الفرج ولو أنزل. ولا كفارة على المرأة لو كانت نائمة، أو مجنونة، أو مكرهة.
<hr><span data-type="title">منحة السلوك</span>

وعن أبي حنيفة: أن الجماع في الدبر لا يوجب الكفارة (¬١).
والأصح: أنها تجب كما في القبل (¬٢).

<span data-type="title" id=toc-428>[أحوال سقوط الكفارة]</span>
قوله: ولا كفارة بالجماع فيما دون الفرج، كالتبطين، والتفخيذ، ولو أنزل؛ لعدم الجماع صورةً، وعليه القضاء؛ لوجوده معنى (¬٣).
قوله: ولا كفارة على المرأة لو كانت نائمة، أو مجنونةً.
يعني: إذا جومعت المرأة، وهي نائمة، أو مجنونة، أو مكرهة، فعليها القضاء لا الكفارة؛ لعدم الجناية، لأنها تكون بالقصد، ولا قصد (¬٤).
_________
(¬١) وهي رواية الحسن ضه.
الهداية ١/ ١٣٤، تحفة الفقهاء ١/ ٣٦٢.
(¬٢) وهي رواية أبي يوسف، عن أبي حنيفة، وهو قول: أبي يوسف، ومحمد، وإليه ذهب المالكية، والشافعية، والحنابلة.
تحفة الفقهاء ١/ ٣٦٢، الهداية ١/ ١٣٤، العناية ٢/ ٣٣٨، تبيين الحقائق ١/ ٣٢٧، الشرح الصغير ١/ ٢٤٩، الكافي لابن عبد البر ص ١٢٥، تحفة المحتاج ٣/ ٤٤٧، الوجيز ١/ ١٠٤، الكافي لابن قدامة ١/ ٣٥٦، زاد المستقنع ص ١٧٦.
(¬٣) وإليه ذهب الشافعية، والحنابلة.
وعند المالكية: عليه الكفارة مع القضاء.
تحفة الفقهاء ١/ ٣٦١، كشف الحقائق ١/ ١١٩، شرح الوقاية ١/ ١١٩، الهداية ١/ ١٣٤، حاشية رد المحتار ٢/ ٤٠٩، بدائع الصنائع ٢/ ١٠٠، الشرح الكبير للدردير ١/ ٥٢٩، أسهل المدارك شرح إرشاد السالك في فقه الإمام مالك ١/ ٤٢١ الإقناع في حل ألفاظ أبي شجاع ١/ ٢٢١، عمدة السالك وعدة الناسك ص ٢١٧، المستوعب ٣/ ٤٢٨، حاشية ابن قاسم على الروض المربع ٣/ ٤١٣.
(¬٤) وإليه ذهب الحنابلة، وهو قول: الثوري، والأوزاعي. =','3','154',NULL,NULL,'0');
INSERT INTO [b46] VALUES (830,'ولا كفارة في إفساد صوم غير رمضان أداء.
ومن احتقن، أو استعط، أو أقطر في أذنه دواء، أو دهنًا، أو
<hr><span data-type="title">منحة السلوك</span>

وقال زفر (¬١)، والشافعي (¬٢): لا يجب القضاء، ولا الكفارة.
وعلى هذا الخلاف: إذا صُبَّ الماء في حلق النائم، وتأويل المجنونة أن تفيق فلا يستوعب جنونها الشهر، فصار كالنوم، والإغماء (¬٣).
قوله: ولا كفارة في إفساد صوم غير رمضان أداءً.
لأنها وردت في هتك حرمة رمضان، إذ لا يجوز إخلاؤه عن الصوم، بخلاف غيره من الزمان، قيد بقوله: أداء؛ لأنه إذا لم تجب الكفارة في إفساد صوم غير رمضان من حيث الأداء، فبالأولى: أن لا تجب في الإفساد، من حيث القضاء (¬٤).

<span data-type="title" id=toc-429>[ما يفطر من العلاج وما لا يفطر]</span>
قوله: ومن احتقن، أو استعط، أو أقطر في أذنه دواء، أو دهنًا، أو
_________
= وعند المالكية: عليها القضاء، وعلى زوجها كفارتها.
تحفة الفقهاء ١/ ٣٥٤، تبيين الحقائق ١/ ٣٢٧، حاشية الشلبي ١/ ٣٢٧، الاختيار ١/ ١٣٢، جواهر الإكليل ١/ ١٥١، حاشية العدوي ٢/ ٢٥٠، المغني ٣/ ٦٢، المستوعب ٣/ ٤٣٤.
(¬١) الفتاوى التتارخانية ٢/ ٣٧٢.
(¬٢) روض الطالب ١/ ٤٢٥، مغني المحتاج ١/ ٤٤٤.
(¬٣) شرح فتح القدير ٢/ ٣٤١، العناية ٢/ ٣٤١، بداية المبتدي ١/ ١٣٥، الكتاب ١/ ١٦٧، الهداية ١/ ١٣٥، كشف الحقائق ١/ ١١٩، شرح الوقاية ١/ ١١٩، ملتقى الأبحر ١/ ١٩٩.
(¬٤) وفاقًا للمالكية، والشافعية، والحنابلة.
تحفة الفقهاء ١/ ٣٦١، الهداية ١/ ١٣٥، كنز الدقائق ١/ ٣٢٩، حاشية رد المحتار ٢/ ٤٠٤، كشف الحقائق ١/ ١١٩، الدر المختار ٢/ ٤٠٤، تبيين الحقائق ١/ ٣٢٩، أقرب المسالك ص ٤٣، الخرشي على خليل ٢/ ٢٥٢، أسنى المطالب ١/ ٤٢٥، المنهاج ١/ ٥٣١، حاشية المقنع ١/ ٣٦٨، غاية المنتهى ٢/ ١٩٧.','3','155',NULL,NULL,'0');
INSERT INTO [b46] VALUES (831,'داوى جائفة، أو آمة بدواء رطب، لزمه القضاء، لا غير.
<hr><span data-type="title">منحة السلوك</span>

داوى جائفة، أو آمة بدواء رطب، لزمه القضاء؛ لأن الفطر مما دخل وقد وجد لا غير، يعني: لا تجب الكفارة؛ لعدم صورة الفطر، وهو الأكل والشرب من المنفذ المعهود، وهو الفم (¬١). الاحتقان: وضع الحقنة في الدبر (¬٢). والاستعاط: صب السعوط في الأنف (¬٣).
قال في "الأجناس" (¬٤): الحقنة توجب الفطر، ولا يقع به الرضاع.
_________
(¬١) وإليه ذهب الشافعية، والحنابلة.
وذهب المالكية إلى أنه إن احتقن، أو استعط، أو أقطر في أذنيه دواء، لزمه القضاء. وإذا داوى جائفة، أو آمة بدواء، فإنه لا يفطر؛ لأنه لا يدخل مدخل الطعام.
الكتاب ١/ ١٦٧، المختار ١/ ١٣٢، الهداية ١/ ١٣٥، تحفة الفقهاء ١/ ٣٥٥، العناية ٢/ ٣٤١، تبيين الحقائق ١/ ٣٢٩، شرح ملا مسكين على كنز الدقائق ص ٧٣، القوانين الفقهية ص ٨٠، منح الجليل ٢/ ١٤٧، جواهر الإكليل ١٤٩، ١٥٢، أسنى المطالب ١/ ٤١٥، مغني المحتاج ١/ ٤٢٧، أنوار المسالك شرح عمدة السالك ص ٢١٧، كشاف القناع ٢/ ٣١٨، منتهى الإرادات ١/ ٤٤٧، الروض المربع ص ١٧٥.
(¬٢) لسان العرب ١٣/ ١٢٦ مادة حقن، المغرب ص ١٢٤ مادة حقن، القاموس المحيط ١/ ٦٨٢ مادة ح ق ن، مختار الصحاح ص ٦٢ مادة ح ق ن، المصباح المنير ١/ ١٤٤ مادة حقنت، طلبة الطلبة ص ٥٨.
(¬٣) لسان العرب ٧/ ٣١٤ مادة سعط، القاموس المحيط ٢/ ٥٦٦ مادة س ع ط، مختار الصحاح ص ١٢٦ مادة س ع ط، المغرب ص ٢٢٥ مادة السعوط.
(¬٤) الأجناس لأبي العباس الناطفي (مخطوط) لوحة ٣٨/ ب النسخة الأصلية لدى مكتبة مديرية الأوقاف العامة ببغداد، برقم ٣٦٣٤ ومصورتها لدى معهد البحوث العلمية، وإحياء التراث بجامعة أم القرى برقم ٣٧٩. ونصه فيه: "الأصل في الحقنة توجب الفطر، ولا يقع بها الرضاع".
وكتاب الأجناس رتبها علي بن محمد الجرجاني على ترتيب مختصر الكافي، وقد كان الناطفي لم يرتبها، فرتبها الجرجاني.','3','156',NULL,NULL,'0');
INSERT INTO [b46] VALUES (832,'وإن أقطر في أذنه ماء، أو في ذكره دهنًا، لم يفطر.
<hr><span data-type="title">منحة السلوك</span>

والجائفة: هي الطعنة التي تبلغ الجوف (¬١).
والآمة: الشجة التي تبلغ أُمَّ الرأس (¬٢). قيد بقوله: رطبٍ؛ لأن المفطر هو الرطب عند أبي حنيفة، خلافًا لهما (¬٣).
واليابس ليس بمفطر اتفاقًا (¬٤)، ولكن أكثر المشايخ على أن العبرة للوصول، حتى إذا علم أن اليابس وصل إلى جوفه فسد الصوم، وإن علم أن الرطب لم يصل لا يفسد (¬٥).
قوله: وإن أقطر في أذنه ماء، أو في ذكره دهنًا، لم يفطر (¬٦).
أما إذا أقطر في أذنه ماء: فإنه لا يفطر؛ لعدم الوصول، بخلاف ما إذا
_________
(¬١) مختار الصحاح ص ٥٠ مادة جوف، القاموس المحيط ١/ ٥٥٩ مادة جوف، المغرب ص ٩٦ مادة الجائفة.
(¬٢) القاموس المحيط ١/ ١٧٩ مادة أم م، مختار الصحاح ص ١١ مادة أم م، المغرب ص ٢٩ مادة أئتمنه، طلبة الطلبة ص ٥٨، حلية الفقهاء ص ١٩٦، أنيس الفقهاء ص ٢٩٤.
(¬٣) الهداية ١/ ١٣٥، مختصر الطحاوي ص ٥٧، تبيين الحقائق ١/ ٣٢٩، شرح فتح القدير ٢/ ٣٤٣، حاشية الشلبي ١/ ٣٢٩، العناية ٢/ ٣٤٣، البحر الرائق ٢/ ٢٧٩.
(¬٤) شرح فتح القدير ٢/ ٣٤٣.
(¬٥) شرح فتح القدير ٢/ ٣٤٣، العناية ٢/ ٣٤٣، تبيين الحقائق ١/ ٣٣٠، الهداية ١/ ١٣٥، البحر الرائق ٢/ ٢٧٩، كشف الحقائق ١/ ١٢٠.
(¬٦) وذهب المالكية، والشافعية، والحنابلة: إلى أنه إذا أقطر في أذنه ماء، فوصل إلى الباطن أنه يفطر، وإذا أقطر في ذكره دهنًا لم يفطر عند المالكية، والحنابلة. ويفطر عند الشافعية.
شرح فتح القدير ٢/ ٣٤٢، العناية ٢/ ٣٤٣، الهداية ١/ ١٣٥، كنز الدقائق ١/ ٣٢٩، البحر الرائق ٢/ ٢٧٩، تبيين الحقائق ١/ ٣٢٩، القوانين الفقهية ص ٨٠، بلغة السالك ١/ ٢٤٦، أسنى المطالب ١/ ٤١٦، المنهاج ١/ ٥١٣، شرح المحلي على المنهاج ٢/ ٥٦، عمدة السالك ص ٢١٧، زاد المحتاج ١/ ٥١٢، تحفة المحتاج ٣/ ٤٠٢، المقنع ١/ ٣٦٥، منتهى الإرادات ١/ ٤٤٨.','3','157',NULL,NULL,'0');
INSERT INTO [b46] VALUES (833,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

أقطر دهنًا فإنه يصل بقوة التشرب (¬١). وأما إذا أقطر في ذكره دهنًا، فإنه لا يفطر أيضًا عند أبي حنيفة (¬٢).
وقال أبو يوسف: يفطر (¬٣). ومحمد: مضطرب (¬٤).
وهذا الاختلاف، مبني على أنه: هل بين المثانة، والجوف منفذ أم لا؟ (¬٥) واختلفوا في الإقطار في قُبُلِها (¬٦).
والصحيح الفطر (¬٧).
_________
(¬١) تبيين الحقائق ١/ ٣٢٩، شرح فتح القدير ٢/ ٣٤١، تحفة الفقهاء ١/ ٣٥٥، البحر الرائق ٢/ ٢٧٩.
(¬٢) مختصر الطحاوي ص ٥٦، تحفة الفقهاء ١/ ٣٥٥، تبيين الحقائق ١/ ٣٢٩، البحر الرائق ٢/ ٢٧٩، ملتقى الأبحر ١/ ٢٠٠، الكتاب ١/ ١٦٨، الاختيار ١/ ١٣٣.
(¬٣) مختصر الطحاوي ص ٥٦، تحفة الفقهاء ١/ ٣٥٥، تبيين الحقائق ١/ ٣٢٩، الاختيار ١/ ١٣٣، البحر الرائق ٢/ ٢٧٩، ملتقى الأبحر ١/ ٢٠٠، الكتاب ١/ ١٦٨.
(¬٤) قال الزيلعي: ومحمد توقف فيه، وقيل: هو مع أبي يوسف. والأظهر: أنه مع أبي حنيفة. تبيين الحقائق ١/ ٣٣٠، شرح فتح القدير ١/ ٣٤٤.
(¬٥) قال الزيلعي: وهو ليس باختلاف على التحقيق. والأظهر: أنه لا منفذ له، وإنما يجتمع البول فيها بالترشح ... وهذا الاختلاف فيما إذا وصل إلى المثانة. وأما إذا لم يصل بأن كان في قصبة الذكر بعد، لا يفطر بالإجماع.
تبيين الحقائق ١/ ٣٣٠.
(¬٦) وهو على الخلاف السابق، في الإقطار في ذكر الرجل.
حاشية الشلبي ١/ ٣٣٠، تبيين الحقائق ١/ ٣٣٠، العناية ٢/ ٣٤٤، شرح فتح القدير ٢/ ٣٤٤، البحر الرائق ٢/ ٢٧٩.
(¬٧) وصححه الزيلعي، والسرخسي، وإليه ذهب الشافعية.
وذهب المالكية، والحنابلة: إلى أن الإقطار في قبلها لا يفسد الصوم.
تبيين الحقائق ١/ ٣٣٠، العناية ٢/ ٣٤٤، البحر الرائق ٢/ ٢٧٩، حاشية الشلبي ١/ ٣٣٠، =','3','158',NULL,NULL,'0');
INSERT INTO [b46] VALUES (834,'ومن ذاق شيئًا ومجّه لم يفطر.
ويكره للصائم الذوق، إلا حالة الشراء.
<hr><span data-type="title">منحة السلوك</span>

قوله: ومن ذاق شيئًا، ومجه (¬١) لم يفطر؛ لانعدام الفطر صورة ومعنى (¬٢).

<span data-type="title" id=toc-430>[ما يكره للصائم وما لا يكره]</span>
قوله: ويكره للصائم الذوق؛ لأنه تعريض لإفساد صومه (¬٣).
قوله: إلا حالة الشراء.
يعني: إذا ذاق الصائم الطعام حالة الشراء، لا يكره؛ للضرورة (¬٤).
وقيل: المرأة إذا كان زوجها سيء الخلق، لا بأس أن تذوق المرق بلسانها (¬٥).
_________
= تحفة الفقهاء ١/ ٣٥٥، القوانين الفقهية ص ٨٠، الشرح الصغير ١/ ٢٤٦، مغني المحتاج ١/ ٤٢٨، عميرة ٢/ ٥٦، شرح منتهى الإرادات ١/ ٤٤٩، مطالب أولي النهى ٢/ ١٩٣.
(¬١) أي: رماه.
مختار الصحاح ص ٢٥٧ مادة م ج ج، القاموس المحيط ٤/ ٢٠٤ مادة م ج ج، لسان العرب ٢/ ٣٦١ مادة مجج، المصباح المنير ٢/ ٥٦٤ مادة مجّ.
(¬٢) وفاقًا للثلاثة.
الكتاب ١/ ١٦٩، المبسوط ٣/ ٩٣، الاختيار ١/ ١٣٤، العناية ٢/ ٣٤٤، ملتقى الأبحر ١/ ٢٠١، مختصر خليل ص ٧١، التلقين ص ٥٧، شرح المحلي على المنهاج ٢/ ٦٢، قليوبي ٢/ ٦٢، الروض المربع ص ١٧٨، عمدة الطالب ص ١٩٩.
(¬٣) وفاقًا للثلاثة.
ملتقى الأبحر ١/ ٢٠١، الكتاب ١/ ١٦٩، الاختيار ١/ ١٣٤، المبسوط ٣/ ٩٣، الهداية ١/ ١٣٥، أقرب المسالك ص ٤٢، القوانين الفقهية ص ٧٨، هداية الغلام ص ٦٧، السراج الوهاج ص ١٤٢، المحرر ١/ ٢٢٩، السلسبيل ١/ ٣٣٨.
(¬٤) شرح فتح القدير ٢/ ٣٤٥، تبيين الحقائق ١/ ٣٣٠.
(¬٥) هذا في الفرض. وأما في صوم التطوع: فلا يكره؛ لأن الإفطار فيه مباح بالعذر، وبغيره، على رواية الحسن، عن أبي حنيفة.','3','159',NULL,NULL,'0');
INSERT INTO [b46] VALUES (835,'ويكره للمرأة مضغ الطعام لولدها بغير ضرورة، ومضغ العلك مكروه للصائم، وقيل: يفسد إن كان مُتفتِّتًا أو أسود،
<hr><span data-type="title">منحة السلوك</span>

قوله: ويكره للمرأة مضغ الطعام لولدها بغير ضرورة.
لما قلنا إنه تعريض لإفساد الصوم، بخلاف ما إذا كان ضرورة، بأن لم تجد المرأة من يمضغ لصبيها الطعام، من حائض، أو نفساء، أو غيرهما ممن لا يصوم، ولم تجد طبيخًا، ولا لبنًا حليبًا. ألا يرى أنه يجوز لها الإفطار إذا خافت على الولد؟ فالمضغ أولى (¬١).
قوله: ومضغ العلك مكروه للصائم.
لأنه يتهم به الإفطار؛ لأن من رآه من بعيد يظنه آكلًا (¬٢).
قوله: وقيل يفسد.
أي: مضغ العلك مفسد للصوم، إذا كان مُتفتِّتًا؛ لأنه إذا كان مُتفتِّتًا يصل منه شيء إلى جوفه. وكذلك إذا كان أسود، وإن كان ملتئمًا (¬٣).
_________
(¬١) بدائع الصنائع ٢/ ١٠٦، تبيين الحقائق ١/ ٣٣٠، الكتاب ١/ ١٦٩، بداية المبتدي ١/ ١٣٥، الهداية ١/ ١٣٥.
(¬٢) وإليه ذهب المالكية، والشافعية، والحنابلة.
فإن وجد طعم العلك في حلقه أفطر، عند المالكية، والحنابلة.
وعند الشافعية: لا يضر وصول ريحه، وطعمه إلى جوفه.
المختار ١/ ١٣٤، بدائع الصنائع ٢/ ١٠٦، العناية ٢/ ٣٤٥، شرح فتح القدير ٢/ ٣٤٥، مختصر خليل ص ٧١، الشرح الصغير ١/ ٢٤٣، تحفة المحتاج ٣/ ٤٢٥، شرح المحلي ٢/ ٦٢، قليوبي ٢/ ٦٢، مغني المحتاج ١/ ٤٣٦، زاد المستقنع ص ١٧٨، الكافي لابن قدامة ١/ ٣٦١.
(¬٣) قال المصنف في البناية ٣/ ٦٧٦: لأن الأسود يذوب، ويصل إلى جوفه منه شيء، وإذا كان أبيض ملتئمًا لا يفطره. =','3','160',NULL,NULL,'0');
INSERT INTO [b46] VALUES (836,'ولا يكره للمرأة المفطرة، وفي الرجل خلاف.
ويباح للصائم الكحل،
<hr><span data-type="title">منحة السلوك</span>

قوله: ولا يكره.
أي: مضغ العلك للمرأة المفطرة لأنه يقوم مقام السواك في حقهن؛ لأن أسنانهن ضعيفة لا تحتمل السواك، وهي تنقي الأسنان، وتشد اللثة (¬١)، كالسواك (¬٢).
قوله: وفي الرجل خلاف.
أي: في مضغ العلك للرجل خلاف.
فقيل: يكره إذا لم يكن من علة؛ لما فيه من التشبه بالنساء، والعلة مثل ما إذا كان في فمه بخر.
وقيل: لا يكره (¬٣).

<span data-type="title" id=toc-431>[الكحل للصائم]</span>
قوله: ويباح للصائم الكحل (¬٤).
_________
= بدائع الصنائع ٢/ ١٠٦، الهداية ١/ ١٣٥، تبيين الحقائق ١/ ٣٣١، شرح فتح القدير ٢/ ٣٤٥.
(¬١) اللثة -بالتخفيف-: ما حول الأسنان. والأصل "لثى" فحذفت اللام وعوض عنها الهاء، والجمع "لثات" على لفظ المفرد.
لسان العرب ٢/ ١٨٨ مادة لوث، مختار الصحاح ص ٢٤٧ مادة ل ث ي، مجمل اللغة ص ٦٣٩ باب اللام والثاء وما يثلثهما مادة لثا، المصباح المنير ٢/ ٥٤٩ مادة اللثة.
(¬٢) الهداية ١/ ١٣٥، شرح فتح القدير ٢/ ٣٤٥، تبيين الحقائق ١/ ٣٣١، بدائع الصنائع ٢/ ١٠٦، حاشية الشلبي ١/ ٣٣١.
(¬٣) الهداية ١/ ١٣٥، تبيين الحقائق ١/ ٣٣١، شرح فتح القدير ٢/ ٣٤٥، حاشية الشلبي ١/ ٣٣١.
(¬٤) وإليه ذهب الشافعية؛ لأنه نوع ارتفاق. =','3','161',NULL,NULL,'0');
INSERT INTO [b46] VALUES (837,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

لما روي أنه: -صلى الله عليه وسلم- "كان يكتحل بالإثْمِد (¬١) وهو صائم" رواه الجصاص في شرحه لمختصر الطحاوي (¬٢). وعن عائشة -رضي الله عنها-: "أنه -صلى الله عليه وسلم- اكتحل وهو صائم" رواه الدارقطني (¬٣).
_________
= كنز الدقائق ١/ ٣٣١، الكتاب ١/ ١٦٥، ملتقى الأبحر ١/ ٢٠١، الهداية ١/ ١٣٦، نور الإيضاح ونجاة الأرواح ص ٦٠٢، مغني المحتاج ١/ ٤٢٨، السراج الوهاج ص ١٤٠.
(¬١) بكسر الهمزة، والميم - حجر يكتحل به، ويقال: إنه معرب. ومعادنه بالمشرق.
تاج العروس ٢/ ٣١٢ مادة ثمد، لسان العرب ٣/ ١٠٥ مادة ثمد، المصباح المنير ١/ ٨٤، المطلع على أبواب المقنع ص ١٧٧، الدر النقي ٢/ ٤٠٧.
(¬٢) جـ ١ لوحة ٢٢٩/ أ (مخطوط) النسخة الأصلية لدى متحف الآثار العتيقة بقونية بتركيا، تحت رقم ٣٢٧/ ٤٩٧٩ - ٧ ونصه: "قال أبو جعفر: ولا بأس بالكحل، والسواك للصائم، أما الكحل: فلما حدثنا عبد الباقي بن قانع قال: حدثنا عبد الله بن أحمد قال: حدثنا محمد بن سلمة قال: حدثنا حسان بن علي، عن محمد بن عبد الله بن أبي رافع، عن أبيه، عن جده، "أن النبي -صلى الله عليه وسلم- كان يكتحل بالإثمد، وهو صائم".
ورواه ابن ماجه ١/ ٥٣٦ كتاب الصيام، باب ما جاء في السواك والكحل للصائم رقم ١٦٧٨، والبيهقي ٤/ ٢٦٢ كتاب الصيام باب الصائم يكتحل، وأبو يعلى في مسنده ٨/ ٢٢٥ رقم ٤٧٩٢.
قال ابن حجر في الدراية ١/ ٢٨١: ضعيف جدًا.
وقال في مصباح الزجاجة ٢/ ١٣: إسناده ضعيف.
وقال النووي في المجموع ٦/ ٣٤٨: إسناده ضعيف.
وقال المصنف في البناية ٣/ ٦٤٥: وهو حديث منكر.
(¬٣) لم أقف عليه عند الدارقطني. وإنما رواه البيهقي ٤/ ٢٦٢ كتاب الصيام، باب الصائم يكتحل، وابن ماجه ١/ ٥٣٦ كتاب الصيام، باب ما جاء في السواك والكحل للصائم رقم ١٦٧٨، وأبو يعلى ٨/ ٢٢٥ رقم ٤٧٩٢. وفي سنده سعيد بن عبد الجبار الزبيدي الحمصي.
قال في نصب الراية ٢/ ٤٥٦: وهو مجمع على ضعفه.
وقال النووي في شرح المهذب ٦/ ٣٤٨: رواه ابن ماجه بإسناد ضعيف. =','3','162',NULL,NULL,'0');
INSERT INTO [b46] VALUES (838,'ولو وجد طعمه في حلقه، ودَهْن الشارب إذا قصد بهما غير زينة، وكذا للمفطر.
<hr><span data-type="title">منحة السلوك</span>

قوله: ولو وجد طعمه في حلقه. واصل بما قبله؛ لأنه وصل من المسام، فلا يعتد به (¬١). خلافًا لمالك (¬٢).

<span data-type="title" id=toc-432>[استعمال الصائم للدهن]</span>
قوله: ودهن الشارب.
أي: يباح، للصائم دهن الشارب أيضًا؛ لأنه ليس فيه شيء ينافي الصوم، بخلاف المحرم (¬٣).
والدهن: -بفتح الدال- مصدر، والمعنى هنا على هذا. وبالضم اسم (¬٤).
قوله: إذا قصد بهما.
أي: بالكحل، ودهن الشارب غير زينة، بأن كان قصده التداوي (¬٥).
قوله: وكذا للمفطر.
_________
= وقال في الدراية ١/ ٢٨١: أخرجه ابن ماجه، وفي إسناده سعيد بن أبي سعيد وهو ضعيف جدًا.
وقال في مصباح الزجاجة ٢/ ١٣: إسناده ضعيف؛ لضعف الزبيدي.
(¬١) تحفة الفقهاء ١/ ٣٦٦، بدائع الصنائع ٢/ ١٠٦.
(¬٢) وأحمد. حيث يريان: أن الكحل يفطر.
منح الجليل ٢/ ١٣٢، الشرح الكبير للدردير ١/ ٥٢٤، الروض المربع ص ١٧٥، حاشية المقنع ١/ ٣٦٥.
(¬٣) كنز الدقائق ١/ ٣٣١، بداية المبتدي ١/ ١٣٦، تبيين الحقائق ١/ ٣٣١، الهداية ١/ ١٣٦.
(¬٤) لسان العرب ١٣/ ١٦٠ مادة دهن، مختار الصحاح ص ٨٩ مادة د هـ ن، المصباح المنير ١/ ٢٠٢ مادة دهنت، القاموس المحيط ٢/ ٢٢٦ مادة د هـ ن.
(¬٥) الهداية ١/ ١٣٦، شرح فتح القدير ٢/ ٣٤٧، تبيين الحقائق ١/ ٣٣١.','3','163',NULL,NULL,'0');
INSERT INTO [b46] VALUES (839,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

أي: وكذا يباح الكحل، ودهن الشارب للمفطر أيضًا، إذا قصد بهما غير زينة.
وكذلك يباح له دهن شعر الوجه (¬١) وبذلك (¬٢) جاءت السنة عن رسول الله -صلى الله عليه وسلم- (¬٣)؛ ولأنه يعمل عمل الخضاب (¬٤) (¬٥)، ولا يفعل لتطويل اللحية إذا كانت بالقدر المسنون وهي القبضة (¬٦).
والأصل فيه: ما روي "أن عبد الله بن عمر -رضي الله عنهما- كان يقبض على لحيته فيقطع ما زاد على الكف" رواه أبو داود (¬٧).
_________
(¬١) الهداية ١/ ١٣٦، تبيين الحقائق ١/ ٣٣١، شرح فتح القدير ٢/ ٣٤٧، العناية ١/ ٣٤٧.
(¬٢) أي: بالخضاب. قال المصنف في البناية ٣/ ٧٣٩: لأنه يعمل عمل الخضاب، أي: لأن دهن الشارب يعمل عمل الخضاب، وبالخضاب جاءت السنة.
(¬٣) رواه البخاري ٥/ ٢٢١٠ كتاب اللباس باب ما يذكر في الشيب رقم ٥٥٥٨.
عن عثمان بن عبد الله بن موهب قال: دخلت على أم سلمة -رضي الله عنها-، فأخرجت إلينا شعرًا من شعر النبي -صلى الله عليه وسلم- مخضوبًا.
(¬٤) العناية ٢/ ٣٤٧، تبيين الحقائق ١/ ٣٣١، الهداية ١/ ١٣٦، شرح فتح القدير ٢/ ٣٤٧.
(¬٥) خضب الشيء يخضبه خضبًا: إذا غير لونه بحمرة، أو صفرة.
لسان العرب ١/ ٣٥٧ مادة خضب، القاموس المحيط ٢/ ٦٨ مادة خ ض ب، مختار الصحاح ص ٧٥ مادة خ ض ب، المصباح المنير ١/ ١٧١ مادة خضب.
(¬٦) العناية ٢/ ٣٤٧، شرح فتح القدير ٢/ ٣٤٧.
(¬٧) ٢/ ٣٠٦ كتاب الصوم، باب القول عند الإفطار رقم ٢٣٥٧، والدارقطني ٢/ ١٨٥ كتاب الصيام؛ باب القبلة للصائم رقم ٢٥، والحاكم ١/ ٤٢٢ كتاب الصوم. وذكره البخاري تعليقًا في باب تقليم الأظفار ٦/ ٨٧٥، ومالك ١/ ٣٩٦ كتاب الحج، باب التقصير رقم ١٨٧ عن نافع بلفظ: "كان ابن عمر إذا حلق رأسه في حج، أو عمرة أخذ من لحيته وشاربه، والنسائي في السنن الكبرى ٢/ ٢٥٥ كتاب الصيام، باب ما يقول إذا أفطر رقم ٣٣٢٩. وتمامه عند أبي داود: قال: حدثنا عبد الله بن محمد بن يحيى أبو محمد، =','3','164',NULL,NULL,'0');
INSERT INTO [b46] VALUES (840,'ولا يكره السواك للصائم بمسواك رطب، أو يابس.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-433>[استخدام الصائم للسواك]</span>
قوله: ولا يكره للصائم سواك رطب، أو يابس (¬١).
لما روي عن عبد الله بن عامر بن ربيعة (¬٢)، عن أبيه (¬٣) قال: "رأيت رسول الله -صلى الله عليه وسلم- يستاك وهو صائم، ما لا أعده، ولا أحصي" رواه الترمذي وأبو داود (¬٤). وعن عائشة -رضي الله عنها- عن النبي -صلى الله عليه وسلم- "السواك مطهرة للفم،
_________
= حدثنا علي بن الحسن، أخبرني الحسين بن واقد، ثنا مروان يعني: ابن سالم المقفع قال: رأيت ابن عمر يقبض على لحيته، فيقطع ما زاد على الكف.
قال الدارقطني ٢/ ١٨٥: إسناده حسن، وقال الحاكم ١/ ٤٢٢: على شرط الشيخين.
(¬١) بل يستحب كالمضمضة.
بداية المبتدي ١/ ١٣٦، مختصر الطحاوي ص ٥٦، الهداية ١/ ١٣٦، تبيين الحقائق ١/ ٣٣٢، حاشية الشلبي ١/ ٣٣٢.
(¬٢) هو عبد الله بن عامر بن ربيعة الغزي أبو محمد المدني، أبوه من كبار الصحابة، روى عن أبيه، وعمر وعثمان وعنه الزهري ويحيى بن سعيد الأنصاري وعبد الرحمن بن القاسم، كان له أخ أكبر منه اسمه أيضًا عبد الله، استشهد يوم الطائف، وأمهما أم عبد الله ليلى بنت أبي خيثمة. توفي سنة ٥٩ هـ.
تهذيب التهذيب ٥/ ٢٧٠، الإصابة ٢/ ٣٢٩، الاستيعاب ٢/ ٣٥٧.
(¬٣) هو عامر بن ربيعة بن كعب الغزي صحابي من الولاة، قديم الإسلام. شهد المشاهد كلها مع رسول الله -صلى الله عليه وسلم-، واستخلفه عثمان على المدنية لما حج، له ٢٢ حديثًا، اعتزل فتنة مقتل عثمان ومات بعد مقتل عثمان بأيام.
تهذيب التهذيب ٥/ ٦٢، الاستيعاب ٣/ ٤، الأعلام ٣/ ٢٥١.
(¬٤) الترمذي ٣/ ٧٧ كتاب الصوم، باب ما جاء في السواك للصائم رقم الحديث ٧٢٥، وأبو داود ٢/ ٣٠٧ كتاب الصوم، باب السواك للصائم برقم ٢٣٦٤، ورواه الحميدي ١/ ٧٧ رقم ١٤١ في أحاديث عامر بن ربيعة، وابن أبي شيبة ٢/ ٢٩٤ كتاب الصيام، باب من رخص في السواك للصائم برقم ٩١٤٨، وأحمد ٣/ ٤٤٥، والبغوي في شرح السنة ٦/ ٢٩٨ رقم ١٧٥٧، وعلقه البخاري ٢/ ٦٨٢ كتاب الصوم، باب السواك الرطب واليابس للصائم قال: ويذكر عن عامر بن ربيعة قال: رأيت النبي -صلى الله عليه وسلم- يستاك وهو صائم، بصيغة التمريض، وأبو يعلى في مسنده ١٣/ ١٥٠ في حديث عامر بن ربيعة برقم ٧١٩٣، وابن خزيمة ٣/ ٢٤٧ =','3','165',NULL,NULL,'0');
INSERT INTO [b46] VALUES (841,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

مرضاة للرب" رواه البخاري (¬١).
وعن أبي هريرة -رضي الله عنه- عن النبي -صلى الله عليه وسلم- قال: "لولا أن أشق على أمتي، لأمرتهم بالسواك عند كل وضوء" رواه البخاري (¬٢).
فدل إطلاق الأحاديث على جواز الاستياك مطلقًا؛ لأنه لم يخص
_________
= كتاب الصوم، باب الرخصة في السواك للصائم برقم ٢٠٠٧، والبيهقي في السنن ٤/ ٧٧٢ كتاب الصيام، باب السواك للصائم.
قال الترمذي: حديث عامر بن ربيعة حديث حسن، والعمل على هذا عند أهل العلم، لا يرون بالسواك للصائم بأسًا.
(¬١) رواه البخاري تعليقًا بصيغة الجزم ٢/ ٦٨٢ كتاب الصوم باب السواك الرطب واليابس للصائم، ورواه مسندًا الحميدي في مسنده ١/ ٨٧ رقم ١٦٢، والشافعي في المسند ١/ ٢٧، وابن أبي شيبة ١/ ١٥٦ كتاب الطهارة، باب ما ذكر في السواك رقم ١٧٩٢، وأحمد ٦/ ٤٧، والدارمي ١/ ١٨٥ كتاب الطهارة باب السواك مطهرة للفم رقم ٦٨٨، والنسائي ١/ ١٠ كتاب الطهارة باب الترغيب في السواك رقم ٥، وأبو يعلى ٨/ ٥١ رقم ٤٥٦٩، وابن خزيمة ١/ ٧٠ كتاب الوضوء، باب فضل السواك وتطهير الفم به رقم ١٣٥، وابن حبان ٣/ ٣٤٨ كتاب الطهارة، باب إثبات رضا الله عز وجل للمتسوك رقم ١٠٦٧، والطبراني في الأوسط ١/ ١٩٦ رقم ٢٧٨، وابن عدي في الكامل ٢٣٦، والبيهقي في السنن ١/ ٣٤ كتاب الطهارة، باب في فضل السواك، والبيهقي في معرفة السنن والآثار ١/ ٢٥٨ كتاب الطهارة، باب السواك رقم ٥٨٢، وأبو نعيم في الحلية ٧/ ١٥٩، والبغوي في شرح السنة ١٩٩.
قال النووي في المجموع ١/ ٢٦٧: حديث صحيح، رواه أبو بكر محمد بن إسحاق بن خزيمة إمام الأئمة في صحيحه، والنسائي، والبيهقي، في سننهما، وآخرون بأسانيد صحيحة، وذكره البخاري في صحيحه، كتاب الصيام تعليقًا فقال: وقالت عائشة -رضي الله عنها- عن النبي -صلى الله عليه وسلم-: "السواك مطهرة للفم، مرضاة للرب" وهذا التعليق صحيح.
(¬٢) في صحيحه ٢/ ٦٨٢ معلقًا بصيغة الجزم في كتاب الصوم، باب السواك الرطب واليابس للصائم ولفظه: "وقال أبو هريرة -رضي الله عنه- عن النبي -صلى الله عليه وسلم- ... ".
ورواه مسلم في صحيحه مسندًا عنه ١/ ٢٢٠ كتاب الطهارة، باب السواك رقم ٢٥٢.','3','166',NULL,NULL,'0');
INSERT INTO [b46] VALUES (842,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

الصائم من غيره، ولا الغداة من العشي، ولا غير المبلول من المبلول (¬١).
وقال الشافعي: يكره آخر النهار (¬٢).
وقال أبو يوسف: يكره إذا كان مبلولًا (¬٣) (¬٤).
_________
(¬١) تبيين الحقائق ١/ ٣٣٢، تحفة الفقهاء ١/ ٣٦٧، الهداية ١/ ١٣٦، شرح الوقاية ١/ ١٢٠، كشف الحقائق ١/ ١٢٠.
(¬٢) وإليه ذهب الحنابلة.
التنبيه ص ٦٧، إرشاد الغاوي ١/ ٢٩٧، المقنع في الفقه لأبي الحسن المحاملي (مخطوط) لوحة ٣/ أ، منتهى الإرادات ١/ ٣٨، الروض المربع ص ٢٤.
(¬٣) بالماء أو رطبًا. وما ذهب إليه مردود بالمضمضة فكيف يكره له استعمال العود الرطب وليس فيه من الماء قدر ما يبقى في فمه من البلل من أثر المضمضة.
تحفة الفقهاء ١/ ٣٦٧، العناية ٢/ ٣٤٨، تبيين الحقائق ١/ ٣٣٢.
(¬٤) اختلف العلماء في حكم السواك للصائم على عدة أقوال:
القول الأول: ويقضي باستحباب السواك للصائم مطلقًا، قبل الزوال، وبعده بيابس، أو رطب.
وإلى هذا القول ذهب الحنفية (أ)، وهو رواية عن الإمام أحمد (ب)، وبه قال: النخعي، ومحمد بن سيرين، وعروة بن الزبير وسعيد بن جبير، وبه قال: المزني، وأبو شامة، والعز بن عبد السلام. وبه قال: عمر بن الخطاب، وعلي بن أبي طالب، وابن عمر، وابن عباس، وعائشة (جـ).
القول الثاني: ويقضي بكراهة السواك للصائم بعد الزوال، ويستحب قبله برطب، أو يابس. =
_________
(أ) شرح فتح القدير ٢/ ٣٤٨، تبيين الحقائق ١/ ٣٣٢، بدائع الصنائع ١/ ١٩، العناية ٢/ ٣٤٨.
(ب) الفروع ١/ ١٢٥.
(جـ) نيل الأوطار ١/ ١٣١، قواعد الأحكام ١/ ٣٢، شرح فتح القدير ٢/ ٣٤٨، الشرح الكبير لابن قدامة ١/ ١٣٠، المغني ١/ ١١٠، المجموع ١/ ٢٧٩، الفروع ١/ ١٢٥.','3','167',NULL,NULL,'0');
INSERT INTO [b46] VALUES (843,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
=  وإليه ذهب: الشافعية (أ)، والحنابلة (ب)، وهو مروي عن علي بن أبي طالب أيضًا (جـ)، وبه قال: أبو ثور، وإسحاق بن راهويه، وعطاء، ومجاهد (د).
القول الثالث: يكره للصائم السواك الرطب دون غيره، سواء أول النهار، أو آخره.
وإليه ذهب المالكية (هـ)، ورواية عن الإمام أحمد، اختارها القاضي من الحنابلة (و).
القول الرابع: التفرقة بين صوم الفرض، وصوم النفل، فيكره في الفرائض بعد الزوال، ولا يكره في النفل.
وهذا القول: حكاه صاحب المعتمد من الشافعية عن القاضي حسين، وصدره في الإنصاف بقوله: " وقيل" (ز).
القول الخامس: يكره السواك للصائم إذا كان مبلولًا بالماء. وهو قول أبي يوسف (حـ).
الأدلة:
أولًا: أدلة القول الأول:
١ - استدلوا بعموم قول النبي -صلى الله عليه وسلم-: "لولا أن أشق على أمتي لأمرتهم بالسواك عند كل صلاة" (ط).
إذ يدخل في عموم "كل صلاة" الظهر والعصر للصائم والمفطر (ي). =
_________
(أ) الحاوي الكبير ٣/ ٤٤٦، مغني المحتاج ١/ ٥٦، نهاية المحتاج ١/ ١٨٢، المجموع ٥/ ٢٧٥.
(ب) الشرح الكبير لابن قدامة ١/ ١٣٠، المغني ١/ ١١٠، الفروع ١/ ١٢٥، الإنصاف ١/ ١١٧.
(جـ) شرح فتح القدير ٢/ ٣٤٨.
(د) الشرح الكبير لابن قدامة ١/ ١٣٠، المغني ١/ ١١٠، الحاوي الكبير ٣/ ٤٦٧، المجموع ١/ ٢٧٩.
(هـ) المعونة ١/ ٤٧٤، المدونة ١/ ١٨٠، التفريع ١/ ٣٠٨، الذخيرة ٢/ ٥٠٨، حاشية الدسوقي ١/ ٥٣٤، الشرح الكبير للدردير ١/ ٥٣٤، جواهر الإكليل ١/ ١٥٢.
(و) الفروع ١/ ١٢٥، الإنصاف ١/ ١١٧.
(ز) الإنصاف ١/ ١١٧.
(حـ) تبيين الحقائق ١/ ٣٣٢، العناية ٢/ ٣٤٨.
(ط) رواه البخاري ١/ ٣٠٣، كتاب الجمعة، باب السواك يوم الجمعة رقم ٨٤٧ عن أبي هريرة -رضي الله عنه-.
(ي) بدائع الصنائع ١/ ١٩، شرح فتح القدير ٢/ ٣٤٨.','3','168',NULL,NULL,'0');
INSERT INTO [b46] VALUES (844,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
= ٢ - واستدلوا بما رواه عبد الله بن عامر بن ربيعة عن أبيه قال: "رأيت رسول الله -صلى الله عليه وسلم- يستاك، وهو صائم، ما لا أعده، ولا أحصيه" (١).
٣ - واستدلوا أيضًا بما روته عائشة -رضي الله عنها- أن النبي -صلى الله عليه وسلم- قال: "خير خلال الصائم السواك" (ب).
وجه الاستدلال من الحديث: قالوا: إن الحديث مطلق ولم يفصل بين حال وحال (جـ).
٤ - قالوا: ولأن ما يستحب فعله للصائم قبل الزوال، يستحب له فعله بعد الزوال، كالمضمضة، والاستنشاق (د).
٥ - ولأن السواك طهارة للفم، فلم يكره في جميع النهار (هـ).
٦ - ولأن النصوص الواردة فيه كلها مطلقة، فلا يجوز تقييدها بزمان بمجرد الرأي (و).
أدلة القول الثاني:
١ - استدلوا بقول النبي -صلى الله عليه وسلم-: "لخلوف فم الصائم، أطيب عند الله من ريح المسك" (ز).
قالوا وأطيبية الخلوف، تدل على طلب إبقائه، فكرهت إزالته، والمعنى في اختصاصها بما بعد الزوال أن تغير الفم بالصوم، إنما يظهر حينئذ (حـ).
٢ - استدلوا بقول النبي -صلى الله عليه وسلم- "إذا صمتم فاستاكوا بالغداة ولا تستاكوا بالعشي فإن =
_________
(أ) رواه الترمذي ٣/ ٧٦ رقم ٧٢٥، وأحمد ٣/ ٤٤٥، وأبو داود ٢/ ٣٠٧ رقم ٢٣٦٤، وابن خزيمة ٣/ ٢٤٧ رقم ٢٠٠٧ والبيهقي ٤/ ٢٧٢، قال الترمذي: حديث عامر بن ربيعة حديث حسن.
(ب) رواه ابن ماجة ١/ ٣٥٦ والدارقطني ٢/ ٢٠٣ والبيهقي ٤/ ٢٧٢ من حديث مجالد، عن الشعبي، عن مسروق، عن عائشة، ومجالد قال عنه ابن معين: لا يحتج به، وقال أحمد: ليس بشيء، وقال النسائي: ليس بالقوي. وقال الدارقطني: ضعيف.
ميزان الاعتدال ٣/ ٤٣٨.
(جـ) شرح فتح القدير ٢/ ٣٤٨، العناية ٢/ ٣٤٨، تبيين الحقائق ١/ ٣٣٢.
(د) تبيين الحقائق ١/ ٣٣٢، العناية ٢/ ٣٤٨، الحاوي الكبير ٣/ ٤٦٧.
(هـ) حاشية الشلبي ١/ ٣٣٢، تبيين الحقائق ١/ ٣٣٢.
(و) تبيين الحقائق ١/ ٣٣٢.
(ز) رواه البخاري ٢/ ٦٧١ كتاب الصوم، باب الريان للصائمين رقم ١٧٩٧.
(حـ) الحاوي ٣/ ٤٦٧، مغني المحتاج ١/ ٥٦.','3','169',NULL,NULL,'0');
INSERT INTO [b46] VALUES (845,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
=  الصائم إذا يبست شفتاه كانت نورًا يوم القيامة" (أ).
٣ - قالوا: ولأن في السواك، إزالة الأثر المحمود وهو الخلوف، فشابه دم الشهيد، فكل واحد منهما دم عبادة، وأثر عبادة وصف بالطيب، ففي الخلوف قوله "لخلوف فم الصائم أطيب عند الله من ريح المسك" وفي دم الشهيد قوله -صلى الله عليه وسلم-: "اللون لون الدم والريح ريح المسك" (ب). وما يكون محمودًا عند الله فسبيله الاستبقاء كما في دم الشهيد (جـ).
دليل القول الثالث: قالوا: لما فيه من تعريض الصوم للفساد بما يتحلل منه، ويسبب دخول الرطوبة (د).
دليل القول الرابع: قالوا: لأنه أبعد عن الرياء، والمستحب في النوافل الإخفاء، ولو ترك السواك لا يؤمن أن تظهر رائحة من فمه، فيظهر للناس أنه صائم (هـ).
دليل القول الخامس: قال: لما فيه من إدخال الماء في الفم (و).
والراجح: هو القول الأول. لعدم قيام دليل شرعي، يصلح أن يخصص عمومات نصوص السواك.
وللرد على أدلة المخالفين:
أولًا: الرد على أدلة القول الثاني:
فنقول: أما استدلالهم بقول النبي -صلى الله عليه وسلم-: "لخلوف فم الصائم ... ".
فالجواب عنه: ليس فيما روي دلالة على أنه لا يستاك، وإنما هو إخبار بحالة عند ربه؛ =
_________
(أ) رواه الطبراني في الكبير ٤/ ٦٩٦٣، والدارقطني ٢/ ٢٠٤، والبيهقي ٤/ ٢٧٤ وضعفاه. من طريق عبد الصمد، ثنا كيسان، عن عمرو بن عبد الرحمن عن خباب، عن النبي -صلى الله عليه وسلم- ...
(ب) رواه البخاري ١/ ٩٣ كتاب الوضوء باب ما يقع من النجاسات في السمن والماء رقم ٢٣٥.
(جـ) مغني المحتاج ١/ ٥٦، الحاوي الكبير ٣/ ٤٦٧، المجموع ١/ ٢٧٩، الشرح الكبير لابن قدامة ١/ ١٣٠، المغني ١/ ١١٠، المبدع ١/ ٩٩.
(د) الذخيرة ١/ ٥٠٨، حاشية الدسوقي ١/ ٥٣٤، جواهر الإكليل ١/ ١٥٢.
(هـ) المجموع ١/ ٢٧٩.
(و) تبيين الحقائق ١/ ٣٣٢، العناية ٢/ ٣٤٨.','3','170',NULL,NULL,'0');
INSERT INTO [b46] VALUES (846,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
=  ولأن الخلوف لا يزول بالسواك؛ لأنه من المعدة لا من الفم، إذ لو كان من الفم؛ لوجب أن يمتنع قبله؛ لأن تعاهده بالسواك قبله يمنع وجوده بعده، والسواك لا يزيل الخلوف، وإنما يزيل وسخ الأسنان. ومدح الخلوف يدل على فضليته، لا على أفضليته على غيره، إذ لا يلزم من ذكر ثواب العمل، أن يكون أفضل من غيره؛ لأنه لا يلزم من ذكر الفضيلة حصول الرجحان بالأفضلية، فالوتر أفضل من ركعتي الفجر مع قوله عليه الصلاة والسلام: "ركعتا الفجر، خير من الدنيا، وما عليها" (أ). وكم من عبادة أثنى الشرع عليها مع فضل غيرها؟ عليها وهذه المسألة: من قاعدة ازدحام المصالح، التي يتعذر الجمع بينها، فالسواك لإجلال الرب تعالى حالة خطابه في الصلاة؛ لأن تطهير الأفواه؛ لمخاطبة العظماء؛ تعظيمًا لهم، والخلوف مناف لذلك، فلا تعظيم، ولا إجلال. فكيف يقال: إن فضيلة الخلوف، تربو على تعظيم ذي الجلال بتطييب الأفواه؟ وأحاديث السواك، عامة في جميع الصلوات، وليس في حديث الخلوف ما يخصصه (ب). ولعدم سلامة الاستدلال بهذا الحديث، قال عنه ابن حجر: وفي الاستدلال به نظر (جـ).
وأما الحديث الذي استدلوا به وهو قول النبي -صلى الله عليه وسلم-: "إذا صمتم، فاستاكوا بالغداة، ولا تستاكوا بالعشي ... " فضعيف، ضعفه البيهقي، والدارقطني. ففيه أبو عمر القصاب كيسان، وهو ليس بالقوي (د). ولكون الحديث ضعيفًا، فلا حجة فيه. قال ابن الهمام: الحديث فإنه مع شذوذه ضعيف (هـ).
وأما قياسهم الخلوف على دم الشهيد، فالجواب عليه: نقول: هذا قياس مع الفارق.
فالخلوف أثر العبادة، واللائق به الإخفاء؛ فرارًا عن الرياء، بخلاف دم الشهيد، فإنه أثر الظلم، فيبقى عليه، ليكون شهيدًا له على خصمه يوم القيامة فأما الصوم فبينه، وبين ربه، =
_________
(أ) رواه مسلم ١/ ٥٠١ كتاب صلاة المسافرين وقصرها، باب استحباب ركعتي سنة الفجر رقم ٧٢٥.
(ب) قواعد الأحكام للعز بن عبد السلام ١/ ٣٢، شرح فتح القدير ٢/ ٣٤٨، العناية ٢/ ٣٤٨، تبيين الحقائق ١/ ٣٣٢، الذخيرة ٢/ ٥٠٨.
(جـ) التلخيص الكبير ١/ ٦١.
(د) السنن الكبرى للبيهقي ٤/ ٢٧٤، سنن الدارقطني ٢/ ٢٠٤.
(هـ) شرح فتح القدير ٢/ ٣٤٨.','3','171',NULL,NULL,'0');
INSERT INTO [b46] VALUES (847,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
=  فلا حاجة إلى الشاهد.
وأيضًا: فالشهيد غير مناج لربه؛ ولأن جرحه أشد من الدم، فلا يؤثر زواله، بل بقاؤه يوجب من ربه الرحمة له (أ). ولوجود الفارق بينهما. قال شيخ الإسلام ابن تيمية: وقياسه على دم الشهيد، ونحوه، ضعيف (ب).
هذا، وإن النصوص الواردة في فضل السواك، جاءت عامة من غير تفريق، بما قبل الزوال، أو بعده، ومن فرق فعليه الدليل، ولا دليل على التفريق (جـ).
وللرد على دليل القول الثالث: وهو قولهم: إن التسوك بالسواك الرطب للصائم، فيه تعريض الصوم للفساد بما يتحلل به.
فنقول: إن هذا شيء يسير معفو عنه، وليس تعريضًا للصوم بالفساد. ومنقوض أيضًا: بالمضمضة، والاستنشاق للصائم، بما يبقى في فم الصائم من الماء (د).
وللرد على دليل القول الرابع: نقول: إن التفريق بين صوم الفرض، والنفل في كراهته في الفرائض، دون النوافل، تفريق من غير دليل، والنصوص جاءت عامة في النفل والفرض، ولا يجوز التفريق بين حال وآخر، إلا بدليل، لا بمجرد الرأي، ولا دليل يدل على التفريق بين صوم الفرض، والنفل (هـ).
وللرد على دليل القول الخامس: نقول: إن كراهة السواك المبلول، بحجة إدخال الماء في الفم ممنوع، فما يبقى من الرطوبة بعد المضمضة، أكثر مما يبقى في فمه من البلل بعد السواك، ثم لم تكره المضمضة للصائم، فكذا السواك (و). فلا حاجة إلى الشاهد.
ولسلامة أدلة القول: الأول، وللأجوبة على أدلة المخالفين له، ترجح الأخذ به، وهو =
_________
(أ) تبيين الحقائق ١/ ٣٣٢، حاشية الشلبي ١/ ٣٣٢، الذخيرة ٢/ ٥٠٩.
(ب) فتاوى شيخ الإسلام ابن تيمية ٢٥/ ٢٦٦.
(جـ) فتاوى شيخ الإسلام ابن تيمية ٢٥/ ٢٦٦.
(د) الحاوي الكبير ٣/ ٤٦٧.
(هـ) فتاوى شيخ الإسلام ابن تيمية ٢٥/ ٢٦٦.
(و) تبيين الحقائق ١/ ٣٣٢، حاشية الشلبي ١/ ٣٣٢، العناية ٢/ ٣٤٨.','3','172',NULL,NULL,'0');
INSERT INTO [b46] VALUES (848,'ولا الفصد، ولا الحجامة.
<hr><span data-type="title">منحة السلوك</span>

<span data-type="title" id=toc-434>[الفصد والحجامة للصائم]</span>
قوله: ولا الفصد (¬١)، ولا الحجامة (¬٢)
أي: ولا يكره للصائم الفصد، ولا الحجامة (¬٣)؛ لما روي: "أنه -صلى الله عليه وسلم- احتجم وهو محرم، واحتجم وهو صائم" رواه البخاري، وغيره (¬٤). وما روى
_________
= اختيار المجد ابن تيمية (أ)، وشيخ الإسلام ابن تيمية (ب)، والشوكاني (جـ). وقال -أي الشوكاني-: فالحق: أنه يستحب السواك للصائم، أول النهار، وآخره.
(¬١) الفصد: شق العرق حتى يسيل، بقصد العلاج.
لسان العرب ٣/ ٣٣٦ مادة فصد، القاموس المحيط ٣/ ٤٩٥ مادة ف ص د، معجم مقاييس اللغة ٤/ ٥٠٧ باب الفاء والصاد وما يثلثهما مادة فصد، القاموس الفقهي ص ٢٨٦.
(¬٢) الحَجْمُ: المص، والحجّام: المصّاص، وحرفته وفعله الحجامة. وهي: التشريط، ومص الدَّمِ بزجاجة، ونحوها.
لسان العرب ١٢/ ١١٦ مادة حجم، القاموس المحيط ١/ ٥٩٧ مادة ح ج م، مختار الصحاح ص ٥٣ مادة ح ج م، الدر النقي ٣/ ٣٥٩، المصباح المنير ١/ ١٢٣ مادة حجمه.
(¬٣) وإليه ذهب المالكية.
وعند الشافعية: يكره للصائم الفصد، والحجامة.
وعند الحنابلة: تفسد الصوم.
تحفة الفقهاء ١/ ٣٦٨، بدائع الصنائع ٢/ ١٠٧، تبيين الحقائق ١/ ٣٣٢، منح الجليل ٢/ ١٢٤، الشرح الكبير للدردير ١/ ٥١٨، متن الرسالة ص ٧٠، روضة الطالبين ٢/ ٣٥٧، مغني المحتاج ١/ ٤٣٧، حاشية المقنع ١/ ٣٦٦، المحرر ١/ ٢٢٩.
(¬٤) البخاري ٢/ ٦٨٥ كتاب الصوم، باب الحجامة والقيء للصائم رقم ١٨٣٦، ورواه أيضًا الطحاوي في الشرح ١/ ٣٥٠، وابن ماجه ١/ ٥٣٧ كتاب الصيام، باب ما جاء في الحجامة للصائم رقم ١٦٨٢، وأبو داود ٢/ ٧٧٣ كتاب الصوم باب في الرخصة في ذلك رقم ٢٣٧٢، والترمذي ٢/ ١٣٧ كتاب الصوم، باب ما جاء في الرخصة في ذلك رقم ٧٧٢، =
_________
(أ) المحرر ١/ ١٠.
(ب) فتاوى شيخ الإسلام ابن تيمية ٢٥/ ٢٦٦.
(جـ) نيل الأوطار ١/ ١٣٢.','3','173',NULL,NULL,'0');
INSERT INTO [b46] VALUES (849,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

الترمذي من قوله -صلى الله عليه وسلم-: "أفطر الحاجم، والمحجوم" (¬١). وإليه ذهب أحمد، فمنسوخ بما روينا.
_________
= والبيهقي ٤/ ٢٦٨ كتاب الصيام، باب ما يستدل به على نسخ الحديث.
عن ابن عباس -رضي الله عنهما- أن النبي -صلى الله عليه وسلم-: "احتجم وهو محرم، واحتجم وهو صائم".
(¬١) الترمذي ٣/ ١١٧ كتاب الصوم، باب ما جاء في كراهية الحجامة للصائم رقم ٧٧١. ورواه أيضًا عبد الرزاق ٤/ ٢١٠ كتاب الصيام، باب الحجامة للصائم رقم ٧٥٢٣، ورواه أحمد ٣/ ٤٦٥، وابن خزيمة ٣/ ٢٢٧ كتاب الصيام، باب ذكر البيان أن الحجامة تفطر الحاجم والمحجوم جميعًا رقم ١٩٦٤، والطبراني في الكبير ٤٢٥٧، والحاكم ١/ ٤٢٨ كتاب الصوم، والبيهقي ٤/ ٢٦٥ كتاب الصيام باب الحديث الذي روي في الإفطار بالحجامة من حديث رافع بن خديج. قال ابن خزيمة ٣/ ٢٢٧: فقد ثبت الخبر عن النبي -صلى الله عليه وسلم- أنه قال: "أفطر الحاجم والمحجوم". وسمعت العباس بن عبد العظيم العنبري يقول: سمعت علي بن عبد الله يقول: لا أعلم في: "أفطر الحاجم والمحجوم" حديثًا أصح من ذا. وقال الترمذي: حسن صحيح. وذكر عن أحمد أنه قال: أصح شيء في هذا الباب. وفي صحيح البخاري ٢/ ٦٨٥ كتاب الصوم، باب الحجامة والقيء للصائم، عن الحسن، عن غير واحد مرفوعًا: "أفطر الحاجم والمحجوم".','3','174',NULL,NULL,'0');
INSERT INTO [b46] VALUES (850,'<span data-type="title" id=toc-435>فصل</span>
المريض إذا خاف شِدَّة مرضه، أو تأَخُّر بُرئه أفطر، وقضى.
<hr><span data-type="title">منحة السلوك</span>

فصل
هذا الفصل في بيان العوارض (¬١).

<span data-type="title" id=toc-436>[صوم المريض]</span>
قوله: المريض إذا خاف شدة مرضه، أو تأخر برئه أفطر وقضى.
لأن ذلك قد يفضي إلى الهلاك، فيجب الاحتراز عنه (¬٢).
وطريق معرفته: الاجتهاد، فإذا غلب على ظنه أفطر، وكذا إذا أخبره طبيب حاذق (¬٣)
_________
(¬١) لما ذكر مسائل الصوم شرع في هذا الفصل في بيان وجوه الأعذار المبيحة للفطر في الصوم، وما يتعلق بها.
والأعذار المبيحة للفطر: المرض، والسفر، والحبل، والرضاع إذا أضر بها، أو بولدها، والكبر إذا لم يقدر عليه، والعطش الشديد، والجوع كذلك إذا خيف منهما الهلاك، أو نقصان العقل، كالأمة إذا ضعفت عن العمل وخشيت الهلاك بالصوم، وكذا الغازي إذا كان يعلم يقينًا أنه يقاتل العدو في شهر رمضان، ويخاف الضعف إن لم يفطر يفطر قبل الحرب، مسافرًا كان أو مقيمًا، والعمل الحثيث إذا خشي الهلاك، أو نقصان العقل، وقد جمعها بعضهم فأجمل قائلًا:
وعوارض الصوم التي قد تغتفر ... للمرء فيها الفطر تسع تستطر
مرض وإكراه وإرضاع سفر ... حبل جهاد جوعه عطش كبر
حاشية الشلبي ١/ ٣٣٣، شرح فتح القدير ٢/ ٣٥٠، العناية ٢/ ٣٥٠، مراقي الفلاح ص ٦٣٠.
(¬٢) وكذا عند الشافعية، والحنابلة.
المختار ١/ ١٣٤، الكتاب ١/ ١٦٩، بدائع الصنائع ٢/ ٩٤، شرح فتح القدير ٢/ ٣٥١، السراج الوهاج ص ١٤٣، إخلاص الناوي ١/ ٢٩٦، نيل المآرب ١/ ٢٧٦، المحرر ١/ ٢٢٨.
(¬٣) الحذق: القطع، يقال: حذق السكين الشيء إذا قطعه، ومنه الرجل الحاذق في صناعته، =','3','175',NULL,NULL,'0');
INSERT INTO [b46] VALUES (851,'والمسافر أفطر مطلقًا، وصومه أفضل، إن لم تنله مشقة.
<hr><span data-type="title">منحة السلوك</span>

عدل (¬١).
والصحيح الذي يخشى أن يمرض بالصوم، فهو كالمريض (¬٢). وكذا الأمة التي تخدم، إذا خافت الضعف، جاز أن تفطر، ثم تقضي (¬٣).
قوله: والمسافر أفطر مطلقًا.
أي: سواء خاف المرض، أو لم يخف؛ لأن عين السفر مشقة، وصومه أفضل عندنا إن لم تنله مشقة (¬٤)؛ لقوله تعالى: {وَأَنْ تَصُومُوا خَيْرٌ لَكُمْ} [البقرة: ١٨٤]، ولما روي عن أبي الدرداء -رضي الله عنه- (¬٥) قال: "خرجنا مع رسول الله
_________
= وهو الماهر، وذلك أنه يحذق الأمر، أي: يقطعه، ولا يدع فيه متعلقًا.
تاج العروس ٦/ ٣١٠ مادة حذق، لسان العرب ١٠/ ٤٠ مادة حذق، معجم مقاييس اللغة ٢/ ٣٨ باب الحاء والذال وما يثلثهما مادة حذق، المصباح المنير ١/ ١٢٦ مادة حذق.
(¬١) شرح فتح القدير ٢/ ٣٥١، تبيين الحقائق ١/ ٣٣٣.
(¬٢) وفاقًا للثلاثة.
تبيين الحقائق ١/ ٣٣٣، شرح فتح القدير ٢/ ٣٥٠، جواهر الإكليل ١/ ١٥٣، حاشية الدسوقي ٢/ ٥٣٥، مغني المحتاج ١/ ٤٣٧، شرح المحلي ٢/ ٦٤، زاد المستقنع ص ١٧٣، غاية المنتهى ٢/ ١٨٠.
(¬٣) تبيين الحقائق ١/ ٣٣٣، شرح فتح القدير ٢/ ٣٥٠.
(¬٤) وإليه ذهب المالكية، والشافعية.
الهداية ١/ ١٣٦، بدائع الصنائع ٢/ ٩٤، كنز الدقائق ١/ ٣٣، بداية المبتدي ١/ ١٣٦، تبيين الحقائق ١/ ٣٣٣، التفريع ١/ ٣٠٤، الكوكب الدري في مسائل الغوري (مخطوط) ق ٣١٥ النسخة الأصلية لدى مكتبة أحمد الثالث بتركيا تحت رقم ١٣٧٧، التلقين ص ٥٩، روضة الطالبين ٢/ ٣٧٠، مغني المحتاج ١/ ٢٧١، أسنى المطالب ١/ ٤٢٣.
(¬٥) هو عويمر بن مالك بن قيس بن أمية الأنصاري الخزرجي، أبو الدرداء، صحابي جليل. من الحكماء الفرسان، القضاة، كان قبل البعثة تاجرًا في المدينة، ثم انقطع للعبادة ولما ظهر =','3','176',NULL,NULL,'0');
INSERT INTO [b46] VALUES (852,'فإن ماتا في السفر والمرض فلا قضاء
<hr><span data-type="title">منحة السلوك</span>

- صلى الله عليه وسلم- في بعض غزواته في حر شديد، حتى إن أحدنا ليضع يده على رأسه من شدة الحر، ما فينا صائم، إلا رسول الله -صلى الله عليه وسلم-، وعبد الله بن رواحة" رواه البخاري ومسلم، وأبو داود (¬١).
فعُلم أن الصوم أفضل؛ لأنه اختيار رسول الله -صلى الله عليه وسلم-.
وقال الشافعي: الفطر أفضل (¬٢).
قوله: فإن ماتا في السفر والمرض.
أي: وإن مات المريض في مرضه، والمسافر في سفره، فلا قضاء
_________
= الإسلام اشتهر بالشجاعة والنسك، كان أحد الذين جمعوا القرآن حفظًا على عهد النبي -صلى الله عليه وسلم-، مات بالشام سنة ٣٢ هـ.
أسد الغابة ٦/ ٩٧، الإصابة ٣/ ٤٥، حلية الأولياء ١/ ٢٠٨، غاية النهاية ١/ ٦٠٦.
(¬١) البخاري ٢/ ٦٨٦ كتاب الصوم، باب إذا صام أيامًا من رمضان ثم سافر رقم ١٨٤٣، ومسلم ٢/ ٧٩٠ كتاب الصيام، باب التخيير في الصوم والفطر في السفر رقم ١١٢٢، وأبو داود ٢/ ٣١٧ كتاب الصوم، باب فيمن اختار الصيام رقم ٢٤٠٩.
(¬٢) مذهب الشافعية: أن الصوم أفضل من الفطر، كالحنفية، والمالكية.
قال في المجموع ٦/ ٢٦٥: "مذهبنا: أن صومه أفضل. وبه قال: حذيفة بن اليمان، وأنس بن مالك، وعثمان بن العاص، وعروة بن الزبير، والأسود بن يزيد، وأبو بكر ابن عبد الرحمن بن الحارث، وسعيد بن جبير، والنخعي، والفضيل بن عياض، ومالك، وأبو حنيفة، والثوري، وعبد الله بن المبارك، وأبو ثور، وآخرون".
وقال في المنهاج ١/ ٣٠٧: "والصوم أفضل من الفطر" اهـ.
وقال في مغني المحتاج ١/ ٢٧١: "والصوم -أي صوم رمضان- لمسافر سفرًا طويلًا، أفضل من الفطر؛ لما فيه من تبرئة الذمة، وعدم إخلاء الوقت عن العبادة؛ ولأنه الأكثر من فعله -صلى الله عليه وسلم-".
وقال في الوجيز ١/ ١٠٣: "والصوم أحب من الفطر في السفر".
وما ذكره المصنف من أن الفطر أفضل من الصوم، هو مذهب الحنابلة. وبه قال: ابن =','3','177',NULL,NULL,'0');
INSERT INTO [b46] VALUES (853,'عليهما، وإن صح المريض، أو أقام المسافر، ثم ماتا، وجب الإيصاء (¬١) بقدر ما أدركا.
<hr><span data-type="title">منحة السلوك</span>

عليهما؛ لأنهما لم يدركا عدة أيام أخر (¬٢).
قوله: وإن صح المريض، أو أقام المسافر، ثم ماتا، وجب الإيصاء بقدر ما أدركا (¬٣).
هذا فائدة لزوم القضاء بقدر صحة المريض، وإقامة المسافر (¬٤).
وإذا أوصى: يؤدي الوصي من ثلث ماله، لكل يوم مسكينًا، بقدر ما
_________
= عباس، وابن عمر، وابن المسيب، والشعبي، والأوزاعي، وإسحاق.
الروض المربع ص ١٧٣، مطالب أولي النهى ٢/ ١٨٠، الإفصاح ١/ ٢٤٧، المغني ٣/ ٩٠.
(¬١) في ب بزيادة: "بالطعام".
(¬٢) ولأنهما عذرا في الأداء، فلأن يعذرا في القضاء أولى، وهذا لأن وجوب القضاء فرع وجوب الأداء، فما يمنع وجوب الأصل يمنع وجوب الفرع.
الكتاب ١/ ١٧٠، المختار ١/ ١٣٤، تحفة الفقهاء ١/ ٣٥٩، الاختيار ١/ ١٣٤، العناية ٢/ ٣٥١، الكافي لابن عبد البر ص ١٢٢، الشرح الصغير ١/ ٢٥٣، التذكرة ص ٧٧، قليوبي ٢/ ٦٦، المبدع ٣/ ٤٨، حاشية ابن قاسم على الروض المربع ٣/ ٤٤١.
(¬٣) وعند المالكية: لو مات قبل أن يقضي تلك الأيام يستحب للورثة الإطعام عنه إذا فرط أن يوصي وليس ذلك عليهم بواجب.
وعند الشافعية: إن مات بعد التمكن من القضاء ولم يقض لم يصم عنه وليه في الجديد بل يُخرج من تركته لكل يوم مد طعام، وفي القديم: يجوز أن يصوم عنه وليه ويجوز له الإطعام.
وعند الحنابلة: إذا صحّ المريض أو أقام المسافر ثم ماتا بعد إمكان القضاء فالواجب أن يطعم عنه كل يوم مسكين والإطعام من رأس ماله أوصى به أو لا.
الكتاب ١/ ١٧٠، المختار ١/ ١٣٥، الكافي لابن عبد البر ص ١٢٢، القوانين الفقهية ص ٨٢، شرح المحلي على المنهاج ٢/ ٦٦، عميرة ٢/ ٦٦، الإقناع للحجاوي ٢/ ٣٣٥، الروض المربع ص ١٨٠.
(¬٤) العناية ٢/ ٣٥٢، تبيين الحقائق ١/ ٣٣٤.','3','178',NULL,NULL,'0');
INSERT INTO [b46] VALUES (854,'وقضاء رمضان إن شاء فرَّقه، وإن شاء تابعه، والتتابع أفضل، ولا فدية بتأخيره عن رمضان ثانٍ.
<hr><span data-type="title">منحة السلوك</span>

يجب في صدقة الفطر.
وإن لم يوص، وتبرع الورثة، جاز وإن لم يتبرعوا، لا يلزمهم الأداء، بل سقط في حكم الدنيا (¬١).

<span data-type="title" id=toc-437>[كيفية قضاء رمضان]</span>
قوله: وقضاء رمضان إن شاء فرقه، وإن شاء تابعه؛ لإطلاق النص، ولكن التتابع أفضل؛ للمسارعة في إسقاط الواجب (¬٢).
قوله: ولا فدية بتأخيره عن رمضان ثانٍ.
يعني: إذا أخر من عليه قضاء رمضان عن رمضان ثانٍ، لا يجب عليه الفدية؛ لأن الله تعالى أوجب القضاء خاصة لا الفدية، فلا يجوز زيادة الفدية (¬٣).
وقال الشافعي: عليه الفدية (¬٤).
_________
(¬١) تحفة الفقهاء ١/ ٣٦٠، العناية ٢/ ٣٥٢، شرح الوقاية ١/ ١٢١، كشف الحقائق ١/ ١٢١.
(¬٢) وفاقًا للثلاثة.
الهداية ١/ ١٣٧، تبيين الحقائق ١/ ٣٣٦، شرح الوقاية ١/ ١٢١، كشف الحقائق ١/ ١٢١، مراقي الفلاح ص ٦٣٣، الكافي لابن عبد البر ص ١٢٢، القوانين الفقهية ص ٨٢، روض الطالب ١/ ٤٢٣، أسنى المطالب ١/ ٤٢٣، الكافي لابن قدامة ١/ ٣٥٨، مختصر الخرقي ص ٥١.
(¬٣) لأن تأخير الأداء عن وقته لا يوجب الفدية، فتأخير القضاء وهو مطلق عن الوقت، أولى أن لا يوجبها.
تبيين الحقائق ١/ ٣٣٦، شرح فتح القدير ٢/ ٣٥٤، الكتاب ١/ ١٧٠، المختار ١/ ١٣٦، مراقي الفلاح ص ٦٣٣، الاختيار ١/ ١٣٦.
(¬٤) وكذا قال: مالك، وأحمد.
الشرح الكبير للدردير ١/ ٥٣٧، المعونة ١/ ٤٨٢، التفريع ١/ ٣٠٤، نهاية المحتاج =','3','179',NULL,NULL,'0');
INSERT INTO [b46] VALUES (855,'وللحامل والمرضع الإفطار خوفًا على ولديهما، أو أنفسهما، ولا فدية عليهما.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-438>[صوم الحامل والمرضع]</span>
قوله: وللحامل، والمرضع، الإفطار خوفًا على ولديهما، أو أنفسهما؛ دفعًا للحرج (¬١).
الحامل: هي التي في بطنها ولد (¬٢).
والمرضع: هي التي لها لبن ترضع الولد (¬٣).
قوله: ولا فدية عليهما.
أي: على الحامل، والمرضع؛ لأن الفدية بخلاف القياس في الشيخ، فلا يلحق به خلافه (¬٤) (¬٥).
وقال الشافعي: إذا خافت المرض على الولد فأفطرت، فعليها الفدية (¬٦).
_________
= ٣/ ١٩٥، هداية الغلام ص ٦٧، السلسبيل ١/ ٣٤٢، المقنع في شرح الخرقي ٢/ ٥٦٦.
(¬١) كالمسافر، والمريض.
الهداية ١/ ١٣٧، تبيين الحقائق ١/ ٣٣٦، الوقاية ١/ ١٢١، كشف الحقائق ١/ ١٢١.
(¬٢) لسان العرب ١١/ ٧٧١ مادة حمل، القاموس المحيط ١/ ٧١٢ مادة ح م ل، مختار الصحاح ص ٦٥ مادة ح ب ل، المصباح المنير ١/ ١٥١ مادة الحِمل، مجمل اللغة ص ١٨٥ باب الحاء والميم وما يثلثهما مادة حمل.
(¬٣) لسان العرب ٨/ ١٣٦ مادة رضع، القاموس المحيط ٢/ ٣٤٨ مادة ر ض ع، مختار الصحاح ص ١٠٣ مادة ر ض ع، مجمل اللغة ص ٢٨٥ باب الراء والضاد وما يثلثهما مادة رضع، المصباح المنير ١/ ٢٢٩ مادة رضيع.
(¬٤) في ص: "يلحقون به غيره".
(¬٥) الهداية ١/ ١٣٧، تبيين الحقائق ١/ ٣٣٦، الكتاب ١/ ١٧٠، المختار ١/ ١٣٦، كشف الحقائق ١/ ١٢١، الاختيار ١/ ١٣٦.
(¬٦) مع القضاء. وإليه ذهب الحنابلة.
وعند المالكية، قولان: قيل: تفطر ولا فدية، وقيل: تفطر وتفدي. =','3','180',NULL,NULL,'0');
INSERT INTO [b46] VALUES (856,'والشيخ العاجز عن الصوم يفطر، ويفدي عن كل يوم نصف صاع من بر، أو صاعًا من تمر، أو شعير. فإن قدر على الصوم بعد الفدية قضى.
ومن أوصى بقضاء رمضان أطعم عنه وليه كما مر
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-439>[حكم الشيخ العاجز عن الصوم]</span>
قوله: والشيخ العاجز عن الصوم يفطر، دفعًا للحرج، ويفدي (¬١)؛ لقوله تعالى: {وَعَلَى الَّذِينَ يُطِيقُونَهُ فِدْيَةٌ طَعَامُ مِسْكِينٍ} [البقرة: ١٨٤]. أي: لا يطيقونه.
قوله: فإن قدر على الصوم بعد الفدية قضى.
لأن شرط كون الفدية خلفًا عن الصوم في حقه دوام العجز، فلما قدر على الصوم انتفى شرط الخلفية. ومثل هذا لم يفعل في التيمم؛ لئلا يلزم الحرج بتضاعف الصلوات (¬٢).

<span data-type="title" id=toc-440>[الوصية بقضاء الصوم والصلاة]</span>
قوله: ومن أوصى بقضاء رمضان، أطعم عنه وليه (¬٣) كما مر (¬٤).
_________
= التاج والإكليل ٢/ ٤٤٧، القوانين الفقهية ص ٨٤، زاد المحتاج ١/ ٥٢٨، المنهاج ١/ ٥٢٨، المبدع ٣/ ١٦، المقنع ١/ ٣٦٢.
(¬١) الشيخ الفاني، هو الذي يفدي وحده، دون غيره ممن تقدم. وإليه ذهب الشافعية، والحنابلة.
وذهب المالكية: إلى أنه لا يفدي؛ لأنه مسوغ له الفطر كالمريض.
تحفة الفقهاء ١/ ٣٥٨، بدائع الصنائع ١/ ١٩٧، الوقاية ١/ ١٢١، نور الإيضاح ص ٦٣٥، كشف الحقائق ١/ ١٢١، المعونة ١/ ٤٧٩، القوانين ص ٨٤، التفريع ١/ ٣١٠، الوجيز ١/ ١٠٤، المهذب ١/ ١٧٨، زاد المستقنع ص ١٧٣، الإفصاح ١/ ٢٤٥.
(¬٢) وذهب الشافعية، والحنابلة: إلى أنه إن قدر على الصوم بعد الفطر، فإنه لا يقضي.
تحفة الفقهاء ١/ ٣٥٨، الهداية ١/ ١٣٧، شرح فتح القدير ٢/ ٣٥٧، العناية ٢/ ٣٥٧، مغني المحتاج ١/ ٤٤٠، أسنى المطالب ١/ ٤٢٨، غاية المنتهى ٢/ ١٨٠، مطالب أولي النهى ٢/ ١٨٠.
(¬٣) بداية المبتدي ١/ ١٣٧، كنز الدقائق ١/ ٣٣٤، الهداية ١/ ١٣٧.
(¬٤) في ٣/ ١١.','3','181',NULL,NULL,'0');
INSERT INTO [b46] VALUES (857,'وإن لم يوصِ لا يجب،
<hr><span data-type="title">منحة السلوك</span>

قوله: ويفدي عن كل يوم نصف صاع من بر، أو صاعًا من تمر، أو صاعًا من شعير (¬١).
وعند الشافعي: لكل يوم مد (¬٢).
قوله: وإن لم يوص لا يجب.
أي: لا يجب على الولي الإطعام. ومع هذا، لو أطعم جاز -إن شاء الله تعالى- (¬٣).
وعند الشافعي: يلزم عليه، أوصى أو لم يوص (¬٤).
وعلى هذا الخلاف: الزكاة، وصدقة الفطر (¬٥).
_________
(¬١) بداية المبتدي ١/ ١٣٧، مختصر الطحاوي ص ٥٤، شرح الزيادات لحسن بن منصور الأوزجندي (مخطوط) جـ ١ لوحة ٥٩/ ب النسخة الأصلية لدى مكتبة الأزهر تحت رقم ٤٤٢٦٥/ ٢٩٢٠، تبيين الحقائق ١/ ٣٣٤.
(¬٢) وكذا عند المالكية.
وعند الحنابلة: مقدار الإطعام: مد من بر، أو نصف صاع من غيره.
الشرح الصغير ١/ ٢٥٣، بلغة السالك ١/ ٢٥٣، متن أبي شجاع ص ٩٩، كفاية الأخيار ١/ ١٣٠، الروض المربع ص ١٧٣، شرح منتهى الإرادات ١/ ٤٤٣.
(¬٣) وكذا كفارة اليمين، والقتل. إذا تبرع بالإطعام والكسوة يجوز، ولا يجوز التبرع بالإعتاق؛ لما فيه من إلزام الولاء للميت بغير رضاه.
شرح الوقاية ١/ ١٢١، كشف الحقائق ١/ ١٢١، الهداية ١/ ١٣٧، مراقي الفلاح ص ٦٣٥، تبيين الحقائق ١/ ٣٣٥.
(¬٤) وإليه ذهب الحنابلة.
شرح المحلي على المنهاج ٢/ ٦٦، عميرة ٢/ ٦٦، كشاف القناع ٢/ ٣٣٥، الروض المربع ص ١٧٣.
(¬٥) الهداية ١/ ١٣٧، شرح فتح القدير ٢/ ٣٥٨، تبيين الحقائق ١/ ٣٣٥.','3','182',NULL,NULL,'0');
INSERT INTO [b46] VALUES (858,'والصلاة كالصوم، وكل صلاة كصوم يوم،
<hr><span data-type="title">منحة السلوك</span>

قوله: والصلاة كالصوم.
هذا استحسان. والقياس: أن لا تجوز الفدية عن الصلاة؛ لأن ما ثبت بخلاف القياس، فغيره لا يقاس عليه.
وجه الاستحسان: أن كلًا منهما عبادة بدنية لا تعلق لوجوبها، ولا لأدائها بالمال (¬١). والباقي يعرف في الأصول (¬٢).
قوله: وكل صلاة كصوم يوم.
في أن يؤدي عن كل صلاة، مثل ما يؤدي عن صوم كل يوم. وهذا هو الصحيح (¬٣).
_________
(¬١) فإن النص الوارد بالفداء في الصوم غير معقول المعنى، فالقياس أن يقتصر عليه، لكن النص الوارد فيه، يجوز أن يكون معلولًا بعلة مشتركة بينه وبين الصلاة، وإن كنا لا نعقله، والصلاة في نظير الصوم بل أهم، فأُمر بالفداء فيها احتياطًا.
العناية ٢/ ٣٦٠، شرح فتح القدير ٢/ ٣٦٠، تبيين الحقائق ١/ ٣٣٥، حاشية الشلبي ١/ ٣٣٥.
(¬٢) فالمماثلة قد ثبتت شرعًا بين الصوم والإطعام. والمماثلة بين الصلاة، والصوم ثابتة. ومثل مثل الشيء، جاز أن يكون مثلًا لذلك الشيء. وعلى تقدير ذلك: يجب الإفطار. وعلى تقدير عدمها لا يجب. فالاحتياط في الإيجاب. فإن كان الواقع ثبوت المماثلة، حصل المقصود والذي هو السقوط، وإلا كان برًا مبتدأ يصلح ماحيًا للسيئات. ولذا قال محمد فيه: يجزئه إن شاء الله تعالى من غير جزم، كما قال: في تبرع الوارث بالإطعام، بخلاف إيصائه به عن الصوم، فإنه جزم بالإجزاء.
العناية ٢/ ٣٦٠، تبيين الحقائق ٥/ ٣٣١، الهداية ١/ ١٣٧، كشف الحقائق ١/ ١٢١، حاشية الشلبي ١/ ٣٣٥.
(¬٣) لأن كل صلاة فرض على حدة فكان كصوم يوم.
شرح فتح القدير ٢/ ٣٦٠، الهداية ١/ ١٣٧، تبيين الحقائق ١/ ٣٣٥، حاشية الشلبي ١/ ٣٣٥.','3','183',NULL,NULL,'0');
INSERT INTO [b46] VALUES (859,'ولا يصوم عنه وليه، ولا يصلي.
<hr><span data-type="title">منحة السلوك</span>

وعن محمد بن مقاتل (¬١): تجب لصلوات يوم، نصف صاع (¬٢).
قوله: ولا يصوم عنه وليه، ولا يصلي.
يعني: إذا مات إنسان وعليه صوم، أو صلاة، ليس على وليه أن يصوم، ويصلي عنه (¬٣). خلافًا للشافعي (¬٤).
له ما روي عن عائشة -رضي الله عنها- أن رسول الله -صلى الله عليه وسلم- قال: "من مات وعليه صيام، صام عنه وليه" رواه البخاري، وأبو داود (¬٥).
_________
(¬١) هو محمد بن مقاتل الرازي، قاضي الري، من أصحاب محمد بن الحسن، من طبقة سليمان بن شعيب، وعلي بن معبد. حدث عن وكيع، وجرير، وأبي معاوية.
قال الذهبي في الميزان: تكلم فيه ولم يترك. توفي سنة ٢٤٨ هـ.
الجواهر المضية ٣/ ٣٧٢، تهذيب التهذيب ٩/ ٤٦٩، ميزان الاعتدال ٤/ ٤٧، لسان الميزان ٥/ ٣٨٨.
(¬٢) لأنها كصيام يوم، ثم رجع إلى أن كل صلاة كصوم يوم.
العناية ٢/ ٣٦٠، شرح فتح القدير ٢/ ٣٦٠، حاشية الشلبي على تبيين الحقائق ١/ ٣٣٥.
(¬٣) لأنه لا يصوم عنه في حالة الحياة، فكذا بعد الموت كالصلاة. وإليه ذهب المالكية.
تبيين الحقائق ١/ ٣٣٥، بداية المبتدي ١/ ١٣٧، الهداية ١/ ١٣٧، شرح الوقاية ١/ ١٢١، القوانين الفقهية ص ٨٢، الكافي لابن عبد البر ص ١٢٢.
(¬٤) الذي عند الشافعية في الجديد: لا يصام عنه. وفي القديم: يجوز أن يصام عنه، ويجوز الإطعام كما سبق. أما الصلاة فإنها لا تقضى عن الميت قولًا واحدًا عندهم. وعند الحنابلة: لا يصوم عنه وليه إلا في صوم النذر.
أسنى المطالب ١/ ٤٢٨، نهاية المحتاج ٣/ ١٨٩، التسهيل ص ٨٩، حاشية الروض المربع لابن قاسم ٣/ ٤٤١.
(¬٥) البخاري ٢/ ٦٩٠ كتاب الصيام، باب من مات وعليه صوم رقم ١٨٥١، وأبو داود ٢/ ٣١٥ كتاب الصوم، باب فيمن مات وعليه صيام رقم ٢٤٠٠.
من حديث محمد بن جعفر، عن عروة، عن عائشة -رضي الله عنها-.','3','184',NULL,NULL,'0');
INSERT INTO [b46] VALUES (860,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

ولنا قوله -صلى الله عليه وسلم-: "لا يصوم أحدكم عن أحد، ولا يصلي أحد عن أحد، ولكن يطعم عنه" رواه النسائي (¬١)، وعن ابن عباس وابن عمر -رضي الله عنهما- أنه -صلى الله عليه وسلم- قال: "من مات وعليه صوم شهر، فليطعم عنه مكان كل يوم مسكينًا" رواه ابن ماجه. قال القرطبي (¬٢) (¬٣): إسناده حسن (¬٤).
_________
(¬١) النسائي في السنن الكبرى ٢/ ١٧٥ كتاب الصيام، باب صوم الحي عن الميت رقم ٢٩١٨ وتمامه: "ولكن يطعم عنه مكان كل يوم مدًا من حنطة"، ورواه أيضًا الطحاوي في المشكل ٣/ ١٤١ باب بيان مشكل ما روي عن رسول الله -صلى الله عليه وسلم- في الواجب فيمن مات وعليه صيام هل هو صيام أو إطعام عنه، والبيهقي تعليقًا ٤/ ٢٥٧ كتاب الصيام، باب من قال يصوم عنه وليه.
كلهم عن ابن عباس -رضي الله عنهما- موقوفًا عليه وليس مرفوعًا.
قال ابن حجر في الدراية ١/ ٢٨٣: لم أجده مرفوعًا.
وقال في التلخيص الحبير ٢/ ٢٠٩: رواه النسائي في الكبرى بإسناد صحيح عن ابن عباس.
وقال في الجوهر النقي ٤/ ٢٥٧: إسناده عن ابن عباس على شرط الشيخين إلا محمد بن الأعلى فإنه على شرط مسلم.
(¬٢) هو أبو عبد الله محمد بن أحمد بن أحمد بن أبي بكر بن فرح الأنصاري الخزرجي القرطبي، المفسر، اشتهر بالصلاح والتعبد والزهد، ووفور العقل، رحل إلى المشرق واستقر في أسيوط وبها توفي سنة ٦٧١ هـ، من تصانيفه: الجامع لأحكام القرآن، التذكرة بأمور الآخرة، شرح التقصي.
الديباج المذهب ص ٣١٧، الأعلام ٦/ ٢١٨.
(¬٣) في شرح الموطأ. كذا في تبيين الحقائق ١/ ٣٣٥، حاشية الشلبي على تبيين الحقائق ١/ ٣٣٥.
(¬٤) أما ما رواه ابن عباس -رضي الله عنهما- فقد أخرجه النسائي في السنن الكبرى ٢/ ١٧٥ كتاب الصيام، باب صوم الحي عن الميت رقم ٢٩١٨، والبيهقي في السنن الكبرى ٤/ ٢٥٤ كتاب الصيام، باب من قال: إذا فرط في القضاء بعد الإمكان.
بلفظ: "لا يصلي أحد عن أحد، ولا يصوم أحد عن أحد، ولكن يطعم عنه مكان كل يوم مدًا من حنطة". قال ابن حجر في الدراية ١/ ٢٨٣: إسناده صحيح. =','3','185',NULL,NULL,'0');
INSERT INTO [b46] VALUES (861,'ومن أسلم، أو بلغ، أو طهرت، أو أفاق، أو قدم من سفر، أو بريء من مرض، أو أفطر خطأ، أو عمدًا، أمسك بقية يومه، تشبهًا بالصائمين،
<hr><span data-type="title">منحة السلوك</span>

والجواب عن حديث البخاري: أن المراد منه الطعام الذي يقوم مقام الصوم مجازًا (¬١)، بدليل ما روينا.

<span data-type="title" id=toc-441>[من يلزمهم إمساك بقية اليوم]</span>
قوله: ومن أسلم، أو بلغ، أو طهرت الحائض، أو أفاق، أو قدم من سفر، أو بريء من مرض، أو أفطر خطأ، أو عمدًا، أمسك بقية يومه؛ تشبهًا بالصائمين (¬٢).
_________
= وما رواه ابن عمر -رضي الله عنهما-: فقد أخرجه ابن ماجه، كما قال: المصنف ١/ ٥٥٨ كتاب الصيام، باب من مات وعليه صيام رمضان قد فرط فيه رقم ١٧٥٧، وابن خزيمة ٣/ ٢٧٣ كتاب الصيام، باب الإطعام عن الميت يموت وعليه صوم لكل يوم مسكينًا رقم ٢٠٥٦، والبيهقي في السنن الكبرى ٤/ ٢٥٤ كتاب الصيام، باب من قال إذا فرط في القضاء بعد الإمكان، والبغوي في شرح السنة ١٧٧٥، والترمذي في جامعه ٣/ ٦٩ كتاب الزكاة باب ما جاء من الكفارة رقم ٨١٧.
من طريق أشعث، عن محمد، عن نافع، عن ابن عمر -رضي الله عنهما- قال: قال رسول الله -صلى الله عليه وسلم- ... ".
قال الترمذي ٣/ ٦٩: حديث ابن عمر لا نعرفه مرفوعًا إلا من هذا الوجه، والصحيح عن ابن عمر -رضي الله عنهما- موقوف قوله.
قال البيهقي في معرفة السنن والآثار ٦/ ٣١١: قال أحمد: وحديث محمد بن عبد الرحمن ابن أبي ليلى، عن نافع، عن ابن عمر، عن النبي -صلى الله عليه وسلم- فيمن مات وعليه صوم رمضان قال: "يطعم عنه" لا يصح، ومحمد بن عبد الرحمن: كثير الوهم.
قال ابن حجر في التلخيص ٢/ ٢٠٩: قال الدارقطني: المحفوظ وقفه على ابن عمر -رضي الله عنهما-.
(¬١) العناية ٢/ ٣٦٠، تبيين الحقائق ١/ ٣٣٥، حاشية الشلبي ١/ ٣٣٥.
(¬٢) قضاء لحق الوقت بالتشبه. وإليه ذهب الحنابلة.
وذهب المالكية: إلى أنه لا يلزم المسافر والحائض خاصة، ويلزم الباقين الإمساك.
وعند الشافعية: لا يلزمهم الإمساك.
الهداية ١/ ١٣٨، تحفة الفقهاء ١/ ٣٦٤، تبيين الحقائق ١/ ٣٣٧، الكتاب ١/ ١٧٢، =','3','186',NULL,NULL,'0');
INSERT INTO [b46] VALUES (862,'بخلاف الحائض والنفساء في حال الصوم، ولو أكل فلا قضاء عليه؛ لترك التشبه.
<hr><span data-type="title">منحة السلوك</span>

واختلفوا في هذا الإمساك (¬١)، فقيل: مستحب (¬٢)، وقيل: واجب (¬٣).
وليس على الكافر الذي أسلم، والصبي الذي بلغ، قضاء ذلك اليوم (¬٤). خلافًا لزفر في الكافر الذي أسلم (¬٥).
قوله: بخلاف الحائض، والنفساء في حال الصوم.
يعني: الطاهرة إذا حاضت، أو نفست في أثناء الصوم، لا يلزمها إمساك بقية يومها؛ لتحقق المانع من التشبه (¬٦).
قوله: ولو أكل، فلا قضاء عليه.
أي: ولو أكل الكافر الذي أسلم، أو الصبي الذي بلغ ذلك اليوم الذي
_________
= المختار ١/ ١٣٥، الشرح الصغير ١/ ٢٤٢، التفريع ١/ ٣٠٩، المنهاج ١/ ٥٢٤، زاد المحتاج ١/ ٥٢٤، الإقناع للحجاوي ٢/ ٣٠٩، شرح منتهى الإرادات ١/ ٤٣٩.
(¬١) يعني: الإمساك في رمضان بعد ما أفطر.
حاشية الشلبي ١/ ٣٣٩.
(¬٢) واختاره محمد بن شجاع؛ لأنه مفطر، فكيف يجب عليه الكف عن المفطرات.
العناية ٢/ ٣٦٣، شرح فتح القدير ٢/ ٣٦٣.
(¬٣) وهو اختيار الإمام الصفار، والمرغيناني، والزيلعي، وغيرهم.
العناية ٢/ ٣٦٣، شرح فتح القدير ٢/ ٣٦٣، الهداية ١/ ١٣٨، تبيين الحقائق ١/ ٣٣٩.
(¬٤) بداية المبتدي ١/ ١٣٨، كنز الدقائق ١/ ٣٣٩، العناية ١/ ٣٦٣، شرح فتح القدير ١/ ٣٦٣، تبيين الحقائق ١/ ٣٣٩، الهداية ١/ ١٣٨.
(¬٥) حيث يرى: أنه إذا أسلم الكافر، يجب عليه قضاء ذلك اليوم؛ لأن إدراك جزء من الوقت بعد الإسلام، كإدراك كله، كما في حكم الصلاة.
الهداية ١/ ١٣٨، تبيين الحقائق ١/ ٣٣٩.
(¬٦) شرح فتح القدير ٢/ ٣٦٣، العناية ٢/ ٣٦٣، تبيين الحقائق ١/ ٣٣٩، كشف الحقائق ١/ ١٢١، الهداية ١/ ١٣٨.','3','187',NULL,NULL,'0');
INSERT INTO [b46] VALUES (863,'ومن سافر بعد الفجر ونوى الفطر ثم قدم، أو صح من مرضه قبل الزوال؛ لزمه الصوم، ولو أفطر فلا كفارة عليه.
وإذا علم المسافر أنه يدخل في يومه مصره، أو موضع إقامته،
<hr><span data-type="title">منحة السلوك</span>

أسلم وبلغ فيه، لا قضاء عليهما؛ لانعدام الأهلية من الأول بخلاف الصلاة؛ لأن سبب الوجوب الجزء المتصل بالأداء، وقد وجدت الأهلية عند ذلك الجزء (¬١). فافهم.

<span data-type="title" id=toc-442>[ما يترتب بتغير الحال]</span>
قوله: ومن سافر بعد الفجر ونوى الفطر، ثم قدم، أو صح. أي: المريض من مرضه قبل الزوال، لزمه الصوم؛ لزوال المانع (¬٢). ولو أفطر فلا كفارة عليه؛ للشبهة (¬٣).
قيد بقوله: "قبل الزوال"؛ لأنه إذا قدم، أو صح بعد الزوال، لا تجوز نيته للصوم على ما عرف. فافهم.
قوله: وإذا علم المسافر أنه يدخل في يومه مصره، أو موضع إقامته،
_________
(¬١) شرح فتح القدير ٢/ ٣٦٤، الهداية ١/ ١٣٨، تبيين الحقائق ١/ ٣٣٩، الكتاب ١/ ١٧٢، بداية المبتدي ١/ ١٣٨.
(¬٢) وإليه ذهب الشافعية، والحنابلة. من غير تحديد بما قبل الزوال وعليهما مع الإمساك القضاء.
وعند المالكية: لا يندب له الإمساك بقية اليوم.
كنز الدقائق ١/ ٣٤٠، بداية المبتدي ١/ ١٣٨، شرح الوقاية ١/ ١٢٢، تبيين الحقائق ١/ ٣٤٠، الهداية ١/ ١٣٨، كشف الحقائق ١/ ١٢٢، التفريع ١/ ٣٠٥، الشرح الصغير ١/ ٢٤٢، القوانين الفقهية ص ٨٢، إخلاص الناوي ١/ ٢٩٩، إرشاد الغاوي ١/ ٢٩٩، كشاف القناع ٢/ ٣٠٩، منتهى الإرادات/ ٤٤٠١.
(¬٣) وهو السفر في أوله، أو آخره، كما يسقط الحد بالنكاح الفاسد؛ للشبهة.
تبيين الحقائق ١/ ٣٤٠، شرح فتح القدير ٢/ ٣٦٥، العناية ٢/ ٣٦٥، حاشية الشلبي ١/ ٣٤٠.','3','188',NULL,NULL,'0');
INSERT INTO [b46] VALUES (864,'كره له الفطر.
ومن أغمي عليه، أو جُنَّ في رمضان، قضى ما بعد يوم الإغماء والجنون خاصة.
والجنون المستوعب مسقط للقضاء،
<hr><span data-type="title">منحة السلوك</span>

كره له الفطر.
لما أنه أعرض عن الصوم (¬١). وأما إذا علم أن دخول المصر لا يتفق له حتى تغيب الشمس فلا بأس بأن يفطر؛ لأنه مسافر فيه (¬٢).

<span data-type="title" id=toc-443>[أثر الجنون والإغماء في القضاء]</span>
قوله: ومن أغمي عليه، أو جن في رمضان، قضى ما بعد يوم الإغماء والجنون خاصة.
يعني: لا يقضي اليوم الذي حدث فيه الإغماء، والجنون؛ لأن صومه في ذلك اليوم صحيح، بناء على وجود النية منه ظاهرًا، وقضى ما بعد ذلك اليوم؛ لعدم النية فيه (¬٣).
قوله: والجنون المستوعب مسقط القضاء؛ دفعًا للحرج (¬٤).
_________
(¬١) حاشية الشلبي ١/ ٣٤٠، الفتاوى التتارخانية ٢/ ٣٨٥.
(¬٢) حاشية الشلبي على تبيين الحقائق ١/ ٣٤٠، الفتاوى التتارخانية ٢/ ٣٨٥.
(¬٣) وذهب المالكية، والشافعية، والحنابلة: إلى أن المغمى عليه يقضي اليوم الذي حدث فيه الإغماء، إن استغرق إغماؤه طوال اليوم. وعند المالكية: طواله أو جله.
أما المجنون فلا يقضي ما فاته زمن جنونه. عند الشافعية، والحنابلة. ويقضي عند المالكية.
بداية المبتدي ١/ ١٣٨، كنز الدقائق ١/ ٣٤٠، الهداية ١/ ١٣٨، تبيين الحقائق ١/ ٣٤١، الاختيار ١/ ١٣٥، الخرشي على خليل ٢/ ٢٤٨، حاشية العدوي ٢/ ٢٤٨، المعونة ١/ ٤٧٠، شرح المحلي ٢/ ٦٣، أسنى المطالب ١/ ٤٢٣، روضة الطالبين ٢/ ٣٦٦، كشاف القناع ٢/ ٣١٤، الروض المربع ص ١٧٤.
(¬٤) الاختيار ١/ ١٣٥، الهداية ١/ ١٣٨، شرح فتح القدير ٢/ ٣٦٦.','3','189',NULL,NULL,'0');
INSERT INTO [b46] VALUES (865,'بخلاف الإغماء، وبخلاف الجنون غير المستوعب.
ومن لم ينو في رمضان صومًا، ولا فطرًا، لزمه القضاء،
<hr><span data-type="title">منحة السلوك</span>

خلافًا لمالك (¬١).
قوله: بخلاف الإغماء.
أي: الإغماء إذا استوعب الشهر، لا يسقط القضاء؛ لأنه نوع مرض يضعف القوى، ولا يزيل الحِجا (¬٢) (¬٣).
وكذلك الجنون غير المستوعب لا يسقط القضاء؛ لعدم الحرج (¬٤).
قوله: ومن لم ينو في رمضان صومًا، ولا فطرًا، لزمه القضاء.
لأن المستحق عليه الإمساك بجهة العبادة، فلا يكون ذلك إلا بالنية (¬٥).
_________
(¬١) حيث يرى: أن المجنون يقضي ما فاته زمن جنونه؛ لأنه مسلم عرض له ما فيه انعقاد صومه، فلزمه قضاؤه عند زواله، كالحيض.
وذهب الشافعية، والحنابلة: إلى أن المجنون لا يقضي ما فاته في زمن جنونه، استوعب، أم لم يستوعب.
الخرشي على خليل ٢/ ٢٤٨، الشرح الصغير ١/ ٢٤٢، المعونة ١/ ٤٧٠، التفريع ١/ ٣٠٩، شرح المحلي ٢/ ٦٣، روض الطالب ١/ ٤٢٣، الإقناع للحجاوي ٢/ ٣١٤، الروض المربع ص ١٧٤.
(¬٢) الحجا: العقل، والفطنة.
لسان العرب ١٤/ ١٦٥ مادة حجا، القاموس المحيط ١/ ٥٩٨ مادة ح ج و، مجمل اللغة ص ١٩٧ باب الحاء والجيم وما يثلثهما مادة حجى.
(¬٣) العناية ٢/ ٣٦٦، الهداية ١/ ١٣٨، بدائع الصنائع ٢/ ٨٩، تبيين الحقائق ١/ ٣٤٠.
(¬٤) بدائع الصنائع ٢/ ٨٩، تبيين الحقائق ١/ ٣٤٠، الهداية ١/ ١٣٨، شرح فتح القدير ٢/ ٣٦٦.
(¬٥) وفاقًا للثلاثة.
بداية المبتدي ١/ ١٣٩، كنز الدقائق ١/ ٣٤١، تبيين الحقائق ١/ ٣٤١، المبسوط ٣/ ٨٦، شرح الزرقاني على خليل ٢/ ٢٠١، حاشية البناني على خليل ٢/ ٢٠١، التذكرة ص ٧٦، عمدة السالك وعدة الناسك ص ٢١٦، زاد المستقنع ص ١٧٤، نيل المآرب ١/ ٢٧٣.','3','190',NULL,NULL,'0');
INSERT INTO [b46] VALUES (866,'ومن أصبح غير ناوٍ للصوم، ونوى قبل الزوال، فأكل، فلا كفارة عليه.
والحائض، والنفساء، تفطر وتقضي، بخلاف الصلاة.
<hr><span data-type="title">منحة السلوك</span>

وقال زفر: لا يجب عليه القضاء (¬١).

<span data-type="title" id=toc-444>[أثر النية في الصوم]</span>
قوله: ومن أصبح غير ناوٍ للصوم، ونوى قبل الزوال، فأكل، فلا كفارة عليه؛ للشبهة هذا عند أبي حنيفة (¬٢).
وقالا: إن كان ذلك قبل الزوال فعليه القضاء والكفارة، وإن كان بعده فعليه القضاء لا الكفارة (¬٣).
وقال زفر: عليه الكفارة في الصورتين (¬٤).

<span data-type="title" id=toc-445>[صوم الحائض وصلاتها]</span>
قوله: والحائض والنفساء، تفطر وتقضي، بخلاف الصلاة.
يعني: لا يقضيان الصلاة، لما في قضائها من الحرج، بخلاف قضاء الصوم (¬٥).
_________
(¬١) لأن صوم رمضان يتأدى عنده بدون النية في حق الصحيح المقيم؛ لأن المستحق عليه هو الإمساك، وقد وجد، وهذا لأنه متعين بأصله ووصفه، فعلى أي وجه أتى به وقع عنه، كما إذا وهب كل النصاب من الفقير.
بدائع الصنائع ٢/ ٨٣، تبيين الحقائق ١/ ٣٤١، الهداية ١/ ١٣٩.
(¬٢) الهداية ١/ ١٣٩، المبسوط ٣/ ٨٧، تبيين الحقائق ١/ ٣٤١، حاشية الشلبي ١/ ٣٤١، بدائع الصنائع ٢/ ١٠٠.
(¬٣) الهداية ١/ ١٣٩، بدائع الصنائع ٢/ ١٠٠، تبيين الحقائق ١/ ٣٤١، المبسوط ٣/ ٨٧.
(¬٤) وذهب المالكية، والشافعية، والحنابلة: إلى من لم يُبيِّت النية من الليل، فعليه قضاء ذلك اليوم ولا كفارة عليه.
الهداية ١/ ١٣٩، تبيين الحقائق ١/ ٣٤١، بدائع الصنائع ٢/ ١٠٠، المبسوط ٣/ ٨٧، الشرح الصغير ١/ ٢٤٤، بلغة السالك ١/ ٢٤٤، أنوار المسالك ص ٢١٦، السراج الوهاج ص ١٣٧، المقنع ١/ ٣٦٣، حاشية المقنع ١/ ٣٦٣، الإفصاح ١/ ٢٣٣.
(¬٥) وفاقًا للثلاثة. =','3','191',NULL,NULL,'0');
INSERT INTO [b46] VALUES (867,'ومن ظن بقاء الليل فتسحَّر، أو غروب الشمس فأفطر، وبان خطؤه، لزمه القضاء والتشبه لا غير.
ولو شك في طلوع الفجر، فالأفضل أن لا
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-446>[أثر الظن والشك في الصوم]</span>
قوله: ومن ظن بقاء الليل فتسحَّر، أو غروب الشمس فأفطر، وبان خطوه. بأن ظهر، أن الفجر طالع، والشمس لم تغرب، لزمه القضاء؛ لأنه مضمون عليه بالمثل، ولزمه التشبه؛ موافقة للصائمين (¬١).
قوله: لا غير. يعني: لا تجب الكفارة؛ لقصور الجناية، لعدم القصد (¬٢).
وذكر في "المستصفى": أن المراد من الظن غلبة الظن، حتى لو كان شاكًا تجب الكفارة (¬٣).
قوله: ولو شك في طلوع الفجر بأنه طلع، أو لا، فالأفضل له أن لا
_________
= الهداية ١/ ١٣٩، مختصر الطحاوي ص ٥٤، الهداية ١/ ١٣٩، بداية المبتدي ١/ ١٣٩، المعونة ١/ ٤٧٥، منح الجليل ٢/ ١٢٩، تحفة المحتاج ٣/ ٤٣٢، حاشية الشرواني ٣/ ٤٣٢، العمدة لابن قدامة ص ٣١، منار السبيل ١/ ٢٢١.
(¬١) بدائع الصنائع ٢/ ١٠٠، تبيين الحقائق ١/ ٣٤٢، الكتاب ١/ ١٧٤، بداية المبتدي ١/ ١٣٩، الهداية ١/ ١٣٩.
(¬٢) وفاقًا للثلاثة.
قال في الإفصاح ١/ ٢٣٧: واتفقوا على أنه إذا أكل وهو يظن أن الشمس قد غابت، أو أن الفجر لم يطلع، فبان الأمر بخلاف ذلك، أنه يجب عليه القضاء.
بدائع الصنائع ٢/ ١٠٠، تبيين الحقائق ١/ ٣٤٢، القوانين ص ٧٨، المعونة ١/ ٤٧٢، الهداية ١/ ١٣٩، منح الجليل ٢/ ١٣٤، الوجيز ١/ ١٠٢، التنبيه ص ٦٦، زاد المستقنع ص ١٧٦، شرح الزركشي ٢/ ٥٩٩.
(¬٣) المستصفى شرح الفقه النافع لعبد الله بن أحمد النسفي (مخطوط) ق ٥٤/ أالنسخة الأصلية لدى المكتبة الظاهرية تحت رقم ٢: ١٧٠ الفقه الحنفي. ونصه: "والمراد أنه يظن ظنًا غالبًا قريبًا إلى اليقين، حتى لو كان شاكًا، أو كان أكبر رأيه أنها لم تغرب، تجب الكفارة".','3','192',NULL,NULL,'0');
INSERT INTO [b46] VALUES (868,'يفطر، ولو أفطر فلا قضاء عليه. ولو شك في غروب الشمس، يجب أن لا يفطر، ولو أفطر لزمه القضاء لا غير.
<hr><span data-type="title">منحة السلوك</span>

يفطر؛ تحرزًا عن المحرم، ولو أفطر فلا قضاء عليه؛ لأن الأصل هو الليل، فلا يخرج بالشك إلا إذا تبين أنه أكل بعد ما طلع الفجر، فحينئذ يجب عليه القضاء، لا غير (¬١).
قوله: ولو شك في غروب الشمس، بأنها غربت، أو لا، يجب أن لا يفطر؛ تحرزًا عن إفساد الصوم، ولو أفطر لزمه القضاء (¬٢). وفي الكفارة: روايتان (¬٣). وإن تبين أنه أكل قبل الغروب تجب عليه الكفارة (¬٤).
_________
(¬١) المبسوط ٣/ ٧٧، العناية ١/ ٣٧٥، تبيين الحقائق ١/ ٣٤٢، شرح فتح القدير ١/ ٣٧٥، الهداية ١/ ١٣٩.
(¬٢) شرح فتح القدير ٢/ ٣٧٥، تبيين الحقائق ١/ ٣٤٢، العناية ١/ ٣٧٥.
(¬٣) وذهب المالكية، والشافعية: إلى أنه إذا شك في طلوع الفجر، أو في الغروب، فعليه القضاء، ولا كفارة.
وعند الحنابلة: إن أكل شاكًا في طلوع الفجر صح صومه، وإن أكل شاكًا في غروب الشمس، فعليه القضاء، ولا كفارة.
الهداية ١/ ١٤٠، العناية ٢/ ٣٧٥، تبيين الحقائق ١/ ٣٤٢، شرح فتح القدير ١/ ٣٧٥، الشرح الصغير ١/ ٢٤٧، بلغة السالك ١/ ٢٤٧، المجموع ٦/ ٣٠٦، رحمة الأمة ١/ ١١٧، الروض المربع ص ١٧٦، التسهيل ص ٨٨.
(¬٤) وفاقًا للثلاثة. قال في شرح فتح القدير ١/ ٣٧٥: فإن ظهر أنه أكل قبل الغروب، فعليه الكفارة، لا أعلم فيه خلافًا.
تحفة الفقهاء ١/ ٣٦٥، بدائع الصنائع ٢/ ١٠٥، العناية ١/ ٣٧٥، حاشية الشلبي ١/ ٣٤٢، تبيين الحقائق ١/ ٣٤٣، المبسوط ٣/ ٧٧، منح الجليل ٢/ ١١٨، القوانين الفقهية ص ٧٨، الحاوي الكبير ٣/ ٤٤٤، المجموع ٦/ ٣٦٠، غاية المنتهى ٢/ ٢٠٦، منتهى الإرادات ١/ ٤٥٥.','3','193',NULL,NULL,'0');
INSERT INTO [b46] VALUES (869,'والسحور مستحب، وكذا تأخيره،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-447>[حكم السحور]</span>
قوله: والسحور مستحب (¬١).
وقيل: سنة (¬٢)؛ لقوله -صلى الله عليه وسلم-: "إن فصل ما بين صيامنا، وصيام أهل الكتاب أكلة السحر" (¬٣) ويروى "السحور" رواه الجماعة، إلا البخاري، وابن ماجه (¬٤). وقال -صلى الله عليه وسلم-: "تسحروا فإن في السحور بركة" رواه البخاري ومسلم (¬٥).
والسحور -بفتح السين-: اسم ما يؤكل وقت السحر (¬٦).
قوله: وكذا تأخيره.
أي: وكذا يستحب تأخير السحور (¬٧)؛ لما روى أبو ذر -رضي الله عنه- أنه -صلى الله عليه وسلم-
_________
(¬١) بداية المبتدي ١/ ١٤٠، تبيين الحقائق ١/ ٣٤٣، الهداية ١/ ١٤٠.
(¬٢) الهداية ١/ ١٤٠، شرح فتح القدير ٢/ ٣٧٥، تبيين الحقائق ١/ ٣٤٢، حاشية الشلبي ١/ ٣٤٢.
(¬٣) قال النووي في المجموع ٦/ ٣٦١: السحر -بفتح السين-: هي السحور.
(¬٤) رواه مسلم ٢/ ٧٧١ كتاب الصيام، باب فضل السحور، وتأكيد استحبابه، واستحباب تأخيره، وتعجيل الفطر رقم ١٠٩٦، وأبو داود ٢/ ٣٠٢ كتاب الصوم، باب في توكيد السحور رقم ٢٣٤٣، والترمذي ٣/ ٦١ كتاب الزكاة، باب ما جاء في فضل السحور رقم ٧٠٩، والنسائي ٤/ ١٤٦ كتاب الصيام، باب فصل ما بين صيامنا وصيام أهل الكتاب رقم ٢١٦٦، وأحمد ٤/ ١٩٧.
عن عمرو بن العاص -رضي الله عنه- كلهم بلفظ: "أكلة السحر" إلا النسائي: بلفظ "أكلة السحور".
(¬٥) البخاري ٢/ ٦٧٨ كتاب الصوم باب بركة السحور من غير إيجاب رقم ١٨٢٣، ومسلم ٢/ ٧٧٠ كتاب الصيام باب فضل السحور رقم ١٠٩٥.
من حديث أنس بن مالك -رضي الله عنه-.
(¬٦) لسان العرب ٤/ ٣٥١ مادة سحر، القاموس المحيط ٢/ ٥٢٨ مادة س ح ر، أنيس الفقهاء ص ١٣٥، المطلع على أبواب المقنع ص ١٥٠.
(¬٧) وفاقًا للثلاثة. =','3','194',NULL,NULL,'0');
INSERT INTO [b46] VALUES (870,'ويستحب تعجيل الإفطار.
<hr><span data-type="title">منحة السلوك</span>

كان يقول: "لا تزال أمتي بخير، ما أخروا السحور، وعجلوا الفطر" رواه أحمد (¬١).
قوله: ويستحب تعجيل الإفطار.
لما روينا (¬٢)، ولما روي عن سهل بن سعد -رضي الله عنه- (¬٣) أن النبي -صلى الله عليه وسلم- قال: "لا يزال الناس بخير ما عجلوا الفطر" متفق عليه (¬٤)، وعن أنس -رضي الله عنه- أنه
_________
= المبسوط ٣/ ٧٧، بدائع الصنائع ٢/ ١٠٥، تبيين الحقائق ١/ ٢٤٣، مختصر خليل ص ٧٠، منح الجليل ٢/ ١١٨، التذكرة ص ٧٦، متن أبي شجاع ص ٩٦، نيل المآرب ١/ ٢٧٤، السلسبيل ١١/ ٣٤٠.
(¬١) رواه الإمام أحمد في المسند ٥/ ١٤٧ من طريق سالم بن غيلان، عن سليمان بن أبي عثمان، عن عدي بن حاتم الحمصي، عن أبي ذر -رضي الله عنه- مرفوعًا.
قال في مجمع الزوائد ٣/ ١٥٤: وفيه سليمان بن أبي عثمان قال أبو حاتم: مجهول. اهـ.
ورمز له السيوطي في الجامع الصغير بالصحة ص ٥٧٩ رقم ٩٧٧١.
وقد رواه البخاري ٢/ ٦٩٢ كتاب الصوم، باب تعجيل الإفطار رقم ١٨٥٦، ومسلم ٢/ ٧٧١ كتاب الصيام، باب فضل السحور وتأكيد استحبابه رقم ١٠٩٨، عن سهل بن سعد -رضي الله عنه- بلفظ: "لا يزال الناس بخير ما عجلوا الفطر".
(¬٢) وفاقًا للثلاثة.
بدائع الصنائع ٢/ ١٠٥، تبيين الحقائق ١/ ٣٤٣، بداية المبتدي ١/ ١٤٠، الهداية ١/ ١٤٠، مختصر خليل ص ٧٠، التاج والإكليل ٢/ ٣٩٧، كفاية الأخيار ١/ ١٢٨، هداية الغلام ص ٦٧، نيل المآرب ١/ ٢٧٤، السلسبيل ١/ ٣٤٠.
(¬٣) هو سهل بن سعد بن مالك الأنصاري الخزرجي الساعدي، له ولأبيه صحبة، روى له الستة في كتبهم، كان اسمه حزنًا، فسماه رسول الله -صلى الله عليه وسلم- سهلًا، مات سنة ٨٨، وقيل: ٩١ هـ. وقد جاوز المائة.
سير أعلام النبلاء ٢/ ٤٢٢، المعرفة والتاريخ ١/ ٣٣٨، هجرة أنساب العرب ٣٦٦، أسد الغابة ٢/ ٤٧٢، الإصابة ٢/ ٨٨.
(¬٤) البخاري ٢/ ٦٩٢ كتاب الصوم، باب تعجيل الإفطار رقم ١٨٥٦، ومسلم ٢/ ٧٧١ كتاب =','3','195',NULL,NULL,'0');
INSERT INTO [b46] VALUES (871,'ومن أكل ناسيًا، فظن أنه أفطر، أو علم أنه لم يفطر، فأكل عمدًا، لزمه القضاء لا غير (¬١).
<hr><span data-type="title">منحة السلوك</span>

- صلى الله عليه وسلم- "كان يفطر على رطبات قبل أن يصلي، فإن لم تكن رطبات فتميرات، فإن لم تكن تمرات حسا حسوات من ماء" رواه أحمد، وأبو داود، والترمذي (¬٢).

<span data-type="title" id=toc-448>[تعمد الأكل بعد النسيان]</span>
قوله: ومن أكل ناسيًا فظن أنه أفطر، أو علم أنه لم يفطر، فأكل عمدًا؛ لزمه القضاء لا غير.
يعني: لا الكفارة؛ لتحقق الشبهة (¬٣).
ولو احتجم فظن أنه يفطره، فأكل متعمدًا، فعليه القضاء والكفارة (¬٤).
_________
= الصيام، باب فضل السحور وتأكيد استحبابه رقم ١٠٩٨.
(¬١) في د بزيادة "بخلاف الاحتجام".
(¬٢) أحمد ٣/ ١٦٤، وأبو داود ٢/ ٣٠٦ كتاب الصوم، باب ما يفطر عليه رقم ٢٣٥٦، والترمذي ٣/ ٥٤ كتاب الزكاة، باب ما يستحب عليه الإفطار رقم ٦٩٦.
من طريق جعفر بن سليمان، عن ثابت، عن أنس -رضي الله عنه- ..
ورواه أيضًا الدارقطني ٢/ ١٨٥ كتاب الصيام باب القبلة للصائم رقم ٢٤، والحاكم ١/ ٤٣٢ كتاب الصوم، والبيهقي ٤/ ٢٣٩ كتاب الصيام، باب ما يفطر عليه، والضياء في المختارة ١/ ٤٩٥.
قال الدارقطني في السنن ٢/ ١٨٥: هذا إسناد صحيح.
وقال الترمذي: هذا حديث حسن غريب.
وقال الحاكم: صحيح على شرط مسلم وسكت عنه الذهبي.
(¬٣) وذهب المالكية، والشافعية، والحنابلة: إلى أن من أكل، أو شرب ناسيًا فلا قضاء عليه.
مختصر الطحاوي ص ٥٧، تحفة الفقهاء ١/ ٣٦٣، الهداية ١/ ١٤٠، الشرح الكبير للدردير ١/ ٥٣١، الخرشي على خليل ٢/ ٢٥٦، فتح الوهاب ٢/ ٣٣٤، حاشية الجمل على المنهج ٢/ ٣٣٤، مطالب أولي النهى ٢/ ١٩٣، المقنع ١/ ٣٦٦.
(¬٤) تحفة الفقهاء ١/ ٣٦٣، بداية المبتدي ١/ ١٤٠، الهداية ١/ ١٤٠، مختصر الطحاوي ص ٥٧.','3','196',NULL,NULL,'0');
INSERT INTO [b46] VALUES (872,'ويحرم صوم يوم العيدين، وأيام التشريق.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-449>[الأيام المحرم صومها]</span>
قوله: ويحرم صوم يوم العيدين (¬١).
لما روي أنه -صلى الله عليه وسلم- "نهى عن صيام يومين، يوم الفطر، ويوم الأضحى" رواه مالك في الموطأ وأبو داود في السنن (¬٢).
قوله: وأيام التشريق.
أي: يحرم صوم أيام التشريق أيضًا، وهي ثلاثة أيام بعد عيد الأضحى (¬٣)؛
_________
(¬١) وفاقًا للثلاثة.
تبيين الحقائق ١/ ٣٤٦، الاختيار ١/ ١٢٥، الوقاية ١/ ١٢٣، المختار ١/ ١٢٥، كنز الدقائق ١/ ٣٤٦، كشف الحقائق ١/ ١٢٣، شرح الزرقاني على خليل ٢/ ٢١٧، حاشية البناني ٢/ ٢١٧، أنوار المسالك ص ٢٢٤، متن أبي شجاع ص ٩٧، المحرر ١/ ٢٣١، زاد المستقنع ص ١٨٢.
(¬٢) مالك في الموطأ ١/ ٣٠٠ كتاب الصيام، باب صيام يوم الفطر والأضحى والدهر رقم ٣٦ عن أبي هريرة -رضي الله عنه-، وأبو داود ٢/ ٣١٩ كتاب الصوم، باب في صوم العيدين رقم ٢٤١٧ عن أبي سعيد الخدري -رضي الله عنه-. ورواه البخاري ٢/ ٧٠٢ كتاب الصوم باب الصوم يوم النحر رقم ١٨٩١، ومسلم ٢/ ٧٩٩ كتاب الصيام باب النهي عن صوم يوم الفطر ويوم الأضحى رقم ١١٣٨ كلاهما -أي البخاري ومسلم- عن أبي هريرة -رضي الله عنه-.
(¬٣) وإليه ذهب الشافعية.
وذهب المالكية، والحنابلة: إلى حرمة صيام أيام التشريق، إلا عن دم متعة، أو قران، فيصح صومها لمن عدم الهدي. ورخص المالكية في صوم ثالث أيام التشريق في النذر، والكفارات.
المختار ١/ ١٢٥، كنز الدقائق ١/ ٣٤٦، كشف الحقائق ١/ ١٢٣، الاختيار ١/ ١٢٥، شرح الوقاية ١/ ١٢٣، شرح الزرقاني على خليل ٢/ ٢١٧، حاشية البناني ٢/ ٢١٧، القوانين الفقهية ص ٧٨، الشرح الصغير ١/ ٢٥٤، كفاية الأخيار ١/ ١٢٨، الحاوي الكبير ٣/ ٤٧٧، هداية الغلام ص ٦٧، الكافي لابن قدامة ١/ ٣٦٤، المبدع ٣/ ٥٦، الروض المربع ص ١٨٢، المستوعب ٣/ ٤٥٢، شرح منتهى الإرادات ١/ ٤٦١.','3','197',NULL,NULL,'0');
INSERT INTO [b46] VALUES (873,'ولا يكره صوم الستة من شوال موصولة برمضان.
ويكره صوم الوصال،
<hr><span data-type="title">منحة السلوك</span>

لورود النهي فيها (¬١).
قوله: ولا يكره صوم الستة من شوال موصولة برمضان (¬٢).
لقوله -صلى الله عليه وسلم-: "من صام رمضان، ثم أتبعه بست من شوال، فكأنما صام الدهر" رواه أبو داود، وابن ماجه، وفي روايته "كان كصوم الدهر" (¬٣).

<span data-type="title" id=toc-450>[حكم الوصال]</span>
قوله: ويكره صوم الوصال (¬٤).
_________
(¬١) كما جاء في صحيح البخاري ٢/ ٧٠٣ كتاب الصوم، باب صيام أيام التشريق رقم ١٨٩٤.
عن عائشة وابن عمر -رضي الله عنهما- قالا: "لم يرخص في أيام التشريق أن يصمن إلا لمن لم يجد الهدي".
(¬٢) وفاقًا للشافعية، والحنابلة.
وذهب المالكية: إلى كراهة صيام ست من شوال، مخافة أن يلحق أهل الجهالة برمضان ما ليس منه.
بدائع الصنائع ٢/ ٧٨، شرح فتح القدير ٢/ ٣٤٩، الاختيار ١/ ١٢٥، تبيين الحقائق ١/ ٣٤٦، شرح الوقاية ١/ ١٢٣، مواهب الجليل ٢/ ٤١٤، التاج والإكليل ٢/ ٤١٤، منح الجليل ٢/ ١٢١، شرح المحلي على المنهاج ٢/ ٧٣، قليوبي ٢/ ٧٣، المحرر ١/ ٢٣١، الهداية لأبي الخطاب ١/ ٨٦.
(¬٣) أبو داود ٢/ ٣٢٤ كتاب الصوم، باب في صوم ستة أيام من شوال رقم ٢٤٣٣ بلفظ " ... فكأنما صام الدهر"، وابن ماجه ١/ ٥٤٧ كتاب الصيام باب صيام ستة أيام من شوال رقم ١٧١٦ بلفظ: " ... كان كصوم الدهر".
ورواه أيضًا مسلم في الصحيح ٢/ ٨٢٢ كتاب الصيام، باب استحباب ستة أيام من شوال إتباعًا لرمضان رقم ١١٦٤ بلفظ: "من صام رمضان، ثم أتبعه ستًا من شوال، كان كصيام الدهر". من حديث أبي أيوب الأنصاري -رضي الله عنه-.
(¬٤) وذهب المالكية: إلى أنه يندب صوم الدهر إذا أفطر الأيام المنهي عن صيامها، ولم =','3','198',NULL,NULL,'0');
INSERT INTO [b46] VALUES (874,'فإن أفطر في الأيام الخمسة المحرمة فقولان.
<hr><span data-type="title">منحة السلوك</span>

وهو أن يصوم أيامًا لا يفطر بينها (¬١)؛ لما روي عن ابن عمر -رضي الله عنهما- أن رسول الله -صلى الله عليه وسلم- "نهى عن الوصال قالوا: فإنك تواصل يا رسول الله، قال: إني لست كهيئتكم، إني أُطعَم وأُسقى" رواه أبو داود (¬٢).
قوله: فإن أفطر في الأيام الخمسة المحرمة.
وهي: يوما العيدين، وأيام التشريق فقولان في كراهة الوصال: في قول: يكره (¬٣). وفي قول: لا يكره؛ للفاصل الذي يخرجه من الوصل المنهي عنه (¬٤).
_________
= يضعفه عن عمل من أعمال البر.
وعند الشافعية: صوم الدهر غير العيد، والتشريق، مكروه لمن خاف به ضررًا، أو فوت حق. ومستحب لغيره.
وعند الحنابلة. يكره صيام الدهر، فإن أدخل فيها صيام الأيام المنهي عنها، حرم.
تحفة الفقهاء ١/ ٣٤٤، بدائع الصنائع ٢/ ٧٩، الاختيار ١/ ١٢٥، تبيين الحقائق ١/ ٣٤٦، حاشية الشلبي ١/ ٣٣٢، التاج والإكليل ٢/ ٤٤٣، المعونة ١/ ٤٨٧، جواهر الإكليل ١/ ١٥٣، الشرح الكبير للدردير ١/ ٥٣٤، السراج الوهاج ص ١٤٧، إخلاص الناوي ١/ ٣٠٣، المغني ٣/ ١٠٧، الفروع ٣/ ١١٤.
(¬١) لسان العرب ١١/ ٧٢٨ مادة وصل، المصباح المنير ٢/ ٦٦٢ مادة وصلت، مختار الصحاح ص ٣٠٢ مادة وص ل، القاموس الفقهي ص ٣٨٠.
(¬٢) ٢/ ٣٠٦ كتاب الصوم، باب الوصال رقم ٢٣٦٠، ورواه أيضًا البخاري ٢/ ٦٩٣ كتاب الصوم، باب الوصال رقم ١٨٦١، ومسلم ٢/ ٧٧٤ كتاب الصيام، باب النهي عن الوصال في الصوم رقم ١١٠٢.
(¬٣) بدائع الصنائع ٢/ ٧٩، شرح فتح القدير ٢/ ٣٤٩، الفتاوى التتارخانية ٢/ ٣٨٩.
(¬٤) بدائع الصنائع ٢/ ٧٩، شرح فتح القدير ٢/ ٣٤٩، الفتاوى التتارخانية ٢/ ٣٨٩، حاشية الشبلي ١/ ٣٣٢.','3','199',NULL,NULL,'0');
INSERT INTO [b46] VALUES (875,'ويكره صوم الصمت، وهو: أن لا يتكلم في صومه، ويكره صوم يوم السبت، أو عاشوراء وحده.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-451>[ما يكره صومه]</span>
قوله: ويكره صوم الصمت، وهو أن لا يتكلم في صومه؛ لأن صوم الصمت من فعل المجوس -لعنهم الله- (¬١)، وقال الإمام حميد الدين الضرير (¬٢): إنما يكره الصمت إذا اعتقده قربة، أما إذا لم يعتقده قربة فلا يكره؛ لقوله -صلى الله عليه وسلم-: "من صمت نجا" (¬٣).
قوله: ويكره صوم يوم السبت (¬٤)، أو عاشوراء وحده؛ لما في ذلك من
_________
(¬١) شرح فتح القدير ٢/ ٣٥٠، تحفة الفقهاء ١/ ٣٤٣، الفتاوى التتارخانية ٢/ ٣٨٩.
(¬٢) هو علي بن محمد بن علي الرامشيي البخاري، نجم العلماء، الملقب بحميد الملة والدين الضرير، من علماء الحنفية، كان إمامًا، فقيهًا، أصوليًا، محدثًا، متقنًا، انتهت إليه رئاسة العلم بما وراء النهر. توفي سنة ٦٦٦ هـ. من تصانيفه: الفوائد، شرح المنظومة النسفية، شرح الجامع الكبير.
تابع التراجم ص ٢١٥، الجواهر المضية ٢/ ٥٩٨، الفوائد البهية ١٢٥، كشف الظنون ٢/ ٢٠٣٢.
(¬٣) رواه أحمد ٢/ ١٥٩، والدارمي ٢/ ٧٥٥ كتاب الرقاق، باب في الصمت رقم ٢٦١٣، والترمذي ٧/ ١٩٢ كتاب صفة القيامة، باب إكرام الضيف، وقول الخير من الإيمان رقم ٢٥٠٣، وابن المبارك في الزهد ٣٨٥، وعبد الله بن وهب في الجامع ٤٩، وابن شاهين في الترغيب ١٠٧، وأبو الشيخ في الأمثال ٢٠٧، والقضاعي في مسند الشهاب ١/ ٢١٩ رقم ٣٣٤.
من طريق ابن لهيعة، عن يزيد بن عمرو المعافري، عن أبي عبد الرحمن الحنبلي، عن عبد الله بن عمرو بن العاص -رضي الله عنهما- قال: قال رسول الله -صلى الله عليه وسلم- ....
قال الترمذي ٧/ ١٩٢: هذا حديث غريب لا نعرفه إلا من حديث ابن لهيعة.
وقال النووي في الأذكار ص ٤٨١: إسناده ضعيف.
وقال في تمييز الطيب من الخبيث ص ١٨٦ رقم ١٤١١: ومداره على ابن لهيعة.
ورمز له السيوطي في الجامع الصغير ٢/ ٥٣٣ رقم ٨٨١٩ بالضعف.
(¬٤) وفاقًا للثلاثة. =','3','200',NULL,NULL,'0');
INSERT INTO [b46] VALUES (876,'ويستحب صوم يوم الخميس،
<hr><span data-type="title">منحة السلوك</span>

التشبه باليهود (¬١).

<span data-type="title" id=toc-452>[ما يستحب صومه]</span>
قوله: ويستحب يوم الخميس (¬٢).
أي: صوم يوم الخميس؛ لأن النبي -صلى الله عليه وسلم- "كان يصوم يوم الاثنين، ويوم الخميس" وسئل عن ذلك فقال: "إن أعمال العباد تعرض يوم الاثنين، ويوم الخميس" رواه أبو داود (¬٣)، وعن أبي هريرة -رضي الله عنه- أن النبي -صلى الله عليه وسلم- "كان
_________
= شرح فتح القدير ٢/ ٣٥٠، تحفة الفقهاء ١/ ٣٤٤، الفتاوى التتارخانية ٢/ ٣٨٩، حاشية الشلبي ١/ ٣٣٢، القوانين الفقهية ص ٧٨، المنهاج ١/ ٥٣٥، مغني المحتاج ١/ ٤٤٦، دليل الطالب ١/ ٢٣٠، نيل المراد ص ٨٠.
(¬١) وإليه ذهب الشافعية.
وذهب المالكية، والحنابلة: إلى أنه لا يكره صوم عاشوراء وحده.
شرح فتح القدير ٢/ ٣٥٠، تحفة الفقهاء ١/ ٣٤٤، الفتاوى التتارخانية ٢/ ٣٨٩، بدائع الصنائع ٢/ ٧٩، الذخيرة ٢/ ٥٢٩، الشرح الكبير للدردير ١/ ٥١٦، الكافي لابن عبد البر ص ١٢٩، مغني المحتاج ١/ ٤٤٦، المنهاج ١/ ٥٣٥، حاشية المقنع ١/ ٣٧٨، المبدع ٣/ ٥٢.
(¬٢) وفاقًا للثلاثة. بدائع الصنائع ٢/ ٧٩، تحفة الفقهاء ١/ ٣٤٤، الفتاوى التتارخانية ٢/ ٣٨٩، أقرب المسالك ص ٤٢، الشرح الصغير ١/ ٢٤٣، أنوار المسالك ص ٢٢٣، الوجيز ١/ ١٠٥، منار السبيل ١/ ٢٢٩، العمدة ص ٣٣.
(¬٣) ٢/ ٣٢٥ كتاب الصوم، باب في صوم الاثنين والخميس ٢٤٣٦، ورواه الطيالسي ص ٨٧ رقم ٣٦٢، وابن سعد في الطبقات ٤/ ١، وأحمد ٥/ ٢٠٠، والدارمي ١/ ٤٤٥ كتاب الصوم، باب في صيام يوم الاثنين والخميس رقم ١٦٩٩، وابن خزيمة ٢١١٩ كتاب الصيام، باب في استحباب صوم يوم الاثنين والخميس أيضًا؛ لأن الأعمال فيهما تعرض على الله عز وجل ٣/ ٢٩٩، والنسائي كتاب الصيام، باب صوم النبي بأبي هو وأمي، وذكر اختلاف الناقلين للخبر في ذلك ٤/ ٢٠١ رقم ٢٣٥٨، والبيهقي ٤/ ٢٩٣ كتاب الصيام، باب صوم الاثنين والخميس. =','3','201',NULL,NULL,'0');
INSERT INTO [b46] VALUES (877,'والجمعة،
<hr><span data-type="title">منحة السلوك</span>

يصوم الاثنين، والخميس. فقيل: يا رسول الله إنك تصوم الاثنين والخميس، فقال: إن يوم الاثنين، والخميس يغفر الله فيهما لكل مسلم، إلا مهتجرين، يقول: دعوهما حتى يصطلحا" رواه ابن ماجه (¬١).
قوله: والجمعة.
أي: يستحب صوم يوم الجمعة (¬٢).
قال في الإيضاح: لا بأس بصوم يوم الجمعة في قول: أبي حنيفة، ومحمد. وقال أبو يوسف: قد جاء حديث في كراهته إلا أن يصوم قبله يومًا، وبعده يومًا (¬٣). وهو ما روي عن النبي -صلى الله عليه وسلم- أنه قال: "لا يصم أحدكم يوم الجمعة، إلا أن يصوم قبله بيومٍ، أو بعده" رواه مسلم وأبو داود (¬٤).
_________
= قال المنذري في مختصر السنن ٣/ ٣٢٠: وهو حديث حسن.
عن أسامة بن زيد -رضي الله عنه- أن النبي -صلى الله عليه وسلم-: كان يصوم يوم الاثنين والخميس، وسئل عن ذلك فقال: "إن أعمال العباد تعرض يوم الاثنين ويوم الخميس".
(¬١) ١/ ٥٥٣ كتاب الصيام، باب صيام يوم الاثنين والخميس رقم ١٧٤٠، ورواه أيضًا مسلم ٤/ ١٩٨٧ كتاب البر والصلة باب النهي عن الشحناء والتهاجر رقم ٢٥٦٥.
(¬٢) وإليه ذهب المالكية.
تحفة الفقهاء ١/ ٣٤٤، الفتاوى التتارخانية ٢/ ٣٨٩، حاشية الشلبي ١/ ٣٣٢، الشرح الكبير للدردير ١/ ٥٣٤، حاشية الدسوقي ١/ ٥٣٤، جواهر الإكليل ١/ ١٥٣.
(¬٣) وإليه ذهب الشافعية، والحنابلة.
الفتاوى التتارخانية ٢/ ٣٨٩، حاشية الشلبي ١/ ٣٣٢، المنهاج ١/ ٥٣٥، مغني المحتاج ١/ ٤٤٦، غاية المنتهى ٢/ ٢١٩، التسهيل ص ٩٠.
(¬٤) مسلم ٢/ ٨٠١ كتاب الصيام، باب كراهة صيام يوم الجمعة منفردًا رقم ١١٤٤، وأبو داود ٢/ ٣٢٠ كتاب الصوم، باب النهي أن يخص يوم الجمعة بصوم رقم ٢٤٢٠، ورواه أيضًا البخاري ٤/ ٢٣٢ كتاب الصوم، باب صوم يوم الجمعة رقم ١٩٨٥ عن أبي هريرة -رضي الله عنه- قال: سمعت رسول الله -صلى الله عليه وسلم- يقول .... ".','3','202',NULL,NULL,'0');
INSERT INTO [b46] VALUES (878,'وأيام البيض،
<hr><span data-type="title">منحة السلوك</span>

قوله: وأيام البيض.
أي: يستحب صوم أيام البيض. وهي: الثالث عشر، والرابع عشر، والخامس عشر (¬١).
سميت بيضًا؛ لأن لياليها مقمرة من أول الليل إلى آخره (¬٢).
لما روي عن ابن ملحان (¬٣) القيسي، عن أبيه (¬٤) قال: "كان رسول الله -صلى الله عليه وسلم- يأمرنا أن نصوم البيض، ثلاث عشرة، وأربع عشرة، وخمس عشرة،
_________
(¬١) وفاقًا للشافعية، والحنابلة.
وعند المالكية: يندب صوم ثلاثة أيام من كل شهر، وكره كونها الأيام البيض؛ فرارًا من التحديد فيما لم يحدده الشارع، فإن اتفق صومها بلا قصد، فلا كراهة.
بدائع الصنائع ٢/ ٧٨، تحفة الفقهاء ١/ ٣٤٣، شرح فتح القدير ٢/ ٣٥٠، شرح مقدمة الغزنوي لأبي البقاء محمد بن أحمد بن الضياء (مخطوط) لوحة ١٩٤/ أالنسخة الأصلية لدى مكتبة مكة برقم ١٧ فقه حنفي، جواهر الإكليل ١/ ١٤٧، مختصر خليل ص ٧١، عمدة السالك ص ٢٢٣، التذكرة ص ٧٨، مطالب أولي النهى ٢/ ٢١٣، مختصر الخرقي ص ٥٢، جوهر النظام في علمي الأديان والأحكام ص ١١٧.
(¬٢) لسان العرب ٧/ ١٢٤ مادة بيض، القاموس المحيط ١/ ٣٥٠ مادة ب ي ض، المصباح المنير ١/ ٦٨ مادة البياض، لغة الفقه ص ١٢٩، المطلع على أبواب المقنع ص ١٥١، الدر النقي ٢/ ٣٦٨.
(¬٣) هو عبد الملك بن قتادة بن ملحان القيسي، روى عن أبيه، وعنه أنس بن سيرين، ولم يرو عنه غيره، وليس له في الكتب الستة إلا هذا الحديث.
تهذيب الكمال ٣٤/ ٤٧٦، تهذيب التهذيب ٦/ ٤١٣، ميزان الاعتدال ٢/ ٦٦١.
(¬٤) هو قتادة بن ملحان القيسي، له صحبة، يعد في البصريين، تفرد بالرواية عنه ابنه عبد الملك بن قتادة. ليس له عن النبي -صلى الله عليه وسلم- سوى هذا الحديث.
الإصابة ٣/ ٢٢٥، الاستيعاب ٢/ ٢٥١ خلاصة تذهيب تهذيب الكمال ص ٣١٥، تهذيب الكمال ٢٣/ ٥٢٠.','3','203',NULL,NULL,'0');
INSERT INTO [b46] VALUES (879,'ويوم عرفة لغير الحاج.
<hr><span data-type="title">منحة السلوك</span>

قال: وقال: هن كهيئة الدهر" رواه أبو داود وابن ماجه (¬١).
قوله: ويوم عرفة.
أي: يستحب صوم يوم عرفة (¬٢)؛ لقوله -صلى الله عليه وسلم-: "صيام يوم عرفة أحتسب على الله أن يكفر السنة التي قبله، والسنة التي بعده" رواه مسلم (¬٣).
قوله: لغير الحاج.
قيد به: لأن صوم يوم عرفة للحاج مكروه (¬٤)؛ لما روي أنه -صلى الله عليه وسلم- "نهى
_________
(¬١) أبو داود ٢/ ٣٢٨ كتاب الصوم، باب في صوم الثلاث من كل شهر رقم ٢٤٤٩، وابن ماجه ١/ ٥٤٥ كتاب الصيام، باب ما جاء في صيام ثلاثة أيام من كل شهر رقم ١٧٠٧، والطيالسي ص ١٧٠ رقم ١٢٢٥، وأحمد ٥/ ٢٨، والنسائي ٤/ ٢٢٤ كتاب الصيام، باب ذكر الاختلاف على موسى بن طلحة في الخبر في صيام ثلاثة أيام من كل شهر رقم ٢٤٣٠، وابن حبان بترتيب ابن بلبان ٨/ ٤١١ كتاب الصوم، باب ذكر تفضل الله بكتبة صائمي البيض لهم أجر صوم الدهر رقم ٣٦٥١، والبيهقي ٤/ ٢٩٤ كتاب الصيام، باب من أي الشهر يصوم هذه الأيام الثلاثة، والطبراني في المعجم الكبير ١٩/ ٢٤.
لفظ ابن ماجه: "كان يأمر بصيام البيض، ثلاث عشرة، وأربع عشرة، وخمس عشرة، ويقول: هو كصوم الدهر، أو كهيئة صوم الدهر".
من طريق همام، عن أنس بن سيرين، عن عبد الملك بن قتادة بن ملحان القيسي، عن أبيه قال: كان رسول الله -صلى الله عليه وسلم- ... ".
قال في المجموع ٦/ ٣٨٥: رواه أبو داود، والنسائي، وابن ماجه، بإسناد فيه مجهول.
يشير إلى عبد الملك بن قتادة فقد قال عنه في التقريب ص ٣٦٤ رقم ٤٢٠٣: مقبول.
(¬٢) الفتاوى التتارخانية ٢/ ٣٨٩، تحفة الفقهاء ١/ ٣٤٣، بدائع الصنائع ٢/ ٧٩، جوهر النظام في علمي الأديان والأحكام ص ١١٧، حاشية الشلبي ١/ ٣٣٢.
(¬٣) ٢/ ٨١٨ كتاب الصيام، باب استحباب صيام ثلاثة أيام من كل شهر، وصوم عرفة، وعاشوراء، والاثنين، والخميس رقم ١١٦٢ عن أبي قتادة -رضي الله عنه-.
(¬٤) وفاقًا للمالكية، والشافعية، والحنابلة. =','3','204',NULL,NULL,'0');
INSERT INTO [b46] VALUES (880,'ولا تصوم المرأة تطوعًا بغير إذن زوجها، إلا أن يكون صائمًا،
<hr><span data-type="title">منحة السلوك</span>

عن صوم يوم عرفة" رواه أبو داود، وابن ماجه (¬١).

<span data-type="title" id=toc-453>[صيام المتزوجة والعبد]</span>
قوله: ولا تصوم المرأة تطوعًا بغير إذن زوجها (¬٢).
لقوله -صلى الله عليه وسلم-: "لا تصوم امرأة وبعلها شاهد إلا بإذنه، غير رمضان" رواه أبو داود (¬٣).
قوله: إلا أن يكون صائمًا.
_________
= تحفة الفقهاء ١/ ٣٤٣، بدائع الصنائع ٢/ ٧٩، الفتاوى التتارخانية ٢/ ٣٨٩، حاشية الشلبي ١/ ٣٣٢، منح الجليل ١/ ١١٩، شرح الزرقاني على خليل ٢/ ١٩٧، المنهاج ١/ ٤٤٦، الوجيز ١/ ١٠٥، المقنع ١/ ٣٧٨، الهداية لأبي الخطاب ١/ ٨٦.
(¬١) أبو داود ٢/ ٣٢٦ كتاب الصوم، باب في صوم عرفة بعرفة رقم الحديث ٢٤٤٠، وابن ماجه ١/ ٥٥١ كتاب الصيام باب صيام يوم عرفة رقم الحديث ١٧٣٢، وابن حزم في المحلى ٧/ ١٨، وأحمد ٢/ ٣٠٤، وابن أبي شيبة في مصنفه ٣/ ١٩٥ كتاب الحج في صوم يوم عرفة بمكة رقم الحديث ١٣٣٨٥، والبيهقي في السنن الكبرى ٤/ ٢٨٤ كتاب الصيام، باب الاختيار للحاج في ترك صوم يوم عرفة، والحاكم في المستدرك ١/ ٤٣٤ كتاب الصوم، والنسائي في الكبرى ٢/ ١٥٥ كتاب الصيام، باب النهي عن صوم يوم عرفة بعرفة رقم ٢٨٣٠. من طريق حوشب بن عقيل، عن مهدي الهجري، عن عكرمة، عن أبي هريرة -رضي الله عنه-.
قال الحاكم في المستدرك ١/ ٣٣٤: هذا حديث صحيح على شرط البخاري، ولم يخرجاه. وقال في المجموع ٦/ ٣٨٠: رواه أبو داود والنسائي بإسناد فيه مجهول.
يشير إلى مهدي الهجري فقد قال عنه في التقريب: مقبول ص ٥٤٨ رقم ٦٩٢٨.
(¬٢) شرح فتح القدير ٢/ ٣٥٠ حاشية الشلبي ١/ ٣٣٢.
(¬٣) ٢/ ٣٣٠ كتاب الصوم، باب المرأة تصوم بغير إذن زوجها رقم ٢٤٥٨، ورواه أيضًا البخاري ٥/ ١٩٩٣ كتاب النكاح، باب صوم المرأة باذن زوجها تطوعًا رقم ٤٨٩٦، ومسلم ٢/ ٧١١ كتاب الزكاة، باب ما أنفق العبد من مال مولاه رقم ١٠٢٦.
عن أبي هريرة -رضي الله عنه- بلفظ: "لا تصوم المرأة، وبعلها شاهد إلا بإذنه".','3','205',NULL,NULL,'0');
INSERT INTO [b46] VALUES (881,'ولا العبد بغير إذن مولاه وإن كان لا يضر بمولاه.
و<span data-type="title" id=toc-454>كفارة صوم رمضان:</span> عتق رقبة، فإن لم يجد فصيام شهرين
<hr><span data-type="title">منحة السلوك</span>

أي: إلا أن يكون الزوج صائمًا، أو مريضًا، فحينئذ تتطوع المرأة بغير إذنه؛ لأن النهي لحاجة الزوج، ولا حاجة في تلك الصورتين (¬١).
قوله: ولا العبد.
أي: ولا يصوم العبد أيضًا تطوعًا بغير إذن مولاه، وإن كان صومه لا يضر بمولاه (¬٢). يعني: لعدم ضعفه بسبب الصوم.
وكذلك المدبر، وأم الولد، لا يصومان بغير إذن مولاهما، وإن كان صومهما لا يضره (¬٣).

[كفارة صوم رمضان]
قوله: وكفارة صوم رمضان عتق رقبة إلى آخره (¬٤).
_________
(¬١) وعند المالكية: ليس لمرأة يحتاج لها زوج تطوع بلا إذنه، فإن علمت أنه لا يحتاج لها جاز لها التطوع بلا إذنه.
وذهب الشافعية، والحنابلة: إلى أن المرأة لا تصوم تطوعًا، وزوجها حاضر بغير إذنه.
شرح فتح القدير ٢/ ٣٥٠، حاشية الشلبي ١/ ٣٣٢، الشرح الكبير للدردير ١/ ٥٤١، الشرح الصغير ١/ ٢٥٤، أسنى المطالب ١/ ٤٣٣، المجموع ٦/ ٣٩٢، نيل المآرب ٢/ ٢١٥، الإقناع لابن المنذر ٢/ ١٩٨.
(¬٢) شرح فتح القدير ٢/ ٣٥٠.
(¬٣) وعند الشافعية: إن تضرر العبد بصوم التطوع لضعف، أو غيره، لم يجز بغير إذن السيد، وإلا جاز.
حاشية الشلبي على تبيين الحقائق ١/ ٣٣٢، شرح فتح القدير ٢/ ٣٥٠، أسنى المطالب ١/ ٤٣٣، المجموع ٦/ ٣٩٢.
(¬٤) وتمامه: "فإن لم يجد فصيام شهرين متتابعين، فإن عجز فإطعام ستين مسكينًا". وفاقًا للشافعية، والحنابلة، وأنها على الترتيب. وهي كذلك عند المالكية: إلا أنها على التخيير، لا على الترتيب. =','3','206',NULL,NULL,'0');
INSERT INTO [b46] VALUES (882,'متتابعين، فإن عجز فإطعام ستين مسكينًا
<hr><span data-type="title">منحة السلوك</span>

لما روي عن أبي هريرة -رضي الله عنه- قال: "بينا نحن جلوس مع النبي -صلى الله عليه وسلم- إذ جاءه رجل فقال: يا رسول الله، هلكت، قال: وما أهلكك قال: وقعت على امرأتي وأنا صائم، فقال رسول الله -صلى الله عليه وسلم-: هل تجد رقبة تعتقها؟ قال: لا، قال: فهل تستطيع أن تصوم شهرين متتابعين؟ قال: لا، قال: فهل تجد إطعام ستين مسكينًا؟ قال: لا، قال: فمكث النبي -صلى الله عليه وسلم- فبينا نحن على ذلك، أتي النبي -صلى الله عليه وسلم- بعرق فيه التمر -والعرق: المكيل (¬١) - قال: أين السائل؟ قال: أنا، قال: خذ هذا فتصدق به، فقال الرجل: أعلى أفقر مني يا رسول الله؟ فوالله ما بين لابتيها -يريد الحرتين (¬٢) - أفقر من أهل بيتي، فضحك النبي -صلى الله عليه وسلم- حتى بدت أنيابه (¬٣)، ثم قال: أطعم أهلك" رواه البخاري، وأبو داود (¬٤).
_________
= بداية المبتدي ١/ ١٣٤، كنز الدقائق ١/ ٣٢٧، الهداية ١/ ١٣٤، تبيين الحقائق ١/ ٣٢٧، المختار ١/ ١٣١، متن الرسالة ص ٧١، مختصر خليل ص ٧٢، متن أبي شجاع ص ٩٨، الإقناع في حل ألفاظ أبي شجاع ١/ ٢٢١، العمدة ص ٣١، الإقناع لابن المنذر ١/ ١٩٢.
(¬١) وهو: زبيل منسوج من نسائج الخوص، ويقال له: العَرَقة أيضًا.
لسان العرب ١٠/ ٢٤٦ مادة عرق، معجم مقاييس اللغة ٤/ ٢٨٣ باب العين والراء وما يثلثهما مادة عرق، المصباح المنير ٢/ ٤٠٥ مادة عرق، غريب الحديث لأبي عبيد ١/ ١٠٥، حلية الفقهاء ص ١٠٨.
(¬٢) وهي: الأرض ذات الحجارة السوداء. والجمع لاب ولوب، وهما تكتنفان المدينة.
لسان العرب ١/ ٧٤٥ مادة لوب، القاموس المحيط ٤/ ١٨٠ مادة ل وب، مختار الصحاح ص ٢٥٣ مادة ل وب، مجمل اللغة ص ٦٣٤ باب اللام والواو وما يثلثهما مادة لوب، المصباح المنير ٢/ ٥٦٠ مادة اللابة، المغرب ص ١١٠ مادة الحر.
(¬٣) والناب: هو السن التي خلف الرباعية.
لسان العرب ١/ ٤٦٧ مادة نيب، القاموس المحيط ٤/ ٤٦٧ مادة ن ي ب، المصباح المنير ٢/ ٦٣٢ مادة الناب.
(¬٤) البخاري ٢/ ٦٨٤ كتاب الصوم، باب إذا جامع في رمضان ولم يكن له شيء، فتصدق =','3','207',NULL,NULL,'0');
INSERT INTO [b46] VALUES (883,'كما مر، ولو أفطر مرارًا في رمضان، أو رمضانين، كفته كفارة واحدة،
<hr><span data-type="title">منحة السلوك</span>

قوله: كما مر.
أي: في صدقة الفطر (¬١)، وهو أن يطعم لكل مسكين نصف صاع من بر، أو صاعًا من تمر (¬٢).
قوله: ولو أفطر مرارًا في رمضان، بأن جامع أيامًا، أو أكل أيامًا، أو شرب أيامًا، كفته كفارة واحدة عندنا؛ لاتحاد الجنس.
وكذلك الحكم إذا أفطر مرارًا في رمضانين أو ثلاثة (¬٣).
_________
= عليه، فليكفر رقم ١٨٣٤، وأبو داود ٢/ ٣١٣ كتاب الصيام، باب كفارة من أتى أهله في رمضان رقم ٢٣٩٠، ورواه أيضًا مسلم ٢/ ٧٨١ كتاب الصيام، باب تغليظ تحريم الجماع في نهار رمضان رقم ١١١١.
(¬١) في ٣/ ١٠٨.
(¬٢) وعند المالكية، والشافعية: يطعم لكل مسكين عن كل يوم مد.
وعند الحنابلة: لكل مسكين مد من البر، أو نصف صاع من غيره.
الكتاب ١/ ١٦٧، مختصر الطحاوي ص ٥٤، الاختيار ١/ ١٣١، الهداية ١/ ١٣٤، القوانين الفقهية ص ٨٣، المعونة ١/ ٤٧٨، روض الطالب ١/ ٤٢٧، أسنى المطالب ١/ ٤٢٧، شرح منتهى الإرادات ١/ ٤٥٣، الروض المربع ص ١٧٧.
(¬٣) وعند المالكية: عليه كفارة لكل يوم.
وذهب الشافعية، والحنابلة: إلى أنه إذا أكل، أو شرب عامدًا، فعليه القضاء دون الكفارة ومن جامع في يومين، لزمته كفارتان، ولو تكرر الجماع في يوم واحد، لم يجب إلا كفارة واحدة ما لم يكفر.
مختصر الطحاوي ص ٥٤، تحفة الفقهاء ١/ ٣٦٢، الفتاوى الأسعدية في فقه الحنفية ٧/ ١٧، المبسوط ٣/ ٧٤، بدائع الصنائع ٢/ ١٠١، مختصر الطحاوي ٢/ ١٠١، بداية المجتهد ١/ ٣٠٦، القوانين الفقهية ص ٨٣، رحمة الأمة ١/ ١١٨، فتح الوهاب =','3','208',NULL,NULL,'0');
INSERT INTO [b46] VALUES (884,'إلا إذا تخللت الكفارة.
ويباح الفطر في التطوع بعذر الضيافة، ونحوها.
<hr><span data-type="title">منحة السلوك</span>

قوله: إلا إذا تخللت الكفارة، بأن أفطر في رمضان يومًا، ثم كفر عنه، ثم أفطر يومًا آخر، يلزمه كفارة أخرى في ظاهر الرواية؛ لأن التداخل قبل أداء الأول لا بعده، كما في الحدود، فإنه إذا زنى فحُدَّ، ثم زنى يحد ثانيًا (¬١).

<span data-type="title" id=toc-455>[مبيحات الفطر في النوافل]</span>
قوله: ويباح الفطر في التطوع بعذر الضيافة ونحوها (¬٢).
قيل: يباح الفطر من غير عذر. وهي رواية عن أبي يوسف (¬٣)؛ لما روي عن عائشة -رضي الله عنها- أنها قالت: "دخل النبي -صلى الله عليه وسلم- ذات يوم فقال: هل عندكم شيء؟ فقلنا: لا قال: إني إذًا صائم، ثم أتى يومًا آخر فقلنا: يا رسول الله أهدي إلينا حيس (¬٤) فقال: أرنيه، فلقد أصبحت صائمًا، فأكل" رواه مسلم (¬٥)،
_________
= ٢/ ٣٤٦، السراج الوهاج ص ١٤٥، الروض المربع ص ١٧٧، المحرر ١/ ٢٢٩، منتهى الإرادات ١/ ٤٥٢، مختصر الخرقي ص ٥٠.
(¬١) تحفة الفقهاء ١/ ٣٦٢، المبسوط ٣/ ٧٤، بدائع الصنائع ٢/ ١٠١.
(¬٢) كنز الدقائق ١/ ٣٣٧، الهداية ١/ ١٣٧، تبيين الحقائق ١/ ٣٣٧، شرح فتح القدير ٢/ ٣٦٠، العناية ٢/ ٣٦١.
(¬٣) تبيين الحقائق ١/ ٣٣٧، شرح فتح القدير ٢/ ٣٦٠، العناية ٢/ ٣٦١.
(¬٤) وهو: تمر يخلط بالأقط، ويدقان، ويعجنان بالسمن عجنًا شديدًا، حتى يندر النوى منه نواة نواة، ثم يسوى كالثريد.
لسان العرب ٦/ ٦١ مادة حيس، القاموس المحيط ١/ ٧٤٩ مادة ح ي س، مختار الصحاح ص ٦٩ مادة ح ي س، مجمل اللغة ص ١٩١ باب الحاء والياء وما يثلثهما مادة حيس.
(¬٥) ٢/ ٨٠٩ كتاب الصيام، باب جواز صوم النافلة بنية من النهار قبل الزوال، وجواز فطر الصائم نفلًا من غير عذر رقم ١١٥٤.','3','209',NULL,NULL,'0');
INSERT INTO [b46] VALUES (885,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وزاد النسائي (¬١): "ولكن أصوم يومًا مكانه" وصحح هذه الرواية أبو محمد عبد الحق (¬٢) (¬٣).
وقيل: لا يباح الفطر له إلا من عذر، وهو قول: الكرخي، وأبي بكر (¬٤) (¬٥)؛ لما روي أنه -صلى الله عليه وسلم- قال: "إذا دعي أحدكم إلى طعام فليجب، فإن كان مفطرًا فليأكل، وإن كان صائمًا فليصل" رواه أبو داود (¬٦). وقال هشام (¬٧): والصلاة: الدعاء.
_________
(¬١) في السنن الكبرى ٢/ ٢٤٩ كتاب الصيام، باب ما يجب على الصائم المتطوع إذا أفطر رقم ٣٣٠٠.
(¬٢) هو أبو محمد عبد الحق بن عبد الرحمن بن عبد الله الأزدي الأندلسي، الأشبيلي، ولد بأشبيلية سنة ٥١٠ هـ، كان فقيهًا، حافظًا، عالمًا بالحديث ورجاله وعلله، كان موصوفًا بالصلاح، والورع، ولزوم السنة، والزهد، مشاركًا في الأدب والشعر. توفي ببجاية سنة ٥٨١ هـ. من تصانيفه: المعتل من الحديث، الأحكام الصغرى، الجمع بين الصحيحين.
شذرات الذهب ٤/ ٢٧١، سير أعلام النبلاء ٢١/ ١٩٨، العبر ٣/ ١٨٢، تذكرة الحفاظ ٤/ ١٣٥٠.
(¬٣) تبيين الحقائق ١/ ٣٣٧.
(¬٤) هو أبو بكر الرازي، كما صرح به في بعض كتب الحنفية كما في شرح مقدمة الغزنوي (مخطوط)، وفي حاشية الشلبي ١/ ٣٣٧، وابن الهمام في شرح فتح القدير ٣/ ٣٦٣.
(¬٥) تبيين الحقائق ١/ ٣٣٧، شرح فتح القدير ٢/ ٣٦٣، حاشية الشلبي ١/ ٣٣٧.
(¬٦) ٢/ ٣٣١ كتاب الصوم، باب في الصائم يدعى إلى وليمة رقم ٢٤٦٠، ورواه أيضًا مسلم ٢/ ١٠٥٤ كتاب النكاح، باب الأمر بإجابة الداعي إلى دعوة رقم ١٤٣١.
عن أبي هريرة -رضي الله عنه- كلاهما بلفظ: "فإن كان مفطرًا فليطعم".
(¬٧) هو هشام بن عبيد الله الرازي، الفقيه الحنفي، السني، كان من بحور العلم، لينًا في الرواية، داعيًا إلى السنة محطًا على الجهيمة تفقه على أبي يوسف ومحمد. وعنه أبو حاتم ومحمد بن سعيد القطان وغيرهما. من مصنفاته: صلاة الأثر. توفي سنة ٢٢١ هـ. =','3','210',NULL,NULL,'0');
INSERT INTO [b46] VALUES (886,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

قال القرطبي: ثبت هذا عنه -صلى الله عليه وسلم- (¬١).
ولو كان الفطر جائزًا، كان الأفضل الفطر؛ لإجابة الدعوة التي هي السنة (¬٢).
واختلفوا في الضيافة هل تكون عذرًا؟
قيل: لا تكون عذرًا؛ لما روينا.
وقيل: تكون عذرًا قبل الزوال، وبعد الزوال لا تكون عذرًا، إلا إذا كان من الأبوين (¬٣).
وكذا إذا حلف عليه بالطلاق يفطر قبل الزوال، ولا يفطر بعده (¬٤).
ثم إذا أفطر عليه أن يقضي (¬٥).
_________
= الجواهر المضية ٣/ ٥٦٩، ميزان الاعتدال ٤/ ٣٠٠، تذكرة الحفاظ ١/ ٣٨٧، سير أعلام النبلاء ١١٠/ ٤٤٦.
(¬١) عزاه إليه الزيلعي في تبيين الحقائق ١/ ٣٣٧، وقال الشلبي في حاشيته على تبيين الحقائق ١/ ٣٣٨: ذكره القرطبي في شرح الموطأ.
(¬٢) تبيين الحقائق ١/ ٣٣٧، حاشية الشلبي ١/ ٣٣٨، شرح فتح القدير ٢/ ٣٦١، العناية ٢/ ٣٦١.
(¬٣) أي: إلا إذا كان في عدم الفطر بعده، عقوق لأحد الوالدين، لا غيرهما.
شرح فتح القدير ٢/ ٣٦٠، العناية ٢/ ٣٦١، تبيين الحقائق ١/ ٣٣٧.
(¬٤) وقيل: إن كان صاحب الطعام يرضى بمجرد حضوره، وإن لم يأكل، لا يباح الفطر، وإن كان يتأذى بذلك يفطر.
تبيين الحقائق ١/ ٣٣٨، شرح فتح القدير ٢/ ٣٦٠، حاشية الشلبي ١/ ٣٣٨.
(¬٥) قال في شرح فتح القدير ٢/ ٣٦٠: "اختلاف الرواية في نفس الإفساد هل يباح أو لا؟ ظاهر الرواية: لا، إلا بعذر. ورواية المنتقى: يباح بلا عذر، ثم اختلف المشايخ رحمهم الله على ظاهر الرواية، هل الضيافة عذر أو لا؟ قيل: نعم، وقيل: لا، وقيل: عذر قبل =','3','211',NULL,NULL,'0');
INSERT INTO [b46] VALUES (887,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

خلافًا للشافعي (¬١)؛ لما روي عن عائشة -رضي الله عنها- أنها قالت: "أصبحت أنا وحفصة صائمتين متطوعتين، فأُهدي إلينا طعام فأفطرنا عليه، فدخل علينا رسول الله -صلى الله عليه وسلم- فبدرتني (¬٢) حفصة، وكانت ابنة أبيها، فسألته عن ذلك فقال -صلى الله عليه وسلم-: اقضيا يومًا مكانه" ذكره في الموطأ والنسائي، والترمذي (¬٣). وهو قول أبي
_________
= الزوال لا بعده. . واعتقادي أن رواية المنتقى أوجه" اهـ.
تبيين الحقائق ١/ ٣٣٨، العناية ٢/ ٣٦١، حاشية الشلبي ١/ ٣٣٧.
(¬١) وأحمد. حيث يريان أن من دخل في صيام التطوع فله الخروج منه، ولا قضاء عليه، ولكن يستحب قضاؤه.
وعند المالكية: إذا شرع في صوم نفلًا لم يجز له الخروج منه، فإن أفطر لعذر فلا قضاء عليه، وإن كان بغير عذر وجب عليه القضاء.
التفريع ١/ ٣٠٣، الكافي لابن عبد البر ص ١٢٩، روض الطالب ١/ ٤٣٠، عميرة ٢/ ٧٤، الكافي لابن قدامة ١/ ٣٦٤، منتهى الإرادات ١/ ٤٦١، الإفصاح ١/ ٢٤٩.
(¬٢) بدر إلى الشيء: أسرع، وسبق.
لسان العرب ٤/ ٤٨ مادة بدر، مختار الصحاح ص ١٨ مادة ب د ر، معجم مقاييس اللغة ١/ ٢٠٨ باب الباء والدال وما بعدهما في الثلاث مادة بدر، المصباح المنير ١/ ٣٨ مادة بدر، النظم المستعذب ٢/ ١٦٢.
(¬٣) رواه مالك في الموطأ غير مسند ١/ ٣٠٦ كتاب الصيام ١٨، باب قضاء التطوع رقم الحديث ٥٠، والنسائي في الكبرى ٢/ ٢٤٧ كتاب الصيام، باب ما يجب على الصائم المتطوع إذا أفطر رقم ٣٢٩١، والترمذي ٣/ ٨٤ كتاب الصوم، باب ما جاء في إيجاب القضاء عليه رقم ٧٣٥، ورواه موصولًا: ابن أبي شيبة في المصنف ٢/ ٢٩٠ كتاب الصيام، باب في الرجل يصوم تطوعًا ثم يفطر، والبزار "كشف الأستار" ١/ ٤٩٦ كتاب الصيام، باب فيمن أفطر من صوم التطوع حديث ١٠٦٣، وأحمد ٦/ ٢٦٣، وأبو يعلى ٨/ ١٠١ رقم ٤٦٣٩، ورواه الطحاوي في شرح معاني الآثار ٢/ ١٠٨ كتاب الصيام، باب الرجل يدخل في الصيام تطوعًا ثم يفطر، والبيهقي في السنن ٤/ ٢٨٠ كتاب الصيام، باب من رأى عليه القضاء، والبغوي في شرح السنة ٦/ ٣٧٢ رقم ١٨١٤، وأبو داود ٢/ ٢٣٠ كتاب الصوم باب من رأى عليه القضاء رقم ٢٤٥٧، وابن حزم في المحلى ٦/ ٢٧٠، وابن حبان =','3','212',NULL,NULL,'0');
INSERT INTO [b46] VALUES (888,'ولو شرع في صوم، أو صلاة ظنها عليه، ثم علم انتفاءها، فالأفضل الإتمام، ولو أفسدها فلا قضاء عليه. والله أعلم.
<hr><span data-type="title">منحة السلوك</span>

بكر، وعمر، وعلي، وابن عباس، وغيرهم -رضي الله عنهم- (¬١).

<span data-type="title" id=toc-456>[إفساد العبادة بعد الشروع فيها]</span>
قوله: ولو شرع في صوم، أو صلاة ظنها عليه. أي: ظن أن في ذمته صومًا، أو صلاة، ثم علم بعد الشروع أنها ليست عليه، فالأفضل الإتمام؛ صونًا للمشروع عن البطلان. ولو أفسدها فلا قضاء عليه؛ لأن ذلك مظنون فلا يجب (¬٢). والله أعلم.
_________
= ٨/ ٢٨٤ كتاب الصوم، باب قضاء الصوم رقم ٣٥١٧.
من طريق جعفر بن برقان، عن الزهري، عن عروة، عن عائشة -رضي الله عنها- قالت ... ". قال في المجموع ٦/ ٣٩٨: ضعيف. وقال الخطابي في معالم السنن ٢٣٤٧: إسناده ضعيف.
(¬١) تبيين الحقائق ١/ ٣٣٨، حاشية الشلبي ١/ ٣٣٨.
(¬٢) وكذا عند الحنابلة.
شرح فتح القدير ٢/ ٣٦٢، كشاف القناع ٢/ ٣٤٣، مطالب أولي النهى ٢/ ٢٢٢.','3','213',NULL,NULL,'0');
INSERT INTO [b46] VALUES (889,'<span data-type="title" id=toc-457>كتاب الحج</span>
هو فرض على الفور،
<hr><span data-type="title">منحة السلوك</span>

كتاب الحج
تأخير الحج عما قبله لكونه مركبًا، وما قبله مفرد. والمفرد قبل المركب (¬١). وتقديمه على ما بعده لكونه من الأركان الخمسة للإسلام.
والحج: لغة: القصد (¬٢).
وشرعًا: زيارة مكان مخصوص، في زمان مخصوص، بفعل مخصوص (¬٣).

<span data-type="title" id=toc-458>[فرضية الحج]</span>
قوله: هو فرض على الفور.
أي: الحج فرض على الفور، لا على التراخي (¬٤)؛ لأنه يختص بوقت خاص. والموت في سنة واحدة غير نادر، فيتضيق احتياطًا (¬٥)، ولقوله -صلى الله عليه وسلم-:
_________
(¬١) وذلك أن العبادات، ثلاثة أنواع، بدنية محضة كالصلاة، ومالية محضة كالزكاة، ومركبة كالحج.
حاشية الشلبي ٢/ ٢.
(¬٢) لسان العرب ٢/ ٢٢٦ مادة حجج، القاموس المحيط ١/ ٥٩١ مادة ح ج ج، مختار الصحاح ص ٥٢ مادة ح ج ج.
(¬٣) حلية الفقهاء ص ١١١، لغة الفقه ص ١٣٣، الدر النقي ٢/ ٣٧٦، القاموس الفقهي ص ٧٦، طلبة الطلبة ص ٦٤.
(¬٤) المبسوط ٤/ ٢، العناية ٢/ ٤٠٥، كنز الدقائق ٢/ ٢، حاشية الشلبي ٢/ ٢.
(¬٥) وإليه ذهب المالكية، والحنابلة.
كنز الدقائق ٢/ ٢، تحفة الفقهاء ١/ ٣٨٠، الوقاية ١/ ١٢٧، كشف الحقائق ١/ ١٢٦، بدائع الصنائع ٢/ ١١٩، الجوهرة النيرة ١/ ١٨٢، الهداية ١/ ١٤٥، تبيين الحقائق ٢/ ٣، رؤوس المسائل ص ٢٤٩، المعونة ١/ ٥٠٦، شرح الزرقاني على خليل ٢/ ٢٣٠، التفريع ١/ ٣١٥، زاد المستقنع ص ١٨٧، التسهيل ص ٩٢.','3','214',NULL,NULL,'0');
INSERT INTO [b46] VALUES (890,'مرة في العمر.
<hr><span data-type="title">منحة السلوك</span>

" من أراد الحج فليعجل، فإنه يمرض المريض، وتضل الضالة، وتعرض الحاجة" رواه أحمد، وابن ماجه، والبيهقي (¬١). وهذا قول: أبي يوسف (¬٢).
وقال محمد (¬٣)، والشافعي (¬٤): هو على التراخي؛ لأنه وظيفة العمر (¬٥).
قوله: مرة في العمر (¬٦).
_________
(¬١) أحمد ١/ ٢١٤، وابن ماجه ٢/ ٩٦٢ كتاب المناسك، باب الخروج إلى الحج رقم ٢٨٨٣، والبيهقي ٤/ ٣٤٠ كتاب الحج، باب ما يستحب من تعجيل الحج إذا قدر عليه، وأبو نعيم ١/ ١١٤، والخطيب في الموضح ١/ ٢٣٢. من طريق أبي إسرائيل إسماعيل الملائي، عن فضيل بن عمر، وسعيد بن جبير، عن ابن عباس، أو عن الفضل بن عباس، أو إحداهما عن صاحبه قال: قال رسول الله -صلى الله عليه وسلم-: كذا عند أحمد، وابن ماجه، وعند البيهقي عن ابن عباس، عن الفضل بن عباس -رضي الله عنهما-.
قال البوصيري في الزوائد ٣/ ٣: هذا إسناد فيه مقال، إسماعيل بن خليفة أبو إسرائيل الملائي قال فيه ابن عدي: عامة ما يرويه يخالف الثقات، وقال النسائي: ضعيف، وقال الجوزجاني: مفترٍ زائغ.
(¬٢) تحفة الفقهاء ١/ ٣٨٠، الهداية ١/ ١٤٥.
(¬٣) تحفة الفقهاء ١/ ٣٨٠، الهداية ١/ ١٤٥، الجوهرة النيرة ١/ ١٨٢، بدائع الصنائع ٢/ ١١٩.
(¬٤) الوجيز ١/ ١١٠، مغني المحتاج ١/ ٤٦٠.
(¬٥) وعند المالكية قيل: على الفور، وقيل: على التراخي. والراجح عندهم: أنه على الفور.
وثمرة الخلاف: تظهر في حق المأثم، حتى يفسق، وترد شهادته عند من يقول هو على الفور.
تبيين الحقائق ٢/ ٣، شرح الزرقاني على خليل ٢/ ٢٣٠، حاشية البناني ٢/ ٢٣٠.
(¬٦) لأن الأمر بالفعل لا يقتضي التكرار. والتكرار في باب الصلاة، والزكاة، والصوم، ثبت بدليل زائد، لا بمطلق الأمر. وفاقًا للثلاثة.
قال ابن هبيرة في الإفصاح ١/ ٢٦٢: وأجمعوا على أن الحج يجب على كل مسلم، عاقل، حر، بالغ، صحيح، مستطيع، في العمر مرة واحدة.
كنز الدقائق ٢/ ٢، المختار ١/ ١٣٩، بدائع الصنائع ٢/ ١١٩، الاختيار ١/ ١٣٩، الهداية =','3','215',NULL,NULL,'0');
INSERT INTO [b46] VALUES (891,'على كل مكلف،
<hr><span data-type="title">منحة السلوك</span>

لما روى ابن عباس، أن الأقرع بن حابس (¬١)، سأل رسول الله -صلى الله عليه وسلم- فقال: "يا رسول الله الحج في كل سنة، أو مرة واحدة؟ قال: بل مرة واحدة، فمن زاد فهو تطوع" رواه أبو داود وابن ماجه (¬٢).

<span data-type="title" id=toc-459>[من يجب عليه الحج]</span>
قوله: على كل مكلف.
يتعلق بقوله: "فرض" أي: الحج فرض على كل مكلف. أي: حر، عاقل، بالغ، حتى لا يجب على العبد، والمجنون، والصبي (¬٣).
_________
= ١/ ١٤٥، القوانين ص ٨٦، المعونة ١/ ٤٩٨، الكافي لابن عبد البر ص ١٣٣، تحفة المحتاج ٤/ ٤، حاشية الشرواني على تحفة المحتاج ٤/ ٤، السلسبيل ١/ ٣٥٧، الفروع ٣/ ٢٠٣.
(¬١) هو الأقرع بن حابس بن عقال المجاشعي الدارمي التيمي، صحابي من سادات العرب في الجاهلية، قدم على رسول الله -صلى الله عليه وسلم- في وفد من بني دارم من تميم فأسلموا، وشهد حنينًا، وفتح مكة، والطائف، سكن المدينة، استشهد بالجوزجان سنة ٣١ هـ.
عيون الأثر ٢/ ٢٠٥، الإصابة ١/ ٥٨، الاستيعاب ١/ ٩٦، الثقات ٣/ ١٨.
(¬٢) أبو داود ٢/ ١٣٩ كتاب المناسك، باب فرض الحج رقم ١٧٢١، وابن ماجه ٢/ ٩٦٣ كتاب المناسك، باب فرض الحج رقم ٢٨٨٦، والطيالسي ص ٣٤٨ رقم ٢٦٦٩، وأحمد ١/ ٢٥٥، والدارمي ١/ ٤٥٦ كتاب المناسك، باب كيف وجوب الحج رقم ١٧٣٦، والنسائي ٥/ ١١١ كتاب مناسك الحج، باب وجوب الحج رقم ٢٦٢٠، والدارقطني ٢/ ٢٧٩ كتاب الحج، باب المواقيت رقم ١٩٧، والحاكم ١/ ٤٤١ كتاب المناسك.
من طريق الزهري وابن شهاب، عن أبي سنان الدؤلي عن ابن عباس -رضي الله عنهما-.
قال الحاكم ١/ ٤٤١: هذا إسناد صحيح ووافقه الذهبي.
وقال النووي في المجموع ٧/ ٨: رواه أبو داود والنسائي وابن ماجه، وغيرهم، بأسانيد حسنة.
(¬٣) وعند الشافعية: شرط صحته: الإسلام. وشرط وجوبه: الإسلام، والتكليف، والحرية، والاستطاعة وهي نوعان: أحدهما: استطاعة مباشرة، ولها شروط: أحدها: وجود الزاد، وأوعيته، ومؤونة ذهابه وإيابه، والثاني: وجود الراحلة لمن بينه وبين مكة مرحلتان =','3','216',NULL,NULL,'0');
INSERT INTO [b46] VALUES (892,'صحيح،
<hr><span data-type="title">منحة السلوك</span>

قوله: صحيح.
احتراز عن المريض، والمقعد (¬١)، والمفلوج (¬٢)، ومقطوع الرجلين، والزَّمِن (¬٣) الذي لا يستطيع الثبوت على الراحلة بنفسه (¬٤)، لكن يجب عليهم في مالهم إذا كان لهم مال مقدار ما يحج به غيرهم، فيحجون عنهم، فتجزيء عن حجة الإسلام، ولكن إذا مات المريض قبل زوال العلة (¬٥). أما إذا بريء،
_________
= ويشترط كون الزاد، والراحلة، فاضلين عن دينه، ومؤونة من عليه نفقتهم مدة ذهابه وإيابه، الثالث: أمن الطريق، الرابع: أن يثبت على الراحلة، ووجود محرم، أو نسوة ثقات مع المرأة. والنوع الثاني: استطاعة تحصيل الحج لا بالمباشرة، بل بغيره.
بدائع الصنائع ٢/ ١٢٠، تبيين الحقائق ٢/ ٣، البحر الرائق ٢/ ٣١١، بداية المبتدي ١/ ١٤٥، الكتاب ١/ ١٧٨، منهج الطلاب ٢/ ٣٧٤، فتح الوهاب ٢/ ٣٧٤، المنهاج ١/ ٥٥١، زاد المحتاج ١/ ٥٥١.
(¬١) المقعد: الذي أصابه داء في جسده، ولا يستطيع الحركة للمشي. وهو: الزَّمِن.
لسان العرب ٣/ ٣٥٧ مادة قعد، القاموس المحيط ٣/ ٦٥٥ مادة ق ع د، مختار الصحاح ص ٢٢٧ مادة ق ع د، المصباح المنير ٢/ ٥١٠ مادة قعد.
(¬٢) الفالج: هو داء يرخي بعض البدن طولًا، ويبطل إحساس المصاب، وحركته.
لسان العرب ٢/ ٣٤٦ مادة فلج، القاموس المحيط ٣/ ٥١٦ مادة ف ل ج، المصباح المنير ٢/ ٤٨٠ مادة فلجت، طلبة الطلبة ص ١٢٩، المطلع على أبواب المقنع ص ٢٩٢.
(¬٣) الزمن: هو الذي يصاب بمرض يدوم زمانًا طويلًا.
لسان العرب ١٣/ ١٩٩ مادة زمن، تاج العروس ٩/ ٣٢٧ مادة زمن، المصباح المنير ١/ ٢٥٦ مادة الزمان، المغرب ص ٢١٠ مادة الزمن، طلبة الطلبة ص ١٠٧، النظم المستعذب ٢/ ١٨٤.
(¬٤) مختصر الطحاوي ص ٥٩، تحفة الفقهاء ١/ ٣٨٤، تنوير الأبصار ٢/ ٤٥٩، كشف الحقائق ١/ ١٢٦، البحر الرائق ٢/ ٣١١، منحة الخالق ٢/ ٣١١.
(¬٥) وإليه ذهب الشافعية، والحنابلة: حيث يرون أن المعضوب الذي لا يستمسك على الراحلة، إذا قدر على مال يحج به عن نفسه، يلزمه أن يستنيب من يحج عنه. =','3','217',NULL,NULL,'0');
INSERT INTO [b46] VALUES (893,'بصير،
<hr><span data-type="title">منحة السلوك</span>

وقدر على الحج كان عليه حجة الإسلام، ويكون ما حج عنه تطوعًا (¬١).
قوله: بصير.
احتراز عن الأعمى، فإنه لا يجب عليه الحج عند أبي حنيفة، وإن وجد زادًا، وراحلة، وقائدًا (¬٢). وعندهما: يجب (¬٣).
_________
= وذهب المالكية: إلى أن المعضوب الذي لا يستمسك على الراحلة، غير مستطيع للحج، ولا يلزمه أن يحج عنه غيره من ماله.
مختصر الطحاوي ص ٥٩، شرح فتح القدير ٢/ ٤١٥، تبيين الحقائق ٢/ ٤، منحة الخالق ٢/ ٣١١، المعونة ١/ ٥٠١، الشرح الكبير للدردير ٢/ ٧، مغني المحتاج ١/ ٤٦٩، روض الطالب ١/ ٤٥٠، الروض المربع ص ١٨٩، المبدع ٣/ ٩٥.
(¬١) وكذا عند الشافعية. إلا أن الحج للأجير تطوعًا.
وعند الحنابلة: يجزيء عن المنوب عنه، وإن عوفي.
مختصر الطحاوي ص ٥٩، شرح فتح القدير ٢/ ٤١٥، الكتاب ١/ ١٧٨، حاشية رد المحتار ٢/ ٤٥٨، تنوير الأبصار ٢/ ٤٥٨، الاختيار ١/ ١٤٨، أسنى المطالب ١/ ٤٥٠، مغني المحتاج ١/ ٤٦٩، الكافي لابن قدامة ١/ ٣٨١، حاشية المقنع ١/ ٣٩١.
(¬٢) وهو رواية عنهما.
مختصر الطحاوي ص ٥٩، الهداية ١/ ١٤٥، تحفة الفقهاء ١/ ٣٨٤، تبيين الحقائق ٢/ ٤.
(¬٣) وهو ظاهر الرواية. وهو رواية الحسن عن أبي حنيفة، وإليه ذهب المالكية، والشافعية، والحنابلة.
وثمرة الخلاف: تظهر في وجوب الإحجاج، فعن أبي حنيفة: لا يجب عليهم الإحجاج؛ لأنه بدل عن الحج بالبدن، والأصل لم يجب، فلا يجب البدل.
وعندهما: يجب؛ لأنهم لزمهم الأصل، وهو الحج بالبدن في الذمة، وقد عجزوا عنه فيجب البدل عليهم.
تبيين الحقائق ٢/ ٤، الهداية ١/ ١٤٥، شرح الجامع الصغير؛ لأحمد التمرتاشي (مخطوط) ق ٦٢/ أالنسخة الأصلية لدى مكتبة عارف حكمت بالمدينة، تحت رقم ٢٧٤/ ١٣٨، تحفة الفقهاء ١/ ٣٨٤، مختصر الطحاوي ص ٥٩، الخرشي على خليل ٢/ ٢٨٥، حاشية العدوي ٢/ ٢٨٥، حلية العلماء ٣/ ٢٤٠، الوجيز ١/ ١١٠، المستوعب ٤/ ١٣، الفروع ٣/ ٢٤١.','3','218',NULL,NULL,'0');
INSERT INTO [b46] VALUES (894,'قادر على زاد وراحلة،
<hr><span data-type="title">منحة السلوك</span>

قوله: قادر على زاد، وراحلة (¬١)؛ لأنه -صلى الله عليه وسلم- فسر الاستطاعة به (¬٢).
_________
(¬١) اختلف العلماء في الزاد والراحلة هل هما من شرط وجوب الحج؟
ذهب الحنفية، والشافعية، والحنابلة: إلى أنهما من شرطه، وهي الاستطاعة.
وذهب المالكية: إلى أن الاستطاعة، إمكان الوصول بلا مشقة عظيمة، والأمن على النفس والمال، ولو بلا زاد وراحلة.
الكتاب ١/ ١٧٨، المختار ١/ ١٤٠، الهداية ١/ ١٤٥، تحفة الفقهاء ١/ ٣٨٤، الاختيار ١/ ١٤٠، مختصر خليل ص ٧٧، مواهب الجليل ٢/ ٤٩١، المعونة ١/ ٥٠٠، المهذب ١/ ١٩٦، روض الطالب ١/ ٤٤٤، هداية الراغب ص ٢٠٧، المقنع ١/ ٣٩١.
(¬٢) رواه الدارقطني ٢/ ٢١٦ كتاب الحج رقم ٦، ٧، والحاكم في المستدرك ١/ ٤٤٢ كتاب المناسك من حديث أنس -رضي الله عنه- في قوله تعالى {وَلِلَّهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ إِلَيْهِ سَبِيلًا} [آل عمران، الآية ٩٨] قيل: يا رسول الله ما السبيل؟ قال: "الزاد، والراحلة".
قال الحاكم ١/ ٤٤٢: صحيح على شرط الشيخين ووافقه الذهبي.
وقال في الدراية ٢/ ٤: ورواه أيضًا الحاكم من حديث أنس بسند رواته موثقون.
ورواه الشافعي "ترتيب المسند" ١/ ٢٨٤ كتاب الحج الباب الأول فيما جاء في فرض الحج وشروطه رقم ٧٤٤، والترمذي ٣/ ١٥٩ كتاب الحج، باب ما جاء في إيجاب الحج بالزاد والراحلة رقم ٨١٣ وقال: حديث حسن، وابن ماجه ٢/ ٩٦٧ كتاب المناسك باب ما يوجب الحج رقم ٢٨٩٦، وابن عدي في الكامل في ضعفاء الرجال ١/ ٢٢٨ ترجمة إبراهيم بن يزيد الخوزي المكي، والدارقطني في السنن ٢/ ٢١٧ كتاب الحج رقم ١٠، والبيهقي في السنن الكبرى ٤/ ٣٣٠، كتاب الحج باب الرجل يطيق المشي ولا يجد زادًا ولا راحلة فلا يبين أن يوجب عليه الحج. وغيرهم من حديث ابن عمر من طريق إبراهيم بن يزيد الخوزي عن محمد بن عباد بن جعفر. قال البيهقي ٤/ ٣٣٠: هذا الذي عني الشافعي بقوله منها ما يمتنع أهل العلم من تثبيته وإنما امتنعوا منه لأن الحديث يعرف بإبراهيم بن يزيد الخوزي وقد ضعفه أهل العلم بالحديث. ورواه ابن ماجه ٢/ ٩٦٧ كتاب المناسك باب ما يوجب الحج رقم ٢٨٩٧، والدارقطني ٢/ ٢١٨ كتاب الحج رقم ١٤. من حديث ابن عباس -رضي الله عنهما-.
والدارقطني ٢/ ٢١٧ كتاب الحج رقم ٨، والبيهقي في السنن الكبرى ٤/ ٣٣٠ كتاب =','3','219',NULL,NULL,'0');
INSERT INTO [b46] VALUES (895,'غير عقبة، ونفقة ذهابه ورجوعه،
<hr><span data-type="title">منحة السلوك</span>

قوله: غير عقبة.
صفة لقوله: "راحلة" قيد بها؛ لأنها إذا كانت عقبة لا يجب عليه الحج (¬١).
والعقبة: أن يكتري رجلان بعيرًا واحدًا يتعاقبان في الركوب، يركب كل واحد منهما مرحلة، ويمشي مرحلة (¬٢).
قوله: ونفقة ذهابه، ورجوعه.
أي: قادر على نفقة ذهابه إلى مكة، ورجوعه منها (¬٣).
_________
= الحج باب الرجل يطيق المشي ولا يجد زادًا ولا راحلة فلا يببين أن يوجب عليه الحج. من حديث عائشة -رضي الله عنها-، والدارقطني ٢/ ٢١٥ كتاب الحج رقم ١. من حديث جابر ومن حديث ابن مسعود ومن حديث عمرو بن العاص بأسانيد فيها مقال، ورواه البيهقي في السنن ٤/ ٣٣٠ كتاب الحج باب الرجل يطيق المشي ولا يجد زادًا ولا راحلة فلا يبين أن يوجب عليه الحج. من طرف صحيحة عن الحسن مرسلًا.
قال في التعليق المغني على سنن الدراقطني ٢/ ٢١٦: ورواه ابن ماجه، والدارقطني من حديث ابن عباس، وسنده ضعيف أيضًا، ورواه الدارقطني من حديث جابر، ومن حديث علي بن أبي طالب، ومن حديث ابن مسعود، ومن حديث عائشة، ومن حديث عمرو ابن شعيب عن أبيه عن جده، وطرقه كلها ضعيفة وقد قال عنه إسحاق: إن طرقه كلها ضعيفة، وقال أبو بكر بن المنذر: ولا يثبت الحديث في ذلك مسندًا، والصحيح من الروايات: رواية الحسن المرسلة.
قال في الدراية ٢/ ٤: وعن جابر وابن مسعود، وعبد الله بن عمرو بن العاص، أخرجها الدارقطني بأسانيد ضعيفة.
(¬١) الهداية ١/ ١٤٦، بدائع الصنائع ٢/ ١٢٣، شرح فتح القدير ٢/ ٤١٧، العناية ٢/ ٤١٧.
(¬٢) لسان العرب ١/ ٦١١ مادة عقب، القاموس المحيط ٣/ ٢٦٨ مادة ع ق ب، مختار الصحاح ص ١٨٦ مادة ع ق ب، المصباح المنير ٢/ ٤٢٠ مادة العقب
(¬٣) الكتاب ١/ ١٧٨، المختار ١/ ١٤٠، الهداية ١/ ١٤٦، بدائع الصنائع ٢/ ١٢٣، شرح فتح القدير ٢/ ٤١٧، العناية ٢/ ٤١٧.','3','220',NULL,NULL,'0');
INSERT INTO [b46] VALUES (896,'فاضلًا عما لا بد منه لعياله، إلى وقت رجوعه، بشرط أمن الطريق.
<hr><span data-type="title">منحة السلوك</span>

قوله: فاضلًا.
أي: حال كون الزاد، والراحلة، ونفقة الذهاب والرجوع، فاضلًا عما لا بد منه لعياله، إلى وقت رجوعه (¬١). ويعتبر في نفقته، ونفقة عياله الوسط، من غير تبذير، ولا تقتير (¬٢). ولا يترك نفقة لما بعد إيابه، في ظاهر الرواية.
وقيل: يترك نفقة يوم (¬٣).
وعن أبي يوسف: نفقة شهر (¬٤).
قوله: بشرط أمن الطريق.
لأن الحج لا يتأتى بدونه، فأشبه الزاد والراحلة (¬٥).
ثم قيل: هو شرط الوجوب (¬٦).
_________
(¬١) وإليه ذهب الشافعية.
وعند الحنابلة: فاضلة عن مؤنته، ومؤنة عياله على الدوام.
الكتاب ١/ ١٧٨، المختار ١/ ١٤٠، تبيين الحقائق ٢/ ٤، بدائع الصنائع ٢/ ١٢٢، أسنى المطالب ١/ ٤٤٤، شرح المحلي على المنهاج ٢/ ٨٧، غاية المنتهى ٢/ ٢٧٩، الإقناع للحجاوي ٢/ ٣٨٩.
(¬٢) بدائع الصنائع ٢/ ١٢٢، تبيين الحقائق ٢/ ٤.
(¬٣) تبيين الحقائق ٢/ ٤.
(¬٤) لأنه لا يمكنه التكسب كيما يقدم فيقدر بالشهر.
بدائع الصنائع ٢/ ١٤٤، تحفة الفقهاء ١/ ٣٨٦، تبيين الحقائق ٢/ ٤.
(¬٥) المختار ١/ ١٤٠، الكتاب ١/ ١٧٨، تبيين الحقائق ٢/ ٤، الاختيار ١/ ١٤٠.
(¬٦) اختاره ابن شجاع، وهو مروي عن أبي حنيفة. وإليه ذهب المالكية، والشافعية، والحنابلة.
الهداية ١/ ١٤٦، شرح فتح القدير ٢/ ٤١٦، العناية ٢/ ٤١٦، تبيين الحقائق ٢/ ٤، مختصر خليل ص ٧٧، القوانين الفقهية ص ١٨٦، التذكرة ص ٨٠، نهاية المحتاج ٣/ ٢٤١، مطالب أولي النهى ٢/ ٢٨١، كشاف القناع ٢/ ٣٩١.','3','221',NULL,NULL,'0');
INSERT INTO [b46] VALUES (897,'فإن بذل له ذلك، لم يجب. ولو حج فقير وقع فرضًا.
والمَحْرَم، أو الزوج، شرط في المرأة إذا كان سفرًا.
<hr><span data-type="title">منحة السلوك</span>

وقيل: شرط الأداء (¬١). والخلاف يظهر في وجوب الإيصاء (¬٢). فافهم.

<span data-type="title" id=toc-460>[حج غير المستطيع]</span>
قوله: فإن بذل له ذلك لم يجب.
يعني: إذا أعطي له الزاد والراحلة بطريق الإباحة، لا يلزمه الحج، سواء كان ممن لا تلحقه المنة كالوالدين والمولودين، أو ممن تلحقه، كالأجانب (¬٣).
قوله: ولو حج فقير وقع فرضًا.
يعني: إذا استغنى بعده، لا يجب عليه حجة أخرى؛ لحصول المقصود (¬٤).

<span data-type="title" id=toc-461>[شرط حج المرأة]</span>
قوله: والمحرم، أو الزوج، شرط في المرأة إذا كان سفرًا.
وهو مسيرة ثلاثة أيام فصاعدًا (¬٥)؛ لقوله -صلى الله عليه وسلم-: "لا يحل لامرأة تؤمن بالله
_________
(¬١) واختاره القاضي أبو حازم.
الهداية ١/ ١٤٦، شرح فتح القدير ٢/ ٤١٦، العناية ٢/ ٤١٦، تبيين الحقائق ٢/ ٤.
(¬٢) فمن جعله شرط الأداء يوجبه، ومن جعله شرط الوجوب لا يوجبه.
الهداية ١/ ١٤٦، شرح فتح القدير ٢/ ٤١٦، تبيين الحقائق ٢/ ٤.
(¬٣) وإليه ذهب الشافعية في الأصح عندهم، والحنابلة.
تحفة الفقهاء ١/ ٣٨٦، بدائع الصنائع ٢/ ١٢٢، تبيين الحقائق ٢/ ٤، المنهاج ١/ ٥٦٠، زاد المحتاج ١/ ٥٦٠، المقنع ١/ ٣٩٠، المبدع ٣/ ٩٤.
(¬٤) وفاقًا للشافعية، والحنابلة.
تحفة الفقهاء ١/ ٣٨٤، بدائع الصنائع ٢/ ١٢٠، شرح فتح القدير ٢/ ٤١٩، السراج الوهاج ص ١٥١، حاشية ابن القاسم العبادي ٤/ ٩، الكافي لابن قدامة ١/ ٣٧٨، نيل المآرب ١/ ٢٨٧.
(¬٥) وعند الحنابلة: يعتبر المحرم في كل ما يعد سفرًا عرفًا، طويلًا كان أو قصيرًا. =','3','222',NULL,NULL,'0');
INSERT INTO [b46] VALUES (898,'ونفقة المحرم عليها.
والمحرم العبد، والذمي إذا كان مأمونًا كالحر المسلم.
<hr><span data-type="title">منحة السلوك</span>

واليوم الآخر، أن تسافر سفرًا يكون ثلاثة أيام فصاعدًا، إلا ومعها أبوها، أو ابنها، أو زوجها، أو أخوها، أو محرم منها" رواه مسلم، وأبو داود (¬١).
وهذا حجة على الشافعي، حيث يجوز لها الخروج مع النساء الأمينات (¬٢).
قوله: ونفقة المحرم عليها.
أي: على المرأة؛ لأنها لا تتمكن من الحج إلا بالمحرم، كما لا تتمكن إلا بالزاد والراحلة (¬٣).
قوله: والمحرم العبد، والذمي (¬٤) إذا كان مأمونًا كالحر المسلم.
_________
= المختار ١/ ١٤٠، الكتاب ١/ ١٧٨، الاختيار ١/ ١٤٠، تبيين الحقائق ٢/ ٤، كشاف القناع ٢/ ٣٩٤، المستوعب ٤/ ١٩.
(¬١) مسلم ٢/ ٩٧٧ كتاب الحج، باب سفر المرأة مع محرم إلى حج وغيره رقم ١٣٤٠، وأبو داود ٢/ ١٤٠ كتاب المناسك، باب في المحرمة تحج بغير محرم رقم ١٧٢٦ عن أبي سعيد الخدري -رضي الله عنه-.
(¬٢) وعند المالكية: يشترط في المرأة المحرم، أو زوج، أو رفقة مأمونة، رجالًا كانوا أو نساءً، وكان الحج عليها فرضًا.
التلقين ص ٦٢، جواهر الإكليل ١/ ١٦٣، الشرح الصغير ١/ ٢٦٣، تحفة المحتاج ٤/ ٢٤، حاشية الشرواني ٤/ ٢٤.
(¬٣) وإليه ذهب المالكية، والشافعية، والحنابلة.
الهداية ١/ ١٤٦، تحفة الفقهاء ١/ ٣٨٧، المختار ١/ ١٤١، الخرشي على خليل ٢/ ٢٨٧، حاشية العدوي ٢/ ٢٨٧، الوجيز ١/ ١٠٩، شرح المحلي على المنهاج ٢/ ٨٩، الشرح الكبير لابن قدامة ٣/ ٢٠٦، حاشية العنقري ١/ ٤٦٢.
(¬٤) الذمة: العهد؛ لأن نقضه يوجب الذم. وتفسر: بالأمان، والضمان، وكل ذلك متقارب، =','3','223',NULL,NULL,'0');
INSERT INTO [b46] VALUES (899,'ولا عبرة بصبي، أو مجنون. وللزوج منعها مع المحرم عن النفل، والمنذور، لا عن الفرض.
<hr><span data-type="title">منحة السلوك</span>

لأن الذمي يحفظ محارمه وإن كن مسلمات (¬١)، حتى إذا كان مجوسيًا لا يجوز.
قوله: ولا عبرة بصبي، أو مجنون.
لأن وجودهما كالعدم وكذلك لا عبرة بالفاسق؛ لأنه غير مأمون (¬٢).
قوله: وللزوج منعها.
أي: منع زوجته مع المحرم عن النفل، أي: عن الحج النفل، وعن الحج المنذور؛ لأن في الخروج تفويت حقه. والنفل ليس من أركان الإسلام. والنذر وإن كان واجبًا في حقها، ففي حقه نفل (¬٣).
قوله: لا عن الفرض.
أي: لا يمنعها عن الحج الفرض؛ لأنه من أركان الإسلام فلا يجوز
_________
= ومنها قيل للمعاهدين من الكفار: ذمي؛ لأنه اؤتمن على ماله، وذمه بالجزية.
أنيس الفقهاء ص ١٨٢، لغة الفقه ص ٣١٨، التعريفات ص ١١٩، الدر النقي ٢/ ٢٨٩.
(¬١) وعند المالكية: لا يشترط في المحرم البلوغ، بل المدار على التمييز، ووجود الكفاية.
والعبد هل هو محرم أم لا؟ على قولين: عندهم.
وعند الشافعية: يكفي المحرم الذكر، وإن لم يكن ثقة إذا كان بالغًا عاقلًا.
وعند الحنابلة: يشترط في المحرم أن يكون بالغًا، عاقلًا، مسلمًا، ولو عبدًا.
تبيين الحقائق ٢/ ٦، شرح فتح القدير ٢/ ٤٢٢، بداية المبتدي ١/ ١٤٦، الهداية ١/ ١٤٦، بلغة السالك ١/ ٢٦٤، مواهب الجليل ٢/ ٥٢٤، منح الجليل ٢/ ١٩٨، نهاية المحتاج ٣/ ٢٥٠، أسنى المطالب ١/ ٤٤٧، الإقناع للحجاوي ٢/ ٣٩٥، المبدع ٣/ ١٠١.
(¬٢) تبيين الحقائق ٢/ ٦، شرح فتح القدير ٢/ ٤٢٢، الهداية ١/ ١٤٦.
(¬٣) شرح فتح القدير ٢/ ٤٢٢، بدائع الصنائع ٢/ ١٢٤، تبيين الحقائق ٢/ ٥، تحفة الفقهاء ١/ ٣٨٨.','3','224',NULL,NULL,'0');
INSERT INTO [b46] VALUES (900,'ووقته: شوال، وذو القعدة، وعشر ذي الحجة.
<hr><span data-type="title">منحة السلوك</span>

منعه كما في صلاة الفرض (¬١). وقال الشافعي: له أن يمنعها في الفرض أيضًا (¬٢).

<span data-type="title" id=toc-462>[زمن الحج]</span>
قوله: ووقته. أي: وقت الحج: شوال، وذو القعدة، وعشر ذي الحجة (¬٣) كذا روي عن العبادلة الثلاثة، وعبد الله بن الزبير (¬٤).
_________
(¬١) وإليه ذهب المالكية، والحنابلة.
تحفة الفقهاء ١/ ٣٨٨، المختار ١/ ١٤١، بدائع الصنائع ٢/ ١٢٤، الاختيار ١/ ١٤١، جواهر الإكليل ١/ ١٥٦ - ١٦٣، بداية المجتهد ١/ ٣٢٢، الكافي لابن قدامة ١/ ٣٨٥، منتهى الإرادات ٢/ ٤٧٥.
(¬٢) مغني المحتاج ١/ ٤٦٩، نهاية المحتاج ٣/ ٢٥١.
(¬٣) اختلف العلماء في أشهر الحج.
فقال الحنفية، والحنابلة: هي شوال، وذو القعدة، وعشر من ذي الحجة.
وقال المالكية: هي شوال، وذو القعدة، وذو الحجة جميعه، وقال الشافعية: هي شوال، وذو القعدة، وتسعة أيام من ذي الحجة، وليلة النحر.
وفائدة الخلاف بينهم في ذلك: عند المالكية تعلق الدم بتأخير طواف الإفاضة عن أشهر الحج.
وفائدته عند الشافعية: جواز الإحرام فيها.
وفائدته عند الحنفية، والحنابلة: تعلق الحنث به.
بداية المبتدي ١/ ١٧٢، المختار ١/ ١٤١، الهداية ١/ ١٧٢، شرح فتح القدير ٢/ ١٧، منح الجليل ٢/ ٢٢٣، جواهر الإكليل ١/ ١٦٨، بداية المجتهد ١/ ٣٢٥، الشرح الكبير للدردير ٢/ ٢١، المعونة ١/ ٥٠٨، عميرة ٢/ ٩١، التنبيه ص ٧١، نهاية المحتاج ٣/ ٢٥٦، مختصر الخرقي ص ٥٥، العمدة ص ٣٤، الإفصاح ١/ ٢٦٧.
(¬٤) إنما فصل عبد الله بن الزبير -رضي الله عنه- عن العبادلة الثلاثة وهم: عبد الله بن مسعود، وعبد الله بن عمر، وعبد الله بن عباس؛ لأنه كان لا يفهم في عرفهم من إطلاق العبادلة إلا هؤلاء الثلاثة، هذا ما عليه أهل اللغة، وما عليه الفقهاء.
وأما العبادلة عند المحدثين: فهم عبد الله بن عباس، وعبد الله بن عمر وعبد الله بن =','3','225',NULL,NULL,'0');
INSERT INTO [b46] VALUES (901,'ويُكره تقديم الإحرام على شوال.
والإحرام شرط أيضًا.
<hr><span data-type="title">منحة السلوك</span>

قوله: ويكره تقديم الإحرام على شوال.
لئلا يقع في المحظورات بطول الزمان (¬١).
قوله: والإحرام شرط أيضًا.
أي: كاشتراط الزاد، والراحلة، وأمن الطريق، والنفقة، ونحوها (¬٢)؛ لما مر (¬٣). وعلامة كونه شرطًا: أن يستدام إلى أن يحلق، ويجامع كل ركن من أركان الحج، وأداء الأفعال متأخر عنه (¬٤).
_________
= عمرو بن العاص، وعبد الله بن الزبير، وليس عبد الله بن مسعود من العبادلة؛ لأنه قد تقدم موته.
ثم العبادلة يجوز أن يكونوا جمع "عبدل" لغة في عبد قياسًا؛ لأن من العرب من يقول في عبد عبدل، وفي زيد زيدل، ويجوز أن يكون جمع عبد على غير قياس، كالنساء للمرأة.
مقدمة ابن الصلاح ص ٤٣٠، حاشية الشلبي ٢/ ٤٩.
(¬١) اختلف العلماء في صحة تقديم الإحرام على شوال:
فذهب الحنفية، والمالكية، والحنابلة: إلى صحة ذلك. وهو قول: النخعي، والثوري، وإسحاق. وذهب الشافعية: إلى أنه لا ينعقد الإحرام بالحج في غير أشهره، فإن عقده انقلب. وهو قول: عطاء، وطاووس، ومجاهد.
المختار ١/ ١٤١، بداية المبتدي ١/ ١٧٢، تبيين الحقائق ٢/ ٥٠، العناية ٣/ ١٩، التلقين ص ٦٣، القوانين ص ٧٨، تحفة المحتاج ٤/ ٣٦، حاشية الشرواني ٤/ ٣٦، غاية المنتهى ٢/ ٣٠١، حاشية المقنع ١/ ٣٩٦.
(¬٢) وذهب المالكية، والشافعية، والحنابلة: إلى أن الإحرام ركن من أركان الحج.
الهداية ١/ ١٧١، تبيين الحقائق ٢/ ٤٩، مختصر خليل ص ٧٩، جواهر الإكليل ١/ ١٦٨، التذكرة ص ٨٠، هداية الغلام ص ٦٩، حاشية المقنع ١/ ٤٦٨، الروض المربع ص ٢١٣.
(¬٣) في ٣/ ٢١٩.
(¬٤) تبيين الحقائق ٢/ ٤٩.','3','226',NULL,NULL,'0');
INSERT INTO [b46] VALUES (902,'و<span data-type="title" id=toc-463>أركان الحج:</span> الوقوف بعرفة،
<hr><span data-type="title">منحة السلوك</span>


[أركان الحج]
قوله: وأركان الحج: الوقوف بعرفة (¬١).
لقوله -صلى الله عليه وسلم-: "الحج عرفة، فمن أدركها فقد أدرك الحج" (¬٢).
_________
(¬١) وعند المالكية: أركان الحج: النية، والإحرام، والطواف، والسعي، والوقوف، بعرفة.
وواجباته: أفراد الحج، والإحرام من الميقات المكاني، والتلبية، وطواف القدوم، والمبيت بالمزدلفة ليلة النحر، ورمي الجمار، والحلق أو التقصير، وركعتا الطواف، والمبيت بمنى ليالي الرمي، والجمع بعرفة والمزدلفة.
وعند الشافعية: أركان الحج خمسة: الإحرام، والوقوف بعرفة، والطواف، والسعي، والحلق.
وواجباته: الإحرام من الميقات، ورمي الجمار، والمبيت بمزدلفة، وليالي التشريق، وطواف الوداع.
وعند الحنابلة أركان الحج: الإحرام، والوقوف بعرفة، وطواف الزيارة، والسعي.
وواجباته: الإحرام من الميقات المعتبر له، والوقوف بعرفة إلى الغروب، والمبيت بمنى والمبيت بمزدلفة إلى بعد نصف الليل، والرمي، والحلق أو التقصير، وطواف الوداع.
بدائع الصنائع ٢/ ١٢٥، تحفة الفقهاء ١/ ٣٨١، الهداية ١/ ١٤٨، الشرح الكبير للدردير ٢/ ٢١، القوانين الفقهية ص ٨٧، حاشية الدسوقي ٢/ ٢١، التذكرة ص ٨٠، الإقناع في حل ألفاظ أبي شجاع ١/ ٢٣٣، الروض المربع ص ٢١٣، التسهيل ص ١٠٦.
(¬٢) رواه أحمد ٤/ ٣٠٩، وأبو داود ١٩٤٩ كتاب المناسك، باب من لم يدرك عرفة ٢/ ١٩٦، والترمذي ٣/ ٢٤٨ رقم ٨٨٩ كتاب الحج، باب ما جاء فيمن أدرك الإمام بجمع فقد أدرك الحج، والنسائي ٥/ ٢٥٦ كتاب الحج، باب فرض الوقوف بعرفة رقم ٣٠١٦، وابن ماجه ٣٠١٥ كتاب المناسك، باب فرض من أتى عرفة قبل الفجر ليلة جمع ٢/ ١٠٠٣، والدارمي ٢/ ٤٨٨ كتاب المناسك، باب من لم يتم الحج رقم ١٨٢٧، وابن أبي شيبة ٣/ ٢٢٦ كتاب الحج، باب من قال إذا وقف بعرفة قبل أن يطلع الفجر فقد أدرك رقم ١٣٦٨٣، والطيالسي ص ١٨٥ رقم ١٣٠٩، والحميدي ٢/ ٣٩٩ رقم ٨٩٩، وابن حبان ٩/ ٢٠٣ كتاب الحج، باب رمي الجمار أيام التشريق رقم ٣٨٩٢، وابن الجارود في المنتقى ص ١٢٣ باب المناسك رقم ٤٦٨، وابن خزيمة ٤/ ٢٥٧ كتاب المناسك، باب ذكر الدليل على أن الحاج إذا لم يدرك عرفة قبل طلوع الفجر من يوم فائت الحج غير مدركه رقم ٢٨٢٢، والحاكم =','3','227',NULL,NULL,'0');
INSERT INTO [b46] VALUES (903,'وطواف الزيارة.
وواجباته: الوقوف بمزدلفة (¬١)،
<hr><span data-type="title">منحة السلوك</span>

قوله: وطواف الزيارة (¬٢).
لقوله تعالى: {وَلْيَطَّوَّفُوا بِالْبَيْتِ الْعَتِيقِ} [الحج: ٢٩] المراد من هذا الطواف طواف الزيارة (¬٣)، والله أعلم.

<span data-type="title" id=toc-464>[واجبات الحج]</span>
قوله: وواجباته. أي: واجبات الحج: الوقوف بمزدلفة (¬٤).
_________
= ١/ ٤٦٤ كتاب المناسك، والطحاوي في الشرح ٢/ ٢٠٩ كتاب مناسك الحج باب حكم الوقوف بمزدلفة، والدارقطني ٢/ ٢٤٠ كتاب الحج، باب المواقيت رقم ١٩، والبيهقي ٥/ ١١٦ كتاب الحج باب وقت الوقوف لإدراك الحج، وأبو نعيم في الحلية ٧/ ١١٩، والبغوي ٢٠٠١، والبخاري تعليقًا في التاريخ الكبير ٥/ ٢٤٣.
من طريق سفيان الثوري وشعبة عن بكير بن عطاء عن عبد الرحمن بن يعمر الديلي قال: شهدت رسول الله -صلى الله عليه وسلم- وهو واقف بعرفة، وأتاه ناس وهو بعرفة، فسألوه، فأمر مناديًا ينادي: الحج عرفة ...
قال الذهبي في التلخيص ١/ ١٦٤: صحيح.
وقال الترمذي ٣/ ٢٥٠: وسمعت الجارود يقول: سمعت وكيعًا أنه ذكر هذا الحديث، فقال هذا الحديث: أم المناسك.
وقال ابن ماجه ٢/ ١٠٠٤: قال محمد بن يحيى ما أرى للثوري حديثًا أشرف منه.
وقال البيهقي في السنن ٥/ ١١٦: قال سفيان بن عيينة: قلت لسفيان الثوري: ليس عندكم بالكوفة حديث أشرف، ولا أحسن من هذا.
وقال النووي في المجموع ٨/ ٩٥: حديث عبد الرحمن الديلي صحيح، رواه أبو داود، والترمذي، والنسائي، وابن ماجه، وآخرون، بأسانيد صحيحة.
(¬١) في جـ: "وواجباته: الوقوف بعرفة إلى غروب الشمس، والوقوف بمزدلفة".
(¬٢) بدائع الصنائع ٢/ ١٢٥، تحفة الفقهاء ١/ ٣٨١.
(¬٣) تحفة الفقهاء ١/ ٣٨١، بدائع الصنائع ٢/ ١٢٥.
(¬٤) بدائع الصنائع ٢/ ١٣٣، تحفة الفقهاء ١/ ٣٨١.','3','228',NULL,NULL,'0');
INSERT INTO [b46] VALUES (904,'والسعي بين الصفا والمروة،
<hr><span data-type="title">منحة السلوك</span>

وقال الشافعي: إنه ركن (¬١).
ولنا: ما روي عن ابن عباس -رضي الله عنهما- أنه قال: "أنا ممن قدم النبي -صلى الله عليه وسلم- ليلة المزدلفة في ضعفة" رواه الجماعة (¬٢). فعلم أنه ليس بركن، ولو كان ركنًا لم يجز تركه للضعفاء، كالوقوف بعرفات.
قوله: والسعي بين الصفا والمروة (¬٣).
وقال مالك (¬٤)، والشافعي (¬٥): هو ركن أيضًا.
ولنا قوله تعالى: {إِنَّ الصَّفَا وَالْمَرْوَةَ مِنْ شَعَائِرِ اللَّهِ فَمَنْ حَجَّ الْبَيْتَ أَوِ اعْتَمَرَ فَلَا جُنَاحَ عَلَيْهِ أَنْ يَطَّوَّفَ بِهِمَا وَمَنْ تَطَوَّعَ خَيْرًا فَإِنَّ اللَّهَ شَاكِرٌ عَلِيمٌ} [البقرة: ١٥٨] ورفع الجناح والتخيير، ينافي الفرضية (¬٦).
_________
(¬١) الأصح: أنه واجب، وليس بركن عندهم، وإلى القول بوجوبه ذهب المالكية، والحنابلة.
القوانين ص ٨٧، مختصر خليل ص ٨٣، روضة الطالبين ٣/ ٩٩، الحاوي الكبير ٤/ ١٧٧، زاد المستقنع ص ٢١٣، عمدة الطالب ص ٢٣١، الروض المربع ص ٢١٣.
(¬٢) البخاري ٢/ ٦٠٣ كتاب الحج، باب من قدم ضعفة أهله بليل، فيقفون بالمزدلفة، ويدعون، ويقدم إذا غاب القمر رقم ١٥٩٤، ومسلم ٢/ ٩٤١ كتاب الحج، باب استحباب تقديم دفعة الضعفة من النساء وغيرهن، من مزدلفة إلى منى في أواخر الليالي قبل زحمة الناس رقم ٣٠٢ (١٢٩٣)، وأبو داود ٢/ ١٩٤ كتاب المناسك، باب التعجيل من جمع رقم ١٩٣٩، والترمذي ٣/ ٢٣٠ كتاب الحج، باب ما جاء في تقديم الضعفة من جمع بليل رقم ٨٩٢، والنسائي ٥/ ٢٦١ كتاب الحج، باب تقديم النساء والصبيان إلى منازلهم بمزدلفة رقم ٣٠٣٢، وابن ماجه ٢/ ١٠٠٧ كتاب المناسك، باب من تقدم من جمع إلى من رمي الجمار رقم ٣٠٢٦.
(¬٣) تحفة الفقهاء ١/ ٣٨١، بدائع الصنائع ٢/ ١٣٣، شرح فتح القدير ٢/ ٤٨٣، العناية ٢/ ٤٨٢.
(¬٤) الشرح الكبير للدردير ٢/ ٢١، التلقين ص ٦٤.
(¬٥) وأحمد.
هداية الغلام ص ٦٩، التذكرة ص ٨٠، هداية الراغب ص ٢٣١، المبدع ٣/ ٢٦٣.
(¬٦) الهداية ١/ ١٥٤، تبيين الحقائق ٢/ ٢١.','3','229',NULL,NULL,'0');
INSERT INTO [b46] VALUES (905,'ورمي الجمار، والحلق، أو التقصير،
<hr><span data-type="title">منحة السلوك</span>

قوله: ورمي الجمار.
أي: جمار أربعة أيام، وهي سبعون حصاة، سبعة في يوم العيد، وثلاثة وستون في ثلاثة أيام بعد العيد، كل يوم إحدى وعشرون، عند كل ميل سبعة (¬١).
قوله: والحلق، أو التقصير (¬٢).
لما روي عن أنس -رضي الله عنه- أن النبي -صلى الله عليه وسلم-: "أتى منى، فأتى الجمرة فرماها، ثم أتى منزله بمنى ونحر، وقال للحلاق (¬٣): خذ، وأشار إلى جانبه الأيمن، ثم الأيسر، ثم جعل يعطيه الناس" رواه مسلم، وأبو داود، وأحمد (¬٤).
_________
(¬١) وكذا عند المالكية، والشافعية، والحنابلة.
تحفة الفقهاء ١/ ٣٨١، بدائع الصنائع ٢/ ١٣٣، المختار ١/ ١٥١، مختصر الطحاوي ص ٦٥، بداية المبتدي ١/ ١٥٩، التفريع ١/ ٣٤٤، القوانين الفقهية ص ٨٧، الوجيز ١/ ١٢٢، حاشية الجمل على شرح المنهج ٢/ ٤٧٠، التذكرة ص ٨١، الروض المربع ص ٢٠٨، المقنع ١/ ٤٦١.
(¬٢) وإليه ذهب المالكية، والحنابلة.
وذهب الشافعية: إلى أن الحلق، أو التقصير، ركن من أركان الحج.
تحفة الفقهاء ١/ ٣٨١، بدائع الصنائع ٢/ ١٣٣، شرح الزرقاني على مختصر خليل ٢/ ٢٨٠، حاشية البناني ٢/ ٢٨١، التذكرة ص ٨٠، مغني المحتاج ١/ ٥١٣، هداية الراغب ص ٢٣١، المقنع ١/ ٤٧١.
(¬٣) الحلاق: هو معمر بن عبد الله بن نافع بن نضلة القرشي العدوي.
عون المعبود ٥/ ٤٥٦.
(¬٤) مسلم ٢/ ٩٤٧ كتاب الحج، باب بيان أن السنة يوم النحر أن يرمي، ثم ينحر، ثم يحلق والابتداء في الحلق بالجانب الأيمن من رأس المحلوق رقم ١٣٠٥، وأبو داود ٢/ ٢٠٣ كتاب المناسك، باب الحلق والتقصير رقم ١٩٨١، وأحمد ٣/ ١١١.','3','230',NULL,NULL,'0');
INSERT INTO [b46] VALUES (906,'وطواف الصدر.
<hr><span data-type="title">منحة السلوك</span>

والتقصير: أن يأخذ الرجل، أو المرأة من رؤوس شعر ربع الرأس مقدار الأنملة (¬١).
قوله: وطواف الصدر (¬٢) (¬٣).
وقال مالك (¬٤)، والشافعي: هو سنة (¬٥).
ولنا: ما روي عن ابن عباس -رضي الله عنهما- أنه قال: "كان الناس ينصرفون في
_________
(¬١) وعند المالكية: تأخذ المرأة من جميع شعرها قدر أنملة، ويأخذ الرجل إن قصر من قرب أصل الشعر، ويجزيء الأخذ من الأطراف لجميع الشعر، ولا يجزيء حلق بعض شعر الرأس للذكر، ولا تقصير بعضه للأنثى.
وعند الشافعية: أقل الحلق، أو التقصير، ثلاث شعرات.
وعند الحنابلة التقصير يكون من جميع الشعر، وتقصر المرأة منه قدر أنملة، فأقل.
تبيين الحقائق ٢/ ٣٢، الهداية ١/ ١٦٠، الشرح الصغير ١/ ٢٧٩، أقرب المسالك ص ٤٩، منهج الطلاب ٢/ ٤٦٦، فتح الوهاب ٢/ ٤٦٦، شرح منتهى الإرادات ٢/ ٦٣، الروض المربع ص ٢٠٩.
(¬٢) وكذا عند الحنابلة. طواف الصدر -ويسمى طواف الوداع- من الواجبات.
تحفة الفقهاء ١/ ٣٨١، بداية المبتدي ١/ ١٦٣، المبدع ٣/ ٢٦٤، منتهى الإرادات ٢/ ٧٣.
(¬٣) يقال: صَدَر القوم صَدورًا وأصدرته بالألف، وأصله: الانصراف، واسم لليوم الرابع من أيام النحر؛ لأن الناس يصدرون فيه عن مكة إلى أماكنهم.
لسان العرب ٤/ ٤٤٩ مادة صدر، القاموس المحيط ٢/ ٨٠٥ مادة ص د ر، مختار الصحاح ص ١٥٠ مادة ص د ر، المصباح المنير ١/ ٣٣٥ مادة ص د ر.
(¬٤) مختصر خليل ص ٨٥، جواهر الإكليل ١/ ١٨٥.
(¬٥) في قول: عنه. والقول الآخر: وهو قوله القديم: أنه واجب وهو القول: الأصح عند الشافعية، وهو المذهب عندهم. وبه قال: الحسن البصري، والحكم، وحماد، والثوري، وإسحاق، وأبو ثور.
كفاية الأخيار ١/ ١٣٩، التذكرة ص ٨١، مغني المحتاج ١/ ٥١٠، الوجيز ١/ ١٢٣، روضة الطالبين ٣/ ١١٦، المجموع ٨/ ٢٥٤ - ٢٨٤، الحاوي الكبير ٤/ ٢١٢.','3','231',NULL,NULL,'0');
INSERT INTO [b46] VALUES (907,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

كل وجه، فقال رسول الله -صلى الله عليه وسلم-: "لا ينفر أحد حتى يكون آخر عهده بالبيت" رواه مسلم، وأحمد (¬١) (¬٢).
_________
(¬١) مسلم ٢/ ٩٦٣ كتاب الحج، باب وجوب طواف الوداع وسقوطه عن الحائض رقم ١٣٢٧، وأحمد ١/ ٢٢٢.
(¬٢) اختلف الفقهاء في حكم طواف الوداع على قولين:
القول الأول:
ويقتضي وجوب طواف الوداع، ويجب بتركه دم. ويستثنى من ذلك الحائض فيسقط عنها. وهو قول الحسن البصري، والحكم، وحماد، والثوري، وأبي حنيفة، وأحد قولي: الشافعي، وهو قول إسحاق، وأبي ثور، وأحمد (أ).
القول الثاني:
ويرى: أن طواف الوداع سنة، وليس على من تركه دم. وهو قول مالك، والقول الثاني: للشافعي، وقول ابن المنذر (ب).
الأدلة:
أدلة القول الأول:
١ - حديث ابن عباس -رضي الله عنهما- قال: "أُمِرَ الناسُ أن يكون آخر عهدهم بالبيت، إلا أنه خُفّفَ عن الحائض" متفق عليه (جـ) واللفظ للبخاري.
٢ - حديث ابن عباس -رضي الله عنهما- قال: "كان الناس ينصرفون في كلِّ وَجْهٍ، فقال رسول الله -صلى الله عليه وسلم-: لا ينفرنَّ أحد حتى يكون آخر عهده بالبيت" رواه مسلم (د).
دليل القول الثاني: =
_________
(أ) بدائع الصنائع ٢/ ١٤٢، الاختيار ١/ ١٥٥، شرح النووي على مسلم ٩/ ٤٤٦، المجموع ٨/ ٢٥٤، ٢٨٤، المهذب ٢/ ٨٠٣، المغني ٣/ ٤٥٨، الشرح الكبير ٢/ ٢٥٨، الإنصاف ٤/ ٤٩، ٥٠.
(ب) بداية المجتهد ١/ ٣٤٣، القوانين الفقهية ص ١٣٣، مواهب الجليل ٣/ ١٣٧، المهذب ٢/ ٨٠٣، المجموع ٨/ ٢٥٤، الإقناع لابن المنذر ١/ ٢٣٤، ٢٣٥.
(جـ) صحيح البخاري مع فتح الباري ٣/ ٥٨٥، صحيح مسلم بشرح النووي ٩/ ٤٤٦.
(د) صحيح مسلم بشرح النووي ٩/ ٤٤٦.','3','232',NULL,NULL,'0');
INSERT INTO [b46] VALUES (908,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
=  قالوا: إنه لو كان طواف الوداع واجبًا لما جاز للحائض تركه (أ).
المناقشة:
وقد ناقش أصحاب القول الأول هذا الاستدلال فقالوا: إن سقوطه عن المعذور لا يوجب سقوطه عن غيره، كالصلاة تسقط عن الحائض، وتجب على غيرها، بل تخصيص الحائض بإسقاطه عنها، دليل على وجوبه على غيرها؛ إذ لو كان ساقطًا عن الكل، لم يكن لتخصيصها بذلك معنى (ب).
الترجيح:
الراجح هو القول: بوجوب طواف الوداع وأنه يلزم من تركه دم؛ لقوة أدلتهم، وهو قول: جمهور علماء الأمة، سلفًا، وخلفًا.
ولو رجع من ترك طواف الوداع، فأتى به، فلا يخلو من حالين:
الحال الأول: أن يكون رجع وهو قريب من البيت، فلا شيء عليه.
وهذا قول: عطاء، والثوري، وأبي حنيفة، والشافعي وإسحاق، وأبي ثور، وأحمد (جـ).
والقريب هو من كان من مكة دون مسافة القصر، وهو قول الشافعية، والحنابلة (د). وكان عطاء: يرى الطائف قريبًا (هـ).
وقال الثوري: حد ذلك الحرم، فمن كان فيه فهو قريب، ومن خرج منه فهو بعيد (و).
وقال الحنفية: حد ذلك الميقات، فمن بلغه ولم يطف، وجب عليه الرجوع للطواف (ز). =
_________
(أ) المجموع ٨/ ٢٥٤.
(ب) بدائع الصنائع ٢/ ١٤٢، المغني ٣/ ٤٥٨، الشرح الكبير لابن قدامة ٨/ ٢٥٤.
(جـ) بدائع الصنائع ٢/ ١٤٣، المجموع ٨/ ٢٥٤، ٢٥٥، ٢٨٥، المغني ٣/ ٤٦٠، الشرح الكبير لابن قدامة ٢/ ٢٥٩.
(د) المجموع ٨/ ٢٥٤، ٢٥٥، ٢٨٥، المغني ٣/ ٤٦٠، الشرح الكبير لابن قدامة ٢/ ٢٥٩.
(هـ) الشرح الكبير لابن قدامة ٢/ ٢٥٩.
(و) الشرح الكبير لابن قدامة ٢/ ٢٥٩.
(ز) بدائع الصنائع ٢/ ١٤٣.','3','233',NULL,NULL,'0');
INSERT INTO [b46] VALUES (909,'وركعتا الطواف.
<hr><span data-type="title">منحة السلوك</span>

قوله: وركعتا الطواف (¬١). وقال الشافعي: هي سنة (¬٢).
ولنا: "أنه -صلى الله عليه وسلم- لما انتهى إلى مقام إبراهيم -عليه السلام- قرأ {وَاتَّخِذُوا مِنْ مَقَامِ إِبْرَاهِيمَ مُصَلًّى} [البقرة: ١٢٥]، فصلَّى ركعتين، فقرأ فاتحة الكتاب و {قُلْ يَا
_________
= الثاني: أن يرجع وهو بعيد من البيت فعليه دم، والبعيد عند الشافعية والحنابلة (أ) مسافة القصر فما زاد، وعند الحنفية (ب): إن جاوز الميقات فهو بعيد، ولا يجب عليه الرجوع؛ لأنه لا يمكنه الرجوع إلا بالتزام عمرة.
قالوا: والأولى أن لا يرجع ويريق دمًا مكان الطواف؛ لأن هذا أنفع للفقراء، وأيسر عليه؛ لما فيه من دفع مشقة السفر، وضرر التزام الإحرام.
وقد علل الشافعية والحنابلة لقولهم: أن من رجع وهو دون مسافة القصر لا شيء عليه فقالوا: من دون مسافة القصر في حكم الحاضر في أنه لا يفطر، ولا يقصر، ولذلك عددناه من حاضري المسجد الحرام، ومن لم يمكنه الرجوع لعذر فهو كالبعيد عليه دم (جـ).
وقال ابن قدامة: "ويحتمل سقوط الدم عن البعيد برجوعه لأنه واجب أتى به، فلم يجب عليه بدله كالقريب" (د) ا. هـ.
ولهذا القول: وجاهته، لكنه إن جاوز الميقات كان عليه أن يحرم، ويأتي بعمرة، ثم يطوف طواف الوداع (هـ).
(¬١) وكذا عند المالكية هي من الواجبات.
المختار ١/ ١٤٨، الهداية ١/ ١٥٣، الاختيار ١/ ١٤٨، الشرح الصغير ١/ ٢٧٤، بلغة السالك ١/ ٢٧٤.
(¬٢) وكذا عند الحنابلة. =
_________
(أ) المجموع ٨/ ٢٥٥، المغني ٣/ ٤٦٠، الشرح الكبير لابن قدامة ٢/ ٢٥٩.
(ب) بدائع الصنائع ٢/ ١٤٣.
(جـ) المغني ٣/ ٤٦٠.
(د) المغني ٣/ ٤٦٠، الشرح الكبير لابن قدامة ٢/ ٢٥٩.
(هـ) بدائع الصنائع ٢/ ١٤٣، المغني ٣/ ٤٦١، الشرح الكبير لابن قدامة ٢/ ٢٦٠.','3','234',NULL,NULL,'0');
INSERT INTO [b46] VALUES (910,'وسننه: طواف القدوم، والرمل فيه،
<hr><span data-type="title">منحة السلوك</span>

أَيُّهَا الْكَافِرُونَ}، و {قُلْ هُوَ اللَّهُ أَحَدٌ}، ثم عاد إلى الركن فاستلمه ثم خرج إلى الصفا" رواه أحمد، ومسلم (¬١).

<span data-type="title" id=toc-465>[سنن الحج]</span>
قوله: وسننه. أي: سنن الحج: طواف القدوم (¬٢).
وقال مالك: هو واجب (¬٣)؛ لقوله -صلى الله عليه وسلم-: "من أتى البيت فليحيه بالطواف" (¬٤).
قلنا: سماه تحية، فلا يفيد الوجوب (¬٥).
قوله: والرمل فيه. أي: في الطواف (¬٦)؛ لفعله -صلى الله عليه وسلم- (¬٧).
_________
= هداية الغلام ص ٧٠، متن أبي شجاع ص ١٠٧، دليل الطالب ١/ ٢٦٥، الإقناع للحجاوي ٢/ ٤٨٥.
(¬١) أحمد ٣/ ٣٢٠، ومسلم ٢/ ٨٨٦، كتاب الحج، باب حجة النبي رقم ١٢١٨.
(¬٢) وإلى القول بسنيته، ذهب الشافعية، والحنابلة.
بداية المبتدي ١/ ١٥٣، العناية ٢/ ٤٥٧، شرح أبي قاسم على متن أبي شجاع ١/ ٣٣٤، حاشية البيجوري ١/ ٣٣٤، الإفصاح ١/ ٢٦٩، التسهيل ص ١٠٠.
(¬٣) التفريع ١/ ٣٣٩، القوانين ص ٨٧.
(¬٤) قال الزيلعي في نصب الراية ٣/ ٥١: غريب جدًا.
وقال ابن حجر في الدراية ٢/ ١٧: لم أجده.
(¬٥) الهداية ١/ ١٥٣، تبيين الحقائق ٢/ ١٩، كشف الحقائق ١/ ١٣٠، شرح الوقاية ١/ ١٣٠.
(¬٦) في أشواطه الثلاثة الأولى، وإلى سنته: ذهب المالكية، والشافعية، والحنابلة.
الأصل ١/ ٢٠٠، المبسوط ٤/ ١٠، الهداية ١/ ١٥٢، الخرشي على خليل ٢/ ٣٢٦، حاشية العدوي ٢/ ٣٢٦، روض المطالب ١/ ٤٨١، أسنى المطالب ١/ ٤٨١، زاد المستقنع ص ٢٠٤، حاشية المقنع ١/ ٤٤٣.
(¬٧) فقد روى البخاري في صحيحه ٢/ ٥٨١ كتاب الحج، باب استلام الحجر الأسود رقم ١٥٢٦ عن ابن عمر -رضي الله عنهما- أنه قال: "رأيت رسول الله -صلى الله عليه وسلم- حين قدم مكة إذا استلم الركن الأسود، أول ما يطوف يخب ثلاثة أطواف من السبع".','3','235',NULL,NULL,'0');
INSERT INTO [b46] VALUES (911,'والهرولة في السعي بين الميلين الأخضرين، والمبيت بمنى في أيام منى.
<hr><span data-type="title">منحة السلوك</span>

قوله: والهرولة (¬١) في السعي بين الميلين الأخضرين (¬٢). أحدهما: في ركن الجدار، والآخر متصل بدار ابن عباس، لما روي: "أنه -صلى الله عليه وسلم- نزل إلى المروة، حتى إذا انتصبت قدماه في بطن الوادي رَمَل، حتى إذا صعد مشى حتى أتى المروة" رواه أبو داود (¬٣).
قوله: والمبيت بمنى في أيام منى (¬٤)؛ لما روي: "أنه -صلى الله عليه وسلم- بات بها وظل" رواه أبو داود (¬٥).
_________
(¬١) هرول أي: أسرع في مشيه. وهو دون الخبب. ولهذا يقال: هو دون المشي، والعدو.
لسان العرب ١١/ ٦٩٥، مادة هرول، مختار الصحاح ص ٢٩٨ مادة هـ ر ول، القاموس المحيط ٤/ ٥٠٦ مادة هـ ر ول، فقه اللغة وسر العربية للثعالبي ص ١٨٤.
(¬٢) وكذا عند المالكية، والشافعية، والحنابلة.
بدائع الصنائع ٢/ ١٤٩، كنز الدقائق ٢/ ٢٠، منح الجليل ٢/ ٢٦٨، مختصر خليل ص ٨٣، المنهاج ١/ ٥٩٣، زاد المحتاج ١/ ٥٩٣، المقنع ١/ ٤٤٧، نيل المآرب ١/ ٣٠٨.
(¬٣) ٢/ ١٨٤، كتاب المناسك، باب صفة حج النبي رقم ١٩٠٥، ورواه مسلم في صحيحه ٢/ ٨٨٦ كتاب الحج، باب حجة النبي رقم ٢١٨، عن جابر بن عبد الله -رضي الله عنهما-.
(¬٤) وذهب المالكية، والشافعية، والحنابلة: إلى أن المبيت بمنى في أيام منى من الواجبات.
بدائع الصنائع ٢/ ١٥٩، العناية ٢/ ٥٠١، شرح فتح القدير ٢/ ٥٠١، القوانين الفقهية ص ٨٧، الكافي لابن عبد البر ص ١٤٥، التلقين ص ٦٩، هداية الغلام ص ٧٠، مغني المحتاج ١/ ٥٠٥، التذكرة ص ٨١، هداية الراغب ص ٢٣١، المبدع ٣/ ٢٦٤.
(¬٥) ٢/ ١٩٨ كتاب المناسك، باب يبيت بمكة ليالي منى، رقم ١٦٥٨.
من طريق يحيى عن ابن جريج، قال: حدثني حرير -أو أبو حريز- الشك من يحيى، أنه سمع عبد الرحمن بن فروخ، يسأل ابن عمر قال: إنا نتبايع بأموال الناس فيأتي أحدنا مكة، فيبيت على المال، فقال: أما رسول الله -صلى الله عليه وسلم- فبات بمنى وظل.
والحديث في صحيح البخاري ٢/ ٦١٧، كتاب الحج، باب الزيارة يوم النحر رقم ١٦٤٥، عن ابن عمر "أنه طاف طوافًا واحدًا، ثم يقبل، ثم يأتي منى، يعني: يوم النحر".
قال البخاري: ورفعه عبد الرزاق.','3','236',NULL,NULL,'0');
INSERT INTO [b46] VALUES (912,'والعمرة سنة مؤكدة.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-466>[حكم العمرة]</span>
قوله: والعمرة سنة مؤكدة (¬١). وقيل: واجبة (¬٢)، وقيل: فرض كفاية (¬٣).
وقال الشافعي في القديم: هي تطوع، وفي الجديد: هي فريضة (¬٤).
ولنا ما روي: "أنه أتى أعرابي رسول الله -صلى الله عليه وسلم- فقال: يا رسول الله: أخبرني عن العمرة أواجبة هي؟ فقال -صلى الله عليه وسلم-: لا، وأن تعتمر خير لك".
قال الترمذي: حديث حسن صحيح (¬٥).
_________
(¬١) وإلى سنيتها، ذهب المالكية، وبه قال: أبو ثور، والنخعي.
المختار ١/ ١٥٧، تحفة الفقهاء ٢/ ٣٩٢، الاختيار ١/ ١٥٧، بدائع الصنائع ٢/ ٢٢٦، المعونة ١/ ٥٠٢، حاشية الدسوقي ٢/ ٢.
(¬٢) وهو القول الجديد للشافعي، والصحيح من مذهبهم، وهو الصحيح أيضًا من مذهب الحنابلة، أنها واجبة. وعنه: أنها سنة. وعنه: أنها تجب على الآفاقي دون المكي.
وبالقول بوجوبها، قال: عمر، وابن عباس، وابن عمر، وجابر، وطاووس، وعطاء، وابن المسيب، وسعيد بن جبير، والحسن البصري، وابن سيرين، والشعبي، ومسروق، وأبو بردة ابن أبي موسى الحضرمي، وعبد الله بن شداد، والثوري، وإسحاق، وابن عبيد، وداود.
(¬٣) تبيين الحقائق ٢/ ٨٣.
(¬٤) المهذب ١/ ١٩٥، المجموع ٧/ ٧.
(¬٥) رواه الترمذي ٣/ ٣٩٨، كتاب الحج، باب ما جاء في العمر، أواجبة هي أم لا؟ رقم ٩٣١، وأحمد ٣/ ٣١٦، وابن خزيمة ٤/ ٣٥٦، كتاب المناسك باب ذكر البيان أن العمرة فرض رقم ٣٠٦٨، والدارقطني ٢/ ٢٨٥ كتاب الحج باب المواقيت ٢٢٣، وأبو يعلى ٣/ ٤٤٣ رقم ١٩٣٨، والبيهقي ٤/ ٣٤٩، كتاب الحج باب من قال العمرة تطوع، وأبو نعيم في الحلية ٨/ ١٨٠، والخطيب في تاريخ بغداد ٨/ ٣٣، والطبراني في الصغير ٢/ ٨٩.
من طريق الحجاج بن أرطاة عن محمد بن المنكدر عن جابر قال ... ".
قال الترمذي: "حسن صحيح". قال ابن القيم في تهذيب السنن ٢/ ٣٣٣: وقد نوقش الترمذي في تصحيحه، فإنه من رواية الحجاج بن أرطاة، وقد ضعف، ولو كان ثقة فهو =','3','237',NULL,NULL,'0');
INSERT INTO [b46] VALUES (913,'وركنها: الطواف. وواجباتها: السَّعيُ، والحلق، أو التقصير.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-467>[واجبات العمرة]</span>
قوله: وركنها. أي: ركن العمرة: الطواف. وشرطها: الإحرام.
وواجباتها: السعي، والحلق، أو التقصير (¬١). وعليه (¬٢) إجماع الأمة (¬٣).
_________
= مدلس كبير، وقد قال: عن محمد، لم يذكر سماعًا. ولا ريب أن هذا قادح في صحة الحديث.
وقال ابن حزم في المحلى ٧/ ٣٧، كتاب الحج مسألة ٨١١: إنه حديث كذب. وقال البيهقي في السنن ٤/ ٣٤٩ كتاب الحج باب من قال العمرة تطوع: المحفوظ عن جابر موقوف غير مرفوع، وقال عن المرفوع: ضعيف.
وقال في التلخيص الحبير ٢/ ٢٢٦: وقد نبه صاحب الإمام على أنه لم يزد على قوله حسن في جميع الروايات إلا في رواية الكروخي فقط، فإن فيها حسن صحيح، وفي تصحيحه نظر كبير، من أجل الحجاج، فإن الأكثر على تضعيفه، والاتفاق على أنه مدلس. وقال أيضًا في التلخيص: والصحيح عن جابر من قوله.
وقال الحافظ في الفتح ٣/ ٥٩٧: ولا يثبت في هذا الباب عن جابر شيء.
وقال في بلوغ المرام ص ١٤٨: رواه أحمد والترمذي والراجح وقفه.
وقال النووي في المجموع ٧/ ٦: قول الترمذي: إن هذا حديث حسن صحيح غير معقول، ولا يغتر بكلام الترمذي في هذا، فقد اتفق الحفاظ على أنه حديث ضعيف.
(¬١) وعند المالكية، والحنابلة: أركان العمرة: الإحرام، والطواف، والسعي. وواجباتها: الحلق أو التقصير، والإحرام من الميقات.
وعند الشافعية: أركان العمرة: الإحرام، والطواف، والسعي، والحلق أو التقصير. وواجباتها: الإحرام من الميقات، واجتناب محرمات الإحرام.
تحفقة الفقهاء ٢/ ٣٩٢، العناية ٢/ ٤٦١، تبيين الحقائق ٢/ ٨٢، الشرح الصغير ١/ ٢٨٤، أقرب المسالك ص ٥١، القوانين الفقهية ص ٨٨، مغني المحتاج ١/ ٥١٣، التذكرة ص ٨٠، متن أبي شجاع ص ١٠٤، نهاية المحتاج ٣/ ٣٢٢، الروض المربع ص ٢١٤، منتهى الإرادات ٢/ ٧٣.
(¬٢) أي: على أن الطواف ركن من أركان العمرة.
(¬٣) مراتب الإجماع لابن حزم ص ٤٧.','3','238',NULL,NULL,'0');
INSERT INTO [b46] VALUES (914,'وميقات الإحرام للمدني: ذو الحليفة، وللعراقي: ذات عِرق، وللشامي: الجحفة، وللنجدي:
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-468>[المواقيت]</span>
قوله: وميقات (¬١) الإحرام (¬٢) إلى آخره (¬٣) لحديث ابن عباس -رضي الله عنهما- أنه -صلى الله عليه وسلم- "وقَّت لأهل المدينة ذا الحليفة (¬٤)، ولأهل الشام الجحفة (¬٥) (¬٦)،
_________
(¬١) الوقت: مقدار من الزمن، وكل شيء قدرت له حينًا فهو مؤقت. والميقات: الوقت المضروب للفعل، والموضع، يقال: هذا ميقات أهل الشام، للموضع الذي يحرمون منه.
لسان العرب ٢/ ١٠٧، مادة وقت، القاموس المحيط ٤/ ٦٤٠ مادة وق ت، مختار الصحاح ص ٣٠٤ مادة وق ت، المصباح المنير ٢/ ٦٦٧ مادة وقت.
(¬٢) المختار ١/ ١٤١، مختصر الطحاوي ص ٦٠، الاختيار ١/ ١٤١، الوقاية ١/ ١٢٧، كشف الحقائق ١/ ١٢٧.
(¬٣) وتمامه: "للمدني ذو الحليفة، وللعراقي ذات عرق، وللشامي الجحفة، وللنجدي قرن، ولليماني يلملم، ولمن جاء من غير هذه المواضع".
(¬٤) ذو الحليفة: -بضم الحاء، وفتح اللام تصغير نبت معروف بتلك المنطقة، يسمى الحلفاء.
وهي قرية بينها وبين المدينة ستة، أو سبعة أميال وتبعد عن مسجد النبي -صلى الله عليه وسلم- ١٣ كيلًا، ومنها إلى مكة المكرمة ٤٢٠ كيلومتر، فهي أبعد المواقيت، وهي ميقات أهل المدينة، ومن مرّ عليها وتسمى اليوم آبار علي.
لسان العرب ٩/ ٥٦ مادة حلف، القاموس المحيط ١/ ٦٩٣ مادة ح ل ف، مختار الصحاح ص ٦٣ مادة ح ل ف، المصباح المنير ١/ ١٤٦ مادة حلف، معجم البلدان ٢/ ٢٩٥، معجم ما استعجم ١/ ٤٦٤، توضيح الأحكام من بلوغ المرام ٣/ ٢٧٥.
(¬٥) في ص، ي في الهامش بزيادة: "ولأهل العراق ذات عرق".
(¬٦) الجحفة -بضم الجيم، وسكون الحاء المهملة، وفتح الفاء بعدها هاء-: كانت قرية عامرة، محطة من محطات الحاج، بين الحرمين، وقد كانت تسمى مهيعة، فجحفتها السيول بأهلها فسميت الجحفة. وهي الآن خراب، ويحرم الناس في قرية تسمى رابغ، الواقعة عنها غربًا ببعد ٢٢ ميلًا. ويحاذي الجحفة من خط الهجرة (الخط السريع) من المدينة باتجاه مكة، الكيل (٢٠٨).
لسان العرب ٩/ ٢١ مادة جحف، القاموس المحيط ١/ ٤٤٩ مادة ج ح ف، مختار الصحاح ص ٤٠ مادة ج ح ف، المصباح المنير ١/ ٩١ مادة أجحف، معجم البلدان ٢/ ١١١، =','3','239',NULL,NULL,'0');
INSERT INTO [b46] VALUES (915,'قرن، ولليماني: يلملم.
ولمن جاء من غير هذه المواضع، ما يحاذي واحدًا منها.
والإحرام من وطنه أفضل، إن وثق من نفسه باجتناب محظوراته،
<hr><span data-type="title">منحة السلوك</span>

ولأهل نجد قرن المنازل (¬١)، ولأهل اليمن يلملم (¬٢)، وقال: فهن لهم، ولمن أتى عليهن من غير أهلهن، لمن كان يريد الحج والعمرة" رواه البخاري، ومسلم (¬٣).
قوله: ولمن جاء من غير هذه المواضع ما يحاذي واحدًا منها. أي: من هذه المواضع (¬٤)؛ لما روينا.
قوله: والإحرام من وطنه أفضل، إن وثق من نفسه باجتناب محظوراته.
_________
= معجم ما استعجم ١/ ٣٦٧، توضيح الأحكام من بلوغ المرام ٣/ ٢٧٥.
(¬١) قرن المنازل: بفتح القاف، وسكون الراء -هو الجبل الصغير؛ ويسمى السيل الكبير، ومسافته من بطن الوادي إلى مكة المكرمة ٧٨ كيلومتر. ويسمى قرن الثعالب. وهي بلدة عند الطائف، أو اسم الوادي كله.
لسان العرب ١٣/ ٣٤١ مادة قرن، القاموس المحيط ٣/ ٦٠٧ مادة ق ر ن، مختار الصحاح ص ٢٢٢ مادة ق ر ن، المصباح المنير ٢/ ٥٠٠ مادة قرن، معجم البلدان ٤/ ٣٣٢، معجم ما استعجم ٢/ ٧٨٨ - ١٠٦٧، توضيح الأحكام من بلوغ المرام ٣/ ٢٧٦.
(¬٢) يلملم -بفتح الياء المثناة التحتية، فلام فميم فلام أخرى- ثم ميم أخرى، وقيل: ألملم: موضع على ليلتين من مكة، وهو ميقات أهل اليمن. وهو واد عظيم ينحدر من جبال السراة، إلى تهامة، ثم يصب في البحر الأحمر. ويبعد عن مكة مسافة ١٢٠ كيلومترًا.
لسان العرب ١٣/ ٥٥٢ مادة لمم، مختار الصحاح ص ٢٥٢ مادة ل م م، معجم البلدان ٥/ ٤٤١، معجم ما استعجم ٢/ ١٣٩٨، توضيح الأحكام من بلوغ المرام ٣/ ٢٧٦.
(¬٣) البخاري ٢/ ٥٥٤ كتاب الحج، باب مهل أهل مكة للحج والعمرة رقم ١٤٥٢، ومسلم ٢/ ٨٣٨ كتاب الحج باب مواقيت الحج والعمرة رقم ١١٨١.
(¬٤) مختصر الطحاوي ص ٦٠، تبيين الحقائق ٢/ ٧، الهداية ١/ ١٤٧، شرح الوقاية ١/ ١٢٧، كشف الحقائق ١/ ١٢٧.','3','240',NULL,NULL,'0');
INSERT INTO [b46] VALUES (916,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

أي: باجتناب محرمات الإحرام (¬١)؛ لقوله تعالى: {وَأَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلَّهِ} [البقرة: ١٩٦] قيل: الإتمام بأن يحرم بها من دويرة (¬٢) أهله (¬٣)، وقال -صلى الله عليه وسلم-: "من أهلَّ من المسجد الأقصى بعمرة، أو بحجة، غفر له ما تقدم من ذنبه" رواه أحمد، وأبو داود بنحوه، وابن ماجه. وذكر فيه العمرة دون الحج (¬٤).
_________
(¬١) وإليه ذهب الشافعية.
وذهب المالكية، والحنابلة: إلى أن الأفضل هو الإحرام من الميقات.
تبيين الحقائق ٢/ ٨، الهداية ١/ ١٤٧، المختار ١/ ١٤١، كشف الحقائق ١/ ١٢٨، الاختيار ١/ ١٤١، الكافي لابن عبد البر ص ١٤٨، القوانين الفقهية ص ٨٨، تحفة المحتاج ٤/ ٤٩، مغني المحتاج ١/ ٤٧٥، روض الطالب ١/ ٤٥٩، مختصر الخرقي ص ٥٤، العمدة ص ٣٤.
(¬٢) دويرة: تصغير دار، وكل موضع حل به قوم فهو دارهم.
تاج العروس ٣/ ٢١٣.
(¬٣) قاله علي بن أبي طالب، وطاووس، وابن جبير. وقيل معنى إتمامها: أن يفصل بينهما، فيأتي بالعمرة في غير أشهر الحج. قاله: عمر بن الخطاب، والحسن، وعطاء.
وقيل: إتمامها أنه إذا شرع في أحدهما لم يفسخه حتى يتم. قاله: ابن عباس.
وقيل: إنه فعل ما أمر الله فيهما. قاله: مجاهد.
زاد المسير في علم التفسير ١/ ١٨٥، تفسير ابن جرير الطبري ٢/ ٢١٢، تفسير النسفي ١/ ١٤٠، تفسير ابن كثير ١/ ٣٤٥.
(¬٤) أحمد ٦/ ٢٩٩، وأبو داود ٢/ ١٤٣ كتاب المناسك، باب في المواقيت رقم ١٧٤١ ولفظه: "من أهل بحجة، أو عمرة من المسجد الأقصى إلى المسجد الحرام، غفر له ما تقدم من ذنبه، وما تأخر، أو وجبت له الجنة" شكَّ عبد الله أيتهما قال.
وابن ماجه ٢/ ٩٩٩ كتاب المناسك، باب من أهل بعمرة من بيت المقدس رقم ٣٠٠١ ولفظه: "من أهل بعمرة من بيت المقدس غفر له"، وابن حبان ٩/ ١٣ كتاب الحج، باب ذكر مغفرة الله ما تقدم من ذنوب العبد بالعمرة، إذا اعتمرها من المسجد الأقصى رقم ٣٧٠١، والطبراني ٢٣/ ٣٦١ رقم ٨٤٩، والدارقطني ٢/ ٢٨٣ كتاب الحج، باب المواقيت، وأبو يعلى في مسنده ١٢/ ٣٢٧ رقم ٦٩٠٠، وابن أبي شيبة في الجزء المكمل =','3','241',NULL,NULL,'0');
INSERT INTO [b46] VALUES (917,'ولا يجوز لهؤلاء
<hr><span data-type="title">منحة السلوك</span>

قوله: ولا يجوز لهؤلاء. أي: لأهل المدينة، والشام، والعراق،
_________
= ٨١، والبيهقي ٥/ ٣٠ كتاب الحج، باب فضل من أهل من المسجد الأقصى إلى المسجد الحرام. عن أم سلمة -رضي الله عنها-.
قال المنذري في تهذيب سنن أبي داود ١٦٦٦: "اختلف الرواة في متنه، وفي إسناده اختلافًا كثيرًا.
فأما الاختلاف في السند: فقد رواه أبو داود، وعنه البيهقي، وكذا الدارقطنى، من طريق ابن أبي فديك، عن عبد الله بن عبد الرحمن بن يحنس، عن يحيى بن أبي سفيان، عن جدته حكيمة، عن أم سلمة، ثم رواه الدارقطني: عن الواقدي، عن عبد الله، عن يحيى، عن أمه، عن أم سلمة، ورواه أحمد، وابن حبان، والدارقطني من طريق ابن إسحاق، عن سليمان بن سحيم، عن يحيى، عن أمه أم حكيم، ورواه ابن ماجه عن ابن إسحاق، عن يحيى عن أمه، فأسقط ابن سحيم، ورواه عن ابن إسحاق، عن ابن سحيم، عن أم حكيم، فأسقط يحيى، ورواه أحمد عن ابن لهيعة، عن جعفر بن ربيعة، عن عبد الله بن عبد الرحمن بن أبي صعصعة، عن أم حكيم.
وأما الاختلاف في المتن فبعضهم قال: "من أهل بحجة، أو عمرة، من المسجد الأقصى، إلى المسجد الحرام". وقال بعضهم: "من أهل بعمرة من بيت المقدس" ولم يذكر بعضهم "حجًا ولا عمرة" وقال بعضهم: "غفر له ما تقدم من ذنبه، وما تأخر، ووجبت له الجنة"، وقال بعضهم: "كان من ذنوبه كيوم ولدته أمه" وبعضهم قال: "كانت له كفارة لما قبلها من الذنوب" ا. هـ. ورمز له السيوطي في الجامع الصغير ص ٥٢٠ رقم ٨٥٤٤ بالضعف.
وقال في المجموع ٧/ ٢٠٠: حديث أم سلمة رواه أبو داود، وابن ماجه، والبيهقي، وآخرون، وإسناده ليس بالقوي.
وقال البخاري في تاريخه ١/ ١٦١: لا يثبت ذكره في ترجمة محمد بن عبد الرحمن بن يحنس. وقال: حديثه في الإحرام من بيت المقدس لا يثبت.
لفظ أحمد: "من أهلّ من المسجد الأقصى بعمرة، أو بحجة، غفر الله له ما تقدم من ذنبه".
ولفظ الدارقطني: "من أهلّ بحجة، أو عمرة من بيت المقدس، غفر له ما تقدم من ذنبه".
ولفظ البيهقي: "من أهلّ بحجة، أو عمرة من المسجد الأقصى إلى المسجد الحرام، غفر =','3','242',NULL,NULL,'0');
INSERT INTO [b46] VALUES (918,'إذا قصدوا دخول مكة لحج أو غيره، تأخير الإحرام عنها.
<hr><span data-type="title">منحة السلوك</span>

ونحوهم، إذا قصدوا دخول مكة لحج، أو غيره، مثل التجارة، أو زيارة أحد، تأخير الإحرام عنها. أي: عن هذه المواقيت (¬١)؛ لقوله -صلى الله عليه وسلم-: "لا يدخل أحد مكة إلا بإحرام" (¬٢).
_________
= له ما تقدم من ذنبه وما تأخر، أو وجبت له الجنة". شك عبد الله.
ولفظ ابن حبان: "من أهلّ من المسجد الأقصى بعمرة، غفر له ما تقدم من ذنبه".
ولفظ أبي يعلى: "من أهلّ بعمرة من بيت المقدس، غفر له". وفي لفظ له: "من أهلّ بحجة، أو عمرة من المسجد الأقصى إلى المسجد الحرام، غفر له ما تقدم من ذنبه وما تأخر، أو وجبت له الجنة".
وفي لفظ له: "من أهلّ من المسجد الأقصى بعمرة وبحجة، غفر ما تقدم من ذنبه".
(¬١) وإليه ذهب المالكية، والحنابلة.
وعند الشافعية: من كان مريدًا للنسك، لا يجوز له تجاوز هذه المواقيت إلا بإحرام، ومن لا، فلا.
مختصر الطحاوي ص ٣٩٤، الهداية ١/ ١٤٧، الاختيار ١/ ١٤١، كشف الحقائق ١/ ١٢٧، مختصر خليل ص ٨٠، جواهر الإكليل ١/ ١٧٠، المعونة ١/ ٥١١، التفريع ١/ ٣١٩، أسنى المطالب ١/ ٤٦٠، روضة الطالبين ٣/ ٣٩، الحاوي ٤/ ٧٤، نهاية المحتاج ٣/ ٢٦١، الإقناع للحجاوي ٢/ ٤٠٢، المقنع ١/ ٣٩٤.
(¬٢) رواه ابن أبي شيبة في مصنفه ٣/ ٤١١ كتاب الحج باب من قال لا يجاوز أحد الوقت إلا محرم رقم ١٥٤٦٣ من طريق عبد السلام بن حرب، عن خصيف، عن سعيد بن جبير أن النبي -صلى الله عليه وسلم- قال: "لا يتجاوز أحد الوقت إلا المحرم".
وخصيف، قال عنه الإمام أحمد: ليس بحجة، ولا قوي في الحديث. وقال عنه النسائي: ليس بالقوي. كما أن الحديث مرسل فسعيد بن جبير لم يدرك النبي -صلى الله عليه وسلم-.
وأخرجه الشافعي في الأم ١/ ١٥١ موقوفًا على ابن عباس. قال ابن حجر في الدراية ٢/ ٦ عن الموقوف: إسناده صحيح.
تهذيب الكمال ٨/ ٢٥٨، المجروحين لابن حبان ١/ ٢٨٧، الكاشف ١/ ٢٨٠، تهذيب التهذيب ٣/ ١٤٣.','3','243',NULL,NULL,'0');
INSERT INTO [b46] VALUES (919,'وأهل هذه المواضع ومن دونهم ميقاتهم: الحلُّ الذي بينهم وبين الحرم.
وللمكي، ميقاته للحج: الحرَمُ. وللعمرة: الحل أفضل.
<hr><span data-type="title">منحة السلوك</span>

قوله: ولأهل هذه المواضع. أي: ولأصحاب هذه المواقيت، ومن دونهم ميقاتهم: الحل الذي بينهم وبين الحرم؛ لأن خارج الحرم كله كمكان واحد في حقهم، والحرم في حقهم كالميقات في حق الآفاقي، فلا يدخل إلا محرمًا (¬١).

<span data-type="title" id=toc-469>[ميقات المكي]</span>
قوله: وللمكي ميقاته للحج: الحرم، وللعمرة: الحل بالإجماع (¬٢).
_________
(¬١) وذهب المالكية، والشافعية، والحنابلة: إلى أن من كان منزله دون هذه المواقيت، فميقاته من منزله.
مختصر الطحاوي ص ٣٩٤، المختار ١/ ١٤١، الهداية ١/ ١٤٧، مواهب الجليل ٣/ ٣٤، التاج والإكليل ٣/ ٣٤، الخرشي على خليل ٢/ ٣٠١، حاشية العدوي ٢/ ٣٠١، شرح المحلي على المنهاج ٢/ ٩٤، قليوبي ٢/ ٩٤، الروض المربع ص ١٩٠، منتهى الإرادات ٢/ ٩، المقنع ٢/ ٣٩٦، كشاف القناع ٢/ ٤٠٢.
(¬٢) مراتب الإجماع ص ٤٢.','3','244',NULL,NULL,'0');
INSERT INTO [b46] VALUES (920,'<span data-type="title" id=toc-470>فصل</span>
إذا أراد الإحرام: قَصَّ شاربَهُ، وقَلَّم أظفاره، وحلق عانته، ثم توضأ، أو اغتسل، وهو أفضل،
<hr><span data-type="title">منحة السلوك</span>

فصل
هذا الفصل في بيان كيفية الإحرام.

<span data-type="title" id=toc-471>[مستحبات الإحرام]</span>
قوله: إذا أراد الإحرام قص شاربه، وقلَّم أظفاره، وحلق عانته.
وهذا مستحب (¬١)، كما استُحِبَّ استعمال الطيب (¬٢)، وكذلك ينتف إبطه، ويسرح رأسه (¬٣) (¬٤).
قوله: ثم توضأ، أو اغتسل، وهو أفضل.
أي: الغسل أفضل (¬٥)؛ لما روى زيد بن ثابت -رضي الله عنه- "أنه -صلى الله عليه وسلم- اغتسل لإحرامه" رواه الدارقطني، والترمذي، وقال: حديث حسن (¬٦).
_________
(¬١) المختار ١/ ١٤٣، تبيين الحقائق ٢/ ٩، بداية المبتدي ١/ ١٤٨، الاختيار ١/ ١٤٣، الهداية ١/ ١٤٨.
(¬٢) المختار ١/ ١٤٣، بداية المبتدي ١/ ١٤٨، تبيين الحقائق ٢/ ٩، الهداية ١/ ١٤٨، الأصل ٢/ ٢٩٣.
(¬٣) تسريح الشعر: ترجيله، وتخليص بعضه من بعض بالمشط.
لسان العرب ٤٧٨٢ مادة سرح، القاموس المحيط ٢/ ٥٤٥ مادة س رح، مختار الصحاح ص ١٢٤ مادة س ر ح، المغرب ص ٢٢٣ مادة السرح.
(¬٤) تبيين الحقائق ٢/ ٩، الهداية ١/ ١٤٨، الاختيار ١/ ١٤٣.
(¬٥) وفاقًا للثلاثة.
الأصل ٢/ ٢٩٣، كنز الدقائق ٢/ ٨، الهداية ١/ ٤٨، الاختيار ١/ ١٤٣، أسهل المدارك شرح إرشاد السالك ١/ ٤٥٧، سراج السالك شرح أسهل المسالك ١/ ٢٢٨، عمدة السالك وعدة الناسك ص ٢٣٥، التذكرة ص ٨٢، التسهيل ص ٩٥، العمدة ص ٣٥.
(¬٦) الدارقطني ٢/ ٢٢٠ كتاب الحج، والترمذي ٢/ ١٧٨ كتاب الحج، باب ما جاء في =','3','245',NULL,NULL,'0');
INSERT INTO [b46] VALUES (921,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

والمراد بهذا الغسل: تحصيل النظافة، وإزالة الرائحة لا الطهارة، حتى تؤمر به الحائض والنفساء (¬١)؛ لما روي عن ابن عباس -رضي الله عنهما- أنه -صلى الله عليه وسلم- قال: "أن النفساء، والحائض، تغتسل، وتحرم، وتقضي المناسك كلها، غير أن لا تطوف بالبيت" رواه أبو داود، والترمذي (¬٢).
_________
= الاغتسال عند الإحرام رقم ٨٣٠، والدارمي ١/ ٤٥٨ كتاب المناسك، باب الاغتسال في الإحرام رقم ١٧٤٠، والبيهقي ٥/ ٣٢ كتاب الحج باب الغسل للإهلال، وابن المنذر في الإقناع ١/ ٢٠٦ رقم ٧١ في الحج، باب الاغتسال عند الإحرام، وابن خزيمة ٤/ ١٦١ كتاب المناسك، باب استحباب الاغتسال للإحرام رقم ٢٥٩٥، والطبراني في الكبير ٤٨٦٢.
من طريق أبي غزية، عن عبد الرحمن بن أبي الزناد، عن أبيه، عن خارجة بن زيد بن ثابت، عن أبيه، أن رسول الله -صلى الله عليه وسلم- ... ".
قال البيهقي في السنن ٥/ ٣٢: أبو غزية محمد بن موسى ليس بالقوي.
وقال الدارقطني ٢/ ٢٢٠: قال ابن صاعد: هذا حديث غريب ما سمعناه إلا منه.
وقال الترمذي ٣/ ١٧٨: هذا حديث حسن غريب.
(¬١) وفاقًا للثلاثة.
تبيين الحقائق ٢/ ٨، شرح فتح القدير ٢/ ٤٠٣، سراج السالك شرح أسهل المسالك ١/ ٢٢٨، الكافي لابن عبد البر ص ١٤٩، القوانين الفقهية ص ٨٨، أنوار المسالك ص ٢٣٥، الوجيز ١/ ١١٧، الروض المربع ص ١٩١، المبدع ٣/ ١١٦.
(¬٢) أبو داود ٢/ ١٤٤ كتاب المناسك، باب الحائض تهل بالحج رقم الحديث ١٧٤٤، والترمذي ٣/ ٣١٤ كتاب الحج، باب ما جاء ما تقضي الحائض من المناسك رقم الحديث ٩٤٥، وأحمد ١/ ٣٦٤، والبيهقي في السنن الكبرى ٥/ ١٦٣ كتاب الحج، باب ترك الحائض الوداع، والطبراني في الصغير ١/ ١٥١ رقم ٣٥٩.
قال الترمذي: حديث حسن غريب من هذا الوجه. وسكت عنه أبو داود.
وقال الطبراني في الصغير ١/ ١٥١: لم يروه عن خصيف إلا مروان بن شجاع، وهو لا بأس به.
وقال أحمد شاكر في تعليقه على المسند ٥/ ١٤٥: إسناده صحيح.','3','246',NULL,NULL,'0');
INSERT INTO [b46] VALUES (922,'ولبس إزارًا ورداءًا، جديدين، أبيضين،
<hr><span data-type="title">منحة السلوك</span>

قوله: ولبس إزارًا، ورداءًا (¬١). لأنه -صلى الله عليه وسلم- "لبسهما هو، وأصحابه" رواه مسلم (¬٢).
قوله: جديدين. لأن الجديد أفضل؛ لأنه أنظف؛ لأنه لم تركبه النجاسة (¬٣).
قوله: أبيضين (¬٤).
لقوله -صلى الله عليه وسلم-: "خير ثيابكم البيض، فالبسوا" رواه ابن ماجه (¬٥).
_________
(¬١) الأصل ٢/ ٢٩٣، بداية المبتدي ١/ ١٤٨، كنز الدقائق ٢/ ٨، الهداية ١/ ١٤٨.
(¬٢) لم أجده في مسلم وإنما رواه البخاري ٢/ ٥٦٠ كتاب الحج باب ما يلبس المحرم من الثياب والأردية والأزر رقم ١٤٧٠ وكذا عزاه الزيلعي في نصب الراية ٣/ ١٨ إلى البخاري فقط دون مسلم، وكذا ابن حجر في الدراية ٢/ ٧ عزاه إلى البخاري، دون مسلم.
وتمامه: عن ابن عباس -رضي الله عنهما- قال: انطلق النبي -صلى الله عليه وسلم- من المدينة، بعد ما ترجل، وادهن، ولبس إزاره، ورداءه، هو وأصحابه، ولم ينه عن شيء من الأزر، والأردية يلبس إلا المزعفر، التي تردع على الجلد.
(¬٣) الأصل ٢/ ٢٩٣، بداية المبتدي ١/ ١٤٨، تبيين الحقائق ٢/ ٩، العناية ٢/ ٤٣٠، شرح فتح القدير ٢/ ٤٣٠، الاختيار ١/ ١٤٣.
(¬٤) المختار ١/ ١٤٣، تبيين الحقائق ٢/ ٩، الاختيار ١/ ١٤٣، الهداية ١/ ١٤٨.
(¬٥) ١/ ٤٧٣ كتاب الجنائز، باب ما جاء فيما يستحب من الكفن رقم ١٤٧٢ بلفظ: "خير ثيابكم البياض فكفنوا فيها موتاكم والبسوها"، ورواه أيضًا الإمام أحمد ١/ ٢٤٧، وأبو داود ٤/ ٥١ كتاب اللباس، باب في البياض رقم ٤٠٦١، والترمذي ٤/ ٣٧٥ كتاب الجنائز، باب ما يستحب من الأكفان رقم ٩٩٤، والنسائي ٤/ ٣٤ كتاب الجنائز، باب أي الكفن خير رقم ١٨٩٦، وأبو يعلى ٤/ ٣٠٠ رقم ٢٤١٠، والحميدي ١/ ٢٤٠ رقم ٥٢٠، والطيالسي ص ١٢١ رقم ٨٩٤، والطبراني في الكبير ١١٢٠١ - ١٢٤٢٧ - ١٢٤٨٥ - ١٢٤٩٣، والبيهقي ٣/ ٢٤٥ كتاب الجمعة، باب خير ثيابكم البياض، والشافعي في المسند ٢٦٨، والحاكم ١/ ٣٥٤ كتاب الجنائز. =','3','247',NULL,NULL,'0');
INSERT INTO [b46] VALUES (923,'وهو أفضل، أو غسيلين، وتطيب،
<hr><span data-type="title">منحة السلوك</span>

قوله: وهو أفضل.
أي: الجديد الأبيض أفضل من العتيق، ومن غير الأبيض (¬١)؛ لما ذكرنا.
قوله: وتطيب (¬٢).
لما روي عن عائشة -رضي الله عنها- قالت: "كنت أطيب رسول الله -صلى الله عليه وسلم- لإحرامه قبل أن يحرم، ولإحلاله قبل أن يطوف بالبيت" رواه مالك في الموطأ، والبخاري، وأبو داود (¬٣).
_________
= كلهم من طريق عبد الله بن عثمان بن خثيم، عن سعيد بن جبير، عن ابن عباس. سوى النسائي، والطيالسي عن سمرة بن جندب -رضي الله عنه-.
قال الحاكم: صحيح على شرط مسلم ولم يخرجاه.
وقال الترمذي ٣/ ٣٧٦: حسن صحيح.
وقال النووي في المجموع ٧/ ٢١٥: حديث صحيح، رواه أبو داود، والترمذي، وغيرهما بأسانيد صحيحة.
(¬١) وفاقًا للمالكية، والشافعية، والحنابلة.
المختار ١/ ١٤٣، الهداية ١/ ١٤٨، تبيين الحقائق ٢/ ٩، الاختيار ١/ ١٤٣، أسهل المدارك شرح إرشاد السالك ١/ ٤٥٨، المعونة ١/ ٥٢٨، أسنى المطالب ١/ ٤٧٣، نهاية المحتاج ٣/ ٢٧٢، مغني المحتاج ١/ ٤٨٠، الكافي لابن قدامة ١/ ٣٩١، مطالب أولي النهى ٢/ ٣٠٤.
(¬٢) وإليه ذهب الشافعية، والحنابلة.
وعند المالكية: لا يتطيب قبل الغسل، ولا بعده بما تبقى رائحته.
الكتاب ١/ ١٨٠، بداية المبتدي ١/ ١٤٨، الهداية ١/ ١٤٨، تبيين الحقائق ٢/ ٩، القوانين الفقهية ص ٨٨، بداية المجتهد ١/ ٣٢٨، الذخيرة ٣/ ٢٢٣، الوجيز ١/ ١١٧، روض الطالب ١/ ٤٧٢، الإفصاح ١/ ٢٧٠، غاية المنتهى ٢/ ٣٠٣، المحرر ١/ ٢٣٦.
(¬٣) مالك في الموطأ ١/ ٣٢٨ كتاب الحج، باب ما جاء في الطيب في الحج رقم ١٧، والبخاري ٢/ ٥٥٨ كتاب الحج، باب الطيب عند الإحرام، وما يلبس إذا أراد أن يحرم =','3','248',NULL,NULL,'0');
INSERT INTO [b46] VALUES (924,'وادَّهن إن وجد، وصلى ركعتين،
<hr><span data-type="title">منحة السلوك</span>

قوله: وادهن (¬١).
لما روي عن عائشة -رضي الله عنها- قالت: "كان رسول الله -صلى الله عليه وسلم- إذا أراد أن يحرم تطيب بأطيب ما يجد، ثم أرى وبيص (¬٢) الدهن في رأسه، ولحيته بعد ذلك" رواه مسلم (¬٣).
قوله: إن وجد. قيد للطيب، والدهن جميعًا (¬٤).
قوله: وصلى ركعتين.
يعني: بعد اللبس، والتطيب (¬٥)؛ "لأنه -صلى الله عليه وسلم- صلى ركعتين" رواه مسلم، والبخاري (¬٦).
_________
= رقم ١٤٦٥، وأبو داود ٢/ ١٤٤ كتاب المناسك، باب الطيب عند الإحرام رقم ١٧٤٥.
(¬١) الأصل ٢/ ٢٩٣، شرح فتح القدير ٢/ ٤٣٠، العناية ٢/ ٤٣٠.
(¬٢) الوبيص: البريق، وهو اللمعان.
تاج العروس ٤/ ٤٤٤ مادة وبص، لسان العرب ٣/ ١٠٤ مادة وبص، القاموس المحيط ٤/ ٥٦٦ مادة وب ص، فقه اللغة وسر العربية للثعالبي ص ٣٣٢، المصباح المنير ٢/ ٦٤٦ مادة الوبيص.
(¬٣) ٢/ ٨٤٨ كتاب الحج، باب الطيب للمحرم عند الإحرام رقم ١١٩٠.
(¬٤) بداية المبتدي ١/ ١٤٨، المختار ١/ ١٤٣، الهداية ١/ ١٤٨، الاختيار ١/ ١٤٣.
(¬٥) وكذا عند المالكية، والشافعية، والحنابلة: يسن إحرامه عقب صلاة فرض، أو ركعتين نفلًا.
بداية المبتدي ١/ ١٤٨، الكتاب ١/ ١٨١، مواهب الجليل ٣/ ١٠١، الشرح الصغير ١/ ٢٦٩، الوجيز ١/ ١١٧، روض الطالب ١/ ٤٧٣، منتهى الإرادات ٢/ ١٢، الفروع ٣/ ٢٩٣.
(¬٦) البخاري ٢/ ٥٦٣ كتاب الحج، باب الإهلال مستقبل القبلة برقم ١٤٧٩ عن نافع قال: "كان ابن عمر إذا أراد الخروج إلى مكة، ادهن بدهن ليس له رائحة طيبة، ثم يأتي مسجد ذي الحليفة فيصلي، ثم يركب، وإذا استوت به راحلته قائمة أحرم، ثم قال هكذا رأيت =','3','249',NULL,NULL,'0');
INSERT INTO [b46] VALUES (925,'ويسأل الله التيسير، ثم لبَّى ناويًا نسكه، رافعًا صوته.
<hr><span data-type="title">منحة السلوك</span>

قوله: ويسأل الله التيسير (¬١)؛ لأنه الميسر لكل عسير (¬٢).
قوله: ثم لبى ناويًا نسكه.
أي: حال كونه ناويًا بالتلبية حجة (¬٣)، لما روي عن أنس -رضي الله عنه- "أنه -صلى الله عليه وسلم- صلى الظهر؛ ثم ركب راحلته، فلما علا على جبل البيداء أهلَّ" رواه أبو داود (¬٤)، قيد بقوله: "ناويًا"؛ لأن النية شرط لجميع العبادات (¬٥).
قوله: رافعًا صوته (¬٦).
لقوله -صلى الله عليه وسلم-: "جاءني جبريل، فقال: يا محمد، مُرْ أصحابك فليرفعوا
_________
= النبي -صلى الله عليه وسلم- يفعل". ورواه مسلم بالنسبة لما يفيد الصلاة في ٢/ ٨٤٦ كتاب الحج، باب الصلاة في مسجد ذي الحليفة برقم ١١٨٨ قال: "بات رسول الله -صلى الله عليه وسلم- بذي الحليفة مبدأه وصلى في مسجدها".
ورواه مسلم بالنسبة لما يفيد الطيب في ٢/ ٨٤٦ كتاب الحج، باب الطيب للمحرم عند الإحرام برقم ١١٨٩ عن عائشة -رضي الله عنها- قالت: "طيبت رسول الله -صلى الله عليه وسلم- لحرمه حين أحرم، ولحله قبل أن يطوف".
(¬١) بداية المبتدي ١/ ١٤٨، المختار ١/ ١٤٣.
(¬٢) تبيين الحقائق ٢/ ٩، الهداية ١/ ١٤٨، بداية المبتدي ١/ ١٤٨، المختار ١/ ١٤٣، الاختيار ١/ ١٤٣.
(¬٣) بداية المبتدي ١/ ١٤٨، الكتاب ١/ ١٨١، الهداية ١/ ١٤٨، الاختيار ١/ ١٤٣.
(¬٤) ٢/ ١٥١ كتاب المناسك، باب في وقت الإحرام رقم ١٧٧٤، ورواه أيضًا البخاري ٢/ ٥٦٢ كتاب الحج، باب التحميد والتسبيح والتكبير قبل الإهلال رقم ١٤٧٦.
(¬٥) شرح فتح القدير ٢/ ٤٣٤، المبسوط ٤/ ١٨٧.
(¬٦) وإليه ذهب الشافعية، والحنابلة.
وعند المالكية: يندب توسط في رفع صوته بها، أما المرأة فإنها تخفض صوتها بالتلبية.
بدائع الصنائع ٢/ ١٤٥، تحفة الفقهاء ١/ ٤٠١، مختصر خليل ص ٨٣، أقرب المسالك =','3','250',NULL,NULL,'0');
INSERT INTO [b46] VALUES (926,'والتلبية معروفة.
<hr><span data-type="title">منحة السلوك</span>

أصواتهم بالتلبية، فإنها من شعار الحج" رواه ابن ماجه (¬١).
قوله: والتلبية معروفة.
وهي: "لبيك اللهم لبيك، لبيك لا شريك لك لبيك، إن الحمد والنعمة لك والملك، لا شريك لك" كذا حكى ابن عمر تلبية النبي -صلى الله عليه وسلم- متفق عليه (¬٢).
واختلفوا في الداعي؟
_________
= ص ٤٧، نهاية المحتاج ٣/ ٢٧٣، عمدة السالك وعدة الناسك ص ٢٣٦، السلسبيل ١/ ٣٦٧، حاشية العنقري على الروض المربع ١/ ٤٧٢.
(¬١) ٢/ ٩٧٥ كتاب المناسك، باب رفع الصوت بالتلبية رقم ٢٩٢٣، ورواه أيضًا الشافعي في ترتيب المسند ١/ ٣٠٦ كتاب الحج، الباب الرابع فيما يلزم المحرم عند تلبسه بالإحرام رقم ٧٩٤، والبيهقي في السنن الكبرى ٥/ ٤١ كتاب الحج، باب رفع الصوت بالتلبية، وموطأ محمد بن الحسن الشيباني ص ١٣٦ كتاب الحج، باب رفع الصوت بالتلبية رقم ٣٩٢، والحاكم في المستدرك ١/ ٤٥٠ كتاب المناسك، وأحمد ٥/ ١٩٢، وابن حبان في صحيحه ٩/ ١١٢ كتاب الحج، باب الإحرام رقم ٣٨٠٣، وابن خزيمة في صحيحه ٤/ ١٧٤ كتاب المناسك، باب البيان أن رفع الصوت بالإهلال من شعار الحج رقم ٢٦٢٨، والطبراني في الكبير ٥/ ٥١٦٨ - ٥١٧٣، وابن أبي شيبة في المصنف ٣/ ٣٧٣ كتاب الحج، باب من كان يرفع صوته بالتلبية رقم ١٥٠٥٥.
من طريق خلاد بن السائب، عن زيد بن خالد الجهني، عن رسول الله -صلى الله عليه وسلم- قال ... ".
قال ابن حبان في صحيحه ٦/ ١١٣: سمع هذا الخبر خلاد بن السائب من أبيه، ومن زيد بن خالد الجهني، ولفظاهما مختلفان، وهما طريقان محفوظان.
وقال الحاكم في المستدرك ١/ ٤٥٠ بعد أن ساق أسانيد الحديث: هذه الأسانيد كلها صحيحة.
(¬٢) البخاري ٢/ ٥٦١ كتاب الحج، باب التلبية رقم ١٤٧٤، ومسلم ٢/ ٨٤١ كتاب الحج، باب التلبية وصفتها ووقتها رقم ١١٨٤.
عن ابن عمر -رضي الله عنهما- قال: كانت تلبية النبي -صلى الله عليه وسلم- فذكره. . . .','3','251',NULL,NULL,'0');
INSERT INTO [b46] VALUES (927,'وهي مرَّة شرط،
<hr><span data-type="title">منحة السلوك</span>

فقيل: هو الله تعالى. وقيل: هو رسول الله (¬١). والأظهر أنه الخليل -صلى الله عليه وسلم- (¬٢).
ومعناها: أقيم في طاعتك إقامة بعد إقامة، من ألبَّ بالمكان، ولبَّ به إذا أقام ولزمه، ولم يفارقه (¬٣).

<span data-type="title" id=toc-472>[حكم التلبية]</span>
قوله: وهي. أي: التلبية مرة شرط (¬٤).
لقوله تعالى: {فَمَنْ فَرَضَ فِيهِنَّ الْحَجَّ} [البقرة: ١٩٧] قال ابن عباس: فرض الحج الإهلال (¬٥). وقال ابن عمر: التلبية (¬٦)، ولأن الحج يشتمل على أركان، فوجب أن يشترط في تحريمه ذِكرٌ يُراد به التعظيم كالصلاة (¬٧).
وعن أبي يوسف: يصير شارعًا بالنية وحدها من غير تلبية (¬٨). وبه قال: الشافعي (¬٩).
_________
(¬١) تبيين الحقائق ٢/ ١٠.
(¬٢) تبيين الحقائق ٢/ ١٠، العناية ٢/ ٤٣٥.
(¬٣) لسان العرب ١/ ٧٣١ مادة لبب، القاموس المحيط ٣/ ١١٤ مادة ل ب ب، مختار الصحاح ص ٢٤٦ مادة ل ب ب، المغرب ص ٤١٩ مادة التلبية، المصباح المنير ٢/ ٥٤٧ مادة لُبُّ.
(¬٤) شرح فتح القدير ٢/ ٤٤٦، تبيين الحقائق ٢/ ١٤، حاشية الشلبي ٢/ ١٤.
(¬٥) رواه ابن أبي شيبة في المصنف ٤/ ٢٣١ رقم ١٥٠٤ بلفظ: "فمن فرض فيهن الحج قال التلبية"، وذكره ابن جرير الطبري في تفسيره ١/ ٣٩٤، والسيوطي في الدر المنثور ١/ ٢١٨.
(¬٦) أخرجه أبي شيبة في المصنف ٤/ ٢٢٩ برقم ١٤٩٢ قال ثنا وكيع، عن شريك، عن إبراهيم بن مهاجر، عن مجاهد، عن ابن عمر، وذكره ابن جرير الطبري في تفسيره ١/ ٣٩٤.
(¬٧) تبيين الحقائق ٢/ ١١، العناية ٢/ ٤٣٧، شرح فتح القدير ٢/ ٤٣٩.
(¬٨) تبيين الحقائق ٢/ ١١، شرح فتح القدير ٢/ ٤٣٩، العناية ٢/ ٤٣٧.
(¬٩) وأحمد. حيث يريان أن التلبية سنة. =','3','252',NULL,NULL,'0');
INSERT INTO [b46] VALUES (928,'والزيادة سنة.
<hr><span data-type="title">منحة السلوك</span>

قوله: والزيادة سنة.
أي: الزيادة على مرة سنة؛ لأنها ذكر وتعظيم (¬١). ويصلي على النبي -صلى الله عليه وسلم- بعد التلبية (¬٢)؛ لما روي عن القاسم بن محمد بن أبي بكر (¬٣) أنه قال: "كان يستحب للرجال الصلاة على النبي -صلى الله عليه وسلم- بعد التلبية" رواه أبو داود، والدارقطني (¬٤).
_________
= وذهب المالكية: إلى وجوب التلبية، فإن أخل بها جملة فعليه دم، ويكفي منها مرة واحدة، وما زاد على ذلك فمستحب.
منح الجليل ٢/ ٢٦٢، المعونة ١/ ٥٢٢، بداية المجتهد ١/ ٣٣٧، الوجيز ١/ ١١٦، حاشية الشرواني على تحفة المحتاج ٤/ ٥٠، الإنصاف ١/ ٢٦٨، الإفصاح ٣/ ٤٥٢.
(¬١) تبيين الحقائق ٢/ ١٤، شرح فتح القدير ٢/ ٤٤٦، العناية ٢/ ٤٤٦.
(¬٢) وفاقًا للشافعية، والحنابلة.
وعند المالكية: يستحب للملبي الاقتصار على التلبية فقط: "لبيك اللهم لبيك لبيك لا شريك ... " إلى "لا شريك لك".
شرح فتح القدير ٢/ ٤٣٩، تبيين الحقائق ٢/ ١١، أسهل المدارك ٢/ ٤٥٨، سراج السالك شرح أسهل المسالك ١/ ٢٢٩، أنوار المسالك ص ٢٣٧، المنهاج ١/ ٥٧٩، الإقناع للحجاوي ٢/ ٣٧٨، هداية الراغب ص ٢١٣.
(¬٣) هو القاسم بن محمد بن أبي بكر الصديق، القرشي التيمي، من خيار التابعين وفقهائهم، كان ثقة، عالمًا، إمامًا، فقيهًا، ورعًا، أحد الفقهاء السبعة، كان كثير الصمت. توفي سنة ١٠١ هـ.
تهذيب الكمال ٢٣/ ٤٢٧، الثقات لابن حبان ٥/ ٣٠٢، تذكرة الحفاظ ١/ ٩٦، شذرات الذهب ١١/ ٦٢.
(¬٤) رواه الدارقطني في سننه ٢/ ٢٣٨ كتاب الحج، باب المواقيت رقم ١١، والبيهقي ٥/ ٤٦ كتاب الحج، باب ما يستحب من القول في أثر التلبية، والشافعي في الأم ٢/ ١٧٢ كتاب الحج، باب ما يستحب من القول في أثر التلبية، من طريق عبد الله بن عبد الله الأموي، أنه سمع صالح بن محمد بن زائدة قال: سمعت القاسم بن محمد يقول ... ".
وعزاه صاحب القرى ص ١٧٨ إلى الدارقطني، وأبي ذر. وعزاه السخاوي في القول =','3','253',NULL,NULL,'0');
INSERT INTO [b46] VALUES (929,'ويتقي المحرم: الرفث، والفسوق، والجدال،
<hr><span data-type="title">منحة السلوك</span>

وعن خزيمة بن ثابت -رضي الله عنه- (¬١) عن النبي -صلى الله عليه وسلم-: "أنه كان إذا فرغ من التلبية، سأل رضوانه والجنة، واستعاذ برحمته من النار" رواه الدارقطني (¬٢).

<span data-type="title" id=toc-473>[محظورات الإحرام ومكروهاته]</span>
قوله: ويتقي المحرم أي: يجتنب المحرم: الرفث، والفسوق، والجدال (¬٣)؛ لقوله تعالى: {فَلَا رَفَثَ وَلَا فُسُوقَ وَلَا جِدَالَ} [البقرة: ١٩٧].
_________
= البديع ص ٢٩٩ إلى الدارقطني، والشافعي، وإسماعيل القاضي، وقال: سنده ضعيف.
ولم أجده عند أبي داود، ولعله تصحف من الناسخ من أبي ذر، إلى أبي داود، حيث رواه أبو ذر، كما عزاه إليه صاحب القرى ص ١٧٨، وإسماعيل القاضي في كتابه أفضل الصلاة على النبي ص ٧٠ رقم ٧٩.
قال في التعليق المغني على سنن الدارقطني ٢/ ٢٣٨: وفيه صالح بن محمد، وهو مديني ضعيف.
(¬١) هو خزيمة بن ثابت بن الفاكه بن ثعلبة الأنصاري، صحابي من أشراف الأوس في الجاهلية والإسلام، ومن شجعانهم المقدمين، يقال له: ذو الشهادتين، شهد أحدًا وما بعدها. قتل في صفين سنة ٣٧ هـ.
الإصابة ١/ ٤٢٥، صفة الصفوة ١/ ٧٠٢، تهذيب الكمال ٨/ ٢٤٣.
(¬٢) ٢/ ٢٣٨ كتاب الحج، باب المواقيت رقم ١١، والبيهقي ٥/ ٤٦ كتاب الحج، باب ما يستحب من القول في أثر التلبية، والشافعي في مسنده كتاب الحج ص ٤٩٠، من طريق عبد الله بن عبد الله الأموي، أنه سمع صالح بن محمد بن زائدة، يحدث عن عمارة بن خزيمة، عن أبيه، أن النبي -صلى الله عليه وسلم- كان ... ".
قال في التعليق المغني على سنن الدارقطني ٢/ ٢٣٨: وفيه صالح بن محمد وهو مديني ضعيف.
وقال ابن مفلح في الفروع ٣/ ٣٤٥: فيه صالح بن محمد بن زائدة، قواه الإمام أحمد، وضعفه الجماعة.
وقال في التلخيص الحبير ٢/ ٢٤٠: وفيه صالح بن محمد بن أبي زائدة أبو واقد الليثي، وهو مدني ضعيف.
وقال في بلوغ المرام ص ١٥٧: رواه الشافعي بإسناد ضعيف.
(¬٣) الكتاب ١/ ١٨١، بداية المبتدي ١/ ١٤٩، الهداية ١/ ١٤٩، شرح فتح القدير ٢/ ٤٣٩، تبيين الحقائق ٢/ ١١.','3','254',NULL,NULL,'0');
INSERT INTO [b46] VALUES (930,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

الرفث: الجماع (¬١). وقيل: الفحش في الكلام (¬٢).
والفسوق: الخروج عن حدود الشريعة (¬٣) وقيل: التساب، والتنابز بالألقاب (¬٤).
والجدال: المراء مع الرفقاء، والخدم، والمكارين (¬٥) (¬٦).
_________
(¬١) قاله: ابن عمر، والحسن، وعكرمة، ومجاهد، وقتادة.
زاد المسير ١/ ١٩٢، تفسير ابن جرير الطبري ١/ ٣٩٥، تفسير ابن كثير ١/ ٣٥٥.
(¬٢) قاله: أبو عبد الرحمن اليزيدي.
وقيل: إنه الجماع، وما دونه من التعريض به. وهو مروي عن ابن عمر أيضًا، وابن عباس، وعمرو بن دينار.
زاد المسير ١/ ١٩٢، تفسير ابن جرير الطبري ١/ ٣٩٥، تفسير ابن كثير ١/ ٣٥٥، الكشاف للزمخشري ١/ ١٢٣.
(¬٣) قاله: الحسن، وعطاء، وطاوس، ومجاهد، وقتادة، وعكرمة، وسعيد بن جبير، ومحمد ابن كعب.
زاد المسير ١/ ١٩٢، تفسير ابن جرير الطبري ١/ ٣٩٥، تفسير ابن كثير ١/ ٣٥٦، الكشاف للزمخشري ١/ ١٢٣.
(¬٤) قاله: ابن عمر، وابن عباس، وابن الزبير، وإبراهيم النخعي، ومجاهد، والسدي، والحسن.
زاد المسير ١/ ١٩٢، تفسير ابن جرير الطبري ١/ ٣٩٥، تفسير ابن كثير ١/ ٣٥٦، الكشاف للزمخشري ١/ ١٢٣، الهداية ١/ ١٤٩، العناية ٢/ ٤٣٨، شرح فتح القدير ٢/ ٤٣٩.
(¬٥) لسان العرب ١١/ ١٠٥ مادة جدل، القاموس المحيط ١/ ٤٥٩ مادة ج د ل، مختار الصحاح ص ٤١ مادة ج د ل، المغرب ص ٧٧ مادة جادلة، زاد المسير ١/ ١٩٢، تفسير ابن جرير ١/ ٣٩٦، الكشاف للزمخشري ١/ ١٢٣.
(¬٦) الكراء -بالمد-: الأجرة. وهو مصدر في الأصل، من كاريته، والفاعل: مُكار على النقص، والجمع: مكارون ومكارين. يقال: أكريته الدار، وغيرها، إكراء فاكتراه. بمعنى: آجرته فاستأجَرَ. =','3','255',NULL,NULL,'0');
INSERT INTO [b46] VALUES (931,'وقتل صيد البَرِّ، والدلالة، والإشارة (¬١).
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-474>[قتل الصيد]</span>
قوله: وقتل صيد البر.
أي: ويتقي قتل صيد البر، والدلالة، والإشارة (¬٢)؛ لقوله تعالى: {يَاأَيُّهَا الَّذِينَ آمَنُوا لَا تَقْتُلُوا الصَّيْدَ وَأَنْتُمْ حُرُمٌ} [المائدة: ٩٥] ولحديث أبي قتادة -رضي الله عنه- أنه -صلى الله عليه وسلم- قال: حين سألوه عن لحم حمار وحش اصطاده أبو قتادة قال: "هل منكم أحد أمره، أو أشار إليه؟ قالوا: لا، قال: فكلوا ما بقي من لحمه" رواه البخاري، ومسلم (¬٣). علَّق حِلَّه على عدم الإشارة.
_________
= لسان العرب ١٥/ ٢١٨ مادة كرا، القاموس المحيط ٤/ ٤٥ مادة ك ر ي، مختار الصحاح ص ٢٣٧ مادة ك ر ي، المصباح المنير ٢/ ٥٣٢ مادة الكراء.
(¬١) في ص: "والدلالة عليه والإشارة إليه".
(¬٢) وعند المالكية: المحظورات: لبس المخيط، وتغطية الرأس، والوجه، ولبس الخفين، وحلق الشعر، والطيب، وقص الأظفار، وقتل القمل، والصيد، وعقد النكاح، والوطء، والمباشرة بما دون الفرج.
وعند الشافعية: يحرم على المحرم لبس المخيط، وتغطية الرأس من الرجل، والوجه من المرأة، وترجيل الشعر بالدهن، وحلقه، وتقليم الأظفار، والطيب، وقتل الصيد البري المأكول، أو متولد بينه وبين غيره، وعقد النكاح، والوطء، والمباشرة بشهوة، وقطع نبات الحرم إلا الأذخر.
وعند الحنابلة: محظورات الإحرام: حلق الشعر، وتقليم الأظفار، وتغطية الرأس، ولبس المخيط، والطيب، وقتل الصيد، وعقد النكاح، والمباشرة بما دون الفرج.
بداية المبتدي ١/ ١٤٩، الكتاب ١/ ١٨٢، الهداية ١/ ١٤٩، أقرب المسالك ص ٥٢، مختصر خليل ص ٩٠، التلقين ص ٦٥، التذكرة ص ٨٢، متن أبي شجاع ص ١٠٩، دليل الطالب ١/ ١٤٨، هداية الراغب ص ٢١٣.
(¬٣) البخاري ٢/ ٦٤٧ كتاب الإحصار وجزاء الصيد، باب إذا صاد الحلال فأهدي للمحرم الصيد أكله رقم ١٧٢٥، ومسلم ٢/ ٨٥٢ كتاب الحج، باب تحريم الصيد للمحرم رقم ١١٩٦. واللفظ لمسلم. وتمامه: عن أبي قتادة -رضي الله عنه- قال: خرج رسول الله -صلى الله عليه وسلم- حاجًا، وخرجنا معه، قال: فصرف من أصحابه، فيهم أبو قتادة، فقال: خذوا ساحل البحر، =','3','256',NULL,NULL,'0');
INSERT INTO [b46] VALUES (932,'ويباح له كل صيد البحر.
ويترك لبس المخيط، والعمامة، والقلنسوة، والخُفَّين التَّامَّين،
<hr><span data-type="title">منحة السلوك</span>

والأمر، والإشارة: أن يشير إلى صيد باليد، والدلالة أن يقول: إن في مكان كذا صيدًا.
فالإشارة تكون في الحضور، والدلالة تكون في الغيبة (¬١).
قوله: ويباح له كل صيد البحر.
مثل السمك، وأنواعه؛ لأن المنهي عنه صيد البر (¬٢).

<span data-type="title" id=toc-475>[اللباس المنهي عنه]</span>
قوله: ويترك لبس المخيط؛ لأنه ممنوع منه (¬٣)، وكذلك يترك لبس العمامة، والقلنسوة، والخفين التامين (¬٤).
_________
= حتى تلقوني، قال: فأخذوا ساحل البحر، فلما انصرفوا قبل رسول الله -صلى الله عليه وسلم- أحرموا كلهم، إلا أبا قتادة، فإنه لم يحرم، فبينما هم يسيرون إذ رأوا حمر وحش، فحمل عليها أبو قتادة، فعقر منها أتانًا، فنزلوا فأكلوا من لحمها، قال: فقالوا: أكلنا لحمًا ونحن محرمون، قال: فحملوا ما بقي من لحم الأتان، فلما أتوا رسول الله -صلى الله عليه وسلم-، قالوا يا رسول الله: إنا كنا أحرمنا، وكان أبو قتادة لم يحرم، فرأينا حمر وحش، فحمل عليها أبو قتادة، فعقر منها أتانًا، فنزلنا، فأكلنا من لحمها، فقلنا: نأكل لحم صيد ونحن محرمون، فحملنا ما بقي من لحمها، فقال: هل منكم أحد أمره، أو أشار إليه بشيء؟ قال: قالوا: لا، قال: فكلوا ما بقي من لحمها".
(¬١) العناية ٢/ ٤٤٠، تبيين الحقائق ٢/ ١٢، بدائع الصنائع ٢/ ١٩٧.
(¬٢) وفاقًا للثلاثة.
تحفة الفقهاء ١/ ٤٢١، بدائع الصنائع ٢/ ١٩٦، منح الجليل ٢/ ٣٤٠، أسهل المدارك ١/ ٤٨٧، حاشية الجمل ٢/ ٥٢٢، فتح الوهاب ٢/ ٥٢٢، الروض المربع ص ١٩٥، نيل المراد ص ٨٤.
(¬٣) الهداية ١/ ١٥٠، بدائع الصنائع ٢/ ١٨٣، المختار ١/ ١٤٤، بداية المبتدي ١/ ١٤٩، مختصر الطحاوي ص ٦٨، الكتاب ١/ ١٨٢، الهداية ١/ ١٤٩.
(¬٤) وفاقًا للثلاثة، إلا أنه عند الحنابلة: لا يلبس الخفين إلا إذا لم يجد النعلين، فليلبس =','3','257',NULL,NULL,'0');
INSERT INTO [b46] VALUES (933,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

قيد بالتامين؛ لأنه إذا قطعهما أسفل من الكعبين يجوز؛ لما روي عن ابن عمر -رضي الله عنهما- أنه قال: "سئل رسول الله -صلى الله عليه وسلم- ما يلبس المحرم؟ قال: لا يلبس القميص، ولا العمامة، ولا البرانس (¬١)، ولا السراويل (¬٢)، ولا ثوبًا مسه وَرْس (¬٣)، ولا زعفران، ولا الخفين إلا أن لا يجد النعلين فليقطعهما حتى يكونا أسفل من الكعبين" رواه البخاري، ومسلم، وغيرهما (¬٤).
_________
= الخفين، ولا يقطعهما.
الكتاب ١/ ١٨٢، بداية المبتدي ١/ ١٤٩، الهداية ١/ ١٤٩، الاختيار ١/ ١٤٤، مختصر الطحاوي ص ٦٨، القوانين ص ٩١، الشرح الصغير ١/ ٢٨٥، أسنى المطالب ١/ ٥٠٧، مغني المحتاج ١/ ٥١٩، المهذب ١/ ٢٠٧، الوجيز ١/ ١٢٤، منتهى الإرادات ٢/ ٢٢، حاشية المقنع ١/ ٤٠٦.
(¬١) وهي: قلنسوة طويلة، وكان النُّسّاك يلبسونها في صدر الإسلام، ورأسه منه ملتزق به، دراعة كان، أو قمطرة، أو جبة.
القاموس المحيط ١/ ٢٦٢ مادة ب ر ن س، لسان العرب ٦/ ٢٦ مادة برنس، مختار الصحاح ص ٢٠ مادة ب ر ن س، طلبة الطلبة ص ٦٧، الدر النقي ٢/ ٤٠٢.
(¬٢) السروال: أعجمي معرب، وهو لباس يغطي السرة والركبتين، وما بينهما.
لسان العرب ١١/ ٣٣٤ مادة سرل، القاموس المحيط ٢/ ٥٥٧ مادة س ر ول، مختار الصحاح ص ١٢٤ مادة س ر ول، المصباح المنير ١/ ٢٧٥ مادة السراويل، المعرب ص ٣٩١ مادة السراويل، الدر النقي ٢/ ٤٠١، معجم لغة الفقه ص ٢٤٤.
(¬٣) الورس: نبت أصفر يكون باليمن، يصبغ من لونه الحرير، والثياب.
لسان العرب ٦/ ٢٥٤ مادة ورس، القاموس المحيط ٤/ ٥٩٧ مادة ورس، المصباح المنير ٢/ ٦٥٥ مادة الورس، فقه اللغة وسر العربية للثعالبي ص ٢٤٢.
(¬٤) رواه البخاري ٢/ ٥٥٩ كتاب الحج، باب ما لا يلبس المحرم من الثياب رقم ١٤٦٨، ومسلم ٢/ ٨٣٤ كتاب الحج، باب ما يباح للمحرم بحج أو عمرة، وما لا يباح، وبيان تحريم الطيب عليه رقم ١١٧٧.
وتمامه: "عن ابن عمر -رضي الله عنهما- أن رجلًا سأل رسول الله -صلى الله عليه وسلم-: يا رسول الله ما يلبس المحرم =','3','258',NULL,NULL,'0');
INSERT INTO [b46] VALUES (934,'وتغطية الرأس، والوجه، والدهن، والتطيب،
<hr><span data-type="title">منحة السلوك</span>

قوله: وتغطية الرأس، والوجه (¬١).
لقوله -صلى الله عليه وسلم- في المحرم الذي خرَّ من بعيره: "لا تُخمِّروا (¬٢) وجهه، ولا رأسه" رواه مسلم، وغيره (¬٣).
قوله: والدهن. أي: يترك المحرم الدهن، والتطيب (¬٤).
_________
= من الثياب؟ فقال رسول الله -صلى الله عليه وسلم-: "لا يلبس القميص، ولا العمائم، ولا السراويلات، ولا البرانس، ولا الخفاف، إلا أحد لا يجد نعلين فليلبس خفين، وليقطعهما أسفل من الكعبين، ولا تلبسوا من الثياب شيئًا مسه الزعفران، ولا الورس".
(¬١) وإليه ذهب المالكية.
وذهب الشافعية، والحنابلة: إلى أنه يحرم على المحرم تغطية الرأس. أما الوجه: فإنه يجوز للمحرم تغطية الوجه.
قال ابن رشد في بداية المجتهد ١/ ٣٢٧: واختلفوا في تخمير المحرم وجهه بعد إجماعهم على أنه لا يخمر رأسه.
المختار ١/ ١٤٤، مختصر الطحاوي ص ٦٨، مختصر خليل ص ٨٦، منح الجليل ٢/ ٣٠٣، المجموع ٧/ ٢٦٨، روضة الطالبين ٣/ ١٢٦، الإنصاف ٣/ ٤٦٠، شرح الزركشي ٣/ ١٣٤، كشاف القناع ٢/ ٤٢٥.
(¬٢) التخمير: التغطية. يقال: خمره يخمره تخميرًا: إذا غطاه.
لسان العرب ٤/ ٢٥٨ مادة خمر، القاموس المحيط ٢/ ١٠٦ مادة خ م ر، مختار الصحاح ص ٧٩ مادة خ م ر، مجمل اللغة ص ٢٢٣ باب الخاء والميم وما يثلثهما مادة خمر، طلبة الطلبة ص ٣٦، لغة الفقه ص ٥٦، الدر النقي ٢/ ٣٠٨، المطلع على أبواب المقنع ص ٢٢.
(¬٣) مسلم ٢/ ٨٦ كتاب الحج، باب ما يفعل بالمحرم إذا مات رقم ٩٨ (١٢٠٦)، ورواه أيضًا البخاري ١/ ١٣٧ كتاب الجنائز، باب كيف يكفن المحرم رقم ١٢٦٨. وتمامه: "عن ابن عباس -رضي الله عنهما- أن رجلًا أوقصته راحلته وهو محرم فمات، فقال رسول الله -صلى الله عليه وسلم-: "اغسلوه بماء وسدر، وكفنوه في ثوبيه، ولا تخمروا رأسه، ولا وجهه، فإنه يبعث يوم القيامة ملبيًا ... ".
(¬٤) وإليه ذهب المالكية، والشافعية، والحنابلة: إلا أن الدهن غير المطيب يجوز استعماله عند الحنابلة. =','3','259',NULL,NULL,'0');
INSERT INTO [b46] VALUES (935,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

لقوله -صلى الله عليه وسلم-: "الحاج الشعث (¬١) التفل" (¬٢) رواه أبو ذر الهروي، وغيره (¬٣).
وقال الشافعي: يجوز له الخضاب بالحناء؛ لأنه ليس بطيب (¬٤).
ولنا: أنه -صلى الله عليه وسلم- "نهى المعتدة عن الكحل، والخضاب بالحناء، وقال:
_________
= المختار ١/ ١٤٥، مختصر الطحاوي ص ٦٨، الكتاب ١/ ١٨٢، الاختيار ١/ ١٤٥، القوانين الفقهية ص ٩٢، أقرب المسالك ص ٥١، مغني المحتاج ١/ ٥٢٠، أسنى المطالب ١/ ٥٠٩، مطالب أولي النهى ٢/ ٣٣١، الإقناع للحجاوي ٢/ ٤٣١.
(¬١) التشعث: التفرق، والتنكث. والشَّعِثُ: المغبر الرأس، والحاف الذي لم يدهن فتلبد. تاج العروس ١/ ٦٢٩ مادة شعث، لسان العرب ٢/ ١٦٠ مادة شعث، مختار الصحاح ص ١٤٣ مادة ش ع ث، القاموس المحيط ٢/ ٧١٨ مادة ش ع ث.
(¬٢) التاء والفاء واللام أصل واحد، وهو: خبث الشيء، وكراهته، لتغير رائحته، ولعدم تطيبه.
معجم مقاييس اللغة ١/ ٣٤٩ باب التاء والفاء وما يثلثهما مادة تفل، لسان العرب ١١/ ٧٧ مادة تفل، القاموس المحيط ١/ ٣٧٢ مادة ت ف ل، المصباح المنير ١/ ٧٦ مادة تفلت.
(¬٣) رواه الترمذي ٨/ ١٨١ كتاب التفسير، باب تفسير سورة آل عمران رقم ٣٠٠١، وابن ماجه ٢/ ٩٦٧ كتاب المناسك، باب ما يوجب الحج رقم ٢٨٩٦. من طريق إبراهيم بن يزيد المكي، عن محمد بن عباد بن جعفر المخزومي، عن ابن عمر -رضي الله عنهما- قال: قام رجل إلى النبي -صلى الله عليه وسلم- فقال: يا رسول الله ما يوجب الحج؟ قال: الزاد والراحلة، قال: يا رسول الله فما الحاج؟ قال: الشعث، التفل، وقام آخر فقال: يا رسول الله وما الحج؟ قال: العج، والثج.
قال الترمذي ٨/ ١٨٢: هذا حديث لا نعرفه من حديث ابن عمر، إلا من حديث إبراهيم بن يزيد الخوزي المكي، وقد تكلم بعض أهل العلم في إبراهيم بن يزيد من قبل حفظه.
(¬٤) وكذا عند الحنابلة.
وعند المالكية: لا يجوز له الخضاب بالحناء.
أسهل المدارك ١/ ٤٨٢، بلغة السالك ١/ ٢٨٨، إخلاص الناوي ١/ ٣٤٣، إرشاد الغاوي ١/ ٣٤٣، المستوعب ٤/ ٩٠، الفروع ٣/ ٣٧٧.','3','260',NULL,NULL,'0');
INSERT INTO [b46] VALUES (936,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

الحناء طيب" رواه النسائي (¬١).
_________
(¬١) هذه عدة أحاديث، ساقها المصنف كأنها حديث واحد.
أما نهي المعتدة عن الكحل:
فرواه النسائي ٦/ ٢٠٤ كتاب الطلاق، باب الخضاب للحادة رقم ٣٥٣٦، ورواه أيضًا البخاري ١/ ١١٩ كتاب الحيض، باب الطيب للمرأة عند غسلها من المحيض رقم ٣٠٧، ورواه مسلم ٢/ ١١٢٨ كتاب الطلاق، باب وجوب الإحداد في عدة الوفاة رقم ٦٧ (٩٣٨).
ولفظه: عن أم عطية -رضي الله عنها- قالت: "كنا ننهى أن نحد على ميت فوق ثلاث إلا على زوج أربعة أشهر وعشرًا، ولا نكتحل، ولا نتطيب، ولا نلبس ثوبًا مصبوغًا".
وأما النهي عن الخضاب بالحناء:
فرواه النسائي في الكبرى ٣/ ٣٩٦ كتاب الطلاق، باب الرخصة للحادة أن تمتشط بالسدر رقم ٥٧٣١، والنسائي في الصغرى ٦/ ٢٠٤ كتاب الطلاق، باب الرخصة للحادة أن تمتشط بالسدر رقم ٣٥٣٧، وأبو داود ٢/ ٢٩٢ كتاب الطلاق باب فيما تجتنبه المعتدة في عدتها رقم ٢٣٠٥، والبيهقي في السنن الكبرى ٧/ ٤٤٠ كتاب العدد، باب المعتدة تضطر إلى الكحل.
ولفظه: عن أم سلمة -رضي الله عنها- قالت: دخل عليّ رسول الله -صلى الله عليه وسلم- حين توفي أبو سلمة، وقد جعلت على عيني صبرًا، فقال: ما هذا يا أم سلمة؟ قلت: إنما هو صبر يا رسول الله ليس فيه طيب، قال: إنه يشب الوجه، فلا تجعليه إلا بالليل، ولا تمتشطي بالطيب، ولا بالحناء، فإنه خضاب، قلت: بأي شيء أمتشط يا رسول الله؟ قال: بالسدر تغلفين به رأسك.
قال في نصب الراية ٣/ ٢٦١: وذكره عبد الحق في أحكامه وقال: ليس لهذا الحديث إسناد يعرف.
وقال في التلخيص الحبير ٣/ ٢٣٩: وأعله عبد الحق، والمنذري بجهالة حال المغيرة، ومن فوقه.
وأما الحناء طيب:
فرواه البيهقي في معرفة السنن والآثار ٧/ ١٦٨ كتاب الحج، باب لبس المعصفرات رقم =','3','261',NULL,NULL,'0');
INSERT INTO [b46] VALUES (937,'وحلق الشعر وقصه، وقصُّ الظفر، ولبس المصبوغ، إلا مغسولًا لا ينفض،
<hr><span data-type="title">منحة السلوك</span>

قوله: وحلق الشعر، وقصه (¬١).
لقوله تعالى: {وَلَا تَحْلِقُوا رُءُوسَكُمْ} [البقرة: ١٩٦] والقص في معنى الحلق (¬٢). وأما قص الظفر؛ فلأن فيه إزالة الشعث (¬٣).
قوله: ولبس المصبوغ.
أي: يترك لبس المصبوغ (¬٤)؛ لما روينا من حديث البخاري: "إلا مغسولًا لا ينفض" (¬٥). أي: لا يفوح. وقيل: لا يتناثر؛ لأن النهي عن
_________
= ٩٦٨٩، ورواه الطبراني ٢٣/ ١٠١٢: عن خولة بنت حكيم -رضي الله عنها- عن أمها أن رسول الله -صلى الله عليه وسلم- قال: "لا تطيبي وأنت محرمة، ولا تمسي الحناء، فإنه طيب".
قال البيهقي: وهذا إسناد ضعيف، ابن لهيعة غير محتج به.
وقال في مجمع الزوائد ٣/ ٢١٨: وفيه ابن لهيعة، وفيه كلام.
(¬١) الكتاب ١/ ١٨٢، بداية المبتدي ١/ ١٥٠، الهداية ١/ ١٥٠، تبيين الحقائق ٢/ ١٣.
(¬٢) الهداية ١/ ١٥٠، تبيين الحقائق ٢/ ١٣، العناية ٢/ ٤٤٢.
(¬٣) الهداية ١/ ١٥٠، العناية ٢/ ٤٤٢، تبيين الحقائق ٢/ ١٣، شرح فتح القدير ٢/ ٤٤٢.
(¬٤) الكتاب ٣/ ١٨٣، بداية المبتدي ١/ ١٥٠، مختصر الطحاوي ص ٦٧، الهداية ١/ ١٥٠.
(¬٥) البخاري ٢/ ٥٦٠ كتاب الحج، باب ما يلبس المحرم من الثياب والأردية والأزر رقم ١٤٧٠، ولفظه: عن عبد الله بن عباس -رضي الله عنهما- قال: انطلق النبي -صلى الله عليه وسلم- من المدينة بعد ما ترجل وادهن، ولبس إزاره ورداءه، هو وأصحابه فلم ينه عن شيء من الأردية، والأزر تلبس، إلا المزعفرة التي تردع على الجلد، فأصبح بذي الحليفة، وركب راحلته، حتى استوى على البيداء، أهل هو وأصحابه.
ورواه الإمام أحمد في المسند ١/ ٣٦٢ من طريق حجاج بن أرطاة، عن حسين بن عبد الله، عن عكرمة، عن ابن عباس -رضي الله عنهما- "أن رسول الله -صلى الله عليه وسلم- رخص في الثوب المصبوغ، ما لم يكن به نفض، ولا ردع".
قال ابن حجر في فتح الباري ٣/ ٤٠٤: "وأما المغسول فقال الجمهور: إذا ذهبت =','3','262',NULL,NULL,'0');
INSERT INTO [b46] VALUES (938,'ولا يغسل شعره بخطْمي، ولا بسدر،
<hr><span data-type="title">منحة السلوك</span>

الطيب، لا اللون (¬١).
قوله: ولا يغسل شعره بخطمي (¬٢). لأن فيه إزالة الشعث (¬٣).
قوله: ولا بسدر. وهو: ورق النبق (¬٤)؛ لما قلنا (¬٥).
_________
= الرائحة جاز، خلافًا لمالك، واستدل لهم بما روى أبو معاوية، عن عبد الله بن عمر، عن نافع في هذا الحديث: "إلا أن يكون غسيلًا" أخرجه يحيى بن عبد الحميد الحماني في مسنده عنه، وروى الطحاوي، عن أحمد بن عمران، أن يحيى بن معين أنكره على الحماني، فقال له عبد الرحمن بن صالح الأزدي: قد كتبته عن أبي معاوية، وقام في الحال، فأخرج له أصله، فكتبه عن يحيى بن معين انتهى. وهي زيادة شاذة؛ لأن أبا معاوية وإن كان متقنًا، لكن في حديثه عن غير الأعمش مقال، قال أحمد: أبو معاوية مضطرب الحديث في عبيد الله، ولم يجيء بهذه الزيادة غيره، قلت -القائل ابن حجر-: والحماني ضعيف، وعبد الرحمن الذي تابعه فيه مقال واستدل به المهلب على منع استدامة الطيب وفيه نظر".
(¬١) وذهب المالكية، والشافعية، والحنابلة: إلى أنه لا بأس بلبس المعصفر، والكحلي، وسائر الألوان التي لا طيب فيها.
الهداية ١/ ١٥٠، العناية ٢/ ٤٤٢، الكتاب ١/ ١٨٣، الاختيار ١/ ١٤٥، الكافي لابن عبد البر ص ١٥٣، أسهل المدارك ١/ ٤٨٣، المعونة ١/ ٥٣٠، جواهر الإكليل ١/ ١٨٨، مغني المحتاج ١/ ٥٢٠، روضة الطالبين ٣/ ١٢٩، مختصر الخرقي ص ٥٧، المحرر ١/ ٢٣٩، المستوعب ٤/ ٨٩.
(¬٢) الخطمي: ضرب من النبات، يغسل به الرأس.
تاج العروس ٨/ ٢٨٢ مادة خطم، لسان العرب ١٢/ ١٨٨ مادة خطم، القاموس المحيط ٢/ ٨٠ مادة خ ط م، طلبة الطلبة ص ٣٦.
(¬٣) شرح فتح القدير ٢/ ٤٤٥، الاختيار ١/ ١٤٥، الهداية ١/ ١٥١.
(¬٤) النبق: ثمر السدر.
لسان العرب ١٠/ ٣٥٠ مادة نبق، معجم مقاييس اللغة ٥/ ٣٨٢ باب النون والباء وما يثلثهما مادة نبق، مختار الصحاح ص ٢٦٨ مادة ن ب ق.
(¬٥) وإليه ذهب المالكية. =','3','263',NULL,NULL,'0');
INSERT INTO [b46] VALUES (939,'ولا يحك رأسه إلا برفق إن كان عليه شعرٌ.
وله أن يغتسل، ويدخل الحمام،
<hr><span data-type="title">منحة السلوك</span>

قوله: ولا يحك رأسه إلا برفق إن كان عليه شعر.
لأن الحك بغير رفق يزيل الشعث، وإن لم يكن عليه شعر لا يحك (¬١).

<span data-type="title" id=toc-476>[ما يباح للمحرم]</span>
قوله: وله. أي: للمحرم أن يغتسل، ويدخل الحمام (¬٢).
لأنه -صلى الله عليه وسلم- "اغتسل وهو محرم" رواه مسلم (¬٣). وحكى أبو أيوب الأنصاري اغتسال رسول الله -صلى الله عليه وسلم- متفق عليه (¬٤).
_________
= وذهب الشافعية، والحنابلة: إلى أن للمحرم غسل بدنه، وشعره بسدر، وخطمي، ونحوهما.
الهداية ١/ ١٥١، العناية ٢/ ٤٤٥، المختار ١/ ١٤٥، الكتاب ١/ ١٨٣، القوانين الفقهية ص ٩٢، بداية المجتهد ١/ ٣٢٩، المنهاج ١/ ٦١٤، زاد المحتاج ١/ ٦١٥، كشاف القناع ٢/ ٤٢٤، المستوعب ٤/ ٩٥، الإنصاف ٣/ ٤٦٠.
(¬١) فيجوز للمحرم، حك رأسه وجلده، ويرفق في حك رأسه عند الأربعة.
الأصل ٢/ ٢٩٧، المعونة ١/ ٥٣٠، التفريع ١/ ٣٢٥، الكافي لابن عبد البر ص ١٥٣، المجموع ٧/ ٣٥٢، أسنى المطالب ١/ ٥١٠، المستوعب ٤/ ٩٥، كشاف القناع ٢/ ٤٢٤.
(¬٢) وفاقًا للشافعية، والحنابلة.
وعند المالكية: يحرم على المحرم رجلًا، أو امرأة، إزالة الوسخ عنه، فإن أزال الوسخ عنه، لزمه فدية.
المختار ١/ ١٤٥، الكتاب ١/ ١٨٣، الشرح الكبير للدردير ٢/ ٦٠، جواهر الإكليل ١/ ١٨٩، مغني المحتاج ١/ ٥٢١، زاد المحتاج ١/ ٦١٥، الإقناع للحجاوي ٢/ ٤٢٤، المستوعب ٤/ ٩٤.
(¬٣) ٢/ ٨٦٤ كتاب الحج، باب جواز غسل المحرم بدنه، ورأسه رقم ١٢٠٥.
(¬٤) البخاري ٢/ ٦٥٣ كتاب الحج، باب الاغتسال للمحرم رقم ١٧٤٣، ومسلم ٢/ ٨٦٤ كتاب الحج باب جواز غسل المحرم بدنه ورأسه رقم ١٢٠٥.
وتمامه: "عن إبراهيم بن عبد الله بن حنين، عن أبيه، عن عبد الله بن عباس، والمسور بن مخرمة، أنهما اختلفا بالأبواء، فقال عبد الله بن عباس: يغسل المحرم رأسه، وقال =','3','264',NULL,NULL,'0');
INSERT INTO [b46] VALUES (940,'ويستظل ببيت، أو خيمةٍ، أو محملٍ،
<hr><span data-type="title">منحة السلوك</span>

قوله: ويستظل -بالنصب- عطف على قوله: "أن يغتسل" أي: وللمحرم أن يستظل ببيت، أو خيمة، أو محمل (¬١) (¬٢)؛ لحديث أم الحصين -رضي الله عنها- (¬٣) قالت: "حججت مع رسول الله فرأيت أسامة (¬٤)، وبلالًا،
_________
= المسور: لا يغسل المحرم رأسه، فأرسلني ابن عباس، إلى أبي أيوب الأنصاري، أسأله عن ذلك، فوجدته يغتسل بين القرنين، وهو يستتر بثوب قال: فسلمت عليه فقال: من هذا؟ فقلت: أنا عبد الله بن حنين، أرسلني إليك عبد الله بن عباس أسألك كيف كان رسول الله -صلى الله عليه وسلم- يغسل رأسه وهو محرم؟ فوضع أبو أيوب يده على الثوب، فطأطأه حتى بدا لي رأسه، ثم قال لإنسان يصب: اصبب فصب على رأسه، ثم حرك رأسه بيديه، فأقبل بهما وأدبر، ثم قال: هكذا رأيته -صلى الله عليه وسلم- يفعل".
(¬١) المحمل: ما يحمل فيه الآدمي، وهو الهودج.
لسان العرب ١١/ ١٧٤ مادة حمل، معجم لغة الفقهاء ص ٤١٤، المعجم الوسيط ١/ ١٩٩ مادة المحمل، الدر النقي ٢/ ٤٠٤، المطلع على أبواب المقنع ص ١٧١.
(¬٢) وإليه ذهب الشافعية.
وذهب المالكية، والحنابلة: إلى أنه يجوز للمحرم أن يستظل ببيت وخيمة، ولا يجوز له أن يستظل بمحمل، فإن ظلله فعليه الفدية.
المختار ١/ ١٤٥، الكتاب ١/ ١٨٣، الاختيار ١/ ١٤٥، أقرب المسالك ص ٥١، مختصر خليل ص ٨٦، جواهر الإكليل ١/ ١٨٧، مواهب الجليل ٣/ ١٤٤، المهذب ١/ ٢٠٧، حاشية الجمل على فتح الوهاب ٢/ ٥٠٣، المجموع ٧/ ٢٣٣، الكافي لابن قدامة ١/ ٤٠٦، الشرح الكبير لأبي الفرج ابن قدامة ٣/ ٢٧٧، المستوعب ٤/ ٧٧، هداية الراغب ص ٢١٣، كشاف القناع ٢/ ٤٢٤.
(¬٣) هي أم الحصين بنت إسحاق الأحمسية، جدة يحيى بن الحصين، لها صحبة، روت عن النبي -صلى الله عليه وسلم- وشهدت معه حجة الوداع، روى عنها العيزار بن حريث، وابن ابنها يحيى بن الحصين، روى لها الجماعة سوى البخاري.
الإصابة ٤/ ٤٤٢، الاستيعاب ٤/ ٤٤٥، تهذيب الكمال ٣٥/ ٣٤٥، خلاصة تذهيب تهذيب الكمال ص ٣٩٧.
(¬٤) هو أسامة بن زيد بن حارثة الكلبي، مولى رسول الله -صلى الله عليه وسلم- وابن مولاه، ولد بمكة سنة ٧ =','3','265',NULL,NULL,'0');
INSERT INTO [b46] VALUES (941,'ويشد الهِميان (¬١)،
<hr><span data-type="title">منحة السلوك</span>

أحدهما آخذ بخطام (¬٢) ناقة النبي -صلى الله عليه وسلم- والآخر رافع ثوبه يستره من الحر، حتى رمى جمرة العقبة" رواه مسلم، وأبو داود، والنسائي (¬٣).
قوله: ويشد الهِميان (¬٤).
أي: وله أن يشد الهميان في وسطه؛ لأنه ليس بلبس مخيط، ولا في معناه (¬٥). وكذا شد المنطقة (¬٦)، والسيف، والسلاح،
_________
= قبل الهجرة، ونشأ على الإسلام، كان الرسول -صلى الله عليه وسلم- يحبه حبًا كثيرًا، استعمله النبي -صلى الله عليه وسلم- وهو ابن ثماني عشرة سنة، مات سنة ٥٤ هـ.
الإصابة ١/ ٣١، أسد الغابة ١/ ٧٩، صفة الصفوة ١/ ٥٢١، سير أعلام النبلاء ٢/ ٤٩٦.
(¬١) في ب بزيادة "في وسطه".
(¬٢) الخطم من كل دابة: مقدم أنفها، وفمها. والخِطام: الزِّمام وهو كل حبل يُعلق في حلق البعير، ثم يعقد على أنفه، سواء كان من جلد، أو صوف، أو ليف.
لسان العرب ١٢/ ١٨٦ مادة خطم، معجم مقاييس اللغة ٢/ ١٩٨ باب الخاء والطاء وما يثلثهما مادة خطم، القاموس المحيط ٢/ ٧٨ مادة خ ط م، مختار الصحاح ص ٧٦ مادة خ ط م، المصباح المنير ١/ ١٧٤ مادة الخطم.
(¬٣) مسلم ٢/ ٩٤٤ كتاب الحج، باب استحباب رمي جمرة العقبة يوم النحر راكبًا رقم ٣١٢ (١٢٩٨)، وأبو داود ٢/ ١٦٧ كتاب المناسك، باب في المحرم يظلل رقم ١٨٣٤، والنسائي ٥/ ٢٦٩ كتاب مناسك الحج، باب الركوب إلى الجمار رقم ٣٠٦٠.
(¬٤) الهميان -بالكسر-: كيس يجعل فيه النفقة، ويشد على الوسط وهو معرب.
لسان العرب ١٥/ ٣٦٤ مادة همي، القاموس المحيط ٤/ ٥٣٦ مادة هـ م ي، مختار الصحاح ص ٢٩١ مادة هـ م ي، المعرب ص ٦٣١، المصباح المنير ٢/ ٦٤١ مادة الهميان.
(¬٥) الهداية ١/ ١٥١، العناية ٢/ ٤٤٤، تبيين الحقائق ٢/ ١٤، الكتاب ١/ ١٨٣.
(¬٦) كل شيء شددت به وسطك، فهو منطق، ومنطقه، ونطاق.
لسان العرب ١٠/ ٣٥٤ مادة نطق، مختار الصحاح ص ٢٧٧ مادة ن ط ق، القاموس المحيط ٤/ ٣٩٢ مادة ن ط ق، المصباح المنير ٢/ ٦١١ مادة نطق.','3','266',NULL,NULL,'0');
INSERT INTO [b46] VALUES (942,'ويكثر التلبية بصوتٍ رفيع بعد الصلوات الخمس،
<hr><span data-type="title">منحة السلوك</span>

والتختم بالخاتم (¬١).

<span data-type="title" id=toc-477>[الإكثار من التلبية]</span>
قوله: ويكثر التلبية بصوت رفيع بعد الصلوات الخمس (¬٢).
لما روي عن أبي بكر الصديق -رضي الله عنه- أنه -صلى الله عليه وسلم- "سئل أي الحج أفضل؟ قال: العج والثج" رواه الترمذي (¬٣).
_________
(¬١) وكذا عند الشافعية.
وذهب المالكية: إلى أنه يجوز للمحرم شد الهميان والمنطقة، ولا يجوز تقلده السيف، والسلاح، والخاتم للرجال.
وعند الحنابلة: له أن يلبس الهميان، ولا يعقد منطقة، ولا يتقلد السيف، إلا عند الضرورة.
الهداية ١/ ١٥١، العناية ٢/ ٤٤٤، الكتاب ١/ ١٨٣، كنز الدقائق ٢/ ١٤، تبيين الحقائق ٢/ ١٤، القوانين الفقهية ص ٩١، شرح الزرقاني ٢/ ٢٩١، منح الجليل ٢/ ٣١٠، حاشية البناني ٢/ ٢٩٤، مغني المحتاج ١/ ٥١٨، نهاية المحتاج ٣/ ٣٣١، المقنع ١/ ٤٠٧، المحرر ١/ ٢٣٩، الإنصاف ٣/ ٤٦٧.
(¬٢) بدائع الصنائع ٢/ ١٤٥، تحفة الفقهاء ١/ ٤٠١، تبيين الحقائق ٢/ ١٤، الهداية ١/ ١٥١.
(¬٣) ٣/ ١٧٥ كتاب الحج، باب ما جاء في فضل التلبية والنحر رقم ٨٢٧، والدارمي ٢/ ٣١ كتاب المناسك، باب أي الحج أفضل رقم ١٧٤٣، وأبو يعلى ١/ ١٠٨ رقم ١١٧، وابن ماجه ٢/ ٩٧٥ كتاب المناسك، باب رفع الصوت بالتلبية رقم ٢٩٢٤، والخطيب في الموضح ١/ ١٨، والبيهقي ٥/ ٤٢ كتاب الحج، باب رفع الصوت بالتلبية، والحاكم ١/ ٤٥١ كتاب المناسك.
من طرق عن محمد بن إسماعيل بن أبي فديك، عن الضحاك بن عثمان، عن محمد بن المنكدر، عن عبد الرحمن بن يربوع، عن أبي بكر الصديق -رضي الله عنه- ...
قال الترمذي ٣/ ١٧٥: حديث غريب لا نعرفه إلا من حديث ابن أبي فديك، ومحمد بن المنكدر، لم يسمع من عبد الرحمن بن يربوع.
وقال في الدراية ٢/ ١٢: وفيه انقطاع بين ابن المنكدر وعبد الرحمن بن يربوع.
قال الحاكم ١/ ٤٥١: هذا حديث صحيح الإسناد ولم يخرجاه، ووافقه الذهبي. =','3','267',NULL,NULL,'0');
INSERT INTO [b46] VALUES (943,'وكلما علا شرفًا، أو هبط واديًا، أو لقي ركبًا،
<hr><span data-type="title">منحة السلوك</span>

العج: رفع الصوت بالتلبية (¬١).
والثج: إسالة الدم (¬٢).
قوله: وكلما علا شرفًا. أي: موضعًا عاليًا (¬٣)، أو هبط واديًا، أي: نزل مكانًا سافلًا، أو لقي ركبًا (¬٤)، لما روي أنه -صلى الله عليه وسلم- "كان يلبي إذا لقي ركبًا، أو صعد أكمة (¬٥)، أو هبط واديًا، وفي أدبار المكتوبة (¬٦)، وآخر الليل"
_________
= قال البيهقي ٥/ ٤٣: قال أبو عيسى: سألت عنه البخاري فقال: هو عندي مرسل، محمد بن المنكدر لم يسمع من عبد الرحمن بن يربوع.
(¬١) تاج العروس ٢/ ٧١ مادة عج، لسان العرب ٢/ ٣١٨ مادة عجج، معجم مقاييس اللغة ٤/ ٢٧ باب العين وما بعدها في المضاعف والمطابق والأصم مادة عج، طلبة الطلبة ص ٦٧، غريب الحديث لأبي عبيد ١/ ٢٧٩ مادة عجج.
(¬٢) تاج العروس ٢/ ١٣ مادة ثج، معجم مقاييس اللغة ١/ ٣٦٧ باب الكلام الذي أوله تاء في المضاعف والمطابق والأصم مادة ثج، مختار الصحاح ص ٣٥ مادة ث ج ج، غريب الحديث لأبي عبيد ١/ ٢٧٩ مادة ثجج.
(¬٣) لسان العرب ٩/ ١٧٠ مادة شرف، القاموس المحيط ٢/ ٧٠٠ مادة ش ر ف، مختار الصحاح ص ١٤١ مادة ش ر ف، مجمل اللغة ص ٤٠٣ باب الشين والراء وما يثلثهما مادة شرف.
(¬٤) الكتاب ١/ ١٨٣، كنز الدقائق ٢/ ١٤، مختصر الطحاوي ص ٦٣، تحفة الفقهاء ١/ ٤٠١، بدائع الصنائع ٢/ ١٤٥، الهداية ١/ ١٥١.
(¬٥) الأكمة: الموضع الذي هو أشد ارتفاعًا مما حوله.
لسان العرب ١٢/ ٢١ مادة أكم، معجم مقاييس اللغة ١/ ١٢٥ باب الهمزة والكاف وما يثلثهما مادة أكم، القاموس المحيط ١/ ١٦٥ مادة أك م، المصباح المنير ١/ ١٨ مادة الأكمة.
(¬٦) أي: المفروضة. وهي الصلوات الخمس، من كتب أي: حكم وقضى، أي: أوجبه.
لسان العرب ١/ ٦٩٩ مادة كتب، القاموس المحيط ٤/ ١١ مادة ك ت ب، المصباح المنير ٢/ ٥٢٤ مادة كتب، المغرب ص ٣٩٩، مادة كتبه، النظم المستعذب ١/ ٩٩.','3','268',NULL,NULL,'0');
INSERT INTO [b46] VALUES (944,'وبالأسحار.
فإذا دخل مكة،
<hr><span data-type="title">منحة السلوك</span>

ذكره في الإمام (¬١).
قوله: وبالأسحار (¬٢).
أي: وفي وقت الأسحار، وفي غير الأسحار أيضًا، لكن تخصيص الأسحار؛ لأنها وقت يستجاب فيه الدعاء (¬٣).

<span data-type="title" id=toc-478>[ما يفعله المحرم بعد دخول مكة]</span>
قوله: فإذا دخل مكة.
_________
(¬١) قال الزيلعي في نصب الراية ٣/ ٣٣: وعزي إلى ابن ناجية في فوائده عن جابر قال ... وذكره الشيخ في الإمام ولم يعزه.
وقد ذكره الزركشي في مختصره على الخرقي ٣/ ٩٩ رقم ١٥١٧ عن جابر مرفوعًا.
قال في التلخيص ٢/ ٢٣٩: هذا الحديث ذكره الشيخ في المهذب، وبيض له النووي، والمنذري، وقد رواه ابن عساكر في تخريجه لأحاديث المهذب، من طريق عبد الله بن محمد بن ناجية في فوائده، بإسناد له إلى جابر، وفي إسناده من لا يعرف.
وقال في الدراية ٢/ ١٢: وفي فوائد ابن ناجية عن جابر -رضي الله عنه-: كان رسول الله -صلى الله عليه وسلم- يلبي إذا لقي ركبًا أو صعد أكمة أو ... ".
(¬٢) السحر -بفتحتين-: آخر الليل، قبيل الصبح. والجمع أسحار وقيل: ثلث الليل الآخر إلى طلوع الفجر.
لسان العرب ٤/ ٣٥٠ مادة سحر، القاموس المحيط ٢/ ٥٢٨ مادة س ح ر، معجم مقاييس اللغة ٣/ ١٣٨ باب السين والحاء وما يثلثهما مادة سحر، مختار الصحاح ص ١٢٢ مادة س ح ر، المصباح المنير ١/ ٢٦٧ مادة السحر.
(¬٣) وفاقًا للثلاثة في مواطن التلبية، وزمنها.
شرح فتح القدير ٢/ ٤٤٥، الكتاب ١/ ١٨٣، كنز الدقائق ٢/ ١٤، مختصر الطحاوي ص ٦٣، تبيين الحقائق ٢/ ١٤، جواهر الإكليل ١/ ١٧٧، التفريع ١/ ٣٢٢، فتح الوهاب ٢/ ٤١٧، قليوبي ٢/ ١٠٠، الروض المربع ص ١٩٤، العمدة ص ٣٥.','3','269',NULL,NULL,'0');
INSERT INTO [b46] VALUES (945,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

اعلم أنه إذا دخل مكة يستحب له أن يدخل من الثنية (¬١) العليا، وهي ثنية كَداء من أعلى مكة، على درب المعلى، وطريق الأبطح، ويخرج من الثنية السفلى، وهي ثنية كُدا من أسفل مكة على درب اليمن (¬٢)؛ لما روي أنه -صلى الله عليه وسلم- "كان يدخل من الثنية العليا، ويخرج من الثنية السفلى" رواه الجماعة، إلا الترمذي (¬٣).
ولا يضره دخلها ليلًا أو نهارًا (¬٤)؛ لأنه -صلى الله عليه وسلم- "دخلها ليلًا، ونهارًا" رواه النسائي (¬٥).
_________
(¬١) الثنية: الطريق بين جبلين، وقيل: الطريق العالي فيه.
مختار الصحاح ص ٣٧ مادة ث ن ي، المصباح المنير ١/ ٨٥ مادة الثنية.
(¬٢) وفاقًا للثلاثة.
تبيين الحقائق ٢/ ١٤، شرح فتح القدير ٢/ ٤٤٧، العناية ٢/ ٤٤٧، الهداية ١/ ١٥١، الخرشي علي خليل ٢/ ٣٢٩، حاشية العدوي ٢/ ٣٢٩، تحفة المحتاج ٤/ ٦٦، حاشية الشرواني ٤/ ٦٦، زاد المستقنع ص ٢٠٢، المقنع شرح الخرقي ٢/ ٦١٧.
(¬٣) رواه البخاري ٢/ ٥٧١ في الحج، باب من أين يدخل مكة رقم ١٥٠٠، ومسلم ٢/ ٩١٨ في الحج، باب استحباب دخول مكة من الثنية العليا، والخروج من الثنية السفلى، ودخول بلدة من طريق غير التي خرج منها رقم (١٢٥٧)، وأبو داود ٢/ ١٧٤ كتاب المناسك، باب دخول مكة رقم ١٨٦٦، والنسائي ٥/ ٢٠٠ كتاب المناسك، باب من أين يدخل مكة رقم ٢٨٦٥، وأحمد ٢/ ٣، وابن ماجه ٢/ ٩٨١ كتاب المناسك باب دخول مكة رقم ٢٩٤٠ من حديث ابن عمر -رضي الله عنهما-.
(¬٤) وفاقًا للحنابلة.
وعند المالكية، والشافعية: يستحب دخولها نهارًا.
بداية المبتدي ١/ ١٥١، شرح فتح القدير ٢/ ٤٤٧، الهداية ١/ ١٥١، الخرشي على خليل ٢/ ٣٢٩، حاشية العدوي ٢/ ٣٢٩، نهاية المحتاج ١/ ٢٧٦، مغني المحتاج ١/ ٤٨٣، المغني ٣/ ٣٨٧، الشرح الكبير لأبي الفرج ابن قدامة ٣/ ٣٨٧.
(¬٥) أما دخول مكة نهارًا: فرواه النسائي في السنن الكبرى ٢/ ٣٨١ كتاب الحج، باب دخول =','3','270',NULL,NULL,'0');
INSERT INTO [b46] VALUES (946,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

ويقول عند دخوله الحرم: اللهم إن هذا أمنك وحرمك الذي من دخله كان آمنًا، فحرم لحمي، ودمي، وعظمي، وبشري على النار، اللهم أمِّني من عذابك يوم تبعث عبادك، فإنك أنت الله لا إله إلا أنت الرحمن الرحيم،
_________
= مكة رقم الحديث ٣٨٤٥ من طريق موسى بن عقبة قال: حدثني نافع، أن عبد الله بن عمر -رضي الله عنهما- حدثه أن رسول الله -صلى الله عليه وسلم- كان ينزل بذي طوى، يلبث به حتى يصلي صلاة الصبح ... " وفي المجتبى ٥/ ١٩٩ كتاب مناسك الحج، باب دخول مكة برقم ٢٨٦٢، ورواه البخاري ٢/ ٥٧١ كتاب الحج، باب دخول مكة نهارًا أو ليلًا رقم الحديث ١٤٩٩ من طريق نافع عن ابن عمر -رضي الله عنهما- قال: بات النبي -صلى الله عليه وسلم- بذي طوى، حتى أصبح، ثم دخل مكة، ومسلم ٢/ ٩١٩ كتاب الحج، باب استحباب المبيت بذي طوى عند إرادة دخول مكة، والاغتسال لدخولها، ودخولها نهارًا رقم الحديث ١٢٥٩ بلفظ البخاري المتقدم.
وأما دخول مكة ليلًا فرواه النسائي في الكبرى ٢/ ٣٨١ كتاب الحج، باب دخول مكة ليلًا برقم ٣٨٤٦ عن محرش الكعبي أن النبي -صلى الله عليه وسلم- خرج ليلًا من الجعرانة حتى أمسى معتمرًا، فأصبح بالجعرانة كبائت، والنسائي في الصغرى ٥/ ١٩٩ كتاب مناسك الحج، باب دخول مكة ليلًا برقم ٢٨٦٣، ورواه الترمذي ٣/ ٣٠٣ كتاب الحج باب ما جاء في العمرة من الجعرانة رقم ٩٣٥، ورواه أبو داود ٢/ ٢٠٦ كتاب المناسك، باب المهلة بالعمرة تحيض فيدركها الحج، فتنقض عمرتها وتهل بالحج، هل تقضي عمرتها برقم ١٩٩٦، ورواه البيهقي في السنن الكبرى ٤/ ٣٥٧ كتاب الحج، باب من استحب الإحرام بالعمرة من الجعرانة، ورواه الشافعي ٢/ ١١٤.
قال الترمذي ٣/ ٣٠٤: هذا حديث حسن غريب.
قال النووي في المجموع ٨/ ٧: إسناده جيد.
وقال النووي في شرح صحيح مسلم ٩/ ٦: ثبت أنه -صلى الله عليه وسلم- دخلها محرمًا بعمرة الجعرانة ليلًا.
وقال ابن القيم في زاد المعاد ٢/ ٩٥: ثم دخلها بعمرة من الجعرانة ودخلها في هذه العمرة ليلًا وخرج ليلًا ... ولما قضى عمرته ليلًا رجع من فوره إلى الجعرانة، فبات بها.
وقال ابن حجر في فتح الباري ٣/ ٤٣٦: وأما الدخول ليلًا فلم يقع منه إلا في عمرة الجعرانة فإنه -صلى الله عليه وسلم- أحرم من الجعرانة، ودخل مكة ليلًا فقضى أمر العمرة، ثم رجع ليلًا فأصبح بالجعرانة.','3','271',NULL,NULL,'0');
INSERT INTO [b46] VALUES (947,'طاف للقدوم سبعة أشواط
<hr><span data-type="title">منحة السلوك</span>

وأسألك أن تصلي على سيدنا محمد، وعلى آله (¬١).
ويقول عند دخوله مكة: اللهم افتح لي أبواب رحمتك، وأدخلني فيها، وأعذني من الشيطان الرجيم (¬٢).

<span data-type="title" id=toc-479>[الطواف]</span>
قوله: طاف للقدوم سبعة أشواط (¬٣).
لما روى عروة (¬٤)، عن عائشة -رضي الله عنها-: "إن أول شيء بدأ به رسول الله
_________
(¬١) وعند المالكية: ليس لدخول الحرم دعاء مخصوص، وإنما هو كبقية المساجد، فيقدم رجله اليمنى عند الدخول ويقول: أعوذ بالله من الشيطان الرجيم، اللهم صل على محمد، وعلى آل محمد، اللهم اغفر لي ذنوبي، وافتح لي أبواب رحمتك.
وعند الشافعية: يقول إذا أبصر البيت: اللهم زد هذا البيت تشريفًا، وتعظيمًا، وتكريمًا، ومهابة، وزد من شرفه، وعظمه ممن حجه، أو اعتمره تشريفًا، وتكريمًا، وتعظيمًا، وبرًا، اللهم أنت السلام، ومنهم السلام، فحينا ربنا بالسلام، ثم يدخل من باب بني شيبة، ويطوف للقدوم.
والحنابلة كالشافعية، ويزيد: الحمد لله رب العالمين كثيرًا، كما هو أهله، وكما ينبغي لكريم وجهه، وعز جلاله، والحمد لله الذي بلغني بيته، ورآني لذلك أهلًا، والحمد لله على كل حال، اللهم إنك دعوت إلى حج بيتك الحرام، وقد جئتك لذلك، اللهم تقبل مني، واعف عني، وأصلح لي شأني كله، لا إله إلا أنت. وما زاد من الدعاء فحسن.
تبيين الحقائق ٢/ ١٤، التاج والإكليل ٣/ ١١٣، شرح المحلي على المنهاج ٢/ ١٠٢، عميرة ٢/ ١٠٢، الإقناع للحجاوي ٢/ ٤٧٦، المبدع ٣/ ٢١٢.
(¬٢) تبيين الحقائق ٢/ ١٤، شرح فتح القدير ٢/ ٤٤٧.
(¬٣) بداية المبتدي ١/ ١٥٢، كنز الدقائق ٢/ ١٦، الكافي لابن عبد البر ص ١٣٩، التفريع ١/ ٣٣٧، زاد المستقنع ص ٢٠٣، الكافي ١/ ٤٣١.
(¬٤) هو عروة بن الزبير بن العوام بن خويلد الأسدي القرشي، ثقة، فقيه، مشهور، من كبار التابعين، ولد سنة ٢٣ هـ، وأمه أسماء بنت أبي بكر الصديق. انتقل من المدينة إلى البصرة ثم إلى مصر وأقام بها سبع سنين، وتوفي بالمدينة سنة ٩٤ هـ. =','3','272',NULL,NULL,'0');
INSERT INTO [b46] VALUES (948,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

- صلى الله عليه وسلم- حين قدم مكة، أن توضأ، ثم طاف بالبيت" الحديث رواه البخاري، ومسلم (¬١).
ويكون ملبيًا في دخوله (¬٢)، ويدخل من باب بني شيبة (¬٣) (¬٤)، ويقدم رجله اليمنى في دخوله، ويقول: بسم الله، والحمد لله والصلاة على رسول الله، اللهم افتح لي أبواب رحمتك، وأدخلني فيها، اللهم إني أسألك في مقامي هذا أن تصلي على محمد عبدك ورسولك، وأن ترحمنى، وتقيل عثرتي (¬٥)، وتغفر ذنبي،
_________
= تهذيب الكمال ٢٠/ ١١، طبقات ابن سعد ٥/ ١٧٨، الثقات لابن حبان ٥/ ١٩٤، تهذيب التهذيب ٧/ ١٨٠.
(¬١) البخاري ٢/ ٥٨٤ كتاب الحج، باب من طاف بالبيت إذا قدم مكة قبل أن يرجع إلى بيته، ثم صلى ركعتين، ثم خرج إلى الصفا رقم ١٥٣٦، ومسلم ٢/ ٩٠٦ كتاب الحج، باب ما يلزم ممن طاف بالبيت وسعى من البقاء على الإحرام، وترك التحلل رقم ١٢٣٥.
(¬٢) ويقطع المتمتع التلبية إذا افتتح الطواف، عند الحنفية، والشافعية، والحنابلة.
وعند المالكية: إن كان أحرم بها من الميقات، فإذا دخل الحرم قطع، وإن كان أحرم من أدنى الحل فإذا رأى البيت قطع وإن أحرم بها من الجعرانة قطع إذا دخل بيوت مكة.
شرح فتح القدير ٢/ ٤٤٧، تبيين الحقائق ٢/ ١٥، منح الجليل ٢/ ٦، الشرح الصغير ١/ ٢٧٠، جواهر الإكليل ١/ ١٧٨، أسنى المطالب ١/ ٥١٣، مغني المحتاج ١/ ٥٠١، هداية الراغب ص ٢٢٤، الإفصاح ١/ ٢٨٠.
(¬٣) شرح فتح القدير ٢/ ٤٤٧، تبيين الحقائق ٢/ ١٥.
(¬٤) وهو: باب بني عبد شمس بن عبد مناف، وبهم كان يعرف في الجاهلية والإسلام عند أهل مكة.
تاريخ مكة للأزرقي ٢/ ٨٧، زاد المعاد ٢/ ٢٢٤.
(¬٥) العثرة: الزلة.
لسان العرب ٤/ ٥٣٢ مادة عثر، القاموس المحيط ٣/ ١٥٤ مادة ع ث ر، مختار الصحاح ص ١٧٤ مادة ع ث ر، المصباح المنير ٢/ ٣٩٣ مادة عثر.','3','273',NULL,NULL,'0');
INSERT INTO [b46] VALUES (949,'وراء الحطيم،
<hr><span data-type="title">منحة السلوك</span>

وتضع عني وزري (¬١) (¬٢).
فإذا وقع بصره على البيت: كبَّر، وهلل ثلاثًا، ويقول: اللهم أنت السلام، ومنك السلام، فحينا ربنا بالسلام، اللهم زد بيتك هذا تعظيمًا، وتشريفًا، ومهابة، وزد من شرفه، وعظمه، وكرمه، ممن حجه، أو اعتمره، تشريفًا، وتكريمًا (¬٣).
قوله: وراء الحطيم.
لأن الحطيم من البيت (¬٤)؛ لما روي عن عائشة -رضي الله عنها- أنها: "سألت النبي -صلى الله عليه وسلم- عن الحجر من البيت هو؟ قال: نعم" الحديث متفق عليه (¬٥).
سمي حطيمًا: لأنه حطم من البيت. أي: كسر (¬٦).
_________
(¬١) الوزر: الذنب، والإثم.
لسان العرب ٥/ ٢٨٢ مادة وزر، القاموس المحيط ٤/ ٦٠٤ مادة وزر، مختار الصحاح ص ٢٩٩ مادة وزر، المصباح المنير ٢/ ٦٥٧ مادة الوزر، مجمل اللغة ص ٧٥٠ باب الواو والزاي وما يثلثهما مادة وزر.
(¬٢) تبيين الحقائق ٢/ ١٥، شرح فتح القدير ٢/ ٤٤٧، العناية ٢/ ٤٤٧.
(¬٣) شرح فتح القدير ٢/ ٤٤٨، تبيين الحقائق ٢/ ١٥، المختار ١/ ١٤٦، الكتاب ١/ ١٨٤، الاختيار ١/ ١٤٦.
(¬٤) وفاقًا للثلاثة.
الكتاب ١/ ١٨٥، بداية المبتدي ١/ ١٥٢، الهداية ١/ ١٥٢، تبيين الحقائق ٢/ ١٦، التلقين ص ٦٨، الشرح الصغير ١/ ٢٧٤، أنوار المسالك ص ٢٤٦، زاد المحتاج ١/ ٥٨٧، الروض المربع ص ٢٠٥، الفروع ٣/ ٤٩٩.
(¬٥) البخاري ٢/ ٥٧٣ كتاب الحج، باب فضل مكة وبنيانها رقم ١٥٠٧، ومسلم ٢/ ٩٧٣ كتاب الحج، باب جدر الكعبة وبابها رقم ٤٠٥ (١٣٣٣) وتمامه: "قلت: فما لهم لم يدخلوه في البيت؟ قال: إن قومك قصرت بهم النفقة".
(¬٦) لسان العرب ١٢/ ١٤٠ مادة حطم، معجم ما استعجم ١/ ٤٢٧، القاموس المحيط =','3','274',NULL,NULL,'0');
INSERT INTO [b46] VALUES (950,'يرمل في الثلاثة الأول،
<hr><span data-type="title">منحة السلوك</span>

وسمي حجرًا: لأنه حجر من البيت.
أي: منع منه (¬١)، وهو محوط، ممدود على صورة نصف دائرة، خارج عن جدار البيت من جهة الشام تحت الميزاب. وليس كله من البيت، بل مقدار ستة أذرع منه من البيت (¬٢). لحديث عائشة -رضي الله عنها- أنه -صلى الله عليه وسلم- قال: "ستة أذرع من الحجر من البيت، وما زاد ليس من البيت" رواه مسلم (¬٣).
قوله: يرْمُل (¬٤) في الثلاثة الأول منها (¬٥).
_________
= ١/ ٦٦٦ مادة ح ط م، المصباح المنير ١/ ١٤١ مادة حطم، تهذيب الأسماء واللغات ٣/ ٨٥.
(¬١) المبسوط ٤/ ١١، الهداية ١/ ١٥٢.
(¬٢) شرح فتح القدير ٢/ ٤٥٣، تبيين الحقائق ٢/ ١٧.
(¬٣) ٢/ ٩٦٩ كتاب الحج، باب نقض الكعبة وبنائها رقم الحديث ١٣٣٣ عن عائشة -رضي الله عنها- قالت: قال رسول الله -صلى الله عليه وسلم-: "لولا أن قومك حديثو عهد بشرك، لهدمت الكعبة، فألزقتها بالأرض وجعلت لها بابين، بابًا شرقيًا، وبابًا غربيًا، وزدت فيها ستة أذرع من الحجر، فإن قريشًا اقتصرتها حيث بنت الكعبة".
(¬٤) الرمل -بالتحريك-: الهرولة، وهو دون المشي وفوق العدو، يقال: رَمل الرجل يرمل رملانًا ورَمَلًا: إذا أسرع في مشيته، وهز منكبيه.
لسان العرب ١١/ ٢٩٥ مادة رمل، تاج العروس ٧/ ٣٥٠ مادة رمل، جمهرة اللغة لابن دريد ٢/ ٤١٥ مادة رمل، القاموس المحيط ٢/ ٣٩٢ مادة ر م ل، طلبة الطلبة ص ٦٨، حلية الفقهاء ص ١١٨، المطلع على أبواب المقنع ص ١٩٠، لغة الفقه ص ١٥٢، النظم المستعذب ١/ ٢٠٥.
(¬٥) وفاقًا للثلاثة.
الأصل ٢/ ٣٠٠، كنز الدقائق ٢/ ١٧، تبيين الحقائق ٢/ ١٧، متن الرسالة ص ١٨٥، الكافي لابن عبد البر ص ١٣٩، قليوبي ٢/ ١٠٧، حاشية الشرواني ٤/ ٨٩، حاشية المقنع ١/ ٤٤٣، السلسبيل ١/ ٣٨٩.','3','275',NULL,NULL,'0');
INSERT INTO [b46] VALUES (951,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

لما روي عن جابر -رضي الله عنه- أنه -صلى الله عليه وسلم- "لما قدم مكة، أتى الحجر، فاستلمه، ثم مشى على يمينه، فرمل ثلاثًا، ومشى أربعًا" رواه مسلم، والنسائي (¬١).
وإذا حاذى الملتزم (¬٢) في أول طوافه، وهو بين الباب، والحجر الأسود قال: اللهم إن لك عليّ حقوقًا، فتصدق بها عليّ (¬٣).
_________
(¬١) مسلم ٢/ ٨٨٦ كتاب الحج، باب حجة النبي رقم ١٢١٨، والنسائي في الكبرى ٢/ ٤٠٤ كتاب الحج، باب كيف يطوف أول ما يقدم رقم ٣٩٣٦، والنسائي في الصغرى أيضًا ٥/ ٢٢٨ كتاب مناسك الحج باب كيف يطوف أول ما يقدم رقم ٢٩٣٩.
(¬٢) بضم الميم وفتح الزاي. سمي بذلك: لأنهم يلتزمونه في الدعاء. ويقال: له المدعى، والمتعوذ. وذرعه أربعة أذرع. وهو ما بين ركن الحجر الأسود، والباب.
تهذيب الأسماء واللغات ٤/ ١٥٧، النظم المستعذب ١/ ٢١٣، المطلع على أبواب المقنع ص ٢٠٣، طلبة الطلبة ص ٧٥، لغة الفقه ص ١٥٩.
(¬٣) وعند المالكية: يسن للطائف الدعاء بما يحب بلا حد محدود في ذلك، بل بما يفتح عليه.
وعند الشافعية: يسن أن يقول في أول طوافه: بسم الله، والله أكبر، اللهم إيمانًا بك، وتصديقًا بكتابك، ووفاء بعهدك، واتباعًا لسنة نبيك محمد -صلى الله عليه وسلم-. ويقول قبالة الباب: اللهم إن البيت بيتك، والحرم حرمك، والأمن أمنك، وهذا مقام العائذ بك من النار، وبين اليمانيين: اللهم آتنا في الدنيا حسنة، وفي الآخرة حسنة، وقنا عذاب النار. وليدع بما شاء.
وعند الحنابلة: إذا حاذى الحجر الأسود قال: بسم الله، والله أكبر، اللهم إيمانًا بك، وتصديقًا بكتابك، ووفاءً بعهدك، واتباعًا لسنة نبيك محمد -صلى الله عليه وسلم-. يقول ذلك كلما استلمه، ويقول كلما حاذى الحجر: الله أكبر، ولا إله إلا الله. وبين الركنين: ربنا آتنا في الدنيا حسنة، وفي الآخرة حسنة، وقنا عذاب النار. وفي سائر الطواف: اللهم اجعله حجًا مبرورًا، وسعيًا مشكورًا، وذنبًا مغفورًا، رب اغفر وارحم، وتجاوز عما تعلم، وأنت الأعز الأكرم. ويدعو بما أحب.
شرح فتح القدير ٢/ ٤٥٢، تبيين الحقائق ٢/ ١٧، الشرح الصغير ١/ ٢٧٦، الشرح الكبير =','3','276',NULL,NULL,'0');
INSERT INTO [b46] VALUES (952,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وإذا حاذى الباب يقول: اللهم هذا البيت بيتك، وهذا الحرم حرمك، وهذا الأمن أمنك، وهذا مقام العائذ بك من النار، فأعذني منها (¬١).
وإذا حاذى المقام على يمينه: يقول: اللهم إن هذا مقام إبراهيم العائذ اللائذ بك من النار، حرم لحومنا وبشرتنا على النار (¬٢).
وإذا أتى الركن العراقي (¬٣) يقول: اللهم إني أعوذ بك من الشرك، والشك، والنفاق، والشقاق (¬٤)، وسوء الأخلاق، وسوء المنقلب في الأهل، والمال، والولد (¬٥).
وإذا أتى ميزاب الرحمة يقول: اللهم أظلني تحت ظل عرشك، يوم لا ظل إلا ظل عرشك، وأسقني بكأس محمد -صلى الله عليه وسلم- شربة لا نظمأ بعدها أبدًا (¬٦).
_________
= للدردير ٢/ ٤١، السراج الوهاج ص ١٦٠، المنهاج ١/ ٥٨٦، المقنع ١/ ٤٤١، المحرر ١/ ٢٤٥.
(¬١) تبيين الحقائق ٢/ ١٧، شرح فتح القدير ٢/ ٤٥٢، العناية ٢/ ٤٥٢.
(¬٢) تبيين الحقائق ٢/ ١٧.
(¬٣) وهو الذي يلي الحجر الأسود من ناحية الباب.
زاد المعاد ٢/ ٢٢٦.
(¬٤) يقال: شاقه مشاقة وشقاقًا: خالفه، والعداوة بين فريقين، والخلاف بين الشيئين. سمي بذلك؛ لأن كل فريق قصد شقًا. أي: ناحية.
لسان العرب ١٠/ ١٨٣ مادة شقق، القاموس المحيط ٢/ ٧٣٨ مادة ش ق ق، مختار الصحاح ص ١٤٤ مادة ش ق ق، مجمل اللغة ص ٣٧٨ باب الشين وما بعدها في المضاعف والمطابق مادة شق.
(¬٥) تبيين الحقائق ٢/ ١٧، شرح فتح القدير ٢/ ٤٥٢.
(¬٦) تبيين الحقائق ٢/ ١٧، شرح فتح القدير ٢/ ٤٥٢.','3','277',NULL,NULL,'0');
INSERT INTO [b46] VALUES (953,'ثم يصلي ركعتين عند المقام.
<hr><span data-type="title">منحة السلوك</span>

وإذا أتى الركن الشامي (¬١) يقول: اللهم اجعله حجًا مبرورًا، وسعيًا مشكورًا، وذنبًا مغفورًا، وتجارة لن تبور (¬٢)، يا عزيز يا غفور (¬٣).
وإذا أتى الركن اليماني يقول: اللهم إني أعوذ بك من الكفر، وأعوذ بك من الفقر، ومن عذاب القبر، ومن فتنة المحيا والممات (¬٤)، وأعوذ بك من الخزي في الدنيا والآخرة (¬٥).
قوله: ثم يصلي ركعتين عند المقام.
وهذه الصلاة واجبة عندنا (¬٦).
_________
(¬١) الحجر الأسود، مع الركن اليماني يقال له: اليمانيان، ويقال للحجر الأسود مع الركن الذي يلي الحجر من ناحية الباب: العراقيان. ويقال للركنين اللذين يليان الحجر: الشاميان. ويقال للركن اليماني، والذي يلي الحجر من ظهر الكعبة: العربيان.
زاد المعاد ٢/ ٢٢٦.
(¬٢) البوار: الكساد. وبار عمله: أي بَطَلَ.
لسان العرب ٤/ ٦٨ مادة بور، القاموس المحيط ١/ ٣٤٠ مادة ب ور، مختار الصحاح ص ٢٨ مادة ب ور، المصباح المنير ١/ ٦٥ مادة بار.
(¬٣) تبيين الحقائق ٢/ ١٧، شرح فتح القدير ٢/ ٤٥٢.
(¬٤) أصل الفتنة: الاختبار، ثم استعملت فيما أخرجه الاختبار إلى المكروه، ثم استعملت في المكروه، فجاءت بمعنى الكفر. والمحيا والممات: مفعل من الحياة والموتد تقع على المصدر، والزمان، والمكان. وفتنة المحيا كثيرة، وفتنة الممات هي فتنة القبر وقيل: عند الاحتضار.
المطلع على أبواب المقنع ص ٨٢، لسان العرب ١٣/ ٣١٦ مادة فتن، المصباح المنير ٢/ ٤٦٢ مادة فتن.
(¬٥) تبيين الحقائق ٢/ ١٧، شرح فتح القدير ٢/ ٤٥٢.
(¬٦) وعند المالكية.
المبسوط ٤/ ١٢، الهداية ١/ ١٥٣، الكتاب ١/ ١٨٥، تبيين الحقائق ٢/ ١٧، الذخيرة ٣/ ٢٤٢، المعونة، الكافي ص ١٣٩، جواهر الإكليل ١/ ١٧٩، القوانين الفقهية ص ٩٠.','3','278',NULL,NULL,'0');
INSERT INTO [b46] VALUES (954,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

خلافًا للشافعي (¬١). وقد مر في عد الواجبات (¬٢).
ومن جملة سنن الطواف: أن يستلم الحجر كلما مر به إن استطاع (¬٣)؛ لما روي أنه -صلى الله عليه وسلم-: "طاف على بعير، كلما أتى على الركن، أشار إليه بشيء في يده وكبر" رواه أحمد، والبخاري (¬٤).
ويستحب أن يستلم الركن اليماني (¬٥)؛ لما روي عن ابن عمر -رضي الله عنهما- أنه قال: "ما تركت استلام هذين الركنين، الركن اليماني، والحجر الأسود، منذ رأيت رسول الله -صلى الله عليه وسلم- يستلمهما" رواه مسلم، وأبو داود (¬٦).
ولا يقبله (¬٧).
_________
(¬١) وأحمد. فإنهما يريان أنها سنة.
متن أبي شجاع ص ١٠٧، التذكرة ص ٨١، الشرح الكبير لابن قدامة ٣/ ٤١٤، الروض المربع ص ٢٠٥.
(¬٢) في ٣/ ٢٣٤.
(¬٣) وفاقًا للثلاثة.
بداية المبتدي ١/ ١٥٢، المختار ١/ ١٤٧، الهداية ١/ ١٥٢، الاختيار ١/ ١٤٧، مختصر خليل ص ٨٢، جواهر الإكليل ١/ ١٧٨، زاد المحتاج ١/ ٥٨٦، حاشية الشبراملسي ٣/ ٢٨٤، زاد المستقنع ص ٢٠٤، المحرر ١/ ٢٤٥.
(¬٤) أحمد ١/ ٢٦٤، والبخاري ٢/ ٥٨٣ كتاب الحج، باب التكبير عند الركن رقم ١٥٣٥ عن ابن عباس -رضي الله عنهما-.
(¬٥) مختصر الطحاوي ص ٦٣، بداية المبتدي ١/ ١٥٥، الهداية ١/ ١٥٢، المختار ١/ ١٤٧، الاختيار ١/ ١٤٧.
(¬٦) مسلم ٢/ ٩٢٤ كتاب الحج، باب استحباب استلام الركنين اليمانيين في الطواف دون الركنين الآخرين رقم ١٢٦٨ واللفظ له، وأبو داود ٢/ ١٧٦ كتاب المناسك باب استلام الأركان رقم ١٨٧٦ وتمامه عند مسلم: "في شدة ولا رخاء".
(¬٧) وإليه ذهب المالكية، والشافعية، والحنابلة. =','3','279',NULL,NULL,'0');
INSERT INTO [b46] VALUES (955,'ثم سعى بين الصَّفا والمروة سبعة أشواط،
<hr><span data-type="title">منحة السلوك</span>

وعند محمد: هو سنة فيقبله مثل الحجر الأسود (¬١)؛ لما روي عن ابن عباس -رضي الله عنهما- أنه -صلى الله عليه وسلم- "كان يقبل الركن اليماني، ويضع خده عليه" رواه الدارقطني (¬٢)، وعن ابن عباس -رضي الله عنهما- أنه -صلى الله عليه وسلم- "إذا استلم الركن اليماني قبله" رواه البخاري فى تاريخه (¬٣).

<span data-type="title" id=toc-480>[السعي]</span>
قوله: ثم سعى بين الصفا، والمروة، سبعة أشواط (¬٤). لما روي عن
_________
= مختصر الطحاوي ص ٦٣، تبيين الحقائق ٢/ ١٨، الهداية ١/ ١٥٢، الخرشي على خليل ٢/ ٣٢٨، التفريع ١/ ٣٣٧ المهذب ١/ ٢٢٢، الوجيز ١/ ١١٩، دليل الطالب ١/ ٢٦٥، هداية الراغب ص ٢٢٢.
(¬١) مختصر الطحاوي ص ٦٣، الهداية ١/ ١٥٢، تبيين الحقائق ٢/ ١٨.
(¬٢) ٢/ ٢٩٠ كتاب الحج باب، المواقيت رقم ٢٤٢، وابن خزيمة ٤/ ٢١٧ كتاب المناسك، باب وضع الخد على الركن اليماني عند تقبيله رقم ٢٧٢٧، والحاكم ١/ ٤٥٦ كتاب المناسك، والبيهقي ٥/ ٧٦ كتاب الحج، باب استلام الركن اليماني بيده، وأبو يعلى ٤/ ٤٧٢ رقم ٢٦٠٥.
من طريق عبد الله بن مسلم بن هرمز، عن سعيد بن جبير، ومجاهد، عن ابن عباس -رضي الله عنهما-.
قال الحاكم ١/ ٤٥٦: هذا حديث صحيح الإسناد، ولم يخرجاه.
وقال الذهبي: وعبد الله بن مسلم بن هرمز هذا ضعفه غير واحد، وقال أحمد: صالح الحديث.
وقال البيهقي ٥/ ٧٦: تفرد به عبد الله بن مسلم بن هرمز، وهو ضعيف.
قال الهيثمي في مجمع الزوائد ٣/ ٢٤١: وفيه عبد الله بن هرمز وهو ضعيف.
(¬٣) التاريخ الكبير ١/ ٢٨٩ في ترجمة إبراهيم بن سليمان المؤدب رقم ٩٣٠، والبيهقي في السنن الكبرى ٥/ ٧٦ كتاب الحج، باب استلام الركن اليماني بيده.
وقال -أي البيهقي-: تفرد به عبد الله بن مسلم بن هرمز وهو ضعيف.
(¬٤) وفاقًا للثلاثة.
الأصل ٢/ ٣٠٢، بداية المبتدي ١/ ١٥٤، الهداية ١/ ١٥٤، الشرح الصغير ١/ ٢٧٦، بلغة السالك ١/ ٢٧٦، شرح ابن قاسم الغزي على أبي شجاع ١/ ٣٢٧، حاشية البيجوري ١/ ٣٢٧، هداية الراغب ص ٢٢٣، المقنع ١/ ٤٤٩.','3','280',NULL,NULL,'0');
INSERT INTO [b46] VALUES (956,'يهرول فيما بين الميلين الأخضرين،
<hr><span data-type="title">منحة السلوك</span>

أبي هريرة -رضي الله عنه- أنه -صلى الله عليه وسلم-: "لما فرغ من طوافه، أتى الصفا فعلا عليه، حتى رأى البيت، ورفع يديه، فجعل يحمد الله تعالى ويدعو ما شاء أن يدعو" رواه مسلم، وأبو داود (¬١). وروى جابر -رضي الله عنه-، أنه -صلى الله عليه وسلم-: "بدأ بالصفا، فرقى عليه حتى رأى البيت، فاستقبل القبلة، ووحد الله تعالى وكبره، وقال: لا إله إلا الله وحده، أنجز وعده، ونصر عبده، وهزم الأحزاب وحده، ثم دعا مثل ذلك، فقال: مثل هذا ثلاث مرات، ثم نزل إلى المروة، حتى انتصبت قدماه في بطن الوادي، حتى إذا صعدنا مشى، حتى أتى المروة، ففعل على المروة مثل ما فعل على الصفا" رواه مسلم، وغيره (¬٢).
قوله: يهرول فيما بين الميلين الأخضرين (¬٣).
والهرولة: المشي بالسرعة (¬٤)؛ لما روى جابر -رضي الله عنه- أنه -صلى الله عليه وسلم-: نزل إلى
_________
(¬١) مسلم ٢/ ٨٨٨ كتاب الحج، باب حجة النبي رقم الحديث ١٢١٨ من حديث جابر -رضي الله عنه-، في قصة حجة النبي -صلى الله عليه وسلم- وليس فيه "ورفع يديه" ونص المراد منه: "ثم خرج من الباب إلى الصفا، فلما دنا من الصفا قرأ: إن الصفا والمروة من شعائر الله أبدأ بما بدأ الله به، فبدأ بالصفا، فرقى عليه حتى رأى البيت، فاستقبل القبلة، فوحد الله، وكبره وقال: لا إله إلا الله وحده لا شريك له، له الملك، وله الحمد، وهو على كل شيء قدير، لا إله إلا الله وحده، أنجز وعده، ونصر عبده، وهزم الأحزاب وحده، ثم دعا بين ذلك، قال مثل هذا ثلاث مرات"، وأبو داود ٢/ ١٧٥ كتاب المناسك، باب في رفع اليدين إذا رأى البيت رقم الحديث ١٨٧٢.
(¬٢) سبق تخريجه ٣/ ٢٧٦.
(¬٣) وفاقًا للثلاثة.
بداية المبتدي ١/ ١٥٣، كنز الدقائق ٢/ ٢٠، الهداية ١/ ١٥٣، تبيين الحقائق ٢/ ٢٠، متن الرسالة ص ٨٦، أقرب المسالك ص ٤٩، مختصر المزني ص ١٦٤، حاشية ابن قاسم على تحفة المحتاج ٢/ ١٠٤، الكافي لابن قدامة ١/ ٤٣٧، المقنع ١/ ٤٤٧.
(¬٤) لسان العرب ١١/ ٦٩٥ مادة هرول، القاموس المحيط ٤/ ٥٠٦ مادة هـ ر ول، مختار =','3','281',NULL,NULL,'0');
INSERT INTO [b46] VALUES (957,'ثم يقيم بمكة حرامًا، يطوف ما شاء بلا رَمَلٍ ولا سعي، ويختم كل طوافٍ بركعتين،
<hr><span data-type="title">منحة السلوك</span>

المروة، حتى إذا انتصبت قدماه رمل في بطن الوادي، حتى إذا صعد مشى" رواه أبو داود (¬١).
قوله: ثم يقيم بمكة حرامًا.
لأنه محرم بالحج، فلا يتحلل قبل الإتيان بأفعاله (¬٢).
قوله: يطوف ما شاء.
لأنه يشبه الصلاة، ولا يرمل، ولا يسعى؛ لأن السعي لا يجب فيه إلا مرة واحدة، والتنفل به غير مشروع (¬٣). والرمل لم يشرع إلا مرة واحدة في طواف بعده سعي (¬٤)، ويختم كل طواف بركعتين على ما بينا (¬٥).
_________
= الصحاح ص ٢٨٩ مادة هـ ر ول، فقه اللغة وسر العربية للثعالبي ص ١٨٣، المصباح المنير ٢/ ٦٣٧ مادة هرول.
(¬١) سبق تخريجه ٣/ ٢٧٦.
(¬٢) شرح فتح القدير ٢/ ٤٦٣، العناية ٢/ ٤٦٣، المختار ١/ ١٤٩، الكتاب ١/ ١٨٧، الاختيار ١/ ١٤٩.
(¬٣) المختار ١/ ١٤٩، الكتاب ١/ ١٨٧، تبيين الحقائق ٢/ ٢٢، مختصر الطحاوي ص ١٦٤، الاختيار ١/ ١٤٩، الهداية ١/ ١٥٤، تحفة الفقهاء ١/ ٤٠٣، حاشية ابن قطلوبغا على مجمع البحرين، وشرحه لابن فرشته (مخطوط) لوحة ٨٩/ ب النسخة الأصلية لدى مكتبة الأزهر، تحت رقم ٣٣١٥١/ ٢٢٧٠ حليم.
(¬٤) وإليه ذهب الشافعية في قول لهم، والقول الآخر: يختص الرمل بطواف القدوم. وإلى القول الآخر للشافعية: ذهب المالكية، والحنابلة.
تحفة الفقهاء ١/ ٤٠١، تبيين الحقائق ٢/ ٢٢، الشرح الصغير ١/ ٢٧٦، القوانين الفقهية ص ٨٩، جواهر الإكليل ١/ ١٧٨، شرح المحلي على المنهاج ٢/ ١٠٧، السراج الوهاج ص ١٦٠، كشاف القناع ٢/ ٤٨٠، منتهى الإرادات ٢/ ٥١.
(¬٥) وفاقًا للثلاثة. =','3','282',NULL,NULL,'0');
INSERT INTO [b46] VALUES (958,'ثم يخرج غَدَاة التروية إلى منى، ثم يتوجه إلى عَرَفاتٍ
<hr><span data-type="title">منحة السلوك</span>

قوله: ثم يخرج غداة التروية إلى منى (¬١).
لما روى جابر -رضي الله عنه- أنه -صلى الله عليه وسلم-: "توجه قبل صلاة الظهر يوم النحر إلى منى، وصلى بها الظهر، والعصر، والمغرب، والعشاء، والصبح" رواه مسلم، وغيره (¬٢). ويستحب أن ينزل عند مسجد الخيف (¬٣) (¬٤).
قوله: ثم يتوجه إلى عرفات (¬٥).
لما روى ابن عمر -رضي الله عنهما- أنه -صلى الله عليه وسلم-: "غدا من منى حين طلع الصبح في صبيحة يوم عرفة، حتى أتى عرفة" الحديث رواه أحمد، وأبو داود (¬٦).
_________
= مختصر الطحاوي ص ٦٤، الأصل ٢/ ٣٠٥، تحفة الفقهاء ١/ ٤٠١، تبيين الحقائق ٢/ ٢٢، شرح الزرقاني على خليل ٢/ ٢٧٦، حاشية البناني ٢/ ٢٧٦، جواهر الإكليل ١/ ١٧٩، قليوبي ٢/ ١٠٨، عميرة ٢/ ١٠٨، شرح منتهى الإرادات ٢/ ٥١، الروض المربع ص ٢٠٤.
(¬١) مختصر الطحاوي ص ٦٤، الكتاب ١/ ١٨٧، تبيين الحقائق ٢/ ٢٣.
(¬٢) سبق تخريجه ٣/ ٢٧٦.
(¬٣) تبيين الحقائق ٢/ ٢٣.
(¬٤) الخيف: ما ارتفع من الوادي قليلًا عن مسيل الماء. ومسجد الخيف، بني في خيف الجبل. والخيف لا يكون إلا بين جبلين. ويسمى: خيف بني كنانة، والمحصب، والأبطح، والبطحاء، وشعب الصفي، والمعرس.
لسان العرب ٩/ ١٠٣ مادة خيف، القاموس المحيط ٢/ ١٣٧ مادة خ ي ف، المغرب ص ١٥٧ مادة الخيف، المصباح المنير ١/ ١٨٦ مادة الخيف، معجم البلدان ٢/ ٤١٢، معجم ما استعجم ١/ ٥٢٦، تاريخ مكة للأزرقي ٢/ ١٦٠ - ١٨١ - ٢٧٥، تهذيب الأسماء واللغات ٤/ ١٥٤، النظم المستعذب ١/ ٢١٣، فتح الباري ٣/ ٥٩٠.
(¬٥) الكتاب ١/ ١٨٨، المختار ١/ ١٤٩، مختصر الطحاوي ص ٦٤، الاختيار ١/ ١٤٩، تبيين الحقائق ٢/ ٢٣.
(¬٦) أحمد ٢/ ١٢٩، وأبو داود ٢/ ١٨٨ كتاب المناسك، باب الخروج إلى عرفة رقم ١٩١٣، =','3','283',NULL,NULL,'0');
INSERT INTO [b46] VALUES (959,'فإذا زالت الشمس صلَّى الإمام بالناس الظهر والعصر في وقت الظهر بأذانٍ وإقامتين، ولا يجمع المنفرد،
<hr><span data-type="title">منحة السلوك</span>

قوله: فإذا زالت الشمس. أي: شمس يوم عرفة، صلى الإمام بالناس الظهر، والعصر، في وقت الظهر، بأذان وإقامتين (¬١).
لما روى جابر -رضي الله عنه- في حجة النبي-صلى الله عليه وسلم-: "ثم أذن، ثم أقام فصلى الظهر، ثم أقام فصلى العصر، ولم يصل بينهما شيئًا" رواه مسلم (¬٢).
قوله: ولا يجمع المنفرد.
هذا عند أبي حنيفة (¬٣). خلافًا لهما (¬٤).
_________
= وأصله في صحيح مسلم ٢/ ٩٣٣ كتاب الحج، باب التلبية والتكبير في الذهاب من منى إلى عرفات في يوم عرفة رقم ١٢٨٤ بلفظ: "غدونا مع رسول الله -صلى الله عليه وسلم- من منى إلى عرفات" وفي لفظ: "كنا مع رسول الله -صلى الله عليه وسلم- في غداة عرفة، فمنا المكبر، ومنا المهلل".
(¬١) وإليه ذهب الشافعية، والحنابلة، وهو قول: أبي ثور.
وعند المالكية: بأذانين وإقامتين على الأشهر، وقيل: بأذان واحد وإقامتين، وقيل: بإقامتين فقط.
الاختيار ١/ ١٤٩، الكتاب ١/ ١٨٨، المختار ١/ ١٤٩، التفريع ١/ ٣٤٠، حاشية الدسوقي ٢/ ٤٤، بلغة السالك ١/ ٢٧٨، فتح الوهاب ٢/ ٤٥٥، حاشية الجمل ٢/ ٤٥٥، إخلاص الناوي ١/ ٣٣٢، هداية الراغب ص ٨٣، المغني ٣/ ٤٣٣.
(¬٢) سبق تخريجه ٣/ ٢٧٦.
(¬٣) العناية ٢/ ٤٧١، المبسوط ٤/ ٥٣، الهداية ١/ ١٥٦.
(¬٤) حيث قالا: يجمع بينهما المنفرد أيضًا: لأن جواز الجمع للحاجة إلى امتداد الوقوف، والمنفرد محتاج إليه، وليس الإمام شرطًا في الصلاتين. وإلى ذلك ذهب المالكية، والشافعية، والحنابلة.
الهداية ١/ ١٥٦، الأصل ٢/ ٣٠٦، المبسوط ٤/ ٥٣، شرح فتح القدير ٢/ ٤٧١، جواهر الإكليل ١/ ١٨٠، المعونة على مذهب عالم المدنية الإمام مالك ١/ ٥٧٩، المنهاج =','3','284',NULL,NULL,'0');
INSERT INTO [b46] VALUES (960,'والإمام شرط فيها،
<hr><span data-type="title">منحة السلوك</span>

والأصل في ذلك: أن الجمع بين الظهر والعصر، إنما يجوز بشرط الإمام، والإحرام عند أبي حنيفة، حتى لو صلاهما، أو صلى أحدهما منفردًا، أو غير محرم لم يجز له الجمع (¬١). والمراد بالإحرام: إحرام الحج.
ثم قيل: لا بد من الإحرام قبل الزوال؛ ليجوز الجمع، وإن لم يكن محرمًا قبل الزوال وأحرم بعده، لم يجز له الجمع (¬٢).
والصحيح: أنه يكتفى بالتقديم على الصلاتين (¬٣).
ومن شرط الجمع: أن تكون صلاة الظهر صحيحة، حتى لو تبين فسادها بعد ما صلاهما، أعاد الظهر والعصر جميعًا (¬٤).
وقال زفر: تراعى هذه الشرائط في العصر خاصة (¬٥).
وعندهما: لا يشترط الإمام في حق العصر، حتى يجوز للمنفرد أن يجمع (¬٦).
وعلى هذا الخلاف: جواز الجمع للإمام وحده.
فعنده: لا يجوز (¬٧).
_________
= ١/ ٥٩٥، مغني المحتاج ١/ ١٣٥، كشاف القناع ٢/ ٤٩٢، الشرح الكبير لأبي الفرج عبد الرحمن بن قدامة ٣/ ٤٣٣.
(¬١) العناية ٢/ ٤٧٢، الهداية ١/ ١٥٦، تبيين الحقائق ٢/ ٢٤.
(¬٢) الهداية ١/ ١٥٦، تبيين الحقائق ٢/ ٢٤.
(¬٣) تبيين الحقائق ٢/ ٢٤، العناية ٢/ ٤٧١.
(¬٤) الهداية ١/ ١٥٦، تبيين الحقائق ٢/ ٢٤.
(¬٥) العناية ٢/ ٤٧٢، الهداية ١/ ١٥٥، تبيين الحقائق ٢/ ٢٤.
(¬٦) تبيين الحقائق ٢/ ٢٤، الهداية ١/ ١٥٦.
(¬٧) تبيين الحقائق ٢/ ٢٤.','3','285',NULL,NULL,'0');
INSERT INTO [b46] VALUES (961,'ثم يقف الإمام بعرفة راكبًا بقرب الجبل،
<hr><span data-type="title">منحة السلوك</span>

خلافًا لهما (¬١). ولو نفروا عنه بعد الشروع جاز له الجمع (¬٢).
واختلفوا فيما إذا نفروا عنه قبل الشروع على قوله (¬٣).
والمراد بالإمام: هو الإمام الأعظم، أو نائبه. ولو مات الإمام وهو الخليفة، جمع نائبه، أو صاحب شرطة (¬٤). فافهم.

<span data-type="title" id=toc-481>[الوقوف بعرفة]</span>
قوله: ثم يقف الإمام بعرفة راكبًا بقرب الجبل (¬٥).
وهو: الذي عند الصخرات السود الكبار، وهو الجبل الذي بوسط عرفات. يقال له: "إلال" على وزن هلال (¬٦). والجبل يسمى: جبل الرحمة (¬٧). والموقف: الموقف الأعظم (¬٨).
_________
(¬١) حيث يريان: جواز الجمع للإمام وحده.
تبيين الحقائق ٢/ ٢٤.
(¬٢) تبيين الحقائق ٢/ ٢٤.
(¬٣) فوجه الجواز: الضرورة إذ لا يقدر أن يجعل غيره مقتديًا به.
تبيين الحقائق ٢/ ٢٤.
(¬٤) تبيين الحقائق ٢/ ٢٤.
(¬٥) بداية المبتدي ١/ ١٥٦، العناية ٢/ ٤٧٣، الهداية ١/ ١٥٦، شرح فتح القدير ٢/ ٤٧٣.
(¬٦) إلالُ، وأَلال: جبل بمكة. والأَلال -بالفتح-: جبل بعرفات. والإلُ حَبْل من رمل، به يقف الناس في عرفات عن يمين الإمام وقيل: سمي بذلك؛ لأن الحجيج إذا رأوا الجبل ألوا، أي: رفعوا صوتهم بالدعاء.
لسان العرب ١١/ ٢٧ مادة ألل، معجم ما استعجم ١/ ١٨٥، القاموس المحيط ١/ ١٧١ مادة أل ل، تاريخ مكة للأزرقي ٢/ ١٩٤، تهذيب الأسماء واللغات ٣/ ١٩.
(¬٧) تبيين الحقائق ٢/ ٢٤.
(¬٨) الهداية ١/ ١٥٦، العناية ٢/ ٤٧٣.','3','286',NULL,NULL,'0');
INSERT INTO [b46] VALUES (962,'وعرفات كلها موقف إلا بطن عُرَنَة،
<hr><span data-type="title">منحة السلوك</span>

وذلك لما روي أنه -صلى الله عليه وسلم-: "ركب القصواء (¬١)، حتى أتى الموقف، فجعل بطن ناقته القصواء إلى الصخرات، وجعل جبل المشاة بين يديه، فاستقبل القبلة، فلم يزل واقفًا حتى غربت الشمس" رواه مسلم، وأبو داود، وابن ماجه (¬٢).
قوله: وعرفات كلها موقف، إلا بطن (¬٣) عُرَنة (¬٤).
لقوله -صلى الله عليه وسلم-: "عرفات كلها موقف، وارتفعوا عن بطن عرنة" رواه البخاري (¬٥).
_________
(¬١) القصواء: اسم لناقة النبي -صلى الله عليه وسلم- ولم تكن قصواء؛ لأن القصواء المقطوعة الأذن، وقيل: هي التي هاجر عليها.
تركة النبي ص ٩٩ وما بعدها، زاد المعاد ١/ ١٣٤، غريب الحديث لأبي عبيد ٢/ ٢٠٨، لسان العرب ١٥/ ١٨٤ مادة قصا، النظم المستعذب ١/ ٢١٠.
(¬٢) سبق تخريجه ٣/ ٢٧٦.
(¬٣) البطن: ما غمض، واطمأن من الأرض. وعرنة: على وزن هُمزة: واد بحذاء عرفات، وقيل: بطن عرنة: مسجد عرفة، والمسيل كله. وهو في مقدمة مسجد نمرة وليس هو من موقف عرفات، بل هو حدها الغربي.
معجم البلدان ٤/ ١١١ مادة عرن، معجم ما استعجم ٢/ ١١٩١ مادة مُحَسِّر، لسان العرب ١٣/ ٥٥ مادة بطن، القاموس المحيط ١/ ٢٨٩ مادة ب ط ن.
(¬٤) بداية المبتدي ١/ ١٥٦، الاختيار ١/ ١٥٠، الهداية ١/ ١٥٦، الاختيار ١/ ١٥٠.
(¬٥) لم أجده في البخاري، وإنما رواه: أحمد ٤/ ٨٢، وابن حبان ٩/ ١٦٦ كتاب الحج، باب الوقوف بعرفة، والمزدلفة، والرفع منهما رقم ٣٨٥٤، وابن عدي في الكامل ٧/ ٢٧١٦ في ترجمة يزيد بن عبد الملك بن المغيرة أبي نوفل النوفلي رقم ١١٨، والبيهقي ٩/ ٢٩٥ كتاب الضحايا، باب من قال الأضحى جائز يوم النحر، وأيام منى كلها، والبزار "كشف الأستار" ٢/ ٢٧ كتاب الحج، باب عرفة كلها موقف رقم ١٢٦، والطبراني في الكبير ١٥٨٣ من حديث جبير بن مطعم -رضي الله عنه-. بلفظ: "كل عرفات موقف وارفعوا عن عرنة" زاد أحمد: "عن بطن عرنة .. وكل مزدلفة موقف وارفعوا عن محسر". =','3','287',NULL,NULL,'0');
INSERT INTO [b46] VALUES (963,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

ويحمد الله تعالى، ويكبر، ويهلل، ويلبي، ويصلي، ويدعو الله لحاجته (¬١)؛ لقوله -صلى الله عليه وسلم-: "أفضل الدعاء دعاء يوم عرفة، وأفضل ما قلته أنا والنبيون من قبلي يوم عرفة: لا إله إلا الله وحده لا شريك له، له الملك وله الحمد، يحيي ويميت، وهو حي لا يموت، بيده الخير، وهو على كل شيء قدير" رواه مالك، والترمذي، وأحمد، وغيرهم (¬٢). وكان -صلى الله عليه وسلم- يجتهد في
_________
= قال الهيثمي في مجمع الزوائد ٣/ ٢٥١: ورجاله موثقون.
وقال في التلخيص الحبير ٢/ ٢٥٥: وفي إسناده انقطاع، فإنه من رواية عبد الله بن عبد الرحمن بن أبي حيي.
ورواه ابن ماجه ٢/ ١٠٠٢ كتاب المناسك، باب الموقف بعرفات رقم ٣٠١٢ من طريق هشام بن عمار ثنا القاسم بن عبد الله العمري، ثنا محمد بن المنكدر عن جابر بن عبد الله -رضي الله عنهما- بلفظ: "كل عرفة موقف، وارتفعوا عن بطن عرفة، وكل المزدلفة موقف، وارتفعوا عن بطن محسر".
قال في مصباح الزجاجة ٣/ ٢٧: هذا إسناد ضعيف، القاسم بن عبد الله بن عمر، قال فيه أحمد بن حنبل: كان كذابًا، يضع الحديث، ترك الناس حديثه وقال أبو حاتم، وأبو زرعة، والنسائي: متروك الحديث. وقال في الدراية ٢/ ١٩: وإسناده ضعيف.
ورواه البيهقي ٥/ ١١٥ كتاب الحج باب حيث ما وقف من عرفة أجزأه، والطحاوي في مشكل الآثار ٢/ ٧٢ باب بيان مشكل ما روي عن رسول الله -صلى الله عليه وسلم- من قوله: وارفعوا عن بطن عرنة، والحاكم في المستدرك ١/ ٤٦٢ كتاب المناسك.
قال الحاكم في المستدرك ١/ ٤٦٢: هذا حديث صحيح على شرط مسلم ولم يخرجاه، وسكت عنه الذهبي.
وقال في الجوهر النقي ٥/ ١١٥: واستثناؤه صحيح عند الفقهاء، ومحفوظ.
(¬١) الأصل ٢/ ٣٠٨، كنز الدقائق ٢/ ٢٥، تبيين الحقائق ٢/ ٢٥، الهداية ١/ ١٥٦.
(¬٢) مالك ١/ ٤٢٢ كتاب الحج، باب جامع الحج رقم ٢٤٦، والترمذي ٩/ ٢١٩ في الدعوات، باب في دعاء يوم عرفة رقم الحديث ٣٥٧٩، وأحمد ٢/ ٢١٠، والبيهقي ٥/ ١١٧ كتاب الحج باب أفضل الدعاء دعاء يوم عرفة. عن طلحة بن عبيد الله بن كريز أن رسول الله -صلى الله عليه وسلم- قال ... ". =','3','288',NULL,NULL,'0');
INSERT INTO [b46] VALUES (964,'فإذا غربت الشمسُ، أفاض إلى مزدلفة،
<hr><span data-type="title">منحة السلوك</span>

الدعاء في هذا الموقف. حتى روي أنه -صلى الله عليه وسلم-: "دعا عشية عرفة لِأمَّته بالمغفرة، فاستجيب له، إلا في الدماء، والمظالم، ثم أعاد الدعاء بالمزدلفة، فأجيب حتى الدماء والمظالم" أخرجه ابن ماجه (¬١).

<span data-type="title" id=toc-482>[المبيت بمزدلفة]</span>
قوله: فإذا غربت الشمس أفاض. أي: الإمام إلى مزدلفة (¬٢).
لحديث علي -رضي الله عنه- أنه -صلى الله عليه وسلم-: "دفع حين غابت الشمس" رواه أبو داود، وغيره (¬٣).
_________
= قال ابن عبد البر في التمهيد ٦/ ٣٩: لا خلاف عن مالك في إرسال هذا الحديث كما رأيت، ولا أحفظه بهذا الإسناد مسندًا من وجه يحتج بمثله، وقد جاء مسندًا من حديث علي وعبد الله بن عمرة، ولابن عدي ٩١١ عن نافع عن ابن عمر نحوه.
لفظ الترمذي: "خير الدعاء ... وخير ما قلت ... " من طريق أبي عمر، ومسلم بن عمرو، والحذاء قال: حدثني عبد الله بن نافع، عن حماد بن أبي حميد، عن عمرو بن شعيب، عن أبيه، عن جده، أن النبي -صلى الله عليه وسلم- قال ... ".
قال الترمذي: هذا حديث حسن غريب من هذا الوجه، وحماد بن أبي حميد هو: محمد ابن أبي حميد، وهو أبو إبراهيم الأنصاري المديني وليس هو بالقوي عند أهل الحديث.
وقال البيهقي ٥/ ١١٧: هذا مرسل، وقد روي عن مالك بإسناد آخر موصولًا، ووصله ضعيف.
(¬١) ٢/ ١٠٠٢ كتاب المناسك، باب الدعاء بعرفة رقم الحديث ٣٠١٣، وعبد الله بن أحمد بن حنبل في مسند أبيه ٤/ ١٤، وأبو يعلى في مسنده ٣/ ١٤٩ في مسند العباس بن مرداس السلمي رقم الحديث ١٥٧٨.
من طريق عبد القاهر بن السري عن عبد الله بن كنانة بن عباس بن مرداس، عن أبيه كنانة، عن أبيه عباس بن مرداس، أن النبي -صلى الله عليه وسلم- ....
قال البوصيري في مصباح الزجاجة ٣/ ٢٨: هذا إسناد ضعيف.
(¬٢) مختصر الطحاوي ص ٦٥، الكتاب ١/ ١٨٩، الهداية ١/ ١٥٧.
(¬٣) أبو داود ٢/ ١٩٠ كتاب المناسك باب الدفعة من عرفة رقم ١٩٢٢، والترمذي ٣/ ٢٤٠ كتاب الحج، باب ما جاء أن عرفة كلها موقف رقم ٨٨٥، وأحمد ١/ ٧٥، عن =','3','289',NULL,NULL,'0');
INSERT INTO [b46] VALUES (965,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
=  علي -رضي الله عنه- قال: "وقف النبي -صلى الله عليه وسلم- بعرفة وقال: هذه عرفة، وعرفة كلها موقف، ثم أفاض حين غربت الشمس".
قال الترمذي ٣/ ٢٤٣: حسن صحيح.
وقال النووي في المجموع ٨/ ١٢٦: حديث صحيح.
* وقد اختلف العلماء في وجوب الدم على من أفاض من عرفة قبل الغروب على ثلاثة أقوال:
القول الأول: أنه يجب عليه دم:
وهو قول: أكثر العلماء. منهم عطاء، والثوري وأبو حنيفة، والشافعي في القديم، وأبو ثور، وأحمد بن حنبل (أ) وغيرهم.
القول الثاني: أنه يستحب:
وهو قول: الشافعي في الجديد (ب). قال النووي: "إنه الأصح باتفاق الأصحاب، وهو نصه في الإملاء" (جـ).
القول الثالث: أنه لا شيء عليه:
وهو رواية عن الإمام أحمد (د).
الأدلة:
أدلة القول الأول:
١ - ما روي عن ابن عباس -رضي الله عنهما- أنه قال: "من نسي من نسكه شيئًا فليهرق دمًا".
وفي رواية: "من نسي من نسكه شيئًا أو تركه فليهرق دمًا".
قال أيوب: لا أدري قال: ترك، أو نسي (هـ).
٢ - ولأن نفس الوقوف ركن، واستدامته إلى غروب الشمس واجبة لما فيها من إظهار =
_________
(أ) المبسوط ٤/ ٥٦، شرح فتح القدير ٢/ ٣٧٦، المهذب ٢/ ٧٧٨، المجموع ٨/ ١٠٢، المغني ٣/ ٤١٤، الشرح الكبير لابن قدامة ٢/ ٢٣٤.
(ب) المهذب ٢/ ٧٧٩، المجموع ٨/ ١٠٢.
(جـ) المجموع ٨/ ١٠٢.
(د) الإنصاف ٤/ ٣٠.
(هـ) أخرجه الإمام مالك في الموطأ ١/ ٣٩٧، ٤١٩، والبيهقي في سننه ٥/ ١٥٢، وقد صححه النووي، وقال: إنه موقوف على ابن عباس لا مرفوعًا، المجموع ٨/ ٩٩.','3','290',NULL,NULL,'0');
INSERT INTO [b46] VALUES (966,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
=  مخالفة المشركين، وقد فعله رسول الله -صلى الله عليه وسلم-، وأمر به، وترك الواجب يوجب الجبر بالدم (أ).
دليل القول الثاني والثالث:
استدلوا بالقياس، حيث قاسوا الوقوف بالنهار على الوقوف بالليل في عدم وجوب الدم، إذ أن من أدرك الوقوف بعرفة قبل طلوع فجر يوم النحر، لا يلزمه دم عندهم، بجامع أن كلًا منهما زمان للوقوف.
فقال الشافعية: إنه يستحب؛ لأنه وقف في إحدى زماني الوقوف، فلا يلزمه دم للزمان الآخر كما لو وقف في الليل دون النهار (ب).
وكذا قال المرداوي في تعليله لرواية أحمد التي أوردها: إنه لا يلزمه دم قياسًا على من وقف ليلًا (جـ).
الترجيح:
الراجح هو القول الأول: لصحة مستندهم، ووجاهة ما عللوا به.
والدم الواجب على من أفاض من عرفة قبل غروب الشمس شاة كما ذكر ابن قدامة.
وقال ابن جريج: عليه بدنة.
وقال الحسن البصري: عليه هدي من الإبل (د).
قال ابن قدامة: "ولنا أنه واجب لا يفسد الحج بفواته فلم يوجب البدنة كالإحرام من الميقات " ا. هـ (هـ).
فقد قاس الإفاضة من عرفة قبل الغروب على عدم الإحرام من الميقات؛ إذ أن من لم يحرم من الميقات عليه شاة؛ لإخلاله بواجب من واجبات الحج وهو عدم الإحرام من الميقات، فكذا هذا. وهو الراجح.
_________
(أ) المبسوط ٤/ ٥٦.
(ب) المهذب ٢/ ٧٧٩، المجموع ٨/ ١٠٢.
(جـ) الإنصاف ٤/ ٣٠.
(د) المغني ٣/ ٤١٤، الشرح الكبير لابن قدامة ٢/ ٢٣٤.
(هـ) المغني ٣/ ٤١٤، الشرح الكبير لابن قدامة ٢/ ٢٣٤.','3','291',NULL,NULL,'0');
INSERT INTO [b46] VALUES (967,'ووقف بقرب قُزَح. ومزدلفة كلها موقف إلا وادي مُحَسِّر.
<hr><span data-type="title">منحة السلوك</span>

قوله: ووقف بقرب قزح (¬١).
والمراد من هذا الوقوف النزول؛ لأن الوقوف لا يكون إلا بعد صلاة الفجر بغلس، وإنما ينزل هنا؛ لأنه الموقف (¬٢)؛ لما روي: "أنه -صلى الله عليه وسلم-: لما أصبح وقف على قُزَح" رواه أبو داود (¬٣).
قال في الصحاح: قزح اسم جبل بالمزدلفة (¬٤).
قال في الكشاف (¬٥): المشعر الحرام قزح، وهو الجبل الذي يقف عليه الإمام، وعليه الميقدة.
قوله: ومزدلفة كلها موقف إلا وادي مُحَسِّر (¬٦).
_________
(¬١) الكتاب ١/ ١٨٩، بداية المبتدي ١/ ١٥٧، الهداية ١/ ١٥٧.
(¬٢) تحفة الفقهاء ١/ ٤٠٧، الهداية ١/ ١٥٧، المبسوط ٤/ ١٤.
(¬٣) ٢/ ١٩٣ كتاب المناسك، باب الصلاة يجمع رقم الحديث ١٩٣٥ قال: حدثنا أحمد بن حنبل، ثنا يحيى بن آدم، ثنا سفيان، عن عبد الرحمن بن عياش، عن زيد بن علي، عن أبيه، عن عبيد الله بن أبي رافع، عن علي -رضي الله عنه- قال: "فلما أصبح يعني النبي -صلى الله عليه وسلم- وقف على قزح، فقال: هذا قزح، وهو الموقف، وجمع كلها موقف".
ورواه أحمد ١/ ٧٦، والترمذي ٣/ ٢٤١ كتاب الحج، باب ما جاء أن عرفة كلها موقف رقم الحديث ٨٨٥، وأبو يعلى في المسند ١/ ٢٦٤ رقم الحديث ٣١٢.
عن علي -رضي الله عنه-. قال الترمذي ٣/ ٢٤١: حديث حسن صحيح.
(¬٤) الصحاح ١/ ٣٩٦ مادة قزح.
(¬٥) الكشاف عن حقائق التنزيل لأبي القاسم جار الله محمود بن عمر الزمخشري ١/ ١٢٤.
(¬٦) وفاقًا للمالكية، والشافعية، والحنابلة.
المختار ١/ ١٥٢، مختصر الطحاوي ص ٦٥، الاختيار ١/ ١٥٢، الشرح الصغير ١/ ٢٧٨، الشرح الكبير للدردير ٢/ ٤٤، شرح المحلي على المنهاج ٢/ ١١٣، قليوبي ٢/ ١١٣، الكافي لابن قدامة ١/ ٤٤٤، منتهى الإرادات ٢/ ٥٩.','3','292',NULL,NULL,'0');
INSERT INTO [b46] VALUES (968,'ويصلي بالناس المغرب والعشاء في وقت العشاء، بأذان وإقامة واحدةٍ، ولا يجمع المنفرد،
<hr><span data-type="title">منحة السلوك</span>

لقوله -صلى الله عليه وسلم-: "والمزدلفة كلها موقف، وارتفعوا عن بطن محسر" (¬١) رواه البخاري (¬٢).
قوله: ويصلي بالناس المغرب، والعشاء في وقت العشاء، بأذان، وإقامة واحدة (¬٣).
وقال زفر: بأذان وإقامتين (¬٤). واختاره الطحاوي (¬٥).
ولنا: حديث ابن عمر -رضي الله عنهما-: "أنه -صلى الله عليه وسلم- أذن للمغرب بجمع، فأقام، ثم صلى العشاء بالإقامة الأولى" قال ابن حزم (¬٦):
_________
(¬١) بطن الوادي: ما اطمأن منه، ومحسر -بضم الميم، وفتح الحاء، ثم سين مكسورة مشددة، ثم راء-: واد بين مزدلفة ومنى، قدر رمية بحجر.
معجم البلدان ١/ ٤٤٩ مادة محسر، معجم ما استعجم ٢/ ١١٩١ مادة محسر، لسان العرب ١٣/ ٥٥ مادة بطن، المطلع على أبواب المقنع ص ١٩٦، لغة الفقه ص ١٥٦، تاريخ مكة للأزرقي ٢/ ١٩٢.
(¬٢) سبق تخريجه ٣/ ٢٨٧ وهو قطعة من الحديث: "عرفة كلها موقف، وارفعوا عن بطن عرنة، والمزدلفة كلها موقف وارفعوا عن بطن محسر".
(¬٣) كنز الدقائق ٢/ ٢٧، بدائع الصنائع ٢/ ١٥٤، تبيين الحقائق ٢/ ٢٧.
(¬٤) وإليه ذهب الشافعية، والحنابلة، وهو قول: أبي ثور.
وعند المالكية: قيل: بأذانين وإقامتين على الأشهر، وقيل: بأذان واحد وإقامتين، وقيل: بإقامتين فقط.
بدائع الصنائع ٢/ ١٥٤، تبيين الحقائق ٢/ ٢٧، الشرح الكبير للدردير ٢/ ٤٤، الكافي لابن عبد البر ص ١٤٣، جواهر الإكليل ١/ ١٨٠، الحاوي الكبير ٤/ ١٧٦، المجموع ٨/ ١٤٩، المستوعب ٤/ ٢٣٥، حاشية المقنع ١/ ٤٥٢.
(¬٥) مختصر الطحاوي ص ٦٥.
(¬٦) هو أبو محمد علي بن أحمد بن سعيد بن حزم الأندلسي، ولد عام ٣٨٤ هـ بقرطبة، عالم =','3','293',NULL,NULL,'0');
INSERT INTO [b46] VALUES (969,'ومن صلى المغرب في الطريق أعاد،
<hr><span data-type="title">منحة السلوك</span>

رواه مسلم (¬١).
قوله: ومن صلى المغرب في الطريق. أي: في طريق المزدلفة أعاد، وكذا لو صلاها في عرفات (¬٢).
وقال أبو يوسف: يجوز؛ لأنه صلاها في وقتها المعهود (¬٣).
ولهما: حديث أسامة بن زيد -رضي الله عنه-: "أن رسول الله -صلى الله عليه وسلم- دفع من عرفة، حتى إذا كان بالشعب (¬٤) نزل فبال، ولم يسبغ الوضوء، قلت: الصلاة
_________
= الأندلسي في عصره، كانت له الوزارة فانصرف عنها إلى التأليف والعلم، نشأ في تنعم ورفاهية ورزق ذكاء، كان فقيهًا يستنبط الأحكام من الكتاب والسنة على طريقة أهل الظاهر. توفي سنة ٤٥٦ هـ. من تصانيفه: المحلى، مداوة النفوس، الإحكام في أصول الأحكام. وفيات الأعيان/ ٣٢٥، تذكرة الحفاظ ٣/ ١١٤٦، نفح الطيب ٢/ ٧٧، لسان الميزان ٤/ ١٩٨، سير أعلام النبلاء ١٨/ ١٨٤.
(¬١) ٢/ ٩٣٨ كتاب الحج، باب الإفاضة من عرفات إلى المزدلفة رقم ١٢٨٨ ولفظه: عن ابن عمر -رضي الله عنهما-، قال: "جمع رسول الله -صلى الله عليه وسلم- بين المغرب والعشاء بجمع، صلى المغرب ثلاثًا، والعشاء ركعتين، بإقامة واحدة".
(¬٢) وعند المالكية: من صلى قبلها من غير علة، أعاد إذا أتاها ندبًا.
الهداية ١/ ١٥٨، بداية المبتدي ١/ ١٥٨، مختصر الطحاوي ص ٦٥، بدائع الصنائع ٢/ ١٥٥.
الشرح الكبير للدردير ٢/ ٤٥، القوانين ص ٩٠، منح الجليل ٢/ ٢٧٧، الشرح الصغير ١/ ٢٧٩، المعونة ١/ ٥٨١.
(¬٣) وإليه ذهب الشافعية، والحنابلة.
الهداية ١/ ١٥٣، بداية المبتدي ١/ ١٥٨، مختصر الطحاوي ص ٦٥، بدائع الصنائع ٢/ ١٥٥، نهاية المحتاج ٣/ ٢٩٧، روضة الطالبين ٣/ ٩٤، المستوعب ٤/ ٢٣٥، حاشية المقنع ١/ ٤٥٤.
(¬٤) -بالكسر- الطريق. وقيل: الطريق في الجبل. وهذا الشعب يقال له: شعب الحبال، =','3','294',NULL,NULL,'0');
INSERT INTO [b46] VALUES (970,'ويبيتُ بها، ويصلي بهم الفجر بغلس، ثم يقف بالمشعر الحرام ويدعو.
<hr><span data-type="title">منحة السلوك</span>

يا رسول الله، قال: الصلاة أمامك، فركب، فلما جاء المزدلفة نزل وتوضأ، فأسبغ الوضوء" الحديث رواه البخاري، ومسلم (¬١).
قوله: ويبيت بها. أي: بالمزدلفة، ويصلي بهم الفجر بغلس (¬٢) (¬٣).
لما روي: "أنه -صلى الله عليه وسلم- صلاها يومئذ بغلس" متفق عليه (¬٤).
قوله: ثم يقف بالمشعر الحرام (¬٥) وهو قزح لما مر (¬٦).
قوله: ويدعو (¬٧).
_________
= وهو الشعب الذي بال فيه النبي -صلى الله عليه وسلم- حين دفع من عرفة يريد المزدلفة. ويقع على يسار المقبل من عرفة.
تاريخ مكة للأزرقي ٢/ ١٩٠ - ١٩٧، المصباح المنير ١/ ٣١٣ مادة الشعب، لسان العرب ١/ ٥٠١ مادة شعب، القاموس المحيط ٢/ ٧١٧ مادة ش ع ب.
(¬١) البخاري ٢/ ٦٠١ كتاب الحج، باب الجمع بين الصلاتين بالمزدلفة رقم ١٥٨٨، ومسلم ٢/ ٩٣٤ كتاب الحج باب الإفاضة من عرفات إلى المزدلفة رقم ١٢٨٠.
(¬٢) كنز الدقائق ٢/ ٢٨، الكتاب ١/ ١٩٠، الأصل ٢/ ٣١١، تحفة الفقهاء ١/ ٤٠٧، تبيين الحقائق ٢/ ٢٨.
(¬٣) الغلس: ظلمة آخر الليل، وقيل: أول الصبح حتى ينتشر في الآفاق.
لسان العرب ٦/ ١٥٦ مادة غلس، القاموس المحيط ٣/ ٤٠٩ مادة غ ل س، مختار الصحاح ص ٢٠٠ مادة غ ل س، المصباح المنير ٢/ ٤٥٠ مادة الغلس.
(¬٤) البخاري ٢/ ٦٠٤ كتاب الحج، باب من يصلي الفجر بجمع رقم ١٥٩٨، ومسلم ٢/ ٩٣٨ كتاب الحج، باب استحباب زيادة التغليس بصلاة الصبح يوم النحر بالمزدلفة رقم ١٢٨٩.
عن عبد الله بن مسعود -رضي الله عنه- قال: "ما رأيت رسول الله -صلى الله عليه وسلم- صلى صلاة إلا لميقاتها إلا صلاتين، صلاة المغرب والعشاء بجمع، وصلى الفجر يومئذ قبل ميقاتها، وقال: قبل وقتها بغلس".
(¬٥) كنز الدقائق ٢/ ٢٨، المبسوط ٤/ ١٩، تبيين الحقائق ٢/ ٢٨.
(¬٦) في ٣/ ٢٩٢.
(¬٧) المبسوط ٤/ ١٩، الأصل ٢/ ٣١١، تبيين الحقائق ٢/ ٢٨، كنز الدقائق ٢/ ٢٨.','3','295',NULL,NULL,'0');
INSERT INTO [b46] VALUES (971,'فإذا أسفَر (¬١) أفاض إلى منى. فيرمي جمرة العقبة من بطن الوادي بسبع،
<hr><span data-type="title">منحة السلوك</span>

لما روي: "أنه -صلى الله عليه وسلم- ركب القصواء، حتى أتى المشعر الحرام، فاستقبل القبلة، فدعا الله وكبره، وهلله، ووحده" رواه مسلم (¬٢).

<span data-type="title" id=toc-483>[زمن الانصراف من مزدلفة: صفة الرمي]</span>
قوله: فإذا أسفر جدًا أي: إذا أسفر الصبح إسفارًا كاملًا، أفاض إلى منى، فرمى جمرة العقبة من بطن الوادي سبع حصيات (¬٣).
لما روي: "أنه -صلى الله عليه وسلم- لم يزل واقفًا حتى أسفر جدًا، فدفع قبل أن تطلع الشمس، حتى أتى بطن محسر، فحرك قليلًا، ثم سلك الطريق الوسطى التي تخرج على الجمرة الكبرى، حتى إذا أتى الجمرة التي عند الصخرة فرماها بسبع حصيات، يكبر مع كل حصاة منها، مثل حصى الخذف، رمى من بطن الوادي، ثم انصرف إلى المنحر" رواه مسلم (¬٤).
ولو دفع بليل لعذر به من ضعف، أو علة، جاز ولا شيء عليه (¬٥)؛ لما روى ابن عمر -رضي الله عنهما-: "أنه -صلى الله عليه وسلم- أذن لضعفة الناس، أن يدفعوا بليل" رواه أحمد (¬٦).
_________
(¬١) في ب بزيادة: "الصبح".
(¬٢) سبق تخريجه ٣/ ٢٧٦ وهو قطعة من حديث جابر الطويل في صفة حجة النبي -صلى الله عليه وسلم-.
(¬٣) وفاقًا للثلاثة.
كنز الدقائق ٢/ ٢٩، تبيين الحقائق ٢/ ٢٩، شرح الزرقاني على مختصر خليل ٢/ ٢٧٩، مختصر خليل ص ٨٤، عمدة السالك وعدة الناسك ص ٢٥٧، أنوار المسالك ص ٢٥٧، الكافي لابن قدامة ١/ ٤٤٤، منتهى الإرادات ٢/ ٦٠.
(¬٤) سبق تخريجه ٣/ ٢٧٦.
(¬٥) الهداية ١/ ١٥٩، شرح فتح القدير ٢/ ٤٨٣.
(¬٦) ٢/ ٣٣، ورواه أيضًا البخاري ٢/ ٦٠٣ كتاب الحج، باب من قدم ضعفة أهله بليل رقم ١٥٩٢، ومسلم ٢/ ٩٤١ كتاب الحج، باب استحباب تقديم دفع الضعفة من النساء وغيرهن، من مزدلفة إلى منى، في أواخر الليالي قبل زحمة الناس رقم ١٢٩٥.','3','296',NULL,NULL,'0');
INSERT INTO [b46] VALUES (972,'مثل حصى الخذف.
<hr><span data-type="title">منحة السلوك</span>

قوله: مثل حصى الخذف (¬١) -بالخاء المعجمة-. وهو الرمي برؤوس الأصابع.
يقال: الحذف بالعصا، والخذف بالحصى. الأول: بالحاء المهملة، والثانى: بالخاء المعجمة (¬٢).
وكيفية الرمي: أن يضع الحصاة على ظهر إبهامه اليمنى، ويستعين بالمسبحة (¬٣).
ومقدار الرمي: أن يكون بين الرامي وبينه خمسة أذرع (¬٤).
_________
(¬١) وفاقًا للثلاثة فيما تقدم.
الكتاب ١/ ١٩٠، بداية المبتدي ١/ ١٥٩، القوانين الفقهية ص ٩٠، التفريع ١/ ٣٤٢، شرح الزرقاني على خليل ٢/ ٢٨٥، بداية المجتهد ١/ ٣٥٠، أقرب المسالك ص ٥٠، قليوبي ٢/ ١١٦، عميرة ٢/ ١١٦، العمدة ص ٣٨، نيل المراد ص ٨٩.
(¬٢) تاج العروس ٦/ ٨٠ مادة خذف، لسان العرب ٩/ ٦١ مادة خذف، معجم مقاييس اللغة ٢/ ١٦٥ باب الخاء والذال وما يثلثهما مادة خذف، المغرب ص ١٤١ مادة الخذف، المصباح المنير ١/ ١٦٥ مادة خذفت، طلبة الطلبة ص ٧٣.
(¬٣) وكذا عند المالكية. ويستحب أن يكون الرمي باليد اليمنى، إلا أن يكون أعسر.
وعند الشافعية: يستحب أن يرفع يده في الرمي حتى يرى بياض إبطه، ويسن أن يكون الرمي بيده اليمنى.
وكيفيته عند الشافعية: وجهان عندهم: أحدهما: يستحب أن يكون كصفة الحاذف، كما عند الحنفية. والثاني: وهو الصحيح عندهم أنه يرميها على غير صفة الحذف، والوجه الثاني ذهب إليه الحنابلة، مع استحباب الحنابلة رفع الرامي للجمار يمناه حتى يرى بياض إبطه، وأن يومئها على حاجبه الأيمن.
الهداية ١/ ١٥٩، العناية ٢/ ٤٨٥، شرح الزرقاني على خليل ٢/ ٢٨٥، حاشية البناني ٢/ ٢٨٥، بلغة السالك ١/ ٢٨٢، المجموع ٨/ ١٧٠، المهذب ١/ ٢٢٥، مغني المحتاج ١/ ٥٠٨، كشاف القناع ٢/ ٥٠١، شرح منتهى الإرادات ٢/ ٦٢.
(¬٤) وذهب المالكية، والشافعية، والحنابلة: إلى أنه لا تحديد في ذلك. والمراد عندهم: =','3','297',NULL,NULL,'0');
INSERT INTO [b46] VALUES (973,'يكبر مع كلِّ حصاةٍ، ولا يقف عندها. ويقطع التلبية مع أول حصاة،
<hr><span data-type="title">منحة السلوك</span>

قوله: يكبر مع كل حصاة (¬١).
لما روينا؛ ولو سبح مكان التكبير أجزأه؛ لحصول التعظيم بالذكر (¬٢).
قوله: ولا يقف عندها.
أي: عند جمرة العقبة (¬٣)؛ لما روي عن ابن عمر -رضي الله عنهما-: "أنه كان يرمي جمرة العقبة من بطن الوادي، ولا يقف عندها، ثم ينصرف، ويقول: هكذا رأيت النبي -صلى الله عليه وسلم- يفعله" رواه البخاري (¬٤).

<span data-type="title" id=toc-484>[زمن قطع التلبية للحاج]</span>
قوله: ويقطع التلبية مع أول حصاة (¬٥).
_________
= هو الوصول إلى الجمرة. وحده بعض المتأخرين من الشافعية فقال: موضع الرمي ثلاثة أذرع من سائر الجوانب، إلا في جمرة العقبة، فليس لها إلا وجه واحد.
الهداية ١/ ١٥٩، تبيين الحقائق ٢/ ٤٨٥، الخرشي على خليل ٢/ ٣٣٩، حاشية العدوي ٢/ ٣٣٩، مغني المحتاج ١/ ٥٠٨، المجموع ٨/ ١٧٤، منتهى الإرادات ٢/ ٦١، الإقناع للحجاوي ٢/ ٥٠١.
(¬١) وفاقًا للمالكية، والشافعية، والحنابلة.
مختصر الطحاوي ص ٦٥، الكتاب ١/ ١٩١، الهداية ١/ ١٥٩، متن الرسالة ص ٨٧، مختصر خليل ص ٨٤، المجموع ٨/ ١٦٨، عمدة السالك وعدة الناسك ص ٢٥٨، زاد المستقنع ص ٢٠٩، المحرر ١/ ٢٤٧.
(¬٢) الهداية ١/ ١٥٩، شرح فتح القدير ٢/ ٤٨٦.
(¬٣) وفاقًا للثلاثة.
الكتاب ١/ ١٩١، المختار ١/ ١٥٢، الهداية ١/ ١٥٩، الاختيار ١/ ١٥٢، متن الرسالة ص ٨٧، مختصر خليل ص ٨٤، أنوار المسالك شرح عمدة السالك ص ٢٥٨، المنهاج ١/ ٥٩٩، المغني ٣/ ٤٥٨، المستوعب ٤/ ٢٤٣.
(¬٤) ٢/ ٦٢٣ كتاب الحج، باب رفع اليدين عند جمرة الدنيا والوسطى رقم ١٦٦٥.
(¬٥) وفاقًا للشافعية، والحنابلة. وعند المالكية: يقطعها بعد الزوال من يوم عرفة. وقيل: يقطعها إذا رمي جمرة العقبة. =','3','298',NULL,NULL,'0');
INSERT INTO [b46] VALUES (974,'ولو رمى السبع جُملةً فهي واحدةٌ.
<hr><span data-type="title">منحة السلوك</span>

لما روي عن ابن عباس -رضي الله عنهما-: "أن أسامة كان رديف النبي -صلى الله عليه وسلم- من عرفة إلى المزدلفة، ثم أردف الفضل (¬١)، من المزدلفة إلى منى، قال: كلاهما لم يزل النبي -صلى الله عليه وسلم- يلبي حتى رمى جمرة العقبة" رواه البخاري، ومسلم، وغيرهما (¬٢).

<span data-type="title" id=toc-485>[رمي الجمار دفعة واحدة]</span>
قوله: ولو رمى السبع جملة. أي: ولو رمى سبع حصيات جملة، دفعة واحدة، فهى واحدة؛ لأن المنصوص عليه تفريق الأفعال (¬٣).
_________
= الكتاب ١/ ١٩١، مختصر الطحاوي ص ٦٥، منح الجليل ٢/ ٢٦٣، الشرح الصغير ١/ ٢٧٠، مختصر خليل ص ٨٢، السراج الوهاج ص ١٦٣، زاد المحتاج ١/ ٥٩٩، زاد المستقنع ص ٢٠٩، مختصر الخرقي ص ٦٠.
(¬١) هو الفضل بن العباس بن عبد المطلب الهاشمي القرشي، من شجعان الصحابة، كان أسن ولد العباس، ثبت مع رسول الله -صلى الله عليه وسلم- يوم حنين، وخرج بعد وفاته مجاهدًا إلى الشام، فاستشهد في وقعة أجنادين بفلسطين سنة ١٣ هـ.
طبقات ابن سعد ٤/ ٥٤، الاستيعاب ٣/ ٢٠٨، الإصابة ٣/ ٢٠٨، أسد الغابة ٤/ ٣٦٦.
(¬٢) البخاري ٢/ ٥٥٩ كتاب الحج، باب الركوب والارتداف في الحج رقم ١٤٦٩، ومسلم ٢/ ٩٣٠ كتاب الحج، باب استحباب إدامة الحاج التلبية رقم ١٢٨١، وأبو داود ٢/ ١٦٣ كتاب المناسك، باب متى يقطع التلبية رقم ١٨١٥، والنسائي ٥/ ٢٧٦ كتاب مناسك الحج، باب قطع المحرم التلبية إذا رمى جمرة العقبة رقم ٣٠٨٠، والترمذي ٣/ ٢٨٣ كتاب الحج، باب ما جاء في متى تقطع التلبية في الحج رقم ٩١٨، وابن ماجه ٢/ ١٠١١ كتاب المناسك، باب متى يقطع الحاج التلبية رقم ٣٠٤٠، والدارمي ١/ ٤٩٢ كتاب المناسك، باب في رمي الجمار يرميها راكبًا رقم ١٨٣٩، والطحاوي في معاني الآثار ٢/ ٢٢٤ كتاب مناسك الحج، باب التلبية متى يقطعها الحاج، والبيهقي ٥/ ١١٢ كتاب الحج، باب التلبية يوم عرفة وقبله وبعده حتى يرمي جمرة العقبة، وأحمد ١/ ٢١٠، وابن خزيمة في صحيحه ٤/ ٢٨٢ كتاب المناسك، باب قطع التلبية إذا رمى الحاج جمرة العقبة يوم النحر رقم ٢٨٨٧.
(¬٣) وفاقًا للثلاثة. =','3','299',NULL,NULL,'0');
INSERT INTO [b46] VALUES (975,'ويجوز الرميُ بجنس الأرض،
<hr><span data-type="title">منحة السلوك</span>

قوله: ويجوز الرمي بجنس الأرض.
كالحجر، والمدر (¬١)، والطين، والمغرة (¬٢)، والنورة (¬٣)، والزرنيخ (¬٤)، والملح الجبلي، والكحل، والقبضة من تراب، والأحجار النفيسة كالياقوت، والزبرجد (¬٥)، والزمرد، والبلخش (¬٦) والفيروزج، والبلور،
_________
= بداية المبتدي ١/ ١٥٩، تبيين الحقائق ٢/ ٣٠، الهداية ١/ ١٥٩، بدائع الصنائع ٢/ ١٥٨، التفريع ١/ ٣٤٤، المجموع ٨/ ١٧٠، المهذب ١/ ٢٢٥، غاية المنتهى ٢/ ٢٤١، كشاف القناع ٢/ ٥٠٠.
(¬١) والمدر: قطع الطين اليابس. وقيل: الذي لا رمل فيه.
لسان العرب ٥/ ١٦٢ مادة مدر، تاج العروس ٣/ ٥٣٥ مادة مدر، معجم مقاييس اللغة ٥/ ٣٠٥ باب الميم والدال وما يثلثهما مادة مدر، المصباح المنير ٢/ ٥٦٦ مادة المدر.
(¬٢) وهو الطين الأحمر.
تاج العروس ٣/ ٥٤٧ مادة مغر، لسان العرب ٥/ ١٨١ مادة مغر، مختار الصحاح ص ٢٦٢ مادة م غ ر، المصباح المنير ٢/ ٥٧٦ مادة المغرة.
(¬٣) النور: حجر الكلس.
لسان العرب ٥/ ٢٤٤ مادة نور، تاج العروس ٣/ ٥٨٨ مادة نور، المصباح المنير ٢/ ٦٣٠ مادة النور.
(¬٤) الزرنيخ: حجر منه أبيض، وأحمر، وأصفر. وهو فارسي معرب.
القاموس المحيط ٢/ ٤٤٩ مادة ز ر ن خ، المصباح المنير ١/ ٢٥٢ مادة الزرنيخ، المعرّب ص ٣٥٦.
(¬٥) الزبرجد: حجر كريم يشبه الزمرد، وهو ذو ألوان كثيرة، أشهرها الأخضر، والأصفر.
القاموس المحيط ٢/ ٤٣١ مادة ز ب ر د ج، المعرب ص ٣٥٧ مادة زبرجد، المعجم الوسيط ١/ ٣٨٨ مادة الزبرجد.
(¬٦) البلخش: من المعادن، وهو أبهى أنواع أشباه الياقوت، ويقال له: المرقشيثا.
الجماهر في معرفة الجواهر للبيروني ص ٨١، كتاب الجوهرتين العتيقتين المائعتين الصفراء والبيضاء ص ٢٣٥.','3','300',NULL,NULL,'0');
INSERT INTO [b46] VALUES (976,'لا بالذهب، والفضة. ثم يذبح إن شاء،
<hr><span data-type="title">منحة السلوك</span>

والعقيق (¬١).
قوله: لا بالذهب. أي: لا يجوز بالذهب، والفضة.
وكذلك الخشب، والعنبر (¬٢)، واللؤلؤ، والجواهر (¬٣)؛ لأنها ليست من جنس الأرض، أو لأنه نثار وليس برمى (¬٤).

<span data-type="title" id=toc-486>[ما يفعله الحاج بعد الرمي]</span>
قوله: ثم يذبح إن شاء (¬٥).
وهذا الذبح ليس بواجب على المفرد، أشار إليه بقوله: "إن شاء" ويجب على القارن، والمتمتع (¬٦).
_________
(¬١) وذهب المالكية، والشافعية، والحنابلة: إلى أنه لا يجوز الرمي بغير الحصى، ولا يجزيء الرمي بغيره.
تبيين الحقائق ٢/ ٣١، شرح فتح القدير ٢/ ٤٨٨، بداية المبتدي ١/ ١٥٩، تحفة الفقهاء ١/ ٤٠٧، الشرح الكبير للدردير ٢/ ٤٠، حاشية الدسوقي ٢/ ٤٠، المجموع ٨/ ١٧٠، المهذب ١/ ٢٢٥، شرح منتهى الإرادات ٢/ ٦٠، هداية الراغب ص ٢٢٧.
(¬٢) العنبر: مادة صلبة، تنبعث منها رائحة زكية إذا أحرقت. ويقال: إنه روث دابة بحرية.
لسان العرب ٤/ ٦١٠ مادة عنبر، القاموس المحيط ٣/ ٣١٩ مادة ع ن ب ر، معجم لغة الفقهاء ص ٣٢٣، مختار الصحاح ص ١٩١ مادة ع ن ب ر.
(¬٣) الجوهر: كل حجر يستخرج منه شيء ينتفع به. وهو معرب.
لسان العرب ٤/ ١٥٢ مادة جهر، القاموس المحيط ١/ ٥٦٢ مادة ج وهـ ر، المعرب ص ٢٣٨ مادة الجوهر، المصباح المنير ١/ ١١٢ مادة جهر، مختار الصحاح ص ٤٨ مادة ج هـ ر.
(¬٤) الهداية ١/ ١٦٠، العناية ٢/ ٤٨٩، شرح فتح القدير ٢/ ٤٨٨.
(¬٥) بداية المبتدي ١/ ١٦٠، تحفة الفقهاء ١/ ٤٠٨، الهداية ١/ ١٦٠.
(¬٦) وفاقًا للثلاثة.
تحفة الفقهاء ١/ ٤٠٨، تبيين الحقائق ٢/ ٣٢، بداية المبتدي ١/ ١٦٠، القوانين الفقهية ص ٩١، بداية المجتهد ١/ ٣٣٢، تحفة المحتاج ٣/ ١٤٩، حاشية الجمل على شرح المنهج ٢/ ٤٩٢، حاشية المقنع ١/ ٤٥٨، هداية الراغب ص ٢٢٧.','3','301',NULL,NULL,'0');
INSERT INTO [b46] VALUES (977,'ثم يحلق رُبع رأسه، وهو أفضل، أو يقصر،
<hr><span data-type="title">منحة السلوك</span>

قوله: ثم يحلق ربع رأسه (¬١)؛ لما روي عن أنس -رضي الله عنه- "أنه -صلى الله عليه وسلم- أتى منى، فأتى الجمرة فرماها، ثم أتى منزله بمنى ونحر، وقال للحلاق: خذ، وأشار إلى جانبه الأيمن، ثم الأيسر، ثم جعل يعطيه الناس" رواه مسلم، وأبو داود، وأحمد (¬٢).
قوله: وهو أي: الحلق أفضل من التقصير (¬٣).
لما روى أبو هريرة -رضي الله عنه- أنه -صلى الله عليه وسلم-: "قال: اللهم اغفر للمحلقين، قالوا: يا رسول الله والمقصرين، قال: اللهم اغفر للمحلقين، قالوا: يا رسول الله والمقصرين، قال: وللمقصرين" متفق عليه (¬٤).
_________
(¬١) وعند المالكية: يأخذ الرجل إن قصر من قرب أصله من جميع شعره، وإن حلق يحلق جميع رأسه.
وعند الشافعية: يحلق أو يقصر، وأقله ثلاث شعرات حلقًا، أو تقصيرًا، أو نتفًا، أو إحراقًا، أو قصًا مما يحاذي الرأس، أو مما استرسل عنه.
وعند الحنابلة: يحلق، أو يقصر من جميع شعره.
تبيين الحقائق ٢/ ٣٢، الهداية ١/ ١٦٠، مواهب الجليل ٣/ ١٢٩، الشرح الكبير للدردير ٢/ ٤٦، التاج والإكليل ٣/ ١٢٩، شرح المحلي على المنهاج ٢/ ١١٨، فتح الوهاب ٢/ ٤٦٦، الشرح الكبير لأبي الفرج عبد الرحمن بن قدامة ٣/ ٤٦٣، الروض المربع ص ٢١٠.
(¬٢) مسلم ٢/ ٩٤٧ كتاب الحج، باب بيان أن السنة يوم النحر أن يرمي، ثم ينحر، ثم يحلق، والابتداء في الحلق بالجانب الأيمن من رأس المحلوق رقم الحديث ١٣٠٥، وأبو داود ٢/ ٢٠٣ كتاب المناسك، باب الحلق والتقصير رقم الحديث ١٩٨١، وأحمد ٣/ ٢٠٨.
(¬٣) وفاقًا للثلاثة.
الكتاب ١/ ١٩١، بداية المبتدي ١/ ١٦٠، الهداية ١/ ١٦٠، متن الرسالة ص ٨٧، أقرب المسالك ص ٤٩، منهج الطلاب ٢/ ٤٦٥، قليوبي ٢/ ١١٨، المقنع ١/ ٤٥٦، شرح الزركشي ٣/ ٢٥٨.
(¬٤) البخاري ٢/ ٦١٦ كتاب الحج، باب الحلق والتقصير عند الإحلال رقم ١٦٤١، ومسلم =','3','302',NULL,NULL,'0');
INSERT INTO [b46] VALUES (978,'ويحل له كل شيء إلا النساء.
<hr><span data-type="title">منحة السلوك</span>

قوله: ويحل له كل شيء إلا النساء (¬١).
لحديث عائشة -رضي الله عنها- أنها قالت: "طيبت رسول الله -صلى الله عليه وسلم- لإحرامه حين أحرم، ولحله حين أحل، قبل أن يطوف بالبيت" متفق عليه (¬٢)، وعنها أنها قالت: قال رسول الله -صلى الله عليه وسلم-: "إذا رميتم، وذبحتم، وحلقتم، فقد حل لكم كل شيء إلا النساء، وحل لكم الثياب، والطيب" رواه الدارقطني (¬٣).
_________
= ٢/ ٩٤٦ كتاب الحج، باب تفضيل الحلق على التقصير وجواز التقصير رقم ١٣٠٢، وتمامه: عن أبي هريرة -رضي الله عنه- قال: قال رسول الله -صلى الله عليه وسلم-: "اللهم اغفر للمحلقين، قالوا: يا رسول الله وللمقصرين، قال: اللهم اغفر للمحلقين، قالوا: يا رسول الله وللمقصرين، قال: اللهم اغفر للمحلقين، قالوا: يا رسول الله وللمقصرين، قال: وللمقصرين".
(¬١) وكذا عند الشافعية، والحنابلة.
وعند المالكية: يحل له كل شيء إلا النساء والصيد، ويكره له الطيب. وقيل: يحرم الطيب.
الكتاب ١/ ١٩١، المبسوط ٤/ ٢٢، أقرب المسالك ص ٤٩، مواهب الجليل ٣/ ١٣٠، الكافي لابن عبد البر ص ١٦٦، المهذب ١/ ٢٢٥، أنوار المسالك ص ٢٦٠، الروض المربع ص ٢١٠، هداية الراغب ص ٢٢٨.
(¬٢) البخاري ٢/ ٥٥٨ كتاب الحج، باب الطيب عند الإحرام، وما يلبس إذا أراد أن يحرم رقم ١٤٦٥، ومسلم ٢/ ٨٤٦ كتاب الحج، باب الطيب للمحرم عند الإحرام رقم ١١٨٩.
(¬٣) ٢/ ٢٧٦ كتاب الحج، باب المواقيت رقم ١٨٧، ورواه أيضًا أحمد ٦/ ١٤٣ بلفظ: "إذا رميتم وحلقتم فقد حل لكم ... "، وأبو داود رقم ١٩٧٨ كتاب المناسك، باب في رمي الجمار ٢/ ٢٠٢ بلفظ: "إذا رمى أحدكم جمرة العقبة فقد ... "، وابن خزيمة رقم ٢٩٣٧ كتاب المناسك، باب الرخصة في الاصطياد وجميع ما حرم على المحرم بعد رمي الجمرة يوم النحر ٤/ ٣٠٢، وابن جرير في التفسير ٣٩٦٠، والبيهقي ٥/ ١٣٦ كتاب الحج، باب ما يحل بالتحلل الأول من محظورات الإحرام، والطحاوي في شرح معاني الآثار ٢/ ٢٢٨ كتاب المناسك، باب اللباس والطيب متى يحلان للمحرم، ولفظ ابن خزيمة، والطحاوي كلفظ أحمد.
من طريق الحجاج بن أرطاة، عن أبي بكر بن عمرو بن حزم، عن عمرة، عن عائشة -رضي الله عنها- =','3','303',NULL,NULL,'0');
INSERT INTO [b46] VALUES (979,'ثم يطوف طواف الزيارة. ووقته: أيام النحر. وأفضلها أولها، ويحلُّ له النساء.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-487>[طواف الإفاضة وزمنه]</span>
قوله: ثم يطوف طواف الزيارة (¬١).
لما روي في حديث جابر -رضي الله عنه-: "أنه -صلى الله عليه وسلم- أفاض إلى البيت يوم النحر، فصلى بمكة بعد ما طاف بالبيت" رواه مسلم (¬٢).
قوله: ووقته. أي: وقت أيام طواف الزيارة (¬٣) أيام النحر، وهي ثلاثة أيام (¬٤).
لأن الله تعالى عطف الطواف على الذبح والأكل منه بقوله: {فَكُلُوا} [الحج: ٢٨]، ثم قال: {وَلْيَطَّوَّفُوا} [الحج: ٢٩]. فكان وقتهما واحد، وأولها أفضلها (¬٥) كما في النحر.
قوله: ويحل له النساء.
_________
= قال أبو داود في السنن ٢/ ٢٠٢: هذا حديث ضعيف، الحجاج بن أرطاة، لم ير الزهري، ولم يسمع منه.
(¬١) المبسوط ٤/ ٢٢، المختار ١/ ١٥٣، الاختيار ١/ ١٥٣.
(¬٢) سبق تخريجه ٣/ ٢٧٦.
(¬٣) طواف الزيارة له عدة أسماء: طواف الركن، وطواف الإفاضة، والزيارة، والفرض.
مغني المحتاج ١/ ٥٠٣.
(¬٤) وعند المالكية: إلى آخر شهر ذي الحجة.
وعند الشافعية: يبدأ يوم النحر، ولا آخر لوقته.
وعند الحنابلة: أول وقته من بعد نصف الليل من ليلة النحر، ولا حد لآخره.
المبسوط ٤/ ٢٢، المختار ١/ ١٥٣، الاختيار ١/ ١٥٣، الذخيرة ٣/ ٢٧١، التلقين ص ٦٣، المنهاج ١/ ٦٠١، السراج الوهاج ص ١٦٤، المستوعب ٤/ ٢٤٩، الكافي لابن قدامة ١/ ٤٤٩.
(¬٥) المبسوط ٤/ ٢٢، تحفة الفقهاء ١/ ٤٠٨، الكتاب ١/ ١٩٢، كنز الدقائق ٢/ ٣٣، الهداية ١/ ١٦٠، تبيين الحقائق ٢/ ٣٣.','3','304',NULL,NULL,'0');
INSERT INTO [b46] VALUES (980,'ثم يعود إلى منى، ويرمي الجمار الثلاث بعد الزوال في اليوم الثاني، والثالث، والرابع.
<hr><span data-type="title">منحة السلوك</span>

يعني: بعد ما طاف طواف الزيارة، يحل له النساء أيضًا؛ لإجماع الأمة على ذلك (¬١).

<span data-type="title" id=toc-488>[الرمي وأيامه]</span>
قوله: ثم يعود إلى منى (¬٢).
لما روي: "أنه -صلى الله عليه وسلم- أفاض يوم النحر، ثم رجع فصلى الظهر بمنى" متفق عليه (¬٣).
قوله: ويرمي الجمار الثلاث بعد الزوال في اليوم الثاني، والثالث، والرابع (¬٤).
اعلم أنه يرمي الجمار الثلاث في ثاني النحر بعد الزوال، باديًا لما يلي المسجد، ثم بما يليها، ثم بجمرة العقبة، ويقف عند كل رمي بعده رمي، ثم غدًا كذلك، ثم بعدُ كذلك إن مكث (¬٥)، لما روت عائشة -رضي الله عنها- أنها قالت: "أفاض النبي -صلى الله عليه وسلم- من آخر يومه حين صلى الظهر، ثم رجع إلى منى، فمكث بها ليالي أيام التشريق يرمي الجمار إذا زالت الشمس، كل جمرة بسبع حصيات، يكبر مع كل حصاة، ويقف عند الأولى والثانية، فيطيل القيام، ويتضرع ويرمي الثالثة، ولا يقف عندها" رواه أبو داود (¬٦).
_________
(¬١) مراتب الإجماع ص ٤٥.
(¬٢) كنز الدقائق ٢/ ٣٤، بداية المبتدي ١/ ١٦١، الهداية ١/ ١٦١، تبيين الحقائق ٢/ ٣٤.
(¬٣) البخاري ٢/ ٦٠٧ كتاب الحج، باب من ساق البدن معه رقم ١٦٠٦، ومسلم ٢/ ٩٥٠ كتاب الحج، باب استحباب طواف الإفاضة يوم النحر رقم ١٣٠٨ عن ابن عمر -رضي الله عنهما-.
(¬٤) الكتاب ١/ ١٩٣، الأصل ٢/ ٣١٤، كنز الدقائق ٢/ ٣٣.
(¬٥) بداية المبتدي ١/ ١٦١، شرح فتح القدير ٢/ ٤٩٧، الهداية ١/ ١٦١، العناية ٢/ ٤٩٧.
(¬٦) ٢/ ٢٠١ كتاب المناسك باب في رمي الجمار رقم ١٩٧٣، والبيهقي كتاب الحج، باب الرجوع إلى منى أيام التشريق، والرمي بها كل يوم إذا زالت الشمس ٥/ ١٤٨، وابن =','3','305',NULL,NULL,'0');
INSERT INTO [b46] VALUES (981,'فإذا أراد الرجوع إلى بلده، طاف طواف الصدر.
<hr><span data-type="title">منحة السلوك</span>

فإن لم يمكث في اليوم الرابع يسقط عنه الرمي؛ لأنه مخير فيه (¬١) بقوله تعالى: {فَمَنْ تَعَجَّلَ فِي يَوْمَيْنِ فَلَا إِثْمَ عَلَيْهِ وَمَنْ تَأَخَّرَ فَلَا إِثْمَ عَلَيْهِ لِمَنِ اتَّقَى} [البقرة: ٢٠٣]. والأفضل: أن يرمي موافقة للنبي -صلى الله عليه وسلم- (¬٢).

<span data-type="title" id=toc-489>[طواف الوداع]</span>
قوله: فإذا أراد الرجوع إلى بلده، طاف طواف الصدر.
يعني: إذا فرغ من رمي الجمار، وأراد أن يرجع إلى بلده، نزل بالمُحصّب، وهو الأبطح (¬٣)، ويسمى الحصباء والبطحاء -والخيف وهو ما
_________
= خزيمة ٤/ ٣١١ كتاب المناسك، باب البيتوتة بمنى ليالي أيام التشريق رقم ٢٩٥٦، والطحاوي ٢/ ٢٢٠ كتاب مناسك الحج، باب رمي جمرة العقبة ليلة النحر قبل طلوع الفجر، وابن حبان في المنتقى بترتيب ابن بلبان ٩/ ١٨٠ كتاب الحج، باب رمي جمرة العقبة رقم ٣٨٦٨، وابن الجارود ص ٤٩٢ باب المناسك رقم ٤٩٢، والدارقطني ٢/ ٢٧٤ كتاب الحج، باب المواقيت، والحاكم ١/ ٤٧٧ كتاب المناسك، وأحمد ٦/ ٩٠.
قال الحاكم ١/ ٤٧٧: صحيح على شرط مسلم، وسكت عنه الذهبي.
(¬١) وفاقًا للثلاثة.
الكتاب ١/ ١٩٣، الأصل ٢/ ١٥٩، تحفة الفقهاء ١/ ٤٠٩، تبيين الحقائق ٢/ ٣٤، الخرشي على خليل ٢/ ٣٣٩، حاشية العدوي ٢/ ٣٣٩، أنوار المسالك ص ٢٦٠، عمدة السالك ص ٢٦٠، الشرح الكبير لأبي الفرج بن قدامة ٣/ ٤٩٦، الكافي لابن قدامة ١/ ٤٥٤.
(¬٢) بداية المبتدي ١/ ١٦٢، تحفة الفقهاء ٩/ ٤٠٢، الكتاب ١/ ١٩٣، الهداية ١/ ١٦٢.
(¬٣) وفاقًا للمالكية، والشافعية، والحنابلة.
قال في المجموع ٨/ ٢٥٣: قال القاضي عياض: النزول بالمحصب مستحب عند جميع العلماء قال: وهو عند الحجازيين، أوكد منه عند الكوفيين قال: وأجمعوا على أنه ليس بواجب.
وقال في المغني ٣/ ٤٨٩: ولا خلاف في أنه ليس بواجب، ولا شيء على تاركه.
الأصل ٢/ ٣١٦، بدائع الصنائع ٢/ ١٦٠، الذخيرة ٣/ ٢٨٢، القوانين الفقهية ص ٨٧، المجموع ٨/ ٢٥٢، روضة الطالبين ٣/ ١١٥، كشاف القناع ٢/ ٥١١، المستوعب ٤/ ٢٥٩، المغني ٣/ ٤٨٩.','3','306',NULL,NULL,'0');
INSERT INTO [b46] VALUES (982,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

بين الجبل الذي عند مقابر مكة، والجبل الذي يقابله مصعدًا في الشق الأيسر، وأنت ذاهب إلى منى، مرتفعًا عن بطن الوادي-، وليست المقبرة من المحصب (¬١)؛ لقوله-صلى الله عليه وسلم-: "نحن نازلون عند الخيف خيف بني كنانة" (¬٢) الحديث (¬٣) رواه البخاري، ومسلم، وأبو داود، وابن ماجه، والنسائي (¬٤). ثم يطوف طواف الصدر (¬٥)؛ لما روي: "أنه -صلى الله عليه وسلم- صلى الظهر، والعصر،
_________
(¬١) شرح فتح القدير ٢/ ٥٠٢، تبيين الحقائق ٢/ ٣٥.
(¬٢) وهو الأبطح، والبطحاء، والمحصب. ويسمى شعب الصفي -بضم أوله وكسر ثانيه- وهو الذي تحالفت قريش فيه مع بني كنانة على بني هاشم.
تاريخ مكة للأزرقي ٢/ ١٦٠ - ١٦٢ - ٢٧٣ - ٢٧٥، معجم البلدان ٢/ ٤١٢ مادة خيف، معجم ما استعجم ١/ ٥٢٦، معجم المعالم الجغرافية في السيرة النبوية ص ١١٨.
(¬٣) وتمامه: "حيث تقاسموا على الكفر". وذلك أن قريشًا، وبني كنانة، تحالفت على بني هاشم وبني المطلب، أن لا يناكحوهم، ولا يبايعوهم، حتى يسلموا إليهم رسول الله -صلى الله عليه وسلم-.
(¬٤) البخاري ٢/ ٥٧٦ كتاب الحج، باب نزول النبي مكة رقم الحديث ١٥١٣، ومسلم ٢/ ٩٥٢ كتاب الحج، باب استحباب النزول بالمحصب يوم النفر، والصلاة به رقم الحديث ١٣١٤، وأبو داود ٢/ ٢١٠ كتاب المناسك، باب التحصيب رقم الحديث ٢٠١١، وابن ماجه ٢/ ٩٨١ كتاب المناسك، باب دخول مكة رقم الحديث ٢٩٤٢ والنسائي في الكبرى ٢/ ٤٦٧ كتاب الحج، باب نزول المحصب بعد النفر برقم ٤٢٠٢، عن أسامة بن زيد عن أبي هريرة.
(¬٥) وفاقًا للثلاثة. وسمي هذا الطواف طواف الصدر؛ لأنه يصدر عنه، أي يرجع. والصدر: الرجوع.
ويسمى: طواف الوداع؛ لأنه يودع به البيت.
ويسمى: طواف آخر عهد بالبيت؛ لأنه لا طواف بعده.
ويسمى: طواف الواجب؛ لأنه ينجبر بالدم.
الأصل ٢/ ٣١٦، بدائع الصنائع ٢/ ١٦٠، تبيين الحقائق ٢/ ٣٦، الشرح الكبير ٢/ ٥٢، حاشية الدسوقي ٢/ ٥٢، المنهاج ١/ ٦٠٤، أنوار المسالك ص ٢٦٢، عمدة الطالب =','3','307',NULL,NULL,'0');
INSERT INTO [b46] VALUES (983,'ومن وقف بعرفة لحظةً، ما بين الزوال يوم عرفة، وفجر يوم النحر أجزأه،
<hr><span data-type="title">منحة السلوك</span>

والمغرب، والعشاء بالمحصب، ثم رقد رقدة، ثم ركب إلى البيت، فطاف به" رواه البخاري (¬١).

<span data-type="title" id=toc-490>[الوقوف المجزئ بعرفة]</span>
قوله: ومن وقف بعرفة لحظة، أي: ساعة ما بين الزوال يوم عرفة، وفجر يوم النحر، أجزأه (¬٢).
لقوله -صلى الله عليه وسلم-: "الحج عرفة، فمن وقف بعرفة ساعة من ليل أو نهار، فقد تَمَّ حجه" روي بمعناه أبو داود، وغيره، وصححه الترمذي (¬٣).
_________
= ص ٢٢٩، مطالب أولي النهى ٢/ ٤٣٠.
قال في أسهل المدارك ١/ ٤٨٠:
نطوف وداعًا للرحيل بلادنا ... وأعيننا كالسيل إذا سال مجراه
تداعت رفاقًا بالرحيل فما ترى ... سوى دمع عين بالدماء مزجناه
وودعت الحجاج بيت إلهها ... وكلهم تجري من الفرح عيناه
لفرقة بيت الله والحجر الذي ... نقبله لله نطلب رحماه
وبات حجيج الله بالبيت محدقًا ... ورحمة رب العرش ثمة تغشاه
(¬١) ٢/ ٦٢٦ كتاب الحج، باب من صلى العصر يوم النفر بالأبطح رقم الحديث ١٦٧٥ عن أنس بن مالك -رضي الله عنه- ولفظه: "أن قتادة حدثه عن أنس بن مالك حدثه عن النبي -صلى الله عليه وسلم- أنه صلى الظهر، والعصر، والمغرب، والعشاء، ورقد رقدة بالمحصب، ثم ركب إلى البيت فطاف به".
(¬٢) كنز الدقائق ٢/ ٣٧، الكتاب ١/ ١٩٥، تبيين الحقائق ٢/ ٣٧.
(¬٣) هذه اللفظة قطعة من حديثين:
الحديث الأول: حديث عبد الرحمن بن يعمر الديلي: "الحج عرفة". وقد سبق تخريج هذا الحديث في ٣/ ٢٢٧.
الحديث الثاني: حديث عروة بن مضرس. وقد رواه أبو داود ٢/ ١٩٦ كتاب المناسك، باب من لم يدرك عرفة رقم ١٩٥٠، والترمذي ٣/ ٢٥٠ كتاب الحج، باب ما جاء فيمن أدرك الإمام بجمع فقد أدرك الحج رقم ٨٩١، وأحمد ٤/ ١٥، والنسائي ٥/ ٢٦٣ كتاب =','3','308',NULL,NULL,'0');
INSERT INTO [b46] VALUES (984,'ولو كان نائمًا، أو مغمى عليه، أو جاهلًا بها.
<hr><span data-type="title">منحة السلوك</span>

قوله: ولو كان نائمًا.
أي: ولو كان الحاج حال الوقوف نائمًا، أو مغمى عليه، أو جاهلًا بها (¬١).
_________
= مناسك الحج، باب فيمن لم يدرك صلاة الصبح مع الإمام بالمزدلفة رقم ٣٠٤١، وابن ماجه ٢/ ١٠٠٤ كتاب المناسك، باب من أتى عرفة قبل الفجر ليلة جمع رقم ٣٠١٦، والطيالسي ص ١٨١ رقم ١٢٨٢، والدارمي ١/ ٤٨٨ كتاب المناسك، باب بم يتم الحج رقم ١٨٢٨، والحميدي ٢/ ٤٠٠ رقم ٩٠٠، وابن خزيمة ٤/ ٢٥٥ كتاب الحج، باب ذكر وقت الوقوف بعرفة رقم ٢٨٢٠، وابن حبان ٩/ ١٦٢ كتاب الحج، باب الوقوف بعرفة والمزدلفة والدفع منهما رقم ٣٨٥١، والطبراني في الكبير ١٧/ ١٤٩ رقم ٣٧٧، والأوسط ٢/ ١٧٤ رقم ١٣١٨، والصغير ١/ ١٢٠ رقم ٢٦٨، وابن أبي شيبة ٣/ ٢٢٦ كتاب الحج، باب من قال إذا وقف بعرفة قبل أن يطلع الفجر فقد أدرك رقم ١٣٦٨٢، والحاكم ١/ ٤٦٣ كتاب المناسك، وابن الجارود باب المناسك ص ١٢٣ رقم ٤١٧، والطحاوي في الشرح ٢/ ٢٠٧ كتاب مناسك الحج، باب حكم الوقوف بالمزدلفة، والدارقطني ٢/ ٢٣٩، والبيهقي ٥/ ١١٦ كتاب الحج، باب وقت الوقوف لإدراك الحج.
عن عروة بن مضرس الطائي -رضي الله عنه- قال: أتيت رسول الله -صلى الله عليه وسلم- بالموقف يعني: بجمع قلت: جئت يا رسول الله من جبل طيء، أكللت مطيتي، وأتعبت نفسي، والله ما تركت من جبل إلا وقفت عليه، فهل لي من حج؟ فقال رسول الله -صلى الله عليه وسلم-: "من أدرك معنا هذه الصلاة، وأتى عرفات قبل ذلك، ليلًا أو نهارًا، فقد تم حجه، وقضى تفثه".
قال الترمذي ٣/ ٢٥١: هذا حديث حسن صحيح.
وقال في المجموع ٨/ ٩٨: رواه أبو داود، والترمذي، والنسائي، وابن ماجه، وغيرهم، بأسانيد صحيحة.
(¬١) وعند المالكية: من وقف بعرفة قبل الزوال أجزأه، ولو كان مغمى عليه، أو مجنونًا، أو نائمًا، لا إن كان سكرانًا بحرام ويشترط أن ما مر به عرفة، فيمنع الإجزاء إن جهل المار بها.
وعند الشافعية: يشترط كونه أهلًا للعبادة، فالمغمى عليه جميع وقت الوقوف، لا يجزؤه الوقوف؛ لعدم أهليته للعبادة، فإن أفاق لحظة كفى، والسكران كالمغمى عليه، ولو =','3','309',NULL,NULL,'0');
INSERT INTO [b46] VALUES (985,'والمرأة في أفعالِ الحج كالرَّجل، إلا في كشف الرأس،
<hr><span data-type="title">منحة السلوك</span>

أي: بعرفة؛ لأن الأحاديث مطلقة تعرف في موضعها (¬١).

<span data-type="title" id=toc-491>[المرأة في النسك]</span>
قوله: والمرأة في أفعال الحج كالرجل.
لأن أوامر الشرع عامة لجميع المكلفين، ما لم يقم دليل الخصوص (¬٢).
قوله: إلا في كشف الرأس.
يعني: إلا أنها لا تكشف رأسها، ولكن تكشف وجهها؛ (¬٣) لما روي عن عائشة -رضي الله عنها- أنها قالت: "كان الركبان يمرون بنا ونحن مع رسول الله -صلى الله عليه وسلم- محرمات، فإذا جاوزنا، سدلت إحدانا جلبابها (¬٤) من رأسها على وجهها، فإذا جاوزنا كشفنا" رواه أحمد، وأبو داود، وغيرهما (¬٥).
_________
= غير متعد بسكره، والمجنون أولى من المغمى عليه بعدم الإجزاء، ولا بأس بالنوم، ولو مستغرقًا جميع الوقت.
وعند الحنابلة: يجزئه ولو كان نائمًا، أو جاهلًا، أو مارًا لا إن كان سكرانًا، أو مجنونًا، أو مغمى عليه.
كنز الدقائق ٢/ ٣٧، الكتاب ١/ ١٩٥، جواهر الإكليل ١/ ١٧٦، منح الجليل ٢/ ٢٥٤، مغني المحتاج ١/ ٤٩٨، فتح الوهاب ٢/ ٤٥٨، منار السبيل ١/ ٢٥٨، حاشية المقنع ١/ ٤٥٣.
(¬١) الهداية ١/ ١٦٤، شرح فتح القدير ٢/ ٥٠٨.
(¬٢) المختار ١/ ١٥٦، الهداية ١/ ١٦٤، تبيين الحقائق ٢/ ٣٨، الاختيار ١/ ١٥٦.
(¬٣) الكتاب ١/ ١٩٥، الاختيار ١/ ١٥٦، تبيين الحقائق ٢/ ٣٧، المختار ١/ ١٥٦.
(¬٤) الجلباب: ثوب أوسع من الخمار، ودون الرداء، تغطي به المرأة رأسها، وصدرها.
لسان العرب ١/ ٢٧٢ مادة جلب، مختار الصحاح ص ٤٥ مادة ج ل ب، القاموس المحيط ١/ ٥١٠ مادة ج ل ب، معجم مقاييس اللغة ١/ ٤٦٩ باب الجيم واللام وما يثلثهما مادة جلب، المصباح المنير ١/ ١٠٤ مادة جلبت.
(¬٥) أحمد ٦/ ٣٠، وأبو داود ٢/ ١٦٧ رقم ١٨٣٣ كتاب المناسك، باب في المحرمة تغطي وجهها، وابن ماجه ٢/ ٩٧٩ كتاب المناسك، باب المحرمة تسدل الثوب على وجهها =','3','310',NULL,NULL,'0');
INSERT INTO [b46] VALUES (986,'ولبس المخيط،
<hr><span data-type="title">منحة السلوك</span>

قوله: ولبس المخيط.
يعني: إلا أن لها لبس المخيط (¬١)؛ لأنه -صلى الله عليه وسلم-: "أباح السراويل، والقميص، للنساء المحرمات" رواه أبو داود (¬٢).
_________
= ٢٩٣٥، وابن الجارود في المنتقى ص ١١١ باب المناسك، رقم ٤١٨، وابن خزيمة ٤/ ٢٠٣ رقم ٢٦٩١ كتاب المناسك، باب ذكر الخبر المفسر لهذه اللفظة التي حسبتها مجملة، والدليل على أن للمحرمة تغطية وجهها من غير انتقاب، ولا إمساس الثوب، والدارقطني ٢/ ٢٩٥ كتاب الحج، باب المواقيت رقم ٢٦٢، وابن عدي ٢٥٩٧، وأبو داود في المسائل ١١٠، والبيهقي ٥/ ٤٨ كتاب الحج، باب المحرمة تلبس الثوب من علو، فيستر وجهها وتجافي عنه.
من طريق يزيد بن أبي زياد هو الهاشمي مولاهم الكوفي، عن مجاهد، عن عائشة -رضي الله عنها-.
قال في الدراية ٢/ ٣٢: وفي إسناده يزيد بن أبي زياد، وهو ضعيف.
وقال في المجموع ٧/ ٨: رواه أبو داود وابن ماجه وغيرهما وإسناده ضعيف.
(¬١) الكتاب ١/ ١٩٥، الاختيار ١/ ١٥٦، تبيين الحقائق ٢/ ٣٧، المختار ١/ ١٥٦.
(¬٢) ٢/ ١٦٦ كتاب المناسك، باب ما يلبس المحرم رقم ١٨٢٧ قال: حدثنا أحمد بن حنبل، ثنا يعقوب، ثنا أبي، عن ابن إسحاق قال: فإن نافعًا مولى عبد الله بن عمر حدثني عن عبد الله بن عمر -رضي الله عنهما-، أنه سمع رسول الله -صلى الله عليه وسلم- نهى النساء في إحرامهن عن القفازين، والنقاب، وما مس الورس والزعفران من الثياب، ولتلبس بعد ذلك ما أحبت من ألوان الثياب، معصفرًا، أو خزًا، أو حليًا أو سراويل، أو قميصًا، أو خفًا.
قال أبو داود: روى هذا الحديث عن ابن إسحاق عن نافع عبدة بن سليمان ومحمد بن سلمة إلى قوله: "وما مس الورس والزعفران من الثياب" ولم يذكرا ما بعده.
وقال المنذري في تهذيب السنن ٢/ ٣٥٢: في إسناده محمد بن إسحاق.
وأصل الحديث في صحيح البخاري ٢/ ٦٥٣ كتاب الحج، باب ما ينهى من الطيب للمحرم والمحرمة رقم ١٧٤١ عن ابن عمر -رضي الله عنهما- قال: قام رجل فقال: يا رسول الله، ماذا تأمرنا أن نلبس من الثياب في الإحرام؟ فقال النبي -صلى الله عليه وسلم-: "لا تلبسوا القميص، ولا السراويلات، ولا العمائم، ولا البرانس، إلا أن يكون أحد ليست له نعلان فليلبس الخفين، وليقطع أسفل =','3','311',NULL,NULL,'0');
INSERT INTO [b46] VALUES (987,'ورفع الصوت بالتلبية، والرمل، والهرولة، والحلق، فإنها تخالفه فيها.
<hr><span data-type="title">منحة السلوك</span>

ولا ترفع صوتها بالتلبية، ولا ترمل، ولا تهرول؛ للفتنة (¬١)، ولا تحلق ولكن تقصر (¬٢)؛ لما روى ابن عباس -رضي الله عنهما-، أنه -صلى الله عليه وسلم- قال: "ليس على النساء الحلق، إنما على النساء التقصير" رواه أبو داود وأحمد، وغيرهما (¬٣).
والخنثى المشكل في جميع ما ذكرناه، كالمرأة (¬٤).
قوله: فإنها تخالفه فيها.
أي: فإن المرأة تخالف الرجل، في جميع ما ذكرناه (¬٥).
_________
= من الكعبين ولا تلبسوا شيئًا مسه زعفران، ولا الورس، ولا تنتقب المرأة المحرمة، ولا تلبس القفازين".
(¬١) ولأنه مخل بستر العورة.
الهداية ١/ ١٦٥، تبيين الحقائق ٢/ ٣٨، كنز الدقائق ٢/ ٣٨، بداية المبتدي ١/ ١٦٥.
(¬٢) بداية المبتدي ١/ ١٦٥، كنز الدقائق ٢/ ٣٨، تبيين الحقائق ٢/ ٣٨، الهداية ١/ ١٦٥.
(¬٣) أبو داود ٢/ ٢٠٣ كتاب المناسك، باب الحلق والتقصير رقم ١٩٨٤، والدارمي ١/ ٤٩٣ كتاب المناسك، باب من قال ليس على النساء حلق رقم ١٨٤٢، والدارقطني ٢/ ٢٧١ كتاب الحج، باب المواقيت رقم ١٦٦، والبيهقي ٥/ ١٠٤ كتاب الحج، باب ليس على النساء حلق ولكن يقصرن، والطبراني في الكبير ١٢/ ١٣٠١٨ ولم أقف عليه عند أحمد ولم أر من عزاه إليه.
من طريق ابن جريج، عن صفية بنت شيبة بن عثمان عن أم عثمان بنت أبي سفيان، أن ابن عباس -رضي الله عنهما- قال: قال رسول الله -صلى الله عليه وسلم- .... ".
قال ابن حجر في التلخيص ٢/ ٢٦١: وإسناده حسن.
وقال في بلوغ المرام ص ١٦١ رقم ٢٨: رواه أبو داود، بإسناد حسن.
(¬٤) تبيين الحقائق ٢/ ٣٩.
(¬٥) قال ابن هبيرة في الإفصاح ١/ ٢٨٤: "واتفقوا أن المرأة في ذلك كالرجل، وتنفرد عنه: بأنها يجوز لها لبس القميص، والخف، والسراويل، والخمار، وأنها لا تكشف رأسها، بل تكشف وجهها، وقد رخص لها أن تسدل عليه مع الحاجة ما لا يقع على بشرتها، وأنها =','3','312',NULL,NULL,'0');
INSERT INTO [b46] VALUES (988,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
=  لا ترفع صوتها بالتلبية إلا بمقدار ما تسمع رفيقتها، ولا رمل عليها، ولا سعي، بل طوافها وسعيها مشي كله، وأنه لا حلق عليها، وإنما عليها التقصير اهـ".
وأيضًا لا تضطبع ولا ترقى المشعر ولا الصفا والمروة.
الهداية ١/ ١٦٤، الكتاب ١/ ١٩٥، الاختيار ١/ ١٥٦، المختار ١/ ١٥٦، تبيين الحقائق ٢/ ٣٨، حاشية الدسوقي ٢/ ٥٥، الكافي لابن عبد البر ص ١٥٣، شرح الزرقاني على مختصر خليل ٢/ ٢٩١، الشرح الكبير للدردير ٢/ ٥٤، أقرب المسالك ص ٥١، التاج والإكليل ٣/ ١٠٧، التفريع ١/ ٣٢٢، مختصر خليل ص ٨٢، الشرح الصغير ١/ ٢٧٦، متن الرسالة ص ٨٨، بلغة السالك ١/ ٢٨٥، رحمة الأمة ١/ ١٣٢، تحفة المحتاج ٤/ ٦١، حاشية ابن قاسم العبادي ٤/ ٦١، أنوار المسالك ص ٢٤٧، الشرواني ٤/ ٨٨، شرح المحلي على المنهاج ٢/ ١١٨، قليوبي ٢/ ١١٨، المحرر ١/ ٢٤٨.','3','313',NULL,NULL,'0');
INSERT INTO [b46] VALUES (989,'<span data-type="title" id=toc-492>فصل</span>
القران أفضل من التمتع، والإفراد.
<hr><span data-type="title">منحة السلوك</span>

فصل
هذا الفصل في بيان أحكام القران، والتمتع (¬١) (¬٢). وهو مصدر من قرنت إذا جمعت (¬٣).

<span data-type="title" id=toc-493>[أفضل المناسك]</span>
قوله: القران أفضل من التمتع، والإفراد (¬٤).
وقال الشافعي (¬٥)، ومالك: الإفراد أفضل (¬٦).
_________
(¬١) لما فرغ من بيان الإفراد بالحج، شرع في بيان القران بين الحج والعمرة؛ لأن وجود المفرد، سابق على وجود المركب.
حاشية الشلبي ٢/ ٤٠، العناية ٢/ ٥١٨.
(¬٢) وهو الترفق، وهو الانتفاع بأداء النسكين، العمرة والحج في سفر واحد.
أنيس الفقهاء ص ١٤١، التعريفات ص ٨٠، المصباح المنير ٢/ ٥٦٢ مادة المتاع، لسان العرب ٨/ ٣٢٩ مادة متع.
(¬٣) وهو الجمع بين الحج والعمرة، بإحرام واحد، في سفر واحد، وهو في الأصل: مصدر قرن بين الحج والعمرة، إذا جمع بينهما. وهو قارن.
أنيس الفقهاء ص ١٤٠، التعريفات ص ١٨٨، المصباح المنير ٢/ ٥٠٠ مادة قرن، المغرب ص ٣٨٠ مادة القرن، لسان العرب ١٣/ ٣٣٦ مادة قرن.
(¬٤) بداية المبتدي ١/ ١٦٦، الكتاب ١/ ١٧٦، شرح الهداية لمحمد بن علي بن طولون الصالحي (مخطوط) جـ ٢ لوحة ٥/ أالنسخة الأصلية لدى مكتبة جامعة برنستون في الولايات المتحدة، تحت رقم ٥٤٦ مجموعة يهودا.
وهو أن يقول: لبيك بحجة؛ لأنه أفردها ولم يقرن بها عمرة.
حلية الفقهاء ص ١١٧، أنيس الفقهاء ص ١٤٢، المطلع على أبواب المقنع ص ١٦٨، المصباح المنير ٢/ ٤٦٦ مادة الفرد.
(¬٥) هداية الغلام ص ٧١، حاشية البيجوري ١/ ٣٣٣.
(¬٦) ثم التمتع، ثم القران.
القوانين الفقهية ص ٩١، التلقين ص ٦٧.','3','314',NULL,NULL,'0');
INSERT INTO [b46] VALUES (990,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وقال أحمد: التمتع أفضل (¬١).
ولنا: قوله تعالى: {وَأَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلَّهِ} [البقرة: ١٩٦]، وإتمامهما أن يحرم بهما من دويرة أهله، كذا فسرته الصحابة (¬٢). وهو القران، وحديث أنس أنه قال: سمعت رسول الله -صلى الله عليه وسلم- يقول: "لبيك (¬٣) عمرة، وحجًا" رواه البخاري، ومسلم (¬٤). وعنه قال: سمعت رسول الله -صلى الله عليه وسلم- يقول: "لبيك عمرة وحجًا، لبيك عمرة وحجًا" متفق عليه (¬٥). وعن علي -رضي الله عنه- قال:
_________
(¬١) ثم الإفراد، ثم القران. وعنه: إن ساق الهدي فالقران أفضل، وإن لم يسق الهدي، فالتمتع أفضل.
الإفصاح ١/ ٢٦٣، المقنع ١/ ٣٩٨، كشاف القناع ٢/ ٤١٠.
(¬٢) كعلي بن أبي طالب -رضي الله عنه-، ومن التابعين سعيد بن جبير، وطاووس رحمهما الله.
زاد المسير في علم التفسير ١/ ١٨٥، تفسير ابن جرير الطبري ٢/ ٢١٢، الدر المنثور ١/ ٣٧٦، تفسير ابن كثير ١/ ٣٤٥، تفسير النسفي ١/ ١٤٠.
(¬٣) وهي من إلباب بعد إلباب. والإلباب: اللزوم. أي: أنا عبدك، وأنا مقيم على طاعتك، وأمرك.
لسان العرب ١/ ٧٣١ مادة لبب، القاموس المحيط ٤/ ١١٣ مادة ل ب ب، مختار الصحاح ص ٢٤٦ مادة ل ب ب، مجمل اللغة ص ٦٢٨ باب اللام وما بعدها في المضاعف والمطابق مادة لب، المصباح المنير ٢/ ٥٤٧ مادة لب، حلية الفقهاء ص ١١٧، أنيس الفقهاء ص ١٤٢، المطلع على أبواب المقنع ص ١٦٨.
(¬٤) البخاري ٤/ ١٥٨٢ كتاب المغازي، باب بعث علي بن أبي طالب، وخالد بن الوليد، إلى اليمن قبل حجة الوداع رقم ٤٠٩٦ ولفظه: "أن النبي -صلى الله عليه وسلم- أهل بعمرة، وحجة"، ومسلم ٢/ ٩٠٥ كتاب الحج، باب في الإفراد والقران بالحج والعمرة رقم ١٢٣٢ ولفظه: "سمعت رسول الله -صلى الله عليه وسلم- يقول: لبيك عمرة وحجة".
(¬٥) البخاري ٤/ ١٥٨٢ كتاب المغازي، باب بعث علي بن أبي طالب، وخالد بن الوليد، إلى اليمن قبل حجة الوداع رقم ٤٠٩٦ ولفظه: "أن النبي -صلى الله عليه وسلم- أهل بعمرة وحجة"، ومسلم =','3','315',NULL,NULL,'0');
INSERT INTO [b46] VALUES (991,'وصفته: أن يُهِلَّ بالعمرة والحج معًا من الميقات، فإذا دخل مكة بدأ بالعمرة، ثم بالحج،
<hr><span data-type="title">منحة السلوك</span>

" أتيت النبي -صلى الله عليه وسلم- فقال: كيف أهللت؟ قلت: أهللت (¬١) بإهلالك، فقال: إني سقت الهدي، وقرنت" رواه أبو داود، والنسائي (¬٢).

<span data-type="title" id=toc-494>[صفة القران]</span>
قوله: وصفته: أي: صفة القران: أن يهل، أي: يحرم بالعمرة والحج معًا من الميقات، ويقول: اللهم إني أريد العمرة والحج فيسرهما لي، وتقبلهما مني؛ لما تلونا، وروينا (¬٣).
قوله: فإذا دخل مكة بدأ بالعمرة، ثم بالحج (¬٤).
لقوله تعالى: {فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ إِلَى الْحَجِّ} [البقرة: ١٩٦].
وكلمة "إلى" لانتهاء الغاية، فيقدم العمرة ضرورة، حتى يكون انتهاء
_________
= ٢/ ٩٠٥ كتاب الحج، باب في الإفراد والقران بالحج والعمرة رقم ١٢٣٢ ولفظه: "سمعت رسول الله -صلى الله عليه وسلم- يقول: "لبيك عمرة وحجة" من غير تكرار".
(¬١) الإهلال: رفع الصوت بالتلبية.
لسان العرب ١١/ ٧٠١ مادة هلل، القاموس المحيط ٤/ ٥٢٧ مادة هـ ل ل، مختار الصحاح ص ٢٩١ مادة هـ ل ل، المغرب ص ٥٠٥ مادة أهلوا، تاج العروس ٨/ ١٧١ مادة هلل، الدر النقي ٢/ ٤٢٨، طلبة الطلبة ص ٦٧، المطلع على أبواب المقنع ص ٣٠٦.
(¬٢) أبو داود ٢/ ١٥٨ كتاب المناسك، باب في الإقران رقم ١٧٩٧، والنسائي ٥/ ١٥٨ كتاب مناسك الحج، باب الحج بغير نية يقصده المحرم رقم ٢٧٤٥، ورواه أيضًا البخاري في صحيحه ٢/ ٥٦٤ كتاب الحج، باب من أهل في زمن النبي -صلى الله عليه وسلم- كإهلال النبي رقم ١٤٨٢.
(¬٣) الكتاب ١/ ١٩٦، الاختيار ١/ ١٦٠.
(¬٤) الهداية ١/ ١٦٧، مختصر الطحاوي ص ٦٤، المختار ١/ ١٦٠، كنز الدقائق ٢/ ٤٣، بداية المبتدي ١/ ١٦٧، تبيين الحقائق ٢/ ٤٠، الاختيار ١/ ١٦٠، الأصل ٢/ ٣١٨.','3','316',NULL,NULL,'0');
INSERT INTO [b46] VALUES (992,'فإذا رمى الجمرة يوم النحر، أراق دمًا إن قدر، وإلا صام ثلاثة أيام، آخرها يوم عرفة، وسبعة أيام إذا رجع.
والتمتع أفضل من الإفراد.
<hr><span data-type="title">منحة السلوك</span>

العمرة بالحج (¬١). والآية وإن نزلت في التمتع فالقران بمعناه، من حيث أن كلًا منهما ترفق بأداء النسكين، في سفرة واحدة (¬٢).
قوله: فإذا رمى الجمرة. أي: جمرة العقبة يوم النحر أراق دمًا.
أي: ذبح شاة، أو بدنة، أو سبعها؛ لقوله تعالى: {فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ إِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ} [البقرة: ١٩٦]، والقران: بمعنى التمتع (¬٣). وكان -صلى الله عليه وسلم- قارنًا، وذبح الهدايا (¬٤). وقال جابر: "حججنا مع رسول الله -صلى الله عليه وسلم- فنحرنا البعير عن سبعة والبقرة عن سبعة" رواه البخاري ومسلم (¬٥).
قوله: إن قدر. أي: إن قدر على إراقة الدم، وإلا صام ثلاثة أيام، آخرها يوم عرفة، وسبعة أيام إذا رجع إلى أهله؛ لقوله تعالى: {فَمَنْ لَمْ يَجِدْ فَصِيَامُ ثَلَاثَةِ أَيَّامٍ فِي الْحَجِّ وَسَبْعَةٍ إِذَا رَجَعْتُمْ تِلْكَ عَشَرَةٌ كَامِلَةٌ} [البقرة: ١٩٦] (¬٦).

<span data-type="title" id=toc-495>[التمتع وصفته]</span>
قوله: والتمتع أفضل من الإفراد.
_________
(¬١) تبيين الحقائق ٢/ ٤٣، العناية ٢/ ٥٢٥، شرح فتح القدير ٢/ ٥٢٥.
(¬٢) تبيين الحقائق ٢/ ٤٣، العناية ٢/ ٥٢٦.
(¬٣) الهداية ١/ ١٦٧، كنز الدقائق ٢/ ٤٣، تبيين الحقائق ٢/ ٤٣.
(¬٤) رواه البخاري ٢/ ٥٦٦ كتاب الحج، باب التمتع، والإقران، والإفراد بالحج رقم ١٤٨٦، ومسلم ٢/ ٩٠٥ كتاب الحج، باب في الإفراد، والقران بالحج والعمرة رقم ١٢٣٢.
(¬٥) رواه مسلم ٢/ ٩٥٥ كتاب الحج باب الاشتراك في الهدي وإجزاء البقرة والبدنة كل منهما عن سبعة رقم ٣٥٢ (١٣١٨) ولم أقف عليه عند البخاري.
(¬٦) وكذا عند المالكية.
الكتاب ١/ ١٩٧، كنز الدقائق ٢/ ٤٣، القوانين ص ٩١، الكافي لابن عبد البر ص ١٥٠.','3','317',NULL,NULL,'0');
INSERT INTO [b46] VALUES (993,'وصفته: أن يهل بالعمرة من الميقات، فإذا دخل مكة أدّى العمرة وحلَّ منها، ثم يُحْرمُ بالحج يوم التروية من الحرم، ويفعَلُ ما يفعله المفرد،
<hr><span data-type="title">منحة السلوك</span>

هذا في ظاهر الرواية (¬١). وروى الحسن عن أبي حنيفة: أن الإفراد أفضل (¬٢).
قوله: وصفته: أي: صفة التمتع: أن يهل بالعمرة من الميقات، فإذا دخل مكة أدّى العمرة، فيطوف لها ويسعى، ويحلق أو يقصر، وقد حل منها، ثم يحرم بالحج يوم التروية من الحرم، ويفعل مثل ما يفعله الحاج المفرد، ويقطع التلبية بأول الطواف (¬٣)؛ لما روي: "أنه -صلى الله عليه وسلم- كان يمسك عن التلبية في العمرة، إذا استلم الحجر" رواه أبو داود (¬٤).
_________
(¬١) تحفة الفقهاء ١/ ٤١٣، بداية المبتدي ١/ ١٦٩.
(¬٢) الهداية ١/ ١٦٩، المبسوط ٤/ ٢٥، تحفة الفقهاء ١/ ٤١٣.
(¬٣) بداية المبتدي ١/ ١٦٩، الكتاب ١/ ١٩٨.
(¬٤) ٢/ ١٦٣ كتاب المناسك، باب متى يقطع المعتمر التلبية رقم ١٨١٧، والترمذي ٣/ ٢٨٤ كتاب الحج، باب ما جاء في متى تقطع التلبية في العمرة رقم ٩١٩، والبيهقي ٥/ ١٠٥ كتاب الحج، باب لا يقطع المعتمر التلبية حتى يفتتح الطواف، وأبو يعلى ٤/ ٣٥٩ رقم ٢٤٧٥.
قال الترمذي: حسن صحيح.
من طرق عن ابن أبي ليلى، عن عطاء، عن ابن عباس -رضي الله عنهما-، وابن أبي ليلى ضعيف لسوء حفظه.
قال البيهقي: رفعه خطأ. وكان ابن أبي ليلى هذا كثير الوهم. وخاصة إذا روى عن عطاء، فيخطيء كثيرًا. ضعفه أهل النقل مع كبر محله في الفقه.
وقد أشار أبو داود إلى وقفه بقوله: رواه عبد الملك بن أبي سليمان، وهمام، عن عطاء، عن ابن عباس موقوفًا.
واللفظ الذي ساقه المؤلف للترمذي، والبيهقي، وأبي يعلى.
أما لفظ أبي داود: عن عطاء، عن ابن عباس -رضي الله عنهما- عن النبي -صلى الله عليه وسلم- قال: "يلبي المعتمر حتى يستلم الحجر".','3','318',NULL,NULL,'0');
INSERT INTO [b46] VALUES (994,'وعليه الدم، أو بدله كالقارن.
<hr><span data-type="title">منحة السلوك</span>

قوله: وعليه أي: على المتمتع دم، أو بدله، وهو: أن يصوم ثلاثة أيام، آخرها يوم عرفة، وسبعة أيام إذا رجع. كما مر في القارن (¬١) (¬٢).
_________
(¬١) في ٣/ ٣١٧.
(¬٢) وفاقًا للثلاثة.
الكتاب ١/ ١٩٩، مختصر الطحاوي ص ٧٤، بداية المجتهد ١/ ٣٣٥، التفريع ١/ ٣٣٢، فتح الوهاب ٢/ ٤٨٩، حاشية الجمل ٢/ ٤٨٩، المحرر ١/ ٢٣٥، الكافي لابن قدامة ١/ ٣٩٥.','3','319',NULL,NULL,'0');
INSERT INTO [b46] VALUES (995,'<span data-type="title" id=toc-496>فصل</span>
إذا طيَّب المحرم عضوًا، لزمه دم شاة،
<hr><span data-type="title">منحة السلوك</span>

فصل
هذا الفصل في أحكام الجنايات (¬١).

<span data-type="title" id=toc-497>[محظورات الإحرام]</span>
قوله: إذا طيَّب المحرم عضوًا، لزمه دم أي: شاة.
وذلك مثل الرأس، والفخذ، والساق؛ لأن الجناية تتكامل بتكامل الارتفاق (¬٢)، وذلك في العضو الكامل (¬٣).
وكذا إذا أكل طيبًا كثيرًا عند أبي حنيفة (¬٤).
وقالا: صدقة (¬٥).
_________
(¬١) جنى على نفسه وأهله جناية: إذا فعل مكروهًا.
لسان العرب ١٤/ ١٥٤ مادة جني، القاموس المحيط ١/ ٥٤٤ مادة ج ن ي، مختار الصحاح ص ٤٨ مادة ج ن ي، المصباح المنير ١/ ١١٢ مادة جنيت، المغرب ص ٩٤ مادة الجناية.
(¬٢) يقال: ارتفقت بالشيء، أي: انتفعت به.
لسان العرب ١٠/ ١١٨ مادة رفق، القاموس المحيط ٢/ ٣٦٩ مادة ر ف ق، المغرب ص ١٩٤ مادة رفق، المصباح المنير ١/ ٢٣٤ مادة رفقت.
(¬٣) فيترتب عليه كمال الموجب.
وذهب المالكية، والشافعية، والحنابلة: إلى أنه لا فرق بين أن يطيب جميع جسده، أو عضو منه، أو دون ذلك، فيجب في الجميع فدية.
تبيين الحقائق ٢/ ٥٢، الهداية ١/ ١٧٣، بداية المبتدي ١/ ١٧٣، الكتاب ١/ ٢٠٣، مواهب الجليل ٣/ ١٥٨، الخرشي على خليل ٢/ ٣٥٢، أسنى المطالب ١/ ٥٠٨، روض الطالب ١/ ٥٠٨، غاية المنتهى ٢/ ٢٣١، الفروع ٣/ ٣٧٥.
(¬٤) الأصل ٢/ ٣٩٨، الجامع الصغير لمحمد بن الحسن ص ١٥٤، الهداية ١/ ١٧٣.
(¬٥) لأنه لم يستعمله استعمال الطيب. =','3','320',NULL,NULL,'0');
INSERT INTO [b46] VALUES (996,'وإن كان أقلَّ لزمه صدقةٌ. أي: نصف صاعٍ من برٍّ.
<hr><span data-type="title">منحة السلوك</span>

قوله: وإن كان أقل. أي: من العضو لزمه صدقة؛ لقصور الجناية (¬١).
والمراد من الصدقة في هذا الباب جميعه: نصف صاع من بر، أو صاع من تمر، أو شعير (¬٢)، إلا ما يجب بقتل جرادة (¬٣)،
_________
= وذهب المالكية: إلى أنه إذا أكل طيبًا لم تمسه النار، فعلى روايتين: إحداهما: أن عليه الكفارة، والأخرى: لا كفارة عليه. وإذا أكل طيبًا مسته النار فلا فدية عليه.
وعند الشافعية: لو أكل طيبًا فلا فدية عليه. وعند الحنابلة: إذا استعمل المحرم الطيب في أكل، أو شرب، أو ادهان، حرم وفدى.
الجامع الصغير لمحمد بن الحسن ص ١٥٤، تبيين الحقائق ٢/ ٥٢، التفريع ١/ ٣٢٦، منح الجليل ٢/ ٣١٧، روض الطالب ١/ ٥٠٨، أسنى المطالب ١/ ٥٠٨، مطالب أولي النهى ٢/ ٢٣١، الفروع ٣/ ٣٧٥.
(¬١) وقال محمد: يجب بقدره من الدم.
بداية المبتدي ١/ ١٧٣، كنز الدقائق ٢/ ٥٢، الهداية ١/ ١٧٣، العناية ٣/ ٢٥، تبيين الحقائق ٢/ ٥٢.
(¬٢) وعند المالكية: والشافعية، والحنابلة: الفدية في ارتكاب المحظورات فيما يجب فيه الفدية، هي: صيام ثلاثة أيام، أو إطعام ستة مساكين، لكل مسكين نصف صاع عند المالكية، وعند الشافعية: مد لكل مسكين. وعند الحنابلة: مد بر، أو نصف صاع من غيره، أو نسك شاة. وهذه الفدية على التخيير.
منح الجليل ٢/ ٣٢٨، التلقين ص ٦٦، القوانين ص ٩٣، متن أبي شجاع ص ١١٢، التذكرة ص ٨٤، الكافي لابن قدامة ١/ ٤١٧، المقنع ١/ ٤٢٠.
(¬٣) وعند المالكية: يجب في قتل الجراد القيمة، إلا إذا عم الجراد، فلا يستطيع دفعه، واجتهد المحرم في التحفظ من قتله فلا قيمة.
وعند الشافعية: يجب بقتل الجراد، والقمل القيمة.
وعند الحنابلة: يجب بقتل الجراد القيمة، ويحرم بإحرام قتل قمل، وصئبانه، ولا جزاء فيه.
مختصر خليل ص ٩٠، جواهر الإكليل ١/ ١٩٥، مغني المحتاج ١/ ٥٢٦، تحفة المحتاج ٤/ ١٨٨، الروض المربع ص ١٩٦، المحرر ١/ ٢٤١.','3','321',NULL,NULL,'0');
INSERT INTO [b46] VALUES (997,'وإن خضب رأسه بحناء، لزمه دمٌ، وإن لبَّده لزمه دمان،
<hr><span data-type="title">منحة السلوك</span>

أو قمل (¬١)، أو بإزالة شعرات قليلة من رأسه، أو عضو آخر من أعضائه (¬٢).

<span data-type="title" id=toc-498>[الخضاب للمحرم]</span>
قوله: وإن خضب رأسه بحناء، لزمه دم.
لأن الحناء طيب (¬٣)؛ لقوله -صلى الله عليه وسلم-: "الحناء طيب" رواه البيهقي (¬٤).
قوله: وإن لبَّده. أي: وإن لبد رأسه بالحناء، لزمه دمان، دم للتطيب
_________
(¬١) القمل -بفتح القاف، وسكون الميم-: جمع قملة: وهي حشرة تتولد على البدن، عند دفعه العفونة إلى الخارج.
حياة الحيوان للدميري ٢/ ٢٢٤، المعجم الوسيط ٢/ ٧٦٠ مادة القملة.
(¬٢) وعند المالكية: في إزالة الشعر إلى عشر شعرات لغير إماطة الأذى، وفي إزالة الشعر لإماطة الأذى الفدية حفنة، وفي قتل القملة إلى عشر قملات حفنة من طعام، فإن زاد ففيه الفدية.
وعند الشافعية، والحنابلة: في إزالة الشعر مد من طعام، وفي الشعرتين مدان، وتكمل الفدية في إزالة ثلاث شعرات.
بداية المبتدي ١/ ١٧٣، شرح فتح القدير ٣/ ٢٦، المصفى للنسفي (مخطوط لوحة ٢١/ أالنسخة الأصلية لدى المكتبة الوطنية بتونس تحت رقم ٣٣٦٤)، الهداية ١/ ١٧٣، العناية ٣/ ٢٦، الشرح الصغير ١/ ٢٨٩، بلغة السالك ١/ ٢٨٩، شرح المحلي على المنهاج ٢/ ١٣٤، قليوبي ٢/ ١٣٤، منتهى الإرادات ٢/ ٢١، المقنع ١/ ٤٠٣.
(¬٣) وكذا عند المالكية.
وذهب الشافعية، والحنابلة: إلى أنه لا شيء في الخضاب، أو الحناء.
الأصل ٢/ ٤٠٠، بداية المبتدي ١/ ١٧٣، الهداية ١/ ١٧٣، العناية ٣/ ٢٦، حاشية الدسوقي ١/ ٦٠، نهاية المحتاج ٣/ ٣٣٤، غاية المنتهى ٢/ ٣٣١.
(¬٤) البيهقي في معرفة السنن والآثار ٧/ ١٦٨ كتاب الحج، باب لبس المعصفرات رقم ٩٦٨٩، والطبراني في الكبير ٢٣/ ١٢٩٦٦. من طريق ابن لهيعة، عن بكير بن عبد الله بن الأشج، عن خولة بن حكيم، عن أمها -رضي الله عنها- أن رسول الله -صلى الله عليه وسلم- قال لأم سلمة: "لا تطيبي وأنت محرمة، ولا تمسي الحناء، فإنه طيب". =','3','322',NULL,NULL,'0');
INSERT INTO [b46] VALUES (998,'وإن ادَّهن بزيت،
<hr><span data-type="title">منحة السلوك</span>

ودم لتغطية الرأس (¬١)، فظهر من هذا أن المراد من قوله: "خضب رأسه" هو أن يكون الحناء مانعًا (¬٢).
قوله: وإن ادهن بزيت، إلى قوله: "لزمه دم"، أما إذا ادهن بزيت: فلأنه أصل الطيب فيجب دم. هذا عند أبي حنيفة (¬٣).
وقالا: صدقة (¬٤).
وهذا الخلاف في الزيت البحت (¬٥)، والحل (¬٦) البحت، أي: الخالص الذي لا يخالطه طيب.
أما المطيب بالبنفسج (¬٧)،
_________
= قال البيهقي في معرفة السنن والآثار ٧/ ١٦٨: وهذا إسناد ضعيف، ابن لهيعة غير محتج به.
قال في الجوهر النقي لابن التركماني ٥/ ٦٢: وقد عد أبو حنيفة الدينوري، وغيره من أهل اللغة، الحناء من أنواع الطيب.
(¬١) وعند المالكية: عليه دم واحد.
الهداية ١/ ١٧٣، العناية ٣/ ٢٦، شرح الزرقاني على خليل ٢/ ٣٠٣، التاج والإكليل ٣/ ١٦٤.
(¬٢) تبيين الحقائق ٢/ ٥٢.
(¬٣) بداية المبتدي ١/ ١٧٤، الجامع الصغير ص ١٥٤، الهداية ١/ ١٧٤.
(¬٤) لأنه من الأطعمة، إلا أن فيه نوع ارتفاق، بمعنى قتل الهوام، وإزالة الشعث، فكانت جناية قاصرة.
بداية المبتدي ١/ ١٧٤، الجامع الصغير ص ١٥٤، الهداية ١/ ١٧٤، تبيين الحقائق ٢/ ٥٣.
(¬٥) شرح فتح القدير ٣/ ٢٧، العناية ٣/ ٢٧.
(¬٦) الحل -بالحاء المهملة المفتوحة-: السمسم.
مختار الصحاح ص ٦٣ مادة ح ل ل، لسان العرب ١١/ ١٧٣ مادة حلل.
(¬٧) البنفسج: نبات له زهر، يزرع للزينة، وعطر الرائحة، ويتخذ منه علاجًا لبعض الأمراض =','3','323',NULL,NULL,'0');
INSERT INTO [b46] VALUES (999,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

والزنبق (¬١)، والبان، وما أشبه ذلك يجب فيه الدم بالإجماع (¬٢). وهذا إذا استعمله على وجه التطيب (¬٣).
وأما لو داوى به جرحه، أو شقوق رجليه، فلا شيء عليه بالإجماع (¬٤).
_________
= وهو فارسي معرب.
القاموس المحيط ١/ ٣٢٧ مادة البنفسج، المصباح المنير ١/ ٦٢ مادة البنفسج، المعرب ص ٢٠٤ مادة البنفسج، المعجم الوسيط ١/ ٧١ مادة البنفسج.
(¬١) الزنبق: دهن الياسمين.
لسان العرب ١٠/ ١٤٦، ماة زنبق، القاموس المحيط ٢/ ٤٧٩ ز ن ب ق.
(¬٢) لأنه طيب.
تبيين الحقائق ٢/ ٥٣.
(¬٣) شرح فتح القدير ٣/ ٢٧، العناية ٣/ ٢٧.
(¬٤) لأنه ليس بطيب في نفسه، وإنما هو أصل الطيب، أو هو طيب من وجه، فيشترط استعماله على وجه التطيب، ألا يرى أنه إذا أكله لا يجب عليه شيء؛ لأنه لم يستعمله استعمال الطيب، بخلاف ما إذا تداوى بالمسك وما أشبهه؛ لأنه طيب بنفسه فلا يتغير باستعماله، حتى لو أكل زعفرانًا مخلوطًا بطعام، أو طيب آخر، ولم تمسه النار يلزمه دم. وإن مسته فلا شيء عليه؛ لأنه صار مستهلكًا. وعلى هذه التفاصيل في المشروب، ولو ادهن بالشحم، أو السمن فلا شيء عليه.
وعند المالكية: إن ادهن بمطيب، أو بغير مطيب لا لعلة، افتدى. وأما بغير المطيب لعلة، ففي باطن الكف والقدم لا فدية، وفي الجسد قولان: قيل: فيه فدية، وقيل: لا فدية.
وعند الشافعية: تحرم الأدهان المطيبة، كدهن الورد، والياسمين ويجب فيه الفدية، وغير المطيبة كالشيرج، لا يحرم إلا في الرأس، واللحية.
وعند الحنابلة: الدهن غير المطيب لا شيء فيه. والطيب أو ما ينبته الآدمي لطيب، ويتخذ منه الطيب، كورد، وبنفسج، وياسمين، ونحوها، يجب فيه الدم. وإذا استعمل الطيب لحاجة، فعليه دم.
بداية المبتدي ١/ ١٧٤، تبيين الحقائق ٢/ ٥٣، الهداية ١/ ١٧٤، الشرح الكبير للدردير =','3','324',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1000,'أو لبس مخيطًا يومًا، أو غطى رأسه يومًا، أو حلق رُبع رأسه، أو ربع لحيته،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-499>[اللبس والحلق للمحرم]</span>
وأما إذا لبس مخيطًا يومًا: فعند الشافعي يجب الدم بنفس اللبس (¬١).
ولنا: أن الارتفاق الكامل به لا يحصل إلا بالدوام؛ لأن المقصود منه دفع الحر، والبرد، واليوم يشتمل عليهما فقدرناه به (¬٢). وكذلك الكلام في تغطية الرأس يومًا (¬٣).
وأما إذا حلق ربع رأسه، أو ربع لحيته، فلأن الربع يقوم مقام الكل (¬٤).
_________
= ١/ ٦٠، حاشية الدسوقي ١/ ٦٠، رحمة الأمة ١/ ١٣٣، نهاية المحتاج ٣/ ٣٣٤، مطالب أولي النهى ٢/ ٣٣١، الفروع ٣/ ٣٧٥.
(¬١) وكذا عند الحنابلة.
وعند المالكية: الشرط في ذلك الانتفاع من حر، أو برد، وإن لم ينتفع بالفعل، ولبس ثوبًا شفافًا لا يقي حرًا، ولا بردًا، وتراخى زمنًا طويلًا، فعليه الفدية أيضًا. وإن نزعه فورًا ولم ينتفع به من حر، ولا برد، فلا فدية.
منح الجليل ٢/ ٣٢٧، مواهب الجليل ٣/ ١٦٥، هداية الغلام ص ٧٢، حاشية ابن قاسم العبادي ٤/ ١٥٩، متن أبي شجاع ص ١٠٩، التذكرة ص ٨٢، دليل الطالب ١/ ٢٤٥، هداية الراغب ص ٢١٦.
(¬٢) تبيين الحقائق ٢/ ١٥٣، العناية ٣/ ٢٩، تحفة الفقهاء ١/ ٤١٩، الهداية ١/ ١٧٤.
(¬٣) وعند المالكية: الشرط في ذلك: الانتفاع من حر، أو برد، أو تراخى زمنًا طويلًا، فعليه الفدية. وإن نزعه فورًا ولم ينتفع به من حر، ولا برد، فلا فدية.
وعند الشافعية، والحنابلة: تجب الفدية بمجرد التغطية.
تحفة الفقهاء ١/ ٤٢٠، بداية المبتدي ١/ ١٧٥، منح الجليل ٢/ ٣٢٧، مواهب الجليل ٣/ ١٦٥، هداية الغلام ص ٧٢، حاشية ابن قاسم العبادي ٤/ ١٥٩، هداية الراغب ص ٢١٦، شرح منتهى الإرادات ٢/ ٣٤.
(¬٤) الهداية ١/ ١٧٥، تبيين الحقائق ٢/ ٥٤.','3','325',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1001,'أو كل رقبته، أو أحد إبطيه، لزمه دمٌ.
وإن كان أقل في الكل، لزمه صدقةٌ. وإن قصَّ من شاربه شيئًا، فعليه حكومةٌ عدل.
<hr><span data-type="title">منحة السلوك</span>

وأما إذا حلق كل رقبته: فلأنها عضو كامل يكمل الارتفاق بحلقه (¬١).
وكذلك الإبطان أو أحدهما (¬٢).
قوله: وإن كان أقل في الكل. يعني: إذا لبس، أو غطى رأسه أقل من يوم، أو حلق أقل من ربع رأسه، أو لحيته، أو حلق بعض رقبته، أو بعض إبطه، لزمه صدقة؛ لقصور الجناية (¬٣).
قوله: وإن قص من شاربه شيئًا، فعليه حكومة عدل (¬٤).
وتفسيره: أنه ينظر أن هذا المأخوذ كم يكون من ربع اللحية؟ فيجب
_________
(¬١) الهداية ١/ ١٧٥، تبيين الحقائق ٢/ ٥٤.
(¬٢) وعند المالكية: في قلم الظفر الواحد، لا لإماطة الأذى، ولا لكسره، بل عبثًا، أو ترفهًا، حفنة من طعام. وفي قص ما زاد على الواحد فدية، سواء كان لإماطة الأذى، أو لا. وكذا إن كان لإماطة الأذى، ولو واحدًا. وإن أبان واحدًا بعد آخر: فإن كان في فور ففدية، وإلا ففي كل حفنة، وإن أزال عشر شعرات فأقل، لغير إماطة الأذى، ففيها حفنة من طعام، ولإماطته فدية. ولو زادت على العشرة: ففيها فدية. وعند الشافعية، والحنابلة: في إزالة الظفر، والشعرة الواحدة، مد من طعام. وفي اثنين من كل منهما مدان، وفي إزالة ثلاثة فأكثر، فدية.
بداية المبتدي ١/ ١٧٥، كنز الدقائق ٢/ ٥٤، الهداية ١/ ١٧٥، تبيين الحقائق ٢/ ٥٤، الشرح الكبير للدردير ٢/ ٦٤، حاشية الدسوقي ٢/ ٦٤، منهج الطلاب ٢/ ٥١٣، فتح الوهاب ٢/ ٥١٣، الإقناع للحجاوي ٢/ ٤٢٢، السلسبيل ١/ ٣٦٨.
(¬٣) الهداية ١/ ١٧٥، تبيين الحقائق ٢/ ٥٤، تحفة الفقهاء ١/ ٤٢١، كنز الدقائق ٢/ ٥٤.
(¬٤) بداية المبتدي ١/ ١٧٥، الجامع الصغير ص ١٥٥، تحفة الفقهاء ١/ ٤٢١، الهداية ١/ ١٧٥.','3','326',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1002,'وإن حلق مواضع المحاجم، أو قص في مجلس كل أظفاره، أو ربعها، لزمه دمٌ.
<hr><span data-type="title">منحة السلوك</span>

عليه بحسابه من الطعام، حتى إذا أخذ منه نصف ثمن اللحية، يجب عليه ربع الدم (¬١).

<span data-type="title" id=toc-500>[تقليم الأظافر]</span>
قوله: وإن حلق موضع المحاجم، أو قص في مجلس كل أظفاره، أو ربعها، لزمه دم (¬٢).
وأما إذا حلق موضع الحجامة: فعليه دم عند أبي حنيفة؛ لأنه حلق موجود لأمر مقصود وهو الحجامة (¬٣).
وقالا: عليه صدقة (¬٤).
والمحاجم: جمع محجمة -بكسر الميم، وفتح الجيم- وهي: قارورة الحجام (¬٥).
وأما المحجم: -بفتح الميم والجيم- فهو: اسم مكان من الحجم، وجمعه محاجم أيضًا، والمراد ههنا الأول، وإلا يلزم الخلل على ما لا يخفى على الفطن الفهم.
وأما إذا قص أظفاره في المجلس، فلأن إزالة ما ينمو من بدن الإنسان
_________
(¬١) شرح فتح القدير ٣/ ٣٣، تبيين الحقائق ٢/ ٥٥، الهداية ١/ ١٧٥.
(¬٢) الاختيار ١/ ١٦٢، بداية المبتدي ١/ ١٧٥، الهداية ١/ ١٧٥.
(¬٣) الاختيار ١/ ١٦٢، مختصر الطحاوي ص ٦٩، الهداية ١/ ١٧٥، تبيين الحقائق ٢/ ٥٤.
(¬٤) الهداية ١/ ١٧٥، تبيين الحقائق ٢/ ٥٤، الاختيار ١/ ١٦٢، مختصر الطحاوي ص ٦٩.
(¬٥) لسان العرب ١٢/ ١١٧ مادة حجم، القاموس المحيط ١/ ٥٩٧ مادة ح ج م، مختار الصحاح ص ٥٣ مادة ح ج م، المصباح المنير ١/ ١٢٣ مادة حجمه.','3','327',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1003,'وإن قص الكل في أربعة مجالس، لزمه أربعة دماءٍ. وإن قص أقل من خمسةٍ مجتمعةٍ، أو خمسةٍ متفرقة، لزمه لكل ظفر صدقة.
<hr><span data-type="title">منحة السلوك</span>

من محظورات الإحرام، وقد ارتكبه فيجب عليه الدم، وأما إذا قص ربع أظافره في مجلس، فكذلك يجب دم؛ لأن الربع يقوم مقام الكل (¬١).
قوله: وإن قص الكل. أي: إن قص جميع أظفاره في أربعة مجالس، لزمه أربعة دماء؛ لاختلاف المجلس، فصار كاللبس المتفرق، والتطيب المتفرق (¬٢).
قوله: وإن قص أقل من خمسة مجتمعة، أو خمسة متفرقة، لزمه لكل ظفر صدقة (¬٣).
أما إذا قص أقل من خمسة مجتمعة، فلأنه لم يحصل له الارتفاق الكامل، ولا الزينة فلا يجب الدم (¬٤).
وقال محمد: تجب عليه بحساب ذلك من الدم (¬٥).
وقال زفر (¬٦)، والشافعي (¬٧): إن قص ثلاثة فعليه دم.
_________
(¬١) شرح فتح القدير ٣/ ٣٩، تبيين الحقائق ٢/ ٥٦، بداية المبتدي ١/ ١٧٦، الهداية ١/ ١٧٦.
(¬٢) الهداية ١/ ١٧٦، تبيين الحقائق ٢/ ٥٥، مختصر الطحاوي ص ٦٩.
(¬٣) كنز الدقائق ٢/ ٥٥، مختصر الطحاوي ص ٦٩، تبيين الحقائق ٢/ ٥٥، الهداية ١/ ١٧٦.
(¬٤) بدائع الصنائع ٢/ ١٩٤، الهداية ١/ ١٧٦، تبيين الحقائق ٢/ ٥٦.
(¬٥) فلو قلم خمسًا متفرقة، من يديه ورجليه، فعليه دم؛ اعتبارًا بما لو قصها من كف واحد، وبما إذا حلق ربع رأسه من مواضع متفرقة.
بدائع الصنائع ٢/ ١٩٤، بداية المبتدي ١/ ١٧٦، الهداية ١/ ١٧٦، تبيين الحقائق ٢/ ٥٦.
(¬٦) بدائع الصنائع ٢/ ١٩٤، تبيين الحقائق ٢/ ٥٦.
(¬٧) وأحمد.
شرح المحلي ٢/ ١٣٤، المنهاج ١/ ٦١٤، الإقناع للحجاوي ٢/ ٤٢٢، السلسبيل ١/ ٣٦٨.','3','328',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1004,'وإن تطيب، أو لبس، أو حلق بعذرٍ، يخير بين دمٍ، أو ثلاثة أصوع من بُرٍّ، يطعمها لستة مساكين، أو صوم ثلاثة أيام.
<hr><span data-type="title">منحة السلوك</span>

وأما إذا قص أقل من خمسة متفرقة من يديه، ورجليه، فكذلك صدقة عندهما (¬١). وقال محمد: دم (¬٢).
ولا شيء بأخذ ظفر منكسر (¬٣).

<span data-type="title" id=toc-501>[فعل المحظور بعذر]</span>
قوله: وإن تطيب، أو لبس، أو حلق بعذر، يخير بين دم، أو ثلاثة أصوع من بر، يطعمها لستة مساكين، أو صوم ثلاثة أيام (¬٤).
لما روي عن كعب بن عجرة -رضي الله عنه- أنه قال: "كان بي أذى من رأسي، فحملت إلى رسول الله -صلى الله عليه وسلم- والقمل يتناثر على وجهي، فقال: ما كنت أرى الجهد بلغ منك ما أرى، أتجد شاة؟ قلت: لا، فنزلت الآية {فَفِدْيَةٌ مِنْ صِيَامٍ أَوْ صَدَقَةٍ أَوْ نُسُكٍ} [البقرة: ١٩٦] قال: هو صوم ثلاثة أيام، أو إطعام ستة مساكين، نصف صاع لكل مسكين" متفق عليه (¬٥). وفسر -صلى الله عليه وسلم- النسك بالشاة
_________
(¬١) بدائع الصنائع ٢/ ١٩٤، الاختيار ١/ ١٦٣، الهداية ١/ ١٧٦.
(¬٢) لأنه لا ينمو بعد الانكسار، فأشبه اليابس من شجر الحرم، وحشيشه.
بداية المبتدي ١/ ١٧٦، الكتاب ١/ ٢٠٤، الهداية ١/ ١٧٦، بدائع الصنائع ٢/ ١٩٤، تبيين الحقائق ٢/ ٥٦.
(¬٣) بداية المبتدي ١/ ١٧٦، كنز الدقائق ٢/ ٥٦، بدائع الصنائع ٢/ ١٩٤، الهداية ١/ ١٧٦، تبيين الحقائق ٢/ ٥٦.
(¬٤) وكذا عند المالكية، والشافعية، إلا أن مقدار الإطعام مد لكل مسكين. وكذا عند الحنابلة، إلا أن مقدار الإطعام مد بر، أو نصف صاع من غيره.
الكتاب ١/ ٢٠٥، الاختيار ١/ ١٦٤، منح الجليل ٢/ ٣٢٨، التلقين ص ٦٦، القوانين ص ٩٣، حاشية البيجوري ١/ ٣٤٥، شرح ابن قاسم الغزي ١/ ٣٤٥، حاشية العنقري ١/ ٤٨٦، المقنع ١/ ٤٢٠.
(¬٥) البخاري ٢/ ٦٤٥ كتاب الحج، باب الإطعام في الفدية نصف صاع رقم ١٧٢١، ومسلم =','3','329',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1005,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

فيما رواه أبو داود (¬١).
وكلمة "أو" للتخيير، والصوم يجزئه في أي مكان شاء (¬٢)، وكذا الصدقة عندنا (¬٣). وأما النسك فيختص بالحرم بالاتفاق (¬٤).
_________
= ٢/ ٨٥٩ كتاب الحج، باب جواز حلق الرأس للمحرم إذا كان به أذى، ووجوب الفدية لحلقه، وبيان قدرها رقم ١٢٠١.
(¬١) ٢/ ١٧٢ كتاب المناسك، باب في الفدية رقم ١٨٥٦، عن كعب بن عجرة -رضي الله عنه- أن رسول الله -صلى الله عليه وسلم- مر به زمن الحديبية فقال: "قد آذاك هوام رأسك؟ قال: نعم، فقال النبي -صلى الله عليه وسلم-: احلق، ثم اذبح شاة نسكًا، أو صم ثلاثة أيام، أو أطعم ثلاثة آصع من تمر، على ستة مساكين".
ورواه أيضًا البخاري ٢/ ٦٤٤ كتاب الحج، باب قول الله تعالى: {فَمَنْ كَانَ مِنْكُمْ مَرِيضًا أَوْ بِهِ أَذًى مِنْ رَأْسِهِ فَفِدْيَةٌ مِنْ صِيَامٍ أَوْ صَدَقَةٍ أَوْ نُسُكٍ} رقم الحديث ١٧١٩، ومسلم ٢/ ٨٦٠ كتاب الحج، باب جواز حلق الرأس للمحرم إذا كان به أذى، ووجوب الفدية لحلقه، وبيان قدرها.
(¬٢) وفاقًا للثلاثة.
الهداية ١/ ١٧٧، تبيين الحقائق ٢/ ٥٦، المعونة ١/ ٥٣٢، الكافي لابن عبد البر ص ١٥٤، التفريع ١/ ٣٢٦، متن أبي شجاع ص ١١٤، الإقناع في حل ألفاظ أبي شجاع ١/ ٢٤٧، زاد المستقنع ص ١٩٩، المبدع ٣/ ١٩٠.
(¬٣) وعند مالك.
وذهب الشافعية، والحنابلة: إلى أن الصدقة تختص بالحرم.
الهداية ١/ ١٧٧، تبيين الحقائق ٢/ ٥٦، المعونة ١/ ٥٣٢، التلقين ص ٦٦، أسنى المطالب ١/ ٥٣١، مغني المحتاج ١/ ٥٣٠، المحرر ١/ ٢٥٠، المقنع شرح الخرقي ٢/ ٦٥٥.
(¬٤) كما عند الشافعية، والحنابلة. أما المالكية: فإن الفدية عندهم لا تختص بزمان، أو مكان.
شرح فتح القدير ٣/ ٤١، الكتاب ١/ ١٢٤، المعونة ١/ ٥٣٢، شرح الزرقاني على مختصر خليل ٢/ ٣٠٥، التلقين ص ٦٦، أسنى المطالب ١/ ٥٣١، مغني المحتاج ١/ ٥٣٠، المحرر ١/ ٢٥٠، الروض المربع ص ١٩٩، المقنع شرح الخرقي ٢/ ٦٥٥.','3','330',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1006,'وإن قبَّل، أو لمس بشهوةٍ، لزمه دمٌ. وإن جامع قبل الوقوف بعرفَةَ، فسد حجّهُ، وعليه شاة،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-502>[الجماع ومقدماته]</span>
قوله: وإن قبَّل، أو لمس بشهوة، لزمه دم.
لأن فيه الاستمتاع بالنساء وهو منهي عنه، فإذا أقدم عليه، فقد ارتكب المحرم فيجب دم (¬١).
قوله: وإن جامع قبل الوقوف بعرفة فسد حجه بالإجماع (¬٢)، وعليه شاة عندنا (¬٣).
وعند الشافعي: بدنة (¬٤)؛ اعتبارًا بما لو جامع بعد الوقوف.
ولنا: أن الجناية قبل الوقوف أكمل؛ لوجودها في مطلق الإحرام،
_________
(¬١) وهو بدنة عند الشافعية.
وذهب المالكية إلى أن قبلته إن كانت بدون مني ومذي، ففيها هدي، إذا كانت على الفم لغير وداع، أو رحمة. وإن كانت على غير الفم، لا شيء فيها.
وعند الحنابلة إن أنزل بقبلة، أو لمس لشهوة، عليه بدنة، وإن لم ينزل فشاة.
شرح فتح القدير ٣/ ٤٢، الهداية ١/ ١٧٧، الكتاب ١/ ٢٠٦، الاختيار ١/ ١٦٥، مواهب الجليل ٣/ ١٦٧، الكافي لابن عبد البر ص ١٥٨، جواهر الإكليل ١/ ١٩٢، الشرح الصغير ١/ ٢٩٢، روض الطالب ١/ ٥١٣، أسنى المطالب ١/ ٥١٣، مغني المحتاج ١/ ٥٢٢، الروض المربع ص ١٩٦، المبدع ٣/ ١٦١.
(¬٢) مراتب الإجماع لابن حزم ص ٤٢، الإجماع لابن المنذر ص ٥٦.
(¬٣) الاختيار ١/ ١٦٤، الكتاب ١/ ٢٠٦، بداية المبتدي ١/ ١٧٧، الهداية ١/ ١٧٧، فتاوى الذخيرة جـ ١ لوحة ٢٠٨/ ب النسخة الأصلية لدى مكتبة الأزهر، تحت رقم ١٥٨٤ فقه حنفي.
(¬٤) وكذا عند مالك، وأحمد.
التلقين ص ٧٠، منح الجليل ٢/ ٣٣٢، أقرب المسالك ص ٥٢، حاشية البناني ٢/ ٣٠٧، هداية الغلام ص ٧٢، التذكرة ص ٨٤، الروض المربع ص ١٩٦، المبدع ٣/ ١٦١.','3','331',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1007,'ويتمه، ويقضيه،
<hr><span data-type="title">منحة السلوك</span>

فيكون جزاؤه أغلظ (¬١). وروي أن رجلًا جامع امرأته وهما محرمان، فسأل رسول الله -صلى الله عليه وسلم- فقال لهما: "اقضيا نسككما، واهديا هديًا" الحديث رواه البيهقي (¬٢). والهدي يتناول الشاة.
قوله: ويتمه. أي: يتم ذلك الحج الفاسد، ويقضيه من عام قابل (¬٣).
لما روي عن عمر، وعلي، وابن مسعود -رضي الله عنهم-، أنهم قالوا: "يريقان دمًا، ويمضيان في حجهما، وعليهما الحج من قابل" (¬٤).
_________
(¬١) تبيين الحقائق ٢/ ٥٧، الهداية ١/ ١٧٧، شرح فتح القدير ٣/ ٤٢.
(¬٢) في السنن الكبرى ٥/ ١٦٧ كتاب الحج، باب ما يفسد الحج.
عن يزيد بن نعيم، أن رجلًا من جذام، جامع امرأته وهما محرمان، فسأل الرجل رسول الله -صلى الله عليه وسلم- فقال لهما: "اقضيا نسككما، واهديا هديًا، ثم ارجعا حتى إذا جئتما المكان الذي أصبتما فيه ما أصبتما فتفرقا، ولا يرى واحد منكما صاحبه، وعليكما حجة أخرى، فتقبلان حتى إذا كنتما بالمكان الذي أصبتما فيه ما أصبتما فأحرما، وأتما نسككما، واهديا".
قال البيهقي في السنن الكبرى ٥/ ١٦٧: هذا منقطع.
ونقل في نصب الراية ٣/ ١٢٥ عن ابن القطان قوله: هذا حديث لا يصح.
(¬٣) المبسوط ٤/ ١١٨، الاختيار ١/ ١٦٤، الهداية ١/ ١٧٧، فتاوى الذخيرة جـ ١ لوحة ٢٠٨/ ب.
(¬٤) أخرجه مالك في الموطأ ١/ ٣٨٠ كتاب الحج، باب هدي المحرم إذا أصاب أهله رقم ١٥١، عن عمر، وعلي، وأبي هريرة بلاغًا، وأسنده البيهقي ٥/ ١٦٧ كتاب الحج، باب ما يفسد الحج، من طريق مالك، ورواه من طريق عطاء، عن عمر. وهو منقطع عطاء لم يسمع من عمر.
قال في التلخيص الحبير ٢/ ٢٨٢: وفيه إرسال. وابن أبي شيبة ٢/ ٣٦٠ كتاب الحج، باب في الرجل يقع على امرأته قبل أن يزور البيت، والدارقطني ٣/ ٥٠، والبغوي في شرح السنة ٧/ ٢٨١ رقم ١٩٩٦. قال في التلخيص ٢/ ٢٨٣: رواه سعيد بن منصور، من طريق مجاهد، عن عمر، وهو منقطع، وأخرجه ابن أبي شيبة أيضًا عن علي، وهو منقطع أيضًا بين الحكم وبينه. =','3','332',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1008,'ولا يفارق امرأته في القضاء. وإن جامع بعد الوقوف، لم يفسد حجُّهُ،
<hr><span data-type="title">منحة السلوك</span>

قوله: ولا يفارق امرأته في القضاء.
لأن الافتراق ليس بنسك في الأداء فكذا في القضاء؛ لأن القضاء يحكي الأداء (¬١).
وقال زفر، ومالك، والشافعي: يفترقان فيه. فعند مالك: عند الخروج من المنزل (¬٢).
وعند الشافعي: عند المكان الذي جامعها فيه (¬٣).
وعند زفر: عند الإحرام (¬٤).
قوله: وإن جامع بعد الوقوف، لم يفسد حجه (¬٥).
_________
= وقال في الدراية ٢/ ٤١: وعن علي الأزدي، سألت ابن عمر عن رجل وامرأة من عمان، أقبلا حاجين، فقضيا المناسك حتى لم يبق عليهما إلا الإفاضة وقع عليها، فقال: "ليحجا عامًا قابلًا" أخرجه سعيد بن منصور، وغيره، بإسناد صحيح.
(¬١) ولأن الجامع بينهما وهو النكاح قائم، فلا معنى للافتراق قبل الإحرام؛ لإباحة الوقاع ولو بعده؛ لأنهما يتذاكران ما لحقهما من المشقة العظيمة، بسبب لذة يسيرة، فيزدادان ندمًا، وتحرزًا، فلا معنى للافتراق، ولأنه لا يؤمر أن يفارقها في الفراش حالة الحيض، ولا حالة الصوم، مع توهم تذاكرهما ما كان بينهما حالة الطهر، والفطر.
الأصل ٢/ ٣٩٣، كنز الدقائق ٢/ ٥٨، تبيين الحقائق ٢/ ٥٨، المبسوط ٤/ ١١٩، العناية ٣/ ٤٦.
(¬٢) بل حين إحرامه بحجة القضاء.
الشرح الكبير للدردير ٢/ ٧٠، حاشية الدسوقي ٢/ ٧٠، جواهر الإكليل ١/ ١٩٣، الذخيرة ٣/ ٣٤٠.
(¬٣) وكذا عند الحنابلة.
رحمة الأمة ١/ ١٣٥، التنبيه ص ٧٣، المقنع ١/ ٤١٩، هداية الراغب ص ٢١٥.
(¬٤) المبسوط ٤/ ١١٩، العناية ٣/ ٤٦، تبيين الحقائق ٢/ ٥٨.
(¬٥) وكذا عند المالكية وعليه هدي. =','3','333',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1009,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

خلافًا للشافعي (¬١)؛ لقوله -صلى الله عليه وسلم-: "من أدرك معنا هذه الصلاة، وأتى عرفات قبل ذلك ليلًا، أو نهارًا، فقد تم حجه وقضى تفثه" رواه أبو داود، والنسائي، وابن ماجه، والترمذي. وقال: حديث حسن صحيح (¬٢).
فبعد التمام لا يلحقه الفساد (¬٣).
_________
= الاختيار ١/ ١٦٤، الكتاب ١/ ٢٠٦، جواهر الإكليل ١/ ١٩٢، الخرشي على خليل ٢/ ٣٥٩.
(¬١) وأحمد. حيث يريان: أنه يفسد حجه؛ لأنه وطء صادف إحرامًا صحيحًا لم يحصل فيه التحلل الأول، فأشبه ما قبل الوقوف. وعليه بدنة.
مغني المحتاج ١/ ٥٢٢، حاشية أبي العباس الرملي على أسنى المطالب ١/ ٥١١، الإقناع للحجاوي ٢/ ٤٤٣، الكافي لابن قدامة ١/ ٤٥٨.
(¬٢) أبو داود ١٩٥٠ كتاب المناسك، باب من لم يدرك عرفة ٣/ ٤٨٦، والنسائي ٢/ ٤٨ كتاب الحج، باب فيمن لم يدرك صلاة الصبح مع الإمام بالمزدلفة، وابن ماجه ٢/ ١٠٠٤ كتاب المناسك، باب من أتى عرفة قبل الفجر ليلة جمع ٣٠١٦، والترمذي ٣/ ٢٣٨ كتاب الحج، باب ما جاء فيمن أدرك الإمام بجمع فقد أدرك الحج رقم ٨٩١، والدارمي ٢/ ٥٩، والطحاوي في شرح المعاني ١/ ٤٠٨ كتاب مناسك الحج، باب حكم الوقوف بالمزدلفة ٢/ ٢٠٧، وابن الجارود في المنتقى ص ١٦٥ باب المناسك رقم ٤٦٧، وابن حبان ١٠١٠ كما في موارد الظمآن ص ٢٤٩ كتاب الحج، باب ما جاء في الوقوف بعرفة والمزدلفة، والدارقطني ٢/ ٢٣٩ كتاب الحج، باب المواقيت رقم ١٧، والحاكم ١/ ٤٦٣ كتاب المناسك، والبيهقي في السنن ٥/ ١١٦ كتاب الحج، باب وقت الوقوف لإدراك الحج، والطيالسي ١٠٥٧ "منحة المعبود" ١/ ٢٢٠ كتاب الحج والعمرة، باب وجوب الوقوف بعرفة، وفضله، والدعاء عند ذلك، وأحمد ٤/ ١٥، والحميدي ٩٠٠ عن عروة بن مضرس.
قال الترمذي ٣/ ٢٣٨: حسن صحيح.
وقال الحاكم: صحيح على شرط كافة أئمة الحديث، وهي قاعدة من قواعد الإسلام.
(¬٣) الاختيار ١/ ١٦٤، الكتاب ١/ ٢٠٦، تبيين الحقائق ٢/ ٥٨، حاشية الشلبي ٢/ ٥٨.','3','334',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1010,'وعليه بدنةٌ. وإن جامع بعد الحلق، فعليه شاةٌ. وجماع الناسي، والعامد سواء.
ومن طاف للقدوم، أو للصدر محدثًا، فعليه صدقةٌ.
<hr><span data-type="title">منحة السلوك</span>

قوله: وعليه بدنة؛ لأنه لا قضاء عليه، فتغلظ الجناية، فتجب البدنة (¬١).
قوله: وإن جامع بعد الحلق، فعليه شاة؛ لخفة الجناية لوجود الحل في حق غير النساء (¬٢).
قوله: وجماع الناسي، والعامد سواء (¬٣).
لوجود المذكر، بخلاف الصوم. وكذا جماع الطائع والمكره (¬٤).

<span data-type="title" id=toc-503>[الطواف محدثًا]</span>
قوله: ومن طاف طواف القدوم، أو الصدر محدثًا، فعليه صدقة؛ لأنه دخله نقص بترك الطهارة، فيجبر بالصدقة (¬٥).
_________
(¬١) الهداية ١/ ١٧٧، تبيين الحقائق ٢/ ٥٨، المختار ١/ ١٦٤، الكتاب ١/ ٢٠٦، الاختيار ١/ ١٦٤.
(¬٢) قال المصنف في البناية ٣/ ٦٩٩: أي: في حق إفساد الحج والإحرام، لا في حق الإثم. الاختيار ١/ ١٦٤، الكتاب ١/ ٢٠٦، الهداية ١/ ١٧٨، تبيين الحقائق ٢/ ٥٨.
(¬٣) المبسوط ٤/ ١٢١، بداية المبتدي ١/ ١٧٨.
(¬٤) لاستوائهما في الارتفاق وهو الموجب. وكذا جماع النائمة، والمكرهة، مفسد لما ذكرنا. وفاقًا للمالكية، والحنابلة.
وذهب الشافعية: إلى أن جماع الجاهل، والناسي، والمكره لا يفسد الحج.
المبسوط ٤/ ١٢١، العناية ٣/ ٤٨، منح الجليل ٢/ ٣٢٩، الكافي لابن عبد البر ص ١٥٨، حاشية ابن قاسم العبادي ٤/ ١٧٧، الإقناع في حل ألفاظ أبي شجاع ١/ ٢٤٠، كشاف القناع ٢/ ٤٤٣، هداية الراغب ص ٢١٥.
(¬٥) وكذا الحكم لكل طواف هو تطوع، ولا يجب فيه دم؛ لأنه لو وجب لكان مثل طواف الزيارة؛ وهو دونه، فيجب فيه دون ما يجب في طواف الزيارة؛ إظهارًا للتفاوت بينهما.
وذهب المالكية، والشافعية، والحنابلة في أصح الروايتين عندهم؛ إلى أن الطهارة شرط =','3','335',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1011,'وإن طاف جنبًا، فعليه شاةٌ. ومن طاف للزيارة محدثًا، فعليه شاة. وإن طاف جنبًا، فعليه بدنة.
ومن ترك من طواف الزيارة ثلاثة أشواط فما دونها،
<hr><span data-type="title">منحة السلوك</span>

قوله: وإن طاف جنبًا. أي: وإن طاف طواف القدوم، أو الصدر جنبًا، فعليه شاة؛ لأنه نقص كبير (¬١).
قوله: ومن طاف للزيارة محدثًا، فعليه شاة؛ لأن النقص الحاصل بالحدث يسير فوجب جبره بالشاة فصار كترك شوط منه (¬٢).
قوله: وإن طاف جنبًا. أي: إن طاف طواف الزيارة جنبًا فعليه بدنة (¬٣).
كذا روي عن ابن عباس (¬٤)؛ ولأن الجناية أغلظ (¬٥).

<span data-type="title" id=toc-504>[نقص أشواط الطواف]</span>
قوله: ومن ترك من طواف الزيارة ثلاثة أشواط، فما دونها، مثل
_________
= في الطواف، فلو طاف على غير طهارة لم يصح طوافه.
والرواية الأخرى عند الحنابلة: يجزئه، ويجبر بدم، وكذلك إن كان جنبًا.
كنز الدقائق ٢/ ٥٩، بداية المبتدي ١/ ١٧٨، الهداية ١/ ١٧٨، تبيين الحقائق ٢/ ٥٩، مواهب الجليل ٣/ ٦٨، التاج والإكليل ٣/ ٦٨، قليوبي ٢/ ١٠٣، عميرة ٢/ ١٠٣، المستوعب ٤/ ٢١٦، الإنصاف ٤/ ١٦، الإفصاح ١/ ٢٧٧.
(¬١) وهو دون طواف الزيارة، فيجب فيه دون ما يجب في طواف الزيارة.
الهداية ١/ ١٧٩، تبيين الحقائق ٢/ ٥٩، كنز الدقائق ٢/ ٦٠، بداية المبتدي ١/ ١٧٩.
(¬٢) تبيين الحقائق ٢/ ٥٩، الهداية ١/ ١٨٠، كنز الدقائق ٢/ ٥٨، بداية المبتدي ١/ ١٧٩.
(¬٣) كنز الدقائق ٢/ ٥٨، بداية المبتدي ١/ ١٧٩، الهداية ١/ ١٧٩.
(¬٤) قال الزيلعي في نصب الراية ٣/ ١٢٨: غريب.
وقال ابن حجر في الدراية ٢/ ٤١: لم أجده.
(¬٥) أي: من الحدث، فيجب جبر نقصانها بالبدنة؛ إظهارًا للتفاوت بينهما. وكذا إذا طاف أكثره جنبًا، أو محدثًا؛ لأن للأكثر حكم الكل.
العناية ٣/ ٥٢، تبيين الحقائق ٢/ ٥٩.','3','336',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1012,'فعليه شاةٌ. وإن ترك أربعةً، فهو محرمٌ أبدًا حتى يطوفها، ومن ترك من طواف الصَّدر ثلاثة أشواطٍ، فعليه صدقةٌ. وإن ترك أربعةً فعليه دمٌ.
<hr><span data-type="title">منحة السلوك</span>

شوطين، أو شوط، فعليه شاة؛ لأن النقصان يسير، فيجبر بالدم (¬١).
قوله: وإن ترك أربعة. أي: وإن ترك أربعة أشواط من طواف الزيارة، فهو محرم أبدًا في حق النساء حتى يطوفه؛ لأن للأكثر حكم الكل، فصار كأنه لم يطف (¬٢).
قوله: ومن ترك من طواف الصدر ثلاثة أشواط، فعليه صدقة.
وهي: نصف صاع من بر لكل شوط، ولا يجب فيه دم. بخلاف طواف الزيارة (¬٣).
قوله: وإن ترك أربعة. أي: أربعة أشواط من طواف الصدر فعليه دم؛ لأن طواف الصدر واجب، فتركه يوجب الدم. فكذا أكثره (¬٤).
_________
(¬١) وذهب المالكية، والشافعية، والحنابلة: إلى أنه لو اقتصر في الطواف على دون سبعة أشواط، لم يصح طوافه، وعليه إعادته.
الكتاب ١/ ٢٠٨، كنز الدقائق ٢/ ٥٩، الهداية ١/ ١٨٠، تبيين الحقائق ٢/ ٥٩، الشرح الصغير ١/ ٢٧٥، القوانين ص ٨٩، الوجيز ١/ ١١٨، مغني المحتاج ١/ ٤٨٦، غاية المنتهى ٢/ ٤٠١، الإقناع للحجاوي ٢/ ٤٨٥.
(¬٢) العناية ٣/ ٥٢، الهداية ١/ ١٨٠، الاختيار ١/ ١٥٤، الكتاب ١/ ٢٠٨.
(¬٣) وطواف العمرة، حيث يجب فيها الدم بترك الأقل؛ لأنهما فرض. ولهذا لو تركهما لا ينجبران بالدم. وطواف الصدر ينجبر به؛ لأنه واجب، فتجب الصدقة بترك أقله؛ إظهارًا للتفاوت بينهما، وفرقًا بين ترك الكل والأقل.
العناية ٣/ ٥٦، تبيين الحقائق ٢/ ٦٠، الكتاب ١/ ٢٠٩، بداية المبتدي ١/ ١٨٠.
(¬٤) لأن للأكثر حكم الكل.
الهداية ١/ ١٨٠، تبيين الحقائق ٢/ ٦٠، الكتاب ١/ ٢٠٩، بداية المبتدي ١/ ١٨٠.','3','337',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1013,'ومن ترك السعي، أو أفاض من عرفة قبل الإمام، أو ترك الوقوف بمزدلفة، أو رمي كل الجمار، أو رمي وظيفة يومٍ، أو أكثرها، لزمه دمٌ،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-505>[ترك الواجبات]</span>
قوله: ومن ترك السعي، أي: السعي بين الصفا والمروة (¬١)، أو أفاض من عرفة قبل الإمام (¬٢)، أو ترك الوقوف بمزدلفة (¬٣)، أو ترك رمي كل الجمار (¬٤) أو ترك رمي وظيفة يوم، أو ترك أكثرها، بأن ترك الجمرة الأولى والثانية، أو الثانية والثالثة، أو الأولى والثالثة، لزمه دم؛ لأن في كل ذلك ترك الواجب، فيجبر بالدم (¬٥).
_________
(¬١) ذهب المالكية، والشافعية، والحنابلة: إلى أن السعي بين الصفا والمروة، من أركان الحج، وتركه لا تجبره الفدية.
شرح الزرقاني على خليل ٢/ ٢٦٦، بلغة السالك ١/ ٢٧٣، التذكرة ص ٨٠، متن أبي شجاع ص ١٠٣، زاد المستقنع ص ٢١٤، المحرر ١/ ٢٤٣، شرح الزركشي ٣/ ٢٤٤.
(¬٢) وعند المالكية، والشافعية، والحنابلة: من أفاض من عرفة قبل الإمام فلا شيء عليه، فهو من السنن.
التفريع ١/ ٣٤١، القوانين ص ٩٠، المجموع ٨/ ١٣٢، الحاوي الكبير ٤/ ١٧٤، شرح الزركشي ٣/ ٢٤٤، كشاف القناع ٢/ ٤٩٦.
(¬٣) وفاقًا للثلاثة.
الكافي لابن عبد البر ص ١٤٣، مختصر خليل ص ٨٣، نهاية المحتاج ٣/ ٣٠١، مغني المحتاج ١/ ٤٩٨، الروض المربع ص ٢١٤، المقنع ١/ ٤٦٩.
(¬٤) وفاقًا للثلاثة.
جواهر الإكليل ١/ ١٨١، شرح الزرقاني على خليل ٢/ ٢٧٩، أسنى المطالب ١/ ٤٩٧، مغني المحتاج ١/ ٥٠٩، الروض المربع ص ٢١٤، المبدع ٣/ ٢٦٤.
(¬٥) وعند المالكية، والشافعية، والحنابلة: من ترك رمي الجمار كلها في يوم من أيام الرمي، أو في جميعها، أو ترك أحد الجمار، فعليه دم.
الكتاب ١/ ٢٠٩، كنز الدقائق ٢/ ٦١، الهداية ١/ ١٨٠، العناية ٣/ ٥٩، التفريع ١/ ٣٤٥، حدود ابن عرفة ١/ ١٨٦، روض الطالب ١/ ٤٩٧، أنوار المسالك شرح عمدة السالك وعدة الناسك ص ٢٦٥، الروض المربع ص ٢١٤، المبدع ٣/ ٢٦٤، كشاف القناع ١/ ٥١٠.','3','338',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1014,'فإن كان أقل لزمه صدقة.
ومن أخَّر الحلق، أو طواف الزيارة عن وقته، لزمه دم،
<hr><span data-type="title">منحة السلوك</span>

قوله: فإن كان أقل. أي: فإن كان تركه من الرمي أقل من وظيفة يوم، بأن ترك الجمرة الأولى، أو الوسطى، أو الأخيرة، لزمه صدقة (¬١) لكل حصاة. نصف صاع من بر، أو صاع من تمر، أو شعير (¬٢).

<span data-type="title" id=toc-506>[تأخير مناسك الحج]</span>
قوله: ومن أخر الحلق، أو طواف الزيارة عن وقته، وهو أيام النحر لزمه دم هذا عند أبي حنيفة (¬٣).
وقالا: لا شيء عليه فيهما (¬٤).
وعلى هذا الخلاف في تأخير الرمي، وفي تقديم نسك على نسك، كالحلق قبل الرمي، ونحر القارن قبل الرمي، والحلق قبل الذبح (¬٥).
_________
(¬١) لقصور الجناية.
بدائع الصنائع ٢/ ١٣٩، بداية المبتدي ١/ ١٨١، تحفة الفقهاء ١/ ٤٢٠.
(¬٢) بداية المبتدي ١/ ١٨١، تحفة الفقهاء ١/ ٤٢٠، الهداية ١/ ١٨١، بدائع الصنائع ٢/ ١٣٩.
(¬٣) لأن التأخير عن المكان يوجب الدم فيما هو مؤقت بالمكان كالإحرام، فكذا التأخير عن الزمان فيما هو مؤقت بالزمان.
الكتاب ١/ ٢١٠، بداية المبتدي ١/ ١٨١، الهداية ١/ ١٨١، تبيين الحقائق ٢/ ٦٢.
(¬٤) لأن ما فات يستدرك بالقضاء، ولا يجب مع القضاء شيء آخر، وفاقًا للثلاثة. إلا أن الحلق، والطواف، لا آخر لوقتهما عند الشافعية، والحنابلة. وكذا الحلق عند المالكية.
أما الطواف عند المالكية: فإن آخره شهر ذي الحجة، فإن أخره لشهر محرم فعليه دم.
كنز الدقائق ٢/ ٦٢، تبيين الحقائق ٢/ ٦٢، الهداية ١/ ١٨١، بلغة السالك ١/ ٢٨٠، الشرح الصغير ١/ ٢٨٠، الذخيرة للقرافي ٣/ ٢٦٨، زاد المحتاج ١/ ٦٠١، المنهاج ١/ ٦٠١، منتهى الإرادات ٢/ ٦٤، هداية الراغب ص ٢٢٨.
(¬٥) وعند الشافعية: الترتيب بين هذه الأنساك سنة.
تبيين الحقائق ٢/ ٦٢، العناية ٣/ ٦١، شرح المحلي ٢/ ١١٩، قليوبي ٢/ ١١٩.','3','339',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1015,'وكذلك الحلق في وقته خارج الحرم.
<hr><span data-type="title">منحة السلوك</span>

قوله: وكذلك الحلق في وقته خارج الحرم.
المراد منه: أن يحلق في غير الحرم في أيام النحر. وأما إذا خرج أيام النحر، فحلق في غير الحرم، فعليه دمان عند أبي حنيفة (¬١).
وقال محمد: دم واحد في الحج، والعمرة (¬٢).
وقال زفر: إن حلق للحج في أيام النحر فلا شيء عليه، وإن حلق بعده فعليه دم (¬٣).
_________
(¬١) تبيين الحقائق ٢/ ٦٢، العناية ٣/ ٦٣، المبسوط ٤/ ٧٠، بداية المبتدي ١/ ١٨٢.
(¬٢) المبسوط ٤/ ٧٠، العناية ٣/ ٦٣، الهداية ١/ ١٨٢.
(¬٣) المبسوط ٤/ ٧٠، شرح فتح القدير ٣/ ٦٣.','3','340',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1016,'<span data-type="title" id=toc-507>فصل</span>
محرمٌ قتل صيدًا، أو سبعًا غير صائِل، عمدًا، أو سهوًا،
<hr><span data-type="title">منحة السلوك</span>

فصل
هذا الفصل في بيان الجنايات على الصيد.

<span data-type="title" id=toc-508>[صيد المحرم]</span>
قوله: محرم قتل صيدًا.
الصيد: هو الحيوان الممتنع، المتوحش بأصل الخلقة. وهو بري إذا كان توالده، وتناسله في البر. وبحري إذا كان في الماء (¬١) (¬٢).
ويحرم الأول على المحرم دون الثاني (¬٣)؛ لقوله تعالى: {لَا تَقْتُلُوا الصَّيْدَ وَأَنْتُمْ حُرُمٌ} [المائدة: ٩٥]، وقوله تعالى: {أُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ} [المائدة: ٩٦].
قوله: أو سبعًا. أي: لو قتل سبعًا غير صائل. أي: جامل. قيد به: إذا قتله لصولته، أو جملته، لا يجب عليه شيء (¬٤). خلافًا لزفر (¬٥).
قوله: عمدًا، أو سهوًا.
_________
(¬١) فالمولد هو الأصل، والتعيش بعد ذلك عارض، فلا يتغير به.
تبيين الحقائق ٢/ ٦٣، الهداية ١/ ١٨٣، بدائع الصنائع ٢/ ١٩٦، تحفة الفقهاء ١/ ٤٢٢.
(¬٢) القاموس المحيط ٢/ ٨٧٣ مادة ص ي د، لسان العرب ٣/ ٢٦١ مادة صيد، مختار الصحاح ص ١٥٧ مادة ص ي د، التعريفات ص ١٤٧.
(¬٣) تبيين الحقائق ٢/ ٦٣، تحفة الفقهاء ١/ ٤٢١، الهداية ١/ ١٨٣، بدائع الصنائع ٢/ ١٩٦.
(¬٤) الأصل ٢/ ٣٧٨، شرح فتح القدير ٣/ ٦٨.
(¬٥) ومالك، والشافعي، وأحمد، حيث يرون: أنه إذا قتل المحرم السبع ابتداء، فلا ضمان عليه.
بدائع الصنائع ٢/ ١٩٧، حاشية الدسوقي ٢/ ٧٤، المعونة ١/ ٥٤٩، أنوار المسالك شرح عمدة السالك وعدة الناسك ص ٢٤٢، إخلاص الناوي ١/ ٣٤٧، الإفصاح ١/ ٢٩٣، كشاف القناع ٢/ ٤٣٩.','3','341',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1017,'أو عودًا، أو بدءًا، أو دل عليه من قتله.
<hr><span data-type="title">منحة السلوك</span>

أي: سواء قتله بطريق العمد والقصد، أو السهو، وسواء كان في ذلك عائدًا أو باديًا (¬١).
والمراد بالبادي: الذي قتل الصيد مرة (¬٢).
والعائد: الذي قتل مرة بعد مرة (¬٣). لأن الموجب للضمان لا يختلف باختلاف هذه الأحوال (¬٤).
قوله: أو دل عليه. أي: على الصيد من قتله.
بأن قال: إن في مكان كذا صيدًا، فقتله المدلول. يجب على الدال الجزاء سواء كان المدلول محرمًا، أو حلالًا. وذلك لارتكابه محظور إحرامه (¬٥).
_________
(¬١) وفاقًا للثلاثة. خلافًا لداود، وابن عباس، وأبي ثور، وابن المنذر.
الكتاب ١/ ٢١١، الاختيار ١/ ١٦٦، الكافي لابن عبد البر ص ١٥٥، القوانين الفقهية ص ٩٣، المعونة ١٠/ ٥٣٥، مغني المحتاج ١/ ٥٢٤، فتح الوهاب ٢/ ٥٢١، هداية الراغب ص ٢١٨، مختصر الخرقي ص ٦٣، شرح الزركشي ٣/ ٣٣٥، المحلى ٧/ ٣٢٣.
(¬٢) من بدا في الأمر، وعاد.
القاموس المحيط ١/ ٢٢٦ مادة ب د أ، لسان العرب ١/ ٢٦ مادة بدأ، مختار الصحاح ص ١٨ مادة ب د أ، المصباح المنير ١/ ٤٠ مادة بدا.
(¬٣) من عاد إليه يعود عودة وعودًا: إذا رجع.
لسان العرب ٣/ ٣١٥ مادة عود، القاموس المحيط ٣/ ٣٣٩ مادة ع ود، مختار الصحاح ص ١٩٣ مادة ع ود، المصباح المنير ٢/ ٤٣٦ مادة عاد.
(¬٤) بدائع الصنائع ٢/ ١٩٧، العناية ٣/ ٧١.
(¬٥) وإليه ذهب الحنابلة.
الهداية ١/ ١٨٣، العناية ٣/ ٦٨، تبيين الحقائق ٢/ ٦٣، الاختيار ١/ ١٦٥، تحفة الفقهاء ١/ ٤٢١، الكتاب ١/ ٧١١ التسهيل ص ٩٦، دليل الطالب ١/ ٢٤٨.','3','342',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1018,'فعليه قيمته بقول عدلين، ويُخيَّر فيها بين الهدي، والطعام، والصيام.
<hr><span data-type="title">منحة السلوك</span>

وقال الشافعي: لا شيء عليه (¬١).

<span data-type="title" id=toc-509>[جزاء قتل الصيد]</span>
قوله: فعليه. أي: فعلى المحرم المذكور قيمة الصيد الذي قتله (¬٢).
قوله: بقول عدلين.
حال من "قيمته". أي: عليه قيمة الصيد حال كونها مقومة بقول: "عدلين". وهو أن يقوماه في مقتله، أو أقرب موضع منه (¬٣)، ثم يخير (¬٤) فيها بين الهدي إن بلغت قيمته هديًا، والطعام يتصدق به على كل مسكين نصف صاع، من بر، أو صاعًا من تمر، أو شعير.
والصيام يصوم عن كل نصف صاع يومًا وهذا عندهما (¬٥).
وقال محمد (¬٦)، والشافعي (¬٧): يجب النظير فيما له نظير. ففي الظبي
_________
(¬١) وكذا قال: مالك.
مواهب الجليل ٣/ ١٧٦، القوانين ص ٩٢، مختصر المزني ص ١٦٨، الحاوي الكبير ٤/ ٣٠٦.
(¬٢) تحفة الفقهاء ١/ ٤٢٢، بداية المبتدي ١/ ١٨٣، الهداية ١/ ١٨٣، تبيين الحقائق ٢/ ٦٣.
(¬٣) إن كان في برية.
تبيين الحقائق ٢/ ٦٣.
(¬٤) أي: هو مخير في القيمة، إن شاء ابتاع بها هديًا وذبحه، إن بلغت قيمته هديًا.
تبيين الحقائق ٢/ ٦٣.
(¬٥) أي: عند أبي حنيفة، وأبي يوسف.
الاختيار ١/ ١٦٦، بداية المبتدي ١/ ١٨٣، المختار ١/ ١٦٦، الهداية ١/ ١٨٤، تبيين الحقائق ٢/ ٦٣.
(¬٦) الهداية ١/ ١٨٣، الكتاب ١/ ٢١٣، الاختيار ١/ ١٦٦.
(¬٧) ومالك، وأحمد.
المعونة ١/ ٥٤٠، الشرح الصغير ١/ ٢٩٨، سراج السالك ١/ ٢٤٢، بلغة السالك =','3','343',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1019,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

شاة، وفي الضبع شاة، وفي الأرنب عناق، وفي اليربوع (¬١) جفرة (¬٢)، وفي النعامة (¬٣) بدنة، وفي حمار الوحش (¬٤)، وبقر الوحش (¬٥)، بقرة، وفيما لا نظير له، كالعصفور ونحوه، تجب القيمة (¬٦).
_________
= ١/ ٢٩٨، أسهل المسالك ص ١١٩، متن أبي شجاع ص ١١٣، التذكرة ص ٨٤، حلية العلماء ٣/ ٣١٦، الروض المربع ص ٢٠٠، المغني ٣/ ٥٤٥، منار السبيل ١/ ٢٥٤.
(¬١) ويسمى: الدرص، وهو حيوان صغير على هيئة الجرذ الصغير، وله ذنب طويل، ينتهي بخصلة من الشعر، وهو قصير اليدين، طويل الرجلين.
حياة الحيوان للدميري ٢/ ٤٣٥، المعجم الوسيط ١/ ٣٢٥ مادة اليربوع، المصباح المنير ١/ ٢١٧ مادة الربع.
(¬٢) الجفرة -بفتح الجيم-: هي ما بلغت أربعة أشهر من أولاد المعز، وفصلت عن أمها. والذكر جفر، سمي بذلك: لأنه جفر جنباه، أي: عظُما، والجمع: أجفار، وجفار.
حياة الحيوان للدميري ١/ ٢٧٩، لسان العرب ٤/ ١٤٢ مادة جفر، القاموس المحيط ١/ ٥٠٤ مادة ج ف ر، المصباح المنير ١/ ١٠٣ مادة الجفر.
(¬٣) النعامة: طائر كبير الجسم، طويل العنق، قصير الجناح، شديد العدو، وهو مركب من خلقة الطير والجمل، ويطلق على الذكر، والأنثى: نعامة.
حياة الحيوان للدميري ٢/ ٣٦٠، المعجم الوسيط ٢/ ٩٣٥ مادة النعامة، المصباح المنير ٢/ ٦١٤ مادة النعم، لسان العرب ١٢/ ٥٨٢ مادة نعم.
(¬٤) حمار الوحش: نوع من الصيد على خلقة الحمار الأهلي؛ لأنهما من فصيلة واحدة، نسبة إلى الوحش لتوحشها في الأمكنة الخالية المقفرة. جمعها: وحوش ووحشان. ويسمى الفراء.
توضيح الأحكام من بلوغ المرام ٣/ ٣٠١، حياة الحيوان للدميري ١/ ٣٦١.
(¬٥) بقر الوحش: أربعة أنواع: المها، والأيل، واليحمور، والثيتل وهي أشبه شيء بالمعز الأهلية. وقرونها صلاب جدًا، قيل: إنه في كل سنة تنبت على قرنه شعبة زائدة، وهي سريعة العدو.
حياة الحيوان للدميري ٢/ ٣٢٥، عجائب المخلوقات وغرائب الموجودات ص ٢٤٧.
(¬٦) الهداية ١/ ١٨٣، الكتاب ١/ ٢١٣، المختار ١/ ١٦٦، شرح الجامع الصغير لأحمد ابن =','3','344',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1020,'ولو عيَّب الصيد، ضمن نقصانه. ولو أزال امتناعه، ضمن كل القيمة
<hr><span data-type="title">منحة السلوك</span>

قوله: ولو عيَّب الصيد. بأن جرحه، أو قطع عضوه، أو نتف شعره، ضمن نقصانه؛ اعتبارًا للجزء بالكل في حقوق العباد (¬١). وكذلك لو اقتلع سنه أو ضرب عينه فابيضت (¬٢).
قوله: ولو أزال امتناعه، ضمن كل القيمة.
لأنه فوت عليه الأمن بنقص آلة الامتناع، فيغرم قيمته. وزوال الامتناع أعم من أن يكون بقطع القوائم (¬٣)، ونتف الريش (¬٤).
_________
= محمد البخاري العتابي لوحة ٢٩/ أالنسخة الأصلية لدى مكتبة أحمد الثالث بتركيا، تحت رقم ٧٢٩، متن أبي شجاع ص ١١٣، التذكرة ص ٨٤، حلية العلماء ٣/ ٣١٦.
(¬١) وهذا إذا بريء، وبقي أثره. وإن لم يبق له أثر لا يضمن؛ لزوال الموجب.
تبيين الحقائق ٢/ ٦٥، الهداية ١/ ١٨٥، شرح فتح القدير ٣/ ٨٠.
(¬٢) فنبت له سن، أو زال البياض. وإليه ذهب الشافعية، والحنابلة. إلا أنه عند الحنابلة إذا نتف ريشه، أو شعره، فعاد فلا شيء عليه.
وذهب المالكية: إلى أنه لا شيء في الصيد إذا جرح وسلم؛ لأن الضمان رتبه الله على القتل، وقد سماه كفارة، والكفارة لا تتبعض على أجزاء المكفر عنه. وقيل: عليه الجزاء بقدر النقص.
الاختيار ١/ ١٦٧، الكتاب ١/ ٢١٣، الهداية للمرغيناني ١/ ١٨٥، شرح فتح القدير ٣/ ٨٠، تبيين الحقائق ٢/ ٦٥، التفريع ١/ ٣٣٠، الكافي لابن عبد البر ص ١٥٧، الذخيرة ٣/ ٣١٧، التنبيه ص ٧٢، المهذب ١/ ٢١٦، شرح منتهى الإرادات ٢/ ٤٣، المقنع ١/ ٤٣٦، المغني ٣/ ٥٥٠.
(¬٣) قوائم الدابة: أربعها.
لسان العرب ١٢/ ٥٠١ مادة قوم، القاموس المحيط ٣/ ٧١٩ مادة ق وم، مختار الصحاح ص ٢٣٣ مادة ق وم، معجم مقاييس اللغة ٥/ ٤٣ باب القاف والميم وما يثلثهما مادة قوم.
(¬٤) وإليه ذهب الشافعية، والحنابلة: لأنه عطله فصار كالتالف، وكما لو أزمن عبدًا لزمه كل قيمته. =','3','345',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1021,'ولو كسر بيض صيدٍ، ضمنه، وضمن فرخه الميت إن خرج منه.
<hr><span data-type="title">منحة السلوك</span>

قوله: ولو كسر بيض صيد ضمنه. أي: ضمن قيمة البيض؛ لأنه أصل للصيد (¬١).
قوله: وضمن فرخه الميت إن خرج منه.
أي: من البيض؛ لأن البيض مُعدٌّ ليخرج منه فرخ، والتمسك بالأصل واجب، حتى يظهر خلافه. وكسر البيض قبل وقته سبب لموت الفرخ، والظاهر أنه مات به، فيجب (¬٢).
وكذا لو ضرب بطن ظبية فألقت جنينًا ميتًا، ثم ماتت، يجب عليه قيمتهما؛ لأن الضرب سبب صالح لموتهما (¬٣)، بخلاف من ضرب بطن امرأة،
_________
= وعند المالكية: لا شيء عليه؛ لأن الضمان مرتب على القتل، وقد سماه الله كفارة. والكفارة لا تتبعض على أجزاء المكفر عنه، وقيل: عليه الجزاء بقدر النقص.
بداية المبتدي ١/ ١٨٥، الكتاب ١/ ٢١٤، الهداية ١/ ١٨٥، شرح فتح القدير ٣/ ٨٠، التفريع ١/ ٣٣٠، الكافي لابن عبد البر ص ١٥٧، الذخيرة ٣/ ٣١٧، الخرشي على خليل ٢/ ٣٦٨، مغني المحتاج ١/ ٥٢٧، روضة الطالبين ٣/ ١٦١، المغني ٣/ ٥٥١، كشاف القناع ٢/ ٤٣٨.
(¬١) لأنه مُعدٌّ ليكون صيدًا فأُعطي له حكم الصيد في إيجاب الجزاء على المحرم.
الهداية ١/ ١٨٥، تبيين الحقائق ٢/ ٦٦، الاختيار ١/ ١٦٧، بداية المبتدي ١/ ١٨٥.
(¬٢) وإليه ذهب الشافعية، والحنابلة.
وعند المالكية: في الجنين، والبيض، عشر دية الأم، ولو تحرك الجنين، أو الفرخ عقب انفصاله حركة ضعيفة لا تدل على استقرار حياته. وفي الجنين، والبيض: جزاء الأم إن مات بعد أن استهل صارخًا عقب انفصاله عن أمه حية، أو عن بيضه.
بداية المبتدي ١/ ١٨٥، الأصل ٢/ ٣٧٠، الهداية ١/ ١٨٥، تبيين الحقائق ٢/ ٦٦، جواهر الإكليل ١/ ٢٠٠، منح الجليل ٢/ ٣٦٧، مختصر خليل ص ٩٠، أقرب المسالك ص ٥٤، مغني المحتاج ١/ ٥٢٤، نهاية المحتاج ٣/ ٣٤٤، الإنصاف ٤/ ٤٧٩، كشاف القناع ٢/ ٤٣٥.
(¬٣) شرح فتح القدير ٣/ ٨١، تبيين الحقائق ٢/ ٦٦، العناية ٣/ ٨١.','3','346',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1022,'ولا شيء في قتل الغراب المؤذي، والحدأة، والحيَّة، والعقرب،
<hr><span data-type="title">منحة السلوك</span>

فألقت جنينًا ميتًا، ثم ماتت، حيث يجب ضمان الأم، ولا يجب ضمان الولد غير الغرة (¬١) في الحرة (¬٢)، وفي الأمة يجب قيمة الأم، ونصف عشر قيمة الولد لو كان ذكرًا، وعشر قيمته لو كان أنثى؛ لأن الجنين جزء من وجه، ونفس من وجه. فجزاء الصيد مبني على الاحتياط، فرجحنا فيه جانب النفس، فأوجبنا فيه ضمانهما، بخلاف حقوق العباد (¬٣). فافهم.

<span data-type="title" id=toc-510>[قتل الفواسق والحشرات]</span>
قوله: ولا شيء عليه، أي: على المحرم في قتل الغراب المؤذي.
المراد منه: الغراب الأبقع (¬٤)، الذي يأكل الجيف، أو يخلط (¬٥). وأما العقعق (¬٦)،
_________
(¬١) الغِرة -بالكسر-: الغفلة. والغُرة -بالضم-: أول الشيء، وأكرمه، وأنفسه. والجمع: غرر، مثل غرفة وغرف. والغرر: ثلاث ليال من أول الشهر، والغرة: عبد، أو أمة. والغرة أكثر ما تطلق عليهما. والغرة من العبيد: الذي يكون ثمنه عشر الدية، والغرة في الجبهة بياض فوق الدرهم.
المصباح المنير ٢/ ٤٤٤ مادة الغرة، لسان العرب ٥/ ١٩ مادة غرر، القاموس المحيط ٣/ ٣٨١ مادة غ ر ر، المطلع ص ٣٦٤، التعريفات ص ١٧٦، مختار الصحاح ص ١٩٧ مادة غ ر ر.
(¬٢) العناية ٣/ ٨٢، تبيين الحقائق ٢/ ٦٦، شرح فتح القدير ٣/ ٨١، بدائع الصنائع ٢/ ٣٠٣.
(¬٣) العناية ٣/ ٨٢، تبيين الحقائق ٢/ ٦٦، بدائع الصنائع ٢/ ٢٠٣.
(¬٤) الغراب الأبقع: ما فيه سواد، وبياض. وقيل: ما في صدره بياض.
لسان العرب ٨/ ١٧ مادة بقع، القاموس المحيط ١/ ٣٠٢ مادة ب ق ع، الحيوان للدميري ٢/ ٢٣٧، المصباح المنير ١/ ٥٧ مادة البقعة.
(¬٥) وفاقًا للثلاثة.
الكتاب ١/ ٢١٤، كنز الدقائق ٢/ ٦٦، بلغة السالك ١/ ٢٩٤، أسهل المسالك ١/ ١١٩، الوجيز ١/ ١٢٧، حاشية الجمل ٢/ ٥٢١، شرح منتهى الإرادات ٢/ ٢٨، المستوعب ٤/ ١١١.
(¬٦) العقعق: طائر على قدر الحمامة، وهو على شكل الغراب، وجناحاه أكبر من جناحي =','3','347',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1023,'والفأرة، والكلب العقور، والذئب،
<hr><span data-type="title">منحة السلوك</span>

فلا يحل قتله للمحرم (¬١).
والأصل فيه: أنه -صلى الله عليه وسلم-: "أمر بقتل خمس فواسق في الحل والحرم، الغراب، والحدأة، والعقرب، والفأرة، والكلب العقور" متفق عليه (¬٢).
والمراد من الكلب العقور (¬٣): الذئب (¬٤).
فعلى هذا: الكلب غير العقور لا يحل قتله (¬٥).
_________
= الحمامة، وهو ذو لونين، أبيض وأسود، طويل الذنب.
الحيوان للدميري ٢/ ٢٠٢، المعجم الوسيط ٢/ ٦١٦.
(¬١) وإن قتله فعليه الجزاء؛ لأنه لا يسمى غرابًا عرفًا، ولا يبتديء بالأذى.
تبيين الحقائق ٢/ ٦٦، الهداية ١/ ١٨٦.
قال في كشاف القناع ٢/ ٤٣٩: وما يباح أكله من الغربان لا يباح قتله؛ لأنه من الصيد.
(¬٢) الغراب، والحدأة، والعقرب، والفأرة، والكلب العقور، وردت في صحيح البخاري ٢/ ٦٥٠ كتاب الحج، باب ما يقتل المحرم من الدواب رقم ١٧٣٢، وفي صحيح مسلم ٢/ ٨٥٧ كتاب الحج، باب ما يندب للمحرم وغيره قتله من الدواب في الحل والحرم رقم ٧١ (١١٩٨)، عن عائشة -رضي الله عنها- بلفظ: "خمس من الدواب كلها فواسق، تقتل في الحرم: الغراب، والحدأة، والكلب العقور، والعقرب، والفأرة".
وقتل الحية ورد في صحيح مسلم ٢/ ٨٥٦ كتاب الحج، باب ما يندب للمحرم، وغيره قتله من الدواب، في الحل والحرم برقم ٦٧ (١١٩٨) عن عائشة -رضي الله عنها- بلفظ: "خمس فواسق يقتلن في الحل والحرم: الحية، والغراب الأبقع، والفأرة، والكلب العقور، والحُدَيَّا".
(¬٣) العقور: كل سبع يعقر، أي: يجرح، ويقتل، من الأسد، والفهد، والذئب، والعقور. من أبنية المبالغة.
لسان العرب ٤/ ٥٩٤ مادة عقر، القاموس المحيط ٣/ ٢٧٢ مادة ع ق ر، المصباح المنير ٢/ ٤٢١ مادة عقره، مختار الصحاح ص ١٨٧ مادة ع ق ر.
(¬٤) الهداية ١/ ١٨٦، شرح فتح القدير ٣/ ٨٢.
(¬٥) وكذا عند المالكية.
العناية ٣/ ٨٣، شرح فتح القدير ٣/ ٨٣، حاشية الدسوقي ٢/ ٧٤، سراج السالك ١/ ٢٤٣.','3','348',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1024,'والنمل، والبراغيث، والقراد، والبق، والذباب.
<hr><span data-type="title">منحة السلوك</span>

وعن أبي حنيفة: أن الكلب العقور، وغير العقور، والمستأنس منه، والمستوحش، سواء (¬١).
وأما النمل، والبراغيث (¬٢)، والقراد (¬٣)، والبق (¬٤)، والذباب، فلأنها ليست بصيود، وإنما هن من الحشرات (¬٥). وكذلك السلحفا (¬٦)،
_________
(¬١) الهداية ١/ ١٨٦، العناية ٣/ ٨٣.
قال النووي في المجموع ٧/ ٣١٦: "وأما الكلب الذي ليس بعقور: فإن كان فيه منفعة مباحة، فقتله حرام بلا خلاف، وإن لم يكن فيه منفعة مباحة: فالأصح أنه يحرم قتله. وقيل: يكره، والأمر بقتل الكلاب منسوخ".
(¬٢) البراغيث: جمع برغوث، وهو طائر يبيض ويفرخ، بعد أن يتولد له أنياب يعض بها، وخرطوم يمص به.
حياة الحيوان للدميري ١/ ١٧٧، لسان العرب ٢/ ١١٦ مادة برغث، مختار الصحاح ص ٢٠ مادة ب ر غ ث، المعجم الوسيط ١/ ٥٠ مادة برغوث.
(¬٣) القراد: دويبة متطفلة، ذات أرجل كثيرة، تعيش على الدواب والطيور. واحد القردان. وهي منتشرة في أعطان الإبل، وغيرها.
حياة الحيوان للدميري ٢/ ٣٢٩، تاج العروس ٢/ ٤٦٤ مادة قرد، لسان العرب ٣/ ٣٤٨ مادة قرد، القاموس المحيط ٣/ ٥٨٥ مادة ق ر د.
(¬٤) البق: البعوض.
حياة الحيوان للدميري ١/ ٢٢٢، لسان العرب ١٠/ ٢٣ مادة بقق، القاموس المحيط ١/ ٣٠٣ مادة ب ق ق، مختار الصحاح ص ٢٤ مادة ب ق ق.
(¬٥) العناية ٣/ ٨٤، تبيين الحقائق ٢/ ٦٦، الكتاب ١/ ٢١٥، بداية المبتدي ١/ ١٨٦، الهداية ١/ ١٨٦.
(¬٦) هي حيوان برمائي معمَّر من قسم الزواحف، يحيط بجسمها صندوق عظمي، مغطى بحراشيف قرنية صغيرة.
لسان العرب ٩/ ١٦٢ مادة سلحف، المعجم الوسيط ١/ ٤٤٢ مادة السلحفاة، القاموس المحيط ٢/ ٥٩٢ مادة س ل ح ف، المصباح المنير ١/ ٢٨٤.','3','349',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1025,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

والخنفسا (¬١) (¬٢).
والمراد من النمل: السوداء، أو الصفراء التي تؤذي بالعض، وما لا يؤذي لا يحل قتلها؛ ولكن لا يضمن؛ لأنها ليست بصيد (¬٣).
وفي "المحيط": وليس في القنافذ (¬٤)، والوزغ، والزنبور (¬٥)، والحلمة (¬٦)،
_________
(¬١) هي دويبة سوداء، أصغر من الجعل، منتنة الريح.
لسان العرب ٦/ ٧٣ مادة خنفس، القاموس المحيط ٢/ ١٢١ مادة خ ن ف س، المغرب ص ١٤٩ مادة الخنفساء.
(¬٢) بداية المبتدي ١/ ١٨٦، تبيين الحقائق ٢/ ٦٦، الهداية ١/ ١٨٦.
(¬٣) ولا هي متولدة من البدن.
وعند المالكية: لا يحل قتل الدود، والنمل، والذباب، والذر، ونحوها. ولو قتلها فعليه التصدق بقبضة من طعام، ولو كثر جدًا.
وعند الشافعية: ما ليس مأكولًا لا جزاء فيه، ولو أكله مضطرًا ضمن.
وعند الحنابلة: لا يحرم قتل براغيث، وقراد، ونحوهما كنمل وبق وبعوض.
الهداية ١/ ١٨٦، العناية ٣/ ٨٤، تبيين الحقائق ٢/ ٦٦، الشرح الكبير للدردير ٢/ ٧٤، الشرح الصغير ١/ ٢٩٥، الوجيز ١/ ١٢٧، حاشية الجمل ٢/ ٥٢١، منتهى الإرادات ٢/ ٢٨، الإقناع للحجاوي ٢/ ٤٣٩.
(¬٤) القنفذ: دويبة من الثدييات، ذات شوك حاد. يلتف فيصير كالكرة، وبذلك يقي نفسه من خطر الاعتداء عليه، ويسمى: الشيهم، والدُّلدُل.
لسان العرب ٣/ ٥٠٥ مادة قنفذ و ١٢/ ٣٢٨ مادة شهم، المعجم الوسيط ٢/ ٧٦٣ مادة القنفذ، حياة الحيوان للدميري ٢/ ٣٦٠.
(¬٥) الزنبور: نوع من الذباب لساع. والزنبور من الفأر العظيم.
لسان العرب ٤/ ٣٣١ مادة زنبر، القاموس المحيط ٢/ ٤٧٨ مادة ز ن ب ر، المعجم الوسيط ١/ ٤٠٢ مادة الزنبار، حياة الحيوان الكبرى للدميري ٢/ ١٣.
(¬٦) الحلمة: القراد الضخم وقيل: الصغير. =','3','350',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1026,'ومن قتل قملةً، أو جرادةً، تصدق بكفٍّ من طعامٍ، أو بتمرةٍ.
<hr><span data-type="title">منحة السلوك</span>

وصياح الليل (¬١)، والصرصر (¬٢)، وأم حبين (¬٣)، وابن عرس (¬٤) شيء؛ لأنها من هوام الأرض، وليست بصيود (¬٥).
قوله: ومن قتل قملة، أو جرادة، تصدق بكف من طعام، أو بتمرة (¬٦).
لما روي: "أن أهل حمص أصابوا جرادًا كثيرًا في إحرامهم، فجعلوا يتصدقون مكان كل جرادة بدرهم، فقال عمر -رضي الله عنه-: أرى دراهمكم كثيرة يا
_________
= القاموس المحيط ١/ ٦٩٩ مادة ح ل م، لسان العرب ١٢/ ١٤٦ مادة حلم، مختار الصحاح ص ٦٤ مادة ح ل م، المصباح المنير ١/ ١٤٨ مادة حلم.
(¬١) صياح الليل: ويسمى الجُدجُد، وهو دويبة على خلقة الجندب، إلا أنها سُويداء صغيرة، وهو قفاز، وفيه شبه بالجراد. وقيل: هو الصرصر.
لسان العرب ٣/ ١١٤ مادة جدد، القاموس المحيط ١/ ٤٥٤ مادة ج د د.
(¬٢) الصرصر: طائر يصر بالليل، أصفر اللون. سمي بصوته. يقال: صر العصفور يصر: إذا صاح، وصر الجندب يصر صريرًا، وصر الباب يصر. وكل صوت شبه ذلك، فهو صرير.
لسان العرب ٤/ ٤٥٠ مادة صرر، المصباح المنير ١/ ٣٣٨ مادة الصر، القاموس المحيط ٢/ ٨١٣ مادة ص ر ر.
(¬٣) أم حبين: دويبة منتنة الريح، كالحرباء، عظيمة البطن إذا مشت تطأطيء رأسها كثيرًا، وترفعه؛ لعظم بطنها. وهي تشبه الضب.
لسان العرب ١٣/ ١٠٥ مادة حبن، المصباح المنير ١/ ١٢٠ مادة أم حبين، القاموس المحيط ١/ ٥٨٢ مادة ح ب ن.
(¬٤) ابن عرس: حيوان كالفأر، يفتك بالدجاج، وهو دون السنور، له ناب. والجمع: بنات عرس، ويسمى: ابن آوى كذلك.
حياة الحيوان للدميري ٢/ ٢٣٢، لسان العرب ٦/ ١٣٧ مادة عرس، المعجم الوسيط ٢/ ٥٩٢ مادة العِرس.
(¬٥) تبيين الحقائق ٢/ ٦٦.
(¬٦) الكتاب ١/ ٢١٥، كنز الدقائق ٢/ ٦٦، تبيين الحقائق ٢/ ٦٦.','3','351',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1027,'ويجب الجزاء بأكل الصيد مضطرًا.
<hr><span data-type="title">منحة السلوك</span>

أهل حمص، تمرة خير من جرادة" (¬١).
والتصدق بكف من الطعام في الجراد: فيما إذا قتل قملة، أو قملتين (¬٢)، وأما إذا قتل كثيرًا، أطعم نصف صاع من بر (¬٣).
قوله: ويجب الجزاء بأكل الصيد مضطرًا.
أي: في حالة الاضطرار؛ لأن الإذن مقيد بالكفارة بالنص (¬٤)، وهو قوله تعالى: {فَمَنْ كَانَ مِنْكُمْ مَرِيضًا أَوْ بِهِ أَذًى مِنْ رَأْسِهِ فَفِدْيَةٌ} [البقرة: ١٩٦].
وجه التمسك: أن الحلق محظور الإحرام، وقد أذن له الشارع فيه حالة الضرورة مقيدًا بالكفارة، وكذا قتل الصيد محظور الإحرام، يستباح لأجل
_________
(¬١) رواه مالك في الموطأ ١/ ٤١٦ كتاب الحج، باب فدية من أصاب شيئًا من الجراد وهو محرم، وابن أبي شيبة ٣/ ٤٢٥ كتاب الحج، باب في المحرم يقتل الجرادة رقم ١٥٦٢٥، وعبد الرزاق ٤/ ٤١٠ كتاب الحج، باب الهر والجراد رقم ٨٢٤٧.
لفظ مالك: "عن يحيى بن سعيد أن رجلًا جاء إلى عمر بن الخطاب -رضي الله عنه- فسأله عن جرادات قتلها وهو محرم، فقال عمر -رضي الله عنه- لكعب -رضي الله عنه-: تعال حتى نحكم فقال كعب -رضي الله عنه-: درهم، فقال عمر لكعب: إنك لتجد الدراهم لتمرة خير من جرادة".
ولفظ عبد الرزاق: عن يزيد بن إبراهيم، عن كعب -رضي الله عنه- "أنه مرت به جرادة فضربها بسوطه، فأخذها فشواها، فقالوا له، فقال: هذا خطأ، وأنا أحكم على نفسي، في هذا درهمًا، فأتى عمر فقال: وإنكم أهل حمص أكثر شيء دراهم، تمرة خير من جرادة". وإسناده صحيح.
(¬٢) الهداية ١/ ١٨٦، شرح فتح القدير ٣/ ٨٥، تبيين الحقائق ٢/ ٦٦، العناية ٣/ ٨٥.
(¬٣) تبيين الحقائق ٢/ ٦٦، شرح فتح القدير ٣/ ٨٥، الهداية ١/ ١٨٦.
(¬٤) وكذا عند الحنابلة.
الهداية ١/ ١٨٧، العناية ٣/ ٨٩، كنز الدقائق ٢/ ٦٧، بداية المبتدي ١/ ١٨٧، المقنع ١/ ٤١٥، حاشية المقنع ١/ ٤١٥.','3','352',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1028,'ويحل للمحرم ذبح غير الصيد، والحمام المسرول، والظبي المستأنس، صيدٌ، بخلاف البعير النَّاد.
<hr><span data-type="title">منحة السلوك</span>

الضرورة مقيدًا بالكفارة (¬١). فافهم.

<span data-type="title" id=toc-511>[ما يباح للمحرم قتله]</span>
قوله: ويحل للمحرم ذبح غير الصيد.
مثل الشاة، والبقرة، والبعير، والدجاجة، والبط الأهلي (¬٢)؛ لإجماع الأمة عليه (¬٣).
قوله: والحمام المسرول (¬٤)، والظبي المستأنس، صيد.
لأنهما صيد بأصل الخلقة (¬٥)، والاستئناس عارض، فلا يبطل الحكم الأصلي، بخلاف البعير الناد حيث لا يكون صيدًا في حق المحرم، ولكن يأخذ حكم الصيد في حق الذكاة (¬٦).
_________
(¬١) العناية ٣/ ٨٩، تبيين الحقائق ٢/ ٦٧، الهداية ١/ ١٨٧.
(¬٢) الكتاب ١/ ٢١٦، بداية المبتدي ١/ ٢٨٧.
(¬٣) مراتب الإجماع ص ٤٣، الكتاب ١/ ٢١٦، بداية المبتدي ١/ ١٨٧، الهداية ١/ ١٨٧، تبيين الحقائق ٢/ ٦٧، الكافي لابن عبد البر ص ١٥٦، شرح الزرقاني على مختصر خليل ٢/ ٣١٨، شرح الزركشي ٣/ ٣٣٧، زاد المستقنع ص ١٩٥، الروض المربع ص ١٩٥.
(¬٤) وهو الذي في رجليه ريش كأنه سراويل.
لسان العرب ١١/ ٣٣٤ مادة سرل، القاموس المحيط ٢/ ٥٥٧ مادة س ر ول، مختار الصحاح ص ١٢٥ مادة س ر ول، المغرب ص ٢٢٤ مادة حمام مسرول.
(¬٥) وفاقًا للثلاثة.
بداية المبتدي ١/ ١٨٧، كنز الدقائق ٢/ ١٦٧، الهداية ١/ ١٨٧، تبيين الحقائق ٢/ ٦٧، القوانين ص ٩٢، جواهر الإكليل ١/ ١٩٤، مختصر خليل ص ٩١، أقرب المسالك ص ٥٣، الحاوي الكبير ٤/ ٢٩٢، مغني المحتاج ١/ ٥٢٤، شرح منتهى الإرادات ٢/ ٢٥، كشاف القناع ١/ ٤٣٢.
(¬٦) شرح فتح القدير ٣/ ٩٠، العناية ٣/ ٩٠.','3','353',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1029,'ويحل للمحرم لحمُ صيدٍ اصطاده حلال، وذبحه بلا واسطة مُحرمٍ.
وفي صيد الحرم إذا ذبحه الحلال، قيمته يتصدق بها، لا غير.
<hr><span data-type="title">منحة السلوك</span>

قوله: ويحل للمحرم لحم صيد اصطاده حلال، وذبحه بلا واسطة محرم.
يعني: إن لم يدل عليه ولم يأمره بصيده (¬١). وذلك لأن أبا قتادة -رضي الله عنه- لم يصد الحمار الوحشي لنفسه خاصة، بل صاده لنفسه، ولأصحابه وهم محرمون، فأباحه لهم رسول الله -صلى الله عليه وسلم- ولم يحرمه عليهم بإرادته أنه لهم (¬٢).
هكذا قاله: الطحاوي (¬٣).

<span data-type="title" id=toc-512>[صيد الحرم وحشيشه]</span>
قوله: وفي صيد الحرم إذا ذبحه الحلال، قيمته يتصدق بها، لا غير.
يعني: لا يجزئه الصوم (¬٤)؛ لقوله -صلى الله عليه وسلم-: "إن الله حرم مكة لا يختلى خلاها، ولا يعضد شوكها، ولا ينفر صيدها، فقال العباس: إلا الإذخر (¬٥)،
_________
(¬١) وفاقًا للثلاثة.
الاختيار ١/ ١٦٨، الكتاب ١/ ٢١٦، المختار ١/ ١٦٨، التلقين ص ٦٦، بلغة السالك ١/ ٢٩٧، إخلاص الناوي ١/ ٣٥٢، إرشاد الغاوي ١/ ٣٥٢، الإقناع لابن المنذر ١/ ٢١٨، المحرر ١/ ٢٤٠.
(¬٢) الحديث سبق تخريجه ٣/ ٢٥٦.
(¬٣) مختصر اختلاف العلماء للطحاوي ٢/ ٢١٥.
(¬٤) وذهب المالكية، والشافعية، والحنابلة: إلى أن حكمه حكم المحرم.
الهداية ١/ ١٨٨، الجامع الصغير ص ١٥٠، بداية المبتدي ١/ ١٨٨، التفريع ١/ ٣٢٧، الكافي لابن عبد البر ص ١٥٧، روض الطالب ١/ ٥٢٠، أسنى المطالب ١/ ٥٢٠، المقنع ١/ ٤٣٦، الكافي لابن قدامة ١/ ٤٢٣.
(¬٥) الإذخر: حشيش طيب الريح، أطول من الثيل، وإذا جف ابيض. واحدتها: إذخرة، يسقف بها البيوت فوق الخشب.
لسان العرب ٤/ ٣٠٣ مادة ذخر، القاموس المحيط ٢/ ٢٥١ مادة ذ خ ر، المصباح المنير ١/ ٢٠٧ مادة ذخرته، المغرب ص ١٧٤ مادة الإذخر.','3','354',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1030,'وكذا في حشيشه، وشجره، غير المملوك، والمنبت عادةً، ما لم يَجف
<hr><span data-type="title">منحة السلوك</span>

فإنه لقبورنا وبيوتنا، فقال -صلى الله عليه وسلم-: إلا الإذخر" متفق عليه (¬١).
وإنما لم يجزه الصوم؛ لأنه غرامة وليس بكفارة، فأشبه غرامات الأموال (¬٢).
قوله: وكذا في حشيشه أي: وكذا تجب القيمة في حشيش الحرم، وشجره، غير المملوك والمنبت عادة، وغير المنبت عادة، ما لم يجف؛ لما روينا.
أما التقييد بغير المملوك: فلأنه إذا كان في ملك إنسان فعلى قاطعه قيمتان (¬٣)،
_________
(¬١) البخاري ٢/ ٦٥١ كتاب الحج، باب لا يحل القتال بمكة رقم ١٧٣٧، ومسلم ٢/ ٩٨٦ كتاب الحج، باب تحريم مكة، وصيدها، وخلالها، وشجرها، ولقطتها إلا لمنشد على الدوام رقم ١٣٥٣، عن ابن عباس -رضي الله عنهما-.
(¬٢) الهداية ١/ ١٨٨، العناية ٣/ ٩٦، تبيين الحقائق ٢/ ٦٧.
(¬٣) اتفق العلماء: على أن شجر الحرم مضمون على المحل، والمحرم، إلا مالكًا فإنه قال ليس بمضمون، وإنما يستغفر الله ولا كفارة عليه.
واختلفوا فيما غرسه الآدميون:
فقال أبو حنيفة: إن كان من جنس ما يغرسه الناس، جاز قطعه، سواء غرسه غارس، أو لم يغرسه، مثل شجر اللوز، والجوز، وغيره. وإن كان مما لا يغرسه الغارس، فغرسه غارس، لم يجب بقطعه جزاء. وإن أنبته الله تعالى لا بكسب آدمي وجب فيه الجزاء، كالقصب، ونحوه.
وقال الشافعي: يجب بإتلافه الجزاء في الحالين.
وقال أحمد: ما غرسه الآدميون من الشجر يجوز قطعه، ولا ضمان على قاطعه، وما نبت بلا كسب آدمي فلا يجوز قطعه. فإن قطعه ضمنه، سواء كان من جنس ما يغرسه الآدميون، أو لم يكن.
واختلفوا فيما يضمن به الشجرة الكبيرة، والصغيرة: =','3','355',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1031,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

قيمة حقًا للشرع، وقيمة لمالكه (¬١).
وأما التقييد بغير المنبت عادة: فلأنه إذا كان منبتًا عادة مثل الحنطة، والبقول، والرياحين، فالضمان عليه لحق صاحبه، لا لحق الحرم (¬٢).
وأما الذي هو ليس بمنبت عادة، كأم غيلان (¬٣)، فلا يخلو: إما أنبته منبت، أو نبت بنفسه.
والنابت بنفسه لا يخلو أيضًا: إما أن يكون نبت في ملك أحد، أو في غير ملك أحد.
أما الذي أنبته منبت: فلا ضمان فيه لحق الحرم، حيث ملكه بالإنبات فصار مما ينبته الناس عادة. وأما الذي نبت بنفسه، وكان في ملك أحد: فعلى القاطع فيه ضمانان: ضمان لحق الحرم، وضمان لحق صاحبه (¬٤).
وأما الذي نبت بنفسه، ولم يكن في ملك أحد: فعليه فيه ضمان واحد
_________
= فقال أبو حنيفة: يضمن الجميع بالقيمة.
وقال الشافعي، وأحمد: يضمن الكبيرة ببقرة، والصغيرة بشاة.
بداية المبتدي ١/ ١٩٠، تحفة الفقهاء ١/ ٤٢٤، الهداية ١/ ١٩٠، منح الجليل ٢/ ٣٥٥، حاشية الدسوقي ٢/ ٧٩، فتح الوهاب ٢/ ٥٣١، حاشية الجمل ٢/ ٥٣١، شرح منتهى الإرادات ٢/ ٤٥، الإفصاح ١/ ٢٩٤.
(¬١) الهداية ١/ ١٩٠، تبيين الحقائق ٢/ ٧٠، شرح فتح القدير ٣/ ١٠٢.
(¬٢) شرح فتح القدير ٣/ ١٠٢، العناية ٣/ ١٠٢، تبيين الحقائق ٢/ ٧٠.
(¬٣) أم غيلان -بالفتح-: ضرب من العضاة، وهو شجر السَّمر.
لسان العرب ١١/ ٥٣١ مادة غيل، القاموس المحيط ٣/ ٤٣٦ مادة غ ي ل، مختار الصحاح ص ٢٠٣ مادة غ ي ل، المصباح المنير ٢/ ٤٦٠ مادة أغال.
(¬٤) تبيين الحقائق ٢/ ٧٠، شرح فتح القدير ٣/ ١٠٢، بدائع الصنائع ٢/ ٢١٠.','3','356',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1032,'ولا يُرْعى حشيش الحرم، ولا يقطع منه غير الإذخر، ويحلُّ قلع الكمأة.
<hr><span data-type="title">منحة السلوك</span>

لحق الحرم (¬١).
وأما التقييد بعدم الجفاف: فلأنه إذا قطع شجرة يابسة، أو حشيشًا يابسًا، لا شيء عليه؛ لأنه حطب (¬٢).
قوله: ولا يرعى حشيش الحرم (¬٣)؛ لما روينا.
وجوز أبو يوسف رعيه؛ لمكان الحرج (¬٤).
قوله: ولا يقطع منه. أي: من حشيش الحرم، غير الإذخر؛ لما روينا (¬٥).
قوله: ويحل قلع الكمأة (¬٦). أي: من الحرم؛ لأنها ليست من نبات
_________
(¬١) بدائع الصنائع ٢/ ٢١٠، الكتاب ١/ ٢١٧، تبيين الحقائق ٢/ ٧٠، شرح فتح القدير ٣/ ١٠٢، تحفة الفقهاء ١/ ٤٢٦.
(¬٢) تحفة الفقهاء ١/ ٤٢٦، الهداية ١/ ١٩٠، بدائع الصنائع ٢/ ٢١٠.
(¬٣) الأصل ٢/ ٣٨٤، مختصر الطحاوي ص ٦٩، تحفة الفقهاء ١/ ٤٢٦.
(¬٤) وفاقًا للمالكية، والشافعية، والحنابلة.
الأصل ٢/ ٣٨٤، مختصر الطحاوي ص ٦٩، تبيين الحقائق ٢/ ٧٠، مواهب الجليل ٢/ ١٧٨، التفريع ١/ ٣٣١، فتح الوهاب ٢/ ٥٣١، حاشية الجمل ٢/ ٥٣١، منتهى الإرادات ٢/ ٤٥، الروض المربع ص ٢٠١.
(¬٥) وكذا عند الحنابلة، وأضاف إليه المالكية السنا، وهو نبات مسهل يتداوى به.
وعند الشافعية: يجوز أخذ النابت للدواء من السنا وغيره، كالحنظل. أما اليابس فيجوز التعرض له.
الأصل ٢/ ٣٨٤، مختصر الطحاوي ص ٧٠، تحفة الفقهاء ١/ ٤٢٦، منح الجليل ٢/ ٣٥٥، حاشية الدسوقي ٢/ ٧٩، فتح الوهاب ٢/ ٥٣١، حاشية الجمل ٢/ ٥٣١، أسنى المطالب ١/ ٥٢٢، شرح منتهى الإرادات ٢/ ٤٥، الروض المربع ص ٢٠١.
(¬٦) الكمأة: نبات ينقض الأرض فيخرج كما يخرج الفطر. والجمع: أكمؤ، وكمأة. =','3','357',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1033,'وما يوجب على المفرد دمًا، يوجب على القارن دمين.
ولو قتل محرمان صيدًا. فعلى كل واحدٍ منهما جزاءٌ.
<hr><span data-type="title">منحة السلوك</span>

الأرض، وإنما هي مودعة فيها، ولأنها لا تنمو، ولا تبقى، فأشبهت اليابس من النباتات (¬١).
قوله: وما يوجب على المفرد دمًا، يوجب على القارن دمين، دمًا لحجته، ودمًا لعمرته (¬٢).
وقال الشافعي: دم واحد (¬٣).
وهذه قاعدة مطردة، إلا في مسألة واحدة، وهي: مجاوزة القارن للميقات، فإن عليه دمًا واحدًا فيه (¬٤).
وقال زفر: دمان (¬٥).

<span data-type="title" id=toc-513>[الجزاء في الاشتراك]</span>
قوله: ولو قتل محرمان صيدًا، فعلى كل واحد منهما جزاء.
_________
= لسان العرب ١/ ١٤٨ مادة كمأ، القاموس المحيط ٤/ ٧٩ مادة ك م أ، معجم مقاييس اللغة ٥/ ١٣٧ باب الكاف والميم وما يثلثهما مادة كمى.
(¬١) وإليه ذهب الحنابلة.
تبيين الحقائق ٢/ ٧٠، تحفة الفقهاء ١/ ٤٢٦، بدائع الصنائع ٢/ ٢١١، شرح منتهى الإرادات ٢/ ٤٥، الإقناع للحجاوي ٢/ ٤٧٠.
(¬٢) بداية المبتدي ١/ ١٩٠، الكتاب ١/ ٢١٧، الهداية ١/ ١٩٠، تبيين الحقائق ٢/ ٧٠.
(¬٣) وإليه ذهب المالكية، والحنابلة.
بداية المجتهد ١/ ٣٣٥، التلقين ص ٦٨، رحمة الأمة ١/ ١٣٦، حلية العلماء ٣/ ٣٢٠، زاد المستقنع ص ١٩٨، منتهى الإرادات ٢/ ٣٥.
(¬٤) لأن الواجب عليه إحرام واحد؛ لتعظيم البقعة.
شرح فتح القدير ٣/ ١٠٤، العناية ٣/ ١٠٤، تبيين الحقائق ٢/ ٧١.
(¬٥) لأنه أخَّر الإحرامين من الميقات، فيلزمه لكل واحد منهما دم؛ اعتبارًا بسائر المحظورات.
الهداية ١/ ١٩١، العناية ١/ ١٠٤، تبيين الحقائق ٢/ ٧١.','3','358',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1034,'ولو قتل حلالان صيدَ الحرم، فعليهما جزاءٌ واحد. وبيع المحرم الصيد، وشراؤه باطل.
<hr><span data-type="title">منحة السلوك</span>

أي: جزاء كامل؛ لأن كلًا منهما جانٍ (¬١).
وقال الشافعي: جزاء واحد (¬٢).
قوله: ولو قتل حلالان صيد الحرم، فعليهما جزاء واحد.
لأن الواجب فيه بدل المحل، لا جزاء الفعل، وهو واحد (¬٣).
قوله: وبيع المحرم الصيد، وشراؤه باطل (¬٤).
لأن بيعه حيًا تعرض للصيد، وبيعه بعد قتله بيع ميتة (¬٥)، بخلاف ما إذا
_________
(¬١) وإليه ذهب المالكية.
بداية المبتدي ١/ ١٩١، الكتاب ١/ ٢١٧، الهداية ١/ ١٩١، تحفة الفقهاء ١/ ٤٢٥، جواهر الإكليل ١/ ١٩٧، منح الجليل ٢/ ٣٤٩.
(¬٢) وإليه ذهب الحنابلة. والخلاف مبني على أنه هل هو كفارة، أو قيمة؟.
الذخيرة ٣/ ٣٢٠، التنبيه ص ٧٤، الوجيز ١/ ١٢٩، كشاف القناع ٢/ ٤٣٣، الإفصاح ١/ ٢٩٣.
(¬٣) وإليه ذهب الشافعية، والحنابلة.
وذهب المالكية: إلى أنه يلزم كل واحد جزاء.
الكتاب ١/ ٢١٨، كنز الدقائق ٢/ ٧١، تحفة الفقهاء ١/ ٤٢٥، الهداية ١/ ١٩١، بلغة السالك ١/ ٢٩٦، الذخيرة ٣/ ٣٢٠، الخرشي على خليل ٢/ ٣٦٩، الوجيز ١/ ١٢٩، التنبيه ص ٧٤، المقنع ١/ ٤٣٦، الكافي لابن قدامة ١/ ٤٢٣.
(¬٤) وفي صحة شرائه عند المالكية قولان: قيل: العقد صحيح، وهو قول: ابن حبيب. وقيل: فاسد، كما في الموازية.
وعند الحنابلة: لا يملك المحرم الصيد بالبيع، ولا بالهبة. وعليه رده.
الكتاب ١/ ٢١٨، كنز الدقائق ٢/ ٧١، الهداية ١/ ١٩١، العناية ٣/ ١٠٦، الخرشي على خليل ٢/ ٣٦٦، مواهب الجليل ٣/ ١٧٢، المبدع ٣/ ١٥٣، حاشية المقنع ١/ ٤١١.
(¬٥) الهداية ١/ ١٩١، العناية ٣/ ١٠٦، تبيين الحقائق ٢/ ٧١.','3','359',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1035,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

باع لبن الصيد، أو بيضه، أو الجراد، أو شجر الحرم؛ لأن هذه الأشياء لا يشترط فيها الذكاة (¬١). والله أعلم.
_________
(¬١) ثم إذا قبض المشتري وعطب في يده، فعليه وعلى البائع الجزاء؛ لأنهما قد جنيا عليه، البائع بالتسليم، والمشتري بإثبات اليد عليه. ويضمن المشتري أيضًا للبائع؛ لفساد البيع.
ولو رده على البائع يجب على المشتري الجزاء؛ للتعدي بالتسليم إليه، وجعله عرضة للهلاك، ويبرأ من الضمان للبائع.
تبيين الحقائق ٢/ ٧١، العناية ٣/ ١٠٦، الهداية ١/ ١٩١، حاشية الشلبي ٢/ ٧١.','3','360',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1036,'<span data-type="title" id=toc-514>فصل</span>
محرم منعه عدو، أو مرض، جاز له التحلل ببعث شاةٍ تذبح في يوم يعلمه؛ ليتحلل بعد الذبح.
<hr><span data-type="title">منحة السلوك</span>

فصل
هذا الفصل مشتمل على أحكام المحصر (¬١)، والعمرة، والحج عن الغير، والهدي.

<span data-type="title" id=toc-515>[حكم المحصر]</span>
قوله: محرم منعه عدو، أو مرض، جاز له التحلل ببعث شاة تذبح في يوم يعلمه؛ ليتحلل بعد الذبح (¬٢).
لقوله تعالى: {فَإِنْ أُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ} [البقرة: ١٩٦]. والعبرة بعموم اللفظ، لا بخصوص السبب (¬٣). فيعم الإحصار بالعدو والمرض (¬٤)، لا كما قاله الشافعي: إن الإحصار بالعدو فقط (¬٥).
_________
(¬١) الإحصار لغة: هو المنع يقال: حصره العدو، وأحصره المرض.
واصطلاحًا: هو منع الوقوف، والطواف. فإذا قدر على أحدهما فليس بمحصر.
لسان العرب ٤/ ١٩٥ مادة حصر، مختار الصحاح ص ٥٩ مادة ح ص ر، تبيين الحقائق ٢/ ٧٧، حاشية الشلبي ٢/ ٧٧، الاختيار ١/ ١٦٨، الهداية ١/ ١٩٦، العناية ٣/ ١٢٤.
(¬٢) الكتاب ١/ ٢١٨، الاختيار ١/ ١٦٨، المختار ١/ ١٦٨.
(¬٣) حيث إن الآية وردت لبيان حكم إحصاره -صلى الله عليه وسلم- وأصحابه، وكان بالعدو سنة ست عام الحديبية، حين حال المشركون بين رسول الله -صلى الله عليه وسلم- وبين الوصول إلى البيت.
تفسير ابن كثير ١/ ٣٤٧، الدر المنثور في التفسير بالمأثور ١/ ٣٨٤، جامع البيان في تأويل آي القرآن ٢/ ٢١٩.
(¬٤) الكتاب ١/ ٢١٨، الاختيار ١/ ١٦٨، المختار ١/ ١٦٨.
(¬٥) وإليه ذهب المالكية، والحنابلة: فإذا مرض المحرم لم يجز له التحلل، ويقيم على إحرامه حتى يصل إلى البيت. فإن فاته الحج فعل ما يفعله المفوت من عمل العمرة، والهدي، والقضاء. =','3','361',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1037,'ويتوقت (¬١) دم الإحصار بالحرم، لا بيوم النحر. بخلاف دم المتعة، والقران.
<hr><span data-type="title">منحة السلوك</span>

قوله: ويتوقت دم الإحصار بالحرم، حتى لا يجوز ذبحه في غيره، ولا يتوقت بيوم النحر، حتى جاز ذبحه في أي وقت شاء. وهذا عند أبي حنيفة (¬٢).
وقالا: يتوقت بالزمان وهو أيام النحر، وبالمكان وهو الحرم (¬٣). وهذا الخلاف في المحصر بالحج.
وأما دم المحصر بالعمرة: فلا يتعين بالزمان بالإجماع (¬٤).
قوله: بخلاف دم المتعة والقران.
حيث يختصان بالحرم، ويوم النحر؛ لأنه دم نسك كالأضحية (¬٥).
_________
= القوانين ص ٩٤، الكافي لابن عبد البر ص ١٦٠، أسهل المدارك ١/ ٥١٣، فتح العزيز ٨/ ٨، روضة الطالبين ٣/ ١٧٣، كفاية الأخيار ١/ ١٤٤، حلية العلماء ٣/ ٣٥٥، الإنصاف ٤/ ٧١، الإقناع لابن المنذر ١/ ٢٣٥.
(¬١) في د: "ويتوقف".
(¬٢) بداية المبتدي ١/ ١٩٦، العناية ٣/ ١٢٦، الهداية ١/ ١٩٦.
(¬٣) وعند المالكية: لا يجب عليه هدي. وإن كان معه هدي نحره حيث حل، ولا يتوقت بزمن.
وذهب الشافعية، والحنابلة: إلى أنه يذبح دم الإحصار حيث أحصر، ولا يتوقت بزمان.
الهداية ١/ ١٩٦، بداية المبتدي ١/ ١٩٦، العناية ٣/ ١٢٦، بداية المجتهد ١/ ٣٥٥، حاشية الدسوقي ٢/ ٩٣، إخلاص الناوي ١/ ٣٥٧، الإقناع في حل ألفاظ أبي شجاع ١/ ٢٤٤، المستوعب ١/ ٣٠٢، الروض المربع ص ٢١٥، الإفصاح ١/ ٣٠٠.
(¬٤) مراتب الإجماع لابن حزم ص ٤٧.
(¬٥) اتفق الأئمة الأربعة: على أن أي موضع نحر فيه من الحرم أجزأه، إلا مالكًا فإنه قال: لا ينحر في الحج إلا بمنى، ولا في العمرة إلا بمكة.
وأما زمنه: فذهب المالكية، والحنابلة: إلى أن وقته أيام النحر. =','3','362',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1038,'والمحصر بالحج إذا تحلل، فعليه حجةٌ، وعمرةٌ. وعلى المحصر بالعمرة: القضاءُ.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-516>[الآثار المترتبة على تحلل المحصر]</span>
قوله: والمحصر بالحج إذا تحلل، فعليه حجة، وعمرة (¬١)
كذا روي عن ابن عباس، وابن عمر -رضي الله عنهما- (¬٢).
وقال الشافعي: يلزمه حجة لا غير (¬٣).
قوله: وعلى المحصر بالعمرة: القضاء.
_________
= وذهب الشافعية: إلى أن دم المتعة، والقران، لا يختصان بزمان، بل يفعلان في يوم النحر، وغيره، ولكن يسن يوم النحر، وأيام التشريق.
الهداية ١/ ١٩٦، بدائع الصنائع ٢/ ١٨٠، شرح فتح القدير ٣/ ١٢٩، تبيين الحقائق ٢/ ٧٩، الشرح الكبير للدسوقي ٢/ ٩٣، الشرح الصغير ١/ ٣٠١، مختصر خليل ص ٩٤، بداية المجتهد ١/ ٣٧٨، المعونة ١/ ٥٦٥، مغني المحتاج ١/ ٥٣٠، نهاية المحتاج ٣/ ٣٥٩، الإقناع للحجاوي ٢/ ٤٥٤ - ٤٦٠، منتهى الإرادات ٢/ ٣٦ - ٣٩.
(¬١) الكتاب ١/ ٢١٩، الاختيار ١/ ١٦٩، الهداية ١/ ١٩٦.
(¬٢) قال ابن حجر في الدراية ٢/ ٤٦: "لم أجده، نعم ذكره أبو بكر الرازي عن ابن عباس وابن مسعود بغير إسناد".
وقال في نصب الراية ٣/ ١٤٤: "ذكره أبو بكر الرازي عن ابن عباس".
والمروي عن ابن عباس أنه ليس عليه حج من قابل، رواه البخاري معلقًا ٢/ ٦٤٣ كتاب الإحصار، وجزاء الصيد في المحصر، باب من قال: ليس على المحصر بدل، وابن جرير في التفسير رقم ٣٢٣٥ و ٣٣٠٠.
(¬٣) وكذا عند الحنابلة. وهو قول: أبي ثور، وإسحاق.
وذهب المالكية: إلى أنه لا قضاء عليه؛ لأنه ممنوع عن الوصول إلى فعل المناسك بيد غالبة، فلم يلزمه قضاء.
التفريع ١/ ٥٩٠، التلقين ص ٧٠، بداية المجتهد ١/ ٣٥٥، السراج الوهاج ص ١٧٢، شرح المحلي على المنهاج ٢/ ١٥٠، مختصر الخرقي ص ٦٣، دليل الطالب ١/ ٢٦٩، المغني ٣/ ٥٦٩.','3','363',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1039,'وعلى القارن: حجَّةٌ، وعمرتان.
ولو زال الإحصار قبل الذبح، فإن قدر على إدراك الهدي، والحج لزمه التوجه،
<hr><span data-type="title">منحة السلوك</span>

يعني: إذا تحلل المحصر بالعمرة، وجب عليه القضاء لا غير (¬١).
والإحصار عنها: يتحقق عندنا (¬٢).
وقال مالك (¬٣)، والشافعي (¬٤): لا يتحقق.
قوله: وعلى القارن: حجة وعمرتان.
لأنه صح شروعه في الحج والعمرة، فيلزمه بالتحلل قضاؤها وقت عمرة أخرى، إذا لم يقض الحج في تلك السنة (¬٥).

<span data-type="title" id=toc-517>[زوال الإحصار]</span>
قوله: ولو زال الإحصار قبل الذبح، فإن قدر على إدراك الهدي والحج، لزمه التوجه لأداء الحج، وليس له أن يتحلل بالهدي؛ لأنه قدر على
_________
(¬١) وكذا عند الحنابلة.
الكتاب ١/ ٢١٩، كنز الدقائق ٢/ ٨٠، تبيين الحقائق ٢/ ٨٠، مطالب أولي النهى ٢/ ٤٥٥، شرح منتهى الإرادات ٢/ ٧٥.
(¬٢) الهداية ١/ ١٩٦، العناية ٣/ ١٣١.
(¬٣) الشرح الصغير ١/ ٣٠٦، بلغة السالك ١/ ٣٠٦.
(¬٤) حلية العلماء ٣/ ٣٥٦، روض الطالب ١/ ٥٢٤.
(¬٥) وعند المالكية كما سبق لا قضاء عليه.
أما عند الشافعية، والحنابلة: فإن عليه أن يقضي بمثل ما أهل به من قابل. فالقضاء عندهم: يجب على حسب الأداء، في صورته ومعناه.
شرح فتح القدير ٣/ ١٣١، تبيين الحقائق ٢/ ٨٠، الكتاب ١/ ٢١٩، الاختيار ١/ ١٦٩، التلقين ١/ ٥٩٠، بداية المجتهد ١/ ٣٥٥، التفريع ١/ ٥٩٠، مغني المحتاج ١/ ٥٣٧، أسنى المطالب ١/ ٥٢٨، كشاف القناع ٢/ ٥٢٤، المغني ٣/ ٥٦٩.','3','364',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1040,'وإلا فلا.
ومن قدر على الوقوف، أو الطواف، أو مُنع بعد الوقوف، فليس بمحصر.
<hr><span data-type="title">منحة السلوك</span>

الأصل قبل حصول المقصود بالبدل، ويصنع بالهدي ما شاء (¬١).
قوله: وإلا، فلا. يعني: وإن لم يقدر على إدراك الهدي والحج، لا يجب عليه التوجه (¬٢)، وإن توجه ليتحلل بأفعال العمرة جاز؛ لأن فيه فائدة، وهي سقوط العمرة عنه في القضاء (¬٣).

<span data-type="title" id=toc-518>[حد الإحصار]</span>
قوله: ومن قدر على الوقوف -أي: بعرفة-، أو الطواف -أي: طواف الزيارة-، أو منع بعد الوقوف بعرفة، فليس بمحصر (¬٤).
_________
(¬١) تبيين الحقائق ٢/ ٨٠، تحفة الفقهاء ١/ ٤١٨، بداية المبتدي ١/ ١٩٦، كنز الدقائق ٢/ ٨٠، إرشاد الغاوي ١/ ٣٥٧، إخلاص الناوي ١/ ٣٥٧.
(¬٢) كنز الدقائق ٢/ ٨٠، تحفة الفقهاء ١/ ٤١٩، الهداية ١/ ١٩٦، تبيين الحقائق ٢/ ٨٠.
(¬٣) تبيين الحقائق ٢/ ٨٠، الهداية ١/ ١٩٦، تحفة الفقهاء ١/ ٤١٩.
(¬٤) وإليه ذهب المالكية. فعندهم لا يكون محصرًا، ولا سبيل إلى تحلله، ويبقى محرمًا أبدًا حتى يطوف للزيارة.
وذهب الشافعية، والحنابلة: إلى أن الإحصار قبل الوقوف بعرفة، وبعد الوقوف بها كله، سواء في إثبات حكم الإحصار، وأن المحصر في حالة من هذه الأحوال، كمن لم يقدر عليها كلها؛ لأن قوله تعالى: {فَإِنْ أُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ} [البقرة: ١٩٦] محمول على العموم في حق كل من أحصر سواء كان قبل الوقوف، أو بعده، وبمكة أو بغيرها، وسواء كان طاف بالبيت أم لم يطف. وله أن يتحلل؛ لأنه سبحانه أطلق ذلك في قوله، ولم يخصه.
كنز الدقائق ٢/ ٨١، الاختيار ١/ ١٧٠، الشرح الصغير ١/ ٣٠٦، بلغة السالك ١/ ٣٠٦، الكافي لابن عبد البر ص ١٦٠، التفريع ١/ ٣٥١، أسنى المطالب ١/ ٥٢٤، المجموع ٨/ ٣٠١، المهذب ١/ ٢٣٣، الإفصاح ١/ ٢٩٨، الإقناع للحجاوي ٢/ ٥٢٥، منتهى الإرادات ٢/ ٧٥.','3','365',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1041,'ومن فاته الوقوف، حتى طلع الفجر يوم النحر فقد فاته الحج فيتحلل بعمرةٍ ويقضي الحجَّ ولا دم عليه.
والعمرة لا تفوت. وهي جائزة في كل وقت، إلا يوم عرفة، ويوم
<hr><span data-type="title">منحة السلوك</span>

أما إذا قدر على الوقوف؛ فلأنه أمن من الفوات (¬١).
وأما إذا قدر على الطواف، فلأن فائت الحج يتحلل به، والدم بدل عنه في التحلل، فلا حاجة إلى الهدي (¬٢).
وأما إذا منع بعد الوقوف، فلأنه لا يتصور الفوات بعده، فأمن منه (¬٣).

<span data-type="title" id=toc-519>[من فاته الوقوف بعرفة]</span>
قوله: ومن فاته الوقوف. أي: بعرفة حتى طلع الفجر يوم النحر، فقد فاته الحج؛ لأنه لا يمكن تدارك الوقوف بعده؛ لذهاب وقته، فيتحلل بعمرة، ويقضي الحج من قابل، ولا دم عليه (¬٤).

<span data-type="title" id=toc-520>[زمن العمرة]</span>
قوله: والعمرة لا تفوت؛ لأنها غير مؤقتة. وعليه الإجماع (¬٥).
قوله: وهي، أي: العمرة، جائزة في كل وقت، إلا يوم عرفة، ويوم
_________
(¬١) المبسوط ٤/ ١١٤، العناية ٣/ ١٣٤، الاختيار ١/ ١٧٠.
(¬٢) تحفة الفقهاء ١/ ٤١٨، تبيين الحقائق ٢/ ٨١.
(¬٣) كنز الدقائق ٢/ ٨١، تبيين الحقائق ٢/ ٨١.
(¬٤) وهي رواية عن الإمام أحمد.
وذهب المالكية، والشافعية، وأصح الروايتين عن الإمام أحمد، وهي المذهب: إلى أن من فاته الوقوف بعرفة فقد فاته الحج، فيتحلل بعمرة، ويقضي الحج، وعليه دم.
بداية المبتدي ١/ ١٩٧، الكتاب ١/ ٢٢١، تحفة الفقهاء ١/ ٤١٧، الهداية ١/ ١٩٧، تبيين الحقائق ٢/ ٨٢، الشرح الصغير ١/ ٣٠٥، بلغة السالك ١/ ٣٠٥، التفريع ١/ ٣٥٢، المعونة ١/ ٥٩١، التنبيه ص ٨٠، نهاية المحتاج ٣/ ٣٧٠، حاشية المقنع ١/ ٤٧٠، المبدع ٣/ ٢٦٨، شرح منتهى الإرادات ٢/ ٧٤.
(¬٥) مراتب الإجماع ص ٤٧.','3','366',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1042,'النحر، وأيام التشريق، وهي سنة.
<hr><span data-type="title">منحة السلوك</span>

النحر، وأيام التشريق (¬١).
لما روي عن ابن عباس -رضي الله عنهما-: "لا تعتمر في خمسة أيام، واعتمر فيما قبلها، وبعدها" (¬٢).
قوله: وهي، أي: العمرة سنة (¬٣).
وهذا مكرر لا طائل تحته؛ لأنه ذكرها مرة في أول الحج (¬٤).
_________
(¬١) فتكره، والمراد أنه يكره فعلها. أي: إنشاء الإحرام في هذه الأيام أما لو كان قارنًا، أو فائت الحج، يجوز أداء أفعالها بلا كراهة فيها، كما في سائر الأيام.
وذهب المالكية: إلى أن أهل منى خاصة، لا يجوز لهم أن يعتمروا في هذه الأيام الخمسة.
وذهب الشافعية، والحنابلة: إلى أن العمرة تباح في كل وقت من أوقات السنة، ولا تكره يوم عرفة، ولا يوم النحر، ولا أيام التشريق؛ لأن الأصل الإباحة، ولا دلالة على الكراهة.
بداية المبتدي ١/ ١٩٨، الكتاب ١/ ٢٢١، القوانين الفقهية ص ٩٥، التلقين ص ٦٣، السراج الوهاج ص ١٥٤، مختصر المزني ص ١٥٩، الشرح الكبير لابن قدامة ٣/ ٢٣٠، منتهى الإرادات ٢/ ٧٢، كشاف القناع ٢/ ٥٢٠، الإفصاح ١/ ٢٧٤.
(¬٢) عزاه في نصب الراية ٣/ ١٤٧: إلى الشيخ في "الإمام" قال: "وقال الشيخ في الإمام: وروى إسماعيل بن عياش، عن إبراهيم بن نافع، عن طاووس قال: قال البحر -يعني: ابن عباس-: خمسة أيام: يوم عرفة، ويوم النحر، وثلاثة أيام التشريق، اعتمر قبلها، وبعدها ما شئته".
قال -أي: الزيلعي-: ولم يعزه -أي الشيخ- في الإمام".
وقد عزاه صاحب القرى ص ٦٠٧ إلى سعيد بن منصور.
(¬٣) كنز الدقائق ٢/ ٨٢، بداية المبتدي ١/ ١٩٨، الهداية ١/ ١٩٨، تبيين الحقائق ٢/ ٨٢.
(¬٤) في ٣/ ٢٣٧.','3','367',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1043,'وتجوز النيابة في نفل الحج
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-521>[النيابة في الحج]</span>
قوله: وتجوز النيابة في نفل (¬١) الحج (¬٢).
اعلم أن الإنسان له أن يجعل ثواب عمله لغيره عند أهل السنة والجماعة صلاة كان أو صومًا، أو حجًا، أو صدقةً، أو قراءة القرآن، والأذكار، إلى غير ذلك من جميع أنواع البر، يصل ذلك إلى الميت وينفعه (¬٣).
وقالت المعتزلة: ليس له ذلك، ولا يصل إليه ولا ينفعه (¬٤).
وقال الشافعي (¬٥)، ومالك: يجوز ذلك في الصدقة، والعبادة المالية، كالحج، ولا يجوز في غيره من الطاعات، كالصلاة، والصوم، وقراءة القرآن، وغيره (¬٦).
ولنا: ما روي أنَّ رجلًا سأل النبي -صلى الله عليه وسلم- فقال: كان لي أبوان أبرهما حال حياتهما، فكيف إليّ ببرهما بعد موتهما؟ فقال له النبي -صلى الله عليه وسلم-: "إن من البر بعد البر، أن تصلي لهما مع صلواتك، وأن تصوم لهما مع صومك" رواه الدارقطني. وعن علي -رضي الله عنه- أن النبي -صلى الله عليه وسلم- قال: "من مرَّ على المقابر وقرأ: {قُلْ هُوَ اللَّهُ أَحَدٌ} [الإخلاص: ١] إحدى عشرة مرة ثم وهب أجرها
_________
(¬١) في ر: "فعل".
(¬٢) الجامع الصغير لمحمد بن الحسن ص ١٦٦، الأصل ٢/ ٤١٧، الهداية ١/ ١٩٨.
(¬٣) شرح فتح القدير ٣/ ١٤٢، العناية ٣/ ١٤٢، تبيين الحقائق ٢/ ٨٣.
(¬٤) تبيين الحقائق ٢/ ٨٣، شرح فتح القدير ٣/ ١٤٢، العناية ٣/ ١٤٢.
(¬٥) الوجيز ١/ ١٨٨، كفاية الأخيار ١/ ١٧٥.
(¬٦) كطهارة من حدث واعتكاف، وإلى هذا ذهب الحنابلة.
جواهر الإكليل ١/ ١٦٣، حاشية العدوي على خليل ٢/ ٢٨٩، كشاف القناع ٣/ ٢٦٥، المغني ٥/ ٢٠٧، هداية الراغب ص ٢٨٣.','3','368',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1044,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

للأموات، أعطي من الأجر بعدد الأموات" رواه الدارقطني (¬١). وعن معقل بن يسار -رضي الله عنه- (¬٢) قال: قال رسول الله -صلى الله عليه وسلم-: "اقرؤوا على موتاكم سورة يس" رواه أبو داود (¬٣). وعنه -صلى الله عليه وسلم-:
_________
(¬١) قال السخاوي في الفتاوى الحديثية ص ١٩٢: "ويمكن أن تخريج الدارقطني له إنما هو في الإفراد؛ لأنه لا وجود له في سننه" ا. هـ.
وقد أخرجه أبو محمد الخلال في فضائل الإخلاص ق ٢٠١/ ٢، والديلمي في مسند الفردوس ٤/ ١٠٨ رقم ٥٨٣٤. عن عبد الله بن أحمد بن عامر، حدثنا أبي، حدثنا علي بن موسى، عن أبيه، عن أبيه محمد بن علي، عن أبيه، عن أبيه الحسين، عن أبيه علي مرفوعًا.
قال الذهبي في ميزان الاعتدال ٢/ ٣٩٠: عبد الله بن أحمد بن عامر، عن أبيه، عن علي الرضا، عن آبائه بتلك النسخة الموضوعة الباطلة، ما تنفك عن وضعه، أو وضع أبيه.
وقال الحافظ السخاوي في الفتاوى الحديثية ص ١٩٢: رواه القاضي أبو يعلى بإسناده عن علي، ورواه الدارقطني أيضًا والنجاد، كما ذكره الإمام شمس الدين محمد بن إبراهيم المقدسي في جزء فيه أصول القراءة على الميت، وعزاه القرطبي في تذكرته للسلفي، وأسنده صاحب مسند الفردوس أيضًا، كلاهما من طريق عبد الله أحمد بن عامر الطائي، عن أبيه، عن علي بن موسى الرضي، عن علي، لكن عبد الله وأبوه كذابان، ولو أن لهذا الحديث أصلًا، لكان حجة في موضوع النزاع، ولا ارتفع الخلاف.
والحديث أورده العجلوني في كشف الخفاء ٢/ ٣٧١ وقال: رواه الرافعي في تاريخه عن علي.
وذكره السيوطي في ذيل الأحاديث الموضوعة ص ١٤٤.
وقال الألباني في سلسلة الأحاديث الموضوعة ٣/ ٤٥٢ رقم ١٢٩٠: "موضوع".
(¬٢) هو معقل بن يسار بن عبد الله المزني، البصري، صحابي من أهل بيعة الرضوان، أسلم قبل الحديبية، حفر نهر معقل بالبصرة بأمر عمر بن الخطاب فنسب إليه. مات بالبصرة في آخر خلافة معاوية.
أسد الغابة ٥/ ٢٣٢، الإصابة ٣/ ٤٤٧، تاريخ الفسوي ١/ ٣١٠، سير أعلام النبلاء ٢/ ٥٧٦.
(¬٣) ٣/ ١٩١ كتاب الجنائز، باب القراءة عند الميت رقم ٣١٢١ بلفظ "اقرؤوا يس على =','3','369',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1045,'مطلقًا،
<hr><span data-type="title">منحة السلوك</span>

" أنه ضحَّى بكبشين أملحين (¬١)، أحدهما عن نفسه والآخر عن أُمته" متفق عليه (¬٢). أي: جعل ثوابه لأمته.
قوله: مطلقًا.
يعني: سواء كان له عجز دائمٌ إلى الموت، أو لم يكن، وذلك لأن باب
_________
= موتاكم". ورواه أيضًا أبو داود الطيالسي ص ١٢٦ رقم ٩٣١، وأحمد ٥/ ٢٧، وابن ماجه ١/ ٤٦٦ كتاب الجنائز، باب ما جاء فيما يقال عند المريض إذا حضر رقم ١٤٤٨، وابن حبان ٧/ ٢٦٩ كتاب الجنائز، فصل في المحتضر رقم ٣٠٠٢، والحاكم ١/ ٥٦٥ كتاب فضائل القرآن، باب ذكر فضائل سور، وآي متفرقة، وابن أبي شيبة ٢/ ٤٤٥ كتاب الجنائز، باب ما يقال عند المريض إذا حضر رقم ١٠٨٥٣، والبيهقي في السنن الصغرى ٢/ ٧ كتاب الجنائز، باب تلقين المريض إذا حضره الموت، وما يستحب قراءته عنده، وما يصنع هو ويقول رقم ١٠١٣، والبيهقي في السنن الكبرى ٣/ ٣٨٣ كتاب الجنائز، باب ما يستحب من قراءته عنده، والطبراني في معجمه الكبير ٢٠/ ٥١٠، والبغوي ٥/ ٢٩٥ رقم ١٤٦٤.
قال الحاكم ١/ ٥٦٥: وقفه يحيى بن سعيد وغيره، عن سليمان التيمي، والقول فيه قول: ابن المبارك، فقد رواه موصولًا، إذ الزيادة من الثقة مقبولة.
وقال ابن حجر في التلخيص الحبير ٢/ ١٠٤: أعله ابن القطان بالاضطراب، وبالوقف، وبجهالة حال أبي عثمان، وأبيه. ونقل أبو بكر بن العربي عن الدارقطني أنه قال: هذا حديث ضعيف الإسناد، مجهول المتن، ولا يصح في الباب حديث.
(¬١) الأملح: أسود الرأس، أبيض البدن.
معجم مقاييس اللغة ٥/ ٣٤٧ باب الميم واللام وما يثلثهما مادة ملح، فقه اللغة وسر العربية للثعالبي ص ٦٥.
(¬٢) البخاري ٥/ ٢١١٣ كتاب الأضاحي، باب من ذبح الأضاحي بيده، رقم ٥٢٣٨، ومسلم ٣/ ١٥٥٦ كتاب الأضاحي، باب استحباب الضحية وذبحها مباشرة بلا توكيل، والتسمية والتكبير رقم ١٩٦٦.
عن أنس بن مالك -رضي الله عنه- بلفظ: "ضحى النبي -صلى الله عليه وسلم- بكبشين أملحين، فرأيته واضعًا قدمه على صحافهما يسمي، ويكبر، فذبحهما بيده".','3','370',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1046,'وفي فرضه عند العجز الدَّائِم إلى الموت،
<hr><span data-type="title">منحة السلوك</span>

النفلِ أوسع (¬١).
قوله: وفي فرضه.
أي: تجوز النيابة في فرض الحج عند العجز الدائم إلى الموت (¬٢)؛ لأنه فرض العمر، فيعتبر عجز مستوعب لبقية العمر، ليقع به اليأس عن الأداء بالبدن، حتى لو حجَّ عن نفسه وهو مريض، يكون مراعى، فإن مات به أجزأه، وإن تعافى بطل (¬٣). وكذا لو أحجَّ عن نفسه وهو محبوسٌ (¬٤).
ثم الصحيح من المذهب فيمن حجَّ عن غيره: أن أصل الحج ينفع عن المحجوج عنه (¬٥)؛ لقوله -صلى الله عليه وسلم- لرجل: "حجَّ عن أبيك، واعتمر" رواه أبو داود، والنسائي، والترمذي وقال: حديث حسن صحيح (¬٦).
_________
(¬١) الهداية ١/ ١٩٩، العناية ٣/ ١٤٥، كنز الدقائق ٢/ ٨٥، تبيين الحقائق ٢/ ٨٥، شرح فتح القدير ٣/ ١٤٥.
(¬٢) وفاقًا للثلاثة.
كنز الدقائق ٢/ ٨٥، الهداية ١/ ١٩٩، شرح فتح القدير ٣/ ١٤٥، تبيين الحقائق ٢/ ٨٥، الخرشي على خليل ٢/ ٢٩٥، مختصر خليل ص ٧٨، منهج الطلاب ٢/ ٣٨٨، فتح الوهاب ٢/ ٣٨٨، الإقناع للحجاوي ٢/ ٣٩٠، الروض المربع ص ١٨٩.
(¬٣) تبيين الحقائق ٢/ ٨٥، شرح فتح القدير ٣/ ١٤٥، الهداية ١/ ١٩٩.
(¬٤) شرح فتح القدير ٣/ ١٤٦، تبيين الحقائق ٢/ ٨٥.
(¬٥) وفاقًا للثلاثة.
تبيين الحقائق ٢/ ٨٥، شرح فتح القدير ٣/ ١٤٦، الخرشي على خليل ٢/ ٣٢٩٥، مختصر خليل ص ٧٨، منهج الطلاب ٢/ ٣٨٨، فتح الوهاب ٢/ ٣٨٨، كشاف القناع ٢/ ٣٩١، هداية الراغب ص ٢٠٨.
(¬٦) أبو داود ٢/ ١٦٢ كتاب المناسك، باب الرجل يحج عن غيره رقم ١٨١٠، والنسائي ٥/ ١١٧ كتاب مناسك الحج، باب العمرة عن الرجل الذي لا يستطيع ٥/ ١١٧ =','3','371',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1047,'ودم القران على المأمور، ودم الإحصار على الآمِر.
<hr><span data-type="title">منحة السلوك</span>

قوله: ودم القران على المأمور.
لأنه واجب شكرًا لما وفقه الله بين الحج، وبين النسكين. والمأمور هو المختص بهذه النعمة (¬١).
قوله: ودم الإحصار على الآمر.
لأنه هو الذي أدخله في هذه العهدة، فيجب عليه تخليصه (¬٢).
_________
= رقم ٢٦٣٧، والترمذي ٣/ ٢٩٦ كتاب الحج، باب ما جاء في الحج عن الشيخ الكبير والميت رقم ٩٣٠، ورواه أيضًا ابن ماجه ٢/ ٩٧٠ كتاب المناسك، باب الحج عن الحي إذا لم يستطع رقم ٢٩٠٦، والطيالسي ص ١٤٧ رقم ١٠٩١، وأحمد ٤/ ١٠، وابن خزيمة ٤/ ٤٣٥ كتاب المناسك، باب العمرة عن الذي لا يستطيع العمرة من الكبر رقم ٣٠٤٠، وابن الجارود في المناسك ص ١٣٢ رقم ٥٠٠، وابن حبان ٩/ ٣٠٤ كتاب الحج، باب ذكر الأمر بالعمرة عمن لا يستطيع ركوب الراحلة رقم ٣٩٩١، والطبراني في الكبير ١٩/ ٢٠٣ رقم ٤٥٧، والدارقطني ٢/ ٢٨٣ كتاب الحج، باب المواقيت رقم ٢٠٩، والحاكم ١/ ٤٨١ كتاب المناسك، والبيهقي ٤/ ٣٥٠ كتاب الحج، باب من قال بوجوب العمرة، وابن سعد في الطبقات ٥/ ٥١٨، وابن جرير في التفسير رقم ٣٢٢٣. عن أبي رزين العقيلي أنه قال: يا رسول الله، إن أبي شيخ كبير، لا يستطيع الحج، ولا العمرة، ولا الظعن، قال: "حج عن أبيك، واعتمر".
قال الحاكم: صحيح على شرط الشيخين ولم يخرجاه، وسكت عنه الذهبي.
وقال الدارقطني في السنن ٢/ ٢٨٣: كلهم ثقات.
وقال الترمذي ٣/ ٢٩٦: حسن صحيح.
(¬١) وعند المالكية، والشافعية، والحنابلة: على الآمر.
الاختيار ١/ ١٧١، كنز الدقائق ٢/ ٨٦، الهداية ١/ ١٩٩، تبيين الحقائق ٢/ ٨٦، شرح الزرقاني على خليل ٢/ ٢٤١، حاشية الدسوقي ٢/ ١٤، أسنى المطالب ١/ ٤٥٤، مغني المحتاج ١/ ٥١٧، كشاف القناع ٢/ ٣٩٨، شرح منتهى الإرادات ٢/ ٧.
(¬٢) وفاقًا للمالكية، والشافعية، والحنابلة.
الهداية ١/ ١٩٩، العناية ٣/ ١٥٢، الاختيار ١/ ١٧١، تبيين الحقائق ٢/ ٨٦، الشرح =','3','372',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1048,'و<span data-type="title" id=toc-522>الهدي:</span> من الإبل، والبقر، والغنم. والعيب مانع كالأُضحية.
<hr><span data-type="title">منحة السلوك</span>


[الهدى]
قوله: والهدي من الإبل والبقر والغنم.
وهو مجمع عليه (¬١). والهدي: ما يهدى من النعم إلى الحرم (¬٢).
قوله: والعيب مانع كالأُضحية.
أي: كما يمنع في الأضحية، والذي يمنع فيهما هو العوراء، والعرجاء التي لا تمشي إلى المنسك، والعجفاء التي لا تنقي (¬٣)، ومقطوعة الأذن، والذنب. ولا يمنع الجماء (¬٤)، والخصي (¬٥)، والثولاء (¬٦)،
_________
= الكبير للدردير ٢/ ١٤، حاشية البناني ٢/ ٢٤١، أسنى المطالب ١/ ٤٥٦، روض الطالب ١/ ٤٥٦، كشاف القناع ٢/ ٣٩٨، شرح منتهى الإرادات ٢/ ٦.
(¬١) مراتب الإجماع لابن حزم ص ٤٢.
(¬٢) الهداية ١/ ٢٠١، تبيين الحقائق ٢/ ٨٩، الاختيار ١/ ١٧٢.
(¬٣) أي: الهزيلة التي لا مخ لها.
مختار الصحاح ص ١٧٤ مادة ع ج ف، القاموس المحيط ٣/ ١٦٢ مادة ع ج ف، فقه اللغة وسر العربية للثعالبي ص ٦٤، طلبة الطلبة ص ٢١٧، الدر النقي ٣/ ٤٩١.
(¬٤) الشاة الجماء: التي لا قرن لها.
مختار الصحاح ص ٤٧ مادة ج م م، فقه اللغة وسر العربية للثعالبي ص ٥٩، القاموس المحيط ١/ ٥٣٣ مادة ج م م، لسان العرب ١١٢/ ١٠٨ مادة ج م م، طلبة الطلبة ص ١٧، المطلع على أبواب المقنع ص ٢٠٥.
(¬٥) الذي سل أنثياه، وبقي ذكره.
معجم مقاييس اللغة ٢/ ١٨٨ باب الخاء والصاد وما يثلثهما مادة "خصى"، مختار الصحاح ص ٧٥ مادة خ ص ي، المغرب ص ١٤٧ مادة الخصية، أنيس الفقهاء ص ١٦٦، طلبة الطلبة ص ١٠١.
(¬٦) الثول: داء يصيب الشاة فترتخي أعضاؤها، يشبه الجنون.
معجم مقاييس اللغة ١/ ٣٩٦ باب الثاء والواو وما يثلثهما مادة ثول، المغرب ص ٧٣ مادة الثولاء، مختار الصحاح ص ٣٨ مادة ث ول، المصباح المنير ٨٨١ مادة ثول.','3','373',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1049,'ويجوز الأكل من هدي التطوع، والمتعة، والقران خاصة.
ويتوقت دم المتعةِ، والقران خاصةً، بيوم النحر،
<hr><span data-type="title">منحة السلوك</span>

والجرباء (¬١) (¬٢).
قوله: ويجوز الأكل من هدي التطوع، والمتعة، والقران خاصة (¬٣).
لقوله تعالى: {فَإِذَا وَجَبَتْ جُنُوبُهَا فَكُلُوا مِنْهَا} [الحج: ٣٦]. ولا يجوز الأكل من هدي الجنايات؛ لأنها دماء كفارة (¬٤).

<span data-type="title" id=toc-523>[زمن دم المتعة والقران]</span>
قوله: ويتوقَّت دم المتعة، والقران خاصةً بيوم النحر (¬٥).
_________
(¬١) وفاقًا للمالكية، والشافعية، والحنابلة. إلا أن الشافعية يرون: أن مشروطة الأذن تجزيء، وكذا مكسورة كل القرن، أو بعضه.
الاختيار ١/ ١٧٣، الكتاب ١/ ٢٢٢، بداية المبتدي ١/ ٢٠١، الهداية ١/ ٢٠١، كنز الدقائق ٢/ ٨٩، القوانين الفقهية ص ٩٣ - ١٢٧، الشرح الصغير ١/ ٣٠٧ - ٣٠٩، أسنى المطالب ١/ ٥٣٠، المهذب ١/ ٢٣٥، غاية المنتهى ٢/ ٤٦٥، الإفصاح ١/ ٣٠٨.
(¬٢) الجرب: خلط غليظ يحدث تحت الجلد، من مخالطة البلغم الملح للدم، يكون معه بُثُور، وربما حصل معه هزال؛ لكثرته.
مختار الصحاح ص ٤٢ مادة ج ر ب، المصباح المنير ١/ ٩٥ مادة جرب، معجم مقاييس اللغة ١/ ٤٤٩ باب الجيم والراء وما يثلثهما مادة جرب، القاموس المحيط ١/ ٤٤٦ مادة ج ر ب.
(¬٣) الكتاب ١/ ٢٢٣، بداية المبتدي ١/ ٢٠١، الاختيار ١/ ١٧٣، الهداية ١/ ٢٠١.
(¬٤) وإليه ذهب الحنابلة.
وعند المالكية: يأكل من الهدي كله، إلا من جزاء الصيد، وفدية الأذى، ونذر المساكين، وهدي التطوع إذا عطب قبل أن يبلغ محله.
وذهب الشافعية: إلى أنه لا يجوز الأكل من هدي المتعة والقران، ولا من هدي الجنايات.
الهداية ١/ ٢٠١، تبيين الحقائق ٢/ ٥٩، الاختيار ١/ ١٧٣، التلقين ص ٧٠، المعونة على التفريع ١/ ٣٣٢، روض الطالب ١/ ٥٣٠، تحفة المحتاج ٤/ ١٩٨، المحرر ١/ ٢٥١، العمدة لابن قدامة ص ٤١، الروض المربع ص ٢١٨.
(¬٥) وذهب المالكية، والحنابلة: إلى أن دم المتعة، والقران يختصان بيوم النحر يوم العيد، ويومين بعده. =','3','374',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1050,'ويجوز التصدق بها على مساكين الحرم، وغيرهم.
<hr><span data-type="title">منحة السلوك</span>

لقوله تعالى: {فَكُلُوا مِنْهَا وَأَطْعِمُوا الْبَائِسَ الْفَقِيرَ (٢٨) ثُمَّ لْيَقْضُوا تَفَثَهُمْ وَلْيُوفُوا نُذُورَهُمْ وَلْيَطَّوَّفُوا بِالْبَيْتِ الْعَتِيقِ} الآية [الحج: ٢٨، ٢٩].
وقضاء التفث، والطواف، يختص في الحرم بأيام النحر، فكذا الذبح؛ ليكون الكلام مسرودًا على نسقٍ واحد (¬١).
ويختص جميع دم يجب على الحاج بالحرم؛ لقوله تعالى: {هَدْيًا بَالِغَ الْكَعْبَةِ} [المائدة: ٩٥] (¬٢).
قوله: ويجوز التصدق بها.
أي: بالدماء على مساكين الحرم، وغيرهم من الفقراء؛ لأنه سد خلَّة المحتاج، ولا فرق فيه بينهم وبين غيرهم (¬٣).
_________
= وذهب الشافعية: إلى أن دم المتعة والقران، لا يتوقتان بوقت.
الهداية ١/ ٢٠١، الاختيار ١/ ١٧٣، بداية المبتدي ١/ ٢٠١، الشرح الصغير ١/ ٣١٠، الشرح الكبير للدسوقي ٢/ ٨٦، الذخيرة ٣/ ٣٧١، السراج الوهاج ص ١٧١، المهذب ١/ ٢٣٥، الروض المربع ص ٢١٧، حاشية المقنع ١/ ٤٧٦.
(¬١) الهداية ١/ ٢٠٢، شرح فتح القدير ٣/ ١٦٢، تبيين الحقائق ٢/ ٥٩.
(¬٢) وإليه ذهب الشافعية. وكذا عند الحنابلة: سوى فدية الأذى واللبس ونحوها، وكل محظور فعله خارج الحرم، وكذا دم الإحصار حيث وجد سببه، من حل أو حرم، ويجزيء بالحرم أيضًا.
وقال المالكية: الفدية نسك ينحرها حيث شاء، وما عدا ذلك فإنه هدي ينحره بمكة، ويختص بأهل الحرم.
بداية المبتدي ١/ ٢٠٢، الكتاب ١/ ١٢٤، الهداية ١/ ٢٠٢، تبيين الحقائق ٢/ ٥٩، الشرح الكبير للدسوقي ٢/ ٦٧، جواهر الإكليل ١/ ١٩٢، حاشية ابن قاسم العبادي على تحفة المحتاج ٤/ ١٨، السراج الوهاج ص ١٧١، الروض المربع ص ١٩٩، التسهيل ص ٩٨.
(¬٣) وإليه ذهب المالكية. =','3','375',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1051,'والله أعلم.
<hr><span data-type="title">منحة السلوك</span>

وقال الشافعي: لا يجوز التصدق على غيرهم (¬١). والله أعلم بالصواب.
_________
= الكتاب ١/ ١٢٤، بداية المبتدي ١/ ٢٠٢، الهداية ١/ ٢٠٢، تبيين الحقائق ٢/ ٩٠، الشرح الكبير للدسوقي ٢/ ٦٧، جواهر الإكليل ١/ ١٩٢.
(¬١) إلا دم الإحصار، فيذبح ويفرق حيث أحصر.
وعند الحنابلة: كل هدي، أو إطعام، فهو لمساكين الحرم؛ لأن القصد التوسعة عليهم، وفدية الأذى، واللبس، والطيب، ونحوها، وكل محظور فعله خارج الحرم، ودم الإحصار، حيث وجد سببه، من حل، أو حرم. وله تفرقتها في الحرم أيضًا.
روض الطالب ١/ ٥٣١، حاشية قليوبي ٢/ ١٤٦، كشاف القناع ٢/ ٤٦٠، المستوعب ٤/ ٣٤٣، الروض المربع ص ١٩٩.','3','376',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1052,'<span data-type="title" id=toc-524>كتاب الجهاد</span>
هو فرض كفايةٍ، وإن لم يبدأ الكفار.
<hr><span data-type="title">منحة السلوك</span>

كتاب الجهاد
أقولُ: لما فرغ من بيان الحج، شرع في بيان الجهاد على التناسب الذي في خطبة الكتاب.
ويُسمى هذا كتاب السير أيضًا (¬١)، وهو مصدر جاهد (¬٢).

<span data-type="title" id=toc-525>[حكمه]</span>
قوله: هو. أي: الجهاد فرض كفاية، وإن لم يبدأ الكفار بالقتال (¬٣)؛ لقوله تعالى: {وَقَاتِلُوا الْمُشْرِكِينَ كَافَّةً} [التوبة: ٣٦]، فإذا حصل من البعض سقط عن الباقين، كصلاة الجنازة، ودفن الميت، ورد السلام (¬٤)، وكانت
_________
(¬١) والجهاد أعم وأغلب من السير؛ لاشتماله على الدعوة إلى الدين، وقتالهم إن لم يقبلوا.
ويقال أيضًا: كتاب المغازي، وهو أيضًا أعم. وهو: قصد العدو للقتال خُصَّ في عرف أهل المغازي بقتال الكفار.
شرح فتح القدير ٥/ ٤٣٥، العناية ٥/ ٤٣٤، تبيين الحقائق ٣/ ٢٤٠.
(¬٢) الجهد: الطاقة والجَهْدُ المشقة.
وشرعًا: هو قتال الكفار خاصة.
مختار الصحاح ص ٤٨ مادة ج هـ د، القاموس المحيط ١/ ٥٤٥ مادة ج هـ د، المغرب ص ٩٧ مادة جهده، أنيس الفقهاء ص ١٨١، الدر النقي ٣/ ٧٦٥، العناية ٥/ ٤٣٧.
(¬٣) وفاقًا للثلاثة.
قال في الإفصاح ٢/ ٢٧٣: واتفقوا على أن الجهاد فرض على الكفاية، إذا قام به قوم سقط عن باقيهم، ولم يأثموا بتركه.
الاختيار ٤/ ١١٧، تحفة الفقهاء ٣/ ٢٩٤، المختار ٤/ ١١٧، الهداية ٢/ ٤٢٦، متن الرسالة ص ٩٦، أسهل المسالك ص ١٣٥، التذكرة ١٥٤، متن الزبد ص ٦٦، العمدة لابن قدامة ص ١١٢، شرح الزركشي ٦/ ٤٢٤.
(¬٤) ولأن في اشتغال الكل قطع مادة الجهاد من الكراع، والسلاح، فينقطع الجهاد بسبب =','3','377',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1053,'ولا جهاد على عبدٍ، وامرأةٍ، وأعمى، ومُقْعَد، وأقْطَع، إلَّا إذا هَجَمَ العدوُّ.
<hr><span data-type="title">منحة السلوك</span>

الصحابة يغزو بعضهم، ويقعد البعض. ولو كان فرض عين لما قعدوا (¬١).

<span data-type="title" id=toc-526>[المعذورون عن الجهاد]</span>
قوله: ولا جهاد على عبدٍ، وامرأة، وأعمى، ومقعد، وأقطع (¬٢).
لقوله تعالى: {لَيْسَ عَلَى الْأَعْمَى حَرَجٌ} [الفتح: ١٧]، الآية نزلت في أصحاب الأعذار، حين اهتموا بالخروج مع النبي -صلى الله عليه وسلم- لما نزلت آية التخلف (¬٣).
قوله: إلا إذا هَجَم العدو.
فحينئذ يكون الجهاد فرض عينٍ، تخرج المرأة، والعبد، بلا إذن زوجها، وسيده (¬٤).
_________
= ذلك، فينبغي أن يتولى بعضهم الجهاد، وبعضهم التجارة، والحرث، والحرف التي تقوم بها المصالح والتقوية؛ فوجب على الكفاية.
تبيين الحقائق ٣/ ٢٤١، الهداية ٢/ ٤٢٦، تحفة الفقهاء ٣/ ٢٩٤، الكتاب ٤/ ١١٥.
(¬١) تبيين الحقائق ٣/ ٢٤١، العناية ٥/ ٤٤٠، تحفة الفقهاء ٣/ ٢٩٤، الاختيار ٤/ ١١٧، مختصر الوقاية في مسائل الهداية، لصدر الشريعة عبيد الله بن مسعود ص ١٧٣.
(¬٢) وفاقًا للثلاثة.
بداية المبتدي ٢/ ٤٢٦، الكتاب ٤/ ١١٥، الهداية ٢/ ٤٢٦، المختار ٤/ ١١٧، مختصر خليل ص ١١١، منح الجليل ٣/ ١٣٦، الوجيز ٢/ ١٨٧، أنوار المسالك، في شرح عمدة السالك، وعدة الناسك ص ٤٥٣، شرح منتهى الإرادات ٢/ ٩١، كشاف القناع ٣/ ٣٥.
(¬٣) لباب النقول في أسباب النزول ص ٦٠، جامع البيان في تأويل آي القرآن ٩/ ٣٥١.
(¬٤) وفاقًا للثلاثة.
الاختيار ٤/ ١١٨، بداية المبتدي ٢/ ٤٢٦، الهداية ٢/ ٤٢٦، تحفة الفقهاء ٣/ ٢٩٤، المختار ٤/ ١١٨، الشرح الصغير ١/ ٣٥٦، بلغة السالك ١/ ٣٥٦، المنهاج ٤/ ٢٩٠، زاد المحتاج ٤/ ٢٩٠، الإقناع للحجاوي ٣/ ٣٧، منتهى الإرادات ٢/ ٩٢.','3','378',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1054,'ويقدم طلب الإسلام، ثم الجزية،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-527>[ما يُقدم قبل القتال]</span>
قوله: ويقدم طلب الإسلام.
يعني: إذا حاصر أهل الإسلام الكفار، يدعونهم إلى الإسلام أولًا (¬١)؛ لما روي عن ابن عباس -رضي الله عنهما- أنه قال: "ما قاتل رسول الله -صلى الله عليه وسلم- قومًا قط، إلا دعاهم" رواه أحمد (¬٢).
قوله: ثم الجزية (¬٣).
يعني: إذا لم يقبلوا الإسلام يدعونهم إلى الجزية (¬٤)؛ لما روي أنه -صلى الله عليه وسلم-: "كان إذا أمَّر أميرًا على جيش، أو سريةٍ (¬٥)، أمره به" في حديث فيه
_________
(¬١) تحفة الفقهاء ٣/ ٢٩٤، الاختيار ٤/ ١١٨، الهداية ٢/ ٤٢٦، المختار ٤/ ١١٨، بداية المبتدي ٢/ ٤٢٦.
(¬٢) ١/ ٢٣١، ورواه أيضًا عبد الرزاق ٥/ ٢١٨ كتاب الجهاد، باب دعاء العدو رقم ٩٤٢٧، وابن أبي شيبة ٦/ ٤٧٦ كتاب الجهاد باب في دعاء المشركين قبل أن يقاتلوا رقم ٣٣٠٦٧، والدارمي ٢/ ٦٦٥ كتاب السير باب في الدعوة إلى الإسلام قبل القتال رقم ٢٣٥٣، وأبو يعلى ٤/ ٤٦٢ رقم ٢٥٩١، والطحاوي في الشرح ٣/ ٢٠٧ كتاب السير باب الإمام يريد قتال أهل الحرب هل عليه قبل ذلك أن يدعوهم أم لا؟، والطبراني في الكبير ١١١٥٩، والحاكم ١/ ١٥ كتاب الإيمان، والبيهقي ٩/ ١٠٧ كتاب السير باب دعاء من لم تبلغه الدعوة من المشركين، وأبو يوسف في الخراج ٢٠٧.
قال الحاكم: هذا حديث صحيح من حديث الثوري، ولم يخرجاه.
وذكره الهيثمي في المجمع ٥/ ٣٠٤ وعزاه للطبراني، وأبي يعلى، وقال: رجال أحدهما رجال الصحيح.
(¬٣) الجزية: فعلة من الجزاء. وهي: المال الذي يعقد للكتابي من أهل الذمة. وجمعها جزى.
القاموس المحيط ١/ ٤٩٠ مادة ج ز ى، المغرب ص ٨١ مادة جزأت، مختار الصحاح ص ٤٤، مادة ج ز ي، أنيس الفقهاء ص ١٨٢، لغة الفقه ص ٣١٨، المطلع على أبواب المقنع ص ٢١٨.
(¬٤) بداية المبتدي ٢/ ٤٢٧، مختصر الطحاوي ص ٢٨١، المختار ٤/ ١١٨، الهداية ٢/ ٤٢٧.
(¬٥) السرية: هي قطعة من الجيش، يبلغ أقصاها أربعمائة، وقيل: من خمسة أنفس إلى =','3','379',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1055,'فإن أبوهما قوتلوا بالسلاح، والمنجنيق، والماء، والنار، وقطع الشجر،
<hr><span data-type="title">منحة السلوك</span>

طول، رواه أحمد، ومسلم، والترمذي وصححه (¬١).
قوله: فإن أبوهما. أي: إن أبوا الإسلام، والجزية، قوتلوا بالسلاح، والمنجنيق (¬٢)، إلى آخره (¬٣).
_________
= ثلاثمائة، أو أربعمائة، تبعث إلى العدو. وجمعها سرايا؛ سموا بذلك: لأنهم خلاصة العسكر، وخيارهم.
القاموس المحيط ٢/ ٥٥٨ مادة س ر ى، مجمل اللغة ص ٣٨٤ باب السين والراء وما يثلثهما مادة سرو، المصباح المنير ١/ ٢٧٥ مادة سريت، طلبة الطلبة ص ١٦٦، لغة الفقه ص ٣١٨، المطلع على أبواب المقنع ص ٢١٥.
(¬١) أحمد ٤/ ٤٦، ومسلم ٣/ ١٣٥٧ كتاب الجهاد، باب تأمير الإمام الأمراء على البعوث، ووصيته إياهم بآداب الغزو وغيرها رقم (٣/ ١٧٣١)، والترمذي ٥/ ٣٣٨ كتاب السير، باب ما جاء في وصية النبي -صلى الله عليه وسلم- في القتال رقم ١٦١٧.
وتمامه: عن بريدة -رضي الله عنه- قال: كان رسول الله -صلى الله عليه وسلم- إذا أمر أميرًا على جيش، أو سرية، أوصاه في خاصته بتقوى الله، ومن معه من المسلمين خيرًا، ثم قال: "اغزوا باسم الله، في سبيل الله، قاتلوا من كفر بالله، اغزوا ولا تغلوا، ولا تغدروا، ولا تمثلوا، ولا تقتلوا وليدًا، وإذا لقيت عدوك من المشركين فادعهم إلى ثلاث خصال، أو خلال، فأيتهن ما أجابوك، فاقبل منهم وكف عنهم، ثم ادعهم إلى الإسلام، فإن أجابوك فاقبل منهم وكف عنهم، ثم ادعهم إلى التحول من دارهم إلى دار المهاجرين، وأخبرهم أنهم إن فعلوا ذلك فلهم ما للمهاجرين، وعليهم ما على المهاجرين، فإن أبوا أن يتحولوا منها، فأخبرهم أنهم يكونون كأعراب المسلمين، يجري عليهم حكم الله الذي يجري على المؤمنين، ولا يكون لهم في الغنيمة والفيء شيء، إلا أن يجاهدوا مع المسلمين، فإن هم أبوا فسلهم الجزية، فإن هم أجابوك فاقبل منهم وكف عنهم، فإن هم أبوا فاستعن بالله وقاتلهم".
(¬٢) بكسر الميم: آلة ترمى بها الحجارة. وهي معربة.
القاموس المحيط ٤/ ٢٨٦ مادة المنجنيق، مختار الصحاح ص ٤٥ مادة ج ق، المصباح المنير ٢/ ٥٦٤ مادة المنجنيق.
(¬٣) وفاقًا للمالكية، والشافعية، والحنابلة.
المختار ٤/ ١١٩، مختصر الطحاوي ص ٢٨٢، الاختيار ٤/ ١١٩، بداية المبتدي =','3','380',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1056,'وإفساد الزرع. ويرمون مقصودين (¬١)، ولو تترسُوا بالمسلمين.
ويكرهُ إخراج النساء، والمصاحف إن خيف عليهما.
<hr><span data-type="title">منحة السلوك</span>

لما روي أنه -صلى الله عليه وسلم- كان يقول في وصية أمراء الجيش: فإن هم أبوا فسلهم الجزية، فإن هم أجابوكَ فاقبل منهم، وكُفَّ عنهم، فإن هم أبوا فاستعن بالله، وقاتلهم" رواه مسلم (¬٢).
قوله: ويرمون قاصدين الكفار، ولو تترسوا بالمسلمين (¬٣).
أي: بالمسلمين الذين هم أُسارى عندهم؛ لأن دفع الضرر العام بالضرر الخاص جائز (¬٤)، وفي بعض النسخ (¬٥): "ويرمون مقصودين" فإن صح هذا، فوجهه: أن يقرأ "يرمون" على صيغة المبني للمفعول، ويكون "مقصودين" حالًا من الضمير الذي في "يرمون".

<span data-type="title" id=toc-528>[ما يكره ويحرم في القتال]</span>
قوله: ويكره إخراج النساء، والمصاحف إن خيف عليهما.
_________
= ٢/ ٤٢٧، جواهر الإكليل ١/ ٢٥٢، سراج السالك ٢/ ٣٢٤٩، التنبيه ص ٢٣٢، روض الطالب ٤/ ١٨٨، كشاف القناع ٣/ ٤١، مختصر الخرقي ص ١٢٨.
(¬١) في ب، جـ بزيادة "الكفار".
(¬٢) سبق تخريجه في ٣/ ٣٨٠.
(¬٣) التترس: التستر.
القاموس المحيط ١/ ٣٦٥ مادة ت ر س، مختار الصحاح ص ٣٢ مادة ت ر س، المصباح المنير ١/ ٧٤ مادة التُّرسُ، المطلع على أبواب المقنع ص ٢١٢.
(¬٤) وفاقًا للثلاثة.
قال في الإفصاح ٢/ ٢٧٥: واتفقوا على أنه إذا تترس المشركون بالمسلمين، جاز لبقية المسلمين الرمي، ويقصدون المشركين.
الهداية ١/ ٤٢٨، تحفة الفقهاء ٣/ ٢٩٥، مختصر الطحاوي ص ٢٨٤، بداية المبتدي ٢/ ٤٢٨، التاج والإكليل ١/ ٣٥١، منح الجليل ٣/ ١٥٠، حاشية الشرواني ٩/ ٢٤٢، حاشية ابن قاسم العبادي الشافعي ٩/ ٢٤٠، المقنع ١/ ٤٨٨، حاشية المقنع ١/ ٤٨٨.
(¬٥) كما في نسخة أ، ب، جـ، د.','3','381',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1057,'ويحرم الغلول،
<hr><span data-type="title">منحة السلوك</span>

لما فيه من تعريض المصحف على الاستخفاف، وتعريض المرأة على الضياع والفضائح، وإن لم يخف عليهما فلا بأس بإخراج العجائز للخدمة، من الطبخ، والخبز، ومعالجة المرضى، وغير ذلك.
وأما الشواب منهن، فقرارهن في البيوت أسلم (¬١).
والأولى: أن لا يخرجن أصلًا، فإن تحققت الضرورة، تخرج الإماء دون الحرائر (¬٢).
قوله: ويحرم الغلول (¬٣)؛ لأنه -صلى الله عليه وسلم- نهى عنه (¬٤)، وهو الخيانة (¬٥)،
_________
(¬١) بداية المبتدي ٢/ ٤٢٨، الكتاب ٤/ ١١٨، الهداية ٢/ ٤٢٨، تبيين الحقائق ٣/ ٢٤٤.
(¬٢) وعند المالكية: يحرم السفر بالمرأة في أرض الحرب إذا كانت مع غير جيش آمن، وأما معه فإنه يجوز السفر بها إلى أرض الحرب؛ لأنها تنبه عن نفسها.
وعند الشافعية: يصطحب الإمام معه النساء للمداواة، والسقي.
وعند الحنابلة: يمنع النساء، إلا طاعنة في السن لمصلحة، كسقي الماء، ومعالجة الجرحى. ويحرم السفر بالمصحف إلى دار الحرب.
شرح فتح القدير ٥/ ٤٥١، الهداية ٢/ ٤٢٩، الكتاب ٤/ ١١٨، تبيين الحقائق ٣/ ٢٤٤، مختصر خليل ص ١١٢، الخرشي على خليل ٣/ ١١٥، روض الطالب ٤/ ١٧٦، أسنى المطالب ٤/ ١٧٦، الإقناع للحجاوي ١/ ١٣٦، ٣/ ٦٢، منتهى الإرادات ١/ ٧٣، ٢/ ١٠٣.
(¬٣) وفاقًا للثلاثة.
شرح فتح القدير ٥/ ٤٥١، الهداية ٢/ ٤٢٩، المبسوط ١٠/ ٥، تبيين الحقائق ٣/ ٢٤٤، الكافي لابن عبد البر ص ٢١٢، أقرب المسالك ص ٦٥، روض الطالب ٤/ ١٧٦، أسنى المطالب ٤/ ١٧٦، المحرر ٢/ ١٧٨، السلسبيل ٢/ ١١١.
(¬٤) سبق تخريجه في ٣/ ٣٨٠.
(¬٥) الغلول في المغنم: هو أن يخفي الشيء فلا يُردّ إلى القسم، كأن صاحبه قد غله بين ثيابه. وقيل: هو الخائن.
معجم مقاييس اللغة ٤/ ٣٧٥ باب مادة غلَّ، مختار الصحاح ص ٢٠٠ مادة غ ل ل، =','3','382',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1058,'والمثلة، والغدرُ، وقتل المجنون، والصبيّ، والمرأةِ
<hr><span data-type="title">منحة السلوك</span>

وكذلك يحرم المثلة (¬١)، والغدر (¬٢) (¬٣)؛ لقوله -صلى الله عليه وسلم-: "لا تمثلوا، ولا تغدروا، ولا تقتلوا وليدًا" رواه ابن ماجه (¬٤).
والغدر: الخيانة أيضًا، إلا أنَّ الغلول في المغنم، والغدر أعم (¬٥).
قوله: وقتل المجنون.
أي: يحرم قتل المجنون، والصبي، والمرأة (¬٦)، إلى آخره (¬٧)؛ لما روي أنه -صلى الله عليه وسلم- "نهى عن قتل النساء، والصبيان" رواه أحمد والبخاري، ومسلم، وجماعة آخرون (¬٨)
_________
= المصباح المنير ٢/ ٤٥١ مادة الغِلُّ، المغرب ص ٣٤٣ مادة الغُلة، طلبة الطلبة ص ١٦٦، المطلع ص ١١٨.
(¬١) بالضم. ومَثَّلَ بالقتيل: جدع أنفه، أو أذنه، أو شيئًا من أطرافه، تنكيلًا به.
مختار الصحاح ص ٢٥٦ مادة م ث ل، القاموس المحيط ٤/ ٢٠٣ مادة م ث ل، المصباح المنير ٢/ ٥٦٤ مادة المِثل، طلبة الطلبة ص ١٦٧، المطلع على أبواب المقنع ص ٣١٥.
(¬٢) وهو ترك الوفاء.
القاموس المحيط ٣/ ٣٧٢ مادة غ د ر، مختار الصحاح ص ١٩٦ مادة غ د ر، المصباح المنير ٢/ ٤٤٣ مادة غدر، طلبة الطلبة ص ١٦٧.
(¬٣) الاختيار ٤/ ١٢٠، المبسوط ١٠/ ٥، الهداية ٢/ ٤٢٩.
(¬٤) ٢/ ٩٥٣ كتاب الجهاد باب وصية الإمام رقم ٢٨٥٨، ورواه أيضًا مسلم ٣/ ١٣٥٧ كتاب الجهاد والسير، باب تأمير الإمام الأمراء على البعوث، ووصيته إياهم بآداب الغزو وغيرها رقم ١٧٣١ عن بريدة -رضي الله عنه-.
(¬٥) تبيين الحقائق ٣/ ٢٤٤، شرح فتح القدير ٥/ ٤٥١، الهداية ٢/ ٤٢٩، الاختيار ٤/ ٢١٠.
(¬٦) الاختيار ٤/ ١٢٠، مختصر الطحاوي ص ٢٨٣، الهداية ٢/ ٤٢٩، تبيين الحقائق ٣/ ٢٤٤.
(¬٧) وتمامه: غير الملكة، والهرم، والأعمى، والمقعد.
(¬٨) أحمد ٢/ ٢٢، والبخاري ٣/ ١٠٩٨ كتاب الجهاد، باب قتل النساء في الحرب رقم ٢٨٥٢، ومسلم ٣/ ١٣٦٤ رقم ١٧٤٤ كتاب الجهاد والسير، باب تحريم قتل النساء والصبيان في الحرب، والبيهقي ٩/ ٧٧ كتاب السير، باب النهي عن قصد النساء =','3','383',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1059,'غير الملكَةِ،
<hr><span data-type="title">منحة السلوك</span>

وعن أنس -رضي الله عنه- أن رسول الله -صلى الله عليه وسلم- قال: "انطلقوا بسم الله، وعلى ملة رسول الله، لا تقتلوا شيخًا فانيًا، ولا طفلًا، ولا صغيرًا، ولا امرأةً" الحديث رواه أبو داود (¬١).
وقيد بقوله: غير الملكةِ؛ لأنَّ المرأة إذا كانت ملكة تقتل؛ لأن في قتلها كسر شوكتهم (¬٢).
_________
= والولدان بالقتل، وابن أبي شيبة ١٢/ ٣٨١ كتاب الجهاد، من ينهى عن قتله في دار الحرب، والطحاوي في شرح معاني الآثار ٣/ ٢٢٠ كتاب السير، باب ما ينهى عن قتله من النساء والولدان في دار الحرب، وابن ماجه ٢/ ٩٤٨ في كتاب الجهاد، باب الغارة والبيات وقتل النساء والصبيان رقم ٢٨٤١، وأبو داود ٣/ ٥٣ كتاب الجهاد، باب في قتل النساء رقم ٢٦٦٨، والترمذي ٥/ ٢٩٦ كتاب السير، باب ما جاء في النهي عن قتل النساء والصبيان رقم ١٥٦٩، ومالك ٢/ ٤٤٧ كتاب الجهاد، باب النهي عن قتل النساء والولدان في الغزو رقم ٩، والدارمي ٢/ ٦٧١ كتاب السير، باب النهي عن قتل النساء والصبيان رقم ٢٣٧٠، وابن الجارود ص ٢٦١ باب النهي عن قتل النساء والولدان رقم ١٠٤٣، وابن حبان في صحيحه ١١/ ١٠٧ كتاب السير، باب ذكر الزجر عن قتل نساء أهل الحرب في القصد رقم ٤٧٨٥. عن ابن عمر -رضي الله عنهما- أن رسول الله -صلى الله عليه وسلم-: "رأى في بعض مغازيه امرأة مقتولة، فنهى عن قتل النساء والصبيان".
(¬١) ٣/ ٣٧ كتاب الجهاد باب في دعاء المشركين رقم ٢٦١٤، ورواه أيضًا بن أبي شيبة ٦/ ٤٨٣ كتاب الجهاد، باب من ينهى عن قتله في دار الحرب رقم ٧٣١١٨، وابن حزم في المحلى ٧/ ٤٧٢ من طريق حسن بن صالح عن خالد بن الفِزْر عن أنس بن مالك -رضي الله عنه- "أن رسول الله -صلى الله عليه وسلم- قال ... "، ورواه مسلم ٣/ ١٣٥٧ كتاب الجهاد والسير، باب تأمير الإمام الأمراء على البعوث رقم ١٧٣١ عن بريدة بن الحصيب -رضي الله عنه- قال: كان رسول الله -صلى الله عليه وسلم- إذا أمر أميرًا على جيش، أو سرية، أوصاه في خاصته بتقوى الله، ومن معه من المسلمين خيرًا، ثم قال: "اغزوا باسم الله، في سبيل الله، قاتلوا من كفر بالله، اغزوا ولا تغلوا، ولا تغدروا، ولا تمثلوا، ولا تقتلوا وليدًا ... ".
(¬٢) تحفة الفقهاء ٣/ ٢٩٥، شرح فتح القدير ٥/ ٤٥٤، الاختيار ٤/ ١٢٠، مختصر الطحاوي ص ٢٨٣.','3','384',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1060,'والهَرِم، والأعمى، والمقعد، ونحوهم، إلَّا دفعًا لشر قتاله، أو رَأيهِ.
<hr><span data-type="title">منحة السلوك</span>

قوله: والهرم. وهو: الشيخ الفاني (¬١).
قوله: ونحوهم مثل المقطوع إحدى يديه، وإحدى رجليه، أو اليمنى (¬٢).
قوله: إلا دفعًا لشرِّ قتاله.
يعني: إذا كان أحد من هؤلاء مقاتلًا، أو ذا رأي في الحرب يقتل (¬٣)؛ لما قلنا، وقد صحَّ أن رسول الله -صلى الله عليه وسلم- قتل دُريد بن الصمة، وكان ابن مائة وعشرين سنة. وقيل: ابن مائة وستين سنة؛ لأنه كان صاحب رأي وهو أعمى (¬٤).
_________
(¬١) القاموس المحيط ٤/ ٥٠٣ مادة هـ ر م، مختار الصحاح ص ٢٨٩ مادة هـ ر م، فقه اللغة وسر العربية للثعالبي ص ٨٤، المصباح المنير ٢/ ٦٣٧ مادة هرم.
(¬٢) الاختيار ٤/ ١٢٠، تبيين الحقائق ٣/ ٢٤٥، مختصر الطحاوي ص ٢٨٣، شرح فتح القدير ٥/ ٤٥٤، تحفة الفقهاء ٣/ ٢٩٥.
(¬٣) وفاقًا للمالكية، والشافعية، والحنابلة. لأن في قتله كسر شوكتهم، وإزالة ضررهم عن المسلمين.
مختصر الطحاوي ص ٢٨٣، الكتاب ٤/ ١١٩، المختار ٤/ ١٢٠، تحفة الفقهاء ٣/ ٢٩٥، تبيين الحقائق ٣/ ٢٤٥ أسهل المسالك ص ١٣٦، سراج السالك ٢/ ٢٧٣، نهاية المحتاج ٨/ ٦٤، حاشية الشبراملسي ٨/ ٦٤، المبدع ٣/ ٣٢٢، المقنع ١/ ٤٨٧ التسهيل ص ١٩٣.
(¬٤) رواه البخاري ٤/ ١٥٧١ كتاب المغازي، باب غزوة أوطاس رقم ٤٠٦٨، ومسلم ٤/ ١٩٤٣ كتاب فضائل الصحابة، باب من فضائل أبي موسى، وأبي عامر الأشعريين رقم ٢٤٩٨.
عن أبي موسى الأشعري -رضي الله عنه- قال: "لما فرغ النبي -صلى الله عليه وسلم- من حنين، بعث أبا عامر على جش إلى أوطاس، فلقي دريد بن الصمة، فقتل دريد، وهزم الله أصحابه".
واختلف في قاتله، فقيل: ربيعة بن رفيع، وقيل: هو الزبير بن العوام.
فتح الباري ٨/ ٤٢، الروض الأنف ٧/ ١٦٢.','3','385',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1061,'ويكره للمسلم قتل أبيه الكافر، إلا دفعًا كالمسلم.
وللإمام الصلح مجانًا،
<hr><span data-type="title">منحة السلوك</span>

قوله: ويُكره للمسلم قتل أبيه الكافر (¬١).
لقوله تعالى: {وَصَاحِبْهُمَا فِي الدُّنْيَا مَعْرُوفًا} [لقمان: ١٥]، وليست البداية بالقتل من المعروف (¬٢).
قوله: إلَّا دفعًا. استثناء من قوله: "ويُكره". يعني: إذا قصد الأب قتله، ولم يمكنه دفعه إلا بقتله، فله أن يقتله دفعًا (¬٣).
قوله: كالمسلم. يعني: كما يجوز له أن يدفع أباه المسلم بالقتل، إذا قصد الأب قتله، فإذا ثبت في هذه الصورة، ففي الصورة الأولى، أولى وأحرى (¬٤).

<span data-type="title" id=toc-529>[حكم الصلح]</span>
قوله: وللإمام الصلح مجانًا.
_________
(¬١) بداية المبتدي ٢/ ٢٢٩، الجامع الصغير ص ٣١٩، الهداية ٢/ ٢٢٩، شرح فتح القدير ٥/ ٤٥٤، تبيين الحقائق ٣/ ٢٤٥.
(¬٢) ولأنه سبب لإحيائه، فلا يكون هو سببًا لإفنائه.
الهداية ٢/ ٤٤٩، شرح فتح القدير ٥/ ٥٤٥، تبيين الحقائق ٣/ ٢٤٥.
(¬٣) تبيين الحقائق ٣/ ٢٤٥، الهداية ٢/ ٤٢٩، شرح فتح القدير ٥/ ٤٥٤، تحفة الفقهاء ٣/ ٢٩٧.
(¬٤) وعند المالكية: لا يقتل المسلم أباه الكافر، إلا أن يضطره لذلك، بأن يخافه على نفسه.
وعند الشافعية: يكره لغازٍ قتل قريب، وقتل قريب مَحْرَم أشد، إلا أن يسمعه يسب الله، أو رسوله.
وعند الحنابلة: للمسلم أن يقتل أباه، أو ابنه في المعترك، ولا كراهة.
الهداية ٢/ ٤٢٩، تبيين الحقائق ٣/ ٢٤٥، القوانين ص ٩٨، السراج الوهاج ص ٥٤٣، حاشية ابن قاسم العبادي ٩/ ٢٤٠، غاية المنتهى ٢/ ٥١٩، الإقناع للحجاوي ٣/ ٥٢.','3','386',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1062,'وبمالٍ، أخذًا، أو دفعًا، ونقضه بعد الإِعلام متى رآهُ مصلحةً،
<hr><span data-type="title">منحة السلوك</span>

يعني: بلا شيء، هذا إذا كان الصلح خيرًا للمسلمين (¬١)؛ لقوله تعالى: {وَإِنْ جَنَحُوا لِلسَّلْمِ فَاجْنَحْ لَهَا} [الأنفال: ٦١] أي: وإن مالوا للصلح (¬٢).
قوله: وبمالٍ.
أي: وللإمام الصلح أيضًا بمالٍ، أخذًا، أو دفعًا (¬٣). فالأخذ: أن يأخذ المال منهم، والدفع: أن يدفع المال إليهم، وذلك لأن الصلح جهاد في المعنى إذا كان فيه مصلحة (¬٤)، إذ المقصود من الجهاد دفع الشر، ولكن الصلح بالدفع إنما يجوز، إذا خاف الهلاك على المسلمين؛ لأن دفع الهلاك بأي طريق أمكن واجبٌ، وإذا لم يخف لا يفعل ذلك؛ لما فيه من إلحاق الذلة بالمسلمين (¬٥).
قوله: ونقضه.
أي: وللإمام نقض الصلح بعد الإعلام متى رآه مصلحة؛ لأن المصلحة لمَّا تبدَّلت كان النقض جهادًا. هذا إذا صالحهم مدة فرأى نقضه قبل مضي
_________
(¬١) بداية المبتدي ٢/ ٤٢٩، تحفة الفقهاء ٣/ ٢٩٧، الهداية ٢/ ٤٢٩، تبيين الحقائق ٣/ ٢٤٥، المختار ٤/ ١٢١.
(¬٢) الكشاف للزمخشري ٢/ ١٣٣، تفسير ابن كثير ٢/ ٥٠٦، جامع البيان في تأويل آي القرآن ٦/ ٢٧٨، شرح فتح القدير للشوكاني ٢/ ٣٢٢.
(¬٣) تحفة الفقهاء ٣/ ٢٩٧، الاختيار ٤/ ١٢١، الهداية ٢/ ٤٢٩، تبيين الحقائق ٣/ ٢٤٥.
(¬٤) الهداية ٢/ ٤٣٠، تبيين الحقائق ٣/ ٢٤٥، العناية ٥/ ٤٥٦، شرح فتح القدير ٥/ ٤٥٦.
(¬٥) وفاقًا للثلاثة.
الهداية ٢/ ٤٣٠، العناية ٥/ ٤٥٦، شرح فتح القدير ٥/ ٤٥٦، الهداية ٢/ ٤٣٠، تبيين الحقائق ٣/ ٢٤٥، القوانين ص ١٠٤، بداية المجتهد ١/ ٣٨٧، الوجيز ٢/ ٢٠٣، مغني المحتاج ٤/ ٦١، الروض المربع ص ٢٢٤، منتهى الإرادات ٢/ ١٢٥.','3','387',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1063,'وإن بدؤوا بخيانة لم يجب الإعلام.
ويكره بيع السلاح، والحديد، والخيل منهم، ولو كانوا سلمًا
<hr><span data-type="title">منحة السلوك</span>

المدة. وأما إذا انقضت المدة، يبطل الصلح بُمضيِّها (¬١) (¬٢).
قوله: وإن بدؤوا بخيانة لم يجب الإعلام.
يعني: وإن بدأ الكفار بخيانة بعد الصلح، نقض الإمام الصلح بدون الإعلام؛ لأن الإعلام لنقض العهد، وقد انتقض بالخيانة (¬٣).

<span data-type="title" id=toc-530>[معاملة العدو]</span>
قوله: ويكره بيع السلاح، والحديد، والخيل منهم.
أي: من الكفار؛ لأن فيه تقويةً لهم فيحرم (¬٤).
قوله: ولو كانوا سِلمًا واصل بما قبله. السلم: -بكسر السين وفتحها- بمعنى: الصلح. يعني: ولو كانوا مصطلحين مع المسلمين، يكره بيع السلاح منهم؛ لما ذكرنا (¬٥).
_________
(¬١) وفاقًا للثلاثة.
تحفة الفقهاء ٣/ ٢٩٧، تبيين الحقائق ٣/ ٢٤٦، شرح فتح القدير ٥/ ٤٥٦، الاختيار ٤/ ١٢١، مختصر خليل ص ١١١٩، جواهر الإكليل ١/ ٢٧٠، المنهاج ٤/ ٣٦٣، زاد المحتاج ٤/ ٣٦٣، الروض المربع ص ٢٢٤، شرح منتهى الإرادات ٢/ ١٢٥.
(¬٢) وفي م: "الإعلام: أن يعلم ملكهم، أما إذا خانوا بغير علم ملكهم فلا ينتقض".
(¬٣) وفاقًا للمالكية، والشافعية، والحنابلة.
بداية المبتدي ٢/ ٤٣٠، الاختيار ٤/ ١٢١، الجامع الصغير ص ٣١٩، شرح فتح القدير ٥/ ٤٥٧، تبيين الحقائق ٣/ ٢٤٦، أقرب المسالك ص ٦٦، الشرح الصغير ١/ ٣٥٩، مغني المحتاج ٤/ ٢٦٠، نهاية المحتاج ٨/ ١٦٠، كشاف القناع ٣/ ١١١، شرح منتهى الإرادات ٢/ ١٢٥.
(¬٤) العناية ٥/ ٤٦١، شرح فتح القدير ٥/ ٤٦١، الهداية ٢/ ٤٣٠، بداية المبتدي ٢/ ٤٣٠، المختار ٤/ ١٢١.
(¬٥) الهداية ٢/ ٤٣١، شرح فتح القدير ٥/ ٤٦١، تبيين الحقائق ٣/ ٢٤٦.','3','388',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1064,'بخلاف الطعام، واللباس
<hr><span data-type="title">منحة السلوك</span>

قوله: بخلاف الطعام.
أي: لا يكره بيع الطعام، واللباس منهم (¬١). والقياس أن يمنع منهم؛ لأن فيه تقويتهم إلا أنَّا تركناه (¬٢)؛ لما روي عنه -صلى الله عليه وسلم- أنه: أمر ثمامة (¬٣)، أن يمير (¬٤) أهل مكة (¬٥).
_________
(¬١) الهداية ٢/ ٤٣١، تبيين الحقائق ٣/ ٢٤٧، الاختيار ٤/ ١٢٢، شرح فتح القدير ٥/ ٤٦٢.
(¬٢) الهداية ٢/ ٤٣١، تبيين الحقائق ٣/ ٢٤٧، المختار ٤/ ١٢٢، العناية ٥/ ٤٦٢.
(¬٣) هو ثمامة بن أثال بن النعمان بن سلمة الحنفي اليمامة، أبو أمامة، صحابي جليل، سيد أهل اليمامة، قاتل مسيلمة مع قومه الذين لم يرتدوا، وقاتل المرتدين من أهل البحرين وقتل بعد ذلك سنة ١٢ هـ.
الاستيعاب ١/ ٢٠٣، الإصابة ١/ ٢١١، الأعلام ٢/ ١٠٠.
(¬٤) الميرة -بالكسر-: جلب الطعام.
تاج العروس ٣/ ٥٥٢ مادة ماير، القاموس المحيط ٤/ ٣٠١ مادة م ي ر، معجم مقاييس اللغة ٥/ ٢٨٩ باب الميم والياء وما يثلثهما مادة "مير"، مختار الصحاح ص ٢٦٧ مادة م ي ر، المصباح المنير ٢/ ٥٨٧ مادة ما رَهُمْ.
(¬٥) رواه البيهقي في دلائل النبوة ٤/ ٨٠ باب حديث الإفك. وذكره ابن هشام في أواخر السيرة ٤/ ٢٤٦.
وأصل القصة في صحيح البخاري ٤/ ١٥٨٩ كتاب المغازي، باب وفد بني حنيفة، وحديث ثمامة بن أثال رقم ٤١١٤، ومسلم ٣/ ١٣٨٦ كتاب الجهاد والسير، باب ربط الأسير وحبسه، وجواز المن عليه رقم ١٧٦٤.
وتمامه: عن سعيد بن أبي سعيد، أنه سمع أبا هريرة -رضي الله عنه- يقول: "بعث رسول الله -صلى الله عليه وسلم- خيلًا قبل نجد، فجاءت برجل من بني حنيفة يقال له: ثمامة بن أثال سيد أهل اليمامة فربطوه بسارية من سواري المسجد، فخرج إليه رسول الله -صلى الله عليه وسلم- فقال: ماذا عندك يا ثمامة؟ فقال: عندي يا محمد خير، إن تقتل تقتل ذا دم، وإن تنعم تنعم على شاكر، وإن كنت تريد المال فسل تعط منه ما شئت، فتركه رسول الله -صلى الله عليه وسلم- حتى كان من الغد فقال: ماذا عندك يا ثمامة؟ قال: ما قلت لك: إن تنعم تنعم على شاكر، وإن تقتل تقتل ذا دم، =','3','389',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1065,'وإذا أمنهم حُرٌّ صحَّ، ولزم،
<hr><span data-type="title">منحة السلوك</span>

قوله: وإذا أمنهم حرٌّ، صح.
يعني: أمان الحر الواحد من المسلمين، كافرًا واحدًا، أو جماعة، صحيح (¬١)؛ لقوله -صلى الله عليه وسلم-: "ذمَّة المسلمين واحدة، يسعى بها أدناهم" رواه أحمد (¬٢).
_________
= وإن كنت تريد المال فسل تعط منه ما شئت، فتركه رسول الله -صلى الله عليه وسلم- حتى كان من الغد فقال: ماذا عندك يا ثمامة؟ فقال: عندي ما قلت لك: إن تنعم تنعم على شاكر، وإن تقتل تقتل ذا دم، وإن كنت تريد المال فسل تعط منه ما شئت، فقال رسول الله -صلى الله عليه وسلم-: أطلقوا ثمامة، فانطلق إلى نخل قريب من المسجد فاغتسل، ثم دخل المسجد فقال: أشهد أن لا إله إلا الله، وأشهد أن محمدًا عبده ورسوله، يا محمد، والله ما كان على الأرض وجه أبغض إلي من وجهك، فقد أصبح وجهك أحب الوجوه كلها إلي، والله ما كان من دين أبغض إلي من دينك، فأصبح دينك أحب الدين كله إلي، والله ما كان من بلد أبغض إلي من بلدك، فأصبح بلدك أحب البلاد كلها إليّ، وإن خيلك أخذتني وأنا أريد العمرة، فماذا ترى؟ فبشره رسول الله -صلى الله عليه وسلم- وأمره أن يعتمر، فلما قدم مكة، قال له قائل: أصبوت؟ فقال: لا، ولكني أسلمت مع رسول الله، ولا والله لا يأتيكم من اليمامة حبة حنطة حتى يأذن فيها رسول الله -صلى الله عليه وسلم-" اهـ.
وليس في الصحيحين أمر الرسول لثمامة أن يرد الميرة على أهل مكة، وإنما الإذن رواه البيهقي في دلائل النبوة ٤/ ٨٠ باب سرية نجد بعد أن ساق ما في الصحيحين وزاد: "وايم الذي نفس ثمامة بيده، لا تأتيكم حبة من اليمامة -وكانت ريف مكة- ما بقيت حتى يأذن فيها محمد، وانصرف إلى بلده، ومنع الحمل إلى مكة حتى جهدت قريش، فكتبوا إلى رسول الله يسألونه بأرحامها أن يكتب إلى ثمامة يخلي حمل الطعام، ففعل رسول الله".
(¬١) الاختيار ٤/ ١٢٢، بداية المبتدي ٢/ ٤٣١، تبيين الحقائق ٣/ ٢٤٧، شرح فتح القدير ٥/ ٤٦٢.
(¬٢) في المسند ١/ ١٢٦. ورواه أيضًا، البخاري ٦/ ٢٤٨٢ كتاب الفرائض، باب إثم من تبرأ من مواليه برقم ٦٣٧٤، ومسلم ٢/ ٩٩٤ كتاب الحج، باب فضل المدينة رقم ١٣٧٠.
عن علي -رضي الله عنه- قال: ما عندنا شيء إلا كتاب الله تعالى، وهذه الصحيفة عن =','3','390',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1066,'إلا أن يرى الإمام نقضه.
ولا يصح أمان ذميٍّ، وأسير، وتاجرٍ، ومسلم غير مهاجر،
<hr><span data-type="title">منحة السلوك</span>

والذمة: العهد. وأدناهم. أي: أقلُّهم عددًا، وهو الواحد (¬١).
قوله: إلا أن يرى الإمام نقضه.
أي: نقض أمان الحرِّ الواحد إذا كان شرًا لمصالح المسلمين، واحترازًا عن الغدر (¬٢). وقال -صلى الله عليه وسلم-: "لكل غادر لواءٌ يوم القيامة يعرف به" رواه أحمد، والبخاري، ومسلم (¬٣).

<span data-type="title" id=toc-531>[الأمان وصحته]</span>
قوله: ولا يصح أمان ذمي؛ لأنه منهم (¬٤)، ولا أمان أسيرٍ، وتاجرٍ؛ لأنهما مقهوران تحت أيديهم (¬٥)، ولا أمان مسلم غير مهاجر (¬٦)، وهو الذي
_________
= النبي -صلى الله عليه وسلم-: "المدينة حرام ما بين عائر إلى ثور، من أحدث فيها حدثًا، أو آوى محدثًا، فعليه لعنة الله والملائكة والناس أجمعين، لا يقبل منه عدل ولا صرف، وقال: ذمة المسلمين واحدة، فمن أخفر مسلمًا فعليه لعنة الله والملائكة والناس أجمعين، لا يقبل منه صرف ولا عدل، ومن تولى قومًا بغير إذن مواليه، فعليه لعنة الله والملائكة والناس أجمعين، لا يقبل الله منه صرفًا ولا عدلًا".
(¬١) الهداية ٢/ ٤٣١، العناية ٥/ ٤٦٢، تبيين الحقائق ٣/ ٢٤٧.
(¬٢) بداية المبتدي ٢/ ٤٣١، الاختيار ٤/ ١٢٣، الهداية ٢/ ٤٣١، المختار ٤/ ١٢٣، تبيين الحقائق ٣/ ٢٤٧.
(¬٣) أحمد ٢/ ١٦، والبخاري ٦/ ٢٥٥٥ كتاب الحيل، باب ولكل غادر لواء يوم القيامة رقم ٦٥٦٥، ومسلم ٣/ ١٣٦١ كتاب الجهاد والسير، باب تحريم الغدر رقم ١٣ (١٧٣٦).
عن ابن عمر -رضي الله عنهما-، وتمامه: "يقال: هذه غدرة فلان".
(¬٤) الهداية ٢/ ٤٣١، تحفة الفقهاء ٣/ ٢٩٦، تبيين الحقائق ٣/ ٢٤٧، الاختيار ٤/ ١٢٣.
(¬٥) بداية المبتدي ٢/ ٤٣١، الاختيار ٤/ ١٢٣، الهداية ٢/ ٤٣١، تبيين الحقائق ٣/ ٢٤٧، تحفة الفقهاء ٣/ ٢٩٦.
(¬٦) العناية ٥/ ٤٦٥، شرح فتح القدير ٥/ ٤٦٥، تحفة الفقهاء ٣/ ٢٩٦، تبيين الحقائق ٣/ ٢٤٧.','3','391',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1067,'وعبد غير مأذونٍ في القتالِ.
<hr><span data-type="title">منحة السلوك</span>

أسلم في دارهم، ولم يهاجر إلينا (¬١)، ولا أمان عبد غير مأذون في القتال؛ لأنه لم يباشر القتال، فلا يخافونه، فلا يصح أمانه (¬٢).
وقال محمد (¬٣)، والشافعي: يجوز أمانه (¬٤).
_________
(¬١) العناية ٥/ ٤٦٥، شرح فتح القدير ٥/ ٤٦٥، تبيين الحقائق ٣/ ٢٤٧.
(¬٢) الهداية ٢/ ٤٣٢، العناية ٥/ ٤٦٥، الاختيار ٤/ ١٢٣، بداية المبتدي ٢/ ٤٣٢، تبيين الحقائق ٣/ ٢٤٧.
(¬٣) بداية المبتدي ٢/ ٤٣٢، شرح فتح القدير ٥/ ٤٦٥ الهداية ٢/ ٤٣٢، تبيين الحقائق ٣/ ٢٤٧.
(¬٤) وإلى جواز أمان العبد، ذهب المالكية، والحنابلة. كما أن المالكية، والشافعية، والحنابلة، يرون: أن الأمان يصح من كل مسلم مكلف، مختار، سواء كان ذكرًا، أو أنثى.
الشرح الصغير ١/ ٣٥٩، جواهر الإكليل ١/ ٢٥٨، المعونة ١/ ٦٢٣، الكافي لابن عبد البر ص ٢١٠، الوجيز ٢/ ١٩٤، التذكرة ص ١٥٥، مغني المحتاج ٤/ ٢٣٦، أسنى المطالب ٤/ ٢٠٢، المحرر ٢/ ١٨٠، العمدة لابن قدامة ص ١١٥، كشاف القناع ٣/ ١٠٤.','3','392',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1068,'<span data-type="title" id=toc-532>فصل</span>
وإذا فتح الإمام بلدًا قهرًا، فله الخيار في قسمته بين الغانمين، وإبقائِه عليهم بالجزية والخراج،
<hr><span data-type="title">منحة السلوك</span>

فصل
هذا الفصل في بيان أحكام الغنائم (¬١)، وقسمتها (¬٢).

<span data-type="title" id=toc-533>[الفتح عنوة]</span>
قوله: وإذا فتح الإمام بلدًا قهرًا، فله الخيار في قسمته بين الغانمين.
يعني: بعد إخراج الخمس (¬٣)، كما فعل رسول الله -صلى الله عليه وسلم- بخيبر (¬٤)، وإبقائه. أي: إبقاء البلد عليهم بقطع الجزية على رؤوسهم، والخراج على أراضيهم، كما فعل عمر -رضي الله عنه- بسواد العراق (¬٥)
_________
(¬١) الغنيمة: ما نيل من أهل الشرك عنوة والحرب قائمة.
والفيء: ما نيل منهم بعد أن تضع الحرب أوزارها.
مختار الصحاح ص ٢٠١ مادة غ ن م، المصباح المنير ٢/ ٤٥٤ مادة غنمت، لسان العرب ١٢/ ٤٤٥ مادة غنم، حاشية الشلبي ٣/ ٢٤٨، شرح فتح القدير ٥/ ٤٦٩، العناية ٥/ ٤٦٩.
(¬٢) ذكر الغنائم بعد ما ذكر الأمان؛ لأن الإمام إذا حاصر الكفار، إما أن يؤمنهم، وإما أن يقتلهم ويغنم أموالهم، فكانت الغنيمة وقسمتها أحد ما يختاره الإمام.
حاشية الشلبي ٣/ ٢٤٨، شرح فتح القدير ٥/ ٤٦٩، العناية ٥/ ٤٦٩.
(¬٣) الاختيار ٤/ ١٢٤، تحفة الفقهاء ٣/ ٣٠١، الهداية ٤/ ٤٣٢، بدائع الصنائع ٧/ ١١٩، العناية ٥/ ٤٧٤.
(¬٤) البخاري ٢/ ٧٩٨ كتاب الإجارة، باب إذا استأجر أرضًا فمات أحدهما رقم ٢١٦٥، ومسلم ٣/ ١١٨٦ كتاب المساقاة، باب المساقاة والمعاملة بجزء من الثمر والزرع رقم ١٥٥١.
ولفظ الصحيحين: عن ابن عمر -رضي الله عنهما- قال: أعطى رسول الله -صلى الله عليه وسلم- خيبر اليهود أن يعملوها ويزرعوها، ولهم شطر ما يخرج منها.
(¬٥) سمي بذلك لسواده بالزروع والنخيل والأشجار؛ لأنه متاخم لجزيرة العرب التي لا زرع فيها ولا شجر، فكانوا إذا خرجوا من أرضهم، ظهرت لهم خضرة الزرع والأشجار، =','3','393',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1069,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

بموافقة الصحابة -رضي الله عنهم- (¬١) (¬٢).
_________
= فيسمونه سوادًا. كما إذا رأيت شيئًا من بعد، قلت: ما ذلك السواد. وهم يسمون الأخضر سوادًا، والسواد أخضر.
معجم البلدان ٣/ ٢٧٢، المصباح المنير ١/ ٢٩٤ مادة السواد، تهذيب الأسماء واللغات ٣/ ١٦٠ مادة سواد العراق، المغرب ص ٢٣٧ مادة السيد.
(¬١) وهو المذهب عند الحنابلة.
وعند المالكية: لا تقسم الأرض، وتكون وقفًا يصرف خراجها في مصالح المسلمين، إلا أن يرى الإمام أن المصلحة تقتضي القسمة، فإن له أن يقسم الأرض. وهي رواية عن الإمام أحمد.
وعند الشافعية: يجب على الإمام قسمها بين الغانمين كسائر الأموال، إلا أن تطيب أنفسهم بوقفها على المسلمين، ويسقطوا حقوقهم فيها فيقفها.
الهداية ٤/ ٤٣٢، تحفة الفقهاء ٣/ ٣٠١، بدائع الصنائع ٧/ ١١٩، الاختيار ٤/ ١٢٤، بداية المجتهد ١/ ٤٠١، الذخيرة ٣/ ٤١٦، رحمة الأمة ٢/ ١٧١، روضة الطالبين ١٠/ ٢٧٧، الفروع ٦/ ٢٤٠، حاشية المقنع ١/ ٥١٠، هداية الراغب ص ٢٣٨، الروض المربع ص ٣٣٢.
(¬٢) فعل عمر بسواد العراق بالخراج على أراضيهم. رواه عبد الرزاق ١٠/ ٣٣٣. كتاب أهل الكتابين، باب ما يؤخذ من أراضيهم وتجاراتهم رقم ١٩٢٧٦ من طريق قتادة، عن أبي مجلز: "أن عمر بن الخطاب، بعث عثمان بن حنيف إلى الكوفة، وجعله على مساحة الأرض، فوضع على الجريب من النخل عشرة دراهم، وعلى الجريب من العنب ثمانية دراهم، وعلى الجريب من القصب ستة دراهم، وعلى الجريب من البر أربعة دراهم، وعلى الجريب من الشعير درهمين، فرضي بذلك عمر".
ورواه ابن أبي شيبة في المصنف ٢/ ٤٣٠ كتاب الزكاة، باب ما يؤخذ من الكروم والرطاب والنخل، وما يوضع على الأرض رقم ١٠٧٢٤.
عن أبي مجلز قال: "بعث عمر عثمان بن حنيف على مساحة الأرض، فوضع عثمان على الجريب من الكرم عشرة دراهم، وعلى جريب النخل ثمانية دراهم، وعلى جريب الشعير درهمين، وجعل على كل رأس في السنة أربعة وعشرين درهمًا، وعطل النساء والصبيان".
قال في الدراية ٢/ ١٣٠ عما رواه عبد الرزاق وابن أبي شيبة: وهو منقطع. =','3','394',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1070,'وله الخيار أيضًا في قتل الأسرى إن لم يسلموا، واسترقاقهم ولو أسلموا، وجعلهم ذمة،
<hr><span data-type="title">منحة السلوك</span>

قوله: وله. أي: للإمام الخيارُ أيضًا، إن شاء قتل الأسرى، كما فعل رسول الله -صلى الله عليه وسلم- ببني قريظة، فإنه قتل مقاتلتهم، واسترق (¬١) ذراريهم (¬٢)، وإن شاء استرقهم؛ لأن فيه توفير المنفعة لهم بالإسترقاق، إلا مشركي العرب، والمرتدين (¬٣)، على ما يجيء (¬٤).
قيَّد بقوله: إن لم يسلموا.
لأنهم إذا أسلموا لا يتعرض لهم بالقتل، ووضع الجزية، ولكن له أن يسترقهم، وإن شاء جعلهم ذمةً للمسلمين (¬٥).
_________
= ورواه أبو عبيد القاسم بن سلام في كتاب الأموال ١/ ٥٧.
وقال في الدراية ٢/ ١٣٠ عن سند رواية أبي القاسم بن سلام في كتاب الأموال: وهذا منقطع.
(¬١) الرقيق: هو المملوك كُلًا، أو بعضًا.
أنيس الفقهاء ص ١٥٢، المطلع على أبواب المقنع ص ١٣٨، التعريفات ص ١٢٣، مختار الصحاح ص ١٠٦ مادة ر ق ق، المصباح المنير ١/ ٢٣٥ مادة ر ق.
(¬٢) رواه مسلم ٣/ ١٣٨٨ كتاب الجهاد والسير، باب جواز قتال من نقض العهد، وجواز إنزال أهل الحصن على حكم حاكم عدل أهل للحكم رقم ١٧٦٨. عن أبي سعيد الخدري -رضي الله عنه-، قال: نزل أهل قريظة على حكم سعد بن معاذ، فأرسل رسول الله -صلى الله عليه وسلم- إلى سعد فأتاه على حمار، فلما دنا قريبًا من المسجد، قال رسول الله -صلى الله عليه وسلم- للأنصار: "قوموا إلى سيدكم" ثم قال: "إن هؤلاء نزلوا على حكمك" قال: تقتل مقاتلهتم، وتسبى ذريتهم قال: فقال النبي -صلى الله عليه وسلم-: "قضيت بحكم الله ... ".
(¬٣) الهداية ٢/ ٤٣٣، تحفة الفقهاء ٣/ ٣٠١، المختار ٤/ ١٢٤، بداية المبتدي ٢/ ٤٣٣.
(¬٤) في ٣/ ٤٢٢.
(¬٥) الاختيار ٤/ ١٢٥، الكتاب ٤/ ١٢٤، الهداية ٢/ ٤٣٣، المختار ٤/ ١٢٥، تحفة الفقهاء ٣/ ٣٠١.','3','395',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1071,'ولا يطلقهم بمال ولا يفادي بهم أسرانا.
وإن تعذر نقل مواشيهم ذبحها، وحرقها،
<hr><span data-type="title">منحة السلوك</span>

قوله: ولا يطلقهم بمالٍ.
أي: ولا يخلي سبيلهم بأخذ المال منهم، ولا يُفادي بهم أسرانا؛ لأن في ذلك تقويتهم على المسلمين، وعودهم حربًا عليهم (¬١).
وعن أبي حنيفة: أنه لا بأس أن يُفادي بهم أُسارى المسلمين (¬٢)، وهو قول: محمد (¬٣).

<span data-type="title" id=toc-534>[التصرف فى الغنائم]</span>
قوله: وإن تعذَّر نقل مواشيهم ذبحها وحرقها.
كيلا ينتفع بها، كما يخرِّب بيوتهم، ويقطع أشجارهم، ويقلع زرعهم (¬٤).
_________
(¬١) بدائع الصنائع ٧/ ١١٩، تبيين الحقائق ٣/ ٢٤٩، شرح فتح القدير ٥/ ٤٧٤، العناية ٥/ ٤٧٤، السير الكبير لمحمد بن الحسن ٤/ ١٥٨٧.
(¬٢) بدائع الصنائع ٧/ ١١٩، تبيين الحقائق ٣/ ٢٤٩، العناية ٥/ ٤٧٤.
(¬٣) وأبي يوسف، وهو أظهر الروايتين عن أبي حنيفة؛ لأن تخليص أسراء المسلمين من أيدي المشركين واجب، ولا يتوصل إلى ذلك إلا بطريق المفاداة، وليس في هذا أكبر من ترك القتل لأسراء المشركين، وذلك جائز لمنفعة المسلمين.
وذهب المالكية، والشافعية، والحنابلة: إلى أن الإمام مخير في الأسارى بين الفداء بالمال، وبالأسارى، وبين المن عليهم، أو القتل، أو الاسترقاق.
السير الكبير لمحمد بن الحسن ٤/ ١٥٨٧، العناية ٥/ ٤٧٤، بدائع الصنائع ٧/ ١١٩، مختصر خليل ص ١١٣، التلقين ص ٧٣، تحفة المحتاج ٩/ ٢٤٧، الشرواني ٩/ ٢٤٧، مختصر الخرقي ص ١٢٩، دليل الطالب ١/ ٢٨٨، الإفصاح ٢/ ٢٨١.
(¬٤) وعند المالكية: إن تعذر نقل مواشيهم ذبحها، أو عقرها، وتتلف أمتعتهم إن عجز عن حملها.
وعند الشافعية: ما أصابه المسلمون من مال الكفار، وخيف أن يرجع إليهم ينظر فيه؛ =','3','396',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1072,'لا غير. وحرَّق الأسلحة، وما لا يحترق يَدْفنهُ. ولا يقسم غنيمةً في دار الحرب (¬١)
<hr><span data-type="title">منحة السلوك</span>

قوله: لا غير.
يعني: لا تعقر؛ لأنه مثلة. وكذلك لا تحرق قبل الذبح؛ لأنه منهي عنه (¬٢).
قوله: وحرَّق الأسلحة؛ لئلا ينتفعوا بها (¬٣).
قوله: وما لا يحترق.
أي: وما لا يمكن إحراقه يُدفن في مكانٍ لا يقفون عليه؛ كيلا ينتفعوا بها (¬٤)
قوله: ولا يقسم غنيمة في دار الحرب؛ لأن فيه قطع حق المدد، فلا يشرع (¬٥).
_________
= فإن كان غير الحيوان أتلف. وإن كان حيوانًا لم يجز إتلافه.
وذهب الحنابلة: إلى أن ما عجز المسلمون عن سياقته وأخذه؛ إن كان مما يستعين به الكفار في القتال كالخيل جاز عقره وإتلافه؛ لأنه مما يحرم إيصاله إلى الكفار بالبيع، فتركه لهم بغير عوض أولى بالتحريم. وإن كان مما يصلح للأكل فللمسلمين ذبحه والأكل منه مع الحاجة وعدمها، وما عدا هذين القسمين لا يجوز إتلافه؛ لأنه مجرد إفساد، وإتلاف.
تبيين الحقائق ٣/ ٢٥٠، شرح فتح القدير ٥/ ٤٧٧، بداية المبتدي ٢/ ٤٣٤، كنز الدقائق ٣/ ٢٥٠، الهداية ٢/ ٤٤٣، أقرب المسالك ص ٦٦، منح الجليل ٣/ ١٥٨، المهذب ١/ ٢٤١، الوجيز ١/ ١٩١، المغني ١٠/ ٥٠١.
(¬١) في ب زيادة جملة "حتى يخرجها إلى دار الإسلام".
(¬٢) شرح فتح القدير ٥/ ٤٧٧، تبيين الحقائق ٣/ ٢٥٠، الهداية ٢/ ٤٣٤.
(¬٣) شرح فتح القدير ٥/ ٤٧٧، تبيين الحقائق ٣/ ٢٥٠، الهداية ٢/ ٤٣٤.
(¬٤) الهداية ٢/ ٤٣٤، شرح فتح القدير ٥/ ٤٧٧، تبيين الحقائق ٣/ ٢٥٠.
(¬٥) وذهب المالكية، والشافعية، والحنابلة: إلى أن الغنمية تقسم في دار الحرب. =','3','397',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1073,'إلا للإيداع، والردء في الغنيمةِ كالمقاتل، بخلاف السُّوقي.
<hr><span data-type="title">منحة السلوك</span>

قوله: إلا للإيداع.
يعني: القسمة بين الغانمين على وجه الإيداع يجوز ليحملوها إلى دار الإسلام، ثم يرتجعها منهم فيها، فإن أبوا أن يحملوها أجبرهم على ذلك بأجر المثل (¬١).
وقيل: لا يجبر. ولو كان في بيت المال، أو في الغنيمة حمولة حمل عليها؛ لأن الكل مالهم (¬٢).

<span data-type="title" id=toc-535>[استحقاق الغنائم]</span>
قوله: والردء في الغنيمة كالمقاتل.
لتحقق سبب الاستحقاق، وهو المجاوزة على قصد القتال (¬٣).
الردء -بكسر الراء، وسكون الدال في آخره همزة-: هو المعين (¬٤).
قوله: بخلاف السوقي.
يعني: السوقي (¬٥) ليس كالمقاتل؛ لانعدام السبب في حقه؛ لأن قصده
_________
= بداية المبتدي ٢/ ٤٣٤، تحفة الفقهاء ٣/ ٢٩٩، الهداية ٢/ ٤٣٤، العناية ٥/ ٤٧٨، الذخيرة ٣/ ٤٢٤، متن الرسالة ص ٩٧، رحمة الأمة ٢/ ١٦٩، روضة الطالبين ١٠/ ٢٦٤، الروض المربع ص ٢٢٢، المحرر ٢/ ١٧٣.
(¬١) تبيين الحقائق ٣/ ٢٥٠، الهداية ٢/ ٤٣٥، شرح فتح القدير ٥/ ٤٨٣، العناية ٣/ ٤٨٣.
(¬٢) الهداية ٢/ ٤٣٥، شرح فتح القدير ٥/ ٤٨٣، تبيين الحقائق ٣/ ٢٥٠، العناية ٥/ ٤٨٣.
(¬٣) الهداية ٢/ ٤٣٤، العناية ٥/ ٤٨١، بداية المبتدي ٢/ ٤٣٤، الاختيار ٤/ ١٢٧.
(¬٤) القاموس المحيط ٢/ ٣٢٢ مادة ردأ، مختار الصحاح ص ١٠١ مادة ردأ، تاج العروس ١/ ٦٩ مادة ردأ، المصباح المنير ١/ ٢٢٥ مادة رَدُؤَ.
(¬٥) وهو المنسوب إلى السوق التي يُبَاعُ فيها.
المصباح المنير ١/ ٦٩٦ مادة سُقتُ، المغرب ص ٢٣٩ مادة السوق، مختار الصحاح ص ١٣٥ مادة س وق.','3','398',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1074,'والمدد قبل إخراج الغنيمة إلى دار الإسلام كالأصل، ومن مات قبل إخراج الغنيمة سقط حقه،
<hr><span data-type="title">منحة السلوك</span>

التجارة لا إعزاز الدين، ولا إرهاب العدو، إلا إن قاتل فيستحق حينئذ (¬١).
وفي قولٍ للشافعي: يُسهم له (¬٢).
قوله: والمدد قبل إخراج الغنيمة إلى دار الإسلام كالأصل.
لأن سبب الملك هو القهر، وتمام القهر بالإحراز بالدار، وقد شاركه في هذا المعنى، بخلاف ما إذا لحقه المدد بعد إخراج الغنيمة (¬٣).
قوله: ومن مات قبل إخراج الغنيمة سقط حقه.
_________
(¬١) الاختيار ٤/ ١٢٧، بداية المبتدي ٢/ ٤٣٥، العناية ٥/ ٤٨٢، شرح فتح القدير ٥/ ٤٨٣، المختار ٤/ ١٢٧، الهداية ٢/ ٤٣٥.
(¬٢) يعني: إذا حضر الوقعة ولم يقاتل. وهو قول: المالكية، والحنابلة؛ لأنه لما لم تمنع التجارة والصناعة من الحج، لم تمنع من الجهاد.
والقول الثاني للشافعي: لا سهم له، كالحنفية، وإذا قيل: لا سهم لهم، أعطوا رضخًا لإدراكهم زمن الاستحقاق. فإن حضروا الوقعة، وقاتلوا أسهم لهم؛ لأنهم بالقتال قد عدلوا عن قصد التكسب إلى نية الجهاد.
القوانين الفقهية ص ١٠٠، التفريع ١/ ٣٦٠، الحاوي الكبير ٨/ ٤٢٥، روضة الطالبين ٦/ ٣٨٢، كشاف القناع ٣/ ٨٢، الروض المربع ص ٢٢٢.
(¬٣) وذهب المالكية، والشافعية، والحنابلة: إلى أن المدد الذي يصل بعد انقضاء الحرب، وقبل حيازة الغنيمة إلى دار الإسلام، لا سهم لهم. أما بعد قسمة الغنيمة وحيازتها، فليس للمدد في ذلك حصة اتفاقًا. وإذا أدركوا الوقعة قبل انقضاء الحرب، فإنهم يشاركون الجيش في الغنيمة.
تبيين الحقائق ٣/ ٢٥١، الكتاب ٤/ ١٢٥، كنز الدقائق ٣/ ٢٥١، العناية ٥/ ٤٨٢، القوانين ص ١٠٠، التلقين ص ٧٢، الحاوي الكبير ٨/ ٤٢٥، مغني المحتاج ٣/ ١٠٣، أسنى المطالب ٣/ ٦٩، الإفصاح ٢/ ٢٧٩، شرح منتهى الإرادات ٢/ ١١٥.','3','399',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1075,'وبعدهُ لا يسقط.
وللعسكر الانتفاعُ بالغنيمةِ قبل الإخراج، أكلًا، وعلفًا، ودهنًا، وإيقادًا، وقتالًا بسلاح
<hr><span data-type="title">منحة السلوك</span>

يعني: لا يورث نصيبه؛ لأن الإرث يجري في الملك، ولا ملك قبله (¬١).
قوله: وبعده. أي: بعد إخراج الغنيمة لا يسقط حقه، بل يورث منه (¬٢).

<span data-type="title" id=toc-536>[الانتفاع من الغنائم]</span>
قوله: وللعسكر الانتفاع بالغنيمة قبل الإخراج.
أي: إلى دار الإسلام، أكلًا أيْ: من حيث الأكل، والعلف، والدهن، والإيقاد، والقتال بالسلاح (¬٣)؛ لما روي عن ابن عمر -رضي الله عنها- أنه قال: "كنا
_________
(¬١) الهداية ٢/ ٤٣٥، تبيين الحقائق ٣/ ٢٥٢، المختار ٤/ ١٢٦، بداية المبتدي ٢/ ٤٣٥، الاختيار ٤/ ١٢٦.
(¬٢) وذهب المالكية، والشافعية، والحنابلة: إلى أن من حضر الوقعة يعطى من الغنيمة، سواء مات قبل إخراج الغنيمة، أو بعدها. فسهمه لا يسقط، وإنما يقوم وارثه مقامه في سهمه.
بداية المبتدي ٢/ ٤٣٥، الاختيار ٤/ ١٢٦، الهداية ٢/ ٤٣٥، تبيين الحقائق ٣/ ٢٥٢، الكافي لابن عبد البر ص ٢١٤، جواهر الإكليل ١/ ٢٦٣، التنبيه ص ٢٣٥، حاشية البيجوري ٢/ ٢٧٨، حاشية المقنع ١/ ٥٠٨، المغني ١٠/ ٤٤٠.
(¬٣) ذهب المالكية: إلى أنه يجوز الانتفاع بالأطعمة، والأشربة من غير قسم، ما داموا في دار الحرب، ويدخل في ذلك القوت، والفواكه، واللحم، والعلف، بقدر الحاجة لمن كان محتاجًا إليه، أو غير محتاج. ويرد وجوبًا ما فضل عن حاجته من كل ما أخذه إن كثر، فإن تعذر تصدق به. وإن كان يسيرًا انتفع به. ويجوز ذبح الأنعام للأكل، وأخذ الجلود للنعال والخفاف. وكذلك الدابة يركبها إلى بلده، ثم يردها إلى الغنيمة. والثياب يلبسها، ثم يردها. وكذا السلاح يقاتل به إن احتاج، ثم يرده إلى الغنيمة.
وعند الشافعية: للغانمين التبسط في الغنيمة، بأخذ القوت دون السلاح والدابة والمال، ولهم التبسط بأخذ ما يصلح به القوت، كزيت، وسمن، وعسل، وكذا لحم، وشحم، =','3','400',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1076,'ونحوها بلا قسمةٍ،
<hr><span data-type="title">منحة السلوك</span>

نصيب في مغازينا العسل، والعنب، ونأكله ولا نرفعه" رواه البخاري (¬١).
قوله: ونحوها.
مثل الانتفاع بالحطب، والعسل، والعنب، ونحوها (¬٢). ولا فرق في الطعام بين أن يكون مهيّئًا للأكل، وبين أن لا يكون مُهيّئًا له، حتى يجوز لهم ذبح المواشي من البقر، والغنم، والجزور. وكذا أكل الحبوب، والسكر، والفواكه الرطبة، واليابسة، والسمن، والزيت، وكل شيء هو مأكولٌ عادةً (¬٣).
قوله: بلا قسمةٍ. متعلق بقوله: "الانتفاع". وإنما لم تجز القسمة؛ لما
_________
= وكل طعام يعتاد أكله عمومًا. ولهم علف الدواب تبنًا وشعيرًا ونحوهما، وذبح مأكولٍ للحم. والصحيح: جواز الفاكهة، ولا تجب قيمة المذبوح، ولا يختص الجواز بمحتاج إلى طعام وعلف. ومن رجع إلى دار الإسلام ومعه بقية، لزمه ردها إلى المغنم.
وعند الحنابلة: من أخذ طعامًا، ولو سكرًا ونحوه، أو أخذ علفًا، فله أكله، وله إطعام سبي اشتراه ونحوه، وله علف دابته، ولو كانت للتجارة. ويرد فاضلًا من طعام، وعلف، ولو كان يسيرًا. ويجوز القتال بسلاح من الغنيمة، ويرده. ولا يجوز القتال على فرس، أو نحوها من الغنيمة، ولا لبس ثوب منها، ولا تجوز التضحية بشيء يجب فيه الخمس، من إبل، أو بقر، أو غنم. ويجوز لحاجة دهن بدنه، ودابته.
الهداية ٢/ ٤٣٥، تحفة الفقهاء ٣/ ٣٠٠، بدائع الصنائع ٧/ ١٢٣، مختصر خليل ص ١١٣، التفريع ١/ ٣٦٢، التلقين ص ٧٢، القوانين ص ١٠٠، الشرح الصغير ١/ ٣٥٨، منح الجليل ٣/ ١٥٦، المعونة ١/ ٦١٠، مغني المحتاج ٤/ ٢٣١، السراج الوهاج ص ٥٤٥، فتح الوهاب ٥/ ٢٩٩، جواهر الإكليل ١/ ٢٥٤، شرح منتهى الإرادات ٢/ ١٠٩، الإقناع للحجاوي ٣/ ٧٣، المغني ١٠/ ٤٨٠.
(¬١) ٣/ ١١٤٩ كتاب الجهاد باب ما يصيب من الطعام في أرض الحرب رقم ٢٩٨٥.
(¬٢) كنز الدقائق ٣/ ٢٥٢، شرح فتح القدير ٥/ ٤٨٦، تبيين الحقائق ٣/ ٢٥٢، الكتاب ٤/ ١٢١، الهداية ٢/ ٤٣١.
(¬٣) الهداية ١/ ٤٣٦، بدائع الصنائع ٧/ ١٢٣، بداية المبتدي ٢/ ٤٣٦، الكتاب ٤/ ١٢١.','3','401',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1077,'من غير بيع، وتموُّل. بخلاف الثياب، والدواب، وبعد الإخراج يردون ما فَضَل معهم من ذلك.
وخُمسُ الغنيمة يُقسم أثلاثًا بين اليتامى، والمساكين، وابن
<hr><span data-type="title">منحة السلوك</span>

ذكرنا من أن فيه قطع حق المدد (¬١).
قوله: من غير بيع. متعلق أيضًا بقوله: "الانتفاع". وإنما لم يجز البيع؛ لأنهم لا يملكون بالأخذ ما لم يخرجوا، وإنما أُبيح لهم التناول بالضرورة، والمباح له لا يملك البيع، وإذا باعه أحدهم ردَّ الثمن إلى المغنم (¬٢).
قوله: بخلاف الثياب.
يعني: لا يجوز الانتفاع بالثياب، والدواب، والمتاع، والسلاح؛ لأنه مال مشترك بينهم، فلا يجوز الانتفاع به بلا حاجة (¬٣).
والأولى: أن يقسم الإمام بينهم إذا احتاجوا إليه كلهم؛ لأن المحظور يستباح للضرورة (¬٤).
قوله: وبعد الإخراج.
أي: إلى دار الإسلام يردون ما فضل معهم من ذلك، ولا ينتفعون به؛ لزوال المبيح وهو الضرورة (¬٥).

<span data-type="title" id=toc-537>[قسمة الغنائم]</span>
قوله: وخمس الغنيمة يُقسم أثلاثًا: بين اليتامى، والمساكين، وابن
_________
(¬١) بدائع الصنائع ٧/ ١٢٣، تبيين الحقائق ٣/ ٢٥٣، بداية المبتدي ٢/ ٤٣٦، الكتاب ٤/ ١٢١، الهداية ٢/ ٤٣٦.
(¬٢) بدائع الصنائع ٧/ ١٢٤، تبيين الحقائق ٣/ ٢٥٣، الهداية ٢/ ٤٣٦، الكتاب ٤/ ١٢٢.
(¬٣) تبيين الحقائق ٣/ ٢٥٣، شرح فتح القدير ٥/ ٤٨٥، بدائع الصنائع ٧/ ١٢٤، الهداية ٢/ ٤٣٦.
(¬٤) الهداية ٢/ ٤٣٦، تبيين الحقائق ٣/ ٢٥٣، شرح فتح القدير ٥/ ٤٨٥، بدائع الصنائع ٧/ ١٢٤.
(¬٥) بدائع الصنائع ٧/ ١٢٤، شرح فتح القدير ٥/ ٤٨٥، تبيين الحقائق ٣/ ٢٥٣، العناية ٥/ ٤٨٥.','3','402',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1078,'السبيل، يُقدَّم منهم فقراءُ ذوي القربى خاصة.
<hr><span data-type="title">منحة السلوك</span>

السبيل (¬١)، يقدَّمُ منهم. أي: من هؤلاء فقراء ذوي القربى خاصةً، ولا حق لأغنيائهم (¬٢).
وقال الشافعي (¬٣): لذوي القربى: خمس الخمس، يستوي فيه فقيرهم، وغنيهم، ويقسم بينهم للذكر مثل حظ الأنثيين، ويكون ذلك لبني هاشم، وبني المطلب، ولا يكون لغيرهم.
فحاصله: أن الخمس يقسم أثلاثًا عندنا (¬٤)، وعنده أخماسًا. سهم لذوي القربى، وسهم للنبي -صلى الله عليه وسلم- يخلفه فيه الإمام، ويصرفه إلى مصالح المسلمين، والباقي للثلاثة.
ولنا: أن الخلفاء الراشدين قسموه على ثلاثة على نحوِ ما قلنا بمحضر من الصحابة، فكان إجماعًا (¬٥).
_________
(¬١) تحفة الفقهاء ٣/ ٣٠٢، الاختيار ٤/ ١٣١، تبيين الحقائق ٣/ ٢٥٣، بداية المبتدي ٢/ ٤٣٦.
(¬٢) تحفة الفقهاء ٣/ ٣٠٣، الاختيار ٤/ ١٣١، الهداية ٢/ ٤٣٦، المختار ٤/ ١٣١، تبيين الحقائق ٣/ ٢٥٣.
(¬٣) وأحمد.
التذكرة ص ١١٦، كفاية الاختيار ٢/ ١٣١، الروض المربع ص ٢٢٢، المبدع ٣/ ٣٦٢.
(¬٤) تحفة الفقهاء ٣/ ٣٠٣، تبيين الحقائق ٣/ ٢٥٦، الهداية ٢/ ٤٤٠، بدائع الصنائع ٧/ ١٢٥، العناية ٥/ ٥٠٣.
(¬٥) اتفق العلماء على أن ما يحصل في أيدي الغانمين من الغنيمة من جميع الأموال، عينها، وعروضها سوى الأراضي، فإنه يؤخذ منه الخمس، ثم اختلفوا فيمن يقسم هذا الخمس:
فقال أبو حنيفة: يقسم على ثلاثة أسهم، سهم لليتامى، وسهم للمساكين، وسهم لابن السبيل، يدخل فقراء ذوي القربى فيهم دون أغنيائهم، فأما سهم النبي -صلى الله عليه وسلم-: فهو خمس الله وخمس رسوله، وهو خمس واحد، وقد سقط بموت النبي -صلى الله عليه وسلم- كما سقط الصَّفيّ، وسهم ذوي القربى كانوا يستحقونه في زمن النبي -صلى الله عليه وسلم- بالنصرة، وبعده لا سهم لهم، وإنما =','3','403',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1079,'وذكر الله تعالى في الخمس؛ للتبرك باسمه. وسهم النبي -صلى الله عليه وسلم- سقط بموته،
<hr><span data-type="title">منحة السلوك</span>

قوله: وذكر الله تعالى في الخمس؛ للتبرك باسمه في افتتاح الكلام، وهو غير محتاج إلى شيء؛ لأن الكل له محتاج (¬١).
قوله: وسهم النبي -صلى الله عليه وسلم- سقط بموته (¬٢).
_________
= يستحقونه بالفقر خاصة، ويستوي فيه ذكرهم وأنثاهم.
وقال مالك: هذا الخمس لا يستحق لشخص دون شخص، وإنما هو راجع إلى اجتهاد الإمام، يأخذ منه كفايته، ويصرف الباقي في مصالح المسلمين.
وقال الشافعي، وأحمد: يقسم الخمس المذكور على خمسة أسهم: سهم للرسول -صلى الله عليه وسلم- وهو باقٍ لم يسقط بموته، وسهم لبني هاشم، وبني عبد المطلب خاصة، دون بني نوفل، وبني عبد شمس، وإنما هو مختص ببني هاشم، وبني عبد المطلب؛ لأنهم ذوو القربى، غنيهم، وفقيرهم سواء، إلا أن للذكر منهم مثل حظ الأنثيين، ولا يستحقه أولاد البنات منهم. وسهم لليتامى، وسهم لأبناء السبيل. وهؤلاء الثلاثة: يستحقونه بالفقر والحاجة، لا بالاسم. وسهم الرسول -صلى الله عليه وسلم- عند الشافعي، وأحمد: يصرف مصرف الفيء في المصالح، من إعداد السلاح والكراع، وعقد القناطر، وبناء المساجد، ونحو ذلك، وهو لمن يلي الخلافة بعد الرسول -صلى الله عليه وسلم-.
واتفقوا جميعًا: على أن أربعة أخماس الغنيمة تقسم على من شهد الواقعة، إذا كان من أهل القتال.
تبيين الحقائق ٣/ ٢٥٦، بدائع الصنائع ٧/ ١٢٥، الهداية ٢/ ٤٤٠، القوانين الفقهية ص ١٠١، الذخيرة ٣/ ٤٣١، روض الطالب ٣/ ٨٨، أنوار المسالك ص ٤٥٥، التذكرة ص ١١٦، السراج الوهاج ص ٣٥١، الروض المربع ص ٢٣٢، المبدع ٣/ ٣٦٢، كشاف القناع ٣/ ٨٤، منتهى الإرادات ٢/ ١١٣، الإفصاح ٢/ ٢٧٦.
(¬١) تبيين الحقائق ٣/ ٢٥٧، شرح فتح القدير ٥/ ٥٠٧، بداية المبتدي ٢/ ٤٤٠، كنز الدقائق ٣/ ٢٥٧.
(¬٢) قال ابن حجر في الدراية ٢/ ١٢٧: وأما قوله: إن سهم الرسول -صلى الله عليه وسلم- سقط بموته، فلم أجد دليله.','3','404',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1080,'كالصفيّ.
<hr><span data-type="title">منحة السلوك</span>

لأنه -صلى الله عليه وسلم- كان يستحقه بالرسالة، ولا رسول بعده (¬١).
قوله: كالصفيِّ. أي: كما يسقط الصفي (¬٢)، وهو شيء كان رسول الله -صلى الله عليه وسلم- يصطفيه لنفسه، ويستعين به على أمور المسلمين (¬٣) (¬٤). "وكانت صفية (¬٥) من الصفي" رواه أبو داود (¬٦).
_________
(¬١) تحفة الفقهاء ٣/ ٣٠٣، بداية المبتدي ٢/ ٤٤٠، تبيين الحقائق ٣/ ٢٥٧، العناية ٥/ ٥٠٧.
(¬٢) الهداية ٢/ ٤٤٠، بدائع الصنائع ٧/ ١٢٥، بداية المبتدي ٢/ ٤٤٠، كنز الدقائق ٣/ ٢٥٧، تحفة الفقهاء ٣/ ٣٠٠.
قال في المبدع ٣/ ٣٦٣: وانقطع ذلك بغير خلاف نعلمه، إلا أبا ثور.
(¬٣) أخرج أبو داود في سننه ٣/ ١٥٢ كتاب الخراج والإمارة والفيء رقم ٢٩٩١ عن عامر الشعبي قال: "كان للنبي سهم يدعى الصفي، إن شاء عبدًا، وإن شاء أمة، وإن شاء فرسًا، يختاره قبل الخمس".
قال ابن حجر في الدراية ٢/ ١٢٧: وهذا مرسل.
وأخرج أبو داود أيضًا في سننه ٣/ ١٥٢ كتاب الخراج والإمارة والفيء رقم ٢٩٩٢ عن ابن عون قال: سألت محمدًا عن سهم النبي -صلى الله عليه وسلم-، والصفيّ، قال: كان يضرب له بسهم مع المسلمين وإن لم يشهد، والصفيّ: يؤخذ له من الخمس قبل كل شيء.
قال ابن حجر في الدراية ٢/ ١٢٧: وهذا مرسل أيضًا.
(¬٤) التعريفات ص ١٤٦، طلبة الطلبة ص ١٧٢، المصباح المنير ١/ ٣٤٣، مادة صفو، مختار الصحاح ص ١٥٣ مادة ص ف ا، المغرب ص ٢٦٩ مادة الصفيّ.
(¬٥) هي صفية بنت حيي بن أخطب من الخزرج من أزواج النبي -صلى الله عليه وسلم-، كانت في الجاهلية من ذوات الشرف تدين باليهودية، من أهل المدينة استصفاها رسول الله -صلى الله عليه وسلم- وصارت في سهمه ثم أعتقها وجعل عتقها صداقها، كانت حليمة، عاقلة، فاضلة. توفيت سنة ٥٠ هـ.
الإصابة ٤/ ٣٤٦، الاستيعاب ٤/ ٣٤٦، طبقات ابن سعد ٨/ ٨٥، صفة الصفوة ٢/ ٢٧.
(¬٦) ٣/ ١٥٢ كتاب الخراج والإمارة والفيء، باب ما جاء في سهم الصفي رقم ٢٩٩٤، وأخرجه الحاكم ٣/ ٣٩ كتاب المغازي.
كلاهما من طريق سفيان، عن هشام بن عروة، عن أبيه، عن عائشة -رضي الله عنها- قالت: كانت صفية من الصفي. =','3','405',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1081,'وأربَعةُ الأخماس بين الغانمين، للفارس سهمان، وللراجل سهم.
<hr><span data-type="title">منحة السلوك</span>

قوله: وأربعة الأخماس بين الغانمين، للفارس سهمان، وللراجل سهم.
وهذا عند أبي حنيفة (¬١) وقالا: للفارس ثلاثة أسهم (¬٢)، وبه أخذ الشافعي (¬٣). لقول ابن عمر -رضي الله عنهما-: أنه -صلى الله عليه وسلم- "أسهم للفارس ثلاثة أسهم، وللراجل سهمًا" رواه الجماعة (¬٤).
_________
= قال ابن حجر في الدراية ٢/ ١٢٧: وإسناده قوي.
(¬١) الاختيار ٤/ ١٢٩، تحفة الفقهاء ٣/ ٣٠٠، بدائع الصنائع ٧/ ١٢٥، الهداية ٢/ ٤٤٠.
(¬٢) الاختيار ٤/ ١٢٩، تحفة الفقهاء ٣/ ٣٠٠، بدائع الصنائع ٧/ ١٢٥.
(¬٣) ومالك وأحمد، أما الراجل فإن له سهمًا واحدًا اتفاقًا.
قال في الإفصاح ٢/ ٢٧٨: واتفقوا على أن الراجل له سهم واحد.
أقرب المسالك ص ٦٧، المعونة ١/ ٦١٤، متن الرسالة ص ٩٧، التفريع ١/ ٣٦٠، شرح ابن قاسم على أبي شجاع ٢/ ٢٧٩، حاشية البيجوري ٢/ ٢٩٧، الروض المربع ص ٢٢٢، المحرر ٢/ ١٧٦.
(¬٤) البخاري ٤/ ١٥٤٥ كتاب الجهاد، باب غزوة خيبر رقم ٣٩٨٨، ومسلم ٣/ ١٣٨٣ كتاب الجهاد والسير، باب كيفية قسمة الغنيمة بين الحاضرين رقم ١٧٦٢.
ولفظ الصحيحين: "قسم رسول الله -صلى الله عليه وسلم- للفرس سهمين، وللراجل سهمًا" زاد البخاري: "قسم رسول الله -صلى الله عليه وسلم- يوم خيبر ... ".
قال البخاري ٤/ ١٥٤٥: فسره نافع فقال: "إذا كان مع الرجل فرس فله ثلاثة أسهم، فإن لم يكن له فرس فله سهم".
ورواه أيضًا أبو داود ٣/ ٧٥ كتاب الجهاد، باب في سهمان الخيل رقم ٢٧٣٣ ولفظه: عن ابن عمر -رضي الله عنهما- "أن رسول الله -صلى الله عليه وسلم-: أسهم لرجل، ولفرسه ثلاثة أسهم، سهمًا له، وسهمين لفرسه".
والترمذي ٥/ ٢٧٤ كتاب السير، باب في سهم الخيل رقم ١٥٥٤ ولفظه: عن ابن عمر -رضي الله عنهما-: "أن رسول الله -صلى الله عليه وسلم- قسم في النفل، للفرس بسهمين، وللرجل بسهم". وابن ماجه ٢/ ٩٥٢ كتاب الجهاد، باب قسمة الغنائم رقم ٢٨٥٤ ولفظه: "أن النبي -صلى الله عليه وسلم- أسهم يوم =','3','406',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1082,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

ولأبي حنيفة قول مجمع بن جارية -رضي الله عنه- (¬١): قسمت خيبر إلى أن قال: "أنه -صلى الله عليه وسلم- أعطى الفارس سهمين، والراجل سهمًا" رواه أحمد، وأبو داود (¬٢).
_________
= خيبر للفارس ثلاثة أسهم، للفرس سهمان، وللرجل سهم".
ولم أجد الحديث عند النسائي عن ابن عمر، وإنما عن عبد الله بن الزبير في السنن الكبرى ٣/ ٤٣ كتاب الخيل، بابٌ سهما الخيل رقم ٤٤٣٤ عن يحيى بن عباد بن عبد الله بن الزبير، عن جده، أنه كان يقول: "ضرب رسول الله -صلى الله عليه وسلم- عام خيبر للزبير بن العوام أربعة أسهم، سهم للزبير، وسهم لذي القربى، لصفية بنت عبد المطلب أم الزبير، وسهمين للفرس".
(¬١) هو مجمِّع بن جارية بن عامر الضبعي الأوسي الأنصاري، أحد الصحابة، وممن جمع القرآن في عهد النبي -صلى الله عليه وسلم- وكان ذلك في صباه، توفي بالمدينة في خلافة معاوية سنة ٥٠ هـ.
الإصابة ٣/ ٣٦٦، تهذيب الكمال ٢٧/ ٢٤٤، المعرفة والتاريخ ١/ ٣٨٩، الاستيعاب ٣/ ٤١٤، أسد الغابة ٥/ ٦٦.
(¬٢) أحمد ٣/ ٤٢٠، وأبو داود ٣/ ٧٦ كتاب الجهاد، باب فيمن أسهم له سهمًا رقم ٢٧٣٦، وابن أبي شيبة ١٢/ ٣٣٩ كتاب الجهاد، باب من قال للفارس سهمان رقم ١٥٠٣١، وأبو يوسف في الخراج ٢٤، والحاكم ٢/ ١٣١ كتاب قسمة الفيء، باب أعطى الفارس سهمين، وأعطى الراجل سهمًا، والطحاوي في الشرح ٣/ ٢٥١، والدارقطني ٤/ ١٠٥ كتاب السير حديث رقم ١٥، والبيهقي في السنن الكبرى ٦/ ٣٢٥ كتاب قسمة الفيء والغنيمة، باب ما جاء في سهم الراجل والفرس، وابن ماجه ٢/ ٩٥٢ كتاب الجهاد، باب قسمة الغنائم رقم ٢٨٥٤. قال الحاكم ٢/ ١٣١: هذا حديث كبير، صحيح الإسناد، ولم يخرجاه. وسكت عنه الذهبي.
كلهم من رواية مجمع بن يعقوب بن مجمع قال: سمعت أبي يعقوب يذكر عن عمه عبد الرحمن بن يزيد الأنصاري -رضي الله عنه-، عن عمه مجمع بن جارية الأنصاري قال: شهدنا الحديبية مع رسول الله -صلى الله عليه وسلم- وذكر الحديث ... ".
ورواه البخاري ٣/ ١٠٥١ كتاب الجهاد، باب سهام الفرس رقم ٢٧٠٨، ومسلم ٣/ ١٣٨٣ كتاب الجهاد والسير، باب كيفية قسمة الغنيمة بين الحاضرين رقم ١٧٦٢ عن ابن عمر -رضي الله عنهما-.','3','407',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1083,'والبرذون والعربيُّ سواء.
<hr><span data-type="title">منحة السلوك</span>

وما رووه محمولٌ على التنفيل (¬١) (¬٢)، كما روي أنه -صلى الله عليه وسلم- "أعطى سلمة بن الأكوع سهم الفارس، والراجل" رواه أحمد، ومسلم بمعناه (¬٣).
قوله: والبرذون (¬٤)، والعربي سواءٌ.
لأن السبب هو الإرهاب، وذلك باسم الخيل وهو يتناولهما (¬٥).
_________
(¬١) النفل: الزيادة، وللغازي زيادة على سهمه.
أنيس الفقهاء ص ١٨٣، طلبة الطلبة ص ١٧٨، المطلع على أبواب المقنع ص ٢١٤، المصباح المنير ٢/ ٦١٩ مادة نفل، مختار الصحاح ص ٢٨١ مادة ن ف ل.
(¬٢) الهداية ٢/ ٤٣٨، بداية المبتدي ٢/ ٤٣٨، بدائع الصنائع ٧/ ١٢٥، تحفة الفقهاء ٣/ ٣٠٠.
(¬٣) أحمد ٤/ ٥٠، مسلم ٣/ ١٣٧٤ كتاب الجهاد والسير، باب استحقاق القاتل سلب القتيل رقم ١٧٥٤، عن سلمة بن الأكوع.
وتمام القصة عند مسلم، عن إياس بن سلمة قال: حدثني أبي سلمة بن الأكوع: غزونا مع رسول الله -صلى الله عليه وسلم- هوازن، فبينما نحن نتضحى مع رسول الله -صلى الله عليه وسلم- إذ جاء رجل على جمل أحمر فأناخه، ثم انتزع طلقًا من حقبه، فقيد به الجمل، ثم تقدم يتغدى مع القوم، وجعل ينظر وفينا ضعفة، ورقة في الظهر، وبعضنا مشاة، إذ خرج يشتد فأتى جمله فأطلق قيده، ثم أناخه وقعد عليه، فأثاره فاشتد به الجمل، فاتبعه رجل على ناقة ورقاء، قال سلمة: وخرجت أشتد، فكنت عند ورك الناقة، ثم تقدمت حتى كنت عند ورك الجمل، ثم تقدمت حتى أخذت بخطام الجمل فأنخته، فلما وضع ركبته في الأرض اخترطت سيفي، فضربت رأس الرجل فندر، ثم جئت بالجمل أقوده، عليه رحله وسلاحه، فاستقبلني رسول الله -صلى الله عليه وسلم - والناس معه فقال: "من قتل الرجل؟ قال: ابن الأكوع، قال له: سلبه أجمع".
(¬٤) الخيل أربعة: أحدها: أن يكون أبواه عربيين، فيقال له: الضيق.
الثاني: عكسه، وهو الذي أبواه غير عربيين، ويسمى البرذون.
والثالث: الذي أمه غير عربية، فيسمى: الهجين.
والرابع: الذي أبوه غير عربي فيسمى: المقرف.
مختار الصحاح ص ١٩ مادة ب ر ذ ن، وصفحة ١٧٧ مادة ع ر ب، المصباح المنير ١/ ٤١ مادة البرذون، و ٢/ ٤٠١ مادة العرب، المطلع على أبواب المقنع ص ٢١٦.
(¬٥) وإليه ذهب الشافعية، وهو رواية عن الإمام أحمد. وذهب المالكية: إلى أن البرذون =','3','408',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1084,'ولا سهم لبعير، وبغل. ويعتبر كونه فارسًا، أو راجلًا عند مجاوزة الدرب، لا عند القتال.
<hr><span data-type="title">منحة السلوك</span>

قوله: ولا سهم لبعير وبغل؛ لعدم الإرهاب بهما (¬١).

<span data-type="title" id=toc-538>[الاعتبار في قسمة الغنيمة]</span>
قوله: ويعتبر كونه فارسًا، أو راجلًا عند مجاوزة الدرب، لا عند القتال، حتى لو دخل دار الحرب فارسًا فنفق فرسه، أي: هلك، وقاتل راجلًا، استحق سهم الفارس (¬٢)، ولو دخل راجلًا فاشترى فرسًا استحق سهم الراجل (¬٣).
_________
= كالعربي، إن أجازه الإمام.
وذهب الحنابلة: إلى أن للفارس ثلاثة أسهم، سهم له، وسهمان لفرسه إن كان عربيًا، وللفارس على فرس غير عربي سهمان فقط، سهم له وسهم لفرسه. وهي الرواية الصحيحة عن الإمام أحمد. وروي عنه: أن البراذين إن أدركت إدراك العراب، أسهم لها مثل الفرس العربي، وإلا فلا. وعنه: لا يسهم لها.
الهداية ٢/ ٤٣٨، بدائع الصنائع ٧/ ١٢٦، تحفة الفقهاء ٣/ ٣٠٢، جواهر الإكليل ١/ ٢٦٢، الذخيرة ٣/ ٤٢٦، المعونة ١/ ٦١٥، منح الجليل ٣/ ١٩٢، شرح المحلي ٣/ ١٩٤، عميرة ٣/ ١٩٤، المبدع ٣/ ٣٦٧، المغني ١٠/ ٤٣٦، الروض المربع ص ٢٢٢، التسهيل ص ١٩٥، الإقناع للحجاوي ٣/ ٨٨.
(¬١) وإليه ذهب المالكية، والشافعية، والحنابلة، وبه قال: الحسن، ومكحول، والثوري. قال ابن قدامة في المغني ١٠/ ٤٣٩: وهذا هو الصحيح إن شاء الله تعالى.
وروي عن الإمام أحمد: أنه يسهم للبعير سهم واحد.
مختصر الطحاوي ص ٢٨٥، الكتاب ٤/ ١٣٢، المختار ٤/ ١٢٩، تحفة الفقهاء ٣/ ٣٠٠، التفريع ١/ ٣٦١، الخرشي على خليل ٣/ ١٣٥، شرح المحلي ٣/ ١٩٤، عميرة ٣/ ١٩٤، شرح الزركشي ٦/ ٤٩٣، نيل المآرب ١/ ٣٢٤، الإفصاح ٢/ ٢٧٨.
(¬٢) الكتاب ٤/ ١٣٢، كنز الدقائق ٢/ ٢٥٤، المختار ٤/ ١٢٩، بداية المبتدي ٢/ ٤٣٩، الاختيار ٤/ ١٢٩، الهداية ٢/ ٤٣٩، تبيين الحقائق ٢/ ٢٥٤.
(¬٣) الهداية ٢/ ٤٣٩، تبيين الحقائق ٢/ ٢٥٤، الاختيار ٤/ ١٢٩.','3','409',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1085,'ويرضَخُ الإمام للعبد، والصبي، والمرأة، والذمي ما يراه.
<hr><span data-type="title">منحة السلوك</span>

وعند الشافعي: يُعتبر كونه فارسًا، وراجلًا، حال انقضاء الحرب (¬١).

<span data-type="title" id=toc-539>[الرضخ]</span>
قوله: ويرضخ الإمام للعبد، والصبي، والمرأة، والذمي، ما يراه (¬٢).
لقول ابن عباسٍ -رضي الله عنهما-: "لم يكن للمرأة، والعبد سهم، إلا أن يحذيا من غنائم القوم" رواه أحمد، ومسلم (¬٣)؛ ولأن الجهاد عبادة، والذمي ليس من أهله، والمرأة والصبيُّ عاجزان عنه (¬٤)، وإنما يرضخ لهم إذا باشروا القتال، أو كانت المرأة تُداوي الجَرحى، وتقوم بمصالح المرضى، أو دل الذمي الطريق (¬٥).
ولا يبلغ بالرضخ السهم (¬٦). والرضخ -بالضاد والخاء المعجمتين-:
_________
(¬١) وكذا عند المالكية، والحنابلة. وبه قال: الأوزاعي، وأبو ثور، وإسحاق.
الكافي لابن عبد البر ص ٢١٤، منح الجليل ٣/ ١٩٢، جواهر الإكليل ١/ ٢٦٢، الشرح الكبير للدردير ٢/ ١٩٣، التنبيه ص ٢٣٥، قليوبي ٣/ ١٩٣، الحاوي الكبير ٨/ ٤٢١، مختصر الخرقي ص ١٣٠، الشرح الكبير لابن قدامة ١٠/ ٥٠٨.
(¬٢) وإليه ذهب المالكية، والشافعية، والحنابلة.
الاختيار ٤/ ١٣٠، بداية المبتدي ٢/ ٤٣٩، تبيين الحقائق ٢/ ٢٥٤، الهداية ٢/ ٤٣٩، بداية المجتهد ١/ ٣٩٢، القوانين ص ١٠١، المهذب ١/ ٢٤٥، التذكرة ص ١١٧، المغني ١٠/ ٤٤٢، مطالب أولي النهى ٢/ ٥٥٥.
(¬٣) أحمد ١/ ٢٢٤، ومسلم ٣/ ١٤٤٦ كتاب الجهاد والسير، باب النساء الغازيات يرضخ لهن ولا يسهم لهن، والنهي عن قتل صبيان أهل الحرب رقم ١٤٠ (١٨١٢).
عن ابن عباس -رضي الله عنهما-: "أنه كتب إلى نجدة الحروري ... "
(¬٤) الهداية ٢/ ٤٣٩، تبيين الحقائق ٣/ ٢٥٦، الاختيار ٤/ ١٣٠.
(¬٥) الهداية ٢/ ٤٤٠، تبيين الحقائق ٣/ ٢٥٦، الاختيار ٤/ ١٣٠، شرح فتح القدير ٥/ ٥٠٩.
(¬٦) وفاقًا للثلاثة.
الاختيار ٤/ ٤٤٠، بداية المبتدي ٢/ ٤٣٩، المختار ٤/ ٤٤٠، الهداية ٢/ ٤٣٩، تبيين الحقائق ٣/ ٢٥٦، الشرح الكبير للدردير ٢/ ١٩٢، السراج الوهاج ص ٣٥٤، عميرة ٣/ ١٩٤، الشرح الكبير لابن قدامة ١٠/ ٤٩٥، غاية المنتهى/ ٥٥٥.','3','410',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1086,'ولا يخمس ما أخذه واحدٌ، أو اثنان، مُغيرَين؛ بل ما أخذهُ جماعة لها منعة.
<hr><span data-type="title">منحة السلوك</span>

العطاء ليس بالكثير، من رضخ يرضَخُ -بفتح العين فيهما- (¬١).

<span data-type="title" id=toc-540>[تخميس الغنيمة]</span>
قوله: ولا يخمس ما أخذه واحدٌ، أو اثنان مغيرين.
لأن الخمس وظيفة الغنيمة، وهي المأخوذة قهرًا وغلبةً، وهذا اختلاس وسرقة (¬٢).
قوله: "مغيرين" -بفتح الراء- حال من قوله: "اثنان"، من أغار يُغيرُ (¬٣).
قوله: بل ما أخذه جماعة.
أي: بل يخمس ما أخذه جماعةٌ لها منعة، أي: شوكة؛ لأنَّ ما ذكرنا
_________
(¬١) تاج العروس ٢/ ٢٥٨ مادة رضخ، معجم مقاييس اللغة ٢/ ٤٠٢ باب الراء والضاد وما يثلثهما مادة رضخ، مختار الصحاح ص ١٠٣ مادة ر ض خ، لغة الفقه ص ٣١٨، المصباح المنير ١/ ٢٢٨ مادة رضخت، المطلع على أبواب المقنع ص ١٦.
(¬٢) وذهب المالكية، والحنابلة: إلى أن المغير إذا لم يستند للجيش بأن كان مستقلًا بنفسه، فله ما غنمه، ولا دخل للجيش فيه. وإذا كان مستندًا للجيش فهو غنيمة مخمسة؛ لأنه مال حصل الاستيلاء عليه قهرًا بقوة الجيش.
وعند الشافعية: ما أخذه واحد، أو جمع من دار الحرب، بسرقة، أو نحوها، يعتبر غنيمة كالمال المأخوذ من أهل الحرب قهرًا، فهو غنيمة مخمسة لا يختص به الآخذ.
بداية المبتدي ٢/ ٤٤١، الكتاب ٤/ ١٣٤، الهداية ٢/ ٤٤١، شرح فتح القدير ٥/ ٥٠٩، الشرح الصغير ١/ ٣٦٤، جواهر الإكليل ١/ ٢٦٣، مغني المحتاج ٤/ ٢٣٠، أسنى المطالب ٤/ ١٩٦، كشاف القناع ٣/ ٨١، شرح منتهى الإرادات ٢/ ١١٣.
(¬٣) أي: دفع بخيله على العدو.
القاموس المحيط ٣/ ٤٢٧ مادة غ ور، المغرب ص ٣٤٧ مادة الغارة، مختار الصحاح ص ٢٠٢ مادة غ ور، الأضداد للأنباري ص ٣٦٨.','3','411',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1087,'ويجوز<span data-type="title" id=toc-541> التنفيل بالسَّلب</span>، وغيره، تحريضًا على القتال.
<hr><span data-type="title">منحة السلوك</span>

من المعنى يحصل بهذا (¬١).

[التنفيل بالسلب]
قوله: ويجوز التنفيل بالسلب.
بأن يقول الإمام: من قتل قتيلًا فله سلبه؛ لأنه تحريض على القتال، وهو مندوب إليه (¬٢). قال الله تعالى: {يَاأَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِنِينَ عَلَى الْقِتَالِ} [الأنفال: ٦٥] وحرض -صلى الله عليه وسلم- بالتنفيل على القتال فقال: "من قتل قتيلًا عليه سلبه، فله سلبه" رواه أحمد، والبخاري، ومسلم (¬٣). والسَّلب: مركبه، وثيابه، وسلاحه، وما معه على الدابة، من ماله، أو في وسطه (¬٤).
قوله: وغيره.
_________
(¬١) الهداية ٢/ ٤٤١، الاختيار ٤/ ١٣٢، تبيين الحقائق ٣/ ٢٥٨، شرح فتح القدير ٥/ ٥٠٩.
(¬٢) فعند الحنفية: إن شرطه الإمام للقاتل فهو له، وإن لم يشرط ذلك لم يتفرد به.
وعند المالكية: إن شرطه الإمام كان له من خمس الخمس، وهو سهم النبي، فإن كان قيمته بقدر الخمس استحق جميعه، وإن كانت قيمته أكثر منه استحق منه بقدر الخمس، ولا يستحقه من أصل الغنيمة. وإن لم يشرطه الإمام فلا حق له.
وذهب الشافعية، والحنابلة: إلى أن القاتل يستحق سلب مقتوله من أصل الغنيمة، سواء شرط ذلك الإمام، أو لم يشرطه.
بداية المبتدي ٢/ ٤٤١، الهداية ٢/ ٤٤١، الاختيار ٤/ ١٣٢، تبيين الحقائق ٣/ ٢٥٨، الخرشي على خليل ٣/ ١٣٠، جواهر الإكليل ١/ ٢٦١، مختصر المزني ص ٢٤٨، متن الزبد ص ٦٧، حاشية المقنع ١/ ٤٩٥، الإنصاف ٤/ ١٤٨، الإفصاح ٢/ ٢٧٩.
(¬٣) أحمد ٥/ ٣٠٦، والبخاري ٣/ ١١٤٤ كتاب الجهاد، باب من لم يخمس الأسلاب رقم ٢٩٧٣، ومسلم ٣/ ١٣٧٠ كتاب الجهاد والسير، باب استحقاق القاتل سلب القتيل رقم ١٧٥١.
عن أبي قتادة -رضي الله عنه- أن رسول الله -صلى الله عليه وسلم- قال ذلك يوم حنين، بعد ما برد القتال، وفيه قصة، ولفظه: "من قتل قتيلًا له عليه بينة، فله سلبه".
(¬٤) الاختيار ٤/ ١٣٣، تحفة الفقهاء ٣/ ٢٩٧، تبيين الحقائق ٣/ ٢٥٨.','3','412',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1088,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

أي: وغير السلب، بأن يقول للسرية: جعلت لكم الربع بعد الخمس (¬١)؛ لما ذكرنا، ولما رُوي أنه -صلى الله عليه وسلم-: "نفل الربع بعد الخمس في رجعته" رواه أحمد، وأبو داود (¬٢)، وكان -صلى الله عليه وسلم-: "يُنفل في البداية الربع، وفي الرجعة الثلث" رواه أحمد، وابن ماجه، والترمذي (¬٣).
_________
(¬١) وفاقًا للثلاثة.
بداية المبتدي ٢/ ٤٤١، الاختيار ٤/ ١٣٢، الهداية ٢/ ٤٤١، تبيين الحقائق ٣/ ٢٥٨، الخرشي على خليل ٣/ ١٣٠، مختصر المزني ص ٢٤٨، متن الزبد ص ٦٧، الروض المربع ص ٢٢٢، الإنصاف ٤/ ١٤٦.
(¬٢) أحمد ٤/ ١٦٠، وأبو داود في السنن ٣/ ١٨٢ كتاب الجهاد، باب فيمن قال الخمس قبل النفل رقم الحديث ٢٧٤٩، وأبو عبيد في الأموال ص ٣٩٦ كتاب الخمس وأحكامه وسننه، باب النفل والربع بعد الخمس حديث ٨٠٠، وابن ماجه في السنن ٢/ ٩٥١ كتاب الجهاد باب النفل رقم الحديث ٢٨٥٣، وابن الجارود في المنتقى ص ٣٦١ - ٣٦٢ باب نفل السرايا بعد الخمس بعد ما أصابوا حديث ١٠٧٩، والحاكم في المستدرك ٢/ ١٣٣ كتاب قسم النفل باب تنفيل الربع في البداءة والثلث في الرجعة، والبيهقي في السنن الكبرى ٦/ ٣١٤ كتاب قسم الفيء والغنيمة، باب النفل بعد الخمس.
وله عندهم ألفاظ. من حديث حبيب بن مسلمة، "أن الرسول -صلى الله عليه وسلم- كان ينفل الربع بعد الخمس في البداءة، وينفلهم الثلث بعد الخمس في الرجعة".
قال الحاكم في المستدرك ٢/ ١٣٣: صحيح الإسناد ولم يخرجاه.
(¬٣) أحمد ٤/ ١٦٠، وابن ماجه ٢/ ٩٥١ كتاب الجهاد، باب النفل رقم ٢٨٥٢، والترمذي ٥/ ٢٨٤ كتاب السير، باب ما جاء في النفل رقم ١٥٦١، وعبد الرزاق ٥/ ١٩٠ كتاب الجهاد باب النفل رقم ٩٣٣٤، وسعيد بن منصور ٢/ ٢٦٢ كتاب الجهاد باب النفل والسلب في الغزو والجهاد رقم ١٧٠٢، وأبو عبيد في الأموال ٧٩٧، والدارمي ٢/ ٦٧٧ كتاب السير، باب في أن ينفل في البدأة الربع، وفي الرجعة الثلث رقم ٢٣٨٨، وأبو داود ٣/ ٨٠ كتاب الجهاد، باب فيمن قال الخمس قبل النفل رقم ٢٧٤٩، وابن الجارود ص ٢٧١ باب نفل السرايا بعد الخمس بعد ما أصابوا رقم ١٠٧٩، والطحاوي ٣/ ٢٤٠ كتاب السير، باب النفل بعد الفراغ من قتال العدو وإحراز الغنيمة، وابن حبان كتاب السير، باب ذكر ما =','3','413',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1089,'والتُّرك، والروم، يملكُ كل طائفةٍ منهم ما استولت عليه من نفوس الطائفة الأُخرى، وأموالها. ويملك الكفار كلُّهم أموالَنا بالاستيلاء، لا نفوسنا
<hr><span data-type="title">منحة السلوك</span>

قوله: والترك، والروم، يملك كل طائفة منهم ما استولت عليه من نفوس الطائفة الأخرى، وأموالها.
لأن الاستيلاء في المباح سبب الملك، كالاحتطاب، والاصطياد (¬١).

<span data-type="title" id=toc-542>[ما يملكه الكفار]</span>
قوله: ويملك الكفار كلهم أموالنا بالاستيلاء.
لزوال العصمة (¬٢). وقال الشافعي: لا يملكونها (¬٣).
قوله: لا نفوسنا.
أي: لا يملكون نفوسنا؛ لأن الآدمي لم يخلق محلًا للتمليك، بل
_________
= يستحب للإمام أن ينفل السرية إذا خرجت عند البعث الشديد رقم ٤٨٣٥، والطبراني في الكبير ٤/ ٢٣ رقم ٣٥٢٥ ورقم ٣٥١٨ - ٣٥٢٩، والطبراني في الصغير ١/ ٩٧، وابن عدي في الكامل ٣/ ١١١٩.
كلهم عن حبيب بن مسلمة، سوى الترمذي، وعبد الرزاق، والدارمي، فعن عبادة بن الصامت.
قال الحاكم: صحيح وسكت عنه الذهبي. وقال الترمذي: حديث عبادة حديث حسن.
(¬١) الهداية ٢/ ٤٤٢، تبيين الحقائق ٣/ ٢٦٠، كنز الدقائق ٣/ ٢٦٠، الكتاب ٤/ ١٢٧.
(¬٢) وإليه ذهب المالكية.
وعند الحنابلة يملكون أموالنا حتى عبد مسلم، وأم ولد، ومكاتب، ولا يملكون وقفًا.
بداية المبتدي ٢/ ٤٤٢، الاختيار ٤/ ١٣٣، الهداية ٢/ ٤٤٢، تبيين الحقائق ٣/ ٢٦٠، شرح شرح فتح القدير ٦/ ٤، التلقين ص ٧٢، بداية المجتهد ١/ ٣٩٨، القوانين ص ١٠١، الشرح الصغير ١/ ٣٦١، كشاف القناع ٣/ ٨٠، شرح منتهى الإرادات ٢/ ١١٠.
(¬٣) وهو رواية عن الإمام أحمد.
التنبيه ص ٢٣٥، رحمة الأمة ٢/ ١٦٨، المغني ١٠/ ٤٧٥، الشرح الكبير لابن قدامة ١٠/ ٤٧٣.','3','414',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1090,'إلَّا خالِصَ رقيقنا.
والمالك القديم، أَحق بماله قبل القسمة مجانًا،
<hr><span data-type="title">منحة السلوك</span>

ليملك لا يُملك، وإنما ثبتت في الكافر محلية الملك، بالكفر العارض (¬١).
قوله: إلا خالِص رقيقنا.
أي: يملكون خالص رقيقنا؛ لأنه كالمال (¬٢). واحترز بالخلوص عن المدبر، والمكاتب، وأم الولد، فإن الحرية قد توجهت إليهم، ولم يكونوا أرقاء خالصة (¬٣).

<span data-type="title" id=toc-543>[تملك المال أثناء الحرب]</span>
قوله: والمالك القديم، أحق بماله قبل القسمة مجانًا.
يعني: إذا غلب المسلمون على أهل الحرب الذين أخذوا أموالنا، فمن وجد منا ماله الذي أخذه العدو منهم قبل أن يقسم الغنيمة بين المسلمين، أخذه بغير شيء؛ لأنه عين حقه (¬٤).
_________
(¬١) الهداية ٢/ ٤٤٣، شرح فتح القدير ٦/ ٤، الكتاب ٤/ ١٢٨، كنز الدقائق ٣/ ٢٦٣، تبيين الحقائق ٣/ ٢٦٣، العناية ٦/ ٤.
(¬٢) تبيين الحقائق ٣/ ٢٦٣، شرح فتح القدير ٦/ ١١، الهداية ٢/ ٤٤٣، بداية المبتدي ٢/ ٤٤٣.
(¬٣) بداية المبتدي ٢/ ٤٤٣، الاختيار ٤/ ١٣٤، تبيين الحقائق ٣/ ٢٦١، بدائع الصنائع ٧/ ١٢٨.
(¬٤) إذا أخذ الكفار أموال المسلمين، ثم قهرهم المسلمون فأخذوها منهم:
فإن علم صاحبها قبل قسمتها، رُدَّت إليه بغير شيء في قول: عامة أهل العلم، منهم عمرو، وعطاء، والنخعي، وسليمان بن ربيعة، والليث، والأوزاعي، والثوري، وأبو حنيفة، ومالك، والشافعي، وأحمد.
وقال الزهري: لا يرد إليه وهو للجيش، ونحوه عن عمرو بن دينار؛ لأن الكفار ملكوه باستيلائهم فصار غنيمة كسائر أموالهم.
وإن أدركه بعد أن قسم:
فذهب أبو حنيفة، والثوري، والأوزاعي، ومالك، والحنابلة: إلى أن صاحبه أحق به =','3','415',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1091,'وبعدها بالقيمة، أو بالثمن إن كان مشتريًا.
<hr><span data-type="title">منحة السلوك</span>

قوله: وبعدها بالقيمة.
أي: يأخذها بعد القسمة بالقيمة؛ لأنه زال ملكه بغير رضاه فكان له حق الاسترداد نظرًا له، غير أن في الأخذ بعد القسمة ضررًا بالمأخوذ منه، بإزالة ملكه الخاص، فيأخذه بالقيمة إن شاء؛ ليعتدل النظر من الجانبين.
والشركة قبل القسمة عامة، فيقل الضرر فيأخذه بغير شيء (¬١).
قوله: أو بالثمن إن كان مُشتريًا.
يعني: لو اشترى ما أخذه العدو منهم تاجر، وأخرجه إلى دار الإسلام، أخذه المالك القديم بالثمن الذي اشترى به التاجر من العدو؛ ونظرًا للجانبين؛ لأنه لو أخذه بغير شيءٍ يتضرَّرُ التاجر، وإن اشتراه بعرض أخذه بقيمة العرض، ولو كان البيع فاسدًا يأخذه بقيمة نفسه (¬٢).
_________
= بالثمن الذي حسب به على من أخذه، وكذلك إن بيع ثم قسم ثمنه، فهو أحق بالثمن.
وقال الشافعي: يأخذه صاحبه قبل القسمة وبعدها، ويعطى مشتريه ثمنه من خمس المصالح؛ لأنه لم يزل عن ملك صاحبه، فوجب أن يستحق أخذه بغير شيء، كما قبل القسمة، ويعطى من حسب عليه القيمة؛ لئلا يفضي إلى حرمان آخذه حقه من الغنيمة، وجعل من سهم المصالح؛ لأن هذا منها، وهذا قول ابن المنذر.
وروي عن الإمام أحمد: أنه إذا قسم فلا حق له فيه بمال. وهو قول: عمر بن الخطاب، وعلي، وسليمان بن ربيعة، وعطاء، والنخعي، والليث بن سعد.
تبيين الحقائق ٣/ ٢٦١، بدائع الصنائع ٧/ ١٢٨، الكتاب ٤/ ١٢٧، الاختيار ٤/ ١٣٣، بداية المجتهد ١/ ٣٩٨، القوانين الفقهية ص ١٠٢، الذخيرة ٣/ ٤٣٤، أسنى المطالب ٤/ ٢٠٩، مغني المحتاج ٤/ ٢٤٢، المغني ١٠/ ٤٧١، الشرح الكبير لابن قدامة ١٠/ ٤٦٨، شرح منتهى الإرادات ٢/ ١١٢، كشاف القناع ٣/ ٧٨.
(¬١) الهداية ٢/ ٤٤٣، بدائع الصنائع ٧/ ١٢٨، الكتاب ٤/ ١٢٨، الاختيار ٤/ ١٣٣.
(¬٢) تبيين الحقائق ٣/ ٢٦٢، بدائع الصنائع ٧/ ١٢٩، الاختيار ٤/ ١٣٣، الكتاب ٤/ ١٢٨، الهداية ٢/ ٤٤٣.','3','416',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1092,'مسلمٌ دخل دار الحرب تاجرًا يحرم عليه الخيانةُ، والغدرُ بهم، فإن خان في شيء وأخرجَهُ، تصدق به.
ولو دخل حربي إلينا بأمان، يقال له: إن أقمت سنَةً جُعلتَ ذميًا،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-544>[انتقال الحربي إلى ذمي]</span>
قوله: مسلمٌ دخل دار الحرب تاجرًا يحرم عليه الخيانة، والغدرُ بهم (¬١)؛ لما روينا "أنه -صلى الله عليه وسلم- نهى عن الغدر" (¬٢).
قوله: فإن خان في شيء وأخرجه، تصدق به.
لأنه وإن كان ملكه باستيلائه على مال مباح، ولكنه محظور؛ لأنه حصل بسبب الغدر، فأوجب ذلك خبثًا فيه، فيؤمر بالتصدق به (¬٣).
قوله: ولو دخل حربيٌّ إلينا بأمان، يقال له: إن أقمت سنةً جُعلت ذميًا (¬٤).
الأصل فيه: أن الكافر لا يمكن من إقامة دائمة في دارنا إلا باسترقاق، أو جزية؛ لأنه يبقى ضررًا على المسلمين؛ لكونه عينًا لهم، وعونًا علينا، ويمكن من الإقامة اليسيرة؛ لأن في منعها قطع المنافع من الميرة، والجلب (¬٥)، وسد باب التجارات، ففصلنا بينهما بسنةٍ؛ لأنها مدة تجب فيها
_________
(¬١) كنز الدقائق ٣/ ٢٦٦، الكتاب ٤/ ١٣٤، تبيين الحقائق ٣/ ٢٦٦، الهداية ٢/ ٤٤٣.
(¬٢) سبق تخريجه ٣/ ٣٨٣.
(¬٣) وكذا عند الحنابلة، إلا أنه إذا خانهم، أو سرق منهم، أو اقترض شيئًا، وجب رده إلى أربابه، فإن جاؤوا إلى دار الإسلام أعطاه لهم، وإلا بعثه إليهم؛ لأنه مال معصوم بالنسبة إليه.
تبيين الحقائق ٣/ ٢٦٦، شرح فتح القدير ٦/ ١٨، بداية المبتدي ٢/ ٤٤٥، الهداية ٢/ ٤٤٥، الكتاب ٤/ ١٣٥، كشاف القناع ٣/ ١٠٨.
(¬٤) الاختيار ٤/ ١٣٦، بداية المبتدي ٢/ ٤٤٦، تبيين الحقائق ٣/ ٢٦٨، الهداية ٢/ ٤٤٦.
(¬٥) الجلب: ما يجلب للتجارة، من إبل، وغنم، ومتاع، وغيره، من بلد إلى بلد. =','3','417',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1093,'فإن أقام سنةً صار ذميًا، فلا يُمكَّنُ من الرجوع.
والجزيةُ على الغني كل سنةٍ: ثمانيةٌ وأربعون درهمًا،
<hr><span data-type="title">منحة السلوك</span>

الجزية (¬١).
قوله: فإن أقام سنة صار ذميًا؛ لالتزامه الجزية واعتبار المدة من وقت التقدم إليه، لا من وقت دخوله دار الإسلام (¬٢).
قوله: فلا يمكَّنُ من الرجوع.
أي: إلى دار الحرب كما لا يُمكَّن منه بعد ما وضع عليه الخراج، أو إذا تزوجت الحربية ذميًا (¬٣).

<span data-type="title" id=toc-545>[الجزية قدرها وأصحابها]</span>
قوله: والجزية على الغني كل سنة: ثمانية وأربعون درهمًا.
هذا التقدير إذا لم تُوضع الجزية بالتراضي، وأنه متى وُضعت بالتراضي لا يُعدل عنها (¬٤)، كما رُوي عن ابن عباس -رضي الله عنهما- أنه قال: "صالح رسول الله -صلى الله عليه وسلم- أهل نجران على ألفي حلة، النصف في صفر، والنصف في رجب، يؤدونها إلى المسلمين، وعارية ثلاثين درعًا، وثلاثين فرسًا، وثلاثين بعيرًا، وثلاثين من كل صنف من أصناف السلاح يغزون بها، والمسلمون ضامنون
_________
= مختار الصحاح ص ٤٥ مادة ج ل ب، القاموس المحيط ١/ ٥٠٩ مادة ج ل ب، المصباح المنير ١/ ١٠٤ مادة جَلَبْتُ، المغرب ص ٨٦ مادة جلب.
(¬١) شرح فتح القدير ٦/ ٢٢، الهداية ٢/ ٤٤٦، العناية ٦/ ٢٢، تبيين الحقائق ٣/ ٢٦٨.
(¬٢) تبيين الحقائق ٣/ ٢٦٨، العناية ٦/ ٢٢، المختار ٤/ ١٣٦، الكتاب ٤/ ١٣٥، الاختيار ٤/ ١٣٦.
(¬٣) كنز الدقائق ٣/ ٢٦٩، الاختيار ٤/ ١٣٦، تبيين الحقائق ٣/ ٢٦٩، بداية المبتدي ٢/ ٤٤٦، الكتاب ٤/ ١٣٦، المختار ٤/ ١٣٦.
(¬٤) بداية المبتدي ٢/ ٤٥٢، تحفة الفقهاء ٣/ ٢٠٧، الكتاب ٤/ ١٤٣، الهداية ٢/ ٤٥٢، تبيين الحقائق ٣/ ٢٦٩.','3','418',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1094,'وعلى وسط الحال: أربعةٌ وعشرون، وعلى الفقير المعتمل اثنا عشرَ.
<hr><span data-type="title">منحة السلوك</span>

لها حتَّى يُردوها عليهم" الحديث (¬١) رواه أبو داود (¬٢).
فإذا لم توضع بالتراضي، بل وضعت بالقهر بأن غلب الإمام على الكفار وأقرهم على أملاكهم، فيوضع على الغني ثمانية وأربعون درهمًا، يؤخذ منه في كل شهر أربعة دراهم، وعلى وسط الحال: أربعة وعشرون درهمًا، يؤخذ منه في كل شهر درهمان، وعلى الفقير المعتمل (¬٣): اثنا عشر درهمًا، يؤخذ منه في كل شهرٍ درهم (¬٤). نُقِل ذلك عن عمر، وعثمان،
_________
(¬١) وتمامه: "إن كان باليمن كيد، أو غدرة، على أن لا تهدم لهم بيعة، ولا يخرج لهم قس، ولا يفتنوا عن دينهم، ما لم يحدثوا حدثًا، أو يأكلوا الربا".
(¬٢) ٣/ ١٦٧ كتاب الخراج والإمارة والفيء، باب في أخذ الجزية رقم ٣٠٤١، ورواه أيضًا أبو يوسف في الخراج ٧٧، والبيهقي ٩/ ١٩٥ كتاب الجزية باب كم الجزية.
من طريق مصرف بن عمرو اليامي ثنا يونس بن بكير ثنا أسباط بن نصر الهمداني عن إسماعيل بن عبد الرحمن القرشي عن ابن عباس -رضي الله عنهما- قال ...
ورواه أبو عبيد في الأموال ٥٠٢ عن أبي المليح.
قال ابن حجر في الدراية ٢/ ١٣٢: رواته موثقون، إلا أن في سماع السدي من ابن عباس نظر.
(¬٣) أي: الذي يعمل، ومطبوع على العمل بنفسه.
مختار الصحاح ص ١٩١ مادة ع م ل، القاموس المحيط ٣/ ٣١٤ مادة ع م ل.
المصباح المنير ٢/ ٤٣٠ مادة عمِلته.
(¬٤) وهو رواية عن الإمام أحمد.
وقدرها عند المالكية: على الغني أربعة دنانير، أو أربعون درهمًا. وأما الفقير: فينقص عن ذلك، تؤخذ منهم في آخر كل سنة. وعند الشافعية: أقل الواجب من الجزية، دينار على الفقير والمتوسط والغني، ولا حد لأكثره، تؤخذ في آخر السنة.
وعند الحنابلة: مرجع الجزية إلى الإمام، فهي غير مقدرة، بل يرجع فيها إلى اجتهاده، في الزيادة والنقصان، وتؤخذ منهم في آخر كل حول.
الاختيار ٤/ ١٣٧، الكتاب ٤/ ١٤٣، بدائع الصنائع ٧/ ١١٢، المختار ٤/ ١٣٧، الهداية =','3','419',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1095,'وتوضع الجزيةُ على الكتابي، والمجوسي، وعابد الوثن من العجم.
<hr><span data-type="title">منحة السلوك</span>

وعلي، والصحابة متوافرون، ولم ينكر عليهم أحدٌ منهم، فصار إجماعًا (¬١).
قوله: وتوضع الجزية على الكتابي، والمجوسي، وعابد الوثن من العجم (¬٢).
لقوله تعالى: {مِنَ الَّذِينَ أُوتُوا الْكِتَابَ حَتَّى يُعْطُوا الْجِزْيَةَ} [التوبة: ٢٩].
ورُوي عن ابن عمر -رضي الله عنهما-: "أنه لم يأخذ الجزية من المجوس، حتى شهد عبد الرحمن بن عوف -رضي الله عنه- أن النبي -صلى الله عليه وسلم- أخذها من مجوس هجر" (¬٣) رواه
_________
= ٢/ ٤٥٢، الشرح الكبير للدردير ٢/ ٢٠١، حاشية الدسوقي ٢/ ٢٠١، بداية المجتهد ١/ ٤٠٤، المنهاج ٤/ ٣٤٣، زاد المحتاج ٤/ ٣٤٣، كشاف القناع ٣/ ١٢١، الشرح الكبير لابن قدامة ١٠/ ٥٩٣، منتهى الإرادات ٣/ ١٣٠.
(¬١) الهداية ٢/ ٤٥٢، بدائع الصنائع ٧/ ١١٢، تبيين الحقائق ٣/ ٢٧٨، وانظر الدراية لابن حجر ٢/ ١٣٣، ونصب الراية ٣/ ٤٤٧.
(¬٢) اتفق العلماء: على أن الجزية تضرب على أهل الكتاب، وعلى المجوس.
واختلفوا فيمن لا كتاب له، ولا شبهة كتاب، كعَبَدة الأوثان من العرب، والعجم، هل تؤخذ منهم الجزية أم لا؟.
فذهب الحنفية، ورواية عن الإمام أحمد: إلى أنها لا تقبل إلا من العجم منهم، دون العرب.
وذهب المالكية: إلى أنها تؤخذ من كل كافر، عربيًا كان أو أعجميًا.
وذهب الشافعية، والحنابلة: إلى أنها لا تقبل من عبدة الأوثان على الإطلاق، عربيهم وعجميهم، فإما الإسلام، أو القتل.
الاختيار ٤/ ١٣٧، تحفة الفقهاء ٣/ ٣٠٧، الهداية ٢/ ٤٥٢، بدائع الصنائع ٧/ ١١٢، الشرح الصغير ١/ ٣٣٦، بلغة السالك ١/ ٣٦٦، أقرب المسالك ص ٦٨، المنهاج ٤/ ٣٣٦، زاد المحتاج ٤/ ٣٣٦، المحرر ٢/ ١٨٢، المغني ١٠/ ٥٦٣، الإفصاح ٢/ ٢٩٢.
(¬٣) هجر: اسم لجميع أرض البحرين. =','3','420',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1096,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

البخاري، وأحمد، وجماعة أخر (¬١)، وعن المغيرة بن شعبة -رضي الله عنه-، أنه قال لعامل كسرى: "أقرنا نبينا -صلى الله عليه وسلم- أن نُقاتلكم حتى تعبدوا الله وحده، أو تؤدوا الجزية" رواه أحمد، والبخاري (¬٢). وكانوا عبدة الأوثان.
_________
= معجم البلدان ٥/ ٣٩٣، معجم ما استعجم ٤/ ١٣٤٦، القاموس المحيط ٤/ ٤٨١ مادة هـ ج ر.
(¬١) البخاري ٣/ ١١٥١ كتاب الجهاد والسير، باب الجزية والموادعة مع أهل الذمة والحرب رقم ٢٩٨٧، وأحمد ١/ ١٩٠، ورواه أيضًا الشافعي ١١٨٤، وأبو داود ٣/ ١٦٨ كتاب الخراج والإمارة والفيء، باب في أخذ الجزية من المجوس ٣٠٤٣، والنسائي في الكبرى ٥/ ٢٣٤ كتاب السير، باب أخذ الجزية من المجوس رقم ٨٧٦٨، والترمذي ٥/ ٣١٤ كتاب في السير، باب ما جاء في أخذ الجزية من المجوس رقم ١٥٨٦، والدارمي ٢/ ٦٨٤ كتاب السير، باب في أخذ الجزية من المجوس رقم ٢٤٠٦، وابن الجارود ١١٠٥ في باب الجزية ص ٢٧٨، والبيهقي ٩/ ١٨٩ في الجزية، باب المجوس أهل كتاب الجزية تؤخذ منهم، والدارقطني ٢/ ١٥٤ كتاب زكاة الفطر، باب في جزية المجوس وما روي في أحكامهم، والطيالسي ص ٣١ رقم ٢٢٥، وعبد الرزاق كتاب أهل الكتاب، باب أخذ الجزية من المجوس ٦/ ٦٨ رقم ١٠٠٢٤، وابن أبي شيبة ٦/ ٤٢٩ كتاب الجهاد، باب ما قالوا في المجوس تكون عليهم جزية رقم ٣٢٦٤٨، والبغوي في الجهاد ١١/ ١٦٨، الجزية من المجوس رقم ٢٧٥٠، وأبو عبيد في الفيء والخمس والصدقة، باب أخذ الجزية من المجوس ص ٤٠٠، وابن زنجويه في الفيء ووجوهه وسبله باب أخذ الجزية من المجوس ١/ ١٣٧، والحميدي ١/ ٣٥ رقم ٦٤.
ولفظه عند البخاري: حدثنا سفيان قال: سمعت عمرًا قال: كنت جالسًا مع جابر بن زيد، وعمرو بن أوس، فحدثهما بجالة سنة سبعين، عام حجَّ مصعب بن الزبير بأهل البصرة، عند درج زمزم، قال: كنت كاتبًا لجزء بن معاوية عم الأحنف، فأتانا كتاب عمر بن الخطاب -رضي الله عنه- قبل موته بسنة: فرقوا بين كل ذي محرم من المجوس ولم يكن عمر أخذ الجزية من المجوس، حتى شهد عبد الرحمن بن عوف أن رسول الله -صلى الله عليه وسلم- أخذها من مجوس هجر.
(¬٢) البخاري ٣/ ١١٥٢ كتاب الجزية، باب الجزية والموادعة مع أهل الذمة والحرب رقم ٢٩٨٩. =','3','421',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1097,'ولا توضع على عابد الوثن من العرب، ولا المرتد، ولا جزية عَلى من لا يقتل. وتؤخذُ من القسيسين، والرهبان، وأصحاب الصوامع المعتملين.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-546>[المرفوع عنهم الجزية]</span>
قوله: ولا توضع على عابد الوثن من العرب، ولا المرتد؛ لغلظ كفرهما (¬١).
قوله: ولا جزية على من لا يُقتَل -بضم الياء، وفتح التاء- كالصبيِّ، والمرأة، والعبد، والمكاتب، والزمن، والأعمى، والراهب الذي لا يخلط؛ لأنها خلفٌ عن النصرة، وعقوبة، وهم بمعزل عن ذلك (¬٢).
قوله: وتؤخذ من القسيسين، والرهبان، وأصحاب الصوامع المعتملين (¬٣).
لأنهم باعتمالهم أهل الجزية (¬٤). والقسيسون: جمع قسيس، وهو
_________
= ولم أقف عليه عند الإمام أحمد.
قال العلامة عبد الله بن جبرين في تحقيقه لشرح الزركشي ٦/ ٤٤٦: ولم أعثر عليه في المسند.
(¬١) المختار ٤/ ١٣٧، الهداية ٢/ ٤٥٣، العناية ٦/ ٤٩، بداية المبتدي ٢/ ٤٥٣، الاختيار ٤/ ١٣٧.
(¬٢) الاختيار ٤/ ١٣٨، كنز الدقائق ٣/ ٢٧٨، تبيين الحقائق ٣/ ٢٧٨، شرح فتح القدير ٦/ ٥٠.
(¬٣) أي: الذين يعملون، ويكسبون بأنفسهم.
مختار الصحاح ص ١٩١ مادة عمل، القاموس المحيط ٣/ ٣١٤ مادة ع م ل، المصباح المنير ٢/ ٤٣٠ مادة عملته.
(¬٤) وكذا عند المالكية، والحنابلة.
وعند الشافعية: لا جزية على الصبي، والعبد، والمكاتب. وتجب على الزمن، والشيخ، والهرم، والأعمى، والراهب.
الاختيار ٤/ ١٣٨، كنز الدقائق ٣/ ٢٧٨، تبيين الحقائق ٣/ ٢٧٨، شرح فتح القدير ٦/ ٥٠، تحفة الفقهاء ٣/ ٣٠٨، أسهل المسالك ص ١٣٧، سراج السالك ٢/ ٢٧٦، مختصر =','3','422',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1098,'ومن أسلم، أو مات وعليه جزية، سقطت.
وإن اجتمعت جزيتان تداخلتا.
<hr><span data-type="title">منحة السلوك</span>

العالم (¬١). والرهبان: جمع راهب، وهو العَابد (¬٢).
قوله: ومن أسلم، أو مات وعليه جزية، سقطت.
لأنها بدل عن النصرة، وعقوبة على الكفر، فينتفيان بعد الإسلام والموت (¬٣).
وقال الشافعي: لا تسقُط بهما بعد مضي السنة (¬٤).

<span data-type="title" id=toc-547>[تداخل الجزيتين]</span>
قوله: وإن اجتمعت جزيتان تداخلتا.
يعني: إذا لم يؤخذ منه الجزية حتى جاء عليه حولان، وهذا عند أبي حنيفة (¬٥).
_________
= خليل ص ١١٧، الكافي لابن عبد البر ص ٢١٧، جواهر الإكليل ١/ ٢٦٧، نهاية المحتاج ٨/ ٨٨، تحفة المحتاج ٩/ ٢٧٩، المبدع ٣/ ٤٠٩، الشرح الكبير لابن قدامة ١٠/ ٥٨٦.
(¬١) مختار الصحاح ص ٢٢٣ مادة ق س س، القاموس المحيط ٣/ ٦١٨ مادة ق س س، المصباح المنير ٢/ ٥٠٣ مادة القسيس، المعجم الوسيط ٢/ ٧٣٤ مادة القسيس.
(¬٢) مختار الصحاح ص ١٠٩ مادة ر هـ ب، معجم مقاييس اللغة ٢/ ٤٤٧ باب الراء والهاء وما يثلثهما مادة رهب، المصباح المنير ١/ ٢٤١ مادة رهِبَ، التعريفات ص ١٢١.
(¬٣) وإليه ذهب المالكية.
تحفة الفقهاء ٣/ ٣٠٨، الاختيار ٤/ ١٣٨، الهداية ٢/ ٤٥٤، العناية ٦/ ٥٤، منح الجليل ٣/ ٢١٤، الشرح الصغير ١/ ٣٦٨، الشرح الكبير للدردير ٢/ ٢٠٢.
(¬٤) تحفة المحتاج ٤/ ٢٤٩، حاشية الجمل ٥/ ٢١٤.
(¬٥) وعند الحنابلة: من أسلم بعد الحول، وعليه جزية سقطت عنه. وإن مات في أثناء الحول تسقط أيضًا. ولا تسقط إن مات بعد الحول.
تحفة المحتاج ٩/ ٢٨٠، حاشية الشرواني ٩/ ٢٨٠، مغني المحتاج ٤/ ٢٤٩، منتهى الإرادات ٢/ ١٣٠، الإقناع للحجاوي ٣/ ١٢٢.','3','423',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1099,'ويكلف الذميُّ إحضَارها بنفسه، فيعطيها قائمًا، والقابضُ منه قاعدًا.
وفي رواية: يأخذ بتلبيبه ويهزهُ، ويقولُ له:
<hr><span data-type="title">منحة السلوك</span>

وقالا: لا يتداخلان (¬١)، وبه قال: الشافعي (¬٢).
وخراج الأرض، قيل: على هذا الخلاف (¬٣)، وقيل: لا يتداخل فيه اتفاقًا (¬٤).

<span data-type="title" id=toc-548>[كيفية أخذ الجزية]</span>
قوله: ويكلَّف الذميَّ إحضارها بنفسه، فيعطيها قائمًا، والقابض منه -أي: من الذميّ - قاعدًا؛ إظهارًا للصغار عليهم (¬٥).
قيد بقوله: "إحضارها بنفسه"، لأنه إذا بعثها على يد نائبهِ لا يقبل في الصحيح من الرواية (¬٦).
قوله: وفي رواية: يأخذ بتلبيبه ويهزه. أي: يهُزهُ القابض، ويقول له:
_________
(¬١) مختصر الطحاوي ص ٢٩٤، تحفة الفقهاء ٣/ ٣٠٨، الاختيار ٤/ ١٣٨، الهداية ٢/ ٤٥٤.
(¬٢) لأنها عوض، والأعواض لا تسقط بمضي الزمان، فصار كخراج الأرض.
مختصر الطحاوي ص ٢٩٤، تحفة الفقهاء ٣/ ٣٨، تبيين الحقائق ٣/ ٢٧٨، شرح فتح القدير ٦/ ٥٣، الهداية ٢/ ٤٥٤.
(¬٣) ومالك، وأحمد.
الكافي لابن عبد البر ص ٢١٧، الشرح الصغير ١/ ٣٦٨، شرح المحلي ٤/ ٢٣٠، السراج الوهاج ص ٥٥٠، الإفصاح ٢/ ٢٩٤، المحرر ٢/ ١٨٤.
(¬٤) أي: إذا مضت سنون، لم يؤخذ منه خراج عند أبي حنيفة.
وعندهما: يؤخذ منه ما مضى.
بدائع الصنائع ٧/ ١١٢، شرح فتح القدير ٦/ ٥٥، تبيين الحقائق ٣/ ٢٧٩، حاشية الشلبي ٣/ ٢٧٩.
(¬٥) لأنه يجب مؤنة الأرض قائمًا مقام العشر، ولهذا لا يجتمعان، والعشر يتكرر فكذا هذا.
العناية ٦/ ٥٥، شرح فتح القدير ٦/ ٥٥، بدائع الصنائع ٧/ ١١٢، تبيين الحقائق ٣/ ٢٧٩.
(¬٦) الهداية ٢/ ٤٥٥، شرح فتح القدير ٦/ ٥٦، تبيين الحقائق ٣/ ٢٧٩.','3','424',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1100,'اعطِ الجزية يَا ذِمَيُّ. وفي رواية: يا عدوَّ الله.
وتجب بأول الحول، ويمهل إلى آخره تيسيرًا.
<hr><span data-type="title">منحة السلوك</span>

اعطِ الجزية يا ذميُّ (¬١)، وفي رواية: يا عدوَّ الله (¬٢). هذا كلُّه لأجل الذل، والهوان. قال الله تعالى: {حَتَّى يُعْطُوا الْجِزْيَةَ عَنْ يَدٍ وَهُمْ صَاغِرُونَ} [التوبة: ٢٩] أى: أذلاّء (¬٣).

<span data-type="title" id=toc-549>[وقت وجوب الجزية]</span>
قوله: وتجب. أي: الجزيةُ بأوَّل الحول، ويمهل إلى آخره تيسيرًا؛ ليتمكن من القدرة على أدائها (¬٤).
وقال الشافعي: إذا وُضعت الجزية على الذميّ، فلا تجب بعد حولان الحول (¬٥).
_________
(¬١) الهداية ٢/ ٤٥٥، تبيين الحقائق ٣/ ٢٧٩، الاختيار ٤/ ١٣٩.
(¬٢) الاختيار ٤/ ١٣٩.
(¬٣) وإلى هذا ذهب المالكية، والحنابلة: فمن أخذها منهم أخذها على وجه الإهانة، والذل، والصغار؛ امتثالًا لأمر الله.
وذهب الشافعية: إلى أن الجزية تؤخذ منهم برفق، ولا يُؤذَون في أخذها بقول ولا فعل، والصغار المذكور في الآية يتمثل في أخذها منهم وجريان أحكام الإسلام عليهم.
الهداية ٢/ ٤٥٥، العناية ٦/ ٥٦، شرح فتح القدير ٦/ ٥٦، تبيين الحقائق ٣/ ١٣٩، جواهر الإكليل ١/ ٢٦٧، منح الجليل ٣/ ٢١٧، المهذب ٢/ ٢٥٣، منهج الطلاب ٥/ ٢١٩، مغني المحتاج ٤/ ٢٤٩، الحاوي الكبير ١٤/ ٢٩٦، غاية المنتهى ٢/ ٥٩٩، المغني ١٠/ ٦٢٠، الشرح الكبير لابن قدامة ١٠/ ٥٩٨، تفسير ابن كثير ٢/ ٥٤٣، الكشاف للزمخشري ٢/ ١٤٨.
(¬٤) العناية ٦/ ٥٦، تبيين الحقائق ٣/ ١٣٩، شرح فتح القدير ٦/ ٥٦.
(¬٥) وإلى هذا ذهب المالكية، والحنابلة.
الشرح الكبير للدردير ٢/ ٢٠٢، الشرح الصغير ١٠/ ٦٧، متن الزبد ص ٦٧، التذكرة ص ١٥٦، التسهيل ص ١٩٥، العمدة لابن قدامة ص ١١٦.','3','425',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1101,'<span data-type="title" id=toc-550>فصل</span>
ولا يجوز إحداث بيعةٍ، ولا كنيسةٍ، في دار الإسلام، ويعاد ما انهدم كما كان،
<hr><span data-type="title">منحة السلوك</span>

فصل
هذا الفصل في بيان ما يعْمل مع أهل الذمة، وبيان مصارف الجزية (¬١) ونحوها (¬٢).

<span data-type="title" id=toc-551>[معابد أهل الذمة]</span>
قوله: ولا يجوز إحداث بيعة، ولا كنيسة، في دار الإسلام.
لقوله -صلى الله عليه وسلم-: "لا خصاء في الإسلام، ولا كنيسة" (¬٣). أي: لا يُخصي إخصاء، ولا يحدث كنيسة في موضع لم يكن فيه. وبيت النار كالكنيسة (¬٤). فالبيعة: لليهود، والكنيسة: للنصارى.
قوله: ويعادُ ما انهدم كما كان.
لأنه جرى التواتر من لدن رسول الله -صلى الله عليه وسلم- إلى يومنا هذا بترك الكنائس
_________
(¬١) كالخراج، وهدايا أهل الحرب. ولما فرغ المصنف من بيان ما يجب على أهل الذمة من الجزية، شرع في بيان معابدهم، ما يجوز منها، وما لا يجوز.
حاشية الشلبي ٣/ ٢٧٩.
(¬٢) بداية المبتدي ٢/ ٤٥٥، الاختيار ٤/ ١٤٠، الهداية ٢/ ٤٥٥، المختار ٤/ ١٤٠.
(¬٣) رواه أبو عبيد القاسم بن سلام في كتاب الأموال ص ٩٤، قال: حدثنا عبد الله بن صالح، ثنا الليث بن سعد، حدثني توبة بن النمر الحضرمي - قاضي مصر-، عمن أخبره عن النبي -صلى الله عليه وسلم- قال ... "، ورواه البيهقي ١٠/ ٢٤ كتاب السبق والرمي، باب كراهية خصاء البهائم. من طريق مقدام بن داود، ثنا النضر بن عبد الجبار، ثنا ابن لهيعة، عن عطاء، عن ابن عباس -رضي الله عنهما- أن رسول الله -صلى الله عليه وسلم- قال: "لا إخصاء في الإسلام، ولا بنيان كنيسة".
قال ابن حجر في الدراية ٢/ ١٣٥: إسناد البيهقي ضعيف وما رواه أبو عبيد مرسل.
(¬٤) شرح فتح القدير ٦/ ٥٩، العناية ٦/ ٥٩، تبيين الحقائق ٣/ ٢٨٠.','3','426',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1102,'ولا ينقل.
<hr><span data-type="title">منحة السلوك</span>

في أمصار المسلمين، ولا يقوم البناء دائمًا، فكان دليلًا على جواز الإعادة (¬١).
قوله: ولا ينقل.
أي: لا تُنقل البيعة، أو الكنيسة من مكان إلى مكان؛ لأنه إحداث في ذلك الموضع في الحقيقة (¬٢) (¬٣).
_________
(¬١) تبيين الحقائق ٣/ ٢٨٠، شرح فتح القدير ٦/ ٥٨، بداية المبتدي ٢/ ٤٥٥، الهداية ٢/ ٤٥٥، العناية ٦/ ٥٧.
(¬٢) تبيين الحقائق ٣/ ٢٨٠، الهداية ٢/ ٤٥٦، شرح فتح القدير ٦/ ٥٨.
(¬٣) أمصار المسلمين على ثلاثة أقسام:
القسم الأول: ما مصره المسلمون. كالبصرة، والكوفة، وبغداد، وواسط، فلا يجوز فيه - أي المصر- إحداث كنيسة، ولا بيعة، ولا مجتمع لصلاتهم. ولا يجوز صلحهم على ذلك. وقد ساق الإجماع على ذلك ابن هبيرة في الإفصاح ٢/ ٣٠٠ فقال: واتفقوا على أنه لا يجوز إحداث كنيسة، ولا بيعة في المدن، والأمصار في بلاد الإسلام". ا. هـ.
القسم الثاني: ما فتحه المسلمون عنوة، فلا يجوز إحداث شيء من ذلك؛ لأنها صارت ملكًا للمسلمين. وإليه ذهب الحنفية، والشافعية، والحنابلة، وهو قول: عند المالكية.
والمذهب المعتمد عند المالكية: أن ما فتحه المسلمون عنوة لا يجوز إحداث كنيسة فيه، ولا ترميم منهدم، إلا إن شرط الإحداث عند ضرب الجزية عليه، ورضي الإمام به، وإلا فهو مقهور.
القسم الثالث: ما فتح صلحًا وهو نوعان:
أحدها: أن يصالحهم على أن الأرض لهم ولنا الخراج، فلهم إحداث ما يحتاجون عنها لأن الدار لهم.
النوع الثاني: أن يصالحهم على أن الدار للمسلمين، ويؤدون الجزية إلينا، فالحكم في البيع، والكنائس على ما يقع عليه الصلح معهم، من إحداث ذلك، وعمارته؛ لأنه إذا جاز أن يقع الصلح معهم على أن الكل لهم، جاز أن يصالحوا على أن يكون بعض البلد لهم. وهذا عند الحنفية، والشافعية، والحنابلة. =','3','427',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1103,'ويُميَّزُ أهل الذمَّة عن المسلمين في زيِّهم، ومراكبهم، وسروجهم، وقلانسهم،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-552>[معاملة أهل الذمة]</span>
قوله: ويُميَّز أهل الذمة عن المسلمين في زيهم، ومراكبهم، وسروجهم، وقلانسهم.
إظهارًا للصّغار عليهم، فلا يلبسون ما يختصُّ بأهل العلم، والزهد، والشرف، ولا يلبسون طيالسة (¬١) مثل طيالسة المسلمين، ولا أردية مثل أرديتهم (¬٢).
_________
= وذهب المالكية: إلى أن للصلحيّ الإحداث والترميم في أرضه مطلقًا، شرط ذلك أم لا، ولهم رم ما تشعث منها، وإصلاحها؛ لأن المنع من ذلك يفضي إلى خرابها وذهابها، فجرى مجرى هدمها.
تبيين الحقائق ٣/ ٢٨٠، شرح فتح القدير ٦/ ٥٨، الهداية ٢/ ٤٥٥، الاختيار ٤/ ١٤٠، جواهر الإكليل ١/ ٢٦٨، الخرشي على خليل ٣/ ١٤٨، القوانين ص ١٠٥، أقرب المسالك ص ٦٨، التاج والإكليل ٣/ ٣٨٥، الشرح الصغير ١/ ٣٦٩، الذخيرة ٣/ ٤٥٨، رحمة الأمة ٢/ ١٨٦، مغني المحتاج ٤/ ٢٥٣، الحاوي الكبير ١٤/ ٣٢٠، أسنى المطالب ٤/ ٢١٩، كشاف القناع ٣/ ١٣٣، المغني ١٠/ ٥٩٩، شرح منتهى الإرادات ٢/ ١٣٤، المبدع ٣/ ٤٢١، الروض المربع ص ٢٢٦.
(¬١) الطيالسة: نوع من الأوشحة، يُلْبَسُ على الكتف، أو يحيط بالبدن، خالٍ من التفصيل والخياطة، معرب تالسان.
المصباح المنير ٢/ ٣٧٥ مادة الطِّلسْ، المعرب ص ٢٩١ مادة الطيلسان، المغرب ص ٤٤٦، مختار الصحاح ص ١٦٦ مادة ط ل س، المعجم الوسيط ٢/ ٥٦١ مادة الطالِسان.
(¬٢) وفاقًا للمالكية، والشافعية، والحنابلة، في التفريق بين أهل الذمة في هيئتهم، في الملبس، والمركب، وبين هيئات المسلمين.
الهداية ٢/ ٤٥٦، تبيين الحقائق ٣/ ٢٨٠، الاختيار ٤/ ١٣٩، شرح فتح القدير ٦/ ٦٢، العناية ٦/ ٦١، مختصر خليل ص ١١٨، مواهب الجليل ٣/ ٣٨٥، الشرح الصغير ١/ ٣٦٩، جواهر الإكليل ١/ ٢٦٨، أنوار المسالك ص ٤٥٧، الحاوي الكبير ١٤/ ٣٢٥، الروض المربع ص ٢٢٦، المحرر ٢/ ١٨٥، منتهى الإرادات ٢/ ١٣٢، المبدع ٣/ ٤١٧، المقنع ٢/ ٥٢٨.','3','428',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1104,'ولا يركبون الخيل، ولا يحملون السلاح، ويُجعلُ على أبوابهم علامة، حتى لا يقف عليها سائل يدعو لهم. ويميزُ نساؤهم عن نسائِنا في الطريق، والحمَّامات بعلامةٍ، ويوؤمرُ الذمي بشد الزِّنارِ من الصوف الغليظ، دون الإبريسم.
<hr><span data-type="title">منحة السلوك</span>

قوله: ولا يركبون الخيل.
لأنهم ليسوا من أهل الجهاد. وإن ركبوا لضرورة من سفر، أو مرض نزلوا في مجامع المسلمين، ولذلك لا يحملون السلاح (¬١).
قوله: ويجعل على أبوابهم علامة، حتى لا يقف عليها سائل؛ كيلا يدعو لهم بمثل المغفرة والرحمة (¬٢).
قوله: ويميز نساؤُهم عن نسائِنا في الطريق، والحمامات بعلامة؛ لأن في تركها ذلًا لنساء المسلمين (¬٣).
قوله: ويؤمر الذمي بشدِّ الزنار (¬٤) من الصوف الغليظ؛ لأن في ذلك إهانة لهم (¬٥).
قوله: دون الإبريسم (¬٦).
_________
(¬١) الاختيار ٤/ ١٤٠، كنز الدقائق ٣/ ٢٨٠، بداية المبتدي ٢/ ٤٥٦، المختار ٤/ ١٤٠، الهداية ٢/ ٤٥٦، شرح فتح القدير ٦/ ٦١، تبيين الحقائق ٣/ ٢٨٠.
(¬٢) الهداية ٢/ ٤٥٦، شرح فتح القدير ٦/ ٦٢، تبيين الحقائق ٣/ ٢٨١.
(¬٣) تبيين الحقائق ٣/ ٢٨١، شرح فتح القدير ٦/ ٦١، العناية ٦/ ٦١، الهداية ٢/ ٤٥٦.
(¬٤) الزنار: حزام للنصارى، يشد على الوسط.
تاج العروس ٤/ ٢٤٣ مادة زنر، القاموس المحيط ٢/ ١٨٢ مادة ز ن ر، مختار الصحاح ص ١١٦ مادة ز ن ر، المغرب ص ٣٥٠ مادة الزَّنْرُ.
(¬٥) الهداية ٢/ ٤٥٦، العناية ٦/ ٦١، شرح فتح القدير ٦/ ٦١، تبيين الحقائق ٢/ ٤٥٦.
(¬٦) الإبريسم: أحسن الحرير. =','3','429',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1105,'ويمنع من لباسٍ يختصُّ به أهل العلم، والزهد، والشرف، كالصوف ونحوه.
<hr><span data-type="title">منحة السلوك</span>

أي؛ يمنع من شد الزنار من الإبريسم؛ لأنه لا إهانة في ذلك (¬١)، ولا يمنع من الكستيج وهو: الخيط الغليظ (¬٢).
قوله: ويمنع من لباس يختص به أهل العلم، والزهد، والشرف، كالصوف، والفرجية (¬٣)، والعمامة المدوَّرة (¬٤)، والعذبة (¬٥)، والدراعة (¬٦)، والطيالسة، ونحوها (¬٧).
_________
= المعجم الوسيط ١/ ٢ مادة الإبريسم، المصباح المنير ١/ ٤١ مادة البرسام، المغرب ص ١٣٠، مادة الإبريسم، مختار الصحاح ص ٢٠ مادة ب رس م.
(¬١) تبيين الحقائق ٣/ ٢٨١، شرح فتح القدير ٦/ ٦١، العناية ٦/ ٦١.
(¬٢) القاموس المحيط ١/ ٤٧ مادة ك س ت ج، المغرب ص ٤٠٧ مادة الكستيج، تاج العروس ٢/ ٩١ مادة كوسج.
(¬٣) الفرجية: رداء للمولدين يلبس فوق الثياب، وهو ثوب واسع الأكمام، يتزيا به علماء الدين.
محيط المحيط ص ٦٨١ مادة الفرجية، المعجم الوسيط ٢/ ٦٧٩ مادة الغربية.
(¬٤) العمامة: جمعها عمائم، وتعممت كورت العمامة على الرأس، والعمائم تيجان العرب، يقال: كور العمامة، أي: دورها، أو قد كار العمامة، أي: لفها.
المصباح المنير ٢/ ٤٣٠ مادة عَمَّ، محيط المحيط ٦٣٤ مادة العمامة، المعجم الوسيط ٢/ ٦٢٩ مادة العمامة، طلبة الطلبة ص ٢٨.
(¬٥) العَذبَة: طرف كل شيء، والاعتذاب: أن تسبِلَ للعمامة عذبتين من خلفها.
القاموس المحيط ٣/ ١٧٦، تاج العروس ١/ ٣٧٠ مادة عذب، المصباح المنير ٢/ ٣٩٨ مادة عذب.
(¬٦) الدراعة: جبة مشقوقة المقدم، ولا تكون إلا من صوف.
تاج العروس ٥/ ٣٢٦ مادة د ر ع، القاموس المحيط ٢/ ١٧٠ مادة د ر ع، مختار الصحاح ص ٨٥ مادة د ر ع، لسان العرب ٨/ ٨٢ مادة د ر ع.
(¬٧) الهداية ٢/ ٤٥٦، تبيين الحقائق ٣/ ٢٨١، شرح فتح القدير ٦/ ٦٠.','3','430',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1106,'ولا يبتدأ بالسلام، ولا بأس برد سلامه، ولا يزيد الرَّاد على قوله: وعليكم. ولو قال في جوابه: السلام على من اتبع الهدى جاز.
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-553>[السلام على الذمي]</span>
قوله: ولا يبتدأ بالسلام.
أي: ولا يبدأ الذميُّ بالسلام؛ لأن فيه إكرامًا له (¬١).
قوله: ولا بأس برد سلامه.
يعني: إذا سلم الذمي على المسلم لا بأس للمسلم أن يردَّ سلامه (¬٢) ولا يزيد الراد على قوله: وعليكم (¬٣)؛ لقوله -صلى الله عليه وسلم-: "إذا سلم عليكم أحدٌ من أهل الكتاب، فقولوا: وعليكم" رواه ابن ماجه (¬٤).
قوله: ولو قال في جوابه: السلام على من اتبع الهدى جاز (¬٥)؛ لورود الأثر بذلك (¬٦).
_________
(¬١) بداية المبتدي ٢/ ٤٥٦، شرح فتح القدير ٦/ ٦٠، الهداية ٢/ ٤٥٦، تبيين الحقائق ٣/ ٢٨١، العناية ٦/ ٦١.
(¬٢) شرح فتح القدير ٦/ ٦١.
(¬٣) وكذا عند المالكية، والشافعية، والحنابلة، في السلام ورده.
شرح فتح القدير ٦/ ٦١، تبيين الحقائق ٣/ ٢٨١، القوانين ص ٢٩٢. التلقين ص ١٨٨، الكافي لابن عبد البر ص ٦١٠، شرح المحلي ٤/ ٢١٥، الحاوي الكبير ١٤/ ٣١٩، منتهى الإرادات ٢/ ١٣٣، كشاف القناع ٣/ ١٣٠.
(¬٤) ٢/ ١٢١٩ كتاب الأدب، باب رد السلام على أهل الذمة رقم ٣٦٩٧، ورواه أيضًا البخاري ٥/ ٢٣٠٩ كتاب الاستئذان، باب كيف الرد على أهل الذمة بالسلام رقم ٥٩٠٣، ومسلم ٤/ ١٧٠٥ كتاب السلام باب النهي عن ابتداء أهل الكتاب بالسلام وكيف يرد عليهم رقم ٢١٦٣. عن أنس بن مالك -رضي الله عنه-.
(¬٥) شرح فتح القدير ٦/ ٦١، تبيين الحقائق ٣/ ٢٨١.
(¬٦) يشير إلى ما رواه البخاري في صحيحه ٥/ ٢٣١٠ كتاب الاستئذان، باب كيف يكتب الكتاب إلى أهل الكتاب رقم ٥٩٠٥ من طريق عبيد الله بن عبد الله بن عتبة، أن ابن =','3','431',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1107,'ولو قال للذميَّ: أطال الله بقاك لم يجز، إلا إذا نَوىَ به إطالة بقائه لإسلامه، أو لمنفعة الجزية. ويُضيق عليه الطريق. ولا ينتقض عهد الذمة، إلا بأن يلحق بدار الحرب، أو يغلبوا على موضعٍ ويحاربونا،
<hr><span data-type="title">منحة السلوك</span>


<span data-type="title" id=toc-554>[الدعاء لأهل الذمة]</span>
قوله: ولو قال للذمي: أطال الله بقاك لم يجز؛ لأن فيه التمادي على الكفر، إلا إذا نوى به. أي: بهذا الدعاء إطالة بقائه لأجل أن يسلم، أو لمنفعة الجزية؛ لأن الدعاء فيهما لا يرجع إلى الذمي (¬١).

<span data-type="title" id=toc-555>[انتقاض عهد الذمة]</span>
قوله: ويضيق عليه الطريق؛ للإهانة (¬٢)، ولا ينتقض عهد الذمة إلا أن يلحق بدار الحرب؛ لأنه بذلك صار حربًا علينا، فينتفي المقصود من بقاء العهد، وكذلك إذا غلبوا على موضع، وحاربوا (¬٣).
_________
= عباس أخبره، أن أبا سفيان بن حرب أخبره، أن هرقل أرسل إليه في نفر من قريش، وكانوا تجارًا بالشام فأتوه، قال: ثم دعا بكتاب رسول الله -صلى الله عليه وسلم- فقريء، فإذا فيه: "بسم الله الرحمن الرحيم، من محمد عبد الله ورسوله، إلى هرقل عظيم الروم، السلام على من اتبع الهدى، أما بعد ... ".
(¬١) تبيين الحقائق ٣/ ٢٨١، الهداية ٢/ ٤٥٦.
(¬٢) وفاقًا للمالكية، والشافعية والحنابلة.
الهداية ٢/ ٤٥٦، تبيين الحقائق ٣/ ٢٨١، بداية المبتدي ٢/ ٤٥٦، كنز الدقائق ٣/ ٢٨٢، القوانين الفقهية ص ١٠٥، جواهر الإكليل ١/ ٢٦٨، السراج الوهاج ص ٥٥٣، روض الطالب ٤/ ٢٢١، الروض المربع ص ٢٢٦، غاية المنتهى ٢/ ٦٠٨، الإقناع للحجاوي ٣/ ١٣٠، شرح منتهى الإرادات ٢/ ١٣٣.
(¬٣) اختلف العلماء فيما ينتقض به عهد الذمي:
فقال الحنفية: لا ينتقض إلا أن يكون لهم منعة، ويحاربونا بها، أو يلحقوا بدار الحرب.
وذهب المالكية، والشافعية، والحنابلة: أنه ينتقض عهده بمنع الجزية، وبإبائه أن تجرى أحكام الإسلام عليه إذا حكم حاكم عليه بها. فإن فعل أحدهم ما يجب عليه تركه، والكف عنه مما فيه ضرر على المسلمين، أو آحادهم من مال، أو نفس، وذلك أحد ثمانية أشياء: الاجتماع على قتال المسلمين، أو يزني بمسلمة، أو يصيبها باسم نكاح، أو يفتن مسلمًا عن دينه، أو يقطع عليه الطريق، أو يؤدي للمشركين جاسوسًا، أو يعاون على المسلمين =','3','432',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1108,'فعند ذلك هم كالمرتدين،
<hr><span data-type="title">منحة السلوك</span>

قوله: فعند ذلك هم كالمرتدين (¬١) في حل قتلهم، ودفع مالهم لورثتهم؛ لأنهم التحقوا بالأموات بتباين الدارين (¬٢).
_________
= بدلالة، وهو أن يكاتب المشركين بأخبار المسلمين، أو يقتل مسلمًا أو مسلمة عمدًا.
وهل ينتقض عهده بذلك أم لا؟
ذهب الحنفية: إلى أنه لا ينتقض عهده بهذه الأشياء الثمانية، ولا بمنع الجزية، ولا بإبائه جريان أحكام الإسلام عليه، إلا أن يكون لهم منعة، فيغلبوا على موضع، ويحاربونا، أو يلحقوا بدار الحرب.
وعند المالكية: ينتقض عهده بقتال عام للمسلمين يقتضي خروجه عن الذمة، وبغضب حرة مسلمة، وبتطلعه على عورات المسلمين، وبسب نبي بما لم يكفر به، أي: بما لم نقرهم عليه من كفرهم، لا بما أقروه كنحو عيسى ابن الله.
وعند الشافعية: ينتقض عهده بقتال عام للمسلمين، سواء شرط ذلك عليه أم لا، ولو زنى بمسلمة، أو أصابها بنكاح، أو دل أهلَ الحرب على عورة للمسلمين، أو فتن مسلمًا عن دينه، أو طعن في الإسلام، أو القرآن، أو ذكر رسول الله -صلى الله عليه وسلم- بسوء، انتقض العهد بها، إن شرط انتقاض العهد بها وإلا فلا.
وعند الحنابلة: ينتقض عهده إذا قاتلنا منفردًا، أو مع أهل حرب، أو لحق بدار حرب مقيمًا، أو زنى بمسلمة، أو أصابها باسم نكاح، أو قطع طريقًا، أو تجسس، أو آوى جاسوسًا، أو ذكر الله، أو ذكر كتابه، أو دينه، أو رسوله -صلى الله عليه وسلم- بسوء ونحوه، أو تعدى على مسلم بقتل، أو فتنة عن دينه، انتقض عهده، سواء شرط عليهم ذلك، أو لم يشرط.
شرح فتح القدير ٣/ ٦٣، تبيين الحقائق ٣/ ٢٨٢، الكتاب ٤/ ١٥٢، العناية ٦/ ٦٣، الهداية ٢/ ٤٥٦، الشرح الكبير للدردير ٢/ ٢٠٥، مختصر خليل ص ١١٨، جواهر الإكليل ١/ ٢٦٨، بلغة السالك ١/ ٣٦٩، المنهاج ٤/ ٣٥٦، زاد المحتاج ٤/ ١٥٦، مغني المحتاج ٤/ ٢٥٨، رحمة الأمة ٢/ ١٨٤، الشرح الكبير لابن قدامة ١٠/ ٦٢٣، شرح منتهى الإرادات ٢/ ١٣٨، الروض المربع ص ٢٢٧، كشاف القناع ٣/ ١٤٣، الإفصاح ٢/ ٢٩٨، المحرر ٢/ ١٨٨، المبدع ٣/ ٤٣٣.
(¬١) في س، ر، ص، م، ي زيادة "أي: عند اللحاق بدار الحرب، أو الغلبة على موضع يصيرون كالمرتدين".
(¬٢) كنز الدقائق ٣/ ٢٨٢، الهداية ٢/ ٤٥٦، تبيين الحقائق ٣/ ٢٨٢.','3','433',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1109,'إلا أَنَّهم يسترقُّون بخلاف المرتدين.
ومال الخراج، والجزيَةِ، وهدايا أهل الحرب، تصرف في مصالح
<hr><span data-type="title">منحة السلوك</span>

قوله: إلا أنهم يسترقون.
يعني: صيرورتهم كالمرتدين ليست من جميع الوجوه؛ لأنهم يسترقون، ولا يجبرون على قبول الذمة، بخلاف المرتدين حيث لا يسترقون ويجبرون على الإسلام؛ لأن كفرهم أغلظ، فأوجب الزيادة في العقوبة (¬١).

<span data-type="title" id=toc-556>[مصارف الجزية]</span>
قوله: ومال الخراج، والجزية، وهدايا أهل الحرب، تصرف إلى آخره (¬٢).
لأنه مأخوذ بقوة المسلمين، فيصرف إلى مصالحهم (¬٣).
_________
(¬١) تبيين الحقائق ٣/ ٨٢، العناية ٦/ ٦٣، الهداية ٢/ ٤٥٦، شرح فتح القدير ٣/ ٢٨٢.
(¬٢) وتمامه: "في مصالح المسلمين، كسد الثغور، وبناء القناطر، والجسور، وأرزاق القضاة، والعلماء، والغزاة مع أولادهم".
(¬٣) وإليه ذهب الحنابلة، في أن مال الخراج، والجزية وهدايا أهل الحرب تصرف في مصالح المسلمين، ولا يختص بالمقاتلة. ويبدأ بالأهم فالأهم، من سد بشق، وتعزيل نهر.
وعند المالكية: يأخذ الإمام من الخمس حاجته منه، بغير تقدير، ويصرف الباقي من الخمس في مصالح المسلمين، كسد الثغور، والمخاوف، واستعداد آلة الحرب، وبنيان المساجد، والقناطر. والأربعة الأخماس الباقية بعد الخمس، تقسم على المقاتلين.
وعند الشافعية: يخمس مال الخراج، والجزية، خمسة أخماس، ويقسم خمسه على خمسة أسهم، سهم لرسول الله -صلى الله عليه وسلم- ومصرفه بعده للمصالح العامة، كسد الثغور، وعمارة الحصون، والقناطر، والمساجد، وأرزاق القضاة، والأمراء. الأهم فالأهم. وسهم لذوي القربى، وسهم لليتامى، وسهم للمساكين، وسهم لابن السبيل. ومن فُقد من هذه الأصناف، أعطي الباقون نصيبه، إلا سهم رسول الله -صلى الله عليه وسلم- فإنه لا يصرف إلا للمصالح العامة. وأما الأربعة الأخماس: فهي للمرتزقة، وهم المرصدون للجهاد.
الكتاب ٤/ ١٥٣، الهداية ٢/ ٤٥٧، شرح فتح القدير ٦/ ٦٧، التلقين ص ٧٣، القوانين ص ١٠١، جواهر الإكليل ١/ ٢٦٠، أسنى المطالب ٣/ ٨٧، مغني المحتاج ٣/ ٩٣، =','3','434',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1110,'المسلمين، كسد الثغور، وبناء القناطر، والجسور، وأرزاق القضاةِ، والعلماء، والغزاة
<hr><span data-type="title">منحة السلوك</span>

والثغور: جمع ثغرٍ (¬١). والقناطر: جمع قنطرة. والجسور: جمع جسر.
والقنطرة تستلزم الجنس من دون عكسٍ؛ لأنها ما يُبنى من الحجر، بخلاف الجسر فإنه من الحجر، والخشب، وغير ذلك (¬٢).
والقُضاة: جمع قاضٍ (¬٣). والغزاة: جمع غازٍ (¬٤).
اعلم أن ما يجيء إلى بيت المال أنواع أربعة:
أحدها: هذا الذي ذكرناه مع مصرفه.
والثاني: الزكاة، والعشر. ومصرفهما ما ذكره الله تعالى من قوله: {إِنَّمَا الصَّدَقَاتُ لِلْفُقَرَاءِ وَالْمَسَاكِينِ} الآية [التوبة: ٦٠]، وقد مر (¬٥).
_________
= الروض المربع ص ٢٢٤، الإقناع للحجاوي ٣/ ١٠٠، الإفصاح ٢/ ٢٩٠.
(¬١) وهو: موضع المخافة، من حصن وغيره. وقيل: موضع المخافة من أطراف البلاد، وما يلي دار الحرب.
معجم مقاييس اللغة ١/ ٣٧٨ باب الثاء والغين وما يثلثهما مادة ثغر، مختار الصحاح ص ٣٦ مادة ث غ ر، القاموس المحيط ١/ ٤٠٨ مادة ث غ ر، المصباح المنير ١/ ٨١ مادة الثغر.
(¬٢) المغرب ص ٨٣ مادة الجسر، المصباح المنير ١/ ١٠١، لسان العرب ٤/ ١٣٦ مادة "جسر".
(¬٣) والقاضي -اسم فاعل-: وهو الحاكم الشرعي؛ لأنه يفصل الدعاوى بين الأخصام، ويحكم بها، ويبين الحق من الباطل.
تهذيب الأسماء واللغات ٤/ ٩٥ مادة قضى، المصباح المنير ٢/ ٥٠٧ مادة قضيتُ، معجم مقاييس اللغة ٥/ ٩٩ باب القاف والضاد وما يثلثهما مادة قضى، لسان العرب ١٥/ ١٨٦ مادة قضي.
(¬٤) وهو الذي يغزو العدو في بلادهم.
تاج العروس ١٠/ ٢٦٥ مادة غزا، المغرب ص ٣٣٩ مادة غزوت، لسان العرب ١٥/ ١٢٣ مادة غزا، المصباح المنير ٢/ ٤٤٧ مادة غزوت.
(¬٥) في ٣/ ٨٠.','3','435',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1111,'مع أولادهم، والعمال.
<hr><span data-type="title">منحة السلوك</span>

والثالث: خمس الغنائم، والمعدن، والركاز. ومصرفه ما ذكرهم الله في قوله: {فَأَنَّ لِلَّهِ خُمُسَهُ} الآية (¬١).
والرابع: اللقطات، والتركات (¬٢) التي لا وارث لها، وديات مقتول لا ولي له (¬٣). ومصرفها: الفقراء الذين لا أولياء لهم، يُعطون منه نفقتهم، وأدويتهم، ويكفَّن به موتاهم، وتُعقل به جنايتهم.
وعلى الإمام أن يتقي الله، ويصرف إلى كل مستحقَّ قدر حاجته من غير زيادة، فإن قصَّر في ذلك كان الله عليه حسيبًا (¬٤).
قوله: مع أولادهم.
يعني: يصرف إليهم بقدر ما يكفيهم، ويكفي أولادهم؛ لأنهم لو لم يعطوا هكذا، لاحتاجوا إلى الاكتساب، فتتعطل مصالح المسلمين (¬٥).
قوله: والعُمال -بضم العين-: جمع عامل، وهو الذي يقبض
_________
(¬١) سورة الأنفال الآية: ٤١ وتمامها {وَلِلرَّسُولِ وَلِذِي الْقُرْبَى وَالْيَتَامَى وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ إِنْ كُنْتُمْ آمَنْتُمْ بِاللَّهِ وَمَا أَنْزَلْنَا عَلَى عَبْدِنَا يَوْمَ الْفُرْقَانِ يَوْمَ الْتَقَى الْجَمْعَانِ وَاللَّهُ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}.
(¬٢) تركة الرجل: ميراثه. والتركة في الأصل: ما يتركه الشخص، ويبقيه.
وفي الاصطلاح: ما ترك الإنسان، صافيًا خاليًا عن حق الغير.
القاموس المحيط ١/ ٣٦٦ مادة ت ر ك، المصباح المنير ١/ ٧٤ مادة تركت، مختار الصحاح ص ٣٢ مادة ت ر ك.
(¬٣) تبيين الحقائق ٣/ ٢٨٣.
(¬٤) تبيين الحقائق ٣/ ٢٨٣.
(¬٥) الهداية ٢/ ٤٥٧، شرح فتح القدير ٦/ ٦٧، الكتاب ٤/ ١٥٣، كنز الدقائق ٣/ ٢٨٣، تبيين الحقائق ٣/ ٢٨٣.','3','436',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1112,'ومن مات قبل القبض سقط نصيبه.
<hr><span data-type="title">منحة السلوك</span>

الزكوات، والعُشورات، والخراجات، والجزية (¬١).
قوله: ومن مات قبل القبض سقط نصيبه.
لأنه صلةٌ فلا يملك قبل القبض (¬٢)، وعلى هذا قيل: إن الإمام، أو المؤذن، أو المدرس إذا مات قبل أن يقبض معلومه، ليس لورثته أن يأخذ ذلك (¬٣).
_________
(¬١) المصباح المنير ٢/ ٤٣٠ مادة عملته، لسان العرب ١١/ ٤٧٤ مادة عمل، القاموس المحيط ٣/ ٣١٤ مادة عمل، مختار الصحاح ص ١٩١ مادة ع م ل.
(¬٢) الهداية ٢/ ٤٥٧، العناية ٦/ ٧٦، كنز الدقائق ٣/ ٢٨٣، بداية المبتدي ٢/ ٤٥٧، تبيين الحقائق ٣/ ٢٨٣.
(¬٣) الهداية ٢/ ٤٥٧، شرح فتح القدير ٦/ ٦٧، تبيين الحقائق ٢/ ٢٨٣.','3','437',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1113,'<span data-type="title" id=toc-557>فصل</span>
ومن ارتدَّ عُرِض عليه الإسلام، وكشفت شبهته،
<hr><span data-type="title">منحة السلوك</span>


فصل
هذا الفصل في أحكام المرتدين (¬١).

<span data-type="title" id=toc-558>[حكم المرتد]</span>
قوله: ومن ارتد عُرِض عليه الإسلام، وكشفت شبهته.
والعَرْضُ مروي عن عمر -رضي الله عنه- (¬٢)، وهو مستحبٌّ وليس بواجب؛ لأن الدعوة قد بلغته، غير أنه يحتمل أنه اعتراه شبهةٌ، فيعرض عليه ليُزاح، ويعود
_________
(¬١) الردة، لغة: الرجعة. يقال: ارتد فهو مرتد، إذا رجع.
وشرعًا: هو الراجع عن الإسلام إلى الكفر.
ولما فرغ المصنف من بيان أحكام الكفر الأصلي، شرع في بيان أحكام الكفر الطاريء؛ لأن العارض بعد الأصلي في الوجود، فناسب أن يكون كذلك وضعًا.
مختار الصحاح ص ٩٩ مادة رج ع، المصباح المنير ١/ ٢٢٤ مادة رددت، القاموس المحيط ٢/ ٣٠٧ مادة ر ج ع، أنيس الفقهاء ص ١٨٦، المطلع على أبواب المقنع ص ٣٧٨، حاشية الشلبي ٣/ ٢٨٤، شرح فتح القدير ٦/ ٦٨، العناية ٦/ ٦٨.
(¬٢) رواه مالك في الموطأ ٢/ ٧٣٧ كتاب الأقضية، باب القضاء فيمن ارتد عن الإسلام رقم ١٦، والبيهقي في السنن الكبرى ٨/ ٢٠٦ كتاب المرتد، باب من قال يحبس ثلاثة أيام.
من طريق عبد الرحمن بن محمد بن عبد الله بن عبد القاريء، عن أبيه أنه قال: قدم على عمر بن الخطاب رجل من قبل أبي موسى الأشعري، فسأله عن الناس فأخبره، ثم قال له عمر: هل كان فيكم من مغربة خبر؟ -أي: من خبر جديد جاء من بلد بعيد- فقال: نعم، رجل كفر بعد إسلامه قال: فما فعلتم به؟ قال: قربناه فضربنا عنقه، فقال عمر: أفلا حبستموه ثلاثًا، وأطعمتموه كل يوم رغيفًا، واستتبتموه، لعله يتوب ويراجع أمر الله، ثم قال عمر: اللهم إني لم أحضر، ولم آمر، ولم أرض إذ بلغني.
قال الشافعي في الأم ٦/ ١٥٩: وممن قال: لا يتأتى به، من زعم أنه ليس بثابت؛ ولأنه لم يعلمه متصلًا.','3','438',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1114,'وحُبِس ثلاثة أيام استحبابًا، وقيل: وجوبًا
<hr><span data-type="title">منحة السلوك</span>

إلى الإسلام؛ لأن عوده مرجوٌّ (¬١).
قوله: وحُبِس ثلاثة أيام استحبابًا (¬٢)، وقيل: وجوبًا (¬٣).
وهو قول: الشافعي (¬٤)؛ لأن ارتداده يكون عن شبهة ظاهرًا، فلا بد من مدة يمكنه أن يتأمَّل فيها، فقدرت بالثلاث؛ لأنها مدة ضربت لإيلاء الأعذار (¬٥).
_________
(¬١) وذهب المالكية، والشافعية، والحنابلة: إلى وجوب العرض عليه، فربما عرضت له شبهة، فيسعى في إزالتها.
الهداية ٢/ ٤٥٨، العناية ٦/ ٦٨، كنز الدقائق ٣/ ٢٨٤، الكتاب ٤/ ١٤٨، شرح فتح القدير ٦/ ٦٨، تبيين الحقائق ٣/ ٢٨٤، سراج السالك ٢/ ٤٨٥، القوانين ص ٢٣٨، مغني المحتاج ٤/ ١٣٩، روض الطالب ٤/ ٢٢، المبدع ٩/ ١٧٣، المغني ١٠/ ٧٢، مطالب أولي النهى ٦/ ٢٨٨، الشرح الكبير لابن قدامة ١٠/ ٧٦.
(¬٢) تحفة الفقهاء ٣/ ٣٠٨، الهداية ٢/ ٤٥٨، تبيين الحقائق ٣/ ٢٨٤، شرح فتح القدير ٦/ ٦٨.
(¬٣) شرح فتح القدير ٦/ ٦٩، المبسوط ١٠/ ٩٩، تحفة الفقهاء ٣/ ٣٠٨، تبيين الحقائق ٣/ ٢٨٤.
(¬٤) وإليه ذهب المالكية، والحنابلة. مع اتفاق الأئمة الأربعة: على أن المرتد يجب قتله، وإنما الخلاف هل يتحتم عليه القتل في الحال، أو يستتاب؟
فمذهب الحنفية: أنه لا تجب استتابته، ويقتل في الحال.
وذهب بعض الحنفية: إلى أنه تجب استتابته ثلاثة أيام. وإليه ذهب المالكية، والشافعية، والحنابلة.
شرح فتح القدير ٦/ ٦٩، المبسوط ١٠/ ٩٩، متن الرسالة ص ١٤٣، مختصر خليل ص ٣٢٢، أقرب المسالك ص ١٧٥، الكافي لابن عبد البر ص ٥٨٤، شرح ابن قاسم الغزي ٢/ ٢٦٥، حاشية البيجوري ٢/ ٢٦٥، الشرح الكبير لابن قدامة ١٠/ ٧٩، الإفصاح ٢/ ٢٩٩، المحرر ٢/ ١٦٧، غاية المنتهى ٦/ ٢٨٨.
(¬٥) المبسوط ١٠/ ٩٩، تبيين الحقائق ٣/ ٢٨٤.','3','439',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1115,'فإن لم يسلم قُتل، فإن قتله رجل قبل عرض الإسلام عليه كُرِه، ولا شيء عَليه.
<hr><span data-type="title">منحة السلوك</span>

قوله: فإن لم يسلم قتل (¬١).
لقوله -صلى الله عليه وسلم-: "من بدَّل دينه فاقتلوه" رواه أحمد، والبخاري، وغيرهما (¬٢).
قوله: فإن قتله رجل قبل عرض الإسلام عليه كُره.
لأنَّ فيه تفويت الغرض المستحب (¬٣).
وقال صاحب الهداية (¬٤): معنى الكراهة هنا: ترك المستحب (¬٥).
قوله: ولا شيء عليه.
يعني: لا يجب شيء على القاتل؛
_________
(¬١) المختار ٤/ ١٤٥، الكتاب ٤/ ١٤٩، بداية المبتدي ٢/ ٤٥٨، الهداية ٢/ ٤٥٨.
(¬٢) أحمد في المسند ١/ ٢١٧، ٢٨٢. والبخاري ٣/ ١٠٨٩ كتاب الجهاد باب لا يعذب بعذاب الله رقم ٢٨٥٤، ورواه أيضًا أبو داود ٤/ ٥٢٠ كتاب الحدود، باب الحكم فيمن ارتد رقم ٤٣٥١، والترمذي ٤/ ٥٩ كتاب الحدود، باب في المرتد رقم ١٤٥٨، والنسائي ٧/ ١٠٤ كتاب تحريم الدم، باب الحكم في المرتد رقم ٤٠٥٩، وابن ماجه ٢/ ٨٤٨ كتاب الحدود، باب المرتد عن دينه رقم ٢٥٣٥، وعبد الرزاق في المصنف ١٠/ ١٦٨ كتاب اللقطة باب الكفر بعد الإيمان رقم ١٨٧٠٦، وابن أبي شيبة في المصنف ١٠/ ١٣٩ كتاب الحدود باب المرتد عن الإسلام رقم ٩٠٤١. عن ابن عباس -رضي الله عنهما-.
(¬٣) تبيين الحقائق ٣/ ٢٨٤، الهداية ٢/ ٤٥٨، الكتاب ٤/ ١٤٩، الاختيار ٤/ ١٤٥، بداية المبتدي ٢/ ٤٥٨.
(¬٤) برهان الدين، أبو الحسن، علي بن أبي بكر المرغيناني ت ٥٩٢ هـ.
(¬٥) الهداية ٢/ ٤٥٨.','3','440',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1116,'والمرتدة لا تُقتَل، بل تحبس حتى تُسلم، وكذا الصبي المُميز.
<hr><span data-type="title">منحة السلوك</span>

لأنه مباح الدم بالحديث (¬١) (¬٢).

<span data-type="title" id=toc-559>[ارتداد المرأة والصبى]</span>
قوله: والمرتدة لا تُقتَل، بل تحبس حتى تُسلم.
لأن المبيحَ للقتل كفر المحارب (¬٣).
وقال الشافعي: تُقتَلُ، ولو قتلها لا شيء عليه للشبهة (¬٤).
قوله: وكذا الصبىُّ المميز.
أي: وكذا لا يقتل الصبي المميز إذا ارتدَّ. وهذا عند أبي حنيفة، ومحمد (¬٥).
_________
(¬١) يشير إلى ما رواه البخاري ٦/ ٢٥٢١ كتاب الديات، باب قول الله: {أَنَّ النَّفْسَ بِالنَّفْسِ وَالْعَيْنَ بِالْعَيْنِ ... } رقم ٦٤٨٤، ومسلم ٣/ ١٣٠٢ كتاب القسامة، باب ما يباح به دم المسلم رقم ١٦٧٦.
عن ابن مسعود -رضي الله عنه- قال: قال رسول الله -صلى الله عليه وسلم-: "لا يحل دم امريء مسلم يشهد أن لا إله إلا الله، وأني رسول الله -صلى الله عليه وسلم-، إلا بإحدى ثلاث: الثيب الزاني، والنفس بالنفس، والتارك لدينه، المفارق للجماعة".
(¬٢) وعند المالكية، والشافعية، والحنابلة: يعزر؛ لافتياته على الإمام، ولا شيء عليه من قصاص، أو دية.
الاختيار ٤/ ١٤٦، الكتاب ٤/ ١٤٩، شرح فتح القدير ٦/ ٧١، تبيين الحقائق ٣/ ٢٨٤، جواهر الإكليل ٢/ ٢٥٥، منح الجليل ٩/ ٨، مغني المحتاج ٤/ ١٤٠، روض الطالب ٤/ ١٢٢، شرح منتهى الإرادات ٣/ ٣٨٩، دليل الطالب ٢/ ٤٠٦.
(¬٣) الكتاب ٤/ ١٤٩، الهداية ٢/ ٤٥٨، شرح فتح القدير ٦/ ٧٢.
(¬٤) وكذا عند المالكية، والحنابلة، وهو مروي عن أبي بكر، وعلي. وبه قال: الحسن، والزهري، والنخعي، ومكحول، وحمادة، والليث، والأوزاعي، وإسحاق.
وروي عن علي، والحسن، وقتادة: أنها تسترق، ولا تقتل.
القوانين الفقهية ص ٢٣٩، منح الجليل ٩/ ٢١٣، مغني المحتاج ٤/ ١٣٩، الإقناع في حل ألفاظ أبي شجاع ٢/ ٢٠٦، شرح الزركشي ٦/ ٢٣٣، نيل المآرب ٢/ ٣٩٠، المغني ١٠/ ٧٢.
(¬٥) وكذا عند المالكية، والحنابلة. =','3','441',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1117,'ويزول ملكُ المرتد عن أمواله، زَوَالًا موقوفًا،
<hr><span data-type="title">منحة السلوك</span>

وقال أبو يوسف (¬١)، وزُفر (¬٢)، والشافعي (¬٣): ارتداده ليس بارتداد (¬٤).

<span data-type="title" id=toc-560>[ممتلكات المرتد]</span>
قوله: ويزول ملك المرتد عن أمواله زوالًا موقوفًا.
هذا عند أبي حنيفة (¬٥)، وعندهما: لا يزول؛ لأن تأثير الردة يظهر في إباحة دمه، لا في زوال ملكه (¬٦).
وله: أن الملك يكون بالعصمة، وقد زالت بالردة، غير أنه يُدعى إلى الإسلام بالإجبار عليه، ويُرجى عوده إليه، فلم يتم سبب الزوال فيتوقف (¬٧).
_________
= المختار ٤/ ١٤٨، أنفع الوسائل إلى تحرير المسائل ص ٥٨، بداية المبتدي ٢/ ٤٦٣، جواهر الإكليل ٢/ ٢٨٠، منح الجليل ٩/ ٢٧٧، كشاف القناع ٦/ ١٧٦، الشرح الكبير لابن قدامة ١٠/ ٨٥.
(¬١) بداية المبتدي ٢/ ٤٦٣، مختصر الطحاوي ص ٢٦٠، تحفة الفقهاء ٣/ ٣٠٩.
(¬٢) تبيين الحقائق ٣/ ٢٩٢، الهداية ٢/ ٤٦٣، بداية المبتدي ٢/ ٤٦٣، مختصر الطحاوي ص ٢٦٠، تحفة الفقهاء ٣/ ٣٠٩.
(¬٣) فتح الوهاب ٥/ ١٢٥، حاشية الجمل ٥/ ١٢١.
(¬٤) المختار ٤/ ١٤٦، الكتاب ٤/ ١٤٩، الاختيار ٤/ ١٤٦، تبيين الحقائق ٣/ ٢٩٢.
(¬٥) الاختيار ٤/ ١٤٨، تحفة الفقهاء ٣/ ٣٠٩.
(¬٦) تبيين الحقائق ٣/ ٢٨٥، العناية ٦/ ٧٤، الاختيار ٤/ ٤٦، بداية المبتدي ٢/ ٤٥٩، الهداية ٢/ ٤٥٦.
(¬٧) وذهب المالكية: إلى أنه يزول ملكه بردته، فإن رجع إلى الإسلام، رد إليه تمليكًا مستنأنفًا؛ لأن عصمة نفسه وماله إنما تثبت باسلامه، فزوال إسلامه يزيل عصمتهما، كما لو لحق بدار الحرب؛ ولأن المسلمين ملكوا إراقة دمه بردته، فوجب أن يملكوا أمواله بها.
وعند الشافعية: في زوال ملكه عن ماله بها، أقوال:
أظهرها: الوقف؛ لتضع زوجته، سواء التحق بدار الحرب أم لا.
وعليه: إن هلك مرتدا بان زواله بها، فما ملكه فيء، وإن أسلم بان أنه لم يزل؛ لأن بطلان أعماله تتوقف على هلاكه على الردة، فكذا زوال ملكه. والقول الثاني عندهم: يزول =','3','442',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1118,'فإن أسلم عاد ملكُه،
<hr><span data-type="title">منحة السلوك</span>

قوله: فإن أسلم تفصيلٌ لما قبله، أي: فإن أسلم المرتد عاد ملكه،
_________
= بنفس الردة؛ لزوال العصمة بردته، فماله أولى.
والقول الثالث: لا يزول؛ لأن الكفر لا ينافي الملك، كالكافر الأصلي.
ويتفرع على هذه الأقوال: أنه يقضى من مال المرتد دين لزمه قبلها، وعلى القول الأظهر يوقف ملكه، فتصرفه الواقع منه في ردته، إن احتمل الوقف، كعتق، وتدبير، ووصية موقوف، إن أسلم نفذ. وإن مات مرتدًا لم ينفذ؛ لأن الوقف لا يضره. وبيعه، وهبته، ورهنه، وكتابته، ونحوها، مما لا يقبل الوقف باطلة. بناء على بطلان وقف العقود، وهو القول: الجديد، وفي القديم: هي موقوفة. بناءً على صحة وقف العقود، فإن أسلم حكم بصحتها وإلا فلا.
وعلى الأقوال السابقة عند الشافعية: يجعل ماله عند عدل يحفظه، وتجعل أمته عند امرأة ثقة، أو من يحل له الخلوة بها، كالمحرم؛ احتياطًا لتعلق حق المسلمين به، ويؤدي مكاتبُه النجوم إلى القاضي، حفظًا لها، ويعتق بذلك. وإنما لم يقبضها المرتد؛ لأن قبضه غير معتبر.
وعند الحنابلة: لا يحكم بزوال ملك المرتد بردته.
فعلى هذا إن قتل، أو مات، زال ملكه بموته. وإن رجع إلى الإسلام فملكه باقٍ له.
وعلى هذا: تصرفاته في ردته، بالبيع، والهبة، والعتق، والتدبير، والوصية، ونحو ذلك، موقوفة. إن أسلم تبينا أن تصرفه كان صحيحًا، فإن قتل، أو مات كان باطلًا. ويؤخذ مال المرتد فيجعل عند ثقة من المسلمين. وإن كان له إماء جعلن عند امرأة ثقة؛ لأنهن محرمات عليه فلا يمكن منهن، ومكاتبه يؤدي إلى الحاكم، ويعتق بالأداء، وإن تزوج لم يصح تزوجه؛ لأنه لا يقر على النكاح. وإذا وجد من المرتد سبب يقتضي الملك، كالصيد، والاحتشاش، والاتهاب، والشراء، وإيجار نفسه إجارة خاصة، أو مشتركة، ثبت الملك له؛ لأنه أهل للملك، ولذلك بقيت أملاكه الثابتة له.
والفرق بين قول أبي حنيفة، والحنابلة: أنه عند الحنفية إن مات، أو قتل ولم يدخل في الإسلام، زوال المال يكون من حين الردة.
أما عند الحنابلة: يزول بموته، بعد أن كان موقوفًا.
تبيين الحقائق ٣/ ٢٨٥، العناية ٦/ ٧٤، أقرب المسالك ص ١٧٥، أسهل المسالك =','3','443',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1119,'وإن مَات، أو قُتل، فكسب إسلامِه لورثته، وكسْبُ ردته فيء، ويعتق مدبروه، وأمهات أولاده، وتحل الديون التي عليه. والمرتدة كَسْبُهَا لورثتها.
<hr><span data-type="title">منحة السلوك</span>

وإن مات على الردَّة، أو قُتل عليها فكسب إسلامه لورثته المسلمين، وكسب ردته فيء -أي: غنيمةٌ- (¬١).
وعندهما: كلاهما لورثَته المسلمين (¬٢).
وعند الشافعي: كلاهما فيءٌ (¬٣).
قوله: ويُعتق مدبروه، وأمهات أولاده، وتحل الديون التي عليه.
لأن هذه أحكام معلقة، تتنجز بالموت (¬٤).
قوله: والمرتدَّة كسبها لورثتها.
إذ لا حراب منها، فلم يتحقق سبب الفيء، ويرثها زوجها المسلم إن
_________
= ص ٢٤٨، الشرح الكبير للدردير ٤/ ٣٠٦، الذخيرة ١٢/ ٤٤، الكافي لابن عبد البر ص ٥٨٤، الشرح الصغير ١٢/ ٤١٨، السراج الوهاج ص ٥٢١، مختصر المزني ص ٣٦٧، شرح المحلي ٤/ ١٧٦، تحفة المحتاج ٩/ ٩٥، مغني المحتاج ٤/ ١٤٢، الحاوي الكبير ١٣/ ١٦٠، أسنى المطالب ٤/ ١٢٤٣، الإقناع للحجاوي ٦/ ١٨٢، الروض المربع ص ٤٧٥، الشرح الكبير لابن قدامة ١٠/ ٩٨، شرح منتهى الإرادات ٣/ ٣٩٣، الإنصاف ١٠/ ٣٣٩، المغني ١٠/ ٧٩.
(¬١) مختصر الطحاوي ص ٢٦١، الاختيار ٤/ ١٤٧، الهداية ٢/ ٤٥٩.
(¬٢) مختصر الطحاوي ص ٢٦١، بداية المبتدي ٢/ ٤٥٩، المختار ٤/ ١٤٧، الهداية ٢/ ٤٥٩.
(¬٣) وكذا عند المالكية، والحنابلة.
الكافي لابن عبد البر ص ٥٨٤، مختصر خليل ص ٣٢٢، منح الجليل ٩/ ٢١٣، شرح المحلي ٤/ ١٧٦، تحفة المحتاج ٩/ ٩٥، المغني ١٠/ ٧٩، شرح منتهى الإرادات ٣/ ٣٩٣.
(¬٤) الهداية ٢/ ٤٦٠، شرح فتح القدير ٦/ ٧٩، الاختيار ٤/ ١٤٧، الكتاب ٤/ ١٥٠.','3','444',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1120,'ولحاقه بدار الحرب مع الحكم به، كالموتِ.
و<span data-type="title" id=toc-561>تصرفات المرتد</span>، أقسَام:
نافذ:
<hr><span data-type="title">منحة السلوك</span>

ارتدَّت وهي مريضة؛ لقصدها إبطال حقه بعد تعلق حقه بمالها، فصارت فارَّة (¬١). وإن كانت صحيحةً لا يرثها زوجها؛ لأن ردَّتها ليست سببًا لهلاكها؛ لأنها لا تُقتل، فلم يتعلَّق حقه بمالِهَا (¬٢).
قوله: ولحاقه بدار الحرب مع الحكم به. أي: باللحاق كالموتِ.
لأنه باللحاق صار من أهل الحراب وهم أمواتٌ، ولكن لا يستقر إلحاقه إلَّا بحكم الحاكم؛ لاحتمال أن يعود إلينا (¬٣). وفيه خلاف الشافعي (¬٤).
وفائدة كون لحاقه بالموت: أنه يصير مثل الميت، حتى تحل ديونه، ويعتق مُدبَّروه، ومكاتبوه، وأمهات أولاده (¬٥)؛ لِما مرَّ.

[تصرفات المرتد]
قوله: وتصرفات المرتد، أقسام: نافذ.
_________
(¬١) الهداية ٢/ ٤٥٩، العناية ٦/ ٧٨، شرح فتح القدير ٦/ ٧٨.
(¬٢) وعند الثلاثة: المرأة في أحكام الردة كالرجل.
العناية ٦/ ٧٨، شرح فتح القدير ٦/ ٧٨، الهداية ٢/ ٤٥٩، القوانين ص ٢٣٩، منح الجليل ٩/ ٢١٣، قليوبي ٤/ ١٧٧، عميرة ٤/ ١٧٧، شرح الزركشي ٦/ ٢٣٣، نيل المآرب ٢/ ٣٩٠.
(¬٣) الكتاب ٤/ ١٥٠، بداية المبتدي ٢/ ٤٦٠، بدائع الصنائع ٧/ ١٣٧، تحفة الفقهاء ٣/ ٣١٠.
(¬٤) ومالك، وأحمد، حيث يرون: أن المرتد إذا لحق بدار الحرب، فالحكم فيه، كالحكم فيمن هو في دار الإسلام.
الشرح الصغير ٢/ ٤١٨، الذخيرة ١٢/ ٤٣، مغني المحتاج ٤/ ١٤٢، روض الطالب ٤/ ١٢٣، المغني ١٠/ ٨٢، منتهى الإرادات ٣/ ١٩٣، الروض المربع ص ٤٧٥.
(¬٥) الاختيار ٤/ ١٤٧، الكتاب ٤/ ١٥٠، المختار ٤/ ١٤٧، الهداية ٢/ ٤٥٩.','3','445',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1121,'كالطلاق، والاستيلادِ، وقبول الهبة، وإسقاط الشفعة.
وباطل: كالنكاح، والذبح.
وموقوف: كالمفاوضة، والبيع، والشراء، والرهن، والإجارة، والهبة، والإعتاق، والتدبير.
<hr><span data-type="title">منحة السلوك</span>

أي: الأوَّل: تصرف نافذ. كالطلاق، والاستيلاد، وقبول الهبة، وإسقاط الشفعة؛ لأنها لا تستدعي الولاية. حتى تَصِح هذه التصرفات من العبد أيضًا، وكذلك الحجر على عبده المأذون (¬١).
قوله: وباطلٌ.
أي: القسمُ الثاني: تصرُّف باطل. كالنِّكاح، والذَّبح؛ لأنه يعتمد الملَّة، ولا ملَّة له. وكذلك الإرث (¬٢) (¬٣).
قوله: وموقوفٌ.
أي: القسم الثالث: تصرُّفٌ موقوفٌ. كالمفاوضة، والبيع، والشراء، والرَّهن، والإجارة، والهبة، والإعتاق، والتَّدبير (¬٤).
ومعنى كونه موقوفًا: أنه إن أسلم نفذ تصرُّفه، وإن هلك بطل. أمَّا مفاوضَتُهُ فهي موقوفة اتفاقًا. وكذلك تصرُّفه على ولده الصغير، ومال ولده؛ لأنها تعتمد المساواة، ولا مساواة بين المسلم والمرتدِّ ما لم يُسلم.
_________
(¬١) الهداية ٢/ ٤٦١، شرح فتح القدير ٦/ ٨٢، بدائع الصنائع ٧/ ١٣٧، تحفة الفقهاء ٣/ ٣١٠.
(¬٢) العناية ٦/ ٨٢، شرح فتح القدير ٦/ ٨٢، الهداية ٢/ ٤٦١، بدائع الصنائع ٧/ ١٣٧، تحفة الفقهاء ٣/ ٣١٠.
(¬٣) في نسخة ي بعد كلمة الإرث "كما في المكاتب إذا ادعى ولد أمته المشتراة في حال كتابته".
(¬٤) بداية المبتدي ٢/ ٤٦١، الهداية ٢/ ٤٦١، بدائع الصنائع ٧/ ١٣٧، شرح فتح القدير ٦/ ٨٢.','3','446',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1122,'ولا تصحُّ ردة مجنون،
<hr><span data-type="title">منحة السلوك</span>

وأما غيرها: فكونها موقوفة مذهب أبي حنيفة (¬١).
وعندهما: نافذة، عاد إلى الإسلام أو لم يعد (¬٢).

<span data-type="title" id=toc-562>[من لا تصح ردته]</span>
قوله: ولا تصِحُّ ردَّةُ مجنون.
لأن إقراره لا يدل على اعتقاده، فلا يعتبر (¬٣). وكذا الصبيُّ، والسكران اللذان لا يعقلان (¬٤).
_________
(¬١) بدائع الصنائع ٧/ ٢٣٦، شرح فتح القدير ٦/ ٨٢، الهداية ٢/ ٤٦١.
(¬٢) بدائع الصنائع ٧/ ١٣٦، شرح فتح القدير ٦/ ٨٣، الهداية ٢/ ٤٦١.
(¬٣) وفاقًا للثلاثة.
الهداية ٢/ ٤٦٤، العناية ٦/ ٩٧، شرح فتح القدير ٦/ ٨٣، القوانين ص ٢٣٩، الذخيرة ١٢/ ٢٩، أسهل المسالك ص ٢٤٧، الخرشي على خليل ٨/ ٦٢، أنوار المسالك، شرح عمدة السالك ص ٤٥١، روضة الطالبين ١٠/ ٧١، أسنى المطالب ٤/ ١٢٠، التذكرة ص ١٥٠، السراج الوهاج ص ٥١٩، التسهيل ص ١٩١، المحرر ٢/ ١٦٧، شرح منتهى الإرادات ٣/ ٣٨٦، غاية المنتهى ٦/ ٢٨٨.
(¬٤) ذهب الأئمة الأربعة: أبو حنيفة، ومالك، والشافعي، وأحمد: إلى عدم اعتبار ردة الصبي الذي لا يعقل، وقد ساق الإجماع على ذلك ابن قدامة في المغني ١٠/ ٧٣ قال: "فأما من لا عقل له كالطفل الذي لا عقل له، والمجنون ... فلا تصح ردته، ولا حكم لكلامه بغير خلاف" اهـ.
واختلفوا في ردة السكران:
فذهب الحنفية، والمالكية، ورواية عن الإمام أحمد: إلى عدم اعتبار ردة السكران؛ لأن ذلك يتعلق بالاعتقاد، والقصد. والسكران لا يصح عقده، فأشبه المعتوه؛ ولأنه زائل العقل فلم تصح ردته كالنائم والمجنون؛ ولأنه غير مكلف فأشبه المجنون.
وذهب الشافعية، والحنابلة: إلى صحة الردة من السكران، كسائر تصرفاته.
الهداية ٢/ ٤٦٤، العناية ٦/ ٩٧، الذخيرة ١٢/ ٢٩، القوانين ص ٢٣٩، أسنى المطالب ٤/ ١٢٠، روضة الطالبين ١٠/ ٧١، كشاف القناع ٦/ ١٧٦، شرح منتهى الإرادات =','3','447',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1123,'ويصح إسلام الصَّبي المميز.
<hr><span data-type="title">منحة السلوك</span>

قوله: ويصح إسلام الصبي المميز (¬١). خلافًا لزفر (¬٢)، والشافعي (¬٣).
ولنا: أنه -صلى الله عليه وسلم-: "صحح إيمان عليّ -رضي الله عنه- وقد كان آمن صَبيًا، وافتخاره بذلك معروفٌ" (¬٤).
_________
= ٣/ ٣٩٠، الشرح الكبير لابن قدامة ١٠/ ٨٥، المغني ١٠/ ٧٣.
(¬١) وإليه ذهب المالكية، والحنابلة. وبه قال: إسحاق، وابن أبي شيبة، وأبو أيوب.
تحفة الفقهاء ٣/ ٣٠٩، المختار ٤/ ١٤٨، الشرح الكبير للدردير ٤/ ٣٠٥، حاشية الدسوقي ٤/ ٣٠٥، جواهر الإكليل ٢/ ٢٨٠، منح الجليل ٩/ ٧٢٢٦ الذخيرة ١٢/ ١٥، الكافي لابن عبد البر ص ٥٨٥، شرح منتهى الإرادات ٣/ ٣٨٩، الإقناع للحجاوي ٦/ ١٧٥، المغني ١٠/ ٨٥، الشرح الكبير لابن قدامة ١٠/ ٨١.
(¬٢) المختار ٤/ ١٤٨، تبيين الحقائق ٣/ ٢٩٢.
(¬٣) حيث قالا: لا يصح إسلامه حتى يبلغ.
تحفة المحتاج ٣/ ٩٣، حاشية عميرة ٤/ ١٧٦، الحاوي الكبير ١٣/ ١٧١.
(¬٤) أما إسلام علي وهو صغير:
فأخرج البخاري في التاريخ الكبير ٦/ ٢٥٩ عن عروة قال: أسلم علي، وهو ابن ثمان سنين.
وأخرج الحاكم في المستدرك ٣/ ١١١ كتاب معرفة الصحابة، باب ذكر إسلام أمير المؤمنين علي، من طريق ابن إسحاق: أن عليًا أسلم وهو ابن عشر سنين.
وأخرج في الموضع السابق من طريق قتادة، عن الحسن أنه كان عمره خمس عشرة سنة.
وأخرج أيضًا من طريق ابن عباس في الموضع السابق أن رسول الله -صلى الله عليه وسلم- دفع الراية إلى علي يوم بدر، وهو ابن عشرين سنة. وقال: صحيح على شرط الشيخين ولم يخرجاه.
قال الذهبي في مختصره للمستدرك ٣/ ١١١ كتاب معرفة الصحابة، باب ذكر إسلام أمير المؤمنين علي: هذا نص في أنه أسلم وله أقل من عشر سنين، بل نص في أنه أسلم ابن سبع سنين، أو ثمان سنين. وهو قول: عروة.
وأخرج أيضًا في المستدرك ٣/ ١٨٣ كتاب معرفة الصحابة باب ومنهم خديجة بنت خويلد، من طريق عفيف بن عمرو قال: كنت امرأ تاجرًا، وكنت صديقًا للعباس بن عبد المطلب في الجاهلية، فقدمت لتجارة، فنزلت على العباس بمنى، فجاء رجل فنظر إلى الشمس حين =','3','448',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1124,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وذكر أبو جعفر: أنه أسلم وهو ابن خمس سنين (¬١). وذكر القُتيبي (¬٢): أنَّ عمرهُ كان سبعَ سنين (¬٣).
وعن عروة أنه قال: أسلم علي وعمرُهُ ثمان سنين. أخرجه البخاري (¬٤).
_________
= مالت فقام يصلي، ثم جاءت امرأة فقامت تصلي، ثم جاء غلام قد راهق الحلم فقام يصلي، فقلت للعباس: من هذا؟ فقال. هذا محمد ابن أخي، يزعم أنه نبي، ولم يتابعه على أمره غير امرأته هذه خديجة بنت خويلد، وهذا الغلام ابن عمه علي بن أبي طالب، قال عفيف: فلوددت أني أسلمت يومئذ، فيكون لي ربع الإسلام.
قال الحاكم: صحيح الإسناد ولم يخرجاه.
وقال الذهبي في مختصره على المستدرك: صحيح.
وقال ابن حجر في الدراية ٢/ ١٣٧: فعلى هذا: يكون عمره حين أسلم خمس سنين؛ لأن إسلامه كان في أول المبعث، ومن المبعث إلى بدر خمس عشرة، فلعل فيه تجويزًا بإلغاء الكسر الذي فوق العشرين، حتى يوافق قول عروة.
وقال ابن حجر أيضًا في الدراية ٢/ ١٣٧: وأما تصحيح النبي فمستند من كونه أقره على ذلك.
وأما الدليل على صحة إسلام الصبي:
فما رواه البخاري ١/ ٤٥٤ كتاب الجنائز، باب إذا أسلم الصبي فمات هل يصلى عليه؟ رقم ١٢٩٠.
عن أنس -رضي الله عنه- قال: كان غلام يهودي يخدم النبي -صلى الله عليه وسلم- فمرض، فأتاه النبي -صلى الله عليه وسلم- يعوده، فقعد عند رأسه، فقال له: أسلم، فنظر إلى أبيه وهو عنده فقال له: أطع أبا القاسم، فأسلم فخرج النبي -صلى الله عليه وسلم- وهو يقول: "الحمد لله الذي أنقذه من النار".
(¬١) العناية ٦/ ٩٤، شرح فتح القدير ٦/ ٩٥، تبيين الحقائق ٣/ ٢٩٢.
(¬٢) لم أقف له على ترجمة.
(¬٣) العناية ٦/ ٩٤، شرح فتح القدير ٦/ ٩٥، تبيين الحقائق ٣/ ٢٩٢.
(¬٤) في التاريخ الكبير ٦/ ٢٥٩ رقم ٢٣٤٣ باب علي.
قال: قال يحيى بن بكير، عن ليث، عن أبي الأسود، عن عروة قال: أسلم علي، وهو =','3','449',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1125,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
=  ابن ثمان سنين، وإسناده صحيح.
* اختلف العلماء -رحمهم الله تعالى- في صحة إسلام الصبي على قولين:
القول الأول: أن إسلام الصبي صحيح في الجملة، وبهذا قال: أبو حنيفة، وصاحباه، وإسحاق، وابن أبي شيبة، وأيوب، والإمام أحمد (أ).
القول الثاني: أن إسلام الصبي لا يصح حتى يبلغ الحلم، وبهذا قال الشافعي، وزفر، وهو رواية عن الإمام أحمد (ب).
الأدلة:
استدل أصحاب القول الأول بما يأتي:
١ - العمومات في النصوص، ومنها:
أ - قول النبي -صلى الله عليه وسلم-: "من قال: لا إله إلا الله دخل الجنة" متفق عليه (جـ).
ب- وقوله -صلى الله عليه وسلم-: "أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فإذا قالوها: عصموا مني دماءهم، وأموالهم إلا بحقها، وحسابهم على الله" متفق عليه (د).
جـ - وقوله -صلى الله عليه وسلم-: "كل مولود يولد على الفطرة، فأبواه يهودانه، أو ينصرانه، حتى يعرب عنه لسانه، إما شاكرًا وإما كفورًا" (هـ). =
_________
(أ) المبسوط ١٠/ ٦٢، المغني لابن قدامة ١٢/ ٢٧٨، جامع أحكام الصغار ٢/ ٩٣.
(ب) المبسوط ١٠/ ٦٢، الإمام زفر وآراؤه الفقهية ١/ ٢٣٧، المهذب ٢/ ٢٤٠، المقنع ٣/ ٥١٧، الإنصاف ١٠/ ٣٢٩، شرح الزركشي على مختصر الخرقي ٦/ ٢٥٠ - ٢٥٣، المحرر لابن تيمية ٢/ ١٦٩.
(جـ) رواه البخاري ٧/ ١٩٢، كتاب اللباس، باب الثياب البيض، ومسلم ١/ ٩٥، كتاب الإيمان، باب من مات لا يشرك بالله شيئًا ١/ ٩٥.
(د) رواه البخاري كتاب الزكاة، باب وجوب الزكاة ٢/ ١٣١، ومسلم كتاب الإيمان، باب الأمر بقتال الناس حتى يقولوا لا إله إلا الله ١/ ٥١ - ٥٢.
(هـ) رواه البخاري ٢/ ١٢٥ كتاب الجنائز، باب إذا أسلم الصبي، ومسلم ٤/ ٢٠٤٧، كتاب القدر، باب معنى كل مولود يولد على الفطرة، والزيادة عند أحمد ٢/ ٢٣٣، وينظر: سنن أبي داود ٢/ ٥٣١، والموطأ ١/ ٢٤١، وعارضة الأحوذي ٨/ ٣٠٣، وفيض القدير ٥/ ٣٣.','3','450',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1126,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
_________
= ٢ - قال ابن قدامة في المغني ١٢/ ٢٧٩: "ولأن الإسلام عبادة محضة، فصحت من الصبي العاقل، كالصلاة، والحج، ولأن الله تعالى دعا عباده إلى دار السلام، وجعل طريقها الإسلام، وجعل من لم يجب دعوته في الجحيم، والعذاب الأليم، فلا يجوز منع الصبي من إجابة دعوة الله مع إجابته إليها، وسلوك طريقها، ولا إلزامه بعذاب الله، والحكم عليه بالنار، وسد طريق النجاة عليه، مع هربه منها، ولأن ما ذكرناه إجماع. قال عروة: أسلم علي، والزبير، وهما ابنا ثمان سنين، وبايع النبي -صلى الله عليه وسلم- الزبير لسبع، أو ثمان سنين. ولم يرد النبي -صلى الله عليه وسلم- على أحد إسلامه من صغير، أو كبير" ا. هـ مختصرًا.
واستدل أصحاب القول الثاني بما يأتي:
أولًا: بما روته عائشة -رضي الله عنهما- أن النبي -صلى الله عليه وسلم- قال: "رفع القلم عن ثلاثة عن الصبي حتى يحتلم، وعن المعتوه حتى يفيق، وعن النائم حتى يستيقظ" (أ).
ثانيًا: أنه لا عبرة لعقله قبل البلوغ، حتى صار تبعًا لغيره في أحكام الدين، وأنه لو صح إسلامه بنفسه كان ذلك منه فرضًا؛ لاستحالة القول بكونه مستقلًا في الإسلام، ومن ضرورة كونه فرضًا، أن يكون مخاطبًا بالإسلام، وهو غير مخاطب بالاتفاق (ب).
المناقشة:
أما حديث "رفع القلم عن ثلاث" فإنه يدل على أن هؤلاء يكتب لهم، ولا يكتب عليهم، وذلك لوجود أدلة كثيرة تبين هذا، ومنها: أن المريض -وقد يكون فاقد العقل- يكتب له ما كان يعمل صحيحًا معافى، ولا يكتب عليه شيء من السيئات بحمد الله تعالى. وأما تعليل الشافعي وزفر فهي أمور عقلية لا تصادم النص.
ثمرة الخلاف:
عند القول: بصحة إسلامه، فإن الإسلام يوجب عليه الزكاة في ماله، ويوجب عليه نفقة قريبه المسلم، ويحرم ميراث قريبه الكافر، ويفسخ نكاحه من الكافرة. وهذه أيضًا =
_________
(أ) رواه البيهقي في السنن الكبرى ٦/ ٢٠٦ وابن ماجه ١/ ٦٥٨ رقم ٢٠٤١.
(ب) المبسوط ١٠/ ٦٢، الإمام زفر وآراؤه الفقهية ١/ ٣٢٧، ٣٢٨، فتح القدير ٤/ ٤٠٤.','3','451',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1127,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

مسألةٌ:

<span data-type="title" id=toc-563>[حكم الساحر]</span>
الساحر (¬١): يقتل ولا يستتاب (¬٢)، ولا يُقبلُ قوله: إنِّي أترك السحر
_________
= محل خلاف في التفريعات عند القائلين بصحة إسلامه (أ).
الترجيح:
الراجح صحة إسلام الصبي لقوة أدلته، ولفعل النبي -صلى الله عليه وسلم-، ولما رواه الإمام أحمد عن جابر -رضي الله عنه- عن النبي -صلى الله عليه وسلم- قال: "كل مولود يولد على الفطرة، حتى يعرب عنه لسانه، فإذا أعرب عنه لسانه فإما شاكرًا، وإما كفورًا" (ب).
(¬١) السحر: -بوزن العلم- وهو في اللغة: صرف الشيء عن وجهه. يقال: ما سحرك عن كذا، أي: ما صرفك، وسحره أيضًا بمعنى خدعه.
والسحر: عُقد، ورقى، وكلام يتكلم به، أو يكتبه، أو يعمل شيئًا يؤثر في بدن المسحور، وهو كل ما لطف مأخذه، ودق.
القاموس المحيط ٢/ ٥٢٨ مادة س ح ر، مختار الصحاح ص ١٢٢ مادة س ح ر، المطلع على أبواب المقنع ص ٣٨٥، المصباح المنير ١/ ٢٦٧ مادة السحر، مجمل اللغة ص ٣٧٠ باب السين والحاء وما يثلثهما مادة سحر.
(¬٢) وهو القول الراجح عند المالكية. والمذهب عند الحنابلة، وهو قول: الليث، وإسحاق.
وذهب الشافعية، وهو قول: عند المالكية، ورواية عن الإمام أحمد: إلى أن الساحر تقبل توبته. وهو قول: العنبري، وروي عن علي، وابن مسعود.
تبيين الحقائق ٣/ ٢٩٣، شرح فتح القدير ٦/ ٩٩، مواهب الجليل ٦/ ٢٧٩، التاج والإكليل ٦/ ٢٧٩، جواهر الإكليل ٢/ ٢٧٨، رحمة الأمة ٢/ ١٢٦، روض الطالب ٤/ ١١٧، المبدع ٩/ ١٧٩، الإفصاح ٢/ ٢٢٧، الشرح الكبير لابن قدامة ١٠/ ٨٧، المغني ١٠/ ١١٣.
_________
(أ) المغني لابن قدامة ١٢/ ٢٧٨، ٢٨٠، شرح الزركشي على الخرقي ٦/ ٢٥٠، فتح القدير ٤/ ٤٠٤، الاختيار ٤/ ١٤٨، نصب الراية ٢/ ٣٣٣، الإمام زفر وآراؤه الفقهية ١/ ٣٢٨، جامع أحكام الصغار ٢/ ٩٣، ٩٨.
(ب) مسند الإمام أحمد ٣/ ٣٥٣.','3','452',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1128,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

وأتوب منه، إذا شهد الشهود أنه الآن ساحرٌ، أو أقرَّ بذلك (¬١).
وقيل؛ إن اعتقد أنه خالق لما يفعل، ثم تاب عن ذلك وقال الله تعالى خالق كل شيء، وتبرَّأ عمَّا اعتقده، تُقبل توبته ولا يُقتل (¬٢).
والمرأة الساحرة: تُقتل أيضًا (¬٣)؛ لأن عمر -رضي الله عنه- كتب إلى نوابه: "أن اقتلوا الساحر، والساحرة" رواه أحمد، وأبو داود، والبخاري (¬٤). وعن
_________
(¬١) تبيين الحقائق ٣/ ٢٩٣
(¬٢) تبيين الحقائق ٣/ ٢٩٣.
(¬٣) وفاقًا للثلاثة.
تبيين الحقائق ٣/ ٢٩٣، العناية ٦/ ٧٢، مواهب الجليل ٦/ ٢٧٩، التاج والإكليل ٦/ ٢٧٩، منح الجليل ٩/ ٢٠٧، الحاوي الكبير ١٣/ ١٥٥، مغني المحتاج ٤/ ١٣٩، أسنى المطالب ٤/ ١١٧، الروض المربع ص ٤٧٤، مطالب أولي النهى ٦/ ٢٨٨.
(¬٤) أحمد ١/ ١٩٠، وأبو داود ٣/ ١٦٨ كتاب الخراج والإمارة والفيء، باب في أخذ الجزية من المجوس رقم ٣٠٤٣، والبيهقي ٨/ ١٧٦ كتاب القسامة، باب تكفير الساحر وقتله إن كان ما يسحر به كلام كفر صحيح. وتمام كتاب عمر بن الخطاب -رضي الله عنه-: عن عمرو بن أوس قال: سمعت بجالة يحدث عمرو بن أوس، وأبا الشعثاء قال: كنت كاتبًا لجزء بن معاوية عم الأحنف بن قيس، إذ جاءنا كتاب عمر قبل موته بسنة، اقتلوا كل ساحر، وفرقوا بين كل ذي محرم من المجوس، وانهوهم عن الزمزمة. فقتلنا في يوم ثلاثة سواحر، وفرقنا بين كل رجل من المجوس وحريمه في كتاب الله، وصنع طعامًا كثيرًا فدعاهم، فعرض السيف على فخذه، فأكلوا ولم يزمزموا، وألقوا وقْر بغل، أو بغلين من الورق، ولم يكن عمر أخذ الجزية من المجوس، حتى شهد عبد الرحمن بن عوف أن رسول الله -صلى الله عليه وسلم- أخذها من مجوس هجر.
وأصل كتاب عمر رواه البخاري ٣/ ١٥١١ كتاب الجزية والموادعة، باب الجزية والموادعة مع أهل الذمة والحرب رقم ٢٩٨٧.
وليس في البخاري اللفظة التي ساقها المؤلف.','3','453',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1129,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

جُندب -رضي الله عنه- (¬١) أنه -صلى الله عليه وسلم- قال: "حدُّ الساحر ضربة بالسيف" رواه الدارقطني (¬٢).
والزنديق (¬٣): يُقتل أيضًا ولا تقبل توبته (¬٤). وهو -بكسر الزاي-
_________
(¬١) هو جندب بن عبد الله، وقيل: ابن زهير بن عبد الله الأزدي الغامدي، يقال له: جندب الخير، يكنى أبا عبد الله، من أهل الكوفة، له صحبة، روى عنه تميم بن الحارث والحسن البصري وأبو عثمان الهندي، روى له الترمذي حديثًا واحدًا، قتل في صفين.
تهذيب الكمال ٥/ ١٤١، الاستيعاب ١/ ٢١٨، أسد الغابة ١/ ٣٠٥، سير أعلام النبلاء ٣/ ١٧٥، الإصابة ١/ ٢٤٧.
(¬٢) ٣/ ١١٤ كتاب الحدود والديات وغيره، ورواه أيضًا الترمذي ٥/ ١٥٦ كتاب الحدود، باب ما جاء في حد الساحر رقم ١٤٦٠، والحاكم ٤/ ٣٦٠ كتاب الحدود، والبيهقي ٨/ ١٣٦ كتاب القسامة، باب تكفير الساحر وقتله إن كان ما يسحر به كلام كفر صريح.
قال الترمذي ٥/ ١٥٦: هذا حديث لا نعرفه مرفوعًا إلا من هذا الوجه، وإسماعيل بن مسلم المكي يضعف في الحديث، وإسماعيل بن مسلم العبدي البصري قال وكيع: هو ثقة، ويروى عن الحسن أيضًا، والصحيح عن جندب موقوفًا.
وقال الحاكم ٤/ ٣٦٠: هذا حديث صحيح الإسناد، وإن كان الشيخان تركا حديث إسماعيل بن مسلم، فإنه غريب صحيح، وله شاهد صحيح على شرطهما جميعًا في ضد هذا.
وقال البيهقي ٨/ ١٣٦: إسماعيل بن مسلم ضعيف.
وقال الذهبي في الكبائر ص ٤٦: والصحيح أنه من قول جندب.
(¬٣) وهو الذي لا يتمسك بشريعة، ويقول: بدوام الدهر. والعرب تعبر عن هذا بقولهم: ملحد. أي: طاعن في الأديان.
المطلع على أبواب المقنع ص ٣٧٨، المعرب ص ٣٤٢ مادة زنديق، القاموس المحيط ٢/ ٤٨١ مادة ز ن د ق، المصباح المنير ١/ ٢٥٦ مادة الزنديق.
(¬٤) وإليه ذهب المالكية، والحنابلة، وهو وجه عند الشافعية، وقول: الليث، وإسحاق.
وذهب الشافعية: إلى أن الزنديق تقبل توبته، ولو كان لا يتناهي خبثه. وهو رواية عن الإمام أحمد، وهو قول: العنبري. =','3','454',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1130,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .
<hr><span data-type="title">منحة السلوك</span>

كالقرامطة (¬١)، والمانوية (¬٢)، ونحوهما.
_________
= شرح فتح القدير ٦/ ٩٨، تبيين الحقائق ٣/ ٢٩٣، المعونة ٣/ ١٣٦٣، متن الرسالة ص ١٤٣، الكافي لابن عبد البر ص ٥٨٥، مواهب الجليل ٦/ ٢٧٩، التاج والإكليل ٦/ ٢٨٠، روضة الطالبين ١٠/ ٧٥، أسنى المطالب ٤/ ١٢٢، كشاف القناع ٦/ ١٧٧، غاية المنتهى ٦/ ٢٩٢، الشرح الكبير لابن قدامة ١٠/ ٨٧، الروض المربع ص ٤٧٤.
(¬١) القرامطة -بفتح القاف وكسر الميم-: فرقة من غلاة الشيعة الباطنة، نشأت بالعراق سنة ٢٨١ هـ، ومن مبادئها الإباحية. الواحد منهم قرمطي نسبة إلى حمدان الملقب بقرمط.
دراسة عن الفرق في تاريخ المسلمين ص ٢١٧، الملل والنحل ١/ ٢٩٠.
(¬٢) المانوية: هم أصحاب ماني بن فاتك المجوسي، وعقيدتهم: أن العالم مخلوق من أصلين قديمين، هما النور والظلمة.
الملل والنحل ١/ ٢٤٤.','3','455',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1131,'<span data-type="title" id=toc-564>فصل</span>
والخوارجُ يُدعون إلى الإسلام، وتكشف شبهتهم،
<hr><span data-type="title">منحة السلوك</span>

فصل
هذا الفصل في بيان أحكام البغاة (¬١) والخوارج.

<span data-type="title" id=toc-565>[الخوارج وأحكامهم]</span>
قوله: والخوارج يدعون إلى الاسلام وتكشف شبهتهم (¬٢).
لأن عليًا -رضي الله عنه- بعث عبد الله بن عباس إلى أهل حرورى، فدعاهم إلى التوبة، وناظرهم قبل قتالهم (¬٣).
_________
(¬١) البغي: مصدر بغى يبغي بغيًا: إذا تعدى. وأهل البغي: هم الخارجون عن طاعة الإمام المعتدون عليه.
مختار الصحاح ص ٢٤ مادة ب غ ي، القاموس المحيط ١/ ٢٩٩ مادة ب غ ي، مجمل اللغة ص ٨٠ باب الباء والغين وما يثلثهما مادة بغى، المصباح المنير ١/ ٥٧ مادة بغيته، أنيس الفقهاء ص ١٧٨، طلبة الطلبة ص ٢١٢، المطلع على أبواب المقنع ص ٢٧٧.
(¬٢) بداية المبتدي ٢/ ٤٦٤، الكتاب ٤/ ١٥٤، الهداية ٢/ ٤٦٤، المختار ٤/ ١٥١.
(¬٣) رواه النسائي في السنن الكبرى ٥/ ١٦٥ كتاب الخصائص، باب ذكر مناظرة عبد الله بن عباس الحرورية واحتجاجه فيما أنكروه على أمير المؤمنين علي بن أبي طالب -رضي الله عنه- رقم ٨٥٧٥، والحاكم في المستدرك ٢/ ١٥٠ كتاب قتال أهل البغي، والبيهقي ٨/ ١٧٩ كتاب قتال أهل البغي، باب لا يبدأَ الخوارج بالقتال حتى يسألوا ما نقموا، ثم يؤمروا بالعود، ثم يؤذنوا بالحرب.
عن أبي زميل قال: حدثني عبد الله بن عباس قال: لما خرجت الحرورية اعتزلوا في دار وكانوا ستة آلاف ... إلى أن قال: فرجع منهم ألفان، وخرج سائرهم، فقتلوا على ضلالتهم، قتلهم المهاجرون والأنصار.
ورواه الطحاوي مختصرًا في شرح معاني الآثار ٣/ ٢١٢ كتاب السير، باب الإمام يريد قتال أهل الحرب هل عليه قبل ذلك أن يدعوهم أم لا؟
قال الحاكم ٢/ ١٥٠: صحيح على شرط مسلم ولم يخرجاه.
وقال ابن حجر في الدراية ٢/ ١٣٨: وإسناده صحيح.','3','456',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1132,'ولا يبدأ بهم الإمام بالقتال، حتى يبدؤوه به، أو يجتمعوا له، فعند ذلك يقاتلهم، حتى يفرقهم
<hr><span data-type="title">منحة السلوك</span>

قوله: ولا يبدأ بهم الإمام بالقتال حتى يبدؤوه به. أي: بالقتال، أو يجتمعوا له. أي: للقتال، فعند ذلك يقاتلهم، حتى يفرق جمعهم (¬١).
وعند الشافعي: لا يبدأ الإمام حتى يبدؤوا بالقتال حقيقة (¬٢).
ولنا قوله تعالى: {فَقَاتِلُوا الَّتِي تَبْغِي حَتَّى تَفِيءَ إِلَى أَمْرِ اللَّهِ} [الحجرات: ٩]، يعني: من غير قيد بالبداية منهم (¬٣)، وقول عليّ -رضي الله عنه-: سمعت رسول الله -صلى الله عليه وسلم- يقول: "سيخرج قوم في آخر الزمان، حداث الأسنان، سفهاء الأحلام (¬٤)، يقولون: من قول: خير البريَّة لا يجاوز إيمانهم حناجرهم (¬٥)،
_________
(¬١) وكذا عند المالكية، والحنابلة.
الاختيار ٤/ ١٥١، الكتاب ٤/ ١٥٤، الهداية ٢/ ٤٦٤، بداية المبتدي ٢/ ٤٦٤، مختصر الطحاوي ص ٢٥٧، أقرب المسالك ص ١٧٤، القوانين ص ٢٣٨، جواهر الإكليل ٢/ ٢٧٧، مختصر خليل ص ٣٢١، القوانين ص ٢٣٨، الشرح الصغير ٢/ ٤١٥، الشرح الكبير لابن قدامة ١٠/ ٥٢، الروض المربع ص ٤٧٢.
(¬٢) منهج الطلاب ٥/ ١١٥، فتح الوهاب ٥/ ١١٥، أسنى المطالب ٤/ ١١٢.
(¬٣) تبيين الحقائق ٣/ ٢٩٤، شرح فتح القدير ٦/ ١٠٢، تفسير النسفي ٤/ ١٦٩، تفسير البغوي ٥/ ١٢٠١.
(¬٤) أي: جهلة صغار العقول.
مختار الصحاح ص ١٢٧ مادة س ف هـ، القاموس المحيط ٢/ ٥٧٧ مادة س ف هـ، المصباح المنير ١/ ٢٨٠ مادة سَفِه، مجمل اللغة ص ٣٥٠ باب السين والفاء وما يثلثهما مادة سفه.
(¬٥) الحنجرة: الحلقوم، وهي رأس القلصمة من خارج، وقيل: هي رأس القلصمة. وقيل: جوف الحلقوم.
القاموس المحيط ١/ ٧٢٢ مادة ح ن ج ر، تاج العروس ٣/ ١٦٠ مادة حنجر، لسان العرب ٤/ ٢١٦ مادة حنجر.','3','457',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1133,'فإن كانت لهم فئة، أجهز على جريحهم، واتبع مُولِّيهم، وإلا فلا.
<hr><span data-type="title">منحة السلوك</span>

يمرُقون (¬١) من الدين كما يمرق السهم (¬٢) من الرمية، فأينما لقيتموهم فاقتلوهم، فإن في قتلهم أجرًا لمن قتلهم يوم القيامة" رواه أحمد، ومسلم، والبخاري (¬٣).
قوله: فإن كانت لهم فئة. أي: جماعةٌ، أجهز على جريحهم. يعني: يتم جرحه، واتبع موليهم؛ دفعًا لشرِّهم؛ لئلَّا يلحق المولِّي والجريح بالفئة (¬٤).
قوله: وإلا فلا. أي: وإن لم يكن لهم فئة، لا يجهز على جريحهم، ولا يتبع موليهم (¬٥).
_________
(¬١) مرق، بمعنى: خرج، ومنه سميت الخوارج مارقة.
لسان العرب ١٠/ ٣٤١ مادة مرق، القاموس المحيط ٤/ ٢٣٢ مادة م ر ق، مختار الصحاح ص ٢٥٨ مادة م ر ق، المصباح المنير ٢/ ٥٦٩ مادة المرق.
(¬٢) السهم: النبل، وهو عود من خشب، يسوى في طرفه نصل، يرمى به عن القوس. القاموس المحيط ٢/ ٤٣٩ مادة س هـ م، المصباح المنير ١/ ٢٩٣ مادة السهم، لسان العرب ١٢/ ٣٠٨ مادة سهم.
(¬٣) أحمد ١/ ٨٨، ومسلم ٢/ ٧٤١ كتاب الزكاة، باب ذكر الخوارج وصفاتهم رقم ١٠٦٤، والبخاري ٣/ ١٢١٩ كتاب الأنبياء، باب قول الله: {وَأَمَّا عَادٌ فَأُهْلِكُوا بِرِيحٍ صَرْصَرٍ عَاتِيَةٍ} رقم ٣١٦٦.
(¬٤) وذهب المالكية، والشافعية، والحنابلة: إلى أنه لا يجهز على جريحهم، ولا مواليهم، ولا تسبى ذراريهم، ولا تغنم أموالهم، ولا يتبع مدبرهم، ولا يقتل أسيرهم، سواء كانت لهم فئة، أم لا.
بداية المبتدي ٢/ ٤٦٤، شرح فتح القدير ٦/ ١٠٣، الكتاب ٤/ ١٥٥، الهداية ٢/ ٤٦٤، الشرح الكبير للدردير ٤/ ٢٩٩، حاشية الدسوقي ٤/ ٩٩، منح الجليل ٩/ ٢٠٠، المنهاج ٤/ ١٧٤، زاد المحتاج ٤/ ١٧٧، العمدة لابن قدامة ص ١١١، مختصر الخرقي ص ١٢٣، الإفصاح ٢/ ٢٣١.
(¬٥) بداية المبتدي ٢/ ٤٦٤، الاختيار ٤/ ١٥٢، الهداية ٢/ ٤٦٤، المختار ٤/ ١٥٢، الكتاب ٤/ ١٥٥، شرح فتح القدير ٦/ ١٠٣.','3','458',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1134,'ولا تُسْبَى ذراريهم، ولا تُغنم أموالهم.
ويجوز القتال بأسلحتهم، وركوب خيلهم عند الحاجة.
<hr><span data-type="title">منحة السلوك</span>

قوله: ولا تُسبى (¬١) ذراريهم، ولا تغنم أموالهم.
لأنهم معصومون في الدماء والأموال (¬٢)، ولكن تُحبس حتى يتوبوا، فترد عليهم بالإجماع (¬٣).
قوله: ويجوز القتال بأسلحتهم، وركوب خيلهم عند الحاجة (¬٤).
لأن عليًا -رضي الله عنه- قسم سلاحهم بالبصرة من بين أصحابه (¬٥)، وكانت قسمته للحاجة لا للتمليك (¬٦).
_________
(¬١) السبي: أخذ النساء والصبيان أسرى من العدو.
القاموس المحيط ٢/ ٥١٧، مادة س ب ي، الدر النقي ٣/ ٧٤٢.
(¬٢) الهداية ٢/ ٤٦٥، العناية ٦/ ١٠٤، الكتاب ٤/ ١٥٥، الاختيار ٤/ ١٥٢، سنن الحقائق ٣/ ٢٩٥.
(¬٣) مراتب الإجماع ص ١٣٢، قال في المغني ١٠/ ٦٢: فأما غنيمة أموالهم، وسبي ذريتهم، فلا نعلم في تحريمه بين أهل العلم خلافًا.
(¬٤) الاختيار ٤/ ١٥٢، بداية المبتدي ٢/ ٤٦٥، تبيين الحقائق ٣/ ٢٩٥، الهداية ٢/ ٤٦٥، المختار ٤/ ١٥٢، العناية ٦/ ١٠٤، المحيط الرضوي "مخطوط" لرضي الدين محمد بن محمد السرخسي جـ ٢ لوحة ٢١٤/ أالنسخة الأصلية لدى مكتبة شستربتي بلندن، تحت رقم ٥٠٥٥.
(¬٥) روى ابن أبي شيبة في مصنفه ٧/ ٥٤٤ كتاب الجمل، باب في مسيرة عائشة وعلي، وطلحة والزبير -رضي الله عنهم- رقم ٣٧٨٢٠. قال حدثنا وكيع، عن قطر، عن منذر، عن ابن الحنفية، أن عليًا قسم يوم الجمل في العسكر ما أجافوا عليه، من كراع، وسلاح.
ورواه ابن سعد في الطبقات ٥/ ٩١ في ترجمة محمد بن الحنفية، أخبرنا الفضل بن دكين، حدثنا قطر بن خليفة، عن منذر الثوري قال: سمعت محمد بن الحنفية وذكر يوم الجمل قال: لما هزموا قال علي: لا تجهزوا على جريح، ولا تتبعوا مدبرًا، وقسم فيهم بينهم ما قوتل به من سلاح، وكراع، وأخذنا ما جلبوا به علينا من كراع، أو سلاح.
(¬٦) الهداية ٢/ ٤٦٥، شرح فتح القدير ٦/ ١٠٥، تبيين الحقائق ٣/ ٢٩٥.','3','459',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1135,'ويحبِس الإمام أموالهم حتى يتوبوا، فيردها عليهم.
وما جبوه من الزكاة، والعُشر، والخراج، من البلاد التي غلبوا عليها لم يثن، ويفتى المأخوذ منه بإعادة الزكاة، والعشر، إن كان الآخذون أغنياء،
<hr><span data-type="title">منحة السلوك</span>

وقال الشافعي: لا يقاتل به (¬١).
قوله: ويحبس الإمام أموالهم حتى يتوبوا فيردها عليهم؛ لما قلنا: أن أموالهم معصومة، فلا تملك (¬٢).

<span data-type="title" id=toc-566>[معاملة الخوارج]</span>
قوله: وما جبوه من الزكاة، والعشر، والخراج، من البلاد التي غلبوا عليها لم يثن.
لأن التقصير من الإمام، حيث لم يحمِهم، بخلاف ما إذا مرَّ بهم فعشروه حيث يؤخذ ثانيًا؛ لأن التقصير منه حيث مرَّ بهم (¬٣).
قوله: وبفتى المأخوذ منه بإعادة الزكاة والعشر، إن كان الآخذون أغنياء.
_________
(¬١) إلا في حال الضرورة. وإليه ذهب المالكية، والحنابلة.
جواهر الإكليل ٢/ ٢٧٧، الشرح الصغير ١/ ٤١٥، الخرشي على خليل ٨/ ٦١، حاشية العدوي على خليل ٨/ ٦١، الوجيز ٢/ ١٦٥، الإقناع في حل ألفاظ أبي شجاع ٢/ ٢٠٤، روض الطالب ٤/ ١١٤، شرح منتهى الإرادات ٣/ ٣٨٣، الإفصاح ٢/ ٢٣١، الفروع ٦/ ١٥٤.
(¬٢) وفاقًا للمالكية، والشافعية، والحنابلة.
الكتاب ٤/ ١٥٥، المختار ٤/ ١٥٢، الهداية ٢/ ٤٦٥، العناية ٦/ ١٠٥، بلغة السالك ١/ ٤٧٥، منح الجليل ٩/ ٢٠١، تحفة المحتاج ٩/ ٧٢، السراج الوهاج ص ٥١٧، المحرر ٢/ ١٦٦، منتهى الإرادات ٢/ ٣٨٤، كشاف القناع ٦/ ١٦٤.
(¬٣) شرح فتح القدير ٦/ ١٠٥، الكتاب ٤/ ١٥٦، بداية المبتدي ٢/ ٤٦٥، الهداية ٢/ ٤٦٥.','3','460',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1136,'بخلاف الخراج.
<hr><span data-type="title">منحة السلوك</span>

هذا الإفتاء فيما بينهم وبين الله؛ لأنهم لم يصرفوها إلى مستحقيها ظاهرًا (¬١).
قوله: بخلاف الخراج.
يعني: لا يفتى منه بالإعادة؛ لأنهم مصارف له، لكونهم مقاتلةً (¬٢).
وقيل: إذا نوى بالدفع التصدق عليهم أجزأ له الصدقات أيضًا، كالخراج (¬٣)؛ لأنهم لو حوسبوا بما عليهم من التبعات ظهروا فقراء.
وأما ملوك زماننا فهل نسقط هذه الحقوق بأخذهم من أصحاب الأموال أم لا؟ قال الهندواني: تسقُط وإن لم يضعوها في أهلها؛ لأن حق الأخذ لهم، فكان الوبال عليهم.
وقال أبو بكر بن سعيد: يسقط الخراج عنهم، ولا تسقط الصدقات كما في البغاة.
_________
(¬١) الهداية ٢/ ٤٦٥، شرح فتح القدير ٦/ ١٠٥، الكتاب ٤/ ١٥٦، بداية المبتدي ٢/ ٤٦٥.
(¬٢) الهداية ٢/ ٤٦٥، شرح فتح القدير ٦/ ١٠٥، الكتاب ٤/ ١٥٦، بداية المبتدي ٢/ ٤٦٥.
(¬٣) وذهب المالكية: إلى أن ما أخذه البغاة من الخراج والزكوات لا تسقط عمن كان عليه، كالغاصب. خلافًا لأشهب، وهو قول: أبي عبيد.
وذهب الشافعية، والحنابلة، وقول للثوري: إلى أن ما أخذوا في حال امتناعهم من زكاة، أو خراج، أو جزية لم يعد عليهم، ولا على صاحبه، وأجزأ؛ لوقوعه موقعه؛ لأن عليًا لما ظهر على أهل البصرة لم يطالبهم بشيء مما جباه أهل البغي، وكان ابن عمر، وسلمة ابن الأكوع، يأتيهم ساعي نجدة الحروري فيدفعوا إليه زكاتهم؛ ولأن في ترك الاحتساب به ضررًا عظيمًا، ومشقة كثيرة؛ لأنهم قد يغلبوا على البلاد السنين الكثيرة، خوارج كانوا، أو غيرهم.
شرح فتح القدير ٦/ ١٠٥، الذخيرة ١٢/ ٩، القوانين ص ٢٣٩، المنهاج ٤/ ١٧٥، مغني المحتاج ٤/ ١٢٥، أسنى المطالب ٤/ ١١٤، المقنع ٤/ ١٥١، العمدة لابن قدامة ص ١١١، كشاف القناع ٦/ ١٦٥، المغني ١٠/ ٦٦.','3','461',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1137,'ولو قتل بعضهم بَعضًا، ثم ظهرنا عليهم، فهو هدر. ولو غلبوا على بلد، وقتل رجل من أهله رجلًا آخر، ثم ظهرنا على البلد قبل استقرار ملكهم، وإجراء أحكامهم، وجب القصاص، وإلا فهو هدر.
<hr><span data-type="title">منحة السلوك</span>

وقال أبو بكر الإسكاف (¬١): لا يسقط الجميع.
وقيل: إذا نوى بالدفع إليهم التَّصدق يسقط، وإلا فلا.
وعلى هذا: ما يؤخذ من الرجل في جبايات الظلمة، والمصادرات، إذا نوى بالدفع التصدق عليهم جاز عما نوى.

<span data-type="title" id=toc-567>[قتل الخوارج بعضهم بعضًا]</span>
قوله: ولو قتل بعضهم بعضًا، ثم ظهرنا عليهم، فهو هدر.
يعني: إن قتل باغ باغيًا مثله في عسكرهم، ثم ظهرنا عليهم، لم يجب عليه القصاص كالقتل في دار الحرب (¬٢).
قوله: ولو غلبوا على بلد، وقتل رجل من أهله. أي: من أهل البلد رجلًا آخر، يعني: من أهل المصر، ثم ظهرنا على البلد قبل استقرار ملكهم.
أي: ملك البغاة، وإجراء أحكامهم، وجب القصاص؛ لأن ولاية إمام أهل العدل لم تنقطع قبل أن تجري أحكامهم، فيجب القصاص (¬٣).
قوله: وإلا فهو هدر (¬٤).
_________
(¬١) هو أبو بكر محمد بن أحمد الإسكاف البلخي، فقيه حنفي، إمام كبير، جليل القدر، أخذ الفقه عن محمد بن سلمة وأبي سليمان الجوزجاني وبه انتفع وعليه تخرج. من تصانيفه: شرح الجامع الكبير، شرح الجامع الصغير. توفي سنة ٣٣٣ هـ.
الجواهر المضية ٣/ ٧٦، كشف الظنون ١/ ٥٦٩، هدية العارفين ٢/ ٣٧، الفوائد البهية ص ١٦٠.
(¬٢) كنز الدقائق ٣/ ٢٩٥، بداية المبتدي ٢/ ٤٦٥، تبيين الحقائق ٣/ ٢٩٥، الهداية ٢/ ٤٦٥.
(¬٣) الهداية ٢/ ٤٦٥، تبيين الحقائق ٣/ ٢٩٥، كنز الدقائق ٣/ ٢٩٥، بداية المبتدي ٢/ ٤٦٥.
(¬٤) بسكون الدال المهملة، وفتحها، أي: باطلًا. =','3','462',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1138,'ولا يأثم العادل، ولا يضمن بإتلاف مال الباغي، أو نفسه. والباغي يأثم فيما يفعل بالعادل، ولا يضمن.
<hr><span data-type="title">منحة السلوك</span>

يعني: وإن ظهرنا عليهم بعد استقرارهم، وإجراء أحكامهم فالقصاص هدر؛ لانقطاع ولاية الإمام العادل، فلا يجب (¬١).

<span data-type="title" id=toc-568>[تصرفات الباغي]</span>
قوله: ولا يأثم العادل، ولا يضمن بإتلاف مال الباغي، أو نفسه.
لأن قتل الباغي واجب، فلا إثم على قاتله، ولا ضمان (¬٢).
قوله: والباغي يأثم فيما يفعل بالعادل؛ لأن قتله حرام (¬٣).
قوله: ولا يضمن.
يعني: لا يجب عليه الضمان في قتله العادل؛ لأنه قتل حصل بتأويل صحيح عنده، وإن كان فاسدًا في نفسه (¬٤).
_________
= مختار الصحاح ص ٢٨٨ مادة هـ د ر، القاموس المحيط ٤/ ٤٩٠ مادة هـ د ر، المصباح المنير ٢/ ٦٣٥ مادة هدر، المطلع على أبواب المقنع ص ٣٦١.
(¬١) تبيين الحقائق ٣/ ٢٩٥، شرح فتح القدير ٦/ ١٥٠، الهداية ٢/ ٤٦٥، كنز الدقائق ٣/ ٢٩٥، العناية ٦/ ١٠٥.
(¬٢) الهداية ٢/ ٤٦٦، المبسوط ١٠/ ١٣٢، بدائع الصنائع ٧/ ١٤١، تبيين الحقائق ٣/ ٢٩٦.
(¬٣) بدائع الصنائع ٧/ ١٤١، شرح فتح القدير ٦/ ١٠٥، تبيين الحقائق ٣/ ٢٩٦.
قال العز بن عبد السلام: "لا يتصف إتلافهم بإباحة، ولا بتحريم؛ لأنه خطأ معفو عنه، بخلاف ما يتلفه الكفار حال القتال، فإنه حرام غير مضمون" مغني المحتاج ٤/ ١٢٥.
(¬٤) اتفق العلماء: على أن ما يتلفه أهل العدل على أهل البغي، فلا ضمان عليه، وذلك في حال الحرب.
كما اتفقوا: على ما أتلفه أهل العدل على أهل البغي، وعكسه في غير حال الحرب، فإنه مضمون. واختلفوا فيما يتلفه أهل البغي على أهل العدل هل يضمن أم لا؟
ذهب الحنفية والحنابلة، وهو القول الجديد للشافعي: أنهم لا يضمنون؛ لأن تضمينهم يفضي إلى تنفيرهم من الرجوع إلى الطاعة فسقط، كأهل الحرب، أو كأهل العدل. =','3','463',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1139,'ولو<span data-type="title" id=toc-569> قتل العادل الباغي </span>ورثه، ولو قتله الباغي وقال: قتلته محقًا ورثه،
<hr><span data-type="title">منحة السلوك</span>

قوله: ولو قتل العادل الباغي ورثه.
لأن حرمان الإرث جزاء الجريمة، ولا جريمة في القتل الواجب، أو الجائز، فلا يحرم (¬١).
وقال الشافعي: لا يرث (¬٢).

[قتل العادل الباغي]
قوله: ولو قتله الباغي، وقال: قتلته محقًا ورثه.
_________
= وذهب الشافعي في القول القديم، ورواية عن الإمام أحمد: إلى أنهم يضمنون ما أتلفوه؛ لأنها نفوس، وأموال، معصومة أتلفت بغير حق، ولا ضرورة دفع مباح، فوجب ضمانه كالذي في غير حال الحرب.
وعند المالكية: لا يضمن متأول ما أتلف نفسًا، أو مالًا، وغير المتأول يأثم، ويضمن النفس، والمال فيقتص منه، ويغرم عوض المال إن أتلفه.
الهداية ٤/ ٤٦٦، تبيين الحقائق ٣/ ٢٩٦، بدائع الصنائع ٧/ ١٤١، شرح فتح القدير، أقرب المسالك ص ١٧٤، مختصر خليل ص ٣٢٢، جواهر الإكليل ٢/ ٢٧٧، الخرشي على خليل ٣/ ٢٩٦، القوانين ص ٢٣٨، الذخيرة ١٢/ ١٠، مغني المحتاج ٤/ ١٢٥، تحفة المحتاج ٩/ ٦٩، شرح الزركشي ٦/ ٢٢٥، كشاف القناع ٦/ ١٦٥، الشرح الكبير لابن قدامة ١٠/ ٥٩، الإفصاح ٢/ ٢٣٢.
(¬١) وإليه ذهب المالكية، والحنابلة.
المبسوط ١٠/ ١٣١، تبيين الحقائق ٣/ ٢٩٦، الهداية ٢/ ٤٦٦، المختار ٤/ ١٥٣، بداية المبتدي ٢/ ٤٤٦، منح الجليل ٩/ ٢٠٢، التاج والإكليل ٦/ ٢٧٩، الخرشي على خليل ٨/ ٦١، جواهر الإكليل ٢/ ٢٧٧، العمدة لابن قدامة ص ٦٦، الإفصاح ٢/ ٩٢، الروض المربع ص ٣٥٥.
(¬٢) وهو رواية عن الإمام أحمد، وقول ابن حامد من الحنابلة.
أنوار المسالك، شرح عمدة السالك وعمدة الناسك ص ٣٦٣، الإقناع في حل ألفاظ أبي شجاع ٢/ ٤٩، المغني ١٠/ ٧٦٦، الإنصاف ٧/ ٣٦٩، المبدع ٦/ ٢٦٢.','3','464',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1140,'وإن قال: قتلته مبطلًا لم يرثه.
<hr><span data-type="title">منحة السلوك</span>

لأنه أتلف ما أتلف عن تأويل فاسد، والفاسد فيه ملحق بالصحيح إذا انضمت إليه منعة، وهو عندهما (¬١).
وقال أبو يوسف: لا يرث الباغي (¬٢).
قوله: وإن قال: قتلته مبطلًا لم يرثه. وهذا بالاتفاق (¬٣). والله أعلم.
* * *
انتهى الجزء الثالث ويليه الجزء الرابع وأوله
"كتاب الصيد والذبائح"
_________
(¬١) وإليه ذهب المالكية، والشافعية، وهو رواية عن الإمام أحمد، والمذهب عند الحنابلة: أن الباغي يرث العادل إذا قتله.
المختار ٤/ ١٥٣، كنز الدقائق ٣/ ٢٩٥، المبسوط ١٠/ ١٣٢، تبيين الحقائق ٣/ ٢٩٥، الاختيار ٤/ ١٥٣، جواهر الإكليل ٢/ ٣٣٨، الذخيرة ١٣/ ٦٨، عمدة السالك، وعدة الناسك ص ٣٦٣، السراج الوهاج ص ٣٢٩، الإنصاف ٧/ ٣٦٩، الفروع ٥/ ٥٥، تصحيح الفروع ٥/ ٥٥، الروض المربع ص ٣٥٥، المبدع ٦/ ٢٦٢، منتهى الإرادات ٢/ ٦٣٧، العمدة لابن قدامة ص ٦٦، الإفصاح ٢/ ٢٩.
(¬٢) الاختيار ٤/ ١٥٣، بداية المبتدي ٢/ ٤٦٦، تبيين الحقائق ٣/ ٢٩٥، الهداية ٢/ ٤٦٦.
(¬٣) الهداية ٢/ ٤٦٦، المبسوط ١٠/ ١٢٨، بداية المبتدي ٢/ ٤٦٦، كنز الدقائق ٣/ ٢٩٥.','3','465',NULL,NULL,'0');
INSERT INTO [b46] VALUES (1141,'المَسْبُوكُ
عَلى
منْحَة السُّلوْك
