-- Insert data for b535
INSERT INTO [b535] VALUES (1,'<span data-type="title" id=toc-1>١</span>- أبو اسحاق ابراهيم بن محمد بن يحيى شيخ العدالة والتزكية وأحسن مشايخنا مواساة لغرباء الصنعة ولا أعرف احدا تكلم فيه بحجة.
<span data-type="title" id=toc-2>٢</span>- ابو احمد محمد بن عيسى صاحب كتاب مسلم من أعيان',NULL,'55',NULL,NULL,'0');
INSERT INTO [b535] VALUES (2,'الفقراء والزهاد ومن اصحاب المعاملات في التصوف قد كان ضاعت سماعاته من ابراهيم بن محمد بن سفيان الفقيه فنسخ البعض من نسخة لم يكن له فيها سماع.
<span data-type="title" id=toc-3>٣</span>- ابو احمد الحسين بن علي التميمي شيخ العرب في بلدنا ومن ورث الثروة القديمة واسلافه اجلة تميمي سليطي',NULL,'56',NULL,NULL,'0');
INSERT INTO [b535] VALUES (3,'معاذي رجاني ولم يجمع لغيره والغالب على سماعاته الصدق.
<span data-type="title" id=toc-4>٤</span>- ابو احمد محمد بن محمد بن مطر ابوه رحمنا الله وإياهما ثقة وقد كان في حياته كتب لابنه هذا فوائد بخطه من سماعات شهد له بها إلا أن الحديث لم يكن من شأنه وقد كان قديما من أعيان الشهود ثم سكتوا عنه.
<span data-type="title" id=toc-5>٥</span>- ابو بكر محمد بن محمد بن داود السجزي صدوق ما رأينا منه في الأخذ والأداء إلا ما يليق بأهل الصدق.
<span data-type="title" id=toc-6>٦</span>- ابو بكر محمد بن محمود بن إسحاق قد كتبنا عنه أبيه',NULL,'57',NULL,NULL,'0');
INSERT INTO [b535] VALUES (4,'وعميه وابن عمه ابني عمرو وكلهم من أهل الصدق وأبو بكر هذا لم نكتب عنه إلا أن القلب يشهد أنه لم يقل على ستر سلفه رضي الله عنهم أجمعين.
<span data-type="title" id=toc-7>٧</span>- ابو الحسن محمد بن عبد الله بن ابراهيم بن عبدة شيخ من سليط تميمي رأيت له عن البوشنجي وجعفر بن الترك وأقرانهما سماعات صحيحة ثم وقع إليه أبو بكر ابن الغازي الوراق فزاد فيها على',NULL,'58',NULL,NULL,'0');
INSERT INTO [b535] VALUES (5,'ما بلغني فقبلها والله أعلم.
<span data-type="title" id=toc-8>٨</span>- ابو الحسن علي بن احمد بن عبد العزيز الجرجاني صاحب كتاب البخاري كثير السماع معروف بالطلب الا انه وقع إلى أبي بشر المصعبي الفقيه وكأنه أخذ بسيرته في الحديث فظهرت منه المجازفة عند الحاجة إليه فترك.
<span data-type="title" id=toc-9>٩</span>- ابو الحسين طاهر بن محمد بن سهلوية',NULL,'59',NULL,NULL,'0');
INSERT INTO [b535] VALUES (6,'عدل صدوق في الاخذ والاداء كثير السماع معتمد فيما كتب عنه من اصوله.
<span data-type="title" id=toc-10>١٠</span>- ابو سهل محمد بن سليمان الفقيه مفتي البلد وفقيهها واجدل من رأينا من الشافعيين بخراسان ومع ذلك اديب شاعر نحوي كاتب عروضي محب للفقراء والله أعلم.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b535] VALUES (7,'ابو الطيب عبد الله بن محمد بن أحمد بن حيان هذا ابن فورس قاضي طوس حمل الى اصوله في نيف واربعين لما عزم على الخروج الى الحج فلم اقف منها على زلة وخرجت له الفوائد وهو في الجملة صدوق إلا أن الحديث لم يكن من شأنه.
<span data-type="title" id=toc-11>١٢</span>- ابو عبد الله محمد بن احمد بن بشر المزكي حج معنا سنة احدى واربعين ورايته يحدث من اصوله في القطيعة عن البوسنجي وغيره وهو عندنا صدوق في اكثر احواله.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b535] VALUES (8,'<span data-type="title" id=toc-12>١٣</span>- ابو عبد الله الحسين بن احمد الصفار الهروي كذاب لا يشتغل بالسؤال عنه.
<span data-type="title" id=toc-13>١٤</span>- ابو علي محمد بن احمد بن بالويه شاهد من المعمرين وقد كان سمع على كبر السن وهو صدوق صاحب كتاب.
<span data-type="title" id=toc-14>١٥</span>- ابو العباس محمد بن الفتح الانماطي حاله قريب من',NULL,'62',NULL,NULL,'0');
INSERT INTO [b535] VALUES (9,'حال ابي علي إلا ان الغالب عليه الغفلة وقرئ عليه حديث منكر عن ابي القاسم ابن منيع
<span data-type="title" id=toc-15>١٦</span>- ابو العباس المصري احمد بن محمد بن عيسى حافظ قديم الرحلة كثير الطلب ولما احتيج اليه وقد ضاعت سماعاته القديمة حدث من حفظه بأحاديث وذكر أنه يعرفها وغير مستبدع لمثله أن يحفظ سؤالات الشيوخ فأما مذاكرته فإن كان يتحرى في اكثرها الصدق واطلعنا على كتبه بعد وفاته فما رأينا إلا الخير والله أعلم.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b535] VALUES (10,'ابو الفضل العباس بن محمد بن ابراهيم الهاشمي قد كتبنا عنه ابيه وكتب هو وإخوانه معا وكان الغالب عليه الغفلة والله يرحمنا وإياه
<span data-type="title" id=toc-16>١٨</span>- أبو عمرو محمد بن محمد الفامي كثير السماع صاحب كتاب إلا أن بعض الغرباء خرج له فوائد فوجد فيها يعني في كتبه أطباقا عن الحسين بن شاكر فأخرج له عنه وحدث عنه بها ببغداد فلما وافا وقفته على حقيقة الحال فرجع وأناب وثاب فرأيت أن أتقدم بالكتابة عنه.
<span data-type="title" id=toc-17>١٩</span>- ابو عثمان سعيد بن أبي سعيد الصوفي صدوق كثير السماع مشهور بالطلب أدركته المنية قبل الحاجة.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b535] VALUES (11,'<span data-type="title" id=toc-18>٢٠</span>- ابو عبد الرحمن محمد بن الحسين السلمي كثير السماع والطلب متقن فيه من بيت الحديث والزهد والتصوف.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b535] VALUES (12,'<span data-type="title" id=toc-19>٢١</span>- ابو القاسم ابراهيم بن محمد بن احمد',NULL,'66',NULL,NULL,'0');
INSERT INTO [b535] VALUES (13,'النصراباذي واعظ الصوفية في عصره طلب الحديث على صغر السن بخراسان والعراقين والشام ومصر وكتب الكثير وجمع وضيع اكثر اصوله توفي بمكة وانا ببغداد فبيعت كتبه في داره وكشفت تلك الكتب عن احوال والله أعلم.
<span data-type="title" id=toc-20>٢٢</span>- ابو محمد عبد الله بن احمد الشيباني أكثر اقرانه في بلدنا سماعا وتفقه على الحديث وهو ان شاء الله صدوق في اكثر ما حدث به وإنه كان يخمن ولا يميز بين سماعه وغير ذلك.
<span data-type="title" id=toc-21>٢٣</span>- ابو محمد عبد الله بن اسماعيل الميكالي أوجه الوجوه.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b535] VALUES (14,'بخراسان وادلهم واكبرهم واكتبهم واكفا الرؤساء لم يشتغل بالحديث فلما عزم على الخروج جمع شيئا من سماعاته ولم يميز بين المخرج له بين سماعه من ابي حامد الخشاب وعبد الله بن الشرقي فوقع من حديث ذا في ذا وهو بنفسه صدوق كبير المحل.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b535] VALUES (15,'<span data-type="title" id=toc-22>٢٤</span>- ابو الحسن يوسف بن اسحاق الفقيه الملقي لم يكن الحديث من شأنه وقد كان اقام بالعراق برهة من الدهر فلم يسمع الحديث وحدث في آخر عمره عندنا بأحاديث مستقيمة عن ابي نعيم وغيره والغالب على احواله الصدق.
<span data-type="title" id=toc-23>٢٥</span>- ابو حامد احمد بن ابراهيم بن محمد بن يحيى سمعه ابوه في صغره وقيد المؤدب سماعاته وقد تأملت اكثرها فوجدتها على السداد وهو بنفسه دين ورع مجتهد في العبادة صدوق الشأن.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b535] VALUES (16,'<span data-type="title" id=toc-24>٢٦</span>- ابو عبد الله محمد بن احمد بن علي بن نصير احد',NULL,'70',NULL,NULL,'0');
INSERT INTO [b535] VALUES (17,'الشهود عندنا ومن اولاد التجار سمعوه صغيرا من محمد بن اسحاق وابي العباس السراج والماسرجسي والخرج له منهم وليس الحديث من شأن الشيخ والله اعلم.
<span data-type="title" id=toc-25>٢٧</span>- ابو علي محمد بن احمد بن عروة الكاتب محمود في المكاتبة حسن السيرة فيها الا انه لم يشهر بالطلب وحدث عن شيخنا ابي العباس بحديث يبطل عمل سنين كثيرة.
قال ابو الحسن والحديث الذي رواه ابو علي ابن عروة الكاتب حدثنا به مسعود بن علي السجزي قال اخبرنا ابو علي محمد بن احمد بن',NULL,'71',NULL,NULL,'0');
INSERT INTO [b535] VALUES (18,'عروة بن محمد بن عروة قرأه عليه ابو سعيد الشعيبي في مجلس ابي زكريا الحربي قال حدثنا ابو العباس الاصم اخبرنا احمد بن شيبان الرملي حدثنا سفيان بن عيينة عن الزهري عن انس قال قال رسول الله صلى الله عليه وسلم من حسن اسلام المرء تركه ما لا يعنيه
<span data-type="title" id=toc-26>٢٨</span>- ابو بكر الكسائي المؤدب صاحب كتاب مسلم كذاب لا يشتغل بمثله.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b535] VALUES (19,'<span data-type="title" id=toc-27>٢٩</span>- ابو جعفر محمد بن احمد السليطي كبير المحل في اصل البيوتات صحيح السماعات وليس الحديث من شانه.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b535] VALUES (20,'<span data-type="title" id=toc-28>٣٠</span>- سمعت مسعود بن علي يقول سألت الحاكم ابا عبد الله عن ابي احمد الحبيبي فقال كان يكذب مثل السكر
فقلت الحسنويي خير ام الحبيبي؟',NULL,'74',NULL,NULL,'0');
INSERT INTO [b535] VALUES (21,'فقال الحسنويي احسن منه حالا
<span data-type="title" id=toc-29>٣١</span>- قال الحاكم: سمعت محمد بن صالح بن هانيء يقول كنت انا فيمن شيع احمد بن علي الحسنويي حيث رحل الى ابي حاتم الرازي.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b535] VALUES (22,'<span data-type="title" id=toc-30>٣٢</span>- وسمعت مسعودا يقول سألت الحاكم عمن يقدم من اصحاب مالك والثوري وشعبة؟
فقال: من اكثر الرواية عنهم فلا يقدم احد على يحيى بن سعيد',NULL,'76',NULL,NULL,'0');
INSERT INTO [b535] VALUES (23,'وعبد الرحمن بن مهدي إلا من مات قبلهم الذي يتعجب بروايتهم عنه.
وسألت أتقدم يحيى أم عبد الرحمن؟
فقال: يحيى وكلاهما كبيران.
<span data-type="title" id=toc-31>٣٣</span>- وسألته عن محمد بن ثابت البناني أهو ثقة
فقال لا بأس به فإنه لم يأت بحديث منكر لكن الشيخين لم يخرجاه وهو عزيز الحديث اسند خمسة عشر حديثا.
وقال يروي عن ثابت البناني اربعة كلهم اسمهم محمد بن ثابت
احدهم محمد بن ثابت البناني
والثاني محمد بن ثابت الأنصاري',NULL,'77',NULL,NULL,'0');
INSERT INTO [b535] VALUES (24,'والثالث محمد بن ثابت العبدي
والرابع محمد بن ثابت العصري
<span data-type="title" id=toc-32>٢٤</span>- وسألته عن حاجب الطوسي',NULL,'78',NULL,NULL,'0');
INSERT INTO [b535] VALUES (25,'فقال لم يسمع قط حديثا لكنه كان له عم قد سمع الحديث فجاء البلاذري إليه وقال هل كنت مع عملك في المجلس؟
فقال بلى فانتخب من كتب عمه تلك الاجزاء وهي خمسة.
<span data-type="title" id=toc-33>٣٥</span>- وسمعته يقول امامنا الشافعي المطلبي رضي الله عنه قرشي النسب من صميمها وليس بالهاشمي فإن بني هاشم ينتمون إلى',NULL,'79',NULL,NULL,'0');
INSERT INTO [b535] VALUES (26,'هاشم بن عبد مناف بن قصي والإمام الشافعي ينتمي إلى هاشم بن المطلب بن عبد مناف
<span data-type="title" id=toc-34>٣٦</span>- وسمعته يقول ابو الحسن الذي يروي عنه ابو حنيفة هو موسى بن أبي عائشة.
<span data-type="title" id=toc-35>٣٧</span>- وسألته عن ابراهيم بن الحسين بن دازيل',NULL,'80',NULL,NULL,'0');
INSERT INTO [b535] VALUES (27,'فقال ثقة مأمون.
<span data-type="title" id=toc-36>٣٨</span>- وسألته عن احمد بن زيد المصري الذي يروي عن سفيان بن عيينة؟
فقال ساقط
<span data-type="title" id=toc-37>٣٩</span>- وسألته عن زكريا بن يحيى بن أسد؟
فقال ثقة
<span data-type="title" id=toc-38>٤٠</span>- واحمد بن شيبان الرملي ثقة واحمد بن اجل وأوثق.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b535] VALUES (28,'<span data-type="title" id=toc-39>٤١</span>- وسألته عن ابراهيم بن عبد الله السعدي
فقال ثقة مامون الا انه طويل اللسان وكان يستخف بمسلم بن الحجاج فغمزه مسلم بلا حجة.
<span data-type="title" id=toc-40>٤٢</span>- وسمعته يقول عثمان بن عبد الله القرشي الذي يروي عن مالك كذاب يكنى ابا عمرو قدم خراسان بعد الثلاثين والمائتين فحدث',NULL,'82',NULL,NULL,'0');
INSERT INTO [b535] VALUES (29,'عن مالك والليث بن سعد وابن لهيعة والحمادين وغيرهم بأحاديث أكثرها موضوعة.
<span data-type="title" id=toc-41>٤٣</span>- وسألته عن علي الأزدي الذي يروي عن ابن عمر فقال: هو علي بن عروة البارقي ثقة مأمون قد احتج به',NULL,'83',NULL,NULL,'0');
INSERT INTO [b535] VALUES (30,'البخاري ومسلم
<span data-type="title" id=toc-42>٤٤</span>- وسمعته يقول يروي الشعبي عن ام هانيء اربعة احاديث
<span data-type="title" id=toc-43>٤٥</span>- وسالته عن سعدان الخلمي الذي يروي عن داود بن ابي هند',NULL,'84',NULL,NULL,'0');
INSERT INTO [b535] VALUES (31,'فقال: هو سعدان بن نصر بن سعيد الخلمي شيخ ثقة من اهل بلخ عزيز الحديث يجمع حديثه في الخراسانيين.
<span data-type="title" id=toc-44>٤٦</span>- وسألته عن ترفل بن داود الذي يروي عن شعبة
<span data-type="title" id=toc-45>٤٧</span>- وسمعته يقول أنيس بن أبي يحيى ثقة مأمون إلا أن في',NULL,'85',NULL,NULL,'0');
INSERT INTO [b535] VALUES (32,'أهل بيته ضعفاء فإنه عم
<span data-type="title" id=toc-46>٤٨</span>- ابراهيم بن محمد بن ابي يحيى الذي روىعنه الشافعي رضي الله عنه.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b535] VALUES (33,'<span data-type="title" id=toc-47>٤٩</span>- وسألته عن يحيى بن عبدك القزويني
فقال ثقة ومن قال عبدك فانه يختصر اسم ابيه فإنه يحيى بن عبد الأعظم.
<span data-type="title" id=toc-48>٥٠</span>- وسألته عن ابي يعلى احمد بن علي بن المثنى
فقال ثقة مأمون.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b535] VALUES (34,'<span data-type="title" id=toc-49>٥١</span>- وسمعته يقول مذهب سفيان بن سعيد ان يكني المجروجين من المحدثين اذا روى عنهم مثل بحر السقاء فيقول حدثنا ابو الفضل والصلت بن دينار يقول حديثا ابو شعيب والكلبي يقول حديثنا الو النضر وسليمان بن ارقم يقول حدثنا ابو',NULL,'88',NULL,NULL,'0');
INSERT INTO [b535] VALUES (35,'معاذ
<span data-type="title" id=toc-50>٥٢</span>- وسمعته يقول عليلة بن بدر ممن يقلب الاسانيد',NULL,'89',NULL,NULL,'0');
INSERT INTO [b535] VALUES (36,'ويروي عن الثقات المقلوبات وعن الضعفاء الموضوعات.
<span data-type="title" id=toc-51>٥٣</span>- وسألته عن ابي يحيى التيمي الذي يروي عن سهيل بن ابي صالح
فقال اسمه اسماعيل بن يحيى بن عبيد الله ولا أشك في ضعفه
<span data-type="title" id=toc-52>٥٤</span>- وسمعته يقول: الحجاج بن ارطاة ممن لا يحتج به.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b535] VALUES (37,'<span data-type="title" id=toc-53>٥٥</span>- وسمعته يقول: عيسى بن جعفر قاضي الري ثقة ثبت
<span data-type="title" id=toc-54>٥٦</span>- وسألته عن عاصم الاسجعي
فقال هو عاصم بن عبد العزيز بن عاصم الاشجعي ابو عبد العزيز والغالب على حديث الخطا
<span data-type="title" id=toc-55>٥٧</span>- وسمعته يقول ابو حاجب صخر بن محمد.......',NULL,'91',NULL,NULL,'0');
INSERT INTO [b535] VALUES (38,'الحاجبي الذي يروي عن انس بن مالك هو شيخ من اهل مرو ذاهب الحديث بمرة
<span data-type="title" id=toc-56>٥٨</span>- وسألته عن عمار بن عبد الجبار الذي يروي عن شعبة؟
فقال هو عمار بن عبد الجبار المروزي ثقة مأمون جاوز بمكة وبها توفي
<span data-type="title" id=toc-57>٥٩</span>- وسمعته يقول حريث بن أبي مطر.......................',NULL,'92',NULL,NULL,'0');
INSERT INTO [b535] VALUES (39,'الفزاري ابو مطر اسمه عمرو وهو عزيز الحديث في الكوفيين
<span data-type="title" id=toc-58>٦٠</span>- وسألته عن بقية بن الوليد
فقال ثقو مامون
