INSERT INTO [b535] VALUES (74,'بباب القادسية وهو بها مدفون وله بها آيات تذكر وهو اعز اهل البيت حديثا
<span data-type="title" id=toc-119>١٢٢</span>- وسالته هل سمع ابن عون من انس
فقال راه ولم يسمع منه
<span data-type="title" id=toc-120>١٢٣</span>- وسمعته يقول إن مما يلزم الحديثي من الضبط والاتقان اذا',NULL,'128',NULL,NULL,'0');
INSERT INTO [b535] VALUES (75,'روى حديثا وساق المتن ثم اعقبه باسناد آخر ان يفرقث بين ان يقول مثله او نحوه فانه لايحل له ان يقول مثله الا بعد ان يقف على المتنين والحديث جميعا فيعلم انهما على لفظ واحد فإذا لم يميز ذلك حل له ان يقول نحوه فإنه اذا قال نحوه فقد بين انه مثل معانية.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b535] VALUES (76,'<span data-type="title" id=toc-121>١٢٤</span>- وسمعته يقول محمد بن الحسين بن خالد القنبيطي حافظ ثقة مأمون الا انه اخطا في حديث واحد وهو الحديث الذي اخبرنيه عيسى بن حامد بن بشر الرخجي ببغداد قال حدثني خالد بن محمد بن الحسين بن خالد حدثنا الحسن بن علي العجلي حدثنا عبيد الله',NULL,'130',NULL,NULL,'0');
INSERT INTO [b535] VALUES (77,'ابن موسى حدثنا سفيان عن الاعمش عن خليفة بن الحصين عن جده قيس بن عاصم المنقري انه اتى النبي صلى الله عليه وسلم فأمره ان يغتسل بماء وسدر وهذا الحديث عن الثوري عن الاغر عن خليفة بن الحصين وهذا كما يقال ان الجواد يعتر
<span data-type="title" id=toc-122>١٢٥</span>- وسمعته يقول قتادة بن النعمان هو اخي ابي سعيد الخدري لامه',NULL,'131',NULL,NULL,'0');
INSERT INTO [b535] VALUES (78,'<span data-type="title" id=toc-123>١٢٦</span>- وسالته عن احمد بن عبيد بن ناصح ابو جعفر النحوي فقال هو امام في النحو وقد سكت مشايخنا عنه في الرواية
<span data-type="title" id=toc-124>١٢٧</span>- وسالته عن عبد العزيز بن يحيى المدني',NULL,'132',NULL,NULL,'0');
INSERT INTO [b535] VALUES (79,'فقال صدوق لم يتهم في رواياته عن مالك
