INSERT INTO [b535] VALUES (179,'فقيل كرواته محمد بن يحيى عن عبد الله بن نافع ومطرف
فقلت هذا محمد بن يحيى اصل فأما القعنبي فانه مقدم على الروايتين عن مالك
قال الحاكم فعلقت اقاويل اهل العلم في القعنبي ليستدل بها على ما ذكرته
<span data-type="title" id=toc-306>٣٠٩</span>- حدثنا ابو النضر محمد بن محمد بن يوسف الفقيه حدثنا عثمان بن سعيد الدارمي قال سمعت علي بن عبد الله المدني وذكر',NULL,'233',NULL,NULL,'0');
INSERT INTO [b535] VALUES (180,'عنده اصحاب مالك فقيل له معن ثم القعنبي فقال لا بل القعنبي ثم معن
<span data-type="title" id=toc-307>٣١٠</span>- حدثنا علي بن عمر الحافظ حدثنا محمد بن مخلد الدوري قال سمعت محمد بن علي المدني يقول سمعت ابي يقول لا يقدم من رواة الموطا احد على القعنبي',NULL,'234',NULL,NULL,'0');
INSERT INTO [b535] VALUES (181,'<span data-type="title" id=toc-308>٣١١</span>- سمعت عمر بن جعفر البصري يقول سمعت بن علي يقول كان عبد الله بن مسلمة القعنبي مجاب الدعوة
