INSERT INTO [b5795] VALUES (35,'عَن أنس قَالَ جَاءَ',NULL,'93',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (36,'زيد بن حَارِثَة',NULL,'94',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (37,'يشكو وَذكر الحَدِيث',NULL,'95',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (38,'وَقَالَ فِي تَفْسِير سُورَة الْأَنْفَال حَدثنِي أَحْمد',NULL,'99',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (39,'حَدثنَا عبيد الله بن معَاذ',NULL,'100',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (40,'حَدثنِي أبي',NULL,'101',NULL,NULL,'0');
