INSERT INTO [b5795] VALUES (73,'عَن نَافِع',NULL,'145',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (74,'عَن ابْن عمر أَن رَسُول الله صلى الله عَلَيْهِ وَسلم كَانَ إِذا خرج إِلَى مَكَّة يُصَلِّي فِي مَسْجِد الشَّجَرَة',NULL,'146',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (75,'قَالَ أَبُو عبد الله الْحَاكِم وَأَبُو نصر هُوَ أَبُو الْعَبَّاس الذهلي وَيُقَال الشَّيْبَانِيّ الْمروزِي مَاتَ يَوْم عَاشُورَاء سنة اثْنَتَيْنِ وَعشْرين وَمِائَتَيْنِ
INSERT INTO [b5795] VALUES (76,'حَدثنَا أَحْمد بن أبي الطّيب حَدثنَا إِسْمَاعِيل بن مجَالد',NULL,'149',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (77,'أَنا بَيَان بن بشر عَن وبرة',NULL,'150',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (78,'عَن همام سَمِعت عمار بن يَاسر يَقُول رَأَيْت رَسُول الله صلى الله عَلَيْهِ وَسلم وَمَا مَعَه إِلَّا خَمْسَة أعبد وَامْرَأَتَانِ وَأَبُو بكر',NULL,'151',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (79,'قَالَ أَبُو عبد الله النَّيْسَابُورِي وَغَيره هُوَ أَحْمد بن بن سُلَيْمَان أَبُو سُلَيْمَان مولى لبَعض المراوزة وَأَبوهُ سُلَيْمَان يكنى أَبَا الطّيب كَانَ على شَرط بُخَارى',NULL,'152',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (80,'وَقَالَ ابو زرْعَة الرَّازِيّ هُوَ بغدادي الأَصْل خرج إِلَى مرو ثمَّ رَجَعَ فسكن الرّيّ كتبنَا عَنهُ وَنسبه أَبُو مُحَمَّد الْأصيلِيّ فِي نسخته أَحْمد بن سُلَيْمَان أَيْضا وَقَالَ إِنَّه مروزي بَاب وَقَالَ فِي الصَّلَاة وَفِي الذَّبَائِح',NULL,'153',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (81,'وَالتَّفْسِير',NULL,'154',NULL,NULL,'0');
INSERT INTO [b5795] VALUES (82,'وَالْحيض حَدثنَا أَحْمد بن أبي رَجَاء',NULL,'155',NULL,NULL,'0');
