INSERT INTO [b5795] VALUES (118,'زوج النَّبِي صلى الله عَلَيْهِ وَسلم وَقَالَ أَحْمد بن صَالح الْكُوفِي فِي تَارِيخه أَحْمد بن حميد الَّذِي تعرف بِهِ دَار أم سَلمَة مَوضِع كَانَ ينزله ثِقَة',NULL,'195',NULL,NULL,'0');
