CREATE TABLE "b5947"(id INTEGER PRIMARY KEY,content TEXT, part TEXT, page TEXT, number TEXT, services TEXT, is_deleted TEXT);

-- Insert data for b5947
INSERT INTO [b5947] VALUES (1,'أخبرنَا أَبُو الْعَبَّاس أَحْمد بن عمر بن أنس العذري بالجزيرة وقرأته عَلَيْهِ قَالَ أَنا أَبُو بكر عبد الله بن الْحُسَيْن بن عقال الْمرَادِي وَأَبُو مُحَمَّد مكي بن عَليّ ابْن عبسون الْمرَادِي بمكه فِي الْمَسْجِد الْحَرَام فِي شهر ربيع الأول سنة اثْنَتَيْ عشره وَأَرْبَعمِائَة قَالَا ثَنَا أَبُو الْقَاسِم عبيد الله بن مُحَمَّد بن أَحْمد بن جَعْفَر السَّقطِي بقرَاءَته فِي الْمَسْجِد الْحَرَام عمره الله تَعَالَى قَالَ أَنا أَبُو مُحَمَّد جَعْفَر بن مُحَمَّد بن الْخَواص قِرَاءَة عَلَيْهِ وَأَنا أسمع قَالَ ثَنَا أَبُو جَعْفَر مُحَمَّد بن عُثْمَان بن أبي شَيْبه قَالَ قلت لعَلي بن عبد الله الْمَدِينِيّ يَا أَبَا الْحسن ان يحيى بن معِين ذكر لنا أَن مَشَايِخ من الْبَصرِيين كَانُوا يرْمونَ بِالْقدرِ إِلَّا أَنهم لَا يدعونَ إِلَيْهِ وَلَا يأْتونَ فِي حَدِيثهمْ بِشَيْء مُنكر مِنْهُم قتاده وَهِشَام صَاحب الدستوَائي',NULL,'45',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (2,'وَسَعِيد بن أبي عروبه وَأَبُو هِلَال وَعبد الْوَارِث وَسَلام كَانُوا ثِقَات يكْتب حَدِيثهمْ فماتوا وهم يرَوْنَ الْقدر وَلم يرجِعوا عَنهُ فَقَالَ لي عَليّ رَحمَه الله أَبُو زَكَرِيَّا كَذَا كَانَ يَقُول عندنَا إِلَّا أَن أَصْحَابنَا ذكرُوا أَن هِشَام الدستوَائي رَجَعَ قبل مَوته وَلم يَصح ذَلِك عندنَا
حَدثنَا مُحَمَّد بن عُثْمَان قَالَ سَأَلت عَليّ بن عبد الله عَن
١ - بشر بن حَرْب فَقَالَ كَانَ ثِقَة عندنَا',NULL,'46',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (3,'٢ - وَسَأَلت عليا عَن أبي المهزم فَقَالَ كَانَ ضَعِيفا',NULL,'47',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (4,'٣ - وَسَأَلت عليا عَن مطر الْوراق فَقَالَ كَانَ صَالحا وسطا وَلم يكن بِالْقَوِيّ
٤ - وَسَأَلت عليا عَن يزِيد الرقاشِي فَقَالَ كَانَ ضَعِيفا',NULL,'48',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (5,'٥ - وَسَأَلت عليا عَن حَكِيم الْأَثْرَم فَقَالَ كَانَ حَكِيم عندنَا ثقه
٦ - وَسَأَلت عليا عَن يزِيد بن عَطاء فَقَالَ كَانَ ضَعِيفا وَهُوَ من موَالِي أبي عوانه',NULL,'49',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (6,'٧ - وَسَأَلت عليا عَن الْحَارِث بن نَبهَان فَقَالَ كَانَ ضَعِيفا ضَعِيفا
٨ - وَسَأَلت عليا عَن مهْدي بن مَيْمُون فَقَالَ كَانَ مهْدي عندنَا ثقه
٩ - وَسَأَلت عليا عَن ابراهيم بن عبد الْملك أبي اسماعيل',NULL,'50',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (7,'القناد فَقَالَ كَانَ ضَعِيفا عندنَا
١٠ - وَسَأَلت عليا عَن عرْعرة بن البرند فَقَالَ كَانَ عرْعرة ثِقَة ثبتا
١١ - وَسَأَلت عليا عَن مَرْحُوم بن عبد الْعَزِيز الْعَطَّار فَقَالَ كَانَ مَرْحُوم من الثِّقَات',NULL,'51',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (8,'١٢ - وَسَأَلت عليا عَن عبد الْمُؤمن بن عبيد الله الْبَصْرِيّ فَقَالَ كَانَ صَالحا وسطا
١٣ - وَسَأَلت عليا عَن عباد بن مَنْصُور فَقَالَ ضَعِيف عندنَا وَكَانَ قدريا وَكَانَ قَاضِيا على البصره',NULL,'52',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (9,'١٤ - وَسَأَلت عليا عَن جَعْفَر بن سُلَيْمَان الضبعِي فَقَالَ ثِقَة عندنَا وَقد كَانَ يحيى بن سعيد لَا يرْوى عَنهُ',NULL,'53',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (10,'١٥ - وَسَأَلت عليا عَن زيد الْعمي فَقَالَ كَانَ ضَعِيفا عندنَا
١٦ - وَسَأَلت عليا عَن عباد بن مَنْصُور فَقَالَ ضَعِيف عندنَا وَكَانَ قدريا وَكَانَ قَاضِيا على البصره
١٧ - وَسَأَلت عليا عَن أبان بن أبي عَيَّاش فَقَالَ كَانَ ضَعِيفا ضَعِيفا عندنَا',NULL,'54',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (11,'١٨ - وَسَأَلت عليا عَن أبي حره وَاصل بن عبد الرَّحْمَن فَقَالَ كَانَ ضَعِيفا
١٩ - وَكَانَ أَخُوهُ سعيد بن عبد الرَّحْمَن عندنَا ثقه',NULL,'55',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (12,'٢٠ - وَسَأَلت عليا عَن صَالح المري فَقَالَ لَيْسَ بِشَيْء ضَعِيف ضَعِيف',NULL,'56',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (13,'٢١ - وَحدثنَا مُحَمَّد بن عُثْمَان قَالَ سَأَلت عليا عَن عَليّ بن زيد ابْن جدعَان فَقَالَ هُوَ ضَعِيف عندنَا
٢٢ - وَسَأَلت عليا عَن جزير بن حَازِم',NULL,'57',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (14,'٢٣ - وأخيه يزِيد بن حَازِم فَقَالَ كَانَا ثقتين عندنَا
٢٤ - وَسَأَلت عليا عَن سوار بن عبد الله القَاضِي فَقَالَ هُوَ ثِقَة عندنَا',NULL,'58',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (15,'٢٥ - وَسَأَلت عليا عَن الرّبيع بن صبيح فَقَالَ هُوَ عندنَا صَالح لَيْسَ بِالْقَوِيّ
٢٦ - وَسَأَلت عليا عَن الْمُبَارك بن فضاله فَقَالَ هُوَ صَالح وسط',NULL,'59',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (16,'٢٧ - وَسَأَلت عليا عَن نصر بن طريف فَقَالَ ضَعِيف ضَعِيف لَا يكْتب حَدِيثه
٢٨ - وَسَأَلت عليا عَن أَيُّوب بن خوط فَقَالَ ضَعِيف',NULL,'60',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (17,'ضَعِيف لَا يكْتب حَدِيثه
٢٩ - وَسَأَلت عليا عَن الْقَاسِم بن الْفضل الْحدانِي فَقَالَ هُوَ عندنَا ثقه
٣٠ - وَسَأَلت عليا عَن يزِيد بن ابراهيم التسترِي فَقَالَ هُوَ عندنَا ثقه',NULL,'61',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (18,'٣١ - وَسَأَلت عليا عَن عيينه بن عبد الرَّحْمَن فَقَالَ عيينه عندنَا ثقه
٣٢ - وَسَأَلت عليا عَن الْحسن بن أبي جَعْفَر فَقَالَ ضَعِيف ضَعِيف',NULL,'62',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (19,'٣٣ - وَسَأَلت عليا عَن عبد الْأَعْلَى بن أبي الْمسَاوِر فَقَالَ ضَعِيف لَيْسَ بِشَيْء
٣٤ - وَسَأَلت عليا عَن همام بن يحيى فَقَالَ ثِقَة ثبتا',NULL,'63',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (20,'٣٥ - وَسَأَلت عليا عَن مُحَمَّد بن ثَابت الْعَبْدي فَقَالَ هُوَ صَالح لَيْسَ بِالْقَوِيّ',NULL,'64',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (21,'٣٦ - وَسَأَلت عليا عَن أبي الْأَشْهب جَعْفَر بن حَيَّان فَقَالَ ثِقَة ثبتا
٣٧ - وَسَأَلت عليا عَن هِلَال بن أبي دَاوُد الحبطي فَقَالَ كَانَ ثِقَة
٣٨ - وَسَأَلت عليا عَن نوح بن قيس الْحدانِي فَقَالَ هُوَ صَالح وَلَيْسَ بِالْقَوِيّ',NULL,'65',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (22,'٣٩ - وَسَأَلت عليا عَن خَارِجَة بن مُصعب فَقَالَ هُوَ عندنَا ضَعِيف
٤٠ - وَسَأَلت عليا عَن أبي حتروش فَقَالَ اسْمه شَمله بن هزال وَهُوَ عندنَا ضَعِيف',NULL,'66',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (23,'٤١ - وَسَأَلت عليا عَن آدم بن الحكم الْبَصْرِيّ فَقَالَ ضَعِيف ضَعِيف
٤٢ - وَسَأَلت عليا عَن سُلَيْمَان بن أبي سُلَيْمَان القافلاني فَقَالَ ضَعِيفا ضَعِيفا لَيْسَ بِشَيْء',NULL,'67',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (24,'٤٣ - وَسَأَلت عليا عَن أبي أُمِّيّه بن يعلى فَقَالَ ضَعِيف
٤٤ - وَسَأَلت عليا عَن عدي بن الْمفضل فَقَالَ كَانَ ضَعِيفا
٤٥ - وَسَأَلت عليا عَن عَمْرو بن فَايِد الْقرشِي الأسواري',NULL,'68',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (25,'فَقَالَ كَانَ ذَلِك عندنَا ضَعِيف وَكَانَ يَقُول بِالْقدرِ
٤٦ - وَسَأَلت عليا عَن عمرَان بن زيد التغلبي فَقَالَ كَانَ عندنَا ثِقَة ثبتا
٤٧ - وَسَأَلت عليا عَن عَوْف الْأَعرَابِي فَقَالَ ثِقَة ثَبت',NULL,'69',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (26,'٤٨ - وَسَأَلت عَن كهمس بن الْحسن فَقَالَ ثِقَة ثَبت
٤٩ - وَسَأَلت عليا عَن أبي عبيده النَّاجِي فَقَالَ قد',NULL,'70',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (27,'روى عَن الْحسن وَكَانَ عندنَا ثِقَة
٥٠ - وَسَأَلت عليا عَن أبان بن يزِيد الْعَطَّار فَقَالَ كَانَ عندنَا ثِقَة',NULL,'71',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (28,'٥١ - وَسَأَلت عليا عَن سَلام بن مِسْكين فَقيل ثِقَة من أهل الأمانه
٥٢ - وَسَأَلت عليا عَن السّري بن يحيى فَقَالَ كَانَ ثِقَة ثبتا',NULL,'72',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (29,'٥٣ - وَسَأَلت عليا عَن عُثْمَان الْبري فَقَالَ كَانَ ضَعِيفا لَيْسَ بِشَيْء
٥٤ - وَسَأَلت عليا عَن بسطَام بن مُسلم فَقَالَ كَانَ عندنَا ثِقَة',NULL,'73',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (30,'٥٥ - وَسَأَلت عليا عَن عبد الحميد بن بهْرَام فَقَالَ كَانَ عندنَا ثِقَة انما كَانَ يروي عَن شهر بن حَوْشَب من كتاب كَانَ عِنْده
٥٦ - وَسَأَلت عليا عَن أبي مودود فَقَالَ كَانَ صَالحا لَا بَأْس بِهِ',NULL,'74',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (31,'٥٧ - حَدثنَا مُحَمَّد بن عُثْمَان قَالَ سَمِعت عليا وَسُئِلَ عَن أبي عبد الله الشقري فَقَالَ كَانَ ثِقَة عندنَا واسْمه سلمه بن تَمام
٥٨ - وَسمعت عليا وَذكر عَمْرو بن عبيد فَقَالَ لَيْسَ بِشَيْء وَلَا نرى الرِّوَايَة عَنهُ',NULL,'75',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (32,'٥٩ - وَسُئِلَ عَن عَليّ الصَّلْت بن دِينَار الْبَصْرِيّ فَقَالَ كَانَ ذَاك ضَعِيفا عندنَا وكنيته أَبُو شُعَيْب
٦٠ - وَسمعت عليا وَسُئِلَ عَن هِشَام أبي الْمِقْدَام فَقَالَ كَانَ ضَعِيفا
٦١ - وَسمعت عليا وَسُئِلَ عَن أبي ريحانه الَّذِي يروي عَن',NULL,'76',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (33,'سفينة فَقَالَ هُوَ صَالح وسط لَيْسَ بِهِ بَأْس
٦٢ - وَسُئِلَ عَليّ عَن ابراهيم بن عبد الْملك الْبَصْرِيّ وَأَنا أسمع فَقَالَ كَانَ ذَلِك شَيخا ضَعِيفا لَيْسَ بِشَيْء
٦٣ - وَسُئِلَ عَليّ عَن الْفضل بن مَيْمُون وَأَنا أسمع فَقَالَ لم يزل عندنَا ضَعِيفا ضَعِيفا
٦٤ - وَسَأَلَ عَليّ وَأَنا أسمع عَن أبي عقيل يحيى بن المتَوَكل',NULL,'77',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (34,'فَقَالَ ذَاك عندنَا ضَعِيف وَكَانَ منزله بِبَغْدَاد
٦٥ - وَسُئِلَ عَليّ وَأَنا أسمع عَن مُطِيع بن مَيْمُون فَقَالَ ذَاك شيخ عندنَا ثِقَة
٦٦ - وَسُئِلَ عَن سكين بن عبد الْعَزِيز فَقَالَ كَانَ شَيخا لَا بَأْس بِهِ
٦٧ - وَسمعت عليا وَسُئِلَ عَن حَمَّاد بن شُعَيْب فَقَالَ لم يزل حَمَّاد عندنَا ضَعِيفا لَيْسَ بِالْقَوِيّ',NULL,'78',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (35,'٦٨ - وَسَأَلت عليا عَن حشرج بن نَبَاته فَقَالَ كَانَ عندنَا حشرج ثِقَة
٦٩ - وَسمعت عليا يَقُول زَكَرِيَّا بن يحيى الذِّرَاع ثِقَة',NULL,'79',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (36,'٧٠ - وَسمعت عليا وَذكر عمَارَة بن زَاذَان الصيدلاني فَقَالَ قد رُوِيَ عَنهُ يحيى أَحَادِيث
٧١ - وَسمعت عليا يَقُول مُحَمَّد بن جحاده كَانَ يتهم بِشَيْء من الْقدر',NULL,'80',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (37,'٧٢ - سَمِعت عليا يَقُول أَبُو عوانه بَاعَ كِتَابه
٧٣ - سَمِعت عليا يَقُول كنت اذا قدمت الى بَغْدَاد مُنْذُ أَرْبَعِينَ سنه كَانَ الَّذِي يذاكر أَحْمد بن حَنْبَل فَرُبمَا اخْتَلَفْنَا فِي الشَّيْء فنسأل أَبَا زَكَرِيَّا يحيى بن معِين فَيقوم فيخرجه',NULL,'81',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (38,'مَا كَانَ أعرفهُ بِموضع حَدِيثه
٧٤ - وَسمعت عليا يَقُول أَصْحَاب أبي هريره هَؤُلَاءِ السته سعيد بن الْمسيب وَأَبُو سلمه والأعرج وَأَبُو صَالح وَمُحَمّد بن سِيرِين وَطَاوُس وَكَانَ همام ابْن مُنَبّه يشبه حَدِيثه حَدِيثهمْ الا أحرف وَحدثنَا مُحَمَّد بن عُثْمَان قَالَ سَمِعت عليا يَقُول قلت لعبد الرَّحْمَن بن',NULL,'82',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (39,'مهْدي وَأَنا عِنْده وَقد أخرج إِلَيّ كتاب همام فَقلت لَهُ هَات أنظر فِيهِ فَقَالَ دَعْنِي حَتَّى أملي عليا فاذا فرغت مِنْهَا فَانْظُر فِيهَا فأملا عليا ثمَّ رمي بِالْكتاب الي فَلم أجد فِيهِ شَيْئا الا أَحَادِيث معروفه وَلم أكتب مِنْهَا شَيْئا فَقَالَ أَترَانِي كنت أترك مِنْهَا شَيْئا مِمَّا تريده
٧٥ - وَسمعت عليا يَقُول مينا مولى حميد بن عبد الرَّحْمَن لَيْسَ بِشَيْء
٧٦ - وَسمعت عليا يَقُول نَظرنَا فَإِذا يحيى بن سعيد يروي',NULL,'83',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (40,'عَن سعيد بن الْمسيب مَا لَيْسَ يرْوى أحد مثلهَا ونظرنا فاذا الزُّهْرِيّ يروي عَن سعيد بن الْمسيب شَيْئا لم يروه أحد ونظرنا فاذا قتاده يروي عَن سعيد بن الْمسيب شَيْئا لم يروه أحد
٧٧ - وَسمعت عليا يَقُول قَالَ أبي عبد الله بن جَعْفَر قَالَ شُعْبَة مَاتَت أُمِّي فجائني التَّيْمِيّ وَابْن عون',NULL,'84',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (41,'فَحدث التَّيْمِيّ عَن أبي نضره بِحَدِيث فَقَالَ ابْن عون قد رَأَيْت أَبَا نضره فَقَالَ لَهُ التَّيْمِيّ رَأَيْت أَبَا نضره
٧٨ - قد سَمِعت عليا يَقُول أَنا لَا أحفظ عَن خَالِد عَن سعيد ابْن أبي الْحسن الا هَذَا الحَدِيث يَعْنِي حَدِيث أم سلمه تقتل عمار الفئه الباغيه',NULL,'85',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (42,'٧٩ - وَسَأَلت عليا عَن مَشَايِخ من أهل الْحجاز وَغَيرهم سَأَلته عَن صَالح مولى التوأمه فَقَالَ صَالح ثقه الا انه خرف وَكبر فَسمع مِنْهُ قوم وَهُوَ خرف كَبِير فَكَانَ سماعهم',NULL,'86',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (43,'لَيْسَ بِصَحِيح سُفْيَان الثَّوْريّ من سمع مِنْهُ بعد مَا خرف وَكَانَ ابْن أبي ذِئْب قد سمع مِنْهُ قبل ان يخرف
٨٠ - وَسَأَلت عليا عَن أبي الزبير الْمَكِّيّ فَقَالَ ثقه ثَبت واسْمه مُحَمَّد بن مُسلم وَكَانَ مولى ابْن حزَام',NULL,'87',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (44,'٨١ - وَسَأَلت عليا عَن عبد الله بن مُحَمَّد بن عقيل فَقَالَ كَانَ ضَعِيفا
٨٢ - وَسَأَلت عليا عَن حُسَيْن بن عبد الله بن عبيد الله بن عَبَّاس فَقَالَ كَانَ ضَعِيفا لَيْسَ بِشَيْء',NULL,'88',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (45,'٨٣ - وَسَأَلت عليا عَن مُحَمَّد بن اسحاق بن يسَار مولى آل مخرمه فَقَالَ هُوَ صَالح وسط',NULL,'89',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (46,'٨٤ - وَسَأَلت عليا عَن كثير بن عبد الله الْمُزنِيّ فَقَالَ ضَعِيف
٨٥ - وَسَأَلت عليا عَن حَمْزَة بن أبي حمزه الْمدنِي فَقَالَ كَانَ ضَعِيفا',NULL,'90',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (47,'٨٦ - وَسَأَلت عليا عَن صَالح بن مُحَمَّد بن زائده الْمدنِي فَقَالَ كَانَ ضَعِيفا
٨٧ - وَسَأَلت عليا عَن أبي حزرة فَقَالَ اسْم أبي حزرة يَعْقُوب بن مُجَاهِد وَكَانَ عندنَا ثقه وَكَانَ قَاصا بالمدينه
٨٨ - وَسَأَلت عليا عَن مُسلم الْخياط فَقَالَ كَانَ عندنَا ثقه',NULL,'91',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (48,'٨٩ - وَسَأَلت عليا عَن الْقَاسِم بن الْعَبَّاس فَقَالَ كَانَ ثِقَة
٩٠ - وَسَأَلت عليا عَن أبي الْحُوَيْرِث فَقَالَ كَانَ عندنَا ثقه قد روى عَنهُ الثَّوْريّ وَشعْبَة وسُفْيَان بن عيينه وَكَانَ شُعْبَة يَقُول أَبُو الحويرثه',NULL,'92',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (49,'٩١ - وَسَأَلت عليا عَن سعد بن ابراهيم بن عبد الرَّحْمَن بن عَوْف فَقَالَ كَانَ أَصْحَابنَا يرمونه بِالْقدرِ وَكَانَ عندنَا ثِقَة ثبتا وَكَانَ مَالك بن أنس يتَكَلَّم فِيهِ وَكَانَ لَا يروي عَنهُ مَالك شَيْئا وَكَانَ سعد قد طعن على مَالك فِي نسبه
٩٢ - وَسَأَلت عليا عَن عبد السَّلَام مولى قُرَيْش فَقَالَ كَانَ عندنَا ثِقَة',NULL,'93',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (50,'٩٣ - وَسَأَلت عليا عَن مُوسَى بن عقبه فَقَالَ ثقه ثَبت
٩٤ - وَسَأَلت عليا عَن مُحَمَّد بن عَمْرو بن علقمه فَقَالَ كَانَ ثقه وَكَانَ يحيى بن سعيد يُضعفهُ بعض الضعْف',NULL,'94',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (51,'٩٥ - وَسَأَلت عليا عَن أبي جَابر البياضي فَقَالَ لَيْسَ عندنَا من أهل الثقه
٩٦ - وَسَأَلت عليا عَن مُوسَى بن مُحَمَّد بن ابراهيم فَقَالَ كَانَ ضَعِيفا ضَعِيفا ضَعِيفا
٩٧ - وَسَأَلت عليا عَن كثير بن زيد فَقَالَ هُوَ صَالح وَلَيْسَ بِالْقَوِيّ',NULL,'95',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (52,'٩٨ - وَسَأَلت عليا عَن أسَامَه بن زيد بن أسلم فَقَالَ هُوَ عندنَا ثقه وَلَيْسَ بِالْقَوِيّ وَقد أنكر أَصْحَابنَا عَلَيْهِ أَحَادِيث وَبَنُو زيد بن أسلم مِنْهُم عبد الرَّحْمَن وَغَيره وَبَنُو زيد كلهم لَيْسَ بالأقوياء
٩٩ - وَسَأَلته عَن عبد الله بن أبي نجيح الْمَكِّيّ فَقَالَ أما',NULL,'96',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (53,'الحَدِيث فَهُوَ فِيهِ ثقه وَأما الرَّأْي فَكَانَ قدريا مُعْتَزِلا
١٠٠ - وَسَأَلت عليا عَن حَنْظَلَة بن أبي سُفْيَان فَقَالَ كَانَ حَنْظَلَة وَأَخُوهُ عَمْرو بن أبي سُفْيَان مكيين من بني جمح وَكَانَا ثقتين',NULL,'97',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (54,'١٠١ - وَسَأَلت عليا عَن عبيد الله بن الْوَلِيد (الْوَصَّافِي) فَقَالَ كَانَ من بني عجل وَكَانَ ضَعِيفا لَيْسَ بِشَيْء
١٠٢ - وَسَأَلته عَن مُوسَى بن ابراهيم الْمدنِي الَّذِي كَانَ يروي عَن سلمه بن الْأَكْوَع فَقَالَ كَانَ صَالحا وسطا
١٠٣ - وَسَأَلت عليا عَن أُسَامَة بن زيد اللَّيْثِيّ فَقَالَ ذَاك كَانَ عندنَا ثقه',NULL,'98',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (55,'١٠٤ - وَسَأَلت عليا عَن مُوسَى بن عَليّ بن رَبَاح اللَّخْمِيّ فَقَالَ كَانَ عندنَا ثِقَة ثبتا
١٠٥ - وَسَأَلت عليا عَن عبد الحميد بن جَعْفَر الْأنْصَارِيّ',NULL,'99',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (56,'فَقَالَ كَانَ يَقُول بِالْقدرِ وَكَانَ عندنَا ثِقَة وَكَانَ سُفْيَان الثَّوْريّ يُضعفهُ
١٠٦ - وَسَأَلت عليا عَن أبي معشر الْمدنِي فَقَالَ كَانَ ذَلِك شَيخا ضَعِيفا ضَعِيفا وَكَانَ يحدث عَن مُحَمَّد بن قيس وَيحدث عَن مُحَمَّد بن كَعْب بِأَحَادِيث صَالحه وَكَانَ يحدث',NULL,'100',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (57,'عَن المَقْبُري وَعَن نَافِع بِأَحَادِيث منكره
١٠٧ - وَسَأَلته عَن سُلَيْمَان بن بِلَال فَقَالَ كَانَ عندنَا ثِقَة واليا على السُّوق
١٠٨ - وَسَأَلته عَن أبي غَسَّان مُحَمَّد بن مطرف فَقَالَ كَانَ شَيخا وسطا صَالحا',NULL,'101',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (58,'١٠٩ - وَسَأَلته عَن هِشَام بن سعد فَقَالَ هُوَ صَالح وَلم يكن بِالْقَوِيّ
١١٠ - وَسَأَلت عليا عَن مُحَمَّد بن عبد الرَّحْمَن مولى آل طلحه فَقَالَ كَانَ عندنَا ثِقَة وَقد أنْكرت عَلَيْهِ أَحَادِيث
١١١ - وَسَأَلت عليا عَن عبد الله بن مُسلم بن هُرْمُز فَقَالَ كَانَ ضَعِيفا ضَعِيفا عندنَا وَهُوَ مكي',NULL,'102',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (59,'١١٢ - وَسَأَلت عليا عَن عبد الْعَزِيز بن عمر بن عبد الْعَزِيز فَقَالَ كَانَ عبد الْعَزِيز ثبتا ثِقَة وَقد روى عَنهُ الثِّقَات ابْن أبي ذُؤَيْب وَغَيره
١١٣ - وَحدثنَا مُحَمَّد بن عُثْمَان قَالَ سَمِعت عليا على الْمِنْبَر يَقُول من زعم أَن الْقُرْآن مَخْلُوق فقد كفر وَمن زعم أَن الله عز وَجل لَا يرى فَهُوَ كَافِر وَمن زعم أَن الله عز وَجل لم يكلم مُوسَى على الحقيقه فَهُوَ كَافِر
١١٤ - حَدثنَا مُحَمَّد قَالَ سَأَلت عليا عَن عمر بن عبد الرَّحْمَن بن دلاف فَقَالَ كَانَ ثقه',NULL,'103',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (60,'١١٥ - وَسَأَلت عليا عَن طَلْحَة بن عبد الْملك الْأَيْلِي فَقَالَ قد روى عَنهُ مَالك بن أنس وَكَانَ عندنَا ثِقَة ثبتا
١١٦ - وَسَأَلت عليا عَن عَمْرو بن شُعَيْب فَقَالَ مَا روى عَنهُ أَيُّوب وَابْن جريج فَذَلِك كُله صَحِيح وَمَا روى عَمْرو عَن أَبِيه عَن جده فَذَلِك كتاب وجده فَهُوَ ضَعِيف',NULL,'104',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (61,'١١٧ - وَسَأَلت عليا أبي صَالح الزيات فَقَالَ ذَاك روى',NULL,'105',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (62,'عَنهُ الْأَعْمَش وَمَنْصُور وَكَانَ عندنَا ثِقَة ثبتا وَكَانَ اسْمه ذكْوَان
١١٨ - وَأَبُو صَالح الَّذِي روى عَنهُ اسماعيل بن أبي خَالِد التَّفْسِير اسْمه باذام وَكَانَ مولى أم هاني بنت أبي طَالب لَيْسَ بِذَاكَ ضَعِيف',NULL,'106',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (63,'١١٩ - وَسَأَلته عَن أبي صَالح الْحَنَفِيّ فَقَالَ ثَبت ثَبت
١٢٠ - وَسَأَلته عَن أبي صَالح مولى ضباعه فَقَالَ كَانَ ثبتا وَكَانَ من التَّابِعين وَهُوَ الَّذِي يروي عَنهُ أهل الكوفه',NULL,'107',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (64,'١٢١ - وَسَأَلته عَن أبي صَالح زهرَة بن معبد مولى عُثْمَان بن عَفَّان فَقَالَ كَانَ زهرَة ثِقَة ثبتا وَقد حدث عَن عُثْمَان رضوَان الله عَلَيْهِ قَالَ السَّقطِي قَالَ عَليّ بن عمر الدَّارَقُطْنِيّ هَكَذَا قَالَ ابْن أبي شيبَة عَن عَليّ وَهُوَ عِنْدِي وهم لِأَن زهرَة بن معبد كَانَ يكنى أَبَا',NULL,'108',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (65,'عقيل وَلم يلق عُثْمَان بن عَفَّان وَلَا روى عَنهُ وَلَا أدْركهُ وانما يروي عَن أبي صَالح مولي عُثْمَان بن عَفَّان وَيُقَال ان اسْم ابي صَالح الَّذِي يروي عَنهُ زهرَة الْحَارِث مولى عُثْمَان يروي عَن عُثْمَان وَعَن أبي هريره وَالله أعلم
١٢٢ - وَحدثنَا مُحَمَّد بن عُثْمَان قَالَ وَسَأَلت عليا عَن أبي صَالح',NULL,'109',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (66,'الَّذِي روى عَنهُ سُلَيْمَان التَّيْمِيّ فَقَالَ كَانَ عندنَا ثبتا وَهُوَ تَابِعِيّ اسْمه ميزَان
١٢٣ - وَسَأَلته عَن أبي صَالح الَّذِي روى عَنهُ يحيى بن أبي كثير فَقَالَ كَانَ ثِقَة ثبتا وَكَانَ يُسمى قيلولة وَكَانَ من التَّابِعين
١٢٤ - وَسَأَلته عَن أبي صَالح صَاحب الْكَلْبِيّ وَقد روى عَنهُ سماك فَقَالَ ذَاك مولى أم هاني وَهُوَ ضَعِيف وَكَانَ من التَّابِعين قَالَ عَليّ وَكَانَ لأبي صَالح ثَلَاثَة بَنِينَ',NULL,'110',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (67,'كلهم ثِقَة سهل بن أبي صَالح وَعباد بن أبي صَالح وَصَالح بن أبي صَالح كلهم ثِقَة ثَبت
١٢٥ - وَسَأَلت عليا عَن عبد الْوَهَّاب بن مُجَاهِد فَقَالَ غير ثقه وَلَا يكْتب حَدِيثه
١٢٦ - وَسَأَلت عليا عَن عبد الرَّحْمَن بن اسحاق الْمدنِي',NULL,'111',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (68,'فَقَالَ هُوَ عندنَا صَالح وسط وَكَانَ يحيى بن سعيد يُضعفهُ
١٢٧ - وَسَأَلت عليا عَن طَلْحَة بن عَمْرو فَقَالَ هُوَ مكي ضَعِيف لَيْسَ بِشَيْء',NULL,'112',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (69,'١٢٨ - وَسَأَلت عليا عَن الْقَاسِم بن عبد الله الْعمريّ فَقَالَ ذَاك ضَعِيف عندنَا لَيْسَ بِشَيْء
١٢٩ - وَسَأَلت عليا عَن حُسَيْن بن زيد بن عَليّ فَقَالَ كَانَ فِيهِ ضعف وَيكْتب حَدِيثه قَالَ أَبُو جَعْفَر حَدثنَا عَليّ عَنهُ على الْمِنْبَر يَعْنِي حُسَيْنًا
١٣٠ - وَسَأَلت عليا عَن أبي عَامر الخزاز فَقَالَ عَليّ كَانَ',NULL,'113',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (70,'يحدث النَّاس عَن ابْن أبي مليكه وَكَانَ ضَعِيفا لَيْسَ بِشَيْء
١٣١ - وَسَأَلت عليا عَن مُسلم بن خَالِد الزنْجِي فَقَالَ كَانَ عندنَا ضَعِيفا لَيْسَ بِالْقَوِيّ
١٣٢ - وَسَأَلت عليا عَن عمر بن قيس الْمَكِّيّ فَقَالَ كَانَ',NULL,'114',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (71,'ضَعِيفا ضَعِيفا لَيْسَ بِشَيْء كَانُوا يلقبونه بسندلا
١٣٣ - وَسَأَلت عليا عَن سعيد بن سَالم القداح فَقَالَ كَانَ ثِقَة وَلم يكن بِالْقَوِيّ
١٣٤ - وَسَأَلت عليا عَن مُحَمَّد بن عبد الرَّحْمَن بن الْمُغِيرَهْ بن أبي ذِئْب فَقَالَ كَانَ عندنَا ثِقَة وَكَانُوا يوهنونه فِي أَشْيَاء رَوَاهَا عَن الزُّهْرِيّ',NULL,'115',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (72,'١٣٥ - وَسَأَلت عليا عَن حَكِيم بن رُزَيْق فَقَالَ كَانَ ثِقَة ثبتا
١٣٦ - وَسَأَلت عليا عَن هَارُون فَقَالَ كَانَ شَيخا لَا بَأْس بِهِ روى عَن سعيد بن الْمسيب وَقد روى عَنهُ الْكُوفِيُّونَ',NULL,'116',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (73,'١٣٧ - وَسَأَلت عليا عَن فليح بن سُلَيْمَان فَقَالَ كَانَ فليح وَأَخُوهُ عبد الحميد ضعيفين
١٣٨ - وَسَأَلت عليا عَن عبد الله بن عَامر الْأَسْلَمِيّ فَقَالَ ذَاك عندنَا ضَعِيف ضَعِيف',NULL,'117',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (74,'١٣٩ - وَسَأَلت عليا عَن اسماعيل بن ابراهيم بن عقبه فَقَالَ كَانَ ذَاك من أهل المدينه وَكَانَ عندنَا ثِقَة
١٤٠ - وَسَأَلت عليا عَن حَاتِم بن اسماعيل فَقَالَ كَانَ حَاتِم عندنَا ثِقَة ثبتا
١٤١ - وَسَأَلت عليا عَن أنس بن عِيَاض فَقَالَ كَانَ من أهل المدينه وَكَانَ عندنَا ثِقَة',NULL,'118',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (75,'١٤٢ - وَسمعت عليا يَقُول بَنو أبي محذوره الَّذين يحدثُونَ عَن جدهم كلهم ضَعِيف لَيْسَ بِشَيْء
١٤٣ - قلت لعَلي يَا أَبَا الْحسن مَا تَقول فِي أَيُّوب بن سيار فَقَالَ كَانَ ذَاك عندنَا غير ثقه لَا يكْتب حَدِيثه',NULL,'119',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (76,'١٤٤ - وَسَأَلت عليا عَن مَرْوَان بن مُعَاوِيه فَقَالَ كَانَ يوثق وَكَانَ يروي عَن قوم لَيْسُوا ثِقَات ويكنى عَن أَسْمَاءَهُم
١٤٥ - وَسَأَلت عليا عَن مُوسَى بن عبيده الربذي فَقَالَ كَانَ ضَعِيفا ضَعِيفا كَانَ يحيى الْقطَّان لَا يرى أَن يكْتب حَدِيثه',NULL,'120',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (77,'١٤٦ - وَسَأَلت عليا عَن يُونُس بن الْحَارِث الطَّائِفِي فَقَالَ كُنَّا نضعف ذَاك ضعفا شَدِيدا
١٤٧ - وَسمعت عليا يَقُول كَانَ ابراهيم بن أدهم ثِقَة وَكَانَ من أعبد النَّاس',NULL,'121',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (78,'١٤٨ - وَسمعت عليا يَقُول كَانَ أَبُو جَعْفَر الرَّازِيّ عندنَا ثِقَة
١٤٩ - وَسَأَلت عليا عَن مُحَمَّد بن الْوَلِيد الزبيدِيّ فَقَالَ كَانَ عندنَا ثِقَة ثبتا',NULL,'122',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (79,'١٥٠ - وَسمعت عليا يَقُول ابْن أخي الزُّهْرِيّ ضَعِيف لَيْسَ بِالْقَوِيّ وَنحن نكتب حَدِيثه
١٥١ - وَسمعت عليا يَقُول الْوَلِيد بن مُحَمَّد الموقري ضَعِيف لَيْسَ بِشَيْء وَكَانَ قد روى عَن الزُّهْرِيّ وَلَا نروي عَنهُ شَيْئا',NULL,'123',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (80,'١٥٢ - وَسمعت عليا يَقُول زِيَاد بن سعد ثِقَة ثَبت
١٥٣ - وَسمعت عليا يَقُول ابراهيم بن أبي يحيى كَذَّاب وَكَانَ يَقُول بِالْقدرِ',NULL,'124',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (81,'١٥٤ - وَكَانَ أَخُوهُ أنيس بن أبي يحيى عندنَا ثِقَة
١٥٥ - وسمعته وَسُئِلَ عَن شبْل بن عباد الْمَكِّيّ فَقَالَ وسط وَلم يكن بِهِ بَأْس
١٥٦ - حَدثنَا مُحَمَّد بن عُثْمَان بن أبي شيبَة فَقَالَ سَمِعت عليا وَسُئِلَ بن عباد بن كثير فَقَالَ لم يكن بِشَيْء وَهُوَ',NULL,'125',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (82,'من أهل البصره الا أَنه كَانَ ينزل مكه
١٥٧ - وَلَكِن عباد بن كثير الرَّمْلِيّ كَانَ ثِقَة لَا بَأْس بِهِ
١٥٨ - وَسمعت عليا وَسُئِلَ عَن حارثه بن أبي الرِّجَال فَقَالَ لم يزل أَصْحَابنَا يضعفونه',NULL,'126',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (83,'١٥٩ - وَكَانَ أَخُوهُ عبد الرَّحْمَن بن أبي الرِّجَال أصلح مِنْهُ فِي الحَدِيث وَكَانَ ينزل الشَّام
١٦٠ - وَسمعت عليا وَسُئِلَ عَن عبد الْعَزِيز الدَّرَاورْدِي فَقَالَ هُوَ عندنَا ثِقَة ثَبت',NULL,'127',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (84,'١٦١ - وَسمعت عليا وَسُئِلَ عَن يزِيد بن عِيَاض بن جعديه فَقَالَ ضَعِيف ضَعِيف لَيْسَ بِشَيْء
١٦٢ - وَسمعت عليا وَسُئِلَ عَن ابْن أبي سبره فَقَالَ كَانَ',NULL,'128',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (85,'ضَعِيفا فِي الحَدِيث وَكَانَ ابْن جريج أَخذ مِنْهُ مناولة',NULL,'129',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (86,'١٦٣ - وَسمعت عليا وَسُئِلَ عَن سعيد بن سمْعَان فَقَالَ قد روى عَنهُ ابْن أبي ذِئْب وَهُوَ عندنَا ثِقَة
١٦٤ - وَسمعت عليا وَذكر ابْن أبي حَازِم فَقَالَ كَانَ حَاتِم بن اسماعيل يطعن عَلَيْهِ فِي أَحَادِيث حدث بهَا عَن أَبِيه',NULL,'130',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (87,'وَحدثنَا مُحَمَّد بن عُثْمَان قَالَ سَمِعت أبي يَقُول أردْت السماع من عبد الْعَزِيز بن أبي حَازِم فَجِئْنَا الى حَاتِم وَهُوَ فِي دكانه فَسَأَلْنَاهُ عَنهُ فَقَالَ قد روى عَن أَبِيه أَحَادِيث نهيناه عَنْهَا فَلم ينْتَه قَالَ فَلم نكتب عَنهُ
١٦٥ - حَدثنَا مُحَمَّد قَالَ وَسمعت عليا وَذكر لَهُ عبد الرَّحْمَن بن أبي الزِّنَاد فَقَالَ كَانَ عِنْد أَصْحَابنَا ضَعِيفا',NULL,'131',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (88,'١٦٦ - وَسمعت عليا وَسُئِلَ عَن يَعْقُوب بن مُحَمَّد بن طحلاء فَقَالَ كَانَ صَالحا وَكَانَ من أهل المدينه
١٦٧ - وَسمعت عليا وَسُئِلَ عَن عبد الْوَهَّاب بن الْورْد فَقَالَ كَانَ عابدا وَكَانَ من اهل مكه وَكَانَ أَصْحَابنَا يوثقونه
١٦٨ - وَسُئِلَ عَليّ وَأَنا أسمع عَن عبد الله بن زِيَاد بن سمْعَان فَقَالَ ذَلِك عندنَا ضَعِيف ضَعِيف',NULL,'132',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (89,'١٦٩ - وَسمعت عليا وَسُئِلَ عَن عِكْرِمَهْ بن عمار فَقَالَ كَانَ عِنْد أَصْحَابنَا ثِقَة ثَبت
١٧٠ - وَسمعت عليا وَسُئِلَ عَن أَيُّوب بن عَتبه اليمامي فَقَالَ عِنْد أَصْحَابنَا ضَعِيفا',NULL,'133',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (90,'١٧١ - وَسمعت عليا وَسُئِلَ عَن الحكم بن عبد الله الْأَيْلِي فَقَالَ كَانَ ضَعِيفا لَيْسَ بِشَيْء',NULL,'134',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (91,'١٧٢ - وَعَن عبد الْجَبَّار بن عمر الْأَيْلِي فَقَالَ لم يكن بِشَيْء
١٧٣ - وَعَن أبي أويس الْمدنِي فَقَالَ كَانَ عِنْد أَصْحَابنَا ضَعِيفا',NULL,'135',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (92,'١٧٤ - وَعَن مُحَمَّد بن مُسلم الطَّائِفِي فَقَالَ كَانَ صَالحا وسطا
١٧٥ - وَعَن عطاف بن خَالِد المَخْزُومِي فَقَالَ كَانَ عندنَا وَعند أَصْحَابنَا ثِقَة',NULL,'136',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (93,'١٧٦ - وَسمعت عليا يَقُول اسماعيل بن جَعْفَر وَأَخُوهُ مُحَمَّد بن جَعْفَر المدنيان ثقتان
١٧٧ - وَسمعت عليا يَقُول رميس بن مُحَمَّد الْمَكِّيّ من أهل خُرَاسَان صَالح فِي الحَدِيث لَا بَأْس بِهِ
١٧٨ - وَسمعت عليا وَسُئِلَ عَن منكدر بن مُحَمَّد بن الْمُنْكَدر فَقَالَ هُوَ عندنَا صَالح وَلَيْسَ بِالْقَوِيّ',NULL,'137',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (94,'١٧٩ - وَعَن حبيب بن عبد الرَّحْمَن بن أردك فَقَالَ كَانَ ضَعِيفا
١٨٠ - وَعَن عبد الْملك بن قدامه الجُمَحِي فَقَالَ لَيْسَ بِهِ بَأْس',NULL,'138',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (95,'١٨١ - وَكَانَ أَخُوهُ صَالح بن قدامه ايضا لَا بَأْس بِهِ
١٨٢ - وَعَن عبد الله بن سعد بن أبي هِنْد فَقَالَ كَانَ عِنْد أَصْحَابنَا ثِقَة
١٨٣ - وَأما عبد الله بن سعيد المَقْبُري فَلم يكن بِشَيْء',NULL,'139',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (96,'١٨٤ - وَحدثنَا مُحَمَّد بن عُثْمَان فَقَالَ سُئِلَ عَليّ وَأَنا حَاضر عَن ابراهيم بن أبي النَّضر الْمَدِينِيّ فَقَالَ كَانَ لقبه بردَان وَكَانَ عِنْد أَصْحَابنَا ثِقَة
١٨٥ - وَسمعت عليا وَسُئِلَ عَن أبي مودود الْمَدِينِيّ فَقَالَ صَالحا لَا بَأْس بِهِ',NULL,'140',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (97,'١٨٦ - سَمِعت عليا وَذكر نَافِع بن أبي نعيم الْقَارئ فَقَالَ كَانَ عندنَا لَا بَأْس بِهِ
١٨٧ - سَمِعت عليا وَسُئِلَ عَن حَمَّاد الْخياط فَقَالَ كَانَ عندنَا ثِقَة وَكَانَ من أهل المدينه',NULL,'141',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (98,'١٨٨ - وَعَن مُوسَى بن أبي الْفُرَات فَقَالَ كَانَ عندنَا ثِقَة
١٨٩ - وَعَن اسحاق بن يحيى بن طلحه بن عبيد الله فَقَالَ كَانَ ضَعِيفا لَيْسَ بِشَيْء
١٩٠ - وَعَن عمر بن صهْبَان الْمدنِي فَقَالَ كَانَ ضَعِيفا لَا يكْتب حَدِيثه وَلَيْسَ بِشَيْء',NULL,'142',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (99,'١٩١ - سَمِعت عليا يَقُول عبد الله بن مُسلم بن هرمزا الْمَكِّيّ ضَعِيف ضَعِيف
١٩٢ - وَسمعت عليا وَسُئِلَ عَن الْحَارِث بن عبد الرَّحْمَن الَّذِي حدث عَنهُ ابْن أبي ذِئْب فَقَالَ لَيْسَ هُوَ الْحَارِث بن أبي ذُبَاب',NULL,'143',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (100,'١٩٣ - سَمِعت عليا وَسُئِلَ عَن أبي عمر الصَّنْعَانِيّ فَقَالَ كُنَّا نوثقه وَكَانَ يطعن عَلَيْهِ فِي سَمعه انه كَانَ عرضا',NULL,'144',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (101,'١٩٤ - وَسمعت عليا وَسُئِلَ عَن عَاصِم بن سُلَيْمَان الْأَحول فَقَالَ كَانَ ثقه
١٩٥
- وَعَن أَيمن بن نابل فَقَالَ كَانَ ثِقَة وَلَيْسَ بِالْقَوِيّ',NULL,'145',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (102,'١٩٦ - وَعَن عِيسَى بن أبي عِيسَى الحناط فَقَالَ كَانَ ضَعِيفا وَلَيْسَ بِالْقَوِيّ
١٩٧ - وَعَن أبي سُفْيَان الَّذِي روى عَنهُ أَبُو مُحَمَّد الْأَعْمَش فَقَالَ اسْمه طلحه بن نَافِع وَكَانَ أَصْحَابنَا يضعفونه فِي حَدِيثه',NULL,'146',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (103,'وَعَن الْوَلِيد بن أبي الْوَلِيد فَقَالَ كَانَ صَالحا وسطا وَلم يحدث عَنهُ إِلَّا المصريون
١٩٩ - سَمِعت عليا يَقُول روح بن أسلم ذهب حَدِيثه يَعْنِي ضَاعَ',NULL,'147',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (104,'٢٠٠ - سَمِعت عليا يَقُول اسحاق بن ادريس لَيْسَ بِشَيْء
٢٠١ - سَمِعت عليا يَقُول يُونُس بن بكير قد كتبت عَنهُ وَلست احدث عَنهُ حَدثنَا مُحَمَّد قَالَ رَأَيْت وَكَانَ فِي كِتَابه الْبيُوع عَنهُ حَدِيث يَعْنِي يُونُس بن بكير فَقَالَ سلمُوا لابي عبد الرَّحْمَن نسخه يَعْنِي ابْن حسن يحدث عَنهُ حَدثنَا مُحَمَّد قَالَ قَالَ لي يحيى الْحمانِي لَا نستحل الرِّوَايَة عَنهُ',NULL,'148',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (105,'٢٠٢ - وَحدثنَا مُحَمَّد قَالَ سَمِعت عليا يَقُول أُمِّيّه بن شبْل مَا بحَديثه بَأْس
٢٠٣ - وَسمعت عليا يَقُول كَانَ عبد الرَّزَّاق أشبه بأصحاب الحَدِيث من هِشَام بن يُوسُف كَانَ عبد الرَّزَّاق يذاكر',NULL,'149',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (106,'٢٠٤ - وَسمعت عليا يَقُول حَدِيث سمعته عَن قتاده عَن أنس عَن النَّبِي صلى الله عَلَيْهِ وَسلم
يَا أَيهَا الرِّجَال لم يروه غير يزِيد بن هَارُون
٢٠٥ - وَسُئِلَ عَليّ عَن أبي بكر الداهري فَقَالَ لَيْسَ بِشَيْء لَا يكْتب حَدِيثه',NULL,'150',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (107,'٢٠٦ - وَعَن ابراهيم بن أوهم فَقَالَ كَانَ عندنَا ثِقَة من أعبد النَّاس
٢٠٧ - وَعَن ابراهيم بن أبي عبله فَقَالَ كَانَ أحد الثِّقَات
٢٠٨ - وَعَن مُحَمَّد بن زِيَاد الْأَلْهَانِي فَقَالَ ثِقَة مَأْمُون',NULL,'151',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (108,'٢٠٩ - وَعَن حريز بن عُثْمَان فَقَالَ لم يكن من أدركناه من أَصْحَابنَا يوثقونه',NULL,'152',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (109,'٢١٠ - وَسَأَلت عليا عَن الْقَاسِم أبي عبد الرَّحْمَن صَاحب أبي أَمَامه فَقَالَ كَانَ عندنَا عِنْد من أدركناه من أَصْحَابنَا ثِقَة
٢١١ - وَسُئِلَ عَليّ وَأَنا أسمع عَن زرْعَة بن ابراهيم فَقَالَ كَانَ يضعف وَلم يكن بِالْقَوِيّ
٢١٢ - وَسمعت عليا يَقُول عبد الْعَزِيز بن عبيد الله بن حمزه ابْن صُهَيْب اللَّخْمِيّ كَانَ ضَعِيفا',NULL,'153',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (110,'٢١٣ - وَحميد بن مَالك اللَّخْمِيّ كَانَ ضَعِيفا وَكَانَ اسماعيل بن عَيَّاش يحدث عَن هذَيْن الشخصين
٢١٤ - سَمِعت عليا ذكر عِنْده عَطاء بن عجلَان فَقَالَ كَانَ ذ ١ ك شَيخا ضَعِيفا لَيْسَ بِشَيْء
٢١٥ - وَسمعت عليا وَسُئِلَ عَن عَامر بن نجيح فَقَالَ كَانَ أَصْحَابنَا يوثقونه',NULL,'154',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (111,'٢١٦ - سَمِعت عليا يَقُول عِيسَى بن سِنَان
٢١٧ - وَسَعِيد بن سِنَان ضعيفان الشَّامي
٢١٨ - وَسَأَلت عليا عَن عَليّ بن يزِيد فَقَالَ كَانَ ضَعِيفا',NULL,'155',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (112,'وَسَأَلته عَن جَعْفَر بن الزبير فَقَالَ كَانَ جَعْفَر لَا يكْتب حَدِيثه ضَعِيفا لَا يُسَوِّي شَيْئا
٢٢٠ - سَمِعت عليا يسئل عَن عبد الرَّحْمَن بن زِيَاد بن أنعم فَقَالَ كَانَ أَصْحَابنَا يضعفونه وَأنكر أَصْحَابنَا أَحَادِيث كَانَ يحدث بهَا لَا تعرف',NULL,'156',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (113,'٢٢١ - سَمِعت عليا وَسُئِلَ عَن عبد الرَّحْمَن بن رُومَان فَقَالَ ذَلِك شيخ ضَعِيف
٢٢٢ - وَحدثنَا مُحَمَّد قَالَ سَمِعت أَبَا شُعَيْب الْحجام يسْأَل عليا عَن خُلَيْد بن دعْلج فَقَالَ كَانَ خُلَيْد ضَعِيفا
٢٢٣ - سَأَلت عليا عَن سعيد بن بشير فَقَالَ كَانَ ضَعِيفا',NULL,'157',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (114,'٢٢٤ - وَسَأَلت عليا عَن عُثْمَان بن عَطاء فَقَالَ كَانَ ضَعِيفا
٢٢٥ - وَعَن معَان بن رِفَاعَة فَقَالَ كَانَ شَيخا ضَعِيفا',NULL,'158',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (115,'٢٢٦ - وَعَن يزِيد بن عبد الْعَزِيز فَقَالَ كَانَ يضعف يزِيد هَذَا شَامي كَذَا قَالَ أَبُو جَعْفَر
٢٢٧ - وَسَأَلته عَن خَالِد بن يزِيد بن أبي مَالك فَقَالَ كَانَ ضَعِيفا
٢٢٨ - وَسَأَلته عَن عَتبه بن أبي حَكِيم الشَّيْبَانِيّ فَقَالَ كَانَ ضَعِيفا',NULL,'159',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (116,'٢٢٩ - وَعَن مُوسَى بن أَيُّوب الغافقي فَقَالَ كَانَ ثِقَة وَأَنا أنكر من أَحَادِيثه أَحَادِيث رَوَاهَا عَن عَمه فَكَانَ يرفعها
٢٣٠ - وَسَأَلت عليا عَن يحيى بن حَمْزَة الدِّمَشْقِي فَقَالَ كَانَ عِنْد أَصْحَابنَا ثِقَة
٢٣١ - وَعَن مُحَمَّد بن مهَاجر فَقَالَ كَانَ وسطا',NULL,'160',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (117,'٢٣٢ - وَعَن مُحَمَّد بن رَاشد فَقَالَ كَانَ ثِقَة
٢٣٣ - وَعَن اسماعيل بن عَيَّاش فَقَالَ كَانَ يوثق فِيمَا روى عَن أَصْحَابه أهل الشَّام فَأَما مَا روى عَن غير أهل الشَّام فَفِيهِ ضعف',NULL,'161',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (118,'٢٣٤ - وَعَن الْفرج بن فضاله فَقَالَ هُوَ وسط وَلَيْسَ لقوي
٢٣٥ - وَسَأَلته عَن شيخ حدث عَنهُ أَبُو اسماعيل الْمُؤَدب يُقَال',NULL,'162',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (119,'لَهُ سُلَيْمَان بن أبي هِنْد مولي زيد بن الْخطاب فَقَالَ سَأَلنَا عَن هَذَا الشبيخ فَذكر أَصْحَابنَا أَنه لم يكن بِهِ بَأْس
٢٣٦ - حَدثنَا مُحَمَّد قَالَ وَقَالَ عَليّ ذاكرني بعض أَصْحَابِي بِحَدِيث عَن أبي ذِئْب عَن عبد الله بن رَافع وَهَذَا خطأ وانما هُوَ عبد الله بن أبي نَافِع انه صلى خلف أبي هريره فَقَرَأَ {إِذا السَّمَاء انشقت} فَسجدَ فِيهَا',NULL,'163',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (120,'٢٣٧ - وَسمعت عليا وَسُئِلَ عَن رَاشد بن سعد الْعَبْسِي فَقَالَ كَانَ ضَعِيفا لَيْسَ بِشَيْء وَلَا يكْتب حَدِيثه
٢٣٨ - وَسمعت عليا يَقُول لم يسمع قتاده من سُلَيْمَان الْيَشْكُرِي شَيْئا وَمَا روى عَنهُ من صحيفَة قراءها عَلَيْهِ من سَمعهَا من سُلَيْمَان الْيَشْكُرِي قَالَ ثَنَا عَليّ قَالَ ثَنَا سُفْيَان ابْن عيينه قَالَ سَمِعت الزُّهْرِيّ يَقُول حَدثنِي الْحسن ابْن مُحَمَّد وَكَانَ حسن أوثق فِي أَنْفُسنَا وَكَانَ عبد الله',NULL,'164',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (121,'يتبع حَدِيثه عَن أَبِيهِمَا عَن عَليّ أَن رَسُول الله صلى الله عَلَيْهِ وَسلم نهى عَن نِكَاح الْمُتْعَة وَعَن لحم الْحمر الْأَهْلِيَّة وَحدثنَا مُحَمَّد قَالَ سَمِعت عليا يَقُول مَا أَدْرِي سمع قتاده من أبي أم لَا كَانَ أَبُو من أهل خُرَاسَان سَمِعت عليا يَقُول قتاده سمع من الْمُغِيرَهْ بن سُلَيْمَان سَمِعت عليا يَقُول مَا أرى قَتَادَة سمع من ابي ثمامه الثَّقَفِيّ ٠',NULL,'165',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (122,'٢٣٩ - وَسُئِلَ عَليّ وَأَنا أسمع عَن أبي قتاده الْحَرَّانِي فَقَالَ كَانَ أَصْحَابنَا يضعفونه
٢٤٠ - وَسُئِلَ عَليّ عَن يُوسُف بن عطيه الصفار فَقَالَ كَانَ ضَعِيفا',NULL,'166',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (123,'٢٤١ - وَسُئِلَ عَليّ وَأَنا أسمع عَن سَلام بن سَالم الطَّوِيل فَقَالَ كَانَت لَهُ أَحَادِيث منكره
٢٤٢ - وَسُئِلَ عَليّ وَأَنا أسمع عَن عتاب بن بشير فَقَالَ كَانَ أَصْحَابنَا يضعفونه',NULL,'167',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (124,'٢٤٣ - وَسُئِلَ عَليّ عَن مُحَمَّد بن سلمه الْحَرَّانِي فَقَالَ كَانَ مُحَمَّد ثِقَة
٢٤٤ - وَعَن أبي الرّبيع السمان فَقَالَ كَانَ ضَعِيفا
٢٤٥ - وَعَن مهْدي بن هِلَال فَقَالَ كَانَ يتهم بِالْكَذِبِ',NULL,'168',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (125,'٢٤٦ - وَسمعت عليا عَن مُعلى بن هِلَال فَقَالَ لَا يكْتب حَدِيث مثل مُعلى كَانَ غير ثِقَة
٢٤٧ - سَمِعت عليا عَن سُلَيْمَان بن قرم فَقَالَ لم يكن بِالْقَوِيّ وَهُوَ صَالح
٢٤٨ - وَسمعت عليا وَسُئِلَ عَن عقبَة الْأَصَم فَقَالَ كَانَ ضَعِيفا',NULL,'169',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (126,'٢٤٩ - وَسمعت عليا يَقُول الْحسن بن دِينَار ضَعِيف لَيْسَ بِشَيْء
٢٥٠ - سَمِعت عليا يَقُول أَبُو ريحانه الَّذِي يحدث عَن سَفِينِهِ',NULL,'170',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (127,'صَالح وسط لَيْسَ بِهِ بَأْس
٢٥١ - وَسمعت عليا يَقُول وَسُئِلَ عَن النَّضر بن عَرَبِيّ فَقَالَ كَانَ النَّضر عِنْد أَصْحَابنَا ثِقَة
٢٥٢ - وَسمعت عليا وَسُئِلَ عَن الْهَيْثَم بن جماز فَقَالَ كَانَ عِنْد أَصْحَابنَا ضَعِيفا',NULL,'171',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (128,'٢٥٣ - وَسُئِلَ عَن زِيَاد الصفار أبي عمر فَقَالَ كَانَ عِنْد أَصْحَابنَا ضَعِيفا
٢٥٤ - وَسمعت عليا يَقُول أَبُو هُرْمُز الَّذِي يروي عَن أنس ضَعِيف ضَعِيف لَيْسَ بِشَيْء',NULL,'172',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (129,'٢٥٥ - وَسمعت عليا وَذكر لَهُ غَالب بن عبيد الله الْجَزرِي فَقَالَ كَانَ ضَعِيفا لَيْسَ بِشَيْء
٢٥٦ - وَسمعت عليا وَذكر عَن معقل بن عبيد الله فَقَالَ كَانَ معقل ثِقَة عِنْد أَصْحَابه
٢٥٧ - وَسمعت عليا يَقُول قُرَيْش بن حَيَّان ثِقَة',NULL,'173',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (130,'٢٥٨ - حَدثنَا مُحَمَّد بن عُثْمَان فَقَالَ سَمِعت عليا يَقُول حزم بن أبي حزم ثِقَة
٢٥٩ - وَسُئِلَ عَن أبي الْمليح فَقَالَ كَانَ عِنْد أَصْحَابنَا ثِقَة
٢٦٠ - وَسُئِلَ عَليّ وَأَنا أسمع عَن مُوسَى بن أعين فَقَالَ كَانَ صَالحا وسطا',NULL,'174',NULL,NULL,'0');
INSERT INTO [b5947] VALUES (131,'<span data-type="title" id=toc-1>آخر مَا انْتهى اليه الْمَنْقُول من الأَصْل </span>هَكَذَا من السؤالات الْحَمد لله رب الْعَالمين وَصلى الله على سيدنَا مُحَمَّد وَآله وحسبنا الله وَنعم الْوَكِيل',NULL,'175',NULL,NULL,'0');
