CREATE TABLE "b6065"(id INTEGER PRIMARY KEY,content TEXT, part TEXT, page TEXT, number TEXT, services TEXT, is_deleted TEXT);

-- Insert data for b6065
INSERT INTO [b6065] VALUES (1,'<span data-type="title" id=toc-1>(بَاب فِي زِيَادَة ونقصانه وَأَنه قَول وَعمل)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب عَن رَسُول الله صلى الله عَلَيْهِ وَسلم شَيْء ".','1','25',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (2,'<span data-type="title" id=toc-2>بَاب (فِي المرجئة والجهمية والقدرية والأشعرية)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب عَن رَسُول الله صلى الله عَلَيْهِ وَسلم شَيْء ".','1','29',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (3,'(٣)<span data-type="title" id=toc-3> بَاب (فِي أَن كَلَام الله عز وَجل قديم غير مَخْلُوق)</span>
قَالَ ابْن الْجَوْزِيّ رَحمَه الله: " قد ورد فِي هَذَا الْبَاب أَحَادِيث، لَيْسَ فِيهَا شَيْء يثبت ".','1','53',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (4,'<span data-type="title" id=toc-4>بَاب (فِي خلق الْمَلَائِكَة)</span>
عَن أبي هُرَيْرَة رَضِي الله تَعَالَى عَنهُ قَالَ: قَالَ رَسُول الله صلى الله عَلَيْهِ وَسلم: " يُؤمر جِبْرِيل كل غَدَاة، فَيدْخل بَحر النُّور، فينغمس فِيهِ انغماسة ثمَّ يخرج فينفض انتفاضة سبعين ألف قَطْرَة (!) يخلق تَعَالَى من قَطْرَة ملكا ... الحَدِيث ". قَالَ عبد الْغَنِيّ بن سعيد الْحَافِظ رَحمَه الله: " لَهُ طرق. وَلَا يَصح عَن رَسُول الله صلى الله عَلَيْهِ وَسلم فَمِنْهَا شَيْء وَلَا من غَيرهَا ".','1','55',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (5,'<span data-type="title" id=toc-5>بَاب (فِي التَّسْمِيَة بِمُحَمد أَو أَحْمد)</span>
قَالَ أَبُو حَاتِم الرَّازِيّ: " قد ورد فِي هَذَا الْبَاب أَحَادِيث عَن رَسُول الله صلى الله عَلَيْهِ وَسلم لَيْسَ فِيهَا مَا يَصح ".','1','57',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (6,'<span data-type="title" id=toc-6>بَاب (فِي الْعقل)</span>
قَالَ أَبُو جَعْفَر الْعقيلِيّ: " لَا يثبت فِي هَذَا الْمَتْن شَيْء ". وَقَالَ أَبُو حَاتِم: " لَيْسَ عَن النَّبِي صلى الله عَلَيْهِ وَسلم خبر صَحِيح من الْعقل ".','1','59',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (7,'<span data-type="title" id=toc-7>بَاب (فِي تعمير الْخضر وإلياس)</span>
سَأَلَ إِبْرَاهِيم الْحَرْبِيّ بن حَنْبَل عَن تعمير الْخضر وإلياس وأنهما باقيان يريان ويروى عَنْهُمَا فَقَالَ: " من أحَال على غَائِب لم ينتصف مِنْهُ، وَمَا ألْقى هَذَا بَين النَّاس إِلَّا الشَّيْطَان ". وَسُئِلَ البُخَارِيّ - رَحمَه الله - عَن الْخضر وإلياس: هَل هما فِي الْأَحْيَاء؟ فَقَالَ: " كَيفَ يكون هَذَا وَقد قَالَ النَّبِي صلى الله عَلَيْهِ وَسلم: " لَا يبْقى على رَأس مائَة سنة مِمَّن هُوَ على ظهر الأَرْض الْيَوْم أحد ". وَقَالَ ابْن الْجَوْزِيّ: " وَمَا جعلنَا لبشر من قبلك الْخلد ".','1','77',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (8,'<span data-type="title" id=toc-8>بَاب (طلب الْعلم فَرِيضَة)</span>','1','83',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (9,'<span data-type="title" id=toc-9>بَاب (من سُئِلَ عَن علم فكتم)</span>
قَالَ أَحْمد بن حَنْبَل: " لَا يَصح فِي هَذَا الْبَاب شَيْء ".','1','105',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (10,'<span data-type="title" id=toc-10>بَاب (ذكر فَضَائِل الْقُرْآن)</span>
قد ورد: " من قَرَأَ سُورَة كَذَا فَلهُ أجر كَذَا " من أول الْقُرْآن إِلَى آخِره ... قَالَ ابْن الْمُبَارك: " أَظن الزَّنَادِقَة وَضَعتهَا ". قَالَ المُصَنّف: " فَلم يَصح فِي هَذَا الْبَاب شَيْء غير قَوْله فِي فَاتِحَة الْكتاب لأبي: ١ - " أَلا أعلمك سُورَة هِيَ أعظم سُورَة فِي الْقُرْآن؟ الْحَمد لله رب الْعَالمين ... ". ٢ - وَقَوله عَلَيْهِ الصَّلَاة وَالسَّلَام: " الْبَقَرَة وَآل عمرَان غمامتان ". ٣ - وَفِي آيَة الْكُرْسِيّ لأبي بن كَعْب: " أَتَدْرِي أَي آيَة من كتاب الله مَعَك أعظم؟ قَالَ: " الله لَا إِلَه إِلَّا هُوَ الْحَيّ القيوم ". ٤ - وَقَوله: " يُؤْتى يَوْم الْقِيَامَة بِالْقُرْآنِ وَأَهله الَّذين كَانُوا يعلمُونَ بِهِ فِي الدُّنْيَا تقدمهم سُورَة الْبَقَرَة ". ٥ - و ... " إِن الشَّيْطَان يفر من الْبَيْت الَّذِي تقْرَأ فِيهِ سُورَة الْبَقَرَة ". ٦ - وَقَوله: " من قَرَأَ بالآيتين من آخر سُورَة الْبَقَرَة كفتاه ".','1','121',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (11,'٧ - وَقَول الشَّيْطَان لأبي هُرَيْرَة رَضِي الله عَنهُ: " إِذا أويت إِلَى فراشك فاقرأ آيَة الْكُرْسِيّ، فَإِنَّهُ لن يزَال عَلَيْك من الله حَافظ، وَلَا يقربك شَيْطَان. فَقَالَ النَّبِي صلى الله عَلَيْهِ وَسلم: " صدق وَهُوَ كذوب ". ٨ - وَفِي الْكَهْف: " من قَرَأَ مِنْهَا عشر آيَات أَمن من فتْنَة الدَّجَّال ". ٩ - و ... " قل هُوَ الله أحد تعدل ثلث الْقُرْآن " ... ١٠ - وَفِي المعوذتين: " أنزل عَليّ آيَات لم ير مِثْلهنَّ قطّ:','1','122',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (12,'<span data-type="title" id=toc-11>بَاب (فِي فَضَائِل أبي بكر الصّديق)</span>
مِنْهَا: ١ - " أَنه تَعَالَى يتجلى للنَّاس عَامَّة، وَلأبي بكر الصّديق خَاصَّة " ٢ - و: " مَا صب الله فِي صَدْرِي شَيْئا، إِلَّا صببته فِي صدر أبي بكر ". ٣ - و: " كَانَ إِذا اشتاق إِلَى الْجنَّة قبل شيبَة أبي بكر ". ٤ - و: " أَنا وَأَبُو بكر كفرسي رهان ". ٥ - و: " إِن الله تَعَالَى لما اخْتَار الْأَرْوَاح، اخْتَار روح أبي بكر ". إِلَى غير ذَلِك مِمَّا يعرف وَضعه ببديهة الْعُقُول. قَالَ ابْن الْجَوْزِيّ رَحمَه الله:
" لم أر هَذِه الْأَحَادِيث أثرا فِي الصَّحِيح وَلَا فِي الْمَوْضُوع وَإِنَّمَا تسمع من الْعَوام ".','1','147',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (13,'<span data-type="title" id=toc-12>بَاب (فضل عَليّ بن أبي طَالب)</span>
قد ورد أَنه سُئِلَ: من يحمل رَايَتك يَوْم الْقِيَامَة؟ فَقَالَ: الَّذِي كَانَ يحملهَا فِي الدُّنْيَا. عَليّ بن أبي طَالب. قَالَ ابْن مردوية:
" لَيْسَ فِيهَا مَا يَصح ".','1','149',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (14,'<span data-type="title" id=toc-13>بَاب (فَضَائِل قبائل الْعَرَب)</span>
سُئِلَ عَن بني عَامر فَقَالَ:
" جمل أَزْهَر " وَعَن بني تيم فَقَالَ:
" هضبة حَمْرَاء " ... الحَدِيث بِطُولِهِ. قَالَ الْعقيلِيّ:
" الرِّوَايَة فِي هَذَا الْبَاب لَيْسَ فِيهَا شَيْء يَصح ".','1','151',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (15,'<span data-type="title" id=toc-14>بَاب (فضل بَيت الْمُقَدّس، والصخرة، وعسقلان، وقزوين)</span>
قَالَ المُصَنّف:
" لَا يَصح فِي هَذَا الْبَاب شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم غير ثَلَاثَة أَحَادِيث فِي بَيت الْمُقَدّس ". أَحدهَا: " لَا تشد الرّحال إِلَّا إِلَى ثَلَاثَة مَسَاجِد، وَالْآخر: أَنه سُئِلَ عَن أول بَيت وضع فِي الأَرْض؟ فَقَالَ: الْمَسْجِد الْحَرَام، ثمَّ قيل: مَاذَا؟! قَالَ: ثمَّ الْمَسْجِد الْأَقْصَى. قيل: كم بَينهمَا؟ قَالَ: أَرْبَعُونَ عَاما. وَالْآخر: " أَن الصَّلَاة فِيهِ تعدل سَبْعمِائة صَلَاة ".','1','153',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (16,'<span data-type="title" id=toc-15>بَاب (فِي فضل مُعَاوِيَة)</span>
قَالَ إِسْحَق بن إِبْرَاهِيم الْحَنْظَلِي:
لَا يَصح عَن النَّبِي صلى الله عَلَيْهِ وَسلم فِي فضل مُعَاوِيَة بن أبي سُفْيَان شَيْء ".','1','165',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (17,'<span data-type="title" id=toc-16>بَاب (مَا ورد فِي مدح أبي حنيفَة وَالشَّافِعِيّ وذمهما)</span>
قَالَ المُصَنّف:
" لَا يَصح فِي هَذَا الْبَاب عَن النَّبِي صلى الله عَلَيْهِ وَسلم شَيْء على الْخُصُوص ".','1','169',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (18,'(١٧)<span data-type="title" id=toc-17> بَاب (إِذا بلغ المَاء قُلَّتَيْنِ لم يحمل خبثاً)</span>
قَالَ المُصَنّف:
" لم يَصح فِي هَذَا الْبَاب عَن النَّبِي صلى الله عَلَيْهِ وَسلم شَيْء، وَفِي " الصَّحِيحَيْنِ " ضد ذَلِك (!) ".','1','171',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (19,'<span data-type="title" id=toc-18>بَاب (فِي المَاء المشمس)</span>
قَالَ الْعقيلِيّ: " لَا يَصح فِي المَاء المشمس حَدِيث إِنَّمَا يرْوى فِيهِ شَيْء عَن عمر بن الْخطب ".','1','173',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (20,'<span data-type="title" id=toc-19>بَاب (فِي التَّسْمِيَة على الْوضُوء)</span>
قَالَ أَحْمد: " لَيْسَ فِيهِ شَيْء يثبت ".','1','177',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (21,'<span data-type="title" id=toc-20>بَاب (كَرَاهِيَة الْإِسْرَاف فِي الْوضُوء)</span>
قد ورد: " أَن للْوُضُوء شَيْطَانا يُقَال لَهُ الولهان، فَاتَّقُوا وسواس المَاء ". قَالَ التِّرْمِذِيّ: " وَلَا يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم ".','1','195',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (22,'<span data-type="title" id=toc-21>بَاب (فِي التنشيف من الْوضُوء)</span>
قَالَ التِّرْمِذِيّ: " لَا يَصح فِي هَذَا الْبَاب عَن رَسُوله صلى الله عَلَيْهِ وَسلم شَيْء ".','1','199',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (23,'<span data-type="title" id=toc-22>بَاب (تَخْلِيل اللِّحْيَة وَمسح الْأُذُنَيْنِ والرقبة)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم ".','2','205',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (24,'<span data-type="title" id=toc-23>بَاب (الْوضُوء بنبيذ التَّمْر)</span>
قد ورد من طرق. قَالَ أَبُو زرْعَة: " هَذَا الحَدِيث لَيْسَ بِصَحِيح ".','2','225',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (25,'<span data-type="title" id=toc-24>بَاب (أَن لمس النِّسَاء لَا ينْقض الْوضُوء)</span>
قَالَ البُخَارِيّ: " لَا يَصح فِي هَذَا الْبَاب عَن النَّبِي صلى الله عَلَيْهِ وَسلم شَيْء ".','2','229',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (26,'<span data-type="title" id=toc-25>بَاب (الْأَمر بِالْغسْلِ لمن غسل مَيتا)</span>
قَالَ أَحْمد: " لَا يثبت فِي هَذَا حَدِيث صَحِيح ".','2','231',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (27,'<span data-type="title" id=toc-26>بَاب (النَّهْي عَن دُخُول الْحمام)</span>
قَالَ المُصَنّف: " لم يَصح فِي هَذَا الْبَاب شَيْء، عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ".','2','247',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (28,'<span data-type="title" id=toc-27>بَاب (بِسم الله الرَّحْمَن الرَّحِيم آيَة من كل سُورَة)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ".','2','255',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (29,'(٢٨)<span data-type="title" id=toc-28> بَاب (فِي الْجَهْر ب " بِسم الله الرَّحْمَن الرَّحِيم </span>")
قَالَ الدَّارَقُطْنِيّ: " كل مَا روى عَن النَّبِي صلى الله عَلَيْهِ وَسلم فِي الْجَهْر ب (بِسم الله الرَّحْمَن الرَّحِيم) فَلَيْسَ بِصَحِيح ".','2','257',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (30,'(٢٩)<span data-type="title" id=toc-29> بَاب الإِمَام ضَامِن، والمؤذن مؤتمن)</span>
قد ورد من طرق ... قَالَ ابْن الْمَدِينِيّ: " لَا يَصح فِي هَذَا الْبَاب عَن النَّبِي صلى الله عَلَيْهِ وَسلم حَدِيث صَحِيح، إِلَّا حَدِيثا رَوَاهُ الْحسن مُرْسلا ".','2','259',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (31,'(٣٠) <span data-type="title" id=toc-30>(بَاب (لَا صَلَاة لِجَار الْمَسْجِد إِلَّا فِي الْمَسْجِد)</span>
قَالَ المُصَنّف: " لَا يَصح فِي الْبَاب عَن النَّبِي صلى الله عَلَيْهِ وَسلم شَيْء. وَكَذَلِكَ الحَدِيث فِي الْجُمُعَة: " من تَركهَا وَله إِمَام عَادل أَو جَائِر إِلَّا لَا صَلَاة لَهُ، أَلا لَا حج لَهُ ". إِلَى غير ذَلِك ...','2','271',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (32,'(٣١)<span data-type="title" id=toc-31> بَاب (الصَّلَاة خلف كل برٍ وَفَاجِر)</span>
قد ورد من طرق. قَالَ الْعقيلِيّ وَالدَّارَقُطْنِيّ: " لَيْسَ فِي هَذَا مَا يثبت ". وَسُئِلَ أَحْمد عَنهُ فَقَالَ: " مَا سمعنَا بِهَذَا ".','2','273',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (33,'(٣٢)<span data-type="title" id=toc-32> بَاب لَا صَلَاة لمن عَلَيْهِ صَلَاة)</span>
سَأَلَ إِبْرَاهِيم الْحَرْبِيّ أَحْمد بن حَنْبَل: مَا معنى هَذَا الحَدِيث؟؟ فَقَالَ: " لَا أعرف هَذَا الْبَتَّةَ ". قَالَ إِبْرَاهِيم: " وَلَا سَمِعت أَنا بِهَذَا عَن النَّبِي صلى الله عَلَيْهِ وَسلم قطّ ".','2','277',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (34,'(٣٣)<span data-type="title" id=toc-33> بَاب (إِثْم إتْمَام الصَّلَاة فِي السّفر)</span>
قد ورد فِيهِ أَحَادِيث ... قَالَ الْعقيلِيّ: " إِنَّمَا روى: الصَّائِم فِي السّفر كالمفطر فِي الْحَظْر، مَعَ ضعف الرِّوَايَة، وَلَيْسَ فِي هَذَا الْمَتْن شَيْء يثبت ".','2','279',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (35,'(٣٤)<span data-type="title" id=toc-34> بَاب (الْقُنُوت فِي الْفجْر إِلَى أَن فَارق الدُّنْيَا)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء، عَن رَسُول الله صلى الله عَلَيْهِ وَسلم. وَفِي " الصَّحِيحَيْنِ " من حَدِيث أنسٍ رَضِي الله عَنهُ قَالَ: " قنت رَسُول الله صلى الله عَلَيْهِ وَسلم شهرا بعد الرُّكُوع، يَدْعُو على أَحيَاء من الْعَرَب، ثمَّ تَركه ".','2','281',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (36,'(٣٥)<span data-type="title" id=toc-35> بَاب النَّهْي عَن الصَّلَاة على الْجِنَازَة فِي الْمَسْجِد)</span>
قَالَ المُصَنّف: " لَا يَصح عَن رَسُول الله صلى الله عَلَيْهِ وَسلم شَيْء فِي هَذَا الْبَاب ".','2','285',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (37,'(٣٦)<span data-type="title" id=toc-36> بَاب رفع الْيَدَيْنِ فِي تَكْبِيرَات الْجِنَازَة)</span>
قَالَ المُصَنّف: " وَلَا يَصح عَن النَّبِي صلى الله عَلَيْهِ وَسلم، وَلَا أَنه لم يرفع ".','2','287',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (38,'(٣٧) <span data-type="title" id=toc-37>(أَن الصَّلَاة لَا يقطعهَا شَيْء)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ".','2','291',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (39,'(٣٨)<span data-type="title" id=toc-38> بَاب (صَلَاة الرغائب، والمعراج، وَالنّصف من شعْبَان، وَصَلَاة الْإِيمَان، والأسبوع كل يَوْم وَلَيْلَة، وبر الْوَالِدين يَوْم عَاشُورَاء، وَغير ذَلِك)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم. وَالصَّحِيح من النَّوَافِل: السّنَن الرَّوَاتِب، والتراويح، وَالضُّحَى، وَصَلَاة اللَّيْل، وتحية الْمَسْجِد، وشكر الْوضُوء، وَصَلَاة الاستخارة، وَالْعِيدَيْنِ - على قَول من لَا يراهما واجبين - وَصَلَاة الْكُسُوف وَالِاسْتِسْقَاء ".','2','297',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (40,'(٣٩)<span data-type="title" id=toc-39> بَاب (صَلَاة التَّسْبِيح)</span>
قَالَ الْعقيلِيّ: " لَيْسَ فِي صَلَاة التسابيح، حَدِيث صَحِيح ".','2','299',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (41,'(٤٠)<span data-type="title" id=toc-40> بَاب (عدد التَّكْبِير فِي صَلَاة الْعِيدَيْنِ)</span>
قَالَ أَحْمد: " لَيْسَ يرْوى فِي التَّكْبِير فِي الْعِيدَيْنِ حَدِيث صَحِيح عَن النَّبِي صلى الله عَلَيْهِ وَسلم ".','2','301',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (42,'(٤١)<span data-type="title" id=toc-41> بَاب (زَكَاة الحلى)</span>
قَالَ المُصَنّف: لَا " يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم ".','2','313',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (43,'(٤٢)<span data-type="title" id=toc-42> بَاب (زَكَاة الْعَسَل)</span>
لَا يَصح عَن النَّبِي صلى الله عَلَيْهِ وَسلم فِي هَذَا الْبَاب كَبِير شَيْء. .','2','319',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (44,'(٤٣)<span data-type="title" id=toc-43> بَاب (لَوْلَا كذب السَّائِل مَا أَفْلح من رده)</span>
قَالَ الْعقيلِيّ: " لَا يَصح فِي هَذَا الْبَاب عَن النَّبِي صلى الله عَلَيْهِ وَسلم شَيْء ".','2','329',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (45,'(٤٤)<span data-type="title" id=toc-44> بَاب (زَكَاة الخضراوات)</span>','2','331',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (46,'(٤٥)<span data-type="title" id=toc-45> بَاب (الطّلب من الرُّحَمَاء والحسان الْوُجُوه)</span>
قَالَ الْعقيلِيّ: " لَيْسَ فِي هَذَا الْبَاب عَن النَّبِي صلى الله عَلَيْهِ وَسلم شَيْء يثبت ".','2','335',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (47,'(٤٦)<span data-type="title" id=toc-46> بَاب (فِي التحذير من التبرم بحوائج النَّاس)</span>
قَالَ الْعقيلِيّ: " قد روى فِي هَذَا الْبَاب أَحَادِيث، لَيْسَ فِيهَا شَيْء يثبت ".','2','337',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (48,'(٤٧)<span data-type="title" id=toc-47> بَاب (فعل الْمَعْرُوف مَحل الضَّيْعَة)</span>
قَالَ الْعقيلِيّ: " لَا يَصح فِي هَذَا الْبَاب شَيْء ".','2','341',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (49,'(٤٨)<span data-type="title" id=toc-48> بَاب (إِن السخي قريب من الله، والبخيل بعيد من الله)</span>
قَالَ الدَّارَقُطْنِيّ: " لَا يثبت مِنْهَا شَيْء بِوَجْه ".','2','343',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (50,'(٤٩)<span data-type="title" id=toc-49> بَاب (فِي فضل عَاشُورَاء)</span>
قد صنف ابْن شاهين جُزْءا كَبِيرا، وَفِيه من الصَّلَوَات والإنفاق، والخضاب، والإدهان، والاكتحال، والحبوب، وَغير ذَلِك. قَالَ المُصَنّف: " لم يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم - غير أَنه صَامَهُ، وَأمر بصيامه، وصومه يكفر سنة ".','2','345',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (51,'(٥٠)<span data-type="title" id=toc-50> بَاب (الاكتحال)</span>
فِيهِ قَالَ الْحَاكِم: " لم يرو عَن رَسُول الله صلى الله عَلَيْهِ وَسلم فِيهِ أثر، وَهِي بِدعَة ابتدعها قتلة الْحُسَيْن ".','2','347',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (52,'(٥١)<span data-type="title" id=toc-51> بَاب (لَا صِيَام لمن لم يعزم الصّيام من اللَّيْل)</span>
قَالَ المُصَنّف: " لَا يَصح فِيهِ شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم. وَفِي " الصَّحِيحَيْنِ " ضد ذَلِك: " أَنه كَانَ يَنْوِي النَّفْل من النَّهَار ".','2','365',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (53,'(٥٢)<span data-type="title" id=toc-52> بَاب (صِيَام رَجَب وفضله)</span>
قَالَ عبد الله الْأنْصَارِيّ: " مَا صَحَّ فِي فضل رَجَب، فِي صِيَامه عَن رَسُول الله صلى الله عَلَيْهِ وَسلم شَيْء ".','2','371',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (54,'(٥٣)<span data-type="title" id=toc-53> بَاب (إِن الْحجامَة تفطر الصَّائِم - وَأفْطر الحاجم والمحجوم)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ".','2','373',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (55,'(٥٤)<span data-type="title" id=toc-54> بَاب (حجُّوا قبل أَن لَا تَحُجُّوا)</span>
و: " من أمكنه الْحَج وَلم يحجّ، فليميت إِن شَاءَ الله يَهُودِيّا وَإِن شَاءَ نَصْرَانِيّا " إِلَى غير ذَلِك ... قَالَ الْعقيلِيّ: " لَا يَصح فِي هَذَا الْبَاب شَيْء ". وَقَالَ الدَّارَقُطْنِيّ: " لَا يَصح مِنْهَا شَيْء ".','2','399',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (56,'(٥٥)<span data-type="title" id=toc-55> بَاب</span>
قَالَ أَحْمد: " أَرْبَعَة أَحَادِيث تروى عَن رَسُول الله صلى الله عَلَيْهِ وَسلم فِي الْأَسْوَاق، لَيْسَ لَهَا أصل: ١ - " من بشرني بِخُرُوج نيسان ضمنت لَهُ على الله الْجنَّة ". ٢ - و: " من آذَى ذِمِّيا فقد آذَانِي ". ٣ - و: " يَوْم صومكم يَوْم نحركم ". ٤ - و: للسَّائِل حق، وَإِن جَاءَ على فرس ".','2','401',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (57,'(٥٦)<span data-type="title" id=toc-56> بَاب كل قرض جر نفعا فَهُوَ رَبًّا)</span>
قَالَ المُصَنّف: " لم يَصح فِيهِ شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم ". وَفِي " الصَّحِيح ": " أَنه اقْترض صَاعا، ورد صَاعَيْنِ ".','2','403',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (58,'(٥٧)<span data-type="title" id=toc-57> بَاب (بيع الكالئ بالكالئ)</span>
قَالَ أَحْمد: " لَيْسَ فِي هَذَا الْبَاب مَا يَصح ".','2','405',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (59,'(٥٨)<span data-type="title" id=toc-58> بَاب (لَا نِكَاح إِلَّا بولِي أَو شَاهِدي عدلٍ)</span>
قَالَ المُصَنّف: " لَا يَصح فِي النِّكَاح بِغَيْر ولي، وَأَنه بَاطِل عَن النَّبِي صلى الله عَلَيْهِ وَسلم حَدِيث صَحِيح، وَكَذَلِكَ فِي الشُّهُود فِي النِّكَاح ". قَالَ أَحْمد بن حَنْبَل: " لم يثبت فِي الشَّهَادَة فِي النِّكَاح شَيْء ". وَقَالَ ابْن الْمُنْذر: " الْأَحَادِيث فِي الشَّهَادَة فِي النِّكَاح لَا تصح ".','2','407',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (60,'(٥٩)<span data-type="title" id=toc-59> بَاب (اتَّخذُوا السراري فَإِنَّهُنَّ مباركات الْأَرْحَام)</span>
قَالَ الْعقيلِيّ: " لَا يَصح فِي ذكر السراري عَن النَّبِي صلى الله عَلَيْهِ وَسلم شَيْء ".','2','431',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (61,'(٦٠) <span data-type="title" id=toc-60>(إيَّاكُمْ وَأَبْنَاء الْمُلُوك، فَإِن لَهُم شَهْوَة كشهوة العذارى)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم ".','2','433',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (62,'(٦١)<span data-type="title" id=toc-61> بَاب (مدح الْعزبَة نَحْو: " عزابها نجابها " وَأَشْبَاه ذَلِك)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء، وَفِي " الصَّحِيح ": لَكِن أَصوم وَأفْطر، وأتزوج النِّسَاء، فَمن رغب عَن سنتي، فَلَيْسَ مني ".','2','435',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (63,'<span data-type="title" id=toc-62>(بَاب (النَّهْي عَن قطع السدر)</span>
قَالَ الْعقيلِيّ: " لَا يَصح فِي قطع السدر شَيْء ". وَقَالَ أَحْمد: " لَيْسَ فِيهِ حَدِيث صَحِيح ".','2','437',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (64,'(٦٣)<span data-type="title" id=toc-63> بَاب (فِي إيثارة اللَّبن، ومدحه الْعَسَل، والباقلاء، والجبن دَاء، والجوز دَوَاء، والباذنجان لما أكل لَهُ، مَاء زَمْزَم لما شرب لَهُ، وَالرُّمَّان، وَالزَّبِيب)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب عَن رَسُول الله صلى الله عَلَيْهِ وَسلم شَيْء، وَإِنَّمَا الزَّنَادِقَة وضعُوا مثل هَذِه الْأَحَادِيث، وقصدوا بهَا شين الْإِسْلَام، وَأَنه مَا كَانَ يعرف الْحِكْمَة، وَتَكْذيب النَّبِي صلى الله عَلَيْهِ وَسلم ".','2','441',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (65,'(٦٤)<span data-type="title" id=toc-64> بَاب (أفضل طَعَام الدُّنْيَا وَالْآخِرَة: اللَّحْم)</span>
قَالَ الْعقيلِيّ: " لَا يَصح فِي هَذَا الْمَتْن شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ".','2','447',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (66,'(٦٥)<span data-type="title" id=toc-65> بَاب (النَّهْي عَن قطع اللَّحْم بالسكين، وَأَنه من صنع الْأَعَاجِم)</span>
قَالَ أَحْمد: " لَيْسَ بِصَحِيح، وَكَانَ رَسُول الله صلى الله عَلَيْهِ وَسلم يحتز من لحم الشَّاة وَيَأْكُل ".','2','451',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (67,'(٦٦)<span data-type="title" id=toc-66> بَاب (فِي الهريسة)</span>
قد صنف فِي ذَلِك جُزْء. . قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم ".','2','453',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (68,'(٦٧)<span data-type="title" id=toc-67> بَاب (النَّهْي عَن أكل الطين)</span>
قَالَ أَحْمد: " مَا أعلم فِي أكله شَيْئا يَصح ". وَقَالَ مرّة: " لَيْسَ فِيهِ شَيْء يثبت، إِلَّا أَنه يضر بِالْبدنِ ".','2','455',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (69,'(٦٨) <span data-type="title" id=toc-68>(الْأكل فِي السُّوق)</span>
قَالَ الْعقيلِيّ: " لَا يَصح فِي هَذَا الْبَاب عَن رَسُول الله صلى الله عَلَيْهِ وَسلم شَيْء ... "','2','457',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (70,'(٦٩)<span data-type="title" id=toc-69> بَاب (فِي الْبِطِّيخ وفضائله)</span>
قَالَ أَحْمد: " لَا يَصح فِي فَضَائِل الْبِطِّيخ شَيْء إِلَّا أَن رَسُول الله صلى الله عَلَيْهِ وَسلم كَانَ يَأْكُلهُ ".','2','459',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (71,'(٧٠)<span data-type="title" id=toc-70> بَاب (فِي النرجس، والورد، والذرنجوش، والبنفسج، والبان)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ".','2','461',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (72,'(٧١)<span data-type="title" id=toc-71> بَاب (الديك الْأَبْيَض صديقي ... الحَدِيث)</span>
قَالَ الْخَطِيب: " لَا يَصح من هَذَا الحَدِيث وَلَا إِسْنَاده ".','2','463',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (73,'(٧٢)<span data-type="title" id=toc-72> بَاب فَضَائِل الْحِنَّاء وَأَنه ورد أَنه من الْجنَّة، وَأَنه يَجْعَل فِي الأكفان، وَغير ذَلِك، وَأَنه يجوز للرِّجَال</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ".','2','465',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (74,'(٧٣)<span data-type="title" id=toc-73> بَاب النَّهْي عَن نتف الشيب</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ".','2','469',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (75,'(٧٤)<span data-type="title" id=toc-74> بَاب النَّهْي عَن تَغْيِير الشيب</span>
إِمَّا بِالْحِنَّاءِ، والكتم، فقد صبغ بهما أَبُو بكر وَعمر بحتاً. . أَخْرجَاهُ. . وَفِي " أَفْرَاد البُخَارِيّ من حَدِيث أم سَلمَة: " كَانَ إِذا أصَاب صَبيا عين أخرجت لَهُم أم سَلمَة شعرًا من شعر النَّبِي صلى الله عَلَيْهِ وَسلم. . ". وَأما بِالسَّوَادِ: فقد صبغ بِهِ الْحسن وَالْحُسَيْن وَسعد بن أبي وَقاص، وَمن التَّابِعين خلق كثير. . وَفِي " صَحِيح البُخَارِيّ " أَن رَأس الْحُسَيْن لما جِيءَ بِهِ كَانَ مخضوباً بالوشمة. . وَقد ورد: " وَيكون فِي آخر الزَّمَان قوم يخضبون بِالسَّوَادِ، لَا يريحون رَائِحَة الْجنَّة ". قَالَ المُصَنّف: " وَلَا يَصح فِي هَذَا الْبَاب شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم غير قَوْله فِي حق أبي قُحَافَة: " وجنبوه السوَاد. وَالْجَوَاب عَنهُ من وَجْهَيْن: الأول: أَن أَحَادِيث مُسلم لَا تقاوم أَحَادِيث البُخَارِيّ (!) الثَّانِي: أَن الْحسن وَالْحُسَيْن وَسعد بن أبي وَقاص قد صبغوا بِالسَّوَادِ، فَلَو كَانَ','2','477',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (76,'حَرَامًا لما فَعَلُوهُ، وَكَذَلِكَ كَانُوا فِي زمَان الصَّحَابَة رضوَان الله عَلَيْهِم أَجْمَعِينَ، فَلَو كَانَ حَرَامًا لأنكروا عَلَيْهِم ". وَعَن أبي هُرَيْرَة رَضِي الله عَنهُ، عَن النَّبِي صلى الله عَلَيْهِ وَسلم، أَنه قَالَ " إِن الْيَهُود وَالنَّصَارَى لَا يصبغون فخالفوهم " أَخْرجَاهُ وَفِي " الصَّحِيحَيْنِ " أَن النَّبِي صلى الله عَلَيْهِ وَسلم أَمر بتغيير الشيب مُطلقًا ...','2','478',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (77,'(٧٥)<span data-type="title" id=toc-75> بَاب (التَّخَتُّم بالعقيق)</span>
قَالَ الْعقيلِيّ: " لَا يثبت فِي هَذَا، عَن صلى الله عَلَيْهِ وَسلم شَيْء ".','2','485',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (78,'(٧٦) <span data-type="title" id=toc-76>(التَّخَتُّم فِي الْيَمين)</span>
قَالَ المُصَنّف: " لم يَصح فِي هَذَا الْبَاب شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ". وَقَالَ الدَّارَقُطْنِيّ رَحمَه الله: " اخْتلف الرِّوَايَات فِيهِ عَن أنس، وَالْمَحْفُوظ أَنه كَانَ يتختم فِي يسَاره ".','2','487',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (79,'(٧٧)<span data-type="title" id=toc-77> بَاب (النَّهْي عَن أَن تقص الرُّؤْيَا على النِّسَاء)</span>
قد ورد ذَلِك من طرق ... قَالَ الْعقيلِيّ " لَا يحفظ من وَجه يثبت ".','2','489',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (80,'(٧٨)<span data-type="title" id=toc-78> بَاب كَلَام النَّبِي صلى الله عَلَيْهِ وَسلم بِالْفَارِسِيَّةِ</span>
قد ورد: " الْعِنَب دو، دو درد اشكنب إِلَى غير ذَلِك ". قَالَ المُصَنّف: " لم يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم، غير ثَلَاثَة أَحَادِيث: ١ - قَوْله صلى الله عَلَيْهِ وَسلم: " قومُوا فقد صنع لكم جَابر سور ". أَخْرجَاهُ. ٢ - وَقَوله عَلَيْهِ الصَّلَاة وَالسَّلَام لِلْحسنِ: " كخ كخ ". أخرجه مُسلم. ٣ - وَقَوله صلى الله عَلَيْهِ وَسلم حِكَايَة عَن جِبْرِيل عَلَيْهِ الصَّلَاة وَالسَّلَام: " لَو رَأَيْتنِي وَأَنا أَخذ من حَال الْبَحْر وأدس فِي فَم فِرْعَوْن مَخَافَة أَن تُدْرِكهُ الرَّحْمَة ".','2','491',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (81,'(٧٩)<span data-type="title" id=toc-79> بَاب (كَرَاهِيَة الْكَلَام بِالْفَارِسِيَّةِ، وَأَنَّهَا لُغَة أهل النَّار)</span>
قَالَ المُصَنّف: " لم يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم، وَقد ذكرنَا آنِفا أَنه ص صلى الله عَلَيْهِ وَسلم تكلم ثَلَاث كَلِمَات بِالْفَارِسِيَّةِ ".','2','493',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (82,'(٨٠)<span data-type="title" id=toc-80> بَاب (أَن ولد الزِّنَا لَا يدْخل الْجنَّة)</span>
قَالَ ابْن الْجَوْزِيّ: " قد ورد فِي ذَلِك أَحَادِيث، لَيْسَ فِيهَا شَيْء يَصح، وَهِي مُعَارضَة لقَوْله تَعَالَى: {وَلَا تزر وَازِرَة وزر أُخْرَى} ... "','2','495',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (83,'(٨١)<span data-type="title" id=toc-81> بَاب (لَيْسَ لفَاسِق غيبَة) فقد ورد من طرق! وَهُوَ بَاطِل</span>
قَالَه الدَّارَقُطْنِيّ والخطيب ...','2','497',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (84,'(٨٢)<span data-type="title" id=toc-82> بَاب (النَّهْي عَن سبّ البرغوث)</span>
قَالَ الْعقيلِيّ: " لَا يَصح فِي سبّ البراغيث عَن النَّبِي صلى الله عَلَيْهِ وَسلم شَيْء. . "','2','499',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (85,'(٨٣)<span data-type="title" id=toc-83> بَاب (ذمّ السماع)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم ".','2','503',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (86,'(٨٥)<span data-type="title" id=toc-84> بَاب (تَحْرِيم اللّعب بالشطرنج)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم ".','2','505',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (87,'(٨٥)<span data-type="title" id=toc-85> بَاب لَا تقتل الْمَرْأَة إِذا ارْتَدَّت)</span>
قَالَ الدَّارَقُطْنِيّ: " لَا يَصح هَذَا الحَدِيث عَن النَّبِي صلى الله عَلَيْهِ وَسلم ". وَفِي " الصَّحِيحَيْنِ ": " من بدل دينه فَاقْتُلُوهُ ".','2','507',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (88,'(٨٦)<span data-type="title" id=toc-86> بَاب (إِذا وجد الْقَتِيل بَين قريتين ضمن أقربهما)</span>
قَالَ الْعقيلِيّ: " لَيْسَ هَذَا الحَدِيث أصل ".','2','509',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (89,'(٨٧)<span data-type="title" id=toc-87> بَاب (فِيمَن أهديت إِلَيْهِ هَدِيَّة وَعِنْده جمَاعَة فهم شركاؤه)</span>
قَالَ الْعقيلِيّ: " لَا يَصح فِي هَذَا الْبَاب شَيْء ".','2','511',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (90,'(٨٨)<span data-type="title" id=toc-88> بَاب (ذمّ الْكسْب وفتنته المَال)</span>
قد ورد فِي ذَلِك أَحَادِيث: " أَن عبد الرَّحْمَن بن عَوْف - رَضِي الله عَنهُ يدْخل الْجنَّة حبوأ ". . إِلَى غير ذَلِك. قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَعنِي ذمّ الْكسْب ".','2','513',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (91,'(٨٩)<span data-type="title" id=toc-89> بَاب (ترك الْأكل وَالشرب من الْمُبَاحَات)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ".','2','515',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (92,'(٩٠)<span data-type="title" id=toc-90> بَاب (فِي الْحجامَة)</span>
قَالَ الْعقيلِيّ: " لَيْسَ يثبت فِي الْحجامَة شَيْء، وَلَا فِي اختبارها، وَالْكَرَاهَة شَيْء ثَبت ". وَقَالَ عبد الرَّحْمَن بن الْمهْدي: " مَا صَحَّ عَن النَّبِي صلى الله عَلَيْهِ وَسلم فِيهَا شَيْء إِلَّا أَنه أَمر بهَا ".','2','517',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (93,'(٩١)<span data-type="title" id=toc-91> بَاب (الاحتكار)</span>
قَالَ المُصَنّف: " قد ورد فِي ذَلِك أَحَادِيث مغلطة، وَلَيْسَ فِيهَا مَا يَصح غير قَوْله عَلَيْهِ الصَّلَاة السَّلَام: " من احتكر فَهُوَ خاطئ " انْفَرد بِهِ مُسلم: وَالْجَوَاب عَنهُ من وُجُوه: الأول: أَن رَاوِي هَذَا الحَدِيث، سعيد بن الْمسيب، عَن معمر بن أبي معمر، وَكَانَ سعيد بن الْمسيب يحتكر، فَقيل لَهُ فِي ذَلِك فَقَالَ: إِن معمراً الَّذِي كَانَ يحدث بِهَذَا كَانَ يحتكر، والراوي إِذا خَالف الحَدِيث دلّ على نُسْخَة أَو ضعفه ({). . (}). وَالثَّانِي: أَن للنَّاس فِي انْفِرَاد مُسلم بِهَذَا كلَاما وَالثَّالِث: أَنه يحمل على مَا إِذا كَانَ يضر بِأَهْل الْبَلَد ".','2','519',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (94,'(٩٢)<span data-type="title" id=toc-92> بَاب (مسج الْوَجْه باليدين بعد الدُّعَاء)</span>
قَالَ أَحْمد: " لَا يعرف هَذَا عَن النَّبِي صلى الله عَلَيْهِ وَسلم، وَإِنَّمَا يرْوى عَن الْحسن الْبَصْرِيّ ".','2','521',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (95,'(٩٣)<span data-type="title" id=toc-93> بَاب (موت الْفجأَة)</span>
قَالَ الْأَزْدِيّ: " لَيْسَ فِيهَا صَحِيح عَن رَسُول الله صلى الله عَلَيْهِ وَسلم ".','2','525',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (96,'(٩٤)<span data-type="title" id=toc-94> بَاب (الْمَلَاحِم والفتن)</span>
قد روى: " أَن عليا رَضِي الله عَنهُ خلا بِابْن الزبير يَوْم الْجمل فَقَالَ: أنْشدك الله، هَل سَمِعت من رَسُول صلى الله عَلَيْهِ وَسلم، وَأَنت لاوى يَدي فِي سقفه بني فلَان، " لَتُقَاتِلنَّهُ وَأَنت ظَالِم لَهُ ". . الحَدِيث ". قَالَ الْعقيلِيّ: " لَا يرْوى فِي هَذَا الْمَتْن حَدِيث من وَجه يثبت ".','2','527',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (97,'(٩٥)<span data-type="title" id=toc-95> بَاب (فِي ظُهُور الْآيَات فِي الشُّهُور)</span>
قد ورد: " تكون فِي رَمَضَان هدة، وَفِي شَوَّال همهمة، إِلَى غير ذَلِك ". قَالَ الْعقيلِيّ: " لَيْسَ لهَذَا الحَدِيث أصل عَن ثِقَة، وَلَا من وَجه يثبت ".','2','529',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (98,'(٩٦)<span data-type="title" id=toc-96> بَاب (ذمّ المولودين بعد الْمِائَة)</span>
قد ورد أَحَادِيث. . قَالَ أَحْمد بن حَنْبَل: " لَيْسَ بِصَحِيح، كَيفَ وَقد كَانَ من الْأَئِمَّة والثقات ولدُوا بعد الْمِائَة ".','2','531',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (99,'(٩٧)<span data-type="title" id=toc-97> بَاب (وصف مَا يكون بعد الثَّلَاثِينَ وَمِائَة، وَالسِّتِّينَ وَمِائَة)</span>
قد ورد: " الغرباء ثَلَاثَة: قُرْآن فِي جَوف ظَالِم، ومصحف فِي بَيت لَا يقْرَأ فِيهِ، وَرجل صَالح بَين قوم سوء ". وَزَاد: " وَفِي سِتِّينَ وَمِائَة، مَسْجِد لَا يصلى فِيهِ ". قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب شَيْء عَن النَّبِي صلى الله عَلَيْهِ وَسلم ".','2','533',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (100,'(٩٨)<span data-type="title" id=toc-98> بَاب (ظُهُور الْآيَات بعد الْمِائَتَيْنِ)</span>
قَالَ الدَّارَقُطْنِيّ: " لَيْسَ فِي الرِّوَايَات فِيهِ شَيْء صَحِيح، عَن النَّبِي صلى الله عَلَيْهِ وَسلم ".','2','535',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (101,'(٩٩)<span data-type="title" id=toc-99> بَاب (لِأَن يُربي أحدكُم جرواً خير لَهُ من أَن يُربي ولدا)</span>
وَفِي حَدِيث آخر: " يكون الْمَطَر قيظاً، وَالْولد غيظاً ". قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب عَن رَسُول الله صلى الله عَلَيْهِ وَسلم شَيْء ".','2','537',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (102,'(١٠٠)<span data-type="title" id=toc-100> بَاب (تَحْرِيم قِرَاءَة الْقُرْآن بالألحان)</span>
قَالَ المُصَنّف: " لَا يَصح فِي هَذَا الْبَاب عَن رَسُول الله صلى الله عَلَيْهِ وَسلم شَيْء ". وَفِي " الصَّحِيحَيْنِ ": أَن النَّبِي صلى الله عَلَيْهِ وَسلم دخل مَكَّة يَوْم الْفَتْح، وَيقْرَأ سُورَة الْفَتْح يرجع بهَا ". قَالَ الرَّاوِي: وَلَوْلَا أَن يجْتَمع عَليّ النَّاس لرجعت كَمَا رَجَعَ ". قَالَ الرَّاوِي: " والترجيع آء آء آء ". وَالْبُخَارِيّ أخرجه عَن مُعَاوِيَة، وَمُسلم أخرجه عَن عبد الله بن مُغفل ".','2','539',NULL,NULL,'0');
INSERT INTO [b6065] VALUES (103,'(١٠١)<span data-type="title" id=toc-101> بَاب (فِي تَحْلِيل النَّبِيذ)</span>
قد روى: " أَن أَعْرَابِيًا شرب من إداوة عمر، فَسَكِرَ، فَأمر بجلده، فَقَالَ: " أَنا شربت من إداوتك ". فَقَالَ عمر: " إِنَّمَا نجلدك على السكر ". قَالَ أَحْمد: " مَا أعلم فِي تَحْلِيل النَّبِيذ حَدِيثا صَحِيحا، فاتهموا الشُّيُوخ ". قَالَ المُصَنّف: " المُرَاد مِنْهُ التَّشْدِيد ".','2','541',NULL,NULL,'0');
