CREATE TABLE "b6106"(id INTEGER PRIMARY KEY,content TEXT, part TEXT, page TEXT, number TEXT, services TEXT, is_deleted TEXT);

-- Insert data for b6106
INSERT INTO [b6106] VALUES (1,'بِسم الله الرَّحْمَن الرَّحِيم وَمَا توفيقي إِلَّا بِاللَّه
<span data-type="title" id=toc-2>حكم اكتحال الصَّائِم بالإثمد والبرود</span>

١ - سَأَلت أبي عَن الصَّائِم يكتحل بالإثمد قَالَ يقل مِنْهُ
٢ - قلت والبرود قَالَ يجتنبه أحب إِلَيّ
<span data-type="title" id=toc-3>مسَائِل فِي زَكَاة الدّين وتعجيل الزَّكَاة وتأخيرها ومصارفها</span>

وَسَأَلت إِلَى أَي شَيْء تذْهب فِي الدّين يُزكي','1','121',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (2,'قَالَ إِذا قَبضه زَكَّاهُ لما مضى وَفِيه اخْتِلَاف إِلَّا أَنِّي أذهب إِلَى أَن يُزَكِّيه لما مضى
٤ - وَسَأَلته عَن تَعْجِيل الزَّكَاة قَالَ لَا بَأْس إِذا وجد لَهَا موضعهَا','1','122',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (3,'٥ - قَالَ وَلَا يُعْطي لكل نفس أَكثر من خمسين','1','123',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (4,'٦ - قلت يعان مِنْهَا فِي السَّبِيل الله قَالَ يُجهز مِنْهَا فِي السَّبِيل
٧ - قلت وَفِي الْحَج قَالَ لَا','1','124',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (5,'٨ - قلت فِي الْعتْق قَالَ كنت أذهب إِلَيْهِ ثمَّ إِنِّي جنبت عَنهُ وَلَكِن يعين فِيهِ
٩ - قلت فيؤخر الزَّكَاة قَالَ لَا','1','125',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (6,'<span data-type="title" id=toc-4>حكم شم الطّيب للصَّائِم</span>

١٠ - قلت يشم الصَّائِم الطّيب قَالَ نعم
<span data-type="title" id=toc-5>من حلف بِالْمَشْيِ إِلَى بَيت الله ثَلَاثِينَ حجَّة</span>

١١ - وَسَأَلت أبي عَن رجل حلف بِالْمَشْيِ إِلَى بَيت الله ثَلَاثِينَ حجَّة قَالَ لَا أُفْتِي بِشَيْء
١٢ - قلت فَإلَى أَي شَيْء كنت تذْهب فِيهِ قَالَ إِلَى كَفَّارَة الْيَمين وَلَكِن قد لهج النَّاس بِهِ فَلَا أحب أَن أُجِيب فِيهِ','1','126',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (7,'<span data-type="title" id=toc-6>حج وَلم يدْرك عَشِيَّة عَرَفَة</span>

١٣ - وَسَأَلته عَمَّن لم يدْرك عَشِيَّة عَرَفَة قَالَ إِن أدْرك قبل أَن يطلع الْفجْر فقد حج','1','127',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (8,'<span data-type="title" id=toc-7>حكم طَلَاق الْمَعْتُوه والسكران</span>

١٤ - وَسَأَلته عَن طَلَاق الْمَعْتُوه فَقَالَ إِذا كَانَ فِي حَال ذهَاب عقله لَا يجوز عَلَيْهِ الطَّلَاق
١٥ - قلت فالسكران قَالَ لَا أُجِيب فِيهِ بِشَيْء','1','128',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (9,'١٦ - قلت فَلَيْسَ هُوَ عنْدك بِمَنْزِلَة الْمَجْنُون قَالَ قد قَالَ قوم ذَلِك قَالَ السَّكْرَان لَيْسَ بمرفوع عَنهُ الْقَلَم وَالْمَجْنُون قد رفع عَنهُ الْقَلَم
<span data-type="title" id=toc-8>حكم خل الْخمر</span>

١٧ - وَسَأَلته عَن خل الْخمر فَقَالَ إِذا أفسدت مُتَعَمدا لَا تُؤْكَل إِلَّا أَن تفْسد هِيَ','1','129',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (10,'<span data-type="title" id=toc-9>حكم الْغسْل بإتيان الْمَرْأَة فِيمَا دون الْفرج وَفِي التقاء الختانين</span>

١٨ - وَسَأَلته عَن الرجل يَأْتِي الْمَرْأَة فِيمَا دون الْفرج هَل يجب عَلَيْهِ الْغسْل قَالَ لَا إِلَّا أَن ينزل فَإِذا التقى الختانان وَجب الْغسْل إِذا تَوَارَتْ الْحَشَفَة
١٩ - قلت وَكنت تذْهب إِلَى أَن المَاء من المَاء','1','130',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (11,'قَالَ لَا من يكذب عَليّ فِي هَذَا أَكثر من ذَلِك قَالَ أبي وَكَانَ هِشَام بن عُرْوَة يذهب إِلَيْهِ وَالْأَعْمَش','1','131',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (12,'<span data-type="title" id=toc-10>حكم الْمَضْمَضَة والغوص فِي المَاء للصَّائِم</span>

٢٠ - وَسَأَلته عَن الصَّائِم يغط فِي المَاء فكرهه
٢١ - قلت فيستنقع قَالَ إِذا أجهد','1','132',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (13,'٢٢ - قلت يتمضمض قَالَ إِذا أجهد
<span data-type="title" id=toc-11>مَسْأَلَة فِيمَن نذر الْمَشْي وَفِي مِقْدَار كَفَّارَة الْيَمين</span>

٢٣ - وَسَأَلته إِلَى أَي شَيْء تذْهب فِي الْمَشْي قَالَ كَفَّارَة يَمِين وَلَا أحملهُ على الْحِنْث فَإِن أحنث أطْعم عشرَة مَسَاكِين إِذا كَانَ عقده عقد الْيَمين','1','133',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (14,'٢٤ - قلت فَأَي شَيْء يطعم قَالَ مُدبر أَقَله إِن شَاءَ وَإِلَّا فَنصف صَاع تمر أَقَله
<span data-type="title" id=toc-12>معنى طَلَاق السّنة</span>

٢٥ - وَسَأَلته عَن طَلَاق السّنة قَالَ يطلقهَا طَاهِرا من غير جماع لِأَن ابْن عمر طلق امْرَأَته وَهِي','1','134',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (15,'حَائِض فَأمره النَّبِي صلى الله عَلَيْهِ وَسلم أَن يُرَاجِعهَا ويطلقها إِذا طهرت من غير جماع
<span data-type="title" id=toc-13>فِي كم تقصر الصَّلَاة وَمَتى تقصر</span>

٢٦ - وَسَأَلته فِي كم تقصر الصَّلَاة قَالَ فِي أَرْبَعَة برد وَهِي سِتَّة عشر فرسخا','1','135',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (16,'٢٧ - قلت يقصر إِذا وارى الْبيُوت وَيفْطر قَالَ نعم','1','136',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (17,'<span data-type="title" id=toc-14>حكم الِاشْتِرَاط فِي الْحَج</span>

٢٨ - قلت تذْهب فِي الِاشْتِرَاط إِلَى حَدِيث ضباعة قَالَ نعم','1','137',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (18,'<span data-type="title" id=toc-15>حكم قصر الصَّلَاة من دخل مَكَّة</span>

٢٩ - وَسَأَلته عَمَّن دخل مَكَّة أيقصر الصَّلَاة قَالَ إِذا زَاد على إِقَامَة أَرْبَعَة أَيَّام وَزِيَادَة صَلَاة أتم لِأَن النَّبِي صلى الله عَلَيْهِ وَسلم دخل صبح رَابِعَة وخامسة وسادسة وسابعة وَصلى يَوْم الثَّامِن الْفجْر بِمَكَّة وَمضى إِلَى منى كل ذَلِك يقصر فَإِذا زَاد على إِقَامَة أَرْبَعَة أَيَّام وَزِيَادَة صَلَاة أتم','1','138',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (19,'<span data-type="title" id=toc-16>حكم قطع يَد النباش</span>

٣٠ - وَسَأَلته عَن النباش يقطع قَالَ إِذا كَانَ قيمَة الْكَفَن ثَلَاثَة دَرَاهِم كَأَنَّهُ يقطع فِي قيمَة مَا يقطع فِيهِ السَّارِق
<span data-type="title" id=toc-17>إِحْرَام أَحْمد من يَلَمْلَم</span>

٣١ - قَالَ أبي أَحرمت من يَلَمْلَم وَهِي قريبَة من مَكَّة وَأَنا جَاءَ من عِنْد','1','139',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (20,'عبد الرَّزَّاق
<span data-type="title" id=toc-18>كَرَاهَة معمر الْحلق</span>

٣٢ - قَالَ أبي كَانَ معمر يكره استئصال الشّعْر','1','140',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (21,'<span data-type="title" id=toc-19>حكم الِاسْتِثْنَاء فِي الطَّلَاق</span>

٣٣ - سُئِلَ أبي وَأَنا شَاهد عَن رجل طلق امْرَأَته وَاسْتثنى فَقَالَ سل غَيْرِي
٣٤ - قيل لَهُ لم لَا تَقول فِيهَا قَالَ إِن الطَّلَاق لَا كَفَّارَة لَهُ وَلَيْسَ هُوَ بِمَنْزِلَة الْيَمين لِأَن الْيَمين يكفر وَالطَّلَاق لَا كَفَّارَة لَهُ','1','141',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (22,'<span data-type="title" id=toc-20>إِذا اغتمس الْجنب فِي المَاء وَلم يتَوَضَّأ وضوءه للصَّلَاة</span>

٣٥ - وَسَأَلته عَن الْجنب يغتمس فِي المَاء وَلَا يتَوَضَّأ وضوءه للصَّلَاة قَالَ يجْزِيه إِذا أَمر المَاء على بَشرته قَالَ الله عز وَجل {وَإِن كُنْتُم جنبا فاطهروا} وَلم يحده حد الْوضُوء','1','142',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (23,'<span data-type="title" id=toc-21>كم مرّة يغسل أَعْضَاء الْوضُوء</span>

٣٦ - سَأَلت أبي عَن الْوضُوء فَقَالَ ثَلَاث أَسْبغ مَا يكون
٣٧ - قلت فَإِن تَوَضَّأ وَاحِدَة قَالَ ثَلَاث أَسْبغ','1','143',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (24,'<span data-type="title" id=toc-22>الْمَسْبُوق يسْجد مَعَ الإِمَام سَجْدَتي السَّهْو ثمَّ يقْضِي مَا فَاتَهُ</span>

٣٨ - وَسَأَلته عَن رجل فَاتَتْهُ مَعَ الإِمَام رَكْعَة وسها الإِمَام يسْجد مَعَه سَجْدَتي السَّهْو أَو يقوم يقْضِي قَالَ يسْجد مَعَه أذهب إِلَى حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم إِنَّمَا جعل الإِمَام ليؤتم بِهِ
<span data-type="title" id=toc-23>تَفْسِير آيَة الزَّكَاة</span>

٣٩ - وَسَأَلته عَن هَذِه الْآيَة {إِنَّمَا الصَّدقَات للْفُقَرَاء وَالْمَسَاكِين والعاملين عَلَيْهَا والمؤلفة قُلُوبهم وَفِي الرّقاب والغارمين وَفِي سَبِيل الله وَابْن السَّبِيل}','1','144',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (25,'قَالَ أبي الصَّدقَات زَكَاة الْإِبِل وَالْبَقر وَالْغنم وَالْمَال وكل شَيْء وَبَعض النَّاس يَقُول الْفُقَرَاء فُقَرَاء الْمُهَاجِرين وَبَعض النَّاس يَقُول الْفُقَرَاء الَّذين لَا يسْأَلُون وَالْمَسَاكِين مَسَاكِين النَّاس والعاملين عَلَيْهَا السُّلْطَان والمؤلفة قُلُوبهم قَالَ كَانَ النَّبِي صلى الله عَلَيْهِ وَسلم يتألف قُريْشًا على الْإِسْلَام أَلا','1','145',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (26,'ترَاهُ أعْطى الْأَقْرَع بن حَابِس وَغَيره يتألفهم على الْإِسْلَام وَفِي الرّقاب يعْتق مِنْهَا والغارمين المديونون وَفِي سَبِيل الله يحمل مِنْهَا فِي سَبِيل الله وَابْن السَّبِيل الْمُنْقَطع بِهِ','1','146',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (27,'<span data-type="title" id=toc-24>هَل يُوقف الْمولي وَمَتى وَهل يُطلق عَلَيْهِ السُّلْطَان</span>

٤٠ - سَأَلته عَن الْإِيلَاء فَقَالَ إِذا قَالَ الرجل لامْرَأَته وَالله لَا قربتك فَإِذا مَضَت الْأَرْبَعَة أشهر أوقف فَقيل لَهُ إِمَّا أَن تفيء وَإِمَّا أَن تطلق فَإِن لم يفِيء طلق عَلَيْهِ','1','147',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (28,'٤١ - قلت يُطلق عَلَيْهِ السُّلْطَان قَالَ نعم وَأما ابْن مَسْعُود فَيَقُول إِذا مَضَت الْأَرْبَعَة أشهر فقد بَانَتْ بِوَاحِدَة وَعلي وَعَائِشَة وَابْن عمر يَقُولُونَ يُوقف بعد الْأَرْبَعَة','1','148',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (29,'وَالْمَوْقُوف يُقَال لَهُ إِمَّا أَن تفيء وَإِمَّا أَن تطلق
<span data-type="title" id=toc-25>حكم الْجَارِيَة إِذا قبل سَيِّدهَا ابْنَتهَا</span>

٤٢ - وَسُئِلَ وَأَنا شَاهد عَن رجل اشْترى جَارِيَة وَلها ابْنة ابْنة عشر سِنِين فقبلها قَالَ تحرم عَلَيْهِ أمهَا وَإِن كَانَت ابْنة تسع حرمت عَلَيْهِ الْأُم وَقَالَ لَا أعلم بَين النَّاس فِي هَذَا اخْتِلَافا إِلَى سبع سِنِين
٤٣ - قلت فَإِن كَانَت بنت خمس ثمَّ قبل بِشَهْوَة قَالَ لَا يُعجبنِي','1','149',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (30,'<span data-type="title" id=toc-26>من غشي امْرَأَة وَتزَوج ابْنَتهَا</span>

٤٤ - وَسَأَلته عَن رجل غشي إمرأة وَتزَوج ابْنَتهَا قَالَ يفارقها حَلَال كَانَ أَو حرَام
<span data-type="title" id=toc-27>حكم كِتَابَة لأبي فلَان على الرسائل</span>

٤٥ - وَسُئِلَ وَأَنا شَاهد يكْتب لأبي فلَان قَالَ يكْتب إِلَى أبي فلَان أحب إِلَيّ','1','150',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (31,'<span data-type="title" id=toc-28>الحكم فِيمَن طهرت ثمَّ عاودها الدَّم</span>

٤٦ - وَسَأَلته عَن امْرَأَة جَلَست أَيَّامهَا ثمَّ طهرت ثمَّ عاودها الدَّم قَالَ تصلي وَلَا تلفت إِلَيْهِ وتتوضأ لكل صَلَاة
<span data-type="title" id=toc-29>حكم صَلَاة التَّطَوُّع على الرَّاحِلَة واستقبال الْقبْلَة فِيهَا</span>

٤٧ - وَسَأَلته أيصلي الرجل على دَابَّته التَّطَوُّع قَالَ يُصَلِّي حَيْثُ مَا تَوَجَّهت بِهِ ويعجبني أَن يسْتَقْبل الْقبْلَة فِي أول صلَاته','1','151',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (32,'<span data-type="title" id=toc-30>حكم الِاكْتِفَاء على التمسح بِالْحِجَارَةِ فِي الإستنجاء</span>

٤٨ - وَسَأَلته عَن الرجل يَبُول ويتمسح بِالْحَائِطِ أَو الْحِجَارَة قَالَ يجْزِيه أَن لَا يمس المَاء','1','152',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (33,'<span data-type="title" id=toc-31>مسَائِل فِي مَوَاقِيت الصَّلَاة</span>

٤٩ - وَسَأَلته عَن وَقت صَلَاة الْفجْر فَقَالَ إِذا طلع الْفجْر إِلَى أَن تطلع الشَّمْس إِلَّا أَنِّي أحب أَن يعجل
٥٠ - وَسَأَلته عَن وَقت صَلَاة الظّهْر فَقَالَ من الزَّوَال إِلَى أَن يصير ظلّ كل شَيْء مثله','1','153',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (34,'٥١ - وَسَأَلته عَن وَقت صَلَاة الْعَصْر فَقَالَ إِذا كَانَ ظلّ كل شَيْء مثله وَهُوَ آخر وَقت الظّهْر وَأول وَقت الْعَصْر وَآخر وَقت الْعَصْر مَا لم تَتَغَيَّر الشَّمْس
٥٢ - وَسَأَلته عَن وَقت صَلَاة الْمغرب فَقَالَ إِذا وَجَبت الشَّمْس إِذا غَابَ حاجبها الْأَعْلَى وَآخر وَقتهَا إِلَى','1','154',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (35,'أَن يغيب الشَّفق والشفق فِي الْحَضَر أَن تذْهب الْحمرَة وَيذْهب الْبيَاض وَفِي السّفر أَرْجُو أَن تكون الْحمرَة
٥٣ - وَسَأَلته عَن وَقت عشَاء الْآخِرَة قَالَ إِذا غَابَ الشَّفق إِلَى ثلث اللَّيْل وَقد قيل إِلَى نصف اللَّيْل
٥٤ - وَسَأَلته عَن الصَّلَاة يَوْم الْجُمُعَة إِذا أخروها','1','155',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (36,'قَالَ يُصليهَا لوَقْتهَا ويصليها مَعَ الإِمَام','1','156',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (37,'٥٥ - وَسَأَلته عَن الصَّلَاة تصلى لوَقْتهَا أَو ينْتَظر الإِمَام قَالَ يُصليهَا لوَقْتهَا
<span data-type="title" id=toc-32>مَسْأَلَة فِيمَن ذبح فأبان الرَّأْس</span>

٥٦ - سَأَلت أبي عَن الرجل يذبح فيبين الرَّأْس قَالَ لَا بَأْس بِهِ إِذا سمى وَأَرَادَ التذكية','1','158',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (38,'<span data-type="title" id=toc-33>مسَائِل فِي الْأَذَان وَالْإِقَامَة</span>

٥٧ - وَسَأَلت أبي عَن الْكَلَام فِي الْأَذَان فَقَالَ لَا بَأْس بِهِ وَهُوَ فِي الْإِقَامَة أوكد وَقَالَ لَا يُعجبنِي أَن يتَكَلَّم فِي الْإِقَامَة','1','159',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (39,'٥٨ - وَسَأَلته يستدير الْمُؤَذّن فِي الْأَذَان قَالَ يلْتَفت يَمِينا وَشمَالًا وَلَا يزِيل قَدَمَيْهِ
٥٩ - وَسَأَلت أبي عَن الْمُؤَذّن يَأْخُذ فِي أَذَانه كرى فكرهه','1','160',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (40,'٦٠ - وَسَأَلت أبي عَن الْأَعْمَى يُؤذن فَقَالَ إِذا عرف الْوَقْت أذن أَو يُؤذن بِأَذَان غَيره فَإِن كَانَ فِي قَرْيَة لَا يعرف الْوَقْت فَلَا
٦١ - سَأَلت أبي عَن الرجل يسمع الْأَذَان يُجَاوِزهُ قَالَ نعم يُجَاوِزهُ وَلَكِن إِذا كَانَ فِي الْمَسْجِد فَلَا يخرج','1','161',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (41,'٦٢ - سَأَلته عَن الرجل يُصَلِّي فِي بَيته يُؤذن قَالَ إِن أذن فَلَيْسَ بِهِ بَأْس وَإِن لم يُؤذن أَجزَأَهُ أَذَان الْحَيّ
٦٣ - سَأَلت أبي عَن الْمَرْأَة تؤذن قَالَ يجزيها إِن لم تؤذن
<span data-type="title" id=toc-34>مسَائِل فِي الْوضُوء</span>

٦٤ - سَأَلت أبي عَن الرجل يتَوَضَّأ وَلَا يُسَمِّي قَالَ يُسَمِّي أعجب إِلَيّ وان لم يسم أَجزَأَهُ','1','162',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (42,'٦٥ - وَسَأَلت أبي عَن الْوضُوء فَقَالَ يجْزِيه إِذا أَسْبغ وَاحِدَة وتجزيه ثِنْتَانِ
٦٦ - وَسَأَلت أبي عَن الرجل يمسح برجليه قَالَ لَا يُعجبنِي يغسلهما
٦٧ - قلت فَإِن مسح قَالَ لَا يجْزِيه يعود إِلَى أول الْآيَة','1','163',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (43,'<span data-type="title" id=toc-35>حكم الِاسْتِجْمَار بالأحجار وَأَقل مَا يجْزِيه فِيهِ مِنْهُ وَمن المَاء</span>

٦٨ - سَأَلت أبي عَن الرجل يستجمر بالأحجار قَالَ لَا بَأْس بِهِ إِذا استجمر بِثَلَاثَة أَحْجَار إِذا أنقى وَأَقل مَا يجْزِيه من المَاء سبع مَرَّات','1','164',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (44,'<span data-type="title" id=toc-36>مسَائِل فِي الْوضُوء</span>

٦٩ - سَأَلت أبي عَن الرجل يتَوَضَّأ وَيتْرك شَيْئا من جسده قَالَ إِذا كَانَ قد جف الْوضُوء أعَاد الْوضُوء كُله ويجزيه من الْجَنَابَة أَن يغسل الْموضع الَّذِي لم يصبهُ المَاء','1','165',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (45,'٧٠ - سَأَلت أبي عَمَّن نسي الْمَضْمَضَة وَالِاسْتِنْشَاق حَتَّى يصلى قَالَ يُعِيد الْمَضْمَضَة وَالِاسْتِنْشَاق وَيُعِيد الصَّلَاة
٧١ - قلت مسح الرَّأْس قَالَ يبْدَأ بِمقدم رَأسه ثمَّ بمؤخر رَأسه إِلَى الْمُقدم','1','166',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (46,'ويعجبني أَن يَأْخُذ للأذن ماءا جَدِيدا
٧٢ - وَسَأَلت أبي عَن الرجل ينسى مسح رَأسه حَتَّى يُصَلِّي قَالَ إِن كَانَ قد جف الْوضُوء أعَاد الْوضُوء كُله وَإِن كَانَ لم يجِف مسح رَأسه وَغسل رجلَيْهِ على مخرج الْكتاب','1','167',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (47,'٧٣ - وَسَأَلت أبي عَن الْمَرْأَة إِذا تَوَضَّأت وَهِي مختضبة أتمسح على الخضاب قَالَ لَا يُعجبنِي أَن يمسح على الخضاب
٧٤ - قلت تختضب وَهِي حَائِض قَالَ لَا بَأْس
٧٥ - وَسَأَلت أبي عَن الْمَرْأَة كَيفَ تمسح برأسها قَالَ لَا تبالي كَيفَ مسحت','1','168',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (48,'٧٦ - وَسَأَلت أبي عَن الْمسْح بالمنديل بعد الْوضُوء قَالَ لَيْسَ بِهِ بَأْس
٧٧ - وَسَأَلت أبي عَن الرجل يطَأ فِي عذرة هَل يغسل قَدَمَيْهِ قَالَ فِي الْعذرَة الرّطبَة يغسل قَدَمَيْهِ
٧٨ - وَسَأَلت أبي عَن الرجل يُصِيبهُ من طين الْمَطَر فَقَالَ كل شَيْء تَأتي عَلَيْهِ السَّمَاء أَرْجُو أَن لَا يكون بِهِ بَأْس','1','169',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (49,'<span data-type="title" id=toc-37>حكم الصَّلَاة فِي الرّحال فِي اللَّيْلَة الْبَارِدَة</span>

٧٩ - سَأَلت أبي عَن الصَّلَاة فِي الرّحال فِي اللَّيْلَة القرة فَقَالَ إِذا كَانَ يُحَال بَينه وَبَين ذَلِك فَلَا بَأْس
<span data-type="title" id=toc-38>حكم الْوضُوء من مس الذّكر والأنثيين</span>

٨٠ - وَسَأَلت أبي عَن مس الذّكر يتَوَضَّأ مِنْهُ قَالَ لَا يتَوَضَّأ إِلَّا من مس الذّكر وَحده
٨١ - قلت وَإِن مس أنثييه قَالَ من الْقَضِيب وَحده الْوضُوء','1','170',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (50,'<span data-type="title" id=toc-39>حكم الْوضُوء مِمَّا غيرت النَّار</span>

٨٢ - سَأَلت أبي عَن الْوضُوء مِمَّا غيرت النَّار قَالَ لَا يتَوَضَّأ مِمَّا غيرت النَّار
<span data-type="title" id=toc-40>حكم الاعتداء فِي الدُّعَاء</span>

٨٣ - سَأَلت أبي عَن الاعتداء فِي الدُّعَاء فَقَالَ يَدْعُو بِدُعَاء مَعْرُوف','1','171',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (51,'<span data-type="title" id=toc-41>الرعاف فِي الصَّلَاة</span>

٨٤ - سَأَلت أبي عَن الرجل يرعف فِي الصَّلَاة فَقَالَ أعجب إِلَيّ أَن يسْتَأْنف الصَّلَاة','1','172',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (52,'<span data-type="title" id=toc-42>مسَائِل فِي الْمِيَاه</span>

٨٥ - سَأَلت أبي عَن الْوضُوء من المَاء الَّذِي ترد السبَاع قَالَ إِذا كَانَ قدره قُلَّتَيْنِ فَلَا بَأْس','1','173',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (53,'والقلتان أَربع قرب فَمَا فَوق
٨٦ - وَسَأَلت أبي عَن المَاء الَّذِي يلقى فِيهِ الجيفة والمحايض قَالَ إِذا كَانَ قدر الْقلَّتَيْنِ فَلَا بَأْس مَا لم يتَغَيَّر طعم أَو ريح','1','174',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (54,'قَالَ وَالْبَوْل والعذرة ينْزح حَتَّى يَغْلِبهُمْ المَاء والعذرة لَا يبْقى مِنْهَا شَيْء','1','175',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (55,'٨٧ - وَسَأَلت أبي عَن سُؤْر الْكَلْب والسنور وَالْحمار يتَوَضَّأ مِنْهُ قَالَ سُؤْر السنور أَرْجُو أَن لَا يكون بِهِ بَأْس وَقَالَ الْحمار لَا يُعجبنِي أَن يتَوَضَّأ مِنْهُ وَالْكَلب يغسل مِنْهُ الْإِنَاء سبع مرار','1','176',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (56,'وَقَالَ فِي سور الْفرس لَا بَأْس بِهِ','1','177',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (57,'<span data-type="title" id=toc-43>مَا يُوجب الْوضُوء من النّوم</span>

٨٨ - وَسَأَلته عَمَّا يُوجب الْوضُوء من النّوم فَقَالَ إِذا اضْطجع أَو استثقل فِي النّوم وَهُوَ جَالس
٨٩ - وَسَأَلته عَن الرجل يسْجد وينام قَالَ إِذا استثقل تَوَضَّأ','1','178',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (58,'<span data-type="title" id=toc-44>مَا يُوجب الْوضُوء من الدَّم</span>

٩٠ - وَسَأَلته عَمَّا يُوجب الْوضُوء من الدَّم فَقَالَ إِذا كثر وفحش أعَاد الْوضُوء
٩١ - وَسَأَلته عَن القصاب يكون فِي ثَوْبه الدَّم قَالَ لَا يُعجبنِي أَن يصلى فِيهِ','1','179',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (59,'٩٢ - وَسَأَلته عَن الرجل يرى فِي ثَوْبه الدَّم وَهُوَ فِي الصَّلَاة قَالَ إِذا كَانَ كثيرا انْصَرف واستقبل الصَّلَاة وَإِذا كَانَ قَلِيلا مضى
<span data-type="title" id=toc-45>كَيْفيَّة طَهَارَة الثَّوْب إِذا أَصَابَهُ بَوْل الْغُلَام أَو الْجَارِيَة</span>

٩٣ - وَسَأَلته عَن بَوْل الصَّبِي قَالَ يرش مَا لم يطعم فَإِذا طعم غسل وَبَوْل الْجَارِيَة يغسل','1','180',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (60,'<span data-type="title" id=toc-46>حكم بَوْل مَا يُؤْكَل لَحْمه من الْحَيَوَانَات</span>

٩٤ - وَسَأَلته عَن بَوْل الْغنم وَالْبَقر وَالْإِبِل فَقَالَ لَا بَأْس بِهِ إِذا كَانَ يستشفي بِهِ','1','181',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (61,'<span data-type="title" id=toc-47>حكم تيَمّم الْمُسَافِر من الْجَنَابَة</span>

٩٥ - وَسَأَلته عَن الرجل تصيبه الْجَنَابَة وَهُوَ مُسَافر قَالَ إِذا خَافَ الْعَطش يتَيَمَّم
<span data-type="title" id=toc-48>حكم غشيان الْمُسَافِر أَهله وَلَيْسَ مَعَه مَاء</span>

٩٦ - وَسَأَلته عَن الْمُسَافِر يغشى أَهله وَيعلم أَن بَينه وَبَين المَاء يَوْمَيْنِ أَو ثَلَاثَة قَالَ أَرْجُو أَن لَا يكون بِهِ بَأْس وَيتَيَمَّم','1','182',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (62,'<span data-type="title" id=toc-49>النَّجَاسَة يَرَاهَا الْمُصَلِّي فِي لِبَاسه</span>

٩٧ - وَسَأَلته عَن الرجل يكون فِي الصَّلَاة فَيرى فِي ثَوْبه دَمًا قَالَ إِن كَانَ يظنّ أَنه فَاحش فلينصرف
٩٨ - قلت فيستأنف الصَّلَاة قَالَ نعم يسْتَأْنف
٩٩ - قلت فَإِن كَانَ قَلِيلا قَالَ إِن شَاءَ رمى بِالثَّوْبِ الَّذِي عَلَيْهِ وَإِن شَاءَ مضى فِي صلَاته
١٠٠ - قلت فَإِن كَانَ بولا قَالَ أما الْبَوْل وَالْغَائِط فَإِنَّهُ يُعِيد من قَلِيله وَكَثِيره
١٠١ - قلت فَإِن كَانَ الْبَوْل فِي النَّعْل والخف فَهُوَ مثل الثَّوْب قَالَ أَرْجُو أَن يكون أسهل قَالَ وَأما حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم خلع النَّعْل فِي الصَّلَاة من شَيْء كَانَ عَلَيْهِ فَإِنَّهُ لم يَجِيء بِبَيَان مَا كَانَ فِي النَّعْل بَوْل أَو غَيره','1','183',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (63,'<span data-type="title" id=toc-50>حكم الْأَذَان وَالْإِقَامَة من غير وضوء</span>

١٠٢ - قلت الْمُؤَذّن يُؤذن على غير وضوء قَالَ يجزيء وَأحب إِلَيّ أَن لَا يُؤذن إِلَّا طَاهِر وَأما الْإِقَامَة فَلَا يُقيم إِلَّا وَهُوَ طَاهِر
<span data-type="title" id=toc-51>حكم الْمَشْي فِي الْإِقَامَة</span>

١٠٣ - قلت فالرجل يمشي فِي الْإِقَامَة قَالَ أحب إِلَيّ أَن يُقيم فِي مَكَانَهُ وَلم يبلغنِي فِيهِ شَيْء إِلَّا حَدِيث بِلَال أَنه قَالَ للنَّبِي صلى الله عَلَيْهِ وَسلم لَا تسبقني بآمين','1','185',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (64,'<span data-type="title" id=toc-52>أَذَان الْجنب</span>

١٠٤ - قلت الْجنب يُؤذن قَالَ لَا','1','186',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (65,'<span data-type="title" id=toc-53>حكم الرَّهْن إِذا لم يَأْتِ الرَّاهِن بِمَالِه عِنْد الْأَجَل</span>

١٠٥ - قلت رجل رهن رهنا وَأخذ مَالا فَلَمَّا حل الْأَجَل لم يَأْته بِمَالِه والتوى عَلَيْهِ كَيفَ يصنع بِالرَّهْنِ قَالَ يكون عِنْده بَاقِيا على حَاله إِلَّا أَن يُوكله بِبيعِهِ
١٠٦ - قلت فَإِن قَالَ لَهُ إِن جئْتُك بِمَالك إِلَى كَذَا وَكَذَا وَإِلَّا فَأَنت وَكيلِي فِي بيع هَذَا قَالَ هَذَا جَائِز','1','187',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (66,'<span data-type="title" id=toc-54>حكم الْمَرْأَة تحيض أثْنَاء الطّواف الْوَاجِب</span>

١٠٧ - وَقَالَ فِي امْرَأَة طافت بِالْبَيْتِ خَمْسَة أَشْوَاط أَو أقل فَحَاضَت قبل أَن تتمّ أسبوعا قَالَ لَا يجزيها الطّواف حَتَّى تتمّ أسبوعا يَعْنِي طواف الزِّيَارَة','1','188',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (67,'<span data-type="title" id=toc-55>مِقْدَار الصَّاع</span>

١٠٨ - قلت الصَّاع كم رطلا قَالَ قدرناه فَهُوَ خَمْسَة أَرْطَال وَثلث حِنْطَة أَو تمر','1','189',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (68,'<span data-type="title" id=toc-56>حكم الظِّهَار من الْأمة</span>

١٠٩ - قلت الظِّهَار من الْأمة والحرة سَوَاء قَالَ إِذا كَانَت الْأمة امْرَأَته تزَوجهَا بِمهْر فَهُوَ ظِهَار وَإِن كَانَت ملك يَمِين أَو أم ولد فَلَيْسَ مِنْهَا ظِهَار وَقَالَ مرّة أُخْرَى فِيهَا كَفَّارَة يَمِين
<span data-type="title" id=toc-57>حكم لبس جُلُود السبَاع وَالصَّلَاة فِيهَا</span>

١١٠ - قلت الصَّلَاة فِي جُلُود السبَاع قَالَ أكرهه
١١١ - قلت فلبسه من غير أَن يُصَلِّي فِيهِ','1','190',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (69,'قَالَ هُوَ أسهل وَقد رُوِيَ عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه نهى أَن تفترش جُلُود السبَاع','1','191',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (70,'<span data-type="title" id=toc-58>القَوْل فِي السمور والسنجاب</span>

١١٢ - قلت فالسمور والسنجاب أَسَبْعٌ هُوَ قَالَ لَا أَدْرِي هَذَا يكون فِي بِلَاد التّرْك','1','192',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (71,'<span data-type="title" id=toc-59>إِذا صلى الإِمَام وَهُوَ على غير وضوء أَو انْتقض وضوءه وَهُوَ فِي الصَّلَاة</span>

١١٣ - وَقَالَ إِذا صلى الإِمَام وَهُوَ على غير وضوء فَإِنَّهُ يُعِيد وَلَا يعيدون وَإِن كَانَ فِي الصَّلَاة ثمَّ انْتقض عَلَيْهِ الْوضُوء فِي الصَّلَاة يُعِيد ويعيدون','1','193',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (72,'<span data-type="title" id=toc-60>حكم الصَّغِيرَة إِذا زَوجهَا أَبوهَا من رجل وأخوها من رجل</span>

١١٤ - وَسَأَلته عَن جَارِيَة صَغِيرَة زَوجهَا أَبوهَا من رجل وأخوها من رجل قَالَ هُوَ الَّذِي زوج الْأَب رضيت أم كرهت نرى نِكَاح الْأَب جَائِزا على الصَّغِيرَة','1','194',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (73,'<span data-type="title" id=toc-61>نقص الْوضُوء من خلع الْعِمَامَة بعد الْمسْح عَلَيْهَا</span>

١١٥ - قلت الرجل يمسح على عمَامَته ثمَّ يخلع الْعِمَامَة قَالَ يُعِيد الْوضُوء
<span data-type="title" id=toc-62>حكم الصَّوْم يَوْم الشَّك</span>

١١٦ - قلت رجل صَامَ يَوْم الشَّك قَالَ إِذا كَانَ فِي السَّمَاء غيم فَأصْبح وَقد أجمع الصّيام من اللَّيْل فصَام فَإِذا هُوَ من رَمَضَان فَإِنَّهُ لَا يُعِيد وَقد جَازَ صَوْمه وَإِذا لم يجمع الصّيام وَلكنه أصبح وَهُوَ يَقُول أَصوم إِن صَامَ النَّاس وَأفْطر إِن أفطر النَّاس وَلم يجمع الصّيام كَذَلِك فصَام ذَلِك الْيَوْم وَإِذا هُوَ من رَمَضَان فَإِنَّهُ يُعِيد يَوْمًا مَكَانَهُ','1','195',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (74,'<span data-type="title" id=toc-63>مسَائِل فِي الْجمع بَين الْأُخْتَيْنِ الأمتين</span>

١١٧ - قلت رجل لَهُ أمة يَطَأهَا فَأَرَادَ أَن يتَزَوَّج أُخْتهَا أَو يتسرى قَالَ لَا يجمع بَين الْأُخْتَيْنِ الأمتين
١١٨ - قلت فَإِن زوج أُخْتهَا الَّتِي عِنْده من رجل قَالَ إِذا زَوجهَا لم يكن بِهِ بَأْس إِذا حرم عَلَيْهَا فرجهَا','1','196',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (75,'١١٩ - قلت فَإِنَّهُ زَوجهَا من رجل ثمَّ وطيء أُخْتهَا فَطلق الرجل هَذِه الَّتِي تزوج هَذَا أُخْتهَا فَرَجَعت فِي ملكه قَالَ يَنْبَغِي أَن يخرج إِحْدَاهمَا من ملكه','1','197',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (76,'<span data-type="title" id=toc-64>حكم الزَّكَاة فِي الْإِبِل الَّتِي تسْتَعْمل نصف السّنة وتسيب نصف السّنة</span>

١٢٠ - قلت رجل لَهُ مائَة من الْإِبِل فيستعملها نصف السّنة ويسيبها نصف السّنة لترعى وَلَا يستعملها هَل فِيهَا صَدَقَة قَالَ إِذا سيبها أَكثر مِمَّا يستعملها فَفِيهَا صَدَقَة قَالَ وَأهل الْحجاز يَقُولُونَ السَّائِمَة والمستعملة كلهَا سَوَاء فِيهَا الصَّدَقَة','1','198',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (77,'<span data-type="title" id=toc-65>وَقت وجوب الزَّكَاة فِي المَال الْمُسْتَفَاد</span>

١٢١ - قلت على المَال الْمُسْتَفَاد زَكَاة قَالَ لَا حَتَّى يحول عَلَيْهِ الْحول
١٢٢ - قَالَ والمستفاد من الْعَطاء وَالْهِبَة وَنَحْو ذَلِك فَأَما مَا كَانَ من ربح المَال أَو مَا كَانَ من أصل المَال فَلَيْسَ بمستفاد
١٢٣ - قلت فَإِذا حَال عَلَيْهِ حول فزكاه ضمه إِلَى مَاله بعد قَالَ نعم','1','200',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (78,'<span data-type="title" id=toc-66>الْمَفْقُود وَبَعض أَحْكَامه</span>

١٢٤ - قَالَ أبي امْرَأَة الْمَفْقُود تربص أَربع سِنِين وَأَرْبَعَة أشهر وَعشرا ثمَّ تتَزَوَّج
١٢٥ - قَالَ وَكَذَلِكَ مَاله يتربص بِهِ
١٢٦ - قَالَ والمفقود أَن يفقد الرجل فِي الْحَرْب أَو يكسر بِهِ فِي الْبَحْر أَو يكون نَائِما على فرَاشه فَلَا يرى وَنَحْو ذَلِك
١٢٧ - قلت فالرجل يغيب عَن أَهله وَلَا يدرى مَكَانَهُ قَالَ لَيْسَ هَذَا بمفقود','1','201',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (79,'<span data-type="title" id=toc-67>حكم الْقَتْل بالمثقل</span>

١٢٨ - قلت لَو أَن رجلا ضرب رجلا بخشبة أَو بِحجر يُرِيد قَتله فَقتله أَكَانَ هَذَا عمدا قَالَ إِذا كَانَ مَا يضْربهُ بِهِ أَكثر من عَمُود الْفسْطَاط فَهُوَ عمد وَإِذا كَانَ دون ذَلِك فَلَيْسَ بعمد أذهب إِلَى حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم رَوَاهُ الْمُغيرَة بن شُعْبَة أَن امْرَأَة ضربت بعمود فسطاط فَلم يكون فِيهِ قَود','1','202',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (80,'<span data-type="title" id=toc-68>آلَة الْقود مِمَّن قتل بمثقل</span>

١٢٩ - قلت لَو أَن رجلا ضرب رجلا بخشبة فَقتله كَيفَ يُقَاد مِنْهُ قَالَ يُقَاد مِنْهُ بِالسَّيْفِ','1','203',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (81,'<span data-type="title" id=toc-69>حكم من نسي الْمَضْمَضَة وَالِاسْتِنْشَاق وَصلى</span>

١٣٠ - قلت رجل نسي الْمَضْمَضَة وَالِاسْتِنْشَاق وَصلى قَالَ يُعِيد الصلاه
١٣١ - قلت يُعِيد الصَّلَاة قلت وَيُعِيد الْوضُوء قَالَ لَا وَلكنه يمضمض ويستنشق
<span data-type="title" id=toc-70>حكم أكل الْمحرم من لحم الصَّيْد</span>

١٣٢ - قلت مَا تَقول فِي محرم أكل صيدا اصطاده حَلَال قَالَ إِذا لم يصد من أَجله فَلَا بَأْس بِهِ','1','204',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (82,'<span data-type="title" id=toc-71>حكم النَّفَقَة وَالسُّكْنَى للمطلقة ثَلَاثًا</span>

١٣٣ - قلت الْمُطلقَة ثَلَاثًا لَهَا السُّكْنَى وَالنَّفقَة قَالَ لَا أَنا أذهب إِلَى حَدِيث فَاطِمَة بنت قيس','1','205',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (83,'<span data-type="title" id=toc-72>أَحْكَام بعض أَلْفَاظ الْكِنَايَات فِي الطَّلَاق</span>

١٣٤ - قلت رجل قَالَ لامْرَأَته أَنْت عَليّ حرَام وَنوى الطَّلَاق قَالَ لَا يكون طَلَاقا نوى أَو لم ينْو
١٣٥ - قلت فِيهِ كَفَّارَة الظِّهَار قَالَ نعم فِيهِ كَفَّارَة الظِّهَار
١٣٦ - قلت فَإِن قَالَ الْحل عَليّ حرَام قَالَ كَذَلِك أَيْضا','1','206',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (84,'١٣٧ - قلت إِن لم ينْو الطَّلَاق قَالَ نوى أَو لم ينْو
١٣٨ - قلت فَرجل قَالَ الْحل عَلَيْهِ حرَام أَعنِي بِهِ الطَّلَاق قَالَ طلقت امْرَأَته ثَلَاثًا
١٣٩ - قلت ثَلَاثًا قَالَ نعم وَلَكِن لَا أُفْتِي بِهِ','1','207',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (85,'<span data-type="title" id=toc-73>حكم أَخذ الشّعير بدل الْحِنْطَة فِي السّلم</span>

١٤٠ - قلت رجل أسلف رجلا دَرَاهِم فِي بر فَلَمَّا حل الْأَجَل لم يكن عِنْده بر فَقَالَ قوم الْبر دَرَاهِم وَخذ بِالدَّرَاهِمِ شَعِيرًا قَالَ لَا يَأْخُذ مِنْهُ شَعِيرًا إِلَّا مثل كيل الْبر أَو أنقص لَا يَأْخُذ مِنْهُ زِيَادَة
١٤١ - قلت فَإِن كَانَ الْبر عشرَة أجربة يَأْخُذ مِنْهُ الشّعير عشرَة أجربة قَالَ نعم','1','208',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (86,'<span data-type="title" id=toc-74>من أَحْكَام الْمُزَارعَة</span>

١٤٢ - قلت رجل يدْفع أرضه إِلَى الأكار على الثُّلُث وَالرّبع قَالَ لَا بَأْس بذلك إِذا كَانَ الْبذر من رب الأَرْض وَالْبَقر وَالْحَدِيد وَالْعَمَل من الأكار أذهب فِيهِ مَذْهَب الْمُضَاربَة
١٤٣ - قلت فَإِن كَانَ الْبذر مِنْهُمَا جَمِيعًا قَالَ لَا يُعجبنِي','1','209',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (87,'<span data-type="title" id=toc-75>الْقِرَاءَة خلف الإِمَام إِذا لم يسمع قِرَاءَته</span>

١٤٤ - قلت الرجل يكون خلف الإِمَام يَوْم الْجُمُعَة وَلَا يستمع قِرَاءَة الإِمَام قَالَ إِن شَاءَ قَرَأَ
<span data-type="title" id=toc-76>مسَائِل فِي بِئْر انصب فِيهِ بَوْل</span>

١٤٥ - وَسَأَلته عَن بِئْر يصب فِيهَا بَوْل قَالَ تنزح لِأَن النَّبِي صلى الله عَلَيْهِ وَسلم نهى أَن يبال فِي المَاء الدَّائِم','1','210',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (88,'١٤٦ - قلت وَإِن كَانَ الْبَوْل قَلِيلا قَالَ لَا أَدْرِي قد نهى النَّبِي صلى الله عَلَيْهِ وَسلم أَن يبال فِي المَاء الدَّائِم
١٤٧ - قلت فَإنَّا قد توضأنا مِنْهَا أَيَّامًا وصلينا قَالَ تُعَاد الصَّلَوَات
١٤٨ - قلت فَإنَّا لَا نَدْرِي كم يَوْمًا صلينَا قَالَ تتوخون أَكثر مَا ترَوْنَ حَتَّى لَا يكون فِي قُلُوبكُمْ شَيْء
١٤٩ - قلت فالثياب قَالَ تغسل الثِّيَاب','1','211',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (89,'<span data-type="title" id=toc-77>تَعْرِيف شركَة الْمُفَاوضَة</span>

١٥٠ - وَقَالَ الشريكان المتفاوضان هما الرّجلَانِ يَشْتَرِكَانِ فَيَقُولَانِ مَا ورثنا من مِيرَاث أَو أصبْنَا من فَائِدَة أَو مَال فَهُوَ أَيْضا بَيْننَا قَالَ هَذَا كَلَام محَال وَلم يره شَيْئا
<span data-type="title" id=toc-78>هَل يسْتَحْلف فِي الْقَذْف وَالْحُدُود</span>

١٥١ - قلت افتري على رجل وَلم يكن لَهُ بَيِّنَة أستحلفه قَالَ لَا','1','212',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (90,'١٥٢ - قلت وَكَذَلِكَ الْحُدُود كلهَا قَالَ اخْتلف النَّاس فِي ذَلِك
<span data-type="title" id=toc-79>من ترك الرَّمْي وَالْمَبِيت بمنى</span>

١٥٣ - قلت رجل حج فَوقف بِعَرَفَة ثمَّ زار الْبَيْت يَوْم النَّحْر فَمضى على وَجهه وَلم ينْصَرف إِلَى منى وَلم يرم الْجمار قَالَ عَلَيْهِ دم','1','213',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (91,'<span data-type="title" id=toc-80>التَّوَقُّف قَلِيلا بعد التَّكْبِيرَة الرَّابِعَة فِي صَلَاة الْجِنَازَة</span>

١٥٤ - قلت الرجل إِذا صلى على الْجِنَازَة فَكبر الرَّابِعَة قَالَ أعجب إِلَيّ أَن يقف بعد الرَّابِعَة قَلِيلا ثمَّ يسلم
١٥٥ - قلت فَيَقُول شَيْئا قَالَ لَا
<span data-type="title" id=toc-81>الْمَفْقُود إِذا قدم وَقد تزوجت امْرَأَته وَقسم مَاله</span>

١٥٦ - قلت الْمَفْقُود إِذا قدم وَقد تزوجت امْرَأَته وَقسم مَاله قَالَ يرد عَلَيْهِ مَاله وَيُخَير بَين امْرَأَته وَبَين الصَدَاق صداقه الَّذِي كَانَ سَاق إِلَيْهَا','1','214',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (92,'١٥٧ - قلت إِن اخْتَار الصَدَاق دفع إِلَيْهِ قَالَ نعم قَالَ وَإِن اخْتَار امْرَأَته اعْتدت من زَوجهَا الْأَخير ثمَّ ردَّتْ إِلَيْهِ','1','215',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (93,'<span data-type="title" id=toc-82>إِذا دفع الدَّرَاهِم وَقَالَ اتّجر فِيهَا بِمَا شِئْت هَل تدخل فِيهِ الزِّرَاعَة</span>

١٥٨ - وَسَأَلت أبي عَن رجل دفع إِلَى رجل ألف دِرْهَم فَقَالَ اتّجر فِيهَا بِمَا شِئْت فزرع بهَا زرعا فَسلم فربح قَالَ الْمُضَاربَة جَائِزَة وَالرِّبْح بَينهمَا على مَا اصطلحا عَلَيْهِ
<span data-type="title" id=toc-83>مَسْأَلَة فِي مِقْدَار الْجِزْيَة</span>

١٥٩ - وَسَأَلت أبي إِلَى أَي شَيْء تذْهب فِي الْجِزْيَة فَقَالَ أما أهل الشَّام فعلى مَا وظف عمر أَرْبَعَة دَنَانِير وَكِسْوَة','1','216',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (94,'وزيت وَأما أهل الْيمن فعلى كل حالم دِينَار وَأهل الْعرَاق على مَا يُؤْخَذ مِنْهُم الْيَوْم
<span data-type="title" id=toc-84>مَسْأَلَة فِي بيع الْوَلَاء وهبته</span>

١٦٠ - وَقَالَ الْوَلَاء أذهب إِلَى أَن لَا يُبَاع وَلَا يُوهب','1','217',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (95,'<span data-type="title" id=toc-85>الحكم إِذا ادّعى أحد الْخَصْمَيْنِ الْكل وَالْآخر النّصْف</span>

١٦١ - حَدثنِي أبي قَالَ حَدثنَا عَبدة قَالَ حَدثنَا حسن بن صَالح عَن ابْن شبْرمَة وَابْن أبي ليلى وَرَبِيعَة الرَّأْي فِي رجلَيْنِ كَانَ بَينهمَا كيس فِيهِ','1','218',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (96,'ألف دِرْهَم فَقَالَ أَحدهمَا لي كُله وَقَالَ الآخر لي نصفه قَالَ ابْن شبْرمَة قد أقرّ أَحدهمَا للْآخر بِنصفِهِ فَلَيْسَ لَهُ فِي نصفه شَيْء وَالنّصف الْبَاقِي بَينهمَا نِصْفَيْنِ وَقَالَ ابْن أبي ليلى يقسم الْألف على ثِنْتَيْنِ وَيحلف فَيكون لصَاحب الْجَمِيع ثلثا ألف وَيكون لصَاحب النّصْف ثلث الْألف وَقَالَ ربيعَة الرَّأْي هُوَ بَينهمَا نِصْفَيْنِ وَقَالَ أبي إِذا كَانَ فِي أَيْدِيهِمَا لم يصدق هَذَا على ذَا وَلَا هَذَا على ذَا وَهُوَ بَينهمَا نِصْفَانِ فَإِذا لم يكن فِي أَيْدِيهِمَا فَقَالَ هَذَا لي الْكل وَقَالَ هَذَا لي','1','219',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (97,'النّصْف قد أقرّ صَاحب النّصْف أَنه لَا حق لَهُ فِيهِ فَهُوَ لمُدعِي الْكل وَيبقى النّصْف قد ادعياه جَمِيعًا فيستهمان عَلَيْهِ فَأَيّهمَا أَصَابَته الْقرعَة حلف وَكَانَ النّصْف الْبَاقِي لَهُ وَهَذَا على حَدِيث أبي هُرَيْرَة عَن','1','220',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (98,'النَّبِي صلى الله عَلَيْهِ وَسلم أَن رجلَيْنِ تداآ فِي دَابَّة لَيْسَ لوَاحِد مِنْهُمَا بَيِّنَة فَأَمرهمَا النَّبِي صلى الله عَلَيْهِ وَسلم أَن يستهما على الْيَمين أحبا أَو كرها وَكَذَا لَو أَقَامَا الْبَيِّنَة جَمِيعًا سَقَطت الْبَيِّنَتَانِ جَمِيعًا لِأَن كل وَاحِدَة مِنْهُمَا قد أكذبت صاحبتها ويستهمان على الْيَمين','1','221',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (99,'<span data-type="title" id=toc-86>الحكم إِذا جَاوَزت الْوَصِيَّة المَال</span>

١٦٢ - قلت رجل أوصى أَن لي عِنْد فلَان ثَلَاثمِائَة دِرْهَم لفُلَان مائَة وَلفُلَان مائَة وَلفُلَان مائَة فَدفع إِلَى الأول مائَة وَإِلَى الثَّانِي مائَة وَبَقِي الثَّالِث لم يتم مائَة قَالَ يتحاصون بَينهم وَإِذا أوصى لرجل بِمَالِه وَللْآخر بِنصْف مَاله فَلم يجز ذَلِك الْوَرَثَة وَكَانَ لصَاحب الْجَمِيع ثلثا الثُّلُث وَلِصَاحِب النّصْف ثلث الثُّلُث فَكَأَنَّهُ تِسْعَة أسْهم فَلصَاحِب الْجَمِيع سِتَّة أسْهم وَلِصَاحِب النّصْف ثَلَاث وَهُوَ قَول ابْن أبي ليلى','1','222',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (100,'<span data-type="title" id=toc-87>معنى السائبة</span>

١٦٣ - قَالَ أبي السائبة أَن تعتقه لوجه الله لَا يُرِيد من مِيرَاثه شَيْئا','1','223',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (101,'<span data-type="title" id=toc-88>حكم وعد النَّفْل قبل الْقِتَال وَهل هُوَ من الْخمس أَو من غَيره</span>

١٦٤ - وَسَأَلته عَن الإِمَام يبْعَث السّريَّة يُسَمِّي لَهَا النَّفْل حِين يبعثها قَالَ إِذا كَانَ يُرِيد أَن يضر بهَا على الْعَدو فَلَا بَأْس وَقَالَ النَّفْل يخرج الْخمس وَيكون النَّفْل فِي الْبَاقِي وَالَّذِي يَقُول','1','224',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (102,'مَالك وَسَعِيد بن الْمسيب النَّفْل من الْخمس خلاف مَا يرْوى أَن النَّبِي صلى الله عَلَيْهِ وَسلم نفل الرّبع بعد الْخمس وَالثلث بعد الْخمس وَمِمَّا','1','225',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (103,'يُقَوي ذَلِك قَول الله تبَارك وَتَعَالَى فِي كِتَابه {وَاعْلَمُوا أَنما غَنِمْتُم من شَيْء فَأن لله خمسه} الْآيَة وَيكون أَرْبَعَة أَخْمَاس لمن قَاتل وَيكون النَّفْل فِي الْأَرْبَعَة أَخْمَاس
<span data-type="title" id=toc-89>حكم فرس حمل عَلَيْهَا رجل فِي سَبِيل الله فغزا عَلَيْهَا</span>

١٦٥ - وَقَالَ أبي كل من حمل على فرس فِي سَبِيل الله فغزا عَلَيْهِ فَهُوَ كَسَائِر مَاله وَمِمَّا يُثبتهُ حملان عمر على الْفرس فرآها تبَاع أَو بعض نتاجها فَأَرَادَ شِرَاءَهُ فَقَالَ لَهُ النَّبِي لَا تعد فِي صدقتك','1','226',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (104,'<span data-type="title" id=toc-90>عزل عمر رَضِي الله عَنهُ قَاضِيا</span>

١٦٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يُونُس بن مُحَمَّد قَالَ حَدثنَا جرير عَن مُحَمَّد بن سِيرِين قَالَ أنبئت أَن عمر بن الْخطاب اسْتعْمل رجلا على الْقَضَاء فَجَاءَهُ رجلَانِ فاختصما إِلَيْهِ فِي دِينَار فَحل من كمه فَدفعهُ إِلَيْهِمَا فَبلغ ذَلِك عمر فَقَالَ أعتزل قضاءنا','1','227',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (105,'<span data-type="title" id=toc-91>قَضَاء شُرَيْح فِي متخاصمين فِي سنور وَلَيْسَت لَهما بَيِّنَة</span>

١٦٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو نعيم قَالَ حَدثنَا الْمُغيرَة بن أبي الْحر قَالَ أَخْبرنِي الحكم بن عتيبة قَالَ جَاءَ رجل يُخَاصم إِلَى شُرَيْح فِي سنور فَقَالَ هَات بينتك فَقَالَ وَالله لقد علمت مَا أجد بَيِّنَة لسنور ولدت عندنَا قَالَ فاذهبا بهَا إِلَى أمهَا فأرسلاها فَإِن هِيَ اسْتَقَرَّتْ واستمرت وَدرت فَهِيَ سنورك وَإِن هِيَ اقشعرت وفرت وقدت فَلَيْسَتْ بسنورك','1','228',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (106,'<span data-type="title" id=toc-92>قَضَاء شُرَيْح فِي شَاة كَانَت تَأْكُل الذبان</span>

١٦٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا الزبير قَالَ حَدثنَا المَسْعُودِيّ عَن الْقَاسِم عَن شُرَيْح أَنه اخْتصم إِلَيْهِ فِي شأة تَأْكُل الذبان فَقَالَ لبن طيب وعلف مجان','1','229',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (107,'<span data-type="title" id=toc-93>اخر الْجُزْء الثَّامِن من أَجزَاء صَالح</span>

<span data-type="title" id=toc-94>مسَائِل فِي النّفاس والاستحاضة</span>

١٦٩ - حَدثنَا صَالح قَالَ سَأَلت أبي عَن امْرَأَة نفسَاء رَأَتْ الطُّهْر فِي أقل من ثَلَاثِينَ يَوْمًا فَمَكثت أَرْبَعَة أَيَّام طَاهِرا ثمَّ رَأَتْ فِي كل يَوْم بعد ذَلِك شَيْئا كالكدرة حَتَّى كَانَ الْأَرْبَعُونَ فرأت الدَّم الْأسود فَلَيْسَ يَنْقَطِع قَالَ أما وَمَا عاودها من الدَّم فِيمَا بَينهَا وَبَين الْأَرْبَعين فقد يكون اسْتِحَاضَة أَو بَقِيَّة نِفَاس أَو حيضا فالاحتياط لَهَا أَن تصلي وتصوم ثمَّ تعود للصَّوْم لِأَنَّهُ إِن كَانَ نفاسا أَو حيضا لم يجزها الصَّوْم وَأما','1','130',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (108,'مَا كَانَ بعد الْأَرْبَعين فَإِن كَانَ فِي أَيَّام قد كَانَت تعرفه من أَيَّام حَيْضهَا فَهُوَ حيض وَإِن لم يكن فِي أَيَّام كَانَت تعرفه من أَيَّامهَا الَّتِي كَانَت تحيضها فَهِيَ اسْتِحَاضَة فَهِيَ تَصُوم فِيهِ وَتصلي وَلَا تعيد الصَّوْم','1','231',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (109,'١٧٠ - قلت الْمُسْتَحَاضَة قَالَ للمستحاضة سنَن فَإِذا جَاءَت فَزَعَمت أَنَّهَا مُسْتَحَاضَة سُئِلت عَن شَأْنهَا فَإِذا زعمت أَنه كَانَ لَهَا أَيَّام مَعْلُومَة تجلسها فِي وَقت مَعْلُوم قيل لَهَا إِذا جَاءَ ذَلِك الْوَقْت من الشَّهْر فاجلسي عدد تِلْكَ الْأَيَّام الَّتِي كنت تجلسين فِيمَا خلا فَإِذا جَاوَزت تِلْكَ الْأَيَّام فاغتسلي غسلا وَاحِدًا ثمَّ توضيء لكل صَلَاة وَصلي وَإِن شَاءَت اغْتَسَلت لكل صَلَاة فَذَلِك أَكثر مَا جَاءَ فِيهِ وَإِن شَاءَت جمعت بَين الظّهْر وَالْعصر بِغسْل وَبَين الْمغرب وَالْعشَاء بِغسْل وَاغْتَسَلت للصبح','1','232',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (110,'غسلا فَهَذَا وسط مَا جَاءَ فِيهِ وَإِن تَوَضَّأت فَهُوَ أقل مَا جَاءَ فِيهِ وَهُوَ يجزيها إِن شَاءَ الله وَالْحجّة فِي أَن الْوضُوء يجزيها قَول النَّبِي صلى الله عَلَيْهِ وَسلم إِنَّمَا ذَلِك عرق وَلَيْسَ بالحيضة فَلَا يكون الْغسْل من غير الْحَيْضَة وَهَذِه سنة الَّتِي كَانَت تعرف وَقت جلوسها وَعدد أَيَّام جلوسها وَهَذَا فِي حَدِيث نَافِع عَن سُلَيْمَان بن يسَار عَن أم سَلمَة','1','233',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (111,'وَسنة أُخْرَى للمستحاضة إِذا جَاءَت فَزَعَمت أَنَّهَا كَانَت تستحاض فَلَا تطهر قيل لَهَا أَنْت الْآن لَيْسَ لَك أَيَّام مَعْلُومَة فتجلسيها وَلَكِن انظري إِلَى إقبال الدَّم وإدباره فَإِذا أَقبلت الْحَيْضَة وإقبالها أَن ترى دَمًا أسود يعرف فَإِذا تغير دَمهَا فَكَانَ إِلَى الصُّفْرَة والرقة فَذَلِك دم الِاسْتِحَاضَة فاغتسلي وَصلي ثمَّ توضيء لكل صَلَاة وَإِن لم يَنْقَطِع الدَّم إِلَى خمس عشرَة فَلَا تنظر بعد خمس عشرَة إِلَى الدَّم ولتكن بعد خمس عشرَة','1','234',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (112,'مُسْتَحَاضَة لِأَن أَكثر الْحيض خمس عشرَة فَهَذِهِ سنة الَّتِي لم تكن تعرف أَيَّامهَا وَهَذَا فِي حَدِيث هِشَام بن عُرْوَة عَن أَبِيه عَن عَائِشَة أَن فَاطِمَة ابْنة أبي حيبش سَأَلت النَّبِي صلى الله عَلَيْهِ وَسلم
قَالَ أبي وَأكْثر النّفاس فِي قَول أهل الحَدِيث أَرْبَعُونَ وَفِي قَول أهل','1','235',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (113,'الْمَدِينَة أَكْثَره سِتُّونَ وَالْحجّة فِيهِ قَول عُثْمَان بن أبي الْعَاصِ وعائذ بن عَمْرو وَعمر بن الْخطاب وَأنس وَأَقل النّفاس أَن ترى الطُّهْر','1','236',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (114,'فَمَتَى رَأَتْ الطُّهْر فِيمَا دون الْأَرْبَعين اغْتَسَلت وصلت وَلَا يَأْتِيهَا زَوجهَا وَإِذا رَأَتْ الطُّهْر فِي عشر فَمَكثت عشرا أُخْرَى طَاهِرا ثمَّ عاودها الدَّم فِيمَا دون الْأَرْبَعين قيل افعلي كَمَا تفعل الْمُسْتَحَاضَة فِي هَذِه الْأَيَّام الَّتِي رَأَيْت فِيهَا الدَّم فِيمَا دون الْأَرْبَعين فَإِذا كَانَ بعد وَرَأَيْت الطُّهْر بعد الْأَرْبَعين فعودي إِلَى الْأَيَّام الَّتِي صمتيها فِي الدَّم فِيمَا دون الْأَرْبَعين فاقضيها وَلَا تقضي الصَّلَاة وَذَلِكَ لِأَنَّك رَأَيْت الدَّم فِيمَا دون الْأَرْبَعين وَهُوَ وَقت النّفاس وَقد رَأَيْت الطُّهْر قبل ذَلِك الدَّم وَلَا تدرين لَعَلَّ هَذَا الدَّم بَقِيَّة من النّفاس أَو حيض لِأَنَّهُ فِي وقته وَلَا تدرين لَعَلَّه عرق عَائِد لِأَنَّك قد رَأَيْت الطُّهْر وَلَا تدرين لَعَلَّه حيض فَإِن كَانَ حيضا فقد احتطنا لَك حِين أمرناك أَن تصلي وتصومي وَإِذا لم تعلمي حَائِضًا أَنْت أَو مُسْتَحَاضَة فَإِن كنت','1','237',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (115,'مُسْتَحَاضَة فقد قضيت وَإِن كنت حَائِضًا فقد أمرناك بِقَضَاء الصَّوْم بعد الطُّهْر وَلم نأمر بِقَضَاء الصَّلَاة لِأَن الْحَائِض لَا تقضي الصَّلَاة
<span data-type="title" id=toc-95>الْمُتَيَمم يرى المَاء وَهُوَ فِي الصَّلَاة</span>

١٧٢ - قلت الْمُتَيَمم يرى المَاء وَهُوَ فِي الصَّلَاة قَالَ قد كنت أَقُول يمْضِي فِي صلَاته ثمَّ وقفت فِيهَا','1','238',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (116,'<span data-type="title" id=toc-96>حكم من نسي طواف يَوْم النَّحْر</span>

١٧٣ - وَقَالَ أبي إِذا نسي الطّواف الْوَاجِب وَهُوَ طواف يَوْم النَّحْر لم يزل حَرَامًا حَتَّى يعود من قَابل
<span data-type="title" id=toc-97>احْتِيَاط سُفْيَان فِي مسَائِل الطَّلَاق وتساهله فِي مسَائِل الْحيض والمناسك</span>

١٧٤ - قَالَ أبي كَانَ سُفْيَان إِذا سُئِلَ عَن شَيْء من الْحيض أَو الْمَنَاسِك يَقُول لَا حرج لَا حرج وَإِذا سُئِلَ عَن شَيْء من الطَّلَاق يَقُول من يحسن هَذَا من يحسن هَذَا','1','239',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (117,'<span data-type="title" id=toc-98>مناصحة ابْن عُيَيْنَة للسُّلْطَان فِي أَمر الْمُسلمين</span>

١٧٥ - سَمِعت أبي قَالَ دخل سُفْيَان بن عُيَيْنَة على معن بن زَائِدَة وَهُوَ بِالْيمن وَلم يكن سُفْيَان تلطخ بِشَيْء من أَمر السُّلْطَان بعد فَجعل سُفْيَان يعظه وَيذكر لَهُ أَمر الْمُسلمين فَجعل معن يَقُول لَهُ أبوهم أَنْت أخوهم أَنْت
<span data-type="title" id=toc-99>حكم من نسي الظّهْر فَذكرهَا فِي آخر وَقت الْعَصْر</span>

١٧٦ - وَسَأَلته عَن رجل نسي صَلَاة الظّهْر فَذكرهَا فِي آخر وَقت الْعَصْر قَالَ إِذا خَافَ فَوت الْعَصْر صلى الْعَصْر ثمَّ صلى الظّهْر وَإِذا ذكر','1','240',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (118,'الظّهْر وَقد فرغ من الْعَصْر صلى الظّهْر وَلم يعد الْعَصْر فَإِن ذكرهَا وَهُوَ فِي الصَّلَاة أعَاد الظّهْر وَالْعصر وَإِن كَانَ فِي جمَاعَة مضى مَعَهم وَإِن كَانَ وَحده انْصَرف وَأعَاد','1','241',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (119,'<span data-type="title" id=toc-100>حكم مَا فضل من نَفَقَة الْحَج عَن الْمَيِّت</span>

١٧٧ - قَالَ أبي إِذا قَالَ حجُّوا عني حجَّة بِأَلف دِرْهَم فَمَا فضل فَهُوَ للَّذي يحجّ وَإِذا قَالَ حجُّوا عني حجَّة فَمَا فضل فَهُوَ بَين الْوَرَثَة وَإِذا قَالَ حجُّوا عني بِأَلف دِرْهَم فَمَا فضل فَهُوَ فِي الْحَج','1','242',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (120,'<span data-type="title" id=toc-101>حكم غسل الْمحرم رَأسه بالخطمي</span>

١٧٨ - وَقَالَ فِي الْمحرم يغسل رَأسه بالخطمي قَالَ عَلَيْهِ دم قد رجل شعره وَلَعَلَّه يقطع الشّعْر من الْغسْل
<span data-type="title" id=toc-102>حكم الخلية والبرية والبائن وَحل الله عَليّ حرَام</span>

١٧٩ - وَقَالَ أبي إِذا قَالَ حل الله عَليّ حرَام وَله امْرَأَة فكفارة مُغَلّظَة عتق رَقَبَة أَو صَوْم شَهْرَيْن أَو إطْعَام سِتِّينَ مِسْكينا وَشبهه بقوله أَنْت عَليّ كَظهر أُمِّي فَقَالَ إِنَّمَا أَرَادَ بقوله أَنْت عَليّ كَظهر أُمِّي التَّحْرِيم
١٨٠ - قلت فَإِذا قَالَ حل الله عَليّ حرَام أَعنِي بِهِ الطَّلَاق فقد حرم وَخص بِالطَّلَاق','1','243',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (121,'قَالَ كَذَلِك الخلية والبرية والبائن لَا أَقُول فِيهَا شَيْئا وأخاف أَن تكون ثَلَاثًا
<span data-type="title" id=toc-103>ممازحة فُضَيْل سُفْيَان</span>

١٨١ - قَالَ أبي قَالَ فُضَيْل مَا بَال أخي سُفْيَان لَا يعْطى ويعرض سُفْيَان','1','244',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (122,'<span data-type="title" id=toc-104>حكم افتراش الْحَرِير</span>

١٨٢ - سَأَلت أبي عَن افتراش الْحَرِير هُوَ عنْدك بِمَنْزِلَة لبسه قَالَ نعم قد نهى النَّبِي صلى الله عَلَيْهِ وَسلم عَن افتراش مسوك السبَاع
١٨٣ - قلت وَرُوِيَ عَن عُبَيْدَة افتراش الْحَرِير مثل لبسه قَالَ نعم','1','245',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (123,'<span data-type="title" id=toc-105>حكم شِرَاء الْوَصِيّ من مِيرَاث الْيَتِيم</span>

١٨٤ - وَسَأَلت أبي عَن الْوَصِيّ يَشْتَرِي من الْمِيرَاث قَالَ لَا يَشْتَرِي كَيفَ يَشْتَرِي وَهُوَ يَبِيع يَشْتَرِي لإحدى يَدَيْهِ من الْأُخْرَى
<span data-type="title" id=toc-106>حكم نقض الْمُصحف ليَكُون أخف فِي الْحمل</span>

١٨٥ - سَأَلت أبي عَن رجل عِنْده مصحف جَامع يُرِيد أَن ينْقضه فَيَجْعَلهُ أَثلَاثًا ليَكُون أخف عَلَيْهِ فأيش ترى فِي ذَلِك قَالَ لَا أعلم بِهِ بَأْسا','1','246',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (124,'<span data-type="title" id=toc-107>حكم الْوضُوء من النّوم فِي الصَّلَاة</span>

١٨٦ - وَسَأَلته عَن الرجل ينَام قَاعِدا أَو قَائِما فِي صَلَاة وَفِي السُّجُود وَالرُّكُوع قَالَ أما إِذا نَام قَائِما أَو قَاعِدا فَإِذا طَال نَومه حَتَّى يحلم فَأحب إِلَيّ أَن يتَوَضَّأ وَأما إِذا نَام رَاكِعا فَهُوَ عِنْدِي أَشد من الْقيام وَالْقعُود وَالسُّجُود عِنْدِي أَشد من الرُّكُوع لِأَنَّهُ ينفتح
<span data-type="title" id=toc-108>حكم الْوضُوء من الدَّم</span>

١٨٧ - قلت الرجل يَتَخَلَّل فيبصق فَيرى فِي بصاقه الدَّم وَرُبمَا كَانَ نصف بصاقه دَمًا أَو أقل أَو أَكثر قَالَ الَّذِي أذهب إِلَيْهِ فِي الدَّم أَنه لَا يتَوَضَّأ من الدَّم حَتَّى يفحش عِنْده لِأَنَّهُ يرْوى عَن ابْن عَبَّاس أَنه قَالَ إِذا كَانَ فَاحِشا أعَاد
<span data-type="title" id=toc-109>حكم الْعتْق قبل الْملك وَالطَّلَاق قبل النِّكَاح</span>

١٨٨ - وَقَالَ أبي الْعتْق قبل الْملك لَا أجتريء عَلَيْهِ لَا يقوم عِنْدِي مقَام الطَّلَاق','1','247',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (125,'١٨٩ - قلت وَالرجل يَقُول كل امْرَأَة يَتَزَوَّجهَا فَهِيَ طَالِق قَالَ الطَّلَاق قبل النِّكَاح وَقت أَو عَم إِذا كَانَ قد تزوج لم آمره أَن يُفَارق وَإِن كَانَ لَهُ والدان فأمراه بِالتَّزْوِيجِ أَمرته أَن يتَزَوَّج أَو خَافَ على نَفسه الْعَنَت أَمرته أَن يتَزَوَّج','1','248',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (126,'<span data-type="title" id=toc-110>مَسْأَلَة فِي تَارِيخ قصَّة ذِي الْيَدَيْنِ</span>

١٩٠ - وَسَأَلت أبي قلت قصَّة ذِي الْيَدَيْنِ كَانَت قبل بدر أَو بعد بدر','1','249',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (127,'فَقَالَ أَبُو هُرَيْرَة يحكيه وَإِنَّمَا كَانَ إِسْلَامه بعد بدر عِنْد فتح خَيْبَر وَإِنَّمَا صحب النَّبِي صلى الله عَلَيْهِ وَسلم ثَلَاث سِنِين وشيئا','1','251',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (128,'<span data-type="title" id=toc-111>حكم اسْتِعْمَال الصُّور</span>

١٩١ - وَسَأَلت أبي عَن قوم يرخصون فِي الصُّور وَيَقُولُونَ كَانَ نقش خَاتم سُلَيْمَان عَلَيْهِ السَّلَام فِيهِ صوره وَغَيره فَقَالَ أبي إِنَّمَا هَذِه الخواتيم كَانَت نقشت فِي الْجَاهِلِيَّة والصور لَا يَنْبَغِي لبسهَا لما رُوِيَ فِيهِ عَن النَّبِي صلى الله عَلَيْهِ وَسلم من صور صُورَة كلف أَن ينْفخ فِيهَا الرّوح وَلَيْسَ بنافخ وعذب وَقد قَالَ إِبْرَاهِيم أصَاب أَصْحَابنَا خمائص فِيهَا صلب فَجعلُوا يضربونها بِالسِّوَاكِ','1','252',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (129,'يمحونها بذلك وَفِي حَدِيث أبي طَلْحَة رَضِي الله عَنهُ أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ لَا تدخل الْمَلَائِكَة بَيْتا فِيهِ كلب وَلَا صُورَة','1','253',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (130,'<span data-type="title" id=toc-112>مسَائِل فِي الْوَصِيَّة</span>

١٩٢ - وَسَأَلته عَن رجل أوصى أَن أخرجُوا ثلث مَالِي بعد قَضَاء الدّين وَيكفر عني مائَة يَمِين وأعتقوا عني رَقَبَة وَيحمل على فرس فِي سَبِيل الله وَمَا بَقِي إِن عرف أحد من غرام وَالِدي قضي وَإِن لم يعرف مِنْهُم أحد فليعمل الْوَصِيّ فِي ذَلِك بِمَا رأى ويفرقه فِي قَرَابَتي إِن شَاءَ الله قَالَ أبي أما كَفَّارَة الْيَمين فَيعْطى الْمَسَاكِين كل مِسْكين مُدبر أَو نصف صَاع تمر لَا يزادون عَلَيْهِ وَإِن كَانَ الدَّقِيق أسهل عَلَيْهِم فليعطوا رطلا وَثلثا دَقِيقًا وَلَا يزادون على ذَلِك وَأَرْجُو أَن يجزيهم ذَلِك وأكره','1','254',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (131,'الْقيمَة لِأَنَّهُ خلاف كتاب الله وَمَا عمل بِهِ أَصْحَاب رَسُول الله صلى الله عَلَيْهِ وَسلم وَأما مَا أوصى بِهِ من غُرَمَاء أَبِيه بعد مَا يفضل فَإِن عرف مِنْهُم أحدا فَأَقَامَ الْبَيِّنَة أعطي فَإِن لم يكن لَهُ شَاهِدَانِ وَكَانَ لَهُ شَاهد وَاحِد اسْتحْلف مَعَ الشَّاهِد وَأعْطِي وَإِن كَانَ رجل من أهل السّتْر','1','255',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (132,'والصدق عِنْدهم فَإِنِّي أحب للْوَرَثَة أَن يمضوا مَا أوصى بِهِ وَلَا يلْزمهُم ذَلِك لِأَن هَذَا لَيْسَ علما وَإِنَّمَا هِيَ دَعْوَى فَإِن كَانَ فيهم صَغِير فَلَا يجوز عَلَيْهِ وَأما الْكِبَار فَأحب لَهُم أَن ينفذوا مَا أوصى بِهِ وَأما الرَّقَبَة فَيعتق رجل يعتمل وَلَا يعْتق عَنهُ إِلَّا من يعتمل يكون وسطا لَيْسَ بالمرتفع الثّمن وَلَا المنخفض','1','256',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (133,'ويحملوا على فرس فِي سَبِيل الله وَلَا يغالوا بِهِ إِذا كَانَ يغزا على مثله اشْترى وَحمل عَلَيْهِ بِغَيْر أَدَاة لِأَنَّهُ لم يسم الأداة وَقَالَ لَا يعْطى أحد من قرَابَته من كَفَّارَة الْأَيْمَان إِذا كَانَ قد أوصى لَهُم وَإِن لم يكن نالهم من الْوَصِيَّة شَيْء أعْطى من كَانَ مِنْهُم فَقِيرا من كَفَّارَة الْأَيْمَان قَالَ أبي وَأما مَا كَانَ من الْوَصِيَّة لِقَرَابَتِهِ فَلْينْظر إِلَى فعله فِي حَيَاته فَإِن كَانَ يصل الْغَنِيّ مِنْهُم وَالْفَقِير فِي حَيَاته أعْطوا جَمِيعًا وَإِلَّا فَإِن الْفُقَرَاء عِنْدِي أولى بِهِ لِأَنَّهُ لم يكن يصل الْأَغْنِيَاء','1','257',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (134,'وَلَا يعْطى أحدا من قرَابَته مرَّتَيْنِ
<span data-type="title" id=toc-113>مَسْأَلَة فِي الْحيض</span>

١٩٣ - سَأَلت أبي عَن إمرأة يكون طهرهَا ثَلَاثَة أشهر وَأَقل من ذَلِك وشهرين وَأَقل من ذَلِك ثمَّ رَأَتْ الدَّم فِي عشر فَكيف تصنع قَالَ أبي إِذا كَانَت لَهَا أَيَّام مَعْلُومَة فَإِنَّهَا تقعد تِلْكَ الْأَيَّام فَإِن زَاد على أَيَّامهَا لم تلْتَفت إِلَى الزِّيَادَة وَإِن كَانَ حَيْضهَا تقدم مرّة وَتَأَخر أُخْرَى فَإِنَّهَا تقعد أَيَّامهَا الَّتِي كَانَت تقعد فَإِن زَاد حَيْضهَا على أَيَّامهَا الَّتِي كَانَت تعرف وعاودها فَإِنَّهَا لَا تلْتَفت إِلَى الزِّيَادَة حَتَّى ترى مرّة ومرتين وَثَلَاثًا وَهَذَا حِينَئِذٍ حيض متنقل فَإِن كَانَت صَامت فِي تِلْكَ الْأَيَّام الَّتِي زَادَت على حَيْضهَا وأيامها قضته','1','258',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (135,'<span data-type="title" id=toc-114>حكم الصَّلَاة بَين الأساطين</span>

١٩٤ - وَسَأَلته عَن الصَّلَاة بَين الأساطين فَقَالَ تكره الصَّلَاة بَينهَا
<span data-type="title" id=toc-115>الْأَشْيَاء الَّتِي تجتنبها الْمُتَوفَّى عَنْهَا زَوجهَا والمطلقة ثَلَاثًا</span>

١٩٥ - وَسَأَلته عَن الْمُطلقَة مَا تجتنب من اللبَاس وَالطّيب والزينة والمتوفى عَنْهَا زَوجهَا فَقَالَ الْمُتَوفَّى عَنْهَا زَوجهَا والمطلقة ثَلَاثًا يجتنبان الطّيب والزينة','1','259',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (136,'<span data-type="title" id=toc-116>إِذا أعتق مَمْلُوك وَله مَال لمن يكون مَاله</span>

١٩٦ - وَسَأَلته عَن مَمْلُوك أعتق وَله مَال لمن يكون مَاله قَالَ إِذا أعتق الْمَمْلُوك وَله مَال فَالْمَال للسَّيِّد
<span data-type="title" id=toc-117>حكم بيع الْوَقْف وَالرُّجُوع فِيهِ</span>

١٩٧ - وَسَأَلته عَن رجل أوقف ضَيْعَة على أهل بَيته هَل يجوز لَهُ الرُّجُوع فِيهَا بعد سنة أَو أقل أَو أَكثر وَهل يَبِيعهَا','1','260',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (137,'فَقَالَ لَا يجوز بيع الْوَقْف إِذا كَانَ قَالَ فِي وَقفه لَا يُبَاع وَلَا يُورث فَلَيْسَ لأحد أَن يرجع
<span data-type="title" id=toc-118>حكم نتف اللِّحْيَة</span>

١٩٨ - وَسَأَلته عَن رجل قد بلي بنتف لحيته وَقطع ظفره بِيَدِهِ لَيْسَ يصبر عَنْهُمَا قَالَ إِن صَبر على ذَلِك فَهُوَ أحب إِلَيّ','1','261',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (138,'<span data-type="title" id=toc-119>حكم إِخْرَاج قيمَة الْمُوصى بِهِ</span>

١٩٩ - وَسَأَلته عَن رجل أوصى أَن يتَصَدَّق عَنهُ فِي فُقَرَاء مَسْجده طَعَام أَو حِنْطَة بِأَلف دِرْهَم هَل يجوز للْوَصِيّ أَن يُعْطي عَنهُ فضَّة بِقِيمَة الْألف قَالَ أبي لَا يُعْطي إِلَّا مَا قد أوصى بِهِ والوصايا يَنْتَهِي فِيهَا إِلَى مَا أوصى الْمُوصي لَا يتَعَدَّى ذَاك
<span data-type="title" id=toc-120>حكم الزواج والتسري بابنة امْرَأَة وَطئهَا أَبوهُ</span>

٢٠٠ - وَسَأَلته عَن رجل كَانَت لَهُ أمة يَطَأهَا فَزَوجهَا مَمْلُوكا لَهُ فَولدت مِنْهُ جَارِيَة هَل يجوز لمولاها أَن يهب هَذِه الْجَارِيَة لبَعض بنيه يتسرى بهَا قَالَ أبي أما أَكثر الْفُقَهَاء فَلَا يرَوْنَ بَأْسا أَن يتَزَوَّج الرجل ابْنة امْرَأَة كَانَ أَبوهُ وَطئهَا إِلَّا طَاوُوس فَإِنَّهُ كَانَ يكره إِذا وطيء الرجل الْمَرْأَة أَن يُزَوّج ابْنه ابْنَتهَا وَمَا كَانَ بعد فَلَا بَأْس بِهِ وَقد يكون الرجل يتَزَوَّج الْمَرْأَة ويتزوج ابْنه ابْنَتهَا وَمَا كَانَ بعد فَلَا بَأْس بِهِ وَلم يخْتَلف النَّاس فِي ذَلِك','1','262',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (139,'<span data-type="title" id=toc-121>حكم تجصيص الْقُبُور وتطيينها</span>

٢٠١ - وَسَأَلته عَن تطيين الْقُبُور وتجصيصها فَقَالَ أما التجصيص فمكروه والتطيين أسهل
<span data-type="title" id=toc-122>حكم صَدَقَة أحد الشُّرَكَاء بِحِصَّتِهِ مشَاعا غير مقسوم</span>

٢٠٢ - وَسَأَلته عَن رجل بَينه وَبَين قوم بَيت مشَاع غير مقسوم فَتصدق أحدهم على بَعضهم حِصَّته مشَاعا غير مقسوم هَل يجوز ذَلِك','1','263',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (140,'قَالَ أبي إِذا كَانَ سهم من كَذَا وَكَذَا سهم فَهُوَ جَائِز
<span data-type="title" id=toc-123>من نفر من منى وَقدم ثقله ثمَّ ودع الْبَيْت وَلحق بهم</span>

وَسَأَلته عَن رجل نفر من منى ثمَّ قدم ثقله ميلًا أَو ميلين أَو أقل من ذَلِك أَو أَكثر ثمَّ ودع الْبَيْت وَلحق بهم هَل يجوز لَهُ ذَلِك قَالَ أبي إِذا خرج ثقله من منى فَلَا بَأْس أَن ينفر وَإِن كَانَ تَأَخّر خُرُوجه من مَكَّة','1','264',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (141,'<span data-type="title" id=toc-124>حكم الزواج فِي حق من لَا يجد أَكثر من قوته</span>

٢٠٤ - وَسَأَلته عَن رجل يعْمل الخوص قوته وَلَيْسَ يُصِيب مِنْهُ أَكثر من قوته هَل يقدم على التَّزْوِيج قَالَ أبي يقدم على التَّزْوِيج فَإِن الله يَأْتِي برزقها وَقَالَ ويتزوج ويستقرض أَيْضا وَإِن كَانَ عِنْده مِائَتَا دِرْهَم تبلغه الْحَج وَخَافَ على نَفسه الْفِتْنَة أَمرته أَن يتَزَوَّج وَلَا يحجّ','1','265',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (142,'<span data-type="title" id=toc-125>حكم إِنْفَاق الدَّرَاهِم الزائفة</span>

٢٠٥ - وَسَأَلته عَن الرجل يدْفع إِلَيْهِ أَبَوَاهُ الدَّرَاهِم الزائفة والمزيفة ويأمرانه بإنفاقها أَيجوزُ لَهُ ذَلِك قَالَ أبي لَا يجوز لَهُ إنفاقها
<span data-type="title" id=toc-126>حكم من يتْرك الْوتر مُتَعَمدا</span>

٢٠٦ - وَسَأَلته عَن الرجل يتْرك الْوتر مُتَعَمدا مَا عَلَيْهِ فِي ذَلِك قَالَ أبي هَذَا رجل سوء هُوَ سنة سنّهَا رَسُول الله صلى الله عَلَيْهِ وَسلم وَأَصْحَابه','1','266',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (143,'<span data-type="title" id=toc-127>مَسْأَلَة فِي الْأَحَادِيث الَّتِي تروى أَن الْوتر على من يقْرَأ الْقُرْآن</span>

٢٠٧ - قلت لأبي هَذِه الْأَحَادِيث الَّتِي تروى أَن الْوتر على من يقْرَأ الْقُرْآن أفيكون من لَا يقْرَأ الْقُرْآن فِي الْوتر كمن يقْرَأ الْقُرْآن قَالَ إِنَّمَا يرْوى هَذَا مُرْسلا لَيْسَ هُوَ بأسناد جيد يرْوى عَن عَليّ قَالَ هِيَ سنة سنّهَا رَسُول الله صلى الله عَلَيْهِ وَسلم','1','267',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (144,'<span data-type="title" id=toc-128>الشّعبِيّ وَالزهْرِيّ أَيهمَا أعلم</span>

٢٠٨ - وَسَأَلته عَن الشّعبِيّ وَالزهْرِيّ إِذا اخْتلفَا أَيهمَا أعجب إِلَيْك وَأيهمَا أعلم قَالَ لَا أَدْرِي لَا أحد هَذَا كِلَاهُمَا عَالم قد يكون الزُّهْرِيّ سمع من النَّبِي صلى الله عَلَيْهِ وَسلم الحَدِيث فَيذْهب إِلَيْهِ فَهُوَ أعجب إِلَيْنَا أَو يكون الشّعبِيّ قد سمع الحَدِيث وَلم يسمعهُ الزُّهْرِيّ وَهُوَ أعجب إِلَيْنَا
<span data-type="title" id=toc-129>هَل يتْرك رفع الْيَدَيْنِ إِذا صلى عِنْد قوم ينكرونه</span>

٢٠٩ - وَسَأَلته عَن رجل يبْلى بِأَرْض يُنكرُونَ فِيهَا رفع الْيَدَيْنِ فِي الصَّلَاة وينسبون إِلَيْهِ الرَّفْض إِذا فعل ذَلِك هَل يجوز لَهُ ترك الرّفْع قَالَ أبي لَا يتْرك وَلَكِن يداريهم','1','268',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (145,'<span data-type="title" id=toc-130>حكم التَّكْبِير عقب كل صَلَاة يَوْم النَّحْر</span>

٢١٠ - وَسَأَلته عَن النَّاس يكبرُونَ فِي دبر كل صَلَاة يَوْم النَّحْر كَمَا يكبرُونَ فِي الْمَكْتُوبَة أم لَا قَالَ أبي إِن ذهب رجل إِلَى ذَا فقد رُوِيَ ذَاك عَن بعض النَّاس وَالْمَعْرُوف فِي الْمَكْتُوبَة','1','269',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (146,'<span data-type="title" id=toc-131>حكم الطّواف للْمحرمِ فِي ليَالِي منى</span>

٢١١ - وَسَأَلته عَن الرجل يَأْتِي الْبَيْت فِي أَيَّام منى فيطوف فِي كل لَيْلَة وَيرجع هَل يسْتَحبّ لَهُ ذَلِك أَن يسْتَحبّ لَهُ الْمقَام بمنى قَالَ أبي لَا بَأْس أَن يَأْتِي الْبَيْت إِذا كَانَ أحد طرفِي اللَّيْل بمنى','1','270',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (147,'<span data-type="title" id=toc-132>من ترك صَدَقَة الْفطر عمدا</span>

٢١٢ - وَسَأَلته عَن رجل ترك صَدَقَة الْفطر على عمد مَا عَلَيْهِ فِي ذَلِك فَقَالَ لَا يعجبنا تَركهَا قَالَ ابْن عمر فرض رَسُول الله صلى الله عَلَيْهِ وَسلم صَدَقَة الْفطر
<span data-type="title" id=toc-133>حكم نظر الزَّوْج محَاسِن زَوجته بعد مَوتهَا وإدخالها فِي الْقَبْر</span>

٢١٣ - وَسَأَلته عَن رجل مَاتَت امْرَأَته هَل يجوز لَهُ أَن ينظر إِلَى شَيْء من محاسنها ويدخلها قبرها قَالَ أبي النَّاس يَخْتَلِفُونَ فِي هَذَا وَقد رُوِيَ عَن عمر أَنه قَالَ فِي امْرَأَته','1','271',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (148,'لما توفيت فَقَالَ لأوليائها أَنْتُم أَحَق بهَا وَرُوِيَ عَن أبي بكرَة أَنه واثب إخْوَة امْرَأَته حَتَّى أدخلها الْقَبْر','1','272',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (149,'<span data-type="title" id=toc-134>امْرَأَة تَدعِي الطَّلَاق وَلَيْسَ لَهَا بَيِّنَة وَالزَّوْج يُنكر ذَلِك</span>

٢١٤ - وَسَأَلته عَن امْرَأَة ادَّعَت أَن زَوجهَا طَلقهَا وَلَيْسَ لَهَا بَيِّنَة وَزوجهَا يُنكر ذَلِك قَالَ أبي القَوْل قَول الزَّوْج إِلَّا أَن تكون لَا تشك فِي طَلَاقه قد سمعته طَلقهَا ثَلَاثًا فَإِنَّهُ لَا يَسعهَا الْمقَام مَعَه وتهرب مِنْهُ وتفتدي بمالها
<span data-type="title" id=toc-135>لون الْكَفَن الْمُسْتَحبّ</span>

٢١٥ - وَسَأَلته عَن الْكَفَن الْبيَاض أعجب إِلَيْك أَو غير ذَلِك قَالَ يُقَال إِن النَّبِي صلى الله عَلَيْهِ وَسلم كفن فِي ثَلَاثَة أَثوَاب سحُولِيَّة بيض لَيْسَ','1','273',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (150,'فِيهَا قَمِيص وَلَا عِمَامَة وَهَذَا أثبت مَا رُوِيَ
<span data-type="title" id=toc-136>حكم الْفَصْل بَين سورتي الْأَنْفَال وَالتَّوْبَة بالبسملة</span>

٢١٦ - وَسَأَلته عَن سُورَة الْأَنْفَال وَسورَة التَّوْبَة هَل يجوز للرجل أَن يفصل بَينهمَا بِبسْم الله الرَّحْمَن الرَّحِيم قَالَ أبي ينتهى فِي الْقُرْآن إِلَى مَا أَجمعُوا عَلَيْهِ أَصْحَاب مُحَمَّد عَلَيْهِ السَّلَام لَا يُزَاد فِيهِ وَلَا ينقص','1','274',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (151,'<span data-type="title" id=toc-137>حكم تصرف الْأَب فِي مَال ابْنه بِالْهبةِ أَو الْعتْق أَو الصَّدَقَة وَنَحْوهَا</span>

٢١٧ - وَسَأَلته عَن الرجل هَل يجوز لَهُ أَن يتَصَدَّق من مَال ابْنه أَو يهب أَو يَبِيع على ابْنه أَو يعْتق عَلَيْهِ قَالَ أبي كل مَا أَخذ الرجل من مَال ابْنه حَتَّى يحوزه فَهُوَ لَهُ وَأحب أَن لَا يكون ذَلِك على الْإِضْرَار وَقد رُوِيَ عَن الْحسن وَابْن أبي ليلى أَنَّهُمَا كَانَا يجيزان عتق الرجل فِي مَال ابْنه وَخَالفهُم غَيرهم','1','275',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (152,'<span data-type="title" id=toc-138>إِذا أوصى بِجَمِيعِ مَا وَرثهُ عَن أَبِيه هَل يدْخل فِيهِ الْمُصحف وَالثيَاب وَنَحْوهَا</span>

٢١٨ - وَسَأَلته عَن رجل أوصى فَقَالَ ادفعوا إِلَى فُلَانَة جَمِيع مَا ورثته عَن أبي من مَتَاع الْبَيْت وَهُوَ من الثُّلُث هَل يدْخل فِيهِ الْمُصحف والصفد وَالصُّوف وَثيَاب الْبدن قَالَ أبي كل شَيْء وَرثهُ عَن أَبِيه يفعل بِهِ كَمَا قَالَ وَيكون ذَلِك فِي ثلثه إِذا لم يكن لَهُ وَارِث','1','276',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (153,'<span data-type="title" id=toc-139>حكم الْأَذَان قبل طُلُوع الْفجْر وَقبل الزَّوَال</span>

٢١٩ - وَسَأَلته عَن رجل أذن قبل زَوَال الشَّمْس وَأقَام بعد زَوَال الشَّمْس وَأذن قبل طُلُوع الْفجْر وَأقَام بعد طُلُوع الْفجْر قَالَ أما الْأَذَان قبل طُلُوع الْفجْر فَلَا بَأْس بِهِ إِذا كَانَت الْإِقَامَة بعد طُلُوع الْفجْر وَأما قبل الزَّوَال فَلَا','1','277',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (154,'<span data-type="title" id=toc-140>حكم الْعتْق والزواج فِي الْمَرَض</span>

٢٢٠ - وَسَأَلته عَن رجل كَانَت لَهُ سريتان فَمَرض حَتَّى اشْتَدَّ مَرضه وَصَارَ فِي حد ترك فِيهِ الصَّلَاة فَدَعَا قوما فأشهدهم أَنه أعتقهما وتزوجهما على مهر كَذَا وَكَذَا هَل يجوز لَهُ ذَلِك قَالَ إِن كَانَ تَزْوِيجه إيَّاهُمَا بِمهْر أَكثر من مهر مثلهمَا فَإِن الزِّيَادَة تكون فِي ثلثه وعتقهما من الثُّلُث
<span data-type="title" id=toc-141>زَكَاة الْحُبُوب ومقدارها</span>

٢٢١ - وَسَأَلته عَن الْحُبُوب مَا زَكَاتهَا فَقَالَ أما مَا كَانَ من الْخضر الَّتِي لَا تبقى وَلَا تدخر وَلَا يَقع فِيهَا القفيز فَلَا زَكَاة إِلَّا فِي أثمانها وَأما مَا كَانَ يدّخر أَو يَقع فِيهِ القفيز','1','278',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (155,'حَتَّى يكون مَعْنَاهُ معنى الْبر وَالتَّمْر وَالزَّبِيب وَالشعِير فَفِيهِ الْعشْر إِذا كَانَ يسقى بِمَاء تَأتي بِهِ السَّمَاء وَمَا كَانَ بالكلفة فَنصف الْعشْر إِذا كَانَ الرجل يملك رَقَبَة الأَرْض','1','279',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (156,'<span data-type="title" id=toc-142>حَدِيث من أقرّ بالخراج وَهُوَ قَادر على أَن لَا يقربهُ فَعَلَيهِ لعنة الله الخ</span>

٢٢٢ - وَسَأَلته عَن حَدِيث رَوَاهُ نصير بن مُحَمَّد الرَّازِيّ صَاحب ابْن الْمُبَارك عَن عُثْمَان بن زَائِدَة عَن الزبير بن عدي عَن أنس بن مَالك رَفعه قَالَ من أقرّ بالخراج وَهُوَ قَادر على أَن لَا يقربهُ فَعَلَيهِ لعنة الله وَالْمَلَائِكَة وَالنَّاس أَجْمَعِينَ لَا يقبل الله مِنْهُ صرفا وَلَا عدلا فَقَالَ مَا سمعنَا بِهَذَا هَذَا حَدِيث مُنكر وَقد رُوِيَ عَن ابْن عمر أَنه كَانَ يكره','1','280',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (157,'الدُّخُول فِي الْخراج وَقَالَ إِنَّمَا كَانَ الْخراج على عهد عمر
<span data-type="title" id=toc-143>من أَحَق بِزَكَاة الرجل أهل بَيته الَّذين لَا يؤدون الْفَرَائِض أم جِيرَانه المتدينون</span>

٢٢٣ - وَسَأَلته عَن رجل لَهُ أهل بَيت لَا يُقِيمُونَ الصَّلَوَات وَلَا يعْرفُونَ السّنَن والفرائض وَفِي جِيرَانه قوم يُقِيمُونَ الصَّلَاة والفرائض وَالسّنَن أيضع زَكَاة مَاله فِي جِيرَانه هَؤُلَاءِ أَو فِي أهل بَيته قَالَ يَنْبَغِي لَهُ أَن يعلمهُمْ الْفَرَائِض وَالسّنَن وزكاته هم أولى بهَا','1','281',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (158,'حِينَئِذٍ وَإِذا كَانَت حَاجتهم وحاجة غَيرهم سَوَاء فالقرابة أولى وَيُقَال لَا يحابى بهَا قريب وَلَا تمنع من بعيد وَإِنَّمَا هُوَ حق الله فِي المَال
<span data-type="title" id=toc-144>حكم إِقْرَار الدّين للْبِنْت عِنْد الْوَفَاة</span>

٢٢٤ - سَأَلت أبي عَن رجل قَالَ عِنْد وَفَاته لفلانة ابْنَتي عَليّ ألفا دِرْهَم وَسَبْعمائة دِرْهَم هَل يجوز ذَلِك قَالَ إِن كَانَ يعرف ذَلِك أَو كَانَ لَهَا بَيِّنَة فِي حَيَاة مِنْهُ وَصِحَّة فلهَا ذَاك وَإِلَّا فَلَا يجوز','1','282',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (159,'<span data-type="title" id=toc-145>كَفَّارَة الْحلف بِالْقُرْآنِ</span>

٢٢٥ - وَسمعت أبي يَقُول إِذا حلف الرجل بِالْقُرْآنِ فقد رُوِيَ عَن الْحسن عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه قَالَ من حلف بِسُورَة من الْقُرْآن فبكل آيَة مِنْهَا يَمِين صَبر وَرُوِيَ ذَلِك عَن عبد الله بن مَسْعُود وَإِبْرَاهِيم النَّخعِيّ','1','283',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (160,'<span data-type="title" id=toc-146>من حلف بِهَدي مَا لَا يقدر عَلَيْهِ من إِنْسَان وَنَحْوه</span>

٢٢٦ - قَالَ سَمِعت أبي يَقُول من حلف بِهَدي مَا لَا يقدر عَلَيْهِ من إِنْسَان أَو دَار أَو غير ذَلِك قَالَ أبي فَإِن كَانَ حلف يُرِيد الْيَمين مثل قَوْله مَاله فِي الْمَسَاكِين أَو هُوَ يهدي فلَانا فأرجو أَن يجْزِيه كَفَّارَة يَمِين إِذا كَانَ يُرِيد الْيَمين وَقد قَالَ بعض النَّاس بِحجَّة وَقَالَ بعض النَّاس يهدي','1','284',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (161,'بَدَنَة وَقَالَ بَعضهم شَاة
<span data-type="title" id=toc-147>من حلف بِشَيْء يَظُنّهُ كَمَا حلف فَبَان خِلَافه</span>

٢٢٧ - @سَأَلت أبي عَن رجل حلف بِالطَّلَاق مَا فعل كَذَا وَكَذَا وَمَا فِي نِيَّته كَذَا وَكَذَا وَهُوَ يرى أَنه على مَا حلف وَنسي وَكَانَ على خلاف مَا حلف أيلزمه الطَّلَاق قَالَ أبي لَو كَانَ هَذَا الْحَالِف حلف بِمَا يكفر كنت أَرْجُو أَن لَا تلْزمهُ الْكَفَّارَة فَأَما الطَّلَاق وَالْعتاق فَإِنَّهُمَا لَا يكفران وأخاف أَن يكون هَذَا حانثا فِيمَا حلف عَلَيْهِ
<span data-type="title" id=toc-148>من الَّذِي يعْطى من الزَّكَاة وَكم يعْطى</span>

٢٢٨ - قَالَ سَمِعت أبي يَقُول لَا يعْطى من الزَّكَاة أَكثر من خمسين درهما وَلَا يعْطى من عِنْده خَمْسُونَ درهما أَو قيمتهَا ذَهَبا إِلَّا أَن يكون رجلا مديونا فَيعْطى عَن دينه وَإِن كَانَ لَهُ عِيَال أعطي على كل عيل خمسين','1','285',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (162,'<span data-type="title" id=toc-149>الحكم إِذا عَفا عَن الْقَاتِل بعض أَوْلِيَاء الْمَقْتُول</span>

٢٢٩ - سَأَلت أبي عَن رجل قتل رجلا فَعَفَا بعض الْأَوْلِيَاء للباقين أَن يقتلوه قَالَ إِذا عَفا بعض الْأَوْلِيَاء عَن الدَّم فَلَيْسَ للباقين أَن يقتلوه وَلَهُم الدِّيَة وَلَيْسَ للعافي من الدِّيَة شَيْء','1','286',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (163,'<span data-type="title" id=toc-150>حكم ربح مَال الْخِيَانَة وَالْمَغْصُوب</span>

٢٣٠ - سَأَلت أبي عَن رجل اغتصب قوما مَالا ثمَّ تَابَ ورد المَال وَكسب فِيهِ مَالا مَا ترى فِي كَسبه هَذَا أيطيب لَهُ هَذَا الرِّبْح قَالَ أبي إِذا غصب رجل رجلا مَالا ثمَّ ربح فِيهِ رد الأَصْل وَالرِّبْح على صَاحبه
٢٣١ - سَأَلت أبي عَن رجل خَان قوما بِمَال وَكسب فِيهِ مَالا ورد الْخِيَانَة أيطيب لَهُ الرِّبْح قَالَ أبي يرد الْخِيَانَة وربحها على أَرْبَابهَا','1','287',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (164,'<span data-type="title" id=toc-151>كَيفَ يصنع فِي المَال الْمَأْخُوذ ظلما إِذا مَاتَ صَاحبه وَلم يعرف لَهُ وَارِث</span>

٢٣٢ - سَأَلت أبي عَن رجل ظلم قوما مَالا وَقد تَابَ وَهُوَ يُرِيد رده وَقد مَاتُوا هَؤُلَاءِ الْقَوْم وَلَا وَرَثَة لَهُم وَلَا يعرف الَّذين ظلمهم كَيفَ يصنع قَالَ إِذا كَانَ لَا يعرف من ظلم وَلَا يعرف لَهُ وَارِثا تصدق بِهِ
<span data-type="title" id=toc-152>حكم لُحُوق ولد الْأمة بسيدها إِذا كَانَ يعْزل عَنْهَا</span>','1','288',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (165,'٢٣٣ - سَأَلت أبي عَن جَارِيَة كَانَ عزل عَنْهَا سَيِّدهَا فَأخذت مَاءَهُ وَهُوَ لَا يعلم فَحَملته فَحملت من ذَلِك المَاء أيلحق بِهِ الْوَلَد أَو هَل يجوز لَهُ أَن يَدعِيهِ قَالَ أبي يلْزمه الْوَلَد إِذا كَانَ يعْزل عَنْهَا','1','233',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (166,'<span data-type="title" id=toc-153>الحكم فِي ولد جَارِيَة وَطئهَا سَيِّدهَا بِدُونِ اسْتِبْرَاء بعد شِرَائهَا</span>

٢٣٤ - وَسَأَلته عَن رجل اشْترى جَارِيَة وَلم يَسْتَبْرِئهَا وطيها فَجَاءَت بِولد وَقد شكّ المُشْتَرِي أَن يكون مِنْهُ أَو من الأول قَالَ أبي إِن كَانَت جَاءَت بِهِ لأَقل من سِتَّة أشهر فَلَيْسَ هُوَ لَهُ بِولد وَلَا يلْحق بِهِ وَإِن جَاءَت بِهِ لأكْثر من سِتَّة أشهر فقد يكون الْوَلَد لَهُ وَللْبَائِع فيدعى للْوَلَد الْقَافة فيلحقون بِمن كَانَ لَهُ','1','289',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (167,'<span data-type="title" id=toc-154>حكم بط المَاء والدبيلة والفصد</span>

٢٣٥ - وَسَأَلته عَن الرجل بِهِ المَاء والدبيلة أيبط عَنْهُمَا وَمَا ترى فِي الفصد وَشرب الْأَدْوِيَة قَالَ أما مَا كَانَ يخَاف عَلَيْهِ مثل المَاء إِن بط عَنهُ مَاتَ فَلَا أرى لِأَن يبط عَنهُ وَلَا بَأْس بِقطع الْعُرُوق إِذا احْتِيجَ إِلَى ذَلِك','1','290',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (168,'<span data-type="title" id=toc-155>مَسْأَلَة فِي بيع مَتَاع من مَاتَ فِي أَرض غربَة</span>

٢٣٦ - وَسَأَلت أبي عَن رجل مَاتَ فِي أَرض غربَة لَا قَاضِي فِيهَا وَخلف جواري ومالا وثيابا أَتَرَى أَن يقوم بِهِ رجل من الْمُسلمين فيبيع الْجَوَارِي وَالثيَاب وَيُؤَدِّي فِيهِ الْأَمَانَة وَإِن كَانَ مَاتَ فِي طَرِيق قَالَ أما مَا كَانَ من مَتَاع خرثي أَو حَيَوَان لَيْسَ بجوار واضطر إِلَى بَيْعه وَلم يكن بحضرتهم قَاض فَلَا أرى بَأْسا أَن يُبَاع إِذا استوفى الثّمن وَأدّى فِيهِ الْأَمَانَة وَأما الْجَوَارِي فَأحب إِلَيّ أَن يكون يَلِي بيعهنَّ حَاكم من حكام الْمُسلمين','1','291',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (169,'<span data-type="title" id=toc-156>مَسْأَلَة فِي السّلم</span>

٢٣٧ - وَسَأَلت أبي عَن السّلم فَقَالَ لَا بَأْس بالسلم فِي كيل مَعْلُوم وَوزن مَعْلُوم إِلَى أجل أَو صفة يصفها من نَبَات أَو حَيَوَان إِذا كَانَ يُؤْتى بِهِ على الصّفة فَلَا بَأْس بذلك إِذا كَانَ إِلَى أجل','1','292',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (170,'فَإِن كَانَ الْمُسلم فِيهِ خيرا من الصّفة فَلَا وَإِن كَانَ دون فَلَا بَأْس
<span data-type="title" id=toc-157>مُدَّة تَعْرِيف اللّقطَة</span>

٢٣٨ - حَدثنَا صَالح قَالَ سَأَلت أبي عَن اللّقطَة كم يعرفهَا فَقَالَ اللّقطَة إِذا كَانَت دَرَاهِم أَو ذَهَبا أَو فضَّة فَإِنَّهُ يعرفهَا سنة فَإِن جَاءَ صَاحبهَا دَفعهَا إِلَيْهِ فَإِن لم يَجِيء صَاحبهَا فَهِيَ كَسَائِر مَال هَذَا الْوَاجِد لَهَا فَإِن جَاءَ صَاحبهَا أَدَّاهَا إِلَيْهِ وَإِن كَانَت من الْإِبِل فَلَا يعرض لَهَا فَإِنَّهَا ترجع إِلَى أَرْبَابهَا','1','293',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (171,'<span data-type="title" id=toc-158>حكم بِنَاء الْمَسْجِد إِلَى جَانب مَسْجِد عِنْد الْحَاجة</span>

٢٣٩ - سَأَلت أبي كم يسْتَحبّ أَن يكون بَين المسجدين إِذا أَرَادوا أَن يبنوا إِلَى جَانِبه مَسْجِدا قَالَ لَا يَبْنِي مَسْجِدا يُرَاد بِهِ الضَّرَر لمَسْجِد إِلَى جَانِبه فَإِن كثر النَّاس حَتَّى يضيق عَلَيْهِم فَلَا بَأْس يَبْنِي وَإِن قرب ذَلِك مِنْهُ
<span data-type="title" id=toc-159>الْجَمَاعَة فِي مَسَاجِد الحوانيت</span>

٢٤٠ - سَأَلت أبي عَن الْمَسَاجِد الَّتِي فِي الحاناة أيجمع فِيهَا','1','294',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (172,'قَالَ إِذا كَانَ مَسْجِد يُنَادى فِيهِ بِالصَّلَاةِ فَلَا بَأْس بِالْجَمَاعَة فِيهِ إِذا كَانَ لَا يمْنَع مِنْهُ أحد
<span data-type="title" id=toc-160>حكم تَحْويل الْمَسْجِد إِلَى مَوضِع آخر</span>

٢٤١ - وَسَأَلته عَن رجل بنى مَسْجِدا ثمَّ أَرَادَ تحويله إِلَى مَوضِع آخر أَله أَن يحوله ويهدم الأول أَو يَدعه على حَاله وَيَبْنِي الآخر وَإِن كَانَ الَّذِي يبنيه ضَرَر بِالْأولِ مَا ترى قَالَ إِن كَانَ الْمَسْجِد الَّذِي بناه يُرِيد أَن يحوله خوفًا من لصوص أَو يكون مَوْضِعه مَوضِع قذر فَلَا بَأْس أَن يحوله يُقَال إِن بَيت المَال نقب وَكَانَ فِي الْمَسْجِد فحول الْمَسْجِد ابْن مَسْعُود','1','295',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (173,'<span data-type="title" id=toc-161>حكم جمع الْكَفَّارَات وَمِقْدَار مَا يعْطى كل مِسْكين مِنْهَا</span>

٢٤٢ - وَسَأَلته عَن رجل عَلَيْهِ كَفَّارَات أَيجوزُ لَهُ أَن يجمع عشرَة مَسَاكِين فيطعمهم عشر كَفَّارَات فِي يَوْم وَاحِد وَهل يعْطى كل مِسْكين فِي كل يَوْم أَكثر من مد قَالَ أبي إِذا كَانَ يجد مَسَاكِين فَأحب إِلَيّ أَن يعطيهم وَلَا يُكَرر عَلَيْهِم فَإِن ضَاقَ عَلَيْهِ فَلَا بَأْس أَن يجمع عشرَة فيعطيهم ليمينين لثَلَاثَة وَقَالَ كل من أكل من الطَّعَام يعْطى مدا
<span data-type="title" id=toc-162>حكم تَعْجِيل الزَّكَاة وصرفها للصغار</span>

٢٤٣ - وَقَالَ لَا بَأْس أَن يُعْطي من الزَّكَاة الصغار والكبار مِمَّن يَأْكُل الطَّعَام','1','297',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (174,'خمسين خمسين إِذا لم يكن لَهُم خَمْسُونَ درهما أَو قيمتهَا من الذَّهَب وَلَا بَأْس بتعجيل الزَّكَاة إِذا وجد لَهَا موضعا
<span data-type="title" id=toc-163>حكم الْحَج عَن الْمَيِّت بِالْأَجْرِ وَغَيره</span>

٢٤٤ - وَسَأَلت أبي عَن الْحَج عَن الْمَيِّت فَقَالَ لَا بَأْس بِأَن يعان فِي الْحَج وَأما يسْتَأْجر فَلم أسمع بِهِ','1','298',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (175,'<span data-type="title" id=toc-164>حكم الْهِبَة إِذا خص بهَا الْأَب بعض أبنائه وَمَات</span>

٢٤٥ - وَسَأَلت أبي عَن رجل خص ابْنا لَهُ بِهِبَة دون بَعضهم وَقد قَبضه الإبن وَمَات الْأَب أَتَرَى الْهِبَة مَاضِيَة قَالَ الَّذِي يعجبنا أَن لَا يخص ولدا دون ولد يُرِيد الْإِضْرَار ببعضهم دون بعض فَأَما إِذا مَاتَ الْوَاهِب على هبة قد قبضهَا الْمَوْهُوب لَهُ فَإِنِّي أحب الْعَافِيَة مِنْهَا','1','299',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (176,'<span data-type="title" id=toc-165>حكم الصَّلَاة فِي جُلُود الْميتَة وَالسِّبَاع</span>

٢٤٦ - سَمِعت أبي يَقُول كل مَا كَانَ من السبَاع فَإِنَّهُ لَا يعجبنا أَن يصلى فِي جلده وَإِن دبغ وَقَالَ جُلُود الْميتَة إِذا دبغت مِمَّا يُؤْكَل لَحْمه فَفِيهِ اخْتِلَاف فِي الرِّوَايَة وأعجب إِلَيّ أَن لَا يصلى فِيهِ
<span data-type="title" id=toc-166>حكم رُؤْيَة الْهلَال قبل الزَّوَال</span>

٢٤٧ - سَأَلته عَن قوم رَأَوْا الْهلَال لتَمام ثَلَاثِينَ قبل الزَّوَال قَالَ لَا يفطرون
<span data-type="title" id=toc-167>من حلف بِيَمِين وَلم يدر بِمَاذَا حلف</span>

٢٤٨ - سَأَلته عَن رجل حلف على يَمِين لَا يدْرِي بِمَاذَا حلف بِاللَّه أَو بِالطَّلَاق أَو بِالْمَشْيِ فَقَالَ لَو أَنه إِذا عرف اجتريت أَن أُجِيب فِيهَا فَكيف إِذا لم يدر','1','300',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (177,'<span data-type="title" id=toc-168>ذكر مِقْدَار المَاء الَّذِي لَا ينجس إِلَّا بِتَغَيُّر أحد الْأَوْصَاف</span>

٢٤٩ - وَقَالَ الَّذِي سمعنَا أَن المَاء إِذا كَانَ قدر قُلَّتَيْنِ أَو ثَلَاث لم ينجس والقلال قلال هجر وَيُقَال إِن الْقلَّة تسع نَحْو القربتين فَإِذا كَانَ المَاء خمس قرب سِتّ قرب كلما كَانَ أَكثر فَهُوَ أحب إِلَيْنَا لم يُنجسهُ إِلَّا مَا كَانَ غير طعمه أَو رِيحه فَإِذا تغير طعم أَو ريح أَو لون لم يقرب إِلَّا الْبَوْل والعذرة الرّطبَة الَّتِي تقع فِي المَاء فَلَا يقدر عَلَيْهَا فَإِن ذَلِك ينجس إِلَّا أَن تكون هَذِه المصانع الَّتِي فِي طَرِيق مَكَّة فَإِن ذَلِك لَا يُنجسهُ شَيْء','1','301',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (178,'<span data-type="title" id=toc-169>سُؤال عَن قَول إِبْرَاهِيم كَانَ يعجبهم حَدِيث جرير الخ</span>
سَأَلت أبي عَن قَول إِبْرَاهِيم كَانَ يعجبهم حَدِيث جرير لِأَن إِسْلَامه كَانَ بعد نزُول الْمَائِدَة فَقَالَ لقَوْل الله تبَارك وَتَعَالَى {وأرجلكم إِلَى الْكَعْبَيْنِ} وَكَانَت الْآيَة نزلت قبل إِسْلَامه','1','302',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (179,'<span data-type="title" id=toc-170>من توقف فِي النَّبِيذ</span>

٢٥١ - سَأَلته من قَالَ فِي النَّبِيذ شربه قوم على التَّأْوِيل وَتَركه قوم على التَّحْرِيم كَأَنَّهُ وقف فِي قَوْله قَالَ أبي لَا يُعجبنِي هَذَا القَوْل التَّحْرِيم أثبت عِنْدِي وَأقوى لَا يثبت عِنْدِي فِي تَحْلِيل الْمُسكر شَيْء','1','303',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (180,'<span data-type="title" id=toc-171>معنى حَدِيث إيَّاكُمْ والغلو</span>

٢٥٢ - وَسَأَلته عَن حَدِيث ابْن عَبَّاس إيَّاكُمْ والغلو فَإِنَّمَا أهلك من كَانَ قبلكُمْ الغلو قَالَ أبي لَا تغلوا فِي شَيْء حَتَّى الْحبّ والبغض
<span data-type="title" id=toc-172>تَفْسِير الشُّبُهَات</span>

٢٥٣ - وَسَأَلته عَن حَدِيث النُّعْمَان بن بشير من اتَّقى الشُّبُهَات اسْتَبْرَأَ لدينِهِ وَعرضه مَا الشُّبُهَات','1','304',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (181,'قَالَ الشُّبْهَة هِيَ منزلَة بَين الْحَلَال وَالْحرَام فَإِذا اسْتَبْرَأَ لدينِهِ لم يَقع فِيهَا
<span data-type="title" id=toc-173>حكم الْهَدِيَّة ليهدى إِلَيْهِ أَكثر</span>

٢٥٤ - قلت لَهُ حَدِيث يحدث بِهِ عَن عبد الله بن دَاوُد أَن الْهَدِيَّة لَا تحل لأحد بعد النَّبِي صلى الله عَلَيْهِ وَسلم وَلَا لأبي بكر وَعمر رَضِي الله عَنْهُمَا هَل تعرفه قَالَ لَا أعرفهُ وَأنْكرهُ وَقَالَ إِنَّمَا رُوِيَ عَن الضَّحَّاك {وَلَا تمنن تستكثر}','1','305',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (182,'قَالَ الضَّحَّاك إِنَّمَا هَذِه للنَّبِي صلى الله عَلَيْهِ وَسلم خَاصَّة أَن لَا يهدي ليهدى إِلَيْهِ أَكثر من ذَلِك وَأما سَائِر النَّاس فَلَيْسَ بِهِ بَأْس
<span data-type="title" id=toc-174>السّلم فِي اللَّحْم</span>

٢٥٥ - وَسَأَلته عَن قَول سُفْيَان كره السّلم فِي اللَّحْم مَا مَعْنَاهُ وَعَطَاء لَا يرى بِهِ بَأْسا','1','306',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (183,'قَالَ الَّذِي كره يَقُول لَا يَجِيء على الصّفة وَقَالَ أبي لَا بَأْس بِهِ إِذا كَانَ بِصفة سمين أَو غث أَو وسط لحم فَخذ أَو لحم جنب أَو غَيره
<span data-type="title" id=toc-175>حكم خل الْخمر</span>

٢٥٦ - وَسَأَلته عَن قَول عمر لَا يُؤْكَل خل من خمر أفسدت حَتَّى يكون الله بَدَأَ فَسَادهَا فأفسدها رجل هَل يكون سَوَاء أَو لَا يكون سَوَاء قَالَ أبي لَا يأكلها إِذا أفسدها وَذَلِكَ أَنه لَو جَازَ فَسَادهَا فانتقلت عَن اسْم الْخمر كَانَ يَجْعَلهَا فِي اللَّبن والكامخ والمرقة لِأَنَّهُ انْتقل اسْم الْخمر عَنْهَا وانتقلت عَن طباعها وَلَا يجوز فَسَادهَا حَتَّى يكون الله يبْدَأ بفسادها','1','307',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (184,'<span data-type="title" id=toc-176>يشمت الْعَاطِس ثَلَاثًا</span>

٢٥٧ - وَسَأَلته عَن الرجل يشمت الْعَاطِس فِي مَجْلِسه ثَلَاثًا قَالَ أَكثر مَا قيل فِيهِ ثَلَاث
<span data-type="title" id=toc-177>شَهَادَة من سَمعهَا وَهُوَ مار</span>

٢٥٨ - وَسَأَلته عَمَّن قَالَ اسْمَع شَهَادَة يجوز للرجل أَن يَأْبَى أَن يشْهد إِذا سمع وَهُوَ مار قَالَ إِذا لم يشْهد لَهُ أَن لَا يشْهد وَإِن قَامَ بهَا فَهِيَ شَهَادَة','1','308',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (185,'<span data-type="title" id=toc-178>كَيْفيَّة الدُّعَاء عِنْد الْقَبْر</span>

٢٥٩ - سَأَلته عَمَّن رأى الْقَبْر أيقف قَائِما أَو يجلس فيدعو قَالَ أَرْجُو أَن لَا يكون بِهِ بَأْس
<span data-type="title" id=toc-179>الرَّد على من أنكر صِحَة حَدِيث مَا أسكر كَثِيره فقليله حرَام</span>

٢٦٠ - وَسَأَلته عَمَّن قَالَ لَا يَصح حَدِيث فِيمَا رُوِيَ مَا أسكر كَثِيره فقليله حرَام مَا يكون قَوْله قَالَ هَذَا رجل مغل','1','309',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (186,'<span data-type="title" id=toc-180>بَيَان معنى إِحْرَام الْمَرْأَة فِي وَجههَا</span>

٢٦١ - وَسَأَلته عَمَّن قَالَ إِحْرَام الْمَرْأَة فِي وَجههَا مَا مَعْنَاهُ كَأَنَّهَا لَا تجتنب الزِّينَة إِلَّا فِي وَجههَا أَو كَيفَ قَالَ لَا تخمر وَجههَا وَلَا تنتقب والسدل لَيْسَ بِهِ بَأْس تسدل على وَجههَا','1','310',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (187,'<span data-type="title" id=toc-181>تَفْسِير مَا دخر عَن الْقَوْم شَيْء خبيء لكم</span>

٢٦٢ - وَسَأَلته عَن قَول إِبْرَاهِيم مَا دخر عَن الْقَوْم شَيْء خبيء لكم لفضل عنْدكُمْ قَالَ يَقُول إِن أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم لم يدّخر عَنْهُم
<span data-type="title" id=toc-182>مَسْأَلَتَانِ فِي نقض الْعَهْد من أهل الذِّمَّة</span>

٢٦٣ - وَسَأَلته عَن قوم من أهل الْعَهْد فِي حصن مَعَ الْمُسلمين فنقضوا الْعَهْد وَخَرجُوا بالذرية فلحقهم الْأَمِير دون الدَّرْب مَا السَّبِيل فيهم','1','311',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (188,'قَالَ إِذا نقضوا الْعَهْد فَمن كَانَ مِنْهُم بَالغا فَيجْرِي عَلَيْهِ مَا يجْرِي على أهل الْحَرْب من الْأَحْكَام وَأما الذُّرِّيَّة فَلَا
٢٦٤ - وَسَأَلته عَن قوم من أهل الْعَهْد فِي حصن وَمَعَهُمْ مُسلمُونَ فنقضوا الْعَهْد والمسلمون مَعَهم فِي الْحصن مَا السَّبِيل فيهم قَالَ مَا ولد لَهُم بعد نقضهم الْعَهْد فالذرية بِمَنْزِلَة من نقض الْعَهْد يسبون وَمن كَانَ قبل ذَلِك لَا يسبون وَذَلِكَ أَن امْرَأَة عَلْقَمَة بن علاثة لما ارْتَدَّ قَالَت إِن كَانَ عَلْقَمَة ارْتَدَّ فَأَنا لم أرتد ويروى عَن الْحسن فِيمَن نقض الْعَهْد قَالَ لَيْسَ على الذُّرِّيَّة شَيْء','1','312',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (189,'<span data-type="title" id=toc-183>الزَّكَاة فِي أَمْوَال التِّجَارَة</span>

٢٦٥ - وَسَأَلته عَن قَول ابْن عَبَّاس فِي الْمَتَاع إِذا كَانَ للتِّجَارَة فحال عَلَيْهِ الْحول قَالَ يُزكي الثّمن فَإِن كَانَ فِيهِ ربح زَكَّاهُ بعد مَا مَعْنَاهُ قَالَ أما الَّذِي يرْوى عَن عمر أَنه قَالَ لحماس قوم وزك فَهُوَ عندنَا على مَا قَالَ عمر يقوم مَتَاعه يَوْم يحول عَلَيْهِ الْحول ويزكيه','1','313',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (190,'وَأما قَول ابْن عَبَّاس فيرويه عَامر الْأَحول قَالَ يُزكي الثّمن الَّذِي اشْتَرَاهُ بِهِ','1','314',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (191,'٢٦٦ - قلت يرْوى فِي الْقِيَامَة شَيْء يشبه هَذَا قَالَ لَا
<span data-type="title" id=toc-184>عَمَّن يخرج النَّفْل</span>

٢٦٧ - وَسَأَلته عَن قَول ابْن عَبَّاس النَّفْل من الْخمس كَأَنَّهُ من خمس الإِمَام إِذا عَزله','1','315',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (192,'قَالَ النَّفْل أَن يَجْعَل للْقَوْم شَيْء فَيكون ذَلِك فِي الْخمس وَلَا يكون من الْأَرْبَعَة الْأَخْمَاس الَّتِي لمن قَاتل وَهَذَا شَيْء يرويهِ الْأَوْزَاعِيّ وَالنَّاس يخالفونه عَن الزُّهْرِيّ وَأما حَدِيث حبيب بن مسلمة فَإِنَّهُ قَالَ شهِدت النَّبِي صلى الله عَلَيْهِ وَسلم نفل فِي بَدأته الرّبع بعد الْخمس وَفِي رجعته الثُّلُث بعد الْخمس فَهَذَا إِنَّمَا يكون يرفع الْخمس فَيكون لمن سَمَّاهُ الله ثمَّ يُعْطي النَّفْل ثمَّ يكون مَا بَقِي بعد النَّفْل لمن قَاتل وَهَذَا أشبه بِمَعْنى الْكتاب لِأَنَّهُ قَالَ {وَاعْلَمُوا أَنما غَنِمْتُم من شَيْء فَأن لله خمسه} فَجعله لهَؤُلَاء الَّذين','1','316',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (193,'سماهم
<span data-type="title" id=toc-185>ذكر بعض خَصَائِص النَّبِي صلى الله عَلَيْهِ وَسلم</span>

٢٦٨ - وَسَأَلته عَمَّا يرْوى من فعل النَّبِي صلى الله عَلَيْهِ وَسلم لَهُ خَاص مَا هُوَ يكون مثل النّوم والصفي وَمَا فِي مَعْنَاهُ من الفعال مِمَّا يَفْعَله غَيره قَالَ مثل مَا أُبِيح لَهُ من النِّسَاء مَاتَ عَن تسع وَتزَوج أَربع عشرَة','1','317',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (194,'وَقَالَ تنام عَيْني وَلَا ينَام قلبِي وَكَانَ يصطفي من الْمغنم
<span data-type="title" id=toc-186>من وهبت نَفسهَا للنَّبِي صلى الله عَلَيْهِ وَسلم</span>

٢٦٩ - وَسَأَلته عَن الْمَرْأَة الَّتِي وهبت نَفسهَا للنَّبِي صلى الله عَلَيْهِ وَسلم تزَوجهَا قَالَ فِيهِ اخْتِلَاف أما مُجَاهِد فَكَانَ يَقُول','1','318',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (195,'{إِن وهبت} أَي لم تهب
<span data-type="title" id=toc-187>التَّفْرِيق بَين الزَّوْجَيْنِ للعجز عَن نَفَقَتهَا</span>

٢٧٠ - وَسَأَلته عَن حَدِيث أبي الزِّنَاد عَن سعيد بن الْمسيب فِي الرجل يعجز عَن نَفَقَة امْرَأَته يفرق بَينهمَا قَالَ قلت سنة قَالَ سنة','1','319',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (196,'<span data-type="title" id=toc-188>شرح كل قرض جر مَنْفَعَة فَهُوَ حرَام</span>

٢٧١ - وَسَأَلته عَن قَوْله كل قرض جر مَنْفَعَة حرَام مَا مَعْنَاهُ قَالَ مثل الرجل تكون لَهُ الدَّار فَيَجِيء السَّاكِن فَيَقُول أَقْرضنِي خمسين درهما حَتَّى أسكن فيقرضه ويسكن فِي دَاره أَو يكون يقْرضهُ الْقَرْض فيهدي لَهُ الْهَدِيَّة وَقد كَانَ قبل ذَلِك لَا يهدي لَهُ أَو يقْرضهُ الْقَرْض ويستعمله الْعَمَل الَّذِي كَانَ لَا يَسْتَعْمِلهُ قبل أَن يقْرضهُ فَيكون قرضه جر هَذِه الْمَنْفَعَة وَهَذَا بَاب من أَبْوَاب الرِّبَا وَذَلِكَ أَن يرجع بقرضه وَقد ازاداد مَنْفَعَة','1','320',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (197,'<span data-type="title" id=toc-189>حَدِيث إِذا مر أحدكُم بحائط فليناد ثَلَاثًا</span>

٢٧٢ - وَسَأَلته عَن حَدِيث أبي سعيد إِذا مر أحدكُم بحائط فليناد ثَلَاثًا فكره أَحْمد هَذَا أَن يَأْكُل إِذا لم يكن مُحْتَاجا قَالَ أما الْأَحَادِيث فتروى هَكَذَا وَلَكِن إِذا كَانَ عَلَيْهَا حَائِط فَلَا يدْخل إِلَّا بِإِذن وَذَاكَ أَن الْحَائِط حَرِيم','1','321',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (198,'<span data-type="title" id=toc-190>مَسْأَلَة فِي قَول لم يَخْتَلِفُوا فِي الْأَهِلّة حَتَّى قتل عُثْمَان</span>

٢٧٣ - وَسَأَلته عَن قَوْله كَانُوا لَا يَخْتَلِفُونَ فِي الْأَهِلّة حَتَّى قتل عُثْمَان مَا مَعْنَاهُ قَالَ لَا أَدْرِي دَعه
<span data-type="title" id=toc-191>عدد خطب النَّبِي صلى الله عَلَيْهِ وَسلم فِي الْحَج</span>

٢٧٤ - وَسَأَلته عَن خطْبَة النَّبِي صلى الله عَلَيْهِ وَسلم الَّتِي خطبهَا أَي يَوْم هَذَا كَأَنَّهُ وَاحِدَة فِي جَمِيع الرِّوَايَة قَالَ يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه خطب غير خطْبَة فَأَما الَّذِي رَوَاهُ أَبُو بكرَة فقد بَين','1','322',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (199,'٢٧٥ - وَسَأَلته عَن خطْبَة الْحَج كم هِيَ الَّتِي يعْمل النَّاس عَلَيْهَا مَا يَصح من الرِّوَايَة قَالَ الَّذِي رُوِيَ عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه خطب غير خطْبَة يرْوى عَن مُجَاهِد أَنه قَالَ خطب النَّبِي صلى الله عَلَيْهِ وَسلم بَين الْجَمْرَتَيْن فِي أَيَّام التَّشْرِيق وَقَالَ الزُّهْرِيّ خطب يَوْم النَّحْر وَلم يخْطب فِي غَيره من أَيَّام منى إِلَّا يَوْم النَّحْر فَقَط ثمَّ أخر النَّاس ذَلِك بعد ذَلِك إِلَى الْغَد ليصيبهم يَوْمئِذٍ','1','323',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (200,'وَرُوِيَ عَن مرّة قَالَ حَدثنِي رجل من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ قَامَ فِينَا رَسُول الله صلى الله عَلَيْهِ وَسلم على نَاقَة حَمْرَاء مخضرمة فَقَالَ أَتَدْرُونَ أَي يَوْم هَذَا قُلْنَا يَوْم النَّحْر وَرُوِيَ عَن أبي بكرَة أَن النَّبِي صلى الله عَلَيْهِ وَسلم خطب النَّاس يَوْم النَّحْر وَكَانَ ابْن الزبير يضع منبره بَين الظّهْر وَالْعصر أَيَّام الْعشْر فَيعلم النَّاس الْحَج وخطبته بِعَرَفَة لم يخْتَلف النَّاس فِيهَا','1','324',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (201,'فَقَالَ بعض النَّاس عَن نبيط بن شريط رَأَيْت رَسُول الله صلى الله عَلَيْهِ وَسلم يخْطب على بعير قبل الصَّلَاة بِعَرَفَة
<span data-type="title" id=toc-192>حَدِيث مُعَاوِيَة قصرت عَن النَّبِي صلى الله عَلَيْهِ وَسلم عِنْد الْمَرْوَة</span>

٢٧٦ - وَسَأَلته عَن حَدِيث مُعَاوِيَة قصرت عَن النَّبِي صلى الله عَلَيْهِ وَسلم عِنْد الْمَرْوَة','1','325',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (202,'بمشقص كَأَن التَّقْصِير فِي الْعمرَة أفضل من الْحلق قَالَ إِنَّمَا يُرَاد من حَدِيث مُعَاوِيَة حَيْثُ قصر النَّبِي صلى الله عَلَيْهِ وَسلم على الْمَرْوَة إِنَّمَا كَانَ النَّبِي صلى الله عَلَيْهِ وَسلم','1','326',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (203,'حَاجا وَأَصْحَابه مِنْهُم من أهل بِحَجّ وَعمرَة وَمِنْهُم من أهل بِعُمْرَة فَلَمَّا قدمُوا مَكَّة أَمرهم أَن يجْعَلُوا حجهم عمْرَة وَلم يفعل هُوَ ذَاك لِأَنَّهُ سَاق الْهَدْي فَلم يحل إِلَّا من رَأسه حَيْثُ أَخذ من شعره فَكَانَ مُعَاوِيَة ينْهَى عَن الْمُتْعَة فَقَالَ ابْن عَبَّاس هَذَا حجَّة على مُعَاوِيَة أَن','1','327',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (204,'النَّبِي صلى الله عَلَيْهِ وَسلم قد حل من بعض إِحْرَامه وَلم يحل من شَيْء سوى رَأسه لسوقه الْهَدْي وَكَانَ عَطاء يَقُول لَا يحل إِلَّا مِمَّا حل مِنْهُ النَّبِي صلى الله عَلَيْهِ وَسلم وَكَانَ عَطاء يذهب إِلَى مَا يذهب إِلَيْهِ ابْن عَبَّاس من أَمر النَّبِي صلى الله عَلَيْهِ وَسلم أَصْحَابه بالإحلال
<span data-type="title" id=toc-193>فضل شُهُود الْفِدَاء</span>

٢٧٧ - وَسَأَلته هَل يرْوى فِي شُهُود الفدى أَنه أفضل من الْغَزْو','1','228',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (205,'قَالَ مَا سَمِعت إِن شهد فقد شهد خيرا كثيرا
<span data-type="title" id=toc-194>شرح ذِي مرّة سوي</span>

٢٧٨ - وَسَأَلته عَن قَوْله الصَّدَقَة لَا تحل لَغَنِيّ وَلَا لذِي مرّة سوي فَقَالَ الْمرة السوي الَّذِي لَيْسَ بِهِ عِلّة يَقُول أَن يعتمل لِأَن النَّبِي عَلَيْهِ السَّلَام قَالَ لَا حَظّ فِيهَا لَغَنِيّ وَلَا لقوي مكتسب فقد يكون قَوِيا لَا يتَوَجَّه للكسب','1','329',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (206,'<span data-type="title" id=toc-195>سَماع خلاس من عَليّ وعمار</span>

٢٧٩ - وَسَأَلته عَن خلاس بن عَمْرو هَل سمع من عَليّ وَمَا سمع مِنْهُ فَقَالَ أما هُوَ فقد سمع من عمار بن يَاسر وَيُقَال إِنَّه كَانَ فِي شرطة عَليّ','1','330',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (207,'<span data-type="title" id=toc-196>الْعَاقِلَة وَمَا يتَحَمَّل من جِنَايَة الْمَجْنُون</span>

٢٨٠ - وَسَأَلته عَن رجل معتوه قتل رجلا لم جعل دِيَته على الْعَاقِلَة يشبه الْخَطَأ وَمن الْعَاقِلَة قَالَ على الْعَاقِلَة كل مَا كَانَ من قبل الْآبَاء
<span data-type="title" id=toc-197>حكم الصَّلَاة فِي ثِيَاب الْكفَّار</span>

٢٨١ - وَسَأَلته عَن ثِيَاب الْمُشْركين يصلى فِيهَا قَالَ لَا حَتَّى تغسل','1','331',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (208,'<span data-type="title" id=toc-198>وَقت الْقُنُوت</span>

٢٨٢ - وَسَأَلته عَن الْقُنُوت فَقَالَ فِي النّصْف من شهر رَمَضَان فَإِن قنت السّنة كلهَا فَلَا بَأْس بِهِ وَكَانَ النَّبِي صلى الله عَلَيْهِ وَسلم إِذا دَعَا على قوم أَو استنصر لقوم قنت فِي صَلَاة الْغَدَاة','1','332',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (209,'<span data-type="title" id=toc-199>الْجنب يتَيَمَّم</span>

٢٨٣ - وَقَالَ الْجنب يتَيَمَّم لكل صَلَاة أحدث أَو لم يحدث
<span data-type="title" id=toc-200>حكم الْوتر وتاركه</span>

٢٨٤ - وَقَالَ الْوتر سنة سنّهَا النَّبِي صلى الله عَلَيْهِ وَسلم والمسلمون بعده
٢٨٥ - قلت من ترك الْوتر قَالَ هَذَا رجل سوء','1','333',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (210,'<span data-type="title" id=toc-201>إِزَالَة الْجَنَابَة من الثِّيَاب</span>

٢٨٦ - وَسَأَلته عَن الْجنب إِذا أَصَابَته الْجَنَابَة فِي ثِيَابه وَلم يجد مَا يغسلهُ قَالَ يمسحه بإذخرة أَو بِخرقَة وَإِن كَانَ جافا فركه أَجزَأَهُ
<span data-type="title" id=toc-202>بَوْل الْفرس</span>

٢٨٧ - قلت يَبُول الْفرس فَيَجِيء مطر فيختلط بعض بِبَعْض قَالَ مَا أكل لَحْمه فَلَا بَأْس بِهِ وَإِن كنت أحب أَن يجتنبه','1','334',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (211,'<span data-type="title" id=toc-203>من رووا عَنهُ الْوتر بِرَكْعَة</span>

٢٨٨ - وَقَالَ يرْوى عَن أَرْبَعَة من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه أوتر بِرَكْعَة ابْن عَبَّاس وَعَائِشَة وَابْن عمر وَزيد بن خَالِد','1','335',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (212,'وَكَانَ ابْن عمر يسْتَحبّ أَن يتَكَلَّم بَينهمَا يفصلهما بِكَلَام
<span data-type="title" id=toc-204>التِّجَارَة فِي دَار مَغْصُوبَة</span>

٢٨٩ - وَسَأَلته عَن دَار غصب يَشْتَرِي الرجل فِيهَا وَيبِيع قَالَ لَا
<span data-type="title" id=toc-205>الزَّكَاة فِيمَا وهبه لمملوكه من المَال</span>

٢٩٠ - وَسَأَلته عَن رجل وهب لمملوكه مَالا ثمَّ حَال عَلَيْهِ الْحول هَل فِيهِ زَكَاة قَالَ أبي يُزَكِّيه','1','336',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (213,'<span data-type="title" id=toc-206>زَكَاة العَبْد وَالْفرس</span>

٢٩١ - وَسَأَلته عَن حَدِيث عمر أَنه كَانَ يَأْخُذ من الرَّأْس عشرَة وَمن الْفرس عشرَة وَمن البرذون خَمْسَة مَا مَعْنَاهُ قَالَ أبي حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم لَيْسَ على الرجل فِي عَبده وَلَا فِي فرسه صَدَقَة وَكَانَ عمر يَأْخُذ مِنْهُم ثمَّ يرْزق عبيدهم بعد','1','337',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (214,'وَقَالَ أبي وكل عبد أَو أمة أَو فرس لَا يُرَاد بِهِ التِّجَارَة فَلَيْسَ فِيهِ زَكَاة وكل شَيْء يُرَاد بِهِ التِّجَارَة يقوم ويزكى
<span data-type="title" id=toc-207>زَكَاة الدّين</span>

٢٩٢ - وَسَأَلته عَن رجل يكون لَهُ على رجل مَال فيمكث عَلَيْهِ سِنِين ثمَّ يقبضهُ أيش عَلَيْهِ من الزَّكَاة قَالَ يُزَكِّيه لما مضى
<span data-type="title" id=toc-208>نِصَاب الْبَقر</span>

٢٩٣ - وَسَأَلته عَن صَدَقَة الْبَقر كم فِي خمس مِنْهَا كم فِي ثَلَاثِينَ وَكم فِي أَرْبَعِينَ وَكم فِي خمسين فَقَالَ لَيْسَ فِي خَمْسَة شَيْء إِلَى أَن تبلغ ثَلَاثِينَ فَإِذا بلغت ثَلَاثِينَ فَفِيهَا تبيع وَفِي أَرْبَعِينَ مُسِنَّة فَإِذا صَارَت سِتِّينَ فَفِيهَا تبيعان','1','338',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (215,'<span data-type="title" id=toc-209>الطَّلَاق للرِّجَال</span>

٢٩٤ - وَسُئِلَ عَن حر تَحْتَهُ أمة فَطلقهَا تَطْلِيقَتَيْنِ أَله أَن يَتَزَوَّجهَا قبل أَن تنْكح زوجا غَيره قَالَ إِذا كَانَ تَحْتَهُ أمة ثمَّ اشْتَرَاهَا لم يَطَأهَا بِملك الْيَمين إِن كَانَ عبدا وَإِن كَانَ حرا فقد بَقِي من طَلَاقه تَطْلِيقَة وأذهب فِيهِ إِلَى قَول عُثْمَان وَزيد الطَّلَاق للرِّجَال','1','339',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (216,'<span data-type="title" id=toc-210>من وَقع على جَارِيَة امْرَأَته</span>

٢٩٥ - وَسَأَلته عَن حَدِيث النُّعْمَان بن بشير أَن رَسُول الله صلى الله عَلَيْهِ وَسلم قَالَ فِي رجل وَقع بِجَارِيَة امْرَأَته قَالَ إِن كَانَ أحلتها لَهُ فاجلدوه وَإِن لم تكن أحلتها لَهُ فارجموه','1','340',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (217,'قَالَ أبي أذهب إِلَى حَدِيث النُّعْمَان بن بشير','1','341',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (218,'<span data-type="title" id=toc-211>نِكَاح الْمحرم</span>

٢٩٦ - وَسَأَلته عَن الْمحرم أَله أَن يتَزَوَّج قَالَ لَا يتَزَوَّج وَإِن تزوج فرق بَينهمَا
<span data-type="title" id=toc-212>الْوضُوء على من غسل الْمَيِّت</span>

٢٩٧ - وَسَأَلته عَمَّن غسل مَيتا أيتوضأ أم يغْتَسل قَالَ أَكثر مَا فِيهِ الْوضُوء
<span data-type="title" id=toc-213>عدَّة من علمت وَفَاة زَوجهَا أَو الطَّلَاق بعد سنة</span>

٢٩٨ - وَسَأَلته عَن الْمَرْأَة يَأْتِيهَا نعي زَوجهَا أَو طَلَاقه إِيَّاهَا بعد سنة أتكون','1','342',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (219,'الْعدة قد انْقَضتْ أم تسْتَأْنف الْعدة قَالَ إِذا قَامَت الْبَيِّنَة فَمن يَوْم مَاتَ أَو طلق
<span data-type="title" id=toc-214>من قَالَ لزوجته أَمرك بِيَدِك</span>

٢٩٩ - وَسَأَلته عَن رجل قَالَ لامْرَأَته أَمرك بِيَدِك فَاخْتَارَتْ نَفسهَا بعد يَوْم قَالَ أبي إِذا لم يكن رَجَعَ فِي الأول وَلَا وَطئهَا فلهَا الْخِيَار
<span data-type="title" id=toc-215>نَفَقَة الْمُتَوفَّى عَنْهَا زَوجهَا الْحَامِل</span>

٣٠٠ - وَسَأَلته عَن الْمُتَوفَّى عَنْهَا زَوجهَا وَهِي حَامِل من ينْفق عَلَيْهَا قَالَ ينْفق عَلَيْهَا من نصِيبهَا','1','343',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (220,'<span data-type="title" id=toc-216>نَفَقَة المختلعة الْحَامِل</span>

٣٠١ - وَسَأَلته عَن نَفَقَة المختلعة الْحَامِل على من هُوَ قَالَ على الزَّوْج إِلَّا أَن يكون تَبرأ
<span data-type="title" id=toc-217>مُرَاجعَة المختلعة زَوجهَا</span>

٣٠٢ - وَسَأَلته عَن المختلعة إِذا أَرَادَت أَن تراجع زَوجهَا فِي الْعدة تراجعه بِنِكَاح جَدِيد أَو يجْزِيه أَن يشْهد على رَجعتهَا قَالَ تراجعه بولِي وشهود وصداق مُسَمّى
<span data-type="title" id=toc-218>كم عدَّة المختلعة</span>

٣٠٣ - وَسَأَلته المختلعة كم عدتهَا قَالَ ثَلَاث حيض','1','344',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (221,'<span data-type="title" id=toc-219>من مرض أَو وجد مَا يعْتق أثْنَاء الصَّوْم لكفارة الظِّهَار</span>

٣٠٤ - وَسَأَلته عَمَّن صَامَ من كَفَّارَة الظِّهَار شهرا ثمَّ وجد مَا يعْتق أَو عرض لَهُ مرض قَالَ يمْضِي على صَوْمه وَإِذا كَانَ شَيْء لَيْسَ مِمَّا فعله فَهُوَ يقْضِي
<span data-type="title" id=toc-220>حكم طَلَاق الصَّبِي</span>

٣٠٥ - وَسَأَلته عَن رجل زوج ابْنا لَهُ صَغِيرا فَطلقهَا الْغُلَام قبل أَن يَحْتَلِم قَالَ إِن كَانَ مِمَّن يعقل الطَّلَاق فطلاقه جَائِز','1','345',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (222,'<span data-type="title" id=toc-221>الحكم إِذا رَاجع زَوجته الْحَامِل بولدين قبل أَن تضع الثَّانِي</span>

٣٠٦ - وَسَأَلته عَن الرجل يُطلق امْرَأَته وَفِي بَطنهَا ولدان فَوضعت أَحدهمَا ثمَّ رَاجعهَا زَوجهَا قَالَ مالم تضع الآخر فَهُوَ أَحَق بهَا
<span data-type="title" id=toc-222>حكم الْحَد فِي الزِّنَا بِغَيْر الْبَالِغ</span>

٣٠٧ - وَسَأَلته عَن رجل زنى بِجَارِيَة قبل أَن تحيض أَو امْرَأَة زنت بِغُلَام قبل أَن يَحْتَلِم قَالَ إِن كَانَ مثلهَا يُوطأ فَعَلَيهِ الْحَد وَإِذا زنت بِغُلَام مثله يصل إِلَيْهَا','1','346',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (223,'ترْجم
<span data-type="title" id=toc-223>صِيغ الظِّهَار</span>

٣٠٨ - وَسَأَلته عَن رجل يَقُول لامْرَأَته أَنْت عَليّ كَظهر أُخْتِي وكظهر امْرَأَة أَجْنَبِيَّة قَالَ إِن ظَاهر بِذَات محرم مِنْهُ فَهُوَ ظِهَار','1','347',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (224,'<span data-type="title" id=toc-224>حد الزَّانِي المتزوج الَّذِي لم يدْخل بِزَوْجَتِهِ</span>

٣٠٩ - وَسَأَلته عَن رجل يكون لَهُ امْرَأَة وَهُوَ بكر لم يدْخل بهَا فزنى مَا يجب عَلَيْهِ الْجلد أَو الرَّجْم قَالَ لَيْسَ على الْبكر رجم حَتَّى يدْخل بأَهْله
<span data-type="title" id=toc-225>حكم بيع الْأمة الَّتِي لَهُ مِنْهَا أَوْلَاد قبل تَملكهَا</span>

٣١٠ - وَسَأَلته عَن حر تَحْتَهُ أمة فَولدت مِنْهُ أَوْلَادًا ثمَّ اشْتَرَاهَا أَله أَن يَبِيعهَا قَالَ نعم مَا لم تكن ولدت فِي ملكه','1','348',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (225,'<span data-type="title" id=toc-226>اللّعان مَعَ الزَّوْجَة النَّصْرَانِيَّة</span>

٣١١ - وَسَأَلته عَن الْمُسلم تكون تَحْتَهُ النَّصْرَانِيَّة أَيكُون بَينهمَا لعان قَالَ نعم كل زوج يُلَاعن
<span data-type="title" id=toc-227>حد العَبْد الْبكر الزَّانِي</span>

٣١٢ - وَسَأَلته عَن العَبْد إِذا زنى أيقام عَلَيْهِ الْحَد وَهُوَ بكر قَالَ يجلد خمسين','1','349',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (226,'<span data-type="title" id=toc-228>مَا يجتنبه من يُرِيد أَن يُضحي</span>

٣١٣ - قلت لأبي مَا يجْتَنب الرجل إِذا أَرَادَ أَن يُضحي قَالَ لَا يَأْخُذ من شعره وَلَا من بشره
٣١٤ - قَالَ أبي سَأَلت يحيى بن سعيد الْقطَّان عَن حَدِيث عَائِشَة أَن النَّبِي صلى الله عَلَيْهِ وَسلم كَانَ يبْعَث بِالْهَدْي وَلَا يجْتَنب مَا يجتنبه الْمحرم وَعَن حَدِيث أم سَلمَة إِذا أَرَادَ أحدكُم أَن يُضحي فَلَا يمسن من شعره وَلَا من بشره','1','450',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (227,'فَقَالَ يحيى بن سعيد لهَذَا وَجه وَلِهَذَا وَجه قَالَ أبي وَسَأَلت عبد الرَّحْمَن بن مهْدي فَسكت
<span data-type="title" id=toc-229>كَفَّارَة الصّيام عَن المغلوب على عقله</span>

٣١٥ - وَسَأَلته عَن المغلوب على عقله هَل يكفر عَنهُ لتَركه صِيَام شهر رَمَضَان أم لَا فَقَالَ إِذا كَانَ بِمَنْزِلَة الَّذِي قد أيس مِنْهُ يكون بِمَنْزِلَة الشَّيْخ الْكَبِير يطعم عَنهُ كل يَوْم مِسْكين وَأَقل مَا يطعم مد','1','351',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (228,'<span data-type="title" id=toc-230>حكم تَطْهِير السكين بمسح الدَّم وَالْبَوْل عَنْهَا</span>

٣١٦ - وَسَأَلته عَن رجل ذبح بسكين فَمسح السكين بِخرقَة ثمَّ قطع بهَا جبنا رطبا هَل يُؤْكَل الْجُبْن أم لَا قَالَ إِذا كَانَت السكين لَيْسَ عَلَيْهَا أثر دم وَقطع الْجُبْن وَلَيْسَ عَلَيْهِ أثر دم فَلَا بَأْس بِهِ
٣١٧ - قلت وَكَيف القَوْل إِن أصَاب السكين بَوْل فمسحه قَالَ الْبَوْل لَا يشبه الدَّم قد يُصَلِّي الرجل وَفِي ثَوْبه من الدَّم الْقَلِيل وَلَا يُعِيد لذاك وَالْبَوْل يُعِيد من الْقَلِيل وَالْكثير قَالَ الله عز وَتَعَالَى {قل لَا أجد فِيمَا أُوحِي إِلَيّ محرما على طاعم يطعمهُ إِلَّا أَن يكون ميتَة أَو دَمًا مسفوحا} قَالَ أبي فَسمِعت سُفْيَان يَقُول المسفوح العبيط','1','352',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (229,'<span data-type="title" id=toc-231>حكم رَوْث الْحمار</span>

٣١٨ - وَسَأَلته من أَصَابَهُ شَيْء من رَوْث حمَار قَالَ كل شَيْء من الْحمار يجْتَنب لِأَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ هِيَ رِجْس
<span data-type="title" id=toc-232>على من لَا يُصَلِّي الإِمَام</span>

٣١٩ - سَأَلت أبي على من لَا يُصَلِّي الإِمَام قَالَ على قَاتل نَفسه وعَلى الغال','1','353',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (230,'<span data-type="title" id=toc-233>إطْعَام الْمِسْكِين كفارتين وَثَلَاثَة عِنْد الْحَاجة</span>

٣٢٠ - وَسَأَلته عَن الرجل يُرِيد أَن يطعم مائَة مِسْكين وَلَيْسَ يجد فِي محلته مائَة مِسْكين فَقَالَ إِذا جمع عشرَة فَلَا بَأْس بِهِ أَن يُطعمهُمْ كفارتين وَثَلَاثَة
<span data-type="title" id=toc-234>حكم الْبَوْل قبل الْغسْل من الْجَنَابَة</span>

٣٢١ - قلت الرجل تصيبه الْجَنَابَة أَو يقرب أَهله إِن لم يبل يجْزِيه قَالَ إِن بَال فَلَا بَأْس وَإِن لم يبل فَإِذا اسْتَبْرَأَ فَلَا بَأْس','1','354',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (231,'<span data-type="title" id=toc-235>مَا يصنع بِمَال من غَابَ أَرْبَعِينَ سنة</span>

٣٢٢ - وَسَأَلته عَن رجل خلف مَالا عِنْد ابْن أُخْته وَغَابَ أَرْبَعِينَ سنة أَو نَحْوهَا وَلَا يدْرِي حَيّ هُوَ أَو ميت وَلَيْسَ لَهُ وَارِث إِلَّا ابْن أُخْته هَذَا مَا يصنع بِالْمَالِ يتَصَدَّق بِهِ أَو يمسِكهُ قَالَ أبي إِن كَانَ مفقودا وَمعنى الْمَفْقُود أَن يكون الرجل فِي أَهله فيصبحون وَلَيْسَ هُوَ فيهم أَو يَكُونُوا ركبُوا الْبَحْر فَكسر بهم أَو لقوا الْعَدو فأصيب بَعضهم أَو رجل كَانَ مَعَ قوم فِي سفر ففقدوه من بَينهم فَهَذَا وأشباهه أَسبَاب الْمَفْقُود فَإِن كَانَ الرجل فِي معنى هَذِه الْمعَانِي أَو مَا يشبهها وغيبته نَحْو من أَرْبَعِينَ سنة أَو نَحْو مَا ذكرت قسم هَذَا المَال على وَارِث إِن كَانَ فَإِن لم يكن لَهُ وَارِث فَإِن كَانَت لَهُ عصبَة فهم أولى بِهِ فَإِن لم يكن لَهُ عصبه فالموالي فَإِن لم يكن موَالِي فذو رَحْمَة فَإِن لم يكن لَهُ إِلَّا ابْن أُخْته هَذَا فَهُوَ لَهُ وَإِن كَانَت غيبته','1','355',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (232,'فِي تِجَارَة أَو خرج يُرِيد الْحَج أَو يَبِيع مَا يَبِيع النَّاس فَغَاب فَلَيْسَ هَذَا بمفقود فَيُوقف هَذَا المَال حَتَّى يَأْتِي عَلَيْهِ مائَة سنة أَو تسعون سنة أَو أَكثر مَا يعِيش أهل زَمَاننَا فِيهِ فَإِن كَانَ يَوْم غَابَ قد عرف سنه فَلْينْظر إِلَى سنه وَإِلَى غيبته كم تكون فَإِن بلغت مائَة سنة أَو تسعين سنة وَكلما احتاط فِي طول الْغَيْبَة فَهُوَ أَحْرَى ثمَّ يقسم هَذَا المَال على مَا ذكرنَا
<span data-type="title" id=toc-236>مَحل الْمسْح على الْخُفَّيْنِ</span>

٣٢٣ - وَسَأَلته عَن الْمسْح على الْخُفَّيْنِ يمسح ظاهرهما وباطنهما وَهل يعْمل بِحَدِيث الْمُغيرَة بن شُعْبَة قَالَ أبي الْمسْح على الْخُفَّيْنِ فَإِنَّمَا يمسح أعلاهما وَقَالَ بعض النَّاس وأسفلهما وَلَيْسَ هُوَ بِحَدِيث ثَبت عندنَا','1','356',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (233,'<span data-type="title" id=toc-237>تَزْوِيج امْرَأَة لَيْسَ لَهَا ولي</span>

٣٢٤ - وَسَأَلته عَن الرجل تجْعَل الْمَرْأَة أمرهَا إِلَيْهِ وَلَيْسَ لَهَا ولي هَل يُزَوّجهَا تزويجا طَاهِرا دون السُّلْطَان قَالَ أبي لَا يُزَوّجهَا وَلَا يَتَزَوَّجهَا إِلَّا بِإِذن ولي فَإِن لم يكن ولي فالسلطان','1','357',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (234,'<span data-type="title" id=toc-238>من حلف بِمَا أحل الله عَلَيْهِ حرَام</span>

٣٢٥ - وَسَأَلته عَن رجل حلف مَا أحل الله عَلَيْهِ حرَام إِن دخل الْبَيْت بنهار ثمَّ دخل قَالَ أبي يكفر كَفَّارَة الظِّهَار يعْتق رَقَبَة إِن وجد وَإِلَّا صَامَ شَهْرَيْن مُتَتَابعين فَإِن لم يسْتَطع فإطعام سِتِّينَ مِسْكينا وَأَقل إطْعَام الْمَسَاكِين مُدبر أَو نصف صَاع تمر لكل مِسْكين
<span data-type="title" id=toc-239>كَيْفيَّة فسخ الْحَج</span>

٣٢٦ - وَسَأَلت أبي عَن فسخ الْحَج كَيفَ هُوَ إِذا أَرَادَ أَن يفْسخ مَا يَقُول وَكَيف يعْمل','1','358',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (235,'قَالَ يطوف بِالْبَيْتِ وَيسْعَى بَين الصَّفَا والمروة ثمَّ يحل
<span data-type="title" id=toc-240>من بَطل اعْتِكَافه بِالْجِمَاعِ هَل عَلَيْهِ الْقَضَاء</span>

٣٢٧ - وَسَأَلته عَن الْمُعْتَكف إِذا جَامع بَطل اعْتِكَافه يكون عَلَيْهِ قَضَاء إِذا كَانَ نذرا أَو لَا قَالَ أبي إِن كَانَ نذرا فَعَلَيهِ قَضَاؤُهُ وَإِن تكلم بِهِ مُتَطَوعا وأوجبه على نَفسه فَعَلَيهِ قَضَاؤُهُ','1','359',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (236,'<span data-type="title" id=toc-241>دَعْوَى التَّزْوِيج لَا يثبت إِلَّا بالشهود</span>

٣٢٨ - وَسَأَلته عَن الْمَرْأَة يَدعِي الرجل تَزْوِيجهَا يصدق فِي ذَلِك قَالَ أبي لَا يثبت تَزْوِيجه إِلَّا بِشُهُود
<span data-type="title" id=toc-242>حكم من قَالَ طلقت وَلم يُطلق</span>

٣٢٩ - قلت الرجل يَقُول قد طلقت وَلم يُطلق مَا يكون قَالَ يلْزمه','1','360',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (237,'<span data-type="title" id=toc-243>وَقت قَضَاء الصَّلَاة الَّتِي نَسِيَهَا</span>

٣٣٠ - قَالَ أبي أذهب إِلَى من نسي صَلَاة فليصلها إِذا ذكرهَا إِلَّا أَن يكون فِي صَلَاة يخَاف فَوتهَا
<span data-type="title" id=toc-244>حَدِيث عمر فِي نسيانه الْقِرَاءَة فِي الرَّكْعَة الأولى</span>

٣٣١ - قلت حَدِيث عمر أَنه لم يقْرَأ فِي الرَّكْعَة الأولى من الْمغرب فَقَرَأَ فِي الثَّانِيَة الْحَمد وَسورَة ثمَّ أَعَادَهَا أَلَيْسَ هُوَ هَكَذَا','1','361',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (238,'قَالَ أبي هَكَذَا يروي عِكْرِمَة بن عمار وَلَا أذهب إِلَيْهِ قَالَ وأذهب إِلَى أَن عمر صلى فَلم يقْرَأ فَأَعَادَ الصَّلَاة
<span data-type="title" id=toc-245>من غصب جَارِيَة فزادت قيمتهَا ثمَّ مَاتَت</span>

٣٣٢ - قلت رجل غصب جَارِيَة وَهِي تَسَاوِي ألفا فبلغت إِلَى أَن صَارَت تَسَاوِي أَلفَيْنِ ثمَّ مَاتَت عِنْده مَا عَلَيْهِ قَالَ أبي عَلَيْهِ قيمتهَا يَوْم مَاتَت لِأَنَّهَا كَانَت فِي ضَمَانه','1','362',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (239,'<span data-type="title" id=toc-246>من نسي الْقِرَاءَة فِي رَكْعَتَيْنِ</span>

٣٣٣ - قلت من نسي الْقِرَاءَة فِي الرَّكْعَتَيْنِ من الْمغرب قَالَ أبي كل رَكْعَة لَا يَأْتِي بِفَاتِحَة الْكتاب لَا تجزيه
<span data-type="title" id=toc-247>هَل يدْرك فضل الْجَمَاعَة من أدْركهَا وَالْإِمَام فِي سَجْدَتي السَّهْو</span>

٣٣٤ - قلت من أدْرك الإِمَام وَهُوَ فِي سَجْدَتي السَّهْو كبر مَعَه يكون لحق صلَاته قَالَ أَرْجُو أَن يكون يُضَاعف لَهُ إِن شَاءَ الله
<span data-type="title" id=toc-248>معنى لَيْسَ فِي الطَّعَام إِسْرَاف</span>

٣٣٥ - قلت قَول الْحسن لَيْسَ فِي الطَّعَام إِسْرَاف قَالَ يَقُول إِن أَكثر مِنْهُ فَلَيْسَ فِيهِ إِسْرَاف','1','363',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (240,'<span data-type="title" id=toc-249>حكم أَذَان رجل ضَعِيف لَا يرفع صَوته</span>

٣٣٦ - قلت رجل ضَعِيف لَا يرفع صَوته يجوز أَذَانه إِذا كَانَ لَا يخرج من الْمَسْجِد قَالَ إِذا كَانَ يسمع أهل الْمَسْجِد وَالْجِيرَان فَلَا بَأْس
<span data-type="title" id=toc-250>حَدِيث أَكثر منافقي أمتِي قراؤها</span>

٣٣٧ - قلت قَول النَّبِي صلى الله عَلَيْهِ وَسلم أَكثر منافقي أمتِي قراؤها هُوَ صَحِيح قَالَ الله أعلم مَا أَدْرِي','1','364',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (241,'<span data-type="title" id=toc-251>من كتب أَو شهد على شَيْء من الرِّبَا وَهُوَ لَا يعلم</span>

٣٣٨ - قلت قَوْله الرِّبَا على من أربى يكون الرجل شهد على شَيْء من ذَا','1','365',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (242,'وَلم يعلم أَو كتب يكون عَلَيْهِ إثمه إِذا لم يعلم قَالَ يَنْبَغِي لَهُ أَن يعرف مَا يَأْتِي
<span data-type="title" id=toc-252>معنى زَينُوا الْقُرْآن بِأَصْوَاتِكُمْ</span>

٣٣٩ - قلت قَوْله زَينُوا الْقُرْآن بِأَصْوَاتِكُمْ مَا مَعْنَاهُ قَالَ أبي التزيين أَن يُحسنهُ','1','366',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (243,'<span data-type="title" id=toc-253>معنى التَّغَنِّي بِالْقُرْآنِ</span>

٣٤٠ - قلت قَوْله مَا أذن الله لشَيْء كأذنه لنَبِيّ يتَغَنَّى بِالْقُرْآنِ يجْهر بِهِ مَا مَعْنَاهُ قَالَ أبي أما التَّغَنِّي فَمن النَّاس من يَقُول يَسْتَغْنِي بِهِ وَكَانَ سُفْيَان بن عُيَيْنَة يَقُول يَسْتَغْنِي بِهِ وَبَعض النَّاس يَقُول إِذا رفع صَوته فقد تغنى بِهِ','1','367',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (244,'<span data-type="title" id=toc-254>معنى حَدِيث حدثوا عَن بني إِسْرَائِيل وَلَا حرج</span>

٣٤١ - قلت قَول النَّبِي صلى الله عَلَيْهِ وَسلم حدثوا عَن بني إِسْرَائِيل وَلَا حرج يحدث الرجل بِكُل شَيْء يُرِيد قَالَ أبي يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه قَالَ من حدث عني شَيْئا يرى أَنه','1','368',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (245,'كذب فَهُوَ أحد الْكَذَّابين وَقَالَ النَّبِي صلى الله عَلَيْهِ وَسلم حدثوا عَن بني إِسْرَائِيل وَلَا حرج فَفرق بَين مَا يحدث عَنهُ وَبَين مَا يحدث عَن بني إِسْرَائِيل فَقَالَ حدثوا عَن بني إِسْرَائِيل وَلَا حرج فَإِنَّهُ كَانَت فيهم الْأَعَاجِيب فَيكون الرجل يحدث عَن بني إِسْرَائِيل وَهُوَ يرى أَنه لَيْسَ كَذَلِك فَلَا بَأْس أَن يحدث بِهِ وَلَا يحدث عَن النَّبِي صلى الله عَلَيْهِ وَسلم إِلَّا مَا يرى أَنه صدق','1','369',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (246,'<span data-type="title" id=toc-255>كَيفَ يقْضِي من أدْرك رَكْعَة من الرّبَاعِيّة</span>

٣٤٢ - قلت رجل أدْرك رَكْعَة من الْعَصْر يقْرَأ الْحَمد وَسورَة فِيمَا يقْضِي قَالَ يقوم فَيقْرَأ فَاتِحَة الْكتاب وَسورَة ثمَّ يجلس ثمَّ يقوم فَيقْرَأ فَاتِحَة الْكتاب وَسورَة ثمَّ يقوم وَلَا يجلس ثمَّ يقوم فَيقْرَأ فَاتِحَة الْكتاب وَحدهَا
<span data-type="title" id=toc-256>من أهل بِعُمْرَة وسَاق الْهَدْي ثمَّ أحْصر</span>

٣٤٣ - وَسَأَلته عَمَّن أهل بِعُمْرَة وسَاق الْهَدْي فأحصر قَالَ إِن كَانَ من عَدو نحر هَدْيه وَحل لِأَن النَّبِي صلى الله عَلَيْهِ وَسلم صده الْمُشْركُونَ فَنحر هَدْيه بِالْحُدَيْبِية وَكَانَ أهل بِعُمْرَة وَحل وَرجع إِلَى الْمَدِينَة وَإِن','1','370',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (247,'كَانَ أهل بِحَجّ ثمَّ أحْصر فقد اخْتلف النَّاس فِيهِ فَقَالَ ابْن عمر لَا يحله إِلَّا الطّواف بِالْبَيْتِ لَا يزَال محرما حَتَّى يَأْتِي الْبَيْت فيطوف بِهِ وَقَالَ ابْن مَسْعُود يبْعَث بِهَدي ويواعد الَّذِي يبْعَث مَعَه الْهَدْي فَإِذا جَاءَ الْوَقْت الَّذِي واعده فِيهِ نحر هَدْيه ثمَّ حل هُوَ هَهُنَا وَقد رُوِيَ','1','371',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (248,'عَنهُ أَنه يستظهر بِيَوْم أَو يَوْمَيْنِ وَذَلِكَ فِي الْعمرَة الَّتِي قَالَ ابْن مَسْعُود وَإِن كَانَ أهل بِحَجّ أَو عمْرَة فقد أوجب على نَفسه شَيْئا فَهُوَ لَا يحل إِلَّا بِالطّوافِ بِالْبَيْتِ إِذا كَانَ إحصاره بِغَيْر عَدو فَلَا يزَال محرما حَتَّى يَأْتِي الْبَيْت وَإِنَّمَا قُلْنَا يحل من الْعمرَة إِذا كَانَ عَدو لِأَن النَّبِي صلى الله عَلَيْهِ وَسلم حل وَإِن حل قبل أَن ينْحَر هَدْيه فَعَلَيهِ فديَة من صِيَام أَو صَدَقَة أَو نسك','1','372',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (249,'وَالصَّدَََقَة ثَلَاثَة آصَع من تمر بَين سِتَّة مَسَاكِين والنسك شَاة وَالصِّيَام ثَلَاثَة أَيَّام وَهُوَ فِي ذَلِك مُخَيّر
<span data-type="title" id=toc-257>حكم لبس الرواح</span>

٣٤٤ - وَسَأَلته عَن لبس الرواح قَالَ أَرْجُو
٣٤٥ - قلت فَإِن لبسه فِي الصَّلَاة قَالَ يطْرَح أحد طَرفَيْهِ على الآخر','1','373',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (250,'<span data-type="title" id=toc-258>مَسْأَلَة عَن السدل</span>

٣٤٦ - وَسَأَلته عَن السدل قَالَ يلبس الثَّوْب فَإِذا لم يطْرَح أحد طَرفَيْهِ على الآخر فَهُوَ سدل فَلَا يُصَلِّي وَهُوَ مسدل الثَّوْب
<span data-type="title" id=toc-259>مَتى يفوت الْحَج وماذا يجب على من فَاتَهُ الْحَج</span>

٣٤٧ - وَقَالَ كل من وطيء عَرَفَة بلَيْل إِلَى طُلُوع الْفجْر فقد أدْرك الْحَج فَإِذا طلع الْفجْر فقد فَاتَهُ فَعَلَيهِ أَن يطوف بِالْبَيْتِ وَيسْعَى بَين الصَّفَا والمروة وَعَلِيهِ الْحَج من قَابل وَمَا اسْتَيْسَرَ من الْهَدْي','1','374',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (251,'<span data-type="title" id=toc-260>حكم تَارِك الصَّلَاة وقضائها عَن الْمَيِّت</span>

٣٤٨ - قلت رجل فرط فِي الصَّلَاة فَلَمَّا أدْركهُ الْمَوْت أقرّ بذلك فَقَالَ الصَّلَاة لَا تقضى وَلَكِن يصدق عَنهُ
٣٤٩ - قلت فَإِن تَركهَا وَلم يصل قَالَ إِذا كَانَ عَامِدًا استتبته ثَلَاثًا فَإِن تَابَ وَإِلَّا قتل','1','375',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (252,'٣٥٠ - قلت فتوبته أَن يُصَلِّي قَالَ نعم
<span data-type="title" id=toc-261>حكم التَّفْرِيق فِي الأسعار</span>

٣٥١ - وَسَأَلته عَن رجل يَجِيئهُ الذِّمِّيّ يَشْتَرِي مِنْهُ الْمَتَاع فيماكسه مكاسا شَدِيدا فيبيعه الْمَتَاع وَيَجِيء بعد ذَلِك الرجل الْمُسلم فيستقضي أَيْضا فِي شدَّة المكاس فيبيعه أغْلى مِمَّا يَبِيع الذِّمِّيّ وَرُبمَا بَاعَ من الذِّمِّيّ أغْلى','1','376',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (253,'قَالَ أَرْجُو أَن لَا يكون عَلَيْهِ فِي ذَلِك شَيْء إِذا كَانَ المُشْتَرِي يماكسه
<span data-type="title" id=toc-262>حكم إِعْطَاء المُشْتَرِي غير مَا يَطْلُبهُ من غير بَيَان</span>

٣٥٢ - قلت رجل يبْعَث إِلَيْهِ الذِّمِّيّ بِدَرَاهِم ليَشْتَرِي لَهُ الْمَتَاع من بعض الْمَوَاضِع فيبعث إِلَيْهِ مَا عِنْده وَمَا لم يكن عِنْده اشْترى لَهُ فَيكون مَا يُوَجه إِلَيْهِ مِمَّا عِنْده وَمِمَّا يَشْتَرِي لَهُ سَوَاء فِي الاستقضاء للذِّمِّيّ وَالْمُسلم قَالَ لَا يُعجبنِي أَن يبْعَث إِلَيْهِ مِمَّا عِنْده حَتَّى يبين أَنه قد بعث إِلَيْهِ مِمَّا عِنْده','1','377',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (254,'<span data-type="title" id=toc-263>حكم التَّفْرِيق فِي الأسعار لأجل النَّقْد والنسيئة</span>

٣٥٣ - قلت الرجل يَبِيع الْمَتَاع فَيَقُول أبيعك بِالنَّقْدِ بِأَلف وَإِلَى شهر بِأَلف وَمِائَة وَإِلَى شَهْرَيْن بِأَلف وَمِائَتَيْنِ قَالَ هَذَا مَكْرُوه إِلَى أَن يُفَارِقهُ على أحد الْبيُوع
<span data-type="title" id=toc-264>حكم إقراض المُشْتَرِي</span>

٣٥٤ - قلت الرجل يَشْتَرِي من الرجل الْمَتَاع فيستقرض مِنْهُ الشَّيْء فيقرضه قَالَ إِن كَانَ الْقَرْض الَّذِي يقْرضهُ يجر إِلَيْهِ مَنْفَعَة فَلَا خير فِيهِ','1','378',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (255,'<span data-type="title" id=toc-265>حِسَاب الْأَمْوَال وتقويمها عِنْد إِخْرَاج الزَّكَاة</span>

٣٥٥ - قلت رجل حسب مَاله فَوَجَبَ عَلَيْهِ الزَّكَاة فِي السّنة أَلفَانِ فَمَكثَ يُعْطي عَن ذَلِك على ذَلِك سِنِين وَلَا يدْرِي نقص مَاله أَو زَاد إِلَّا أَنه يرى أَنه قد زَاد قَالَ يَنْبَغِي لَهُ أَن يحْسب مَاله فِي كل سنة وَثمن مَتَاعه يقومه بِقِيمَة يَوْم حَال عَلَيْهِ الْحول فيزكيه
<span data-type="title" id=toc-266>بِمَاذَا يَدْعُو فِي التَّشَهُّد الْأَخير</span>

٣٥٦ - قلت إِذا تشهد الرجل فِي آخر رَكْعَة فَقَالَ أشهد أَن لَا إِلَه إِلَّا الله وَأشْهد أَن مُحَمَّدًا عَبده وَرَسُوله يَقُول اللَّهُمَّ صل على مُحَمَّد وعَلى آل مُحَمَّد وَبَارك على مُحَمَّد وعَلى آل مُحَمَّد قَالَ يُعجبنِي يَدْعُو بِدُعَاء ابْن مَسْعُود رَبنَا آتنا فِي الدُّنْيَا حَسَنَة وَفِي الْآخِرَة حَسَنَة','1','379',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (256,'<span data-type="title" id=toc-267>حكم التَّسْمِيَة عِنْد الْوضُوء</span>
٣٥٧ - قلت إِن تَوَضَّأ وَلم يسم قَالَ أَرْجُو','1','380',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (257,'٣٥٨ - قلت الحَدِيث الَّذِي يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ لَا يثبت عِنْدِي إِسْنَاده ضَعِيف
<span data-type="title" id=toc-268>مِقْدَار الصَّاع وَالْمدّ وَمَا يَجْزِي فِي كَفَّارَة الْيَمين</span>

٣٥٩ - قلت الصَّاع كم هُوَ قَالَ خَمْسَة أَرْطَال وَثلث بِالْبرِّ','1','381',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (258,'٣٦٠ - قلت فالمد كم هُوَ من الصَّاع قَالَ رَطْل وَثلث
٣٦١ - قَالَ وَأَقل مَا يَجْزِي فِي كَفَّارَة الْيَمين مد بر وَمن التَّمْر ثَلَاثَة أَرْطَال غير ثلث
<span data-type="title" id=toc-269>حكم الْوضُوء بِوضُوء الرجل</span>

٣٦٢ - قلت يتَوَضَّأ الرجل بِوضُوء الرجل قَالَ لَا يُعجبنِي مَا سَمِعت فِي هَذَا شَيْئا','1','382',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (259,'<span data-type="title" id=toc-270>من أكل فِي رَمَضَان نَاسِيا هَل عَلَيْهِ الْقَضَاء</span>

٣٦٣ - وَقَالَ إِذا أكل فِي رَمَضَان وَهُوَ نَاس فَلَيْسَ عَلَيْهِ قَضَاء يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه قَالَ إِذا أكل نَاسِيا فَإِنَّمَا هُوَ رزق أطْعمهُ الله وسقاه قَالَ وَبَلغنِي عَن مَالك أَنه كَانَ يَقُول عَلَيْهِ الْقَضَاء','1','383',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (260,'<span data-type="title" id=toc-271>تَعْلِيق الطَّلَاق بمجيء الْهلَال وَنَحْوه</span>

٣٦٤ - قَالَ وَإِذا قَالَ أَنْت طَالِق إِذا جَاءَ الْهلَال أَو أَنْت طَالِق عِنْد الْهلَال فَهُوَ يسْتَمْتع مِنْهَا إِلَى الْهلَال وَإِذا قَالَ أَنْت طَالِق إِلَى الْهلَال فَإِن كَانَ أَرَادَ إِذا جَاءَ الْهلَال فَهُوَ على مَا أَرَادَ وَإِن كَانَ أَرَادَ من السَّاعَة الَّتِي تكلم بِهِ إِلَى الْهلَال فَهُوَ على مَا أَرَادَ تطلق سَاعَة قَالَ','1','384',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (261,'وَكَانَ الْحسن وَسَعِيد بن الْمسيب وَالزهْرِيّ لَا يؤجلون فِي الطَّلَاق يَقُولُونَ إِذا قَالَ أَنْت طَالِق إِذا جَاءَ الْهلَال فَهِيَ طَالِق السَّاعَة وَكَانَ إِبْرَاهِيم وَالشعْبِيّ يَقُولَانِ لَا تطلق حَتَّى يَجِيء الْهلَال وَقَالَ بعض من يذهب مَذْهَب أهل الْمَدِينَة إِذا كَانَ الشَّيْء لَا محَالة أَن يَجِيء مثل الشَّهْر وَالسّنة فَهِيَ طَالِق سَاعَة يَقُول ذَلِك وَإِن كَانَ مِمَّا يكون وَلَا يكون مثل قَوْله إِن قدم فلَان من غيبته فَأَنت طَالِق أَو ذهب فلَان فَأَنت طَالِق فَلَا تطلق حَتَّى يقدم فلَان أَو يذهب فلَان وَإِذا قَالَ إِذا حِضْت فَأَنت طَالِق فقد تكون تحيض وَلَا تحيض فَشبه بعض النَّاس قَوْله إِذا جَاءَ الْهلَال فَأَنت طَالِق قَالَ هَذَا أجل مثل الْمُتْعَة الَّذِي','1','385',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (262,'يَتَزَوَّجهَا إِلَى الْهلَال قَالَ فَهِيَ طَالِق السَّاعَة وَقَالَ بعض النَّاس هَذَا مُخَالف للمتعة لِأَن الْمُتْعَة إِنَّمَا تزَوجهَا إِلَى أجل فَكَانَ عقد النِّكَاح فَاسِدا وَالَّذِي يَقُول لَهَا إِذا جَاءَ الْهلَال فَأَنت طَالِق فَالنِّكَاح ثَابت إِلَّا إِنَّه وَقت وقتا هما مُخْتَلِفَانِ
<span data-type="title" id=toc-272>حكم البيع على البرنامج بِدُونِ ذكر الثّمن</span>

٣٦٥ - وَسَأَلته عَن رجل يَجِيئهُ البرنامج من السمسار أَنِّي حملت لَك مَتَاع كَذَا وَكَذَا فيجيئه المُشْتَرِي فَيَقُول ادْفَعْ إِلَى البرنامج فَإِذا وصل الْمَتَاع إِلَيْك فاحمله إِلَيّ فَإِنِّي لَا أخالفك فَلَمَّا وصل الْمَتَاع حمله إِلَيْهِ فنشره المُشْتَرِي فرضيه ثمَّ حمله من تِلْكَ إِلَى بلد آخر ثمَّ جَاءَ من بعد شهر','1','386',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (263,'فَقَالَ كَيفَ بِعْت قسم الْمَتَاع الَّذِي أخذت مِنْك فَقَالَ بِكَذَا وَكَذَا فَرضِي بِمَا قَالَ فألزم الْمَتَاع الرِّبْح ثمَّ حمل المَال وَقد اسْتهْلك الْمَتَاع فَهَل يصلح ذَلِك أم لَا فَقَالَ الْمَتَاع مَتَاع للْبَائِع بعد فَإِن اصطلحا على شَيْء بَينهمَا فَذَاك وَإِلَّا لزمَه قيمَة الْمَتَاع يَوْم بَاعَ
<span data-type="title" id=toc-273>من نَام عَن وتره حَتَّى سمع الْأَذَان</span>

٣٦٦ - سَأَلته عَن رجل نَام عَن وتره حَتَّى يسمع الْأَذَان قبل أَن يُوتر ترى يُوتر بِرَكْعَة ويخفف أَو بِثَلَاث فَقَالَ أما حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم إِذا خفت الْفَوْت فأوتر بِرَكْعَة','1','387',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (264,'وَأحب أَن يكون قبلهَا صَلَاة مُتَقَدّمَة
<span data-type="title" id=toc-274>هَل يَجْزِي قَوْله رَبنَا وَلَك الْحَمد عَن العطاس وَالْقِيَام عَن الرُّكُوع</span>

٣٦٧ - وَسَأَلته عَن رجل كَانَ يُصَلِّي فَأَرَادَ أَن يرْكَع فعطس فَلَمَّا رفع رَأسه من الرُّكُوع قَالَ رَبنَا وَلَك الْحَمد يَنْوِي بذلك لما عطس وللركوع قَالَ لَا يجْزِيه إِذا عطس فِي الصَّلَاة يحمد الله فِي نَفسه','1','388',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (265,'وَيَقُول إِذا رفع رَأسه من الرُّكُوع رَبنَا وَلَك الْحَمد وَإِذا كَانَ وَحده أَو كَانَ إِمَامًا يَقُول سمع الله لمن حَمده','1','389',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (266,'وَالَّذِي نَخْتَار أَن يَقُول رَبنَا وَلَك الْحَمد ملْء السَّمَاوَات وملء الأَرْض وملء مَا شِئْت من شَيْء بعد وَإِذا كَانَ خلف الإِمَام قَالَ رَبنَا وَلَك الْحَمد فَقَط لَا يزِيد','1','390',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (267,'<span data-type="title" id=toc-275>حكم بيع الْمُدبر وَهل يكون عتقه من الثُّلُث أَو من جَمِيع المَال</span>

٣٦٨ - وَسَأَلته عَن الْمُدبر أَمن جَمِيع المَال أم من الثُّلُث وَهل يجوز بَيْعه قَالَ هُوَ من الثُّلُث وَقَالَ لَا يَبِيع الْوَارِث الْمُدبر فَإِن كَانَ لَهُ من المَال بِقدر مَا يخرج من الثُّلُث عتق وَإِن لم يكن لَهُ من المَال إِلَّا العَبْد وَحده عتق مِنْهُ الثُّلُث وَيكون بَاقِيه رَقِيقا وَهُوَ الَّذِي أذهب إِلَيْهِ وَقَالَ بعض النَّاس يستسعى العَبْد فِي بَاقِيه قَالَ أبي الْمُدبر يَبِيعهُ سَيّده إِن شَاءَ','1','391',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (268,'<span data-type="title" id=toc-276>الْمَسْبُوق يسْجد مَعَ الإِمَام سَجْدَتي السَّهْو ثمَّ يقْضِي مَا فَاتَهُ من الصَّلَاة</span>

٣٦٩ - وَسَأَلته عَن رجل صلى مَعَ الإِمَام وَقد سبقه بِرَكْعَة فَلَمَّا كَانَ فِي آخر صلَاته أَرَادَ الإِمَام أَن يسْجد سَجْدَتي السَّهْو أيسجد مَعَ الإِمَام أم يتم صلَاته ثمَّ يسْجد قَالَ يسْجد مَعَ الإِمَام وَقَالَ أذهب إِلَى قَول النَّبِي صلى الله عَلَيْهِ وَسلم إِنَّمَا جعل الإِمَام ليؤتم بِهِ
<span data-type="title" id=toc-277>حكم الزَّكَاة فِي مَال العَبْد وَالْمكَاتب</span>

٣٧٠ - وَسَأَلته فِي عبد لَهُ مَال عَلَيْهِ فِيهِ الزَّكَاة أم الزَّكَاة على سَيّده وَهل فِي مَال مكَاتب زَكَاة قَالَ أما العَبْد فيستأذن سَيّده وَالْمكَاتب لَيْسَ فِي مَاله زَكَاة وَذَلِكَ أَن الْمكَاتب قد حيل بَين سَيّده وَبَين مَاله بالمكاتبة وَذَاكَ أَنه لَيْسَ لَهُ أَن يَأْخُذ مَاله فيعجز عَن مُكَاتبَته','1','392',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (269,'<span data-type="title" id=toc-278>هَل على الْمَدْيُون زَكَاة</span>

٣٧١ - قلت رجل لَهُ ألف دِرْهَم وَعَلِيهِ دين ألف دِرْهَم وَله من الْعرُوض لغير التِّجَارَة قيمَة ألف دِرْهَم هَل تجب عَلَيْهِ زَكَاة أم لَا قَالَ إِن كَانَ عرض لَا يديره للتِّجَارَة فَلَيْسَ عَلَيْهِ فِيهِ زَكَاة
<span data-type="title" id=toc-279>زَكَاة الْبُقُول وَالْخضر وَنَحْوهَا</span>

٣٧٢ - قلت مَا تَقول فِيمَا أخرجت الأَرْض من الْبُقُول والزعفران والرياحين والطرفاء والقصب الْفَارِسِي والحشيش فِيهِ الْعشْر أَو','1','393',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (270,'الزَّكَاة إِذا بيع وَقِيمَته مِائَتَا دِرْهَم وَحَال عَلَيْهِ الْحول قَالَ كل شَيْء من الْخضر والقثاء وَالْخيَار والبطيخ فَلَيْسَ فِيهِ زَكَاة إِلَّا فِي ثمنه إِذا حَال عَلَيْهِ الْحول وكل مَا كَانَ مثل العدس والحمص واللوبيا والأرز والذرة وَمَا يدّخر من الْفَوَاكِه حَتَّى يَقع فِيهِ الْكَيْل وَيكون مفارقا للخضر وكل مَا كَانَ يضْرب فِيهِ القفيز فَفِيمَا سقِِي مِنْهُ بالدوالي نصف الْعشْر وَمَا كَانَ سيحا أَو سقيا بالأنهار أَو سقته السَّمَاء فَفِيهِ الْعشْر
<span data-type="title" id=toc-280>بِمَا تستبرأ الْأمة الَّتِي لم تَحض وَقد قاربت الْبلُوغ</span>

٣٧٣ - وَسَأَلته إِذا اشْترى الرجل أمة وَهِي صبية لم تَحض وَقد قاربت الْحيض والإدراك بِمَا يَسْتَبْرِئهَا سَيِّدهَا قَالَ يستبريء بِثَلَاثَة أشهر وَذَلِكَ أقل مَا يبين فِيهِ الْحمل','1','394',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (271,'<span data-type="title" id=toc-281>حكم الزواج فِي دَار الْحَرْب من نِسَائِهِم</span>

٣٧٤ - قلت الرجل يدْخل دَار الْحَرْب فِي تِجَارَة أَله أَن يتَزَوَّج من نِسَائِهِم قَالَ هَذَا مَكْرُوه
<span data-type="title" id=toc-282>حكم التَّلْبِيَة فِي الْحَج</span>

٣٧٥ - قلت رجل لبس إزارا ورداء وَنوى الْإِحْرَام فَقَالَ اللَّهُمَّ إِنِّي أُرِيد الْحَج فيسره لي وتقبله مني وَلم يلب فِي ذَلِك الْوَقْت ثمَّ لبّى بعد مَعَ النَّاس حَتَّى قضى مَنَاسِكه أَيكُون دَاخِلا فِي الْإِحْرَام وقاضيا لحجة قَالَ هُوَ وَإِن لم يسم فَهُوَ على نِيَّته وَمَتى لبّى فقد أوجب عَلَيْهِ الْحَج','1','395',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (272,'<span data-type="title" id=toc-283>حج الْمغمى عَلَيْهِ</span>

٣٧٦ - قلت بلغ الْمِيقَات وَهُوَ مغمى عَلَيْهِ فَأحْرم عَنهُ رَفِيقه أَو غُلَامه وَقدم مَكَّة فَطَافَ بِهِ وسعى وَأَوْقفهُ بِعَرَفَات وَقضى عَنهُ جَمِيع الْمَنَاسِك أيجزيه ذَلِك من حجَّة الْإِسْلَام قَالَ إِن كَانَ أَفَاق بِعَرَفَة حَتَّى عقل أَجزَأَهُ الْحَج وَإِن كَانَ لم يعقل بِعَرَفَة فَلَا حج لَهُ
<span data-type="title" id=toc-284>حكم من أفطر يَوْمًا لمَرض فِي صِيَام كَفَّارَة الظِّهَار</span>

٣٧٧ - قلت رجل عَلَيْهِ كَفَّارَة ظِهَار فصَام شَهْرَيْن مُتَتَابعين غير أَنه مرض يَوْمًا مَرضا لم يُمكنهُ الصَّوْم قبل أَن يتم الشَّهْرَيْنِ فَأفْطر ترى لَهُ أَن يَبْنِي على صَوْمه أَو يسْتَقْبل الصَّوْم قَالَ يَبْنِي على صَوْمه
<span data-type="title" id=toc-285>كَيفَ يصنع من نذر صَوْم يَوْم الْفطر والأضحى</span>

٣٧٨ - قلت من نذر أَن يَصُوم يَوْم الْفطر وَيَوْم الْأَضْحَى كَيفَ يصنع وَمَا يجب عَلَيْهِ','1','396',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (273,'قَالَ أما ابْن عمر فَقَالَ أَمر الله بوفاء النّذر ونهانا رَسُول الله صلى الله عَلَيْهِ وَسلم عَن صِيَام هذَيْن الْيَوْمَيْنِ وَأما عقبَة بن عَامر فَقَالَ النّذر حلفه وَقَالَ لَا يَصُوم يَوْم النَّحْر','1','397',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (274,'وَلَا يَوْم الْفطر وَيكفر عَن يَمِينه ويصوم يَوْمًا
<span data-type="title" id=toc-286>امْرَأَة أفطرت فِي رَمَضَان يَوْمًا ثمَّ حَاضَت فِي آخِره</span>

٣٧٩ - قلت امْرَأَة أفطرت يَوْمًا فِي شهر رَمَضَان متعمدة فَلَمَّا كَانَ فِي آخر النَّهَار حَاضَت قَالَ لَا أوجب الْكَفَّارَة إِلَّا فِي الغشيان وَإِن فعلت خيرا فَلَا بَأْس فَإِن كَانَ بغشيان أَمرته بِمَا أَمر النَّبِي صلى الله عَلَيْهِ وَسلم','1','398',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (275,'وَقَالَ بعض النَّاس يجب عَلَيْهِ فِي الْأكل وَالشرب مَا يجب على الْمظَاهر
<span data-type="title" id=toc-287>عدد سكتات الإِمَام وَهل يقْرَأ فِي السكتة</span>

٣٨٠ - سَأَلت أبي قلت للْإِمَام سكتتين قَالَ نعم سكتة إِذا افْتتح الصَّلَاة','1','399',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (276,'وسكتة بعد مَا يفرغ من السُّورَة وَمن النَّاس من يَقُول إِذا فرغ من الْحَمد
٣٨١ - قلت فَيقْرَأ إِذا سكت الإِمَام قَالَ يقْرَأ فَإِذا قَرَأَ الإِمَام أمسك','1','400',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (277,'<span data-type="title" id=toc-288>حكم صَلَاة الرجل مَعَ صبي خلف الإِمَام</span>

٣٨٢ - قلت الرجل يُصَلِّي وَخَلفه رجل وَغُلَام قَالَ أما الْفَرِيضَة فَلَا يُصَلِّي حَتَّى يدْرك وَأما التَّطَوُّع فَلَا بَأْس بِهِ','1','401',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (278,'<span data-type="title" id=toc-289>كَيفَ يكون الْمُحَلّل فِي السَّبق</span>

٣٨٣ - قلت الْمُحَلّل كَيفَ يكون قَالَ يكون لِرجلَيْنِ فرسَان فَيخرج هَذَا سبقا وَيخرج هَذَا سبقا وَيَجِيء رجل آخر لَهُ فرس وَلَا يكون بدونهما فِي الجري فَأَيّهمَا سبق أَخذ سبقه وَإِن سبق الْمُحَلّل أَخذ السَّبق وَإِن سبق لم يكن عَلَيْهِ شَيْء','1','403',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (279,'<span data-type="title" id=toc-290>كَيفَ الْقصاص فِي الْيَد</span>

٣٨٤ - قلت رجل قطع يَد رجل فَأَرَادَ الْمَقْطُوع الْيَد أَن يقطع أصبعا من يَد الْقَاطِع قَالَ لَا يقطع إِلَّا من الْموضع الَّذِي قطعت يَده قَالَ الله تَعَالَى {والجروح قصاص}
<span data-type="title" id=toc-291>نِكَاح الْأمة على الْحرَّة أَو مَعَ طول للْحرَّة</span>

٣٨٥ - قلت رجل تَحْتَهُ أمة وَهُوَ يجد السَّبِيل إِلَى الْحرَّة فَلم يتَزَوَّج حرَّة وَمكث مُقيما مَعهَا دهرا لم يتَزَوَّج أَو كَانَت تَحْتَهُ حرَّة فَتزَوج عَلَيْهَا أمة قَالَ لَا يتَزَوَّج الْأمة على الْحرَّة','1','404',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (280,'وَيقسم للْحرَّة يَوْمَيْنِ وللأمة يَوْمًا يرْوى هَذَا عَن عَليّ وَقَالَ ابْن عَبَّاس إِذا وجد طولا للْحرَّة حرمت عَلَيْهِ الْأمة
<span data-type="title" id=toc-292>من أوصى بِعِتْق رَقَبَة مُسلم أَو كَافِر</span>

٣٨٦ - وَسَأَلته عَن رجل أوصى فَقَالَ أعتقوا عني رَقَبَة بِعشْرين دِينَارا مُسلم أَو غير مُسلم هَل يجوز ذَلِك قَالَ لَا يعْتق عَنهُ إِلَّا مُسلم','1','405',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (281,'<span data-type="title" id=toc-293>حكم شِرَاء الْوَصِيّ رَقَبَة بِأَكْثَرَ مِمَّا أوصى بِهِ</span>

٣٨٧ - قلت رجل أوصى إِلَى رجل أَن أعتق عني رَقَبَة بِخَمْسِمِائَة دِرْهَم فَاشْترى الْوَصِيّ رَقَبَة بستمائة دِرْهَم وَزَاد الْوَصِيّ من مَاله مائَة دِرْهَم وَنوى أَن هَذِه الرَّقَبَة بأجمعها عَن الْمَيِّت قَالَ لَا بَأْس بذلك
<span data-type="title" id=toc-294>حكم قَول الرجل لزوجته أَمرك بِيَدِك أَو اخْتَارِي نَفسك</span>

٣٨٨ - قلت إِلَى أَي شَيْء تذْهب فِي قَول الرجل لامْرَأَته أَمرك بِيَدِك أَو قَالَ لَهَا اخْتَارِي نَفسك قَالَ إِذا قَالَ لَهَا أَمرك بِيَدِك فَأمرهَا إِلَيْهَا إِلَى وَقت يرجع فِيمَا قَالَ أَو يطَأ وَإِذا قَالَ اخْتَارِي نَفسك فَهُوَ مَا دَامَت فِي مجلسها أَو يأخذان فِي شَيْء غير ماكانا فِيهِ فَإِن اخْتَارَتْ نَفسهَا فَوَاحِدَة يملك الرّجْعَة','1','406',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (282,'<span data-type="title" id=toc-295>أَقْوَال الْعلمَاء فِيمَن قَالَ لامْرَأَته أَنْت عَليّ حرَام</span>

٣٨٩ - قلت الرجل يَقُول لامْرَأَته أَنْت عَليّ لحرام قَالَ يجب عَلَيْهِ كَفَّارَة الظِّهَار يعْتق رَقَبَة فَإِن لم يجد صَامَ شَهْرَيْن مُتَتَابعين فَإِن لم يسْتَطع أطْعم سِتِّينَ مِسْكينا وَالنَّاس فِيهِ مُخْتَلفُونَ فَمنهمْ من يَقُول عَلَيْهِ أغْلظ الْكَفَّارَات وَهُوَ الَّذِي ذكرنَا وَمِنْهُم من يَقُول كَفَّارَة يَمِين وَمِنْهُم من يَقُول ثَلَاث','1','407',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (283,'وَمِنْهُم من ينويه وَيَقُول إِن نوى وَاحِدَة فبائن وَإِن نوى ثِنْتَيْنِ فَلَا تكون ثِنْتَيْنِ وَإِن نوى ثَلَاثًا فَثَلَاث
<span data-type="title" id=toc-296>مَا بَين الْمقَام إِلَى الْحجر قبر تِسْعَة وَتِسْعين نَبيا</span>

٣٩٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سليم عَن عبد الله بن عُثْمَان عَن عبد الرحمن بن سابط عَن عبد الله بن ضَمرَة السَّلُولي قَالَ مَا بَين الْمقَام إِلَى الرُّكْن إِلَى زَمْزَم إِلَى الْحجر قبر تِسْعَة وَتِسْعين نَبيا جَاءُوا حاجين فقبروا هُنَالك','1','408',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (284,'قَالَ أبي لم أسمع من يحيى بن سليم غير هَذَا الحَدِيث
<span data-type="title" id=toc-297>آخر الْجُزْء التَّاسِع من أَجزَاء صَالح</span>

<span data-type="title" id=toc-298>إِخْرَاج الْمثنى من وَاسِط لثنائه على بشر المريسي</span>

٣٩١ - حَدثنَا صَالح قَالَ قَالَ أبي بَلغنِي أَن الْمثنى الْأنمَاطِي قعد بواسط فَأثْنى على بشر المريسي فَقَامَ يزِيد بن هَارُون فَقَالَ لَا وَالله أَو ينفى مِنْهَا فَأخْرجهُ من وَاسِط','1','409',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (285,'<span data-type="title" id=toc-299>حِكَايَة عَن سُفْيَان بن عُيَيْنَة</span>

٣٩٢ - قَالَ أبي لما قيل لِسُفْيَان بن عُيَيْنَة من السُّلْطَان تكلمُوا فَقَالَ وجدْتُم مقَالا فتكلموا
<span data-type="title" id=toc-300>من نذر الْمَشْي إِلَى بَيت الله الْحَرَام وَصدقَة كل مَا يملكهُ وَعتق كل مَمْلُوك لَهُ</span>

٣٩٣ - حَدثنَا صَالح قَالَ قلت لأبي رجل قَالَ كل شَيْء أملكهُ الْيَوْم أَو أملكهُ إِلَى ثَلَاثِينَ سنة فِي الْمَسَاكِين وَعَلِيهِ الْمَشْي إِلَى بَيت الله الْحَرَام وكل مَمْلُوك لَهُ حر قَالَ إِذا قَالَ مَاله فِي الْمَسَاكِين وكل مَمْلُوك لَهُ حر إِن فعلت كَذَا وَكَذَا وَهُوَ يُرِيد الْيَمين فَإنَّا نَذْهَب إِلَى أَنه تجزيه كَفَّارَة يَمِين وَإِذا قَالَ مَاله فِي الْمَسَاكِين إِن بَرِئت من مرضِي أَو قدم أخي من سفر أَو أَتَى معافى فَقدم أَو برأَ فَإِنَّهُ يُخرجهُ من مَاله الثُّلُث فَيتَصَدَّق بِهِ وكل شَيْء يُرِيد بِهِ النّذر أَو الْقرْبَة إِلَى الله فَإِنَّهُ يجْزِيه فِي ذَلِك ثلث مَاله وكل شَيْء يُرِيد بِهِ الْيَمين فكفارة يَمِين وَقد قَالَ بعض النَّاس فِي الْحَج إِلَّا أَن','1','410',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (286,'يكون على جِهَة النّذر فَإِن كَانَ على جِهَة النّذر فَعَلَيهِ إِنْفَاذ ذَلِك إِلَّا أَن يكون معذبابه كَمَا نذرت أُخْت عقبَة بن عَامر أَن تحج حافية فَقَالَ النَّبِي صلى الله عَلَيْهِ وَسلم لعقبة إِن الله عَن تَعْذِيب أختك نَفسهَا لَغَنِيّ مرها فلتركب ولتكفر','1','411',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (287,'فَإِن كَانَ على وَجه النّذر فَعَلَيهِ الإنقاذ بِالْحَجِّ إِلَّا أَن يكون مِمَّن لَا يَسْتَطِيع الْمَشْي فيكفر عَن يَمِينه ويركب وَأما العبيد فأحرار قَالَ وَإِذا قَالَ امْرَأَته كَذَا وَكَذَا يُسَمِّي الطَّلَاق فَحنث فِي يَمِينه فَإِن ذَلِك يلْزمه الطَّلَاق وَالْعتاق لِأَن الطَّلَاق وَالْعتاق لاكفارة فيهمَا
<span data-type="title" id=toc-301>حكم بيع المَاء</span>

٣٩٤ - قلت مَا تَقول فِي بيع المَاء قَالَ يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه نهى عَن بيع المَاء فَقَالَ الَّذِي روى هَذَا الحَدِيث لَا أَدْرِي أَي مَاء هُوَ وَقَالَ عبد الله بن عَمْرو لقيم لَهُ وَمَاء فَأمره برده','1','412',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (288,'وَرُوِيَ عَن أبي هُرَيْرَة عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه قَالَ لَا يمْنَع فضل المَاء ليمنع بِهِ الْكلأ وَرُوِيَ عَن عَائِشَة أَن النَّبِي صلى الله عَلَيْهِ وَسلم نهى عَن منع نقع','1','413',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (289,'الْبِئْر فَمن النَّاس من يحْتَج بِحَدِيث أبي هُرَيْرَة فِي أَنه لَا يمْنَع فضل مَاء ليمنع الْكلأ فَقَالَ الَّذِي احْتج بِهَذَا الحَدِيث إِذا كَانَ لي أَن أبيع مائي وَلَيْسَ فِيهِ فضل فلي أَن أبيعه ولي أَن أمْنَعهُ وَأما فضل المَاء الَّذِي نهى عَنهُ فَإِنَّمَا نهى عَنهُ ليمنع بِهِ الْكلأ وَذَلِكَ أَن الْكلأ شَيْء مُبَاح لَيْسَ لأحد فِيهِ كلفة فَمَتَى منع هَذَا فضل مَائه لم يرع النَّاس حوله وَلم يَجدوا مَا','1','414',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (290,'يشربون فَكَأَنَّهُ منع الْكلأ
<span data-type="title" id=toc-302>حكم الشُّفْعَة فِي الشّرْب</span>

٣٩٥ - سَأَلته عَمَّن بَاعَ أَرضًا بشربها وَله شرب يعرف بِهَذِهِ الأَرْض لم يزل يشْربهَا وَهُوَ شرب لَيْسَ يعقله أهل الْبَلَد بِالصّفةِ فجَاء شَفِيع هَذِه الأَرْض أَرَادَ شفعته فَقَالَ لَهُ المُشْتَرِي إِنَّمَا تجب الشُّفْعَة فِي الْعقار أَرض أَو دَار وَهَذَا الشّرْب هُوَ مَاء وَلَيْسَ هُوَ مِمَّا يجب فِيهِ شُفْعَة وَإِنَّمَا لَك الأَرْض بِقِيمَتِهَا قَالَ أبي النَّاس مُخْتَلفُونَ فِي الشُّفْعَة فَأهل الْحجاز يذهبون إِلَى أَنه إِذا ط طرقت الطّرق وَعرف النَّاس حدودهم فَلَا شُفْعَة إِلَّا للخليط وَلَا','1','415',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (291,'شُفْعَة للْجَار وَقَالَ أهل الْعرَاق للْجَار شُفْعَة وَقَالَ بَعضهم إِنَّمَا تكون الشُّفْعَة للْجَار إِذا كَانَ فِي طريقهما وَاحِدًا فَأَما هَذَا الَّذِي بَاعَ أرضه فَلِلْمُشْتَرِي حُقُوق هَذِه الأَرْض وَالشرب من حُقُوقهَا وَفِي قَول من قَالَ للْجَار شُفْعَة فَإِنَّمَا يَأْخُذهَا وشربها بِمَا ملكهَا المُشْتَرِي وللماء ثمَّ حِصَّة من الثّمن فلولا أَن للْمَاء حِصَّة مَا اشْتَرَاهَا المُشْتَرِي وَلكنه اشْتَرَاهَا بشربها','1','416',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (292,'وحقوقها فَإِنَّمَا يملكهَا الشَّفِيع بِمَا ملك المُشْتَرِي وَلَا تسْقط حِصَّة المَاء من الثّمن وَالْحجّة فِي ذَلِك أَنه إِذا أشترى الرجل الدَّار اشْتَرَاهَا بحقوقها كلهَا دَاخل فِيهَا وخارج مِنْهَا وبطرقها ومسيل ماءها وَلَو بيع هَذَا على الِانْفِرَاد لم يكن بيعا
<span data-type="title" id=toc-303>أَقْوَال أَحْمد فِي رجال</span>

٣٩٦ - سَمِعت أبي يَقُول عَبْثَر ثِقَة صَدُوق
٣٩٧ - قلت حديج قَالَ لَا أعلم إِلَّا خيرا','1','417',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (293,'٣٩٨ - قلت ابْن أبي الزِّنَاد قَالَ مُضْطَرب الحَدِيث
<span data-type="title" id=toc-304>حكم كِتَابَة شَيْء من الْقُرْآن وَدَفنه للآبق</span>

٣٩٩ - قلت يكْتب الشَّيْء من الْقُرْآن فِي قرطاس ويدفن للآبق قَالَ لَا بَأْس بِهِ
<span data-type="title" id=toc-305>رِوَايَة فِي توسيع النَّفَقَة على الْعِيَال يَوْم عَاشُورَاء</span>

٤٠٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي حَدثنَا سُفْيَان بن عُيَيْنَة قَالَ حَدثنِي جَعْفَر الْأَحْمَر عَن إِبْرَاهِيم بن مُحَمَّد بن الْمُنْتَشِر قَالَ أبي ثِقَة صَدُوق','1','418',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (294,'أَنه بلغه أَنه من وسع على عِيَاله يَوْم عَاشُورَاء أوسع الله عَلَيْهِ سَائِر سنته','1','419',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (295,'<span data-type="title" id=toc-306>أَقْوَال أَحْمد فِي رجال</span>

٤٠١ - سَأَلت أبي عَن معلي قَالَ كَانَ من أَصْحَاب أبي حنيفَة
٤٠٢ - وَسَأَلته عَن شَبابَة','1','420',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (296,'فَقَالَ كَانَ يذهب إِلَى الإرجاء زَعَمُوا أَنه غير نَحوا من خمسين حَدِيثا
٤٠٣ - قَالَ أبي كَانَ عَمْرو بن مرّة ذهب إِلَى الإرحاء بِآخِرهِ وَكَانَ قيس بن مُسلم وعلقمة بن مرْثَد مرجيين
<span data-type="title" id=toc-307>حكم حبس الْبَوْل</span>

٤٠٤ - وَسَأَلته عَن الرجل يحبس الْبَوْل قَالَ مَا لم يعجله فَلَا بَأْس','1','421',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (297,'<span data-type="title" id=toc-308>قِرَاءَة مُحَمَّد بن سعيد التِّرْمِذِيّ</span>

٤٠٥ - قَالَ أبي كُنَّا عِنْد وهب بن جرير سنة مِائَتَيْنِ وَكَانَ مُحَمَّد بن سعيد التِّرْمِذِيّ قد نزل قَرِيبا من منزل أبي دَاوُد فَاجْتَمَعْنَا عِنْد وهب بن','1','422',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (298,'جرير فَقَالَ لي إِنْسَان قل لمُحَمد يقرل فَقلت مَا سَمِعت قِرَاءَته قطّ أَو كلَاما نَحْو هَذَا
٤٠٦ - فَقلت لأبي إِنَّه يَحْكِي عَنْك أَنَّك قلت مَا سَمِعت قِرَاءَته وَإِنِّي لأشتهي أَن أسمعها قَالَ قد كَانَ مني مَا أَخْبَرتك بِهِ وَمَا علمت إِلَّا خيرا إِلَّا هَذِه الْقِرَاءَة
<span data-type="title" id=toc-309>حكم دفع الرجل مَتَاعا ليَبِيعهُ بِكَذَا فَمَا ازْدَادَ فَهُوَ لَهُ</span>

٤٠٧ - وَسَأَلت أبي عَن الرجل يدْفع إِلَى الرجل الثَّوْب فَيَقُول بِعْهُ بِكَذَا وَكَذَا فَمَا ازددت فلك قَالَ لَا بَأْس بذلك','1','423',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (299,'٤٠٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ أخبرنَا عَمْرو بن دِينَار عَن عَطاء عَن ابْن عَبَّاس أَنه كَانَ لَا يرى بَأْسا أَن يدْفع الرجل إِلَى الرجل الثَّوْب فَيَقُول بِعْهُ بِكَذَا وَكَذَا فَمَا ازددت فلك
<span data-type="title" id=toc-310>قَول أَحْمد فِي سعيد بن جمْهَان</span>

٤٠٩ - سَأَلت أبي عَن سعيد بن جمْهَان قَالَ بَصرِي قد روى عَنهُ البصريون
<span data-type="title" id=toc-311>مسَائِل فِي التَّفْضِيل بَين الصَّحَابَة وَالتَّرْتِيب فِي الْخلَافَة</span>

٤١٠ - قلت إِلَى أَي شَيْء تذْهب فِي التَّفْضِيل قَالَ إِلَى حَدِيث ابْن عمر','1','424',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (300,'٤١١ - قلت وَتذهب إِلَى حَدِيث سفينة','1','425',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (301,'قَالَ نعم نستعمل الْخَبَرَيْنِ جَمِيعًا حَدِيث سفينة الْخلَافَة ثَلَاثُونَ سنة فَملك أَبُو بكر سنتَيْن وشيئا وَعمر عشرا وَعُثْمَان اثْنَتَيْ عشر وَعلي سِتا رضوَان الله عَلَيْهِم
٤١٢ - قلت فَإِن قَالَ قَائِل يَنْبَغِي لمن يثبت خلَافَة عَليّ أَن يربع بِهِ قَالَ إِنَّمَا نتبع مَا جَاءَ وَأما قَوْلنَا نَحن عَليّ عندنَا خَليفَة فقد سمى نَفسه أَمِير الْمُؤمنِينَ وَسَماهُ أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم أَمِين الْمُؤمنِينَ وَأهل بدر متوافرون يسمونه أَمِير الْمُؤمنِينَ ويحج بِالنَّاسِ وَيقطع ويرجم
٤١٣ - قلت فَإِن قَالَ قَائِل قد تَجِد الْخَارِجِي يخرج فيسمي بأمير الْمُؤمنِينَ','1','426',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (302,'ويسميه النَّاس بأمير الْمُؤمنِينَ قَالَ هَذَا قَول سوء خَبِيث يُقَاس عَليّ إِلَى رجل خارجي وَيُقَاس أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم إِلَى سَائِر النَّاس هَذَا قَول رَدِيء فَنَقُول إِنَّمَا كَانَ عَليّ خارجيا إِذن بئس القَوْل هَذَا','1','427',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (303,'<span data-type="title" id=toc-312>حكم القَوْل رَبنَا وَلَك الْحَمد</span>

٤١٤ - وَسَأَلته ترى أَن يَقُول الرجل اللَّهُمَّ رَبنَا وَلَك الْحَمد فَقَالَ أَحَادِيث الزُّهْرِيّ كلهَا رَبنَا وَلَك الْحَمد وَمَا سمعنَا أحدا','1','429',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (304,'قَالَ اللَّهُمَّ رَبنَا لَك الْحَمد إِلَّا أَن يَقُول رَبنَا لَك الْحَمد كَمَا جَاءَ الحَدِيث أَو يَقُول رَبنَا وَلَك الْحَمد
٤١٥ - قلت لَا يُعْجِبك أَن يَقُول اللَّهُمَّ رَبنَا وَلَك الْحَمد فَقَالَ مَا سمعنَا فِي هَذَا شَيْئا','1','430',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (305,'<span data-type="title" id=toc-313>قَول أَحْمد فِي عَليّ بن الْحسن بن شَقِيق</span>

٤١٦ - سَأَلت أبي عَن عَليّ بن الْحسن بن شَقِيق فَقَالَ كَانَ قدم علينا فَذَهَبت إِلَيْهِ وكتبت عَنهُ ثمَّ نحلوه إِلَى الإرجاء فَجَلَست عَنهُ ثمَّ إِنَّه انْتَفَى مِنْهُ','1','431',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (306,'<span data-type="title" id=toc-314>حكم بيع السَّيْف الْمحلى بِذَهَب أَو فضَّة</span>

٤١٧ - وَسَأَلته عَن السَّيْف الْمحلى يُبَاع بِذَهَب أَو فضَّة قَالَ لَا يُعجبنِي
٤١٨ - قلت تذْهب إِلَى حَدِيث فضَالة بن عبيد عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ نعم','1','432',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (307,'<span data-type="title" id=toc-315>أَحْكَام الْعُمْرَى والرقبى وَالسُّكْنَى</span>

٤١٩ - وَسَأَلته مَا قَوْلك فِي الْعُمْرَى قَالَ جَائِزَة هِيَ لمن أعمرها ولورثته','1','433',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (308,'٤٢٠ - قلت فَإِن قَالَ إِذا مت رجعت إِلَيّ قَالَ لَيْسَ هَذَا عمرى هَذِه رقبى
٤٢١ - قلت فالرقبى كَيفَ هِيَ قَالَ يَقُول هَذِه الدَّار لَك حياتك فَإِذا مت فَهِيَ لغيرك لرجل يُسَمِّيه أَو ترجع إِلَيّ
٤٢٢ - قلت فالسكنى قَالَ السُّكْنَى غير الْعُمْرَى إِذا أسْكنهُ الدَّار رجعت إِلَيْهِ على كل حَال','1','434',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (309,'<span data-type="title" id=toc-316>وَقت الْقُنُوت وَقَضَاء رَكْعَتي الْفجْر</span>

٤٢٣ - وَسَأَلته عَن الْقُنُوت فَقَالَ مذهبي فِي الْقُنُوت فِي شهر رَمَضَان أَن يقنت فِي النّصْف الآخر وَإِن قنت السّنة كلهَا فَلَا بَأْس وَإِن كَانَ إِمَام يقنت قنت خَلفه
٤٢٤ - وَقَالَ من فَاتَهُ رَكعَتَا الْفجْر فَإِنَّهُ يقضيهما إِذا أضحى بعد طُلُوع الشَّمْس وَهُوَ مذْهبه
<span data-type="title" id=toc-317>حكم الطَّلَاق قبل النِّكَاح</span>

٤٢٥ - وَسَأَلته عَن الطَّلَاق قبل النِّكَاح قَالَ وَقت أَو لم يُوَقت فَهُوَ وَاحِد مذْهبه إِذا تزوج أَن لَا يفارقها','1','435',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (310,'وَإِن لم يتَزَوَّج فَإِن تزوج غَيرهَا أحب إِلَيّ وَإِن خَافَ على نَفسه فَتَزَوجهَا فَلَا بَأْس
<span data-type="title" id=toc-318>كَيفَ يعْمل فِي التَّشَهُّد الْأَخير من فَاتَهُ بعض الصَّلَاة مَعَ الإِمَام</span>

٤٢٦ - وَسَأَلته عَن رجل فَاتَهُ بعض الصَّلَاة مَعَ الإِمَام قَالَ إِذا جلس مَعَ الإِمَام فِي آخر صلَاته فَإِنَّهُ يردد التَّشَهُّد وَلَا يَدْعُو
<span data-type="title" id=toc-319>كَيْفيَّة صَلَاة التَّطَوُّع على ظهر الدَّابَّة</span>

٤٢٧ - وَسَأَلته عَن الرجل يُصَلِّي التَّطَوُّع على ظهر الدَّابَّة أَيْنَمَا تَوَجَّهت بِهِ قَالَ إِذا كبر جعل وَجهه إِلَى الْقبْلَة فَكبر وَوَجهه إِلَى الْقبْلَة وَإِن كَانَ فِي محمل فَقدر أَن يسْجد فِي الْمحمل فليسجد','1','436',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (311,'<span data-type="title" id=toc-320>حكم من حلف بِثَلَاثِينَ حجَّة</span>

٤٢٨ - وَسَأَلته عَن رجل حلف بِثَلَاثِينَ حجَّة فَقَالَ لَا أَقُول فِي هَذَا شَيْئا وَإِن قَالَ عَليّ حجَّة إِن فعلت كَذَا وَكَذَا قَالَ لَا أحملهُ على الْحِنْث وَإِن حنث فَعَلَيهِ كَفَّارَة يَمِين وَالْكَفَّارَة مَدين من حِنْطَة
<span data-type="title" id=toc-321>الْأَب ينْحل لبَعض الْأَوْلَاد</span>

٤٢٩ - وَسَأَلته عَن رجل لَهُ أَوْلَاد فزوج بعض بَنَاته فجهزها وَأَعْطَاهَا قَالَ يُعْطي جَمِيع وَلَده مثل مَا أَعْطَاهَا','1','437',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (312,'<span data-type="title" id=toc-322>حكم لعان الزَّوْجَة الْكِتَابِيَّة</span>

٤٣٠ - وَسَأَلته عَن الْيَهُودِيَّة والنصرانية تكون تَحت الْمُسلم فيقذفها قَالَ يلاعنها
<span data-type="title" id=toc-323>حكم شَهَادَة الْقَاذِف إِذا تَابَ</span>

٤٣١ - وَسَأَلته عَن الْقَاذِف إِذا تَابَ قَالَ تقبل شَهَادَته
٤٣٢ - قلت جلد أَو لم يجلد قَالَ نعم أذهب إِلَى قَول عمر بن الْخطاب وتوبته أَن يكذب نَفسه','1','438',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (313,'أَن يَتُوب مِمَّا قذف بِهِ
<span data-type="title" id=toc-324>عدَّة أم الْوَلَد الْمُتَوفَّى عَنْهَا سَيِّدهَا</span>

٤٣٣ - وَسَأَلته عَن عدَّة أم الْوَلَد قَالَ حَيْضَة يذهب إِلَى أَنَّهَا أمة قَالَ لَو كَانَ عدتهَا أَرْبَعَة أشهر وَعشرا ورثت','1','439',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (314,'<span data-type="title" id=toc-325>حكم الصَّلَاة خلف الصَّفّ وَحده</span>

٤٣٤ - وَسَأَلته عَن الرجل يُصَلِّي خلف الصَّفّ وَحده قَالَ يُعِيد الصَّلَاة
<span data-type="title" id=toc-326>حكم طَلَاق المختلعة</span>

٤٣٥ - وَسَأَلته عَن المختلعة يطلقهَا زَوجهَا وَهِي فِي عدتهَا قَالَ لَا يلْحقهَا الطَّلَاق','1','440',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (315,'<span data-type="title" id=toc-327>حكم من قَالَ لامْرَأَته أَنْت طَالِق ثَلَاث مَرَّات أَو طلق غير مَدْخُول بهَا ثَلَاثًا</span>

٤٣٦ - وَسَأَلته عَن رجل قَالَ لامْرَأَته أَنْت طَالِق أَنْت طَالِق أَنْت طَالِق قَالَ إِن كَانَت غير مَدْخُول بهَا فَهِيَ وَاحِدَة لِأَنَّهَا بَانَتْ بِالْأولَى وَإِن كَانَت مَدْخُولا بهَا فَأَرَادَ أَن يفهمها وَيعلمهَا وَيُرِيد بذلك الأولى وَاحِدَة فأرجو أَن تكون وَاحِدَة وَإِلَّا فَثَلَاث
٤٣٧ - قلت فَإِن طلق الَّتِي لم يدْخل بهَا ثَلَاثًا قَالَ لَا تحل لَهُ حَتَّى تنْكح زوجا غَيره','1','441',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (316,'<span data-type="title" id=toc-328>حكم الظِّهَار قبل النِّكَاح</span>

٤٣٨ - وَسَأَلته عَن الظِّهَار قبل النِّكَاح قَالَ يلْزمه لِأَنَّهُ يَمِين وَلَيْسَ بِمَنْزِلَة الطَّلَاق نَذْهَب إِلَى حَدِيث عمر بن الْخطاب
<span data-type="title" id=toc-329>الْمحرم الْمُضْطَر يجد الْميتَة وَالصَّيْد</span>

٤٣٩ - وَسَأَلته عَن الْمحرم يضْطَر إِلَى الْميتَة وَالصَّيْد قَالَ يَأْكُل الْميتَة','1','442',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (317,'٤٤٠ - قلت فَإِن أضطر إِلَى الصَّيْد قَالَ يصيد وَيَأْكُل وَيكفر
<span data-type="title" id=toc-330>الْمدَّة الَّتِي يتم فِيهَا الصَّلَاة وَحكم الْقصر بمنى إِذا خرج إِلَيْهَا</span>

٤٤١ - وَسَأَلته عَن الْمُسَافِر إِذا دخل مَكَّة فَنوى أَن يُقيم أَرْبَعَة أَيَّام وَزِيَادَة صَلَاة قَالَ يتم فَإِذا خرج إِلَى منى قصر لِأَنَّهُ قد ابْتَدَأَ فِي السّفر حِين خرج إِلَى منى','1','443',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (318,'<span data-type="title" id=toc-331>الْوتر على ظهر الدَّابَّة</span>

٤٤٢ - وَسَأَلته عَن الرجل يُوتر على ظهر الدَّابَّة قَالَ نعم
٤٤٣ - قلت أَيْنَمَا كَانَ وَجهه قَالَ نعم
<span data-type="title" id=toc-332>حكم الشُّفْعَة للشَّرِيك</span>

٤٤٤ - وَسَأَلته عَن الشُّفْعَة للشَّرِيك وَاجِبَة قَالَ نعم
٤٤٥ - قلت فَإِن كَانُوا شُرَكَاء عدَّة قَالَ الشُّفْعَة بَينهم وَقَالَ الشُّفْعَة لَا تجب إِلَّا بعد البيع','1','444',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (319,'<span data-type="title" id=toc-333>من قَالَ لامْرَأَته اخْتَارِي</span>

٤٤٦ - وَسَأَلته عَن الرجل يَقُول لامْرَأَته اخْتَارِي قَالَ فَإِن اخْتَارَتْ نَفسهَا فَوَاحِدَة وَإِن اخْتَارَتْ زَوجهَا فَلَا شَيْء
<span data-type="title" id=toc-334>حكم قَضَاء الْمغمى عَلَيْهِ الصَّلَاة</span>

٤٤٧ - وَسَأَلته عَن الْمغمى عَلَيْهِ','1','445',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (320,'قَالَ يُعِيد الصَّلَاة كلهَا
<span data-type="title" id=toc-335>مَسْأَلَة فِي بيع دَار مُشْتَركَة مساومة أَو مُرَابحَة</span>

٤٤٨ - وَسَأَلته عَن دَار بَين ثَلَاثَة اشْترى أحدهم ثلثهَا بِمِائَة وَاشْترى الآخر الثُّلُث الآخر بمائتين وَاشْترى الآخر الثُّلُث الآخر بثلاثمائة فَبَاعُوهَا مساومة أَو مُرَابحَة قَالَ الثّمن بَينهم بِالسَّوِيَّةِ','1','446',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (321,'<span data-type="title" id=toc-336>الْمَشْي أَمَام الْجِنَازَة</span>

٤٤٩ - وَقَالَ الْمَشْي أَمَام الْجِنَازَة أعجب إِلَيّ وَيكون قَرِيبا مِنْهَا
<span data-type="title" id=toc-337>الْمضَارب إِذا خَالف</span>

٤٥٠ - وَسَأَلته عَن الْمضَارب إِذا خَالف قَالَ بِمَنْزِلَة الْوَدِيعَة عَلَيْهِ الضَّمَان وَالرِّبْح لرب المَال إِذا خَالف إِلَّا أَن الْمضَارب أعجب إِلَيّ أَن يعْطى بِقدر مَا عمل','1','448',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (322,'<span data-type="title" id=toc-338>إِذا أُقِيمَت الْفَرِيضَة وَهُوَ فِي النَّافِلَة</span>

٤٥١ - وَسَأَلته عَن الرجل يفْتَتح الرَّكْعَتَيْنِ قبل صَلَاة الْفجْر ثمَّ تُقَام الصَّلَاة قَالَ يتم الرَّكْعَتَيْنِ ثمَّ يدْخل مَعَ الْقَوْم فِي الصَّلَاة
<span data-type="title" id=toc-339>الْمسْح على النَّعْلَيْنِ</span>

٤٥٢ - وَسَأَلته عَن الْمسْح على النَّعْلَيْنِ قَالَ إِذا كَانَ فِي الْقدَم جوربان قد ثبتا فِي الْقدَم فَلَا بَأْس أَن يمسح على النَّعْلَيْنِ','1','449',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (323,'<span data-type="title" id=toc-340>حكم الْوضُوء من لُحُوم الْإِبِل وَأَلْبَانهَا وَشرب أبوالها للدواء</span>

٤٥٣ - وَسَأَلته عَن الْوضُوء من لُحُوم الْإِبِل قَالَ يتَوَضَّأ
٤٥٤ - قلت فالوضوء من أَلْبَانهَا قَالَ لَا يتَوَضَّأ من أَلْبَانهَا
٤٥٥ - قلت يشرب أبوالها للدواء قَالَ لَا بَأْس بِهِ','1','450',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (324,'<span data-type="title" id=toc-341>حكم ذَكَاة الْجَنِين</span>

٤٥٦ - وسمعته يَقُول ذَكَاة الْجَنِين ذَكَاة أمه
٤٥٧ - قلت أشعر أَو لم يشْعر قَالَ نعم
<span data-type="title" id=toc-342>أقل الْحيض وَأَكْثَره</span>

٤٥٨ - وَسَأَلته كم أقل الْحيض قَالَ أما الَّذِي أخْتَار أَنا فأقله يَوْم
٤٥٩ - قلت فكم أَكْثَره قَالَ خَمْسَة عشر يَوْمًا
٤٦٠ - قلت لَا يكون أَكثر من خَمْسَة عشر يَوْمًا قَالَ لَا','1','451',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (325,'<span data-type="title" id=toc-343>كَيفَ يقْضِي من أدْرك رَكْعَة أَو رَكْعَتَيْنِ من الظّهْر</span>

٤٦١ - وَسَأَلته عَن الرجل يدْرك رَكْعَتَيْنِ من الظّهْر مَعَ الإِمَام قَالَ يقْرَأ فِيمَا يقْضِي فِي كل رَكْعَة الْحَمد وَسورَة وَإِن أدْرك رَكْعَة مَعَ الإِمَام فَإِنَّهُ يقوم فَيقْرَأ الْحَمد وَسورَة ثمَّ يجلس ثمَّ يقوم فَيقْرَأ الْحَمد وَسورَة وَلَا يجلس ثمَّ يقوم فَيقْرَأ الْحَمد وَحدهَا ثمَّ يجلس
<span data-type="title" id=toc-344>هَل الْعَارِية مُؤَدَّاة</span>

٤٦٢ - سَأَلته عَن الْعَارِية مُؤَدَّاة','1','452',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (326,'قَالَ الْعَارِية مُؤَدَّاة خَالف أَو لم يُخَالف فَهُوَ ضَامِن وَذكر حَدِيث سَمُرَة عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ على الْيَد مَا أخذت حَتَّى تُؤَدِّيه وَقَالَ الْعَارِية أَخَذتهَا الْيَد والوديعة دفعت إِلَيْك','1','453',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (327,'<span data-type="title" id=toc-345>حكم ربح الْوَدِيعَة</span>

٤٦٣ - وَسَأَلته عَن رجل استودع دَرَاهِم فَعمل بهَا فربح قَالَ الرِّبْح لرب المَال
<span data-type="title" id=toc-346>حكم الدّفن والحصاد لَيْلًا</span>

٤٦٤ - وَسَأَلته عَن الدّفن لَيْلًا فَقَالَ لَا بَأْس بِهِ وَكره جذاذ النّخل والحصاد بِاللَّيْلِ','1','454',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (328,'<span data-type="title" id=toc-347>من قَالَ حل الله حرَام وَلَيْسَت لَهُ امْرَأَة</span>

٤٦٥ - وَسَأَلته عَن رجل قَالَ حل الله حرَام وَلَيْسَت لَهُ امْرَأَة وَلم ينْو قَالَ أَرْجُو أَن تجزيه كَفَّارَة يَمِين','1','455',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (329,'<span data-type="title" id=toc-348>أَخذ القملة فِي الصَّلَاة</span>

٤٦٦ - وَسَأَلته عَن الرجل يكون فِي الصَّلَاة فَيَأْخُذ الْقمل قَالَ إِن قَتلهَا فَلَا بَأْس وَإِن دَفنهَا فَلَا بَأْس
<span data-type="title" id=toc-349>هَل يفْطر إِذا رأى هِلَال شَوَّال نَهَارا</span>

٤٦٧ - وَسَأَلته عَن هِلَال شَوَّال إِذا رَأَوْهُ نَهَارا قَالَ لَا يفطرون إِن رَأَوْهُ قبل الزَّوَال وَبعده فَإِنَّهُم لَا يفطرون حَتَّى يشْهد رجلَانِ من الْمُسلمين أَنَّهُمَا رأياه بالْأَمْس أذهب إِلَى حَدِيث عمر','1','456',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (330,'<span data-type="title" id=toc-350>كَيفَ تجْلِس الْمَرْأَة فِي الصَّلَاة</span>

٤٦٨ - وَسَأَلته عَن الْمَرْأَة كَيفَ تجْلِس فِي الصَّلَاة قَالَ تربع أَو تسدل
٤٦٩ - قلت كَمَا يسدل الرجل قَالَ نعم
٤٧٠ - قلت تقعي قَالَ لَا','1','457',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (331,'<span data-type="title" id=toc-351>حكم دفع الطَّعَام بدل الدَّرَاهِم فِي الْقَرْض والمحاباة فِيهِ</span>

٤٧١ - وَسَأَلته عَن رجل أقْرض رجل دَرَاهِم فَلَمَّا طَالبه بهَا قَالَ لَيْسَ عِنْدِي دَرَاهِم خُذ مني طَعَاما فَأخذ طَعَاما أرخص عَلَيْهِ وحاباه ونقصه من السّعر قَالَ لَا بَأْس بِهِ','1','458',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (332,'<span data-type="title" id=toc-352>حكم بيع مَا يجْرِي فِيهِ الرِّبَا نَسِيئَة ثمَّ شِرَاؤُهُ أَو أَخذ الْعرُوض بِثمنِهِ قبل الْقَبْض</span>

٤٧٢ - وَسَأَلته عَن رجل بَاعَ بيعا مِمَّا يُكَال أَو يُوزن إِلَى أجل فَلَمَّا جَاءَ الْأَجَل أعْطى مَا يُكَال أَو يُوزن فكرهه قَالَ إِن كَانَ هَذَا طَعَام بِطَعَام نسَاء
٤٧٣ - قلت أفيأخذ عرضا من الْعرُوض أَو مَا كَانَ قَالَ نعم لَا يَأْخُذ كَيْلا وَلَا وزنا','1','459',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (333,'<span data-type="title" id=toc-353>حكم الِاغْتِسَال من غسل الْمَيِّت</span>

٤٧٤ - وَسَأَلت عَن الرجل يغسل الْمَيِّت أيغتسل قَالَ لَا يَصح الحَدِيث فِيهِ وَلَكِن يتَوَضَّأ
<span data-type="title" id=toc-354>حكم قَضَاء التَّكْبِير على الْجِنَازَة</span>

٤٧٥ - وَسَأَلته عَن الرجل يفوتهُ التَّكْبِير عَن الْجِنَازَة أيقضيه قَالَ نعم','1','460',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (334,'<span data-type="title" id=toc-355>حكم الطَّلَاق ثَلَاثًا بنية الْوَاحِد وَعَكسه</span>

٤٧٦ - سَأَلته عَن رجل طلق ثَلَاثًا وَهُوَ يَنْوِي وَاحِدَة قَالَ هِيَ ثَلَاث
٤٧٧ - قلت طلق وَاحِدَة وَهُوَ يَنْوِي ثَلَاثًا قَالَ هِيَ وَاحِدَة قَالَ إِنَّمَا النِّيَّة فِيمَا خَفِي وَلَيْسَ فِيمَا ظهر','1','461',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (335,'<span data-type="title" id=toc-356>حكم الصَّلَاة فِي أعطان الْإِبِل ودمن الْغنم</span>

٤٧٨ - وَسَأَلته عَن الصَّلَاة فِي أعطان الْإِبِل فكرهه وَفِي دمن الْغنم فَرخص فِيهِ','1','462',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (336,'<span data-type="title" id=toc-357>هَل للْمَرْأَة الرُّجُوع فِيمَا وهبت لزَوجهَا من الْمهْر</span>

٤٧٩ - وَسَأَلته عَن امْرَأَة وهبت مهرهَا لزَوجهَا ثمَّ بدا لَهُ أَن يطلقهَا قَالَ إِذا كَانَ الزَّوْج سَأَلَهَا ذَلِك فلهَا أَن ترجع فِيهِ وَإِن لم يسْأَلهَا وَلكنهَا وهبته بِطيبَة نَفسهَا فَلَيْسَ لَهَا أَن ترجع
<span data-type="title" id=toc-358>من كبر تَكْبِيرَة الْإِحْرَام قبل الإِمَام</span>

٤٨٠ - وَسَأَلته عَن الرجل يكبر تَكْبِيرَة الإفتتاح قبل الإِمَام قَالَ هَذَا لَيْسَ مَعَ الإِمَام
قلت يُعِيد الصَّلَاة قَالَ نعم','1','463',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (337,'<span data-type="title" id=toc-359>حكم الْمسْح على الْخُفَّيْنِ إِذا لبسهما قبل تَمام الْوضُوء</span>

٤٨٢ - سَأَلته عَن الرجل غسل قَدَمَيْهِ فَلبس خفيه ثمَّ مَشى ثمَّ تَوَضَّأ وَمسح على خفيه قَالَ لَا يجوز فَأنكرهُ وَقَالَ هَذَا خلاف كتاب الله وَسنة رَسُول الله صلى الله عَلَيْهِ وَسلم قَالَ الله {إِذا قُمْتُم إِلَى الصَّلَاة فَاغْسِلُوا وُجُوهكُم وَأَيْدِيكُمْ إِلَى الْمرَافِق}
٤٨٣ - قلت حَدِيث ابْن جريج عَن عَطاء فَأنكرهُ وَقَالَ الَّذِي يرْوى عَن عَطاء التَّفْرِيق فِي الْوضُوء وَقَالَ','1','464',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (338,'أَدخل النَّبِي صلى الله عَلَيْهِ وَسلم رجلَيْهِ الْخُف وهما طاهرتان بِتمَام الْوضُوء','1','465',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (339,'<span data-type="title" id=toc-360>حكم صَلَاة الْجِنَازَة على الْقَبْر ومدتها</span>

٤٨٤ - وَسَأَلته عَن الصَّلَاة على الْقَبْر قَالَ جَائِز
٤٨٥ - قلت إِلَى كم تجوز قَالَ إِلَى شهر
٤٨٦ - قلت بِإِمَام قَالَ نعم
<span data-type="title" id=toc-361>حكم التَّيَمُّم لصَلَاة الْجِنَازَة وَالْعِيدَيْنِ</span>

٤٨٧ - وَسَأَلته عَن الرجل يحضر الْجِنَازَة وَهُوَ غير متوضىء أيتيمم وَيُصلي قَالَ اخْتلف النَّاس فِي هَذَا اخْتِلَافا كثيرا وَذكر عَن ابْن عمر أَنه كَانَ لَا يُصَلِّي على الْجِنَازَة إِلَّا وَهُوَ متوضىء','1','466',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (340,'٤٨٨ - قلت فالعيدين قَالَ أما الْعِيدَيْنِ فَلَا يُصَلِّي إِلَّا متوضأ الْبَتَّةَ','1','467',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (341,'<span data-type="title" id=toc-362>خُرُوج النِّسَاء لصَلَاة الْعِيدَيْنِ</span>

٤٨٩ - وَسَأَلته عَن النِّسَاء يخْرجن إِلَى الْعِيدَيْنِ قَالَ لَا يُعجبنِي فِي زَمَاننَا هَذَا لِأَنَّهُ فتْنَة','1','468',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (342,'<span data-type="title" id=toc-363>حكم شَهَادَة الْأَب للإبن والإبن للْأَب</span>

٤٩٠ - وَسَأَلته عَن شَهَادَة الإبن للْأَب وَشَهَادَة الْأَب للإبن فَقَالَ لَا تجوز لِأَن الْأَب لَهُ أَن يَأْخُذ من مَال ابْنه والإبن لَهُ أَن يَأْخُذ من مَال أَبِيه إِذا احْتَاجَ
<span data-type="title" id=toc-364>حكم شَهَادَة أحد الزَّوْجَيْنِ للْآخر وَالشَّرِيك لشَرِيكه</span>

٤٩١ - سَأَلته عَن شَهَادَة الزَّوْج لامْرَأَته وَالْمَرْأَة لزَوجهَا فَقَالَ لَا تجوز وَلَا تجوز شَهَادَة الشَّرِيك لشَرِيكه','1','469',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (343,'<span data-type="title" id=toc-365>حكم الْأَنْكِحَة الْفَاسِدَة وَالصَّدَاق فِيهَا</span>

٤٩٢ - وَقَالَ من تزوج على نِكَاح الشّغَار أَو تزوج امْرَأَة على عَمَّتهَا أَو خَالَتهَا فَإِنَّهُ يفرق بَينهمَا وَلها الْمهْر إِذا أَصَابَهَا وَإِن لم يكن دخل بهَا فَلَا شَيْء عَلَيْهِ','1','470',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (344,'٤٩٣ - قلت فَإِن خلا بهَا وَلم يَمَسهَا قَالَ إِذا أغلق بَابا وأرخى سترا فلهَا الْمهْر
<span data-type="title" id=toc-366>حكم الْجَهْر بآمين</span>

٤٩٤ - وَقَالَ يجْهر الإِمَام وَمن خَلفه بآمين
<span data-type="title" id=toc-367>إِذا أصَاب الثَّوْب الْجَنَابَة وَلم يعرف مَكَانهَا</span>

٤٩٥ - وَسَأَلته عَن الرجل يجنب فِي الثَّوْب فَيصَلي يَعْنِي وَلَا يعرف مَكَانَهُ','1','471',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (345,'قَالَ إِن شَاءَ غسل الثَّوْب كُله وَإِن شَاءَ فركه كُله
٤٩٦ - قلت ويجزيه الفرك قَالَ نعم
<span data-type="title" id=toc-368>حكم الشَّهَادَة على من لَا يعرف</span>

٤٩٧ - وَسَأَلته عَن الرجل يشْهد على من لَا يعرف فَقَالَ لَا يشْهد إِلَّا لمن يعرف وعَلى من يعرف','1','472',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (346,'<span data-type="title" id=toc-369>إِذا ظهر فِي الْمَبِيع عيب بعد تصرف المُشْتَرِي فِيهِ بِالْقطعِ وَنَحْوه</span>

٤٩٨ - وَسَأَلته عَن الرجل اشْترى ثوبا فَقَطعه وخاطه ثمَّ ظهر بِهِ عيب خرق أَو غَيره قَالَ إِن شَاءَ أَخذ الثَّوْب وَوضع لَهُ بِقدر مَا نَقصه الْعَيْب وَإِن شَاءَ رده على صَاحبه ورد مَعَه بِقدر مَا نقص من الثَّوْب لقطعه وخياطته
<span data-type="title" id=toc-370>حكم النِّكَاح بِغَيْر ولي</span>

٤٩٩ - وَسَأَلته عَن رجل تزوج امْرَأَة بِشُهُود بِغَيْر ولي قَالَ لَا يجوز','1','473',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (347,'<span data-type="title" id=toc-371>الْأَمِير أَحَق بِالتَّزْوِيجِ أَو القَاضِي</span>

٥٠٠ - وَسَأَلته عَن الْأَمِير أَحَق أَن يُزَوّج أَو القَاضِي قَالَ القَاضِي لِأَن إِلَيْهِ الْفروج وَالْأَحْكَام
<span data-type="title" id=toc-372>على من الصَدَاق إِذا زوج الْأَب ابْنه الصَّغِير</span>

٥٠١ - وَسَأَلته عَن رجل زوج ابْنه وَهُوَ صَغِير على من الصَدَاق قَالَ إِذا تقبل بِهِ الْأَب فَهُوَ على الْأَب وَإِلَّا فَهُوَ على الإبن
٥٠٢ - قلت إِن الإبن لما أدْرك قَالَ لَا أرْضى قَالَ لَيْسَ لَهُ ذَلِك','1','474',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (348,'<span data-type="title" id=toc-373>مسَائِل فِي نِكَاح العَبْد وطلاقه وَالصَّدَاق إِذا تزوج بِغَيْر إِذن سَيّده</span>

٥٠٣ - وَسَأَلته عَن العَبْد كم يتَزَوَّج قَالَ ثِنْتَيْنِ','1','475',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (349,'٥٠٤ - وَسَأَلته عَن مَمْلُوك تزوج بِغَيْر إِذن موَالِيه قَالَ لَا يجوز
٥٠٥ - قلت فَإِن أجَاز الموَالِي قَالَ فنكاح جَدِيد','1','504',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (350,'٥٠٦ - قلت فَإِن زوجه مَوْلَاهُ بيد من الطَّلَاق قَالَ بيد الْمَمْلُوك
٥٠٧ - قلت فَإِن تزوج بِغَيْر إِذن الْمولى فَدخل بهَا هَل لَهَا مهر قَالَ فِيهِ اخْتِلَاف قَالَ عُثْمَان بن عَفَّان لَهَا خمْسا الْمهْر
٥٠٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الله بن بكر قَالَ حَدثنَا سعيد عَن قَتَادَة عَن خلاس أَن غُلَاما لأبي مُوسَى تزوج','1','476',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (351,'مولاة أَحْسبهُ تيجان التَّيْمِيّ بِغَيْر إِذن أبي مُوسَى وَكتب فِي ذَلِك إِلَى عُثْمَان فَكتب إِلَيْهِ أَن فرق بَينهمَا وَأجر لَهَا الْخمسين من صَدَاقهَا وَكَانَ صَدَاقهَا خَمْسَة أَبْعِرَة','1','477',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (352,'قَالَ قَتَادَة فَذكرت ذَلِك لِبلَال فَقَالَ نعم ذَاك غلامنا رواح أَو رواح وَقَالَ يزِيد مولاة لتيجان أخي ابْن عَابس
٥٠٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أسود بن عَامر قَالَ حَدثنَا أبان عَن قَتَادَة عَن خلاس أَن غُلَاما لأبي مُوسَى يُقَال لَهُ رواح أَو رواح تزوج مولاة لتيجان فساق خَمْسَة أَبْعِرَة وَلم يكن مَوْلَاهُ اطلع عَلَيْهِ','1','478',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (353,'فَكتب بذلك أَبُو مُوسَى إِلَى عُثْمَان فَكتب إِلَيْهِ عُثْمَان أجز لَهَا بَعِيرَيْنِ ورد ثَلَاثَة قَالَ أبي وَأَنا أذهب إِلَيْهِ وَهُوَ فِي رَقَبَة العَبْد
<span data-type="title" id=toc-374>قِرَاءَة الْبَسْمَلَة فِي أول كل سُورَة</span>

٥١٠ - وسمعته يَقُول يقْرَأ الرجل بِسم الله الرَّحْمَن الرَّحِيم فِي أول كل سُورَة','1','479',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (354,'٥١١ - قلت الرجل يقْرَأ فَاتِحَة الْكتاب وَهُوَ فِي الصَّلَاة فَإِذا فرغ وافتتح سُورَة أُخْرَى يقْرَأ بِسم الله الرَّحْمَن الرَّحِيم قَالَ نعم وَلَا يجْهر بهَا لِأَن ابْن عمر قَرَأَهَا مرَّتَيْنِ حِين ابْتَدَأَ الْحَمد وَسورَة وعدها ابْن عَبَّاس آيَة','1','480',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (355,'<span data-type="title" id=toc-375>الْوضُوء لمن أَرَادَ معاودة الْوَطْء</span>

٥١٢ - وَسُئِلَ أبي وَأَنا شَاهد عَن الرجل يغشى أَهله ثمَّ يُرِيد أَن يعود فَقَالَ يتَوَضَّأ أحب إِلَيّ
<span data-type="title" id=toc-376>من نسي تَخْلِيل اللِّحْيَة ثمَّ صلى</span>

٥١٣ - وَسُئِلَ أبي عَن رجل نسي أَن يخلل لحيته ثمَّ صلى هَل يُعِيد قَالَ لَا يُعِيد','1','481',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (356,'<span data-type="title" id=toc-377>حكم تقلد السَّيْف للْمحرمِ</span>

٥١٤ - وَسَأَلته عَن الْمحرم يتقلد السَّيْف قَالَ إِذا خَافَ من عَدو
<span data-type="title" id=toc-378>مسَائِل فِي الْحَج عَن الْغَيْر</span>

٥١٥ - وَسُئِلَ عَن رجل لم يحجّ يصلح لَهُ أَن يَأْخُذ دَرَاهِم ويحج عَن غَيره قَالَ لَا قَالَ أبي وَلَا يُعجبنِي أَن يَأْخُذ الدَّرَاهِم ويحج عَن غَيره','1','482',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (357,'٥١٦ - وَسُئِلَ عَن رجل وَجب عَلَيْهِ الْحَج من خُرَاسَان يحجّ عَنهُ من مَكَّة قَالَ لَا يحجّ عَنهُ من مَوضِع وَجب عَلَيْهِ الْحَج
<span data-type="title" id=toc-379>لَا يقتل حر بِعَبْد</span>

٥١٧ - وَسَأَلته يُقَاد حر بِعَبْد قَالَ لَا يُقَاد','1','483',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (358,'<span data-type="title" id=toc-380>حكم السّمك الطافي</span>

٥١٨ - سَأَلته عَن السّمك الطافي قَالَ لَيْسَ بِهِ بَأْس وَقَالَ إِن أَبَا بكر أكله','1','484',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (359,'<span data-type="title" id=toc-381>قصَّة الْأَعْمَش مَعَ أبي حُصَيْن</span>

٥١٩ - وَسمعت أبي يَقُول قَالَ شُعْبَة لَو كلمت أَبَا حُصَيْن لطم عَيْني سَمِعت أبي يَقُول قَالَ أَبُو حُصَيْن كنت وَلَا يصطلي بناري قَالَ أبي كَانَ الْأَعْمَش يقريء فِي الْمَسْجِد وَكَانَ قاريء يقْرَأ على الْأَعْمَش فَقَالَ للقاريء إِذا قَرَأت الْحُوت فاهمزها قَالَ وَكَانَ أَبُو حُصَيْن يسمع قراءتهم وَكَانَ ابْن حُصَيْن يؤمهم قَالَ فصلى بهم فَقَرَأَ الْحُوت فهمزها فَلَمَّا انصرفوا قَالَ الْأَعْمَش لقد أصبح صلب الْحُوت مكسورا قَالَ فَقَامَ أَبُو حُصَيْن بالنعل فَلَطَمَهُ فَشَجَّهُ قَالَ وَكَانَ أَبُو','1','485',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (360,'حُصَيْن رجلا من الْعَرَب قَالَ فَلم يقل لَهُ الْأَعْمَش شَيْئا قَالَ فتحول الْأَعْمَش من بني أَسد قَالَ أبي قَالَ شُعْبَة قَالَ أَبُو مَرْيَم لأبي حُصَيْن حَدثَك يحيى بن وثاب أَن مسروقا حَدثهُ أَن عبد الله حَدثهُ قَالَ واجترأ عَلَيْهِ قَالَ أَبُو حُصَيْن نعم','1','486',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (361,'<span data-type="title" id=toc-383>حكم نقل الزَّكَاة من بلد إِلَى بلد</span>

٥٢٠ - سَأَلت أبي تخرج الزَّكَاة من بلد إِلَى بلد قَالَ لَا
٥٢١ - قيل لَهُ فَإِذا كَانَ يَدُور قَالَ ينظر أَكثر مقَامه وَأكْثر مَاله أَيْن هُوَ يُزَكِّيه ثمَّ','2','5',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (362,'<span data-type="title" id=toc-384>مَتى يجب الصَّوْم على الْمُتَمَتّع</span>

٥٢٢ - وَسَأَلته عَن الْمُتَمَتّع مَتى يجب عَلَيْهِ الصَّوْم قَالَ إِذا خَافَ أَن يقطع بِهِ صَامَ
<span data-type="title" id=toc-385>الْحَاج عَن الْغَيْر لَا ينْفق من مَال المحجوج عَنهُ على غَيره فِي الْحَج</span>

٥٢٣ - وَسُئِلَ عَن رجل أَخذ دَرَاهِم يحجّ بهَا فَأخْرج مَعَه أمه أَو بعض نِسَائِهِ','2','6',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (363,'قَالَ إِذا كَانَ ينْفق عَلَيْهَا من عِنْده فَلَا بَأْس بِهِ
٥٢٤ - قيل لَهُ فيخدمها وينزلها ويصعدها يجوز لَهُ ذَلِك قَالَ نعم أَرْجُو أَن لَا يضيق هَذَا عَلَيْهِ
<span data-type="title" id=toc-386>مِيرَاث الغرقى وَنَحْوهم</span>

٥٢٥ - سَأَلت أبي عَن أهل بَيت وجدوا موتى قَالَ أذهب إِلَى يُورث بعض من بعض وَكَذَلِكَ الغرقى أَيْضا','2','7',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (364,'<span data-type="title" id=toc-387>كم يصلى قبل الْجُمُعَة وَبعدهَا</span>

٥٢٦ - سَأَلت أبي كم يصلى بعد الْجُمُعَة قَالَ سِتّ رَكْعَات
٥٢٧ - قلت قبل الْأَذَان قَالَ كثير وَكَانَ ابْن عمر يُطِيل الصَّلَاة يَوْم الْجُمُعَة','2','8',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (365,'<span data-type="title" id=toc-388>مُدَّة صَلَاة الْجِنَازَة على الْقَبْر</span>

٥٢٨ - قَالَ أبي يصلى على الْقَبْر إِلَى شهر
<span data-type="title" id=toc-389>بِدعَة ترك الْعَمَل بزعم التَّوَكُّل</span>

٥٢٩ - سَأَلَ أبي وَأَنا شَاهد عَن قوم لَا يعْملُونَ وَيَقُولُونَ نَحن متوكلون قَالَ هَؤُلَاءِ مبتدعة
<span data-type="title" id=toc-390>التَّعْلِيم أحب من الْمَسْأَلَة</span>

٥٣٠ - قَالَ لَهُ إِنْسَان التَّعْلِيم أحب إِلَيْك أَو الْمَسْأَلَة قَالَ التَّعْلِيم أحب إِلَيّ من الْمَسْأَلَة','2','9',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (366,'<span data-type="title" id=toc-391>هَل الْقبْلَة تنقض الْوضُوء</span>

٥٣١ - وَسَأَلته عَن الرجل يقبل فَقَالَ إِذا كَانَ لشَهْوَة عَلَيْهِ الْوضُوء وَإِذا لم يكن لشَهْوَة فَلَيْسَ عَلَيْهِ الْوضُوء وَإِذا لم يكن لشَهْوَة فَلَيْسَ عَلَيْهِ شَيْء
<span data-type="title" id=toc-392>الْجنب يَأْكُل أَو يشرب</span>

٥٣٢ - وَسَأَلته عَن الْجنب يَأْكُل أَو يشرب قَالَ هُوَ أسهل من النّوم وَالنَّوْم يتَوَضَّأ','2','10',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (367,'<span data-type="title" id=toc-393>الْوضُوء لمن أَرَادَ معاودة الْوَطْء</span>

٥٣٣ - وَسَأَلته عَن الرجل تكون لَهُ الْجَوَارِي يأتيهن أجمع قَالَ إِذا أَرَادَ أَن يعود يتَوَضَّأ
٥٣٤ - قلت أَو كَذَا الْحَرَائِر قَالَ نعم','2','11',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (368,'<span data-type="title" id=toc-394>الْجنب يدْخل يَده فِي المَاء ينظر حره من برده</span>

٥٣٥ - وَسَأَلته عَن جنب وضع لَهُ مَاء فَأدْخل يَده ينظر حره من برده قَالَ إِن كَانَ أصْبع أَرْجُو أَن لَا يكون بِهِ بَأْس
٥٣٦ - قلت فاليد أجمع فَكَأَنَّهُ كرهه','2','12',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (369,'<span data-type="title" id=toc-395>الآذان رَاكِبًا</span>

٥٣٧ - وَسَأَلت أبي عَن الرجل يُؤذن وَهُوَ رَاكب قَالَ أَرْجُو قد كَانَ ابْن عمر يُؤذن وَهُوَ رَاكب','2','13',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (370,'<span data-type="title" id=toc-396>الطَّهَارَة من فضل الْجنب وَالْحَائِض</span>

٥٣٨ - سَأَلت أبي عَن فضل الْجنب وَالْحَائِض فَقَالَ إِذا خلت بِهِ فَلَا يُعجبنِي وَلَكِن إِذا كَانَا جَمِيعًا فَلَا بَأْس بِهِ','2','14',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (371,'<span data-type="title" id=toc-397>الصَّلَاة فِي السباخ وَالتَّيَمُّم مِنْهَا</span>

٥٣٩ - وَسَأَلت أبي عَن الصَّلَاة فِي السباخ وَالتَّيَمُّم مِنْهَا قَالَ أما الصَّلَاة فَجَائِز وَأما التَّيَمُّم فَلَا يُعجبنِي لانه لَا يثبت فِي يَدَيْهِ مِنْهُ شَيْء يخرج مِنْهَا إِلَى غَيرهَا قَالَ وَأطيب الصَّعِيد أَرض الْحَرْث
<span data-type="title" id=toc-398>الْإِقْرَار بِولد من فجور</span>

٥٤٠ - وَسَأَلت أبي عَن رجل أقرّ بِولد فِي مَرضه من خَادِم امْرَأَته أَو بِولد من فجور قَالَ لَا يلْزمه لقَوْل النَّبِي صلى الله عَلَيْهِ وَسلم الْوَلَد للْفراش وللعاهر الْحجر','2','15',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (372,'<span data-type="title" id=toc-399>خُرُوج الْمَرْأَة فِي الْعدة من الْمنزل الَّذِي مَاتَ فِيهِ زَوجهَا</span>

٥٤١ - قلت امْرَأَة جهلت وَخرجت من منزل زَوجهَا الَّذِي مَاتَ فِيهِ بعد جُمُعَة وَلم تعد إِلَيْهِ قَالَ لم يكن يَنْبَغِي لَهَا أَن تفعل لقَوْل النَّبِي صلى الله عَلَيْهِ وَسلم لفريعة امكثي فِي الْبَيْت الَّذِي أصبت فِيهِ حَتَّى يبلغ الْكتاب أَجله','2','16',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (373,'<span data-type="title" id=toc-400>حكم الصَّلَاة فِي الثَّوْب الَّذِي ينشف فِيهِ الْمَيِّت</span>

٥٤٢ - قلت الثَّوْب الَّذِي ينشف فِيهِ الْمَيِّت يصلى فِيهِ قَالَ أَرْجُو إِن لم يكن أثر وَقد رُوِيَ عَن الْحسن أَنه لم يكن يرى بِهِ بَأْسا
<span data-type="title" id=toc-401>تكفين الْمَيِّت على المغتسل</span>

٥٤٣ - قلت يُكفن الْمَيِّت على المغتسل قَالَ إِذا كَانَ جافا فأرجو','2','17',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (374,'<span data-type="title" id=toc-402>إطْعَام الصغار من كَفَّارَات الْيَمين</span>

٥٤٤ - قلت يطعم الصغار فِي كَفَّارَات الْيَمين قَالَ الْكِبَار أعجب إِلَيّ فَإِن كَانَ مِمَّن يطعم الطَّعَام فَأَقل مَا قيل فِيهِ عشرَة مَسَاكِين مد لكل مِسْكين إِن كَانَ من وَصِيَّة فَلَا يزدادون على مد مد وَإِن كَانَ رجل يكفر عَن يَمِينه فشاء أَن يُعْطي نصف صَاع بر أعْطى أَو صَاع تمر أَو صَاع شعير وَالْمدّ ربع الصَّاع والصاع خَمْسَة أَرْطَال وَثلث بِالْبرِّ
<span data-type="title" id=toc-403>النفط طَاهِر</span>

٥٤٥ - قلت الثَّوْب يُصِيبهُ النفط','2','18',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (375,'قَالَ لَيْسَ النفط عِنْدِي بِنَجس
<span data-type="title" id=toc-404>حكم التَّعَوُّذ وَنَحْوه فِي الرُّكُوع وَالسُّجُود وَنَحْوه</span>

٥٤٦ - قلت من قَالَ فِي سُجُوده أعوذ بِاللَّه أَو فِي رُكُوعه بِسم الله أَو نَحْو هَذَا مِمَّا يذكرهُ فِي الصَّلَاة قَالَ لَا يَنْبَغِي أَن يفعل فَإِن فعل فأرجو
<span data-type="title" id=toc-405>من صلى وَعَلِيهِ أثر الزَّعْفَرَان أَو الخلوق</span>

٥٤٧ - قلت من صلى وَبِيَدِهِ شَيْء من أثر زعفران أَو خلوق أَو على أَنفه','2','19',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (376,'قَالَ أَرْجُو وَقد نهى أَن يتزعفر الرجل
<span data-type="title" id=toc-406>مسَائِل من أَبْوَاب الْحَج</span>

٥٤٨ - قلت من دخل الْمَسْجِد من غير بَاب بني شيبَة قَالَ لَا بَأْس','2','20',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (377,'٥٤٩ - قلت فَإِن خرج إِلَى السَّعْي من غير بَاب الصَّفَا قَالَ لَا بَأْس
٥٥٠ - قلت من سَهَا فابتدأ بِطواف الْحَج قبل طواف الْعمرَة قَالَ نَحن نقُول يجْزِيه طواف وَاحِد
٥٥١ - قلت وَحين اشْترط ابْتَدَأَ بِذكر الْحَج قبل قَالَ على نِيَّته
٥٥٢ - قلت من زار الْبَيْت وودعه يَأْخُذ فِي أَي طَرِيق شَاءَ قَالَ نعم','2','21',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (378,'٥٥٣ - قلت يدْخل الْمَسْجِد من أَي بَاب شَاءَ قَالَ نعم
٥٥٤ - قلت وَيتَكَلَّم فِي طَوَافه قَالَ نعم
٥٥٥ - قلت وَيَشْتَرِي بعد زيارته أَو وداعه قَالَ لَا يَشْتَرِي بعد وداعه وَلَكِن يمْضِي إِذا ودع كَمَا هُوَ
٥٥٦ - قلت فَإِن وقف وَقْفَة أَو رَجَعَ جَاهِلا أَو نَاسِيا مِقْدَار غلوة قَالَ أَرْجُو','2','22',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (379,'٥٥٧ - قلت وَأي وَقت يَرْمِي الْجمار قَالَ بعد الزَّوَال
٥٥٨ - قلت من رمي الْجمار قبل الزَّوَال قَالَ يُعِيد
٥٥٩ - قلت إِن كَانَ قد مضى قَالَ من ترك رمي الْجمار فَعَلَيهِ دم
٥٦٠ - قلت من لم يقم عِنْد الْجَمْرَتَيْن إِلَّا مِقْدَار عشر آيَات قَالَ يَنْبَغِي لَهُ أَن يقوم ويطيل','2','23',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (380,'<span data-type="title" id=toc-407>صَلَاة الْعَتَمَة فِي السّفر قبل غرُوب الشَّفق</span>

٥٦١ - قلت من كَانَ فِي سفر فصلى أهل الْقَافِلَة الْعَتَمَة قبل أَن يغيب الشَّفق قَالَ أَرْجُو','2','24',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (381,'<span data-type="title" id=toc-408>حكم الصَّلَاة خلف من لَا يعرف</span>

٥٦٢ - قلت من خَافَ أَن يصلى خلف من لَا يعرف قَالَ يُصَلِّي فَإِن تبين لَهُ أَن صَاحب بِدعَة أعَاد
<span data-type="title" id=toc-409>نزح الْبِئْر إِذا مَاتَ فِيهِ صبي</span>

٥٦٣ - قلت صبي وَقع فِي بِئْر وفيهَا مَاء غزير فَمَاتَ فِيهَا قَالَ ينْزح حَتَّى يَغْلِبهُمْ المَاء','2','25',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (382,'<span data-type="title" id=toc-410>حكم إِنْكَار المعدم الدّين وَالْحلف على ذَلِك</span>

٥٦٤ - قلت لأبي إِن بعض من يَقُول لَو أَن لرجل على رجل مَالا ثمَّ كَانَ معدما فقدمه جَازَ لَهُ أَن يحلف أَن مَاله قبله شَيْء قَالَ هَذَا قَول رَدِيء خَبِيث
٥٦٥ - قلت إِنَّه يحْتَج بقول الله تبَارك وَتَعَالَى {وَإِن كَانَ ذُو عسرة فنظرة إِلَى ميسرَة} فَقَالَ هَذِه إِنَّمَا أنزلت فِي الْأَنْصَار {يَا أَيهَا الَّذين آمنُوا اتَّقوا الله وذروا مَا بَقِي من الرِّبَا إِن كُنْتُم مُؤمنين}','2','26',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (383,'٥٦٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْمَاعِيل بن إِبْرَاهِيم قَالَ أخبرنَا أَيُّوب عَن مُحَمَّد قَالَ جَاءَ رجل إِلَى شُرَيْح فَكَلمهُ فَجعل يَقُول إِنَّه مُعسر قَالَ فَظَنَنْت أَنه يكلمهُ فِي مَحْبُوس فَقَالَ شُرَيْح إِن الرِّبَا كَانَ فِي هَذَا الْحَيّ من الْأَنْصَار فَأنْزل الله تَعَالَى {وَإِن كَانَ ذُو عسرة فنظرة إِلَى ميسرَة} وَقَالَ {إِن الله يَأْمُركُمْ أَن تُؤَدُّوا الْأَمَانَات إِلَى أَهلهَا} وَمَا كَانَ الله ليأمرنا بِأَمْر ثمَّ يعذبنا عَلَيْهِ أَدّوا الْأَمَانَات إِلَى أَهلهَا','2','27',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (384,'٥٦٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن زَكَرِيَّا بن أبي زَائِدَة قَالَ حَدثنِي يزِيد بن إِبْرَاهِيم عَن مُحَمَّد بن سِيرِين قَالَ جَاءَ رجل بِرَجُل إِلَى شُرَيْح يَطْلُبهُ بدين فَجعل الَّذِي عَلَيْهِ الْحق يَقُول إِنِّي مُعسر فَقَالَ شُرَيْح إِنَّمَا أنزلت هَذِه الْآيَة فِي الرِّبَا فِي الْأَنْصَار قَالَ فَقَامَ رجل فَقَالَ يَا رَسُول تَصَدَّقت بِمَالي ثمَّ قَامَ آخر فَقَالَ يَا رَسُول الله تَصَدَّقت بِمَالي يَعْنِي تَصَدَّقت بِالَّذِي كَانَ فِي الرِّبَا فَقَالَ شُرَيْح {إِن الله يَأْمُركُمْ أَن تُؤَدُّوا الْأَمَانَات إِلَى أَهلهَا} وَالله لَا يَأْمُرنَا الله بِشَيْء يعذبنا عَلَيْهِ
٥٦٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ أخبرنَا يُونُس وَهِشَام عَن ابْن سِيرِين أَن رجلا خَاصم رجلا إِلَى شُرَيْح فَقضى','2','28',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (385,'عَلَيْهِ وَأمر بحبسه فَقَالَ رجل عِنْد شُرَيْح إِنَّه مُعسر وَالله يَقُول {وَإِن كَانَ ذُو عسرة فنظرة إِلَى ميسرَة} فَقَالَ شُرَيْح إِنَّمَا ذَلِك فِي الرِّبَا فَإِن الله قَالَ فِي كِتَابه {إِن الله يَأْمُركُمْ أَن تُؤَدُّوا الْأَمَانَات إِلَى أَهلهَا وَإِذا حكمتم بَين النَّاس أَن تحكموا بِالْعَدْلِ} وَالله لَا يَأْمُرنَا بِشَيْء ثمَّ يعذبنا عَلَيْهِ
<span data-type="title" id=toc-411>حكم الزواج ببنت الربيب والربيبة</span>

٥٦٩ - وَسَأَلت أبي عَن الرجل تكون لَهُ الإمرأة فتموت وَلها ابْن لَهُ ابْنة أيتزوج الرجل بابنة ابْنهَا قَالَ لَا يتَزَوَّج وَكَذَا لَو كَانَت لَهَا ابْنة ولابنتها بنت لم يتَزَوَّج','2','29',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (386,'<span data-type="title" id=toc-412>التقية بالْقَوْل لَا بِالْعَمَلِ</span>

٥٧٠ - قلت لأبي بعض من يَقُول لَو أَن رجلا كَانَ فِي مَوضِع تقية فَأمر أَن يقتل يقتل وَيشْرب الْخمر ويزني قَالَ إِن التقية بِاللِّسَانِ لَا بِالْيَدِ
٥٧١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن سُفْيَان عَن ابْن','2','30',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (387,'جريج عَن رجل عَن ابْن عَبَّاس قَالَ التقية بِاللِّسَانِ لَيْسَ بِالْيَدِ وَقَالَ النَّبِي صلى الله عَلَيْهِ وَسلم كن عبد الله الْمَقْتُول وَلَا تكن عبد الله الْقَاتِل وَقَالَ','2','31',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (388,'لَا تكون إِلَّا بِاللِّسَانِ وَدفع هَذَا القَوْل وَقَالَ المضطرب لَا يشرب الْخمر يُقَال إِنَّهَا لَا تروي
٥٧٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد عَن سُفْيَان عَن مَنْصُور عَن إِبْرَاهِيم فِي الْأَمِير يَأْمر الرجل فَيقْتل قَالَ','2','32',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (389,'هُوَ عَلَيْهِمَا جَمِيعًا وَقَالَ سُفْيَان يقتل الْقَاتِل','2','33',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (390,'<span data-type="title" id=toc-413>حكم الصَّلَاة مَعَ الْجَمَاعَة</span>

٥٧٣ - وَقَالَ أبي الصَّلَاة جمَاعَة أخْشَى أَن تكون فَرِيضَة وَلَو ذهب النَّاس يَجْلِسُونَ عَنْهَا لتعطلت الْمَسَاجِد ويروى عَن عَليّ وَابْن مَسْعُود وَابْن عَبَّاس من سمع النداء فَلم يجب فَلَا صَلَاة لَهُ
٥٧٤ - حَدثنَا صَالح قَالَ حَدثنَا أبي قَالَ حَدثنَا هشيم عَن يحيى يَعْنِي أَبَا حَيَّان التَّيْمِيّ عَن أَبِيه عَن عَليّ قَالَ لَا صَلَاة لِجَار الْمَسْجِد إِلَّا فِي الْمَسْجِد','2','34',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (391,'٥٧٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم عَن مَنْصُور عَن','2','35',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (392,'الْحسن عَن عَليّ قَالَ من سمع النداء فَلم يَأْته لم تجَاوز صلَاته رَأسه إِلَّا من عذر
٥٧٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ حَدثنَا مسعر عَن أبي حُصَيْن عَن أبي بردة عَن أبي مُوسَى قَالَ من سمع الْمُنَادِي فَلم يجب من غير عذر فَلَا صَلَاة لَهُ
٥٧٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ حَدثنَا سُلَيْمَان','2','36',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (393,'ابْن الْمُغيرَة عَن أبي مُوسَى الْهِلَالِي عَن ابْن مَسْعُود قَالَ من سمع النداء فَلم يجب من غير عذر فَلَا صَلَاة لَهُ
٥٧٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن سُفْيَان عَن مَنْصُور عَن عدي بن ثَابت عَن عَائِشَة رَضِي الله عَنْهَا قَالَت من سمع الْمُنَادِي فَلم يجب من غير عذر فَلم يجد خيرا وَلم يرد بِهِ','2','37',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (394,'٥٧٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ حَدثنَا شُعْبَة عَن عدي بن ثَابت عَن سعيد بن جُبَير عَن ابْن عَبَّاس قَالَ من سمع الْمُنَادِي فَلم يجب من غير عذر فَلَا صَلَاة لَهُ
٥٨٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن سُفْيَان عَن أبي حَيَّان التَّيْمِيّ عَن أَبِيه عَن عَليّ قَالَ لَا صَلَاة لِجَار الْمَسْجِد إِلَّا فِي الْمَسْجِد قيل وَمن جَار الْمَسْجِد قَالَ من سمع الْمُنَادِي
٥٨١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن إِسْرَائِيل عَن أبي إِسْحَاق عَن الْحَارِث عَن عَليّ قَالَ لَا صَلَاة لِجَار الْمَسْجِد إِلَّا فِي الْمَسْجِد','2','38',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (395,'<span data-type="title" id=toc-414>رِوَايَة عَن ابْن عمر فِي البيع بِالْبَرَاءَةِ</span>

٥٨٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يزِيد بن هَارُون قَالَ حَدثنَا يحيى بن سعيد الْأنْصَارِيّ أَن سَالم بن عبد الله أخبرهُ أَن عبد الله بن عمر بَاعَ غُلَاما بِالْبَرَاءَةِ بثمانمائة دِرْهَم ثمَّ إِن صَاحب الْغُلَام خَاصم ابْن عمر إِلَى عُثْمَان فَقَالَ بَاعَنِي وَبِه دَاء قد علمه لم يُبينهُ لي فَقَالَ ابْن عمر قد بِعته بِالْبَرَاءَةِ فَقَالَ لَهُ عُثْمَان تحلف بِاللَّه لقد بِعته وَمَا بِهِ دَاء','2','39',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (396,'عَلمته فَأبى ابْن عمر أَن يحلف فَرد العَبْد إِلَيْهِ فَذكر سَالم أَن العَبْد صَحَّ عِنْد ابْن عمر حَتَّى باعة بِأَلف وَأَرْبَعمِائَة
<span data-type="title" id=toc-415>آثَار وأقوال فِي الرَّعْد والبرق والصاعقة</span>

٥٨٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن عمر بن أبي زَائِدَة قَالَ سَمِعت عِكْرِمَة يَقُول {ويسبح الرَّعْد بِحَمْدِهِ} قَالَ الرَّعْد ملك يزْجر السَّحَاب بِصَوْتِهِ','2','40',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (397,'٥٨٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن شُعْبَة وَمُحَمّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن الحكم عَن مُجَاهِد قَالَ الرَّعْد ملك يزْجر السَّحَاب بِصَوْتِهِ','2','41',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (398,'٥٨٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن المَسْعُودِيّ عَن سَلمَة بن كهيل عَن رجل عَن عَليّ قَالَ سُئِلَ عَن الرَّعْد فَقَالَ ملك وَسُئِلَ عَن الْبَرْق فَقَالَ مخاريق بأيدي الْمَلَائِكَة
٥٨٦ - حَدثنِي أبي قَالَ حَدثنَا عبد الرَّحْمَن بن مهْدي عَن سُفْيَان عَن سَلمَة بن كهيل عَن سعيد بن أَشوع عَن ربيعَة بن الْأَبْيَض عَن عَليّ قَالَ الْبَرْق مخاريق الْمَلَائِكَة','2','42',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (399,'٥٨٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّحْمَن بن مهْدي قَالَ حَدثنَا حَمَّاد بن سَلمَة عَن أبي مُحَمَّد الْهَاشِمِي عَن أَبِيه عَن عَليّ قَالَ الرَّعْد ملك والبرق مِخْرَاق من حَدِيد','2','43',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (400,'٥٨٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عَفَّان قَالَ حَدثنَا أَبُو عوَانَة قَالَ حَدثنَا مُوسَى الْبَزَّار عَن شهر فِي حَوْشَب عَن ابْن عَبَّاس قَالَ الرَّعْد ملك يَسُوق السَّحَاب كَمَا يَسُوق الْحَادِي الْإِبِل بحدائه','2','44',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (401,'٥٨٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن عبد الله بن الزبير قَالَ حَدثنَا عتاب بن زِيَاد التَّمِيمِي قَالَ سَمِعت عِكْرِمَة يَقُول الرَّعْد ملك فِي السَّمَاء يجمع السَّحَاب كَمَا يجمع الرَّاعِي الْإِبِل
٥٩٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ حَدثنَا إِسْمَاعِيل بن سَالم عَن أبي صَالح فِي قَوْله {ويسبح الرَّعْد بِحَمْدِهِ} قَالَ الرَّعْد ملك من الْمَلَائِكَة يسبح','2','45',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (402,'٥٩١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا حُسَيْن بن مُحَمَّد فِي تَفْسِير شَيبَان عَن قَتَادَة فِي قَوْله {ويسبح الرَّعْد بِحَمْدِهِ وَالْمَلَائِكَة من خيفته} قَالَ الرَّعْد خلق من خلق الله سامع مُطِيع لَهُ وَذكر لنا أَن رجلا أنكر الْقُرْآن وَكذب النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ فَأرْسل الله صَاعِقَة فأهلكته وَأنزل الله فِيهِ {وهم يجادلون فِي الله وَهُوَ شَدِيد الْمحَال} قَالَ شَدِيد الْقُوَّة
٥٩٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُعْتَمر عَن أَبِيه عَن أبي عمرَان الْجونِي قَالَ إِن من فَوْقكُم بحرا من نَار فَمِنْهَا تكون الصَّوَاعِق','2','46',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (403,'٥٩٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الصَّمد بن عبد الْوَارِث قَالَ حَدثنَا أبان بن يزِيد قَالَ حَدثنَا أَبُو عمرَان الْجونِي عَن عبد الرَّحْمَن بن صحار الْعَبْدي أَن رَسُول الله صلى الله عَلَيْهِ وَسلم بعث إِلَى جَبَّار يَدعُوهُ إِلَى الله فَقَالَ أَرَأَيْتُم ربكُم أذهب هُوَ أَو فضَّة هُوَ ألؤلؤ هُوَ أسرقة هُوَ','2','47',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (404,'قَالَ بَينهمَا هُوَ كَذَلِك يجادله إِذْ بعث الله سَحَابَة فَرعدَت وَبَرقَتْ فَأرْسلت عَلَيْهِ صَاعِقَة فَقتلته فَأنْزل الله {وَيُرْسل الصَّوَاعِق فَيُصِيب بهَا من يَشَاء وهم يجادلون فِي الله وَهُوَ شَدِيد الْمحَال}
<span data-type="title" id=toc-416>تَفْسِير قَوْله تَعَالَى {فسلكه ينابيع فِي الأَرْض}</span>

٥٩٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن يمَان عَن سُفْيَان عَن جَابر عَن الشّعبِيّ {فسلكه ينابيع فِي الأَرْض} قَالَ كل ندى وَمَاء فِي الأَرْض أَصله من السَّمَاء','2','48',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (405,'٥٩٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد القدوس بن الْحجَّاج قَالَ حَدَّثتنَا عَبدة بنت خَالِد بن معدان عَن أَبِيهَا خَالِد قَالَ إِن الْمَطَر يخر من تَحت الْعَرْش فَينزل من سَمَاء إِلَى سَمَاء حَتَّى يَنْتَهِي إِلَى السَّمَاء الدُّنْيَا فيجتمع فِي مَوضِع يُقَال لَهُ الأبزم فتجيء السحابة السَّوْدَاء فتشربه','2','49',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (406,'<span data-type="title" id=toc-417>أَحَادِيث وآثار فِي الرّيح</span>

٥٩٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد قَالَ حَدثنَا سعيد قَالَ حَدثنِي حبيب بن أبي ثَابت عَن ذَر عَن سعيد بن','2','50',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (407,'عبد الرَّحْمَن بن أَبْزَى عَن أَبِيه عَن أبي بن كَعْب قَالَ لَا تسبوا الرّيح وَلَكِن قُولُوا اللَّهُمَّ إِنَّا نَسْأَلك من خَيرهَا وَخير مَا فِيهَا وَخير مَا أرْسلت بِهِ ونعوذ بك من شَرها وَشر مَا فِيهَا وَشر مَا أرْسلت بِهِ
٥٩٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو مُعَاوِيَة قَالَ حَدثنَا الْأَعْمَش عَن مَسْعُود بن مَالك عَن سعيد بن جُبَير عَن ابْن عَبَّاس','2','51',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (408,'قَالَ قَالَ رَسُول الله صلى الله عَلَيْهِ وَسلم إِنِّي نصرت بالصبا وَإِن عادا أهلكت بالدبور
٥٩٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ أخبرنَا معمر عَن الزُّهْرِيّ قَالَ حَدثنِي ثَابت بن قيس أَن أَبَا هُرَيْرَة قَالَ أخذت النَّاس ريح بطرِيق مَكَّة وَعمر بن الْخطاب حَاج فاشتدت عَلَيْهِم فَقَالَ عمر لمن حوله من يحدثنا عَن الرّيح فَلم يرجِعوا إِلَيْهِ شَيْئا فبلغني الَّذِي سَأَلَ عَنهُ عمر رَضِي الله عَنهُ من ذَلِك فاستحثثت رَاحِلَتي حَتَّى أَدْرَكته فَقلت لَهُ يَا أَمِير الْمُؤمنِينَ أخْبرت أَنَّك سَأَلت عَن الرّيح وَإِنِّي سَمِعت رَسُول الله صلى الله عَلَيْهِ وَسلم يَقُول الرّيح','2','52',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (409,'تَأتي بِالرَّحْمَةِ وَتَأْتِي بِالْعَذَابِ فَإِذا رأيتموها فَلَا تسبوها واسألوا الله خَيرهَا واستعيذوا بِاللَّه من شَرها
<span data-type="title" id=toc-418>مَاذَا يَقُول إِذا رأى الْغَيْم</span>

٥٩٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ أخبرنَا معمر عَن أَيُّوب عَن الْقَاسِم بن مُحَمَّد عَن عَائِشَة أَن النَّبِي صلى الله عَلَيْهِ وَسلم كَانَ إِذا رأى الْغَيْم قَالَ اللَّهُمَّ صبا هَنِيئًا أَو صيبا هَنِيئًا','2','53',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (410,'<span data-type="title" id=toc-419>الاسْتِسْقَاء فِي خطْبَة الْجُمُعَة</span>

٦٠٠ - وَحدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا بهز قَالَ حَدثنَا حجاج بن مُحَمَّد قَالَا حَدثنَا سُلَيْمَان بن الْمُغيرَة عَن ثَابت قَالَ قَالَ أنس إِنِّي لقاعد عِنْد الْمِنْبَر يَوْم الْجُمُعَة وَرَسُول الله صلى الله عَلَيْهِ وَسلم يخْطب إِذْ قَالَ بعض أهل الْمَسْجِد يَا رَسُول الله حبس الْمَطَر هَلَكت الْمَوَاشِي ادْع الله أَن يسقينا قَالَ أنس فَرفع يَدَيْهِ رَسُول الله صلى الله عَلَيْهِ وَسلم وَمَا أرى فِي السَّمَاء من سَحَاب قَالَ حجاج فِي حَدِيثه فألف الله بَين السَّحَاب فوبلتنا سبعا حَتَّى رَأَيْت الرجل الشَّديد تهمه نَفسه أَن يَأْتِي أَهله قَالَ فمطرنا سبعا وَخرج رَسُول الله صلى الله عَلَيْهِ وَسلم يخْطب الْمُقبلَة إِذْ قَالَ بعض أهل الْمَسْجِد يَا رَسُول الله تهدمت الْبيُوت حبس الصفار ادْع الله أَن يرفعها عَنَّا','2','54',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (411,'قَالَ فَرفع يَدَيْهِ فَقَالَ اللَّهُمَّ حوالينا وَلَا علينا قَالَ فتقور مَا فَوق رؤوسنا مِنْهَا حَتَّى كأنا فِي إكليل يمطر مَا حولنا وَلَا نمطر
<span data-type="title" id=toc-420>تَفْسِير قَوْله تَعَالَى {فَالتقى المَاء على أَمر قد قدر}</span>

٦٠١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هَاشم بن الْقَاسِم قَالَ حَدثنَا مُحَمَّد بن مُسلم قَالَ أخبرنَا أَيُّوب بن مُوسَى عَن مُحَمَّد بن كَعْب فِي قَوْله {فَالتقى المَاء على أَمر قد قدر} قَالَ كَانَ الْقدر قبل الْبلَاء','2','55',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (412,'٦٠٢ - حَدثنِي أبي قَالَ وَحدثنَا يُونُس بن مُحَمَّد قَالَ حَدثنَا أبان يَعْنِي ابْن يزِيد عَن قَتَادَة قَالَ {على أَمر قد قدر} قَالَ مَاء الأَرْض وَمَاء السَّمَاء
<span data-type="title" id=toc-421>النَّهْي عَن سبّ الرّيح</span>

٦٠٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا زِيَاد بن عبد الله البكائي قَالَ أبي كتبنَا عَنهُ فِي حَيَاة هشيم كَانَ قد سمع الْمَغَازِي من مُحَمَّد ابْن إِسْحَاق قَالَ حَدثنَا مَنْصُور يَعْنِي ابْن الْمُعْتَمِر عَن','2','56',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (413,'مُجَاهِد قَالَ هَاجَتْ ريح على عهد عبد الله بن عَبَّاس فسبها النَّاس فَقَالَ ابْن عَبَّاس لَا تسبوها فَإِنَّهَا تَجِيء بِالْعَذَابِ وَالرَّحْمَة وَلَكِن قُولُوا اللَّهُمَّ اجْعَلْهَا رَحْمَة وَلَا تجعلها عذَابا اللَّهُمَّ لَا تجْعَل الرّيح علينا عذَابا
<span data-type="title" id=toc-422>تَفْسِير قَوْله {وأنزلنا من المعصرات مَاء ثجاجا}</span>

٦٠٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ وَحدثنَا إِبْرَاهِيم بن الحكم بن أبان الْعَدنِي قَالَ حَدثنِي أبي عَن عِكْرِمَة فِي قَوْله {وأنزلنا من المعصرات مَاء ثجاجا} قَالَ المعصرات السَّحَاب وَمَاء ثجاجا مَاء صبا وَقد قَالَ مرّة كثيرا','2','57',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (414,'٦٠٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن سَلمَة عَن ابْن إِسْحَاق عَن وهب بن كيسَان سمع ابْن الزبير يَقُول وأنزلنا بالمعصرات مَاء ثجاجا
٦٠٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا روح بن عبَادَة حَدثنَا شبْل عَن ابْن أبي نجيح عَن مُجَاهِد {وأنزلنا من المعصرات}','2','58',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (415,'الرّيح وَكَذَلِكَ كَانَ يَقْرَأها بالمعصرات مَاء ثجاجا منصبا
٦٠٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ أخبرنَا معمر عَن قَتَادَة فِي قَوْله {من المعصرات} قَالَ السَّمَاء وَبَعْضهمْ يَقُول الرّيح
٦٠٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن آدم قَالَ حَدثنَا سُفْيَان عَن الْأَعْمَش عَن ابْن عَبَّاس أَنه قَرَأَ {وأنزلنا من المعصرات} قَالَ الرّيح','2','59',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (416,'٦٠٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عمر بن سعد أَبُو دَاوُد الْحَفرِي قَالَ حَدثنَا سُفْيَان عَن الْأَعْمَش عَن الْمنْهَال بن عَمْرو عَن سعيد بن جُبَير عَن ابْن عَبَّاس {وأنزلنا من المعصرات} قَالَ الرِّيَاح
٦١٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أسود بن عَامر قَالَ حَدثنَا إِسْرَائِيل عَن أبي يحيى يَعْنِي القَتَّات وَهُوَ الْكِنَانِي وَقد روى عَنهُ أَبُو بكر بن عَيَّاش عَن مُجَاهِد قَالَ المزن السَّحَاب آخر الْجُزْء السَّادِس من أَجزَاء أبي عَليّ','2','60',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (417,'<span data-type="title" id=toc-423>قَول عبد الله بن عَمْرو فِي الشَّمْس وَالْقَمَر والرعد والبرق وَالروح</span>

٦١١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الْملك بن عَمْرو قَالَ حَدثنَا عبد الْجَلِيل عَن شهر قَالَ بَيْنَمَا النَّاس عِنْد عبد الله بن عَمْرو يستفتونه فَقَالَ كَعْب هلك أخي هَكَذَا تكون الْفِتَن','2','61',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (418,'اذْهَبْ إِلَيْهِ فَقل لَهُ لَا تكذبن على الله فَإِن غضب فَدَعْهُ وَإِن لم يغْضب فَاسْأَلْهُ فَأَتَاهُ فَقَالَ لَهُ يَقُول لَك كَعْب لَا تكذبن على الله فَقَالَ نصح لي أخي إِنَّه من كذب على الله سود الله وَجهه يَوْم الْقِيَامَة قَالَ فَإِنِّي أَسأَلك عَن الشَّمْس وَالْقَمَر أَفِي السَّمَوَات السَّبع هما أم فِي السَّمَاء الدُّنْيَا أم فِي الْهَوَاء أم دون ذَلِك قَالَ بل هم فِي السَّمَوَات السَّبع ووجوههما إِلَى الْعَرْش وَأَقْفِيَتهمَا إِلَى الأَرْض قَالَ الله {وَجعل الْقَمَر فِيهِنَّ نورا وَجعل الشَّمْس سِرَاجًا} قَالَ فَإِنَّهُ يَسْأَلك عَن الرَّعْد مِمَّا هُوَ قَالَ ملك يزْجر السَّحَاب بالتسبيح كَمَا يزْجر الْحَادِي الحثيت الْإِبِل إِذا اشتدت سَحَابَة ضمهَا لَو يُفْضِي إِلَى الأَرْض صعق من يبصره قَالَ فَإِنَّهُ يَسْأَلك عَن الْبَرْق مَا هُوَ قَالَ هُوَ من كَذَا وَكَذَا من الْبرد','2','62',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (419,'قَالَ عبد الْملك أَحْسبهُ قَالَ من اصطفاق الْبرد فِي السَّمَاء قَالَ الله {من جبال فِيهَا من برد فَيُصِيب بِهِ من يَشَاء ويصرفه عَن من يَشَاء يكَاد سنا برقه يذهب بالأبصار} قَالَ فَإِنَّهُ يَسْأَلك أَيْن تلتقي أَرْوَاح أهل الْجنَّة وأرواح أهل النَّار قَالَ أما أَرْوَاح أهل الْجنَّة فتلتقي بالجابية وَأما أَرْوَاح أهل النَّار فبحضرموت','2','63',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (420,'قَالَ فَإِنَّهُ يَسْأَلك عَن الْحَشْر مَا هُوَ قَالَ نَار تزوي النَّاس تظهر من قبل الْمشرق','2','64',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (421,'<span data-type="title" id=toc-424>آثَار فِي السَّحَاب</span>

٦١٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عُثْمَان بن عمر قَالَ حَدثنَا أُسَامَة بن زيد عَن معَاذ بن عبد الله بن خبيب قَالَ رَأَيْت ابْن عَبَّاس على بغلة فَسَأَلَ تبيع ابْن امْرَأَة كَعْب هَل سَمِعت كَعْبًا يَقُول فِي السَّحَاب شَيْئا قَالَ نعم سَمِعت كَعْبًا يَقُول السَّحَاب غربال الْمَطَر لَوْلَا السَّحَاب أفسد الْمَطَر مَا أصَاب فَقَالَ ابْن عَبَّاس صدقت قد سَمِعت كَعْبًا يَقُول هَذَا','2','65',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (422,'٦١٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو مُعَاوِيَة قَالَ حَدثنَا الْأَعْمَش عَن الْمنْهَال بن عَمْرو عَن قيس بن السكن عَن عبد الله فِي قَوْله {وَأَرْسَلْنَا الرِّيَاح لَوَاقِح} قَالَ يبْعَث الله الرّيح فتلقح السَّحَاب قَالَ ثمَّ تمريه فيدر كَمَا تدر اللقحة ثمَّ تمطر
٦١٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُؤَمل قَالَ حَدثنَا سُفْيَان','2','66',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (423,'قَالَ حَدثنَا الْأَعْمَش عَن الْمنْهَال بن عَمْرو عَن قيس بن السكن عَن عبد الله فِي قَوْله {وأنزلنا من المعصرات مَاء ثجاجا} قَالَ يبْعَث الله الرّيح فَتحمل المَاء فتمري بِهِ السَّحَاب فيدر كَمَا تدر اللقحة ثمَّ يبْعَث أَو قَالَ ثمَّ يُرْسل من السَّمَاء أَمْثَال العزالى فتصيبه الرّيح أَو قَالَ الرِّيَاح فَينزل مُتَفَرقًا
<span data-type="title" id=toc-425>رِوَايَتَانِ فِي الشَّمْس وَالْقَمَر</span>

٦١٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يُونُس بن مُحَمَّد قَالَ حَدثنَا عبد الْعَزِيز بن الْمُخْتَار يُقَال لَهُ الدّباغ عَن عبد الله الداناج قَالَ','2','67',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (424,'شهِدت أَبَا سَلمَة بن عبد الرَّحْمَن بن عَوْف زمن خَالِد بن عبد الله بن أسيد فِي هَذَا الْمَسْجِد يَعْنِي الْجَامِع بِالْبَصْرَةِ قَالَ وَجَاء الْحسن فَجَلَسَ إِلَيْهِ قَالَ فَحدث فَقَالَ حَدثنَا أَبُو هُرَيْرَة عَن رَسُول الله صلى الله عَلَيْهِ وَسلم أَنه قَالَ إِن الشَّمْس وَالْقَمَر ثوران مكوران فِي النَّار يَوْم الْقِيَامَة قَالَ فَقَالَ الْحسن وَمَا ذنبهما قَالَ فَقَالَ أحَدثك عَن رَسُول الله صلى الله عَلَيْهِ وَسلم قَالَ فَسكت','2','68',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (425,'٦١٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الله بن وَاقد عَن سعيد ابْن أبي أَيُّوب عَن عقيل عَن ابْن شهَاب قَالَ الشَّمْس وَالْقَمَر ثوران عقيران من نَار خلقا وَإِلَى النَّار يصيران','2','69',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (426,'<span data-type="title" id=toc-426>انْتِقَال الْمُطلقَة ثَلَاثًا من مَوضِع طَلاقهَا عِنْد الْخَوْف</span>

٦١٧ - قلت لأبي امْرَأَة طَلقهَا زَوجهَا ثَلَاثًا وَهِي فِي بلد بَينهَا وَبَين أَهلهَا أَكثر من مسيرَة ثَلَاثَة أَيَّام وَعِنْدهَا ولي لَهَا لَا يُمكنهُ الْمقَام عَلَيْهَا إِلَى انْقِضَاء عدتهَا وَزوجهَا غير مَأْمُون عَلَيْهَا أَتَرَى لوَلِيّهَا أَن يردهَا إِلَى بَلَدهَا قبل انْقِضَاء عدتهَا قَالَ إِن فَاطِمَة بنت قيس طَلقهَا زَوجهَا قَالَ الشّعبِيّ ثَلَاثًا وَقَالَ غَيره آخر تطليقاتها فَأمرهَا النَّبِي صلى الله عَلَيْهِ وَسلم أَن تَعْتَد فِي غير منزل','2','70',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (427,'زَوجهَا وَكَانَ ابْن عَبَّاس وَجَابِر بن عبد الله لَا يريان بَأْسا أَن تنْتَقل من بَيت زَوجهَا
<span data-type="title" id=toc-427>عدَّة أم الْوَلَد</span>

٦١٨ - وَقَالَ أبي إِذا زوج أم وَلَده فَمَاتَ عَنْهَا زَوجهَا تَعْتَد عدَّة الْأمة شَهْرَيْن وَخَمْسَة أَيَّام وَإِذا مَاتَ السَّيِّد عَن أم الْوَلَد فعدتها حَيْضَة يرْوى','2','71',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (428,'عَن ابْن عمر وَابْن مَسْعُود مَاتَ عَنْهَا أَو أعْتقهَا وَإِذا مَاتَ سَيِّدهَا وَقد زَوجهَا ثمَّ مَاتَ الزَّوْج بعد موت السَّيِّد فعدتها عدَّة الْحرَّة
<span data-type="title" id=toc-428>عدَّة من زعم زَوجهَا أَنه طَلقهَا مُنْذُ سنة</span>

٦١٩ - قلت إِذا جَاءَ الرجل فَزعم أَنه قد طَلقهَا مُنْذُ سنة وَلم يثبت ذَلِك عِنْدهَا قَالَ تَعْتَد من سَاعَة قَالَ لَهَا وَإِذا جَاءَ كِتَابه وَثَبت عِنْدهَا فعلى مَا فِي كِتَابه إِن كَانَ فِيهِ إِذا وصل كتابي إِلَيْك تَعْتَد من ذَلِك الْيَوْم وَإِلَّا على مَا فِي الْكتاب
<span data-type="title" id=toc-429>حكم عبد بَين شَرِيكَيْنِ أعتق أَحدهمَا نصِيبه</span>

٦٢٠ - قلت عبد بَين نفسين أعتق أَحدهمَا نصِيبه','2','72',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (429,'قَالَ قد عتق نصفه وَإِن كَانَ للْمُعْتق بِقدر نصف قيمَة العَبْد عتق فِي مَاله ويؤديه إِلَى الَّذِي لم يعْتق وان لم يكن فِي مَاله كَانَ للْعَبد وللرجل يَوْم','2','73',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (430,'<span data-type="title" id=toc-430>آثَار فِي عدَّة أم الْوَلَد</span>

٦٢١ - حَدثنَا صَالح قَالَ حَدثنَا أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر وَعبد الْوَهَّاب عَن سعيد عَن مطر عَن رَجَاء بن حَيْوَة عَن قبيصَة بن ذويب عَن عَمْرو بن الْعَاصِ قَالَ لَا تلبسوا علينا سنة نَبينَا صلى الله عَلَيْهِ وَسلم عدتهَا عدَّة الْمُتَوفَّى عَنْهَا زَوجهَا أَرْبَعَة أشهر وَعشرا يَعْنِي أم الْوَلَد','2','74',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (431,'٦٢٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يزِيد عَن سعيد عَن قَتَادَة عَن رَجَاء قَالَ أبي قَتَادَة أثبت عندنَا فِي الحَدِيث من مطر
٦٢٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا الْوَلِيد بن مُسلم عَن سعيد بن عبد الْعَزِيز عَن سُلَيْمَان بن مُوسَى عَن رَجَاء بن حَيْوَة عَن','2','75',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (432,'قبيصَة بن ذويب عَن عَمْرو بن الْعَاصِ قَالَ عدَّة أم الْوَلَد عدَّة الْحرَّة قَالَ أبي قلت للوليد بن مُسلم من حَدثكُمْ قَالَ سعيد
٦٢٤ - حَدثنَا عبد الله قَالَ حَدثنِي أبي قَالَ حَدثنَا الْوَلِيد عَن الْأَوْزَاعِيّ','2','76',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (433,'وَسَعِيد عَن الزُّهْرِيّ عَن قبيصَة عَن عَمْرو بن الْعَاصِ عدَّة أم الْوَلَد عدَّة الْحرَّة
٦٢٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد قَالَ حَدثنَا ثَوْر قَالَ سَمِعت رَجَاء بن حَيْوَة قَالَ سُئِلَ عَمْرو بن الْعَاصِ عَن عدَّة أم الْوَلَد قَالَ لَا تلبسوا علينا فِي ديننَا إِن تكن أمة فعدتها عدَّة حرَّة قَالَ أبي فَهَؤُلَاءِ لم يَقُولُوا سنة نَبينَا فَكَأَنَّهُ ضعفه','2','77',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (434,'٦٢٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد عَن عبيد الله قَالَ أَخْبرنِي نَافِع عَن ابْن عمر قَالَ تستبرىء أم الْوَلَد إِذا مَاتَ عَنْهَا سَيِّدهَا أَو أعْتقهَا حَيْضَة
٦٢٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْحَق بن يُوسُف قَالَ حَدثنَا عبيد الله بن عمر عَن نَافِع عَن ابْن عمر أَنه قَالَ فِي عدَّة أم الْوَلَد إِذا أعْتقهَا سَيِّدهَا أَو مَاتَ عَنْهَا فعدتها حَيْضَة','2','78',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (435,'٦٢٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن أبي عدي عَن دَاوُد عَن الشّعبِيّ عَن ابْن عمر أَنه قَالَ فِي عدَّة أم الْوَلَد إِذا توفّي عَنْهَا سَيِّدهَا أَو أعْتقهَا حَيْضَة
٦٢٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا الْوَلِيد بن مُسلم قَالَ حَدثنَا سعيد بن عبد الْعَزِيز أَن مَكْحُولًا حَدثهُ أَن عُثْمَان قضى فِي عدَّة أم الْوَلَد من الْعتْق والوفاة من سَيِّدهَا بِحَيْضَة قَالَ مَكْحُول ثمَّ زَادهَا مُعَاوِيَة حَيْضَة أُخْرَى','2','79',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (436,'٦٣٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن آدم قَالَ حَدثنَا عمار بن رُزَيْق عَن فراس عَن الشّعبِيّ عَن عبد الله قَالَ إِذا أشترى الرجل الْأمة اسْتَبْرَأَ رَحمهَا بِحَيْضَة فَإِن أعْتقهَا اعْتدت حَيْضَة وَإِن مَاتَ عَنْهَا وَقد ولدت مِنْهُ اعْتدت حَيْضَة','2','80',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (437,'٦٣١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يَعْقُوب بن إِبْرَاهِيم قَالَ حَدثنَا عبد الْعَزِيز بن الْمطلب عَن عبد الله بن أبي بكر عَن ابْن شهَاب عَن عبَادَة بن الصَّامِت أَنه قَالَ فِي عدَّة أم الْوَلَد حَيْضَة
<span data-type="title" id=toc-431>كَيفَ يعْمل من نسي سَجْدَة</span>

٦٣٢ - قَالَ قَالَ الشَّافِعِي فِي الَّذِي تفوته سَجْدَة يَعْنِي ينساها إِذا صلى رَكْعَة','2','81',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (438,'أُخْرَى وَسجد فِيهَا سَجْدَة أضافها إِلَى تِلْكَ السَّجْدَة فَيكون لَهُ رَكْعَة قد أَتَى بسجدتين وَكَانَ يحْتَج على أَصْحَاب أبي حنيفَة قَالُوا إِذا قيد بِسَجْدَة أَجزَأَهُ قَالَ فَكَذَلِك إِذا أجزتم أَنْتُم هَذَا أجزنا نَحن هَذَا
<span data-type="title" id=toc-432>كَيْفيَّة طَلَاق السّنة</span>','2','82',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (439,'٦٣٣ - قلت كَيفَ طَلَاق السّنة قَالَ يطلقهَا طَاهِرَة من غير جماع على حَدِيث ابْن عمر ويطلقها تَطْلِيقَة وَهُوَ يملك الرّجْعَة مَا دَامَت فِي الْعدة لقَوْل الله تبَارك وَتَعَالَى','2','83',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (440,'{لَعَلَّ الله يحدث بعد ذَلِك أمرا} وَقَالَ عَليّ من طلق طَلَاق السّنة لم ينْدَم
<span data-type="title" id=toc-433>إِذا أرضعت الزَّوْجَة الْكَبِيرَة أَو أمهَا الزَّوْجَة الصَّغِيرَة</span>

٦٣٤ - قَالَ قَالَ أبي إِذا تزوج الرجل صبية مُرْضِعَة فأرضعتها امْرَأَة لَهُ أُخْرَى لم يدْخل بهَا حرمت عَلَيْهِ الْكَبِيرَة لِأَنَّهَا صَارَت من أُمَّهَات النِّسَاء وَإِذا أرضعت هَذِه الْكَبِيرَة وَهِي مَدْخُول بهَا صَغِيرَة بلبنه حرمتا عَلَيْهِ وَترجع الصَّغِيرَة بِنصْف مهرهَا على الْكَبِيرَة لِأَنَّهَا قد فرقت بَينهَا وَبَين زَوجهَا وَلَو أَن أم هَذِه الْكَبِيرَة أرضعت الصَّغِيرَة حرمتا عَلَيْهِ جَمِيعًا','2','84',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (441,'وَترجع الصَّغِيرَة على أم الْكَبِيرَة بِنصْف الْمهْر ولإبنة هَذِه أَن ترجع على أمهَا بِنصْف الْمهْر إِذا كَانَت غير مَدْخُول بهَا ويخطب أَيَّتهمَا شَاءَ لِأَن لَيْسَ عَلَيْهِمَا عدَّة فَإِن كَانَ قد دخل بالكبيرة وأرضعت أم الْكَبِيرَة الصَّغِيرَة حرمتا عَلَيْهِ وَترجع الصَّغِيرَة على أم الْكَبِيرَة بِنصْف الْمهْر وَإِن شَاءَ تزوج الْكَبِيرَة فِي عدتهَا لِأَن المَاء مَاؤُهُ وَلَا يتَزَوَّج الصَّغِيرَة حَتَّى تَنْقَضِي عدَّة الْكَبِيرَة وَإِنَّمَا يتَزَوَّج الْكَبِيرَة فِي عدتهَا لِأَن الْمُرضعَة لَا عدَّة عَلَيْهَا وَهِي غير مَدْخُول بهَا وَإِذا مَاتَ عَنْهَا وَهِي مُرْضِعَة فعدتها عدَّة الْمُتَوفَّى عَنْهَا زَوجهَا أَرْبَعَة أشهر وَعشرا وتجتنب الطّيب لِأَنَّهَا فِي عدَّة وَفَاة','2','85',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (442,'<span data-type="title" id=toc-434>حكم الْعدة وَالْمِيرَاث إِذا قَالَ الرجل لزوجته طَلقتك مُنْذُ سنة</span>

٦٣٥ - قلت وَإِذا جَاءَ الرجل فَقَالَ قد طَلقتك ثَلَاثًا مُنْذُ سنة فَقَالَت قد انْقَضتْ عدتي قَالَ وَإِذا لم تعلم قَوْله وَلم تقم بَيِّنَة وَقَالَت هِيَ قد انْقَضتْ عدتي فَإِنَّهَا تَعْتَد من يَوْم قَالَ لَهَا إِن كَانَت مِمَّن تحيض فَثَلَاث حيض وَإِن كَانَت مِمَّن لَا تحيض فَثَلَاثَة أشهر فَإِن مَاتَت لم يَرِثهَا وَإِذا قَالَ قد كنت طَلقتك تَطْلِيقَة مُنْذُ سنة فَقَالَت قد انْقَضتْ عدتي لم يَرِثهَا وترثه هِيَ مَا كَانَت فِي الْعدة وَإِنَّمَا تَعْتَد من يَوْم يَقُول لَهَا إِذا كَانَ ذَلِك لم يثبت وَلم تقم بِهِ بَيِّنَة','2','86',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (443,'<span data-type="title" id=toc-435>من تَرْجَمَة تَوْبَة الْهِلَالِي</span>

٦٣٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن تَوْبَة الْهِلَالِي قَالَ وَكَانَ يقْرَأ قِرَاءَة عبد الله بل أدْرك علمهمْ
<span data-type="title" id=toc-436>كَرَاهَة طلب الْإِمَارَة وتكفير الْيَمين وإتيان مَا هُوَ خير</span>

٦٣٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الله بن بكر قَالَ حَدثنَا هِشَام عَن الْحسن عَن عبد الرَّحْمَن بن سَمُرَة عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه قَالَ','2','87',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (444,'يَا عبد الرَّحْمَن لَا تسْأَل الْإِمَارَة فَإنَّك إِن أعطيتهَا عَن مَسْأَلَة وكلت إِلَيْهَا وَإِن أعطيتهَا عَن غير مَسْأَلَة أعنت عَلَيْهَا وَإِذا حَلَفت على يَمِين فَرَأَيْت غَيرهَا خيرا مِنْهَا فَكفر عَن يَمِينك وائت الَّذِي هُوَ خير
٦٣٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ أخبرنَا مَنْصُور وَيُونُس عَن الْحسن عَن عبد الرَّحْمَن بن سَمُرَة قَالَ قَالَ لي رَسُول الله صلى الله عَلَيْهِ وَسلم يَا عبد الرَّحْمَن بن سَمُرَة إِذا آلَيْت على يَمِين فَرَأَيْت غَيرهَا خيرا مِنْهَا فَائت الَّذِي هُوَ خير وَكفر عَن يَمِينك وَرِوَايَة ابْن عون','2','88',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (445,'<span data-type="title" id=toc-437>حكم الرِّبَا فِي دَار الْحَرْب</span>

٦٣٩ - قلت لأبي الرِّبَا فِي أَرض الْحَرْب قَالَ إِذا دخل بِأَمَان فَلَا يُعجبنِي
<span data-type="title" id=toc-438>لَا يفرق بَين السبيئة وَوَلدهَا وَإِن رضيت</span>

٦٤٠ - قلت السبيئة إِذا رضيت يفرق بَينهَا وَبَين وَلَدهَا قَالَ لَا يفرق بَينهَا وَبَين وَلَدهَا','2','89',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (446,'<span data-type="title" id=toc-439>حَدِيث فَاطِمَة بنت قيس فِي الْمُسْتَحَاضَة لَيْسَ بِشَيْء</span>

٦٤١ - قلت حَدِيث فَاطِمَة بنت أبي حُبَيْش فِي الْمُسْتَحَاضَة رَوَاهُ أَبُو الزبير عَن جَابر عَن فَاطِمَة بنت قيس فِي الْمُسْتَحَاضَة قَالَ أبي لَيْسَ هَذَا بِشَيْء
٦٤٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ حَدثنَا ابْن جريج قَالَ حَدثنِي هِشَام بن عُرْوَة عَن عُرْوَة أَن عَائِشَة حدثته أَن فَاطِمَة بنت أبي حُبَيْش جَاءَت رَسُول الله صلى الله عَلَيْهِ وَسلم وَكَانَت تستحاض فَقَالَت يَا رَسُول الله مَا اطهر أفأترك الصَّلَاة أبدا قَالَ إِنَّمَا ذَاك عرق وَلَيْسَ بالحيضة فَإِذا أَقبلت الْحَيْضَة فاتركي الصَّلَاة فَإِذا ذهب قدرهَا فاغسلي عَنْك الدَّم وَصلي
<span data-type="title" id=toc-440>إِذا اخْتلف زَكَرِيَّا وَإِسْرَائِيل</span>

٦٤٣ - قَالَ أبي إِذا اخْتلف زَكَرِيَّا وَإِسْرَائِيل فَإِن زَكَرِيَّا أحب إِلَيّ فِي أبي','2','90',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (447,'إِسْحَاق من إِسْرَائِيل ثمَّ قَالَ مَا أقربهما
<span data-type="title" id=toc-441>أثر عَليّ فِي نِكَاح الربيبة إِذا لم تكن فِي حجره</span>

٦٤٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ أخبرنَا ابْن جريج قَالَ أَخْبرنِي إِبْرَاهِيم بن عبيد بن رِفَاعَة قَالَ عبد الرَّزَّاق أَو إِبْرَاهِيم يَعْنِي ابْن ميسرَة عَن عبيد بن رِفَاعَة قَالَ أَخْبرنِي مَالك بن أَوْس بن الْحدثَان النصري قَالَ كَانَت عِنْدِي امْرَأَة فَولدت لي فَتُوُفِّيَتْ فَوجدت عَلَيْهَا فلقيني عَليّ بن أبي طَالب فَقَالَ مَالك فَقلت توفيت الْمَرْأَة فَقَالَ عَليّ ألها ابْنة قلت نعم قَالَ كَانَت فِي حجرك','2','91',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (448,'قلت لَا هِيَ بِالطَّائِف فَقَالَ انكحها قلت فَأَيْنَ قَوْله {وربائبكم اللَّاتِي فِي حجوركم من نِسَائِكُم} قَالَ إِنَّهَا لم تكن فِي حجرك وَإِنَّمَا ذَاك إِذا كَانَت فِي حجرك
<span data-type="title" id=toc-442>أثر عمر وَعلي فِي النِّكَاح ببنت الربيب</span>

٦٤٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ أخبرنَا ابْن جريج قَالَ أخبرنَا إِبْرَاهِيم بن ميسرَة أَن رجلا من بني سوأة يُقَال لَهُ عبيد الله ابْن معية أثنى عَلَيْهِ خيرا أخبرهُ أَن أَبَاهُ أَو جده كَانَ نكح امْرَأَة ذَات ولد من غَيره فاصطحبا مَا شَاءَ الله ثمَّ نكح امْرَأَة شَابة فَقَالَ لَهُ أحد بني الأولى قد نكحت على أمنا وَكَبرت واستغنيت عَنْهَا بِامْرَأَة شَابة فَطلقهَا فَقَالَ لَا وَالله إِلَّا أَن تنكحني ابْنَتك فَطلقهَا وأنكحه ابْنَته وَلم','2','92',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (449,'تكن فِي حجره هِيَ وَلَا أَبوهَا ابْن الْعَجُوز الْمُطلقَة قَالَ فَجئْت سُفْيَان بن عبد الله الثَّقَفِيّ فَقلت استفت لي عمر فَقَالَ لتحجن معي فَأَدْخلنِي عَلَيْهِ بمنى فقصصت عَلَيْهِ الْخَبَر فَقَالَ لَا بَأْس بذلك واذهب فاسأل فلَانا ثمَّ تعال فَأَخْبرنِي قَالَ وَلَا أرَاهُ قَالَ إِلَّا عليا قَالَ فَسَأَلته فَقَالَ لَا بَأْس بذلك قَالَ فجمعهما','2','93',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (450,'<span data-type="title" id=toc-443>حكم رمي الْجَمْرَة بخزف أَو جص</span>

٦٤٦ - سَأَلت أبي عَمَّن رمى الْجَمْرَة بخزف أَو جص فَقَالَ لَا يجْزِيه لقَوْل النَّبِي صلى الله عَلَيْهِ وَسلم ارْمِ بِمثل حَصى الْخذف
<span data-type="title" id=toc-444>إِذا صلى الْحَاج الْمغرب بِعَرَفَة</span>

٦٤٧ - قلت فَإِن صلى الْمغرب بِعَرَفَة قَالَ أَرْجُو وَقد رخص بعض النَّاس فِي الصَّلَاة دون جمع','2','94',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (451,'<span data-type="title" id=toc-445>مُشَاورَة القَاضِي الْعلمَاء</span>

٦٤٨ - قَالَ أبي ولي سعد بن إِبْرَاهِيم قَضَاء الْمَدِينَة فَكَانَ يجلس بَين الْقَاسِم وَسَالم يشاورهما وَولي محَارب بن دثار الْكُوفَة فَكَانَ يجلس بَين الحكم وَحَمَّاد يشاورهما قَالَ وَكَانَ سعد رجلا هيوبا','2','95',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (452,'<span data-type="title" id=toc-446>آخر الْجُزْء الْعَاشِر من أَجزَاء صَالح</span>

<span data-type="title" id=toc-447>أثران فِي النِّكَاح بالربيبة وببنت الربيب</span>

٦٤٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا حجاج عَن ابْن جريج قَالَ أَخْبرنِي إِبْرَاهِيم بن ميسرَة أَن رجلا من بني سوأة يُقَال لَهُ عبيد الله بن معية وَأثْنى عَلَيْهِ خيرا أخبرهُ أَن أَبَاهُ أَو جده كَانَ نكح امْرَأَة شَابة فَذكر مثل معنى حَدِيث عبد الرَّزَّاق إِلَّا أَنه قَالَ فَسَأَلته فَقَالَ لَا بَأْس بذلك فجمعتهما
٦٥٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا روح بن عبَادَة قَالَ أخبرنَا ابْن جريج قَالَ أَخْبرنِي إِبْرَاهِيم بن عبيد بن رِفَاعَة قَالَ أَخْبرنِي مَالك بن أَوْس بن الْحدثَان النصري قَالَ كَانَت عِنْدِي امْرَأَة فَولدت فَتُوُفِّيَتْ فَوجدت عَلَيْهَا فَذكر مثل حَدِيث عبد الرَّزَّاق قَالَ أبي إِبْرَاهِيم بن عبيد بن رِفَاعَة لَيْسَ بِمَشْهُور وَعبيد الله بن معية','2','96',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (453,'لَيْسَ بِمَشْهُور بِالْعلمِ وَإِنَّمَا حكى أَن أَبَاهُ أَو جده
<span data-type="title" id=toc-448>أثران فِي الزواج بِأم الزَّوْجَة إِذا مَاتَت الزَّوْجَة قبل الدُّخُول بهَا</span>

٦٥١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ الرجل يتَزَوَّج الْمَرْأَة فتموت قبل أَن يدْخل بهَا أيتزوج بأمها قَالَ حَدثنَا سعيد عَن قَتَادَة عَن الْحسن عَن عمرَان بن حُصَيْن قَالَ لَا تحل لَهُ على حَال وَهُوَ قَول الْحسن
٦٥٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا سعيد عَن قَتَادَة عَن خلاس أَن عليا كَانَ لَا يرى بَأْسا مَاتَت عِنْده أَو طَلقهَا مالم يغشها وينزلها بِمَنْزِلَة الربيبة','2','97',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (454,'<span data-type="title" id=toc-449>إِذا أوصى رجل أَن تدفن كتبه بعد مَوته</span>

٦٥٣ - سَأَلت عَن رجل أوصى أَبَاهُ إِذا هُوَ مَاتَ أَن يدْفن كتبه قَالَ الْأَب بعد موت ابْنه مَا اشتهي أَن أدفنها قَالَ إِنِّي أَرْجُو إِذا كَانَت مِمَّا ينْتَفع بِالنّظرِ فِيهَا ورثته رَجَوْت إِن شَاءَ الله تَعَالَى
<span data-type="title" id=toc-450>ضَمَان الْأَب دين الإبن الْمَيِّت</span>

٦٥٤ - وَسَأَلته عَن رجل عَلَيْهِ دين فَمَاتَ فَقَالَ الْأَب لأَصْحَاب الدّين عَليّ هَذَا الدّين يبرأ الْمَيِّت من ذَلِك','2','98',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (455,'قَالَ أما ضَمَانه فَجَائِز وَلَكِن إِنَّمَا يبرأ مِنْهُ إِذا قضى دينه
<span data-type="title" id=toc-451>تسنيم الْقَبْر أفضل</span>

٦٥٥ - وَقَالَ فِي الْقَبْر أعجب إِلَيّ أَن يكون مسنما
<span data-type="title" id=toc-452>الضَّرْب على قوم لَا يستأهلون أَن يحدث عَنْهُم</span>

٦٥٦ - وَسَأَلته عَن رجل أوصى رجلا وَله فِي يَده كتاب وَفِيه قوم لَا يستأهلون أَن يحدث عَنْهُم فَقَالُوا لَهُ اضْرِب عَلَيْهِم فَلم يفعل حَتَّى مَاتَ فترى أَن يضْرب على هَؤُلَاءِ أَو يستأمر ورثته قَالَ يضْرب عَلَيْهِم','2','99',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (456,'<span data-type="title" id=toc-453>كَيفَ يعْمل إِذا كَانَت آيَة تحْتَمل أَن تكون عَامَّة وخاصة</span>

٦٥٧ - سُئِلَ أبي عَن الْآيَة إِذا جَاءَت تحْتَمل أَن تكون عَامَّة وتحتمل أَن تكون خَاصَّة مَا السَّبِيل فِيهَا فَقَالَ إِذا كَانَت لِلْآيَةِ ظَاهر ينظر مَا عملت السّنة فَهُوَ الدَّلِيل على ظَاهرهَا وَمِنْه قَول الله تَعَالَى {يُوصِيكُم الله فِي أَوْلَادكُم} فَلَو كَانَت على ظَاهرهَا لزم من قَالَ بِالظَّاهِرِ أَن يُورث كل من وَقع عَلَيْهِ اسْم ولد وَإِن كَانَ قَاتلا أَو يَهُودِيّا أَو نَصْرَانِيّا أَو مجوسيا أَو عبدا فَلَمَّا قَالَ رَسُول الله صلى الله عَلَيْهِ وَسلم لَا يَرث الْمُسلم الْكَافِر وَلَا الْكَافِر الْمُسلم كَانَ ذَلِك معنى الْآيَة فَإِذا لم يكن عَن النَّبِي صلى الله عَلَيْهِ وَسلم فِي ذَلِك شَيْء مشروح يخبر فِيهِ عَن خُصُوص أَو عُمُوم ينظر إِلَى عمل أَصْحَابه بِهِ فَيكون ذَلِك معنى الْآيَة فَإِذا اخْتلفُوا ينظر إِلَى أَي الْقَوْلَيْنِ أشبه بقول رَسُول الله','2','100',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (457,'صلى الله عَلَيْهِ وَسلم فَيكون الْعَمَل عَلَيْهِ
<span data-type="title" id=toc-454>حكم قطع الْأصْبع الزَّائِدَة وشق بطن الْمَرْأَة إِذا مَاتَت وَفِي بَطنهَا ولد حَيّ</span>

٦٥٨ - وَسَأَلته عَن الْمَرْأَة تَمُوت وَفِي بَطنهَا ولد قَالَ إِذا لم يقدر النِّسَاء فليسط عَلَيْهَا رجل يُخرجهُ','2','101',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (458,'٦٥٩ - قلت الصَّبِي يُولد وأصبع لَهُ زَائِدَة يقطع فَقَالَ لَا يقطع
٦٦٠ - وَقَالَ فِي الْمَرْأَة تَمُوت وَفِي بَطنهَا صبي حَيّ يشق عَنْهَا قَالَ لَا يشق عَنْهَا إِن أَرَادَ الله أَن يُخرجهُ أخرجه','2','102',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (459,'<span data-type="title" id=toc-455>حكم الْقرعَة</span>

٦٦١ - وَقَالَ فِي الْقرعَة أَرَاهَا قد أَقرع رَسُول الله صلى الله عَلَيْهِ وَسلم فِي خَمْسَة مَوَاضِع قَالَ إِذا أكره الإثنان على الْيَمين واستحباها فليستهما عَلَيْهَا وَإِذا ادّعَيَا شَيْئا وَلَيْسَ فِي يَد وَاحِد مِنْهُمَا يقرع بَينهمَا فَأَيّهمَا أَصَابَته الْقرعَة حلف وأقرع النَّبِي صلى الله عَلَيْهِ وَسلم فِي العبيد السِّتَّة وَكَانَ النَّبِي صلى الله عَلَيْهِ وَسلم يقرع بَين نِسَائِهِ إِذا أَرَادَ سفرا وأقرع فِي الْوَلَد حَدِيث الْأَجْلَح عَن الشّعبِيّ عَن أبي','2','103',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (460,'الْخَلِيل عَن زيد بن أَرقم وَهُوَ مُخْتَلف فِيهِ وَفِي الْقُرْآن {إِذْ يلقون أقلامهم أَيهمْ يكفل مَرْيَم} ) وَفِي يُونُس','2','104',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (461,'فساهم فَكَانَ من المدحضين وَقد تزْعم أَصْحَاب أبي حنيفَة إِذا قسمت الدّور والأرضيين أَقرع بَينهم فَأَيهمْ أَصَابَته الْقرعَة كَانَ لَهُ مَا أَصَابَهُ من ذَلِك
٦٦٢ - قَالَ أبي وسألوني عِنْد يزِيد بن هَارُون فتكلمت فِيهَا فَقَالُوا رجل كن لَهُ أَربع نسْوَة فَطلق إِحْدَاهُنَّ ثمَّ تزوج خَامِسَة ثمَّ مَاتَ وَلم يدر أيتهن طلق قلت قَالَ إِبْرَاهِيم وَحَمَّاد يرثن جَمِيعًا ويعتددن جَمِيعًا وَقَالَ الْحسن','2','105',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (462,'وَسَعِيد بن الْمسيب وَقَتَادَة يقرع بَينهُنَّ فأيتهن أصابتها الْقرعَة فَهِيَ هِيَ وَفِي قَول من زعم أَنَّهُنَّ يرثن جَمِيعًا ويعتددن جَمِيعًا أَنه لَا يشك أَنه قد ورث من لم يجب لَهَا الْمِيرَاث وَأوجب الْعدة عَلَيْهَا وَلم يجب عَلَيْهَا الْعدة وَالَّذِي يَقُول يقرع بَينهُنَّ فسببها مغيب وَقد يكون أصَاب وَقد يكون لم يصب وَذَاكَ لم يشك فِيهِ أَنه قد أوجب الْمِيرَاث لمن لَا مِيرَاث لَهَا وَأوجب الْعدة عَلَيْهَا وَهَذَا أشبه بِسنة رَسُول الله صلى الله عَلَيْهِ وَسلم الْقرعَة يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه أَقرع فِي خمس مَوَاضِع
<span data-type="title" id=toc-456>حكم بيع الْأمة واستثناء مَا فِي بَطنهَا</span>

٦٦٣ - وَقَالَ فِي الرجل يَبِيع الْأمة ويستثني مَا فِي بَطنهَا وَهِي الْحَامِل لشهر أَو','2','106',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (463,'أَكثر من ذَلِك فَقَالَ حَدِيث ابْن عمر أَنه أعتق أمة وَاسْتثنى مَا فِي بَطنهَا وَقَول ابْن عمر شَبيه أَو قريب من هَذَا
٦٦٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا ابْن مهْدي عَن عباد بن عباد عَن عبيد الله عَن نَافِع عَن ابْن عمر نَحوه','2','107',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (464,'<span data-type="title" id=toc-457>الدُّخُول بِالْمَرْأَةِ قبل أَن يُعْطِيهَا شَيْئا</span>

٦٦٥ - وَقَالَ فِي رجل تزوج امْرَأَة فَأَرَادَ أَن يدْخل بهَا قبل أَن يُعْطِيهَا شَيْئا قَالَ لَا بَأْس وَإِن قدم شَيْئا فَلَا بَأْس
<span data-type="title" id=toc-458>من تزوج ذَات محرم</span>

٦٦٦ - وَقَالَ فِي رجل يتَزَوَّج ذَات محرم مِنْهُ وَهُوَ لَا يعلم ثمَّ يعلم قَالَ إِن كَانَ عمد يقتل وَيُؤْخَذ مَاله وَإِن كَانَ لَا يعلم يفرق بَينهمَا وَاسْتحبَّ أَن يكون لَهَا مَا أخذت مِنْهُ وَلَا يرجع عَلَيْهَا بِشَيْء','2','108',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (465,'<span data-type="title" id=toc-459>كم تجْلِس المبتدأة بِالدَّمِ</span>

٦٦٧ - وَقَالَ فِي المبتدأة بِالدَّمِ لَيْسَ فِيهَا سنة يَقُول بعض النَّاس تجْلِس أقل مَا تجلسه النِّسَاء وَهُوَ يَوْم إِذا كَانَ مثلهَا تحيض وَتصلي فِيمَا سوى ذَلِك وتصوم فَإِن عاودها الدَّم ثَانِيَة وثالثة فاستقام لَهَا على أَيَّام تعرفها فَهُوَ حيض وَينظر فِيمَا صَامت فَإِن كَانَت صَامت فِي رَمَضَان فِي الْأَيَّام الَّتِي رَأَتْ فِيهَا الدَّم سوى الْيَوْم الَّذِي تركت فِيهِ الصَّلَاة أعادت الصَّوْم لِأَنَّهُ لَا يجزيها أَن تَصُوم وَهِي حَائِض وَمن النَّاس من يَقُول إِذا اسْتمرّ بهَا الدَّم جَلَست أَكثر مَا تجلسه النِّسَاء وَهُوَ خمس عشرَة','2','109',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (466,'<span data-type="title" id=toc-460>أقل مُدَّة الْحيض</span>

٦٦٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا النُّفَيْلِي قَالَ قَرَأت على معقل بن عبيد الله عَن عَطاء قَالَ أدنى وَقت الْحيض يَوْم قَالَ أبي وَكَذَا كَانَ الشَّافِعِي يَقُول يَوْم','2','110',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (467,'<span data-type="title" id=toc-461>وقُوف الْمغمى عَلَيْهِ بِعَرَفَة</span>

٦٦٩ - قلت الْمغمى عَلَيْهِ يُوقف بِعَرَفَة فَقَالَ إِذا لم يعقل الْوُقُوف حَتَّى ينفجر الْفجْر فَلَا حج لَهُ يرْوى عَن الْحسن وَعَطَاء وَمَا علمت أَن أحدا قَالَ يجْزِيه وَمن احْتج فَزعم أَن الْحَج عَرَفَة فَلَو كَانَ هَذَا على ظَاهر الْكَلَام فَوقف بِعَرَفَة وَرجع إِلَى أَهله ووطيء وَأصَاب الصَّيْد كَانَ يلْزمه أَن يَقُول لَيْسَ عَلَيْهِ شَيْء لِأَن الْحَج عَرَفَة وَإِنَّمَا قَوْله الْحَج عَرَفَة على السَّلامَة إِذا عمل مَا يعْمل','2','111',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (468,'النَّاس من طواف يَوْم النَّحْر وَهُوَ الْوَاجِب لِأَنَّهُ لم يخْتَلف النَّاس فِيمَا علمنَا أَنه من لم يطف يَوْم النَّحْر أَنه يرجع حَتَّى يطوف وَإِن كَانَ قد أَتَى أَهله وَذَلِكَ يشبه قَول رَسُول الله صلى الله عَلَيْهِ وَسلم من أدْرك من الصَّلَاة رَكْعَة فقد أدْركهَا فَإِذا أدْرك رَكْعَة [أ] فَلَيْسَ عَلَيْهِ أَن يَأْتِي بهَا على كمالها وَمَا أفسد آخرهَا أفسد أَولهَا وَإِنَّمَا ذَلِك على إكمالها قَالَ وَمن قَالَ إِن الْمغمى عَلَيْهِ يجْزِيه الْوُقُوف بِعَرَفَة فقد يجْزِيه أَن لَا يُعِيد الصَّلَاة وَكَذَلِكَ الصَّوْم وَلَو أُغمي عَلَيْهِ فِي يَوْم من رَمَضَان حَتَّى يَنْسَلِخ عَنهُ رَمَضَان أَنه يجْزِيه لِأَنَّهُ لم يطعم فِيهِ','2','112',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (469,'<span data-type="title" id=toc-462>صِيَام الْمغمى عَلَيْهِ</span>

٦٧٠ - وَقَالَ إِذا أجمع الصّيام من اللَّيْل ثمَّ أصبح وَهُوَ على نِيَّته ثمَّ أُغمي عَلَيْهِ أَجزَأَهُ أول يَوْم وَعَلِيهِ إِعَادَة بَقِيَّة الشَّهْر سوى ذَلِك الْيَوْم
<span data-type="title" id=toc-463>حكم أَخذ الدَّائِن جَارِيَة الْمَدْيُون إِذا جحد الدّين</span>

٦٧١ - وَسَأَلته عَن رجل كَانَ لَهُ على رجل ألف دِرْهَم فجحده عَلَيْهِ فَوجدَ هَذَا لَهُ جَارِيَة يَأْخُذهَا فَقَالَ أَنا أَقُول إِن وجد لَهُ دَرَاهِم لَا يَأْخُذهَا وَذَاكَ أَن هَذَا الْملك ملك الرجل فَكيف يجوز أَن يَأْخُذ مَالا يملك
٦٧٢ - قلت إِنَّهُم يحتجون بِحَدِيث هِنْد حَيْثُ جَاءَت إِلَى النَّبِي صلى الله عَلَيْهِ وَسلم تَشكوا أَبَا سُفْيَان فَقَالَت إِن أَبَا سُفْيَان رجل شحيح فَقَالَ خذي مَا','2','113',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (470,'يَكْفِيك وَيَكْفِي ولدك فَقَالَ هَذَا بَيتهَا وَبَيت وَلَدهَا وَرخّص أَن تكون تَأْخُذ مَا يكفيها وَقَالَ يَطَأهَا بِالْحَقِّ إِذن كَيفَ يطَأ مَا لَيْسَ هُوَ لَهُ بِملك إِمَّا يَزُول الْملك بِبيع أَو هبة أَو صَدَقَة أَو تمْلِيك يملكهُ الْمَالِك
<span data-type="title" id=toc-464>إِذا ظهر عيب فِي الْمَبِيع بعد تصرف المُشْتَرِي فِيهِ</span>

٦٧٣ - قَالَ وَإِذا اشْترى ثوبا فَقَطعه ثمَّ ظهر بِهِ عيب يرْوى عَن عُثْمَان أَنه مُخَيّر وَقَالَ بَعضهم يردهُ وَإِن كَانَ قد لبسه وَالَّذِي أذهب إِلَيْهِ أَنه','2','114',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (471,'مُخَيّر فَإِن رده رده ورد نُقْصَان مَا أحدث فِيهِ وَإِن هُوَ حَبسه رَجَعَ على البَائِع بِقدر نُقْصَان الْعَيْب
<span data-type="title" id=toc-465>طَلَاق السَّكْرَان وَالْمَجْنُون</span>

٦٧٤ - وَقَالَ كَانَ شُعْبَة يروي فِي طَلَاق السَّكْرَان عَن أَيُّوب عَن عَمْرو بن دِينَار قَالَ لَا يجوز طَلَاقه ويروى عَن عُثْمَان أَنه قَالَ لَيْسَ لمَجْنُون وَلَا سَكرَان طَلَاق رَوَاهُ ابْن أبي ذِئْب عَن الزُّهْرِيّ عَن أبان بن عُثْمَان عَن عُثْمَان وَهُوَ أرفع شَيْء فِيهِ','2','115',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (472,'<span data-type="title" id=toc-466>حكم دين العَبْد إِذا أعتق</span>

٦٧٥ - وَقَالَ فِي عبد عتقه مَوْلَاهُ وَقد كَانَ أذن لَهُ فِي التِّجَارَة فادان يُؤْخَذ السَّيِّد بِمَا ادان لما أذن لَهُ فِيهِ وَإِن كَانَ غير ذَلِك فادان العَبْد فَهُوَ فِي ذمَّة العَبْد وَلَيْسَ على الْمولى شَيْء يُؤَدِّيه العَبْد على نَفسه
<span data-type="title" id=toc-467>إِذا وَقع فِي الْبِئْر نقطة خمر أَو بَوْل</span>

٦٧٦ - قلت بِئْر وَقع فِيهَا نقطة خمر قَالَ مَا لم يُغير طعم أَو ريح
٦٧٧ - قلت فنقطة بَوْل قَالَ أتوقاه لقَوْل النَّبِي صلى الله عَلَيْهِ وَسلم لَا يَبُول أحدكُم فِي المَاء الدَّائِم
<span data-type="title" id=toc-468>إِذا ولدت زَوجته مِنْهُ أَوْلَادًا ثمَّ تبين أَنَّهَا أمة</span>

٦٧٨ - وَقَالَ فِي رجل تزوج أمة وَهُوَ يرى أَنَّهَا حرَّة فَولدت مِنْهُ أَوْلَادًا ثمَّ جَاءَ الْمولى فَأَقَامَ الْبَيِّنَة أَنَّهَا أمته أبقت من عِنْده فعلى أَبِيهِم أَن يفديهم','2','116',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (473,'وَترد الْأمة إِلَى مَالِكهَا وَقَالَ بَعضهم مَكَان كل وصيف وصيف فَإِن كَانَ رجل غره مِنْهَا فعلى الْغَار الَّذِي غره أَن يفْدي وَلَده لَهُ فَإِن لم تقر أَنَّهَا أمة وَلم تكن لَهُ بَيِّنَة فَلَا شَيْء لَهُ حَتَّى يثبت لَهُ أَو تقر هِيَ أَنَّهَا أمته','2','117',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (474,'<span data-type="title" id=toc-469>حكم مَال الْمكَاتب الزَّائِد عَن كِتَابَته</span>

٦٧٩ - وَقَالَ فِي مكَاتب مَاتَ وَترك مَالا وَترك فِيهِ أَكثر من مُكَاتبَته قَالَ إِذا مَاتَ يَوْم مَاتَ وَلم يؤد بَقِيَّة مُكَاتبَته فَمَا ترك من شَيْء فَهُوَ لمَوْلَاهُ لِأَنَّهُ مَاتَ وَهُوَ عبد وَمَال العَبْد لسَيِّده وَإِن كَانُوا وَلَده ولدُوا فِي مُكَاتبَته فهم عبيد وَإِن كَانَ كاتبهم مَعَ أَبِيهِم فَلهم أَن يقومُوا بكتابتهم وترفع عَنْهُم مُكَاتبَة الْأَب كَأَنَّهُ كَاتبه وبنيه على ألف فيرفع عَن وَلَده بِحِصَّة أَبِيهِم وَكَذَا إِن مَاتَ وَاحِد من وَلَده رفع عَن أَبِيهِم حِصَّته
<span data-type="title" id=toc-470>الأحق بِالْإِمَامَةِ</span>

٦٨٠ - وَقَالَ قَالَ رَسُول الله صلى الله عَلَيْهِ وَسلم يؤم الْقَوْم أقرؤهم لكتاب الله فَإِن كَانُوا فِي الْقِرَاءَة سَوَاء فأعلمهم بِالسنةِ فَيَنْبَغِي للَّذي يقْرَأ الْقُرْآن أَن يتَعَلَّم من السّنة مَا يُقيم بِهِ صلَاته فَهُوَ حِينَئِذٍ أولى بِالصَّلَاةِ','2','118',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (475,'<span data-type="title" id=toc-471>زِيَادَة الْإِيمَان ونقصانه</span>

٦٨١ - وَسَأَلت أبي عَمَّن يَقُول الْإِيمَان يزِيد وَينْقص مَا زِيَادَته ونقصانه فَقَالَ زِيَادَته بِالْعَمَلِ ونقصانه بترك الْعَمَل مثل تَركه الصَّلَاة وَالزَّكَاة وَالْحج وَأَدَاء الْفَرَائِض فَهَذَا ينقص وَيزِيد بِالْعَمَلِ وَقَالَ إِن كَانَ قبل زِيَادَته تَاما فَكيف يزِيد التَّام فَكَمَا يزِيد كلما ينقص وَقَالَ كَانَ وَكِيع رُبمَا قَالَ إِيمَان الْحجَّاج مثل إِيمَان أبي بكر وَعمر','2','119',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (476,'<span data-type="title" id=toc-472>حكم رفع الْيَدَيْنِ والجهر بآمين وَفصل الْوتر</span>

٦٨٢ - وَسَأَلت أبي عَن رفع الْأَيْدِي عِنْد الرُّكُوع وَبعد الرُّكُوع والجهر بآمين عِنْد {وَلَا الضَّالّين} وَفصل الْوتر فَقَالَ يرفع يَدَيْهِ قبل الرُّكُوع وَبعد الرُّكُوع ويؤتر بِرَكْعَة إِذا كَانَ قبلهَا صَلَاة مُتَقَدّمَة على حَدِيث ابْن عمر يفصل بَين الرَّكْعَتَيْنِ وَقَالَ يجْهر بآمين عِنْد {وَلَا الضَّالّين}
٦٨٣ - قلت مَا تَقول فِي رجل يؤم قوما وَيرْفَع يَدَيْهِ فِي الصَّلَاة ويجهر بآمين ويفصل الْوتر والمأمومون لَا يرضون بذلك وَمِنْهُم من يرضى حَتَّى ان أحدهم ليترك الْوتر لحَال التَّفْصِيل وَيخرج من الْمَسْجِد فترى أَن يرجع إِلَى قَول الْمَأْمُومين أم يثبت على مَا يَأْمُرهُ أهل الْفِقْه فَقَالَ بل يثبت على صلَاته وَلَا يلْتَفت إِلَيْهِم','2','120',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (477,'<span data-type="title" id=toc-473>عدد الضربات فِي التَّيَمُّم</span>

٦٨٤ - قلت لأبي مَا تَقول فِي التَّيَمُّم فَقَالَ ضَرْبَة للْوَجْه وَالْكَفَّيْنِ على حَدِيث عمار
<span data-type="title" id=toc-474>هَل يجوز التَّيَمُّم للحطاب وَأَمْثَاله</span>

٦٨٥ - قلت لأبي مَا تَقول فِي الحراث أَو الْحطاب يكون على رَأس فَرسَخ وَلَا يجد المَاء قَالَ إِذا كَانَ يخَاف إِن طلب المَاء فَاتَتْهُ الصَّلَاة تيَمّم وَقَالَ','2','121',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (478,'إِن ابْن عمر كَانَ فِي سفر فَتَيَمم وَصلى وَدخل الْمَدِينَة وَعَلِيهِ نَهَار فَلم يعد
<span data-type="title" id=toc-475>الْوضُوء مرّة أَو مرَّتَيْنِ أَو ثَلَاث</span>

٦٨٦ - قلت مَا تَقول فِي الْوضُوء مرّة أَو مرَّتَيْنِ أَو ثَلَاثًا قَالَ ثَلَاث أعجب إِلَيّ وَإِذا أَسْبغ بِوَاحِدَة فأرجو
<span data-type="title" id=toc-476>نقض الْوضُوء من خلع الْخُفَّيْنِ بعد الْمسْح عَلَيْهِمَا</span>

٦٨٧ - قلت مَا تَقول فِيمَن تَوَضَّأ وخلع خفيه وَقد مسح عَلَيْهِ قَالَ يُعِيد الْوضُوء كُله وَالْحجّة أَن الطَّهَارَة لَا ينْتَقض بَعْضهَا دون بعض فَمن زعم أَنه يغسل رجلَيْهِ فقد زعم أَن الطَّهَارَة منتقضة عَن الرجلَيْن وَهُوَ حَيْثُ مسح على خفيه فقد طهرت رِجْلَاهُ فَمن زعم أَنه','2','122',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (479,'يغسل قَدَمَيْهِ فقد زعم أَن الطَّهَارَة قد انتقضت عَن الْقَدَمَيْنِ وَهَذَا محَال أَن ينْتَقض بَعْضهَا دون بعض وَقد يزْعم بعض النَّاس أَنه لَو خلع أحد الْخُفَّيْنِ وَقد كَانَ مسح عَلَيْهِمَا أَنه يجب عَلَيْهِ خلع الْخُف الآخر حَتَّى يغسل قَدَمَيْهِ جَمِيعًا وَالْحجّة على','2','123',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (480,'من زعم أَن الطَّهَارَة منتقضة عَن الْقَدَمَيْنِ إِذا هُوَ خلع الْخُفَّيْنِ أَنه يَقُول إِذا خلع أحد الْخُفَّيْنِ فقد انتقضت الطَّهَارَة عَن الرجل الْأُخْرَى بخلعه الْخُف الْوَاحِد فقد زعم أَن الطَّهَارَة منتقضة عَن الرجل الَّتِي لم يحدث فِيهَا شَيْئا
<span data-type="title" id=toc-477>الْمسْح أَعلَى الْخُفَّيْنِ أَو أسفلهما</span>

٦٨٨ - قلت مَا تَقول فِي الْمسْح على الْخُفَّيْنِ أَعْلَاهُ وأسفله قَالَ أما أَنا فأرجو أَن يجْزِيه الْأَعْلَى دون الْأَسْفَل وَرُوِيَ عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَن الْمُسَافِر يمسح ثَلَاثَة أَيَّام ولياليهن والمقيم يَوْمًا وَلَيْلَة وَقَالَ بعض النَّاس مسح النَّبِي صلى الله عَلَيْهِ وَسلم أعلاهما وأسفلهما','2','124',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (481,'٦٨٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا الْوَلِيد بن مُسلم عَن ثَوْر بن زيد عَن رَجَاء بن حَيْوَة عَن كَاتب الْمُغيرَة عَن الْمُغيرَة بن شُعْبَة أَن النَّبِي صلى الله عَلَيْهِ وَسلم','2','125',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (482,'مسح أَعلَى الْخُفَّيْنِ وأسفله قَالَ أبي فَذكرت لعبد الرَّحْمَن بن مهْدي فَذكر عَن ابْن الْمُبَارك عَن ثَوْر قَالَ حدثت عَن رَجَاء عَن كَاتب الْمُغيرَة وَلم يذكر فِيهِ الْمُغيرَة وَلَا أرى الحَدِيث ثَبت وَقد رُوِيَ عَن سعيد وَأنس أَنَّهُمَا مسحا أَعلَى الْخُفَّيْنِ','2','126',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (483,'<span data-type="title" id=toc-478>حكم الرَّد على الإِمَام إِذا سلم</span>

٦٩٠ - قلت مَا تَقول فِي الرَّد على الإِمَام إِذا سلم وَمَتى يرد عَلَيْهِ قَالَ إِذا سلم الإِمَام فَهُوَ خُرُوجه من الصَّلَاة وَمن سلم خَلفه فَإِن نوى الرَّد عَلَيْهِ بِتَسْلِيمِهِ وَخُرُوجه فَلَا بَأْس
<span data-type="title" id=toc-479>المتوضيء يَأْخُذ من أشعاره وَمن أَظْفَاره</span>

٦٩١ - قلت مَا تَقول فِي المتوضيء يَأْخُذ من شعره وأظفاره قَالَ لَا بَأْس بِهِ
<span data-type="title" id=toc-480>هَل ينْتَقض الْوضُوء من الْكَذِب والغيبة وَنَحْوهمَا</span>

٦٩٢ - قلت مَا تَقول فِي الْغَيْبَة وَالْكذب والخنا وَالْفُحْش ينْقض الْوضُوء قَالَ أَرْجُو','2','127',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (484,'<span data-type="title" id=toc-481>حكم الطَّهَارَة من المَاء الَّذِي يُوجد فِي الصَّحَارِي</span>

٦٩٣ - قلت الَّذِي يكون فِي الصَّحَارِي والمفاوز فيجد المَاء فِي مَوَاضِع شَتَّى قَالَ إِذا اجْتمع من مَاء السَّمَاء فَأخذ مِنْهُ رجل وان كَانَ ذَلِك مُتَفَرقًا فِي بقاع شَتَّى فَاجْتمع لَهُ قدر الْمَدّ وَهُوَ رَطْل وَثلث اجزأه وَذَلِكَ لوضوئه إِذا أَسْبغ وَإِن هُوَ أَخذ مِنْهُ بِقدر الصَّاع وَهُوَ خَمْسَة أَرْطَال وَثلث أَجزَأَهُ لغسله إِذا أَسْبغ
<span data-type="title" id=toc-482>رفع الْيَدَيْنِ فِي الصَّلَاة</span>

٦٩٤ - قلت مَا تَقول فِي رفع الْيَدَيْنِ عِنْد الِافْتِتَاح وَقد جَاءَ الْقَوْلَانِ قَول عمر وَوَائِل ابْن حجر','2','128',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (485,'قَالَ أبي يرفع يَدَيْهِ عِنْد الِافْتِتَاح وَقبل الرُّكُوع وَبعد الرُّكُوع وَفِي بعض مَا رُوِيَ عَن وَائِل بن حجر أَن النَّبِي صلى الله عَلَيْهِ وَسلم كَانَ يرفع يَدَيْهِ إِذا كبر وَإِذا ركع وَإِذا رفع رَأسه من الرُّكُوع وَإِذا أَرَادَ أَن يسْجد رفع يَدَيْهِ
<span data-type="title" id=toc-483>الْقِرَاءَة خلف الإِمَام فِي سكتاته</span>

٦٩٥ - قلت مَا تَقول فِي سكتتي الإِمَام وَمَوْضِع سكتته وَإِن عجل الإِمَام قبل أَن يفرغ من خَلفه من قِرَاءَة فَاتِحَة الْكتاب قَالَ إِذا قَرَأَ مَعَ الإِمَام فسبقه يتبع الإِمَام وَفِي سكتتي الإِمَام يقْرَأ إِن','2','129',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (486,'شَاءَ وَهُوَ إِن أدْرك الإِمَام رَاكِعا أَجزَأَهُ قِرَاءَة الإِمَام
<span data-type="title" id=toc-484>حكم التَّسْمِيَة عِنْد الْوضُوء</span>

٦٩٦ - قلت مَا تَقول فِيمَن نسي التَّسْمِيَة عَن الْوضُوء أَو تعمد تَركه','2','130',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (487,'قَالَ لَا يَنْبَغِي أَن يعاند وَأَرْجُو أَن يجْزِيه والْحَدِيث الَّذِي يرْوى فِيهِ لَا أرَاهُ ثَبت
<span data-type="title" id=toc-485>من ترك التَّسْبِيح فِي الرُّكُوع وَالسُّجُود</span>

٦٩٧ - وَقلت لأبي مَا تَقول فِي الصَّلَاة الْمَكْتُوبَة من ترك من التَّسْبِيح فِي الرُّكُوع وَالسُّجُود نَاسِيا أَو عَامِدًا قَالَ إِذا عمد لشَيْء من تَركهَا أعَاد الصَّلَاة وَإِن كَانَ سَاهِيا فأرجو وَإِذا ترك التَّشَهُّد عَامِدًا أعَاد وَالْحجّة فِي أَنه لَا إِعَادَة عَلَيْهِ إِذا كَانَ سَاهِيا أَن النَّبِي صلى الله عَلَيْهِ وَسلم نَهَضَ من ثِنْتَيْنِ فقد ترك التَّشَهُّد فَلم يتَشَهَّد فِي الْأَوليين وَترك تَكْبِيرَة الْجُلُوس للتَّشَهُّد فَنَهَضَ فَسجدَ سَجْدَتي السَّهْو قبل السَّلَام فقد ترك تشهدا وتكبيرا فَلم تفْسد صلَاته وَسجد سَجْدَتَيْنِ قبل التَّسْلِيم فيهمَا','2','131',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (488,'<span data-type="title" id=toc-486>كَيْفيَّة الْمَشْي مَعَ الْجِنَازَة وَحكم الْجُلُوس قبل أَن تُوضَع</span>

٦٩٨ - قلت مَا تَقول فِي الْمَشْي مَعَ الْجِنَازَة أَي ذَلِك أحب إِلَيْك وَقد ذكر عَن النَّبِي صلى الله عَلَيْهِ وَسلم مَا ذكر وَمَا قَالَ عَليّ وَالله إِن فضل الْمَاشِي خلفهَا على الْمَاشِي أمامها كفضل الْمَكْتُوبَة على التَّطَوُّع وَمَا قَالَ عَليّ إِذا صرت إِلَى الْمقْبرَة فَقُمْ وَلَا تقعد حَتَّى يدلى فِي حفرته قَالَ يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم من تبع جَنَازَة فَلَا يجلس حَتَّى تُوضَع','2','132',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (489,'وَقَالَ يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه كَانَ يمشي أمامها وَقَالَ ربيعَة ابْن عبد الله بن الهدير رَأَيْت عمر يقدم النَّاس أَمَام جَنَازَة زَيْنَب بنت جحش','2','133',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (490,'<span data-type="title" id=toc-487>مُدَّة الصَّلَاة على الْقَبْر</span>

٦٩٩ - قلت مَا تَقول فِيمَن غَابَ عَن بَلَده سنة فَرجع وَقد مَاتَ بعض أَقَاربه هَل يُصَلِّي على تِلْكَ الْقُبُور وَإِن حضر جَنَازَة وَقد صلوا عَلَيْهَا كَيفَ يُصَلِّي قَالَ يُصَلِّي مَا بَينه وَبَين شهر وَقَالَ إِن النَّبِي صلى الله عَلَيْهِ وَسلم صلى على قبر أم سعد بعد شهر وانْتهى إِلَى قبر جَدِيد فصلى عَلَيْهِ','2','134',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (491,'<span data-type="title" id=toc-488>حكم اللّقطَة</span>

٧٠٠ - قلت مَا تَقول فِي اللقط يُصِيب الْإِنْسَان مِنْهَا مَا يبلغ عشرَة دَرَاهِم أَو عشْرين أَو أقل أَو أَكثر قَالَ نعم فِيهَا سنة إِذا كَانَت دَرَاهِم أَو ذَهَبا أَو فضَّة فَإِن جَاءَ صَاحبهَا وَإِلَّا فَهُوَ مَال الله يؤتيه الله من يَشَاء فَإِن كَانَت إبِلا لم يقربهَا وَإِن كَانَت غنما فقد قَالَ النَّبِي صلى الله عَلَيْهِ وَسلم هِيَ لَك أَو لأخيك أَو للذئب فَلَا يقربهَا وَالْبَقَرَة لم نسْمع فِيهَا شَيْء وَأما الْمَتَاع فَإِنَّهُ يعرفهُ','2','135',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (492,'<span data-type="title" id=toc-489>شرح حَدِيث إِذا أَتَى أحدكُم بستانا فليناد ثَلَاثًا الخ</span>

٧٠١ - قلت مَا تَقول فِي حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم إِذا أَتَى أحدكُم بستانا فلينادي ثَلَاثًا وَكَذَلِكَ راعي الْإِبِل فَإِن أجابوك وَإِلَّا فَكل واشرب قَالَ هَذَا فِي الْمُسَافِر يمر بِالْحَائِطِ فينادي ثَلَاثًا فَإِن أُجِيب وَإِلَّا أكل وَلم يحمل إِذا لم يكن عَلَيْهِ حَائِط فَإِذا كَانَ عَلَيْهِ حَائِط فَلَا يدْخل يَقُول ذَلِك ابْن عَبَّاس وَرُوِيَ عَن ابْن عمر عَن النَّبِي صلى الله عَلَيْهِ وَسلم لَا تحتلب مواشي الْقَوْم إِلَّا بإذنهم','2','136',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (493,'<span data-type="title" id=toc-490>سُؤال عَن حَدِيث لَا يبل أحدكُم فِي مستحمه</span>

٧٠٢ - قلت مَا تَقول فِي حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم لَا يبل أحدكُم فِي مستحمه قَالَ يَقُول إِن مِنْهُ الوسواس إِذا كَانَ يَبُول موضعا يغْتَسل فِيهِ
<span data-type="title" id=toc-491>الْغسْل بِمَاء الْحمام</span>

٧٠٣ - قلت مَا تَقول فِي الْغسْل بِمَاء الْحمام قَالَ مَاء الْحمام بِمَنْزِلَة المَاء الْجَارِي عِنْدِي','2','137',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (494,'<span data-type="title" id=toc-492>مسَائِل فِي زَكَاة الْفطر</span>

٧٠٤ - قلت مَا تَقول فِي زَكَاة الْفطر وَوقت إِعْطَائِهِ يحملهُ إِلَى مَسْجِد أَو يفرقه على أهل بَيت من المحاويج قَالَ إِن حمله إِلَى السُّلْطَان فَلَا بَأْس وَإِن قسمه فَلَا بَأْس وَيُعْطِي قبل الْعِيد بِيَوْم اَوْ يَوْمَيْنِ ويقدمها قبل صَلَاة الْعِيد
٧٠٥ - قلت مَا تَقول فِي امْرَأَة مسكينة تكون معي فِي دَاري أُوتى بِشَيْء للْمَسَاكِين فأعطيها مِنْهُ إِذا قسمته فِي الْمَسَاكِين','2','138',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (495,'قَالَ لَا يحابيها من ذَلِك ويعطيها كَمَا يُعْطي غَيرهَا
<span data-type="title" id=toc-493>لَا يحجّ عَن الْغَيْر حَتَّى يحجّ عَن نَفسه</span>

٧٠٦ - قلت مَا تَقول فِي رجل لم يحجّ عَن نَفسه أيحج عَن غَيره وَمَا جَاءَ عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه قَالَ حج عَن نَفسك ثمَّ احجج عَن شبْرمَة وَمَا سَأَلت الخثعمية إِن أبي شيخ كَبِير أفأحج عَنهُ فَقَالَ نعم فَقَالَ لَا يحجّ عَن أحد حَتَّى يحجّ عَن نَفسه وَقد بَين ذَلِك النَّبِي صلى الله عَلَيْهِ وَسلم','2','139',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (496,'فَقَالَ أحجج عَن نَفسك ثمَّ احجج عَن شبْرمَة وَحَدِيث ابْن عَبَّاس إِذْ قَالَت الْمَرْأَة يَا رَسُول الله إِن أبي شيخ كَبِير لَا يسْتَمْسك على الرحل أفأحج عَنهُ قَالَ نعم حجي عَن أَبِيك وَهُوَ جملَة لم تبين حجت أَو لم تحج
<span data-type="title" id=toc-494>حمل الْعلم عَمَّن يرتكب بعض المنهيات</span>

٧٠٧ - قلت مَا تَقول فِي رجل يَبُول قَائِما وَيمْسَح فرجه بِيَمِينِهِ ويستقرض الدَّرَاهِم وَلَا يرد وَيَقُول القَوْل وَيحلف وَيَأْمُر بِالْمَعْرُوفِ وَلَا يَأْتِيهِ وَينْهى عَن الْمُنكر ويرتكب بعض ذَلِك أحمل عَنهُ الْعلم فَقَالَ أكره أَن يمس الرجل فرجه بِيَمِينِهِ وَالْبَوْل قَائِما لَا بَأْس بِهِ','2','140',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (497,'ويروى عَن النَّبِي صلى الله عَلَيْهِ وَسلم وَإِذا مَاتَ وَلم يقْض دينه وَلم يقْض عَنهُ وَلم يوص بذلك وَلم يكفر عَن يَمِينه فَلَيْسَ هَذَا بِعدْل
<span data-type="title" id=toc-495>تَعْرِيف الْخمر وَحكم بيع الْعِنَب مِمَّن يَتَّخِذهُ خمرًا</span>

٧٠٨ - قلت مَا تَقول فِي رجل يَبِيع كرمه مِمَّن يعلم أَنه يَتَّخِذهُ خمرًا يشْربهَا هَل يحل بَيْعه وكل شراب يخَامر الْعقل فَهُوَ خمر عنْدك قَالَ لَا يَبِيعهُ مِمَّن يَتَّخِذهُ خمرًا وكل مَا أسكر كَثِيره فقليله حرَام','2','141',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (498,'وَإِذا طبخه وَبَقِي ثُلُثَاهُ فَلَا بَأْس
<span data-type="title" id=toc-496>حكم جعل الْعصير خلا</span>

٧٠٩ - قلت مَا تَقول فِي رجل يصب الشيرج وَهُوَ الْعصير فِي منزله حَتَّى يصير خلا قَالَ إِذا كَانَ عِنْده عصير فيعجبنا أَن يصب عَلَيْهِ من الْخلّ مَالا يكون يغلي فَإِذا صَار خلا أكله وَإِن تَركه حَتَّى يغلي من ذَاته خشيت أَن يكون جمعه وإياه الْخمر لِأَنَّهُ يغلق عَلَيْهِ بَابه وَهُوَ خمر فَإِذا صب فِيهِ الْخلّ حَتَّى لَا يغلي أَمن من ذَلِك فَإِذا غلى فقد صَار خمرًا فَكلما أفْسدهُ فَهُوَ بعد غليانه فَلَا يَأْكُلهُ وَقد قيل إِن أَبَا طَلْحَة سَأَلَ النَّبِي صلى الله عَلَيْهِ وَسلم عَن أَيْتَام فِي حجره ورثوا خمرًا أنجعلها خلا فَقَالَ لَا وَرُوِيَ عَن عمر','2','142',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (499,'قَالَ لَا تَأْكُلُوا خل خمر أفسدها أَهلهَا حَتَّى يُبْدِي الله فَسَادهَا فَذَاك حِين طَابَ الْخلّ
<span data-type="title" id=toc-497>تَفْضِيل التَّمَتُّع على مَا سواهُ من أَنْوَاع الْحَج</span>

٧١٠ - قلت الْحَج أَي ذَلِك أحب إِلَيْك الْإِفْرَاد أم الْقرَان قَالَ رُوِيَ عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه أفرد وَرُوِيَ عَنهُ أَنه قرن وَرُوِيَ عَنهُ','2','143',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (500,'أَنه خرج من الْمَدِينَة ينْتَظر الْقَضَاء وَلم يذكر لَا حجا وَلَا عمْرَة فَلَمَّا قدم مَكَّة أَمر أَصْحَابه أَن يحلوا وَقَالَ لَو اسْتقْبلت من أَمْرِي مَا اسْتَدْبَرت وَلم أسق الْهدى ولحللت كَمَا تحِلُّونَ وَهَذَا بعد أَن قدم مَكَّة وَهُوَ آخر الْأَمريْنِ مِنْهُ وَقَالَ هَذَا القَوْل وَهُوَ بِمَكَّة لَو اسْتقْبلت من أَمْرِي مَا اسْتَدْبَرت لم أسق الْهدى فَالَّذِي يخْتَار الْمُتْعَة لِأَنَّهُ آخر مَا أَمر بِهِ النَّبِي صلى الله عَلَيْهِ وَسلم وَهُوَ يجمع الْحَج وَالْعمْرَة جَمِيعًا وَيعْمل لكل وَاحِد مِنْهُمَا على حِدة','2','144',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (501,'<span data-type="title" id=toc-498>تيَمّم الْجنب وَالْحَائِض للْإِحْرَام</span>

٧١١ - قلت لأبي مَا تَقول فِي الْجنب وَالْحَائِض إِذا صَارا فِي مَوضِع الْإِحْرَام فَلم يجدا المَاء قَالَ يتيممان إِذا لم يجدا المَاء أَو حيل بَينهمَا وَبَينه
<span data-type="title" id=toc-499>من الْفَاجِر وَالْفَاسِق</span>

٧١٢ - قلت من الْفَاجِر وَالْفَاسِق من النَّاس قَالَ هَذَا كَلَام يحْتَمل مَعَاني شَتَّى','2','145',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (502,'<span data-type="title" id=toc-500>مسَائِل تتَعَلَّق بالصغار من الزواج وَالْغسْل وَالْعدة وَالْوَصِيَّة وَنَحْوهَا</span>

٧١٣ - قلت لأبي مَا تَقول فِي تَزْوِيج الْأَب الصَّغِيرَة قَالَ أما الْأَب فَيجوز تَزْوِيجه على الصَّغِيرَة وَلَا خِيَار لَهَا وَذَاكَ أَن النَّبِي صلى الله عَلَيْهِ وَسلم تزوج عَائِشَة زَوجهَا أَبُو بكر وَهِي بنت سبع فَلَا خِيَار لَهَا إِذا هِيَ أدْركْت','2','146',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (503,'وَلَيْسَ ذَلِك لغير الْأَب أَن يُزَوّج صَغِيرَة حَتَّى تبلغ تسع سِنِين لِأَن النَّبِي صلى الله عَلَيْهِ وَسلم دخل بعائشة وَهِي بنت تسع فَإِذا بلغت تسع سِنِين استؤمرت فَإِذا أَذِنت فَلَا خِيَار لَهَا وَيجب عَلَيْهَا الْغسْل فِي غشيانه إِيَّاهَا وَهِي بنت تسع إِذا كَانَ مثلهَا يُوطأ فعلَيْهَا الْغسْل وَلم يعلم النَّاس اخْتلفُوا إِذا مَاتَ عَنْهَا وَهِي صَغِيرَة لم تبلغ أَن عَلَيْهَا من الْعدة مَا على الْكَبِيرَة وَلَيْسَ ذَلِك لِمَعْنى الغشيان وَلكنه لما وَقع عَلَيْهِ أسم زَوْجَة وَجب عَلَيْهَا الْعدة وَكَذَلِكَ غشيانه إِيَّاهَا وَإِن لم تكن بلغت فعلَيْهَا الْغسْل','2','147',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (504,'وَالصَّغِير يجوز للْأَب أَن يُزَوجهُ وَلَا يضمن الْأَب الصَدَاق إِلَّا أَن يضمنهُ الْأَب فَيكون عَلَيْهِ وَالْوَصِيَّة تجوز إِذا بلغ عشر سِنِين وَأصَاب الْحق وَالْجَارِيَة أَرْجُو أَن تجوز وصيتها إِذا بلغت تسعا','2','148',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (505,'<span data-type="title" id=toc-501>حكم الصَّلَاة خلف من يشرب النَّبِيذ وَالرِّوَايَة عَنهُ</span>

٧١٤ - وَسَأَلته عَن رجل يُصَلِّي فِي مَسْجِد وَهُوَ يشرب من النَّبِيذ مَا يسكر مِنْهُ فيقيم الْمُؤَذّن وَالْإِمَام غَائِب فيتقدم هُوَ أيصلى خَلفه قَالَ إِذا كَانَ متأولا وَلم يسكر فأرجو فَإِن سكر لم يصل خَلفه قَالَ وَنحن نروي عَمَّن كَانَ يشرب
<span data-type="title" id=toc-502>الصَّلَاة خلف من يغتاب النَّاس</span>

٧١٥ - قلت مَا تَقول فِي رجل يُؤذن ويؤم قوما وَقد عرف بالغيبة حَتَّى لَا يكَاد يسلم عَلَيْهِ كثير من النَّاس يصلى خَلفه قَالَ دعها ثمَّ قَالَ لَا يحل لنا أَن نغتاب أحدا لَو كَانَ كل من عصى أَو أَتَى ذَنبا لَا يصلى خَلفه مَتى كَانَ يقوم النَّاس على هَذَا','2','149',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (506,'<span data-type="title" id=toc-503>الْمُزَارعَة بِالثُّلثِ وَالرّبع</span>

٧١٦ - قَالَ وَلَا بَأْس بالمزارعة بِالثُّلثِ وَالرّبع
<span data-type="title" id=toc-504>حكم بيع المَاء</span>

٧١٧ - وَسَأَلته عَن بيع المَاء فَقَالَ لَا أَدْرِي مَا بيع المَاء
<span data-type="title" id=toc-505>حكم مجالسة شَارِب الْخمر وَقبُول دَعوته</span>

٧١٨ - قلت مَا تَقول فِي رجل يشرب الْخمر يدعوني إِلَى غدائه وعشائه أُجِيبهُ وأجالسه قَالَ تَأمره وتنهاه فَإِذا كَانَ كَسبه كسبا طيبا وَعصى الله فِي بعض أمره يَدْعُو أَلا يُجَاب','2','150',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (507,'<span data-type="title" id=toc-506>حكم كيل المَاء بالفنجان</span>

٧١٩ - قلت مَا تَقول فِي كيل المَاء بالفنجان لأَحَدهم ثَلَاثَة وَللْآخر خمسين أَو عشْرين قَالَ لَا أَدْرِي أَي شَيْء هَذَا ثمَّ قَالَ إِن كَانَ لقوم ملك فَاصْطَلَحُوا مِنْهُ على شَيْء فَلَا بَأْس إِذا كالوا فِيمَا بَينهم
<span data-type="title" id=toc-507>الْقبْلَة للصَّائِم</span>

٧٢٠ - قلت مَا تَقول فِي الصَّائِم يقبل امْرَأَته فِي رَمَضَان قَالَ إِن كَانَ شَابًّا فخاف أَن يجرح صَوْمه فَلَا يفعل','2','151',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (508,'فَإِن فعل عَامِدًا أعَاد صَوْمه وَلَا كَفَّارَة عَلَيْهِ
<span data-type="title" id=toc-508>الْوضُوء من الْمُلَامسَة والمباشرة لشَهْوَة</span>

٧٢١ - وَقَالَ فِي الْمُلَامسَة ومباشرة الرجل امْرَأَته إِذا كَانَت لشَهْوَة أعَاد الْوضُوء','2','152',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (509,'<span data-type="title" id=toc-509>الْمسْح على النَّعْلَيْنِ والعمامة وَنقض الْوضُوء من خلعهما</span>

٧٢٢ - قلت مَا تَقول فِي حَدِيث عَليّ أَنه مسح على نَعْلَيْه ثمَّ خلعهما وَأم الْقَوْم وَلم يحدث وضُوءًا مَا مَعْنَاهُ قَالَ يرْوى هَذَا عَن عَليّ
٧٢٣ - قلت فَإِن فعل هَذَا رجل قَالَ مَا يُعجبنِي يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه قَالَ ويل لِلْأَعْقَابِ من النَّار فَإِن كَانَ أَتَى الْمسْح على الأعقاب وَغسل الرجلَيْن فَلَا بَأْس','2','153',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (510,'٧٢٤ - قَالَ وَفِي الْعِمَامَة لَا بَأْس أَن يمسح عَلَيْهَا فَإِذا خلعها خلع الْوضُوء كُله','2','154',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (511,'<span data-type="title" id=toc-510>حكم أكل الصَّيْد للْمحرمِ</span>

٧٢٥ - قلت مَا تَقول فِي أكل الصَّيْد للْمحرمِ قَالَ إِذا كَانَ يصاد لَهُ لم يَأْكُلهُ وَإِذا صيد لغيره فَلَا بَأْس أَن يَأْكُلهُ الْمحرم إِذا صيد فِي الْحل وَذبح فِي الْحل وَقد رُوِيَ عَن جَابر عَن النَّبِي صلى الله عَلَيْهِ وَسلم مَا صيد لكم فَلَا تأكلوه وَرُوِيَ عَن عُثْمَان أَنه قَالَ إِنَّمَا صيد من أَجلي فَلم نأكله وَمَا قَالَ أَبُو قَتَادَة إِنَّه قتل وَهُوَ حَلَال الصَّيْد وَلم','2','155',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (512,'يرد بِهِ النَّبِي صلى الله عَلَيْهِ وَسلم وَلَا محرما يُصِيبهُ فَأتى الصَّحَابَة وهم محرمون فَأَبَوا أَن يأكلوه حَتَّى سَأَلُوا النَّبِي صلى الله عَلَيْهِ وَسلم فَأَمرهمْ بِأَكْلِهِ','2','156',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (513,'<span data-type="title" id=toc-511>حكم التظليل للْمحرمِ وَأكل الْأَطْعِمَة الَّتِي فِيهَا طيب</span>

٧٢٦ - قلت مَا تَقول فِي التظليل للْمحرمِ وَأكل الْملح الْأَصْفَر والخشكنانج قَالَ أما الْملح فَلَا يُعجبنِي لِأَنَّهُ لم تصبه النَّار أما الخشكنانج فَلَا بَأْس والتظليل للْمحرمِ قَالَ ابْن عمر إضح لمن أَحرمت لَهُ','2','157',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (514,'فَإِن استظل بِعُود أَو مَا يُشبههُ فأرجو','2','158',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (515,'<span data-type="title" id=toc-512>تَأْوِيل حَدِيث جمع النَّبِي صلى الله عَلَيْهِ وَسلم بِالْمَدِينَةِ من غير خوف وَلَا مطر</span>

٧٢٧ - قلت حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم أَنه جمع بَين الظّهْر وَالْعصر فِي غير سفر وَلَا خوف قَالَ يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم
٧٢٨ - قلت قَوْله صليت مَعَ النَّبِي صلى الله عَلَيْهِ وَسلم سبعا جَمِيعًا وثمانيا جَمِيعًا بِالْمَدِينَةِ من','2','159',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (516,'غير خوف وَلَا مطر','2','160',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (517,'قَالَ قد جَاءَت الْأَحَادِيث بتحديد الْمَوَاقِيت لِلظهْرِ وَالْعصر وَالْمغْرب وَالْعشَاء فَأَما الْمَرِيض فأرجو
<span data-type="title" id=toc-513>الْمُؤَذّن يفرغ من أَذَانه فِي مَوْضِعه</span>

٧٢٩ - قلت الْمُؤَذّن إِذا أذن يفرغ من أَذَانه فِي مَوْضِعه أَو يتَقَدَّم','2','162',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (518,'قَالَ يفرغ من أَذَانه فِي مَكَانَهُ قَالَ قَالَ بِلَال للنَّبِي صلى الله عَلَيْهِ وَسلم لَا تسبقني بآمين
<span data-type="title" id=toc-514>حكم من نسي التَّسْلِيم ونهض</span>

٧٣٠ - قلت الرجل يُصَلِّي مَعَ الإِمَام فينهض وَقد نسي التَّسْلِيم قَالَ إِن كَانَ قد تكلم أعجب إِلَيّ أَن يُعِيد الصَّلَاة وَإِن لم يكن تكلم رَجَعَ فَسلم لِأَن تَحْلِيل الصَّلَاة التَّسْلِيم
<span data-type="title" id=toc-515>حكم صَوْم الْمغمى عَلَيْهِ</span>

٧٣١ - وَسَأَلته عَن رجل نوى الصّيام من اللَّيْل ثمَّ أُغمي عَلَيْهِ بعد طُلُوع الْفجْر فِي أول يَوْم من رَمَضَان فَقَالَ يجْزِيه صِيَام ذَلِك الْيَوْم وَيُعِيد صِيَام بَقِيَّة الشَّهْر','2','163',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (519,'<span data-type="title" id=toc-516>حكم صَوْم المتلوم يَوْم الشَّك</span>

٧٣٢ - قلت الرجل يتلوم يَوْم الشَّك يَقُول إِذا كَانَ من رَمَضَان صمت وَإِن كَانَ من غير رَمَضَان لم أَصمّ قَالَ لَيْسَ هَذَا بمجمع فِي قَول ابْن عمر وَحَفْصَة لَا صِيَام لمن لم يجمع الصّيام من اللَّيْل','2','164',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (520,'<span data-type="title" id=toc-517>حكم إِحْدَاث قَول جَدِيد فِي مَسْأَلَة إِذا اخْتلف الصَّحَابَة فِيهَا</span>

٧٣٣ - قَرَأت على أبي أَن بعض من يَقُول إِذا اخْتلف أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم فلي أَن أَقُول غير أقاويلهم ويحتج بِحَدِيث يحيى بن سعيد عَن سعيد بن الْمسيب عَن عمر فِي الأضراس فِي كل ضرس جمل وَفِي الْأَسْنَان خمس خمس وَفِي الأضراس بعير بعير وَقضى مُعَاوِيَة فِي السن بِخمْس وَفِي الأضراس بِخمْس قَالَ سعيد لَو كنت أَنا لجعلت فِي الأضراس بَعِيرَيْنِ بَعِيرَيْنِ وَفِي الْأَسْنَان خمْسا خمْسا فَخَالف ابْن الْمسيب عمر وَمُعَاوِيَة فَقَالَ أبي إِذا احْتج بِحَدِيث سعيد بن الْمسيب فقد احْتج بقول رجل من التَّابِعين على أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم وَهُوَ لَا يرى فِي قَول التَّابِعين حجَّة ثمَّ','2','165',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (521,'قَالَ أبي إِذا قَالَ لي أَن أخرج من أقاويلهم إِذا اخْتلفُوا كَمَا خرج سعيد بن الْمسيب وَقَالَ لَو كنت لقضيت خلافهم يُقَال لَهُ تَأْخُذ بقول التَّابِعين فَإِن قَالَ نعم يُقَال لَهُ تركت قَول أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم وَأخذت بقول التَّابِعين فَإِذا كَانَ لَك أَن تتْرك قَوْلهم إِذا اخْتلفُوا كَذَلِك أَيْضا تتْرك قَوْلهم إِذا اجْتَمعُوا لِأَنَّك إِذا اخْتلفُوا لم تَأْخُذ بقول وَاحِد مِنْهُم وَحَيْثُ تَقول ذَلِك فَكَذَلِك إِذا اجْتَمعُوا أَن لَا تَأْخُذ بقَوْلهمْ
<span data-type="title" id=toc-518>رِسَالَة لأبي عبد الله رَحمَه الله بِسم الله الرَّحْمَن الرَّحِيم</span>

٧٣٤ - حَدثنَا صَالح قَالَ كتب رجل إِلَى أبي يسْأَله عَن مناظرة أهل الْكَلَام وَالْجُلُوس مَعَهم فأملى عَليّ جَوَابه أحسن الله عاقبتك وَدفع عَنْك كل مَكْرُوه ومحذور الَّذِي كُنَّا نسْمع وأدركنا عَلَيْهِ من أدركنا من أهل الْعلم أَنهم كَانُوا يكْرهُونَ الْكَلَام والخوض مَعَ أهل الزيغ وَإِنَّمَا الْأَمر فِي التَّسْلِيم والانتهاء إِلَى مَا فِي كتاب الله جلّ وَعز لَا يعد ذَلِك','2','166',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (522,'وَلم يزل النَّاس يكْرهُونَ كل مُحدث من وضع كتاب أَو جُلُوس مَعَ مُبْتَدع ليورد عَلَيْهِ بعض مَا يلبس عَلَيْهِ فِي دينه فالسلامة إِن شَاءَ الله فِي ترك مجالستهم والخوض مَعَهم فِي بدعتهم وضلالتهم فليتق الله رجل وليصر إِلَى مَا يعود عَلَيْهِ نَفعه غَدا من عمل صَالح يقدمهُ لنَفسِهِ وَلَا يكون مِمَّن يحدث أمرا فَإِذا هُوَ خرج مِنْهُ أَرَادَ الْحجَّة لَهُ فَيحمل نَفسه على المحك فِيهِ وَطلب الْحجَّة لما خرج مِنْهُ بِحَق أَو بَاطِل ليزين بِهِ بدعته وَمَا أحدث وَأَشد ذَلِك أَن يكون قد وَضعه فِي كتاب فَأخذ عَنهُ فَهُوَ يُرِيد يزين ذَلِك بِالْحَقِّ وَالْبَاطِل وَإِن وضح لَهُ الْحق فِي غَيره نسْأَل الله التَّوْفِيق لنا وَلَك وَلِجَمِيعِ الْمُسلمين وَالسَّلَام عَلَيْك
<span data-type="title" id=toc-519>من تَرْجَمَة يعلي بن أُميَّة</span>

٧٣٥ - قلت لأبي يعلي بن أُميَّة هُوَ يعلي بن منية قَالَ أمه منية وَأَبوهُ أُميَّة','2','167',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (523,'<span data-type="title" id=toc-520>سُؤال عَن الصنابح الأحمسي</span>

٧٣٦ - قلت الصنابح بن الأعسر الأحمسي هُوَ عبد الله الصنَابحِي أَو','2','168',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (524,'أَبُو عبد الله عبد الرَّحْمَن الصنَابحِي قَالَ يَخْتَلِفُونَ فِيهِ قَالَ بَعضهم الصنَابحِي وَقَالَ بَعضهم الصنابح روى عَنهُ قيس بن أبي حَازِم وَأما الَّذِي روى عَنهُ عَطاء بن يسَار فَإِن مَالِكًا روى عَن زيد بن أسلم عَن عَطاء بن يسَار عَن عبد الله الصنَابحِي أَن رَسُول الله صلى الله عَلَيْهِ وَسلم قَالَ إِن الشَّمْس تطلع وَمَعَهَا قرن الشَّيْطَان','2','169',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (525,'وروى زُهَيْر بن مُحَمَّد قَالَ حَدثنَا زيد بن أسلم عَن عَطاء بن يسَار قَالَ سَمِعت الصنَابحِي يَقُول سَمِعت رَسُول الله صلى الله عَلَيْهِ وَسلم فَذكر هَذَا الحَدِيث','2','170',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (526,'٧٣٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع وَيحيى قَالَا حَدثنَا اسماعيل عَن قيس عَن الصنَابحِي وَقَالَ وَكِيع الصنَابحِي قَالَ رَسُول الله صلى الله عَلَيْهِ وَسلم أَنا فَرَطكُمْ على الْحَوْض وَقَالَ شُعْبَة الصنَابحِي وَقَالَ ابْن نمير الصنَابحِي الأحمسي وَقَالَ مجَالد الصنَابحِي وَقَالَ يزِيد بن هَارُون الصنَابحِي رجل من','2','171',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (527,'بجيلة ثمَّ أحمس
٧٣٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا سُفْيَان بن عُيَيْنَة عَن إِسْمَاعِيل أَنه سمع قيسا يَقُول سَمِعت الصنابح الأحمسي يَقُول سَمِعت رَسُول الله صلى الله عَلَيْهِ وَسلم يَقُول أَنا فَرَطكُمْ على الْحَوْض فَكَأَنَّهُ من أهل الْكُوفَة من بجيلة قَالَ أبي وَبَلغنِي عَن حَمَّاد بن زيد عَن مجَالد قَالَ رُبمَا الصنابح وَرُبمَا قَالَ الصنَابحِي','2','172',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (528,'قَالَ أبي فَأَما عباد بن عباد فَإِنَّهُ حَدثنَا عَن مجَالد عَن قيس قَالَ الصنَابحِي وَرَوَاهُ مرْثَد بن عبد الله أَبُو الْخَيْر الْيَزنِي عَن عبد الرَّحْمَن بن عسيلة الصنَابحِي قَالَ رَأَيْت أَبَا بكر مسح على الْخمار
<span data-type="title" id=toc-521>أَوْقَات الصَّلَاة</span>

٧٣٩ - قَالَ أبي إِذا طلعت الشَّمْس فارتفعت قيد رمح أَو رُمْحَيْنِ فَالصَّلَاة مَقْبُولَة حَتَّى يقوم قَائِم الظهيرة وَذَلِكَ قبل الزَّوَال فَإِذا قَارب الزَّوَال فَأمْسك عَن الصَّلَاة حَتَّى تَزُول الشَّمْس فَإِذا زَالَت فَهُوَ وَقت الظّهْر فصل مَا بدا لَك حَتَّى يدْخل وَقت الْعَصْر إِذا صَار ظلّ كل شَيْء مثله من حِين تَزُول إِلَى أَن يصير ظلّ كل شَيْء مثلَيْهِ أَو تصفر','2','173',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (529,'الشَّمْس فَهُوَ آخر وَقت الْعَصْر فصل مَا بَين ذَلِك فَإِذا صليت الْعَصْر فَلَا تصل حَتَّى تغرب الشَّمْس وَهُوَ وَقت الْمغرب إِلَى أَن يغيب الشَّفق فصل مَا بدا لَك فَإِذا غَابَ الشَّفق فَهُوَ وَقت الْعشَاء الْآخِرَة فَإِذا صليت الْعشَاء الْآخِرَة فآخر وَقتهَا مِنْهُم من يَقُول إِلَى ثلث اللَّيْل وَمِنْهُم من يَقُول إِلَى نصف اللَّيْل فَإِذا صليت الْعشَاء فتطوع مَا بدا لَك إِلَى أَن يطلع الْفجْر فَإِذا طلع وَاعْترض فَهُوَ وَقت صَلَاة الْفجْر فَإِذا صليت الْفجْر فَلَا تطوع بِشَيْء حَتَّى تطلع الشَّمْس وَتَكون قيد رمح أَو رُمْحَيْنِ
<span data-type="title" id=toc-522>إحراق مُحَمَّد بن مسلمة بَاب سعد بن أبي وَقاص بِأَمْر أَمِير الْمُؤمنِينَ عمر</span>

٧٤٠ - أملي عَليّ أبي فَقَرَأته عَلَيْهِ قَالَ حَدثنَا إِسْمَاعِيل قَالَ أخبرنَا أَبُو حَيَّان','2','174',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (530,'عَن عَبَايَة بن رَافع بن خديج قَالَ بلغ عمر أَن سَعْدا اتخذ بَابا ثمَّ قَالَ انْقَطع الصويت فَبعث إِلَيّ مُحَمَّد بن مسلمة فَأَتَاهُ فَقَالَ انْطلق فَحرق بَاب سعد الَّذِي اتَّخذهُ ثمَّ خُذْهُ بِيَدِك فَأخْرجهُ إِلَى النَّاس فَقل هَاهُنَا فَاقْعُدْ النَّاس فَبعث غُلَامه بِهِ مَكَانَهُ إِلَى أَهله فَأمره أَن يَأْتِيهِ براحلتين وَزَاد من أَهله وَانْطَلق يمشي قبل','2','175',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (531,'الْكُوفَة حَتَّى أدْركهُ غُلَامه بسويق وعجوة من عَجْوَة الْمَدِينَة فَسَار حَتَّى قدم جباية الْكُوفَة فَرَأى نبطيا يدْخل الْكُوفَة بقصب لَهُ على حمَار لَهُ يَبِيعهُ فابتاعه مِنْهُ وَاشْترط عَلَيْهِ أَن يلقيه عِنْد بَاب الْأَمِير فجَاء حَتَّى أَلْقَاهُ عِنْد بَاب الْأَمِير وأورى زنده فَأتي سعد فَأخْبر فَقيل لَهُ إِن رجلا أسود طَويلا عَظِيما بَين إِزَار ورداء عَلَيْهِ عِمَامَة خرقانية على غير قلنسية فَقَالَ ذَاك مُحَمَّد بن مسلمة دَعوه حَتَّى يبلغ حَاجته لَا يعرض لَهُ إِنْسَان بِشَيْء فَحرق الْبَاب حَتَّى صَار فحما ثمَّ خرج إِلَيْهِ سعد فساءله ثمَّ حلفه بِاللَّه الَّذِي لَا إِلَه غَيره مَا تكلم بِالْكَلِمَةِ الَّتِي','2','176',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (532,'بلغت أَمِير الْمُؤمنِينَ وَلَقَد بلغه كَاذِب ثمَّ عرض عَلَيْهِ الْمنزل ليدْخل فَأبى وأنصرف مَكَانَهُ رَاجعا وَأتبعهُ سعد بزاد فَرده مَعَ رَسُوله وَقَالَ لَهُ أرجع بطعامك إِلَى صَاحبك فَإِن لَهُ عيالا وَإِن مَعنا فضلَة من زَاد فَرجع هُوَ وَغُلَامه حَتَّى انْقَضى زادهما وأرملا أَيَّامًا حَتَّى أكلا من الشّجر ثمَّ كَانَ أول من أدْركَا من الْإِنْس امْرَأَة فِي غنم فَقَامَ مُحَمَّد يُصَلِّي وَانْطَلق الْغُلَام إِلَى الْغنم حَتَّى بَايع صَاحِبَة الْغنم بِشَاة صَغِيرَة من غنمها بعصابة كَانَت عَلَيْهِ فصرعها يُرِيد أَن يذبحها وَمُحَمّد قَائِم يُصَلِّي فَأَشَارَ إِلَيْهِ أَن لَا يذبحها فأنصرف فَسَأَلَهُ عَن حَدِيثهَا فحدثه فَقَالَ أرجع بِالشَّاة فَإِن كَانَ فِي الْغنم صَاحبهَا فَبَايعهُ أَو سلم بيع الْأمة فاقبل بهَا وَإِن كَانَت إِنَّمَا هِيَ راعية غنم فَردهَا وَأَقْبل فَإِن الْجُوع خير من مأكل السوء فَأقبل فَإِذا هِيَ راعية فَأقبل بعصابته ورد الشَّاة ثمَّ صَار حَتَّى قدم على عمر فحدثه بِمَا لَقِي فِي الطَّرِيق من الْجُوع وَالَّذِي أتبعه بِهِ سعد فَرده إِلَيْهِ فَقَالَ عمر فَمَا مَنعك أَن تقبل مِنْهُ قَالَ أَنْت يَا أَمِير الْمُؤمنِينَ قَالَ أَنا قد رَأَيْت مَكَان ذَاك','2','177',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (533,'<span data-type="title" id=toc-523>النَّهْي عَن الْعَبَث باللحية فِي الصَّلَاة</span>

٧٤١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا سعيد بن خثيم قَالَ حَدثنَا مُحَمَّد بن خَالِد عَن سعيد بن جُبَير قَالَ نظر سعيد إِلَى رجل وَهُوَ قَائِم فِي الصَّلَاة قَالَ وَهُوَ يعبث بلحيته فَقَالَ سعيد لَو خشع قلب هَذَا لخشعت جوارحه
<span data-type="title" id=toc-524>جَوَاز الاكتناء لمن لَيْسَ لَهُ ولد</span>

٧٤٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا سعيد بن خثيم قَالَ حَدثنَا معمر بن خثيم الْهِلَالِي عَن أبي جَعْفَر مُحَمَّد بن عَليّ قَالَ قَالَ لي أَبُو جَعْفَر يَا معمر مَا تكنى قَالَ قلت مَا اكتنيت وَمَالِي من ولد وَلَا امْرَأَة وَلَا جَارِيَة قَالَ وَمَا يمنعك من ذَلِك قَالَ قلت أما إِن أبي يجْهد عَليّ أَن يزوجني أَو يبْتَاع لي جَارِيَة فأستحي من ذَلِك قَالَ فَمَا','2','178',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (534,'يمنعك أَن تكتني قَالَ قلت حَدِيث بَلغنِي عَن عَليّ رَضِي الله عَنهُ قَالَ وَمَا هُوَ قلت بلغنَا أَن عليا رَضِي الله عَنهُ قَالَ من اكتنى وَلَيْسَ لَهُ ولد فَهُوَ أَبُو جعر فَقَالَ أَبُو جَعْفَر لَيْسَ هَذَا من حَدِيث عَليّ إِنَّا لنكني أَوْلَادنَا فِي صغرهم مَخَافَة الشرار يلْحق بهم أَنا أكنيك قَالَ قلت أقبل قَالَ أَنْت أَبُو مُحَمَّد','2','179',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (535,'<span data-type="title" id=toc-525>مَسْأَلَة هَامة فِي الْإِيلَاء</span>

٧٤٣ - قَالَ قَالَ قَرَأت على أبي قلت الرجل يحلف أَن لَا يقرب أَهله سنة أَو أَكثر من أَرْبَعَة أشهر فَمن النَّاس من يَقُول يُوقف بعض مُضِيّ الْأَرْبَعَة فإمَّا أَن يَفِي وَإِمَّا أَن يُطلق وَقَالَ بعض النَّاس إِذا مَضَت أَرْبَعَة أشهر بَانَتْ مِنْهُ بِوَاحِدَة وَقَالَ بعض النَّاس هِيَ تَطْلِيقَة وَلَيْسَت بَائِنا وَقَالَ بعض النَّاس إِذا آلى دون الْأَرْبَعَة لم يكن إِيلَاء','2','180',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (536,'وَقَالَ بعض النَّاس هُوَ إِيلَاء إِذا مَضَت أَرْبَعَة أشهر وَإِذا قَالَ وَالله لَا أقْربك فِي هَذِه الدَّار سنة لم يكن ذَلِك إِيلَاء لِأَنَّهُ إِن شَاءَ جَامعهَا فِي غير تِلْكَ الدَّار وَقَالَ بعض النَّاس تَعْتَد بعد ماتبين عدَّة الْمُطلقَة وَذَلِكَ بعد مُضِيّ الْأَرْبَعَة أشهر وَقَالَ بعض النَّاس إِذا مَضَت الْأَرْبَعَة أشهر تزوجت إِن شَاءَت وَلَيْسَ عَلَيْهَا عدَّة بعد مُضِيّ الْأَرْبَعَة أشهر وَرُوِيَ ذَلِك عَن ابْن عَبَّاس قَالَ لَا تطولوا عَلَيْهَا إِذا مَضَت الْأَرْبَعَة أشهر فَلَا عدَّة عَلَيْهَا','2','181',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (537,'قَالَ أبي أَنا أَقُول إِذا انْقَضتْ أَرْبَعَة أشهر وَقد حلف أَن لَا يَغْشَاهَا أَكثر من أَرْبَعَة أشهر فَجَاءَت تطالبه بعد مُضِيّ الْأَرْبَعَة أشهر وقف لَهَا فإمَّا أَن يَفِي وَإِمَّا أَن يُطلق وَلَا يكون طَلَاقا حَتَّى يُوقف وَإِن طَال ذَلِك فمضت عَلَيْهِ سنة أَو أَكثر لم يكن طَلَاق حَتَّى يُطلق فَإِن طلق اعْتدت عدَّة الْمُطلقَة إِن كَانَت مِمَّن تحيض فَثَلَاث حيض وَإِن كَانَت مِمَّن لَا تحيض فَثَلَاثَة أشهر وَالْوَقْف أشبه بِمَعْنى الْكتاب لقَوْله الله تبَارك وَتَعَالَى {للَّذين يؤلون من نِسَائِهِم} يَقُول يقسمون {تربص أَرْبَعَة أشهر فَإِن فاؤوا} فَكَانَ الْفَيْء بعد مُضِيّ الْأَرْبَعَة وَقَالَ {وَإِن عزموا الطَّلَاق} فَجعل الْفَيْء بعد والعزم بِهِ بعد مُضِيّ الْأَرْبَعَة فَلَا يكون طَلَاقا إِلَّا بِالزَّوْجِ لِأَنَّهُ قَالَ {فَإِن فاؤوا فَإِن الله غَفُور رَحِيم وَإِن عزموا الطَّلَاق} فهما أَمْرَانِ جعلا بِهِ وَلَا','2','182',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (538,'يكون ذَلِك بِمُضِيِّ الشُّهُور وَلَيْسَ لَهُ أَن يعضلها إِذا وقف إِمَّا أَن يفِيء وَإِمَّا أَن يُطلق يعضلها لَا يَطَأهَا وَكَانَ يدْخل على عَائِشَة رجل فَكَانَت تَقول أما آن لَك أَن تفي
<span data-type="title" id=toc-526>الْمحرم فِي أَيَّام التَّشْرِيق يبْدَأ بِالتَّكْبِيرِ أَو بِالتَّلْبِيَةِ</span>

٧٤٤ - قلت الْمحرم فِي أَيَّام التَّشْرِيق يبْدَأ بِالتَّكْبِيرِ أَو بِالتَّلْبِيَةِ قَالَ يبْدَأ بِالتَّكْبِيرِ يَوْم عَرَفَة إِلَى آخر أَيَّام التَّشْرِيق يكبر فِي الْعَصْر وَيقطع وَهُوَ قَول عَليّ وَذَلِكَ فِي الْأَمْصَار وَقد يَقُول بعض النَّاس','2','183',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (539,'إِنَّمَا يكبر النَّاس بمنى إِذا رموا الْجَمْرَة فَإِذا ترك التَّلْبِيَة بَدَأَ فِي الظّهْر من يَوْم النَّحْر لَا يجْتَمع التَّكْبِير والتلبية لِأَنَّهُ إِذا رمى الْجَمْرَة يَوْم النَّحْر فقد انْقَضتْ التَّلْبِيَة فَيبْدَأ بِالتَّكْبِيرِ فِي الظّهْر من يَوْم النَّحْر
<span data-type="title" id=toc-527>عدَّة أم الْوَلَد</span>

٧٤٥ - وَسَأَلته كم عدَّة أم الْوَلَد إِذا توفى عَنْهَا مَوْلَاهَا أَو أعْتقهَا فَقَالَ عدتهَا حَيْضَة وَإِنَّمَا هِيَ أمة فِي كل أحوالها إِن جنت فعلى سَيِّدهَا قيمتهَا وَإِن جنى عَلَيْهَا فعلى الْجَانِي مَا نَقصهَا من قيمتهَا وَإِن مَاتَت فَمَا تركت من شَيْء فلسيدها وَإِن أَصَابَت حدا فحدها حد الْأمة وَإِن زَوجهَا سَيِّدهَا فَمَا ولدت فَهُوَ بمنزلتها يعتقون بِعتْقِهَا ويرقون برقها','2','184',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (540,'وَقد اخْتلف النَّاس فِي عدتهَا فَقَالَ بعض النَّاس أَرْبَعَة أشهر وَعشرا فَهَذِهِ عدَّة الْحرَّة وانما هِيَ أمة خرجت من الرّقّ إِلَى الْحُرِّيَّة فَلَزِمَ من قَالَ أَرْبَعَة أشهر وَعشرا أَن يُورثهَا وَأَن يَجْعَل أَحْكَامهَا أَحْكَام الْحرَّة لِأَنَّهُ قد أَقَامَهَا فِي الْعدة مقَام الْحرَّة وَقَالَ بعض النَّاس عدتهَا ثَلَاثَة حيض وَهَذَا قَول لَيْسَ لَهُ وَجه إِنَّمَا تَعْتَد ثَلَاث حيض الْمُطلقَة وَلَيْسَت بمطلقة وَإِنَّمَا ذكر الله الْعدة للزَّوْج فَقَالَ {وَالَّذين يتوفون مِنْكُم ويذرون أَزْوَاجًا يَتَرَبَّصْنَ بِأَنْفُسِهِنَّ أَرْبَعَة أشهر وَعشرا} وَلَيْسَت أم الْوَلَد بحرة وَلَا زَوْجَة فَتعْتَد أَرْبَعَة أشهر وَعشرا وَقَالَ {والمطلقات يَتَرَبَّصْنَ بِأَنْفُسِهِنَّ ثَلَاثَة قُرُوء}','2','185',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (541,'وَلَيْسَت أم الْوَلَد بمطلقة فتتربص ثَلَاثَة قُرُوء وَإِنَّمَا هِيَ أمة خرجت من الرّقّ إِلَى الْحُرِّيَّة
<span data-type="title" id=toc-528>من أَحْكَام أهل الذِّمَّة</span>

٧٤٦ - قلت رجل يَهُودِيّ ادّعى على رجل مُسلم أَنه أهراق خمرة قَالَ لَيْسَ للخمر ثمن نهى النَّبِي صلى الله عَلَيْهِ وَسلم عَن ثمن الْخمر
٧٤٧ - قلت فَإِنَّهُ أدعى أَنه شربهَا قَالَ لَا أَقْْضِي عَلَيْهِ فِيهَا بِشَيْء وَلَو أَقَامَ الْبَيِّنَة لم أقض على الْمُسلم بِشَيْء وَإِن أهراقها لم أقض عَلَيْهِ بِشَيْء وَلَيْسَ لَهُ أَن يظْهر الْخمر وَلَكِن يمْنَع الْمُسلمُونَ من أذاهم وَأَن يفسدوا لَهُم شَيْئا فَإِن أتلفوا لَهُم شَيْئا من غير مَا حرم الله ضمنُوا الْقيمَة كَأَنَّهُ كسر إِنَاء فِيهِ خمر','2','186',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (542,'فَيضمن الْإِنَاء وَلَا يضمن الْخمر وَلَيْسَ للْيَهُود وَالنَّصَارَى أَن يحدثوا فِي مصر مصره الْمُسلمُونَ بيعَة وَلَا كَنِيسَة وَلَا يضْربُوا بناقوس إِلَّا فِيمَا كَانَ لَهُم صلح وَلَيْسَ لَهُم أَن','2','187',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (543,'يظهروا الْخمر فِي أَمْصَار الْمُسلمين وَلَيْسَ لَهُم أَن يشتروا مِمَّا سبى الْمُسلمُونَ يمْنَعُونَ من ذَلِك لأَنهم إِذا صَارُوا إِلَيْهِم يثبتوا على كفرهم وَيُقَال إِن عمر كَانَ يَأْمر فِي عَهده لأهل الشَّام أَن يمنعوا من شِرَاء مَا سبى الْمُسلمُونَ','2','188',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (544,'<span data-type="title" id=toc-529>حكم من مرض فِي رَمَضَان وَاسْتمرّ بِهِ الْمَرَض حَتَّى مَاتَ</span>

٧٤٨ - قلت رجل مرض فِي رَمَضَان ثمَّ اسْتمرّ بِهِ الْمَرَض حَتَّى مَاتَ قَالَ لَيْسَ عَلَيْهِ شَيْء لِأَنَّهُ كَانَ فِي عذر إِلَّا أَن يكون صَحَّ فيطعم عَنهُ لكل يَوْم مِسْكين مُدبر وَالْمدّ رَطْل وَثلث حِنْطَة فَإِن كَانَ نذرا صَامَ عَنهُ وليه إِذا مَاتَ يُقَال أَن النَّبِي صلى الله عَلَيْهِ وَسلم أَمر أَن يصام عَن النّذر وَكَذَا يرْوى عَن ابْن عَبَّاس وَيطْعم عَنهُ إِذا كَانَ قد فرط فِي ذَلِك بِقدر الْأَيَّام الَّتِي فرط كَأَنَّهُ مرض شهر رَمَضَان فبرأ مِنْهُ خَمْسَة عشر يَوْمًا','2','189',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (545,'<span data-type="title" id=toc-530>إِذا لم يقْرَأ الْأَمَام فِي الصَّلَاة</span>

٧٤٩ - سَأَلت أبي عَن الإِمَام إِذا لم يقْرَأ قَالَ يُعِيد وَيُعِيد من خَلفه
٧٥٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو مُعَاوِيَة قَالَ حَدثنَا الْأَعْمَش عَن إِبْرَاهِيم عَن همام أَن عمر صلى الْمغرب فَلم يقْرَأ فَلَمَّا انْصَرف قَالُوا يَا أَمِير الْمُؤمنِينَ إِنَّك لم تقْرَأ قَالَ إِنِّي حدثت نَفسِي وَأَنا فِي الصَّلَاة بعير جهزتها من الْمَدِينَة حَتَّى دخلت الشَّام ثمَّ أعَاد وَأعَاد الْقِرَاءَة','2','190',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (546,'٧٥١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا معمر بن سُلَيْمَان الرقي قَالَ حَدثنَا الْحجَّاج عَن الحكم بن عتيبة عَن النَّخعِيّ عَن همام بن الْحَارِث عَن عَطاء عَن عبيد بن عُمَيْر أَن عمر بن الْخطاب صلى بِالنَّاسِ الْفجْر قَالَ وَمن النَّاس من يَقُول هِيَ صَلَاة الْمغرب فَلم يقْرَأ فَلَمَّا انْصَرف قَالَ النَّاس يَا أَمِير الْمُؤمنِينَ إِنَّك لم تقْرَأ قَالَ إِنِّي','2','191',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (547,'جهزت عيرًا ثمَّ نزلتها منزلا منزلا حَتَّى أتيت الشَّام ثمَّ قَالَ للمؤذن أقِم فَأَعَادَ الصَّلَاة
٧٥٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن أبي عدي عَن ابْن عون عَن الشّعبِيّ قَالَ قَالَ الْأَشْعَرِيّ صلى بِنَا عمر فَدخل وَلم يقْرَأ فاتبعته واتبعته حَتَّى أتيت الْأَطْنَاب فَقلت يَا أَمِير الْمُؤمنِينَ إِنَّك لم تقْرَأ قَالَ مَا قَرَأت شَيْئا قلت مَا قَرَأت شَيْئا قَالَ لقد رَأَيْتنِي أجهز عيرًا بِكَذَا وأفعل كَذَا قَالَ فَأمر المؤذنين فأذنوا وَأَقَامُوا وَأعَاد بِنَا الصَّلَاة
٧٥٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا معَاذ بن معَاذ قَالَ حَدثنَا ابْن عون عَن الشّعبِيّ عَن أبي مُوسَى الْأَشْعَرِيّ قَالَ صلى بِنَا عمر بن الْخطاب الْمغرب فَدخل وَلم يقْرَأ شَيْئا فَذكر مثله','2','192',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (548,'٧٥٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن أبي زَائِدَة قَالَ حَدثنَا عبد الله بن عون عَن عَامر قَالَ قَالَ أَبُو مُوسَى صلى عمر الْمغرب بِالنَّاسِ فَلم يقْرَأ فِيهَا شَيْئا فَذكر مَعْنَاهُ
٧٥٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ حَدثنَا يُونُس بن أبي إِسْحَاق عَن الشّعبِيّ أَن عمر بن الْخطاب صلى الْمغرب فنسي أَن يقْرَأ فَأَعَادَ الصَّلَاة وَقَالَ لَا صَلَاة إِلَّا بِقِرَاءَة
٧٥٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ نَا سُفْيَان عَن جَابر عَن عَامر عَن زِيَاد بن عِيَاض الْأَشْعَرِيّ أَنه أَمر المؤذنين يَعْنِي عمر فأذنوا وَأعَاد الصَّلَاة','2','193',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (549,'٧٥٧ - حَدثنَا صَالح قَالَ حَدثنَا أبي قَالَ حَدثنَا سلم بن قُتَيْبَة قَالَ حَدثنَا يُونُس عَن الشّعبِيّ عَن زِيَاد بن عِيَاض الْأَشْعَرِيّ أَن عمر صلى بهم الْمغرب فَلم يقْرَأ شَيْئا فَقَالَ لَهُ أَبُو مُوسَى يَا أَمِير الْمُؤمنِينَ مَا قَرَأت شَيْئا فَأقبل على عبد الرَّحْمَن بن عَوْف فَقَالَ مَا يَقُول قَالَ صدق فَأمر مؤذنه فَأَقَامَ وَلم يُؤذن فَلَمَّا فرغ من صلَاته قَالَ لَا صَلَاة لَيست فِيهَا قِرَاءَة إِنَّمَا شغلني عَن الصَّلَاة عير جهزتها إِلَى الشَّام فَجعلت أفكر فِي أحلاسها وأقتابها','2','194',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (550,'<span data-type="title" id=toc-531>مَا نقص من الرَّهْن أَو تلف فعلى الرَّاهِن وَمَا زَاد فَلهُ</span>

٧٥٨ - سَأَلت أبي عَن رجل رهن عبدا عِنْد رجل على ألف دِرْهَم فَمَرض العَبْد عِنْد الْمُرْتَهن وَصَارَ يُسَاوِي مائَة دِرْهَم قَالَ للْمُرْتَهن حَقه كَامِلا لِأَن الْملك ملك الرَّاهِن
٧٥٩ - قلت لَو أَن رجلا رهن عِنْد رجل رهنا على عشرَة دَرَاهِم وَالرَّهْن يُسَاوِي مائَة فَضَاعَ الرَّهْن قَالَ أذهب إِلَى مَا يرْوى عَن الزُّهْرِيّ عَن سعيد بن الْمسيب أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ لَا يغلق الرَّهْن هُوَ من ربه لَهُ غنمه وَعَلِيهِ غرمه قَالَ أبي إِن زَاد فَهُوَ لَهُ وَإِن نقص فَعَلَيهِ','2','195',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (551,'<span data-type="title" id=toc-532>من اغتصب جَارِيَة ثمَّ وَطئهَا بعد زواج غير صَحِيح</span>

٧٦٠ - سَأَلته ن رجل أغتصب جَارِيَة بكرا لَهَا أَب وإخوة فَقَالَ لَهَا إجعلي أَمرك إِلَيّ حَتَّى أتزوجك فَخرج ثمَّ دخل إِلَيْهَا فَقَالَ قد تَزَوَّجتك وأشهدت وَلم يدْخل عَلَيْهَا شُهُودًا ثمَّ وَطئهَا قَالَ أرى أَن يفرق بَينهمَا وَيضْرب وينكل بِهِ وَيُطَاف بِهِ
<span data-type="title" id=toc-533>بيع الدّين بِالدّينِ</span>

٧٦١ - سَأَلت أبي عَن رجل كَانَ لَهُ على رجل ألف دِرْهَم فَأعْطَاهُ أَرْبَعِينَ دِينَارا وَمضى ثمَّ أَنه عَاد بعد ذَلِك فَقَالَ تِلْكَ الْأَرْبَعين دِينَارا بِالْألف قَالَ لَا يجوز حَتَّى يحضر أَحدهمَا
<span data-type="title" id=toc-534>من كَانَ عِنْده مِائَتَا دِرْهَم فَلم يزكها حَتَّى حَال عَلَيْهِ حول آخر</span>

٧٦٢ - سَأَلت أبي عَن رجل كَانَت عِنْده مِائَتَا دِرْهَم فَلم يزكها فحال عَلَيْهِ حول آخر فَقَالَ يزكيها للعام الَّذِي مضى لِأَنَّهَا هَذِه السّنة تصير مِائَتَيْنِ غير خَمْسَة','2','196',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (552,'<span data-type="title" id=toc-535>من حلف لَا يَأْكُل لَحْمًا فَأكل سمكًا طريا</span>

٧٦٣ - سَأَلته عَن رجل حلف أَن لَا يَأْكُل لَحْمًا فَأكل سمكًا طريا فَقَالَ يكون ذَلِك عِنْدِي على قدر نِيَّته
<span data-type="title" id=toc-536>إِذا أخرجت الْمُتَوفَّى عَنْهَا زَوجهَا من الْبَيْت الَّذِي هِيَ فِيهِ</span>

٧٦٤ - سَأَلت أبي عَن الْمُتَوفَّى عَنْهَا زَوجهَا إِذا أخرجت من الدَّار الَّتِي هِيَ فِيهَا قَالَ إِذا أخرجت فَمَا تصنع','2','197',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (553,'٧٦٥ - قلت فتبيت فِي بَيت أمهَا أَو أُخْتهَا قَالَ لَا تبيت فِي الْبَيْت الَّذِي صَارَت إِلَيْهِ وَلها طرفِي النَّهَار
<span data-type="title" id=toc-537>من أغمى عَلَيْهِ يَوْم عَرَفَة</span>

٧٦٦ - وَسَأَلته عَن الرجل يغمى عَلَيْهِ يَوْم عَرَفَة حَتَّى يدْفع الإِمَام قَالَ أَخَاف قد يكون فسد حجه
<span data-type="title" id=toc-538>من فَاتَهُ الْمبيت بِمُزْدَلِفَة</span>

٧٦٧ - سَأَلته عَن رجل فَاتَهُ الْوُقُوف بِجمع وَقد وقف بِعَرَفَة وَمر بِجمع بعد طُلُوع الشَّمْس قَالَ عَلَيْهِ دم
<span data-type="title" id=toc-539>آخر الْجُزْء الْحَادِي عشر من أَجزَاء صَالح</span>','2','198',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (554,'<span data-type="title" id=toc-540>رِوَايَتَانِ فِي نِسْيَان عمر الْقِرَاءَة فِي صَلَاة الْمغرب</span>

٧٦٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ حَدثنَا عِكْرِمَة بن عمار عَن ضَمْضَم بن جوش الهفاني عَن عبد الله بن حَنْظَلَة بن الراهب قَالَ صلى بِنَا عمر الْمغرب فنسي أَن يقْرَأ فِي الرَّكْعَة الأولى فَلَمَّا قَامَ فِي الثَّانِيَة قَرَأَ بِفَاتِحَة الْكتاب مرَّتَيْنِ وسورتين فَلَمَّا قضى الصَّلَاة سجد سَجْدَتَيْنِ وَقَالَ معَاذ الهزاني قَالَ أبي وَإِنَّمَا هُوَ الهفاني','2','199',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (555,'٧٦٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد عَن عبد الله قَالَ أَخْبرنِي مُحَمَّد بن إِبْرَاهِيم عَن أبي سَلمَة قَالَ صلى عمر الْمغرب فَلم يقْرَأ فِي الرَّكْعَتَيْنِ شَيْئا فَقيل لَهُ فَقَالَ كَيفَ كَانَ الرُّكُوع وَالسُّجُود قَالُوا حسن قَالَ فَلَا بَأْس إِنِّي جهزت عيرًا بأحقابها وأقتابها ومنازلها','2','200',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (556,'قَالَ أبي أَبُو سَلمَة لم يدْرك عمر وَتلك أثبت قَالُوا صلى بِنَا عمر
<span data-type="title" id=toc-541>الصَّلَاة فِي أعطان الْإِبِل</span>

٧٧٠ - وَسَأَلته عَمَّن صلى فِي أعطان الْإِبِل يُعِيد قَالَ نعم يُعِيد إِذا صلى فِي الْموضع الَّذِي تأوي إِلَيْهِ
<span data-type="title" id=toc-542>الرَّد على من قَالَ بِوُجُوب الْقِرَاءَة خلف الإِمَام</span>

٧٧١ - قَالَ أبي يدْخل على من قَالَ يُعِيد من لم يقْرَأ لَو أَنه أدْرك الإِمَام رَاكِعا وَلم يقْرَأ وَركع أَنه لَا تجزيه صلَاته لِأَنَّهُ لم يقْرَأ خلف الإِمَام وَلَو أَن الإِمَام لم يقْرَأ فَهُوَ يزْعم أَنه إِذا صلى خَلفه أَجْزَأته فَكَأَن صلَاته تجزيه وَإِن لم يقْرَأ الإِمَام
<span data-type="title" id=toc-543>الْمغمى عَلَيْهِ يقْضِي الصَّلَوَات كلهَا</span>

٧٧٢ - قلت الْمغمى عَلَيْهِ كم يُعِيد قَالَ يُعِيد الصَّلَاة كلهَا','2','201',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (557,'٧٧٣ - قلت فَإِن ابْن عمر أُغمي عَلَيْهِ أَكثر من لَيْلَة فَلم يعد الصَّلَاة قَالَ وَرُوِيَ عَن عمار أَنه أُغمي عَلَيْهِ ثَلَاثًا فَقضى وَرُوِيَ عَن عمرَان بن حُصَيْن وَسمرَة بن جُنْدُب أَنه يُعِيد قَالَ سَمُرَة مَعَ كل صَلَاة صَلَاة يَقُول مَعَ الظّهْر الظّهْر وَمَعَ الْعَصْر الْعَصْر قَالَ عمرَان بل يعيدهن جَمِيعًا فَمن ذهب إِلَى حَدِيث ابْن عمر يَقُول إِن الْقَلَم عَنهُ مَرْفُوع فَلَا يُعِيد شَيْئا فَأَما من قَالَ خمس صلوَات فَلَا نعلم لَهُ','2','202',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (558,'معنى إِمَّا أَن لَا يُعِيد وَإِمَّا يُعِيد الصَّلَوَات كُلهنَّ ويروى عَن إِبْرَاهِيم النَّخعِيّ أَنه يُعِيد خمس صلوَات
<span data-type="title" id=toc-544>حكم لبس مَا سداه حَرِير وَلحمَته قطن</span>

٧٤٤ - قلت الثَّوْب فِيهِ حَرِير سداه وَلحمَته قطن قَالَ هَذَا شَبيه بالخز قَالَ ابْن عَبَّاس نهى النَّبِي صلى الله عَلَيْهِ وَسلم عَن المصمت من الْحَرِير وَقد لبس عدد من الصَّحَابَة أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم الْخَزّ','2','203',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (559,'<span data-type="title" id=toc-545>غسل دم الْحيض</span>

٧٧٥ - قلت مَا تَقول فِي غسل دم الْحيض وَمَا قَالَ النَّبِي صلى الله عَلَيْهِ وَسلم أغسليه بِمَاء وَسدر قَالَ إِذا أنقى وَإِنَّمَا أَرَادَ الإنقاء وَقَالَ فِي بعض الحَدِيث قَالَ لَهَا النَّبِي صلى الله عَلَيْهِ وَسلم أجعلي فِيهِ ملحا وَكَانَ ابْن عمر إِذا لم يذهب أثر الدَّم قرضه بالمقراض','2','204',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (560,'<span data-type="title" id=toc-546>حكم وضع الْيُمْنَى على الْيُسْرَى بعد الرُّكُوع</span>

٧٧٦ - قلت كَيفَ يضع الرجل يَده بعد مَا يرفع رَأسه من الرُّكُوع أيضع الْيُمْنَى على الشمَال أم يسدلها قَالَ أَرْجُو أَن لَا يضيق ذَلِك إِن شَاءَ الله
<span data-type="title" id=toc-547>حكم الْقِرَاءَة فِي الْفَرِيضَة بِسُورَة فِيهَا سَجْدَة</span>

٧٧٧ - هَل يجوز للرجل أَن يقْرَأ فِي الْفَرِيضَة بِسُورَة فِيهَا سَجْدَة وَهُوَ إِمَام غير يَوْم جُمُعَة أيسجد قَالَ أَرْجُو أَن لَا يكون بِهِ بَأْس','2','205',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (561,'<span data-type="title" id=toc-548>حكم الْخِتَان يَوْم السَّابِع</span>

٧٧٨ - قلت يختن الصَّبِي لسبعة أَيَّام قَالَ يرْوى عَن الْحسن أَنه قَالَ هُوَ فعل الْيَهُود وَسُئِلَ وهب بن مُنَبّه عَن ذَلِك فَقَالَ إِنَّمَا يسْتَحبّ ذَلِك فِي يَوْم السَّابِع لخفته على الصّبيان فَإِن الْمَوْلُود يُولد وَهُوَ خدر الْجَسَد كُله لَا يجد ألم مَا أَصَابَهُ سبعا فَإِذا لم يختن لذَلِك فَدَعوهُ حَتَّى يقوى','2','206',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (562,'<span data-type="title" id=toc-549>من خلع النَّعْلَيْنِ بعد الْمسْح عَلَيْهِمَا مَعَ الجوربين</span>

٧٧٩ - قلت من مسح على جوربه وَنَعله وَنِيَّته الْمسْح على الجوربين أَيجوزُ لَهُ أَن يخلع النَّعْلَيْنِ وَيُصلي قَالَ إِن كَانَ مسح على النَّعْلَيْنِ مَعَ الجوربين ثمَّ خلع نَعْلَيْه يُعِيد الْوضُوء كُله وَإِن كَانَ مسح على الجوربين وَلبس نَعْلَيْه وَلم يمسح على النَّعْلَيْنِ ثمَّ خلعهما فَلَا بَأْس
<span data-type="title" id=toc-550>حكم لبس النَّعْل السندي والبطيطات الْحمر والتختم فِي الْيَسَار</span>

٧٨٠ - قلت لبس النَّعْل السندي قَالَ إِذا كَانَ للْوُضُوء فأرجو وَأما للزِّينَة فأكره للرجل وَالنِّسَاء','2','207',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (563,'سُئِلَ عَنهُ بعض أهل الْعلم فَقَالَ سنة رَسُول الله صلى الله عَلَيْهِ وَسلم أحب إِلَيْنَا من سنة الأكهر
٧٨١ - وَقَالَ أبي وَيكرهُ لبس البطيطات الْحمر
٧٨٢ - وَقَالَ التَّخَتُّم فِي الْيَسَار أحب إِلَيّ
<span data-type="title" id=toc-551>مسَائِل فِي الْعَقِيقَة</span>

٧٨٣ - قلت الرجل يُولد لَهُ ابْن وَلَيْسَ عِنْده مَا يعق عَنهُ أحب إِلَيْك أَن يستقرض ويعق عَنهُ أم يُؤَخر ذَلِك حَتَّى يؤسر لَهُ قَالَ أَشد مَا سَمِعت فِي الْعَقِيقَة مَا روى الْحسن عَن سَمُرَة عَن النَّبِي صلى الله عَلَيْهِ وَسلم كل غُلَام رهينة بعقيقته يذبح عَنهُ يَوْم','2','208',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (564,'سابعه وَيُسمى فِيهِ ويحلق رَأسه وَرُوِيَ عَن سُلَيْمَان بن عَامر الضَّبِّيّ عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ مَعَ الْغُلَام عقيقة أميطوا عَنهُ الْأَذَى وأريقوا عَنهُ دَمًا','2','209',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (565,'وَسُئِلَ الْحسن عَن قَوْله أميطوا عَنهُ الْأَذَى قَالَ يحلق رَأسه وَكَانَ يسْتَحبّ لمن عق عَن وَلَده أَن يذبح عَنهُ يَوْم السَّابِع فَإِن لم يفعل فَفِي أَربع عشرَة فَإِن لم يفعل فَفِي إِحْدَى وَعشْرين والعقيقة لَا يكسر لَهَا عظم تفصل جداول فيؤكل مِنْهَا وَيطْعم وَإِنِّي لأرجو إِن','2','210',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (566,'اسْتقْرض أَن يعجل الله لَهُ الْخلف لِأَنَّهُ أحيى سنة من سنَن النَّبِي صلى الله عَلَيْهِ وَسلم وأتبع مَا جَاءَ عَنهُ وَقد جَاءَ عَن النَّبِي صلى الله عَلَيْهِ وَسلم عَن الْغُلَام شَاتَان متكافئتان وَعَن الْجَارِيَة شَاة وَقَالَ','2','211',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (567,'بَعضهم إِن النَّبِي صلى الله عَلَيْهِ وَسلم عق عَن الْحسن بكبش وَعَن الْحُسَيْن بكبش','2','212',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (568,'وَيُقَال إِن فَاطِمَة حلقت رؤوسهما وتصدقت بِوَزْن شعرهما وَرقا
<span data-type="title" id=toc-552>مسَائِل وآثار فِي الْمُوَالَاة</span>

٧٨٤ - وَسَأَلته عَن الرجل يسلم فيوالي قوما','2','213',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (569,'قَالَ أبي الَّذِي أذهب إِلَيْهِ حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم الْوَلَاء لمن أعتق
٧٨٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ حَدثنَا الرّبيع بن أبي صَالح عَن شيخ يكنى أَبَا مدرك أَن رجلا من أهل السوَاد يُقَال لَهُ خشني أَتَى عليا يوليه فَأبى أَن يواليه فَرده فَأتى ابْن عَبَّاس أَو الْعَبَّاس فوالاه','2','214',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (570,'٧٨٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم عَن يُونُس عَن الْحسن قَالَ لَا وَلَاء إِلَّا لذِي نعْمَة
٧٨٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَسْبَاط قَالَ حَدثنَا مطرف عَن عَامر أَنه سُئِلَ عَن الرجل يسلم على يَدي الرجل قَالَ لَا وَلَاء إِلَّا لذِي نعْمَة إِذا أسلم فَمَاتَ وَرثهُ الْمُسلمُونَ وَإِن جنى جِنَايَة فعقله على الْمُسلمين وَإِن أوصى فأحاطت وَصيته بِمَالِه فَجَائِز','2','215',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (571,'٧٨٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ حَدثنَا معمر عَن الزُّهْرِيّ قَالَ قضى عمر بن الْخطاب فِي رجل والى قوما فَجعل مِيرَاثه لَهُم وعقله عَلَيْهِم
٧٨٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الصَّمد قَالَ حَدثنِي أبي قَالَ حَدثنَا حميد قَالَ حَدثنَا مُجَاهِد قَالَ أَتَى رجل مُعَاوِيَة فَقَالَ إِن رجلا من أهل الأَرْض والانى وَأسلم على يَدي وَلَيْسَ لَهُ موَالٍ مَاتَ لمن مِيرَاثه فَقَالَ مَالك ولميراثه مِيرَاثه لنا قَالَ يَا أَمِير الْمُؤمنِينَ والانى وَأسلم على يَدي قَالَ لست من مِيرَاثه فِي شَيْء قَالَ يَا أَمِير الْمُؤمنِينَ فَإِنَّهُ قتل أبنا لي فأعقله قَالَ أخرج غرب الله عَلَيْك','2','216',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (572,'٧٩٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ أخبرنَا ابْن جريج قَالَ قلت لعطاء السَّاقِط أَلَيْسَ يوالي من شَاءَ قَالَ بلَى ويزعمون عَن ابْن مَسْعُود أَنه قَالَ يوالي من شَاءَ مالم يوال الْأَوَّلين قلت لعطاء السَّاقِط يولج إِلَى الْقَوْم وَلَا يواليهم ويعقلون عَنهُ وَيعْقل عَنْهُم وينصرونه ثمَّ يَمُوت لمن مِيرَاثه قَالَ لَهُم قلت السَّاقِط لم يوالج أحدا وَلم يوال أحدا فَيَمُوت كَذَلِك من يَرِثهُ قَالَ الْمُسلمُونَ مِيرَاثه فِي بَيت المَال وهم يعْقلُونَ عَنهُ قلت لعطاء الرجل من الْعَرَب يكون فِي الْقَوْم لَا يعلم لَهُ أصل قد عقلوا عَنهُ وعاقلهم يَمُوت لمن مِيرَاثه قَالَ بلغنَا أَن عمر بن الْخطاب قَالَ من كَانَ يغْضب لَهُ أَو يحوطه أَو ينصره مِيرَاثه لَهُم وَقَالَهَا لى عَمْرو بن دِينَار
<span data-type="title" id=toc-553>الْمحرم لَا يخمر رَأسه</span>

٧٩١ - سَأَلته عَن الْمحرم يخمر رَأسه قَالَ لَا يخمر وَلَا يمس طيبا','2','217',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (573,'<span data-type="title" id=toc-554>إِذا خرج من الْمَيِّت شَيْئا بعد غسله سبعا</span>

٧٩٢ - قُلْنَا إِذا غسل إِلَى سبع ثمَّ خرج مِنْهُ شَيْء قَالَ يصب على ذَلِك مَاء وَلَا يلْتَفت إِلَيْهِ بعد السَّبع
<span data-type="title" id=toc-555>شَهَادَة أهل الذِّمَّة</span>

٧٩٣ - قَالَ أبي لَا تجوز شَهَادَة أهل الذِّمَّة إِلَّا فِي مَوضِع فِي السّفر الَّذِي قَالَ الله {أَو آخرَانِ من غَيْركُمْ إِن أَنْتُم ضَرَبْتُمْ فِي الأَرْض فأصابتكم مُصِيبَة الْمَوْت} فأجازها الْأَشْعَرِيّ','2','218',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (574,'وَرُوِيَ عَن ابْن عَبَّاس أَنه قَالَ {أَو آخرَانِ من غَيْركُمْ} من أهل الْكتاب وَهَذَا مَوضِع ضَرُورَة لِأَنَّهُ فِي سفر وَلَا يجد من يشْهد من الْمُسلمين وَإِنَّمَا جَازَت من هَذَا الْمَعْنى وَإِنَّمَا قَالَ الله {مِمَّن ترْضونَ من الشُّهَدَاء} وَقَالَ {وَأشْهدُوا ذَوي عدل مِنْكُم} فليسوا بعدول وَلَيْسوا بِرِضا وَرُوِيَ عَن الْحسن أَنه قَالَ لَا يحل لحَاكم من حكام الْمُسلمين أَن يجييز شَهَادَة أهل الْكتاب فِي شَيْء','2','219',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (575,'وَقد روى بعض النَّاس عَن الزُّهْرِيّ أَنه قَالَ لَا تجوز شَهَادَة بَعضهم على بعض لقَوْل الله عز وَجل {فأغرينا بَينهم الْعَدَاوَة والبغضاء}
<span data-type="title" id=toc-556>الْمُحرمَة يَمُوت محرمها بِمَكَّة</span>

٧٩٤ - سُئِلَ أبي عَن الْمَرْأَة وَأَنا شَاهد تخرج بِمحرم إِلَى الْحَج فَإِذا صَارَت إِلَى مَكَّة يَمُوت محرمها كَيفَ تصنع ترجع أَو تقيم قَالَ هَذِه مضطرة أَرْجُو
<span data-type="title" id=toc-557>رجل فجر بِأم امْرَأَته</span>

٧٩٥ - وَسَأَلته عَن الرجل يفجر بِأم امْرَأَته','2','220',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (576,'قَالَ إِذا وطىء حرمت الإبنة عَلَيْهِ وَكَذَا إِذا فجر بابنتها حرمت الْأُم عَلَيْهِ وَهَذَا إِذا وطىء فَمَا لم يطَأ مثل الْقبْلَة وَمَا أشبهه فَلَا أُجِيب فِيهِ قَالَ عمرَان بن حُصَيْن إِذا فجر بِأم امْرَأَته حرمتا عَلَيْهِ
<span data-type="title" id=toc-558>إِذا سرق العَبْد من مَوْلَاهُ أَو الزَّوْج من امْرَأَته</span>

٧٩٦ - وَقَالَ أبي إِذا سرق العَبْد من مَوْلَاهُ لم يقطع
٧٩٧ - قلت فالزوج من مرته قَالَ إِذا كَانَا جَمِيعًا فِي الْبَيْت فَهَذَا جَائِز','2','221',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (577,'<span data-type="title" id=toc-559>وجوب الصَدَاق بإغلاق الْبَاب</span>

٧٩٨ - وَقَالَ أبي إِذا أغلق الْبَاب وأرخى السّتْر لزمَه الصَدَاق
٧٩٩ - قلت فَإِن لم يطَأ قَالَ وَإِن لم يطَأ أَرَأَيْت لَو جَاءَت بِولد أَلَيْسَ تلْزمهُ إِيَّاه الْعَجز جَاءَ من قبله
٨٠٠ - قلت فَإِن قَالَ لم أَطَأ وَقَالَت لم يطأني قَالَ هَذَا فار من الصَدَاق هَذِه فارة من الْعدة','2','222',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (578,'<span data-type="title" id=toc-560>حكم ذَبَائِح الْمَجُوس وَنِكَاح نِسَائِهِم</span>

٨٠١ - سَأَلت أبي عَن تَزْوِيج المجوسيات وذبائحهم فَقَالَ قَالَ الله {وَلَا تنْكِحُوا المشركات حَتَّى يُؤمن} وَقَالَ فِي سُورَة الْمَائِدَة وَهِي من آخر مَا أنزل من الْقُرْآن {الْيَوْم أحل لكم الطَّيِّبَات وَطَعَام الَّذين أُوتُوا الْكتاب حل لكم وطعامكم حل لَهُم وَالْمُحصنَات من الْمُؤْمِنَات وَالْمُحصنَات من الَّذين أُوتُوا الْكتاب من قبلكُمْ إِذا آتَيْتُمُوهُنَّ أُجُورهنَّ محصنين غير مسافحين وَلَا متخذي أخدان}
٨٠٢ - حَدثنَا أبي قَالَ حَدثنَا إِبْرَاهِيم بن إِسْحَاق الطَّالقَانِي قَالَ حَدثنَا ابْن الْمُبَارك عَن يُونُس عَن الزُّهْرِيّ قَالَ قَالَ الله {وَلَا تنْكِحُوا}','2','223',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (579,'المشركات حَتَّى يُؤمن) الْآيَة ثمَّ أحل نِكَاح الْمُحْصنَات من أهل الْكتاب فَلم ينْسَخ من هَذِه الْآيَة غير ذَلِك فنكاح كل مُشركَة سوى نسَاء أهل الْكتاب حرَام وَنِكَاح المسلمات من الْمُشْركين حرَام
<span data-type="title" id=toc-561>رِوَايَات فِي زواج ووطىء الْإِمَاء من أهل الْكتاب وَالْمَجُوس</span>

٨٠٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا جرير عَن مُغيرَة عَن حَمَّاد عَن إِبْرَاهِيم قَالَ إِذا سبين اليهوديات والنصرانيات يجبرن على الْإِسْلَام فَإِن أسلمن أَو لم يسلمن وطئن واستخدمن وَإِذا سبين المجوسيات وَعَبدَة الْأَصْنَام جبرن على الْإِسْلَام فَإِن أسلمن وطئن واستخدمن وَإِن لم يسلمن استخدمن وَلم يوطين','2','224',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (580,'٨٠٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا جرير عَن مُوسَى بن أبي عَائِشَة قَالَ سَأَلت مرّة الْهَمدَانِي عَن النَّاس يشْتَرونَ المجوسيات فَيَقَع أحدهم عَلَيْهَا قبل أَن تسلم قَالَ لَا يصلح هَذَا وَسَأَلت سعيد بن جُبَير فَقَالَ مَا هم بِخَير مِنْهُم إِذا فعلوا ذَلِك فَكَانَ أشدهما قولا فِيهِ','2','225',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (581,'٨٠٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن شريك عَن لَيْث عَن مُجَاهِد قَالَ لَا يَطَأهَا حَتَّى تسلم وتغتسل
٨٠٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن شريك عَن سماك عَن أبي سَلمَة يَعْنِي ابْن عبد الرَّحْمَن قَالَ لَا يَطَأهَا حَتَّى تسلم فِي الْمَجُوسِيَّة','2','226',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (582,'٨٠٧ - حَدثنَا صَالح قَالَ حَدثنَا أبي قَالَ حَدثنَا عبد الله بن يزِيد قَالَ حَدثنَا حَيْوَة قَالَ حَدثنَا من سمع أَبَا المصعب مشرح بن هاعان الْمعَافِرِي وواهب بن عبد الله الْمعَافِرِي وَقيس بن رَافع الْعَبْسِي والْحَارث بن يزِيد الْحَضْرَمِيّ وَعبد الله بن هُبَيْرَة السبائي يَقُولُونَ لَا يطَأ الرجل الْأمة','2','227',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (583,'مِمَّا ملكت يَمِينه إِذا كَانَت مَجُوسِيَّة أَو بربرية أَو سَوْدَاء أَو غير ذَلِك حَتَّى تسلم
٨٠٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّحْمَن بن مهْدي عَن سُفْيَان عَن حَمَّاد قَالَ سَأَلت سعيد بن جُبَير عَن تَزْوِيج الْيَهُودِيَّة والنصرانية قَالَ لَا بَأْس بِهِ فَقلت إِن الله يَقُول {وَلَا تنْكِحُوا المشركات حَتَّى يُؤمن} قَالَ أهل الْأَوْثَان وَالْمَجُوس','2','228',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (584,'<span data-type="title" id=toc-562>آثَار فِي دِيَة أهل الْكتاب وَالْمَجُوس والصابئة</span>

٨٠٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ حَدثنَا سُفْيَان عَن أبي الْمِقْدَام ثَابت بن هريمز مولى لبكر بن وَائِل عَن سعيد بن الْمسيب عَن عمر بن الْخطاب قَالَ دِيَة الْيَهُودِيّ وَالنَّصْرَانِيّ أَرْبَعَة آلَاف أَرْبَعَة آلَاف ودية الْمَجُوسِيّ ثَمَانمِائَة','2','228',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (585,'٨١٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ حَدثنَا الْفضل بن دلهم عَن الْحسن أَن عمر قَالَ دِيَة الْيَهُودِيّ وَالنَّصْرَانِيّ أَرْبَعَة آلَاف ودية الْمَجُوسِيّ ثَمَانمِائَة
٨١١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ أخبرنَا معمر عَن قَتَادَة عَن ابْن الْمسيب قَالَ دِيَة الْيَهُودِيّ وَالنَّصْرَانِيّ أَرْبَعَة آلَاف ودية الْمَجُوسِيّ ثَمَانمِائَة
٨١٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أسود بن عَامر قَالَ حَدثنَا حَمَّاد بن سَلمَة عَن دَاوُد عَن عَمْرو بن شُعَيْب عَن عمر بن عبد الْعَزِيز','2','230',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (586,'قَالَ دِيَة الْمَجُوسِيّ ثَمَانمِائَة
٨١٣ - قَالَ حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا سهل بن يُوسُف عَن عَمْرو عَن الْحسن قَالَ دِيَة الصابي بِمَنْزِلَة دِيَة الْمَجُوسِيّ','2','231',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (587,'٨١٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الْوَهَّاب عَن عَمْرو قَالَ كَانَ الْحسن يَقُول دِيَة الصابي مثل دِيَة الْمَجُوسِيّ ثَمَانمِائَة دِرْهَم
٨١٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يزِيد بن هَارُون قَالَ أخبرنَا يحيى بن سعيد عَن سُلَيْمَان بن يسَار قَالَ كَانَ النَّاس فِي الزَّمن الأول يقضون فِي دِيَة الْمَجُوسِيّ بثمان مائَة وَكَانُوا يقضون فِي دِيَة الْيَهُودِيّ وَالنَّصْرَانِيّ بِالَّذِي كَانُوا يتعاقلون بِهِ فِي قَومهمْ ثمَّ رفعت الدِّيَة إِلَى سِتَّة آلَاف','2','232',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (588,'٨١٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ يُونُس أخبرنَا عَن الْحسن أَنه قَالَ دِيَة الْيَهُودِيّ وَالنَّصْرَانِيّ أَرْبَعَة آلَاف أَرْبَعَة آلَاف والمجوسي ثَمَانمِائَة
٨١٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ أخبرنَا يحيى بن سعيد عَن سُلَيْمَان بن يسَار مثل ذَلِك
٨١٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ أخبرنَا ابْن أبي ليلى عَن عَطاء عَن عمر بن الْخطاب أَنه قَالَ ذَلِك
٨١٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد عَن ابْن أبي عرُوبَة عَن قَتَادَة عَن سعيد بن الْمسيب أَيْضا','2','233',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (589,'<span data-type="title" id=toc-563>من كَانَ عِنْده صبوح أَو غبوق فَلَيْسَ بمضطر</span>

٨٢٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ أخبرنَا ابْن عون قَالَ دَخَلنَا على الْحسن فَأخْرج لنا كتابا من سَمُرَة فَإِذا فِيهِ إِنَّه يَجْزِي من الِاضْطِرَار صبوح أَو غبوق قَالَ نبئت إِنَّهَا كتب
<span data-type="title" id=toc-564>أثر لِابْنِ عمر فِي النَّهْي عَن لبس الْحَرِير</span>

٨٢١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْحَاق بن يُوسُف قَالَ حَدثنَا ابْن عون عَن الْحسن قَالَ دَخَلنَا على عبد الله بن عمر بالبطحاء فَقَالَ لَهُ رجل يَا أَبَا عبد الرَّحْمَن إِن ثيابنا هَذِه قد خالطها الْحَرِير وَهُوَ قَلِيل قَالَ دعوا الْحَرِير قَلِيله وَكَثِيره','2','334',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (590,'<span data-type="title" id=toc-565>هَل تَرث الْمَرْأَة إِذا طَلقهَا زَوجهَا فِي مَرضه</span>

٨٢٢ - وَسَأَلته عَن الرجل إِذا طلق فِي مَرضه قَالَ تَرثه مَا أَمْسَكت نَفسهَا عَن الْأزْوَاج قَالَ وَأما أهل الْمَدِينَة فَيَقُولُونَ تَرثه وَإِن تزوجت لِأَن هَذَا فار من الْمِيرَاث
<span data-type="title" id=toc-566>مِقْدَار مَا تقص الْمَرْأَة من شعرهَا فِي الْحَج وَالْعمْرَة</span>

٨٢٣ - وَقَالَ فِي الْمَرْأَة تقص من أَطْرَاف شعرهَا قدر أُنْمُلَة','2','235',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (591,'<span data-type="title" id=toc-567>إسدال الْمُحرمَة ثوبا على وَجههَا</span>

٨٢٤ - وَقَالَ تسدل على وَجههَا شَيْئا رَقِيقا
<span data-type="title" id=toc-568>من تَرْجَمَة حجاج بن أَرْطَاة</span>

٨٢٥ - وَقَالَ أبي حجاج بن أَرْطَاة لم يكن يحيى بن سعيد يرى أَن يرْوى عَنهُ شَيْئا قَالَ وَهُوَ مُضْطَرب الحَدِيث
<span data-type="title" id=toc-569>فيمَ الاحتكار وَفِي مثل أَي مَوضِع يكون الاحتكار</span>

٨٢٦ - سَأَلت أبي عَن رجل يَشْتَرِي التَّمْر من الْبَصْرَة إِلَى بَغْدَاد أَو إِلَى بلد من الْبلدَانِ يُرِيد بَيْعه فيكسد عَلَيْهِ ويلحقه فِيهِ وضيعة فَيكْرَه أَن يَبِيعهُ بوضيعة فيحبسه الشَّهْر أَو الشَّهْرَيْنِ يَرْجُو بذلك أَن يصير السّعر إِلَى حَال يسلم من الوضيعة هَل تكون هَذِه حكرة','2','236',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (592,'وَهل يُسمى من فعل هَذَا محتكرا وَهُوَ لَا يعرف بالحكر فَقَالَ أَرْجُو أَن لَا يكون فِي مثل هَذَا الْبَلَد حكرة وَلَا أعرف لَهَا حدا وَلَكِن يكون هَذَا فِي مثل الْمَدِينَة وَمَكَّة وأشباههما من الْبلدَانِ يَشْتَرِي الرجل الطَّعَام أَو التَّمْر الَّذِي هُوَ قوتهم فيحتكره فَأَخَاف أَن يكون هَذَا حِينَئِذٍ متحكرا فَأَما مثل هَذِه الْمَدِينَة أَو الْبَصْرَة فَرُبمَا احتكروا فَكَانَ فِي ذَلِك مرفق للنَّاس وَلَكِن يَنْبَغِي للرجل إِذا اشْترى شَيْئا من قوت الْمُسلمين أَن يحسن نِيَّته وَلَا يتَمَنَّى الغلاء','2','237',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (593,'<span data-type="title" id=toc-570>من زوج ابْنَته الْبكر الْبَالِغَة وَلم يستأمرها</span>

٨٢٧ - وَسَأَلته عَن رجل زوج أبنته وَهِي بكر وَقد أدْركْت وَلم يستأمرها فَقَالَ فِيهَا اخْتِلَاف أما أهل الْحجاز فَيَقُولُونَ نِكَاحه إِيَّاهَا جَائِز وَلَيْسَ لَهَا خِيَار وَقَالَ بعض النَّاس لَهَا الْخِيَار إِذا كَانَت بَالغا أوغير بَالغ فَإِذا بلغت كَانَ لَهَا خِيَار فَأَما إِذا كَانَت صَغِيرَة فَزَوجهَا أَبوهَا فَإِنَّهُ لَا خِيَار لَهَا عندنَا وَإِن بلغت فَأَما الْبَالِغ فقد كَانَ يَنْبَغِي لأَبِيهَا أَن يستأمرها
<span data-type="title" id=toc-571>حكم تَزْوِيج ابْن الْعم مَعَ تَزْوِيج الْعم</span>

٨٢٨ - قلت امْرَأَة زَوجهَا عَمها وَهِي كارهة مُنكرَة لتزويجه غير راضية فَأَتَت ابْن عَم لَهَا فَزَوجهَا مِمَّن رضيت هِيَ وَهُوَ هَل يجوز ذَلِك وَهل يكون ابْن الْعم وليا مَعَ الْعم','2','238',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (594,'قَالَ الْعم أولى بهَا من ابْن عَمها فَإِن زَوجهَا الْعم وَلم يستأمرها فَإِن ذَلِك النِّكَاح يَنْفَسِخ إِذا أَرَادَت ذَلِك ويزوجها بعد من ترْضى فَأَما تَزْوِيج ابْن عَمها إِيَّاهَا وَقد زَوجهَا الْعم فَإِن الَّذِي يعجبنا من ذَلِك فسخ نِكَاح ابْن عَمها ويلي نِكَاحهَا عَمها وَهُوَ أولى من ابْن الْعم
<span data-type="title" id=toc-572>حكم ولَايَة الذِّمِّيّ للمسلمة وَولَايَة الْخَال</span>

٨٢٩ - وَقَالَ فِي امْرَأَة لَهَا أَب ذمِّي وَلها أَخ مُسلم أَيهمَا يكون وَليهَا وَهل يكون الذِّمِّيّ ولي الْمسلمَة إِذا لم يكن ولي غَيره وَهل يكون الْخَال وليا إِذا لم يكن أقرب مِنْهُ قَالَ لَا يكون الذِّمِّيّ وليا وَلَكِن يكون أدنى الْعصبَة فَهُوَ أولى وَلَا','2','239',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (595,'يكون الذِّمِّيّ وليا وَلَا يكون الْخَال وليا إِنَّمَا الْوَلِيّ الْعصبَة
<span data-type="title" id=toc-573>تَكْفِي سَجْدَتَانِ لسَهْوه وسهو الإِمَام</span>

٨٣٠ - قلت رجل سبقه الإِمَام بِبَعْض وَقد سَهَا الإِمَام فِيمَا سبقه أَو فِيمَا أدْرك فَلم يسْجد مَعَ الإِمَام قَامَ ليقضي فَسَهَا هُوَ فِي الْقَضَاء هَل تجزيه سَجْدَتَانِ لسَهْوه وسهو الإِمَام قَالَ تجزيه سَجْدَتَانِ لسَهْوه وسهو الإِمَام وَقد كَانَ يَنْبَغِي لَهُ أَن يتبع الإِمَام فِي سَهْوه','2','240',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (596,'<span data-type="title" id=toc-574>حكم بيع متاعين بِكَذَا وَأَحَدهمَا بِكَذَا</span>

٨٣١ - وَقَالَ فِي رجل لَهُ مَتَاع فِي موضِعين فَأَتَاهُ رجل فساومه بهما وَقد قلبهما جَمِيعًا فَقَالَ لَهُ صَاحب الْمَتَاع قد بِعْتُك هَذَا لأَحَدهمَا بِكَذَا وَكَذَا فَإِن قبضت الآخر فهما عَلَيْك بِكَذَا وَكَذَا مِمَّا بَاعه الأول وَأحد المتاعين أقل ثمنا من الآخر فقبضهما جَمِيعًا أَو قبض الأول مِنْهُمَا هَل يَصح هَذَا البيع قَالَ أَرْجُو أَن يكون هَذَا البيع صَحِيحا إِذا كَانَ قد قلبهما وقبضهما بعد بَيْعه إيَّاهُمَا
<span data-type="title" id=toc-575>آثَار فِي دِيَة أهل الْكتاب وَالْمَجُوس وذبائحهم وَنِكَاح نِسَائِهِم</span>

٨٣٢ - عَن عمر قَالَ دِيَة الْيَهُودِيّ وَالنَّصْرَانِيّ أَرْبَعَة آلَاف أَرْبَعَة آلَاف والمجوسي ثَمَانمِائَة حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا سعيد عَن قَتَادَة عَن سعيد بن الْمسيب وَالْحسن عَن عمر بن الْخطاب أَنه جعل دِيَة الْيَهُودِيّ وَالنَّصْرَانِيّ أَرْبَعَة آلَاف والمجوسي ثَمَانمِائَة','2','241',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (597,'٨٣٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ أخبرنَا ابْن جريح قَالَ قلت لعطاء دِيَة الْمَجُوسِيّ قَالَ ثَمَانمِائَة دِرْهَم
٨٣٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ أخبرنَا ابْن جريج قَالَ أَخْبرنِي عَمْرو بن شُعَيْب أَن أَبَا مُوسَى الْأَشْعَرِيّ كتب إِلَى عمر بن الْخطاب أَن الْمُسلمُونَ يقعون على الْمَجُوس فيقتلونهم فَمَاذَا ترى فَكتب إِلَيْهِ عمر إِنَّمَا هم عبيد فأقمهم قيمَة العبيد فِيكُم فَكتب إِلَيْهِ أَبُو مُوسَى ثَمَانمِائَة دِرْهَم فوضعها عمر للمجوس
٨٣٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد عَن عُثْمَان بن غياث قَالَ سَأَلت الْحسن وَعِكْرِمَة عَن دِيَة الْيَهُودِيّ وَالنَّصْرَانِيّ قَالَ قَالَا أَرْبَعَة آلَاف ودية الْمَجُوسِيّ ثَمَانمِائَة','2','242',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (598,'٨٣٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ ثَنَا سُفْيَان عَن قيس بن مُسلم عَن الْحسن بن مُحَمَّد قَالَ كتب رَسُول الله صلى الله عَلَيْهِ وَسلم إِلَى مجوس هجر يعرض عَلَيْهِم الْإِسْلَام فَمن أسلم قبل مِنْهُ وَمن أَبى ضربت عَلَيْهِم الْجِزْيَة على أَن لَا تُؤْكَل لَهُم ذَبِيحَة وَلَا تنْكح لَهُم امْرَأَة
٨٣٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْمَاعِيل بن إِبْرَاهِيم قَالَ حَدثنَا عَوْف قَالَ حَدثنَا عباد عَن بجالة بن عَبدة قَالَ كتب عمر','2','243',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (599,'إِلَى أبي مُوسَى أَن أَعرضُوا على من قبلكُمْ من الْمَجُوس أَن يدعوا نِكَاح أمهاتهم وبناتهم وأخواتهم ويأكلوا جَمِيعًا كَيْمَا نلحقهم بِأَهْل الْكتاب واقتلوا كل كَاهِن وساحر
٨٣٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ مُحَمَّد بن جَعْفَر قَالَ حَدثنَا عَوْف عَن عباد عَن بجالة بن عَبدة الْعَنْبَري قَالَ كتب إِلَيْنَا عمر أَن أَعرضُوا على من قبلكُمْ من الْمَجُوس فَذكر مثله','2','244',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (600,'<span data-type="title" id=toc-576>حكم طبخ الْجَرَاد وَهُوَ حَيّ</span>

٨٣٩ - سَأَلت أبي عَن الْجَرَاد يطْبخ وَهُوَ حَيّ فَقَالَ لَا بَأْس بِهِ
<span data-type="title" id=toc-577>إِذا شرب المصروع المَاء فِي رَمَضَان وَهُوَ لَا يعقل</span>

٨٤٠ - سَأَلته عَن رجل صرع فِي شهر رَمَضَان فجَاء رجل بكوز مَاء فرش على وَجهه مَاء فَأخذ الْكوز فَشرب مِنْهُ فَقيل لَهُ فَقَالَ مَا عقلت بِهِ هَل عَلَيْهِ قَضَاء','2','245',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (601,'قَالَ لَا يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم رفع الْقَلَم عَن الْمَجْنُون حَتَّى يفِيق
٨٤١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ أخبرنَا يُونُس عَن الْحسن عَن عَليّ قَالَ سَمِعت رَسُول الله صلى الله عَلَيْهِ وَسلم يَقُول رفع الْقَلَم عَن ثَلَاثَة عَن الصَّغِير حَتَّى يبلغ وَعَن النَّائِم حَتَّى يَسْتَيْقِظ وَعَن الْمُصَاب حَتَّى يكْشف عَنهُ
٨٤٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنِي بهز قَالَ حَدثنَا همام عَن قَتَادَة عَن الْحسن عَن عَليّ أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ رفع الْقَلَم','2','256',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (602,'عَن ثَلَاثَة عَن النَّائِم حَتَّى يَسْتَيْقِظ وَعَن الْمَعْتُوه أَو قَالَ الْمَجْنُون حَتَّى يعقل وَالصَّغِير حَتَّى يشب
٨٤٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا حسن بن مُوسَى وَعَفَّان وروح عَن حَمَّاد بن سَلمَة عَن حَمَّاد يَعْنِي ابْن أبي سُلَيْمَان عَن إِبْرَاهِيم عَن الْأسود عَن عَائِشَة عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ رفع الْقَلَم عَن ثَلَاثَة عَن الصَّبِي حَتَّى يَحْتَلِم وَعَن النَّائِم حَتَّى يَسْتَيْقِظ وَعَن الْمَجْنُون حَتَّى يعقل','2','247',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (603,'قَالَ عَفَّان وَعَن الْمَجْنُون حَتَّى يعقل وَقَالَ حَمَّاد عَن الْمَعْتُوه حَتَّى يعقل كَانَ حَمَّاد مرّة يَقُول الْمَعْتُوه وَمرَّة يَقُول الْمَجْنُون
<span data-type="title" id=toc-578>من سمع مِنْهُ الْحسن الْبَصْرِيّ من الصَّحَابَة وَمن لم يسمع مِنْهُ</span>

٨٤٤ - قَالَ أبي سمع الْحسن من ابْن عمر وَأنس بن مَالك وَابْن مُغفل وَقَالَ بَعضهم حَدثنِي عمرَان بن حُصَيْن وَقَالَ بَعضهم حَدثنَا','2','248',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (604,'أَبُو هُرَيْرَة وَسمع من عَمْرو بن تغلب أَحَادِيث وَهُوَ من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم وَقَالَ بَعضهم سمع من سَمُرَة بن جُنْدُب وَحكي عَن الْحسن أَنه سمع عَائِشَة وَهِي تَقول إِن نَبِيكُم صلى الله عَلَيْهِ وَسلم بَرِيء مِمَّن فرق دينه
<span data-type="title" id=toc-579>إِذا قتل نَصْرَانِيّ نَصْرَانِيّا ثمَّ أسلم</span>

٨٤٥ - سَأَلت أبي عَن نَصْرَانِيّ قتل نَصْرَانِيّا ثمَّ أسلم قَالَ يقتل بِهِ لِأَنَّهُ قَتله وَهُوَ نَصْرَانِيّ فَلَيْسَ يدْرَأ عَنهُ الْإِسْلَام الْقَتْل','2','249',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (605,'<span data-type="title" id=toc-580>رِوَايَة فِي قتل الْكَلْب الْأسود البهيم</span>

٨٤٦ - حَدثنِي أبي قَالَ حَدثنِي وَكِيع عَن أبي سُفْيَان بن الْعَلَاء قَالَ سَمِعت الْحسن يحدث أَن رَسُول الله صلى الله عَلَيْهِ وَسلم قَالَ لَوْلَا أَن الْكلاب أمة من الْأُمَم لأمرت بقتلها فَاقْتُلُوا كل أسود بهيم قَالَ فَقَالَ لَهُ رجل يَا أَبَا سعيد مِمَّن سَمِعت هَذَا فَقَالَ حَدَّثَنِيهِ ثمَّ حلف عبد الله بن مُغفل عَن النَّبِي صلى الله عَلَيْهِ وَسلم مُنْذُ كَذَا وَكَذَا وَلَقَد حَدثنَا فِي ذَلِك الْمجْلس كَأَنَّهُ أَرَادَ غير هَذَا الحَدِيث','2','250',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (606,'<span data-type="title" id=toc-581>إِذا رَأَتْ الْمَرْأَة الدَّم فِي غير أَيَّامهَا</span>

٨٤٧ - قلت امْرَأَة رَأَتْ الدَّم فِي غير أَيَّامهَا أقل من يَوْم ثمَّ أنقطع وَكَيف إِن رَأَتْهُ يَوْمًا تَاما فَإِن رَأَتْهُ أقل من يَوْم ثمَّ انْقَطع عَنْهَا ثمَّ رَأَتْهُ فِي الْيَوْم الثَّانِي أقل من يَوْم ثمَّ انْقَطع عَنْهَا ثمَّ رَأَتْهُ فِي الْيَوْم الثَّالِث كَذَلِك ثمَّ انْقَطع عَنْهَا مَا يجب عَلَيْهَا وَهل يكون الْحيض مَعَ الْحمل قَالَ كل دم كَانَت ترَاهُ فِي أَيَّام تعرفها من أَيَّامهَا الَّتِي كَانَت تحيضها فَهُوَ حيض إِذا كَانَ ذَلِك ابْتِدَاء دم رَأَتْهُ فِي أَيَّامهَا فَإِن انْقَطع عَنْهَا حَتَّى ترى الْبيَاض خَالِصا ثمَّ عاودها فالحيطة عندنَا فِيهَا لَهَا أَن تصلي وَلَا يَغْشَاهَا زَوجهَا حَتَّى تمْضِي الْأَيَّام الَّتِي كَانَت تعرفها من أَيَّامهَا الَّتِي كَانَت تحيضها فَإِذا جَاوَزت أَيَّامهَا الَّتِي كَانَت تعرفها من حَيْضهَا فَلَا تَعْتَد بِشَيْء من دم ترَاهُ وتعده اسْتِحَاضَة وَتصلي فِي تِلْكَ الْأَيَّام','2','251',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (607,'<span data-type="title" id=toc-582>إِذا تزوج أمة فأولدها ثمَّ اشْتَرَاهَا هَل تكون أم ولد لَهُ</span>

٨٤٨ - وَقَالَ إِذا تزوج الرجل الْأمة فأولدها ثمَّ اشْتَرَاهَا بعد ذَلِك فَأكْثر مَا سمعنَا عَنهُ من التَّابِعين يَقُولُونَ لَا تكن أم ولد حَتَّى تَلد عِنْده وَهُوَ يملكهَا وَقَالَ بعض النَّاس هِيَ أم ولد وَلَيْسَ لَهُ بيعهَا
<span data-type="title" id=toc-583>حكم الْمُعَامَلَة مَعَ من لَا يُؤَدِّي الزَّكَاة</span>

٨٤٩ - قلت الرجل يعلم أَنه لَا يُؤَدِّي الزَّكَاة هَل يجوز مُعَامَلَته فِي الشِّرَاء أَو البيع فَقَالَ يوعظ وَيُؤمر وَيُقَال أد زكاتك وَإِنِّي أحب أَن يجفى فِي مُعَامَلَته ويتنكب فِي ذَلِك لَعَلَّه أَن يَتُوب أَو يرجع','2','252',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (608,'<span data-type="title" id=toc-584>حكم تَزْوِيج من يسكر</span>

٨٥٠ - سُئِلَ أبي وَأَنا شَاهد هَل يُزَوّج الَّذِي يسكر قَالَ لَا يُزَوّج إِذا سكر قد يُطلق وَلَا يعلم وَأي شَيْء أعظم من السكر
<span data-type="title" id=toc-585>إِذا تزوج الْعَرَبِيّ القرشية</span>

٨٥١ - وَسُئِلَ هَل يُزَوّج الْعَرَبِيّ القرشية قَالَ لَا','2','253',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (609,'٨٥٢ - قيل فَإِن تزوج قَالَ يفرق فِيمَا بَينهمَا فَقَالَ وَجعل يشدد فِيهِ وَقَالَ الْأَكفاء قُرَيْش لقريش وَالْعرب للْعَرَب
<span data-type="title" id=toc-586>حكم خِيَار الْمجْلس</span>

٨٥٣ - وَسُئِلَ عَن البيعين بِالْخِيَارِ اشْترى رجل من رجل عبدا وهما قائمان فَأعْتقهُ المُشْتَرِي فَقَالَ البَائِع لَا أُجِيز لي الْخِيَار فَقَالَ يجوز عَلَيْهِ
٨٥٤ - قيل لَهُ فَلَيْسَ بِمَنْزِلَة الشَّرْط قَالَ أَرَأَيْت لَو مَاتَ من مَال من كَانَ','2','254',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (610,'<span data-type="title" id=toc-587>حكم عُهْدَة الرَّقِيق</span>

٨٥٥ - وَسُئِلَ عَن مَذْهَب أهل الْمَدِينَة فِي عُهْدَة الرَّقِيق فَقَالَ لَا يُعجبنِي','2','255',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (611,'<span data-type="title" id=toc-588>من قَالَ الْحل عَليّ حرَام أَعنِي بِهِ الطَّلَاق</span>

٨٥٦ - وَقَالَ إِذا قَالَ الْحل عَليّ حرَام أَعنِي بِهِ الطَّلَاق قَالَ أخْشَى أَن / ١٠٠ يكون ثَلَاثًا وَلَا أُجِيب فِيهِ
<span data-type="title" id=toc-589>إِذا قَالَ أَمرك بِيَدِك</span>

٨٥٧ - وَقَالَ إِذا قَالَ لَهَا أَمرك بِيَدِك فالقضاء مَا قَضَت
٨٥٨ - قيل فَإِن رَجَعَ من قبل أَن يقوم قَالَ لَهُ فَإِن طلقت نَفسهَا جَازَ عَلَيْهِ وَإِن قَالَت اخْتَرْت نَفسِي فَلَا يكون شَيْء','2','256',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (612,'<span data-type="title" id=toc-590>الْوتر على الرَّاحِلَة</span>

٨٥٩ - سَأَلت أبي يؤتر الرجل على بعيره قَالَ نعم قد أوتر النَّبِي صلى الله عَلَيْهِ وَسلم على بعيره
<span data-type="title" id=toc-591>إِذا نذر صَوْم شهر يَصُومهُ مُتَفَرقًا أَو مُتَتَابِعًا</span>

٨٦٠ - قلت من نذر أَن يَصُوم شهرا يَصُومهُ مُتَفَرقًا قَالَ إِذا سمى شهر بِعَيْنِه لم يصم مُتَفَرقًا وَإِذا لم يسم شهرا بِعَيْنِه وَقَالَ عَليّ أَن أَصوم شهرا فَلَا بَأْس أَن يَصُوم مُتَفَرقًا','2','257',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (613,'<span data-type="title" id=toc-592>حكم الْقود فِيمَن ضرب رجلا بعصا فَقتله</span>

٨٦١ - سُئِلَ أبي وَأَنا شَاهد عَن رجل ضرب رجلا بعصا فَقتله قَالَ إِذا كَانَت أطول من عَمُود الفسقاط رَأَيْت عَلَيْهِ الْقود فَمَا كَانَ دون ذَلِك فَلَا
<span data-type="title" id=toc-593>إِذا بَاعَ الْعِنَب قبل إِخْرَاج الْعشْر</span>

٨٦٢ - سُئِلَ عَن الْعِنَب إِذا كَانَ خَمْسَة أوسق فَبيع قَالَ يخرج من الدَّرَاهِم الْعشْر
<span data-type="title" id=toc-594>بيع أحد الشَّرِيكَيْنِ للثَّانِي مَالا غير مُشْتَرك بَينهمَا</span>

٨٦٣ - وَسُئِلَ وَأَنا شَاهد عَن رجلَيْنِ شَرِيكَيْنِ لكل وَاحِد مِنْهُمَا مَال على حِدة فَرُبمَا أَرَادَ أَحدهمَا أَن يَبِيع الشَّيْء فَيَقُول لَهُ صَاحبه انْظُر بِمَا تطلب حَتَّى أشتريه مِنْك قَالَ لَا بَأْس بذلك','2','258',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (614,'<span data-type="title" id=toc-595>حكم كِرَاء الْإِبِل</span>

٨٦٤ - وَسُئِلَ عَن كِرَاء الْإِبِل فَقَالَ إِذا كَانَ لَا يحمل عَلَيْهَا مَالا تطِيق فَلَا بَأْس بكرائها
<span data-type="title" id=toc-596>حكم إعداد الشَّيْء ليَبِيعهُ بنسيئة إِلَى أجل</span>

٨٦٥ - وَسُئِلَ عَن الرجل يعد الشَّيْء ليَبِيعهُ بنسيئة إِلَى أجل فَقَالَ إِذا أعده أَن يَبِعْهُ بنسيئة وَلَا يَبِيعهُ بِنَقْد فَلَا يُعجبنِي لِأَن هَذِه عينه حِينَئِذٍ','2','259',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (615,'<span data-type="title" id=toc-597>إِذا زوج الصَّبِي عَمه هَل لَهُ الْخِيَار إِذا عقل</span>

٨٦٦ - وَسُئِلَ عَن صبي زوجه عَمه فَلَمَّا عقل كره تَزْوِيج الْعم إِيَّاه قَالَ فَإِن كَانَ رَضِي فِي وَقت من الْأَوْقَات جَازَ ذَلِك وَإِن كَانَ لم يرض وَأَرَادَ فسخ النِّكَاح أجزته
<span data-type="title" id=toc-598>حكم من أدْرك رَكْعَة مَعَ الإِمَام</span>

٨٦٧ - سَأَلته عَن رجل أدْرك مَعَ الإِمَام رَكْعَة قَالَ يقوم فَيقْرَأ فَاتِحَة الْكتاب وَسورَة ثمَّ يجلس فيتشهد ثمَّ يقوم فَيقْرَأ بِفَاتِحَة الْكتاب وَسورَة وَلَا يقْعد ثمَّ يقوم فَيقْرَأ فَاتِحَة الْكتاب وَحدهَا وَيقْعد فيتشهد وَيسلم ويروى عَن أبي هُرَيْرَة وَأنس بن مَالك أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ صل مَا أدْركْت وأقض مَا سَبَقَك','2','260',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (616,'٨٦٨ - قلت فَكَأَنَّهُ يتَشَهَّد ثَلَاث مَرَّات قَالَ الأولى إِنَّمَا يتبع الإِمَام ويروى عَن عَليّ يقْرَأ فِيمَا أدْرك وَقَالَ ابْن عمر يقْرَأ فِيمَا يقْضِي وَقَالَ ابْن مَسْعُود مَا أدْركْت مَعَ الإِمَام فَهُوَ آخر صَلَاتك','2','261',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (617,'<span data-type="title" id=toc-599>مَسْأَلَة فِي ركُوب المحامل</span>

٨٦٩ - وَسُئِلَ عَن المحامل فَقَالَ قد ركبهَا الْعلمَاء ورخصوا فِيهَا
<span data-type="title" id=toc-600>معنى الشَّفق</span>

٨٧٠ - وَسُئِلَ عَن الشَّفق فَقَالَ أما فِي الْحَضَر حَتَّى يذهب الْبيَاض وَفِي السّفر إِذا ذهبت الْحمرَة
<span data-type="title" id=toc-601>التَّوْفِيق بَين حديثين ظاهرهما التَّعَارُض</span>

٨٧١ - قَالَ أبي سَأَلت عبد الرَّحْمَن بن مهْدي عَمَّا يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه كَانَ إِذا بعث بِالْهَدْي لم يمسك عَن شَيْء يمسك عَنهُ الْمحرم وَعَن قَوْله إِذا دخل الْعشْر وَأَرَادَ أَن يُضحي فَلَا يَأْخُذ من شعره وَلَا من بشره فَلم يجبني عبد الرَّحْمَن بِشَيْء وَسكت فَسَأَلت','2','262',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (618,'يحيى بن سعيد فَقَالَ لهَذَا وَجه وَلِهَذَا وَجه قَالَ وَلِهَذَا أَمْثَال وَأَشْبَاه فِي السّنَن نهى النَّبِي صلى الله عَلَيْهِ وَسلم حكيما أَن يَبِيع مَا لَيْسَ عِنْده وَأذن فِي السّلم وَالسّلم بيع مَضْمُون إِلَى أجل فَلَو رد أحد الْحَدِيثين بِالْآخرِ فَيَقُول قد نهى النَّبِي صلى الله عَلَيْهِ وَسلم عَن بيع مَا لَيْسَ عنْدك وَالسّلم بيع مَا لَيْسَ عنْدك فَهُوَ مَرْدُود لم يجز ذَلِك وَيُعْطى هَذَا وَجهه وَذَلِكَ وَجهه فَيجوز السّلم وَلَا يجوز أَن يَبِيع مَا','2','263',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (619,'لَيْسَ عِنْده وَنهى عَن الصَّلَاة بعد الْعَصْر وَقَالَ من أدْرك من صَلَاة الْعَصْر رَكْعَة قبل أَن تغرب الشَّمْس فقد أدْركهَا فَلهَذَا وَجه وَلِهَذَا وَجه لَا يبتدىء صَلَاة بعد الْعَصْر مُتَطَوعا فَإِذا أدْرك رَكْعَة من عصر يَوْمه فقد أدْرك وَكَذَلِكَ لَو ذكر صَلَاة عصر فَاتَتْهُ صلاهَا بعد مَا يُصَلِّي الْعَصْر لقَوْله من نَام عَن صَلَاة أَو نَسِيَهَا فليصلها إِذا ذكرهَا وَقَوله من بَاعَ شَاة مصراة فصاحبها بِالْخِيَارِ إِن شَاءَ','2','264',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (620,'أمْسكهَا وَإِن شَاءَ ردهَا وصاعا من تمر وَقَوله الْخراج بِالضَّمَانِ فَلهَذَا وَجه وَلِهَذَا وَجه إِذا اشْترى الشَّاة أَو النَّاقة الْمُصراة فحلبها فَإِن أَرَادَ ردهَا ورد مَعهَا صَاعا من تمر وَإِذا اشْترى عبدا فاستغله ثمَّ وجد','2','265',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (621,'بِهِ عَيْبا كَانَ لَهُ الْغلَّة بِالضَّمَانِ فَلهَذَا وَجه وَلِهَذَا وَجه وَمِنْه قَول النَّبِي صلى الله عَلَيْهِ وَسلم لفاطمة بنت أبي حُبَيْش إِذْ سَأَلته فَقَالَت إِنِّي أسْتَحَاض فَلَا أطهر أفأدع الصَّلَاة فَقَالَ إِنَّمَا ذَلِك عرق وَلَيْسَ بالحيضة فَإِذا أَقبلت الْحَيْضَة فدعي الصَّلَاة وَإِذا أَدْبَرت فأغسلي عَنْك الدَّم وَصلي وَقَالَ للَّتِي لَهَا أَيَّام مَعْلُومَة اجلسي قدر مَا كَانَت تحبسك حيضتك وَقَالَ لحمنة إِذْ قَالَت إِن دمي يثج فَقَالَ لَهَا تحيضي فِي علم الله سِتا أَو سبعا لِأَنَّهَا وصفت من دَمهَا مالم','2','266',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (622,'تصف فَاطِمَة فَحكم لكل وَاحِدَة مِنْهُمَا بِحكم فلهذه مَا قَالَ لَهَا ولهذه مَا قَالَ لَهَا وَلَا تضرب الْأَحَادِيث بَعْضهَا بِبَعْض يعْطى كل حَدِيث وَجهه
<span data-type="title" id=toc-602>إِذا احتال على رجل فأفلس أَو مَاتَ هَذَا الرجل</span>

٨٧٢ - وَسَأَلته عَن الرجل يحتال على الرجل فيفلس أَو يَمُوت قَالَ إِذا احتال عَلَيْهِ فَلَيْسَ لَهُ أَن يرجع أذهب إِلَى حَدِيث أبي هُرَيْرَة أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ إِذا أُحِيل على مَلِيء فَليَحْتَلْ وَإِذا انْتقل ملكه فَكيف يرجع','2','267',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (623,'<span data-type="title" id=toc-603>حكم الرُّجُوع فِي الْهِبَة</span>

٨٧٣ - وَسَأَلته عَن الرجل يهب الْهِبَة هَل لَهُ أَن يرجع فِيهَا قبل أَن يعوض فَقَالَ إِذا وهب الرجل هبة فقبلها فَلَيْسَ للْوَاهِب أَن يرجع فِيهَا وَذَلِكَ لما يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم الرَّاجِع فِي هِبته كَالْكَلْبِ يعود فِي قيئه وَقَالَ بَعضهم كَالْكَلْبِ يعود فِيهِ وَرُوِيَ عَن ابْن عَبَّاس عَن النَّبِي صلى الله عَلَيْهِ وَسلم الْعَائِد فِي هِبته كالعائد فِي قيئه وَقَالَ قَتَادَة عَن سعيد بن الْمسيب عَن ابْن عَبَّاس عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ الْعَائِد فِي هِبته كالعائد فِي قيئه وَقَالَ ابْن','2','268',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (624,'عَبَّاس عَن النَّبِي صلى الله عَلَيْهِ وَسلم لَيْسَ لنا مثل السوء
<span data-type="title" id=toc-604>من اشْترى عَبْدَيْنِ بِالثّمن الْوَاحِد ثمَّ وجد أَحدهمَا معيبا أَو حرا</span>

٨٧٤ - قلت الرجل يَشْتَرِي الْعَبْدَيْنِ بِالثّمن الْوَاحِد صَفْقَة وَاحِدَة فيجد بِأَحَدِهِمَا الْعَيْب قَالَ يردهُ بِحِصَّتِهِ من الثّمن','2','269',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (625,'٨٧٥ - قلت الرجل يَشْتَرِي الْعَبْدَيْنِ فيجد أَحدهمَا حرا قَالَ يرجع بِقِيمَتِه من الثّمن لِأَن الْملك قد زَالَ عَن البَائِع
<span data-type="title" id=toc-605>إِذا اشْترى الدَّرَاهِم بالدينار فَوجدَ بِأَحَدِهِمَا عَيْبا</span>

٨٧٦ - قلت الرجل يَشْتَرِي الدَّرَاهِم بالدينار فَيخرج مِنْهَا الدِّرْهَم الزائف والستوق فَقَالَ أما الْحسن وَقَتَادَة فَقَالَا لَهُ أَن يسْتَبْدل وَقَالَ مَالك يرجع هَذَا بديناره وَيرجع هَذَا بدراهمه كَأَنَّهُ ذهب إِلَى أَن العقد على فَسَاد وَقَالَ غير مَالك يرد الستوق وَيكون شَرِيكه فِي الدِّينَار بِقدر ذَلِك وَأَرْجُو أَن يكون الْأَمر فِيهِ سهلا','2','270',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (626,'<span data-type="title" id=toc-606>إِذا تلف المَال بعد وجوب الزَّكَاة فِيهِ</span>

٨٧٧ - قلت الرجل يكون فِي يَده المَال قد وَجَبت فِيهِ الزَّكَاة ثمَّ يتْلف هَل يجب عَلَيْهِ الزَّكَاة قَالَ أما أَنا فَيُعْجِبنِي أَن يُزكي','2','271',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (627,'وَقَالَ بعض النَّاس إِذا كَانَت عِنْده مِائَتَا دِرْهَم فَسرق مِنْهَا مائَة دِرْهَم يُزكي مَا بَقِي فِي يَدَيْهِ
<span data-type="title" id=toc-607>حكم الزَّكَاة فِي التبر والحلي من الذَّهَب وَالْفِضَّة</span>

٨٧٨ - قلت التبر من الذَّهَب وَالْفِضَّة هَل تجب فِيهِ الزَّكَاة قَالَ يجب فِيهِ الزَّكَاة إِلَّا الْحلِيّ الَّذِي يعار ويلبس
<span data-type="title" id=toc-608>مِقْدَار جلدات التَّعْزِير وَحكم الْحَبْس فِي الدّين</span>

٨٧٩ - قَالَ يرْوى عَن أبي بردة بن نيار عَن النَّبِي صلى الله عَلَيْهِ وَسلم لَا','2','272',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (628,'يجلد فَوق عشر جلدات إِلَّا فِي حد وَقد رُوِيَ عَن عمر وَعلي خلاف ذَلِك جازا بِهِ الْعشْرَة','2','273',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (629,'٨٨٠ - قلت الْحَبْس فِي الدّين قَالَ يحبس فِي الدّين
<span data-type="title" id=toc-609>شَهَادَة أهل الْكتاب بَعضهم على بعض</span>

٨٨١ - قلت شَهَادَة الْيَهُود وَالنَّصَارَى بَعضهم على بعض قَالَ لَا تجوز شَهَادَة أحد من أهل الشّرك بَعضهم على بعض وَلَا','2','274',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (630,'على الْمُسلمين قَالَ الله تَعَالَى {مِمَّن ترْضونَ من الشُّهَدَاء}
<span data-type="title" id=toc-610>الرّجلَانِ يدعيان شَيْئا وَهُوَ فِي أَيْدِيهِمَا جَمِيعًا</span>

٨٨٢ - قلت الرجلَيْن يدعيان الشَّيْء وَهُوَ فِي أَيْدِيهِمَا جَمِيعًا قَالَ إِذا كَانَ الشَّيْء فِي أَيْدِيهِمَا فادعياه جَمِيعًا فَهُوَ بَينهمَا نِصْفَيْنِ فَإِن ادّعى أَحدهمَا الْكل وَادّعى الآخر النّصْف فَهُوَ بَينهمَا نِصْفَيْنِ لكينونة الشَّيْء فِي أَيْدِيهِمَا وَمن النَّاس من يَقُول للَّذي ادّعى الْكل ثَلَاثَة أَربَاع وللذي ادّعى النّصْف الرّبع','2','275',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (631,'وَإِذا لم تكن السّلْعَة فِي أَيْدِيهِمَا فادعياها وَأَقَامَا الْبَيِّنَة جَمِيعًا أَقرع بَيْنَمَا على الْيَمين فَأَيّهمَا أَصَابَته الْقرعَة حلف وَكَانَت السّلْعَة لَهُ
<span data-type="title" id=toc-611>حكم الْمُزَارعَة على النّصْف وَالثلث وَالطَّعَام وَالدَّرَاهِم</span>

٨٨٣ - قلت الْمُزَارعَة على النّصْف وَالثلث وَالطَّعَام وَالدَّرَاهِم قَالَ لَا بَأْس بِهِ على النّصْف وَالثلث إِذا كَانَ الدَّاخِل يعْمل فِيهِ بحديده وبقره وَيكون الْبذر من صَاحب الأَرْض فَيعْمل الدَّاخِل فِيهَا كَمَا يعْمل الْمضَارب فِي المَال','2','276',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (632,'<span data-type="title" id=toc-612>إِذا أَقَامَ أحد الْخَصْمَيْنِ شَاهِدين وَالْآخر أَرْبَعَة</span>

٨٨٤ - قلت الرجلَيْن يدعيان الشَّيْء فيقيم أَحدهمَا شَاهِدين وَالْآخر أَرْبَعَة قَالَ فِيهَا اخْتِلَاف
<span data-type="title" id=toc-613>أهل الْبَيْت والقرابة فِي الْوَصِيَّة</span>

٨٨٥ - قلت الرجل يُوصي لأهل بَيته أَو لِقَرَابَتِهِ أَو لجنسه من هم فَإِن مَاتَ بَعضهم بعد الْمَيِّت قبل أَن تقسم الْوَصِيَّة أَيكُون لَهُ وَصِيَّة قَالَ أما الْقَرَابَة فَلَا يجاز بهم أَرْبَعَة آبَاء لِأَن النَّبِي صلى الله عَلَيْهِ وَسلم','2','277',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (633,'قسم سهم ذِي الْقُرْبَى فِي بني هَاشم وَبني الْمطلب وَلم يعد بِهِ هَؤُلَاءِ','2','278',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (634,'وَقد وَجب لكل من أوصى لَهُ إِذا كَانَ حَيا يَوْم يُوصي لَهُ
<span data-type="title" id=toc-614>من أحدث فِي الْقعدَة الْأَخِيرَة بعد التَّشَهُّد</span>

٨٨٦ - قلت الرجل يقْعد فِي الرَّكْعَة الْأَخِيرَة بعد التَّشَهُّد ثمَّ يحدث قَالَ هُوَ فِي الصَّلَاة مالم يخرج مِنْهَا بالتحليل وَهُوَ التَّسْلِيم وَمَا أفسد أَولهَا أفسد آخرهَا
<span data-type="title" id=toc-615>عدد التَّكْبِيرَات فِي الْعِيدَيْنِ</span>

٨٨٧ - قلت التَّكْبِير فِي الْعِيدَيْنِ قَالَ فِي الرَّكْعَة الأولى سبع ثمَّ يقْرَأ وَفِي الثَّانِيَة يكبر خمس ثمَّ يقْرَأ يبْدَأ بِالتَّكْبِيرِ فِي الرَّكْعَتَيْنِ جَمِيعًا','2','279',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (635,'<span data-type="title" id=toc-616>مسَائِل حَدِيثِيَّةٌ</span>

٨٨٨ - قلت حَدِيث أبي هُرَيْرَة من رِوَايَة خَالِد الْحذاء اني لأسبح فِي الْيَوْم وَاللَّيْلَة اثنى عشر ألف تَسْبِيحَة قدر ديتي هُوَ فِي الحَدِيث عَن أبي هُرَيْرَة أَو قَول عِكْرِمَة أَو مِمَّن دونه قَالَ الحَدِيث عَن أبي هُرَيْرَة حَدِيث الزُّهْرِيّ عَن ابْن أكيمَة عَن أبي هُرَيْرَة فِي الْقِرَاءَة فِي الصَّلَاة قَالَ فَانْتهى النَّاس عَن الْقِرَاءَة هُوَ فِي الحَدِيث عَن أبي هُرَيْرَة أَو من كَلَام الزُّهْرِيّ','2','280',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (636,'قَالَ أما عبد الرَّزَّاق فَحكى عَن معمر عَن الزُّهْرِيّ قَالَ سمع ابْن أكيمَة يحدث بِحَدِيث عَن أبي هُرَيْرَة أَن رَسُول الله صلى الله عَلَيْهِ وَسلم صلى صَلَاة جهر فِيهَا بِالْقِرَاءَةِ وَذكر الحَدِيث فَانْتهى النَّاس عَن الْقِرَاءَة مَعَ رَسُول الله صلى الله عَلَيْهِ وَسلم فِيمَا يجْهر بِهِ من الْقِرَاءَة حِين سمعُوا ذَلِك من رَسُول الله صلى الله عَلَيْهِ وَسلم وَقَالَ ابْن عُيَيْنَة فَذكر الحَدِيث وَقَالَ معمر عَن الزُّهْرِيّ فَانْتهى النَّاس فِي الْقِرَاءَة فِيمَا يجْهر بِهِ رَسُول الله صلى الله عَلَيْهِ وَسلم قَالَ سُفْيَان خفيت على هَذِه الْكَلِمَة وَقَالَ إِسْمَاعِيل عَن عبد الرَّحْمَن بن إِسْحَاق عَن الزُّهْرِيّ وَذكر','2','281',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (637,'الحَدِيث فَانْتهى إِلَى قَوْله إِنِّي أَقُول مَا بالي أنازع الْقُرْآن فَلم يزدْ على هَذَا قالذي نرى أَن قَوْله فَانْتهى النَّاس عَن الْقِرَاءَة أَنه قَول الزُّهْرِيّ
٨٩٠ - قلت حَدِيث الزُّهْرِيّ عَن أبي سَلمَة عَن جَابر إِنَّمَا قضى رَسُول الله صلى الله عَلَيْهِ وَسلم بِالشُّفْعَة فِي كل مَا لم يقسم فَإِذا وَقعت الْحُدُود فَلَا شُفْعَة قَوْله فَإِذا وَقع الْحُدُود فى شُفْعَة فِي الحَدِيث عَن جَابر عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَو هُوَ من كَلَام أبي سَلمَة قَالَ معمر يَقُول عَن أبي سَلمَة عَن جَابر عَن النَّبِي صلى الله عَلَيْهِ وَسلم وَصَالح بن أبي الْأَخْضَر كَذَا يَقُول أَيْضا وَرَوَاهُ مَالك','2','282',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (638,'عَن الزُّهْرِيّ عَن سعيد بن الْمسيب وَأبي سَلمَة مُرْسلا قَالَا قضى رَسُول الله صلى الله عَلَيْهِ وَسلم بِالشُّفْعَة فِي كل مَا لم يقسم فَإِذا وَقعت الْحُدُود فَلَا شُفْعَة
٨٩١ - قلت حَدِيث عَاصِم عَن أبي وَائِل عَن عبد الله فِي قصَّة ابْن','2','283',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (639,'النواحة قَالَ فمضت السّنة أَن الرُّسُل لَا تقتل هُوَ فِي الحَدِيث عَن عبد الله أَو من كَلَام أبي وَائِل قَالَ كَذَا الحَدِيث
٨٩٢ - قلت حَدِيث دَاوُد عَن الشّعبِيّ عَن عَلْقَمَة عَن عبد الله فِي قصَّة لَيْلَة الْجِنّ قَالَ رَسُول الله صلى الله عَلَيْهِ وَسلم لَا تستنجوا بالعظام وَلَا بالبعر فَإِنَّهُ زَاد إخْوَانكُمْ من الْجِنّ هُوَ من قَول عَلْقَمَة عَن عبد الله أَو من قَول الشّعبِيّ قَالَ أما إِسْمَاعِيل بن إِبْرَاهِيم وَيحيى بن زَكَرِيَّا بن أبي زَائِدَة فَقَالَا جَمِيعًا قَالَ الشّعبِيّ وَلَيْسَ هُوَ فِي حَدِيث عَلْقَمَة سَأَلُوهُ الزَّاد وَكَانُوا من','2','284',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (640,'جن الجزيرة فَذكره الحَدِيث فال أبي فبلغني أَن حَفْص بن غياث حدث بِهِ فَجعله فِي حَدِيث عَلْقَمَة عَن عبد الله فنرى أَنه وهم وَهَذَا أثبت
٨٩٣ - قلت حَدِيث الزُّهْرِيّ عَن هِنْد بنت الْحَارِث عَن أم سَلمَة قَالَ كن','2','285',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (641,'النِّسَاء يشهدن مَعَ رَسُول الله صلى الله عَلَيْهِ وَسلم الصُّبْح فينصرفن متلففات بمروطهن مَا يعرفن من الْغَلَس قَالَت وَكَانَ النَّبِي صلى الله عَلَيْهِ وَسلم يمْكث قَلِيلا وَكَانُوا يرَوْنَ أَن ذَلِك كَيْمَا يتَقَدَّم النِّسَاء قبل الرِّجَال فِي الحَدِيث عَن أم سَلمَة أَو هُوَ من كَلَام الزُّهْرِيّ قَالَ رَوَاهُ معمر عَن الزُّهْرِيّ عَن هِنْد عَن أم سَلمَة قَالَت كَانَ رَسُول الله صلى الله عَلَيْهِ وَسلم إِذا سلم مكث قَلِيلا وَكَانُوا يرَوْنَ أَن ذَلِك كَيْمَا ينفذ النِّسَاء قبل الرِّجَال وَقَالَ إِبْرَاهِيم بن سعد قَالَ ابْن شهَاب فنرى وَالله أعلم أَن ذَلِك كَانَ لكَي ينْصَرف النِّسَاء قبل أَن يدركهن من انْصَرف من الْقَوْم
<span data-type="title" id=toc-617>شَهَادَة النِّسَاء فِيمَا لَا يطلع عَلَيْهِ الرِّجَال</span>

٨٩٤ - قلت مَا تجوز شَهَادَة النِّسَاء فِيمَا لَا يطلع عَلَيْهِ الرِّجَال قَالَ فِيهِ اخْتِلَاف كثير','2','286',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (642,'٨٩٥ - قلت الى أَي شَيْء تذْهب قَالَ دعها
<span data-type="title" id=toc-618>من تجب لَهُ الشُّفْعَة</span>

٨٩٦ - قلت الشُّفْعَة لمن تجب قَالَ أذهب إِلَى حَدِيث أبي سَلمَة عَن جَابر إِنَّهَا فِي كل مَا لم يقسم','2','287',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (643,'<span data-type="title" id=toc-619>من وَقع على امْرَأَته فِي نَهَار رَمَضَان مُتَعَمدا</span>

٨٩٧ - وَسَأَلته عَن الرجل يَقع على امْرَأَته فِي شهر رَمَضَان مُتَعَمدا قَالَ عَلَيْهِ الْكَفَّارَة على حَدِيث الزُّهْرِيّ الَّذِي يرويهِ عَن حميد أما سُفْيَان وَمعمر وَإِبْرَاهِيم بن سعد وَغَيرهم فَمَعْنَى حَدِيثهمْ أَنه','2','288',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (644,'تَجِد مَا تعْتق قَالَ لَا قَالَ تَسْتَطِيع أَن تَصُوم شَهْرَيْن مُتَتَابعين فَكَانَ معنى الحَدِيث على معنى الْمظَاهر وَأما ابْن جريج وَمَالك فَإِنَّهُمَا قَالَا أعتق أَو صم أَو تصدق روياه عَن الزُّهْرِيّ فَكَأَنَّهُ مُخَيّر وَكَذَا معنى حَدِيث عَائِشَة أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ أَيْن المحترق وأتى بعرق فِيهِ تمر قَالَ أطْعم هَذَا','2','289',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (645,'وَأما النَّاسِي فَإِن مُجَاهدًا وَالْحسن كَانَا يعذرانه وَقَالَ عَطاء لَيْسَ مثل هَذَا ينسى فَلم يعذرهُ وَقَالَ يُعجبنِي قَول عَطاء
<span data-type="title" id=toc-620>مسَائِل فِي التَّارِيخ وَالرِّجَال</span>

٨٩٨ - حَدثنَا صَالح قَالَ حَدثنَا أبي قَالَ حَدثنَا إِسْمَاعِيل بن إِبْرَاهِيم عَن','2','290',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (646,'مُحَمَّد بن إِسْحَاق عَن يزِيد بن أبي حبيب عَن مرْثَد بن عبد الله أبي الْخَيْر وَأما أَبُو عبد الله فَلم أقع عَلَيْهِ وَلَعَلَّ بَعضهم قَالَ أَبُو عبد الرَّحْمَن
٨٩٩ - قلت أَبُو ثَعْلَبَة الْخُشَنِي هُوَ أَبُو ثَعْلَبَة الْأَشْجَعِيّ قَالَ لَا أَظُنهُ هَذَا
٩٠٠ - قلت أم حُصَيْن الأحمسية قَالَ هِيَ جدة يحيى بن الْحصين الَّذِي يحدث عَنهُ شُعْبَة','2','291',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (647,'٩٠١ - وَقَالَ أم حَبِيبَة بنت جحش هِيَ أُخْت حمْنَة بنت جحش وَأم حبيب بنت جحش كَانَت تَحت عبد الرَّحْمَن بن عَوْف وَيُقَال أم حبيب وَأم حَبِيبَة وَحمْنَة كَانَت تَحت طَلْحَة وَزَيْنَب زَوْجَة النَّبِي صلى الله عَلَيْهِ وَسلم وَعبد الله بن جحش هُوَ الَّذِي شهد بَدْرًا وَبَعثه النَّبِي صلى الله عَلَيْهِ وَسلم فِي السّريَّة فَقَالَ إِنَّهَا أول سَرِيَّة بعثت وَعبيد الله بن جحش تنصر حِين خَرجُوا إِلَى النَّجَاشِيّ','2','292',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (648,'٩٠٢ - قَالَ أبي بَلغنِي أَن عُرْوَة بن الزبير قطعت رجله وَكَانَ يَدْعُو فَيَقُول لَئِن كنت أبليت فطالما عافيت وَإِن كنت أخذت فطالما أبقيت
٩٠٣ - قَالَ أبي وَبَلغنِي أَن عَامر بن عبد الله بن الزبير قَالَ مَا سَأَلت الله حَاجَة بعد موت أبي إِلَّا لأبي إِلَّا بعد سنة
٩٠٤ - سَمِعت أبي يَقُول صليت خلف إِبْرَاهِيم بن سعد غير مرّة فَكَانَ يسلم وَاحِدَة
٩٠٥ - قَالَ أبي ورأنى يَوْمًا وَأَنا أكتب فِي الألواح فَقَالَ لي أتكتب','2','293',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (649,'٩٠٦ - قَالَ أبي وَقَالَ ابْنه سعد فِي حَدِيث الزُّهْرِيّ عَن سعيد بن الْمسيب الماعون بِلِسَان قُرَيْش المَال فَأنكرهُ إِبْرَاهِيم وَقَالَ الزُّهْرِيّ مُرْسل فَقَالَ لَهُ سعد كنت حدثت بِهِ عَن سعيد فَأبى وَقَالَ لَا
٩٠٧ - قَالَ جَاءَ رجل من مَدِينَة أبي جَعْفَر شيخ فَقَالَ يَا أَبَا إِسْحَاق حَدثنِي قَالَ كَيفَ أحَدثك وَهَذَا هَاهُنَا قَالَ أبي وَكنت حاضره
٩٠٨ - سَمِعت أبي يَقُول قَالَ ابْن عون عَن مُحَمَّد بن سِيرِين قَالَ إِن أَمر خُرَاسَان ليهمني
٩٠٩ - قلت لَهُ من حَدثَك قَالَ عَفَّان قَالَ حَدثنَا سليم بن أَخْضَر عَن ابْن عون عَن مُحَمَّد','2','294',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (650,'قَالَ أبي وَمَا سمعته من أحد غير عَفَّان
٩١٠ - سَمِعت أبي يَقُول عبد الله بن شَدَّاد لم يسمع من النَّبِي صلى الله عَلَيْهِ وَسلم شَيْئا سمع من عَليّ وَمن عمر قَالَ سَمِعت نشيج عمر','2','295',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (651,'٩١١ - سَمِعت أبي يَقُول حَدثنَا يزِيد بن هَارُون عَن عبد الْملك بن أبي سُلَيْمَان عَن أنس بن سِيرِين قَالَ دَخَلنَا على زيد بن ثَابت
٩١٢ - قَالَ أبي مُحَمَّد بن سِيرِين سمع من أبي هُرَيْرَة وَابْن عمر وَأنس وَلم يسمع من ابْن عَبَّاس شَيْئا كلهَا يَقُول نبئت عَن ابْن عَبَّاس وَقد سمع من عمرَان بن حُصَيْن','2','296',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (652,'٩١٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا سيار قَالَ حَدثنَا جَعْفَر قَالَ حَدثنَا عباد بن عَمْرو قَالَ سَأَلت الْحسن قلت أَبَا سعيد مَا الْحور الْعين قَالَ هن عجائزكم هَؤُلَاءِ الدرد ينشئهن الله خلقا آخر فَقَالَ يُرِيد بن أبي مَرْيَم السَّلُولي لِلْحسنِ من حَدثَك هَذَا الحَدِيث يَا أَبَا سعيد قَالَ فحسر عَن كم قَمِيصه فَقَالَ حَدثنِي فلَان بن فلَان الْمُهَاجِرِي وحَدثني فلَان بن فلَان الْأنْصَارِيّ حَتَّى عد خَمْسَة من الْمُهَاجِرين وَأَرْبَعَة من الْأَنْصَار','2','297',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (653,'٩١٤ - قَالَ أبي أَبُو الْجلد جيلان بن فَرْوَة
٩١٥ - قَالَ أبي كتبنَا هَذَا من كتاب ابْن الْأَشْجَعِيّ عَن أَبِيه عَن سُفْيَان عبيد الْمكتب عَن فُضَيْل بن عَمْرو عَن الشّعبِيّ عَن أنس بن مَالك قَالَ كُنَّا مَعَ رَسُول الله صلى الله عَلَيْهِ وَسلم فَضَحِك حَتَّى بَدَت نَوَاجِذه فَقَالَ هَل تَدْرُونَ مِم أضْحك من مُخَاطبَة الرب عَبده يَوْم الْقِيَامَة قَالَ يَقُول يَا رب ألم تُجِرْنِي من الظُّلم قَالَ يَقُول','2','298',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (654,'بلَى قَالَ فَإِنِّي لَا أُجِيز عَليّ شَاهدا إِلَّا من نَفسِي قَالَ فَيَقُول كفى بِنَفْسِك الْيَوْم عَلَيْك شَهِيدا وَبِالْكِرَامِ الْكَاتِبين شُهُودًا قَالَ فيختم على فِيهِ فَيُقَال لِأَرْكَانِهِ انْطِقِي فَتَنْطِق بِأَعْمَالِهِ ثمَّ يخلى بَينه وَبَين الْكَلَام فَيَقُول بعدا لَكِن وَسُحْقًا عنكن كنت أُنَاضِل
<span data-type="title" id=toc-621>آخر الْجُزْء السَّابِع من أَجزَاء الشَّيْخ عَليّ القحطبي</span>

٩١٦ - حَدثنَا صَالح قَالَ قَالَ أبي وَابْن الْأَشْجَعِيّ أَعْطَانَا كتاب أَبِيه عَن سُفْيَان عَن الْمِقْدَام بن شُرَيْح عَن أَبِيه عَن جده قَالَ قلت يَا','2','299',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (655,'رَسُول الله دلَّنِي على عمل يدخلني الْجنَّة فَقَالَ إِن من مُوجبَات الْمَغْفِرَة بذل السَّلَام وَحسن الْكَلَام
٩١٧ - قَالَ أبي ونسخنا من كتاب الْأَشْجَعِيّ عَن سُفْيَان عَن عبد الْملك بن أبي سُلَيْمَان الْفَزارِيّ وَهُوَ الْعَرْزَمِي عَن أنس بن سِيرِين قَالَ قَالَ رَأَيْت على ابْن زيد بن ثَابت إزارا ورداء وعمامة لَيْسَ عَلَيْهِ قَمِيص
٩١٨ - سَمِعت أبي يَقُول زِيَاد بن أبي مُسلم وَيَقُولُونَ ابْن مُسلم وَهُوَ أَبُو عمر الْفراء ثِقَة ثِقَة رجل صَالح','2','300',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (656,'٩١٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أسود بن عَامر قَالَ حَدثنَا شُعْبَة قَالَ عبد الله بن دِينَار أَخْبرنِي قَالَ سَمِعت ابْن عمر يحدث عَن النَّبِي صلى الله عَلَيْهِ وَسلم فِي لَيْلَة الْقدر قَالَ من كَانَ متحريا فليتحرها فِي لَيْلَة سبع وَعشْرين قَالَ شُعْبَة وَذكر لي رجل ثِقَة عَن سُفْيَان أَنه كَانَ يَقُول إِنَّمَا قَالَ من كَانَ متحريا فليتحرها فِي السَّبع الْبَوَاقِي قَالَ شُعْبَة فَلَا أَدْرِي قَالَ ذَا أَو ذَا','2','301',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (657,'قَالَ أبي أَظن أَن هَذَا الرجل الثِّقَة يحيى بن سعيد الْقطَّان
<span data-type="title" id=toc-622>دُعَاء سعد وَعلي على أهل الْكُوفَة</span>

٩٢٠ - سَمِعت أبي يَقُول قَالَ أَبُو أُسَامَة دَعَا عَلَيْهِم رجلَانِ صالحان من أهل بدر وَقَالَ مرّة قد شَهدا بَدْرًا على أهل الْكُوفَة سعد وَعلي','2','302',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (658,'<span data-type="title" id=toc-623>تَفْضِيل أهل الْكُوفَة عليا على عُثْمَان</span>

٩٢١ - قَالَ أبي أهل الْكُوفَة كلهم يفضلون عليا على عُثْمَان إِلَّا رجلَيْنِ طَلْحَة بن مصرف وَعبد الله بن إِدْرِيس
٩٢٢ - قلت لَهُ زبيد قَالَ لَا كَانَ يحب عليا أَي كَأَنَّهُ يفضله على عُثْمَان
<span data-type="title" id=toc-624>صَاحب الْبَيْت أَحَق بِالْإِمَامَةِ وَإِن كَانَ عبدا</span>

٩٢٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو مُعَاوِيَة قَالَ حَدثنَا','2','303',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (659,'دَاوُد عَن أبي نَضرة عَن أبي سعيد مولى أبي أسيد قَالَ تزوج وَكَانَ عبدا فحضره عبد الله بن مَسْعُود وَأَبُو ذَر وَحُذَيْفَة وَغَيرهم من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم فَحَضَرت الصَّلَاة فقدموه هُوَ','2','304',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (660,'مَمْلُوك ثمَّ قَالُوا لَهُ إِذا دخلت على أهلك فصل رَكْعَتَيْنِ ثمَّ خُذ بِرَأْس أهلك فَقل اللَّهُمَّ بَارك لي فِي أَهلِي وَبَارك لأهلي فِي وارزقهم مني وارزقني مِنْهُم ثمَّ شَأْنك وشأن أهلك
٩٢٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا ابْن فُضَيْل قَالَ حَدثنَا دَاوُد بن أبي هِنْد عَن أبي نَضرة عَن أبي سعيد مولى أبي أسيد قَالَ تزوجت وَأَنا عبد مَمْلُوك فدعوت نَاسا من أَصْحَاب رَسُول الله صلى الله عَلَيْهِ وَسلم فيهم أَبُو ذَر وَأَبُو مَسْعُود قَالَ أبي وَهُوَ خطأ إِنَّمَا هُوَ ابْن مَسْعُود وَحُذَيْفَة فَحَضَرت الصَّلَاة فَتقدم أَبُو ذَر فَقَالُوا لَهُ وَرَاءَك فَالْتَفت إِلَى أَصْحَابه فَقَالَ أَكَذَلِك قَالُوا لَهُ نعم فقدموني نَحوا من حَدِيث أبي مُعَاوِيَة قَالَ أبي فِيهِ أَنهم أجابوا مَمْلُوكا وقدموه لِأَنَّهُ صَاحب الْبَيْت','2','305',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (661,'<span data-type="title" id=toc-625>من نسي الْقُنُوت فِي الْوتر هَل عَلَيْهِ سجدتا السَّهْو</span>

٩٢٥ - قَالَ أبي سَأَلت إِسْمَاعِيل عَمَّن نسي الْقُنُوت فِي الْوتر هَل عَلَيْهِ سجدتا السَّهْو قَالَ مَا أرى عَلَيْهِ ذَلِك قَالَ وَسَأَلت هشيما عَن ذَلِك فَقَالَ يعجبنا أَن يسْجد لذَلِك سَجْدَتي السَّهْو','2','306',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (662,'<span data-type="title" id=toc-626>تجهيز أَيُّوب من أحجه بِكُل مَا يحْتَاج إِلَيْهِ</span>

٩٢٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْمَاعِيل قَالَ بَلغنِي أَن أَيُّوب أحج رجلا فجهزه بِكُل شَيْء يحْتَاج إِلَيْهِ حَتَّى صنع لَهُ سفرة
<span data-type="title" id=toc-627>اخْتِيَار أَيُّوب أَن يُكفن فِيمَا قد صلى فِيهِ</span>

٩٢٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْمَاعِيل قَالَ بَلغنِي أَن أَيُّوب كَانَ يخْتَار أَن يُكَفِّنهُ فِيمَا قد صلى فِيهِ
<span data-type="title" id=toc-628>قَول أَيُّوب فِيمَن يحجّ عَن الْمَيِّت يخرج كَمَا دخل</span>

٩٢٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْمَاعِيل قَالَ بَلغنِي أَن أَيُّوب كَانَ يَقُول فِي الَّذِي يحجّ عَن الْمَيِّت فيفضل مَعَه الْفضل قَالَ يخرج كَمَا دخل','2','307',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (663,'<span data-type="title" id=toc-629>سُلَيْمَان التَّيْمِيّ كَانَ يجْهر بِالتَّسْمِيَةِ</span>

٩٢٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْمَاعِيل قَالَ كَانَ سُلَيْمَان التَّيْمِيّ يجْهر بِبسْم الله الرَّحْمَن الرَّحِيم
<span data-type="title" id=toc-630>آخر الْجُزْء الثَّانِي عشر من أَجزَاء صَالح</span>

<span data-type="title" id=toc-631>من تراجم بعض الرِّجَال</span>

٩٣٠ - حَدثنَا صَالح قَالَ سَمِعت أبي يَقُول كَانَ سُلَيْمَان بن يسَار مولى لميمونة بنت الْحَارِث خَالَة ابْن عَبَّاس
٩٣١ - قَالَ أبي اسْم أبي عوَانَة وضاح وَكَانَ أَصله من وَاسِط ثمَّ انه نزل الْبَصْرَة وَكَانَ مولى ليزِيد بن عَطاء الْبَزَّاز
٩٣٢ - قلت الْمِقْدَاد بن الْأسود هُوَ الْمِقْدَاد بن عَمْرو قَالَ نعم','2','308',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (664,'٩٣٣ - قلت الْمِقْدَام أَبُو كَرِيمَة هُوَ الْمِقْدَام بن معدي كرب قَالَ نعم
٩٣٤ - قلت جُنْدُب بن سُفْيَان هُوَ جُنْدُب بن عبد الله العلقي حَيّ من بجيلة قَالَ نعم كَانَ يكون بِالْكُوفَةِ ثمَّ قدم الْبَصْرَة فروى عَنهُ أهل الْكُوفَة وَأهل الْبَصْرَة
٩٣٥ - قلت عَمْرو بن حُرَيْث الْكُوفِي هُوَ عَمْرو بن حُرَيْث الَّذِي رووا عَنهُ أهل الشَّام','2','309',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (665,'قَالَ لَيْسَ هَذَا الْكُوفِي الَّذِي يروي عَنهُ أهل مصر ذَاك غير هَذَا
٩٣٦ - قلت جَابر بن عبد الله هُوَ جَابر بن عبد الله الَّذِي يحدث عَنهُ أَبُو سَلمَة عَن جَابر عَن النَّبِي صلى الله عَلَيْهِ وَسلم مر بِي جِبْرِيل فَضَحكت إِلَيْهِ فَتَبَسَّمَ إِلَيّ قَالَ نعم هُوَ الَّذِي روى عَنهُ أَبُو سَلمَة وروى عَنهُ عَطاء وَكَانَ','2','310',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (666,'مجاورا بِمَكَّة وروى عَنهُ مُجَاهِد وَابْن الْمُنْكَدر وَأَبُو سُفْيَان
<span data-type="title" id=toc-632>مِقْدَار الدِّيَة من الذَّهَب وَالْوَرق وَالْإِبِل</span>

٩٣٧ - قلت الدِّيَة كم هِيَ من الذَّهَب وَالْوَرق وَالْإِبِل قَالَ من الْوَرق اثْنَا عشر ألفا وَهُوَ أَكثر مَا جَاءَ فِيهِ رَوَاهُ عِكْرِمَة عَن النَّبِي صلى الله عَلَيْهِ وَسلم الدِّيَة اثْنَا عشر وَمن الذَّهَب ألف دِينَار وَمن الْإِبِل مائَة','2','311',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (667,'<span data-type="title" id=toc-633>من ملك ذَا رحم محرم</span>

٩٣٨ - قلت الرجل يملك ذَا رحم محرم قَالَ فِيهَا اخْتِلَاف','2','312',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (668,'<span data-type="title" id=toc-634>شَهَادَة العَبْد</span>

٩٣٩ - قلت شَهَادَة العَبْد قَالَ فِيهَا اخْتِلَاف','2','313',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (669,'<span data-type="title" id=toc-635>إِذا قتل العَبْد سَيّده</span>

٩٤٠ - قلت العَبْد يقتل سَيّده قَالَ إِن شَاءَ الْوَرَثَة قَتَلُوهُ وَإِن شَاءُوا عَفوه
<span data-type="title" id=toc-636>حكم جُلُود الْميتَة إِذا دبغت</span>

٩٤١ - قلت جُلُود الْميتَة إِذا دبغت قَالَ لَا يُعجبنِي وأذهب فِيهِ إِلَى حَدِيث عبد الله بن عكيم','2','314',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (670,'<span data-type="title" id=toc-637>إِذا أعتق أحد الشَّرِيكَيْنِ نصِيبه من العَبْد</span>

٩٤٢ - قلت العَبْد يكون بَين الرجلَيْن فَيعتق أَحدهمَا نصِيبه وَهُوَ مُوسر ثمَّ اخْتَار الآخر الْعتْق أَيْضا قَالَ إِذا أعتق وَهُوَ مُوسر عتق فِي مَاله وَكَانَ الْوَلَاء لَهُ
<span data-type="title" id=toc-638>إِذا شهد أحد الشَّاهِدين على عشرَة وَالْآخر على عشْرين</span>

٩٤٣ - قلت الشَّاهِدَانِ يَخْتَلِفَانِ فَيشْهد أَحدهمَا على عشرَة وَالْآخر على عشْرين قَالَ تجوز شَهَادَة الَّذِي شهد على عشْرين مَعَ يَمِين الطَّالِب','2','316',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (671,'<span data-type="title" id=toc-639>حكم شِرَاء الطَّعَام وَنَحْوه بِمَال الزَّكَاة وَالتَّصَدُّق بِهِ</span>

٩٤٤ - قلت الرجل يَشْتَرِي من زَكَاته الطَّعَام أَو الْكسْوَة فَيتَصَدَّق بهَا قَالَ يُعْطي كَمَا يجب من الْوَرق وَغير ذَلِك
<span data-type="title" id=toc-640>مِقْدَار كَفَّارَة الْيَمين</span>

٩٤٥ - قلت كَفَّارَة الْيَمين قَالَ مُدبر أَو نصف صَاع تمر
<span data-type="title" id=toc-641>مَا يجب فِي النّذر</span>

٩٤٦ - قلت النّذر مَا يجب فِيهِ إِذا كَانَ طَاعَة أَو مَعْصِيّة قَالَ أما فِي المَال إِذا قَالَ إِن بَرِئت من مرضِي أَو سلمت من سَفَرِي أَو قدم أبي أَو أخي سالما فَمَا لي فِي الْمَسَاكِين يجْزِيه من ذَلِك الثُّلُث لقَوْل النَّبِي صلى الله عَلَيْهِ وَسلم لأبي لبَابَة إِذْ قَالَ لَهُ إِن من تَوْبَتِي أَن أَنْخَلِع','2','317',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (672,'من مَالِي وأهجر دَار قومِي فَقَالَ يجْزِيك الثُّلُث وَإِذا كَانَ مَعْنَاهُ معنى الْيَمين فكفارة يَمِين فِي المَال وَلَا يكون ذَلِك فِي الْعتْق وَلَا الطَّلَاق وَرُوِيَ عَن ابْن عَبَّاس وَابْن عمر أَنَّهُمَا أوجبا الْعتْق
<span data-type="title" id=toc-642>من نذر نذرا وَلم يسمه</span>

٩٤٧ - قلت من نذر نذرا وَلم يسمه قَالَ كَانَ ابْن عَبَّاس يَقُول عَلَيْهِ أغْلظ الْكَفَّارَات','2','318',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (673,'وَقَالَ غير وَاحِد من التَّابِعين كَفَّارَة يَمِين
<span data-type="title" id=toc-643>مَتى تجب الزَّكَاة فِي المَال الْمُسْتَفَاد</span>

٩٤٨ - قلت الْفَائِدَة من المَال يضم بَعْضهَا إِلَى بعض قَالَ لَا يضم بَعْضهَا إِلَى بعض مَا كَانَ من مِيرَاث أَو صَدَقَة أَو هبة أَو عَطاء فَلَا يُزكي حَتَّى يحول عَلَيْهِ الْحول إِلَّا أَن يكون تَاجر قد زكى مَاله ثمَّ ربح فَإِنَّهُ يُزكي الرِّبْح مَعَ مَاله وَذَلِكَ لقَوْل عمر إِذْ مر على صَاحب الجعاب والأدم فَقَالَ قوم وزك وَذَلِكَ لِأَن نماءها مِنْهَا وَكَذَلِكَ فِي الْإِبِل وَالْبَقر وَالْغنم إِذا توالدت فَإِنَّهُ يزكيها صغارها وكبارها','2','319',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (674,'<span data-type="title" id=toc-644>آثَار فِي نِكَاح الْكِتَابِيَّة</span>

٩٤٩ - حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا سعيد عَن قَتَادَة أَن حُذَيْفَة بن الْيَمَان وَطَلْحَة بن عبيد الله والجارود بن المعلي وأذينة الْعَبْدي تزوج كل وَاحِد مِنْهُم امْرَأَة من أهل الْكتاب فَقَالَ لَهُم عمر بن الْخطاب طلقوهن فطلقوا كلهم إِلَّا حُذَيْفَة فَقَالَ لَهُ عمر طَلقهَا قَالَ تشهد أَنَّهَا حرَام قَالَ هِيَ جَمْرَة طَلقهَا هِيَ جَمْرَة طَلقهَا قَالَ تشهد أَنَّهَا حرَام قَالَ هِيَ جَمْرَة قَالَ لقد علمت أَنَّهَا جَمْرَة وَلكنهَا لي حَلَال فَأبى أَن يطلقهَا فَلَمَّا كَانَ بعد طَلقهَا فَقيل لَهُ أَلا كنت طَلقتهَا حِين أَمرك عمر قَالَ لَا كرهت أَن يظنّ النَّاس أَنِّي ركبت أمرا لَا يَنْبَغِي لي','2','320',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (675,'٩٥٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ حَدثنَا الصَّلْت بن بهْرَام عَن أبي وَائِل قَالَ تزوج حُذَيْفَة يَهُودِيَّة من أهل الْمَدَائِن فَكتب إِلَيْهِ عمر طَلقهَا فَكتب إِلَيْهِ حُذَيْفَة حرَام ترَاهَا قَالَ لَا وَلَكِنِّي خفت أَن تتعاطوا المومسات مِنْهُنَّ يَعْنِي الفواجر','2','321',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (676,'٩٥١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن شُعْبَة عَن الحكم عَن جَار لِحُذَيْفَة أَن حُذَيْفَة تزوج يَهُودِيَّة وَعِنْده عربيتان
<span data-type="title" id=toc-645>حد من شرب الْخمر فِي رَمَضَان</span>

٩٥٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ حَدثنَا سُفْيَان عَن عَطاء بن أبي مَرْوَان أبي مُصعب الْأَسْلَمِيّ عَن أَبِيه أَن عليا أُتِي بالنجاشي سَكرَان من الْخمر فِي رَمَضَان قَالَ فَضَربهُ ثَمَانِينَ ثمَّ أَمر','2','322',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (677,'بِهِ إِلَى السجْن ثمَّ أخرجه من الْغَد فَضَربهُ عشْرين ثمَّ قَالَ إِنَّمَا ضربتك هَذِه الْعشْرين لجرأتك على الله وإفطارك فِي رَمَضَان قَالَ أبي أذهب إِلَيْهِ قَالَ أبي شُعْبَة لم يسمع هَذَا من عَطاء بن أبي مَرْوَان سَمعه من رجل عَنهُ
<span data-type="title" id=toc-646>من حلف بِشرب أَو أكل شَيْء فَضَاعَ</span>

٩٥٣ - سَأَلت أبي الرجل يحلف أَن يشرب هَذَا المَاء الَّذِي فِي الْكوز فانصب قَالَ يَحْنَث وَكَذَا إِن حلف أَن يَأْكُل هَذَا الرَّغِيف فجَاء كلب فَأَكله قَالَ يَحْنَث لِأَن هَذَا شَيْء لَا يقدر عَلَيْهِ','2','323',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (678,'<span data-type="title" id=toc-647>تَعْلِيق رجلَيْنِ الطَّلَاق على أَمر لَا يدرى من وجد فِيهِ مِنْهُمَا</span>

٩٥٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا جرير عَن عَطاء بن السَّائِب قَالَ سُئِلَ الشّعبِيّ عَن رجل قَالَ الآخر إِنَّك لحسود قَالَ الآخر أحسدنا امْرَأَته طَالِق ثَلَاثًا قَالَ الآخر نعم قَالَ قد خبتما وخسرتما وَبَانَتْ مِنْكُمَا امرأتاكما جَمِيعًا
٩٥٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا جرير عَن مُغيرَة عَن','2','324',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (679,'الْحَارِث قَالَ أدينهما وآمرهما بتقوى الله وَأَقُول أَنْتُمَا أعلم بِمَا حلفتما عَلَيْهِ قَالَ وَبَاب التديين فِي هَذَا وأشباهه قَالَ أبي هَذَا شَيْء لَا يدْرك قد ألقاهما فِي التَّهْلُكَة
<span data-type="title" id=toc-648>شرب النَّجَاشِيّ الْخمر فِي رَمَضَان وَجلد عَليّ إِيَّاه</span>

٩٥٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا','2','325',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (680,'شُعْبَة عَن غيلَان بن جَامع قَالَ كَانَ على قَضَاء الْكُوفَة أَنه سمع عَطاء بن أبي مَرْوَان يحدث عَن أَبِيه أَن عليا أَتَى بالنجاشي قد شرب خمرًا فِي رَمَضَان فجلده ثَمَانِينَ الْحَد وَعشْرين لإفطاره فِي رَمَضَان فَقَالَ النَّجَاشِيّ
(إِذا سقى الله قوما صوب غادية ... فَلَا سقى الله أهل الْكُوفَة الْمَطَر)
(ضربوني ثمَّ قَالُوا قدر ... قدر الله لَهُم شَرّ الْقدر)
<span data-type="title" id=toc-649>دُعَاء سعد بن أبي وَقاص على رجلَيْنِ</span>

٩٥٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن أبي بلج قَالَ سَمِعت مُصعب بن سعد أَن سَعْدا كَاتب','2','326',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (681,'غُلَاما لَهُ فَأَرَادَ مِنْهُ شَيْئا فَقَالَ مَا عِنْدِي مَا أُعْطِيك وَعمد إِلَى دَنَانِير فَجَعلهَا فِي نَعله فَدَعَا سعد عَلَيْهِ فسرقت نعلاه
٩٥٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن أبي بلج عَن مُصعب بن سعد عَن سعد أَن رجلا نَالَ من عَليّ بن أبي طَالب فَدَعَا عَلَيْهِ سعد بن مَالك فَجَاءَت نَاقَة أَو جمل فَقتله قَالَ شُعْبَة فَأرَاهُ قد قَالَ فَحلف سعد أَن لَا يَدْعُو على أحد وَأَحْسبهُ قَالَ وَأعْتق نسمَة قَالَ أبي سعد بن مَالك هُوَ سعد بن أبي وَقاص كَانَ كنية مَالك أَبُو وَقاص
<span data-type="title" id=toc-650>إِذا علق الطَّلَاق على أكل الْعرق فانتهزته السنور</span>

٩٥٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا قرَان بن تَمام أَبُو تَمام الْكُوفِي','2','327',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (682,'كَانَ حسن الْهَيْئَة وَكَانَ فَارِسًا قَالَ أخبرنَا عمر بن بشير كُوفِي وَكَانَ أَبُو النَّضر كثير الرِّوَايَة عَنهُ قَالَ سُئِلَ الشّعبِيّ عَن رجل كَانَت مَعَه امْرَأَة جالسة على الخوان فَقَالَ إِن لم تأكلي هَذَا الْعرق فَأَنت طَالِق ثَلَاثًا فَجَاءَت السنور فانتهزته قَالَ هُوَ كَمَا قَالَ
<span data-type="title" id=toc-651>قتل مُعَاوِيَة حجر بن عدي وَأَصْحَابه</span>

٩٦٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو الْمُغيرَة قَالَ حَدثنَا ابْن عَيَّاش قَالَ حَدثنِي شُرَحْبِيل بن مُسلم قَالَ لما بعث بِحجر بن','2','328',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (683,'عدي بن الأدبر وَأَصْحَابه من الْعرَاق إِلَى مُعَاوِيَة بن أبي سُفْيَان اسْتَشَارَ النَّاس فِي قَتلهمْ فَمنهمْ المشير وَمِنْهُم السَّاكِت فَدخل مُعَاوِيَة إِلَى منزله فَلَمَّا صلى الظّهْر قَامَ فِي النَّاس خَطِيبًا فَحَمدَ الله وَأثْنى عَلَيْهِ ثمَّ جلس على منبره فَقَامَ الْمُنَادِي فَنَادَى أَيْن عَمْرو بن الْأسود الْعَنسِي فَقَامَ فَحَمدَ الله وَأثْنى عَلَيْهِ ثمَّ قَالَ أَلا إِنَّا بحصن من الله حُصَيْن لم نؤمر بِتَرْكِهِ وقولك يَا أَمِير الْمُؤمنِينَ فِي أهل الْعرَاق أَلا وَأَنت الرَّاعِي وَنحن الرّعية أَلا وَأَنت أعلمنَا بدائهم وأقدرنا على دوائهم وَإِنَّمَا علينا أَن نقُول {سمعنَا وأطعنا غفرانك رَبنَا وَإِلَيْك الْمصير} فَقَالَ مُعَاوِيَة أما عَمْرو بن الْأسود فقد تَبرأ إِلَيْنَا من دِمَائِهِمْ وَرمى بهَا مَا بَين عَيْني','2','329',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (684,'مُعَاوِيَة ثمَّ قَامَ الْمُنَادِي فَنَادَى أَيْن أَبُو مُسلم الْخَولَانِيّ فَقَامَ فَحَمدَ الله وَأثْنى عَلَيْهِ ثمَّ قَالَ أما بعد فَلَا وَالله مَا أبغضناك مُنْذُ أَحْبَبْنَاك وَلَا عصيناك مُنْذُ أطعناك وَلَا فارقناك مُنْذُ جامعناك وَلَا نكثنا بيعتنا مُنْذُ بايعناك سُيُوفنَا على عواتقنا إِن أمرتنا أطعناك وَإِن دَعوتنَا أجبناك وَإِن سبقتنا أدركناك وَإِن سبقناك نظرناك ثمَّ جلس ثمَّ قَامَ الْمُنَادِي فَقَالَ أَيْن عبد الله بن مخمر الشرعبي فَقَامَ فَحَمدَ الله وَأثْنى عَلَيْهِ ثمَّ قَالَ وقولك يَا أَمِير الْمُؤمنِينَ فِي هَذِه الْعِصَابَة من أهل الْعرَاق إِن تعاقبهم فقد أصبت وَإِن تَعْفُو فقد أَحْسَنت فَقَامَ الْمُنَادِي فَنَادَى أَيْن عبد الله بن أَسد الْقَسرِي فَقَامَ فَحَمدَ الله وَأثْنى عَلَيْهِ ثمَّ قَالَ يَا أَمِير الْمُؤمنِينَ رعيتك وولايتك وَأهل طَاعَتك إِن تعاقبهم فقد جنوا أنفسهم الْعقُوبَة وَإِن تعفوا فَإِن الْعَفو أقرب للتقوى يَا أَمِير الْمُؤمنِينَ لَا تُطِع فِينَا من كَانَ غشوما لنَفسِهِ ظلوما بِاللَّيْلِ نؤوما عَن عمل الْآخِرَة','2','330',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (685,'يَا أَمِير الْمُؤمنِينَ إِن الدُّنْيَا قد انخشعت أوتادها ومالت بهَا عمادها وأحبها أَصْحَابهَا واقترب مِنْهَا ميعادها ثمَّ جلس فَقلت لشرحبيل فَكيف صنع قَالَ قتل بَعْضًا واستحيى بَعْضًا وَكَانَ فِيمَن قتل حجر بن عدي بن الأدبر قَالَ قدم لتضرب عُنُقه فَقَالَ لَا تطلقوا عني حديدا وادفنوني وَمَا أصَاب الثرى من دمي فَإِنِّي ألتقي أَنا وَمُعَاوِيَة بالجادة قَالَ أَبُو الْمُغيرَة كَانَ ابْن عَيَّاش لَا يكَاد يحدث بِهَذَا الحَدِيث إِلَّا بَكَى بكاء شَدِيدا
<span data-type="title" id=toc-652>حكم تَزْوِيج الْأَخ لأَب مَعَ وجود الْأَخ الشَّقِيق</span>

٩٦١ - سَأَلت أبي عَن الْمَرْأَة يكون لَهَا أَخَوان أَخ من أَبِيهَا وَأَخ لأمها وأبيها هَل يجوز أَن يُزَوّجهَا الْأَخ الَّذِي لأَبِيهَا','2','331',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (686,'قَالَ نعم هما سَوَاء فِي ولَايَة النِّكَاح وَإِذا كَانَ من قبل أمهَا لم يجز
<span data-type="title" id=toc-653>حكم الطَّلَاق قبل النِّكَاح</span>

٩٦٢ - سَأَلت أبي عَن الطَّلَاق قبل النِّكَاح قَالَ أما الطَّلَاق قبل النِّكَاح فَإِن تزوج لم آمره أَن يُفَارق سمى أَو لم يسم أما الْعتْق قبل الْملك فَلَا أَقُول فِيهِ شَيْئا
<span data-type="title" id=toc-654>عدَّة الْمُطلقَة إِذا ارْتَفع حَيْضهَا</span>

٩٦٣ - سَأَلت أبي إِذا طلقت فارتفع حَيْضهَا كم تَعْتَد','2','332',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (687,'قَالَ إِن كَانَت تعلم مَا الَّذِي رفع حَيْضهَا فَلَا بُد لَهَا من أَن تَأتي بِثَلَاث حيض كَأَنَّهَا كَانَت ترْضع فارتفع حَيْضهَا أَو مَرضت فارتفع حَيْضهَا وَإِن كَانَت لَا تعرف مَا الَّذِي رفع حَيْضهَا تَأتي بِسنة تِسْعَة أشهر للْحَمْل وَثَلَاثَة أشهر مَكَان الْحيض
<span data-type="title" id=toc-655>الْمَفْقُود وعدة زَوجته</span>

٩٦٤ - سَأَلته عَمَّن فقد من معركة الْحَرْب أَو من قَرْيَة لم يدر قتل أم حَيّ كم تَعْتَد امْرَأَته قَالَ الفقيد يكون مثل قوم لقوا الْعَدو فَقتل بَعضهم وانفلت بعض أَو قوم ركبُوا الْبَحْر فَكسر بهم فغرق بَعضهم وأفلت بَعضهم وَرجل بَات فِي أَهله فَأَصْبحُوا لم يروه أما رجل خرج لسفر وتجاره فَلَا يكون هَذَا فقيدا فَتعْتَد أَربع سِنِين وَأَرْبَعَة أشهر وَعشرا
<span data-type="title" id=toc-656>حكم تَزْوِيج ابْن الْعم مَعَ غيبَة الْأَخ</span>

٩٦٥ - وَسَأَلته عَن الْأَخ إِذا كَانَ غَائِبا هَل يجوز لِأَبْنِ الْعم أَن يُزَوّجهَا قَالَ إِذا كَانَت غيبَة قد طَالَتْ وَكَانَ موضعا مُنْقَطِعًا جَازَ','2','333',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (688,'<span data-type="title" id=toc-657>حكم الصَّلَاة الْمَكْتُوبَة على الرَّاحِلَة</span>

٩٦٦ - وَسَأَلته هَل يجوز للرجل فِي السّفر أَن يُصَلِّي الْمَكْتُوبَة على رَاحِلَته قَالَ لَا يُصَلِّي الْمَكْتُوبَة إِلَّا على الأَرْض مَرِيضا كَانَ أَو غَيره
<span data-type="title" id=toc-658>نَفَقَة الْمَرْأَة لما مضى من السنين</span>

٩٦٧ - قلت الرجل يغيب عَن أَهله سِنِين ثمَّ يقدم أَو يَمُوت هَل يفْرض عَلَيْهِ نَفَقَتهَا لما مَضَت من السنين أَو كَانَت حَاضِرَة فَلم تطلب ثمَّ طلبت بعد أَو طَلقهَا قبل أَن يضْرب لَهَا فِي مَاله إِذا كَانَ حَبسه عَنْهَا من غير عصيان قَالَ يضْرب لَهَا فِي مَاله بِقدر نَفَقَة مثلهَا','2','334',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (689,'<span data-type="title" id=toc-659>لَا نَفَقَة وَلَا سُكْنى للمطلقة ثَلَاثًا</span>

٩٦٨ - قَالَ وَأَقُول إِذا طَلقهَا ثَلَاثًا فَلَا سُكْنى لَهَا وَلَا نَفَقَة أذهب إِلَى حَدِيث فَاطِمَة بنت قيس
<span data-type="title" id=toc-660>مسَائِل فِي بِنَاء الْمَسْجِد</span>

٩٦٩ - وَسَأَلته الكناسة تكون فِي الحارة يعمد بعض أهل الحارة فيتخذها مَسْجِدا قَالَ لَا يبْنى مَسْجِدا أبدا إِلَّا بِملك يملكهُ رجل فيبنيه وَلَا يبْنى مَسْجِد فِي الطَّرِيق
٩٧٠ - وَسَأَلته يكبس الْمَسْجِد بِتُرَاب لَيْسَ بنظيف قَالَ لَا يُعجبنِي','2','335',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (690,'٩٧١ - وَسَأَلته إِذا كَانَ فِي الْمَسْجِد خَشَبَة غصب هَل تحل الصَّلَاة فِيهِ قَالَ يجْتَنب ذَلِك الْموضع الَّذِي فِيهِ الْغَصْب
٩٧٢ - سَأَلته عَمَّن بنى مَسْجِدا فِي طَرِيق الْمُسلمين قَالَ لَا يُعجبنِي أَن يصلى فِيهِ
<span data-type="title" id=toc-661>إِذا وَقعت فارة فِي زَيْت ثمَّ خرجت حَيَّة</span>

٩٧٣ - وَسَأَلته عَن فارة وَقعت فِي زَيْت لَا يكون قلَّة فَخرجت مِنْهُ حَيَّة أَو جرة','2','336',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (691,'أَو غَيره أَو فِي عشرَة أَرْطَال أَو خَمْسَة أَرْطَال قَالَ أَرْجُو أَن لَا تنجسه إِن شَاءَ الله
<span data-type="title" id=toc-662>إِذا وَقعت فَأْرَة فِي جب الدَّقِيق وَمَاتَتْ</span>

٩٧٤ - قلت فارة وَقعت فِي جب فَمَاتَتْ فِيهِ ثمَّ أخرج مِنْهُ الدَّقِيق فَخرجت فِي الدَّقِيق لَا يدْرِي مَاتَت فِي أَعلَى الْجب أَو وَسطه أَو أَسْفَله وَقد اخْتَلَط الدَّقِيق بعضه فِي بعض قَالَ إِن كَانَ لَا يضْبط فَلَا أرى أَن يُؤْكَل يطعم مَالا يُؤْكَل لَحْمه','2','337',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (692,'<span data-type="title" id=toc-663>حكم الطَّلَاق فِي النِّكَاح الْفَاسِد الْمُخْتَلف فِيهِ</span>

٩٧٥ - وَسَأَلته عَن امْرَأَة تزوجت بِغَيْر إِذن وَليهَا فَطلقهَا هَذَا الَّذِي تزوج بهَا ثَلَاثًا ثمَّ أجَاز الْوَلِيّ النِّكَاح هَل تحل لَهُ من قبل أَن تنْكح زوجا غَيره لِأَن هَذَا النِّكَاح الأول كَانَ فَاسِدا قَالَ لَا ترجع إِلَيْهِ إِلَّا بِزَوْج لِأَن هَذَا النِّكَاح الَّذِي تزَوجهَا هَذَا بِهِ إِن جَاءَت مِنْهُ بِولد كَانَ الْوَلَد لاحقا بِهِ لِأَن هَذَا نِكَاح شُبْهَة فَلَا تحل لَهُ إِلَّا أَن تنْكح زوجا غَيره
<span data-type="title" id=toc-664>من استكره بكرا على نَفسهَا</span>

٩٧٦ - وَسَأَلته عَن رجل استكره بكرا عَن نَفسهَا مَا عَلَيْهِ قَالَ عَلَيْهِ صدَاق مثلهَا وَعَلِيهِ الْحَد','2','338',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (693,'<span data-type="title" id=toc-665>حكم قبض الْأُم مَا وهب لإبنها الصَّغِير مَعَ وجود الْأَب</span>

٩٧٧ - وَسَأَلته عَن رجل وهب لصبي صَغِير هبة أَو تصدق عَلَيْهِ بِصَدقَة فقبضت الْأُم ذَلِك وَله أَب حَاضر هَل يكون قبض الْأُم قبضا وَالْأَب حَاضر أَو غَائِب أَو الْأَخ أَو الْعم أَو الْوَصِيّ إِذا كَانَ الْأَب حَاضرا أَو غَائِبا قَالَ لَا أعرف الْأُم يكون لَهَا الْقَبْض وَلَا يكون إِلَّا للْأَب
<span data-type="title" id=toc-666>الْوَصِيَّة بِالْوَاجِبِ تكون من جَمِيع المَال</span>

٩٧٨ - وَسَأَلته عَن رجل أوصى بِزَكَاة وَاجِبَة عَلَيْهِ أَو بِحَجّ وَاجِب ثمَّ مَاتَ أَيكُون من جَمِيع المَال أَو من ثلثه فَإِن أوصى بِحجَّة تطوع أَو بعزو وَعَلِيهِ حج وَاجِب وَلَا يخرج ذَلِك من الثُّلُث هَل يرد ذَلِك فَيجْعَل فِي الْفَرِيضَة وَيتْرك التَّطَوُّع قَالَ الْفَرِيضَة من جَمِيع المَال والتطوع من ثلثه فَإِن ضَاقَ الثُّلُث تحاصوا فِي الثُّلُث إِذا عجز فَكَأَنَّهُ أوصى للْمَسَاكِين أَو لقوم','2','339',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (694,'<span data-type="title" id=toc-667>كَيفَ يصنع إِذا كَانَت فِي الْمَسْأَلَة أَحَادِيث مُخْتَلفَة</span>

٩٧٩ - وَسَأَلته عَن الرجل يكون فِي الْقرْيَة وَقد روى الحَدِيث ووردت عَلَيْهِ مَسْأَلَة فِيهَا أَحَادِيث مُخْتَلفَة كَيفَ لَهُ أَن يصنع قَالَ لَا يَقُول فِيهَا شَيْئا
<span data-type="title" id=toc-668>حكم أَخذ التُّرَاب والآجر من الدّور والتلال العادية</span>

٩٨٠ - وَسَأَلته هَل يحل أَخذ التُّرَاب والآجر من الدّور والتلال العادية قَالَ إِن كَانَت تِلْكَ الدّور حصونا أَو ملكا لقوم قد عرفُوا فَلَا يُؤْخَذ مِنْهُ شَيْء
<span data-type="title" id=toc-669>مسَائِل فِي الْموَات وإحيائها</span>

٩٨١ - وَسَأَلته هَل بِأَرْض الْجَبَل موَات','2','340',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (695,'فَقَالَ إِنَّمَا الْموَات تكون فِي الأَرْض الَّتِي لم تملك فَمن أَحْيَاهَا فَهِيَ لَهُ والإحياء يكون يُحِيط عَلَيْهَا حَائِطا فَيمْنَع مِنْهَا أَو يحْفر فِيهَا بِئْرا فَتكون لَهُ حريمها خَمْسَة وَعشْرين ذِرَاعا حولهَا','2','341',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (696,'<span data-type="title" id=toc-670>حكم الصَدَاق إِذا زوج أمته بِعَبْدِهِ</span>

٩٨٢ - سَأَلت أبي عَن رجل أَرَادَ أَن يُزَوّج جَارِيَته بِعَبْدِهِ قَالَ يمهرها وَيشْهد وينقد مَا تيَسّر
<span data-type="title" id=toc-671>حكم الْكحل للصَّائِم</span>

٩٨٣ - وَسَأَلته عَن الْكحل للصَّائِم فَقَالَ يُعجبنِي أَن يقل مِنْهُ
٩٨٤ - قلت البرود قَالَ البرود أَكثر من الْكحل فَكَأَنَّهُ كرهه
<span data-type="title" id=toc-672>إِذا رَأَتْ الْمَرْأَة الدَّم فِي غير أَيَّامهَا</span>

٩٨٥ - سَأَلته عَن امْرَأَة رَأَتْ الدَّم فِي غير أَيَّامهَا فَقَالَ تَصُوم وَتصلي حَتَّى تبلغ أَيَّامهَا الَّتِي كَانَت تقعد فِيهَا فَإِذا بلغت أَيَّامهَا لم تصم وَلم تصل','2','342',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (697,'<span data-type="title" id=toc-673>سَجْدَة التِّلَاوَة فِي سُورَة الانشقاق</span>

٩٨٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن مَرْوَان الْأَصْفَر قَالَ سَمِعت أَبَا رَافع قَالَ رَأَيْت أَبَا هُرَيْرَة سجد فِي {إِذا السَّمَاء انشقت} قَالَ فَسَأَلته قَالَ سجد فِيهَا خليلي وَلَا أَزَال أَسجد حَتَّى أَلْقَاهُ
<span data-type="title" id=toc-674>أثران لعمر فِي الْقُنُوت</span>

٩٨٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن مَرْوَان الْأَصْفَر قَالَ سَمِعت أَبَا رَافع قَالَ صليت خلف عمر فقنت بعد الرُّكُوع فَدَعَا على الْكَفَرَة','2','343',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (698,'٩٨٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن مَرْوَان الْأَصْفَر قَالَ سَأَلت أنسا قنت عمر قَالَ وَخير من عمر قَالَ أبي لَيْسَ فِي كتاب غنْدر إِلَّا هَذِه الثَّلَاثَة عَن مَرْوَان الْأَصْفَر
<span data-type="title" id=toc-675>رِوَايَة عَن أنس فِي لبس الْخَزّ</span>

٩٨٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن عَامر بن عُبَيْدَة الْبَاهِلِيّ قَالَ رَأَيْت أنس بن مَالك عَلَيْهِ جُبَّة خَز فَسَأَلته فَقَالَ أعوذ بِاللَّه من شَرها قَالَ قلت هَل لبسهَا أحد من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم فَقَالَ كلهم غير عمر وَابْن عمر قَالَ أبي لَيْسَ فِي كتاب غنْدر غير هَذَا الحَدِيث عَن عَامر بن عُبَيْدَة الْبَاهِلِيّ','2','344',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (699,'<span data-type="title" id=toc-676>رِوَايَة عَن ابْن الْمسيب فِي مِيرَاث الْمُرْتَد</span>

٩٩٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة قَالَ سَمِعت أَبَا الصَّباح مُوسَى بن أبي كثير قَالَ سَأَلت سعيد بن الْمسيب عَن الْمُرْتَد فَقَالَ وَيلك نرثهم وَلَا يرثونا قَالَ أبي لَيْسَ غير هَذَا الحَدِيث عَن مُوسَى بن أبي كثير فِي كتاب غنْدر
<span data-type="title" id=toc-677>أثر عَن كريب بن يزِيد الرَّحبِي</span>

٩٩١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو الْمُغيرَة قَالَ حَدثنَا حريز عَن يزِيد بن خمير عَن كريب بن يزِيد الرَّحبِي أَنه كَانَ يسْتَحبّ','2','345',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (700,'أَن يرْكَع رَكْعَتي الْفجْر وَرَكْعَتَيْنِ بعد الْمغرب وَلَيْسَ بَينهم وَبَين الْقبْلَة شَيْء
<span data-type="title" id=toc-678>مَا سَمعه الحكم من مقسم</span>

٩٩٢ - قَالَ أبي سمع الحكم من مقسم أَرْبَعَة الَّذِي يَصح حَدِيث الْوتر أَن النَّبِي صلى الله عَلَيْهِ وَسلم كَانَ يُوتر وعزيمة الطَّلَاق والفيء','2','346',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (701,'الْجِمَاع وَهُوَ من عَن مقسم عَن ابْن عَبَّاس وَعَن ابْن عَبَّاس أَن عمر قنت فِي الْفجْر وَعَن مقسم وَهُوَ رَأْيه فِي محرم أصَاب صيدا قَالَ عَلَيْهِ جَزَاؤُهُ فَإِن لم يكن عِنْده قوم الْجَزَاء دَرَاهِم ثمَّ يقوم الدَّرَاهِم طَعَاما ثمَّ يَصُوم مَكَان كل نصف صَاع يَوْمًا قَالَ وَالْبَاقِي فَالله أعلم وحجاج روى عَنهُ عَن مقسم عَن ابْن','2','347',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (702,'عَبَّاس نَحوا من خمسين حَدِيثا وَقَالَ مرّة قَالَ شُعْبَة هَذِه الْأَرْبَعَة الَّتِي صححها الحكم يَعْنِي سَمَاعا من مقسم
<span data-type="title" id=toc-679>مسَائِل فِيمَن وَقع بأَهْله فِي رَمَضَان</span>

٩٩٣ - سَأَلت أبي عَمَّن وَقع بأَهْله فِي رَمَضَان قَالَ أذهب فِيهِ إِلَى حَدِيث الزُّهْرِيّ فِي الرجل الَّذِي جَاءَ النَّبِي صلى الله عَلَيْهِ وَسلم فَقَالَ لَهُ قد وَقعت بأهلي فَقَالَ لَهُ أعتق رَقَبَة فَقَالَ لَا أَسْتَطِيع قَالَ صم شَهْرَيْن أَو أطْعم سِتِّينَ مِسْكينا
٩٩٤ - قلت فَإِن لم يجد أَن يطعم قَالَ لَا بُد لَهُ من أَن يطعم','2','348',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (703,'٩٩٥ - قلت فَإِن لم يكن عِنْده وَأطْعم عَنهُ رجل يكون لَهُ ولعياله فَقَالَ نعم على حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم ٩٦٦
قلت أفليس يرْوى أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ لَيْسَ لأحد بعْدك فَقَالَ لَيْسَ هَذَا بِشَيْء
٩٩٧ - قلت وَيَقْضِي يَوْمًا مَكَانَهُ مَعَ الْكَفَّارَة قَالَ نعم','2','349',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (704,'<span data-type="title" id=toc-680>عدَّة الْمُطلقَة الْحَامِل والمتوفى عَنْهَا زَوجهَا الْحَامِل</span>

٩٩٨ - سَأَلت أبي عَن الْمُتَوفَّى عَنْهَا زَوجهَا الْحَامِل قَالَ إِذا وضعت فقد حلت وَلَكِن لَا يَطَأهَا حَتَّى تطهر من الدَّم وَكَذَلِكَ الْمُطلقَة الْحَامِل أجلهَا أَن تضع حملهَا
<span data-type="title" id=toc-681>رِوَايَة فِي مغْفرَة الرب سُبْحَانَهُ</span>

٩٩٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة قَالَ سَمِعت يحيى بن أبي سليم وَهُوَ أَبُو بلج يحدث عَن عَمْرو بن مَيْمُون عَن عبد الله بن عَمْرو أَنه قَالَ لَو أَن الْعباد لم يذنبوا لخلق الله خلقا يذنبون ثمَّ يغْفر لَهُم إِنَّه هُوَ الغفور الرَّحِيم','2','350',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (705,'<span data-type="title" id=toc-682>فضل الْحبّ لله</span>

١٠٠٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن أبي بلج عَن عَمْرو بن مَيْمُون عَن أبي هرية عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ من أحب أَن يجد طعم الْإِيمَان فليحب المرأ لَا يُحِبهُ إِلَّا لله
<span data-type="title" id=toc-683>تَعْرِيف الْغَيْبَة</span>

١٠٠١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة قَالَ سَمِعت الْعَبَّاس الْجريرِي يحدث عَمَّن سمع ابْن عمر يَقُول إِذا قلت للرجل مَا لَيْسَ فِيهِ فَهِيَ فِرْيَة فَإِذا قلت مَا فِيهِ فَهِيَ غيبَة','2','351',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (706,'<span data-type="title" id=toc-684>من حَيَاة أبي هُرَيْرَة رَضِي الله عَنهُ</span>

١٠٠٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن عَبَّاس الْجريرِي قَالَ سَمِعت أَبَا عُثْمَان يَعْنِي النَّهْدِيّ يحدث عَن أبي هُرَيْرَة أَنهم أَصَابَهُم جوع قَالَ وَنحن سَبْعَة فأعطانا النَّبِي صلى الله عَلَيْهِ وَسلم سبع تمرات لكل إِنْسَان تَمْرَة قَالَ أبي لَا أعلم شُعْبَة حدث عَن عَبَّاس الْجريرِي إِلَّا هذَيْن الْحَدِيثين
<span data-type="title" id=toc-685>قَول عمر لَا هِجْرَة بعد الرَّسُول</span>

١٠٠٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة قَالَ سَمِعت يحيى بن هاني بن عُرْوَة يحدث عَن نعيم بن دجَاجَة قَالَ سَمِعت عمر بن الْخطاب يَقُول لَا هِجْرَة بعد رَسُول الله صلى الله عَلَيْهِ وَسلم قَالَ أبي لَيْسَ فِي كتاب غنْدر عَن يحيى بن هاني غير هَذَا','2','352',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (707,'<span data-type="title" id=toc-686>إسهام أبي مُوسَى الْأَشْعَرِيّ للنِّسَاء</span>

١٠٠٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة قَالَ سَمِعت الْعَوام الْقَيْسِي وَقَالَ وَكِيع الْعَوام بن مراجم يحدث عَن خَالِد بن سيحان قَالَ شهِدت تستر فَكَانَ فِينَا أَربع نسْوَة مِنْهُنَّ أم مجزأَة فَكُن يسقين المَاء ويداوين الْجَرْحى فَأَسْهم لَهُنَّ أَبُو مُوسَى','2','353',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (708,'<span data-type="title" id=toc-687>الْقود يَوْم الْقِيَامَة</span>

١٠٠٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن الْعَوام الْقَيْسِي عَن أبي السَّلِيل عَن أبي عُثْمَان النَّهْدِيّ عَن سلمَان أَنه قَالَ إِن الله يدين يَوْم الْقِيَامَة للنَّاس أَو للعباد حَتَّى يُقَاد للشاة الجلحاء من القرناء نطحتها قَالَ أبي لَيْسَ فِي كتاب غنْدر غير هذَيْن الْحَدِيثين عَن الْعَوام','2','354',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (709,'<span data-type="title" id=toc-688>كَلَام عمر على الْمِنْبَر قبل أَن يخْطب</span>

١٠٠٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنِي مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة قَالَ سَمِعت بكر بن وَائِل يحدث عَن الزُّهْرِيّ عَن عبد الله بن ثَعْلَبَة بن الأصعر أَو ابْن أبي صعير قَالَ كَانَ عمر بن الْخطاب إِذا صعد الْمِنْبَر يُكَلِّمنَا حَتَّى يخْطب قَالَ أبي لَيْسَ فِي كتاب غنْدر عَن شُعْبَة عَن بكر بن وَائِل إِلَّا هَذَا الحَدِيث
<span data-type="title" id=toc-689>الْمسْح بالمنديل بعد الْوضُوء</span>

١٠٠٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة قَالَ سَمِعت أَسِير بن ربيع بن عميلة قَالَ رَأَيْت أبي وَأَبا','2','355',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (710,'الْأَحْوَص توضآ ثمَّ مسحا وُجُوههمَا بمنديل قَالَ أبي لَيْسَ فِي كتاب غنْدر غير هَذَا الحَدِيث عَن أَسِير
<span data-type="title" id=toc-690>قَول ابْن عمر وَابْن عَبَّاس فِي الصّرْف</span>

١٠٠٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن صَالح بن أبي سُلَيْمَان قَالَ سَأَلت ابْن عمر وَابْن عَبَّاس عَن الصّرْف فنهاني ابْن عمر وَرخّص لي ابْن عَبَّاس','2','356',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (711,'<span data-type="title" id=toc-691>من قَالَ لإمرأته أَنْت فِي بَريَّة</span>

١٠٠٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن صَالح بن أبي سُلَيْمَان قَالَ سَأَلت أنس بن مَالك عَن رجل قَالَ لامْرَأَته أَنْت مني بَريَّة قَالَ لَو أَن عمر أدْرك هَذَا لفرق بَينهمَا قَالَ أبي لَيْسَ عَن شُعْبَة عَن صَالح غير هذَيْن فِي كتاب غنْدر
<span data-type="title" id=toc-692>أَوْلَاد عبد الله بن مَسْعُود</span>

١٠١٠ - قَالَ أبي هَؤُلَاءِ ولد عبد الله بن مَسْعُود أَبُو عُبَيْدَة بن عبد الله','2','357',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (712,'وَعبد الرَّحْمَن بن عبد الله بن مَسْعُود وَعتبَة بن عبد الله بن مَسْعُود
<span data-type="title" id=toc-693>ذكر الكنى لبَعض الْمُحدثين</span>

١٠١١ - سَمِعت أبي يَقُول رَجَاء بن حَيْوَة أَبُو الْمِقْدَام ونوف الْبكالِي أَبُو يزِيد عبد الْخَالِق بن سَلمَة أَبُو روح','2','358',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (713,'<span data-type="title" id=toc-694>فضل الْحبّ فِي الله</span>

١٠١٢ - قَالَ أبي حَدثنَا يزِيد بن هَارُون عَن شُعْبَة عَن أَشْعَث بن سليم عَن عَمْرو بن مَيْمُون عَن أبي هُرَيْرَة عَن النَّبِي صلى الله عَلَيْهِ وَسلم من أحب أَن يجد طعم الْإِيمَان فليحب الْمَرْء لَا يُحِبهُ إِلَّا لله قَالَ أبي فَقلت ليزِيد بن هَارُون مَا هُوَ يحيى بن أبي سليم أَبُو بلج قَالَ سمعته مِنْهُ بِبَغْدَاد وَأَنا فِي آخر النَّاس ومنذ سمعته أَنا أَشك فِيهِ فَقَالَ لَهُم اجعلوه على رجل
<span data-type="title" id=toc-695>ذكر الكنى لبَعض الرِّجَال</span>

١٠١٣ - قَالَ أبي حَمْزَة بن عبد الْمطلب أَبُو عمَارَة حَكِيم بن حزَام أَبُو خَالِد عتبَة بن ربيعَة أَبُو الْوَلِيد سُهَيْل بن عَمْرو وَأَبُو يزِيد الزبير بن عدي','2','359',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (714,'أَبُو عدي عبد الْكَرِيم الْجَزرِي أَبُو سعيد يُونُس بن خباب أَبُو حَمْزَة شَدَّاد بن أَوْس أَبُو يعلي عمار مولى بني هَاشم أَبُو عبد الله حميد الْأَعْرَج أَبُو صَفْوَان عبد الله بن أبي الْهُذيْل أَبُو الْمُغيرَة','2','360',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (715,'أسيد بن حضير أَبُو عتِيك صلَة بن زفر أَبُو الْعَلَاء الْحَارِث بن سُوَيْد أَبُو عَائِشَة مَسْرُوق بن الأجدع أَبُو عَائِشَة عَاصِم الجححدري أَبُو مجشر سعد بن معَاذ أَبُو عَمْرو عسعس بن سَلامَة','2','361',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (716,'أَبُو صفرَة رَجَاء بن حَيْوَة أَبُو الْمِقْدَام عِيسَى بن دِينَار أَبُو عَليّ المستظل بن حُصَيْن أَبُو الميثاء الْقَاسِم بن مُحَمَّد أَبُو عبد الرَّحْمَن رَافع الطَّائِي أَبُو الْحسن','2','362',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (717,'١٠١٤ - قَالَ أبي سَمِعت من عُبَيْدَة عَن هِلَال بن حميد أَبُو معبد عبد الله بن عكيم أَبُو معبد
١٠١٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو الْمُغيرَة عَن صَفْوَان','2','363',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (718,'كَعْب الْأَحْبَار أَبُو إِسْحَاق شُرَيْح بن عبيد أَبُو الصَّلْت حَوْشَب بن سيف أَبُو روح عبد الله بن بسر أَبُو صَفْوَان مُحَمَّد بن زِيَاد الْأَلْهَانِي أَبُو سُفْيَان يزِيد بن ميسرَة أَبُو يُوسُف إِلَى هَاهُنَا عَن أبي الْمُغيرَة صَفْوَان بن عَمْرو أَبُو عَمْرو أَرْطَاة بن','2','364',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (719,'الْمُنْذر أَبُو عدي ضَمرَة بن حبيب أَبُو عتبَة خَالِد بن معدان أَبُو عبد الله عَمْرو بن الْأسود الْعَنسِي أَبُو عِيَاض يحيى بن أبي كثير ابو نصر حميد بن هِلَال أَبُو نصر عَطاء بن يزِيد أَبُو مُحَمَّد عقيل بن أبي طَالب أَبُو يزِيد غُضَيْف بن الْحَارِث أَبُو أَسمَاء','2','365',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (720,'أَبُو بحريّة عبد الله بن قيس التراغمي صَفْوَان بن أُميَّة أَبُو وهب
١٠١٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ','2','366',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (721,'معمر كنيته أَبُو عُرْوَة عبد الرَّحْمَن بن حَرْمَلَة كنيته أَبُو حَرْمَلَة سعيد بن الْمسيب أَبُو مُحَمَّد
<span data-type="title" id=toc-696>يزِيد بن أبي حبيب لم يسمع من الزُّهْرِيّ</span>

١٠١٧ - يزِيد بن أبي حبيب لم يسمع من الزُّهْرِيّ ابْن شهَاب شَيْئا وَإِنَّمَا كتب إِلَيْهِ الزُّهْرِيّ وَقَالَ مرّة يزِيد بن أبي حبيب عَن الزُّهْرِيّ كتاب إِلَّا مَا سمى بَينه وَبَين الزُّهْرِيّ
<span data-type="title" id=toc-697>ابْن أبي ذِئْب وسماعه من الزُّهْرِيّ</span>

١٠١٨ - قلت ابْن أبي ذِئْب سمع من الزُّهْرِيّ قَالَ نعم سمع مِنْهُ','2','367',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (722,'١٠١٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد عَن ابْن أبي ذِئْب قَالَ حَدثنِي الزُّهْرِيّ وَغير يحيى يَقُول سَأَلت الزُّهْرِيّ وَهَذَا يحيى بن سعيد يَقُول حَدثنِي الزُّهْرِيّ
١٠٢٠ - وسمعته يَقُول ابْن أبي ذِئْب خير من مَالك وَأفضل','2','368',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (723,'١٠٢١ - وسمعته يَقُول قَالَ حَمَّاد الْخياط كَانَ ابْن أبي ذِئْب يشبه بِسَعِيد بن الْمسيب
١٠٢٢ - وسمعته يَقُول قَالُوا لمَالِك بن أنس إِن سُفْيَان الثَّوْريّ يُفْتِي قَالَ وَيفْعل فَقَالُوا لِابْنِ أبي ذِئْب فَقَالَ مَاله وَله مَا رَأَيْت مشرقيا خيرا مِنْهُ يَعْنِي سُفْيَان
١٠٢٣ - قَالَ أبي كَانَ ابْن أبي ذِئْب صديق سُفْيَان
١٠٢٤ - قَالَ أبي أهل الْمَدِينَة يسمون أهل الْعرَاق مشرقيا
١٠٢٥ - قَالَ أبي ابْن أبي ذِئْب مُحَمَّد بن عبد الرَّحْمَن بن الْمُغيرَة بن أبي ذِئْب
١٠٢٦ - سمعته يَقُول كَانَ ابْن أبي ذِئْب قوالا بِالْحَقِّ','2','369',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (724,'١٠٢٧ - قَالَ أبي وَكَانَ لَا يملي عَلَيْهِم إِنَّمَا كَانُوا يتحفظون فَمن حفظ حفظ وَمن لم يحفظ لَيْسَ بِشَيْء إِلَّا أَن حجاجا قَالَ سَمِعت ابْن أبي ذِئْب ثمَّ عرضتها عَلَيْهِ
١٠٢٨ - قلت لَهُ مَالك بن أنس قدم على أبي جَعْفَر قَالَ لَا إِنَّمَا ابْن أبي ذِئْب قدم على أبي جَعْفَر مَالك لم يقدم عَلَيْهِ لم يبرح بِالْمَدِينَةِ
<span data-type="title" id=toc-698>أَبُو بكر بن أبي سُبْرَة من الوضاعين</span>

١٠٢٩ - قَالَ أبي كَانَ أَبُو بكر مُحَمَّد بن عبد الله بن أبي سُبْرَة يضع الحَدِيث','2','370',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (725,'١٠٣٠ - قَالَ أبي كَانَ ابْن جريج يحدث عَن أبي بكر ابْن أبي سُبْرَة قَالَ حجاج بن مُحَمَّد فكتبتها وَذَهَبت إِلَيْهِ فعرضتها عَلَيْهِ فَقَالَ عِنْدِي سَبْعُونَ ألف حَدِيث فِي الْحَلَال وَالْحرَام
<span data-type="title" id=toc-699>تَوْثِيق بشر بن مَنْصُور</span>

١٠٣١ - وَقَالَ أبي بشر بن مَنْصُور ثِقَة وَزِيَادَة
<span data-type="title" id=toc-700>رِوَايَة خلاس عَن عَليّ</span>

١٠٣٢ - قَالَ أبي كَانَ يحيى بن سعيد يتوقي أَن يحدث عَن خلاس عَن عَليّ خاصته وأظن أَنه قد حَدثنَا عَنهُ بِحَدِيث','2','371',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (726,'<span data-type="title" id=toc-701>أَقْوَال وَأَحَادِيث فِي كِتَابَة الحَدِيث</span>

١٠٣٣ - قَالَ أبي كُنَّا عِنْد إِسْمَاعِيل بن إِبْرَاهِيم فجَاء إِنْسَان فَذكر حَدِيث مُحَمَّد بن إِسْحَاق عَن عَمْرو بن شُعَيْب عَن أَبِيه عَن جده قَالَ قلت يَا رَسُول الله أكتب عَنْك مَا أسمع مِنْك قَالَ نعم قلت يَا رَسُول الله فِي الرِّضَا وَالْغَضَب قَالَ نعم فَإِنَّهُ لَا يَنْبَغِي لي أَن أَقُول فِي ذَلِك إِلَّا حَقًا قَالَ فَقَالَ إِسْمَاعِيل أعوذ بِاللَّه من الْكَذِب وَأَهله أعوذ بِاللَّه من الْكَذِب وَأَهله
١٠٣٤ - قَالَ كَانَ ابْن عون وَابْن سِيرِين لَا يَكْتُبُونَ وَلَا يَكْتُبُونَ','2','372',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (727,'١٠٣٥ - قَالَ أبي قَالَ إِسْمَاعِيل قَالَ ابْن عون أرى هَذِه الْكتب سَيكون لَهَا غب سوء
١٠٣٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْمَاعِيل قَالَ حَدثنَا همام عَن زيد بن أسلم عَن عَطاء بن يسَار عَن أبي سعيد قَالَ قَالَ رَسُول الله صلى الله عَلَيْهِ وَسلم لَا تكْتبُوا عني شَيْئا سوى الْقُرْآن من كتب شَيْئا سوى الْقُرْآن فليمحه قَالَ أبي إِنَّمَا أنكر إِسْمَاعِيل قصَّة عَمْرو بن شُعَيْب من أجل حَدِيث همام','2','373',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (728,'<span data-type="title" id=toc-702>عودة إِلَى تَرْجَمَة ابْن أبي ذِئْب</span>

١٠٣٧ - قَالَ أبي ابْن أبي ذِئْب كنيته أَبُو الْحَارِث وَكَانَ صَاحب أَمر وَنهي وَقَالَ بَعضهم حِين تكلم عِنْد أبي جَعْفَر كنت أتوقع أَن يَأْمر بِهِ يقتل
<span data-type="title" id=toc-703>من كَانَ يخضب من الْمُحدثين</span>

١٠٣٨ - قَالَ أبي لم يكن وَكِيع يخضب قَالَ أَبُو مُعَاوِيَة كَانَ يخضب وَكَانَ جيد الخضاب وَحَفْص وَابْن إِدْرِيس وَعباد بن الْعَوام كَانَ خضابة إِلَى السوَاد مَا هُوَ وَجَرِير كَانَ يخضب وَابْن نمير كَانَ يخضب وَابْن فُضَيْل كَانَ يخضب وغندر يخضب والبرساني كَانَ يخضب عباد بن عباد يخضب وَابْن أبي','2','374',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (729,'زَائِدَة يخضب خضابا جيدا ابْن عُيَيْنَة لم يكن يخضب الْوَلِيد بن مُسلم يخضب خضابا قَلِيلا وَكَانَ أسود الرَّأْس وَابْن مهْدي كَانَ يخضب قَالَ رَأَيْته فِي سنة خمس وَثَمَانِينَ وَهُوَ يَوْمئِذٍ ابْن خمسين وَقد خضب وَيحيى بن سعيد كَانَ يخضب وَرَأَيْت عبد الرَّحْمَن بن مهْدي سنة إِحْدَى وَثَمَانِينَ وَقد خضب سنة كَانَ عندنَا أَبُو بكر بن عَيَّاش هشيم كَانَ يخضب حَمَّاد بن مسْعدَة يخضب مُعْتَمر يخضب وَكَانَ لَهُ جميمة صَغِيرَة ومرحوم الْعَطَّار يخضب وَيزِيد بن هَارُون يخضب وَمُحَمّد بن يزِيد','2','375',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (730,'يخضب وَرَأَيْت إِسْحَاق الْأَزْرَق رَأسه مرّة يخضب خضابا خَفِيفا حجاج يخضب خضابا جيدا عَليّ بن عَاصِم خضابا خَفِيفا
١٠٣٩ - قلت إِبْرَاهِيم بن سعد قَالَ لَا ادري كَانَ آدم لَكِن سعد وَيَعْقُوب كَانَا يخضبان أَبُو دَاوُد كَانَ يخضب عبد الْأَعْلَى لم يكن يخضب وَلَا سهل بن','2','376',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (731,'يُوسُف معَاذ خضاب خَفِيف عبد الصَّمد لم يكن يخضب وروح يخضب وَأَبُو النّظر كَانَ يخضب وَعبد الرَّزَّاق كَانَ يخضب وَأَبُو أُسَامَة لم يكن يخضب إِلَّا أَنِّي رَأَيْته مرّة قد غسل رَأسه بِالْحِنَّاءِ وَأَبُو نعيم كَانَ يخضب مُحَمَّد بن سَلمَة لَا أَدْرِي مُحَمَّد بن عبيد ويعلى كَانَا يخضبان كَانَ عبد الرَّزَّاق يخضب','2','377',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (732,'أَخُوهُ لم يكن يخضب ربعى بن علية خضابا خَفِيفا أَبُو عَامر لم يكن يخضب وَلَا أَزْهَر السمان وَلَا عبد الله بن سَلمَة الْأَفْطَس أَبُو كَامِل لم يكن يخضب لَا هُوَ وَلَا مُوسَى بن دَاوُد','2','378',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (733,'وَلَا يحيى بن آدم كَانَ فِي رَأسه سَواد أَبُو الْمُغيرَة وَأَبُو الْيَمَان وَعلي بن عَيَّاش وعصام بن خَالِد وَبشر بن شُعَيْب كَانُوا يخضبون عَليّ بن ثَابت لم يكن يخضب أَبيض الرَّأْس واللحية','2','379',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (734,'قَالَ أبي الخضاب بِالشَّام أَكثر من ذَلِك والمقرىء كَانَ يخضب يحيى بن سعيد الْأمَوِي لم يكن يخضب وَلَكِن أَخُوهُ مُحَمَّد بن سعيد كَانَ يخضب يحيى بن أبي بكير يخضب كَانَ قَاضِيا على كرمان
١٠٤٠ - قلت مَرْوَان بن مُعَاوِيَة قَالَ شَيْئا كَذَا كَانَ يخضب مَرْوَان بن شُجَاع كَانَ يخضب','2','380',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (735,'شُجَاع بن الْوَلِيد أَبُو بدر كَانَ يخضب حميد الرواسِي كَانَ يخضب يحيى بن حَمَّاد كَانَ يخضب وَكَانَ رُبمَا حَدثنَا وَهُوَ مخضوب إِبْرَاهِيم بن خَالِد كَانَ يخضب أَبُو سعيد مولى بني هَاشم لم يكن يخضب مُؤَمل لم يكن يخضب أَبُو خَالِد الْأَحْمَر لم يكن يخضب كَانَ','2','381',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (736,'أَبيض الرَّأْس واللحية كَانَ يحدث بِحِفْظ مَا كتبنَا عَنهُ إِلَّا بحفظه أَبُو تُمَيْلة لَا يخضب زيد بن الْحباب لَا يخضب عثام بن عَليّ يخضب
<span data-type="title" id=toc-704>الصَّائِم إِذا قبل</span>

١٠٤١ - قلت لأبي مَا تَقول فِي الَّذِي يقبل قَالَ إِذا أمذى يُعجبنِي أَن يقْضِي قَالَ وَبَعض يَقُول لَيْسَ عَلَيْهِ شَيْء إِلَّا أَنه يُعجبنِي أَن يُعِيد يَوْمًا مَكَانَهُ لِأَنَّهُ قد جرح صَوْمه بالإمذاء','2','382',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (737,'١٠٤٢ - قلت وَإِن لم يمذ قَالَ لَيْسَ عَلَيْهِ قَضَاء وَلَا شَيْء عَلَيْهِ
<span data-type="title" id=toc-705>من جَاءَ يَوْم الْجُمُعَة وَالْإِمَام يخْطب</span>

١٠٤٣ - سَأَلته عَمَّن جَاءَ يَوْم الْجُمُعَة وَالْإِمَام يخْطب فَقَالَ يُصَلِّي رَكْعَتَيْنِ
١٠٤٤ - قلت فَإِن قَالَ قَائِل إِن النَّبِي صلى الله عَلَيْهِ وَسلم قد رخص فِي لبس الْحَرِير لعبد الرَّحْمَن وللزبير فَهُوَ للنَّاس أَن يلبسوا','2','383',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (738,'فَقَالَ مَا يشبه هَذَا من الْحَرِير إِن النَّبِي صلى الله عَلَيْهِ وَسلم نهى عَن لبس الْحَرِير ثمَّ رخص لعبد الرَّحْمَن وَلم ينْه عَن الصَّلَاة وَإِنَّمَا ذَلِك أَمر مِنْهُ صلى الله عَلَيْهِ وَسلم','2','384',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (739,'<span data-type="title" id=toc-706>حكم توديع الْحَائِض الْبَيْت وَطواف الْمُسْتَحَاضَة</span>

١٠٤٥ - سَأَلت أبي عَن الْحَائِض تودع الْبَيْت فَقَالَ لَا تودع الْبَيْت حَتَّى تطهر فَإِن كَانَت قد طافت يَوْم النَّحْر نفرت وَهُوَ الطّواف الْوَاجِب طواف يَوْم النَّحْر
١٠٤٦ - قلت فالمستحاضة تَطوف بِالْبَيْتِ فَقَالَ نعم الْمُسْتَحَاضَة بِمَنْزِلَة الطَّاهِر تَطوف بِالْبَيْتِ
<span data-type="title" id=toc-707>بَيَان أَسمَاء وكنى لبَعض الرِّجَال</span>

١٠٤٧ - سَأَلت أبي مَا أسم الشّعبِيّ فَقَالَ عَامر بن شرَاحِيل بن عبد بن ذِي كبار من أهل الْيمن','2','385',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (740,'١٠٤٨ - وَإِبْرَاهِيم النَّخعِيّ ابراهيم بن يزِيد أَبُو عمرَان
١٠٤٩ - وَسَأَلته عَن كنية التَّيْمِيّ فَقَالَ لَا أَدْرِي من أهل الْيمن
١٠٥٠ - الْأَعْمَش سُلَيْمَان بن مهْرَان أَبُو مُحَمَّد
<span data-type="title" id=toc-708>كَيْفيَّة الْقعُود فِي الصَّلَاة</span>

١٠٥١ - وَسَأَلته عَن الْقعُود فِي الصَّلَاة فَقَالَ أذهب فِي الْأُخْرَيَيْنِ إِلَى حَدِيث أبي حميد يتورك وَفِي الْأَوليين يقْعد على رجله الْيُسْرَى وينصف الْيُمْنَى','2','386',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (741,'<span data-type="title" id=toc-709>كَرَاهَة ابْن سِيرِين أَن يَقُول شيعت فلَانا وَأَن يَقُول أَكثر شَيْء</span>

١٠٥٢ - حَدثنَا صَالح قَالَ حَدثنَا أبي قَالَ حَدثنَا هشيم عَن مَنْصُور عَن ابْن سِيرِين أَنه كَانَ يكره أَن يَقُول شيعت فلَانا وَقَالَ إِنَّمَا يشيع الْمَيِّت
١٠٥٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم عَن ابْن عون عَن ابْن سِيرِين أَنه كَانَ يكره أَن يَقُول أَكثر شَيْء','2','387',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (742,'<span data-type="title" id=toc-710>عتق ابْن عمر ولد الزِّنَا وَأمه</span>

١٠٥٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا قرَان بن تَمام أَبُو تَمام عَن عبيد الله بن عمر عَن نَافِع عَن ابْن عمر أَنه أعتق ولد زنا وَأمه فَكَانَ يغسل رَأسه بالخطمي قبل أَن يحلق
<span data-type="title" id=toc-711>فضل اتِّبَاع الْجَنَائِز</span>

١٠٥٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا قرَان عَن عُثْمَان بن الْأسود عَن مُجَاهِد قَالَ اتِّبَاع الْجَنَائِز أفضل من النَّافِلَة
<span data-type="title" id=toc-712>حكم الْحجامَة للْمحرمِ</span>

١٠٥٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا قرَان عَن عُثْمَان بن الْأسود عَن عَطاء قَالَ لَا بَأْس أَن يحتجم الْمحرم مالم يحلق شعرًا','2','388',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (743,'<span data-type="title" id=toc-713>شرب المَاء فِي الصَّلَاة النَّافِلَة</span>

١٠٥٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ مَنْصُور أخبرنَا عَن الحكم قَالَ رَأَيْت عبد الله بن الزبير يشرب وَهُوَ فِي الصَّلَاة قَالَ أبي أَرَادَ التَّطَوُّع
<span data-type="title" id=toc-714>رِوَايَتَانِ فِي عَلْقَمَة بن قيس</span>

١٠٥٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا قرَان عَن الْأَعْمَش عَن إِبْرَاهِيم أَن عَلْقَمَة لم يكن يخْطب إِلَى من هُوَ فَوْقه ويخطب إِلَى من هُوَ أسلف مِنْهُ','2','389',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (744,'١٠٥٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا قرَان عَن الْأَعْمَش عَن إِبْرَاهِيم أَن أَبَا بردة كتب وَفْدًا قَالَ قرَان وَكَانَ قَاضِيا فَكتب عَلْقَمَة فيهم فَأرْسل إِلَيْهِ عَلْقَمَة أَن امحوني
<span data-type="title" id=toc-715>ضرب عَليّ حَدَّيْنِ فِي مقَام</span>

١٠٦٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا قرَان عَن أبي سيار عَن ثَابت عَن الضَّحَّاك أَن عليا ضرب رجلا حَدَّيْنِ فِي مقَام
<span data-type="title" id=toc-716>فضل الاقتصاد</span>

١٠٦١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم عَن يُونُس عَن الْحسن قَالَ مَا عَال رجل مَعَ اقتصاد','2','390',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (745,'<span data-type="title" id=toc-717>كَرَاهَة نتف الشيب</span>

١٠٦٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ حَدثنَا مُغيرَة عَن الشّعبِيّ وَيُونُس عَن الْحسن أَنَّهُمَا كرها نتف الشيب
<span data-type="title" id=toc-718>سَاعَات الْأَذَى يذْهبن سَاعَات الْخَطَايَا</span>

١٠٦٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا قرَان عَن أبي بشر الْحلَبِي عَن الْحُسَيْن قَالَ قَالَ رَسُول الله صلى الله عَلَيْهِ وَسلم سَاعَات الْأَذَى يذْهبن سَاعَات الْخَطَايَا','2','391',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (746,'<span data-type="title" id=toc-719>كَرَاهَة نتف الشيب</span>

١٠٦٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ أخبرنَا حُصَيْن عَن إِبْرَاهِيم قَالَ كَانَ يكره نتف الشيب
١٠٦٥ - قَالَ أبي سَأَلت ابْن مهْدي أَنا أَو غَيْرِي عَن هذَيْن الْحَدِيثين فَقَالَ من سمعهما من هشيم فَقلت أَنا حَدثنَا هشيم قَالَ أخبرنَا حُصَيْن عَن إِبْرَاهِيم قَالَ كَانَ يكره نتف الشيب قَالَ أبي وَحدثنَا هشيم حَدثنَا مُغيرَة عَن الشّعبِيّ وَيُونُس عَن الْحسن أَنَّهُمَا كرها نتف الشيب فَقَالَ ابْن مهْدي هَكَذَا هُوَ
<span data-type="title" id=toc-720>كَيفَ يكون التنين</span>

١٠٦٦ - حَدثنَا صَالح قَالَ أملاه عَليّ أبي وقرأته عَلَيْهِ قَالَ حَدثنَا أَبُو الْيَمَان الحكم بن نَافِع قَالَ حَدثنَا صَفْوَان بن عَمْرو عَن حَوْشَب بن سيف','2','392',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (747,'الْمعَافِرِي عَن شَدَّاد بن أَفْلح المقرإي أَنه حَدثهُ أَنه انْصَرف هُوَ وَجَابِر بن آزاذ بعد راهط إِلَى منزلهما فَقَالَ لَهُ جَابر هَل لَك فِي عِيَادَة عَمْرو الْبكالِي فَقَالَ نعم فَانْطَلقَا حَتَّى دخلا عَلَيْهِ فوجدا الْجند قد عادوه وَهُوَ قَاعد يُحَدِّثهُمْ فَذكر ذَاكر التنين فَقَالَ لَهُم عَمْرو أَو مَا تَدْرُونَ كَيفَ يكون التنين فَقَالُوا وَكَيف يكون تنينا فَقَالَ يكون حَيَّة فيعدو على حَيَّة فيأكلها ثمَّ يَأْكُل الْحَيَّات فَلَا يزَال يأكلهم ويعظم وينتفخ وَيُزَاد فِي حمته حَتَّى يحرق فيعدو على','2','393',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (748,'الأَرْض فيهلكها فيسوقه الله حَتَّى يَأْتِي نَهرا قد سَمَّاهُ فيضربه تيار المَاء حَتَّى يدْخلهُ الْبَحْر فيصنع بدواب الْبَحْر كَمَا صنع بدواب الْبر وَيُزَاد فِي حمته حَتَّى يعج مِنْهُ دَوَاب الْبَحْر إِلَى الله فيبعث الله إِلَيْهِ ملكا فيزمه حَتَّى يخرج رَأسه من المَاء ثمَّ تدلى إِلَيْهِ السَّحَاب والبروق فتحمله فتلقيه إِلَى يَأْجُوج وَمَأْجُوج جزرا لَهُم يجتزرونه كَمَا تجتزرون الْإِبِل وَالْبَقر
١٠٦٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو الْيَمَان قَالَ حَدثنَا صَفْوَان بن عَمْرو عَن شُرَيْح بن عبيد عَن كَعْب مثل ذَلِك
<span data-type="title" id=toc-721>مِقْدَار كَفَّارَة الْيَمين</span>

١٠٦٨ - وَسَأَلته عَن كَفَّارَة الْيَمين فَقَالَ ثَلَاثَة أَرْطَال غير ثلث لكل مِسْكين وَإِلَّا فرطل وَثلث دَقِيق','2','394',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (749,'<span data-type="title" id=toc-722>اعْتِبَار النِّيَّة فِي الْإِيمَان</span>

١٠٦٩ - وَسَأَلته عَمَّن حلف أَن لَا يَأْكُل لَحْمًا فَأكل شحما قَالَ إِن كَانَ إِنَّمَا حلف أَن يدْفع عَن نَفسه مَنْفَعَة اللَّحْم وَالدَّسم فَلَا يَأْكُل الشَّحْم وَإِن كَانَ إِنَّمَا حلف على اللَّحْم لِأَنَّهُ تأذى مِنْهُ فَلَا بَأْس أَن يَأْكُل الشَّحْم
١٠٧٠ - قَالَ وَكَذَلِكَ لَو أَن رجلا كَانَ يمن على رجل بِمَا يُعْطِيهِ فَحلف أَن لَا يقبل مِنْهُ دَرَاهِم قَالَ إِن كَانَ إِنَّمَا يُرِيد أَن يدْفع عَنهُ منَّة فَلَا يقبل مِنْهُ شَيْئا لَا ثوبا وَلَا غَيره لِأَنَّهُ إِنَّمَا أَرَادَ أَن يدْفع عَن نَفسه منَّة','2','395',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (750,'<span data-type="title" id=toc-723>تخطئة أبي جزي جرير بن حَازِم فِي رِوَايَة</span>

١٠٧١ - قَالَ أبي قَالَ عَفَّان جَاءَ أَبُو جزي إِلَى جرير بن حَازِم يشفع لإِنْسَان قَالَ فَقَالَ جرير حَدثنَا قَتَادَة عَن أنس قَالَ كَانَت قبيعة سيف رَسُول الله صلى الله عَلَيْهِ وَسلم من فضَّة قَالَ قَالَ أَبُو جزي وَأَخْطَأ فِيهِ جرير بن حَازِم','2','396',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (751,'<span data-type="title" id=toc-724>قَول مَالك فِي الْإِيمَان وَخلق الْقُرْآن</span>

١٠٧٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا سُرَيج بن النُّعْمَان قَالَ أَخْبرنِي عبد الله بن نَافِع قَالَ كَانَ مَالك يَقُول أَنا مُؤمن وَيَقُول الْإِيمَان قَول وَعمل وَيَقُول كلم الله مُوسَى ويستفظع قَول من','2','397',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (752,'يَقُول الْقُرْآن مَخْلُوق قَالَ يوجع ضربا وَيحبس حَتَّى يَتُوب وَقَالَ مَالك الله فِي السَّمَاء وَعلمه فِي كل مَكَان لَا يَخْلُو مِنْهُ شَيْء
<span data-type="title" id=toc-725>رِوَايَة فِي عايذ وَالْحكم الْغِفَارِيّ</span>

١٠٧٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ مَنْصُور أخبرنَا عَن ابْن سِيرِين أَن عايذ بن عَمْرو وَالْحكم الْغِفَارِيّ كَانَا يتخاوفان
<span data-type="title" id=toc-726>بَدَأَ الْعَلَاء بن الْحَضْرَمِيّ بِنَفسِهِ فِي الْكتاب</span>

١٠٧٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم عَن مَنْصُور عَن ابْن سِيرِين عَن بعض ولد الْعَلَاء قَالَ كَانَ الْعَلَاء بن الْحَضْرَمِيّ','2','398',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (753,'عَاملا للنَّبِي صلى الله عَلَيْهِ وَسلم فَكَانَ إِذا كتب إِلَيْهِ النَّبِي صلى الله عَلَيْهِ وَسلم بَدَأَ بِنَفسِهِ
<span data-type="title" id=toc-727>قَول أَحْمد فِي يحيى بن عباد</span>

١٠٧٥ - سَأَلت أبي عَن يحيى بن عباد فَقَالَ كتبت عَنهُ حَدِيثا وَاحِدًا
١٠٧٦ - فَقلت فأيش حَاله قَالَ مَا أعلم عَلَيْهِ حجَّة قَالَ أبي أول مَا رَأَيْته فِي مجْلِس أَسْبَاط كيس يذاكر الحَدِيث
<span data-type="title" id=toc-728>أَوْلَاد إِبْلِيس وأعمالهم</span>

١٠٧٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هَاشم بن الْقَاسِم أَبُو النَّضر قَالَ حَدثنَا مُحَمَّد يَعْنِي ابْن طَلْحَة عَن زبيد عَن مُجَاهِد قَالَ إِن','2','399',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (754,'لإبليس خَمْسَة من وَلَده قد جعل كل وَاحِد مِنْهُم على شَيْء من أمره قَالَ ثمَّ سماهم فَذكر ثبر والأعور ومسوط وداسم وزلنبور فَأَما ثبر فَهُوَ صَاحب المصيبات الَّذِي يَأْمر بالثبور وشق الْجُيُوب وَلَطم الخدود وَدَعوى الْجَاهِلِيَّة وَأما الْأَعْوَر فَهُوَ صَاحب الزِّنَا الَّذِي يَأْمر بِهِ ويزينه ويعمي عَنهُ وَأما مسوط فَهُوَ صَاحب الْكَذِب الَّذِي يشيع الْكَذِب فَيلقى الرجل فيخبره بالْخبر فَينْطَلق الرجل إِلَى الْقَوْم فَيَقُول لقِيت رجلا أعرف وَجهه وَلَا أَدْرِي مَا أُسَمِّهِ حَدثنِي بِكَذَا وَكَذَا وَمَا هُوَ إِلَّا هُوَ وَأما داسم الَّذِي يدْخل مَعَ الرجل إِلَى أَهله يرِيه الْعَيْب فيهم ويغضبه عَلَيْهِم وَأما زلنبور فَهُوَ صَاحب راية السُّوق يركز رايته فِي السُّوق فَلَا يزالون ملتطمين','2','400',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (755,'<span data-type="title" id=toc-729>عِيَادَة الْأَوْزَاعِيّ ابْن سِيرِين</span>

١٠٧٨ - قَالَ أبي يحْكى عَن الْأَوْزَاعِيّ قَالَ دَخَلنَا على ابْن سِيرِين فعدناه من قيام
<span data-type="title" id=toc-730>مسح أبي الْعَالِيَة على رجله لأجل الْمَرَض</span>

١٠٧٩ - حَدثنَا أبي سنة تسع وَعشْرين وَمِائَتَيْنِ فِي رَجَب قَالَ حَدثنَا عبد الْمُؤمن بن عبد الله بن خَالِد أَبُو الْحسن الْعَبْسِي الْكُوفِي سنة اثْنَتَيْنِ وَثَمَانِينَ قَالَ حَدثنَا دَاوُد قَالَ اشْتَكَى أَبُو الْعَالِيَة رجله ثمَّ تَوَضَّأ وَمسح عَلَيْهَا وَقَالَ هَذِه مَرِيضَة','2','401',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (756,'<span data-type="title" id=toc-731>إِذا شَكَكْتُمْ فِي الْيَاء وَالتَّاء فاجعلوها يَاء</span>

١٠٨٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الْمُؤمن قَالَ حَدثنَا دَاوُد عَن الشّعبِيّ عَن عَلْقَمَة بن قيس النَّخعِيّ قَالَ كَانَ عبد الله يَقُول إِذا شَكَكْتُمْ فِي الْيَاء وَالتَّاء فاجعلوها يَاء فَإِن الْقُرْآن ذكر فذكروه
<span data-type="title" id=toc-732>حكم بيع الْمَصَاحِف</span>

١٠٨١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الْمُؤمن عَن دَاوُد قَالَ سَأَلت أَبَا الْعَالِيَة عَن بيع الْمَصَاحِف فَقَالَ لَو لم يبيعوك لم تشتر قَالَ وَأما الشّعبِيّ فَقَالَ إِنَّمَا يبيعونك أجر أَيْديهم وَالْوَرق وَلَا يبيعون كتاب الله قَالَ أبي سَمِعت من عبد الْمُؤمن قبل موت هشيم','2','402',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (757,'<span data-type="title" id=toc-733>من أَقْوَال إِبْرَاهِيم النَّخعِيّ وأفعاله</span>

١٠٨٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عَمْرو بن مجمع الْكُوفِي أَبُو الْمُنْذر قَالَ حَدثنَا يُونُس بن خباب أَبُو حَمْزَة قَالَ كَانَ إِبْرَاهِيم النَّخعِيّ يلبس الملاحف الْحمر وَقَالَ إِبْرَاهِيم الْكفْر كفران كفر بِاللَّه وَكفر بِالنعَم وَقَالَ إِبْرَاهِيم','2','403',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (758,'من لَا يشْكر النَّاس لَا يشْكر الله وَقَالَ إِبْرَاهِيم سووا مناكبكم لَا تخْتَلف قُلُوبكُمْ ويتخللكم الشَّيْطَان كأولاد الْحَذف
<span data-type="title" id=toc-734>منع الشّعبِيّ من إنكاح الْوَلِيّ كريمته من الْفَاسِق</span>

١٠٨٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عَليّ بن مُجَاهِد الكابلي من أهل الرّيّ أَبُو مُجَاهِد فِي سنة اثْنَتَيْنِ وَثَمَانِينَ وَمِائَة قَالَ أخبرنَا','2','404',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (759,'الْخَلِيل بن زُرَارَة عَن مطرف عَن الشّعبِيّ قَالَ من أنكح كريمته من فَاسق فقد قطع رَحمَه
<span data-type="title" id=toc-735>هدي النَّبِي صلى الله عَلَيْهِ وَسلم عَام الْحُدَيْبِيَة</span>

١٠٨٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عَليّ بن مُجَاهِد عَن مُوسَى بن عُبَيْدَة عَن إِيَاس بن سَلمَة عَن أَبِيه قَالَ أهْدى','2','405',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (760,'رَسُول الله صلى الله عَلَيْهِ وَسلم فِي الْبدن عَام الْحُدَيْبِيَة جملا كَانَ تَحت أبي جهل يَوْم بدر فِي رَأسه برة من فضَّة
<span data-type="title" id=toc-736>كَيفَ يصنع بِمن مَاتَ فِي الْبَحْر</span>

١٠٨٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عَليّ بن مُجَاهِد عَن حجاج قَالَ سَأَلت عَطاء عَن الْمَيِّت يَمُوت فِي الْبَحْر قَالَ فَقَالُوا يكفنون ويحنطون ويغسلون وَيصلونَ عَلَيْهِ ويستقبلون بِهِ الْقبْلَة ويضعون على بَطْنه حجرا حَتَّى يرسب','2','406',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (761,'<span data-type="title" id=toc-737>قَول مُجَاهِد فِي دِيَة الْجَنِين</span>

١٠٨٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عَليّ بن مُجَاهِد عَن سُفْيَان الثَّوْريّ عَن لَيْث عَن مُجَاهِد فِي الْجَنِين غرَّة عبد أَو أمة قَالَ سُفْيَان قَالَ هِشَام بن عُرْوَة فرس أَو خَمْسمِائَة','2','407',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (762,'<span data-type="title" id=toc-738>صَلَاة سعيد بن جُبَير فِي المطاف</span>

١٠٨٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عَليّ بن مُجَاهِد عَن أبي شهَاب قَالَ رَأَيْت سعيد بن جُبَير يُصَلِّي فِي المطاف
<span data-type="title" id=toc-739>إِخْرَاج الْملك عَن أهل بَيت قتل مِنْهُم أحد رجلا من أهل بَيت نبوة</span>

١٠٨٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا الْحَارِث بن مرّة بن مجاعَة اليمامي أَبُو مرّة الْحَنَفِيّ قَالَ حَدثنَا مطر الْوراق أَنه لَيْسَ أحد من أهل بَيت مملكة يقتل رجلا من أهل بَيت نبوة إِلَّا أخرج الله الْملك من أهل ذَلِك الْبَيْت ثمَّ لَا يُعِيدهُ فيهم أبدا قَالَ فَقَالَ لَهُ أَبُو نَوْفَل قَالَ','2','408',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (763,'وَكَانَ يمازحه كثيرا هَذَا الْآن خطأ قد قتل الْحُسَيْن فِي خلَافَة يزِيد بن مُعَاوِيَة فَقَالَ إِنَّه لَيْسَ بِهَذَا يَا خَامِس إِنَّمَا هُوَ أَن يخرج الله الْملك من ذَلِك الرجل ثمَّ لَا يُعِيدهُ فِيهِ وَلَا فِي وَلَده
<span data-type="title" id=toc-740>الإِمَام ضَامِن والمؤذن مؤتمن</span>

١٠٨٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو حَفْص المعيطي قَالَ حَدثنَا عبد الْملك الْعَزْرَمِي قَالَ حَدثنَا عَطاء قَالَ قَالَ رَسُول الله صلى الله عَلَيْهِ وَسلم الامام ضَامِن والمؤذن مؤتمن اللَّهُمَّ اغْفِر للمؤذنين وأرشد الْأَئِمَّة','2','409',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (764,'<span data-type="title" id=toc-741>تكني عَائِشَة بِعَبْد الله بن الزبير</span>

١٠٩٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو حَفْص قَالَ حَدثنَا هِشَام عَن أَبِيه عَن عَائِشَة قَالَت قَالَ النَّبِي صلى الله عَلَيْهِ وَسلم أَلا تكتنين قلت بِمن أكتنى قَالَ أكتني بابنك عبد الله يَعْنِي ابْن الزبير قَالَ فَكَانَت تكنى بِأم عبد الله','2','410',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (765,'<span data-type="title" id=toc-742>الْعَهْد يَمِين</span>

١٠٩١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا حُسَيْن بن الْوَلِيد النَّيْسَابُورِي قَالَ سَأَلت مسعر عَن الرجل يَقُول عَليّ عهد الله وميثاقه فَقَالَ قَالَ حَمَّاد الْعَهْد يَمِين
<span data-type="title" id=toc-743>خُرُوج ابْن عَبَّاس إِلَى التَّنْعِيم للْعُمْرَة</span>

١٠٩٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا حُسَيْن عَن عبد الله بن المؤمل عَن أبن أبي مليكَة عَن ابْن عَبَّاس أَنه كَانَ إِذا أَرَادَ أَن يعْتَمر خرج إِلَى التَّنْعِيم','2','411',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (766,'<span data-type="title" id=toc-744>قَول أَيُّوب السّخْتِيَانِيّ كَانُوا يحجون للفيء</span>

١٠٩٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا حُسَيْن عَن سُفْيَان عَن أَيُّوب يَعْنِي السّخْتِيَانِيّ قَالَ كَانُوا يحجون للفيء
<span data-type="title" id=toc-745>النَّهْي عَن حمل السِّلَاح يَوْم الْعِيد</span>

١٠٩٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا حُسَيْن عَن سُفْيَان عَن جُوَيْبِر عَن الضَّحَّاك أَن النَّبِي صلى الله عَلَيْهِ وَسلم نهى أَن يحمل السِّلَاح يَوْم عيد','2','412',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (767,'<span data-type="title" id=toc-746>الْأَخْرَس يُشِير إِلَى السَّمَاء عِنْد الذّبْح</span>

١٠٩٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا حُسَيْن قَالَ حَدثنَا خَارِجَة بن مُصعب عَن خَالِد الْحذاء قَالَ سُئِلَ عِكْرِمَة كَيفَ يذبح الْأَخْرَس قَالَ يُشِير بِيَدِهِ إِلَى الْمسَاء','2','413',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (768,'<span data-type="title" id=toc-747>لَيْسَ فِي الطواحين صَدَقَة</span>

١٠٩٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا حُسَيْن بن الْوَلِيد قَالَ حَدثنَا خَارِجَة عَن معمر عَن الْحُسَيْن قَالَ لَيْسَ فِي الطواحين صَدَقَة
<span data-type="title" id=toc-748>الصُّورَة فِي حجلة عُرْوَة</span>

١٠٩٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو حَفْص المعيطي قَالَ حَدثنَا هِشَام بن عُرْوَة قَالَ كَانَ لأبي حجلة فِيهَا الصُّورَة','2','414',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (769,'<span data-type="title" id=toc-749>فضل الْأَذَان</span>

١٠٩٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو حَفْص المعيطي عَن ابْن حَفْص قَالَ حَدثنَا أَبُو حَيَّان التَّيْمِيّ قَالَ حَدثنِي أبي قَالَ قَالَ عَليّ مَا نَدِمت على شَيْء ندامتي أَن لَا أكون سَأَلت النَّبِي صلى الله عَلَيْهِ وَسلم الْأَذَان لِلْحسنِ وَالْحُسَيْن
<span data-type="title" id=toc-750>قتال حَمْزَة يَوْم أحد بسيفين</span>

١٠٩٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُعَاوِيَة بن عَمْرو قَالَ حَدثنَا أَبُو إِسْحَاق عَن ابْن عون عَن عُمَيْر بن إِسْحَاق قَالَ','2','415',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (770,'كَانَ حَمْزَة يُقَاتل يَوْم أحد بَين يَدي رَسُول الله صلى الله عَلَيْهِ وَسلم بسيفين وَيَقُول أَنا أَسد الله
<span data-type="title" id=toc-751>رِوَايَة فِي كرمان وقندابيل</span>

١١٠٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُعَاوِيَة بن عَمْرو قَالَ حَدثنَا أَبُو إِسْحَاق قَالَ قَالَ عبد الله يَعْنِي ابْن عمر بَلغنِي أَن عمر قَالَ فِي أول مَا فتحت كرمان من يخبرنا عَن قندابيل قَالَ فَقَالَ','2','416',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (771,'رجل يَا أَمِير الْمُؤمنِينَ مَاؤُهَا وشل وتمرها دقل ولصها بَطل إِن كَانَ بهَا الْكثير جَاعُوا وَإِن كَانَ بهَا الْقَلِيل ضَاعُوا قَالَ أَنْت رجل شَاعِر قَالَ بل أَنا رجل خابر قَالَ عمر لَا يسألني الله عَن أحد من الْمُسلمين بعثته إِلَيْهَا أبدا
<span data-type="title" id=toc-752>تَارِيخ وَفَاة حَمَّاد بن زيد وهشيم</span>

١١٠١ - سَمِعت أبي يَقُول جَاءَنَا موت حَمَّاد بن زيد وَنحن على بَاب هشيم يملي علينا الْجَنَائِز فَقَالُوا مَاتَ حَمَّاد بن زيد وَمَات سنة ثِنْتَيْنِ وَثَمَانِينَ قبل موت هشيم بِسنة وَمَات هشيم فِي شعْبَان سنة ثَلَاث وَثَمَانِينَ','2','417',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (772,'وَهُوَ يَوْمئِذٍ ابْن تسع وَسبعين وَكَانَ أتقن من سُفْيَان بن عُيَيْنَة وَولد هشيم سنة أَربع وَمِائَة وَولد سُفْيَان بن عُيَيْنَة سنة سبع وَمِائَة
<span data-type="title" id=toc-753>قَول سعيد بن جُبَير فِي الْأَمِير</span>

١١٠٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا سيار بن حَاتِم أَبُو سَلمَة الْعَنزي قَالَ حَدثنَا جَعْفَر قَالَ حَدثنَا مَالك بن دِينَار قَالَ سَأَلت سعيد بن جُبَير وَهُوَ فِي الْمَسْجِد الْحَرَام يَا أَبَا عبد الله مَا أميركم هَذَا قَالَ يُفَسر الْقُرْآن تَفْسِيرا أزرقيا فِي طَاعَة شامية يَعْنِي الْحجَّاج
<span data-type="title" id=toc-754>من كَانَ حَامِل راية رَسُول الله صلى الله عَلَيْهِ وَسلم</span>

١١٠٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا سيار بن حَاتِم قَالَ حَدثنَا','2','418',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (773,'جَعْفَر قَالَ حَدثنَا مَالك قَالَ سَأَلت سعيد بن جُبَير قلت أَبَا عبد الله من كَانَ حَامِل راية رَسُول الله صلى الله عَلَيْهِ وَسلم قَالَ فَنظر إِلَيّ وَقَالَ أَنَّك لترخي اللبب قَالَ فَغضِبت وشكوته إِلَى إخْوَانِي من الْقُرَّاء قلت أَلا تعْجبُونَ من سعيد بن جُبَير إِنِّي سَأَلته من كَانَ حَامِل راية رَسُول الله صلى الله عَلَيْهِ وَسلم فَنظر إِلَيّ وَقَالَ إِنَّك لرخي اللبب فَقَالُوا لي وَأَنت حِين تسأله وَهُوَ خَائِف من الْحجَّاج قد لَاذَ بِالْبَيْتِ كَانَ حاملها عَليّ كَانَ حاملها عَليّ كَانَ حاملها عَليّ
<span data-type="title" id=toc-755>آخر الْجُزْء الثَّالِث عشر من أَجزَاء صَالح</span>

<span data-type="title" id=toc-756>رِسَالَة أبي عبد الله رَحمَه الله فِي الْقُرْآن</span>

١١٠٤ - بِسم الله الرَّحْمَن الرَّحِيم وَصلى الله على سيدنَا مُحَمَّد وَآله حَدثنَا صَالح قَالَ كتب عبيد الله بن يحيى إِلَى أبي رَحْمَة الله عَلَيْهِ يُخبرهُ أَن أَمِير الْمُؤمنِينَ أَمرنِي أَن أكتب إِلَيْك أَسأَلك عَن أَمر الْقُرْآن لَا مَسْأَلَة','2','419',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (774,'الْمُؤمنِينَ أَمرنِي أَن أكتب إِلَيْك أَسأَلك عَن أَمر الْقُرْآن لَا مَسْأَلَة امتحان وَلَكِن مَسْأَلَة معرفَة وبصيرة فأملى عَليّ أبي رَحْمَة الله إِلَى عبيد الله أحسن الله عاقبتك أَبَا الْحسن فِي الْأُمُور كلهَا وَدفع عَنْك مكارة الدُّنْيَا وَالْآخِرَة برحمته قد كتبت إِلَيْك رَضِي الله عَنْك بِالَّذِي سَأَلَ عَنهُ أَمِير الْمُؤمنِينَ بِأَمْر الْقُرْآن بِمَا حضرني وَإِنِّي أسأَل الله أَن يديم توفيق أَمِير الْمُؤمنِينَ فقد كَانَ النَّاس فِي خوض من الْبَاطِل وَاخْتِلَاف شَدِيد يغتمسون فِيهِ حَتَّى أفضت الْخلَافَة إِلَى أَمِير الْمُؤمنِينَ فنفى الله بأمير الْمُؤمنِينَ كل بِدعَة وانجلى عَن النَّاس مَا كَانُوا فِيهِ من الذل وضيق المحابس فصرف الله ذَلِك كُله وَذهب بِهِ أَمِير الْمُؤمنِينَ وَوَقع ذَلِك من الْمُسلمين موقعا عَظِيما ودعوا الله لأمير الْمُؤمنِينَ','2','420',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (775,'فأسأل الله أَن يستجيب فِي أَمِير الْمُؤمنِينَ صَالح الدُّعَاء وَأَن يتم ذَلِك لأمير الْمُؤمنِينَ وَيزِيد فِي نِيَّته ويعينه على مَا هُوَ عَلَيْهِ وَقد ذكر عَن عبد الله بن عَبَّاس رَحْمَة الله عَلَيْهِ أَنه قَالَ لَا تضربوا كتاب الله بعضه بِبَعْض فَإِن ذَلِك يُوقع الشَّك فِي قُلُوبكُمْ وَذكر عبد الله بن عَمْرو أَن نَفرا كَانُوا جُلُوسًا بِبَاب النَّبِي صلى الله عَلَيْهِ وَسلم فَقَالَ بَعضهم ألم يقل الله كَذَا وَقَالَ بَعضهم ألم يقل الله كَذَا قَالَ فَسمع ذَلِك رَسُول الله صلى الله عَلَيْهِ وَسلم فَخرج فَكَأَنَّمَا فقىء فِي وَجهه حب الرُّمَّان فَقَالَ أَبِهَذَا أمرْتُم أَن تضربوا كتاب الله بعضه بِبَعْض إِنَّمَا ضلت الْأُمَم قبلكُمْ فِي مثل هَذَا إِنَّكُم لَسْتُم مِمَّا هَا هُنَا فِي شَيْء انْظُرُوا الَّذِي أمرْتُم بِهِ فاعملوا بِهِ وانظروا الَّذِي نهيتم عَنهُ فَانْتَهوا عَنهُ','2','421',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (776,'وروى عَن أبي هُرَيْرَة عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ مراء فِي الْقُرْآن كفر وروى عَن أبي جهيم رجل من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه قَالَ لَا تماروا فِي الْقُرْآن فَإِن مراء فِيهِ كفر وَقَالَ عبد الله بن عَبَّاس قدم على عمر بن الْخطاب رجل فَجعل عمر يسْأَله عَن النَّاس فَقَالَ يَا أَمِير الْمُؤمنِينَ قد قَرَأَ الْقُرْآن مِنْهُم كَذَا وَكَذَا فَقَالَ ابْن عَبَّاس فَقلت وَالله مَا أحب أَن يتسارعوا يومهم هَذَا فِي الْقُرْآن هَذِه المسارعة قَالَ فزبرني عمر','2','422',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (777,'ثمَّ قَالَ مَه فَانْطَلَقت إِلَى منزلي مكتئبا حَزينًا فَبَيْنَمَا أَنا كَذَلِك إِذْ أَتَانِي رجل فَقَالَ أجب أَمِير الْمُؤمنِينَ فَخرجت فَإِذا هُوَ بِالْبَابِ ينتظرني فَأخذ بيَدي فَخَلا بِي وَقَالَ مَا الَّذِي كرهت مِمَّا قَالَ الرجل قَالَ إِنَّمَا قلت يَا أَمِير الْمُؤمنِينَ مَتى مَا يتسارعوا هَذِه المسارعة يحتقوا وَمَتى مَا يحتقوا يختصموا وَمَتى مَا يختصموا يَخْتَلِفُوا وَمَتى مَا يَخْتَلِفُوا يقتتلوا قَالَ لله أَبوك وَالله إِن كنت','2','432',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (778,'لأكاتمها النَّاس حَتَّى جِئْت بهَا وَرُوِيَ عَن جَابر بن عبد الله قَالَ كَانَ النَّبِي صلى الله عَلَيْهِ وَسلم يعرض نَفسه على النَّاس بالموقف فَيَقُول هَل من رجل يحملني إِلَى قومه فَإِن قُريْشًا قد مَنَعُونِي أَن أبلغ كَلَام رَبِّي وَرُوِيَ عَن جُبَير بن نفير قَالَ قَالَ رَسُول الله صلى الله عَلَيْهِ وَسلم إِنَّكُم لن ترجعوا إِلَى الله بِشَيْء أفضل مِمَّا خرج مِنْهُ يَعْنِي الْقُرْآن وَرُوِيَ عَن أبي أُمَامَة عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ مَا تقرب','2','424',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (779,'الْعباد إِلَى الله بِمثل مَا خرج مِنْهُ يَعْنِي الْقُرْآن وَرُوِيَ عَن عبد الله بن مَسْعُود أَنه قَالَ جردوا الْقُرْآن وَلَا تكْتبُوا فِيهِ شَيْئا إِلَّا كَلَام الله وَرُوِيَ عَن عمر بن الْخطاب أَنه قَالَ إِن هَذَا الْقُرْآن كَلَام الله فضعوه على موَاضعه وَقَالَ رجل لِلْحسنِ الْبَصْرِيّ يَا أَبَا سعيد إِذا قَرَأت كتاب الله وتدبرته وَنظرت فِي عَمَلي كدت أَن أيأس وَيَنْقَطِع رجائي','2','425',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (780,'قَالَ فَقَالَ لَهُ الْحسن إِن الْقُرْآن كَلَام الله وأعمال بني آدم إِلَى الضعْف وَالتَّقْصِير فاعمل وأبشر وَقَالَ فَرْوَة بن نَوْفَل الْأَشْجَعِيّ كنت جارا لخباب وَهُوَ من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم فَخرجت مَعَه يَوْمًا من الْمَسْجِد وَهُوَ آخذ بيَدي فَقَالَ يَا هناة تقرب الله إِلَى بِمَا اسْتَطَعْت فَإنَّك لن تقرب إِلَيْهِ بِشَيْء أحب إِلَيْهِ من كَلَامه وَقَالَ رجل للْحكم بن عتيبة مَا حمل أهل الْأَهْوَاء على هَذَا قَالَ الْخُصُومَات وَقَالَ مُعَاوِيَة بن قُرَّة وَكَانَ أَبوهُ مِمَّن أَتَى النَّبِي صلى الله عَلَيْهِ وَسلم إيَّاكُمْ','2','426',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (781,'وَهَذِه الْخُصُومَات فَإِنَّهَا تحبط الْأَعْمَال وَقَالَ أَبُو قلاية وَكَانَ أدْرك غير وَاحِد من أَصْحَاب رَسُول الله صلى الله عَلَيْهِ وَسلم لَا تجالسوا أَصْحَاب الْأَهْوَاء أَو قَالَ أَصْحَاب الْخُصُومَات فَإِنِّي لَا آمن أَن يغمسوكم فِي ضلالتهم ويلبسوا عَلَيْكُم بعض مَا تعرفُون وَدخل رجلَانِ من أَصْحَاب الْأَهْوَاء على مُحَمَّد بن سِيرِين فَقَالَا يَا أَبَا بكر نحدثك بِحَدِيث قَالَ لَا قَالَا فنقرأ عَلَيْك آيَة من كتاب الله قَالَ لَا لتقومان عني أَو لأقومنه قَالَ فَقَامَ الرّجلَانِ فَخَرَجَا فَقَالَ بعض الْقَوْم يَا أَبَا بكر مَا كَانَ عَلَيْك أَن يقرآ عَلَيْك آيَة من كتاب الله فَقَالَ مُحَمَّد بن سِيرِين إِنِّي خشيت أَن يقرآ عَليّ آيَة فيحرفانها فَيقر ذَلِك فِي قلبِي فَقَالَ مُحَمَّد لَو أعلم أَنِّي أكون مثل السَّاعَة لتركتهما وَقَالَ','2','427',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (782,'رجل من أهل الْبدع لأيوب السّخْتِيَانِيّ يَا أَبَا بكر أَسأَلك عَن كلمة فولى وَهُوَ يَقُول بِيَدِهِ وَلَا نصف كلمة وَقَالَ ابْن طَاوس لِابْنِ لَهُ وَتكلم رجل من أهل الْبدع أَدخل أصبعيك فِي أذنيك حَتَّى لَا تسمع مَا يَقُول ثمَّ قَالَ اشْدُد اشْدُد وَقَالَ عمر بن عبد الْعَزِيز من جعل دينه عرضا للخصومات أَكثر التنقل وَقَالَ إِبْرَاهِيم النَّخعِيّ إِن الْقَوْم لم يدّخر عَنْهُم شَيْء خبىء لكم الْفضل عنْدكُمْ وَكَانَ الْحسن الْبَصْرِيّ يَقُول شرداء خالط قلبا يعْنى الْهوى','2','428',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (783,'وَقَالَ حُذَيْفَة بن الْيَمَان وَكَانَ من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم اتَّقوا الله معاشر الْقُرَّاء خُذُوا طَرِيق من كَانَ قبلكُمْ وَالله لَئِن اسْتَقَمْتُمْ لقد سبقتم سبقا بَعيدا وَلَئِن تَرَكْتُمُوهُ يَمِينا وَشمَالًا لقد ضللتم ضلالا بَعيدا أَو قَالَ مُبينًا وَإِنَّمَا تركت ذكر الْأَسَانِيد لما تقدم من الْيَمين الَّتِي قد حَلَفت بهَا مِمَّا قد علمه أَمِير الْمُؤمنِينَ لَوْلَا ذَلِك ذكرتها بأسانيدها وَقد قَالَ الله جلّ ثَنَاؤُهُ {وَإِن أحد من الْمُشْركين استجارك فَأَجره حَتَّى يسمع كَلَام الله} وَقَالَ {أَلا لَهُ الْخلق وَالْأَمر} فَأخْبر تبَارك وَتَعَالَى بالخلق ثمَّ قَالَ وَالْأَمر فَأخْبر أَن الْأَمر غير الْخلق وَقَالَ تبَارك وَتَعَالَى {الرَّحْمَن علم الْقُرْآن خلق الْإِنْسَان علمه الْبَيَان} فَأخْبر تبَارك وَتَعَالَى أَن الْقُرْآن','2','429',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (784,'من علمه وَقَالَ {وَلنْ ترْضى عَنْك الْيَهُود وَلَا النَّصَارَى حَتَّى تتبع ملتهم قل إِن هدى الله هُوَ الْهدى وَلَئِن اتبعت أهواءهم بعد الَّذِي جَاءَك من الْعلم مَا لَك من الله من ولي وَلَا نصير} وَقَالَ {وَلَئِن أتيت الَّذين أُوتُوا الْكتاب بِكُل آيَة مَا تبعوا قبلتك وَمَا أَنْت بتابع قبلتهم وَمَا بَعضهم بتابع قبْلَة بعض وَلَئِن اتبعت أهواءهم من بعد مَا جَاءَك من الْعلم إِنَّك إِذا لمن الظَّالِمين} وَقَالَ {وَكَذَلِكَ أَنزَلْنَاهُ حكما عَرَبيا وَلَئِن اتبعت أهواءهم بعد مَا جَاءَك من الْعلم مَا لَك من الله من ولي وَلَا واق} فالقرآن من علم الله وَفِي هَذِه الْآيَات دَلِيل على أَن الَّذِي جَاءَهُ صلى الله عَلَيْهِ وَسلم هُوَ الْقُرْآن لقَوْله {وَلَئِن اتبعت أهواءهم بعد الَّذِي جَاءَك من الْعلم} وَقد رُوِيَ عَن غير وَاحِد مِمَّن مضى من سلفنا أَنهم كَانُوا يَقُولُونَ الْقُرْآن كَلَام الله لَيْسَ بمخلوق وَهُوَ الَّذِي أذهب إِلَيْهِ وَلست بِصَاحِب كَلَام وَلَا أرى الْكَلَام فِي شَيْء من هَذَا إِلَّا مَا كَانَ فِي كتاب الله أَو فِي حَدِيث عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَو عَن أَصْحَابه رَحْمَة الله عَلَيْهِم أَو عَن التَّابِعين فَأَما غير ذَلِك فَإِن الْكَلَام فِيهِ غير مَحْمُود وَإِنِّي أسأَل الله أَن يُطِيل بَقَاء الْأَمِير وَأَن يُثبتهُ ويمده مِنْهُ بمعونة إِنَّه على كل شَيْء قدير','2','430',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (785,'<span data-type="title" id=toc-757>من تراجم بعض الرِّجَال</span>

١١٠٥ - حَدثنَا صَالح قَالَ سَمِعت أبي يَقُول بَلغنِي أَن شُعْبَة أَقَامَ على الحكم بن عتيبة ثَمَانِيَة عشر شهرا حَتَّى بَاعَ جُذُوع بَيته
١١٠٦ - وسمعته يَقُول لم يسمع سعيد بن أبي عرُوبَة من الحكم بن عتيبة وَلَا من حَمَّاد وَلَا من عَمْرو بن دِينَار وَلَا من هِشَام بن عُرْوَة وَلَا من إِسْمَاعِيل بن أبي خَالِد وَلَا من عبيد الله بن عمر وَلَا من أبي بشر وَلَا من زيد بن أسلم وَلَا من أبي الزِّنَاد قَالَ أبي وَحدث سعيد عَن هَؤُلَاءِ كلهم وَلم يسمع مِنْهُم وَرُبمَا قَالَ رجل عَنْهُم وَقَالَ قد سمع سعيد بن أبي عرُوبَة من عَاصِم بن أبي النجُود','2','431',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (786,'١١٠٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو الْأَشْهب هَوْذَة عَن هِشَام بن حسان عَن الْحسن قَالَ مر بِي أنس بن مَالك وَقد بَعثه زِيَاد إِلَى أبي بكرَة يعاتبه فَانْطَلَقت مَعَه فَدَخَلْنَا على الشَّيْخ وَهُوَ مَرِيض فأبلغه عَنهُ فَقَالَ إِنَّه يَقُول ألم أسْتَعْمل عبيد الله على فَارس ألم أسْتَعْمل رَوَّادًا على دَار الرزق ألم أسْتَعْمل عبد الرَّحْمَن على الدِّيوَان وَبَيت المَال فَقَالَ أَبُو بكرَة فَهَل زَاد على أَن أدخلهم النَّار قَالَ فَقَالَ أنس إِنِّي لَا أعلمهُ إِلَّا مُجْتَهدا فَقَالَ أَبُو بكرَة اقعدوني فَقَالَ قلت إِنِّي لَا أعلمهُ إِلَّا مُجْتَهدا وَأهل حروراء قد اجتهدوا أفأصابوا أم أخطأوا قَالَ الْحسن فرجعنا مخصومين','2','432',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (787,'١١٠٨ - قَالَ وَقَالَ أَبُو رَجَاء العطاردي رميت عليا بأسهم قَالَ يَا لهفي عَلَيْهَا
<span data-type="title" id=toc-758>اخْتِيَار أَحْمد رَبنَا وَلَك الْحَمد</span>

١١٠٩ - قَالَ أبي أخْتَار رَبنَا وَلَك الْحَمد وَمن قَالَ لَك فَلَا بَأْس
<span data-type="title" id=toc-759>رِوَايَة فِي تَفْسِير {يَوْم تبدل الأَرْض غير الأَرْض}</span>

١١١٠ - أملاه عَليّ أبي حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن أبي إِسْحَاق قَالَ سَمِعت عَمْرو بن مَيْمُون يحدث عَن عبد الله قَالَ فِي هَذِه الْآيَة {يَوْم تبدل الأَرْض غير الأَرْض} قَالَ أَرض كالفضة بَيْضَاء نقية لم يسفك عَلَيْهَا دم وَلم يعْمل فِيهَا خَطِيئَة يسمعهم الدَّاعِي وَينْفذهُمْ الْبَصَر حُفَاة عُرَاة قيَاما نحسب كَمَا خلقُوا حَتَّى يلجمهم الْعرق شكّ شُعْبَة فِي قيام وَحده','2','433',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (788,'قَالَ شُعْبَة ثمَّ سمعته يَقُول سَمِعت عَمْرو بن مَيْمُون وَلم يذكر عبد الله ثمَّ عاودته فَقَالَ حَدثنَا هُبَيْرَة عَن عبد الله
<span data-type="title" id=toc-760>كنية عبد الله بن أبي الْهُذيْل</span>

١١١١ - قَالَ أبي عبد الله بن أبي الْهُذيْل كنيته أَبُو الْمُغيرَة
<span data-type="title" id=toc-761>أدنى حد السَّجْدَة</span>

١١١٢ - قَالَ أبي حدث عباد بن الْعَوام يَوْمًا يحدث عَن سعيد بن أبي عرُوبَة عَن يعلي بن حَكِيم عَن عَطاء وَطَاوُس وَعِكْرِمَة وَلم يشك','2','434',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (789,'أَنهم قَالُوا إِذا أمكن جَبهته من الأَرْض فقد قضى مَا عَلَيْهِ من السُّجُود فَلم يشك
١١١٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْمَاعِيل قَالَ قَالَ سعيد بن أبي عرُوبَة وأظن عَطاء ثالثهم فَقَالَ أَبُو يَعْقُوب هُوَ مولى أبي عبيد الله إِن إِسْمَاعِيل يشك فِي عَطاء فَقَالَ عباد فَلهُ أَن يضع الْقَلَم عَن أُذُنه فَذكرُوا عبادا لإسماعيل فَقَالَ إِسْمَاعِيل مَا أعرفهُ مَا أعرفهُ يَعْنِي مَا أعرف عبادا
<span data-type="title" id=toc-762>تَصْحِيح الإِمَام أَحْمد لعبد الرَّحْمَن بن مهْدي كلمة أشرف</span>

١١١٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرَّحْمَن بن مهْدي عَن','2','435',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (790,'إِبْرَاهِيم بن سعد عَن الزُّهْرِيّ عَن عبد الله بن كَعْب بن مَالك أَن عمر فِي قصَّة الْجَيْش حِين بَعثهمْ فخطبهم عمر فَتكلم بِكَلَام قَالَ عبد الرَّحْمَن أسرف قَالَ أبي فَقلت إِن أَبَا كَامِل قَالَ قد أشرف عَلَيْهِم قَالَ أبي فَقَالَ لي عبد الرَّحْمَن سل بَهْزًا قَالَ أبي فَأتيت بَهْزًا فَسَأَلته فَقَالَ أشرف عَلَيْهِم
<span data-type="title" id=toc-763>من تراجم بعض الرِّجَال</span>

١١١٥ - حَدثنَا صَالح قَالَ حَدثنَا أبي قَالَ حَدثنَا عبد الرَّزَّاق قَالَ حَدثنَا الثَّوْريّ قَالَ إِذا ذكر عَمْرو بن قيس افتن فِيهِ افتنانا يَعْنِي سُفْيَان يثني عَلَيْهِ','2','436',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (791,'١١١٦ - قَالَ أبي كَانَ عمر بن عَليّ المقدي رجلا صَالحا يَقُولُونَ إِنَّه جَاءَ إِلَى معَاذ بن معَاذ فَأدى إِلَيْهِ مِائَتي ألف يَعْنِي كَانَت وَدِيعَة عِنْده
١١١٧ - إملاء حَدثنِي أبي قَالَ حَدثنَا يحيى بن عبد الْملك قَالَ حَدثنَا زَمعَة بن صَالح قَالَ كتب إِلَيّ أبي حَازِم بعض بني أُميَّة يعزم عَلَيْهِ أَلا رفع إِلَيْهِ حَوَائِجه فَكتب إِلَيْهِ أما بعد فقد جَاءَنِي كتابك تعرض عَليّ أَلا رفعت إِلَيْك حوائجي وهيهات رفعت حوائجي','2','437',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (792,'إِلَى مولائي فَمَا أَعْطَانِي مِنْهَا قبلت وَمَا أمسك عَليّ مِنْهَا قنعت
١١١٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن عبد الْملك بن حميد بن أبي غنية قَالَ حَدثنَا زَمعَة بن صَالح قَالَ قَالَ الزُّهْرِيّ لِسُلَيْمَان بن هِشَام أَلا تسْأَل أَبَا حَازِم مَا قَالَ فِي الْعلمَاء قَالَ يَا أَبَا حَازِم مَا قلت فِي الْعلمَاء قَالَ وَمَا عَسَيْت أَن أَقُول فِي الْعلمَاء إِلَّا خيرا إِنِّي أدْركْت الْعلمَاء وَقد استغنوا بعلمهم عَن أهل الدُّنْيَا وَلم يسْتَغْن أهل الدُّنْيَا بدنياهم عَن علمهمْ فَلَمَّا رأى ذَلِك هَذَا وَأَصْحَابه تعلمُوا الْعلم فَلم يستغنوا بِهِ وَاسْتغْنى أهل الدُّنْيَا بدنياهم عَن علمهمْ فَلَمَّا رَأَوْا ذَلِك قذفوا بعلمهم إِلَى أهل الدُّنْيَا وَلم ينلهم أهل الدُّنْيَا من دنياهم شَيْئا إِن هَذَا وَأَصْحَابه لَيْسُوا عُلَمَاء إِنَّمَا هم رُوَاة قَالَ الزُّهْرِيّ إِنَّه لجاري بَيت بِبَيْت وَمَا علمت أَن هَذَا عِنْده قَالَ صدق أما أَنِّي لَو كنت غَنِيا عَرفتنِي قَالَ فَقَالَ لَهُ سُلَيْمَان مَا الْمخْرج مِمَّا نَحن فِيهِ قَالَ تمْضِي مَا فِي يَديك لما أمرت','2','438',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (793,'بِهِ وتكف عَمَّا نهيت عَنهُ فَقَالَ سُبْحَانَ الله وَمن يُطيق هَذَا قَالَ من طلب الْجنَّة وفر من النَّار وَمَا هَذَا فِيمَا يطْلب أَو يفر مِنْهُ
١١١٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو الْمُغيرَة قَالَ حَدثنَا حريز قَالَ قَالَ جُبَير بن نفير جِئْت عبد الله بن عمر أستفتيه فِي بعض الْأَمر فَقَالَ مِمَّن أَنْت قلت من أهل حمص قَالَ تركت الْجند الْمُقدم نَاصِيَة أَصْحَاب مُحَمَّد سَارُوا بلواء النَّبِي صلى الله عَلَيْهِ وَسلم حَتَّى حلوا بهَا جَمِيعًا أما أَنا لَا أفتيك فِي شَيْء
١١٢٠ - قَالَ أبي عَليّ بن الْمُبَارك ثِقَة قَالَ كَانَت عِنْده كتب بَعْضهَا سَمعهَا من يحيى بن أبي كثير وَبَعضهَا عرض حَدثنَا عَنهُ يحيى بن سعيد قَالَ حَدثنِي عَليّ بن الْمُبَارك','2','439',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (794,'١١٢١ - وَقَالَ زَعَمُوا أَن عَليّ بن الْمُبَارك قَالَ جَاءَنِي يحيى بن سعيد
١١٢٢ - قَالَ أبي إِسْحَاق بن حَازِم شيخ ثِقَة إِلَّا أَنه كَانَ يرى الْقدر
١١٢٣ - قَالَ أبي أَبُو زيد الْهَرَوِيّ شيخ ثِقَة لم أسمع مِنْهُ شَيْئا بَصرِي
١١٢٤ - قَالَ أبي عِيسَى الحناط لَيْسَ بِشَيْء ضَعِيف','2','440',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (795,'١١٢٥ - وَقَالَ أبي السرى بن إِسْمَاعِيل أحب إِلَيّ من عِيسَى الحناط قَالَ أبي السّري لَيْسَ هُوَ بِالْقَوِيّ الحَدِيث
١١٢٦ - قَالَ أبي قَالَ عباد أَخطَأ هشيم فِي حَدِيث حُصَيْن عَن عَمْرو بن عبد الْملك بن الْحُوَيْرِث وَكَانَ هشيم يَقُول عبد الْملك بن عَمْرو بن الْحُوَيْرِث وَالْخَطَأ فِي يَد عباد وَأصَاب هشيم
١١٢٧ - قَالَ أبي وَكَانَ عباد رُبمَا قَالَ أنبأناه وأخبرناه وأنبأناه عَاصِم بن كُلَيْب','2','441',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (796,'١١٢٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ حُصَيْن أَنا عَن عبد الْملك بن عَمْرو بن الْحُوَيْرِث قَالَ حدثت أَن رَسُول الله صلى الله عَلَيْهِ وَسلم كَانَ مِمَّا يضع يَده الْيُمْنَى على الْيُسْرَى فِي الصَّلَاة وَكَانَ رُبمَا يمس لحيته وَهُوَ يُصَلِّي
١١٢٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن جَعْفَر قَالَ حَدثنَا شُعْبَة عَن حُصَيْن عَن عبد الْملك بن أخي عَمْرو بن حُرَيْث أَن النَّبِي صلى الله عَلَيْهِ وَسلم رُبمَا مس لحيته فِي الصَّلَاة','2','442',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (797,'١١٣٠ - قَالَ أبي سمع شُعْبَة من يزِيد بن الْبَراء بن عَازِب حَدِيثا وَاحِدًا
١١٣١ - قَالَ أبي عمر بن أَيُّوب لَيْسَ بِهِ بَأْس قدم علينا من الْموصل
١١٣٢ - قَالَ أبي كَانَ عبد الْملك بن أبي سُلَيْمَان من الْحفاظ إِلَّا أَنه كَانَ يُخَالف ابْن جريج فِي إِسْنَاد أَحَادِيث وَقَالَ أبن جريح أثبت عندنَا مِنْهُ
١١٣٣ - قَالَ أبي عَمْرو بن دِينَار وَابْن جريج أثبت النَّاس فِي عَطاء','2','443',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (798,'١١٣٤ - وسمعته يَقُول عمر بن عَامر روى عَنهُ مُعْتَمر بن سُلَيْمَان وأبن أبي عرُوبَة وَيزِيد بن زُرَيْع وَكَانَ يحيى بن سعيد أدْركهُ وَكَانَ لَا يرضاه وَعباد أروى النَّاس عَنهُ
١١٣٥ - سَمِعت أبي يَقُول وهيب بن خَالِد لَيْسَ بِهِ بَأْس
١١٣٦ - سمعته يَقُول سَمِعت ابْن إِدْرِيس قَالَ أخبرنَا لَيْث يَعْنِي بن أبي سليم عَن وبرة بن عبد الرَّحْمَن أبي خُزَيْمَة الْأَسْلَمِيّ','2','444',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (799,'١١٣٧ - قَالَ أبي كَانَت مسَائِل عبيد الله القواريري لهشيم عَن مُغيرَة عَن إِبْرَاهِيم وَيُونُس عَن الْحسن وَعبد الْملك عَن عَطاء قَالَ أبي وَكَانَ يسْأَله ويحدثه
<span data-type="title" id=toc-764>قَول الْأَعْمَش لَوْلَا الشُّهْرَة لتسحرت بعد الصَّلَاة</span>

١١٣٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع قَالَ قَالَ الْأَعْمَش لَوْلَا الشُّهْرَة لتسحرت بعد الصَّلَاة
<span data-type="title" id=toc-765>تراجم رجال</span>

١١٣٩ - قَالَ أبي دَاوُد بن عبد الله الأودي كُوفِي روى عَنهُ أَبُو عوَانَة وَزُهَيْر','2','445',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (800,'أَبُو خَيْثَمَة وروى دَاوُد الأودي عَن حميد بن عبد الرَّحْمَن
١١٤٠ - قَالَ أبي مَكْحُول اثْنَان مَكْحُول الْأَزْدِيّ بَصرِي مَكْحُول الشَّامي
١١٤١ - قَالَ أبي كَانَ وَكِيع يحدثنا ناسفيان يدغم فَقيل لَهُ بالأنبار إِنَّهُم يَكْتُبُونَ ناسفيان قَالَ أَلَيْسَ هوذا أحدثهم أَقُول حَدثنَا سُفْيَان','2','446',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (801,'١١٤٢ - قَالَ أبي دَاوُد بن يزِيد الأودي عَم ابْن إِدْرِيس وَدَاوُد عَم ابْن إِدْرِيس يحدث عَن الشّعبِيّ ضَعِيف الحَدِيث
١١٤٣ - قَالَ أبي الَّذِي روى عَنهُ أَبُو عوَانَة وَزُهَيْر أقدم من هَذَا وَهُوَ غير هَذَا وَهُوَ دَاوُد بن عبد الله
١١٤٤ - مَكْحُول الشَّامي كنيته أَبُو عبد الله قَالَ أبي هُوَ جليب أَو سبي','2','447',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (802,'١١٤٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد قَالَ سَأَلت شُعْبَة كم سَمِعت من أبي معشر قَالَ أَرْبَعَة بتر
١١٤٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا حَمَّاد الْخياط قَالَ قَالَ شُعْبَة مَا لَقِي إِبْرَاهِيم أَبَا عبد الله يَعْنِي الجدلي
١١٤٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد عَن إِسْمَاعِيل عَن عَامر قَالَ مَا رَأَيْت رجلا أفقه صاحبا من عبد الله بن مَسْعُود','2','448',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (803,'<span data-type="title" id=toc-766>إِنْكَار شُعْبَة رِوَايَة أبي الرّبيع عَن أبي بشر حَدِيث الطير ورد هشيم عَلَيْهِ</span>

١١٤٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى قَالَ كَانَ شُعْبَة يضعف حَدِيث أبي بشر عَن مُجَاهِد وَقَالَ حَدِيث الطير أَن ابْن عمر رأى قوما نصبوا طيرا يرمونه قَالَ شُعْبَة هَذَا الحَدِيث حَدِيث الْمنْهَال وَحدث بِهِ أَبُو الرّبيع السمان عَن أبي بشر فَأنكرهُ شُعْبَة فَقَالَ لَهُ هشيم أَنا سمعته من أبي بشر أيش تنكر عَلَيْهِ','2','449',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (804,'<span data-type="title" id=toc-767>إِذا علق رجل عتق عبد على بَيْعه وَالْآخر على شِرَائِهِ</span>

١١٤٩ - قلت إِذا قَالَ أَنْت حر إِن بِعْتُك وَقَالَ الآخر إِن اشْتَرَيْته فَهُوَ حر فَقَالَ قَالَ بعض النَّاس يعْتق من مَال المُشْتَرِي فَيلْزم من قَالَ هَذَا أَن لَا يُجِيز وَصِيَّة لمَيت لِأَن الْوَصِيَّة إِنَّمَا تجب بعد الْمَوْت وَقُلْنَا إِنَّه يعْتق من مَال البَائِع كَمَا تجب الْوَصِيَّة للْمُوصى لَهُ وَإِنَّمَا تجب بعد الْمَوْت
<span data-type="title" id=toc-768>من لحق بِأَرْض الْحَرْب أتبين مِنْهُ زَوجته</span>

١١٥٠ - قلت الرجل يلْحق بِأَرْض الْحَرْب أتبين مِنْهُ امْرَأَته قَالَ فِي هَذَا اخْتِلَاف قَالَ حجاج بن أَرْطَاة عَن عَمْرو بن شُعَيْب عَن','2','450',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (805,'أَبِيه عَن جده قَالَ رد النَّبِي صلى الله عَلَيْهِ وَسلم زَيْنَب إِلَى أبي الْعَاصِ بِالنِّكَاحِ','2','451',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (806,'وَابْن إِسْحَاق يَقُول فِي حَدِيثه إِن زَيْنَب أطلقت أَبَا الْعَاصِ فَهَذَا يدل على النِّكَاح الأول من اسْتِفْهَام فِي أطلقت أَبَا الْعَاصِ','2','452',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (807,'<span data-type="title" id=toc-769>حكم عبد يجيىء مُسلما إِلَى دَار الْإِسْلَام</span>

١١٥١ - وَقَالَ فِي عبد الْمُشْركين إِذا جَاءَ إِلَى دَار الْإِسْلَام وَهُوَ مُسلم فَهُوَ حر على حَدِيث أهل الطَّائِف وَإِذا جَاءَ الْمولى قبل العَبْد ثمَّ يجىء بعده مَوْلَاهُ مُسلما فَهُوَ لمَوْلَاهُ
<span data-type="title" id=toc-770>من دفع إِلَى رجل مَالا وَقَالَ اعْمَلْ بِهِ فَمَا رزق الله من ربح بيني وَبَيْنك</span>

١١٥٢ - وَقَالَ فِي رجل دفع مَالا إِلَى رجل فَقَالَ اعْمَلْ بِهِ فَمَا رزق الله من ربح بيني وَبَيْنك قَالَ هَذَا إِن خسر شَيْئا فعلى المَال وَجهه وَجه الْمُضَاربَة أَصْحَاب أبي حنيفَة يَقُولُونَ خلاف هَذَا','2','453',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (808,'<span data-type="title" id=toc-771>أَقْوَال أَحْمد فِي رجال</span>

١١٥٣ - وَقَالَ أَبُو عقيل الدَّوْرَقِي ثِقَة اسْمه بشير بن عقبَة روى عَنهُ يحيى بن سعيد وَأَبُو الْوَلِيد وَأَبُو عقيل الَّذِي روى عَنهُ اللَّيْث بن سعد اسْمه زهرَة بن معبد ثِقَة جده من أَصْحَاب','2','454',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (809,'النَّبِي صلى الله عَلَيْهِ وَسلم ١١٥٤
وَقَالَ بهية مَا أَدْرِي من يروي عَنْهَا سوى أبي عقيل يحيى بن المتَوَكل
١١٥٥ - عزْرَة روى عَنهُ قَتَادَة وَسليمَان التَّيْمِيّ وَدَاوُد بن أبي هِنْد وخَالِد الْحذاء
١١٥٦ - وَيزِيد بن هُرْمُز هُوَ يزِيد الْفَارِسِي روى عَنهُ عَوْف كَذَا حكوا','2','455',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (810,'عَن ابْن مهْدي
١١٥٧ - حَدِيث ابْن أبي بَصِير زُهَيْر وَإِسْرَائِيل يَقُولُونَ عَن ابْن أبي بَصِير عَن أَبِيه وَقَالَ شُعْبَة عَن ابْن أبي بَصِير قَالَ شُعْبَة','2','456',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (811,'عَن أبي إِسْحَاق قد سَمعه من أبي بَصِير مُرْسل
١١٥٨ - زُهَيْر وَإِسْرَائِيل وزَكَرِيا فِي حَدِيثهمْ عَن أبي إِسْحَاق لين سمعُوا مِنْهُ بآخرة وَشريك كَانَ أثبت فِي أبي إِسْحَاق مِنْهُم سمع قَدِيما وَزُهَيْر فِيمَا روى عَن الْمَشَايِخ ثَبت بخ بخ','2','457',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (812,'<span data-type="title" id=toc-772>حكم بيع الرَّقِيق من أهل الذِّمَّة إِذا كَانَ مِنْهُم</span>

١١٥٩ - وَقَالَ لَا يُبَاع الرَّقِيق من يَهُودِيّ أَو نَصْرَانِيّ أَو مَجُوسِيّ من كَانَ مِنْهُم وَذَلِكَ لِأَنَّهُ إِذا بَاعه أَقَامَ على الشّرك وَكتب فِيهِ عمر بن الْخطاب ينْهَى عَنهُ أُمَرَاء الْأَمْصَار فَانْتهى الْمُسلمُونَ لم يباعوا من أهل الذِّمَّة
<span data-type="title" id=toc-773>حكم زَكَاة الْفطر على العَبْد والْحَدِيث الْوَارِد فِيهِ</span>

١١٦٠ - وَقَالَ الجُمَحِي روى حديثين عَن عبيد الله بن عمر حَدِيث مِنْهُمَا فِي صَدَقَة الْفطر وَقد أنكر على مَالك هَذَا الحَدِيث وَمَالك','2','458',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (813,'إِذا انْفَرد بِحَدِيث فَهُوَ ثِقَة وَمَا قَالَ أحد مِمَّن قَالَ بِالرَّأْيِ أثبت مِنْهُ فِي الحَدِيث
١١٦١ - قَالَ أبي أنكر عَليّ الجُمَحِي هذَيْن الْحَدِيثين أَحدهمَا هَذَا وَالْآخر لَا أقدم عَلَيْهِ
١١٦٢ - وَقَالَ على العَبْد النَّصْرَانِي صَدَقَة الْفطر يَقُوله أَبُو هُرَيْرَة وَعَطَاء','2','459',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (814,'وأوضح فِي الْمَعْنى من قَالَ من الْمُسلمين لِأَن الصَّدَقَة طهرة وَهُوَ أقوى قد رَوَاهُ الْعمريّ الصَّغِير والجمحي وَمَالك
<span data-type="title" id=toc-774>كَيفَ يقْضِي من أفطر فِي رَمَضَان أَو غَيره</span>

١١٦٣ - وَقَالَ من أفطر من رَمَضَان أَو غَيره من مرض أَو سفر إِن صَامَ مُتَتَابِعًا فَهُوَ الَّذِي لَا اخْتِلَاف فِيهِ وَإِن صَامَ مُتَفَرقًا فَهِيَ رخصَة قَالَ الله {أَيَّامًا معدودات} وَقد قيل أحص الْعدة وصم كَيفَ شِئْت وَقد قيل أحص الْعدة وصم كَيفَ شِئْت','2','460',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (815,'وَقَالَ ابْن عمر صمه كَمَا أفطرته وَأنكر أبي على من يَقُول لَا يجْزِيه إِلَّا متتابع
<span data-type="title" id=toc-775>حكم الْخُرُوج للْجِهَاد لمن لَهُ بَنَات وَأم وَعَلِيهِ دين</span>

١١٦٤ - وَقَالَ فِي رجل لَهُ بَنَات وَأم وَعَلِيهِ دين وَله من يقوم بِدِينِهِ وأذنت لَهُ أمه أَن يَغْزُو فَإِن لم يكن لَهُ حُرْمَة يقوم بأَهْله يدْخل عَلَيْهِم لمَوْت أَو حَيَاة لَا أرى لَهُ الْخُرُوج لقَوْل النَّبِي صلى الله عَلَيْهِ وَسلم هَل تركت فِي أهلك من كَاهِل وَأَنا أذهب إِلَى ذَا وَلَكِنِّي أرى لَهُ أَن يُجهز غازيا','2','461',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (816,'أَو يخلفه فِي أَهله وَقد قَالَ النَّبِي صلى الله عَلَيْهِ وَسلم من جهز غازيا فَلهُ مثل أجره
<span data-type="title" id=toc-776>حكم تخريب الْكَنَائِس وتحريق النّخل</span>

١١٦٥ - وَقَالَ تخريب الْكَنَائِس وَمَا أشبههَا مَا ادري مَا هُوَ','2','462',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (817,'١١٦٦ - وَقَالَ تحريق النّخل قد قطع النَّبِي صلى الله عَلَيْهِ وَسلم نخل بني النَّضِير وَحرق
<span data-type="title" id=toc-777>الضحك فِي الصَّلَاة لَا ينْقض الْوضُوء</span>

١١٦٧ - وَقَالَ الضحك فِي الصَّلَاة لَا يُعَاد مِنْهُ الْوضُوء والْحَدِيث الَّذِي عَن أبي الْعَالِيَة ضَعِيف ويروى عَن أبي مُوسَى وَجَابِر يُعِيد','2','463',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (818,'الصَّلَاة وَلَا يُعِيد الْوضُوء وَالشعْبِيّ أَيْضا يَقُول ذَلِك','2','464',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (819,'<span data-type="title" id=toc-778>حكم الْقِرَاءَة يَوْم الْجُمُعَة خلف الإِمَام</span>

١١٦٨ - وَقَالَ يقْرَأ يَوْم الْجُمُعَة خلف الإِمَام إِذا لم يسمع الْقِرَاءَة
<span data-type="title" id=toc-779>حكم الْقِرَاءَة فِي الصَّلَاة</span>

١١٦٩ - وَقَالَ قَول عمر من لم يقْرَأ فِي الصَّلَاة أَنه يُعِيد الصَّلَاة إِذا لم يقْرَأ أذهب فِيهِ إِلَى حَدِيث جَابر مَالك عَن وهب بن كيسَان عَن جَابر قَالَ لَا صَلَاة إِلَّا بِقِرَاءَة فِي كل رَكْعَة
<span data-type="title" id=toc-780>من نسي سَجْدَة أَو أَكثر</span>

١١٧٠ - وَقَالَ فِي رجل ركع وَسجد سَجْدَة لَا تجزيه لِأَن كل رَكْعَة معقودة بسجدتين وَأَصْحَاب أبي حنيفَة يَقُولُونَ لَو أَن رجلا نسي أَربع سَجدَات من أَربع رَكْعَات أَنه يسْجد أَربع سَجدَات وَهُوَ جَالس','2','465',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (820,'وَآخَرُونَ يَقُولُونَ فِي رجل ترك سَجْدَتَيْنِ من أول صلَاته وَآخر صلَاته أَنه يَجْعَل السَّجْدَة الْآخِرَة مَعَ الأولى وَيقوم فَيصَلي رَكْعَة يَقُول هَذَا الشَّافِعِي وَلَا يُعجبنِي هَذَا وأذهب أَن كل رَكْعَة معقودة بسجدتين فَإِذا لم يَأْتِ فِي رَكْعَة بسجدتين لم يعْتد بِتِلْكَ رَكْعَة
<span data-type="title" id=toc-781>توقف من الْإِجَابَة فِي الإملاجة والإملاجتين</span>

١١٧١ - وَقَالَ الاملاجة والاملاجتان لَا أُجِيب فِيهَا بِشَيْء','2','466',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (821,'<span data-type="title" id=toc-782>صَلَاة الْجُمُعَة للْمُسَافِر</span>

١١٧٢ - وَقَالَ المسافرون يجمعُونَ يَوْم لجمعة قد صلى عبد الله يَوْم الْجُمُعَة فِي الْحَضَر فَجمع
<span data-type="title" id=toc-783>قَول أَحْمد فِي عَاصِم بن عَليّ وَعِكْرِمَة بن عمار</span>

١١٧٣ - وَقَالَ عَاصِم بن عَليّ بن عَاصِم مَا أقل خطأه قد عرض عَليّ بعض حَدِيثه','2','467',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (822,'١١٧٤ - عِكْرِمَة بن عمار لَا أُجِيب فِيهِ بِشَيْء وَقَالَ فِي غير يحيى بن أبي كثير أَرْجُو
<span data-type="title" id=toc-784>حكم السّفر يَوْم الْجُمُعَة</span>

١١٧٥ - وَقَالَ فِي الرجل يخرج يَوْم الْجُمُعَة من الْمصر لَا يخرج حَتَّى يجمع لَيْسَ هُوَ بِمَنْزِلَة الْمُسَافِر لَيْسَ عَلَيْهِ جمعه
<span data-type="title" id=toc-785>مَسَافَة الْقصر وَحكم السّفر وَحده</span>

١١٧٦ - وَقَالَ فِي الرجل يقصر من أَرْبَعَة برد
١١٧٧ - وَقَالَ فِي الرجل يسير وَحده قَالَ مَعَ الْجَمَاعَة أحب إِلَيّ وَقَالَ قَالَ الْقَاسِم بن مُحَمَّد بعث النَّبِي صلى الله عَلَيْهِ وَسلم يزِيد إِلَى رجل','2','468',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (823,'<span data-type="title" id=toc-786>هَل للأسير إِذا وجد السِّلَاح أَن يحمل على الْعَدو</span>

١١٧٨ - قلت الْأَسير يجد السَّيْف أَو السِّلَاح فَيحمل عَلَيْهِم وَهُوَ لَا يعلم أَنه لَا ينجو أعَان على نَفسه قَالَ أما سَمِعت قَول عمر حِين سَأَلَهُ الرجل فَقَالَ إِن أبي أَو خَالِي ألْقى بِيَدِهِ إِلَى التَّهْلُكَة فَقَالَ عمر ذَلِك أشترى الْآخِرَة بالدنيا','2','469',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (824,'<span data-type="title" id=toc-787>إِذا منع الْأَسير من الصَّلَاة كَيفَ يصنع</span>

١١٧٩ - وَقَالَ فِي الْأَسير يمْنَع أَن يصلى فَيتْرك الصَّلَاة ويقضيها بعد
١١٨٠ - قلت يؤمي أيماء وَهُوَ يخَاف قَالَ نعم {فَإِن خِفْتُمْ فرجالا أَو ركبانا} هُوَ بِمَنْزِلَة المطاردة','2','470',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (825,'<span data-type="title" id=toc-788>هَل يسرق الْأَسير من الْعَدو</span>

١١٨١ - قلت الْأَسير يسرق مِنْهُم قَالَ لَا يسرق إِذا كَانَ عِنْدهم فِي حد الْأَمَانَة وَلَكِن يَأْخُذ مِنْهُم أَو يطعم مِنْهُم وَإِن أمنوه على مَنَازِلهمْ فَلَا يَأْخُذ وَإِن ضيق عَلَيْهِ أَخذ قوته
<span data-type="title" id=toc-789>عمل الْأَسير للْكفَّار</span>

١١٨٢ - قلت الْأَسير يخيط لَهُم أَو يعْمل قَالَ إِذا كَانَ يجرى عَلَيْهِ أَو كَانَ مستغنيا فأكره أَن يعينهم فَإِن لم يجر عَلَيْهِ وضيق عَلَيْهِ فليعمل لَهُم
<span data-type="title" id=toc-790>حكم عبد خرج إِلَى الْمُسلمين بِأَمَان</span>

١١٨٣ - قلت لأبي العَبْد يخرج إِلَى الْمُسلمين بِأَمَان أَو ينزل من حصن قَالَ حر
<span data-type="title" id=toc-791>من وجد جَارِيَته أَو فرسه فِي الْغَنِيمَة</span>

١١٨٤ - وَقَالَ فِي الرجل بجد جَارِيَته أَو فرسه قَالَ هُوَ أَحَق بِهِ مَا لم يقسم فَإِن قسم يَأْخُذهُ بِالثّمن','2','471',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (826,'<span data-type="title" id=toc-792>حكم حرق الْمزَارِع وَقتل الْأُسَارَى</span>

١١٨٥ - قلت الزَّرْع يحرق قَالَ لَا يحرق لِأَنَّهُ مضرَّة عَلَيْهِم وعَلى الْمُسلمين لِأَنَّهُ تَجِيء السّريَّة فَلَا يصيبون علفا
١١٨٦ - قلت إِذا حاصر الْعَدو الْمُسلمين أَو أخذُوا عَلَيْهِم الطَّرِيق يضْربُونَ أَعْنَاق الْأُسَارَى قَالَ نعم يغيظونهم بِهِ كي يخلوا لَهُم
<span data-type="title" id=toc-793>حكم الطَّبْخ فِي قدور الْمُشْركين</span>

١١٨٧ - قلت الْقدر للْمُشْرِكين يطْبخ فِيهَا قَالَ إِن أُصِيب غَيرهَا فَلَا يطْبخ فِيهَا وَإِن لم يصب فلتغسل بِالْمَاءِ','2','472',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (827,'<span data-type="title" id=toc-794>مَحل الْمسْح على الْخُفَّيْنِ وَعدد الضَّرْبَة فِي التَّيَمُّم</span>

١١٨٨ - قَالَ أبي فِي التَّيَمُّم أذهب إِلَى حَدِيث عمار بن يَاسر ضَرْبَة
١١٨٩ - وَقَالَ فِي الْمسْح على ظُهُور الْخُفَّيْنِ
<span data-type="title" id=toc-795>من تيَمّم وَصلى ثمَّ وجد المَاء</span>

١١٩٠ - قلت من تيَمّم ثمَّ وجد المَاء يُعِيد الصَّلَاة قَالَ لَا يُعِيد قد تيَمّم ابْن عمر فِي وَقت فَلم يعد الصَّلَاة
<span data-type="title" id=toc-796>معنى الرِّدَّة وَمُدَّة اسْتِتَابَة الْمُرْتَد</span>

١١٩١ - وَقَالَ أبي الْمُرْتَد يُسْتَتَاب ثَلَاثَة أَيَّام حَدِيث عمر أَلا أدخلتموه بَيْتا وَابْن مَسْعُود استتاب وَقتل وَحَدِيث أنس يروي عَن عمر','2','473',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (828,'أدخلهم من الْبَاب الَّذِي خَرجُوا مِنْهُ أحب إِلَيّ من كَذَا وَكَذَا وقصة معَاذ قدم الْيمن وَقد كَانَ أَبُو مُوسَى استتاب الرجل شهرا فَقَالَ','2','474',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (829,'معَاذ لَا أنزل حَتَّى أضْرب عُنُقه
١١٩٢ - وَقَالَ أبي التبديل الْإِقَامَة على الشّرك فَأَما من تَابَ فَإِنَّهُ لَا يكون تبديلا أَرْجُو','2','475',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (830,'<span data-type="title" id=toc-797>حكم من تكلم بعد تَسْلِيمَة قبل تَمام الصَّلَاة</span>

١١٩٣ - حَدِيث ذِي الْيَدَيْنِ قصرت الصَّلَاة أَو نسيت قَالَ هَذَا الإِمَام يسْأَل إِذا ارتاب كي يثبت بِنَحْوِ مَا تكلم بِهِ النَّبِي صلى الله عَلَيْهِ وَسلم ١١٩٤
قلت فالرجل يكلم الإِمَام قَالَ الإِمَام لَا يُعِيد صلَاته وَمن كَلمه أعَاد صلَاته
١١٩٥ - قلت فقد كلم النَّبِي صلى الله عَلَيْهِ وَسلم فَلم يَأْمر بِالْإِعَادَةِ قَالَ لِأَن ذَا الْيَدَيْنِ كَانَت الصَّلَاة عِنْده مَقْصُورَة ثمَّ تمت فخاف أَن يكون رجعت إِلَى الْقصر فَقَالَ أنسيت يَا رَسُول الله أم قصرت الصَّلَاة فَقَالَ لم أنس وَلم تقصر الصَّلَاة وَالْيَوْم قد كملت فَهَذَا لَا يشبه حَال ذِي الْيَدَيْنِ','2','476',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (831,'<span data-type="title" id=toc-798>حكم الصَّوْم فِي سَبِيل الله</span>

١١٩٦ - قلت الرجل يَصُوم فِي سَبِيل الله فَلهُ كَذَا وَكَذَا الْفَرِيضَة قَالَ لَا يَصُوم فَإِن صَامَ لَا يُعِيد
١١٩٧ - قلت حَدِيث حَمْزَة بن عَمْرو الْأَسْلَمِيّ','2','477',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (832,'قَالَ ذَاك فِي الرُّخْصَة قَالَ النَّبِي صلى الله عَلَيْهِ وَسلم لَيْسَ الْبر الصَّوْم فِي السّفر وَقَالَ أَبُو سعيد لم يعب الصَّائِم على الْمُفطر وَلَا الْمُفطر على الصَّائِم وَقَالَ ابْن عَبَّاس الْإِفْطَار عَزمَة من كَانَ مَرِيضا أَو على سفر وَابْن عَبَّاس قَالَ صَامَ النَّبِي صلى الله عَلَيْهِ وَسلم حَتَّى بلغ الكديد ثمَّ أفطر','2','478',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (833,'قَالَ الزُّهْرِيّ فَيُؤْخَذ بِالْآخرِ من فعل رَسُول الله صلى الله عَلَيْهِ وَسلم يَعْنِي أفطر
<span data-type="title" id=toc-799>مَكَان رَكْعَتي الْغَدَاة وَحكم الْكَلَام بَينهمَا وَبَين صَلَاة الْفجْر</span>

١١٩٨ - قلت رَكعَتَا الْغَدَاة أَيْن يُصَلِّيهمَا قَالَ فِي بَيته
١١٩٩ - قلت يتَكَلَّم فِيمَا بَين الرَّكْعَتَيْنِ وَصَلَاة الْغَدَاة قَالَ الْكَلَام فِي قَضَاء الْحَاجة وَلَيْسَ الْكَلَام الْكثير كَانَ عبد الله يعز عَلَيْهِ أَن يسمع متكلما','2','479',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (834,'<span data-type="title" id=toc-800>بِأَيّ شَيْء يَدْعُو الْمَأْمُوم إِذا فرغ من التَّشَهُّد قبل الإِمَام</span>

١٢٠٠ - قلت الإِمَام يتَشَهَّد فيطيل فِي الجلسة الأولى فيفرغ الرجل قَالَ يُعِيد التَّشَهُّد
١٢٠١ - قلت فالتشهد الثَّانِي إِذا فرغ من التَّشَهُّد بِأَيّ شَيْء يَدْعُو قَالَ يتَخَيَّر من الدُّعَاء بِمثل مَا قَالَ ابْن مَسْعُود
<span data-type="title" id=toc-801>مَتى يقوم الرجل عِنْد الْإِقَامَة</span>

١٢٠٢ - قلت مَتى يقوم الرجل إِذا فرغ الْمُؤَذّن من الْإِقَامَة قَالَ إِذا قَالَ قد قَامَت الصَّلَاة','2','480',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (835,'<span data-type="title" id=toc-802>مَتى يطوف الْمُتَمَتّع لحجه</span>

١٢٠٣ - قلت الْمُتَمَتّع مَتى يطوف لحجه قَالَ إِذا رَجَعَ من منى غير أَن لَيْسَ عَلَيْهِ رمل بِالْبَيْتِ وَعَلِيهِ أَن يسْعَى بَين الصَّفَا والمروة ويجزيه للحجة وللزيارة وَلَا يجْزِيه طواف الزِّيَارَة من الطّواف بِحجَّة','2','481',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (836,'<span data-type="title" id=toc-803>هَل على أهل مَكَّة رمل وَكم يطوف الْمُتَمَتّع والقارن</span>

١٢٠٤ - وَقَالَ أبي لَيْسَ على أهل مَكَّة رمل
١٢٠٥ - وَقَالَ كَانَ ابْن عمر إِذا أهل بهما جَمِيعًا طَاف لَهما طَوافا وَاحِدًا وَإِذا تمتّع طَاف لَهما طوافين طَوافا لعمرته وطوافا لحجه','2','482',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (837,'وَيَقُول جَابر مَا طَاف أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم إِلَّا طَوافا وَاحِدًا
<span data-type="title" id=toc-804>بكم يرْمى الْجَمْرَة</span>

١٢٠٦ - قلت الرجل يَرْمِي الْجَمْرَة بِخمْس أَو سِتّ قَالَ خمس لَا وَلَكِن سِتّ أَو سبع كَمَا قَالَ سعد بن أبي وَقاص','2','483',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (838,'<span data-type="title" id=toc-805>من يتعجل فِي يَوْمَيْنِ هَل يَرْمِي لليوم الثَّالِث</span>

١٢٠٧ - قلت لأبي الرجل يتعجل فِي يَوْمَيْنِ يَرْمِي لليوم الآخر قَالَ لَا إِنَّمَا يَرْمِي لما حضر
<span data-type="title" id=toc-806>إِذا قَالَ أَنا يَوْم يَهُودِيّ وَيَوْم نَصْرَانِيّ أَو مَالِي فِي الْمَسَاكِين</span>

١٢٠٨ - وَقَالَ فِي الرجل يَقُول أَنا يَوْم يَهُودِيّ وَأَنا يَوْم نَصْرَانِيّ أَو مَجُوسِيّ اَوْ مَالِي فِي الْمَسَاكِين قَالَ قَالَ ابْن عمر كَفَّارَة يَمِين وَإِن تفرد بِيَهُودِيٍّ أَو نَصْرَانِيّ أَو وَاحِد من هَذِه الْيَمين قَالَ فِيهِ كَفَّارَة يَمِين وَإِذا جَمعهمَا فِيهِ كَفَّارَة وَاحِدَة
<span data-type="title" id=toc-807>إِذا قَالَ جاريتي حرَّة إِن لم أصنع كَذَا أَو مَالِي فِي الْمَسَاكِين</span>

١٢٠٩ - وَقَالَ أبي إِذا قَالَ جاريتي حرَّة إِن لم أصنع كَذَا وَكَذَا قَالَ ابْن عَبَّاس وَابْن عمر تعْتق وَإِذا قَالَ كل مَالِي فِي رتاج الْكَعْبَة أَو','2','484',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (839,'مَالِي فِي الْمَسَاكِين لم يدْخل فِيهِ جَارِيَته
١٢١٠ - قَالَ قَالَ أبي ذَا لَا يشبه ذَا أَلا ترى أَن ابْن عمر فرق بَينهمَا الْعتْق وَالطَّلَاق لَا يكفران وَقَالَ أَصْحَاب أبي حنيفَة يَقُولُونَ إِذا قَالَ الرجل مَالِي فِي الْمَسَاكِين إِنَّه يتَصَدَّق بِهِ على الْمَسَاكِين وَإِذا قَالَ مَالِي على فلَان صَدَقَة قَالُوا لَيْسَ بِشَيْء حَتَّى يقبضهُ وَإِذا قَالَ فِي الْمَسَاكِين خرج مِنْهُ إِلَّا قدر قوته فَكَانَ يَنْبَغِي أَن يكون قَوْله على','2','1210',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (840,'الْمَسَاكِين أبعد مِنْهُ على رجل بِعَيْنِه ويعجب مِمَّا يَقُولُونَ فِي الْحِيَل فِي الْأَيْمَان ويبطلون الْأَيْمَان بالحيل وَقَالَ الله تَعَالَى {وَلَا تنقضوا الْأَيْمَان بعد توكيدها} وَقَالَ الله {يُوفونَ بِالنذرِ} وَابْن عُيَيْنَة قَالَ لِسُفْيَان أفتى رجلا غير ثِقَة فاجترأ يَعْنِي أَبَا حنيفَة وَكَانَ ابْن عُيَيْنَة يشْتَد عَلَيْهِ أَمرهم وَأمر هَذِه الْحِيَل كَانَ الشّعبِيّ وَالْحكم يَقُولَانِ إِذا قَالَ الرجل مَالِي فِي الْمَسَاكِين لَيْسَ عَلَيْهِ كَفَّارَة وَلَا شَيْء وَكَانَ ابْن عمر إِذا حلف على يَمِين فكررها أعتق رَقَبَة وَإِذا حلف على يَمِين وَاحِدَة كفر كَفَّارَة وَاحِدَة','2','486',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (841,'<span data-type="title" id=toc-809>إِذا حلف على الدَّار أَو العَبْد إِن بَاعه بِكَذَا فَبَاعَهُ بِأَقَلّ مِنْهُ</span>

١٢١١ - قلت يَقُولُونَ إِذا حلف على العَبْد وَالدَّار إِن بعتهما بِكَذَا وَكَذَا فباعهما بِأَقَلّ من كَذَا وَكَذَا أَنه لَا يَحْنَث
قَالَ أبي سُبْحَانَ الله فَمَا يَقُولُونَ فِي رجل أوصى لرجل بِثُلثِهِ أَلَيْسَ يَقُولُونَ يجب لَهُ بعد الْمَوْت فَكَذَا إِذا قَالَ إِن بِعته فَهُوَ حر فَبَاعَهُ عتق من مَال البَائِع','3','5',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (842,'<span data-type="title" id=toc-810>كم يملك من الطَّلَاق من تزوج زَوجته الْمُطلقَة بعد زوج آخر</span>

١٢١٢ - وَقَالَ أبي إِذا طَلقهَا تَطْلِيقَة أَو تَطْلِيقَتَيْنِ فَتزوّجت زوجا فَدخل بهَا ثمَّ طَلقهَا أذهب إِلَى أَنَّهَا على مَا بَقِي من طَلاقهَا وَهُوَ أصح فِي الْمَعْنى
<span data-type="title" id=toc-811>من تَرْجَمَة مزِيد</span>

١٢١٣ - وَقَالَ أبي مزيدة الَّذِي روى عَنهُ الحكم مَعْرُوف روى عَنهُ ابْن أبي ليلى
<span data-type="title" id=toc-812>أَدِلَّة لثُبُوت الْحجر</span>

١٢١٤ - وَالْحجر لَو لم يكن فِي الْحجر حَدِيث إِلَّا حزر الْأَمْوَال','3','6',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (843,'وَالْحجاج يروي عَن ابْن عَبَّاس فِي الْحجر وَشُرَيْح يرْوى عَنهُ فِي الْحجر وَحَدِيث هِشَام بن عُرْوَة فِي قصَّة عُثْمَان أَرَادَ أَن يحْجر على عبد الله بن جَعْفَر لم أسمعهُ إِلَّا من أبي يُوسُف','3','7',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (844,'<span data-type="title" id=toc-813>من تَرْجَمَة مَنْصُور بن الْمُعْتَمِر</span>

١٢١٥ - مَنْصُور لم يرو عَن أبي صَالح ذكْوَان وروى عَن أبي صَالح باذام
<span data-type="title" id=toc-814>من صلى ثمَّ أدْرك الْجَمَاعَة هَل يُعِيد الصَّلَاة</span>

١٢١٦ - قلت الرجل يُصَلِّي ثمَّ يدْرك الْجَمَاعَة يُعِيد الصَّلَاة
قَالَ ابْن عمر كره أَن تُعَاد الصَّلَاة فَأَما إِذا دخلت وَأَنت لَا تعلم','3','8',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (845,'فَلَا تخرج حَتَّى تصلي على حَدِيث جَابر بن يزِيد بن الْأسود وَالْعصر والغداة كَذَلِك','3','9',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (846,'وَإِن دخل مُتَطَوعا يُصَلِّي مَعَ النَّاس لَا بَأْس إِلَّا الْمغرب فَإِنَّهُ يضيف إِلَيْهَا رَكْعَة','3','10',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (847,'<span data-type="title" id=toc-815>حَدِيث النَّهْي عَن بيع المَاء</span>

١٢١٧ - حَدِيث ابْن عُيَيْنَة عَن عَمْرو بن دِينَار عَن أبي الْمنْهَال عَن إِيَاس بن عبد المزني فِي بيع المَاء قَالَ عَمْرو لَا أَدْرِي أَي مَاء هُوَ
١٢١٨ - قلت الرجل يَسْتَقِي من دجلة أَو مَا أشبهه
قَالَ هَذَا لَا بَأْس بِهِ إِذا كَانَ قد تكلفه أَو حمله على ظَهره حَدِيث أبي هُرَيْرَة أَن النَّبِي صلى الله عَلَيْهِ وَسلم نهى عَن بيع فضل المَاء ليمنع بِهِ الكلاء وَنهى النَّبِي صلى الله عَلَيْهِ وَسلم عَن نقع الْبِئْر هَذَا مَا اجْتمع فِي الْبِئْر فَأَما إِذا تكلفه فَلَا بَأْس بِهِ
<span data-type="title" id=toc-816>مَا هُوَ المَاء الدَّائِم</span>

١٢١٩ - قَالَ أبي المَاء الدَّائِم مَا كَانَ لَيْسَ لَهُ مدد وكل شَيْء مَحْظُور عَلَيْهِ الْبِئْر يَقُولُونَ لَهَا عُيُون وَقَالَ الْبِئْر هُوَ مَحْظُور عَلَيْهَا
١٢٢٠ - قلت فَمثل حِيَاض مَكَّة
قَالَ ذَاك مَا تكلمُوا فِي مثل بِئْر بضَاعَة وَمَا يشبهها','3','11',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (848,'<span data-type="title" id=toc-817>حكم التَّوَارُث إِذا مَاتَ أحد الزَّوْجَيْنِ قبل الدُّخُول</span>

١٢٢١ - قلت حَدِيث بروع يَرِثهَا وترثه
قَالَ نعم
١٢٢٢ - قلت مَا الْحجَّة يَرِثهَا كَمَا تَرثه يرْوى عَن زيد بن ثَابت كره أَن يتَزَوَّج بِالْأُمِّ وَقَالَ لَا يرثهما جَمِيعًا كَأَنَّهُ تزوج مرّة فَمَاتَتْ قبل أَن يدْخل بهَا
قَالَ لَا يتَزَوَّج أمهَا يرْوى عَن زيد بن ثَابت','3','12',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (849,'<span data-type="title" id=toc-818>حكم الظِّهَار الموقت</span>

١٢٢٣ - وَقَالَ فِي رجل قَالَ لامْرَأَته أَنْت الْيَوْم عَليّ حرَام أَو كَظهر أُمِّي هَذَا قد وَقت فَإِذا كَانَ غَدا لاتحرم فَمن ذهب أَنَّهَا تحرم يشبهها بِالطَّلَاق وَأقوى فِي الْمذَاهب الَّذِي يزْعم أَنَّهَا لَا تحرم وأذهب إِلَيْهِ وَذَاكَ أَن الظِّهَار وَالْحرَام عِنْدِي يَمِين
١٢٢٤ - قلت فَمن يَقُول فِي الشَّيْء يُوَقت
قَالَ عَطاء بن أبي رَبَاح','3','13',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (850,'١٢٢٥ - قلت فَمن يَقُول إِنَّه يَقع عَلَيْهَا إِذا وَقت وقتا
قَالَ إِبْرَاهِيم وَغَيره وشبهوه بِالطَّلَاق وَهُوَ عِنْدِي لَا يشبه الطَّلَاق
<span data-type="title" id=toc-819>مَتى يجوز الْإِفْطَار للْمَرِيض</span>

١٢٢٦ - قلت الْمَرِيض مَتى يفْطر
قَالَ إِذا لم يسْتَطع
١٢٢٧ - قلت مثل الْحمى
قَالَ وَأي مرض أَشد من الْحمى قَالَ الله تَعَالَى {وَمن كَانَ مَرِيضا أَو على سفر}','3','14',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (851,'<span data-type="title" id=toc-820>إفطار الْحَامِل والمرضع خوفًا على نَفسهَا</span>

١٢٢٨ - قلت الْمُرْضع وَالْحَامِل تخَاف على نَفسهَا أتفطر
قَالَ إِذا أفطرت تقضي وَتطعم أذهب فِيهِ إِلَى حَدِيث أبي هُرَيْرَة
<span data-type="title" id=toc-821>كَيفَ يصنع من توالى عَلَيْهِ رمضانان</span>

١٢٢٩ - قلت فِي رجل توالى عَلَيْهِ رمضانان
قَالَ قَالَ أَبُو هُرَيْرَة يقْضِي وَيطْعم كل يَوْم مِسْكينا وَابْن عَبَّاس وَابْن عمر يَقُولَانِ يطعم وَلَا يَصُوم كَانَ ابْن عَبَّاس يتَأَوَّل يقْرؤهَا / يطوقونه / قَالَ يكلفونه وَمن قَرَأَ {يطيقُونَهُ} فَإِنَّهَا مَنْسُوخَة','3','15',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (852,'نسخهَا {فَمن شهد مِنْكُم الشَّهْر فليصمه} يرويهِ سعيد عَن قَتَادَة عَن عُرْوَة عَن سعيد بن جُبَير عَن ابْن عَبَّاس
<span data-type="title" id=toc-822>مصارف الزَّكَاة وَحكم نقلهَا إِلَى بلد آخر</span>

١٢٣٠ - قَالَ أبي سَمِعت سُفْيَان يَقُول سَمِعت الْعلمَاء يَقُولُونَ فِي الزَّكَاة لَا يحابى بهَا وَلَا يدْفع بهَا مذمة','3','16',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (853,'١٢٣١ - وَقَالَ لَا تخرج الزَّكَاة من بلد إِلَى غَيره وَلَا يعْطى أَكثر من خمسين درهما
١٢٣٢ - وَقَالَ يقْضى الدّين من الزَّكَاة كل بَالغا مَا بلغ
١٢٣٣ - قلت الرجل لَهُ الْعِيَال
قَالَ يُعْطون من الزَّكَاة كل إِنْسَان خمسين درهما
<span data-type="title" id=toc-823>مسَائِل فِي زَكَاة الْفطر</span>

١٢٣٤ - حَدِيث أبي سعيد فِي زَكَاة الْفطر لَيْسَ هُوَ مثل حَدِيث ابْن عمر قَالَ أَبُو سعيد كُنَّا نخرج على عهد رَسُول الله صلى الله عَلَيْهِ وَسلم وَقَالَ ابْن عمر فرض رَسُول الله صلى الله عَلَيْهِ وَسلم من كل شَيْء صَاعا صَاعا
١٢٣٥ - وَالتَّمْر أحب إِلَيّ أَن يعْطى
كَانَ ابْن سِيرِين يحب أَن ينقى الطَّعَام وَهُوَ أحب إِلَيّ','3','17',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (854,'١٢٣٦ - قلت قوم يَقُولُونَ الطَّعَام أَنْفَع للْمَسَاكِين وَقوم يَقُولُونَ الْخبز خير فكرهه أبي وَقَالَ تُوضَع السّنَن على موَاضعهَا قَالَ الله {فإطعام سِتِّينَ مِسْكينا} وَلم يَأْمُرنَا بِالْقيمَةِ وَلَا الشَّيْء نعطي مَا أمرنَا بِهِ
وَحَدِيث ابْن عمر فرض رَسُول الله صلى الله عَلَيْهِ وَسلم صَدَقَة الْفطر صَاعا من تمر أَو صَاعا من شعير فَيعْطى مَا فرض رَسُول الله صلى الله عَلَيْهِ وَسلم
وَقَالَ لم يلْتَفت أَبُو سعيد وَلَا ابْن عمر إِلَى قيمَة مقومة','3','18',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (855,'١٢٣٧ - الصَّاع خَمْسَة أَرْطَال وَثلث من التَّمْر تمرنا مَا يتجافى لَا يَجِيء الصَّاع خَمْسَة أَرْطَال وَثلثا وتمر الْمَدِينَة ثقيل يدْخل فِي الصَّاع أَكثر من تمرنا
<span data-type="title" id=toc-824>مُدَّة تَعْرِيف اللّقطَة</span>

١٢٣٨ - وَقَالَ اللّقطَة تعرف سنة
١٢٣٩ - قلت حَدِيث أبي ثَلَاث سِنِين
قَالَ هَذَا يخْتَلف فِيهِ عَن سَلمَة بن كهيل','3','19',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (856,'<span data-type="title" id=toc-825>هَل يبين فِي الْإِشْهَاد على اللّقطَة مقدارها</span>

١٢٤٠ - قلت الْإِشْهَاد على اللّقطَة يبين كم هِيَ
قَالَ لَا يبين كم هِيَ وَلَكِن يشْهد أَنِّي قد أصبت لقطَة دَنَانِير أَو دَرَاهِم أَو كَذَا أَو كَذَا ويعرفها سنة ثمَّ هِيَ كَمَاله فَإِن جَاءَ صَاحبهَا أَدَّاهَا إِلَيْهِ وَاحْتج بِحَدِيث زيد بن خَالِد الْجُهَنِيّ
١٢٤١ - قلت هَؤُلَاءِ يَقُولُونَ يتَصَدَّق بهَا
قَالَ أبي شَيْء أَهْون من أَن نرد الْأَحَادِيث وَكَيف يجوز لَهُ أَن يرد الْأَحَادِيث وَقد رَوَاهَا الثِّقَات وَيَنْبَغِي للْإنْسَان إِذا لم يعرف الشَّيْء أَن لَا يرد الْأَحَادِيث وَهُوَ لَا يحسن يَقُول لَا أحسن','3','20',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (857,'<span data-type="title" id=toc-826>كم يملك من الطَّلَاق من تزوج زَوجته الْمُطلقَة بعد زوج آخر</span>

١٢٤٢ - قَالَ أبي إِذا طَلقهَا تَطْلِيقَة أَو تَطْلِيقَتَيْنِ فَتزوّجت زوجا فَدخل بهَا ثمَّ طَلقهَا فَتزَوج بهَا الأول فَهِيَ عِنْده على مَا بَقِي لِأَن الزَّوْج الثَّانِي لم يبح مِنْهَا شَيْئا قَالَ الله {فَإِن طَلقهَا فَلَا تحل لَهُ من بعد حَتَّى تنْكح زوجا غَيره} وَتلك تحل لَهُ بالواحدة والثنتين أَن يتراجعا وَإِنَّمَا لَا تحل لَهُ حَتَّى تنْكح زوجا غَيره فِي الْمُطلقَة ثَلَاثًا
<span data-type="title" id=toc-827>كل فرض على الْإِنْسَان إِذا مَاتَ فَهُوَ من جَمِيع المَال</span>

١٢٤٣ - وَقَالَ الْحَج وَالْكَفَّارَات وكل فرض على الرجل إِذا مَاتَ فَهُوَ من جَمِيع المَال
<span data-type="title" id=toc-828>كَيفَ تستبرأ الْأمة الَّتِي لَا تحيض</span>

١٢٤٤ - وَقَالَ الْأمة الَّتِي لَا تحيض تستبرأ بِثَلَاثَة أشهر
<span data-type="title" id=toc-829>المرخصون فِي اقْتِضَاء الذَّهَب من الْوَرق من الصَّحَابَة</span>

١٢٤٥ - وَقَالَ من رخص فِي اقْتِضَاء الذَّهَب من الْوَرق من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم عمر بن الْخطاب وَابْن مَسْعُود وَابْن عمر ويروى','3','21',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (858,'عَن ابْن مَسْعُود اخْتِلَاف قَالُوا خُذ بالسعر
<span data-type="title" id=toc-830>حَدِيث عَليّ أَمرك بِيَدِك</span>

١٢٤٦ - حَدِيث عَليّ أَمرك بِيَدِك يَقُول الْقَضَاء مَا قَضَت يرويهِ عَطاء بن السَّائِب يسْندهُ وَالْحكم يُرْسِلهُ','3','22',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (859,'<span data-type="title" id=toc-831>من صلى بِالنَّاسِ وَهُوَ جنب أَو غير متوضئ</span>

١٢٤٧ - قلت فِي الَّذِي يُصَلِّي بِالنَّاسِ وَهُوَ جنب
قَالَ يُعِيد وَلَا يعيدون
١٢٤٨ - قلت فَغير متوضئ
قَالَ الْجنب أَكثر يرْوى عَن عمر وَيَرْوِيه عَن ابْن عمر سَالم وَنَافِع','3','23',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (860,'١٢٤٩ - حَدثنِي أبي قَالَ حَدثنَا حَمَّاد الْخياط عَن عبد الله الْعمريّ عَن نَافِع عَن ابْن عمر ويروي الْحجَّاج عَن أبي إِسْحَاق عَن الْحَارِث عَن عَليّ يُعِيد وَلَا يعيدون
<span data-type="title" id=toc-832>كم ضَرْبَة فِي التَّيَمُّم</span>

١٢٥٠ - قَالَ أبي التَّيَمُّم ضَرْبَة للْوَجْه وَالْكَفَّيْنِ
قَالَ الله {وَالسَّارِق والسارقة فَاقْطَعُوا أَيْدِيهِمَا}
<span data-type="title" id=toc-833>مَتى يسْجد للسَّهْو</span>

١٢٥١ - وَسَأَلته عَن السُّجُود فِي السَّهْو قبل أَو بعد','3','24',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (861,'فَقَالَ حَدِيث ابْن بُحَيْنَة السُّجُود قبل التَّسْلِيم وَحَدِيث أبي سعيد قبل التَّسْلِيم
١٢٥٢ - فَقلت لَهُ إِن مَالك بن أنس يَقُول مَا كَانَ من نُقْصَان فَهُوَ قبل وَمَا كَانَ من زِيَادَة فَهُوَ بعد
فَقَالَ مَا أَدْرِي مَا هَذَا حَدِيث أبي سعيد مُخَالف لقَوْل مَالك','3','25',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (862,'١٢٥٣ - قَالَ أبي إِن كَانَت خَامِسَة شفعتا صلَاته وَإِن كَانَت رَابِعَة كَانَتَا ترغيما للشَّيْطَان وَقد أمرنَا بِالسُّجُود قبل التَّسْلِيم يسْندهُ مُحَمَّد بن عجلَان والماجشون وَسليمَان بن بِلَال وَكَانَ فِي حلق زيد بن أسلم شَيْء فَكَانَ مرّة يسْندهُ لَهُم وَمرَّة يقصر','3','26',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (863,'<span data-type="title" id=toc-834>حكم قَضَاء الصَّلَاة للمجنون وَالطَّلَاق عَنهُ وَعَن الْمَفْقُود</span>

١٢٥٤ - قَالَ أبي الْمَجْنُون لَا يقْضِي صلَاته قد رفع عَنهُ الْقَلَم وَيُطلق عَنهُ وليه إِذا خَافُوا على امْرَأَته أَن يَقْتُلهَا أَو يعقرها يُطلق عَنهُ
١٢٥٥ - قلت الْمَفْقُود
قَالَ الْمَفْقُود أبعد لِأَنَّهُ غَائِب وَهَذَا حَاضر','3','27',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (864,'<span data-type="title" id=toc-835>الْمغمى عَلَيْهِ يقْضِي الصَّلَوَات</span>

١٢٥٦ - قَالَ الْمغمى عَلَيْهِ يقْضِي الصَّلَوَات
١٢٥٧ - قلت لَهُ فَإِن قوما يَقُولُونَ إِن ابْن عمر لم يقْض وَمَا كَانَ أَكثر من يَوْم وَلَيْلَة لم يقْض
قَالَ هَؤُلَاءِ يَقُولُونَ لَا يقْضِي أَكثر من خمس صلوَات وَكَانَ ابْن عمر لَا يرى قَضَاء
<span data-type="title" id=toc-836>من دخل الْمَسْجِد بعد مَا صلى ثمَّ أُقِيمَت الصَّلَاة</span>

١٢٥٨ - قَالَ أبي إِذا كَانَ الرجل فِي الْمَسْجِد وَقد صلى قبل أَن يدْخل وأقيمت الصَّلَاة وَهُوَ فِي الْمَسْجِد فَلَا يخرج حَتَّى يُصَلِّي أَي صَلَاة كَانَت
<span data-type="title" id=toc-837>إِنْكَار أَحْمد قَول الزُّهْرِيّ بيع الثَّمر بِالتَّمْرِ</span>

١٢٥٩ - سَأَلته عَن حَدِيث الزُّهْرِيّ الَّذِي يروي عَن بيع الثَّمر بِالتَّمْرِ
فَقَالَ هَكَذَا يَقُول الزُّهْرِيّ وَلَيْسَ لهَذَا وَجه إِنَّمَا يَقُول النَّاس التَّمْر بِالتَّمْرِ','3','28',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (865,'<span data-type="title" id=toc-838>مِيرَاث من أسلم على يَدي رجل</span>

١٢٦٠ - حَدِيث تَمِيم الدارى من أسلم على يَدي رجل فَهُوَ أولى النَّاس بمحياه ومماته أَبُو نعيم يرويهِ يَقُول سَمِعت تميما الدارى وَيحيى بن حَمْزَة يدْخل بَينهمَا رجلا','3','29',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (866,'١٢٦١ - قلت لَهُ أَلَيْسَ قَالَ النَّبِي صلى الله عَلَيْهِ وَسلم الْوَلَاء لمن أعتق
قَالَ بلَى وَحَدِيث تَمِيم إِذا أسلم على يَدَيْهِ فَلهَذَا وَجه وَلِهَذَا وَجه لَيْسَ كَمَا يَقُول هَؤُلَاءِ يعْنى أَصْحَاب أبي حنيفَة لَهُ أَن ينْتَقل مالم يعقل عَنهُ فَهُوَ مرّة مَوْلَاهُ وَمرَّة لَيْسَ هُوَ مَوْلَاهُ
<span data-type="title" id=toc-839>هَل يَرث من أسلم على مِيرَاث</span>

١٢٦٢ - قلت الرجل يسلم على مِيرَاث هَل يَرث
قَالَ يرْوى عَن عمر وَعُثْمَان أَنَّهُمَا كَانَا يورثانه وَقَالَ سعيد بن الْمسيب قد بددت الْمَوَارِيث','3','30',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (867,'<span data-type="title" id=toc-840>نَفَقَة الْحَامِل الْمُتَوفَّى عَنْهَا زَوجهَا</span>

١٢٦٣ - قلت الْحَامِل الْمُتَوفَّى عَنْهَا زَوجهَا
فَقَالَ هِيَ مثل تِلْكَ تنْفق من نصِيبهَا قد بددت الْمَوَارِيث
١٢٦٤ - قلت الوليدة الْمُتَوفَّى عَنْهَا زَوجهَا وَهِي حَامِل من أَيْن تنْفق
قَالَ هَذِه غير تِلْكَ ينْفق عَلَيْهَا من الْجَمِيع','3','31',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (868,'<span data-type="title" id=toc-841>إِذا أوصِي بِحَجّ لمن يكون مَا فضل بعده</span>

١٢٦٥ - قلت الرجل يُوصي بِالْحَجِّ
قَالَ الْحَج على ثَلَاثَة أوجه إِذا قَالَ حجُّوا عني بِأَلف فَمَا فضل رد فِي الْحَج حَتَّى يسْتَغْرق الْألف وَإِذا قَالَ حجُّوا عني حجَّة بِأَلف مَا بَقِي فللحاج وَإِذا أوصى أَن حجُّوا عني حجَّة فَمَا فضل رد على الْوَرَثَة
<span data-type="title" id=toc-842>من ضَاعَت عَنهُ الْأُضْحِية أَو الْبَدنَة فَاشْترى غَيرهَا ثمَّ وجدهَا</span>

١٢٦٦ - قلت الْأُضْحِية أَو الْبَدنَة تضيع فيشتري غَيرهَا ثمَّ يجدهَا
قَالَ ينحرهما جَمِيعًا يرْوى ذَلِك عَن ابْن عمر وَعَائِشَة وَابْن عَبَّاس وَابْن الزبير','3','32',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (869,'<span data-type="title" id=toc-843>إِذا أعورت الْأُضْحِية أَو عجفت</span>
١٢٦٧ - قلت الْأُضْحِية إِذا اشْتَرَاهَا فأعورت أَو عجفت
قَالَ يذبحها تجزيه فَإِن أَرَادَ أَن يَبِيع الْأُضْحِية فَلَا بَأْس أَن يَبِيعهَا وَيَشْتَرِي مَا هُوَ خير مِنْهَا فَأَما أَن يَشْتَرِي مَا هُوَ دونهَا فَلَا
<span data-type="title" id=toc-844>الرجل يعْطى فرسا فِي سَبِيل الله</span>

١٢٦٨ - قلت الرجل يعْطى فرسا فِي سَبِيل الله','3','33',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (870,'قَالَ إِذا لم يقل حبيس فَهُوَ لَهُ إِذا غزا عَلَيْهِ
١٢٦٩ - قلت يَبِيعهُ
قَالَ هُوَ لَهُ
١٢٧٠ - قلت فَإِنَّهُ يَبِيعهُ
قَالَ إِذا كَانَ هَذَا عَادَته فَهَذِهِ طعمة سوء
١٢٧١ - قلت لَهُ الْفرس الحبيس إِذا قَامَ أَو عطب يُبَاع
قَالَ نعم وَيجْعَل فِي آخر مثله
<span data-type="title" id=toc-845>حكم نقل الْمَسْجِد إِلَى مَكَان آخر</span>

١٢٧٢ - قلت الْمَسْجِد يخرب أَو يذهب أَهله ترى أَن يحول مَكَانا آخر
قَالَ نعم
١٢٧٣ - قلت لَهُ مَسْجِد يحول من مَكَان إِلَى مَكَان
قَالَ إِذا كَانَ إِنَّمَا يُرِيد مَنْفَعَة النَّاس فَنعم وَإِلَّا فَلَا وَابْن مَسْعُود قد حول مَسْجِد الْجَامِع من التمارين فَإِذا كَانَ على الْمَنْفَعَة فَنعم وَإِلَّا فَلَا','3','34',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (871,'<span data-type="title" id=toc-846>مسَائِل فِي المبتدأة بِالدَّمِ</span>

١٢٧٤ - وَقَالَ الْمَرْأَة إِذا بدئت بِالدَّمِ تجْلِس سِتَّة أَو سَبْعَة
١٢٧٥ - قلت على حَدِيث حمْنَة
قَالَ لَا وَلَكِن النِّسَاء أَكثر حيضهن على هَذَا أَو تجْلِس يَوْمًا
١٢٧٦ - قلت فَإِن اسْتمرّ بهَا الدَّم شَهْرَيْن أَو ثَلَاثَة
قَالَ على إقبال الدَّم وإدباره
<span data-type="title" id=toc-847>مسَائِل فِي الْوضُوء من الدَّم والرعاف والحجامة وَغَيرهَا</span>

١٢٧٧ - قلت الْوضُوء من الدَّم
قَالَ على قدر كثير الدَّم
١٢٧٨ - قلت الرعاف والحجامة
قَالَ فيهمَا الْوضُوء','3','35',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (872,'قلت والبثرة
قَالَ لَيْسَ فِيهِ وضوء ابْن عمر ينْصَرف من قَلِيل الدَّم وَكَثِيره
وَابْن عَبَّاس إِذا كَانَ فَاحِشا وَأَبُو هُرَيْرَة أَدخل أَصَابِعه أَنفه وَابْن أبي أوفى تنخع دَمًا وَجَابِر يرويهِ أَبُو الزبير عَن جَابر','3','1279',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (873,'١٢٨٠ - قلت فِي الْجَسَد وَالثَّوْب سَوَاء إِذا كَانَ فَاحِشا
قَالَ نعم وَقَالَ الْجَنَابَة مثله أَيْضا
١٢٨١ - قلت دم الْحيض يُصِيب الثَّوْب القطرة أَو الشَّيْء
قَالَ إِذا كَانَ فَاحِشا وكل شَيْء يخرج من السَّبِيلَيْنِ فَفِيهِ الْوضُوء
<span data-type="title" id=toc-848>من نسي مسح رَأسه</span>

١٢٨٢ - قلت وَمن نسي مسح رَأسه أعَاد الْوضُوء إِذا جف وضوءه وَسَائِر أَعْضَائِهِ
قَالَ نعم','3','37',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (874,'<span data-type="title" id=toc-849>حكم الْوضُوء من النّوم جَالِسا</span>

١٢٨٣ - قلت إِذا نَام الرجل جَالِسا عَلَيْهِ الْوضُوء
قَالَ إِذا أَطَالَ ذَاك
<span data-type="title" id=toc-850>حكم تَزْوِيج الإبن إِذا عضل الْأَب وتزويج الصغار</span>

١٢٨٤ - قلت الْأَب إِذا عضل وَلم يُزَوّج يُزَوّج الإبن
قَالَ نعم يرْوى عَن عُثْمَان إِذا وَضعهَا فِي الكفوء وَإِذا لم يُزَوّج الْوَلِيّ يُزَوّج الْحَاكِم عَلَيْهِ وَلَا يُزَوّج الصَّغِيرَة إِلَّا أَبوهَا وَلَا يُزَوّج الْجد وَلَا يُزَوّج الصَّغِيرَة الْأَخ وَلَا الْوَلِيّ إِلَّا أَن تكون بنت تسع سِنِين فتستأمر فَإِن أَذِنت لم يكن لَهَا خِيَار إِذا كَانَ مثلهَا يُوطأ
قَالَ الْغُلَام لَا يُزَوجهُ الْجد إِذا كَانَ صَغِيرا لَا يُزَوجهُ إِلَّا الْأَب','3','38',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (875,'<span data-type="title" id=toc-851>دفع الأَرْض بِالثُّلثِ أَو الرّبع أَو الدَّرَاهِم</span>

١٢٨٥ - قلت الرجل يدْفع أرضه بِالثُّلثِ أَو الرّبع أَو الدَّرَاهِم
قَالَ كُله سَوَاء لَيْسَ بِهِ بَأْس
<span data-type="title" id=toc-852>كَيْفيَّة الْمُزَارعَة</span>

١٢٨٦ - وَقَالَ الشّركَة فِي الزَّرْع أحب إِلَيّ ان يكون الْبذر على رب الأَرْض وَالْحَدِيد وَالْبَقر على الدَّاخِل مثل الْمضَارب
<span data-type="title" id=toc-853>حكم إقراض الأكار ومستأجر الدّكان</span>

١٢٨٧ - قلت الرجل يُعْطي الأكار وَالْبذْر وَالْبَقر يقْرضهُ
قَالَ أكره من أجل أَنه قرض جر مَنْفَعَة
١٢٨٨ - وَقَالَ هَا هُنَا قوم يكرون دكاكينهم ويقرضونهم فَهَذَا لَا يصلح قرض جر مَنْفَعَة','3','39',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (876,'<span data-type="title" id=toc-854>حكم بيع الشَّيْء بِنَقْد بِكَذَا وبنسيئة بِكَذَا</span>

١٢٨٩ - قلت الرجل يَبِيع الشَّيْء فَيَقُول بِنَقْد بِكَذَا وبنسيئة بِكَذَا
قَالَ إِذا افترق على وَاحِد فَلَا بَأْس
<span data-type="title" id=toc-855>تَفْسِير البيع وَالسَّلَف</span>

١٢٩٠ - قلت السّلف وَالْبيع مَا هُوَ
فَقَالَ يسلف فَيَقُول إِن لم يكن عنْدك بعتكه فَلَا يجوز سلف وَبيع فَيكون يزْدَاد عَلَيْهِ فِي البيع بِمَا أقْرضهُ أَو يكون يقْرضهُ ويبايعه
<span data-type="title" id=toc-856>الْمغمى عَلَيْهِ يقْضِي الصَّلَوَات كلهَا</span>

١٢٩١ - قَالَ الْمغمى عَلَيْهِ يقْضِي الصَّلَوَات كلهَا النَّبِي صلى الله عَلَيْهِ وَسلم نَام عَن الصَّلَاة فَقضى','3','40',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (877,'<span data-type="title" id=toc-857>أَقْوَال أَحْمد فِي رجال</span>

١٢٩٢ - عَليّ بن زيد بن جدعَان لَيْسَ هُوَ بِالْقَوِيّ قد روى النَّاس عَنهُ
١٢٩٣ - وَإِبْرَاهِيم الخوزي مَتْرُوك الحَدِيث
١٢٩٤ - أُسَامَة بن زيد بن أسلم مُنكر الحَدِيث ضَعِيف
١٢٩٥ - إِسْحَاق بن يحيى بن طَلْحَة أَخُو طَلْحَة بن يحيى مُنكر الحَدِيث لَيْسَ بِشَيْء','3','41',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (878,'١٢٩٦ - ملازم بن عَمْرو حَاله مقارب
١٢٩٧ - أَكثر من فِي يحيى بن أبي كثير من أهل الْبَصْرَة هِشَام الدستوَائي
١٢٩٨ - وَحرب بن شَدَّاد وَأَبَان وشيبان ثَبت فِي كل الْمَشَايِخ وَهَمَّام','3','42',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (879,'١٢٩٩ - قلت الْأَوْزَاعِيّ
قَالَ هَؤُلَاءِ أثبت من الْأَوْزَاعِيّ
١٣٠٠ - مقَاتل بن سُلَيْمَان صَاحب التَّفْسِير مَا يُعجبنِي أَن أروي عَنهُ شَيْئا
١٣٠١ - ضَمْضَم بن جوش لَيْسَ بِهِ بَأْس روى عَنهُ يحيى بن أبي كثير وَعِكْرِمَة بن عمار
١٣٠٢ - وَقَالَ كَانَ هشيم كثير السُّكُوت قل مَا يتَكَلَّم إِلَّا يَقُول لَا إِلَه إِلَّا الله كَانَ رُبمَا سبق بالتكبيرة الأولى فيمضي حَتَّى يُصَلِّي فِي مَسْجِد آخر وَقَالَ لَهُ إِنْسَان يَا أَبَا مُعَاوِيَة إِن إِسْمَاعِيل بن علية يحدث فَقَالَ إِلَى مثل إِسْمَاعِيل فاذهبوا
١٣٠٣ - وَكَانَ الْوَلِيد بن مُسلم كثير السُّكُوت','3','43',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (880,'<span data-type="title" id=toc-858>حكم التَّطَوُّع بَين التَّرَاوِيح</span>

١٣٠٤ - قَالَ أبي لَا يتَطَوَّع بَين التَّرَاوِيح يرْوى عَن عقبَة بن عَامر وَعبادَة بن الصَّامِت وَأبي الدَّرْدَاء يرويهِ عِيسَى بن يُونُس عَن ثَوْر عَن رَاشد بن سعد أَن أَبَا الدَّرْدَاء كَانَ يكره الصَّلَاة بَين التَّرَاوِيح وَسَعِيد بن الْمسيب وَزيد بن أسلم كَانَا يكرهان الصَّلَاة بَين كل شفع','3','44',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (881,'<span data-type="title" id=toc-859>كَيْفيَّة الصَّلَاة فِي السَّفِينَة</span>

١٣٠٥ - وَقَالَ الصَّلَاة فِي السَّفِينَة إِذا أمكنه صلى قَائِما وَإِذا لم يُمكنهُ قَائِما صلى جَالِسا
<span data-type="title" id=toc-860>حكم الْقصر للملاح</span>

١٣٠٦ - والملاح إِذا كَانَ مَعَه أَهله وَبَنوهُ أتم الصَّلَاة وَإِن لم يكن أَهله مَعَه قصر الصَّلَاة مثل الرَّاعِي يرْوى عَن الْحسن وَعَطَاء قَالَا فِي الملاح إِذا كَانَ مَعَه أَهله أتم الصَّلَاة','3','45',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (882,'<span data-type="title" id=toc-861>يُقَاد الرجل من الْمَرْأَة فِيمَا دون النَّفس</span>

١٣٠٧ - قَالَ سُفْيَان الثَّوْريّ كَانَ الْعلمَاء الشّعبِيّ وَعمر بن عبد العزيز وَإِبْرَاهِيم يرَوْنَ يُقَاد الرجل من الْمَرْأَة فِيمَا دون النَّفس يُرِيدُونَ جراحه الْعمد
<span data-type="title" id=toc-862>الْمُرْتَدَّة تقتل</span>

١٣٠٨ - قَالَ أبي إِذا ارْتَدَّت الْمَرْأَة عَن الْإِسْلَام تقتل
<span data-type="title" id=toc-863>إِذا أصَاب الْمَنِيّ الْفراش وَنَحْوه</span>

١٣٠٩ - قلت لأبي الْفراش يُصِيبهُ الْمَنِيّ يبسط عَلَيْهِ','3','46',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (883,'فَقَالَ الْمَنِيّ شَيْء آخر وَسَهل فِي الْمَنِيّ جدا وَقَالَ أَيْن الْمَنِيّ من الْبَوْل الْبَوْل شَدِيد والمني يفرك وَقد جَاءَ أَنه بمنزله المخاط يَقُوله ابْن عَبَّاس
<span data-type="title" id=toc-864>حكم الصَّلَاة على ثوب قد أصَاب نَاحيَة مِنْهُ قذر</span>

١٣١٠ - قلت الرجل يبسط الثَّوْب فَيصَلي عَلَيْهِ وناحية مِنْهُ قد أَصَابَهَا الْمَنِيّ
قَالَ إِذا صلى على النَّاحِيَة الْأُخْرَى الَّتِي لم يصبهَا قذر فَلَا بَأْس
١٣١١ - قلت الرجل يكون مَوضِع سُجُوده قذرا وَمَوْضِع قَدَمَيْهِ فَأنْكر قَول من يَقُول لَا يضر إِلَّا أَن يكون مَوضِع سُجُوده قَالَ هَذَا كُله مَكْرُوه','3','47',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (884,'<span data-type="title" id=toc-865>حكم الصَّلَاة فِي ثِيَاب الْمَجُوس وَأهل الْكتاب</span>

١٣١٢ - قَالَ أبي وَلَا يصلى فِي ثِيَاب الْمَجُوس ثِيَاب الْيَهُود وَالنَّصَارَى عِنْدِي أسهل مَا لم يكن ثوب يَلِي سفلته فَإِنَّهُم لَا يتنزهون من الْبَوْل
<span data-type="title" id=toc-866>إِذا أصَاب الثَّوْب الْمَذْي أَو الْمَنِيّ</span>

١٣١٣ - قلت لأبي الْمَذْي يُصِيب الثَّوْب
قَالَ حَدِيث مُحَمَّد بن إِسْحَاق لَا أعرفهُ عَن غَيره وَلَا أحكم لمُحَمد بن إِسْحَاق يَعْنِي حَدِيث سهل بن حنيف وَغسل الْمَنِيّ من الثَّوْب أحوط وَأثبت فِي الرِّوَايَة وَقد جَاءَ الفرك أَيْضا','3','48',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (885,'<span data-type="title" id=toc-867>طَريقَة تَطْهِير الأَرْض من الْبَوْل</span>

١٣١٤ - بَوْل الْأَعرَابِي يجْزِيه أَن يصب عَلَيْهِ المَاء دلوا أَو دلوين','3','49',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (886,'<span data-type="title" id=toc-868>حكم مَاء المصانع الَّتِي فِي طَرِيق مَكَّة</span>

١٣١٥ - المصانع الَّتِي فِي طَرِيق مَكَّة لَيست بنجسة وَلَا ينجسها بَوْل وَلَا شَيْء والْحَدِيث الَّذِي جَاءَ وَالله أعلم لَا يبولن أحدكُم فِي المَاء الدَّائِم ثمَّ يتَوَضَّأ مِنْهُ إِنَّمَا هُوَ على آبار الْمَدِينَة وَمَا أشبههَا فَأَما المصانع لَا ينجسها شَيْء عِنْدِي لسعتها وَمَا فِيهَا من المَاء
<span data-type="title" id=toc-869>معنى حَدِيث أم سَلمَة يطهره مَا بعده</span>

١٣١٦ - حَدِيث أم سَلمَة يطهره مَا بعده لَيْسَ هَذَا عِنْدِي على أَنه إِذا','3','50',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (887,'أَصَابَهُ بَوْل ثمَّ مر بعده على الأَرْض أَنَّهَا تطهره وَلَكِن يمر بِالْمَكَانِ يتقذره فيمر بعده بمَكَان هُوَ أطيب مِنْهُ فيطهره الطّيب
<span data-type="title" id=toc-870>أَذَان الْجنب</span>

١٣١٧ - قلت الْجنب يُؤذن
قَالَ يُعجبنِي أَن يتوقى
<span data-type="title" id=toc-871>مسَائِل فِي أَوْقَات الصَّلَاة واستحباب تَعْجِيلهَا وَمَعْرِفَة الزَّوَال</span>

١٣١٨ - التَّعْجِيل فِي الصَّلَوَات إِلَّا فِي الصَّلَاتَيْنِ صَلَاة الظّهْر يبرد بهَا من شدَّة الْحر وَصَلَاة الْعشَاء الْآخِرَة تُؤخر
١٣١٩ - إسفار الْفجْر عِنْدِي طلوعه','3','51',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (888,'١٣٢٠ - ظلّ كل شَيْء مثله وظل كل شَيْء مثلَيْهِ هَذَا بعد الزَّوَال وَهُوَ أَن يقدر الشَّمْس فَإِذا زَالَت فَينْظر كم زَالَت فَإِذا عرف ذَلِك ثمَّ صَار الظل بعد ذَلِك مثله فَهُوَ آخر وَقت الظّهْر وَأول وَقت الْعَصْر
١٣٢١ - تَعْجِيل الْعَصْر أحب إِلَيّ آخر وَقت الْعَصْر عِنْدِي مالم تصفر الشَّمْس وَلَا أَقُول إِن آخر وَقتهَا أَن يكون ظلّ كل شَيْء مثلَيْهِ هَذَا أَكثر','3','52',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (889,'قَالَ النَّبِي صلى الله عَلَيْهِ وَسلم من أدْركهَا من الْعَصْر رَكْعَة قبل أَن تغرب الشَّمْس فقد أركها قَالَ هَذَا على الْفَوْت لَيْسَ على أَن يتْرك الْعَصْر إِلَى هَذَا الْوَقْت وَأول وَقت الْعَصْر هُوَ آخر وَقت الظّهْر وَآخر وَقت الظّهْر أَن يكون ظلّ كل شَيْء مثله إِذا زَالَت الشَّمْس فَكَانَ الظل بعد ذَلِك مثله فَهُوَ ذَلِك الْبلدَانِ تخْتَلف وَالزَّمَان يخْتَلف فَرُبمَا زَالَت على قدم وَرُبمَا زَالَت على أَكثر يكون الْفَيْء سَاعَة تَزُول قدما إِنَّمَا يحْسب الْمثل بعد الزَّوَال الشَّمْس فِي أول النَّهَار يكون لَهَا طول ثمَّ ينقص ذَلِك وَلَا يزَال ينقص حَتَّى يقف فَإِذا وقف ثمَّ زَاد فقد زَالَت
<span data-type="title" id=toc-872>لانفقة وَلَا سُكْنى للمطلقة ثَلَاثًا</span>

١٣٢٢ - قلت لأبي تذْهب إِلَى حَدِيث فَاطِمَة بنت قيس أَنَّهَا طلقت ثَلَاثًا لم تجْعَل لَهَا سُكْنى وَلَا نَفَقَة
قَالَ نعم','3','53',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (890,'<span data-type="title" id=toc-873>قَول أَحْمد فِي إِسْمَاعِيل السّديّ</span>

١٣٢٣ - إِسْمَاعِيل السّديّ مقارب الحَدِيث صَالح
<span data-type="title" id=toc-874>وَقت كَفَّارَة الْيَمين</span>

١٣٢٤ - كَفَّارَة الْيَمين قبل وَبعد ابْن عمر كفر بعد وَقبل وسلمان قبل','3','54',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (891,'<span data-type="title" id=toc-875>التَّكْبِير أَيَّام التَّشْرِيق</span>

١٣٢٥ - التَّكْبِير أَيَّام التَّشْرِيق إِذا صلى جمَاعَة كبر وَإِذا لم يصل جمَاعَة لم يكبر كَانَ ابْن عمر إِذا صلى جمَاعَة كبر وَإِذا لم يصل جمَاعَة لم يكبر
<span data-type="title" id=toc-876>مسَائِل فِي الْأُضْحِية</span>

١٣٢٦ - إِذا ذبح قبل الصَّلَاة أعَاد الذّبْح
١٣٢٧ - النَّحْر مَكْرُوه بِاللَّيْلِ
١٣٢٨ - الْبَقَرَة عَن سَبْعَة مثل الْبَدنَة يرْوى عَن عَليّ يضحى بهَا عَن سَبْعَة','3','55',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (892,'<span data-type="title" id=toc-877>مَتى يَصُوم الْمُتَمَتّع</span>

١٣٢٩ - كَانَ ابْن عمر وَعَائِشَة يَقُولَانِ يَصُوم الْمُتَمَتّع حِين يهل فَإِن فَاتَهُ صَامَ أَيَّام التَّشْرِيق','3','56',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (893,'<span data-type="title" id=toc-878>الْمسْح على الْعِمَامَة</span>

١٣٣٠ - قَالَ أبي أرى الْمسْح على الْعِمَامَة
<span data-type="title" id=toc-879>إِذا قَالَ الْحل عَليّ حرَام</span>

١٣٣١ - وَقَالَ فِي الْحل حرَام لَا أُجِيب
<span data-type="title" id=toc-880>غسل عَلْقَمَة امْرَأَته</span>

١٣٣٢ - حَدثنَا صَالح قَالَ حَدثنَا هشيم قَالَ حَدثنِي أبي قَالَ أَنا أَيُّوب أَبُو الْعَلَاء عَن أبي هَاشم أَن عَلْقَمَة غسل امْرَأَته','3','57',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (894,'<span data-type="title" id=toc-881>هَل تحصن الْكِتَابِيَّة وَالْأمة</span>

١٣٣٣ - تحصن الْيَهُودِيَّة والنصرانية وَلَا تحصن الْأمة
<span data-type="title" id=toc-882>حكم اللّعان بَين كل زَوْجَيْنِ وَنفي الْوَلَد بِهِ</span>

١٣٣٤ - بَين كل زَوْجَيْنِ لعان لِأَنَّهُ يَنْفِي عَنهُ الْوَلَد إِذا لاعنها نفى عَنهُ وَلَدهَا وَإِذا لم يلتعنا فَالْوَلَد قَائِم
<span data-type="title" id=toc-883>حكم صَلَاة الْجِنَازَة على الْقَبْر ومدتها</span>

١٣٣٥ - الصَّلَاة على قبر يصلى عَلَيْهِ صلى النَّبِي صلى الله عَلَيْهِ وَسلم على قبر وَأكْثر مَا بلغنَا شهر','3','58',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (895,'<span data-type="title" id=toc-884>كَيفَ يصنع إِذا سبق بِالتَّكْبِيرِ على الْجِنَازَة</span>

١٣٣٦ - إِذا سبق بِالتَّكْبِيرِ على الْجِنَازَة يُبَادر يكبر قبل أَن ترْتَفع
<span data-type="title" id=toc-885>قَول ابْن عمر فِي زواج الْكِتَابِيَّة</span>

١٣٣٧ - كَانَ ابْن عمر يَقُول لَا يتَزَوَّج الرجل من أهل الْكتاب
<span data-type="title" id=toc-886>دِيَة الْكِتَابِيّ والمجوسي</span>

١٣٣٨ - أذهب إِلَى ان دِيَة أهل الْكِتَابَيْنِ على نصف دِيَة الْمُسلم حَدِيث عَمْرو بن شُعَيْب عَن أَبِيه عَن جده أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ دِيَة أهل الْكِتَابَيْنِ على نصف دِيَة الْمُسلم الْمَجُوسِيّ ثَمَانمِائَة','3','59',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (896,'<span data-type="title" id=toc-887>لَا يقتل مُؤمن بِكَافِر وَلَا حر بِعَبْد</span>

١٣٣٩ - وَقَالَ لَا تقتل مُسلما بِكَافِر وَلَا حرا بِعَبْد
<span data-type="title" id=toc-888>مسَائِل فِي الزِّيَارَة</span>

١٣٤٠ - وَقَالَ فِي الَّذِي يحجّ الْفَرِيضَة يبْدَأ بِمَكَّة قبل الْمَدِينَة فَإِنِّي لَا أَدْرِي لَعَلَّه يحدث بِهِ شَيْء فِي الَّذِي يدْخل الْمَدِينَة وَلَا يمس الْحَائِط وَيَضَع يَده على الرمانة وَمَوْضِع الَّذِي جلس فِيهِ النَّبِي','3','60',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (897,'صلى الله عَلَيْهِ وَسلم وَلَا يقبل الْحَائِط وَكَانَ ابْن عمر يمسح النَّبِي صلى الله عَلَيْهِ وَسلم وَكَانَ يتبع آثَار النَّبِي صلى الله عَلَيْهِ وَسلم وَلَا يمر بِموضع صلى فِيهِ النَّبِي صلى الله عَلَيْهِ وَسلم إِلَّا صلى','3','61',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (898,'حَتَّى مر بشجرة صب النَّبِي صلى الله عَلَيْهِ وَسلم فِي أَصْلهَا مَاء فصب فِي أَصْلهَا المَاء
<span data-type="title" id=toc-889>من قَالَ الْحل عَليّ حرَام أَعنِي بِهِ الطَّلَاق</span>

١٣٤١ - وَإِذا قَالَ الْحل عَلَيْهِ حرَام أَعنِي بِهِ الطَّلَاق وَقَالَ أردْت وَاحِدَة قَالَ لَا ألتفت إِلَى قَوْله وأخشى أَن تكون ثَلَاثًا
<span data-type="title" id=toc-890>حكم غسل الشَّهِيد</span>

١٣٤٢ - الشَّهِيد لَا يغسل إِذا مَاتَ فِي المعركة قَالَ الشّعبِيّ لَا يغسل الَّذِي يقْتله اللُّصُوص','3','62',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (899,'<span data-type="title" id=toc-891>لَا تشد الرّحال إِلَّا إِلَى ثَلَاثَة مَسَاجِد</span>

١٣٤٣ - لَا تشد الرّحال إِلَّا إِلَى ثَلَاثَة مَسَاجِد الْمَسْجِد الْحَرَام وَالْمَسْجِد الْأَقْصَى وَمَسْجِد الْمَدِينَة
آخر ٠ الثَّامِن من أَجزَاء عَليّ

<span data-type="title" id=toc-893>حكم السّلم فِي اللَّحْم وَالثَّوْب والفاكهة</span>

١٣٤٤ - حَدثنَا صَالح قَالَ قَالَ أبي السّلم فِي اللَّحْم مائَة رَطْل بِكَذَا وَكَذَا على أَن يُوفيه كل يَوْم رطلا إِذا وَصفه السّمن وَالْحَد فَلَا بَأْس
١٣٤٥ - السّلم فِي الثَّوْب إِذا كَانَ مَوْصُوفا فَلَا بَأْس بِهِ','3','63',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (900,'٣ - ١٣٤٦ السّلم فِي الْفَاكِهَة مَا أَدْرِي أيش سلمه قد يَجِيء وَقت لَا يكون فِيهِ
<span data-type="title" id=toc-894>حكم وضع أَسْنَان الْغنم وَنَحْوهَا مَكَان الْأَسْنَان الساقطة</span>

١٣٤٧ - الْأَسْنَان تسْقط فَيَضَع فِيهَا من غير سنه سنّ الْغنم لَا بَأْس بِهِ فسنه يُعِيدهَا من الرَّأْس لَا بَأْس بِهِ يكره سنّ غَيره
<span data-type="title" id=toc-895>من خلع الْخُف بعد الْمسْح عَلَيْهِ</span>

١٣٤٨ - قلت الرجل يمسح الْخُف ثمَّ يخلعه
قَالَ يسْتَقْبل الْوضُوء','3','64',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (901,'<span data-type="title" id=toc-896>إِذا ترك الْجنب أَو المتوضيء الْمَضْمَضَة والإستنشاق</span>

١٣٤٩ - الْجنب يتْرك الْمَضْمَضَة والإستنشاق أعَاد الْوضُوء وَالصَّلَاة
١٣٥٠ - قَالَ أبي المتوضيء إِذا ترك الْمَضْمَضَة والإستنشاق يُعِيد الْوضُوء وَالصَّلَاة تَفْرِيق الْغسْل لَا بَأْس
<span data-type="title" id=toc-897>إِذا وَقع الْجنب فِي المَاء وَلم يتَوَضَّأ</span>

١٣٥١ - الْجنب يَقع فِي المَاء وَلم يتَوَضَّأ أحب إِلَيّ أَن يبْدَأ بِالْوضُوءِ وَلَكِن لَا بَأْس بِهِ
<span data-type="title" id=toc-898>حكم التَّفْضِيل فِي النَّحْل</span>

١٣٥٢ - النَّحْل أذهب إِلَى حَدِيث النُّعْمَان بن بشير قَالَ لَهُ النَّبِي صلى الله عَلَيْهِ وَسلم اردده
١٣٥٣ - قلت لَهُ قَالَ أشهد غَيْرِي وَقَالَ فِي الْمَدْيُون إِن النَّبِي','3','65',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (902,'صلى الله عَلَيْهِ وَسلم قَالَ صلوا على صَاحبكُم
فَقَالَ فِي ذَلِك لَا أشهد وَهَذَا لَا يشبه الْمَدْيُون وَقد صلى النَّبِي صلى الله عَلَيْهِ وَسلم على صَاحب الدّين بعد','3','66',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (903,'<span data-type="title" id=toc-899>من وَقع على جَارِيَة امْرَأَته</span>

١٣٥٤ - حَدِيث النُّعْمَان بن بشير عَن النَّبِي صلى الله عَلَيْهِ وَسلم فِي الَّذِي يَقع على جَارِيَة امْرَأَته أذهب إِلَيْهِ إِن كَانَت أحلتها لَهُ جلدته مائَة وَإِن كَانَت لم تحلها لَهُ رَجَمْته حَدِيث عمر أَيْضا قُوَّة لهَذَا
<span data-type="title" id=toc-900>إِذا مَضَت أَرْبَعَة أشهر فِي الْإِيلَاء</span>

١٣٥٥ - قلت قَول ابْن عَبَّاس فِي الْإِيلَاء إِذا مَضَت أَرْبَعَة أشهر إِن ابْن','3','67',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (904,'عَبَّاس قَالَ لَا تحبسوها قد انْقَضى أجلهَا تزوج من شَاءَت وَفِي قَول ابْن عَبَّاس قد انْقَضتْ عدتهَا فِي الْأَرْبَعَة أشهر فيوافقه أحد على هَذَا
قَالَ لَا إِلَّا أَن يكون جَابر بن زيد وَهُوَ كَانَ يرويهِ عَنهُ وَأَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم خالفوه مِنْهُم ابْن مَسْعُود وَالنَّاس خالفوه فَالَّذِينَ قَالُوا يوقفوه
قَالَ أُولَئِكَ الَّذين يعدون ذَا شَيْئا ويذهبون إِلَى الْوَقْف
١٣٥٦ - قلت لأبي أَلَيْسَ ترى أَنْت أَن توقفه
قَالَ بلَى هُوَ أصح فِي الْمَعْنى','3','68',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (905,'<span data-type="title" id=toc-901>كم عدَّة المختلعة</span>

١٣٥٧ - قلت المختلعة كم عدتهَا
قَالَ ثَلَاث حيض
١٣٥٨ - قلت فَمن قَالَ حَيْضَة
قَالَ عُثْمَان بن عَفَّان وَابْن عَبَّاس وَعِكْرِمَة يُرْسِلهُ عَن النَّبِي صلى الله عَلَيْهِ وَسلم','3','69',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (906,'<span data-type="title" id=toc-902>حكم من عطس فِي الصَّلَاة فَقَالَ الحمدلله</span>

١٣٥٩ - قلت الرجل يعطس فِي الصَّلَاة فَيَقُول الحمدلله
قَالَ يُعِيد الصَّلَاة إِذا رفع صَوته لِأَنَّهُ لَيْسَ من شَأْن الصَّلَاة أَن يجْهر بِهِ
١٣٦٠ - قلت فَإِن قَالَ فِي نَفسه
قَالَ فَلَا شَيْء عَلَيْهِ
<span data-type="title" id=toc-903>من علق الطَّلَاق أَو الْعتاق على مَجِيء وَقت</span>

١٣٦١ - قلت الرجل يَقُول لامْرَأَته أَنْت طَالِق رَأس السّنة ولغلامه أَنْت حر إِلَى سنة
قَالَ إِذا جَاءَ الْأَجَل طلقت وَعتق','3','70',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (907,'١٣٦٢ - قلت لَهُ الطَّلَاق وَالْعتاق سَوَاء
قَالَ نعم
<span data-type="title" id=toc-904>من حلف بِالطَّلَاق إِن لم يتَزَوَّج عَلَيْهَا</span>

١٣٦٣ - قلت الرجل يحلف على امْرَأَته بِالطَّلَاق إِن لم يتَزَوَّج عَلَيْهَا
قَالَ إِن كَانَ لَهُ نِيَّة سُئِلَ عَن نِيَّته وَإِن لم يكن لَهُ نِيَّة
١٣٦٤ - قلت لَهُ يَقع عَلَيْهَا الطَّلَاق
قَالَ إِذا صَار فِي حد أَو فِي حَال لَا يقدر على أَن يتَزَوَّج إِذا وَقع فِي النزع وَقع الطَّلَاق حِينَئِذٍ وَقَالَ تَرثه كَأَنَّهُ طلق وَهُوَ مَرِيض
<span data-type="title" id=toc-905>حكم الْوَصِيَّة لغير الْقَرَابَة</span>

١٣٦٥ - قلت الرجل يُوصي لغير قرَابَته هَل يردهُ
قَالَ لَا','3','71',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (908,'<span data-type="title" id=toc-906>من تذكر فِي الصَّلَاة أَنه ترك غسل ذراعه وَهُوَ إِمَام</span>

١٣٦٦ - سَأَلت أبي عَن رجل صلى بِقوم فَلَمَّا ركع رَكْعَة الثَّالِثَة تذكر أَنه قد ترك ذراعه لم يغسلهُ
قَالَ يَنْفَتِل من صلَاته وَيُعِيد ويعيدون
١٣٦٧ - قلت فَيتم صلَاته
قَالَ لَا ينْصَرف كَمَا هُوَ
<span data-type="title" id=toc-907>حكم رد الْوَصِيَّة على الْقَرَابَة بعد مَا أوصى لغَيرهم</span>

١٣٦٨ - قلت حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم الَّذِي يرويهِ عمرَان بن حُصَيْن أَن رجلا أعتق سِتَّة أعبد وَقد كَانَ لَهُ قرَابَة فَأجَاز النَّبِي صلى الله عَلَيْهِ وَسلم وَلم يردهُ
قَالَ رُبمَا استحسنت أَن يرد على الْقَرَابَة يؤاسيهم وَالْحسن يَقُول يرد على الْقَرَابَة ثلثا الثُّلُث','3','72',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (909,'١٣٦٩ - قلت فِي رجل يُوصي لغير قرَابَته يلقن إِذا حضر أَن يَجْعَل ذَلِك فِي قرَابَته فَإِذا أوصى
قَالَ يمْضِي كَمَا أوصى
<span data-type="title" id=toc-908>هَل الْأمة لَهَا فرَاش</span>

١٣٧٠ - قلت الْأمة لَهَا فرَاش
قَالَ نعم قضى النَّبِي صلى الله عَلَيْهِ وَسلم فِي عبد بن زَمعَة أَن الْوَلَد للْفراش وَقَالَ عمر بن الْخطاب من أقرّ بِوَطْء ألزمته الْوَلَد وَنَذْهَب أَنه إِذا أقرّ بِوَطْء لَا يقدر أَن يتبرأ مِنْهُ','3','73',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (910,'<span data-type="title" id=toc-909>حكم إِعَادَة الْعُضْو الْمَقْطُوع إِلَى مَكَانَهُ</span>

١٣٧١ - قلت قطع عُضْو من الْجَسَد
قَالَ لَا بَأْس أَن يُعِيدهُ مَكَانَهُ وَذَاكَ أَن فِيهِ الرّوح مثل الْأذن تقطع فيعيدها بطراتها
<span data-type="title" id=toc-910>حكم الْكَلَام والتلقين بعد سَهْو الإِمَام</span>

١٣٧٢ - قلت رجل يُصَلِّي بِقوم فَأَرَادَ أَن يرْكَع فَسجدَ فسبح بِهِ الْقَوْم فَلم يدر
قَالَ إِن كَانُوا تكلمُوا أعادوا الصَّلَاة
١٣٧٣ - قلت فالإمام حِين كَلمهمْ يُعِيد الصَّلَاة
قَالَ لَا هَذَا إِذا كَانَ يتثبت وَلَيْسَ على النَّاس أَن يجيبوا الإِمَام فَإِذا كلموا الإِمَام أعادوا الصَّلَاة
١٣٧٤ - قلت احْتَجُّوا بِحَدِيث مُعَاوِيَة بن الحكم قَالُوا لم يَأْمُرهُم','3','74',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (911,'النَّبِي صلى الله عَلَيْهِ وَسلم أَن يُعِيدُوا الصَّلَاة
قَالَ أبي أَلا يرَوْنَ إِلَى حَدِيث ابْن مَسْعُود لما أَن تكلمُوا فِي الصَّلَاة','3','75',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (912,'آخر ٠ الرَّابِع عشر من أَجزَاء صَالح

<span data-type="title" id=toc-912>حكم أَخذ الْأجر على الْحَج عَن الْغَيْر</span>

١٣٧٥ - حَدثنَا صَالح قَالَ سَأَلت أبي عَن الرجل يعْطى الدَّرَاهِم ليحج بهَا عَن الْمَيِّت
قَالَ أكرهه
١٣٧٦ - قلت فالقرابة
قَالَ أَلَيْسَ يُقَال إِن رجلا لبّى لبيْك عَن شبْرمَة فَقَالَ من شبْرمَة فَقَالَ قرَابَة
١٣٧٧ - قَالَ وَأَنا أرى أَن يُوصي الرجل بِالْحَجِّ وَلَكِن أكره للرجل أَن يكون يَأْخُذ على شَيْء من فعل الْخَيْر أجرا
<span data-type="title" id=toc-913>هَل يحل من سَاق الْهَدْي فِي الْعشْر</span>

١٣٧٨ - قَالَ وَإِذا سَاق الْهَدْي فِي الْعشْر فَلَا يحل من إِحْرَامه','3','76',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (913,'<span data-type="title" id=toc-914>لَا يدْخل مَكَّة أحد إِلَّا محرما</span>

١٣٧٩ - قَالَ وَلَا يدْخل مَكَّة أحد إِلَّا محرما فِي أَيَّام الْحَج وَلَا فِي غَيرهَا ثمَّ يطوف بِالْبَيْتِ
<span data-type="title" id=toc-915>وَكِيع أثبت من يحيى بن يمَان</span>

١٣٨٠ - سَمِعت أبي يَقُول وَكِيع أثبت من يحيى بن يمَان يحيى مُضْطَرب فِي بعض حَدِيثه','3','77',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (914,'<span data-type="title" id=toc-916>من قَالَ لإمرأته أَنْت طَالِق إِن لم أطلقك</span>

١٣٨١ - سَأَلت أبي عَن رجل قَالَ لإمرأته أَنْت طَالِق إِن لم أطلقك
قَالَ يُعجبنِي أَن يطلقهَا تَطْلِيقَة وَتَكون عِنْده على ثِنْتَيْنِ
<span data-type="title" id=toc-917>الْمُتَمَتّع إِذا صَامَ ثمَّ وجد مَا يذبح</span>

١٣٨٢ - قلت الرجل يدْخل بِعُمْرَة فيخاف أَن لَا يجد مَا يذبح فيصوم ثمَّ يجد مَا يذبح
قَالَ إِذا دخل فِي الصَّوْم أَجزَأَهُ
<span data-type="title" id=toc-918>التَّعْلِيم أحب من الْمَسْأَلَة</span>

١٣٨٣ - قلت رجل لَهُ وَالِد يعلم بِلَا مشارطة وَهُوَ يسْأَل وَالِده الْخُرُوج إِلَى الثغر فتكره خُرُوجه
قَالَ لَا يخرج فالتعليم أحب إِلَيّ من الْمَسْأَلَة','3','78',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (915,'<span data-type="title" id=toc-919>من أَيْن يحرم الْمُتَمَتّع لِلْحَجِّ</span>

١٣٨٤ - قلت رجل دخل بِعُمْرَة فَلَمَّا حل أَرَادَ أَن ينشيء الْحَج من أَيْن ينشىء
قَالَ من الْمَسْجِد أَو من أَي مَكَان أحب
<span data-type="title" id=toc-920>حكم السُّكْنَى بِمَكَّة بِالْأُجْرَةِ</span>

١٣٨٥ - قلت السُّكْنَى بِمَكَّة وَإِعْطَاء الْأجر
فَقَالَ ويجد النَّاس من هَذَا بدا يُقَال إِن عمر اشْترى دَار السجْن وَعَامة النَّاس تكرههُ لقَوْل الله سَوَاء العاكف فِيهِ والباد','3','79',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (916,'١٣٨٦ - قَالَ أبي جَلَست أَنا وَإِسْحَاق بن رَاهْوَيْةِ يَوْمًا إِلَى الشَّافِعِي فناظره إِسْحَاق فِي السُّكْنَى بِمَكَّة فعلا إِسْحَاق يَوْمئِذٍ الشَّافِعِي','3','80',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (917,'<span data-type="title" id=toc-921>حكم الْغسْل من مَاء زَمْزَم</span>

١٣٨٧ - قلت الْغسْل من مَاء زَمْزَم وَقد قَالَ الْعَبَّاس لَا أحلهَا لمغتسل
فَقَالَ يَتَمَالَك النَّاس من هَذَا قَالَ وَكَانَ سُفْيَان بن عُيَيْنَة يَحْكِي عَن ابْن عَبَّاس لَا أحلهَا لمغتسل فيحكى عَن الْعَبَّاس وَابْن الْعَبَّاس
قَالَ وَإِن توقاه أعجب إِلَيّ
<span data-type="title" id=toc-922>حكم قتل الرجل بِالْمَرْأَةِ وَالْحر بِالْعَبدِ</span>

١٣٨٨ - قَالَ أبي أقتل الرجل بِالْمَرْأَةِ وَلَا أقتل الْحر بِالْعَبدِ وَلَا أذهب إِلَى','3','81',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (918,'حَدِيث سَمُرَة وَكَانَ الْحسن يَقُول لَا يقتل حر بِعَبْد','3','82',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (919,'<span data-type="title" id=toc-923>من أعتق أمته وَجعل عتقهَا صَدَاقهَا</span>

١٣٨٩ - قلت الرجل يعْتق الْأمة فَيَقُول أجعَل عتقك صداقك أَو صداقك عتقك
قَالَ كل جَائِز إِذا كَانَت لَهُ نِيَّة فنيته
<span data-type="title" id=toc-924>حكم النَّفَقَة والسكني للمطلقة ثَلَاثًا</span>

١٣٩٠ - قَالَ حَدِيث فَاطِمَة بنت قيس أذهب إِلَيْهِ هُوَ صَحِيح لَيْسَ لَهَا سُكْنى وَلَا نَفَقَة
١٣٩١ - قلت فَإِن إِبْرَاهِيم النَّخعِيّ يَقُول لَهَا السُّكْنَى وَلَا نَفَقَة
قَالَ هَذِه قُوَّة لحَدِيث فَاطِمَة','3','83',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (920,'<span data-type="title" id=toc-925>قَول أَحْمد فِي رجال</span>

١٣٩٢ - وَقَالَ أَبُو المتَوَكل الناجى وَأَبُو نَضرة مَا علمت إِلَّا خيرا وَأَبُو الْهَيْثَم صَاحب الْقصب مَعْرُوف روى عَنهُ الثَّوْريّ
<span data-type="title" id=toc-926>إِذا أسلم فِي طَعَام وَلم يذكر مَكَان الْإِيفَاء</span>

١٣٩٣ - قلت الرجل يسلم فِي طَعَام فِي كيل مَعْلُوم إِلَى أجل مَعْلُوم وَلَا يُسَمِّي فِي أَي بَلْدَة
قَالَ يردهُ إِلَى بَلَده الَّذِي أسلف فِيهِ حَتَّى يُوفيه فِي الْموضع الَّذِي أسلف فِيهِ','3','84',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (921,'<span data-type="title" id=toc-927>قَول أَحْمد فِي هَاشم بن هَاشم</span>

١٣٩٤ - وَقَالَ هَاشم بن هَاشم بن عتبَة لَيْسَ بِهِ بَأْس
<span data-type="title" id=toc-928>من وَقع على امْرَأَته قبل رمي الْجمار</span>

١٣٩٥ - قلت الرجل يَقع على امْرَأَته قبل أَن يَرْمِي الْجَمْرَة
قَالَ أفسد حجه
<span data-type="title" id=toc-929>مِقْدَار كَفَّارَة الْيَمين</span>

١٣٩٦ - وَقَالَ كَفَّارَة الْيَمين رَطْل وَثلث حِنْطَة أَو دَقِيق
<span data-type="title" id=toc-930>مَتى يحل من دخل بِعُمْرَة فِي الْعشْر وَمَعَهُ الْهَدْي</span>

١٣٩٧ - قلت الرجل يدْخل بِعُمْرَة فِي الْعشْر ويسوق مَعَه الْهَدْي','3','85',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (922,'قَالَ لَا يحل حَتَّى ينْحَر أَلَيْسَ أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم إِنَّمَا دخلُوا فِي الْعشْر
<span data-type="title" id=toc-931>صَلَاة النَّهَار مثنى مثنى</span>

١٣٩٨ - وَقَالَ صَلَاة النَّهَار مثنى مثنى
<span data-type="title" id=toc-932>الْخَطَأ والعمد فِي قتل الصَّيْد سَوَاء</span>

١٣٩٩ - وَقَالَ الْخَطَأ والعمد فِي قتل الصَّيْد سَوَاء وَقَالَ عبد الله بن مَسْعُود حكم عَلَيْهِ حِين ألْقى على الصَّيْد جوالق وَعمر بن الْخطاب أشرك بَين الْعمد وَالْخَطَأ','3','86',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (923,'<span data-type="title" id=toc-933>إِلَى مَتى تَرث مُطلقَة الْمَرِيض</span>

١٤٠٠ - وَقَالَ الفار الْمُطلق فِي الْمَرَض تَرثه امْرَأَته مالم تزوج
<span data-type="title" id=toc-934>حكم الْجَهْر بِالْقِرَاءَةِ فِي قَضَاء الصَّلَاة الجهرية</span>

١٤٠١ - وَقَالَ الْمُصَلِّي الَّذِي يقْضِي صَلَاة يجْهر فِيهَا بِالْقِرَاءَةِ قَالَ لَيْسَ عَلَيْهِ أَن يجْهر إِنَّمَا الْجَهْر على الإِمَام يسمع النَّاس
<span data-type="title" id=toc-935>حكم بيع الْحَشِيش الَّذِي نبت فِي أرضه وقطعه من أَرض الْغَيْر</span>
١٤٠٢ - لَا أرى بيع الْحَشِيش إِلَّا أَن يقطعهُ','3','87',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (924,'١٤٠٣ - قلت الزَّرْع يكون فِيهِ الْحَشِيش فَيدْخل الرجل فَيقطع مِنْهُ
قَالَ لَا يدْخل رجل أَرض رجل بِغَيْر إِذْنه
<span data-type="title" id=toc-936>الحكم فِيمَن وطئ ذَات محرم</span>

١٤٠٤ - وَقَالَ أبي إِذا وطئ الرجل ذَات محرم قتل وَأخذ مَاله
<span data-type="title" id=toc-937>حكم الرَّهْن وَالْكَفِيل فِي السّلم</span>

١٤٠٥ - وَقَالَ أكره الرَّهْن وَالْكَفِيل فِي السّلم حَتَّى يكون كَسَائِر الْغُرَمَاء يخَاف ويرجو','3','88',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (925,'<span data-type="title" id=toc-938>مَسْأَلَة فِي الْفَرَائِض</span>

١٤٠٦ - قلت رجل مَاتَ وَترك أَرضًا ومتاعا ودنانير وجاريتين وَله ولد وَامْرَأَة وَأعْتق إِحْدَى الجاريتين فِي مَرضه وَأوصى بِالدَّنَانِيرِ أَن تقسم بعده فِي الْمَسَاكِين وَأَن تبَاع الْجَارِيَة الْأُخْرَى فَيقْضى بهَا دينه الَّذِي عَلَيْهِ فعمدت امْرَأَته بعد وَفَاته فباعت الْجَارِيَة وَلم تقض الدّين
قَالَ يقوم الجاريتين والضيعة وَالدَّنَانِير وَمَا ترك فَيخرج الْجَارِيَة الَّتِي أعتق وَالدَّنَانِير من الثُّلُث فَإِن خرج من الثُّلُث وَإِلَّا كَانَ بِالْحِصَصِ وَترد الْجَارِيَة الَّتِي باعتها الْمَرْأَة حَتَّى يَبِيعهَا الْوَصِيّ فَإِن كَانَت استهلكت قومت عَلَيْهَا وَيجوز بيع الْمَرْأَة فِي نصِيبهَا
<span data-type="title" id=toc-939>حكم شِرَاء الْعرُوض بالسلم وَالسّلم فِي الْحَيَوَان</span>

١٤٠٧ - قلت السّلم أشترى بِهِ الْعرُوض
قَالَ هَذَا بيع مَا لَيْسَ عنْدك','3','89',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (926,'١٤٠٨ - وأكره بيع الْحَيَوَان بِالْحَيَوَانِ نَسِيئَة
١٤٠٩ - وَلَا أرى بالسلم فِي الْحَيَوَان بَأْسا وَإِنَّمَا كره ابْن مَسْعُود من نتاج مَعْرُوف','3','90',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (927,'<span data-type="title" id=toc-940>إِذا اشار الْمحرم إِلَى الْحَلَال بالصيد</span>

١٤١٠ - وَقَالَ فِي الْمحرم يُشِير إِلَى الْحَلَال بالصيد قَالَ عَلَيْهِ الْجَزَاء
<span data-type="title" id=toc-941>حكم بيع الْغَائِب بالحاضر</span>

١٤١١ - وَقَالَ لَا يُبَاع غَائِب بناجز على حَدِيث عمر هاوها
<span data-type="title" id=toc-942>الحكم إِذا طلق الْأمة تَطْلِيقَتَيْنِ ثمَّ اشْتَرَاهَا</span>

١٤١٢ - قلت إِذا طلق الْأمة تَطْلِيقَتَيْنِ ثمَّ اشْتَرَاهَا','3','91',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (928,'قَالَ لَا تحل لَهُ حَتَّى تنْكح زوجا غَيره أذهب إِلَى حَدِيث زيد بن ثَابت وَعلي بن أبي طَالب
١٤١٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم عَن خَالِد عَن أبي معشر عَن إِبْرَاهِيم وَالْحكم قَالَ ذكر أَحدهمَا عَن عُبَيْدَة عَن عَليّ قَالَ لَا تحل لَهُ حَتَّى تنْكح زوجا غَيره','3','92',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (929,'<span data-type="title" id=toc-943>سنة وَفَاة أبي الْقَاسِم بن أبي الزِّنَاد</span>

١٤١٤ - قَالَ أبي أَبُو الْقَاسِم بن أبي الزِّنَاد سنة ثَلَاث وَثَمَانِينَ يعْنى سمع مِنْهُ مكث قَلِيلا ثمَّ مَاتَ
<span data-type="title" id=toc-944>تَصْحِيف أبي سَلمَة فِي حَدِيث زَيْنَب فِي الْحداد</span>

١٤١٥ - حَدِيث حميد بن نَافِع حَدِيث زَيْنَب فِي الْحداد أَبُو سَلمَة','3','93',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (930,'الْخُزَاعِيّ يصحف فِيهِ قَالَ تقيض قَالَ إِنَّمَا هِيَ فتفتض بِهِ
<span data-type="title" id=toc-945>اضْطِرَاب فِي حَدِيث ابْن عَبَّاس فِي الدّباغ</span>

١٤١٦ - قَالَ دباغ الْجُلُود حَدِيث ابْن عَبَّاس ابْن وَعلة يَقُول سَمِعت النَّبِي صلى الله عَلَيْهِ وَسلم وَعِكْرِمَة يَقُول عَن ابْن عَبَّاس عَن سَوْدَة وعبيد الله بن عبد الله يَقُول عَن ابْن عَبَّاس عَن','3','94',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (931,'مَيْمُونَة ابْن عُيَيْنَة يَقُول عَن ابْن عَبَّاس عَن مَيْمُونَة هُوَ خطأ يُخَالف النَّاس لَيْسَ فِيهِ دباغه يُونُس وَمعمر وَمَالك لَا يذكرُونَ دباغه وَلَيْسَ عِنْدِي فِي دباغ الْميتَة حَدِيث صَحِيح وَحَدِيث ابْن عكيم هُوَ أصَحهمَا','3','95',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (932,'١٤١٧ - قَالَ أبي الله قد حرم الْميتَة فالجلد هُوَ من الْميتَة وأذهب إِلَى حَدِيث ابْن عكيم أَرْجُو أَن يكون صَحِيحا لَا تنتفعوا من الْميتَة بإهاب وَلَا عصب
<span data-type="title" id=toc-946>عدَّة من ارْتَفع حَيْضهَا لمَرض وَالَّتِي لَا تَدْرِي مَا رفع حَيْضهَا</span>

١٤١٨ - وَقَالَ الْمَرْأَة الَّتِي يرْتَفع حَيْضهَا وَهِي مِمَّن تحيض وَلَا تَدْرِي مَا رفع حَيْضهَا على حَدِيث عمر تَعْتَد بِسنة تِسْعَة أشهر ثمَّ ثَلَاثَة أشهر
١٤١٩ - قلت فالتي تحيض ومرضت
قَالَ على حَدِيث ابْن مَسْعُود الْعدة بِالْحيضِ
١٤٢٠ - قلت من قَالَ مَرضت','3','96',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (933,'قَالَ سَمِعت وكيعا قَالَ حَدثنَا الْأَعْمَش عَن إِبْرَاهِيم عَن عَلْقَمَة قَالَ مَرضت امْرَأَتي فَقَالَ عبد الله بن مَسْعُود حبس الله عَلَيْك مِيرَاثهَا
<span data-type="title" id=toc-947>عدَّة الْمُطلقَة إِذا اسْتمرّ بهَا الدَّم وَلَيْسَ لَهَا أَيَّام مَعْلُومَة</span>

١٤٢١ - قلت الْبكر تطلق فيستمر بهَا الدَّم وَلَا تعلم لَهَا أَيَّامًا
فَقَالَ مثل حَدِيث عمر إِذا اسْتمرّ بهَا الدَّم تنْتَظر سنة','3','97',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (934,'<span data-type="title" id=toc-948>الْحَائِض إِذا تغير حَيْضهَا بِالزِّيَادَةِ</span>

١٤٢٢ - قلت الْحَائِض إِذا تغير حَيْضهَا فَكَانَت تحيض خمْسا أَو نَحْو هَذَا ثمَّ زَاد حَيْضهَا
قَالَ تصلي مَا زَاد حَتَّى تعلم أَنه حيض منتقل وَإِنَّمَا يعرف ذَلِك إِذا عاودها ثَلَاث مرار فَإِذا علمت أَنه حيض منتقل فَإِن كَانَت صَامت فِي تِلْكَ الْأَيَّام صوما أعادته لِأَنَّهُ لَا يجزيها أَن تَصُوم وَهِي حَائِض
<span data-type="title" id=toc-949>عدَّة من ارْتَفع حَيْضهَا</span>

١٤٢٣ - وَإِذا كَانَت لَا تَدْرِي مَا الَّذِي رفع حَيْضهَا فعلى مَا روى عَن عمر أَنَّهَا تربص سنة تِسْعَة أشهر للْحَمْل وَثَلَاثَة أشهر مَكَان ثَلَاث حيض
وَإِذا كَانَت تَدْرِي مَا الَّذِي رفع حَيْضهَا أَو كَانَت مَرِيضَة فارتفع حَيْضهَا أَو كَانَت ترْضع فارتفع حَيْضهَا فعده هَذِه بِالْحيضِ وَإِن تطاول بهَا
<span data-type="title" id=toc-950>حكم من كَانَت تحيض فِي السّنة حَيْضَة ثمَّ اسْتمرّ بهَا الدَّم</span>

١٤٢٤ - وَإِن كَانَت تحيض فِي كل سنة حَيْضَة فَإِذا اسْتمرّ بهَا الدَّم جَلَست مَا زَاد','3','98',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (935,'<span data-type="title" id=toc-951>حكم الْحَائِض إِذا نقص حَيْضهَا</span>

١٤٢٥ - قلت وَإِن كَانَت تحيض عشرا فطهرت فِي خمس
قَالَ تصلي وتصوم فَإِن رَأَتْ الدَّم قبل الْعشْر أَمْسَكت عَن الصَّلَاة وَلَا تعيد الصَّوْم الَّذِي صَامت قبل الْعشْر لِأَنَّهَا كَانَت طَاهِرا فَإِذا جَازَ الْعشْر وَاسْتمرّ بهَا الدَّم صلت وصامت حَتَّى تعرف أَنه قد انْتقل حَيْضهَا
<span data-type="title" id=toc-952>حكم النُّفَسَاء إِذا طهرت ثمَّ عاودها الدَّم قبل الْأَرْبَعين</span>

١٤٢٦ - وَقَالَ فِي النُّفَسَاء إِذا طهرت فِي عشْرين قَالَ تَصُوم وَتصلي فَإِن مكثت خمس عشرَة وصامت وصلت ثمَّ عاودها الدَّم فِي الْخَمْسَة الْأَخِيرَة تَصُوم ثمَّ تعيد الصّيام لِأَنَّهُ لَا يَأْمَن أَن يكون بَقِيَّة نِفَاس أَو حيض هَذِه الْخَمْسَة أَيَّام تحتاط لَهَا فِي الصَّوْم قبل الْأَرْبَعين وَأما الْخمس عشرَة لَا تعيد الصّيام لِأَنَّهَا كَانَت طَاهِرا','3','99',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (936,'وَقَالَ ابْن عَبَّاس إِذا رَأَتْ الدَّم البحراني تدع الصَّلَاة وَإِذا رَأَتْ الطُّهْر وَلَو سَاعَة صلت وَقَالَ أَرَأَيْت لَو حجت وطافت طواف الزِّيَارَة ثمَّ رجعت إِلَى أَهلهَا ثمَّ عاودها الدَّم بعد أكنا نأمرها أَن ترجع إِلَى الْبَيْت فتعيد طَوافا نقُول قد أجزأها وَهِي طَاهِر
<span data-type="title" id=toc-953>حكم الصُّفْرَة الَّتِي ترَاهَا الْمَرْأَة أَيَّام حَيْضهَا وَبعدهَا</span>

١٤٢٧ - وَقَالَ الْمَرْأَة ترى الصُّفْرَة أَيَّام حَيْضهَا تجْلِس كَمَا كَانَت تجْلِس فِي أَيَّامهَا وَأما الصُّفْرَة إذاهي طهرت لَا تلْتَفت إِلَيْهَا إِذا رَأَتْ الْقِصَّة الْبَيْضَاء وَلَكِن كل شَيْء ترَاهُ فِي أَيَّامهَا من صفرَة وَغير ذَلِك فَهُوَ حيض','3','100',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (937,'<span data-type="title" id=toc-954>حكم من طهرت من الْحيض قبل الْمغرب أَو قبل الْفجْر</span>

١٤٢٨ - وَقَالَ الْمَرْأَة ترى الطُّهْر قبل الْمغرب أذهب إِلَى حَدِيث عبد الرحمن بن عَوْف تعيد الظّهْر وَالْعصر وَإِذا طهرت قبل الْفجْر تعيد الْمغرب وَالْعشَاء على حَدِيث ابْن عَبَّاس وعبد الرحمن بن عَوْف','3','101',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (938,'<span data-type="title" id=toc-955>إِذا ادَّعَت الْمُطلقَة أَن عدتهَا قد انْقَضتْ</span>

١٤٢٩ - وأذهب إِلَى حَدِيث عَليّ وَشُرَيْح إِن جَاءَت من بطانة أَهلهَا مِمَّن يرضى دينه وأمانته أَنَّهَا صَامت وصلت قَالَ أبي إِذا كَانَ أَكثر من ثَلَاثِينَ يَوْمًا صدقت
<span data-type="title" id=toc-956>كَيفَ تتطهر الْمُسْتَحَاضَة للصَّلَاة</span>

١٤٣٠ - وَقَالَ أذهب إِلَى أَن تَغْتَسِل الْمُسْتَحَاضَة عِنْد انْقِطَاع الدَّم وَتَوَضَّأ لكل صَلَاة وَقَالَ هَذَا أقل مَا نأمرها فَإِن جَاءَت بِغسْل لكل صَلَاة فَهُوَ أفضل','3','102',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (939,'<span data-type="title" id=toc-957>حكم الصَّيْد للْمحرمِ قبل طواف الْإِفَاضَة إِذا رمى وَحلق وَذبح</span>

١٤٣١ - قلت الْمحرم إِذا رمى وَحلق وَذبح قبل أَن يطوف بِالْبَيْتِ أَله أَن يصيد فِي غير الْحرم
قَالَ نعم أَلَيْسَ قَالَ النَّبِي صلى الله عَلَيْهِ وَسلم إِذا حلقتم وذبحتم فقد حل لكم كل شَيْء إِلَّا النِّسَاء','3','103',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (940,'<span data-type="title" id=toc-958>مَذْهَب ابْن سِيرِين فِي قبُول الْمُرْسل</span>

١٤٣٢ - حَدثنِي أبي قَالَ حَدثنَا إِسْمَاعِيل بن إِبْرَاهِيم عَن أَيُّوب قَالَ كَانَ الرجل يحدث مُحَمَّدًا بِالْحَدِيثِ فَيَقُول إِنِّي وَالله مَا أتهمك وَلَا أتهم ذَاك وَلَكِن أتهم من بَيْنكُمَا كَأَنَّهُ يَقُول لَا أتهمك وَلَا أتهم الرجل الَّذِي من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم وَلَكِن من بَيْنكُمَا
<span data-type="title" id=toc-959>شرح الشَّافِعِي للقصة الْبَيْضَاء</span>

١٤٣٣ - قَالَ أبي قَالَ الشَّافِعِي الْقِصَّة الْبَيْضَاء هُوَ شَيْء يتبع الْحيض أَبيض فَإِذا رَأَتْ ذَلِك فقد طهرت
<span data-type="title" id=toc-960>إِذا ادَّعَت الْمَرْأَة أَن عدتهَا قد انْقَضتْ</span>

١٤٣٤ - وَقَالَ إِذا طلق الرجل امْرَأَته فَجَاءَت فَزَعَمت أَن عدتهَا قد انْقَضتْ فِي شهر لم تصدق وَنَذْهَب فِيهِ إِلَى قَول عَليّ حِين سَأَلَ شُرَيْح إِن أَقَامَت الْبَيِّنَة من بطانة أَهلهَا أَنَّهَا حَاضَت فِي شهر ثَلَاث حيض وَتَكون بَينهمَا تَصُوم وَتصلي وَتفعل مَا يفعل الطَّاهِر فَإِن ادَّعَت أَن','3','104',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (941,'عدتهَا قد انْقَضتْ فِي أَكثر من شهر صدقت القَوْل قَول أبي إِن الْمَرْأَة ائتمنت على فرجهَا
<span data-type="title" id=toc-961>من أرسل كَلْبه فِي الْحل فَأخذ الصَّيْد فِي الْحرم</span>

١٤٣٥ - قلت الرجل يُرْسل كَلْبه فِي الْحل فيصيد فِي الْحرم
قَالَ لَيْسَ عَلَيْهِ جَزَاء إِذا لم يكن بِالْقربِ
<span data-type="title" id=toc-962>من اصطاد طيرا من شَجَرَة أَصْلهَا الْحل فِي وَأَغْصَانهَا فِي الْحرم</span>
قلت الشَّجَرَة يكون أَصْلهَا فِي الْحل وَأَغْصَانهَا فِي الْحَرَام أصاد رجل مِنْهَا طيرا
قَالَ عَلَيْهِ الْجَزَاء','3','105',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (942,'<span data-type="title" id=toc-963>من قَالَ لزوجته أَمرك بِيَدِك أَو اخْتَارِي نَفسك</span>

١٤٣٧ - وَقَالَ فِي أَمرك بِيَدِك أذهب إِلَى الْقَضَاء مَا قَضَت وَإِذا قَالَ أَمرك بِيَدِك فَأمرهَا بِيَدِهَا مالم يغشها أَو يرجع
وَإِذا قَالَ اخْتَارِي فعلى جَوَاب الْكَلَام مالم يطلّ الْكَلَام
<span data-type="title" id=toc-964>حكم النِّيَّة فِي الطَّلَاق</span>

١٤٣٨ - وَقَالَ النِّيَّة فِي الطَّلَاق مثل الخلية والبرية والبائن والبتة
والحرج يَقُول أَنْت عَليّ حرج أَخَاف أَن تكون هَذِه ثَلَاثًا ثَلَاثًا
١٤٣٩ - وَقَالَ ينوى إِذا قَالَ حبلك على غاربك رده عَليّ بن أبي طَالب إِلَى نِيَّته','3','106',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (943,'١٤٤٠ - وَفِي الْمَوْهُوبَة إِن قبلهَا أَهلهَا فتطليقة وَاحِدَة يملك الرّجْعَة وَإِن لم يقبلوها فَلَا شَيْء
<span data-type="title" id=toc-965>مسَائِل من الْفَرَائِض</span>

١٤٤١ - قَالَ أبي يَرث من الْجدَّات ثَلَاثَة أم الْأُم وَأم الْأَب وَأم أَبِيه
١٤٤٢ - الْخَالَة والعمة للخالة الثُّلُث وللعمة الثُّلُثَانِ','3','107',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (944,'١٤٤٣ - كَانَ عمر يَجْعَل للْجدّ السُّدس ثمَّ جعل لَهُ الثُّلُث بعد وَكَانَ عَليّ يَجْعَل لَهُ الثُّلُث ثمَّ جعل لَهُ السُّدس بعد
١٤٤٤ - وَقَالَ مَا كَانَ أحد ينزل بني الْأَخ مَعَ الْجد بمنزله الْأَخ اللَّهُمَّ إِلَّا عَليّ يرويهِ إِسْمَاعِيل ابْن أبي خَالِد عَن الشّعبِيّ','3','108',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (945,'<span data-type="title" id=toc-966>بعض نواقض الْوضُوء</span>

١٤٤٥ - وَقَالَ يتَوَضَّأ من أَشْيَاء كَثِيرَة كل شَيْء خرج من السَّبِيلَيْنِ والرعاف وَمَسّ الذّكر والضحك لَيْسَ فِيهِ حَدِيث صَحِيح
<span data-type="title" id=toc-967>لَا يُورث الْحميل إِلَّا بِبَيِّنَة</span>

١٤٤٦ - والحميل يُورث بِبَيِّنَة مِمَّن جَاءَ مَعَه يَعْنِي فِي السَّبي يخرجُون فَيدعونَ فَلَا يقبل قَوْلهم حَتَّى يقيموا الْبَيِّنَة مِنْهُم','3','109',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (946,'١٤٤٧ - وَأهل الذِّمَّة لَا يورثون إِلَّا من ولد فِي دَار الْإِسْلَام
<span data-type="title" id=toc-968>إِذا أرضعت زَوجته الْكَبِيرَة الصَّغِيرَة</span>

١٤٤٨ - قلت رجل يتَزَوَّج الْمَرْأَة وَلم يدْخل بهَا ثمَّ يتَزَوَّج صَغِيرَة فأرضعت الْكَبِيرَة الصَّغِيرَة
قَالَ حرمت عَلَيْهِ الْكَبِيرَة قَالَ الله {وَأُمَّهَات نِسَائِكُم}
<span data-type="title" id=toc-969>حكم غشيان الْمَرْأَة قبل أَن تَغْتَسِل من الْحيض</span>

١٤٤٩ - قلت الْمَرْأَة تحيض أيغشاها زَوجهَا قبل أَن تَغْتَسِل
قَالَ لَا حَتَّى تَغْتَسِل بِالْمَاءِ قَالَ الله {حَتَّى يطهرن فَإِذا تطهرن} فَإِذا تطهرن بِالْمَاءِ','3','110',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (947,'١٤٥٠ - قلت الرجل تكون مَعَه الْمَرْأَة فِي السّفر فتحيض فَلَا تَجِد المَاء أيغشاها زَوجهَا
قَالَ تيَمّم هَذِه حَال ضَرُورَة
<span data-type="title" id=toc-970>أَقْوَال أَحْمد فِي رجال</span>

١٤٥١ - وَقَالَ عبد الرحمن بن إِبْرَاهِيم لَيْسَ بِهِ بَأْس حَدثنَا عَنهُ عَفَّان كَانَ قَاصا من أهل الْمَدِينَة كَانَت عِنْده كراسة للعلاء بن عبد الرحمن
١٤٥٢ - وَيحيى بن حَمْزَة لَيْسَ بِهِ بَأْس
١٤٥٣ - سُلَيْمَان بن عتبَة يروي عَنهُ يُونُس بن ميسرَة لَا أعرفهُ','3','111',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (948,'١٤٥٤ - سعيد بن عبد الرَّحْمَن الجُمَحِي لَيْسَ بِهِ بَأْس كَانَ قَاضِي يَعْنِي عَسْكَر الْمهْدي يروي حَدِيث ابْن عمر فِي صَدَقَة الْفطر عَن كل صَغِير وكبير من الْمُسلمين قد رَوَاهُ مَالك والعمري الصَّغِير وَالْعَمَل عَلَيْهِ وَقَالَ إِنَّمَا الصَّدَقَة طهرة فاليهودي وَالنَّصْرَانِيّ أَي طهرة لَهُ
<span data-type="title" id=toc-971>حكم الْقِسْمَة بِالْأُجْرَةِ</span>

١٤٥٥ - قلت كرى القسام
قَالَ أكره يرويهِ مُوسَى بن طريف عَن عَليّ حَدِيث النَّبِي','3','112',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (949,'صلى الله عَلَيْهِ وَسلم اشْترى رجل سَرَاوِيل وَثمّ وزان يزن بِالْأَجْرِ
قَالَ مَا أَدْرِي مَا هَذَا
<span data-type="title" id=toc-972>حكم أُجْرَة الْمعلم</span>

١٤٥٦ - وَقَالَ أجر الْمعلم النَّاس فِيهِ مُخْتَلفُونَ','3','113',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (950,'<span data-type="title" id=toc-973>حكم إحْيَاء الْموَات</span>

١٤٥٧ - وَقَالَ من حجر أَرضًا لَيست لأحد فَهِيَ لَهُ
١٤٥٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن بشر عَن سعيد عَن قَتَادَة عَن سُلَيْمَان الْيَشْكُرِي عَن جَابر بن عبد الله عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ من أحَاط على أَرض فَهِيَ لَهُ
١٤٥٩ - وَقَالَ أذهب إِلَى أَنه إِذا كَانَ حَرِيم بِئْر عَادِية خمسين ذِرَاعا فَلَيْسَ لأحد أَن يدْخل فِيهَا
<span data-type="title" id=toc-974>قَول أَحْمد فِي الْحَارِث بن بِلَال</span>

١٤٦٠ - والْحَارث بن بِلَال يرْوى عَنهُ فِي الْحَج حَدِيث وَاحِد واستنكره','3','114',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (951,'<span data-type="title" id=toc-975>حَرِيم الْبِئْر بعد الْإِحْيَاء</span>

١٤٦١ - إِذا لم كَانَ الْبِئْر عَادِية فحريمها خَمْسُونَ وَإِذا لم تكن عَادِية فخمسة وَعِشْرُونَ
<span data-type="title" id=toc-976>كنية عبد العزيز الدَّرَاورْدِي</span>

١٤٦٢ - عبد العزيز الدَّرَاورْدِي مَا كنيته','3','115',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (952,'<span data-type="title" id=toc-977>حكم الْحلف إِذا بَاعَ عبدا فَظهر بِهِ عيب على أَنه لم يعلم بِهِ</span>

١٤٦٣ - قلت الرجل يَبِيع العَبْد فيأبق أَو يظْهر بِهِ جُنُون يسْتَحْلف على الْبَتَات أَنه مَا يعلم أَنه أبق
فَقَالَ عُثْمَان اسْتحْلف ابْن عمر حِين بَاعَ فَقَالَ أتحلف أَنَّك بِعته وَمَا علمت بِهِ عَيْبا فَأبى ابْن عمر أَن يحلف فَرده عَلَيْهِ
وَمن النَّاس من يسْتَحْلف الْوَارِث إِذا ورث وعَلى ميته دين','3','116',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (953,'<span data-type="title" id=toc-978>ذكر بعض الروَاة عَن عبد الرحمن بن عمار</span>

١٤٦٤ - وَقَالَ عبد الرحمن بن عمار بن أبي زَيْنَب روى عَنهُ يحيى بن سعيد الْقطَّان وَمُحَمّد بن إِسْحَاق
<span data-type="title" id=toc-979>حكم التَّفْرِيق بَين الزَّوْجَيْنِ إِذا عجز الرجل عَن نَفَقَتهَا</span>

١٤٦٥ - وَقَالَ إِذا عجز الرجل عَن نَفَقَة امْرَأَته يفرق بَينهمَا
١٤٦٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا ابْن إِدْرِيس عَن عبيد الله عَن نَافِع عَن ابْن عمر أَن عمر كتب إِلَى أُمَرَاء الأجناد خزروا من قبلكُمْ بِالنَّفَقَةِ وَإِلَّا فليطلقوا','3','117',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (954,'<span data-type="title" id=toc-980>حكم الْخُرُوج بِالنسَاء إِلَى الثغور</span>

١٤٦٧ - وَقَالَ لَا أرى ان يخرج بِالنسَاء إِلَى الثغور
<span data-type="title" id=toc-981>رِوَايَة عَن عمر بن عبد العزيز فِي امْرَأَة الْمَفْقُود</span>

١٤٦٨ - حَدثنَا صَالح قَالَ حَدثنَا أبي قَالَ حَدثنَا عبد الرحمن بن مهْدي عَن مَنْصُور بن سعد عَن ابْن شبْرمَة أَن عمر بن عبد العزيز قَالَ امْرَأَة الْمَفْقُود تلوم وتصبر وَقَالَ مَيْمُون بن مهْرَان وَيُونُس بن أبي شبيب','3','118',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (955,'<span data-type="title" id=toc-982>حد الزَّانِي الْمُحصن</span>

١٤٦٩ - وَقَالَ أرى أَن يرْجم الْمُحصن وَلَا يجلد
١٤٧٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم عَن يُونُس وَمَنْصُور عَن ابْن سِيرِين إِلَّا أَن منصورا قَالَ عَن أَفْلح مولى أبي أَيُّوب أَن عمر رجم وَلم يجلد','3','119',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (956,'<span data-type="title" id=toc-983>قَول أَحْمد فِي حرَام بن عُثْمَان</span>

١٤٧١ - وَقَالَ حرَام بن عُثْمَان مديني لَا يرْوى حَدِيثه
<span data-type="title" id=toc-984>رِوَايَتَانِ فِي امْرَأَة الْمَفْقُود وَمَاله</span>

١٤٧٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنِي عبد الرزاق قَالَ أَخْبرنِي ابْن جريج قَالَ أَخْبرنِي عَطاء الْخُرَاسَانِي عَن الزُّهْرِيّ أَن عمر وَعُثْمَان قَالَا فِي امْرَأَة الْمَفْقُود تربص أَربع سِنِين ثمَّ تَعْتَد أَرْبَعَة أشهر وَعشرا وَيقسم مِيرَاثه','3','120',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (957,'١٤٧٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا السَّهْمِي عَن سعيد عَن قَتَادَة نَحوه
<span data-type="title" id=toc-985>قَول أَحْمد فِي عَليّ بن عَاصِم</span>

١٤٧٤ - وَقَالَ أروي حَدِيث عَليّ بن عَاصِم هُوَ مثل النَّاس يغلط أتراه أَضْعَف من حَدِيث ابْن لَهِيعَة مَا أرَاهُ أَضْعَف
<span data-type="title" id=toc-986>إِذا تزوجت الْمَرْأَة على عبد فَخرج حرا</span>

١٤٧٥ - وَقَالَ إِذا تزوجت الْمَرْأَة على عبد فَخرج حرا فلهَا قِيمَته','3','121',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (958,'<span data-type="title" id=toc-987>حكم سهم ذِي الْقُرْبَى</span>

١٤٧٦ - وَقَالَ فِي سهم ذِي الْقُرْبَى أذهب إِلَيْهِ فقيرهم وغنيهم فِيهِ سَوَاء
١٤٧٧ - وَقَالَ ابْن عَبَّاس أرادنا عمر على أَن نزوج أيامانا وَأَن نقضي عَن مديوننا فأبينا إِلَّا كُله
١٤٧٨ - قَالَ قسم النَّبِي صلى الله عَلَيْهِ وَسلم سهم ذَوي الْقُرْبَى إِلَى أَرْبَعَة آبَاء','3','122',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (959,'<span data-type="title" id=toc-988>حكم من أدْرك بعض الصَّلَاة مَعَ الإِمَام</span>

١٤٧٩ - قلت رجل أدْرك مَعَ الإِمَام بعض الصَّلَاة كَيفَ يقْضِي
قَالَ يقْرَأ فِيمَا يقْضِي
١٤٨٠ - قلت فالجلوس
قَالَ يصيره أول صلَاته كَمَا صنع ابْن مَسْعُود
<span data-type="title" id=toc-989>حكم من قَالَ لزوجته أَنْت طَالِق إِن شَاءَ الله</span>

١٤٨١ - قلت قَول الرجل لإمرأته أَنْت طَالِق إِن شَاءَ الله
قَالَ أَخَاف أَن يكون قد وَقع الطَّلَاق','3','123',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (960,'<span data-type="title" id=toc-990>كم يُؤْخَذ من أهل الْحَرْب وَمن أهل الذِّمَّة</span>

١٤٨٢ - قلت كم يُؤْخَذ من أهل الْحَرْب
قَالَ الْعشْر من كل عشرَة دَنَانِير دِينَار وَمن أهل الذِّمَّة من كل عشْرين دِينَارا دِينَار فَإِن نقصت من عشْرين لم يُؤْخَذ مِنْهُم شَيْء
حَدِيث عمر كم يَأْخُذُونَ مِنْكُم يَعْنِي أهل الْحَرْب إِذا قدمتم عَلَيْهِم
قَالُوا الْعشْر
قَالَ خُذُوا مِنْهُم الْعشْر على حَدِيث أنس بن مَالك','3','124',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (961,'<span data-type="title" id=toc-991>حكم الْحصْر عَن الْحَج</span>

١٤٨٣ - قلت أَيّمَا أقوى فِي الْمَذْهَب لَا حصر إِلَّا حصر الْعَدو وَقَول ابْن مَسْعُود اجعلوا بَيْنكُم وَبينهمْ يَوْم أمار
قَالَ أذهب أَنه لَا حصر إِلَّا حصر الْعَدو وَحَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم حَدِيث ابْن عمر أصنع كَمَا صنع رَسُول الله صلى الله عَلَيْهِ وَسلم يَعْنِي أَن النَّبِي صلى الله عَلَيْهِ وَسلم لما مَنعه الْمُشْركُونَ ذبح وَحلق وَرجع
وَقَالَ ابْن عمر لَا يخْتَلف عَنهُ وَابْن عَبَّاس يخْتَلف عَنهُ يعْنى فِي الْحصْر
<span data-type="title" id=toc-992>حكم من فَاتَهُ الْحَج</span>

١٤٨٤ - وَقَالَ الرجل يفوتهُ الْحَج عَلَيْهِ دم وَعَلِيهِ الْحَج من قَابل','3','125',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (962,'<span data-type="title" id=toc-993>حَدِيث ضباعة فِي الإشتراط فِي الْحَج</span>

١٤٨٥ - حَدِيث ضباعة عبد الرزاق يرويهِ عَن معمر عَن الزُّهْرِيّ عَن عُرْوَة عَن عَائِشَة وَابْن عمر أنكر الشَّرْط أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ لضباعة وَأَبُو أُسَامَة يرويهِ عَن هِشَام عَن أَبِيه عَن عَائِشَة وَحَدِيث عباد عَن هِلَال بن خباب عَن عِكْرِمَة عَن ابْن عَبَّاس سَمِعت من عباد','3','126',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (963,'١٤٨٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا البرْسَانِي عَن ابْن جريج عَن أبي الزبير عَن عِكْرِمَة وَطَاوُس عَن ابْن عَبَّاس
قَالَ أخْشَى أَن يكون لَيْسَ بِمَحْفُوظ فِي قصَّة ضباعة عَن جَابر إِنَّمَا هُوَ عَن ابْن عَبَّاس
<span data-type="title" id=toc-994>قصَّة الْعَبَّاس فِي تَعْجِيل الزَّكَاة</span>

١٤٨٧ - قلت حَدِيث مَنْصُور عَن الحكم قصَّة الْعَبَّاس وتعجيل الزَّكَاة أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ لعمر أما علمت أَنا أَخذنَا مِنْهُ زَكَاة الْعَام عَام أول','3','127',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (964,'<span data-type="title" id=toc-995>إِذا زوج الْأَب ابْنه الصَّغِير على من يكون الصَدَاق وَالنَّفقَة</span>

١٤٨٨ - قلت الرجل يُزَوّج صَغِيرا من امْرَأَة كَبِيرَة على من النَّفَقَة
قَالَ إِذا كَانَ للإبن مَال فَمن مَاله وَإِلَّا فَلَيْسَ على الْأَب شَيْء إِلَّا أَن يضمن حَدِيث ابْن عمر أَنْتُم رَضِيتُمْ بِهِ
١٤٨٩ - قلت فَإِن مَاتَ فعلى من الْمهْر
قَالَ الْمهْر هَكَذَا إِلَّا أَن يضمن الْأَب','3','128',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (965,'<span data-type="title" id=toc-996>حكم خِيَار الْبلُوغ للصَّغِير وَالصَّغِيرَة</span>

١٤٩٠ - قلت الرجل يُزَوّج ابْنه وَهُوَ صَغِير فَإِذا كبر قَالَ لَا أُرِيد
قَالَ لَيْسَ لَهُ ذَاك عقد الْأَب عَلَيْهِ عقد
١٤٩١ - قلت فالجارية الصَّغِيرَة يُزَوّجهَا أَبوهَا
قَالَ لَيْسَ بَين النَّاس فِي هَذَا اخْتِلَاف لَيْسَ لَهَا أَن ترجع
<span data-type="title" id=toc-997>إِذا زوج الْأَب بنته الْبكر وَلم يتسأمرها</span>

١٤٩٢ - قلت الْأَب يستأمر الْبكر
قَالَ إِن زوج الْأَب وَلم يستأمرها فَالنِّكَاح جَائِز لَيست هَذِه مثل الثّيّب الَّتِي لَهَا أَن ترجع وَإِذا زوج الْبكر وَهِي بَالغ فَمن النَّاس من يَقُول لَا خِيَار لَهَا وَمن النَّاس من يَقُول لَهَا الْخِيَار حَتَّى تأمرها','3','129',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (966,'<span data-type="title" id=toc-998>حكم الرُّجُوع فِي بدل الطَّلَاق إِذا لم يطلقهَا</span>

١٤٩٣ - قلت الْمَرْأَة تَقول للرجل طَلقنِي على أَن أجعَل لَك كَذَا وَكَذَا فَجعلت لَهُ فَلم يطلقهَا
قَالَ لَهَا أَن ترجع
<span data-type="title" id=toc-999>الحكم فِيمَن قَالَ أَنْت طَالِق رَأس الشَّهْر</span>

١٤٩٤ - قلت الرجل يَقُول أَنْت طَالِق رَأس الشَّهْر
قَالَ إِذا جَازَ رَأس الشَّهْر طلقت أذهب إِلَى حَدِيث أبي ذَر هُوَ عَتيق إِلَى رَأس الْحول
<span data-type="title" id=toc-1000>حكم الْحِيَل</span>

١٤٩٥ - وَقَالَ الْحِيَل لَا نرَاهَا','3','130',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (967,'<span data-type="title" id=toc-1001>مِيرَاث الْمُرْتَد</span>

١٤٩٦ - وَالْمُرْتَدّ لَا يَرِثهُ ورثته لِأَنَّهُ يقتل على الْكفْر وَلَيْسَ اخْتِلَاف أَن الْمُسلم لَا يَرث الْكَافِر
<span data-type="title" id=toc-1002>من تزوج امْرَأَة أَبِيه</span>

١٤٩٧ - قلت الَّذِي تزوج امْرَأَة أَبِيه أَو أمته يُسْتَتَاب
قَالَ لَا هَذَا على الإستحلال يقتل إِذا عرس
<span data-type="title" id=toc-1003>مُدَّة اسْتِتَابَة الْمُرْتَد والزنديق</span>

١٤٩٨ - وَقَالَ الْمُرْتَد يُسْتَتَاب ثَلَاثَة أَيَّام وَيطْعم كل يَوْم رغيفا حَدِيث عمر
١٤٩٩ - وَقَالَ الزنديق يُسْتَتَاب وَالنَّاس فِيهِ مُخْتَلفُونَ يُسْتَتَاب ثَلَاثًا','3','131',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (968,'<span data-type="title" id=toc-1004>إِذا اخْتلف الزَّوْجَانِ فِي الْمهْر</span>

١٥٠٠ - قلت الرجل يتَزَوَّج الْمَرْأَة فَتُدْعَى مهر أَلفَيْنِ وَيَقُول الرجل إِنَّمَا تَزَوَّجتهَا على ألف وَقد دخل بهَا
قَالَ لَهَا صدَاق نسائها فَإِن كَانَ صدَاق نسائها أَكثر من أَلفَيْنِ لم تعط أَكثر من أَلفَيْنِ وَإِن كَانَ أقل من ألف أَعْطَيْت مَا أقرّ بِهِ أَو تقيم الْبَيِّنَة
<span data-type="title" id=toc-1005>فِي المَال حق سوى الزَّكَاة</span>

١٥٠١ - قلت فِي المَال حق سوى الزَّكَاة','3','132',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (969,'قَالَ قد قَالَ ذَلِك ابْن عمر لِقَرَابَتِهِ وَغَيرهم وَالزَّكَاة إِنَّمَا هِيَ حق المَال
<span data-type="title" id=toc-1006>عدَّة المختلعة</span>

١٥٠٢ - وَقَالَ عدَّة المختلعة ثَلَاث حيض وَعُثْمَان يَقُول حَيْضَة
<span data-type="title" id=toc-1007>حكم حَدِيث الْأَحْوَص بن حَكِيم</span>

١٥٠٣ - وَقَالَ الْأَحْوَص بن حَكِيم لَا يرْوى حَدِيثه يرفع الْأَحَادِيث إِلَى النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ أَبُو بكر بن عَيَّاش قيل للأحوص بن حَكِيم مَا هَذِه الْأَحَادِيث الَّتِي تحدث بهَا عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ لَيْسَ الحَدِيث كُله عَن النَّبِي صلى الله عَلَيْهِ وَسلم','3','133',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (970,'<span data-type="title" id=toc-1008>إِذا أعتقت الْأمة فِي الْعدة وَزوجهَا حر</span>

١٥٠٤ - قلت الْأمة تطلق تَطْلِيقَتَيْنِ وَزوجهَا حر ثمَّ تعْتق فِي الْعدة
قَالَ عدتهَا عدَّة الْحرَّة إِذا كَانَ حرا فطلاقه ثَلَاث
<span data-type="title" id=toc-1009>إِذا عتقت الْأمة فِي الْعدة وَزوجهَا عبد</span>

١٥٠٥ - قلت الْأمة تطلق تَطْلِيقَتَيْنِ وَزوجهَا عبد
قَالَ عدتهَا عدَّة الْأمة لِأَنَّهُ لم يبْق من طَلاقهَا شَيْء
<span data-type="title" id=toc-1010>حكم من ترك الصَّوْم لأجل الْمَرَض وَعَلِيهِ صِيَام شَهْرَيْن مُتَتَابعين</span>

١٥٠٦ - قلت الرجل يكون عَلَيْهِ صِيَام شَهْرَيْن مُتَتَابعين ثمَّ يمرض
قَالَ هَذَا مَعْذُور يَبْنِي على صِيَامه','3','134',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (971,'<span data-type="title" id=toc-1011>من وجد المَاء بعد التَّيَمُّم</span>

١٥٠٧ - قلت الرجل يتَيَمَّم ثمَّ يجد المَاء
قَالَ إِذا كَانَ تَوَضَّأ وَأعَاد الصَّلَاة لَا يضرّهُ
<span data-type="title" id=toc-1012>الحكم إِذا أكذب المتلاعن نَفسه</span>
١٥٠٨ - وَقَالَ المتلاعن إِذا أكذب نَفسه يجلد الْجلد وَيلْحق بِهِ الْوَلَد وَلَا يرجع إِلَيْهَا أبدا لِأَنَّهُ حرمهَا على نَفسه وَهَذَا أصح فِي الْمَعْنى','3','135',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (972,'<span data-type="title" id=toc-1013>عدَّة الْمُدبرَة وَأم الْوَلَد</span>

١٥٠٩ - قلت الْمُعتقَة عَن دبر كم تَعْتَد
قَالَ حَيْضَة يرْوى ذَلِك عَن ابْن عمر وَقَالَ من قَالَ عدَّة أم الْوَلَد أَرْبَعَة أشهر وَعشرا فقد جعلهَا حرَّة أفيورثها إِذن إِن كَانَ عدتهَا عدَّة حرَّة
وَقَالَ عدتهَا حَيْضَة فِي الْوَفَاة وَالْعِتْق
<span data-type="title" id=toc-1014>قَول أَحْمد فِي عُثْمَان الْبري</span>

١٥١٠ - وَقَالَ عُثْمَان الْبري حَدِيثه مُنكر وَكَانَ رَأْيه رأى سوء','3','136',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (973,'<span data-type="title" id=toc-1015>من أَحَق بِالصَّلَاةِ على الْمَيِّت وَلَده أَو الْمُوصى إِلَيْهِ</span>

١٥١١ - قلت الرجل يُوصي أَن يُصَلِّي عَلَيْهِ رجل هُوَ أَحَق أَو وَلَده
قَالَ الْمُوصى إِلَيْهِ أَحَق أَبُو بكر صلى عَلَيْهِ عمر وَعمر صلى عَلَيْهِ صُهَيْب وابو بكرَة صلى عَلَيْهِ أَبُو بَرزَة ومسروق صلى عَلَيْهِ شُرَيْح وَيُونُس بن جُبَير صلى عَلَيْهِ أنس بن مَالك','3','137',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (974,'<span data-type="title" id=toc-1016>حكم الظِّهَار قبل النِّكَاح</span>

١٥١٢ - وَقَالَ الرجل يظاهر من قبل أَن يتَزَوَّج أذهب إِلَى حَدِيث عمر حَدِيث الْقَاسِم كَفَّارَة الظِّهَار
١٥١٣ - قلت لَهُ لَا يكون مثل الطَّلَاق
قَالَ هَذَا يَمِين لَا يكون مثل الطَّلَاق
<span data-type="title" id=toc-1017>هَل ظِهَار العَبْد وإيلاؤه مثل ظِهَار الْحر وإيلائه</span>

١٥١٤ - قلت ظِهَار العَبْد مثل ظِهَار الْحر
قَالَ أما ظِهَار العَبْد فَمَا أقل مَا اخْتلف النَّاس فِيهِ مثل ظِهَار الْحر وَلَكِن الْإِيلَاء أَكثر النَّاس يَقُول على النّصْف من إِيلَاء الْحر
وَقَالَ ظَاهر الْآيَة ظِهَاره ظِهَار وَمن قَالَ نصف يَقُول فِي الظِّهَار وَالْإِيلَاء سَوَاء وأذهب إِلَى مثل ظِهَار الْحر','3','128',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (975,'<span data-type="title" id=toc-1018>الْمُطلقَة ثَلَاثًا لَا يحلهَا للزَّوْج الأول إِلَّا النِّكَاح الصَّحِيح</span>

١٥١٥ - قلت الرجل يتَزَوَّج الْمَرْأَة فيطلقها ثَلَاثًا ويتزوجها آخر فِي عدتهَا فَيُفَرق بَينهمَا هَل ترجع إِلَى زَوجهَا الأول الَّذِي طَلقهَا
قَالَ لَا ترجع إِلَى زَوجهَا الأول بِهَذَا النِّكَاح
١٥١٦ - قلت إِن تزوجت عبدا بِغَيْر إِذن مَوْلَاهُ
قَالَ لَا ترجع إِلَى زَوجهَا الأول بِهَذَا النِّكَاح
١٥١٧ - وَقَالَ فِي الرجل يتَزَوَّج على الْمُتْعَة مثل ذَلِك أَيْضا لَا ترجع إِلَى زَوجهَا الأول','3','139',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (976,'١٥١٨ - وَقَالَ فِي الرجل يتَزَوَّج الْمَرْأَة فيحلها لَا ترجع إِلَى زَوجهَا الأول إِنَّمَا ترجع إِلَى زَوجهَا الأول بِالنِّكَاحِ الصَّحِيح
<span data-type="title" id=toc-1019>الْإِيمَان يزِيد وَينْقص</span>

١٥١٩ - وَقَالَ الْإِيمَان يتفاضل بعضه أفضل من بعض يزِيد وَينْقص زِيَادَته فِي الْعَمَل ونقصانه فِي ترك الْعَمَل لِأَن القَوْل هُوَ مقرّ بِهِ
<span data-type="title" id=toc-1020>الحكم فِيمَن أعلن مهْرا وأخفى آخر</span>

١٥٢٠ - قلت الرجل يعلن مهْرا ويخفي آخر
قَالَ إِذا أعلن أَخذ بِمَا يعلن لِأَن الْعَلَانِيَة قد أشهد على نَفسه وَيَنْبَغِي لَهُم أَن يفوا لَهُ بِمَا كَانَ أسر','3','140',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (977,'<span data-type="title" id=toc-1021>وجوب الْمهْر بإرخاء السّتْر وإغلاق الْبَاب</span>

١٥٢١ - وَقَالَ قصَّة أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم بإرخاء السّتْر وإغلاق الْبَاب وَقَالَ زيد بن ثَابت أَرَأَيْت إِن جَاءَت بِولد حِين احْتج عَلَيْهِ مَرْوَان
<span data-type="title" id=toc-1022>إِذا تزوج الْمحرم يفرق بَينهمَا</span>

وَقَالَ الْمحرم إِذا تزوج يفرق بَينهمَا عمر وَزيد بن ثَابت قَالَا','3','141',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (978,'يفرق بَينهمَا حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم الْمحرم لَا ينْكح وَلَا ينْكح
<span data-type="title" id=toc-1023>مِيرَاث ذَوي الْأَرْحَام</span>

١٥٢٣ - قلت يُعْطي ذَوي الْأَرْحَام
قَالَ نعم إِذا لم يكن عصبَة وَلَا موَالِي لحَدِيث ابْنة حَمْزَة أعْطى النَّبِي صلى الله عَلَيْهِ وَسلم ابْنة حَمْزَة النّصْف وَبنت الْمولى النّصْف','3','142',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (979,'وَقَالَ إِبْرَاهِيم النَّخعِيّ ذكر حَدِيث ابْنة حَمْزَة فَأنكرهُ وَقَالَ إِنَّمَا أطعمها رَسُول الله صلى الله عَلَيْهِ وَسلم فَقَالَ أطعمها كَمَا أطعمها رَسُول الله صلى الله عَلَيْهِ وَسلم وَقَالَ الشّعبِيّ يَقُول لَا أَدْرِي حَدِيث ابْنة حَمْزَة بعد الْفَرَائِض','3','143',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (980,'<span data-type="title" id=toc-1024>عبد الله بن شَدَّاد وسماعه من النَّبِي صلى الله عَلَيْهِ وَسلم وَعمر وَعلي</span>

١٥٢٤ - وَقَالَ عبد الله بن شَدَّاد قديم سمع من عمر وَعلي
١٥٢٥ - قلت وَسمع من النَّبِي صلى الله عَلَيْهِ وَسلم
قَالَ لَا
<span data-type="title" id=toc-1025>الموَالِي عصبَة</span>

١٥٢٦ - وَقَالَ الموَالِي عصبَة
<span data-type="title" id=toc-1026>حكم النَّفَقَة للمختلعة</span>

١٥٢٧ - قلت المختلعة لَهَا نَفَقَة
قَالَ نَحن نقُول الْمُطلقَة ثَلَاثًا لَيْسَ لَهَا نَفَقَة فَكيف المختلعة لِأَنَّهَا برت نَفسهَا وَلَكِن إِذا كَانَت حَامِلا كَانَ لَهَا النَّفَقَة','3','144',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (981,'<span data-type="title" id=toc-1027>دِيَة الْمَرْأَة إِذا قتلت فِي الْحرم</span>

١٥٢٨ - وَقَالَ إِذا قتلت الْمَرْأَة فِي الْحرم دِيَة وَثلث عُثْمَان جعل لإمرأة قتلت فِي الْحرم دِيَة وَثلث
ودية الْمَرْأَة على النّصْف من دِيَة الرجل
<span data-type="title" id=toc-1028>كم يردد إِذا أقرّ الرجل بِالزِّنَا وَهل يقبل رُجُوعه</span>

١٥٢٩ - قلت الرجل يقر بِالزِّنَا
قَالَ يردده أَربع مرار
١٥٣٠ - قلت فَإِن رَجَعَ
قَالَ يدْرَأ عَنهُ الْحَد لقَوْل النَّبِي صلى الله عَلَيْهِ وَسلم أَلا تَرَكْتُمُوهُ','3','145',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (982,'وَقَالَ أهل الْمَدِينَة يقتل إِذا أقرّ وماعز ردده النَّبِي صلى الله عَلَيْهِ وَسلم أَرْبعا','3','146',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (983,'<span data-type="title" id=toc-1029>عدد الْإِقْرَار الْمُعْتَبر فِي قطع السَّارِق</span>

١٥٣١ - وَقَالَ لَا يقطع السَّارِق حَتَّى يقر مرَّتَيْنِ
١٥٣٢ - قلت إِلَى أَي شي تذْهب
قَالَ إِلَى قَول عَليّ أَقَامَ عَلَيْهِ الْحَد لما أقرّ مرَّتَيْنِ وَأَصْحَاب أبي حنيفَة يَأْخُذُونَ بِهِ إِذا رَجَعَ بعد الْأَرْبَعَة فِي الزِّنَا يتركونه وَيَقُولُونَ فِي السّرقَة يأخذونه بِالْإِقْرَارِ مرّة وَاحِدَة وَإِن أنكر لم يقبلُوا مِنْهُ','3','147',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (984,'<span data-type="title" id=toc-1030>الْمحرم يعْمل عِنْد الْحَاجة</span>

١٥٣٣ - قلت الْمحرم يحْتَاج فَيعْمل فِي إِحْرَامه
قَالَ نعم ويستقي المَاء وَلَكِن لَا يدْخلهُ فِي صَدره يحملهُ على رَأسه كَذَا قَالَ عَطاء فَإِذا حمل على صَدره افتدى
قَالَ ابْن عَبَّاس لما رد عَلَيْهِ كفر','3','148',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (985,'<span data-type="title" id=toc-1031>من لم يمر بِجمع عَلَيْهِ دم</span>

١٥٣٤ - وَقَالَ إِذا لم يمر بِجمع يهريق دَمًا
<span data-type="title" id=toc-1032>مسَائِل فِي غسل الْمَيِّت وتكفينه</span>

١٥٣٥ - وَقَالَ الْمَيِّت يغسل بِمَاء وَسدر الثَّلَاث غسلات
١٥٣٦ - قلت فَيبقى عَلَيْهِ
قَالَ وَأي شَيْء يكون هُوَ أنقى لَهُ حَدِيث ابْن عَبَّاس إِن رجلا وقصت بِهِ رَاحِلَته فَقَالَ النَّبِي صلى الله عَلَيْهِ وَسلم اغسلوه بِمَاء وَسدر حَدِيث أم عَطِيَّة اغسلنها بِمَاء وَسدر ويوضأ مرّة وَاحِدَة','3','149',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (986,'إِلَّا أَن يخرج مِنْهُ شَيْء فيعاد عَلَيْهِ الْوضُوء وَيغسل إِلَى سبع مرار وَلَا يُزَاد عَلَيْهِ لِأَنَّهُ يسترخي
١٥٣٧ - قلت وينقي مَا فِي الْأَظْفَار من وسخ
قَالَ نعم ويقص أَظْفَاره إِن كَانَت فَاحِشَة وَترد فِي أَكْفَانه','3','150',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (987,'١٥٣٨ - وَقَالَ الْعَانَة تحلق إِذا كَانَ الشّعْر كثيرا دَعَا بمُوسَى
١٥٣٩ - قَالَ ويكفن فِي ثَلَاثَة أَثوَاب يدرج فِيهَا إدراجا
<span data-type="title" id=toc-1033>نِصَاب الذَّهَب فِي الزَّكَاة</span>

١٥٤٠ - وَقَالَ إِذا نقصت يَعْنِي الزَّكَاة من عشْرين دِينَارا نصف دِينَار فَلَا زَكَاة فِيهَا وَالَّذِي سمعنَا لَيْسَ فِي أقل من عشْرين دِينَارا شَيْء','3','151',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (988,'<span data-type="title" id=toc-1034>أَقْوَال أَحْمد فِي رجال</span>

١٥٤١ - عُثْمَان بن الْمُغيرَة هُوَ عُثْمَان الْأَعْشَى وَهُوَ ابْن أبي زرْعَة وَهُوَ الثَّقَفِيّ كوفى مولى ثِقَة لَيْسَ أحد أروى عَنهُ من شريك
١٥٤٢ - عُثْمَان بن أبي سُلَيْمَان ثِقَة روى عَنهُ ابْن جريج
١٥٤٣ - وَقَالَ لَيْسَ أحد أروى عَن مُجَاهِد من مَنْصُور إِلَّا ابْن أبي نجيح فَأَما من الغرباء فَلَيْسَ أحد أروى عَنهُ من مَنْصُور
١٥٤٤ - قلت مَنْصُور وَمَالك بن أنس أَيهمَا أثبت فِي الزُّهْرِيّ
قَالَ مَالك أثبت فِي الزُّهْرِيّ','3','152',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (989,'١٥٤٥ - قلت قوم يَقُولُونَ مَنْصُور أثبت فِي الزُّهْرِيّ
قَالَ أبي وَأي شَيْء روى مَنْصُور عَن الزُّهْرِيّ هَؤُلَاءِ جهال مَنْصُور إِذا نزل إِلَى الْمَشَايِخ اضْطر إِلَى أبي إِسْحَاق وَالْحكم وحبِيب بن أبي ثَابت وَسَلَمَة بن كهيل روى حَدِيث أم سَلمَة فِي الْوتر خَالف فِيهِ وَحَدِيث ابْن أَبْزَى خَالف فِيهِ','3','153',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (990,'١٥٤٦ - سعد بن إِبْرَاهِيم مَالك لم يرو عَنهُ وَهُوَ ثِقَة كَانَ فَاضلا وَكَانَ الزُّهْرِيّ يَقُول سعد سعد ولي الْقَضَاء فَكَانَ الْقَاسِم وَسَالم أَحدهمَا يجلس عَن يَمِينه وَالْآخر عَن شِمَاله ويشاورهما فِي الْقَضَاء
١٥٤٧ - قَالَ أبي سَمِعت يُوسُف بن يَعْقُوب بن أبي سَلمَة الْمَاجشون يَقُول ولدت فِي زمن سُلَيْمَان فعرضت على عمر بن عبد العزيز عيلا','3','154',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (991,'١٥٤٨ - وَقَالَ عبد الله بن جَعْفَر بن عبد الرحمن بن مسور بن مخرمَة كَانَ قَرِيبا فِي السن من إِبْرَاهِيم بن سعد وَحدث عَنهُ إِبْرَاهِيم بن سعد
١٥٤٩ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا ابْن إِدْرِيس عَن أَبِيه قَالَ رَأَيْت محَارب بن دثار وَالْحكم عَن يَمِينه وَحَمَّاد عَن يسَاره وَهُوَ يلْتَفت إِلَى هَذَا مرّة وَإِلَى هَذَا مرّة يَعْنِي يشاورهم فِي الْقَضَاء
١٥٥٠ - وَقَالَ لَيْسَ أحد أروى عَن سعد بن إِبْرَاهِيم من الْعِرَاقِيّين شُعْبَة وسُفْيَان ومسعر رووا عَنهُ
١٥٥١ - إِبْرَاهِيم بن سعد أَحَادِيثه مُسْتَقِيمَة
١٥٥٢ - المخرمى لَيْسَ بحَديثه بَأْس وَإِنَّمَا أنكر عَلَيْهِ أهل الْمَدِينَة لِأَنَّهُ خرج مَعَ حُسَيْن بفخ','3','155',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (992,'١٥٥٣ - وَذكر أبي ابْن أبي ذِئْب قلت كَانَ يرْمى بِالْقدرِ قَالَ مَا علمت كَانَ رجلا صَالحا صَاحب أَمر بِالْمَعْرُوفِ وَكَانَ أَكثر من مَالك
١٥٥٤ - وعبد العزيز بن أبي سَلمَة كَانَ أَكثر فِي اللِّسَان من مَالك كَانَ مَالك قطيع اللِّسَان
١٥٥٥ - سيف بن سُلَيْمَان ثِقَة
١٥٥٦ - مُجَاهِد بن جُبَير مولى عبد الله بن السَّائِب','3','156',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (993,'<span data-type="title" id=toc-1035>حكم بيع الْبُقُول والفجل والباذنجان وَنَحْوهَا قبل الْقطع</span>

١٥٥٧ - وَقَالَ البقل إِذا طلع بيع والفجل والسلجم والجزر يقطع ثمَّ يُبَاع لِأَنَّهُ لَا يدرى مَا فِي الأَرْض والبطيخ والباذنجان أَيْضا كلما بيع مِنْهُ على أَن يجز فَلَا بَأْس مثل الرّطبَة والبقول وكل شَيْء يتَوَارَى فَلَا يُبَاع حَتَّى يخرج
<span data-type="title" id=toc-1036>حكم الْأَذَان للْجنب</span>

١٥٥٨ - وَقَالَ الْجنب لَا يُؤذن قَالَ عَليّ بن أبي طَالب لَا يقْرَأ وَلَا حرفا وَأحب إِلَيّ أَن يُؤذن وَهُوَ طَاهِر','3','157',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (994,'<span data-type="title" id=toc-1037>رِوَايَات وأقوال فِي رجال</span>

١٥٥٩ - حَدثنِي أبي قَالَ حَدثنَا يحيى بن يمَان عَن الْأَعْمَش عَن إِبْرَاهِيم قَالَ كَانَ عبد الله لطيفا فطنا
١٥٦٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن يمَان عَن الْأَعْمَش عَن أبي صَالح قَالَ مَا كنت أَتَمَنَّى من الدُّنْيَا إِلَّا ثَوْبَيْنِ نظيفين أجالس فيهمَا أَبَا هُرَيْرَة
١٥٦١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنِي يحيى بن سعيد الْأمَوِي عَن الْأَعْمَش عَن أبي وَائِل قَالَ قَالَ عبد الله لَا يزَال الْمَسْرُوق يسوء الظَّن حَتَّى يكون أعظم إِثْمًا من السَّارِق','3','158',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (995,'١٥٦٢ - وَقَالَ مُحَمَّد بن زِيَاد الْأَلْهَانِي ثَبت
١٥٦٣ - ابْن أبي الورقاء الَّذِي كَانَ بالموصل لَيْسَ بِهِ بَأْس
١٥٦٤ - وَقَالَ مَاتَ قَتَادَة سنة سبع عشرَة وَمِائَة سنه وَسن الْأَعْمَش سَوَاء مَاتَ الْأَعْمَش سنة ثَمَان وَأَرْبَعين وَولد وَكِيع سنة تسع وَعشْرين
١٥٦٥ - حَنْظَلَة بن ابي سُفْيَان ثِقَة قَالَ وَكِيع حَدثنَا حَنْظَلَة وَكَانَ يروي عَن طَاوس','3','159',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (996,'١٥٦٦ - وَكَانَ حَنْظَلَة السدُوسِي ضَعِيف الحَدِيث يروي عَن أنس بن مَالك أَحَادِيث مَنَاكِير روى أينحني بَعْضنَا لبَعض وَفِي الْقُنُوت وَكَانَ يؤمهم فِي مَسْجِد قبَاء فِي بني سدوس
١٥٦٧ - مَاتَ ابْن لَهِيعَة سنة أَربع وَسبعين وَلَيْث بعده سنة خمس','3','160',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (997,'١٥٦٨ - يحيى بن يمَان كَانَ من أَصْحَاب سُفْيَان
١٥٦٩ - عبد العزيز بن رفيع مكي سكن الْكُوفَة
١٥٧٠ - إِسْمَاعِيل بن أُميَّة مكي وَأَيوب بن مُوسَى أَيْضا وَهُوَ ابْن عَم إِسْمَاعِيل وَهُوَ من بني أُميَّة
<span data-type="title" id=toc-1038>حَدِيث عَائِشَة فِي مَنَاقِب عمر</span>

١٥٧١ - حَدِيث عَائِشَة أَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ إِن كَانَ فِي الْأُمَم محدثون فَإِن يكن فِي أمتِي فعمر بن الْخطاب كَانَ يلهم الشَّيْء من الْحق وَقَوله السكينَة تنطق على لِسَان عمر إِبْرَاهِيم بن سعد','3','161',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (998,'عَن أَبِيه عَن أبي هُرَيْرَة وَابْن عجلَان يَقُول عَن سعد عَن أبي سَلمَة عَن عَائِشَة فَقَالَ هُوَ فِي كِتَابه عَن أَبِيه مُرْسل وَإِنَّمَا حدث بِهِ من حفظه وَهُوَ عَن عَائِشَة
<span data-type="title" id=toc-1039>حكم الْجنب يخرج مِنْهُ شَيْء بعد الْغسْل وَلم يبل قبله</span>

١٥٧٢ - قلت الْجنب يغْتَسل فَيخرج مِنْهُ الشَّيْء وَلم يبل قبل الْغسْل
قَالَ يرْوى عَن ابْن عَبَّاس أَنه قَالَ يتَوَضَّأ وَقَالَ الْحسن يُعِيد','3','162',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (999,'الْغسْل وَرُوِيَ عَن عَليّ إِن لم يكن بَال اغْتسل
<span data-type="title" id=toc-1040>حكم الرَّد على أَصْحَاب الْفُرُوض</span>

١٥٧٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم قَالَ حَدثنَا مطرف عَن الشّعبِيّ أَن عليا وعبد الله قَالَا ذُو السهْم أَحَق مِمَّن لَا سهم لَهُ','3','163',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1000,'<span data-type="title" id=toc-1041>رفع الْيَدَيْنِ من تَمام الصَّلَاة</span>

١٥٧٤ - حَدثنَا صَالح قَالَ حَدثنَا أبي قَالَ حَدثنَا عبد الرحمن بن مهْدي عَن حَمَّاد بن زيد عَن هِشَام عَن مُحَمَّد قَالَ هُوَ من تَمام الصَّلَاة رفع الْيَدَيْنِ
<span data-type="title" id=toc-1042>فضل رفع الْيَدَيْنِ فِي الصَّلَاة</span>

١٥٧٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا السالحيني والأشيب عَن ابْن لَهِيعَة عَن عبد الله بن هُبَيْرَة عَن أبي مُصعب مشرح بن هاعان عَن عقبَة بن عَامر قَالَ إِن الرجل بِكُل إِشَارَة يُشِير بهَا فِي الصَّلَاة عشر حَسَنَات قَالَ السالحيني بِكُل أصْبع','3','164',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1001,'<span data-type="title" id=toc-1043>ذكر بعض من رفع الْيَدَيْنِ من الصَّحَابَة والمحدثين</span>

١٥٧٦ - قَالَ أبي رَأَيْت إِسْمَاعِيل بن أبي علية يرفع يَدَيْهِ وَكَانَ حسن الصَّلَاة ومعتمر بن سُلَيْمَان وعبد الرحمن بن مهْدي وَيحيى بن سعيد كَانُوا يرفعون أَيْديهم فِي الصَّلَاة
١٥٧٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرحمن بن مهْدي عَن حَمَّاد بن سَلمَة عَن الْأَزْرَق بن قيس عَن حطَّان قَالَ صلى بِنَا أَبُو مُوسَى وَكَانَ يرفع يَدَيْهِ','3','165',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1002,'<span data-type="title" id=toc-1044>ذكر بعض من روى تَحْرِيم الفضيخ</span>

١٥٧٨ - وَقَالَ الْخمر يرْوى عَن أبي هُرَيْرَة وَابْن عمر وَابْن عَبَّاس وَمَعْقِل بن يسَار وَأنس كَانَ شرابهم الفضيخ حَيْثُ حرمت الْخمر','3','166',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1003,'<span data-type="title" id=toc-1045>حكم غسل الأبوال</span>

١٥٧٩ - وَقَالَ الأبوال تغسل كلهَا وَقد رخص قوم فِيمَا أكل لَحْمه ووإبراهيم وَعَطَاء وَقَالَ الْحسن وَجَابِر بن زيد الأبوال نجس تَأْوِيل قوم حَدِيث أنس أَن النَّبِي صلى الله عَلَيْهِ وَسلم أَمرهم أَن يشْربُوا من أبوالها والبانها وَهَذَا على الضَّرُورَة لَيْسَ على أَنه مُبَاح','3','167',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1004,'<span data-type="title" id=toc-1046>كم تجْلِس المبتدأة</span>

١٥٨٠ - وَقَالَ أول مَا يبْدَأ الدَّم بِالْمَرْأَةِ تقعد سِتَّة أَيَّام أَو سَبْعَة أَيَّام وَهُوَ أَكثر مَا تجْلِس النِّسَاء على حَدِيثه حمْنَة وَمن قَالَ تجْلِس يَوْمًا فَهَذَا احْتِيَاط إِلَّا أَنه إِذا حَاضَت ثَلَاث حيض فَحَاضَت سِتا أَو سبعا فَهُوَ حيض مُسْتَقِيم ثمَّ تعيد الصَّوْم إِن كَانَت صامته فِي تِلْكَ الْأَيَّام لِأَنَّهُ لَا يجزيها أَن تَصُوم وَهِي حَائِض لِأَنَّهُ قد استقام بهَا حَيْضهَا
<span data-type="title" id=toc-1047>حكم وَصِيَّة الْمَرْأَة حينما يضْربهَا الطلق</span>

١٥٨١ - قلت الْمَرْأَة يضْربهَا الطلق فتوصي أَيكُون من المَال كُله أَو من الثُّلُث
قَالَ من الثُّلُث لِأَنَّهُ يشبه بِالْمرضِ أَلا ترى أَن قوما قَالُوا فِي الْمُرْضع وَالْحَامِل إِنَّهَا لَا تَصُوم شبهوه بِالْمرضِ','3','168',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1005,'<span data-type="title" id=toc-1048>حكم وَصِيَّة الرجل وَهُوَ بَين الصفين</span>

١٥٨٢ - قلت الرجل إِذا كَانَ بَين الصفين يُوصي من المَال كُله أَو من الثُّلُث قَالَ من المَال كُله وَلَا يشبه هَذَا الْمَرْأَة إِذا ضربهَا الطلق لَيْسَ هُنَا مرض إِنَّمَا هُوَ خوف
<span data-type="title" id=toc-1049>أَنْوَاع الْعدة</span>

١٥٨٣ - وَقَالَ عدَّة النِّسَاء على ثَلَاثَة وُجُوه على حَدِيث عمر إِذا لم تدر مَا رفع حَيْضهَا وَهِي مِمَّن تحيض تَعْتَد تِسْعَة أشهر ثمَّ ثَلَاثَة أشهر
والمرضع على حَدِيث عُثْمَان وَعلي تَعْتَد بِالْحيضِ وَقد علمت مَا رفع حَيْضهَا وَالْمَرَض على حَدِيث عبد الله بن مَسْعُود حَدِيث إِبْرَاهِيم عَن','3','169',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1006,'عَلْقَمَة أَنه طلق امْرَأَته تَطْلِيقَة أَو تَطْلِيقَتَيْنِ فمرضت فارتفع حَيْضهَا فعلى ثَلَاثَة أوجه
<span data-type="title" id=toc-1050>حَدِيث من قتل لَهُ قَتِيل فَهُوَ بِخَير النظرين</span>

١٥٨٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد عَن ابْن أبي ذِئْب عَن سعيد بن أبي سعيد المَقْبُري قَالَ سَمِعت أَبَا شُرَيْح الكعبي عَن النَّبِي صلى الله عَلَيْهِ وَسلم قَالَ من قتل لَهُ قَتِيل فَهُوَ بِخَير النظرين إِمَّا أَن يقتل وَإِمَّا أَن يعْفُو وَإِمَّا أَن يَأْخُذ حَدِيث طَوِيل فِيهِ أَنه مُخَيّر إِن شَاءَ قتل وَإِن شَاءَ أَخذ الدِّيَة وَإِن شَاءَ عَفا','3','170',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1007,'<span data-type="title" id=toc-1051>دِيَة المنقلة والمأمومة والجائفة والموضحة</span>

١٥٨٥ - وَفِي المنقلة خمس عشرَة وَفِي المأمومة ثلث الدِّيَة وَفِي الْجَائِفَة ثلث الدِّيَة والموضحة فِي الْوَجْه مثل الْمُوَضّحَة فِي الرَّأْس','3','171',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1008,'<span data-type="title" id=toc-1052>دِيَة الْيَهُودِيّ وَالنَّصْرَانِيّ</span>

١٥٨٦ - وَقَالَ كنت أذهب إِلَى دِيَة الْيَهُودِيّ وَالنَّصْرَانِيّ أَرْبَعَة آلَاف فَأَنا الْيَوْم أذهب إِلَى نصف دِيَة الْمُسلم حَدِيث عَمْرو بن شُعَيْب النّصْف وَحَدِيث عُثْمَان بن عَفَّان الَّذِي يرويهِ الزُّهْرِيّ عَن سَالم عَن أَبِيه أَن رجلا مُسلما قتل رجلا من أهل الذِّمَّة عَامِدًا فغلظ عَلَيْهِ عُثْمَان الدِّيَة وَعمر بن عبد العزيز وَمَالك يَقُولَانِ الدِّيَة على النّصْف من دِيَة الْمُسلم اثْنَا عشر ألفا','3','172',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1009,'<span data-type="title" id=toc-1053>دِيَة الَّذِي يقتل فِي الْحرم</span>

١٥٨٧ - الَّذِي يقتل فِي الْحرم دِيَته دِيَة وَثلث عُثْمَان وَابْن عَبَّاس أَيْضا قَالَا فِي الَّذِي يقتل فِي الْحرم دِيَته عشرُون ألفا لحُرْمَة الشَّهْر أَرْبَعَة آلَاف وللحرم أَرْبَعَة آلَاف
<span data-type="title" id=toc-1054>كم الدِّيَة من الْغنم وَالْخَيْل</span>

١٥٨٨ - قلت كم الدِّيَة من الْغنم
قَالَ ألفا شَاة الدِّيَة من الْخَيل لَيْسَ فِيهِ شَيْء صَحِيح','3','173',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1010,'<span data-type="title" id=toc-1055>دِيَة الْمُوَضّحَة وتفسيرها</span>

١٥٨٩ - فِي الْمُوَضّحَة نصف عشر الدِّيَة والموضحة فِي الرَّأْس والجسد تكون مَا أوضحت الْعظم فَهِيَ مُوضحَة
<span data-type="title" id=toc-1056>حكم إِرْث الْمُطلقَة فِي الْمَرَض</span>

١٥٩٠ - الْمَرْأَة إِذا طَلقهَا زَوجهَا وَهُوَ مَرِيض تَرثه إِذا مَاتَ بعد انْقِضَاء الْعدة
١٥٩١ - قلت فَإِن تزوجت فِي مَرضه وَقد انْقَضتْ عدتهَا
قَالَ لَا
١٥٩٢ - قلت لم هُوَ وَاجِب لَهَا
قَالَ إِنَّمَا هَذَا اتِّبَاع يرْوى عَن أبي بن كَعْب تَرثه مالم تزوج ويروى عَن عَطاء تَرثه مالم تزوج','3','174',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1011,'١٥٩٣ - قلت وَلم لَا تَرثه وَقد وَجب لَهَا الصَدَاق
قَالَ أبي فَمَا تَقول إِن طَلقهَا فِي مَرضه ثمَّ صَحَّ تَرثه
قلت لَا قَالَ فَكَذَلِك لَا تَرثه إِنَّمَا هُوَ اتِّبَاع
١٥٩٤ - قلت وَقَول أهل الْمَدِينَة إِنَّهَا تَرثه وَلَو تزوجت
فَقَالَ لَا أذهب إِلَيْهِ
<span data-type="title" id=toc-1057>هَل يَرث الْوَلَد وَالِده إِذْ نَفَاهُ فِي الْمَرَض</span>

١٥٩٥ - قلت الرجل يَنْفِي وَلَده وَهُوَ مَرِيض يَرِثهُ
قَالَ مالم يُلَاعن يَرِثهُ قد يُنكره ثمَّ يقربهُ بعد فَإِذا كَانَ فرَاش فَهُوَ يَرِثهُ مالم يُلَاعن وَقَالَ إِنَّمَا هَذَا حق الْوَلَد فَلَا يبرأ مِنْهُ إِلَّا بِاللّعانِ
وَإِن لم يكن لَهُ أم يَرِثهُ إِذا أقرّ بِالْوَطْءِ وَله فرَاش وَإِن كَانَت أمه فقد قضى النَّبِي صلى الله عَلَيْهِ وَسلم بالفراش لأمه','3','175',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1012,'<span data-type="title" id=toc-1058>حكم التَّفْرِيق بَين المتلاعنين</span>

١٥٩٦ - وَقَالَ فِي اللّعان فرق رَسُول الله صلى الله عَلَيْهِ وَسلم بَين أخوي بني العجلان
وَقَالَ بعض النَّاس تَطْلِيقَة بَائِنَة قَالَ سهل بن سعد شهِدت المتلاعنين على عهد رَسُول الله صلى الله عَلَيْهِ وَسلم فَفرق بَينهمَا وَقَالَ لَا يَجْتَمِعَانِ يرْوى عَن سعيد بن الْمسيب قَالَ اللّعان تَطْلِيقَة بَائِنَة
<span data-type="title" id=toc-1059>حكم الصَّلَاة على السقط</span>

١٥٩٧ - وَقَالَ السقط يصلى عَلَيْهِ إِذا تمّ خلقه سعيد بن الْمسيب قَالَ قَالَ أَبُو بكر أَحَق من صلينَا عَلَيْهِ أطفالنا وَالصَّلَاة لَا تضر والمغيرة بن','3','176',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1013,'شُعْبَة يرفعهُ إِلَى النَّبِي صلى الله عَلَيْهِ وَسلم يصلى عَلَيْهِ
إِذا تمّ خلقه عتقت بِهِ الْأمة يَعْنِي أم الْوَلَد إِذا تبين يَد أَو رجل وَانْقَضَت بِهِ الْعدة وَيصلى عَلَيْهِ وَيغسل كَانَ مُحَمَّد بن سِيرِين يرى أَن يُسَمِّيه
<span data-type="title" id=toc-1060>حكم الْجمع بَين الصَّلَاتَيْنِ لأجل الْمَرَض</span>

١٥٩٨ - وَقَالَ الْمَرِيض يجمع بَين الصَّلَاتَيْنِ كَانَ عَطاء يرخص لَهُ أَن يجمع','3','177',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1014,'<span data-type="title" id=toc-1061>حكم من اخْتَارَتْ نَفسهَا فِي حَال مرض الزَّوْج</span>

١٥٩٩ - قلت الرجل يُخَيّر امْرَأَته فِي مَرضه فتختار نَفسهَا
قَالَ أذهب إِلَى الْخِيَار أَنَّهَا وَاحِدَة يملك الرّجْعَة
<span data-type="title" id=toc-1062>حكم إِرْث المختلعة</span>

١٦٠٠ - وَفِي الْخلْع لَا تَرثه لِأَنَّهَا برت نَفسهَا مِنْهُ وَقَالَ قوم الْخلْع لَيْسَ بِطَلَاق
وَقد قَالَ قوم إِنَّه طَلَاق','3','178',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1015,'<span data-type="title" id=toc-1063>خطأ معمر فِي حَدِيث غيلَان</span>

١٦٠١ - حَدِيث غيلَان أَنه أسلم وَله عشر نسْوَة فَقَالَ لَهُ النَّبِي صلى الله عَلَيْهِ وَسلم اختر مِنْهُنَّ أَرْبعا معمر أَخطَأ فِيهِ بِالْبَصْرَةِ فِي هَذَا الْإِسْنَاد وَرجع بِالْيمن جعله مُنْقَطِعًا','3','179',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1016,'<span data-type="title" id=toc-1064>حكم شَهَادَة من يشرب الْمُسكر متأولا وَالصَّلَاة خَلفه وَحده</span>

١٦٠٢ - الَّذِي يشرب الْمُسكر متأولا أقبل شَهَادَته وأصلي خَلفه وأجلده ثَمَانِينَ
<span data-type="title" id=toc-1065>نَفَقَة الْحَامِل إِذا مَاتَ عَنْهَا زَوجهَا أَو طَلقهَا</span>

١٦٠٣ - قلت الْحَامِل يَمُوت عَنْهَا زَوجهَا ويطلقها من أَيْن تنْفق
قَالَ إِن صَحَّ الْخَبَر وَقَامَت الْبَيِّنَة من نصِيبهَا وَهُوَ أصح فِي الْمَعْنى','3','180',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1017,'وَقد ترددت الْمَوَارِيث وَإِذا لم يَصح الْخَبَر وَلم تقم الْبَيِّنَة من جَمِيع المَال لِأَنَّهَا حبست نَفسهَا عَلَيْهِ
<span data-type="title" id=toc-1066>من تَرْجَمَة مَنْصُور بن حَيَّان وَأَبِيهِ وَيزِيد بن هَارُون</span>

١٦٠٤ - مَنْصُور بن حَيَّان الْأَسدي حَيَّان أَبُو الْهياج
١٦٠٥ - روى يزِيد بن هَارُون عَن مَنْصُور أَحَادِيث لم يسمع من أَبِيه وَسمع من الشّعبِيّ يزِيد بن هَارُون من سمع مِنْهُ بواسط هُوَ أصح مِمَّن سمع بِبَغْدَاد لِأَنَّهُ كَانَ بواسط يلقن فَيرجع إِلَى مَا فِي الْكتب
<span data-type="title" id=toc-1067>شرح ابْن مَسْعُود طَلَاق السّنة</span>

١٦٠٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو مُعَاوِيَة عَن الْأَعْمَش عَن مَالك بن الْحَارِث عَن عبد الرحمن بن زيد عَن عبد الله بن','3','181',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1018,'مَسْعُود قَالَ طَلقهَا طَاهِرا فِي غير جماع
وَذكر حَدِيث أبي الْأَحْوَص فِي طَلَاق السّنة فَقَالَ ذَلِك يخْتَلف فِيهِ
<span data-type="title" id=toc-1068>حكم من رمى أَو نفر قبل الزَّوَال</span>

١٦٠٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرزاق قَالَ سَمِعت عبيد الله يحدث عَن هِشَام بن حسان عَن نَافِع عَن ابْن عمر قَالَ إِذا رمى الرجل قبل الزَّوَال أعَاد الرَّمْي وَإِذا نفر قبل الزَّوَال أهراق دَمًا أذهب إِلَيْهِ','3','182',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1019,'<span data-type="title" id=toc-1069>حكم الْوضُوء مِمَّا غيرت النَّار</span>

١٦٠٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنِي يحيى بن سعيد عَن سعيد عَن قَتَادَة عَن أنس أَن أَبَا طَلْحَة كَانَ يتَوَضَّأ مِمَّا غيرت النَّار
١٦٠٩ - قيل لَهُ وضوء الصَّلَاة
قَالَ نعم أَلا ترى أَن أنسا أنكر على الْحجَّاج كَيفَ لم يتَوَضَّأ
<span data-type="title" id=toc-1070>تَأْوِيل مَالك لحَدِيث ابْن عمر فِي الْوضُوء من الرعاف</span>

١٦١٠ - مَالك يتَأَوَّل حَدِيث ابْن عمر يغسل الدَّم إِذا رعف يُرِيد ينْصَرف فيتوضأ','3','183',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1020,'وَقَالَ مَالك لَا يرى الْوضُوء إِلَّا مِمَّا خرج من السَّبِيلَيْنِ
ويروي حَدِيث سعيد بن الْمسيب أَنه رعف فَذهب فَتَوَضَّأ يتَأَوَّل هَذَا أَيْضا
<span data-type="title" id=toc-1071>رِوَايَة فِي مَنَاقِب أبي بكر وَعمر وَعُثْمَان</span>

١٦١١ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرزاق عَن معمر عَن أبي حَازِم عَن سهل بن سعد قَالَ كَانَ النَّبِي صلى الله عَلَيْهِ وَسلم على أحد وأبوبكر وَعمر وَعُثْمَان فارتج فَقَالَ اثْبتْ فَمَا عَلَيْك إِلَّا نَبِي وصديق وشهيدان','3','184',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1021,'<span data-type="title" id=toc-1072>رِوَايَة عَن عُثْمَان فِيمَا لَا شُفْعَة فِيهِ</span>

١٦١٢ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا ابْن إِدْرِيس عَن مُحَمَّد بن عمَارَة عَن أبي بكر بن حزم عَن أبان بن عُثْمَان عَن عُثْمَان قَالَ لَا شُفْعَة فِي بِئْر وَلَا فَحل والأرف إِذا علم كل قوم حَقهم تقطع كل شُفْعَة','3','185',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1022,'١٦١٣ - قلت لَهُ أحد يَقُول ولاارف غير ابْن إِدْرِيس
فَقَالَ يَكْفِيك بِابْن إِدْرِيس
<span data-type="title" id=toc-1073>حكم من قَالَ لزوجته أَنْت طَالِق ثَلَاثًا للسّنة</span>

١٦١٤ - وَقَالَ الرجل يَقُول لإمرأته وَلم يدْخل بهَا أَنْت طَالِق ثَلَاثًا للسّنة قَالَ يَقع عَلَيْهَا وَإِن قَالَ لَهَا وَقد دخل بهَا يَقع عَلَيْهَا
١٦١٥ - قلت لَهُ مَا الَّذِي يتَأَوَّل إِنَّمَا يَقع عَلَيْهَا وَاحِدَة الَّتِي غير مَدْخُول بهَا
قَالَ لَا يُعجبنِي هَذَا القَوْل وَهُوَ عِنْدِي يَقع عَلَيْهَا
<span data-type="title" id=toc-1074>الْأَحْدَاث الَّتِي يسْتَقْبل بهَا الصَّلَاة</span>

١٦١٦ - قَالَ إِذا أحدث وَهُوَ فِي الصَّلَاة يسْتَقْبل الصَّلَاة وَيسْتَقْبل الْقَوْم إِذا لم يكن فِي صلب الصَّلَاة الرعاف أَيْضا يسْتَقْبل','3','186',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1023,'١٦١٧ - قلت فالدم لَيْسَ هُوَ أسهل
قَالَ بلَى وَلَكِن أرى أَن يستقبلوا
<span data-type="title" id=toc-1075>ذكر من اسْتخْلف من الصَّحَابَة عِنْد الْحَدث</span>

١٦١٨ - قلت فَمن اسْتخْلف
قَالَ عَليّ قدم إِسْمَاعِيل بن سميع عَن أبي رزين عَن عَليّ
ويروى من حَدِيث حُصَيْن عَن عَمْرو بن مَيْمُون أَن عمر قدم عبد الرحمن بن عَوْف ويروى اخْتِلَاف أَن عبد الرحمن تقدم','3','187',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1024,'<span data-type="title" id=toc-1076>الْمغمى عَلَيْهِ يقْضِي الصَّلَاة وَالصَّوْم</span>

١٦١٩ - قلت الْمغمى عَلَيْهِ
قَالَ يقْضِي الصَّلَاة وَيُعِيد الصّيام إِلَّا الْيَوْم الَّذِي أُغمي عَلَيْهِ فِيهِ لِأَنَّهُ كَانَ قد عزم من اللَّيْل وَقَالَت حَفْصَة وَابْن عمر لَا صِيَام لمن لم يجمع الصّيام من اللَّيْل
<span data-type="title" id=toc-1077>من نفى الْوَلَد وَهُوَ مَرِيض</span>

١٦٢٠ - الَّذِي يَنْفِي وَلَده وَهُوَ مَرِيض يلْزمه الْوَلَد حَتَّى يُلَاعن الْوَلَد للْفراش مالم يُلَاعن
<span data-type="title" id=toc-1078>قَول أَحْمد فِي عَاصِم بن مُحَمَّد ومسكين بن بكير</span>

١٦٢١ - عَاصِم بن مُحَمَّد الْمدنِي ضَعِيف والكوفي ثِقَة','3','188',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1025,'١٦٢٢ - وَقَالَ مِسْكين بن بكير كَانَ هَاهُنَا سمع من شُعْبَة
<span data-type="title" id=toc-1079>إِذا أوصى لرجل بِثلث وَللْآخر بِمَالِه كُله</span>

١٦٢٣ - قلت لرجل يُوصي بِثلث مَاله وَلآخر بِمَالِه
قَالَ من أَرْبَعَة لهَذَا ثَلَاثَة وَلِهَذَا وَاحِد
<span data-type="title" id=toc-1080>حكم الرُّجُوع فِي الأَرْض المعارة للزَّرْع</span>

١٦٢٤ - وَقَالَ الرجل يعير الرجل الأَرْض يَزْرَعهَا لَيْسَ لَهُ أَن يرجع حَتَّى يدْرك الزَّرْع','3','189',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1026,'<span data-type="title" id=toc-1081>منحة الْوَرق وَاللَّبن</span>

١٦٢٥ - قلت منحة لبن أَو منحة ورق
قَالَ منحة ورق هُوَ الْقَرْض ومنحة لبن هُوَ الْعَارِية فَكَذَا هُوَ
<span data-type="title" id=toc-1082>حكم بيع العَبْد قبل الْقَبْض</span>

١٦٢٦ - وَقَالَ فِي الرجل إِذا اشْترى عبدا وَأَرَادَ بَيْعه قبل أَن يقبضهُ أذهب فِيهِ إِلَى قَول عُثْمَان','3','190',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1027,'١٦٢٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا الْوَلِيد بن مُسلم عَن الْأَوْزَاعِيّ عَن الزُّهْرِيّ عَن حَمْزَة بن عبد الله بن عمر عَن أَبِيه قَالَ مَا أدْركْت الصَّفْقَة حَيا مجموعا فَهُوَ من مَال المُشْتَرِي
<span data-type="title" id=toc-1083>حكم لبن الْفَحْل</span>

١٦٢٨ - أذهب فِي الرَّضَاع إِلَى حَدِيث عَائِشَة قصَّة أبي قعيس','3','191',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1028,'<span data-type="title" id=toc-1084>الحكم إِذا قتل الْمحرم الصَّيْد مُنْفَردا أَو بالإشتراك</span>

١٦٢٩ - إِذا قتل الْمحرم الصَّيْد عَلَيْهِ جَزَاء وَإِذا اشْتَركُوا عَلَيْهِم جَزَاء وَاحِد
<span data-type="title" id=toc-1085>حكم الْقرعَة</span>

١٦٣٠ - أذهب إِلَى الْقرعَة لِأَن النَّبِي صلى الله عَلَيْهِ وَسلم أَقرع
<span data-type="title" id=toc-1086>حكم من نسي التَّسْلِيم</span>

١٦٣١ - وَقَالَ إِذا نسي التَّسْلِيم إِن تكلم أعَاد الصَّلَاة وسجدتي السَّهْو يسْجد مَا كَانَ بِالْقربِ فِي الْمَسْجِد
<span data-type="title" id=toc-1087>تَعْرِيف الْمَفْقُود</span>

١٦٣٢ - وَقَالَ الْمَفْقُود إِذا ركب الْبَحْر وَإِذا لَقِي الْعَدو وَإِذا خرج للصَّلَاة فَأَما إِذا كَانَ بِالْبَصْرَةِ ثمَّ خرج إِلَى سواهَا فَلَا','3','192',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1029,'<span data-type="title" id=toc-1088>رِوَايَة فِي الْقَضَاء بِالْيَمِينِ مَعَ الشَّاهِد</span>

١٦٣٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الله بن الْحَارِث من أهل مَكَّة مَا كَانَ بِهِ بَأْس عَن سيف بن سُلَيْمَان وَقَالَ بَعضهم إِبْنِ أبي سُلَيْمَان عَن قيس بن سعد عَن عَمْرو بن دِينَار عَن ابْن عَبَّاس قَالَ قضى رَسُول الله صلى الله عَلَيْهِ وَسلم بِالْيَمِينِ مَعَ الشَّاهِد قَالَ عَمْرو فِي الْأَمْوَال
<span data-type="title" id=toc-1089>قَول أَحْمد فِي رجال</span>

١٦٣٤ - قَالَ أبي عَمْرو بن دِينَار من أهل مَكَّة مولى ابْن باذان وعبد الله بن دِينَار من أهل الْمَدِينَة مولى ابْن عمر
١٦٣٥ - قَالَ أبي سعد بن سعيد أَخُو يحيى بن سعيد ضَعِيف','3','193',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1030,'<span data-type="title" id=toc-1090>دَرَجَة حَدِيث نهى عَن صَلَاتَيْنِ</span>

١٦٣٦ - حَدِيث عَائِشَة أَن النَّبِي صلى الله عَلَيْهِ وَسلم نهى عَن صَلَاتَيْنِ كذب لَيْسَ بِشَيْء
<span data-type="title" id=toc-1091>تَأْوِيل حَدِيث أَن أنسا صلى بهم رَكْعَتَيْنِ ثمَّ رَكْعَتَيْنِ</span>

١٦٣٧ - حَدِيث أنس بن سِيرِين أَن أنسا صلى بهم رَكْعَتَيْنِ ثمَّ رَكْعَتَيْنِ
فَقَالَ هُوَ عِنْدِي التَّطَوُّع
<span data-type="title" id=toc-1092>دِيَة من قَتله قوم خطأ</span>

١٦٣٨ - وَقَالَ فِي الْقَوْم يقتلُون خطأ قَالَ دِيَة وَاحِدَة وَكَفَّارَة على كل وَاحِد مِنْهُم وَلَوْلَا حُرْمَة الْقَتْل لَكَانَ الْقيَاس على الصَّيْد وَلَكِن حُرْمَة الْقَتْل','3','194',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1031,'<span data-type="title" id=toc-1093>شرح الْعرية</span>

١٦٣٩ - قلت نَاس يَقُولُونَ إِن الْعرية تكون للرجل نخل يَسْتَثْنِي مِنْهُ ثَلَاث نخلات أَو أَربع نخلات
قَالَ لَا لَيْسَ هَذَا وَجه الْعرية مَالك يَقُول هُوَ الرجل يكون لَهُ الْحَائِط وللرجل الآخر فِيهِ نَخْلَة أَو نخلتان فيشتريه صَاحب الْحَائِط
وَهَذَا أَيْضا لَيْسَ وَجه الْعرية والعرية أَن يعري الرجل فِيمَا دون خَمْسَة أوسق فيبيعه يخرضها بِالتَّمْرِ عَن سُفْيَان بن عُيَيْنَة وسُفْيَان بن حُسَيْن هَذَا وَجه الْعرية','3','195',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1032,'<span data-type="title" id=toc-1094>إِذا جَاءَت الْأمة بِولد لأَقل من سِتَّة أشهر وَقد وَطئهَا</span>

١٦٤٠ - الرجل يَشْتَرِي الْجَارِيَة فتجئ بِولد لأَقل من سِتَّة أشهر وَقد وَطئهَا
قَالَ لَا يلْحق بِهِ الْوَلَد وَلَا يَبِيعهُ يعتقهُ لِأَنَّهُ قد شركه فِي المَاء لِأَن المَاء يزِيد فِي الْوَلَد حَدِيث النَّبِي صلى الله عَلَيْهِ وَسلم نهى أَن تُوطأ الحبالي حَتَّى يَضعن حَدِيث أبي الدَّرْدَاء مر على بَاب','3','196',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1033,'فسطاط فَإِذا بِامْرَأَة مجح فَقَالَ أيلم بهَا فَقَالَ لقد هَمَمْت أَن ألعنه لعنة تدخل مَعَه فِي قَبره كَيفَ يورثه وَهُوَ كَذَا
<span data-type="title" id=toc-1095>إِذا مَاتَ الْمَكْفُول بِهِ فِي الْكفَالَة النَّفس</span>

١٦٤١ - أرى الْكفَالَة بِالنَّفسِ فَإِذا مَاتَ فَلَا شَيْء لَهُ
<span data-type="title" id=toc-1096>نقص الْوضُوء من ريح خَارج من الْفرج</span>

١٦٤٢ - وَسَأَلته عَن امْرَأَة يخرج من فرجهَا الرّيح
فَقَالَ مَا خرج من السَّبِيلَيْنِ فَفِيهِ الْوضُوء','3','197',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1034,'<span data-type="title" id=toc-1097>الْحَائِض تحل شعرهَا عِنْد الْغسْل</span>

١٦٤٣ - قلت الْحَائِض إِذا كَانَت ممتشطة وأرادت الطُّهْر تحل شعرهَا
قَالَ نعم
<span data-type="title" id=toc-1098>حكم الْقصر للأسير</span>

١٦٤٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا إِسْحَاق بن عِيسَى الطباع عَن عبد الله بن وهب عَن يُونُس عَن الزُّهْرِيّ وَابْن وهب عَن لَيْث بن سعد عَن يحيى بن سعيد قَالَا الْأَسير يتم الصَّلَاة
قَالَ إِذا كَانَ قد منع وَطَالَ أمره يتم الصَّلَاة','3','198',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1035,'<span data-type="title" id=toc-1099>أَقْوَال أَحْمد فِي رجال</span>

١٦٤٥ - سماك بن حَرْب أصلح حَدِيثا من عبد الملك بن عُمَيْر وَذَاكَ أَن عبد الملك يخْتَلف عَلَيْهِ الْحفاظ
١٦٤٦ - مُوسَى بن عُبَيْدَة وَأَخُوهُ لَا يشْتَغل بهما وَذَلِكَ أَنه يروي عَن عبد الله بن دِينَار شَيْئا لَا يرويهِ النَّاس
١٦٤٧ - وعبد الله بن دِينَار ثِقَة مُسْتَقِيم الحَدِيث','3','199',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1036,'١٦٤٨ - مُحَمَّد بن عجلَان ثِقَة
١٦٤٩ - سَمِعت أبي يَقُول قَالَ الْقَاسِم بن مخيمرة لأم ولد لَهُ مَا بالي كنت أَتَمَنَّى الْمَوْت حَتَّى إِذا جَاءَنِي كرهته
١٦٥٠ - قَالَ أبي بلغ عَطاء أَن يُوسُف بن مَاهك يتَمَنَّى الْمَوْت فكره ذَلِك لَهُ وعابه
١٦٥١ - قَالَ أبي جَاءَ عَليّ بن حُسَيْن وَأَبُو بكر بن عبد الرحمن بن الْحَارِث بن هِشَام إِلَى بَاب طَارق وَال كَانَ بِالْمَدِينَةِ فوقفا بِالْبَابِ فسمعاه','3','200',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1037,'يَقُول وَالله لَأَضرِبَن الَّذِي فِيهِ عينا سعيد بن الْمسيب فَأتيَاهُ فَقَالَ عَليّ بن حُسَيْن إِنَّا مَرَرْنَا بِبَاب طَارق فسمعناه يَقُول كَذَا وَكَذَا قَالَ فتقولا مَاذَا قَالَا تخرج من الْمَدِينَة فَقَالَ أَمن مَدِينَة رَسُول الله صلى الله عَلَيْهِ وَسلم طَائِعا غير مكره قَالَا فتختفي بِالْبَيْتِ قَالَ والمنادى يُنَادي حَيّ على الصَّلَاة حَيّ على الْفَلاح قَالَا فتتنحى عَن الكوة الَّتِي يُصَلِّي إِلَى جنبها فَقَالَ وَالله لَا أحدث لما جئتماني لَهُ شَيْئا
قَالَ أبي صحت نِيَّته فَسلم رأى وَمَا قبل مِنْهُمَا
<span data-type="title" id=toc-1100>قَول فِي تمني يُوسُف عَلَيْهِ السَّلَام الْمَوْت</span>

١٦٥٢ - قَالَ أبي وَيُقَال إِنَّه مَا تمنى أحد من الْأَنْبِيَاء الْمَوْت إِلَّا يُوسُف فَإِنَّهُ قَالَ {توفني مُسلما وألحقني بالصالحين}','3','201',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1038,'<span data-type="title" id=toc-1101>الْفجْر الَّذِي يحرم الطَّعَام وَالشرَاب للصَّائِم</span>

١٦٥٣ - سَأَلت أبي عَن الْفجْر الَّذِي يحرم الطَّعَام وَالشرَاب
فَقَالَ هما فجران الْفجْر المستطيل وَالْفَجْر الْمُعْتَرض فَالَّذِي يحرم الطَّعَام وَالشرَاب الْفجْر الْمُعْتَرض
<span data-type="title" id=toc-1102>صَوْم يَوْم الشَّك</span>

١٦٥٤ - وَسَأَلته عَن يَوْم الشَّك فَقَالَ الشَّك على جِهَتَيْنِ يَوْم غيم فَهُوَ الَّذِي يصبح النَّاس فِيهِ صياما وَيَوْم صحو لَا يرى فَذَلِك يصبح النَّاس مفطرين كَانَ ابْن عمر إِذا حَال دون منظره شَيْء أصبح صَائِما','3','202',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1039,'وَبعث الحكم بن أَيُّوب وَكَانَ على الْبَصْرَة إِلَى أنس أَنِّي صَائِم فصَام أنس وَقَالَ هَذَا يكمل لي أحدا وَثَلَاثِينَ وَقد صَامت أَسمَاء وَعَائِشَة وَمُعَاوِيَة وَجَمَاعَة من التَّابِعين يَوْم الشَّك','3','203',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1040,'<span data-type="title" id=toc-1103>حكم الْمسْح على الْخُف الْخرق</span>

١٦٥٥ - قلت الْخُف إِذا كَانَ مخرقا يمسح عَلَيْهِ','3','204',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1041,'قَالَ إِذا بدا من الْقدَم فَلَا يمسح إِلَّا أَن يكون عَلَيْهِ جورب أَو يكون خرق يَنْضَم على الْقدَم
<span data-type="title" id=toc-1104>إِذا مر إِنْسَان بِموضع فَسَقَطت عَلَيْهِ قَطْرَة أَو قطرتان</span>

١٦٥٦ - وَسَأَلته يمر الرجل بالموضع فيقطر عَلَيْهِ قَطْرَة أَو قطرتان
قَالَ إِن كَانَ من مخرج غسله وَإِن لم يكن من مخرج فَلَا يسْأَل عَنهُ
<span data-type="title" id=toc-1105>من ترك الْمَضْمَضَة والإستنشاق فِي الْوضُوء</span>

١٦٥٧ - وَقَالَ إِذا ترك الْمَضْمَضَة والإستنشاق يُعِيد الْمَضْمَضَة والإستنشاق وَيُعِيد الصَّلَاة','3','205',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1042,'<span data-type="title" id=toc-1106>الرَّد على من قَالَ إِن الْيَهُود وَالنَّصَارَى من أمة مُحَمَّد</span>

١٦٥٨ - قلت أحد يَقُول الْيَهُودِيّ وَالنَّصْرَانِيّ من أمة مُحَمَّد صلى الله عَلَيْهِ وَسلم
فَقَالَ سُبْحَانَ الله النَّبِي صلى الله عَلَيْهِ وَسلم يَقُول اخْتَبَأْت شَفَاعَتِي لأمتي أيشفع إِذا لِلْيَهُودِيِّ وَالنَّصْرَانِيّ أحد يَقُول هَذَا
<span data-type="title" id=toc-1107>الحكم إِذا نكل الْمُدعى عَلَيْهِ عَن الْيَمين</span>

١٦٥٩ - قلت إِذا نكل عَن الْيَمين وَجب عَلَيْهِ الحكم
قَالَ قدم ابْن عمر إِلَى عُثْمَان فِي عبد فَقَالَ لَهُ عُثْمَان أَحْلف أَنَّك مَا بِعته يَوْم بِعته وَبِه عيب عَلمته فَأبى ابْن عمر أَن يحلف فَرد عَلَيْهِ عُثْمَان العَبْد','3','206',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1043,'<span data-type="title" id=toc-1108>ثَنَاء أَحْمد على ابْن عُيَيْنَة</span>

١٦٦٠ - وَقَالَ مَا رَأَيْت أحدا أعلم بالسنن من سُفْيَان بن عُيَيْنَة
آخر ٠ الْخَامِس عشر من أَجزَاء الشَّيْخ صَالح

<span data-type="title" id=toc-1110>حكم نقض الْوضُوء من الضحك فِي الصَّلَاة</span>

١٦٦١ - حَدثنَا صَالح بن أَحْمد قَالَ سَمِعت أبي يَقُول من ضحك فِي الصَّلَاة لَا وضوء عَلَيْهِ وَإِن تَوَضَّأ لم يضرّهُ حَدِيث أبي الْعَالِيَة مُرْسل
<span data-type="title" id=toc-1111>حكم نقض الْوضُوء من خلع الْعِمَامَة وقص الظفر وَالشعر</span>

١٦٦٢ - من مسح على الْعِمَامَة ثمَّ خلعها يُعِيد الْوضُوء
١٦٦٣ - من تَوَضَّأ ثمَّ قصّ أَظْفَاره أَو شعره لَا وضوء عَلَيْهِ وَلَا يمسهُ المَاء
<span data-type="title" id=toc-1112>حكم التَّرْتِيب فِي الْوضُوء وَالْغسْل</span>

١٦٦٤ - الْغسْل من الْجَنَابَة غير الْوضُوء قَالَ إِن بَدَأَ بِبَعْض أَعْضَائِهِ قبل بعض أَجزَأَهُ لِأَن الله قَالَ {وَإِن كُنْتُم جنبا فاطهروا} فَإِنَّمَا عَلَيْهِ أَن','3','207',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1044,'يتَطَهَّر وَالْوُضُوء لَيْسَ كَذَلِك فَرْضه الله فِي الْقُرْآن تأليف شَيْء بعد شَيْء
<span data-type="title" id=toc-1113>حكم الْغسْل من التقاء الختانين</span>

١٦٦٥ - وَقَالَ إِذا التقى الختانان وَلم ينزل اغتسلا
<span data-type="title" id=toc-1114>الْقَيْح والصديد أَهْون من الدَّم</span>

١٦٦٦ - الْقَيْح والصديد يخرج من الْجرْح إنَّهُمَا أَهْون من الدَّم
<span data-type="title" id=toc-1115>حكم مس الْمُصحف لغير الطَّاهِر</span>

١٦٦٧ - لَا يمس الْمُصحف إِلَّا طَاهِر وَاحْتج بِحَدِيث سعد وَإِذا أَرَادَ أَن يقْرَأ فِي الْمُصحف على غير طَهَارَة لم يمسهُ ويصفحه بِعُود أَو بِشَيْء','3','208',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1045,'<span data-type="title" id=toc-1116>الحكم إِذا أَدخل الْجنب فَمه فِي المَاء</span>

١٦٦٨ - الْجنب يدْخل فَمه فِي المَاء فَيغسل بِالْمَاءِ الَّذِي بفمه يَده
قَالَ فَمه وَيَده سَوَاء
<span data-type="title" id=toc-1117>الثَّوْب يُصِيبهُ الدَّم أَو الْمَذْي أَو الْمَنِيّ أَو الْبَوْل</span>

١٦٦٩ - الدَّم فِي الثَّوْب أسهل من الْبَوْل والمني والمذي وَالدَّم أسهل من الْبَوْل وَالْغَائِط والمني والمذي إِذا فحش فِي الثَّوْب أعَاد','3','209',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1046,'<span data-type="title" id=toc-1118>حكم الْوضُوء من الْقبْلَة بِشَهْوَة</span>

١٦٧٠ - سَمِعت أبي يَقُول إِذا قبل لشَهْوَة فَأحب أَن يتَوَضَّأ
<span data-type="title" id=toc-1119>حكم التكني بِأبي الْقَاسِم</span>
يفعل
١٦٧١ - قلت رجل يكتني بِأبي الْقَاسِم
قَالَ قد رخص بعض النَّاس فِي ذَلِك وَالَّذِي يعجبنا أَن لَا يفعل وروى بعض النَّاس لَا تجمعُوا بَين اسْمِي وكنيتي','3','210',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1047,'<span data-type="title" id=toc-1120>حكم الصَّلَاة خلف من يجْهر أَو يقنت</span>

١٦٧٢ - قلت الصَّلَاة خلف من يجْهر أَو يقنت
فَقَالَ نَحن نجهر وَلَا نقنت فَإِن جهر رجل وَلَيْسَ بِصَاحِب بِدعَة يتبع مَا روى عَن ابْن عمر وَابْن عَبَّاس فَلَا بَأْس بِالصَّلَاةِ خَلفه والقنوت هَكَذَا إِذا كَانَ يتبع مَا رُوِيَ عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه قنت فِي الْفجْر فَدَعَا على قوم ودعا لقوم','3','211',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1048,'<span data-type="title" id=toc-1121>حكم إِفْسَاد الْمُسكر على صَاحبه وَهُوَ لَا يعلم</span>

١٦٧٣ - قلت رجل مَعَه فِي منزله من يتَّخذ مُسكرا فَإِن صب فِيهِ خل أَو ملح أَو شَيْء مِمَّا يفْسد بِهِ وَصَاحبه لَا يعلم وانتقل الْمُسكر فَصَارَ خلا
فَقَالَ لَا بَأْس أَن يُفْسِدهُ عَلَيْهِ فَإِذا صَار خلا لم يَأْكُلهُ حَتَّى يكون الله يُبْدِي فَسَاده
<span data-type="title" id=toc-1122>إِذا غسل الْمُسكر أَو الدَّم من الثَّوْب فَلم يذهب لَونه</span>

١٦٧٤ - قلت إِن أصَاب الْمُسكر ثوبا فَغسله فَلم يذهب لَونه مَا ترى فِي الصَّلَاة فِيهِ
قَالَ لَا بَأْس إِذا غسله وَإِن لم يذهب أَثَره وَكَذَلِكَ الدَّم أَيْضا','3','212',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1049,'<span data-type="title" id=toc-1123>مسَائِل فِي الزواج مَعَ الْأمة وَأَقل الصَدَاق وَغَيرهمَا</span>

١٦٧٥ - قلت لأبي الرجل يمرض فَيحْتَاج إِلَى من يمرضه وَإِلَى من يَلِي عَوْرَته وَلَيْسَ عِنْده مَا يصدق ويتزوج مرّة حرَّة وَلَا مَا ينْفق عَلَيْهَا فَهَل يجوز لَهُ أَن يتَزَوَّج أمة قوم أَو أم ولد أَو مُدبرَة وَيجوز أَن يصدق درهما وَاحِدًا وَكم أقل مَا يجوز من الصَدَاق
قَالَ مَا تواضوا عَلَيْهِ
١٦٧٦ - قلت وَهل يجوز التَّزْوِيج على شَيْء من الْعرُوض
قَالَ لَا بَأْس أَن يتَزَوَّج هَذَا الرجل الْمَرِيض إِذا كَانَ لَا يَسْتَطِيع طولا لحرة أمة أَو أم ولد أَو مُدبرَة إِذا كَانَ زوجه السَّيِّد بِحَضْرَة شُهُود وصداق مَا تراضوا عَلَيْهِ فَهُوَ جَائِز وَلَا بَأْس أَن يتَزَوَّج على مَا كَانَ من الْعرُوض فَإِن زَوجهَا سَيِّدهَا لم ير لَهَا عَورَة وَلَا ترى','3','213',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1050,'لَهُ عَورَة إِلَّا مَا يجوز لغيره فَإِن هُوَ مَاتَ عَنْهَا فَإِن كَانَت أمة أَو أم ولد أَو مُدبرَة فعدتها شَهْرَان وَخَمْسَة أَيَّام وَإِن هُوَ طَلقهَا فعدتها إِن كَانَت مِمَّن تحيض حيضتان وَإِن كَانَت مِمَّن لَا تحيض فشهران فَهَذَا الَّذِي أخْتَار وَيَقُول بعض النَّاس شهر وَنصف وَمَا أحب أَن يشْتَرط على السَّيِّد النَّفَقَة لِأَنَّهُ إِذا تزَوجهَا فقد وَجَبت النَّفَقَة','3','214',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1051,'عَلَيْهِ فَإِن تطول السَّيِّد بذلك فَلَا بَأْس وَتعْتَد فِي الْموضع الَّذِي توفّي عَنْهَا فِيهِ وَإِن عوفي هَذَا الرجل فَالنِّكَاح ثَابت وَلَا يحل لسَيِّدهَا أَن يَطَأهَا حَتَّى تَعْتَد كَمَا ذكرنَا إِذا كَانَت قد خلت بزوجها ومست مِنْهُ مَا لَا يحل لغَيْرهَا
<span data-type="title" id=toc-1124>مسَائِل من الْإِجَارَة وَالزَّكَاة</span>

١٦٧٧ - قلت لأبي الْأَجِير يعْمل بِطَعَام بَطْنه وَكسوته تجب عَلَيْهِ صَدَقَة الْفطر وَيجوز للَّذي يعْمل عِنْده أَن يُعْطِيهِ من زَكَاته وَمن الْكَفَّارَات وَصدقَة الْفطر وَيجوز لغيره أَن يُعْطِيهِ من ذَلِك شَيْئا وَيجوز لَهُ أَن يَأْخُذ من ذَلِك شَيْئا وَقد كفى طَعَامه وَكسوته
قَالَ لَا بَأْس أَن يسْتَأْجر الرجل الْأَجِير بِطَعَام بَطْنه وَكسوته وَأَقل مَا يكون من الطَّعَام مُدبر وَهُوَ رَطْل وَثلث وَمن الْكسْوَة ثوب جَامع','3','215',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1052,'وَلَا تجب صَدَقَة الْفطر على الْمُسْتَأْجر وَإِنَّمَا تجب صَدَقَة الْفطر على من يمون وَيكون فِي عِيَاله وَإِنَّمَا هَذَا مُسْتَأْجر وَلَا يجوز لرجل أَن يُعْطي من زَكَاته مَا يدْفع عَن نَفسه بِهِ مذمة أَو يبقي بهَا مَاله وَلَا بَأْس أَن يَأْخُذ الرجل من الزَّكَاة وَمن صَدَقَة الْفطر إِذا كَانَ يَوْم يعْطى لَيْسَ يملك خمسين درهما أَو حِسَابهَا من الذَّهَب فَإِذا ملك خمسين درهما أَو حِسَابهَا من الذَّهَب لم يَأْخُذ من الزَّكَاة وَلَا صَدَقَة الْفطر وَلَا شَيْء من الْكَفَّارَات فَإِن كَانَ لَهُ من الْحلِيّ مَا يبلغ أَرْبَعِينَ درهما أَو قيمتهَا من الذَّهَب فَلَا يَأْخُذ من الزَّكَاة إِلَّا مَا يكمل الْخمسين
<span data-type="title" id=toc-1125>مسَائِل فِي القلس وَالدَّم</span>

١٦٧٨ - وَقَالَ القلس لَيْسَ فِيهِ وضوء وَلَا فِي خُرُوج الدَّم من الْجَسَد وضوء حَتَّى يكون فَاحِشا فَإِذا فحش عِنْده أعَاد وَإِن صَار من القلس إِلَى لِسَانه شَيْء وَهُوَ صَائِم فِي شهر رَمَضَان فبلعه أعَاد صَوْمه وَأما','3','216',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1053,'الصَّلَاة فَإِن كَانَ بِقدر مَا يكون إِلَى اللِّسَان فأرجو أَن لَا يكون عَلَيْهِ قَضَاء الصَّلَاة
<span data-type="title" id=toc-1126>مَوَاضِع سَجْدَتي السَّهْو</span>

١٦٧٩ - وَقَالَ يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه سجد سَجْدَتي السَّهْو فِي خمس مَوَاضِع فموضعين قبل التَّسْلِيم وَثَلَاث مَوَاضِع بعد التَّسْلِيم فَأَما قبل التَّسْلِيم فَإِنَّهُ نَهَضَ من ثِنْتَيْنِ فَلَمَّا كَانَ قبل أَن يسلم سجد سَجْدَتَيْنِ كَأَنَّهُ لم يتَشَهَّد فيهمَا ثمَّ سلم وَأما الْيَقِين فَأمر رَسُول الله صلى الله عَلَيْهِ وَسلم أَن يسْجد قبل التَّسْلِيم وَالْيَقِين أَن يشك فِي الثِّنْتَيْنِ والواحدة لَا يشك فِيهَا أَو يشك فِي الثَّلَاث والثنتين لَا يشك فيهمَا فَأمر فيهمَا رَسُول الله صلى الله عَلَيْهِ وَسلم بِالسُّجُود قبل التَّسْلِيم وَالْيَقِين أَن يرجع إِلَى الْوَاحِدَة أَو الثِّنْتَيْنِ الَّتِي لَا يشك فيهمَا وَالَّذِي بعد التَّسْلِيم هُوَ التَّحَرِّي هُوَ أَكثر ظَنّه ووهمه وَالَّذِي يرجع إِلَى التَّحَرِّي يسجدهما بعد التَّسْلِيم فَإِذا سجدهما بعد','3','217',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1054,'التَّسْلِيم تشهد فيهمَا وَإِذا سجدهما قبل التَّسْلِيم لم يتَشَهَّد فيهمَا وَإِذا سلم من ثِنْتَيْنِ أَو من ثَلَاث سجدهما بعد التَّسْلِيم فَهَذِهِ يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم هَكَذَا يعْطى كل حَدِيث مِنْهُمَا وَجهه لَا يعدى وَالَّذِي نَخْتَار بعد هَذِه الْخَمْسَة مَوَاضِع أَن يَأْتِي بسجدتي السَّهْو قبل التَّسْلِيم لِأَنَّهُ إِنَّمَا هُوَ يكمل بِهِ الصَّلَاة فَلَا يكون أَن يخرج مِنْهَا بِالتَّسْلِيمِ ثمَّ يعود فِيهَا إِلَّا بمعناها الأول كَمَا يبتدأ الصَّلَاة','3','218',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1055,'<span data-type="title" id=toc-1127>حكم تَعْجِيل الزَّكَاة</span>

١٦٨٠ - سَأَلت عَن تَعْجِيل الزَّكَاة فَقَالَ لَا بَأْس أَن يعجل لسنة إِذا وجد لَهَا موضعا
<span data-type="title" id=toc-1128>مصارف الزَّكَاة</span>

١٦٨١ - وَقَالَ سُفْيَان بلغنَا عَن الْعلمَاء لَا يحابى بهَا قريب وَلَا يمْنَع مِنْهَا بعيد وَلَا يدْفع بهَا مذمة وَلَا يقي الرجل بهَا مَاله
قَالَ أبي إِنَّمَا هِيَ لمن ذكر الله تَعَالَى فِي الْقُرْآن
١٦٨٢ - قلت الرجل يَشْتَرِي للرجل فرسا من زَكَاته
قَالَ يدْفع إِلَيْهِ الدَّنَانِير حَتَّى يَشْتَرِي هُوَ
١٦٨٣ - قلت يعْتق مِنْهَا رَقَبَة
قَالَ يعان مِنْهَا فِي الرّقاب لِأَنَّهُ إِذا أعتق جر وَلَاء
<span data-type="title" id=toc-1129>مِقْدَار الْجِزْيَة على الْفَقِير</span>

١٦٨٤ - قلت الْفَقِير الَّذِي وظفه عمر
قَالَ لَا أَدْرِي قد اخْتلفُوا فِيهِ','3','219',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1056,'١٦٨٥ - قلت كَيفَ يؤدون
قَالَ يكون ذَلِك أَيْضا على قدر طاقتهم
<span data-type="title" id=toc-1130>إِذا قَالَ أَنْت طَالِق أَنْت طَالِق أَنْت طَالِق</span>

١٦٨٦ - قلت إِذا قَالَ أَنْت طَالِق أَنْت طَالِق أَنْت طَالِق
فَقَالَ قد يكون أَرَادَ أَن يفهمها فَإِذا كَانَت غير مَدْخُول بهَا فَقَالَ أَنْت طَالِق أَنْت طَالِق أَنْت طَالِق فقد لحقتها التطليقة الأولى وَإِن أَرَادَ رَجعتهَا جدد لَهَا مهْرا وَكَانَت عِنْده على ثِنْتَيْنِ وَإِن لم يرد رَجعتهَا فَهِيَ أولى بِنَفسِهَا وَهُوَ على مَا أَرَادَ وَلَكِن إِذا قَالَ أَنْت طَالِق وَطَالِق وَطَالِق فَهَذِهِ ثَلَاث
<span data-type="title" id=toc-1131>أَقْوَال أَحْمد فِي رجال</span>

١٦٨٧ - سَأَلته عَن سَلمَة بن كهيل فَقَالَ أبي قَالَ الثَّوْريّ لحماد بن سَلمَة رَأَيْت سَلمَة بن كهيل فَقَالَ رَأَيْت شَيخا كيسا','3','220',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1057,'١٦٨٨ - قلت حبيب بن أبي ثَابت
قَالَ مَا أعلم إِلَّا خيرا سمع من ابْن عَبَّاس وَابْن عمر
١٦٨٩ - قلت أَبُو نَضرة
قَالَ لَا أعلم إِلَّا خيرا
١٦٩٠ - قلت فَأَبُو عُثْمَان النَّهْدِيّ
قَالَ من رضَا النَّاس
١٦٩١ - حَدثنِي أبي قَالَ حَدثنَا يحيى بن سعيد قَالَ قَالَ شُعْبَة أَتَانِي سُلَيْمَان التَّيْمِيّ وَابْن عون يعزياني بِأبي فَقَالَ التَّيْمِيّ حَدثنَا أَبُو نَضرة قَالَ فَقَالَ ابْن عون قد رَأَيْت أَبَا نَضرة قَالَ قَالَ التَّيْمِيّ فَمه أَو فَمَا رَأَيْت
١٦٩٢ - قَالَ أبي عبد الله الرَّازِيّ روى عَنهُ الحكم وَابْن أبي ليلى وَفطر','3','221',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1058,'وَالْأَعْمَش وروى عَنهُ سعيد بن مَسْرُوق وَأمه أم ولد لعَلي وَقَالَ بَعضهم سَرِيَّة
١٦٩٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن مقَاتل الْمَرْوذِيّ قَالَ أخبرنَا عباد بن الْعَوام قَالَ حَدثنَا الْحجَّاج عَن عبد الله بن عبد الله مولى بني هَاشم وَكَانَ ثِقَة قَالَ وَكَانَ الحكم يَأْخُذ عَنهُ
١٦٩٤ - قَالَ أبي وجعفر بن أبي ثَوْر روى عَنهُ سماك بن حَرْب وَعُثْمَان بن','3','222',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1059,'عبد الله بن موهب وَأَشْعَث بن أبي الشعْثَاء وجده جَابر بن سَمُرَة أَبُو أمه
<span data-type="title" id=toc-1132>مسَائِل فِي زِيَادَة الْإِيمَان ونقصانه والإستثناء</span>

١٦٩٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو سَلمَة الْخُزَاعِيّ قَالَ قَالَ مَالك وَشريك وَأَبُو بكر بن عَيَّاش وعبد العزيز بن أبي سَلمَة وَحَمَّاد بن سَلمَة وَحَمَّاد بن زيد الْإِيمَان الْمعرفَة وَالْإِقْرَار وَالْعَمَل إِلَّا أَن حَمَّاد بن زيد كَانَ يفرق بَين الْإِيمَان وَالْإِسْلَام وَيجْعَل الْإِسْلَام عَاما وَالْإِيمَان خَاصّا','3','223',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1060,'١٦٩٦ - قَالَ أبي سَمِعت أَبَا نعيم يَقُول سَمِعت سُفْيَان يَقُول الْإِيمَان يزِيد وَينْقص
١٦٩٧ - قَالَ وَسمعت وكيعا يَقُول سَمِعت سُفْيَان يَقُول الْإِيمَان يزِيد وَينْقص
١٦٩٨ - قَالَ وَسمعت سُفْيَان بن عُيَيْنَة يَقُول إِذا سُئِلَ مُؤمن أَنْت إِن شَاءَ لم يجبهُ وَإِن شَاءَ قَالَ سؤالك إيَّايَ بِدعَة وَلَا أَشك فِي إيماني لَا يعنف من قَالَ الْإِيمَان ينقص إِن قَالَ إِن شَاءَ الله لَيْسَ يكره وَلَيْسَ بداخل فِي الشَّك
١٦٩٩ - قَالَ أبي سَمِعت يحيى بن سعيد يَقُول مَا أدْركْت أحدا من أَصْحَابنَا وَلَا بَلغنِي إِلَّا على الإستثناء وَحسن يحيى الإستثناء وَرَآهُ','3','224',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1061,'١٧٠٠ - قَالَ سَمِعت وكيعا يَقُول قَالَ سُفْيَان النَّاس عندنَا مُؤمنُونَ فِي الْأَحْكَام والمواريث وَنَرْجُو أَن يكون كَذَلِك وَلَا نَدْرِي مَا حَالنَا عندالله
<span data-type="title" id=toc-1133>من حنث وَلَيْسَ عِنْده شَيْء</span>

١٧٠١ - وَقَالَ يرْوى فِي الرجل يحلف فَيحنث وَلَيْسَ عِنْده فَروِيَ عَن إِبْرَاهِيم قَالَ إِذا كَانَ عِنْده عشرُون درهما كفر وَقَالَ الْحسن أما يجد وأف وَرُوِيَ عَنهُ أَيْضا أَنه كَانَ لَا يُوَقت فِي ذَلِك وَلَا مُحَمَّد بن','3','225',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1062,'سِيرِين وَرُوِيَ عَن سعيد بن جُبَير إِذا كَانَ عِنْده ثَلَاث دَرَاهِم كفر
قَالَ أبي إِذا كَانَ عِنْده أَكثر من قوت يَوْم كفر ويروى عَن النَّبِي صلى الله عَلَيْهِ وَسلم إِذا كَانَ عِنْده مَا يبيته لم يسْأَل وَإِن لم يكن عِنْده صَامَ ثَلَاثَة أَيَّام مُتَتَابِعَات
<span data-type="title" id=toc-1134>حكم القَوْل الله خليفتي عَلَيْك</span>

١٧٠٢ - قلت الْمَرْأَة تَقول لإبنها الله خليفتي عَلَيْك
قَالَ لَو استودعته الله كَانَ أعجب إِلَيّ فَأَما خليفتي فَمَا أَدْرِي','3','226',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1063,'<span data-type="title" id=toc-1135>نِصَاب الزَّكَاة فِي الشَّاة وَمعنى الخليط</span>

١٧٠٣ - وَقَالَ إِذا كَانَ ثَلَاثِينَ شَاة وَعشر سخال فَفِيهَا شَاة مُسِنَّة يحْتَسب بالسخال عَلَيْهِم وَلَا يُؤْخَذ فِي الصَّدَقَة إِلَّا الثني والجذع من الضَّأْن كَذَلِك','3','227',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1064,'١٧٠٤ - لَو أَن لأربعين رجلا اربعين شَاة فِي مَوضِع مُجْتَمع كَانَ عَلَيْهِم فِيهَا شَاة إِذا كَانُوا خلطاء والخليط أَن يكون مشرعها وَاحِدًا ومراعها وَاحِدًا
<span data-type="title" id=toc-1136>حكم الإستثناء فِي الطَّلَاق</span>

١٧٠٥ - قلت رجل حلف بِالطَّلَاق ثَلَاثًا فَقَالَ إِن شَاءَ الله
قَالَ لَا أَقُول فِيهِ شَيْئا','3','228',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1065,'<span data-type="title" id=toc-1137>من حلف بِالْمَشْيِ إِلَى بَيت الله</span>

١٧٠٦ - قلت الرجل يحلف بِالْمَشْيِ إِلَى بَيت الله
قَالَ إِذا كَانَ عقده عقد يَمِين فكفارة يَمِين
<span data-type="title" id=toc-1138>من قَالَ كل مَا أملك فِي الْمَسَاكِين أَو فِي سَبِيل الله</span>

١٧٠٧ - قلت الرجل يَقُول كل مَال أملك فِي الْمَسَاكِين صَدَقَة أَو كل مَا أملك فِي سَبِيل الله
قَالَ عَلَيْهِ كَفَّارَة يَمِين إِذا كَانَ عقده عقد الْيَمين وَلم يكن على وَجه النّذر
<span data-type="title" id=toc-1139>حكم من قَالَ أَحْلف أَو أقسم وَلم يقل بِاللَّه</span>

١٧٠٨ - قلت الرجل يحلف فَيَقُول أقسم وَلَا يَقُول بِاللَّه وَيَقُول أَحْلف وَلَا يَقُول بِاللَّه وَأشْهد وَلَا يَقُول بِاللَّه
قَالَ فِيهِ اخْتِلَاف فَمن النَّاس من يَقُول إِذا قَالَ أقسم وَلم يُقَال بِاللَّه فَهِيَ يَمِين وَقَالَ بَعضهم لَا حَتَّى يَقُول أقسم بِاللَّه وأحلف بِاللَّه وَأشْهد بِاللَّه','3','229',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1066,'١٧٠٩ - قلت مَا تَقول فِيهَا
قَالَ فِيهَا بعض الإختلاف
<span data-type="title" id=toc-1140>من حلف فَقَالَ وَالله وَالله وَالله للتوكيد</span>

١٧١٠ - قلت الرجل يحلف فَيَقُول وَالله وَالله وَالله لَا فعلت كَذَا وَكَذَا
يُرِيد بذلك التَّأْكِيد على نَفسه والتغليظ حَتَّى لَا يفعل
قَالَ أما ابْن عمر فَكَانَ إِذا وكد أعتق والتأكيد أَن يحلف على الشَّيْء فيكرر الْيَمين وَأَرْجُو أَن تجزيه كَفَّارَة يَمِين
<span data-type="title" id=toc-1141>من حلف على شَيْء وَهُوَ يرى أَنه صَادِق ثمَّ تبين خِلَافه</span>

١٧١١ - قلت الرجل يحلف بِالطَّلَاق أَو بِغَيْرِهِ من الْأَيْمَان على الشَّيْء الَّذِي يرى أَنه فِيهِ صَادِق لَا يشك فِيهِ ثمَّ تبين لَهُ بعد ذَلِك أَنه لَيْسَ كَمَا حلف عَلَيْهِ يكون هَذَا لَغوا
قَالَ أما الطَّلَاق فَلَا أَقُول فِيهِ شَيْئا وَأما الْيَمين من غير الطَّلَاق فَلَا شَيْء عَلَيْهِ وَهُوَ من اللَّغْو
<span data-type="title" id=toc-1142>مِقْدَار زَكَاة الْفطر</span>

١٧١٢ - قلت زَكَاة الْفطر
قَالَ الْبر خَمْسَة أَرْطَال وَثلث وَإِذا كَانَ عِنْده أَكثر من قوت يَوْم أطْعم','3','230',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1067,'<span data-type="title" id=toc-1143>من عَلَيْهِ كَفَّارَة يَمِين وَلَيْسَ لَهُ مَال</span>

١٧١٣ - قلت رجل عَلَيْهِ كَفَّارَة يَمِين وَلَيْسَ لَهُ مَال
قَالَ إِذا كَانَ عِنْده من الْوَرق أَكثر من قوت يَوْم كفر عَن يَمِينه بِالْفَضْلِ من قوت يَوْمه وَإِن لم يكن عِنْده صَامَ ثَلَاثَة أَيَّام مُتَوَالِيَة
<span data-type="title" id=toc-1144>حكم وضع الدّين من الزَّكَاة</span>

١٧١٤ - قلت رجل لَهُ مَال على رجل مِسْكين تحل لَهُ الصَّدَقَة فيتركه لَهُ ويحسبه من زَكَاة مَاله
قَالَ لَا يحسبه من الزَّكَاة لِأَن هَذَا مَال لَا يدْرِي يصل إِلَيْهِ أم لَا
<span data-type="title" id=toc-1145>حكم الزَّكَاة فِي الدّين ومهور النِّسَاء والحلي</span>

١٧١٥ - قلت رجل لَهُ دين على رجل مؤسر هَل يجب عَلَيْهِ الزَّكَاة
قَالَ إِذا قَبضه مِنْهُ زكى لما مضى
١٧١٦ - قلت هَل على النِّسَاء زَكَاة فِي حليهن وَفِي مهورهن
قَالَ إِذا قبضن مهورهن زكين لما مضى وَأما الْحلِيّ إِذا كَانَ يعار أَو يلبس فَلَا زَكَاة فِيهِ','3','231',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1068,'<span data-type="title" id=toc-1146>إِذا طهرت الْمَرْأَة من الْحيض فِي وَقت صَلَاة أَو حَاضَت وَلم تصلها</span>

١٧١٧ - قلت الْمَرْأَة تحيض فِي وَقت صَلَاة كم يجب عَلَيْهَا إِذا طهرت أَن تصلي
قَالَ إِذا حَاضَت فِي وَقت صَلَاة فَمن النَّاس من يَقُول تقضيها إِذا طهرت تجعلها أول صَلَاة تصليها وَمن النَّاس من يَقُول لَا قَضَاء عَلَيْهَا لِأَنَّهُ قد كَانَ لَهَا أَن تؤخرها إِلَى آخر وَقتهَا فَأَما الَّتِي تطهر فَإِذا طهرت فِي وَقت الْعَصْر قَضَت الظّهْر وَالْعصر فَإِذا طهرت فِي وَقت عشَاء الْآخِرَة قَضَت الْمغرب وَالْعشَاء
<span data-type="title" id=toc-1147>الْوضُوء من كل مَا خرج من السَّبِيلَيْنِ</span>

١٧١٨ - قلت الرجل يكون بِهِ الْحبّ أَو الدُّود يخرج من دبره
قَالَ كل شَيْء من السَّبِيلَيْنِ من دبر أَو فرج فَمَا خرج مِنْهُمَا من شَيْء من ريح كَانَ أَو غَيره فَفِيهِ الْوضُوء','3','232',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1069,'<span data-type="title" id=toc-1148>من وجد على طرف إحليله بللا وَلم يعلم أَنه احْتَلَمَ</span>

١٧١٩ - قلت الرجل يجد على طرف إحليله بللا وَهُوَ نَائِم وَلم ير أَنه احْتَلَمَ
قَالَ إِذا كَانَ شَابًّا مِمَّن لَيْسَ لَهُ أهل فالحيطة لَهُ أَن يغْتَسل لِأَنَّهُ قد يَحْتَلِم الرجل وَلَا يعلم وَإِن كَانَ لَهُ أهل فلاطف أَهله أَو لمسها أَو قبل ثمَّ نَام على ذَلِك فأرجو أَن لَا يجب عَلَيْهِ الْغسْل إِذا لم يكن رأى فِي مَنَامه شَيْئا
<span data-type="title" id=toc-1149>الحكم إِذا خرج شَيْء من الذّكر بعد غسل الْجَنَابَة</span>

١٧٢٠ - قلت الرجل يغْتَسل من الْجَنَابَة ثمَّ يخرج من ذكره شَيْء بعد الْغسْل وَلم يبل
قَالَ يتَوَضَّأ وَقد رُوِيَ عَن عَليّ أَنه قَالَ إِن كَانَ بَال وَلَا اغْتسل وَقَالَ ابْن عَبَّاس يَجْزِي مِنْهُ الْوضُوء','3','233',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1070,'<span data-type="title" id=toc-1150>مسَائِل فِي الصّرْف</span>

١٧٢١ - قلت الرجل يجِئ إِلَى الْبَقَّال فَيَقُول عنْدك دَرَاهِم بِدِينَار فَيَقُول لَا عِنْدِي بِنصْف دِينَار فيشتري مِنْهُ بِنصْف دِينَار وَيدْفَع إِلَيْهِ الدِّينَار فَيَقُول للبقال هَذِه الدَّرَاهِم بِعشْرَة قراريط من هَذَا الدِّينَار وَعشرَة قراريط لي عَلَيْك فيتراضيان على ذَلِك ثمَّ يَأْخُذ مِنْهُ بعد ذَلِك الدِّرْهَم وَالدِّرْهَمَيْنِ وَالثَّلَاثَة وَلَا يصارفه على شَيْء حَتَّى إِذا أَخذ مِنْهُ بَقِيَّة النّصْف الدِّينَار قَالَ الدَّرَاهِم الَّتِي كَانَت لَك عَليّ بِالْعشرَةِ قراريط الَّتِي كَانَت لي عَلَيْك هَل يطيب ذَلِك لَهما وَكَيف الْحِيلَة لَهما
قَالَ إِن كَانَ يُرِيد أَن يحتال بِأَن لَا يَسْتَوْفِي حَتَّى يكون هَا وَهَا فَلَا يعجبنا ذَلِك وَإِن كَانَ قَالَ هَذِه الدَّرَاهِم الَّذِي سلم إِلَيْهِ بِمَا سلم إِلَيْهِ من الذَّهَب وَهُوَ كَذَا وَكَذَا قيراطا فَأخذ المُشْتَرِي الدَّرَاهِم وَأخذ البَائِع للدراهم الدِّينَار قبل أَن يفترقا وَيكون بَقِيَّة الدِّينَار لصَاحبه وَهُوَ شريك لَهُ فَلَا بَأْس فَإِن أَرَادَ أَن يَبِيعهُ بِبَقِيَّة الدِّينَار فأعجب إِلَيْنَا أَن يَسْتَوْفِي بَقِيَّة ثمن الدِّينَار وَيسلم إِلَيْهِ وَلَا يعجبنا أَن يَأْخُذ درهما ودرهمين ويصارفه
١٧٢٢ - قلت الرجل يدْفع إِلَى الصَّيْرَفِي الدِّينَار فيشترى بِهِ دَرَاهِم فَيَقُول مَا رد عَلَيْك من هَذِه الدَّرَاهِم فَهُوَ عَليّ','3','234',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1071,'قَالَ هَذَا مَكْرُوه أَن يشْتَرط مَا رد عَلَيْهِ عَلَيْهِ وَلَا يفترقان وَبَينهمَا لبس وَالذَّهَب بالورق رَبًّا إِلَّا هَا وَهَا
١٧٢٣ - قلت الرجل يَبِيع ثَلَاثَة دَنَانِير مكسورا بدينارين غير دِرْهَم هَل يجوز هَذَا
قَالَ لَا يعجبنا وَلَكِن إِن شَاءَ أَن يَبِيعهَا بِدَرَاهِم وَيَشْتَرِي بهَا من الذَّهَب مَا شَاءَ
١٧٢٤ - قلت الرجل يَبِيع عشرَة دَرَاهِم فضَّة مَكْسُورَة بِثمَانِيَة دَرَاهِم وفلسين
قَالَ هَذَا خَبِيث رَدِيء لَا يجوز وَلَكِن يَبِيعهَا بِكَذَا وَكَذَا قِيرَاط من الذَّهَب','3','235',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1072,'<span data-type="title" id=toc-1151>لَا اعْتِبَار للشَّكّ فِي نقض الْوضُوء</span>

١٧٢٥ - قلت الرجل يخيل إِلَيْهِ وَهُوَ يُصَلِّي أَنه قد خرج من إحليله شَيْء فَرُبمَا نظر فَإِذا بَلل وَهُوَ فِي الصَّلَاة
قَالَ إِن كَانَ تخيل إِلَيْهِ فَلَا يلْتَفت إِلَيْهِ حَتَّى يستيقن وَلَا يتَعَاهَد ذَلِك من نَفسه
<span data-type="title" id=toc-1152>حكم الإكتفاء على الْأَحْجَار فِي الإستنجاء</span>

١٧٢٦ - قلت الرجل يخرج من الْخَلَاء ثمَّ يستنجي بِثَلَاثَة أَحْجَار طَاهِرَة وَلَا يستنجي بِمَاء أَتَرَى بذلك بَأْسا
قَالَ إِذا نقى بالأحجار أَو بِمَاء فَكل ذَلِك يَجْزِي إِلَّا أَن يكون تلطخ غير مَوضِع الْخَلَاء فَلَا يَجْزِي
<span data-type="title" id=toc-1153>من صلى وَفِي ثَوْبه دم أَو غَائِط أَو جَنَابَة</span>

١٧٢٧ - قلت الرجل يُصَلِّي وَفِي ثَوْبه دم أَو غَائِط أَو جَنَابَة فَيصَلي وَلَا يعلم ثمَّ يعلم بِهِ بعد
قَالَ أما الْبَوْل والعذرة فَإِنَّهُ يُعِيد مِنْهُ قل أَو كثر يحْتَاط حَتَّى لَا يكون فِي نَفسه مِنْهُ شَيْء','3','236',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1073,'<span data-type="title" id=toc-1154>من كَانَ بِهِ جرح لَا يرقأ يتَوَضَّأ لكل صَلَاة</span>

١٧٢٨ - قلت الرجل بِهِ الدماميل أَو جرح لَا يرقأ أيجب عَلَيْهِ الْوضُوء لكل صَلَاة
قَالَ يتَوَضَّأ لكل صَلَاة إِذا كَانَ لَا يرقأ بِمَنْزِلَة الْمُسْتَحَاضَة يتَوَضَّأ لكل صَلَاة
<span data-type="title" id=toc-1155>أَيْن تَعْتَد الْمُتَوفَّى عَنْهَا زَوجهَا</span>

١٧٢٩ - قلت الْمَرْأَة يَمُوت زَوجهَا
قَالَ تَعْتَد فِي بَيت زَوجهَا الَّذِي أُصِيبَت فِيهِ إِلَّا أَن تكون سَاكِنة فَتخرج
<span data-type="title" id=toc-1156>من أقسم على أحد فأحنثه على من الْكَفَّارَة</span>

١٧٣٠ - قلت الرجل يَقُول للرجل بِاللَّه عَلَيْك أَلا أكلت فَلَا يَأْكُل أَو يَقُول أَقْسَمت عَلَيْك بِاللَّه أَلا أكلت
قَالَ النَّاس فِيهِ مُخْتَلفُونَ فَمن النَّاس من يَقُول تجب الْكَفَّارَة على الَّذِي أقسم أَو حلف وَمن النَّاس من يَقُول على الَّذِي أقسم عَلَيْهِ','3','237',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1074,'<span data-type="title" id=toc-1157>حكم عتق الْكِتَابِيّ فِي الْكَفَّارَات</span>

١٧٣١ - قَالَ أبي لَا يجوز عتق الْيَهُودِيّ وَلَا النَّصْرَانِي فِي شَيْء من الْكَفَّارَات وَأما التَّطَوُّع فَلَا باس بِهِ
<span data-type="title" id=toc-1158>كم يصلى قبل الْجُمُعَة وَبعدهَا</span>

١٧٣٢ - قلت كم يصلى قبل الْجُمُعَة وَبعدهَا
قَالَ لَا بَأْس بِمَا صلى إِن صلى بعْدهَا سِتا أَو أَرْبعا أَو رَكْعَتَيْنِ فَلَا بَأْس
<span data-type="title" id=toc-1159>مَتى يُورث السقط</span>

١٧٣٣ - قَالَ السقط يُورث إِذا اسْتهلّ والإستهلال أَن يبكي أَو يصْرخ فَإِن لم يصْرخ وَلم يبك واختلج فَلَا يُورث','3','238',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1075,'<span data-type="title" id=toc-1160>كَيْفيَّة صَلَاة المقتدى إِذا صلى الإِمَام جَالِسا</span>

١٧٣٤ - قَالَ أبي الإِمَام إِذا صلى جَالِسا صلوا جُلُوسًا قَالَ بعض النَّاس لَا يؤم أحدا جَالِسا فَيصَلي من وَرَاءه قيَاما لَا ينْتَقل فرض أحد دون أحد يُصَلِّي كل إِنْسَان فَرْضه وَاحْتج هَذَا بِأَن النَّبِي صلى الله عَلَيْهِ وَسلم صلى قَاعِدا وَأَبُو بكر قَائِما وَكَانَ أَبُو بكر يأتم بِالنَّبِيِّ صلى الله عَلَيْهِ وَسلم وَالنَّاس يأتمون بِأبي بكر فَكَانَ النَّبِي صلى الله عَلَيْهِ وَسلم هُوَ الإِمَام وَهَذَا قَول الشَّافِعِي وَقَالَ بعض النَّاس وَرُوِيَ عَن النَّبِي صلى الله عَلَيْهِ وَسلم أَنه جحش شقَّه الْأَيْمن قَالَ فَدَخَلْنَا عَلَيْهِ نعوده فَحَضَرت الصَّلَاة فصلى قَاعِدا وصلينا قعُودا فَلَمَّا قضى الصَّلَاة قَالَ إِنَّمَا جعل الإِمَام ليؤتم بِهِ فَإِذا كبر فكبروا وَإِذا ركع فاركعوا وَإِذا سجد فاسجدوا وَإِذا قَالَ سمع الله لمن حَمده فَقولُوا رَبنَا وَلَك الْحَمد وَإِن صلى قَاعِدا فصلوا قعُودا أَجْمَعُونَ
حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا سُفْيَان عَن الزُّهْرِيّ سَمعه من أنس ويروى عَن جَابر بن عبد الله أَنه قَالَ صرع رَسُول الله','3','239',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1076,'صلى الله عَلَيْهِ وَسلم من فرس فَوَثَبت رجله فَدَخَلْنَا عَلَيْهِ نعوده وَهُوَ يُصَلِّي قَاعِدا فَأَشَارَ إِلَيْنَا بِيَدِهِ أَن اجلسوا ثمَّ دَخَلنَا عَلَيْهِ من الْغَد وَهُوَ يُصَلِّي الْمَكْتُوبَة قَاعِدا فَأَشَارَ إِلَيْنَا بِيَدِهِ أَن اجلسوا فَلَمَّا انْصَرف قَالَ إِذا صلى الإِمَام قَاعِدا فصلوا قعُودا وَإِذا صلى قَائِما فصلوا قيَاما وَالَّذِي احْتج بِأَن النَّبِي صلى الله عَلَيْهِ وَسلم صلى قَاعِدا إِذْ جلس عَن يسَار أبي بكر فَكَانَ أَبُو بكر بِالنَّبِيِّ وَالنَّاس يأتمون بِأبي بكر فَهَذَا الْموضع كَانَ الْمُبْتَدِئ بِالصَّلَاةِ أَبُو بكر فَكَانُوا يأتمون بِأبي بكر وَأَبُو بكر يأتم وهم قيام وَحَيْثُ أَوْمَأ إِلَيْهِم النَّبِي صلى الله عَلَيْهِ وَسلم فقعدوا كَانَ هُوَ الْمُبْتَدِئ للصَّلَاة فَقَالَ اقعدوا فقعدوا وَلَيْسَ ثمَّ إِمَام غير النَّبِي صلى الله عَلَيْهِ وَسلم فصلوا بِصَلَاتِهِ قعُودا وَهُوَ قَاعد وَرُوِيَ عَن أبي هُرَيْرَة عَن النَّبِي صلى الله عَلَيْهِ وَسلم إِذا كبر الإِمَام فكبروا وَإِذا ركع فاركعوا وَإِذا سجد فاسجدوا وَإِن صلى جَالِسا فصلوا جُلُوسًا وَالَّذِي يذهب إِلَيْهِ أبي هَذِه الْأَحَادِيث','3','240',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1077,'وروت عَائِشَة أَن رَسُول الله صلى الله عَلَيْهِ وَسلم دخل عَلَيْهِ النَّاس فِي مَرضه يعودونه فصلى بهم جَالِسا فَجعلُوا يصلونَ قيَاما فَأَشَارَ أَن اجلسوا فَلَمَّا فرغ قَالَ إِنَّمَا جعل الإِمَام ليؤتم بِهِ فَإِذا ركع فاركعوا وَإِذا رفع فارفعوا وَإِذا صلى جَالِسا فصلوا جُلُوسًا وَقد رُوِيَ فِي ذَلِك عَن أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم إِن جَابِرا صلى بهم وَهُوَ جَالس وهم جُلُوس وَأسيد بن حضير وَأَبُو هُرَيْرَة معنى قَوْلهم وفعلهم إِذا صلى الإِمَام قَاعِدا فصلوا قعُودا فَأَما من قَالَ لَا يُؤمن أحد يَعْنِي جَالِسا فَهَذَا خلاف مَا رُوِيَ عَن أبي هُرَيْرَة وَعَائِشَة وَأسيد وَجَابِر عَن النَّبِي صلى الله عَلَيْهِ وَسلم وَخلاف فعله إِذْ مرض فصلى','3','241',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1078,'قَاعِدا وَأَبُو بكر قَائِم يأتم بِهِ فَهُوَ خلاف هَذِه الْأَخْبَار جَمِيعًا فَإِن كَانَ إِمَامًا مبتدئا للصَّلَاة فصلى بِقوم بعض صلَاته فجَاء الإِمَام الْأَكْبَر وَهُوَ مَرِيض فَإِن شَاءَ جلس عَن يسَاره كَفعل النَّبِي صلى الله عَلَيْهِ وَسلم فَيكون الإِمَام الأول الَّذِي ابْتَدَأَ الصَّلَاة يأتم بِهِ النَّاس ويأتم هُوَ بِالْإِمَامِ الَّذِي جَاءَ كَفعل النَّبِي صلى الله عَلَيْهِ وَسلم
<span data-type="title" id=toc-1161>حكم تَعْجِيل الزَّكَاة لسنة</span>

١٧٣٥ - قَالَ أبي يعجل من الزَّكَاة للسّنة لِأَن النَّبِي صلى الله عَلَيْهِ وَسلم تعجل صَدَقَة الْعَبَّاس وَقَالَ إِنَّا كُنَّا تعجلنا صَدَقَة الْعَبَّاس الْعَام عَام أول
<span data-type="title" id=toc-1162>حكم تَعْجِيل الْكَفَّارَة قبل الْحِنْث</span>

١٧٣٦ - وَلَا بَأْس بتعجيل الْكَفَّارَة قبل الْحِنْث يرْوى فِيهِ عَن سلمَان وَابْن عمر كفرا قبل الْحِنْث وَقَالَ الله عز وَجل {وَالَّذين يظاهرون من نِسَائِهِم ثمَّ يعودون لما قَالُوا فَتَحْرِير رَقَبَة من قبل أَن يتماسا} فقد أَمر','3','242',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1079,'بِالْكَفَّارَةِ قبل الْحِنْث فَلَو أَنه طَلقهَا أَو مَاتَ عَنْهَا قبل أَن يكفر لم يكن عَلَيْهِ كَفَّارَة لِأَنَّهُ لم يَحْنَث وَقَالَ بعض النَّاس لَا تجزىء الْكَفَّارَة إِلَّا بعد الْحِنْث
<span data-type="title" id=toc-1163>مصارف الزَّكَاة</span>

١٧٣٧ - قلت الرجل يحمل من زَكَاته فِي سَبِيل الله
قَالَ يُعْطي الَّذِي يُرِيد حمله دَنَانِير فَيكون يَشْتَرِي هُوَ لنَفسِهِ وَلَا يعْتق من الزَّكَاة ويعين فِيهِ وَقد كنت أذهب مرّة إِلَى أَن يعْتق ثمَّ إِنِّي جبنت عَنهُ لَا يجر وَلَاء وَيكون لَهُ مَنْفَعَة
<span data-type="title" id=toc-1164>من قَالَ أَنْت طَالِق أَنْت طَالِق أَنْت طَالِق أَو أَنْت طَالِق وَطَالِق وَطَالِق</span>

١٧٣٨ - وَقَالَ إِذا قَالَ الرجل لإمرأته أَنْت طَالِق أَنْت طَالِق أَنْت طَالِق وَقد دخل بهَا فَهُوَ على مَا أَرَادَ إِن كَانَ أَرَادَ إفهامها فَهُوَ الَّذِي أَرَادَ وَإِن أَرَادَ غير ذَلِك فَهُوَ على مَا أَرَادَ وَإِذا كَانَ ذَلِك قبل الدُّخُول فَقَالَ أَنْت طَالِق أَنْت طَالِق أَنْت طَالِق بَانَتْ بِالْأولَى وَلَا تلحقها','3','243',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1080,'الثنتان فَإِن أَرَادَ تَزْوِيجهَا فَهِيَ أملك بِنَفسِهَا يَتَزَوَّجهَا تزوج الْأَجْنَبِيَّة بِمهْر وشهود وَولي
وَإِذا قَالَ أَنْت طَالِق وَطَالِق وَطَالِق وَذَلِكَ قبل الدُّخُول فَهِيَ ثَلَاث لإنه كَلَام مَعْطُوف بعضه على بعض وَلَيْسَ بِمَنْزِلَة أَنْت طَالِق أَنْت طَالِق أَنْت طَالِق لِأَن كل كلمة من هَذِه مكتفية بِنَفسِهَا وَهِي كلمة جَامِعَة وَكَذَا الْمَدْخُول بهَا وَقَوله أَنْت طَالِق وَطَالِق وَطَالِق بِمَنْزِلَة قَوْله أَنْت طَالِق ثَلَاثًا
<span data-type="title" id=toc-1165>الْأَذَان مثنى مثنى</span>

١٧٣٩ - قَالَ أبي الْأَذَان مثنى مثنى والمثنى أَن يَقُول الله أكبر الله أكبر الله أكبر الله أكبر أشهد أَن لَا إِلَه إِلَّا الله أشهد أَن لَا إِلَه إِلَّا الله أشهد أَن مُحَمَّدًا رَسُول الله أشهد أَن مُحَمَّدًا رَسُول الله حَيّ على الصَّلَاة حَيّ على الصَّلَاة حَيّ على الْفَلاح حَيّ على الْفَلاح الله أكبر الله أكبر لَا إِلَه إِلَّا الله هَذَا فِي حَدِيث عبد الله بن زيد','3','244',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1081,'<span data-type="title" id=toc-1166>إِذا كَانَ فِي الْوَلِيمَة مُنكر خرج وَلم يَأْكُل لَهُم طَعَاما</span>

١٧٤٠ - قلت الْوَلِيمَة يكون فِيهَا الْمُسكر
قَالَ إِذا كَانَ فِيهَا الْمُسكر أَو فِيهَا شَيْء من آنِية الْمَجُوس الذَّهَب وَالْفِضَّة أَو سترت الْجدر بالثياب فَإِذا رأى ذَلِك خرج وَلم يطعم لَهُم طَعَاما أَو كَانَ فِيهِ ضرب معازف يُقَال إِن أَبَا أَيُّوب وَكَانَ ابْن عمر أعرس على إبنه سَالم قَالَ سَالم فَكَانَ فِيمَن أذنا أَبُو أَيُّوب فجَاء فَدخل فَرَأى الْبَيْت قد ستر بجنادى أَخْضَر فَقَالَ أَي عبد الله أتسترون الْجدر فَقَالَ ابْن عمر غلبنا النِّسَاء فَقَالَ لَا آكل لكم طَعَاما وَلَا أَدخل لكم بَيْتا فَخرج','3','245',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1082,'<span data-type="title" id=toc-1167>جَوَاز السّلم فِي الطَّعَام وَنَحْوه وَكَرَاهَة الرَّهْن وَالْكَفِيل فِيهِ</span>

١٧٤١ - قَالَ أبي السّلم جَائِز فِي كل مَا أسلم فِيهِ الرجل من الطَّعَام وَالتَّمْر وَالشعِير والذرة والسلت وَالثيَاب وَالْحَيَوَان إِذا كَانَ ذَلِك بِصفة وَأجل مُسَمّى فَلَا بَأْس بِهِ وَيكرهُ الرَّهْن والقبيل يَعْنِي الْكَفِيل فِي ذَلِك
<span data-type="title" id=toc-1168>حكم ربح الْوَدِيعَة إِذا اتّجر بهَا بِدُونِ إِذن صَاحبهَا</span>

١٧٤٢ - قلت الرجل يكون عِنْده وَدِيعَة فينفقها وَيدْفَع مثلهَا إِلَى صَاحبهَا هَل يطيب لَهُ ربحها فَإِن أعلمهُ وأحله لَهُ
قَالَ إِذا كَانَت عِنْد رجل وَدِيعَة لم ينفقها إِلَّا بِإِذن رَبهَا فَإِن اتّجر فِيهَا فَالرِّبْح لصَاحِبهَا إِلَّا أَن يطيبه لَهُ
<span data-type="title" id=toc-1169>من قَالَ هَذَا الطَّعَام عَليّ حرَام</span>

١٧٤٣ - قلت الرجل يَقُول هَذَا الطَّعَام عَليّ حرَام إِن أكلت مِنْهُ شَيْئا
قَالَ يكفر إِذا أكل','3','246',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1083,'<span data-type="title" id=toc-1170>إِذا حدث رجل نَفسه بِمَا إِن سكت يخَاف أَن يكون أشرك</span>

١٧٤٤ - قلت الرجل يحدث نَفسه بِمَا إِن سكت يخَاف أَن يكون قد أشرك وَذهب دينه
قَالَ يرْوى عَن النَّبِي صلى الله عَلَيْهِ وَسلم إِن الله تجَاوز لأمتي عَمَّا حدثت بِهِ أَنْفسهَا مَا لم تعْمل بِهِ أَو تَتَكَلَّم فَإِذا حدث نَفسه بِشَيْء صرف ذَلِك عَن نَفسه
<span data-type="title" id=toc-1171>إِذا علق الطَّلَاق على خُرُوج الْمَرْأَة من الْبَيْت فأخرجت رجلهَا</span>

١٧٤٥ - قلت إِذا حلف الرجل على امْرَأَته فَقَالَ أَنْت طَالِق ثَلَاثًا إِن خرجت من الْبَيْت فأخرجت رجلهَا
حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا مُحَمَّد بن يزِيد قَالَ أخبرنَا أَيُّوب يَعْنِي أَبَا الْعَلَاء القصاب عَن قَتَادَة فِي رجل قَالَ لإمرأته إِن دخلت بَيت فلَان فَهِيَ طَالِق فتناولت شَيْئا من الْبَيْت قَالَ لَيْسَ بِطَلَاق حَتَّى تدخل','3','247',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1084,'قَالَ وَقَالَ حَمَّاد إِذا أدخلت يَدهَا أَو شَيْئا من جَسدهَا فِيهِ طَالِق
حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا بن بكر قَالَ حَدثنَا سعيد عَن قَتَادَة فِي رجل قَالَ لإمرأته إِن دخلت دَار فلَان فَأَنت طَالِق فأدخلت رَأسهَا قَالَ لَيْسَ ذَلِك بِدُخُول حَتَّى تدخل رِجْلَيْهَا فَإِذا أدخلت رِجْلَيْهَا فقد دخلت إِنَّمَا يَقع بِالرجلَيْنِ
حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا أَبُو الْمُغيرَة قَالَ حَدثنَا صَفْوَان قَالَ حَدثنَا بن جُبَير بن نفير عَن أَبِيه عَن عَوْف بن مَالك الْأَشْجَعِيّ قَالَ أتيت النَّبِي صلى الله عَلَيْهِ وَسلم فَسلمت عَلَيْهِ فَقَالَ عَوْف فَقلت نعم قَالَ ادخل قلت كلي أَو بَعْضِي قَالَ بل كلك قَالَ اعدد يَا عَوْف سِتا فَذكر الحَدِيث','3','248',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1085,'قَالَ ابي مَا أجتري أَن أُفْتِي فِيهَا وَكَأَنِّي أميل إِلَى أَن لَا تطلق حَتَّى تدخل كلهَا لَكِن إِذا قَالَ يدك طَالِق أَو رجلك أَو أصبعك فقد طلقت
<span data-type="title" id=toc-1172>الحكم إِذا طلق بعض تَطْلِيقَة</span>

١٧٤٦ - وَإِذا قَالَ أَنْت طَالِق نصف تَطْلِيقَة أَو ثلث أَو ربع فَهِيَ وَاحِدَة على الْكَمَال يرْوى عَن الْحسن وَالشعْبِيّ وَعمر بن عبد العزيز يَعْنِي مَذْهَبهم إِذا نطق نصف أَو ربع أَو سدس فَهِيَ وَاحِدَة
١٧٤٧ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرزاق قَالَ حَدثنَا','3','249',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1086,'معمر عَن قَتَادَة عَن الْحسن قَالَ إِذا كَانَ للرجل أَربع نسْوَة فَقَالَ اقتسمن بينكن تَطْلِيقَة أَو اثْنَتَيْنِ أَو ثَلَاثًا أَو أَرْبعا فقد طلق كُلهنَّ بتطليقة حَتَّى يَقُول خمْسا أَو سِتا أَو سبعا أَو ثمانيا فَإِن قَالَ ذَلِك طلقن كُلهنَّ تَطْلِيقَتَيْنِ حَتَّى يَقُول اقسمن بينكن تسعا أَو فَوق ذَلِك فَإِذا قَالَ ذَلِك طلقن
١٧٤٨ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرزاق عَن معمر عَن قَتَادَة قَالَ إِذا قَالَ أَنْت طَالِق سدس تَطْلِيقَة أَو ربع أَو خمس أَو بعض تَطْلِيقَة فَهِيَ وَاحِدَة وَإِذا قَالَ أصبعك طَالِق فقد وَقع الطَّلَاق عَلَيْهَا
١٧٤٩ - قَالَ وَسُئِلَ قَتَادَة عَن رجل قَالَ لإمرأته أَنْت طَالِق ملْء بَيت فَفرق بَينهمَا','3','250',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1087,'<span data-type="title" id=toc-1173>إِنْكَار عَطاء من كِتَابَة الحَدِيث</span>

١٧٥٠ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا وَكِيع عَن مِسْكين قَالَ وَكِيع أَبُو هُرَيْرَة التَّيْمِيّ وَكَانَ ثبتا قَالَ كُنَّا جُلُوسًا عِنْد عَطاء وَنحن نسائله إِذا جَاءَ رجل بِصَحِيفَة فَقَالَ يَا أَبَا مُحَمَّد إِنِّي من أَرض شاسعة قَلِيل علماؤها فَأَنا أحب أَن تكْتب لي مَا سَمِعت من أَصْحَاب النَّبِي صلى الله عَلَيْهِ وَسلم الَّذِي حَدثهمْ بِهِ مِمَّا أَمرهم بِهِ ونهاهم عَنهُ لَيْسَ فِي الْقُرْآن قَالَ فَقَالَ لَهُ عَطاء وترضى بِمَا قَالَ أَصْحَاب رَسُول الله صلى الله عَلَيْهِ وَسلم قَالَ وَمَالِي لَا أرْضى قَالَ فَإِن رَسُول الله صلى الله عَلَيْهِ وَسلم لما مضى اجْتمع أَصْحَابه فَقَالُوا نكتب مَا أمرنَا بِهِ ونهينا عَنهُ مِمَّا لَيْسَ فِي الْقُرْآن قَالَ ثمَّ أجمع رَأْيهمْ على أَن بني إِسْرَائِيل إِنَّمَا تَفَرَّقت فِي الْكتب فلست بكاتب لَك','3','251',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1088,'<span data-type="title" id=toc-1174>حكم من قَالَ لزوجته أَنْت طَالِق ثَلَاثًا للسّنة</span>

١٧٥١ - قَالَ أبي إِذا قَالَ لإمرأته أَنْت طَالِق ثَلَاثًا للسّنة وَلم يدْخل بهَا فَلَا يعلم لهَذِهِ سنة فَهُوَ يَقع عَلَيْهَا الثَّلَاث جَمِيعًا واستثناؤه السّنة لَيْسَ بِشَيْء فَإِذا قَالَ لَهَا ذَلِك وَقد دخل بهَا فقد قيل إِن ابْن عمر طلق امْرَأَته حَائِضًا فَسَأَلَ عمر النَّبِي صلى الله عَلَيْهِ وَسلم فَقَالَ مره فَلْيُرَاجِعهَا حَتَّى إِذا طهرت وحاضت ثمَّ طهرت فَلْيُطَلِّقهَا قبل أَن يُجَامِعهَا فَتلك الْعدة الَّتِي أَمر الله بهَا أَن يُطلق لَهَا النِّسَاء فاحتج مُحْتَج بِأَن النَّبِي صلى الله عَلَيْهِ وَسلم لما أمره أَن يطلقهَا طَاهِرا من قبل أَن يُجَامِعهَا فَذَلِك الطَّلَاق فِي ذَلِك الْوَقْت هُوَ السّنة وَلم يَأْمُرهُ النَّبِي صلى الله عَلَيْهِ وَسلم بِوَاحِدَة وَلَا اثْنَتَيْنِ وَلَا ثَلَاثًا فَمَتَى جَاءَ بِالْوَقْتِ الَّذِي أَمر بِهِ النَّبِي صلى الله عَلَيْهِ وَسلم ابْن عمر أَن يطلقهَا فِيهِ فقد جَاءَ بِالسنةِ واستثناؤه بِالسنةِ بَاطِل لِأَنَّهُ قد جَاءَ بِالْوَقْتِ الَّذِي أَمر بِهِ النَّبِي صلى الله عَلَيْهِ وَسلم ابْن عمر فَيَقَع الثَّلَاث جَمِيعًا وَلَا يكون اسْتثِْنَاء بِشَيْء
<span data-type="title" id=toc-1175>الحكم إِذا سرق العَبْد من غير سَيّده</span>

١٧٥٢ - قلت العَبْد إِذا سرق من غير مَال سَيّده','3','252',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1089,'١٧٥٣ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم عَن أبي بشر عَن يُوسُف بن مَاهك أَن غُلَاما لبَعض أهل مَكَّة سرق رِدَاء لِصَفْوَان بن أُميَّة فَأتى بِهِ النَّبِي صلى الله عَلَيْهِ وَسلم فَأمر بِقطعِهِ فَقَالَ يَا رَسُول الله تقطعه من أجل ثوبي خل عَنهُ قَالَ فَهَلا قبل أَن تَأتِينِي بِهِ فَقَطعه
١٧٥٤ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا هشيم عَن أبي بشر عَن يُوسُف بن مَاهك أَن غُلَاما لبَعض أهل مَكَّة سرق رِدَاء لِصَفْوَان بن أُميَّة فَأتى بِهِ النَّبِي صلى الله عَلَيْهِ وَسلم فَأمره بِقطعِهِ
١٧٥٥ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرحمن بن مهْدي قَالَ حَدثنَا شريك عَن أبي إِسْحَاق أَن رجلا ادّعى على عَبده أَنه سرق فرفعه إِلَى شُرَيْح فاعترف فَقَالَ هُوَ عبد لَا يجوز إعترافه
١٧٥٦ - حَدثنَا صَالح قَالَ حَدثنِي أبي قَالَ حَدثنَا عبد الرحمن بن مهْدي عَن عبد الله بن الْمُبَارك عَن معمر عَن الزُّهْرِيّ قَالَ دخلت على عمر بن','3','253',NULL,NULL,'0');
INSERT INTO [b6106] VALUES (1090,'عبد الْعَزِيز قَالَ مَا بلغك فِي العَبْد الْآبِق إِذا سرق قَالَ قلت لَا أَدْرِي قَالَ كَانَ عُثْمَان ومروان لَا يقطعانه قَالَ فَقدمت الْمَدِينَة فَحَدثني سَالم بن عبد الله أَن ابْن عمر قطع عبدا لَهُ آبق سرق','3','254',NULL,NULL,'0');
