CREATE TABLE "b6371"(id INTEGER PRIMARY KEY,content TEXT, part TEXT, page TEXT, number TEXT, services TEXT, is_deleted TEXT);

-- Insert data for b6371
INSERT INTO [b6371] VALUES (1,'الْحَمد لله مُجيب الداعين ومثيب الساعين ومعطي الطالبين ومرضي الراغبين ومنجد الهالكين ومرشد السالكين رَحِيم بِالْمُؤْمِنِينَ رحمان تعم رحمتهاع الطائعين والعاصين أَحْمَده سُبْحَانَهُ وَتَعَالَى وأشكره وَأَتُوب إِلَيْهِ وأستغفره وَأشْهد أَن لَا إِلَه إِلَّا الله وَحده لَا شريك لَهُ شَهَادَة نبلغ بهَا أحسن المآب وَأشْهد أَن سيدنَا مُحَمَّدًا عَبده وَرَسُوله الْمَبْعُوث لتشريع الْملَّة وتوجيه الْخطاب وإيضاح الْأَحْكَام من الْمُبَاح',NULL,'57',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (2,'وَالْمَنْدُوب والمحظور والمفروض وَالْوَاجِب والاستحباب وَالْأَمر بأدائها بالالتزام والإيجاب صلى الله عَلَيْهِ وعَلى آله وصحبة صَلَاة وَسلَامًا دائمين إِلَى يَوْم الْجَزَاء وَفصل الْخطاب',NULL,'58',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (3,'<span data-type="title" id=toc-1>الصِّفَات</span>

١ -<span data-type="title" id=toc-2> فصل</span>
صانع',NULL,'59',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (4,'الْعَالم لَيْسَ بحادث فَلَو كَانَ حَادِثا فَلَا بُد لَهُ من صانع أحدثه ومبدع أنشأه وَذَلِكَ هُوَ الله تَعَالَى وَهُوَ المبدئ المنشئ تبَارك الله رب',NULL,'60',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (5,'الْعَالمين
٢ -<span data-type="title" id=toc-3> فصل</span>
صانع الْعَالم مَوْجُود من نظر فِي عجائب خلق السَّمَوَات',NULL,'61',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (6,'وَالْأَرْض وبدائع فطْرَة الْحَيَوَان يعلم أَن تِلْكَ الْأُمُور الْعَجَائِب وَذَاكَ الصنع البديع وَالتَّرْتِيب الْمُحكم لَا بُد لَهُ من صانع يدبره ويحكمه ويفرده
فيستدل بِوُجُود المصنوعات على وجود الصَّانِع قَالَ الله تَعَالَى {أَفِي الله شكّ فاطر السَّمَاوَات وَالْأَرْض}

٣ -<span data-type="title" id=toc-4> فصل</span>
معرفَة الصَّانِع وَاجِبَة لِأَنَّهُ منعم وشكر الْمُنعم وَاجِب عقلا وَشرعا فَأول دَرَجَة الشُّكْر معرفَة الْمُنعم',NULL,'62',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (7,'<span data-type="title" id=toc-5>الصِّفَات السلبية</span>

<span data-type="title" id=toc-6>أَولا الوحدانية</span>',NULL,'63',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (8,'٤ -<span data-type="title" id=toc-7> فصل</span>

صانع الْعلم وَاحِد لَا شريك لَهُ لِأَنَّهُ لَو كَانَ لَهُ صانعان أَو أَكثر لوقع بَينهمَا تمانع وتدافع وَذَلِكَ خفض إِلَى الْفساد وَيُؤَدِّي إِلَى',NULL,'64',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (9,'عجز أَحدهمَا وَالْعَاجِز لَا يصلح أَن يكون إِلَهًا فَإِذا تعذر إِثْبَات صانعين كَانَ وَاحِدًا ضَرُورَة
<span data-type="title" id=toc-8>ثَانِيًا الْقدَم</span>

٥ -<span data-type="title" id=toc-9> فصل</span>

صانع الْعَالم قديم لَا أول لَهُ لِأَنَّهُ لَو كَانَ مُحدثا لاقتضى',NULL,'65',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (10,'مُحدثا ثمَّ كَذَلِك محدثه اقْتضى مُحدثا آخر فيتسلسل إِلَى مَا لَا نِهَايَة لَهُ فَثَبت أَن صانع الْعَالم قديم
<span data-type="title" id=toc-10>ثَالِثا الْبَقَاء</span>

٦ -<span data-type="title" id=toc-11> فصل</span>

صانع الْعَالم ابدي لَا آخر لَهُ لِأَن من ثَبت قدمه اسْتَحَالَ',NULL,'66',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (11,'عَدمه وَلِأَن وجوده وَاجِب وَوُجُوب وجوده يمْنَع عدم بَقَائِهِ
<span data-type="title" id=toc-12>رَابِعا الْمُخَالفَة للحوادث</span>

٧ -<span data-type="title" id=toc-13><span data-type="title" id=toc-14> فصل</span></span>

صانع الْعَالم لَيْسَ بجوهر لِأَن الْجَوْهَر متجزئ وتحله الْحَوَادِث تَعَالَى الله عَن ذَلِك علوا كَبِيرا
٨ - فصل

صانع الْعَالم لَيْسَ بجسم لِأَن الْجِسْم مؤلف من الْجَوْهَر',NULL,'67',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (12,'وَإِذا بَطل كَونه جوهرا بَطل كَونه جسما ضَرُورَة
٩ -<span data-type="title" id=toc-15><span data-type="title" id=toc-16> فصل</span></span>

صانع الْعَالم لَيْسَ بِعرْض لِأَن الْعرض لَا قيام لَهُ بِذَاتِهِ بل هُوَ مفتقر إِلَى جسم يقوم بِهِ وَالْقَدِيم عز وَجل قَائِم بِذَاتِهِ غير مفتقر إِلَى مَحل يقوم بِهِ
١٠ - فصل

صانع الْعَالم لَيْسَ بِصُورَة لِأَن الصُّورَة تنشأ عَن التَّرْكِيب',NULL,'68',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (13,'فَإِذا نَفينَا كَونه جوهرا وجسما نَفينَا كَونه صُورَة
١١ -<span data-type="title" id=toc-17> فصل</span>

صانع الْعَالم لَا يُوصف باللون والطعم والرائحة والحرارة والبرودة والرطوبة واليبوسة لِأَن الألوان والطعوم والحرارة والبرودة والروائح والطبائع الْأَرْبَعَة أَعْرَاض تحل فِي الْجَوَاهِر فَإِذا نَفينَا كَونه عرضا وَكَونه محلا للأعراض يَنْتَفِي جَمِيع ذَلِك
<span data-type="title" id=toc-18>خَامِسًا الْقيام بِالنَّفسِ</span>

١٢ -<span data-type="title" id=toc-19> فصل صانع الْعَالم لَيْسَ فِي جِهَة وَلَا تحويه الْجِهَات السِّت</span>',NULL,'69',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (14,'لِأَنَّهَا حَادِثَة وَهُوَ الَّذِي خلقهَا فَلَو صَار مُخْتَصًّا بِجِهَة بَعْدَمَا خلقهَا لَكَانَ يتخصص بمخصص وَذَلِكَ بَاطِل
١٣ -<span data-type="title" id=toc-20> فصل</span>

صانع الْعَالم لَيْسَ فَوق الْعَالم وَلَا فِي جِهَة خَارجه عَنهُ',NULL,'70',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (15,'لِأَنَّهُ لَو كَانَ كَذَلِك لَكَانَ محاذيا للْعَالم وكل محاذ بجسم إِمَّا أَن يكون مثله أَو أكبر أَو اصغر وَكَانَ ذَلِك تَقْديرا يحْتَاج إِلَى مُقَدّر تَعَالَى عَن ذَلِك
١٤ -<span data-type="title" id=toc-21> فصل</span>

رفع الْأَيْدِي إِلَى السَّمَاء عِنْد الدُّعَاء إِنَّمَا ترفع لِأَنَّهَا قبْلَة الدُّعَاء كالتوجه إِلَى الْكَعْبَة فِي الصَّلَاة وَوضع الْوَجْه على الأَرْض عِنْد السُّجُود وَإِن لم يكن الله عز وَجل فِي الْكَعْبَة وَلَا تَحت الأَرْض',NULL,'71',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (16,'١٥ -<span data-type="title" id=toc-22> فصل</span>

صانع الْعَالم لَا يُوصف بِكَوْنِهِ مُتَمَكنًا فِي مَكَان لِأَنَّهُ كَانَ فِي الْأَزَل غير مُتَمَكن فَلَو تمكن بَعْدَمَا خلق الْمَكَان لتغير عَمَّا كَانَ تعالي الله عَن ذَلِك',NULL,'72',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (17,'<span data-type="title" id=toc-23>الاسْتوَاء</span>',NULL,'73',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (18,'١٦ -<span data-type="title" id=toc-24> فصل</span>

استواؤه على الْعَرْش حق وَصدق وَنحن نؤمن ونعتقد على الْوَجْه الَّذِي أَرَادَهُ وَلَا نشتغل بكيفيته',NULL,'75',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (19,'<span data-type="title" id=toc-25>النُّزُول</span>

١٧ -<span data-type="title" id=toc-26> فصل</span>

نُزُوله إِلَى السَّمَاء الدُّنْيَا تفضل وَرَحْمَة لَا نقلة',NULL,'76',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (20,'وحركة لما ذكرنَا
١٨ -<span data-type="title" id=toc-27> فصل</span>

وَله يدان هما صفته يخلق بهما مَا يَشَاء وهما يَد خلق',NULL,'78',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (21,'وقدرة لَا يَد بَطش وجارحة لما ذكرنَا
١٩ -<span data-type="title" id=toc-28><span data-type="title" id=toc-29> فصل</span></span>
وَله وَجه هُوَ صفته وَهُوَ وَجه إكرام وإقبال لَا وَجه مُقَابلَة ومواجهة لما ذكرنَا
٢٠ - فصل

صانع الْعَالم لَا يشبه الْعَالم',NULL,'79',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (22,'وَلَا شَيْئا مِنْهُ لِأَنَّهُ لَو كَانَ يُشبههُ للَزِمَ حُدُوثه أَو قدم الْعَالم وَكِلَاهُمَا منتفيان
٢١ -<span data-type="title" id=toc-30> فصل</span>
صانع الْعَالم لَا يُقَال لَهُ مَا هُوَ لِأَن مَا سُؤال عَن الْجِنْس وَلَا جنس',NULL,'80',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (23,'لَهُ
٢٢ -<span data-type="title" id=toc-31><span data-type="title" id=toc-32><span data-type="title" id=toc-33> فصل</span></span></span>
صانع الْعَالم لَا يُقَال كَيفَ هُوَ لِأَن الكيف يستخبر بِهِ عَن الْهَيْئَة وَالْحَال وَلَا هَيْئَة لَهُ وَلَا حَال
٢٣ - فصل

صانع الْعَالم لَا يُقَال لَهُ أَيْن هُوَ لِأَن أَيْن يستخبر بِهِ عَن الْمَكَان وَلَا مَكَان لَهُ
٢٤ - فصل

صانع الْعَالم لَا يُقَال لَهُ كم هُوَ لِأَن الْكمّ يستخبر بِهِ',NULL,'81',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (24,'عَن الْعدَد وَلَا عدد لَهُ
٢٥ -<span data-type="title" id=toc-34> فصل</span>
صانع الْعَالم لَا يُقَال لَهُ مَتى كَانَ لِأَن مَتى سُؤال عَن',NULL,'82',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (25,'الزَّمَان وَلَا يجْرِي عَلَيْهِ زمَان
٢٦ -<span data-type="title" id=toc-35> فصل</span>
صانع الْعَالم لَا يُقَال لَهُ لم فعل لِأَن لم يُقَال لمن فعل',NULL,'83',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (26,'لعِلَّة أَو حَاجَة أَو ضَرُورَة وَهُوَ منزه عَن ذَلِك
٢٧ -<span data-type="title" id=toc-36> فصل</span>

صانع الْعَالم لَا نزُول لَهُ وَلَا صعُود لَهُ وَلَا الْتِفَات وَلَا تفكر وَلَا حَاجَة وَلَا شَهْوَة وَلَا نوم وَلَا سنة وَلَا آفَة وَلَا علل وَلَا سرُور وَلَا حزن وَلَا رضى وَلَا غضب بِمَعْنى التَّغَيُّر فِي ذَاته وَلَا رَجَاء',NULL,'84',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (27,'وَلَا طمع وَلَا حسد وَلَا أكل وَلَا شرب وَلَا قيام وَلَا قعُود وَلَا',NULL,'85',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (28,'مشي وَلَا عَدو وَلَا هرولة وَلَا استناد وَلَا اتكاء وَلَا اضطجاع وَلَا ضحك وَلَا تَبَسم وَلَا قهقهة وَلَا قرب وَلَا بعد بِمَعْنى الْمسَافَة',NULL,'86',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (29,'وَالْمَكَان وَلَا وَزِير لَهُ وَلَا شريك وَلَا مُدبر لَهُ وَلَا نَظِير لَهُ وَلَا معِين',NULL,'87',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (30,'وَلَا قرين وَلَا حَاجِب وَلَا بواب وَلَا فَوق وَلَا تَحت وَلَا يَمِين وَلَا يسَار وَلَا أَمَام وَلَا وَرَاء وَلَا خاطر وَلَا رَأْي وَلَا حَظّ فِيمَا أعْطى وَلَا نَدم فِيمَا وهب لِأَن هَذِه الْأَشْيَاء من أَمَارَات الْحُدُوث وَهُوَ قديم منزه عَن جَمِيع الحادثات وَعَن تغيره من حَال إِلَى حَال تبَارك الله رب الْعَالمين
٢٨ -<span data-type="title" id=toc-37> فصل</span>

وَلَا وَالِد لَهُ وَلَا ولد وَلَا صَاحِبَة',NULL,'88',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (31,'لِأَن الْوَالِد سَبَب لحدوث الْوَلَد وَهُوَ قديم لَا يحدث لَهُ وَالْولد جُزْء الْوَالِد وَهُوَ إحدي الذَّات صمدي الصِّفَات',NULL,'89',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (32,'لَا يقبل التجزء والانقسام وَالزَّوْجَة لمن جارت عَلَيْهِ الشَّهْوَة وَهُوَ سُبْحَانَهُ وَتَعَالَى منزه عَنْهَا
٢٩ -<span data-type="title" id=toc-38> فصل</span>

صانع الْعَالم لَا عِلّة لصنعه وَلَا فِي أَفعاله وَلَا زيغ فِي أَحْكَامه وَلَا ميل فِي قَضَائِهِ وَقدره لِأَنَّهُ يُوصف بِصفة الْعدْل',NULL,'90',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (33,'وَالْفضل
٣٠ -<span data-type="title" id=toc-39> فصل</span>
صانع الْعَالم لَا يقدره فهم وَلَا يصوره وهم وَلَا يُدْرِكهُ بصر وَلَا عقل وَلَا يبلغهُ علم وَلَا يقوم بِذَاتِهِ حَادث وَلَا يدْخل فِي',NULL,'91',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (34,'صِفَاته تغير وكل مَا خطر ببالك كَذَلِك فَهُوَ قَادر على أَن يخلق ذَلِك وَأَمْثَاله فَتَبَارَكَ رب الْعَالمين',NULL,'92',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (35,'<span data-type="title" id=toc-40>صفة الْحَيَاة</span>

٣١ -<span data-type="title" id=toc-41> فصل</span>
صانع الْعَالم حَيّ بحياة أزلية لَا بِروح بداخله وَلَا نفس يخرج مِنْهُ لِأَن وجود هَذَا الْعَالم البديع صَنعته لن يتَصَوَّر إِلَّا من حَيّ قَادر',NULL,'93',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (36,'<span data-type="title" id=toc-42>الْعلم</span>

٣٢ -<span data-type="title" id=toc-43> فصل</span>
صانع الْعَالم عَالم بِجَمِيعِ المعلومات كليتها وجزئياتها',NULL,'94',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (37,'لَا يعزب عَن علمه مِثْقَال ذرة فِي السَّمَوَات الْعلَا وَلَا فِي الْأَرْضين السُّفْلى لِأَنَّهُ لَو لم يكن عَالما لَكَانَ مَوْصُوفا بضده وَهُوَ الْجَهْل وَذَلِكَ نقص تَعَالَى الله عَن ذَلِك علوا كَبِيرا',NULL,'95',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (38,'<span data-type="title" id=toc-44>الْقُدْرَة</span>

٣٣ -<span data-type="title" id=toc-45> فصل</span>

صانع الْعَالم قَادر بقدرة كَامِلَة لِأَن حُصُول الْأَفْعَال المحكمة المتقنة لن يتَصَوَّر وجودهَا إِلَّا نم قَادر قدير',NULL,'96',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (39,'<span data-type="title" id=toc-46>الْإِرَادَة</span>

٣٤ -<span data-type="title" id=toc-47> فصل صانع الْعَالم مُرِيد الكائنات مُدبر الحادثات لِأَنَّهُ لَو لم يكن مريدأ وَلَا تحدث الْأَشْيَاء إِلَّا بإرادته ومشيئته لَكَانَ مُضْطَرّا وَهُوَ أَمارَة الْعَجز تَعَالَى الله عَن ذَلِك</span>',NULL,'97',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (40,'<span data-type="title" id=toc-48>السّمع وَالْبَصَر</span>

٣٥ -<span data-type="title" id=toc-49> فصل</span>

صانع الْعَالم سميع بَصِير لِأَن السّمع وَالْبَصَر هما',NULL,'98',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (41,'صفتا مدح وَكَمَال فنفيهما نقص تَعَالَى الله عَن ذَلِك',NULL,'99',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (42,'<span data-type="title" id=toc-50>الْكَلَام</span>

٣٦ -<span data-type="title" id=toc-51> فصل</span>
صانع الْعَالم مُتَكَلم لِأَنَّهُ لَو لم يكن متكلما لَكَانَ مَوْصُوفا',NULL,'100',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (43,'بضده وَهُوَ الخرس تَعَالَى الله عَن ذَلِك
٣٧ -<span data-type="title" id=toc-52> فصل</span>

وَله كَلَام لِأَن الْأَمر وَالنَّهْي لَا يتم إِلَّا بالْكلَام وَكَلَامه قديم لِأَنَّهُ لَو لم يكن قَدِيما لَكَانَ الله تَعَالَى فِي الْأَزَل متغيرا عَن الْكَلَام تَعَالَى الله عَن ذَلِك وَكَلَامه غير مَخْلُوق لِأَنَّهُ لَو كَانَ مخلوقا لَكَانَ الله تَعَالَى الله عَن ذَلِك وَكَلَامه غير مَخْلُوق لِأَنَّهُ لَو كَانَ مخلوقا لَكَانَ الله تَعَالَى محلا للحوادث تَعَالَى الله عَن ذَلِك وَكَلَامه قَائِم بِذَاتِهِ لَا يقبل الِانْفِصَال عَنهُ والافتراق بالانتقال الى الْقُلُوب والأوراق لِأَنَّهُ كَلَامه وَكَلَامه',NULL,'101',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (44,'صفته وَصِفَاته قَائِمَة بِذَاتِهِ لَا تقبل الِانْفِصَال عَنهُ والافتراق وَهَذِه الْعبارَات دَالَّة على كَلَامه الْقَدِيم الأزلي الْقَائِم بِذَاتِهِ وَتسَمى الْعبارَات كَلَام الله تَعَالَى وَهِي محدثة مخلوقة وَهِي الْحُرُوف والأصوات وتتابع الْحُرُوف والكلمات وَهِي قَائِمَة بِذَاتِهِ بمحلها وَغير مخلوقة يعبر بِمَا هُوَ الْمَخْلُوق دلّ عَلَيْهِ قَول الشَّاعِر
(إِن الْكَلَام لفي الْفُؤَاد وَإِنَّمَا ... جعل اللِّسَان على الْفُؤَاد دَلِيلا)
٣٨ -<span data-type="title" id=toc-53> فصل</span>

وَكَلَامه لَيْسَ بِحرف لِأَن الْحُرُوف فِي أَنْفسهَا متضادة',NULL,'102',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (45,'وَلَا تُوجد دفْعَة وَاحِدَة إِلَّا متعاقبة وَذَلِكَ يُوجب حُدُوثه وَكَلَامه قديم
٣٩ -<span data-type="title" id=toc-54><span data-type="title" id=toc-55> فصل</span></span>
وَكَلَامه لَيْسَ بِصَوْت لِأَن الْأَصْوَات يدْرك تجانسها بِالْجِنْسِ فَلَو أَن كَلَامه صَوتا لَكَانَ جِنْسا من هَذِه الْأَصْوَات وَذَلِكَ محَال لاقْتِضَائه الْحَدث وَكَلَامه لَيْسَ بعربي وَلَا بسرياني وَلَا بعبراني لِأَن هَذِه اللُّغَات أَوْصَاف للفظ الْمركب من الْحُرُوف وَكَلَامه لَيْسَ بِحرف
٤٠ - فصل

وَقِرَاءَة كَلَام الله بِالْعَرَبِيَّةِ تسمى قُرْآنًا وبالسريانية تسمى إنجيلا وبالعبرانية تسمى توراة وَيكون الْكل كَلَام الله عز وَجل على معنى أَنه يُتْلَى بلغتهم',NULL,'103',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (46,'٤١ -<span data-type="title" id=toc-56><span data-type="title" id=toc-57> فصل </span>الْقُرْآن كَلَام الله تَعَالَى غير مَخْلُوق وَهُوَ مَكْتُوب فِي الْمَصَاحِف مقروء بالألسنة مَحْفُوظ فِي الْقُلُوب غير حَال فِيهَا كَمَا تَقول إِن الله تَعَالَى مَذْكُور بالألسن مَعْلُوم فِي الْقُلُوب معبود فِي الْمَسَاجِد غير حَال فِيهَا</span>
فَالْمُرَاد بقولنَا إِن الْقُرْآن كَلَام الله تَعَالَى المقروء دون الْقِرَاءَة الَّتِي هِيَ فعل العَبْد لِأَن الْقُرْآن فِي اللُّغَة وَإِن كَانَ عبارَة عَن الْقِرَاءَة حَقِيقَة لَكَانَ جَازَ أَن يذكر وَيُرَاد بِهِ المقروء
وعَلى هَذَا قَالَ مَشَايِخنَا لَا يجوز أَن يُقَال الْقُرْآن غير مَخْلُوق وَلَكِن يجب أَن يُقَال الْقُرْآن الَّذِي هُوَ كَلَام الله غير مَخْلُوق
٤٢ - فصل

وَالْكَلَام وَاحِد كَالْعلمِ وَالْقُدْرَة والإرادة لِأَن الْوَاحِد',NULL,'104',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (47,'لَا بُد لَهُ من إِثْبَات وَالْعدَد يتعارض القَوْل فِيهِ بِلَا عدد أَو من عدد
٤٣ -<span data-type="title" id=toc-58><span data-type="title" id=toc-59><span data-type="title" id=toc-60><span data-type="title" id=toc-61> فصل</span></span></span></span>

وتسميه كَلَامه قُرْآنًا وتوراة وإنجيلا وزبورا لَا يَقْتَضِي كَثْرَة الْكَلَام كَمَا أَن الله عز وَجل يُسمى بِالْعَرَبِيَّةِ الله وبالعجمية خداي وبالتركية تكري وَهُوَ وَاحِد فَكَذَا كَلَامه
٤٤ - فصل

وَكَلَامه أَمر وَنهي وَخبر ونداء ووعد ووعيد وقصص وأمثال وموعظة وَهُوَ كَلَام وَاحِد
٤٥ - فصل

وَكَلَامه لَا يجوز أَن يسمع على الْمَعْنى الَّذِي ذَكرْنَاهُ
٤٦ - فصل

إِذا ثَبت أَن الْبَارِي سُبْحَانَهُ وَتَعَالَى حَيّ عَالم قَادر مُرِيد سميع بصر مُتَكَلم ثَبت أَن لَهُ حَيَاة وعلما وقدرة وَإِرَادَة وسمعا وبصيرا وكلاما إِذْ القَوْل بعالم لَا علم لَهُ وقادر لَا قدرَة لَهُ كالقول بمتحرك لَا حَرَكَة لَهُ وَسَاكن لَا سُكُون لَهُ وكالقول بِأَن الله لَا علم لَهُ بِنَا وَلَا قدرَة لَهُ علينا وَهَذَا شنيع محَال',NULL,'105',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (48,'٤٧ -<span data-type="title" id=toc-62> فصل</span>
وَعلمه لَيْسَ بكسبي وَلَا ضَرُورِيّ لِأَن ذَلِك من أَمَارَات الْحُدُوث',NULL,'106',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (49,'<span data-type="title" id=toc-63>الْأَسْمَاء</span>

٤٨ -<span data-type="title" id=toc-64> فصل</span>
وَأَسْمَاء الله تَعَالَى صِفَاته وَهُوَ قَوْله عز وَجل وَللَّه',NULL,'107',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (50,'الْأَسْمَاء الْحسنى) أَي صِفَاته الْعلَا
٤٩ -<span data-type="title" id=toc-65> فصل</span>
وَأَسْمَاء الله عز وَجل تُؤْخَذ توقيفا وَلَا يجوز أَخذهَا قِيَاسا',NULL,'108',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (51,'٥٠ -<span data-type="title" id=toc-66><span data-type="title" id=toc-67><span data-type="title" id=toc-68> فصل</span></span></span>
وَصِفَاته لَيست بأعراض لِأَن الْعرض لَا يَدُوم وجوده وَصِفَاته بَاقِيَة بِبَقَائِهِ فبقاؤه بَقَاء لَهُ وللصفات
٥١ - فصل

وَصِفَاته مُخْتَصَّة بِذَاتِهِ لَا يُقَال هِيَ هُوَ وَلَا بعضه لَا أغيار لَهُ لِأَن حَقِيقَة الغيرين يجوز وجود أَحدهمَا مَعَ عدم مصاحبة أَو يجوز مُفَارقَة أَحدهمَا لصَاحبه وَذَلِكَ فِي صِفَاته محَال
٥٢ - فصل
لَا يُقَال لصفاته إِنَّهَا مَعَ الله عز وَجل أَو فِيهِ بل هِيَ مُخْتَصَّة قَائِمَة بِهِ أَو نقُول هِيَ معنى وَرَاء الذَّات قَائِمَة بِهِ',NULL,'109',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (52,'٥٣ -<span data-type="title" id=toc-69><span data-type="title" id=toc-70><span data-type="title" id=toc-71><span data-type="title" id=toc-72> فصل</span></span></span></span>

لَا يُقَال لصفاته إِنَّهَا تخَالفه أَو توافقه لِأَنَّهُ يُؤَدِّي إِلَى الْمُغَايرَة والتغاير بَين الله وَصِفَاته محَال
٥٤ - فصل
صانع الْعَالم لَا يُوصف بالأحوال لِأَن الْأَحْوَال مَا تَزُول فِي الصِّفَات وَذَلِكَ فِي صِفَاته محَال
٥٥ - فصل

إِرَادَة الله عز وَجل نَافِذَة فِي جَمِيع مراداته لَا يجوز أَن يُرِيد كَون الشَّيْء فَلَا يكون أَو يُرِيد أَن لَا يكون شَيْئا فَيكون لِأَن من جرى فِي سُلْطَانه مَا لَا يُرِيد كَانَ سَاهِيا أَو مَغْلُوبًا وَذَلِكَ نقص تَعَالَى الله عَن ذَلِك
٥٦ - فصل

معلوماته ومقدوراته ومراداته لَا نِهَايَة لَهَا لِأَنَّهُ لَو كَانَ لَهَا نِهَايَة لَكَانَ لعلمه نِهَايَة ولانهاية لعلمه',NULL,'110',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (53,'٥٧ -<span data-type="title" id=toc-73><span data-type="title" id=toc-74> فصل</span></span>
صانع الْعَالم قَائِم بِذَاتِهِ مستغن عَمَّا سواهُ لِأَنَّهُ لَو لم يكن قَائِما بِذَاتِهِ لَكَانَ مفتقرا إِلَى غَيره تَعَالَى الله عَن ذَلِك
٥٨ - فصل
صانع الْعَالم عَظِيم الْقدر وَالصّفة لَا يُقَال إِنَّه عَظِيم الذَّات لِأَن العظمة بِالذَّاتِ لَا تكون إِلَّا بِكَثْرَة الْأَجْزَاء وَهُوَ وَاحِد لَا يتَجَزَّأ وَلَا يَنْقَسِم',NULL,'111',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (54,'<span data-type="title" id=toc-75>صفة التكوين</span>

٥٩ -<span data-type="title" id=toc-76> فصل</span>
اعْلَم بِأَن التكوين والتخليق والإيجاد ولإحداث والإبداع والاختراع عبارَة ترجع إِلَى معنى وَاحِد وَهُوَ إِيجَاد الشَّيْء من',NULL,'112',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (55,'الْعَدَم إِلَى الْوُجُود
٦٠ -<span data-type="title" id=toc-77><span data-type="title" id=toc-78><span data-type="title" id=toc-79> فصل</span></span></span>

اعْلَم أَن التكوين غير المكون لِأَن القَوْل بإيجاد التكوين والمكون كالقول بِأَن الضَّرْب عين الْمَضْرُوب وَالْقَتْل عين الْمَقْتُول وَهَذَا محَال
٦١ - فصل

والتكوين صفة الْبَارِي سُبْحَانَهُ وَتَعَالَى لِأَن حُدُوث الْعَالم بتكوينه فَكَانَ هُوَ الْمُحدث والمكون فَيكون التكوين صفته
٦٢ - فصل

والتكوين قَائِم بِذَاتِهِ لِأَنَّهُ لَو لم يكن قَائِما بِذَاتِهِ لَكَانَ قَائِما بِمحل آخر فَيكون المكون الْخَالِق مَا قَامَ بِهِ التكوين وَهَذَا محَال',NULL,'113',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (56,'٦٣ -<span data-type="title" id=toc-80><span data-type="title" id=toc-81> فصل</span></span>

والتكوين صفة أزلية غير حَادِثَة والباري عز وَجل لم يزل مكونا خَالِقًا لِأَنَّهُ كَانَ لَو كام حَادِثا لَكَانَ ذَات الْبَارِي محلا للحوادث تَعَالَى الله عَن ذَلِك علوا كَبِيرا
فَثَبت أَن التكوين غير المكون وَأَنه صفة أزلية بِذَات الْبَارِي كالحياة وَالْعلم وَالْقُدْرَة
٦٤ - فصل

وصفات الْبَارِي كلهَا أزلية قَائِمَة بِذَاتِهِ لَا يُقَال أَن هَذِه من صِفَات الْفِعْل لِأَن فِيهِ جَوَاز الْحُدُوث على',NULL,'114',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (57,'ذَاته وَلَيْسَ مُنْذُ خلق الْخلق اسْتَفَادَ اسْم الْخَالِق وَلَا بإحداثه الْبَريَّة اسْتَفَادَ اسْم الْبَارِي لَهُ معنى الربوبية وَلَا مربوب وَمعنى الْخَالِق وَلَا مَخْلُوق كَمَا أَنه محيي الْمَوْتَى بَعْدَمَا أَحْيَا اسْتحق هَذَا الِاسْم قبل إحيائهم كَذَلِك اسْم الْخَالِق قبل إنشائهم ذَلِك بِأَنَّهُ على كل شَيْء قدير',NULL,'115',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (58,'<span data-type="title" id=toc-82>رُؤْيَة الله تَعَالَى</span>

٦٥ -<span data-type="title" id=toc-83> فصل</span>

صانع الْعَالم مرئي فِي الدَّار الْآخِرَة بالعيون الناظرة من',NULL,'116',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (59,'غير إحاطة وَلَا كَيْفيَّة وَلَا إِدْرَاك وَلَا نِهَايَة لِأَن المجوز للرؤية الْوُجُود فَالله تَعَالَى مَوْجُود فَثَبت جَوَاز رُؤْيَته ضَرُورَة',NULL,'17',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (60,'<span data-type="title" id=toc-84>النبوات</span>

٦٦ -<span data-type="title" id=toc-85> فصل</span>

إرْسَال الرُّسُل لَيْسَ بمتنع عقلا لِأَن الله عز وَجل',NULL,'119',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (61,'خَالق الْخلق ومالكهم فَمن لَهُ الْخلق وَالْأَمر وَالْملك لَهُ أَن يتَصَرَّف فِي ملكه ومماليكه كَمَا يَشَاء فَجَاز أَن يَأْمُرهُم وينهاهم لينتفعوا بذلك وينالوا خير الدُّنْيَا وَالْآخِرَة وَهَذَا مَا لَا اسْتِحَالَة بِهِ أصلا فَجَاز إرسالهم
٦٧ -<span data-type="title" id=toc-86> فصل</span>

إرْسَال الرُّسُل فِي الْحِكْمَة من الْوَاجِبَات لوَجْهَيْنِ
أَحدهمَا أَن شكر نعْمَة الْمُنعم وَاجِب عقلا وَشرعا وَالْعقل لَا يَهْتَدِي لمعْرِفَة ذَلِك بطرِيق التَّفْصِيل إِلَّا بِالسَّمْعِ والسمع بإرسال الرُّسُل فَكَانَ وَاجِبا قَضِيَّة للْحكم
الثَّانِي أَن فِي بعث الرُّسُل إِثْبَات الْحجَّة وَقطع الْحجَّة للْحكم وَتَحْقِيق مَا وعد الله عز وَجل بِالْجنَّةِ وَالنَّار لأَنهم لَو لم يبعثوا لثبت للْكفَّار حجَّة فِي عدم إِيمَانهم كَمَا قَالَ تَعَالَى {رسلًا مبشرين ومنذرين لِئَلَّا يكون للنَّاس على الله حجَّة بعد الرُّسُل وَكَانَ الله عَزِيزًا حكيما} فَإِذا كَانَ بَعثهمْ',NULL,'120',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (62,'لهَذِهِ الْحِكْمَة فَيكون وَاجِبا ونعني بِالْوُجُوب أَن من قَضِيَّة الْحِكْمَة أَن يُوجد لَا محَالة لَا انه يجب على الله تَعَالَى بإيجابه أَو بِإِيجَاب غَيره عَلَيْهِ تَعَالَى عَن ذَلِك علوا كَبِيرا
٦٨ -<span data-type="title" id=toc-87> فصل</span>

رِسَالَة شخص بِعَيْنِه لَيست وَاجِبَة يجوز أَن يكون ذَلِك غَيره فَلَا بُد من دَلِيل يدل عَلَيْهِ',NULL,'121',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (63,'وَذَلِكَ قيام المعجزة فَإِذا قَامَت المعجزة على يَده تعين انه رَسُول الله',NULL,'122',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (64,'٦٩ -<span data-type="title" id=toc-88> فصل</span>

بعث الله تَعَالَى الْأَنْبِيَاء عَلَيْهِم السَّلَام رسلًا مبشرين ومنذرين وَبعث مُحَمَّدًا صلى الله عَلَيْهِ وَسلم رَسُولا وَنَبِيًّا بشيرا وَنَذِيرا وَالدّلَالَة على ذَلِك قيام المعجزات الظَّاهِرَة على يَده كانشقاق الْقَمَر بإشارته ومجيء الشَّجَرَة من موضعهَا إِلَيْهِ عِنْد إِشَارَته إِلَيْهَا وعودها إِلَى مَكَانهَا',NULL,'123',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (65,'وَتَسْلِيم الْحجر عَلَيْهِ وتسبيح الْحَصَاة فِي يَده ونبع المَاء من بَين أَصَابِعه وحنين الأسطوانة',NULL,'124',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (66,'وشكاية النَّاقة إِلَيْهِ وإخبار الشَّاة المصلية إِلَيْهِ عَن السم الَّذِي كَانَ فِيهَا',NULL,'125',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (67,'وإشباعه الْخلق الْكثير من الطَّعَام الْقَلِيل وَكَذَا شرب المَاء الْكثير من الْبشر من المَاء الْقَلِيل والسحاب الَّذِي كَانَ يظله',NULL,'126',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (68,'حَال صغره وَمَا كَانَ من خَاتم النُّبُوَّة بَين كَتفيهِ وَأَنه كَانَ أطيب النَّاس رَائِحَة من الْمسك وإخباره عَن الغيوب فِي',NULL,'127',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (69,'الْمَاضِي والمستقبل كَانَ كَمَا أخبر مَعَ انه كَانَ أُمِّيا وَبشَارَة عِيسَى عَلَيْهِ السَّلَام',NULL,'128',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (70,'ببعثه صلى الله عَلَيْهِ وَسلم وَغير ذَلِك مِمَّا لَا يُحْصى وَلَا يعد',NULL,'129',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (71,'وَمن معجزاته صلى الله عَلَيْهِ وَسلم الْيَوْم بَيْننَا الْقُرْآن الْكَرِيم فان الْعَرَب',NULL,'130',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (72,'بأسرهم مَعَ فصاحتهم وبلاغتهم وتمييزهم عجزوا عَن الْإِتْيَان بِمثلِهِ أَو بِسُورَة من مثله فدلت المعجزات الظَّاهِرَة والآيات الْوَاضِحَة',NULL,'131',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (73,'على صدق نبوته وَصِحَّة رسَالَته صلى الله عَلَيْهِ وَسلم',NULL,'132',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (74,'<span data-type="title" id=toc-89>الْمِعْرَاج</span>

٧٠ -<span data-type="title" id=toc-90> فصل</span>

والمعراج حق عرج رَسُول الله صلى الله عَلَيْهِ وَسلم بشخصه فِي الْيَقَظَة إِلَى',NULL,'133',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (75,'السَّمَاء ثمَّ إِلَى حَيْثُ شَاءَ الله من الْعلَا وَأسرى بِهِ من الْمَسْجِد الْحَرَام إِلَى',NULL,'134',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (76,'الْمَسْجِد الْأَقْصَى ثَبت ذَلِك فِي كتاب الله تَعَالَى وَسنة رَسُوله الله صلى الله عَلَيْهِ وَسلم وَلَيْسَ ذَلِك بمستحيل فِي قدرَة الله تَعَالَى',NULL,'135',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (77,'<span data-type="title" id=toc-91>عصمَة الْأَنْبِيَاء</span>

٧١ -<span data-type="title" id=toc-92> فصل</span>

وَاخْتلف النَّاس فِي عصمَة الْأَنْبِيَاء عَلَيْهِم الصَّلَاة',NULL,'136',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (78,'وَالسَّلَام
قَالَ عَامَّة الْمُعْتَزلَة لَا يجوز شَيْء من الْخَطَأ والزلل والمعاصي وَلَا شَيْء من الْمُبَاحَات المستخففة عَلَيْهِم لِأَن ذَلِك مُوجب النقير عَلَيْهِم وَقَالَ بَعضهم يجوز ذَلِك فعلا وقولا لِأَنَّهُ مُوجب ارْتِفَاع الثِّقَة عَن أَحْوَالهم
وَقَالَ بعض أهل السّنة وَالْجَمَاعَة بِأَن الزلل لَا يكون من الْأَنْبِيَاء إِلَّا بترك الْأَفْضَل وَهَذَا القَوْل وَإِن كَانَ حسنا من حَيْثُ الصُّورَة لكنه غير سديد من وَجه آخر لِأَن الْأَفْضَل يَقْتَضِي فَاضلا فِي مُقَابلَته فَيَقْتَضِي أَن يكون أكل الشَّجَرَة من آدم عَلَيْهِ السَّلَام فَاضلا مَعَ كَونه مَنْهِيّا عَنهُ مَعَ قَوْله تَعَالَى {وَعصى آدم ربه فغوى}',NULL,'138',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (79,'وَقَالَ بعض أهل السّنة هم معصومون عَن الْكَبَائِر دون الصَّغَائِر لِأَنَّهُ أثبت لَهُم مقَام الشَّفَاعَة وَلِأَن من لم يبتل بالبلية لَا يرق على الْمُبْتَلى بهَا
وَالْمذهب السديد أَنه لَا يجب الْإِيمَان بِتَصْدِيق الْقُرْآن والكتب عَن تَأْوِيل مَا ورد فِي حق الْأَنْبِيَاء عَلَيْهِم السَّلَام من هَذِه النُّصُوص لِأَن فِي تَأْوِيلهَا تعرضا لأحوالهم على وَجه لَا يَأْمَن الْخَطَأ فِي ذَلِك مَعَ أَنا غير مكلفين بذلك فَيجب الْكَفّ عَنهُ',NULL,'139',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (80,'<span data-type="title" id=toc-93>التَّفَاضُل بَين الْأَنْبِيَاء</span>

وَبَعض الْأَنْبِيَاء أفضل وَالرسل أفضل مِنْهُم وَالرسل أفضل من',NULL,'140',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (81,'بعض وَنَبِينَا مُحَمَّد صلى الله عَلَيْهِ وَسلم أفضل الْأَنْبِيَاء وَالرسل عَلَيْهِم السَّلَام وَجُمْلَة',NULL,'141',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (82,'الْأَنْبِيَاء عَلَيْهِم السَّلَام كَانُوا مئة ألف نَبِي وَعشْرين ألف نَبِي وَأَرْبَعَة آلَاف الرُّسُل مِنْهُم ثَلَاث مئة وَثَلَاثَة عشر',NULL,'144',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (83,'وأولو الْعَزْم كَانُوا خَمْسَة نوح وَإِبْرَاهِيم ومُوسَى وَعِيسَى',NULL,'145',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (84,'وَمُحَمّد صلى الله عَلَيْهِ وَسلم وَأَرْبَعَة أَحيَاء عِيسَى عَلَيْهِ السَّلَام ... .',NULL,'146',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (85,'وَإِدْرِيس وَالْخضر ... . .',NULL,'148',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (86,'وإلياس عَلَيْهِم السَّلَام',NULL,'151',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (87,'<span data-type="title" id=toc-94>الكونيات</span>

<span data-type="title" id=toc-95>الْمَلَائِكَة</span>

٧٢ -<span data-type="title" id=toc-96> فصل</span>

وَالْمَلَائِكَة عَلَيْهِم السَّلَام بَعضهم أفضل من بعض وأفضلهم أَرْبَعَة جِبْرَائِيل ... ... ... ...',NULL,'153',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (88,'وَمِيكَائِيل ... ... ... . .',NULL,'154',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (89,'وإسرافيل وعزرائيل عَلَيْهِم السَّلَام وَفضل الْأَرْبَعَة ثَبت ذَلِك',NULL,'155',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (90,'بِالْكتاب وَالسّنة
٧٣ -<span data-type="title" id=toc-97> فصل</span>

خَواص بني آدم أفضل من جملَة الْمَلَائِكَة وعوام بني آدم وهم الأتقياء أفضل من عوام الْمَلَائِكَة',NULL,'156',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (91,'لِأَن آدم كَانَ مسجودا لَهُ وَالْمَلَائِكَة كَانُوا ساجدين وَلَا شكّ أَن السُّجُود لَهُ أفضل من الساجد
وَإِذا ثَبت تَفْضِيل الْخَواص على الْخَواص ثَبت تَفْضِيل الْعَوام على الْعَوام
٧٤ -<span data-type="title" id=toc-98> فصل</span>

الْمَلَائِكَة عباد الله عز وَجل وهم معصومون من',NULL,'157',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (92,'الْمعاصِي وَالْإِنْس وَالْجِنّ غير معصومين إِلَّا الْأَنْبِيَاء من الْإِنْس
٧٥ -<span data-type="title" id=toc-99> فصل</span>

الْمِيثَاق الَّذِي أَخذه الله عز وَجل من آدم عَلَيْهِ السَّلَام وَذريته حق',NULL,'158',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (93,'وَالْعرش ... ... ... . .',NULL,'159',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (94,'والكرسي واللوح والقلم ومقاديرهم فِي اللَّوْح حق وَلَو',NULL,'160',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (95,'اجْتمع الْخلق كلهم على شَيْء كتبه الله عز وَجل فِي اللَّوْح أَنه كَائِن ليجعلوه غير كَائِن لم يقدروا عَلَيْهِ وَكَذَلِكَ على الْعَكْس قد جف الْقَلَم بِمَا هُوَ كَائِن إِلَى يَوْم الْقِيَامَة',NULL,'161',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (96,'<span data-type="title" id=toc-100>كرامات الْأَوْلِيَاء</span>

٧٦ -<span data-type="title" id=toc-101> فصل</span>

ظُهُور كرامات الْأَوْلِيَاء على طَرِيق نقض الْعَادة وخرقها',NULL,'162',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (97,'جَائِز لِأَنَّهُ فِي قدرَة الله تَعَالَى مُمكن وَلَيْسَ فِيهِ وَجه من وُجُوه الاستحالة وَيجوز أَن الله تَعَالَى أكْرم وليا بِكُل آيَة يَخُصُّهُ بذلك ثَبت بِالْكتاب وَالسّنة
٧٧ -<span data-type="title" id=toc-102> فصل</span>
الْوَلِيّ لَا يكون أفضل من النَّبِي بل نَبِي وَاحِد أفضل من جَمِيع الْأَوْلِيَاء وبرهانه وَاضح وَالْوَلِيّ وَإِن علت دَرَجَته وَارْتَفَعت مَنْزِلَته لَا تسْقط عَنهُ الْعِبَادَات الْمَفْرُوضَة وَمن زعم أَن من صَار وليا وصل إِلَى الْحَقِيقَة سقط عَنهُ أَحْكَام الشَّرِيعَة فَهُوَ ضال على غير السَّبِيل لِأَن',NULL,'163',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (98,'الْعِبَادَات مَا سَقَطت عَن الْأَنْبِيَاء فَكيف تسْقط عَن الْأَوْلِيَاء
اعْلَم أَن الِاسْتِطَاعَة نَوْعَانِ استطاعة حَال وَهِي الْأَعْضَاء السليمة والأسباب الصَّالِحَة',NULL,'164',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (99,'واستطاعة فعل وَهُوَ عرض يحدث سَاعَة فساعة عِنْد وجود الْفِعْل مُقَارنَة بِخلق الله تَعَالَى وَأما الأولى فَلَا شكّ فِي ثُبُوتهَا
وَأما الثَّانِيَة فالدلالة على مقارنتها بِالْفِعْلِ لِأَنَّهَا لَو كَانَت سَابِقَة على الْفِعْل لانعدمت عِنْد وجود الْفِعْل لِأَنَّهَا عرض وَلَا بَقَاء للأعراض',NULL,'165',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (100,'<span data-type="title" id=toc-103>أَفعَال الْعباد</span>

٧٨ -<span data-type="title" id=toc-104> فصل</span>
أَفعَال الْعباد خَيرهَا وشرها مخلوقة بِخلق الله عز وَجل',NULL,'166',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (101,'وَجل لِأَن قدرَة الله قديمَة لَا تتخصص بِبَعْض المقدورات دون الْبَعْض بل تتَعَلَّق بِكُل مَا يصلح مَقْدُورًا فِي نَفسه وأفعال الْعباد حوادث صلحت مقدورة فِي نَفسهَا فَيتَعَلَّق بهَا فَإِذا وجدت كَانَت مخلوقة بِخلق الله تَعَالَى
٧٩ -<span data-type="title" id=toc-105> فصل</span>

العَبْد لَيْسَ بخالق لأفعاله وَلَا بموجد لَهَا لِأَنَّهُ لَو كَانَ',NULL,'167',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (102,'قَادِرًا على الْخلق والإيجاد لَكَانَ فعله على الْوَجْه الَّذِي قَصده وأراده وَحَيْثُ لم يَقع علم انه لَيْسَ بخالق
٨٠ -<span data-type="title" id=toc-106><span data-type="title" id=toc-107> فصل</span></span>
لِلْخلقِ أَفعَال صَارُوا بهَا عصاة ومطيعين فَهِيَ مخلوقة لله تَعَالَى فَيتَعَلَّق الثَّوَاب وَالْعِقَاب بِفِعْلِهَا وَقت تخليقها من الله عز وَجل لِأَن فعل الْفَاعِل مَا يدْخل تَحت قَصده وإرادته دَاعِيَة وَيمْتَنع دُخُوله تَحت كَرَاهِيَة ومصادقة وَهَذَا تَمام فِي أَفعَال الْعباد فَكَانَت فعلا لَهُم
٨١ - فصل

دُخُول مَقْدُور وَاحِد تَحت قدرتين إِحْدَاهمَا قدرَة الاختراع وَالْأُخْرَى قدرَة الِاكْتِسَاب جَائِز كَمَا فِي الحسيات وَإِنَّمَا الْمُمْتَنع الدُّخُول',NULL,'168',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (103,'تَحت قدرتين وكل وَاحِدَة قدرَة الاختراع أَو الِاكْتِسَاب
٨٢ -<span data-type="title" id=toc-108><span data-type="title" id=toc-109> فصل</span></span>

الْمُتَوَلد من فعل العَبْد مَخْلُوق لله عز وَجل مثل الْأَلَم فِي الْمَضْرُوب عقيب الضَّرْب والانكسار عقيب الْكسر لآن هَذِه الْآثَار لَو كَانَت فعلا للْعَبد يَنْبَغِي أَن يقدر العَبْد على الِامْتِنَاع من الْأَلَم فِي الْمَضْرُوب وَحَيْثُ لم يقدر علم أَنه غير مقدوره
٨٣ - فصل

صانع الْعَالم لَا يُكَلف عباده مَا لَيْسَ فِي وسعهم لآن مَا',NULL,'169',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (104,'يَقْتَضِيهِ التَّكْلِيف لَا يتخفف مَعَ الْعَجز لِأَن قَضِيَّة كَونه بِحَال لَو أَتَى بِهِ يُثَاب عَلَيْهِ بِاعْتِبَار كَونه مُطيعًا وَلَو تَركه يُعَاقب بِاعْتِبَار كَونه عَاصِيا وَهَذَا لَا يتَحَقَّق مَعَ الْعَجز وَعدم الْآلَة
٨٤ -<span data-type="title" id=toc-110> فصل</span>

صانع الْعَالم متفضل بالخلق والاختراع متطول بتكليف الْعباد لم يكن الْخلق والتكليف وَاجِبا عَلَيْهِ لِأَنَّهُ هُوَ الْمُوجب والآمر',NULL,'170',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (105,'والناهي وَكَيف يسلب الْإِيجَاب أَو يتعوض للُزُوم خطاب الله تَعَالَى رب الأرباب',NULL,'171',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (106,'<span data-type="title" id=toc-111>الصّلاح والأصلح</span>

٨٥ -<span data-type="title" id=toc-112> فصل</span>

الْأَصْلَح لَيْسَ بِوَاجِب على الله وَلَا مَا هُوَ الْمصلحَة لِأَنَّهُ',NULL,'172',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (107,'خلق الْكفْر وَالْمَعْصِيَة فَلَو كَانَ الْأَصْلَح وَاجِبا عَلَيْهِ لما خلقهما لِأَنَّهُمَا ليسَا بمصلحة بل هما مفْسدَة فِي حق العَبْد لِأَنَّهُمَا سَبَب للعقاب فِي الدُّنْيَا وَالْآخِرَة',NULL,'173',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (108,'<span data-type="title" id=toc-113>الثَّوَاب وَالْعِقَاب</span>

٨٦ -<span data-type="title" id=toc-114><span data-type="title" id=toc-115> فصل</span></span>

الطَّاعَات عَلَامَات الثَّوَاب لَا عللا والمعاصي عَلَامَات الْعقَاب لَا عللها لِأَن الْقَدِيم سُبْحَانَهُ وَتَعَالَى لَا يسْتَحق عَلَيْهِ وَهُوَ المعبود الْمُسْتَحق لِلْعِبَادَةِ ثَوَابه وعقابه عدل لَا وَاجِب على الله عز وَجل لِأَن الْوَاجِب يَقْتَضِي مُوجبا والموجب فَوق الْمُوجب عَلَيْهِ وَلَيْسَ أحد فَوق الله عز وَجل
٨٧ - فصل

جَزَاء الْأَعْمَال من أَعمال الثَّوَاب وَالْعِقَاب يتَعَلَّق بِأَفْعَال الْعباد لَا بِتَقْدِير الله عز وَجل لقَوْله تَعَالَى وَلَا تُجْزونَ إِلَّا مَا',NULL,'174',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (109,'كُنْتُم تَعْمَلُونَ)
٨٨ -<span data-type="title" id=toc-116> فصل</span>

الْمَقْتُول ميت بأجله وَلَا أجل لَهُ سوى ذَلِك وَلَا يتَقَدَّم',NULL,'175',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (110,'أَجله وَلَا يتَأَخَّر لِأَنَّهُ إِذا علم الله أَنه يَمُوت غَدا بأجله يَسْتَحِيل أَن يقتل الْيَوْم لَا بأجله لِأَنَّهُ يُؤَدِّي إِلَى تعجيز الله تَعَالَى عَن إحْيَاء عَبده إِلَى الْغَد وَأَنه محَال
٨٩ -<span data-type="title" id=toc-117><span data-type="title" id=toc-118> فصل</span></span>

وكل آدَمِيّ لَهُ أجل وَاحِد لِأَنَّهُ لَو كَانَ لَهُ أجلان من تعْيين يُؤَدِّي إِلَى أَن الله تَعَالَى لَا يعرف عواقب الْأُمُور تَعَالَى الله عَن ذَلِك
٩٠ - فصل

وَالْأَجَل عبارَة عَن الْمدَّة وَعَن نِهَايَة الْمدَّة إِلَّا أَنه فِي الثَّانِي أَكثر اسْتِعْمَالا وَالْقَتْل فعل قَائِم بالقاتل وَالْمَوْت إزهاق الرّوح مَخْلُوق لله تَعَالَى لَا صنع للْقَاتِل فِي الْمحل وَكَذَلِكَ كل مُحدث يحدث فِي الْعَالم بِغَيْر صانع فَهُوَ مَخْلُوق لله تَعَالَى وَهُوَ مُحدث بإحداثه بِمَا ذكرنَا فِي حُدُوث الْعَالم',NULL,'176',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (111,'<span data-type="title" id=toc-119>الرزق والأرزاق</span>

٩١ -<span data-type="title" id=toc-120> فصل</span>

الرزق مَا يصل إِلَى العَبْد ويتغذى بِهِ سَوَاء كَانَ حَلَالا أَو حَرَامًا مَمْلُوكا أَو غير مَمْلُوك لِأَنَّهُ لَو كَانَ الرزق حَلَالا أَو مَمْلُوكا لما تصور أَن يرْزق من لم يقدر على الْحَلَال أَو من لَيْسَ لَهُ ملك',NULL,'177',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (112,'٩٢ -<span data-type="title" id=toc-121> فصل</span>

وكل وَاحِد يَسْتَوْفِي رزق نَفسه وَلَا يتَصَوَّر اسْتِيفَاؤهُ رزق غَيره لما بَقِي لذَلِك الآخر رزق يَسْتَوْفِيه فَيُؤَدِّي إِلَى هَلَاكه
الْمعاصِي بِإِرَادَة الله تَعَالَى ومشيئته وكل فعل من أَفعَال الْعباد إِذا وجد على أَي صفة وجد فان كَانَ طَاعَة فَهُوَ بِمَشِيئَة الله تَعَالَى وإرادته وقضائه وَقدره ورضائه ومحبته وَأمره وَإِن كَانَ مَعْصِيّة فَهُوَ بمشيئته وإرادته وقضائه وَقدره وَلَيْسَ بأَمْره وَلَا كَانَ رِضَاهُ وَلَا محبته لِأَن أمره وَرضَاهُ ومحبته ترجع إِلَى كَون الشَّيْء مستحسنا عِنْده وَذَلِكَ يَلِيق بِالطَّاعَةِ دون الْمعاصِي وَلِأَن أَفعَال الْعباد كلهَا مخلوقة بِخلق الله تَعَالَى فَإِذا كَانَت مخلوقة بخلقه',NULL,'178',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (113,'كَانَت بإرادته إِذْ لَو لم يكن بإرادته لم يكن مُخْتَارًا فِي خلقهَا بل يكون مُضْطَرّا وَإنَّهُ كفر وضلال
وَقَالَت الْمُعْتَزلَة الْمعاصِي لَيست بِإِرَادَة الله تَعَالَى وَلَا بمشيئته بل بكراهيته
٩٣ -<span data-type="title" id=toc-122> فصل</span>

إِرَادَة الله تَعَالَى ومشيئته مُوَافقَة لعلمه لَا بأَمْره وَنَهْيه فَكل',NULL,'180',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (114,'مَا علم الله تَعَالَى فِي الْأَزَل أَن يُوجد فقد أَرَادَ وجوده خيرا كَانَ أَو شرا وَمَا علم أَنه لَا يُوجد فقد أَرَادَ أَن لَا يُوجد وَلما علم من فِرْعَوْن الْكفْر وَكَذَا من سَائِر العصاة والكفرة
وَقَالَت الْمُعْتَزلَة إِرَادَته مُطَابقَة لأَمره وَذَلِكَ أَن مَا أَمر الله تَعَالَى فقد أَرَادَهُ وكل مَا نهى عَنهُ فقد كرهه',NULL,'181',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (115,'دليلنا أَن الله تَعَالَى لَو شَاءَ من كَافِر الْإِيمَان وَالْكَافِر شَاءَ من نَفسه الْكفْر لكَانَتْ مَشِيئَة الْكفَّار أنفذ من مَشِيئَة الله تَعَالَى وَهُوَ أَمارَة الْعَجز تَعَالَى الله عَن ذَلِك
٩٤ -<span data-type="title" id=toc-123><span data-type="title" id=toc-124> فصل</span></span>

وَأما الْأَمر وَالنَّهْي فَنَقُول مَا أَمر الْكَافِر بِالْإِيمَان ليؤمن بِاللَّه تَعَالَى وَمَا نهى عَن الْكفْر لينتهي عَنهُ بل ليجب الْإِيمَان عَلَيْهِ وَيحرم الْكفْر عَلَيْهِ فَيتْرك الْإِيمَان الْوَاجِب وَيقدم على الْكفْر الْمنْهِي عَنهُ فَيسْتَحق بذلك الْعقَاب فَيتَحَقَّق بذلك علم الله بترك الْإِيمَان الْوَاجِب وَهُوَ يرتكب الْكفْر الْمَحْظُور وَيصير بذلك أَهلا للتخليد فِي النَّار فَيتَحَقَّق بذلك علمه وإخباره فَإِذا كل ذَلِك لتحَقّق علمه وإرادته
٩٥ - فصل

وَالْعَبْد لَا يصير مجبورا بِعلم الله عز وَجل إِن كَانَ لَا يُمكنهُ الْخُرُوج من إِرَادَة الله تَعَالَى لِأَن مَا أَرَادَ مِنْهُ الْأَفْعَال الاختيارية لَهُ من الْإِيمَان ليستحق الثَّوَاب أَو الْعقَاب لَا الْإِيمَان وَالْكفْر جبرا',NULL,'182',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (116,'<span data-type="title" id=toc-125>الْقَضَاء وَالْقدر</span>

٩٦ -<span data-type="title" id=toc-126> فصل</span>

فِي الْقَضَاء وَالْقدر اعْلَم بِأَن الْقدر سر وَالْقَضَاء ظُهُور السِّرّ',NULL,'183',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (117,'على اللَّوْح وَالْحكم نُزُوله على العَبْد فَالْحكم يَقْتَضِي التَّسْلِيم وَالْقَضَاء يَقْتَضِي الرِّضَا وَالْقدر يَقْتَضِي التَّفْوِيض وَالْقدر فِي علم الله عز وَجل لَا فِي وَجه اللَّوْح والقلم الِاطِّلَاع وَإِذا اطلع اللَّوْح عَلَيْهِ سمي قَضَاء وَإِذا وصل إِلَى العَبْد سمي حكما وَالْقدر مُقَدّر فِي علمه الَّذِي علم وُصُوله إِلَى العَبْد ان شَاءَ وَالْقدر صفته والمقدور ملكه وَالْقدر لَيْسَ بمحدود وَلَا مَعْدُود والمقدور مَحْدُود ومعدود كَذَلِك الْقَضَاء والمقضي وَالْحكم والمحكوم وَالْقدر صفة ربوبيته من غير ابْتِدَاء تصويبا من الله عز وَجل وَالْقَضَاء إِلْزَام مَا صَوبه وَالْحكم تَعْلِيق مَا الزمه على العَبْد',NULL,'184',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (118,'٩٧ -<span data-type="title" id=toc-127> فصل</span>

الْجَبْر على ضَرْبَيْنِ جبر من الْإِجْبَار وجبر من',NULL,'187',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (119,'الجبروت والإجبار يزِيل الْأَفْعَال والجبروت يزِيل الِاسْتِغْنَاء فَالْعَبْد لَيْسَ بمجبور إجبارا يزِيل الْفِعْل بل هُوَ مُخْتَار فِي الْفِعْل تَحت الجبروت مفتقر إِلَى الله عز وَجل بورود التَّوْفِيق وَوُجُود الِاسْتِطَاعَة فَمن جِهَة تخليق الْأَفْعَال فِي أَعْضَائِهِ وإخراجها من الْعَدَم إِلَى الْوُجُود مجبور يَعْنِي لَيْسَ بخالق الْأَفْعَال وَإِنَّمَا حصلت الْأَفْعَال بالتخليق فَهُوَ فِي اسْتِعْمَالهَا غير مجبور بل مُخْتَار فِي اسْتِعْمَالهَا لِأَن الله تَعَالَى أعْطى لَهُ التَّمْيِيز متولدا من الْعقل والفهم والذهن لَيْسَ كشجرة تحركها الرّيح تسخيرا من غير تَمْيِيز',NULL,'188',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (120,'أَو كسحاب وَالشَّمْس وَالْقَمَر وَسَائِر المسخرات لِأَنَّهُ مَأْمُور مَنْهِيّ
والمجبورات غير مأمورات وَلَا منهيات وَالْعَبْد مثاب ومعاقب والمسخرات لَا ثَوَاب لَهَا وَلَا عِقَاب فَثَبت أَن العَبْد لَيْسَ بمجبور إجبارا يزِيل الْفِعْل وَلَيْسَ بمستغن يقدر على إِيجَاد الْمَعْدُوم لِأَنَّهُ لَيْسَ بخالق

٩٨ -<span data-type="title" id=toc-128> فصل</span>

اعْلَم أَن الْمَذْهَب الْمُسْتَقيم أَن تَقْدِير الْخَيْر وَالشَّر من الله تَعَالَى وَفعل الْخَيْر وَالشَّر من العَبْد وَالْعَبْد مُخْتَار فِي فعله اخْتِيَار تَمْيِيز وَتَحْصِيل لَا اخْتِيَار مَشِيئَة وقدرة وَالْعَبْد مُخَاطب بمراعاة الْأَمر وَالنَّهْي وبالنظر إِلَى الْقَضَاء وَالْقدر فَيحصل لَهُ الْخَوْف والرجاء وَالِاجْتِهَاد وَالرَّغْبَة وَهُوَ غير مسؤول فِي جَانب الْقَضَاء وَالْقدر ليثاب ويعاقب بل هُوَ مسؤول فِي جَانب الْأَمر وَالنَّهْي للثَّواب وَالْعِقَاب وَلَيْسَ للْعَبد أَن يَقُول عاذرا لنَفسِهِ بِأَن الْقَضَاء وَالْقدر هَكَذَا أجري عَليّ فَمَا ذَنبي بل العَبْد مُلْزم',NULL,'189',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (121,'بمراعاة الْأَمر وَالنَّهْي فَيُقَال لَهُ إِنَّك سلمت إِلَى الله عز وَجل الربوبية وصدقته بِأَن الْقَضَاء وَالْقدر مِنْهُ ربوبية فَكَذَلِك الْأَمر وَالنَّهْي
٩٩ -<span data-type="title" id=toc-129> فصل</span>

وَاعْلَم أَن لكل عبد هدى ورشدا فَمن الله عز وَجل',NULL,'190',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (122,'فضل وكل من خذل وَحرم فَمن الله عدل وَصفَة الله عز وَجل الْفضل وَالْعدْل فَمن أعطَاهُ الْهدى فقد عَامله بِالْفَضْلِ وَمن حرمه فقد عَامله',NULL,'191',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (123,'بِالْعَدْلِ وَلَا يُوصف بالجور وَالْخَطَأ يظهران من الله تَعَالَى الْأُمُور لَا من الْأَمر فَمنع التَّوْفِيق لَيْسَ بِقدر للْعَبد لِأَنَّهُ عَادل فِي مَنعه متفضل فِي إِعْطَائِهِ فَالْكل مِنْهُ وَإِلَيْهِ لَيْسَ للْعَبد اعْتِرَاض وَلَا مِنْهُ مهرب فَيَنْبَغِي للْعَبد أَن يُرْضِي بِجَمِيعِ مَا قضى الله تَعَالَى عَلَيْهِ وَقدره وَيلْزم طَرِيق الصَّبْر وَالتَّسْلِيم والتفويض وَهُوَ لَا يَخُوض فِي قَضَاء الله وَقدره أَو بوسوسة أَو مقَال فَإِن الله تَعَالَى أخْفى علم الْقدر عَن عباده ونهاهم عَن مرامه ومنعهم عَن الِاعْتِرَاض فِيهِ',NULL,'192',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (124,'وَالسُّؤَال عَنهُ كَمَا قَالَ تَعَالَى لَا يسئل عَمَّا يفعل وهم يسئلون وَقَالَ صلى الله عَلَيْهِ وَسلم (لما خلق الله الْخلق جعل طباعهم فِي النَّهْي متحركة وَفِي الْأَمر سَاكِنة وَأمرهمْ أَن يسكنوا على المتحرك وان يتحركوا بالساكن وَلَا تَجدوا إِلَى ذَلِك سَبِيلا إِلَّا بحول الله وقوته)
وَخَالَفنَا فِي هَذِه الْمَسْأَلَة القدري والجبري
فَقَالَ القدري الْخَيْر وَالشَّر فعل العَبْد لَيْسَ لله تَعَالَى صنع فِيهِ
وَقَالَ الجبري الْخَيْر وَالشَّر من الله تَعَالَى عز وَجل لَيْسَ للْعَبد فِيهِ فعل الدّلَالَة على بطلَان مَا قَالَا مَا ذكرنَا من الدَّلَائِل
١٠٠ -<span data-type="title" id=toc-130><span data-type="title" id=toc-131> فصل</span></span>

اعْلَم أَن جَمِيع أَحْكَام الله تَعَالَى ثَلَاثَة حكم شَاءَ الله وأحبه وَهُوَ الْفَرَائِض وَحكم شَاءَ الله وأحبه وَلم يَأْمر بِهِ وَهُوَ النَّوَافِل وَحكم شَاءَ الله وَلم يُحِبهُ وَلم يَأْمر بِهِ وَهُوَ الْمعاصِي
١٠١ - فصل

اعْلَم أَن جَمِيع مَا قضى الله عز وَجل أَرْبَعَة قَضَاء الطَّاعَة وَقَضَاء الْمعْصِيَة وَقَضَاء النِّعْمَة وَقَضَاء الشدَّة',NULL,'193',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (125,'فعلى العَبْد إِذا قضى لَهُ بِالطَّاعَةِ أَن يستقبلها بِالْحَمْد وَالْإِخْلَاص ليكرم بالتوفيق وَإِذا قضى لَهُ بالمعصية أَن يستقبلها بِالتَّوْبَةِ وَالِاسْتِغْفَار ليرزق الْمحبَّة وَالْمَغْفِرَة وَإِذا قضى لَهُ بِالنعْمَةِ أَن يستقبلها بالشكروالصدق ليكرم بِالزِّيَادَةِ واذا قضى لَهُ بالشدة أَن يستقبلها بِالصبرِ وَالرِّضَا ليكرم بِالْأَجْرِ وَالثَّوَاب
١٠٢ -<span data-type="title" id=toc-132><span data-type="title" id=toc-133><span data-type="title" id=toc-134> فصل</span></span></span>

الْهدى والضلال من الله تَعَالَى لِأَن أَفعَال العَبْد مخلوقة بِخلق الله تَعَالَى
١٠٣ - فصل

الإسعار من قبل الله تَعَالَى لَا يتَغَيَّر بعكس الْعباد قَالَ الله تَعَالَى وَالله يقبض ويبصط
١٠٤ - فصل

حَقِيقَة النِّعْمَة اللَّذَّة وَحَقِيقَة الشُّكْر الِاعْتِرَاف بِنِعْمَة الْمُنعم على سَبِيل الخضوع لَهُ وَالدَّلِيل عَلَيْهِ اطراده وانعكاسه فِي جَمِيع أَحْوَاله',NULL,'194',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (126,'١٠٥ -<span data-type="title" id=toc-135><span data-type="title" id=toc-136> فصل</span></span>

اعْلَم بِأَن الله تَعَالَى قد أنعم على الْمُؤمنِينَ بالمعرفة وَالْإِيمَان وللسائل أَن يَقُول هَل أنعم عَلَيْهِم بالشدائد والمحن
كَمَا أَنه تَعَالَى لم ينعم على الْكفَّار بالمعرفة وَالْإِيمَان بل إِن إنعامه عَلَيْهِم كَانَ بالمنافع والملاذ العاجلة
وَعَلِيهِ فَيجب أَن يُقَال إِن كل نفع وضر يُوصل العَبْد إِلَى الطَّاعَات وَالنَّعِيم الأبدي فَهُوَ من نعم الله تَعَالَى ظَاهِرَة وباطنة وكل مَا لَا يوصله إِلَى ذَلِك اويوصله إِلَى اكْتِسَاب الْمعاصِي فَهُوَ نعْمَة فِي الظَّاهِر نقمة فِي الْبَاطِن
١٠٦ - فصل

اعْلَم أَن الله تَعَالَى لَو أَدخل جَمِيع الْخلق الْجنَّة من غير طَاعَة يكون حسنا وَحِكْمَة بَالِغَة وَلَو أدخلهم النَّار من غير مَعْصِيّة هَل يحسن ذَلِك فِي الْحِكْمَة
قَالَ بعض أهل السّنة وَالْجَمَاعَة يكون حسنا وَحِكْمَة',NULL,'195',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (127,'وَقَالَ بعض مَشَايِخنَا رَحِمهم الله لَا يحسن ذَلِك فِي الْحِكْمَة لِأَنَّهُ جمع بَين الْعَدو وَالْوَلِيّ فِي النَّار من غير ذَنْب
١٠٧ -<span data-type="title" id=toc-137><span data-type="title" id=toc-138><span data-type="title" id=toc-139> فصل</span></span></span>

الْفَاسِق الْمُؤمن لَا يخرج من الْإِيمَان بِفِسْقِهِ لِأَن الْخُرُوج من الْإِيمَان إِنَّمَا يكون بِزَوَال التَّصْدِيق والتصديق بَاقٍ فَيكون مُؤمنا
١٠٨ - فصل

الْفَاسِق لَا يخلد فِي النَّار لِأَن الخلود للْكفَّار وَهُوَ مُؤمن مُصدق
١٠٩ - فصل

الْفَاسِق من أهل الْمَغْفِرَة لِأَن الله تَعَالَى عَفْو غَفُور رَحِيم وَالْعَفو وَالْمَغْفِرَة وَالرَّحْمَة إِنَّمَا يتَحَقَّق فِي رفع عُقُوبَة من هُوَ جَائِز التعذيب بِسَبَب الْجِنَايَة إِذا ثَبت جَوَاز الْمَغْفِرَة لصَاحب',NULL,'196',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (128,'الْكَبِيرَة ابْتِدَاء جَازَ أَن يغْفر ذَنبه بشفاعة الشافعين لِأَن مبْنى الشَّفَاعَة بِجَوَاز',NULL,'197',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (129,'الْمَغْفِرَة فَإِذا جَازَ ذَلِك ابْتِدَاء من غير شَفَاعَة فَلِأَن يجوز مَعَ الشَّفَاعَة بِالطَّرِيقِ الأولى
١١٠ -<span data-type="title" id=toc-140> فصل</span>

الْفَاسِق إِذا خرج من دُنْيَاهُ من غير تَوْبَة وَقد ختم لَهُ على',NULL,'198',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (130,'الْإِيمَان لَا يجوز أَن يُقَال أَن الله تَعَالَى يعذبه لَا محَالة وَلَا أَن يُقَال يعْفُو عَنهُ لَا محَالة بل هُوَ فِي مَشِيئَة الله تَعَالَى كَمَا قَالَ {إِن الله لَا يغْفر أَن يُشْرك بِهِ وَيغْفر مَا دون ذَلِك لمن يَشَاء} إِن شَاءَ عَفا عَنهُ بفضله وَكَرمه أَو ببركة مَا مَعَه من الايمان أَو بشفاعة الشافعين أَو يعذبه بِقدر ذَنبه ثمَّ يدْخلهُ الْجنَّة',NULL,'199',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (131,'<span data-type="title" id=toc-141>عَلَامَات السَّاعَة</span>

١١١ -<span data-type="title" id=toc-142> فصل</span>

وَمن عَلَامَات السَّاعَة خُرُوج الدَّجَّال',NULL,'200',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (132,'ونزول عِيسَى ابْن مَرْيَم عَلَيْهِ السَّلَام',NULL,'202',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (133,'وَظُهُور دَابَّة الأَرْض
وطلوع الشَّمْس من مغْرِبهَا',NULL,'203',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (134,'وَخُرُوج يَأْجُوج وَمَأْجُوج',NULL,'204',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (135,'وَظُهُور الْفِتَن واندراس الْعلم وَالْعُلَمَاء وَغير ذَلِك مِمَّا',NULL,'205',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (136,'جَاءَ من الْأَخْبَار عَن السَّيِّد الْمُخْتَار عَلَيْهِ أفضل الصَّلَاة وَالسَّلَام فِي أَشْرَاط',NULL,'206',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (137,'السَّاعَة
١١٢ -<span data-type="title" id=toc-143><span data-type="title" id=toc-144> فصل</span></span>

وَإِذا نزل عِيسَى عَلَيْهِ السَّلَام من السَّمَاء فِي آخر الزَّمَان فَإِنَّمَا ينزل على شَرِيعَة نَبينَا مُحَمَّد صلى الله عَلَيْهِ وَسلم وَيَدْعُو النَّاس إِلَى شَرِيعَته وَيكون كواحد الدعاة
١١٣ - فصل

وَلَا نصدق كَاهِنًا وَلَا عرافا وَلَا من يَدعِي شَيْئا',NULL,'207',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (138,'بِخِلَاف الْكتاب وَالسّنة وَإِجْمَاع الْأمة
١١٤ -<span data-type="title" id=toc-145> فصل</span>

من ادّعى النُّبُوَّة تجب استتابته فان لم يتب يجب قَتله لاختتام النُّبُوَّة وانسداد بَابهَا',NULL,'208',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (139,'١١٥ -<span data-type="title" id=toc-146> فصل</span>

اخْتلف النَّاس فِي أَطْفَال الْمُشْركين',NULL,'209',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (140,'قَالَ بَعضهم فِي الْجنَّة
وَقَالَ بَعضهم فِي النَّار وَقَالَ بَعضهم هم خدام أهل الْجنَّة
فَإِذا اخْتلف النَّاس فيهم فالسكوت أولى فهم فِي مَشِيئَة الله تَعَالَى
١١٦ -<span data-type="title" id=toc-147> فصل</span>

اخْتلف النَّاس فِي عدد الْحفظَة
قَالَ بَعضهم أَرْبَعَة اثْنَان بِالنَّهَارِ وَاثْنَانِ فِي اللَّيْل وَهُوَ الصَّحِيح
وَقَالَ بَعضهم خَمْسَة وَالْخَامِس لَا يُفَارِقهُ لَيْلًا وَلَا نَهَارا',NULL,'210',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (141,'١١٧ -<span data-type="title" id=toc-148><span data-type="title" id=toc-149> فصل</span></span>

اخْتلف النَّاس فِي كتبة الْحفظَة
قَالَ بَعضهم يَكْتُبُونَ جَمِيع أَفعَال الْعباد من بني آدم وأقوالهم وَقَالَ بَعضهم يَكْتُبُونَ الْجَمِيع فَإِذا صعدوا إِلَى السَّمَاء حذفوا مَا لَا أجر فِيهِ وَلَا إِثْم قَالَ ابْن عَبَّاس رَضِي الله عَنْهُمَا يَكْتُبُونَ الْخَيْر وَالشَّر وَالْأول أصح لقَوْله تَعَالَى {وَوضع الْكتاب فترى الْمُجْرمين مشفقين مِمَّا فِيهِ}
١١٨ - فصل

اخْتلف النَّاس فِي الْكفَّار هَل عَلَيْهِم حفظَة',NULL,'211',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (142,'قَالَ بَعضهم لَيْسَ عَلَيْهِم حفظَة وَقَالَ بَعضهم عَلَيْهِم حفظَة وَهُوَ الصَّحِيح
قَالَ الله تَعَالَى فِي حَقهم {كلا بل تكذبون بِالدّينِ وَإِن عَلَيْكُم لحافظين كراما كاتبين يعلمُونَ مَا تَفْعَلُونَ}
١١٩ -<span data-type="title" id=toc-150><span data-type="title" id=toc-151> فصل</span></span>

يحْشر الوحوش والطيور والبهائم يَوْم الْقِيَامَة لِأَنَّهُ يجوز ذَلِك فِي الْعقل إِظْهَارًا لقدرة الله تَعَالَى كَمَا أَنه خلق الْخلق إِظْهَارًا لربوبيته
١٢٠ - فصل

صانع الْعَالم قَادر على إِعَادَة الموجودات مَا فني من جواهرها وأجسامها وأعراضها لَان الْإِعَادَة بِمَعْنى الِابْتِدَاء من حَيْثُ إِنَّه',NULL,'212',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (143,'إِعَادَة من الْعَدَم
١٢١ -<span data-type="title" id=toc-152> فصل</span>

الْمَوْت حق وَسَكَرَاته حق لقَوْله تَعَالَى {قل الله يُحْيِيكُمْ ثمَّ يميتكم}
وَقَوله تَعَالَى {وَجَاءَت سكرة الْمَوْت بِالْحَقِّ}',NULL,'213',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (144,'<span data-type="title" id=toc-153>الغيبيات</span>

١٢٢ -<span data-type="title" id=toc-154><span data-type="title" id=toc-155> فصل</span></span>

ملك الْمَوْت الَّذِي يقبض بِهِ الْأَرْوَاح حق لقَوْله تَعَالَى {حَتَّى إِذا جَاءَ أحدكُم الْمَوْت توفته رسلنَا}
١٢٣ - فصل

صانع الْعَالم يُمِيت الْخَلَائق إِلَّا وَجهه الْكَرِيم كَمَا قَالَ تَعَالَى كل شَيْء هَالك إِلَّا وَجهه',NULL,'214',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (145,'١٢٤ -<span data-type="title" id=toc-156><span data-type="title" id=toc-157> فصل</span></span>

عَذَاب الْقَبْر حق لقَوْله تَعَالَى {النَّار يعرضون عَلَيْهَا غدوا وعشيا}
ثَبت عرض آل فِرْعَوْن على النَّار قبل يَوْم الْقِيَامَة غدوا وعشيا وَلَيْسَ ذَلِك إِلَّا عَذَاب الْقَبْر
١٢٥ - فصل

يُقَال رُجُوع الْحَيَاة إِلَى الْمَيِّت فِي الْقَبْر كلهَا أَو بَعْضهَا بِقدر مَا يقدر الْعقل السُّؤَال وَيفهم ويتلذذ بالإكرام إِن كَانَ مُؤمنا ويتألم',NULL,'215',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (146,'بِالْعَذَابِ إِن كَانَ كَافِرًا لقَوْله تَعَالَى {رَبنَا أمتنَا اثْنَتَيْنِ وأحييتنا اثْنَتَيْنِ}
١٢٦ -<span data-type="title" id=toc-158> فصل</span>

سُؤال مُنكر وَنَكِير حق لقَوْله صلى الله عَلَيْهِ وَسلم لعمر بن الْخطاب رَضِي الله عَنهُ (كَيفَ أَنْت يَا عمر من مُنكر وَنَكِير) قَالَ يَا رَسُول الله وَمَا مُنكر وَنَكِير قَالَ (ملكا الْقَبْر وهما شخصان مهيبان فتانان أسودان أزرقان أعينهما كالنحاس أَي كالدخان وأبصارهما كالبرق الخاطف وأصواتهما كالرعد القاصف يضمان أشفارهما ويحفرون الأَرْض بأنيابهما مَعَهُمَا إرزبتان لَو اجْتمع عَلَيْهِمَا أهل السَّمَوَات وَأهل الأَرْض مَا نقلوهما أَي من ثقلهما يقعدان العَبْد فِي قَبره سويا ويقولان من رَبك وَمَا دينك وَمَا نبيك) قَالَ عمر رَضِي الله عَنهُ على أَي حَال أَنا يَوْمئِذٍ يَا رَسُول الله قَالَ (حالك الْيَوْم) قَالَ إِذا أكفهما',NULL,'216',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (147,'١٢٧ -<span data-type="title" id=toc-159> فصل</span>

الْمَيِّت ينْتَفع بِمَا يهدى إِلَيْهِ من الْخيرَات وَالصَّدقَات',NULL,'218',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (148,'لقَوْله صلى الله عَلَيْهِ وَسلم لعَلي بن أبي طَالب رَضِي الله عَنهُ (يَا عَليّ تصدق عَن موتاك فَإِن الله تَعَالَى وكل مَلَائِكَة يحملون صدقَات الْأَحْيَاء إِلَيْهِم فيفرحون بهَا كأشد مَا يكون من الْفَرح ثمَّ يَجدونَ أحزانا ويندمون على مَا خلفوا وَيَقُولُونَ اللَّهُمَّ اغْفِر لمن نور قبورنا وبشره بِالْجنَّةِ كَمَا بشرنا فيا أسفا على مَا خلفنا من بَعدنَا)
١٢٨ -<span data-type="title" id=toc-160> فصل</span>

نفخ الصُّور حق',NULL,'220',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (149,'قيل يكون نفختين نفخة للهلاك ونفخة للبعث
وَقيل ثَلَاثَة وَهُوَ الصَّحِيح
قَالَ الله تَعَالَى {وَيَوْم ينْفخ فِي الصُّور فَفَزعَ من فِي السَّمَاوَات وَمن فِي الأَرْض إِلَّا من شَاءَ الله وكل أَتَوْهُ داخرين}
١٢٩ -<span data-type="title" id=toc-161> فصل</span>

اعْلَم بِأَن الْبَعْث بعد الْمَوْت حق والتصديق بِهِ وَاجِب',NULL,'221',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (150,'وان الله تَعَالَى يحيي الْخلق بعد فنائهم قَالَ تَعَالَى ذَلِك بِأَن الله هُوَ الْحق وَأَنه يحى الْمَوْتَى وَأَنه على كل شَيْء قدير وَإِن السَّاعَة آتِيَة لَا ريب فِيهَا وَإِن الله يبْعَث من فِي الْقُبُور
١٣٠ -<span data-type="title" id=toc-162> فصل</span>

يجمع الْخَلَائق فِي عرصات الْقِيَامَة ويوقفون',NULL,'223',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (151,'خمسين موقفا فِي كل موقف ألف سنة
قَالَ تَعَالَى {فِي يَوْم كَانَ مِقْدَاره خمسين ألف سنة}
وَقيل أَرْبَعِينَ سنة يقفون على قُبُورهم حيارى أَي مثل سكارى ينتظرون من الله تَعَالَى كَمَا قَالَ تَعَالَى {يَوْم ترونها تذهل كل مُرْضِعَة عَمَّا أرضعت وتضع كل ذَات حمل حملهَا وَترى النَّاس سكارى وَمَا هم بسكارى وَلَكِن عَذَاب الله شَدِيد}
وَقَالَ تَعَالَى {فَإِذا هم قيام ينظرُونَ} ثمَّ بعد أَرْبَعِينَ سنة يؤمرون',NULL,'224',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (152,'بالمحاسبة فيخوضون إِلَى موقف الْحساب ويعرضون على رَبهم ويسئلون عَن أَعْمَالهم الْخَيْر وَالشَّر ويحاسبون على أفعالهم وأقوالهم قَلِيلا كَانَ أَو كثيرا فَالله عز وَجل يقْضِي بَينهم بِالْحَقِّ وينصف الْمَظْلُوم من الظَّالِم وَتظهر الفضائح والقبائح كَمَا قَالَ تَعَالَى {يَوْم تبلى السرائر} وَالنَّاس متفاوتون فِي ذَلِك مناقش فِي الْحساب والى مسامح فِيهِ والى من يدْخل الْجنَّة بِغَيْر حِسَاب والى من يدْخل النَّار بِغَيْر حِسَاب فينادي مُنَاد',NULL,'225',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (153,'{الْيَوْم تجزى كل نفس بِمَا كسبت لَا ظلم الْيَوْم إِن الله سريع الْحساب}
١٣١ -<span data-type="title" id=toc-163><span data-type="title" id=toc-164> فصل</span></span>

وَالله تَعَالَى يغْضب ويرضى لَا كَأحد من الورى أَي يصير العَبْد مُسْتَحقّا لِرَحْمَتِهِ فَيدْخل الْجنَّة أَو مستوجبا لعذابه فَيدْخل النَّار نَعُوذ بِاللَّه من غضب الله وَسخطه
١٣٢ - فصل

قِرَاءَة الْكتاب حق فَمن النَّاس من يعْطى كِتَابه بِيَمِينِهِ وَمِنْهُم من يعْطى كِتَابه بِشمَالِهِ وَمِنْهُم من يعْطى كِتَابه من وَرَاء ظَهره
قَالَ الله تَعَالَى وَنخرج لَهُ يَوْم الْقِيَامَة كتابا يلقاه منشورا اقْرَأ كتابك كفى',NULL,'226',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (154,'بِنَفْسِك الْيَوْم عَلَيْك حسيبا) وَقَالَ عز وَجل {وَأما من أُوتِيَ كِتَابه وَرَاء ظَهره}
١٣٣ -<span data-type="title" id=toc-165> فصل</span>

الْمِيزَان ذُو الكفتين حق الَّذِي يُوزن فِيهِ أَعمال الْخلق بقدرة الله تَعَالَى كَمَا شَاءَ عز وَجل وَقيل يُوزن فِيهِ كتب الْأَعْمَال وَصفته فِي الْعظم مثل طباق السَّمَوَات وَالْأَرْض قَالَ الله تَعَالَى وَنَضَع',NULL,'227',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (155,'١٣٤ -<span data-type="title" id=toc-166><span data-type="title" id=toc-167> فصل</span></span>

ثقل الْمِيزَان وَخِفته حق قَالَ الله تَعَالَى {وَالْوَزْن يَوْمئِذٍ الْحق}
وَقَالَ تَعَالَى فَمن ثقلت مَوَازِينه فاؤلئك هم المفلحون وَمن خفت مَوَازِينه فَأُولَئِك الَّذين خسروا أنفسهم فِي جَهَنَّم خلدون
١٣٥ - فصل

حَوْض نَبينَا مُحَمَّد صلى الله عَلَيْهِ وَسلم حق يشرب مِنْهُ الْمُؤْمِنُونَ المَاء',NULL,'228',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (156,'وماؤه ابيض من الثَّلج وَأحلى من الْعَسَل من شربه لم يظمأ بعده أبدا
قَالَ الله تَعَالَى {إِنَّا أعطيناك الْكَوْثَر} اللَّهُمَّ اسقنا مِنْهُ بِفَضْلِك يَا كريم',NULL,'229',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (157,'الشَّفَاعَة
١٣٦ -<span data-type="title" id=toc-168> فصل</span>

شَفَاعَة نَبينَا مُحَمَّد الْمُصْطَفى والأنبياء عَلَيْهِم الصَّلَاة',NULL,'230',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (158,'وَالسَّلَام وَالْعُلَمَاء وَالصديقين وَالشُّهَدَاء وَالصَّالِحِينَ حق',NULL,'234',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (159,'قَالَ الله تَعَالَى {عَسى أَن يَبْعَثك رَبك مقَاما مَحْمُودًا} يَعْنِي مقَام الشَّفَاعَة
١٣٧ -<span data-type="title" id=toc-169> فصل</span>

الْجنَّة حق وَلها ثَمَانِيَة أَبْوَاب وَالنَّار حق وَلها سَبْعَة أَبْوَاب الْآيَة',NULL,'236',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (160,'وَأما الْجنَّة فَذكر أساميها مُتَفَرِّقَة',NULL,'237',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (161,'١٣٨ -<span data-type="title" id=toc-170><span data-type="title" id=toc-171> فصل</span></span>

فَإِذا فرغوا من حسابهم يُقَال لَهُم هلموا إِلَى الْجنَّة والى النَّار فَإِذا وصلوا إِلَى رَأس الطَّرِيقَيْنِ يفرق بَين أهل الْجنَّة وَالنَّار فيساق كل فريق إِلَى مَا أعد لَهُ
قَالَ الله تَعَالَى {فريق فِي الْجنَّة وفريق فِي السعير}
١٣٩ - فصل

الصِّرَاط حق وَهُوَ جسر مَمْدُود على متن جَهَنَّم أدق من الشّعْر وَأحد من السَّيْف يُورد النَّاس جَمِيعًا على الصِّرَاط وورودهم',NULL,'238',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (162,'قيامهم حول النَّار ثمَّ يَمرونَ على الصِّرَاط بِقدر أَعْمَالهم
قَالَ الله تَعَالَى {وَإِن مِنْكُم إِلَّا واردها كَانَ على رَبك حتما مقضيا}
١٤٠ -<span data-type="title" id=toc-172> فصل</span>

الْوُرُود على الصِّرَاط حق فَمن النَّاس من يمر مثل الْبَرْق الخاطف وَمِنْهُم من يمر مثل الطير وَمِنْهُم من يمر على أَجود الْخَيل وَمِنْهُم كعدو الرجل حَتَّى أَن آخِرهم يمشي وَيَقَع هَكَذَا ورد فِي الحَدِيث',NULL,'239',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (163,'١٤١ -<span data-type="title" id=toc-173><span data-type="title" id=toc-174> فصل</span></span>

الْمُؤْمِنُونَ الموحدون المتقون كلهم يدْخلُونَ الْجنَّة بَعضهم بأعمالهم وَبَعْضهمْ بشفاعة الشافعين وَبَعْضهمْ بِفضل الله وبرحمته وَالْكل بِفضل الله وَرَحمته
١٤٢ - فصل

الْأَنْبِيَاء عَلَيْهِم السَّلَام والأتقياء والأولياء وَالْعُلَمَاء لَهُم مقَام',NULL,'240',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (164,'الشَّفَاعَة وكل نَبِي يدْخل الْجنَّة مَعَ أمته وَنَبِينَا مُحَمَّد صلى الله عَلَيْهِ وَسلم يدْخل مَعَ أمته وَهُوَ أول من يدْخل الْجنَّة مَعَ أمته كَمَا قَالَ صلى الله عَلَيْهِ وَسلم نَحن الْآخرُونَ',NULL,'241',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (165,'السَّابِقُونَ أول من يقرع بَاب الْجنَّة أَنا صدق رَسُول الله صلى الله عَلَيْهِ وَسلم',NULL,'242',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (166,'<span data-type="title" id=toc-175>الْجنَّة وَالنَّار</span>

١٤٣ -<span data-type="title" id=toc-176> فصل</span>

الْمُؤْمِنُونَ لَهُم مَرَاتِب فِي الْجنَّة على قدر أَعْمَالهم وَالله',NULL,'243',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (167,'عز وَجل يكرمهم بنعيم الْجنَّة من حور الْعين والقصور',NULL,'244',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (168,'والغلمان والولدان وَالشرَاب الطّهُور وَالْخُلُود فِيهَا لَا يموتون فِيهَا وَلَا يخرجُون مِنْهَا ويكرمهم أَيْضا بِرُؤْيَتِهِ كَمَا يَشَاء الله تَعَالَى وُجُوه يؤمنذ',NULL,'145',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (169,'ناضرة إِلَى رَبهَا ناظرة) اللَّهُمَّ اجْعَلْنَا مِنْهُم بِرَحْمَتك يَا أرْحم الرَّاحِمِينَ
١٤٤ -<span data-type="title" id=toc-177><span data-type="title" id=toc-178> فصل</span></span>

الْمُؤْمِنُونَ المذنبون فِي الْمَشِيئَة إِن شَاءَ يعذبهم وَإِن شَاءَ يرحمهم فَإِن عذبهم فِي النَّار بِقدر معاصيهم ثمَّ يرحمهم ويخرجهم ويدخلهم الْجنَّة قَالَ صلى الله عَلَيْهِ وَسلم (يخرج من النَّار من كَانَ فِي قلبه مِثْقَال ذرة من الْإِيمَان)
١٤٥ - فصل

الْكفَّار كلهم يدْخلُونَ النَّار ويخلدون فِيهَا أبدا',NULL,'246',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (170,'لَا يموتون فِيهَا وَلَا يخرجُون مِنْهَا يُعَذبُونَ بأنواع الْعَذَاب على قدر معاصيهم وكفرهم نَعُوذ بِاللَّه مِنْهَا
١٤٦ -<span data-type="title" id=toc-179><span data-type="title" id=toc-180> فصل</span></span>

واعتقد أَن الْجنَّة وَالنَّار مخلوقتان لأهليهما لَا يفنيان أبدا هَكَذَا ورد لفظ الحَدِيث
١٤٧ - فصل

اعْلَم بِأَن الله تَعَالَى خلق للجنة أَهلا وللنار أَهلا فَمن شَاءَ مِنْهُم للجنة فضلا مِنْهُ وَمن شَاءَ مِنْهُم للنار عدلا مِنْهُ فَإِن الله تَعَالَى أعلم',NULL,'248',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (171,'عدد من يدْخل الْجنَّة وَعدد من يدْخل النَّار جملَة وَاحِدَة فَلَا يُزَاد فِي ذَلِك الْعدَد وَلَا ينقص مِنْهُ كَذَلِك أفعالهم فِيمَا علم أَن يفعلوه وكل ميسر لما خلق لَهُ فَمن كَانَ من أهل الْجنَّة يسر الله عز وَجل عَلَيْهِ عمل أهل الْجنَّة وَكَذَا من كَانَ من أهل النَّار نَعُوذ بِاللَّه من النَّار',NULL,'249',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (172,'<span data-type="title" id=toc-181>الْإِيمَان</span>

١٤٨ -<span data-type="title" id=toc-182> فصل</span>

واعتقد أَن الْإِيمَان ... ... ... .',NULL,'250',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (173,'فِي التَّحْقِيق وَهُوَ التَّصْدِيق بِالْقَلْبِ وَهُوَ الْإِيمَان الْمَفْرُوض على العَبْد',NULL,'251',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (174,'الْإِقْرَار بِاللِّسَانِ ليظْهر عِنْد النَّاس مَا فِي الْجنان فتجري عَلَيْهِ أَحْكَام الْإِسْلَام فَمن أُتِي بالتصديق بِالْقَلْبِ يكون مُؤمنا بَينه وَبَين الله تَعَالَى وَمن أَتَى بهما يكون مُؤمنا عِنْد الله وَعند النَّاس
وَالْإِيمَان أَن تؤمن بِاللَّه وَمَلَائِكَته وَكتبه وَرَسُوله وَالْيَوْم الآخر دلَالَة أَن الْإِيمَان هُوَ التَّصْدِيق بِالْقَلْبِ وَأَن ضد الْإِيمَان هُوَ كفر وَتَكْذيب والتصديق والتكذيب عمل الْقلب',NULL,'252',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (175,'١٤٩ -<span data-type="title" id=toc-183><span data-type="title" id=toc-184> فصل</span></span>

وَسَائِر الْعِبَادَات من أَحْكَام الْإِيمَان لِأَن اسْم الْإِيمَان لَو كَانَ وَاقعا على مَجْمُوع التَّصْدِيق وَالْإِقْرَار والأعمال لوَجَبَ زَوَال الايمان بِزَوَال بعض الْأَعْمَال أَو بِزَوَال كلهَا وَلَا يُوجب ذَلِك زَوَال الايمان
١٥٠ - فصل

الْإِيمَان لَا يزِيد وَلَا ينقص بانضمام الطَّاعَات إِلَيْهِ وَلَا ينتقص بارتكاب الْمعاصِي لِأَن الْإِيمَان عبارَة عَن التَّصْدِيق وَالْإِقْرَار',NULL,'254',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (176,'١٥١ -<span data-type="title" id=toc-185> فصل</span>

وَأما تَأْوِيل مَا ورد من الزِّيَادَة فِي الْقُرْآن فَمن وُجُوه',NULL,'256',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (177,'أَحدهَا أَنهم آمنُوا وَصَدقُوا فِي الْجُمْلَة ثمَّ يزْدَاد فرض بعد فرض فيؤمنون بِكُل فرض خَاص فَيَزْدَاد إِيمَانهم من حَيْثُ التَّفْصِيل مَعَ إِيمَانهم بِالْجُمْلَةِ
وَالثَّانِي الثَّبَات والدوام عَلَيْهِ زِيَادَة عَلَيْهِ فِي كل سَاعَة',NULL,'257',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (178,'الثَّالِث زيادتهم إِيمَانًا أَي يَقِينا وإخلاصا فِي كل سَاعَة غير شكّ من حَيْثُ إِنَّهُم إِذا رَأَوْا معْجزَة النَّبِي صلى الله عَلَيْهِ وَسلم يعد معْجزَة نصْرَة بعد نصْرَة وَدخل النَّاس فِي دين الْإِسْلَام ازْدَادَ يقينهم وإخلاصهم فِي صدق نبوته ورسالته وَحَقِيقَة دين الْإِسْلَام مِثَاله إِذا كَانَ وليا وَله مُرِيد كلما رأى مِنْهُ الكرامات وَزِيَادَة الْعِبَادَات ازْدَادَ للمريد يقينه وإخلاصه واعتقاده فِيهِ وَكَذَا هَذِه
١٥٢ -<span data-type="title" id=toc-186><span data-type="title" id=toc-187><span data-type="title" id=toc-188> فصل</span></span></span>

العَبْد إِذا آمن وَصدق وَعرف الله حق مَعْرفَته بتعريفه إِيَّاه وهدايته يحكم بِكَوْنِهِ مُؤمنا حَقًا لِأَن القَوْل بِكَوْن الشَّخْص مُؤمنا حَقًا فِيهِ اعْتِبَار الْحَقَائِق لِأَن من كَانَ مُؤمنا فِي نَفسه حَقِيقَة كمن كَانَ طَويلا أَو قَصِيرا يكون عِنْد الله كَذَلِك
١٥٣ - فصل

الْإِيمَان فِيهِ طرفان فعل الله تَعَالَى وَفعل العَبْد فَمن حَيْثُ إِنَّه فعل الله تَعَالَى وَهُوَ التَّوْفِيق وَالْهِدَايَة غير مَخْلُوق لِأَنَّهُ صفته وَصفته أزلية وَمن حَيْثُ إِنَّه فعل العَبْد وَهُوَ الْإِقْرَار والتصديق فَهُوَ مَخْلُوق والتصديق فَهُوَ مَخْلُوق لِأَن العَبْد بِجَمِيعِ أَفعاله مَخْلُوق بِخلق الله تَعَالَى
١٥٤ - فصل

إِيمَان الْمُقَلّد صَحِيح وَهُوَ الَّذِي اعْتقد جَمِيع مَا فرض',NULL,'258',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (179,'عَلَيْهِ من حُدُوث الْعَالم وَوُجُود الصَّانِع وَقدمه ووحدانيته بِجَمِيعِ صِفَاته وإرسال رسله وإنزال كتبه وَغير ذَلِك اعتقادا صَحِيحا جزما بِلَا شكّ وارتياب من غير دَلِيل عَقْلِي فَهَذَا مُؤمن وإيمانه صَحِيح فِي الدُّنْيَا وَالْآخِرَة',NULL,'260',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (180,'<span data-type="title" id=toc-189>الْإِسْلَام وَالْإِيمَان</span>

١٥٥ -<span data-type="title" id=toc-190> فصل</span>

الْإِيمَان وَالْإِسْلَام شَيْء وَاحِد وَالْإِيمَان وَالْإِسْلَام',NULL,'261',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (181,'يترادفان عَلَيْهِ وكل مُؤمن مُسلم وكل مُسلم مُؤمن دلّ عَلَيْهِ قَوْله تَعَالَى {وَمن يبتغ غير الْإِسْلَام دينا فَلَنْ يقبل مِنْهُ} وَقَوله تَعَالَى {إِن الدّين عِنْد الله الْإِسْلَام} أَي دين الله تَعَالَى هُوَ الْإِسْلَام
وَإِن كَانَ الْإِيمَان غير الْإِسْلَام فَهُوَ غير مَقْبُول وَالْإِيمَان دين لَا محَالة فَلَو كَانَ دينا غير الْإِسْلَام لم يكن دين الله عز وَجل وَلم يكن مَقْبُولًا وَالْأَمر بِخِلَافِهِ
١٥٦ -<span data-type="title" id=toc-191> فصل</span>

اعْلَم أَن قَوْله أَنا مُؤمن إِن شَاءَ الله تَعَالَى اسْتثِْنَاء وَالِاسْتِثْنَاء شكّ وَالشَّكّ فِي أصل الْإِيمَان كفر وضلال دلّ عَلَيْهِ أَن الْكَافِر',NULL,'263',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (182,'لَو قَالَ ابْتِدَاء أَنا مُؤمن إِن شَاءَ الله لَا يصير مُؤمنا لوقت الْإِيمَان أَو قَالَ آمَنت بِاللَّه وَرُسُله إِلَى ألف سنة لم يصر مُؤمنا تفكر أَنه مُؤمن إِلَى ألف سنة يحكم بِكُفْرِهِ فِي الْحَال
وَالِاسْتِثْنَاء شرع فِي الْأَعْمَال المؤقتة لَا المؤبدة وَالْإِيمَان مَعْقُود إِلَى الْأَبَد من غير تَوْقِيت وَإِن قَالَ أكون مُؤمنا إِن شَاءَ الله أَمُوت مُؤمنا إِن شَاءَ الله يكون إِيمَانًا مَقْبُولًا إِن شَاءَ الله يكون مستحسنا لِأَن الْمُؤمن أبدا فَيَنْبَغِي أَن يكون بَين الْخَوْف والرجاء خُصُوصا خوف الخاتمة فَإِنَّهُ من أهم الْأُمُور وَمَا يدْرِي العَبْد أَنه يخْتم عمره على الْإِيمَان أَو على الْكفْر ولأجلها كَانَ أَكثر بكاء الْخَائِفِينَ فَمن هَذَا الْوَجْه يجب الِاسْتِثْنَاء وَيكون شكا فِي الثَّبَات والدوام وَالْقَبُول فِي أصل الْإِيمَان
١٥٧ -<span data-type="title" id=toc-192> فصل</span>

إِيمَان المحسن الْمُسِيء سَوَاء دلّ عَلَيْهِ أَن الله تَعَالَى سوى بَين شَهَادَة الْمَلَائِكَة وَالْمُؤمنِينَ حَيْثُ قَالَ تعلى شهد الله أَنه لَا إِلَه إِلَّا هُوَ وَالْمَلَائِكَة وَأولُوا الْعلم قَائِما بِالْقِسْطِ يَعْنِي الْمُؤمنِينَ فلولا إِيمَانهم',NULL,'264',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (183,'١٥٧ -<span data-type="title" id=toc-193> فصل</span>

إِيمَان المحسن المسيىء سَوَاء دلّ عَلَيْهِ أَن الله تَعَالَى سوى بَين شَهَادَة الْمَلَائِكَة وَالْمُؤمنِينَ حَيْثُ قَالَ تَعَالَى شهد الله أَنه لَا إِلَه إِلَّا هُوَ وَالْمَلَائِكَة وَأولُوا الْعلم قَائِما بِالْقِسْطِ يَعْنِي الْمُؤمنِينَ فلولا ان إِيمَانهم',NULL,'265',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (184,'وَاحِد وَإِلَّا لما سوى شَهَادَتهم وَقَالَ أَيْضا {فَإِن آمنُوا بِمثل مَا آمنتم بِهِ فقد اهتدوا} يَعْنِي أَن أهل الْكتاب وَالْمُشْرِكين إِن هم آمنُوا بِمَا آمن بِهِ مُحَمَّد صلى الله عَلَيْهِ وَسلم وَأَصْحَابه رَضِي الله عَنْهُم فقد احتدوا فَدلَّ أَن إِيمَان الْمُشْركين لَو آمنُوا وَأهل الْكتاب وإيمان أَن النَّبِي صلى الله عَلَيْهِ وَسلم وإيمان الصَّحَابَة سَوَاء
١٥٨ -<span data-type="title" id=toc-194> فصل</span>

السعيد من سعد بِقَضَاء الله وَقدره والشقي من شقي',NULL,'266',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (185,'بِقَضَاء الله وَقدره لقَوْله صلى الله عَلَيْهِ وَسلم بِمَا مَعْنَاهُ (السعيد من سعد فِي بطن أمه والشقي من شقي فِي بطن أمه والأعمال بالخواتم) نسْأَل الله تَعَالَى خَاتِمَة الْخَيْر بمنه وَكَرمه فَمن ختم لَهُ بِالْإِيمَان فقد حصلت لَهُ السَّعَادَة الأبدية وَمن ختم لَهُ بالْكفْر نَعُوذ بِاللَّه من سوء الخاتمة فقد حصلت لَهُ شقاوة أبدية',NULL,'267',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (186,'١٥٩ -<span data-type="title" id=toc-195> فصل</span>

أعلم أَن من آمن يحكم بِكَوْنِهِ مُؤمنا فِي تِلْكَ السَّاعَة وَكَذَا من كفر وَلَا يحكم بِكَوْنِهِ كَافِرًا فِي أول عمره لِأَنَّهُ يُؤَدِّي إِلَى أَن يحكم بِكَوْنِهِ كَافِرًا حِين كَانَ مُؤمنا مُصدقا لله عز وَجل وَرَسُوله صلى الله عَلَيْهِ وَسلم موقنا مخلصا آتِيَا بالعبادات والطاعات وَهَذَا ظَاهر الْفساد وَكَذَا يُؤَدِّي إِلَى عصيان آدم عَلَيْهِ السَّلَام وَكَذَا دَاوُد وَكَذَا يُؤَدِّي لى أَن سحرة فِرْعَوْن كَانُوا مُؤمنين حِين كَانُوا يعْبدُونَ الْأَوْثَان وَاعْلَم أَن من شاخ لَا يتَبَيَّن أَنه كَانَ شَيخا حَال عنفوان شبابه أَو طوليته فِي بطن أمه وَكَمن يُوجد مِنْهُ الْقعُود فِي الْحَال يُسمى قَاعِدا قطعا وَإِن كَانَ يقوم بعد ذَلِك فَدلَّ أَن هَذَا الحكم إِنْكَار الْحَقَائِق وَالله أعلم بِالصَّوَابِ نسْأَل الله تَعَالَى أَن يخْتم لنا بِالْإِيمَان بفضله اللَّهُمَّ صلى على مُحَمَّد وعَلى آله وَصَحبه وَسلم',NULL,'268',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (187,'<span data-type="title" id=toc-196>الْإِمَامَة</span>

١٦٠ -<span data-type="title" id=toc-197> فصل</span>

لَا بُد للْمُسلمين من إِمَام يقوم بمصالحهم من تَنْفِيذ',NULL,'269',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (188,'أحكامهم وَإِقَامَة حدودهم وتجهيز جيوشهم وَأخذ صَدَقَاتهمْ وصرفها إِلَى مستحقيهم لِأَنَّهُ لَو لم يكن لَهُم إِمَام فَإِنَّهُ يُؤَدِّي إِلَى إِظْهَار الْفساد فِي الأَرْض',NULL,'270',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (189,'وشرطة أَن يكون الإِمَام عَاقِلا بَالغا ذكرا عادلا عَالما بالحلال وَالْحرَام مهتديا إِلَى وجوب السادات والتدابير بِأَسْبَاب الحروب قَادِرًا على الْعدْل وعَلى إِقَامَة الْجُمُعَة والأعياد وَغير ذَلِك مِمَّا يحْتَاج إِلَيْهِ النَّاس لِأَنَّهُ لَو لم تكون فِيهِ هَذِه الشَّرَائِط يكون نَاقِصا وعاجزا فَيُؤَدِّي إِلَى إِظْهَار الْفِتَن',NULL,'272',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (190,'١٦١ -<span data-type="title" id=toc-198> فصل</span>
وَمن شرائطها أَن يكون قرشيا لقَوْله صلى الله عَلَيْهِ وَسلم (الْأَئِمَّة من قُرَيْش)',NULL,'273',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (191,'وَالْأَفْضَل أَن يكون هاشميا وَكَونه مَعْصُوما أَو أفضل النَّاس',NULL,'275',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (192,'أَو مُجْتَهدا فِي الْأُصُول وَالْفُرُوع لَيْسَ بِشَرْط
١٦٢ -<span data-type="title" id=toc-199> فصل</span>

الْإِمَامَة تثبت بِاخْتِيَار أهل الصّلاح وتنعقد بِقدر رجل وَاحِد من أهل الْعَدَالَة وَالِاجْتِهَاد وَدلَالَة الثُّبُوت تَفْوِيض النَّبِي صلى الله عَلَيْهِ وَسلم ولَايَة الْإِمَامَة إِلَى الصَّحَابَة حَيْثُ قَالَ (إِن وليتموها أَبَا بكر تَجِدُوهُ ضَعِيفا فِي',NULL,'176',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (193,'نَفسه قَوِيا فِي أَمر الله',NULL,'278',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (194,'وَدلَالَة الِانْعِقَاد أَن الصَّحَابَة رضوَان الله عَلَيْهِم أَجْمَعِينَ لم يشترطوا لَهَا الْإِجْمَاع والإعداد محصورا وَإِنَّمَا اعتبروا فِيهَا العقد
ثمَّ أوجبوا الْمُبَايعَة بعد ذَلِك وَلِهَذَا عقدهَا أَبُو بكر لعمر رَضِي الله عَنْهُمَا وَحده ثمَّ جوز الْبَاقُونَ وَبَايَعُوهُ',NULL,'279',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (195,'١٦٣ -<span data-type="title" id=toc-200><span data-type="title" id=toc-201> فصل</span></span>

طَاعَة الْأَئِمَّة وَاجِبَة وَهِي فرض عين من فروض الشَّرْع لِأَن الإِمَام إِذا لم يكن مُطَاعًا يُؤَدِّي ذَلِك إِلَى إخلال نظام الدّين وَالدُّنْيَا من الْفساد مَا لَا يُحْصى وَكَذَا طَاعَة السلاطين والأمراء والولاة وَاجِبَة لقَوْله تَعَالَى {أطِيعُوا الله وَأَطيعُوا الرَّسُول وأولي الْأَمر مِنْكُم} إِلَّا فِيمَا يأمرون من الْمعاصِي فَحِينَئِذٍ لَا إِثْم على الآبي
١٦٤ - فصل

وَلَا يحل الْخُرُوج عَلَيْهِم وَإِن جاروا وَلَا ينعزلون عَن',NULL,'281',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (196,'الْإِمَامَة وَالْولَايَة وَإِن ظلمُوا أَو ارتكبوا كَبِيرَة وَلَا ندعوا عَلَيْهِم إِذا ظلمُوا بل ندعوا لَهُم بالصلاح وَالْعدْل
١٦٥ -<span data-type="title" id=toc-202> فصل</span>

الصَّحَابَة رَضِي الله عَنْهُم أَجمعُوا على خلَافَة أبي بكر الصّديق رَضِي الله عَنهُ اسْتِدْلَالا بتفويض النَّبِي صلى الله عَلَيْهِ وَسلم وَاتَّفَقُوا عَلَيْهِ وبثبوت خِلَافَته تثبت خلَافَة عمر رَضِي الله',NULL,'283',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (197,'عَنهُ لِأَنَّهُ هُوَ الَّذِي ولاه واستخلفه وَهَكَذَا انْعَقَد الْإِجْمَاع ثمَّ بعد وَفَاة',NULL,'184',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (198,'عمر رَضِي الله عَنهُ اجْتمعت الصَّحَابَة رَضِي الله عَنْهُم على خلَافَة عُثْمَان',NULL,'285',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (199,'رَضِي الله عَنهُ
وَهَؤُلَاء الثَّلَاثَة كَانُوا قريشيين ثمَّ بعد وَفَاة عُثْمَان رَضِي الله عَنهُ اجْتمعت الصَّحَابَة على عَليّ بن أبي طَالب كرم الله وَجهه وَهُوَ قريشي',NULL,'286',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (200,'وهاشمي ثمَّ بعد وَفَاة عَليّ رَضِي الله عَنهُ بأَشْيَاء فَلم يُوجب ذَلِك قدحا فِي حَقهم رَضِي الله عَنْهُم
١٦٦ -<span data-type="title" id=toc-203> فصل</span>

أفضل الْأمة أَبُو بكر ثمَّ عمر ثمَّ عُثْمَان ثمَّ عَليّ رَضِي الله عَنْهُم أَجْمَعِينَ',NULL,'287',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (201,'ثمَّ تَمام الْعشْرَة ثمَّ بَقِيَّة الصَّحَابَة على حسب مَرَاتِبهمْ وأقدارهم ثمَّ التابعون ثمَّ تبع التَّابِعين ثمَّ عُلَمَاء السّلف وَمن بعدهمْ من أَئِمَّة الدّين رَضِي الله عَنْهُم أَجْمَعِينَ
١٦٧ -<span data-type="title" id=toc-204> فصل</span>

وَنحن نحب أهل بَيت رَسُول الله صلى الله عَلَيْهِ وَسلم وَآله وَسلم',NULL,'289',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (202,'وأزواجه وذرياته وقراباته وَالصَّحَابَة أَجْمَعِينَ ونذكرهم بِالْخَيرِ ونثني عَلَيْهِم وندعوا لَهُم بِالْخَيرِ ونترحم عَلَيْهِم وَلَا نفرط فِي حب أحد مِنْهُم وَلَا نتبرأ من أحد مِنْهُم ونحب من يُحِبهُمْ ونبغض من يبغضهم وَمن ذكرهم بِسوء فَهُوَ على غير السَّبِيل وحبهم دين',NULL,'290',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (203,'وإيمان وبغضهم كفر وطغيان ونحسن القَوْل فيهم ونسكت عَمَّا جرى',NULL,'291',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (204,'بَينهم رَضِي الله عَنْهُم أَجْمَعِينَ
١٦٨ -<span data-type="title" id=toc-205> فصل</span>

وَمَا جرى بَين عَليّ وَمُعَاوِيَة رَضِي الله عَنْهُمَا كَانَ مَبْنِيا على الِاجْتِهَاد والمناعة من مُعَاوِيَة لعَلي وَعلي رَضِي الله عَنهُ كَانَ مصيبا فِي',NULL,'292',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (205,'جَمِيع مَا عمل من خُرُوجه وصلحه وَغَيرهمَا دَار الْحق حَيْثُ دَار كرم الله وَجهه ورضى رَضِي الله عَنهُ الْأَبْرَار
وَقد قيل لكل مُجْتَهد نصيب وكل مُجْتَهد مُصِيب إِذْ ظن عَليّ أَن تَسْلِيم قتلة عُثْمَان رَضِي الله عَنهُ مَعَ كَثْرَة عَشَائِرهمْ واختلاطهم بالعسكر',NULL,'293',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (206,'يُؤَدِّي إِلَى إضطراب أَمر الْإِمَامَة فِي بدايتها فَرَأى التَّأْخِير أصوب وَظن',NULL,'294',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (207,'مُعَاوِيَة أَن تَأْخِير أَمرهم مَعَ عظم جنايتهم يُوجب الْعَزْل من الْإِمَامَة وَتعرض دِمَاء للسفك
وَقد قيل الْمُصِيب وَاحِد فَلم نَذْهَب إِلَى تخطية عَليّ رَضِي الله عَنهُ دنو تَحْصِيل أصلا فَثَبت تخطية مُعَاوِيَة بِالضَّرُورَةِ',NULL,'295',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (208,'١٦٩ -<span data-type="title" id=toc-206> فصل</span>

فِي مسَائِل مُتَفَرِّقَة واعتقد أَن من آمن بِاللَّه وَمَلَائِكَته وَكتبه وَرُسُله وَالْيَوْم الآخر وبالقدر خَيره وشره من الله تَعَالَى وَالْعَبْد مكتسب لَهما ويعتقد الْحَلَال حَلَالا وَالْحرَام حَرَامًا وَالْحق حَقًا وَالْبَاطِل بَاطِلا وَلَا يكون سبابا وَلَا طعانا فِي الصَّحَابَة وَأهل الْبَيْت وَالتَّابِعِينَ وَتبع التَّابِعين',NULL,'297',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (209,'وَمن بعدهمْ من أَئِمَّة الدّين واعتقادهم وَلَا مرتكبا شَيْئا من الْمُحرمَات والمنهيات مستحلا لَهُ يحكم بِكَوْنِهِ مُسلما حَقًا وَلَا نرى السَّيْف على أحد من الْمُسلمين إِلَّا من وَجب عَلَيْهِ ذَلِك بِحَق ونرى الصَّلَاة خلف كل بر وَفَاجِر من أهل الْقبْلَة وعَلى من مَاتَ مِنْهُم ونسمى أهل قبلتنا مُسلمين',NULL,'298',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (210,'مُؤمنين مَا لم يظْهر مِنْهُم خلاف وَنَتبع السّنة وَالْجَمَاعَة ونجتنب الْبِدْعَة',NULL,'299',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (211,'والضلالة والأهواء الْمُخْتَلفَة الردية ونحب أهل الْخَيْر والسداد ونبغض أهل الشَّرّ وَالْفساد وَلَا نخالف جمَاعَة الْمُسلمين ونرى الْجَمَاعَة حَقًا وصوابا والفرقة زيغا وَعَذَابًا وَمَا رَآهُ الْمُؤْمِنُونَ حسنا فَهُوَ عِنْد الله حسن',NULL,'300',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (212,'وَلَا نكفر أحدا من أهل الْقبْلَة بذنب مَا لم يستحله وَلَا نخرج العَبْد من',NULL,'301',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (213,'الْإِيمَان غاا بجحود مَا أدخلهُ فِيهِ وَالْإِيمَان وَاحِد وَأَهله فِي أَصله سَوَاء',NULL,'302',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (214,'والتفاضل بَينهم بالتقوى والمخالفون فِي أصُول الدّين هم أهل الْأَهْوَاء والبدع وَلَا ننزل أحدا من الْمُسلمين جنَّة وَلَا نَارا وَلَا نشْهد عَلَيْهِم',NULL,'303',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (215,'بِكفْر وَلَا شرك وَلَا نفاق مَا لم يظْهر مِنْهُم ذَلِك وَنذر سرائرهم إِلَى الله تَعَالَى ونشهد للأنبياء عَلَيْهِم السَّلَام بِالْجنَّةِ وَلمن شهد لَهُ رَسُول الله صلى الله عَلَيْهِ وَسلم',NULL,'304',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (216,'وَنَرْجُو للمحسنين من الْمُؤمنِينَ وَلَا نَأْمَن عَلَيْهِم ونشهد لَهُم بِالْجنَّةِ ونستغفر لمسيئهم ونخاف عَلَيْهِم وَلَا',NULL,'305',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (217,'نقنطهم والأمن والإياس ينقلان عَن مِلَّة الْإِسْلَام وسبيل الْحق بَينهمَا الْأَهْل الْقبْلَة والمؤمنون كلهم أَوْلِيَاء الرَّحْمَن وَأكْرمهمْ عِنْده أطوعهم لَهُ وَالله عز وَجل مولى الْمُؤمنِينَ وَأَن الْكَافرين لَا مولى لَهُم',NULL,'306',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (218,'١٧٠ -<span data-type="title" id=toc-207> فصل</span>

ونعتقد أَن الْغسْل وَالْوُضُوء وَالتَّيَمُّم وَالْمسح على',NULL,'307',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (219,'الْخُفَّيْنِ وَالصَّلَاة وَالزَّكَاة وَالصَّوْم وَالْحج وَالْجُمُعَة وَالْجَمَاعَة',NULL,'308',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (220,'وَالْأَذَان وَالْإِقَامَة وَالْجهَاد وَالصَّلَاة على ... ... . .',NULL,'309',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (221,'الْجِنَازَة وَصَلَاة الْعِيدَيْنِ وَالْأَمر بِالْمَعْرُوفِ وَالنَّهْي عَن الْمُنكر',NULL,'310',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (222,'وصلَة الرَّحِم وَطَاعَة الْوَالِدين وَغير ذَلِك من جَمِيع أوَامِر الشَّرْع حق',NULL,'311',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (223,'وَصدق والكف عَن أَذَى الْجَار وَعَن جَمِيع النَّاس وَاجِب وَالْكذب والغيبة والنميمة والبهتان وَشَهَادَة',NULL,'312',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (224,'الزُّور ... ... . .',NULL,'313',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (225,'وَإِيقَاد نَار الْفِتْنَة بَين الْمُسلمين حرَام
وَكَذَا لعن الْمُسلم وَدُعَاء السوء وَإِن كَانَ ظَالِما حرَام وَلَكِن يَقُول اللَّهُمَّ إِن كَانَ من أهل التَّوْبَة فتب عَلَيْهِ وَإِن لم يكن من أَهلهَا فَكف شَره عَنَّا وَعَن جَمِيع الْمُسلمين وَكَذَا الطعْن فِي أَئِمَّة الدّين',NULL,'314',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (226,'وعلماء السّلف وارتكاب جَمِيع المنهيات حرَام وَأَن دين الله فِي السَّمَاء وَاحِد وَهُوَ الْإِسْلَام ونسأل الله الثَّبَات على الْإِسْلَام',NULL,'315',NULL,NULL,'0');
INSERT INTO [b6371] VALUES (227,'فَهَذَا ديننَا واعتقادنا ظَاهرا وَبَاطنا وَالله أعلم
تمّ كتاب الغزنوي فِي أصُول الدّين بِحَمْدِهِ وعونه وَحسن توفيقه وَصلى الله على سيدنَا مُحَمَّد وعَلى آله وَصَحبه وَسلم تَسْلِيمًا وَالْحَمْد لله رب الْعَالمين آمين سنة ١١٣٩ بعد ألف ومئة من الْهِجْرَة النَّبَوِيَّة على صَاحبهَا أفضل الصَّلَاة وَالسَّلَام على يَد أفقر الْعباد إِلَى الله تَعَالَى أَحْمد بن أبي الْخَيْر المرحومي غفر الله وَلمن دَعَا لَهُ بِالرَّحْمَةِ آمين',NULL,'316',NULL,NULL,'0');
