INSERT INTO [b682] VALUES (87,'٨٥ - (صحيح) عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: صنفان من أهل النار لم أرهما: قوم معهم سياط كأذناب البقر يضربون بها الناس (إشارة الى الحكام الظلمة أعداء الشعوب ونساء كاسيات عاريات مميلات مائلات رؤوسهن كأسنمة البخت المائلة لا يدخلن الجنة ولا يجدن ريحها وإن ريحها ليوجد من مسيرة كذا وكذا)',NULL,'70',NULL,NULL,'0');
