INSERT INTO [b6887] VALUES (1,'<span data-type="title" id=toc-1>بِسم الله الرَّحْمَن الرَّحِيم</span>
INSERT INTO [b6887] VALUES (2,'قَالَ: أَنا إِسْمَاعِيل بن أَحْمد بن عمر، قَالَ: أخبرنَا أَبُو الْحُسَيْن بن النقور أَحْمد بن مُحَمَّد، قَالَ: أخبرنَا أَبُو الْقَاسِم عبيد الله بن أَحْمد الصيدلاني، قَالَ: أخبرنَا أَبُو عمر مُحَمَّد بن عبد الْوَاحِد الزَّاهِد قَالَ:',NULL,'166',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (3,'<span data-type="title" id=toc-3>فَاتِحَة الْكتاب</span>
INSERT INTO [b6887] VALUES (4,'(صفحة فارغة)',NULL,'168',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (5,'<span data-type="title" id=toc-4>وَمن سُورَة الْبَقَرَة</span>
INSERT INTO [b6887] VALUES (6,'أجد على النَّار هدى} أَي: هادياً
INSERT INTO [b6887] VALUES (7,'وَالْمَرَض: الْكفْر وَمِنْه قَوْله - عز وَجل: {فِي قُلُوبهم مرض}
INSERT INTO [b6887] VALUES (8,'والند: والمثل، وَمِنْه قَوْله - عز وَجل: {فَلَا تجْعَلُوا لله أندادا} أَي: أَمْثَالًا. {ويسفك الدِّمَاء} أَي: يصب الدِّمَاء بِغَيْر حق , ويسفك - أَيْضا: يصب الدِّمَاء بِحَق. {الَّذين يظنون} : يتيقنون , ويظنون - فِي مَكَان آخر: يَشكونَ. {يستحيون} أَي: يَسْتَبقُونَ.',NULL,'172',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (9,'و {الْمَنّ} : الْعَسَل. و {السلوى} طَائِر، والسلوى فِي غير الْقرَان: الْعَسَل.
INSERT INTO [b6887] VALUES (10,'{الطّور} : الْجَبَل. وكل عوان فَهُوَ بعد شَيْء، يُقَال: حَرْب عوان، إِذا كَانَت قبلهَا حَرْب، هَذَا أصل الْعوَان، والعوان - فِي غير هَذَا من الْحَيَوَان - الشَّيْء بَين الشَّيْئَيْنِ، لَا كَبِير وَلَا صَغِير. والشية: لون مُخَالف لسَائِر الْجلد.',NULL,'174',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (11,'والأماني: التِّلَاوَة. و {تظْهرُونَ} : تعاونون. و {الخزي} : المباعدة من الْخَيْر.
INSERT INTO [b6887] VALUES (12,'طهر.
INSERT INTO [b6887] VALUES (13,'{وأطعنا} : أَمرك. و {الْفِتْنَة} : الاختبار، والفتنة: المنحة، والفتنة: المَال، والفتنة: الْأَوْلَاد،
INSERT INTO [b6887] VALUES (14,'عَنهُ.
INSERT INTO [b6887] VALUES (15,'و {الْعلم} هَاهُنَا: الْقُرْآن.
INSERT INTO [b6887] VALUES (16,'الْقمَار.
INSERT INTO [b6887] VALUES (17,'و {الملا} : الرؤساء من النَّاس.
INSERT INTO [b6887] VALUES (18,'{صَفْوَان} : جبل أملس.
INSERT INTO [b6887] VALUES (19,'والطيبات: الْحَلَال، فِي كل الْقرَان. {الْأَلْبَاب} : الْعُقُول، فِي كل مَكَان. {ابْتِغَاء وَجه الله} أَي: طلب وَجه الله - عز وَجل - وَرضَاهُ. {فأذنوا} : فاعلموا، وآذنتكم، أَي: أعلمتكم. {وَلَا يبخس} أَي لَا ينقص. {سَفِيها} أَي: ضَعِيف الْعقل.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (20,'{بِالْعَدْلِ} أَي: بِالْحَقِّ والإنصاف. {أَن تضل} أَي: أَن تنسى. و {لَا تسأموا} أَي: لَا تملوا. و {أقسط} : أعدل.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (21,'<span data-type="title" id=toc-5>وَمن سُورَة آل عمرَان</span>
INSERT INTO [b6887] VALUES (22,'{شهد الله} أَي: قَالَ الله، وَشهد الله، أَي: كتب الله، وَشهد الله، أَي: علم الله. و {الْقسْط} [٣ / أ] : الْعدْل. و {حبطت} : بطلت وَسَقَطت. وَقَوله: {إِلا أَيَّاماً معدوات} قَالَ: هِيَ عدد الْأَيَّام الَّتِي عبدُوا فِيهَا الْعجل، وَقَالُوا: نعذب بِعَدَد تِلْكَ الْأَيَّام، ثمَّ ندخل الْجنَّة.
INSERT INTO [b6887] VALUES (23,'والتقاة والتقية وَاحِد، والاتقاء وَالتَّقوى، كُله بِمَعْنى وَاحِد. {محررا} : معتقا معدا لطاعتك فِي بَيت الْمُقَدّس. {وكفلها} : ضمهَا، وكفلها ك ضمنهَا. {الْمِحْرَاب} : الفرفة. {حصورا} : أَي: لَا يَأْتِي النِّسَاء.',NULL,'187',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (24,'وَالرَّمْز: الْإِشَارَة. و {الحواريون} الْأَنْصَار، والحواريون: الْخَاصَّة من الصَّحَابَة. {ومكروا} أَي: ودبروا، ومكر الله، أَي: دبر الله. {وَالله خير الماكرين} أَي: خير المدبرين.',NULL,'188',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (25,'و {نبتهل} أَي: نَدْعُو ونلتعن، والبهلة والبهلة جَمِيعًا: اللَّعْنَة. إِلَى {كلمة سَوَاء} أَي: إِلَى نصفة. {وَجه النَّهَار} أَي: صدر النَّهَار. {لَا خلاق لَهُم} أَي: لَا نصيب لَهُم من الْخَيْر، والخلاق: الدّين.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (26,'{وَمن يبتغ غير الْإِسْلَام دينا فَلَنْ يقبل مِنْهُ} أَي: من يطْلب. {فَمن افترى على الله الْكَذِب} أَي: كذب على الله - تبَارك وَتَعَالَى. و {حَنِيفا} أَي: مُسْتَقِيمًا على الْإِسْلَام. وَقَوله: {كُنْتُم خير أمة} أَي: أَنْتُم، وَقَوله - أَيْضا: كُنْتُم خير أمة، أَي: فِي علم الله - عز وَجل.
INSERT INTO [b6887] VALUES (27,'وَقَوله - تبَارك وَتَعَالَى: {لَا يألونكم خبالا} أَي: لَا يقصرون. و {خبالا} فَسَادًا. وَقَوله - عز وَجل: {طرفا} أَي: قِطْعَة. {وكأين من نَبِي} أَي: وَكم من نَبِي. وَقَوله - عز وَجل: {ثَوَاب الدُّنْيَا} الثَّوَاب يكون خيرا',NULL,'191',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (28,'وَيكون شرا، وَكَذَلِكَ: الْبشَارَة: تكون بِخَير، وَتَكون بشر، وَمن الثَّوَاب الشَّرّ قَوْله - جلّ وَعز: {فأثابكم غما بغم} . {إِذْ تحسونهم} أَي: تقتلونهم. {لبرز الَّذين} أَي: لظهر. {لانْفَضُّوا بَين حولك} أَي: لتفرقوا.',NULL,'192',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (29,'{وَإِن يخذلكم} أَي: يترككم من نَصره. {يغل} أَي: يخون
INSERT INTO [b6887] VALUES (30,'{يخوف أولياءه} أَي: يخوفكم بأوليائه. {فَمن زحزح عَن النَّار} أَي: فَمن نجي. {فقد فَازَ} أَي: فقد نجا، والفوز الْعَظِيم: النَّجَاء الْكثير. {الْغرُور} : الدُّنْيَا، والغرور، الشَّيْطَان [٣ / ب] . {لتبلون} أَي: لتختبرن.',NULL,'194',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (31,'<span data-type="title" id=toc-6>وَمن سُورَة النِّسَاء</span>
INSERT INTO [b6887] VALUES (32,'قَالَ وَقَوله: {نحلة} أَي: دينا وتدينا. قَوْله: {سديدا} أَي: حَقًا مستويا. {يُورث كَلَالَة} : الْكَلَالَة: النّسَب كُله، مَا خلا الْوَلَد والوالدين.',NULL,'196',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (33,'{وَلَا تعضلوهن} أَي: لَا تمنعوهن.
INSERT INTO [b6887] VALUES (34,'{بالجبت والطاغوت} قَالَ: الجبت: رَئِيس الْيَهُود، والطاغوت: رَئِيس النَّصَارَى.
INSERT INTO [b6887] VALUES (35,'الجريمة. {يصدون عَنْك صدودا} أَي: يعرضون عَنْك إعْرَاضًا، وَصد، أَي: أعرض، وَصد: ضج، وَصد: منع، وَصد: هجر، وَصد يصد، إِذا ضج، وَالْبَاقِي كُله من: فعل يفعل مضموم. {حرجا} أَي: ضيقا. {فانفروا ثبات} أَي: فرقا.',NULL,'199',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (36,'{أَوانْفِرُوا جَمِيعاً} أَي انفروا مُجْتَمعين. {الَّذين يشرون الْحَيَاة الدُّنْيَا} أَي: يبيعون، ويشعرون، أَي: يشْتَرونَ. {وَلَو كُنْتُم فِي بروج مشيدة} أَي: قُصُور مُطَوَّلَة. {يستنبطونه} أَي: يستخرجون مَعَانِيه.',NULL,'200',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (37,'{يصلونَ} ينتسبون. {حصرت صُدُورهمْ} أَي: ضَاقَتْ، و {حَصِرَةْ صُدُورُهُمْ} أَي: ضيقَة صُدُورهمْ. {عرض الْحَيَاة الدُّنْيَا} أَي: مَتَاع الْحَيَاة الدُّنْيَا.',NULL,'201',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (38,'{مراغما} أَي: مضطربا، يُقَال: عبد مراغم من موَالِيه، أَي: مُضْطَرب. {على الْمُؤمنِينَ كتابا موقوتا} أَي: فرضا مَفْرُوضًا فِي أَوْقَات مَعْلُومَة. {مِنْ بخوَاهُم} النَّجْوَى الْجَمَاعَة والنجوى: الْكَلَام الْخَفي.',NULL,'202',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (39,'{فليبتكن آذان الْأَنْعَام} أَي: فليقطعن آذان الْإِبِل. {فليغيرن خلق الله} قَالَ: يَعْنِي: الإخصاء. {قيلا} أَي: قولا. {خَلِيلًا} : محبا. {وَكيلا} أَي: كَفِيلا كَافِيا. {مذبذبين} أَي: مرددين بَين ذَلِك.',NULL,'203',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (40,'{لَا إِلَى هَؤُلَاءِ وَلَا إِلَى هَؤُلَاءِ} : لَا إِلَى الْمُؤمنِينَ وَلَا إِلَى الْكَافرين. و {الدَّرك} : الطَّبَق من أطباق جَهَنَّم، ويسكن - أَيْضا. {واعتصموا بِاللَّه} أَي: وامتنعوا بِاللَّه. {قُلُوبنَا غلف} أَي: عَلَيْهَا مَانع من الْفَهم، وغلف: جمع',NULL,'204',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (41,'غلاف، وَمَعْنَاهُ: قُلُوبنَا أوعية للْعلم، فَمَا بالها لَا تعي مَا تَقول أَنْت؟ {وَمَا قَتَلُوهُ يَقِينا} قَالُوا: " يَقِينا " بدل من الْهَاء، كَأَنَّهُ فال: وَمَا قتلوا الْيَقِين يَقِينا، وَيجوز: وَمَا قتلوا الشَّك يَقِينا، وَيجوز: وَمَا قتلوا الشبيه يَقِينا. وَقَوله: {بل رَفعه الله إِلَيْهِ} هُوَ رد لكل مَا ادَّعَتْهُ النَّصَارَى',NULL,'205',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (42,'على الْمَسِيح - صلى الله عَلَيْهِ وَسلم. {لن يستنكف الْمَسِيح} أَي: لن يأنف الْمَسِيح. {أَن تضلوا} بِمَعْنى: أَن لَا تضلوا.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (43,'<span data-type="title" id=toc-7>وَمن سُورَة الْمَائِدَة</span>
INSERT INTO [b6887] VALUES (44,'والكاد عَلَيْهِم. و {عزرتموهم} أَي: نصرتموهم، وَالتَّعْزِير - فِي كَلَام الْعَرَب: التوقير، وَالتَّعْزِير - أَيْضا النَّصْر بِاللِّسَانِ، والتقرير - أَيْضا: النَّصْر بِالسَّيْفِ، وَالتَّعْزِير - أَيْضا التَّوْقِيف على الْفَرَائِض وَالْأَحْكَام، وَالتَّعْزِير: دون الْحَد، وَلَو بِسَوْط وَاحِد. {فأغرينا بَينهم} أَي: ألقينا.',NULL,'208',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (45,'و {المقدسة} المطهرة. {فَلَا تأس} : فَلَا تحزن. {فطوعت} أَي: فسامحت. و {للسحت} : الْحَرَام.',NULL,'209',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (46,'{ومهيمنا عَلَيْهِ} أَي: شَاهدا. {شرعة} أَي: مِلَّة. و (َمِنْهَاجاً} أَي: طَريقَة دين. {يَبْغُونَ} أَي: يطْلبُونَ.',NULL,'210',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (47,'{فِي قُلُوبهم مرض} أَي: كفر. {أَذِلَّة على الْمُؤمنِينَ} أَي: رحماء رَفِيقَيْنِ بِالْمُؤْمِنِينَ. {أعزة على الْكَافرين} أَي: غِلَاظ شَدَّاد على الْكَافرين. {تَنْقِمُونَ} أَي: تنكرون. {بِاللَّغْوِ} أَي: مَا كَانَ بِلَا نِيَّة من الْأَيْمَان، واللغو: الهذيان من الْكَلَام، لَا فِي الْأَيْمَان، واللغو: مَا لَا يحْسب من الْحَيَوَان فِي',NULL,'211',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (48,'الصَّدَقَة، واللغا واللغو وَاحِد. {جنَاح} أَي إِثْم. {تناله أَيْدِيكُم} يَعْنِي: بيض النعام، وَالْعرب تَقول: صدت نعاما، وصدت بَيْضَة، أَي: أخذتهما بيَدي. {ورماحكم} يَعْنِي: الْحمير الوحشية، والنعام الجافل.',NULL,'212',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (49,'{أَو عدل ذَلِك} أَي: وَقِيمَة ذَلِك.
INSERT INTO [b6887] VALUES (50,'والوصيلة: قَالَ: كَانَت الْعَرَب إِذا ولدت الشَّاة جديين - أخذُوا وَاحِدًا لأَنْفُسِهِمْ، وذبحوا الآخر للصنم، فَإِذا ولدت جديا وعناقا لم يذبحوها، وَلم يذبحوا أخاها، وَقَالُوا: قد وصلته، وَلم تذبح، وَلم تُؤْكَل، وربيت وَقَالُوا: قد وصلت أخاها. قَالَ أَبُو الْعَبَّاس ثَعْلَب: وَأجْمع النَّاس كلهم على أَن الوصيلة لَا تكون إِلَّا فِي الْغنم.',NULL,'214',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (51,'{وَلَا حام} قَالَ: الحامي: الْبَعِير، الَّذِي قد خرج من صلبه عشرَة بطُون، فَإِذا كَانَ هَكَذَا - قَالُوا: قد حمى ظَهره، فَلَا يركب وَلَا يحمل عَلَيْهِ شَيْء، وَيَقُولُونَ: لَا يحل لنا أَن نَسْتَعْمِلهُ. {فَإِن عثر} أَي: اطلع.',NULL,'215',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (52,'(صفحة فارغة)',NULL,'216',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (53,'<span data-type="title" id=toc-8>وَمن سُورَة الْأَنْعَام</span>
INSERT INTO [b6887] VALUES (54,'وَالنَّهَار. {فاطر السَّمَاوَات} : خَالق. {وَمن بلغ} أَي: وَمن بلغه الْقُرْآن إِلَى يَوْم الْقِيَامَة. {يعرفونه} أَي: يعْرفُونَ مُحَمَّدًا - صلى الله عَلَيْهِ وَسلم - فِي التَّوْرَاة بِصفتِهِ ونعته، بشرعه وشرائعه. قَالَ ابْن الْأَعرَابِي: وَمِنْه قَول عمر
INSERT INTO [b6887] VALUES (55,'وَجل - فِي صفة مُحَمَّد - صلى الله عَلَيْهِ وَسلم؟ قَالَ: نعرفه كَمَا نَعْرِف أبناءنا، ونعرفه بعد هَذَا معرفَة أبين من معرفَة أَوْلَادنَا، قَالَ: فَقَالَ عمر: كَيفَ؟ قَالَ: يَا أَمِير الْمُؤمنِينَ إِن أَحَدنَا ليشك فِي وَلَده، حَتَّى يَقُول: هُوَ ابْني، لَيْسَ هُوَ ابْني، وَنحن لَا نشك فِي مُحَمَّد - صلى الله عَلَيْهِ وَسلم - أَنه صَادِق مُصدق. {بل بدا لَهُم} أَي: ظهر لَهُم، وَمِنْه قَوْله تَعَالَى: (ثُمَّ',NULL,'219',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (56,'بَدَا لَهُمْ مِنْ بَعْدِ مَا رَأَوُا الأياتِ} أَي: ظهر لَهُم من الرَّأْي أَن يسجنوه. {نَمُوتُ وَنَحْيَا} قَالَ ثَعْلَب: اخْتلف النَّاس، فَقَالَت طَائِفَة: هُوَ مقدم ومؤخر، [٥ / أ] وَمَعْنَاهُ: نحيا وَنَمُوت وَلَا نحيا بعد ذَلِك. وَقَالَت طَائِفَة: مَعْنَاهُ: نحيا وَنَمُوت وَلَا نحيا أبدا، وتحيا أَوْلَادنَا بَعدنَا، فَجعلُوا حَيَاة أَوْلَادهم بعدهمْ كحياتهم، ثمَّ',NULL,'220',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (57,'قَالُوا: وَيَمُوت أَوْلَادنَا بَعدنَا، فَلَا نحيا نَحن وَلَا هم. {فَذُوقُوا الْعَذَاب} قَالَ: الذَّوْق كَون بالفم وَبِغير الْفَم. {وهم يحملون أوزارهم} أَي: أثقال الآثام. {فَلَمَّا نسوا مَا ذكرُوا بِهِ} أَي: تركُوا. {وَيعلم مَا جرحتم بِالنَّهَارِ} أَي: مَا كسبتم. {وهم لَا يفرطون} أَي: لَا يقصرون.',NULL,'221',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (58,'{أَن تبسل} أَي: أَن تحبس فِي جَهَنَّم. {عَذَاب الْهون} أَي: عَذَاب الهوان. وَقَالَ: هان يهون هونا، والهون الِاسْم. وَمن الرِّفْق: هان يهون هونا، يتَّفق فيهمَا المصدران. وَقَوله - جلّ وَعز: {يَمْشُونَ على الأَرْض هونا} أَي: بِرِفْق وَسُكُون ووقار. {لقد تقطع بَيْنكُم} أَي: تقطع وصلكم، وَمن قَرَأَ:',NULL,'222',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (59,'{بَيْنكُم} أَي: انْقَطع الَّذِي بَيْنكُم. {وخرقوا} أَي: كذبُوا.
INSERT INTO [b6887] VALUES (60,'{زخرف القَوْل غرُورًا} أَي: حسن القَوْل بترقيش الْكَذِب، والزخرف فِي غير هَذَا الْموضع: الذَّهَب. {ولتصغى إِلَيْهِ} أَي: لتميل إِلَيْهِ، وَمِنْه قَوْله - جلّ وَعز: {فقد صغت قُلُوبكُمَا} أَي: مَالَتْ. {صغَار عِنْد الله} أَي: ذل.',NULL,'224',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (61,'{ضيقا حرجا} الْحَرج: أَشد الضّيق. {حمولة وفرشا} : الحمولة: القوية على الْحمل، والفرش: الصَّغِيرَة الضعيفة عَن الْحمل
INSERT INTO [b6887] VALUES (62,'{أَو الحوايا} فالحوايا: بَنَات اللَّبن، واحدتها: حاوية وحوية. {من إملاق} أَي: من فقر. {وصدف عَنْهَا} : أعرض عَنْهَا.',NULL,'226',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (63,'<span data-type="title" id=toc-9>وَمن سُورَة الْأَعْرَاف</span>
INSERT INTO [b6887] VALUES (64,'{مَدْحُورًا} [٥ / ب] أَي: مطروداً، وَيُقَال: منفيا. {وريشا} كل شَيْء يعِيش بِهِ الْإِنْسَان، فَهُوَ ريش من مَال أَو مَتَاع أَو مَأْكُول أَو مشروب، قَالَ: والرياش مثله. {ولباس التَّقْوَى} قَالَ: هُوَ الْحيَاء.',NULL,'228',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (65,'{من غل} أَي: من حقد. {الَّذين يصدون} قَالَ: يصدون: يعرضون، ويصدون، أَي: يضجون. {أَو مِمَّا رزقكم الله} قَالَ: يَعْنِي الْخبز وَالطَّعَام، قَالَ أَبُو عبد الله: فَلم يُصَرح الله - عز وَجل - بِذكر الْخبز وَالطَّعَام، لقلته عِنْده، وَصرح بِذكر المَاء، لِأَنَّهُ شرفه، لِأَن كل شَيْء خلقه - من الْحَيَوَان والفاكهة وَغير ذَلِك - حَيَاته بِالْمَاءِ، وَهُوَ قَوْله - جلّ',NULL,'229',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (66,'وَعز: {وَجَعَلْنَا مِنَ الْمَاءِ كُلَّ شَيْءٍ حَيّ} . وَقَوله: {أولم يهد للَّذين يَرِثُونَ الأَرْض} : أَي: أولم يبين. {نزع يَده} أَي: أخرج يَده. {إِن هَؤُلَاءِ متبر مَا هم فِيهِ} أَي: مهلك مَا هم فِيهِ، ومدمر عَلَيْهِم. {لَهُ خوار} أَي: صياح.',NULL,'230',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (67,'{وَلما سقط فِي أَيْديهم} أَي: ندموا عِنْدَمَا فعلوا. قَالَ أَبُو عبد الله: وَمِنْه قَوْله تَعَالَى: {لم يرَوا أَنه لَا يكلمهم} أَي: عَابَ الْعجل بذلك، وَهَذَا دَلِيل على أَن الله يتَكَلَّم، وَلم يزل متكلماً، لِأَنَّهُ لَا يكون هُوَ بِصفة مَا عَابَ.',NULL,'231',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (68,'{غَضْبَان أسفا} أَي: ممتلئ غيظاً. قَالَ أَبُو عبد الله: وَقَوله: جلّ وَعز {وَكَذَلِكَ نجزي المفترين} قَالَ يَعْنِي: أهل الْبدع. {وَلما سكت عَن مُوسَى الْغَضَب} أَي: سكن. {وَيَضَع عَنْهُم إصرهم} اْْلإصر: الثّقل فِي كل شَيْء من الْكَلَام والفعال وَالدّين. {بِعَذَاب بئيس} أَي: شَدِيد.',NULL,'232',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (69,'{فخلف من بعدهمْ خلف} الْخلف: الرَّدِيء من كل شَيْء. {وَإِذ نتقنا الْجَبَل} أَي: رفعناه. {وَلكنه أخلد إِلَى الأَرْض} أَي: مَال.',NULL,'233',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (70,'{وَالْآصَال} : العشيات.',NULL,'234',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (71,'<span data-type="title" id=toc-10>وَمن سُورَة الْأَنْفَال</span>
INSERT INTO [b6887] VALUES (72,'قَوْله - جلّ وَعز: {وَلِتَطْمَئِنَّ بِهِ [٦ / أ] قُلُوبُكُمْ} أَي: ترجو وتلين عِنْد الْوَعيد، وَذكر الله - عز وَجل. و {الشَّوْكَة} : السِّلَاح، وحدة الْحَرْب وخشونتها. {أَمَنَة مِنْهُ} قَالَ: الأمنه والأمان والأمن كُله بِمَعْنى وَاحِد، وَقد حكيت: إمن - بِالْكَسْرِ - وَالْأول أفْصح. {الرعب} : الْفَزع.',NULL,'236',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (73,'{وَاتَّقوا فتْنَة لَا تصيبن الَّذين ظلمُوا مِنْكُم خَاصَّة} قَالَ: تصيب الظَّالِمين وَالْمُؤمنِينَ، فالظالمون معذبون، وَالْمُؤمنِينَ ممتحنون ممحصون. {إِلَّا مكاء وتصدية} قَالَ: المكاء: الصفير، والتصدية: التصفيق. {بالعدوة الدُّنْيَا} : جَانب الْوَادي مِمَّا يَلِي النَّاس.',NULL,'237',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (74,'و {العدوة القصوى} : الْبَعِيدَة من النَّاس، لَيْسَ بِسَمَاع.',NULL,'238',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (75,'{وَتذهب ريحكم} الرّيح: الْغَلَبَة.
INSERT INTO [b6887] VALUES (76,'الصُّلْح، فاجنح لَهَا: أَي فمل أَنْت: - أَيْضا - إِلَى الصُّلْح، لِأَنَّهُ قَالَ - جلّ وَعز: {وَالصُّلْح خير} . {حَتَّى يثخن فِي الأَرْض} : حَتَّى يغلب وَيقتل. {تُرِيدُونَ عرض الدُّنْيَا} أَي: تُرِيدُونَ مَتَاع الدُّنْيَا.',NULL,'240',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (77,'<span data-type="title" id=toc-11>وَمن سُورَة بَرَاءَة</span>
INSERT INTO [b6887] VALUES (78,'{بِمَا رَحبَتْ} أَي: اتسعت، يُقَال مِنْهُ: فعل يفعل فعلا. {الشقة} السفرة الْبَعِيدَة الشاقة. {إِلَّا خبالا} أَي: إِلَّا فَسَادًا. {ولأوضعوا} : ولأسرعوا إِلَى الْهَرَب.',NULL,'242',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (79,'{خلالكم} أَي: مَا تفرق من الْجَمَاعَة لطلب الْخلْوَة للفرار. {وَفِيكُمْ سماعون لَهُم} قَالَ: يَعْنِي: الجواسيس. {إِلَّا مَا كتب الله لنا} مَعْنَاهُ: إِلَّا مَا كتب الله علينا. {من يَلْمِزك} أَي: يعيبك.',NULL,'243',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (80,'{وهم يجمحون} [٦ / ب] أَي: وهم يَمْشُونَ بالعجلة فِي جَانب. {أَنه من يحادد الله وَرَسُوله} أَي: يخالفهما. {والمؤتفكات} : المتقلبات بالخسف والزلازل. {وَمَا نقموا} أَي: وَمَا أَنْكَرُوا. {أولُوا الطول} أَي: أولو الْغنى وَالْمَال الْكثير.',NULL,'244',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (81,'{رَضوا بِأَن يَكُونُوا مَعَ الْخَوَالِف} أَي: مَعَ النِّسَاء. {المعذرون} قَالَ: المعذرون المقصرون، والمعذرون: الَّذين لَهُم عذر. قَالَ: وَرُوِيَ عَن ابْن عَبَّاس أَنه قَالَ: " لعن الله المعذرين، ورحم الله المعتذرين ".',NULL,'245',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (82,'(صفحة فارغة)',NULL,'246',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (83,'{مَرَدُوا على النِّفَاق} أَي: تطاولوا على النِّفَاق. {وَآخَرُونَ مرجون} أَي: مؤخرون. {وَإِرْصَادًا} أَي: إعدادا. {إِن الله اشْترى من الْمُؤمنِينَ أنفسهم} قَالَ ابْن الْأَعرَابِي:',NULL,'247',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (84,'يُقَال: لَيْسَ فِي الْكِرَام أكْرم مِمَّن يَشْتَرِي من عَبده مَا وهبه لَهُ، وَالله - عز وَجل - أكْرم الأكرمين، اشْترى من عبيده أنفسهم، وأنفسهم ملكه دونهم وَاشْترى مِنْهُم أَمْوَالهم، وَهِي مِنْهُ نعم عَلَيْهِم، فَهَذِهِ صفة من الْكَرم لَا يقدر عَلَيْهَا أحد غَيره - جلّ وَعز.
INSERT INTO [b6887] VALUES (85,'{وظنوا} : تيقنوا - هَاهُنَا.',NULL,'249',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (86,'(صفحة فارغة)',NULL,'250',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (87,'<span data-type="title" id=toc-13>وَمن سُورَة يُونُس - عَلَيْهِ السَّلَام</span>
INSERT INTO [b6887] VALUES (88,'وَجعلت مَكَانهَا جَوْهَرَة أُخْرَى، قَالَ: وَمِنْه قَول الْقَائِل: نحى السديس وانتحى للمعدل عزل الْأَمِير لِلْأَمْرِ الْمُبدل وَقَالَ: أَلا ترى انه قد نحى جسما، وَجعل مَكَانَهُ جسما غَيره؟',NULL,'252',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (89,'قَالَ أَبُو عمر: فعرضت الْكَلَام على مُحَمَّد بن يزِيد الْمبرد، فَاسْتَحْسَنَهُ، وَقَالَ فِيهِ: قد بقيت فِيهِ فاصلة أُخْرَى على احْمَد ابْن يحيى، قلت: وَمَا هِيَ، أعزّك الله؟ قَالَ: بَقِي أَن الْعَرَب قد جعلت " بدلت " بِمَعْنى [٧ / أ] " أبدلت " وَهُوَ قَوْله - عز وَجل: {فَأُولَئِك يُبدل الله سيئاتهم حَسَنَات} أَلا ترى أَنه - تبَارك وَتَعَالَى - قد أَزَال السَّيِّئَات، وَجعل مَكَانهَا حَسَنَات؟ قَالَ: وَأما شَرط لَك أَحْمد بن يحيى فَهُوَ بِمَعْنى قَوْله - عز وَجل: {كلما نَضِجَتْ جُلُودهمْ بَدَّلْنَاهُمْ جُلُودًا غَيرهَا} قَالَ: فَهَذِهِ هِيَ الْجَوْهَرَة،',NULL,'253',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (90,'وتبديلها تَغْيِير صورتهَا الى غَيرهَا، لِأَنَّهَا كَانَت ناعمة، فاسودت بِالْعَذَابِ، فَردَّتْ صُورَة جُلُودهمْ الأولى لما نَضِجَتْ تِلْكَ الصُّورَة، فالجوهرة وَاحِدَة، وَالصُّورَة مُخْتَلفَة. {فقد لَبِثت} أَي: فقد أَقمت، وَيُقَال مِنْهُ: فعل يفعل فعالا ووفعلا وفعالة. وَقَوله - عز وَجل: {حَتَّى إِذا كُنْتُم فِي الْفلك وجرين بهم برِيح طيبَة} قَالَ ثَعْلَب والمبرد: خرج من المخاطبة إِلَى الْإِخْبَار، فالمخاطبة {حَتَّى إِذا كُنْتُم فِي الْفلك} . {وجرين بهم} : إِخْبَار.',NULL,'254',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (91,'{وَلَا يرهق وُجُوههم قتر وَلَا ذلة} يرهق: يغشى، والقتر: الْغُبَار، والذلة: الذل، فَهَذِهِ من صفة الْكفَّار، وَقد عدلت هَذِه الصّفة عَن الْمُؤمنِينَ، فوجوههم نَضرة. {هُنَالك تبلو كل نفس} أَي: تختبر، و {تتلوا} تقْرَأ.',NULL,'255',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (92,'{ويستنبئونك أَحَق هُوَ} أَي: يستخبرونك. {قل إِي وربي} أَي: نعم. {إِذْ تفيضون فِيهِ} أَي: إِذْ تأخذون فِي حَدِيثه وَأمره. {وَمَا يعزب} أَي: وَمَا يغرب: أَي وَمَا يبعد. {يخرصون} أَي: يكذبُون.',NULL,'256',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (93,'و {الْكِبْرِيَاء} أَي: العظمة، وَالْغَلَبَة. {فاليوم} : وَاحِد الْأَيَّام. {ننجيك ببدنك} ننجيك من النجَاة، ببدنك، أَي: بجسمك، وننجيك من: النجوة، وَهِي: الدكة. ببدنك، أَي: بدرعك. قَالَ أَبُو عبد الله: وَذَلِكَ أَن بني إِسْرَائِيل شكوا فِي غرق فِرْعَوْن، فَأمر الله الْبَحْر أَن يقذفه على دكة فِي الْبَحْر بِبدنِهِ، أَي:',NULL,'257',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (94,'بدرعه، وَكَانَت من لُؤْلُؤ منظوم، فَلَمَّا قذفه الْبَحْر رَأَتْهُ بَنو إِسْرَائِيل، فَقَالُوا: نعم يَا مُوسَى، هَذَا فِرْعَوْن قد غرق، فَخرج الشَّك من قُلُوبهم، وابتلع الْبَحْر فِرْعَوْن كَمَا كَانَ. قَالَ أَبُو عمر: سَمِعت الْإِمَامَيْنِ ثعلبا والمبرد يَقُولَانِ: معنى {فَإِن كنت فِي شكّ} أَي: قل يَا مُحَمَّد للْكَافِرِ: فَإِن كنت فِي شكّ من الْقُرْآن فاسأل من اسْلَمْ من الْيَهُود، الَّذين يقرءُون الْكتاب من',NULL,'258',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (95,'قبلك، أَي: يَا عَابِد [٧ / ب] الوثن، إِن كنت فِي شكّ من الْقرَان فاسأل من أسلم من الْيَهُود - يَعْنِي: عبد الله بن سَلام، وَأَمْثَاله - لِأَن عَبدة الْأَوْثَان كَانُوا يقرونَ للْيَهُود أَنهم أعلم مِنْهُم، من أجل أَنهم أَصْحَاب كتاب، فَدَعَاهُمْ الرَّسُول - صلى الله عَلَيْهِ وَسلم - إِلَى أَن يسْأَلُوا من يقرونَ بِأَنَّهُم أعلم مِنْهُم: هَل بعث الله رَسُولا من بعد مُوسَى - عَلَيْهِ السَّلَام؟',NULL,'259',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (96,'(صفحة فارغة)',NULL,'260',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (97,'<span data-type="title" id=toc-14>وَمن سُورَة هود - عَلَيْهِ السَّلَام</span>
INSERT INTO [b6887] VALUES (98,'الْإِنْجِيل {وَمن قبله} أَي: من قبل الْإِنْجِيل {كتاب مُوسَى} - صلى الله على نَبينَا وَعَلِيهِ وعَلى الْأَنْبِيَاء وَسلم - أَي: التَّوْرَاة. قَالَ ثَعْلَب: وَمَعْنَاهُ: إِن شَكَكْتُمْ فِي الْقُرْآن وَفِي الْإِنْجِيل - فانظروا فِي التَّوْرَاة، فَإِنَّكُم تجدونني بصفتي وبرسالتي وبصدق مَا قلت. قَالَ ثَعْلَب: لِأَنَّهُ - صلى الله عَلَيْهِ وَسلم - مَعْرُوف فِي التَّوْرَاة، ومعروف فِي الْإِنْجِيل. {وأخبتوا إِلَى رَبهم} أَي: تضرعوا إِلَى رَبهم.',NULL,'262',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (99,'وَأما قَوْله: {وَبشر المخبتين} أَي: بشر الْمُؤمنِينَ المتواضعين لله - جلّ وَعز. والإخبات: التضرع فِي وَقت، والإخبات: التَّوَاضُع لله - عز وَجل - فِي كل وَقت. {بَادِي الرَّأْي} من همز {بَادِىءَ الرَّأي} أَرَادَ: فِي ابْتِدَاء الرَّأْي. وَمن قَرَأَ {بَادِي الرَّأْي} وَلم يهمز {بَادِىء} أَرَادَ: فِي ظَاهر الرَّأْي، فَبَدَأَ - مهموزا -: ابْتَدَأَ، وبدا - غير مَهْمُوز -: ظهر.',NULL,'263',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (100,'وَقد يَأْتِي " بَادِي " غير مَهْمُوز، بِمَعْنى: الِابْتِدَاء، وَلم يَأْتِ " باديء " مهموزا - بِمَعْنى: ظهر. {تزدري} أَي: تحتقر. {يعصمني} أَي: يَمْنعنِي. {وغيض المَاء} أَي: نقص. {واعْتَراك} أَي: مسك، يُقَال: عراه واعتراه: إِذا أَتَاهُ.',NULL,'264',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (101,'{عنيد} العنيد: الْمعَارض للحق بِالْبَاطِلِ. {أَلا بعدا لعاد قوم هود} قَالَ: الْبعد: الْهَلَاك، والتباعد من الْخَيْر، يُقَال: بعد يبعد بعدا: إِذا تَأَخّر وتباعد، وَبعد يبعد بعدا: إِذا هلك. {غير تخسير} أَي: غير إبعاد من الْخَيْر، والتخسير لَهُم، لَا لَهُ - صلى الله عَلَيْهِ وَسلم - كَأَنَّهُ قَالَ: غير تخسير لكم، أَي: غير إبعاد من الْخَيْر لكم [٨ / أ] لَا لي. {بعجل حنيذ} اخْتلف النَّاس، فَقَالُوا: الحنيذ: المشوي',NULL,'265',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (102,'الكبيس، وَقَالَت طَائِفَة: الحنيذ: يكون السمين مشويا كبيسا وَغير كبيس. {فَضَحكت} اخْتلف النَّاس فِيهِ، ووسمعت أَبَا مُوسَى يسْأَل ثعلبا - قَالَ: جَاءَ فِي الْخَبَر: فَضَحكت: أَي حَاضَت، فَقَالَ ثَعْلَب: نسلم للتفسير كَمَا جَاءَ، وَلَيْسَ فِي كَلَام الْعَرَب: ضحِكت إِلَّا من الضحك، الَّذِي هُوَ ضد الْبكاء، وَإِنَّمَا ضحِكت',NULL,'266',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (103,'تَعَجبا من الْغُلَام بعد الْكبر، فَقَالَ لَهُ أَبُو مُوسَى: فَأَنت أنشدتنا: تضحك الضبع لقتلى هُذَيْل قَالَ: تضحك - هَاهُنَا - تكشر، وَيُقَال للضاحك: قد كشر، قَالَ: وَذَلِكَ أَن الذِّئْب يُنَازع الضبع على الْقَتِيل، فتكشر الضبع فِي',NULL,'267',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (104,'وَجهه تهددا ووعيدا، فيتركها ويمر. {منيب} : تائب، يُقَال أناب وَتَابَ - عِنْدِي - وَاحِد. {عَصِيبٌ} أَي: شَدِيد',NULL,'268',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (105,'{يُهْرَعُونَ} أَي: يسرعون فِي فزع. {بِقِطْعٍ مِنَ الَّيْلِ} أَي: بساعة من اللَّيْل. {إِلَّا امْرَأَتَكَ} خرج من النَّهْي إِلَى الْإِخْبَار، وَمَعْنَاهُ: إِلَّا امْرَأَتك: فَإِنَّهَا تلْتَفت، وَالنّصب لَيْسَ فِيهِ عمل. {وَلَا تعثوا} قَالَ: العثو: أَشد الْفساد، يُقَال: عثا يعثو،',NULL,'269',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (106,'وعاث يعيث. {لَا يجرمنكم} اخْتلف النَّاس، فَقَالَت طَائِفَة: لَا يحملنكم، وَقَالَت طَائِفَة أُخْرَى: لَا يكسبنكم. {وَدُودٌ} متحبب إِلَى عباده بنعمه وإحسانه. {غَيْرَ تَتْبِيبٍ} قَالَ: التبيب: التخسير والهلاك لكم لَا',NULL,'270',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (107,'لي. {غَيْرَ مَجْذُوذ} أَي: غير مَقْطُوع. {وَزُلَفاً مِنَ اللَّيْل} قَالَ: الزلف: السَّاعَات، وَاحِدهَا: زلفة، وَقَالَ قوم: الزلفة: أول سَاعَة من اللَّيْل بعد مغيب الشَّمْس. {وَجَاءَكَ فِي هَذِهِ الْحَقّ} قَالَ: فِي هَذِه: يَعْنِي: الدُّنْيَا،',NULL,'271',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (108,'وَقَالَ قوم: فِي هَذِه السُّورَة. قَالَ ثَعْلَب: وَالْعَمَل على الأول، لِأَن فِي كل سُورَة قد جَاءَ الْحق.',NULL,'272',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (109,'<span data-type="title" id=toc-15>وَمن سُورَة يُوسُف - عَلَيْهِ السَّلَام</span>
INSERT INTO [b6887] VALUES (110,'حجاب الْقلب [٨ / ب] وَمن قرا: {شَعَفَهَا} فَمَعْنَاه: أحرق حبه قَلبهَا، وعَلى الأول الْعَمَل. {فيسقي ربه} أَي: مَالِكه ومولاه. {وَقَالَ للَّذي ظن أَنه} أَي: تَيَقّن أَنه نَاجٍ مِنْهُمَا.',NULL,'274',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (111,'{اذْكُرْنِي عِنْد رَبك} أَي: عِنْد مَوْلَاك ومالكك. {وَقَالَ لفتيانه} أَي: لِغِلْمَانِهِ ومماليكه، وَمن قَرَأَ {لفتيانه} أَي: لحشمه الْأَحْرَار من حوارييه، وَمِنْه قَوْله:',NULL,'275',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (112,'{إِنَّهُم فتية آمنُوا برَبهمْ} لأَنهم كَانُوا أحرارا. {إِلَّا حَاجَة فِي نفس يَعْقُوب قَضَاهَا} ، قَالَ: الْحَاجة: خَافَ عَلَيْهِم الْعين. و {كَذَلِك كدنا ليوسف} أَي: دبرنا لَهُ؛ قَالَ وَذَلِكَ أَن السّنة كَانَت أَيَّام الْعَزِيز - من سرق أَخذ بسرقته وَملك. {حَتَّى تكون حرضا} قَالَ: الحرض، الَّذِي لَا ينْتَفع بِهِ عِنْد',NULL,'276',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (113,'الْعَرَب من كل شَيْء. {من الهالكين} أَي: من الميتين. {لَا تَثْرِيب عَلَيْكُم} أَي: لَا توبيخ. {لَوْلَا أَن تفندون} أَي: لَوْلَا تضعفون رَأْيِي. {وكأين من آيَة} مَعْنَاهُ: وَكم من آيَة.',NULL,'277',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (114,'(صفحة فارغة)',NULL,'278',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (115,'<span data-type="title" id=toc-16>وَمن سُورَة الرَّعْد</span>
INSERT INTO [b6887] VALUES (116,'{تغيض الْأَرْحَام} أَي: تنقص من دم الْحيض. {وَمَا تزداد} أَي: من دم الْحيض. {يَحْفَظُونَهُ من أَمر الله} قَالَ أَبُو عبد الله: يحفظهم لَهُ من أَمر الله، كَأَنَّهُ أَمرهم بِأَن يحفظوا العَبْد. {وَهُوَ شَدِيدُ الْمِحَالِ} قَالَ: والمحال: الْمَكْر، وَالْمَكْر من الله - جلّ وَعز: التَّدْبِير بِالْحَقِّ.',NULL,'280',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (117,'{إِلَّا كَبَاسِطِ كَفَّيْهِ إِلَى الْمَاءِ} قَالَ: مَعْنَاهُ: أَن يَأْتِي إِلَى بِئْر فِيهَا مَاء لَا ينَال إِلَّا بِحَبل ودلو، فيمد هُوَ يَده إِلَى المَاء، فَلَا يقدر عَلَيْهِ، فَضَربهُ الله مثلا للْكفَّار. {وَيَدْرءُونَ بِالْحَسَنَةِ السَّيِّئَةَ} أَي: يدْفَعُونَ بِالتَّوْبَةِ وَالطَّاعَة. {وَلَوْ أَنَّ قُرْآناً سُيِّرَتْ بِهِ الْجِبَالُ أَوْ قُطِّعَتْ بِهِ الأَرْضُ أَوْ كُلِّمَ بِهِ الْمَوْتَى بَلْ لِلَّهِ الأَمْرُ جَمِيعاً} قَالَ ثَعْلَب: هَذَا مَحْذُوف الْجَواب،',NULL,'281',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (118,'وَالْمعْنَى: لَكَانَ هَذَا الْقُرْآن. وَقَالَ أَبُو عمر: سَأَلت الْمبرد عَنهُ، فَقَالَ: صَحِيح فصيح من كَلَام الْعَرَب. {قَارِعَةٌ} أَي: داهية. {أَوْ تَحُلّ} أَنْت بجيشك. {قَرِيبا} مِنْهُم، وَلَيْسَت تحل القارعة.',NULL,'282',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (119,'{مَثَلُ الْجَنَّةِ} أَي: صفة الْجنَّة. {أم الْكتاب} : اللَّوْح الحفوظ. [٩ / أ] {وَمَنْ عِنْدَهُ عِلْمُ الْكِتَابِ} قَالَ مُجَاهِد: هُوَ عبد الله بن سَلام، وَذَلِكَ أَنه وقف الْيَهُود على صفة مُحَمَّد - صلى الله عَلَيْهِ وَسلم - فِي التَّوْرَاة، وقطعهم بالحجج.',NULL,'283',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (120,'(صفحه فارغه)',NULL,'284',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (121,'<span data-type="title" id=toc-17>وَمن سُورَة إِبْرَاهِيم - عَلَيْهِ السَّلَام</span>
INSERT INTO [b6887] VALUES (122,'تفضل مِنْهُ، والمن من الْعباد مَذْمُوم، لأَنهم يعددون نعمهم على الْإِنْسَان، وَمِنْه قَوْله - جلّ وَعز: {يَمُنُّونَ عَلَيْكَ أَنْ أَسْلَمُوا قُلْ لَا تَمُنُّوا عَلَيَّ إِسْلامَكُمْ بَلِ اللَّهُ يَمُنُّ عَلَيْكُمْ أَنْ هَذَنكُمْ لِلإِيمَانِ} . قَالَ ثَعْلَب: فأجمع أهل اللُّغَة كلهم على أَن الْمَنّ من الله مَحْمُود، لانه تفضل، وَأَن الْمَنّ من الْعباد مَذْمُوم، لأَنهم يعددون نعمهم، وَلِأَن الْمَنّ من الْعباد مَذْمُوم، وَأَنه من الله - جلّ وَعز - نعْمَة وتفضل، وَمن الْآدَمِيّين تقريع وتوبيخ أَو من. {دَار الْبَوَار} أَي: دَار الْهَلَاك بِالْعَذَابِ',NULL,'286',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (123,'الشَّديد. {تهوي إِلَيْهِم} أَي: تهوي إِلَيْهِم، فتحج الْبَيْت. أخبرنَا أَبُو عمر - قَالَ: أخبرنَا ثَعْلَب عَن ابْن الْأَعرَابِي، قَالَ: معنى تهوي إِلَيْهِم، أَي: تهواهم، فتحج إِلَى الْبَيْت، قَالَ وَهَذَا من الْهَوِي الْمَحْمُود.',NULL,'287',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (124,'(صفحه فارغه)',NULL,'288',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (125,'<span data-type="title" id=toc-18>وَمن سُورَة الْحجر</span>
INSERT INTO [b6887] VALUES (126,'{يعمهون} يتحيرون، يُقَال مِنْهُ: فعل يفعل فعلا. {مشرقين} أَي: مصبحين، يُقَال: رجل مشرق، إِذا اصبح، وشارق مَعَ طُلُوع الشَّمْس. {للمتوسمين} أَي: المتفرسين المميزين الْعُقَلَاء. {وَإِن كَانَ أَصْحَاب الأيكة لظالمين} قَالَ ثَعْلَب: مَعْنَاهُ: وَمَا',NULL,'290',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (127,'كَانَ أَصْحَاب الأيكة إِلَّا ظالمين. {وإنهما لبإمام مُبين} أَي: بطرِيق بَين. {الَّذين جعلُوا الْقُرْآن عضين} قَالَ: عضوا فِيهِ القَوْل، أَي: فرقوا فِيهِ القَوْل؛ فَقَالَت طَائِفَة: هُوَ سحر، وَقَالَت طَائِفَة: هُوَ شعر، وَقَالَت طَائِفَة: هُوَ كهَانَة.',NULL,'291',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (128,'{فَاصْدَعْ بِمَا تُؤمر} : فاقصد. {الْيَقِين} هَاهُنَا: الْمَوْت.',NULL,'292',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (129,'[٩ /<span data-type="title" id=toc-19> ب] وَمن سُورَة النَّحْل</span>
INSERT INTO [b6887] VALUES (130,'الَّذِي فِي كَلَام الْعَرَب، فَقَالَ فَخر عَلَيْهِم السّقف من فَوْقهم، أَي عَلَيْهِم وَقع، وَكَانُوا تَحْتَهُ فهلكوا، وَمَا افلتوا. {على تخوف} أَي: على تنقص من الْأَمْوَال والأنفس والثمرات، حَتَّى أهلكهم كلهم. {واصبا} أَي: دَائِما، يُقَال: وصبت عَلَيْهِم الْحمى: أَي: دَامَت.',NULL,'294',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (131,'{تَجئَرُونَ} أَي: تضجون وتستغيثون بِأَصْوَات عالية. {وَأَنَّهُمْ مفرطون} أَي: متروكون منسيون فِي النَّار. {من بَين فرث وَدم} الفرث - هَاهُنَا: السرجين. {سائغا للشاربين} أَي: لَذَّة للشاربين، قَالَ ثَعْلَب: وَرُوِيَ.',NULL,'295',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (132,'عَن ابْن عَبَّاس أنة قَالَ: مَا غص إِنْسَان بِلَبن قطّ. {بَنِينَ وحفدة} اخْتلف النَّاس، فَقَالَت طَائِفَة: هم الأعوان والأختان، وَقَالَت طَائِفَة: كل من أسْرع فِي حَاجَتك، فَهُوَ حافد، قرَابَة كَانَ أَو غير قرَابَة، يُقَال حافد وحفدة، مثل: كَاتب وكتبة.',NULL,'296',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (133,'{أَحدهمَا أبكم} قَالَ: الأبكم: الَّذِي لَا يفهم مَا تَقول لَهُ، والاكمه الَّذِي يُولد أعمى. {وَهُوَ كل على مَوْلَاهُ} قَالَ: الْكل: الثّقل، ومولاه - هَاهُنَا: مَالِكه وَصَاحبه، يَعْنِي: الصَّنَم هَاهُنَا، لانه يحْتَاج أَن يحملهُ فِي النَّقْل. {سرابيل تقيكم الْحر وسرابيل تقيكم بأسكم}',NULL,'297',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (134,'السرابيل: القمص، وَاحِدهَا: سربال، وَقَوله: تقيكم الْحر - أَرَادَ: الْحر وَالْبرد، وَلكنه حذف، كَمَا قَالَ: تمر بِنَا ريَاح الصَّيف دوني',NULL,'298',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (135,'أَرَادَ: والشتاء، وَهَذَا اخْتِصَار، كَمَا قَالَ - عز وَجل: {لَا يسْأَلُون النَّاس إلحافا} أَرَادَ: وَلَا غير إلحاف، فَحذف، وكما قَالَ - جلّ وَعز: {وأشربوا فِي قُلُوبهم الْعجل} مَعْنَاهُ: وأشربوا فِي قُلُوبهم حب الْعجل، فَحذف، كَمَا قَالَ: {واسأل الْقرْيَة الَّتِي كُنَّا فِيهَا} أَرَادَ: أهل الْقرْيَة، وكما قَالَ: {وَالْعير الَّتِي أَقبلنَا فِيهَا} أَرَادَ: أهل',NULL,'299',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (136,'العير. و {تِبْيَاناً لِكُلِّ شَيْءٍ} أَي: بَيَانا لكل شَيْء. {وَلَا تَتَّخِذُوا أَيْمَانكُم دخلا بَيْنكُم} أَي: فَسَادًا [١٠ / أ] بَيْنكُم. {هِيَ أربى من أمة} أَي: هِيَ أَي أَزِيد.',NULL,'300',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (137,'{أمة وَاحِدَة} أَي: دينا وَاحِد، وملة وَاحِدَة. {مَا عنْدكُمْ ينْفد وَمَا عِنْد الله بَاقٍ} يُقَال: نفد الشَّيْء ينْفد، إِذْ فني، وَنفذ ينفذ، إِذا خرج. {إِنَّه لَيْسَ لَهُ سُلْطَان} أَي: حجَّة، وسلطان: قُوَّة {وَالَّذين هم بِهِ مشركون} قَالَ ثَعْلَب: مَعْنَاهُ: الَّذين صَارُوا مُشْرِكين بطاعتهم للشَّيْطَان، فصاروا بعبادتهم الشَّيْطَان مُشْرِكين، وَلَيْسَ الْمَعْنى: أَنهم آمنُوا بِاللَّه - عز وَجل - وأشركوا بالشيطان، وَلَكِن',NULL,'301',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (138,'عبدُوا الله - عز وَجل - وعبدوا مَعَه الشَّيْطَان، فصاروا بعبادتهم الشَّيْطَان مُشْرِكين، لَيْسَ انهم أشركوا بالشيطان، وآمنوا بِاللَّه وَحده. قَالَ الشَّيْخ أَبُو عمر: فعرضت هَذَا الْكَلَام على مُحَمَّد بن يزِيد الْمبرد، فَقَالَ: هَذَا كَلَام متلئب صَحِيح.',NULL,'302',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (139,'{يلحدون} أَي: يميلون إِلَيْهِ. {وَمَا أهل لغير الله بِهِ} أَي: ذبح لغير الله، أَي: الصَّنَم والوثن. {إِن إِبْرَاهِيم كَانَ أمة قَانِتًا} أخبرنَا أَبُو عمر - قَالَ أخبرنَا ثَعْلَب عَن ابْن الْأَعرَابِي: قَالَ الْأمة: الْعَالم وَالنِّهَايَة فِي وقته، وَالْأمة: الْملَّة وَالدّين، وَالْأمة - أَيْضا: الْجَمَاعَة من النَّاس،',NULL,'303',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (140,'وَالْأمة: الْحِين وَالْوَقْت، وَالْأمة وَالأُم وَاحِد، وَالْأمة الْعَامَّة.',NULL,'304',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (141,'<span data-type="title" id=toc-20>وَمن سُورَة بني إِسْرَائِيل</span>
INSERT INTO [b6887] VALUES (142,'{طَائِره فِي عُنُقه} قَالَ: طَائِره: عمله من خير أَو شَرّ. {مُتْرَفِيهَا} قَالَ: المترف: الْملك، وَقيل: الْمُنعم: أمرناهم بِالطَّاعَةِ، فعصوا. {مخذولا} أَي: متروكا من نصر الله - عز وَجل. {وَقضى رَبك} أَي: أَمر رَبك - هَاهُنَا.',NULL,'306',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (143,'{للأوابين} أَي: التوابين. {خشيَة إملاق} أَي: فقر. {مَدْحُورًا} أَي: مباعدا من الْخَيْر. {وَفِي آذانهم وقرا} أَي: ثقلا من الصمم. {قل كونُوا حِجَارَة أَو حديدا} أَي: قدرُوا فِي أَنفسكُم أَنكُمْ حِجَارَة أَو حَدِيد، فَإِنَّهُ - عز وَجل - يعيدكم كَمَا كُنْتُم.',NULL,'307',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (144,'{أَو خلقا مِمَّا يكبر فِي صدوركم} يَعْنِي: الْمَوْت نَفسه، يَقُول: نَحن نعيد الْمَوْت لَو مَاتَ. {فسينغضون إِلَيْك رؤوسهم} أَي: يحركون رؤوسهم استهزاء. {وَمَا منعنَا أَن نرسل بِالْآيَاتِ} قَالَ الإمامان: يَعْنِي بِالْآيَاتِ - هَاهُنَا - الْآيَات الشرطيات، الَّتِي يشْتَرط مَعهَا: لَو',NULL,'308',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (145,'كَذبْتُمْ [١٠ / ب] بهَا هلكتم، وَإِنَّمَا تَركهَا رَحْمَة لأمة مُحَمَّد - عَلَيْهِ السَّلَام. {والشجرة الملعونة فِي الْقُرْآن} قَالَ: شَجَرَة الزقوم. {قَالَ أرأيتك} قَالَ: الْعَرَب تَقول: أريتك، فِي معنى: أَخْبرنِي، وَمِنْهُم من يَقُول: أرأيتك فِي مَعْنَاهَا، فَإِذا قَالُوا: أَرَأَيْت - فَهُوَ على ضَرْبَيْنِ: رُؤْيَة الْعين، ورؤية الْعلم.',NULL,'309',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (146,'{لأحتنكن} أَي: لاستأصلن، أخبرنَا أَبُو عمر - قَالَ: أخبرنَا ثَعْلَب عَن ابْن الْأَعرَابِي - قَالَ الْعَرَب تَقول: احتنك الْجَرَاد الزَّرْع، إِذا استأصله. {موفورا} أَي: تَاما وافيا.',NULL,'310',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (147,'{يزجي} أَي: يَسُوق. {ضل} أَي: غَابَ. {حاصبا} أَي: حِجَارَة.',NULL,'311',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (148,'{قاصفا} أَي: ريحًا قاتلة تقصف الأصلاب وَالشَّجر. {بِهِ تبيعا} أخبرنَا أَبُو عمر - قَالَ: أَنا ثَعْلَب عَن ابْن الْأَعرَابِي - قَالَ: يُقَال للطَّالِب بالشَّيْء: تَابع وتبيع بِهِ تبيعا. {بإمامهم} قَالَ ثَعْلَب: اخْتلف النَّاس، فَقَالَت طَائِفَة: بِكِتَابِهِمْ وَقَالَت طَائِفَة: بِنَبِيِّهِمْ، وَقَالَت طَائِفَة: بشرعهم.',NULL,'312',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (149,'{لدلوك الشَّمْس} قَالَ ثَعْلَب: اخْتلف النَّاس، فَقَالَت طَائِفَة: الدلوك: زَوَالهَا عِنْد الظُّهُور، وَقَالَت طَائِفَة: الدلوك: زَوَالهَا عِنْد المغيب.
INSERT INTO [b6887] VALUES (150,'{أَعْرَضَ وَنئا بِجَانِبِهِ} أخبرنَا أَبُو عمر - قَالَ: أَنا ثَعْلَب عَن ابْن الْأَعرَابِي، قَالَ: يُقَال للمتكبر على الْحق: أعرض ونأى بجانبه. {على شاكلته} : على طبعه وشكله. {ظهيرا} أَي: معينا.',NULL,'314',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (151,'{من زخرف} الزخرف - هَاهُنَا: الذَّهَب. {كلما خبت} أَي: سكن لهيبها، فَإِذا تغير جمرها عَن بريقه قيل: همدت. {ورفاتا} الرفات: فتات الطَّعَام، إِذا فتت.',NULL,'315',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (152,'{مثبورا} أَي: هَالكا، ومثبورا: أَي مَمْنُوعًا من الْخَيْر، قَالَ: وَالْعرب تَقول: مَا ثبرك عني: أَي مَا مَنعك مني، وَمَا بترك عني: أَي مَا حَبسك، وَمَا ثبر فلَانا، أَي: مَا أهلكه. {وَلَا تجْهر بصلاتك وَلَا تخَافت بهَا} مَعْنَاهُ: وَلَا تجْهر بِقِرَاءَة صَلَاتك. وَلَا تخَافت بِقِرَاءَة صَلَاتك. وَهُوَ من الْمُخْتَصر.',NULL,'316',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (153,'<span data-type="title" id=toc-22>وَمن سُورَة الْكَهْف</span>
INSERT INTO [b6887] VALUES (154,'{الْكَهْف والرقيم} قَالَ: الرقيم: لوح كتب فِيهِ أَسمَاؤُهُم، وَكَيف خَرجُوا، وَمن أَي شَيْء هربوا. {شططا} أَي: جورا. {فِي فجوة مِنْهُ} أَي: فِي سَعَة مِنْهُ.',NULL,'318',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (155,'{أيقاظا} أَي: منتبهين، واحدهم: يقظ ويقظ. {وهم رقود} أَي: نيام، {بالوصيد} قَالَ أَبُو الْعَبَّاس: اخْتلف النَّاس فِيهِ، فَقَالَت طَائِفَة: الوصيد: الْبَاب نَفسه، وَقَالَت طَائِفَة: الوصيد: الفناء. {بورقكم} أَي: بدراهمكم، يُقَال لِلْفِضَّةِ: ورق، وورق، وورق، ورقة هـ، وأنشدنا الْمبرد وثعلب فِي الرقة:',NULL,'319',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (156,'خَالِد من ربه على ثِقَة لاذهبا يبعثكم وَلَا دقه أَي: وَلَا فضَّة، فقد فرق بَينهمَا، فالورق (بِالتَّحْرِيكِ) المَال كُله، من من الصَّامِت والناطق كُله. {اأزكى} أَي: أحل.',NULL,'320',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (157,'{وَلَا يشعرن بكم أحدا} أَي: وَلَا يعلمن بكم أحدا، وَمن قرا: وَلَا يشعرن، أَي: لَا يفطنن، وَالْعَمَل على الأول، لِأَن فِي الأيه {احدا} . {أعثرنا} أَي: أطْلعنَا. {وَلَا تستفت فيهم مِنْهُم أحدا} قَالَ الإمامان: الْهَاء وَالْمِيم فِي {فيهم} لأَصْحَاب الْكَهْف، وَالْهَاء وَالْمِيم فِي {مِنْهُم} للْيَهُود.',NULL,'321',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (158,'{وَلَبِثُوا فِي كهفهم ثَلَاث مائَة سِنِين} قَالَ ثَعْلَب: وَهَذَا كُله وَبعده أَخْبَار عَمَّن عدهم فِي الْقُرْآن، لِأَنَّهُ قَالَ: {قل الله أعلم بِمَا لَبِثُوا} . قَالَ: وَقَوله - عز وَجل: {وازدادوا تسعا} هُوَ - أَيْضا - إِخْبَار عَمَّن عدهم، وَلم يصب. قَالَ الشَّيْخ أَبُو عمر: سِنِين بِمَعْنى: سنة، وَهَذَا لفظ جمع بِمَعْنى وَاحِد، كَمَا جَاءَ لفظ الْوَاحِد',NULL,'322',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (159,'بِمَعْنى الْجمع، وَهُوَ قَوْله - جلّ وَعز: {إِن الْإِنْسَان لفي خسر إِلَّا الَّذين آمنُوا} وَالْإِنْسَان بِمَعْنى: النَّاس - هَاهُنَا -، لِأَن الْجَمَاعَة لَا تستثني من وَاحِد، وَمن قَرَأَ: {ثَلاثمِائَةٍ سِنِينَ} جعله على الْبَدَل، وَمن قراء: {ثَلاثمائةَ سِنِينَ} جعله على التَّرْجَمَة.',NULL,'323',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (160,'{ملتحدا} أَي: ملْجأ. {فرطا} أَي: عجلة بِغَيْر تثبت. {فَمن شَاءَ فليؤمن وَمن شَاءَ فليكفر} قَالَ ثَعْلَب: هَذَا تهدد ووعيد، كَمَا قَالَ: {اعْمَلُوا مَا شِئْتُم} إِنَّمَا هُوَ [١١ / ب] تهديد ووعيد، وَلَيْسَ بِأَمْر.',NULL,'324',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (161,'{إِنَّا أَعْتَدْنَا} أَي: أعددنا. و {سرادقها} أَي: سورها. {كَالْمهْلِ} : الْمهل: الْمُذَاب من الرصاص. {حسبانا} أَي: مرامي يَا هَذَا، والحسبانة: السَّحَاب،',NULL,'325',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (162,'والحسبانة - أَيْضا - الوسادة، والحسبانة - أَيْضا - الصاعقة. {وَترى الأَرْض بارزة} قَالَ أَبُو عبد الله: أَي: ظَاهره بِلَا جبل، وَلَا تل وَلَا رمل. {فَلم نغادر} : فَلم نَتْرُك. {ففسق عَن أَمر ربه} أَي: خرج عَن الطَّاعَة.',NULL,'326',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (163,'{موبقا} كل شَيْء حاجز بَين شَيْئَيْنِ، فَهُوَ موبق. {جدلا} أَي: جدالا ومجادلة. {ليدحضوا} أَي: ليسقطوا، وَمِنْه قَوْله - عز وَجل: {حجتهم داحضة} أَي: سَاقِطَة. {لَا أَبْرَح} أَي: لَا أَزَال.',NULL,'327',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (164,'{لقد جِئْت شَيْئا إمرا} أَي: عجبا. {زكيه} و {زاَكِيَّةً} بِمَعْنى وَاحِد، بِمَعْنى: مُؤمنَة. {نكرا} أَي: مُنْكرا. {عذرا} أَي: أعذاراً. {فِي عين حمئة} و {حامية} فحمئة: كَثِيرَة',NULL,'328',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (165,'الحمأة، وحاميه: حارة. {خرجا} الخرج على الرؤوس، وَالْخَرَاج على الْأَرْضين. {زبر الْحَدِيد} أَي: قطع الْحَدِيد، وَاحِدهَا: زبرة.',NULL,'329',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (166,'{بَين الصدفين} يَعْنِي جَانِبي الْجَبَل. و {سآوي} وَسوى: بِمَعْنى وَاحِد. {عَلَيْهِ قطرا} قَالَ الْقطر: النّحاس. {أَن يظهروه} أَن يعلوا عَلَيْهِ بالتسلق. {نقبا} أَي: ثقبا.',NULL,'330',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (167,'و {اسطاعوا} واستطاعوا وَاحِد، أَي: مَا قدرُوا. {دكاء} أَي: ملتصقا بِالْأَرْضِ، أَي: متهدما متهشما، ودكا: مثله. {فَلَا نُقِيم لَهُم يَوْم الْقِيَامَة وزنا} قَالَ: الْعَرَب تَقول: مَا',NULL,'331',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (168,'لفُلَان عندنَا وزن، أَي: قدر، من خسته. {لَا يَبْغُونَ عَنْهَا حولا} أَي: لَا يطْلبُونَ عَنْهَا تحويلا إِلَى غَيرهَا.',NULL,'332',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (169,'<span data-type="title" id=toc-23>وَمن سُورَة مَرْيَم - عَلَيْهَا السَّلَام</span>
INSERT INTO [b6887] VALUES (170,'{عاقرا} أَي: لَا تَلد، يُقَال: عقرت، والعقيم مثلهَا، يُقَال: عقمت. {عتيا} يُقَال: [١٢ / أ] عتا الشَّيْء وعسا وصلب وجف، وَهُوَ النِّهَايَة فِي الْكفْر، وَغَيره. {قَالَ رب اجْعَل لي آيَة} قَالَ: الآيه: الْعَلامَة.',NULL,'334',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (171,'قَالَ: وَقَوله - عز وَجل: {سويا} أَي: من غير خرس. {فَأوحى إِلَيْهِم} أَي: فَأَشَارَ بِيَدِهِ. {وَحَنَانًا من لدنا} أخبرنَا أَبُو عمر - قَالَ: أَنا ثَعْلَب، عَن',NULL,'335',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (172,'ابْن الْأَعرَابِي، عَن الْمفضل - قَالَ: الحنان: الرَّحْمَة، والحنان - أَيْضا: الرزق، والحنان - أَيْضا: الْبركَة، والحنان أَيْضا: الهيبة. {وَسَلام عَلَيْهِ} السَّلَام - هَاهُنَا: السَّلامَة. {وَلم أك بغيا} الْبَغي - عِنْد الْعَرَب: الْفَاجِرَة.',NULL,'336',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (173,'{فأجاءها الْمَخَاض} أَي: فألجأها، قَالَ: وَالْعرب تَقول فِي أَمْثَالهَا: إِذا طلبت الْمَعْرُوف من الْبَخِيل اللَّئِيم مَا أشاءك وأجاءك إِلَى مخة عرقوب، أَي ألجأك إِلَى أَن تطلب المخ من العرقوب. قَالَ: وَأَخْبرنِي عَمْرو، عَن أَبِيه، قَالَ: وبمعناها - أَيْضا: مَا أضك',NULL,'337',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (174,'إِلَى هَذَا: أَي: مَا ألجأك؟ قَالَ: وأنشدني: وَهِي ترى ذَا حَاجَة مؤتضا أَي: ملْجأ مُضْطَرّا. {نسيا منسيا} أما النسي فَهُوَ مَا ألقِي مِمَّا لَا يحْتَاج إِلَيْهِ وَلَا ينْتَفع بِهِ، ومنسيا: متروكا. .',NULL,'338',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (175,'{سريا} قَالَ ثَعْلَب: يُقَال: السّري - هَاهُنَا - النَّهر، وَيُقَال: السّري - هَاهُنَا - عِيسَى بن مَرْيَم، وَيُقَال: السّري - هَاهُنَا - النَّبِيل الْجَلِيل. {صوما} أَي: صمتا. {فريا} أَي: عجبا.',NULL,'339',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (176,'{أسمع بهم وَأبْصر} قَالَ أَبُو الْعَبَّاس: الْعَرَب تَقول هَذَا فِي مَوضِع التَّعَجُّب، فَتَقول: أسمع بزيد وَأبْصر، أَي: مَا أسمعهُ وأبصره، فَمَعْنَاه أَنه - عز وَجل - عجب نبيه -[صلى الله عَلَيْهِ وَسلم]- مِنْهُم. {لأرجمنك} أَي: لأهجرنك، ولأرجمنك، أَي: لأسبنك.',NULL,'340',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (177,'{مَلِيًّا} أَي: قطعه من الزَّمَان. {حفيا} أَي: كَانَ بِي بارا. وَأما قَوْله - عز وَجل: {يَسْأَلُونَك كَأَنَّك حفي عَنْهَا} أَي: كَأَنَّك معنى بهَا. {خروا سجدا وبكيا} خروا: سقطوا، وَسجدا: جمع ساجد، وبكيا: جمع باك، وَهُوَ مِمَّا جَاءَ على: فَاعل وفعيل.',NULL,'341',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (178,'{وَأحسن نديا} أَي: مَجْلِسا. {تؤزهم أزا} أَي: تزعجهم إزعاجا. {شَيْئا إدا} أَي: شَيْئا عجبا.',NULL,'342',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (179,'{وَفْدًا} : ركبانا. {وردا} : حُفَاة مشَاة. {ودا} أَي: محبَّة. {لدا} أَي: شديدي الْخُصُومَة، الذّكر: أَلد، وَالْأُنْثَى:',NULL,'343',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (180,'لداء، وَالْجمع مِنْهُمَا جَمِيعًا: لد، والتصريف مِنْهُمَا: لدد يلدد لددا. {هَل تحس} : هَل تبصر. {ركزا} أَي: صَوتا.',NULL,'344',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (181,'<span data-type="title" id=toc-24>وَمن سُورَة طه</span>
INSERT INTO [b6887] VALUES (182,'{هدى} أَي: هاديا. {الْمُقَدّس} أَي: المطهر. {من غير سوء} السوء - هَاهُنَا: البرص. {ولتصنع على عَيْني} أخبرنَا أَبُو عمر - قَالَ: أخبرنَا ثَعْلَب، عَن ابْن الْأَعرَابِي قَالَ: مَعْنَاهُ ك تربي حَيْثُ أَرَاك.',NULL,'346',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (183,'{وَلَا تنيا فِي ذكري} أَي: لَا تضعفا وَلَا تفترا. {أَن يفرط علينا} أَي: أَن يعجل بجهله. {لأولي النهى} أَي: لأولي الْعُقُول. {فيسحتكم} أَي: يستأصلكم. {المثلى} أَي: الفضلى.',NULL,'347',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (184,'{وَقد أَفْلح} أَي: قد ظفر. {من استعلى} أَي: من غلب. {فأوجس فِي نَفسه خيفة مُوسَى} قَالَ الإمامان: الخيفة - هَاهُنَا: الْخَوْف، قَالَ: وَإِنَّمَا خَافَ على بني إِسْرَائِيل، الَّذين آمنُوا',NULL,'348',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (185,'مَعَه أَن يرتدوا لما رَأَوْا من السحر الْعَظِيم، وَلم يكن خَوفه على نَفسه وَلَا على أَخِيه هَارُون - عَلَيْهِمَا السَّلَام {تلقف} أَي: تَأْخُذ. {فنسي} أخبرنَا آبو عمر - قَالَ: أَنا ثَعْلَب، عَن ابْن الْأَعرَابِي - قَالَ: فنسي، أَي: فَترك مَا أمره مُوسَى بِهِ من الْإِيمَان، وضل {زرقا} أخبرنَا أَبُو عمر - قَالَ: أَنا ثَعْلَب عَن ابْن الْأَعرَابِي',NULL,'349',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (186,'- قَالَ: يُقَال فِي قَول الله - عز وَجل: نحشرهم زرقا، أَي: عميانا، وَيُقَال: نحشرهم زرقا، أَي: عطاشا، وَيُقَال: نحشرهم زرقا، أَي: طامعين فِيمَا لَا ينالونه. {ينسفها رَبِّي نسفا} أَي: يقلعها قلعا من أُصُولهَا، ثمَّ يذرها رملا: تسيل سيلا، ثمَّ يصيرها كالصوف المنقوش تطيرها الرِّيَاح هَكَذَا وَهَكَذَا. قَالَ: وَلَا يكون العهن من الصُّوف إِلَّا الْمَصْبُوغ.',NULL,'350',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (187,'{قاعا صفصفا} القاع: الأَرْض الملساء بِلَا نَبَات وَلَا بِنَاء، والصفصف: القرعاء.
INSERT INTO [b6887] VALUES (188,'{وخشعت الْأَصْوَات للرحمن} : خَشَعت خضعت وذلت. {إِلَّا همسا} قَالَ: الهمس: صَوت الْأَقْدَام، بَعْضهَا على بعض. {وعنت الْوُجُوه للحي القيوم} أَي: خضعت وذلت.',NULL,'352',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (189,'{وَلَا هضما} الهضم: النَّقْص. {وَأَنَّكَ لَا تَظْمَأُ [١٣ / أ] فِيهَا} أَي: لَا تعطش. {وَلَا تضحى} أَي: لَا تصيبك الشَّمْس، فتؤذيك. {يخصفان} : أَي: يلصقان.',NULL,'353',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (190,'{عَلَيْهِمَا من ورق الْجنَّة} قَالَ: أخذا من ورق تين الْجنَّة، لِأَنَّهُ وَاسع. {معيشة ضنكا} أَي: معيشة ضيقَة. {لَكَانَ لزاما} أَي: فصلا، وَيُقَال: لَكَانَ لزاما، أَي: ملازما، وَالْأول عَلَيْهِ الْعَمَل. {وَمِنْ آنَائِ اللَّيْلِ} أَي: من سَاعَات اللَّيْل.',NULL,'354',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (191,'{وأطراف النَّهَار} سَأَلت الْمبرد عَنهُ، فَقَالَ: مَعْنَاهُ: وأطراف سَاعَات النَّهَار، وَسَأَلت ثعلبا عَنهُ، فَقَالَ: أَرَادَ الطَّرفَيْنِ بقوله: أَطْرَاف، لِأَن الِاثْنَيْنِ جمع. وَوَاحِد الآناء: أَنِّي وَأَنِّي وَإِنِّي.',NULL,'355',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (192,'{الصِّرَاط السوي} أَي: المستوي الْمُسْتَقيم. {وَمن اهْتَدَى} أَي: وَمن آمن.',NULL,'356',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (193,'<span data-type="title" id=toc-25>وَمن سُورَة الْأَنْبِيَاء - عَلَيْهِم السَّلَام</span>
INSERT INTO [b6887] VALUES (194,'الطَّعَام، قَالَا: وَمثله فِي الْكَلَام: مَا سَمِعت مِنْك أقبل مِنْك [أَي] إِنَّمَا سَمِعت مِنْك لأقبل مِنْك، قَالَا: فَإِذا كَانَ فِي أول الْكَلَام جحد كَانَ الْكَلَام مجحودا جحدا حَقِيقِيًّا، وَهُوَ مثل قَوْلك: مَا زيد بِخَارِج، فَإِذا جمعت الْعَرَب الجحدين فِي أول الْكَلَام كَانَ أَحدهمَا صله: مَا قُمْت تُرِيدُ: مَا قُمْت، وَمثله: مَا إِن قُمْت،',NULL,'358',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (195,'تُرِيدُ: مَا قُمْت. {فِيهِ ذكركُمْ} قَالَ ثَعْلَب: مَعْنَاهُ: فِيهِ شرفكم. {وَلَا يستحسرون} أَي: لَا يملون وَلَا يعيون وَلَا يفشلون. {كَانَتَا رتقا} أَي: مصمتة، ففتقت السَّمَاء بالمطر، وفتقت',NULL,'359',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (196,'الأَرْض بالنبات. {أَهَذا الَّذِي يذكر آلِهَتكُم} أَي: يعيبها، ويتنقصها. {خلق الْإِنْسَان من عجل} قَالَ ثَعْلَب: الْعجل: العجلة، والعجل - أَيْضا: الطين.',NULL,'360',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (197,'{يكلؤكم} أَي: يحفظكم. {ايصحبون} أَي: يحفظون، وَيمْنَعُونَ. {بردا وَسلَامًا} أَي: سَلامَة، وَهَكَذَا قَوْله - عز وَجل: {فسلام لَك من أَصْحَاب الْيَمين} أَي: إِنَّمَا وَقعت سلامتهم من أَجلك، [١٣ / ب] وَالسَّلَام - فِي اللُّغَة: اسْم من أَسمَاء الله - جلّ وَعز - وَالسَّلَام: السَّلامَة، وَالسَّلَام: التَّسْلِيم فِي الصَّلَاة وَغَيرهَا،',NULL,'361',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (198,'وَالسَّلَام: الاستسلام، وَالسَّلَام: شجر مَعْرُوف، وواحدته: سَلامَة، فعبد الله بن سَلام وَاحِد من هَذِه، وَلَا يَجْعَل السَّلَام اسْما من أَسمَاء الْجَبَّار - جلّ وَعز - فِي هَذَا النَّوْع. {وَذَا النُّون إِذْ ذهب مغاضبا} قَالَ ثَعْلَب: مَعْنَاهُ:',NULL,'362',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (199,'مغاضبا الْملك. {فَظن أَن لن نقدر عَلَيْهِ} هُوَ من: التَّقْدِير، وَلَيْسَ هُوَ من: الْقُدْرَة، يُقَال: قدر الله لَك الْخَيْر يقدره، ويقدره',NULL,'363',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (200,'تَقْديرا، بِمَعْنى: قدره. قَالَ: وَمِنْه الْخَبَر: " فاقدروا لَهُ "، أَي: " قدرُوا لَهُ " فَهَذَا كُله من التَّقْدِير، ونقول من الْقُدْرَة: قدرت على الشَّيْء اقدر عَلَيْهِ قدرَة، وَفِي لُغَة أُخْرَى: قدرت عَلَيْهِ أقدر قدرَة.',NULL,'364',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (201,'{وهم من كل حدب يَنْسلونَ} قَالَ: الحدب: التلال، والآكام، وَاحِدهَا: حدبة، وينسلون، أَي يسرعون. و {حصب جَهَنَّم} أخبرنَا أَبُو عمر - قَالَ: أَنا ثَعْلَب، عَن ابْن الْأَعرَابِي، قَالَ: الْعَرَب تَقول: هَذَا حصب النَّار وحضبها وحطبها، كُله بِمَعْنى وَاحِد، وَهُوَ مَا تَأْكُله النَّار.',NULL,'365',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (202,'(صفحة فارغة)',NULL,'366',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (203,'<span data-type="title" id=toc-26>وَمن سُورَة الْحَج</span>
INSERT INTO [b6887] VALUES (204,'{وَغير مخلقة} أَي: لم تصور بعد. {بهيج} أَي: حسن. {ثَانِي عطفه} أَي: متكبرا، يُقَال: ثنى عطفه ونأى بجانبه، إِذا تكبر.',NULL,'368',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (205,'{على حرف} أَي: على شكّ. {يأتوك رجَالًا} أَي: رجالة، يُقَال: راجل وَرِجَال، مثل: صَائِم وَصِيَام، وقائم وَقيام. {تفثهم} : قَضَاء حوائجهم من الْحلق، والتنظيف، وَأخذ الشّعْر، وَرفع الْوَسخ.',NULL,'369',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (206,'{فَإِذا وَجَبت} أَي: سَقَطت بعد النَّحْر. {القانع} الَّذِي يسْأَل: وترده اللُّقْمَة وَالتَّمْرَة. {والمعتر} : الَّذِي لَا يسْأَل، فَيبْدَأ بِالصَّدَقَةِ.',NULL,'370',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (207,'{وَإِن الله لَهو خير الرازقين} قَالَ أَبُو الْعَبَّاس: يُقَال: إِن كل إنْسَانا إِذا كَانَ يرْزق إِنْسَان رزقا قد سَمَّاهُ لَهُ، ثمَّ غضب عَلَيْهِ قطع ذَلِك [١٤ / أ] الرزق، وَالله - عز وَجل - إِذا غضب على عَبده لم يقطع رزقه مَا دَامَ حَيا.',NULL,'371',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (208,'(صفحة فارغة)',NULL,'372',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (209,'<span data-type="title" id=toc-27>وَمن سُورَة الْمُؤمنِينَ</span>
INSERT INTO [b6887] VALUES (210,'{وهم لَهَا سَابِقُونَ} أَي: إِلَيْهَا سَابِقُونَ. {لناكبون} أَي: لعادلون. {همزات الشَّيَاطِين} أَي: غمزات الشَّيَاطِين ووساوسها. {وهم فِيهَا كَالِحُونَ} أَي: قد كشروا عَن الْأَسْنَان، حَتَّى',NULL,'374',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (211,'تبنت من الشدَّة.',NULL,'375',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (212,'(صفحة فارغة)',NULL,'376',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (213,'<span data-type="title" id=toc-28>وَمن سُورَة النُّور</span>
INSERT INTO [b6887] VALUES (214,'{لَا شرقية وَلَا غربية} قَالَ الإمامان جَمِيعًا: مَعْنَاهُ: لَا شرقية كلهَا، وَلَا غربية كلهَا، هِيَ شرقية وغربية، وَهُوَ أحسن مَا يكون من الشّجر تطلع عَلَيْهَا الشَّمْس، وتغرب عَلَيْهَا الشَّمْس. {ثمَّ يَجعله ركاما} أَي: وَيجْعَل بعضه على بعض، ليثخن',NULL,'378',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (215,'ويغلظ. و {الودق} : الْمَطَر.
INSERT INTO [b6887] VALUES (216,'(صفحة فارغة)',NULL,'380',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (217,'<span data-type="title" id=toc-29>وَمن سُورَة الْفرْقَان</span>
INSERT INTO [b6887] VALUES (218,'{بورا} أَي: هلكى. {حجرا مَحْجُورا} أَي: حَرَامًا محرما، أَي: منعا منعا.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (219,'{وَقدمنَا} أَي: وقصدنا. {مرج الْبَحْرين} أَي: أجراهما.
INSERT INTO [b6887] VALUES (220,'{هونا} أَي: مشيا رويدا. {غراما} أَي: لَازِما.',NULL,'384',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (221,'<span data-type="title" id=toc-30>وَمن سُورَة الشُّعَرَاء</span>
INSERT INTO [b6887] VALUES (222,'{إِذْ قَالَ لَهُم أخوهم نوح} سَمِعت الْإِمَامَيْنِ يَقُولَانِ: أخوهم فِي النّسَب، لَيْسَ فِي الدّين. {بِكُل ريع} الرّيع: الصومعة، والريع: البرج للحمام - أَيْضا - يكون فِي الصَّحرَاء، والريع: التل العالي [١٤ / ب] .',NULL,'386',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (223,'{هضيم} أَي: مريء، وهضيم - أَيْضا - ناعم. {فارهين} : حاذقين. {إِنَّمَا أَنْت من المسحرين} أَي: من المعللين بِالطَّعَامِ',NULL,'387',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (224,'وَالشرَاب، وَمن المسحرين: أَي: المسحورين، وَمن المسحرين: أَي من المخدوعين. {من القالين} أَي: من المبغضين.',NULL,'388',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (225,'{فِي الغابرين} أَي: فِي البَاقِينَ من الْمُعَذَّبين. {والجبلة الْأَوَّلين} أَي: خلق الْأَوَّلين. {وَمَا يَنْبَغِي لَهُم} أَي: مَا يصلح لَهُم، وَهَذَا مثل قَوْله: {وَمَا علمناه الشّعْر وَمَا يَنْبَغِي لَهُ} أَي: وَمَا يصلح لَهُ. {أفاك} أَي: كَذَّاب.',NULL,'389',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (226,'{أثيم} أَي: عَاص.',NULL,'390',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (227,'<span data-type="title" id=toc-31>وَمن سُورَة النَّمْل</span>
INSERT INTO [b6887] VALUES (228,'{أوزعني} ألهمني. وَأما قَوْله تَعَالَى {فهم يُوزعُونَ} أَي: يحبسن أَوَّلهمْ، حَتَّى يَأْتِي آخِرهم بسُلْطَان مُبين، أَي: بِحجَّة مبينَة. {كتاب كريم} قَالَ: كَانَ مَخْتُومًا. {أعزة أَهلهَا أَذِلَّة} : انْقَطع كَلَامهَا هِيَ، فَقَالَ الله - جلّ',NULL,'392',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (229,'وَعز: {وَكَذَلِكَ يَفْعَلُونَ} ثمَّ رَجَعَ إِلَى الْإِخْبَار عَنْهَا فَقَالَ: {وَإِنِّي مُرْسلَة إِلَيْهِم بهدية} . {لَا قبل لَهُم بهَا} أَي: لَا قُوَّة وَلَا طَاقَة لَهُم بهَا. قَالَ ثَعْلَب: معنى قَوْله - جلّ وَعز: {قَالَ الَّذِي عِنْده علم من الْكتاب} : اخْتلف النَّاس، فَقَالَت طَائِفَة: هُوَ اصف بن برخيا،',NULL,'393',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (230,'وَكَانَ من الْعلمَاء، وَقَالَت طَائِفَة: هَذَا الْقَائِل هُوَ: سُلَيْمَان نَفسه، لِأَنَّهُ كَانَ أقدر على الدُّعَاء، وَأَشد تمَكنا من الْقُدْرَة بِاللَّه - جلّ وَعز - من آصف والعفريت، قَالَ: فَدَعَا سُلَيْمَان نَفسه ربه - جلّ وَعز - فَأَجَابَهُ، وصور بَين يَدَيْهِ الْعَرْش فِي لَحْظَة. {بل هم قوم يعدلُونَ} أَي: يشركُونَ بِاللَّه - جلّ وَعز - أَي: يجْعَلُونَ مَعَه عدلا، أَي: مِثَالا، لَا إِلَه إِلَّا هُوَ.',NULL,'394',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (231,'{من جَاءَ بِالْحَسَنَة فَلهُ خير مِنْهَا} قَالَ: عشر أَمْثَالهَا، فعشرة اكثر من وَاحِد.',NULL,'395',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (232,'(صفحة فارغة)',NULL,'396',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (233,'<span data-type="title" id=toc-32>وَمن سُورَة الْقَصَص</span>
INSERT INTO [b6887] VALUES (234,'شَيْء كَادَت تبدي بِهِ، قَالَ: كَادَت تَقول: مَا فِي قلبِي إِلَّا حزنه، وكادت تَقول: قد فرغ قلبِي من حزنه لوعد رَبِّي إيَّايَ أَن يردهُ إِلَيّ، قَالَ: وَلَو أبدت أحد الْقَوْلَيْنِ لقتل مُوسَى - صلى الله عَلَيْهِ وَسلم - وَلَكِن الله - عز وَجل - امسك لسانها عَن أَن تبدي مَا فِي قَلبهَا، ليبلغ مُوسَى مَا أَرَادَهُ.',NULL,'398',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (235,'و {قصيه} أَي: تبصريه. {عَن جنب} أَي: عَن نَاحيَة. {وهم لَا يَشْعُرُونَ} أَي: وهم لَا يعلمُونَ بك. {جذوة} أَي: شعلة. {صرحا} أَي: قصرا.',NULL,'399',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (236,'{ثاويا} أَي: مُقيما. {سرمدا} أَي: دَائِما. {ويكأن الله} أَي: اعْلَم.',NULL,'400',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (237,'<span data-type="title" id=toc-33>وَمن سُورَة العنكبوت</span>
INSERT INTO [b6887] VALUES (238,'(صفحة فارغة)',NULL,'402',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (239,'<span data-type="title" id=toc-34>وَمن سُورَة الرّوم</span>
INSERT INTO [b6887] VALUES (240,'{لمبلسين} : لمتحيرين.',NULL,'404',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (241,'<span data-type="title" id=toc-35>وَمن سُورَة لُقْمَان</span>
INSERT INTO [b6887] VALUES (242,'{إِلَّا كل ختار} أَي: غدار. {الْغرُور} بِالْفَتْح: الشَّيْطَان، والغرور (بِالضَّمِّ) : الدُّنْيَا.',NULL,'406',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (243,'<span data-type="title" id=toc-36>وَمن سُورَة السَّجْدَة</span>
INSERT INTO [b6887] VALUES (244,'(صفحة فارغة)',NULL,'408',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (245,'<span data-type="title" id=toc-37>وَمن سُورَة الْأَحْزَاب</span>
INSERT INTO [b6887] VALUES (246,'{ظاهروهم} أَي: عاونوهم. {من صياصيهم} أَي: من قصورهم وحصونهم. {وَإِذ تَقول للَّذي أنعم الله عَلَيْهِ وأنعمت عَلَيْهِ} قَالَ: انْعمْ الله عَلَيْهِ [١٥ / ب] بِالْإِسْلَامِ، وانعمت عَلَيْهِ بِالْعِتْقِ. {وطرا} أَي: حَاجَة.',NULL,'410',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (247,'{غير ناظرين إناه} أَي: منتظرين إناه، أَي: بُلُوغه وإنضاجه. {قولا سديدا} أَي: مستويا.',NULL,'411',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (248,'(صفحة فارغة)',NULL,'412',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (249,'<span data-type="title" id=toc-38>وَمن سُورَة سبأ</span>
INSERT INTO [b6887] VALUES (250,'{من محاريب} أَي: من غرف. {اعْمَلُوا آل دَاوُد شكرا} أَي: قُولُوا: لَا إِلَه إِلَّا الله. {منسأته} أَي: عَصَاهُ. {فَلَمَّا خر} أَي: سقط.',NULL,'414',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (251,'{من ظهير} أَي: معِين. {إِلَّا كَافَّة} أَي: جمَاعَة الْخلق من الثقلَيْن من الْجِنّ وَالْإِنْس من قَوْله: {يَا معشر الْجِنّ وَالْإِنْس} . {ازلفى} أَي: قربى. {معشار} أَي: عشرا وَاحِدًا.',NULL,'415',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (252,'{التناوش} بِلَا همز: التَّنَاوُل، و {التَّنَاؤُش} بِالْهَمْز: أَي التَّأْخِير.',NULL,'416',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (253,'<span data-type="title" id=toc-39>وَمن سُورَة فاطر</span>
INSERT INTO [b6887] VALUES (254,'{أجاج} أخبرنَا أَبُو عمر - قَالَ: أخبرنَا ثَعْلَب عَن ابْن الْأَعرَابِي - قَالَ: الأجاج أَشد المَاء ملوحة. {نصب} أَي: كلال وتعب. و {لغوب} فَتْرَة وتوان.',NULL,'418',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (255,'{وَجَاءَكُم النذير} قَالَ ثَعْلَب: اخْتلف النَّاس، فَقَالَت طَائِفَة: النذير - هَاهُنَا: الشيب، وَقَالَت طَائِفَة: النذير: مُحَمَّد - صلى الله عَلَيْهِ وَسلم - قَالَ ثَعْلَب: وعَلى هَذَا الْعَمَل، لَيْسَ على الأول، لأَنا قد رَأينَا من يَمُوت قبل الشيب',NULL,'419',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (256,'<span data-type="title" id=toc-40>صفحة فارغة</span>',NULL,'420',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (257,'<span data-type="title" id=toc-41>وَمن سُورَة يس</span>
INSERT INTO [b6887] VALUES (258,'{حَتَّى عَاد كالعرجون الْقَدِيم} قَالَ: العرجون الَّذِي يبْقى من الكباسة فِي النَّخْلَة إِذا قطعت، وَالْقَدِيم الْبَالِي. {لَا الشَّمْس يَنْبَغِي لَهَا} أَي: لَا يصلح لَهَا. {من مثله مَا يركبون} أَي: من الْحَيَوَان من الْخَيل وَالْجمال وَالْبِغَال وَالْحمير.',NULL,'422',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (259,'{من مرقدنا} قَالَ ثَعْلَب: يرْوى عَن ابْن عَبَّاس انه [١٦ / أ] قَالَ: للْعَالم رقدة فِي الْقُبُور قبل السَّاعَة، فَمِنْهَا قَالُوا: من مرقدنا، قَالَ: فأجيبوا: {هَذَا مَا وعد الرَّحْمَن وَصدق المُرْسَلُونَ} . {مَلَكُوتُ كُلِّ شَيْءٍ} أَي: ملك كل شَيْء.',NULL,'423',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (260,'<span data-type="title" id=toc-42>صفحة فارغة</span>',NULL,'424',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (261,'<span data-type="title" id=toc-43>وَمن سُورَة الصافات</span>
INSERT INTO [b6887] VALUES (262,'{ثاقب} أَي: مضيء. {من طين لازب} وَلَا زق وَاحِد. {وَأَْوَاجَهُم} أَي: وأشكالهم. {رزق مَعْلُوم} : مُقَدّر لَهُم يَأْتِيهم فِي وقته.',NULL,'426',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (263,'{كأنهن بيض مَكْنُون} أَي: مَسْتُور مصون. {لمدينون} : لمجزيون. {هَل أَنْتُم مطلعون} أَي: اطلعوا، لَيْسَ هِيَ استفهاما هَاهُنَا، إِنَّمَا هِيَ بِمَعْنى الْأَمر. قَالَ: وَمِنْه لما نزلت: آيَة تَحْرِيم',NULL,'427',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (264,'الْخمر قَامَ عمر - رَضِي الله عَنهُ - قَائِما بَين يَدي النَّبِي - صلى الله عَلَيْهِ وَسلم - ثمَّ رفع رَأسه إِلَى السَّمَاء، فَقَالَ: يَا رب، بَيَانا أشفي من هَذَا فِي الْخمر، فَنزلت: {فَهَل أَنْتُم مُنْتَهُونَ} قَالَ: فَنَادَى عمر: انتهينا يَا رَبنَا انتهينا. {طلعها كَأَنَّهُ رُؤُوس الشَّيَاطِين} قَالَ ثَعْلَب: اخْتلف النَّاس، فَقَالَت طَائِفَة: كأنة رُؤُوس الشَّيَاطِين من الْجِنّ وَحْشَة، وَقَالَت طَائِفَة: الشَّيَاطِين - هَا هُنَا - الْحَيَّات، قَالَ: وَالْعرب تَقول إِذا فقدوا',NULL,'428',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (265,'طَعَاما: أكله الشَّيَاطِين، يعنون: الْحَيَّة. {لشوبا من حميم} : شرابًا مختلطا بِغَيْرِهِ من الْعَذَاب. {إِنَّهُمْ أَلْفَوْا آبَائهم ضَالِّينَ} أَي: صادفوا آبَاءَهُم كفَّارًا فاتبعوهم.',NULL,'429',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (266,'{يُهْرَعُونَ} أَي: يسرعون إسراعا فِيهِ تحير ودهش. {وَتَركنَا عَلَيْهِ فِي الآخرين} أخبرنَا أَبُو عمر - قَالَ: أخبرنَا ثَعْلَب عَن ابْن الْأَعرَابِي - قَالَ: مَعْنَاهُ تركنَا عَلَيْهِ فِي الآخرين ثَنَاء حسنا.',NULL,'430',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (267,'{وَإِن من شيعته لإِبْرَاهِيم} الْهَاء لمُحَمد - صلى الله عَلَيْهِ وَسلم - أَي: إِبْرَاهِيم - عَلَيْهِ السَّلَام - خبر بِخَبَرِهِ، فَاتبعهُ ودعا لَهُ. {بقلب سليم} : أَي: لَيْسَ فِيهِ غش وَلَا غل.',NULL,'431',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (268,'{أَتَدعُونَ بعلا} قَالَ ثَعْلَب: اخْتلف النَّاس فِي قَوْله - جلّ وَعز - هَاهُنَا: بعلا، فَقَالَت طَائِفَة البعل - هَاهُنَا: الصَّنَم، وَقَالَت طائفه: البعل - هَاهُنَا: ملك. {فنبذناه بالعراء} أَي: تَرَكْنَاهُ بالصحراء. و {اليَقْطِينٍ} يُقَال: إِنَّه شَجَرَة الدُّبَّاء، وَيُقَال: إِنَّهَا شَجَرَة',NULL,'432',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (269,'غَيرهَا. {من إفكهم} أَي: من كذبهمْ.',NULL,'433',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (270,'<span data-type="title" id=toc-44>صفحة فارغة</span>',NULL,'434',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (271,'<span data-type="title" id=toc-45>وَمن سُورَة ص</span>
INSERT INTO [b6887] VALUES (272,'وَلما أَن رَأَيْت بني عَليّ / عرفت الود وَالنّسب القرابا {إِلَّا اخْتِلَاق} أَي: كذب. {فوَاق} أَي: سُكُون، وَيُقَال: الفواق',NULL,'436',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (273,'والفواق: السّكُون بَين الحلبتين من النَّاقة، ليؤوب اللَّبن، فَأَما الفؤاق: الوجع، فَهُوَ بِالْهَمْزَةِ وَالضَّم لَا غير. {قطنا} أخبرنَا أَبُو عمر - قَالَ: أَنا ثَعْلَب، عَن ابْن الْأَعرَابِي قَالَ: القط: الصَّحِيفَة، والقط: الْكتاب، وَمَعْنَاهُ: عجل',NULL,'437',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (274,'لنا كتَابنَا إِلَى النَّار. {أواب} : تواب، وأواب: مسبح. {الصافنات الْجِيَاد} أَي: الْخَيل، الَّتِي تقف وَتثني سنبك إِحْدَى الرجلَيْن، وَهِي أَجود الْخَيل.',NULL,'438',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (275,'و {الْخَيْر} هَاهُنَا: الْخَيل. {حَتَّى تَوَارَتْ} التَّاء للشمس. {ردوهَا عَليّ} الْهَاء وَالْألف للخيل. {فَطَفِقَ} أَي: أقبل.',NULL,'439',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (276,'{مسحا} أَي: قطعا. {بِالسوقِ} : السيقان. {رخاء} أَي: سَاكِنة. {حَيْثُ أصَاب} أَي: حَيْثُ أَرَادَ. قَالَ: وَالْعرب تَقول: أصَاب الصَّوَاب، وَأَخْطَأ الْجَواب، أَي: أَرَادَ الصَّوَاب، فَأَخْطَأَ',NULL,'440',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (277,'الْجَواب. {ضغثا} أَي: باقة من كل شَيْء، من قضبان، من ريحَان، من عيدَان. {قاصرات الطّرف} أَي: غاضات الطّرف، إِلَّا عَن',NULL,'441',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (278,'أَزوَاجهنَّ. {من شكله} أَي: من مثله. {هَذَا فَوْج} أَي: جمَاعَة، وَجَمعهَا: أَفْوَاج.',NULL,'442',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (279,'<span data-type="title" id=toc-46>وَمن سُورَة الزمر</span>
INSERT INTO [b6887] VALUES (280,'رِجَاله الْكُوفِيّين والبصريين: قَالُوا: ظلمَة الْبَطن، وظلمة اللَّيْل، وظلمة المشيمة، وَقَالَت طَائِفَة: ظلمَة الْبَطن، وظلمة المهبل - وَهُوَ مَوضِع الْوَلَد - وظلمة المشيمة. {خوله} : أعطَاهُ ورزقه. {لَهُم من فَوْقهم ظلل من النَّار وَمن تَحْتهم ظلل} قَالَ: قلت لِابْنِ الْأَعرَابِي: ظلل من فَوْقهم، نعم، فَكيف تكون الظلل من تَحْتهم؟ قَالَ: الظلل من تَحْتهم ظلل لمن تَحْتهم من الطَّبَق',NULL,'444',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (281,'الثَّانِي فَهِيَ لَهُم هم بِسَاط، وَهِي لمن تَحْتهم ظلل، وَهَكَذَا هَلُمَّ جرا، حَتَّى يَنْتَهِي إِلَى القعر من النَّار. [١٧ / أ] (فَيَتَّبِعُونَ أَحْسَنَهُ} قَالَ ثَعْلَب: كُله حسن، وَلَكِن فِيهِ الْقصاص، وَفِيه الْعَفو عَن الْقصاص، وَالْعَفو أحسن من',NULL,'445',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (282,'الْقصاص. {شَرَحَ} فتح، وَمِنْه قَوْله - جلّ وَعز: {ألم نشرح لَك صدرك} أَي: ألم نفتح لَك صدرك. {متشاكسون} أَي: مُخْتَلفُونَ.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (283,'{اشمأزت} أَي: اقشعرت. {فِي جنب الله} أَي: فِي قرب الله - عز وَجل - من الْجنَّة. {لَهُ مقاليد} أَي: مَفَاتِيح، وَاحِدهَا: إقليد.',NULL,'447',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (284,'{حافين} أَي: طائفين من حول الْعَرْش، يُقَال: قد حفت العساكر بملكها، إِذا طافت بِهِ.',NULL,'448',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (285,'<span data-type="title" id=toc-47>وَمن سُورَة الْمُؤمن</span>
INSERT INTO [b6887] VALUES (286,'والبراءة. {وَقَالَ فِرْعَوْن ذروني أقتل مُوسَى} قَالَ: لم يسألهم من بَاب الْأَمر وَالنَّهْي، وَلَكِن من بَاب المشورة، أَي: أَشِيرُوا عَليّ. {يصبكم بعض الَّذِي يَعدكُم} قَالَ ثَعْلَب: وعدهم شَيْئَيْنِ من الْعَذَاب: عَذَاب الدُّنْيَا، وَعَذَاب الْآخِرَة، فَقَالَ: يصبكم هَذَا الْعَذَاب فِي الدُّنْيَا، وَهُوَ بعض الوعيدين.',NULL,'450',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (287,'{وَلَهُم اللَّعْنَة وَلَهُم سوء الدَّار} قَالَ: مَعْنَاهُ: عَلَيْهِم اللَّعْنَة، وَعَلَيْهِم سوء الدَّار. {يؤفك} أَي: يصرف. {بِمَا كُنْتُم تفرحون فِي الأَرْض بِغَيْر الْحق} قَالَ ثَعْلَب: هَذَا يدل على انه يكون فَرح بِحَق.',NULL,'451',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (288,'(صفحة فارغة)',NULL,'452',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (289,'<span data-type="title" id=toc-49>وَمن سُورَة السَّجْدَة</span>
INSERT INTO [b6887] VALUES (290,'{نحسات} أَي: مشائيم. {وَأما ثَمُود فهديناهم} أَي: بَينا لَهُم. {وقيضنا لَهُم} أَي: مثلنَا لَهُم. {يلحدون فِي آيَاتنَا} أَي: يميلون عَلَيْهَا وفيهَا',NULL,'454',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (291,'بالطعن. {اعْمَلُوا مَا شِئْتُم} هُوَ تهديد ووعيد، كَمَا تَقول لِلْعَدو: اعْمَلْ مَا شِئْت فَإِنِّي أكافئك، فَكَذَلِك: {اعْمَلُوا مَا شِئْتُم} . {من أكمامها} أَي: من أغطيتها. {قَالُوا آذَنَّك} أَي: أعلمناك.',NULL,'455',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (292,'{فِي مرية} أَي: فِي شكّ.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (293,'<span data-type="title" id=toc-51>وَمن سُورَة حم عسق</span>
INSERT INTO [b6887] VALUES (294,'(صفحة فارغة)',NULL,'458',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (295,'<span data-type="title" id=toc-53>وَمن سُورَة الزخرف</span>
INSERT INTO [b6887] VALUES (296,'يصعدون عَلَيْهَا إِلَى السقوف. {وسئلْ مَنْ أَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رُسُلِنَا} أَي قل يَا مُحَمَّد لمن شكّ فِي أَمرك: سل كتب من أرسلنَا، لتعلم أَن صفة مُحَمَّد - صلى الله عَلَيْهِ وَسلم - فِي كل كتاب أَنزَلْنَاهُ. {فاستخف قومه} أَي: فاستجهل قومه {فأطاعوه} .',NULL,'460',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (297,'{فَلَمَّا آسفونا} أَي: أغضبونا. {تحبرون} أَي: تنعمون. {وأزواجكم} نِسَاؤُكُمْ. {أول العابدين} أَي: أول الغضاب الآبقين،',NULL,'461',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (298,'وَقيل فَأَنا أول العابدين، أَي: فَأَنا أول الجاحدين لما تَقولُونَ، وَقيل: فَأَنا أول العابدين، أَي: أَنا أول من يعبده على الوحدانية، مُخَالفا لكم.',NULL,'462',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (299,'<span data-type="title" id=toc-54>وَمن سُورَة الدُّخان</span>
INSERT INTO [b6887] VALUES (300,'والسندس: الْحَرِير دون الديباج، أرق مِنْهُ، وَهُوَ اللاذ.',NULL,'464',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (301,'<span data-type="title" id=toc-55>وَمن سُورَة الجاثية</span>
INSERT INTO [b6887] VALUES (302,'{الْكِبْرِيَاء} : العظمة.',NULL,'466',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (303,'<span data-type="title" id=toc-56>وَمن سُورَة الْأَحْقَاف</span>
INSERT INTO [b6887] VALUES (304,'(صفحة فارغة)',NULL,'468',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (305,'<span data-type="title" id=toc-57>وَمن سُورَة مُحَمَّد - عَلَيْهِ السَّلَام</span>
INSERT INTO [b6887] VALUES (306,'{ولتعرفنهم فِي لحن القَوْل} أَي: فِي معنى القَوْل، ويروى عَن ابْن عَبَّاس: فِي لحن القَوْل - قَالَ: ببغضهم عليا - عَلَيْهِ السَّلَام.',NULL,'470',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (307,'<span data-type="title" id=toc-58>وَمن سُورَة الْفَتْح</span>
INSERT INTO [b6887] VALUES (308,'{تراهم ركعا سجدا} أَي: فِي حَالين لَيْسَ فِي حَال وَاحِدَة، أَي: ركعا وَسجدا. {ذَلِك مثلهم فِي التَّوْرَاة} وَتمّ الْكَلَام، ثمَّ اسْتَأْنف، وَقَالَ: {وَمثلهمْ فِي الْإِنْجِيل} فوصفهم. {شطأه} أَي: فرخه، وَجمعه: أشطأء.',NULL,'472',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (309,'{فَئآزَرَهُ} أَي: فساواه فِي طوله. {فَاسْتَوَى على سوقه} أَي: تمّ فِي طوله، و {سوقه} هَاهُنَا: أُصُوله.',NULL,'473',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (310,'{يعجب الزراع} أَي: الزارعين.',NULL,'474',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (311,'<span data-type="title" id=toc-59>وَمن سُورَة الحجرات</span>
INSERT INTO [b6887] VALUES (312,'(صفحة فارغة)',NULL,'476',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (313,'[١٨ / أ] وَمن سُورَة ق
INSERT INTO [b6887] VALUES (314,'{باسقات} أَي: طوَالًا. {نضيد} أَي: منضود، أَي: بعضه على بعض. {تحيد} أَي: تجور وتفر عَنهُ. {حَدِيد} أَي: إِلَى لِسَان الْمِيزَان، وَيُقَال: (فَبَصَرُكَ',NULL,'478',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (315,'الْيَوْمَ حَدِيد} أَي: فرأيك الْيَوْم نَافِذ. {مِنْ لُغُوبٍ} أَي: من تَعب.',NULL,'479',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (316,'(صفحة فارغة)',NULL,'480',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (317,'<span data-type="title" id=toc-61>وَمن سُورَة والذاريات</span>
INSERT INTO [b6887] VALUES (318,'{يفتنون} أَي: يحرقون ويعذبون. {وَفِي السَّمَاء رزقكم وَمَا توعدون} رزقكم: الْمَطَر لِأَن بِهِ يَتَأَتَّى الْعَيْش، وَمَا توعدون: الْجنَّة.',NULL,'482',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (319,'{وَهل أَتَاك} أَي: قد جَاءَك. {فِي صرة} أَي: فِي صرخة وصيحة، وَفِي صرة، أَي: فِي جمَاعَة من نسائها. {فصكت وَجههَا} أَي: فَضربت وَجههَا تَعَجبا.',NULL,'483',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (320,'{عقيم} أَي: لَا تَلد.',NULL,'484',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (321,'<span data-type="title" id=toc-62>وَمن سُورَة وَالطور</span>
INSERT INTO [b6887] VALUES (322,'{يدعونَ} أَي: يدْفَعُونَ دفعا. {وَمَا ألتناهم} أَي: نقصناهم، يُقَال: ألته يألته ألتا، وآلته يؤلته إيلاتا، ولاته يليته ليتا، كُله إِذا نَقصه.',NULL,'486',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (323,'{أحلامهم} أَي: عُقُولهمْ.',NULL,'487',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (324,'(صفحة فارغة)',NULL,'488',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (325,'<span data-type="title" id=toc-63>وَمن سُورَة والنجم</span>
INSERT INTO [b6887] VALUES (326,'{وأكدى} أَي: قطع وَمنع. {وَأَنه هُوَ أغْنى وأقنى} أغْنى، أَي: أعْطى مَا يَكْفِي، وأقنى، أَي: أعْطى مَا يدّخر.',NULL,'490',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (327,'{فَبِأَي آلَاء رَبك تتمارى} الآلاء: النعم، وَاحِدهَا: إِلَيّ، والى، وَالِي.',NULL,'491',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (328,'(صفحة فارغة)',NULL,'492',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (329,'<span data-type="title" id=toc-64>وَمن سُورَة اقْتَرَبت</span>
INSERT INTO [b6887] VALUES (330,'{من الأجداث} أَي: من الْقُبُور، وَاحِدهَا: جدث. {مهطعين} أَي: مُسْرِعين. {منهمر} أَي: منصب. {كهشيم المحتظر} أَي: الرجل الْجَامِع للورق،',NULL,'494',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (331,'والمحتظر، أَي: كالورق إِذا جف وَجمع.',NULL,'495',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (332,'(صفحة فارغة)',NULL,'496',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (333,'<span data-type="title" id=toc-66>وَمن سُورَة الرَّحْمَن تَعَالَى</span>
INSERT INTO [b6887] VALUES (334,'{شواظ} وشواظ، أَي: الْقطعَة من النَّار. {وَبَين حميم آن} أَي: نضيج حَار. {ذواتا أفنان} أَي: أَغْصَان.',NULL,'498',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (335,'{مدهامتان} أَي: خضراوان من الرّيّ. {لم يطمثهن} أَي: لم يقربهن.',NULL,'499',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (336,'(صفحة فارغة)',NULL,'500',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (337,'<span data-type="title" id=toc-67>وَمن سُورَة الْوَاقِعَة</span>
INSERT INTO [b6887] VALUES (338,'و {الهيم} أَي: العطاش من الْإِبِل. {وننشئكم فِي مَا لَا تعلمُونَ} أَي: ننشئكم فِيمَا لَا تعلمُونَ من الْأُمُور، من: الْخَيْر، وَالشَّر، والعافية، وَالْمَرَض، والغنى والفقر. {فظلتم تفكهون} أَي: أَي أَصْبَحْتُم تَنْدمُونَ.',NULL,'502',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (339,'{للمقوين} للأغنياء والفقراء. {مدهنون} أَي: مُنَافِقُونَ.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (340,'(صفحة فارغة)',NULL,'504',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (341,'<span data-type="title" id=toc-68>وَمن سُورَة الْحَدِيد</span>
INSERT INTO [b6887] VALUES (342,'{كِفْلَيْنِ} أَي: نَصِيبين من رَحمته.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (343,'<span data-type="title" id=toc-69>وَمن سُورَة المجادلة</span>
INSERT INTO [b6887] VALUES (344,'(صفحة فارغة)',NULL,'508',NULL,NULL,'0');
