INSERT INTO [b6887] VALUES (346,'(صفحة فارغة)',NULL,'510',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (347,'<span data-type="title" id=toc-71>وَمن سُورَة الممتحنة</span>
INSERT INTO [b6887] VALUES (348,'(صفحة فارغة)',NULL,'512',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (349,'<span data-type="title" id=toc-72>وَمن سُورَة الصَّفّ</span>
INSERT INTO [b6887] VALUES (350,'(صفحة فارغة)',NULL,'514',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (351,'<span data-type="title" id=toc-73>وَمن سُورَة الْجُمُعَة</span>
INSERT INTO [b6887] VALUES (352,'(صفحة فارغة)',NULL,'516',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (353,'<span data-type="title" id=toc-74>وَمن سُورَة المُنَافِقُونَ</span>
INSERT INTO [b6887] VALUES (354,'(صفحة فارغة)',NULL,'518',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (355,'<span data-type="title" id=toc-75>وَمن سُورَة التغابن وَالطَّلَاق</span>
INSERT INTO [b6887] VALUES (356,'(صفحة فارغة)',NULL,'520',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (357,'<span data-type="title" id=toc-76>وَمن سُورَة التَّحْرِيم</span>
INSERT INTO [b6887] VALUES (358,'(صفحة فارغة)',NULL,'522',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (359,'<span data-type="title" id=toc-77>وَمن سُورَة الْملك</span>
INSERT INTO [b6887] VALUES (360,'(صفحة فارغة)',NULL,'524',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (361,'<span data-type="title" id=toc-78>وَمن سُورَة ن</span>
INSERT INTO [b6887] VALUES (362,'(صفحة فارغة)',NULL,'526',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (363,'<span data-type="title" id=toc-80>وَمن سُورَة الحاقة</span>
INSERT INTO [b6887] VALUES (364,'{على أرجائها} أَي: على نَوَاحِيهَا، وَاحِدهَا: رجأ، وَيكْتب بِالْألف، لَان تثنيته: رجوان. {ظَنَنْت} أَي: تيقنت.',NULL,'528',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (365,'<span data-type="title" id=toc-81>وَمن سُورَة سَأَلَ سَائل</span>
INSERT INTO [b6887] VALUES (366,'و {نزاعة} أَي: ناشطة. {تَدْعُو من أدبر وَتَوَلَّى} : تعذب: قَول الْمبرد. وَتَدْعُو: تنادي: قَول ثَعْلَب. {هلوعا} أَي: جَبَانًا.',NULL,'530',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (367,'{منوعا} أَي: يمْنَع غَيره، ومنيعا: يمْنَع نَفسه. {مهطعين} أَي: جماعات مُتَفَرّقين. {عزين} أَي: جماعات متفرقات.',NULL,'531',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (368,'(صفحة فارغة)',NULL,'532',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (369,'<span data-type="title" id=toc-83>وَمن سُورَة نوح - عَلَيْهِ السَّلَام</span>
INSERT INTO [b6887] VALUES (370,'{فِيهِنَّ نورا} أَي: مَعَهُنَّ نورا. {كبارًا} أخبرنَا أَبُو عمر - قَالَ: أخبرنَا ثَعْلَب عَن سَلمَة، عَن الْفراء - قَالَ: يُقَال: شَيْء كَبِير، فَإِذا زَاد قيل: كبار - خَفِيفا، فَإِن زَاد حَتَّى بلغ النِّهَايَة قيل: كبار - مشددا.',NULL,'534',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (371,'<span data-type="title" id=toc-84>وَمن سُورَة قل أُوحِي</span>
INSERT INTO [b6887] VALUES (372,'(صفحة فارغة)',NULL,'536',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (373,') سَبْحاً) (المزمل: من الْآيَة
INSERT INTO [b6887] VALUES (374,'الله عَلَيْهِ وَسلم: لعَائِشَة - رَضِي الله عَنْهَا: لَا تسبخي أَي: لَا تخففي عَنهُ من الْإِثْم.',NULL,'538',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (375,'{هجرا جميلا} أَي: بِلَا سَبَب فِي الْهِجْرَة. {وبيلا} أَي: شَدِيدا.',NULL,'539',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (376,'(صفحة فارغة)',NULL,'540',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (377,'<span data-type="title" id=toc-87>وَمن سُورَة المدثر - عَلَيْهِ السَّلَام</span>
INSERT INTO [b6887] VALUES (378,'{من قسورة} قَالَ ثَعْلَب: اخْتلف النَّاس فِيهِ، فَقَالَت طَائِفَة: القسورة هَاهُنَا: الْأسد، وَقَالَت طَائِفَة: الرُّمَاة، وَقَالَت طَائِفَة: سَواد أول اللَّيْل، وَلَا يُقَال لسواد آخر اللَّيْل: قسورة.',NULL,'542',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (379,'<span data-type="title" id=toc-88>وَمن سُورَة الْقِيَامَة</span>
INSERT INTO [b6887] VALUES (380,'{معاذيره} يُقَال: هِيَ ستوره، وَيُقَال: اعتذاره. {باسرة} أَي: كالحة. {تظن} أَي: تتيقن.',NULL,'544',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (381,'{فاقرة} أَي: داهية. {أولى لَك فَأولى} تهدد ووعيد. {سدي} أَي: مهملا.',NULL,'545',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (382,'(صفحة فارغة)',NULL,'546',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (383,'<span data-type="title" id=toc-89>وَمن سُورَة هَل أَتَى على الْإِنْسَان</span>
INSERT INTO [b6887] VALUES (384,'{أسرهم} أَي: مَوضِع الْبَوْل وَالْغَائِط، يُقَال لَهما: مصرتان، حَتَّى يجلس الرجل لقَضَاء حَاجته مِنْهُمَا، فتنفتح المصرتان، فَإِذا خرج مِنْهُمَا الْأَذَى تقبضتا كَمَا كَانَتَا.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (385,'<span data-type="title" id=toc-91>وَمن سُورَة المرسلات</span>
INSERT INTO [b6887] VALUES (386,'{ذِي ثَلَاث شعب} أَي: يمْنَع الْكفَّار من الْخُرُوج من جَهَنَّم.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (387,'<span data-type="title" id=toc-92>وَمن سُورَة عَم يتساءلون</span>
INSERT INTO [b6887] VALUES (388,'{حسابا} : أَي: كَافِيا. {وَقَالَ صَوَابا} أَي: قَالَ: لَا إِلَه إِلَّا الله.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (389,'<span data-type="title" id=toc-94>وَمن سُورَة والنازعات</span>
INSERT INTO [b6887] VALUES (390,'{الطامة} أَي: يَوْم الْقِيَامَة.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (391,'<span data-type="title" id=toc-95>وَمن سُورَة عبس</span>
INSERT INTO [b6887] VALUES (392,'وَالْأَب: كل شَيْء يرْعَى. و {الصاخة} الْقِيَامَة.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (393,'<span data-type="title" id=toc-96>وَمن سُورَة كورت</span>
INSERT INTO [b6887] VALUES (394,'{وَإِذا العشار عطلت} أَي [٢٠ / أ] : الدّور مَاتَ أَهلهَا، فتعطلت. {بضنين} أَي: ببخيل، وبظنين، أَي: بمتهم.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (395,'<span data-type="title" id=toc-98>وَمن سُورَة انفطرت</span>
INSERT INTO [b6887] VALUES (396,'(صفحة فارغة)',NULL,'560',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (397,'<span data-type="title" id=toc-100>وَمن سُورَة المطففين</span>
INSERT INTO [b6887] VALUES (398,'(صفحة فارغة)',NULL,'562',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (399,'<span data-type="title" id=toc-101>وَمن سُورَة إِذا السَّمَاء انشقت</span>
INSERT INTO [b6887] VALUES (400,'يُقَال: فلَان يكدح على عِيَاله ولعياله، أَي: يعْمل ويكتسب. {ثبورا} أَي: هَلَاكًا. {أَن لن يحور} أَي: أَن لن يرجع إِلَيْنَا فِي الْقِيَامَة بعد الْمَوْت.',NULL,'564',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (401,'<span data-type="title" id=toc-103>وَمن سُورَة البروج</span>
INSERT INTO [b6887] VALUES (402,'{الْمجِيد} أَي: الرفيع.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (403,'<span data-type="title" id=toc-104>وَمن سُورَة الطارق</span>
INSERT INTO [b6887] VALUES (404,'{من مَاء دافق} فِي معنى: مدفوق، وهومما جَاءَ على لفظ الْفَاعِل وَهُوَ مفعول بِهِ، وَمثله: {فِي عيشة راضية} أَي: مرضية. {من بَين الصلب والترائب} أَي: من صلب الرجل وترائب الْمَرْأَة، وَهُوَ مَوضِع القلادة من الْإِنْسَان وَالْمَرْأَة. قَالَ: أَرَادَ التربية، وَلَكِن جمعهَا - عز وَجل - بِمَا حولهَا، كَمَا قيل: هِيَ وَاضِحَة اللبات، وَإِنَّمَا لَهَا لبة وَاحِدَة، فجمعها بِمَا حولهَا.',NULL,'568',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (405,'{وَالسَّمَاء ذَات الرجع} أَي: ذَات الْمَطَر، لِأَنَّهَا ترجع بِهِ عَاما بعد عَام. و {ذَات الصدع} أَي: ذَات الشق بالنبات',NULL,'569',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (406,'(صفحة فارغة)',NULL,'570',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (407,'<span data-type="title" id=toc-105>وَمن سُورَة الْأَعْلَى</span>
INSERT INTO [b6887] VALUES (408,'{ثمَّ لَا يَمُوت فِيهَا وَلَا يحيى} قَالَ ثَعْلَب: أَي لَا يَمُوت فِيهَا موتا قَاضِيا، فيستريح، وَلَا يحيا فِيهَا حَيَاة تَامَّة، فيستريح، فَهُوَ حَيّ كميت.',NULL,'572',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (409,'<span data-type="title" id=toc-106>وَمن سُورَة الغاشية</span>
INSERT INTO [b6887] VALUES (410,'(صفحة فارغة)',NULL,'574',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (411,'<span data-type="title" id=toc-107>وَمن سُورَة الْفجْر</span>
INSERT INTO [b6887] VALUES (412,'{قدر} و {قدر} وَاحِد. {أكلا لما} أَي: شَدِيدا. و {جما} أَي: كثيرا.',NULL,'576',NULL,NULL,'0');
