INSERT INTO [b6887] VALUES (431,'<span data-type="title" id=toc-117>وَمن سُورَة لايلاف قُرَيْش</span>
INSERT INTO [b6887] VALUES (432,'وَقطعَة من هَذَا، فَإِذا قَالَ: الْجُوع وَالْخَوْف فهما التامان.',NULL,'596',NULL,NULL,'0');
INSERT INTO [b6887] VALUES (433,'<span data-type="title" id=toc-118>وَمن سُورَة أَرَأَيْت [الماعون]</span>
INSERT INTO [b6887] VALUES (434,'وَقَالَت طَائِفَة: هُوَ الزَّكَاة، وَهُوَ قَول أَمِير الْمُؤمنِينَ عَليّ بن أبي طَالب - رضوَان الله عَلَيْهِ - قَالَ ثَعْلَب: وَعَلِيهِ الْعَمَل',NULL,'598',NULL,NULL,'0');
