INSERT INTO [b7251] VALUES (894,'٤١-الألباني: محمد ناصر الدين، ضعيف سنن أبي داود، مكتب التربية العربي لدول الخليج، ط. الأولى، ١٤١٢هت. = ١٩٩٢م.
