INSERT INTO [b7562] VALUES (1085,'"<span data-type="title" id=toc-41>أبواب الفتن</span>"','2','521',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1086,'٥٨١ -[٢١٦٥] "من أراد بُحبُوحة الجنَّة" بضم الموحدتين بينهما حاء مهملة ساكنة، وبعد الواو أخرى، قال في النهاية: "بحبُوحة الدَّار وسطها، يقال: تبحبح إذا تمكن وتوسط المنزل والمقام".
وقال ابن الخازن: "بحبوحَة الجنة وسطها وخيارها، وأراد بذلك تفضيل الموضع وشرفه على غيره من الأمكنة".','2','521',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1087,'٥٨٢ -[٢١٦٦] "يد الله مع الجماعة" قال في النهاية: "هو كناية','2','521',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1088,'عن الحفظ، أي: أنَّ الجماعة المتفقة من أهل الإسلام في كنف الله ووقايته".','2','522',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1089,'٥٨٣ -[٢١٧٠] "وتجتلدوا بأسيافكم" يقال: جلدته بالسيف، إذا ضربته به، والجِلاد والمجالدة: الضربُ بالسيف، والمجتلَد: موضع القتال.','2','522',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1090,'٥٨٤ -[٢١٧٦] "إنَّ الله زَوَى لِي الأرْضَ" أي: جمعها،','2','522',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1091,'وطواها.
"فرَأَيْتُ مَشَارِقَهَا وَمَغَارِبَهَا" هذا أصل لطي المسافة، ورفع الحجب الذي هو أحد كرامات الأولياء.
"وأعطيتُ الكَنْزَيْنِ الأحمر والأبيض" قال في النِّهاية:
"الأحمر ملك الشَّام، والأبيض ملك فارس، وإنما قال لفارس الأبيض؛ لبياض ألوانهم، ولأنَّ الغالب على أموالهم الفضة، كما أنَّ الغالب على أهل الشَّام الحمرة، وعلى أموالهم الذَّهب".
"وَأَنْ لاَ يُسَلِّطَ عليهمْ عَدوًّا من سِوَى أَنْفسِهِمْ".
استدل به ابن مالك على أنَّ "سِوَى" تقع غير ظرف، وتجر بغير "في".
"فيَسْتَبِحَ بَيْضتَهمْ" قالَ فِي النِّهاية: "أي: مجتمعهم وموضع سُلطانهم، ومستقر دعوتهم وبيضة الدار: وسطها ومُعظمها؛ أراد عدُوًّا يستأصلهم ويهلكهم جميعًا. قيل: أراد إذا أُهلك أهل البيضة كان هلاك كل ما فيها من طعم أو فَرْخ، إذا لم يهلك أصل البيضة ربَّما سلم بعض فراخِهَا.','2','523',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1092,'وقيل: أراد بالبيضة الخوذة، فكأنه شبه مكان اجتماعهم والتئامهم ببيضة الحديد".
٥٨٤ م- ٢١٧٨ "عن زِياد بن سِيمِين كُوشَ: تكون فتنة تستنظف العرَب" بالظاء المعجمة، قال في النِّهاية: "أي تستوعبهم هلاكًا يقال: استنظف الشيء، إذا أخذته كله".','2','524',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1093,'٥٨٥ -[٢١٧٩] "في جَذْرِ قلُوبِ الرِّجَالِ" بِفَتح الجيم وسكون','2','524',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1094,'الذال أي أصلها.
"مِثْلَ الوَكْتِ" جمع وكتة، بالتاء المثناة من فوق؛ وهو الأثر في الشيء، كالنقطة في غير لونه.
"مثل المَجْلِ" بفتح الميم، وسكون الجيم وفتحها أيضًا، يقال: مَجَلت يده تَمْجُلُ مَجْلاً، وَمَجِلْت تَمْجَل مَجَلاً، إذَا ثَخُنَ جلدها وتَعجَّر، وظَهَر فيها ما يشبه البَثْر من العمَل بِالأشياء الصلبة الخَشِنَة.
"فتَرَاه مُنْتَبرًا" بضم الميم، وسكون النون، وفتح التاء المثناة من فوق، وكسر الموحدة، وراء؛ أي: مرتفعًا في جسمك.','2','525',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1095,'٥٨٦ -[٢١٨١] "عذبة سَوطه" بفتح العين المهملة والذال','2','525',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1096,'المعجمة والموحدة؛ أي طرفه.','2','526',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1097,'٥٨٧ -[٢١٨٥] "وَقَذفٌ" بالذال المعجمة هو الرمي بقوة.','2','526',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1098,'٥٨٨ -[٢١٨٩] "سَتَرَوْنَ بَعْدِي أَثَرَةً" بفتح الهمزة والتاء المثلثة، الاسم من: آثر يؤثر، إيثارًا، إذا أعطى، أراد أنه يستأثر عليكم فيفضل غيركم في نصيبه من الفيء.','2','526',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1099,'٥٨٩ -[٢١٩٣] "لا تَرجِعُوا بَعْدِي كفارًا يضرب بعضكم رقاب بعض" قال القاضي عياض: "الرواية، يضربُ بِالرَّفع، كذا رواه','2','526',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1100,'المتقدمون والمتأخرون وهُوَ الصواب وبه يصح المقصود هنا.
وضبطه بعض العلماء بالسكون، وهو إحالة للمعنى، والصواب الضم".
وقال ابن مالك: "فما خفي على أكثر النحويين استعمال "رجع" كصار، معنًى وعملاً، ومنه الحديث، "ولا ترجعوا بعدي كفارًا" أي لا تصيروا.
وقول الشَّاعر:
قد يرجع المرء بعد المقت ذامقة ... بالحكم ما دار به بغضاء ذي إحن','2','527',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1101,'قال: ويجوز في يضرب الرفع والجزم" انتهى.','2','528',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1102,'٥٩٠ -[٢١٩٥] "فِتنًا كَقِطعِ اللَّيلِ المُظْلِمِ" قال في النِّهاية: "قطع اللَّيل جمع قطعة وهي طائفة منه، أراد فتنة مظلمة سوداء تعظيمًا لشأنها".
"بِعَرَض مِنَ الدُّنْيَا" بفتح الراء، متاعَها وحُطامهَا.
"عن عدَيْسَةَ" بضم العين وفتح الدال المهملتين، وتحتية ساكنة وسين مهملة.
"بِنْتِ أُهْبَانَ" بضم الهمزة وسكون الهاء وموحدة وآخره نون، ويقال: وَهَبَان.
"بن صَيْفِيِّ".
قيل هو ابن أخت أبي ذرٍّ ورده ابن منده.','2','528',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1103,'٥٩١ -[٢٢٠٦] "مَا مِنْ عَامٍ إِلاَّ وَالَّذِي بَعْدَهُ شَرٌّ مِنْهُ حَتَّى تَلْقَوْا ربَّكُمْ".
روى البيهقي في شعب الإيمان عن ابن مسعود، قال: لا يأتي عام إلاَّ والَّذي بعده شرّ منه، قالوا: فإنه يأتي علينا العام نَخْصِب فيه والعام لا نَخْصِب فيه، قال: "إني والله لا أعني خصبكم ولا جَدْبكم، ولكن ذهاب العِلم والعُلَماء، قد كان قبلكم عمر فأروني العام مثله" وهذا يصلح أن يفسر به حديث أنس هذا.','2','529',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1104,'٥٩٢ -[٢٢٠٨] "تَقيءُ الأرضُ" من القيء.
"أَفْلاَذَ كَبِدِهَا" بالفاء [والذال] المعجمة جمع فلَذٍ، والفِلَذُ','2','529',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1105,'جمع فِلْذَة: وهي القطعة المقطوعة؛ أي تخرج كنوزها المدْفُونة فيها وتطرحها على ظهرهَا، كقوله تعالى: {وَأَخْرَجَتِ الْأَرْضُ أَثْقَالَهَا (٢) } قال في النِّهاية: "سمي ما في الأرض "قطعًا" تشبيهًا وتمثيلاً، وخصَّ الكبد لأنها من أطايب الجَزُور، واستعار القيء للإخراج.
"أمثالَ الأسْطُوانِ" بضم الهمزة، والطاء، بينهما سين مهملة ساكنة.','2','530',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1106,'٥٩٣ -[٢٢٠٩] "لُكَّعُ بن لُكَّعٍ" هو اللَّئيم وقيل: الوسخ، وأكثر ما يستعمل في النداء.','2','530',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1107,'٥٩٤ -[٢٢١٠] "وإذا كان المغْنَم دُولاً" جمع دُولة بالضم، وهو','2','530',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1108,'ما يتداول من المال فيكون لقوم دون قومٍ.
"والزَّكاة مَغْرَمًا" أي يرى رب المال أنَّ إخراج زكاته غرامة يغرمها.
"وكان زعيم القَوْمِ" أي رئيسهم.
"أرذلهُمْ".
"وَاتُّخِذَتِ القَيْنَاتُ" جمع قينة، وهي المغنية وأصلها الأمة.
"والمعازف" بعين مهملة، وزاي وفاء، هي الدفوف وغيرها مما يضرب.','2','531',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1109,'٥٩٥ -[٢٢١١] "قطع سِلْكهُ" بكسو السين هي الخيط.','2','531',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1110,'٥٩٦ -[٢٢١٣] "بُعِثْتُ فِي نَفْسِ السَّاعَةِ" قال في النِّهاية:','2','531',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1111,'"أي: بعثتُ وقد حان قيامها وقرب، إلاَّ أنَّ الله أخرَّها قليلاً، فبعثني في ذلك النَّفس على القرب.
وقيل: معناه: أنه جعل للساعة نفسًا كنفس الإنسان، أراد: إذا بعثت في وقت قريب منها أحس فيه بنفسها كما يحس بنفس الإنسان إذا قرب منه، يعني: بعثت في وقت بانت أشراطُهَا فيه، وظهرت علاماتها".
٥٩٦ م- ٢١١٤ "بُعِثْتُ أَنَا والسَّاعَةُ" بالرفع.
"كَهَاتَيْنِ" وَأشَارَ أَبُو داود بالسَّبابَةِ والوُسْطَى؛ قال الحكيم الترمذي في نوادر الأصُول: "رُوِي لنا عن أصابع رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أنَّ المشيرة كانت أطول من الوُسطى والوسطى أقصر منها، ثم البنصر','2','532',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1112,'أقصر من الوُسطى".
ثم استدلَّ بما أخرجه من حديث ميمونة بنت كردم. قالت:
خرجتُ في حجة الوداع رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - فرأيتُ رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - على راحلته، وسأله أبي عن أشياء، فلقد رأيتني أتعجب -وأنا جارية- من طول إصبعه التي تلي الإبهام على سائر أصابعه "فذُكِر ذلك لعبد الله بن الحسَن، فقال: نعم، كذلك كانت أصابع رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -.','2','533',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1113,'٥٩٨ -[٢٢١٥] "كَأَنَّ وُجُوهَهُمُ المَجَانُّ المُطْرَقَةُ" أي:','2','533',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1114,'التِّراس التي ألبست العَقب: شيئًا فوق شيء.
وَرُوِيَ بتشديد الراء للتكثير، والأول أشهر.','2','534',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1115,'٥٩٩ -[٢٢٢٠] "وَمُبِيْرٌ" بالموحَّدة أي: مهلك يسرف في إهلاك الناس.','2','534',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1116,'٦٠٠ -[٢٢٢١] "ثمَّ يأتِي من بعدِهِمْ قَوْمٌ يَتَسَمَّنُونَ" قال في','2','534',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1117,'النِّهاية: "أي يتكثَّرون بما ليس فيهم، ويدعون ما ليس فيهم، ويدعون ما ليس لهم من الشرف، وقيل: أراد جمعهم الأموال.
وقيل: يحبُّون التوسع في المآكل والمشارب، وهي أسباب السمن.','2','535',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1118,'٦٠١ -[٢٢٢٤] "زِياد بن كُسَيب" بضم الكاف، وفتح السين المهملة وآخره موحدة مُصغَّر.
"مَنْ أهان سلطان الله في الأرض أهانه الله" قال ابن الخازن في كتاب "نزهة الأخيار في شرح محاسِن الأخبار": "المراد منه أنَّ الله نصب السلطان ليُنَفِّذ أوامره، فإذا أكرمه الإنسان أكرم من نصبه، فيكرمه الله وبالعكس.
وإهانته ترك أوامره في الطاعات، وإكرامه المسارعة إلى أمره في طاعة الله.
وقيل: من نظر إليه بعين الإكرام والتعظيم، فذلك تعظيم الله تعالى والله يكرمُه بذلك، وكذا الكلام في الإهانة.','2','535',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1119,'وفيه دليل على تحريم قتال السلطان العادِل والخروج عليه" انتهى.','2','536',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1120,'٦٠٢ -[٢٢٢٦] "بَنُو الزَّرقاءِ".','2','536',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1121,'٦٠٣ -[٢٢٢٧] "في جمهورٍ" أي جماعة.','2','536',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1122,'٦٠٤ -[٢٢٢٨] "يقال لهُ جهْجَاهُ" في النِّهاية: "جَهْجَأه الرَّجل، أي زبره، وفي الحديث حتى يملك رجل يقال له جهجاه، كأنه مركب من هذا ويروى جهجل".
٥ ٦٥- ٢٢٣٢ "إن في أمتي المهدي" قال الرافعي في "تاريخ قزوين": "أورده الخطيب في "تاريخ بغداد" في ترجمة أمير المؤمنين المهدي العباسي" فكأنه أشار إلى حمل الحديث عليه.
٦ ٥ ٦-٢٢٣٨ "عن يزيد بن قُطَيْبٍ" بالتصغير.','2','537',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1123,'"عن أبي بَحْرِيَّةَ".','2','538',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1124,'٦٠٧ -[٢٢٤٠] "عن النواس بن سمعان" بكسر السين وفتحها.','2','538',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1125,'"فَخَفَضَ فِيهِ وَرَفَّعَ" أي عظم فتنة ورفع قدرها، ثم وَهَّنَ أمره وقدره وهوَّنه.
وقيل: أراد أنه رفع صوته وخفضه في اقتصاص أمره.
"قَطَطٌ" بفتح القاف والطاء هو الشديد الجُعُودَة.
"عيْنُهُ قَائمَة" هي الباقية في موضعها صحيحة، وإنما ذهب','2','539',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1126,'نظرها وإبصَارهَا.
"فَعَاثَ" بعين مهملة ومثلثة؛ أي أفسد.
"قلنا يا رسول الله ومَا لَبثة في الأرضِ، قال: أَرْبَعِيْنَ يَوْمًا".
قال أبو البقاء في إعراب الحديث: "هكذا في هذه الرواية والوجه فيه أن يقدّر بـ: يَلْبَثُ أربعين، أو يقيم أربعين، ودلَّ على ذلك قوله: ما لَبْثهُ".
"سَارِحَتُهمْ" فيِ الماشِيَة.
"كأَطْوَلِ مَا كَانتْ ذُرًّا" بضم الذال المعجمة، [جمع ذُروة وهي أعلام سنام البَعِير.
"كَيعاسِيْبِ النَّحْلِ" بالحاء المهملة، جمع يعسُوب وهو كبير النحل، في بعض النسخ النخل، بالخاء المعجمة] وعزى تصحيحه إلى السِّلفي.
"جزْلتين" بكسر الجيم وسكون الزاي قطعتين.
"بَيْنَ مَهْرودَتَيْنِ". قال في النِّهاية: "أي شقتين، أو حلتين،','2','540',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1127,'وقيل: الثوب المهرود: الذي يُصْبَغ بالورس ثم بالزَّعفران فيجيء لونه مثل لون زهر الحواذنة.
وقال القتبي: هو خطأ من النقلة، وأرَاه "مَهْروَّدتين" أي:
صفراوين، يقال: هريت العمامة إذا لبستها صفراء، وكأنَّ منه: هَرَوْت، فإن كان محفوظًا بالدال فهو من الهرد: الشق، وخطيء ابن قتيبة في استداركه وإشتقاقه.
قال ابن الأنباري: القول عندنا في الحديث: "بين مهرودتين" يروى بالدال، والذال: أي بين [ممصرتين على] ما جاء في الحديث، ولم نسمعه إلاَّ فيه، وكذلك أشياء كثيرة لم تسمع إلاَّ في الحديث، والممصَّرة من الثياب: التي فيها صُفرة خفيفة، وقيل: المهرود: الثوب الذي يصبغ بالعروق، والعروق يقال لها: الهرد". انتهى.
"تَحَدَّرَ مِنهُ جُمَّانٌ كَاللُّؤلُوءِ" أي عرق كما في رواية، لأنَّ الجمان هو اللؤلؤ نفسه. واحدهُ جُمَانة.
"وَلاَ يَجِدُ رِيْحَ نَفَسِهِ" بفتح الفاء.','2','541',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1128,'"بِبابِ لد" قال في النِّهاية: "وَهوَ موْضِعٌ بالشَّام، وقيل بفلسطين".
"حَرِّز عِبَادِي إلَى الطُّورِ" بحاء مهملة، ثم راء ثم زاي؛ أي ضُمَّهُم إليه واجعَله لهم حِرْزًا، ويُروى حَوِّزْ بالواو من التحيز.
"النَّغَفَ" بفتح النون والغين المعجمة وفاء، دود يكون في أُنُوف
الإِبل، وِالغنم، واحدها نغفة.
"فيصبحونَ فَرْسَى" أي قتلى، الواحد فريس من فرس الذئب الشاة وافترسها إذا قتلها.
"مَلأتْه زُهْمتُهُمْ" بضم الزاي: الريح النتن؛ أراد أنَّ الأرض تنتن من جيفهم.
"فَتَطْرحُهُمْ بِالمَهْبِلِ" هو اسم موضع.
"وَجِعَابِهِمْ" جمع جعبَة وهي الكنانة التي تجعل فيها السهام.
"فيَتْرُكهَا كالزَّلفَةِ" بفتح الزاي واللام والفاء: مصانع الماء،
وجَمْعُهَا: زَلَفٌ، وَمَزالف، أراد أنَّ المطر يُغَدِّرُ في الأرضِ فتصير كأنَّها مصْنَعَةٌ من مصانع الماء.
وقيل: الزلفة: المرآة، شبهها بها.','2','542',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1129,'لاستوائها ونظافتها.
وقيل: الزلفة: الروضة، ويقال: بالقاف أيضًا.
"ويَسْتَظِلُّونَ بِقِحْفِها" قال في النِّهاية: "أراد قشرهَا، تشبيهًا بقحف الرأس، وهو الذي فوق الدِّماغ.
"في الرِّسلِ" بكسر الراء وسكون السين المهملة: اللَّبن.
"الفِئامَ" مهموز: الجماعة الكثيرة.
"يَتَهَارَجونَ". قال أبو موسى المديني أي يتسافدون. قال الزمخشري: أي يتشاورون.','2','543',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1130,'٦٠٨ -[٢٢٤١] "كَأَنَّهَا عِنَبةٌ طافئة" قال في النِّهاية: "هِي الحبة التي قد خرجت عن حدِّ نبتة أخواتها، فظهرت من بينها وارتفعت، وقيل: أراد به الحبَّة الطَّافئة على وجه الماء، شبَّه عينَه بها".','2','543',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1131,'٦٠٩ -[٢٢٤٣] "فِي الفدَّادِينَ" بفتح الفاء، وتشديد الدال الأولى: الذين تعلو أصواتهم في حروثهم ومواشيهم، وأحدهم فداد، وقيل: هم المكثرون من الإبل، وقيل: هم الجمالُون، والبقارُون، والحمارُون، والرعيان، وقيل: إنما هو الفدادين مخففًا، واحدها فدَّاد مشدد، وهي البقر التي يحرث بها، وأهلها أهل جفاء وغلظة".
"وَأهْلِ الوَبَرِ" أي الإبل.','2','544',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1132,'٦١٠ -[٢٢٤٩] "أُطُمِ".','2','544',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1133,'بضمتين بناء مرتفع.
"بَني مَغَالةَ" بفتح الميم والغين المعجمة.','2','545',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1134,'٦١١ -[٢٢٤٨] "فَرضَاخِية" أي ضخمة.','2','545',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1135,'٦١٢ -[٢٢٥٠] "نَفْس منفوسة" أي مولُودَة.','2','546',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1136,'٦١٣ -[٢٢٥٣] "عين زغر" بضم الزاي وفتح الغين المهملة','2','546',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1137,'وراء: عين بالشَّام من أرض البلقاء، قيل: هو اسم لها، وقيل: اسم امرأة نسبت إليها.','2','547',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1138,'٦١٤ -[٢٢٥٦] "مَنْ سَكَن البَادِيَة جَفَا" أي غلظ طبعه وصار جافيًا بعد لطف الأخلاق، لفقد من يروضه ويؤدبهُ.
"وَمَنْ اتَّبَعَ الصَّيْدَ غَفَلَ"، لأنه إذا كان مهتمًّا به غفل عن مصالحه.
"وَمَنْ أَتَى أَبْوَابَ السُّلْطَانِ افتَتَنَ" ضبط بالبناء للفاعل والمفعول.
قالى ابن الخازن: "سبب فتنته أنه يرى سعة الدنيا والخير هناك، فيحتقر نعمة الله عليه، وربما استخدمه، فلا يكاد يسلم في تصرفه من الإثم في الآخرة، أو العقوبة في الدُّنيا. ويجوز أن يكون سبب الافتتان؛ أنه لا يمكنه أن ينكر ما يجب إنكاره".','2','547',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1139,'٦١٥ -[٢٢٦١] "المُطَيطَاءَ" "بالمد والقصر: مشية فيها تَبَخْتُرٌ ومَدُّ اليدين، وهي من المُصغَّراتِ التي لم يُسْتَعمل بها مُكَبَّر"، قاله في النِّهاية.','2','547',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1140,'"<span data-type="title" id=toc-42>أبواب الرُّؤْيَا</span>"','2','548',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1141,'٦١٦ -[٢٢٧٠] "إِذَا اقْتَرَبَ الزَّمَانُ" قال في النِّهاية: "أراد اقتراب الساعة، وقيل: اعتدال اللَّيل والنَّهار، وتكون الرؤيا فيه صحيحة لاعتدال الزمان، واقترَبَ: افْتَعَلَ، من القُرْبِ.','2','548',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1142,'٦١٧ -[٢٢٧٦] "مِنْ رَآني في المنام فقد رآني" قال الشيخ تقي','2','548',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1143,'الدِّين السبكي في "شرح المنهاج": "تعبير الرؤيا علم شريف". وقال ابن الرفعة: إنه شرعي"، وما أظنه كما قال؛ فإنَّ حقيقته راجعة إلى معرفة معنى رُؤية المنام، وما هو المرئي فيها، وذلك يتعلق بالحِكمة ومعرفة حقائق الأمور، وَقلَّ من يعرفها، وتعز معرفته بالاكتساب بل هو هبَة من الله تعالى.
وانظر إلى تعبير يوسف عليه الصلاة والسلام.
وكان النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - يقول: "مَنْ رَأى منكم اللَّيلة رُؤيا"، وكان لأبي بكر الصديق رضي الله عنه حظ وافر من هذا العِلم؛ وللنَّفس في حال النَّوم تجرد لم يكن حال اشتغالها بالبدن حالة اليقظة، وهو شبيه بتجردها بعد الموت، وإن كان بينهما فرق كبير، فإذا تجردت حالة النوم ورأت ما لَمْ تكن تراه، ويختلف النَّاس في ذلك التجرد اختلافًا كثيرًا عَلى قدر','2','549',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1144,'مراتبهم.
وتارةً تكون الرؤيا صحيحة من الله تعالى، ومن المَلك الذي وكله الله بالرؤيا فيكون لها تعبير صحيح، أو تقع كما هي من غير تعبير، وتارةً لا تكون صحيحه، بأن تكون من الشَّيطان، أو حديث نفس، والذي تراه في الرؤيا الصحيحة يبعد أن يكون هو ذلك الشخص الذي وقع في نفس النائم أنه رآه بعينه؛ لأنا نرى شخصًا ميتًا أو حيًّا لا علم له برؤيتنا له، هذا أمر قطعي، فالمرئي حينئذٍ على ما يظهر لنا صُورة مخلوقة لله تعالى على مثال تلك الصُورة، ثم تلك الصورة إما مع عين روحانية -وهو بعيد-؛ لأنه لو كان كذلك لكان عنده شعور بها، ونحن نراه ثم [نسأله] عن ذلك فلا يكون عنده علم منهُ البتة، فلم يبق إلاَّ أنَّ الله تعالى خلق حقيقة مشتملة على مثال صورته وروحانيته، وأرانا إيَّاها وأوقع في نفسنا مخاطبتنا إيَّاها، أو جعلهَا تخاطبنا حقيقة، وقد يختلف المرئيون فمنهم من يكون المرئي مثال صورتهِ ومعناه، ومنهم من يكون مثال صورته وحقيقته معناه، بأن يكون جعل الله لها ذلك، ومنهم من ينتزع من صورته ومعناه بعينها حقيقة مطابقة لتلك الحقيقة ويرينا إيَّاها، وإنما ذكرنا هذه الاحتمالات ليفهم بها قوله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "مَنْ رَآنِي في المنامِ فقد رآني حقًّا" فقوله: رءاني في الشرط والجزاء ليس من الرؤية البصرية، ولا العلميَّة، بل من الرؤيا المناميَّة، فالمعنى من تعلقت رؤياه بي فهو تعلق صحيح، فإنَّ الشيطان لا يتمثل به، ولكن الشرط والجزاء لا بد من تغايرهما، فالمعنى من تعلقت رؤياه بي في اعتقاده','2','550',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1145,'فهي رؤيا صحيحة، فعلى هذا متى وقع في نفس الرائي أنه رأى النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - فهو قد رآه، سواء كان على هيئته المنقولة في اليقظة أم لا، وقد كنتُ أقمت دهرًا أظن أنَّ هذا إنما يكون فيما إذا رأى تلك الصورة بعينها، وإنما يعلم بذلك الصحابة الذين رأوهُ في اليقظة، أو من وفقه الله لذلك من غيرهم، ثم اعترضت على نفسِي بأنَّ ذلك إنما يكون لو كانت رؤيا بصرية وإنما هي حلمية، ثم باتحاد الشرط والجزاء -ولا بد من تغايرهما- فسلكت الطريقة المتقدمة، ومع ذلك إذا وقع في نفسِهِ أو في سمعه في المنام أنَّ النَّبيَّ - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -[أمره بأمرٍ، لا يجب العمل به؛ لأنَّ الذي أخبره به النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -] هي رؤيته، ولم يخبرنا بأنه يقول له ويكلمه، والنَّائم ليس على يقين من كلامه ولا من كلام تلك الصُّورة المرئية، وليست تلك رؤية بصريَّة بل رؤيا حلميَّة أكثر النَّاس لا يعرفون حقيقتها؛ فلذلك لا يجب الأخذ بها، لكن إذا لم يكن فيها مخالفة لحكم الظاهر يحسن العمل بها أدبًا مع صُورته - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - مثالَهَا، ولا تقول أنَّ النَّبيَّ
- صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أَمَرَهُ وَلاَ خَاطَبَهُ ولا انتقل من مكانه، ولا أحاط علمه الشريف بذلك البتة، وإنما الله أراه إياه لحكمة علمها قد يكون ذلك، وقد يكون عن علم من النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، الله أعلم أي الحالتين كان.
وقد يختلف بعض الرائين مع بعض في ذلك، وقد يقع في نفس نائم أنه رأى ولَم يكن رأى، فلا يوجد الشرط الذِي رتب النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -','2','551',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1146,'[عليه] الجزاء.
والحاصل أنَّ ارتباط الرؤيا -وهي تعلق النَّفس بالمرئي- بارتباط الجزاء -بمعنى أنَّ المرئي لا يتمثل به الشيطان- صحيح قطعًا، وما عدا ذلك يمكن أن يقع للنَّائم غلط فيه.
والصور المختلفة التي يرى النَّائم النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - فيها يجوز أن يتكون أحوالاً يعرض لحقيقته، والحقيقة هي المشار إليها ثانيًا، وهي الأجزاء الأصليَّة وعناصِرها مع الروح، ولها مثال يطابق موكل به ملك الرؤيا، يعصم به عن تمثل الشيطان به". انتهى كلام السُبكي.','2','552',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1147,'٦١٨ -[٢٢٧٧] "الرؤيا من الله والحلم من الشَّيطان" قال في','2','552',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1148,'النِّهاية: "الرؤيا والحلم: عبارة عما يراه النَّائم في نومه من الأشياء، لكن غلبت الرؤيا على ما يراه من الخير، والشيء الحسن، وغلب الحُلم على ما يرَاه من الشرِّ والقبيح".
١٩ ٦- ٢٢٧٨ "وهي على رِجْلِ طائرٍ" قال في النِّهاية: "أي أنها على رِجْلِ قدَرٍ جارٍ وقضاء ماض، من خير أو شرٍّ" وإنَّ ذلك هو الذي قسمها لصاحبها، من قولهم: اقتسموا دارًا فطار سهم فلان في ناحيتها، أي وقع سهمه وخرج.
وكل حركةٍ من كلمة أو شيء يجري لك فهو طائر، والمراد أنَّ','2','553',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1149,'الرؤيا هي التي يَعْبُرها المعبِّر الأوَّل، فكأنَّها كانت على رجل طائر فسقطت ووقعت حيث عبرت، كما يسقط الذي يكون على رجل الطائر بأدنى حركة.
وقال الطيبي: "التركيب من باب التشبيه التمثيلي، شبَّه الرؤيا بالطائر السريع طيرانه، وقد عُلق على رجله شيء يسقط بأدنى حركة؛ فينبغي أن يتوهم للمشبَّه حالات متعددة مناسبة لهذه الحالات، وهي أنَّ الرؤيا مستقرَّة على ما يسوقه التقدير إليه من التعبير.
فإذا كانت في حكم الواقع قيَّضَ وألهم من يتكلم بتأويلها على ما قدَّر فيقع سريعًا، وإن لم يكن في حكمه لم يقدر لهَا من يعبرها".','2','554',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1150,'٦٢٠ -[٢٢٨٩] "ذَنُوبًا" هي الدلو التي فيها ماء.','2','554',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1151,'"فاستحالتْ غرْبًا" بفتح العين المعجمة وسكون الراء وموحدة وهي الدلو العظيمة التي تتَّخذ من جلد ثور.
قال في النِّهاية: "وهذا تمثيل، ومعناه أنَّ عمر لمَّا أخذ الدلو ليستقي عظُمت في يده؛ لأنَّ الفتوح كانت في زمنه أكثر منها في زمن أبي بكر، ومعنى استحالت: انقلبت من الصِّغر إلى الكبر".
"فلم أر عبقريًّا" هو سيد القوم وكبيرهم، وقويّهم، والأصل في العبقري فيما قيل: أنَّ عبقر قرية سكنها الجن فيما يزعُمون، فكلما رأوا شيئًا فائقًا غريبًا مما يصعب عمله ويدق، أو شيئًا عظيمًا في نفسِه نسبوه إليها، فقالوا: عبقري، ثم اتَّسع فيه حتى سُمِي به السيد والكبير.
"يفري فريهُ" أي: يعمل عمله، ويقطع قَطْعَهُ.
"وفرِيَّه" روي بكسر الراء وتشديد المثناة من تحت وسكون الراء، والتخفيف؛ وحُكِي عن الخليل أنه أنكر التثقيل وغلط قائله.
وأصل الفرى القطع، يقال: فريت الشيء أفريه فريًا: إذا شققته وقطعته للإصلاح، وأفريته: إذا شققته على جهة الإفساد".','2','555',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1152,'٦٢١ -[٢٢٩٠] "ثَائِرَةَ الرَّأْسِ" أي: منتشرة [الشعر] قائمته.
"بمَهْيَعَةٍ" بفتح الميم، وإسكان الهاء، وفتح الياء التحتية، والعين المهملة: اسم للجُحْفَة.','2','556',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1153,'٦٢٢ -[٢٢٩٣] "ظُلَّةً" من السحَاب.','2','556',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1154,'"يَنْطِفُ" بِكسر الطاء وضمها، أي: يقطر.
"سببًا" هو الحبل.','2','557',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1155,'"<span data-type="title" id=toc-43>أبواب الشهادات</span>"','2','558',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1156,'٦٢٣ -[٢٢٩٨] "صاحب غِمْر" بكسر الغين وسكون الميم: وهو الحقد.','2','558',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1157,'"<span data-type="title" id=toc-44>أبواب الزُّهد</span>"
قال ابن القيم: "الفرق بين الزُّهد والورع، أنَّ الزُّهد: ترك ما لا ينفع في الآخرة، والورع: ترك ما يخشى ضرره في الآخرة".','2','559',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1158,'٦٢٤ -[٢٣٠٤] "نعمتان مغبون فيهما كثيرٌ من النَّاس: الصحة والفراغ".
قال ابن الخازن: "النعمة ما يتنعَّم به الإنسان ويستلذه، والغبن: أن يشتري بأضعاف الثمن، أو يبيع بدُون ثمن المثل، فمن صحَّ بدنه وتفرغ للأشغال العائقة، ولم يَسْعَ لإصلاح آخرته فهو كالمغبُون في البيع".','2','559',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1159,'٦٢٥ -[٢٣٠٦] "بادِرُوا بالأعْمَالِ سَبْعًا"
قال الطيبي: "أي سابقوا وقوع الفتن، بالاشتغال بالأعمال الصالحة، واهتموا بها قبل نُزُولها".
"أو هَرمٍ مُفَنِّدٍ" قال في النِّهاية: "الفند في الأصل الكذب، وأفند تكلم بالفند. ثم قالوا: الشيء إذا هرم: قد أفند، لأنه يتكلَّم بالمحرَّف من الكلام عن سنن الصِّحه، وأفنده الكبر: إذا أوقعهُ في الفند".
"أو موتٍ مُجْهزٍ" بجيم وزاي آخره: أي سَريع.
يقال: أجهز على الجريح، يجهز، إذا أسرع قتله.','2','560',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1160,'٦٢٦ -[٢٣٠٧] "أكْثروا ذكْرَ هَاذِمِ اللَّذَّاتِ" بالذال المعجمة، أي','2','560',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1161,'قاطعها.
"الموتَ" قال المُظهري: "بالجر عطف بيان، وبالرفع خبر مبتدأ
محذوف، وبالنصب على تقدير: أَعنِي"','2','561',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1162,'٦٢٧ -[٢٣٠٨] "أفظع" بفاء وظاء معجمة وعين مهملة؛ أي أشد وأشْنع.','2','561',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1163,'٦٢٨ -[٢٣١٢] "أطَّتِ السَّمَاءُ" بفتح الهمزة والطاء المهملة المشددة، قال في النهاية: "الأطيط صوت الأقتاب، وأطيط الإبل؛','2','561',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1164,'وهو من جوامع الكلم التي أعطيها - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -".
قالى ابن عبد البر: " كلامه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - هذا من الكلام الجامع للمعاني الكثيرة الجليلة في الألفاظ القليلة، وهو مما لم يقله أحد قبله، إلاَّ أنه روي في صحف شيث: من عدَّ كلامَه مِنْ عَمَلِهِ، قلَّ كلامُهُ إلاَّ فيما يَعينه".
قال الفاكهاني: "هذا خاص بالكلام، وأما الحديث فهو أعمّ من الكلام؛ لأنَّ مما لا يعينه التوسع في الدنيا، وطلب المناصب والرئاسة، وحب المحمدة والثناء وغير ذلك".','2','563',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1165,'٦٣٠ -[٢٣٢٢] "إِنَّ الدُّنيا ملْعُونَةٌ، مَلْعُونٌ مَا فِيْهَا إِلاَّ ذِكْرَ اللهِ وَمَا وَالاَهُ، وَعَالِمٌ، أَوْ مُتَعَلِّمٌ" هُمَا منْصُوبَانِ؛ لأنَّ الاستثناء من موجب، وكتب بلا ألف على طريقة كثير من المحدثين.','2','563',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1166,'٦٣١ -[٢٣٢٣] "فِي اليَمِّ" هو البحر، وقيل: إنه معرَّب.','2','564',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1167,'٦٣٢ -[٢٣٢٥] "مَا نَقَصَ مَالُ عَبْدٍ مِنْ صَدَقَةٍ".
قال الشيخ عزُّ الدِّين بن عبد السلام في أماليه: "معناه أنَّ ابن آدم لا يضيع له شيءٌ، ومَا لم ينتفع به في دنياه انتفع به في أُخراه، فالإنسان إذا كان له داران فحوَّل بعض ماله من إحدى داريه إلى الأخرى، لا يقال ذلك البعض المحوَّل نقصَ من ماله، وقد كان بعض السَّلف يقول إذا رأى السائلين: مرحبًا بمن جاء يُحَوِّل ما لَنَا من دُنيانا لأخرانا؛ فهذا معنى الحديث، وليس معناه أنَّ المال لا ينقص في الحس ولا أنَّ الله يخلف','2','564',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1168,'عليه؛ لأنَّ ذلك معنى مستأنف" انتهى.','2','565',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1169,'٦٣٣ -[٢٣٢٨] "لاَ تَتَّخِذوا الضَّيْعَةَ"، قَال في النِّهاية:
"هي مَا يَكُونُ منه المَعاش، كالصنعة والتجارة، والزراعة، وغير ذلك".','2','565',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1170,'٦٣٤ -[٢٣٣٢] "لاَ تقُومُ السَّاعَةُ حَتَّى يَتَقَارَبَ الزَّمانُ، فَتَكُونُ السَّنَةُ كَالشَّهْرِ" الحديث.
قال في النِّهاية: "أراد: يطيب الزمان حتى لا يُستطال، وأيام السُّرُورِ والعافية قصِيرة، وقيل: يَقَعُ كناية عن قصر الأعمار وقلة البركة".','2','565',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1171,'"كالضَّرمَة بالنَّارِ" بفتح الضاد المعجمة.
قال في النِّهاية: "الضَّرْمَةُ؛ بالتَّحريكِ: النَّارُ".
وفي القاموس: "ضرمت النَّار اشتعلت".','2','566',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1172,'٦٣٥ -[٢٣٤١] "وَجلف الخبز".
قال في النِّهاية: "الجِلْفُ: الخُبزُ وَحده لا أُدْمَ مَعهُ، وقيل:
الخبز الغليظُ اليابِسُ، ويُروى بفتح اللام -جمع جِلْفَة- وَهِيَ الكِسْرَةُ مِنَ الخُبْز.','2','566',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1173,'٦٣٦ -[٢٣٤٤] "لَوْ أَنَّكُمْ كنْتُمْ تَوكَّلونَ علَى اللهِ حَقَّ تَوَكُّلِهِ، لَرُزِقْتمْ كَمَا يُرزَقُ الطيْرُ: تَغْدُو خِمَاصًا، وَتَرُوحُ بِطَانًا" أي:','2','566',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1174,'تغدُو بكرة وهي جِيَاع، وتروح عشاء وهي ممتلئة البطون. وَالخِمَاص؛ بِكَسْرِ الخَاء المعجَمة وآخرهُ صاد مُهملة جمع خميص: وهو الضامر البطنِ.
والبِطَان؛ بِكسر الموحدة، جمع بطين: وهو العظيم البطن.
قال البيهقي في شعب الإيمان: "ليس في هذا الحديث دلالة على القُعود عن الكسْبِ، بل فيه ما يدُل على طلب الرزق؛ لأنَّ الطير إذَا غدت فإنما تغْدُوا لطلب الرزق، وإنما أراد -والله أعلم- لو توكلوا على الله في ذهابهم، ومجيئهم وتصرُّفهم، ورأوا أنَّ الخير بيده ومن عنده، لم ينصرفوا إلاَّ سالمين غانمين كالطير تغدُوا خِمَاصًا وترُوحُ بطَانًا، لكنهم يعتمدون على قوتهم وجلدهم، ويغشون ويكذبون ولا ينصحون، وهذا خلاف التوكل".','2','567',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1175,'٦٣٧ -[٢٣٤٦] "آمِنًا في سِربِه" قال: [في] النِّهاية: "بكسر السين: أي في نفسه.
قال: ويروى بالفتح، وهو المسلك والطَّريق".','2','567',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1176,'٦٣٨ -[٢٣٤٧] "خَفِيْفُ الحَاذِ" بحاء مُهملة وآخره ذال معجمة خفيفة، قال في النِّهاية: "الحاذُ، والحال واحد، وأصل الحاذ: طريقة المتن؛ وهو ما يقع عليه اللَّبْدُ من ظَهرِ الفرس: أي خفيف الظَّهر من العِيَال".
"وَكَانَ غامِضًا فِي النَّاسِ" بإعجام الغين والضاد معًا؛ أي:
مغمورًا غير مشهور، وفي بعض النُّسخ بإهمال الصَّاد، فهو فاعل بمعنى مفعول، أي مغمُوصًا، بمعنى محتقرًا مزدرى، وضبطه الحكيم في نوادره بالوجهين.','2','568',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1177,'٦٣٩ -[٢٣٥٠] "تِجْفَافًا".','2','568',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1178,'قال في النِّهاية: "التجفاف: ما جُلِّلَ به الفرس من سلاح وآلة
تقيه الجراح، والتاء فيه زائدة والجمع تجافيف".','2','569',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1179,'٦٤٠ -[٢٣٥١] "فُقَراء المهاجرين يدخلون الجنَّة قبل أغنيائهم
بخمسمائة عامٍ".
روى أبو محمَّد الحسن بن محمَّد بن الحسن الخلاَّل في كتابه "فضل الفقير والفقراء": من حديث القاضي بدر بن الهيثم: "حدثنا سليمان بن الربيع، حدَّثنا الحارث بن إدريس، عن خارجة بن','2','569',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1180,'مصعب، عن زيد بن أسلم عن أنس بن مالك قال: "بعث الفقراء
إلى رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - ... " الحديث، وفيه: "يدخل الفقراء الجنَّة قبل
الأغنياء بنصف يوم، وهو خمسمائة عام".
قال الحارث: قال سفيان: تفسيره: أنَّ في الجنَّةِ ثماني أبواب، ما بين الباب إلى الباب خمسمائة عام لكل باب أهل، فينسى الغَنِيُّ بابه فيجيء إلى باب غيره، فيقول البواب؛ ارجع إلى بابك، فيرجع إلى بابه وهو خمسمائة عام".','2','570',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1181,'٦٤١ -[٢٣٦٢] "كَان لا يَّدخر شيئًا لِغَدٍ" قال البيهقي في شعب الإيمان: "قال أبو سهل محمَّد بن سُليمان في إملائه على هذا','2','570',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1182,'الحديث؛ فإن قال قائل: كان النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - يرجع إلى ملبس ومفرش، وكان
يعد للجميع ما يعده، وكان له الدرع والسَّيف والقوس، والفرس والبغل والحمار، وكان يُنْبَذُ له بالعشي فيشربه بالغداة وينبذ له بالغداة فيشربه بالعشي، وكان يَحْبِسُ لنسائه قوت سنة مما أفاء الله تعالى عليه، وكل هذا ادخار، فكيف يسلم على هذه [الأخبار] هذا الخبر المأثور؟ قال الأستاذ أبو سهل: الرواية صحيحة، وعلى حكم الدراية مستقيمة، والتنافي عن هذه الرواية منصرف، ووجه ذلك أنه كان يتعامل فيما بينه وبين مولاه على حسن الظن والانتظار دون الحبس والادخار، وكان لا يحتجز لنفسه ليومه من أمسِهِ.
فأما ثيابه فإنما يعدَها لدينه لا على بقاء عليها لِغَد، وكذا آلات الحرب كان يحبسها لنَصْرِ الأولياء وكبت الأعداء على حكم الاستعمال مما تصدق به في حياته.
ولهذا قال: "إنَّا لا نورث، ما تركنا صدقة".
ْوأما ما كان ينبذ له فإنما نساؤه كنَّ ينبذن له ما صار في ملكهن، ويَدهِن تمليكًا وتمويلاً منه لهن، وقد صحَّ أنه لم يكن يدخر شيئًا لغد، فإن احتبس عنده شيئًا فلا على نية الغد. ولكن ... وتصرفه في نائبة من نوائب الدين، وقيل: لا يَدَّخِر مُلكًا بل يدَّخِرُ تَمْلِيكًا.
وقيل: لم يكن يدخره على أمل البقاء إلى غد" انتهى.','2','571',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1183,'وقال الحافظ ابن حجر في أجوبته: "قد حسنه [الترمذي] وكأنه حسنه لأنَّ له شاهدًا من حديث أبي سعيد الخدري، أخرجه ابن ماجه، وصححه الحاكم.
قلتُ: وله شاهد آخر من حديث عبادة بن الصامت، أخرجه الطبراني والبيهقي في سننه.
وقال القاضي تاج الدِّين السبكي في التوشيح: "سمعت الشَّيخ الإمام الوالد يقول: لم يكن رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - فقيرًا من المال قط، ولا كانت حاله حال فقير، كان أغنى النَّاس بالله، قد كفي دنياه في نفسه وعِيَاله، وكان في قوله: "اللَّهم أحيني مسكينًا" أنَّ المراد به استكانة القلب، لاَ المسكنة التي هي نَوْع من الفقر، وكان يشدد النكير','2','573',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1184,'على من كان يعتقد خلاف ذلك".
وقال البيهقي في سُننه: "الذي يدُل عليه حاله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - عند وفاته، أنه لم يسأل المسكنة التي يرجع معناها إلى القلة، فقد مات مكفيا بما أفاء الله عليه، وإنما سأل المسكنة التي يَرجع معناها إلى الإخبات والتواضع، وكأنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - سَأل الله تعالى أن لا يجعله من الجبارين المتكبرين، وأن لا يحشره في زمرة الأغنياء المترفين".
قال القتبي: "المسكنة: حرف مأخوذ من السكون، يقال: تمسكن، أي تخشع وتواضع".','2','574',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1185,'٦٤٣ -[٢٣٦٤] "نُثَرِّيهِ" بالمثلثة، أي نَبُلُّهُ بالماء.','2','574',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1186,'٦٤٤ -[٢٣٦٥] "والحُبْلَةَ" بضم الحاء وسُكون الباء الموحدة','2','574',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1187,'ثمر السمر، وقيل: ثمر القضاه.
"يُعَزِّرُونِي فِي الدِّيْنِ" قال في النِّهاية: " [أي] توقفني عليه،
وقيل: توبخني على التقصير فيه".','2','575',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1188,'٦٤٥ -[٢٣٦٨] "مِنَ الخَصَاصَةِ".
قال في النِّهاية: "أي: الجوع والضعف، وأصلها الفقر والحاجة إلى الشيء".
"حتى تقولَ الأعْرَاب: هَؤلاءِ مَجَانِينُ، أَو مَجَانُونُ".
قال في النِّهاية: "المجانين، جمع تكسير لمجنون، وأما مجانون','2','575',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1189,'فشاذ، كما شذَّ شياطون في شياطين".','2','576',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1190,'٦٤٦ -[٢٣٦٩] "بِقِربَةٍ يَزْعَبُهَا" بزاي وعين مهملة وباء مُوحدة؛ أي: يتدافع بها ويحمله لثقلها، وقيل: زعب بحمله إذا استقام.','2','576',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1191,'٦٤٧ -[٢٣٧١] "وَرَفَعنَا عنْ بُطُونِنَا عن حَجَرٍ حَجَرٍ" الحكمة في ذلك أنه يخف ببرد الحجَر حرارة الجوع.','2','577',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1192,'٦٤٨ -[٢٣٧٢] "من الدَّقَلِ" بفتح الدال المهملة والقاف: هو رديءُ التمر ويابسه.','2','577',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1193,'٦٤٩ -[٢٣٧٨] "مُوسى بن ورِدان عن أبي هريرة قال: قال رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "المَرءُ على دِيْنِ خَلِيْلِهِ فلْيَنْظُرْ أَحَدُكُمْ مَنْ يُخَالِلُ"".
"هذا حديث غريب"، هذا أحد الأحاديث التي انتقدها الحافظ سراج الدِّين [القزويني في المصابيح وزعم أنه موضوع.
وقال الحافظ صَلاح الدِّين] العَلائي: نسبَة هذا الحديث إلى','2','577',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1194,'كونه مَوضوعًا جهلٌ قبيحٌ، بل هو حَسنٌ كما ذكره الترمذي، فإنَّ موسى ابن وردان وثَّقه العجلي وأبو داود، وقال فيه أحمد بن حنبل: لا أعلم إلاَّ خيرًا. وقال أبو حاتم والدارقطني: لا بأس به، ولم يتكلم فيه أحد، وزهير بن محمَّد هو المروزي، وثقه أحمد وابن معين، وتكلم فيه غيرهما، واحتج به الشيخان في الصحيحين، وذلك يدفع ما تكلم به فيه، فتفرده يكون حسنًا غريبًا ولا ينتهي إلى الضعف، فضلاً عن الوضع" انتهى.','2','578',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1195,'٦٥٠ -[٢٣٨٠] "مَا مَلأ آدَمِيٌّ وِعَاءً شَرًّا من بَطْنٍ، بِحَسْبِ ابن آدَمَ أُكُلَاتٌ" بضم الهمزة والكاف جمع "أُكلة" بالضم: وهي اللقمة.','2','578',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1196,'"يُقِمْنَ صُلْبَهُ" فإنْ كان لا محَالَةَ، فثلُثٌ لِطعامه، وثُلث لشرابه، وثُلث لِنفْسِهِ".
قال ابن القيم في الهدي: "الأمراض نوعان: أمراض حادثة تكون عن زيادة مادة أفرطت في البدن حتى أضرت بأفعاله الطبيعيَّة وهي الأمراض الأكثرية؛ وسببها إدخال الطعام على البدن قبل هضم الأول، والزيادة في القدر الذي يحتاج إليه البدن، وتناول الأغذية القليلة النفع البطيئة الهضم، والإكثار من الأغذية المختلفة التراكيب المتنوعة.
وإذا ملأ الآدمي بطنه من هذه الأغذية واعتاد ذلك، أورثته أمراضًا متنوعة.
فإذا توسط في الغذاء وتناول منه قدر الحاجة -وكان معتدلاً في كميته وكيفيته- كان انتفاع البدن به أكثر من انتفاعه بالغذاء الكثير.
ومراتب الغذاء ثلاثة: أحدها: مرتبة الحاجة.
ْوالثانية: مرتبة الكفاية.
والثالثة: مرتبة الفَضْلَة.
فأخبر النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أنه يكفيه لقيمات يقمن صلبه، فلا تسْقط قُوَّته ولا يَضعُفُ معها، فإن تجاوزها فليأكل في ثلث بطنه، ويدع الثلث الآخر للماء، والثلث للنفس؛ وهذا من أنفع ما للبدن والقلب، فإنَّ البطن إذا امتلأ من الطعام ضاق عن الشراب، فإذا ورد عليه الشراب ضاق عن النفس وعرض له الكرب والتعب بحمله، بمنزلة حامِلِ الحِمْلِ الثقيل؛','2','579',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1197,'والشبع المفرط يُضعف القُوى والبدن، وإنما يَقْوى البدن بحسب ما يقبل من الغذاء لا بحسب كثرته، ولما كان في الإنسان جزء أرضيّ وجزء مائي، وجزء هوائي، قسم النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - طعامه وشرابه ونفسه إلى الأجزاء الثلاثة، فإن قيل: فأين الحظ الناري؟ قيل: هذه مسألة خلاف.
فمن النَّاس من قال: ليس في البدن جزء ناري، وعليه طائفة من الأطبَّاء وغيرهم، ومنهمِ من أثبته" انتهى.','2','580',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1198,'٦٥١ -[٢٣٨٢] "نَشَغَ" بنون وشين وغين معجمتين، قال في','2','580',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1199,'النِّهاية: "النشيغ في الأصل: الشهيق حتى يكاد يبلغ به الفشي، وإنما يفعل الإنسان ذلك تشوقًا إلى شيءٍ فائت وأسَفًا عليه".
"بل أَردَتْ أَنْ يُقَال: فُلانٌ قَارِيءٌ، فقد قيل ذاك" سُئل الشَّيخ تقي الدِّين بن الصَّلاح عن معنى هذا الحديث، وهل هو محمُول على أنه لم يكن له حسنة غير العلم، أو على أنَّ له حسنات غيره، فاحبطت نيته في العِلم حسناته، وهذا خلاف قوله تعالى: {إِنَّ الْحَسَنَاتِ يُذْهِبْنَ السَّيِّئَاتِ} ؟ فأجاب: كان بمثابة لو أخلص في عمله، لنَجَّاه عمله من العذاب الذي وُجِد مُقْتَضِيه، فلما لم يخلص نزل به مُوجِب المقتضى لعذابه.
أو هذا فيمن ترجحت سيئات ريَائه بالعِلم على حسناته، فلم تدفع عنه حسناتُه عذاب ذنب الرياء، فعذب والله أعلم".','2','581',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1200,'٦٥٢ -[٢٣٨٤] "الرَّجل يَعْمَل العَمل فَيُسِرُّهُ، فإِذَا اطُّلِعَ علَيْهِ أَعْجَبه"','2','581',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1201,'قال في النِّهاية: "أي: نرميه يريد به الخيبة، وأن لا يعطوا عليه شيئًا، ومنهم من يخبر به على ظاهره، فيرمي فيها [التراب] .','2','583',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1202,'٦٥٥ -[٢٤٠٤] "يَخْتَلُونَ الدُّنْيَا بِالدِّيْنِ".
قال في النِّهاية: "أي: يطلبون الدنيا بعمل الآخرة.
يقال: ختله يختلهُ، إذا خدعه ورَاوغه، وختل الذئب الصيد إذا تخفي له".','2','583',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1203,'٦٥٦ -[٢٤٠٥] "أتيحنهم فِتْنَةً" يقال: أتاح الله لفلان كذا، أي','2','583',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1204,'قدره له وأنزله به.','2','584',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1205,'٦٥٧ -[٢٤٠٦] "أَمْلِكْ عَليْكَ لِسَانَكَ" أي لا تُجِره إلاَّ بما يكون لك لا عليك.','2','584',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1206,'٦٥٨ -[٢٤٠٧] "فَإِنَّ الأعْضَاءَ كُلَّهَا تكَفِّرُ اللِّسَانَ".
قال في النِّهاية: "أي تذل وتخضع.
والتكفير: هو أن يَنْحَنِيَ الإنْسَانُ وَيُطَاطِىءَ رَأسه قريبًا من الرُّكُوعِ كما يفعَلُ من يُريدُ تعظيم صَاحِبِه".','2','584',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1207,'٦٥٩ -[٢٤١٣] "مُتَبذلة" التبذل ترك التزين والتهيؤ بالهيئة','2','584',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1208,'الحسنة الجميلة.
قال في النِّهاية: "يُرْوى متبذلة ومبتذلة، وهما بمعنى"','2','585',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1209,'٦٦٠ -[٢٤١٥] "تَرجُمَانٌ" بفتح التاء وضم الجيم.','2','585',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1210,'"ثُمَّ يَنْظُرَ أَيْمَن مِنْهُ" بالنَّصب على الظرف؛ أي: عن يمينه.
"ثُمَّ يَنْظُرَ أَشْأَمَ مِنْهُ" أي: عن شماله.','2','586',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1211,'٢٢٦١ -[٢٤٢٠] "لِلْشَاةِ الجَلْحَاءِ" هي التي لا قرن لها.','2','586',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1212,'٦٦٢ -[٢٤٢١] "فتَصْهُرُهُمْ الشَّمْسُ" أي: تذيبهم.
ويحتمل أن يكون معناه: تَقْرُب منهم، وتَدْنُو.','2','586',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1213,'٦٦٣ -[٢٤٢٣] "غُرْلاً" بغين مضمُومَة وراء ساكنة ولام: أي:','2','586',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1214,'غير مختونين، جمع أغرل.','2','587',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1215,'٦٦٤ -[٢٤٢٦] "مَنْ نوقِشَ الحِسَابَ" أي من استُقْصِيَ في مُحاسبته وَحُوقق.','2','587',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1216,'٦٦٥ -[٢٤٢٧] "كَأنَّهُ بَذجٌ" بفتح الموحدة والذال المعجمة','2','587',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1217,'وجيم؛ وهو ولد الضأن، وجمعه بِذْجَانٌ.','2','588',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1218,'٦٦٦ -[٢٤٢٨] "وَتَرَكْتُكَ تَرْأَسُ" يقال: رأس القوم، يرأسهم رياسة، إذا صار رئيسهم ومُقَدَّمَهم.
"وَتَربَعُ" أي: تأخذ ربع الغنيمة، يقال: رَبَعت القَوم أَرْبَعُهُم:
إِذَا أَخَذْتُ رُبْع أَمْوَالِهِم، يريد جعلتك رئيسًا مطاعًا؛ لأنَّ الملِك كان يأخذ الرُّبع من الغنيمة في الجاهلية دون أصحابه.','2','588',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1219,'٦٦٧ -[٢٤٣٤] "فَنهس مِنْهَا نَهْسَةً" النَّهس بالسين المهملة:','2','588',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1220,'أخذ اللحم بأطراف أَسنانه.
"وَيَنْفُذُهُمْ البَصَر".
قال في النِّهاية: " قال أبو حاتم: أصحاب الحديث يروونه بالذال المعجمة، وإنما هو بالمهملة: أي يبلغ أولهم وآخرهم حتى يراهم كلَّهم ويستوعبهم، من نفذ الشيء وأنْفَدْته.
قيل: المراد به ينفدهم بصر الرَّحمن حتى يأتي عليهم كُلِّهم، وقيل: أراد ينفدهم بصر النَّاظر؛ لاستواء الصعيد، وحمل الحديث على بَصر المبصِر أولى من حمله على بصر الرَّحمن، لأنَّ الله يجمع النَّاس','2','589',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1221,'يوم القيامة في أرض يشهد جميع الخلائق فيها محاسبة العبد الواحد على انفراده، ويرون ما يصير إليه".','2','590',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1222,'٦٦٨ -[٢٤٣٦] "شفاعتِي لأِهْلِ الكَبَائِرِ مِنْ أُمَّتِي".
قال النووي في الأذكار: "روى النّحاس عن أبي بكر محمَّد بن أبي يحيى قال: وكان من الفقهاء الأدباء العلماء.
قال: لا تقل اللَّهم ارزقنا شفاعة النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، فإنما يشفع لمن استوجب النَّار.
وقال النووي: هذا خطأ فاحش، وجهالة بيِّنة ولولا خوف الاغترار بهذا اللَّفظ، وكونه قد ذُكر في كتب مصنِفه لما تجاسرت على حكايته، فكم من حديث في الصحيح جاء في ترغيب المؤمنين الكاملين بوعدهم شفاعة النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -. [فإنَّما يشفع لمن استوجب النَّار] . "من','2','590',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1223,'قال مثل ما يقولُ المؤذن حلت له شفاعتي" وغير ذلك.
ولقد أحسنَ الإمام الحافظ الفقيه أبو الفضل عياض رحمه الله في قوله: قد عرف بالنقل المستفيض سُؤال السلف الصَّالح رضي الله عنهم شفاعة نبينا - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، ورغبتهم فيها، قال: وعلى هذا لا يلتفت إلى كراهة من كره ذلك -لكونها لا تكون إلاَّ للمذنبين- لأنه ثبت في الأحاديث في صحيح مسلم وغيره إثبات الشفاعة لأقوام في دخولهم الجنَّة بغير حساب، ولقوم في زيادة درجاتهم في الجنَّة. قال: ثم كل عاقل -يعترف بالتقصير فيحتاج إلى العفو- مشفقٌ من كونه من الهالكين.
ومنهم: "من يشفع للعُصبَةِ" هم الجماعة من النَّاس إلى العشرة إلى الأربعين ولا واحد لها من لفظها.','2','591',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1224,'٦٦٩ -[٢٤٤٤] "إِلى عمَّانَ البَلْقَاءِ" قال في النِّهاية: "هي بفتح','2','591',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1225,'العين وتشديد الميم: مدينة قديمه بالشَّام من أرض البلقاء، فأمَّا بالضم والتخفيف فهو سُقْع عند البحرين".
"السدد" جمع سدة وهي كالظلة على الباب، لتقي الباب من المطر، وقيل: هي البَاب نفسه، وقيل: هي الساحة بين يديه.','2','592',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1226,'٦٧٠ -[٢٤٤٥] "لَم يَظْمأ آخِرَ مَا عَلَيهِ" قال أبو البقاء: "هو منصوب على الظرف، والتقدير لم يظمأ أبدًا، وقد جاء في حديث آخر بهذا اللَّفظ، والمعنى لم يظمأ ذلك الشَّارب إلى آخر مدة بقائه، ومعلومٌ','2','592',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1227,'أنه يبقى أبدًا، فيكون معناه أنه لم يظمأ أبدًا".
وذكر البطليوسي مثله، وقال: والحقيقة تقديره: لم يظمأ آخر ما عليه أن يبقى، والعرب تستعمل الآخر، يريد به معنى الأبد، كقول الشَّاعر:
أَمَا لَكَ عَمْرٌ إنَّما أَنت حيَّة ... إذا هي لم تُقتل تعش آخر الدَّهر','2','593',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1228,'٦٧١ -[٢٤٤٦] "عُكَّاشة" بضم أوله، وتشديد الكاف وتخفيفها أيضًا.','2','593',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1229,'٦٧٢ -[٢٤٤٦] "تخيَّلَ، واخْتَال" هما تفعل وافتعل، من الخيلاء وهو الكبر والعجب.','2','594',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1230,'٦٧٣ -[٢٤٥٠] "مَنْ خَافَ أَدْلَجَ" يقال: أدلج بالتخفيف: إذا سار أول اللَّيل، وأدَّلَج " [بالتشديد] إذا سار من آخره.','2','594',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1231,'٦٧٤ -[٢٤٥٣] "شِرَّةٌ" بكسر الشِّين وتشديد الراء: النشاط والرغبة.','2','594',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1232,'"الكَيِّسُ مَنْ دَانَ نَفْسَهُ" أي: أذلها واستعبدها، وقيل: حاسبها.','2','595',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1233,'٦٧٥ -[٢٤٦٠] "يَكتشرونَ" الكشر بالشين المعجمة','2','595',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1234,'ظهور الأسنَان للضحك.','2','596',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1235,'٦٧٦ -[٢٤٦١] "على رَمل حَصِيرٍ" بفتح الراء والميم: هو السَّعف المنسُوج.','2','596',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1236,'٦٧٧ -[٢٤٦٨] "قِرَامُ سِتْرٍ" بِكَسْرِ القاف وراء: الستر الرقيق، وقيل: الصفيق من صوف ذي ألوان، والإضافة فيه، كقولك: ثوب قميص، وقيل: القرام: الستر الرقيق وراء الستر الغليظ، ولذلك أضافه.','2','596',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1237,'"سَمَلُ قَطِيْفَةٍ" السَّمَلُ: الخَلَقُ مِن الثِّيَاب.','2','597',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1238,'٦٧٨ -[٢٤٧١] "إِنْ كُنَّا" هي المخففة من الثقيلة.
"آل محمَّدٍ" بالنَّصب على الاختصاص.
- ٢٤٧٣ "إهابًا معْطونًا" هو النتن المُتمزق من الشعر يقال: عطِنَ الجلد فهو عطنٌ ومعطون، إذا مزقت شعره وأنتن في','2','597',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1239,'الدباغ.
"فجَوَّبْتُ وَسطهُ" بالجيم وتشديد الواو، أي: قطعته.
"وهو يَسْقي بِبَكرةٍ" بسكون الكاف.','2','598',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1240,'٦٧٩ -[٢٤٧٦] "مَرقوعة بِفَرْوٍ".','2','598',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1241,'٦٨٠ -[٢٤٧٧] "ولا يأوُون" أي لا يلتفتُون، ولا يعطون.','2','598',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1242,'٦٨١ -[٢٤٨٥] "انْجَفل النَّاسُ إليه" أي: ذهبوا مسرعين نحوه.','2','599',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1243,'٦٨٢ -[٢٤٨٧] "وَأَشْرَكُونَا في المَهْنَإِ" أي: الأمر الهنيء؛ قال','2','599',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1244,'في القاموس: "الهَنِيءُ، والمَهْنَأُ؛ مما أتاك الله بلا مشقة"','2','600',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1245,'٦٨٣ -[٢٤٨٩] "في مَهْنَةِ أَهلِهِ" بفتح الميم وسكون الهاء، وهي الخدمة.
قال الأصمعي: "ولا يقال بالكسر".
وقال الزمخشري: "الكسر خطأ عند الأثبات".','2','600',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1246,'٦٨٤ -[٢٤٩٢] "أمثال الذرِّ" هو النمل الأحمر الصغير، الواحد ذرَّة، وسئل ثعلب عنها، فقال: إنَّ مائة نملة وزن حبَّة.','2','600',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1247,'"تَعْلوهُمْ نَارُ الأَنْيارِ". قال أبو البقاء في إعراب الحديث: "كذا وقع في هذه الرواية، ويريد بذلك جمع نار، والأشبه أنه حمل الأنيار على النيران حيث شاركها في الجمع، كما قال بعضهم في جمع ريح: أرياح، لما رآهم قالوا: رياح.','2','601',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1248,'٦٨٥ -[٢٤٩٨] "دوِّيَّة" بالتشديد نسبة إلى الدوّ؛ وهي الصحراء التي لا نبات بها.','2','601',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1249,'٦٨٦ -[٢٥٠٦] "حدثنا سَلمة بن شبيب، حدثنا أميه بن القاسم، ثنا حفص بن غياث عن برد بن','2','601',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1250,'سنان عن مكحول عن واثلة بن الأسقع قال: قال رسول
الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "لا تُظهرِ الشَّمَاتَةَ لأِخِيْكَ، فيرحَمَهُ اللهُ ويَبْتَلِيْكَ"".
"هذا حديثٌ حسنٌ غريبٌ، ومكحول قد سمع من واثلة".
هذا أحد الأحاديث التي انتقدها الحافظ سراج الدِّين القزويني على المصابيح، وزعم أنه موضوع. وقال الحافظ صلاح الدِّين العلائي:
"هذا الحديث ذكره ابن الجوزي في الموضوعات، وقال: تفرد به عمر بن إسماعيل بن مجالد وهو متروك، عن حفص بن غياث وعُمر بن إسماعيل كما ذكر اتَّفقوا على ضعفه ووهائه، لكن لم ينفرد به،','2','602',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1251,'فقد رواه الترمذي من طريق أميه بن القاسم عن حفص قال: شيخنا المزي في الأطراف: "كذا وقع في جميع الروايات، أميَّة بن القاسم وهو خطأ، وصوابه القاسم بن أميَّة الحذَّاء العبدِي، رواه عنه محمَّد بن غالب ابن حرب [بن] تمام فقال: حدثنا القاسم بن أميَّة الحذاء بالبصرة، فذكره، وقد ذكرهُ عبد الرَّحمن بن أبي حاتم في كتابه وقال: سئل أبي عنه، فقال: ليس به بأس صدوق وسُئل أبو زرعة عنه، فقال: كان صدُوقًا".
قال العلائي: "فَبَرِىءَ عمر بن إسماعيل بن مجالد من عهدته، وبقي الحديث حسنًا كما قال الترمذي، لكنه غريب لتفرد القاسم بن أميَّة به.
قال: "والعجب أنَّ شيخنا المزي ذكر هذا في الأطراف، ولم يذكر في التهذيب سوى أميَّة بن القاسم في حرف الألف، ولم يزد على أن قال: روى عن حفص بن غياث، روى عنه سلمة بن شبيب، روى له الترمذي ولم يذكر [في حرف] القاف، القاسم بن أميَّة، لأنه لم يجىء في كتاب الترمذي هكذا، ولم ينبه عليه في حرف الألف كما فعل','2','603',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1252,'في الأطراف" انتهى.','2','604',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1253,'٦٨٧ -[٢٥١٤] "عَافَسْنَا الأزْوَاج" المعافسة، المعالجة، والممارسة، والملاعبة، [والضَّيعة: المعاش] .
"ولكن يا حَنْظَلَةُ سَاعةٌ وَسَاعَة"ٌ قال أبو البقاء: "يجوز النَّصب على معنى تذْكُر ساعةً، وتلهُو ساعةً، والرفع على تقدير: لنا ساعةٌ، ولله ساعةٌ".
وقال الحكيم في نوادره: "أي: ساعة للذكر، وساعة للنفس".','2','604',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1254,'٦٨٨ -[٢٥١٦] "احفظِ الله يحفظك".','2','604',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1255,'قال الفاكهاني: "معناه؛ احفظ أمر الله واتقه، فلا يراك حيث نهاك، واحفظ حدود الله ومراسمه التي أوجبها عليك، فلا تُضيع منها شيئًا، فإذا فعلت ذلك حفظك الله في نفسك ودينك ودنياك. وهذا من أحسنِ العبارات عن هذا المعنى وأبلغها وأجزلها، وهو من جوامع الكلم التي أوتيها - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -".
"احفظ الله تَجِدْة تجَاهَكَ". قال الفاكهاني: "معنا تجده معَك بالحفظ والإحاطة والتأييد حيث ما كنت، وهو من أبلغ المجاز وأحسنه، إذ الجهة في حقه تعالى محال، وخصَّ اتجاه دون غيره من الجهات الست؛ لأنَّ الإنسان مُسافر إلى الآخرة، والمسافر إنما يطْلبُ تُجَاهه لا غير، وكان المعنى: تجده حيث ما توجهت.
"رُفعَتِ الأَقْلامُ، وَجَفتِ الصُّحُفُ". قال الفاكهاني: "معناه أنَّ ذلك أمر ثابت لا يبدل، ولا ينسخ، ولا يغير عمَّا هو عليه".','2','605',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1256,'٦٨٩ -[٢٥١٧] "اعقلها وتوكَّلْ" قال ابن الخازن: قال أهل','2','605',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1257,'التأويل: أراد طمأنينة النفس في حالة الشدة والرخاء.','2','606',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1258,'٦٩٠ -[٢٥١٨] "دَعْ مَا يَريبك إلى ما لا يريبك" قال في النِّهاية:
"يروى بفتح [الياء] وضمها؛ أي دع ما تشك فيه إلى ما لا تشك فيه".','2','606',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1259,'٦٩١ -[٢٥١٩] "لا يعْدَلُ بالرِّعَةِ" هو الورع، يقال: ورِعَ يَرِعُ وَرِعَةً؛ مثل وثق ثقة.','2','606',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1260,'٦٩٢ -[٢٥٢٠] "وَأمن النَّاسِ بَوَائِقهُ" أي: غوائله وشروره، وأحدها بائقة وهي الداهية.','2','607',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1261,'"<span data-type="title" id=toc-45>أبواب صفة الجنَّة</span>"','2','608',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1262,'٦٩٣ -[٢٥٢٦] "لَو أنَّكم تَكُونُونَ إِذَا خَرَجْتُمْ مِنْ عِنْدِي عَلَى حَالِكُمْ ذلك لَزَارَتْكم الملائكة في بُيُوتِكُمْ"
هذا دليل على إمكان رؤية الملائكة كرامة للأولياء.
"وَلَوْ لَمْ تذْنِبوا لَجَاءَ اللهُ بخَلْقٍ جَدِيدٍ، كَيْ يُذْنِبُوا فَيَغْفِرَ لَهُمْ". قال ابن الخازن: "مراده أنَّ الله قدر الذنوب ليظهر ذل العبودية من النادم، فيقابل بالعفو، فيظهر عز الرَّبوبية".','2','608',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1263,'"وَمِلاطُهَا" بكسر الميم: هو الطين الذي يُجعَل بين سَاقي البناء يملط به الحائط، أي يخلط.
"وَحَصْبَاؤها" هي الحصى الصَّغار.
"يَنْعم لاَ يَيْأَسُ" لا يفتقر ولا يحتاج.','2','609',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1264,'٦٦٩٤ -[٢٥٢٨] "إنَّ في الجنَّة جنتين من فضَّة آنيتهما وما فيهما".
قال الكرماني: في "آنيتهما" مبتدأ، "ومن فضة" خبره، ويحتمل أن تكون آنيتهما فاعل "فضة" كما قال ابن مالك في قولهم: "مررتُ بوادٍ أثْلٍ كلُّهُ" أنَّ "كله" فاعل على "الأثل" أي: جنتين مفضض آنيتهما.','2','609',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1265,'"وَمَا بَيْنَ القَوْمِ وَبَيْنَ أَنْ يَنْظُرُوا إِلَى رَبِّهِم إِلاَّ رِدَاءُ الكِبْرَيَاءِ علَى وجهه في جنَّة عَدْنٍ".
قال النووي: "أي والناظرون في جنة عدن، فهي ظرف للناظر".','2','610',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1266,'٦٩٥ -[٢٥٣٠] "والفِردَوْسُ؛ أعلى الجنَّةِ، وَأَوْسَطُهَا" أي: خيرها.
"وَفَوْقَ ذلك عَرشُ الرَّحمَنِ".
قال ابن القيم في كتابه: "نكت شتى وفوائد حسان": "أنزه الموجودات وأظهرها وأنورها وأشرقها، وأعلاها ذاتًا وقدرًا وأوسعها عرش الرَّحمن جلَّ جلاله، وكلما كان أقرب إلى العرش كان أنور وأزهر وأشرف ما بعد عنه، ولهذا كانت جنة الفردوس أعلى الجنان وأشرفَهَا وأنورَهَا وأجَلَّهَا؛ لقربها من العَرش إذ هو سقفهَا، وكلما بعد عنه كان
أظلم وأضيق.
ولهذا كان أسفلُ سافلين شرَّ الأمكنة وأضيقها وأبعدها من كل','2','610',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1267,'خير".','2','611',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1268,'٦٩٦ -[٢٥٣٧] "مِنَ الأُلُوَّةِ"
قال في النِّهاية: "هو العود الذي يتَبَخَّرُ به، وتفتح همزته وتضم وهي أصلية، وقيل زائدة".','2','611',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1269,'٦٩٧ -[٢٥٣٨] "لو أنَّ ما يقِلُّ ظُفْرٌ" أي ما يرفعه، ويحمله','2','611',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1270,'"بَدَا" بلا همز؛ أي ظهر.
"لَتَزَخْرَفَتْ" أي: تزينت.
"مَا بَيْنَ خَوَافِقِ السَّمَوَاتِ" قال في النِّهاية: "في الجهات التي يخرج منها الرِّياح الأربع"','2','612',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1271,'٦٩٨ -[٢٥٤١] "فِي ظِلِّ الفَنَنِ" بفتح الفاء ونونين؛ وهو غصن الشجرة.','2','612',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1272,'٦٩٩ -[٢٥٤٨] "لَيُضْغَطُونَ عَلَيْهِ" أي: يزدحمون يقال: ضغطه، يضغطه، ضغطًا إذا عصره.','2','612',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1273,'٧٠٠ -[٢٥٤٩] "إلاَّ حاضَرَهُ اللهُ مُحَاضَرَةً".
قال التوربشتي: "الكلمتان بالحاء المُهملة والضاد المعجمة، والمراد من ذلك كشف الحجاب، والمقاولة مع العبد من غير حجاب ولا ترجمان".
"حَتَّى يَتَخيَّلَ علَيْهِ" أي: يظهر عليه لباس أحسن من لباس','2','613',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1274,'صاحبه.','2','614',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1275,'٧٠١ -[٢٥٥٠] "إِنَّ في الجنَّةِ لسُوقًا مَا فِيْهَا شِرَاءٌ وَلاَ بَيْعٌ، إِلاَّ الصُّورَ مِنَ الرِّجَالِ والنِّسَاءِ، فإِذَا اشْتَهَى الرَّجُلُ صُورَةً دَخَل فِيهَا".
قال الطيبي: "قيل يحتمل معنيين أحدهما: أن يكون معناه عرض الصورة المستحسنة عليه، فإذا تمنى صورة من تلك الصور المعروضة عليه، صوَّره الله تعالى بشكل تلك الصورة بقدرته.
والثاني: أنَّ المراد من الصُورة الزينة التي يتزيَّن الشخص بها في تلك السوق، ويتلبس بها ويختار لنفسه من الحلي والحلل والتاج، يقال لفلان صورة حسنة، أي: شارَة حسنة وهيئات مليحة.
وعلى كلا المعنيين، التغيير في الصفة لا في الذات؛ والمراد بالسوق المجمع، والاستثناء منقطع".
وقال الحافظ ابن حجر في القول المسدد: "هذا الحديث أورده ابن الجوزي في الموضوعات، وقال: هذا حديث لا يصح، والمتَّهم به عبد الرَّحمن بن إسحاق، وهو أبو شيبة الواسطي.','2','614',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1276,'قال أحمد: ليس بشيء، منكر الحديث.
وقال [يحيى] : متروك.
وقد أخرجه الترمذي من طريقه، وقال: غريب؛ وحسَّن له غيْرَهُ
مع قوله: إنَّه تُكُلِّمَ فيه من قِبل حفظه.
وصحح لهُ الحاكم حديثًا غير هذا.
وأخرج له ابن خزيمة في الصِّيام من صحيحه آخر، لكن قال: في القلب من عبد الرَّحمن.
وله شاهد أخرجه الطبراني في الأوسط من حديث جابر مرفوعًا:
"إنَّ في الجنَة لَسُوقًا ما يُباعُ فِيها وَلاَ يُشْتَرَى إلاَّ الصُّور، فمن أحب صُورة من رجل وامرآة دخل فيها". وفي إسناده جابر بن يزيد الجعفي،','2','615',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1277,'وهو ضعيف. والمستغرب منه قوله: "دخل فيها" والذي يظهر لي أنَّ المراد به أنَّ صورته تتغيَّر، فتصير شبيهة بتلك الصورة، لا أنه دخل فيها حقيقة. أو المراد بالصورَة الشكل والهيئة والبزة" انتهى.','2','616',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1278,'٧٠٢ -[٢٥٥٧] "أُتِيَ بالمَوْتِ ملَبَّبًا" يقال: لُبِّبَ الرَّجل، إذا','2','616',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1279,'جعلت في عنقه ثوبًا أو غيره وجررته به.','2','617',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1280,'٧٠٣ -[٢٥٥٨] "إِذَا كَانَ يَوم القيامة أُتِي بِالمَوْتِ كَالكَبْشِ الأمْلح".
قال الشيخ عز الدِّين بن عبد السلام: "فيه سؤال، وهو أنَّ الموت عرض والعرض كيف يكون كبشًا؟ وكيف يُذْبَحُ مع أنَّه لا يبقى زمانين؟
قال: والجواب: أنَّ الله خلق كبشًا وسماه باسم الموت، لا أنه نفس العرض، وخلق فرسًا وسماه الحياة، فلا ينظر أحدٌ هذا الكبش إلاَّ مات.
ولاَ يَأْتِي عزرائيل [إلى] أحدٍ إلاَّ به، فساعَة وقوع بصره عليه تزهق رُوحه.
وكذلك الفرس لا يَحِلُّ في شيء إلاَّ حَيِي، وهو الفرس الذي كان تحت جبريل يوم غرِق فرعون، وأخذ السامري من تراب حافره شيئًا فألقى به في العِجل الذهبي فَحَيِيَ.','2','617',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1281,'ويلزم هذا القائل أن لا يدعو بالمغفرة والرَّحمة، لأنها لأصحاب الذنوب، وكل هذا ما عرف من عادة السلف والخلف" انتهى.','2','618',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1282,'٧٠٤ -[٢٤٤٠] "إِنَّ مِنْ أُمَّتي من يشفع للفِئَام" هو بالهمز: الجماعة الكثيرة.','2','618',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1283,'"<span data-type="title" id=toc-46>أَبْوَابُ صِفَة جَهَنّمَ</span>"','2','619',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1284,'٧٠٥ -[٢٥٧٨] "وَفَخذُهُ مِثْلُ البَيْضَاءِ" قال في النِّهاية: "قِيل هو اسمُ جَبَل".','2','619',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1285,'٧٠٦ -[٢٥٨١] "سَقَطَتْ فروَةُ وَجْهِهِ" قال في النِّهاية: "أي جلدته، استعارها من الرأس للوجه".','2','619',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1286,'٧٠٧ -[٢٥٨٢] "فَيَسْلَتُ مَا فِي جَوْفِهِ" أي يقطعه ويستأصِله.','2','619',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1287,'٧٠٨ -[٢٥٨٣] "وَوَقَعَتْ فروَة رَأْسِهِ" قال في النِّهاية: "الأصل','2','619',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1288,'المهملة.','2','621',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1289,'٧١٠ -[٢٥٩٦] "فَلَقَدْ رَأيتُ رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - يضْحَكُ حَتَّى بَدَتْ نَوَاجِذُهُ" بالذال المعجمة.
قال في النِّهاية: " وهي -من الأسنان- الأنياب، أو التي تلي الأنياب، وآخر الأضراس أو أقصاها، والمراد الأول؛ لأنه ما كان لا يبلغ به الضحك حتى يبدُو آخر أضراسه، كيف وقد جاء في صِفة ضحكه التبسُّم؟ وإن أريد بها الأواخر، فالوجه فيه أن يراد مبالغته مثله في ضحكه، من غير أن يراد ظهور نواجذه في الضحك، وهو أقيس القولين؛ لاشتهار النواجذ بآخر الأسنان".','2','621',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1290,'٧١١ -[٢٥٩٧] "حُممًا".','2','621',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1291,'جِمع حُمة وهي الفحمة.
"فيَنبُتُونَ كَمَا يَنْبُتُ الغُثَاءُ فِي حُمَالَةِ السَّيْلِ".
قال في النِّهاية: ["بضم الغين المعجمة ومثلثة، ومد؛ يريد] ما احتمله السيل من البزُورات فإنها إذا استقرت على شط مجرى السيل تنبت في يوم وليلة، فشبه بها سُرعة عود أبدانهم وأجسادهم إليهم بعد إحراق النَّار لها".','2','622',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1292,'٧١٢ -[٢٦٠١] "مَا رَأَيْتُ مِثْلَ النَّارِ نَامَ هَارِبُهَا، ولاَ مِثْلَ الجَنَّةِ نَامَ طَالِبُهَا".','2','622',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1293,'قال ابن الخازن: "إسناد هذا الحديث واه، و [هو] لا يصح عن رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، وهو محفوظ من كلام عامر بن عبد قيس.
ومقصود الحديث التعجب من مؤمن بالدارين وهو لا يعمل بمقتضى علمه.
إنما نعرفه من حديث يحيى بن عبيد الله، ويحيى بن عبيد الله ضعيف عند أهل الحديث، تكلم فيه شعبة".
قلتُ: أخرجه البيهقي في شعب الإيمان من هذا الطريق، ثم أخرجه من طريق عبد الرَّحمن بن شَرِيْك عن أبيه عن محمَّد الأنصاري، والسُّدِّي عن أبيه عن أبي هريرة مرفوعًا به، فهذه','2','623',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1294,'متابعة ليحيى، ثم قال البيهقي: "وروي ذلك أيضًا عن عاصم عن زِر عن عبد الله بن مسعود مرفوعًا وروي عنه موقوفًا" انتهى.','2','624',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1295,'٧١٣ -[٢٦٠٤] "إِنَّ أَهوَن أَهْلِ النَّارِ عَذَابًا رجُلٌ فِي أَخْمَص قَدَمَيْهِ جَمْرَتَانِ" قيل: هو أبو طالب.','2','624',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1296,'٧١٤ -[٢٦٠٥] "كُلُّ ضعيفٍ، مُتَضَعِّفٍ" قال في النِّهاية:','2','624',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1297,'"يريد الذي يتضعَّفه النَّاس ويتجبرون عليه في الدُّنيا؛ للفقْر ورثاثة الحال".
"كُلُّ عُتُلٍّ" هو الشديد الجافي، والفظُّ الغليظُ من النَّاس.
"جَوَّاظٍ" هو الجموع المنوع، وقيل: الكثير اللحم المختال في مشيه، وقيل: القصير البطين.','2','625',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1298,'"<span data-type="title" id=toc-47>أبواب الإيمان</span>"','2','626',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1299,'٧١٥ -[٢٦١٠] "وَيتَقفَّرونَ العِلْمَ" قال في النِّهاية: "جاء في','2','626',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1300,'رواية بتقديم الفاء على القاف والمشهور بالعكس؛ قال بعض المتأخرين هي عندي أصح الروايات، وأليقها بالمعنى، يعني أنهم يستخرجون غامضه ويفتحون مغلقه، وأصله من فَقَرْتُ البئر إذا حفرتها لاستخراج مَائها، فلما كانت القدرية بهذه الصِّفة من البحث والتَّتَبُّع لاستخراج المعاني الغامضة بدقائق التأويلات وصفهم بذلك"، ومعنى الرواية المشهورة يطلبون العلم.
"وَأنَّ الأمرَ أُنُفٌ" بضم الهمزة والنون؛ أي يستأنف استينافًا من غير أن يسبق به سابق قضاء وتقدير.
"أَنْ تَلِدَ الأمَةُ رَبَّتَهَا".
قال البيضاوي في "شرح المصابيح": "تأنيث ربَّتها وإضافتها: إما لأجل أنه سبب عتقها، أو لأنه ولد ربها، أو مولاها بعد الأب، وذلك إشارة إلى قوة الإسلام؛ لأنَّ كثرة السبْي والتَسَرِّي دليل على استعلاء الدِّين، واستيلاء المسلمين، وهي من الأمَارَات؛ لأنَّ قوته وبلوغ أمرهِ غايته منذرٌ بالتراجع والانحباط المؤذن بأنَّ القيامة','2','627',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1301,'ستقوم".
"العَالَةَ" أي الفقراء جمع عَائِل.
"يَتَطَاوَلُون في البنْيَانِ".
قال الطَّيِّبي: "أي: يتفاخرون على طول بيوتهم ورفعتها، من تطاوَل الرَّجل، إذا تكبَّر".','2','628',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1302,'٧١٦ -[٢٦١٣] "وَمَا رَأَيْتُ مِنْ نَاقِصَاتِ عَقْلٍ".
قال الطيِّبي: ""من ناقصات" صفة لموصُوف محذوف، أي: ما رَأَيْتُ أحدًا، "ومن" مزيدة استغراقية؛ لمجيئها بعد النفي.
والعقل غرِيزة في الإنسان يدرك بها المعنى، ويمنعه من القبائح، وهو نور الله في قلب المؤمن".
"أَغْلَبَ لذَوي الألْبَابِ" جمع لب، وهو العقل الخالص من الشوائب، سُمِّي بذلك لكونه خالِصَ ما فِي الإنسان من قُواه، كاللبابِ من الشيء، وقيل: هو ما زكَى من العقل، وكل لب عقل، وليس كل','2','628',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1303,'عقلٍ لُبًّا.
"مِنْكُنَّ" قال [الطيبي] : "من" فيه متعلِّقٌ "بأغلب" والمفضل عليه مفروض مُقدَّر، ويحتمل أن يكون "من" بيان "ناقصَات" على سَبيل التجريد، كقولك: رأيتُ منك أسدًا جرد منهن ناقصَات".','2','629',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1304,'٧١٧ -[٢٦١٤] "الإيمانُ بِضْعٌ وسَبْعُونَ بَابًا" قال البيضاوي','2','629',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1305,'في شرح المَصابِيح: "يحتمل أنَّ المراد به التكثير دون التعدِيدِ، كما في قوله تعالى: {إِنْ تَسْتَغْفِرْ لَهُمْ سَبْعِينَ مَرَّةً} واستعمال لفظة السبعة والسبعين للتكثير.
ويحتمل أن يكون المراد تَعْدَادَ الخصال وحصرها، فيقال: إنَّ شعب الإيمان وإن كانت متعددة، إلاَّ أنَّ حاصلها يرجع إلى أصلِ واحدٍ، وهو تكميل النَّفس على وجه يُصلح معاشَه ويحسن معادَه، وذلك أن يعتقد الحق، ويستقيم في العمل، وإليه أشار - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - حيثُ قال: -لِسفيان حين سأله في الإسلام قولاً جامعًا-: "قُلْ آمنتُ بِالله ثمَّ اسْتَقِم" وفنون اعتقاد الحق ستة عشر:
طلب العلم، ومعرفة الصَّانع، وتنزيهه عن النقائص وما يتداعى إليها، والإيمان بصفات الإكرام، مثل: الحياة، والعلم، والقدرة.
والإقرار بالوحدانية، والاعتراف بأنَّ ما عداهُ صِفَة لا يوجد ولا','2','630',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1306,'يعدم إلاَّ بقضائه وقدره، والإيمان بملائكته المطهرة عن الرجس، وتصديق رسُله المؤيدين بالآيات في دعوى النبوَّة، وحسن الاعتقاد فيهم، والعلم بحدوث العالم، واعتقاد فنائه على ما ورد به التنزيل.
والجزم بالنشأة الثانية، وإعادة الأرواح إلى الأجساد، والإقرار باليوم الآخر، أعني: بما فيه من الصراط والحساب، وموازنة الأعمال، وسائر ما تواتر عن الرسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، والوثوق على وعد الجنة وثوابها.
واليقين بوعيد النَّار وعقابها.
وفن العلم ينقسم إلى ثلاثة أقسام:
أحدها: يتعلق بالمرء نفسه.
وهو ينقسم إلى قسمين:
أحدهما: ما يتعلق بالباطن؛ وحاصِله تزكية النفس عن الرذائل، وأمهاتها عشرة: شره الطعام وشره الكلام وحب الجاه وحب المال، وحب الدنيا، والحقد، والحسد والرِياء والعجب.
وتحلية النفس بالكمالات؛ وأمهاتها ثلاث عشرة:
التوبة، والخوف، والرجاء، والزهد، والحياء، والشكر، والوفاء، والصبر، والإخلاص، والصدق، والمحبة، والتوكل، والرضى بالقضاء.','2','631',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1307,'وثانيهما: [يتعلق] بالظَّاهر، ويسمى بالعبادات، وشعبها ثلاث عشرة:
طهارة البدن من الحدث والخبث، وإقامة الصلاة، وإيتاء الزكاة، والقيام بأمر الجنائز، وصيام رمضان، والاعتكاف، وقراءة القرآن، وحج البيت، والعمرة، وذبح الضحايا، والوفاء بالنذور، وتعظيم الأيمان، وأداء الكفارات.
وثانيها: ما يتعلق به وبخواصه وأهل منزله، وشعبها ثمان:
التعفف عن الزنا، والنكاح والقيام بحقوقه، والبر بالوالدين، وصلة الرحم، وطاعة السادة، والإحسان إلى المماليك، والعِتق.
وثالثها: ما يعم النَّاس وينوط به إصلاح العباد، وشعبها سبع عشرة:
القيام بإمارة المسلمين، واتباع الجماعة، ومطاوعة أولي الأمر، ْومعاونتهم على البر، وإحياء معالم الدِّين ونشرها، والأمر بالمعروف والنهي عن المنكر، وحفظ الدِّين بالزجر عن الكفر، ومجاهدة الكفار، والمرابطة في سبيل الله، وحفظ النَّفس بالكف عن الجِنايات، وإقامة حقوقها من القِصَاصِ والديات، وحفظ أموال النَّاس بطلب الحلال، وأداء الحقوق، والتجافي عن المظالم، وحفظ الأنساب، وأعراض النَّاس بإقامة حدود الزنا والقذف، وصيانة العقل بالمنع عن تناول','2','632',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1308,'المسكرات، والمجنِّنات بالتهديد، والتأديب عليه، ودفع الضرر عن المسلمين، ومن هذا القبيل، إماطة الأذى عن الطريق.
وقال الراغب: "هذا حديث من تأمَّله وعرف حقيقته علم أنَّ الإيمان بالواجب هو اثنان وسبعون [درجة] لا يصح أكثر منها ولا أقل، ولا يوجد من الإيمان ما هو خارج عنها بوجه".
"فأدناها". قال الطيبي: "أي: أقربها منزلة، وأدونها مقدارًا من الدنو، بمعنى القريب، يقال: فلان دانى القدر، وقريب المنزلة، كما يعبر بالبعدي عن ذلك فيقال: فلان بعيد الهمة، وبعيد المنزلة بمعنى العالي، وكذلك استعمله في مقالة الأعلى قال: والفاء فيه جزاء شرط محذوف، كأنه قيل: إذا كان الإيمان ذا شُعَبٍ يلزم التعدُّد وحصُولُ الفاضل والمفضول، بخلاف إذا كان أمرًا واحدًا".
"إِمَاطَةُ الأذى عن الطَّريق" يقال: أماط الشيء عن الشيء إذا أزاله','2','633',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1309,'عنه وأذهبه، والأذى هنا اسم ما يُؤذي النَّاس نحو الشوك، والحجر.','2','634',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1310,'٧١٨ -[٢٦١٥] "الحياءُ من الإِيْمَانِ" هو تغيير وانكسار يعتري المؤمن من خوف ما يلام به، قيل: هو مأخوذ من الحياة، فكأنَّ الحَيِيَّ صارَ لِمَا يَعتريه منكسر القُوى، ولذلك قيل: مات حياءً، وَجَمَدَ في مكانه خَجَلاً".','2','634',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1311,'٧١٩ -[٢٦١٦] "أَخْبِرنِي بِعَمَلٍ يُدْخِلْنِي الجنَّةَ وَيُبَاعِدُنِي مِنَ النَّارِ".','2','634',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1312,'قال التوربشتي: "الجزم فيهما على جواب الأمر غير مستقيم رواية ومعنى".
قال الطيبي: "أما الرواية فغير معلومة، وأما المعنى فاستقامته بما ذكره البيضاوي، قال: وإن صحَّ الجزم فيه، كان جزاءُ الشرط محذوفًا تقديره: أخبرني بعملٍ إن عملته يدخلني الجنَّة، والجملة الشرطيَّة بأسرها صفة يعمل، أو جوابًا للأمر، وتقديره: أنَّ إخبار الرسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لمَّا كان وسيلة إلى عمله، [وعمله] ذريعة إلى دخول الجنة، كان الإخبار سببًا بوجهٍ ما لإدخال العمل إيَّاه الجنة".
"قالَ لَقد سَأَلْتَنِي عنْ عَظِيمٍ، وإنهُ ليَسِيْرٌ علَى مَنْ يَسَّرَهُ اللهُ عَلَيْهِ".
قال المظهري: "أي: سألتني عن شيء عظيم مُشكلٍ متعسِّرِ','2','635',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1313,'الجواب، ولكنه سهلٌ على من يسَّره الله؛ لأنَّ معرفة العمل الذي يدخل الرجل الجنَّة من علم الغيب، وعلم الغيب لا يعلمه أحد إلاَّ الله تعالى، ومن علمه الله".
قال الطيبي: "ذهب إلى أن "عظيم" صفة موصوف محذوف، أي: عن سُؤال عظيم، والأظهر أن يقال: إنَّ الموصوف "أمر" ويعني به العَمل؛ لأنَّ قوله: "تعْبُد الله" إلى آخره، استئناف وقع بيانًا لذلك الأمر العظيم، قال: وعلية يبنى كلام البيضاوي، حيث قال: "وإنه ليَسير" إشارة إلى أنَّ أفعال العباد واقعة بأسباب ومرجحَات يفيض عليهم من عنده.
وذلك إن كان نحوَ طاعة، يسمَّى توفيقًا ولطفًا، وإن كان نحو مَعصيةٍ يسمى خُذْلاَنًا وطبعًا" ثم قال: "ألاَ أدُلُّكَ عَلى أبوابِ الخيرِ"، الصَّومُ جُنَّةٌ، والصَّدقة تُطْفيءُ الخَطِيئةَ كما يُطْفِيءُ المَاءُ النَّارَ، وصلاَةُ الرَّجُل فِي جوفِ اللَّيلِ" ثم تلا: {تَتَجَافَى جُنُوبُهُمْ عَنِ الْمَضَاجِعِ} قال المظهري: "التعريف في "الخير" للجنس، جعل هذه الأشياء أبواب الخير؛ لأنَّ الصوم شديد على النفس، وكذا إخراج المال في','2','636',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1314,'الصدقة، وكذا الصلاة في جوف اللَّيل، فمن اعتادها تسهَّل عليه كل خير، وتأتَّى منه كل خير؛ لأنَّ المشقة في دخول الدار تكون بفتح الباب المغلق.
ويحتمل أن يكون التعريف للعهد الخارجي التقديري؛ وهو ما يُعلم من قوله: "تعبد الله ولا تشرك به شيئًا" إلى آخره، المعْنِيُّ بهِ: الإسلام والإيمان الذي هُو سَبَبٌ لدخول الجنَّة، والمباعدة من النَّار ظاهرًا.
أو المعنيُّ بِأبواب الخير: النوافل، دلَّ عليه قوله: "وَصَلاَةُ الرَّجلِ فِي جَوْفِ اللَّيلِ" لئلاَّ يلزم التكّرار، وسميت النوافل أبواب للفرائض؛ لأنها مقدمات ومكمِّلات لها، فمن فاتته السنن حُرم الفرائض.
قال العلماء: من ترك الأدب عوقب بحرمان النوافل، ومن ترك النوافل عوقب بحرمان السنن، ومن ترك السنن عوقب بحرمان الفرائض، ومن ترك الفرائض يوشِك أن يعاقب بحرمان المعرفة.
وقال الطيبي: "قوله: الصدقة تطفيء الخطيئة" [أصله] "تُذهب" كقوله تعالى: {إِنَّ الْحَسَنَاتِ يُذْهِبْنَ السَّيِّئَاتِ} .
ثم في الدرجة الثانية: "تمحو" الخطيئة لقوله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "وَأتبع السيئة','2','637',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1315,'الحسنةَ تَمْحُهَا" أي السيئة المثبتة في صحيفة الكرام الكاتبين، وإنما قُدرت الصحيفة لقرينة "تمحو".
ثم في الدرجة الثالثة: "تطفيء الخطيئة" لمقام الحكاية عن المباعدة عن النَّار، فلما وضَعَ الخطيئة موضع النَّار على الاستعارة المكنية، أثبت لها -على سبيل الاستعارة التخييلية- ما يلائم النَّار من الإطفاء، لتكون قرينة مانعة لها من إرادة الحقيقة من الخطيئة.
وقال البيضاوي: "قوله: وصلاة الرَّجلِ" مبتدأ، خبره محذوف، أي: كذلك؛ أي: تطفيء الخطيئة، أو هي من أبواب الخير، قال: والأول أظهر، لاستشهاده - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - بالآية، وهي متضمنة للصلاة والإنفاق".
قال الطيبي: ويعضده تقييد القرينتين السابقتين -أعني: الصوم، والصدقة- بفائدتين زائدتين: وهي الجنة وإطفاء الخطيئة؛ لأنَّ الظاهر أن يقال: أبواب الخير: الصوم، والصدقة لا غير، وصلاة الرَّجل في جوف الليل، فلما قُيِّدتا بهِمَا يجب أن يقيَّد هذا بما يناسبها.','2','638',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1316,'والأظهر أن يقدر: الخيرُ شعارُ الصَّالحين، ويفيد فائدة مطلوبة زائدة على القرينتين، وهي أنهما كما أفادتا المباعدة عن النَّار، فتفيد بهذه الإدخال في الجنَّة، ويتمُّ الاستشهاد بالآية؛ لأنَّ قرَّة العين كناية عن السرور والفوز التَّام وهي مباعدة النَّار ودخول الجنة، كما قال تعالى: {فَمَنْ زُحْزِحَ عَنِ النَّارِ وَأُدْخِلَ الْجَنَّةَ فَقَدْ فَازَ} انتهى.
قلتُ: وعندي أن يُعرب الصوم خبرَ مبتدإٍ محذوف أي: هي الصوم، أو مبتدأ خبرُهُ محذوف، أي: منها الصوم، والصدقة، وصلاة الرَّجل كلاهما عُطِفَ عليه.
وقوله: "جُنَّةٌ" خبر مبتدأ مُقدَّر، أي: هو.
وكذا قوله: "تُطْفِيءُ الخطيئةَ" خبر مقدر، أي: هي: "وَذِرْوَةِ سنَامِهِ" بكسر الذَّال المعجمة: أي: أعلى الشيء، والسنام بفتح السين: ْما ارتفع من ظهر الجمل.
"رَأْسُ الأَمْرِ الإِسْلامِ" قال التوربشتي: "أراد بالأمر" هنا: أمرَ الدِّين، "وبالإسلام" كلمتي الشهادة، يعني ما لم يُقِرَّ العبدُ بهما لم يكن له من الدِّين شيءٌ أصلاً، وإذا أقرَّ بهما حصل له أصل الدِّين، إلاَّ أنه ليسَ له قوَّة وكمال، كالبيت الذي ليس له عمود، فإذا صلَّى وداوم على الصلاة قوِي دينُه، ولكنه لم يكن له رِفْعَةٌ وكمال، فإذا جاهد حصَلَ لدينه الرِّفعةُ.','2','639',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1317,'وقال الحليمي: يعني هذا والله أعلم: أنَّ الإسلام هو الذي لا يَصح شيء من الأعمال إلاَّ به، وإذا فات لم يبق معهُ عمل، فهو كالرأس الذي لا يسلم شيء من الأعضاء إلاَّ ببقائه، فإذا فارق الجُملة لم يُنْتَفع بعده بشيء من الأعضاء.
وأما الصلاة فإنها عمود الأمر، والأمر هو الدِّين؛ لأنَّ الإسلام لا ينفع ولا يثبت من غير الصلاة، ولا يُغْني قبولها عن فعلها، لأنَّ الإسلام وحده لا يحقن الدَّم حتى يكون معه إقامة الصلاة، وأما قوله: "ذِرْوَةِ سِنَامِهِ الجِهَادُ" [فقيل] : معناه لا شيء من معالِم الإسلام أشهر ولا أظهر منه، فهو كذِرْوَةِ السَّنام التي لا شيء في البعير أعلا منه، وعليه يقع بصر النَّاظر من بُعد.
"بِمَلاَكِ ذلِكَ" قال التوربشتي: "مِلاك الأمر قوامه، وما يتم به".
ِ وقال البيضاوي: "أصله ومبناه، وأصله ما يملك به كالنظام".
وقال المظهري: "مَا به إحكام الشيء، وتقويته، من: مَلَكَ','2','640',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1318,'العجين: إذا أحسن عجنه، وبالغ فيه، وأهل اللغة يكسرون الميم ويفتحونَهَا، والرواية بكسر الميم".
"فأخذ بِلسَانه" قال الطيبي: "الباء زائدة، والضمير راجع إلى النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -.
"كُف عليك هذا" قال البيضاوي: "أي: كف عليك لسانك، فلا تتكلم بما لا يعنيك، أو لا تتكلم بما يهجس في نفسك من الوسْواس فإنك غير مؤاخذ به ما لم يظهر".
"ثَكِلَتكَ أُمُّكَ" قال الطيبي: "أي: فقدتك، والثُّكْلُ: موت الولد، وفقد الحبيب. وهذا وأمثالُهُ أشياء مُزالة عن أصلها إلى معنى التعجب وتعظيم الأمر".
وقال المظهري: "هذا دُعاء عليه، ولا يراد وقوعه، بل تأديب وتنبيه من الغفلة".
"وَهَلْ يَكُبُّ النَّاسَ" مضارع كبَّهُ، بمعنى: صرعه على وجهه، وهذا من النوادر، فإنَّ ثلاثيَّهُ مُتَعدٍّ، ورُبَاعِيَّه لاَزِم.
"علَى وُجُوهِهمْ، أو منَاخِرِهِم" شك من الراوي.
"إِلاَّ حَصَائِدُ أَلْسِنَتهم" جمع حصيدة، فعيلة بمعنى مفعُولة، مِنْ حصَد: إذا قطع الزرع، وهذا إضافة اسم المفعُول إلى فاعله، أي: محصودات الألسنة، شبَّه ما تكلم به اللِّسان بالزرع المحصود بالمِنْجَل،','2','641',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1319,'فكما أنَّ المِنْجَلَ يقطع ولا يُمَيِّز بين الرطب واليابس، والجيد والرديء، فكذلك لسان بعض النَّاس يتكلم بكل نوع من الكلام القبيح والحسن، ثم حذف المشبَّه وأقام به مقامهُ على سبيل الاستعارة المصرَّحة، وجعَلَ الإضافة قرينة لها والاستثناء مفرغ؛ لأنَّ في الاستفهام معنى النفي، والتقدير: لا يَكُبُّ النَّاس في النَّارِ شيءٌ من الأشياء إلاَّ حصائد ألسنتهم من الكلام القبيح، ذكر ذلك كله الطيبيُّ.
قال في النِّهاية: "وروي، إلاَّ حصا ألسنتهم وهو جمع حصاةِ اللِّسان، وهي: ذَرَابَتَهُ".','2','642',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1320,'٧٢٠ -[٢٦١٧] "إِذَا رَأَيْتُمْ الرَّجُلَ يَتَعَاهَدُ المَسْجِدَ" قال التوربشتي: "هو بمعنى التعهد: وهو التحفظ بالشيء، وتجديد العهد به، وقال روى يتعاهد ويعتاد، والاعتياد، معاودته إلى المسجد مرَّة','2','642',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1321,'بعد أخرى لإقامة الصلاة، وكلاهما حسن.
وقال الطيبي: "يتعاهد: أشمل معنى وأجمع لما يُناطُ به أمرُ المسجد، من العمارة واعتياد الصلاة وغيرهما، ألا ترى كيف استشهد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - بقوله: {إِنَّمَا يَعْمُرُ مَسَاجِدَ اللَّهِ} قال في الكشاف: "العمارة تتناول رمَّ ما يتهدَّم منها، وقمَّها وتنظيفها، وتنويرها بالمصابيح وتعظيمها، واعتيادها للعبادة والذكر".
وقوله: "فاشْهدُوا لهُ بالإيمان" أي: اقطعوا له به، فإنَّ الشهادة قول صدر عن مُواطَأَةِ القلْبِ اللِّسانَ على سبيل القطع.','2','643',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1322,'٧٢١ -[٢٦٢٠] "بَيْنَ العَبْدِ وَبَيْنَ الكُفْرِ تَرْكُ الصَّلاَةِ".','2','643',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1323,'قال الطيبي: "ترك الصلاة: مبتدأ، والظرف خبره، ومتعلَّقُه محذوف، قدم ليفيد [به] الاختصاص، ويؤيده الحديث الثابت، وظاهر الحديث نظم قوله تعالي: {بِرَسُولِهِمْ لِيَأْخُذُوهُ وَجَادَلُوا بِالْبَاطِلِ} ، وقوله: {بَيْنَ الْبَحْرَيْنِ حَاجِزًا أَئِلَهٌ} فإذا ذهب إلى هذا المعنى يوجب خلاف المقصُود، ولذلك قيل فيه وجوه:
أحدها: أنَّ ترك الصلاة معبر عن فعل ضده؛ لأنَّ فعل الصلاة هو الحاجز بين الإيمان والكفر، فإذا ارتفع رفع المانع قال التوربشتي.
الثاني: قال البيضاوي: يحتمل أن يُؤوَّلَ ترك الصلاة بالحد الواقع بينهما، فمن تركها دخل الحد وحام حول الكفر ودنا منه.
الثالث: قال أيضًا: متعلق الظرف محذوف تقديره ترك الصلاة وُصلةٌ بين العبد والكفر، والمعنى: يوصله إليه.
قال الطيبي: وأقوى الوجوه الثاني، ثم هو من باب التغليظ،','2','644',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1324,'أي: المؤمن لا يتركها.
قال: ويمكن أن يقال: إنَّ الكلام منصوب على غير مقتضى الظاهر؛ لأنَّ الظاهر أن يقال؛ بين الإيمان والكفر ترك الصلاة، أو بين المؤمن والكافر تركها، فوضع موضع المؤمن العبد. وموضع الكافر الكفر، فجعله نفسَ الكفر مبالغة".','2','645',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1325,'٧٢٢ -[٢٦٢١] "العَهدُ الذي بيْنَنَا وَبَيْنَهمْ الصَّلاَة".
قال البيضاوي: "الضمير الغائب للمنافقين، شبه الموجِب بإبقائهم وحقن دماءئهم بالعهد المقتضي لإبقاء المعاهد والكف عنه، والمعنى: أنَّ العمدة في إجراء أحكام الإسلام عليهم تشبههم بالمسلمين في حضور صلاتهم وجماعتهم، فإذا تركوا ذلك كانوا هم وسائر الكفار سواء".','2','645',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1326,'٧٢٣ -[٢٦٢٢] "لا يَرَوْن".','2','645',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1327,'قال الطيبي: "من الرأي. "شيئًا" مفعول له. "من الأعمال" نعته، وكذا الجملة، وهي: ترْكُهُ كُفْرٌ.
"غيْرَ الصَّلاَةِ" استثناء والمستثنى منه الضمير الراجع إلى "شيئًا".
ويجوز أن يكون "غَيرَ" صفةً أخرى لِـ: شيئًا.
المعنى ما كانوا معتقدين ترك شيء من الأعمال موجِب للكفر إلاَّ الصلاة.','2','646',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1328,'٧٢٤ -[٢٦٢٣] "ذَاقَ طَعْمَ الإيمانِ"
قال الراغب: الذوق: وجود الطعم في الفم، وأصلُهُ فيما يَقِلُّ تناوله، فإذا كثُر يقال له: الأكل.
واستعمل في التنزيل بمعنى الإصابة، إما في الرَّحمة، وإما في العذاب.
وقال الطيبي: "مجاز قوله: " [ذاق طعم] الإيمان"، كمجاز قوله:','2','646',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1329,'"وجدَ حلاوة الإيمان".
وكذلك موقعه كموقعه، لأنَّ من أحب أحدًا يتحرَّى مراضيه، ويُوثر رضاه على رضى نفسه".','2','647',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1330,'٧٢٥ -[٢٦٢٤] "ثَلاَثٌ مَنْ كُنَّ فِيهِ وَجَدَ بِهِنَّ طَعْمَ الإِيْمَانِ".
قال الطيبي: "ثلاث مبتدأ، والجملة الشرطية خبره، وجاز ذلك؛ لأنَّ التقدير: خصال ثلاث.
ويجوز أن تكون الجملة الشرطيَّة صفة لثلاث، ويكون الخبر "مَنْ كَانَ اللهُ، ورَسُولُهُ أحَبَّ إليه".
وعلى التقديرين لا بد من تقدير مضاف قبل "من كان"؛ لأنه على الأول: إما بدل من "ثلاث" أو بيان، وعلى الثاني: خبر، ولا بد من','2','647',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1331,'إضمار مضاف قبل كان لاستقامة المعنى، تقديره قبل من محبَّة "من كان الله".
"مِمَّا سِواهُمَا".
قال البيضاوي: "فإن قيل: لما ثنى الضمير هنا ورد على الخطيب: "ومن عصاهم فقد غوى" وأمره بالإفراد؟ فالجواب: أنه ثنى هنا إيماءً إلى أنَّ المعتبر هو المجموع المركب من المحبتين، لا كل واحدة، فإنها وحدها لاغية، وأمر بالإفراد هنا كإشعار بأنَّ كل واحد من العصيانيين مستقل باستلزام الغواية، فإنَّ قوله: "ومن عصى الله ورسُوله" من حيث أنَّ العطف في تقدير التكرير، والأصل فيه استقلال كلٍّ من المعطوف والمعطوف عليه في قوة قولنا: ومن عصى الله فقد غوى، ومن عصى الرسول فقد غوى".
قال الطيبي: " هذا كلام حسن متين، ويؤيده قوله تعالى: {أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ وَأُولِي الْأَمْرِ مِنْكُمْ} لم يُعِدْ {أَطِيعُوا} في أولى الأمر، كما','2','648',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1332,'أعاده في {وَأَطِيعُوا الرَّسُولَ} ليؤذن بأنه لاستقلال لهم في الطاعة استقلال الرسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -.','2','649',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1333,'٧٢٦ -[٢٦٢٧] "المُسْلِم مَنْ سَلِمَ المُسْلِمونَ مِنْ لِسَانِهِ وَيَدِهِ".
قال الراغب: "كل اسم نوع فإنه يستعمل على وجهين:
أحدهما: دلالته على المسمى وفصلا بينه وبين غيره.
والثاني: لوجود المعنى المختص به، وذلك هو الذي يمدح به، وذلك أنَّ كل ما أوجده الله في هذا العالم جعله صالحًا لفعل خاص، ولا يصلح لذلك العمل سواه، كالفرس للعَدْوِ الشديد، والبعير لقطع الفلاة البعيدة، والإنسان ليعلم ويعمل.
وكل شيء لم يوجد كاملاً لِمَا خُلِقَ له، لم يسْتَحِق اسمه مُطلقًا، بل قد يُنْفَى عنه، كقولهم: فلان ليس بإنسان، أي: لا يوجد فيه المعنى الذي خُلِقَ لأجله من العِلم والعَمل، فعلى هذا إذا وجدت مسلمًا يؤذي المسلمين بلسانه ويده، وقلت له: لستَ بِمُسلِمٍ، عَنَيْتَ أنَّك لستَ بِكامل فيما تحليت به من حلية الإسلام".
"وَالمُؤْمِنُ مَنْ أَمِنَهُ النَّاسُ علَى دِمَائِهِمْ وَأَمْوَالِهِمْ".','2','649',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1334,'زاد الحاكم والبيهقي من حدِيث فُضالة بن عبيد: "والمجاهد من جاهد نفسه في طاعة الله ورسوله، والمهاجر من هجر الخطايا والذنوب".
قال الطيبي: "في ترتب "من سَلم" على "المسلم" و"من آمنه" على "المؤمن" رعاية للمطابقة لغة".','2','650',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1335,'٧٢٧ -[٢٦٢٩] "إِنَّ الإسلام بَدأ غريبًا وَسَيَعُودُ كَمَا بدأَ".
قال النووي: "بدأ بالهمز، من الابتداء، كذا ضبطناه".','2','650',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1336,'٧٢٨ -[٢٦٣٠] "إِنَّ الدِّينَ لَيأرِزُ إِلَى الحِجَازِ" أي يضم إليه','2','650',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1337,'ويجتمع بعضه إلى بعض فيه.
"وَلَيعْقِلَنَّ الدِّينُ من الحِجَازِ مَعْقلَ الأرْوِيَّةِ من رَأسِ الجَبَلِ"
قال في النِّهاية: "أي ليتحصن ويعتصم ويلتجيء كما يلتجيء الوعل إلى رأس الجبَل".
"والأروية" بضم الهمزة، وسكون الراء وكسر الواو، وتشديد المثناة التحتية؛ الأنثى من الوعول.
وقال الطيبي: "معقل؛ مصدر، بمعنى العقل، ويجوز أن يكون اسم مكان".','2','651',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1338,'٧٢٩ -[٢٦٣١] "آيَةُ المُنَافقِ" أي: علامتهُ ثلاث، زاد في رواية','2','651',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1339,'الصحيحين، "وإن صلَّى، وصام، وزعم أنه مسلم".','2','652',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1340,'٧٣٠ -[٢٦٣٢] "أَرْبَعٌ مَنْ كُنَّ فِيهِ كَانَ مُنَافِقًا".
قال البيضاوي: "يحتمل أن يكون مختصًّا بأبناء زمانه؛ فإنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - علِمَ بنُورِ الوحي بواطن أحواله، وميَّز بين من آمن به صدقًا ومن أذعن له نفاقًا، وأراد تعريف أصحابه بأحوالهم ليكونوا على حذر منهم، ولم يصرح بأسمائهم؛ لأنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - علِمَ أنَّ منهم سيتوب، فلم يفضحهم بين النَّاس؛ ولأنَّ عدم التعيين أوقع في النصيحة، وأجلب للدعوة إلى الإيمان، وأبعد عن النفور والمخاصمة.
ويحتمل أن يكون عامًا لينزجر الكل عن هذه الخصال على آكد وجه؛ إيذانًا بأنها طلائع النفاق الذي هو أسمح القبائح.','2','652',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1341,'ويحتمل أن يكون المراد بالمنافق العرفي، وهو من يخالف سِرُّه علنه مطلقًا، ويشهد له قوله: "ومن كانتْ فيه خَصْلَة مِنْهُنَّ كَانَتْ فِيهِ خَصْلَةٌ منَ النفاقِ حتَّى يَدَعها".
وكذا قوله: "كَانَ منافقًا خالصًا"؛ لأنَّ الخصال التي بها المخالفة بين السر والعلن لا تزيد على هذا، فإذا نقصت منها خصلة نقص الكمال" انتهى.','2','653',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1342,'٧٣١ -[٢٦٣٩] "سِجِلاً" بالكسر والتشديد، الكتاب الكبير.
"بِطاقةٌ" قال في النِّهاية: "هي رقعة صغيرة يثبت فيها مقدار ما يجعل فيه إن كان عينًا فوزنه أو عدده، وإن كان متاعًا فثمنه.
قيل: سميت بذلك لأنها تشد بطاقة من الثوب، فتكون الباء حينئذٍ زائدة، وهي كلمة كثيرة الاستعمال بمصْر، ويُروى بالنون، وهو','2','653',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1343,'غريب".
"فِيها أَشْهدُ أنْ لاَ إلَهَ إلاَّ الله وأنَّ محمَّدًا عبده ورَسُولهُ" قال القرطبي في التذكرة: "ليست هذه شهادة التوحيد؛ لأنَّ من شأن الميزان أن يوضع في كفته بشيء [وفى] الأخرى ضده، فتوضع الحسنات في كفة والسيئات في كفة، فهذا غير مستحيل؛ لأنَّ العبد قد يأتي بهما جميعًا، ويستحيل أن يأتي بالكفر والإيمان جميعًا عبد واحد حتى يوضع الإيمان في كفة والكفر في كفة؛ فلذلك استحال أن توضع شهادة التوحيد في الميزان، وأما بعد ما آمن العبد فالنطق منه بـ "لا إله إلاَّ الله" حسنة توضع في الميزان مع سائر الحسنات". قاله الترمذي الحكيم، في نوادر الأصول.
وقال غيره: "إنَّ النطق منه بها زيادة ذكر على حسنٍ منهُ، ويكون طاعة مقبُولة، قالها على خلْوَة وخُفيةٍ من المخلوقين.
فيكون له عند الله تعالى يردها إليه في ذلك اليوم، فيعْظُم قدرها ويَجِلُّ مَوْضِعُهَا، وترجح بخطاياه وإن كثرت، وبذنوبه وإن عظمت، ولله الفضل على عباده، ويتفضل بما شاء على من شاء.
قال القرطبي: "ويدل على هذا قوله في الحديث: فيقول: "بلى إنَّ لك عندَنَا حَسَنَةً" ولم يقُلْ إنَّ لك عندنا إيمانًا، وقد سُئِل - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - عن لا إله إلاَّ الله أمن الحسنات هي؟ قال: هي أعظم الحسنات.','2','654',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1344,'ويجوز أن تكون هذه الكلمة هي آخر كلامه في الدنيا، كما في حديث: "من كان آخر كلامه لا إله إلاَّ الله وجبت له الجنة".
وقيل: يجوز حمل هذه الشهادة على الشهادة التي هي الإيمان، ويكون في كل مؤمن، وكل مؤمن ترجح حسناته، ويوزن إيمانه كما توزن حسناته، وإيمانه يرجح بسيئاته كما في هذا الحديث، ويدخل النَّار بعد ذلك فيطهره من ذنوبه، ويدخله الجنَّة بعد ذلك، وهذا مذهب قوم يقولون: "إنَّ كل مؤمن يعطى كتابه بيمينه، وكل مؤمن يثقل ميزانه ويتأولون قول الله تعالى: {فَمَنْ ثَقُلَتْ مَوَازِينُهُ فَأُولَئِكَ هُمُ الْمُفْلِحُونَ} أي الناجون من الخُلود، وقوله: {فَهُوَ فِي عِيشَةٍ رَاضِيَةٍ} . أي: يومًا مَّا، وقوله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "من كان آخر كلامه لا إله إلاَّ الله دخل الجنة" أي أنه صائر إليها لا محالة أصابه قبل ذلك ما أصابه.
قال القرطبي: وهذا تأويل فيه نظر يحتاج إلى دليل من خارج يَنُصُّ عليه.
والذي يدل عليه الآيُ والأخبار: أنَّ من ثقلت موازينه فقد نجا وسلم وبالجنة أيقن، وعلم أنه لا يدخل النَّار بعد ذلك والله أعلم.','2','655',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1345,'٧٣٢ -[٢٦٤١] "لَيَأتِيَنَّ عَلَى أُمَّتِي مَا أَتَى عَلَى بَنِي إِسْرَائِيْلَ".','2','655',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1346,'قال القرطبي: "الإتيان: مجيء بسهُولة، وعُدِي بعلى لمعنى الغلبة المؤدية إلى الهلاك، والمراد بالأمة: من تجمعهم دائرة الدعوة مِن أهل القبلة؛ لأنه أضافهم إلى نفسه، وأكثر ما ورد في الحديث على هذا الأسلوب، فإنَّ المراد منه أهل القبلة، ولو ذهب إلى أنَّ المراد أُمَّةُ الدعوة فله وَجه، وحينئذِ يتناول أصناف أهل الكفر".
"حَذوَ النَّعْلِ بِالنَّعْلِ". قال في النِّهاية: "أي: يعملون مثل أعمالهم، كما يقطع إحدى النعلين على قدر الأخرى، والحذو: التقدير والقطع".
وقال المظهري: "الحذو: جعل الشيء مثل شيء آخر، وهو منصوب على المصدر، أي أفعال بعض أمتي في القبح مثل: أفعال بني إسرائيل".
"حَتَّى إن كان منهم" مكسُورةٌ شرطية.
ْ "مَن أتى أمَّه علانيةً" قال الطيبي: لعل المراد زوجة الأب، والتقييد بالعلانية لبيان وقاحته وصفاقة وجهه".
"لكان في أمَّتِي من يَصْنَع ذلك" اللام فيه جواب "إن" على تأويل','2','656',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1347,'"لو" كأنَّ "لَوْ" تأتي بمعنى "إن".','2','657',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1348,'٧٣٣ -[٢٦٤٢] "إِنَّ الله تعالى خلق خَلْقهُ في ظُلمَةٍ فَأَلْقَى علَيْهِمْ نُوره، فَمَنْ أَصَابَهُ مِنْ ذلكَ النُّورِ اهْتَدَى، وَمَنْ أَخْطأةُ ضَلَّ".
قال الطيبي: " أي: خلق الثقلين -من الجنِّ والإنس- كائنين في ظلمة النفس الأمَّارة بالسوء، المجبولة بالشَّهوات المردية والأهواء المضلة، والنور الملقى عليهم ما نصب من الشواهد والحجج، وما أنزل عليهم من الآيات والنذر، فمن شاهد آياته هو الذي أصابه ذلك النور، فيخلص من تلك الظلمة واهتدى، ومن لم يشاهد آياته بقي في ظلمات الطبيعة متحيرًا.
ويمكن أن يحمل قوله: "خلقَ خلْقَهُ" على خلق الذرِّ المستخرج من صلب آدم عليه الصلاة والسلام، فعبر بالنُّور عن الألطاف التي','2','657',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1349,'هي تباشير صبح الهداية، وإشراف لمعان برق العناية.
[ثم أشار بقوله: "أصاب وأخطأ" إلى ظهور أثر تلك العناية] في الإنزال من هداية بعضٍ وضلال بعض.
"فلذلكَ" يعني: من أجل عدم تغير ما جرى تقديره من الإيمان والطاعة، والكفر والمعصية.','2','658',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1350,'٧٣٤ -[٢٦٤٣] "أَتَدْرِي مَا حَقُّ اللهِ على العِبادِ" هو بمعنى الواجب واللازم، فتدري ما حقهم على الله.
قال النووي: "هي على جهة المقابلة والمشاكلة لحقه عليهم".','2','658',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1351,'"<span data-type="title" id=toc-48>أبواب العلم</span>"','2','659',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1352,'٧٣٥ -[٢٦٤٧] "من خرج في طلب العلم، فهو في سبيل الله حتى يرجع"
قال المظهري: "وجه مشابهة طلب العلم بالمجاهدة في سبيل الله أنه إحياء الدِّين، وإذلال الشيطان، وإتعاب النَّفس وكسر الهوى واللَّذة".','2','659',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1353,'٧٣٦ -[٢٦٥٠] "إنَّ النَّاس لكمْ تَبَعٌ".
قال الطيبي: "أي: تابعون، فوضع المصدر موضعه مبالة نحو','2','659',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1354,'رجل عدل، وقال المظهري: " "لكم" خطابٌ للصحابة ".
"وإنَّ رِجالاً يأتُونَكُمْ" عطف على "إنَّ النَاس".
"من أقطار الأرض" أي: جوانبها.
"يتفقَّهون في الدِّين" جملة استئنافية لبيان علة الإتيان، أو حال من الضمير المرفوع في يأتونكم، وهو أقرب إلى الذوق.
"فاسْتَوْصوا بهِمْ خَيرًا" الاستيصاء قبُول الوصِيَّة.','2','660',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1355,'٧٣٧ -[٢٦٥٤] "من طلب العِلم ليجاري به العُلَماء".
قال في النِّهاية: "أي يجري معهم في المناظرة والجدال ليظهر [علمه] إلى النَّاسِ رياءً وسُمعةً".
"أَوْ لِيجَارِي بِهِ السُّفَهَاءَ" أي يحاجهم، ويجادلهم.
"أَو يصرفَ بهِ وجُوهَ النَّاسِ إِلَيْهِ".
قال المظهري: "أي يطلب العلم على نية تحصيل المال، والجاه، وصرف وجوه العوام إليه، وجعلهم إياه معقب القدم.','2','660',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1356,'٧٣٨ -[٢٦٥٦] "نَضَّرَ الله امْرَأً"','2','660',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1357,'قال التوربشتي: "النضرة: الحسن، والرونق، يتعدَّى، ولا يتعدى، وروي بالتخفيف والتشديد، والمعنى خصَّه الله بالبهجة والسرور لما رزق بعلمه ومعرفته من القدر، والمنزلة بين النَّاس في الدنيا، ونعمه في الآخرة، حتى يرى عليه الرخاء ورفيف النعمة، وإنما خصَّ حافظ سُنَّته ومبلِّغها بهذا الدعاء؛ لأنه سعى في نضارة العِلم وتجديد السنة، فجازاه في دعائه له بما يناسب حاله في المعاملة".
"فرُبَّ حامِلِ فقهٍ إِلَى من هو أفقَهُ مِنْهُ".
قال التوربشتي: "رب وضعت للتقليل، فاستعيرت في الحديث للتكثير".','2','661',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1358,'٧٣٩ -[٢٦٥٨] "ثَلاث لاَ يُغَلُّ عليهنَّ قَلبُ مُسْلِمٍ" الحديث.','2','661',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1359,'قال في النِّهاية: "يروى يُغلُّ؛ بضم الياء من الإغلال، وهو الخيانة في كل شيء وبفتحها من الغل، وهو الحقد، والشحناء؛ أي لا يدخله حقد يزيله عن الحق، وروى "يغل" بتخفيف اللام من الوغول في الشيء؛ والمعنى أنَّ هذه الخلال الثلاث يستصلح بها القُلوب، فمن تمسك بها طهر قلبه من الخيانة والدغل والشر.
"وعليْهِنَّ" في موضع الحال، تقديره لا يغل كائنًا عليهنَّ" انتهى.
وقال البيضاوي: "هذه الجملة استئنافية تأكيد لما قبله، فإنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لما حرَّض على تعلم السنن ونشرها قفاه برد ما عسى أن يعرض مانعًا وهو الغل من ثلاثة أوجه:
أحدها: أنَّ تعلم الشرائع، ونقلها ينبغي أن يكون خالصًا لوجه الله مبرأ عن شوائب المطامع والأغراض الدنيويَّة، وما كان كذلك لا يتأثر عن الحِقد، والحسَد.
وثانيها: أنَّ أداء السنن إلى المسلمين نصيحة لهم، وهي من وظائف الأنبياء، فمن تعرض لذلك وقام به كان خليفة لمن يبلغ عنه، وكما لا يليق بالأنبياء أن يهملوا أعداءهم ولا ينصحوهم لا يحسن من','2','662',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1360,'حامل الأخبار وناقل السنن أن يمنحها صديقه، ويمنع عدوه.
وثالثها: أنَّ النقل ونشر الأحاديث إنما يكون غالبًا بين الجماعات، فحث على لزومها ومنع عن النأي عنها لحقد، وضغينة، تكون بينه وبين حاضر بها، ببيان ما فيها من الفائدة العظمى، وهي إحاطة دعائهم بهم من ورائهم فتحرسهم عن مكائد الشيطان وتسويله". انتهى.
"فإنَّ دعوتهم تُحِيط من ورائهم".
قال في النِّهاية: "أي: تحوطهم وتكنُفُهُمْ، وتحفظهم يريد أهل السنة دون أهل البدعة، والدَّعوة المرَّة الواحدة من الدعاء".
قال الطيبي: "وهذا يرشد إلى أنَّ الصواب فتح مَنْ موصولاً مفعولاً لا تحيط، وقد يجوز أن يكون تقدير الكلام: فعليه أي يلزم الجماعة فإنَّ دعوتهم تحيط من ورائهم".','2','663',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1361,'٧٤٠ -[٢٦٦٣] "لاَ أُلْفِيَنَّ أَحدكُم متَّكئًا على أريكتِهِ"','2','663',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1362,'قال الطيبي: "ألفيت الشيء وجدته وهو كقولهم: لا أرينك هاهنا، نهى رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - نفسه عن أن يراهم على هذه الحالة، والمراد نهيهم عن أن يكونوا على تلك الحالة، فإنهم إذا كانوا عليها وجدهم كذلك فهو من باب إطلاق المسبب على السبب، ومن الكناية الإيمائية، والأريكة، سرير مزين في قبة أو بيت، فإذا لم يكن فيه سرير فهو حَجَلة.
"يأتيهِ أمري" هو بمعنى الشأن وقوله: "مِمَّا أمرتُ بهِ أو نهيتُ عنهُ" بيان للأمر الذي هو الشأن لأنه أعم من الأمر، والنَّهي، وقوله: "فيقول: لا أدري" أي لا أدري غير القرآن، ولا أتبع غيره، وهو مرتب على يأتيه والجملة كما هي حال [أخرى] من المفعول، ويكون النَّهي منصبًا على المجموع. أي: لا ألفين أحدكم وحاله أنه متكّىء ويأتيه الأمر فيقول: لا أدري انتهى.','2','664',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1363,'٧٤١ -[٢٦٦٤] "وإنَّ ما حرَّم رسول الله كما حرَّم الله".
قال الطيبي: "يحتمل أن يكون من كلام الراوي كما ذهبوا إليه،','2','664',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1364,'وأن يكون من كلامه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - على سبيل التجريد تنبيهًا به على أنَّ من اسمه رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - حقيق بأن يستقل بأحكام سوى ما أنزله الله عليه".','2','665',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1365,'٧٤٢ -[٢٦٦٩] "بلِّغُوا عنِّي ولو آية".
قال البيضاوي: قال: ولو آية، ولم يقل ولو حديث، لأنَّ الأمر بتبليغ الحديث يفهم من هذا بطريق الأولوية، فإنَّ الآيات مع انتشارها وكثرة حملتها، وتكفل الله سبحانه بحفظها، وصونها عن الضياع والتحريف إذا كانت واجبة التبليغ، فالحديث الذي لا شيء فيه مما ذكر أولى.','2','665',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1366,'٧٤٣ -[٢٦٧٣] "كفلٌ" بكسر الكاف أي حظ ونصيب.','2','665',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1367,'بأسنانه، استظهارًا للمحافظة".','2','667',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1368,'٧٤٥ -[٣٦٧٧] "مَنْ أَحْيا سُنَّةً مِنْ سُنَّتِي" قال المظهري:
"السنة: [ما شرعه] رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - من أحكام الدِّين، وهي قد تكون فرضًا؛ كزكاة الفطر، وغير فرض؛ كصلاة العيد، وصلاة الجماعة، وقراءة القرآن في غير الصلاة، وما أشبه ذلك، وإحياؤها أن يعمل بها، ويحرض النَّاس عليها، ويحثهم على إقامتها".
وقال الأشرفي: " الظاهر، يقتضي من سُنَنِي " بصيغة الجمع لكن الرواية بصيغة المفرد.
وقال الطيبي: "هو جنس شائع في أفراده، و"أحيا" استعير للعمل بها وحث النَّاس عليها، وقوله: "قد أميتَتْ بَعْدِي" استعارة أخرى لما يقابلها من الترك، ومنع النَّاس إقامتها وهي كالترشيح للاستعارة الأولى.','2','667',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1369,'"ومن ابتدع بدعة ضلالةٍ".
قال الأشرفي: "يروى بالإضافة، ويجوز أن ينتصبا نعتًا، ومنعوتًا".','2','668',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1370,'٧٤٦٦ -[٢٦٨٠] "عن أبي هريرة رِوَايةً".
قال الطيبي: "نصب على التمييز، وهو كناية عن رفع الحديث إلى رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، وإلاَّ كان موقوفًا عليه.
"يوشك أن يضربَ النَّاسُ أكْبَاد الإِبل".
قال الطيبي: "يوشك أي يقرب و"أن يضرب النَّاس" في موضع الرفع اسم ليوشك، والمسند والمسند إليه أغنيا عن الخبر، وضرب أكباد الإبل كناية عن السير السريع لأنَّ من أراد ذلك يركب الإبل، ويضرب على أكبادها بالرِجل؛ وقال غيره: كأنه عبارة عن','2','668',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1371,'قال الطيبي: "الضمير المجرور في "به" عائد إلى من، والبا للتعدية؛ أي يوفقه أن يسلك طريق الجنة، ويجوز أن يرجع الضمير إلى العلم، والباء سببية، ويكون سَلك بمعنى سهَّل والعائد إلى "من" محذوف، والمعنى سهَّل الله له بسبب العِلم طريقًا من طرق الجنَّة، فعلى الأول "سلك" من السلوك فعدي بالياء، وعلى الثَّاني: من السلك، والمفعول محذوف كقوله تعالى: {يَسْلُكْهُ عَذَابًا صَعَدًا (١٧) } ، قيل: عذابًا مفعول ثاني وعلى التقديرين نسب سلك إلى الله تعالى على طريق المشاكلة.
قوله: "إنَّ الملائكة" جملة معطوفة على الجملة الشرطية، وكذا الجمل بعده المصدرة بأن.
"لتضع أجْنِحَتَهَا" يحتمل أن يكون حقيقة، وإن لم يشاهد، أي تكف أجنحتها عن الطيران وتنزل لسماع العِلم كقوله: في حديث الذكر: "إلاَّ نزلت عليهم السكينة وحفت بهم الملائكة"، وأن يكون مجازًا،','2','670',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1372,'عن التواضع كقوله تعالى: {وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّبَعَكَ} .
وقيل: معناه؛ المعونة وتيسير السعي في طلب العلم.
"رِضى لِطالبِ العِلمِ" مفعول له، وليس فاعلاً لفاعل المعلل فيقدر مضاف؛ أي إرادة رضى.
"وفضل العَالم على العابد، كَفضلِ القَمرِ على سَائرِ الكَوْكَبِ"
قال البيضاوي: "العبادة كمال ونور ملازم ذات العابد لا يتخطاه فشابه نور الكواكب، والعِلم كمال يوجب للعالم في نفسه شرفًا وفضلاً ويتعدى منه إلى غيره فيستضيء بنوره ويكمل بواسطته لكنه كمال ليس للعالم من ذاته بل نور يتلقاه عن النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، فلذلك شبَّه بالقمر".
قال الطيبي: "ولا تظنن أنَّ العالم المفضل عارٍ عن العمل، ولا العابد عن العِلم، بل إن علم ذاك غالب على عمله، وعمل هذا غالب على علمه، ولذلك جعل العلماء ورثة الأنبياء الذين فازوا بالحسنيين، العلم والعَمل وحازوا الفضيلتين، الكمال، والتكميل، وهذه طريقة العارفين بالله وسبيل السائرين إلى الله.','2','671',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1373,'٧٤٩ -[٢٦٨٤] "خِصلتان لا تجتمعان في منافق حسنُ سمت،','2','671',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1374,'ولا فقه في الدِّين".
قال الطيبي: "ليس المراد أنَّ واحدة منهما قد تحصل في المنافق دون الأخرى بل هو تحريض للمؤمن على اتصافه بهما معًا، والاجتناب عن ضدهما فإنَّ المنافق يكون عاريًا منهما وهو من باب التغليظ ونحوه، قوله تعالى: {وَوَيْلٌ لِلْمُشْرِكِينَ (٦) الَّذِينَ لَا يُؤْتُونَ الزَّكَاةَ} وليس من المشركين من يزكي لكنه حث للمؤمنين على الأداء، وتخويف من المنع حيث جعله من أوصاف المشركين، وحسن عطف، "ولا فقه" على "حسن سمت" وهو مثبت لأنه في سياق النفي" انتهى.
وفي الفائق للزمخشري: "حسن السمت أخذ النهج ولزوم المحجة، ثم قيل لكل طريقة ينتهجها الإنسان في تحري الخير، والتَّزَيِّي بزيِّ الصالحين".
وفي النِّهاية: "السمت؛ حسن الهيئة والمنظر في الدِّين، وليس من الحسن والجمال، وقيل هو من السمت: الطريق، يقال الزم هذا','2','672',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1375,'السمت، وفلان حسن السمت أي: حسن القصد".
وقال التوربشتي: "حقيقة الفقه في الدِّين ما وقع في القلب ثم ظهر على اللِّسان، فأفاد العلم، وأورث الخشية، والتقوى، فأما ما يتدارس ليتعزز به فإنه بمعزل عن الرتبة العظمى، لأنَّ الفقه [تعلق] بلسانه، دون قلبه".
"فضل العالم على العابدِ، كَفَضْلي علَى أدْنَاكُمْ".
قال الشيخ كمال الدِّين الزملكاني في كتابه المسمى "تحقيق الأولى من أهل الرفيق الأعلى": "اعلم أنَّ التفضيل تارةً يكون بين الصفتين وتارةً يكون بين المتصفين، ثم التفضيل بين المتصفين قد يراد به الأكثر منهما ثوابًا، وقد يراد به الأقرب إلى الله تعالى وفي كلام كثير من العلماء الإشارة إلى أنَّ الفضيلة تكون بكثرة الثواب، وهذا يحتاج إلى تفصيل لأنه إن أريد بكثرة الثواب ما يعطيه الله تعالى للعبد في الآخرة من درجات الجنة، ولذاتها، ومآبها ومآكلها، ومشاربها [ومساكنها] ومناكحها وملكها ونعيمها الجسماني فللمنع في ذلك مجال، وإن أريد به ما يعطيه الله تعالى للعبد من مقامات القرب، ولذة النظر إليه، وسماع كلامه، ولذات المعارف الإلهية التي تحصل عند كشف الغطاء وما ناسب ذلك فهو القول الآخر، وهو الأقرب إلى أن يقال أنَّ الثوابين','2','673',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1376,'متلازمان فمن كان أرفع في أحدهما فهو أرفع في الآخرة وفي ذلك نظر للمتأمل، ثم الفضيلة تارةً تكون باعتبار ذاتي وتارةً تكون باعتبار عرضي فالذي بالاعتبار الذاتي كتفضيل أحد الجنسين على الآخر في قوله تعالى: {الرِّجَالُ قَوَّامُونَ عَلَى النِّسَاءِ بِمَا فَضَّلَ اللَّهُ بَعْضَهُمْ عَلَى بَعْضٍ} والذي بالاعتبار العرضي فما يمكن اكتسابه كقوله تعالى: {وَفَضَّلَ اللَّهُ الْمُجَاهِدِينَ عَلَى الْقَاعِدِينَ} .
وقد يطلق المفضل على كل عطية لا تلزم المعطى، ثم إنَّ الصفة التي يستحق بها التفضيل قد يكون فضِيلة بالنسبة إلى ما دونها كما يكون في التفاضل بين الحيوانات في كثرة الحمل أو في حسن المشي، أو في قوة العَدو فإنما تظهر فضيلة أحدهما على الآخر بالنسبة إلى اعتبار حال الآخر، وقد تكون فضيلة في نفسها كالعِلم فإنه شريف مطلوب لذاته، وهو فضيلة بالنسبة إلى ما دونه أيضًا.
ومن وجه آخر، وهو أنَّ الفضيلة قد تراد لذاتها وقد يراد لما يتوصَّل بها إليه كالعلم، والعبادة، فإنَّ العلم في ذاته مطلوب متلذذ به مفتخر به وتراد العبادة لما توصل إليه من السَّعادة الأخروية، ويشاركها في ذلك العِلم فيظهر بهذا أنَّ التفضيل بين أمرين قد يكون باعتبار ذاتيهما وقد يكون باعتبار ما يوصلان إليه، وقد أطلق بعضهم أنَّ','2','674',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1377,'الفضل في الأعمال الصَّالحة باعتبار كثرة الثواب، وعندي أنَّ ذلك ليس على إطلاقه بل إن كانت ذات هذا الوصف، أو العَمل أشرف، وأعلى فهو أفضل، وقد يخص الله تعالى بعض الأعمال من الوعد بما لا يخص به الآخر ترغيبًا فيه إمَّا لنفرة النَّفس عنه أو لمشقته عليها فيرغب فيه بمزيد الثواب، أو لأنَّ غيره مما يكتفي فيه بداعي النَّفس والثواب عليه فضل، فالإنصاف أنَّ المفاضلة تارةً تكون بكثرة الثواب، وتارةً تكون بحسب ثمرتهما، وتارةً تكون بحسَب الوصفين بالنظر إليهما، وتارةً تكون بحسب متعلقاتها وقد تكون بأمر عرضي، هذا إذا كان الكلام في وصفين لذات، وأما المفاضلة بين الذاتين فقد يكون لأمر يرجع إلى الجنسين، وهذا أمر لا يدخل تحت الاكتساب، كفضل الإنسان على الحمار، وقد يكون لأمر يرجع إلى الشخصين، وهذا النوع من التفضيل عند التحقيق يرجع إلى التفضيل بالأوصاف.
قال ابن حزم: التفضيل قسمان لا ثالث لهما فضل اختصاص من الله [تعالى] بلا عمل، وفضل مجازاة بعمل فأما فضل الاختصاص دُون العمل فيشترك فيه جميع المخلوقين من الحيوان الناطق، وغير الناطق، والجمادات، والأعراض كفضل الملائكة، وفضل الأنبياء، وفضل إبراهيم بن رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - على الأطفال، وناقة صالح، وذبيح إبراهيم وفضل مكة، والمدينة، والمساجد على البقاع، والحجر الأسود على الحجارة، وشهر رمضان ويوم الجمعة، وليلة القدر، وأما فضل','2','675',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1378,'المجازات فلا يكون إلاَّ للحي الناطق، وهم الملائكة والإنس، والجن فقط، والأقسام المستحق بها التفضيل [في] هذا القسم، وهو المستحق بعمل، سبعة:
ماهيَّة العمل وكميته وفي العرض فيه، وكيفيته والكمّ، والزمان، والمكان، والإضافة، فالماهيَّة أن يكون أحدهما يوفي فروضه، والآخر لا يوافيها، يكثر النوافل، أو نوافل أحدهما أفضل من نوافل الآخر، والكمية أن يخلص أحدهما في العمل ويشوبه الآخر ببعض المقاصد الدنيوية، والكيفية أن يوفي أحدهما جميع حقوق العَمل ورتبه، والآخر يأتي به ولكن ينقص من رتبه، والكم أن يستويا في الفرض ويتفاوتا في النوافل، والزمان كصدر الإسلام، أو وقت الحاجة، والمكان كالصَّلاة في المسجد الحرام، أو المدينة، والإضافة كعمل نبي، أو عمل مع نبي؛ فهذا تلخيص ما ذكره في جهات الفضل، ثم قال: ونتيجة الفضل بهذه الوجوه، شيئان:
أحدهما: تعظيم الفاضل على المفضُول، فهذا يشترك فيه ما كان فضله بغير عمل بل باختصاص، وما كان فضله بعمل.
والثاني: علو الدَّرجة في الجنَّة إذ لا يجوز الحكم للمفضول بعلو [الدرجة في الجنة] على الفاضل، وإلاَّ لبطل الفضل، وهذا القِسم من التفضيل يختص به الفاضل بفضل عمله، دون من حكم بفضله لاختصاص هذا خلاصة ما ذكره.
واعلم أنَّ فضيلة العَمل على العمل، والوصف على الوصف.','2','676',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1379,'والشخص على الشخص من الأمور التوقيفيَّة التي لا يسع الإنسان الكلام فيها من قبل نفسه، ولا ينبغي أحد أن يحكم بتفضيل شخص، ولا نوع على نوع إلاَّ بتوقيف ممن له التفضيل، أو بدليل يستند إلى كتاب الله تعالى، أو سنة رسوله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، أو إجماع الأمة، فإذا قام دليل شرعي على تفضيل مقام على مقام، أو نوع على نوع، علمنا بمقتضى الدليل الشرعي، وأما غير ذلك فلا سبيل إليه لأنه لاَ استقلال للعقل في الأحكام الشرعية لا سيَّما في فضائل الأعمال فإنها ترجع في الحقيقة إلى مقادير الثواب والعقاب، أو إلى تفاوُت درجات القرب الإلهي ولا مجال للعقل في ذلك، وقد يعرض لبعض العَاملين أن يعطى نوعًا من الأجر في الآخرة لا يحصل لغيره، ويكون ما فعلَهُ غيره أفضل من فعلهُ، كما ورد أنَّ الصائمين يدخلون الجنَّة من باب الريان لا يدخل منه غيرهم" كرامة لهم مع أنَّ في العبادات ما هو أفضل من الصيام، وقد يكون الأجر على العمل بحسب فضله على غيره ذلك فضل الله يؤتيه من يشاء، وقد وردت في أعمال خاصَّة، وعُود بأُجور لم يرد مثلها على غيرها بل قد ورد تخصِيص بعض الأعمال المفضُولة بنوع من الأجر لم يحصُل على العمل الفاضِل، مثاله: ما روى أبو موسى الأشعري أنَّ النَّبيَّ
- صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - قال: "ثلاثة لهم أجران رجُلٌ من أهل الكتاب آمن بنبيه، وآمن بمحمَّد','2','677',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1380,'- صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، والعبد المملوك إذا أدى حق الله تعالى، وحق مواليه، ورجل كانت لهُ أمة فأدبها فأحسن تأديبها، وعلَّمها فأحسن تعليمها ثم أعقتها، وتزوجها فله أجران" وكان في الصحابة جماعَة آمنوا بأنبيائهم، وآمنوا بمحمَّد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - مع أنَّ غيرهم من الصحابة أفضل منهم، واختص هؤلاء بأنَّ لهم أجرين، وبأن يؤتوا أجرهم مرتين، وكذلك العبد المملوك، والمتزوج عتيقته وكما ورد في أجر الشهيد من الحياة بعد الموت وكذلك كثير من الخصائص، وهذه الخصوصيات لم تحصل لغيرهم، فثبت أنَّ الدرجات تتفاوت تارةً بحسب تفاوت الأعمال وتارةً بحسب رتب الأعمال، وتارةً بحسب خُصُوصِيَّة عمل خاص، أو وقت خاص، فإذا حاولنا الكلام في تفضيل مرتبة على مرتبة أو عمل على عمل فلا بد من ملاحظة ذلك فيما لم يكن فيه نصّ بتفضيل فيحتاج إلى الاجتهاد في
جِهات الترجيح.
وأما ما ورد النَّص بكونه أفضل من شيء آخر من غير معَارض فلا يعدل عن المنصوص عليه ولا حكم سِوى شريعة الله المأخوذة عن رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -.
وأما العلم فهو فضل في ذاته وشرف للذات المتصفة به كيف ما كان، وهو خير من الجهل على كل حال، لكن هذا الفضل، والشرف','2','678',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1381,'الذي يشير إليه عقليٌ، وأما فضل العِلم من جهة الشرع فإنما كان لكونه قربة إلى الله تعالى ومقتضيًا لثوابه، وموجبًا لخشيته، ومؤديًا إلى معرفته أو الفهم عنه أو فهم كلامه، أو هداية ضال، أو إرشاد مسترشد، وكل واحد من هذه الأمور فضله بحسب متعلقه، وما ترتب عليه من الخير في الدنيا والآخرة.
وعلم لا يؤدي إلى مقصود شرعي فليس هو العِلم النافع الذي به يستحق العالم التفضيل الشرعي، والعلوم تنقسم إلى محمودة، ومذمومة.
والمحمود هاهنا ينقسم إلى فرض عين، وفرض كفاية، وإلى مندوب، وإلى ما يختلف في هذه الرتب بحسب الأشخاص، أو الأزمان، أو الأمكنة، وعلى الجملة فكل علم أدى إلى مقصود شرعي من غير معارض معتبر فهو في قسم العِلم المحمود ومنها فاضل، ومنها مفضول، ومنها ما لا يوصف المتصف به بفضل شرعي كعلم العروض مثلاً، ومنها ما يكون مذمومًا شرعًا كعلم السحر، والطلسمات، وأحكام النجوم وما جرى مجرى ذلك، ومنها ما لا يدخل فيه مدح ولا ذم إلاَّ بحسب ما يستعمل فيه، أو يقصد به كعِلم الهندسة، وما شاكله، وجميع العُلُوم الشرعيَّة يجري فيها كلام يناسب ما ذكرناه في تفاضل العبادات، فإنَّ الفاضل منها قد يكون مفضولاً باعتبار، والمفضول قد يصير فاضلاً باعتبار، وقد ينتقل العلم بحسن قصد متعلمه واستعماله له في مقصوده شرعي من درجة الإباحة إلى درجة الندب كعِلم الحساب، وتسييرات الشمس، والقمر إذا تعلمه ليتوصَّل من هذا إلى قسمة المواريث، ومن هذا إلى معرفة أوقات العبادات، وكذلك قد يصير فرض الكفاية من','2','679',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1382,'العلوم فرض عين، وهذا ظاهر، وأما إدراك فضل علم على علم بالنظر إلى ذاته لا بالنظر إلى حال متعلمه، ولا قصده، ولا ما عرض من كونه في وقت معيَّن أو زمنٍ مُعَيَّن، بل من حيث كونه علمًا فالحق فيه أنَّ شرف العلم بشرف معلومه، فكلما كان متعلق العِلم أشرف كان العلم أشرف، فعلى هذا الأشرف من العِلم الموصل إلى معرفة الله تعالى، ومعرفة صِفاته، والغوص في معاني كلامه، والفهم عنه، وتحقيق توحيده، وتنزيهه، إما بالأدلة، وذلك شأن علماء أصول الدِّين القائمين بحقه، أو بالمعارف الإلهية، وذلك شأن العارفين بالله تعالى، ويحتاج إدراك هذا العلم إلى المبالغة في تزكية النفس، وتطهير القلب، والتنزه عن أوضار الذنوب، ورذائل الأخلاق.
إذا تقرر هذا فشرف العالم وفضله بشرف العِلم وفضله، فكلما كان العِلم أشرف، وأفضل كان العالم به من حيث اتصافه به أشرف وأفضل من المتصف بما دونه من حيث اتصافه به، نعم قد يعرض للمتصف بالعِلم المفضول حالة يكون فيه أفضل من المتَّصف بالعِلم الذي هو أعلى رُتبة منه كما يعرض للعلم المفضول [به] حالة يكون فيها أفضل من العِلم الفاضِل فيكون التفضيل في هذا المقام بحسب العَوارض فإذا انتفت العَوارض،
أو قطع النظر عنها رجع الأمر إلى تفضيل العِلم على الاخر من حيث هو هو، فلذلك لا نقطع القول بإطلاق تفضيل العالم في الجملة فإنه قد لا يكون عالمًا بعلم يقتضي التفضيل بل العالم بالعلم الذي يقتضي التفضيل، كالعالم بعلم الشريعة الذي هو وراثة النبوة وعلم الحلال والحرام الذي يهتدي به إلى طريق الآخرة إذا لم يكن قائمًا بحق علمه','2','680',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1383,'عاملاً به، أو فسدت نيته في علمه، أو استعمله في غير وجهه لا يحكم له بالفضل، وإن كان علمه فاضلاً في نفسه شريفًا عَلِيَّ الدَّرجة لكن هو كالبضاعة النفيسة في الوِعاء الخبيث.
وإذا فسد العالم لم يكن فساده مقصورًا على نفسه بل هُو فاسدٌ، مفسِدٌ، وهو فتنة على النَّاس وضرر عليهم إن كان في محل الاقتداء به لا سيَّما إذا استعمل ما علمه الله تعالى أو ما أعطاه من الجدل، والحجاج، والتفقه في استنباط الباطل، أو المراء في الدِّين، وتدقيق الحيل في بلوغ المقاصد والتقدم عند الأكابر بإنالتهم أغراضهم وتشبيه الباطل بالحق، وتلبيسه على النَّاس، أو المغالبة في المناظرة، وكيف يقال في هذا العالم أنه أفضل من صدِّيق، أو شهيد، أو أحد من المؤمنين المطيعين، كلا بل هو أشبه بإبليس حين غرَّ آدم، وحواء، بقوله: {مَا نَهَاكُمَا رَبُّكُمَا عَنْ هَذِهِ الشَّجَرَةِ إِلَّا أَنْ تَكُونَا مَلَكَيْنِ أَوْ تَكُونَا مِنَ الْخَالِدِينَ (٢٠) } والأحاديث، والآثار في تمييز علماء الآخرة من علماء السوء كثيرة، والذي استقرَّ من ذلك أنَّ العلم النَّافع في الآخرة من الفضائل العظيمة
وليس كل عالم به مستحقًا للتفضيل، والعالم المستحق للتفضيل المطلق هو الذي يعلم العِلم النَّافع شرعًا في الدُّنيا والآخرة وقام بحق علمه من عمل، أو نفع، أو هداية، أو غير ذلك من حقوق العِلم النافع فذلك هو العَالم المفضل بعلمه. انتهى كلام الزملكاني رحمه الله.','2','681',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1384,'٧٥٠ -[٢٦٨٦] "لن يشْبَع المُؤمِن من خيرٍ يسمعهُ حتَّى يكون','2','681',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1385,'مُنْتَهاه الجنَّةُ".
قال الطيبي: "شبه استلذاذه بالمسمُوع باستلذاذه بالمطعُوم؛ لأنه أرغب وأشهى، وأكثر إتعابًا لتحصيله، و"حتى" للتدرج في استماع الخير، والترقي في استلذاذه، والعمل به إلى أن يوصله الجنَّة، ويبلغه إليها لأنَّ سماع الخير سبب العمل، والعمل سبب دخول الجنَّة ظاهرًا، ولما كان قوله: "لن يشبعَ" فعلاً مضارعًا يكون فيه دلالة على الاستمرار تعلق حتى به".','2','682',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1386,'٧٥١ -[٢٦٨٧] "الكلمة الحكمة ضالة المؤمن" أي مطلوبه، قال في النِّهاية: "أي لا يزال يتطلبها كما يتطلب الرَّجل ضالته".
"فحيث وَجَدَهَا فهو أحق بها" قال التوربشتي: "أي بالعمل بها، واتباعها والمعنى؛ إنَّ كلمة الحكمة ربما تكلم بها من ليس لها أهل','2','682',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1387,'ثم وقعت إلى أهلها فهو أحق بها من غيره كما أنَّ صاحب الضالة لا ينظر إلى خساسة من وجدها عنده، كذلك المؤمن لا ينظر إلى خساسة من تفوه بالكلمة الحكمة بل يأخذها منه أخذ صاحب الضالة إياها ممن هي عنده والمراد بالكلمة الجملة المفيدة، والحكمة التي أحكمت مبانيها بالعلم والعقل، ويدل على معنى فيه دقة.','2','683',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1388,'"<span data-type="title" id=toc-49>أبواب الاستنذان والآداب</span>"','2','684',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1389,'٧٥٢ -[٢٦٨٨] "لا تدخلوا الجنَّة حَتَّى تُؤمنُوا، ولا تؤمنوا حتَّى تحابُّوا" فيه حذف النون من "لا تدخلوا"، ولا "تؤمنوا" من غير ناصب ولا جازم على حد قول الشاعر:
أبيت أسرى وتبيتي تدلكي
ذكره ابن مالك.','2','684',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1390,'٧٥٣ -[٢٦٨٩] "فقال النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: عشْرٌ" قال الطيبي: "أي له','2','684',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1391,'عشر حسنات، أو كتب له عشر حسنات، أو المكتوب له".','2','685',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1392,'٧٥٤ -[٢٦٩٤] " أولاهُمَا باللهِ "
قال الطيبي: " أي: أقربهما إلى رحمة الله ".','2','685',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1393,'٧٥٥ -[٢٧٠١] "السَّامُ" هو الموت، وألفهُ منقلبة عن واو.','2','685',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1394,'٧٥٦ -[٢٧٠٣] "يُسَلِّمُ الرَّاكِب على الماشِي، والماشِي على','2','685',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1395,'القاعِدِ".
قال الماوردي: " للإيذان بالسلامة وإزالة الخوف، قال: " والقليل على الكثير " للتواضع.
"وَيُسَلم الصَّغير علي الكبيرِ" للتوقير، والتعظيم.','2','686',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1396,'٧٥٧ -[٢٧٠٦] "ثم إذَا قَامَ فَلْيسَلِّمْ، فَلَيْسَتِ الأوَلى بأَحقِّ من الآخرَةِ".','2','686',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1397,'قال الطيبي: "وقيل: كما أنَّ التسليمة الأولى إخبارٌ عن سلامتهم من شره عند الحضور، فكذلك الثانية إخبار عن سلامتهم من شرِّه عند الغيبة، وليست السلامة عند الحضور أولى من السلامة عند الغيبة بل الثانية أولى.','2','687',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1398,'٧٥٨ -[٢٧١٠] "وضَغَابِيسَ" قال في النِّهاية: "هي صغار القثاء، واحدها ضغبوس، وقيل: هي نبت في أصول التُّمام يشبه الهلْيَوْنَ يسلق بالخل، والزيت ويؤكل.','2','687',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1399,'٧٥٩ -[٢٧١٣] "حدثنا محمود بن غيلان حدثنا شبابة عن حمزة عن أبي الزبير، عن جابر أنَّ النَّبيَّ - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - قال: إذا كتب أحدكم','2','687',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1400,'كتابًا فليترِّبه فإنه أنجح لحاجته".
"هذا حديثٌ منكرٌ لا نعرفه عن أبي الزبير إلاَّ من هذا الوجه وحمزة عندي هو ابن عمرو النصيبي، وهو ضعيف في الحديث".
هذا أحد الأحاديث التي انتقدها الحافظ سراج الدِّين القزويني على المصابيح وزعم أنه موضوع، وقال الحافظ صلاح الدين العلائي: هذا ليس من الحسان قطعًا فهو مما ينكر على صاحب المصابيح جعلها منها، وقد اعترض الحفاظ على الترمذي، وقالوا: بل حمزة هذا هو ابن أبي حمزة ميمون النصيبي قال فيه ابن معين: لا يساوى فلسًا، وقال البخاري: منكر الحديث.
وقال النسائي: متروك وقال ابن عدي: عامة رواياته موضوعة.
وله طريق ثانٍ أخرجه ابن ماجه، من طريق ابن هارون، عن بقية،','2','688',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1401,'عن أبي أحمد، عن أبي الزبير به، وبقيَّة يروي عن المجاهيل، وشيخه أبو أحمد مجهولٌ، وقد رواه عمار بن نصر، أبو ياسر عن بقيَّة، عن عمر، عن أبي عمر، عن أبي الزبير، ذكره شيخنا المزي في الأطراف ثم قال: وقيل عنه عن بقية، عن عُمر بن موسى، عن أبي الزبير، قال العلائي: إن كان أبو أحمد هو عمر بن أبي عمر فقد قال فيه ابن عدي: منكر الحديث، وساق له من رواية بقيَّة عنه أحاديث واهية، وإن كان عمر بن موسى، فهو الوجيهي، روى عنه بقية أيضًا، قال فيه ابن معين: ليس بثقة، وقال البخاري: منكر الحديث، وقال ابن عدي: هو ممن يضع الحديث متنًا، وإسنادًا وأيًا ما كان فالحديث ضعيف منكر، وله سند آخر ذكره ابن أبي حاتم في العِلل من رواية بقيَّة، عن ابن جريج، عن عطاء، عن ابن عباس،','2','689',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1402,'رفعه، وذكر عن أبيه، أبي حاتم أنه قال: هذا حديث باطل انتهى.
وقال الحافظ ابن حجر: كذا قال الترمذي: أنَّ حمزة هو ابن عمرو النصيبي، وقال المزي: المحفوظ أنه حمزة بن ميمون، وكأن الترمذي عرف ذلك، وخالف فيه، ومن ثم قيَّد بقوله: " عندي " وقد ورد من رواية غيره عن شيخه أبي الزبير فأخرجه ابن ماجه من طريق أبي أحمد بن علي الكلاعي عن أبي الزبير عن جابر، وأخرجه البيهقي من طريق عمر بن أبي عمر فقيل: " إنَّ عمر هذا هو أبو أحمد الكلاعي، وقيل غيره، والحديث عنده من رواية بقيَّة بن الوليد عنه فقال: تارةً عن أبي أحمد بن علي وقال: تارة عن عمر بن أبي عمر، فقيل: هما واحد، وقيل: اثنان، وعلى الحالتين يمكن أن يخرج الحديث عن كونه موضوعًا بوجوده بسندين مختلفين " انتهى.
قال في النِّهاية: "قوله فليترِّبه"؛ أي ليجعل عليه التراب، وقال الطيبي: " أي ليسقطه على التراب حتى يصير أقرب إلى المقصد.
قال أهل التحقيق: إنما أمره بالإسقاط على التراب اعتمادًا على الحق سُبحانه وتعالى في إيصاله إلى المقصد.
وقيل: المراد به ذرّ التراب على المكتوب، وقيل: معناه فليخاطب الكاتب خطابًا على غاية التواضع، والمراد بالتتريب المبالغة في التواضع في الخطاب.','2','690',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1403,'٧٦٥ -[٢٧١٤] "ضعِ القَلَمَ علَى أُذُنِكِ فإنَّه أَذكَرُ للمُمْلِي".
قال الطيبي: "قيل: السِّرُّ في ذلك أنَّ القلم أحد اللِّسانين المترجمين عمَّا في القلب من الكلام، وفنون العبارات، فتارةً يترجم عنه اللِّسان اللحمي المعبِّر عنه بالقول، وتارةً يعبر عنه بالقلم وهو المسمَّى بالكتابة، وكل واحد من اللِّسانين يسمع ما يريد من القول وفنون الكلام من القلب، ومحل الاستماع الأذن، واللِّسان موضوع دائمًا على محل الاستماع، ودرج القلب، فلم يزل يسمع منه الكلام، والقلم منفصِل عنه خارج عن محل الاستماع، فيحتاج في الاستماع إلى القرب من محل الاستماع، والدنو إلى طريقه ليستمع من القلب ما يريد من العبارات، وفنون الكلام، ويكتب. وهذا الحديث أورده ابن الجوزي في الموضوعات، وأعلهُ بعنبسة فلم يصب، وقد ورد من طريق آخر من حديث أنس، أخرجه ابن عساكر في تاريخه، وقد تقدم في كلام الحافظ ابن حجر أنَّ الحديث يخرج عن كونه موضوعًا بوجوده بسندين مختلفين.','2','691',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1404,'٧٦١ -[٢٧٢١] "إنَّ عليك السَّلاَمُ تَحِيَّةُ المَيِّتِ" هذا يشعر بأنَّ','2','691',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1405,'السنة في السلام على الموتى، أن يقال: عليكم السلام بتقديم الصلة، وقد صحَّ الحديث أنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - قال لهم: " السلام عليكم دار قوم مؤمنين " فيحتاج إلى الجمع حتى أنَّ بعضهم قال: هذا أصح من حديث النَّهي.
وذهب آخرون إلى أنَّ السنة ما دلَّ عليه حديث النَّهي، قال ابن القيم في البدائع: " وكل من الفريقين إنما أُتُوا من عدم فهم مقصود الحديث فإنَّ قوله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "عليك السَّلام تحية الميت" ليس تشريعًا منه وإخبارًا عن أمر شرعي، وإنما هو إخبار عن الواقع المعتاد الذي جرى','2','692',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1406,'على ألسنة النَّاس في الجاهلية، فإنهم كانوا يقدمون اسم الميت على
الدعاء، كما قال الشَّاعر:
عليك سلام الله قيس بن عاصِم
وقول الذي رثي عمر بن الخطاب:
عليك سلام من أمير وباركت
وهو في أشعارهم كثير، والإخبار عن الواقع لا يدُل على الجواز فضلاً عن الاستحباب، فتعين المصير إلى ما ورد عنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - من تقديم لفظ السلام حين يسلم على الأموات.
قال: فإن تخيَّل متخيل في الفرق أنَّ السلام على الأحياء يتوقع جوابه فقدم الدعاء على المدعو له بخلاف الميت، قلنا: والسلام على الميت يتوقع جوابه أيضًا، كما ورد به الحديث.','2','693',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1407,'٧٦٢ -[٢٧٣٢] "واللهِ ما رَأيته عُريانًا قبْلهُ، ولا بعدهُ".
قال البيضاوي: "لعلها أرادت ما رأيته عريانًا استقبل رجلاً','2','693',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1408,'واعتنقه، فاختصرت الكلام لدلالة الحال".','2','694',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1409,'٧٦٣ -[٢٧٣٣] "اذهب بِنا".
قال الطيبي: " الباء في "بنا" بمعنى المصاحبة.
"لو سمعك كان لهُ أربعةُ أَعينٍ".
قال التوربشتي: " أي لسرَّ بقولك سرورًا يزداد به نورًا إلى نوره، كذي عينين أصبح يبصر بأربع ".
وقال الطيبي: " هو كناية عن السرور المضاعف؛ لأنهم يكنون عن السرور بقرة العين ".
"فسألاهُ عن تسعِ آيات بيِّناتٍ فقال لهم: "لاَ تُشركوا بالله شيئًا" إلى آخره.
قال الطيبي: "كان عند اليهود عشر كلمات تسع منها مشتركة بينهم','2','694',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1410,'وبين المسلمين وواحدة مختصَّة بهم، فسألوا عن التسع المشتركة، وأضمرُوا ما كان مختضًا بهم، فأجابهم - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - عما سألوه وعما أضمروه ليكون أدل على معجزته ولذلك قبَّلا يديه.
"وَتَمْشُوا بِبَرِيءٍ إِلى ذِي سُلطَانٍ"
قال الطيبي: " الباء في "ببريء" للتعدية؛ أي لا تتكلموا بسوء فيمن ليس له ذنب.
"وعليكم خاصَّةً، اليهُودَ، أن لا تعتدُوا في السَّبتِ" قال الطيبي: "عليكم" خبر لـ "أنَ" لا تعتدُوا"، وقيل هي كلمة الإغراء.
"وأن لا تعتدُوا" مفعوله أي: الزموا، واحفظوا، ترك الاعتداء.
"وَخَاصَّةً" منوَّن حال.
"واليهُود" نصب على التخصيص؛ أي أعني اليهود، ويجوز أن يكون خاصَّة بمعنى خصوصًا، فيكون اليهود معمولاً لفعله أي: أخص اليهُود خصوصًا، وفي رواية، يهودُ، مضمومًا بلا لام على أنه منادى.','2','695',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1411,'"<span data-type="title" id=toc-50>أبواب الأدب</span>"','2','696',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1412,'٧٦٤ -[٢٧٣٦] "للمسلمِ على المسلم ستٌّ بالمعرُوفِ".
قال الطيبي: "بالمعروف، صفة بعد صفة لموصوف محذوف أي ست ملتبسة بالمعروف، وهو ما عرف في الشرع".','2','696',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1413,'٧٦٥ -[٢٧٥٣] "لَعَنَ اللهُ علَى لِسَانِ مُحَمدٍ من قعد وسْطَ الحَلْقَةِ".
قال الخطابي: "هذا مؤوَّلْ على وجهين: أحدهما: أن يأتي حلقة قوم فيتخطى رقابهم ويقعد وسطها، ولا يقعد حيث ينتهي به المجلس،','2','696',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1414,'والثاني: أن يقعد وسط الحلقة فيحُول بين الوجوه، ويحجب بعضهم من بعض، فيتضررون".','2','697',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1415,'٧٦٦ -[٢٧٦٢] "كان يَأخذُ مِنْ لِحيتِهِ من عَرضِهَا، وَطَولِهَا".
قال الطيبي: " هذا لا ينافي قوله "اعفُو اللحى" لأنَّ المنهي عنه هو قصَّها كفعل الأعاجم، والأخذ من الأطراف قليلاً لا يكون من القص في شيء ".','2','697',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1416,'٧٦٧ -[٢٧٦٨] "إنَّ هذه ضجعَةٌ لا يُحِبُّهَا اللهُ" هي بكسر','2','697',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1417,'الضاد، الهيئة، وبفتحها المرة، والأوجه هنا الكسر.','2','698',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1418,'٧٦٨ -[٢٧٧٤] ، "أَنْمَاطٌ" هي ضرب من البسط له خمل رقيق واحدها نمط.','2','698',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1419,'٧٦٩ -[٢٧٧٦] "عن نَظْرَةِ الفُجأةِ" هي أن يقع النظر إلى الأجنبيهَ من غير قصد بغتة.','2','698',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1420,'٧٧٠ -[٢٧٧٧] "لا تُتْبعِ النَّظرةَ النَّظرةَ فإنَّ لكَ الأولى وليسَتْ لَك الآخِرَة".','2','698',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1421,'قال الطيبي: "يدل على أنَّ الأولى نافعة كما أنَّ الثانية ضارة لأنَّ الناظر إذا أمسك عنان نظره ولم يتبع الثانية أُجِرَ".','2','699',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1422,'٧٧١ -[٢٧٨٧] "وَخَيْرُ طِيبِ النِّسَاءِ مَا ظَهَرَ لَوْنهُ وَخَفِيَ رِيحُهُ"
قال البغوي في شرح السنة: "حملوا ذلك على مَا إذا أرَادَت أن تخرج، فأما إذا كانت عند زوجها فَلْتَتَطَيَّبْ بما شاءت".','2','699',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1423,'٧٧٢ -[٢٧٩٩] "نظفُوا أفنيتكم" جمع فناء، وهو المتَّسع أمام','2','699',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1424,'الدار.','2','700',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1425,'٧٧٣ -[٢٨٠٦] "قِرَامُ" ستر، قال في النِّهاية: " القِرام: الستر الرقيق، وقيل: الصَّفيق من صُوف ذي ألوان، والإضافة فيه كقولك: ثوب قميص وقيل: القرام: الستر الرقيق وراء الستر الغليظ، ولذلك أضافه ".','2','700',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1426,'٧٧٤ -[٢٨١١] "فِي لَيْلَةٍ إضْحِيَانٍ"','2','700',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1427,'قال في النِّهاية: " أي مضيئةِ مُقمِرَة، يقال: ليلة إِضْحِيَانٌ وَإضحيَانَةٌ، والألف والنون زائدتان ".
وقال في الفائق: " هو بكسر الهمزة، وإفعلان مما قلَّ في كلامهم ".','2','701',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1428,'٧٧٥ -[٢٨١٣] "مِرْطٌ" هو الكساء.','2','701',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1429,'٧٧٦ -[٢٨١٤] "أَسْمَالَ مليقين" قال في النِّهاية: "أسمال جمع','2','701',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1430,'سمل وهو الخلق من الثياب وميلتين؛ تثنية ملية وهي تصغير الملاءة، وهي الإزار".','2','702',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1431,'٧٧٧ -[٢٨٢٢] "المستشار مؤتمنٌ" قال الطيبي: " معناه أنه أمين فيما يسأل من الأمور ولا ينبغي أن يخون المستشير بكتمان مصْلَحَته ".','2','702',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1432,'٧٧٨ -[٢٨١٧] "مَنْ لَبِسَ الحَرِيْرَ في الدُّنْيَا لَمْ يَلْبَسَهُ في الآخرَة"
زاد ابن حبان: "وإن دخل الجنَّة لبسه أهل الجنَّة ولم يلبسه هو"
قال القرطبي: "وهذا نص صريح في أنه يحرمه إذا دخل الجنَّة إذا','2','702',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1433,'لم يتب، فإن كانت هذه الجملة من قول النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - فهو الغاية [في البيان] وإن كان من قول الراوي -على ما ذُكر أنه موقوف- فهو أعلم بالمقال وأقعد بالحال، ومثله لا يُقالُ من جهة الرأي، وقد قيل إنَّ حرمانه ذلك إنما هو في الوقت الذي يعذب في النَّار، فإذا خرج منها، وأدخل الجنة لم يحرم منها شيئًا، لا حريرَا، ولا خمرًا، ولا غيره لأنَّ حرمان شيء من لذات الجنة لمن كان في الجنة نوع عقوبة ومؤاخذة، والجنة ليست بدار عقوبة، ولا مؤاخذة فيها بوجه من الوجوه، والحديث يرد هذا القول، وكما لا يشتهي منزلة من هو أرفع منهُ وليس ذلك بعقوبة كذلك لا يشتهي خمر الجنة، ولا حريرها، ولا يكون ذلك عقوبة انتهى".','2','703',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1434,'٧٧٩ -[٢٨٢٩] "الحَزَوَّز" قال في النِّهاية: " الحَزَوَّرُ؛ الذي قارب البُلُوغ ".','2','703',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1435,'٧٨٠ -[٢٨٣٢] "وَوضعِ الأذى عنْهُ" قال في النِّهاية: " يريد الشعر، والنجاسة وما يخرج على رأس الصبي حين يولد، يحلق عنه يوم سابعهِ. "يُنَافِحُ" [عنه] بالحاء المهمله، أي يكافح ويدافع.','2','704',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1436,'٧٨٢ -[٢٨٤٨] "كَانَ يَتَمَثَّل بشِعْرِ ابن رَوَاحَة، ويَتَمَثَّلُ: ويأتيك بالأخبار [من لم] تُزَوِّدِ".
في مسند أحمد، ومصنف ابن أبي شيبة عن عائشة قالت: "كان','2','704',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1437,'رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - إذا استراث الخبر تمثل ببيت طرفة: ويأتيك بالأخبار من لم تُزَوِّدِ"
وروى ابن أبي شيبة، وعبد بن حميد، عن قتادة، قال: "بلغني أنه قيل لعائشة: هل كان رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - يتمثل بشيءٍ من الشِّعر، قالت: كان أبغض الحديث إليه، غير أنه كان يتمثل ببيت أخي بني قيس، فيجعل أوَّله آخره وآخره أوله، يقول: ويأتيك من لم تزود بالأخبار، فقال له أبو بكر: ليس هذا فقال رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - إنِّي والله ما أنا بشاعر، وما ينبغي لي.','2','705',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1438,'٧٨٣ -[٢٨٥١] "لأن يمتليء جوف أحدكم قيحًا يريْهِ".
قال في النِّهاية: "هو من الوَرْي، قال الأزهري: "الورى، مثل الري: داء يداخل الجوف غير مهمُوز، قال الجوهري: ورَوى القيح','2','705',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1439,'جَوْفَهُ، يَريَه، ورْيًا: أكله، وقال قوم: "معناه حتَّى يصيب رِئته".
"خيْر لَهُ مِنْ أَنْ يمتَليءَ شِعْرًا".
قال النووي: قالوا: المراد منه أن يكون الشعر غالبًا عليه مُستوليًا بحيث يشغله عن القرآن، أو غيره من العلوم الشرعية وذكر الله تعالى.','2','706',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1440,'٧٨٤ -[٢٨٥٣] "إنَّ الله يَبْغَضُ البَليغَ من الرِّجَالِ الَّذِي يَتَخَلَّلُ بِلِسَانِهِ كَمَا تَتَخَلَّلُ البَقَرَةُ" قال في النِّهاية: "هو الذي يتشدق في الكلام، ويلفه بلسانه كما تلف البقرة الكلأ بلسانها لفًّا".','2','706',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1441,'٧٨٥ -[٢٨٥٨] "إذا سَافَرْتمْ فِي الخِصْبِ" بكسر أوله.
"فَأعْطُوا الإبل حظَّهَا من الأرضِ"','2','706',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1442,'قال البيضاوي: "يعني دعوها ساعةً فساعةً ترعى.
"وَإِذَا سافَرتُمْ في السَّنَةِ" أي في الجدب.
"فبادِرُوا بنَقْيهَا" أي أسرعوا السير عليها ما دامت قوتها باقية.
النقي، وهي بكسر النون وسكون القاف [المخ] ، قاله النووي.
قال التوربتشي: " ومن النَّاس من يرويه نقبها، بالباء الموحدة بعد القاف، وهو تصحيف ".
وقال الأشرفي: " قال في الصحاح: نقب البعير -بالكسر- إذا رقت أخفافه.
فيمكن أن يجعل هذا اللفظ بهذا المعنى، فلا يكون تصحيفًا.
وقال الحافظ العراقي في شرح الألفية: "قرأ عليَّ بعض العجم في المصابيح حديثًا: "إذا سافرتم في الخصب فأعطوا الإبل حقها، وإذا سافرتم في الجدب فبادرُوا بها نقبها"؛ بفتح النون، وبالباء الموحَّدة بعد القاف، فقلتُ: إنَّما هو نقْيها بالكسر، وبالياء آخر الحروف. فقال: هكذا ضبطها بعض الشُّيوخ في طرَّة الكتاب فأخذت منه الكتاب، وإذا على الحاشية كما ذكر، وقال: النَّقب الطريق الضيِّق بين جبلين، فقلتُ هذا خطأ وتصحيف فاحش، وإنما هو النَّقِيُّ، أي: المخ الذي في','2','707',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1443,'العظم، ومنه قوله في حديث أم زرع: " لا سمين فينتقي " وفي حديث الأضحية: " والعجفاء التي لا تنقي ".
قال: فليحذر طالب العلم، ضبط ذلك من الحواشي إلاَّ إذا كان بخط من يعرف خطه من الأئمة. انتهى.
وقال الطيبي: " نِقيهَا يحتمل الحركات الثلاث، أن يكون منصوبًا مفعولاً به "وبها" حال منه؛ أي بادروا نقيها مستعينين بسيرهَا، وأن يكون مرفُوعًا فاعلاً للظرف، وهو حال؛ أي: بادروا إلى المقصد ملتبسين بها نقيها، أو مبتدأ والجار والمجرور خبره، والجملة حال، كقولهم: فُوه إلى فِيَّ، وأن يكون مجرورًا بدلاً من الضمير المجرور والمعنى سارعُوا بها إلى المقصد باقية النَّقْي فالجار والمجرور الحال.
قال: وليت شعري كيف يستقيم المعنى مع إرادة نقب الخف ".
"وَإِذَا عَرَّسْتُمْ" التعريس، النزول آخر اللَّيل.','2','708',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1444,'٧٨٦ -[٢٨٥٥] "يتَخوَّلنا بِالمَوْعِظَةِ" بالخاء المعجمة، قال في النِّهاية: " أي: يتعهدنا، من قولهم: فلان حائل مالٍ، وهو الذي يصلحه ويقوم به.
وقال أبو عمر: الصواب: يتخوَّلنا بالحاء المهملة؛ أي يطلب الحال التي ينشطون بها للموعظة فيعِظهم فيها، ولا يُكْثِرُ عليهم فيملُّون، وكان الأصمعي يرويه: يتخوَّنُنا بالنون، أي: يتعهَّدنا ".','2','709',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1445,'"<span data-type="title" id=toc-51>أبوابُ الأمثال</span>"','2','710',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1446,'٧٨٧ -[٢٨٥٩] "إِنَّ الله ضَرَبَ مَثلاً صِراطًا مستقِيمًا".
قال الطيبي: "بدل من "مثلاً" لا على إهدار المبدل، كقولك زيدٌ رأيت غلامه رجلاَّ صَالحًا، إذ لو أسقطت غلامه لم يتبيَّن".','2','710',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1447,'٧٨٨ -[٢٨٦١] "كأنهم الزُّطُّ"','2','710',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1448,'قال في النِّهاية: "هم جنس من السُّودان، والهُنُود".','2','711',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1449,'٧٨٩ -[٢٨٦٣] "مَنْ فَارَقَ الجَمَاعَة قِيدَ شِبْرٍ فَقد خلع رِبْقةَ الإسلامِ من عُنُقِهِ".','2','711',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1450,'قال في النِّهاية: " مفارقة الجماعة: ترك السُنَّةَ واتِّباع البِدعةَ، والرَّبقة في الأصل: عُرْوة في حَبْلِ، تُجْعل في عُنُقِ البهيمة أو يَدِهَا تُمسِكهَا، فاستَعَارَهَا للإسلام، يعني ما يشدُّ المسلم به نفسه من عُرى الإسلام: أي حدوده، وأحكامه وأوامره ونواهيه ".
" والقِيدَ " القدر.
"وَمن ادَّعى دعوى الجَاهِلِيَّةِ" هو قولهم عند الأمر الحادِث الشديد، يا آل فلان.
"فإنَّه من جُثى جهنَّم" بالجيم والمثلثة جمع جُثوة، بالضم وهو الشيء المجموع.','2','712',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1451,'٧٩٠ -[٢٨٦٦] "شَجرَ الأرْزِ".
بِسكون الراء، وفتحها، ثم زاي، قال في النِّهاية: "خشب','2','712',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1452,'معروف، وقيل: هو الصنوبر".','2','713',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1453,'٧٩١ -[٢٨٦٨] "من دَرَنهِ" هُو الوَسخ.','2','713',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1454,'٧٩٢ -[٢٨٦٩] "مَثَلُ أُمَّتِي مَثَلُ المَطَرِ لاَ يُدْرَى أَوَّلَهُ خَيْرٌ أَمْ آخِرُهُ" قال التوربشتي: " لا يحمل هذا الحديث على التردد في فضل الأول، على الآخر، فإنَّ القرن الأول هُم المفضلون على سائر القرون من غير مِرْيَة، ثم الَّذِين يلُونهم، ثم الذين يلونهم، وإنما أراد نفعهم في بث الشريعة والذب عن الحقيقة.','2','713',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1455,'وقال البيضاوي: " نفي تعلق العلم بتفاوت طبقات الأمَّة في الخيرية، وأراد به نفي التفاوت لاختصاص [كل] طبقة منهم بخاصيَّة، وفضيلة توجب خيريتها، كما أنَّ كل نوبة من نوب المطر لها فائدة في النشو والنماء لا يمكن إنكارها، والحكم بعدم نفعها، فإنَّ الأولين آمنوا وشاهدوا من المعجزات، وتلقوا دعوةَ الرَّسُول بالإجابة والإيمان، والآخرين آمنُوا بالغيب لما تواتر عندهُم من الآيات، واتَّبعوا من قبلهم
بإحسان، وكما أنَّ المتقدمين اجتهدوا في التأسيس والتمهيد، فالمتأخرون بذلُوا وسعهم في التلخيص، والتجريد، وصرفوا عمرهم في التقدير والتأكيد فكل مغفُور وسعيهم مشكورٌ، وأجرهم موفور.
وقال الطيبي: " تمثيل الأمة بالمطر إنما يكون بالهدى والعِلم، [كما أنَّ تمثيله صلوات الله عليه وسلاه بالغيث والهدى والعِلم] فتختص هذه الأمة المشبَّهة بالمطر، بالعُلماء الكاملين منهم، والمكملين لغيرهم. فيستدعى هذا التفسير أن يراد بالخير النفع، فلا يلزم من هذا المساواة في الأفضلية، ولو ذهب إلى الخيريَّة، فالمراد وصف الأمة قاطبة سابقها ولاحقها، أولها وآخرهَا بالخيرية، وأنها ملتحمة بعضها مع بعض، مرصوصة كالبنيان، على حد قول الأنماريَّة: هم كالحلقة المفرغة لا يدري أين طرفَاها. وقول الشَّاعِر:','2','714',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1456,'إنَّ الخيار من القبائل واحد ... وبنو حنيفة كلهُم أخيار
فالحاصل أنَّ الأمة بأسرها مرتبطة بعضها مع بعض في الخياريَّة، بحيث أَبْهَمَ أمرها، وارتفع التمييز بينها، وإن كان بعضها أفضل من بعض في نفس الأمر، وهو قريب من باب سوق المعلوم مساق غيره، وفي معناه قولُهُ:
تشابه يومًا بأسه ونواله ... فما نحن ندْري أي يوميه أفضل
أيوم نداه الغمر أم يوم بأسه ... وما منهُمَا إلاَّ أغر محجل
ومعلوم علمًا جليًّا أنَّ يوم نداه الغمر أفضل من يوم بأسه لكن الندى لما لم يكن يكمل إلاَّ بالبأس أشكل عليه الأمر فقال ما قال، وكذلك أمر المطر، والأمة". انتهى.','2','715',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1457,'٧٩٣ -[٢٨٧٢] "إنَّما النَّاس كإبل مائة لا يجد الرَّجل فيها راحلة" قال الخطابي: "معناه أنَّ النَّاس في أحكام الدِّين سواء لا','2','715',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1458,'فضل فيها لشريف على مشروف، ولا لرفيع منهم على وضيع كالإبل المائة لا يكون فيها راحلة.
وقال في النِّهاية: " يعني أنَّ الرضي المنتخب من النَّاس في عِزَّة وجوده كالنجيب من الإبل القوي على الأحمال، والأسفار الذي لا يُوجد في كثير من الإبل ".
قال الأزهري: " الذي عندي فيه أنَّ الله تعالى ذم الدنيا وحذَّر العباد سوء مغبتها، وضرب لهم فيها الأمثال ليعتبروا ويحذروا.
وكان عليه الصلاة والسلام يحذرهم ما حذرهم الله، ويُزهدهم فيها، فرغِب النَّاس بعدَهُ وتنافسوا عليها، حتى كان الزُّهد في النَّادِر القليل منهم فقال: "تجدون النَّاس بعدِي كإبل مائة ليس فيها راحلة". أي أنَّ الكامل في الزُّهد في الدنيا، والرَّغبة في الآخره قليل كقلة الراحلة في الإبل، والرَّاحلة: هي البعير القوي على الأسفار، والأحمال، النجيب التمام الخلق الحسن المنظر، ويقع على الذكر والأنثى والهاء فيه للمبالغة" انتهى.','2','716',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1459,'"<span data-type="title" id=toc-52>أبواب فضائل القرآن</span>"','2','717',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1460,'٧٩٤ -[٢٨٧٥] "ما أنزل في التوراة، ولا في الإنجيل، ولا في الزبُورِ، ولا في الفُرقان مِثْلُهَا".
قال ابن حبان: معناه أنه لا يعطى القاريء للتوراة والإنجيل من الثواب مثل ما يعطى لقارئ الفاتحة لأنه تعالى فضَّل هذه الأمة على غيرها من الأمم وأعطاها على قراءة كلامه أكثر مما أعطى غيرها على قراءة كلامه".','2','717',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1461,'٧٩٥ -[٢٨٧٧] "لاَ تَجعلُوا بُيُوتُكُم مقابرَ".','2','717',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1462,'قال البيضاوي: "أي كالمقابر خالية عن الذكر والطاعة، واجعلوا لها نصيبًا من القراءة والصلاة"','2','718',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1463,'٧٩٦ -[٢٨٧٨] "لِكل شيء سنام" قال في النِّهاية: " سنام كل شىء أعلاه ".
"وفيها آيةٌ هي سيِّدَةٌ آي القُرآنِ هي آية الكُرسِي".
قال البيضاوي: " إنما كانت أعظم آية لأنها مشتملة على أمهات المسائل الإلهية، فإنها دالة على أنه تعالى واحد في الإلهية، متَّصف بالحياة، قائم بنفسه، مقوم لغيره، منزه عن التحيز والحلول، مبرأ عن التغير والفتور، لا يناسب الأشباح، ولا يعتريه ما يعتري الأرواح، مالك الملك والملكوت، مبدع الأصُول، والفروع، ذو البطش الشديد الذي لا يشفع عنده إلاَّ من أذن له، العالم وحده بالأشياء كلها، جليِّها','2','718',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1464,'وخفيّها، كلِّيِّها وجزئيِّها، واسع الملك والقدرة، لا يؤوده شاق، ولا يشغله شأن، متعال عن أن يدركه وهمٌ، عظيم لا يحيط به فهم".','2','719',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1465,'٧٩٧ -[٢٨٨٠] "سَهْوَة" قال في النِّهاية: " هي بيتٌ صغيرٌ منحدرٌ في الأرض قليلاً، شبيه بالمُخدَع والخزانة.
وقيل: هي كالصُّفَّة تكون بين يدي البيت، وقيل: شبيه بالرَّفِّ أو الطاق يُوضع فيه الشىء".
"فَكَانَتْ تجيءُ الغُولُ".
قال في النِّهاية: " هي أحد الغيلان، وهي جنس من الجن، والشياطين ".
"قال: صَدَقَتْ وهي كذوبٌ"','2','719',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1466,'قال الطيبي: "تتميم في غاية الحسن، فإنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لما قال: صدقَتْ، وأثبت لها الصدق، وأوهم المدح، استدرك بصيغة تفيد المبالغة، أي صدقت في هذا القول مع أنَّ عادتها الكذب البالغ في بابه، وفي المثل: إنَّ الكذوب قد يصدق".','2','720',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1467,'٧٩٨ -[٢٨٨١] "من قرأ الآيتين من آخرِ سُورَةِ البَقَرةِ".
قال المظهري: " هما آمن الرَّسُول " إلى آخر السُورة.
قال: ومَعْنَى "كفتاهُ" دفعتا عن قارئها شر الجن والإنس.','2','720',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1468,'٧٩٩ -[٢٨٨٢] "إنَّ الله كَتَبَْ كِتَابًا قَبْلَ أَنْ يَخْلُقَ السَّمَوَاتِ وَالأرضِ بأَلْفَي عَام، أنزل مِنه آيتَيْنِ خَتَمَ بِهِمَا سُورَة البقَرةِ".','2','720',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1469,'قال الطيبي: " فإن قيل كيف الجمع بين هذا وبين حديث عبد الله بن عمرو: "وقدر الله المقادير قبل أن يخلق السموات والأرض بخمسين ألف سنة".
فالوجه فيه أن نقول: اختلاف الزمانين في إثبات الأمرين لا يقتضي التناقض بينهما، لأنَّ من الجائز أن لا يكون مظهر الكوائن في اللوح دفعة واحدة، بل يثبته الله شيئًا فشيئًا فيكون أمر المقادير على ما ذكر، وأمر النوع الذي أنزل منه آيتين على ما ذكرنا، وفائدة التوقيت تعريفُهُ - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - إيانا فعل الآيتين، فإن سبق الشيء بالذكر على سائر أجناسه وأنواعه يدل على فضيلة مختصَّة به.','2','721',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1470,'٨٠٠ -[٢٨٨٣] "يَأتِي القُرآن، وأهله الذينَ يعملُون بهِ في الدُّنيا تقدُمُه سُورَةُ البَقَرَةِ، وآلُ عِمرَانَ".','2','721',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1471,'قال الطيبي: " الضمير في "تقدمه" راجع إلى القرآن، قيل:
يقدم ثواب القرآن ثوابهما، وقيل يصور صورَة بحيث يجيء يوم القيامة يراه النَّاس، كما يجعل الله لأعمال العباد خيرها وشرها، صورة، ووزنا يوضع في المِيزان، فليقبل المؤمن هذا وأمثاله، ويعتقده بإيمانه فإنه ليس للعقل إلى مثل هذا سبيل، وفي تقدم هاتين السورتين دليل على أنهما أعظم من غيرهما، لأنهما أطول، والأحكام فيهما أكثر.
"كأنهما غيايتان" بفتح الغين المعجمة، وتخفيف المثناتين التحتيتين.
قال في النِّهاية: " الغياية: كل شيء أظلَّ الإنسان فوق رأسه كالسَّحابة وغيرهَا ".
"بَيْنَهُمَا شَرْقٌ" بفتح الراء وإسكانها، وهو الأشهر في الرواية، واللغة، قال النووي: قال في النِّهاية: "الشَّرق هنا: الضَّوءُ، وهو الشَّمس والشقُّ أيضًا".','2','722',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1472,'وفي الفائق: "هو من قولهم: شاة شرقاء؛ أي بينهما فرجة" وفصل، لتميزها بالتسمية.
"أو كأنهما غمامتان سوداوان".
قال التوربشتي: " وصفهما بالسواد لاتساقهما، وارتكام البعض منهما على بعض وذلك أجدى ما يكُون من الظلال.','2','723',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1473,'٨٠١ -[٢٨٨٥] "تِلْكَ السَّكينةُ" في الغريبين: " هي السكون، والطمأنيه، وقيل: هي الرَّحمة، وقيل: الوقار، وما يسكن به الإنسان " قال التوربشتي: " إظهار هذه الأمثال على العباد من باب التأييد الإلهي يؤيِّدُ بها المؤمن فيزداد يقينًا، ويطمئن قلبه بالإيمان إذا كوشف بها".','2','723',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1474,'٨٠٢ -[٢٨٨٧] "إنَّ لِكُلِّ شيءً قَلْبًا وقلبُ القُرآنِ يَس" قال','2','723',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1475,'أبو عبيد: "أي: لبه، وقلب كل شيء لبّه وخالصه" قال التوربشتي:
"وذلك لاحتوائها مع قصر نظمها على الآيات الساطعة والبراهين القاطعة والعلوم المكنونة، والمعاني الدقيقة، والمواعيد الرغيبة، والزواجر البالغة، والإشارات الباهرة، والشواهد البليغة وغير ذلك".
وقال حجة الإسلام الغزالي: " إنما كانت قلب القرآن لأنَّ الإيمان صحته الاعتراف بالحشر، والنشر، وهذا المعنى مقرر فيها بأبلغ وجه ".','2','724',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1476,'٨٠٣ -[٢٨٨٨] "مَنْ قَرأ حم الدُّخَانَ".','2','724',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1477,'٨٠٤ -[٢٨٩٣] "مَنْ قَرأ إذا زلزلَتِ عُدِلَتْ لَهُ بِنِصْفِ القُرْآنِ"','2','724',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1478,'قال التوربشتي والبيضاوي: " يحتمل أن يقال: المقصود الأعظم بالذات من القرآن بيان المبدأ، والمعاد، وإذا زلزلت مقصُورة على ذكر المعاد مستقلة ببيان أحواله فتعادل نصفه، وجاء في الحديث الآخر: " إنها ربع القرآن " وتقريره أن يقال: القرآن يشتمل على تقرير التوحيد، والنبوات وبيان أحكام المعاش، وأحكام المعاد، وهذه السُورة مشتمل على القسم الأخير من الأربع: {قُلْ يَا أَيُّهَا الْكَافِرُونَ (١) } محتوية على القسم الأول منها؛ لأنَّ البراءة من الشرك إثبات للتوحيد فتكون كل واحدة منها كأنها رُبع القرآن.
قال الطيبي: " فإن قلت: هلا حملُوا المعادلة على التسوية في الثواب على المقدار المنصُوص عليه؟
قلتُ: منعهم من ذلك لزوم فضل "إذا زلزلت" على سُورة الإخلاص.
والقول الجامع فيه، ما ذكره التوربشتي من قوله: " نحن وإن سلكنا هذا المسلك بمبلغ علمنا، نعتقد، ونعترف أنَّ بيان ذلك على الحقيقة إنما يلتقى من قبل الرَّسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، فإنه هو الذي ينتهي إليه في معرفة حقائق الأشياء والكشف عن خفيَّات العُلُومِ، فأما القول الذي نحن بصدده، ونحومُ حوله على مقدار فهمنا، وإن سلم من الخلل، والزلل لا يتعدَّى عن ضرب من الاحتمال ".','2','725',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1479,'٨٠٥ -[٢٨٩٨] "مُحي عنْهُ ذُنُوب خَمْسِيْنَ سَنَةً إِلاَّ أنْ يَكُونَ عَلَيْهِ دين".
قال الطيبي: "جعل الدِّين من جنس الذنوب تهويلاً له ثم استثنى منها".','2','726',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1480,'٨٠٦ -[٢٩٠٤] "الَّذِي يَقْرَأُ القُرآن، وهو ماهرٌ بِهِ" هو الحاذق بالقِرَاءَةِ.
والذي يقْرَأُهُ، وهو عليه شاقٌ له أجران".','2','726',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1481,'قال ابن الجوزي في "جامع المسانيد": " ربما توهم السامع من ذكر الأجرين أنهما يزيدان على أجر الماهر، وليسَ كذلك؛ لأنَّ المضاعفة للماهر لا تحصى، فإنَّ الحسنة قد تضاعف إلى سبعمائة وأكثر، والأجر شيء مقدَّر فالحسنة لها ثواب معلومٌ ففاعلها يعطى ذلك الثواب مضاعفًا إلى عشر مرَّات ولهذا المقصر منه أجران.','2','727',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1482,'٨٠٧ -[٢٩٠٥] "مَنْ قَرَأ القرآن واستظهره" قال في النِّهاية: أي حفظه، تقول قرأت القرآن عن ظهر قلبي، أي قرأته من حفظي.','2','727',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1483,'٨٠٨ -[٢٩٠٦] "وهو الفضلْ".','2','727',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1484,'قال البيضاوي: " أي: الفاضل بين الحق والباطل، وصف بالمصدر [مبالغة] كرجل عدل.
"ليس بالهزلِ" أي جدٌّ كله ليس فيه ما يخلو عن إتقان، وتحقيق.
"قصمهُ اللهُ" أي كسره وأماته.
"ومن ابتغى الهُدَى في غيره أضلَّه اللهُ".
قال الطيبي: "يحتمل الخبر، والدعاء".
"وهو حبل الله المتين".
قال الطيبي: " أي الموصلة التي يوثق عليها فيتمسك بها من أراد الترقي والعروج إلى معراج القدس، وجوار الحق ".
"وهو الذكر" أي: المذكور.
"الحكيم" أي المحكم الذي لا يأتيه الباطل من بين يديه، ولا من خلفه، أو المشتمل على الحقائق.
"والحكيمُ" بمعنى ذو الحكمة:
"لا تزيغُ به الأهوَاءُ" أي: لا تميل عن الحق باتباعه، أو ما دامت','2','728',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1485,'تتبعه.
"ولا تلتبس به الألسنة" أي لا تختلط به غيره بحيث يشتبه الأمر ويلتبس الحق بالباطل فإنه تعالى تكفل بحفظه، وقيل: معناه لا يتعسَّر على ألسنة أهل اللغات المختلفة بل يتيسَّر ويتسهَّل عليهم تلاوته.
"ولا يشبَعُ منه العُلمَاء" أي لا يحيط علمهم بكنهه فيقفوا عن طلبه وقوف من شبع عن مطعُوم فإنَّ النَّاظر فيه لا ينتهي إلى حد إلاَّ وهو بعد طالب لحقائقه باحث عن دقائقه.
"ولا يخلقُ على كثْرَةِ الرَّدِّ" لا يزول رونقه ولذة قراءته، واستماعه عن كثرة تردادِهِ على ألسنة التالين وتكراره على أذان المستمعين على خلاف ما عليه كلام المخلوقين.
"لم تنته الجِنُّ" أي لم يتوقفوا ولم يمكثوا.
"من قال به صدَقَ".
قال الطيبي: " فيه وجهان أحدهما: أنَّ قال متضمن معنى أخبر، والآخر أنه مثل قولهِ "سُبحَانَ من لبس العِزَّ، وقال به"، أي أحبَّه واختصَّه لنفسه، كما يقال: فلان يقول بفلان؛ أي بمحبته واخصتاصه، فعلى هذا معنى صدق العمل بمقتضاه، والتحرِّي لرضى الله، فحينئذٍ ينطبق عليه قوله.
"ومن عمل به أُجر" وقوله: " ومن دُعي إليه هُدِي " روي مجهولاً، ولا بد فيه من ضمير راجع إلى "من" فيصير الهادي مهتديًا. ومعناه:','2','729',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1486,'من دعا النَّاس إلى القرآن وفق للهداية، ولو روي معروفًا كان المعنى من دعا النَّاس إلى القرآن هداهم إلى صراط مستقيم.','2','730',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1487,'٨٠٩ -[٢٩١٠] "لا أقول ألم حرفٌ، ولكنْ أَلِفٌ [حرف] ولامٌ حرفٌ وميم حرفٌ".
قال الطيبي: " يعني مسمَّى ميم -وهو مَهْ- حرف، لما تقرر أنَّ لفظة ميم اسم لهذا المسمَّى، فحمل الحرف في هذا الحديث على المذكورات مجازًا؛ لأنَّ المراد منه في مثل "ضرب" في "ضرب الله مثلاً" كل واحدٍ من "ضَهْ، ورَهْ، وَبَهْ".
فعلى هذا إن أريد بـ "ألم" مفتتح سُورة الفيل يكون عدد الحسنات ثلاثين، وإن أريد به مفتتح سُورة البقرة، وشبهها يبلغ العدد تسعين.','2','730',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1488,'٨١٠ -[٢٩١١] "مَا أذنَ اللهُ لِعَبْدٍ"
قال الطيبي: " هو من أذِنَتْ للشيء أذَنًا، إذا أصغيت إليه، وهو هنا عبارة عن الإقبال من الله بالرأفة والرَّحمة على العبد. وذلك أنَّ العبد إذا كان في الصَّلاةِ وقد فرغ من الشواغل متوجهًا إلى مولاه مناجيًا له بقلبه ولسانه، فإنه تعالى أيضًا مقبل عليه بلطفه وإحسانه إقبالاً لا يقبله في غيره من العبادات، فكنَّى عنه بالإذن.
"وإنَّ البرَّ ليُذَرُّ على رأس العَبْدِ" بالذال المعجمة، أي: ينثر، ويفرق، وقيل بالمهملة، أي يصبّ.
"وما تقرَّب العِبَادُ إلى الله بمِثلِ مَا خَرَجَ مِنْهُ"
قال ابن فورك: "الخروج على وجهين:','2','731',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1489,'أحدهما خروج الجسم من الجسم، وذلك بمفارقة مكانه، واستبداله مكانًا آخر، وذلك محال على الله تعالى.
والثاني: ظهور الشيء من الشيء، كقوله خرج لنا من كلامك نفع وخير، أي: ظهر لنا من كلامك، وهذا هو المراد، فالمعنى: ما أنزل الله تعالى على نبيِّه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - وأفهم عباده قال: وقد قال قائلون: أنَّ الهاء في قوله: " خرج منه " عائد إلى العبد، وخروجه منه وجوده على لسانه محفوظًا في صدره مكتوبًا بيده ".
وقال الأشرفي: " خرج منه " أي من كتابه المبين -وهو اللوح المحفوظ-.','2','732',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1490,'٨١١ -[٢٩١٤] "يُقال لصاحِبِ القرآنِ" قال التوربشتي:
"الصحبة للشيء، الملازمة له، ويكون بالبدن، وهو الأصل والأكثر، ويكون بالعناية والهمة، وصاحب القرآن هو الملازم له بالهمة والعناية، ويكون ذلك تارةً بالحفظ والتلاوة، وتارةً بالتدبر له والعمل به، فإن ذهبنا إلى الأول، فالمراد من الدرجات بعضها دون بعض،','2','732',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1491,'والمنزلة التي في الحديث [هي] ما يناله العبد من الكرامة على حسب منزلته في الحفظِ والتلاوة لا غير، وذلك لما عرفنا من أصل الدِّين: أنَّ العامل بكتاب الله المتدبر له أفضل من الحافظ والتالي له إذا لم ينل شأوه في العمل والتدبر. وإن ذهبنا إلى الثاني -وهو أحق الوجهين وأتمهما- فالمراد من الدرجات التي يستحقها بالآيات سائرها، وحينئذٍ تُقَدَّر التلاوة في القيامة على مقدار العمل، فلا يستطيع أحد أن يتلو آية إلاَّ وقد أقام ما يجب عليه فيها، واستكمال ذلك إنما يكونُ للنَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - ثم للأمة بعده على مراتبهم ومنازلهم في الدِّين، كل منهم يقرؤه على مقدار مُلازمته إياه تدبرًا، وعملاً".','2','733',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1492,'٨١٢ -[٢٩١٦] "عُرِضتْ عليَّ أخور أُمتِي حتَّى القذَاة يخْرِجهَا [الرَّجلُ] من المسجدِ"".','2','733',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1493,'قال التوربشتي: "القذاة ما يقع في العين من تراب، أو تبن، أو وسخ ولا بد هنا من تقدير مضاف؛ أي أجور أعمال أمتي، وأجر القذاة، أو أجر إخراج القذاة وتحتمل الجر و"حتى" بمعنى إلى، فحينئذٍ التقدير: إلى أجر إخراج القذاة، "فيخرجها من المسجد" جملة مستأنفة للبيان، والرَّفع عطفًا على أجور، والتقدير ما مرَّ. و"حتى" يحتمل أن تكون هي الدَّاخلة على الجملة فحيئذٍ التقدير: حتى أجر القذاة يخرجها على الابتداء والخبر" انتهى.
وقال الشيخ ولي الدِّين العراقي: قوله: "حتَّى القذاة" بالرفع عطفًا على قوله أجور أمتي، ويجوز فيه الجر بتقدير: "حتى أجر القذاة" ثم حذفَ المضاف وأبقى المضاف إليه على إعرابه، ويجوز فيه النَّصب بتقدير: حتَّى رأيت القذاة" انتهى.
"وعُرِضَتْ عَليَّ أمَّتي فلم أَرَ ذَنْبًا أعظم من سُورةٍ من القرآن، أو آيةٍ أوتيها رجلٌ ثمَّ نسِيَهَا".
قال التوربشتي: " هذا مقتبس من قوله تعالى: {كَذَلِكَ أَتَتْكَ آَيَاتُنَا فَنَسِيتَهَا وَكَذَلِكَ الْيَوْمَ تُنْسَى (١٢٦) } وإنما قال أوتيها ولم يقل "حفظها" لينبِّه به على أنها كانت نعمة عظيمة أولاها الله إيَّاها ليقوم بها، ويشكر','2','734',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1494,'مولاها، فلما نسيها كأنه كفر تلك النعمة، فبالنظر إلى هذا المعنى كان أعظم جرمًا، فلما عدَّ إخراج القذاة التي لا يؤبه لها من الأجور تعظيمًا لبيت الله تعالى عدَّ أيضًا النسيان من أعظم الجرم تعظيمًا لكلام الله تعالى، كأنَّ فاعل ذلك عدَّ الحقير عظيمًا بالنسبة إلى العظيم، فأزاله عنه، وصاحب هذا عدَّ العظيم حقيرًا، فأزاله عن قلبه.
وقال الشيخ ولي الدِّين العراقي في "شرح سنن أبي داود":
"استدلَّ بهذا الحديث على أنَّ نِسيان القرآن من الكبائر، وقد صرَّح بذلك صاحب "العُدَّة" من أصحابنا وتوقف فيه الرافعي، وهذا الكلام المحكي عن صاحب "العدَّة" ظاهره أنه في نسيان جميع القرآن، ويحتمل أنه أراد به أي جزء من القرآن، وهذا الحديث يدل عليه كقوله: "من نسي سورة من القرآن أو آية" وهذا يحتمل أنه شك من الراوي في اللَّفظ الذي قاله النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، ويحتمل أن يكون تنويعًا من النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، وأنَّ الوعيد يترتب على كل منهما.
قال: " وهذا الحديث -إن صحَّ- يقتضي أنَّ هذا أكبر الكبائر ولا قائل به، وقد يحمل نسيانها على رفضها ونبذها، كما في قوله تعالى: {أَتَتْكَ آَيَاتُنَا فَنَسِيتَهَا} وهذا يقتضي الكفر وهو أكبر الكبائر ولا قائل به، [وقد يحمل على الذنوب المتعلقة بالنسيان وقد يحمل على','2','735',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1495,'الذنوب التي اطَّلع عليها في ذلك الوقت. فإن قلت: كيف يكون النسيان ذنبًا وهو مرفوع عن هذه الأمة؟ قلتُ: المعدود ذنبًا هو التفريط في محفوظه من القرآن بترك تعاهده ودرسه، فإنه سبب ظاهر للنسيان" انتهى كلام الشيخ ولي الدِّين.
وأقول: يحتمل أنَّ المراد بالذنوب التي عرضت الصغائر فيكون نسيان ما أوتيه الإنسان من القرآن أعظم الصَّغائر والمراد الذنوب التي خصَّت بها هذه الأمة بدليل قوله: " ذُنوب أمَّتي " فإنَّ الأمم السابقة ما كُلِّفُوا حفظ كتبهم، بل ولا تيسَّر لهم ذلك، فلا يدخل الذنُوب التي اشتركت فيها الأمم كالقتل والزنا والسَّرقة وسائر الكبائر، ويكون نسيان القرآن أعظم الذنوب لم تحرَّم إلاَّ في هذه الشريعة كالتصوير، ولبس الحرير، وكشف العورة، والله أعلم.
وقال الدارقطني في "العلل": " هذا الحديث غير ثابت؛ لأنَّ ابن جريج لم يسمع من المطلب شيئًا ويقال: كان يدلسه عن أبي سبَرة أو غيره من الضعفاء.','2','736',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1496,'٨١٣ -[٢٩١٧] "من قرأ القرآن فليسأل الله به".','2','736',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1497,'قال الطيبي: "يحتمل وجهين:
أحدهما: أنه كلَّما قرأ آية رحمة يسأل من الله، وآية عذاب يتعوَّذ منها إلى غير ذلك.
والثاني: أنه يدعو بعد الفراغ من القراءة بالأدعية المأثورة.','2','737',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1498,'٨١٤ -[٢٩١٨] "مَا آمَن بِالقُرآنِ مَنِ اسْتَحلَّ مَحَرمَهُ".','2','737',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1499,'قال الطيبي: " من استحل ما حرَّم الله تعالى في القرآن فقد كفر مطلقًا، فخصَّ ذكر القرآن لعظمته وجلالته ".','2','738',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1500,'٨١٥ -[٢٩١٩] "الجَاهِرُ بِالقُرآنِ كَالجَاهِرِ بِالصَّدَقَةِ والمُسِرُّ بالقُرآنِ، كَالمسِرِّ بالصَّدَقَةِ".
قال الطيبي: "شبَّه القرآن جهرًا وسرًّا بالصَّدقَةِ جهرًا وسرًّا ووجه الشبه ما ذكره الشيخ محيي الدِّين النووي حيث قال: جاءت آثار بفضيلة رفع الصَّوت بالقرآن وآثار بفضيلة الإسرار".
قال العلماء: والجمع بينهما أن الإسرار أبعد من الرياء، فهو أفضل في حق من يخاف ذلك، فإن لم يخفْ فالجهر أفضل بشرط أن لا يؤذي غيره من مُصلِّ، أو نائم أو غيرهما".','2','738',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1501,'٨١٦ -[٢٩٢١] "كَانَ يَقرأ المُسبِّحاتِ".','2','738',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1502,'قال الطيِّبي: "هي كل سُورة افتتحت بسُبحان، وسبَّح، ويسبح.
"يقول: إنَّ فيهنَّ آيةً خيرٌ من ألفِ آيةٍ".
قال الحافظ عماد الدِّين بن كثير: " [هي مبهمة] .
وقال الطيبي: "هي مبهمة كإخفاء ليلة القدر في رمضان، وساعة الإجابة في يوم الجمعة".','2','739',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1503,'٨١٧ -[٢٩٢٢] "مَن قال حينَ يصبح ثلاثَ مرَّاتٍ أعُوذ باللهِ السَّمِيعِ العَلِيمِ".
في تفسير ابن مردويه رواية ولذلك لم يشرح حديث من قال حين يصبح إلى آخره.','2','739',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1504,'٨١٨ -[٢٩٢٣] "فإذا هي تَنْعتُ" أي تصف.','2','739',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1505,'قال الطيبي: "ويحتمل وجهين:
أحدهما: أن يقول كانت قراءته كيت وكيت.
والثاني: أن تقرأ مرتلة مبيِّنة كقراءة النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -.','2','740',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1506,'٨١٩ -[٢٩٢٤] . "سعةً" لعله بفتح السين.
حدثثا محمَّد بن الحسين بن أبي يزيد الهمداني عن عمرو بن','2','740',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1507,'قيس عن عطيَّة عن أبي سعيد قال: " قال رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "يقول الرَّبُ عزَّ وجلَّ: من شغله القرآنُ عن ذِكْرِي ومسألتِي أعطيتهُ أفض ما أعطي السَّائلين ... الحديث".
هذا الحديث أورده ابن الجوزي في الموضوعات، من حديث عمر بن الخطاب.
وقال الحافظ ابن حجر في "أماليه على الأذكار" إنَّه حديث حسن، وأنَّ ابن الجوزي لم يصب، وقد بسطت الكلام على ذلك في التعقبات على "الموضوعات" وقال الشيخ عز الدِّين ابن عبد السلام في أماليه: "هذا الحديث يدل على تقديم الذكر على الدعاء، وقوله تعالى: {ادْعُونِي أَسْتَجِبْ لَكُمْ} {قُلْ مَا يَعْبَأُ بِكُمْ رَبِّي لَوْلَا دُعَاؤُكُمْ}','2','741',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1508,'{ادْعُوا رَبَّكُمْ تَضَرُّعًا وَخُفْيَةً} .
هذه الآيات تدل على الأمر بالدعاء.
قال ووجه الجمع بين الظواهر: أنَّ الأوقات على ثلاثة أقسام:
وقت دلَّ الدليل الشرعي على أنَّ الدعاء فيه أفضل كوقت السجود، فيقدم الدعاء، ويكون راجحًا، ووقت دلَّ الدليل على أنَّ الذكر أفضل كوقت الركوع لقوله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "أمَّا الركوع فعظموا فيه الرَّب، وأما السُّجود فأكثروا فيه من الدعاء" فيقدم الذكر، ووقت لم يدل فيه دليل على أحدهما فيقدم الذكر لقوله: " من شغلهُ ذكري عن مسألتي " وفي تاريخ ابن عساكر عن سفيان بن عيينة أنه قال لأصحاب الحديث: بم تشبهون حديث النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: " ما شغل عبدي ذكري عن مسألتي إلاَّ أعطيته أفضل ما أعطي السَّائلين " فقالوا له: تقول من يرحمك الله قال: بقول الشَّاعر:
وفتى خلا من ماله ... ومن المرؤة غير خال
أعطاك قبل سُؤاله ... وكفاك مكروه السؤال','2','742',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1509,'"<span data-type="title" id=toc-53>أبواب القراءات</span>"','2','743',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1510,'٨٢١ -[٢٩٢٧] "كان رسولُ الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - يُقطِّعُ قِرَاءَتَهُ، يقرأُ:
{الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ (٢) } {الرَّحْمَنِ الرَّحِيمِ (٣) } ثم يقفُ".
قال الطيبي: " هذه الرواية ليست بسديدة في الألسنة، ولا بمُرضِية في اللَّهجة العربية، بل هي صيغة لا يكاد يرتضيها أهل البلاغة، وأصحاب اللِّسان فإنَّ الوقف الحسن ما اتَّفق عنده الفصل والوقف التام من أول الفاتحة عند قوله: {مَالِكِ يَوْمِ الدِّينِ (٤) } .
وكان النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أفصح النَّاس لهجة وأتمهم بلاغة، وإنما كان يقف على الآية ليبين للمستمعين رؤس الآي ولو لم يكن لهذه العلة لما وقف على [رب] العالمين، ولا على: {الرَّحْمَنِ الرَّحِيمِ} لأنَّ الوقف عليهما قطع للصَّفة عن الموصوف.','2','743',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1511,'٨٢٢ -[٢٩٤٢] "بئسَ مَا لأَحَدِهِم أَنْ يَقُولَ".
قال الطيبي: " ما نكرة موصوفة، وأن يقول مخصوص بالذم؛ أي: بئس شيئًا كائنًا للرَّجل، قوله: " نسيت بل هو نُسِّي " إضراب عن القول بنسبة النسيان إلى نفسه.
"واسْتَذكِرُوا القُرآن" قال الطيبي: " السين للمبالغة، أي اطلبوا من أنفسكم المذاكرة به، والمحافظة على قراءته. وهو عطف من حيث المعنى على قوله: " بئسما لأحدهم أن يقول ": [أي] لا تقصروا في معاهدة القرآن، واستذكروه.
"لهو أشدُّ تفَصِّيًا" أي: تفلتًا، وأصل التفَصِّي من الشيء، التخلص منه تقول: تفصَّيتُ من الديون، إذا خرجت منها.
"من صُدُورِ الرِّجالِ من النَّعَمِ من عُقلِهِ" "من" الأولى متعلقة بـ "تفصيَّا"، والثانية بـ "أشد"، والثالثة: بـ "تفصِّي " مقدرًا، أي: من تفصِّي النعم من عقلها، وذكر الضمير على أحد اللغتين. والعقل: جمع عقال؛ مثل كتاب، وكتب، وهو الحبل الذي يشد به ذراع البعير.','2','744',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1512,'٨٢٣ -[٢٩٤٣] "أساورهُ" أي: أنازعه.
"لَبَّبْتُهُ بِرِدَائِهِ"
قال في النِّهاية: " يقال: لبَبْتُ الرَّجُلُ إذا جعلْتَ في عُنُقِهِ ثوبًا وجررْته به "','2','745',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1513,'٨٢٤ -[٢٩٤٦] "لم يفْقَهْ" أي: لم يفهم ظاهر معاني القرآن.','2','745',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1514,'٨٢٥ -[٢٩٤٨] "الحال المُرتَحِلُ".
قال في النِّهاية: "هو الذي يختم القرآن بتلاوته، ثم يفتتح التِّلاوة من أوَّله، شبه بالمسافر يبلغ المنزل فيحلَّ فيه، ثم يفتتح سيره، أي يبتدِؤُهُ، وقيل: أراد بالحالِّ المرتحل الغازي الذي لا يقف عن غزوٍ إلاَّ عقبه بآخر".','2','746',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1515,'"<span data-type="title" id=toc-54>أبواب تفسير القرآن</span>"
"فصلَّى كُلُّ رجُلٍ مِنَّا على حيَالِهِ" قال في النهاية: " أي تلقاء وجهه ".','2','747',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1516,'٨٢٧ -[٢٩٧٩] "صِمَامًا واحدًا" قال في النِّهاية: " أي في مسلك واحد، والصِّمام: ما يسد به الفُرْجة، فَسُمِّي به الفرج، ويجوز أن يكون على حذف المضاف؛ أي موضع صِمام، ويُروَى بالسِّين.','2','747',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1517,'٨٢٨ -[٢٩٨٠] "حوَّلْتُ رَحْلِي البَارحَةَ".','2','747',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1518,'قال في النِّهاية: " كَنَّى بِرحله عن زوجَتهِ أرادَ به غِشْيانها في قُبُلهَا من جهَّة ظهرها؛ لأنَّ المجامع يعلُو المَرأةَ وَيركَبُهَا مما يلي وجهَهَا، فَحَيْثُ رَكبهَا من جهَةِ ظَهْرهَا كَنَّى عنه بتحويل رَحْلِهِ، إما أن يريد به المنزل والمأوى، وإما أن يريد به الرَّحل الذي [يركَّبُ على الإبل، وهو الكور] .','2','748',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1519,'٨٢٩ -[٢٩٨١] "لا ترجع إليك أبدًا آخر ما عليك ... فقالَ سَمْعًا لربِّي وطاعةً".','2','748',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1520,'٨٣٠ -[٢٩٨٢] "فأمَلتُ عَليَّ" بالتشديد.','2','749',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1521,'٨٣١ -[٢٩٨٨] "إنَّ [للشَّيطانِ] لمَّةَّ بَابنِ آدمَ، وللملَكِ لَمَّةً" قال في النِّهاية: " اللَّمة الهمَّة، والخطرة تقع في القلب، أراد إلمام الملك أو الشيطان به، والقُرْب منه، فما كان من خطرات الخير فهو من الملك، وما كان من خطرات الشَّرِّ فهو من الشَّيطان".','2','749',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1522,'٨٣٢ -[٣٠٠٧] "تحت جحفته" هي الترس.','2','749',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1523,'٨٣٣ -[٣٠١٠] "فكلَّمهُ كِفَاحًا"
أي: مواجهة ليس بينهما حجاب، ولا رسول.','2','750',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1524,'٨٣ -[٣٠١١] "أنَّ أرواحهم في طيرٍ خُضْرٍ، تسرَحُ فِي الجنَّةِ حيثُ شَاءَت".','2','750',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1525,'قال الشيخ كمال الدين الزملكاني في كتابه المسمَّى "تحقيق الأولى من أهل الرفيق الأعلى: " في هذا الحديث دليلان على مسألتين من مسائل أصول الدِّين.
إحداهما: أنَّ الجنَّة والنَّار مخلوقتان موجودتان في وقتنا هذا، وهو مذهب أهل السنة، وأكثر المسلمين، وقال به من المعتزلة: الجُبائي وأبو الحسين البصري، وآيات القرآن شاهدة بذلك كثيرة جدًا، والأحاديث الدالة على ذلك كثيرة صحيحة.
وقد أجمعت الأمة في الصَّدر الأول على ذلك، والمخالف فيه محجوج بالإجماع قبل ظهور الخلاف، فلا عبرة بخلافه لتقدم الإجماع.
والثانية: في الروح ومفارقتها البدن وبقائها بعده وتنعُّمها في البرزخ. قال القاضي عياض: "في هذا الحديث أرواح الشهداء".
وفي حديث كعب بن مالك: " إنَّما نسمة المؤمن ".','2','751',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1526,'قال والنَّسمة تطلق على ذات الإنسان جسمًا وروحًا وتطلق على الروح مفردة، وهو المراد هنا؛ لأنها في الحديث الآخر مفسَّر: بالروح، ولأنَّ الجسم يفنى ويأكله التراب، ولقوله في الحديث: "حتَّى يرجعه الله إلى جسده يوم القيامة" وعلى هذا فالحياة المذكورة في الآية محمولة على ما حصل للروح، إذ روح غير الشهيد ممن يؤخر للحساب لا يدخل الجنة عند مفارقتها للبدن فقد ورد: " أرواح المؤمنين على أفنية قُبُورهم ".
وورد عرض مقعد المؤمن عليه من الجنَّة بكرة وعشيًّا، وفي بعض ألفاظ هذا الحديث: " أنَّ أرواح المؤمنين ".
قال القاضي عياض: " فيحمل على المؤمنين الذين يدخلون الجنَّة بغير عذاب، فهم يدخلونها الآن، وقد قيل أنَّ هذا المُنعَّم، والمعذَّب من الأرواح جزء من الجسد تبقى فيه الروح، فهو الذي يتألم ويعذَّب ويلتذ وينعَّم، وهو الذي يقول: " رب ارجعُون " وهو الذي يسرح في الجنة.
فيمكن أن يكون هو الذي يجعل طائرًا أو في جوف طائر. فإن قيل','2','752',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1527,'فإذا كان الشَّهيد حيًّا فهل هي تحدث له عقب موته، وما الفرق بين حياته وبين حياة من يعذَّب في قبره ويُنعَّم؟ قلتُ: قد قدمنا الجواب عن هذا في أثناء الكلام، وذكرنا أنَّ الحياة راجعة إلى الروح وكونها مختصَّة بهذا النَّعيم أو إلى بعض أجزاء البدن وفيه الروح وغير روح الشهيد، ممن يوقف للحساب لا يحصُل لها ذلك ويبين امتياز حياة الشهيد عن حياة غيره.
قال الغزالي: " الذي يشهد له طرق الاعتبار وتنطق به الآيات والأخبار أنَّ الموت معناه تغيُّر حال فقط، وأنَّ الروح باقية بعد مفارقة الجسد، إمَّا منعَّمة، وإمَّا معذَّبة، ومعنى مفارقتها للجسد انقطاع تصرُّفها فيه.
قال: وحقيقة الإنسان نفسه وروحه، وهي باقية، نعم تغير حاله من وجهين:
أحدهما: أنه سلب منه أعضاؤه وأهله، وولده، وجميع أمواله، فلا فرق بين سلب هذه من الإنسان، أو سلب الإنسان منها، فالمؤلم هو الفراق، فمعنى الموت سلب الإنسان عن أمواله بإزعاجه إلى عالم آخر لا يناسب هذا العالم، فيعظم تحسُّره على ما كان يأنس إليه من ذلك، ومن كان لا يفرح إلاَّ بذكر الله تعالى ولا يأنس إلاَّ به فإنه يعظَّم نعيمه وتتم سعادته؛ لأنه خُلِّي بينه وبين محبوبه، وقطعت عنه العلائق','2','753',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1528,'والشواغل.
والثاني: أنه ينكشف له ما لم يكن مكشوفًا، فمنه حسناته وسيِّئاته، وعندها يتحسَّر على ما فرط، ثم عند الدفن قد تُردُّ روحه إلى الجسد لنوع من العذاب وقد يعفي عنه. نعم، ولا يمكن كشف الغطاء عن كُنه حقيقة الموت، إذ لا يعرف الموت من لا يعرف الحياة، ومعرفة الحياة بمعرفة حقيقة الروح في نفسها وإدراك ماهية ذاتها، ولم يؤذن لرسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أن
يتكلم فيها ولا أن يزيد على أن يقول: " الروح من أمر ربي " ولكن بالموت ينتقل [إما] إلى سعادة وإما إلى شقاوة، وكل ما سوى الله تعالى ذكره، والأنس به فلا بد من فراقه عند الموت لا محالة.
قال عبد الله بن عمرو: " إنما مثل المؤمن حين مفارقته روحه مثل رجل كان في سجن فأخرج منه ".
وهذا الذي ذكره حال من تجافي عن الدنيا ولم يكن أنسه إلاَّ بذكر الله [تعالى] وكانت شواغل الدنيا تحجُبه عن محبُوبه، وفي الموت خلاصهُ من جميع المؤذيات وانفراده بمحبُوبه من غير عائق وما أجدر ذلك بأن يكون منتهى النَّعيم واللذات، وأكمل اللذات للشهداء الذين قتلوا في سبيل [الله] ؛ لأئَهم ما أقدموا على القتال إلاَّ قاطعين التفاتهم عن علائق الدنيا، مشتاقين إلى الله تعالى، راضين بالقتل فِي','2','754',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1529,'طلب مرضاته، فإن نظر إلى الدنيا فقد باعها طوعًا بالآخرة، والبائع لا يلتفت قلبُه إلى المبيع، وإن نظر إلى الآخرة فقد اشتراها، وتشوق إليها، فما أعظم فرحُه بما اشتراه إذا رآه، وما أقلَّ التفاته إلى ما باعه، إذا فارقه، وتجرُّد القلب لحبِّ الله تعالى قد يتَّفق في بعض الأحوال ولكن لا يدركه الموت عليه، فيتغير، والقتال سبب للموت فكان سببًا لإدراكه على مثل هذا الحال، فلهذا عظم النَّعيم، إذ معنى النَّعيم أن ينال الإنسان ما يريده، قال الله تعالى: {مَثَلًا ظَلَّ وَجْهُهُ مُسْوَدًّا} فكان هذا أجمع عبارة لمعاني لذَّات الجنَّة، وأعظم العذاب أن يمنع الإنسان عن مراده كما قال تعالى: {وَحِيلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُونَ} فكان هذا أجمع عبارة لعقوبات أهل جهنَّم، وهذا النَّعيم يدركه الشَّهيد كما انقطع نفسه من [غير] تأخير، وهذا أمر انكشف لأرباب القُلوب، وإن أردت عليه شهادة من جهة السَّمع، فجميع أحاديث الشُّهداء تُدل عليه.
وكل حديث يشتمل على التعبير عن منتهى نعيمهم بعبارة أخرى وهذا الذي ذكره الغزالي مع ما قدَّمناه يوضح لك ما بين حال الشَّهيد وحياته، وبين حال سائر الموتى. وقال أبو الحكم بن برَّجان: "حياة','2','755',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1530,'الشُّهداء عند ربهم كاملة، بالإضافة إلى حياتهم في الدُّنيا مخلصة من خبث الأجساد الدنيويَّة وظلماتها، مطهَّرة من أرجاسها، سالمة من الأضداد، متَّصلة بالحياة الأخرويَّة اتِّصالاً صحيحًا، لكنها إنما تتم بوجودها في أجسادها يوم بعثها، ويكمَّل الكمال الذي أُهِلَّت له بدخولها في دار الحيوان، في جوار الحي الذي لا يموت" فهذا الكلام من هذا الرَّجل يدل على أنه أراد أنَّ حياة الشَّهيد في البرزخ أكمل من حياته في الدُّنيا، ويكون عند رد رُوحه إلى جسده أكمل، قال: وينبغي أن يكون معنى قوله "في حواصل خضر" أنَّ الشَّهيد يطير في دار البرزخ لا أنه على صورة طائر؛ بل على صُورته التي كان عليها في الدُّنيا وأحسن، تطير فيما هنالك، وذكر الحواصل إعلامًا بأنهم أحياء، وأنَّ أرواحهم حاصلة في حقائق أجسادهم الدنيوية، وهو أظهر من أن يكون في صُورة طائر لما جاء أنَّ الله خلق الإنسان في أحسن تقويم ولو كانوا على صُورة طائر لكان ضربًا من المسخ ولخرج عن طريق الإكرام. انتهى كلامه.
وهذا الذي ذكره من رجوع رُوحه إلى غير الجسد وإلى صُورة مثل صُورته لم أقف عليه لغيره، وإنما قاله على سبيل البحث، وهو','2','756',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1531,'بحث حسن لو ساعد عليه النقل عن العُلماء، وفي حديث جعفر بن أبي طالب: "أنَّ الله عوَّضه عن يدَيهِ جناحين من ياقوت يطير بهما في الجنَّة وإنه مرَّ به في نفر من الملائكة يبشرون أهل بيته بالمطر" فيحتمل أنه مرَّ به في صورته ويحتمل أنه مرَّ به في صورة طائر [لقوله: يطير مع الملائكة، ويحتمل أنه إنما جُعل في صُورة طائر] فرَّق بين حياة البرزخ وحياة البعث، وإن كان الشَّهيد حيًّا في الحالتين، ورأيت في "كتاب الجهاد" لابن المبارك حديثًا عن النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - قال: " إذا استشهد الشَّهيد أخرج الله له جسدًا كأحسن جسد، ثم أمر بروحه فأدخل فيه فينظر إلى جسده الذي خرج منهُ كيف يصنع به، وينظر إلى من حوله ممن يتحزَّن عليه، فيظن أنهم يسمعونه أو يرونهُ".
فإن صحَّ هذا الحديث أو كان مما تقوم به الحجة فهوَ ظاهر في ما ذكرنا، والله أعلم. انتهى كلام الزملكاني.','2','757',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1532,'٨٣٥ -[٣٠٢٠] "واليمين الغموسُ" هي الكاذبة الفاجرة كالتي','2','757',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1533,'يقتطع بها الحالف مال غيره، سُمِّيت غموسًا؛ لأنها تغمس صاحبها في الإثم، وفي النَّار، وفعُول للمبالغة. "يمين صبرٍ" هي التي لزم بها، وحبس عليها، وكانت لازمة لصاحبها من جهة الحكم، ويقال لها مصبُورة، وإن كان صاحبها في الحقيقة هو المصبور؛ لأنه إنما صبر من أجلها، أي حبس، فوصفت بالصبر، وأضيفت إليه مجازًا.','2','758',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1534,'٨٣٧ -[٣٠٣٦] "ضَجْنَانَ" بفتح الضَّاد المعجمة وسكون الجيم ونونين بينهما ألف، موضع أو جبل بين مكة والمدينة.
"ضَافِطةٌ" بضاد معجمة وفاء، وطاء مهملة جمع ضافط، وهو','2','758',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1535,'الذي يجلب الميرَة والمتاع إلى المدن.
"منَ الدَّرمَكِ" هو الدقيق الحوَّارى.
"اخْتَرَطَ سَيْفَهُ" أي: سلَّه من غمده، وهو افتعل من الخرط.','2','760',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1536,'٨٣٨ -[٣٠٤٧] "حتَّى تأطِرُوهم أطرًا" بالطاء، والراء المهملتين، أي: تعطفوهم، وتثنوهم.
قالى في النِّهاية: "ومن غريب ما يحكي فيه عن نِفْطويه، قال:','2','760',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1537,'إنه بالظاء المعجمة من باب ظأر، ومنه الظَّئر المُرضِعَة، وجعل الكلمة مقلُوبة فقدم الهمزة على الظاء".','2','761',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1538,'٨٣٩ -[٣٠٥٨] "قال: لا، بل أجرُ خمسين منكُمْ".
قال الطيبي: فِيه تأويلان:
أحدهما: أن يكون أجر كل واحد منهم على تقدير أنه غير مبتلى ولم يضاعف أجره.
والثاني: أن يراد أجر خمسين منهم ممن لم يبتلوا ببلائه.
وقال الشيخ كمال الدِّين الزملكاني: " فإن قيل كيف يجمع بين هذا الحديث، وبين قوله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "خير القرون قرني، ثم الذين يلونهم، ثم الذين يلونهم"؟ قلنا: هذا لا يمنع تفضيل الأوَّلين على هؤلاء؛ لأنَّ','2','761',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1539,'غاية ما في هذا أنَّ هؤلاء الأخيرين يعملون على [مشقة] شديدة، إذ القابض على دينه كالقابض على الجمر، فيضاعف ثواب العامل منهم على عمله لقلة من يعمل ذلك العمل، ولا يلزم من ذلك أفضليته على من تقدَّم، بل يكُون ذلك العمل الخاص الذي عمله هذا المتأخر مضاعف الثواب لقلة الأعوان عليه، كما قال - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "إنَّكم تجدون على الخير أعوانًا ولا تجدُون على الشر أعوانًا".
ويمتاز المتقدم بأمور لا يجدها المتأخر توازي هذه المضاعفة في هذه الأعمال الخاصَّة وتفضلها بأضعاف كثيرة، كيف وقد قال النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - في حق الأوَّلين: " لو أنفق أحدكم مثل أُحْد ذهب " ما بلغَ مُدَّ أحدهم، ولا نصيفه " فصحَّ أنَّ خير القرون قرن النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لرُؤيتهم له.
وصلاتهم خلفه، وغزوهم بين يديه وغير ذلك انتهى.
وقال الشيخ عز الدِّين ابن عبد السلام في أماليه: "حمل هذا','2','762',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1540,'الحديث على الإطلاق خطأ، بل هو مبنيٌّ على قاعدتين:
إحداهما: أنَّ الأعمال تشرف بثمراتها.
الثانية: أنَّ الغريب في أول الإسلام هو كالغريب في آخره، وبالعكس لقوله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "بدأ الإسلام غريب وسيعُود كما بدأ، فطوبى للغرباء".
"من أمَّتي" أي المتفردين بالتقوى دُون أهل زمانه، إذا تقرر ذلك فنقول: الإنفاق في أول الإسلام أفضل لقوله عليه السلام لخالد: " لو أنفق أحدكم مثل أحد ذهبًا ما بلغ مد أحدهم ولا نصيفه " أي: مد الحنطة، وسبب ذلك أنَّ تلك النفقة أثمرت في فتح الإسلام وإعلاء كلمة الله ما لا تثمره غيرها وكذلك الجهاد بالنفوس لا يصل المتأخرون فيه إلى فضل المتقدمين وقلَّة أنصارهم، فكان جهادهم أفضل؛ لأنَّ بذل النَّفس مع النصرة، ورجاء الحياة ليس كبذلها مع عدمها، ولذلك قال عليه الصلاة والسلام. "أفضل الجهاد كلمة حق عند سُلطان جائر" لأنه أَيِسَ من حياته، وأما النَّهي عن المنكر بين ظُهور المسلمين وإظهار','2','763',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1541,'شعائر الإسلام فإنَّ ذلك شاق على المتأخرين لعدم المعين وكثرة النكير، فهم كالمنكر على الملك الجائر، ولذلك علل عليه الصلاة والسلام بكون القابض على دينه كالقابض على الجمر والقابض على الجمر لا يستطيع دوام ذلك لمزيد المشقة فكذلك المتأخر في دينه، وأما المتقدمون فليسوا كذلك لكثرة المعين، وعدم المنكر فعلى هذا يُنزَّل الحديث " انتهى.','2','764',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1542,'٨٤٠ -[٣٠٥٩] "ففقَدُوا جامًا من فضلة مخوصًا بالذَّهب".
قالى في النِّهاية: " أي عليه صفائح الذهب مِثل خُوص النَّخل ".','2','764',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1543,'٨٤١ -[٣٠٧٦] "لمَّا خلق الله آدم مسَحَ ظهرَهُ".','2','764',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1544,'قال البيضاوي: " يحتمل أن يكون الماسح هو الموكل على تصوير الأجنة وتخليقها، وجمع موادها وإعداد عددها، وإنما أسند إلى الله تعالى من حيث هو الآمر به كما أسند إليه التوفي في قوله تعالى: {اللَّهُ يَتَوَفَّى الْأَنْفُسَ} والمتولي لها هو الملائكة لقوله تعالى: {الَّذِينَ تَتَوَفَّاهُمُ الْمَلَائِكَةُ} ويحتمل أن يكون الماسح الباري تعالى، والمسح من باب التمثيل، وقيل هو من المساحة بمعنى التقدير، كأنه قال قدَّر ما في ظهره من الذرية فسقط من ظهره.
"كل نسْمَةٍ".
قال الطيبي: " النسمة: كل ذي روح، وقيل كل ذي نفس، مأخوذة من النسيم.
"هوَ خالقُهَا" قال الطيبي: "صفة "نسمة" ذكرها لتعلق به إلى يوم','2','765',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1545,'القيامة.
وقوله: " وجعل بيْنَ عَيْنَيْ كُلَّ إِنْسَانٍ منْهُمْ وَبِيصًا" إيذانًا بأنَّ الذرية كانت في صُورة إنسان على مقدار الذرِّ، والوبيص: البريق واللمعان، وفي ذكره تنبيه على الفطرة السَّليمة الأصلية.
"فرأى رجُلاً منهُمْ فأعجَبَهُ وبِيصُ ما بينَ عَيْنَيْهِ"
قال الطيبي: " في تخصيص العجب من وبيص داود: إظهار كرامة من كراماته، ومدح له، فلا يدل على تفضيله على الغير؛ فإنَّ في الأنبياء من هو أفضل وأكثر كرامة، قال: وفيه إشارة إلى حديث "يهرم بن آدم، ويشِبَّ منه اثنتان: الحرص على المال، والحرص على العُمر".
قُلتُ: الذي عندي في توجيه حب آدم الحياة، وموسى، ونحوهما أنهم لم يحبوا الحياة لذاتها، ولا كراهة للموت، معاذ الله، ولكن حُبِّب إليهم عبادة الله، ومحلها دار الدنيا، وبالموت ينقطع التكليف بالعبادة، فأحبُّوا طول البقاء ليستكثروا من العبادة.','2','766',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1546,'٨٤٢ -[٣٥٨١] "يَهْتِفُ بِربِّهُ" أي يصيح به، ويدعوه، فأتاه','2','766',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1547,'أبو بكْرٍ فأخذ رِدَاءهُ فألقاهُ على منكبَيْهِ ثُمَّ التَزَمَهُ من ورائه فقال: يا نَبيَّ الله! كفاك مُناشدتُك ربَّك، إنَّه سيُنْجِزُ لَكَ مَا وَعَدَك". قاله السبكي.','2','767',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1548,'٨٤٣ -[٣٠٨٧] "فإنَّما هنَّ عَوَانٍ عنْدَكُمْ" قال في النِّهاية: "أي','2','767',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1549,'أسرى، أو كالأسرى".
"عن زيد بن يُثَيعٍ".','2','768',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1550,'٨٤٤ -[٣٠٩٤] "لو علمنا أيُّ المَالِ خيرٌ فَنَتَّخذهُ".
قال الطيبي: " لو للتمني ولذلك نصب فنتخذه "وأي" رفع بالابتداء، والخير: خبر، والجملة سادَّة مسَد الفعلين لـ "علمنا" تعليقًا.','2','768',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1551,'٨٤٥ -[٣١٠٢] "فخرجت قُرَيْشٌ مُغيثين لِعِيرهم".','2','768',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1552,'قال في النهاية: " أي: مُغوثين، فجاء به على الأصل ولم يُعِله كاستحوذ، واستنوق.
قال: ولو رُوى "مُغَوِّثين" بالتشديد من غوَّث بمعنى أغاث لكان وجهًا".
"بعث إليَّ أبو بكرٍ الصِّدِّيق مَقْتَلَ أهْلِ اليَمَامَةِ" قال الطيبي:','2','769',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1553,'"مقتل: ظرف زمان؛ أي أيام قتل أهل اليمامة، واليمامة بلاد الحر".
"قد اسْتَحر" قال في النِّهاية: " أي كثُر واشتدَّ وهو استَغْفَلَ من الحَرِّ الشَّديد ".
"هُوَ والله خيرٌ". قال الطيبي: " ردٌّ لقوله: كيف أفعل شيئًا لم يفعله رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، وإشعار بأنَّ من البِدَعِ ما هو حسن وخير.
"والعُسُبِ" جمع عسيب، وهو سعف النخل.
"واللخافِ" جمع لخفة، وهي الحجارة البيض الرقاق".','2','770',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1554,'٨٤٧ -[٣١٠٤] "فأرسل إلى حفصة أن أرسلي إلينا بالصحف".','2','770',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1555,'قال السخاوي في "شرح الرائية" قيل: ما قصد عثمان بإرساله إلى حفصة وإحضاره الصحف وقد كان زيدٌ ومن أضيف إليه','2','771',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1556,'حفظة؟ قلت الغرض بذلك سد باب المقالة وأن يزعم زاعم أنَّ في المصحف قرآنًا لم يكتب، ولئلا يرى إنسان فيما كتبوه شيئًا مما لم يقرأ به فينكره، فالصحف شاهد بصحة جميع ما كتبوه.
"ما اخْتَلَفْتُمْ فِيهِ أَنْتُمْ وزيد بن ثَابِتٍ فاكتُبُوهُ بِلسَانِ قرَيشٍ فإنَّما نزل بلسانهم".
قال الطيبي: " فإن قلت كيف الجمع بين هذا، وبين قوله: " أنزل القرآن على سبعة أحرف ". أي لغات، قلت: الكتابة والإثبات في المصحف بلغة قريش لا يقدح في القراءة بتلك اللغات.
وقوله: " إنما أنزل [بلسانهم، يريد به أنَّ أول ما أنزل] بلغة قريش وهي الأصل ثم خفف ورخَّص أن يقرأ بسائر اللغات.','2','772',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1557,'٨٤٨ -[٣١٠٧] "من حالِ البَحر".','2','772',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1558,'قال في النِّهاية: "الحالُ: الطين الأسود كالحمأة".','2','773',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1559,'٨٤٩ -[٣١٠٩] "أين كان ربُّنَا قبلَ أن يَخْلُقَ خَلْقَهُ؟ قَالَ: كان في عَمَاء".
قال في النِّهاية: " العماء بالفتح والمد: السَّحاب.
قال أبو عبيد: لا يُدْرَى كيْفَ كان ذلك العماء.
قال: وفي رواية: "كان في عمًى" بالقَصْر، ومعناه ليس معه شيء وقيل: هو كل أمرٍ لا تُدركه عُقُول بني آدم، ولا يبْلُغُ كُنْهَهُ الوَصْفُ والفِطَنُ وَلاَ بُدَّ في قوله: "أيْنَ كَانَ رَبُّنَا" من مُضاف محذوف، كما حذف في قوله: {جَاءَهُمْ رَسُولٌ مِنْ عِنْدِ اللَّهِ} ونحوه فيكون','2','773',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1560,'التَّقدير أين كان عرْش ربِّنا؟ ويدل عليه قوله: " وَكَان عرشه على الماء ".
قال الأزهري: نحنُ نؤمن به ولا نكَيِّفه بصفة: "أي نُجْرِي اللَّفظ على ما جاء عليه من غير تأويلٍ" انتهى.','2','774',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1561,'٨٥٠ -[٣١١٦] "إِلا في ذِرْوَةٍ" بكسر الذال المعجمة، أي: ثروة.','2','774',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1562,'٨٥١ -[٣١١٧] "مخاريق" قال في النِّهاية: "جمع مِخْراق، وهو','2','774',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1563,'في الأصل ثوب يُلف ويضْرِب به الصبيان بعضهم بعضًا، أراد أنها آلة يزجر بها السحاب ويسوقه".
"عِرقَ النّساء" قال في النِّهاية: " بوزن العصا: عِرْق يخرج من الوَرِكِ فيستبْطِن الفخِذَ.
قال: والأفصح أن يقال له النَّسا.
لا عِرق النِّساء".','2','775',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1564,'٨٥٢ -[٣١٢٧] "اتَّقُوا فِرَاسَةَ المُؤمِنِ".
قال في النِّهاية: " الفراسة تقال على معنيين، أحدهما: ما دلَّ ظاهر هذا الحديث عليه، وهو ما يُوقِعُهُ الله تعالى في قلوب أوليائه، فيعلمون أحوال بعض النَّاس بنوع من الكرامات وإصابة الظَّن والحدْس.
والثاني: نوع يُتَعلَّم بالدلائل والتجارب والخَلق والأخلاق، فيُعْرَفُ به أحوال النَّاس، وللنَّاس فيه تصانيف قديمة وحديثة".','2','775',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1565,'٨٥٣ -[٣١٣٠] "مُضْطَرِبُ" قال في النِّهاية: "هو مُفْتَعِل من','2','775',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1566,'الضَّربِ والطاء بدلٌ من تاءِ الافتعال.
والضرب من الرِّجال الخفيف اللحم الممشُوق، المُسْتَدِقّ".
"رَجِلِ الرَّأْسِ" أي شعره ليس شديد الجُعُودَة ولا شديد السبُوطة بل بينهما.
"كأنَّّهُ من رِجَالِ شَنؤةَ" بشين معجمة مفتوحة ثم نون ثم واو، ثم همز ثم هَاء، قبيلة معروفة.','2','776',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1567,'٨٥٤ -[٣١٣١] "فَارْفَضَّ عَرَقًا" أي جرى عرقه وسال.
"قال جِبْرِيلُ بِإِصْبُعِهِ" من إطلاق القول على الفِعل.
قال في النِّهاية: "العرب تَجْعَل القَوْلَ عبارة عن جميع الأفعال،','2','776',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1568,'وتُطلقه على غير الكلام، واللسان، فتقول قال بيده: أي أخذ: وقال برجله: أي مشى. وقالت له العينانِ سمْعًا وطاعة؛ أي أومأت.
وقال بالماء على يده: أي قلب، وقال بثوبه: أي رفعه وكلُّ ذلك على المجاز والاتِّساع.','2','777',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1569,'٨٥٥ -[٣١٣٨] "يَطْعَنُهَا" بضم العَين.
"بِمِخصَرَةٍ". قال في النِّهاية: " المِخْصرة: ما يخْتَصره الإنسان بيده فيُمسكه من عصى، أو عُكَّازةٍ، أو مِقْرَعَةٍ، أو قضيب ".','2','777',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1570,'٨٥٦ -[٣١٤٤] "مَن احْتَجَ بالقرآن فقد أفْلجَ" بفاء، ولام،','2','777',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1571,'وجيم، أي غلب.','2','778',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1572,'٨٥٧ -[٣١٤٧] "ثُمَّ رَجَعَا عَوْدَهُمَا علَى بَدْئِهِمَا".
قال أبو حيان في الارتشاف: رجع عوده على بدئه عند الكوفيين، منصوب على المصدر أي عاد على بدئه، وأجاز بعضهم نصْبه على المفعول أي رد عوده على بدئه، وأما عند أصحابنا فعلى الحال على التقديرات الثلاث في كلمته: فاهُ إلى فيَّ، على اختلاف قائلها، وإذا انتصب على الحال لم يجُز تقديم المجرور عليه؛ لأنه من صلته، وإن كان مفعولاً جاز، ويجوز رفع عوده فاعلاً برجع، أو مبتدأ خبره: على بدئه، وعلى هذين يجوز تقديم على عوده.
وقال الرضي: " قولهم على بدئه " متعلق بـ "عوده" أو بـ "رجع"','2','778',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1573,'والحال مؤكدة، والبدء مصدر بمعنى الابتداء، جعل بمعنى المفعُول؛ أي عائدًا على ما ابتدأه ويجوز أن يكون عوده مفعولاً مطلقًا لرجع؛ أي رجع على بدئه عوده المعهود؛ كأنه عهد منه أن لا يستقر على ما ينتقل إليه، بل يرجع إلى ما كان عليه قبلُ، فيكون نحو قوله تعالى: {وَفَعَلْتَ فَعْلَتَكَ الَّتِي فَعَلْتَ} .
وقال أبو علي الفارسي: " إنَّ هذا المصدر منصوب على أنه مفعول مطلق للحال المقدر أي رجع عائدًا [عوده] وهو مضاف إلى الفاعل.','2','779',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1574,'٨٥٨ -[٣١٤٨] "أنا سيد ولد آدم يوم القيامة"','2','779',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1575,'قال النووي: " قال الهروي: السيد هو الذي يفوق قومه في الخير، وقال غيره: هو الذي يفزع إليه في النوائب والشدائد فيقوم بأمور ويتحمل عنهم مكارههم، ويدفعها عنهم، والتقييد بيوم القيامة مع أنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - سيدهم في الدنيا والآخرة معناه: أنه يظهر يوم القيامة سؤدده بلا منازع، ولا معاند بخلاف الدنيا فقد نازعه فيها ملوك الكفار، وزعماء المشركين، وهو قريب من معنى قوله تعالى: {لِمَنِ الْمُلْكُ الْيَوْمَ لِلَّهِ الْوَاحِدِ الْقَهَّارِ (١٦) } مع أنَّ الملك له قبل ذلك لكن كان في الدنيا من يدَّعي الملك، أو من يضاف إليه مجازًا فانقطع كل ذلك في الآخرة".
"ولا فخْرَ" قال الطيبي: "حال مؤكدة؛ أي أقول هذا ولا أفخر"
وقال التوربشتي: " الفخر ادِّعاء العظم، والمباهاة بالأشياء الخارجة عن الإنسان كالمال، والجاه ".
وقال النووي: " فيه وجهان:
أحدهما: قاله امتثالاً لأمر الله تعالى: {وَأَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ} .
والثاني: أنه من البيان الذي يحث عليه تبليغه إلى أمته ليعرفوه
ويعتقدوه ويعملوا بمقتضاه في توقيره - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - وقال في النِّهاية: [أي في','2','780',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1576,'قوله: أنا سيد ولد آدم]- قاله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - إخبارًا عما أكرمه الله تعالى من الفضل والسُّؤدد، وتحدثًا بنعمة الله تعالى عنده، وإعلامًا لأُمَّته ليكون إيمانُهُمْ به على حسبه ومُوجبه، ولهذا أتْبَعه بقوله: ولا فخر، أي أنَّ هذه الفَضيلة التي نِلْتُهَا كرَامة من الله تعالى لم أنَلْهَا من قِبل نفسي، ولا بلغتها بقُوَّتي، فليس لي أن أفتخر بها ".
"وَبيَدِي لِوَاءُ الحمْدِ". قال في النِّهاية: " اللواء: الرَّاية، ولا يُمسكُهَا إلا صاحب الجَيْش ".
وقال الطيبي: " يريد به انفراده بالحمد يوم القيامة وشهرته على رؤوس الخلائق.
ويحتمل أن يكون بيده لواء يوم القيامة حقيقةً يسمى لواء الحمد، وعليه كلام التوربشتي حيث قال: " لا مقام من مقامات عباد الله الصالحين أرفع، وأعلى من مقام الحمد ودونه تنتهي سائر المقامات، ولمَّا كان نبينا - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أحمد الخلائق في الدنيا والآخرة أعطي لواء الحمد ليأوي إلى لوائه الأولون، والآخرون، وإليه أشار بقوله: " آدم فمن دُونه تحت لوائي ".
ولهذا المعنى افتتح كِتابه بالحمد، واشتق اسمه من الحمد، فقيل: محمَّد وأحمد، وأقيم يوم القيامة المقام المحمُود، ويفتح عليه في ذلك المقام من المحامد ما لم يفتح على أحد قبْلَهُ، ونعت أمَّته في الكتب الحمادُون.
"وَمَا مِنْ نبِيٍّ يَوْمَئذ". قال في الطَّيِّبي: "نبي نكرة وقعت في','2','781',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1577,'سياق النَّفي وأدخل عليه "منْ" الاستغراقية فيفيد استغراق الجنس.
وقوله: "آدم فمن سِواه"، بدل أو بيان من محله، و"منْ" فيه موصُولة، و"سواه" صِلة، وصحَّ، لأنه ظرف، وأوثر الفاء التفصيلية في "فمن" على الواو، للترتيب، على منوال قولهم: الأمثل فالأمثل.
"مَا حَلَّ بها عن دين الله" أي: دافع وجادل، من المِحال، بالكسر، وهو الكيد، وقيل المكر وقيل القوة، والشِدَّة، وميمه أصليَّة.
"فَأُقَعْقِعُهَا" أي: أحركها لتصوت، والقعقعة، حكاية حركة لشيء يسمع له صوت.','2','782',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1578,'٨٥٩ -[٣١٤٩] "قال يَا مُوسى إنَّك على علم من علم الله علمك الله لا أعلمهُ وأنا على علم من علم الله علمنيه لا تعلمه".','2','782',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1579,'"قال: بغير نوْلٍ" أي بغير أجر ولا جعل، وهو مصدر ناله ينوله: إذا أعطاه.','2','783',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1580,'٨٦٠ -[٣١٥١] "جَلَسَ على فَروَةٍ بَيضَاءَ" قال في النِّهاية: " الفروة الأرض اليابسة، وقيل: الهشيم اليابسُ من النَّباتِ ".
"فاهتزَّت تحته خضراء". قال الطيبي: " إنها تمييز أو حال ".','2','784',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1581,'٨٦١ -[٣١٥١] "نغفًا" بفتح النون، والغين المعجمة وفاء، دود يكون في أنوف الإبل، والغنم، واحدها نغفة.
"وتَشْكُرُوا شُكْرًا" قال في النهاية: " أي تسمن وتمْتلي شحمًا.
يقال: شكرت الشاةُ تَشْكر شَكَرًا، بالتَّحريك إذا سمنتْ وامْتَلأَ ضَرْعُهَا لَبَنًا".','2','784',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1582,'٨٦٢ -[٣١٥٦] "فيشرئبون" أي: يرفعون رُؤسهم لينظروا إليه وكل رافع رأسه مشرائب أي غريب من بين الصفوف؛ [فيضجع ويذبح] .
"ترحًا" هو ضد الفرح، كحُضْر الفرس بضم الحاء المهملة وسكون الضاد المعجمة، وراء: أي عدوه، ثم كشد الرجل: أي عدوه.','2','785',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1583,'٨٦٣ -[٣١٦٩] "فَيئس القَوم" أي: سكتوا [حتَّى ما أبْدَوا','2','785',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1584,'بِضَاحِكَةٍ] .','2','786',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1585,'٨٦٤ -[٣١٧٧] "الخندمة" قال أبو موسى المديني: أظنه جبلاً، وقال في النِّهاية: "هُو جبلٌ معروف عند مكة".','2','786',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1586,'٨٦٥ -[٣١٧٩] "فَتَلَكَّأَتْ" أي توقفت، وتباطأت أن تقولها.','2','786',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1587,'"وتنكَصَت". قال في النِّهاية: " النُّكُوص الرُّجوع إلى وراء وهو القَهْقَرَي ".
"سَابغَ الألْيَتَيْنِ" أي تامَّهما، وعظيمهما.
"خَدَلًّج السَّاقَيْنِ" أي عظيمهما] .','2','787',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1588,'٨٦٦ -[٣١٨٠] "أَبْنُوا أَهْلِي" أي اتَّهمُوها.','2','787',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1589,'"فَبَقَرَت لِي الحَدِيثَ" بالباء الموحدة، وقاف، وراء، أي فتحته وكشفته.
"حَتَّى أسْقَطُوا لها به". قال في النِّهاية: " يعني الجارية: أي سبُّوهَا، وقَالُوا لها من سقط الكلام، وهو رَديئُهُ ".
"ما كَشفتُ كنِفَ أُنثى". قال في النِّهاية: " يجوز أن يكون بكسْر الكاف وسكون النون، من الكنف، وهو الوعاء، وبالفتح والتحريك من الكنف وهو الجانب والناحيَة" أي أقرت.
"يَسْتَوْشِيهِ" أي يستخرج الحديث بالبحث عنه.','2','789',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1590,'٨٦٧ -[٣١٨٥] "وَسَأَبلّها ببلالِها". قال في النِّهاية: "أي','2','789',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1591,'أصِلكم في الدنيا، والبلال: جمع بلل، وقيل هو كُلُّ ما بَلَّ [الحلق من ماءٍ] ، أو لبن، أو غيره".','2','790',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1592,'٨٦٨ -[٣١٨٦] "يَا صَبَاحَاهُ". قال في النِّهاية: " هذه كلمةٌ يقولها المُستغيث، وأصلُهَا إذا صاحوا للغارة؛ لأنهم أكثر ما كانوا يغيرون عند الصَّباح، ويُسَمُّون يوم الغارة يوم الصَّباح، فكأنَّ القائل يا صباحاه.
يقولُ قد غشِينَا العَدُوُّ، وقيل: إنَّ المُتقاتلين كانوا إذا جاء اللَّيلُ يَرْجعُون عن القتال، فإذا عادَ النَّهار عاوَدُوهُ، فكأنه يريد بقوله يا صباحاه: قد جاءْ وقتُ الصَّباح فتأهَّبُوا للقتال ".','2','790',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1593,'- ٣١٨٨ "إنَّ ما حمله عليه الجزع". قال في النِّهاية: "يروى بالجيم والزَّاي وهو الخوف، وقال ثعلب: إنما هو بالخاء والرَّاء وهو الضعف والانكسار في مناجيته. كلمة في مناجيته لم ترو في النهاية ولم يظهر لي وجهها فليتأكد.
- ٣١٩٢ " {الم (١) غُلِبَتِ الرُّومُ (٢) } " بنون وحاء مهملة بعدها باء موحدة؛ أي مراهنته لقريش بين الروم، والفرس.','2','791',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1594,'٨٦٩ -[٣١٩٥] "لاَ تَبيعُوا القينات" أي الإماء المغنيات.
- ٣٢٠٠ "طَلْحَةُ مِمَّنْ قَضى نحْبَهُ". قال في النِّهاية: "النَّحْب:','2','791',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1595,'النَّذْرُ، كأنه ألزم نفسه أن يصدقَ أعداء الله في الحرب فوفَى به.
وقيل: الموت، كأنه ألزم نفسه أن يقاتل حتى يموت".','2','792',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1596,'٨٧١ -[٣٢٠٥] "فَجَلَّلَهُمْ" أي غشاهم.','2','792',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1597,'٨٧٢ -[٣٢٢٠] "قُولُوا اللَّهُمَّ صَل على محمَّد، وعَلَى آل محمَّدٍ"','2','792',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1598,'قال الرافعي في تاريخ قزوين: " قولنا: اللَّهم صلِّ على محمَّد قيل في تفسيره: عظِّم محمَّدًا في الدنيا بإعلاء ذكره وإدامة شرعه وفي الآخرة بتشفيعه في أمته وإجزال مثوبته وإبداء فضله للأولين [والآخرين] بالمقام المحمود، وتقديمه على كافة المؤمنين بالشُّهود وهذه أمور قد أنعم الله تعالى بها عليه لكن لها درجات ومراتب، وقد يزيدها الله تعالى بدعاء المصلين عليه.
ويذكر أنَّ أصل الصلاة في اللِّسان التعظيم.
والآل في قولنا: "اللَّهمَّ صلِّ على محمَّد وعلى آل محمَّد" فسره الشافعي في رواية حرملة ببني هاشم، وبني المطلب، ويوافقه ما ورد في الحديث: "لا تحل الصدقة لمحمَّد ولا لآل محمَّد" فيدخل في "آله" زوجاته، ألا ترى إلى قول عائشة رضي الله عنها: "كُنَّا آل محمَّد نمكث شهرًا ما نستوقد نارًا" وأيضًا فأصل "آل" أهل، ولذلك إذا صُغر قيل: أهيل، ردًا إلى الأصل، ولا شكَّ في وقوع اسم الأهل على الزوجة. انتهى.','2','793',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1599,'٨٧٣ -[٣٢٢١] "أُدرَةٌ" بالضم نفخة في الخصيبة.','2','793',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1600,'"وَطَفِقَ بِالحَجَرِ ضَرْبًا"
قال الطيبي: " بالحَجَرِ متعلق بخبر "طفق"؛ أي طفق يضرب بالحجر ضربًا ".
"إِنَّ بالحجر لَنَدبًا" قال في النِّهاية: "النَّدْبُ بالتحريك أثر الجُرح إذا لم يرتفع عن الجلد، فشُبِّه به أثر الضرب في الحجر".','2','794',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1601,'٨٧٤ -[٣٢٢٣] "علَى صفْوَانٍ" قال في النهاية: "هُو الحَجَرُ','2','794',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1602,'الأمْلَسُ، وجمعه صُفيٌّ، وقيل: هو جمع، واحدُهُ صَفْوَانَةٌ".','2','795',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1603,'٨٧٥ -[٣٢٣٣] "أتانِي اللَّيْلَةَ رَبِّي تَبَارَكَ وَتَعَالَى في أَحْسَنِ صُوَرَةٍ" قال في النِّهاية: " الصُّورة ترِدُ في كلام العَرَب على ظاهرها، وعلى معنى حقيقة الشيء وهيْئَتِهِ [وعلى معنى صفته، يقال: صُورَةُ الفِعل كذا وكذا: أي هيئتهِ] . وصورة الأمر كذَا وكذا: أي صِفتُهُ فيكون المراد بما جاء في الحديث أنه أتاه في أحسنِ صِفته.
ويجوز أن يعُود المعنى إلى النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: أي أتاني ربي وأنا في أحسن صُورة. وتجري معاني الصورَةِ كلِّها عليه، إن شئت ظاهرها أو هيئاتِهَا، أو صِفتها فأما إطلاق ظاهر الصُّورة على الله تعالى فلا،','2','795',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1604,'تعالى الله عن ذلك عُلُوًّا كبيرًا".
وقال البيضاوي: " إذا كان ذلك رؤيا رآها في المنامِ فلا إشكال إذ الرائي قد يرى غير المتشكل متشكلاً، ويرى المتشكل غير متشكل ثم لا يعد ذلك خللاً في الرؤيا ولا في خلل الرائي بل له أسباب أخر تذكر في علم المنامات، ولولا تلك الأسباب لما افتقرت رؤيا الأنبياء عليهم الصلاة والسلام إلى التعبير وإذا كان ذلك في اليقظة فلا بد من التأويل، فنقول: صُورة الشيء ما يتميز به الشيء عن غيره سواء كان عين ذاته أو جزئه المميز، وكما يطلق ذلك في الجثث يطلق في المعاني، فيقال: صُورة المسألة كذا، وصورة الحال كذا، وصُورته تعالى -والله أعلم-.
ذاته المخصوصة المنزهة عن ممثالة ما عداه من الأشياء، كما قال تعالى: {لَيْسَ كَمِثْلِهِ شَيْءٌ} البالغة إلى أقصى مراتب الكمال.
وقال المطُهَّري: "إذا أجريت الصُورة على الله تعالى ويراد به الصفة، كان المعنى: إنَّ ربي تعالى كان أحسن إكرامًا، ولطفًا ورحمة عليَّ من وقت آخر.
وإذا أجريت على النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، كان المعنى: أنا في تلك الحالة كنت في أحسن صُورة، وصِفة من غاية إنعامه ولطفه تعالى عليَّ
وقال التوربشتي: " مذهب أكثر أهل العلم من السلف، في أمثال هذا الحديث أن نؤمن بظاهره، ولا يفسر بما يفسر به صفات الخلق، بل ينفي عنه الكيفية ويوكل علم باطنه إلى الله تعالى فإنه سُبحانه وتعالى','2','796',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1605,'يُري رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - ما يشاء من وراء أستار الغيب مما لا سبيل لأحدٍ إلى إدراك حقيقته بالجد والاجتهاد، فالأولى: أن لا يتجاوز هذا الحد فإنَّ الخطب فيه جليل والإقدام على منزله اضطربت عليها أقدام الراسخين شديد، ولأن نرى أنفسنا أحقاء بالجهل والنقصان أزكى وأسلم وهذا لعمر الله هو المنهج الأقوم والمذهب الأحوط.
"فيمَ يَخْتَصِمُ المَلأُ الأعلى". قال في النِّهاية: " يريد الملائكة المقرَّبين " وقال التوربشتي: " المراد [بالاختصام] التقاوُل الذي كان بينهم في الكفارات والدرجات، شبه تقاولهم [في ذلك وما يجري بينهم عن السُّؤال والجواب بما يجري بين المتخاصمين.
وقال البيضاوي: " اختصامهم إما عبارة عن تبادرهم إلى كتب تلك الأعمال، أو الصعود بها إلى السماء، وإمَّا عن تقاولهم في فضلها، وشرفها، وأناقتها على غيرها، وإما عن اغتباطهم النَّاس بتلك الفضائل لاختصاصهم بها وتفضيلهم على الملائكة بسببها مع تهافتهم في الشَّهوات، وتماديهم في الجنايات.
"فوَضَعَ يَدَهُ بين كتفي". قال البيضاوي: "هو مجاز عن تخصيصه إياه بمزيد الفضل عليه وإيصال فيضه إليه؛ لأنه من ديدن','2','797',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1606,'الملوك إذا أرادُوا أن يدنو إلى أنفسهم بعض خدمهم، ويسرهم بعض أحوال مملكتهم يضعُون يدهم على ظهره تلطفًا به، وتعظيمًا لشأنه، وتنشيطًا له في فهم ما يقول، فجعل ذلك حيث لا يد ولا وضع حقيقة كناية عن التخصيص بمزيد الفضل والتأييد وتمكين الملهم في الروع.
وقوله: "حتى وَجَدْت بردَهَا بينَ ثَدْيَيَّ" كناية عن وصول ذلك الفيض إلى قلبه وتأثره عنه، ورسُوخه فيه، وإتقانه له، يقال ثلج صدره، وأصابه برد اليقين لمن تيقن الشيء وتحققه.
وقوله: "فعلِمْت ما في السَّموات وما في الأرض" يدل على أنَّ وصول ذلك الفيض صار سببًا لعِلمِهِ، وفي بعض طرق الحديث زيادة.
{وَكَذَلِكَ نُرِي إِبْرَاهِيمَ مَلَكُوتَ السَّمَاوَاتِ وَالْأَرْضِ} على سبيل الاستشهاد، والمعنى أنه تعالى كما أرى إبراهيم عليه السلام ملكوت السموات والأرض، وكشف له ذلك، فتح عليَّ أبواب الغيوب حتى علمته ما فيها من الذَّوات، والصفات، والظواهر والمغيبات.
"في الكفَّاراتِ" قال في النهاية: " هي عبارة عن الغفلة، والخَصْلَة الَّتِي من شأنها أن تُكفِّر الخطيئة: التي تسْتُرُهَا وتَمْحُوهَا وهي فعَّالَة للمبالغة، ضَرَّابَة، وهيِ منِ الصِّفات الغالبة في باب الاسميَّة ".
"من فعل ذلك عاش بِخيْرٍ".
قال البيضاوي: " هو من قوله تعالى: {مَنْ عَمِلَ صَالِحًا مِنْ ذَكَرٍ أَوْ أُنْثَى وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهُ حَيَاةً طَيِّبَةً} أي لنرزقنه في الدنيا حياةً طيبة، وذلك أنَّ المؤمن مع العمل الصَّالح موسرًا كان أو معسرًا [يعيش عيشًا','2','798',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1607,'طيبًا، إن كان موسرًا فلا يقال فيه، وإن كان معسرًا] فمعه ما يطيب عيشه، وهو القناعة والرضى بقسمة الله [تعالى] .
وأما الفاجر فأمره على العكس، إن كان معسرًا فلا إشكال في أمره، وإن كان موسرًا فالحرص لا يدعه أن يَتَهَنَّى بعيشه، قال: ومعنى قوله: "ومات بخيرٍ" أنه يأمن في العاقبة ويكون له روح، وريحان إذا بلغت الحلقوم ويقال: {يَا أَيَّتُهَا النَّفْسُ الْمُطْمَئِنَّةُ (٢٧) ارْجِعِي إِلَى رَبِّكِ رَاضِيَةً مَرْضِيَّةً (٢٨) فَادْخُلِي فِي عِبَادِي (٢٩) وَادْخُلِي جَنَّتِي (٣٠) } .
"وَإِذا أَرَدْتَ بعبادِك فِتْنَةً فاقْبِضني إليك غير مفْتُونٍ". قال المُطهَّري: " إذا أردت أن تضل قومًا عن الحق قدِّر موتي غير مفتون أي: غير ضال.
"وَالدَّرَجَاتُ؛ إفشَاءُ السَّلاَمِ". قال الطيبي: " مبتدأ أو خبر، أي ما يرفع به الدرجات أو يوصل إلى الدرجات العالية هذه الخصال الثلاث ".','2','799',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1608,'٨٧٦ -[٣٢٣٨] ["جَاءَ يَهُودِيٌّ فقال: يا محمَّد إنَّ الله يُمْسِكُ السَّمَوَاتِ على إصبع ... الحديث"] .','2','799',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1609,'٨٧٧ -[٣٢٤٣] "كَيْف أنعَمُ" قال في النِّهاية: " أي كيف أتنعَّم، من النَّعمة، بالفتح، وهي المسرَّة والفرح والتَّرَفُّه ".','2','800',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1610,'٨٧٨ -[٣٢٤٥] "فلا أدري أرفَعَ رأسه قبْلِي أم كان ممَّن استثْنى الله. من قال: أنا خيرٌ من يُونس بن مَتَّى فقد كذبَ".','2','800',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1611,'٨٧٩ -[٣٢٤٦] "وَإِنَّ لَكُمْ أَنْ تنْعَموا فَلا تَبأسُوا أَبدًا".','2','800',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1612,'قال في النِّهاية: "بؤس، يَبْؤُس، بالضم فيهما بأسًا، إذا اشتد".','2','801',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1613,'٨٨٠ -[٣٢٥٣] "مَا ضَلَّ قومٌ بعدَ هُدًى كَانُوا عَلَيْهِ إِلاَّ أُوْتُوا الجَدَلَ".
قال الطيبي: "أوتوا" حال، وقد مقدرة والمستثنى منه أعم عام الأحوال وصاحبها الضمير المستقر، في خبر كان، والمعنى: ما ضلَّ قوم مهديون كائنين على حال من الأحوال إلاَّ على إيتاء الجدل، يعني من ترك سبيل الهدى وركب متن الضلال، عارفًا بذلك لا بد أن يسلك طريق العِناد واللجَاج ولا يتمشى له ذلك إلاَّ بالجَدَل.
وقال البيضاوي: " المراد بهذا الجدل العِناد، والمراء والتعصُّب،
ثم تلا رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - هذه الآية: {مَا ضَرَبُوهُ لَكَ إِلَّا جَدَلًا بَلْ هُمْ قَوْمٌ خَصِمُونَ} .','2','801',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1614,'قال الطيبي: " فإن قلت: كيف طابق هذا المعنى معنى الآية حتى استشهد بها؟ قلت: من حيث إنهم عرفوا الحق بالبراهين الساطعة ثم عاندوا وانتهزوا مجالاً للطعن، فلما تمكنوا مما التمسوه جادلوا الحق بالباطل، وهكذا دأب الفرقة الزائفة من الزنادقة وغيرها ".','2','802',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1615,'٨٨١ -[٣٢٥٤] "فَأحصَّت كل شيءٍ" أي: أذهبته.
"إِذا رأى مخيلةً" قال في النِّهاية: "المخيلة: موضع الخَيْل،','2','802',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1616,'وهو الظنُّ، كالمظِنَّة، وهي السحابة الخليقة بالمطر.
ويجوز أن تكون مُسَمَّاةً بالمخيلة التي هي مصدرٌ كالمحبسة من الحَبْسِ".','2','803',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1617,'٨٨٣ -[٣٢٥٨] "اغْتِيْلَ" قال في النِّهاية: "الاغتيال أن يُخدع، ويُقتل في موضع لا يراه فيه أحدٌ".
["اسْتُطِيرَ" أي ذهب فيه بسرعة كأنَّ الطير حملته أو اغتاله أحد] .','2','803',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1618,'"كُلُّ عظم لم يُذكَرُ اسْمُ اللهِ عَلَيْهِ" في رواية مسلم: " كُلُّ عَظم ذُكِر اسمُ الله عَلَيْهِ ".
قال بعضهم: رواية مُسلِمٍ في حق المؤمنين، ورواية المصنف في حق غيرهم.
قال السُّهيلي: " وهذا قول صحيح تعضده الأحاديث ".','2','804',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1619,'٨٨٤ -[٣٢٦١] "مَنُوطًا" أي معلقًا.
"بِالثُّرَيَّا" قال ابن يعيش في شرح المفصَّل: " الثريَّا تصفير [الثروى فُعْلَى] من الثروة، قيل لها ذلك لكثرة كواكبها، وهي سبعة، أو نحوها، قال الشَّاعر:
خليلي أني للثريا لحاسد ... وإني على ريب الزمان لواجد','2','804',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1620,'تجمَّع منها شملها وهي سبعة ... وأُفْقِدُ من أحببته وهو واحد وأصلها ثريوا، فاجتمعت الواو والياء، وقد سبق الأول منهما بالسكون فقلبت الواو ياء، وأدغمت الياء في الياء على حدِّ سيِّد، وميِّت، ثم دخلت عليها الألف، واللام للعهد ثم غلب اللفظ على هذه الكواكب دون سائر ما يوصف بالثروة، والكثرة انتهى.','2','805',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1621,'٨٨٥ -[٣٢٦٢] "نَزَرْتَ رَسولَ اللهِ" أي ألححت عليه في المسألة.
"فَما نشِبْتُ" أي: لبثت.','2','805',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1622,'٨٨٦ -[٣٢٦٣] "هئيئًا، مريئًا" قال أبو حيان في الارتشاف:','2','805',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1623,'"قال سيبويه: "هنيئًا مريئًا" صفتان نصبوهما على نصب المصادر المدعو بها في الفعل غير المستعمل إظهاره للدلالة التي في الكلام عليه، كأنهم قالوا: ثبت ذلك هنيئًا مريئًا وهناه هنيئًا ففي تقدير ثبت يكون حالاً مبنية وفي تقديره "هناه" يكون حالاً مؤكَّدة وأجاز أبو البقاء العكبري أن يكون مصدرين جاءا على وزن فعيل كالصَّهيل والنَّكير.
"مريئًا" تابع لهنيء وزعم بعضهم أنَّ مريئًا يستعمل وحده غير تابع لهنيءٍ ولا يحفظ ذلك. وإذا قلت هنيئًا مريئًا، فمري، صفة لهنيء عند بعضهم، وبه قال أبو الحسن الحوفي.
وذهب الفارسي إلى أنَّ مريئًا انتصب انتصاب هنيئًا، التقدير عنده: ثبت مريئًا','2','806',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1624,'٨٨٧ -[٣٢٧٠] "عبيَّة الجاهلية" قال في النِّهاية: "يعني الكِبْر','2','806',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1625,'وتُضم عينُها وتكسر، وهي فُعُّولة أو فُعِّيلة، فإن كانت فُعُّولة فهي من التبعية، لأنَّ المُتَكَبر ذو تكلُّف وتبعية خلا من استرسل على سجيَّتِهِ، وإن كانت فُعِّيلة فهي من عُبَاب الماء، وهو أوله وارتفاعهُ، وقيل: " إنَّ اللام قُلبت ياءً كما فعلوا في: تقضِّي البازي ".','2','807',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1626,'٨٨٨ -[٣٢٧٢] "لاَ تَزَالُ جَهَنَّمُ تَقُولُ هَلْ مِنْ مَزِيدٍ حتَّى يَضَعَ فِيهَا ربُّ العزَّة قَدَمَهُ".
قال في النِّهاية: " أي الذين قدَّمَهُمْ لها من شِرَارِ خَلْقِهِ، فهُمْ قَدَمُ اللهِ للنَّار، كما أنَّ المسلمين قدَمُهُ للجنَّةِ. والقدم: كلُّ ما قدمت من خير','2','807',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1627,'أو شر.
وقيل: وضع القدم على الشيء مثَل للرَّدع والقمع، فكأنه قال: يأتيها أمْرُ اللهِ فيُكفها عن طلب المزيد.
"وقيل أراد به تسكين فَوْرَتهَا كما يقال للأمر تُريد إبطاله؛ وضعته تحت قدَمِي".
"فَتَقُولُ: قطْ. قَطْ".
قالَ في النِّهاية: "بمعنى حسب، وتكرارها للتأكيد، وهي ساكنة الطاء مخفَّفة".
"ويُزْوى" بالزاي، أي يجمع، ويطوى، ويضم.','2','808',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1628,'٨٨٩ -[٣٢٧٣] "عَلَى الخَبِيرِ سَقَطَتْ".
قال في النِّهاية: " أي على العارف به وقعت، وهو مثَل سائرٌ للعربِ ".','2','808',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1629,'"غنته الجرادتان".
قال في النِّهاية: "هُمَا مُغَنِّيتَان كانتا بمكة في الزَّمن الأوَّل، مشهورتان بِحُسْن الصَّوْتِ والغناء".
"خُذْهَا رَمَادًا رِمْدِدًا". قال في النِّهاية: " الرِّمْدِد بالكسر، المُتَناهي في الاحتراق والدِّقة، كما يقال ليلٌ ألْيَل، ويَوْمٌ أيْوَم، إذا أرادُوا المبالغة".','2','809',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1630,'٩٩٠ -[٣٢٧٦] "المُقْحِمَاتِ" قال في النِّهاية: "أي الذُّنُوب العظام التي تُقْحِمُ أصحابها في النَّار: أي تُلقيهم فيها".','2','809',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1631,'٨٩١ -[٣٢٧٨] "قَفَّ لَهُ شَعْرِي" أي قام من الفزع.','2','809',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1632,'٨٩٢ -[٣٢٨٣] "فِي حُلَّةٍ من رَفْرَفٍ" هو الديباج الرقيق الحسَن الصنعة، وجمعه رفارف، وقيل هو جمع، واحده رفرفة.','2','810',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1633,'٨٩٣ -[٣٢٨٤] عن ابن عباس: {الَّذِينَ يَجْتَنِبُونَ كَبَائِرَ الْإِثْمِ وَالْفَوَاحِشَ إِلَّا اللَّمَمَ} . زاد ابن جرير قال: " هو الرَّجل يلم بالفاحشة ثم يتوب، قال النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "إنْ تَغْفِرْ اللَّهُمَّ تَغْفِرْ جَمَّا، وَأيُّ عبدٍ لَكَ لاَ ألَمَّا" قال ابن الشجري في','2','810',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1634,'أماليه: " أي لم يلم بالذنوب " وهذا مما تمثل به النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - من أشعار الجاهلية، أخرج ابن جرير في تفسيره: " عن مجاهد قال: كان أهل الجاهلية يطوفون بالبيت وهم يقولون:
إن تَغْفِر اللَّهُمَّ تَغْفِرْ جَمَّا ... وَأَيُّ عَبْدٍ لَكَ لاَ أَلَمَّا
وقال البيضاوي: " البيت لأميَّة بن أبي الصلت أنشده النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -.
وَقوله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: {وَمَا عَلَّمْنَاهُ الشِّعْرَ وَمَا يَنْبَغِي لَهُ} إنشاء الشعر، لا إنشاده.
وقال الطيبي: "وجه مطابقة الآية وتفسيرها للبيت، أن يقال أنَّ الشرط والجزاء في البيت متحدان، فيدل على كمال الاتحاد الغفران ونهايته، ومجيئهما مضارعين للدلالة على الاستمرار وأنَّ هذا من شأنه تعالى، وكذا الاعتراض بـ "اللَّهمَّ" يدل على فخامة الشأن، أي من شأنك اللَّهمَّ أن تغفر غفرانًا كثيرًا للذنوب العظيمة".','2','811',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1635,'٨٩٤ -[٣٢٩١] "لَقَد قرأتُهَا على الجِنِّ ليْلَةَ الجِنِّ فَكَانوا أَحْسَنَ مَردُودًا مِنْكُمْ" قال الشيخ كمال الدِّين الزملكاني: "ههنا دقيقة لا بد','2','811',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1636,'من التنبيه عليها، وهي أنَّ هذا القول من النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لم يكن تفضيلاً لحال الجن على حال الإنس ولا لأدبهم على أدب الصحابة بل هو تفضيل للجواب على الجواب فإنَّ من عصر النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - من البِشر منهم من أجاب فردوهم المخالفون، والمؤمنون سمعوا وأنصتوا وامتثلوا قوله تعالى: {وَإِذَا قُرِئَ الْقُرْآَنُ فَاسْتَمِعُوا لَهُ وَأَنْصِتُوا لَعَلَّكُمْ تُرْحَمُونَ (٢٠٤) } .
فالصحابة العارفون بالله تعالى أنصتوا لكلامه وتدبروا معانيه وائتمروا بأمره، وانتهوا عن نهيه فلم يقتصروا على عدم التكذيب بل زادُوا عليه بالفهم والعقل، والكفار أجابوا بالرد والتكذيب، والجن اقتصروا على الإيمان فأجابوا بعدم التكذيب [فكان] هذا الجواب أحسن من ذلك الجواب وليس في الحديث ما يدُل على أنَّ جوابهم أحسن من سكوت الصحابة رضي الله عنهم " انتهى.','2','812',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1637,'٨٩٥ -[٣٢٩٦] "رُمُصًا" قال في النِّهاية: " الرمص هو البياض الذي تقْطعه العين، ويجتمع في زوايا الأجفان ".','2','813',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1638,'٨٩٦ -[٣٢٩٧] "شَيَّبَتْني هُودٌ" روى البيهقي، وابن عساكر عن أبي القاسم القشيري، قال: سمعتُ الشيخ أبا عبد الرَّحمن السلمي يقول: سمعتُ أبا علي الشبوي يقول: "رأيتُ النَّبيَّ - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - في','2','813',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1639,'المنام فقلت له رُوِي عنك أنَّك قلت شيبتني هود؟ قال: نعم، فقلتُ له ما الذي شيبك منها، قصص الأنبياء أو هلاك الأمم؟ فقال لاَ، ولكن قوله: "فاستقم كما أمرت".
"والواقعة، والمرسلات، و {عَمَّ يَتَسَاءَلُونَ (١) } و {إِذَا الشَّمْسُ كُوِّرَتْ (١) }','2','814',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1640,'٨٩٧ -[٣٢٩٨] "هذا العَنَانُ" بفتح العين السحاب، الواحدة عنانة.
"رَوَايَا الأرض" قال في النِّهاية: "الرَّوايا من الإبل: الحوَامِل','2','814',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1641,'للماء، واحدتُهَا رَاويَة فشبَّه الصحابة بها وبه سُمِّيت المزاده روايه وقيل بالعكس".
"فَإِنَّهَا الرَّقِيعُ" بالقاف.
قال في النِّهاية: "كل سماءٍ يُقال لها رقِيع، وقيل: الرقيعُ اسمُ سماءِ الدُّنيا".
"وَمَوْجٌ مَكْفوفٌ".
قال الشيخ عز الدِّين ابن عبد السلام في أماليه: "معناه أنَّها للطافتها تخترق كما يخترق الماء".','2','815',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1642,'٨٩٨ -[٣٢٩٩] "قَالَ: أَنْتَ بِذاكَ؟ " قال في النِّهاية: "أي المُبتلى','2','815',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1643,'بذاك".
"وَحْشَي" قال في النِّهاية: " يقال: رجُلٌ وَحْشِيٌّ بالسكون، إذ كان جائعًا لا طعام له، قال: وفي رواية الترمذيِّ: "وحشى" كأنه أراد جماعةً وحْشى ".','2','816',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1644,'٨٩٩ -[٣٣٠٠] "شَعِيرَةٌ" هو ضرب من الحلي أمثال الشعير.
"لزَهِيدٌ" أي قليل الشيء.','2','816',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1645,'٩٠٠ -[٣٣٠٥] "رَوْضةَ خَاخٍ" بخاوين معجمتين موضع بين','2','816',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1646,'مكة والمدينة.
"تَتَعَادَى" أي تعدُوا.
"منْ عِقَاصِهَا" قال في النِّهاية: " أي ضفائرها، جمع عقِيصَة، وعِقْصة، وقيل: هو الخيط الذي يُعْقَصُّ به أطراف الذَّوائب، والأول الوَجْهِ ".
"مُلْصَقًا في قرَيْشٍ". الملصق: هو الرَّجل المقيم في الحي وليس منهم بنسب ".
"وَمَا يُدريك لعلَّ الله اطَّلَعَ عَلَى أَهْلِ بَدْرٍ، فقَالَ: اعْمَلُوا مَا شِئْتُمْ فَقَدْ غَفَرْتُ لَكُمْ"
قال ابن القيم في كتابه المسمَّى بـ "فوائد شتى ونكت حسان": أشكل على كثير من النَّاس، معناه، فإنَّ ظاهره إباحة كل الأعمال لهم وتخيرهم فيما شاؤوا منها، وذلك ممتنع فقالت طائفة منهم ابن','2','817',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1647,'الجوزي: ليس المراد من قوله: "اعْمَلُوا" الاستقبال، وإنما هو للماضي، وتقديره، أي: عمل كان لكم فقد غفرته قال: ويدل على ذلك شيئان:
أحدهما: أنه لو كان للمستقبل كان جوابه قولهُ: سأغفر لكم.
والثاني: أنه كان يكون إطلاقًا في الذنوب، ولا وجه لذلك.
وحقيقة هذا الجواب إنِّي قد غفرت لم بهذه الغزوة ما سلف من ذنوبكم، لكنَّه ضعيف من وجهين:
أحدهما: أنَّ لفظ " اعملوا " يأباهُ؛ فإنه للاستقبال دون الماضي، وقوله: " قد غفرتُ لَكُمْ " لا يوجب أن يكون "اعملوا" مثلهُ؛ فإنَّ قوله: "قد غفرتُ لكُمْ" تحقيق لوقوع المغفرة في المستقبل، كقوله: {أَتَى أَمْرُ اللَّهِ فَلَا تَسْتَعْجِلُوهُ} ونظائره.
الثاني: أنَّ نفس الحديث يرده، فإنَّ سببه قصَّة حاطب وتجسسه على النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - وذلك ذنب واقع بعد غزوة بدر لا قبلها، وهو سبب الحديث فهو مراد منه قطعًا، فالذي يظهر في ذلك -والله أعلم- أنَّ هذا خطاب لقوم قد علم الله سبحانه وتعالى أنهم لا يفارقون دينهم، بل يموتون على الإسلام، وأنهم قد يقارفون بعض ما يقارفه غيرهم من الذنوب، ولكن لا يتركهم سبحانه مُصرِّين عليها، بل يوفقهم لتوبة نصوح واستغفار وحسنات تمحُو أثر ذلك.
ويكون تخصيصهم بهذا دون غيرهِم، لأنه قد تحقق ذلك فيهم،','2','818',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1648,'وأنهم مغفور لهم، ولا يمنع ذلك كون المغفرة حصلت بأسباب تقوم بهم كما لا يقتضي ذلك أن يعطلوا الفرائض، وثوقًا بالمغفرة، فلو كانت قد حصلت بدون الاستمرار على القيام بالأوامر لما احتاجوا بعد ذلك إلى صلاة، ولا صيام، ولا حج، ولا زكاة، ولا جهاد، وهذا محال.
ومن أوجب الواجبات التوبة بعد الذنب فضمان المغفرة لا يوجب تعطيل أسباب المغفرة ونظير هذا قوله في الحديث الآخر: " أذنب عبد ذنبًا فقال: أي رب أذنبتُ ذنبًا فاغفره لي، فغفر له، ثم مكث ما شاء الله أن يمكث ثم أذنب ذنبًا آخر فقال: أي رب أصبت ذنبًا فاغفره لي، فغفر له ثم مكث ما شاء الله أن يمكث ثم أذنب ذنبًا آخر، فقال رب أصبت ذنبًا فاغفره لي، [ثم مكث ما شاء الله أن يمكث ثم أذنب ذنبًا آخر فقال رب أصبت ذنبًا فاغفره فقال الله علم عبدي أنَّ له ربًا يغفر الذنُوب ويأخذ به قد غفرتُ لعبدي فليعمل ما شاء " فليس في هذا إطلاق وإذن منه سبحانه له المحرمات والجرائم، وإنما يدل على أنه يغفر له ما دام كذلك، إذا أذنب تاب.
واختصاص هذا العبد بهذا لأنه قد علم أنه لا يصر على ذنب، وأنه كلما أذنب تاب، حكم يعم كل من كانت حاله حاله لكن ذلك العبد مقطوع له بذلك كما قُطِعَ به لأهل بدر.','2','819',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1649,'وكذلك كل من بشره رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - بالجنة أو أخبره بأنه مغفورٌ له، لم يفهم منه هو ولا غيره من الصحابة إطلاق الذنوب والمعاصي له ومسامحته بترك الواجبات، بل كان هؤلاء أشد اجتهادًا وحذرًا، وخوفًا بعد البشارة منهم قبلها، وكالعشرة المشهود لهم بالجنة.
وقد كان الصديق شديد الحذر والمخافة، وكذلك عمر، فإنهم علموا أنَّ البشارة المطلقة مقيَّدة بشروطها والاستمرار عليها إلى الموت، ومقيَّدة بانتفاء موانعها ولم يفهم أحد منهم من ذلك الإطلاق الإذن فيما شاؤوا من الأعمال " انتهى.','2','820',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1650,'٩٠١ -[٣٣١٥] "كَسَعَ رَجُلاً" أي ضرب دُبره بيده.
"دَعوهَا فَإِنَّهَا مُنْتِنَةٌ". قال في النِّهاية: "أي مذمومة في الشرع مُجْتَنَبةٌ مكروهة.','2','820',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1651,'كما يُجتنَبُ الشيءُ المُنْتِن يريد قولهم: يا آل فُلان".','2','821',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1652,'٩٠٢ -[٣٣١٨] "أُهبَةً" -بفتحات- جمع إهاب، وهو الجلد قبل','2','821',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1653,'الدباغ.','2','822',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1654,'٩٠٣ -[٣٣٢٠] "ثَمَانِيةُ أَوْ عَالٍ" قال في النِّهاية: أي: ملائكة','2','822',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1655,'على صُورة الأوعال، وهيِ تُيُوس الجَبَل واحدُهَا: وَعِلٌ بكسر العيْنِ".','2','823',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1656,'٩٠٤ -[٣٣٢٢] "فروَةُ وَجْهِهِ" قال في النِّهاية: " أي جلدته استعارهَا من الرَّأس للوِجه ".','2','823',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1657,'٩٠٥ -[٣٣٢٥] "فجئثت" بجيم، ثم همزة، ثم مثلثة، أي فزعت','2','823',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1658,'منه وخفت، ويروى بتقديم المثلثة على الهمزة، وبمثلتين.
قال الحربي: جعل الهمزة ثاء.','2','824',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1659,'٩٠٦ -[٣٣٤٠] "لَقِنًا" أي فهمًا، حسن التلقن لما يسمعه.','2','824',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1660,'٩٠٧ -[٣٣٤٣] "رَجُلٌ عَارمٌ" أي خبيث شرير.','2','825',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1661,'٩٠٨ -[٣٣٤٩] "فزبره" أي نهره وأغلظ له في القول.','2','825',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1662,'٩٠٩ -[٣٣٦٨] "وَكِلْتَا يَدَيْ رَبِّي يَمِيْنٌ".
قال في النِّهاية: " أي: أنَّ يديه تبارك وتعالى بصفة الكمال، أي لا نقص في واحدة منهما، لأنَّ الشِّمال تنقُصُ عن اليمين وكلُّ ما جاء في القرآن والحديث من إضافة اليدِ، والأيدي، واليمين وغير ذلك من أسماء الجوارح إلى الله تعالى فإنَّما هو على سبيل المجازِ والاستعارة.
والله تعالى مُنزَّهٌ عن التَّشْبيهِ والتَّجسيم ".','2','826',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1663,'"<span data-type="title" id=toc-55> أبوابُ الدَّعوات </span>"','2','827',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1664,'٩١٠ -[٣٣٧٠] "لَيْسَ شيء أكرم على الله من الدُّعاء"
قال الطيبي: " أكرم بالنصب خبر ليسَ.','2','827',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1665,'٩١١ -[٣٣٧١] "الدُّعاء مُخُّ العِبادَة"
قال في النِّهاية: " مُخُّ الشيء: خالصه، وإنما كان مُخَّها لأمرين: أحدهما: أنه امتثال أمرِ الله تعالى حيث قال: " ادعُونِي " فهو مَحْضُ العِبَادَة، وخالصُهَا.
والثاني: أنه إذا رأى نجاح الأمور من الله قطع أمله عن سواه ودعاه لحاجته وحدَهُ، وهذا هو أصل العبادة، ولأنَّ الغرض من العبادة الثوابُ عليها وهو المطلوب الدُّعاء".','2','827',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1666,'وقال الحكيم في نوادر الأصول: " إنما صار مخًا [لها] لأنه تبرَّؤ من الحول والقوة واعتراف بأنَّ الأشياء كلها له وتسليم إليه ثم يسأله.','2','828',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1667,'٩١٢ -[٣٣٧٢] "الدُّعاء هُوَ العِبادة".
قال الطيبي: " أتى بضمير الفصل، والخبر المعرف باللام ليدل على القصد وأنَّ العبادة ليست عين الدُعاء.
"ثُمَّ قرَأَ: {وَقَالَ رَبُّكُمُ ادْعُونِي} الآية".
قال البيضاوي: " لما حكم بأنَّ الدَّعاء هو العبادة الحقيقيَّة التي تستأهل أن تسمَّى عبادة من حيث إنه يدُل على أنَّ فاعله مقبل بوجهه إلى الله تعالى معرض عمَّن سِواه لا يرجُو ولا يخاف إلاَّ منه استدل عليه بالآية، فإنها تدُل على أنه أمر مأمُور به إذا أتى به المكلف قُبل منه لا محالة وترتب عليه المقصود، ترتب الجزاء على الشرط، والمسبَّب على السبب، وما كان كذلك كان أتم العبادات وأكملها".','2','828',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1668,'٩١٣ -[٣٣٧٣] "من لم يسْأَلِ الله يَغْضَبْ عليه".
قال الطيبي: "وذلك لأنَّ الله تعالى يحب أن يُسأل من فضله، فمن لم يسأله يبغضه، والمبغُوض مغضوب عليه لا محالة".','2','829',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1669,'٩١٤ -[٣٣٧٥] "أَتَشَبَّثُ بِهِ" أي أتعلق به.
"لا يَزَالُ لِسانكَ رَطْبًا من ذكْرِ اللهِ".
قال الطيبي: " رطوبة اللسان [عبارة] عن سهولة جريانه كما أن يبسه عبارة عن ضده، ثم إنَّ جريان اللِّسان حينئذٍ عبارة عن مُداومة الذكر قبل ذلك، فكأنه قيل داوم الذكر فهو من أسلوب قوله تعالى: {وَلَا تَمُوتُنَّ إِلَّا وَأَنْتُمْ مُسْلِمُونَ (١٠٢) } .','2','829',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1670,'٩١٥ -[٣٣٧٧] "أَلاَ أُنبِّئكم بِخَيْرِ أعمَالِكمْ ... الحديث".
قال الشيخ عز الدِّين بن عبد السلام في القواعد: " هذا الحديث يدل على أنَّ الثواب لا يترتب على قدر النصب في جميع العبادات بل قد يأجر الله تعالى على قليل الأعمال أكثر مما يأجر على كثيرها، فإذًا الثواب يترتب على تفاوت الرتب في الشرف ".
"وخَيْرٌ لكم مِنْ إنفاق الذَّهبِ".
قال الطيبي: " مجرور عُطف على "خير أعمالكم" من حيث المعنى؛ لأنَّ المعنى ألا أنبئكم بما هو خيرٌ لكم من بذل أموالكم ونفُوسكم ".','2','830',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1671,'٩١٦ -[٣٣٧٩] "آلله ما أجلسكم".','2','830',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1672,'قال الطيبي: " هو بالنصب؛ أي أتقسمون بالله؟ فحذف الجار، وأوصل الفعل، ثم حذف الفعل ".
"خَرَج على حلقةٍ من أصحابه" بسكون اللام والجمع حِلق، بكسر أوله كبدرة، وبدار، وقِصعة، وقِصع قاله الأصمعي.
وقال غيره: الجمع حلق بالفتح، وهو جمع خارج عن القياس، قال ثعلب كلهم يجيزه على ضعفه.
وقال أبو عمر: الواحد حلَقَة بالتحريك والجمع حلق وَحَلقات.
وعن الشيباني: ليس في الكلام حلقه، إلاَّ قولك حلقة جمع حالق.','2','831',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1673,'٩١٧ -[٣٣٨٠] "كَانَ عَليْهِمْ تِرَةً" أي تبعه.','2','831',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1674,'- ٣٣٨٠ "أفضل الذِّكر لا إله إلاَّ الله وأفضل الدُّعاء الحمد لله".
قال الطيبي: " قال بعض المحققين إنما جعل التهليل أفضل الذكر لأنَّ لها تأثيرًا في تطهير الباطن عن الأوصاف الذميمة التي هي معبودات في الظاهر.
قال الله تعالى: {أَفَرَأَيْتَ مَنِ اتَّخَذَ إِلَهَهُ هَوَاهُ} فيفيد نفي عموم الآلهة بقوله: " لا إله، ويثبت الواحد بقوله: " إلاَّ الله " ويعود الذكر من ظاهر لسانه إلى باطن قلبه فيتمكن فيه ويستولى على جوارحه، وجد حلاوة هذا من ذاق. وأطلق الدعاء على الحمد من باب المجاز ولعله جعل أفضل الدعاء من حيث أنه سؤال لطيف يدق مسلكه.
ومن ذلك قول أميَّة بن أبي الصَلت حين خرج إلى بعض الملوك يطلب نائله.
إذا أثنى عليك المرء يومًا ... كفاه من تعرضه الثناء','2','832',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1675,'وقال المظهري: " إنما كان التهليل أفضل الذكر؛ لأنه لا يصح الإيمان إلاَّ به، وإنما جعل "الحمد" أفضل الدُعاء؛ لأنَّ الدعاء عبارة عن ذكر الله، وأن [يطلب] منه حاجته، و"الحمد لله" يشملها؛ فإنَّ من حمد الله إنما يحمده على نعمته، والحمد على النعمة طلب مزيد.
قال تعالى: {لَئِنْ شَكَرْتُمْ لَأَزِيدَنَّكُمْ} .
قال الطيبي: " ويمكن أن يكون قوله: " الحمد لله " منِ باب التلميح والإشارة إلى قوله: {اهْدِنَا الصِّرَاطَ الْمُسْتَقِيمَ (٦) صِرَاطَ الَّذِينَ أَنْعَمْتَ عَلَيْهِمْ} وأي دعاء أفضل، وأكمل، وأجمع من ذلك! ".
وفي نوادر الأصول للحكيم الترمذي من طريق الجارود قال: كان وكيع يقول: الحمد لله، شكر لا إله إلاَّ الله.
قال الحكيم: فيا لها من كلمة لوكيع لأنَّ لا إله إلاَّ الله أعظم النعم فإذا حمد الله عليها كان في كلمة الحمد قول لا إله إلاَّ الله متضمنة، مشتملة عليها الحمد لله ".','2','833',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1676,'٩١٨ -[٣٣٩٠] "وَأمْسَيْنَا، وأمسى المُلكُ لله والحمد لله" قال','2','833',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1677,'المظهري: " عطف على "أمسينا وأمسى الملك لله " وأمسى إذا دخل في المساء، وأمسى [إذا صار، يعني] دخلنا في المساء، وصرنا نحن، وجميع الملك، وجميع الحمد لله ".
وقال الطيبي: " الظاهر أنه عطف على قوله: " الملك لله " ويدل عليه قوله بعد "له الملك وله الحمد [وقوله] :
"وَأمسى الملك لله" حال من "أمسينا" إذا قلنا أنه فعل تام، ومعطوف على "أمسينا" إذا قلنا أنه ناقص والخبر محذوف لدلالة الثاني عليه، أو خبر، والواو فيه كما في قول الحماسي:
فلما صرح الشر ... فأمسى وهو غير ثان
قال أبو البقاء: "أمسى" هنا ناقصة، والجملة بعدها خبر لها.
فإن قلت: خبر كان مثل المبتدأ، وخبر المبتدأ لا يجوز أن تدخل عليه الواو؟ قيل: الواو إنما دخلت في خبر كان، لأنَّ اسم كان يشبه ْالفاعل، وخبرها يشبه الحال.
وقوله: " لا إله إلاَّ الله وحدَهُ لا شريك له " عطف على "الحمد لله" على تأويل، و"أمسى" الفردانية، والوحدانية مختصين بالله.','2','834',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1678,'فإن قلت: ما معنى "أمسى الملك لله".
والملك له أبدًا، وكذلك الحمد؟ قلت: هو بيان حال القائل أي عرفنا أنَّ الملك، والحمد لله لا لغيره، فالتجأنا إليه واستغنينا به، وخصَّصناه بالعبادة، والثناء عليه والشكر له ".
"وأعوذ بِكَ من الكَسَلِ".
قال التوربشتي: " هو التثاقل عما لا ينبغي التثاقل عنه، ويكون ذلك لعدم انبعاث النَّفس للخير مع ظهور الاستطاعة ".
"وَسُوءِ الكِبرِ" قال في النِّهاية: " يُروى بسكون الباء وفتحها فالسُّكون، بمعنى البطر، والفتح بمعنى الهرم، والخرف ".
قال المظهري: " والفتح أصح ".','2','835',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1679,'٩١٩ -[٣٣٩١] "اللَّهمَّ بكَ أصبَحْنَا" قال الطيبي: " الباء متعلقة بمحذوف، وهو خبر أصح " ولا بد من تقدير مُضاف، أصبحنا','2','835',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1680,'ملتبسين نعمتك أي بحياطتك، وكلآتك، وبذكرك، واسمك "وبك نَحْيَا، وبكَ نَمُوتُ" قال النووي: "أي أنت تحييني، وأنت تميتني، وإليك المصير". قال في النهاية: " أي إليك المرجِع يقال: صرتُ إلى فلان، أصِير مصيرًا. وهو شاذٌّ والقياس مصارا مثل، معاش ".
" وَإِليك النُّشُورُ " يقال: نشر الميت، ينشر نشورًا، إذا عاش بعد الموت.','2','836',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1681,'٩٢٠ -[٣٣٩٢] "ومليكَهُ".
قال الطيبي: "فعيلة، بمعنى فاعل للمبالغة، كالقدير، بمعنى القادر".
"وَمن شرِّ الشَّيطانِ وشِركِهِ"
قال في النِّهاية: " يُروى بكسر الشين، وسكون الراء؛ أي ما يدعُو إليه، ويُوسْوِسُ به من الإشراك بالله تعالى.
وبفتح الشين، والراء: أي حبائله ومصائده. واحدها شرَكة ".
قال الطيبي: "فالإضافة على الثاني محضة، وعلى الأول إضافة','2','836',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1682,'المصدر إلى فاعله".','2','837',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1683,'٩٢١ -[٣٣٩٣] "أَلاَ أَدُلُّكَ على سيِّدِ الاسْتِغْفارِ".
قال الطيبي: " السيد، مستعار من الرئيس المقدم الذي يصمد إليه في الحوائج، ويرجع إليه في الأمور كهذا الدعاء الذي هو جامع لمعاني التوبة كلها. [وقوله] .
"وَأنا عَبْدُكَ" "يجوز أن تكون مؤكدة، وأن تكون مقررة؛ أي أنا عابد لك، وينصره عطفُ".
"وَأنا على عهدك ووعدك ما استطعت". قال البغوي في شرح السنة: " يريد أنا على ما عاهدتك عليه، وواعدتك من الإيمان بك، وإخلاص الطاعة لك. وقد يكون معناه: إنِّي مقيم على ما عاهدتك على أمرك ومتمسِّك ومتنجِّز وعدك في المثوبة، والأجر عليه، واشتراط الاستطاعة في ذلك معناه الاعتراف بالعجز، والقصور عن كنه','2','837',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1684,'الواجب من حقه عزَّ وجل ".
قال الطيبي: " ويجوز أن يُراد بالعهد، والوعد، ما في قوله تعالى {وَإِذْ أَخَذَ رَبُّكَ مِنْ بَنِي آَدَمَ مِنْ ظُهُورِهِمْ ذُرِّيَّتَهُمْ وَأَشْهَدَهُمْ عَلَى أَنْفُسِهِمْ أَلَسْتُ بِرَبِّكُمْ قَالُوا بَلَى شَهِدْنَا} .
"وأبوءُ لكَ" قال في النِّهاية: "أي ألتزم، وأرجع وأُقِرُّ".','2','838',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1685,'٩٢٢ -[٣٣٩٤] "مُت على الفطرةِ" أي دين الإسلام.
"تقول: اللَّهمَّ أَسلمتُ نفْسِي إليك ووجَّهت وجهي إليك، وفوَّضت أمرِي إليك رغبةً ورهْبةً إليك، وألجأتُ ظهري إليك لا مَلْجأ، ولا منجى مِنكَ إلاَّ إليكَ".
قال القرطبي: "المراد بالنفس هنا الذات، وبالوجه القصد".','2','838',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1686,'وقال الطيبي: " في هذا النظم عجائب وغرائب لا يعرفها إلاَّ المتقن من أهل البيان، فقوله: " أسلمتُ نفسي " إشارة إلى أنَّ جوارحه منقادَة لله تعالى في أوامره، ونواهيه.
وقوله: " وَجَّهْتُ، وجْهِي " إلى أنَّ ذاته، وحقيقته مخلصة له بريئة من النفاق.
وقوله: " وَفوَّضْتُ " إلى أنَّ أمُوره الخارجة، والداخلة مفوضة إليه، لا مدبر لها غيره.
وقوله: " ألجأتُ ظَهرِي إليكَ " بعد قوله: " وفوَّضتُ أمري " أي أنه بعد تفويض أموره التي مفتقر إليها وبها معاشه وعليها مدار أمره يلجأ إليه مما يضره ويؤذيه من الأسباب الداخلة، والخارجة.
ثم قوله: "رغبةً، ورهبةً" منصوبان على المفعول [له] على طريقة اللف، والنشر؛ أي: " فوَّضت أمري إليك " رغبةً، و"ألجأت ظهري" من المكاره والشدائد إليك، رهبةً منك؛ لأنه لا ملجأ، ولا منجى مِنْكَ إلاَّ إليْكَ".
وقوله: " رغبةً، ورهبَةً إليكَ " من باب قوله: متقلدًا سيفًا ورمحًا و" ملجأ " مهموز، و" منجا " مقصُور، هُمَز للازدواج " انتهى.
وقال الحافظ ابن حجر: " قد رواه أحمد، والنسائي بلفظ: " رهبةً منك، ورغبةً إليك ". وزاد النسائي في أوله: " بسمِ اللهِ ".
قال البراءُ، فقُلتُ: " ورسُولك الذي أرْسلتَ فطعَن بِيدِهِ في صدري " لفظ النسائي: "فوضع يده في صدري ثم قال: ونبيِّك الذي','2','839',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1687,'أرسلتَ" في رواية، فقال: "قل ونبيِّك".
قال في فتح الباري: " أولى ما قيل في الحكمة في رده - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، على من قال: " الرسُول " بدل "النبي" أنَّ ألفاظ الأذكار توقيفية، ولها خصائص، وأسرار لا يدخلها القياس، فيجب المحافظة على اللَّفظ الذي وردت به وهذا اختيار المازري.
قال: فيقتصر فيه على اللَّفظ الوارد بحُروفه، وقد يتعلق الجزاء بتلك الحروف، ولعله أوحى إليه بهذه الكلمات فيتعيَّن أداؤها بحروفها ".
ورواه منصور بن المعتمر، عن سعد بن عبيدة عن البراء".
قال الحافظ بن حجر: كذا قال الأكثر وخالفهم إبراهيم بن طهمان فقال: عن منصور عن الحكم عن سعد بن عبيدة زاد في الإسناد الحكم، أخرجه النسائي، وقد سأل ابن أبي حاتم عنه أباه فقال:
هذا خطأ ليس فيه الحكم فهو من المزيد في متَّصل الأسانيد".','2','840',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1688,'٩٢٣ -[٣٣٩٦] "وَآوَانَا" قال النووي: " قيل: معناه هنا رحمنا، وقوله: " فكمْ ممَّن لا كافي له ولا مُؤوِي " أي لا رَحم له ولا عاطف عليه ".
وقال المظهري: " الكافي، والمؤوي هو الله تعالى، يكفي بعض الخلق شر بعض، ويهيِّى لهم المأوى، والمسكن ".','2','841',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1689,'٩٢٤ -[٢٣٩٩] "يتوسد يمينه" أي يجعلها تحت رأسه.','2','841',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1690,'٩٢٥ -[٣٤٠١] "فلينفضه بصنفة إزاره" بفتح الصَّاد المهملة،','2','841',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1691,'وكسر النون طرفه مما يلي طُرَّته.
"فإنه لا يدري ما خلفه عليه". قال في النِّهاية: " لعلَّ هامَّةً دبَّت فصارت فيه بعده، وخِلاف الشيء: بعده ".','2','842',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1692,'٩٢٦ -[٣٤٠٢] "نَفثَ فيهِمَا".
قال في النِّهاية: " النَّفث بالفم شبيه بالنَّفْخ وهو أقلُّ من التَّفْل؛ لأنَّ التَّفْل لا يكون إلاَّ ومعه شيءٌ من الرِّيق ".','2','842',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1693,'٩٢٧ -[٣٤٠٧] "يَهُبَّ" أيْ يستيقظ.','2','842',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1694,'٩٢٨ -[٣٤٠٨] "مَجلَ يديْهَا" قال في النِّهاية: "يقال: مَجَلَت يدُه، تمْجُل، مَجْلاً، ومجِلت إذا ثَخُنَ جِلْدُهَا وتعجَّر، وظهر منها ما يُشْبِه البَثْر من العمل بالأشياى الصُّلبة الخَشِنَة".','2','843',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1695,'٩٢٩ -[٣٤١٠] "خَلَّتَانِ" أي خصلتان.','2','843',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1696,'"لا يُحْصِيهِمَا" أي لا يحافظ عليهما.','2','844',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1697,'٩٣٠ -[٣٤١٢] "مُعَقِّبَاتٌ لا يَخيبُ قَائِلهُنَّ".
قال في النِّهاية: " سُمِّيَتْ مُعَقَّبات لأنها عاودتْ مرَّة بعد مرَّة، أو لأنها تقال عقِب الصَّلاَةِ، والمُعَقِّب من كل شيءٍ: ما جاءَ عَقِبَ مَا قَبْلَهُ ".','2','844',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1698,'٩٣١ -[٣٤١٤] "من تَعَارَّ" قال في النِّهاية: " أي استيقظ، ولا يكُون إلاَّ يقظةً مع كلامٍ.
وقيل: تمطَّى وأنَّ ".','2','845',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1699,'٩٣٢ -[٣٤١٦] "فأسْمَعُهُ الهَوِيَّ من اللَّيلِ" قال في النِّهاية:
"الهَوِي بالفتح: الحينُ الطَّويل من الزَّمانِ، وقيل: هو مُخْتَصٌّ باللَّيلِ".
-٣٤١٧ "الحَمْد للهِ الَّذِي أَحْيَا نَفْسِي بَعْدَ مَا أَمَاتَهَا".','2','845',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1700,'قال في النِّهاية: " سمَّى النَّوم موتًا، لأنه يزول معه العقل والحركةُ، تمثيلاً، وتشبيهًا لا تحقيقًا. وقيل: الموت في كلام العرب يطلق على [السكون] ".','2','846',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1701,'٩٣٤ -[٣٤١٩] "وتَلُمَّ بِهَا شَعْثِي" أي تجمع بها ما تفرق من','2','846',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1702,'أمري.
"كما تجير بين البحور" أي تفصل بينهما وتمنع أحدها من الاختلاط بالآخر والبغي عليه.
"وَمن دعوة الثُّبُور". قال في النِّهاية: " هو الهلاك ".
"اللَّهمَّ ذَا الحَبْلِ الشَّدِيدِ" قال في النِّهاية: " هكذَا يرويه المحدِّثُون بالباء الموحدة، والمراد به القرآن، أو الدِّين أو السَّببُ. ومنه قوله تعالى: {وَاعْتَصِمُوا بِحَبْلِ اللَّهِ} . وصفَه بالشِّدَّة لأنها من صفات الحِبال، والشِّدة في الدِّين، الثَّبات والاستقامة. وقال الأزهري: "الصَّواب الحيْل بالياء [المثناه] التحتية، وهو القوَّة، يقال: حَوْل وَحَيْل، بمعنى ".
"سِلمًا" أي صلحًا.
"سبحانَ الَّذِي تَعَطَّفَ بالعِزِّ". قال في النِّهاية: " أي تردَّى بالعِزِّ العِطاف والمِعْطَفُ: " الرَّداءُ، وقد تعطَّفَ به، وتعطَّفَهُ، وسمَّى عِطافًا لوُقُوعِهِ على عطفي الرَّجل، وهما ناحِيَتَا عُنُقه، والتَّعطُف في حقِّ الله مجازٌ يُرادُ بهِ الاتِّصاف، كأنَّ العِزَّ شَمِلَهُ شُمُول الرِّداء".
"وقال به" أي أحبه، واختصَّه لنفسه، كما يقال: فلان يقول بفلان، أي: بمحبته، واختصاصه، وقيل: معناه حكم به، فإنَّ القول','2','847',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1703,'يستعمل في معنى الحكم. وقال الأزهري: معناه: غلب به".','2','848',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1704,'٩٣٥ -[٣٤٢٦] "من قال، يعْنِي إِذا خرجَ مِنْ بَيتِهِ: بسم الله توكَّلتُ على الله، لا حول ولا قُوَّةَ إِلاَّ باللهِ".
"يقال له كفيت ووقيت، وتنحى عنه الشيطان".
قال الطيبي: " فيه، لف، ونشر، فإنَّ العبد إذا استعان بالله، وباسمه المبارك فإنَّ الله يهديه، ويرشدُهُ، ويعينه في الأمُور الدينيَّة، والدنيوية وإذا توكل على الله، وفوَّض أمره إليه كفاه فيكون هو حسبه {وَمَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَهُوَ حَسْبُهُ} ، ومن قال: " لا حول ولا قوَّة إِلاَّ بالله " وقاه الله شرَّ الشيطان، ولا يسلط عليه.','2','848',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1705,'٩٣٦ -[٣٤٢٨] "مَن دخل السُّوق فقَالَ: ... الحديث".','2','848',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1706,'قال الطيبي: " إنما خصَّ السوق بالذِّكر؛ لأنه مكان الاشتغال عن الله وعن ذكره بالتجارة، والبيع، والشراء، فمن ذكر الله تعالى فيه دخل في زمرة من قيل في حقه: {رِجَالٌ لَا تُلْهِيهِمْ تِجَارَةٌ وَلَا بَيْعٌ عَنْ ذِكْرِ اللَّهِ} ".','2','849',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1707,'٩٣٧ -[٣٤٣١] "إلاَّ عُوفِي من ذلك البلاء كائنًا ما كان".
قال الطيبي: " هو حال من الفاعل هذا [هو الوجه] " وذهب المظهري: إلى أنه حال من المفعول.','2','849',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1708,'٩٣٨ -[٣٤٣٣] "فكثُر فيهِ لغَطُهُ".','2','849',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1709,'قال التُورَبشتِي: "اللَّغط -بالتحريك-[الصَّوت] وأراد به الهزا من القول، وما لا طائل تحته من الكلام، فأحل ذلك محل الصَّوت العرِّي عن المعنى".','2','850',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1710,'٣٣٩ -[٣٤٣٨] "اللَّهُمَّ أَنْتَ الصَّاحِبُ فِي السَّفَرِ".','2','850',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1711,'قال التُوربشتي: " الصَّاحب هو الملازم وأراد بذلك مصاحبة الله إيَّاه بالعناية، والحفظ، والاستئناس بذكره، والدفاع لما ينوبه من النوائب ".
"والخَليفةُ في الأَهْلِ" ينوب الخليفة هو الذي عن المستخلف، يعني: أنت الذي أرجوه وأعتمد عليه في سفري، وغيبتي عن أهلي، بأن يكُون معيني وحافظي، وأن تلم شعثهم، وتداوي سقمهم، وتحفظ عليهم دينهم، وأمانتهم ".
"اللَّهمَّ اصْحَبْنَا بِصحْبته، وأقْلِبْنَا بِذِمَّة". قال في النِّهاية:
"أي: احفظنا بحفظِك في سفَرِنَا وَأَرْجعْنَا بأَمَانِكَ، وعهدِك إِلَى بلدنا".
"أَزْوِ" أي أطو.
"من وَعْثَاءِ السَّفَرِ" أي شدَّته ومشقته، وأصله من الوعث وهو الرَّمل.
والمشي فيه يشتد على صاحبه، ويشق عليه ووقع في رواية المستدرك: "من وعثا السفر" قال أبو زرعة: وكان أبو هريرة رجلاً عربيًا،','2','851',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1712,'لو أراد أن يقول "وعثاء السفر" لقال.
"وكآبَةِ المنقلَبِ" الكآبة: تغير النفس بالإنكاد من شِدَّة الغم، والحزن؛ المعنى أن يرجع من سفره بأمرٍ يحزنه إما إصابة في سفره، وإما قدم عليه، مثل أن يعُود غير مقضي الحاجة، أو أصابت ماله آفة، أو يقدم على أهله فيجدهم مرضى أو قد فقد بعضهم.','2','852',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1713,'٩٤٠ -[٣٤٣٩] "ومَن الحَوْر بعْد الكَوْر".
قال في النِّهاية: "أي من النُّقصان بعد الزِّيادة، وقيل من فساد أمورنا بعد صلاحها. وقيل من الرُّجُوع عن الجماعة بعد أن كان منهم، وأصله من نَقْضِ العِمَامَة بعد لفِّها". ويروى: " الحَوْرِ بَعْدَ الكَوْنِ ".
قال الزمخشري في الفائق: " أي الرجوع بعد الحُصول على حالة جميلة يريد التراجع بعد الاقبال ".','2','852',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1714,'٩٤١ -[٣٤٤٠] " آيِبونَ، عابِدُون، لرَبِّنَا حَامِدُونَ ".','2','852',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1715,'قال الطيبي: "يجُوز أن يتعلق "لربنا" بقوله: " عابدُون "؛ لأنَّ عمل اسم الفاعل ضعيف فيقوي، أو بـ "حامدون" ليفيد التخصيص أي نحمد ربنا لا نحمده غيره، قال: وهذا أولى؛ لأنه كالخاتمة للدعاء ".','2','853',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1716,'٩٤٢ -[٣٤٤١] "أوضَعَ رَاحِلَتَهُ" أي حملها على سُرعة السَّير.','2','853',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1717,'٩٤٣ -[٣٤٤٥] "على كُلِّ شَرفٍ" أي مكان مرتفع.','2','853',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1718,'٩٤٤ -[٣٤٥٦] "غيرُ مُودَّعٍ" قال في النِّهاية: " أي غير متروك الطَّاعة، وقيل: هو من الوداع وإليه يرجع ".
"ولا مُسْتغنًى عنْه رَبُّنا". قال في النِّهاية: " بالنصب على النداء، والرفع على الابتداء، المؤخر؛ أي ربنا غير مودع، ويجوز أن يكون الضمير للحمد؛ أي ولا يستغنى عن الحمد ".','2','854',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1719,'٩٤٥ -[٣٤٦٢] "وأنَّها قِيعَانٌ" جمع قاع وهو المستوى من الأرض.','2','854',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1720,'"وأَنَّ غِراسها سُبْحَان اللهِ والحَمْدُ للهِ ولا إله إلاَّ الله، والله أَكْبَرُ".
قال الطيبي: " في هذا إشكال؛ لأنَّ ظاهره يدل على أنَّ أرض الجنة خالية عن الأشجار، والقُصُور، وقوله تعالى: {أُعِدَّتْ لِلْمُتَّقِينَ (١٣٣) } يدل على أنها غير خالية، لأنها إنما سُمِّيت جنة لأشجارها المتكاثفة، المظلة بالتفاف أغصانها وتركيب الجنة دائر على معنى الستر، وأنها مخلوقة معدة للمتَّقين.
قال: والجوابُ: أنها كانت قيعانًا، ثم إنَّ الله تعالى أوجد بفضله، وسعة رحمته، فيها أشجارًا وقصُورًا على حسب أعمال العاملين، لكل عامل ما يختص بحسب عمله، ثم إنَّ الله لما يسره لما خلق له من العمل لينال به ذلك الثواب، جعله كالفارس لتلك الأشجار على سبيل المجاز إطلاقًا للسبب على المسبب ".','2','855',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1721,'٩٤٦ -[٣٤٦٧] "كلمتَانِ خَفِيْفَتَانِ علَى اللِّسانِ ثَقِيلَتَانِ في الميزان".
قال الطيبي: " الخفة، مستعارة من السُهُولة، شبه سُهُولة جريان الكلمتين على اللِّسان بما يخفف على الحامل من بعض الأمتعة، فلا يتعبه كالشيء الثقيل، فذكر المشبه به وأراد المشبه، وأما الثقل فعلى','2','855',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1722,'الحقيقة عند علماء أهل السنة؛ إذ الأعمال تتجسَّم حينئذٍ".','2','856',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1723,'٣٤٧ -[٣٤٦٨] "وإنْ كَانَتْ أكثر من زَبَدِ البَحْرِ".
قال الطيبي: " هذا وأمثاله، نحو: " ما طلعت عليه الشمس كنايات عبر بها عن الكثرة عرفًا ".','2','856',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1724,'٩٤٨ -[٣٤٧٤] " من قال في دُبُر صلاةِ الفَجْرِ، وهُوَ ثاني رِجْلَيْهِ قَبْلَ أَنْ يَتَكَلَّم، لا إِلهَ إلاَّ الله وحده لا شريكَ لَة، لَهُ المُلكُ، ولهُ الحَمْدُ يُحْيِي وَيُمِيتُ وَهُوَ عَلَى كلِّ شيءٍ قَدِيرٌ عشْرَ مَرَّاتٍ "،','2','856',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1725,'٩٤٩ -[٣٤٧٥] "لقد سأل الله باسمه الأعظم".
قال المظهري: "قيل الاسم الأعظم هنا بمعنى العظيم، وليس أفعل التفضيل؛ لأنَّ جميع أسمائه عظيم، وليس بعضها أعظم من بعض.
وقيل: بل هو للتفضيل؛ لأنَّ كل اسم فيه أكثر تعظيمًا لله فهو أعظم من الرَّحيم، والله أعظم من الرَّب، فإنه لا شريك له في تسميته به لا بالإضافة، ولا بدونها. وأما الرَّب فيضاف إلى المخلوقات، كما يقال: رب الدار".
"الذي إذَا دُعِي به أجاب، وإذا سئِلَ به أعطى".
قال الطيبي: " فإن قلت ما الفرق بين الجملة الأولى، والثانية؟
قلتُ: الأولى أبْلغ؛ فإنَّ إجابة الدعاء تدل على شرف الداعي','2','857',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1726,'ووجاهته عند المجيب فيتضمَّن أيضًا قضاء حاجته بخلاف السؤال".','2','858',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1727,'٩٥٠ -[٣٤٧٩] "ادْعُوا الله وَأَنْتُمْ مُوْقِنُونَ بِالإجَابِة".
قال التوربشتي: " فيه وجهان:
أحدهما أن يقال: كونوا أوان الدعاء على حالة تستحقون فيها الإجابة، وذلك بإتيان المعروف، واجتناب المنكر، وغير ذلك من مراعاة أركان الدعاء، وآدابه، حتى تكون الإجابة على قلبه أغلب من الرد.
والثاني: ادعُوهُ معتقدين لوقوع الإجابة، لأنَّ الداعي إذا لم يكن متحققًا [في الرجاء] لم يكن رجاؤه صادقًا، وإذا لم يكن رجاؤه صادقًا لم يكُن الدُعاء خالصًا، والداعي مخلصًا، فإنَّ الرجاء هو الباعث على الطلب، ولا يتحقق الفرع إلاَّ بتحقق الأصل ".','2','858',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1728,'٩٥١ -[٣٤٨٤] "وَضَلَعِ الدَّيْنِ" بالتحريك، قال في الغريبين:','2','858',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1729,'"يعني ثقله حتى يميل بصاحبه عن الاستواء، والاعتدال، والضلع الاعوجاج".
"وغلبة الرِّجالِ".
قال التوربشتي: "كأنه يريد به هيجان النفس من شدَّة الشبق، وإضافته إلى المفعول أي يغلبهم ذلك إلى هذا المعنى بسبق فهمي، ولم أجد في تفسيره نقلاً".
وقال الطيبي: " أي قهرهم للدائن وغلبتهم عليه بالتقاضي، وليس له ما يقضي دينه فإضافته إلى الفاعل ".','2','859',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1730,'٩٥٢ -[٣٤٩٢] "ومن شرِّ مَنِييِّ".','2','859',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1731,'قال المظهري: " أي من شر غلبة منيي حتى لا أقع في الزنا، والنظر إلى المحارم ".','2','860',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1732,'٩٥٣ -[٣٤٩٧] "لِيَعزِم المسألَةَ" أي يجزمها، ويقطعها.','2','860',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1733,'٩٥٤ -[٣٤٩٩] ["أيّ الدعاء أسمع، قال: جوف اللَّيل الآخر"] .','2','860',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1734,'٩٥٥ -[٣٥٠٢] "اللَّهمَّ اقْسِمْ لَنَا مِنْ خَشْيَتِكَ".','2','860',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1735,'قال البيضاوي: " أي اجعل لنا قسمًا ونصيبًا قال، وقوله: " ومن اليقين ما تهوِّن علينا مُصيبات الدنيا" أيْ ارزقنا يقينًا بك، وبأن لا مرد لقضائك، وقدرك وأن لا يصيبنا إلاَّ ما كتبه علينا، وأنَّ ما قدرته لا يخلو عن حكمة، ومصلحة، واستجلاب مثوبة تهون به مُصيبات الدنيا.
"وَمَتِّعْنَا بِأَسمَاعِنَا، وَأَبْصَارِنَا، وَقُوَّتِنَا، مَا أَحْيَيْتَنَا وَاجْعَلْهُ الوَارِثَ مِنَّا".
قال: الضمير في "اجعله" للمصدر كما في قولك: زيدٌ أظنه منطلق، أي اجعل الجعل. و"الوارث" هو المفعول الأول، و"منَّا" في موضع المفعول الثاني، على معنى واجعل الوراث من نسلِنَا، لا كلالة عنَّا، كما قال تعالى، حكاية عن دعوة زكريا: {فَهَبْ لِي مِنْ لَدُنْكَ وَلِيًّا (٥) يَرِثُنِي وَيَرِثُ مِنْ آَلِ يَعْقُوبَ} وقيل: الضمير للتمتع الذي دل عليه، ومتِّعنا، ومعناه: اجعل تمتعنا بها باقيًا عنَّا مورُوثًا لمن بعدنا، أو محفوظًا لنا إلى يوم الحاجة.
وهو المفعول الأول، "والوارث" مفعول ثان، و"منَّا" صلة له.
وقيل: الضمير لما سبق من الأسماع، والأبصار، والقوة، وإفراده، وتذكيره على تأويل المذكور، كما في قول رؤبة:
فيها خطوط من سواد وبلق ... كأنه في الجلد توليع البهق
والمعني بوراثتها لزومها له عند موته لزوم الوارث له.','2','861',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1736,'"واجعل ثَأرَنَا عَلَى مَنْ ظَلَمَنَا" أي: مقصورًا عليه، ولا تجعلنا ممن تعدَّى في طلب ثأره فأخذ به غير الجاني، كما كان معهودًا في الجاهلية، أو اجعل إدراك ثأرنا على من ظلمنا فندرك منه ثأرنا.
"ولا تجعل مُصيبتنا في ديننا"
قال المظهري: "أي لا تصيبنا بما ينقص ديننا من أكل الحرام أو اعتقاد سوء، أو فترة في العبادة".
"وَلاَ تَجعلِ الدُّنيا أكبرَ هَمِّنَا"
قال الطيبي: " فيه أنَّ قليلاً من الهمِّ مما لا بد منه من أمر المعاش مرخص، بل مستحب ".
"ولا تُسلِّطْ علينا من لا يَرْحَمُنَا".
قال الطيبي: "أي لا تجعلنا مغلوبين للظلمة، والكفار".
ويحتمل أن يراد لا تجعل الظالمين علينا حاكمين فإنَّ الظالم لا يرحم الرعية".
ويحمل: "من لا يرحمنا" على ملائكة العذاب في القُبُور وفي النَّارِ.','2','862',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1737,'٩٥٦ -[٣٥٠٧] "إِنَّ لله تسعة، وتسعين اسمًا، مائة غير واحد"','2','862',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1738,'قال الرافعي في أماليه: " إنما قال مائة غير واحد لئلا يتوهم أنه على التقريب، وفيه فائدة رفع الاشتباه، فقد يشبه في الخط تسعة وتسعين، بسَبْعَة وسبعين ".
"من أحصاها دخل الجنة".','2','863',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1739,'قال الخطابي: الإحصاء في هذا يحتمل وجوهًا:
أحدها: أن يعدَّها حتى يستوفيها؛ يريد أنه لا يقتصِر على بعضها لكن يدعُو الله بها كلهَا ويثني عليه بجميعها فيسْتَوْجب الموعُود عليها من الثواب.
الثاني: المراد بالإحصاء، الإطاقة، كقوله تعالى: {عَلِمَ أَنْ لَنْ تُحْصُوهُ} حديث: "استقيمُوا، ولن تحصُوا" أي تبلغوا كنه الاستقامة، والمعنى من أطاق القيام بحق هذه الأسماء، والعمل بمقتضاها، وهو أن يعتبر معانيها، فيلزم نفسه بواجبها، فإذا قال: الرزَّاق، وثق بالرزق، وكذا سائر الأسماء.
الثالث: المراد الإحاطة بمعانيها من قول العرب: فلان ذو حصَافة؛ أي: [ذُو] عقل، ومعر فة. انتهى.
قال ابن الجوزي في غريب الحديث: "فيه خمسة أقوال:
أحدها: من استوفاها حفظها.
والثاني: من أطاق العمل بمقتضاها، مثل: أن يعلم أنه سميع، فيكف لسانه عن القبيح، وأنه حكيم، فيُسلِّم لحكمته.
والثالث: من عقل معانيها.','2','866',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1740,'والرابع: من أحصاها علمًا وإيمانًا، قاله الأزهري.
والخامس: أن يكون المعنى من قرأ القرآن حتى يختمه لأنها فيه، زاد في النهاية.
وقيل: من استخرجها من كتاب الله، وأحاديث رسوله، لأنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لم يعدَّها لهم، إلاَّ ما جاء في روايةٍ عن أبي هريرة وتكلَّموا فيها، وقيل: أراد من أخطر ببَاله عند ذكرها معناهَا، [وتفكَّر] في مَدْلُولَهَا مُعَظِّمًا لمُسَمَّاها، ومُقَدِّسًا ومعتبرًا بمعانيها، ومُتَدَبِّرًا، راغبًا فيها وراهبًا".
وقال القرطبي: " المرجو من كرم الله تعالى أنَّ من حصل له إحصاء هذه الأسماء على إحدى هذه المراتب مع صحة النية أنه يدخل الجنة، وقال النووي: " معنى أحصاها حفظها هكذا فسَّره البخاري، والأكثرون، ويؤيِّده أنه ورد في رواية في الصحيح: "من حفظها دخل الجنة".
قال الطيبي: " أراد بالحفظ القراءة بظهر القلب، وقد اختلف في هذا العدد هل المراد به حصر الأسماء في هذه العِدَّة، أو أنها أكثر من ذلك ولكن اختصَّت هذه بأنَّ من أحصاها دخل الجنَّة.','2','867',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1741,'فذهب الجمهور إلى الثاني، ونقل النووي اتفاق العلماء [عليه] قال: فالمراد الإخبار عن دخول الجنة [بإحصائها] لا الإخبار','2','871',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1742,'بحصر الأسماء، وأما الحكمة في القصر على العدد المخصوص، فذكر الفخر الرازي عن الأكثر أنه تعبد لا يعقل معناه، كما قيل في عدد الصلوات، وغيرها.
وقال أبو خلف محمَّد بن عبد الملك الطبري: "إنما خصَّ هذا العدد إشارة إلى أنَّ الأسماء لا تؤخذ قياسًا وقيل: الحكمة فيه أنَّ معاني الأسماء ولو كانت كثيرة جدًا موجودة في التسعة والتسعين المذكورة، وقيل: الحكمة فيه أنَّها في القرآن، كما في بعض طرقه، وقال قوم: الأسماء الحسنى مائة على عدد درجات الجنَّة استأثر الله منها بواحد وهو الاسم الأعظم، فلم يطلع عليه أحدًا فكأنه قيل: مائة، لكن واحد منها عند الله.
وقال بعضهم: ليس الاسم الذي يكمل المائة مخفيًا بل هو الجلالة، وبه جزم السهيلي فقال: الأسماء الحسنى مائة على عدد درجات الجنة الذي يكمل المائة الله، ويؤيِّده قوله تعالى: {وَلِلَّهِ الْأَسْمَاءُ الْحُسْنَى فَادْعُوهُ بِهَا} فالتسعة والتسعون لله فهي زائدة عليه وبه يكمل المائة" انتهى.','2','872',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1743,'وقد روي من غير وجه عن أبي هريرة، قال الحافظ ابن حجر، رواه عن أبي هريرة أيضًا همَّام بن منبه عند مسلم، ومحمَّد بن سِيرين عنده، وأبو سلمة بن عبد الرَّحمن عند أحمد، وابن ماجه، وعطاء بن يسار وسعيد المقبري وسعيد بن المسيب وعبد الله بن شقيق، ومحمَّد بن جبير بن مطعم، والحسن البصري، أخرجها أبو نعيم،','2','873',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1744,'وعراك بن مالك عند البزار وغيره، وذكر ابن عطية في تفسيره أنه تواتر عن أبي هريرة، فقال: لم يتواتر الحديث من أصله وإن خرج في الصحيح، ولكنه تواتر عن أبي هريرة: {هُوَ اللَّهُ الَّذِي لَا إِلَهَ إِلَّا هُوَ} .
قال الطيبي: هو مبتدأ، "الله" خبره، "لا إله إلاَّ هو" صفته، و"الرَّحمن" إلى آخره خبر بعد خبر، والجملة مُستأنفة، إما لبيان كمية تلك الأعداد أنها ما هي في قوله: "إنَّ لله تسعةً وتسْعِيْنَ اسمًا" وذكر الضمير نظر إلى الخبر، وإما بيان لكيفيَّة الإحصاء في قوله: "من أحصَاهَا دَخَلَ الجنَّةَ" وأنه كيف يحصى فالضمير راجع إلى المسمى الدال عليه قوله: " الله " كأنه لما قيل: "إنَّ لله تسعةً وتسعِيْنَ اسمًا" سئل وما تلك الأسماء، فأُجِيبَ: هو الله [ولما قيل من أحصاها دخل الجتة سئل كيف يحصيها فأجيب هو الله] فعلى هذا يكون الضمير ضمير الشأن، والله مبتدأ، وقوله: "الَّذِي لا إلهَ إلاَّ هوَ" خبر، والجملة خبر الأول، ويجوز أن يكون الرَّحمن خبره والموصُول مع الصلة صفة الله:','2','874',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1745,'" {الرَّحْمَنِ الرَّحِيمِ} " اسمان بنيا للمبالغة من الرَّحمة، وهي في اللغة رقة قلب وانعطاف يقتضي التفضل، والإحسان على من رق له، وأسماء الله تعالى وصفاته إنما تؤخذ باعتبار الغايات التي هي أفعال دون المباديء التي تكون انفعالات، فرحمة الله للعباد إما إرادة الإنعام عليهم ودفع [ضرِ] الضرر عنهم فتكون الإسمان من صفات الذات، أو نفس الإنعام، والدفع فيعودان إلى صفات الأفعال،','2','875',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1746,'والرَّحمن أبلغ من الرَّحيم لزيادة بنائه.
"الملك" معناهُ ذو الملك، وهو إذا كان عبارة عن','2','876',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1747,'القدرة على التصرف كان من صفات الذات، كالقادر، وإذا كان عبارة عن التصرف في الأشياء بالخلق والإبداع والإماتة، والإحياء، كان من أسماء الأفعال كالخلق، وعن بعض المحققين "الملك" هو الغني مطلقًا في ذاته وفي صفاته عن كل ما سواهُ، ويحتاج إليه كل ما سواه.
"القُدُّوسُ" فعول من القدس، وهو الطهارة، والنزاهة ومعناهُ المنزه عن سمات النقص، وموجبات الحدوث بل المبرَّأ أن يدركه حس، أو يتصوره خيال، أو يسبق إليه وهم، أو يحيط به عقل وهو من أسماء التنزيه.','2','877',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1748,'"السَّلام" مصدر نعت به، والمعنى ذو السلام من كل آفة،','2','878',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1749,'ونقيصة، أي الذي سَلم ذاته عن الحُدُوث والعيب، وصفاته عن النقص، وأفعاله عن الشرِّ المحض، فإن ما تراه من الشرور فهي مقضيَّة لا لأنها كذلك بل لما يتضمَّنه من الخير الغالب الذي يؤدي تركه إلى شر عظيم، فالمقتضي، والمفعول بالذات هو خير، والشر داخل تحت القضاء، وعلى هذا يكون من أسماء التنزيه.
والفرق بينه وبين القدوس، أنَّ القدوس يدل على براءة الشيء من نقص تقتضيه ذاته وتقُوم به فإنَّ القدس، طهارة الشيء في نفسه ولذلك جاء الفعل منهُ على فعُل بالضم، و"السلام" يدل على نزاهته عن نقص يعتريه لعروض آفه، أو صدور فعْل، ويقرب منه ما قيل: " القدُّوس " فيما لم يزل وَالسلام فيما لا يزال، وقيل: معناه: مالك تسليم العباد، من المخاوف، والمهالك، فيرجع إلى القدرة، فيكون من صِفات الذات، وقيل ذُو السلام على المؤمنين في الجنان كما قال تعالى: {سَلَامٌ قَوْلًا مِنْ رَبٍّ رَحِيمٍ (٥٨) } فيكون مرجعه إلى الكلام القديم.','2','879',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1750,'"المؤمن" هو في الأصل الذي يجعل غيره آمنًا، ويقال:','2','882',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1751,'للمصدق من حيث أنه جعل الصِّدق أمنًا من التكذيب، والمخالفة، وإطلاقه على الله تعالى باعتبار كل واحدٍ من المعنيين صحيح فإنه تعالى المصدق بأنَّ صدق رسله، بقوله: الصِّدق فيكون [مرجعه] إلى الكلام أو بخلق المعجزات، وإظهارها عليهم فيكون من أسماء الأفعال، وقيل: معناه الذي آمن البريَّة بخلق أسباب الأمان، وسد أبواب المخاوف، وإفادة آلاتٍ يدفع بها المضار فيكون أيضًا من أسماء الأفعال، وقيل: معناه؛ أنه يؤمن عباده الأبرار يوم العرض من الفزع','2','883',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1752,'الأكبر إما بقول مثل: {أَلَّا تَخَافُوا وَلَا تَحْزَنُوا وَأَبْشِرُوا بِالْجَنَّةِ الَّتِي كُنْتُمْ تُوعَدُونَ (٣٠) } ، أو بخلق الأمن، والطمأنينة فيرجع إلى الكلام، أو الخلق.
"المُهَيْمِنُ" الرقيب المبالغ في المراقبة والحفظ.
من قولهم: هيمن الطائر إذا نشر جناحه على فرخه صيانة له هكذا قاله الخليل.
فإن قيل: كيف يجعله مُرادفًا "للرقيب" والمستفاد من أحد المترادفين غير المستفاد من الآخر فلا يكون في إحصاء المباني فائدة لأنَّ','2','884',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1753,'فضيلة هذه الأسامي لما تحتها من المعاني فإذا دلَّ عليه بلفظ لم يكن للدلالة عليه بلفظ آخر مزيد فضل.
قلتُ لا أجعله مُرادفًا إذ في "المهيمن" من المبالغة باعتبار الاشتقاق، والزنة ما ليس في الرقيب فهما كالغافر والغفور، والرَّحمن والرَّحيم، ومعناه الشاهد؛ أي العالم الذي لا يعزب عنه مثقال ذرَّة فيرجع إلى العلم والذي يشهد على كل نفس بما كسبت فيرجع إلى القول، وقيل: أصْلُهُ مُؤتمن فقلبت الهمزة هاء كما قلبت في هرقت، وهناك، ومعناه الأمين الصَّادق وعده.
وقيل: هو القائم على خلقه بأعمالهم وأرزاقهم، وآجالهم، فيرجع إلى القدرة.
قال الغزالي: " المهيمن " اسم لمن استجمع ثلاث خصال، العلم بحال الشيء، والقدرة التَّامة على مراعاة مصالحه، والقيام عليها، وهو كالشرح، والتفصيل للقول الأول، فإنَّ المراقبة، والمبالغة في الحفظ إنما تتم بهذه الثلاثة، وإن صحَّ وصفهُ لهذا كان من الأسماء المركبة من صِفات المعنى، والفِعل.
"العزيز" الغالب من قولهم عزَّ إذا غلب، ومرجعه إلى القدرة','2','885',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1754,'المتعالية عن المعارضة فمعناه مركب من وصف حقيقي، ونعت تنزيهي، وقيل: القوي الشديد، مق قولهم عزَّ يعزُّ إذا قوى واشتدَّ وقيل: عديم المثل فيكون من أسماء التنزيه وقيل: هو الذي تتعذر الإحاطة بوصفه، ويعسُر الوُصول إليه.
"الجبَّارُ" بناء مبالغة من الجبر وهو في الأصل إصلاح الشيء بضرب من القهر، ثم يطلق تاره في الإصلاح المجرَّد، وتارةً في القهر','2','887',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1755,'المجرَّد ثم تجوَّز عنه لمجرد العُلو؛ لأنَّ القهر سبب عنه، وكذلك قيل: الجبار هو المصلح لأمور العباد، والمتكفل بمصالحهم فهو إذنْ من أسماء الأفعال، وقيل: معناه حامل العباد على ما يشاء لا انفكاك لهم عمَّا شاء مِن الأخلاق والأعمال والأرزاق، والآجال فمرجعه أيضًا إلى الفعل وقيل: معناه المتعالي عن أن يناله كيد الكائدين ويؤثر فيه قصد القاصدين فيكون مرجعه إلى التقديس، والتنزيه.','2','889',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1756,'"المُتَكبِّر" وهو الذي يرى غيره بالإضافة إلى ذاته، نظر المالك إلى عبده وهو على الإطلاق لا يتصوَّر إلاَّ لله تعالى، فإنه المتفرد','2','890',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1757,'بالعظمة، والكبرياء بالنسبة إلى كل شيء من كل وجه، ولذلك لا يطلق على غيره إلاَّ في معرض الذم.
فإن قيل هذا اللَّفظ من باب التفعل ووضعه للتكلف في إظهار ما لا يكون فينبغي أن لا يطلق على الله تعالى.
قلتُ: لما تضمَّن التكلف بالفعل مبالغة فيه أطلق اللَّفظ، وأريد به مبالغة، ونظير ذلك فيه شائع في كلامهم مع أنَّ التفعل جاء لغير التكلف كثيرًا كالتعمُّم، والتقمُّص.
" {الْخَالِقُ الْبَارِئُ الْمُصَوِّرُ} " قيل: أنها أسماء مترادفة وهو','2','891',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1758,'وهم، فإنَّ الخالق من الخلق وأصْلُهُ التقدير المستقيم، ويستعمل بمعنى الإبداع، وهو إيجاد الشيء من غير أصله، لقوله تعالى: {خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ} وبمعنى التكوين، كقوله تعالى: {خَلَقَ الْإِنْسَانَ مِنْ نُطْفَةٍ} ، وقوله: {وَخَلَقَ الْجَانَّ مِنْ مَارِجٍ مِنْ نَارٍ} . " والباري ".','2','893',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1759,'من البراء، وأصله خلوص الشيء من غيره إمَّا على سبيل التقصِّي منه، وعليه قولهم: برىء فلان من مرضِه، والمديُون من دينه، واستبرأت الجارية رحمها، وإما على سبيل الإنشاء، ومنه: برأ الله النسمة وهو الباريء لها، وقيل: البارى هو الذي خلق الخلق بريئًا من التفاوت، والتنافر المخلين بالنظام الكامل، وهو أيضًا مأخوذ من معنى التقصِّي.
"والمصور" مبدع صُوَر المخترعات، ومزيِّنها','2','894',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1760,'ومرقبها فإنَّ الله سُبحانه خالق كل شيء، بمعنى أنه مقدِّره، ومُوجده من أصله، ومن غير أصل، وباريه بحسب ما اقتضته حكمته، وسبقت به كلمته من غير تفاوُت، واختلال، ومُصَوره بصُورة يترتب عليها خواصه،','2','895',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1761,'ويتم بها كماله، وثلاثتها من أسماء الأفعال، اللَّهمَّ إلاَّ إذا فسِّر الخالق بالمقدِّر فيكون من صفات المعاني لأنَّ مرجع التقدير إلى الإرادة وإن فسِّر الخالق بالمقدر فوجه الترتيب ظاهر لأنه يكون التقدير أولاً، ثم الإحداث على الوجه المقدر ثانيًا، ثم التسوية، والتصوير ثالثًا.
وإن فسر بالموجد، فالإسمان الآخران كالتفصيل له فإنَّ الخالق هو الموجد بتقدير، [واختيار] سواء كان الموجد مادَّة، أو صُورة، ذاتًا، أو صِفة.
"الغفَّار" "في الأصل بمعنى الستَّار، من الغفر، بمعنى ستر الشيء بما يصونه، ومنه المِغْفَرُ، ومعناهُ أنه يستر القبائح، والذنوب بإسبال الستر عليها في الدنيا، وترك المؤاخذة بالعفو عنها في العقبى، ويصون العبد من أوزارها، وهو من [أسماء] الأفعال، وقد جاء التوقيف','2','896',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1762,'في التنزيل بالغفار، والغفور. والغافر. والفرق بينها، أنَّ الغافر يدُل على اتصافه بالمغفرة مُطلقًا، والغفَّار، والغَفُور يدلاَّن عليه مع المبالغة، والغفار أبلغ لما فيه من زيادة الثناء، ولعلَّ المبالغة في الغفور، باعتبار الكيفية، وفي الغفار باعتبار الكميَّة وهو قياس المشدد للمبالغة في النعوت، والأفعال، وقال بعض الصَّالحين: أنه غافر لأنه يزيل معصيتك من ديوانك، وغفور لأنه ينسِي الملائكة أفعالك، وغفار لأنه ينسيكَ ذنبك حتى كأنك لم تفعله، وقال آخر: أنه غافر لمن له علم اليقين، وغفُور لمن له عين اليقين، وغفار لمن له حق اليقين ".
"القهَّار" هو الذي لا موجود إلاَّ وهو مقهور تحت قدرته مسخر لقضائه عاجز في قبضته، ومرجعُه إلى القدرة فيكون من [صفات]','2','897',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1763,'المعنى، وقيل هوَ الذي أذلَّ الجبابرة، وقصم ظهورهم بالإهلاك ونحوه فهو إذَنْ من أسماء الأفعال.
"الوهَّاب" كثير النَعم دائم العطاء، وهو من أسماء الأفعال.','2','898',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1764,'" الرزَّاق " خالق الأرزاق والأسباب الذي يتمتع بها.
"الفتَّاح" الحاكم بين الخلائق من الفتح، بمعنى الحكم،','2','899',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1765,'ومرجعه إما إلى القول القديم، أو الأفعال المنصفة للمظلومين، من الظلمة، وقيل: هو الذي يفتح خزائن الرَّحمة على أصناف البريَّة قال تعالى: {مَا يَفْتَحِ اللَّهُ لِلنَّاسِ مِنْ رَحْمَةٍ فَلَا مُمْسِكَ لَهَا} ، وقيل: معناه مبدع الفتح، والنصرة، وقيل هُو الذي فتح على النفوس باب توفيقه، وعلى الأسرار باب تحقيقه.
"العليم" بناء للمبالغة من العِلم، وهو من صِفات الذات.','2','900',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1766,'"القابض الباسط" "مضيق الرزق على من أراد، وموسعه لمن','2','901',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1767,'يشاء وقيل هو الذي يقبض الأرواح عن الأشباح عند الممات، وينشر الأرواح في الأجساد عند الحياة، وقيل: قبض القلوب، وبسْطها تارة بالضلال، والهدى وأخرى بالخشية، والرجاء، وهما من صِفات الأفعال، وإنما يحسُن إطلاقها معًا ليدُل على كمال القدرة، والحكمة".
"الخافض الرافع" هو الذي يخفض القسط، ويرفعه أو يخفض','2','902',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1768,'الكفار بالخِزي، والصَّغار، ويرفع المؤمنين بالنصر، والإعزار، أو يخفض أعداءه بالإبعاد، ويرفع أولياءه بالتقريب، والإسعاد أو يخفض أهل الشقاء بالطبع، والإضلال ويرفع ذوي السَّعادة بالتوفيق، والإرشاد وهما من صِفات الأفعال".
"المُعِزُّ، المُذل" الإعزاز جعل الشيء ذا كمال يصير بسببه مرغوبًا فيه، قليل المثال والإذلال جعله ذا نقيصة بسببها، يرغب عنه','2','904',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1769,'ويسْقط عن درجات الاعتبار.
"السميع البصير" "هما من أوصاف الذات، والسمع؛ إدراك المسْمُوعات حال حدوثَهَا، والبَصر إدراك المبصرات حال وجُودها، وقيل أنهما في حقه تعالى صِفتان تنكشف بهما المسْمُوعَات، والمبصرات انكشافًا تامًّا، ولا يلزم من افتقار هذين النوعين مِن الإدراك فينا إلى آلة افتقارهما إليه بالنسبة إلى الله تعالى لأنَّ صفاته تعالى مخالفة لِصفات المخلُوقين بالذات وإن كانت تشاركها فإنما تشاركها بالعوارض، وفي بعض اللوازم ألا ترى أنَّ صفاتنا عارضة، معرضة للآفة، والنقصان وصفاته تعالى مقدسة عن ذلك".
"الحكم" "الحاكم الذي لا مردَّ لقضائه، ولا معقِّب لحكمه،','2','905',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1770,'ومرجعُه إلى القول الفاصل بين الحق، والباطل، والبر، والفاجر، والمبين لكل نفس جزاء ما عملت من خير أو شر، وإما إلى الفِعل الدال على ذلك كنصْب الدلائل، والأمارات الدالة عليه".
"العدل" معناه البالغ في العدل، وهو الذي لا يفعل إلاَّ ما له فعله، مصدر به للمبالغة، وهو من صفات الأفعال".
"اللطيف" قيل: معناه الملطف، أي المحسن، الموصل','2','906',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1771,'للمنافع برفع كالجميل؛ فإنه بمعنى المجمل فيكون من أسماء الأفعال، وقيل: معناه العليم بخفيات الأمُور، ودقائقها، وما لطف منها.
وقيل: هو في الأصل ضد الكثيف، ومن خواصه أن لا يحسَّ به فإطلاقه على الله تعالى باعتبار أنه متعال عن أن يحسَّ به فيكون من الصِّفات التنزيهيَّة، وعليه قوله: {لَا تُدْرِكُهُ الْأَبْصَارُ} ، ثم قال: {وَهُوَ اللَّطِيفُ الْخَبِيرُ (١٠٣) } .
العليم ببواطن الأشياء من الخبرة، وهو العِلم بالخفايا الباطنة، وقيل: هو المتمكن من الأخبار عمَّا عمله ".
"الحليم" الذي لا يستفزه غضب ولا يحمله غيظ على استعجال','2','907',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1772,'العُقُوبة، والمسارعة إلى الانتقام وحاصله راجع إلى التنزيه عن','2','908',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1773,'العجلة".
"العظيم" هو البالغ أقصى مراتب العظمة، وهو الذي لا يتصوَّره عقل ولا يحيط بكنهه بصيرة، وحاصلهُ يرجع إلى التنزيه، والتعالي عن إحاطة العُقُول بكنه ذاته".
"الغفُور" كثير المغفرة.','2','909',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1774,'"الشكُور" هو الذي [يعطي الثواب] الجزيل على العمل القليل فيرجع إلى الفعل، وقيل: هو المثني على العُبَّاد، والمطيعين، فيرجع إلى القول وقيل: المُجازي عباده على شكرهم فيكون الاسم من قبيل الازدواج كما سمَّى جزاء السيِّئة، سيِّئة".
"العليُّ" معناه البالغ في علوِّ الرتبة إلى حيث لا رتبة إلاَّ وهي','2','910',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1775,'منحطة عنه، وهو من الأسماء الإضافية.
"الكبير" معناه العالي الرتبة إما باعتبار أنه أكمل الموجُودات،','2','911',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1776,'وأشرفها من حيث أنه أزلي، غني على الإطلاق، وما سِواه حادث بالذات نازل في حضيض الحاجة، والافتقار، وإما باعتبار أنه كبير عن مُشاهدة الحواس، وإدراك العقول، وعلى الوجهين فهو من أسماء التنزيه.
"الحفيظ" الحفظ صون الشيء عن الزوال، والاختلال إما في','2','912',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1777,'الذِّهن، وبإزائه النسيان، وإما في الخارج، وبإزائه التضييع، والحفيظ؛ يصح إطلاقه على الله تعالى بكل واحد من الاعتبارين فإنَّ الأشياء كلها محفُوظة في علمه تعالى لا يمكن زوالها عنه بسهو أو نسيانٍ، فإنه تعالى يحفظ الموجودات، من الزوال، والاختلال ما شاء، ويصُون المتضادات بعضها عن بعض، ويحفظ على العباد أعمالهم، ويحصى عليهم أقوالهم، وأفعالهم".
"المُقِيتُ" "خالق الأقوات البدنِية والروحانية وموصلها إلى','2','913',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1778,'الأشباح، والأرواح فهو من صِفات الأفعال، وقيل: هو المقتدر بلغة قريش وقيل: الشاهد والمُطَّلع على الشيء فهو على الوجهين من صفات الذات".
"الحسيب" "الكافي في الأمور من أحسبني، إذا كفاني، فعيل،','2','914',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1779,'بمعنى مفعل كأليم، وقيل: المحاسب يحاسب الخلائق يوم القيامة، فعيل بمعنى مفاعل كالجليس، والنديم، فمن جمعه بالمعنى الأول إلى الفعل، وبالمعنى الثاني إليه، أن جعل المحاسبة عبارة عن المكافأة وإني القول إن أريد بها السُؤال والمعاتبة، وتعداد ما عملوا من الحسنات، والسيئات، وقيل الشريف، والحسب الشرف".
"الجليل" "المنعُوت بنعوت الجلال، وهي من الصفات','2','915',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1780,'التنزيهيَّة، كالقدوس".
"والغني" قال الإمام الرازي: الفرق بينه وبين الكبير، والعظيم؛ أنَّ الكبير اسم الكامل في الذات، والجليل؛ اسم الكامل في الصفات.
"والعظيم"؛ اسم الكامل فيهما
"الكريم" المفضل الذي يعطى من غير مسألة ولا وسيله،','2','916',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1781,'وقيل: المتجاوز لا يستقصي في العقاب، وقيل: المقدس عن النقائص، والعُيُوب من قولهم كرائم الأموال، لنفائسها.
"الرقيب".','2','917',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1782,'الحفيظ" الذي يراقب الأشياء ويلاحظها، فلا يعزب عنه مثقال ذرَّة.
"المُجيب" هو الذي يجيب دعوة الدَّاعي إذا دعاه ويسعف','2','918',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1783,'السائل إذا ما التمسه واستدعاه.
"الواسع" "فسِّر بالعالم، المحيط علمه بجميع المعلومات','2','919',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1784,'كليها، وجزئيها موجودها، ومعدومها، وبالجواد؛ الذي عمَّت نعمته، وشملت رحمته كل بر وفاجر ومؤمن، وكافر".
"وبالغني" التام الغني المتمكن مما يشاء.
وعن بعض العارفين، الواسع الذي لا نهاية لبرهانه، ولا غاية لسُلطانه، ولا حد لإحسانه.
"الحكيم" "ذو الحكمة وهو عبارة عن كمال العِلم، وإحسان العمل والإتقان فيه.
وقد يستعمل بمعنى العليم، والمحكم، وقيل هو مبالغة الحاكم فعلى الأوَّل مركب من صفتين:
أحدهما: من صفات الذات والأخرى من صِفات الأفعال، وعلى الثاني يرجع إلى القول.
"الودُود" "مبالغة الواد، ومعناه الذي يحب الخير لجميع','2','920',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1785,'الخلائق، ويحسن إليهم في الأحوال كلها، وقيل: المحب لأوليائه وحاصِلهُ يرجع إلى إرادة مخصُوصَة".
"المجيد" "مبالغة الماجد من المَجْدِ، وهو سعة الكرم.','2','921',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1786,'قال القشيري: "قيل هو بمعنى العظيم الرفيع القدر، فهو فعِيل بمعنى مفعل، وقيل: معناه الجزيل العطاء فهو فعيل بمعنى فاعل وكل وصف من أوصافه يحتمل معنيين فمن أثنى عليه بذلك الوصف فقد أتى بالمعنيين، وكل من قال له مجيد فقد وصفه بأنه عظيم رفيع القدر، وأنه مُحسن جزيل البِر".
"الباعث" هو الذي يبعث من في القُبُور، وقيل: باعث الرُسل','2','922',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1787,'إلى الأمم، وقيل: باعث الهمم إلى الترقي في ساحات التوحيد، وهو من صفات الأفعال.
"الشَّهيد" "من الشهود، وهو الحضور، ومعناه العليم بظاهر الأشياء، وما يمكن مشاهدتها، كما أنَّ الخبير؛ هو العليم بباطن الأشياء،','2','923',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1788,'وما لا يمكن الإحساس بها وقيل: مبالغة الشاهد، والمعنى أنه تعالى يشهد على الخلق يوم القيامة، وهو على الوجهين من صِفات المعاني لأنَّ مرجعه إما إلى العلم، أو إلى الكلام.
"الحق" " الثابت، وهو من صفات الذات، وقيل معناه المحق؛ أي: المظهر للحق أو الموجد للشيء حسب [ما] تقتضيه الحكمة فيكُون من صفات الأفعال ".
"الوكيل" "القائم بأمور العباد، وبتحصيل ما يحتاجُون إليه،','2','924',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1789,'وقيل: الموكول إليه تدبير البريَّة"
"القَوي المتين" القوة، القدرة التامَّة البالغة إلى الكمال،','2','925',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1790,'والمتانة؛ شدة الشيء، واستحكامه، ومرجعها إلى الوصف بكمال القدرة وشدتها.
"الولي" المحب الناصر، وقيل متولي أمر الخلائق.
"الحميد" المحمُود المستحق للثناء فإنه [الموصوف','2','926',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1791,'بكل] كمال والمولي لكل نوال.
"المحصِي" العالم الذي يحصي المعلومات ويحيط بها إحاطة العاد ما يعده، وقيل: القادر الذي لا يشذ عنه شيء من المقدُورات.
"المُبدي المُعيد" المُبدي المظهر للشيء من العَدم إلى الوُجود، وهو بمعنى الخالق المنشي، والإعادة خلق الشيء بعدما عدم.
"المُحيي المميت"','2','927',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1792,'الأحياء خلق الحياة في الجسم، والإماتة إزالتها عنه.
"الحي" ذو الحياة وهي صفة حقيقيَّة قائمة بذاته لأجلها صحَّ لذاته أن يعلم، ويقدر.
"القيُّوم" القائم بنفسه، المقيم لغيره.','2','928',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1793,'"الواجد الماجد" الذي يجد كل ما يطلبُه، ويريده، ولا يعوزه شيء من ذلك، وقيل: الغني؛ مأخوذ من الوجد، "الماجد"؛ بمعنى المجيد إلاَّ أنَّ في المجيد مبالغة ليست في الماجد.
"الواحد" هو الذي لا ينقسم بوجه، ولا مشابهة بينه وبين غيره','2','929',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1794,'بوجه، ووقع فِي سُنَن ابن ماجه زيادة الأحد، ولم يقع في رواية المصنف وقد ذكرت الفرق بين الواحد والأحد في التعليق الذي على سُنَنِ ابن ماجه.
"الصَّمدُ" السيد الذي يصمد إليه في الحوايج، وقيل: المنزه عن الآفات، وقيل: الذي لا يطعم، وقيل: الباقي الذي لا يزول.','2','930',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1795,'"القادر، المقتدر" معناهما ذو القدرة إلاَّ أنَّ المقتدر أبلغ لزيادة البناء.
"المقدم، المؤخر" "هو الذي يقدم الأشياء بعضها على بعض','2','931',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1796,'إما بالوجود كتقديم الأسباب على مسبِّباتها، أو بالشرف والقربة كتقديم الأنبياء المرسلين والصَّالحين من عباده على من عداهم، أو بالمكان كتقديم الأجسام العلوية على السفلية، والصاعدات منها على الهابطات، أو بالزمان، كتقديم الأطوار والقرون بعضها على بعض".
"الأوَّل" "السابق على الأشياء كلها فإنه موجدها، ومعيدها".
"الآخر" الباقي وحده بعد أن يفنى الخلق كله.
"الظَّاهر" الجلي وجوده بآياته الباهرة.','2','932',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1797,'"الباطن" المحتجب كنه ذاته عن نظر الخلق بحجب كبريائه.
" الوالي " الذي تولَّى الأمور، وملك الجمهُور.
"المُتَعال" البالغ في العلا والمرتفع عن النقائص.
"البر" المحسِن.','2','933',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1798,'"التواب" القابل توبة عباده، وقيل: الذي يُيَسِّر للمُذنبِين أسباب التَّوبة ويوفقهم لها.
"المنتقم" المعاقب للعُصاة.
"العَفُوُّ" الذي يمحو السيِّئات ويتجاوز عن المعاصي وهو أبلغ','2','934',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1799,'من الغفور؛ لأنَّ الغفران، ينبئ عن الستر، والعفو؛ ينبىء عن المحو.
"الرَّؤوف" ذو الرَّأفة، وهي شِدَّة الرَّحمة فهو أبلغ من الرَّحيم بمرتبة، ومن الرَّاحِم بمرتبتين وقيل: الفرق بين الرأفة والرَّحمة أنَّ الرأفة إحسان مبدؤه شفقة المحسِن، والرَّحمة؛ إحسان مبدؤه فاقة المحسِن إليه.
"مالك الملك"
هو الذي تنفذ مشيئته في مُلكه، تجري الأمور فيه','2','935',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1800,'على ما يشاء لا مردَّ لقضائه ولا معقب لحكمه".
"ذُو الجلال، والإكرام" هو الذي لا شرف ولا كمال إلاَّ وهو لهُ،','2','936',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1801,'ولا كرامة، ولا مكرمة إلاَّ وهي منه".
"المقسِط" العادل، الذي ينتصف للمظلومين ويدرأ بأس الظلمة عن المستضعفين.
"الجامع" المؤلف بين شتَّات الحقائق المختلفة.
"الغني والمغني" الذي يستغني عن كلِّ شيء لا يحتاج إليه في','2','937',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1802,'ذاته، ولا في شيء من صِفاته.
"المُغني" الذي وفر على كل شيء ما يحتاج إليه حسب ما اقتضته حكمته، وسبقت به كلمته، فأغناه من فضله.
"المانع" الذي يدفع أسباب الهلاك والنقصان في الأبدان، والأديان.
"الضَّار، النَّافع" هما كوصف واحد، وهو الوصف بالقدرة التامَّة الشاملة فهو الذي يصدر عنه النفع، والضر، ولا خير، ولا شر، ولا نفع، ولا ضر إلاَّ وهو صادر عنه منسوب إليه.
"النور" هو الظاهر بنفسه المظهر لغيره.','2','938',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1803,'"الهادي" هو الذي أعطى كل شيء خلقه ثم هدى.
"البديع" المبدع، وهو الذي أتى بما لم يسبق إليه.','2','939',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1804,'وقيل: هو الذي لم يعهد له مثل في ذاته ولا نظير في صفاته، ومرجعه بالمعنى الأول إلى صِفات الأفعال، وبالمعنى الثاني إلى صفات التنزيه.
"الباقي" الدائم الوجود الذي لا يقبل الفناء.
"الوارث" الباقي بعد فناء الموجودات، فترجع إليه الأملاك بعد فناء الملاك، وهذا بالنظر العامي، وأما بالنظر الحقيقي فهو المالك على الإطلاق من أزل الأزال إلى أبد الآباد لم يتبدَّل ملكه ولا يزال كما قيل.
"الوارث" الذي يرث بلا توريث أحد.','2','940',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1805,'"الباقي" الذي ليس لملكه أمد.
"الرَّشيد" "الذي تساق تدابيره إلى غاياتها على سنن السداد من غير استشاره، وإرشاد، وقيل: هو المرشد فعيل بمعنى مفعل، كالأليم، والوجيع".
"الصَّبُور" الذي لا يعجَل في مؤاخذة العُصَاة ومعاقبة المذنبين.
وقيل: هو الذي لا تحمله العجلة على المنازعة إلى الفعل قبل أوانه، وهو أعم من الأوَّل.
والفرق بينه، وبين الحليم، أنَّ الصَّبُور يشعر بأنه يعاقب','2','941',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1806,'بالآخرة، بخلاف الحليم".
"هذا حديث غريب حدثنا به غير واحد عن صفوان ابن صالح، ولا نعرفه إلاَّ من حديث صفوان بن صالح".
قال الحافظ ابن حجر: لم ينفرد به صفوان، فقد أخرجه البيهقي من طريق موسى بن أيوب النصيبي وهو ثقة عن الوليد أيضًا، [وقد روى] هذا الحديث من غير وجه عن أبي هريرة عن النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - ولا نعْلَم في كبير شيء من الروايات ذكر الأسماء إلاَّ في هذا الحديث، وقد روي آدم بن أبي إيَّاس هذا الحديثما بإسناد غير هذا عن أبي هريرة عن النَّبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - وذكر فيه الأسماء، وليس له إسناد صحيح".
قال الحافظ ابن حجر: وقع سَرد الأسماء في رواية زُهير بن محمَّد عن موسى بن عقبة عند ابن ماجه، وهذان الطريقان','2','942',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1807,'يرجعان إلى رواية الأعرج وفيهما اختلاف شديد في سرد الأسماء وزيادة، ونقص، ووقع سَرد الأسماء أيضًا في طريق ثالثة أخرجها الحاكم في المستدرك وجعفر الفريابي في الذكر من طريق عبد العزيز بن الحصَين عن أيوب عن محمَّد بن سيرين عن أبي هريرة، واختلف العلماء في سَردِ الأسماء هل هو مرفُوع، أو مدرج في الخبر من بعض الرُواة، فمشى كثير منهم على الأول، وذهب آخرون إلى أنَّ التعيين مدرج لخلو أكثر الروايات عنه، ونقله عبد العزيز النخشبي عن كثير من العُلماء.
قال الحاكم -بعد تخريج الحديث من طريق صفوان بن صالح عن الوليد بن مسلم-: صحيح على شرط الشيخين، ولم يخرجاه بسياق الأسماء، والعِلة فيه عندهما تفرد الوليد بن مسلم، قال: ولا','2','943',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1808,'أعلم خلافًا عند أهل الحديث، أنَّ الوليد أوثق وأحفظ، وأجل، وأعلم من بشر بن شعيب وعلي بن عياش، وغيرهما من أصحابِ شعيب.
قال الحافظ ابن حجر: يشير إلى أنَّ بشرًا وعليًّا وأبا اليمان رووه عن شعيب بدون سياق الأسماء، فرواية أبي اليمان عند البخاري، ورواية علي عند النسائي، ورواية بشر عند البيهقي، قال: وليست العِلة عند الشيخين تفرد الوليد فقط بل الاختلاف عليه، والاضطراب، وتدليسه واحتمال الإدراج.
قال البيهقي: يحتمل أن يكون التعيين وقع من بعض الرواة في الطريقين معًا، ولهذا وقع الاختلاط الشديد ولهذا الاحتمال ترك ْالشيخان تخريج التعيين ".','2','944',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1809,'٩٥٧ -[٣٥١٠] "إذا مَرَرْتُمْ بِرياضِ الجنَّةِ فَارْتَعُوا". قال في','2','944',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1810,'النهاية: " أراد برياض [الجنة] ، ذِكرَ الله وشبَّه الخوض فيه بالرَّتع في الخِصْب ".
"حِلَقُ الذِّكْرِ" قال في النهاية: " بكسر الحاء وفتح اللام: جمع حلقة مثل قَصْعَة وقِصَع، وَهي جماعة من الناس. يستديرون كحَلْقة الباب وَغيره.
قال الجوهري: " جمع الحَلقة حَلَق، بفتح الحاء على غير قياس " وعن أبي عمران الواحد حَلَقه بالتحريك، والجمع حَلَق بالفتح ".','2','945',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1811,'٩٥٨ -[٣٥١١] "فليقل إِنَّا لله وإنَّا إِلَيْهِ رَاجِعُونَ". قال الرافعي في تاريخ قزوين: " كلمة إنَّا لله إقرار بأنه المالك يفعَل في ملكه ما يشاء.
وإنَّا إلَيْهِ رَاجِعُونَ إقرار [إليه] بالفناء، والبعث، وقيل: معناه نرجع','2','945',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1812,'إليه ليكشف عنا مَا أصَابنا".
"فأجُرْني فيهَا" بالقصر وضم الجيم قال الرافعي: " يقال: آجره الله، يأجره، أي أثابه، والأجر، الثواب. وذكر بعضهم أنه يقال آجره بالمد أيضًا بهذا المعنى، وأن الأصمعي أنكره، [فإن] جوّز، فيجوز، أجرني، بالمد، وَأما من الأول فتسكن الهمزة، وتضم الجيم ".','2','946',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1813,'٩٥٩ -[٣٥١٦] "اللهم خِرْ لِي واخْتَرْ لِي" أي اختر لي أصلح الأمرين، واجعل لي الخيرة فيه.','2','946',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1814,'٩٦٠ -[٣٥١٧] "الوضوء شطر الإيمان". قال النووي: " أصل الشطر النصف، قيل معناه أن الأجر في الوضوء ينتهي إلى نصف أجر الإيمان، وقيل المراد بالإيمان الصلاة، قال الله تعالى: {وَمَا كَانَ اللَّهُ','2','946',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1815,'لِيُضِيعَ إِيمَانَكُمْ} والطهارة شرط في صحتها فصارت كالشطر، وليس بلازم في الشطر أن يكون نصفًا حقيقيًا".
"وسبحان الله، والحمد لله تملآن، أو تملأ". ضبط بالمثناة من فوق، فالأول: ظاهر، وَالثاني: فيها ضمير الجملة.
"ما بين السموات والأرض". أي لو قدر ثوابهما جسْما لملأ ذلك.
"والصَّلاة نورٌ". أي تمنع من المعَاصي وتنهي عن الفحشاء، والمنكر، وتهدي للصَواب كالنور، وقيل: أراد بالنور الأمر الذي يهتدي به صاحبه يوم القيامَة.
"والصدقة برهان". أي دليل على إيمان فاعلها.
"وَالصَّبْرُ ضِيَاءٌ". أي الصبر على طاعة الله، وعلى اجتناب معَاصِيه وعلى النائبات، والمكاره. لا يزال صَاحبه مستضيئًا مهتديًا مستمرًا على الصواب.
"والْقرآنُ حُجَّةٌ لَكَ أوْ عَليْكَ". معناه أنه ينتفع به إن تلاه وَعمل به وإلا فهو وبال عليْه.
"كُلُّ النَّاسِ يَغْدُو فَبَائِعٌ نَفْسهُ فَمُعْتقُهَا، أَوْ موبِقُهَا". معناه كل إنسان يَسْعى بنفسِه فمنهُم من يبيعهَا من الله بطاعته فيعتقهَا، ومنهم يبيعهَا من الشيطان والهوَى فيهلكهَا، قال الطيبي: "كل الناس يغدو مجمل','2','947',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1816,'والفاء في قوله: فبائع؛ تفصيلية، وفي قوله: فمعتقهَا سببيّة".
وقال الأشرفي: "بائع نفسه خبر، أي هو يشتري نفسه، بدليل قوله: فمعتقهَا، وَالإعتاق إنما يكون من المشتري، وَهو محذوف المبتدأ فإنه يحذف كثيرًا بعْد إلغاء الجزائيّة،
وقوله: "فمعتقها" خبر بعْد الخبر، وَيجوز أن يكون بدل بعض من قولهِ فبائع نفسه".','2','948',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1817,'٩٦١ -[٣٥١٩] "التَّسْبيحُ نِصفُ الْمِيزانِ، والحَمْد لله يَمْلَؤُهُ"
قال الطيبي فيه وجهان: "أحدهما أن يراد التسوية بين التسبيح والتحميد بأن كل وَاحد منهما يأخذ نصف الميزان، فيملآن الميزان معًا، وذلك لأن الأذكار التي هي أم العبادات البدنية، والغرض الأصْلي من شرعهَا ينحصر في نوعين: أحدهما: التنزيه، وَالآخر التمجيد. والتسبيح يستوعب القسم الأول، والتحميد [يتضمّن] القسْم الثاني.','2','948',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1818,'وَثانيهما أن المراد بيَان تفضِيل الحمد على التسبيح، وَأن ثوابه ضِعف ثواب التسْبيح لأن التسْبيح نصف الميزان والحمد لله وحده تملآن لأن الحمد المطلق إنما يستحقه من كان يتبرأ عن النقائص منعُوتًا بنعوت الجلال، وَصِفات الإكرام فيكون الحمد شاملاً للأمرين، وَأعلى القسمين حتى تخلص إليه، أي تصِل ".
"وَالصَّوْمُ نِصْفُ الصّبْرِ، والطُّهُورُ نِصْفِ الإيمانِ". قالَ في النهاية: " لأن الإيمان يطهر نجاسَة الباطن، وَالطهور يطهر نجاسة الظاهر ".','2','949',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1819,'٩٦٢ -[٣٥٢٠] "وَلَكَ رَبِّ تُرَاثي". قال في النهايهَ: "هو ما يُخَلِّفه الرّجُل لوَرَثتِه، والتاء فيه بدَل من الواو" قلت: كأنه يريد أنه لا يورث، وأن ما يخلفه صَدقة لله.','2','949',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1820,'٩٦٣ -[٣٥٢٢] "إلاّ وَقَلْبُه يَيْنَ أُصْبُعينِ من أصَابِع الله تعَالى".
قال في النهاية: "الأصابع: جمع أصبع، وَهي الجَارحةُ. وذلك من صفات الأجسَام، تعالى الله عن ذلك وتقدّس. وَإطلاقُهَا عليه مجازٌ، كإطلاق اليدِ، وَاليمينِ، والعَينِ والسّمع، وَهو جاري مَجْرى التمثيل، والكِنايَة عن سُرْعَة تَقَلَّب القُلُوب، وإن ذلك أمرٌ معقُود بمشيئةِ الله تعالى، وتخصِيصُ ذكر الأصابع كِنايَةٌ عن إجراء القُدْرَة، والبَطْشِ؛ لأن ذلك باليَدِ، وَالأصَابعُ أجزاؤُهَا".','2','950',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1821,'٩٦٤ -[٣٥٢٣] "وَرَبَّ الشَّياطينِ وَمَا أضَلَّتْ". كان الأصل،','2','950',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1822,'وما أضلوا لكن رُوعي أظلت، وأقلت، للازدواج.','2','951',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1823,'٩٦٥ -[٣٥٢٤] "إذا كَرَبهُ أمْر"ٌ. أي أصابه كرب بسَببه.','2','951',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1824,'٩٦٦ -[٣٥٢٤] "ألِظُّوا بِيَاذا الْجَلالِ وَالإكْرَامِ". أي الزمُوه، وَاثبتوا عليه، وأكثروا من قوله، والتلفظ به في دعائكم.','2','951',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1825,'٩٦٧ -[٣٥٢٩] "وأَنْ أقْتَرِفَ". أي أكسب.','2','951',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1826,'٩٦٨ -[٣٥٣٤] "حدثنا قتيبة، حدثنا الليث عن الجلاح أبي كثير، عن أبي عبد الرحمن الحلبي، عن عمارة بن شبيب','2','951',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1827,'السبَائي، قال: قالَ رَسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: من قالَ: لا إله إلاّ الله وَحْدهُ لاَ شَرِيكَ لهُ لهُ الْمُلْكُ، وَلهُ الْحمدُ يُحييْ، وَيُميتُ، وَهو على كُلِّ شَيْءٍ قَديرٌ عَشْرَ مَرَّاتٍ إثْرِ الْمَغْرِبِ بَعثَ الله لهُ مَسْلَحةً يَحْفَظُونهُ من الشِّيَاطينِ حتى يصبحَ وكتب له بهَا عشر حسَنات موجبَات، ومحى عنه عشر سيئات موبقات، وَكَانتْ لهُ تعدلُ عَشْرِ رِقَابٍ مُؤمِناتٍ".
"هذا حديث حسن غريب لا نعرفه إلا من حديث ليث بن سَعد وَلا نعرف لعمارة بن شبيب سَماعا من النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -".
قلت: أخرجه ابن أبي الدنيَا في كتاب "الدعاء" بزيادَة في سَنده، ومتنه، قال: أخبرت عن أبي عبيد الله ابن أخي ابن وَهب، أخبرنا ابن','2','952',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1828,'وهب، أخبرني عمرو بن الحارث عن الجلاح، أن أبا عبد الرحمن المغافري حدثه عن عمار السبائي أن رجلاً من الأنصَار حدثه، أن رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - قال: " من قالَ بعد المَغْرِبِ، أو الصبح لاَ إلهَ إلاّ الله وَحْدهُ لا شَرِيكَ لهُ، لهُ المُلْكُ، وَلَهُ الْحمدُ، يُحْيي، وَيُميتُ، وهو على كُلِّ شَيْءٍ قَديرٌ عَشْرَ مَرَّاتٍ، بَعَثَ الله لهُ مَسْلَحةً يَحرسُونهُ حتى يَصْبِح، أو من حين يُصْبِح حتى يُمسي، وكتب الله له بهَا عَشْرَ حَسنات موجبَات، ومحي عَنْهُ عَشَر سَيئاتٍ موجبَات".
قال الحافظ ابن حجر في الإصَابَة: " عمارة بن شبيب السبَائي؛ بفتح المهملة، وَالموحدة وَهمزة مكسُورَة مقصُورَة، مختلف في صحبته وَقيل: عمار، قال ابن السكن له صحبة، وقال ابن يونس: حديثه معلول، وَبين البخاري علته في تاريخه، وذكره في الصحَابَة، وقال ابن حبان: من قال أن له صحبَة فقد وَهم، قَال أبو عمر مَات سنة خمسين، وقال ابن أبي حاتم، قلت لأبي: له صحبَة، قال: مَا أدري، كتبناه على الظن في الوحدان، وصحف ابن فتحون اسم أبيه، فقال: عمارة بن','2','953',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1829,'حبيب، وصحفه أبو علي البكري، فقال: عمَارة بن ثُبَيْت، بمثلثه ثم موحدة مصغر، وَآخره مثناة، وَهو تصحِيف أيضًا، والصواب شبيب بالمعجمة، انتهى".
"بَعثَ الله لهُ مَسْلَحةً". هم القوم الذين يحفظون الثغور من العدو، سمّوا مَسْلحة، لأنهم يكونون ذوي سلاح.','2','954',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1830,'٩٦٩ -[٣٥٣٦] "بِصَوْتٍ جَهُوريٍّ". أي شديد عال والواو زائدة، وهو منسوب إلى جَهور بصوته.','2','954',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1831,'"هَاؤُمُ".
"جِلْفٍ". هو الأحمق.','2','955',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1832,'٩٧٠ -[٣٥٣٧] "ما لم يُغَرْغِر". أي ما لم تبلغ روحه حلقومه، فيكون منزلة الشيء الذي يتغرغر به المريض.','2','955',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1833,'٩٧١ -[٣٥٤٠] "بِقُرابِ الأرْضِ". قال في النهاية: " أي بما يُقارِب مَلأَهَا، وهو مصدر: قارب، يُقارِب ".','2','955',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1834,'٩٧٢ -[٣٥٤٣] "إنَّ رَحْمتي تَغْلِبُ غَضَبي". قال في النهاية:','2','955',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1835,'"هو إشارَة إلى سعَة الرحمة، وشمولها الخلق، كما يقال: غلب على فلان الكرم؛ أي هو أكثر خصَاله، وإلا فرحمة الله، وغضبه صفتان راجعتان إلى إرادته الثواب، والعقاب، وصفاته لا توصف بقلبة إحداهما الأخرى، وإنما هو على سبيل المجَاز للمبالغة".','2','956',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1836,'٩٧٣ -[٣٥٤٥] "رَغِمَ أنفُ رَجُلٍ". أي ذل، وعجز.','2','956',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1837,'٩٧٤ -[٣٥٤٨] "إنَّ الدّعاءَ يَنْفعُ مِمَّا نَزلَ، ومِمَّا لم يَنْزلْ". قال التوربشتي: "أما نفعه فصَبره عليه، وتحمله لهُ، ورضاه به حتى لا يكون هريرة.','2','956',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1838,'في نزوله متمنيا خلاف مَا كان، وَأما نفعه مما لم ينزل فهو أن يصرفه عنه، أو يمدّهُ قبل النزول بتأييد من عنده حتى يَخف معَه أعبَاء ذلك إذا نزل به".','2','957',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1839,'٩٧ -[٣٥٤٩] "عَلَيْكُمْ بِقِيامِ اللّيْلِ فَإنَّهُ دَأْبُ الصّالِحينَ". قال في النهاية: " الدَأْبُ: العادةُ والشَّأنُ، وقد يُحرَّك، وأصله من دَأب فِي العَملِ إذا جَدَّ وتَعِب، إلاَّ أنّ العَرب حَوَّلَت مَعْنَاهُ إلى العَادَةِ، والشأنِ ".
"قَبْلَكُمْ". قال الطيبي: " أي هي عبَادة قديمَة واظب عليَها الأنبيَاء، والأولياء [السابقون] ".
"وَمَنْهاةٌ عن الإثْمِ". قال في النهاية: "أي حالةٌ عَن الإثم، وهي','2','957',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1840,'مَكانٌ مُخْتصٌّ بذلك. وهي مغفلة من النَّهي، وَالميم زائدة".
"ومَطْرَدَةُ لِلدَّاءِ عن الْجَسدِ". قال في النهاية: "أي أنها حالةٌ من شَأْنها إبعَادُ الدَّاء، أو مكانٌ مختص به ويعرف، وَهوَ مَفْعَلة من الطَّرد" [وَقال الموفق عبد اللطيف البغدادي] .
" ومُكفَرةٌ لِلسَّيئَاتِ " قال البيضاوي: " أي خصلة تكفر سيّئاتكم ".','2','958',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1841,'٩٧٨ -[٣٥٥١] "وَامُكُر لي، وَلا تَمْكر عَليَّ"؛ قال في النهاية: "مَكْرُ الله إيقاِعُ بَلائه بأعدَائه دون أوليائه. وَقيل: هو اسْتِدْرَاج العبد بالطاعَاتِ، فيَتَوَهَّم أنها مقبُولةٌ، وَهيَ مردودةٌ. والمعنى ألْحِقْ مَكرَك بأعدائي لا بي. وأصلُ المَكْرُ: الخِدَاعُ".
"مخْبِتًا". قال في النهاية: "أي خاشعًا مُطيعًا، والإخْبَاتُ:','2','958',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1842,'الخُشوع، والتَّواضُع، وقد أخْبتَ لله يُخْبِتُ".
"أوَّاهًا". قال في النهاية: "الأوّاه: المتأوّه المُتَضرّع. وقيل هو الكثير البكاء. وقيل الكثير الدُعاء".
"مُنِيبًا". قال في النهاية: " الإنابة: الرجوع إلى الله تعَالى بالتَّوبَة، يقال: أناب، يُنيب، إنَابَةً فهو منيب إذا أقبل ورجع ".
"وَاغْسلْ حَوْبَتي". أي إثمي.
"وَثبِّتْ حُجَّتي". قال في النهاية: "أي قَوْلي، وتصديقي في الدُّنيا وعنْد جواب الملَكَيْن في القَبْر".
"وَاسْلُلْ سَخِيمةَ صَدْرِي". قال في النهاية: " هي الحقد في النفس ".','2','959',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1843,'٩٧٩ -[٣٥٥٣] "عِدْلَ أرْبعِ رِقابٍ من وَلَدِ إسْماعِيلَ". قال الطيبي: "من ولد" صفة رقاب، المعنى حصَل له من الثواب مثل مَا لو اشترى من أولاد إسْمعِيل وأعتقهُم، وإنما خصْهُ لأنه أشرَف الناس".
وفي النهايَة: "العِدْل بالكسر وبالفتح. وهما بمعنى المِثْل. وَقيْل:','2','959',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1844,'بالفتح مَا عدَلَه من جنْسِه، وبالكسْر مَا ليسَ من جنْسِه. وَقيل بالعَكس".','2','960',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1845,'٩٨٠ -[٣٥٥٦] " [إن الله حيي كريم، يسْتحي إذا رفع الرجل إليه يَديه] أن يردهما صِفرا ". أي خاليين.','2','960',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1846,'٩٨١ -[٣٥٥٧] "أحد، أحد". قال في النهاية: "أي أشار بإصبع وَاحدة لأن الذي تدعُو إليه وَاحد، وِهو الله تعَالى".','2','960',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1847,'٩٨٢ -[٣٥٥٩] "مَا أصَرَّ من اسْتَغْمْر". قال في النهاية: " أصرَّ على الشيء، يُصِرُّ، إصْرَارًا إذا لزِمه، ودَاوَم عليه وثبت عليه. وَأكثر مَا يُسْتَعْمَل في الشرِّ والذنوب، يعني من أَتْبَع الذنب بالاستغفارِ فليس','2','960',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1848,'بمُصِرٍّ عليه، وإن تكرر منه".
"ولو فعَله في اليَوْمِ سَبْعينَ مَرَّةً". قال البيهقي في شعب الإيمان: قال الشيخ أبو بكر محمد بن علي الشاشي: المراد بالسبعين هنا الكثرة، لا عدد السبعين بعينه.
"كَانَ في كَنْفِ اللهِ": أي في ظل رَحمته.','2','961',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1849,'٩٨٣ -[٣٥٦٣] "لَو كَانَ عَليْكَ مِثْلُ جَبلِ صِيرٍ". وفي نسْخة صبرٍ وصوب الأول، قال في النهاية: " هذه الكلمة جاءت في حَدِيثين لِيَعلى، ومعَاذ: أمَّا عليّ فهو [صير] ، وهو جبل لِطَيء، وَأما معَاذ، فصبِيرٍ، وَهو اسم جَبَل باليَمَن كذا كذا فرق بينهما بعضهم ".','2','961',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1850,'٩٨٤ -[٣٥٦٥] "لا يُغَادرُ". أي لا يترك.','2','961',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1851,'٩٨٥ -[٣٥٦٧] "مِن أرْذَلِ العُمرِ". أي آخره في حال الكبر، والعجز، وَالخوف.','2','962',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1852,'٩٨٦ -[٣٥٧٠] "حدثثا أحمد بن الحسَن، حدثثا سُليمان بن عبد الرحمن الدمشقي، حدثنا الوليد بن مُسْلم، حدثنا ابن جريج عن عطاء بن أبي رَباح، وعكرمة مولى ابن عباس، عن ابن','2','962',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1853,'عباس، قال: بَيْنما نَحْنُ عِنْدَ رَسُولِ اللهِ - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، إذْ جَاءهُ عَليُّ بن أبي طَالِبٍ، فقال: أنت بأبي، وأُمِّي لقد تَفلَّتَ هذا القُرْآن من صَدْري".','2','963',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1854,'الحديث، هذا الحديث أورده ابن الجوزي في الموضُوعات".','2','964',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1855,'٩٨٧ -[٣٥٧١] "سَلوا الله من فَضْلِه فَإنَّ الله يُحبُّ أنْ يُسألَ، وَأفْضلُ العِبَادَةِ انْتظارُ الفَرَجِ". قال المظهري: " يعني إذا نزل بأحد بَلاء فترك الشكايَة، وَصبر، وَانتظر الفرج فذلك أفضل العبَادات، لأن الصّبر في البلاء انقيَاد لقضاء الله وإنما استتبع انتظار الفرج.
قوله: " يحب أن يسأل " لأن المراد بقوله: سلوا الله من فضله ادعُوا الله لإذهاب البلاء، والحزن، وانتظروا الفرج، ولا تستعجلوا في طلب إجابة الدُعاء ".','2','964',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1856,'٩٨٨ -[٣٥٧٣] "إذًا نُكْثر، قال: الله أكْثرُ". قال الطيبي: "أي','2','964',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1857,'أكثر إجابَة مِن دعائكم، المعنى إن إجابة الله في بابهَا أكثر، وأبلغ من دعائكم في بابه وهو قريب من قولهم: العسل أحلى من الخل، والصيف أحر من الشتاء، وإنما قال أكثر بالثاء المثلثة مشاكلة لقول: نكثر".','2','965',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1858,'٩٨٩ -[٣٥٧٧] "أستَغْفِرُ الله العظيم الّذِي لا إلهَ إلا هو الحيُّ القَيُّومُ". قال الطيبي: " يجوز في الحي القيوم النصب، صفة الله تعالى أو مدحًا، وَالرفع بدلاً من الضّمير، أو خبر مبتدأ محذوف على المدح ".
"من الزَّحف". هو الجيش الدهم الذي يرى لكثرته كأنه يزحف، أي يدب، دبيبًا.','2','965',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1859,'٩٩٠ -[٣٥٧٩] "أقْرَب مَا يكُون الرَّب من العَبْدِ في جَوْفِ الليْلِ الآخرِ". قال الطيبي: " الآخر: صفة لجوف الليل، على أن ينصف الليل ويجعل لكل نصف جوف، والقرب يحصُل في جوف النصْف','2','965',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1860,'الثاني، فابتداؤه يكون من الثلث الآخر، وَهو وقت القيام للتهجّد. قال:
وقوله: " في جوف الليل " يحتمل أن حالاً من الرب؛ أي قائلاً في جوف الليل: " من يدعوني فأستجيب [له] " سَدت مسَدّ الخبر، أو من العَبْد أي قائما في جوف الليل داعيًا، مستغفرًا على نحو قولك ضربني زيدًا قائمًا، ويحتمل أن يكون خبرًا الأقرب.
فإن قلت مَا الفرق بين قوله في هذا الحديث: " أقْرَبُ مَا يكُوُن الرَّب من العَبْدِ " وفي الحديث الآخر: "أقرب مَا يكون العبد من ربه وَهو سَاجد". قلت: " رحمة الله سابقة، فقرب رَحمة الله من المحسنين سَابق على إحسَانهم، [فإذا سجدُوا] قربوا من ربهم بإحسَانهم، كما قال تعَالى: {وَاسْجُدْ وَاقْتَرِبْ (١٩) } وَفيه أن توفيق الله، وَلطفه، وإحسَانه سَابق على عمل العَبْد، وسبَب له، وَلولاه لم يصْدر من العَبْد خير قط. وَفي قوله: " فإن اسْتَطَعْتَ " إشارة إلى تعظيم شأن الذكر، وتفخيمه، وفوز من يستعد به، وَمن ثمّ قالَ: " أنْ تكُونَ مِمَّنْ يَذْكُرُ الله " أي تنخرط في زمرة الذاكرين الله، ويكُون لك مسَاهمة فيهم وَهو أبلغ مما لو قيل: إن استطعت أن تكون ذاكرًا".','2','966',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1861,'"عن أمه حُمَيْضَةَ". بضم الحاء المهملة، وفتح الميم، وَالضاد المعجمة بينهما تحتِية ساكنة.
"عنْ جدتها يُسَيْرة". بمثناة تحتية مصغر.','2','967',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1862,'٩٩١ -[٣٥٨٣] "عَليْكُنَّ بالتَّسْبيح، والتَّهْليل وَالتّقْديسِ". قال الحكيم، في نوادره: "التهليَل هو التوحيد والتقديس، التنزيه، وَهو التطهير، قالَ: والفرق بينه وبين التسْبيح، أن التسْبيح للأسْماء،
والتقديس للآلاء وكلاهُما يؤدّيان إلى الطهر".','2','967',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1863,'٩٩٢ -[٣٥٨٥] "خيْرٌ الدُّعَاءِ، دُعَاءُ يَوْمِ عَرَفَةَ". قال الطيبي: "الإضافة فيه أن يكون بمعنى اللام أي دعاءً خصّ بذلك اليوم، وَقوله:','2','967',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1864,'"وَخَيْرُ مَا قُلْتَ" بمعنى خير مَا دعوت، بيَان له، فالدعاء له؛ قوله "لا إلهَ إلا الله ... " إلى آخره. فإن قيل هُوَ ذكرٌ ليس بدعاء، أجيب بوجهين: أحدهما: أنه على سَبيل التعريض تجنبًا من التصْريح مُراعاة للأدب، وقد قيل لسُفيان هذا الثناء، فأين الدعاء فأنشد قول أمية بن أبي الصلت:
إذا أثنى عليك المرء يومًا ... كفاه من تعرضه الثناء
والثاني: الاشتغال بخدمة المولى، والإعراض عن الطلب اعتماد على كرمه فإنه لا يضيع أجر المحسنين، والفرق بين الوجهَين أن الذاكر في الأولى وإن لم يصَرّح بالطلب فهو طالب بما هو أبلغ من التصْريح بخلاف الثاني انتهى".
وروى البيهقي في "شعب الإيمان" من طريق يعقُوب بن سُفيان قالَ: حدثنا الحسين بن الحسَن المروزي قال: سألت سُفيان بن عيينة عن تفسير قول النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "أكثر دعائي، ودعاء الأنبياء قبلي بعرفة لا إله إلا الله وَحده لا شريك له، له الملك وله الحمد وَهو على كل شيء قدير" وإنما هو ذكر ليس [فيه] دعاء، قال سُفيان: تعرف حديث منصور عن مَالك بن الحارث: يقول الله تبارك وتعَالى: "من','2','968',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1865,'شغله ذكري عن مسئلتي أعطيته أفضل مَا أعطي السائلين" قلت: نعَم، قال: ذاك تفسير هذا، ثم قال: أتدري ما قال أمية بن أبي الصلت حين أتى ابن جدعان يطلب نائله، ومعروفه؟ قلت: لا، قال: لما أتاه قال:
أأذكر حاجتي أم قد كفاني ... ثناؤك إن شيمتك الحياء
إذا أثنى عليك المرء يومًا ... كفاه من تعرضه الثناء
قال سُفيان: هذا مخلوق حين ينسَب إلى الجود قيل يكفيك من تعرضه الثناء عليك حتى تأتي على حاجتنا فكيف بالخالق ".','2','969',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1866,'٩٩٣ -[٣٥٨٦] "أسْألُكَ من صَالِحِ مَا تُؤتي النَّاسَ من المَالِ، وَالأهْلِ، وَالوَلدِ". قال الطيبي: " منْ -الأولى- زائدة على مذهب الأخفش، (ويجُوز أن تكون بمعنى البعض) ومِنْ -الثانية-: بيان مَا.
وَقوله: "غَيْرِ الضَّالِّ " مجرور بدل من كل وَاحد من المال، والأهل،','2','969',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1867,'وَالولد، على سَبيل المبدل، والضال هنا يحتمل أن يكون للنسْبَة أي غير ذي ضلال".','2','970',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1868,'٩٩٤ -[٣٥٩١] "من مُنْكراتِ الأخْلاقِ، وَالأعْمالِ والأهواءَ".
قال الطيبي: " الإضافة في القرينتين الأوليين إضافة الصفة إلى الموصوف، والثالثة: بمعنى، لأن الأهواء كلها منكرة ".','2','970',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1869,'٩٩٥ -[٣٥٩٦] "المستهترون في ذكر الله". قال في النهاية:
"يعني الذين أولعُوا به".','2','970',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1870,'٩٩٦ -[٣٦٠٠] "فُضْلاً". قال في النهاية: "أي زائدة عن','2','970',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1871,'الملائكة المُرَتَّبين مع الخلائق، ويُروى بسُكون الضاد وضمّهَا، قال بعضهم: والسكون أكثر وأصْوَب، وهما مَصْدر بمعنى الفَضْلة، والزيادة".
"هَلُمُّوا". أي تعالوا، فيحفّون بهم، أي يطوفون بهم ويدُورُون حولهُم.
"هُمُ القَوْمُ لا يَشْقَى لَهُمْ جَلِيسٌ". قال الطيبي: "بمعنى أن مجالستهم مؤثرة في الجليس، فإذا لم يكن للجليس نصيب مما أصابهم كان محرومًا فيشقى، فإذًا لا يستقيم وصفًا لقوم بهذه الصفة، ولو قيل هم قوْم يسعد بهم جليسهم لم يكن بهذه الحيثية".','2','971',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1872,'٩٩٧ -[٣٦٠٢] "لِكُلِّ نبيٍّ دَعْوَةٌ مُسْتجابةٌ". أي في حق أمته،','2','971',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1873,'فكل من الأنبياء نالهَا في الدنيَا بإهلاك قومه.
"وَإنِّي اخْتَبَأتُ دَعْوَتِي". أي ادخرتها، وجعلتهَا خبيئة.
"وَهِي نَائِلةٌ". أي واصلة.
"من مَاتَ". في محل النصب على أنه مفعُول نائلة.
"لا يُشْرِكُ بِالله ". نصب على الحال من فاعل مَات.','2','972',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1874,'٩٩٨ -[٣٦٠٣] "أنا عِنْدَ ظَنِّ عَبْدِي بِي". قال البيضاوي:','2','972',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1875,'"يصح إجراء الظن على ظاهِره؛ أي أعامله على حسب ظنه، وأفعل به مَا يتوقعه مني، والمراد الحث على تغليب الرجاء على الخوف وحسن الظن بالله، ويجوز أن يفسّر بالعِلم والمعنى أنا عند يقينه بي وعلمه بأن مصيره إليّ، وحسابه عليّ، وأن ما قضيت له من خير وشرّ فلا مَرد له، "لا مُعطي لما منعت، ولا مَانع لما أعطيت". أي إذا تمكن العبد في مقام التوحيد ورسخ في الإيمان والوثوق بالله تعَالى قرب منه وَرفع دونه الحجاب بحيث إذا دعاه أجاب، وإذا سأله استجاب.
"وَأنا معَهُ حِينَ يَذْكُرني". أي بالتوفيق، والمعُونة أو أسمع ما يقول.
"فإن ذَكَرني في نَفْسِهِ". أي سرًّا أو خفية إخلاصًا، [وتجنبًا] للرياء.
"ذكَرتُه في نفسِي". أي أُسِرُّ بثوابه على منوال عَمله، وأتولى بنفسِي إثابته لا أَكله إلى أحَد من خلقي.
"وَإنْ ذَكَرني في مَلأٍ ذَكَرتهُ في مَلأٍ خَيْرٍ منْه". أي ملأ من الملائكة المقربين، وأرواح المرسلين، والمراد منه مجازاة العبد بأحسَن مما فعَلهُ وأفضل مما جاء به" انتهى كلام البيضاوي، قال الطيبي:','2','973',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1876,'"وإنما قيده بقوله، وَأروَاح المرسلين لئلا يسْتدل بهذا الحديث على أن الملائكة أفضل من البشر، على أن المراد من الملأ الملائكة فحسب".
قال: وقوله: "ذَكَرتهُ في نَفْسِي" جاء على سَبيل المشاكلة.
"وَإن اقْتَرَبَ مني شبرًا اقْتَرَبْتُ مِنْهُ ذِرَاعًا" إلى آخره. قال النووي: " هذا من أحاديث الصفات ويستحيل إرادَة ظاهِره، ومعناه: من تقرب إلىّ بطاعتي تقربت إليه برحمتي، وَإن زاد، زدته ".','2','974',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1877,'٩٩٩ -[٣٦٠٤] "لذعته حُمةٌ". قال في النهاية: " هي بالتخفيف: السَمُّ، وَقد تشَدّد، وأنكره الأزهري، ويُطْلَق على إِبْرة العَقْرب للمُجاورَة لأنّ السمَّ منهَا يَخْرج، وأصلُها حَموٌ وحُمَيٌ بوزن صُرَدْ. والهاء فيهَا عوض من الواو المحذوفة والياء ".','2','974',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1878,'١٠٠٠ -[٣٦٠٤] "اللَّهُمَّ مَتِّعني بِسمعي، وَبَصري، وَاجْعَلْهما الوارث مِنِّي". ذكر أن المراد بالسمع، والبصر هنا، أبو بكر، وَعمر','2','974',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1879,'لقوله في الحديث الآتي، هذان السمع، والبصَر.','2','975',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1880,'١٠٠١ -[٣٦٠٤] "حتى يْسألَ شِسْعَ نَعْلهِ". هو أحد سيور النعل، وَهو الذي يدخل بين الإصبعين، ويدخل طرفه في الثقب الذي هو صَدر النعل المشدود في الزمَام، والزمَام السير الذي يدخل فيه الشسع".','2','975',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1881,'"<span data-type="title" id=toc-56>أبَواب المناقب</span>"','2','976',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1882,'١٠٠٢ -[٣٦٠٧] "كَمَثلِ نَخْلَةٍ في كَبْوَةٍ". قال في النهاية: "قال شَمِر: لم تُسْمع الكَبْوةَ، ولكنا سَمِعْنا الكِبَا، والكُبَة، وَهي الكُنَاسَة والتُّراب الذي يُكْنَس من البَيْت. وقال غيره: [الكُبَة] من الأسماء الناقصة، أصْلها: كُبْوَة، مثل قُلَةٌ، وَثُبَة؛ أصلها قُلْوَة، وثُبْوَة.
وَيقال للَّربْوة، كُبْوَة بالضم. قال الزمخشري: الكِبَا: الكُنَاسَة، وجَمْعُهَا: أكْبَاء. والكُبَة بوزن قُلَة، وَظُبَة نحوها، وأصْلهَا: كبوة وعلى الأصل جاء الحديث، إلا أنّ المُحَدِثَ لم يضْبط الكلمة فجعَلها كَبْوة بالفتح، فإن صحَّت الرِّوايَة بهَا فَوَجْهِه أن تُطْلق الكَبْوَة. وَهي المرَّة الواحِدة من الكَسْح على الكُسَاحَة، والكُنَاسَة".','2','976',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1883,'١٠٠٣ -[٣٦٠٩] "مَتَى وَجبت لَكَ النُّبُوةُ؟ قال: وَآدمُ بَيْنَ الرُّوحِ،','2','976',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1884,'والْجَسَدِ". قال الرافعي: هو معنى قوله: أنا أول الناس خروجًا.
وقال السبكي: " أنا أوّل من تنشق عنه الأرض إذا بعثوا كنت أمام النبيين ".
قال التوربشتي: " هو بكسْر الهمزة وَالذي يفتحهَا، وينصبه عَلى الظرف، فإنه لم يصب ".
- ٣٦١٣ "وَصَاحب شفاعتهم" قال الرافعي في تاريخ قزوين: " يجوز أن يقال معناه، وصَاحب الشفاعة [العَامّة بينهم، ويجوز أن يريد وصَاحب الشفاعة] لهُم ".','2','977',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1885,'١٠٠٤ -[٣٦١٦] "وأنا أول شافع وأنا أول مشفع". قال','2','977',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1886,'الرافعي: " فيه دليل على أن غيره يشفع، ويشفع وكونه أولاً في الشفاعَة، والتشفيع يبين علوّ مرتبته ".','2','978',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1887,'١٠٠٥ -[٣٦١٨] "حتَّى أنكرْنا قُلُوبَنا". قال التوربشتي: " يريد أنهم لم يجدُوا قلوبهم على مَا كانت عليه من الصّفا، والألفة والرقة لإنقطاع مَادَّة الوحي، وفقدان مَا كانوا يمدّون به من قبل الرسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - من التأييد والتعليم، وَلم يرد أنهم لم يجدُوهَا على ما كانت عليه من التصديق ".','2','978',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1888,'١٠٠٦ -[٣٦١٩] "خَذْق الفِيلِ". بخاء، وذال معجمتين','2','978',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1889,'وقاف؛ أي روثه.
"محيلا". بحاء مهْملة؛ أي متغيرًا.','2','979',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1890,'١٠٠٧ -[٣٦٢٠] "عن أبي بكر بن أبي موسى الأشعري، عن أبيه، قال: خَرَجَ أبو طالبٍ إلى الشَّام، ومَعَه النبيُّ - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - في أشْياخٍ من قُرَيْش فَلمَّا أشْرفُوا على الرَّاهب ... " الحديث.','2','979',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1891,'١٠٠٨ -[٣٦٢٣] "بالطّويلِ البَائنِ". أي المفرط طولاً الذي يعد عن قد الرجال الطوَال.
"ولا بِالأبْيَضِ الأمْهَقِ". هو الكريه البيَاض كلون الجص، يريد أنه كان نيّر البيَاض.
"ولا بالأدم". هو الأسمر الشديد.','2','980',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1892,'١٠٠٩ -[٣٦٣٢] "كفلق الصُّبح". بفتح الفاء، وَاللام ضؤه،','2','980',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1893,'وإنارته.
"صلصَلة". هي صَوت الحدِيد إذا حرَّك.
"فَيفْصمُ عَنهُ". بالفاء، أي يقطع.
"وَإنَّ جَبِينه لَيَتَفصَّدُ عرقًا". بالفاء، أي يسيل تشبيهًا في كثرته بالفصَاد.
"وعرقًا". منصوب على التمييز.','2','981',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1894,'١٠١٠ -[٣٦٣٧] "ضخم الكرادِيس". هي رؤوس العظام، واحدهَا كردوس، وَقيل: هي ملتقى كل عظمين، كالركبتين، وَالمرفقين، والمنكبين أرَاد أنه ضخم الأعضاء.','2','981',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1895,'"تَكفَّأ". تميل.','2','982',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1896,'١٠١١ -[٣٦٣٨] "أجردَ ذُو مَسْربَة". قال في النهاية: " الأجْرد؛ الذي ليس على بدنه شَعَر، وَلم يكن كذلك، وإنما أراد به أن الشَّعر كان في أمَاكن من بدنه كالمسْرُبَة، والسَاقَين فإن ضِدّ الأجْرَد الأشْعَرُ، وهو الذي على جميع بدَنه شَعَرٌ ".
"لهَجَة". هو اللسان.
"وأليَنُهم عَرِيكةً". قال في النهاية: " العَرِيكةُ: الطَّبيعة. يقال: فُلان ليِّن العرِيكة، إذا كان سَلِسًا، مُطَاعًا، منْقَادًا، قليل الخلاف، والنُّفُور ".','2','982',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1897,'١٠١٢ -[٣٦٣٩] "ما كان - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - يَسْرُدُ سَرْدكَمْ". قال في النهاية:','2','982',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1898,'"أي لم يكن يتابع الحديث ويَسْتَعجل فيه".
"فَصْلٌ". أي بَيّن ظاهر يفصل بين الحق، والبَاطل.','2','983',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1899,'١٠١٣ -[٣٦٤٣] "مِثْلُ زِرِّ الْحَجَلةِ". قال في النهاية: " الزِّرُّ: وَاحِدُ الأزْرَار التي تُشَد بهَا الكَلَلُ، والسّتورُ على مَا يكون في حَجَلة العَروس، وقيل إنما هو بتقديم الراء على الزاي، ويريد بالحَجَلة القبجَة، مأخوذ من أرَزَّت الجَرادة إذا كبست ذنَبهَا في الأرض فباضَت قالَ: وَيشهَد له قوله: في الحديث الذي يليه:','2','983',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1900,'١٠١٤ -[٣٦٤٤] "غُدَّةً حَمْراءَ مثل بيضَةِ الحمَامَة" انتهى،','2','983',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1901,'"وغدة" بالدال المهملة وصحفهَا بعض أهل عصرنا بالزاي، وسألني عنها فقلت له: إنما هي بالدال.','2','984',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1902,'١٠١٥ -[٣٦٤٥] "حُمُوشةٌ". بحاء مهمَلة، وشين معجَمة، أي دقة.','2','984',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1903,'١٠١٦ -[٣٦٤٦] "ضليع الفَمِ". قال فيِ النهاية: " أيْ عظيمة.
وقيل واسِعَة والعَربُ تحمد عِظَمَ الفَمِ، وتذمُّ صِغره ".
"أشْكَلُ العَينينِ". قال في النهاية: " أي في بَيَاضها شيء من حُمْرة، وهو محمودٌ محبوبٌ ".
"منهوس العقب". قال في النهاية: "يروى بالسين وبالشين','2','984',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1904,'أيضًا".','2','985',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1905,'١٠١٧ -[٣٦٤٨] "كأنَّ الشّمسَ تجري في وَجْههِ". قال الطيبي: " شبه جريَان الشمس في فلكها، بجريان الحسن في وَجهه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، وفيه عكس التشبيه للمبَالغة، قال: ويحتمل أن يكون من بَاب تناهي التشبيه جعل وَجهه مقرًّا ومكَانًا للشمس ".','2','985',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1906,'١٠١٨ -[٣٦٥٥] "ولو كنت متخذًا خليلا لاتخذت ابن أبي قحافة خليلاً". قال البيضاوي: " الخليل الصّاحب الوادّ الذي يفتقر إليه ويعتمد في الأمُور عليه فإن أصل التركيب للحاجَة، والمعْنى لو كنت','2','985',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1907,'متخذًا من الخلق خليلا أرجع إليه الحاجات وَأعتمد عليه في المهمّات لاتخذت أبا بكر، ولكن الذي ألجأ إليه وَأعتمد عليه في جملة الأمور، وَمجامع الأحوال هو الله تعَالى".','2','986',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1908,'١٠١٩ -[٣٦٥٨] "وأنعما". قال في النهَاية: " أي زاد فَضَلا، يقال: أحسَنْتَ إلي وَأنْعَمْتَ: أي زِدتَ عليّ الإنْعام. وَقيل: معناه صَار إلى النعيم وَدخلا فيه كما يقال: أَشْمَلَ، إذا دَخل في الشِّمال ".
وفي تاريخ ابن عسَاكر في آخر الحديث فقلت لأبي سَعِيد، ومَا أنعما، قال: وَأهل ذلك هما، ومن طريق آخر، قال: أتدري مَا أنعما، قال لا، قال: وحق لهما. ومن طريق أحمد بن حنبل سمعتُ سفيَان بن عيينة يقول: وأنعما؛ قال: وَأهلا، ومن طريق خالد بن محمد بن','2','986',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1909,'خالد قال: سمعت أبا عبيد القاسم ابن سَلام يقول: معنى قوله في هذا الحديث: وأنعما؛ يعني وأرفعا.','2','987',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1910,'١٠٢٠ -[٣٦٦٤] "هَذان سَيِّدَا كهولِ أهْلِ الجنَّةِ". قال الطيبي: "اعتبر مَا كانوا عليه في الدنيَا وإلاّ فليس في الجنة كهل" كقوله تعالى: {وَآَتُوا الْيَتَامَى أَمْوَالَهُمْ} وقال في النهاية: "الكهل من الرجَال من زاد على ثلاثين إلى تمام الخمسين، وَقيل أراد بالكهْل هنا الحليم العاقل؛ أي أن الله يدخل أهل الجنة حلماء، وعقلاء".','2','987',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1911,'١٠٢١ -[٣٦٧١] "هَذان السَّمْعَ والبَصَرُ". قال البيضاوي: "أي','2','987',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1912,'هما في المسْلمين بمنزلة السمّع، والبصَر في الأعضاء، أو منزلتهما في الدين منزلة السمع، والبصر في الجسَد، أو هما من في العزة كالسمع، والبصر، ويحتمل أنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - سَماهما بذلك لشدة حِرصهما على استماع الحق واتباعه وتهَالكهما على النظر في الآيات المبينة في الأنفس، والآفاق، والتأمل فيهَا، والإعتبار بهَا".','2','988',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1913,'١٠٢٢ -[٣٦٧٢] "إنكن أنتن صَواحب يوسف". قال الشيخ عز الدين بن عبد السلام في أمَاليه: " كيف يصح التشبيه بصواحب يوسف مع أن القضيتين متباينتان لا سيما بأتم أنواع التشبيه؟ قال: والجواب: أن','2','988',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1914,'التشبيه وقع باعتبار المكر الموجود في القضيتين، لأن المكر هو أن يكون الظاهر مخالفًا للباطن، وصَواحب يوسف أتين زليخا، ليعتبْنهَا ومقصودهن أن يدعون يوسف لأنفسهن، وَهذا مكر، وعائشة رضي الله عنها كان مرادهَا أن لا يتطير الناس بابيهَا لوقوفه مكان رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -".','2','989',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1915,'١٠٢٣ -[٣٦٧٣] "حدثنا نصر بن عبد الرحمن الكوفي، حدثنا أحمد بن بشير عن عيسى بن ميمون الأنصاري عن القاسم بن محمد عن عائشة قالت: قال رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: لا ينبغي لقوم فيهم أبو بكر أن يؤمّهم غيره". أورد ابن الجوزي هذا الحديث في الموضوعات، وترجم عليه باب إمَامة من اسمه أبو بكر في كل من يكون اسْمه أبا بكر وهذا فهم عجيب بل هو خاص بالصديق رضي الله عنه كما فهمه الترمذي، قال المظهري: "هذا دليل على فضله على جميع','2','989',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1916,'الصَحابة. فإذا ثبت هذا فقد ثبتت خلافته فإن خلافة المفضول مع
وجود الفاضل لا تصحّ".','2','990',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1917,'١٠٢٤ -[٣٦٧٤] "من أنفقَ زَوْجَينِ". قال في النهاية: " الأصْلُ
في الزوج: الصِّنف، والنَّوع من كل شيء، وكل شيئين مُقْترِنَين؛ شكلين
كانا أو نقيضين فهما زوجَان وكلُّ واحد منهما زوج. يريد من أنفق
صِنْفَين من مَاله في سبيل الله ".','2','990',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1918,'١٠٢٥ -[٣٦٧٥] "ووافقَ ذلكَ مالاً". أي صَادف أمْره بالتصدق','2','990',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1919,'حصُول مَال عنده.
"فقال: يَا أبا بكر ما أبقيت. لأهلك؟ فقال: أبقيت لهُم الله ورَسُوله". قال البيهقي في شعَب الإيمان: " أخبرنا أبو عبد الرحمن السُلمي قال سُئل الأستاذ أبو سَهْل محمد بن سليمان عن هذا فقال: هو التجريد لله بالكلية، وإدخال الرسُول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - فيه لمكان الإيمان، وحقيقة التعَلق بالسبب في الوصول إلى المسبب، الأعلى وأن إليه إنقطاعه، فإذا كمل توكل المتوكل، تحقق فيه، أخبر إن شاء عن السبب وَإن شاء عن المسبب لأن الكل عنده وَاحد لتعلق الفروع في الكل بالأصل".','2','991',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1920,'١٠٢٦ -[٣٦٨٠] "فأما وزيراي من أهل السماء فجبريل وميكائيل". قال الطيبي: " فيه دلالة ظاهرة على فضله صلوات الله وسلامه عليه [على] جبريل ومكائيل. والوزير من الوزر، وهو','2','991',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1921,'الثقل، فإنه يتحمل عن الملك أوزاره، ومؤنه".','2','992',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1922,'١٠٢٧ -[٣٦٨١] "اللهم أعز الإسلام بأحب هذين الرجلين إليك". قال الطيبي: " أي قوِّهِ وانْصُره ".','2','992',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1923,'١٠٢٨ -[٣٦٨٢] "إنّ الله جَعَل الحق على لسَان عمر". قال الطيبي: " ضمن "جعل " معنى "أجرى" فعدّاه بـ "عَلى" ومنه معنى ظهور الحق، واستعلائه على لسَانه، وَفي وضع "الجعل" موضع "أجرى" إشعَار بأن ذلك خليق ثابت مُستقر ".
"مَا نزلَ بِالنَّاسِ أمرٌ قَطٌ فَقالوا فيهِ وقال فيه عُمرُ إلا نزلَ فيهِ القُرْآنُ على نحو مَا قال عُمرُ".....','2','992',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1924,'١٠٢٩ -[٣٦٨٩] "يا بِلالُ بِمَ سَبَقْتني إلى الجنَّةِ مَا دَخَلْتُ الجنَّةَ قَطُّ إلا سَمِعْتُ خَشْخَشتكَ أمَامي". قال العراقي في شرح التقريب: "إن قيل مَا معنى رؤياه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لبلال أمَامَهُ في الجنة كلما دخل مع كونه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، أول من يدخل الجنة فكيف معنى تقدم بلال عليه في هذه الرؤيَا؟ فالجواب: أنه لم يقل في هذه الرؤيا أنه يدخلهَا قبله في القيامَة وإنما رَآه أمامهُ في مَنامِه، وأما الدخول حقيقة فهو - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أول من يدخلهَا مُطلقًا وأما هذا الدخول فالمراد به سَريَان الروح في حالة النوم فلا إشكال في ذلك".
" خَشْخَشتكَ ". قال في النهاية: " الخَشْخَشة: حركة لها صوت كصَوْت النحاس ".','2','993',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1925,'١٠٣٠ -[٣٦٩٠] "إنِّي كُنْتُ نَذرْت إنْ رَدَّكَ الله سَالمًا، أنْ أضْربَ','2','993',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1926,'بَيْنَ يَديْكَ بِالدُّفِّ". قال التوربشتي: " إنما مكنها من ضرب الدف بين يدَيه لأنهَا قد نذرت، فدل نذرهَا على أنها عدّت انصرَافه عل حال السلامة نعمة من نعم الله عليهَا فانقلبَ الأمر فيه من صيغة اللهو إلى صيغة الحق ومن المكروه إلى المسْتحبّ "، وفي النهاية: " الدُّف؛ بالضم وَالفتح ".','2','994',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1927,'١٠٣١ -[٣٦٩١] "فسَمعْنا لَغطًا". هو الصّوت الشدِيد الذي لا','2','994',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1928,'يفهم.
" تُزْفنُ " بزاي، وفاء، ونون؛ أي ترقص.
"فَانفضَّ النَّاسُ عَنْها". أي تفرق النظارَة الذين كانوا حول الحبشية الراقِصَة عنها، لمهَابَة عمر رضي الله عنه، والخوف من إنكاره عليهم.','2','995',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1929,'١٠٣٢ -[٣٦٩٣] "قد كاد يكون في الأمم مُحَدَّثون". قال في النهاية: " جاء في الحديث تفسِيره: أنهم المُلْهَمُون. وَالمُلْهَم هو الذِي يُلْقَى في نفسِه الشيء فيُخْبِر بِه حَدْسًا، وفِراسَة، وَهو نوع يَخْتَصُّ به من يشاء من عباده الذين اصطفاهم مِثْلَ عُمِر، كأنَّهم حُدِّثو بشيء فقالوهُ ".
"فإنْ يَكُن في أمتي أحدٌ فعُمرُ". قال التوربشتي: " لم يرد هذا القول مورد التردد، فإن أمته أفضل الأمم وإذا كانوا [موجودين] في غيرهم من الأمم فبالأحرى أن يكونوا في هذه الأمّة أكثر عددًا، أو أعلى رتبة، وإنما ورد مورد التأكيد، والقطع به، ولا يخفي على ذي الفهم محْمله، يقول الرّجُل إن يكن لي صَديق فهو فلان يريد بذلك اختصَاصه بالكمال في صَداقته لا نفي الأصْدِقاء".','2','995',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1930,'١٠٣٣ -[٣٦٩٥] " [يَوْمَ] السَّبع". قال في النهاية: "قال','2','995',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1931,'ابن الأعرابي: السَّبْع بسكون الباء الموضع الذي يكونُ إليه المحْشَر يَوْم القيامَة، أراد مَنْ لهَا يوم القيامةِ، والسَّبعْ أيضًا: الذُّعرُ سبَعْتُ فلانًا، إذا ذَعَرْته. وسَبعَ الذئبُ الغنم إذا فرسَهَا أي من لها يومَ الفَزَع. وَقيل: هذا التأويل يفْسُدُ بقول الذئب في تَمام الحديث:
"يَوْمَ لا رَاعِي لَها غيْري". والذئب لا يكونُ لها رَاعٍ يوم القيامَة.
وقيل أرادَ من لهَا عِنْدَ الفِتَن حين يتركها الناسُ همَلا لا رَاعِي لَهَا نُهْبَةً للذئاب، والسَّبَاع، فجعل السبُع لها راعيًا إذ هُو مُنْفَردٌ بهَا، ويكُونُ حينئذ بضم الباء. وهذا إنذارٌ بما يكون من الشَّدَائدِ والفِتَن التي يُهْملُ الناسُ فيهَا موَاشِيهَم فتسْتَمْكن منها السِّباع بلا مَانِع. وقال أبو عبيدة: "يوم','2','996',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1932,'السبْع عيْدٌ كان لهم في الجاهليَّة يشتَغِلون بعِيدِهم ولَهْوهِم، وليس بالسَّبُع الذي يَفْتَرسُ الناسَ". قال أبو موسى: " وأملاه أبو عامر العبْدَري الحافظ، بضم الباءِ، وكان من العِلْم، وَالإتقَان بمكَانٍ ".','2','997',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1933,'١٠٣٤ -[٣٧٠٠] "من جيش العسرة". هو جيش غزوَة تبوك لأنهَا كانت في شدة الحر، وجَدب البلاد.
"بأحْلاسِها وأقْتابهَا". الحلس؛ كساء رقيق يجعل تحت البردعَة، والقتب للجمل، كالإكاف لغيره.
"مَا على عُثمانَ مَا عَملَ بَعْدَ هَذه". قال المظهري: " أي ما عليه أن يعمل بعد هَذِه من النوافل دون الفرائض لأن تلك الحسَنة تكفيه عن جميع النوافل ".
وقال الطيبي: "المعْنى لا على عثمان بأس الذي عمل بعد هذه من الذنوب، فإنهَ مغفُورة مكفرة ونحوه قوله: "الله قد اطلع على أهل بدر','2','997',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1934,'فقال: اعملوا ما شئتم فقد غفرت لكم".','2','998',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1935,'١٠٣٥ -[٣٧٠٢] "بيعَةِ الرَّضْوانِ". هي البيعة التي جرت تحت الشجَرة عام الحديبيَة، سميت بذلك لما نزل في أهلهَا: {لَقَدْ رَضِيَ اللَّهُ عَنِ الْمُؤْمِنِينَ إِذْ يُبَايِعُونَكَ} الآية.
"إن عُثمانَ في حَاجةِ الله، وحَاجةِ رَسولهِ ". قال الطيبي: "هو من باب قوله تعالى: {إِنَّ الَّذِينَ يُؤْذُونَ اللَّهَ وَرَسُولَهُ} " في أن رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - بمنزلةٍ عند الله ومكانةٍ كأن حاجته، حاجته، تعالى عن الاحتياج علوًا كبيرًا.','2','998',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1936,'١٠٣٦ -[٣٧٠٣] "شَهدْتُ الدَّارَ". أي حضرت دار عثمان التِي','2','998',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1937,'حاصرُوه فيهَا.
"رُومة". بضم الراء اسم بئر بالمدينة.
"يَجْعَلْ دَلْوَة مع دِلاءِ المسْلمين". قال الطيبي: " مَعَ هو المفعول الثاني. "ليجعل"؛ أي يجعَل دلوه مصَاحبًا، وواحدًا من دلاء المسلمين، وَهو كناية عن التوقف، التسْبيل ".
"بِخْيَرٍ" الباء، باء البدَل، تتعلق بيشتري، وليسَت مثلها في قوله: اشتريت هذا بدرهم، المعنى من يشتريها بثمن ثم يبدلها بخير منهَا.
" [من] ماءِ البَحْرِ". أي ما فيه ملوحة كماء البحر، وَالإضافة فيه للبيَان، أي ماء شبيه ماء البحر.
"اللَّهُمَّ نَعَمْ". قال المظهري: " قد يؤتى باللهم قبل كلمتي الجحد، والتصديق في جواب المستفهم كقولهم اللهم لا، ونعَم تمكينًا للجواب ".
"بالحَضِيض". هو قرار الأرض، وأسفل الجبل.','2','999',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1938,'١٠٣٧ -[٣٧٠٤] "مُقنَّعٌ في ثَوْبٍ". أي مطيلس.','2','1000',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1939,'١٠٣٨ -[٣٧٠٥] "يُقَمِّصُكَ قَميصًا". استعار القميس للخلافة ورشحهَا، بقوله: " فإنْ أرَادُوكَ على خَلْعِه فَلا تَخْلعهُ لَهُمْ ". قال في الأساس: " ومن المجاز قمصّه الله وَشي الخلافة وتقمص لباس العروس ".','2','1000',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1940,'١٠٣٩ -[٣٧٠٦] " [لك أجْرُ رَجلٍ شَهدَ بَدْرًا، وسَهمهُ] ".','2','1000',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1941,'١٠٤٠ -[٣٧١٠] "على بَلْوى تُصِيبهُ". قال البيضاوي: " "على" هنا بمعنى مع ".','2','1001',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1942,'١٠٤١ -[٣٧١١] "قد عَهدَ إليَّ عهدًا، فأنا صَابرٌ عَليْهِ". قال','2','1001',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1943,'الطيبي: " أي أوصاني بأن أصبر، ولا أقاتل، ولا يجوز أن يقال الوصية هي قوله: " فإن أرادوك على خلعه فلا تخلعه " فإن ذلك يوهم المقاتلة معهم للدفع ".
١٠٤١ م - ٣٧١٤ "من كُنْتُ مَوْلاَهُ فعَليٌّ مَوْلاهُ". أراد بذلك ولاء الإسلام، كقوله تعالى: {ذَلِكَ بِأَنَّ اللَّهَ مَوْلَى الَّذِينَ آَمَنُوا وَأَنَّ الْكَافِرِينَ لَا مَوْلَى لَهُمْ (١١) } وقيل: سبب ذلك أن أسَامة قال لعَلي لسْت مولاي إنما مولاي رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - فقال - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - ذلك.','2','1002',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1944,'١٠٤٢ -[٣٧١٩] "وَلا يؤدِّي عَنِّي إلا أنا، أوْ عَليٌّ". قال الطيبي: "كان الظاهر أن يقال لا يودي عني إلا علي فأدخل أنا تأكيدًا لمعنى','2','1002',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1945,'الاتصَال في قوله عليٌّ مني، وأنا من عليّ".
وقال التوربشتي: " كان من دأب العَرب إذا كان بينهم مقاوَلة في نقض وإبرام وصُلح، ونبذ عهد أن لا يودي ذلك إلا سيد القوم، أو من يليه من ذوي قرابته، القريبة، ولا يقبلُونه من سواهم ".','2','1003',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1946,'١٠٤٣ -[٣٧٢١] "حدثنا سُفيان بن وكيع، حدثنا عبيد الله بن موسى، عن عيسى بن عمر، عن السدي، عن أنس بن مَالك، قال: " كان عِنْدَ النبيِّ - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - طَيْرٌ فقال: اللهُمَّ ائتنِي بأحَبِّ خَلْقكَ إلَيْكَ يأكُلُ مَعي هذا الطَّيرَ. فجاء عليٌّ فأكلَ مَعهُ ". هذا أحد الأحاديث التي انتقدهَا الحافظ سِراج الدين القزويني على المصَابيح،','2','1003',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1947,'وزعم أنه موضوع، وقالَ الحافظ صَلاح الدين العلائي: " ليسَ بموضوع بل له طرق كثيرة غالبهَا وَاه، ومنهَا مَا فيه ضعف قريب، وربما يقوي بعض منهَا بمثله إلى أن ينتهي إلى درجة الحسن " والسدي إسماعيل احتج به مسلم، والناس، وعيسَى بن عُمَر هو الأسَدي، الكُوفي، القاري، وثقة يحيى بن معين وغيره، ولم يُتَكلم فيه وَعبيد الله بن موسى مشهور من رجال الصحيحين، وقد تابعه على روايته عن عيسَى بن عمر مسهر بن عبد الملك، أخرجه النسائي في خصَائص علي، ومُسْهر هذا وثقة ابن حبّان، والحسَن بن حماد الورّاق وقال النسَائي ليسَ بالقويّ، وقال البخاري، فيه بعض النظر وعلى هذا فيصْلح حديثه متابعًا، وقد روَاهُ الحاكم في المسْتدرك من ْطريق محمّد بن أحمد بن عيَاض، حدثنا أبي حدثنا يحيى بن حسّان، عن سُليمان بن بلال عن يحيى بن سعِيد عن أنس أطول ممّا تقدم وكل','2','1004',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1948,'رجال هذا ثقات، لكن أحمد بن عياض لم أر من تكلم فيه بتوثيق، ولا جرح، وابنه محمد مشهُور صَدوق، روى عن حرملة، وجماعة، ورواه عنه الطبَراني، وَطائفة، فهذان الطريقان أمثل مَا روى فيه وقد سَاق ابن الجوزي في العِلل المتناهيَة للحديث طرقًا كثيرة عن أنس واهِيَة، وقال الحاكم في المستدرك: " روَاه عن أنس جماعَة أكثر من ثلاثين نفسًا ثم صحت الرواية عن علي، وأبي سعيد، وسفينة " ولم يذكر طرق أحاديث هؤلاء، وخرج أبو بكر بن مردوية في طرق هذا الحديث جزءً، وقال ابن طاهر الحافظ: كل طرقه باطِله معلولة وهو غلو منه في مقابلة تساهُل الحاكم، والحُكم على الحديث بالوضع بعيد جدًّا، ولذلك لم يذكرُه أبو الفرج في كتاب الموضوعات انتهى.
قال التوربشتي: قوله: " بأحب خَلْقَكَ إلَيْكَ " مؤوّل، أي بمن هو من أحب خلقك إليك فيشاركه غيره، وَهم المفضلون بإجماع الأمّة، وهذا مثل قولهم: فلان أفضل الناس، وأعقلهم، أي من أفضلهم، وأعقلهم ومما يبين لك أن حمله على العموم غير جائز أنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - من جملة خلق الله ولا جائز أن يكون أحبّ إلى الله منه، أو يؤول على أنه أراد به أحبّ خلقه إليه من بني عمّه، وذويه، وقد كان - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - يطلق القول، وَهو يريد تقييده، ويُعِم به ويريد تخصِيصه فيعرفه ذُو الفهم بالنظر إلى الحال،','2','1005',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1949,'أو الوقت، أو الأمر الذي هو فيه.','2','1006',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1950,'١٠٤٤ -[٣٧٢٣] "حدثنا إسماعيل بن موسى، حدثنا محمد بن عمر الرومي، حدثنا شَرِيك، عن سَلمة بن كهيل، عن سُويد ابن غفلة، عن الصُنابحي عن علي قال: قال رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: أنا دار الحكمة، وَعلي بابها". "هذا حديث غريب منكر، وروى بعضهم هذا الحديث عن شَرِيك ولم يذكروا فيه عن الصُنابحي ولا يعرف هذا الحديث عن أحد من الثقات غير شَرِيك وفي الباب عن ابن عباس".','2','1006',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1951,'هذا أحد الأحاديث التي انتقدَها الحافظ سراج الدين القزويني على المصَابيح، وزعم أنه موضُوع وقال الحافظ صَلاح الدين العلائي في أجوبته: " هَذا الحديث ذكره أبو الفرج ابن الجوزي في الموضوعات من طرق عدة وجزم ببطلان الكل "، وكذلك قال بعده جماعة، منهم الذهبي في الميزان، وَغيره، والمشهور به رواية أبي الصلت عبد السلام بن صَالح الهروي عن أبي معَاويَة، عن الأعمش، عن [مجاهِد] ، عن ابن عباس مرفوعًا، وعبد السلام هذا تكلم فيه كثير، قال النسائي: ليسَ بثقة، وقال الدارقطني، وَابن عدي: متهم، زاد الدارقطني: رافضيّ، وقال أبو حاتم: لم يكن عندي بصَدُوق وضرب أبو زُرعة على حديثه، ومع ذلك فقد قال: قال الحاكم:','2','1007',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1952,'حدثنا الأصم، حدثنا عباس يعني الدوري قال: سألت يحيى بن معين عن أبي الصلت، فقال ثقة فقلت: أليس قد حدّث عن أبي معَاويَة حديث أنا مدينة العِلم، فقال: قد حدث به محمد بن جعفر الفيدي وَهو ثقة عن أبي معَاويَة، وكذلك روى صَالح جزرَة أيضًا عن ابن معين، ثم سَاقه الحاكم من طريق محمد بن يحيى بن الضريس، وَهو ثقة حافظ عن محمد بن جعفر الفيدي عن [أبي] معَاويَة".
وَقالَ أبو العباس أحمد بن محمد بن محرز: سَألت يحيى بن معين عن أبي الصلت، فقال: ليسَ ممن يكذب، فقيل له في حديث أبي معاويَة: " أنا مدينة العِلم " فقال هو من حديث أبي معاويَة أخبرني ابن نمير قال: حدث به أبو معاوية قديمًا ثم كف عنه، وكان أبو','2','1008',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1953,'الصلت رَجلا مُوسرًا يطلب هذه الأحاديث، ويلزم المشايخ، قلت: فقد برىء أبو الصلت عبد السلام من عهدته وأبو معاوية ثقة مأمون من كبار الشيوخ، وحفاظهم المتفق عليهم، وقد تفرد به عن الأعمش فكان مَاذا؟ وأي استحالة في أن يقول لله مثل هذا في حق علي، وَلم يأت كل من تكلم في الحديث وجزم بوضعه بجواب عن هذِه الروَايات الصحيحَة عن يحيى بن معين، ومَع ذلك فله شاهِد قوي روَاه الترمذي من حديث علي، ورواه أبو موسى الكجّي وغيره عن محمد بن عمر ابن الرومي، وَهو ممن روى عنه البخاري في غير الصحيح، وقد وثقه ابن حبان وضعفه أبو داود، وقال أبو زرعَة: فيه لين، وقال الترمذي ورى بعضهم هذا عن شَريك، فقد برئ محمد بن الرومي من التفرد به وشَرِيك هو ابن عبد الله النخعي القاضي، احتج به مسْلم، وعلق له البخاري ووثقه يحيى بن معين، وقال العجلي ثقة حسن الحديث.
وقال عيسَى بن يونس مَا رأيت أحدًا قط أوْرَع في علمه من شَرِيك فعَلى هذا يكون تفرده حسنًا، فكيف إذا انضم إلى حديث أبي معَاوَية المتقدم، ولا يرد عليه روايَة من أسقط منه الصُنابحي، لأن سُويد بن غفلة تابعي مخضرم أدرك الخلفاء الأربعة، وسمع منهم فَذِكْرُ الصُنابحي فيه من المزيد في متصل الأسَانيد، ولم يأت أبو الفرج، ولا غيره بعله','2','1009',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1954,'قادحَة في حديث شَرِيك سوَى [دعوى] الوضع دفعًا بالصدر. انتهى كلام العلائي.
وقال الحافظ ابن حجر في أجوبته: " حديث ابن عباس أخرجه ابن عبد البر في كتاب الصحابة المسمى بالاستيعَاب ولفظه: " أنا مدينة العلم، وعلي بابهَا فمن أراد العِلم فلياته من بابه " وصححه الحاكم، وأخرجه الطبراني من حديث ابن عباس بهذا اللفظ، وَرجاله رجال الصحيح إلا عبد السلام الهروي فإنه ضعيف عندهم ". وقال في جواب فتيا رُفِعَت إليه في هذا الحديث. قال الطيبي: " تمسّك الشيعَة بهذا الحديث على أن أخذ العِلم، والحكمة مختص به لا يتجاوزه إلى غيره إلا بواسطته لأن الدار إنما يدخل إليها من بابهَا، ولا حجة لهم فيه إذ ليسَ دار الجنة بأوسع من دار الحكمة، ولها ثمانية أبواب ".','2','1010',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1955,'١٠٤٥ -[٣٧٢٦] "ولكنَّ الله انْتجَاهُ". أي أمرني أن أناجيه.','2','1010',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1956,'١٠٤٦ -[٣٧٢٧] "حدثنا علي بن المنذر، حدثنا ابن فضيل','2','1010',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1957,'عن سَالم بن أبي حفصَة عن عطِيّة، عن أبي سعيد قال: قال رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لعَليٍّ: "يَا عَليُّ لا يَحلُّ لأحدٍ يجنبُ في هذا المسجد غيري، وَغيْرَكَ". قال علي بن المنذر: قلت لضرَارِ بن صُرَّدَ مَا معنى هذا الحديث؟ قال: " لا يحل لأحد يَسْتطْرقهُ جُنبًا غيري، وغيرُكَ "
"هذا حديث حسن غريب لا نعَرِفهُ إلا من هذا الوجْهِ، وقد سَمعَ محمدُ بن إسماعيلَ مني هذا الحديث، واستغْربه". هذا أحد الأحاديث التي انتقدها الحافظ سراج الدين القزويني على المصَابيح، وزعم أنه موضُوع، وقال الحافظ صَلاح الدين العَلائي في أجوبته: " هذا الحديث ليس من الحسان قطعًا بل هو حديث ضعيف وَاه لكنه لا ينتهي إلى الوضع "، وقد حسّنه الترمذي وسَالم بن أبي حفصَة، وعطية العَوفي، كل منهما شيعي ضعيف، قال النسائي في سالم: "ليسَ بثقة"، وقال عمر الفلاس فيه: "ضعيف يفرط في التشيع"، وكان هشيم يتكلم في عطية العَوفي، وضَعّفه أحمد بن حنبل، وعلي بن المديني، والنسائي، والجماعَة والعجب من تحسين الترمذي له وَقد تفرد به هذان وضرار','2','1011',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1958,'بن صرد [أحد] المتهمين بالكذب، وممَا يدُل على نكارة هذا الحديث أن النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لم يختص عن الأمة بشيء من الرخص فبما يقتضي تعظيم حرمَات الله، والقيام بإجلاله أصلاً، وإنما كان ترخصه في الأمور الدنيوية، كإباحة مَا وراء الأربع في النكاح، ونحو ذلك، فلم يكن - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - يترخص لهم بإباحة الجلوس في المسجد حال الجنَابة أبدًا".
انتهى.
وقال الحافظ ابن حجر في أجوبته: السبب في ذلك أن بيته كان مجاور المسْجد، وبابه من داخل المسجد كبيت النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، [وقد ورد] من طرق كثيرة صحيحة أن النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لما أمر بسد الأبواب الشارعة في المسجد إلا باب علي شق على بعض من الصحابة فأجابهم بعذر في ذلك، وقد وقع في بعض الطرق من حديث أبي هريرة أن سكنى علي كانت مع النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - في المسجد يعني مجاورَة المسجد وورد لحديث أبي سعيد شاهد من حديث سَعد بن أبي وقاص أخرجه البزار من رواية خارجة بن سعد عن أبيه، ورِواته ثقات " انتهى.
ْقال الطيبي: "الظاهر أن يقال أن يجنب ليكون فاعلا، لقوله لا','2','1012',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1959,'يحل، وفي المسْجد ظرف ليجنب".','2','1013',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1960,'١٠٤٧ -[٣٧٣١] "أنت مني بمنزلة هَارون من مُوسى". قال النووي: " ليس فيه دلالَة على استخلافه من بعده كما توهمه الرافضة لأنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - قال هذا حين استخلفه على المدينة في غزوة تبوك ويؤيد هذا أن هارُون المشبه به لم يكن خليفة بعد موسى لأنه توفي قبل وفاة موسى بنحو أربعين سنة، وإنما استخلفه حين ذهب إلي الميقات للمناجاة".
وقال الطيبي: " "مني" خبر المبتدأ، و"من" اتصالية، ومتعلق الخبر خاصّ، والباء زائدة، كما في قوله تعالى: {فَإِنْ آَمَنُوا بِمِثْلِ مَا آَمَنْتُمْ بِهِ} أي فإن آمنوا إيمانًا مثل إيمانكم، يعني أنت متصل بي، ونازل مني منزلة هارُون من موسى، وفيه تشبيه، وَوجه التشبيه مبهم لم يفهم أنه رضي الله عنه فيم شبهه به صَلوات الله وسلامه عليه، فبين قوله: " إلا أنه لا نَبيَّ بَعْدِي " أن اتصالهُ به ليسَ من جهة النبوة فبقي الاتصال من جهَة الخلافة لأنها تلي النبوة في المرتبة ثم إما أن يكون في','2','1013',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1961,'حياته، أو بعد مماته لأن هَارون عليه السلام مات قبل موسى فتعين أن يكون في حياته عند مسِيره إلى غزوَة تبوك".','2','1014',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1962,'١٠٤٨ -[٣٧٣٢] "حدثنا محمد بن حميد الرازي، حدثنا إبراهيم ابن المختار عن شعبَة عن أبي صَالح عن عَمرو بن ميمون عن ابن عباس، أن رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أمر بِسَدِّ الأبْوابِ إلا بابَ عَليٍّ".','2','1014',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1963,'١٠٤٩ -[٣٧٣٨] "أوْجَبَ طَلْحةُ". أي عمل عملا أوجب له الجنة.','2','1014',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1964,'١٠٥٠ -[٣٧٤٤] "وأن حواري الزيير". أي خاصّتي من','2','1014',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1965,'أصحابي، وناصري، قال القاضي عياض: " اختلف في ضبطه، فضبطهُ جماعَة من المحققين بفتح الياء المشدّدَة، وضبطه أكثرهم بكسْرهَا ".','2','1015',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1966,'١٠٥١ -[٣٧٥٣] "الحَزَوَّرُ". هو الذي قارب البلوغ، والجمع حَزَاوِرة.','2','1015',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1967,'١٠٥٢ -[٣٧٥٦] "سَهرَ مَقْدَمَهُ المدينةَ" قال الطيبي:','2','1015',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1968,'"مقدمة، مصْدر ميميّ ليسَ بظرف لعَمله في المدينَة، ونصبه عَلى الظرف على تقدير مُضاف وهو الوقت، والزمان".
"لَيْلَةً". بدل البعض من المقدر أي سَهرَ، ليلة من الليَالي، وقت قُدومه المدِينة.','2','1016',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1969,'١٠٥٣ -[٣٧٥٨] "بِوجوهٍ مبْشرَةٍ". قال التوربشتي: "هو بضم الميم وسكون الباء، وفتح الشين، يريد بوجوه عليهَا البشر".
"فإنما عَمَّ الرَّجُلِ صِنْوُ أبيهِ". قال في النهاية: " الصِّنْوُ: المِثْل. وأصْلُهُ أن تَطْلُع نَخْلَتان من عِرْق وَاحدٍ. يُريدُ أن أصل العباس وَأصْلَ أبي وَاحد، وهو مثلُ أبي، وَجمعه صِنْوان".','2','1016',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1970,'١٠٥٤ -[٣٧٦٢] "اللهُمْ احْفَظهُ في وَلَدِهِ". قال الطيبي: "أي','2','1016',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1971,'أكرمه وَراع أمْرهُ كيلا يضيع في شأن وَلده، وهذا معنى رواية رزين": "واجعل الخلافة باقية في عقبه".
"رأيتُ جَعْفَرًا يَطِيرُ في الجنَّةِ مَعَ الملائِكَةِ".','2','1017',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1972,'١٠٥٥ -[٣٧٦٣] "ما احتذى النِّعالَ". أي انتعل.
"ولا رَكبَ المطَايَا". جمع مطية وهي الناقة التي يركب مطاهَا؛ أي ظهرها ويقال يمطى بهَا في السير، أي يمدّ.
"ولا رَكبَ الكُور". بضم الكاف وهو رحل الناقة بأداته، قال في النهاية: "وكثير من الناس يفتح الكاف وهو خطأ".','2','1017',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1973,'"بَعْدَ رَسُولِ الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أفضلُ من جَعْفرٍ".','2','1018',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1974,'١٠٥٧ -[٣٧٦٥] "عن البراء بن عازب أن النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - قال لجعفر ابن أبي طالب: " أشْبَهتَ خَلْقي، وخُلُقي ".','2','1018',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1975,'١٠٥٨ -[٣٧٦٨] "الحسَنُ، وَالحسينُ سَيِّدَا شَبابِ أهْلِ الجنَّةِ".
قال ابن الحاجب في أماليه: " هذا الحديث فيْه إشكال لأن قوله: شباب أهل الجنة يفهم منه أن الجنة فيها شباب وَغير شبَاب، وليس الأمر كذلك بل كل من فيهَا شباب على مَا وردت به الأخبار، والدليل على أنه يفهم منه ذلك لو لم يكن كذلك لم يكن للتخصيص فائدة إذ ذكر الشباب يقع ضائعًا وكان ينبغي أن يقال سَيِّدا أهل الجنة، قال: وَيجاب بأمور أحدهَا -وَهو الظاهر-: أنه سَماهُم باعتبار مَا كانوا عليه عند مفارقة الدنيَا ولذلك يصح أن يقال للصغير يموت من صغار أهل الجنة، والشيخ المحكوم بصَلاحه من شيوخ أهل الجنة فهما [سيدا] شباب أهل الجنة بهذا الاعتبار، وَحسن الإخبار عنهما بذلك، وإن كانا لم ينتقلا عن الدنيا','2','1018',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1976,'شابين لأنهما كانا عند الإخبار كذلك. الثاني: أن يراد أنهما سيدا شباب أهل الجنة باعتبار ذلك الوقت الذي كانا فيه شابين ولا يرد على الوجه الأول وَالثاني إلزام أنهما سيّدا المرسلين لأنهما شباب في الجنة، لأنهم غير داخلين في شباب أهل الجنة على المعنين جميعًا. الثالث: أن أهل الجنة وإن كانوا شبَابًا كلهم إلا أن الإضافة هنا إضافة توضيح باعتبار بيان العَام بالخاص كما تقول جميع القوم، وكل الدرَاهم لأن كل، وجميعًا يصلحَان لكل ذي آحاد فإذا قلت: القوم، والدرَاهم فقد خصصته بعد أن كان شائعًا فكذلك شباب وَإن كان أهل الجنة كلهم شباب، إلا أنه يصح إطلاقهُ على من في الجنة، وعلى من في غيرهَا فخصص شياعه، تقول [أهل] الجنة، كما خصص شيَاع كل وجميع بالقوم، والدراهم لمَّا كان هو مقصود المتكلم دون غيره، ويرد على هذا إلزام سيَادتهم المرسَلين لأنهم داخلون على هذا التأويل، وجوابه: أنه عام خصّص عُلم تخصِيصه بالإجماع فإنّ المرسَلين أفضل من غيرهم بالإجماع انتهى".
[وقال النووي في فتاويه] وقال المُظهَّري مَعناه هما أفضل من مات شابًا في سَبيل الله من أصحَاب الجنة، ولم يرد أنهما من الشباب، لأنهما ماتا وقد كهلا بل ما يفعَله الشباب من المروة كما تقول فلان فتى، وإن كان شيخًا، تشير إلى مودته، وفتوته، أو أنهما سيدا أهل الجنة سوى الأنبياء، والخلفاء الراشدين وذلك لأن أهل الجنة كلهم في سن واحد، وَهو الشباب وَليسَ فيهم شيخ ولا كهل، وقال الطيبي: "يمكن أن يراد','2','1019',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1977,'هما الآن سيدا شبابهم مِن أهل الجنة من شباب هذا الزمان".','2','1020',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1978,'١٠٥٩ -[٣٧٧٠] "هُمَا رَيْحانتايَ في الدُّنيَا". قال الزمخشري في الفائق: "أن من رِزق الله الذي رزقنيه، ويجوز أن يراد به المَشْمُوم لأن الأولاد يشمون، ويقبَّلون [كلا منهم] فكأنهم من جملة الرياحين التي أنبتها الله تعالى"، وفي النهاية: " الريحان يُطلقُ على الرَّحمة، والرزق، والرَّاحة، وبالرزق سُمّي الولد رَيّحانًا ". وقال الطيبي: موقع "من الدنيا" من هنا كموقعها في قوله: " حبب إليَّ من الدنيا الطيب والنساء " [أي] نصيبي، ونَصبَ ريحانتي على المدح.','2','1020',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1979,'١٠٦٠ -[٣٧٨٠] "نُضدَتْ". أي جعل بعضهَا فوق بعض.','2','1020',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1980,'١٠٦١ -[٣٧٨٤] "عن ابن عباس قال: كان رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - حَاملَ الحَسَن بن عَليٍّ على عَاتقه فقالْ رَجُل: نعمَ المرَكَبُ رَكِبْتَ يَا غُلامُ فقال النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، وَنِعْمَ الرَّاكبُ هو".','2','1021',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1981,'١٠٦٢ -[٣٧٨٨] "إني تارك فيكم ما إن تمسّكتم به". قال الطيبي: "مَا" موصُولة، والجملة الشرطية صِلتها، ومعنى التمسك بالقرْآن العمل بما فيه وهو الائتمار بأوامره، والانتهاء عن نواهيه، والتمسك بالعترة محبتهم والاهتداء بهُدَاهم وسيرِهم، وفي إشارَة إلى أنهُمَا بمنزلة التوأمين، الخليفتين عن رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -".','2','1021',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1982,'١٠٦٣ -[٣٧٨٥] "أعْطِيَ سَبْعَةَ نُجبَاءَ". قال في النهاية:','2','1021',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1983,'"النجيب: الفاضل من كل حَيَوان".
"رُقباءَ". قال في النهاية: " أي حَفَظه يكونون معَه ".
"وأُعْطيتُ أنا أربعةَ عَشرَ". في فوائد تمام، وتاريخ ابن عساكر من طريق عبد الله بن مُلَيْل عن علي سبعَة من قريش، وسبعة من المهاجرين وذكر فيهم أبا ذر، وحذيفة، والمقداد، وَلم يذكر مصعبًا.','2','1022',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1984,'١٠٦٤ -[٣٧٨٩] "أحبّوا الله لما يغذوكم من نعمه". قال الحليمي هذا يحتمل أن يكون عامًا لأنعُمه كلهَا، وأن يكون اسْمًا لغذاء الطعام، والشراب حقيقه ولما عداهما من التوفيق، وَالهداية، ونصب أعلام هذه المعرفة، وخلق الحواس، والعقل مجازًا، أو يكون جميع ذلك بالاسم مرادًا فقد قال النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "ثلاث من كن فيه فقد وجد حلاوَة الإيمان" وفي بعض الروايات طعم الإيمان، وإنما يكون الطعم','2','1022',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1985,'للأغذية، ومَا يجري مجراهَا فإذا جاز الإيمَان بالطعم جازت تسْميته غدا دخَل الإيمان في جميع نعم الله عز وجل في هذا الحديث والله أعلم.
قال: " ومحبّة الله، اسم لمعان كثيرة، أحدهَا الاعتقاد أنه عزَّ اسمه محمود من كل وجه لا شيء من صفاته إلا وهو مدحة له. الثاني: الاعتقاد أنه محسن إلى عبَاده منعم متفضل عليهم. الثالث: الاعتقاد أن الإحسان الواقع منه أكبر، وَأجل من أن يقضي قول العبد، وَعمله وإن حسنا، وكثرًا شكره. الرابع: أن لا يستثقل العبد قضايَاه، ويستكثر تكاليفه.
الخامس: أن يكون في عامة الأوقات مشفقًا وجلاً من إعراضه عنه وسلبه معرفته التي أكرمه بهَا، وتوحيده الذي حلاه وزيّنه له. السادس: أن تكُون آمَاله منعقدة به لا يرى في حال من الأحوال أنه غني عنه. السابع: أن يحمله تمكن هذه المعاني في قلبه على أن يديم ذكره بأحسن ما يقدر عليه. الثامن: أن يحرص على أداء فرائضه، والتقرب إليه من نوافل الخير بما يطيقه. التاسع: أن يسمع من غيره ثناء عليه، وعرف منه تقربًا إليه، وجهادًا في سَبيله سرًّا أو إعلانًا مَالاه ووالاهُ. العاشر: أنه إن سمع من أحد ذكرًا له أعانه بما يحكى عنه أو عرف منه غيا عن سبيله سرّا، أو علانية، باينه، وناوَاه، فإذا استجمعت هذه المعاني في قلب أحد فاستجماعهَا هو المشار إليه باسم محبة الله تعَالى، وَهي وإن تذكر مجتمعة في موضع فقد جاءت متفرقة عن النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -فمن دونه " انتهى.','2','1023',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1986,'١٠٦٥ -[٣٧٩٠] "وأفرضهم زيد بن ثابت، وأقرؤهم أُبي بن','2','1023',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1987,'كعب". قال الطيبي وغيره: " لا يحل هذا على أفضليتها على أبي بكر، وعمر مثلاً لأن لهُما فضائل لم تكن لغيرهما من الصحَابة، ولا يلزم أن يكون في الفاضل جميع خصال المفضول ".
"وأمينُ هذه الأمَّةِ أبو عبَيْدَة بن الجرَّاحِ". قال الطيبي: " أي هو الثقة المرضي، والأمانة مشتركة بينه وبين غيره من الصحَابة، لكن النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - خصّ بعضهم بصِفات غلبت عليه وكان بهَا أخصّ ".','2','1024',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1988,'١٠٦٦ -[٣٧٩٢] "عن أنس بن مالك قال: قال رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لأُبي بن كعب: " إن الله أمرني أن أقْرأ عَليْكَ: {لَمْ يَكُنِ الَّذِينَ كَفَرُوا} " قال: وسمَّاني؛ قال: "نعم" فبكى".','2','1024',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1989,'١٠٦٧ -[٣٧٩٤] "عن أنس بن مالك قال: جمع القرآن على عهد رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أربعة كلهم من الأنصار، أبيُّ بن كَعْب، وَمُعاذُ بن جَبل، وزَيْدُ بن ثابتٍ، وَأبو زَيْدٍ قُلْتُ لأنَسٍ: من أبو زيد؟ قال: أحد عمومتي ... ".','2','1025',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1990,'١٠٦٨ -[٣٧٩٦] "فأشْرف لَهَا النَّاسُ". أي تطلعوا لهَا.','2','1025',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1991,'١٠٦٩ -[٣٧٩٧] "إنَّ الجنَّةَ لتَشْتاقُ إلى ثَلاثةٍ". قال الطيبي: "سبيل اشتياق الجنة إلى هؤلاء الثلاثة سَبيل اهتزاز العرش لموت سَعد".','2','1026',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1992,'١٠٧٠ -[٣٧٩٨] "مَرحبًا بالطَّيِّبِ المُطَيَّبِ". قال في النهاية: "أي الطاهر، المطَهَّر".','2','1026',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1993,'١٠٧١ -[٣٨٠١] "ما أظلَّتِ الخَضْراء". أي السماء.
"ومَا أقَلتِ الغَبْراءُ". أي [حملت] الأرض.','2','1026',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1994,'"أصْدقَ من أبي ذرٍّ". قال في النهاية: "أراد أنه مُتَناهٍ في الصدق إلى الغاية، فجاء به على اتساع الكلام والمجاز".
"شِبْهَ عيسى بن مَريمَ". قال في المشكاة: "يعني في الزهد".','2','1027',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1995,'١٠٧٣ -[٣٨٠٥] "بهدي عمار". أي سيروا بسيرته.
"وتمسّكوا بعهد ابن مسعود". قال التوربشتي: " يريد ما يعهد إليهم ويوصيهم به، وأرى أشبه الأشياء بما يراد من عهده أمر الخلافة فإنه أول من شهد بصحتها وأشار إلى استقامتها من أفاضل الصحَابة، وَأقام عليهَا الدليل، فقال؛ لا نؤخر من قدّم رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -"،','2','1027',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1996,'"ألا نرضى لدنيَانا من رضيه لديننا". قال: " ومما يؤيد هذا التأويل المناسَبة الواقعَة بين أول الحديث، وآخره، ففي أوله: "اقتدوا باللذين من بعدي أبي بكر، وَعمر" وفي آخره: "وتمسكوا بعهد ابن مسعُود" وَمما يدل على صحة ما ذهبنا إليه قوله في حديث حذيفة أيضًا: "إن أستخلف عليكم فعصيتموه عذبتم، ولكن إذا حدثكم حذيفة فصدقوه" وهَذا إشارَة إلى مَا أسرّ إليه من أمر الخلافة في الحديث الذي نحن فيه ".','2','1028',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1997,'١٠٧٤ -[٣٨٠٧] "أقْربُ النَّاس هَدْيًا ودَلا، وسَمْتًا". قال البيضاوي: " الدل قريب من الهدي والمراد به السكينة، وَالوقار، ومما يدل على كمال صَاحبه من ظواهر أحواله، وحسن مقاله، وبالسمت القصد في الأمور، وبالهدى من حسن السيرة وسلوك الطريقة المرضية".
"أنَّ ابن أُمِّ عَبْدٍ". هو عبد الله بن مسعود.','2','1028',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1998,'١٠٧٥ -[٣٨٠٨] "لأمرتُ ابن أُمِّ عَبْدَ". قال التوربشتي: "لا بد','2','1028',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (1999,'من تأويله أنه أراد تأميره على جيش، أو نحوه ولا يجوز أن يحمل على
غير ذلك فإنه لم يكن من قريش وقد نصّ - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - على أن هذا الأمر في قريش".','2','1029',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2000,'١٠٧٦ -[٣٨١٠] "خذوا القرآن من أربعَة". قال النووي: "قالوا هؤلاء الأربعة تفرغوا لأخذ القرآن عنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - مشافهَة، وغيرهم اقتصروا على أخذ بعضهم عن بعض أو أن هؤلاء تفرغوا لأن يؤخذ عنهم، أو أنه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أراد الإعلام بما يكون بعد وفاته - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - من تقديم هؤلاء الأربعة، أو أنهم أقرأ من غيرهم ".','2','1029',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2001,'١٠٧٧ -[٣٨١١] "وابن مَسْعُود صَاحبُ طهُور رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، وَنَعْليهِ". قال البيضاوي: "يريد أنه كَان يخدم الرسُول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -، ويلازمه','2','1029',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2002,'في الحالات كلها فيهيء طهوره، ويحمل معَه المطهَرة إذا قام إلى الوضوء، ويأخذ نعله، وَيضعهَا إذا جلس، وَحِين ينهض" انتهى.
"وَحُذَيْفةُ صَاحبُ سرِّ رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -". قال الطيبي: " قيل من تلك الأسرار أسماء المنافقين وأنسابهم، أسرّ بهَا رَسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - إليه".','2','1030',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2003,'١٠٧٨ -[٣٨١٧] "وقد أصْمَتَ". قال في النهاية: " يقال صَمَت العَليل وَأصْمَتَ فهو صَامِتٌ، ومُصْمت، إذا اعتقِلَ لسَانِه ".','2','1030',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2004,'١٠٧٩ -[٣٨٢٤] "اللهمَّ عَلِّمهُ الحِكمةَ". قال الطيبي: "الظاهر','2','1030',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2005,'أن يراد بهَا السنة لأنها إذا قرنت بالكتاب يراد بها الحكمة قال تعالى: {وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ} ".','2','1031',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2006,'١٠٨٠ -[٣٨٢٨] "ياذا الأذنين". قال في النهاية: " قيل معناه الحض على حسن الاستماع والوعي لأن السمع بحاسية الأذن ومن خلق الله له أذنين فأغفل الاستماع، ولم يحسن الوعي لم يعذر، وَقيل: أن هذا القول من جملة مزحه - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - ولطيف أخلاقه، كما قال للمرأة عن زوجها: ذاك الذي في عينيه بيَاض ".','2','1031',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2007,'١٠٨١ -[٣٨٣٠] "عن أنس قال: كناني رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - بِبَقْلة كُنْتُ أجْتَنِيهَا". قال في النهاية: " أي كناه أبا حمزة".
قال الأزهري: " البقلة التي جناها أنس كان في طعمها لذع فسميت حمزة بفعلها يقال: رمانة حامزة؛ أي فيها حموضة ".','2','1031',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2008,'١٠٨٢ -[٣٨٤٤] "أسلم الناس، وآمن عمرُو بن العَاص". قال
في النهاية: " كان هذا إشارة إلى جماعة آمنوا معه خَوْفًا من السيف، وأن عَمْرًا كان مُخلِصًا، وهذا من العامِّ الذي يُراد به الخاصّ ".
وقال الطيبي: " التعريف في الناس للعهد، والمعهود مُسْلمة الفتح ".','2','1032',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2009,'١٠٨٣ -[٣٨٤٨] "اهتزَّ عَرْشُ الرَّحْمنِ". قال النووي:
"اختلفوا في تأويله، فقال قوم هو على ظاهره، واهتزاز العرش تحركه فرحًا بقدوم روح سَعْد، وَجَعَل الله في العرش تمييزًا، ولا مَانع منه، وهذا القول هو المختار، وقيل: المراد اهتزاز أهل العرش، وَهم حملته، وغيرهم من الملائكة فحذف المضاف وَالمراد بالاهتزاز','2','1032',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2010,'الاستبشار وَمنه قول العَرب فلان يهتز بالمكارم لا يريدون اضطراب جسمه وحركته، وَإنما يريدون ارتياحه إليها، وَإقباله عليها".
وقال الحربي: "هو كنايَة عن تعظيم شأن وفاته والعرب تنسب الشيء العظيم إلى أعظم الأشياء فتقول: أظلمت لموت فلان الأرض، وقامت له القيامَة".','2','1033',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2011,'١٠٨٤ -[٣٨٥٣] "وهو يَهْدبُها". بالدال المهملة؛ أي يجتنيهَا.','2','1033',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2012,'١٠٨٥ -[٣٨٤] "ذي طمرين". تثنية طمر وَهو الثوب الخلق.
"لا يؤبَهُ لهُ". أي لا يبالى به، ولا يلتفت إليه لحقارته.','2','1033',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2013,'"لَو أقْسم على الله لأبَرَّة منْهم البَراء بن مَالك".','2','1034',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2014,'١٠٨٦ -[٣٨٥٥] "لقد أعطيتَ مِزْمَارًا من مَزَامير آل دَاودَ". قال البيضاوي: " المزمار هنا مستعار للصوت الحسن والنغمة الطيبة، أي أعطيت حسن صوت يشبه بعض الحسن الذي كان لصوت داود، والمراد بآل دواد، داود نفسه وآل مفخم إذ لم يكن له آل مشهور يحسن الصّوت، والمشهور به هو نفسه".
وفي النهاية: " شبَّه حُسْن صَوته، وحلاوَة نَغْمَته بصوت المِزْمَارِ وداود هو النبيّ، وإليه المنتهى في حسن الصوت بالقراءة. والآل مُقْحَمةٌ، قيل: معناها هنا الشخصُ ".','2','1034',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2015,'١٠٨٧ -[٣٨٥٩] "خَيْر النَّاس قَرني، ثمَّ الذين يَلونَهمْ". قال','2','1034',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2016,'في النهاية: " يعني الصحَابة، ثم التابعين. والقرن: أهل كل زمان وهو مقدار التوسط في أعمار أهل كل زمَان مأخوذ من الاقتران، فكأنه المِقدار الذي يقترن فيه أهل ذلك الزمان في أعمارهم، وأحوالهم، وقيل: القرن، أربعون سنة، وقيل مائة، وقيل هو مُطَلَق من الزمَان وهو مصدر: قَرَن، يَقْرن ".
"ثمَّ يأتي قَوْم بَعْدَ ذلكَ تَسْبِق أيمانهم شَهادَتهمْ، أو شهادَتِهمْ أيْمانهمْ".','2','1035',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2017,'١٠٨٨ -[٣٨٦١] "لا تسبوا أصحابي". أحسن ما قيل فيه أنه خطاب.','2','1035',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2018,'١٠٨٩ -[٣٨٦٢] "الله الله في أصْحَابي". قال الطيبي: "أي اتقوا','2','1035',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2019,'الله ثم اتقوا الله في حق أصحابي لا تنقصوا من حقهم، ولا تسبوهم، أو التقدير أذكركم الله، وأنشدكم في حق أصحابي، وتعظيمهم، وتوقيرهم".
"من أحَبَّهم فَبِحبِّي أحَبَّهُمْ". أي بسبب حبه إياي أحبهم أي إنما أحبهم لأنه يحبني، وإنما أبغضهم لأنه يبغضني والعياذ بالله فحق لذلك قول من قال: من سبهم فقد استوجب القتل في الدنيا" انتهى.','2','1036',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2020,'١٠٩٠ -[٣٨٦٦] "إذا رأيْتُم الذِينَ يَسُبُّونَ أصْحابي، فقولوا لَعْنةُ الله على شَرِّكُمْ". قال الطيبي: " هذا من الكلام المنصف الذي كل من سمعه من مُوال أو منافر قال لمن خطب به قد أنصفك صَاحبك ومنه بيت حسان:
أتهجوه ولست له بكفوء ... فشركما لخيركما الفداء"','2','1036',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2021,'١٠٩١ -[٣٨٦٧] "فإنها بضعَة مني". قال في النهاية: "هي','2','1036',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2022,'بالفتح القطعَة من اللحم وقد تكسر أي أنها جزء منّي، كما أن القطعة من اللحم [جزء من اللحم] ".
"يَريبُنْي مَا رَابهَا". أي يسؤني ما يسؤها ويزعجني ما أزعجها.','2','1037',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2023,'١٠٩٢ -[٣٨٦٩] "ويُنْصبُني ما أنْصَبهَا". أي يتعبني ما أتعبها.
"وحمامّتي". بحاء مهملة، وميم مشدودة، قال في النهاية: "حامّة الإنسان: خاصَّتهُ ومن يَقْرُب منه. وهو الحميم أيضًا".','2','1037',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2024,'١٠٩٣ -[٣٨٧١] "أذْهبْ عَنْهمُ الرِّجْسَ، وطَهِّرهُمْ تَطْهيرًا". قال','2','1037',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2025,'الطيبي: "استعار للذنوب الرجس، وللتقوى الطهر، لأن عرض المقترف يتلوث بهَا، ويتدنس. كما يتلوث بدَنه بالأرجاس وأما الحسان فالعرض منها نقي مصون كالثوب الطاهر".','2','1038',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2026,'١٠٩٤ -[٣٨٧٢] "إني إذًا لبذْرَةٍ". بفتح الموحدة، وكسْر الذال','2','1038',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2027,'المعجمة، وراء، قال في النهاية: " البذر: الذي يغشى السر، ويظهر مَا يسْمعه "','2','1039',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2028,'١٠٩٥ -[٣٨٧٩] "يحيى بن دُرُستَ". بضم الدال المهملة، والراء وسكون السين المهملة.','2','1039',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2029,'١٠٩٦ -[٣٨٨٣] "مَا أشْكل عليْنا أصحَابَ رَسُولِ الله". بالنصب','2','1039',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2030,'على الاختصاص.','2','1040',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2031,'١٠٩٧ -[٣٨٨٥] "على جيش ذات السلاسل". قال في النهاية: "هو بضم السين الأولى، وكسر الثانية: ماء بأرض جذام".','2','1040',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2032,'١٠٩٨ -[٣٨٨٨] "أعزُبْ". بالعين المهملة، وزاي؛ أي أبعد.
"مَقْبُوحًا منبوحًا". قال في النهاية: "أي مبعَدًا".','2','1040',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2033,'١٠٩٩ -[٣٨٧٥] "ما غِرتُ على أحد مَا غِرتُ على خَدِيجَةَ"','2','1040',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2034,'قال الطيبي: "ما الثانية: يجوز أن تكون مصدرية، أو موصُولة، أي ما غرت مثل غيرتي، أو مثل الذي غرتها".','2','1041',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2035,'١١٠٠ -[٣٨٧٦] "بَبيْتٍ في الجنة من قَصَب". قال في النهاية: "أراد من زمردة، [أو لؤلؤة] مجوّفة". وقال في حرف القاف "القصب في هذا الحديث، لؤلؤ مُجَوّف، وَاسع كالقصر المنيف، والقَصَب من الجوهر: ما استطال منه في تجويف ".
"لا صخب فيه". هو الضجة، واضطراب الأصْوات للخصَام.
"ولا نصَبَ". أي ولا تعب، قال البغوي في شرح السنة: " نفي عن البيت الصخب والنصَب؛ لأنه مَا من بيت في الدنيَا يسكنه قوم إلا كان بين أهله صخب وجلبَة، وإلا كان في بنائه وإصلاحه نصب وتعب، فأخبر أن قصور الجنة خالية عن هذه الآفات ".','2','1041',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2036,'١١٠١ -[٣٨٧٧] "خَيْر نِسَائِهَا خَديجة بِنْت خَوَيْلَدٍ، وخَيْرُ','2','1041',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2037,'نِسائِهَا مَريمُ بِنتُ عِمْرَان". قال الطيبي: "الضمير في الثاني عائد إلى الأمة التي كانت فيها مريم، وفي الأول إلى هذه الأمة" انتهى.
وفي مسند الحارث من طريق حماد عن هشام بن عروة عن أبيه، قال: قال رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: "خَدِيجَةُ خَيْرُ نِسَاءِ عَالمهَا، ومَرْيمُ خَيْرُ نِسَاء عَالمها، وفاطمة خير نساء عالمها". قال الحافظ ابن حجر:
"هذا مرسل صحيح الإسناد، وهو يفسر حديث الترمذي ".','2','1042',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2038,'١١٠٢ -[٣٨٧٨] "حَسْبُكَ" مبتدأ "من نِساءِ العَالمينَ" متعلق به.','2','1042',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2039,'"مريم" خبره، الخطاب إمَّا عام أو للإنس. أي: كافيك معرفتك فضلهن من معرفة سائر النساء. قاله الطيبي.','2','1043',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2040,'١١٠٣ -[٣٩٠٣] "عن أنس، عن أبي طلحة قال: قال لي رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ -: أقْرئ قَوْمكَ السلام، فإنَّهمْ مَا عَلِمْتُ أعِفة صُبر" قال في مسند الطيالسي: " من ذا الطريق عن أنس قال: دخل أبو طلحة على النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - في شكواه الذي قبض فيه، فقال فذكره.
"أقرئ قومك السلام" قال في النهاية: " يقال: أقْرِىء فُلانًا السلام، وإقْرَأ عليه السلام، كأنه حين يُبَلِّغه سَلامه يَحْمِله على أن يَقرأ السَّلام ".
"فإنهم ما علمت أعفة صبر". قال الطيبي: "أعفة؛ جمع عفيف مرفوع خبر إن، و"ما علمت" معترضة. و"ما" موصُولة، والخبر','2','1043',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2041,'محذوف، أي الذي علمت منهم أنهم كذلك يتعففون عن السؤال، ويتحملون الصبر عند القتال وهو مثل الحديث الآخر، ويكثرون عند العف.','2','1044',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2042,'١١٠٥ -[٣٩٠٧] "الأنصار كَرِشي، وعَيْبَتِي". قال في النهاية: "أراد أنهم بطانته، وموضع سِرَّه، وأمانَتِهِ، والذين يعْتَمد عليهم في أموره، واستعار الكَرِش، والعيْبَة لذلك؛ لأن المُجْترَّ يجمع عَلَفه في كَرِشه، والرجل يضع ثيابه في عيبته، وقيل: [أراد] بالكَرِش الجماعة. أي جماعتي وصحابتي، ويقال: عليه كرِشٌ من الناس: أي جماع".','2','1044',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2043,'١١٠٦ -[٣٩٠٨] "اللهُمَّ أذقْتَ أَوَّلَ قُرَيْشٍ نكالاً" أي عقوبة يوم','2','1044',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2044,'بدر، والأحزاب.','2','1045',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2045,'١١٠٧ -[٣٩١٨] "ولأوَائِهَا" هي الشدة، وضِيق المعيشة.','2','1045',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2046,'١١٠٨ -[٣٩٢٠] "وتُنصِّع طَيِّبهَا" بنون ثم صاد، وعين مهملتين، أي تخلصه، ويروى تنصح طيبها؛ أي تظهر، ويروى بالباء الموحدة، والضاد المعجمة، كذا ذكره الزمخشري، وقال: هو من أبضعته بضاعة إذا دفعتها إليه، يعني أن المدينة تعطي طيبها ساكنها، والمشهور الأول، وروي بالضاد، والخاء المعجمتين، وبالحاء المهملة','2','1045',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2047,'من النضح، والنضح؛ وهو رش الماء.','2','1046',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2048,'١١٠٩ -[٣٩٢١] "ما ذَعرتُهَا" أي ما نفرتها.','2','1046',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2049,'١١١٠ -[٣٩٢٢] "هذا جَبَل يُحِبُّنَا ونُحبُّهُ" قال الخطابي: " هذا محمولٌ على المجاز، أراد يحبّنا أهله، ونحب أهلهُ، وهم الأنصار ".
وقال البغوي في شرح السنة: " الأولى إجراؤه على ظاهره ولا ينكر وصف الجمادات بحبّ الأنبياء والأولياء وأهل الطاعة، كما حنَّ الجذع لفراقه، وكما أخبر - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - أن حجرًا كان يسلم عليه قبل الوحي','2','1046',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2050,'فلا ينكر أن يكون جبل أحد وجميع أجزاء المدينة كانت تحبه، وتحن إلى لقائه حالة مفارقته".
وقال الطيبي: " هذا هو المختار ولا محيد عنه ".
وقال التوربشتي: " لعله أراد بالجبل أرض المدينة كلها، وإنما خص الجبل بالذكر لأنه أول ما يبدُو من أعلامها له ".','2','1047',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2051,'١١١١ -[٣٩٢٣] "أو قِنسْرينَ" بكسر القاف.','2','1047',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2052,'١١١٢ -[٣٩٢٥] "على الحزْوَرةِ" قال في النهاية: "هي موضع بمكة عند باب الحنَّاطِين وهو بوزن قَسْورَة".','2','1047',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2053,'قال الشافعي: " الناس يشددون الحزورة والحديبية وهما مخففان ".
وفي الأمثال للميداني: " أن وكيع بن سلمة بن زهير بن إياد -وكان وَلي أمر البيت بعد جُرْهُم- بنى صَرْحًا بأسْفل مكة، وجعل فيه أمَةً له يقال لها حزوَرة، وبها سميت حزورَة مكة".','2','1048',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2054,'١١١٣ -[٣٩٣٢] "لأنا بهم أو ببعْضهمْ، أوْثقُ مِنِّي بِكُمْ، أو بِبَعْضُكُمْ" قال المظهري: " المعنى، وثوقي، واعتمادي بهم، أو ببعضهم أكثر من وثوقي بكم، أو ببعضكم ". وقال الطيبي: " المخاطب بقوله بكم، أو ببعضكم قوم مخصُوصُون دعُوا إلى الإنفاق في سبيل الله فتقاعسوا عنه فهو كالتأنيب والتعيير، يدل عليه قوله في الحدث الآخر: {وَإِنْ تَتَوَلَّوْا يَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ} فإنه جاء عقب قوله: {هَا أَنْتُمْ هَؤُلَاءِ تُدْعَوْنَ لِتُنْفِقُوا فِي سَبِيلِ اللَّهِ فَمِنْكُمْ مَنْ يَبْخَلُ} ".','2','1048',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2055,'"١١١٤- ٣٩٣٤ أن النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - نظر إلى اليمن، فقال: اللهم أقبل بقلوبهم" زاد الطبراني: " ونظر قبل العراق فقال: اللهم أقبل بقلوبهم، [ونظر قبل الشام] فقال: اللهم أقبل بقلوبهم " ثم أخرج من طريق منصور بن زاذان، عن قتادة، عن أنس قال: دعَا رسُول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - لأمته فقال: " اللهمَّ أقبل بقلوبهم على دينك، وحط من ورَائهم برحمتك ".
قال الطبراني: " ولم يذكر زيد بن ثابت ".','2','1049',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2056,'١١١٥ -[٣٩٣٥] "وأَرَق أفئدة" قال في النهاية: "ألين، وأقبل للموعظة. والمراد بالرِّقة ضد القسوة، والشِّدة".','2','1049',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2057,'"الإيمانُ يمانٍ، والحِكمةُ يمانيَّة" قال في النهاية: " إنما قال ذلك لأن الإيمان بَدَأ بمكة وهي من تهامة، وتهامةُ من أرض اليمن، ولهذا يقال الكعبة اليمانية، وقيل: إنه قال هذا القول وهو بتبوك ومكة والمدينة يومئذ بينه وبين اليمن، فأشار إلى ناحية اليمن، وهو يريد مكة والمدينة.
وقيل: أراد بهذا القول الأنصار؛ لأنهم يمانيّون، وهُم نَصَرُوا الإيمان، والمؤمنين وآوَوْهم، فَنُسِبَ الإيمانُ إليهم ".','2','1050',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2058,'١١١٦ -[٣٩٣٦] "المُلْك في قُرَيْشٍ، والقَضَاءُ في الأنصَارِ، والأذان في الحبشة" قال في النهاية: "خص القضاء بالأنصار لأن أكثرهم فقهاء، منهم معاذ بن جبل، وأبي بن كعب، وزيد بن ثابت وغيرهم".','2','1050',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2059,'١١١٧ -[٣٩٣٧] "الأزدِ" قال التوربشتي: " هو بسكون الزاي، ويقال: الأسد، بسكون السين، وهو بالسين أفصح. أبو حيٍّ من اليمن، وهما أزدان، أزد شنوءة، وأزد عمان ".
وقال البيضاوي: "المراد في الحديث أزد شنؤة".
"أزد الله في الأرض" قال الطيبي: "يحتمل وجوهًا: أحدها','2','1050',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2060,'اشتهارهم بهذا الاسم بأنهم ثابتون في الحرب لا يفرون، ولهذا قال البيضاوي: إضافتهم إلى الله من حيث أنهم حزبه، وأهل نصرة رسوله.
والثاني: أن تكون الإضافة للاختصاص، والتشريف، كبيت الله، وناقة الله. والثالث: أن يراد به الشجاعة والكلام على التشبيه، أي الأزد أسد الله، فجاء به، إما مشاكلة، أو قلب السين زايًا".','2','1051',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2061,'١١١٨ -[٣٩٤٤] "في ثَقيفٍ كذَّاب، ومُبِيرٌ" أي مهلك، أشار بالكذاب إلى المختار بن أبي عبيد الثقفي الذي ادعى النبوة وبالمبير إلى الحجاج.','2','1051',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2062,'١١١٩ -[٣٩٤٧] "والأشْعرونَ" قال الطيبي: " سقوط الياء في جامع الترمذي " قال الجوهري: "تقول العرب جأتك الأشعَرون بحذف الياء".','2','1052',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2063,'١١٢٠ -[٣٩٤٨] "أسْلَم سَالمهَا الله، وغِفَارُ غَفَرَ اللهُ لَهَا، وعُصَية عَصَتِ الله ورسولهُ". قال في النهاية: "سالمها الله من المُسالمة','2','1052',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2064,'وترك الحرب. يحتمل أن يكون دعاء وإخْبار: إما دعاء لها أن يُسَالِمهَا الله ولا يأمر بحَرْبهَا، وأخبرَ أن الله قد سَالمهَا ومنَع من حربها". "وغفر الله لها" يحتمل أن يكون دعا لها بالمغفرة أو إخبار أن الله قد غفر لها".
وقال الطيبي: " يحتمل أن يكونا خبرين وأن يحمل على الدعاء لهُما، وأما قوله: " وعُصيَّة عصت الله ورسوله " [فهو] إخبار ولا يجوز حمله على الدعاء ".
وقال البغوي: " قيل إنما دعا لأسلم وغفار؛ لأن دخولهما في الإسلام كان من غير حرب، وكانت غفار توبن -أي تتهم- بسرقة الحجاج، فدعا رسول الله - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - بأن يمحو عنها تلك السيئة، ويغفرها لهم. وأما عُصَيَّة فهُم الذين قتلوا القُرَّاء ببئر معونة فكان النبي - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ - يقنت عليهم ".
وقال القاضي عياض: " هذا من حسن الكلام، والمجانسة في الألفاظ ".','2','1053',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2065,'١١٢١ -[٣٩٥٤] "طُوبى للشَّام" قال في النهاية: "المراد','2','1053',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2066,'بطوبى في هذا الحديث فُعْلَى من الطّيب، لا الجنة ولا الشجرة التي فيها كما يراد في غيره من الأحاديث".
قال مؤلفه رحمه الله تعالى عليه: آخر ما علقته على جامع الترمذي رحمه الله فرغت من تأليفه يوم الأربعاء، سلخ رجب، سنة أربع وتسعمائة. [انتهى، والحمد لله رب العالمين، وصلى الله على محمد وآله وصحبه وسلم تسليمًا كثيرًا.
آخر ما علقه الإمام العلامة المجتهد الشيخ جلال الدين السُّيوطي تغمده الله برحمته، وكان الفراغ من كتابته في غرة رجب الفرد سنة ١١٢٢ هـ] .','2','1054',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2067,'<span data-type="title" id=toc-57>الخاتمة</span>
وبعد، فهذا ما وسعه الجهد وسمح به الوقت، وجاد به القلم، وتمكن منه الفهم وعذرى أني لم أدخر وسعًا في إخراج هذا الكتاب على أكمل صورة، فإن يكن صوابًا فمن الله، وإن يكن فيه نقص أو قصور فمني، وهو من سمة بني آدم والكمال لله وحده، وأرجو ممن عثر في هذا العمل على سقطة لسان أو سبق قلم، أو قصور في العبارة، أو نقص في الفهم، أن ينبهني على ذلك وله جزيل الشكر والدعاء، وسيجد أذنًا صاغية، وقلبًا مفتوحًا وأسأل الله تبارك وتعالى أن ينفعني بهذا العمل وينفع به من قرأه، وأن يكتب لي الأجر فيه، وقد رأيت في هذه الجولة التي قضيتها في رحاب هذا السفر المبارك أن أضمن هذه الخاتمة بعض الأمور التي توصلت إليها من خلال البحث وهي تتلخص فيما يلي:
١- يعتبر "قوت المغتذي" الشرح الرابع للسيوطي على كتب السنة بعد الصحيحين والموطأ وسنن أبي داود، ففي الصحيحين ضبَط الألفاظ، وتفسير الغريب، وبيان اختلاف الروايات وكذا الموطأ إلا أنه صرّح فيه بأنه أوسع منهما ولخص في مرقاة الصعود في شرح سنن أبي داود معالم السنن للخطابي وأما في قوت المغتذي فنوّع مصادره، وعدد أغراضه، التي عالج فيها مقاصده التي توخّاها في هذا الشرح، فكان شرحًا أو تعليقًا وسطًا بين الإيجاز المخل والشرح المطول.
ْ٢- تعدُّد أصناف مصادره التي اعتمد عليها يُظهر مقدار ثراء مادة هذا الشرح.
٣- حوى فيه ثلاثة شروح مشهورة من شروح الجامع العارضة','2','1055',NULL,NULL,'0');
INSERT INTO [b7562] VALUES (2068,'للقاضي ابن العربي، والنفح الشذي لابن سيد الناس، وتكملته للعراقي.
٤- تميُّز أسلوب السيوطي في هذا الكتاب جعله في متناول طلاب العلم عامة وأهل الحديث خاصة وابتعد عن التكلف أو ضعف العرض.
٥- أهمية المقدمة وغزارة المعلومات التي ضمنها السيوطي فيها والخاصة بالجامع وعنايته بشرح مصطلحات الترمذي في حكمه على الأحاديث والتي توضح مراده فيها.
٦- ضرورة العناية بمثل هذه التعليقات المفيدة التي توضّح المبهم من الغريب في المتن، والتنكيت على الرواة حيث تظهر تفرُّد أحد المصنفين الستة دون غيره بتخريج حديث ذلك الراوي.
وبعد: فأحمد الله عز وجل وأشكره حمدًا كثيرًا كما ينبغي لجلال وجهه وعظيم سلطانه، حمدًا كثيرًا طيبًا ملء السموات وملء الأرض وملء ما بينهما وملء ما شاء ربنا من شيء بعد، على ما وفق وأعان من إعداد هذا البحث، والحمد لله الذي بنعمته تتم الصالحات، وآخر دعوانا أن الحمد لله رب العالمين وصلى الله وبارك على محمد وعلى آله وصحبه أجمعين.

* * *','2','1056',NULL,NULL,'0');
