INSERT INTO [b8137] VALUES (1,'<span data-type="title" id=toc-1>مقدمة</span>
INSERT INTO [b8137] VALUES (2,'<span data-type="title" id=toc-2>مسائل الجاهلية</span>
INSERT INTO [b8137] VALUES (3,'<span data-type="title" id=toc-4>الثانية: أنهم متفرقون في دينهم</span>، كما قال تعالى: {كل حزب بما لديهم فرحون} [الروم: ٣٢] ، وكذلك في دنياهم، ويرون ذلك هو الصواب، فأي بالاجتماع في الدين بقوله: {شرع لكم من الدين ما وصى به نوحاً والذي أوحينا إليك وما وصينا به إبراهيم وموسى وعيسى أن أقيموا الدين ولا تتفرقوا فيه} [الشورى: ١٣] ، فقال تعالى: {إن الذين فرقوا دينهم وكانوا شيعاً لست منهم في شيء} [الأنعام:١٥٩] .
INSERT INTO [b8137] VALUES (4,'<span data-type="title" id=toc-5>الثالثة: أن مخالفة ولي الأمر وعدم الانقياد له فضيلة، والسمع والطاعة ذل ومهانة</span>، فخالفهم رسول الله صلى الله عليه وسلم، وأمر بالصبر على جور الولاة، وأمر بالسمع والطاعة لهم والنصيحة، وغلظ غي ذلك، وأبد أفيه وأعاد.
INSERT INTO [b8137] VALUES (5,'<span data-type="title" id=toc-6>الرابعة: أن دينهم مبني على أصول أعظمها التقليد</span>، فهو القاعدة الكبرى لجميع الكفار أولهم وآخرهم، كما قال تعالى: {وكذلك ما أرسلنا من قبلك في قرية من نذير إلا قال مترفوها إنا وجدنا آباءنا على أمة وإنا على آثارهم مقتدون} [الزخرف: ٢٣] ، وقال تعالى: {وإذا قيل لهم اتبعوا ما أنزل الله قالوا بل نتبع ما وجدنا عليه آباءنا أو لو كان الشيطان يدعوهم إلى عذاب السعير} [لقمان: ٢١] ، فأتاهم بقوله: {قل إنما أعظكم بواحدة أن تقوموا لله مثنى وفرادى ثم تتفكروا ما بصاحبكم من جنة} الآية [سبأ:٤٦] .
INSERT INTO [b8137] VALUES (6,'<span data-type="title" id=toc-7>الخامسة: أن من أكبر قواعدهم الاغترار بالأكثر</span>، ويحتجون به على صحة الشيء، ويستدلون على بطلان الشيء بغربته، وقله أهله، فأتاهم بضد ذلك، وأوضحه في غير موضع من القرآن.',NULL,'8',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (7,'<span data-type="title" id=toc-8>السادسة: الاحتجاج بالمتقدمين</span>، كقوله: {فما بال القرون الأولى} [طه:٥١] {ما سمعنا بهذا في آبائنا الأولين} [المؤمنين:٢٤] .',NULL,'9',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (8,'<span data-type="title" id=toc-9>السابعة: الاستدلال بقوم أعطوا قوى في الأفهام والأعمال، وفي الملك والمال والجاه</span>، فرد الله ذلك بقوله: {ولقد مكناهم فيما إن مكناكم فيه} الآية [الأحقاف: ٢٦] وقوله: {وكانوا من قبل يستفتحون على الذين كفروا فلما جاءهم ما عرفوا كفروا به} [البقرة: ٨٩] .
INSERT INTO [b8137] VALUES (9,'<span data-type="title" id=toc-10>الثامنة: الاستلال على بطلان الشيء بأنه لم يتبعه إلا الضعفاء</span>، كقوله: {أنؤمن لك واتبعك الأرذلون} [الشعراء:١١١] .
INSERT INTO [b8137] VALUES (10,'<span data-type="title" id=toc-11>التاسعة: الاقتداء بفسقة العلماء</span>، فأتى بقوله: {يا أيها الذين آمنوا إن كثيراً من الأحبار والرهبان ليأكلون أموال الناس بالباطل ويصدون عن سبيل الله} [التوبة: ٣٤] .
INSERT INTO [b8137] VALUES (11,'<span data-type="title" id=toc-12>العاشرة: الاستدلال على بطلان الدين بقلة أفهام أهله، وعدم حفظهم</span>، كقوله {بادي الرأي} [هود:٢٧] .',NULL,'10',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (12,'<span data-type="title" id=toc-13>الحادية عشرة: الاستدلال بالقياس الفاسد</span>، كقوله: {إن أنتم إلا بشر مثلنا} [إبراهيم:١٠] .',NULL,'10',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (13,'<span data-type="title" id=toc-14>الثانية عشرة: إنكار القياس الصحيح</span>، والجامع لهذا وما قبله عدم فهم الجامع والفارق.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (14,'<span data-type="title" id=toc-15>الثالثة عشرة: الغلو في العلماء والصالحين</span>، كقوله: {يا أهل الكتاب لا تغلوا في دينكم ولا تقولوا على الله إلا الحق} [النساء: ١٧١] .',NULL,'10',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (15,'<span data-type="title" id=toc-16>الرابعة عشرة: أن كل ما تقدم مبني على قاعدة وهي النفي والإثبات</span>، فيتبعون الهوى والظن، ويعرضون عما آتاهم الله.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (16,'<span data-type="title" id=toc-17>الخامسة عشرة: اعتذارهم عن إتباع ما آتاهم الله بعدم الفهم</span>، كقوله: {قلوبنا غلف} [النساء: ١٥٥] ، {يا شعيب ما نفقه كثيراً مما تقول} [هود: ٩١] ، فأكذبهم الله، وبين أن ذلك بسبب الطبع على قلوبهم، والطبع بسبب كفرهم.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (17,'<span data-type="title" id=toc-18>السادسة عشرة: اعتياضهم عما أتاهم من الله بكتب السحر</span>، كما ذكر الله ذلك في قوله: {نبذ فريق من الذين أوتوا الكتاب كتاب الله وراء ظهورهم كأنهم لا يعلمون * واتبعوا ما تتلوا الشياطين على ملك سليمان} [البقرة:١٠١-١٠٢] .',NULL,'11',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (18,'<span data-type="title" id=toc-19>السابعة عشرة: نسبة باطلهم إلى الأنبياء</span>، كقوله: {وما كفر سليمان} [البقرة:١٠٢] .
INSERT INTO [b8137] VALUES (19,'<span data-type="title" id=toc-20>الثامنة عشرة: تناقضهم في الانتساب</span>، ينتسبون إلى إبراهيم، مع إظهارهم ترك إتباعه.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (20,'<span data-type="title" id=toc-21>التاسعة عشرة: قدحهم في بعض الصالحين بفعل بعض المنتسبين</span>، كقدح اليهود في عيسى، وقدح اليهود والنصارى في محمد صلى الله عليه وسلم.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (21,'<span data-type="title" id=toc-22>العشرون: اعتقادهم في مخاريق السحرة وأمثالهم أنها من كرامات الصالحين</span>، ونسبته إلى الأنبياء كما نسبوه لسليمان.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (22,'<span data-type="title" id=toc-23>الحادية والعشرون: تعبدهم بالمكاء والتصدية</span>.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (23,'<span data-type="title" id=toc-24>الثانية والعشرون: أنهم اتخذوا دينهم لهواً ولعباً</span>.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (24,'<span data-type="title" id=toc-25>الثالثة والعشرون: أن الحياة الدنيا غرتهم</span>، فظنوا أن عطاء الله منها يدل على رضاه، كقوله: {نحن أكثر أموالاً وأولاداً وما نحن بمعذبين} [سبأ:٣٥] .',NULL,'12',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (25,'<span data-type="title" id=toc-26>الرابعة والعشرون: ترك الدخول في الحق إذا سبقهم إليه الضعفاء تكبراً وأنفةً</span>، فأنزل الله {ولا تطرد الذين يدعون ربهم} الآية [الأنعام: ٥٢] .',NULL,'12',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (26,'<span data-type="title" id=toc-27>الخامسة والعشرون: الاستدلال على بطلانه بسبق الضعفاء </span>كقوله: {لو كان خيراً ما سبقونا إليه} [الأحقاف:١١] .',NULL,'12',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (27,'<span data-type="title" id=toc-28>السادسة والعشرون: تحريف كتاب الله من بعد ما عقلوه وهم يعلمون</span>.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (28,'<span data-type="title" id=toc-29>السابعة والعشرون: تصنيف الكتب الباطلة ونسبتها إلى الله</span>، كقوله: {فويل للذين يكتبون الكتاب بأيديهم ثم يقولون هذا من عند الله} الآية [البقرة:٧٩] .',NULL,'13',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (29,'<span data-type="title" id=toc-30>الثامنة والعشرون: أنهم لا يعقلون من الحق إلا الذي مع طائفتهم</span>، كقوله: {نؤمن بما أنزل علينا} [البقرة:٩١] .',NULL,'13',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (30,'<span data-type="title" id=toc-31>التاسعة والعشرون: أنهم مع ذلك لا يعلمون بما تقوله الطائفة </span>كما نبه الله عليه بقوله: {فلم تقتلون أنبياء الله من قبل إن كنتم مؤمنين} [البقرة: ٩١] .',NULL,'13',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (31,'<span data-type="title" id=toc-32>الثلاثون: وهي من عجائب آيات الله أنهم لما تركوا وصية الله بالاجتماع</span>، وارتكبوا ما نهى الله عنه من الآفة صار {كل حزب بما لديهم فرحون} [المؤمنون: ٥٣] .',NULL,'13',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (32,'<span data-type="title" id=toc-33>الحادية والثلاثون: وهي من عجائب الله أيضاً، معاداتهم الدين الذي انتسبوا إليه غاية العداوة</span>، ومحبتهم دين الكفار الذين عادوهم وعادوا نبيهم، وفتنتهم غاية المحبة، كما فعلوا مع النبي صلى الله عليه وسلم لما آتاهم بدين موسى واتبعوا كتب السحر، وهي من دين آل فرعون.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (33,'<span data-type="title" id=toc-34>الثانية والثلاثون: كفرهم بالحق إذا كان مع من لا يهودونه</span>، كما قال تعالى: {وقالت اليهود ليست النصارى على شيء، وقالت النصارى ليست اليهود على شيء} الآية [البقرة: ١١٣] .',NULL,'14',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (34,'<span data-type="title" id=toc-35>الثالثة والثلاثون: إنكارهم ما أقروا أنه من دينهم</span>، كما فعلوا في حج البيت فقال تعالى: {ومن يرغب عن ملة إبراهيم إلا من سفه نفسه} [البقرة: ١٣٠] .',NULL,'14',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (35,'<span data-type="title" id=toc-36>الرابعة والثلاثون: أن كل فرقة تدعى أنها الناجية</span>، فأكذبهم الله بقوله: {هاتوا برهانكم إن كنتم صادقين} [النحل: ٦٤] .
INSERT INTO [b8137] VALUES (36,'<span data-type="title" id=toc-37>الخامسة والثلاثون: التعبد بكشف العورات</span>، كقوله: {وإذا فعلوا فاحشة} الآية [الأعراف: ٢٨] .',NULL,'14',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (37,'<span data-type="title" id=toc-38>السادسة والثلاثون: التعبد بتحريم الحلال</span>، كما تعبد بالشرك.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (38,'<span data-type="title" id=toc-39>السابعة والثلاثون: التعبد باتخاذ الأحبار والرهبان أرباباً من دون الله</span>.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (39,'<span data-type="title" id=toc-40>الثامنة والثلاثون: الإلحاد في الصفات</span>، كقوله تعالى: {ولكن ظننتم أن الله لا يعلم كثيرا مما تعملون} [فصلت: ٢٢] .',NULL,'15',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (40,'<span data-type="title" id=toc-41>التاسعة والثلاثون: الإلحاد في الأسماء </span>كقوله تعالى: {وهم يكفرون بالرحمن} [الرعد:٣٠] .',NULL,'15',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (41,'<span data-type="title" id=toc-42>الأربعون: التعطيل</span>، كقول آل فرعون.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (42,'<span data-type="title" id=toc-43>الحادية والأربعون: نسبة النقائص إليه</span>.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (43,'<span data-type="title" id=toc-44>الثانية والأربعون: الشرك في الملك</span>، كقول المجوس.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (44,'<span data-type="title" id=toc-45>الثالثة والأربعون: جحود القدر</span>.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (45,'<span data-type="title" id=toc-46>الرابعة والأربعون: الاحتجاج على الله</span>.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (46,'<span data-type="title" id=toc-47>الخامسة والأربعون: معارضة شرع الله بقدره</span>.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (47,'<span data-type="title" id=toc-48>السادسة والأربعون: مسبة الدهر</span>، كقولهم: {وما يهلكنا إلا الدهر} [الجاثية: ٢٤] .',NULL,'15',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (48,'<span data-type="title" id=toc-49>السابعة والأربعون: إضافة نعم الله إلى غيره </span>كقوله: {يعرفون نعمة الله ثم ينكرونها} [النحل: ٨٣] .',NULL,'16',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (49,'<span data-type="title" id=toc-50>الثامنة والأربعون: الكفر بآيات الله</span>.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (50,'<span data-type="title" id=toc-51>التاسعة والأربعون: جحد بعضها</span>.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (51,'<span data-type="title" id=toc-52>الخمسون: قولهم: "ما أنزل الله على بشر من شيء </span>"
INSERT INTO [b8137] VALUES (52,'<span data-type="title" id=toc-53>الحادية والخمسون: قولهم في القرآن: " إن هذا إلا قول البشر </span>"
INSERT INTO [b8137] VALUES (53,'<span data-type="title" id=toc-54>الثانية والخمسون: القدح في حكمة الله تعالى</span>.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (54,'<span data-type="title" id=toc-55>الثالثة والخمسون: إعمال الحيل الظاهرة والباطنة في دفع ما جاءت به الرسل</span>، كقوله: {ومكروا ومكر الله} [آل عمران: ٥٤] .
INSERT INTO [b8137] VALUES (55,'<span data-type="title" id=toc-56>الرابعة والخمسون: الإقرار بالحق ليتوصلوا به إلى دفعه </span>كما قال في الآية.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (56,'<span data-type="title" id=toc-57>الخامسة والخمسون: التعصب للمذهب</span>، كقوله فيها: {ولا تؤمنوا إلا لمن تبع دينكم} [آل عمران: ٧٣] .',NULL,'17',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (57,'<span data-type="title" id=toc-58>السادسة والخمسون: تسمية إتباع الإسلام شركاً</span>، كما ذكره في قوله تعالى: {ما كان لبشر أن يؤتيه الله الكتاب والحكم والنبوة ثم يقول للناس كونوا عبادا لي من دون الله} الآية [آل عمران: ٧٩] .',NULL,'17',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (58,'<span data-type="title" id=toc-59>السابعة والخمسون: تحريف الكلم عن مواضعه</span>.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (59,'<span data-type="title" id=toc-60>الثامنة والخمسون: تلقيب أهل الهدى بالصباة والحشوية</span>.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (60,'<span data-type="title" id=toc-61>التاسعة والخمسون: افتراء الكذب على الله</span>.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (61,'<span data-type="title" id=toc-62>الستون: كونهم إذا غلبوا بالحجة فزعوا إلى الشكوى للملوك</span>، كما قال: {أتذر موسى وقومه ليفسدوا في الأرض} [الأعراف: ١٢٧] .',NULL,'17',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (62,'<span data-type="title" id=toc-63>الحادية والستون: رميهم إياهم بالفساد في الأرض</span>، كما في الآية.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (63,'<span data-type="title" id=toc-64>الثانية والستون: رميهم إياهم بانتقاص دين الملك </span>كما قال تعالى: {ويذرك وآلهتك} [الأعراف: ١٢٧] وكما قال تعالى: {إني أخاف أن يبدل دينكم} الآية [غافر:٢٦] .',NULL,'18',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (64,'<span data-type="title" id=toc-65>الثالثة والستون: رميهم إياهم بانتقاص آلهة الملك</span>، كما في الآية.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (65,'<span data-type="title" id=toc-66>الرابعة والستون: رميهم لإياهم بانتقاص دين الملك</span>، كما قال تعالى: {ويذرك وآلهتك} الآية، وكما قال تعالى: {إني أخاف أن يبدل دينكم}',NULL,'18',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (66,'<span data-type="title" id=toc-67>الخامسة والستون: رميهم إياهم بتبديل الدين</span>، كما قال: {إني أخاف أن يبدل دينكم أو أن يظهر في الأرض الفساد} [غافر: ٢٦] .',NULL,'18',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (67,'<span data-type="title" id=toc-68>السادسة والستون: رميهم إياهم بانتقاض الملك</span>، كقولهم: {ويذرك وآلهتك} [الأعراف: ١٢٧] .',NULL,'18',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (68,'<span data-type="title" id=toc-69>السابعة والستون: دعواهم العمل بما عندهم من الحق</span>، كقوله: {نؤمن بما أنزل علينا} [البقرة: ٩١] .
INSERT INTO [b8137] VALUES (69,'<span data-type="title" id=toc-70>الثامنة والستون: الزيادة في العبادة</span>، كفعلهم يوم عاشوراء.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (70,'<span data-type="title" id=toc-71>التاسعة والستون: نقصهم منها</span>، كتركهم الوقوف بعرفات.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (71,'<span data-type="title" id=toc-72>السبعون: تركهم الواجب ورعاً</span>.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (72,'<span data-type="title" id=toc-73>الحادية والسبعون: تعبدهم بترك الطيبات من الرزق</span>.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (73,'<span data-type="title" id=toc-74>الثانية والسبعون: تعبدهم بترك زينة الله</span>.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (74,'<span data-type="title" id=toc-75>الثالثة والسبعون: دعواهم الناس إلى الضلال بغير علم</span>.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (75,'<span data-type="title" id=toc-76>الرابعة والسبعون: دعواهم محبة الله مع تركهم شرعه</span>، فطالبهم الله بقوله: {إن كنتم تحبون الله} الآية [آل عمران: ٣١] .',NULL,'19',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (76,'<span data-type="title" id=toc-77>الخامسة والسبعون: دعواهم إياهم إلى الكفر مع العلم</span>.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (77,'<span data-type="title" id=toc-78>السادسة والسبعون: المكر الكبار، كفعل قوم نوح</span>.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (78,'<span data-type="title" id=toc-79>السابعة والسبعون: أن أئمتهم: إما عالم فاجر، وإما عابد جاهل</span>، كما في قوله: {وقد كان فريق منهم يسمعون كلام الله} إلى قوله: {ومنهم أميون} [البقرة: ٧٥-٧٨] .',NULL,'19',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (79,'<span data-type="title" id=toc-80>الثامنة والسبعون: تمنيهم الأماني الكاذبة</span>، كقوله لهم: {لن تمسنا النار إلا أياماً معدودة} [البقرة: ٨٠] ، وقولهم: {لن يدخل الجنة إلا من كان هوداً أو نصارى} [البقرة: ١١١] .',NULL,'20',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (80,'<span data-type="title" id=toc-81>التاسعة والسبعون: دعواهم محبة الله مع تركهم شرعه </span>فطالبهم الله بقوله: {قل إن كنتم تحبون الله}',NULL,'20',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (81,'<span data-type="title" id=toc-82>الثمانون: اتخاذ قبور أنبيائهم وصالحيهم مساجد</span>.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (82,'<span data-type="title" id=toc-83>الحادية والثمانون: اتخاذ آثار أنبيائهم مساجد</span>، كما ذكر عن عمر.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (83,'<span data-type="title" id=toc-84>الثانية والثمانون: اتخاذ السراج على القبور</span>.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (84,'<span data-type="title" id=toc-85>الثالثة والثمانون: اتخاذها أعياداً</span>.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (85,'<span data-type="title" id=toc-86>الرابعة والثمانون: الذبح عند القبور</span>.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (86,'<span data-type="title" id=toc-87>الخامسة والثمانون: التبرك بآثار المعظمين</span>، كدار الندوة، وافتخار من كانت تحت يده بذلك، كما قيل لحكيم بن حزام: بعث مكة قريش، فقال: ذهب المكارم إلا التقوى.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (87,'<span data-type="title" id=toc-88>السادسة والثمانون: التبرك بآثار المعظمين</span>، كدار الندوة، وافتخار من كانت تحت يده بذلك، كما قيل لحكيم بن حزام: بعت مكرمة قريش؟ فقال: ذهبت المكارم إلا التقوى',NULL,'21',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (88,'<span data-type="title" id=toc-89>السابعة والثمانون: الفخر بالأحساب</span>.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (89,'<span data-type="title" id=toc-90>الثامنة والثمانون: الاستسقاء بالأنواء</span>.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (90,'<span data-type="title" id=toc-91>التاسعة والثمانون: الطعن في الأنساب</span>.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (91,'<span data-type="title" id=toc-92>التسعون: النياحة</span>.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (92,'<span data-type="title" id=toc-93>الحادية والتسعون: أن أجل فضائلهم الفخر بالأنساب</span>، فذكر الله فيه ما ذكر.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (93,'<span data-type="title" id=toc-94>الثانية والتسعون: أن أجل فضائلهم أيضاً الفخر ولو بحق</span>، فنهي عنه.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (94,'<span data-type="title" id=toc-95>الثالثة والتسعون: أن الذي لابد منه عندهم تعصب الإنسان لطائفته، ونصر من هو منها ظالماً أو مظلوماً</span>.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (95,'<span data-type="title" id=toc-96>الرابعة والتسعون: أن دينهم أخذ الرجل بجريمة غيره</span>، فأنزل الله: {ولا تزر وازرة وزر أخرى} [الأنعام:١٤٦] .',NULL,'21',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (96,'<span data-type="title" id=toc-97>الخامسة والتسعون: تعيير الرجل بما في غيره</span>، فقال: «أعيرته بأمه، إنك امرؤ فيك جاهلية» .',NULL,'22',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (97,'<span data-type="title" id=toc-98>السادسة والتسعون: الافتخار بولاية البيت</span>، فذمهم الله بقوله: {مستكبرين به سامراً تهجرون} [المؤمنون:٦٧] .',NULL,'22',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (98,'<span data-type="title" id=toc-99>السابعة والتسعون: الافتخار بكونهم ذرية الأنبياء</span>، فأتى الله بقوله: {تلك أمة قد خلت لها ما كسبت} الآية [البقرة: ١٣٤] .',NULL,'22',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (99,'<span data-type="title" id=toc-100>الثامنة والتسعون: الافتخار بالصنائع، كفعل أهل الرحلتين على أهل الحرث</span>.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (100,'<span data-type="title" id=toc-101>التاسعة والتسعون: عظمة الدنيا في قلوبهم</span>، كقولهم: {لولا نزل هذا القرآن على رجل من القريتين عظيم} [الزخرف: ٣١] .',NULL,'22',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (101,'<span data-type="title" id=toc-102>المائة: التحكم على الله</span>
INSERT INTO [b8137] VALUES (102,'<span data-type="title" id=toc-103>الحادية بعد المائة: ازدراء الفقراء</span>، فأتاهم الله بقوله: {ولا تطرد الذين يدعون ربهم بالغداة والعشي} [الأنعام:٥٢] .',NULL,'23',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (103,'<span data-type="title" id=toc-104>الثانية بعد المائة: رميهم أتباع الرسل بعدم الإخلاص وطلب الدنيا</span>، فأجابهم بقوله: {ما عليك من حسابهم من شيء} الآية [الأنعام: ٥٢] وأمثالها.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (104,'<span data-type="title" id=toc-105>الثالثة بعد المائة: الكفر بالملائكة</span>.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (105,'<span data-type="title" id=toc-106>الرابعة بعد المائة: الكفر بالرسل</span>.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (106,'<span data-type="title" id=toc-107>الخامسة بعد المائة: الكفر بالكتب</span>.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (107,'<span data-type="title" id=toc-108>السادسة بعد المائة: الإعراض عما جاء عن الله</span>.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (108,'<span data-type="title" id=toc-109>السابعة بعد المائة: الكفر باليوم الآخر</span>.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (109,'<span data-type="title" id=toc-110>الثامنة بعد المائة: التكذيب بلقاء الله</span>.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (110,'<span data-type="title" id=toc-111>التاسعة بعد المائة: التكذيب ببعض ما أخبرت به الرسل عن اليوم الآخر</span>، كما في قوله: {أولئك الذين كفروا بآيات ربهم ولقائه} [الكهف: ١٠٥] ومنها التكذيب بقوله: {مالك يوم الدين} [الفاتحة: ٢] وقوله: {لا بيع فيه ولا خلة ولا شفاعة} [البقرة: ٢٥٤] وقوله: {إلا من شهد بالحق وهم يعلمون} [الزخرف: ٦٨] .',NULL,'23',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (111,'<span data-type="title" id=toc-112>العاشرة بعد المائة: الإيمان بالجبت والطاغوت</span>.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (112,'<span data-type="title" id=toc-113>الحادية عشر بعد المائة: تفضيل دين المشركين على دين المسلمين</span>.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (113,'<span data-type="title" id=toc-114>الثانية عشر بعد المائة: لبس الحق بالباطل</span>.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (114,'<span data-type="title" id=toc-115>الثالثة عشر بعد المائة: كتمان الحق مع العلم به</span>.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (115,'<span data-type="title" id=toc-116>الرابعة عشر بعد المائة: قاعدة الضلال، وهي القول على الله بلا علم</span>.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (116,'<span data-type="title" id=toc-117>الخامسة عشر بعد المائة: التناقض الواضح لما كذبوا الحق</span>، كما قال تعالى: {بل كذبوا بالحق لما جاءهم فهم في أمر مريج} [ق: ٥] .',NULL,'24',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (117,'<span data-type="title" id=toc-118>السادسة عشر بعد المائة: الإيمان ببعض المنزل دون بعض</span>.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (118,'<span data-type="title" id=toc-119>السابعة عشر بعد المائة: التفريق بين الرسل</span>.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (119,'<span data-type="title" id=toc-120>الثامنة عشر بعد المائة: مخالفتهم فيما ليس لهم به علم</span>.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (120,'<span data-type="title" id=toc-121>التاسعة عشر بعد المائة: دعواهم إتباع السلف مع التصريح بما خالفتهم</span>.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (121,'<span data-type="title" id=toc-122>العشرون بعد المائة: صدهم عن سبيل الله من آمن به</span>.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (122,'<span data-type="title" id=toc-123>الحادية والعشرون بعد المائة: مودتهم الكفر والكافرين</span>.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b8137] VALUES (123,'<span data-type="title" id=toc-124>الثانية والعشرون بعد المائة إلى الواحدة والثلاثون بعد المائة</span>
