INSERT INTO [b8180] VALUES (92,'لوحة رقم - ١٠ قطعة من الزاوية اليمنى من (ص ٣٦ من الاصل) لمقارنة خطها بخط اللوحة رقم - ١١ المصورة عن ورقة من البردي لوحة رقم - ١١ الصورة رقم (٥١) من اللوحة (رقم ٧) من الجزء الاول من (كتاب الاوراق البردية) وهي قطعة من مكتوب مؤرخ سنة ١٩٥','المقدمة','111',NULL,NULL,'0');
