INSERT INTO [b836] VALUES (1,'<span data-type="title" id=toc-2>١ </span>- تبتدئ باسم الله الذى لا معبود بحق سواه، والمتصف بكل كمال، المنزه عن كل نقص، وهو صاحب الرحمة الذى يفيض بالنعم جليلها ودقيقها، عامها وخاصها، وهو المتصف بصفة الرحمة الدائمة.',NULL,'1',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2,'<span data-type="title" id=toc-3>٢ </span>- الثناء الجميل بكل أنواعه وعلى كل حال لله وحده، ونثنى عليه الثناء كله لأنه منشئ المخلوقات والقائم عليها.',NULL,'1',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3,'<span data-type="title" id=toc-4>٣ </span>- وهو صاحب الرحمة الدائمة ومصدرها، ينعم بكل النعم صغيرها وكبيرها.',NULL,'1',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4,'<span data-type="title" id=toc-5>٤ </span>- وهو وحده المالك ليوم الجزاء والحساب وهو يوم القيامة، يتصرف فيه لا يشاركه أحد فى التصرف ولو فى الظاهر.',NULL,'1',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5,'<span data-type="title" id=toc-6>٥ </span>- لا نعبد إلا إياك، ولا نطلب المعونة إلا منك.',NULL,'1',NULL,NULL,'0');
INSERT INTO [b836] VALUES (6,'<span data-type="title" id=toc-7>٦ </span>- نسألك أن توفقنا إلى طريق الحق والخير والسعادة.',NULL,'1',NULL,NULL,'0');
INSERT INTO [b836] VALUES (7,'<span data-type="title" id=toc-8>٧ </span>- وهو طريق عبادك الذين وفقتهم إلى الإيمان بك، ووهبت لهم نعمتى الهداية والرضا، لا طريق الذين استحقوا غضبك وضلوا عن طريق الحق والخير لأنهم أعرضوا عن الإيمان بك والإذعان لهديك.',NULL,'1',NULL,NULL,'0');
INSERT INTO [b836] VALUES (8,'<span data-type="title" id=toc-10>١ </span>- ألف لام ميم: هذه حروف ابتدأ الله سبحانه وتعالى بها ليشير بها إلى إعجاز القرآن الكريم المؤلف من حروف كالحروف التى يؤلِّف منها العرب كلامهم، ومع ذلك عجزوا عن الإتيان بمثل القرآن، وهى مع ذلك تنطوى على التنبيه للاستماع لتميز جرسها.',NULL,'3',NULL,NULL,'0');
INSERT INTO [b836] VALUES (9,'<span data-type="title" id=toc-11>٢ </span>- هذا هو الكتاب الكامل وهو القرآن الذى ننزله لا يرتاب عاقل منصف فى كونه من عند الله، ولا فى صدق ما اشتمل عليه من حقائق وأحكام، وفيه الهداية الكاملة للذين يستعدون لطلب الحق، ويتوقُّون الضرر وأسباب العقاب.',NULL,'3',NULL,NULL,'0');
INSERT INTO [b836] VALUES (10,'<span data-type="title" id=toc-12>٣ </span>- وهؤلاء هم الذين يصدقون - فى حزم وإذعان - بما غاب عنهم، ويعتقدون فيما وراء المحسوس كالملائكة واليوم الآخر، لأن أساس التدين هو الإيمان بالغيب، ويؤدون الصلاة مستقيمة بتوجه إلى الله وخشوع حقيقى له، والذين ينفقون جانبا مما يرزقهم الله به فى وجوه الخير والبر.',NULL,'3',NULL,NULL,'0');
INSERT INTO [b836] VALUES (11,'<span data-type="title" id=toc-13>٤ </span>- والذين يصدقون بالقرآن المنزل عليك من الله، وبما فيه من أحكام وأخبار، ويعملون بمقتضاه، ويصدقون بالكتب الإلهية التى نزلت على من سبقك من الأنبياء والرسل كالتوراة والإنجيل وغيرهما، لأن رسالات الله واحدة فى أصولها، ويتميزون بأنهم يعتقدون اعتقاداً جازماً بمجئ يوم القيامة وبما فيه من حساب وثواب وعقاب.',NULL,'3',NULL,NULL,'0');
INSERT INTO [b836] VALUES (12,'<span data-type="title" id=toc-14>٥ </span>- هؤلاء الموصوفون بما سبق من صفات، متمكنون من أسباب الهداية الإلهية، مستقرون عليها، أولئك هم وحدهم الفائزون بمطلوبهم ومرغوبهم ثواباً لسعيهم واجتهادهم وامتثالهم الأوامر واجتنابهم النواهى.',NULL,'4',NULL,NULL,'0');
INSERT INTO [b836] VALUES (13,'<span data-type="title" id=toc-15>٦ </span>- هذا شأن المهتدين، أما الجاهلون الذين فقدوا الاستعداد للإيمان إعراضاً منهم وعناداً، فلن يستجيبوا لله، فيستوى عندهم تخويفك لهم وعدم تخويفك.',NULL,'4',NULL,NULL,'0');
INSERT INTO [b836] VALUES (14,'<span data-type="title" id=toc-16>٧ </span>- هؤلاء قد تمكن الكفر منهم حتى كأن قلوبهم مختوم عليها بحجاب لا يدخلها غير ما فيها، وكأن أسماعهم مختوم عليها كذلك، فلا تسمع وعده الحق، وكأن أبصارهم قد غشيها غطاء فهى لا تدرك آيات الله الدالة على الإيمان، ولذلك استحقوا أن ينالهم العذاب الشديد.',NULL,'4',NULL,NULL,'0');
INSERT INTO [b836] VALUES (15,'<span data-type="title" id=toc-17>٨ </span>- ومن الكافرين قوم آخرون من الناس يقولون بألسنتهم ما ليس فى قلوبهم، يظهرون الإيمان فيقولون: إننا آمنا بالله وبيوم القيامة، وليسوا بصادقين فى قولهم، فلا يدخلون فى جماعة المؤمنين.',NULL,'4',NULL,NULL,'0');
INSERT INTO [b836] VALUES (16,'<span data-type="title" id=toc-18>٩ </span>- إنهم يخدعون المؤمنين بما يصنعون، ويظنون أنهم يخادعون الله، إذ يتوهمون أنه غير مطلع على خفاياهم، مع أنه يعلم السر والنجوى، وهم فى الواقع يخدعون أنفسهم لأن ضرر عملهم لا حق بهم، عاجلاً وآجلاً، ولأن من يخدع غيره ويحسبه جاهلاً - وهو ليس كذلك - إنما يخدع نفسه.',NULL,'4',NULL,NULL,'0');
INSERT INTO [b836] VALUES (17,'<span data-type="title" id=toc-19>١٠ </span>- هؤلاء فى قلوبهم مرض الحسد والحقد على أهل الإيمان مع فساد العقيدة، وزادهم الله على مرضهم مرضاً بنصره للحق، إذ كان ذلك مؤذياً لهم بسبب حسدهم وحقدهم وعنادهم، ولهؤلاء عذاب أليم فى الدنيا والآخرة بسبب كذبهم وجحودهم.',NULL,'4',NULL,NULL,'0');
INSERT INTO [b836] VALUES (18,'<span data-type="title" id=toc-20>١١ </span>- وإذا قال أحد من المهتدين لهؤلاء المنافقين: لا تفسدوا فى الأرض بالصدِّ عن سبيل الله، ونشر الفتنة وإيقاد نار الحرب برَّأوا أنفسهم من الفساد، وقالوا: ما نحن إلا مصلحون. وذلك لفرط غرورهم، وهذا شأن كل مفسد خبيث مغرور يزعم فساده إصلاحاً.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b836] VALUES (19,'<span data-type="title" id=toc-21>١٢ </span>- ألا فتنبهوا أيها المؤمنون إلى أنهم هم أهل الفساد حقاً، ولكنهم لا يشعرون بفسادهم لغرورهم، ولا بسوء العاقبة التى ستصيبهم بسبب هذا النفاق.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b836] VALUES (20,'<span data-type="title" id=toc-22>١٣ </span>- وإذا قال قائل لهم ينصحهم ويرشدهم: أقبلوا على ما يجب، وهو أن تؤمنوا إيماناً مخلصاً مثل إيمان الناس الكاملين المستجيبين لصوت العقل؛ سخروا وتهكَّموا وقالوا: لا يليق بنا أن نتبع هؤلاء الجهلاء ضعاف العقول. فرد الله عليهم تطاولهم وحكم عليهم بأنهم - وحدهم - الجهلاء الحمقى. ولكنهم لا يعلمون علماً يقيناً أن الجهل ونقص الإدراك محصور فيهم مقصور عليهم.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b836] VALUES (21,'<span data-type="title" id=toc-23>١٤ </span>- وإذا لقى هؤلاء المنافقون المؤمنين المخلصين قالوا: آمنّا بما أنتم به مؤمنون من صدق الرسول ودعوته، ونحن معكم فى الاعتقاد، وإذا انصرفوا عنهم واجتمعوا بأصحابهم الذين يشبهون الشياطين فى الفتنة والفساد قالوا لهم: إنا معكم على طريقتكم وعملكم، وإنما كان قولنا للمؤمنين ما قلنا: استخفافاً بهم واستهزاء.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b836] VALUES (22,'<span data-type="title" id=toc-24>١٥ </span>- والله سبحانه يجازيهم على استهزائهم، ويكتب عليهم الهوان الموجب للسخرية والاحتقار، فيعاملهم بذلك معاملة المستهزئ، ويمهلهم فى ظلمهم الفاحش الذى يجعلهم فى عمى عن الحق، ثم يأخذهم بعذابه.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b836] VALUES (23,'<span data-type="title" id=toc-25>١٦ </span>- وهؤلاء إذ اختاروا الضلالة بدل الهداية كانوا كالتاجر الذى يختار لتجارته البضاعة الفاسدة الكاسدة فلا يربح فى تجارته، ويضيع رأس ماله، وهم فى عملهم غير مهتدين.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b836] VALUES (24,'<span data-type="title" id=toc-26>١٧ </span>- حال هؤلاء فى نفاقهم كحال من أوقد ناراً لينتفع بها مع قومه، فلما أنارت ما حوله من الأشياء ذهب الله بنورهم وترك موقديها فى ظلمات كثيفة لا يبصرون معها شيئاً، لأن الله قدَّم إليهم أسباب الهداية فلم يتمسكوا بها فصارت بصائرهم مطموسة، فاستحقوا أن يبقوا فى الحيرة والضلال.',NULL,'6',NULL,NULL,'0');
INSERT INTO [b836] VALUES (25,'<span data-type="title" id=toc-27>١٨ </span>- هؤلاء كالصُّمِّ، لأنهم قد فقدوا منفعة السمع، إذ لا يسمعون الحق سماع قبول واستجابة، وهم كالبُكْم الخُرس؛ لأنهم لا ينطقون بالهدى أو الحق، وهم كالذين فقدوا أبصارهم لأنهم لا ينتفعون بها فى اعتبار أو انزجار، فهم لا يرجعون عن ضلالتهم.',NULL,'6',NULL,NULL,'0');
INSERT INTO [b836] VALUES (26,'<span data-type="title" id=toc-28>١٩ </span>- أو حالهم فى حيرتهم وشدة الأمر عليهم وعدم إدراكهم لما ينفعهم ويضرهم، كحال قوم نزل عليهم مطر من السماء ورعد وصواعق، يضعون أطراف أصابعهم فى آذانهم كى لا يسمعوا أصوات الصواعق خائفين من الموت، زاعمين أن وضع الأصابع يمنعهم منه.
INSERT INTO [b836] VALUES (27,'<span data-type="title" id=toc-29>٢٠ </span>- إن هذا البرق الشديد يكاد يخطف منهم أبصارهم لشدته، وهو يضئ لهم الطريق حيناً فيسيرون خطوات مستعينين بضوئه، فإذا انقطع البرق واشتد الظلام يقفون متحيرين ضالين، وهؤلاء المنافقون تلوح لهم الدلائل والآيات فتبهرهم أضواؤها فيهمون أن يهتدوا، ولكنهم بعد قليل يعودون إلى الكفر والنفاق. إن الله واسع القدرة إذا أراد شيئاً فعله، لا يعجزه شئ فى الأرض ولا فى السماء.',NULL,'6',NULL,NULL,'0');
INSERT INTO [b836] VALUES (28,'<span data-type="title" id=toc-30>٢١ </span>- يا أيها الناس اعبدوا ربكم الذى أنشأكم وخلقكم ونماكم كما خلق الذين سبقوكم، فهو خالق كل شئ، لعلكم بذلك تعدون أنفسكم وتهيئونها لتعظيم الله ومراقبته، فتتطهر بذلك نفوسكم وتذعن للحق، وتخاف سوء العاقبة.',NULL,'6',NULL,NULL,'0');
INSERT INTO [b836] VALUES (29,'<span data-type="title" id=toc-31>٢٢ </span>- إنه وحده هو الذى مهد لكم الأرض بقدرته، وبسط رقعتها ليسهل عليكم الإقامة فيها والانتفاع بها، وجعل ما فوقكم من السماء وأجرامها وكواكبها كالبنيان المشيد، وأمدكم بسبب الحياة والنعمة - وهو الماء - أنزله عليكم من السماء فجعله سبباً لإخراج النباتات والأشجار المثمرة التى رزقكم بفوائدها، فلا يصح مع هذا أن تتصوروا أن لله نظراء تعبدونهم كعبادته لأنه ليس له مثيل ولا شريك، وأنتم بفطرتكم الأصلية تعلمون أنه لا مثيل له ولا شريك، فلا تحرفوا هذه الطبيعة.',NULL,'7',NULL,NULL,'0');
INSERT INTO [b836] VALUES (30,'<span data-type="title" id=toc-32>٢٣ </span>- وإن كنتم فى ريب من صدق هذا القرآن الذى تتابع إنزالنا له على عبدنا محمد، فحاولوا أن تأتوا بسورة مماثلة من سور هذا القرآن فى بلاغتها وأحكامها وعلومها وسائر هدايتها، ونادوا الذين يشهدون لكم أنكم أتيتم بسورة مماثلة له فاستعينوا بهم ولن تجدوهم، وهؤلاء الشهداء هم غير الله، لأن الله يؤيد عبده بكتابه، ويشهد له بأفعاله، هذا إن كنتم صادقين فى ارتيابكم فى هذا القرآن.',NULL,'7',NULL,NULL,'0');
INSERT INTO [b836] VALUES (31,'<span data-type="title" id=toc-33>٢٤ </span>- فإن لم تستطيعوا الإتيان بسورة مماثلة لسور القرآن - ولن تستطيعوا ذلك بحال من الأحوال - لأنه فوق طاقة البشر، إذ القرآن كلام الخالق فالواجب عليكم أن تتجنبوا الأسباب التى تؤدى بكم إلى عذاب النار فى الآخرة، التى سيكون وقودها وحطبها من الكافرين ومن الأصنام، ولقد هيئت هذه النار لتعذيب الجاحدين المعاندين.',NULL,'7',NULL,NULL,'0');
INSERT INTO [b836] VALUES (32,'<span data-type="title" id=toc-34>٢٥ </span>- وإذا كان هذا عقاب الفجار الجاحدين، فالجنة مثوى المؤمنين، فأخبر الذين صدَّقوا بالله ورسوله وكتابه، وأذعنوا للحق دون شك أو ارتياب، وعملوا الأعمال الصالحة الطيبة - أخبرهم بخبر يسرهم ويشرح صدورهم، وهو أن الله أعد لهم عنده جنات مثمرة تتخللها الأنهار الجارية تحت أشجارها وقصورها، كلما رزقهم الله وهم فى هذه الجنات - رزقاً من بعض ثمارها قالوا: إن هذا يشبه ما رزقنا من قبل، لأن هذه الثمرات التى ينالونها تشابه أفرادها فى الصورة والجنس ولكنها تتمايز فى الطعم واللذة، ولهم فيها أيضاً زوجات كاملات الطهارة ليس فيهن ما يعاب. وسيبقون فى هذه الجنة فى حياة أبدية لا يخرجون منها.',NULL,'8',NULL,NULL,'0');
INSERT INTO [b836] VALUES (33,'<span data-type="title" id=toc-35>٢٦ </span>- يضرب الله الأمثال للناس لبيان الحقائق العالية، ويضرب بصغائر الأحياء، وكبار الأشياء، وقد عاب من لا يؤمنون ضرب المثل بصغائر الأحياء كالذباب والعنكبوت، فبين الله سبحانه أنه لا يعتريه ما يعترى الناس من الاستحياء، فلا يمنع أن يصور لعباده ما يشاء من أمور بأى مثل مهما كان صغيراً، فيصح أن يجعل المثل بعوضة أو ما فوقها، والذين آمنوا يعلمون وجه التمثيل وأن هذا حق من الله، والذين كفروا يتلقونه بالاستنكار ويقولون: ما الذى أراده الله بهذا المثل؟ وأن هذا المثل يكون سبباً لإضلال الذين لا يطلبون الحق ولا يريدونه، ويكون سبباً لهداية المؤمنين بالحق الذى يطلبونه، فلا يُضَلُّ به إلا المنحرفين المتمردين.',NULL,'8',NULL,NULL,'0');
INSERT INTO [b836] VALUES (34,'<span data-type="title" id=toc-36>٢٧ </span>- الذين ينقضون عهد الله - وهم الذين لم يلتزموا عهد الله القوى الذى أنشأه فى نفوسهم بمقتضى الفطرة موثقاً بالعقل المدرك ومؤيداً بالرسالة - ويقطعون ما أمر الله به أن يكون موصولاً كوصل ذوى الأرحام، والتواد والتعارف والتراحم بين بنى الإنسان، ويفسدون فى الأرض بسوء المعاملات وبإثارة الفتن وإيقاد الحروب وإفساد العمران، أولئك هم الذين يخسرون بإفسادهم فطرتهم وقطعهم ما بينهم وبين الناس ما يجب أن يكون من تواد وتعاطف وتراحم، ويكون مع ذلك لهم الخزى فى الدنيا والعذاب فى الآخرة.',NULL,'8',NULL,NULL,'0');
INSERT INTO [b836] VALUES (35,'<span data-type="title" id=toc-37>٢٨ </span>- إن حالكم تثير العجب! كيف تكفرون ولا توجد شبهة تعتمدون عليها فى كفركم؟ ونظرة إلى حالكم تأبى هذا الكفر ولا تدع لكم عذراً فيه، فقد كنتم أمواتاً فخلقكم الله ووهبكم الحياة وحسن التقويم، ثم هو الذى يعيدكم أمواتاً عند انتهاء أجلكم، ثم يبعثكم أحياء مرة أخرى للحساب والعقاب ثم إليه - لا إلى غيره - تعودون فيحاسبكم ويجازيكم على أعمالكم.',NULL,'9',NULL,NULL,'0');
INSERT INTO [b836] VALUES (36,'<span data-type="title" id=toc-38>٢٩ </span>- وإن الله الذى تجب عبادته وإطاعته هو الذى تفضل عليكم فخلق لمنفعتكم وفائدتكم كل النعم الموجودة فى الأرض، ثم قد توجهت إرادته مع خلقه الأرض بمنافعها إلى السماء فجعل منها سبع سموات منتظمات فيها ما ترون وما لا ترون، والله محيط بكل شئ عالم به.',NULL,'9',NULL,NULL,'0');
INSERT INTO [b836] VALUES (37,'<span data-type="title" id=toc-39>٣٠ </span>- بيَّن - سبحانه - أنه هو الذى أحيا الإنسان ومكَّن له فى الأرض، ثم بيَّن بعد ذلك أصل تكوين الإنسان وما أودع فيه من علم الأشياء وذكره به، فاذكر يا محمد نعمة أخرى من نعم ربك على الإنسان، وهى أنه قال لملائكته: إنى جاعل فى الأرض من أُمكِّنه منها وأجعله صاحبَ سلطان فيها وهو آدم وذريته، استخلفهم الله فى عمارة الأرض.
INSERT INTO [b836] VALUES (38,'<span data-type="title" id=toc-40>٣١ </span>- وبعد أن خلق الله آدم وعلَّمه أسماء الأشياء وخواصَّها ليتمكن فى الأرض وينتفع بها، عرض الله هذه الأشياء على الملائكة وقال لهم: أخبرونى بأسماء هذه الأشياء وخواصها إن كنتم صدقتم فى ظنكم أنكم أحق بخلافة الأرض ولا يوجد أفضل منكم بسبب طاعتكم وعبادتكم.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b836] VALUES (39,'<span data-type="title" id=toc-41>٣٢ </span>- وقد ظهر للملائكة عجزهم فقالوا: إننا ننزهك يا ربَّنا التنزيه اللائق بك، ونقر بعجزنا وعدم اعتراضنا، فلا علم عندنا إلا ما وهبتنا إياه، وأنت العالم بكل شئ، الحكيم فى كل أمر تفعله.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b836] VALUES (40,'<span data-type="title" id=toc-42>٣٣ </span>- قال الله لآدم: أَخْبر الملائكة يا آدم بهذه الأشياء، فأجاب وأظهر فضله عليهم، وهنا قال الله لهم مذكراً لهم بإحاطة علمه: ألم أقل لكم إنى أعلم كل ما غاب فى السموات والأرض ولا يعلمه غيرى، وأعلم ما تُظهرون فى قولكم وما تُخفون فى نفوسكم؟ .',NULL,'10',NULL,NULL,'0');
INSERT INTO [b836] VALUES (41,'<span data-type="title" id=toc-43>٣٤ </span>- واذكر - يا أيها النبى - حين قلنا للملائكة: اخضعوا لآدم تحية له وإقراراً بفضله، فأطاع الملائكة كلهم إلا إبليس، امتنع عن السجود وصار من العاصين له والكافرين بنعم الله وحكمته وعلمه.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b836] VALUES (42,'<span data-type="title" id=toc-44>٣٥ </span>- ثم أمر الله آدم وزوجه أن يعيشا فى جنة النعيم فقال له: اسكن أنت وامرأتك الجنة وكلا منها ما تشاءان أكلاً هنيئاً وافراً من أى مكان ومن أى ثمر تريدان، ولكن الله ذكر لهما شجرة معينة وحذرهما الأكل منها وقال لهما: لا تدنُوَا من هذه الشجرة ولا تأكلا منها، وإلا كنتما من الظالمين العاصين.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b836] VALUES (43,'<span data-type="title" id=toc-45>٣٦ </span>- ولكن إبليس الحاسد لآدم والحاقد عليه أخذ يحتال عليهما ويغريهما بالأكل من الشجرة حتى زلاّ فأكلا منها، فأخرجهما الله مما كانا فيه من النعيم والتكريم، وأمرهما الله تعالى بالنزول إلى الأرض ليعيشا هما وذريتهما فيها، ويكون بعضهم لبعض عدواً بسبب المنافسة وإغواء الشيطان، ولكم فى الأرض مكان استقرار وتيسير للمعيشة، وتمتع ينتهى بانتهاء الأجل.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b836] VALUES (44,'<span data-type="title" id=toc-46>٣٧ </span>- وأحس آدم هو وزوجته بخطئهما وظلمهما لأنفسهما، فألهم الله تعالى آدم كلمات يقولها للتوبة والاستغفار، فقالها، فتقبَّل الله منه وغفر له لأنه كثير القبول للتوبة، وهو الرحيم بعباده الضعفاء.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b836] VALUES (45,'<span data-type="title" id=toc-47>٣٨ </span>- وقلنا لآدم وزوجته ومن سيكون من ذريته وإبليس: اهبطوا إلى الأرض وستكلفون تكليفات فيها، فإن جاءكم ذلك من عندى - وسيأتيكم حتماً - فالذين يستجيبون لأمرى ويتبعون هداى لا يشعرون بخوف، ولا يصيبهم حزن لفوات ثواب، لأن الله لا يضيع أجر من أحسن عملا.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b836] VALUES (46,'<span data-type="title" id=toc-48>٣٩ </span>- والذين جحدوا وكذبوا برسل الله وكتبه، أولئك أهل النار، يظلون فيها أبدا لا يخرجون ولا يفنون.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b836] VALUES (47,'<span data-type="title" id=toc-49>٤٠ </span>- يا بنى إسرائيل اذكروا نعمتى التى تفضلت بها عليكم أنتم وآباؤكم بالتفكير فيها والقيام بواجب شكرها، وأوفوا بعهدى الذى أخذته عليكم وأقررتموه على أنفسكم، وهو الإيمان، والعمل الصالح، والتصديق بمن يجيئ بعد موسى من الأنبياء، حتى أوفى بوعدى لكم وهو حسن الثواب والنعيم المقيم، ولا تخافوا أحداً غيرى، واحذروا من أسباب غضبى عليكم.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b836] VALUES (48,'<span data-type="title" id=toc-50>٤١ </span>- وصدِّقوا بالقرآن الذى أنزلت مصدقا لما عندكم من كتاب وعلم من التوحيد وعبادة الله، والعدل بين الناس، ولا تسارعوا إلى جحود القرآن فتكونوا أول الكافرين به من حيث ينبغى أن تكونوا أول المؤمنين به، ولا تتركوا آيات الله لتأخذوا عن ذلك عوضاً قليلاً زائلاً من متاع الحياة الدنيا، وخُصّونى بالخوف فاتبعوا طريقى، وأعرضوا عن الباطل.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b836] VALUES (49,'<span data-type="title" id=toc-51>٤٢ </span>- ولا تخلطوا الحق المُنزَّل من عندى بالباطل المفترى من عندكم، حتى لا يشتبه هذا بذاك، ولا تكتموا الحق ومنه صِدْق محمد، وأنتم تعلمون أنه حق وصدق.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b836] VALUES (50,'<span data-type="title" id=toc-52>٤٣ </span>- واستجيبوا للإيمان. فأدُّوا الصلاة مستقيمة الأركان، وأعطوا الزكاة لمستحقيها، وصلوا مع جماعة المسلمين لتنالوا ثواب الصلاة وثواب الجماعة، وهذا يستلزم أن تكونوا مسلمين.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b836] VALUES (51,'<span data-type="title" id=toc-53>٤٤ </span>- أتطلبون من الناس أن يتوسعوا فى الخير، وأن يلتزموا الطاعة ويتجنبوا المعصية، ثم لا تعملون بما تقولون، ولا تلتزمون بما تطلبون؟ ، وفى ذلك تضييع لأنفسكم كأنكم تنسونها، مع أنكم تقرءون التوراة وفيها التهديد والوعيد على مخالفة القول للعمل، أليس لديكم عقل يردعكم عن هذا التصرف الذميم؟',NULL,'12',NULL,NULL,'0');
INSERT INTO [b836] VALUES (52,'<span data-type="title" id=toc-54>٤٥ </span>- واستعينوا على أداء التكليفات بالصبر وحبس النفس على ما تكره، ومن ذلك الصوم، وبالصلاة العظيمة الشأن التى تنقى القلب وتنهى عن الفحشاء والمنكر، ولذلك كانت ثقيلة شاقة إلا على الخاضعين المحبين للطاعة، الذين اطمأنت قلوبهم لذكر الله.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b836] VALUES (53,'<span data-type="title" id=toc-55>٤٦ </span>- أولئك هم الخاضعون المطمئنة قلوبهم، الذين يؤمنون باليوم الآخر ويوقنون بأنهم سيلاقون ربهم عند البعث، وإليه - وحده - يعودون ليحاسبهم على ما قدمت أيديهم ويثيبهم عليه.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b836] VALUES (54,'<span data-type="title" id=toc-56>٤٧ </span>- يا بنى إسرائيل اذكروا نعمتى التى أنعمت بها عليكم، من إخراجكم من ظلم فرعون وهدايتكم وتمكينكم فى الأرض بعد أن كنتم مستضعفين فيها، واشكروا واهبها بطاعتكم له، واذكروا أننى أعطيت آباءكم الذين انحدرتم منهم ما لم أعطه أحداً من معاصريكم، والخطاب لجنس اليهود وموجه كذلك للمعاصرين للرسول صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b836] VALUES (55,'<span data-type="title" id=toc-57>٤٨ </span>- وخافوا يوم الحساب الشديد: يوم القيامة الذى لا تدفع فيه نفس عن نفس شيئاً، ولا تغنى فيه نفس عن نفس أخرى شيئاً، ولا يقبل من أى نفس تقديم أى شفيع، كما لا يقبل أى فداء تفدى به الذنوب، ولا يستطيع أحد أن يدفع العذاب عن مستحقيه.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b836] VALUES (56,'<span data-type="title" id=toc-58>٤٩ </span>- واذكروا من نعمنا عليكم أن نجَّيناكم من ظلم فرعون وأعوانه الذين كانوا يذيقونكم أشد العذاب، فهم يذبحون الذكور من أولادكم لتَوَهّم أن يكون منهم من يذهب بملك فرعون ويستبقون الإناث ليستخدموهن، وفى هذا العذاب والتعرض للفناء ابتلاءٌ شديد من ربكم واختبار عظيم لكم.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b836] VALUES (57,'<span data-type="title" id=toc-59>٥٠ </span>- واذكروا كذلك من نعم الله عليكم حين شققنا لكم ومن أجلكم البحر - وفصلنا ماءه بعضه عن بعض لتسيروا فيه - فتتخلصوا من ملاحقة فرعون وجنوده، وبفضلنا نجوتم، وانتقمنا لكم من عدوكم، فأغرقناهم أمام أبصاركم، فأنتم ترونهم وهم يغرقون والبحر ينطبق عليهم عقب خروجكم منه.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b836] VALUES (58,'<span data-type="title" id=toc-60>٥١ </span>- واذكروا حين واعد ربكم موسى أربعين ليلة لمناجاته، فلما ذهب إلى ميعاده وعاد، وجدكم قد انحرفتم واتخذتم العجل الذى صنعه السامرى معبوداً لكم، وكنتم ظالمين باتخاذكم العجل شريكاً لله الذى خلقكم ونجاكم.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b836] VALUES (59,'<span data-type="title" id=toc-61>٥٢ </span>- ثم عفونا عنكم ومحونا عقوبتكم حين تبتم واستغفرتم من إثمكم، لعلكم تشكرون ربكم على صفحه وعفوه وفضله.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b836] VALUES (60,'<span data-type="title" id=toc-62>٥٣ </span>- واذكروا نعمتنا عليكم إذ أنزلنا على نبيكم موسى كتابنا التوراة، وهو الذى يفرِّق بين الحق والباطل، ويميِّز الحلال من الحرام، لكى تسترشدوا بنورها وتهتدوا من الضلال بتدبر ما فيها.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b836] VALUES (61,'<span data-type="title" id=toc-63>٥٤ </span>- واذكروا يوم قال لكم رسولكم موسى: يا قوم، لقد ظلمتم أنفسكم باتخاذكم عجل السامرى معبوداً، فتوبوا إلى ربكم خالقكم من العدم، بأن تغضبوا على أنفسكم الشريرة الآمرة بالسوء وتذلوها، لتتجدد بنفوس مطهرة، فأعانكم الله على ذلك ووفقكم له وكان ذلك خيراً لكم عند خالقكم، ولهذا قَبِل توبتكم وعفا عنكم، فهو كثير التوبة على عباده، واسع الرحمة بهم.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b836] VALUES (62,'<span data-type="title" id=toc-64>٥٥ </span>- واذكروا قولكم لموسى: إننا لن نقر لك بالإيمان حتى نرى الله جهاراً عياناً بحاسة البصر لا يحجبه عنا شئ، فانقضَّت عليكم صاعقة ونار من السماء زلزلتكم جزاء عنادكم وظلمكم وطلبكم ما يستحيل وقوعه لكم، وأنتم تنظرون حالكم وما أصابكم من بلاء وعذاب فى الصاعقة.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b836] VALUES (63,'<span data-type="title" id=toc-65>٥٦ </span>- ثم أيقظناكم من غشيتكم وهمودكم، وعلمناكم لكى تشكروا نعمتنا فى ذلك، وتؤيدوا حق الله عن طريق هذا الشكر.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b836] VALUES (64,'<span data-type="title" id=toc-66>٥٧ </span>- ومن فضلنا عليكم أننا جعلنا السحاب لكم كالظلّة ليصونكم من الحر الشديد، وأنزلنا عليكم المنَّ، وهو مادة حلوة لزجة كالعسل تسقط على الشجر من طلوع الشمس، كما أنزلنا عليكم السلوى وهو الطائر المعروف بالسمان، فهو يأتيكم بأسرابه بكرة وعشيا لتأكلوا وتتمتعوا، وقلنا لكم: كلوا من طيبات رزقنا. فكفرتم بالنعمة، ولم يكن ذلك بضائرنا، ولكنكم تظلمون أنفسكم لأن ضرر العصيان واقع عليكم.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b836] VALUES (65,'<span data-type="title" id=toc-67>٥٨ </span>- واذكروا - يا بنى إسرائيل - حين قلنا لكم: ادخلوا المدينة الكبيرة التى ذكرها لكم موسى نبيكم، فكلوا مما فيها كما تشاءون، كثيراً واسعاً، على أن يكون دخولكم بخشوع وخضوع من الباب الذى سمَّاه لكم نبيكم، واسألوا الله عند ذلك أن يغفر لكم خطاياكم، فمن يفعل ذلك بإخلاص نغفر له خطاياه، ومن كان محسناً مطيعاً زدناه ثواباً وتكريماً فوق العفو والمغفرة.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b836] VALUES (66,'<span data-type="title" id=toc-68>٥٩ </span>- ولكن الذين ظلموا خالفوا أمر ربهم، فقالوا غير ما أمرهم بقوله، استهزاء وتمردا، فكان الجزاء أن أنزل الله على الظالمين عذاباً من فوقهم جزاء فسقهم وخروجهم على أوامر ربهم.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b836] VALUES (67,'<span data-type="title" id=toc-69>٦٠ </span>- واذكروا - يا بنى إسرائيل - يوم طلب نبيكم موسى السقيا لكم من ربه حين اشتد بكم العطش فى التيه، فرحمناكم وقلنا لموسى: اضرب بعصاك الحجر. فانفجر الماء من اثنتى عشرة عيناً، فصار لكل جماعة عين - وكانوا اثنتى عشرة جماعة - فعرفت كل قبيلة مكان شربها، وقلنا لكم: كلوا من المن والسلوى، واشربوا من هذا الماء المتفجر ودعوا ما أنتم عليه، ولا تسرفوا فى الإفساد فى الأرض بل امتنعوا عن المعاصى.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b836] VALUES (68,'<span data-type="title" id=toc-70>٦١ </span>- واذكروا - أيها اليهود - أيضاً يوم سيطر البطر على أسلافكم، ولم يؤدوا لنعمة الله حقها فقالوا لموسى: إننا لن نصبر على طعام واحد (وهو المن والسلوى) فادع لنا ربك كى يخرج لنا مما تنبت الأرض من بقولها وقثائها وعدسها وثومها وبصلها، فتعجب موسى من ذلك، وأنكره عليهم فقال لهم: أتفضلون هذه الأصناف على ما هو أفضل وأحسن، وهو المن والسلوى؟ . . فانزلوا إذن من سيناء وادخلوا مدينة من المدن فإنكم ستجدون فيها ما تريدون، وبسبب ذلك البطر والعناد أحاطت بهؤلاء اليهود المذلة والفقر والخنوع، واستحقوا غضب الله عليهم لما ألفوه من العناد والعصيان، وما جروا عليه من الكفر بآيات الله وبقتلهم الأنبياء مخالفين بذلك الحق الثابت المقرر، وقد جرأهم على ذلك - الكفر وهذا القتل - ما رُكِّب فى نفوسهم من التمرد والعدوان ومجاوزة الحد فى المعاصى.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b836] VALUES (69,'<span data-type="title" id=toc-71>٦٢ </span>- إن الذين آمنوا من الأنبياء من قبل، واليهود والنصارى، ومن يقدسون الكواكب والملائكة، من آمن برسالة محمد بعد بعثته، ووحَّد الله تعالى وآمن بالبعث والحساب يوم القيامة، وعمل الأعمال الصالحة فى دنياه، فهؤلاء لهم ثوابهم المحفوظ عند ربهم، ولا يلحقهم خوف من عقاب. ولا ينالهم حزن على فوات ثواب، والله لا يضيع أجر من أحسن عملا.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b836] VALUES (70,'<span data-type="title" id=toc-72>٦٣ </span>- اذكروا حين أخذنا عليكم العهد والميثاق رافعين جبل الطور، وجعلناه بقدرتنا كالظلة فوقكم حتى خفتم وأذعنتم وقلنا لكم: خذوا ما آتيناكم من هدى وإرشاد بجد واجتهاد، واذكروا ما فيه ذكر من يستجيب له ويعمل به كى تصونوا بذلك أنفسكم من العقاب.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b836] VALUES (71,'<span data-type="title" id=toc-73>٦٤ </span>- ثم إنكم أعرضتم بعد ذلك كله، ولولا فضل الله عليكم ورحمته وتأخيره العذاب عنكم لكنتم من الضالين الهالكين.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b836] VALUES (72,'<span data-type="title" id=toc-74>٦٥ </span>- وأنتم بلا ريب قد عرفتم أولئك الذين تجاوزوا الحد منكم فى يوم السبت، بأن صادوا السمك فيه - مع أنه يوم راحة وعيد والعمل محرم فيه - فمسخ الله قلوب المخالفين، وصاروا كالقردة فى نزواتها وشهواتها، وجعلناهم مبعدين من رحمتنا ينفر الناس من مجالستهم ويشمئزون من مخالطتهم.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b836] VALUES (73,'<span data-type="title" id=toc-75>٦٦ </span>- وقد جعل الله هذه الحال التى آلوا إليها عبرة وتحذيرا لغيرهم من أن يفعلوا مثل فعلهم، جعلها عبرة لمعاصريهم ومن يأتى بعدهم، كما جعلناها موعظة للذين يتقون ربهم، لأنهم هم الذين ينتفعون بنذير العظات والعبر.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b836] VALUES (74,'<span data-type="title" id=toc-76>٦٧ </span>- واذكر - يا محمد - حين قال موسى لقومه وقد قُتل فيهم قتيل لم يعرفوا قاتله: إن الله يأمركم أن تذبحوا بقرة ليكون ذلك مفتاحاً لمعرفة القاتل، ولكنهم استغربوا أن تكون هناك صلة بين قتل القتيل وذَبح البقرة قائلين: أَتسخر منا يا موسى؟ ، فرد عليهم قائلاً: إنى أعتصم بتأديب الله لى أن أكون من الجاهلين الذين يستهزئون بعباده.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b836] VALUES (75,'<span data-type="title" id=toc-77>٦٨ </span>- هنا قالوا لموسى - مترددين فى أمر البقرة: اطلب لنا من ربك أن يبين لنا صفة تلك البقرة، فقال لهم: إن الله أخبرنى بأنها ليست كبيرة وليست صغيرة، بل هى وسط بين الكبر والصغر، فنفذوا ما أمركم الله به.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b836] VALUES (76,'<span data-type="title" id=toc-78>٦٩ </span>- ولكنهم استمروا فى ترددهم فقالوا: اطلب لنا من ربك أن يبين لنا لون هذه البقرة، فأجابهم موسى: بأن الله يقول: إنها بقرة صفراء شديدة الصفرة مع صفاء، تُعْجِبُ الناظر إليها لصفاء لونها ووضوحه.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b836] VALUES (77,'<span data-type="title" id=toc-79>٧٠ </span>- ثم لجوا فى أسئلتهم فقالوا: ادع لنا ربك يبين لنا شأن هذه البقرة، لأن البقر تشابه علينا، وسنهتدى إليها بمشيئة الله.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b836] VALUES (78,'<span data-type="title" id=toc-80>٧١ </span>- فقال لهم: إن الله يقول إنها بقرة لم تذلل بالعمل فى حرث الأرض وقلبها للزراعة، ولا فى سقى الأرض المهيأة للزراعة أو ما فيها من نبات، وهى بريئة من العيوب، سالمة من الآفات، لا لون فيها يخالف سائر جسدها، فقالوا له: الآن جئت بالبيان الواضح، وبحثوا عن البقرة المتصفة بهذه الأوصاف فذبحوها، وقد قاربوا ألا يفعلوا ذلك لكثرة أسئلتهم وطول لجاجهم.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b836] VALUES (79,'<span data-type="title" id=toc-81>٧٢ </span>- واذكروا يوم قتلتم نفسا وتخاصمتم وتدافعتم الجريمة، فاتَّهم بعضكم بعضا بقتلها، والله يعلم الحقيقة وهو كاشفها ومظهرها مع كتمانكم لها.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b836] VALUES (80,'<span data-type="title" id=toc-82>٧٣ </span>- فقلنا لكم على لسان موسى: اضربوا القتيل بجزء من هذه البقرة، ففعلتم: فأحيا الله القتيل وذكر اسم قاتله، ثم سقط ميتاً، وكانت معجزة من الله لموسى.
INSERT INTO [b836] VALUES (81,'<span data-type="title" id=toc-83>٧٤ </span>- ثم إنكم بعد هذه الآيات كلها لم تستجيبوا ولم تستقيموا، ولم تلن قلوبكم أو تخشع، بل غلظت وتصلبت وبقيت على قسوتها، بل إنها أشد قسوة من الحجارة، لأن الحجارة قد تتأثر وتنفعل، فهناك أحجار تتفجر منها المياه الكثيرة فتجرى أنهاراً، وهناك أحجار تتشقق فيخرج منها الماء عيوناً فوارة، ومنها ما يتأثر بقدرة الله وينقاد لمشيئته فيتردى من أعلى الجبال انقياداً لما أراده الله تعالى به، أما قلوبكم أيها اليهود فإنها لا تتأثر ولا تلين ولكم الويل على ذلك، فالله ليس بغافل عن أعمالكم، وهو سيؤدبكم بألوان النقم إذا لم تشكروا أنواع النعم.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b836] VALUES (82,'<span data-type="title" id=toc-84>٧٥ </span>- ما كان ينبغى لكم أيها المؤمنون أن تطمعوا فى أن يُؤمن اليهود بدينكم وينقادوا لكم وقد اجتمعت فى مختلف فرقهم أشتات الرذائل التى تباعد بينهم وبين الإيمان بالحق، فقد كان فريقا منهم - وهم الأحبار - يسمعون كلام الله فى التوراة ويفهمونه حق الفهم ثم يتعمدون تحريفه وهم يعلمون أنه الحق، وأن كتب الله المنزلة لا يجوز تغييرها.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b836] VALUES (83,'<span data-type="title" id=toc-85>٧٦ </span>- وكان فريق من منافقيهم إذا لقوا الذين آمنوا قالوا مخادعين لهم: آمنا بأنكم على الحق وأن محمداً هو النبى الذى جاء وصفه فى التوراة، وإذا خلا بعضهم إلى بعض عاتبهم الفريق الآخر على غفلتهم، إذ تنزلق ألسنتهم فى أثناء خداعهم للمؤمنين بعبارات تفيد خصومهم ولا يستدعيها الخداع، فيذكرون لهم ما ورد فى التوراة من أوصاف محمد ويعطونهم بذلك حُجة عليهم يوم القيامة.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b836] VALUES (84,'<span data-type="title" id=toc-86>٧٧ </span>- وهل غاب عن هؤلاء وأولئك أن الله ليس فى حاجة إلى مثل هذه الحُجة لأنه يعلم ما يخفون وما يبدون؟ .',NULL,'18',NULL,NULL,'0');
INSERT INTO [b836] VALUES (85,'<span data-type="title" id=toc-87>٧٨ </span>- ومن اليهود فريق جهلة أميون لا يعرفون عن التوراة إلا أكاذيب تتفق مع أمانيهم، لفَّقها لهم أحبارهم، وألقوا فى ظنهم أنها حقائق من الكتاب.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b836] VALUES (86,'<span data-type="title" id=toc-88>٧٩ </span>- فالهلاك والعذاب لهؤلاء الأحبار الذين يكتبون كتباً بأيديهم، ثم يقولون للأميين: هذه هى التوراة التى جاءت من عند الله، ليصلوا من وراء ذلك إلى غرض تافه من أغراض الدنيا فيشتروا التافه من حطام الدنيا بثمن غال وعزيز هو الحقيقة والصدق، فويل لهم مما تقوَّلوه على الله، وويل لهم مما يكسبون من ثمرات افترائهم.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b836] VALUES (87,'<span data-type="title" id=toc-89>٨٠ </span>- ومن اختلاقاتهم وأكاذيبهم ما يتلقونه من أحبارهم من أن النار لن تمس يهودياً مهما ارتكب من المعاصى إلا أياماً معدودة، فقل لهم يا محمد: هل تعاهدتم مع الله على ذلك فاطمأننتم، لأن الله لا يخلف عهده، أم أنكم تفترون الكذب عليه؟ .',NULL,'19',NULL,NULL,'0');
INSERT INTO [b836] VALUES (88,'<span data-type="title" id=toc-90>٨١ </span>- الحق أنكم تفترون الكذب على الله، فحكم الله العام نافذ فى خلقه جميعاً لا فرق بين يهودى وغير يهودى، لأن من ارتكب سيئة وأحاطت به آثامه حتى سدت عليه منافذ الخلاص، فأولئك أصحاب النار هم فيها خالدون.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b836] VALUES (89,'<span data-type="title" id=toc-91>٨٢ </span>- والذين آمنوا وعملوا الصالحات أولئك أصحاب الجنة، لأنهم آمنوا وأدوا ما يفرضه عليهم إيمانهم من صالح الأعمال، فهم فيها خالدون.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b836] VALUES (90,'<span data-type="title" id=toc-92>٨٣ </span>- وإن لكم معشر اليهود بجانب هذا كله ماضياً حافلا بالإثم ونقض المواثيق، وتعدى ما وضعه الله لكم من حدود، فلتذكروا إذ أخذنا عليكم فى التوراة ميثاقاً ألا تعبدوا إلا الله، وأن تحسنوا إلى الوالدين والأقربين واليتامى والمساكين، وتستخدموا فى حديثكم مع الناس القول الطيب الذى يؤلف بينكم وبينهم ولا ينفرهم منكم، وتؤدوا ما فرض عليكم من صلاة وزكاة، ولتذكروا ما كان من مسلككم حيال هذا الميثاق إذ نقضتموه وأعرضتم عنه إلا قليلا منكم ممن أذعن للحق.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b836] VALUES (91,'<span data-type="title" id=toc-93>٨٤ </span>- وإذ أخذنا ميثاقاً عليكم فى التوراة ألا يسفك بعضكم دماء بعض، ولا يخرج بعضكم بعضا من ديارهم، وهو ميثاق تقرون أنه فى كتابكم وتشهدون على صحته.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b836] VALUES (92,'<span data-type="title" id=toc-94>٨٥ </span>- وها أنتم أولاء يقتل بعضكم بعضا، ويخرج فريق منكم فريقاً آخر من ديارهم متعاونين فى ذلك عليهم مع غيركم بالإثم والعدوان، ثم إن وقع فريق منكم أسرى لدى من تتعاونون معهم تعملون على إنقاذهم من الأسر بافتدائهم، وإن سئلتم عما حملكم على افتدائهم قلتم: لأن أسفارنا أمرتنا أن نفدى أسرانا من اليهود، أو لم تأمركم أسفاركم كذلك ألا تسفكوا دماء إخوانكم، وألا تخرجوهم من ديارهم؟ ، أفتذعنون لبعض ما جاء فى الكتاب وتكفرون ببعض؟ فما جزاء من يفعل ذلك منكم إلا خزى فى الحياة الدنيا، ويوم القيامة يردهم الله - المطلع على أعمالهم وسرائرهم - إلى أشد العذاب.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b836] VALUES (93,'<span data-type="title" id=toc-95>٨٦ </span>- وذلك لأنهم قد آثروا أعراض الدنيا الزائلة على نعيم الآخرة الدائم، وكانوا بهذا كمن اشترى الحياة الدنيا بالآخرة، فلن يخفف عنهم عذاب جهنم، ولن يجدوا من ينقذهم منه.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b836] VALUES (94,'<span data-type="title" id=toc-96>٨٧ </span>- ولتذكروا كذلك - معشر اليهود - مواقفكم الضالة الآثمة حيال موسى ومن بعثناه من بعده إليكم من المرسلين. فلقد أرسلنا إليكم موسى وآتيناه التوراة وبعثنا إليكم على آثاره عدة رسل، منهم عيسى ابن مريم الذى أمددناه بالمعجزات وأيدناه بروح القدس، وهو جبريل رسول الوحى الأمين، فكنتم كلما جاءكم رسول من هؤلاء بما لا تهوى أنفسكم تستكبرون عن اتباعه، ففريق كذبتموه وفريق آخر قتلتموه.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b836] VALUES (95,'<span data-type="title" id=toc-97>٨٨ </span>- وكذلك كان موقفكم حيال رسولنا - محمد - خاتم النبيين. فلقد قلتم له حينما دعاكم إلى الإسلام: إن قلوبنا مغطاة بأغشية لا تنفذ إليها دعوتك، فلا نكاد نفقه شيئاً مما تقول. ولم تكن قلوبهم كما يزعمون، ولكنهم استكبروا وآثروا الضلالة على الهدى، فلعنهم الله بكفرهم وأوهن يقينهم وأضعف إيمانهم.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b836] VALUES (96,'<span data-type="title" id=toc-98>٨٩ </span>- ولما جاءهم رسولنا بالقرآن - وهو كتاب من عند الله مصدق لما أنزل عليهم من التوراة، وعرفوا من التوراة نفسها صدق ما فى هذا الكتاب - كفروا به عناداً وحسداً لأنه قد جاءهم به رسول من غير شعبهم بنى إسرائيل، مع أنهم كانوا من قبل إذا اشتبكوا مع المشركين فى صراع حربى أو جدلى ذكروا أن الله سينصرهم بإرسال خاتم النبيين الذى بشر به كتابهم، والذى تتفق صفاته كل الاتفاق مع صفات محمد. ألا لعنة الله على أمثالهم من المعاندين الجاحدين.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b836] VALUES (97,'<span data-type="title" id=toc-99>٩٠ </span>- ولبئس ما باعوا به أنفسهم بغياً وعدواناً، إذ مالوا مع أهوائهم وتعصبهم لشعبهم فكفروا بما أنزلنا، ناقمين على غيرهم أن خصهم الله دونهم بإرسال رسول منهم منكرين على الله أن يكون له مطلق الخيرة فى أن ينزل من فضله على من يشاء من عباده، فباءوا بغضب على غضب لكفرهم وعنادهم وحسدهم، وعذبوا بكفرهم وللكافرين عذاب عظيم.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b836] VALUES (98,'<span data-type="title" id=toc-100>٩١ </span>- هذا هو ما كانت تنطوى عليه نفوسهم، ولكنهم كانوا يبررون أمام الخلق عدم إيمانهم بالقرآن حينما يطلب منهم الإيمان بأنهم لا يؤمنون إلا بما أنزل عليهم هم ويكفرون بغيره، ولقد كذبوا فيما يدَّعون من إيمانهم بما أنزل عليهم من التوارة، لأن كفرهم بهذا الكتاب المصدق لما فى كتابهم هو كفر بكتابهم نفسه، ولأنهم قد قتلوا الأنبياء الذين دعوهم إلى ما أنزل عليهم، وقتلهم لهؤلاء أقطع دليل على عدم إيمانهم برسالتهم.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b836] VALUES (99,'<span data-type="title" id=toc-101>٩٢ </span>- بل لقد كفرتم - أيها اليهود - كفراً صريحاً بكتبكم، ورجعتم إلى الشرك فى عهد موسى نفسه، فلقد جاءكم موسى بالبينات والمعجزات الناطقة بصدقه، لكنكم حين تغيب موسى لمناجاة ربه عبدتم العجل ورجعتم إلى سابق وثنيتكم وأنتم ظالمون مبطلون.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b836] VALUES (100,'<span data-type="title" id=toc-102>٩٣ </span>- وحينما جاءكم بالتوراة، ورأيتم ما فيها من تكاليف شاقة، فاستثقلتم أعباءها وارتبتم فيها، أراكم الله آية على صدق هذا الكتاب وفائدة تعاليمه لكم، فرفع جبل الطور فوق رءوسكم حتى صار كأنه ظُلَّة وظننتم أنه واقع بكم، وحينئذٍ أعلنتم القبول والطاعة، فأخذنا عليكم ميثاقاً ألا يأخذكم هوى فى الامتثال لما جاء فى هذا الكتاب، فقلتم: آمنا وسمعنا، ولكن أعمالكم تكشف عن عصيانكم وتمردكم، وأن الإيمان لم يخالط قلوبكم، ولا يمكن أن يكون الإيمان قد خالط قلوب قوم شغفوا حباً بعبادة العجل. فلبئس ما دفعكم إليه إيمانكم إن كنتم مؤمنين.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b836] VALUES (101,'<span data-type="title" id=toc-103>٩٤ </span>- ولقد زعمتم أن الله سيخصكم من بين سائر الناس بنعيم الجنة بعد الممات، فإن كنتم مؤمنين حقاً بما تقولون فليكن الموت محبباً إليكم، ولتتمنوه حتى لا يبطئ عنكم هذا النعيم الذى تدَّعون.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b836] VALUES (102,'<span data-type="title" id=toc-104>٩٥ </span>- ولكنهم فى الواقع لا يرغبون فى الموت أبداً لما اقترفوه من ظلم لا يخفى أمره على الله، الذى يُعلِمُهُم أنهم كاذبون فيما يدعون، وأن النعيم يوم القيامة للمتقين، لا للفجار أمثالهم.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b836] VALUES (103,'<span data-type="title" id=toc-105>٩٦ </span>- بل إنك لتجدنهم أحرص الناس جميعاً على حياتهم على أى شكل عزيزة أو ذليلة، وحرصهم أكثر من حرص المشركين الذين لا يؤمنون ببعث ولا جنة، ولذلك يود أحدهم لو يعمر ألف سنة، ولن يبعد عنه تعميره - مهما طال - ما ينتظر من عذاب الله، إنه عليم بالظالمين وسيذيقهم جزاء ما اقترفوه.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b836] VALUES (104,'<span data-type="title" id=toc-106>٩٧ </span>- ولقد زعم بعضهم أنهم يعادونك ويكفرون بكتابك لأنهم أعداء لجبريل الذى يبلغك هذا الكتاب، فقل أيها النبى لهم: من كان عدواً لجبريل فهو عدو الله، لأن جبريل ما يجئ بهذا الكتاب من عنده، وإنما ينزله بأمر الله مصدقاً لما سبقه من الكتب السماوية، ومصدقاً لكتابهم نفسه، وهدى وبشرى للمؤمنين.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b836] VALUES (105,'<span data-type="title" id=toc-107>٩٨ </span>- فمن كان عدواً لجبريل أو ميكائيل أو لأى ملك أو رسول من ملائكة الله ورسله الذين لا يفعلون ولا يبلغون إلا ما يأمرهم به الله، فإنه بذلك يكون عدواً وكافراً به، والله عدو الكافرين.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b836] VALUES (106,'<span data-type="title" id=toc-108>٩٩ </span>- وما ينزل جبريل على قلبك إلا بآيات بينات لا يسع طالب الحق إلا الإيمان بها، وما يكفر بمثلها إلا المعاندون الخارجون عن سنة الفطرة.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b836] VALUES (107,'<span data-type="title" id=toc-109>١٠٠ </span>- وكما تذبذبوا فى العقيدة والإيمان، تذبذبوا كذلك فيما يبرمونه من عهود، فكانوا كلما عاهدوا المسلمين وغيرهم عهداً نبذه فريق منهم. لأن معظمهم لا يؤمن بحرمة عهد ولا بقداسة ميثاق.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b836] VALUES (108,'<span data-type="title" id=toc-110>١٠١ </span>- ولما جاءهم رسول من عند الله مطابقة أوصافه لما فى أسفارهم - وهو محمد صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - نبذ فريق منهم ما ذكر فى كتبهم عن هذا الرسول، كأنه لم يرد فيها ولم يعلموا شيئاً عنه.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b836] VALUES (109,'<span data-type="title" id=toc-111>١٠٢ </span>- ولقد صدَّقوا ما تَتَقَوَّله شياطينهم وفجرتهم على ملك سليمان، إذ زعموا أن سليمان لم يكن نبياً ولا رسولاً ينزل عليه الوحى من الله، بل كان مجرد ساحر يستمد العون من سحره، وأن سحره هذا هو الذى وطَّد له الملك وجعله يسيطر على الجن والطير والرياح، فنسبوا بذلك الكفرَ لسليمان، وما كفر سليمان، ولكن هؤلاء الشياطين الفجرة هم الذين كفروا، إذ تقوَّلوا عليه هذه الأقاويل، وأخذوا يعلِّمون الناس السحر من عندهم ومن آثار ما أنزل ببابل على الملكين هاروت وماروت، مع أن هذين الملكين ما كانا يعلِّمان أحداً حتى يقولا له: إنما نعلِّمك ما يؤدى إلى الفتنة والكفر فاعرفه واحذره وتَوَقَّ العمل به. ولكن الناس لم ينتصحوا بهذه النصيحة، فاستخدموا ما تعلَّموه منهما فيما يفرقون به بين المرء وزوجه. نعم كفر هؤلاء الشياطين الفجرة إذ تقوَّلوا هذه الأقاويل من أقاويلهم وأساطيرهم ذريعة لتعليم اليهود السحر، وما هم بضارين بسحرهم هذا من أحد، ولكن الله هو الذى يأذن بالضرر إن شاء، وأن ما يؤخذ عنهم من سحر سيضر من تعلَّمه فى دينه ودنياه ولا يفيده شيئاً، وهم أنفسهم يعلمون حق العلم أن من اتجه هذا الاتجاه لن يكون له حظ فى نعيم الآخرة، ولبئس ما اختاروه لأنفسهم لو كانت بهم بقية من علم.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b836] VALUES (110,'<span data-type="title" id=toc-112>١٠٣ </span>- ولو أنهم آمنوا بالحق وخافوا مقام ربهم لأثابهم الله ثواباً حسناً، ولكان ذلك خيراً مما يلقونه من أساطير ويضمرونه من خبث لو كانوا يميزون النافع من الضار.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b836] VALUES (111,'<span data-type="title" id=toc-113>١٠٤ </span>- يا أيها الذين آمنوا خذوا حذركم من هؤلاء اليهود فلا تقولوا للرسول حينما يتلوا عليكم الوحى: (راعنا) قاصدين أن يجعلكم موضع رعايته، ويتمهل عليكم فى تلاوته حتى تعوه وتحفظوه، لأن خبثاء اليهود يتظاهرون بمحاكاتكم فى ذلك، ويلوون ألسنتهم بهذه الكلمة حتى تصير مطابقة لكلمة سباب يعرفونها ويوجهونها للرسول ليسخروا منه فيما بينهم، ولكن استخدموا كلمة أخرى لا يجد اليهود فيها مجالا لخبثهم وسخريتهم: فقولوا: (انظرنا) وأحسنوا الإصغاء إلى ما يتلوه عليكم الرسول، وأن الله ليدخر يوم القيامة عذاباً أليماً لهؤلاء المستهزئين بالرسول.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b836] VALUES (112,'<span data-type="title" id=toc-114>١٠٥ </span>- ولتعلموا أن هؤلاء الكافرين من اليهود والمشركين من عبدة الأصنام لا يرجون إلا ضرركم ولا يودون أن ينزل عليكم خير من ربكم، والله لا يقيم وزناً لما يرجون وما يكرهون. فالله يختص برحمته من يشاء والله ذو الفضل العظيم.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b836] VALUES (113,'<span data-type="title" id=toc-115>١٠٦ </span>- ولقد طلبوا منك - يا محمد - أن تأتيهم بالمعجزات التى جاءهم بها موسى وأنبياء بنى إسرائيل، وحسبنا أننا أيدناك بالقرآن، وأننا إذا تركنا تأييد نبى متأخر بمعجزة كانت لنبى سابق، أو أنسينا الناس أثر هذه المعجزة فإننا نأتى على يديه بخير منها أو مثلها فى الدلالة على صدقه، فالله على كل شئ قدير.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b836] VALUES (114,'<span data-type="title" id=toc-116>١٠٧ </span>- وهو الذى بيده ملكوت السموات والأرض، وليس لكم - أيها الناس - من دونه ولى يعينكم، ولا سند ينصركم.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b836] VALUES (115,'<span data-type="title" id=toc-117>١٠٨ </span>- لعلكم تريدون باقتراحكم معجزات معينة على رسولكم - محمد - أن تحاكوا بنى إسرائيل المعاصرين لموسى، إذ طلبوا إليه معجزات خاصة. إن اقتراحكم هذا ليخفى وراءه العناد والجنوح إلى الكفر، كما كان يخفى ذلك اقتراح بنى إسرائيل على رسولهم. ومن يؤثر العناد والكفر على الإخلاص للحق والإيمان، فقد حاد عن الطريق السوى المستقيم.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b836] VALUES (116,'<span data-type="title" id=toc-118>١٠٩ </span>- ولقد تمنى كثير من اليهود أن يردوكم - أيها المسلمون - إلى الكفر بعد إيمانكم، مع أنه قد تبين لهم من كتابهم نفسه أنكم على الحق، وما ذلك إلا لأنهم يحسدونكم ويخشون أن ينتقل إليكم السلطان ويفلت من أيديهم، فأعرضوا عنهم، واعفوا واصفحوا حتى يأذن الله لكم بمسلك آخر حيالهم، فهو القادر على أن يمكنكم منهم، وهو على كل شئ قدير.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b836] VALUES (117,'<span data-type="title" id=toc-119>١١٠ </span>- وحافظوا على شعائر دينكم، فأقيموا الصلاة، وأعطوا الزكاة، وما تقدموا لأنفسكم من أعمال طيبة وصدقة تجدوا ثوابه عند الله. إن الله بما تعملون عليم، علم من يبصر ويرى.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b836] VALUES (118,'<span data-type="title" id=toc-120>١١١ </span>- ومن أباطيل اليهود والنصارى وأمانيهم الكاذبة ما يزعمه كل منهم: من أن الجنة لن يدخلها إلا من كان على دينهم، فلتطلبوا إليهم أن يأتوا ببرهان على ذلك إن كانوا صادقين.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b836] VALUES (119,'<span data-type="title" id=toc-121>١١٢ </span>- ولن يجدوا على ذلك برهاناً، فالحق أن الذين يدخر لهم الله تعالى نعيم الجنة ويثيبهم يوم القيامة ويقيهم الخوف والحزن، هم الذين يخلصون لله ويتبعون الحق، ويحسنون ما يؤدونه من أعمال.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b836] VALUES (120,'<span data-type="title" id=toc-122>١١٣ </span>- ومن عجب أنهم كما يعادون الإسلام يعادى بعضهم بعضاً، فيقول اليهود: ليست النصارى على شئ من الحق، ويقول النصارى فى اليهود مثل ذلك، وكلاهما يستدل بأسفاره، ويقول المشركون من العرب الذين لا يعلمون شيئاً عن الكتب المنزلة فى اليهود والنصارى معاً ما يقوله كلاهما فى الآخر، ولقد صدقوا جميعاً فى ذلك، فليس منهم فريق على حق، وسيتبين ذلك حينما يحكم الله بينهم يوم القيامة فيما كانوا فيه يختلفون.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b836] VALUES (121,'<span data-type="title" id=toc-123>١١٤ </span>- ومن مظاهر عدائهم بعضهم لبعض؛ وعدائهم للمسلمين، أن بعض طوائفهم خرَّبت معابد الطوائف الأخرى، وأن المشركين منعوا المسلمين من المسجد الحرام، وليس ثمة أحد أشد ظلماً ممن يحول دون ذكر الله فى أماكن العبادة ويسعى فى خرابها، فأولئك لهم خزى فى الدنيا ولهم فى الآخرة عذاب عظيم. وما كان لهم أن يقترفوا مثل هذا الجرم الخطير، وإنما كان ينبغى أن يحفظوا للمعابد حرمتها، فلا يدخلوها إلا خاشعين، ولا يمنعوا غيرهم أن يذكر فيها اسم الله.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b836] VALUES (122,'<span data-type="title" id=toc-124>١١٥ </span>- وإذا كان المشركون قد منعوا المسلمين من الصلاة فى المسجد الحرام، فلن يمنعهم هذا من الصلاة وعبادة الله، فجميع الجهات وجميع البقاع فى الأرض لله، وإن الله ليتقبل من المسلم صلاته ويقبل عليه برضاه أياً كانت البقعة التى يؤدى فيها عبادته، فالله واسع لا يضيِّق على عباده، وهو عليم بنية من يتجه إليه.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b836] VALUES (123,'<span data-type="title" id=toc-125>١١٦ </span>- ومن كان هذا شأنه، وكان جميع ما فى الكون مسخراً لأمره، خاضعاً لمشيئته، فهو أرفع وأجل من أن يحتاج لنسل أو يتخذ ولداً كما يقول هؤلاء اليهود والنصارى والمشركون.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b836] VALUES (124,'<span data-type="title" id=toc-126>١١٧ </span>- وكيف يحتاج لنسل أو يتخذ ولداً من أبدع السموات والأرض وأذعن كل ما فيها لإرادته فلا يستعصى شئ عليه، وإذا أراد أمراً فإنما يقول له: كن، فيكون؟ .',NULL,'26',NULL,NULL,'0');
INSERT INTO [b836] VALUES (125,'<span data-type="title" id=toc-127>١١٨ </span>- هذا ويمعن المشركون من العرب فى عنادهم لمحمد، فيطلبون إليه مثل ما طلبته الأمم السابقة من أنبيائهم، فقد قالوا: إنهم لم يؤمنوا به إلا إذا كلمهم الله وجاءتهم آية حسية تدل على صدقه، كما قال بنو إسرائيل لموسى: لن نؤمن لك حتى نرى الله ويكلمنا، وكما طلب أصحاب عيسى منه أن ينزل عليهم مائدة من السماء، وما ذلك إلا لأن قلوب الكفار والمعاندين فى كل أمة متشابهة، وأنه لا يستبين الحقَّ إلا من صفت بصائرهم وأذعنت عقولهم لليقين، وطلبت الحق.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b836] VALUES (126,'<span data-type="title" id=toc-128>١١٩ </span>- وقد أرسلناك بحقائق يقينية بشيراً للمؤمنين ونذيراً للكافرين، وليس عليك إلا تبليغ رسالتنا، ولن تُسأل عن عدم إيمان من لم يؤمن بك من أصحاب الجحيم.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b836] VALUES (127,'<span data-type="title" id=toc-129>١٢٠ </span>- فلا ترهق نفسك فى استرضاء المعاندين من اليهود والنصارى، فإن هؤلاء لن يرضوا عنك حتى تتبع ملتهم التى يزعمون أنها الهدى، وليس ثمة هدى إلا هدى الله فى الإسلام، ومن يتبع أهواء هؤلاء من بعد أن علم ما أنزلناه إليك من الحق، فلن يكون له يوم القيامة من دون الله ولى يعينه، ولا نصير يدفع عنه العذاب.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b836] VALUES (128,'<span data-type="title" id=toc-130>١٢١ </span>- غير أن ثمة فريقاً من اليهود والنصارى قد تفقهوا فى أسفارهم الأصيلة، وتلوها حق التلاوة، وفطنوا إلى ما دخلها من تحريف، فأولئك يؤمنون بحقائقها ويؤمنون تبعاً لذلك بالقرآن، ومن يكفر بكتاب منزل فأولئك هم الخاسرون.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b836] VALUES (129,'<span data-type="title" id=toc-131>١٢٢ </span>- يا بنى إسرائيل اذكروا نعمتى العظيمة التى أنعمت بها عليكم بإخراجكم من ظلم فرعون وإغراقه، وإعطائكم المن والسلوى، وبعث الأنبياء فيكم، وتعليمكم الكتاب. . وغير ذلك مما شرفتكم به، وأنى فضلتكم - وقتاً من الزمان - على الناس فى جعل مصدر النبوات منكم.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b836] VALUES (130,'<span data-type="title" id=toc-132>١٢٣ </span>- وخافوا عقاب الله فى يوم لا تدفع فيه نفس عن نفس شيئاً، ولا يقبل منها فداء، ولا تنفعها شفاعة، ولا يجد فيه الكافرون نصيراً لهم من دون الله.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b836] VALUES (131,'<span data-type="title" id=toc-133>١٢٤ </span>- واذكروا إذ ابتلى الله جدكم إبراهيم بتكاليف، فقام بها على أتم وجه، فقال له: إنى جاعلك للناس إماماً يتبعونك ويقتدون بك، فطلب إبراهيم من ربه أن يجعل من ذريته أئمة كذلك، فأجابه بأن هذا لن يصل إليه منهم الظالمون، وأشار أنه سيكون من ذريته الأبرار والفجار.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b836] VALUES (132,'<span data-type="title" id=toc-134>١٢٥ </span>- واذكروا كذلك قصة بناء إبراهيم مع ابنه إسماعيل لبيت الله الحرام بمكة، وفى هذه القصة عظة بالغة لمن كان له قلب سليم، فلتذكروا إذ جعلنا هذا البيت ملاذاً للخلق ومأمناً لكل من يلجأ إليه، وإذ أمرنا الناس بأن يتخذوا من موضع قيام إبراهيم لبناء الكعبة مكاناً يصلون فيه، وعهدنا إلى إبراهيم وإسماعيل أن يصونا البيت مما لا يليق بحرمته، وأن يهيئاه تهيئة صالحة لمن يَؤُمُّهُ من الطائفين والمعتكفين والمصلين.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b836] VALUES (133,'<span data-type="title" id=toc-135>١٢٦ </span>- واذكروا اذ طلب إبراهيم من ربه أن يجعل البلد الذى سينشأ حول البيت بلداً آمناً، وأن يرزق من ثمرات الأرض وخيراتها من آمن من أهله بالله واليوم الآخر، فأجابه الله بأنه لن يضنَّ على الكافر نفسه بالرزق فى أثناء حياته القصيرة، ثم يلجئه يوم القيامة إلى عذاب جهنم. ولبئس المصير. . مصير هؤلاء.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b836] VALUES (134,'<span data-type="title" id=toc-136>١٢٧ </span>- وإذ يرفع إبراهيم هو وابنه إسماعيل قواعد البيت وهما يدعوان الله: ربنا يا خالقنا وبارئنا تقبل منا هذا العمل الخالص لوجهك، فأنت السميع لدعائنا العليم بصدق نياتنا.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b836] VALUES (135,'<span data-type="title" id=toc-137>١٢٨ </span>- ربنا وفقنا واجعلنا مخلصين لك واجعل من ذريتنا جماعة مخلصة لك، وعلمنا طريقة عبادتنا لك فى بيتك الحرام وما حوله، وتب علينا إن نسينا أو أخطأنا إنك أنت كثير القبول لتوبة عبادك، الغافر لهم بفضلك ورحمتك.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b836] VALUES (136,'<span data-type="title" id=toc-138>١٢٩ </span>- ربنا وابعث فى ذريتنا رسولا منهم يقرأ عليهم آياتك ويعلّمهم ما يوحى إليه به من كتاب وعلم نافع وشريعة محكمة، ويطهرهم من ذميم الأخلاق، إنك أنت الغالب القاهر الحكيم فيما تفعل وما تأمر به وما تنهى عنه.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b836] VALUES (137,'<span data-type="title" id=toc-139>١٣٠ </span>- ولَنِعم ما فعله إبراهيم وما دعا به ربه، وما اتبعه من ملة قويمة، وأنه لا يعرض عن ملة إبراهيم إلا من امتهن إنسانيته وعقله، ولقد اصطفاه الله فى الدنيا وإنه فى الآخرة لمن الصالحين المقربين.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b836] VALUES (138,'<span data-type="title" id=toc-140>١٣١ </span>- ولقد استجاب إبراهيم لأمر ربه حينما طلب الله إليه أن يذعن، فقال: أذعنت لرب العالمين جميعاً من جن وإنس وملائكة.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b836] VALUES (139,'<span data-type="title" id=toc-141>١٣٢ </span>- ولم يكتف بذلك بل أوصى بنيه بأن يسيروا على هديه، وحاكاه حفيد يعقوب فأوصى هو الآخر بنيه كذلك أن يتبعوا هذه السنن، وبيّن لأبنائه أن الله اصطفى لهم دين التوحيد وأخذ عليهم العهد ألا يموتوا إلا وهم مسلمون ثابتون على هذا الدين.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b836] VALUES (140,'<span data-type="title" id=toc-142>١٣٣ </span>- ولقد زعمتم - أيها اليهود - أنكم تسيرون على الدين الذى مات عليه يعقوب، فهل كنتم شهداء إذ حضره الموت فعرفتم الملة التى مات عليها؟ ألا فلتعلموا أن يعقوب وأبناءه كانوا مسلمين موحدين ولم يكونوا يهوداً مثلكم ولا نصارى، وأن يعقوب حينما حضره الموت جمع بنيه وقال لهم: ما تعبدون من بعدى؟ فأجابوا: نعبد إلهك وإله آبائك إبراهيم وإسماعيل وإسحاق إلهاً واحداً ونحن له خاضعون.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b836] VALUES (141,'<span data-type="title" id=toc-143>١٣٤ </span>- ثم ما لكم - أيها اليهود - والجدل فى هؤلاء! فأولئك قوم قد مضوا لسبيلهم، ثم لهم - وحدهم - ما كسبوا فى حياتهم، فلن تسألوا عن أعمالهم، ولن يفيدكم شئ منها، ولن يكون لكم إلا ما كسبتم أنتم من أعمال.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b836] VALUES (142,'<span data-type="title" id=toc-144>١٣٥ </span>- ولكنهم لا ينفكون يمعنون فى لجاجهم، ويزعم كل فريق منهم أن ملته هى الملة المثلى، فيقول لكم اليهود: كونوا يهوداً تهتدوا إلى الطريق القويم، ويقول النصارى: كونوا نصارى تهتدوا إلى الحق المستقيم، فلتردوا عليهم بأننا لا نتبع هذه الملة ولا تلك، لأن كلتيهما قد حُرِّفَتْ وخرجت عن أصولها الصحيحة، ومازجها الشرك، وبعدت عن ملة إبراهيم، وإنما نتبع الإسلام الذى أحيا ملة إبراهيم نقية طاهرة.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b836] VALUES (143,'<span data-type="title" id=toc-145>١٣٦ </span>- قولوا لهم: آمنا بالله وما أنزل إلينا فى القرآن، وآمنا كذلك بما أنزل إلى إبراهيم وإسماعيل وإسحاق ويعقوب وبنيه الأسباط، وبالتوراة التى أنزلها الله على موسى غير محرَّفة، والإنجيل الذى أنزله الله على عيسى غير محرَّف، وبما أوتى جميع النبيين من ربهم، لا نفرق بين أحد منهم - فنكفر ببعضهم ونؤمن ببعض - ونحن فى هذا كله مذعنون لأمر الله.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b836] VALUES (144,'<span data-type="title" id=toc-146>١٣٧ </span>- فإن آمنوا إيماناً مطابقاً لإيمانكم فقد اهتدوا، وإن تمادوا فى عنادهم وإعراضهم فإنما هم فى نزاع مستمر وخلاف معكم، وسيكفيك الله أمرهم - يا أيها النبى - ويريحك من لجاجهم وشقاقهم، فهو السميع لما يقولون، العليم بما عليه صدورهم.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b836] VALUES (145,'<span data-type="title" id=toc-147>١٣٨ </span>- قولوا لهم: إن الله قد هدانا بهدايته، وأرشدنا إلى حجته، ومن أحسن من الله هداية وحُجة، وأننا لا نخضع إلا لله، ولا نتبع إلا ما هدانا وأرشدنا إليه.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b836] VALUES (146,'<span data-type="title" id=toc-148>١٣٩ </span>- قولوا لهم: أتجادلوننا فى الله زاعمين أنه لا يصطفى أنبياء إلا منكم! وهو ربكم ورب كل شئ، لا يختص به قوم دون قوم، يصيب برحمته من يشاء، ويجزى كل قوم بأعمالهم، غير ناظر إلى أنسابهم ولا أحسابهم، وقد هدانا الطريق المستقيم فى أعمالنا، ورزقنا صفة الإخلاص له.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b836] VALUES (147,'<span data-type="title" id=toc-149>١٤٠ </span>- قولوا لهم: أتجادلوننا فى إبراهيم وإسماعيل وإسحاق ويعقوب وأبنائه الأسباط زاعمين أنهم كانوا يهودا أو نصارى مثلكم؟ ، مع أنه ما أنزلت التوراة والإنجيل اللذان قامت عليهما اليهودية والنصرانية إلا من بعد هؤلاء، وقد أخبرنا الله بذلك، أفأنتم أعلم أم الله؟ ، بل إن الله قد أخبركم أنتم بذلك فى أسفاركم فلا تكتموا الحق المدوَّن فى أسفاركم هذه، ومن أظلم ممن كتم حقيقة يعلمها من كتابه وسيجازيكم الله على ما تلجون فيه من باطل، فليس الله بغافل عما تعملون.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b836] VALUES (148,'<span data-type="title" id=toc-150>١٤١ </span>- ثم ما لكم أيها اليهود والنصارى والجدل فى هؤلاء؟ فأولئك قوم قد مضوا لسبيلهم، لهم ما كسبوا فى حياتهم، ولن تُسألوا عن أعمالهم ولن يفيدكم شئ منها، ولن يكون لكم إلا ما كسبتم أنتم من أعمال.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b836] VALUES (149,'<span data-type="title" id=toc-151>١٤٢ </span>- إن ضعاف العقول الذين أضلتهم أهواؤهم عن التفكر والتدبر من اليهود والمشركين والمنافقين سينكرون على المؤمنين تحوَّلهم من قبلة بيت المقدس التى كانوا يصلّون متجهين إليها إلى قبلة أخرى وهى الكعبة، فقل لهم أيها النبى: إن الجهات كلها لله، لا فضل لجهة على أخرى بذاتها، بل الله هو الذى يختار منها ما يشاء ليكون قبلة للصلاة، وهو يهدى بمشيئته كل أمة من الأمم إلى طريق قويم يختاره لها ويخصها به، وقد جاءت الرسالة المحمدية فنسخت ما قبلها من الرسالات، وصارت القبلة الحقة هى الكعبة.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b836] VALUES (150,'<span data-type="title" id=toc-152>١٤٣ </span>- ولهذه المشيئة هديناكم إلى الطريق الأقوم، وجعلناكم أمة عدولاً خياراً بما وفقناكم إليه من الدين الصحيح والعمل الصالح لتكونوا مقررى الحق بالنسبة للشرائع السابقة، وليكون الرسول مهيمناً عليكم، يسددكم بإرشاده فى حياته، وبنهجه وسنته بعد وفاته. وأما عن قبلة بيت المقدس التى شرعناها لك حيناً من الدهر، فإنما جعلناها امتحاناً للمسلمين ليتميز من يذعن فيقبلها عن طواعية، ومن يغلب عليه هوى تعصبه العربى لتراث إبراهيم فيعصى أمر الله ويضل عن سواء السبيل. ولقد كان الأمر بالتوجه إلى بيت المقدس من الأمور الشاقة إلا على من وفقه الله بهدايته، وكان امتثال هذا الأمر من أركان الإيمان، فمن استقبل بيت المقدس عند الأمر باستقباله - إيماناً منه وطاعة - فلن يضيع عليه ثواب إيمانه وطاعته.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b836] VALUES (151,'<span data-type="title" id=toc-153>١٤٤ </span>- ولقد رأينا كيف كنت تتطلع إلى السماء عسى أن ينزل الوحى بتغيير قبلة بيت المقدس إلى الكعبة التى تحبها لأنها قبلة إبراهيم أبى الأنبياء، وأبى اليهود والعرب، وبها مقام إبراهيم، فهى - لهذا - القبلة الجامعة وإن كانت تخالف قبلة اليهود، فها نحن أولاء نؤتيك سؤلك فاستقبل فى صلاتك المسجد الحرام، واستقبلوه كذلك أيها المؤمنون فى أى مكان تكونون، وإن أهل الكتاب الذين ينكرون عليكم التحول عن قبلة بيت المقدس قد عرفوا فى كتبهم أنكم أهل الكعبة، وعلموا أن أمر الله جار على تخصيص كل شريعة بقبلة، وأن هذا هو الحق من ربهم، ولكنهم يريدون فتنتكم وتشكيككم فى دينكم، والله ليس غافلا عنهم وهو يجزيهم بما يعملون.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b836] VALUES (152,'<span data-type="title" id=toc-154>١٤٥ </span>- وما كان إنكار أهل الكتاب عليكم لشبهة تزيلها الحُجة، بل هو إنكار عناد ومكابرة فلئن جئتهم - أيها الرسول - بكل حُجة قطعية على أن قبلتك هى الحق ما تبعوا قبلتك، وإذا كان اليهود منهم يطمعون فى رجوعك إلى قبلتهم ويعلِّقون إسلامهم على ذلك فقد خاب رجاؤهم وما أنت بتابع قبلتهم، وأهل الكتاب أنفسهم يتمسك كل فريق منهم بقبلته: فلا النصارى يتبعون قبلة اليهود ولا اليهود يتبعون قبلة النصارى، وكل فريق يعتقد أن الآخر ليس على حق، فاثبت على قبلتك ولا تتبع أهواءهم، فمن اتبع أهواءهم - بعد العلم ببطلانها والعلم بأن ما عليه هو الحق - فهو من الظالمين الراسخين فى الظلم.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b836] VALUES (153,'<span data-type="title" id=toc-155>١٤٦ </span>- وإن أهل الكتاب ليعلمون أن التحول إلى قبلة البيت الحرام بمكة هو الحق، ويسلِّمون أنك النبى المنعوت فى كتبهم بنعوت من جملتها أنه يصلى إلى الكعبة، ومعرفتهم نُبُوَّتك وقبلتك كمعرفتهم أبناءهم فى الوضوح والجلاء، ولكن بعضهم يخفون هذا الحق على علم اتباعاً لهواهم، وتعصباً باطلا لملتهم حفاظاً على سلطانهم، ويحاولون تضليلكم.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b836] VALUES (154,'<span data-type="title" id=toc-156>١٤٧ </span>- وإنما الحق هو ما صدر لك من الله تعالى لا ما يضلَّل به أهل الكتاب، فكونوا على يقين منه، ولا تكونوا من أهل الشك والتردد، ومن ذلك الحقِّ أمرُ تحول القبلة إلى البيت الحرام فامضوا عليه ولا تبالوا بالمعارضين.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b836] VALUES (155,'<span data-type="title" id=toc-157>١٤٨ </span>- إن هذه القبلة التى حولناك إليها هى قبلتك وقبلة أمتك، وكذلك لكل أمة قبلة تتجه إليها فى صلاتها حسب شريعتها السابقة، وليس فى ذلك شئ من التفاضل، وإنما التفاضل فى فعل الطاعات وعمل الخيرات، فسارعوا إلى الخيرات وتنافسوا فيها، وسيحاسبكم الله على ذلك، فإنه سيجمعكم يوم القيامة من أى موضع كنتم، ولن يفلت منه أحد، وبيده كل شئ بما فى ذلك الإماتة والإحياء والبعث والنشور.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b836] VALUES (156,'<span data-type="title" id=toc-158>١٤٩ </span>- فَاسْتقْبل - يا محمد - ومن اتبعك المسجد الحرام فى صلاتك من كل مكان كنت فيه، سواء كان ذلك فى حال إقامتك أم فى حال سفرك وخروجك من مكان إقامتك، وإن هذا لهو الحق الموافق لحكمة ربك الرفيق بك، فاحرص عليه أنت وأمَّتك، فإن الله سيجازيكم أحسن الجزاء، والله عالم علماً لا يخفى عليه شئ من عملكم.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b836] VALUES (157,'<span data-type="title" id=toc-159>١٥٠ </span>- والتزم أمر الله فى القبلة واحرص عليه أنت وأمتك، فاجعل وجهك فى ناحية المسجد الحرام من كل مكان خرجت إليه فى أسفارك، واستقبلوه حيثما كنتم من أقطار الأرض مسافرين أو مقيمين، لينقطع ما يحاجّكم به المخالفون ويجادلونكم به، وإذا لم تمتثلوا لأمر هذا التحويل فسيقول اليهود: كيف يصلى محمد إلى بيت المقدس والنبى المنعوت فى كتبنا من أوصافه التحول إلى الكعبة؟ وسيقول المشركون العرب كيف يدعى ملة إبراهيم ويخالف قبلته؟ على أن الظالمين الزائغين عن الحق من الجانبين لن ينقطع جدالهم وضلالهم، بل سيقولون: ما تحوَّل إلى الكعبة إلا مَيْلا إلى دين قومه وحباً لبلده، فلا تبالوا بهم فإن مطاعنهم لا تضركم، وَاخْشَوْنِ فلا تخالفوا أمرى، وقد أردنا بهذا الأمر أن نتم النعمة عليكم وأن تكون هذه القبلة التى وجهناكم إليها أدعى إلى ثباتكم على الهداية والتوفيق.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b836] VALUES (158,'<span data-type="title" id=toc-160>١٥١ </span>- وإن توجيهكم إلى المسجد الحرام بإرسالنا فيكم رسولا منكم يتلو عليكم آيات من إتمام نعمتنا عليكم كما أتممنا عليكم النعمة - القرآن - ويطهر نفوسكم عمليا من دنس الشرك وسيئ الأخلاق والعادات ويكملكم علمياً بمعارف القرآن والعلوم النافعة ويعلمكم ما لم تكونوا تعلمون، فقد كنتم فى جاهلية جهلاء وضلالة عمياء.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b836] VALUES (159,'<span data-type="title" id=toc-161>١٥٢ </span>- فاذكرونى - أيها المؤمنون - بالطاعة أذكركم بالثواب، واشكروا لى ما أسبغت عليكم من النعم ولا تجحدوا هذه النعم بعصيان ما أمرتكم به.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b836] VALUES (160,'<span data-type="title" id=toc-162>١٥٣ </span>- واستعينوا - أيها المؤمنون - فى كل ما تأتون وما تذرون بالصبر على الأمور الشاقة والصلاة التى هى أمّ العبادات، إن الله بقدرته القاهرة مع الصابرين فهو وليهم وناصرهم.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b836] VALUES (161,'<span data-type="title" id=toc-163>١٥٤ </span>- ولن يؤدى الصبر إلا إلى الخير والسعادة فى الدارين، فلا تقعدوا عن الجهاد فى سبيل الله، ولا ترهبوا الموت فيه، فمن مات فى الجهاد فليس بميت بل هو حى حياة عالية وإن كان الأحياء لا يحسون بها.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b836] VALUES (162,'<span data-type="title" id=toc-164>١٥٥ </span>- والصبر درع المؤمن وسلاحه الذى يتغلب به على الشدائد والمشاق، وسيصادفكم كثير من الشدائد فسنمتحنكم بكثير من خوف الأعداء والجوع وقلة الزاد والنقص فى الأموال والأنفس والثمرات، ولن يعصمكم فى هذا الامتحان القاسى إلا الصبر، فبشر - يا أيها النبى - (الصابرين) بالقلب وباللسان.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b836] VALUES (163,'<span data-type="title" id=toc-165>١٥٦ </span>- الذين إذا نزل بهم ما يؤملهم يؤمنون أن الخير والشر من الله، وأن الأمر كله لله فيقولون: إنّا مِلْكٌ لله - تعالى - وراجعون إليه، فليس لنا من أمرنا شئ، وله الشكر على العطاء وعلينا الصبر عند البلاء، وعنده المثوبة والجزاء.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b836] VALUES (164,'<span data-type="title" id=toc-166>١٥٧ </span>- فهؤلاء الصابرون المؤمنون بالله لهم البشارة الحسنة بغفران الله وإحسانه، وهم المهتدون إلى طريق الخير والرشاد.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b836] VALUES (165,'<span data-type="title" id=toc-167>١٥٨ </span>- وكما أن الله رفع شأن الكعبة بجعلها قبلة الصلاة، رفع أمر الجبلين اللذين يُشَارِفانِهَا وهما «الصفا» «والمروة» فجعلهما من مناسك الحج، فيجب بعد الطواف السعى بينهما سبع مرات، وقد كان منكم من يرى فى ذلك حرجاً لأنه من عمل الجاهلية، ولكن الحق أنه من معالم الإسلام، فلا حرج على من ينوى الحج أو العمرة أن يسعى بين هذين الجبلين، وليأت المؤمن من الخير ما استطاع فإن الله عليم بعمله ومثيبه عليه.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b836] VALUES (166,'<span data-type="title" id=toc-168>١٥٩ </span>- وأولئك الذين أنكروا عليكم أمر دينكم فريقان: فريق من أهل الكتاب الذين يعرفون الحق ويخفونه على علم وعناد، وفريق المشركين الذين عميت قلوبهم عن الحق، فاتخذوا أرباباً من دون الله، فأهل الكتاب الذين عرفوا براهين صدقك تبينوا الحق فى دينك ثم أخفوا هذه الدلائل وكتموها عن الناس، أولئك يصب الله عليهم غضبه ويبعدهم عن رحمته، ويدعو عليهم الداعون من الملائكة ومؤمنى الثقلين - الجن والإنس - بالطرد من رحمة الله.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b836] VALUES (167,'<span data-type="title" id=toc-169>١٦٠ </span>- ولا يستثنى من أهل الكتاب إلا من تاب وأحسن فرجع عن الكتمان، وتدارك أمره بإظهار ما كان يخفيه من وصف الرسول والإسلام، فإن الله يتقبل توبته ويمحو ذنبه، فهو الذى يقبل التوبة من عباده رأفة منه ورحمة.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b836] VALUES (168,'<span data-type="title" id=toc-170>١٦١ </span>- أما الذين استمروا على الكفر، وماتوا على ذلك دون توبة ولا ندم، فجزاؤهم لعنة الله والملائكة والناس أجمعين.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b836] VALUES (169,'<span data-type="title" id=toc-171>١٦٢ </span>- وسيستمرون فى هذه اللعنة وفى النار لا يخفف عنهم العذاب، ولن يمهلوا أو يؤخروا، ولو طلبوا الإمهال والتأخير لن يجابوا إليه.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b836] VALUES (170,'<span data-type="title" id=toc-172>١٦٣ </span>- إن إلهكم الذى ينفرد بالعبودية واحد، فلا إله غيره، ولا سلطان لسواه، ثم هو قد اتصف بالرحمة فهو رحيم بعباده فى إنشائهم وتكوينهم.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b836] VALUES (171,'<span data-type="title" id=toc-173>١٦٤ </span>- وقد أقام الله سبحانه وتعالى دلائل وآيات لكل ذى عقل على وجوده وألوهيته، ومن ذلك السموات التى ترونها تسير فيها الكواكب بانتظام دون تزاحم ولا صدام تبعث الحرارة والنور لهذا العالم، والأرض وما فيها من البر والبحر، وتعاقب الليل والنهار وما فى ذلك من المنافع، وما يجرى فى البحر من السفن تحمل الناس والمتاع، ولا يسيِّرها إلا الله، فهو الذى يرسل الرياح التى يسير بها المطر ينزل فيحيى الحيوان ويسقى الأرض والنبات، والرياح وهبوبها فى مهابها المختلفة، والسحاب المعلق بين السماء والأرض، فهل هذه الأشياء كلها بهذا الاتقان والإحكام من تلقاء نفسها أم هى صنع العليم القدير؟ .',NULL,'36',NULL,NULL,'0');
INSERT INTO [b836] VALUES (172,'<span data-type="title" id=toc-174>١٦٥ </span>- ومع هذه الدلائل الواضحة اتخذ بعض الناس ممن ضلّت عقولهم أرباباً غير الله يطيعونهم ويعبدونهم كعبادة الله ويجعلونهم مثل الله، والمؤمن يسلم القيادة لله وحده وطاعته له لا تنقطع، أما هم فإن ولاءهم لآلهتهم يتزلزل عند النوائب فيلجأون إلى الله سبحانه، وهؤلاء الذين ظلموا أنفسهم لو عاينوا ما سينالهم من العذاب يوم الجزاء حين ينكشف ملك الله وتكون الطاعة له وحده، لانتهوا عن جرمهم وأقلعوا عن إثمهم.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b836] VALUES (173,'<span data-type="title" id=toc-175>١٦٦ </span>- فى ذلك اليوم يرجو الأتباعُ أن ينجيهم رؤساؤهم من الضلال فيتنكرون لهم ويتبرَّأون منهم ويقولون: ما دعوناكم لطاعتنا فى معصية ربكم، وإنما هو هواكم وسوء تصرفكم، وتنقطع بينهم الصلات والمودات التى كانت بينهم فى الدنيا، ويصير بعضهم لبعض عدواً.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b836] VALUES (174,'<span data-type="title" id=toc-176>١٦٧ </span>- وهنا يتبين الأتباع أنهم كانوا فى ضلال حين اتبعوا رؤساءهم فى الباطل ويتمنون أن يعودوا إلى الدنيا فيتنكروا لرؤسائهم كما تبرأوا منهم فى هذا اليوم، وتبدو لهم أعمالهم السيئة فتكون حسرات عليهم ويندمون، وقد ألقى بهم فى النار فلا يبرحونها.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b836] VALUES (175,'<span data-type="title" id=toc-177>١٦٨ </span>- يا أيها الناس كُلوا مما خلق الله فى الأرض من الحلال الذى لم ينزل تحريمه، المستطاب الذى تستسيغه النفوس، ولا تسيروا وراء الشيطان الذى يزيِّن لكم أكل الحرام أو تحريم الحلال، فقد علمتم عداوة الشيطان، وبان قبيح ما يأمركم به.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b836] VALUES (176,'<span data-type="title" id=toc-178>١٦٩ </span>- وإنما يزين لكم الشيطان ما هو سيئ فى ذاته، ويضركم فى عافيتكم وما يقبح فعله، وتسيرون بسببه وراء الظنون والأوهام، فتنسبون إلى الله من التحريم والتحليل ما لم يأت دليل عليه من العلم اليقين.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b836] VALUES (177,'<span data-type="title" id=toc-179>١٧٠ </span>- وقد اعتاد الضالون عن سبيل الهدى أن يتمسكوا بما توارثوا عن آبائهم فى العقيدة والعمل، وإذا دعوا إلى ما جاء من هدى الله قالوا: لا نعدل عما وجدنا عليه آباءنا، ومن أكبر الجهل ترجيح اتباع طاعة الآباء على إطاعة الله واتباع هداه، فكيف إذا كان آباؤهم لا يعقلون شيئاً من الدين ولا يستنيرون بنور الهداية والإيمان؟',NULL,'37',NULL,NULL,'0');
INSERT INTO [b836] VALUES (178,'<span data-type="title" id=toc-180>١٧١ </span>- وإن مثل من يدعو أولئك الكافرين الجاحدين إلى الحق والهدى - فلا يستجيبون له ولا يفقهون ما يدعوهم إليه - كمثل راعى الغنم يناجيها، فلا تفقه منه شيئاً ولا يقرع سمعها إلا الصوت ولا تعى غيره، فهم كذلك عن الحق صُمّ الآذان، عُمْى البصائر، خُرْس الألسنة، لا ينطقون بخير، ولا يصدرون عن عقل.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b836] VALUES (179,'<span data-type="title" id=toc-181>١٧٢ </span>- لقد أبحنا للناس كل حلال خلقناه لهم فى الأرض، ونهيناهم أن يتبعوا خطوات الشيطان، فإنْ فعلوا اهتدوا، وإن أبوا فإنا نخص المؤمنين بهدايتنا ونبيِّن الحلال والحرام، في أيها الذين آمنوا أبيح لكم أن تأكلوا من لذيذ الطعام الطيب غير الخبيث، فاشكروا الله على ما أولاكم من نعمة التمكين من الطيبات وإباحتها، ومن نعمة الطاعة والامتثال لأمره لتتم عبادتكم.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b836] VALUES (180,'<span data-type="title" id=toc-182>١٧٣ </span>- وليس المحرم ما زعمه المشركون وما زعمه اليهود، وإنما المحرم عليكم - أيها المؤمنون - الميتة التى لم تذبح من الحيوان، ومن الدم المسفوح، ومثله فى التحريم لحم الخنزير، وما ذكر على ذبحه اسم غير الله من الوثن ونحوه، على أن من اضطر إلى تناول شئ من هذه المحظورات لجوعٍ لا يجد ما يدفعه غيرها أو لإكراه على أكله فلا بأس عليه، وليتجنب سبيل الجاهلية من طلب هذه المحرمات والرغبة فيها ولا يتجاوز ما يسد الجوع.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b836] VALUES (181,'<span data-type="title" id=toc-183>١٧٤ </span>- هذا وقد كان من العالمِين بما أنزل الله فريقٌ يُخفى بَعْضَ الوحى لقاء عَرَضٍ من أعراض الدنيا، فإن اليهود كتموا كثيراً مما جاء فى التوراة من نعت الرسول خشيةَ أن يُسْلِم أهل ملتهم فيزول أمرهم وتضيع مكاسبهم ولذيذ مطاعمهم، وإن مطاعمهم من هذا السبيل لهى كالنار يأكلونها، لأنها ستقودهم إلى النار، وسيعرض الله عنهم يوم القيامة، ولا يطهرهم من دنسهم، وأمامهم عذاب شديد موجع.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b836] VALUES (182,'<span data-type="title" id=toc-184>١٧٥ </span>- وأولئك هم الآثمون الذين اختاروا الضلالة على الهدى فاستحقوا العذاب فى الآخرة بدل الغفران، فكانوا كمن يشترى الباطل بالحق، وما فيه ضلال بما فيه هداية، وإن حالهم لتدعو إلى العجب، إذ يصبرون على موجبات العذاب ويستطيبون ما يؤدى بهم إليه.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b836] VALUES (183,'<span data-type="title" id=toc-185>١٧٦ </span>- ولقد استوجبوا ما قدر لهم من الجزاء لكفرهم بكتاب الله الذى أنزله بالحق والصدق، ولقد اختلفوا فيه اختلافاً كبيراً، دفع إليه حب الجدل ومجانبة الحق والانقياد للهوى، فحرفوه وأفسدوه وفسروه بغير معانيه.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b836] VALUES (184,'<span data-type="title" id=toc-186>١٧٧ </span>- لقد أكثر الناسُ الكلام فى أمر القبلة كأنها هى وحدها الخير، وليس هذا هو الحق، فليس استقبال جهة معينة فى المشرق أو المغرب هو قوام الدين وجماع الخير، ولكن ملاك الخير عدة أمور بعضها من أركان العقيدة الصحيحة، وبعضها من أمهات الفضائل والعبادات، فالأول هو: الإيمان بالله ويوم البعث والنشور والحساب وما يتبعه يوم القيامة، والإيمان بالملائكة وبالكتب المنزلة على الأنبياء وبالأنبياء أنفسهم. والثانى هو: بذل المال عن رغبة وطيب نفس للفقراء من الأقارب واليتامى، ولمن اشتدت حاجتهم وفاقتهم من الناس، وللمسافرين الذين انقطع بهم الطريق فلا يجدون ما يبلغهم مقصدهم، وللسائلين الذين ألجأتهم الحاجة إلى السؤال، ولغرض عتق الأرقاء وتحرير رقابهم من الرق. والثالث: المحافظة على الصلاة. والرابع: إخراج الزكاة المفروضة. والخامس: الوفاء بالعهد فى النفس والمال. والسادس: الصبر على الأذى ينزل بالنفس أو المال، أو وقت مجاهدة العدو فى مواطن الحروب فالذين يجمعون هذه العقائد والأعمال الخيرة هم الذين صَدَقوا فى إيمانهم، وهم الذين اتقوا الكفر والرذائل وتجنبوها.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b836] VALUES (185,'<span data-type="title" id=toc-187>١٧٨ </span>- ومن الشرائع التى فرضناها على المؤمنين أحكام القتل العمد، فقد فرضنا عليكم القصاصَ بسبب القتل، ولا تأخذوا بظلم أهل الجاهلية الذين كانوا يقتلون الحر غير القاتل بالعبد، والذكر الذى لم يقتل بالأنثى، والرئيس غير القاتل بالمرءوس القاتل دون مجازاة القاتل نفسه، فالحر القاتل يقتل بالحر المقتول، وكذلك العبد بالعبد والأنثى بالأنثى، فأساس القصاص هو دفع الاعتداء فى القتل بقتل القاتل للتشفى ومنع البغى، فإن سَمَت نفوس أهل الدم ودفعوا بالتى هى أحسن فآثروا العفو عن إخوانهم وجب لهم دية قتيلهم، وعلى أولياء الدم اتباع',NULL,'39',NULL,NULL,'0');
INSERT INTO [b836] VALUES (186,'هذا الحكم بالتسامح دون إجهاد للقاتل أو تعنيف، وعلى القاتل أداء الدين دون مماطلة أو بخس، وفى حكم القتل الذى فرضناه على هذا الوجه تخفيف على المؤمنين بالنسبة إلى حكم التوراة الذى يوجب فى القتل القصاص، كما فيه رحمة بهم بالنسبة إلى الذين يدعون إلى العفو من غير تعرض للقاتل، فمن جاوز هذا الحكم بعد ذلك فله عذاب أليم فى الدنيا والآخرة.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b836] VALUES (187,'<span data-type="title" id=toc-189>١٧٩ </span>- وإن رحمة الله بكم لعظيمة فى فرض القصاص عليكم، فبفضل القصاص عليكم تتحقق للمجتمع حياة آمنة سليمة. وذلك أن من يهم بالقتل إذا علم أن فى ذلك هلاك نفسه لم ينفذ ما هَمَّ به، وفى ذلك حياته وحياة من هَمَّ بقتله، وإذا قتل الرئيس بالمرءوس وغير المذنب بالمذنب - كما هو شأن الجاهلية - كان ذلك مثاراً للفتن واختلال النظام والأمن. فلْيتدبر أولو العقول مزية القصاص فإن ذلك يحملهم على إدراك لطف الله بهم إلى سبيل التقوى وامتثال أوامر الله سبحانه.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b836] VALUES (188,'<span data-type="title" id=toc-190>١٨٠ </span>- وكما شرع الله القصاص لصلاح الأمة وحفظ المجتمع، كذلك شرع الله شريعة فيها صلاح الأسرة وحفظ كيانها وهى شريعة الوصية، فعلى من ظهرت أمامه إمارات الموت وعلم أنه ميت لا محالة، وكان ذا مال يعتد به أن يجعل من ماله نصيباً لمن يدرك من والديه وأقاربه - الأقربين غير الوارثين - وليراع فى ذلك ما يحسن ويقبل فى عرف العقلاء فلا يعطى الغنى ويدع الفقير، بل يؤثر ذوى الحاجة ولا يسوى إلا بين المتساوين فى الفاقة، وكان ذلك الفرض حقاً واجباً على من آثر التقوى واتبع أوامر الدين.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b836] VALUES (189,'<span data-type="title" id=toc-191>١٨١ </span>- وإذا صدرت الوصية عن الموصى كانت حقاً واجباً لا يجوز تغييره ولا تبديله، إلا إذا كانت الوصية مجافية للعدل، فمن بدّل هذا الحق فغيَّر الوصية العادلة القويمة بعد ما علم هذا الحكم وثبت عنده فقد ارتكب ذنباً عظيماً ينال عقابه، وقد برئ الموصى من تبعته، ولا يظن أحد أن يفعل ذلك ولا يجازى عليه، فإن الله سميع عليم لا تخفى عليه خافية.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b836] VALUES (190,'<span data-type="title" id=toc-192>١٨٢ </span>- أما إذا كانت الوصية زائغة عن العدل وعن الصراط القويم الذى بَيَّنَّاه بأن حَرَم الموصِى الفقيرَ وأعطى الغنى، أو ترك الأقربين وراعى الفقراء غير الوارثين الأجانب، فسعى ساع فى سبيل الخير وأصلح بين الموصى إليهم ليرد الوصية إلى الصواب، فلا إثم عليه فيما يحدثه من تغيير الوصية وتبديلها على هذا الوجه، ولا يؤاخذه الله على ذلك، فإن الله غفور رحيم.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b836] VALUES (191,'<span data-type="title" id=toc-193>١٨٣ </span>- وكما شرع الله لكم القصاص والوصية لصلاح مجتمعكم، والحفاظ على أسركم، شرع الله كذلك فريضة الصيام تهذيباً لنفوسكم، وتقويماً لشهواتكم، وتفضيلا لكم على الحيوان الأعجم الذى ينقاد لغرائزه وشهواته، وكان فرض الصيام عليكم مثل ما فرض على من سبقكم من الأمم فلا يشق عليكم أمره. لأنه فرض على الناس جميعاً، وكان وجوب الصيام والقيام به، لتتربى فيكم روح التقوى، ويقوى وجدانكم، وتتهذب نفوسكم.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b836] VALUES (192,'<span data-type="title" id=toc-194>١٨٤ </span>- وفرض الله عليكم الصيام فى أيام معدودة قليلة لو شاء سبحانه لأطال مدته ولكنه لم يطلها، ولم يكلفكم فى الصوم ما لا تطيقون، فمن كان مريضاً مرضاً يضر معه الصوم، أو كان فى سفر، فله أن يفطر ويقضى الصوم بعد برئه من المرض أو رجوعه من السفر، أما غير المريض والمسافر ممن لا يستطيع الصوم إلا بمشقة لعذر دائم كشيخوخة ومرض لا يرجى برؤه فله الفطر حينئذٍ، وعليه أن يطعم مسكيناً لا يجد قوت يومه، ومن صام متطوعاً زيادة على الفرض فهو خير له، لأن الصيام خير دائماً لمن يعلم حقائق العبادات.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b836] VALUES (193,'<span data-type="title" id=toc-195>١٨٥ </span>- وهذه الأيام هى شهر رمضان الجليل القدر عند الله، لقد أنزل فيه القرآن يهدى جميع الناس إلى الرشد ببيِّاناته الواضحة الموصلة إلى الخير، والفاصلة بين الحق والباطل على مَرِّ العصور والأجيال، فمن أدرك هذا الشهر سليماً غير مريض، مقيماً غير مسافر فعليه صومه، ومن كان مريضاً مرضاً يضر معه الصوم أو كان فى سفر فله أن يفطر وعليه قضاء صيام ما أفطره من أيام الصوم، فإن الله لا يريد أن يَشُقَّ عليكم فى التكاليف وإنما يريد لكم اليسر، وقد بين لكم شهر الصوم وهداكم إليه لتكملوا عدة الأيام التى تصومونها وتكبروا الله على هدايته إياكم وحسن توفيقه.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b836] VALUES (194,'<span data-type="title" id=toc-196>١٨٦ </span>- وإنى مطلع على العباد، عليم بما يأتون وما يذرون، فإذا سألك - يا محمد - عبادى قائلين: هل الله قريب منا بحيث يعلم ما نخفى وما نعلن وما نترك؟ فقل لهم: إنى أقرب إليهم مما يظنون، ودليل ذلك أن دعوة الداعى تصل فى حينها، وأنا الذى أجيبها فى حينها كذلك، وإذا كنت استجبت لها فليستجيبوا هم لى بالإيمان والطاعة فإن ذلك سبيل إرشادهم وسدادهم.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b836] VALUES (195,'<span data-type="title" id=toc-197>١٨٧ </span>- أحَلَّ الله لكم ليلة الصوم إتيان نسائكم لاختلاطكم بهن واختلاطهن بكم فى النهار والمبيت، ولعسر ابتعادكم عنهن وتخفيفاً عليكم. وقد علم الله أنكم كنتم تنقصون حظ نفوسكم وتظلمونها، فتحرمون عليها إتيان النساء فى ليل رمضان فتاب عليكم من الغلو وعفا عنكم، والآن وقد تبين لك حِلُّ ذلك فلا تتحرجوا من مباشرتهن، وتمتعوا بما أباحه الله لكم وكلوا واشربوا فى ليل رمضان حتى يظهر لكم نور الفجر، متميزاً من ظلام الليل، كما يتميز الخيط الأبيض من الخيط الأسود، وإذا ظهر ذلك فصوموا وأتموا الصيام إلى غروب الشمس.
INSERT INTO [b836] VALUES (196,'<span data-type="title" id=toc-198>١٨٨ </span>- وقد حرَّم الله عليكم أكل مال غيركم دون وجه من الحق دائماً، فلا يستحل أحدكم مال غيره إلا بوجه من الوجوه التى شرعها الله كالميراث والهبة والعقد الصحيح المبيح للملك، وقد ينازع أحدكم أخاه فى المال وهو مبطل، ويرفع أمره إلى الحاكم أو القاضى ليحكم له وينتزع من أخيه ماله بشهادة باطلة أو بينة كاذبة، أو رشوة خبيثة، فبئس ما يفعل وما يجرُّ على نفسه من سوء الجزاء.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b836] VALUES (197,'<span data-type="title" id=toc-199>١٨٩ </span>- ويسألك قوم عن الهلال يبدو دقيقاً مثل الخيط ثم يزيد حتى يكتمل ويستوى، ثم لا يزال ينقص حتى يعود كما بدأ، ولا يكون على حالة واحدة كالشمس. فما وراء هذا التغير، حتى صار فى كل شهر هلال وصارت هناك أهلة؟ . فقل لهم: إن لتكرار هذه الأهلة واختلاف نموها حِكَماً ومصالح دينية ودنيوية، فهى أمارات تحدد أوقات المعاملات فى معاشكم، وتعيِّن أوقات الحج الذى هو من أركان دينكم، ولو استقر الهلال على حاله كالشمس ما استقام لكم توقيت معاشكم وحجكم، وليس جهلكم بحكمة اختلاف الهلال مدعاة للشك فى حكمة الخالق، وليس من البر أن تأتوا البيوت من ظهورها، متميزين بذلك عن الناس، ولكن البر هو تقوى القلوب وإخلاصها وأن تأتوا البيوت من أبوابها كما يأتى كل الناس، وأن تطلبوا الحق والدليل المستقيم، فاطلبوا رضا الله، واتقوا عذابه، وارجوا بذلك فَلاَحَكُمْ وفوزكم ونجاتكم من عذاب النار.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b836] VALUES (198,'<span data-type="title" id=toc-200>١٩٠ </span>- ومن تقوى الله تحمل المشاق فى طاعته، وأشد المشاق على النفس هو قتال أعداء الله ولكن إذا اعتدى عليكم فقاتلوا المعتدين، وقد أذن لكم برد اعتداءاتهم، ولكن لا تعتدوا بمبادأتهم أو بقتل من لا يقاتل ولا رأى له فى القتال فإن الله لا يحب المعتدين.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b836] VALUES (199,'<span data-type="title" id=toc-201>١٩١ </span>- واقتلوا أولئك الذين بدأوكم بالقتال حيث وجدتموهم، وأخرجوكم من مكة وطنكم الذى حملوكم على الخروج منه، ولا تتحرجوا من ذلك فقد فعلوا ما هو أشد من القتل فى المسجد الحرام إذ حاولوا فتنة المؤمنين عن دينهم بالتعذيب فى مكة حتى فروا بدينهم من وطنهم، ولكن للمسجد الحرام حرمته فلا تنتهكوها إلا إذا انتهكوها هم بقتالكم فيه، فإن قاتلوكم فاقتلوهم وأنتم الغالبون بفضل الله، وكذلك جزاء الكافرين يفعل بهم ما يفعلونه بغيرهم.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b836] VALUES (200,'<span data-type="title" id=toc-202>١٩٢ </span>- فإن رجعوا عن الكفر ودخلوا فى طاعة الإسلام، فإن الإسلام يجبُّ ما قبله، والله يغفر لهم ما سلف من كفرهم بفضل منه ورحمة.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b836] VALUES (201,'<span data-type="title" id=toc-203>١٩٣ </span>- وقاتلوا هؤلاء الذين حاولوا قتلكم وصدكم عن دينكم بالإيذاء والتعذيب، حتى تستأصل جذور الفتنة ويخلص الدين لله. فإن انتهوا عن كفرهم فقد نجوا أنفسهم وخلصوا من العقاب، فلا ينبغى الاعتداء عليهم حينئذٍ وإنما العدوان على من ظلم نفسه وأوبقها بالمعاصى وتجاوز العدل فى القول والفعل.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b836] VALUES (202,'<span data-type="title" id=toc-204>١٩٤ </span>- فإذا اعتدوا عليكم فى الشهر الحرام فلا تقعدوا عن قتالهم فيه فإنه حرام عليهم، كما هو حرام عليكم، وإذا انتهكوا حرمته عندكم فقابلوا ذلك بالدفاع عن أنفسكم فيه، وفى الحرمات والمقدسات شرع القصاص والمعاملة بالمثل، فمن اعتدى عليكم فى مقدساتكم فادفعوا هذا العدوان بمثله، واتقوا الله فلا تسرفوا فى المجازاة والقصاص، واعلموا أن الله ناصر المتقين.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b836] VALUES (203,'<span data-type="title" id=toc-205>١٩٥ </span>- جهاد الكفار يكون ببذل النفس كما يكون ببذل المال، فأنفقوا فى الإعداد للقتال، واعلموا أن قتال هؤلاء فى سبيل الله، فلا تقعدوا عنه، وابذلوا الأموال فيه فإنكم إن تقاعدتم وبخلتم ركبكم العدو وأذلكم فكأنما ألقيتم أنفسكم بأيديكم إلى الهلاك، فافعلوا ما يجب عليكم بإحسان وإتقان، فإن الله يحب إذا عمل أحدكم عملا أن يحسنه.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b836] VALUES (204,'<span data-type="title" id=toc-206>١٩٦ </span>- وأدوا الحج والعمرة لله على وجه التمام والكمال قاصدين بهما وجه الله، ولا تقصدوا بهما إصابة عَرَض دنيوى من شهرة ونحوها. وإذا قصدتم الحج والعمرة وأحرمتم بهما فمنعكم عدو فى الطريق فلكم أن تتحللوا من إحرامكم بحلق رءوسكم، ولكن عليكم قبل ذلك ذبح ما تيسر لكم - من شاة أو بعير أو بقرة - والتصدق به على المساكين، ولا تحلقوا رءوسكم حتى تقوموا بهذه النسك، ومن كان مُحْرِماً وأذاه شعر رأسه لمرض أو هوام فى رأسه فلا بأس أن يحلق رأسه، وعليه حينئذٍ أن يفدى عن ذلك بصيام ثلاثة أيام، أو التصدق على ستة مساكين بقوت يوم، أو ذبح شاة والتصدق بها على الفقراء والمساكين. وإذا كنتم فى دار الأمان والسلم ولم يعترض طريقكم عدو، وقصدتم الحج والعمرة وتمتعتم أولا بالعمرة إلى أن يحين وقت الحج فتحرموا، فعليكم ذبح شاة لمساكين الحرم وفقرائه، فمن لم يجد شاة أو لم يقدر على ثمنها صام ثلاثة أيام فى مكة وسبعة أيام إذا رجع إلى أهله، وهذا على من لم يكن من أهل مكة، فمن كان من أهلها فلا شئ عليه إذا تمتع.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b836] VALUES (205,'<span data-type="title" id=toc-207>١٩٧ </span>- والحج يقع فى أشهر معلومة لكم، إذ كان أمره معروفاً عندكم من عهد إبراهيم - عليه السلام - وهى شوال وذو القعدة وذو الحجة، فمن فرض الحج على نفسه فى هذه الأشهر ودخل فيه فليراع آدابه، ومن آداب الحج أن يتنزه المحرم عن مباشرة النساء، وعن المعاصى من السباب وغيره، وعن الجدل والمراء مع غيره من الحجيج، وعن كل ما يجر إلى الشحناء والخصام حتى يخرج المحرم مهذب النفس، وليجتهد فى فعل الخير، وطلب الأجر من الله بالعمل الصالح فإن الله عليم بذلك ومجاز عليه، وتزودوا لآخرتكم بالتقوى والائتمار بأوامر الله واجتناب نواهيه، فإن ذلك خير الزاد، واستشعروا خشية الله فيما تأتون وما تذرون كما هو مقتضى العقل والحكمة، فلا تشوبوا أفعالكم بدواعى الهوى والغرض الدنيوى.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b836] VALUES (206,'<span data-type="title" id=toc-208>١٩٨ </span>- ولقد كان منكم من يجد حرجاً فى مزاولة التجارة وابتغاء الرزق فى موسم الحج، فلا حرج عليكم فى ذلك، بل لكم أن تزاولوا التكسب بطرقه المشروعة وتبتغوا فضل الله ونعمته، وإذا صدر الحجاج راجعين من عرفات بعد الوقوف بها، ووصلوا المزدلفة ليلة عيد النحر فليذكروا الله عند المشعر الحرام - وهو جبل المزدلفة - بالتهليل والتلبية والتكبير، وليمجدوه وليحمدوه على هدايته إياهم إلى الدين الحق والعبادة القويمة فى الحج وغيره، وقد كانوا من قبل ذلك فى ضلال عن صراط الهدى والرشاد.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b836] VALUES (207,'<span data-type="title" id=toc-209>١٩٩ </span>- وقد كان قوم من العرب - وهم قريش - لا يقفون مع الناس فى عرفات مع علمهم أنه موقف أبيهم إبراهيم، وذلك ترفعاً أن يساووا غيرهم وهم أهل بيت الله وقطان حرمه، وزعماً منهم أن ذلك تعظيم للحرم الذى لا يريدون الخروج منه إلى عرفات، وهى من الحلال لا من الحرام، فطالبهم الله بأن يقلعوا عن عادات الجاهلية ويقفوا بعرفات ويصدروا عنها كما يصدر جمهور الناس، فلا فضل لأحد على الآخر فى أداء العبادة، وعليهم أن يستغفروا الله فى هذه المواطن المباركة فذلك أدعى أن يغفر الله لهم ما فرط منهم من الذنوب والآثام ويرحمهم بفضله.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b836] VALUES (208,'<span data-type="title" id=toc-210>٢٠٠ </span>- وإذا فرغتم من أعمال الحج وشعائره فدعوا ما كنتم عليه فى الجاهلية من التفاخر بالآباء وذكر مآثرهم، وليكن ذكركم وتمجيدكم لله فاذكروه كما كنتم تذكرون آباءكم، بل اذكروه أكثر من ذكر آبائكم لأنه ولى النعمة عليكم وعلى آبائكم، ومواطن الحج هى مواطن الدعاء وسؤال الفضل والخير والرحمة من عند الله، وقد كان فريق من الحجاج يقصر دعاءه على عرض الدنيا وخيراتها ولا يلقى بالاً للآخرة فهذا لا نصيب له فى الآخرة.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b836] VALUES (209,'<span data-type="title" id=toc-211>٢٠١ </span>- ومن الناس من وفَّقه الله فاتجه بقلبه إلى طلب خيرى الدنيا والآخرة، ودعا الله أن يجنبه شر النار وعذابها.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b836] VALUES (210,'<span data-type="title" id=toc-212>٢٠٢ </span>- فهؤلاء يعطون ما قُدِّر لهم مما كسبوه بالطلب والركون إلى الله. والله يجزى كلاً بما يستحق، وهو سريع الحساب والجزاء.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b836] VALUES (211,'<span data-type="title" id=toc-213>٢٠٣ </span>- واذكروا الله بالتكبير والتهليل والتحميد فى أيام معدودات هى أيام رمى الجمار بمنى وهى: الحادى عشر. والثانى عشر. والثالث عشر. وليس بلازم لأن قوام الخير تقوى الله لا مقدار العدد، واتقوا الله دائماً واعلموا أنكم إليه تحشرون مسئولون عن أعمالكم.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b836] VALUES (212,'<span data-type="title" id=toc-214>٢٠٤ </span>- وإذا كانت تقوى الله هى الأساس فالخسران لفريق من الناس يختلف الذى تضمره قلوبهم عن الذى تنطق به ألسنتهم، أوتوا حلاوة فى صوغ الكلام، يعجبك قولهم فيما يحتالون به على جلب المنفعة فى الحياة الدنيا، ويؤيدون لك بزعمهم بأن الله يعلم صدق قلوبهم فيما تقوله ألسنتهم، وإنهم لأشد الناس خصومة لك وأقساهم عليك.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b836] VALUES (213,'<span data-type="title" id=toc-215>٢٠٥ </span>- وإذا تولى ولاية يكون له فيها سلطان لا يكون سعيه للإصلاح، بل للإفساد وإهلاك الزرع والنسل، والله لا يحبه، لأن الله تعالى لا يحب الفساد.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b836] VALUES (214,'<span data-type="title" id=toc-216>٢٠٦ </span>- وإذا نصحت له حينئذٍ بالخوف من الله ثارت فى نفسه الحمية وظن ذلك هدماً لعزته، وحمله على ارتكاب الإثم فيما نهيته عنه لجاجة وعناداً، فحسبه على ذلك عذاب جهنم ولبئس المستقر.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b836] VALUES (215,'<span data-type="title" id=toc-217>٢٠٧ </span>- فما أبعد الفرق بين هؤلاء المنافقين وبين المؤمنين الصادقين الذين يبيع أحدهم نفسه فى سبيل مرضاة الله، وإعلاء كلمة الحق، ويكون هذا النوع من الناس مقابلا للنوع الأول، ويكون تولية أمراً من أمور الناس من رأفة الله بعباده، والله تعالى يرحمهم بجعل الولاية لهؤلاء ليدفع بهم أذى الأشرار.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b836] VALUES (216,'<span data-type="title" id=toc-218>٢٠٨ </span>- يا أيها الذين آمنوا كونوا جميعاً مسالمين فيما بينكم، ولا تثيروا العصبيات الجاهلية وغيرها من أسباب النزاع والخلاف، ولا تسيروا فى طريق الشيطان الذى يدفعكم إلى الشقاق فإنه لكم عدو مبين.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b836] VALUES (217,'<span data-type="title" id=toc-219>٢٠٩ </span>- فإن انحرفتم عن هذا الطريق الذى دعيتم إليه جميعاً من بعد ظهور الحجج القاطعة على أنه طريق الحق، فاعلموا أنكم مؤاخذون بهذا الانحراف لأن الله عزيز يعاقب من يعرض عن سبيله، حكيم يقدر العقوبة بقدرها.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b836] VALUES (218,'<span data-type="title" id=toc-220>٢١٠ </span>- وهل ينتظر هؤلاء المعرضون عن الإسلام ليقتنعوا أن يروا الله تعالى جهرة فى غمامٍ مع الملائكة وقد قضى الأمر بقطع مطامعهم، لأن الشئون جميعاً فى قبضة الله يصرفها هو حيث يشاء وقد قضى فيها قضاءه الذى سينفذ لا محالة.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b836] VALUES (219,'<span data-type="title" id=toc-221>٢١١ </span>- سل بنى إسرائيل كم سقنا إليهم الأدلة القاطعة على صدق الرسول، وفى ذلك نعمة هدايتهم إلى الله، فكفروا بهذه الأدلة، وعمدوا بتكذيبهم لها إلى تبديل الغرض منها، فبعد أن كانت هذه الآيات للهداية أصبحت بالنسبة لكفر هؤلاء بها سبباً فى زيادة ضلالهم وإثمهم، ومن يبدل نعم الله بهذه الصورة يحق عليه العذاب لأن الله شديد العقاب.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b836] VALUES (220,'<span data-type="title" id=toc-222>٢١٢ </span>- وإنّ السبب فى الانحراف والكفر هو طلب الدنيا، فقد زين للذين كفروا شهوات الحياة الدنيا فمضوا يسخرون من الذين آمنوا لانشغالهم بالحياة الآخرة، والله جاعل الذين آمنوا أعلى مكاناً منهم فى الآخرة. فأما توفر المال وزينة الحياة الدنيا لدى الكفار فلا تدل على أفضليتهم، لأن رزق الله لا يُقدَّر على حساب الإيمان والكفر بل يجرى تبعاً لمشيئته، فمن الناس من يزاد له فى الرزق استدراجاً ومنهم من يقتر عليه اختباراً.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b836] VALUES (221,'<span data-type="title" id=toc-223>٢١٣ </span>- وإنَّ الناس طبيعة واحدة فيها الاستعداد للضلالة، ومنهم من تستولى عليه أسباب الهداية، ومنهم من تغلب عليه الضلالة، ولذلك اختلفوا، فبعث الله إليهم الأنبياء هداة ومبشرين ومنذرين، وأنزل معهم الكتب مشتملة على الحق، لتكون هى الحكم بين الناس فينقطع التنازع، ولكن الذين انتفعوا بهدى النبيين هم الذين آمنوا فقط، والذين هداهم الله فى موضع الاختلاف إلى الحق، والله هو الذى يوفق أهل الحق إذا أخلصوا.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b836] VALUES (222,'<span data-type="title" id=toc-224>٢١٤ </span>- فهل حسبتم أن تدخلوا الجنة بمجرد إقراركم بكلمة الإسلام بدون أن تصابوا بمثل ما أصاب الذين من قبلكم، فقد أصابتهم الشدائد والنوازل وزلزلوا حتى بلغ بهم الأمر أن قال رسولهم نفسه وقالوا معه: متى نصر الله؟ فَيبِرُّ ربهم بوعده فيجابون عندئذٍ بأن نصر الله قريب.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b836] VALUES (223,'<span data-type="title" id=toc-225>٢١٥ </span>- يسألك المؤمنون فى شأن الإنفاق فقل لهم: إن الإنفاق يكون من المال الطيب، ويعْطَى للوالدين والأقربين واليتامى والمساكين ومن انقطع عن ماله وأهله، وما تفعلوا من عمل خير فإن الله يعلمه وهو يثيبكم عليه.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b836] VALUES (224,'<span data-type="title" id=toc-226>٢١٦ </span>- فإذا كان فى الإنفاق على اليتامى والمساكين وغيرهم حماية للمجتمع فى داخله فإن القتال حماية له من أعدائه فى الخارج، ولذلك فرض عليكم - أيها المسلمون - القتال لحماية دينكم والدفاع عن أنفسكم، وأن نفوسكم بحكم جبلتها تكره القتال كرهاً شديداً، ولكن ربما كرهتم ما فيه خيركم وأحببتم ما فيه شركم، والله يعلم ما غاب من مصالحكم عنكم، وأنتم لا تعلمون فاستجيبوا لما فرض عليكم.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b836] VALUES (225,'<span data-type="title" id=toc-227>٢١٧ </span>- وقد كره المسلمون القتال فى الشهر الحرام فسألوك عنه، فقل لهم: نعم إن القتال فى الشهر الحرام إثم كبير، ولكن أكبر منه ما حدث من أعدائكم من صد عن سبيل الله وعن المسجد الحرام، وإخراج المسلمين من مكة، وقد كان إيذاؤهم للمسلمين لإخراجهم من دينهم أكبر من كل قتل،',NULL,'49',NULL,NULL,'0');
INSERT INTO [b836] VALUES (226,'ولذلك أُبيح القتال فى الشهر الحرام لقمع هذه الشرور، فهو عمل كبير يُتقى به ما هو أكبر منه. واعلموا - أيها المسلمون - أن سبيل هؤلاء معكم سبيل التجنى والظلم، وأنهم لا يقبلون منكم العدل والمنطق، ولا يزالون يقاتلونكم حتى يردوكم عن دينكم إن استطاعوا، ومن يضعف أمام هجماتهم ويرتد عن دينه حتى يموت على الكفر فأولئك بطلت أعمالهم الصالحة فى الدنيا والآخرة، وأولئك أهل النار هم فيها خالدون.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b836] VALUES (227,'<span data-type="title" id=toc-229>٢١٨ </span>- وإن الذين آمنوا إيماناً صادقاً دفعهم إلى الهجرة لنصرة الدين والجهاد لإعلاء كلمته فأولئك ينتظرون عظيم ثواب الله لهم، وإن قصروا فى شئ، لأن الله غفور يغفر الذنوب، رحيم يرحم عباده بالهداية والثواب.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b836] VALUES (228,'<span data-type="title" id=toc-230>٢١٩ </span>- ويسألونك - يا محمد - عن حكم الخمر والقمار، فقل: إن فيهما ضرراً كبيراً من إفساد الصحة وذهاب العقل والمال وإثارة البغضاء والعدوان بين الناس، وفيهما منافع وبعض المنافع الصحية والربح السهل، ولكن ضررهما أكبر من نفعهما فاجتنبوهما. ويسألونك عمَّا ينفقون، فأجبهم أن ينفقوا فى ذات الله السهل اليسير الذى لا يشق عليكم إنفاقه، كذلك يبين الله لكم الآيات لعلكم تتفكرون فيما يعود عليكم من مصالح الدنيا والآخرة.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b836] VALUES (229,'<span data-type="title" id=toc-231>٢٢٠ </span>- ويسألونك بشأن اليتامى والذى يوجبه الإسلام حيالهم، فقل: إن الخير لكم ولهم فى إصلاحهم، وأن تضمُّوهم إلى بيوتكم، وأن تخالطوهم بقصد الإصلاح لا الفساد، فهم إخوانكم فى الدنيا يستدعون منكم هذه المخالطة، والله يعلم المفسد من المصلح منكم فاحذروا. ولو شاء الله لشق عليكم، فألزمكم رعاية اليتامى من غير مخالطة لهم، أو تركهم من غير بيان الواجب لهم، فيربون على بغض الجماعة ويكون ذلك إفساداً لجماعتكم وإعناتاً لكم، إذ إن قهرهم وذلهم يجعل منهم المبغضين للجماعة المفسدين فيها، وإن الله عزيز غالب على أمره، ولكنه حكيم لا يشرع إلا ما فيه مصلحتكم.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b836] VALUES (230,'<span data-type="title" id=toc-232>٢٢١ </span>- وإذا كانت مخالطة اليتامى لا حرج فيها فإن الحرج فى مخالطة أهل الشرك، فلا ينكح المؤمن مشركة لا تدين بكتاب سماوى، ولا يحمل المرء منكم على زواج المشركة مالها وجمالها وحسبها ونسبها. فالمؤمنة التى وقع عليها الرق خير من المشركة الحرة ذات المال والجمال والحسب والنسب، ولا يزوج المرء منكم من له عليه ولاية من النساء مشركاً لا يؤمن بالكتب السماوية، ولا يبعث أحدكم على إيثار المشرك غناه وشرفه، فخير منه العبد المؤمن، فأولئك المشركون يجتذبون عشراءهم إلى المعصية والشرك فيستوجبون النار. والله إذ يدعوكم إلى اعتزال المشركين فى النكاح يدعوكم إلى ما فيه صلاحكم ورشادكم لتنالوا الجنة والمغفرة، وتسيروا فى طريق الخير بتيسيره، والله يبين شرائعه وهديه للناس لعلهم يعرفون صلاحهم ورشادهم.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b836] VALUES (231,'<span data-type="title" id=toc-233>٢٢٢ </span>- ويسألونك عن إتيان الزوجات زمن المحيض، فأجبهم: أن المحيض أذى فامتنعوا عن إتيانهن مدته، ولا تأتوهن حتى يطهرن، فإذا تطهرن فأتوهن فى المكان الطبيعى، ومن كان وقع منه شئ من ذلك فليتب، فإن الله يحب من عباده كثرة التوبة والطهارة من الأقذار والفحش.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b836] VALUES (232,'<span data-type="title" id=toc-234>٢٢٣ </span>- زوجاتكم هن موضع النسل كموضع البذر ينبت النبات، فيباح لكم أن تأتوهن على أية طريقة تشاءون إذا كان ذلك فى موضع نسل، واتقوا الله أن تعصوه فى مخالطة المرأة، واعلموا أنكم ملاقوه ومسئولون عنده، والبشرى للذين يقفون عند حدوده تعالى فلا يتعدونها.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b836] VALUES (233,'<span data-type="title" id=toc-235>٢٢٤ </span>- لا تجعلوا اسم الله معرَّضاً لكثرة الحلف به، لأن ذلك ينافى تعظيم اسم الله، وأن الامتناع عن كثرة الحلف باسم الله يؤدى إلى البر والتقوى والقدرة على الإصلاح بين الناس، إذ يكون الممتنع جليل القدر فى أعين الناس موثوقاً به بينهم فيقبل قوله، والله سميع لأقوالكم وأيمانكم، عليم بنياتكم.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b836] VALUES (234,'<span data-type="title" id=toc-236>٢٢٥ </span>- عفا الله عنكم فى بعض الأيمان، فما جرى على الألسنة من صور الأيمان ولم يصحبه قصد ولا عقد قلب، أو كان يحلف على شئ يعتقده حصل وهو لم يحصل فإن الله لا يؤاخذ عليه، ولكن يؤاخذكم بما كسبت قلوبكم من عزم على إيقاع فعل أو عدم إيقاعه. وعلى الكذب فى القول مع التوثيق باليمين، فالله غفور لمن يتوب، حليم يعفو عما لا يكتسبه القلب.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b836] VALUES (235,'<span data-type="title" id=toc-237>٢٢٦ </span>- وهؤلاء الذين حلفوا ألا يقربوا نساءهم يُمْهَلُونَ أربعة أشهر، فإن أتوا نساءهم فى أثنائها استمر الزواج وعليهم كفارة اليمين وغفر لهم وتقبل منهم الكفارة رحمة بهم.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b836] VALUES (236,'<span data-type="title" id=toc-238>٢٢٧ </span>- وإن لم يأتوا نساءهم فى هذه المدة كان ذلك إضراراً بالمرأة، فليس إلا الطلاق، والله سميع لأيمانهم عليم بأحوالهم ومحاسبهم على ذلك يوم القيامة.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b836] VALUES (237,'<span data-type="title" id=toc-239>٢٢٨ </span>- وعلى المطلقات أن ينتظرن دون التطلع إلى زواج يستأنف مدة ثلاث حيضات، استبراء للرحم، وفسحة لاحتمال المراجعة، ولا يحل لهنَّ أن يكتمن ما يكون فى أرحامهن من جنين أو دم حيض، وذلك شأن المؤمنات بالله ولقائه فى اليوم الآخر، وأزواجهن لهم الحق فى إرجاعهن للزوجية ثانياً مدة العدة، وعلى',NULL,'52',NULL,NULL,'0');
INSERT INTO [b836] VALUES (238,'الأزواج عند استعمال هذا الحق أن يقصدوا إلى الإصلاح لا المضرة، وللزوجات من الحقوق مثل ما عليهن من الواجبات بما لا ينكره الشرع الشريف، وللرجال عليهن درجة الرعاية والمحافظة على الحياة الزوجية وشئون الأولاد والله سبحانه فوق عباده يشرع لهم ما يتفق مع الحكمة.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b836] VALUES (239,'<span data-type="title" id=toc-241>٢٢٩ </span>- الطلاق مرَّتان يكون للزوج بعد كل واحدة منها الحق فى أن يمسك زوجته برجعتها فى العدة أو إعادتها إلى عصمته بعقد جديد، وفى هذه الحال يجب أن يكون قصده الإمساك بالعدل والمعاملة بالحسنى، أو أن ينهى الحياة الزوجية مع المعاملة الحسنة وإكرامها من غير مجافاة. ولا يحل لكم - أيها الأزواج - أن تأخذوا مما أعطيتموهن شيئا إلا عند خشية عدم إقامة حقوق الزوجية التى بينها الله سبحانه وتعالى وألزم بها. فإن خفتم - يا معشر المسلمين - ألا تؤدِّى الزوجات حقوق الزوجية سليمة كما بينها الله فقد شرع للزوجة أن تقدم مالا فى مقابل افتراقها عن زوجها، وهذه هى أحكام الله المقررة فلا تخالفوها وتتجاوزوها لأن من يفعل ذلك ظالم لنفسه وظالم للمجتمع الذى يعيش فيه.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b836] VALUES (240,'<span data-type="title" id=toc-242>٢٣٠ </span>- فإن طلق الزوج امرأته مرة ثالثة بعد التطليقتين السابقتين فلا تحل له حينئذ إلا بعد أن تتزوج زوجاً غيره ويدخل بها، فإن طلقها من بعد ذلك الزوجُ الثانى وصارت أهلاً لأن يعقد عليها عقداً جديداً فلا إثم عليها ولا على زوجها الأول فى أن يستأنفا حياة زوجية جديدة بعقد جديد، وعليهما أن يعتزما إقامة حياة زوجية صالحة تراعى فيها كل الأحكام الشرعية التى حددها الله سبحانه وتعالى، وقد بُيِّنَتْ هذه الحدود لمن يؤمن بالشرع الإسلامى ويريد العلم والعمل به.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b836] VALUES (241,'<span data-type="title" id=toc-243>٢٣١ </span>- وإذا طلقتم النساء فشارفن انتهاء عدتهن، فلكم أن تراجعوهن قاصدين إقامة العدل وحسن الصحبة وعدم المضارة، ولكم أن تتركوهن لتنقضى عدتهن ملاحظين المعاملة اللائقة عند الفراق من غير جفوة، ولا يجوز أن يكون القصد من المراجعة مضارة المرأة وتطويل عدتها، ومن يفعل ذلك فقد حَرَم نفسه سعادة الحياة الزوجية وثقة الناس به واستحق سخط الله عليه، ولا تتخذوا أحكام الله فى الأسرة - التى جاءت بها الآيات وجعلت زمام الأسرة بيد الوكيل - سخرية ولهواً وعبثاً، تطلِّقون لغير سبب وترجعونها مضارة وإيذاء. واذكروا نعمة الله عليكم بتنظيم الحياة الزوجية تنظيماً عالياً، وبما أنزل عليكم من كتاب مبين للرسالة المحمدية والعلوم النافعة والأمثال والقصص التى بها تتعظون وتهتدون، واتخذوا بينكم وبين غضب الله وقاية واعلموا أن الله يعلم سركم وجهركم ونياتكم وأعمالكم وهو مجازيكم بما كنتم تعملون.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b836] VALUES (242,'<span data-type="title" id=toc-244>٢٣٢ </span>- وإذا طلقتم النساء وأتممتم عدتهن، وأرادت إحداهن أن تستأنف زواجاً جديداً من المطلق أو من رجل آخر غيره، فلا يحل للأولياء ولا للزوج المطلق أن يمنعوهنَّ من ذلك إذا تراضى الطرفان على عقد جديد وإرادة حياة كريمة تؤدى إلى حسن العشرة بينهما، ذلك يوعظ به من كان منكم يؤمن بالله وباليوم الآخر، ذلكم أدعى إلى تنمية العلاقات الشريفة فى مجتمعكم وأطهر فى نفوسكم من الأدناس والعلاقات المريبة، والله يعلم من مصالح البشر وأسرار نفوسهم ما يجهلون الوصول إليه.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b836] VALUES (243,'<span data-type="title" id=toc-245>٢٣٣ </span>- وعلى الأمهات أن يقمن بإرضاع أولادهن مدة عامين تامين مراعاة لمصلحة الطفل، إذا طلب أحد الوالدين أو كلاهما استيفاء مدة الرضاعة تامة لاحتياج الولد إليها، ويلزم الوالد - باعتبار الولد منسوباً إليه - بالإنفاق على الأمهات حينئذٍ بإطعامهن وكسوتهن على قدر طاقته بلا إسراف ولا تقتير. ولا ينبغى أن يُهضَم حق الأم فى نفقتها أو حضانة ولدها، كما لا ينبغى أن يكون الولد سبباً فى إلحاق الضرر بأبيه بأن يكلف فوق طاقته أو يحرم حقه فى ولده، وإذا مات الأب أو كان فقيراً عاجزاً عن الكسب كانت النفقة على وارث الولد لو كان له مال، فإن رغب الوالدان أو كلاهما فى فطام الطفل قبل تمام العامين وقد تراضيا على ذلك ونظرا إلى مصلحة الرضيع فلا تبعة عليهما، وإذا شئتم - أيها الأباء - أن تتخذوا مراضع للأطفال غير أمهاتهم فلا تبعة عليكم فى ذلك، ولتدفعوا إليهن ما اتفقتم عليه من الأجر بالرضا والمحاسنة، وراقبوا الله فى أعمالكم، واعلموا أنه مطلع عليها ومجازيكم بها.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b836] VALUES (244,'<span data-type="title" id=toc-246>٢٣٤ </span>- والذين يُتَوفَّوْن منكم من الرجال ويتركون زوجات لهم غير حوامل فعليهن أن يمكثن بعدهم دون تعرض للزواج مدة أربعة أشهر هلالية وعشر ليال بأيامها استبراء للرحم. فإذا انتهت هذه المدة فلا تبعة عليكم أيها الأولياء لو تركتموهن يأتين من شريف الأعمال التى يرضاها الشرع ليصلن بها إلى الزواج. فلا ينبغى أن تمنعوهن من ذلك ولا يجوز لهن أن يأتين من الأعمال ما ينكره الشرع ويأباه، فإن الله مطلع على سرائركم ويعلم أعمالكم فيحاسبكم على ما تعملون.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b836] VALUES (245,'<span data-type="title" id=toc-247>٢٣٥ </span>- ولا إثم عليكم - أيها الرجال - فى مدة العدة إذا ألمحتم للمعتدات من وفاة بالزواج وأضمرتم ذلك فى قلوبكم، فإن الله يعلم أنكم لا تصبرون عن التحدث فى شأنهن لميل الرجال إلى النساء بالفطرة، ولهذا أباح لكم التلويح دون التصريح، فلا تعطوهن وعداً بالزواج إلا أن يكون ذلك إشارة لا نكر فيها ولا فحش، ولا تبرموا عقد الزواج حتى تنقضى العدة، وأيقنوا أن الله مطلع على ما تخفونه فى قلوبكم، فخافوا عقابه ولا تقدموا على ما نهاكم عنه، ولا تيأسوا من رحمته إن خالفتم أمره فإنه واسع المغفرة يقبل التوبة من عباده ويعفو عن السيئات، كما أنه حليم لا يعجل بالعقوبة لمن انتهك المحرمات.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b836] VALUES (246,'<span data-type="title" id=toc-248>٢٣٦ </span>- ولا إثم عليكم - أيها الأزواج - ولا مهر إذا طلقتم زوجاتكم قبل الدخول بهن وقبل أن تُقدِّروا لهن مهرَا، ولكن أعطوهن عطية من المال يتمتعن بها لتخفيف آلام نفوسهن، ولتكن عن رضا وطيب خاطر، وليدفعها الغنى بقدر وسعه والفقير بقدر حاله، وهذه العطية من أعمال البر التى يلتزمها ذوو المروءات وأهل الخير والإحسان.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b836] VALUES (247,'<span data-type="title" id=toc-249>٢٣٧ </span>- وإذا طلقتم النساء قبل الدخول بهنَّ بعد تقدير مهورهن، فقد وجب لهن نصف المهر المقدر ويدفع إليهن، إلا إذا تنازلت عنه الزوجة، كما أنَّهُنَّ لا يعطين أكثر من النصف إلا إذا سمحت نفس الزوج فأعطاها المهر كله، وسماحة كل من الزوجين أكرم وأرضى عند الله وأليق بأهل التقوى فلا تتركوها، واذكروا أن الخير فى التفضل وحسن المعاملة، لأن ذلك أجلب للمودة والتحاب بين الناس، والله مطلع على ضمائركم وسيجازيكم على ما تتفضلون.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b836] VALUES (248,'<span data-type="title" id=toc-250>٢٣٨ </span>- احرصوا على إقامة الصلوات كلها، وداوموا عليها، واحرصوا على أن تكون صلاتكم هى الصلاة الفضلى بإقامة أركانها والإخلاص الكامل لله فيها، وأتموا طاعة الله تعالى وذكره مخلصين له خاشعين لجلاله، والصلاة الوسطى هى صلاة الفجر أو العصر على خلاف فى الاجتهاد.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b836] VALUES (249,'<span data-type="title" id=toc-251>٢٣٩ </span>- فإذا أدركتم الصلاة وأنتم خائفون فلا تتركوها بل صلوا كما استطعتم مشاة أو راكبين، فإذا زال الخوف عنكم فصلُّوا الصلاة مستوفية الأركان كما علمتموها ذاكرين الله فيها شاكرين له ما علَّمكم إياه وما منَّ به عليكم من نعمة الأمن.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b836] VALUES (250,'<span data-type="title" id=toc-252>٢٤٠ </span>- والذين يتوفون منكم ويتركون زوجات لهم، فقد أوصى الله بهن أن يقمن فى بيت الزوجية عاماً كاملاً مواساة لهن وإزالة لوحشتهن. ولا يحق لأحد أن يخرجهن، فإن خرجن بأنفسهن فى أثناء العام فلا إثم عليكم - أيها الأولياء - أن تتركوهن يتصرفن فى أنفسهن بما لا ينكره الشرع الشريف عليهن، وأطيعوا الله فى أحكامه واعملوا بما شرع لكم فإنه قادر على أن ينتقم ممن يخالف أمره، وهو ذو حكمة بالغة لا يشرع لكم إلا ما فيه المصلحة وإن غابت حكمتها عن علمكم.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b836] VALUES (251,'<span data-type="title" id=toc-253>٢٤١ </span>- وللنساء اللاتى يطلقن بعد الدخول حق فى أن يعطين ما يتمتعن به من المال جبراً لخاطرهن، يدفع إليهن بالحسنى على قدر غنى الزوج وفقره لأن ذلك مما توجبه تقوى الله ويلزم به أهل الإيمان.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b836] VALUES (252,'<span data-type="title" id=toc-254>٢٤٢ </span>- بمثل هذه البيانات والتشريعات الواضحة المحققة للمصلحة، يبين الله لكم أحكامه ونعمه وآياته لتتدبروها وتعملوا بما فيها من الخير.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b836] VALUES (253,'<span data-type="title" id=toc-255>٢٤٣ </span>- تنبه أيها النبى إلى القصة العجيبة واعلمها، وهى حالة القوم الذين خرجوا من ديارهم فراراً من الجهاد خشية الموت فيه وهم ألوف كثيرة فقضى الله عليهم بالموت والهوان من أعدائهم، حتى إذا استبسلت بقيتهم وقامت بالجهاد أحيا الله جماعتهم به، وإن هذه الحياة العزيزة بعد الذلة المميتة من فضل الله الذى يستوجب الشكران، ولكن أكثر الناس لا يشكرون.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b836] VALUES (254,'<span data-type="title" id=toc-256>٢٤٤ </span>- وإذا علمتم أن الفرار من الموت لا ينجى منه، فجاهدوا وابذلوا أنفسكم لإعلاء كلمة الله، وأيقنوا أن الله يسمع ما يقول المتخلفون وما يقول المجاهدون، ويعلم ما يضمر كُلٌّ فى نفسه فيجازى بالخير خيراً وبالشر شرا.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b836] VALUES (255,'<span data-type="title" id=toc-257>٢٤٥ </span>- والجهاد فى سبيل الله يحتاج إلى المال فقدموا أموالكم، فأى امرئ لا يبذل أمواله لله طيبة بها نفسه وقد وعده الله أن يردها عليه مضاعفة أضعافاً كثيرة؟ والرزق بيد الله فيضيق على مَن يشاء ويوسع لمن يشاء لما فيه مصلحتكم، وإليه مصيركم فيجازيكم على ما بذلتم، ومع أن الرزق من فضل الله وعنايته وأنه هو الذى يعطى ويمنع، سمى المنفق مقرضاً للحث على الإنفاق والتحبيب فيه، وتأكيد الجزاء المضاعف فى الدنيا والآخرة.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b836] VALUES (256,'<span data-type="title" id=toc-258>٢٤٦ </span>- تنبه إلى النبأ العجيب عن جماعة من بنى إسرائيل بعد عهد موسى طلبوا من نبيهم فى ذلك الوقت أن يجعل عليهم حاكماً يجمع شملهم بعد تفرق ويقودهم تحت لوائه إعلاء لكمة الله واسترداداً لعزتهم، سألهم ليستوثق من جدهم فى الأمر: ألن تجبنوا عن القتال إذا فرض عليكم؟ . . فأنكروا أن يقع ذلك منهم قائلين: وكيف لا نقاتل لاسترداد حقوقنا وقد طردنا العدو من أوطاننا؟ . . فلما أجاب الله رغبتهم وفرض عليهم القتال أحجموا إلا جماعة قليلة منهم، وكان إحجامهم ظلماً لأنفسهم ونبيهم ودينهم، والله يعلم ذلك منهم وسيجزيهم جزاء الظالمين.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b836] VALUES (257,'<span data-type="title" id=toc-259>٢٤٧ </span>- وقال لهم نبيهم: إن الله استجاب لكم فاختار طالوت حاكماً عليكم. فاعترض كبراؤهم على اختيار الله قائلين: كيف يكون ملكاً علينا ونحن أولى منه، لأنه ليس بذى نسب ولا مال، فرد عليهم نبيهم قائلا: إن الله اختاره حاكماً عليكم لتوافر صفات القيادة فيه، وهى سعة الخبرة بشئون الحرب، وسياسة الحكم مع قوة الجسم؛ والسلطان بيد الله يعطيه من يشاء من عباده ولا يعتمد على وراثة أو مال، وفضل الله وعلمه شامل، يختار ما فيه مصالحكم.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b836] VALUES (258,'<span data-type="title" id=toc-260>٢٤٨ </span>- وقال لهم نبيهم: إن دليل صدقى على أن الله اختار طالوت حاكماً لكم هو أن يعيد إليكم صندوق التوراة الذى سلب منكم تحمله الملائكة، وفيه بعض آثار آل موسى وآل هارون الذين جاءوا بعدهما، وفى إحضاره تطمئن قلوبكم، وإن فى ذلك لدليلا يدفعكم إلى اتباعه والرضا به إن كنتم تذعنون للحق وتؤمنون به.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b836] VALUES (259,'<span data-type="title" id=toc-261>٢٤٩ </span>- فلما خرج بهم طالوت قال لهم: إن الله مختبركم بنهر تمرُّون عليه فى طريقكم فلا تشربوا منه إلا غرفة، فمن شرب منه أكثر من ذلك فليس من جيشنا ولا من جمعنا لخروجه عن طاعة الله، ولن يصحبنى إلا من لم يشرب منه أكثر من غرفة، فلم يصبروا على هذا الاختبار وشربوا منه كثيراً إلا جماعة قليلة، فاصطحب هذه القلة الصابرة واجتاز بها النهر، فلما ظهرت لهم كثرة عدد عدوهم قالوا: لن نستطيع اليوم قتال جالوت وجنوده لكثرتهم وقلتنا. فقال نفر منهم - ثبت الله قلوبهم لرجائهم فى ثواب الله عند لقائه - لا تخافوا فكثيراً ما انتصرت القلة المؤمنة على الكثرة الكافرة، فاصبروا فإن نصر الله يكون للصابرين.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b836] VALUES (260,'<span data-type="title" id=toc-262>٢٥٠ </span>- ولما تقدم المؤمنون لقتال جالوت وجيشه اتجهوا إلى الله ضارعين داعين له: أن يملأهم بالصبر، ويقوى عزائمهم ويثبتهم فى ميدان القتال، وأن ينصرهم على أعدائهم الكافرين.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b836] VALUES (261,'<span data-type="title" id=toc-263>٢٥١ </span>- فهزموا عدوهم بإذن الله تعالى وقتل داود - وهو أحد جنود طالوت - جالوت قائد الكفار، وأعطاه الله الحكم بعد طالوت والنبوة والعلم النافع وعلمه مما يشاء، وسنة الله أن ينصر الذين يصلحون فى الأرض ولا يفسدون، ولولا أن الله يسلط جنوده على المفسدين لمحو فسادهم، ويسلط الأشرار بعضهم على بعض، ما عمرت الأرض، ولكن الله دائم الإحسان والفضل على عباده.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b836] VALUES (262,'<span data-type="title" id=toc-264>٢٥٢ </span>- تلك القصة من العبر التى نقصّها عليك بالصدق لتكون أسوة لك ودليلا على صدق رسالتك، ولتعلم أننا سننصرك كما نصرنا من قبلك من الرسل.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b836] VALUES (263,'<span data-type="title" id=toc-265>٢٥٣ </span>- هؤلاء الرسل الذين ذكرنا فريقاً منهم وقد فضلنا بعضهم على بعض. فمنهم من كلمه الله دون سفير كموسى، ومنهم من رفعه الله درجات فوق درجاتهم جميعاً وهو محمد الذى اختص بعموم الرسالة، وكمال الشريعة، وختمه الرسالات. ومنهم عيسى ابن مريم الذى أمددناه بالمعجزات كإحياء الموتى، وإبراء الأكمه والأبرص وأيدناه بجبريل روح القدس. وقد جاء هؤلاء الرسل بالهدى، ودين الحق، والبينات الهادية، وكان مقتضى هذا أن يؤمن الناس جميعاً، ولا يختلفوا ولا يقتتلوا، ولو شاء الله ألا يقتتل الناس من بعد مجئ الرسل إليهم بالآيات الواضحة الدالة على الحق ما حدث اقتتال ولا اختلاف، ولكن الله لم يشأ ذلك، ولهذا اختلفوا، فمنهم من آمن ومنهم من كفر، ولو شاء الله ما اقتتلوا ولا اختلفوا بل يكونون جميعاً على الحق، ولكنه يفعل ما يريد لحكمة قدّرها.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b836] VALUES (264,'<span data-type="title" id=toc-266>٢٥٤ </span>- يا أيها المؤمنون بالله وباليوم الآخر أنفقوا بعض ما رزقكم الله فى وجوه الخير، وبادروا بذلك قبل أن يأتى يوم القيامة الذى يكون كله للخير ولا توجد فيه أسباب النزاع، لا تستطيعون فيه تدارك ما فاتكم فى الدنيا، ولا ينفع فيه بيع ولا صداقة ولا شفاعة أحد من الناس دون الله، والكافرون هم الذين يظهر ظلمهم فى ذلك اليوم، إذ لم يستجيبوا لدعوة الحق.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b836] VALUES (265,'<span data-type="title" id=toc-267>٢٥٥ </span>- الله هو الذى يستحق أن يُعبد دون سواه، وهو الباقى القائم على شئون خلقه دائماً، الذى لا يغفل أبداً، فلا يصيبه فتور ولا نوم ولا ما يشبه ذلك لأنه لا يتصف بالنقص فى شئ، وهو المختص بملك السموات والأرض لا يشاركه فى ذلك أحد، وبهذا لا يستطيع أى مخلوق كان أن يشفع لأحد إلا بإذن الله، وهو - سبحانه وتعالى - محيط بكل شئ عالم بما كان وما سيكون، ولا يستطيع أحد أن يدرك شيئاً من علم الله إلا ما أراد أن يعلم به من يرتضيه، وسلطانه واسع يشمل السموات والأرض، ولا يصعب عليه تدبير ذلك لأنه المتعالى عن النقص والعجز، العظيم بجلاله وسلطانه.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b836] VALUES (266,'<span data-type="title" id=toc-268>٢٥٦ </span>- لا إجبار لأحد على الدخول فى الدين، وقد وضح بالآيات الباهرة طريق الحق، وطريق الضلال، فمن اهتدى إلى الإيمان وكفر بكل ما يطغى على العقل، ويصرفه عن الحق، فقد استمسك بأوثق سبب يمنعه من التردى فى الضلال، كمن تمسك بعروة متينة محكمة الرباط تمنعه من التردى فى هوة، والله سميع لما تقولون، عليم بما تفعلون ومجازيكم على أفعالكم.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b836] VALUES (267,'<span data-type="title" id=toc-269>٢٥٧ </span>- الله متولى شئون المؤمنين وناصرهم، يخرجهم من ظلمات الشك والحيرة إلى نور الحق والاطمئنان، والكافرون بالله تستولى عليهم الشياطين ودعاة الشر والضلال، فهم يخرجونهم من نور الإيمان الذى فطروا عليه والذى وضح بالأدلة والآيات إلى ظلمات الكفر والفساد، هؤلاء الكافرون هم أهل النار مخلدون فيها.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b836] VALUES (268,'<span data-type="title" id=toc-270>٢٥٨ </span>- ألم تر إلى من عمى عن أدلة الإيمان وجادل إبراهيم خليل الله فى ألوهية ربه ووحدانيته، وكيف أخرجه غروره بملكه - الذى وهبه ربه - من نور الفطرة إلى ظلام الكفر فعندما قال له إبراهيم: إن الله يحيى ويميت، بنفخ الروح فى الجسم وإخراجها منه، قال: أنا أحيى وأميت بالعفو والقتل، فقال إبراهيم ليقطع مجادلته: إن الله يأتى بالشمس من المشرق فأت بها من المغرب إن كنت إلها كما تدعى. فتحير وانقطع جدله من قوة الحُجة التى كشفت عجزه وغروره، والله لا يوفق المصرِّين المعاندين لاتباع الحق.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b836] VALUES (269,'<span data-type="title" id=toc-271>٢٥٩ </span>- ثم تَدَبَّرْ فى مثل هذه القصة العجيبة، قصة الذى مرَّ على قرية متهدمة سقطت سقوفها وهدمت حيطانها وهلك أهلها، فقال: كيف يحيى الله أهل هذه القرية بعد موتهم؟ فأماته الله وأبقاه على موته مائة عام ثم بعثه ليظهر له سهولة البعث ويزول استبعاده، ثم سئل أى مدة مكثتها ميتاً؟ قال - غير شاعر بطول المدة -: يوماً أو بعض يوم، قيل له بل مكثت على هذه الحالة مائة عام، ثم لفت الله نظره إلى أمر آخر من دلائل قدرته فقال له: فانظر إلى طعامك لم يفسد، وإلى شرابك لم يتغير، وانظر إلى حمارك أيضاً، وقد فعلنا ذلك لتعاين ما استبعدته من إحياء بعد الموت ولنجعلك آية ناطقة للناس تدل على صدق البعث، ثم أمره الله أن ينظر إلى عجيب خلقه للأحياء، وكيف يركِّب عظامها، ثم يكسوها لحما، ثم ينفخ فيها الروح فتتحرك، فلما وضحت له قدرته وسهولة البعث، قال: أعلم أن الله قادر على كل شئ.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b836] VALUES (270,'<span data-type="title" id=toc-272>٢٦٠ </span>- واذكر كذلك قصة إبراهيم إذ قال إبراهيم: رب أرنى كيفية إحياء الموتى، فسأله ربه عن إيمانه بإحياء الموتى ليجيب إبراهيم بما يزيل كل الشك فى إيمانه، فقال الله له: أو لم تؤمن بإحياء الموتى؟ قال: إنى آمنت ولكنى طلبت ذلك ليزداد اطمئنان قلبى. قال: فخذ أربعة من الطير الحى فضمها إليك لتعرفهنَّ جيداً، ثم جَزِّئهن بعد ذبحهن، واجعل على كل جبل من الجبال المجاورة جزءا منهن، ثم نادهنَّ فسيأتينك ساعيات وفيهنَّ الحياة كما هى، واعلم أن الله لا يعجز عن شئ، وهو ذو حكمة بالغة فى كل أمر.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b836] VALUES (271,'<span data-type="title" id=toc-273>٢٦١ </span>- إن حال الذين يبذلون أموالهم فى طاعة الله ووجوه الخير، وينالون على ذلك ثواب الله المضاعف أضعافاً كثيرة، كحال من يبذر حبة فى الأرض طيبة فتنبت منها شجيرة فيها سبع سنابل فى كل سنبلة مائة حبة، وهذا تصوير لكثرة ما يعطيه الله من جزاء على الإنفاق فى الدنيا، والله يضاعف عطاءه لمن يشاء فهو واسع الفضل، عليم بمن يستحق وبمن لا يستحق.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b836] VALUES (272,'<span data-type="title" id=toc-274>٢٦٢ </span>- إن الذين ينفقون أموالهم فى وجوه البر المشروعة دون مَنٍّ أو تفاخر أو تطاول على المحسن إليه. لهم أجرهم العظيم الموعود به عند ربهم، ولا يصيبهم خوف من شئ ولا حزن على شئ.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b836] VALUES (273,'<span data-type="title" id=toc-275>٢٦٣ </span>- قول تطيب به النفوس وتستر معه حال الفقير فلا تذكره لغيره، خير من عطاء يتبعه إيذاء بالقول أو الفعل، والله - سبحانه وتعالى - غنى عن كل عطاء مصحوب بالأذى، ويمكِّن الفقراء من الرزق الطيب، ولا يعجل بعقوبته من لا يعطى رجاء أن يهتدى إلى العطاء.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b836] VALUES (274,'<span data-type="title" id=toc-276>٢٦٤ </span>- لا تضيعوا ثواب صدقاتكم - أيها المؤمنون - بإظهار فضلكم على المحتاجين وإيذائهم فتكونوا كالذين ينفقون أموالهم بدافع الرغبة فى الشهرة وحب الثناء من الناس، وهم لا يؤمنون بالله ولا باليوم الآخر، فإن حال المرائى فى نفقته كحال حجر أملس عليه تراب، هطل عليه مطر شديد فأزال ما عليه من تراب. . فكما أن المطر الغزير يزيل التراب الخصب المنتج من الحجر الأملس، فكذلك المن والأذى والرياء تبطل ثواب الصدقات. . فلا ينتفع المنتفعون بشئ منها، وتلك صفات الكفار فتجنبوها، لأن الله لا يوفق الكافرين إلى الخير والإرشاد.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b836] VALUES (275,'<span data-type="title" id=toc-277>٢٦٥ </span>- حال الذين ينفقون أموالهم طلباً لمرضاة الله وتثبيتاً لأنفسهم على الإيمان، كحال صاحب بستان بأرض خصبة مرتفعة يفيده كثير الماء وقليله، فإن أصابه مطر غزير أثمر مثلين، وإن لم يصبه المطر الكثير بل القليل فإنه يكفى لإثماره لجودة الأرض وطيبها، فهو مثمر فى الحالتين، فالمؤمنون المخلصون لا تبور أعمالهم، والله لا يخفى عليه شئ من أعمالكم.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b836] VALUES (276,'<span data-type="title" id=toc-278>٢٦٦ </span>- إنه لا يحب أحد منكم أن يكون له بستان من نخيل وأعناب تجرى خلالها الأنهار وقد أثمر له البستان من كل الثمرات التى يريدها، وأصابه ضعف الكبر وله ذرية ضعاف لا يقدرون على الكسب ولا يستطيع هو لكبره شيئاً، وجفَّ بستانه فى هذه الحال العاجزة بسبب ريح شديدة فيها نار فأحرقته، وصاحبه وذريته أحوج ما يكونون إليه، وكذلك شأن من ينفق ويتصدق ثم يعقب النفقة والصدقة بالمن والأذى والرياء فيبطل بذلك ثواب نفقته ولا يستطيع أن يتصدق من بعد ذلك طيبة نفسه، ومثل هذا البيان يبين الله لكم الآيات لتتفكروا فيها وتعملوا بها.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b836] VALUES (277,'<span data-type="title" id=toc-279>٢٦٧ </span>- يا أيها المؤمنون أنفقوا من جيِّد ما تحصلونه بعملكم، ومما يتيسر لكم إخراجه من الأرض من زروع ومعادن وغيرها، ولا تتعمدوا الإنفاق من ردئ المال وخبيثه أنكم لن تقبلوا هذا الخبيث لو قُدِّمَ إليكم إلا على إغماض وتساهل صارفين النظر عما فيه من خبث ورداءة، واعلموا أن الله غنى عن صدقاتكم، مستحق للحمد بما أرشدكم إليه من خير وصلاح.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b836] VALUES (278,'<span data-type="title" id=toc-280>٢٦٨ </span>- الشيطان يخوفكم من الفقر ويثبطكم عن كل عمل صالح لتنصرفوا عن الإنفاق فى وجوه الخير ويغريكم بالمعاصى، والله واسع المغفرة قادر على إغنائكم، لا يخفى عليه شئ من أموركم.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b836] VALUES (279,'<span data-type="title" id=toc-281>٢٦٩ </span>- يعطى صفة الحكمة من إصابة الحق فى القول والعمل من يشاء من عباده، ومن أُعْطِىَ ذلك فقد نال خيراً كثيراً لان به انتظام أمر الدنيا والآخرة، وما ينتفع بالعظة والاعتبار بأعمال القرآن إلا ذوو العقول السليمة التى تدرك الحقائق من غير طغيان الأهواء الفاسدة.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b836] VALUES (280,'<span data-type="title" id=toc-282>٢٧٠ </span>- وما أنفقتم من نفقة فى الخير أو الشر، أو التزمتم بنفقة فى طاعة فإن الله يعلمه وسيجزيكم عليه، وليس للظالمين الذين ينفقون رياء أو يؤذون فى نفقتهم أو ينفقون فى المعاصى أعوان يدفعون عنهم عذاب الله فى الآخرة.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b836] VALUES (281,'<span data-type="title" id=toc-283>٢٧١ </span>- إن تظهروا صدقاتكم خالية من الرياء فذلك محمود لكم مرضى منكم، ممدوح من ربكم، وإن تعطوها الفقراء سراً منعاً لحرجهم وخشية الرياء فذلك خير لكم، والله يغفر لكم من ذنوبكم بسبب إخلاصكم فى صدقاتكم، والله يعلم ما تسرون وما تعلنون ويعلم نياتكم فى إعلانكم وإخفائكم.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b836] VALUES (282,'<span data-type="title" id=toc-284>٢٧٢ </span>- ليس عليك - يا محمد - هداية هؤلاء الضالين أو حملهم على الخير، وإنما عليك البلاغ، والله يهدى من يشاء، وما تبذلونه من معونة لغيركم ففائدته عائدة عليكم، والله مثيبكم عليه، وهذا إذا كنتم لا تقصدون بالإنفاق إلا رضاء الله، وأى خير تنفقونه على هذا الوجه يعود إليكم، ويصلكم ثوابه كاملا دون أن ينالكم ظلم.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b836] VALUES (283,'<span data-type="title" id=toc-285>٢٧٣ </span>- وذلك الإنفاق والبذل يكون للفقراء الذين كانوا بسبب الجهاد فى سبيل الله غير قادرين على الكسب، أو لأنهم أصيبوا فى الجهاد بما أقعدهم عن السعى فى الأرض، وهم متعففون عن السؤال يحسبهم الجاهل بحالهم أغنياء، ولكنك إذا تعرفت حالهم عرفت هذه الحالة بعلامتها. وما تبذلونه من معروف فإن الله عليم به، سيجزيكم عليه الجزاء الأوفى.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b836] VALUES (284,'<span data-type="title" id=toc-286>٢٧٤ </span>- الذين من طبعهم السخاء تطيب نفوسهم للإنفاق فى الليل والنهار وفى العلانية والسر، لهم جزاؤهم عند ربهم، لا ينالهم خوف من أمر مستقبلهم، ولا حزن على شئ فاتهم.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b836] VALUES (285,'<span data-type="title" id=toc-287>٢٧٥ </span>- الذين يتعاملون بالربا لا يكونون فى سعيهم وتصرفهم وسائر أحوالهم إلا فى اضطراب وخلل، كالذى أفسد الشيطان عقله فصار يتعثر من الجنون الذى أصابه، لأنهم يزعمون أن البيع مثل الربا فى أن كلا منهما فيه معاوضة وكسب. فيجب أن يكون كلاهما حلالا، وقد رد الله عليهم زعمهم فبين لهم أن التحليل والتحريم ليس من شأنهم، وأن التماثل الذى زعموه ليس صادقاً، والله قد أحل البيع وحرم الربا، فمن جاءه أمر ربه بتحريم الربا واهتدى به، فله ما أخذه من الربا قبل تحريمه، وأمره موكول إلى عفو الله. ومن عاد إلى التعامل بالربا باستحلاله بعد تحريمه، فأولئك يلازمون النار خالدين فيها.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b836] VALUES (286,'<span data-type="title" id=toc-288>٢٧٦ </span>- إن الله يُذهب الزيادة المأخوذة من الربا، ويبارك فى المال الذى تؤخذ منه الصدقات، ويثيب عليها أضعافاً مضاعفة. والله لا يحب الذين يصرون على تحليل المحرمات كالربا، ولا الذين يستمرون على ارتكابها.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b836] VALUES (287,'<span data-type="title" id=toc-289>٢٧٧ </span>- إن الذين آمنوا بالله، وامتثلوا أوامره فعملوا الصالحات التى أمر بها، وتركوا المحرمات التى نهى عنها، وأدوا الصلاة على الوجه الأكمل، وأعطوا الزكاة لأهلها، لهم ثوابهم العظيم المدخر عند ربهم، ولا خوف عليهم من شئ فى المستقبل، ولا هم يحزنون على شئ فاتهم.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b836] VALUES (288,'<span data-type="title" id=toc-290>٢٧٨ </span>- يا أيها الذين آمنوا خافوا الله واستشعروا هيبته فى قلوبكم، واتركوا طلب ما بقى لكم من الربا فى ذمة الناس إن كنتم مؤمنين حقاً.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b836] VALUES (289,'<span data-type="title" id=toc-291>٢٧٩ </span>- فإن لم تفعلوا ما أمركم الله به من ترك الربا فكونوا على يقين من أنكم فى حرب من الله ورسوله لمعاندتكم لأمره، فإن أردتم توبة مقبولة فلكم رءوس أموالكم فلا تأخذوا زيادة عليها قلَّت أو كثرت وأيا كان سبب الدين ومصرفه، لأن الزيادة التى تأخذونها ظلم لغيركم، كما أن ترك جزء من رءوس الأموال ظلم لكم.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b836] VALUES (290,'<span data-type="title" id=toc-292>٢٨٠ </span>- وإن وُجد ذو عسرة فأعطوه وأمهلوه عند انقضاء أجل الدين إلى وقت ميسرته، وتصدقكم عليه بالتنازل عن الدين أو بعضه خير لكم إن كنتم من أهل العلم والفهم لخطاب الله الذى يعلمكم المروءة والإنسانية.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b836] VALUES (291,'<span data-type="title" id=toc-293>٢٨١ </span>- وخافوا أهوال يوم تعودون فيه إلى الله، ثم تستوفى كل نفس جزاء ما عملت من خير أو شر.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b836] VALUES (292,'<span data-type="title" id=toc-294>٢٨٢ </span>- يا أيها الذين آمنوا إذا داين بعضكم بعضا بَدْينٍ مؤجل إلى أجل، ينبغى أن يكون الأجل معلوماً، فاكتبوه حفظاً للحقوق تفادياً للنزاع، وعلى الكاتب أن يكون عادلا فى كتابته، ولا يمتنع عن الكتابة، شكراً لله الذى علمه ما لم يكن يعلم، فليكتب ذلك الدين حسب اعتراف المدين وعلى المدين أن يخشى ربه فلا ينقص من الدين شيئاً، فإن كان المدين لا يحسن التصرف ولا يقدر الأمور تقديراً حسناً، أو كان ضعيفاً لصغر أو مرض أو شيخوخة، أو كان لا يستطيع الإملاء لخرس أو عقدة لسان أو جهل بلغة الوثيقة، فَلْيُنِبْ عنه وليه الذى عينه الشرع أو الحاكم، أو اختاره هو فى إملاء الدين على الكاتب بالعدل التام. وأشهدوا على ذلك الدين شاهدين من رجالكم، فإن لم يوجدا فليشهد رجل وامرأتان تشهدان معاً لتؤديا الشهادة معاً عند الإنكار، حتى إذا نسيت إحداهما ذكرتها الأخرى، ولا يجوز الامتناع عن أداء الشهادة إذا ما طُلب الشهود، ولا تسأموا أن تكتبوه صغيراً كان أو كبيراً ما دام مؤجلا لأن ذلك أعدل فى شريعة الله وأقوى فى الدلالة على صحة الشهادة، وأقرب إلى درء الشكوك بينكم، إلا إذا كان التعامل على سبيل التجارة الحاضرة، تتعاملون بها بينكم، فلا مانع من ترك الكتابة إذ لا ضرورة إليها. ويطلب منكم أن تشهدوا على المبايعة حسماً للنزاع، وتفادوا أن يلحق أى ضرر بكاتب أو شاهد، فذلك خروج على طاعة الله، وخافوا الله واستحضروا هيبته فى أوامره ونواهيه، فإن ذلك يلزم قلوبكم الإنصاف والعدالة، والله يبين ما لكم وما عليكم، وهو بكل شئ - من أعمالكم وغيرها - عليم.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b836] VALUES (293,'<span data-type="title" id=toc-295>٢٨٣ </span>- وإذا كنتم فى سفر فلم تجدوا من يكتب لكم الدين، فليكن ضمان الدين رهناً يأخذه الدائن من المدين. وإذا أودع أحدكم عند آخر وديعة تكون أمانة عنده، وقد اعتمد على أمانته، فليؤد المؤتمن الأمانة عند طلبها، وليتق عقوبة الله له إن خان الأمانة أو غش فى الشهادة. ولا تكتموا الشهادة عند طلبها، ومن يكتمها فهو آثم خبيث القلب، والله بما تعملون عليم، سيجزيكم عليه بحسب ما تستحقون.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b836] VALUES (294,'<span data-type="title" id=toc-296>٢٨٤ </span>- واعلموا أن لله ما فى السموات وما فى الأرض قد أحاط به قدرة وعلما، وسواء أظهرتم ما فى أنفسكم أو أخفيتموه فإن الله عليم خبير، سيحاسبكم عليه يوم القيامة فيغفر لمن يشاء ويعذب من يشاء وهو تعالى على كل شئ قدير.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b836] VALUES (295,'<span data-type="title" id=toc-297>٢٨٥ </span>- إن ما أُنزل إلى الرسول - محمد - هو الحق من عند الله، وقد آمن به وآمن معه المؤمنون كل منهم آمن بالله وملائكته وكتبه ورسله، وهم يسوُّون بين رسل الله فى الإيمان بهم وتعظيمهم قائلين: لا نفرق بين أحد من رسله، وأكدوا إيمانهم القلبى بقولهم اللسانى متجهين إلى الله فى خطابهم: ربنا سمعنا تنزيلك المحكم واستجبنا لما فيه، فامنحنا اللهم مغفرتك، وإليك - وحدك - المصير والمرجع.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b836] VALUES (296,'<span data-type="title" id=toc-298>٢٨٦ </span>- إن الله لا يكلف عباده إلا ما يستطيعون تأديته والقيام به، ولذلك كان كل مكلف مجزياً بعمله: إن خيراً فخير وإن شراً فشر، فاضرعوا إلى الله - أيها المؤمنون - داعين: ربنا لا تعاقبنا إن وقعنا فى النسيان لما كلفتنا إياه، أو تعرضنا لأسباب يقع عندها الخطأ، ربنا ولا تُشدِّد علينا فى التشريع كما شددت على اليهود بسبب تعنتهم وظلمهم، ولا تكلفنا ما لا طاقة لنا به من التكاليف، واعف عنا بكرمك، واغفر لنا بفضلك، وارحمنا برحمتك الواسعة. إنك مولانا، فانصرنا يا رب - من أجل إعلاء كلمتك ونشر دينك - على القوم الجاحدين.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b836] VALUES (297,'<span data-type="title" id=toc-300>١ </span>- الم، حروف صوتية سيقت لبيان أن القرآن المعجز من هذه الحروف.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b836] VALUES (298,'<span data-type="title" id=toc-301>٢ </span>- الله واحد لا إله غيره، وكل ما فى العالم من تنسيق وإبداع يشهد بذلك، وهو الحى الذى لا يموت، القائم بأمر العالم يدبره ويصرفه.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b836] VALUES (299,'<span data-type="title" id=toc-302>٣ </span>- نَزَّلَ عليك - يا محمد - القرآن مشتملا على الحق فى كل ما تضمنه من أصول الشرائع السماوية فى الكتب السابقة، ولقد أنزل الله من قبله التوراة على موسى والإنجيل على عيسى.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b836] VALUES (300,'<span data-type="title" id=toc-303>٤ </span>- أنزلهما قبل القرآن لهداية الناس، فلمَّا انحرفوا أنزل القرآن فارقاً بين الحق والباطل، ومبيِّناً الرشد من الغى، فهو الكتاب الصادق الدائم، وكل من ترك ما أنزله الله فيه وكفر بآياته فله عذاب شديد، والله قادر لا يغلبه شئ، منتقم ممن يستحق الانتقام.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b836] VALUES (301,'<span data-type="title" id=toc-304>٥ </span>- إن الله عليم بكل شئ، فهو لا يخفى عليه شئ فى الأرض ولا فى السماء، صغيراً كان أو كبيراً، ظاهراً أو باطناً.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b836] VALUES (302,'<span data-type="title" id=toc-305>٦ </span>- وهو الذى يصوركم وأنتم أجنة فى الأرحام بصور مختلفة حسبما يريد، لا إله إلا هو العزيز فى ملكه، الحكيم فى صنعه.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b836] VALUES (303,'<span data-type="title" id=toc-306>٧ </span>- وهو الذى أنزل عليك القرآن، وكان من حكمته أن جعل منه آيات محكمات محددة المعنى بيِّنة المقاصد، هى الأصل وإليها المرجع، وأخر متشابهات يدق معناها على أذهان كثير من الناس، وتشبته على غير الراسخين فى العلم، وقد نزلت هذه المتشابهات لتبعث العلماء على العلم والنظر ودقة الفكر فى الاجتهاد، وفى البحث فى الدين، وشأن الزائغين عن الحق أن يتتبعوا ما تشابه من القرآن رغبة فى إثارة الفتنة، ويؤوِّلوها حسب أهوائهم. وهذه الآيات لا يعلم تأويلها الحق إلا الله والذين تثبتوا فى العلم وتمكنوا منه، وأولئك المتمكنون منه يقولون: إنا نوقن بأن ذلك من عند الله، لا نفرق فى الإيمان بالقرآن بين محكمه ومتشابهه، وما يعقل ذلك إلا أصحاب العقول السليمة التى لا تخضع للهوى والشهوة.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b836] VALUES (304,'<span data-type="title" id=toc-307>٨ </span>- وأولئك العلماء العاقلون يقولون: ربنا لا تجعل قلوبنا تنحرف عن الحق بعد إذ أرشدتنا إليه، وامنحنا اللهم رحمة من عندك بالتوفيق والتثبيت إنك أنت المانع المعطى.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b836] VALUES (305,'<span data-type="title" id=toc-308>٩ </span>- ربنا إنك جامع الناس ليوم لا شك فيه لتجازى كلاً على ما فعل، فقد وعدت بذلك وأنت لا تخلف الميعاد.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b836] VALUES (306,'<span data-type="title" id=toc-309>١٠ </span>- إن الكافرين لن تدفع عنهم فى ذلك اليوم أموالهم مهما عظمت، ولا أولادهم مهما كثرت، وسيكونون حطباً للنار تشتعل بهم.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b836] VALUES (307,'<span data-type="title" id=toc-310>١١ </span>- وشأن هؤلاء قوم فرعون والكافرين من قبلهم، كذَّبوا بآيات الله مع وضوحها فنكَّل الله بهم بسبب ما ارتكبوه من الذنوب، والله شديد العقاب.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b836] VALUES (308,'<span data-type="title" id=toc-311>١٢ </span>- قل - يا أيها النبى - لهؤلاء الذين كفروا إنكم فى الدنيا ستهزمون وفى الآخرة ستعذبون، وتكون جهنم فِراشاً لكم وبئس الفِراش.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b836] VALUES (309,'<span data-type="title" id=toc-312>١٣ </span>- لقد كان لكم آية بيِّنة وعبرة ظاهرة فى طائفتين من المحاربين التقتا يوم بدر، إحداهما مؤمنة تحارب لإعلاء كلمة الله ونشر الحق، والأخرى كافرة تحارب فى سبيل الأهواء والشهوات، فكان من تأييد الله للمؤمنين أن جعل الكافرين يرونهم ضعف عددهم الحقيقى، وبذلك وقع الرعب فى قلوب الكفار فانهزموا، والله يمنح نصره لمن يشاء. وإن فى ذلك لعبرة لأصحاب البصائر الرشيدة التى لا تنحرف فى إدراكها عن الحق.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b836] VALUES (310,'<span data-type="title" id=toc-313>١٤ </span>- إن البشر جبلوا على حب الشهوات التى تتمثل فى النساء والبنين والكثرة من الذهب والفضة، والخيل الحسان المعلَّمة، والأنعام التى منها الإبل والبقر والغنم، وتتمثل أيضاً فى الزرع الكثير. لكن ذلك كله متاع الحياة الدنيا الزائلة الفانية، وهو لا يُعد شيئاً إذا قيس بإحسان الله إلى عباده الذين يجاهدون فى سبيله عند أوبتهم إليه فى الآخرة.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b836] VALUES (311,'<span data-type="title" id=toc-314>١٥ </span>- قل يا أيها النبى: أأخبركم بما هو خير من ذلك الذى زُيِّن للناس فى الدنيا؟ ، إن للذين اتقوا ثواباً مضموناً - عند ربهم، هو جنات تجرى من تحت ظلال أشجارها الأنهار، يتمتعون بالحياة الطيبة فيها لا يساورهم خوف من زوال نعيمها إذ كتب لهم الخلود فيها، وأزواج طاهرة نقية من كل ما يشين نساء الدنيا، ورضاء من الله يشعرون فى ظله بنعيم أكبر، والله مطلع على أحوال عباده لا يخفى عليه أمر أو سر من أمورهم وأسرارهم.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b836] VALUES (312,'<span data-type="title" id=toc-315>١٦ </span>- ينال هذا الجزاء أولئك الذين ملأ الإيمان قلوبهم وأعلنوا ذلك بألسنتهم فقالوا - ضارعين إلى الله -: ربنا إننا آمنا استجابة لدعوتك فاعف عن ذنوبنا، واحفظنا من عذاب النار.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b836] VALUES (313,'<span data-type="title" id=toc-316>١٧ </span>- وهم الذين يتحملون المشقة فى سبيل الطاعة وتجنب المعصية واحتمال المكروه، ويصدقون فى أقوالهم وأفعالهم ونياتهم، المداومون على الطاعة فى خشوع وضراعة، الباذلون ما يستطيعون من مال وجاه وغيره فى وجوه التأمل والتفكير فى عظمة الخالق.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b836] VALUES (314,'<span data-type="title" id=toc-317>١٨ </span>- شهد الله أنه المتفرد بالألوهية وبيَّن ذلك - بما بث فى الكون من دلائل وآيات لا ينكرها ذو عقل - وأنه واحد لا شريك له، قائم على شئون خلقه بالعدل، وأقرَّت بذلك ملائكته الأطهار، وَعَلِمَهُ أهل العلم موقنين به، وأنه - جل شأنه - المتفرد بالألوهية الذى لا يغلبه أحد على أمره، وشملت حكمته كل شئ.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b836] VALUES (315,'<span data-type="title" id=toc-318>١٩ </span>- إن الدين الحق المرضى عند الله هو الإسلام، فهو التوحيد والخضوع لله فى إخلاص، وقد اختلف كل من اليهود والنصارى فى هذا الدين فحرَّفوا وبدَّلوا ولم يكن اختلافهم عن شبهة أو جهل إذ جاءهم العلم، بل كان للتحاسد والتطاول، ومن يجحد بآيات الله فلينتظر حساب الله السريع.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b836] VALUES (316,'<span data-type="title" id=toc-319>٢٠ </span>- فإن جادلك هؤلاء فى هذا الدين بعد أن أقمت لهم الحُجج، فلا تجارهم فى الجدل، وقل: أخلصت عبادتى لله - وحده - أنا ومن اتبعنى من المؤمنين، وقل لليهود والنصارى ومشركى العرب: قد بانت لكم الدلائل فأسلموا، فإن أسلموا فقد عرفوا طريق الهدى واتبعوه، وإن أعرضوا فلا تبعة عليك فى إعراضهم، فليس عليك إلا أن تبلغهم رسالة الله، والله مطلع على عباده لا يخفى عليه شئ من أحوالهم وأعمالهم.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b836] VALUES (317,'<span data-type="title" id=toc-320>٢١ </span>- إن الذين يجحدون آيات الله الكونية والمنزَّلة، ويقتلون من بعثهم الله لهدايتهم من الأنبياء، ظلماً بغير حق، ويقتلون دعاة الناس إلى القسط والعدل يستحقون العذاب الأليم فبشرهم به.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b836] VALUES (318,'<span data-type="title" id=toc-321>٢٢ </span>- أولئك المتصفون بتلك الصفات بطلت أعمالهم فى الدنيا والآخرة فلا يقبل لهم عمل وما لهم من ناصر ينصرهم من عذاب الله.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b836] VALUES (319,'<span data-type="title" id=toc-322>٢٣ </span>- ألم تعلم حال الذين أُعطوا حظاً من الكتاب والعلم يدعون إلى كتاب الله وهو القرآن ليفصل الحق من الباطل فيما شجر بينهم من خلاف فلا يسارعون إلى إجابة الداعى، بل يعرض عنه فريق منهم شأنه الإعراض عن دعوة الخير.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b836] VALUES (320,'<span data-type="title" id=toc-323>٢٤ </span>- إن أولئك المعرضين من اليهود زَيَّن لهم ذلك الإعراض أنهم يُمنُّون أنفسهم بالأمانى الباطلة، فيزعمون أن النار لن تسمهم إلا أياماً معدودات ودفعهم إلى ذلك الغرور وتلك الأمانى افتراءاتهم المستمرة فى دينهم.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b836] VALUES (321,'<span data-type="title" id=toc-324>٢٥ </span>- فكيف يكون حالهم وقت أن يجمعهم الله فى الآخرة التى لا شك فى وجودها ولا حسابها فكل نفس تعطى جزاءها وافياً، وهم مستحقون لما نالهم من جزاء.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b836] VALUES (322,'<span data-type="title" id=toc-325>٢٦ </span>- قل - يا أيها النبى - ضارعاً إلى الله مقراً بجبروته: اللهم أنت - وحدك - مالك التصرف فى الأمر كله، تمنح من تشاء من الحكم والسلطان، وتنزعه ممن تشاء، وتهب العزة من تريد من عبادك بتوفيقه إلى الأخذ بأسبابها، وتضرب الذل والهوان على من تشاء، فأنت - وحدك - تملك الخير، لا يعجزك شئ عن تنفيذ مرادك، وما تقتضيه حكمتك فى نظام خلقك.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b836] VALUES (323,'<span data-type="title" id=toc-326>٢٧ </span>- وأنت بما أنشأت ووضعت من الأسباب والسنن، تُدْخِل من الليل فى النهار ما يزيد به النهار طولا، وتدخل من النهار فى الليل ما يزيد به الليل طولا، وتخرج المتصف بمظاهر الحياة من فاقدها، كما تخرج فاقد الحياة من الحى المتمكن من أسباب الحياة، وتهب عطاءك الواسع من تشاء كما تريد على نظام حكمتك، فلا رقيب يحاسبك، ومن كان هذا شأنه لا يعجزه أن يمنح رسوله وأصفياءه السيادة والسلطان والغنى واليسار كما وعدهم.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b836] VALUES (324,'<span data-type="title" id=toc-327>٢٨ </span>- إذا كان الله - سبحانه وتعالى - هو - وحده - مالك الملك، ويعز ويذل، وبيده وحده الخير والخلق والرزق، فلا يصح للمؤمنين أن يجعلوا لغير المؤمنين ولاية عليهم، متجاوزين نصرة المؤمنين؛ لأن فى هذا خذلاناً للدين وإيذاء لأهله، وإضعافاً للولاية الإسلامية، ومن يسلك هذا المسلك فليس له من ولاية الله مالك الملك شئ، ولا يرضى مؤمن بولايتهم إلا أن يكون مضطرا لذلك، فيتقى أذاهم بإظهار الولاء لهم. وعلى المؤمنين أن يكونوا فى الولاية الإسلامية دائماً وهى ولاية الله، وليحذروا أن يخرجوا إلى غير ولايته فيتولى عقابهم بنفسه بكتابة الذلة عليهم بعد العزة. وإليه - وحده - المصير فلا مفرَّ من سلطانه فى الدنيا ولا فى الآخرة.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b836] VALUES (325,'<span data-type="title" id=toc-328>٢٩ </span>- قل - يا أيها النبى - إن تخفوا ما فى صدوركم أو تظهروه فى أعمالكم وأقوالكم فإن الله يعلمه، ويعلم جميع ما فى السموات وما فى الأرض ما ظهر منه وما استتر، وقدرته نافذة فى جميع خلقه.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b836] VALUES (326,'<span data-type="title" id=toc-329>٣٠ </span>- فليحذر الذين يخالفون أمره يوم تجد كل نفس عملها من الخير مهما قلَّ مشاهداً حاضراً، وما اقترفَته من سوء تتمنى أن يكون بعيداً عنها بُعْداً شاسعاً حتى لا تراه استقباحاً له وخوفاً من الوقوع فى مغبته، ويحذركم الله عقابه إذا خرجتم من ولايته التى هى رأفة ورحمة بالعباد.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b836] VALUES (327,'<span data-type="title" id=toc-330>٣١ </span>- قل: إن كنتم صادقين فى دعواكم أنكم تحبون الله وتريدون أن يحبكم الله فاتبعونى فيما آمركم به وأنهاكم عنه، لأننى مبلغ عن الله، فإن ذلك يحبكم الله به، ويثيبكم الله عليه بالإحسان إليكم والتجاوز عن خطاياكم، والله كثير الغفران والرحمة لعباده.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b836] VALUES (328,'<span data-type="title" id=toc-331>٣٢ </span>- قل: أطيعوا الله ورسوله، فإن أعرضوا عنك فهم كافرون بالله ورسوله، والله لا يحب الكافرين.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b836] VALUES (329,'<span data-type="title" id=toc-332>٣٣ </span>- كما اصطفى الله محمداً لتبليغ رسالته، وجعل اتباعه وسيلة لحب الله ومغفرته ورحمته، كذلك اصطفى آدم وجعله من صفوة العالمين، واصطفى نوحاً بالرسالة، واصطفى إبراهيم وآله إسماعيل وإسحاق والأنبياء من أولادهما، ومنهم موسى - عليهم السلام -، واختار آل عمران واختار منهم عيسى وأمه، فعيسى جعله الله رسولا لبنى إسرائيل، ومريم جعلها أماً لعيسى من غير أب.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b836] VALUES (330,'<span data-type="title" id=toc-333>٣٤ </span>- اختارهم ذرية طاهرة، فهم يتوارثون الطهر والفضيلة والخير. والله سميع لأقوال عباده، عليم بأفعالهم وما تُكنّه صدورهم.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b836] VALUES (331,'<span data-type="title" id=toc-334>٣٥ </span>- واذكر - أيها النبى - حال امرأة عمران إذ نذرت وقت حملها تقديم ما تحمله خالصاً لعبادة الله وخدمة بيته، قائلة: يا رب، إنى نذرت ما فى بطنى خالصاً لخدمة بيتك فاقبل منى ذلك، إنك السميع لكل قول، العليم بكل حال.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b836] VALUES (332,'<span data-type="title" id=toc-335>٣٦ </span>- فلما وضعت حملها قالت - معتذرة تناجى ربها -: إنى وَلَدت أُنثى والله عليم بما ولدت، وأن مولودها وهو أنثى خير من مطلوبها وهو الذكر. وقالت: إنى سميتها مريم وإنِّى أسألك أن تحصِّنها هى وذريتها من غواية الشيطان الرجيم.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b836] VALUES (333,'<span data-type="title" id=toc-336>٣٧ </span>- فتقبل الله مريم نذراً لأمها، وأجاب دعاءها، فأنبتها نباتاً حسناً، وربَّاها فى خيره ورزقه وعنايته تربية حسنة مقومة لجسدها، وشأنه أن يرزق من يشاء من عباده رزقاً كثيراً، كلما دخل عليها زكريا فى معبدها وجد عندها رزقاً غير معهود فى وقته. قال - متعجباً -: يا مريم من أين لك هذا الرزق؟ قالت: هو من فضل الله، وجعل زكريا - عليه السلام - كافلا لها. وكان رزقها بغير عدد ولا إحصاء.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b836] VALUES (334,'<span data-type="title" id=toc-337>٣٨ </span>- لما رأى زكريا - عليه السلام - ما رآه من نعمة الله على مريم، اتجه إلى الله ضارعاً أن يهبه من فضله وكرمه وبقدرته ولداً، فهو يسمع دعاء الضارعين، وهو القدير على الإجابة وإن وقفت الأسباب العادية من شيخوخة أو عقم دون تحقيقها.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b836] VALUES (335,'<span data-type="title" id=toc-338>٣٩ </span>- فاستجاب الله دعاءه، فنادته الملائكة وهو قائم فى معبده متجهاً إلى ربه، بأن الله يبشرك بولد اسمه يحيى، يؤمن بعيسى - عليه السلام - الذى سيوجد بكلمة من الله فيكون على غير السُنَّة العامة فى التوالد، ويجعله (أى يحيى) يسود قومه بالعلم والصلاة، يعزف عن الشهوات والأهواء، ويجعله من الأنبياء والصالحين.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b836] VALUES (336,'<span data-type="title" id=toc-339>٤٠ </span>- ولما سيقت إليه هذه البشرى، اتجه إلى ربه متشوقاً إلى معرفة الكيفية التى يكون بها هذا الغلام، مع عدم توافر الأسباب العادية لكبر سنه وعقم زوجه، ورد الله عليه بأنه متى شاء أمراً أوجد له سببه، أو خلقه بغير الأسباب المعروفة. فهو يفعل ما يشاء.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b836] VALUES (337,'<span data-type="title" id=toc-340>٤١ </span>- فدعا زكريا ربه أن يجعل له علامة لتحقق هذه البشرى، فأجابه الله بأن علامتك أن تعجز عن كلام الناس ثلاثة أيام إلا بالإشارة إليهم بما تريد، وثابر على ذكر ربك وتنزيهه فى المساء والصباح.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b836] VALUES (338,'<span data-type="title" id=toc-341>٤٢ </span>- واذكر - أيها النبى - إذ قالت الملائكة: يا مريم إن الله اختارك لتكونى أم نبيه، وطهرك من كل دنس، وخصك بأمومتك لعيسى بفضل على كل نساء العالمين.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b836] VALUES (339,'<span data-type="title" id=toc-342>٤٣ </span>- وهذا يا مريم يستوجب منك الشكر لربك، فالزمى طاعته، وصلى له، وشاركى الذين يعبدونه ويصلون له.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b836] VALUES (340,'<span data-type="title" id=toc-343>٤٤ </span>- ذلك الذى قصه القرآن عليك يا محمد من الأخبار العظيمة عَمَّن اصطفاهم الله، هو من الغيب الذى أوحى الله به إليك. وما كنت حاضراً معهم وهم يقترعون بالسهام ليعلم بالقرعة من يقوم بشئون مريم، وما كنت معهم وهم يختصمون فى نيل هذا الشرف العظيم.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b836] VALUES (341,'<span data-type="title" id=toc-344>٤٥ </span>- اذكر - أيها النبى - إذ بشرت الملائكة مريم بمولود خلقه الله بكلمة منه على غير السُّنَّة العادية فى التوالد، اسمه المسيح عيسى ابن مريم، وقد خلقه الله ذا مكانة فى الدنيا بالنبوة والبراءة من العيوب، وفى الآخرة بعلو درجته مع الصفوة المقربين إلى الله من النبيين أولى العزم.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b836] VALUES (342,'<span data-type="title" id=toc-345>٤٦ </span>- وميَّزه الله بخصائص، فكان يكلم الناس وهو طفل فى مهده كلاماً مفهوماً حكيماً، كما يكلمهم وهو رجل سوى، من غير تفاوت بين حالتى الطفولة والكهولة. وكان ممن منحهم الله الصلاح.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b836] VALUES (343,'<span data-type="title" id=toc-346>٤٧ </span>- قالت مريم - متعجبة من وجود الولد على غير نظام التوالد -: من أين يكون لى ولد ولم يمسسنى رجل؟ فذكر الله تعالى لها أنه يخلق ما يشاء بقدرته غير مقيد بالأسباب العادية، فإنه إذا أراد شيئاً أوجده بتأثير قدرته فى مراده من غير افتقار إلى موجب آخر.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b836] VALUES (344,'<span data-type="title" id=toc-347>٤٨ </span>- والله يُعلِّم هذا الوليد الكتابة، والعلم الصحيح النافع، والتوراة (كتاب موسى) والإنجيل الذى أوحاه الله إليه.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b836] VALUES (345,'<span data-type="title" id=toc-348>٤٩ </span>- ويبعثه رسولا إلى بنى إسرائيل، مستدلا على صدق رسالته بمعجزات من الله، هى أن يصور لكم من الطين صورة مثل صورة الطير، ينفخ فيها فتحل فيها الحياة وتتحرك طائراً بإرادة الله، ويشفى بتقدير الله من وُلِدَ أعمى فيبصر، ومن به برص فيزول برصه، ويعيد الحياة إلى من فقدها. كل ذلك بإذن الله وإرادته، ويخبرهم بما يدَّخرون فى بيوتهم من مأكول وغيره، ويقول لهم: إن هذه الآيات التى أظهرها الله على يدىَّ حجة على أن رسالتى حق إن كنتم ممن يذعنون له ويصدقون به.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b836] VALUES (346,'<span data-type="title" id=toc-349>٥٠ </span>- وأُرسلت إليكم مصدقاً لشريعة التوراة التى نزلت على موسى، ولأبيح لكم بأمر الله بعض ما حُرِّم عليكم من قبل، وقد جئتكم بآية من الله على صدق رسالتى. فاتقوا الله وأطيعون.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b836] VALUES (347,'<span data-type="title" id=toc-350>٥١ </span>- إن الله الذى أدعوكم إليه هو - وحده - ربى وربكم فاعبدوه وأخلصوا العبادة له، فإن هذا هو الطريق الذى لا عوج فيه.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b836] VALUES (348,'<span data-type="title" id=toc-351>٥٢ </span>- ولما جاء عيسى - عليه السلام - دعا قومه إلى الصراط المستقيم، فأبى أكثرهم، فلما علمَ منهم ذلك اتجه إليهم منادياً: من يناصرنى فى هذا الحق الذى أدعو إليه؟ فأجابه خاصة المؤمنين بالله وبه: نحن نؤيدك وننصرك لأنك داع إلى الله، واشهد بأنا مخلصون لله منقادون لأمره.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b836] VALUES (349,'<span data-type="title" id=toc-352>٥٣ </span>- ونحن نقول: يا ربنا، صدَّقنا بكتابك الذى أنزلته على نبيك، وامتثلنا أمر رسولك عيسى - عليه السلام - فاكتبنا من الشاهدين لرسولك بالتبليغ، وعلى بنى إسرائيل بالكفر والجحود.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b836] VALUES (350,'<span data-type="title" id=toc-353>٥٤ </span>- أما الجاحدون فقد دَبَّروا تدبيراً خفياً يحاربون به دعوة عيسى، فأبطل الله كيدهم فلم ينجحوا فيما أرادوا، والله أحكم المدبرين وأقواهم.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b836] VALUES (351,'<span data-type="title" id=toc-354>٥٥ </span>- واذكر - أيها النبى - إذ قال الله: يا عيسى إنى مستوفٍ أجلك، ولا أمكِّن أحداً من قتلك، وإنى رافعك إلى محل كرامتى، ومنجيك من أعدائك الذين قصدوا قتلك، وجاعل المتبعين لك، الذين لم ينحرفوا عن دينك ظاهرين بالقوة والسلطان على الذين لم يهتدوا بهديك إلى يوم القيامة، ثم إلىَّ مصيركم فى الآخرة فأقضى بينكم فى الذى تنازعتم فيه من أمر الدين.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b836] VALUES (352,'<span data-type="title" id=toc-355>٥٦ </span>- فأما الجاحدون، فأذيقهم عذاب الخزى والنكال بتسليط الأمم عليهم فى الدنيا، ولعذاب الآخرة أشد وأخزى. وليس لهم من ينقذهم من عذاب الله.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b836] VALUES (353,'<span data-type="title" id=toc-356>٥٧ </span>- وأما المهتدون بهدى الله، العاملون على سنن الخير، فيعطيهم الله جزاء أعمالهم وافياً. والله لا يمنح ثوابه المتجاوزين لحدود الله الطاغين على دعوته وإحسانه، ولا يرفع لهم قدراً.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b836] VALUES (354,'<span data-type="title" id=toc-357>٥٨ </span>- ذلك الذى قصصناه عليك من الحجج الدالة على صدق رسالتك، هو من القرآن الكريم المشتمل على العلم النافع.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b836] VALUES (355,'<span data-type="title" id=toc-358>٥٩ </span>- ضلَّ قوم فى أمر عيسى، فزعموا أنه ابن الله لأنه ولد من غير أب، فقال الله لهم: إن شأن عيسى فى خلقه من غير أب كشأن آدم فى خلقه من تراب من غير أب ولا أم، فقد صوَّره وأراد أن يكون فكان بشراً سوياً.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b836] VALUES (356,'<span data-type="title" id=toc-359>٦٠ </span>- هذا البيان فى خلق عيسى هو الصدق الذى بيَّن الواقع بإخبار رب الوجود فدم على يقينك، ولا تكن من الشاكين.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b836] VALUES (357,'<span data-type="title" id=toc-360>٦١ </span>- فمن جادلك - يا أيها النبى - فى شأن عيسى من بعد ما جاءك من خبر الله الذى لا شبهة فيه، فقل لهم قولا يظهر علمك اليقينى وباطلهم الزائف: تعالوا يدع كل منَّا ومنكم أبناءه ونساءه ونفسه، ثم نضرع إلى الله أن يجعل غضبه ونقمته على من كذب فى أمر عيسى من كونه خلق من غير أب وأنه رسول الله وليس ابن الله.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b836] VALUES (358,'<span data-type="title" id=toc-361>٦٢ </span>- وذلك هو الحق الذى لا مرية فيه، فليس فى الوجود إله إلا الله الذى خلق كل شئ وأنه لهو المنفرد بالعزة فى ملكه والحكمة فى خلقه.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b836] VALUES (359,'<span data-type="title" id=toc-362>٦٣ </span>- فإن أعرضوا عن الحق بعدما تبين لهم، ولم يرجعوا عن ضلالتهم فهم المفسدون، والله عليم بهم.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b836] VALUES (360,'<span data-type="title" id=toc-363>٦٤ </span>- قل - يا أيها النبى: يا أهل الكتاب تعالوا إلى كلمة عادلة بيننا وبينكم نذكرها على السواء، وهى أن نخص الله بالعبادة ولا نجعل غيره شريكاً له فيها، ولا يطيع بعضنا بعضاً وينقاد له فى تحليل شئ أو تحريمه، تاركاً حكم الله فيما أحلَّ وحرَّم، فإن أعرضوا عن هذه الدعوة الحقة فقولوا لهم: اشهدوا بأنا منقادون لأحكام الله، مخلصون له الدين لا ندعو سواه.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b836] VALUES (361,'<span data-type="title" id=toc-364>٦٥ </span>- يا أهل الكتاب لماذا تتنازعون وتجادلون فى دين إبراهيم، كل منكم يدعى أنه على دينه فى حين أن إبراهيم سابق فى الوجود على التوراة والإنجيل بشريعة خاصة، وما أنزلت التوراة والإنجيل إلا من بعده، فكيف يكون على شريعة واحدة منهما؟ . أليست لكم عقول تدركون بها بطلان هذا الكلام الذى يناقض الواقع؟',NULL,'81',NULL,NULL,'0');
INSERT INTO [b836] VALUES (362,'<span data-type="title" id=toc-365>٦٦ </span>- ها أنتم يا هؤلاء جادلتم فى أمر عيسى وموسى الذى لكم بهما معرفة - كما تزعمون - فكيف تجادلون فى كون إبراهيم يهودياً أو نصرانياً وليس لكم بذلك علم؟ . والله يعلم حقيقة ما تنازعتم فيه، وأنتم لا علم لكم بذلك.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b836] VALUES (363,'<span data-type="title" id=toc-366>٦٧ </span>- إن إبراهيم - عليه السلام - ما كان على دين اليهود ولا على دين النصارى، ولكن كان منصرفاً عن الأديان الباطلة إلى الدين الحق، منقاداً لله، مخلصاً فى طاعته، وما كان من الذين يشركون مع الله غيره فى العبادة.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b836] VALUES (364,'<span data-type="title" id=toc-367>٦٨ </span>- إن أحق الناس بالانتساب إلى إبراهيم ودينه هم الذين أجابوا دعوته واهتدوا بهديه فى زمنه، وكذا محمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - ومن آمن معه، فإنهم أهل التوحيد الخالص وهو دين إبراهيم، والله يحب المؤمنين وينصرهم لأنهم أولياؤه، ويجازيهم بالحسنى وزيادة.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b836] VALUES (365,'<span data-type="title" id=toc-368>٦٩ </span>- إن فريقاً من أهل الكتاب يتمنون إضلال المؤمنين وفتنهم عن دينهم، بإلقاء الشبه التى توهن الاعتقاد، وهم فى عملهم هذا لا يضلُّون إلا أنفسهم بإصرارهم على الضلال الذى يحيق بهم - وحدهم - ولا يعلمون إن عاقبة سعيهم هذا لاحقة بهم ولا تضر المؤمنين.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b836] VALUES (366,'<span data-type="title" id=toc-369>٧٠ </span>- يا أهل الكتاب لم تكذّبون بآيات الله المنزلة الدالة على صدق نبوة محمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - وأنتم تعلمون أنها حق؟ .',NULL,'82',NULL,NULL,'0');
INSERT INTO [b836] VALUES (367,'<span data-type="title" id=toc-370>٧١ </span>- يا أهل الكتاب لم تخلطون الحق الذى جاء به الأنبياء ونزلت به الكتب بما جئتم به من شبهات واهية، وتأويلات باطلة، ولا تذيعون الحق صريحاً واضحاً بعيداً عن التخليط، وأنتم تعرفون أن عقاب الله على مثل هذا الفعل عظيم؟ .',NULL,'82',NULL,NULL,'0');
INSERT INTO [b836] VALUES (368,'<span data-type="title" id=toc-371>٧٢ </span>- وأن أهل الكتاب - فى سبيل إضلال المؤمنين - قالوا لإخوانهم: آمنوا بالقرآن الذى نزل على محمد واتبعه فيه المؤمنون أول النهار، واكفروا فى آخره لعلكم تستطيعون بهذا فتنتهم ببث الريب والشك فيهم، فيرجعون عن دينهم.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b836] VALUES (369,'<span data-type="title" id=toc-372>٧٣ </span>- وقالوا أيضاً: لا تذعنوا إلا لمن تبع دينكم، خشية أن يدعى أحد أنه أوتى مثل ما عندكم، أو يحتج عليكم بإذعانكم عند ربكم، قل لهم - أيها النبى: إن الهدى ينزل من عند الله، فهو الذى يفيض به ويختار له من يشاء، وقل لهم - أيها النبى - إن الفضل من عند الله يعطيه من يريد من عباده، وهو واسع الفضل، عليم بمن يستحقه ومن ينزله عليه.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b836] VALUES (370,'<span data-type="title" id=toc-373>٧٤ </span>- فهو يمنح من يشاء النبوة والرسالة، ومن خصه بذلك فإنما هو محض فضله، والله صاحب الفضل العظيم، لا ينازعه فيه غيره، ولا يحجر عليه فى عطائه.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b836] VALUES (371,'<span data-type="title" id=toc-374>٧٥ </span>- هذا سلوك أهل الكتاب فى الاعتقاد، أما سلوكهم فى المال فمنهم من إن استأمنته على قنطار من الذهب أو الفضة أدَّاه إليك لا ينقص منه شيئاً، ومنهم من إن استأمنته على دينار واحد لا يؤديه إليك إلا إذا لازمته وأحرجته، وذلك لأن هذا الفريق يزعم بأن غيرهم أميون، وأنهم لا ترعى لهم حقوق، ويدعون أن ذلك حكم الله، وهم يعلمون أن ذلك كذب عليه سبحانه وتعالى.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b836] VALUES (372,'<span data-type="title" id=toc-375>٧٦ </span>- حقاً لقد افتروا على الله الكذب، فإن من أدَّى حق غيره ووفَّاه فى وقته كما عاهده عليه وخاف الله فلم ينقص ولم يماطل فإنه يفوز بمحبة الله لأنه اتقاه.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b836] VALUES (373,'<span data-type="title" id=toc-376>٧٧ </span>- إن الذين يتركون عهد الله الذى عاهدهم عليه من أداء الحقوق والقيام بالتكليفات، ويتركون أيمانهم التى أقسموا بها على الوفاء - لثمن قليل من أعراض الدنيا - مهما عظم فى نظرهم - هؤلاء لا نصيب لهم فى متاع الآخرة، ويُعرض عنهم ربهم، ولا ينظر إليهم يوم القيامة نظرة رحمة، ولا يغفر لهم آثامهم، ولهم عذاب مؤلم مستمر الإيلام.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b836] VALUES (374,'<span data-type="title" id=toc-377>٧٨ </span>- وإن من هؤلاء فريقاً يميلون ألسنتهم فينطقون بما ليس من الكتاب، محاولين أن يكون شبيها له، ليحسبه السامع من الكتاب وما هو منه فى شئ، ويَدَّعون أن هذا من عند الله وما هو من الوحى فى شئ وهم بهذا يكذبون على الله، وهم فى أنفسهم يعلمون أنهم كاذبون.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b836] VALUES (375,'<span data-type="title" id=toc-378>٧٩ </span>- وما كان معقولا ولا سائغاً لبشر ينزل الله عليه الكتاب، ويؤتيه العلم النافع والتحدث عن الله أن يطلب من الناس أن يعبدوه من دون الله. ولكن المعقول والواقع أن يطلب منهم أن يكونوا خالصين لربهم الذى خلقهم بمقتضى ما عَلَّمهم من علم الكتاب وما يدرسونه منه.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b836] VALUES (376,'<span data-type="title" id=toc-379>٨٠ </span>- ولا يمكن أن يأمركم بأن تجعلوا الملائكة أو النبيين أرباباً من دون الله، وإن ذلك كفر ليس من المعقول أن يأمركم به بعد أن صرتم مُسلِّمين وجوهكم لله.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b836] VALUES (377,'<span data-type="title" id=toc-380>٨١ </span>- واذكر لهم - أيها النبى: أن الله أخذ العهد والميثاق على كل نبى أنزل عليه الكتاب وآتاه العلم النافع، أنه إذا جاءه رسول توافق دعوته دعوتهم ليؤْمِنُنَّ به وينصرنَّه. وأخذ الإقرار من كل نبى بذلك العهد، وأقروا به وشهدوا على أنفسهم وشهد الله عليهم، وبلغوه لأممهم أن ذلك العهد يوجب عليهم الإيمان والنصرة إن أدركوه وإن لم يدركوه، فحق على أممهم أن يؤمنوا به وينصروه وفاء واتباعاً لما التزم به أنبياؤهم.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b836] VALUES (378,'<span data-type="title" id=toc-381>٨٢ </span>- فمن أعرض عن الإيمان بالنبى بعد هذا الميثاق المؤكد، فهو الفاسق الخارج عن شرع الله، الكافر بالأنبياء أولهم وآخرهم.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b836] VALUES (379,'<span data-type="title" id=toc-382>٨٣ </span>- أيطلبون ديناً غير دين محمد وهو دين الأنبياء وهو - وحده - دين الله - الذى خضع له كل من فى السموات والأرض طوعاً بالإرادة والاختيار، أو كرهاً بالخلق والتكوين، وإليه - وحده - يرجع الخلق كله؟ .',NULL,'84',NULL,NULL,'0');
INSERT INTO [b836] VALUES (380,'<span data-type="title" id=toc-383>٨٤ </span>- أكد الله وحدة الألوهية والرسالة، فأمر نبيه ومن معه بأن يقولوا صدَّقْنا بالله المعبود وحده، ومرسل رسله، وآمنا بما أنزل الله علينا من القرآن والشريعة، وما نزَّله من كتب وشرائع على إبراهيم وإسماعيل وإسحاق ويعقوب وأولاده الأسباط الإثنى عشر، وما أنزل الله على موسى من التوراة وعيسى من الإنجيل، وما أنزل على سائر النبيين لا فرق فى الإيمان بين أحد منهم. ونحن بذلك قد أسلمنا وجهنا لله.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b836] VALUES (381,'<span data-type="title" id=toc-384>٨٥ </span>- فمن يطلب بعد مبعث محمد (صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ) ديناً وشريعة غير دين الإسلام وشريعته فلن يرضى الله منه ذلك، وهو عند الله فى دار جزائه من الذين خسروا أنفسهم فاستوجبوا العذاب الأليم.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b836] VALUES (382,'<span data-type="title" id=toc-385>٨٦ </span>- إن الله لا يوافق قوماً شهدوا بأن الرسول حق، وجاءتهم الأدلة على ذلك، ثم بعد ذلك كفروا به، وبمعجزاته، فكان ذلك ظلماً منهم، والله لا يوفق الظالمين.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b836] VALUES (383,'<span data-type="title" id=toc-386>٨٧ </span>- أولئك عقوبتهم عند الله استحقاق غضبه عليهم، ولعنته، ولعنة صفوة الخلق جميعاً من ملائكة وبشر.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b836] VALUES (384,'<span data-type="title" id=toc-387>٨٨ </span>- لا تفارقهم اللعنة، ولا يخفف عنهم العذاب، ولا هم يمهلون.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b836] VALUES (385,'<span data-type="title" id=toc-388>٨٩ </span>- لكن الذين أقلعوا عن ذنوبهم، ودخلوا فى أهل الصلاح وأزالوا ما أفسدوا، فإن الله تعالى يغفر لهم برحمته ذنوبهم، لأن المغفرة والرحمة صفتان من صفات ذاته العلية.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b836] VALUES (386,'<span data-type="title" id=toc-389>٩٠ </span>- وإن قبول التوبة والرحمة بالغفران شرطهما الاستمرار على الإيمان، فالذين يجحدون الحق بعد الإذعان والتصديق، ويزدادون بهذه الردة جحوداً وفساداً وإيذاء للمؤمنين، لن يقبل الله سبحانه وتعالى - توبتهم لأنها لا يمكن أن تكون صادقة خالصة، وقد صاروا بعملهم بعيدين عن الحق منصرفين عنه.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b836] VALUES (387,'<span data-type="title" id=toc-390>٩١ </span>- وإن الذين جحدوا الحق ولم يذعنوا له واستمروا عليه حتى وهم جاحدون، لن يستطيع أحدهم أن يفتدى نفسه من عذاب الله - سبحانه وتعالى - شيئاً، ولو كان الذى يقدمه فدية له ما يملأ الأرض من الذهب إن استطاع، وعذابهم مؤلم شديد الإيلام.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b836] VALUES (388,'<span data-type="title" id=toc-391>٩٢ </span>- لن تنالوا - أيها المؤمنون - الخير الكامل الذى تطلبونه ويرضاه الله تعالى، إلا إذا بذلتم مما تحبون وأنفقتموه فى سُبُل الله المتنوعة، وإن كان الذى تنفقونه قليلا أو كثيراً، نفيساً أو غيره، فإن الله يعلمه لأنه العليم الذى لا يخفى عليه شئ فى الأرض ولا فى السماء.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b836] VALUES (389,'<span data-type="title" id=toc-392>٩٣ </span>- اعترض اليهود على استباحة المسلمين بعض الأطعمة كلحوم الإبل وألبانها، وادعوا أن ذلك حرمته شريعة إبراهيم. فرد الله سبحانه دعواهم ببيان أن تناول كل المطعومات كان مباحاً لبنى يعقوب من قبل نزول التوراة، إلا ما حرمه يعقوب على نفسه لسبب يختص به فحرموه على أنفسهم. وأمر الله نبيه أن يطلب منهم أن يأتوا من التوراة بدليل يثبت أن شريعة إبراهيم تحرم ذلك إن كانوا صادقين، فعجزوا وأفحموا.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b836] VALUES (390,'<span data-type="title" id=toc-393>٩٤ </span>- وإذ ثبت عجزهم، فمن اختلق منهم الكذب على الله من بعد لزوم الحُجة فهم المستمرون على الظلم المتصفون به حقاً.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b836] VALUES (391,'<span data-type="title" id=toc-394>٩٥ </span>- وبعد تعجيزهم، أمر الله النبى أن يبيّن لهم أنه بعد إفحامهم ثبت صدق الله فيما أخبر، فاتَّبعوا شريعة إبراهيم التى يدعوكم إليها وتكذبون عليها، فإنها الحق الذى لا شك فيه، وما كان إبراهيم من أهل الشرك بالله.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b836] VALUES (392,'<span data-type="title" id=toc-395>٩٦ </span>- وإن من اتباع ملة إبراهيم الاتجاه فى الصلاة إلى البيت الذى بناه والحج إليه، وقد بين الله تعالى ذلك فذكر: إن أول بيت فى القدم والشرف جعله الله متعبداً للناس لهو الذى فى مكة، وهو كثير الخيرات والثمرات، وأودع الله - سبحانه وتعالى - البركة فيه، وهو مكان هداية الناس بالحج والاتجاه فى الصلاة إليه.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b836] VALUES (393,'<span data-type="title" id=toc-396>٩٧ </span>- وفيه دلائل واضحات على حرمته ومزيد فضله، منها مكان قيام إبراهيم للصلاة فيه، ومن دخله يكون آمناً لا يتعرض له بسوء، وحج هذا البيت واجب على المستطيع من الناس، ومن أبى وتمرد على أمر الله وجحد دينه فالخسران عائد عليه، وأن الله غنى عن الناس كلهم.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b836] VALUES (394,'<span data-type="title" id=toc-397>٩٨ </span>- أمر الله - سبحانه وتعالى - رسوله بتوبيخ أهل الكتاب على استمرارهم على الكفر والضلال والتضليل فقال: قل لهم: يا أهل الكتاب لا وجه لكفركم، فلأى سبب تكفرون بدلائل الله الدالة على نبوة محمد وصدقه، والله مطلع على أعمالكم ومجازيكم عليها.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b836] VALUES (395,'<span data-type="title" id=toc-398>٩٩ </span>- يا أهل الكتاب كيف تحاولون صرف مَن آمن بالله ورسوله وأذعن للحق عن سبيل الله الحق المستقيمة، وتحاولون أن تصوروها معوجة، وأنتم عالمون أنها حق، وليس الله غافلا عن أعمالكم وسيجازيكم عليها.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b836] VALUES (396,'<span data-type="title" id=toc-399>١٠٠ </span>- وقد حذَّر المؤمنين مما يثيره بعض أهل الكتاب من شُبَهٍ قائلا: إن تطيعوا بعض أهل الكتاب فيما يبثونه من الشُّبَهِ فى دينكم تعودوا إلى الضلال بعد الهداية، ويردوكم جاحدين بعد الإيمان.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b836] VALUES (397,'<span data-type="title" id=toc-400>١٠١ </span>- وتصوروا حالكم العجيبة وأنتم تضلون وتكفرون بعد الإيمان، والقرآن يتلى عليكم، ورسول الله بينكم، يبين لكم ويدفع الشبه عن دينكم، ومن يلجأ إلى ربه ويستمسك بدينه فنعم ما فعل، فقد هداه ربه إلى طريق الفوز والفلاح.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b836] VALUES (398,'<span data-type="title" id=toc-401>١٠٢ </span>- وإن باب النار مفتوح إذا لم تتقوا الله، في أيها الذين آمنوا خافوا الله الخوف الواجب بامتثال المأمورات واجتناب المنهيات، ودوموا على الإسلام حتى تلقوا الله.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b836] VALUES (399,'<span data-type="title" id=toc-402>١٠٣ </span>- وتمسكوا بدين الله مجتمعين عليه، ولا تفعلوا ما يؤدى إلى فرقتكم، وتذكروا نعمة الله عليكم حين كنتم فى الجاهلية متعادين، فَألَّف بين قلوبكم بالإسلام فصرتم متحابين، وكنتم - بسبب كفركم وتفرقكم - على طرف حفرة من النار فخلصكم منها بالإسلام، بمثل ذلك البيان البديع يبين الله لكم دائماً طرق الخير لتدوموا على الهدى.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b836] VALUES (400,'<span data-type="title" id=toc-403>١٠٤ </span>- وإن السبيل للاجتماع الكامل على الحق فى ظل كتاب الله ورسوله أن تكونوا أمة يدعون إلى كل ما فيه صلاح دينى أو دنيوى، ويأمرون بالطاعة، وينهون عن المعصية، وأولئك هم الفائزون فوزاً كاملاً.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b836] VALUES (401,'<span data-type="title" id=toc-404>١٠٥ </span>- ولا تكونوا بإهمالكم الأمر بالمعروف والنهى عن المنكر اللذين يجمعانكم على الخير والدين الحق، كأولئك الذين أهملوا الأمر بالمعروف والنهى عن المنكر فتفرقوا شيعاً، واختلفوا فى دينهم من بعد ما جاءتهم الحُجج الواضحة المبينة للحق، وأولئك المتفرقون المختلفون لهم عذاب عظيم.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b836] VALUES (402,'<span data-type="title" id=toc-405>١٠٦ </span>- ذلك العذاب العظيم فى اليوم الذى تبيض بالسرور فيه وجوه المؤمنين، وتسود بالكآبة والحزن وجوه الكافرين، ويقال لهم توبيخاً: أكفرتم بعد أن فطرتم على الإيمان والإذعان للحق وجاءتكم البينات عليه؟ ، فذوقوا العذاب بسبب كفركم.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b836] VALUES (403,'<span data-type="title" id=toc-406>١٠٧ </span>- وأما الذين ابيضت وجوههم سروراً بما بشروا به من الخير، ففى الجنة التى رحمهم الله بها هم فيها خالدون.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b836] VALUES (404,'<span data-type="title" id=toc-407>١٠٨ </span>- وإن تلك الآيات الواردة بجزاء المحسن والمسئ نتلوها عليك مشتملة على الحق والعدل، وما الله يريد ظلماً لأحد من الناس والجن.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b836] VALUES (405,'<span data-type="title" id=toc-408>١٠٩ </span>- ولله - وحده - ما فى السموات وما فى الأرض خلقاً وملكاً وتصرفاً، وإليه مصير أمورهم، فيجازى كلاً بما يستحقه.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b836] VALUES (406,'<span data-type="title" id=toc-409>١١٠ </span>- أنتم - يا أمة محمد - أفضل أمة خلقها الله لنفع الناس، ما دمتم تأمرون بالطاعات وتنهون عن المعاصى، وتؤمنون بالله إيماناً صحيحاً صادقاً، ولو صدق أهل الكتاب فى إيمانهم مثلكم لكان خيراً لهم مما هم عليه، ولكن منهم المؤمنون وأكثرهم خارجون عن حدود الإيمان وواجباته.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b836] VALUES (407,'<span data-type="title" id=toc-410>١١١ </span>- لن يضركم هؤلاء الفاسقون بضرر ينالونكم به، ويكون له أثر فيكم، إلا أذى لا يبقى له أثر مثل ما يؤذى أسماعكم من ألفاظ الشرك والكفر وغير ذلك، وإن يُقاتلوكم ينهزموا فارين من لقائكم، ثم لا تكون لهم نصرة عليكم ما دمتم متمسكين بالأمر بالمعروف والنهى عن المنكر.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b836] VALUES (408,'<span data-type="title" id=toc-411>١١٢ </span>- وأخبر - سبحانه - بأنه ألزمهم المهانة فى أى مكان وجدوا فيه، إلا بعقد الذمة الذى هو عهد الله وعهد المسلمين، وأنهم استوجبوا غضب الله وألزمهم الاستكانة والخضوع لغيرهم، وذلك بسبب كفرهم بآيات الله الدالة على نبوة محمد، وقتلهم الأنبياء الذى لا يمكن أن يكون بحق، بل هو عصيان منهم واعتداء.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b836] VALUES (409,'<span data-type="title" id=toc-412>١١٣ </span>- وإن أهل الكتاب ليسوا متساوين، فإن منهم جماعة مستقيمة عادلة يقرءون كتاب الله فى ساعات الليل وهم يصلون.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b836] VALUES (410,'<span data-type="title" id=toc-413>١١٤ </span>- لا يعبدون إلا الله ويصدقون بوجوده ووحدانيته وبالرسل ومجئ يوم القيامة، ويأمرون بالطاعات وينهون عن المعاصى، ويبادرون إلى فعل الخيرات، وهؤلاء عند الله من عداد الصالحين.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b836] VALUES (411,'<span data-type="title" id=toc-414>١١٥ </span>- وما يفعلوا من خير فلن يحرموا ثوابه، والله محيط بأحوالهم ومجازيهم عليها.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b836] VALUES (412,'<span data-type="title" id=toc-415>١١٦ </span>- إن الذين كفروا لن تدفع عنهم أموالهم لو افتدوا بها أنفسهم، ولا أولادهم لو استعانوا بهم شيئاً ولو يسيراً من عذاب الله فى الآخرة. وهؤلاء هم الملازمون للنار، الباقون فيها.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b836] VALUES (413,'<span data-type="title" id=toc-416>١١٧ </span>- إن حال ما ينفقه الكفار فى الدنيا وما يرجون عليه من ثواب فى الآخرة، كحال زرع قوم ظلموا أنفسهم بالكفر والمعاصى، أصابته ريح فيها برد شديد فأهلكته عقوبة لهم. وما ظلمهم الله بضياع أجور أعمالهم، ولكن ظلموا أنفسهم بارتكاب ما أوجب ضياعها، وهو جحود دلالات الإيمان والكفر بالله.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b836] VALUES (414,'<span data-type="title" id=toc-417>١١٨ </span>- يا أيها الذين آمنوا: لا تتخذوا أصفياء تستعينون بهم من غير أهل دينكم، تطلعونهم على أسراركم، لأنهم لا يقصرون فى إفساد أموركم. إذ هم يودون أن يرهقوكم ويضروكم أشد الضرر. وقد ظهرت أمارات البغضاء لكم من فلتات ألسنتهم، وما تضمره قلوبهم أعظم مما بدا، قد أظهرنا لكم العلامات التى يتميز بها الولى من العدو إن كنتم من أهل العقل والإدراك الصحيح.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b836] VALUES (415,'<span data-type="title" id=toc-418>١١٩ </span>- ها أنتم أولاء - أيها المؤمنون - تحبون هؤلاء الكفار المنافقين لقرابة أو صداقة أو مودة، ولا يحبونكم لتعصبهم لدينهم، وأنتم تؤمنون بجميع كتب الله المنزلة، وإذا لقوكم أظهروا الإيمان خداعاً لكم، وإذا فارقوكم عضوا لأجلكم أطراف الأصابع غيظاً وأسفاً. قل - أيها النبى -: دوموا على غيظكم إلى الموت، وإن الله عليم بما تخفيه الصدور، ويجازيكم عليه.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b836] VALUES (416,'<span data-type="title" id=toc-419>١٢٠ </span>- إن جاءتكم نعمة كنصر وغنيمة تحزنهم، وإن تصبكم مساءة كجدب وهزيمة يُسَرُّوا بإصابتكم، وإن تصبروا على أذاهم وتتقوا ما نهيتم عنه من موالاتهم لا يضركم مكرهم وعداوتهم أى ضرر، لأنه تعالى عالم بما يعملونه من الكيد، فلا يعجزه رده عنكم.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b836] VALUES (417,'<span data-type="title" id=toc-420>١٢١ </span>- واذكر - أيها النبى - حين خرجت مبكراً من عند أهلك إلى أُحُد قاصداً إنزال المؤمنين فى مراكز القتال. والله سميع لأقوالكم، عليم بنياتكم.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b836] VALUES (418,'<span data-type="title" id=toc-421>١٢٢ </span>- حين خطر لطائفتين من المؤمنين - وهما بنو مسلمة وبنو حارثة - أن تفشلا وترجعا، فعصمهم الله، فثبتوا ومضوا للقتال لأنه متولى أمرهما بالعصمة والتوفيق، فليأخذ المؤمنون من هذا عبرة، وليتوكلوا عليه لينصرهم.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b836] VALUES (419,'<span data-type="title" id=toc-422>١٢٣ </span>- ذكَّر الله المؤمنين بنعمة النصر فى غزوة بدر حين صبروا، فأكد لهم أنه نصرهم فيها وهم قليلو العدد والعدة، وطلب منهم طاعته لشكر هذه النعمة.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b836] VALUES (420,'<span data-type="title" id=toc-423>١٢٤ </span>- وكان النصر حين قال الرسول للمؤمنين: ألن يكفيكم فى طمأنينة نفوسكم إعانة ربكم إياكم بثلاثة آلاف من الملائكة مرسلين من عند الله لتقويتكم.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b836] VALUES (421,'<span data-type="title" id=toc-424>١٢٥ </span>- بلى يكفيكم ذلك الإمداد، وإن تصبروا على القتال، وتلتزموا التقوى، ويأتكم أعداؤكم على الفور يزد ربكم الملائكة إلى خمسة آلاف مرسلين من عند الله لتقويتكم.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b836] VALUES (422,'<span data-type="title" id=toc-425>١٢٦ </span>- وما جعل اللَّه الإمداد بالملائكة إلا بشارة لكم بالنصر، ولتسكن به قلوبكم، ليس النصر إلا من عند الله الذى يضع الأشياء فى مواضعها، ويدير الأمور لعبادة المؤمنين.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b836] VALUES (423,'<span data-type="title" id=toc-426>١٢٧ </span>- وقد نصركم ليهلك طائفة من الذين كفروا بالقتل، أو يذلهم ويغيظهم بالهزيمة والعار والخزى، فيرجعوا خائبين.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b836] VALUES (424,'<span data-type="title" id=toc-427>١٢٨ </span>- ليس لك من التصرف فى أمر عبادى شئ، بل الأمر لله، فإما أن يتوب عليهم بالإيمان، أو يعذبهم بالقتل والخزى والعذاب يوم القيامة لأنهم ظالمون.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b836] VALUES (425,'<span data-type="title" id=toc-428>١٢٩ </span>- إن للَّه - وحده - ما فى السموات وما فى الأرض خلقاً وملكاً، وهو القادر على كل شئ، وفى يده كل شئ، يغفر لمن يريد له المغفرة، ويعذب من يريد تعذيبه، ومغفرته أقرب، ورحمته أرجى لأنه كثير المغفرة والرحمة.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b836] VALUES (426,'<span data-type="title" id=toc-429>١٣٠ </span>- يا أيها الذين آمنوا لا تأخذوا فى الدَّيْنِ إلا رءوس أموالكم، فلا تزيدوا عليها زيادة تجئ سنة بعد أخرى فتتضاعف وخافوا اللَّه، فلا تأكلوا أموال الناس بالباطل، فإنكم تفلحون وتفوزون باجتنابكم الربا قليله وكثيره.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b836] VALUES (427,'<span data-type="title" id=toc-430>١٣١ </span>- واحذروا النار التى هيئت للكافرين باجتناب ما يوجبها من استحلال الربا.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b836] VALUES (428,'<span data-type="title" id=toc-431>١٣٢ </span>- وأطيعوا اللَّه والرسول فى كل أمر ونهى لترحموا فى الدنيا والآخرة.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b836] VALUES (429,'<span data-type="title" id=toc-432>١٣٣ </span>- وبادروا بالأعمال الصالحة، لتنالوا مغفرة عظيمة لذنوبكم من اللَّه مالك أمركم، وجنة واسعة عرضها كعرض السموات والأرض هيئت لمن يتقون اللَّه وعذابه.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b836] VALUES (430,'<span data-type="title" id=toc-433>١٣٤ </span>- الذين ينفقون أموالهم إرضاء للَّه فى أحوال اليسر والعسر، والقدرة والضعف، ويحبسون أنفسهم عن أن يؤدى غيظهم إلى إنزال عقوبة بمن أساء إليهم خاصة، ويتجاوزون عن المسئ، إنهم بهذا يعدون محسنين، واللَّه تعالى يثيب المحسنين ويرضى عنهم.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b836] VALUES (431,'<span data-type="title" id=toc-434>١٣٥ </span>- والذين إذا ارتكبوا كبيرة، أو تحملوا ذنباً صغيراً، تذكروا اللَّه وجلاله، وعقابه وثوابه، ورحمته ونقمته، فندموا، وطلبوا مغفرته، حيث إنه لا يغفر الذنوب إلا اللَّه، ولم يقيموا على قبيح فعلهم وهم يعلمون قبحه.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b836] VALUES (432,'<span data-type="title" id=toc-435>١٣٦ </span>- أولئك المتصفون بهذه الصفات أجرهم مغفرة عظيمة من ربهم مالك أمرهم، وجنات تجرى الأنهار بين أشجارها لا يبرحونها. ونعم ذلك ثوابا للعاملين بأمر اللَّه.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b836] VALUES (433,'<span data-type="title" id=toc-436>١٣٧ </span>- قد مضت من قبلكم - أيها المؤمنون - سنن اللَّه فى الأمم المكذبة، بإمهالهم، ثم أخذهم بذنوبهم، فتأملوا كيف كان عاقبة أمر المكذبين.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b836] VALUES (434,'<span data-type="title" id=toc-437>١٣٨ </span>- وهذا المذكور من صفات المؤمنين وسنن الله فى الماضين فيه بيان للناس وإرشاد لهم إلى طريق الخير، وزجر عن طريق الشر.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b836] VALUES (435,'<span data-type="title" id=toc-438>١٣٩ </span>- ولا تضعفوا عن الجهاد فى سبيل الله بسبب ما ينالكم فيه، ولا تحزنوا على من يقتل منكم، وأنتم - بتأييد الله وإيمانكم، وقوة الحق الذى تدافعون عنه - الأعلون، ولكم الغلبة إن صدق إيمانكم ودمتم عليه.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b836] VALUES (436,'<span data-type="title" id=toc-439>١٤٠ </span>- إن يكن قد مسّكم بأُحُدْ قتل أو جراح عميقة فى أجسامكم، وأثَّرت فى نفوسكم فلا تهنوا ولا تحزنوا، لأنه قد أصاب خصومكم مثله يوم بدر. وإن أوقات النصر يصرفها الله بين الناس، فيكون النصر لهؤلاء أحياناً ولأولئك أخرى، اختباراً للمؤمنين، وليميز الله الثابتين على الإيمان، وليكرم قوماً بالاستشهاد فى سبيله، والله لا يحب المشركين الظالمين ولو ظفروا بنصر من غيرهم.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b836] VALUES (437,'<span data-type="title" id=toc-440>١٤١ </span>- وينقى الله بهذه الهزيمة الوقتية جماعة المؤمنين، ويطهرهم من مرضى القلوب وضعفاء الإيمان، ودعاة الهزيمة والتردد، ويستأصل بذلك الكفر وأهله.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b836] VALUES (438,'<span data-type="title" id=toc-441>١٤٢ </span>- لا تظنوا - أيها المؤمنون - أنكم تدخلون الجنة دون أن يتبين منكم المجاهدون الصابرون الذين تطهرهم المحن والشدائد.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b836] VALUES (439,'<span data-type="title" id=toc-442>١٤٣ </span>- لقد كنتم تطلبون الموت فى سبيل الله من قبل أن تشاهدوه وتعرفوا هوله، فقد رأيتم الموت حين قتل إخوانكم بين أيديكم وأنتم تنظرون.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b836] VALUES (440,'<span data-type="title" id=toc-443>١٤٤ </span>- لما أشيع قتل محمد فى غزوة أُحد، همّ بعض المسلمين بالارتداد، فأنكر الله عليهم ذلك قائلا: ليس محمد إلا رسول قد مات من قبله المرسلون أمثاله، وسيموت كما ماتوا، وسيمضى كما مضوا، أفإن مات أو قتل رجعتم على أعقابكم إلى الكفر؟ ، ومن يرجع إلى الكفر بعد الإيمان فلن يضر الله شيئاً من الضرر، وإنما يضر نفسه بتعريضها للعذاب، وسيثيب الله الثابتين على الإسلام الشاكرين لنعمه.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b836] VALUES (441,'<span data-type="title" id=toc-444>١٤٥ </span>- لا يمكن أن تموت نفس إلا بإذن الله، وقد كتب الله ذلك فى كتاب مشتمل على الآجال. ومن يرد متاع الدنيا يؤته منها، ومن يرد جزاء الآخرة يؤته منها، وسيجزى الله الذين شكروا نعمته فأطاعوه فيما أمرهم به من جهاد وغيره.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b836] VALUES (442,'<span data-type="title" id=toc-445>١٤٦ </span>- وكم من الأنبياء قاتل مع كل منهم كثيرون من المؤمنين المخلصين لربهم، فما جبنت قلوبهم ولا فترت عزائمهم، ولا خضعوا لأعدائهم بسبب ما أصابهم فى سبيل الله، لأنهم فى طاعته، والله يثيب الصابرين على البلاء.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b836] VALUES (443,'<span data-type="title" id=toc-446>١٤٧ </span>- وما كان قولهم عند شدائد الحرب إلا أن قالوا: ربنا تجاوز عمَّا يكون منا من صغائر الذنوب وكبائرها، وثبتنا فى مواطن الحرب وانصرنا على أعداء دينك الكافرين بك وبرسالة رسلك.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b836] VALUES (444,'<span data-type="title" id=toc-447>١٤٨ </span>- فأعطاهم الله النصر والتوفيق فى الدنيا، وضمن لهم الجزاء الحسن فى الآخرة، والله يثيب الذين يحسنون أعمالهم.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b836] VALUES (445,'<span data-type="title" id=toc-448>١٤٩ </span>- يا أيها الذين آمنوا إن تطيعوا الكفار أعداءكم الذين أعلنوا الكفر أو أخفوه فيما يدعونكم إليه من قول أو فعل يقلبوكم إلى الكفر فتخسروا الدنيا والآخرة.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b836] VALUES (446,'<span data-type="title" id=toc-449>١٥٠ </span>- والله هو ناصركم، ولا تخشوهم لأن الله أعظم الناصرين.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b836] VALUES (447,'<span data-type="title" id=toc-450>١٥١ </span>- ولا يضعفكم ما أصابكم يوم أُحد، فسنقذف الخوف والفزع فى قلوب أعدائكم، لإشراكهم بالله آلهة لم ينزل الله بعبادتها حُجة، لأنها لا تنفع ولا تضر، ومستقرهم النار فى الآخرة وبئس هذا المكان للظالمين مقاماً.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b836] VALUES (448,'<span data-type="title" id=toc-451>١٥٢ </span>- وإن نصر الله محقق واقع، ولقد صدقكم الله الوعد بالنصر حين قتلتم كثيرين منهم أول الأمر بإرادته، حتى إذا ضعف رأيكم فى القتال، واختلفتم فى فهم أمر النبى إياكم بالمقام فى مراكزكم، فرأى بعضكم ترك موقعه حيث ظهر النصر، ورأى البعض البقاء حتى النهاية، وعصى فريق منكم أمر الرسول فمضى لطلب الغنيمة من بعد ما أراكم ما تحبون من النصر، وصرتم فريقين منكم من يريد متاع الدنيا، ومنكم من يريد ثواب الآخرة، لما كان ذلك، منعكم نصره ثم ردكم بالهزيمة عن أعدائكم، ليمتحنكم فيظهر المخلص من غيره. ولقد تجاوز عنكم لما ندمتم. والله ذو الفضل عليكم بالعفو وقبول التوبة.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b836] VALUES (449,'<span data-type="title" id=toc-452>١٥٣ </span>- اذكروا - أيها المؤمنون - حالكم وقت أن كنتم تبعدون فى الأرض هاربين، ولا تلتفتون لأحد من شدة الهرب، والرسول يناديكم من ورائكم لترجعوا، فجازاكم الله حزناً غامراً كالغمة، توالى على نفوسكم لكى لا تحزنوا على ما فاتكم من الغنيمة وما أصابكم من الهزيمة، والله عليم بمقاصدكم وأعمالكم.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b836] VALUES (450,'<span data-type="title" id=toc-453>١٥٤ </span>- ثم أسبغ الله عليكم من بعد الغم نعمة أمن، وكان مظهرها نعاساً يغشى فريق الصادقين فى إيمانهم وتفويضهم لله، أما الطائفة الأخرى فقد كان همهم أنفسهم لا يعنون إلا بها، ولذلك ظنوا بالله الظنون الباطلة كظن الجاهلية، يقولون مستنكرين: هل كان لنا من أمر النصر الذى وعدنا به شئ؟ قل - أيها النبى: - الأمر كله فى النصر والهزيمة لله، يصرف الأمر فى عباده إن اتخذوا أسباب النصر، أو وقعوا فى أسباب الهزيمة. وهم إذ يقولون ذلك يخفون فى أنفسهم أمراً لا يبدونه. إذ يقولون فى أنفسهم: لو كان لنا اختيار لم نخرج فلم نغلب. قل لهم: لو كنتم فى منازلكم وفيكم من كتب عليهم القتل لخرجوا إلى مصارعهم فقتلوا. وقد فعل الله ما فعل فى أُحد لمصالح جمة، ليختبر ما فى سرائركم من الإخلاص وليطهر قلوبكم، والله يعلم ما فى قلوبكم من الخفايا علماً بليغاً.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b836] VALUES (451,'<span data-type="title" id=toc-454>١٥٥ </span>- إن الذين انصرفوا منكم عن الثبات فى أماكنهم - يا معشر المسلمين - يوم التقى جمعكم وجمع الكفار للقتال بأُحد، إنما جرَّهم الشيطان إلى الزلل والخطأ بسبب ما ارتكبوا من مخالفة الرسول، ولقد تجاوز الله عنهم لأنه كثير المغفرة واسع الحلم.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b836] VALUES (452,'<span data-type="title" id=toc-455>١٥٦ </span>- يا أيها الذين آمنوا لا تكونوا كالذين كفروا وقالوا فى شأن إخوانهم - إذا أبعدوا فى الأرض لطلب العيش فماتوا أو كانوا غزاة فقتلوا -: لو كانوا مقيمين عندنا ما ماتوا وما قتلوا، فقد جعل الله ذلك القول والظن حسرة فى قلوبهم، والله هو الذى يحيى ويميت، وبيده مقادير كل شئ، وهو مطلع على ما تعملون من خير أو شر، ومجازيكم عليه.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b836] VALUES (453,'<span data-type="title" id=toc-456>١٥٧ </span>- ولئن قتلتم فى الجهاد أو متم فى أثنائه، لمغفرة من الله لذنوبكم ورحمة منه لكم، خير مما تجمعونه من متاع الدنيا لو بقيتم.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b836] VALUES (454,'<span data-type="title" id=toc-457>١٥٨ </span>- ولئن متم أو قتلتم فى الجهاد فلن تضيع أعمالكم، بل ستحشرون إلى الله فيثيبكم على جهادكم وإخلاصكم.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b836] VALUES (455,'<span data-type="title" id=toc-458>١٥٩ </span>- كان رحمة من الله بك وبهم أن لِنْتَ لهم ولم تغلظ فى القول بسبب خطئهم، ولو كنت جافى المعاملة قاسى القلب، لتفرقوا من حولك، فتجاوز عن خطئهم، واطلب المغفرة لهم، واستشرهم فى الأمر متعرفاً آراءهم مما لم ينزل عليك فيه وحى، فإذا عقدت عزمك على أمر بعد المشاروة فامض فيه متوكلاً على الله، لأن الله يحب من يفوض أموره إليه.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b836] VALUES (456,'<span data-type="title" id=toc-459>١٦٠ </span>- إن يؤيدكم الله بنصره - كما حصل يوم بدر - فلن يغلبكم أحد، وإن قدر لكم الخذلان لعدم اتخاذكم أسباب النصر - كما حصل يوم أُحد - فلا ناصر لكم سواه، وعلى الله - وحده - يجب أن يعتمد المؤمنون ويفوضوا أمرهم إليه.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b836] VALUES (457,'<span data-type="title" id=toc-460>١٦١ </span>- ما صح لنبى أن يخون فى المغنم كما أشاع المنافقون الكذابون، لأن الخيانة تنافى النبوة، فلا تظنوا به ذلك، ومن يخن يأت يوم القيامة بإثم ما خان فيه، ثم تُعطى كل نفس جزاء ما عملت وافياً، وهم لا يظلمون بنقصان الثواب أو زيادة العقاب.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b836] VALUES (458,'<span data-type="title" id=toc-461>١٦٢ </span>- ليس من سعى فى طلب رضا الله بالعمل والطاعة مثل الذى باء بغضب عظيم من الله بسبب المعصية. ومصير العاصى جهنم وبئس ذلك المصير.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b836] VALUES (459,'<span data-type="title" id=toc-462>١٦٣ </span>- ليس الفريقان سواء، بل هم متفاوتون عند الله تفاوت الدرجات والله عالم بأحوالهم ودرجاتهم، فيجازيهم على قدرها.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b836] VALUES (460,'<span data-type="title" id=toc-463>١٦٤ </span>- لقد تفضل الله على المؤمنين الأولين الذين صحبوا النبى، بأن بعث فيهم رسولا من أنفسهم يتلو عليهم آيات الكتاب، ويطهرهم من سوء العقيدة، ويعلمهم علم القرآن والسنة. وقد كانوا من قبل بَعثه فى جهالة وحيرة وضياع.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b836] VALUES (461,'<span data-type="title" id=toc-464>١٦٥ </span>- أجزعتم وتخاذلتم وقلتم مستغربين حين أصابتكم مصيبة يوم أُحد قد أصبتم ضعفيها يوم بدر: من أين لنا هذا القتل والهزيمة ونحن مسلمون ورسول الله فينا؟ . قل - يا محمد -: الذى أصابكم من عند أنفسكم بسبب مخالفتكم الرسول والله قادر على كل شئ، وقد جازاكم بما عملتم.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b836] VALUES (462,'<span data-type="title" id=toc-465>١٦٦ </span>- إن الذى أصابكم - أيها المؤمنون - يوم التقى جمعكم وجمع المشركين بأُحد واقع بقضاء الله، وليظهر للناس ما علمه من إيمان المؤمن حقاً.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b836] VALUES (463,'<span data-type="title" id=toc-466>١٦٧ </span>- وليظهر نفاق الذين نافقوا، وهم الذين قيل لهم حين انصرفوا يوم أُحد عن القتال: تعالوا قاتلوا لأجل طاعة الله، أو قاتلوا دفاعاً عن أنفسكم، قالوا: لو نعلم أنكم ستلقون قتالاً لذهبنا معكم، وهم حين قالوا هذا القول أقرب للكفر منهم للإيمان، يقولون بأفواههم: ليس هناك حرب، مع أنهم يعتقدون فى قلوبهم أنها واقعة. والله أعلم بما يضمرون من النفاق.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b836] VALUES (464,'<span data-type="title" id=toc-467>١٦٨ </span>- وإنهم هم الذين تخلفوا عن القتال وقعدوا عنه، وقالوا فى شأن إخوانهم الذين خرجوا وقتلوا: لو أطاعونا وقعدوا كما قعدنا لنجوا من القتل كما نجونا. قل: فادفعوا عن أنفسكم الموت إن كنتم صادقين فى أن الحذر كان يمنعكم من القدر.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b836] VALUES (465,'<span data-type="title" id=toc-468>١٦٩ </span>- ولا تظنن الذين قُتِلوا فى سبيل الله أمواتاً بل هم أحياء حياةً استأثر الله بعلمها، يرزقون عند ربهم رزقاً حسناً يعلمه هو.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b836] VALUES (466,'<span data-type="title" id=toc-469>١٧٠ </span>- يتألق السرور بالبشر من وجوههم بما أعطاهم الله بسبب فضله من المزايا، ويفرحون بإخوانهم الذين تركوهم فى الدنيا أحياء مقيمين على منهج الإيمان والجهاد، وبأنه لا خوف عليهم من مكروه، ولا هم يحزنون لفوات محبوب.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b836] VALUES (467,'<span data-type="title" id=toc-470>١٧١ </span>- تتألق وجوه الشهداء بما منّ الله به عليهم من نعمة الشهادة ونعيم الجنة وعظيم الكرامة، وبأنه لا يضيع أجر المؤمنين.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b836] VALUES (468,'<span data-type="title" id=toc-471>١٧٢ </span>- الذين لبّوا دعوة الرسول إلى استئناف الجهاد من بعد ما أصابهم فى غزوة أُحد من الجرح العميق، وبذلك أحسنوا، واتقوا عصيان أمر الله ورسوله، فاستحقوا الأجر العظيم فى دار الجزاء والنعيم.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b836] VALUES (469,'<span data-type="title" id=toc-472>١٧٣ </span>- الذين خوفهم الناس بأن قالوا لهم: إن أعداءكم قد جمعوا لكم جيشاً كثيفاً فخافوهم، فما ضعفوا وما وهنوا، بل ازدادوا إيماناً بالله وثقة بنصره، وكان ردّهم: الله كافينا، وهو المتولى أمورنا، وهو نِعْمَ من يفوّض إليه الأمر كله.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b836] VALUES (470,'<span data-type="title" id=toc-473>١٧٤ </span>- ثم خرجوا للجهاد ولقاء الجيش الكثيف، ولكن المشركين جبنوا عن اللقاء، فعاد المؤمنون فائزين بنعمة السلامة مع الرغبة فى الجهاد، وفوزهم بثوابه وفضل اللَّه عليهم فى إلقاء الرعب فى قلوب عدوهم فلم ينلهم أذى. وابتغوا رضوان اللَّه فصاروا أهلاً لفضله، واللَّه صاحب الفضل العظيم.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b836] VALUES (471,'<span data-type="title" id=toc-474>١٧٥ </span>- يبين اللَّه سبحانه للمؤمنين أن أولئك الذين يخوفونكم بأعدائكم لتجْبنوا عن لقائهم ليسوا إلا أعواناً للشيطان الذى يخوف أتباعه فيجعلهم جبناء ولستم منهم. فلا تحفلوا بتخويفهم وخافوا اللَّه - وحده - إن كنتم صادقى الإيمان، قائمين بما يفرضه عليكم هذا الإيمان.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b836] VALUES (472,'<span data-type="title" id=toc-475>١٧٦ </span>- لا تحزن - أيها النبى - إذا رأيت الذين يزدادون كفراً ويسرعون بالانتقال من سيئ إلى أسوأ، فهم لن ينالوا اللَّه بأى ضرر، لأنه القاهر فوق عباده، بل يريد اللَّه ألا يجعل لهم نصيباً من ثواب الآخرة، ولهم فوق حرمانهم هذا الثواب الكريم عذاب عظيم.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b836] VALUES (473,'<span data-type="title" id=toc-476>١٧٧ </span>- إن هؤلاء الذين استبدلوا الكفر بالإيمان، فابتغوا الكفر وتركوا الإيمان، لن يضرُّوا اللَّه شيئا، ولهم فى الآخرة عذاب مؤلم شديد الإيلام.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b836] VALUES (474,'<span data-type="title" id=toc-477>١٧٨ </span>- لا يحسبن هؤلاء الكافرون أن إمهالنا لهم - حين نُمدُ فى أعمارهم، ونهيئ لهم أسباب النعيم فى حياتهم الدنيا - خير لهم، فإن إطالة العمر وسعة الرزق يفضيان بهم إلى الاستمرار فى اكتساب الإثم واستحقاق ما أعد اللَّه لهم من عذاب مهين.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b836] VALUES (475,'<span data-type="title" id=toc-478>١٧٩ </span>- ما كان اللَّه ليترككم - يا معشر المؤمنين - على ما أنتم عليه من اختلاط المؤمن بالمنافق، حتى يميز بينكم بالمحنة والتكليف لتروا المنافق الخبيث والمؤمن الطيب، ولم تجر سنة اللَّه بإطلاع أحد من خلقه على شئ من غيبه، ولكن اللَّه يصطفى من يشاء بإطلاعه على ما يشاء من غيبه، وإن تؤمنوا وتتقوا ربكم بالتزام طاعته يدخلكم الجنة جزاء، ونعم الجزاء إذ هى جزاء عظيم.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b836] VALUES (476,'<span data-type="title" id=toc-479>١٨٠ </span>- لا يظن الذين يبخلون بما أنعم الله عليهم من المال تفضلاً منه، ولا يبذلونه فى الواجبات وسبل الخير أنَّ البخل خير لهم، بل إنه شر سيئ العاقبة عليهم، سيجزون عليه شر الجزاء يوم القيامة، وسيكون العذاب ملازماً لهم ملازمة الطوق للعنق. وإن كل ما فى الوجود يؤول لله - سبحانه وتعالى - وهو المالك له، وهو - سبحانه - يعلم كل ما تعملون، وسيجازيكم عليه.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b836] VALUES (477,'<span data-type="title" id=toc-480>١٨١ </span>- ومع أن الله له ملك السموات والأرض وميراثهما، فقد قال بعض اليهود متهكمين: إن الله فقير يطلب منا أن نقرضه بالإنفاق، ونحن أغنياء ننفق أو لا ننفق، لقد سمع الله قولهم هذا وسجَّل عليهم ذلك القول كما سجل عليهم قتلهم الأنبياء ظلماً وإثماً وعدواناً، وسيقول لهم يوم القيامة: ذوقوا عذاب النار المحرقة.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b836] VALUES (478,'<span data-type="title" id=toc-481>١٨٢ </span>- وذلك العذاب بما قدمت أيديهم من الآثام، وعقاب الله لا يكون إلا عدلا، فهو لا يظلم العباد أبداً.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b836] VALUES (479,'<span data-type="title" id=toc-482>١٨٣ </span>- إنهم هم الذين قالوا: إن الله أمرنا فى التوراة ألا نؤمن مذعنين لرسول إلا إذا دلل على صدقه بأن يأتينا بشئ يقربه لوجه الله وتنزل نار من السماء فتأكله، فقل لهم - أيها النبى -: إن رسلا من الله قد جاءوا من قبل بالأدلة الواضحة، وجاءوا بما اقترحتم، ومع ذلك كذبتموهم وقتلتموهم. فلم فعلتم ذلك إن كنتم صادقين فى وعدكم بالإيمان عندما يتحقق ما تريدون؟',NULL,'100',NULL,NULL,'0');
INSERT INTO [b836] VALUES (480,'<span data-type="title" id=toc-483>١٨٤ </span>- وإن كذبوك - أيها النبى - فلا تحزن، فقد سبق قبلك كثيرون كذبهم أقوامهم تعنتاً وعناداً، مع أنهم جاءوا بالأدلة الساطعة والكتب السماوية الدالة على صدق رسالتهم.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b836] VALUES (481,'<span data-type="title" id=toc-484>١٨٥ </span>- كل نفس تذوق الموت لا محالة، وإذا أصابتكم آلام فى الدنيا فإنما توفون ثوابكم كاملاً يوم القيامة، ومن قارب النار وزحزح عنها فقد نال الفوز، وما الحياة الدنيا إلا متاع زائل يغرُّ ولا يبقى.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b836] VALUES (482,'<span data-type="title" id=toc-485>١٨٦ </span>- تأكدوا - أيها المؤمنون - أنكم ستختبرون فى أموالكم بالنقص أو الإنفاق، وفى أنفسكم بالجهاد وبالأمراض والآلام. وأنكم ستسمعون من اليهود والنصارى والمشركين كثيراً مما يؤذيكم من السب والطعن، فعليكم أن تقابلوا ذلك بالصبر وتقوى الله، لأن ذلك من الأمور الصالحة التى يجب العزم على تنفيذها.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b836] VALUES (483,'<span data-type="title" id=toc-486>١٨٧ </span>- واذكر - أيها النبى - إذ أخذ الله العهد المؤكد على أهل الكتاب أن يوضحوا معانيه، وألا يخفوا شيئاً من آياته عن الناس، فألقوه وراء ظهورهم نابذين له، واستبدلوا به متاع الدنيا طالبين له، ومتاع الدنيا مهما يكن كالثمن البخس الحقير فى مقابل الهداية والإرشاد فقبحاً لما فعلوا.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b836] VALUES (484,'<span data-type="title" id=toc-487>١٨٨ </span>- لا تظنن الذين يفرحون دائماً بما يأتون من أفعال قبيحة ويحبون الثناء بما لم يفعلوه، لا تظنن هؤلاء بمنجاة من العذاب، لأن من شأنهم أن يغلقوا على أنفسهم باب الإيمان والحق كاليهود، ولهم عذاب مؤلم يوم القيامة.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b836] VALUES (485,'<span data-type="title" id=toc-488>١٨٩ </span>- الله - وحده - هو المالك لأمر السموات والأرض، وهو القادر على كل شئ، فيؤاخذ المذنبين بذنوبهم ويثيب المحسنين على إحسانهم.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b836] VALUES (486,'<span data-type="title" id=toc-489>١٩٠ </span>- إن فى خلق الله للسموات والأرض مع ما فيهما من إبداع وإحكام، واختلاف الليل والنهار نوراً وظلمة وطولاً وقصراً لدلائل بينات لأصحاب العقول المدركة على وحدانية الله وقدرته.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b836] VALUES (487,'<span data-type="title" id=toc-490>١٩١ </span>- وشأن أولى الألباب أنهم يستحضرون فى نفوسهم عظمة الله وجلاله فى كل مكان، قائمين وقاعدين وعلى جنوبهم، ويتدبرون فى خلق السموات والأرض وما فيهما من عجائب قائلين: ربنا ما خلقت هذا إلا لحكمة قدَّرتها وأنت منزه عن النقص، بل خلقته دليلاً على قدرتك، وعنواناً لبالغ حكمتك، فاحفظنا من عذاب النار بتوفيقك لنا إلى طاعتك.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b836] VALUES (488,'<span data-type="title" id=toc-491>١٩٢ </span>- يا خالقنا والقائم على أمورنا، والحافظ لنا إن من يستحق النار وتدخله فيها فقد أخزيته، وليس للظالم الذى استحق النار من نصير يمنعه منها.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b836] VALUES (489,'<span data-type="title" id=toc-492>١٩٣ </span>- يا خالقنا والقائم على أمورنا، والحافظ لنا إننا سمعنا رسولك يدعو إلى الإيمان بك فأطعناه وآمنا به، ربنا اغفر لنا كبائر ذنوبنا وامح عنا صغائر سيئاتنا، واجعلنا بعد وفاتنا مع عبادك الأخيار.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b836] VALUES (490,'<span data-type="title" id=toc-493>١٩٤ </span>- يا خالقنا، والقائم على أمورنا، والحافظ لنا، أعطنا الذى وعدتنا على ألسنة رسلك من نصر وتأييد فى الدنيا، ولا تدخلنا النار فتخزنا - يوم القيامة - فشأنك ألا تخلف الميعاد.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b836] VALUES (491,'<span data-type="title" id=toc-494>١٩٥ </span>- فأجاب ربهم دعاءهم، مبيناً لهم أنه لا يضيع على عامل منهم ثواب عمله، سواء كان ذكراً أم أنثى، فالأنثى من الذكر، والذكر من الأنثى. فالذين هاجروا يريدون وجه الله وأخرجوا من ديارهم ونالهم الأذى فى سبيل الله وقاتلوا وتعرضوا للقتل، وقتل منهم من قتل، كتب الله على نفسه أنه سيمحو عنهم سيئاتهم، ويدخلهم جنات تجرى من تحتها الأنهار جزاءً كريماً عالياً من عند الله، والله - وحده - عنده الثواب الحسن الجميل.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b836] VALUES (492,'<span data-type="title" id=toc-495>١٩٦ </span>- لا تتأثر - أيها النبى - بما ترى فيه الذين كفروا من تقلب فى النعيم والتصرف فى التجارة والمكاسب.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b836] VALUES (493,'<span data-type="title" id=toc-496>١٩٧ </span>- فإن ذلك متاع زائل، وكل زائل قليل، ثم يكون المأوى الذى ينتهون إليه جهنم، وبئس منزلا جهنم.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b836] VALUES (494,'<span data-type="title" id=toc-497>١٩٨ </span>- ذلك جزاء الكافرين، أما الذين آمنوا وخافوا ربهم فلهم جنات تجرى من تحتها الأنهار مخلدين فيها، نازلين فى كرم الله سبحانه وما عند الله خير للأبرار مما يتقلب فيه الكافرون من متاع زائل.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b836] VALUES (495,'<span data-type="title" id=toc-498>١٩٩ </span>- إن بعض أهل الكتاب يؤمنون بالله وبما أنزل على محمد وبما أنزل على الرسل من قبله، تراهم خاضعين لله ضارعين إليه، لا يستبدلون بالبينات الظاهرة عرضاً من أعراض الدنيا مهما عظم فهو قليل، هؤلاء لهم الجزاء الأوفى فى دار الرضوان عند ربهم والله سريع الحساب لا يعجزه إحصاء أعمالهم ومحاسبتهم عليها، وهو قادر على ذلك وجزاؤه نازل بهم لا محالة.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b836] VALUES (496,'<span data-type="title" id=toc-499>٢٠٠ </span>- يا أيها المؤمنون تمسكوا بالصبر، وغالبوا أعداءكم به، ولازموا الثغور لحمايتها، وخافوا ربكم، ففى كل ذلك رجاء فلاحكم.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b836] VALUES (497,'<span data-type="title" id=toc-501>١ </span>- يا أيها الناس اتقوا ربكم الذى أوجدكم من نفس واحدة، وأنشأ من هذه النفس زوجها، وخلق منهما رجالاً كثيراً ونساء، فأنتم جميعاً تنتهون إلى تلك النفس الواحدة، واتقوا الله الذى تستعينون به فى كل ما تحتاجون، ويسأل باسمه بعضكم بعضاً فيما تتبادلون من أمور، واتقوا الأرحام فلا تقطعوها قريبها وبعيدها، إن الله دائم الرقابة على أنفسكم، لا تخفى عليه خافية من أموركم، ومجازيكم عليها.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b836] VALUES (498,'<span data-type="title" id=toc-502>٢ </span>- ومَلّكُوا اليتامى ما يستحقون من مال، واحفظوه لهم، ولا تعطوهم الردئ وتحرموهم الجيد، ولا تأخذوا أموالهم وتضيفوها إلى أموالكم، إن ذلك كان إثماً كبيراً.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b836] VALUES (499,'<span data-type="title" id=toc-503>٣ </span>- وإن شعرتم بالخوف من ظلم اليتامى لأنه ذنب كبير، فخافوا كذلك أَلَمَ نسائكم بعدم العدل بينهن، والزيادة على أربع، فتزوجوا منهن اثنتين او ثلاثاً أو أربعاً إذا وثقتم بالقدرة على العدل، فإن خفتم عدم العدل فتزوجوا واحدة، أو استمتعوا بما تملك أيديكم من الإماء، ذلك أقرب إلى عدم الوقوع فى الظلم والجور، وأقرب ألا تكثر عيالكم فتعجزوا عن الإنفاق عليهم.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b836] VALUES (500,'<span data-type="title" id=toc-504>٤ </span>- وأعطوا النساء مهورهن عطية خالصة، وليس لكم حق فى شئ من هذه المهور، فإن طابت نفوسهن بالنزول عن شئ من المهر فخذوه وانتفعوا به طيباً محمود العاقبة.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b836] VALUES (501,'<span data-type="title" id=toc-505>٥ </span>- ولا تعطوا ضعاف العقول ممن لا يحسنون التصرف فى المال أموالهم التى هى أموالكم، فإن مال اليتيم وضعيف العقل مالكم، يعنيكم أمره وإصلاحه حتى لا يضيع المال، فقد جعله الله قوام الحياة، وأعطوهم من ثمراتها النصيب الذى يحتاجون إليه فى الطعام، واكسوهم وعاملوهم بالحسنى، وقولوا لهم: قولاً يرضيهم ولا يؤذيهم ولا يذلُّهم.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b836] VALUES (502,'<span data-type="title" id=toc-506>٦ </span>- واختبروا عقول اليتامى وتبينوا أحوالهم ومعرفتهم بالتصرف قبل البلوغ، حتى إذا أصبحوا صالحين للزواج وتبينتم رشدهم وسدادهم فادفعوا إليهم أموالهم، ولا تأكلوها مسرفين مستعجلين الانتفاع بها قبل أن يبلغوا وتُردُّ إليهم. ومن كان من الأوصياء عليهم غنياً فليتعفف عن أموال اليتامى، ومن كان فقيراً فليكتف بقدر ما يكفيه عُرْفاً، فإذا سلمتموهم أموالهم فأشهدوا عليهم، والله من ورائكم هو المحاسب والمراقب، وكفى به حسيباً ومراقباً.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b836] VALUES (503,'<span data-type="title" id=toc-507>٧ </span>- للرجال نصيب من الأموال التى يتركها الوالدان والأقربون - ميراثاً - وللنساء أيضاً نصيب مما ترك هؤلاء دون منع أو بخس، وهذه الأنصبة الثابتة مفروضة ومقدرة سواء قَلَّتْ الأموال أو كثرت.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b836] VALUES (504,'<span data-type="title" id=toc-508>٨ </span>- وإذا حضر قسمة التركة بعض الأقارب الذين لا يرثون من اليتامى والمساكين فأكرموهم بإعطائهم شيئاً من هذه التركة تطييباً لنفوسهم، ونزعاً للحسد من قلوبهم، ويحسن أن يشفع هذا العطاء بلين القول وحسن الاعتذار.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b836] VALUES (505,'<span data-type="title" id=toc-509>٩ </span>- وعلى الناس ألا يظلموا اليتامى، وليخافوا على ذريتهم الضعاف أن ينالهم من الظلم ما يفعلونه مع اليتامى، وليتقوا الله فيهم، وليقولوا قولاً مسدداً نحو الحق، غير ظالم لأحد.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b836] VALUES (506,'<span data-type="title" id=toc-510>١٠ </span>- إن الذين يظلمون اليتامى بأخذ أموالهم فى غير حق، إنما يأكلون ما يؤدى بهم إلى النار، فسيعذبون يوم القيامة بنار شديدة الإيلام.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b836] VALUES (507,'<span data-type="title" id=toc-511>١١ </span>- يأمركم الله فى شأن توريث أولادكم وأبويكم - إذا مُتُّم - بما يحقق العدل والإصلاح وذلك بأن يكون للذكر مثل نصيب الأنثيين إذا كان الأولاد ذكوراً وإناثاً. فإن كان جميع الأولاد إناثاً يزيد عددهن على اثنتين فلهن الثلثان من التركة. ويفهم من مضمون الآية أن الاثنتين نصيبهما كنصيب الأكثر من اثنتين. وإن ترك بنتاً واحدة فلها نصف ما ترك. وإن ترك أباً وأماً فلكل منهما السدس إن كان له ولد معهما - ولد ذكر أو أنثى - فإن لم يكن له ولد وورثه أبواه فقط فلأمه الثلث والباقى للأب. فإن كان له إخوة فلأمه السدس والباقى للأب ولا شئ للأخوة. تُعْطَى هذه الأنصبة لمستحقيها بعد أداء ما يكون عليه من دين، وتنفيذ ما وصَّى به فى حدود ما أجازه الشارع. هذا حكم الله فإنه عدل وحكمة، وأنتم لا تدرون الأقرب لكم نفعاً من الآباء والأبناء، والخير فيما أمر الله، فهو العليم بمصالحكم، الحكيم فيما فرض لكم.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b836] VALUES (508,'<span data-type="title" id=toc-512>١٢ </span>- للزوج نصف ما تركت الزوجة إن لم يكن لها ولد منه أو من غيره فإن كان لها ولد فلزوجها الربع من بعد وصية تُوصِى بها أو دين. وللزوجة - واحدة أو متعددة - الربع مما ترك الزوج إن لم يكن له منها أو من غيرها ولد، فإن كان له منهن أو من غيرهن فللزوجة أو الزوجات الثمن من بعد وصية يُوصى بها أو دين، وولد الابن كالولد فيما تقدم. وإن كان الميت رجلاً أو امرأة ولا ولد له ولا والد وترك أخاً لأم أو أختاً لأم فلكل واحد منهما السدس، فإن كانوا أكثر من ذلك فهم شركاء فى الثلث يستوى فى ذلك ذَكَرُهم وأنثاهم بمقتضى الشركة من بعد أداء الديون التى عليه وتنفيذ الوصية التى لا تضر بالورثة، وهى التى لا تتجاوز ثلث الباقى بعد الدين، فالزموا - أيها المؤمنون - ما وصاكم الله به، فإنه عليم بمن جار أو عدل منكم، حليم لا يعاجل الجائر بعقوبة.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b836] VALUES (509,'<span data-type="title" id=toc-513>١٣ </span>- تلك الأحكام المذكورة فى بيان المواريث وما سبقها، شرائع الله التى حدَّدها لعباده ليعملوا بها ولا يتعدوها، ومن يطع الله ورسوله فيما حكم به كان جزاؤه الجنة التى تجرى فيها الأنهار خالداً فيها وذلك الفوز العظيم.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b836] VALUES (510,'<span data-type="title" id=toc-514>١٤ </span>- ومن يعص الله ورسوله ويتعد حدود ما شرعه مستبيحاً ذلك التعدى، يجزه ناراً مخلداً فيها، يعذب بها بدنه، إلى جانب عذاب مهين تتألم به روحه.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b836] VALUES (511,'<span data-type="title" id=toc-515>١٥ </span>- واللاتى يأتين الزنا من النساء إن شهد عليهن أربعة من الرجال العادلين يمسكن فى البيوت محافظة عليهم ودفعاً للفساد والشر حتى يأتيهن الموت أو يفتح الله لهن طريقاً للحياة المستقيمة بالزواج والتوبة.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b836] VALUES (512,'<span data-type="title" id=toc-516>١٦ </span>- والرجل والمرأة اللذان يزنيان وهما غير متزوجين فلهما عقوبة محدودة - إذا ثبت الزنا بشهادة شهود أربعة عدول - فإن تابا بعد العقوبة فلا تذكِّروهما بما ارتكبا ولا تعيِّروهما به، إن الله يقبل برحمته توبة التائبين.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b836] VALUES (513,'<span data-type="title" id=toc-517>١٧ </span>- إنما يقبل الله التوبة من الذين يرتكبون المعاصى والذنوب بجهل منهم لعاقبتها، ثم يبادرون بالتوبة قبل حضور الموت، فهؤلاء يقبل الله توبتهم وهو عليم لا يخفى عليه صدق التوبة، حكيم لا يخطئ فى تقدير الأحكام والأمور.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b836] VALUES (514,'<span data-type="title" id=toc-518>١٨ </span>- وليس قبول التوبة للذين يرتكبون الذنوب ويستمرون في ممارستها ولا يبادرون بالأقلاع عنها والندم عليها، إلى أن يحضر أحدهم الموت فيقول: إنى أعلن الندم والتوبة الآن، كما لا تقبل التوبة من الذين يموتون على الكفر، وقد أعد الله للفريقين عذاباً أليماً في دار الجزاء.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b836] VALUES (515,'<span data-type="title" id=toc-519>١٩ </span>- يا أيها الذين آمنوا لا يجوز لكم أن تجعلوا النساء كالمتاع، فترثوهن زوجات لكم من غير صداق، وهن كارهات، ولا تظلموهن بالتضييق عليهن لينزلن عن بعض ما آتيتموهن من مهور، ولا تضيقوا عليهن لتستردوا بعض ما آتيتموهن من مال إلا أن يرتكبن إثماً بيناً بنشوز أو سوء خلق أو فجور، فلكم أن تضيِّقوا عليهن أو تأخذوا بعض ما آتيتموهن عند الفراق، وعليكم - أيها المؤمنون - أن تحسنوا عشرة نسائكم قولاً وعملاً فإن كرهتموهن لعيب فى الخَلْق أو الخُلُق أو غيرهما فاصبروا ولا تتعجلوا فراقهن فعسى أن يجعل الله فى المكروه لكم خيراً كثيراً، وعلم الأمور كلها عند الله.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b836] VALUES (516,'<span data-type="title" id=toc-520>٢٠ </span>- وإن أردتم أن تستبدلوا زوجة مكان أخرى وكنتم قد أعطيتم من تريدون طلاقها مالاً كثيراً فلا يحلُّ لكم أن تأخذوا منه شيئاً، أتأخذونه على وجه البطلان والإثم المبين؟ .',NULL,'110',NULL,NULL,'0');
INSERT INTO [b836] VALUES (517,'<span data-type="title" id=toc-521>٢١ </span>- وكيف يسوغ لكم أن تستردوا ما أعطيتم من مهر وقد امتزج بعضكم ببعض وأخذن منكم عقداً قوياً موثقاً أحل الله به العشرة الزوجية.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b836] VALUES (518,'<span data-type="title" id=toc-522>٢٢ </span>- ولا تتزوجوا - أيها الأبناء - ما تزوج آباؤكم من النساء، إنه كان أمراً فاحش القبح، يمقته الله والناس، وهو أسوأ سبيل ومقصد، وأن الله يعفو عما قد سلف منكم فى زمن الجاهلية.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b836] VALUES (519,'<span data-type="title" id=toc-523>٢٣ </span>- حَرَّمَ الله عليكم أن تتزوجوا أمهاتكم وبناتكم وأخواتكم وعماتكم وخالاتكم وبنات الأخ وبنات الأخت وأمهاتكم اللاتى أرضعنكم وأخواتكم من الرضاعة، وأمهات نسائكم، والمحرمات لغير النسب: أمهات الرضاعة، والأخوات من الرضاعة، وأمهات الزوجات وبنات الزوجات من غير الأزواج إذا دخلتم بهن، وزوجات أبناء الصلب، والجمع بين الأختين، وما سلف فى الجاهلية فإنه معفوٌّ عنه. إن الله غفور لما سلف قبل هذا النهج، رحيم بكم فيما شرع لكم.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b836] VALUES (520,'<span data-type="title" id=toc-524>٢٤ </span>- وحرم عليكم نكاح المتزوجات من النساء عامة، حرائر وغير حرائر، إلا من سبيتم وملكتم منهن فى حرب بينكم وبين الكفار، فإن نكاحهن السابق ينفسخ بالسبى، فيصرن حلالا لكم بعد استبراء أرحامهن، فالزموا ما كتب الله عليكم فى تحريم ما حرم، ولكم فيما عدا هؤلاء المؤمنات المحرمات أن تطلبوا بأموالكم نساء تتزوجون بهن، لا تقصدون الزنا أو المخادنة، فأى نساء استمتعتم بهن بعد الزواج منهن أحل الله لكم الدخول بهن فوفُّوهن مهورهن التى قدَّرتم لهن حقاً عليكم لا تسامح فيه تؤدونه فى موعد، ولا حرج عليكم فيما تم بينكم عن تراض من تنازل زوجة عن بعض مهرها أو زيادة زوج فيه، إن الله كان ولم يزل مُطَّلِعاً على شئون العباد، مُدَبِّراً لهم فى أحكام ما يصلح به أمرهم.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b836] VALUES (521,'<span data-type="title" id=toc-525>٢٥ </span>- ومن لم يستطع منكم نكاح الحرائر المؤمنات فله أن يتجاوزهن إلى ما يستطيع من المملوكات المؤمنات، والله أعلم بحقيقة إيمانكم وإخلاصكم، ولا تستنكفوا من نكاحهن، فأنتم وهن سواء فى الدين، فتزوجوهن بإذن أصحابهن وأدوا إليهن مهورهن التى تفرضونها لهن حسب المعهود بينكم فى حسن التعامل وتوفية الحق، واختاروهن عفيفات، فلا تختاروا زانية معلنة ولا خليلة، فإن أتين الزنا بعد زواجهن فعقوبتهن نصف عقوبة الحرة. وإباحة نكاح المملوكات عند عدم القدرة جائز لمن خاف منكم المشقة المفضية إلى الزنا وصبركم عن نكاح المملوكات مع العفة خير لكم، والله كثير المغفرة، عظيم الرحمة.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b836] VALUES (522,'<span data-type="title" id=toc-526>٢٦ </span>- يريد الله أن يوضح لكم أصلح السبل، ويدلكم على سنن الأنبياء والصالحين فى الحلال والحرام، ويتوب عليكم بالرجوع بكم إلى طريق طاعته، والله مُطَّلِع على شئونكم، مدبر فى أحكامه لما يصلح أمركم.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b836] VALUES (523,'<span data-type="title" id=toc-527>٢٧ </span>- والله يريد أن يرجع بكم إلى طاعته، ويريد الذين يتَّبعون ملاذهم ورغباتهم الفاجرة من الكفار والعصاة أن تبعدوا عن طريق الحق بعداً شديداً.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b836] VALUES (524,'<span data-type="title" id=toc-528>٢٨ </span>- يريد الله أن يُيَسِّر عليكم بتشريع ما فيه سهولة لكم، وتخفيف عليكم، وقد خلق الله الإنسان ضعيفاً أمام غرائزه وميوله، فيناسبه من التكاليف ما فيه يسر وسعة. وذلك هو ما يكلف الله عباده فضلاً وتيسيراً.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b836] VALUES (525,'<span data-type="title" id=toc-529>٢٩ </span>- يا أيها الذين آمنوا لا يأخذ بعضكم مال بعض بغير الحق. ولكن تجوز لكم التجارة بالتراضى منكم، ولا تهلكوا أنفسكم بمخالفة أوامر ربكم، ولا يجنى أحدكم على أخيه فإنما هى نفس واحدة، إن الله دائم الرحمة بكم.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b836] VALUES (526,'<span data-type="title" id=toc-530>٣٠ </span>- ومن يُقْدِمُ على فعل ما حرَّم الله اعتداءً وتجاوزاً لحقِّه فسوف ندخله ناراً يحترق فيها، وكان ذلك على الله هيِّناً ميسوراً.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b836] VALUES (527,'<span data-type="title" id=toc-531>٣١ </span>- إن تبتعدوا عن الذنوب العظيمة التى ينهاكم الله عنها نمنع عنكم ما دونها من السيئات والصغائر ما دمتم باذلين جهدكم فى الاستقامة، وننزلكم فى الدنيا والآخرة منزلاً فيه إحسان لكم وتكريم.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b836] VALUES (528,'<span data-type="title" id=toc-532>٣٢ </span>- ولا يتطلع الرجال إلى ما مَيَّز الله به النساء، ولا النساء إلى ما ميز الله به الرجال، فإن لكل فريق حظاً ملائماً لما طبع عليه من العمل وما أضيف إليه من الحقوق، فليتجه كل إلى رجاء الاستزادة من فضل الله بتنمية مواهبه والاستعانة على ما نيط به. إن الله كان عالماً أتم العلم بكل شئ، وقد أعطى كل نوع ما يصلح له.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b836] VALUES (529,'<span data-type="title" id=toc-533>٣٣ </span>- ولكلٍّ من الرجال والنساء جعلنا مستحقين لتركتهم يكونون خلفاء لهم، وهم الوالدان والأقربون والذين عقد المتوفى لهم عقداً مقتضاه أن يرثوه إذا مات من غير قرابة، وينصروه إذا احتاج إلى نصرتهم فى مقابل ذلك، فآتوا كل ذى حق حقه ولا تنقصوه شيئاً، إن الله كان رقيباً على كل شئ، حاضراً معكم، يشهد ما تتصرفون به.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b836] VALUES (530,'<span data-type="title" id=toc-534>٣٤ </span>- الرجال لهم حق الرعاية للنساء، والقيام بشئونهن بما أعطاهم الله من صفات تهيئهم للقيام بهذا الحق، وبسبب أنهم هم الذين يكدِّون ويكدحون لكسب المال الذى ينفقونه على الأسرة، فالصالحات مطيعات لله ولأزواجهن، حافظات لكل ما يغيب عن أزواجهن بسبب أمر الله بهذا الحفظ وتوفيقه لهن. والزوجات اللاتى تظهر منهن بوادر العصيان، فانصحوهن بالقول المؤثِّر، واعتزلوهن فى الفراش، وعاقبوهن بضرب خفيف غير مبرح ولا مُهين عند التمرد، فإن رجعن إلى طاعتكم فى أى سبيل من هذه السبل الثلاث، فلا تتطلبوا السبيل التى هى أشد منها بغياً عليهن، إن الله فوقكم وينتقم منكم إذا آذيتموهن أو بغيتم عليهن.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b836] VALUES (531,'<span data-type="title" id=toc-535>٣٥ </span>- وإن حدث خلاف بين الزوجين وخفتم منه حدوث انشقاق بينهما يعرضهما للانفصال، فاختاروا حكمين: أحدهما من أهله والآخر من أهلها، إن يريدا إصلاحاً يوفق الله بينهما فى الوصول إلى ما هو خير للزوجين من معاشرة بالمعروف أو تسريح بإحسان. إن الله كان مطلَّعاً على ظواهر العباد وبواطنهم.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b836] VALUES (532,'<span data-type="title" id=toc-536>٣٦ </span>- واعبدوا الله - وحده - ولا تجعلوا معه شريكاً فى الألوهية والعبادة، وأحسنوا إلى الوالدين إحساناً لا تقصير فيه، وإلى أقربائكم وإلى اليتامى، والذين افتقروا بسبب عجزهم أو ذهاب الكوارث بأموالهم، وبالجار القريب النسب والجار الأجنبى، والرفيق لك فى عمل أو طريق أو جلوس، والمسافر المحتاج الذى لا قرار له فى بلد معين، وبما ملكتم من الأرقاء فتيانا وفتيات. إن الله لا يحب من كان متعالياً على الناس، لا تأخذه بهم رحمة، كثير التمدح بنفسه.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b836] VALUES (533,'<span data-type="title" id=toc-537>٣٧ </span>- أولئك الذين يضمّون إلى التكبر والتباهى البخل بأموالهم وجهودهم عن الناس، ويدعون الناس إلى مثل صنيعهم من البخل، ويخفون نعمة الله وفضله عليهم فلا ينفعون أنفسهم ولا الناس بذلك، وقد أعددنا للجاحدين أمثالهم عذاباً مؤلماً مذلاً.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b836] VALUES (534,'<span data-type="title" id=toc-538>٣٨ </span>- والله لا يحب الذين يبذلون المال للرياء قاصدين أن يراهم الناس فيحمدوهم ويعظِّموهم، وهم غير مؤمنين بالله ولا بيوم الجزاء، لأنهم اتبعوا الشيطان فأضلَّهم، ومن يكن الشيطان صاحبه فبئس الصاحب.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b836] VALUES (535,'<span data-type="title" id=toc-539>٣٩ </span>- ألا قبحاً لهؤلاء، فما الذى يضرهم لو آمنوا بالله واليوم الآخر، وبذلوا مما آتاهم الله استجابة لهذا الإيمان، وما يقتضيه من إخلاص النية ورجاء الثواب؟ . والله عالم كل العلم ببواطن الأمور وظواهرها.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b836] VALUES (536,'<span data-type="title" id=toc-540>٤٠ </span>- إن الله لا يظلم أحداً شيئاً فلا ينقص من أجر عمله ولا يزيد فى عذابه شيئاً، ويضاعف للمحسن ثواب حسناته مهما قَلَّت، ويعطى من فضله عطاءً كبيراً غير مقابل بالحسنات التى يضاعفها.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b836] VALUES (537,'<span data-type="title" id=toc-541>٤١ </span>- فكيف يكون حال هؤلاء الباخلين والمُعْرضين عمَّا أمر الله به إذا جئنا يوم القيامة بكل نبى شهيداً على قومه، وجئنا بك - يا أيها النبى - شهيداً على قومك وفيهم المانعون والمعرضون؟ .',NULL,'115',NULL,NULL,'0');
INSERT INTO [b836] VALUES (538,'<span data-type="title" id=toc-542>٤٢ </span>- يوم يحدث هذا، يود الجاحدون المعرضون لو يغيبون فى الأرض كما يغيب الأموات فى القبور، وهم لا يستطيعون أن يخفوا عن الله أى شأن من شئونهم، ويظهر كل أحوالهم وأعمالهم.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b836] VALUES (539,'<span data-type="title" id=toc-543>٤٣ </span>- يا أيها الذين آمنوا لا تقربوا الصلاة فى المساجد حال سكركم حتى تفقهوا ما تقولون، ولا تدخلوا المساجد وأنتم على جنابة إلا إذا كنتم عابرى المساجد عبوراً دون استقرار فيها، حتى تطهروا بالاغتسال. وإن كنتم مرضى لا تستطيعون استعمال الماء خشية زيادة المرض أو بطء البرء، أو مسافرين يشق عليكم وجود الماء، فاقصدوا التراب الطيب، وكذلك إذا جاء أحد منكم من المكان المعد لقضاء الحاجة أو آتيتم النساء فلم تجدوا ماء تتطهرون به لفقده، فاقصدوا تراباً طيباً كذلك فاضربوا به أيديكم، وامسحوا بوجوهكم وأيديكم إن الله من شأنه العفو العظيم والمغفرة.',NULL,'116',NULL,NULL,'0');
INSERT INTO [b836] VALUES (540,'<span data-type="title" id=toc-544>٤٤ </span>- ألا تعجب من أمر هؤلاء الذين أوتوا حظاً من العلم مما جاء فى الكتب السابقة، يتركون الهدى ويبتغون الضلالة فى شأن أنفسهم، ويريدون منكم أن تبعدوا مثلهم عن الحق وهو صراط الله المستقيم؟ .',NULL,'116',NULL,NULL,'0');
INSERT INTO [b836] VALUES (541,'<span data-type="title" id=toc-545>٤٥ </span>- والله أعرف منكم بأعدائكم الحقيقيين، وأخبر بما تنطوى عليه نفوسهم، وولاية الله تحميكم وتكلؤكم وتكفيكم، فلا تطلبوا ولاية غير ولايته، وتكفيكم نصرته فلا تستعينوا بسواه.',NULL,'116',NULL,NULL,'0');
INSERT INTO [b836] VALUES (542,'<span data-type="title" id=toc-546>٤٦ </span>- من اليهود فريق يُميلون الكلام عن معناه، ويقولون فى أنفسهم للنبى: سمعنا القول وعصينا الأمر. ويقولون: اسمع كلامنا، لا سمعت دعاء، يدعون بذلك على النبى ويقولون: اسمع غير مسمع. فاللفظ يسوقونه ومرادهم منه الدعاء عليه، ويوهمون أن مرادهم الدعاء له.
INSERT INTO [b836] VALUES (543,'<span data-type="title" id=toc-547>٤٧ </span>- يا أيها الذين أوتوا الكتاب الذى أنزله الله آمنوا بما أنزلنا من القرآن على محمد مصدقاً لما معكم من قبل أن ننزل بكم عقاباً تنمحى به معالم وجوهكم فتصير كأقفيتها. لا أنف فيها ولا عين ولا حاجب، أو نطردكم من رحمتنا كما طردنا الذين خالفوا أمرنا بفعل ما نهوا عنه من الصيد يوم السبت. وكان قضاء الله نافذاً لا مرد له.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b836] VALUES (544,'<span data-type="title" id=toc-548>٤٨ </span>- إن الله لا يغفر الإشراك به، ويعفو عمّا دون الإشراك من الذنوب لمن يشاء من عباده، ومن يشرك بالله فقد ارتكب - مفترياً على الله - ذنباً كبيراً لا يستحق معه الغفران.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b836] VALUES (545,'<span data-type="title" id=toc-549>٤٩ </span>- لا تعجب من هؤلاء الكافرين الذين يفترون بأعمالهم، فنبين لهم سوء عملهم فيرونه حسناً، ويثنون على أنفسهم مزكين لها، والله - وحده - هو الذى يعلم الخبيث من الطَّيّب، فيزكى من يشاء ولا يظلم أى إنسان مهما كان قدره ضئيلاً.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b836] VALUES (546,'<span data-type="title" id=toc-550>٥٠ </span>- كيف يختلقون على الله الكذب بهذا ومثاله، وكفى بالكذب على الله ذنباً واضحاً يكشف عن خبيث طويتهم.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b836] VALUES (547,'<span data-type="title" id=toc-551>٥١ </span>- ألا تعجب من أمر هؤلاء الذين أوتوا حظاً من علم الكتاب يُرْضُون عبدة الأصنام والشيطان ويقولون عن الذين عبدوا الأوثان: إنهم أهدى من أهل الإيمان طريقاً.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b836] VALUES (548,'<span data-type="title" id=toc-552>٥٢ </span>- أولئك الذين خذلهم الله وطردهم من رحمته، ومن يخذله الله ويطرده من رحمته فليس له من ينصره ويحميه من غضب الله.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b836] VALUES (549,'<span data-type="title" id=toc-553>٥٣ </span>- لقد حُرم هؤلاء نعمة الإذعان للحق، كما حرموا السلطان، ولو أوتوه ما نفعوا الناس به بأى قدر ولو كان ضئيلاً.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b836] VALUES (550,'<span data-type="title" id=toc-554>٥٤ </span>- كيف يستكثر هؤلاء على العرب ما آتاهم الله من فضله ببعث النبى منهم، مع أن الله قد آتى إبراهيم وآله - وهو أبوكم وأبوهم - الكتاب المنزَّل والنبوة والملك العظيم.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b836] VALUES (551,'<span data-type="title" id=toc-555>٥٥ </span>- فَمِن الذين بُعِثَ فيهم إبراهيم وآله منهم مَنْ آمن بالكتاب المنزَّل إليهم، ومنهم من أعرض عنه، وحَسْبُ هؤلاء المعرضين عن دعوة الحق جهنم تكون ناراً حامية.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b836] VALUES (552,'<span data-type="title" id=toc-556>٥٦ </span>- إن الذين جحدوا حُجَجَنَا البينات، وكذَّبوا الأنبياء، سوف ندخلهم النار التى تُكْوَى بها جلودهم، وكلما فقدت الإحساس بالعذاب بدَّلهم الله جلوداً غيرها جديدة ليستمروا فى ألم العذاب، إن الله تعالى غالب على أمره، حكيم فى فعله، يعذب من جحد به وأصرَّ على ذلك حتى مات.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b836] VALUES (553,'<span data-type="title" id=toc-557>٥٧ </span>- والذين صدَّقوا بما جاءهم من ربهم وعملوا الأعمال الصالحة، سنثيبهم على إيمانهم وعملهم، فندخلهم جنات تجرى من تحت أشجارها الأنهار، لا تنتهى حياتهم فيها أبداً، لهم فيها أزواج مطهرة من العيوب والأدناس، ونحييهم حياة ناعمة فى ظل ظليل من العيش الطيب والنعيم المقيم.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b836] VALUES (554,'<span data-type="title" id=toc-558>٥٨ </span>- إن الله يأمركم - أيها المؤمنون - أن توصِّلوا جميع ما ائتمنتم عليه من الله أو الناس إلى أهله بالعدل، فلا تجوروا فى الحكم. هذه موعظة من ربكم فاحرصوا عليها، فنعمت الموعظة التى يعظكم بها. إن الله دائماً سميع لما يقال، بصير بما يفعل، فيعلم من أدَّى الأمانة ومن خان، ومن حكم بالعدل أو جار فَيُجَازِى كُلاً بعمله.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b836] VALUES (555,'<span data-type="title" id=toc-559>٥٩ </span>- يا أيها الذين صدَّقوا بما جاء به محمد أطيعوا الله، وأطيعوا الرسول، والذين يلون أمركم من المسلمين القائمين بالحق والعدل والمنفذين الشرع، فإن تنازعتم فى شئ فيما بينكم فاعرضوه على كتاب الله وعلى سنة رسوله لتعلموا حكمه، فإنه أنزل عليكم كتابه وبينه رسوله، وفيه الحكم فيما اختلفتم فيه، وهذا مقتضى إيمانكم بالله واليوم الآخر، وهو خير لكم، لأنكم تهتدون به إلى العدل فيما اختلفتم فيه، وهو أحسن عاقبة، لأنه يمنع الخلاف المؤدِّى إلى التنازع والضلال.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b836] VALUES (556,'<span data-type="title" id=toc-560>٦٠ </span>- ألا تعجب - أيها النبى - من الذين يدَّعون أنهم صدَّقوا بما أُنْزِل عليك من الكتاب وما أُنْزِل من قبلك من الكتب، يريدون أن يتحاكموا فى خصوماتهم إلى ما فيه الضلال والفساد وحكم غير الله، وقد أمرهم الله أن يجحدوه ولا يتحاكموا إليه، ويريد الشيطان أن يصدَّهم عن طريق الحق والهدى، فيضلهم عنه ضلالاً بعيداً.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b836] VALUES (557,'<span data-type="title" id=toc-561>٦١ </span>- وإذا قيل لهم أقبلوا على ما أنزل الله من قرآن وشريعة، وعلى رسوله ليبين لكم، رأيت الذين ينافقون يُعرضون عنك إعراضاً شديداً.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b836] VALUES (558,'<span data-type="title" id=toc-562>٦٢ </span>- فكيف تكون الحال إذا نزلت بهم نازلة بسبب خبث نفوسهم وسوء أعمالهم، ولم يجدوا ملجأً إلا إليك، فجاءوك يقسمون بالله بين يديك أنهم لا يريدون بأقوالهم وتصرفاتهم إلا الإحسان وطلب التوفيق.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b836] VALUES (559,'<span data-type="title" id=toc-563>٦٣ </span>- أولئك الذين يقسمون أنهم لا يريدون إلا الإحسان والعمل الموفق، يعلم الله حقيقة ما فى قلوبهم وكذب قولهم، فلا تلتفت إلى كلامهم وادعهم إلى الحق بالموعظة الحسنة، وقل لهم قولاً حكيماً بالغاً يصل إلى أعماق نفوسهم.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b836] VALUES (560,'<span data-type="title" id=toc-564>٦٤ </span>- وما أرسلنا من رسول إلا كان الشأن فى رسالته أن يطاع، وأن تكون طاعته بإذن من الله، وأن من ينافق أو يكذب أو يخالفه يكن ظالماً لنفسه، ولو أن هؤلاء الذين ظلموا أنفسهم رجعوا إلى الهدى فجاءوك وطلبوا المغفرة من الله على ما قدَّموا، ورجوت المغفرة لهم بمقتضى رسالتك وما رأيت من تغير حالهم، لوجدوا الله سبحانه وتعالى - كثير القبول للتوبة رحيماً بعباده.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b836] VALUES (561,'<span data-type="title" id=toc-565>٦٥ </span>- فوربك لا يُعَدُّونَ مؤمنين بالحق مذعنين له، حتى يجعلوك حكماً فيما يكون بينهم من نزاع، ثم لا تضيق نفوسهم أى ضيق بما قضيت، ويذعنوا لك إذعان المؤمنين المصدِّقين.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b836] VALUES (562,'<span data-type="title" id=toc-566>٦٦ </span>- ولو أننا فرضنا عليهم المشقة البالغة بأن أمرناهم بالجهاد المستمر، وأن يُعرِّضوا أنفسهم للتلف، أو ينفروا من ديارهم مجاهدين دائماً، ما أطاع إلا عدد قليل، ولكن الله - سبحانه وتعالى - لا يكلف إلا ما تحتمله الطاقة، ولو أنهم فعلوا وقاموا بحقه لكان فى ذلك خير الدنيا والآخرة لهم، وهو يؤدى إلى تثبيت الإيمان، والاستقرار والاطمئنان.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b836] VALUES (563,'<span data-type="title" id=toc-567>٦٧ </span>- وإذا قاموا بحق التكليف الإلهى الذى يكون فى وسعهم، لأعطاهم الله على ذلك الثواب العظيم من فضله.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b836] VALUES (564,'<span data-type="title" id=toc-568>٦٨ </span>- ولكانوا بسبب إطاعتهم فيما يطيقون، قد هداهم الله إلى الطريق المستقيم الذى لا إفراط فيه ولا تفريط.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b836] VALUES (565,'<span data-type="title" id=toc-569>٦٩ </span>- ومن يطع الله والرسول بالتسليم لأمرهما والرضا بحكمهما، فهو مع الذين أنعم الله عليهم بالهداية والتوفيق فى الدنيا والآخرة من أنبيائه وأتباعهم الذين صدقوهم واتبعوا مناهجهم والشهداء فى سبيل الله، والصالحين الذين صلحت سريرتهم وعلانيتهم، وما أحسن هؤلاء من رفقاء لا يشقى جليسهم، ولا يُمَلُّ حَدِيثُهُم.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b836] VALUES (566,'<span data-type="title" id=toc-570>٧٠ </span>- تلك المنزلة العظيمة لمن أطاع الله ورسوله هى الفضل الكبير من الله، وهو عليم بالأعمال ومثيب عليها، ويكفى المؤمن علم الله بحاله، وهو يقوم بطاعته ويطلب مرضاته.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b836] VALUES (567,'<span data-type="title" id=toc-571>٧١ </span>- يا أيها الذين آمنوا كونوا فى حذر دائم من أعدائكم، وخذوا الأهبة لرد كيدهم، واخرجوا لقتالهم جماعات متفرقة، جماعة بعد جماعة، أو اخرجوا لهم مجتمعين.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b836] VALUES (568,'<span data-type="title" id=toc-572>٧٢ </span>- واحذروا المثبطين المعوِّقين، فإن ممن يعيش معكم من يثبط عن القتال ويتخلف عنه، فإن أصابتكم نكبة فى الجهاد قال ذلك الفريق المتخلف شامتاً: قد أنعم الله علىَّ إذ لم أشهد معهم هذا القتال.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b836] VALUES (569,'<span data-type="title" id=toc-573>٧٣ </span>- وإن جاءكم فضل من الله بالنصر والفوز بغنائم القتال، قال ذلك الفريق - متحسراً متمنياً الأمانى - يا ليتنى كنت معهم فى هذا القتال فأفوز بعظيم الغنائم، ويقول هذا القول وكأنه لا رابطة من المودة تربطه بكم.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b836] VALUES (570,'<span data-type="title" id=toc-574>٧٤ </span>- إذا كان منكم من يعوق أو يبطئ لضعف فى إيمانه، أو خور فى عزيمته فليقاتل فى سبيل إعلاء كلمة اللَّه والحق، وهم الذين يبيعون الحياة الدنيا طالبين الحياة الآخرة، ومن يقاتل فى سبيل إعلاء كلمة اللَّه والحق فسينال إحدى الحسنيين. فإما أن يقتل فينال فضل الاستشهاد فى سبيل اللَّه، أو ينتصر فينال فضل الفوز فى الدنيا، وهو فى كلتا الحالتين يؤتيه اللَّه أجراً عظيما فى الآخرة.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b836] VALUES (571,'<span data-type="title" id=toc-575>٧٥ </span>- كيف يسوغ لكم ألا تقاتلوا فى سبيل اللَّه، مع أن المستضعفين من الرجال والنساء والذرية يستغيثون ويستنصرون ضارعين إلى اللَّه يقولون: ربنا أخرجنا من ولاية هؤلاء الظالمين ومكِّنَّا بقوتك ورحمتك من أن نكون تحت ولاية المؤمنين، واجعل لنا من عندك نصيراً ينصرنا.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b836] VALUES (572,'<span data-type="title" id=toc-576>٧٦ </span>- الذين صدَّقوا بالحق وأذعنوا له، يقاتلون فى سبيل إعلاء كلمة اللَّه والعدل والحق، والذين جحدوا أو عاندوا يقاتلون فى سبيل الظلم والفساد، وبذلك كانوا أولياء الشيطان، في أيها المؤمنون قاتلوهم لأنهم أعوان الشيطان وأنصاره واعلموا أنكم منتصرون عليهم بتأييد اللَّه، لأن تدبير الشيطان - مهما عظم فساده - ضعيف، والغلبة للحق.',NULL,'122',NULL,NULL,'0');
INSERT INTO [b836] VALUES (573,'<span data-type="title" id=toc-577>٧٧ </span>- ألم تنظر يا محمد فتعجب إلى الذين رغبوا فى القتال قبل أن يجئ الإذن به فقيل لهم: لم يأت وقت القتال، فكفوا أيديكم عنه، واحرصوا على إقامة الصلاة وإيتاء الزكاة، فلما فرض اللَّه عليهم القتال إذا طائفة منهم يخافون الناس كخوف اللَّه أو أشد وقالوا مستغربين: لِمَ كتبت علينا القتال؟ متوهمين أن فى فرضية القتال تعجيلاً لآجالهم ولذلك قالوا: هلاًّ أخرتنا إلى زمن قريب نستمتع فيه بما فى الدنيا؟ فقل لهم: تقدموا للقتال ولو أدى إلى استشهادكم، فمتاع الدنيا مهما عَظُمَ قليل بجوار متاع الآخرة، والآخرة خير وأعظم لمن اتقى اللَّه وستجزون على أعمالكم فى الدنيا ولا تنقصون من الجزاء شيئا مهما صغر.',NULL,'122',NULL,NULL,'0');
INSERT INTO [b836] VALUES (574,'<span data-type="title" id=toc-578>٧٨ </span>- إن الموت الذى تَفِرُّون منه ملاقيكم أينما كنتم، ولو كانت إقامتكم فى حصون مشيدة وإن هؤلاء الخائرين لضعف إيمانهم يقولون: إن أصابهم فوز وغنيمة هى من عند الله، وإن أصابهم جدب أو هزيمة يقولوا لك - يا محمد - هذا من عندك وكان بشُؤْمك. فقل لهم: كل ما يصيبكم مما تحبون أو تكرهون هو من تقدير اللَّه ومن عنده اختبار وابتلاء، فما لهؤلاء الضعفاء لا يدركون قولاً صحيحاً يتحدث به إليهم.',NULL,'122',NULL,NULL,'0');
INSERT INTO [b836] VALUES (575,'<span data-type="title" id=toc-579>٧٩ </span>- ما يصيبك - أيها النبى - من رخاء ونعمة وعافية وسلامة فمن فضل اللَّه عليك، يتفضل به إحسانا منه إليك، وما أصابك من شدة ومشقة وأذى ومكروه فمن نفسك بسبب تقصير أو ذنب ارتكبته. والخطاب للنبى لتصوير النفس البشرية وإن لم يقع منه ما يستوجب السيئة، وأرسلناك رسولاً من عندنا للناس جميعاً، واللَّه شهيد على تبليغك وعلى إجابتهم، وكفى به عليماً.',NULL,'122',NULL,NULL,'0');
INSERT INTO [b836] VALUES (576,'<span data-type="title" id=toc-580>٨٠ </span>- من يطع الرسول فقد أطاع اللَّه، لأنه لا يأمر إلا بما أمر اللَّه به، ولا ينهى إلا عما نهى اللَّه عنه. فكانت طاعته فى الامتثال والانتهاء طاعة للَّه، ومن أعرض عن طاعتك فما أرسلناك إلا بشيراً ونذيراً لا حفيظاً ومهيمناً عليهم، تحفظ عليهم أعمالهم، إن ذلك لنا لا لك.',NULL,'123',NULL,NULL,'0');
INSERT INTO [b836] VALUES (577,'<span data-type="title" id=toc-581>٨١ </span>- ويقول هذا الفريق المتردد: أمرك مطاع، وليس لك منا إلا الطاعة فيما تأمر وتنهى، ولكن إذا خرجوا من عندك وابتعدوا عنك دبَّرت طائفة منهم أمرا وبيتته، غير الذى تقوله أنت لهم من أمر ونهى، واللَّه - سبحانه وتعالى - يحصى عليهم ما يدبرونه فى خفاء. فلا تلتفت إليهم، وأعرض عنهم، وفوض أمرك إلى اللَّه، وتوكل عليه، وكفى أن يكون اللَّه وكيلك وحافظك تفوض إليه جميع أمورك.',NULL,'123',NULL,NULL,'0');
INSERT INTO [b836] VALUES (578,'<span data-type="title" id=toc-582>٨٢ </span>- أفلا يتدبر أولئك المنافقون كتاب اللَّه فيعلموا حجة اللَّه عليهم فى وجوب طاعته واتباع أمرك، وأن هذا الكتاب من عند اللَّه لائتلاف معانيه وأحكامه، وتأييد بعضه لبعض. فهذا دليل على أنه من عند اللَّه، إذ لو كان من عند غيره لتناقضت معانيه، واختلفت أحكامه اختلافاً كثيراً.',NULL,'123',NULL,NULL,'0');
INSERT INTO [b836] VALUES (579,'<span data-type="title" id=toc-583>٨٣ </span>- وإذا اطَّلَعَت هذه الطائفة المنافقة على أمر يتعلق بقوة المسلمين أو ضعفهم أفشوه ونشروه، جاهرين به للتغرير بالمسلمين أو إلقاء الرعب فى قلوبهم، أو توصيل أبنائهم إلى أعدائهم، ولو أن هؤلاء المنافقين المذيعين ردوا أمر الأمن والخوف إلى الرسول وإلى أولى الأمر من القواد وكبار الصحابة، وطلبوا معرفة الحقيقة من جهتهم لعلم أولئك الذين يحاولون استخراج الوقائع وإذاعتها الحق من جانب الرسول والقادة، ولولا فضل اللَّه عليكم بتثبيت قلوبكم على الإيمان، ومنع الفتنة، ورحمته بتمكينكم من أسباب الظفر والانتصار، لاتبع أكثركم إغواء الشيطان، ولم ينج من إغوائه إلا القليل.',NULL,'123',NULL,NULL,'0');
INSERT INTO [b836] VALUES (580,'<span data-type="title" id=toc-584>٨٤ </span>- وإذا كان بينكم أمثال هؤلاء المنافقين فأعرض عنهم، وقَاتِل فى سبيل كلمة اللَّه والحق، فلست مسئولاً إلا عن نفسك، ثم ادع المؤمنين إلى القتال وحُثّهم عليه، لعل اللَّه يدفع بك وبهم شدة الكافرين، واللَّه مؤيدكم وناصركم، وهو أشد قوة وأشد تنكيلاً بالكافرين.',NULL,'124',NULL,NULL,'0');
INSERT INTO [b836] VALUES (581,'<span data-type="title" id=toc-585>٨٥ </span>- وإن هؤلاء المنافقين يناصرون الفساد، وأهل الإيمان يناصرون الحق، ومن يناصر فى أمر حسن يكن له نصيب من ثوابه، ومن يناصر أهل السوء يكن عليه وزر من عقابه، واللَّه مقتدر على كل شئ محيط به.',NULL,'124',NULL,NULL,'0');
INSERT INTO [b836] VALUES (582,'<span data-type="title" id=toc-586>٨٦ </span>- وإذا حياكم أحد - أياً كان - بتحية من سلام أو دعاء أو تكريم أو غيره، فردوا عليه بأحسن منها أو بمثلها، فإن اللَّه محاسب على كل شئ كبيراً كان أو صغيراً.',NULL,'124',NULL,NULL,'0');
INSERT INTO [b836] VALUES (583,'<span data-type="title" id=toc-587>٨٧ </span>- اللَّه الذى لا إله إلا هو ولا سلطان لغيره، سيبعثكم حتماً من بعد مماتكم، وليحشرنكم إلى موقف الحساب لا شك فى ذلك. وهو يقول ذلك فلا تشكوا فى حديثه، وأى قول أصدق من قول اللَّه.',NULL,'124',NULL,NULL,'0');
INSERT INTO [b836] VALUES (584,'<span data-type="title" id=toc-588>٨٨ </span>- ما كان يسوغ لكم - أيها المؤمنون - أن تختلفوا فى شأن المنافقين الذين يُظْهِرون الإسلام ويُبْطِنون الكفر، وما يسوغ لكم أن تختلفوا فى شأنهم: أهم مؤمنون أم كافرون؟ ويقتلون أم ينظرون؟ وهم قابلون لأن يكونوا مهتدين أم لا ترجى منهم هداية؟ إنهم قلبت مداركهم بما اكتسبوا من أعمال، جعلت الشر يتحكم فيهم وما كان لكم أن تتوقعوا هداية من قَدَّر اللَّه فى علمه الأزلى أنه لن يهتدى، فإن من يكتب فى علم اللَّه الأزلى ضلاله، فلن تجدوا طريقاً لهدايته.',NULL,'124',NULL,NULL,'0');
INSERT INTO [b836] VALUES (585,'<span data-type="title" id=toc-589>٨٩ </span>- إنكم تودُّون هداية هؤلاء المنافقين، وهم يودون أن تكفروا مثلهم فتكونوا متساوين فى الكفر معهم، وإذا كانوا كذلك فلا تتخذوا منهم نصراء لكم، ولا تعتبروهم منكم، حتى يخرجوا مهاجرين ومجاهدين فى سبيل الإسلام. وبذلك تزول عنهم صفة النفاق، فإن أعرضوا عن ذلك وانضموا إلى أعدائكم فاقتلوهم حيث وجدتموهم ولا تعتبروهم منكم ولا تتخذوا منهم نصراء.',NULL,'124',NULL,NULL,'0');
INSERT INTO [b836] VALUES (586,'<span data-type="title" id=toc-590>٩٠ </span>- استثنى من المنافقين الذين يستحقون القتل لإفسادهم لجماعة المؤمنين أولئك الذين يرتبطون بقوم بينهم وبين المؤمنين ميثاق يمنع قتل المنتمين لأحد الفريقين، أو كانوا فى حيرة أيقاتلون مع قومهم الذين هم أعداء المسلمين، وليس ثمة ميثاق، أم يقاتلون مع المؤمنين؟ فإن الأولين يمنع قتلهم لأجل الميثاق، والآخرين يمنع قتلهم لأنهم فى حرج، وإن اللَّه - تعالى - لو شاء لجعلهم يحاربونكم، فإن آثروا الموقف السلبى وسالموكم فلا يسوغ لكم أن تقتلوهم، لأنه لا مسوغ لذلك.',NULL,'125',NULL,NULL,'0');
INSERT INTO [b836] VALUES (587,'<span data-type="title" id=toc-591>٩١ </span>- فإن ظهرتم على الشرك كانوا معكم، وإن ظهر المشركون على الإسلام كانوا مع المشركين، فهم يريدون أن يأمنوا المسلمين ويأمنوا قومهم من المشركين، وهؤلاء فى ضلال مستمر ونفاق، فإن لم يكفوا عن قتالكم ويعلنوكم بالأمن والسلام فاقتلوهم حيث وجدتموهم، لأنهم بعدم امتناعهم عن القتال قد مكنوا المؤمنين من قتلهم، وجعل اللَّه - تعالى - للمؤمنين حجة بيِّنة فى قتالهم.',NULL,'125',NULL,NULL,'0');
INSERT INTO [b836] VALUES (588,'<span data-type="title" id=toc-592>٩٢ </span>- إن تقسيم المنافقين ذلك التقسيم للاحتياط، حتى لا يُقْتَل مؤمن على ظن أنه منافق، لأن قتل المؤمن لا يجوز إلا أن يقع ذلك خطأ غير مقصود، وفى حال قتل المؤمن خطأ إن كان يعيش فى ولاية الدولة الإسلامية تدفع الدية لأهله تعويضاً عما فقدوه، وتعتق رقبة مؤمنة ليعوض جماعة المؤمنين عما فقدت، لأن عتق الرقبة المؤمنة إحياء لها بالحرية، فكأنه يكتفى بتحرير رقبة مؤمنة ليعوض المؤمنين عن فقده وإن كان ينتمى لقوم بينهم وبين المسلمين معاهدة سلم، فإنه يجب تحرير رقبة مؤمنة، وتسليم الدية لأهل المقتول، لأنهم لعهدهم لا يتخذونها لإيذاء المسلمين، وإذا كان القاتل خطأ لا يجد رقبة مؤمنة يعتقها، فإنه يصوم شهرين متتابعين لا يفطر يوما فيهما، لأن ذلك يكون تهذيباً لنفسه وتربية لها على الاحتراس، واللَّه - سبحانه وتعالى - عليم بالنفوس والنيات، حكيم يضع العقوبات فى مواضعها.',NULL,'125',NULL,NULL,'0');
INSERT INTO [b836] VALUES (589,'<span data-type="title" id=toc-593>٩٣ </span>- إن من يقتل مؤمنا قتلاً عدواناً متعمداً مستحلاً ذلك القتل، يكون جزاؤه الذى يكافئ جريمته أن يدخل جهنم ويستمر فيها، ويغضب اللَّه عليه ويطرده من رحمته وقد أعد اللَّه له فى الآخرة عذاباً عظيماً، فإن هذه أكبر جريمة فى الدنيا.',NULL,'126',NULL,NULL,'0');
INSERT INTO [b836] VALUES (590,'<span data-type="title" id=toc-594>٩٤ </span>- الاحتراس من قتل المؤمن واجب فى حال الغزو، فإذا سافرتم مجاهدين فى سبيل اللَّه - تعالى - فتعرفوا شأن الذين تقاتلونهم قبل القتال، أهم أسلموا أو لا يزالون على الشرك؟ ولا تقولوا لمن ألقى إليكم السلام وشارة الأمن لست مؤمناً، تريدون بذلك الأموال والغنائم، بل اقبلوا منهم السلام، فإن اللَّه أعدَّ لكم مغانم كثيرة. وأنتم - أيها المؤمنون - كنتم على الكفر قبل ذلك وهداكم اللَّه، فتبينوا أمر الذين تلقونهم. وأن الله عليم علماً دقيقاً لا يخفى عليه شئ، وأنه محاسبكم بمقتضى علمه.',NULL,'126',NULL,NULL,'0');
INSERT INTO [b836] VALUES (591,'<span data-type="title" id=toc-595>٩٥ </span>- وإن الجهاد مع هذا الاحتراس فضله عظيم جداً، فلا يستوى الذين يقعدون عن الجهاد فى منازلهم والذين يجاهدون بأموالهم وأنفسهم، فقد جعل اللَّه للمجاهدين درجة رفيعة فوق الذين قعدوا إلا إذا كان القاعدون من ذوى الأعذار التى تمنعهم من الخروج للقتال، فإن عذرهم يرفع عنهم الملامة ومع أن المجاهدين لهم فضل ودرجة خاصة بهم، فقد وعد اللَّه الفريقين المنزلة الحسنى والعاقبة الطيبة.',NULL,'126',NULL,NULL,'0');
INSERT INTO [b836] VALUES (592,'<span data-type="title" id=toc-596>٩٦ </span>- وهذه الدرجة التى اختص بها المجاهدين درجة عظيمة رفيعة، حتى كأنها درجات للتفاوت الكبير بينها وبين ما عداها، وإن لهم مع هذه الدرجة مغفرة كبيرة ورحمة واسعة.',NULL,'126',NULL,NULL,'0');
INSERT INTO [b836] VALUES (593,'<span data-type="title" id=toc-597>٩٧ </span>- وأن المسلم عليه أن يهاجر إلى الدولة الإسلامية ولا يعيش فى ذل، فإن الملائكة تسألهم: فيم كنتم حتى ارتضيتم حياة الذل والهوان؟ فيجيبون: كنا مستضعفين فى الأرض يذلنا غيرنا فتقول الملائكة: ألم تكن أرض الله واسعة فتهاجروا فيها بدل الذل الذى تقيمون فيه؟ وأولئك الذين يرضون بالذل مع قدرتهم على الانتقال، مأواهم عذاب جهنم، وأنها أسوأ مصير، فالمسلم لا يصح أن يعيش فى ذل، بل يعيش عزيزاً كريماً.',NULL,'127',NULL,NULL,'0');
INSERT INTO [b836] VALUES (594,'<span data-type="title" id=toc-598>٩٨ </span>- غير أنه يعفى من هذا العقاب من لا يستطيعون الانتقال من الضعفاء من الرجال والنساء والأطفال، فهؤلاء لا يستطيعون حيلة ولا يجدون سبيلا للخروج.',NULL,'127',NULL,NULL,'0');
INSERT INTO [b836] VALUES (595,'<span data-type="title" id=toc-599>٩٩ </span>- وأولئك يُرجى عفو الله عنهم، والله - تعالى - من شأنه العفو والغفران.',NULL,'127',NULL,NULL,'0');
INSERT INTO [b836] VALUES (596,'<span data-type="title" id=toc-600>١٠٠ </span>- ومن يهاجر طالباً بهجرته مناصرة الحق وتأييده، يجد فى الأرض التى يسير فيها مواضع كثيرة يرغم بها أنف أعداء الحق، ويجد سعة الحرية والإقامة العزيزة، وله بذلك الثواب والأجر العظيم، ومن يخرج من بيته مهاجراً إلى موطن الدولة العزيزة التى هى دولة الله ورسوله، ثم يدركه الموت قبل أن يصل فقد ثبت أجره، وتَكَرَّم الله فجعل الأجر حقا عليه، وغفر له ورحمه، لأن من شأنه الغفران والرحمة.',NULL,'127',NULL,NULL,'0');
INSERT INTO [b836] VALUES (597,'<span data-type="title" id=toc-601>١٠١ </span>- الصلاة فريضة محكمة لا تسقط فى السفر، ولكن لا إثم على من يقصرها فيه عن الحضر. فالذين يخرجون مسافرين - إن خافوا أن يتعرض لهم الكافرون بما يكرهون - لهم أن يقصروا الصلاة، فالصلاة التى هى أربع ركعات يصلونها اثنتين، وإن الحذر من تعرض الكافرين واجب لأنهم أعداء، عداوتهم واضحة.',NULL,'127',NULL,NULL,'0');
INSERT INTO [b836] VALUES (598,'<span data-type="title" id=toc-602>١٠٢ </span>- وإذا كنت - أيها النبى الأمين - فيهم وقامت صلاة الجماعة، فلا تنسوا الحذر من الأعداء، وذلك بتقسيم المسلمين إلى طائفتين: إحداهما تبدأ الصلاة مقتدية بك، وتكون الأخرى قائمة على الأسلحة والأمتعة لحراستها، فإذا أتممت نصف الصلاة ذهبت التى صلت وراءك وجاءت الأخرى فصليت بها الباقى، ثم تصلى ما فاتها وتصلى الأولى بقية الصلاة، وتسمى لاحقة والأخرى مسبوقة، إذ تؤدى أول الصلاة، واللاحقة تؤدى آخرها، وذلك التنظيم لكى لا تفوت الصلاة، وللحذر من الكافرين الذين يودون أن تغفلوا عن أسلحتكم وأمتعتكم فيميلوا عليكم دفعة واحدة، ويَنْقَضوُّا عليكم وأنتم فى الصلاة، وأن قتال المشركين مستمراً واجب، ولكن لا إثم عليكم أن تسكنوا إذا كان بكم مرض أو نزل مطر عاق عن القتال، ولكن على أن تكونوا على حذر دائم، وهذا عقاب الله للكافرين فى الدنيا، وفى الآخرة أعد لهم عذاباً مهيناً مذلاً.',NULL,'128',NULL,NULL,'0');
INSERT INTO [b836] VALUES (599,'<span data-type="title" id=toc-603>١٠٣ </span>- وإذا أتممتم صلاة الحرب التى تسمى صلاة الخوف فلا تنسوا ذكر الله دائماً، فاذكروه قائمين محاربين واذكروه وأنتم قاعدون، واذكروه وأنتم نائمون، فإن ذكر الله - تعالى - يُقَوِّى القلوب، وبه اطمئنانها، فإذا ذهب الخوف وكان الاطمئنان، فأدوا الصلاة متكاملة فإن الصلاة قد فرضت على المؤمنين موقوتة بأوقاتها.',NULL,'128',NULL,NULL,'0');
INSERT INTO [b836] VALUES (600,'<span data-type="title" id=toc-604>١٠٤ </span>- لا تضعفوا فى طلب القوم الكافرين الذين أعلنوا عليكم الحرب، وحاولوا أن يغيروا عليكم من كل مكان. والحرب بلا شك ألم، فإذا كنتم تألمون من جراحها وما يكون فيها، فإنهم يألمون أيضاً، والفرق بينكم وبينهم أنهم لا يطلبون الحق ولا يرجون عند الله شيئاً، وأنتم تطلبون الحق وترجون رضا الله والنعيم الدائم. والله عليم بأعمالكم وأعمالهم، حكيم يجازى كلاً بما يعمل.',NULL,'128',NULL,NULL,'0');
INSERT INTO [b836] VALUES (601,'<span data-type="title" id=toc-605>١٠٥ </span>- أنزلنا إليك القرآن حقاً وصدقاً، مشتملاً على كل ما هو حق، مبيناً للحق إلى يوم القيامة ليكون منارك فى الحكم بين الناس، فاحكم بينهم ولا تكن مدافعاً عن الخائنين.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b836] VALUES (602,'<span data-type="title" id=toc-606>١٠٦ </span>- وعند الحكم بين الناس اتَّجِه إلى الله وتذكر عظمته واطلب مغفرته ورحمته، فإن المغفرة والرحمة من شأنه - سبحانه وتعالى -.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b836] VALUES (603,'<span data-type="title" id=toc-607>١٠٧ </span>- ولا تدافع عن الذين يخونون ويبالغون فى إخفاء الخيانة فى أنفسهم، فإن الله لا يحب من يكون من شأنه الخيانة وارتكاب الذنوب.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b836] VALUES (604,'<span data-type="title" id=toc-608>١٠٨ </span>- يختفون ويستترون بخيانتهم من الناس، ولا يمكن أن تخفى على الله وهو معهم دائماً خياناتهم، وهم يتفقون ليلاً على ما لا يرضى الله من القول من رمى التهم على الأبرياء، - والله تعالى - يعلم علماً لا يخفى منه شئ مما يعملون.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b836] VALUES (605,'<span data-type="title" id=toc-609>١٠٩ </span>- إذا كنتم تدافعون عنهم فى الدنيا فلا يعاقبون عقاب الدنيا، فلا يوجد من يدافع عنهم يوم القيامة أمام - الله تعالى -، بل من يقبل أن يكون ولياً عليهم ناصراً لهم.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b836] VALUES (606,'<span data-type="title" id=toc-610>١١٠ </span>- وإن باب التوبة مفتوح، فمن يعمل أمراً سيئاً فى ذاته أو يظلم نفسه بارتكاب المعاصى ثم يطلب مغفرة الله - تعالى -، فإنه يجد الله - تعالى قابلاً توبته غافراً له، لأن من شأنه المغفرة والرحمة.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b836] VALUES (607,'<span data-type="title" id=toc-611>١١١ </span>- وإن الذنوب مضارها على نفس من يفعلها، فمن يكسب ذنباً فإنما هو ضد لنفسه، ومغبته على نفسه، والله - سبحانه وتعالى - يعلم ما ارتكب ويعامله بمقتضى حكمته، فيعاقب أو يغفر على حسب ما تقتضيه الحكمة.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b836] VALUES (608,'<span data-type="title" id=toc-612>١١٢ </span>- ومن يرتكب أخطاء تحيط بالنفس وذنوباً ثم يتهم بهذه الذنوب بريئاً لم يرتكبها، كمن يسرق شيئاً ويتهم غيره بسرقته، فقد وقع عليه وزران: أحدهما: الكذب والافتراء باتهام الأبرياء، والثانى: الذنب الواضح البين.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b836] VALUES (609,'<span data-type="title" id=toc-613>١١٣ </span>- ولولا أن اللَّه تفضل عليك بالوحى ورحمك بالإدراك النافذ، لأرادت طائفة منهم أن يضلوك، ولكنهم لا يضلون إلا أنفسهم، لأن اللَّه مُطْلِعُكَ، وبصيرتك نافذة إلى الحق، فلا ضرر عليك من تدبيرهم وتضليلهم، وقد أنزل عليك القرآن الكريم الذى هو ميزان الحق، وأودع قلبك الحكمة وعلمك من الشرائع والأحكام ما لم تعلمه إلا بوحى منه، وإن فضل اللَّه عليك عظيم دائماً.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b836] VALUES (610,'<span data-type="title" id=toc-614>١١٤ </span>- إن الذين يخفون أحاديث يحدثون بها أنفسهم أو يتحدثون بها فيما بينهم، لا خير فى هذه الأحاديث فى الكثير، لأن الشر يفرخ فى الخفاء، لكن إذا كان التحدث للأمر بصدقة يعطونها، أو للعزم على القيام بعمل غير مستنكر، أو تدبير إصلاح بين الناس، فإن ذلك خير، ومن يفعله طلباً لرضا اللَّه - سبحانه - فإن اللَّه - تعالى - يعطيه جزاءً كبيراً على عمله فى الدنيا والآخرة.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b836] VALUES (611,'<span data-type="title" id=toc-615>١١٥ </span>- وإن الذى يكون فى شقاق مع الرسول من بعد أن يتبين طريق الحق والهداية، ويتبع طريقاً غير طريق المؤمنين، ويدخل فى ولاية أعداء أهل الإيمان، فإنه يكون منهم إذ اختارهم أولياءه، وسيدخله الله - تعالى - النار يوم القيامة.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b836] VALUES (612,'<span data-type="title" id=toc-616>١١٦ </span>- وإن هذا المصير المؤلم لمن هم كذلك، لأنهم أعداء الإسلام، ومثله مثل من أشرك بالله، وإن كل ذنب قابل للغفران إلا الشرك بالله، وعبادة غيره، ومعاندة رسوله فى الحق، فإن الله من شأنه المغفرة إلا أن يشرك به فى عبادته، ويغفر ما دون ذلك لمن يشاء، وإن من يشرك بالله فى عبادته وولائه فقد تاه عن الحق وبَعُدَ عنه كثيراً، لأنه أفسد عقله ونفسه.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b836] VALUES (613,'<span data-type="title" id=toc-617>١١٧ </span>- وإن من أظهر مظاهر الضلال الذى بَعُد به عن الحق الشرك بالله، إنه يعبد ما لا يسمع ولا يبصر، ولا يضر ولا ينفع، ويُسَمَّى آلهته الباطلة بأسماء الإناث، كاللات والعزى ومناة، وغيرها من الأسماء المؤنثة، وإنه يتبع بهذه العبادة الشيطان.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b836] VALUES (614,'<span data-type="title" id=toc-618>١١٨ </span>- وإن هذا الشيطان طرده الله - تعالى - من ظل رحمته، وجعله فى طريق غوايته، وقد أقسم وأخذ على نفسه عهداً أن يتخذ من عباد الله - تعالى - عدداً معلوماً مقدراً يستهويهم بغوايته ويوسوس لهم بشرِّه.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b836] VALUES (615,'<span data-type="title" id=toc-619>١١٩ </span>- وإن قَسَمَه أن يضل الذين استهواهم بإبعادهم عن الحق ويثير أهواءهم وشهواتهم، ويجعلهم يتيهون فى أوهام وأمانٍ كاذبة يتمنونها، وإذا صاروا بهذه الأهواء وتلك الأمانى تحت سلطانه، دفعهم إلى أمور غير معقولة، وحملهم على أن يظنوها عبادة وهى أوهام كاذبة، فوسوس لهم بأن يقطعوا آذان بعض الإبل ويُغَيِّروا خلق الله فيها، وإن ما قطع أذنه لا يذبح ولا يعمل ولا يمنع من مرعى، وكل ذلك بأوامره، ثم يوسوس لهم بأنه دين، وأنهم بهذا يتبعونه، ويتخذونه نصيراً متبعاً من دون الله، ومن يتخذه نصيراً متبعاً يخسر خسراناً واضحاً، لأنه يضل عن الحقائق ويهمل عقله، ويناله الفساد فى الدنيا والعذاب فى الآخرة.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b836] VALUES (616,'<span data-type="title" id=toc-620>١٢٠ </span>- يزين الشيطان لهم الشر، ويعدهم النفع إذا فعلوه، ويلقى فى نفوسهم بأمانٍ يتمنونها، وليس وعده وتزيينه إلا تغريراً.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b836] VALUES (617,'<span data-type="title" id=toc-621>١٢١ </span>- وإن أولئك الذين ألغوا عقولهم واتبعوا وساوس الشيطان فى نفوسهم، مصيرهم إلى جهنم ولا يجدون منها خلاصاً.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b836] VALUES (618,'<span data-type="title" id=toc-622>١٢٢ </span>- هذا مصير أتباع الشيطان، أما مصير أتباع الله فالخير، وهم الذين آمنوا بالله ورسوله وعملوا الأعمال الصالحة، ولم يسيروا وراء أوهام كاذبة، فإن الله - تعالى - سيدخلهم يوم القيامة جنات فيها أنهار تجرى تحت ظلالها، وهى أكبر من أعظم جنات الدنيا، وإن ذلك مؤكد، لأنه وعد الله، ووعد الله لا يكون إلا حقاً، لا غرور فيه، إذ هو مالك كل شئ، ولا يتصور أن يكون أحدٌ فى الوجود أصدق من الله وعداً وقولا.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b836] VALUES (619,'<span data-type="title" id=toc-623>١٢٣ </span>- إن الجزاء ليس هو ما يتمناه ويحلم به الإنسان من غير عمل طيِّب مثمر، فليس الجزاء بما تتمنون - أيها المسلمون - ولا بما يتمناه ويحلم به أهل الكتاب من اليهود والنصارى، وإنما الجزاء والنجاة من العذاب بالإيمان والعمل الصالح، ومن يعمل سيئاً يُجْزَ به، ولا يجد له من دون الله من يواليه أو ينصره.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b836] VALUES (620,'<span data-type="title" id=toc-624>١٢٤ </span>- ومن يعملون الأعمال الصالحة بالقدر الذى يستطيعونه وهم مؤمنون بالله ورسوله، فإنهم يدخلون جنة النعيم ولا ينقصون أى مقدار ولو كان ضئيلا. ولا فرق فى الجزاء بين الذكر والأنثى، لأن الأنثى مكلفة. لها جزاء الخير، وعليها عذاب الشر.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b836] VALUES (621,'<span data-type="title" id=toc-625>١٢٥ </span>- وإن أساس عمل الخير منبعث من الاعتقاد السليم، وأحسن الدين أن يخلص لله - تعالى - فيجعل وجهه وعقله ونفسه لله لا يطلب سوى رضا الله سبحانه، وبذلك تستقيم مداركه فيدرك رسالة الرسل، وأن يقوموا بصفة مستمرة بأحسن الأعمال، ويتبعوا فى ذلك أبا الأنبياء إبراهيم - عليه السلام - فدينه هو دين الله، وهو الدين الذى يتجه إلى طلب الحق دائماً. وأن إبراهيم هو الذى تلتقى عنده الوحدة الدينية للمسلمين واليهود والنصارى، فاتبعوا طريقه، وأن الله أكرم إبراهيم فسماه خليلا.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b836] VALUES (622,'<span data-type="title" id=toc-626>١٢٦ </span>- وإن الإخلاص لله وإسلام الوجه إليه، هو إخلاص لمن أنشأ هذا الوجود وملكه، فلله كل ما فى السموات والأرض، من نجوم وأفلاك وشمس وقمر وجبال ووهاد وصحارٍ ومزارع، وهو مستبين كل شئ، وهو الذى يعلم علم إحاطة بكل ما يعمل الإنسان، ويجازيه بالخير خيراً وبالشر شراً.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b836] VALUES (623,'<span data-type="title" id=toc-627>١٢٧ </span>- قد استفتى الناس النبى فى شأن النساء وكنَّ ولا يزلن ضعيفات، فبيَّن الله لنبيه أن يبين حال النساء وحال الضعفاء فى الأسرة من الولدان واليتامى، وذكر أن يتامى النساء اللاتى يُزوجن ولا يأخذن مهورهن، والأولاد، واليتامى، كل هؤلاء يعاملون بالعدل والرحمة والرعاية، وأن كل ما يفعل من خير فإن الله يعلمه وهو الذى سيجزى به.',NULL,'133',NULL,NULL,'0');
INSERT INTO [b836] VALUES (624,'<span data-type="title" id=toc-628>١٢٨ </span>- وإن الزوجة إذا خافت من زوجها إهمالاً لشئون الأسرة أو إعراضاً عنها وعدم إقبال عليها. فلا إثم عليهما فى أن يحاولا إصلاح ما بينهما بالصلح الجميل والتقريب. والعاقل منهما يبدأ به، والصلح خير دائماً لا شر فيه، وإن الذى يمنع الصلح هو تَمَسُّك كل من الزوجين بحقوقه كاملة، إذ يسيطر الشح النفسى، ولا سبيل لعودة المودة إلا التساهل من أحد الجانبين وهو المحسن المتقى، ومن يعمل العمل الحسن ويتق الله، فإن الله خبير بعمله ومجازيه عليه.',NULL,'133',NULL,NULL,'0');
INSERT INTO [b836] VALUES (625,'<span data-type="title" id=toc-629>١٢٩ </span>- وإن العدل مع النساء بالمحبة الدائمة التى لا تشوبها شائبة، والمساواة بين محبتها بحيث يبادلها ما تبادله، أمر غير ممكن دائماً، وغير ممكنة كذلك المساواة فى المحبة بين الزوجات إذا كان عنده أكثر من واحدة، ولكن إذا حرصتم فلا تجوروا عليها وتميلوا كل الميل إلى غيرها وتتركوها لا هى ذات زوج ولا هى مطلقة، ويجب أن تصلحوا أنفسكم وتقيموا الأسرة على الصلاح من غير إفساد. وتتقوا الله فإن الله يغفر لكم ويرحمكم إذ من شأنه المغفرة والرحمة.',NULL,'133',NULL,NULL,'0');
INSERT INTO [b836] VALUES (626,'<span data-type="title" id=toc-630>١٣٠ </span>- وإذا لم يمكن الإصلاح واستحكمت النفرة، فإن التفريق لازم، وإن يتفرقا يغن الله كل واحد منهما من سعة رحمته وفضله، والأرزاق بيد الله، والله واسع الرحمة والفضل، وهو حكيم يضع الأمور فى مواضعها.',NULL,'133',NULL,NULL,'0');
INSERT INTO [b836] VALUES (627,'<span data-type="title" id=toc-631>١٣١ </span>- إن لب الدين هو الخضوع لمنْشِئ الكون ذى الجلال والإكرام، والاعتراف بسلطانه المطلق، فلله كل ما فى السموات والأرض، وبهذا السلطان المطلق قال تعالى: وصيَّنا أهل الديانات السماوية من أهل الكتاب وأنتم - معشر المسلمين - بأن تخافوه وتعبدوه، وألا تكفروا بعبادته، فهو صاحب السلطان المطلق فى الأرض والسموات، لا يخل بسلطانه شئ، وهو غنى عنكم، ومع ذلك يحمد لكم إيمانكم، لأن من شأنه الغنى، وأن يحمد مع ذلك فعل الخير من عباده.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b836] VALUES (628,'<span data-type="title" id=toc-632>١٣٢ </span>- ولله - سبحانه وتعالى - تدبير كل ما فى السموات والأرض، فهو المسيطر والمُسَيِّر والمدبر وكفى أن يكون هو المتولى أمر الكون لينتظم، وأمر الناس ليعبدوه، ويفوضوا أمورهم إليه ويتقوه.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b836] VALUES (629,'<span data-type="title" id=toc-633>١٣٣ </span>- إنكم - معشر العباد - فى سلطان الله، وهو القادر القاهر، إن يشأ يمتكم ويأت بآخرين، وهو ذو الجلال، قدير على ذلك وعلى كل شئ.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b836] VALUES (630,'<span data-type="title" id=toc-634>١٣٤ </span>- وإن الناس إذا طلبوا نعيم الدنيا ومنافعها الحلال من طريق الحق المستقيم، فإن الله يعطيهم نعيم الدنيا والآخرة، وهو وحده الذى يملك النعيمين.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b836] VALUES (631,'<span data-type="title" id=toc-635>١٣٥ </span>- إن العدل هو نظام الوجود، وهو القانون الذى لا يختلف النظر فيه، في أيها الذين أذعنتم لله الحق، ولدعوة رسله، كونوا مراقبين لأنفسكم فى الإذعان للعدل، ومراقبين للناس، فانصفوا المظلوم، وكونوا قائمين لا لرغبة غنى أو لعطف على فقير، لأن الله هو الذى جعل الغنى والفقير، وهو أولى بالنظر فى حال الغنى أو الفقير، وإن الهوى هو الذى يميل بالنفس عن الحق فلا تتبعوه لتعدلوا وإن تتولوا إقامة العدل أو تعرضوا عن إقامته فإن الله يعلم ما تعملون علماً دقيقاً، ويجازيكم بعملكم، إن خيراً فخير، وإن شراً فشر.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b836] VALUES (632,'<span data-type="title" id=toc-636>١٣٦ </span>- وإن الرسالات السماوية واحدة لوحدة مُرْسِل الرسل، وهو الله، في أيها الذين آمنوا أذعنوا لله وأخلصوا له، وصدقوا رسوله - محمداً - وصدقوا ما جاء فى كتابه الذى أنزله عليه واعملوا به، وصدقوا بالكتب التى نزلت من قبله كما أنزلها الله من غير تحريف ولا نسيان، آمنوا بكل ذلك، فإن من يكفر بالله خالق الوجود، والملائكة، وعالم الغيب، وكتب الله ورسله، وينكر اليوم الآخر، فقد تاه عن الطريق المستقيم، وأوغل فى طريق الضلال وأبعد فيه.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b836] VALUES (633,'<span data-type="title" id=toc-637>١٣٧ </span>- إن الإيمان إذعان مطلق وعمل مستمر بالحق، فالمترددون المضطربون ليسوا بمؤمنين، فالذين يؤمنون ثم يكفرون، ثم يؤمنون ثم يكفرون، وبهذا يزدادون كفراً، ما كان الله غافراً لهم ما يفعلون من شر، ولا ليهديهم إلى الحق، لأن غفران الله يقتضى توبة وإقلاعاً عن الشر، وهدايته تكون لمن يتجهون إلى الحق ويطلبونه.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b836] VALUES (634,'<span data-type="title" id=toc-638>١٣٨ </span>- يا أيها الرسول الكريم أنذر المنافقين بأن لهم عذاباً يوم القيامة مؤلماً.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b836] VALUES (635,'<span data-type="title" id=toc-639>١٣٩ </span>- وإن أولئك المنافقين يجعلون الولاية عليهم للكافرين ويتركون المؤمنين، فهل يطلبون العزة من هؤلاء الكافرين؟ إن العزة لله - وحده - يعطيها عباده المؤمنين، ومن اعتزَّ بالله عزَّ، ومن اعتز بغيره ذلَّ.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b836] VALUES (636,'<span data-type="title" id=toc-640>١٤٠ </span>- وقد نزَّل الله عليكم فى القرآن الكريم أنكم كلما سمعتم آية من الكتاب، وجحد بها الكافرون، فلا تقعدوا معهم حتى ينتقلوا إلى حديث غير حديث الاستهزاء، وإنكم إن لم تفعلوا وسمعتم استهزاءهم كنتم مثلهم فى الاستهزاء بالقرآن، وإن العاقبة وخيمة على الكافرين والمنافقين، فإن الله جامعهم جميعاً فى النار يوم القيامة.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b836] VALUES (637,'<span data-type="title" id=toc-641>١٤١ </span>- وإن أولئك المنافقين ينتظرون انتظار الحاقد الحانق الذى يتمنى السوء لكم إذا كنتم فى حرب مع الأعداء، فإن كان لكم نصر من الله وفتح لطريق الحق، قالوا للمؤمنين - وقد أذهلهم النصر الذى نصر الله به أهل الإيمان -: ألم نكن معكم باعتبارنا من جماعتكم؟ وإن كان للكافرين نصيب من الغلب اتجهوا إليهم وقالوا لهم: ألم نُغلِّب أموركم علينا حتى صارت أمورنا؟ وألم نمنحكم مودتنا ونمنعكم من المؤمنين؟ والله - سبحانه وتعالى - يحكم بينكم وبين هؤلاء المنافقين يوم القيامة، ولن يجعل الله للكافرين سبيلا للغلب على المؤمنين ما دام المؤمنون على صفة الإيمان الحق والعمل الصالح.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b836] VALUES (638,'<span data-type="title" id=toc-642>١٤٢ </span>- إن المنافقين بنفاقهم يحسبون أنهم يخادعون الله - تعالى - ويُخْفُون عنه حقيقة أنفسهم، والله سبحانه - خادعهم، فيمهلهم ويتركهم يرتعون فى شرهم، ثم يحاسبهم على ما يفعلون، وإن لهؤلاء المنافقين مظهراً حسَّا، ومظهراً نفسياً، فالحسى أنهم يقومون إلى الصلاة كسالى متباطئين، وصلاتهم رياء لا حقيقة. والمظهر النفسى أنهم لا يذكرون الله إلا أحياناً نادرة، ولو ذكروه لتركوا النفاق.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b836] VALUES (639,'<span data-type="title" id=toc-643>١٤٣ </span>- وإن المنافقين مترددون مضطربون، لا هم منكم ولا هم فى كل أحوالهم منهم، وذلك من ضعف الإيمان وضعف النفس، ومن الضلال عن الحق، ومن يكتب الله عليه فى علمه الأزلى الضلال، فلن تجد سبيلاً لهدايته.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b836] VALUES (640,'<span data-type="title" id=toc-644>١٤٤ </span>- وإن من أسباب النفاق أن المنافقين جعلوا لأهل غير الإيمان ولاية لهم ونصرة، فتجنبوا هذا - أيها المؤمنون - ولا تتخذوا الكافرين نصراء ذوى ولاية عليكم تخضعون لهم، وإنكم إن فعلتم ذلك كان لله حُجة عليكم بينة، فتدخلون مع المنافقين وتذلون، لأنكم لا تجعلون عزتكم من الله، ومن الحق، ومن العمل الصالح.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b836] VALUES (641,'<span data-type="title" id=toc-645>١٤٥ </span>- إن المنافقين بسبب نفاقهم يكونون فى أعماق جهنم، فهم فى أسفل مكان فيها، وأحط درجاتها، ولن تجد لهم نصيراً يدفع عنهم العذاب.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b836] VALUES (642,'<span data-type="title" id=toc-646>١٤٦ </span>- إلا الذين يتوبون منهم ويعودون إلى الله - تعالى - ويعتصمون به - وحده - ويخلصون ويسلمون وجوههم له، ويعملون الصالحات فإنهم بهذا يكونون من المؤمنين ولهم جزاء المؤمنين، وقد أعد الله - تعالى - جزاءً عظيماً للمؤمنين فى الدنيا والآخرة.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b836] VALUES (643,'<span data-type="title" id=toc-647>١٤٧ </span>- وإن الله - تعالى - لا مطلب له منكم إلا الإيمان به، وشكر نعمته، وإذا كنتم كذلك فلا عذاب لكم، ولكن جزاء على الخير والشكر، وإن الله - تعالى - شاكر يشكر لعباده عمل الخير، وعليم يعلم كل حالهم من خير وشر.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b836] VALUES (644,'<span data-type="title" id=toc-648>١٤٨ </span>- ينهى الله عباده عن قول السوء. إلا من وقع عليه ظلم، فيباح له أن يشكو ظالمه، ويذكر ما فيه من سوء، والله - سبحانه - سميع لكلام المظلوم، عليم بظلم الظالم، ويجازيه على عمله.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b836] VALUES (645,'<span data-type="title" id=toc-649>١٤٩ </span>- إن تُظهروا خيراً أو تُسِروه، أو تصفحوا عمن يسئ إليكم، يثبكم الله لتخلقكم بأخلاقه - تعالى - من العفو مع كمال القدرة، والله - سبحانه - عظيم العفو كامل القدرة.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b836] VALUES (646,'<span data-type="title" id=toc-650>١٥٠ </span>- إن الذين لا يؤمنون بالله ورسله، والذين يريدون التفرقة فى الإيمان بالله ورسله ويقولون: نؤمن ببعض الرسل دون بعض، فيؤمنون بمن يحبون، ويكفرون بمن لا يحبون، والواجب الإيمان بالجميع، لأن الإيمان لا يقبل أن يتجزأ.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b836] VALUES (647,'<span data-type="title" id=toc-651>١٥١ </span>- هؤلاء جميعاً هم الممعنون فى الكفر البين، وقد أعد الله لهم ولأمثالهم عذاباً شديداً مذلاً.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b836] VALUES (648,'<span data-type="title" id=toc-652>١٥٢ </span>- وأما من آمنوا بالله ورسله، ولم يُكذِّبوا بأحد منهم، فإن الله يثيبهم على كامل إيمانهم الثواب العظيم، والله غفور للتائبين، رحيم بعباده.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b836] VALUES (649,'<span data-type="title" id=toc-653>١٥٣ </span>- يسألك - أيها الرسول - أهل الكتاب من اليهود متعنتين، أن تقيم دليلاً على صدق نبوتك، فتأتيهم بكتاب خاص، ينزل عليهم من السماء بصدق رسالتك، ويدعوهم إلى الإيمان بك وطاعتك، فإن استكثرت ما سألوا فلا تعجل، فقد تعنت أسلافهم فسألوا موسى أكبر من ذلك، فقالوا: أرنا الله عيانا فعاقبهم على تعنتهم وظلمهم بصاعقة أهلكتهم ثم اذكر لهؤلاء جرماً أشد وأفظع، وهو أنهم اتخذوا العجل إلهاً لهم من دون خالقهم، بعد ما عاينوا الأدلة التى أظهرها موسى لفرعون وقومه ثم وسعهم عفو الله بعد إنابتهم إليه، وأيد الله موسى بالحُجة الواضحة والكلمة النافذة.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b836] VALUES (650,'<span data-type="title" id=toc-654>١٥٤ </span>- ورفع الله الجبل فوق بنى إسرائيل، تهديداً لهم لامتناعهم عن قبول شريعة التوراة، حتى قبلوا، وأخذ عليهم الميثاق، وأمرهم أن يدخلوا القرية خاضعين لله، وألا يتجاوزوا ما أمرهم بالتزامه من العبادة فى يوم السبت، ولا يعتدوا فيه، وقد أخذ عليهم فى كل ذلك عهداً مؤكداً.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b836] VALUES (651,'<span data-type="title" id=toc-655>١٥٥ </span>- فغضب الله عليهم، بسبب نقضهم هذا الميثاق، وكفرهم بآيات الله، وقتلهم الأنبياء ظالمين - ولا يكون ذلك إلا ظلماً -، وإصرارهم على الضلال بقولهم: قلوبنا محجوبة عن قبول ما نُدْعَى إليه، وليسوا صادقين فى قولهم، بل طمس الله على قلوبهم بسبب كفرهم، فلا يؤمن منهم إلا قلة من الناس.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b836] VALUES (652,'<span data-type="title" id=toc-656>١٥٦ </span>- وغضب الله عليهم بسبب كفرهم وافترائهم على مريم افتراء كبيراً.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b836] VALUES (653,'<span data-type="title" id=toc-657>١٥٧ </span>- وغضب الله عليهم بسبب قولهم مستخفين: إنا قتلنا المسيح عيسى ابن مريم رسول الله، والحق المستيقن أنهم ما قتلوه، كما زعموا وما صلبوه كما ادعوا. . ولكن شُبِّه لهم، فظنوا أنهم قتلوه وصلبوه، وإنما قتلوا وصلبوا من يشبهه، وقد اختلفوا من بعد ذلك فى أن المقتول عيسى أم غيره، وأنهم جميعاً لفى شك من أمره. . والواقع أنهم يقولون ما لا علم لهم به إلا عن طريق الظن، وما قتلوا عيسى قطعاً.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b836] VALUES (654,'<span data-type="title" id=toc-658>١٥٨ </span>- بل رفع الله عيسى إليه وأنقذه من أعدائه، ولم يصلبوه، ولم يقتلوه والله غالب لا يقهر، حكيم فى أفعاله.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b836] VALUES (655,'<span data-type="title" id=toc-659>١٥٩ </span>- وما من أحد من أهل الكتاب إلا ليدرك حقيقة عيسى قبل موته وأنه عبد الله ورسوله، ويؤمن به إيماناً لا ينفعه لفوات أوانه، ويوم القيامة يشهد عليهم عيسى بأنه بَلَّغَ رسالة ربه وأنه عبد الله ورسوله.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b836] VALUES (656,'<span data-type="title" id=toc-660>١٦٠ </span>- فبسبب ما وقع من اليهود من ظلم، عاقبهم الله، فَحَرَّم عليهم ألواناً من الطيبات كانت حلالاً لهم، وكان من هذا الظلم مَنْعُهم كثيراً من الناس من الدخول فى دين الله.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b836] VALUES (657,'<span data-type="title" id=toc-661>١٦١ </span>- وبسبب تعاملهم بالربا - وقد حرَّمه الله عليهم - وأخذهم أموال الناس بغير حق، كان عقاب الدين بتحريم بعض الطيبات عليهم. وقد أعدَّ الله لمن كفر عذاباً مؤلماً.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b836] VALUES (658,'<span data-type="title" id=toc-662>١٦٢ </span>- لكن المتثبتون فى العلم من اليهود والمؤمنون من أمتك - أيها النبى - يصدقون بما أُوحى إليك وما أُوحى إلى الرسل من قبلك. والذين يؤدون الصلاة حق الأداء، ويعطون الزكاة، ويصدقون بالله وبالبعث والحساب، أولئك سيجزيهم الله على إيمانهم وطاعتهم أحسن الجزاء.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b836] VALUES (659,'<span data-type="title" id=toc-663>١٦٣ </span>- إنا أوحينا إليك - أيها النبى - القرآن والشريعة، كما أوحينا من قبلك إلى نوح وإلى النبيين من بعده، وكما أوحينا إلى إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط، وهم أنبياء الله من ذرية يعقوب، وإلى عيسى وأيوب ويونس وهارون وسليمان، وكما أوحينا إلى داود فأنزلنا عليه كتاب الزبور.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b836] VALUES (660,'<span data-type="title" id=toc-664>١٦٤ </span>- وكذلك أرسلنا رسلاً كثيرين ذكرنا لك أنباءهم من قبل، ورسلاً آخرين لم نذكر قصصهم، وكانت طريقة الوحى إلى موسى أن كلَّمه الله تكليماً من وراء حجاب بلا واسطة.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b836] VALUES (661,'<span data-type="title" id=toc-665>١٦٥ </span>- بعثنا هؤلاء الرسل جميعاً، مبشرين من آمن بالثواب، ومنذرين من كفر بالعقاب، حتى لا يكون للناس على الله حُجة يتعللون بها بعد إرسال الرسل، والله قادر على كل شئ، غالب لا سلطان لأحد معه، حكيم فى أفعاله.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b836] VALUES (662,'<span data-type="title" id=toc-666>١٦٦ </span>- لكن إذا لم يشهدوا بصدقك، فالله يشهد بصحة ما أنزل إليك، لقد أنزله إليك مُحكماً بمقتضى علمه، والملائكة يشهدون بذلك، وتغنيك - أيها الرسول - شهادة الله عن كل شهادة.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b836] VALUES (663,'<span data-type="title" id=toc-667>١٦٧ </span>- إن الذين كفروا فلم يصدقوك، ومنعوا الناس عن الدخول فى دين الله، قد بعدوا عن الحق بُعداً شديداً.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b836] VALUES (664,'<span data-type="title" id=toc-668>١٦٨ </span>- إن الذين كفروا وظلموا أنفسهم بالكفر. وظلموا الرسول بجحد رسالته، وظلموا الناس، إذ كتموهم الحق، لن يغفر الله لهم ما داموا على كفرهم، ولن يهديهم طريق النجاة، وما كان من شأنه - سبحانه - أن يغفر لأمثالهم وهم فى ضلالهم.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b836] VALUES (665,'<span data-type="title" id=toc-669>١٦٩ </span>- ولكن يسلك بهم طريق النار. مُخَلَّدين فيها أبداً، وأمر ذلك يسير على الله.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b836] VALUES (666,'<span data-type="title" id=toc-670>١٧٠ </span>- يا أيها الناس قد جاءكم الرسول محمد بالدين الحق من عند ربكم، فَصَدِّقوا بما جاء به يكن خيراً لكم، وإن أبيتم إلا الكفر فالله غنى عن إيمانكم، مالك لكم، فله ما فى السموات والأرض ملكاً وخلقاً وتصرفاً، وهو العليم بخلقه، الحكيم فى صنعه، لا يضيع أجر المحسن، ولا يهمل جزاء المسئ.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b836] VALUES (667,'<span data-type="title" id=toc-671>١٧١ </span>- يا أهل الكتاب لا تتجاوزوا الحق مغالين فى دينكم، ولا تفتروا على الله الكذب، فتنكروا رسالة عيسى، أو تجعلوه إلهاً مع الله، فإنما المسيح رسول كسائر الرسل، خلقه الله بقدرته وكلمته التى بُشَّر بها، ونفخ روحه جبريل فى مريم، فهو سِرٌّ من أسرار قدرته، فآمنوا بالله ورسله جميعاً إيماناً صحيحاً ولا تدَّعوا أن الآلهة ثلاثة، انصرفوا عن هذا الباطل يكن خيراً لكم، فإنما الله واحد لا شريك له، وهو منزه عن أن يكون له ولد، وكل ما فى السموات والأرض ملك له، وكفى به - وحده - مدبِّراً لملكه.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b836] VALUES (668,'<span data-type="title" id=toc-672>١٧٢ </span>- لن يترفع المسيح عن أن يكون عبداً لله، ولن يترفع عن ذلك الملائكة المقربون، ومن يتكبر ويترفع عن عبادة الله فلن يفلت من عقابه يوم يجمع الله الناس للحساب.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b836] VALUES (669,'<span data-type="title" id=toc-673>١٧٣ </span>- فأما الذين آمنوا وعملوا الصالحات فيوفيهم ثواب أعمالهم ويزيدهم من فضله، إكراماً وإنعاماً، وأما الذين أَنِفُوا أن يعبدوه، وترفعوا أن يشكروه فقد أعد لهم عذاباً شديد الإيلام، لن يدفعه عنهم معين ولن يمنعهم منه نصير.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b836] VALUES (670,'<span data-type="title" id=toc-674>١٧٤ </span>- يا أيها الناس، قد جاءتكم الدلائل الواضحة على صدق الرسول محمد، وأنزلنا إليكم على لسانه قرآناً بيناً كالنور، يضئ الطريق ويهديكم إلى النجاة.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b836] VALUES (671,'<span data-type="title" id=toc-675>١٧٥ </span>- فأما الذين صدقوا بالله ورسالاته، وتمسكوا بدينه، فسيدخلهم فى الآخرة جناته، ويغمرهم بفيض رحمته، ويشملهم بواسع فضله وسيوفقهم فى الدنيا إلى الثبات على صراطه المستقيم.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b836] VALUES (672,'<span data-type="title" id=toc-676>١٧٦ </span>- يسألونك - أيها النبى - عن ميراث من مات ولا ولد له ولا والد، فحكم الله فيه هو: أنه إذا كان للمُتَوَفَّى أخت، فلها نصف تركته، وأيضاً إذا كان للمتوفاة التى لا زوج لها ولا ولد أخ فله تركتها، وإن كان للمُوَرِّث أختان فلهما ثلثا تركته وإن كانوا إخوة من ذكور وإناث فنصيب الذكر مثل نصيب الأنثيين. يبين الله لكم هذا البيان حتى لا تضلوا فى تقسيم الأنصباء، والله عالم علماً كاملاً بكل شئ من أعمالكم، ومجازيكم عليها.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b836] VALUES (673,'<span data-type="title" id=toc-678>١ </span>- يا أيها المؤمنون: التزموا الوفاء بجميع العهود التى بينكم وبين الله، والعهود المشروعة التى بينكم وبين الناس. وقد أحل الله لكم أكل لحوم الأنعام من الإبل والبقر والغنم، إلا ما ينص لكم على تحريمه. ولا يجوز لكم صيد البر إذا كنتم مُحْرِمين، أو كنتم فى أرض الحرم. إن الله يقضى بحكمته ما يريد من أحكام، وأن هذا من عهود الله عليكم.',NULL,'143',NULL,NULL,'0');
INSERT INTO [b836] VALUES (674,'<span data-type="title" id=toc-679>٢ </span>- يا أيها المؤمنون لا تستبيحوا حرمة شعائر الله، كمناسك الحج وقت الإحرام قبل التحلل منه وسائر أحكام الشريعة، ولا تنتهكوا حرمة الأشهر الحرم بإثارة الحرب فيها، ولا تعترضوا لما يُهْدَى من الأنعام إلى بيت الله الحرام باغتصابه أو منع بلوغه محله، ولا تنزعوا القلائد، وهى العلامات التى توضع فى الأعناق، إشعاراً بقصد البيت الحرام، وأنها ستكون ذبيحة فى الحج، ولا تعترضوا لِقُصَّادِ بيت الله الحرام يبتغون فضل الله ورضاه، وإذا تحللتم من الإحرام، وخرجتم من أرض الحرم، فلكم أن تصطادوا، ولا يحملنكم بغضكم الشديد لقوم صدوكم عن المسجد الحرام على الاعتداء عليهم. وليتعاون بعضكم مع بعض - أيها المؤمنون - على فعل الخير وجميع الطاعات، ولا تتعاونوا على المعاصى ومجاوزة حدود الله، واخشوا عقاب الله وبطشه، إن الله شديد العقاب لمن خالفه.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b836] VALUES (675,'<span data-type="title" id=toc-680>٣ </span>- حرَّم الله عليكم - أيها المؤمنون - أكل لحم الميتة - وهى كل ما فارقته الروح من غير ذبح شرعى -، وأكل الدم السائل، ولحم الخنزير، وما ذكر اسم غير الله عليه عند ذبحه، وما مات خنقاً، أو التى ضربت حتى ماتت، وما سقط من علو فمات، وما مات بسبب نطح غيره له، وما مات بسبب أكل حيوان مفترس منه. وأما ما أدركتموه وفيه حياة مما يحل لكم أكله وذبحتموه فهو حلال لكم بالذبح. وحرَّم الله عليكم ما ذبح قربة للأصنام، وحرم عليكم أن تطلبوا معرفة ما كتب فى الغيب بواسطة القرعة بالأقداح. وتناول شئ مما سبق تحريمه ذنب عظيم وخروج عن طاعة الله. ومن الآن انقطع رجاء الكفار فى القضاء على دينكم، فلا تخافوا أن يتغلبوا عليكم، واتقوا مخالفة أوامرى. اليوم أكملت لكم أحكام دينكم، وأتممت عليكم نعمتى بإعزازكم وتثبيت أقدامكم، واخترت لكم الإسلام ديناً. فمن ألجأته ضرورة جوع إلى تناول شئ من المحرمات السابقة ففعل لدفع الهلاك عن نفسه غير منحرف إلى المعصية، فإن الله يغفر للمضطر ما أكل، دفعاً للهلاك، وهو رحيم به فيما أباح له.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b836] VALUES (676,'<span data-type="title" id=toc-681>٤ </span>- يسألك المؤمنون - أيها الرسول - ماذا أحل الله لهم من طعام وغيره فقل لهم: أحلّ الله لكم كل طيب تستطيبه النفوس السليمة، وأحلّ لكم ما تصطاده الجوارح التى علمتموها الصيد بالتدريب، مستمدين ذلك مما علمكم الله. فكلوا من صيدها الذى أرسلتموها إليه وأمسكته عليكم، واذكروا اسم الله عند إرسالها، واتقوا الله بالتزام ما شرع لكم، ولا تتجاوزوه، واحذروا مخالفة الله فيه، فإنه سريع الحساب.',NULL,'145',NULL,NULL,'0');
INSERT INTO [b836] VALUES (677,'<span data-type="title" id=toc-682>٥ </span>- اليوم - منذ نزول هذه الآية - أحل الله لك كل طيب تستطيبه النفوس السليمة، وأحل لكم طعام أهل الكتاب، وذبائحهم، مما لم يرد نص بتحريمه، كما أحل لهم طعامكم، وأحل لكم زواج الحرائر والعفائف من المؤمنات ومن أهل الكتاب، إذا أديتم لهن مهورهن قاصدين الزواج، غير مستبيحين العلاقات غير الشرعية علانية، أو بطريق اتخاذ الخلائل. ومن يجحد الدين فقد ضاع ثواب عمله الذى كان يظن أنه قربى، وهو فى الآخرة من الهالكين.',NULL,'145',NULL,NULL,'0');
INSERT INTO [b836] VALUES (678,'<span data-type="title" id=toc-683>٦ </span>- في أيها المؤمنون، إذا أردتم القيام إلى الصلاة ولم تكونوا متوضئين، فتوضأوا بغسل وجوهكم وأيديكم مع المرافق، وامسحوا رءوسكم - كلها أو بعضها - واغسلوا أرجلكم مع الكعبين. وإن كنتم جنباً عند القيام إلى',NULL,'145',NULL,NULL,'0');
INSERT INTO [b836] VALUES (679,'الصلاة بسب ملامسة أزواجكم، فاغسلوا جميع أبدانكم بالماء، وأن كنتم مرضى مرضاً يمنع من استعمال الماء، أو كنتم مسافرين يتعسر عليكم وجود الماء، أو عند رجوعكم من مكان قضاء الحاجة، أو لامستم النساء ولم تجدوا ماء، فعليكم بالتيمم بالتراب الطهور، بمسح وجوهكم وأيديكم به. ما يريد الله فيما أمركم به أن يضيق عليكم، ولكنه شرع ذلك لتطهيركم ظاهراً وباطناً وليتم نعمه عليكم بالهداية والبيان والتيسير، لتشكروا الله على هدايته وتمام نعمته بالمداومة على طاعته.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b836] VALUES (680,'<span data-type="title" id=toc-685>٧ </span>- واذكروا - أيها المؤمنون - نعمة الله عليكم، بهدايتكم إلى الإسلام، وحافظوا على تنفيذ عهده الذى عاهدكم عليه حين بايعتم رسوله - محمداً - على السمع والطاعة، واتقوا الله بالمحافظة على هذه العهود، فإنه سبحانه عليم بخفيات قلوبكم، فمجازيكم عليها.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b836] VALUES (681,'<span data-type="title" id=toc-686>٨ </span>- يا أيها المؤمنون، حافظوا محافظة تامة على أداء حقوق الله، وأدُّوا الشهادة بين الناس على وجهها الحق، ولا يحملنكم بغضكم الشديد لقوم على أن تجانبوا العدل معهم، بل التزموا العدل، فهو أقرب سبيل إلى خشية الله والبعد عن غضبه، واخشوا الله فى كل أموركم، فإنه - سبحانه - عليم بكل ما تفعلون، ومجازيكم عليه.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b836] VALUES (682,'<span data-type="title" id=toc-687>٩ </span>- تفضل الله فوعد الذين صدَّقوا بدينه، وعملوا الأعمال الصالحة أن يعفو عن ذنوبهم، ويجزل لهم الثواب.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b836] VALUES (683,'<span data-type="title" id=toc-688>١٠ </span>- والذين جحدوا دينه، وكذبوا بآياته الدالة على وحدانيته، وصدق رسالته، فأولئك هم أهل جهنم المخلدون فيها.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b836] VALUES (684,'<span data-type="title" id=toc-689>١١ </span>- يا أيها المؤمنون، تذكروا نعمة الله عليكم فى وقت الشدة، حين همَّ قوم - جماعة من المشركين - أن يفتكوا بكم، وبرسولكم، فمنع أذاهم عنكم، ونجاكم منهم. والزموا تقوى الله، واعتمدوا عليه وحده فى أموركم، فهو كافيكم، وشأن المؤمن أن يكون اعتماده على الله وحده دائماً.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b836] VALUES (685,'<span data-type="title" id=toc-690>١٢ </span>- إن الله أخذ العهد على بنى إسرائيل بالسمع والطاعة، فأقام عليهم اثنى عشر رئيساً منهم لتنفيذ العهد، ووعدهم الله وعداً مؤكداً بأن يكون معهم بالعون والنصر إن أدوا الصلاة على وجهها، وآتوا الزكاة المفروضة عليهم، وصدَّقوا برسله جميعاً، ونصروهم، وأنفقوا فى سبيل الخير، وإذا ما فعلوا ذلك، تجاوز الله عن ذنوبهم، وأدخلهم جناته التى تجرى من تحتها الأنهار، فمن كفر ونقض العهد منهم بعد ذلك، فقد حاد عن الطريق السوى المستقيم.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b836] VALUES (686,'<span data-type="title" id=toc-691>١٣ </span>- فبسبب نقض بنى إسرائيل عهودهم، استحقوا الطرد من رحمة الله، وصارت قلوبهم صلبة لا تلين لقبول الحق، وأخذوا يصرفون كلام الله فى التوراة عن معناه، إلى ما يوافق أهواءهم، وتركوا نصيباً وافياً مما أمروا به فى التوراة، وستظل أيها الرسول ترى منهم ألواناً من الغدر ونقض العهد، إلا نفراً قليلا منهم آمنوا بك فلم يخونوا ولم يغدروا. فتجاوز أيها الرسول عما فرط من هؤلاء، واصفح وأحسن إليهم، إن الله يحب المحسنين.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b836] VALUES (687,'<span data-type="title" id=toc-692>١٤ </span>- وكذلك أخذ الله العهد على النصارى - الذين قالوا: إنا نصارى - بالإيمان بالإنجيل وبالوحدانية، فتركوا نصيباً وافراً مما أُمروا به فى الإنجيل، فعاقبهم الله على ذلك بإثارة العداوة والخصومة بينهم، فصاروا فرقاً متعادية إلى يوم القيامة، وسوف يخبرهم الله يومئذ بما كانوا يعملون ويجازيهم عليه.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b836] VALUES (688,'<span data-type="title" id=toc-693>١٥ </span>- يا أهل الكتاب قد جاءكم رسولنا - محمد - داعياً إلى الحق، يظهر لكم كثيراً مما كنتم تكتمونه من التوراة والإنجيل، ويدع كثيراً مما أخفيتموه مِمَّا لم تدع الحاجة إلى إظهاره، قد جاءكم من عند الله شريعة كاملة هى نور فى ذاتها، ويبيِّنها كتاب واضح.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b836] VALUES (689,'<span data-type="title" id=toc-694>١٦ </span>- يهدى الله بهذا الكتاب إلى سبيل النجاة من اتجه إلى مرضاته، ويخرجهم من ظلمات الكفر إلى نور الإيمان بتوفيقه، ويرشدهم إلى طريق الحق.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b836] VALUES (690,'<span data-type="title" id=toc-695>١٧ </span>- لقد كفر الذين زعموا - باطلا - أن الله هو المسيح ابن مريم، فقل - أيها الرسول - لهؤلاء المجترئين على مقام الألوهية: لا يستطيع أحد أن يمنع مشيئة الله إن أراد أن يهلك عيسى وأمه، ويهلك جميع مَن فى الأرض فإن لله - وحده - ملك السموات والأرض وما بينهما، يخلق ما يشاء على أى مثال أراد، والله عظيم القدرة لا يعجزه شئ.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b836] VALUES (691,'<span data-type="title" id=toc-696>١٨ </span>- وقالت اليهود والنصارى: إننا المفضلون، لأننا أبناء الله والمحببون لديه فقل لهم - أيها الرسول -: فلماذا يعذبكم بذنوبكم، ويصليكم نار جهنم؟ لقد كذبتم لأنكم كسائر البشر مخلوقون ومحاسبون على أعمالكم، وبيد الله - وحده - المغفرة لمن يشاء أن يغفر له، والعذاب لمن يشاء أن يعذبه، لأن لله ملك السموات والأرض وما بينهما، وإليه المنتهى.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b836] VALUES (692,'<span data-type="title" id=toc-697>١٩ </span>- يا أهل الكتاب قد جاءتكم رسالة رسولنا الذى يظهر لكم الحق، بعد إذ توقفت الرسالات فترة من الزمن، حتى لا تعتذروا عن كفركم بأن الله لم يبعث إليكم مبشِّراً ولا منذراً، ها هو ذا قد أتاكم بشيرٌ ونذيرٌ، والله هو القادر على كل أمر - ومنه: إنزال الرسالات - ومحاسبكم على ما كان منكم.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b836] VALUES (693,'<span data-type="title" id=toc-698>٢٠ </span>- واذكر - أيها الرسول - حينما قال موسى لقومه: يا قوم اذكروا بالشكر والطاعة نعم الله عليكم، حيث اختار منكم أنبياء كثيرين، وجعلكم أعزة كالملوك، بعد أن كنتم أذلاّء فى مملكة فرعون، ومنحكم من النعم الأخرى ما لم يؤت أحداً غيركم من العالمين.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b836] VALUES (694,'<span data-type="title" id=toc-699>٢١ </span>- يا قوم أطيعوا أمر الله، فادخلوا الأرض المقدسة التى قدَّر الله عليكم دخولها، ولا تتراجعوا أمام أهلها الجبارين، فتعودوا خاسرين نصر الله ورضوانه.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b836] VALUES (695,'<span data-type="title" id=toc-700>٢٢ </span>- قال بنو إسرائيل مخالفين أمر الله: يا موسى، إن فى هذه الأرض جبابرة لا طاقة لنا بهم، فلن ندخلها ما داموا فيها، فإذا ما خرجوا منها دخلناها.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b836] VALUES (696,'<span data-type="title" id=toc-701>٢٣ </span>- قال رجلان من نقبائهم الذين يخشون الله، وأنعم الله عليهما بالإيمان والطاعة: ادخلوا - أيها القوم - على الجبارين باب المدينة مفاجئين، فإذا فعلتم ذلك فإنكم منتصرون عليهم، وتوكلوا على الله - وحده - فى كل أموركم إن كنتم صادقى الإيمان.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b836] VALUES (697,'<span data-type="title" id=toc-702>٢٤ </span>- فأصروا على المخالفة، وقالوا: يا موسى، إنا معتزمون ألا ندخل هذه الأرض أبداً، ما دام فيها الجبارون، فدعنا نحن، فليس لك علينا من سلطان، واذهب أنت وربك فقاتلا الجبارين، فإنا فى هذا المكان مقيمون.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b836] VALUES (698,'<span data-type="title" id=toc-703>٢٥ </span>- حين ذلك فزع موسى إلى ربه قائلاً: ربِّ لا سلطان لى إلا على نفسى وأخى، فاقض بعدلك بيننا وبين هؤلاء المعاندين.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b836] VALUES (699,'<span data-type="title" id=toc-704>٢٦ </span>- فاستجاب الله لموسى، وحرَّم على أولئك المخالفين دخول هذه الأرض طيلة أربعين عاماً، يضلُّون فى الصحراء لا يهتدون إلى جهة. قال الله لموسى يواسيه: لا تحزن على ما أصابهم، فإنهم فاسقون خارجون عن أمر الله.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b836] VALUES (700,'<span data-type="title" id=toc-705>٢٧ </span>- وإن حب الاعتداء فى طبيعة بعض الناس، فاقرأ - أيها النبى - على اليهود - وأنت صادق - خبر هابيل وقابيل ابنى آدم، حين تقرَّب كل منهما إلى الله بشئ، فتقبل الله قربان أحدهما لإخلاصه، ولم يتقبل من الآخر لعدم إخلاصه، فحسد أخاه وتوعده بالقتل حقداً عليه، فرد عليه أخوه مبيناً له أن الله لا يتقبل العمل إلا من الاتقياء المخلصين فى تقربهم.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b836] VALUES (701,'<span data-type="title" id=toc-706>٢٨ </span>- وقال له: لئن أغواك الشيطان فمددت يدك نحوى لتقتلنى، فلن أعاملك بالمثل، ولن أمد يدى إليك لأقتلك، لأنى أخاف عذاب ربى، وهو الله رب العالمين.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b836] VALUES (702,'<span data-type="title" id=toc-707>٢٩ </span>- إنى لن أقاومك حين تقتلنى، لتحمل ذنب قتلك لى، مع ذنبك فى عدم إخلاصك لله من قبل، وبذلك تستحق أن تكون فى الآخرة من أهل النار، وذلك جزاء عادل من الله لكل ظالم.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b836] VALUES (703,'<span data-type="title" id=toc-708>٣٠ </span>- فسهَّلت له نفسه أن يخالف الفطرة، وأن يقتل أخاه، وقتله، فصار فى حكم الله من الخاسرين، إذ خسر إيمانه وخسر أخاه.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b836] VALUES (704,'<span data-type="title" id=toc-709>٣١ </span>- بعد قتله أصابته حسرة وحيرة، ولم يدر ما يصنع بجثته، فأرسل الله غراباً ينبش تراب الأرض ليدفن غراباً ميتاً، حتى يُعَلِّم ذلك القاتل كيف يستر جثة أخيه، فقال القاتل مُحِسَّا بوبال ما ارتكب، متحسراً على جريمته: أعجزت عن أن أكون مثل هذا الغراب فأستر جثة أخى؟! فصار من النادمين على جرمه ومخالفته دواعى الفطرة.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b836] VALUES (705,'<span data-type="title" id=toc-710>٣٢ </span>- بسبب ذلك الطغيان وحب الاعتداء فى بعض النفوس أوجبنا قتل المعتدى، لأنه من قتل نفساً بغير ما يوجب القصاص، أو بغير فساد منها فى الأرض، فكأنه قتل الناس جميعاً، لأنه هتك حرمة دمائهم، وجرّأ عليها غيره، وقتل النفس الواحدة كقتل الجميع فى استجلاب غضب الله وعذابه، ومن أحياها بالقصاص لها، فكأنما أحيا الناس كلهم، لصيانته دماء البشر، فيستحق عليهم عظيم الثواب من ربه. ولقد أرسلنا إليهم رسلنا مؤكدين حكمنا لهم بالأدلة والبراهين، ثم إن كثيراً من بنى إسرائيل بعد ذلك البيان المؤكد أسرفوا فى إفسادهم فى الأرض.',NULL,'151',NULL,NULL,'0');
INSERT INTO [b836] VALUES (706,'<span data-type="title" id=toc-711>٣٣ </span>- إنما عقاب الذين يحاربون الله ورسوله، بخروجهم على نظام الحكم وأحكام الشرع، ويفسدون فى الأرض بقطع الطريق أو انتهاب الأموال: أن يُقْتَلوا بمن قتلوا، وأن يُصلبوا إذا قتلوا وغصبوا المال، وأن تُقطَّع أيديهم وأرجلهم من خلاف إذا قطعوا الطريق وغصبوا المال ولم يقتلوا، وأن يُنفوا من بلد إلى بلد، وأن يُحبسوا إذا أخافوا فقط. ذلك العقاب ذل لهم وإهانة فى الدنيا، ولهم فى الآخرة عذاب عظيم وهو عذاب النار.',NULL,'151',NULL,NULL,'0');
INSERT INTO [b836] VALUES (707,'<span data-type="title" id=toc-712>٣٤ </span>- إلا الذين تابوا من هؤلاء المحاربين للنظام وقطاع الطريق من قبل أن تقدروا عليهم وتتمكنوا منهم، فإن عقوبة الله المذكورة تسقط عنهم وتبقى عليهم حقوق العباد، واعلموا أن الله واسع المغفرة والرحمة.',NULL,'151',NULL,NULL,'0');
INSERT INTO [b836] VALUES (708,'<span data-type="title" id=toc-713>٣٥ </span>- يا أيها الذين آمنوا، خافوا الله باجتناب نواهيه وإطاعة أوامره، واطلبوا ما يقرّبكم إلى ثوابه، من فعل الطاعات والخيرات، وجاهدوا فى سبيله بإعلاء دينه ومحاربة أعدائه، لعلكم تفوزون بكرامته وثوابه.',NULL,'151',NULL,NULL,'0');
INSERT INTO [b836] VALUES (709,'<span data-type="title" id=toc-714>٣٦ </span>- إن الذين كفروا لو كان عندهم ما فى الأرض جميعاً من صنوف الأموال وغيرها من مظاهر الحياة، وكان لهم مثل ما فى الأرض فوق ما فيها، وأرادوا أن يجعلوه فدية لأنفسهم من عذاب الله. يوم القيامة على كفرهم ما نفعهم الافتداء بهذا كله، ولا قبل الله منهم ذلك، فلا سبيل إلى خلاصهم من العقاب، ولهم عذاب مؤلم شديد.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b836] VALUES (710,'<span data-type="title" id=toc-715>٣٧ </span>- يتمنى هؤلاء الكافرون أن يخرجوا من النار، وهم لن يخرجوا منها، ولهم عذاب دائم مستمر.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b836] VALUES (711,'<span data-type="title" id=toc-716>٣٨ </span>- والذى يسرق، والتى تسرق، اقطعوا أيديهما جزاء بما ارتكبا، عقوبة لهما، وزجراً وردعاً لغيرهما. وذلك الحكم لهما من الله، والله غالب على أمره، حكيم فى تشريعه، يضع لكل جريمة ما تستحق من عقاب رادع مانع من شيوعها.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b836] VALUES (712,'<span data-type="title" id=toc-717>٣٩ </span>- فمن تاب من بعد اعتدائه وأصلح عمله واستقام، فإن الله يتقبل توبته، إن الله واسع المغفرة والرحمة.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b836] VALUES (713,'<span data-type="title" id=toc-718>٤٠ </span>- اعلم - أيها المكلف - علماً يقينياً أن الله - وحده - له كل ما فى السموات والأرض، يعذب من يشاء تعذيبه بحكمته وقدرته، ويغفر لمن يشاء أن يغفر له بحكمته ورحمته، والله على كل شئ قدير.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b836] VALUES (714,'<span data-type="title" id=toc-719>٤١ </span>- يا أيها الرسول لا يحزنك صنع الكافرين الذين ينتقلون فى مراتب الكفر من أدناها إلى أعلاها، مسارعين فيها، من هؤلاء المخادعين الذين قالوا: آمنا بألسنتهم ولم تذعن للحق قلوبهم، ومن اليهود الذين يكثرون الاستماع إلى مفتريات أحبارهم ويستجيبون لها، ويكثرون الاستماع والاستجابة لطائفة منهم ولم يحضروا مجلسك تكبراً وبغضاً، وهؤلاء يبدلون ويحرفون ما جاء فى التوراة من بعد أن أقامه الله وأحكمه فى مواضعه، ويقولون لأتباعهم: إن أوتيتم هذا الكلام المحرّف المبدّل فاقبلوه وأطيعوه، وإن لم يأتكم فاحذروا أن تقبلوا غيره، فلا تحزن، فمن يرد الله ضلاله لانغلاق قلبه فلن تستطيع أن تهديه أو أن تنفعه بشئ لم يرده الله له، وأولئك هم الذين أسرفوا فى الضلال والعناد لم يرد الله أن يطهر قلوبهم من دنس الحقد والعناد والكفر، ولهم فى الدنيا ذل بالفضيحة والهزيمة، ولهم فى الآخرة عذاب شديد عظيم.',NULL,'153',NULL,NULL,'0');
INSERT INTO [b836] VALUES (715,'<span data-type="title" id=toc-720>٤٢ </span>- هم كثيرو الاستماع للافتراء، كثيرو الأكل للمال الحرام الذى لا بركة فيه، كالرشوة والربا وغيرهما، فإن جاءوك لتحكم بينهم فاحكم بينهم إذا رأيت المصلحة فى ذلك، أو أعرض عنهم، وإن تعرض عنهم فلن يضروك بأى قدر من الضرر، لأن الله عاصمك من الناس، وإن حكمت بينهم فاحكم بالعدل الذى أمر الله به، إن الله يحب العادلين فيحفظهم ويثيبهم.',NULL,'153',NULL,NULL,'0');
INSERT INTO [b836] VALUES (716,'<span data-type="title" id=toc-721>٤٣ </span>- عجباً لهم {كيف يطلبون حكمك، مع أن حكم الله منصوص عليه عندهم فى التوراة؟} والعجب من أمرهم أنهم يعرضون عن حكمك إذا لم يوافق هواهم، مع أنه الموافق لما فى كتابهم، وهؤلاء ليسوا من المؤمنين الذين يذعنون للحق.',NULL,'153',NULL,NULL,'0');
INSERT INTO [b836] VALUES (717,'<span data-type="title" id=toc-722>٤٤ </span>- إنا أنزلنا التوراة على موسى فيها هداية إلى الحق، وبيان منير للأحكام التى يحكم بها النبيون، والذين أخلصوا نفوسهم لربهم، والعلماء السالكون طريقة الأنبياء والذين عهد إليهم أن يحفظوا كتابهم من التبديل، حرساً عليه، شاهدين بأنه الحق. فلا تخافوا الناس فى أحكامكم، وخافونى أنا ربكم رب العالمين، ولا تستبدلوا بآياتى التى أنزلتها ثمناً قليلاً من متاع الدنيا، كالرشوة والجاه، ومن لم يحكم بما أنزل الله من شرائع مستهينين بها، فهم من الكافرين.',NULL,'154',NULL,NULL,'0');
INSERT INTO [b836] VALUES (718,'<span data-type="title" id=toc-723>٤٥ </span>- وفرضنا على اليهود فى التوراة شرعة القصاص، لنحفظ بها حياة الناس فحكمنا بأن تؤخذ النفس بالنفس، والعين بالعين، والأنف بالأنف، والأذن بالأذن، والسن بالسن، والجروح يقتص فيها إذا أمكن. فمن عفا وتصدق بحقه فى القصاص على الجانى، كان هذا التصدق كفارة له، يمحو الله بها قدراً من ذنوبه. ومن لم يحكم بما أنزل الله من القصاص وغيره، فأولئك هم الظالمون.',NULL,'154',NULL,NULL,'0');
INSERT INTO [b836] VALUES (719,'<span data-type="title" id=toc-724>٤٦ </span>- وأرسلنا من بعد هؤلاء النبيين عيسى ابن مريم، متبعاً طريقهم، مصدِّقاً لما سبقه من التوراة، وأنزلنا عليه الإنجيل فيه هداية إلى الحق، وبيان للأحكام، وأنزلناه مصدقاً لما سبقه وهى التوراة، وفيها هداية إلى الحق وموعظة للمتقين.',NULL,'154',NULL,NULL,'0');
INSERT INTO [b836] VALUES (720,'<span data-type="title" id=toc-725>٤٧ </span>- وأمرنا أتباع عيسى وأصحاب الإنجيل بأن يحكموا بما أنزل الله فيه من أحكام، ومن لم يحكم بما أنزل الله فأولئك هم الخارجون المتمردون على شريعة الله.',NULL,'154',NULL,NULL,'0');
INSERT INTO [b836] VALUES (721,'<span data-type="title" id=toc-726>٤٨ </span>- وأنزلنا إليك - أيها النبى - الكتاب الكامل، وهو القرآن، ملازماً الحقّ فى كل أحكامه وأنبائه، موافقاً ومصدِّقاً لما سبقه من كتبنا، وشاهداً عليها بالصحة، ورقيباً عليها بسبب حفظه من التغيير. فاحكم بين أهل الكتاب إذا تحاكموا إليك بما أنزل الله عليك، ولا تتبع فى حكمك شهواتهم ورغباتهم، فتنحرف عما جاءك منا من حق. لكل أمة منكم - أيها الناس - جعلنا منهاجاً لبيان الحق، وطريقاً واضحاً فى الدين يمشى عليه، ولو شاء الله لجعلكم جماعة متفقة ذات مشارب واحدة، لا تختلف مناهج إرشادها فى جميع العصور، ولكنه جعلكم هكذا ليختبركم فيما آتاكم من الشرائع، ليتبين المطيع والعاصى. فانتهزوا الفرص، وسارعوا إلى عمل الخيرات، فإن رجوعكم جميعاً سيكون إلى الله - وحده - فيخبركم بحقيقة ما كنتم تختلفون فيه، ويجازى كلا منكم بعمله.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b836] VALUES (722,'<span data-type="title" id=toc-727>٤٩ </span>- وأمرناك - أيها الرسول - بأن تحكم بينهم بما أنزل الله، ولا تتبع رغباتهم فى الحكم، واحذرهم أن يصرفوك عن بعض ما أنزله الله إليك. فإن أعرضوا عن حكم الله وأرادوا غيره، فاعلم أن الله إنما يريد أن يصيبهم بفساد أمورهم، لفساد نفوسهم، بسبب ذنوبهم التى ارتكبوها من مخالفة أحكامه وشريعته، ثم يجازيهم عن كل أعمالهم فى الآخرة، وإن كثيراً من الناس لمتمردون على أحكام الشريعة.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b836] VALUES (723,'<span data-type="title" id=toc-728>٥٠ </span>- أيريد أولئك الخارجون عن أمر الله ونهيه أن يحكموا بأحكام الجاهلية التى لا عدل فيها، بل الهوى هو الذى يحكم، بأن يجعلوا أساس الحكم الميل والمداهنة؟ وهذه هى طريقة أهل الجاهلية - وهل يوجد أحسن من الله حكماً لقوم يوقنون بالشرع ويذعنون للحق؟ إنهم هم الذين يدركون حسن أحكام الله.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b836] VALUES (724,'<span data-type="title" id=toc-729>٥١ </span>- يا أيها الذين آمنوا لا يحل لكم أن تتخذوا اليهود ولا النصارى نصراء توالونهم، فهم سواء فى معاداتكم. ومن جعل لهم الولاية عليه فإنه من جملتهم، وإن الله لا يهدى الذين يظلمون أنفسهم بجعل ولايتهم للكافرين.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b836] VALUES (725,'<span data-type="title" id=toc-730>٥٢ </span>- وإذا كانت ولايتهم لا يتبعها إلا الظالمون، فإنك ترى الذين يوالونهم فى قلوبهم مرض الضعف والنفاق، إذ يقولون: نخاف أن تصيبنا كارثة عامة فلا يساعدونا، فعسى الله أن يحقق الفتح لرسوله والنصر للمسلمين على أعدائهم، أو يظهر نفاق أولئك المنافقين، فيصبحوا نادمين آسفين على ما كتموه فى نفوسهم من كفر وشك.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b836] VALUES (726,'<span data-type="title" id=toc-731>٥٣ </span>- وحينئذ يقول المؤمنون الصادقون - متعجبين من المنافقين -: أهؤلاء الذين أقسموا وبالغوا فى القسم بالله على أنهم معكم فى الدين، مؤمنون مثلكم؟ كذبوا وبطلت أعمالهم، فصاروا خاسرين للإيمان، ونصرة المؤمنين.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b836] VALUES (727,'<span data-type="title" id=toc-732>٥٤ </span>- يا أيها الذين آمنوا: من يرجع منكم عن الإيمان إلى الكفر - فلن يضروا الله بأى قدر من الضرر! تعالى الله عن ذلك - فسوف يأتى الله بدلهم بقوم خير منهم، يحبهم الله فيوفقهم للهدى والطاعة، ويحبون الله فيطيعونه، وفيهم تواضع ورحمة بإخوانهم المؤمنين، وفيهم شدة على أعدائهم الكافرين، يجاهدون فى سبيل الله ولا يخشون فى الله لومة أى لائم. ذلك فضل الله يمنحه لمن يشاء ممن يوفقهم للخير، والله كثير الفضل عليم بمن يستحقونه.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b836] VALUES (728,'<span data-type="title" id=toc-733>٥٥ </span>- إنما ولايتكم - أيها المؤمنون - لله ورسوله وأنفسكم، ممن يقيمون الصلاة ويؤتون الزكاة، وهم خاضعون لله.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b836] VALUES (729,'<span data-type="title" id=toc-734>٥٦ </span>- ومن يتخذ الله ورسوله والمؤمنين أولياءه ونصراءه، فإنه يكون من حزب الله، وحزب الله هم المنتصرون الفائزون.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b836] VALUES (730,'<span data-type="title" id=toc-735>٥٧ </span>- يا أيها الذين آمنوا لا تتخذوا أعداء الإسلام الذين اتخذوا دينكم سخرية ولهوا - وهم اليهود والنصارى والمشركون - نصراء، ولا تجعلوا ولايتكم لهم، وخافوا الله إن كنتم صادقين فى إيمانكم.',NULL,'157',NULL,NULL,'0');
INSERT INTO [b836] VALUES (731,'<span data-type="title" id=toc-736>٥٨ </span>- ومن استهزائهم بكم: أنكم إذا دعوتم إلى الصلاة بالأذان استهزأوا بالصلاة، وتضاحكوا عليها ولعبوا فيها، وذلك بسبب أنهم قوم لا يعقلون، ولا يدركون الفرق بين الضلال والهدى.',NULL,'157',NULL,NULL,'0');
INSERT INTO [b836] VALUES (732,'<span data-type="title" id=toc-737>٥٩ </span>- قل - أيها الرسول - لهؤلاء المستهزئين من أهل الكتاب، هل تنقمون علينا إلا إيماننا بالله وبما أنزل إلينا - وهو القرآن - وبما أنزل من قبل على الأنبياء من الكتب الصحيحة: وإيماننا بأن أكثركم خارجون على شريعة الله؟! .',NULL,'157',NULL,NULL,'0');
INSERT INTO [b836] VALUES (733,'<span data-type="title" id=toc-738>٦٠ </span>- قل لهم: ألا أخبركم بأعظم شر فى الجزاء عند الله؟ إنه عملكم أنتم يا من أبعدهم الله من رحمته، وسخط عليهم بسبب كفرهم وعصيانهم، وطمس على قلوبهم، فكانوا كالقردة والخنازير، وعبدوا الشيطان، واتبعوا الضلال. أولئك فى أكبر منزلة من الشر، لأنهم أبعد الناس عن طريق الحق.',NULL,'157',NULL,NULL,'0');
INSERT INTO [b836] VALUES (734,'<span data-type="title" id=toc-739>٦١ </span>- وإذا جاءكم المنافقون كذبوا عليكم بقولهم: آمنا، وهم قد دخلوا إليكم كافرين كما خرجوا من عندكم كافرين، والله أعلم بما يكتمون من النفاق ومعاقبهم عليه.',NULL,'157',NULL,NULL,'0');
INSERT INTO [b836] VALUES (735,'<span data-type="title" id=toc-740>٦٢ </span>- وترى كثيراً من هؤلاء يسارعون فى المعاصى والاعتداء على غيرهم، وفى أكل المال الحرام كالرشوة والربا ولبئس ما يفعلونه من هذه القبائح.',NULL,'157',NULL,NULL,'0');
INSERT INTO [b836] VALUES (736,'<span data-type="title" id=toc-741>٦٣ </span>- أما كان ينبغى أن ينهاهم علماؤهم وأحبارهم عن قول الكذب وأكل الحرام، ولبئس ما كانوا يصنعون من ترك النصيحة والنهى عن المعصية.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b836] VALUES (737,'<span data-type="title" id=toc-742>٦٤ </span>- وقالت اليهود: يد الله مقبوضة لا تنبسط بالعطاء. قبض الله أيديهم وأبعدهم من رحمته، فالله غنى كريم ينفق كما يشاء. وإن كثيراً من هؤلاء - لإمعانهم فى الضلال - ليزيدهم ما أنزل إليك من الله ظلماً وكفراً لما فيهم من حقد وحسد، وأثرنا بينهم العداوة والبغضاء إلى يوم القيامة، وكلما أشعلوا ناراً لحرب الرسول والمؤمنين أطفأها الله بهزيمتهم وانتصار نبيه وأتباعه، وأنهم يجتهدون فى نشر الفساد فى الأرض بالكيد والفتن وإثارة الحروب، والله لا يحب المفسدين.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b836] VALUES (738,'<span data-type="title" id=toc-743>٦٥ </span>- ولو أن أهل الكتاب من اليهود والنصارى آمنوا بالإسلام ونبيه، واجتنبوا الآثام التى ذكرناها، لمحونا عنهم سيئاتهم، وأدخلناهم فى جنات النعيم يتمتعون بها.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b836] VALUES (739,'<span data-type="title" id=toc-744>٦٦ </span>- ولو أنهم حفظوا التوراة والإنجيل كما نزلا، وعملوا بما فيهما. وآمنوا بما أنزل إليهم من ربهم، وهو القرآن، لوسَّع الله عليهم الرزق يأتيهم من كل جهة يلتمسونه منها. وهم ليسوا سواء فى الضلال، ومن هؤلاء جماعة عادلة عاقلة، وهم الذين آمنوا بمحمد وبالقرآن، وكثير منهم لبئس ما يعملونه ويقولونه معرضين عن الحق.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b836] VALUES (740,'<span data-type="title" id=toc-745>٦٧ </span>- يا أيها المرسل من الله، أخبر الناس بكل ما أوحى إليك من ربك. وادعهم إليه، ولا تخش الأذى من أحد، وإن لم تفعل فما بلغت رسالة الله، لأنك قد كُلِّفْت تبليغ الجميع، والله يحفظك من أذى الكفار إذ جرت سننه ألا ينصر الباطل على الحق، إن الله لا يهدى الكافرين إلى الطريق السوى.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b836] VALUES (741,'<span data-type="title" id=toc-746>٦٨ </span>- يا أيها الرسول، قل لأهل الكتاب: إنكم لا تكونون على أى دين صحيح، إلا إذا أعلنتم جميع الأحكام التى أنزلت فى التوراة والإنجيل وعملتم بها، وآمنتم بالقرآن الموحى به من الله إلى رسوله لهداية الناس، ولتتيقن - أيها الرسول - أن معظم أهل الكتاب سيزدادون بالقرآن - الموحى به إليك - ظلماً وكفراً وعناداً، لحسدهم وحقدهم، فلا تحزن على الذين طبعوا على الجحود.',NULL,'159',NULL,NULL,'0');
INSERT INTO [b836] VALUES (742,'<span data-type="title" id=toc-747>٦٩ </span>- إن المصدِّقين بالله، وأتباع موسى من اليهود، والخارجين عن الأديان، وأتباع عيسى من النصارى، كل أولئك إذا أخلصوا فى الإيمان بالله، وصدقوا بالبعث والجزاء، وأتوا بالأعمال الصالحة التى جاء بها الإسلام، فهم فى مأمن من العذاب وفى سرور بالنعيم يوم القيامة.',NULL,'159',NULL,NULL,'0');
INSERT INTO [b836] VALUES (743,'<span data-type="title" id=toc-748>٧٠ </span>- إننا عاهدنا اليهود - من بنى إسرائيل - عهداً مؤكداً فى التوراة على اتباع أحكامها، وبعثنا إليهم أنبياء كثيرين ليبينوها لهم، ويؤكدوا عهدنا، ولكنهم نقضوا العهد، فكانوا كلما أتاهم رسول بما يخالف أهواءهم، كذبوا البعض وقتلوا البعض.',NULL,'159',NULL,NULL,'0');
INSERT INTO [b836] VALUES (744,'<span data-type="title" id=toc-749>٧١ </span>- وظن بنو إسرائيل أنه لا تنزل بهم شدائد تبين الثابتين من غير الثابتين، ولذلك لم يصبروا فى الشدائد، ضل كثيرون منهم، وصاروا كالعميان الصم، وأعرضوا عن الحق، فسلط الله عليهم من أذاقهم الذل. وبعد حين رجعوا إلى الله تائبين، فتقبل توبتهم، وأعاد إليهم عزمهم، ولكنهم من بعد ذلك ضلوا مرة أخرى، وصاروا كالعمى الصم، والله مطلع عليهم، مشاهد لأعمالهم، ومجازيهم عليها.',NULL,'159',NULL,NULL,'0');
INSERT INTO [b836] VALUES (745,'<span data-type="title" id=toc-750>٧٢ </span>- وأنه لم يؤمن بالله من يزعم أن الله حل فى عيسى ابن مريم حتى صار إلهاً، كما يقول النصارى الآن: مع أن عيسى براء من هذه الدعوى، فإنه أمر بنى إسرائيل أن يخلصوا الإيمان لله - وحده - قائلا لهم: إن الله هو خالقى وخالقكم، ومالك أمرنا جميعاً، وإن كل من يدّعى لله شريكاً فإن جزاءه أن لا يدخل الجنة أبداً، وأن تكون النار مصيره، لأنه تعدى حدود الله، وليس لمن يتعدى حدوده ويظلم ناصر يدفع عنه العذاب.',NULL,'159',NULL,NULL,'0');
INSERT INTO [b836] VALUES (746,'<span data-type="title" id=toc-751>٧٣ </span>- وإنه لم يؤمن بالله كذلك كل من ادعى أن الله أحد آلهة ثلاثة، كما يزعم النصارى الآن! {والحق الثابت أنه ليس هناك إله إلا الله وحده، وإذا لم يرجع هؤلاء الضالون عن معتقداتهم الفاسدة إلى طاعة الله، فلا بد أن يصيبهم عذاب شديد.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b836] VALUES (747,'<span data-type="title" id=toc-752>٧٤ </span>- ألا ينتهى هؤلاء عن تلك العقائد الزائفة، ويرجعوا إلى الإيمان بالله، ويطلبوا منه التجاوز عما وقع منهم من الذنوب؟ إن الله واسع المغفرة، عظيم الرحمة.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b836] VALUES (748,'<span data-type="title" id=toc-753>٧٥ </span>- ليس عيسى ابن مريم إلا عبداً من البشر، أنعم الله عليه بالرسالة، كما أنعم على كثير ممن سبقه. وأم عيسى إحدى النساء، طبعت على الصدق فى قولها والتصديق بربها، وكانت هى وابنها عيسى فى حاجة إلى ما يحفظ حياتهما من الطعام والشراب، وذلك علامة البشرية. فتأمل - أيها السامع - حال هؤلاء الذين عموا عن دلالة الآيات الواضحة التى بينها الله لهم، ثم تأمل كيف ينصرفون عن الحق مع وضوحه؟} .',NULL,'160',NULL,NULL,'0');
INSERT INTO [b836] VALUES (749,'<span data-type="title" id=toc-754>٧٦ </span>- قل - أيها الرسول - لهؤلاء الضالين: كيف تعبدون إلها يعجز عن أن يضركم بشئ إن تركتم عبادته، ويعجز عن أن ينفعكم بشئ إن عبدتموه؟ كيف تتركون عبادة الله وهو الإله القادر على كل شئ، وهو ذو السمع والعلم الشامل؟',NULL,'160',NULL,NULL,'0');
INSERT INTO [b836] VALUES (750,'<span data-type="title" id=toc-755>٧٧ </span>- قل - يا أيها الرسول - لأهل الكتاب من اليهود والنصارى: إن الله ينهاكم أن تتجاوزوا فى معتقداتكم حدود الحق إلى الباطل فتجعلوا بعض خلقه آلهة، أو تنكروا رسالة بعض الرسل، وينهاكم أن تسيروا وراء شهوات أناس سبقوكم قد تجنبوا طريق الهدى، ومنعوا كثيراً من الناس أن يسلكوها واستمروا على مجافاتهم طريق الحق الواضح.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b836] VALUES (751,'<span data-type="title" id=toc-756>٧٨ </span>- طرد الله كفار إسرائيل من رحمته، وأنزل هذا فى الزبور على نبيه داود، وفى الإنجيل على نبيه عيسى ابن مريم، وذلك بسبب تمردهم عن طاعة الله، وتماديهم فى الظلم والفساد.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b836] VALUES (752,'<span data-type="title" id=toc-757>٧٩ </span>- كان دأبهم ألا يتناصحوا، فلا ينهى أحد منهم غيره عن قبيح يفعله، وأن إتيانهم المنكر وعدم تناهيهم عنه لمن أقبح ما كانوا يفعلون.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b836] VALUES (753,'<span data-type="title" id=toc-758>٨٠ </span>- ترى كثيراً من بنى إسرائيل يتحالفون مع المشركين، ويتخذونهم أنصاراً يتعاونون فيما بينهم على حرب الإسلام. إن هذا الشر عمل ادخرته لهم أنفسهم، ليجدوا جزاءه غضباً من الله، وخلوداً فى عذاب جهنم.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b836] VALUES (754,'<span data-type="title" id=toc-759>٨١ </span>- ولو صحت عقيدة هؤلاء فى الإيمان بالله ورسوله محمد، وما أنزل إليه من القرآن لمنعهم ذلك الإيمان عن موالاتهم للكفار ضد المؤمنين، ولكن كثيراً من بنى إسرائيل عاصون خارجون عن الأديان.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b836] VALUES (755,'<span data-type="title" id=toc-760>٨٢ </span>- نؤكد لك - أيها النبى - أنك تجد أشد الناس حقداً وكراهية لك، ولمن آمن بك هم اليهود والذين أشركوا مع الله غيره فى العبادة، وتجد أن أقرب الناس مودة ومحبة لك هم أتباع عيسى الذين سموا أنفسهم نصارى، لأن فيهم قسيسين يعلمون دينهم، ورهباناً يخشون ربهم، ولأنهم لا يستكبرون عن سماع الحق.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b836] VALUES (756,'<span data-type="title" id=toc-761>٨٣ </span>- ولأنهم إذا سمعوا القرآن الذى أنزل على الرسول يتأثرون به، فتفيض عيونهم بالدمع، لمعرفتهم أن الذى سمعوه حق، فتميل إليه قلوبهم، وتنطلق ألسنتهم بالدعاء لله قائلين: ربنا آمنا بك وبرسلك، وبالحق الذى أنزلته عليهم، فتقبل إيماننا، واجعلنا من أمة محمد الذين جعلتهم شهداء وحُجة على الناس يوم القيامة.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b836] VALUES (757,'<span data-type="title" id=toc-762>٨٤ </span>- وأى مانع يمنعنا من أن نصدق بالله - وحده - وبما جاءنا من الحق المنزل على محمد؟ ونحن نرجو أن يدخلنا ربنا الجنة مع القوم الذين صلحت عقائدهم وأعمالهم.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b836] VALUES (758,'<span data-type="title" id=toc-763>٨٥ </span>- فكتب الله لهم ثواباً لاعترافهم، هو جنات تجرى الأنهار تحت أشجارها وقصورها، وهم ماكثون فيها دائماً. وذلك الجزاء الذى نالوه هو جزاء كل محسن مثلهم.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b836] VALUES (759,'<span data-type="title" id=toc-764>٨٦ </span>- والذين جحدوا بالله ورسله. وأنكروا أدلته التى أنزلها عليهم هداية للحق هم - وحدهم - الملازمون للعذاب الشديد فى جهنم.',NULL,'162',NULL,NULL,'0');
INSERT INTO [b836] VALUES (760,'<span data-type="title" id=toc-765>٨٧ </span>- يا أيها الذين آمنوا لا تحرِّموا على أنفسكم ما أحلَّ الله لكم من الطيبات، ولا تتجاوزوا الحدود التى شرعها الله لكم من التوسط فى أموركم، إن الله لا يحب المتجاوزين للحدود.',NULL,'162',NULL,NULL,'0');
INSERT INTO [b836] VALUES (761,'<span data-type="title" id=toc-766>٨٨ </span>- وكلوا مما أعطاكم الله ويسَّره لكم، وجعله حلالا لكم تطيب به نفوسكم، واخْشَوْا الله دائماً وأطيعوه ما دمتم مؤمنين به.',NULL,'162',NULL,NULL,'0');
INSERT INTO [b836] VALUES (762,'<span data-type="title" id=toc-767>٨٩ </span>- لا يعاقبكم الله بسبب ما لم تقصدوه من أيْمانكم، وإنما يعاقبكم بسبب الحنث فيما قصدتموه ووثَّقتموه من الأَيْمان، فإن حنثتم فيما حلفتم عليه فعليكم أن تفعلوا ما يغفر ذنوبكم بنقض اليمين، بأن تطعموا عشرة فقراء يوماً، مما جرت العادة بأن تأكلوه أنتم وأقاربكم الذين هم فى رعايتكم، من غير سَرَفٍ ولا تقتير. أو بأن تكسوا عشرة من الفقراء كسوة معتادة، أو بأن تحرروا إنساناً من الرق. فإذا لم يتمكن الحالف من أحد هذه الأمور فعليه أن يصوم ثلاثة أيام. وكل واحد من هذه الأمور يغفر به ذنب الحلف الموثق بالنية إذا نقضه الحالف. وصونوا أيْمانَكم فلا تضعوها فى غير موضعها، ولا تتركوا فعل ما يغفر ذنبكم إذا نقضتموها. على هذا النسق من البيان يشرح الله لكم أحكامه، لتشكروا نعمه بمعرفتها والقيام بحقها.',NULL,'162',NULL,NULL,'0');
INSERT INTO [b836] VALUES (763,'<span data-type="title" id=toc-768>٩٠ </span>- يا أيها المصَدّقون بالله وكتبه ورسله المذعنون للحق، ليس شرب المسكرات، ولا لعب القمار، ونصب الأحجار للذبح عندها تقرباً إلى الأصنام التى تعبدونها، واتخاذ السهام والحصى والورق للتعرف بها على مغيبات القدر. . ليس كل ذلك إلا خبثاً نفسياً باطلا، هو من تزيين الشيطان لفاعليه. . فاتركوه لكى تفوزوا فى الدنيا بحياة فاضلة، وفى الآخرة بنعيم الجنة.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b836] VALUES (764,'<span data-type="title" id=toc-769>٩١ </span>- إن الشيطان لا يريد بتزيينه لكم شرب الخمر ولعب الميسر إلا أن يوجد بينكم الخلاف والشقاق والكراهية، ليضعف أمركم بذهاب الألفة بينكم، وتفتيت وحدتكم، بسبب ما يزينه لكم من شرب المسكرات ولعب القمار، لكى يصرفكم عن عبادة الله، ويلهيكم عن أداء الصلاة، لتسوء آخرتكم كما ساءت دنياكم. فبعد علمكم هذه المفاسد ابتعدوا عما نهيتكم عنه، لتفوتوا على إبليس غرضه.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b836] VALUES (765,'<span data-type="title" id=toc-770>٩٢ </span>- وامتثلوا أمر الله وأمر رسوله فيما يبلغكم به عن ربه، وابتعدوا عما يعرضكم للعذاب إن خالفتم. لأنكم إن أعرضتم عن الاستجابة لما أمركم به، فتيقنوا أنه معاقبكم. وليس لكم عذر بعد أن بين لكم الرسول عاقبة المخالفين، وأنه ليس على رسولنا إلا إخباركم بأحكامنا، وتوضيحها كاملا.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b836] VALUES (766,'<span data-type="title" id=toc-771>٩٣ </span>- ليس على الذين صدَّقوا بالله ورسوله وأتوا بصالح الأعمال إثم فيما يطعمون من حلال طيب، ولا فيما سبق أن طعموه من المحرمات قبل علمهم بتحريمها، إذا خافوا الله، وابتعدوا عنها بعد علمهم بتحريمها، ثم استمروا على خوفهم من الله، وتصديقهم بما شرعه لهم بعد من أحكام، ثم داوموا على خوفهم من الله فى كل حال وأخلصوا فى أعمالهم وأدُّوها على وجه الكمال، فإن الله يثيب المخلصين فى أعمالهم على قدر إخلاصهم وعملهم.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b836] VALUES (767,'<span data-type="title" id=toc-772>٩٤ </span>- يا أيها الذين آمنوا: إن الله يختبركم فى الحج بتحريم بعض من الحيوان والطيور يسهل عليكم اصطياده بأيديكم ورماحكم، ليظهر الذين يراقبونه منكم فى غيبة من أعين الخلق. فالذين تجاوزوا حدود الله بعد بيانها يقع عليهم عذاب مؤلم شديد.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b836] VALUES (768,'<span data-type="title" id=toc-773>٩٥ </span>- يا أيها الذين آمنوا لا تقتلوا الصيد وقد نويتم الحج والعمرة وتقومون بأعمالهما، ومن قتله منكم قاصداً، فعليه أن يؤدى نظير الصيد الذى قتله، يخرجه من الإبل والبقر والغنم. ويعرف النظير بتقدير رجلين عادلين منكم يحكمان به، ويهديه إلى الفقراء عند الكعبة، أو يدفع بدله إليهم، أو يخرج بقيمة المثل طعاماً للفقراء، لكل فقير ما يكفيه يومه، ليكون ذلك مسقطاً لذنب تعديه على الصيد، أو يصوم أياماً بعدد الفقراء الذين كانوا يستحقون الطعام لو أخرجه. وقد شرع ذلك ليحس المعتدى بنتائج جرمه وسوء عاقبته. عفا الله عما سبق لكم من المخالفة قبل تحريمها، ومن رجع إلى التعدى بعد العلم بتحريمه، فإن الله يعاقبه بما ارتكب، وهو غالب لا يُغْلَب، شديد العقاب لمن يصر على الذنب.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b836] VALUES (769,'<span data-type="title" id=toc-774>٩٦ </span>- أحل الله لكم أن تصيدوا حيوان البحار، وأن تأكلوا منه، وينتفع به المقيمون منكم والمسافرون، وحرَّم عليكم أن تصيدوا حيوان البر غير المستأنس، مما جرت العادة بعدم تربيته فى المنازل والبيوت، مدة قيامكم بأعمال الحج أو العمرة بالحرم، وراقبوا الله وخافوا عقابه، فلا تخالفوه، فإنكم إليه ترجعون يوم القيامة، فيجازيكم على ما تعملون.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b836] VALUES (770,'<span data-type="title" id=toc-775>٩٧ </span>- جعل الله الكعبة، وهى البيت الذى عظَّمه وحرَّم الاعتداء فيه على الإنسان والحيوان غير المستأنس وفيما حوله، جعله قائماً معظماً يأمن الناس فيه، ويتجهون إليه فى صلاتهم، ويحجون إليه ليكونوا فى ضيافة الله، وليعملوا على جمع شملهم، وكذلك جعل شهر الحج وما يهدى إلى الكعبة من الأنعام، وخاصة ما يوضع فى عنقه القلائد لإشعار الناظرين بأنه مهدى إلى البيت. ونتيجة القيام بذلك أن تستيقنوا أن علمه محيط بما فى السموات التى ينزل منها الوحى بالتشريع، ومحيط بما فى الأرض، فيشرع لمن فيها بما يقوم بمصالحهم. وإن علمه بكل شئ محيط.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b836] VALUES (771,'<span data-type="title" id=toc-776>٩٨ </span>- اعلموا - أيها الناس - أن عذاب الله شديد ينزل بمن يستبيح حرماته، وأنه كثير المغفرة لذنوب من يتوب ويحافظ على طاعاته، واسع الرحمة بهم فلا يؤاخذهم حينئذ بما وقع منهم.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b836] VALUES (772,'<span data-type="title" id=toc-777>٩٩ </span>- ليس على الرسول إلا أن يبلغ للناس ما يوحى إليه لتقوم عليهم الحُجة، وينقطع عنهم العذر. فلتعملوا بما بلَّغه إليكم، فإن الله يعلم ما تظهرون وما تخفون.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b836] VALUES (773,'<span data-type="title" id=toc-778>١٠٠ </span>- قل - يا أيها النبى - للناس: لا يتساوى ما أباحه الله لكم من الطيبات، وما حرَّمه عليكم من الخبائث، فإن الفرق بينهما كبير عند الله، ولو كثر الخبيث وأعجب كثيراً من الناس. فاجعلوا - يا أصحاب العقول - طاعة الله وقاية لكم من عذابه باختيار الطيبات واجتناب الخبائث، لتكونوا من الفائزين فى الدنيا والآخرة.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b836] VALUES (774,'<span data-type="title" id=toc-779>١٠١ </span>- يا أيها الذين آمنوا: لا تسألوا النبى عن أمور أخفاها الله عنكم لأنها إن تظهر لكم تسؤكم، وإن تسألوا النبى عنها - فى حياته إذ ينزل عليه القرآن - يبينها الله لكم، عفا الله عنكم فى هذه الأشياء فلا يعاقبكم عليها، والله كثير المغفرة واسع الحلم فلا يعجل بالعقوبة.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b836] VALUES (775,'<span data-type="title" id=toc-780>١٠٢ </span>- قد سأل عن أمثال هذه الأمور الشاقة جماعة ممن سبقوكم، ثم بعد أن كلفوا بها على ألْسِنَةَ أنبيائهم ثقل عليهم تنفيذها، فأعرضوا عنها، وكانوا لها منكرين، لأن الله يريد اليسر ولا يريد العسر، ويكلف الناس ما يطيقون.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b836] VALUES (776,'<span data-type="title" id=toc-781>١٠٣ </span>- لم يأذن الله لكم أن تحرِّموا ما أحلَّه لكم، فتشقوا أذن الناقة، وتمتنعوا عن الانتفاع بها، وتسموها «بَحِيرة» ، وتتركوها بناء على نذر، وتسموها «سائبة» ، وتُحَرِّموا الذكر من الشاة، وتهبوه للأصنام، حتى إذا أنتجت الشاة ذكراً وأنثى سميتموها «وَصِيلة» ، ولم تذبحوا الذكر منها. ولم يشرع لكم أن تحرِّموا الانتفاع بالذكر من الإبل إذا ولد منه عشرة أبطن، وتطلقوا عليه اسم «حَام» ، لم يشرع الله لكم شيئاً من ذلك، ولكن الذين كفروا يختلقون الكذب وينسبونه إلى الله، وأكثرهم لا يعقلون.',NULL,'166',NULL,NULL,'0');
INSERT INTO [b836] VALUES (777,'<span data-type="title" id=toc-782>١٠٤ </span>- وإذا قيل لهؤلاء الكافرين: تعالوا إلى ما أنزل الله من القرآن، وإلى ما بيّنه الرسول لنهتدى به قالوا: يكفينا ما وجدنا عليه آباءنا. أيصح أن يقولوا هذا؟ أو لو كان آباؤهم كالأنعام لا يعلمون شيئاً عن الحق، ولا يعرفون طريقاً إلى الصواب! .',NULL,'166',NULL,NULL,'0');
INSERT INTO [b836] VALUES (778,'<span data-type="title" id=toc-783>١٠٥ </span>- يا أيها الذين آمنوا احرصوا على إصلاح أنفسكم بطاعة الله، إنه لا يضركم ضلال غيركم، إذا كنتم على الهدى ودعوتم إلى الحق، وإلى الله - وحده - مرجعكم جميعاً يوم القيامة، فيخبركم بأعمالكم، ويجزى كلاً منكم بما قدم، فلا يؤاخذ أحداً بذنب غيره.',NULL,'166',NULL,NULL,'0');
INSERT INTO [b836] VALUES (779,'<span data-type="title" id=toc-784>١٠٦ </span>- يا أيها الذين آمنوا: حينما تظهر على أحد منكم علامة الموت ويريد أن يوصى بشئ، فالشهادة بينكم على الوصية، أن يشهد اثنان عادلان من أقاربكم، أو آخران من غيركم إذا كنتم فى سفر، وظهرت أمارات الموت، تحبسون هذين الشاهدين بعد أداء الصلاة التى يجتمع عليها الناس. فيحلفان بالله قائلين: لا نستبدل بيمينه عوضاً، ولو كان فيه نفع لنا أو لأحد من أقاربنا، ولا نخفى الشهادة التى أمرنا الله بأدائها صحيحة. إنا إذا أخفينا الشهادة أو قلنا غير الحق، لنكونن من الظالمين المستحقين لعذاب الله.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b836] VALUES (780,'<span data-type="title" id=toc-785>١٠٧ </span>- فإذا ظهر أن الشاهدين قد كذبا فى شهادتهما. أو أخفيا شيئاً، فإن اثنين من أقرب المستحقين لتركة الميت، هما أحق أن يقفا مكان الشاهدين، بعد الصلاة ليظهرا كذبهما، فيحلفان بالله أن الشاهدين قد كذبا وأن يميننا أولى بالقبول من يمينهما، ولم نتجاوز الحق فى أيماننا، ولم نتهم الشاهدين زوراً، فإننا لو فعلنا ذلك نكون من الظالمين المستحقين عقاب من يظلم غيره.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b836] VALUES (781,'<span data-type="title" id=toc-786>١٠٨ </span>- هذا التشريع أقرب الطرق إلى أن يؤدى الشهداء شهادتهم صحيحة محافظة على حلفهم بالله، أو خوفاً من فضيحتهم بظهور كذبهم، إذا حلف الورثة أيماناً لرد أيمانهم. وراقبوا الله فى أيمانكم وأماناتكم، وأطيعوا أحكامه راضين بها. فإن فيها مصالحكم، ولا تخالفوها فتكونوا من الخارجين على الله، فإن الله لا ينفع بإرشاده من خرج على طاعته.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b836] VALUES (782,'<span data-type="title" id=toc-787>١٠٩ </span>- وتذكروا يوم القيامة حين يجمع الله أمامه كلَّ الرسل ويسألهم قائلا لهم: ماذا أجابتكم به أممكم الذين أرسلتكم إليها، أبالإيمان أم بالإنكار؟ والأمم حينئذ حاضرة لتقوم عليهم الحُجة بشهادة رسلهم، بأننا لا نعلم ما كان بعدنا من أمر من أرسلنا إليهم، وأنت - وحدك - الذى تعلم ذلك، لانك الذى أحاط علمه بالخفايا كما أحاط بالظواهر.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b836] VALUES (783,'<span data-type="title" id=toc-788>١١٠ </span>- وفى ذلك الوقت ينادى الله عيسى ابن مريم من بين الرسل فيقول له: اذكر ما أنعمت به عليك وعلى أمك فى الدنيا، حينما ثَبَّتُك بالوحى وأنطقتك وأنت رضيع بما يُبَرِّئُ أمك مما اتُّهمت به، كما أنطقتك وأنت كبير بما قد أوحيت إليك، وحينما أنعمت عليك بتعليمك الكتاب، ووفقتك للصواب من القول والعمل، وعلمتك كتاب موسى والإنجيل الذى أنزلته عليك، وأقدرتك على معجزات تخرج عن طوق البشر، حيث تتخذ من الطين صورة الطير بإذن الله، فتنفخ فيها فتصبح طائراً حَياً بقدرة الله لا بقدرتك، وتشفى من العمى من وُلِدَ أعمى، وتشفى الأبرص من برصه بإذن الله وقدرته، وحينما يجرى على يديك إحياء الموتى بإذن الله وقدرته، وحينما منعت اليهود من قتلك وصلبك عندما أتيتهم بالمعجزات ليؤمنوا، فأعرض فريق منهم، وادعوا أن ما أظهرته من المعجزات ما هو إلا من قبيل السحر الواضح.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b836] VALUES (784,'<span data-type="title" id=toc-789>١١١ </span>- واذكر - أيها الرسول - لأمتك ما حدث فى الماضى حين ألهمنا جماعة ممن دعوناهم أن يؤمنوا بالله وبرسوله عيسى فاستجابوا له، وصاروا من خاصة أصحابه، وقالوا: آمنا واشهد يا ربنا بأننا مخلصون منقادون لأوامرك.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b836] VALUES (785,'<span data-type="title" id=toc-790>١١٢ </span>- اذكر - أيها النبى - ما حدث حين قال أتباع عيسى المخلصون: يا عيسى ابن مريم، هل يجيبك ربك إذا طلبت منه أن ينزل علينا طعاماً من السماء؟
INSERT INTO [b836] VALUES (786,'<span data-type="title" id=toc-791>١١٣ </span>- قالوا: إننا نريد أن نأكل من هذه المائدة لتطمئن قلوبنا بما نؤمن به من قدرة الله، ونعلم عن معاينة أنك قد صدقتنا فيما أخبرتنا عنه سبحانه، ونشهد لك بهذه المعجزة عند من لم يشاهدها.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b836] VALUES (787,'<span data-type="title" id=toc-792>١١٤ </span>- فاستجاب لهم عيسى وقال: يا ربنا ومالك أمرنا، أنزل علينا مائدة من السماء يكون يوم نزولها عيداً للمؤمنين منا، المتقدمين والمتأخرين، ولتكون معجزة تؤيد بها دعوتك، وارزقنا رزقاً طيباً، وأنت خير الرازقين.',NULL,'169',NULL,NULL,'0');
INSERT INTO [b836] VALUES (788,'<span data-type="title" id=toc-793>١١٥ </span>- قال الله له: إنى سأنزل المائدة عليكم من السماء، فأى امرئ منكم يجحد هذه النعمة بعد إنزالها، فإنى أعاقبه عقاباً لا أعاقب بمثله أحداً من الناس، لأنه كفر بعدما شاهد دليل الإيمان الذى اقترحه.',NULL,'169',NULL,NULL,'0');
INSERT INTO [b836] VALUES (789,'<span data-type="title" id=toc-794>١١٦ </span>- واذكر - أيها النبى - ما سيحدث يوم القيامة، حين يقول الله لعيسى ابن مريم قولا يعلن الحق: أأنت الذى قلت لهم: اجعلونى أنا وأمى إلهين، تاركين إفراد الله بالعبودية؟ قال عيسى: أنزهك تنزيهاً تاماً عن أن يكون لك شريك، ولا يصح لى أن أطلب طلباً ليس لى أدنى حق فيه. لو كنت قلت ذلك لعلمته، لأنك تعلم خفايا نفسى، فضلا عن مظاهر قولى، ولا أعلم ما تخفيه عنى، - إنك وحدك - صاحب العلم المحيط بكل خفى وغائب.',NULL,'169',NULL,NULL,'0');
INSERT INTO [b836] VALUES (790,'<span data-type="title" id=toc-795>١١٧ </span>- ما قلت لهم: إلا ما أمرتنى بتبليغه لهم. قلت لهم: اعبدوا الله - وحده - فإنه مالك أمرى وأمركم. وكنت أعلم حالهم وأنا موجود بينهم، فلما انتهى أجل إقامتى الذى قَدَّرته بينهم، كنت أنت - وحدك - المطلع عليهم، وأنت مطلع على كل شئ.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b836] VALUES (791,'<span data-type="title" id=toc-796>١١٨ </span>- إن تعذبهم بما فعلوا فإنهم عبادك تتصرف فيهم كما تريد، وإن تعف عنهم، فإنك - وحدك - القاهر الذى لا يغلب، ذو الحكمة البالغة فى كل ما يصدر عنه.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b836] VALUES (792,'<span data-type="title" id=toc-797>١١٩ </span>- يقول الله: هذا هو اليوم الذى ينفع فيه الصادقين صدقُهم، لهم حدائق تجرى تحت أشجارها الأنهار، وهم مقيمون فيها لا يخرجون منها أبداً، يتمتعون فيها برضوان الله عنهم ورضاهم بثوابه، وذلك النعيم هو الفوز العظيم.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b836] VALUES (793,'<span data-type="title" id=toc-798>١٢٠ </span>- لله - وحده - ملك السموات والأرض وما فيهن، فهو - وحده - المستحق للعبادة، وهو ذو القدرة التامة على تحقيق كل ما يريد.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b836] VALUES (794,'<span data-type="title" id=toc-800>١ </span>- الثناء والذكر الجميل لله، الذى خلق السموات والأرض، وأوجد الظلمات والنور لمنفعة العباد بقدرته وعلى وفق حكمته، ثم مع هذه النعم الجليلة يشرك به الكافرون، ويجعلون شريكاً فى العبادة.',NULL,'171',NULL,NULL,'0');
INSERT INTO [b836] VALUES (795,'<span data-type="title" id=toc-801>٢ </span>- هو الذى بدأ خلقكم من طين، ثم قدر لحياة كل منكم زمناً ينتهى بموته والأجل عنده - وحده - المحدد للبعث من القبور. ثم إنكم - أيها الكافرون - بعد هذا تجادلون فى قدرة الله على البعث، واستحقاقه - وحده - للعبادة.',NULL,'172',NULL,NULL,'0');
INSERT INTO [b836] VALUES (796,'<span data-type="title" id=toc-802>٣ </span>- وهو - وحده - المستحق للعبادة فى السموات والأرض، يعلم ما أخفيتموه وما أظهرتموه، ويعلم ما تفعلون فيجازيكم عليه.',NULL,'172',NULL,NULL,'0');
INSERT INTO [b836] VALUES (797,'<span data-type="title" id=toc-803>٤ </span>- ولا يؤتى المشركون بدليل من أدلة خالقهم، التى تشهد بوحدانيته وصدق رسله، إلا كانوا منصرفين عنه، لا يتأملون فيه ولا يعتبرون به.',NULL,'172',NULL,NULL,'0');
INSERT INTO [b836] VALUES (798,'<span data-type="title" id=toc-804>٥ </span>- فقد كذبوا بالقرآن حين جاءهم، وهو حق لا يأتيه الباطل. فسوف يحل بهم ما أخبر به القرآن من عقاب الدنيا وعذاب الآخرة، ويتبين لهم صدق وعيده الذى كانوا يسخرون منه.',NULL,'172',NULL,NULL,'0');
INSERT INTO [b836] VALUES (799,'<span data-type="title" id=toc-805>٦ </span>- ألم يعلموا أننا أهلكنا أُمماً كثيرة قبلهم، أعطيناهم من أسباب القوة والبقاء فى الأرض ما لم نعطكم إياه - أيها الكافرون - ووسعنا عليهم فى الرزق والنعيم، فأنزلنا عليهم الأمطار غزيرة ينتفعون بها فى حياتهم، وجعلنا مياه الأنهار تجرى من تحت قصورهم، فلم يشكروا هذه النعم. فأهلكناهم بسبب شركهم وكثرة ذنوبهم، وأوجدنا - من بعد - أناساً غيرهم خيراً منهم.',NULL,'172',NULL,NULL,'0');
INSERT INTO [b836] VALUES (800,'<span data-type="title" id=toc-806>٧ </span>- ولو أنزلنا عليك - أيها النبى - دليل رسالتك مكتوباً فى ورق، فرأوه بأعينهم، وتأكدوا منه بوضع أيديهم عليه، لقالوا - تعنتاً -: ما هذا الذى نلمسه إلاّ سحر ظاهر!! .',NULL,'172',NULL,NULL,'0');
INSERT INTO [b836] VALUES (801,'<span data-type="title" id=toc-807>٨ </span>- وقالوا: نطلب أن ينزل الله عليك ملكاً يصدقك. ولو استجبنا لهم، وأرسلنا معه ملكاً كما اقترحوا، ثم عاندوا ولم يؤمنوا. لنفذ الأمر بإهلاكهم، ثم لا يمهلون لحظة.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b836] VALUES (802,'<span data-type="title" id=toc-808>٩ </span>- ولو جعلنا المؤيد للرسول ملكاً كما طلبوا، لجعلناه على هيئة بشر، حتى يستطيعوا مشاهدته والفهم عنه، فإنهم لا يقدرون على رؤية الملك فى صورته الأصلية، ولاشتبه عليهم الأمر واختلط بإرساله فى صورة بشر، وأوقعناهم فى نفس الخطأ الذى يتخبطون فيه.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b836] VALUES (803,'<span data-type="title" id=toc-809>١٠ </span>- ولقد سَخِرَ الكفار كثيراً برسل من قبلك - أيها النبى - فأحاط بالساخرين العذاب الذى أنذرهم به رسلهم، وقد جعلوه موضع سخريتهم من قبل.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b836] VALUES (804,'<span data-type="title" id=toc-810>١١ </span>- قل - أيها النبى - لهؤلاء الكفار: سيروا فى جوانب الأرض وتأملوا كيف كان الهلاك نهاية المكذبين لرسلهم فاعتبروا بهذه النهاية وذلك المصير.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b836] VALUES (805,'<span data-type="title" id=toc-811>١٢ </span>- قل - أيها النبى - لهؤلاء الجاحدين: مَنْ مَالِك السموات والأرضِ ومن فيهن؟ فإن أحجموا فقل الجواب الذى لا جواب غيره: إن مالكها هو الله - وحده - لا شريك له، وأنه أوجب على نفسه الرحمة بعباده، فلا يعجل عقوبتهم، ويقبل توبتهم، إنه ليحشرنكم إلى يوم القيامة الذى لا شك فيه. الذين ضيعوا أنفسهم وعرّضوها للعذاب فى هذا اليوم، هم الذين لا يصدقون بالله، ولا بيوم الحساب.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b836] VALUES (806,'<span data-type="title" id=toc-812>١٣ </span>- ولله ما فى كل زمان، كما أن له ما فى كل مكان، وهو السميع لكل ما يسمع، العليم بكل ما يُعلم.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b836] VALUES (807,'<span data-type="title" id=toc-813>١٤ </span>- قل - أيها النبى -: لا أتخذ غير الله إلهاً وناصراً، وهو - وحده - المنشئ للسموات والأرض على نظام لم يسبق إليه، وهو الرازق لعباده طعامهم، ولا يحتاج منهم إلى طعام. قل: إنى أمرنى الله أن أكون أول من أسلم، ونهانى أن أشرك معه غيره فى العبادة.',NULL,'174',NULL,NULL,'0');
INSERT INTO [b836] VALUES (808,'<span data-type="title" id=toc-814>١٥ </span>- قل: إنى أخاف، إن خالفت أمر ربى وعصيته، عذاب يوم شديد.',NULL,'174',NULL,NULL,'0');
INSERT INTO [b836] VALUES (809,'<span data-type="title" id=toc-815>١٦ </span>- من يُصرف عنه هذا العذاب يوم القيامة، فقد رَحِمَهُ اللَّهُ، وذلك هو الفوز الثابت البيّن.',NULL,'174',NULL,NULL,'0');
INSERT INTO [b836] VALUES (810,'<span data-type="title" id=toc-816>١٧ </span>- وإن يصبك الله بسوء فلا كاشف له إلا هو، وإن يمنحك خيراً فلا راد لفضله، لأنه على كل شئ قدير.',NULL,'174',NULL,NULL,'0');
INSERT INTO [b836] VALUES (811,'<span data-type="title" id=toc-817>١٨ </span>- وهو الغالب بقدرته، المستعلى على عباده، المتصف بالحكمة فى كل ما يفعل، المحيط علمه بما ظهر واستتر.',NULL,'174',NULL,NULL,'0');
INSERT INTO [b836] VALUES (812,'<span data-type="title" id=toc-818>١٩ </span>- قل - أيها النبى - لمن يكذبوك ويطلبون شهادة على رسالتك، أى شئ أعظم شهادة وأحق بالتصديق؟ ثم قل: إن الله أعظم شاهد بينى وبينكم على صدق ما جئتكم به، وقد أنزل علىّ هذا القرآن ليكون حُجّة لصدقى، لأحذركم به أنتم وكل من بلغه خبره، وهو حُجة قاطعة شاهدة بصدقى، لأنكم لا تستطيعون أن تأتوا بمثله!! سلهم: أأنتم الذين تقولون معتقدين أن مع الله آلهة غيره؟ ثم قل لهم: لا أشهد بذلك، ولا أقوله، ولا أقركم عليه، وإنما المعبود بحق إله واحد، وإننى برئ مما تشركون به من أوثان.',NULL,'174',NULL,NULL,'0');
INSERT INTO [b836] VALUES (813,'<span data-type="title" id=toc-819>٢٠ </span>- الذين آتيناهم الكتب السماوية من اليهود والنصارى، يعرفون محمداً وصدق رسالته، من هذه الكتب، كمعرفتهم أبناءهم. إن الذين ضيعوا أنفسهم، لا يقرون بما يعرفون، فهم لا يؤمنون.',NULL,'174',NULL,NULL,'0');
INSERT INTO [b836] VALUES (814,'<span data-type="title" id=toc-820>٢١ </span>- وليس أحد أشد ظلماً لنفسه وللحق ممن افترى على الله الكذب، وادعى أن له ولداً أو شريكاً، أو نسب إليه ما لا يليق، أو أنكر أدلته الدالة على وحدانيته وصدق رسله. إن الظالمين لا يفوزون بخير فى الدنيا والآخرة.',NULL,'175',NULL,NULL,'0');
INSERT INTO [b836] VALUES (815,'<span data-type="title" id=toc-821>٢٢ </span>- واذكر لهم ما سيحصل يوم نجمع الخلق كلهم للحساب، ثم نقول توبيخاً للذين عبدوا مع الله غيره: أين الذين جعلتموهم شركاء لله لينفعوكم؟',NULL,'175',NULL,NULL,'0');
INSERT INTO [b836] VALUES (816,'<span data-type="title" id=toc-822>٢٣ </span>- ثم لم تكن نتيجة محنتهم الشديدة فى هذا الموقف إلا محاولة التخلص من شركهم السابق بالكذب، فقالوا كاذبين: والله ربنا ما أشركنا فى العبادة أحداً غيرك.',NULL,'175',NULL,NULL,'0');
INSERT INTO [b836] VALUES (817,'<span data-type="title" id=toc-823>٢٤ </span>- انظر كيف غالطوا أنفسهم بهذا الكذب، وغاب عنهم ما كانوا يختلقونه من عبادة الأحجار ويزعمونها شركاء لله! {',NULL,'175',NULL,NULL,'0');
INSERT INTO [b836] VALUES (818,'<span data-type="title" id=toc-824>٢٥ </span>- ومنهم من يستمع إليك حين تتلو القرآن، لا ليتفهموه، وليهتدوا به، وإنما ليتلمسوا سبلا للطعن فيه والسخرية منه.
INSERT INTO [b836] VALUES (819,'<span data-type="title" id=toc-825>٢٦ </span>- وهم ينهون الناس عن الإيمان بالقرآن، ويبتعدون عنه بأنفسهم، فلا ينتفعون ولا يدعون غيرهم ينتفع} وما يضرون بذلك الصنيع إلا أنفسهم، وما يشعرون بقبح ما يفعلون.',NULL,'175',NULL,NULL,'0');
INSERT INTO [b836] VALUES (820,'<span data-type="title" id=toc-826>٢٧ </span>- ولو ترى - أيها النبى - هؤلاء الكفار وهم واقفون على النار يعانون أهوالها، لرأيت أمراً غريباً رهيباً، إذ يتمنون الرجوع إلى الدنيا، ويقولون: يا ليتنا نرد إليها لنصلح ما أفسدنا، ولا نكذب بآيات ربنا، ونكون من المؤمنين {',NULL,'176',NULL,NULL,'0');
INSERT INTO [b836] VALUES (821,'<span data-type="title" id=toc-827>٢٨ </span>- وليس قولهم هذا، إلا لأنه قد ظهر لهم ما لا يمكن إخفاؤه والمكابرة فيه، مما كان يخبرهم به الرسول} ولو ردّوا إلى الدنيا كما يتمنون، لعادوا إلى الكفر الذى نهاهم الله عنه، لغرورهم بزخرفها وإطاعة أهوائهم {وإنهم لكاذبون فى دعواهم الإيمان إذا ردوا إلى الدنيا}',NULL,'176',NULL,NULL,'0');
INSERT INTO [b836] VALUES (822,'<span data-type="title" id=toc-828>٢٩ </span>- ولو أعيدوا إلى الدنيا لعادوا إلى سيرتهم الأولى وقالوا: ليس لنا حياة إلا هذه الحياة الدنيا. وما نحن بعد ذلك بمبعوثين {',NULL,'176',NULL,NULL,'0');
INSERT INTO [b836] VALUES (823,'<span data-type="title" id=toc-829>٣٠ </span>- لو تراهم حين يقفون للحساب أمام ربهم، ويعرفون صدق ما أنزله على رسله، لرأيت سوء حالهم إذ يقول الله لهم: أليس هذا الذى تشاهدونه الآن هو الحق الذى أنكرتموه فى دنياكم؟ فيقولون متذللين: بلى وربنا إنه الحق} فيقول الله لهم بعد ذلك: ادخلوا النار بسبب ما كنتم حريصين عليه من الكفر.',NULL,'176',NULL,NULL,'0');
INSERT INTO [b836] VALUES (824,'<span data-type="title" id=toc-830>٣١ </span>- قد خسر الذين أنكروا لقاء الله للحساب والجزاء يوم القيامة، وظلّوا على إنكارهم، حتى إذا فاجأتهم مشاهد يوم القيامة ندموا وقالوا: يا حسرتنا على إهمالنا اتباع الحق فى الدنيا {وهم يومئذٍ يرزحون تحت أعباء ذنوبهم. ألا قُبْحُ ما يحملون من الذنوب.',NULL,'176',NULL,NULL,'0');
INSERT INTO [b836] VALUES (825,'<span data-type="title" id=toc-831>٣٢ </span>- وليست الحياة الدنيا التى حسب الكفار أنه لا حياة غيرها، والتى لا يقصد بالعمل فيها مرضاة الله، إلا لعباً لا نفع فيه، ولهواً يتلهى به} ! وان الدار الآخرة لهى الحياة الحقيقية، وهى أنفع للذين يخافون الله فيمتثلون أمره. أفلا تعقلون هذا الأمر الواضح؟ ، أفلا تفهمون ما يضركم ولا ينفعكم؟',NULL,'176',NULL,NULL,'0');
INSERT INTO [b836] VALUES (826,'<span data-type="title" id=toc-832>٣٣ </span>- إننا نعلم أنه ليحزنك أيها النبى ما يقوله الكفار تكذيباً لك، فلا تحزن من ذلك. لأن الحقيقة أنهم لا يتهمونك بالكذب، ولكنهم لظلمهم لأنفسهم وللحق يكابرون، فينكرون بألسنتهم دلائل صدقك، وعلامات نبوتك.',NULL,'177',NULL,NULL,'0');
INSERT INTO [b836] VALUES (827,'<span data-type="title" id=toc-833>٣٤ </span>- ولقد قوبل رسل من قبلك بالتكذيب والإيذاء من أقوامهم، كما فعل معك قومك، فصبروا على التكذيب والإيذاء حتى نصرناهم، فاصبر كما صبروا حتى يأتيك نصرنا، ولا مغير لوعد الله بنصر الصابرين، فلا بد من تحققه. ولقد قصصنا عليك من أخبار هؤلاء الرسل وتأييدنا لهم، ما فيه تسلية لك، وما توجبه الرسالة من تحمل الشدائد.',NULL,'177',NULL,NULL,'0');
INSERT INTO [b836] VALUES (828,'<span data-type="title" id=toc-834>٣٥ </span>- وإن كان قد شق عليك انصرافهم عن دعوتك، فإن استطعت أن تتخذ طريقاً فى باطن الأرض، أو سلماً تصعد به إلى السماء، فتأتيهم بدليل على صدقك، فافعل. وليس فى قدرتك ذلك. فأرح نفسك واصبر لحكم ربك، ولو شاء الله هدايتهم لحملهم جميعاً على الإيمان بما جئت به قسراً وقهراً، ولكنه تركهم لاختيارهم فلا تكونن من الذين لا يعلمون حكم الله وسنته فى الخلق.',NULL,'177',NULL,NULL,'0');
INSERT INTO [b836] VALUES (829,'<span data-type="title" id=toc-835>٣٦ </span>- إنما يجيب دعوة الحق مقبلين عليه، الذين يسمعون سماع فهم وتدبر. وأما هؤلاء فلا ينتفعون بدعوتك، لأنهم فى حكم الأموات. وسيبعثهم الله يوم القيامة من القبور، ويرجعهم إليه، فيحاسبهم على ما فعلوا.',NULL,'177',NULL,NULL,'0');
INSERT INTO [b836] VALUES (830,'<span data-type="title" id=toc-836>٣٧ </span>- وقال الكفار متعنتين: نطلب أن ينزل على محمد دليل مادى من ربه يشهد بصدق دعوته. قل لهم أيها النبى: إن الله قادر على أن ينزل أى دليل تقترحونه. ولكن أكثرهم لا يعلمون حكمة الله فى إنزال الآيات، وأنها ليست تابعة لأهوائهم، وأنه لو أجاب مقترحاتهم ثم كذبوا بعد ذلك لأهلكهم، ولكن أكثرهم لا يعلمون نتائج أعمالهم!!',NULL,'177',NULL,NULL,'0');
INSERT INTO [b836] VALUES (831,'<span data-type="title" id=toc-837>٣٨ </span>- وإن أقوى دليل على قدرة الله وحكمته ورحمته، أنه خلق كل شئ، وليس فى الأرض حيوان يدب فى ظاهر الأرض وباطنها، أو طائر يطير بجناحيه فى الهواء، إلا خلقها الله جماعات تماثلكم، وجعل لها خصائصها ومميزاتها ونظام حياتها. ما تركنا فى الكتاب المحفوظ عندنا شيئاً إلا أثبتناه. وإن كانوا قد كذبوا، فيحشرون مع كل الأمم للحساب يوم القيامة.',NULL,'178',NULL,NULL,'0');
INSERT INTO [b836] VALUES (832,'<span data-type="title" id=toc-838>٣٩ </span>- والذين لم يصدقوا بأدلتنا الدالة على قدرتنا وصدق رسالتك، لم ينتفعوا بحواسهم فى معرفة الحق، فتخبطوا فى ضلال الشرك والعناد، تخبط الأصم الأبكم فى ظلمات الليل، لا نجاة له من الهلاك. ولو كان فى هؤلاء استعداد للخير لوفقهم الله إليه، فإنه سبحانه إذا أراد إضلال إنسان لفساد قصده، تركه وشأنه، وإذا أراد هدايته لسلامة قصده، يسر له السير فى طريق الإيمان الواضح المستقيم.',NULL,'178',NULL,NULL,'0');
INSERT INTO [b836] VALUES (833,'<span data-type="title" id=toc-839>٤٠ </span>- قل أيها النبى لهؤلاء الكفار: أخبرونى إن جاءكم عذاب من عند الله فى الدنيا أو جاءتكم القيامة بأهوالها، هل تتجهون لغير الله تضرعون إليه فى هذا الوقت فينفعكم شيئاً، إن كنتم صادقين فى عبادتكم لغير الله؟',NULL,'178',NULL,NULL,'0');
INSERT INTO [b836] VALUES (834,'<span data-type="title" id=toc-840>٤١ </span>- بل إنكم لا تتجهون إلا إليه، إذ تدعونه فيكشف عنكم ما تطلبون كشفه إن شاء. وفى حال هذه الشدة، تنسون من تجعلونهم لله شركاء!!',NULL,'178',NULL,NULL,'0');
INSERT INTO [b836] VALUES (835,'<span data-type="title" id=toc-841>٤٢ </span>- لا يشق عليك - أيها النبى - ما تلاقيه من قومك. فلقد بعثنا قبلك رسلا إلى أمم كثيرة قبل أمتك. فكذبوهم، فعاقبناهم بالشدائد تنزل بهم، وبما يضرهم فى أبدانهم، لعلهم يخشعون ويرجعون إلى الله.',NULL,'178',NULL,NULL,'0');
INSERT INTO [b836] VALUES (836,'<span data-type="title" id=toc-842>٤٣ </span>- وكان ينبغى لهم أن يرجعوا إلى ربهم، ولكنهم لم يفعلوا، بل استمرت قلوبهم على قسوتها، وزين لهم الشيطان عملهم القبيح.',NULL,'179',NULL,NULL,'0');
INSERT INTO [b836] VALUES (837,'<span data-type="title" id=toc-843>٤٤ </span>- فلما تركوا الاتعاظ بما ابتليناهم من الفقر والمرض، ابتليناهم بعد ذلك بالرزق الواسع، ففتحنا عليهم أبواب كل شئ من أسباب الرزق، حتى إذا فرحوا بما أنعمنا به عليهم، ولم يشكروا الله عليه، جاءهم العذاب فجأة، فإذا هم متحيّرون يائسون، لا يجدون للنجاة سبيلا {',NULL,'179',NULL,NULL,'0');
INSERT INTO [b836] VALUES (838,'<span data-type="title" id=toc-844>٤٥ </span>- فأبيد هؤلاء القوم الظالمون عن آخرهم. والحمد لله مربى الخلق بالنقم والنعم، ومطهر الأرض من فساد الظالمين.',NULL,'179',NULL,NULL,'0');
INSERT INTO [b836] VALUES (839,'<span data-type="title" id=toc-845>٤٦ </span>- قل لهم - أيها النبى -: أخبرونى إن سلب الله سمعكم، وغطى قلوبكم بما يحجبها عن الإدراك، فجعلكم صماً عمياً لا تفهمون شيئاً، مَنْ تعبدون غير الله. من إله يستطيع أن يرد إليكم ما سلبه الله منكم؟ انظر - أيها النبى - كيف نوضح البراهين وننوعها، ثم هم مع هذا يعرضون عن تدبرها والانتفاع بها} !',NULL,'179',NULL,NULL,'0');
INSERT INTO [b836] VALUES (840,'<span data-type="title" id=toc-846>٤٧ </span>- قل: أخبرونى إن حل بكم عذاب الله فجأة دون توقع، أو جاءكم عيانا على ترقب، لسبق ما ينذركم بوقوعه، هل يصيب هذا العذاب إلا القوم الذين ظلموا أنفسهم بالإصرار على الشرك والضلال؟ أنه لا يصيب غيرهم.',NULL,'179',NULL,NULL,'0');
INSERT INTO [b836] VALUES (841,'<span data-type="title" id=toc-847>٤٨ </span>- وما نرسل الأنبياء إلا ليبشروا من يؤمن بالخير والثواب، وليحذروا من يكفر من العذاب. فمن آمن بدعوتهم وعمل صالحاً، فلا خوف عليهم من شر يصيبهم، ولا يحزنون على خير يفوتهم.',NULL,'179',NULL,NULL,'0');
INSERT INTO [b836] VALUES (842,'<span data-type="title" id=toc-848>٤٩ </span>- والذين كذبوا بالأدلة الواضحة على صدق ما جاء به الرسل، يصيبهم العذاب بسبب خروجهم عن الطاعة والإيمان.',NULL,'179',NULL,NULL,'0');
INSERT INTO [b836] VALUES (843,'<span data-type="title" id=toc-849>٥٠ </span>- قل - أيها الرسول - لهؤلاء الكفار: لا أقول لكم إنى أملك التصرف بما يملكه الله فأجيبكم إلى ما تطلبون، ولا أدعى علم الغيب الذى لم يطلعنى الله عليه، ولا أقول إنى ملك أستطيع الصعود إلى السماء! إنما أنا بشر لا أتبع إلا ما يوحيه الله إلى. قل - أيها النبى -: هل يستوى الضال والمهتدى فى معرفة هذه الحقائق؟ هل يليق بكم أن تعرضوا عن هدى أسوقه لكم، فلا تتأملون فيه بعقولكم حتى يتبين لكم الحق؟',NULL,'180',NULL,NULL,'0');
INSERT INTO [b836] VALUES (844,'<span data-type="title" id=toc-850>٥١ </span>- وحذّر - بما فى هذا القرآن - الذين يخافون من هول يوم تسوقهم فيه الملائكة للحساب والجزاء، حيث لا ناصر لهم ولا شفيع إلا بإذن الله، ليبتعدوا عما يغضب الله.',NULL,'180',NULL,NULL,'0');
INSERT INTO [b836] VALUES (845,'<span data-type="title" id=toc-851>٥٢ </span>- ولا تستجب - أيها النبى - لدعوة المتكبرين من الكفار، فتُبعد عنك المستضعفين من المؤمنين، الذين يعبدون ربهم دائماً، ولا يريدون إلا رضاه. ولا تلتفت لدس المشركين على هؤلاء المؤمنين، فلست مسئولا أمام الله عن شئ من أعمالهم، كما أنهم ليسوا مسئولين عن شئ من أعمالك، فإن استجبت لهؤلاء الكفار المتعنتين، وأبعدت المؤمنين، كنت من الظالمين.',NULL,'180',NULL,NULL,'0');
INSERT INTO [b836] VALUES (846,'<span data-type="title" id=toc-852>٥٣ </span>- وبمثل هذا الابتلاء الذى جرت به سُنَّتنا، امتحنا المتكبرين بسبق الضعفاء إلى الإسلام، ليقول المتكبرون مستنكرين ساخرين، هل هؤلاء الفقراء هم الذين أنعم الله عليهم من بيننا بالخير الذى يعدهم به محمد؟ إن هؤلاء الفقراء يعرفون نعمة الله عليهم بالتوفيق إلى الإيمان فيشكرونه. والله أعلم بمن يشكرون فضله ونعمه.',NULL,'180',NULL,NULL,'0');
INSERT INTO [b836] VALUES (847,'<span data-type="title" id=toc-853>٥٤ </span>- وإذا جاءك الذين يصدقون بالقرآن فقل لهم تكريماً لهم: سلام عليكم، أبشركم برحمة الله الواسعة، التى أوجبها على نفسه تفضلا منه، والتى تقضى بأن من عمل منكم سيئة غير متدبر نتائجها، ثم رجع إلى الله نادماً تائباً، وأصلح أعماله، غفر الله له، لأنه كثير المغفرة واسع الرحمة.',NULL,'180',NULL,NULL,'0');
INSERT INTO [b836] VALUES (848,'<span data-type="title" id=toc-854>٥٥ </span>- وبمثل ذلك البيان الواضح نوضح الدلائل المتنوعة، ليظهر طريق الحق الذى يسلكه المؤمنون، ويتبين طريق الباطل الذى يسلكه الكافرون.',NULL,'181',NULL,NULL,'0');
INSERT INTO [b836] VALUES (849,'<span data-type="title" id=toc-855>٥٦ </span>- قل - أيها النبى - لهؤلاء الكفار: إن الله قد نهانى عن عبادة الذين تعبدونهم من دون الله فلا أتبع أهواءكم، فإنى حين أتبعكم أكون قد انحرفت عن الحق، ولم أكن من المهتدين!',NULL,'181',NULL,NULL,'0');
INSERT INTO [b836] VALUES (850,'<span data-type="title" id=toc-856>٥٧ </span>- قل لهم: إنى على شريعة واضحة منزلة من ربى وقد كذبتم القرآن الذى جاء بها، وليس فى قدرتى أن أقدم ما تستعجلونه من العذاب، بل هو فى قدرة الله، ومرهون بإرادته وحكمته، وليس الأمر والسلطان إلا لله، إن شاء عجل لكم العذاب، وإن شاء أخره، له سبحانه فى ذلك الحكمة، وهو خير الفاصلين بينى وبينكم.',NULL,'181',NULL,NULL,'0');
INSERT INTO [b836] VALUES (851,'<span data-type="title" id=toc-857>٥٨ </span>- قل: لو أن فى قدرتى إنزال العذاب الذى تتعجلونه، لأنزلته عليكم غضبا لربى، وانتهى الأمر بينى وبينكم بذلك، ولكن الأمر لله وهو أعلم بما يستحقه الكافرون من العذاب العاجل أو الآجل.',NULL,'181',NULL,NULL,'0');
INSERT INTO [b836] VALUES (852,'<span data-type="title" id=toc-858>٥٩ </span>- وعند الله علم جميع أبواب المغيبات، لا يحيط بها علماً إلا هو ومن يريد إعطاءه بعضها، ويحيط علمه كذلك بجميع الموجودات فى البر والبحر ولا تسقط ورقة - أيَّة ورقة كانت - إلا يعلمها، ولا تسقط حبة ما فى باطن الأرض ولا شئ رطب ولا يابس، إلا وهو سبحانه محيط بعلمه إحاطة تامة.',NULL,'181',NULL,NULL,'0');
INSERT INTO [b836] VALUES (853,'<span data-type="title" id=toc-859>٦٠ </span>- وهو الذى ينيمكم بالليل، ويوقظكم بالنهار، ويعلم ما كسبتم فيه حتى ينتهى أجل كل منكم فى الدنيا بموته، ثم يوم القيامة ترجعون جميعاً إلى الله - وحده - يخبركم بأعمالكم فى الدنيا من خير أو شر، ويجازيكم عليها.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b836] VALUES (854,'<span data-type="title" id=toc-860>٦١ </span>- هو الغالب بقدرته، المستعلى بسلطانه على عباده، والذى يرسل عليكم ملائكة يحصون كل أعمالكم إلى أن تجئ نهاية كل منكم، فتقبض روحه ملائكتنا الذين نرسلهم لذلك، وهم لا يقصرون فيما يوكل إليهم.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b836] VALUES (855,'<span data-type="title" id=toc-861>٦٢ </span>- ثم يُبعث هؤلاء الأموات يوم القيامة، ويوقفون أمام ربهم الذى يتولى وحده أمورهم بحق. اعلموا أن له - وحده - الفصل بين الخلائق وحسابهم فى ذلك اليوم، وهو أسرع من يتولى الحساب والجزاء.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b836] VALUES (856,'<span data-type="title" id=toc-862>٦٣ </span>- قل - أيها النبى - للمشركين: من الذى ينقذكم من أهوال البر والبحر، إذا حلت بكم، فلجأتم إليه تدعونه فى خضوع ظاهر وباطن قائلين: نقسم لئن أنقذتنا من هذه الأهوال لنكونن من المقرين بفضلك، القائمين بشكرك.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b836] VALUES (857,'<span data-type="title" id=toc-863>٦٤ </span>- قل: الله - وحده - هو الذى ينقذكم من هذه الأهوال، ومن كل شدة أخرى، ثم أنتم مع ذلك تشركون معه فى العبادة غيره مما لا يدفع شراً ولا يجلب خيراً.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b836] VALUES (858,'<span data-type="title" id=toc-864>٦٥ </span>- قل: إن الله - وحده - هو الذى يقدر على أن يرسل عليكم عذاباً يأتيكم من أعلاكم أو من أسفلكم. أو يجعل بعضكم لبعض عدواً. وتكونون طوائف مختلفة الأهواء متناكرة، يعذب بعضكم بعضا عذاباً شديداً! {انظر كيف دلت الدلائل على قدرتنا واستحقاقنا وحدنا للعبادة، لعلهم يتأملونها ويفهمون الحق}',NULL,'182',NULL,NULL,'0');
INSERT INTO [b836] VALUES (859,'<span data-type="title" id=toc-865>٦٦ </span>- وكذب قومك بالقرآن، وهو الحق الذى لا موضع فيه لتكذيب قل أيها النبى لهم: لست موكلا بحفظكم، وإحصاء أعمالكم ومجازاتكم عليها. بل أمركم فيها إلى الله.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b836] VALUES (860,'<span data-type="title" id=toc-866>٦٧ </span>- لكل خبر جاء به القرآن وقت يتحقق فيه. وسوف تعلمون - صِدْق - هذه الأخبار عند وقوعها.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b836] VALUES (861,'<span data-type="title" id=toc-867>٦٨ </span>- وإذا حضرت مجلس الكفار، ووجدتهم يطعنون فى آيات القرآن، أو يستهزئون بها، فانصرف عنهم حتى ينتقلوا إلى حديث آخر. وإن نسيت وجالستهم فى أثناء حديثهم الباطل. ثم تذكرت أمر الله بالبعد عنهم. فلا تجلس بعد التذكر مع القوم الظالمين.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b836] VALUES (862,'<span data-type="title" id=toc-868>٦٩ </span>- وليس على الذين يتقون الله شئ من إثم هؤلاء الظالمين، إذا استمروا على ضلالهم، ولكن يجب أن يُذكِّروهم، لعلهم يخشون عذاب الله ويكفون عن الباطل.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b836] VALUES (863,'<span data-type="title" id=toc-869>٧٠ </span>- واترك - أيها النبى - الذين اتخذوا شريعتهم اللهو واللعب، وخدعتهم الحياة الدنيا عن الآخرة، وذَكِّر دائماً بالقرآن، وحذرهم هول يوم تحبس فيه كل نفس بعملها، حيث لا ناصر ولا معين غير الله، وإن كل فدية للنجاة من العذاب لا تقبل. أولئك الكافرون الذين حبسوا فى العذاب بسبب ما عملوا من شر، لهم فى جهنم شراب من ماء شديد الحرارة، وعذاب شديد الألم بسبب كفرهم.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b836] VALUES (864,'<span data-type="title" id=toc-870>٧١ </span>- قل لأولاء الكفار توبيخاً لهم، هل يصح أن يعبد غير الله مما لا يملك جلب نفع، ولا دفع ضر، وننتكس فى الشرك بعد أن وفقنا الله إلى الإيمان، ونكون كالذى غررت به الشياطين وأضلته فى الأرض، فصار فى حيرة لا يهتدى معها إلى الطريق المستقيم، وله رفقة مهتدون يحاولون تخليصه من الضلال، قائلين له: ارجع إلى طريقنا السوى، فلا يستجيب لهم. قل - أيها النبى -: إن الإسلام هو الهدى والرشاد، وما عداه ضلال، وقد أمرنا الله بالانقياد له، فهو خالق العالمين ورازقهم ومدبر أمورهم.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b836] VALUES (865,'<span data-type="title" id=toc-871>٧٢ </span>- أعرضوا عن المشركين بعد أن تدعوهم إلى الهدى، وانصرفوا إلى عبادة ربكم، وأدوا الصلاة على أكمل وجه من الخضوع، وخافوا الله، وأدوا أوامره، فإنه هو الذى تجمعون عنده.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b836] VALUES (866,'<span data-type="title" id=toc-872>٧٣ </span>- وهو الله وحده الذى خلق السموات والأرض، وأقام خلقهما على الحق والحكمة، وفى أى وقت تتجه إرادته سبحانه إلى إيجاد شئ يوجد فوراً، يوجد الأشياء بكلمة: «كن» ، وكل قول له هو الصدق والحق، وله وحده التصرف المطلق يوم القيامة، حين يُنْفَخ فى البوق إيذاناً بالبعث، وهو سبحانه الذى يستوى فى علمه الغائب والحاضر، وهو الذى يتصرف بالحكمة فى جميع أفعاله، والذى يحيط علمه ببواطن الأمور وظواهرها.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b836] VALUES (867,'<span data-type="title" id=toc-873>٧٤ </span>- واذكر - أيها النبى - ما كان، حين قال إبراهيم لأبيه آزر، منكراً عليه عبادة غير الله: ما كان لك أن تجعل الأصنام آلهة؟ إنى أراك وقومك الذين يشاركونك فى هذه العبادة فى بُعْدٍ واضح عن طريق الحق.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b836] VALUES (868,'<span data-type="title" id=toc-874>٧٥ </span>- وكما رأى إبراهيم - بتوفيقنا - ضلال أمته وقومه فى تأليه الأصنام نريه مُلكنا العظيم للسموات والأرض وما فيهما، ليقيم الحُجة على قومه، وليزداد إيماناً.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b836] VALUES (869,'<span data-type="title" id=toc-875>٧٦ </span>- طلب إبراهيم ربه، فهداه الله، إذ ستر الليل وجه النهار بظلمته، فرأى نجماً متألقاً، قال: هذا ربى. فلما غاب، قال مبطلا لربوبية النجم: لا أقبل عبادة الآلهة الزائلين المتغيرين! .',NULL,'184',NULL,NULL,'0');
INSERT INTO [b836] VALUES (870,'<span data-type="title" id=toc-876>٧٧ </span>- وحين رأى القمر طالعاً بعد ذلك قال محدثاً نفسه: هذا ربى. فلما غاب هو الآخر، وظهر بطلان ربوبيته، قال ليوجه نفوسهم إلى التماس الهداية: أقسم إن لم يهدنى ربى إلى الحق لأكونن من القوم الحائرين.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b836] VALUES (871,'<span data-type="title" id=toc-877>٧٨ </span>- ثم رأى الشمس طالعة بعد ذلك، فقال محدثاً نفسه: هذا ربى، لأنه أكبر ما يرى من الكواكب، فلما غابت قال: يا قوم إنى برئ من الأصنام التى تشركونها مع الله فى العبادة.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b836] VALUES (872,'<span data-type="title" id=toc-878>٧٩ </span>- بعد أن رأى ضعف المخلوقات اتجه إلى خالقها قائلا: إنى وجهت قصدى إلى عبادة الله - وحده - الذى خلق السموات والأرض، مجانباً كل سبيل غير سبيله وما أنا بعد الذى رأيت من دلائل التوحيد - ممن يرضى أن يكون من المشركين مثلهم.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b836] VALUES (873,'<span data-type="title" id=toc-879>٨٠ </span>- ومع ذلك جادله قومه فى توحيد الله، وخوفوه غضب آلهتهم، فقال لهم: ما كان لكم أن تجادلونى فى توحيد الله وقد هدانى إلى الحق، ولا أخاف غضب آلهتكم التى تشركونها مع الله، لكن إذا شاء ربى شيئاً من الضر وقع ذلك، لأنه - وحده - القادر، وقد أحاط علم ربى بالأشياء كلها، ولا علم لآلهتكم بشئ منها. أتغفلون عن كل ذلك فلا تدركون أن العاجز الجاهل لا يستحق أن يعبد؟ {',NULL,'185',NULL,NULL,'0');
INSERT INTO [b836] VALUES (874,'<span data-type="title" id=toc-880>٨١ </span>- وكيف تتصورون أنى يمكن أن أخاف آلهتكم الباطلة، على حين لا تخافون الإله الحق الذى أشركتم به غيره فى العبادة؟} فأى فريق منا فى هذه الحالة أحق بالطمأنينة والأمان، إن كنتم تعلمون الحق وتدركونه؟',NULL,'185',NULL,NULL,'0');
INSERT INTO [b836] VALUES (875,'<span data-type="title" id=toc-881>٨٢ </span>- الذين آمنوا بالله، ولم يخلطوا إيمانهم هذا بعبادة أحد سواه، هؤلاء - وحدهم - هم الأحق بالطمأنينة، وهم - وحدهم - المهتدون إلى طريق الحق والخير.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b836] VALUES (876,'<span data-type="title" id=toc-882>٨٣ </span>- وتلك الحُجة العظيمة على ألوهيتنا ووحدانيتنا، أعطيناها إبراهيم ليقيمها على قومه، فارتفع بها عليهم، وسنتنا فى عبادنا أن نرفع بالعلم والحكمة من نريد منهم درجات. إن ربك - أيها النبى - حكيم يضع الشئ فى موضعه، عليم بمن يستحق الرفعة ومن لا يستحق.',NULL,'186',NULL,NULL,'0');
INSERT INTO [b836] VALUES (877,'<span data-type="title" id=toc-883>٨٤ </span>- ووهبنا لإبراهيم إسحق ويعقوب بن إسحق، ووفقنا كلا منهما إلى الحق والخير كأبيهما، ووفقنا من قبلهم نوحاً إلى ذلك، وهدينا من ذرية نوح داود وسليمان وأيوب ويوسف وموسى وهارون، وكما جزينا هؤلاء نجزى المحسنين بما يستحقون.',NULL,'186',NULL,NULL,'0');
INSERT INTO [b836] VALUES (878,'<span data-type="title" id=toc-884>٨٥ </span>- وهدينا زكريا ويحيى وعيسى وإلياس، كل واحد من هؤلاء من عبادنا الصالحين.',NULL,'186',NULL,NULL,'0');
INSERT INTO [b836] VALUES (879,'<span data-type="title" id=toc-885>٨٦ </span>- وهدينا إسماعيل وإليسع ويونس ولوطا، وفضلنا كل واحد من هؤلاء جميعاً على العالمين فى زمانه، بالهداية والنبوة.',NULL,'186',NULL,NULL,'0');
INSERT INTO [b836] VALUES (880,'<span data-type="title" id=toc-886>٨٧ </span>- واصطفينا بعض آباء هؤلاء وذرياتهم وإخوانهم، ووفقناهم إلى طريق لا اعوجاج فيه.',NULL,'186',NULL,NULL,'0');
INSERT INTO [b836] VALUES (881,'<span data-type="title" id=toc-887>٨٨ </span>- ذلك التوفيق العظيم الذى ناله هؤلاء، هو توفيق من الله، يوفق إليه من يشاء من عباده. ولو أشرك هؤلاء المختارون لضاعت كل أعمال الخير التى يعملونها، فلا يكون عليها ثواب.',NULL,'186',NULL,NULL,'0');
INSERT INTO [b836] VALUES (882,'<span data-type="title" id=toc-888>٨٩ </span>- أولئك الذين آتيناهم الكتب المنزلة والعلم النافع وشرف النبوة، فإن يجحد بهذه الثلاثة مشركو مكة فقد عهدنا برعايتها والانتفاع بها إلى قوم لا يكفرون بها.',NULL,'186',NULL,NULL,'0');
INSERT INTO [b836] VALUES (883,'<span data-type="title" id=toc-889>٩٠ </span>- أولئك الذين وفقهم الله إلى طريق الحق والخير، فاتبعهم فيما اجتمعوا عليه من أصول الدين وأمهات الفضائل، ولا تسلك غير سبيلهم. . قل - أيها النبى - لقومك كما قال هؤلاء لأقوامهم: لا أطلب منكم على تبليغ كلام الله أجراً {ما هذا القرآن إلا تذكير للعالمين، ولا غاية لى إلا أن تنتفعوا به.',NULL,'187',NULL,NULL,'0');
INSERT INTO [b836] VALUES (884,'<span data-type="title" id=toc-890>٩١ </span>- هؤلاء الكفار لم يقدروا الله ورحمته وحكمته حق التقدير، إذ أنكروا أن تنزل رسالته على أحد من البشر} قل - أيها النبى - للمشركين ومن يشايعهم على ذلك من اليهود: من أنزل الكتاب الذى جاء به موسى نوراً يضئ، وهدى يرشد؟ إنكم - أيها اليهود - تجعلون كتابته فى أجزاء متفرقة تظهرون منها ما يتفق وأهواءكم، وتخفون كثيراً مما يلجئكم إلى الإيمان والتصديق بالقرآن، وعلمتم منه ما لم تكونوا تعلمونه أنتم ولا آباؤكم!! وتول أنت - أيها النبى - الجواب، وقل لهم: الله هو الذى أنزل التوراة، ثم اتركهم يمضون فى الضلال عابثين كالصبيان.',NULL,'187',NULL,NULL,'0');
INSERT INTO [b836] VALUES (885,'<span data-type="title" id=toc-891>٩٢ </span>- وهذا القرآن كتاب أنزلناه - كما أنزلنا التوراة - كثير الخير، باق إلى يوم القيامة، مصدق لما تقدمه من الكتب المنزلة، مخبر عن نزولها، لتبشر به المؤمنين، وتخوف الكفار من أهل مكة ومن حولها فى جميع أنحاء الأرض من غضب الله، إذا لم يذعنوا له. والذين يصدقون بيوم الجزاء يحملهم رجاء الثواب والخوف من العقاب على الإيمان به، وهم لذلك يحافظون على أداء صلاتهم كاملة مستوفاة.',NULL,'187',NULL,NULL,'0');
INSERT INTO [b836] VALUES (886,'<span data-type="title" id=toc-892>٩٣ </span>- لم يكذب النبى حين أعلن أن القرآن من عند الله، وليس أحد أكثر ظلماً ممن اختلق الكذب على الله، أو قال: تلقيت وحياً من الله، دون أن يكون قد تلقى شيئاً من الوحى. وليس أحد كذلك أشد ظلماً ممن قال: سآتى بكلام مثل ما أنزله الله {ولو تعلم حال الظالمين، وهم فى شدائد الموت. والملائكة ينزعون أرواحهم من أجسادهم فى قسوة وعنف، لرأيت هوْلاً رهيباً ينزل بهم} ويقال لهم حينئذ: الآن تبدأ مجازاتكم بالعذاب المذل المهين، جزاء ما كنتم تقولون على الله غير الحق، وجزاء استكباركم عن النظر والتدبر فى آيات الله الكونية والقرآنية.',NULL,'188',NULL,NULL,'0');
INSERT INTO [b836] VALUES (887,'<span data-type="title" id=toc-893>٩٤ </span>- ويقول لهم الله يوم القيامة: لقد تأكدتم الآن بأنفسكم أنكم بعثتم أحياء من قبوركم كما خلقناكم أول مرة، وجئتم إلينا منفردين عن المال والولد والأصحاب، وتركتم وراءكم فى الدنيا كل ما أعطيناكم إياه مما كنتم تغترون به ولا نرى معكم اليوم الشفعاء الذين زعمتم أنهم ينصرونكم عند الله، وأنهم شركاء لله فى العبادة {لقد تقطعت بينكم وبينهم كل الروابط، وغاب عنكم ما كنتم تزعمون أنهم ينفعونكم}',NULL,'188',NULL,NULL,'0');
INSERT INTO [b836] VALUES (888,'<span data-type="title" id=toc-894>٩٥ </span>- إن دلائل قدرة الله على البعث، واستحقاقه وحده للعبادة، وبعثه للناس من قبورهم، متوافرة متنوعة، فهو وحده الذى يشق الحب، ويخرج منه النبات، ويشق النوى ويخرج منه الشجر، ويخرج الحى من الميت كالإنسان من التراب، ويخرج الميت من الحى كاللبن من الحيوان، ذلك القادر العظيم هو الإله الحق، فليس هناك صارف يصرفكم عن عبادته إلى عبادة غيره.',NULL,'188',NULL,NULL,'0');
INSERT INTO [b836] VALUES (889,'<span data-type="title" id=toc-895>٩٦ </span>- هو الذى يشق غبش الصبح بضوء النهار، ليسعى الأحياء إلى تحصيل أسباب حياتهم، وجعل الليل ذا راحة للجسم والنفس، وجعل سير الشمس والقمر بنظام دقيق يعرف به الناس مواقيت عباداتهم ومعاملاتهم.
INSERT INTO [b836] VALUES (890,'<span data-type="title" id=toc-896>٩٧ </span>- وهو الذى جعل لكم النجوم لتهتدوا بمواقعها إلى مقاصدكم، وأنتم سائرون فى ظلمات الليل بالبر والبحر، إنا قد بيّنا دلائل رحمتنا وقدرتنا لقوم ينتفعون بالعلم.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b836] VALUES (891,'<span data-type="title" id=toc-897>٩٨ </span>- هو الذى أنشأكم من أصل واحد، هو أبو البشر آدم، وآدم من الأرض، فالأرض مكان استقراركم مدة حياتكم، ومستودع لكم بعد موتكم وتغيبكم فى بطنها، وقد بيّنا دلائل قدرتنا لقوم يدركون ويفهمون الأشياء على وجهها.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b836] VALUES (892,'<span data-type="title" id=toc-898>٩٩ </span>- وهو الذى أنزل من السحاب ماء أخرج به نبات كل صنف، فأخرج من النبات شيئاً غَضَّا طريَّا، ونخرج منه حباً كثيراً بعضه فوق بعض، ومن طلع النخل عراجين نخرجها محملة بالثمار سهلة التناول، وأخرجنا كذلك بالماء جنات من الأعناب والزيتون والرمان، ومنها ما هو متماثل الثمر فى الشكل وغير متماثل فى الطعم والرائحة ونوع الفائدة. انظروا فى تدبر واعتبار إلى ثمره حين يثمر، وإلى نضجه كيف تم بعد أطوار مختلفة؟ إن فى ذلك لدلائل لقوم ينشدون الحق ويؤمنون به ويذعنون له.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b836] VALUES (893,'<span data-type="title" id=toc-899>١٠٠ </span>- واتخذ الكافرون - مع هذه الدلائل - الملائكة والشياطين شركاء لله، وقد خلقهم فلا يصح مع علمهم ذلك أن يعبدوا غيره، وهو الذى خلق الملائكة والشياطين، فلا ينبغى أن يعبدوهم وهم مخلوقون مثلهم {. . واختلق هؤلاء الكفار لله بنين: فزعم النصارى أن المسيح ابن الله، وزعم مشركو بعض العرب أن الملائكة بنات الله، وذلك جهل منهم. تنزّه الله تعالى عما يفترون فى أوصافه سبحانه}',NULL,'189',NULL,NULL,'0');
INSERT INTO [b836] VALUES (894,'<span data-type="title" id=toc-900>١٠١ </span>- الله الذى أنشأ السموات والأرض على غير مثال سبق: كيف يكون له ولد كما يزعم هؤلاء، مع أنه لم تكن له زوجة، وقد خلق جميع الأشياء وفيها هؤلاء الذين اتخذوهم شركاء، وهو عالم بكل شئ يحصى عليهم ما يقولون وما يفعلون، وهو مجازيهم على قولهم وفعلهم.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b836] VALUES (895,'<span data-type="title" id=toc-901>١٠٢ </span>- ذلك المتصف بصفات الكمال هو الله ربكم، لا إله غيره، خالق كل شئ مما كان وما سيكون، فهو - وحده - المستحق للعبادة، فاعبدوه، وهو - وحده - المتولى كل أمر وكل شئ، فإليه - وحده - المرجع والمآب.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b836] VALUES (896,'<span data-type="title" id=toc-902>١٠٣ </span>- لا تبصر ذاته العيون، وهو يعلم دقائق العيون وغير العيون، وهو اللطيف فلا يغيب عنه شئ، الخبير فلا يخفى عليه شئ.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b836] VALUES (897,'<span data-type="title" id=toc-903>١٠٤ </span>- قل - أيها النبى - للناس: قد جاءكم من خالقكم ومالك أمركم حُجج وبينات فى القرآن، تنير لكم طريق الحق، فمن انتفع بها فانتفاعه لنفسه ومن أعرض عنها جنى على نفسه. لست أنا بمحافظ عليكم، بل أنا رسول أبلغكم ما أرسلت به إليكم.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b836] VALUES (898,'<span data-type="title" id=toc-904>١٠٥ </span>- ومثل هذا التنويع البديع فى عرض الدلائل الكونية نعرض آياتنا فى القرآن منوعة مفصلة، لنقيم الحُجة بها على الجاحدين، فلا يجدوا إلا اختلاق الكذب، فيتهموك بأنك تعلمت من الناس لا من الله ولنبين ما أنزل إليك من الحقائق - من غير تأثر بهوى - لقوم يدركون الحق ويذعنون له.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b836] VALUES (899,'<span data-type="title" id=toc-905>١٠٦ </span>- اتبع - أيها النبى - ما جاءك به الوحى من الله، مالك أمرك ومدبر شئونك، إنه - وحده - الإله المستحق للطاعة والخضوع، فالتزم طاعته، ولا تبال بعناد المشركين.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b836] VALUES (900,'<span data-type="title" id=toc-906>١٠٧ </span>- ولو أراد الله أن يعبدوه وحده لقهرهم على ذلك بقوته وقدرته، ولكنه تركهم لاختيارهم، وما جعلناك رقيباً على أعمالهم، وما أنت بمكلف أن تقوم عنهم بتدبير شئونهم وإصلاح أمرهم.',NULL,'191',NULL,NULL,'0');
INSERT INTO [b836] VALUES (901,'<span data-type="title" id=toc-907>١٠٨ </span>- لا تسبوا - أيها المؤمنون - أصنام المشركين التى يعبدونها من دون الله، فيحملهم الغضب لها على إغاظتكم بسب الله تعدياً وسفهاً. مثل ما زينا لهؤلاء حب أصنامهم يكون لكل أمة عملها حسب استعدادها، ثم يكون مصير الجميع إلى الله - وحده - يوم القيامة، فيخبرهم بأعمالهم ويجازيهم عليها.',NULL,'191',NULL,NULL,'0');
INSERT INTO [b836] VALUES (902,'<span data-type="title" id=toc-908>١٠٩ </span>- وأقسم المشركون بأقصى أيمانهم لئن جاءتهم آية مادية من الآيات التى اقترحوها ليكونن ذلك سبباً فى إيمانهم، قل - يا أيها النبى -: إن هذه الآيات من عند الله، فهو - وحده - القادر عليها، وليس لى يد فيها، إنكم - أيها المؤمنون - لا تدرون ما سبق به علمى من أنهم إذا جاءتهم هذه الآيات لايؤمنون.',NULL,'191',NULL,NULL,'0');
INSERT INTO [b836] VALUES (903,'<span data-type="title" id=toc-909>١١٠ </span>- وإنكم لا تدرون أيضاً أننا نقلب قلوبهم عند مجئ الآيات بالخواطر والتأويلات، ونقلب أبصارهم بتوهم التخيلات، فيكونون بعد الآيات كحالهم قبلها، وندعهم فى ظلمهم وعنادهم يتخبطون.',NULL,'191',NULL,NULL,'0');
INSERT INTO [b836] VALUES (904,'<span data-type="title" id=toc-910>١١١ </span>- إن أولئك الذين أقسموا إذا جاءتهم آية ليؤمنن بها كاذبون، والله أعلم بإيمانهم، ولو أننا نزّلنا الملائكة يرونهم رأى العين، وكلَّمهم الموتى بعد إحيائهم وإخراجهم من قبورهم، وجمعنا لهم كل شئ مقابلا لهم مواجهاً يبين لهم الحق، ما كانوا ليؤمنوا إلا أن يشاء الله تعالى أن يؤمنوا، والأكثرون لا يدركون الحق ولا يذعنون له، لما أصاب قلوبهم من عمياء الجاهلية.',NULL,'191',NULL,NULL,'0');
INSERT INTO [b836] VALUES (905,'<span data-type="title" id=toc-911>١١٢ </span>- وكما أن هؤلاء عادوك وعاندوك وأنت تريد هدايتهم جعلنا لكل نبى يبلِّغ عنا أعداء من عتاة الإنس وعتاة الجن الذين يخفون عنك ولا تراهم، يوسوس بعضهم لبعض بكلام مزخرف مُمَوَّه لا حقيقة له، فيلقون بذلك فيهم الغرور بالباطل، وذلك كله بتقدير الله ومشيئته، ولو شاء ما فعلوه، ولكنه لتمحيص قلوب المؤمنين. فاترك الضالين وكفرهم بأقوالهم التى يقترفونها.',NULL,'192',NULL,NULL,'0');
INSERT INTO [b836] VALUES (906,'<span data-type="title" id=toc-912>١١٣ </span>- وإنّهم يموّهون القول الباطل ليغروا أنفسهم ويرضوها، ولتميل إليه قلوب من على شاكلة أولئك العتاة الذين لا يذعنون للآخرة، ويعتقدون أن الحياة هى الدنيا، وليقعوا بسبب عدم اعتقادهم باليوم الآخر فيما يقترفون من آثام وفجور.',NULL,'192',NULL,NULL,'0');
INSERT INTO [b836] VALUES (907,'<span data-type="title" id=toc-913>١١٤ </span>- قل لهم - أيها النبى - هذا حكم الله بالحق بينتهُ الآيات الساطعة، فلا يسوغ أن أطلب حَكَماً غيره يفصل بينى وبينكم، وقد حكم سبحانه فأنزل الكتاب الكريم حُجة لى عليكم، وقد عجزتم أن تأتوا بمثله، وهو مبين للحق وللعدل، وإن الذين أوتوا الكتاب يعلمون أنه منزل من عند الله مشتملا على الحق، كما بشرت كتبهم. وإن حاولوا إخفاء ذلك وكتمانه، فلا تكونن - يا أيها النبى - أنت ومن اتبعك من الذين يشكّون فى الحق بعد بيانه.',NULL,'192',NULL,NULL,'0');
INSERT INTO [b836] VALUES (908,'<span data-type="title" id=toc-914>١١٥ </span>- وإنَّ حُكْم الله قد صدَر، فتمت كلمات ربك الصادقة العادلة، بإنزال الكتاب الكريم مشتملا على الصدق، وفيه الميزان الصادق بين الحق والباطل، ولا يوجد من يغير كلمات الله وكتابه، وهو سبحانه سميع لكل ما يقال، عليم بكل ما يقع منهم.',NULL,'192',NULL,NULL,'0');
INSERT INTO [b836] VALUES (909,'<span data-type="title" id=toc-915>١١٦ </span>- وإذا كان سبحانه هو الحكم العدل الذى يُرْجَعُ إلى كتبه فى طلب الحق ومعرفته، فلا تتبع - أيها النبى - أنت ومن معك أحداً يخالف قوله الحق، ولو كانوا عدداً كثيراً. فإنك إن تتبع أكثر الناس الذين لا يعتمدون على شرع مُنزل يبعدوك عن طريق الحق المستقيم وهو طريق الله تعالى، لأنهم لا يسيرون إلا وراء الظنون والأوهام، وإن هم إلا يقولون عن تخمين لا يُبنى على برهان.',NULL,'192',NULL,NULL,'0');
INSERT INTO [b836] VALUES (910,'<span data-type="title" id=toc-916>١١٧ </span>- وإن ربك هو العليم علماً ليس مثله علم بالذين بعدوا عن طريق الحق، والذين اهتدوا إليه وصارت الهداية وصفاً لهم.',NULL,'192',NULL,NULL,'0');
INSERT INTO [b836] VALUES (911,'<span data-type="title" id=toc-917>١١٨ </span>- وإذا كان الله تعالى هو الذى يعلم المهتدين والضالين، فلا تلتفتوا إلى ضلال المشركين فيتحريم بعض الأنعام، وكلوا منها، فقد رزقكم الله تعالى إياها، وجعلها حلالا وطيبة لا ضرر فى أكلها، واذكروا اسم الله تعالى عليها عند ذبحها، ما دمتم مؤمنين به، مذعنين لأدلته.',NULL,'193',NULL,NULL,'0');
INSERT INTO [b836] VALUES (912,'<span data-type="title" id=toc-918>١١٩ </span>- وإنه لا يوجد أى مبرر أو دليل يمنعكم أن تأكلوا مما يذكر اسم الله تعالى عليه عند ذبحه من الأنعام، وقد بين سبحانه وتعالى المحرَّم فى غير حال الاضطرار، كالميتة والدم. وإن الكثيرين من الناس يبعدون عن الحق بمحض أهوائهم، من غير علم أوتوه، أو برهان قام عندهم، كأولئك العرب الذين حرَّموا بعض النعم عليهم. ولستم معتدين فى أكلكم ما وَلدَ، بل هم المعتدون بتحريم الحلال، والله - وحده - هو العليم علماً ليس مثله علم بالمعتدين حقاً.',NULL,'193',NULL,NULL,'0');
INSERT INTO [b836] VALUES (913,'<span data-type="title" id=toc-919>١٢٠ </span>- ليست التقوى فى تحريم ما أحل الله، إنما التقوى فى ترك الإثم ظاهره وباطنه، فاتركوا الآثام فى أعمالكم ظاهرها وخفيها، وإن الذين يكسبون الإثم سيجزون مقدار ما اقترفوا من سيئات.',NULL,'193',NULL,NULL,'0');
INSERT INTO [b836] VALUES (914,'<span data-type="title" id=toc-920>١٢١ </span>- وإذا كانت الأنعام حلالا لكم بذبحها، فلا تأكلوا مما لم يذكر اسم الله تعالى عليه عند ذبحه إذا تركت فيه التسمية عمداً، أو ذكر فيه اسم غير الله تعالى، فإن هذا فسق وخروج عن حكم الله. . وإن العتاة المفسدين من إبليس وأعوانه ليوسوسون فى صدور من استولوا عليهم، ليجادلوكم بالباطل. وليجروكم إلى تحريم ما أحل الله، وإن اتبعتموهم فإنكم مثلهم فى الإشراك بالله.',NULL,'193',NULL,NULL,'0');
INSERT INTO [b836] VALUES (915,'<span data-type="title" id=toc-921>١٢٢ </span>- وإنكم بإيمانكم لستم مثل المشركين فى شئ، فليس حال مَنْ كان كالميت فى ضلاله فأنار الله بصيرته بالهداية التى هى كالحياة، وجعل له نور الإيمان والحُجج والبيات، يهتدى به ويمشى على ضوئه، كحال الذى يعيش فى الظلام المتكاثف. وكما زين الله الإيمان فى قلوب أهل الإيمان، زين الشيطان الشرك فى نفوس الظالمين الجاحدين.',NULL,'193',NULL,NULL,'0');
INSERT INTO [b836] VALUES (916,'<span data-type="title" id=toc-922>١٢٣ </span>- لا تعجب - أيها النبى - إذا رأيت أكابر المجرمين فى مكة يدبرون الشر ويتفننون فيه! . فكذلك الشأن فى كل مدينة كبيرة يدبر الشر فيها الأكابر من المجرمين، وعاقبته عليهم، وهم لا يشعرون ولا يحسون بذلك.',NULL,'194',NULL,NULL,'0');
INSERT INTO [b836] VALUES (917,'<span data-type="title" id=toc-923>١٢٤ </span>- وإن هؤلاء الكبار من المجرمين يحسدون الناس على ما آتاهم الله من علم ونبوة وهداية، فإذا جاءتهم حُجة قاطعة لا يذعنون لها، ولكن يقولون: لن نذعن للحق حتى ينزل علينا الوحى كما ينزل على الرسل، والله - وحده - هو الذى يصطفى لرسالته من يشاء من خلقه، وإن هؤلاء المعاندين إذا كانوا يطلبون الرياسة بهذا العناد، فسينالهم الصَغار والذل فى الدنيا بسببه، وسينالهم العذاب الشديد فى الآخرة بسبب تدبيرهم السيئ.',NULL,'194',NULL,NULL,'0');
INSERT INTO [b836] VALUES (918,'<span data-type="title" id=toc-924>١٢٥ </span>- إذا كان أولئك قد ضلوا واهتديتم، فبإرادة الله تعالى وقضائه، فمن يُكْتَب له الهداية يتسع صدره لنور الإسلام، ومن يكتب عليه الضلال يكن صدرُه ضيقاً شديد الضيق، كأنه من الضيق كمن يصعد إلى مكان مرتفع بعيد الارتفاع كالسماء، فتتصاعد أنفاسه ولا يستطيع شيئاً، وبهذا يكتب الله الفساد والخذلان على الذين ليس من شأنهم الإيمان.',NULL,'194',NULL,NULL,'0');
INSERT INTO [b836] VALUES (919,'<span data-type="title" id=toc-925>١٢٦ </span>- وهذا الذى بيَّناه هو طريق الحق المستقيم، قد فصَّلناه ووضَّحناه للناس، ولا ينتفع به إلا الذين من شأنهم التذكر وطلب الهداية.',NULL,'194',NULL,NULL,'0');
INSERT INTO [b836] VALUES (920,'<span data-type="title" id=toc-926>١٢٧ </span>- ولهؤلاء المتذكرين المؤمنين دار الأمن، وهى الجنة، وهم فى ولاية الله ومحبته ونصرته، بسبب ما عملوا فى الدنيا من خير.',NULL,'194',NULL,NULL,'0');
INSERT INTO [b836] VALUES (921,'<span data-type="title" id=toc-927>١٢٨ </span>- وإذا كان الذين سلكوا صراط الله المستقيم لهم الأمْن وولاية الله، فالذين سلكوا طريق الشيطان لهم جزاء ما ارتكبوا، حين يحشر الجميع يوم القيامة، ويقول - جل جلاله - للآثمين من الجن والإنس: أيها المجتمعون من الجن قد أكثرتم من إغواء الإنس حتى تبعكم منهم عدد كثير! . فيقول الذين اتبعوهم من الإنس: يا خالقنا والقائم علينا، قد انتفع بعضنا ببعض، واستمتعنا بالشهوات، وبلغنا أجلنا الذى حددته لنا. فيقول - جل جلاله -: مقركم النار خالدين فيها إلا مَنْ شاء الله أن ينقذهم ممن لم ينكروا رسالة الله. وإن أفعال الله دائماً على مقتضى الحكمة والعلم.',NULL,'195',NULL,NULL,'0');
INSERT INTO [b836] VALUES (922,'<span data-type="title" id=toc-928>١٢٩ </span>- وكما متَّعنا عصاة الإنس والجن بعضهم ببعض، فجعل بعض الظالمين أولياء لبعض بسبب ما يكتسبون من كبائر.',NULL,'195',NULL,NULL,'0');
INSERT INTO [b836] VALUES (923,'<span data-type="title" id=toc-929>١٣٠ </span>- والله تعالى يقول لهم يوم القيامة: يا أيها الإنس والجن، لقد جاءتكم الرسل يذكرون لكم الحجج والبينات، ويتلون عليكم الآيات، ويحذرونكم لقاء الله فى يومكم هذا، فكيف تكذبون؟ فأجابوا: قد أقررنا على أنفسنا بما ارتكبنا، وقد خدعتهم الحياة الدنيا بمتعها، وأقروا على أنفسهم أنهم كانوا جاحدين.',NULL,'195',NULL,NULL,'0');
INSERT INTO [b836] VALUES (924,'<span data-type="title" id=toc-930>١٣١ </span>- وإن إرسال الرسل منذرين مبينين إنما كان لأن ربك - أيها النبى - لا يهلك القرى بظلمهم وأهلها غافلون عن الحق، بل لا بد أن يبيّن لهم وينذرهم.',NULL,'195',NULL,NULL,'0');
INSERT INTO [b836] VALUES (925,'<span data-type="title" id=toc-931>١٣٢ </span>- ولكل عامل خير أو عامل شر درجاته من جزاء ما يعمله، إن خيراً فخير، وإن شراً فشر، والله سبحانه وهو الخالق البارئ غير غافل عما يعملون، بل إن عملهم فى كتاب لا يغادر صغيرة ولا كبيرة إلا أحصاها.',NULL,'195',NULL,NULL,'0');
INSERT INTO [b836] VALUES (926,'<span data-type="title" id=toc-932>١٣٣ </span>- والله ربك هو الغنى عن العباد والعبادة، وهو - وحده - صاحب الرحمة الشاملة، وبمقتضاها أمركم بالخير ونهاكم عن الشر، وهو القادر إن يشأ يذهبكم ويجعل فى الأرض خلفاء من بعدكم على حسب مشيئته، وليس ذلك يصعب عليه سبحانه، فقد خلقكم من ذرية آخرين سبقوكم، وكنتم وارثين الأرض من بعدهم.',NULL,'196',NULL,NULL,'0');
INSERT INTO [b836] VALUES (927,'<span data-type="title" id=toc-933>١٣٤ </span>- وإن الذى ينذركم به من عقاب، ويبشركم به من ثواب بعد البعث والجمع والحساب آتٍ لا محالة، وما أنتم بمعجزين من يطلبكم يومئذ، فلا قدرة لكم على الامتناع عن الجمع والحساب.',NULL,'196',NULL,NULL,'0');
INSERT INTO [b836] VALUES (928,'<span data-type="title" id=toc-934>١٣٥ </span>- قل - أيها النبى - لهم مهدِّدا: اعملوا على النحو الذى اخترتموه بكل ما فى قدرتكم، وإنى عامل فى ناحية الحق، وستعلمون حتماً من تكون له العاقبة الحسنة فى الدار الآخرة، وهى لأهل الحق لا محالة، لأنكم ظالمون والله تعالى لم يكتب الفوز للظالمين.',NULL,'196',NULL,NULL,'0');
INSERT INTO [b836] VALUES (929,'<span data-type="title" id=toc-935>١٣٦ </span>- المشركون الذين يعبدون الأوثان فى أوهام مستمرة، فهم يجعلون مما خلق الله تعالى وأنشأه من الزرع ومن الإبل والبقر والغنم، جزءاً لله تعالى ينفقونه على الضيفان والمحتاجين، وجزءاً آخر ينفقونه على خدمة الأوثان التى جعلوها شركاء لله تعالى بزعمهم، فما يجعلونه للأوثان يصل إلى أوثانهم فينفقونه عليها، وما يجعلونه لله بزعمهم لا يصل شى منه إلى الضيفان والفقراء، وما أسوأ حكمهم الظالم، لأنهم جعلوا الأوثان نظراء لخالق الحرث والنسل، ولأنهم لا ينفقون ما جعلوه لله فى مصارفه.',NULL,'196',NULL,NULL,'0');
INSERT INTO [b836] VALUES (930,'<span data-type="title" id=toc-936>١٣٧ </span>- وكما زيَّنَتْ لهم أوهامهم تلك القسمة الظالمة لما خلق الله من حرث وإبل وبقر وغنم، قد زيَّنَتْ لهم أوهامهم فى الأوثان التى زعموها شركاء لله قتْل أولادهم عند الولادة، وأن يُنذروا لآلهتهم ذبح أولادهم، وإن تلك الأوهام تُرديهم وتخلط عليهم أمر الدين، فلا يدركونه على وجهه، وإذا كانت الأوهام لها ذلك السلطان على عقولهم، فاتركهم وما يفترونه على الله تعالى وعليك وسينالون عقاب ما يفترون، وتلك مشيئة الله، فلو شاء ما فعلوا.',NULL,'196',NULL,NULL,'0');
INSERT INTO [b836] VALUES (931,'<span data-type="title" id=toc-937>١٣٨ </span>- ومن أوهامهم أنهم يقولون: هذه إبل وبقر وغنم وزرع ممنوعة، لا يأكلها أحد إلا من يشاءون من خَدَمَة الأوثان، وذلك من زعمهم الباطل، لا من عند الله. وقالوا أيضاً: هذه إبل حُرِّمت ظهورها فلا يركبها أحد، وهم مع ذلك لا يذكرون اسم الله تعالى عند ذبح ما يذبحون من إبل وبقر وغنم، وذلك لكذبهم على الله تعالى بشركهم، والله تعالى سيجزيهم بالعذاب فى الآخرة، بسبب افترائهم وتحريمهم ما يُحرِّمون من غير تحريم الله تعالى.',NULL,'197',NULL,NULL,'0');
INSERT INTO [b836] VALUES (932,'<span data-type="title" id=toc-938>١٣٩ </span>- ومن أوهام هؤلاء المشركين أنهم يقولون: ما فى بطون الأنعام التى جعلوها ممنوعة لا تذبح ولا تركب، ما فى بطونها من أجِنَّة خالص للذكور من الرجال، ويُحْرَم منه النساء، ومع ذلك إذا نزل ميتاً فهم شركاء فيه، يأكلون منه، سيجزيهم الله تعالى على كذبهم الذى وصفوا به فعلهم، إذ ادَّعَوْا أن هذا التحريم من عند الله تعالى، وإن الله عليم بكل شئ، حكيمٌ، كل أفعاله على مقتضى الحكمة وهو يجزى الآثمين بإثمهم.',NULL,'197',NULL,NULL,'0');
INSERT INTO [b836] VALUES (933,'<span data-type="title" id=toc-939>١٤٠ </span>- وقد خسر أولئك الذين قتلوا أولادهم حمقا ووهما، غير عالمين مغبّة عملهم ودَاعِيه، وحرَّموا على أنفسهم ما رزقهم الله من زرع وحيوان، مفترين على الله بادعاء أنه هو الذى حرم، وقد بعدوا عن الحق بسبب ذلك، وما كانوا بسبب هذا الافتراء ممن يتصفون بالهداية.',NULL,'197',NULL,NULL,'0');
INSERT INTO [b836] VALUES (934,'<span data-type="title" id=toc-940>١٤١ </span>- الله - وحده - هو الذى خلق حدائق من الكرم، منها ما يغرس ويرفع على دعائم، ومنها ما لا يقوم على دعائم وخلق النخل والزرع الذى يخرج ثمراً مختلفاً فى اللون والطعم والشكل والرائحة وغير ذلك، وخلق الزيتون والرمان متشابهاً فى بعض الصفات وغير متشابه فى بعضها الآخر، مع أن التربة قد تكون واحدة وتسقى جميعها بماء واحد. فكلوا من ثمرها إذا طاب لكم، وأخرجوا منها الصدقة عند نضجها وجمعها، ولا تسرفوا فى الأكل فتضروا أنفسكم وتضروا الفقراء فى حقهم، إن الله لا يرضى عن المسرفين فى تصرفاتهم وأعمالهم.',NULL,'197',NULL,NULL,'0');
INSERT INTO [b836] VALUES (935,'<span data-type="title" id=toc-941>١٤٢ </span>- وخلق الله من الأنعام - وهى الإبل والبقر والماعز - ما يحمل أثقالكم، وما تتخذون من أصوافها وأوبارها وأشعارها فراشا، وهى رزق الله لكم، فكلوا ما أحل الله منها ولا تتبعوا الشيطان وأولياءه فى افتراء التحليل والتحريم، كما كان يفعل أهل الجاهلية. إن الشيطان لا يريد لكم الخير، لأنه عدو ظاهر العداوة.',NULL,'198',NULL,NULL,'0');
INSERT INTO [b836] VALUES (936,'<span data-type="title" id=toc-942>١٤٣ </span>- خلق الله من كل نوع من الأنعام ذكرا وأنثى، فهى ثمانية أزواج خلق من الضأن زوجين، ومن الماعز زوجين، وقل يا محمد للمشركين منكراً عليهم تحريم ما حرموا من هذا: ما علة تحريم هذه الأزواج كما تزعمون؟ أهى كونها ذكوراً؟ ليس كذلك، لأنكم تحلون الذكور أحياناً. أم هى كونها إناثاً؟ ليس كذلك، لأنكم تحلون الإناث أحياناً، أم هى اشتمال الأرحام عليها. ليس كذلك لانكم لا تحرمون الأجنة على الدوام. أخبرونى بمستند صحيح يعتمد عليه، إن كنتم صادقين فيما تزعمون من التحليل والتحريم.',NULL,'198',NULL,NULL,'0');
INSERT INTO [b836] VALUES (937,'<span data-type="title" id=toc-943>١٤٤ </span>- وخلق الله من الإبل زوجين، ومن البقر زوجين. قل لهم يا محمد منكراً عليهم: ما علة التحريم لما حرمتم من هذه الأزواج كما تزعمون؟ أهى كونها ذكوراً؟ ليس كذلك، لأنكم تحلون الذكور أحياناً، أهى كونها إناثاً؟ ليس كذلك لأنكم تحلون الإناث أحياناً، أم هى اشتمال الأرحام عليها؟ ليس كذلك لأنكم لا تحرمون الأجنة على الدوام، وتزعمون أن هذا التحريم من عند الله! أكنتم حاضرين حين وجه إليكم الله هذا التحريم فسمعتم نهيه؟ لم يكن ذلك قطعاً. انتهوا عما أنتم فيه، فهو ظلم، وليس هناك أظلم ممن كذب على الله فنسب إليه ما لم يصدر عنه، ولا سند له من علم يعتمد عليه، وإنما يريد بذلك إضلال الناس. إن الله لا يوفق الظالمين إذا اختاروا طريق الباطل.',NULL,'198',NULL,NULL,'0');
INSERT INTO [b836] VALUES (938,'<span data-type="title" id=toc-944>١٤٥ </span>- قل - أيها النبى -: لا أجد الآن فى مصدر التحليل والتحريم الذى أوحى به إلىَّ طعاماً محرماً على آكل يأكله، إلا أن يكون هذا الشئ ميتة لم تذَكَّ ذكاة شرعية، أو دماً سائلا، أو لحم خنزير، فإن ذلك المذكور ضارٌّ خبيث لا يجوز أكله أو أن يكون هذا الشئ المحرم فيه خروج من العقيدة الصحيحة، بأن ذكر عند ذبحه اسم غير الله، كصنم معبود آخر. على أن من دعته الضرورة إلى أكل شئ من هذه المحرمات غير طالب اللذة بالأكل، وغير متجاوز قدر الضرورة، فلا حرج عليه لأن ربك غفور رحيم.',NULL,'199',NULL,NULL,'0');
INSERT INTO [b836] VALUES (939,'<span data-type="title" id=toc-945>١٤٦ </span>- فهذا ما حرمناه عليكم. ولقد حرمنا على اليهود أكل اللحم والشحم وغيرهما من كل ما له ظفر من الحيوانات كالإبل والسباع، وحرمنا عليهم من البقر والغنم شحومهما فقط، إلا الشحوم التى حملتها ظهورهما، أو التى توجد على الأمعاء، أو التى اختلطت بعظم. وهذا التحريم عقاب لهم على ظلمهم، وفَطْمٌ لنفوسهم من اندفاعها فى الشهوات، وإنا لصادقون فى جميع أخبارنا التى منها هذا الخبر.',NULL,'199',NULL,NULL,'0');
INSERT INTO [b836] VALUES (940,'<span data-type="title" id=toc-946>١٤٧ </span>- فإن كذبك المكذبون فيما أوحيت به إليك، فقل لهم محذِّراً: إن ربكم الذى يجب أن تؤمنوا به - وحده وتلتزموا أحكامه - ذو رحمة واسعة لمن أطاعه، ولمن عصاه أيضاً، حيث لم يعجل بعقوبتهم، ولكن لا ينبغى أن يغتروا بسعة رحمته، فإن عذابه لا بد واقع بالمجرمين.',NULL,'199',NULL,NULL,'0');
INSERT INTO [b836] VALUES (941,'<span data-type="title" id=toc-947>١٤٨ </span>- سيقول المشركون اعتذاراً عن شركهم، وتحريم ما أحل الله من المطاعم، وتكذيباً لما أبلغتهم من مقت الله لما هم عليه: إن الإشراك منا وتحريم الحلال كانا بمشيئة الله ورضاه، ولو شاء عدم ذلك وكره منا ما نحن عليه، ما أشركنا نحن ولا أسلافنا، ولا حرمنا شيئاً مما أحله لنا. وقد كذَّب الذين من قبلهم رسلهم، كما كذَّبك هؤلاء واستمروا فى تكذيبهم حتى نزل بهم عذابنا! قل لهؤلاء المكذبين، هل عندكم من مستند صحيح على أن الله رضى لكم الشرك والتحليل، فتظهروه لنا؟ ما تتبعون فيما تقولون إلا الظن الذى لا يغنى من الحق شيئاً، وما أنتم إلا كاذبون فيما تزعمون.',NULL,'199',NULL,NULL,'0');
INSERT INTO [b836] VALUES (942,'<span data-type="title" id=toc-948>١٤٩ </span>- قل - يا أيها النبى -: لله الحُجة الواضحة فى كذبكم وادعائكم أن الله رضى بعملكم، ولا حُجة لكم فيما تزعمون من الشرك والتحليل والتحريم وغيرها، فلو شاء الله أن يوفقكم إلى الهداية لهداكم أجمعين إلى طريق الحق، ولكنه لم يشأ ذلك لاختياركم سبيل الضلال.',NULL,'199',NULL,NULL,'0');
INSERT INTO [b836] VALUES (943,'<span data-type="title" id=toc-949>١٥٠ </span>- قل لهم - يا أيها النبى -: هاتوا أنصاركم الذين يشهدون لكم أن الله حرَّم هذا الذى زعمتم أنه حرام، فإن حضروا، وشهدوا، فلا تصدقهم لأنهم كاذبون. ولا تتبع أهواء هؤلاء الذين كذبوا بالأدلة الكونية والقرآن المتلو، الذين لا يؤمنون بالآخرة - وهم مشركون بالله - يساوون به غيره من المعبودات الباطلة.',NULL,'200',NULL,NULL,'0');
INSERT INTO [b836] VALUES (944,'<span data-type="title" id=toc-950>١٥١ </span>- قل لهم - يا أيها النبى - تعالوا أبيِّن لكم المحرمات التى ينبغى أن تهتموا بها وتبتعدوا عنها: لا تجعلوا لله شريكاً ما، بأى نوع كان من أنواع الشرك، ولا تسيئوا إلى الوالدين، بل أحسنوا إليهما إحساناً بالغاً، ولا تقتلوا أولادكم بسبب فقر نزل بكم، أو تخشون نزوله فى المستقبل، فلستم أنتم الرازقين، بل نحن الذين نرزقكم ونرزقهم، ولا تقربوا الزنى فهو من الأمور المتناهية فى القبح، سواء منها ما ظهر للناس حين إتْيانه، وما لم يطلع عليه إلا الله، ولا تقتلوا النفس التى حرم الله قتلها لعدم موجبه، إلا إذا كان القتل بحق تنفيذاً لحكم القضاء. أمركم الله أمراً مؤكداً باجتناب هذه المنهيّات التى تقضى بديهة العقل بالبعد عنها، لتعقلوا ذلك.',NULL,'200',NULL,NULL,'0');
INSERT INTO [b836] VALUES (945,'<span data-type="title" id=toc-951>١٥٢ </span>- ولا تتصرفوا فى مال اليتيم إلا بأحسن تصرف يحفظه وينمّيه، واسْتَمِروا على ذلك حتى يصل اليتيم إلى حالة من الرشد يستطيع معها أن يستقل بالتصرف السليم، وحينئذ ادفعوا إليه ماله. ولا تمسوا الكيل والميزان بالنقص إذا أعطيتم، أو بالزيادة إذا أخذتم، بل أوفوها بالعدل ما وسعكم ذلك، فالله لا يكلف نفساً إلا ما تستطيعه دون حرج. وإذا قلتم قولا فى حكم أو شهادة أو خبر أو نحو ذلك، فلا تميلوا عن العدل والصدق، بل تحروا ذلك دون مراعاة لصلة من صلات الجنس أو اللون أو القرابة أو المصاهرة، ولا تنقضوا عهد الله الذى أخذه عليكم بالتكاليف، ولا العهود التى تأخذونها بينكم، فيما يتعلق بالمصالح المشروعة، بل أوفوا بهذه العهود. أمركم الله أمراً مؤكداً باجتناب هذه المنهيات، لتتذكروا أن التشريع لمصلحتكم.',NULL,'200',NULL,NULL,'0');
INSERT INTO [b836] VALUES (946,'<span data-type="title" id=toc-952>١٥٣ </span>- ولا تحيدوا عن النهج الذى رسمتْه لكم، لأنه هو الطريق المستقيم الموصل إلى سعادة الدارين، بل اتبعوه، ولا تتبعوا الطرق الباطلة التى نهاكم الله عنها حتى لا تتفرَّقوا شيعاً وأحزاباً، وتبعدوا عن صراط الله السوى. أمركم الله أمراً مؤكداً بذلك لتتجنبوا مخالفته.',NULL,'201',NULL,NULL,'0');
INSERT INTO [b836] VALUES (947,'<span data-type="title" id=toc-953>١٥٤ </span>- وقد أنزلنا التوراة على موسى إتماماً للنعمة على من أحسن القيام بأمر الدين، وأنزلناها تفصيلا لكل شئ من التعاليم المناسبة لهم، وهُدىً إلى الطريق السوى، ورحمة لهم باتّباعه، وذلك ليؤمن بنو إسرائيل بلقاء ربهم يوم القيامة ومحاسبتهم على هذه التكاليف.',NULL,'201',NULL,NULL,'0');
INSERT INTO [b836] VALUES (948,'<span data-type="title" id=toc-954>١٥٥ </span>- وهذا القرآن كتاب أنزلناه مبارك، مشتمل على الخير الإلهى والمنافع الدينية والدنيوية، فاتبعوه واتقوا مخالفته ليرحمكم ربكم.',NULL,'201',NULL,NULL,'0');
INSERT INTO [b836] VALUES (949,'<span data-type="title" id=toc-955>١٥٦ </span>- أنزلناه حتى لا تعتذروا عن عصيانكم وتقولوا: إن الوحى لم ينزل إلا على طائفتين من قبلنا، هم أهل التوراة وأهل الإنجيل، ولا علم لنا مطلقاً بتلاوة كتبهم وفهم ما فيها من إرشاد.',NULL,'201',NULL,NULL,'0');
INSERT INTO [b836] VALUES (950,'<span data-type="title" id=toc-956>١٥٧ </span>- وأنزلناه حتى لا تقولوا أيضاً: لو أنا أُنزل علينا الوحى الذى نزل عليهم لكنا أكثر منهم هداية وأحسن حالا، لسعة عقولنا وطيب استعدادنا. لا حُجة لكم بعد اليوم على عصيانكم، ولا محل لقولكم هذا، فقد جاءكم القرآن من ربكم علامة واضحة على صدق محمد، ومبيناً لكم جميع ما تحتاجون إليه فى دينكم ودنياكم، وهادياً إلى الطريق السوى، ورحمة لكم باتباعه. ولا يَكُنْ أحد أظلم ممن كذب بآيات الله التى أنزلها فى كتبه، وآياته التى خلقها فى الكون، وأعرض عنها فلم يؤمن ولم يعمل بها، وسنعاقب الذين يعرضون عن آياتنا، ولا يتدبرون ما فيها بالعذاب البالغ غايته فى الإيلام، بسبب إعراضهم وعدم تدبرهم.',NULL,'201',NULL,NULL,'0');
INSERT INTO [b836] VALUES (951,'<span data-type="title" id=toc-957>١٥٨ </span>- لقد قامت الحُجة على وجوب الإيمان، ولم يؤمن هؤلاء، فماذا ينتظرون لكى يؤمنوا؟ هل ينتظرون أن تأتيهم الملائكة رسلاً بدل البشرَ، أو شاهدين على صدقك؟ أو أن يأتيهم ربك ليروه، أو يشهد بصدقك؟ أو أن تأتيهم بعض علامات ربك لتشهد على صدقك؟! وعندما تأتى علامات ربك مما يلجئهم إلى الإيمان لا ينفعهم إيمانهم، لأنه إيمان اضطرار، ولا ينفع العاصى أن يتوب ويطيع الآن، فقد انتهت مرحلة التكليف، قل لهؤلاء المعرضين المكذبين: انتظروا أحد هذه الأمور الثلاثة، واستمروا على تكذيبكم، إنا منتظرون حكم الله فيكم.',NULL,'202',NULL,NULL,'0');
INSERT INTO [b836] VALUES (952,'<span data-type="title" id=toc-958>١٥٩ </span>- إن الذين فرَّقوا الدين الحق الواحد بالعقائد الزائفة والتشريعات الباطلة، وصاروا بسبب ذلك أحزاباً، تحسبهم جميعاً وقلوبهم مختلفة، لست مؤاخذاً بتفرقهم وعصيانهم ولا تملك هدايتهم، فما عليك إلا البلاغ، والله - وحده - هو الذى يملك أمرهم بالهداية والجزاء، ثم يخبرهم يوم القيامة بما كانوا يفعلونه فى الدنيا ويجازيهم عليه.',NULL,'202',NULL,NULL,'0');
INSERT INTO [b836] VALUES (953,'<span data-type="title" id=toc-959>١٦٠ </span>- مَن عمل صالحاً يضاعف له ثوابه إلى عشرة أمثاله فضلا وكرماً، ومَن عمل عملا سيئاً لا يعاقب إلا بمقدار عصيانه، عدلا منه تعالى، وليس هناك ظلم بنقص ثواب أو زيادة عقاب.',NULL,'202',NULL,NULL,'0');
INSERT INTO [b836] VALUES (954,'<span data-type="title" id=toc-960>١٦١ </span>- قل - يا أيها النبى - مبيناً ما أنت عليه من الدين الحق: إن ربى أرشدنى ووفقنى إلى طريق مستقيم، بلغ نهاية الكمال فى الاستقامة، وكان هو الدين الذى اتبعه إبراهيم مائلاً به عن العقائد الباطلة، وما كان إبراهيم يعبد مع الله إلهاً آخر كما يزعم المشركون.',NULL,'202',NULL,NULL,'0');
INSERT INTO [b836] VALUES (955,'<span data-type="title" id=toc-961>١٦٢ </span>- قل إن صلاتى وجميع عباداتى، وما آتيه فى حال حياتى من الطاعة، وما أموت عليه من الإيمان والعمل الصالح، كله خالص لوجه الله الذى خلق جميع الموجودات، فاستحق أن يعبد - وحده - وأن يطاع وحده.',NULL,'202',NULL,NULL,'0');
INSERT INTO [b836] VALUES (956,'<span data-type="title" id=toc-962>١٦٣ </span>- ولا شريك له فى الخلق، ولا فى استحقاق العبادة، وقد أمرنى ربى بذلك الإخلاص فى التوحيد والعمل، وأنا أول المذعنين الممتثلين، وأكملهم إذعاناً وتسليماً.',NULL,'203',NULL,NULL,'0');
INSERT INTO [b836] VALUES (957,'<span data-type="title" id=toc-963>١٦٤ </span>- قل يا محمد - منكراً - على المشركين دعوتهم إياك لموافقتهم على شركهم: أأطلب للعبادة رباً غير الله، مع أنه خالق كل شئ؟ وقل لهم - منكراً عليهم - إنهم لا يحملون عنك خطاياك إذا وافقتهم. لا تعمل أى نفس عملا إلا وقع جزاؤه عليها - وحدها - ولا تؤاخذ نفس بحمل ذنب نفس أخرى، ثم تبعثون بعد الموت إلى ربكم، فيخبركم بما كنتم تختلفون فيه فى الدنيا من العقائد، ويجازيكم عليه، فكيف أعصى الله اعتماداً على وعودكم الكاذبة؟ .',NULL,'203',NULL,NULL,'0');
INSERT INTO [b836] VALUES (958,'<span data-type="title" id=toc-964>١٦٥ </span>- وهو الذى جعلكم خلفاء للأمم السابقة فى عمارة الكون، ورفع بعضكم فوق بعض درجات فى الكمال المادى والمعنوى لأخذكم فى أسبابه، ليختبركم فيما أعطاكم من النعم كيف تشكرونها؟ وفيما آتاكم من الشرائع كيف تعملون بها؟ إن ربك سريع العقاب للمخالفين، لأن عقابه آت لا ريب فيه وكل آت قريب، وإنه لعظيم المغفرة لمخالفات التائبين المحسنين، واسع الرحمة بهم.',NULL,'203',NULL,NULL,'0');
INSERT INTO [b836] VALUES (959,'<span data-type="title" id=toc-966>١ </span>- المص، هذه الحروف الصوتية تذكر فى أوائل بعض السور المكية، لتنبيه المشركين إلى أن القرآن الكريم مكون من الحروف التى ينطقون بها، ومع ذلك يعجزون عن الإتيان بمثله، كما أن فى هذه الحروف إذا تليت حملا لهم على السماع إذا تواصوا بألا يسمعوا القرآن.',NULL,'204',NULL,NULL,'0');
INSERT INTO [b836] VALUES (960,'<span data-type="title" id=toc-967>٢ </span>- أنزل إليك القرآن لتنذر به المكذبين ليؤمنوا، وتذكِّر به المؤمنين ليزدادوا إيماناً، فلا يكن فى صدرك ضيق عند تبليغه خوفاً من التكذيب.',NULL,'204',NULL,NULL,'0');
INSERT INTO [b836] VALUES (961,'<span data-type="title" id=toc-968>٣ </span>- اتبعوا ما أوحاه إليكم ربكم ولا تتبعوا من دونه أولياء تستجيبون لهم وتستعينون بهم. إنكم قلما تتعظون حين تتركون دين الله وتتبعون غيره مع أن العبر فى ذلك كثيرة.',NULL,'204',NULL,NULL,'0');
INSERT INTO [b836] VALUES (962,'<span data-type="title" id=toc-969>٤ </span>- فقد أهلكنا قرى عدة، بسبب عبادة أهلها غير الله وسلوكهم غير طريقه، بأن جاءهم عذابنا فى وقت غفلتهم واطمئنانهم ليلا وهم نائمون، كما حدث لقوم لوط، أو نهاراً وهم مستريحون وقت القيلولة كقوم شعيب.',NULL,'205',NULL,NULL,'0');
INSERT INTO [b836] VALUES (963,'<span data-type="title" id=toc-970>٥ </span>- فاعترفوا بذنبهم الذى كان سبب نكبتهم فما كان منهم عندما رأوا عذابنا إلا أن قالوا - حيث لا ينفعهم ذلك - إنا كنا ظالمين لأنفسنا بالمعصية ولم يظلمنا الله بعذابه.',NULL,'205',NULL,NULL,'0');
INSERT INTO [b836] VALUES (964,'<span data-type="title" id=toc-971>٦ </span>- وسيكون حساب الله يوم القيامة دقيقاً عادلاً، فلنسألن الناس الذين أرسلت إليهم الرسل: هل بلغتهم الرسالة؟ وبماذا أجابوا المرسلين؟ ولنسألن الرسل أيضاً: هل بلغتم ما أنزل إليكم من ربكم؟ وبماذا أجابكم أقوامكم؟',NULL,'205',NULL,NULL,'0');
INSERT INTO [b836] VALUES (965,'<span data-type="title" id=toc-972>٧ </span>- ولنخبرن الجميع إخباراً صادقاً بجميع ما كان منهم؛ لأننا أحصينا عليهم كل شئ فما كنا غائبين عنهم، ولا جاهلين لما كانوا يعملون.',NULL,'205',NULL,NULL,'0');
INSERT INTO [b836] VALUES (966,'<span data-type="title" id=toc-973>٨ </span>- ويوم نسألهم ونخبرهم، سيكون تقدير الأعمال للجزاء عليها تقديراً عادلاً، فالذين كثرت حسناتهم ورجحت على سيئاتهم هم الفائزون الذين نصونهم عن النار ويدخلون الجنة.',NULL,'205',NULL,NULL,'0');
INSERT INTO [b836] VALUES (967,'<span data-type="title" id=toc-974>٩ </span>- والذين كثرت سيئاتهم ورجحت على حسناتهم هم الخاسرون؛ لأنهم باعوا أنفسهم للشيطان، فتركوا التدبر فى آياتنا كفراً وعناداً.',NULL,'205',NULL,NULL,'0');
INSERT INTO [b836] VALUES (968,'<span data-type="title" id=toc-975>١٠ </span>- ولقد مكناكم فى الأرض فمنحناكم القوة لاستغلالها، والانتفاع بها، وهيأنا لكم وسائل العيش، فكان شكركم لله على هذه النعم قليلا جداً، وستلقون جزاء ذلك.',NULL,'205',NULL,NULL,'0');
INSERT INTO [b836] VALUES (969,'<span data-type="title" id=toc-976>١١ </span>- وفى أخبار الأولين عبر ومواعظ، يتضح فيها أن الشيطان يحاول أن يزيل عنكم النعم بنسيانكم أمر الله، فقد خلقنا أباكم آدم، ثم صورناه، ثم قلنا للملائكة: عظموه فعظموه طاعة لأمر ربهم، إلا إبليس فإنه لم يمتثل.',NULL,'205',NULL,NULL,'0');
INSERT INTO [b836] VALUES (970,'<span data-type="title" id=toc-977>١٢ </span>- قال الله منكراً عليه عصيانه: ما منعك عن تعظيم آدم وقد أمرتك به؟ أجاب إبليس فى عناد وكبر: أنا خير من آدم لأنك خلقتنى من نار وخلقته من طين، والنار أشرف من الطين.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b836] VALUES (971,'<span data-type="title" id=toc-978>١٣ </span>- فجزاه الله على عناده وكبره بطرده من دار كرامته، وقال له: اهبط منها، بعد أن كنت فى منزلة عالية، فما ينبغى لك أن تتكبر وتعصى فيها. . اخرج منها محكوماً عليك بالصغار والهوان.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b836] VALUES (972,'<span data-type="title" id=toc-979>١٤ </span>- قال إبليس لله: أمهلنى ولا تمتنى إلى يوم القيامة.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b836] VALUES (973,'<span data-type="title" id=toc-980>١٥ </span>- فأجابه الله بقوله: إنك من الممهلين المؤخرين.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b836] VALUES (974,'<span data-type="title" id=toc-981>١٦ </span>- ولحقده على آدم وحسده له قال إبليس: بسبب حكمك علىَّ بالغواية والضلال، أقسم لأضلن بنى آدم وأصرفهم عن طريقك المستقيم، متخذاً فى ذلك كل وسيلة ممكنة.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b836] VALUES (975,'<span data-type="title" id=toc-982>١٧ </span>- وأقسم لآتينهم من أمامهم ومن خلفهم، وعن أيمانهم وعن شمائلهم ومن كل جهة استطيعها، ملتمساً كل غفلة منهم أو ضعف فيهم، لأصل إلى إغوائهم، حتى لا يكون أكثرهم مؤمنين بك، لعدم شكرهم لنعمتك.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b836] VALUES (976,'<span data-type="title" id=toc-983>١٨ </span>- فزاده الله نكاية وقال له: اخرج من دار كرامتى مذموماً بكبرك وعصيانك، وهالكاً فى نهايتك، وأقسم أن من اتبعك من بنى آدم لأملأن جهنم منك ومنهم أجمعين.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b836] VALUES (977,'<span data-type="title" id=toc-984>١٩ </span>- ويا آدم اسكن أنت وزوجك دار كرامتى، وهى الجنة، وتنعما بما فيها، فكلا من أى طعام أردتما، إلا هذه الشجرة، فلا تقرباها حتى لا تكونا من الظالمين لأنفسهم بالعقاب المترتب على المخالفة.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b836] VALUES (978,'<span data-type="title" id=toc-985>٢٠ </span>- فزين لهما الشيطان مخالفة أمر الله، ليزيل عنهما الملابس، فتنكشف عوراتهما، وقال لهما: ما نهاكما ربكما عن هذه الشجرة إلا كراهة أن تكونا ملكين، أو كراهة أن تكونا من الخالدين الذين لا ينقطع نعيمهم فى هذه الدار.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b836] VALUES (979,'<span data-type="title" id=toc-986>٢١ </span>- وأقسم لهما أنه من الناصحين لهما، وكرر قسمه.',NULL,'207',NULL,NULL,'0');
INSERT INTO [b836] VALUES (980,'<span data-type="title" id=toc-987>٢٢ </span>- فساقهما إلى الأكل من الشجرة بهذه الخدعة، فلما ذاقا طعمها وانكشفت لهما عوراتهما، جعلا يجمعان بعض أوراق الشجر ليسترا بها عوراتهما وعاتبهما ربهما، ونبههما إلى خطئهما قائلاً: ألم أنهكما عن تلكما الشجرة وأخبركما أن الشيطان لكما عدو مبين لا يريد لكما الخير؟',NULL,'207',NULL,NULL,'0');
INSERT INTO [b836] VALUES (981,'<span data-type="title" id=toc-988>٢٣ </span>- قال آدم وزوجته نادمين متضرعين: يا ربنا ظلمنا أنفسنا بمخالفة أمرك الذى استوجب زوال النعيم، وإن لم تغفر لنا مخالفتنا وترحمنا بفضلك لنكونن من الخاسرين.',NULL,'207',NULL,NULL,'0');
INSERT INTO [b836] VALUES (982,'<span data-type="title" id=toc-989>٢٤ </span>- قال الله لهما وللشيطان: اهبطوا جميعاً بعضكم لبعض عدو، ولكم فى الأرض استقرار وتمتع إلى حين انقضاء آجالكم.',NULL,'207',NULL,NULL,'0');
INSERT INTO [b836] VALUES (983,'<span data-type="title" id=toc-990>٢٥ </span>- فى الأرض تولدون وتعيشون، وفيها تموتون وتدفنون، ومنها عند البعث تخرجون.',NULL,'207',NULL,NULL,'0');
INSERT INTO [b836] VALUES (984,'<span data-type="title" id=toc-991>٢٦ </span>- يا بنى آدم: قد أنعمنا عليكم، فخلقنا لكم ملابس تستر عوراتكم، ومواد تتزينون بها، ولكن الطاعة خير لباس يقيكم العذاب. تلك النعم من الآيات الدالة على قدرة الله وعلى رحمته، ليتذكر الناس بها عظمته واستحقاقه وحده الألوهية. وتلك القصة من سنن الله الكونية التى تبين جزاء مخالفة أمر الله، فيتذكر بها الناس ويحرصون على طاعة الله وعلى شكر نعمه.',NULL,'207',NULL,NULL,'0');
INSERT INTO [b836] VALUES (985,'<span data-type="title" id=toc-992>٢٧ </span>- يا بنى آدم، لا تستجيبوا للشيطان وإضلاله، فتخرجوا من هذه النعم التى لا تدوم إلا بالشكر والطاعة، كما استجاب أبواكم آدم وزوجه فأخرجهما الشيطان من النعيم والكرامة، ونزع عنهما لباسهما وأظهر لهما عوراتهما. إنه يأتيكم هو وأعوانه من حيث لا تشعرون بهم، ولا تحسون بأساليبهم ومكرهم، وليس للشيطان سلطان على المؤمنين، إنا جعلناه وأعوانه أولياء للذين لا يؤمنون إيماناً صادقاً يستلزم الطاعة التامة.',NULL,'207',NULL,NULL,'0');
INSERT INTO [b836] VALUES (986,'<span data-type="title" id=toc-993>٢٨ </span>- وإذا فعل المكذبون أمراً بالغ النكر - كالشرك، والطواف بالبيت عراة، وغيرهما - اعتذروا وقالوا: وجدنا آباءنا يسيرون على هذا المنهاج ونحن بهم مقتدون، والله أمرنا به ورضى عنه حيث أقرنا عليه، قل لهم يا أيها النبى منكراً عليهم افتراءهم: إن الله لا يأمر بهذه الأمور المنكرة، أتنسبون إلى الله ما لا تجدون له مستنداً ولا تعلمون عنه دليل صحة النسب إليه سبحانه؟',NULL,'208',NULL,NULL,'0');
INSERT INTO [b836] VALUES (987,'<span data-type="title" id=toc-994>٢٩ </span>- بَيِّن لهم ما أمر به الله وقل: أمر ربى بالعدل وما لا فحش فيه، وأمركم أن تخصوه بالعبادة فى كل زمان ومكان، وأن تكونوا مخلصين له فيها، وكلكم بعد الموت راجعون إليه، وكما بدأ خلقكم بيسر وكنتم لا تملكون إذ ذاك شيئاً، ستعودون إليه بيسر تاركين ما حولكم من النعم وراء ظهوركم.',NULL,'208',NULL,NULL,'0');
INSERT INTO [b836] VALUES (988,'<span data-type="title" id=toc-995>٣٠ </span>- وسيكون الناس يوم القيامة فريقين: فريقاً وفَّقه الله لأنه اختار طريق الحق فآمن وعمل عملا صالحاً، وفريقاً حُكِمَ عليه بالضلالة؛ لأنه اختار طريق الباطل وهو الكفر والعصيان، وهؤلاء الضالون قد اتخذوا الشياطين أولياء من دون الله فاتبعوهم، وهم يظنون أنهم مُوَفَّقون لاغترارهم بخداع الشياطين.',NULL,'208',NULL,NULL,'0');
INSERT INTO [b836] VALUES (989,'<span data-type="title" id=toc-996>٣١ </span>- يا بنى آدم: خذوا زينتكم من اللباس المادى الذى يستر العورة، ومن اللباس الأدبى وهو التقوى، عند كل مكان للصلاة، وفى كل وقت تؤدون فيه العبادة، وتمتعوا بالأكل والشرب غير مسرفين فى ذلك، فلا تتناولوا المحرم، ولا تتجاوزوا الحد المعقول من المتعة، إن الله لا يرضى عن المسرفين.',NULL,'208',NULL,NULL,'0');
INSERT INTO [b836] VALUES (990,'<span data-type="title" id=toc-997>٣٢ </span>- قل لهم - يا محمد - منكراً عليهم افتراء التحليل والتحريم على الله: مَنْ الذى حرَّم زينة الله التى خلقها لعباده؟ ومن الذى حرم الحلال الطيب من الرزق؟ قل لهم: هذه الطيبات نعمة من الله ما كان ينبغى أن يتمتع بها إلا الذين آمنوا فى الدنيا، لأنهم يؤدون حقها بالشكر والطاعة، ولكن رحمة الله الواسعة شملت الكافرين والمخالفين في الدنيا، وستكون هذه النعم خالصة يوم القيامة للمؤمنين، لا يشاركهم فيها غيرهم، ونحن نفصل الآيات الدالة على الأحكام على هذا المنوال الواضح، لقوم يدركون أن الله - وحده - مالك الملك بيده التحليل والتحريم.',NULL,'208',NULL,NULL,'0');
INSERT INTO [b836] VALUES (991,'<span data-type="title" id=toc-998>٣٣ </span>- قل يا محمد: إنما حرم ربى الأمور المتزايدة فى القبح كالزنى، سواء منها ما يرتكب سراً وما يرتكب علانية، والمعصية أياً كان نوعها، والظلم الذى ليس له وجه من الحق، وحرَّم أن تشركوا به دون حُجة صحيحة، أو دليل قاطع، وأن تفتروا عليه سبحانه بالكذب فى التحليل والتحريم وغيرهما.',NULL,'209',NULL,NULL,'0');
INSERT INTO [b836] VALUES (992,'<span data-type="title" id=toc-999>٣٤ </span>- ولكل أمة نهاية معلومة، لا يمكن لأية قوة أن تقدم هذه النهاية أو تؤخرها أية مدة مهما قلَّت.',NULL,'209',NULL,NULL,'0');
INSERT INTO [b836] VALUES (993,'<span data-type="title" id=toc-1000>٣٥ </span>- يا بنى آدم: إن جاءتكم رسل من جنسكم الآدمى ليبلغوكم آياتى الموحى بها كنتم فريقين: فالذين يؤمنون ويعملون الصالحات مخلصين، فلا خوف عليهم ولا هم يحزنون فى دنياهم أو أُخراهم.',NULL,'209',NULL,NULL,'0');
INSERT INTO [b836] VALUES (994,'<span data-type="title" id=toc-1001>٣٦ </span>- والذين يكذبون بالآيات ويستكبرون عن اتباعها والاهتداء بها، فأولئك أهل النار هم فيها معذبون، خالدون أبداً فى العذاب.',NULL,'209',NULL,NULL,'0');
INSERT INTO [b836] VALUES (995,'<span data-type="title" id=toc-1002>٣٧ </span>- فليس هناك أظلم من الذين يفترون الكذب على الله، بنسبة الشريك والولد إليه، وادِّعاء التحليل والتحريم وغيرهما من غير حُجة، أو يكذبون بآيات الله الموحى بها فى كتبه الموجودة فى كونه، أولئك ينالون فى الدنيا نصيباً مما كتب الله لهم من الرزق أو الحياة أو العذاب، حتى إذا جاءتهم ملائكة الموت ليقبضوا أرواحهم، قالوا لهم موبخين: أين الآلهة التى كنتم تعبدونها من دون الله لتدرأ عنكم الموت؟ فيجيبون: تبرأوا منا، وتركونا وغابوا عنا، وشهدوا على أنفسهم مقرين بأنهم كانوا كافرين.',NULL,'209',NULL,NULL,'0');
INSERT INTO [b836] VALUES (996,'<span data-type="title" id=toc-1003>٣٨ </span>- يقول الله يوم القيامة لهؤلاء الكافرين: ادخلوا النار فى ضمن أمم من كفار الإنس والجن، قد مضت من قبلكم، كلما دخلت أمة النار لعنت الأمة التى كفرت مثلها والتى اتخذتها قدوة، حتى إذا تتابعوا فيها مجتمعين قال التابعون يذمُّون المتبوعين: ربنا هؤلاء أضلونا بتقليدنا لهم، بحكم تقدمهم علينا أو بحكم سلطانهم فينا، فصرفونا عن طريق الحق، فعاقبهم عقاباً مضاعفاً يحملون فيه جزاء عصيانهم وعصياننا، فيرد الله عليهم: لكل منكم عذاب مضاعف لا ينجو منه أحد من الفريقين، يضاعف عقاب التابعين لكفرهم وضلالهم، ولاقتدائهم بغيرهم دون تدبر وتفكر، ويضاعف عقاب المتبوعين لكفرهم وضلالهم وتكفيرهم غيرهم وإضلالهم، ولكن لا تعلمون مدى ما لكل منكم من العذاب.',NULL,'210',NULL,NULL,'0');
INSERT INTO [b836] VALUES (997,'<span data-type="title" id=toc-1004>٣٩ </span>- وهنا يقول المتبوعون للتابعين: إنكم بانقيادكم لنا فى الكفر والعصيان لا تفضلون علينا بما يخفف عنكم من العذاب، فيقول الله لهم جميعا: ذوقوا العذاب الذى استوجبتموه بما كنتم تقترفون من كفر وعصيان.',NULL,'210',NULL,NULL,'0');
INSERT INTO [b836] VALUES (998,'<span data-type="title" id=toc-1005>٤٠ </span>- إن الذين كذبوا بآياتنا المنزلة فى الكتب الموجودة فى الكون، واستكبروا عن الاهتداء بها ولم يتوبوا، ميئوس من قبول أعمالهم ورحمة الله بهم، ومن دخولهم الجنة، كما أن دخول الجمل فى ثقب الإبرة ميئوس منه، وعلى هذا النحو من العقاب نعاقب المكذبين المستكبرين من كل أمة.',NULL,'210',NULL,NULL,'0');
INSERT INTO [b836] VALUES (999,'<span data-type="title" id=toc-1006>٤١ </span>- لهم فى جهنم فراش من نار وأغطية من نار، وعلى هذا النحو فمن ظلم نفسه بالظلم والضلال يعاقب هذا العقاب.',NULL,'210',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1000,'<span data-type="title" id=toc-1007>٤٢ </span>- والذين آمنوا وعملوا الأعمال الصالحة التى لم نكلفهم إلا ما يطيقونه منها، أولئك هم أهل الجنة يتنعمون فيها، خالدين فيها أبداً.',NULL,'210',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1001,'<span data-type="title" id=toc-1008>٤٣ </span>- وأخرجنا من قلوبهم ما كان فيها من غل، فهم فى الجنة إخوان متحابون، تجرى من تحتهم الأنهار بمائها العذب، ويقولون - سرورا بما نالوا من النعيم - الحمد للَّه الذى دلَّنا على طريق هذا النعيم، ووفقنا إلى سلوكه، ولولا أن هدانا اللَّه إليه بإرسال الرسل وتوفيقه لنا، ما كان فى استطاعتنا أن نوفق إلى الهداية. لقد جاءت رسل ربنا بالوحى الحق، وهنا يقول اللَّه لهم: إن هذه الجنة هبة من اللَّه، أعطيتمُوها فضلاً منى دون عوض منكم كالميراث، وهذا التكريم بسبب أعمالكم الصالحة فى الدنيا.',NULL,'211',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1002,'<span data-type="title" id=toc-1009>٤٤ </span>- ونادى أهل الجنة أهل النار قائلين: قد وجدنا ما وعدنا ربنا من الثواب حقا، فهل وجدتم ما وعد ربكم من العذاب حقا؟ فأجابوهم: نعم، فنادى مناد بين أهل الجنة وأهل النار: إن الحرمان أو الطرد من رحمة اللَّه جزاء الظالمين لأنفسهم بالكفر والضلال.',NULL,'211',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1003,'<span data-type="title" id=toc-1010>٤٥ </span>- هؤلاء الظالمون هم الذين يمنعون الناس عن السير فى طريق اللَّه الحق، وهو الإيمان والعمل الصالح، ويضعون العراقيل والشكوك حتى يبدو الطريق معوجاً للناس فلا يتبعوه، وهؤلاء كافرون بالدار الآخرة لا يخشون عقاب اللَّه.',NULL,'211',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1004,'<span data-type="title" id=toc-1011>٤٦ </span>- وبين أهل الجنة وأهل النار حاجز يسبق إلى احتلال أعرافه - وهى أماكنه الرفيعة العالية - رجال من خيار المؤمنين وأفاضلهم، يشرفون منها على جميع الخلائق، ويعرفون كلا من السعداء والأشقياء بعلامات تدل عليهم من أثر الطاعة والعصيان، فينادون السعداء قبل دخولهم الجنة وهم يرجون دخولها، فيبشرونهم بالأمان والاطمئنان ودخول الجنة.',NULL,'211',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1005,'<span data-type="title" id=toc-1012>٤٧ </span>- وإذا تحولت أبصار المؤمنين إلى جهة أصحاب النار بعد هذا النداء، قالوا من هول ما رأوا من نيران: ربنا لا تدخلنا مع هؤلاء الظالمين الذين ظلموا أنفسهم والحقّ والناس.',NULL,'211',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1006,'<span data-type="title" id=toc-1013>٤٨ </span>- ونادى أهل الدرجات العالية فى الجنة، من الأنبياء والصديقين. مَن كانوا يعرفونهم بأوصافهم من أهل النار، قائلين لهم لائمين: ما أفادكم جمعكم الكثير العدد ولا استكباركم على أهل الحق بسبب عصبيتكم وغناكم، وها أنتم أولاء ترون حالهم وحالكم.',NULL,'212',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1007,'<span data-type="title" id=toc-1014>٤٩ </span>- هؤلاء الضعفاء الذين استكبرتم عليهم، وأقسمتم أنه لا يمكن أن ينزل الله عليهم رحمة، كأنكم تمسكون رحمته، قد دخلوا الجنة؛ وقال لهم ربهم: ادخلوها آمنين، فلا خوف عليكم من أمر يستقبلكم، ولا أنتم تحزنون على أمر فاتكم.',NULL,'212',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1008,'<span data-type="title" id=toc-1015>٥٠ </span>- وإن أصحاب النار ينادون أصحاب الجنة قائلين: اتركوا لنا بعض الماء يفيض علينا أو أعطونا شيئاً مما أعطاكم الله تعالى من طيبات المأكل والملبس وسائر متع أهل الجنة، فيجيبهم أهل الجنة: إننا لا نستطيع، لأن الله منع ذلك كله عن القوم الجاحدين، الذين كفروا به وبنعمه فى الدنيا.',NULL,'212',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1009,'<span data-type="title" id=toc-1016>٥١ </span>- هؤلاء الجاحدون الذين لم يسعوا فى طلب الدين الحق، بل كان دينهم اتِّباع الهوى والشهوات، فكان لهواً يتلهون به وعبثاً يعبثونه وخدعتهم الحياة الدنيا بزخرفها فظنوها - وحدها - الحياة، ونسوا لقاءنا، فيوم القيامة ننساهم، فلا يتمتعون بالجنة، ويذوقون النار، بسبب نسيانهم يوم القيامة، وجحودهم بالآيات البينات الواضحات المثبتات للحق.',NULL,'212',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1010,'<span data-type="title" id=toc-1017>٥٢ </span>- ولقد آتيناهم - بياناً للحق - كتاباً بَيَّناه وفصَّلناه، مشتملا على علم كثير، فيه أدلة التوحيد وآيات الله فى الكون، وفيه شرعه، وفيه بيان الطريق المستقيم والهداية إليه، وفيه ما لو اتبعه الناس لكان رحمة بهم، ولا ينتفع به إلا الذين من شأنهم الإذعان للحق والإيمان به.',NULL,'212',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1011,'<span data-type="title" id=toc-1018>٥٣ </span>- إنهم لا يؤمنون به، ولا ينتظرون إلا المآل الذى بيَّنه الله لمن يكفر به. ويوم يأتى هذا المآل - وهو يوم القيامة - يقول الذين تركوا أوامره وبيناته وغفلوا عن وجوب الإيمان به، معترفين بذنوبهم: قد جاءت الرسل من عند خالقنا ومربينا، داعين إلى الحق الذى أرسلوا به، فكفرنا به. ويسألون هل لهم شفعاء يشفعون لهم؟ فلا يجدون، أو هل يردُّون إلى الدنيا ليعملوا صالحاً؟ فلا يجابون. قد خسروا عمل أنفسهم بغرورهم فى الدنيا، وغاب عنهم ما كانوا يكذبونه من ادعاء إله غير الله.',NULL,'213',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1012,'<span data-type="title" id=toc-1019>٥٤ </span>- إن ربكم الذى يدعوكم رسله إلى الحق وإلى الإيمان باليوم الآخر والجزاء فيه، هو خالق الكون ومبدعه، خلق السموات والأرض فى ست أحوال تشبه ستة أيام من أيام الدنيا، ثم استولى على السلطان الكامل فيها، وهو الذى يجعل الليل يستر النهار بظلامه، ويعقب الليل النهار بانتظام وتعاقب مستمر كأنه يطلبه، وخلق الله سبحانه الشمس والقمر والنجوم، وهى خاضعة لله تعالى مُسَيَّرات بأمره، وأنه له - وحده - الخلق والأمر المطاع فيها، تعالت بركات منشئ الكون وما فيه ومن فيه.',NULL,'213',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1013,'<span data-type="title" id=toc-1020>٥٥ </span>- إذا كان الله ربكم قد أنشأ الكون - وحده -، فادعوه بالعبادة وغيرها، معلنين الدعاء متذللين خاضعين، جاهرين أو غير جاهرين، ولا تعتدُوا بإشراك غيره، أو بظلم أحد، فإن الله تعالى لا يحب المعتدين.',NULL,'214',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1014,'<span data-type="title" id=toc-1021>٥٦ </span>- ولا تفسدوا فى الأرض الصالحة بإشاعة المعاصى والظلم والاعتداء، وادعوه - سبحانه - خائفين من عقابه، طامعين فى ثوابه، وإن رحمته قريبة من كل محسن، وهى محققة.',NULL,'214',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1015,'<span data-type="title" id=toc-1022>٥٧ </span>- والله - سبحانه وتعالى وحده - هو الذى يطلق الرياح مبشرة برحمته فى الأمطار التى تنبت الزرع وتسقى الغرس، فتحمل هذه الرياح سحاباً محملا بالماء، نسوقه لبلد لا نبات فيه، فيكون كالميت الذى فقد الحياة، فينزل الماء، فينبت الله به أنواعاً من كل الثمرات، وبمثل ذلك الإحياء للأرض بالإنبات نخرج الموتى فنجعلهم أحياء لعلكم تتذكرون بهذا قدرة الله وتؤمنون بالبعث.',NULL,'214',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1016,'<span data-type="title" id=toc-1023>٥٨ </span>- والأرض الطيبة الجيدة التربة يخرج نباتها نامياً حياً بإذن ربه، والأرض الخبيثة لا تخرج إلا نباتاً قليلاً عديم الفائدة يكون سبب نَكَدٍ لصاحبها.',NULL,'214',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1017,'<span data-type="title" id=toc-1024>٥٩ </span>- لقد عاند المشركون، وكذبوا بالحق إذ جاءهم مؤيداً بالحُجج القاطعة، وذلك شأن الكافرين مع أنبيائهم فى الماضى. لقد أرسلنا نوحاً إلى قومه الذين بعث فيهم، وقال لهم - مذكراً بأنه منهم -: يا قوم اعبدوا الله تعالى - وحده - فليس لكم أى إله غيره، وأنه سيكون البعث والحساب فى يوم القيامة، وهو يوم عظيم، أخاف عليكم فيه عذابه الشديد.',NULL,'214',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1018,'<span data-type="title" id=toc-1025>٦٠ </span>- قال أهل الصدارة والزعامة منهم، مجيبين تلك الدعوة إلى الوحدانية واليوم الآخر: إنا لنراك فى بُعْد بيِّن عن الحق.',NULL,'214',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1019,'<span data-type="title" id=toc-1026>٦١ </span>- قال نوح لهم نافياً ما رموه به: ليس بى كما تزعمون. ولكنى رسول من خالق العالمين ومنشئهم، فلا يمكن أن يكون ما أدعوكم إليه بعيداً عن الحق.',NULL,'214',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1020,'<span data-type="title" id=toc-1027>٦٢ </span>- وإنى فى هذه الدعوة الحق إلى الوحدانية والإيمان باليوم الآخر، أبلِّغكم ما أرسلنى الله به من الأحكام الإلهية التى يصلح بها الإنسان وإنى أمحضكم النصح وأخلصه لكم، وقد علمنى الله تعالى ما لا تعلمون.',NULL,'215',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1021,'<span data-type="title" id=toc-1028>٦٣ </span>- أترموننى بالضلالة والبعد عن الحق؟ وتعجبون أن يجئ إليكم تذكير من الله خالقكم، على لسان رجل جاء إليكم لينذركم بالعقاب إن كذبتم، وليدعوكم إلى الهداية وإصلاح القلوب وتجنب غضب الله تعالى، رجاء أن تكونوا فى رحمة الله تعالى فى الدنيا والآخرة، فلا يصح أن تعجبوا وتكذبوا مع قيام البينات المثبتة للرسالة.',NULL,'215',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1022,'<span data-type="title" id=toc-1029>٦٤ </span>- ولكنهم مع تلك البينات لم يؤمن أكثرهم، فكذبوه، فأنزلنا عليهم عذاباً بالإغراق فى الماء، وأنجينا الذين آمنوا به بالفلك الذى صنعه بهداية منا، وغرق الذين كذبوا مع قيام الدلائل البينة الواضحة، فعاندونا، وكانوا بذلك غير مبصرين الحق وقد عموا عنه.',NULL,'215',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1023,'<span data-type="title" id=toc-1030>٦٥ </span>- وكما أرسلنا نوحاً إلى قومه داعياً إلى التوحيد، أرسلنا إلى عاد هودا واحداً منهم علاقته بهم كعلاقة الأخ بأخيه، فقال لهم: يا قوم اعبدوا الله - وحده - وليس لكم إله غيره، وإن ذلك سبيل الاتقاء من الشر والعذاب وهو الطريق المستقيم، فهلا سلكتموه لتتقوا الشر والفساد؟ .',NULL,'215',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1024,'<span data-type="title" id=toc-1031>٦٦ </span>- قال ذوو الزعامة والصدارة فى قومه: إنا لنراك فى خفة عقل، حيث دعوتنا هذه الدعوة، وإنا لنعتقد أنك من الكاذبين.',NULL,'215',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1025,'<span data-type="title" id=toc-1032>٦٧ </span>- قال: يا قوم ليس بى فى هذه الدعوة أى قدر من خفة العقل، ولست بكاذب، ولكنى جئت بالهداية، وأنا رسول الله إليكم. وهو رب العالمين.',NULL,'215',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1026,'<span data-type="title" id=toc-1033>٦٨ </span>- إنى فيما أقول لكم: أبلِّغكم أوامر ربى ونواهيه، وهى رسالاته إليكم، وإنى أمحضكم نصحاً وإخلاصاً لكم، وأنا أمين فيما أخبركم به، ولست من الكاذبين.',NULL,'215',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1027,'<span data-type="title" id=toc-1034>٦٩ </span>- ثم قال لهم هود: هل أثار عجبكم، واستغربتم أن يجئ إليكم تذكير بالحق على لسان رجل منكم لينذركم بسوء العقبى فيما أنتم عليه؟ إنه لا عجب فى الأمر. ثم أشار إلى ما أصاب المكذبين الذين سبقوهم، وإلى نعمه عليهم، فقال: اذكروا إذ جعلكم وارثين للأرض من بعد قوم نوح الذين أهلكهم الله تعالى لتكذيبهم نوحاً، وزادكم قوة فى الأبدان وقوة فى السلطان، تلك نعمة تقتضى الإيمان، فاذكروا نعمه لعلكم تفوزون.',NULL,'216',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1028,'<span data-type="title" id=toc-1035>٧٠ </span>- ولكنهم مع هذه الدعوة بالحسنى قالوا مستغربين: أجئتنا لتدعونا إلى عبادة الله - وحده - وترك ما كان يعبد آباؤنا من الأصنام؟ وإنا لن نفعل، فأتنا بالعذاب الذى تهددنا به إن كنت من الصادقين؟ .',NULL,'216',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1029,'<span data-type="title" id=toc-1036>٧١ </span>- إنكم لعنادكم قد حق عليكم عذاب الله ينزل بكم، وغضبه يحل عليكم، أتجادلون فى أصنام سميتموها أنتم وآباؤكم آلهة؟ ، وما هى من الحقائق إلا أسماء لا مؤدى لها، وما جعل الله من حُجة تدل على ألوهيتها، فما كان لها من قوة خالقة منشئة تسوغ عبادتكم لها، وإذ لججتم هذه اللجاجة فانتظروا عقاب الله، وأنا معكم، ننتظر ما ينزل بكم.',NULL,'216',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1030,'<span data-type="title" id=toc-1037>٧٢ </span>- فأنجينا هوداً والذين آمنوا معه برحمة منا، وأنزلنا بالكافرين ما أبادهم ولم يُبْق لهم من بقية وأثر، وما كانوا داخلين فى زمرة المؤمنين.',NULL,'216',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1031,'<span data-type="title" id=toc-1038>٧٣ </span>- وأرسلنا إلى ثمود أخاهم صالحا الذى يشاركهم فى النسب والوطن، وكانت دعوته كدعوة الرسل قبله وبعده. قال لهم: أخلصوا العبادة لله - وحده - ما لكم أى إله غيره، وقد جاءتكم حُجة على رسالتى من ربكم، هى ناقة ذات خَلق اختصت به، فيها الحُجة، وهى ناقة الله، فاتركوها تأكل فى أرض الله من عشبها، ولا تنالوها بسوء فينالكم عذاب شديد الإيلام.',NULL,'216',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1032,'<span data-type="title" id=toc-1039>٧٤ </span>- وتذكَّروا أن الله جعلكم وارثين لأرض عاد، وأنزلكم فى الأرض منازل طيبة تتخذون من السهول قصوراً فخمة، وتنحتون الجبال فتجعلون منها بيوتاً، فاذكروا نعم الله تعالى إذ مكنكم من الأرض ذلك التمكين، ولا تَعْثوا فى الأرض فتكونوا مفسدين بعد هذا التمكين.',NULL,'217',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1033,'<span data-type="title" id=toc-1040>٧٥ </span>- قال المتكبرون من أهل الصدارة والزعامة، مخاطبين الذين آمنوا من المستضعفين لائمين لهم ومستعلين عليهم: أتعتقدون أن صالحاً مُرسل من ربه؟ فأجابهم أهل الحق: إنا بما أرسل معتقدون، مذعنون له.',NULL,'217',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1034,'<span data-type="title" id=toc-1041>٧٦ </span>- قال أولئك المستكبرون: إنا جاحدون منكرون للذى آمنتم به: وهو ما يدعو إليه صالح من الوحدانية.',NULL,'217',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1035,'<span data-type="title" id=toc-1042>٧٧ </span>- وَلجَّ العناد بأولئك المستكبرين، فتحدوا الله ورسوله، وذبحوا الناقة، وتجاوزوا الحد فى استكبارهم، وأعرضوا عن أمر ربهم، وقالوا - متحدين -: يا صالح، ائتنا بالعذاب الذى وعدتنا إن كنت ممن أرسلهم الله حقاً.',NULL,'217',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1036,'<span data-type="title" id=toc-1043>٧٨ </span>- فأخذتهم الزلازل الشديدة، فأصبحوا فى دارهم ميتين خامدين.',NULL,'217',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1037,'<span data-type="title" id=toc-1044>٧٩ </span>- وقبل أن تنزل بهم النازلة أعرض عنهم أخوهم صالح، وقال: يا قوم قد أبلغتكم أوامر ربى ونواهيه، ومحضت لكم النصح، ولكنكم بلجاجتكم وإصراركم صرتم لا تحبون من ينصحكم.',NULL,'217',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1038,'<span data-type="title" id=toc-1045>٨٠ </span>- ولقد أرسلنا لوطاً - نبى الله - إلى قومه، يدعوهم إلى التوحيد، وينبههم إلى وجوب التخلى عن أقبح جريمة يفعلونها. أتأتون الأمر الذى يتجاوز الحد فى القبح والخروج على الفطرة وقد ابتدعتم تلك الفاحشة بشذوذكم، فلم يسبقكم بها أحد من الناس؟',NULL,'217',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1039,'<span data-type="title" id=toc-1046>٨١ </span>- وهى أنكم تأتون الرجال مشتهين ذلك، وتتركون النساء، أنتم شأنكم الإسراف، ولهذا خرجتم على الفطرة وفعلتم ما لم يفعله الحيوان.',NULL,'218',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1040,'<span data-type="title" id=toc-1047>٨٢ </span>- وما كان جواب قومه على هذا الاستنكار - لأقبح الأفعال - إلا أن قالوا: أخرجوا لوطاً وآله وأتباعه من قريتكم، لأنهم يتطهرون وَينْأوْن عن هذا الفعل الذى يستقبحه العقل والفطرة ويستحسنونه هم.',NULL,'218',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1041,'<span data-type="title" id=toc-1048>٨٣ </span>- ولقد حقت عليهم كلمة العذاب، فأنجينا لوطاً وأهله، إلا امرأته فإنها كانت من هؤلاء الضالين.',NULL,'218',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1042,'<span data-type="title" id=toc-1049>٨٤ </span>- وأمطرنا عليهم حجارة مخربة، ومادت الأرض بالزلازل من تحتهم فانظر - يا أيها النبى - إلى عاقبة المجرمين وكيف كانت؟ .',NULL,'218',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1043,'<span data-type="title" id=toc-1050>٨٥ </span>- ولقد أرسلنا إلى مدين أخاهم شعيباً قال: يا قوم، اعبدوا الله - وحده - فليس لكم ولى - أى إله - غيره قد جاءتكم الحُجج المبينة للحق من ربكم مثبتة رسالتى إليكم، وجاءتكم رسالة ربكم بالإصلاح بينكم، والمعاملة العادلة، فأوفوا الكيل والميزان فى مبادلاتكم، ولا تنقصوا حقوق الناس، ولا تفسدوا فى الأرض الصالحة بإفساد الزرع ونحوه، وبقطع الأرحام والمودة، فإن ذلك خير لكم إن كنتم تؤمنون بالله تعالى وبالحق المبين.',NULL,'218',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1044,'<span data-type="title" id=toc-1051>٨٦ </span>- ولا تقعدوا بكل طريق من طرق الحق والهداية والعمل الصالح: تهددون سالكه، وبذلك تمنعون طالبى الخير من الوصول، وهم أهل الإيمان الذين يؤمنون بالله، وتريدون أنتم الطريق المعوج، واذكروا إذ كنتم عدداً قليلاً فصيَّركم الله عدداً كثيراً بالاستقامة فى طلب النسل والمال، واعتبروا بعاقبة المفسدين قبلكم.',NULL,'218',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1045,'<span data-type="title" id=toc-1052>٨٧ </span>- وإذا كانت طائفة منكم آمنوا بالحق الذى أرسلت به، وطائفة لم يؤمنوا، فانتظروا حتى يحكم الله بين الفريقين وهو خير الحاكمين.',NULL,'219',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1046,'<span data-type="title" id=toc-1053>٨٨ </span>- هذا شأن شعيب فى دعوته قومه، أما القوم فقد تمالأوا على الباطل، وتولى أكابرهم الذين استكبروا عن الدعوة، واستنكفوا أن يتبعوا الحق، وواجهوا شعيباً بما يضمرون، فقالوا له: إنا لا محالة سنخرجك ومن آمن معك من قريتنا، ونطردكم، ولا ننجيكم من هذا العذاب إلا أن تصيروا فى ديننا الذى هجرتموه. فرد عليهم شعيب - عليه السلام - قائلا: أنصير فى ملتكم ونحن كارهون لها لفسادها؟ لا يكون ذلك أبداً.',NULL,'219',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1047,'<span data-type="title" id=toc-1054>٨٩ </span>- وبالغ فى قطع طمعهم من العودة إلى ملتهم كما يطلبون، فقال: نكون كاذبين على الله إن صرنا فى ملتكم بعد أن هدانا الله إلى الصراط المستقيم، ولا ينبغى لنا أن نصير فى ملتكم بمحض اختيارنا ورغبتنا. إلا أن يشاء الله عودتنا إلى ملتكم، وهيهات ذلك. لأنه ربنا العليم بنا، فلا يشاء رجوعنا إلى باطلكم، فهو - جل شأنه - وسع كل شئ علماً، يهدينا بلطفه وحكمته إلى ما يحفظ علينا إيماننا إليه - وحده - سلمنا أمرنا مع قيامنا بما أوجبه علينا. ربنا افصل بيننا وبين قومنا بالحق الذى مضت به سنتك فى الفصل بين المحقين المصلحين والمبطلين المفسدين، وأنت - لإحاطة علمك وقدرتك - أعدل الحاكمين وأقدرهم.',NULL,'219',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1048,'<span data-type="title" id=toc-1055>٩٠ </span>- هنا يئس القوم من مطاوعة شعيب ومن معه لهم، وعلموا أنهم ثابتون على دينهم، كذلك خافوا أن يكثر المهتدون مع شعيب بظهور قوته وثباته على دعوته، فاتجه كبراؤهم الكافرون إلى متبوعيهم، يهددونهم قائلين: والله إن طاوعتم شعيباً فى قبول دعوته، إنكم لخاسرون شرفكم وثروتكم فى اتباعكم ديناً باطلاً لم يكن عليه سلفكم.',NULL,'219',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1049,'<span data-type="title" id=toc-1056>٩١ </span>- هنا حقَّت عليهم كلمة العذاب، فأصابهم الله بزلزلة اضطربت لها قلوبهم، فصاروا فى دارهم منكبين على وجوههم لا حياة فيهم.',NULL,'219',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1050,'<span data-type="title" id=toc-1057>٩٢ </span>- هذا شأن الله مع الذين كذَّبوا شعيباً، وهددوه وأنذروه بالإخراج من قريتهم، وعملوا على رد دعوته، قد هلكوا وهلكت قريتهم كأن لم يعش فيها الذين كذَّبوا شعيباً، وزعموا أن من يتبعه يكون خاسراً، وأكدوا هذا الزعم وكانوا هم الخاسرين لسعادتهم فى الدنيا والآخرة.',NULL,'220',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1051,'<span data-type="title" id=toc-1058>٩٣ </span>- فلما رأى شعيب ما نزل بهم من الهلاك المدمر، أعرض عنهم، وقال مبرئاً نفسه من التقصير معهم: لقد أبلغتكم رسالات ربكم المفضية إلى الإحسان إليكم لو عملتم بها، وبالغت فى إسداء النصح لكم، والعظة بما به تنجون من عقوبة الله، فكيف أحزن الحزن الشديد على قوم كافرين؟ لا يكون ذلك بعدما أعذرت إليهم، وبذلت جهدى فى سبيل هدايتهم ونجاتهم، فاختاروا ما فيه هلاكهم.',NULL,'220',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1052,'<span data-type="title" id=toc-1059>٩٤ </span>- وما بعثنا نبياً من الأنبياء فى قرية من القرى، يدعو أهلها إلى دين الله القويم، وأعرضوا عن قبول تلك الدعوة، إلا أصبناهم بالفقر والمرض، كى يتذللوا ويبتهلوا إلى الله مخلصين له فى كشف ما نزل بهم، ويستجيبوا لرسوله.',NULL,'220',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1053,'<span data-type="title" id=toc-1060>٩٥ </span>- ثم لما لم يفعلوا ذلك، واستمروا فى كفرهم وعنادهم، امتحناهم بالعافية مكان البلاء استدراجاً، فأعطيناهم رخاء وسعة وصحة وعافية، حتى كثروا ونموَّا فى أموالهم وأنفسهم، وقالوا لجهلهم: إن ما أصاب آباءنا من المحن والبلايا والرفاهية والنعيم، فذلك شأن الدهر، يُدَاول الضرَّاء والسَرَّاء بين الناس، من غير أن ينتبهوا إلى أن هذا جزاء كفرهم فيرتدعوا وبهذا جهلوا سنته - جل شأنه - فى أسباب الصلاح والفساد فى البشر، وما يترتب عليهما من السعادة والشقاء فكانت عاقبة ذلك أن أصابهم الله بالعذاب المدمر فجأة، وهم فاقدون للشعور بما سيحل بهم.',NULL,'220',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1054,'<span data-type="title" id=toc-1061>٩٦ </span>- ولو أن أهل تلك القرى آمنوا بما جاء به الرسل، وعملوا بوصاياهم وابتعدوا عما حرَّمه الله لأعطيناهم بركات من السماء والأرض، كالمطر والنبات والثمار والأنعام والأرزاق والأمن والسلامة من الآفات، ولكن جحدوا وكذبوا الرسل، فأصبناهم بالعقوبات وهم نائمون، بسبب ما كانوا يقترفون من الشرك والمعاصى، فأخذهم بالعقوبة أَثرٌ لازم لكسبهم القبيح، وعبرة لأمثالهم إن كانوا يعقلون.',NULL,'220',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1055,'<span data-type="title" id=toc-1062>٩٧ </span>- أَأَمِن أهل القرى الذين بلغتهم دعوة أنبيائهم ولم يؤمنوا أن يأتيهم عذابنا وقت بياتهم وهم غارقون فى نومهم؟ .',NULL,'221',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1056,'<span data-type="title" id=toc-1063>٩٨ </span>- أغفل هؤلاء وأمنوا أن يأتيهم العذاب فى ضحى النهار وانبساط الشمس وهم منهمكون فيما لا نفع فيه لهم؟ .',NULL,'221',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1057,'<span data-type="title" id=toc-1064>٩٩ </span>- أجهلوا سنة الله فى المكذبين، فأمنوا عذابه ليلاً أو نهاراً، يسوقه بتدبيره الذى يخفى على الناس أمره؟ إنه لا يجهل تدبير الله وسنته فى عقوبة المكذبين إلا الذين خسروا أنفسهم بعدم اليقظة إلى ما فيه سعادتهم.',NULL,'221',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1058,'<span data-type="title" id=toc-1065>١٠٠ </span>- أغاب عن الذين يخلفون من قبلهم من الأمم سنة الله فيمن قبلهم، وأن شأننا فيهم كشأننا فيمن سبقوهم؟ وهو أنهم خاضعون لمشيئتنا، لو نشاء أن نُعَذبهم بسبب ذنوبهم لأصبناهم كما أصبنا أمثالهم، ونحن نختم على قلوبهم لفرط فسادها حتى وصلت إلى حالة لا تقبل معها شيئاً من الهدى، فهم بهذا الطبع والختْم لا يسمعون الحكم والنصائح سماع تفقه واتعاظ.',NULL,'221',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1059,'<span data-type="title" id=toc-1066>١٠١ </span>- تلك القرى التى بعد عهدها، وطال الأمد على تاريخها، نقُص عليك الآن بعض أخبارها مما فيه عبرة. ولقد جاء أهل تلك القرى رسلهم بالبينات الدالة على صدق دعوتهم، فلم يكن من شأنهم أن يؤمنوا بعد مجئ البينات، لتمرسهم بالتكذيب للصادقين، فكذَّبوا رسلهم ولم يهتدوا، وهكذا يجعل الله حجاباً على قلوب الكافرين وعقولهم، فيخفى عليهم طريق الحق ويَنْأوْن عنه.',NULL,'221',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1060,'<span data-type="title" id=toc-1067>١٠٢ </span>- وما وجدنا لأكثر أولئك الأقوام وفاء بميثاق مما أوصيناهم به من الإيمان، على لسان الرسل، وعلى ما يوحى به العقل والنظر السليم. وإن الشأن المطرد فيهم تَمكُّن أكثرهم من الفسوق والخروج عن كل عهد.',NULL,'221',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1061,'<span data-type="title" id=toc-1068>١٠٣ </span>- ثم بعثنا من بعد أولئك الرسل موسى - عليه السلام - ومعه دلائلنا التى تدل على صدقه فيما يُبلغه عنا إلى فرعون وقومه، فبلَّغهم موسى دعوة ربه، وأراهم آية الله، فظلموا أنفسهم وقومهم بالكفر بها، كِبْرا وجحوداً فاستحقوا من الله عقوبة صارمة كانت بها نهاية أمرهم، فانظر - أيها النبى - نهاية المفسدين فى الأرض.',NULL,'222',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1062,'<span data-type="title" id=toc-1069>١٠٤ </span>- وقال موسى: يا فرعون إنى مُرْسَل من الله رب العالمين ومالك أمرهم، لأبلغكم دعوته، وأدعوكم إلى شريعته.',NULL,'222',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1063,'<span data-type="title" id=toc-1070>١٠٥ </span>- وإنى حريص على قول الصدق عن الله تعالى، وقد جئتكم بآية عظيمة الشأن ظاهرة الحُجة فى بيان الحق الذى جئت به، فأطلق معى بنى إسرائيل، وأخرجهم من رق قهرك، ليذهبوا معى إلى دارٍ غير دارك، يعبدون فيها ربهم وربك.',NULL,'222',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1064,'<span data-type="title" id=toc-1071>١٠٦ </span>- قال فرعون لموسى: إن كنت جئت مؤيداً بآية من عند من أرسلك فأظهرها لدى إن كنت من أهل الصدق الملتزمين لقول الحق.',NULL,'222',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1065,'<span data-type="title" id=toc-1072>١٠٧ </span>- فلم يلبث موسى أن ألقى عصاه التى كانت بيمينه أمام فرعون وقومه، فإذا هذه العصا ثعبان ظاهر بيِّن يسعى من مكان إلى آخر، فى قوة تدل على تمام حياته.',NULL,'222',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1066,'<span data-type="title" id=toc-1073>١٠٨ </span>- وأخرج يده من جيبه، فإذا هى ناصعة البياض تتلألأ للناظرين.',NULL,'222',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1067,'<span data-type="title" id=toc-1074>١٠٩ </span>- فلما أظهر موسى آية الله تعالى، ثارت نفوس بطانة فرعون وعظماء قومه، فقالوا تزلُّفاً ومشايعة لفرعون: إن هذا لماهر فى علم السحر، وليس ذلك بآية من الله.',NULL,'222',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1068,'<span data-type="title" id=toc-1075>١١٠ </span>- وقد وجه إرادته لسلب ملككم، وإخراجكم من أرضكم بسحره، وما ينشأ عنه من استمالة أفراد الشعب ليتبعوه، وانظروا ماذا تأمرون بما يكون سبيلا للتخلص منه.',NULL,'222',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1069,'<span data-type="title" id=toc-1076>١١١ </span>- وقالوا: أجّل البت فى أمره وأمر أخيه الذى يعاونه فى دعوته، وأرسل فى مدائن ملكك رجالا من جندك يجمعون الناس أولى العلم بالسحر.',NULL,'223',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1070,'<span data-type="title" id=toc-1077>١١٢ </span>- فيأتوك بكل عليم بفنون السحر، وهم يكشفون لك حقيقة ما جاء به موسى. فلا يفتتن به أحد.',NULL,'223',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1071,'<span data-type="title" id=toc-1078>١١٣ </span>- وجاء إلى فرعون السحرة الذين جمعهم له جنده، وقالوا له: إن لنا لجزاء عظيماً يكافئ ما يُطلب منا إن كانت الغلبة لنا على موسى.',NULL,'223',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1072,'<span data-type="title" id=toc-1079>١١٤ </span>- وجاء فرعون مجيباً لهم إلى ما طلبوا: نعم إن لكم لجزاء عظيماً، وإنكم مع ذلك لمن أهل الحظوة عندنا.',NULL,'223',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1073,'<span data-type="title" id=toc-1080>١١٥ </span>- ثم توجه السحرة إلى موسى بعد أن وعدهم فرعون بما وعدهم، وأظهروا الثقة بأنفسهم واعتدادهم بسحرهم فى ميدان المباراة، وقالوا له إما أن تلقى ما عندك أولا، وإما أن نكون نحن الملقين بما عندنا من دونك.',NULL,'223',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1074,'<span data-type="title" id=toc-1081>١١٦ </span>- فأجابهم موسى إجابة الواثق بالغلبة والظفر، مظهراً عدم مبالاته بهم: ألقوا ما أنتم ملقون أولا. فلما ألقى كل واحد منهم ما كان معه من حبال وعصى، خيلوا إلى أبصار الناس وَموَّهوا عليهم أن ما فعلوه حقيقة وما هو إلا خيال، فهال الأمر الناس وأوقع فى قلوبهم الرَّهَبَ والرعب، وقد جاء السحرة النَّاسَ بسحر مظهره كبير وتأثيره فى أعينهم عظيم.',NULL,'223',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1075,'<span data-type="title" id=toc-1082>١١٧ </span>- وأصدر الله أمره إلى موسى أن ألق بعصاك، فقد جاء وقتها، فألقاها كما أمر، فإذا عصاه تبتلع بسرعة ما يكذبون ويموّهون.',NULL,'223',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1076,'<span data-type="title" id=toc-1083>١١٨ </span>- فثبت الحق وظهر فى جانب موسى - عليه السلام - وبطل تخيل السحرة.',NULL,'223',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1077,'<span data-type="title" id=toc-1084>١١٩ </span>- فَهُزم فرعون وملؤه فى ذلك المجمع العظيم، وعادوا من ذلك المجمع أذلة بما رزئوا من الخذلان والخيبة.',NULL,'224',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1078,'<span data-type="title" id=toc-1085>١٢٠ </span>- هذا ما كان من شأن فرعون وملئه، وأما السحرة فقد بهرهم الحق، فاندفعوا ساجدين لله مذعنين للحق.',NULL,'224',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1079,'<span data-type="title" id=toc-1086>١٢١ </span>- قائلين: آمنا بخالق العالمين، ومالك أمرهم المتصرف فيهم.',NULL,'224',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1080,'<span data-type="title" id=toc-1087>١٢٢ </span>- إنَّه الإله الذى يعتقده ويؤمن به موسى وهارون.',NULL,'224',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1081,'<span data-type="title" id=toc-1088>١٢٣ </span>- فهال هذا الأمر فرعون، وأثار حميته فقال: هل آمنتم وصدقتم برب موسى وهارون قبل أن آذن لكم؟ إن هذا الصنيع الذى صنعتموه أنتم وموسى وهارون كان بالاتفاق، وليس إلا مكراً مكرتموه فى المدينة (مصر) لأجل أن تخرجوا منها أهلها بمكركم، فسوف ترون ما يحل بكم من العذاب جزاء اتباعكم موسى وهارون، وعقاباً على هذا المكر والخداع.',NULL,'224',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1082,'<span data-type="title" id=toc-1089>١٢٤ </span>- وأقسم لأُنكِّلنَّ بكم، وأقطعن أيديكم وأرجلكم من خلاف، فأقطع اليد من جانب والرجل من جانب آخر، ثم لأصلِّبنَّ كل واحد منكم وهو على هذه الحالة المشوهة، لتكونوا عبرة لمن تحدثه نفسه بالكيد لنا أو بالخروج على سلطاننا.',NULL,'224',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1083,'<span data-type="title" id=toc-1090>١٢٥ </span>- فلم يأبهوا لقوله وتهديداته، لتمكن الإيمان من شغاف قلوبهم، فقالوا له: إنا إلى ربنا راجعون، فنتقلب فى رحمته ونعيم جزائه.',NULL,'224',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1084,'<span data-type="title" id=toc-1091>١٢٦ </span>- وما تُنكر منا وتُعَاقِبنا عليه إلا أن صدقنا موسى، وأذعنا لآيات ربنا الواضحة الدالة على الحق لما جاءتنا. ثم توجهوا إلى الله ضارعين إليه قائلين: يا ربنا أفض علينا صبراً عظيماً نقوى معه على احتمال الشدائد، وتوفنا على الإسلام غير مفتونين من وعيد فرعون.',NULL,'224',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1085,'<span data-type="title" id=toc-1092>١٢٧ </span>- وبعد أن شاهد فرعون وقومه ما شاهدوا - من ظهور أمر موسى وقوة غلبته وإيمان السحرة به - قال الكبراء من قومه: أنترك موسى وقومه أحرارا آمنين، ليكون مآلهم أن يفسدوا قومك عليك فى أرض مصر بإدخالهم فى دينهم، ويتركك مع آلهتك فى غير مبالاة، فيظهر للمصريين عجزك وعجزهم؟ قال فرعون مجيبا لهم: سنقتل أبناء قومه تقتيلا ما تناسلوا، ونستبقى نساءهم أحياء، حتى لا يكون لهم قوة كما فعلنا من قبل، وإنا مستعلون عليهم بالغلبة والسلطان قاهرون لهم.',NULL,'225',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1086,'<span data-type="title" id=toc-1093>١٢٨ </span>- وهنا رأى موسى أثر الجزع فى نفوس قومه، فشد من عزمهم، وقال لهم: اطلبوا معونة اللَّه وتأييده، واثبتوا ولا تجزعوا، إن الأرض في قبضة قدرة الله وملكه، يجعلها ميراثا لمن يشاء من عباده لا لفرعون، والعاقبة الحسنة للذين يتقون اللَّه بالاعتصام به والاستمساك بأحكامه.',NULL,'225',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1087,'<span data-type="title" id=toc-1094>١٢٩ </span>- فقال القوم فى حزن وضعف: نحن نالنا الأذى قديماً من فرعون قبل مجيئك إلينا، وحديثا من بعد مجيئك. فقتح موسى لهم باب الأمل وقال لهم: إن المرجو من فضل - ربكم - أن يهلك عدوكم الذى سخركم وآذاكم بظلمه، ويجعلكم خلفاء الأرض التى وعدكم إياها، فيعلم سبحانه ما أنتم عاملون بعد هذا التمكين: أتشكرون النعمة أم تكفرون؟ وتصلحون في الأرض أم تفسدون؟ ليجزيكم فى الدنيا والآخرة بما تعملون.',NULL,'225',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1088,'<span data-type="title" id=toc-1095>١٣٠ </span>- ولقد عاقبنا فرعون وقومه بالجدب والقحط وضيق المعيشة، وبنقص ثمرات الزروع والأشجار، رجاء أن ينتبهوا إلى ضعفهم وعجز ملكهم الجبار أمام قوة اللَّه فيتعظوا ويرجعوا عن ظلمهم لبنى إسرئيل، ويستجيبوا لدعوة موسى - عليه السلام - فإن شأن الشدائد أن تمنع الغرور وتهذب الطباع وتوجه الأنفس إلى قبول الحق، وإرضاء رب العالمين، والتضرع إليه دون غيره.',NULL,'225',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1089,'<span data-type="title" id=toc-1096>١٣١ </span>- ولكن دأب فرعون وأعوانه عدم الثبات على الحق، فسرعان ما يعودون إلى الغدر والمعصية، فهم متقلبون. فإذا جاءهم الخصب والرخاء - وكثيرا ما يكون ذلك - قالوا: نحن المستحقون له لما لنا من الامتياز على الناس، وإن أصابهم ما يسوؤهم كجدب أو جائحة أو مصيبة فى الأبدان والأرزاق، يرون أنهم أصيبوا بشؤم موسى ومن معه، ويغفلون عن أن ظلمهم وفجورهم هو الذى أدى بهم إلى ما نالهم، ألا فليعلموا أن علم شؤمهم عند الله، فهو الذى أصابهم بسبب أعمالهم القبيحة، فهى التى ساقت إليهم ما يسوؤهم، وليس موسى ومن معه، ولكن أكثرهم لا يدرى هذه الحقيقة التى لا شك فيها.',NULL,'226',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1090,'<span data-type="title" id=toc-1097>١٣٢ </span>- ولهذه الفكرة السيئة عندهم أصروا على الجحود، وقالوا عند رؤيتهم لآيات موسى: إنك مهما جئتنا بكل نوع من أنواع الآيات التى تستدل بها على حقيقة دعوتك - لأجل أن تصرفنا بها عما نحن عليه من ديننا ومن استعباد قومك - فما نحن لك بمصدقين ولا مذعنين.',NULL,'226',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1091,'<span data-type="title" id=toc-1098>١٣٣ </span>- فأنزل اللَّه عليهم مزيدا من المصائب والنكبات: بالطوفان الذى يغشى أماكنهم، وبالجراد الذى يأكل ما بقى من نبات أو شجر، وبالقمل وهو حشرة تفسد الثمار وتقضى على الحيوان والنبات، وبالضفادع التى تنتشر فتنغص عليهم حياتهم وتذهب بصفائها، وبالدم الذى يسبب الأمراض الكثيرة كالنزيف من أى جسم، والدم الذى ينحبس فيسبب ضغطا أو ينفجر فيسبب شللا، ويشمل البول الدموى بسبب البلهارسيا ونحوها، أو الذى تحول إليه ماؤهم الذى يستخدمونه فى حاجات معاشهم، أصابهم اللَّه بهذه الآيات المميزات الواضحات فلم يتأثروا بها، وجمدت قرائحهم وفسد ضميرهم، فعتوا عن الإيمان والرجوع إلى الحق من حيث هو حق، وكانوا قوما موغلين فى الإجرام كما هو شأنهم.',NULL,'226',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1092,'<span data-type="title" id=toc-1099>١٣٤ </span>- ولفرط تقلبهم حسب الدواعى، كانوا كلما وقع عليه نوع من العذاب قالوا لشدة تأثيره فيهم وتألمهم به: يا موسى، سل ربك لنا بالذى عهد به إليك أن تدعوه به فيعطيك الآيات ويستجيب لك الدعاء، أن تكشف عنا هذا العذاب، ونحن نقسم لك لئن أزلته عنا لنخضعن، ولنطلقنَّ معك بنى إسرائيل كما أردت.',NULL,'226',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1093,'<span data-type="title" id=toc-1100>١٣٥ </span>- فلما كشفنا عنهم العذاب مرة بعد أخرى إلى وقت هم منتهون إليه فى كل مرة، إذا هم ينقضون عهدهم ويحنثون فى قسمهم، ويعودون إلى ما كانوا عليه، ولم تُجْد فيهم هذه المحن الزاجرة.',NULL,'227',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1094,'<span data-type="title" id=toc-1101>١٣٦ </span>- فأنزلنا عليهم نقمتنا، فأغرقناهم فى البحر بسبب استمرارهم على التكذيب بآياتنا، وتمام غفلتهم عما تقتضيه هذه الآيات من الإيمان والإذعان.',NULL,'227',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1095,'<span data-type="title" id=toc-1102>١٣٧ </span>- وأعطينا القوم الذين كانوا يستضعفون فى مصر - وهم بنو إسرائيل - جميع الأرض التى حباها اللَّه بالخصب والخير الكثير، فى مشارقها ومغاربها، ونفذت كلمة اللَّه الحسنى تامة، ووعد بالنصر شاملاً لبنى إسرائيل بسبب صبرهم على الشدائد، ودمَّرنا ما كان يصنع فرعون وقومه من الصروح والقصور المشيدة، وما كانوا يعرشونه من السقائف للنبات والشجر المتسلق كعرائش العنب، هذا شأن اللَّه، وصدق وعده الجميل لبنى إسرائيل.',NULL,'227',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1096,'<span data-type="title" id=toc-1103>١٣٨ </span>- وتجاوز بنو أسرائيل البحر بعنايتنا وتأييدنا وتيسير الأمر لهم فلما تجاوزوه مروا على قوم ملازمين لعبادة أصنام لهم، فلما شاهدوا هذه الحالة غلب عليهم ما ألفوا قديما من عبادة المصريين للأصنام، فطلبوا من موسى أن يجعل لهم صنما يعبدونه، كما أن لهؤلاء القوم أصناما يعبدونها فسارع موسى - عليه السلام - موبخا لهم رادعا وقال: إنكم قوم سفهاء لا عقول لكم، لا تعرفون العبادة الحقة، ولا من هو الإله الذى يستحق أن يعبد.',NULL,'227',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1097,'<span data-type="title" id=toc-1104>١٣٩ </span>- إن هؤلاء الذين ترونهم يعبدون الأصنام، هالك ما هم فيه من الدين الباطل، وزائل عملهم لا بقاء له.',NULL,'227',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1098,'<span data-type="title" id=toc-1105>١٤٠ </span>- أأطلب لكم معبوداً غير اللَّه رب العالمين، وهو قد منحكم الفضل فأعطاكم نعما لم يعطها غيركم من أهل زمانكم؟! .',NULL,'227',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1099,'<span data-type="title" id=toc-1106>١٤١ </span>- واذكروا إذ أنجاكم اللَّه تعالى بعنايته من آل فرعون الذين كانوا يذيقونكم أشد العذاب، ويسخرونكم لخدمتهم فى مشاق الأعمال، ولا يرون لكم حرمة كالبهائم، فيقتلون ما يولد لكم من الذكور، ويستبقون الإناث لكم لتزدادوا ضعفا بكثرتهن، وفيما نزل بكم من تعذيب فرعون لكم وإنجائكم منه، اختبار عظيم من ربكم ليس وراءه بلاء واختبار.',NULL,'228',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1100,'<span data-type="title" id=toc-1107>١٤٢ </span>- وواعدنا موسى بالمناجاة وإعطاء التوراة عند تمام ثلاثين ليلة يتعبد فيها، وأتممنا مدة الوعد بعشر ليال يستكمل فيها عبادته، فصارت المدة أربعين ليلة، وقال موسى لأخيه هارون حين توجه للمناجاة: كن خليفتى فى قومى، وأصلح ما يحتاج إلى الإصلاح من أمورهم، واحذر أن تتبع طريق المفسدين.',NULL,'228',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1101,'<span data-type="title" id=toc-1108>١٤٣ </span>- ولما جاء لمناجاتنا، وكلَّمه ربه تكليما ليس كتكليمنا، قال رب أرنى ذاتك، وتجلَّ لى أنظر إليك فأزداد شرفا، قال: لن تطيق رؤيتى. ثم أراد سبحانه أن يقنعه بأنه لا يطيقها فقال: لكن انظر إلى الجبل الذى هو أقوى منك، فإن ثبت مكانه عند التجلى فسوف ترانى إذا تجليت لك. فلما ظهر ربه للجبل على الوجه اللائق به تعالى، جعله مفتتا مستويا بالأرض، وسقط موسى مغشياً عليه لهول ما رأى، فلما أفاق من صعقته قال: أنزهك يا رب تنزيها عظيما عن أن تُرى فى الدنيا، إنى تبت إليك من الإقدام على السؤال بغير إذن، وأنا أول المؤمنين فى زمانى بجلالك وعظمتك.',NULL,'228',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1102,'<span data-type="title" id=toc-1109>١٤٤ </span>- لما منع اللَّه موسى من رؤيته، عدد عليه نعمه ليتسلى بها عن المنع فقال: يا موسى إنى فضلتك واخترتك على أهل زمانك، بتبليغ أسفار التوراة وبتكليمى إياك من غير واسطة، فخذ ما فضلتك به، واشكرنى كما يفعل الشاكرون المقدرون للنعم.',NULL,'228',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1103,'<span data-type="title" id=toc-1110>١٤٥ </span>- وبينا لموسى فى ألواح التوراة كل شئ من المواعظ والأحكام المفضلة التى يحتاج الناس إليها فى المعاش والمعاد، وقلنا له: خذ الألواح بجد وحزم، وأمر قومك أن يأخذوا بأفضل ما فيها، كالعفو بدل القصاص، والإبراء بدل الانتظار، واليسر بدل العسر. سأريكم يا قوم موسى فى أسفاركم دار الخارجين على أوامر اللَّه، وما صارت إليه من الخراب لتعتبروا، فلا تخالفوا حتى لا يصيبكم ما أصابهم.',NULL,'229',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1104,'<span data-type="title" id=toc-1111>١٤٦ </span>- سأمنع من التفكير فى دلائل قدرتى القائمة فى الأنفس والأفاق، أولئك الذين يتطاولون فى الأرض ويتكبرون عن قبول الصواب غير محقين، وإن يروا كل آية تدل على صدق رسلنا لا يصدقوها، وإن يشاهدوا طريق الهدى لا يسلكوه، وإن يشاهدوا طريق الضلال يسلكوه. يحدث ذلك منهم بسبب أنهم كذبوا بآياتنا المنزلة، وغفلوا عن الاهتداء بها.',NULL,'229',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1105,'<span data-type="title" id=toc-1112>١٤٧ </span>- والذين كذبوا بآياتنا المنزلة على رسلنا للهداية، وكذبوا بلقائنا يوم القيامة، فأنكروا البعث والجزاء، بطلت أعمالهم التى كانوا يرجون نفعها فلا يلقون إلا جزاء ما استمروا على عمله من الكفر والمعاصى.',NULL,'229',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1106,'<span data-type="title" id=toc-1113>١٤٨ </span>- وبعد أن ذهب موسى إلى الجبل لمناجاة ربه، اتخذ قومه من حليهم المخصصة للزينة جسما على صورة العجل الذى لا يعقل ولا يميز، له صوت يشبه صوت البقر، مما أودع فيه من الصناعة ومرور الريح بداخله. . وقد صنعه لهم السامرى وأمرهم بعبادته. يا لسفاهة عقولهم. ألم يروا حين أتخذوه إلها وعبدوه أنه لا يكلمهم ولا يقدر على هدايتهم إلى طريق الصواب؟! إنهم ظلموا أنفسهم بهذا العمل الشنيع.',NULL,'229',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1107,'<span data-type="title" id=toc-1114>١٤٩ </span>- ولما شعروا بزلتهم وخطئهم، تحيروا وندموا أشد الندم على اتخاذ العجل إلها. وتبينوا ضلالهم تبيناً ظاهراً، وقالوا: واللَّه لئن لم يتب علينا ربنا ويتجاوز عنا لنكونن من الذين خسروا خسراناً مبينا، بوضعهم العبادة فى غير موضعها.',NULL,'229',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1108,'<span data-type="title" id=toc-1115>١٥٠ </span>- ولما رجع موسى من مناجاة ربه إلى قومه، غضبان عليهم لعبادتهم العجل، حزينا لأن اللَّه فتنهم - وكان اللَّه قد اخبره بذلك قبل رجوعه - فقال لهم: ما أقبح ما فعلتم بعد غيبتى، أسَبَقْتم بعبادة العجل ما أمركم به ربكم من انتظارى وحفظ عهدى حتى آتيكم بالتوراة؟! ووضع الألواح، واتجه إلى أخيه لشدة حزنه حين رأى ما رأى من قومه، وأخذ يشد أخاه من رأسه ويجره نحوه من شدة الغضب، ظنا منه أنه قصر فى كفَّهم عما فعلوا، فقال هارون لموسى. يا ابن أمى إن القوم حين فعلوا ما فعلوا قد استضعفونى وقهرونى، وقاربوا قتلى لما نهيتهم عن عبادة العجل، فلا تَسُر الأعداء بإيذائك لى، ولا تعتقدنى واحدا من الظالمين مع براءتى منهم ومن ظلمهم.',NULL,'230',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1109,'<span data-type="title" id=toc-1116>١٥١ </span>- قال موسى: رب اغفر لى ما صنعت بأخى قبل جلية الأمر، واغفر لأخى إن كان فَرّط فى حسن الخلافة، وأدخلنا فى سعة رحمتك لأنك أكثر الراحمين رحمة.',NULL,'230',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1110,'<span data-type="title" id=toc-1117>١٥٢ </span>- إن الذين استمروا على اتخاذ العجل إلها، كالسامرى وأشياعه، سينالهم غضب عظيم من ربهم فى الدار الآخرة، ومهانة شديدة فى الحياة الدنيا، بمثل ذلك الجزاء نجزى كل من اختلق الكذب على اللَّه وعبد غيره.',NULL,'230',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1111,'<span data-type="title" id=toc-1118>١٥٣ </span>- والذين عملوا الأعمال القبيحة من الكفر وعبادة العجل والمعاصى، ثم رجعوا إلى اللَّه من بعد عملها، وصدقوا به، إن ربك من بعد توبتهم ستَّار عليهم، غفَّار لما كان منهم.',NULL,'230',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1112,'<span data-type="title" id=toc-1119>١٥٤ </span>- ولما ذهب عن موسى الغضب باعتذار أخيه، عاد إلى الألواح التى ألقاها وأخذها، وفيما نُسِخَ فيها هدى وإرشاد وأسباب رحمة، للذين يخافون غضب ربهم.',NULL,'230',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1113,'<span data-type="title" id=toc-1120>١٥٥ </span>- ثم أمر اللَّه موسى أن يأتيه فى جماعة من قومه يعتذرون عَمَّن عبدوا العجل، ووعدهم موْعدا، فاختار موسى من قومه سبعين رجلا ممن لم يعبدوا العجل، وهم يمثلون قومه، وذهب بهم إلى الطور، وهنالك سألوا اللَّه أن يكشف عنهم البلاء، ويتوب على من عبد العجل منهم، فأخذتهم فى ذلك المكان زلزلة شديدة غشى عليهم بسببها، وهذا لأنهم لم يفارقوا قومهم حين عبدوا العجل، ولم يأمروهم بالمعروف، ولم ينهوهم عن المنكر، فلما رأى موسى ذلك قال: يا رب لو شئت إهلاكهم أهلكتهم من قبل خروجهم إلى الميقات، وأهلكتنى معهم، ليرى ذلك بنو إسرائيل فلا يتهمونى بقتلهم فلا تهلكنا يا رب بما فعل الجُهَّال منا، فما محنة عبدة العجل إلا فتنة منك، أضللت بها من شئت إضلاله ممن سلكوا سبيل الشر، وهديت بها من شئت هدايته. وأنت ولينا فاغفر لنا وارحمنا.',NULL,'231',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1114,'<span data-type="title" id=toc-1121>١٥٦ </span>- ولأنك يا رب خير من يغفر نسألك أن تقدّر لنا فى هذه الدنيا حياة طيبة، وتوفيقا للطاعة، وفى الآخرة مثوبة حسنة ورحمة، لأننا رجعنا إليك وتبنا إليك، فقال له ربه: عذابى أصيب به من أشاء ممن لم يتب، ورحمتى وسعت كل شئ، وسأكتبها للذين يتقون الكفر والمعاصى من قومك، ويؤدون الزكاة المفروضة، والذين يصدقون بجميع الكتب المنزلة.',NULL,'231',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1115,'<span data-type="title" id=toc-1122>١٥٧ </span>- وأخص بها الذين يتبعون الرسول محمدا، الذى لا يكتب ولا يقرأ، وهو الذى يجدون وصفه مكتوبا عندهم فى التوراة والإنجيل، يأمرهم بكل خير وينهاهم عن كل شر، ويحل لهم الأشياء التى يستطيبها الطبع، ويُحرم عليهم الأشياء التى يستخبثها الطبع كالدم والميتة، ويزيل عنهم الأثقال والشدائد التى كانت عليهم. فالذين صدقوا برسالته وآزروه وأيدوه ونصروه على أعدائه، واتبعوا القرآن الذى أنزل معه كالنور الهادى، أولئك هم الفائزون دون غيرهم ممن لم يؤمنوا به.',NULL,'231',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1116,'<span data-type="title" id=toc-1123>١٥٨ </span>- قل - يأيها النبى - للناس: إنى مرسل من اللَّه إليكم جميعا، لا فرق بين عربى وعجمى وأسود وأبيض واللَّه الذى أرسلنى له - وحده - ملك السموات والأرض يدبر أمرهما حسب حكمته، ويتصرف فيهما كيف يشاء، ولا معبود بحق إلا هو، وهو الذى يقدر على الإحياء والإماتة دون غيره، فآمنوا به وبرسوله النبى الذى لا يقرأ ولا يكتب، وهو يؤمن باللَّه الذى يدعوكم إلى الإيمان، ويؤمن بكتبه المنزلة، واتبعوه فى كل ما يفعل ويقول لتهتدوا وترشدوا.',NULL,'232',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1117,'<span data-type="title" id=toc-1124>١٥٩ </span>- ومن قوم موسى جماعة بقوا على الدين الصحيح يهدون الناس بالحق الذى جاء به موسى من عند ربه، ويعدلون فى تنفيذه إذا حكموا.',NULL,'232',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1118,'<span data-type="title" id=toc-1125>١٦٠ </span>- عدَّد اللَّه نعمه على قوم موسى، فأفاد أنه صيَّرهم اثنتى عشرة فرقة وجعلهم جماعات، وميَّز كل جماعة بنظامها، منعا للتحاسد والخلاف، وأوحى إلى موسى حين طلب منه قومه الماء فى التيه، بأن يضرب الحجر بعصاه، فضربه فانفجرت اثنتا عشرة عينا بعدد الأسباط، وقد عرف كل جماعة منهم مكان شربهم الخاص بهم، فلا يزاحمهم فيه غيرهم، وجعل لهم السحاب يلقى عليهم ظله فى التيه، ليقيهم حر الشمس، وأنزل عليهم المن، وهو طعام يشبه البرد فى منظره، ويشبه الشهد فى مطعمه، وأنزل السلوى، وهو الطير السمانى، وقال لهم: كلوا من مستلذات ما رزقناكم مما أنزلناه عليكم. فظلموا أنفسهم وكفروا بتلك النعم، وطلبوا غيرها، وما رجع إلينا ضرر ظلمهم ولكنه كان مقصورا عليهم.',NULL,'232',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1119,'<span data-type="title" id=toc-1126>١٦١ </span>- واذكر - يأيها النبى - لمن وجد منهم فى زمانك، تقريعا لهم بما فعل أسلافهم، اذكر لهم قولنا لأسلافهم على لسان موسى: اسكنوا مدينة بيت المقدس بعد الخروج من التيه، وكلوا من خيراتها فى أية ناحية من نواحيها شئتم، وقولوا نسألك يا ربنا أن تحط عنا خطايانا، وادخلوا باب القرية مع انحناء الرءوس كهيئة الركوع تواضعا للَّه. إذا فعلتم ذلك تجاوزنا عن ذنوبكم، وسنزيد ثواب من أحسنوا الأعمال.',NULL,'232',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1120,'<span data-type="title" id=toc-1127>١٦٢ </span>- فخالفوا أمر ربهم، فقالوا بسبب ظلمهم قولا غير الذى قيل لهم قصد الاستهزاء بموسى، فأنزلنا عليهم عذابا من السماء بسبب استمرارهم على الظلم وتجاوز الحد.',NULL,'233',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1121,'<span data-type="title" id=toc-1128>١٦٣ </span>- واسأل اليهود - استنكارَا لما فعل أسلافهم - عن خبر أهل قرية - أيلة - التى كانت قريبة من البحر، حين كانوا يتجاوزون حدود الله بصيد السمك فى يوم السبت، وحين كانت تأتيهم حيتان الأسماك وتظهر على وجه الماء يوم السبت، وفى غيره لا تأتيهم، ابتلاء من اللَّه. بمثل ذلك البلاء المذكور نبلوهم بلاء آخر بسبب فسقهم المستمر ليظهر منهم المحسن من المسئ.',NULL,'233',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1122,'<span data-type="title" id=toc-1129>١٦٤ </span>- واذكر أيضا لهؤلاء اليهود إذ قالت جماعة من صلحاء أسلافهم - لم يقعوا فيما وقع فيه غيرهم - لمن يعظون أولئك الأشرار: لأى سبب تنصحون قوما اللَّه مهلكهم بسبب ما يرتكبون أو معذبهم فى الآخرة عذابا شديدا؟! قالوا: وعظناهم اعتذاراً إلى ربكم، لئلا ننسب إلى التقصير، ورجاء أن يتقوا.',NULL,'233',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1123,'<span data-type="title" id=toc-1130>١٦٥ </span>- فلما تركوا ما وعظوا به، أنجينا الذين ينهون عن العمل السئ من العذاب، وأخذنا الذين ظلموا فاعتدوا وخالفوا بعذاب شديد، هو البؤس والشقاء. بسبب استمرارهم على الخروج عن طاعة اللَّه ربهم.',NULL,'233',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1124,'<span data-type="title" id=toc-1131>١٦٦ </span>- فلما قسوا واستمروا على ترك ما نهوا عنه، ولم يردعهم العذاب الشديد، جعلناهم كالقردة فى مسخ قلوبهم وعدم توفيقهم لفهم الحق، مبعدين عن كل خير.',NULL,'233',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1125,'<span data-type="title" id=toc-1132>١٦٧ </span>- واذكر أيضا لهؤلاء اليهود حين أعلم ربك أسلافهم على ألسنة أنبيائهم: ليسلِّطنَّ اللَّه على جماعة اليهود إلى يوم القيامة من يوقع بهم أسوأ أنواع العذاب على ظلمهم وفسقهم، لأن ربك سريع العقاب لأهل الكفر، لأن عقابه واقع لا محالة، وكل آت قريب،، إنه غفور رحيم لمن رجع إليه وتاب.',NULL,'234',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1126,'<span data-type="title" id=toc-1133>١٦٨ </span>- وقد فرقناهم فى الأرض جماعات: منهم الصالحون، وهم الذين آمنوا واستقاموا، ومنهم أناس منحطون عن وصف الصلاح، وقد اختبرناهم جميعا بالنعم والنقم ليتوبوا عما نهوا عنه.',NULL,'234',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1127,'<span data-type="title" id=toc-1134>١٦٩ </span>- فجاء من بعد الذين ذكرناهم وقسمناهم إلى القسمين، خلف سُوءٍ ورثوا التوراة عن أسلافهم ولكنهم لم يعملوا بها، لأنهم يأخذون متاع الدنيا عوضا عن قول الحق، ويقولون فى أنفسهم: سيغفر اللَّه لنا ما فعلناه. يرجون المغفرة. والحال أنهم إن يأتهم شئ مثل ما أخذوه يأخذوه. فهم مصرون على الذنب مع طلب المفغرة، ثم وبخهم الله على طلبهم المغفرة مع إصرارهم على ما هم عليه، فقال: إنا أخذنا عليهم العهد فى التوراة، وقد درسوا ما فيها، أن يقولوا الحق، فقالوا الباطل، وإن نعيم الدار الآخرة للذين يتقون المعاصى خير من متاع الدنيا. أتستمرون على عصيانكم فلا تعقلون أن ذلك النعيم خير لكم، وتؤثرون عليه متاع الدنيا؟',NULL,'234',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1128,'<span data-type="title" id=toc-1135>١٧٠ </span>- والذين يتمسكون بالتوراة، وأقاموا الصلاة المفروضة عليهم، إنا لا نضيع أجرهم، لإصلاحهم وإحسانهم الأعمال.',NULL,'234',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1129,'<span data-type="title" id=toc-1136>١٧١ </span>- رد اللَّه على اليهود فى قولهم: إن بنى إسرائيل لم تصدر منهم مخالفة فى الحق، فقال: واذكر لهم - أيها النبى - حين رفعنا الجبل فوق رؤوس بنى إسرائيل كأنه غمامة، وفزعوا لظنهم أنه واقع عليهم، وقلنا لهم - فى حالة الرفع ورهبتهم - خذوا ما أعطيناكم من هدى فى التوراة بجد وعزم على الطاعة، وتذكروا ما فيه لعلكم تعتبرون وتتهذب نفوسكم بالتقوى.',NULL,'234',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1130,'<span data-type="title" id=toc-1137>١٧٢ </span>- بيَّن اللَّه هنا هداية بنى آدم بنصب الأدلة فى الكائنات، بعد أن بيَّنها عن طريق الرسل والكتب، فقال: واذكر - أيها النبى - للناس حين أخرج ربك من أصلاب بنى أدم ونسلهم وما يتوالدون قرنا بعد قرن، ثم نصب لهم دلائل ربوبيته فى الموجودات، وركز فيهم عقولا وبصائر يتمكنون بها من معرفتنا، والاستدلال بها على التوحيد والربوبية، حتى صاروا بمنزلة من قيل لهم: ألست بربكم؟ قالوا: بل أنت ربنا شهدنا بذلك على أنفسنا، لأن تمكينهم من العلم بالأدلة وتمكنهم منه فى منزلة الإقرار والاعتراف. وإنما فعلنا هذا لئلا تقولوا يوم القيامة: إنا كنا عن هذا التوحيد غافلين لا نعرفه.',NULL,'235',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1131,'<span data-type="title" id=toc-1138>١٧٣ </span>- أو تقولوا: إنما أشرك آباؤنا من قبلنا، وكنا ذرية لهم فاقتدينا بهم، أفتؤاخذنا يا رب فتهلكنا بما فعل المبطلون من آبائنا بتأسيس الشرك الذى جرونا إليه. . فلا حجة لكم.',NULL,'235',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1132,'<span data-type="title" id=toc-1139>١٧٤ </span>- ومثل ذلك البيان الحكيم نُبيِّن لبنى آدم الدلائل على وجود الله، ليرجعوا عن مخالفتهم وتقليد المبطلين.',NULL,'235',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1133,'<span data-type="title" id=toc-1140>١٧٥ </span>- ثم ضرب الله مثلا للمكذبين بآياته المنزلة على رسوله، فقال: واقرأ - أيها النبى - على قومك خبر رجل من بنى إسرائيل آتيناه علما بآياتنا المنزلة على رسلنا، فأهملها ولم يلتفت إليها، فأتبعه الشيطان خطواته، وسلط عليه بإغوائه فصار فى زمرة الضالين.',NULL,'235',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1134,'<span data-type="title" id=toc-1141>١٧٦ </span>- ولو شئنا رفعه إلى منازل الأبرار لرفعناه إليها، بتوفيقه للعمل بتلك الآيات، ولكنه تعلق بالأرض ولم يرتفع إلى سماء الهداية، واتبع هواه، فصار حاله فى قلقه الدائم، وانشغاله بالدنيا، وتفكيره المتواصل فى تحصيلها كحال الكلب فى أسوأ أحواله عندما يلهث دائما، إن زجرته أو تركته، إذ يندلع لسانه من التنفس الشديد، وكذلك طالب الدنيا يلهث وراء متعه وشهواته دائما. إن ذلك الوصف الذى اتصف به المنسلخ من آياتنا، هو وصف جميع الذين كذبوا بآياتنا المنزلة. فاقصص عليهم قصصه ليتفكروا فيؤمنوا.',NULL,'236',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1135,'<span data-type="title" id=toc-1142>١٧٧ </span>- قُبحتْ حال هؤلاء الذين جحدوا آياتنا، وما ظلموا بهذا الانحراف عن الحق إلا أنفسهم.',NULL,'236',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1136,'<span data-type="title" id=toc-1143>١٧٨ </span>- من يوفقه الله لسلوك سبيل الحق فهو المهتدى حقا، الفائز بسعادة الدارين، ومن يحرم من هذا التوفيق بسبب سيطرة هواه، فهذا الفريق هم الخاسرون.',NULL,'236',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1137,'<span data-type="title" id=toc-1144>١٧٩ </span>- ولقد خلقنا كثيرا من الجن والإنس مآلهم النار يوم القيامة، لأن لهم قلوبا لا ينفذون بها إلى الحق، ولهم أعين لا ينظرون بها دلائل القدرة، ولهم آذان لا يسمعون بها الآيات والمواعظ سماع تدبر وإتعاظ. أولئك كالبهائم لعدم انتفاعهم بما وهبهم الله من عقول للتدبر، بل هم أضل منها، لأنها تطلب منافعها وتهرب من مضارها، وهؤلاء لا يدركون ذلك، أولئك هم الكاملون فى الغفلة.',NULL,'236',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1138,'<span data-type="title" id=toc-1145>١٨٠ </span>- وللَّه - دون غيره - الأسماء الدالة على أكمل الصفات، فأجروها عليه دعاء ونداء وتسمية، وابتعدوا عن الذين يميلون فيها إلى ما لا يليق بذاته العلية وإنهم سيُجْزُون جزاء أعمالهم.',NULL,'236',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1139,'<span data-type="title" id=toc-1146>١٨١ </span>- وممن خلقنا للجنة طائفة يدعون غيرهم للحق بسبب حبهم الحق، وبالحق - وحده - يعدلون فى أحكامهم.',NULL,'236',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1140,'<span data-type="title" id=toc-1147>١٨٢ </span>- والذين كذبوا بآياتنا المنزلة سنستدرجهم ونتركهم حتى يصلوا إلى أقصى غاياتهم، وذلك بإدرار النعم عليهم، مع انهماكهم فى الغنى، حتى يفاجئهم الهلاك وهم غافلون يرتعون.',NULL,'237',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1141,'<span data-type="title" id=toc-1148>١٨٣ </span>- وسأمد لهم فى الحياة غير مُهملِ لسيئاتهم، وتدبيرى لهم شديد عليهم، يكافئ سيئاتهم التى كثرت بتماديهم.',NULL,'237',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1142,'<span data-type="title" id=toc-1149>١٨٤ </span>- لقد بادروا بالتكذيب، ولم يتدبروا ما يدعوهم الرسول إليه، وما يقدمه من حجج، بل رموه بالجنون وليس به من جنون، فما هو إلا مُنَذِر لهم من عاقبة شركهم، وإنذاره بين واضح.',NULL,'237',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1143,'<span data-type="title" id=toc-1150>١٨٥ </span>- لقد كذبوا محمدا فيما يدعوهم إليه من توحيد، ولم ينظروا نظر تأمل واستدلال فى ملك الله العظيم للسموات والأرض وما فيها، مما يدل على كمال قدرة الصانع ووحدانيته، ولم يفكروا فى أنه قد اقترب أجلهم، أو عسى أن يكون قد اقترب، فيسارعوا إلى النظر وطلب الحق قبل مفاجأة الأجل، فإذا لم يؤمنوا بالقرآن فبأى كلام يؤمنون بعده؟',NULL,'237',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1144,'<span data-type="title" id=toc-1151>١٨٦ </span>- من يكتب الله عليه الضلالة لسوء اختياره فلا يهديه أحد، ويتركهم - سبحانه - فى ضلالهم يتحيرون لا يهتدون سبيلا.',NULL,'237',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1145,'<span data-type="title" id=toc-1152>١٨٧ </span>- يسألك اليهود - يا محمد - عن الساعة التى تنتهى فيها هذه الدنيا، فى أى وقت تكون ويستقر العلم بها؟ قل لهم: علم وقتها عند ربى - وحده - لا يظهرها فى وقتها أحد سواه. قد عظم هولها عندما تقع إلى أهل السموات والأرض. يسألونك هذا السؤال، كأنك حريص على العلم بها. فكرر الجواب، فقل لهم مؤكدا: إن علمها عند اللَّه، ولكن أكثر الناس لا يدركون الحقائق التى تغيب عنهم، أو التى تظهر لهم! .',NULL,'237',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1146,'<span data-type="title" id=toc-1153>١٨٨ </span>- قل لهم: لا أملك لنفسى جلب نفع ولا دفع ضر إلا الذى شاء اللَّه من ذلك فيملكنى إياه. ولو كنت أعلم ما غاب عنى كما تظنون، لاستكثرت من كل خير، لعلمى بأسبابه، ولدفعت عن نفسى كل سوء باجتناب موجباته، ما أنا إلا نذير بالعذاب ومبشر بالثواب لقوم يؤمنون بالحق ويذعنون له.',NULL,'238',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1147,'<span data-type="title" id=toc-1154>١٨٩ </span>- هو اللَّه الذى أنشأكم من نفس واحدة، وجعل من جنسها زوجها، واستمرت سلالتهما فى الوجود. وكنتم زوجاً وزوجة، فإذا تغشاها حملت محمولاً خفيفا هو الجنين عند كونه علقة ومضغة، فلما ثقل الحمل فى بطنها دعا الزوج والزوجة ربهما قائلين: واللَّه لئن أعطيتنا ولدا سليما من فساد الخلقة، لنكونن من الشاكرين لنعمائك.',NULL,'238',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1148,'<span data-type="title" id=toc-1155>١٩٠ </span>- فلما أعطاهما ما طلبا جعلا الأصنام شركاء للَّه تعالى فى عطيته الكريمة، وتقربا إليها، كأنهما يشكرانها، واللَّه - وحده - هو المستحق للشكر يتعالى ويتسامى عن أن يكون كشركائهم.',NULL,'238',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1149,'<span data-type="title" id=toc-1156>١٩١ </span>- هل يصح أن يشركوا مع الله أصناما لا تقدر أن تخلق شيئا من الأشياء وهم مخلوقون للَّه؟! .',NULL,'238',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1150,'<span data-type="title" id=toc-1157>١٩٢ </span>- ولا يقدرون على نصر لمن يعبدونهم، ولا ينصرون أنفسهم إذا تعدى الغير عليهم.',NULL,'238',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1151,'<span data-type="title" id=toc-1158>١٩٣ </span>- وإن تدعوا - أيها العابدون - الأصنام ليرشدوكم إلى ما تحبون، لا يجيبوكم إلى مرادكم، فمستوٍ عندكم فى عدم الفائدة دعاؤكم إياهم، وسكونكم، فإنه لا يتغير حالهم فى الحالتين.',NULL,'238',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1152,'<span data-type="title" id=toc-1159>١٩٤ </span>- إن الذين تعبدون من غير اللَّه، وترجون النفع منهم، خاضعون للَّه بحكم تكوينهم، من حيث كونهم مسخرين لأمره مثلكم، فإن كنتم صادقين فى زعمكم أنهم يقدرون على شئ، فاطلبوه منهم فلن يحققوه لكم.',NULL,'239',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1153,'<span data-type="title" id=toc-1160>١٩٥ </span>- بل إن هذه الأصنام أقل منكم فى الخلق والتكوين، ألهم أرجل يمشون بها؟ أو أيد يدفعون بها الضر عنكم وعنهم؟ أو أعين يبصرون بها؟ أو آذان يسمعون بها ما تطلبون فيحققوه لكم؟ ليس لهم شئ من ذلك، فكيف تشركونهم مع الله؟ ، وإذا كنتم تتوهمون أنها تنزل الضر بى أو بأحد، فنادوها ودبروا لى معها ما تشاءون من غير إمهال ولا انتظار، فإنها لن تستطيع شيئا، فلا تمهلونى فإنى لا أبالى بها.',NULL,'239',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1154,'<span data-type="title" id=toc-1161>١٩٦ </span>- إن ناصرى عليكم هو اللَّه الذى له ولايتى، وهو الذى أنزل على القرآن، وهو - وحده - الذى ينصر الصالحين من عباده.',NULL,'239',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1155,'<span data-type="title" id=toc-1162>١٩٧ </span>- والأصنام الذين تطلبون منهم النصر دون اللَّه، لا يستطيعون نصركم ولا نصر أنفسهم.',NULL,'239',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1156,'<span data-type="title" id=toc-1163>١٩٨ </span>- وإن تسألوهم الهداية إلى ما فيه خيركم لا يسمعوا سؤالكم فضلا عن إرشادكم، وإنك لتراهم - فى مقابلك - كأنما ينظرون إليك، وهم فى الحقيقة لا يرون شيئا.',NULL,'239',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1157,'<span data-type="title" id=toc-1164>١٩٩ </span>- أعرض - أيها النبى - عن الجاهلين، وسر فى سبيل الدعوة، وخذ الناس بما يسهل، وأمرهم بكل أمر مستحسن تعرفه العقول وتدركه.',NULL,'239',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1158,'<span data-type="title" id=toc-1165>٢٠٠ </span>- وإن تعرض لك من الشيطان وسوسة لصرفك عما أمرت، كأن تغضب من لجاجتهم بالشر، فاستجر باللَّه يصرفه عنك، لأنه سميع لكل ما يقع عليم به.',NULL,'239',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1159,'<span data-type="title" id=toc-1166>٢٠١ </span>- إن الذين خافوا ربهم، وجعلوا بينهم وبين المعاصى وقاية من الشيطان بوسوسة منه طافت بهم لصرفهم عما يجب عليهم تذكروا عداوة الشيطان وكيده، فإذا هم مبصرون الحق فيرجعون.',NULL,'240',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1160,'<span data-type="title" id=toc-1167>٢٠٢ </span>- وإخوان الشياطين من الكفار، تزيدهم الشياطين بالوسوسة ضلالا، ثم هؤلاء الكفار لا يُكفون عن ضلالهم بالتبصر.',NULL,'240',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1161,'<span data-type="title" id=toc-1168>٢٠٣ </span>- وإذا لم تأت الكفار بآية مما يطلبون عناداً وكفراً، قالوا: هلا طلبتها؟ قل لهم: ما أتَّبع إلا القرآن الذى يوحى إلى من ربى، وقل لهم: هذا القرآن حُجج من ربكم تبصركم وجوه الحق، وهو ذو هداية ورحمة للمؤمنين، لأنهم العاملون به.',NULL,'240',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1162,'<span data-type="title" id=toc-1169>٢٠٤ </span>- وإذ تُلِىَ عليكم - أيها المؤمنون - القرآن فاصغوا إليه بأسماعكم. لتتدبَّروا مواعظه، وأحسنوا الاستماع لتفوزوا بالرحمة.',NULL,'240',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1163,'<span data-type="title" id=toc-1170>٢٠٥ </span>- واذكر ربك ذكراً نفسياً، تحس فيه بالتقرب إلى الله والخضوع له والخوف منه، من غير صياح، بل فوق السر ودون الجهر من القول. وليكن ذكرك فى طرفى النهار لتفتتح نهارك بالذكر لربك وتختمه به، ولا تكُنْ فى عامة أوقاتك من الغافلين عن ذكر الله.',NULL,'240',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1164,'<span data-type="title" id=toc-1171>٢٠٦ </span>- إن الذين هم قريبون من ربك بالتشريف والتكريم، لا يستكبرون عن عبادته، وينزهونه عما لا يليق به، وله يخضعون.',NULL,'240',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1165,'<span data-type="title" id=toc-1173>١ </span>- أُخرج النبى من مكة مهاجراً بسبب مكر المشركين وتدبيرهم أمر قتله، وليكون للمسلمين دولة، واستقر بالمدينة حيث النصرة، وكان لا بد من الجهاد لدفع الاعتداء، لكيلا يُفتَنَ أهل الإيمان، فكانت غزوة بدر الكبرى، وكان فيها النصر المبين والغنائم، وكان وراء الغنائم بعض الاختلاف والتساؤل فى توزيعها. يسألونك عن الغنائم: ما مآلها؟ ولمن تكون؟ وكيف تقسم؟ فقل لهم - أيها النبى -: إنها لله والرسول ابتداء، والرسول بأمر ربه يتولى تقسيمها، فاتركوا الاختلاف بشأنها، واجعلوا خوف الله وطاعته شعاركم، وأصْلحوا ما بينكم، فاجعلوا الصِّلاتِ بينكم محبة وعدلا، فإن هذه صفة أهل الإيمان.',NULL,'241',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1166,'<span data-type="title" id=toc-1174>٢ </span>- إن المؤمنين حقاً وصدقاً يستشعرون دائماً خوف الله وطاعته، فإذا ذُكر سبحانه فزعت قلوبهم، وامتلأت هيبة، ولذا كلما قرئت عليهم آيات من القرآن ازداد إيمانهم رسوخاً، وازدادوا إذعاناً وعلماً، ولا يعتمدون إلا على الله الذى خلقهم ويحميهم وينميهم.',NULL,'242',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1167,'<span data-type="title" id=toc-1175>٣ </span>- وأولئك المؤمنون الصادقون فى الإيمان، يؤدون الصلاة مستوفية الأركان، كاملة الخشوع والخضوع، ليكونوا على تذكر الله دائماً، وينفقون مقادير من المال الذى رزقهم الله - سبحانه وتعالى - فى الجهاد والبر ومعاونة الضعفاء.',NULL,'242',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1168,'<span data-type="title" id=toc-1176>٤ </span>- إن هؤلاء المتصفين بتلك الصفات، هم الذين يوصفون بالإيمان حقاً وصدقاً، ولهم جزاؤهم درجات عالية عند الله، وهو الذى يمنحهم رضاه، ويغفر لهم هفواتهم ويرزقهم رزقاً طيباً فى الدنيا، ونعيماً دائماً فى الآخرة.',NULL,'242',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1169,'<span data-type="title" id=toc-1177>٥ </span>- وإن النصر بيد الله، ومقاليد الأمور إليه، وإنَّ حال المؤمنين فى خلافهم حول الغنائم كحالهم عندما أمرك الله بالخروج لقتال المشركين ببدر، وهو حق ثابت، فإن فريقاً من أولئك المؤمنين كانوا كارهين للقتال مؤكدين كراهيتهم.',NULL,'242',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1170,'<span data-type="title" id=toc-1178>٦ </span>- يناظرك أولئك الفريق، ويحاولون أن ينصروا قولهم فى الأمر الحق، وهو الخروج للجهاد، إذ كانوا مع إخوانهم الذين خرجوا لمصادرة أموال قريش الذاهبة إلى الشام، فلم يدركوها، فآثر هذا الفريق العودة من بعد ما تبين أنهم منصورون، لإعلام النبى لهم، ولذعر المشركين منهم، ولشدة كراهيتهم للقتال وعدم أمنهم من عواقبه، وكانوا فى ذهابهم إليه كالذى يساق إلى الموت، وهو ينظر أسبابه ويعاينها.',NULL,'242',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1171,'<span data-type="title" id=toc-1179>٧ </span>- واذكروا - أيها المؤمنون - وعد الله تعالى لكم أن ينصركم على إحدى الطائفتين التى فيها الشوكة والقوة، وأنتم تودون أن تدركوا الطائفة الأخرى التى فيها المال والرجال، وهى قافلة أبى سفيان، فاخترتم المال ولا شوكة فيه، ولكن الله تعالى يريد أن يثبت الحق بإرادته وقدرته وكلماته المعلنة للإرادة والقدرة، ويستأصل الكفر من بلاد العرب بنصر المؤمنين.',NULL,'243',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1172,'<span data-type="title" id=toc-1180>٨ </span>- ليثبت الحق ويزيل الباطل، ولو كره ذلك الكافرون الذين أجرموا فى حق الله، وفى حق المؤمنين وفى حق أنفسهم.',NULL,'243',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1173,'<span data-type="title" id=toc-1181>٩ </span>- اذكروا - أيها المؤمنون، وأنتم تتقاسمون الغنائم وتختلفون - الوقت الذى كنتم تتجهون فيه إلى الله تعالى، طالبين منه الغوث والمعونة، إذ كتب عليكم أنه لا خلاص من القتال، فأجاب الله دعاءكم، وأمدَّكم بملائكة كثيرة تبلغ الألف متتابعة، يجئ بعضها وراء بعض.',NULL,'243',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1174,'<span data-type="title" id=toc-1182>١٠ </span>- وما جعل الله تعالى ذلك الإمداد بالملائكة إلا بشارة لكم بالنصر، لتطمئنوا وتقدموا، والله يعينكم، والنصر لا يجئ إلا بمعونة الله القوى الغالب، الذى يضع الأمور فى مواضعها بمقتضى علمه الذى لا يغيب عنه شئ.',NULL,'243',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1175,'<span data-type="title" id=toc-1183>١١ </span>- اذكروا - أيها المؤمنون - وقت أن خفتم من قلة الماء، ومن الأعداء، فوهبكم الله الأمن، وأصابكم النعاس فنمتم آمنين، وأنزل الماء من السماء لتتطهروا به، ولتذهبوا وساوس الشيطان عنكم، وثبت قلوبكم واثقة بعون الرحمن ولتتماسك به الأرض فتثبت الأقدام.',NULL,'243',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1176,'<span data-type="title" id=toc-1184>١٢ </span>- اذكروا - أيها المؤمنون - أن الله أوحى للملائكة أن تودع فى نفوسكم أنى معكم بالتأييد والنصر، قائلاً لهم: قووا قلوب الذين آمنوا وأذعَنوا للحق وجاهدوا فى سبيل الله، وسأجعل الرعب يستولى على قلوب المشركين، فيفزعون هم دونكم، فاضربوا - أيها المؤمنون - رءوسهم التى فوق أعناقهم، وقطعوا أصابعهم التى يحملون بها السيوف.',NULL,'244',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1177,'<span data-type="title" id=toc-1185>١٣ </span>- كان ذلك النصر والتأييد لكم، والرعب والفزع لهم، لأنهم تحدوا الله ورسوله، فكانوا فى جانب والله ورسوله فى جانب آخر، ومن يحاد الله ورسوله فإنه ينزل به العذاب الأليم لأن عقاب الله شديد.',NULL,'244',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1178,'<span data-type="title" id=toc-1186>١٤ </span>- ذلكم أيها المؤمنون هو القتال فذوقوه مع اليقين بالنصر والتأييد وأن للجاحدين بآياته عقاباً آخر يوم القيامة، هو عذاب النار.',NULL,'244',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1179,'<span data-type="title" id=toc-1187>١٥ </span>- أيها الذين صدقوا بالحق وأذعنوا له، إذا التقيتم بالذين كفروا فى الميدان، وهم زاحفون عليكم بكثرتهم، فلا تفروا منهم وتجعلوا ظهوركم أمام سيوفهم.',NULL,'244',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1180,'<span data-type="title" id=toc-1188>١٦ </span>- ومن لا يلاقهم وجهاً لوجه فاراً منهم، فإن الله يغضب عليه، ومصيره إلى النار، وهى أسوأ مصير لكم، ومن لا يلاقهم بوجهه كيداً ومهارة حربية، أو يترك طائفة لينحاز إلى طائفة أخرى من المؤمنين، لتكون قوة للقاء فإنه لا إثم عليه.',NULL,'244',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1181,'<span data-type="title" id=toc-1189>١٧ </span>- إذا كنتم - أيها المؤمنون - قد انتصرتم عليهم، وقتلتم من قتلتم منهم، فإنكم لم تقتلوهم بقوتكم، ولكن الله تعالى هو الذى نصركم وقتلهم بتأييده لكم وإلقاء الرعب فى قلوبهم، وما رميت - أيها الرسول - إذ كنت ترمى التراب والحصا فى وجوههم إفزاعاً لهم، ولكن الله تعالى هو الذى رمى فأفزعهم الرمى، وكان ذلك لينعم الله على المؤمنين نعماً حسنة، منها الابتلاء بالشدة، ليظهر إخلاصهم، وأن الله عليم بأمورهم، سميع لأقوالهم، وكذلك هو عليم بأمور أعدائهم وأقوالهم.',NULL,'245',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1182,'<span data-type="title" id=toc-1190>١٨ </span>- ذلك هو النصر العظيم، مع أن الله تعالى مُضْعِفٌ لكل تدابير الكافرين.',NULL,'245',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1183,'<span data-type="title" id=toc-1191>١٩ </span>- إن كنتم - أيها المشركون - تتعلقون بأستار الكعبة، طالبين الفصل بينكم وبين المؤمنين، فقد جاءكم الامر الفاصل، وليس نصراً لكم، بل هو نصر للمؤمنين، وإن تعودوا إلى الاعتداء نعد عليكم بالهزيمة، ولن تغنى عنكم جماعتكم المؤتلفة على الإثم شيئاً، ولو كان العدد عندكم كثيراً، فإن الله مع الذين صدقوا بالحق وأذعنوا له.',NULL,'245',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1184,'<span data-type="title" id=toc-1192>٢٠ </span>- يا أيها الذين صدَّقتم بالحق وأذعنتم له، قد علمتم أن النصر كان بتأييد الله وطاعة رسوله، فاستمروا على طاعتكم لله وللرسول، ولا تعرضوا عن دعوة الرسول إلى الحق وأنتم تسمعون وتعون ما يقول.',NULL,'245',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1185,'<span data-type="title" id=toc-1193>٢١ </span>- ولا تكونوا كالمنافقين الذين قالوا: سمعنا الحق ووعيناه، لكنهم لا يذعنون له ولا يؤمنون به، فكانوا كغير السامعين.',NULL,'245',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1186,'<span data-type="title" id=toc-1194>٢٢ </span>- إن أولئك المشركين والمنافقين معهم، هم كشر الدواب التى أصيبت بالصمم فلا تسمع، وبالبكم فلا تتكلم، فهم صمُّوا عن الحق، فلم يسمعوه ولم ينطقوا به ولم يعقلوه.',NULL,'245',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1187,'<span data-type="title" id=toc-1195>٢٣ </span>- ولو علم الله - بعلمه الأزلى - أن فيهم - وهم بهذه الحال - ما يكون خيراً لأنفسهم وللناس وللحق، لأسمعهم سماع هداية يوصل الحق إلى عقولهم، ولو سمعوه وفهموه لانصرفوا عن الاهتداء، وحال الإعراض الآن لا تفارقهم لغلبة الهوى.',NULL,'245',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1188,'<span data-type="title" id=toc-1196>٢٤ </span>- يا أيها الذين صدَّقوا بالحق وأذعنوا له، أجيبوا الله فى اتجاه قلبى إلى ما يأمركم به، وأجيبوا الرسول فى تبليغه ما يأمر به الله إذا دعاكم إلى أوامر الله بالأحكام التى فيها حياة أجسامكم وأرواحكم وعقولكم وقلوبكم، واعلموا علم اليقين أن الله تعالى قائم على قلوبكم، يوجهها كما يشاء فيحول بينكم وبين قلوبكم إذا أقبل عليها الهوى، فهو منقذكم منه إن اتجهتم إلى الطريق المستقيم، وإنكم جميعاً ستجمعون يوم القيامة فيكون الجزاء.',NULL,'246',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1189,'<span data-type="title" id=toc-1197>٢٥ </span>- واجعلوا وقاية بينكم وبين الذنب العظيم الذى يفسد جماعتكم، كالامتناع عن الجهاد، وكالشقاق، وكالامتناع عن الأمر بالمعروف والنهى عن المنكر، فإن ذلك الذنب لا يصيب الذين ظلموا - وحدهم - بل يصيب الجميع واعلموا علماً جازماً أن عقاب الله شديد فى الدنيا والآخرة.',NULL,'246',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1190,'<span data-type="title" id=toc-1198>٢٦ </span>- وتذكروا - أيها المؤمنون فى حال قوتكم - وقت أن كنتم عدداً قليلاً، وضعفاء يستغل أعداؤكم ضعفكم، وقد استولى عليكم الخوف من أن يتخطفكم أعداؤكم، فهاجرتم بأمر الله وجعل من يثرب مأوى لكم، وكان لكم النصر بتأييده وتوفيقه، ورزقكم الغنائم الطيبة رجاء أن تشكروا هذه النعم، فتسيروا فى طريق الجهاد لإعلاء كلمة الحق.',NULL,'246',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1191,'<span data-type="title" id=toc-1199>٢٧ </span>- يا أيها الذين صدَّقوا بالحق وأذعنوا له، لا يصح أن تكون منكم خيانة لله ورسوله بموالاة أعداء الحق، أو بالخيانة فى الغنائم، أو بالقعود عن الجهاد، ولا تخونوا فى الأمانات التى تكون بينكم، وأنتم تعلمون أوامر الله ونواهيه.',NULL,'246',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1192,'<span data-type="title" id=toc-1200>٢٨ </span>- واعلموا - أيها المؤمنون الصادقون - أن فتنة نفوسكم تجئ من فرط محبتكم لأولادكم، فلا تُغلِّبوا محبة المال والولد على محبة الله تعالى، فإن ذلك يفسد أموركم. واعلموا أن ثواب الله عظيم يجزيكم عن المال والولد.',NULL,'246',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1193,'<span data-type="title" id=toc-1201>٢٩ </span>- يا أيها الذين صدَّقوا بالحق وأذعنوا له، إن تخضعوا لأوامر الله فى السر والعلن، يجعل الله تعالى فى أنفسكم قدرة تفرقون بها بين الحق والباطل، ويهبكم نصراً ليفصل بينكم وبين أعدائكم، ويستر سيئاتكم فتذهب ويغفرها لكم، وهو - سبحانه - صاحب الفضل الكبير دائماً.',NULL,'247',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1194,'<span data-type="title" id=toc-1202>٣٠ </span>- واذكر - أيها النبى - نعمة الله عليك، إذ يمكر المشركون للإيقاع بك: إما بأن يحبسوك، وإما بأن يقتلوك، وإما بأن يخرجوك. إنهم يدبرون لك التدبير السيئ، والله تعالى يدبر لك الخروج من شرهم، وتدبير الله هو الخير وهو الأقوى والغالب.',NULL,'247',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1195,'<span data-type="title" id=toc-1203>٣١ </span>- واذكر - أيها النبى - معاندة المشركين عندما كنت تقرأ عليهم آيات القرآن الكريم، وهى آياتنا، فيذهب بهم فرط الجهل والغرور إلى أن يقولوا: لو أردنا أن نقول مثل هذا القرآن لقلنا، فما هو إلا ما سطره الأولون من قصص.',NULL,'247',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1196,'<span data-type="title" id=toc-1204>٣٢ </span>- واذكر - أيها النبى - كيف ذهبوا فى محادَّتك ومحادَّة الله أن قالوا معاندين موجهين النداء لله ربهم: إن كان ما تجئ به هو الأمر الثابت، فاجعل السماء تمطر حجارة، أو أنزل عذاباً شديداً أليماً.',NULL,'247',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1197,'<span data-type="title" id=toc-1205>٣٣ </span>- وما كان من حكمة الله تعالى أن يعذبهم فى الدنيا بعذاب شديد وأنت فيهم تدعو إلى الحق راجياً إجابتهم، وما كان من شأن الله أن يعذب العصاة وهم يستغفرونه ويقلعون عما هم فيه.',NULL,'247',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1198,'<span data-type="title" id=toc-1206>٣٤ </span>- وإن حالهم القائمة الآن تسوغ تعذيبهم، لأنهم يمنعون الناس من المسجد الذى حرم الله القتال حوله، ولكن يؤخرهم الله لما قدَّره فى علمه من إيمان الكثيرين منهم، وإنهم فى حالهم هذه ليسوا نصراء ذلك المسجد المكرم، لأنهم دنَّسوه بالوثنية، وإنما نصراؤه الحقيقيون هم المؤمنون الطائعون لله، ولكن أكثر المشركين لا يعلمون الدين، ولا مقام ذلك البيت الكريم.',NULL,'247',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1199,'<span data-type="title" id=toc-1207>٣٥ </span>- وما كان دعاؤهم وتضرعهم عند هذا البيت العظيم إلا صفيراً وصفقاً بالأيدى، وإذا كانت تلك حالكم فتلقوا الموت وذوقوه فى ميدان القتال، لينزاح الشرك عن البيت، وذلك القتل فيكم بسبب كفركم.',NULL,'248',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1200,'<span data-type="title" id=toc-1208>٣٦ </span>- إن هؤلاء الذين جحدوا بالآيات وأشركوا بالله، ينفقون أموالهم ليمنعوا الناس عن الإيمان بالحق، وهم سينفقونها، ثم تكون الأموال بسبب ضياعها عليهم من غير جدوى موجبةً للندم والألم، وسيغلبون فى ميدان القتال فى الدنيا، ثم يجمعون إلى جهنم فى الآخرة إن استمروا على كفرهم.',NULL,'248',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1201,'<span data-type="title" id=toc-1209>٣٧ </span>- وإن الهزيمة فى الدنيا، والعذاب بالنار فى الآخرة، ليفصل الله خبيث النفس والفعل والقول عن الطيب فى نفسه وقلبه وقوله وفعله، وليجعل الخبيث بعضه فوق بعض، فيجمعه ويضم أجزاءه ويجعله فى النار يوم القيامة، وأولئك المشركون المفسدون هم الخاسرون - وحدهم - فى الدنيا والآخرة.',NULL,'248',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1202,'<span data-type="title" id=toc-1210>٣٨ </span>- وإن باب الرجاء مفتوح مع هذا الترهيب، فقل - يا نبى الرحمة - لهؤلاء الجاحدين: إنهم إن ينتهوا عن العناد والإشراك فإن الله يغفر لهم ما سبق من أعمالهم. وإن استمروا على ضلالهم وعادوا إلى قتالكم فقد تقررت الطريقة الحقة فى الأولين، وهى نصر الحق على الباطل إن التزم أهل الحق الطاعة وسبيل النصر.',NULL,'248',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1203,'<span data-type="title" id=toc-1211>٣٩ </span>- واستمروا - أيها المؤمنون - فى قتال المشركين حتى يمتنعوا عن إفسادهم لعقائد المؤمنين بالاضطهاد والأذى، فإن انتهوا عن الكفر وإيذاء المؤمنين، وخلص الدين لله، فإن الله تعالى عليم بأعمالهم ومجازيهم عليها.',NULL,'248',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1204,'<span data-type="title" id=toc-1212>٤٠ </span>- وإن استمروا على إعراضهم وإيذائهم للمؤمنين، فاعلموا - أيها المؤمنون - أنكم فى ولاية الله، وهى أحب ولاية وأقواها، وهو ناصركم، ونصرته أقوى نصرة وأعظمها.',NULL,'248',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1205,'<span data-type="title" id=toc-1213>٤١ </span>- واعلموا - أيها المسلمون - أن ما ظفرتم به من مال الكفار فحكمه: أن يقسم خمسة أخماس، خُمس منها لله وللرسول ولقرابة النبى واليتامى: وهم أطفال المسلمين الذين مات آباؤهم وهم فقراء، والمساكين، وهم ذوو الحاجة من المسلمين، وابن السبيل: وهو المنقطع فى سفره المباح. والمخصص من خُمس الغنيمة لله وللرسول يرصد للمصالح العامة التى يقررها الرسول فى حياته، والإمام بعد وفاته، وباقى الخُمس يصرف للمذكورين. أما الأخماس الأربعة الباقية من الغنيمة - وسكتت عنها الآية - فهى للمقاتلين، فاعلموا ذلك، واعملوا به إن كنتم آمنتم بالله حقاً، وآمنتم بما أنزل على عبدنا محمد من آيات التثبيت والمدد، يوم الفرقان الذى فرَّقنا فيه بين الكفر والإيمان، وهو اليوم الذى التقى فيه جمعكم وجمع الكافرين ببدر، والله عظيم القدرة على كل شئ، وقد نصر المؤمنين مع قلتهم، وخذل الكافرين مع كثرتهم.',NULL,'249',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1206,'<span data-type="title" id=toc-1214>٤٢ </span>- واذكروا حين كنتم فى الوادى بأقرب الجانبين من المدينة، وهم بأبعد الجانبين، وركْب التجارة الذى تطلبونه أقرب إليكم مما يلى البحر، ولو تواعدتم أنتم على التلاقى للقتال لما اتفقتم عليه، ولكن الله دبر تلاقيكم على غير موعد ولا رغبة منهم. لينفذ أمراً كان ثابتاً فى علمه أنه واقع لا محالة، وهو القتال المؤدى إلى نصركم وهزيمتهم، لتنقطع الشبهات، فيهلك الهالكون عن حُجة بينة بالمشاهدة: وهى هزيمة الكثرة الكافرة، ويحيا المؤمنون عن حجة بينة: وهى نصر الله للقلة المؤمنة. إن الله لسميع عليم لا يخفى عليه شئ من أقوال الفريقين ولا نياتهم.',NULL,'249',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1207,'<span data-type="title" id=toc-1215>٤٣ </span>- واذكر - أيها الرسول - حين تفضل الله عليك، فصور لك فى منامك جيش الأعداء فى قلة ليطمئنكم على أنكم ستغلبونهم، فتثبتوا أمام جمعهم ولو ترككم ترونهم كثيراً دون أن يثبتكم بهذه الرؤيا لهبتموهم، ولترددتم فى قتالهم، ولعجزتم، وكان التنازع فى الإقدام وعدمه، ولكن الله سلم من ذلك ونجَّى من عواقبه، إنه عليم بما فى القلوب التى فى الصدور.',NULL,'249',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1208,'<span data-type="title" id=toc-1216>٤٤ </span>- واذكر - أيها الرسول - حينما كان الله يريكم أعداءكم عند التلاقى قلة فى أعينكم، كما يظهركم الله فى أعين أعدائكم قلة، ولما فى أنفسهم من الغرور بالكثرة، ليقدم كل منكم على قتال الآخر، فيتم تنفيذ أمر علمه الله، وكان لا بد أن يتم، وإلى الله ترجع أمور العالم كله، فلا ينفذ إلا ما قضاه وهيأ أسبابه.',NULL,'250',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1209,'<span data-type="title" id=toc-1217>٤٥ </span>- يا أيها الذين آمنوا إذا لقيتم جماعة مقاتلة من أعدائكم فاثبتوا ولا تفروا، واذكروا الله متمثلين قدرته وحسن وعده بنصر المؤمنين، مكثرين فى ذلك الذكر مع الثبات والصبر، فإنكم إن فعلتم ذلك كان رجاؤكم للفلاح محققاً.',NULL,'250',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1210,'<span data-type="title" id=toc-1218>٤٦ </span>- وأطيعوا الله ورسوله فيما أمرتم به أو نهيتم عنه، ودعوا التنازع والاختلاف، فإنهما مدعاة إلى ضياع القوة وإلى العجز، واصبروا على ما تلقون من مكاره الحرب، فإن الله مع الصابرين بالعون والتأييد والتثبيت وحسن الجزاء.',NULL,'250',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1211,'<span data-type="title" id=toc-1219>٤٧ </span>- ولا تكونوا كأولئك الذين خرجوا من ديارهم، مغرورين بما لهم من قوة ونعمة، مفاخرين ومتظاهرين بهما أمام الناس، يريدون الثناء عليهم بالشجاعة والغلبة، وهم بذلك يصدون عن سبيل الله والإسلام، والله محيط بأعمالهم علماً وقدرة، وسوف يجازيهم عليها فى الدنيا والآخرة.',NULL,'250',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1212,'<span data-type="title" id=toc-1220>٤٨ </span>- واذكروا - أيها المسلمون - حينما حَسَّن الشيطان لهؤلاء المشركين أعمالهم بوسوسته قائلا لهم: إنه لا يستطيع أحد من الناس أن يغلبهم، ويؤكد لهم أنه مجير لهم، فلما تقابل الفريقان فى الحرب بطل كيده ووسوسته ورجع مدبراً، وتبرأ منهم، وخاف أن يهلكه الله، والله شديد العقاب على الذنوب.',NULL,'251',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1213,'<span data-type="title" id=toc-1221>٤٩ </span>- واذكر - أيها الرسول - حينما يقول المنافقون من الكفار وضعفاء الإيمان عند رؤيتكم فى إقدامكم وثباتكم: غرَّ هؤلاء المسلمين دينهم، وإن مَنْ وكل إلى الله أمره مؤمناً به معتمداً عليه، فإن الله يكفيه ما أهمه، وينصره على أعدائه، لأن الله قوى السلطان حكيم فى تدبيره.',NULL,'251',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1214,'<span data-type="title" id=toc-1222>٥٠ </span>- ولو ترى - أيها الرسول - ذلك الهول الخطير، الذى ينزل بهؤلاء الكفار حين تتوفاهم الملائكة فينزعون أرواحهم، وهم يضربونهم من أمام ومن خلف، ويقولون لهم: ذوقوا عذاب النار بسبب أفعالكم السيئة.',NULL,'251',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1215,'<span data-type="title" id=toc-1223>٥١ </span>- وإنَّ الله ليس ظالماً لعبيده فى تعذيبهم على ما ارتكبوه، بل ذلك هو العدل، لأنه لا يستوى المسئ والمحسن، فعقابه على ما اقترفوا من أعمال سيئة.',NULL,'251',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1216,'<span data-type="title" id=toc-1224>٥٢ </span>- إنَّ عادة هؤلاء المشركين وشأنهم فى الكفر، كشأن الفراعنة وسائر العتاة من قبلهم. جحوداً منهم بآيات الله، فعذبهم الله على ذنوبهم، وهو غير ظالم لهم إنَّ الله قوى فى تنفيذ حكمه، شديد المجازاة لمن يستحق عقابه.',NULL,'251',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1217,'<span data-type="title" id=toc-1225>٥٣ </span>- وهذا عدل فى الجزاء، بسبب أن الله لا يُغير نعمة أنعم بها على قوم، كنعمة الأمن والرخاء والعافية، حتى يُغَيِّروا هم ما بأنفسهم من الأحوال والأسباب، وإنَّ الله سميع لما يقولون عليم بما يفعلون.',NULL,'251',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1218,'<span data-type="title" id=toc-1226>٥٤ </span>- وكما أن دأب هؤلاء فى الإنكار لآيات الله ونعمه كدأب آل فرعون والذين من قبلهم فإن دأبهم وشأنهم فى الاستمرار على التكذيب برسله ودلائل نبوتهم، كدأب آل فرعون والذين من قبلهم فالشبه بينهم فى الكفر بالآيات، وجحود رسالة الرسل وتكذيبهم، وفى الاستمرار على ذلك. فكلاَّ أخذ اللَّه بذنبه أولئك بالصواعق والرياح ونحوها، وآل فرعون بالغرق، وكلهم كانوا ظالمين لأنفسهم، واستحقوا ما نزل بهم من العقاب.',NULL,'252',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1219,'<span data-type="title" id=toc-1227>٥٥ </span>- إن شَر ما يدب على وجه الأرض عند اللَّه فى حكمه وعدله، هم الكفار المصرون على كفرهم.',NULL,'252',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1220,'<span data-type="title" id=toc-1228>٥٦ </span>- الذين عقدت معهم العهود والمواثيق، ولا يزالون ينقضونها مرة بعد مرة، وهم اليهود لا يردعهم عن ذلك تعظيم لله، ولا خوف من نقمته وعذابه.',NULL,'252',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1221,'<span data-type="title" id=toc-1229>٥٧ </span>- فإن تدرك - أيها الرسول - هؤلاء الناقضين لعهدهم، وتصادفهم فى الحرب ظافراً بهم، فنكل بهم تنكيلا يسوؤهم ويخيف مَنْ وراءهم، فتفرق جموعهم منْ خلفهم. فذلك التنكيل أرجى لتذكيرهم بنقض العهود، ولدفع غيرهم عن الوقوع فى مثل ما وقع فيه هؤلاء.',NULL,'252',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1222,'<span data-type="title" id=toc-1230>٥٨ </span>- وإن تتوقع من قوم خيانة بأمارات تنبئ بنقضهم لما بينك وبينهم من العهد، فاقطع عليهم طريق الخيانة لك، بأن تعلن فسخك لعهدهم، حتى يكونوا على علم بأمرك، وحتى لا يستطيعوا خيانتك، إن اللَّه لايحب الخائنين ولا يرضى أن توصفوا بوصفهم.',NULL,'252',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1223,'<span data-type="title" id=toc-1231>٥٩ </span>- ولا يظن الذين كفروا أنهم سبقوا ونجوا من عاقبة خيانتهم وغدرهم. إنهم لا يعجزون اللَّه عن الإحاطة بهم، بل هو القادر - وحده - وسيجزيهم بقوته وعدله.',NULL,'252',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1224,'<span data-type="title" id=toc-1232>٦٠ </span>- وأعدوا - يا معشر المسلمين - لمواجهة أعدائكم ما استطعتم من قوة حربية شاملة لجميع عتاد القتال، من المرابطين فى الثغور وأطراف البلاد بخيلهم، لتخيفوا بهذا الإعداد والرباط عدو اللَّه وعدوكم من الكفار المتربصين بكم الدوائر، وتخيفوا آخرين لا تعلمونهم الآن واللَّه يعلمهم. لأنه لا يخفى عليه شئ. وكل ما أنفقتم من شئ فى سبيل إعداد القوة قاصدين به وجه اللَّه، فإن اللَّه يجزيكم عليه جزاء وافيا، دون أن ينقصهم مثقال ذرة مما تستحقون من فضل ربكم.',NULL,'253',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1225,'<span data-type="title" id=toc-1233>٦١ </span>- وإن مآل الأعداء عن جانب الحرب إلى جانب السلم، فاجنح لها - أيها الرسول - فليست الحرب غرضاً مقصودا لذاته عندك إنما أنت قاصد بها الدفاع لعدوانهم، وتحديهم لدعوتك. فاقبل السلم منهم، وتوكل على اللَّه، ولا تخف كيدهم ومكرهم إنه سبحانه هو السميع لما يتشاورون به، العليم بما يدبرون ويأتمرون، فلا يخفى عليه شئ.',NULL,'253',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1226,'<span data-type="title" id=toc-1234>٦٢ </span>- وإن أرادوا من تظاهرهم بالجنوح إلى السلم خدعة ومكرا بك، فإن اللَّه يكفيك أمرهم من كل وجه، وقد سبق له أن أيدك بنصره، حين هيأ لك من الأسباب الظاهرة والخفية ما ثبت به قلوب المؤمنين من المهاجرين والأنصار.',NULL,'253',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1227,'<span data-type="title" id=toc-1235>٦٣ </span>- وجمع بينهم على المحبة بعد التفرق والتعادى، فأصبحوا ملتقين حولك، باذلين أرواحهم وأموالهم فى سبيل دعوتك، وإنك لو أنفقت جميع ما فى الأرض من الأموال والمنافع - فى سبيل هذا التأليف - لما أمكنك أن تصل إليه، لأن القلوب بيد اللَّه، ولكن اللَّه ألفَّ بينهم، بهدايتهم إلى الإيمان والمحبة والإخاء، وإنه تعالى قوى غالب، يدبر أمر العباد على مقتضى ما ينفعهم.',NULL,'253',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1228,'<span data-type="title" id=toc-1236>٦٤ </span>- يأيها النبى كفاك وكفى أتباعك المؤمنين أن اللَّه لكم ناصراً ومؤيدا.',NULL,'253',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1229,'<span data-type="title" id=toc-1237>٦٥ </span>- يأيها النبى حث المؤمنين على القتال لإعلاء كلمة اللَّه ورغبهم فيما وراءه من خير الدنيا والآخرة، لتقوى بذلك نفوسهم، وإنه إن يوجد منكم عشرون معتصمون بالإيمان والصبر والطاعة، يغلبوا مائتين من الذين كفروا، ذلك بأنهم قوم لا يدركون حقائق الأمور، فليس لهم إيمان ولا صبر ولا مطمع فى ثواب.',NULL,'254',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1230,'<span data-type="title" id=toc-1238>٦٦ </span>- وإذا كان واجبكم - أيها المؤمنون - أن تصبروا على ملاقاة أعدائكم فى حال قوتكم، ولو كانوا أمثالكم، فقد رخَص اللَّه لكم فى غير حال القوة أن تصبروا أمام مثليكم فقط من الأعداء لعلمه أن فيكم ضعفا يقتضى التيسير عليكم والترخيص لكم، بعد أن تثبت هيبة الإسلام فى نفوس الكفار، فإن يكن منكم مائة مجاهد صابر يغلبوا مائتين من الكفار، وإن يكن منكم ألف يغلبوا ألفين بإرادة اللَّه ومعونته، واللَّه مع الصابرين بنصره وتأييده.',NULL,'254',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1231,'<span data-type="title" id=toc-1239>٦٧ </span>- لا يسوغ لأحد من الأنبياء أن يكون له أسرى يحتجزهم، أو يأخذ منهم الفداء، أو يمن عليهم بالعفو عنهم حتى يتغلب ويظهر على أعدائه، ويثقلهم بالجراح، فلا يستطيعون قتالا فى الأرض، ولكنكم - يا جماعة المسلمين - سارعتم فى غزوة بدر إلى اتخاذ الأسرى قبل التمكن فى الأرض، تريدون منافع الدنيا واللَّه يريد لكم الآخرة بإعلاء كلمة الحق، وعدم الالتفاف إلى ما يشغلكم عن الدنيا، واللَّه قوى قادر غالب، يدبر الأمور لكم على وجه المنفعة.',NULL,'254',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1232,'<span data-type="title" id=toc-1240>٦٨ </span>- لولا حكم سابق من اللَّه بالعفو عن المجتهد المخطئ لأصابكم فيما أخذتم عذاب كبير بسبب ما تعجلتم به.',NULL,'254',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1233,'<span data-type="title" id=toc-1241>٦٩ </span>- فكلوا مما غنمتم من الفداء حلالاً لكم غير خبيث الكسب، واتقوا اللَّه فى كل أموركم، إن اللَّه عظيم الغفران والرحمة لمن شاء من عباده إذا أناب إلى ربه.',NULL,'254',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1234,'<span data-type="title" id=toc-1242>٧٠ </span>- يأيها النبى، قل للذين وقعوا فى أيديكم من الأسرى: إن يكن فى قلوبكم خير يعلمه اللَّه، يخلف لكم خيرا مما أخذه المؤمنون منكم، ويغفر لكم ما كان من الشرك والسيئات، واللَّه كثير المغفرة والرحمة لمن تاب من كفره ومن ذنبه.',NULL,'255',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1235,'<span data-type="title" id=toc-1243>٧١ </span>- وإن يُريدوا خيانتك بما يُظهر بعضهم من الميل إلى الإسلام مع انطواء صدروهم على قصد مخادعتك، فلا تبتئس، فسيمكنك اللَّه منهم، كما خانوا اللَّه من قبل باتخاذ الأنداد والشركاء والكفر بنعمته، فأمكن منهم إذ نصرك عليهم فى بدر، مع التفاوت بين قوتك فى القلة، وقوتهم فى الكثرة، واللَّه قوى غالب متصرف بحكمته، فأمكن من نصره عباده المؤمنين.',NULL,'255',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1236,'<span data-type="title" id=toc-1244>٧٢ </span>- إن الذين صدَقوا بالحق وأذعنوا لحكمه، وهاجروا من مكه، وجاهدوا بأموالهم وأنفسهم، والذين آووهم فى غربتهم، ونصروا رسول اللَّه يقاتلون من قاتله، ويعادون من عاداه، بعضهم نصراء بعض فى تأييد الحق وإعلاء كلمة اللَّه على الحق. والذين لم يهاجروا، لا يثبت لهم شئ من ولاية المؤمنين ونصرتهم، إذ لا سبيل إلى ولايتهم حتى يهاجروا، وإن طلبوا منكم النصر على من اضطهدوهم فى الدين، فانصروهم. فإن طلبوا النصر على قوم معاهدين لكم لم ينقضوا الميثاق معكم، فلا تجيبوهم، واللَّه بما تعملون بصير لا يخفى عليه شئ، فقفوا عند حدوده لئلا تقعوا فى عذابه.',NULL,'255',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1237,'<span data-type="title" id=toc-1245>٧٣ </span>- والذين كفروا بعضهم أولياء بعض فهم متناصرون على الباطل، متعاونون فى عداوتكم، فلا توالوهم، فإن خالفتم وواليتموهم، تقع الفتنة فى صفوفكم والفساد الكبير فى الأرض.',NULL,'256',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1238,'<span data-type="title" id=toc-1246>٧٤ </span>- والذين آمنوا وهاجروا وجاهدوا فى سبيل اللَّه، والذين آووهم ونصروا الحق وكلمة اللَّه، هم الصادقو الإيمان، واللَّه تعالى يغفر لهم، ولهم رزق كبير فى الدنيا والآخرة.',NULL,'256',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1239,'<span data-type="title" id=toc-1247>٧٥ </span>- والذين آمنوا بعد الأولين وهاجروا أخيراً وجاهدوا مع السابقين، فأولئك منكم يا جماعة المهاجرين والأنصار، لهم من الولاية والحقوق ما لبعضكم على بعض. وذوو الأرحام من المؤمنين لهم - فضلا عن ولاية الإيمان - ولاية القرابة، فبعضهم أولى ببعض فى المودة والمال والنصرة والتأييد، وقد بين ذلك فى كتابه وهو العليم بكل شئ.',NULL,'256',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1240,'<span data-type="title" id=toc-1249>١ </span>- الله ورسوله بريئان من المشركين الذين عاهدتموهم فنقضوا العهد.',NULL,'257',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1241,'<span data-type="title" id=toc-1250>٢ </span>- فلكم الامان - أيها المشركون - مدة أربعة أشهر - من حين البراءة تنتقلون فيها حيث شئتم، واعلموا أنكم حيثما كنتم خاضعين لسلطان الله، وأنتم لا تعجزونه، وإن الله كاتب الخزى على الذين يجحدونه.',NULL,'258',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1242,'<span data-type="title" id=toc-1251>٣ </span>- وبلاغ من الله ورسوله إلى الناس عامة، فى مجتمعهم يوم الحج الأكبر، أن الله ورسوله بريئان من عهود المشركين الخائنين - في أيها المشركون الناقضون للعهد - إذا رجعتم عن شرككم بالله، فإن ذلك خير لكم فى الدنيا والآخرة، أما إن أعرضتم وبقيتم على ما أنتم عليه، فاعلموا أنكم خاضعون لسلطان الله. وأنت - أيها الرسول - أنذر جميع الكافرين بعذاب شديد الإيلام.',NULL,'258',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1243,'<span data-type="title" id=toc-1252>٤ </span>- أما من عاهدتم من المشركين، فحافظوا على عهودكم ولم يُخِلُّوا بشئ منها، ولم يعينوا عليكم أحداً، فأوفوا لهم عهدهم إلى نهايته واحترموه. . إن الله يحب المتقين المحافظين على عهودهم.',NULL,'258',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1244,'<span data-type="title" id=toc-1253>٥ </span>- فإذا انقضت مدة الأمان - الأشهر الأربعة - فاقتلوا المشركين الناقضين للعهد فى كل مكان، وخذوهم بالشدة، واضربوا الحصار عليهم بسد الطرق، واقعدوا لهم فى كل سبيل، فإن تابوا عن الكفر، والتزموا أحكام الإسلام بإقامة الصلاة وإيتاء الزكاة، فلا سبيل لكم عليهم لدخولهم فى دين الله، والله عظيم المغفرة لمن تاب، واسع الرحمة بعباده.',NULL,'258',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1245,'<span data-type="title" id=toc-1254>٦ </span>- وإن طلب منك الأمان - أيها الرسول - أحد من المشركين الذين أمرتم بقتالهم ليسمع دعوتك، فأمّنه حتى يسمع كلام الله، فإن دخل فى الإسلام فهو منكم، وإن لم يدخل فأبلغه مكاناً يكون فيه آمناً. وهذا الأمر - بتأمين المستجير حتى يسمع كلام الله - بسبب ما ظهر من جهله للإسلام، ورغبته فى العلم به.',NULL,'259',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1246,'<span data-type="title" id=toc-1255>٧ </span>- كيف يكون لهؤلاء المشركين - الناقضين للعهود مراراً - عهد محترم عند الله وعند رسوله؟ فلا تأخذوا بعهودهم، إلا الذين عاهدتموهم من قبائل العرب عند المسجد الحرام ثم استقاموا على عهدهم، فاستقيموا أنتم لهم على عهدكم ما داموا مستقيمين، إن الله يحب الطائعين له الموفين بعهودهم.',NULL,'259',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1247,'<span data-type="title" id=toc-1256>٨ </span>- كيف تحافظون على عهودهم، وهم قوم إن يتمكنوا منكم ويكونوا ظاهرين عليكم فلن يدَّخروا جهداً فى القضاء عليكم، غير مراعين فيكم قرابة ولا عهداً، وهؤلاء يخدعونكم بكلامهم المعسول، وقلوبهم منطوية على كراهيتكم، وأكثرهم خارجون عن الحق ناقضون للعهد.',NULL,'259',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1248,'<span data-type="title" id=toc-1257>٩ </span>- أعرَضوا عن آيات الله واستبدلوا بها عَرَضاً قليلاً من أعراض الدنيا، ومنعوا الناس عن الدخول فى دين الله، إن هؤلاء قَبُحَ ما كانوا يعملون.',NULL,'259',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1249,'<span data-type="title" id=toc-1258>١٠ </span>- تلك حال جحودهم، لا يحترمون لمؤمن قرابة ولا عهداً، وهؤلاء هم الذين من شأنهم الاعتداء، فهو مرض لازم لهم.',NULL,'259',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1250,'<span data-type="title" id=toc-1259>١١ </span>- فإن تابوا عن الكفر، والتزموا أحكام الإسلام بإقامة الصلاة وإيتاء الزكاة، فهم إخوانكم فى الدين، لهم ما لكم وعليهم ما عليكم، ويبيّن الله الآيات لقوم ينتفعون بالعلم.',NULL,'259',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1251,'<span data-type="title" id=toc-1260>١٢ </span>- وإن نقضوا عهودهم من بعد توكيدها، واستمروا على الطعن فى دينكم، فقاتلوا رؤساء الضلال ومَن معهم، لأنهم لا عهد لهم ولا ذمة، لينتهوا عن كفرهم.',NULL,'260',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1252,'<span data-type="title" id=toc-1261>١٣ </span>- هلا تسارعون - أيها المؤمنون - إلى قتال جماعة من المشركين، نقضوا عهودكم مراراً، وقد سبق أن همُّوا بإخراج الرسول من مكة وبقتله، وهم الذين بدأوكم بالإيذاء والعدوان من أول الأمر، أتخافونهم؟ لا تخافوهم، فالله - وحده - أحق بأن تخافوه، إن كنتم صادقين فى إيمانكم.',NULL,'260',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1253,'<span data-type="title" id=toc-1262>١٤ </span>- قاتلوهم - أيها المؤمنون - يذقهم الله العذاب على أيديكم، ويذلهم وينصركم عليهم، ويشف - بهزيمتهم وإعلاء عزة الإسلام - ما كان من ألم كامن وظاهر بصدور قوم مؤمنين طالما لحقهم أذى الكفار.',NULL,'260',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1254,'<span data-type="title" id=toc-1263>١٥ </span>- ويملأ الله قلوب المؤمنين فرحاً بالنصر بعد الهم والخوف، ويذهب عنهم الغيظ، ويقبل الله توبة من يشاء توبته منهم، والله واسع العلم بشئون عباده، عظيم الحكمة فيما يشرع لهم.',NULL,'260',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1255,'<span data-type="title" id=toc-1264>١٦ </span>- لا تظنوا - أيها المؤمنون - أن يترككم الله تعالى دون اختبار لكم بالجهاد ونحوه. إن من سنته تعالى الاختبار، ليظهَر علمه بالذين جاهدوا منكم مخلصين، ولم يتخذوا سوى الله ورسوله والمؤمنين بطانة وأولياء، والله عليم بجميع أعمالكم، ومجازيكم عليها.',NULL,'260',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1256,'<span data-type="title" id=toc-1265>١٧ </span>- ليس المشركون أهلاً لأن يعمروا مساجد الله، وهم مستمرون على كفرهم، معلنون له، أولئك المشركون لا اعتداد بأعمالهم ولا ثواب لهم عليها، وهم خالدون فى النار يوم القيامة.',NULL,'260',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1257,'<span data-type="title" id=toc-1266>١٨ </span>- ولكن الذين يعمرون مساجد الله، إنما هم الذين آمنوا بالله - وحده - وصدَّقوا بالبعث والجزاء، وأدَّوا الصلاة على وجهها، وأخرجوا زكاة أموالهم، ولم يخشوا إلا الله - وحده - وهؤلاء يرجى لهم أن يكونوا عند الله من المهتدين إلى الصراط المستقيم.',NULL,'261',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1258,'<span data-type="title" id=toc-1267>١٩ </span>- لا ينبغى أن تجعلوا القائمين بسقاية الحجيج وعمارة المسجد الحرام من المشركين فى منزلة الذين آمنوا بالله - وحده - وصدَّقوا بالبعث والجزاء، وجاهدوا فى سبيل الله. ذلك أنهم ليسوا بمنزلة واحدة عند الله. والله لا يهدى إلى طريق الخير القوم المستمرين على ظلم أنفسهم بالكفر، وظلم غيرهم بالأذى المستمر.',NULL,'261',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1259,'<span data-type="title" id=toc-1268>٢٠ </span>- الذين صدَّقوا بوحدانية الله، وهاجروا من دار الكفر إلى دار الإسلام، وتحمَّلوا مشاق الجهاد فى سبيل الله بأموالهم وأنفسهم، أعظم منزلة عند الله ممن لم يتصف بهذه الصفات، وهؤلاء هم الظافرون بمثوبة الله وكرامته.',NULL,'261',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1260,'<span data-type="title" id=toc-1269>٢١ </span>- هؤلاء يبشرهم الله تعالى برحمته الواسعة التى تشملهم، ويخصهم برضاه، وهو أكبر جزاء، وسيدخلهم يوم القيامة جنات لهم فيها نعيم قائم ثابت دائم.',NULL,'261',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1261,'<span data-type="title" id=toc-1270>٢٢ </span>- وهم خالدون فى الجنة لا يتحولون عنها، وإن الله عنده أجر عظيم وثواب جزيل.',NULL,'261',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1262,'<span data-type="title" id=toc-1271>٢٣ </span>- يا أيها المؤمنون لا تتخذوا من آبائكم وأبنائكم وإخوانكم وعشيرتكم وأزواجكم، نصراء لكم ما داموا يحبون الكفر ويفضلونه على الإيمان، ومن يستنصر بالكافرين، فأولئك هم الذين تجاوزوا الطريق المستقيم.',NULL,'261',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1263,'<span data-type="title" id=toc-1272>٢٤ </span>- قل - يا أيها الرسول - للمؤمنين: إن كنتم تحبون آباءكم وأبناءكم وإخوانكم وأزواجكم، وأقرباءكم، وأموالاً اكتسبتموها، وتجارة تخافون بوارها، ومساكن تستريحون للإقامة فيها أكثر من حبكم لله ورسوله والجهاد فى سبيله، حتى شغلتكم عن مناصرة الرسول، فانتظروا حتى يأتى الله بحكمه فيكم وعقوبته لكم. والله لا يهدى الخارجين على حدود دينه.',NULL,'262',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1264,'<span data-type="title" id=toc-1273>٢٥ </span>- لقد نصركم الله - أيها المؤمنون - على أعدائكم فى كثير من المواقع بقوة إيمانكم، وحين غرتكم كثرتكم فى معركة «حُنَيْن» ترككم الله لأنفسكم أول الأمر، فلم تنفعكم كثرتكم شيئاً، وظهر عليكم عدوكم، ولشدة الفزع ضاقت عليكم الأرض، فلم تجدوا سبيلا للقتال أو النجاة الشريفة، ولم يجد أكثركم وسيلة للنجاة غير الهرب، ففررتم منهزمين، وتركتم رسول الله مع قلة من المؤمنين.',NULL,'262',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1265,'<span data-type="title" id=toc-1274>٢٦ </span>- ثم أدركتكم عناية الله، فأنزل الطمأنينة على رسوله، وملأ بها قلوب المؤمنين، وأمدَّكم بالملائكة جنوده التى ثبتت أقدامكم، ولم تروها، فانتصرتم. . وأذاق الله أعداءكم مرارة الهزيمة، وذلك جزاء الكافرين فى الدنيا.',NULL,'262',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1266,'<span data-type="title" id=toc-1275>٢٧ </span>- ثم يقبل الله توبة من يشاء من عباده فيغفر ذنبه، إذا رجع عنه مخلصاً، والله عظيم المغفرة واسع الرحمة.',NULL,'262',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1267,'<span data-type="title" id=toc-1276>٢٨ </span>- يا أيها المؤمنون، إنما المشركون بسبب شركهم نجست نفوسهم، وهم ضالون فى العقيدة، فلا تمكنوهم من دخول المسجد الحرام بعد هذا العام (التاسع من الهجرة) . وإن خفتم فقراً بسبب انقطاع تجارتهم عنكم، فإن الله سوف يعوضكم عن هذا، ويغنيكم من فضله إن شاء، إن الله عليم بشئونكم، حكيم فى تدبيره لها.',NULL,'263',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1268,'<span data-type="title" id=toc-1277>٢٩ </span>- يا أيها الذين آمنوا، قاتلوا الكافرين من أهل الكتاب الذين لا يؤمنون إيماناً صحيحاً بالله ولا يقرون بالبعث والجزاء إقراراً صحيحاً، ولا يلتزمون الانتهاء عما نهى الله ورسوله عنه، ولا يعتنقون الدين الحق وهو الإسلام. قاتلوهم حتى يؤمنوا، أو يؤدوا إليكم الجزية خاضعين طائعين غير متمردين. ليسهموا فى بناء الميزانية الإسلامية.',NULL,'263',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1269,'<span data-type="title" id=toc-1278>٣٠ </span>- ترك اليهود الوحدانية فى عقيدتهم، وقالوا: عزيز ابن الله، وترك النصارى الوحدانية كذلك، فقالوا: المسيح ابن الله. وقولهم هذا مبتدع من عندهم، يرددونه بأفواههم ولم يأتهم به كتاب ولا رسول، وليس عليهم حُجة ولا برهان، وهم فى هذا القول يشابهون قول المشركين قبلهم، لعن الله هؤلاء الكفار وأهلكهم. عجباً لهم كيف يضلون عن الحق وهو ظاهر، ويعدلون إلى الباطل.',NULL,'263',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1270,'<span data-type="title" id=toc-1279>٣١ </span>- اتخذوا رجال دينهم أرباباً، يشرعون لهم، ويكون كلامهم ديناً، ولو كان يخالف قول رسولهم، فاتبعوهم فى باطلهم، وعبدوا المسيح ابن مريم، وقد أمرهم الله فى كتبه على لسان رسله ألا يعبدوا إلا إلهاً واحداً، لأنه لا يستحق العبادة فى حكم الشرع والعقل إلا الإله الواحد، تنزه الله عن الإشراك فى العبادة والخلق والصفات.',NULL,'264',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1271,'<span data-type="title" id=toc-1280>٣٢ </span>- يريد الكافرون بمزاعمهم الباطلة أن يطفئوا نور الله وهو الإسلام، ولا يريد الله إلا إتمام نوره، بإظهار دينه ونصر رسوله، ولو كانوا كارهين لذلك.',NULL,'264',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1272,'<span data-type="title" id=toc-1281>٣٣ </span>- هو الله الذى كفل إتمام نوره بإرسال رسوله (محمداً) صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ، بالحُجج البينات، ودين الحق (الإسلام) ليعلى هذا الدين على جميع الأديان السابقة عليه، وإن كرهه المشركون، فإن الله يظهره رغماً عنهم.',NULL,'264',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1273,'<span data-type="title" id=toc-1282>٣٤ </span>- يا أيها المؤمنون: اعلموا أن كثيراً من علماء اليهود ورهبان النصارى يستحلون أموال الناس بغير حق، ويستغلون ثقة الناس فيهم واتباعهم لهم فى كل ما يقولون، ويمنعون الناس عن الدخول فى الإسلام. والذين يستحوذون على الأموال من ذهب وفضة، حابسين لها، ولا يؤدون زكاتها، فأنذرهم - أيها الرسول - بعذاب موجع.',NULL,'264',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1274,'<span data-type="title" id=toc-1283>٣٥ </span>- فى يوم القيامة، يوقد على هذه الأموال فى نار جهنم، ثم تحرق بتلك الأموال المحماة جباه أصحابها، وجنوبهم وظهورهم، ويقال توبيخاً لهم: هذا ما ادخرتموه لأنفسكم، ولم تؤدوا منه حق الله، فذوقوا اليوم عذاباً شديداً.',NULL,'264',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1275,'<span data-type="title" id=toc-1284>٣٦ </span>- إن عدة شهور السنة القمرية اثنا عشر شهراً، فى حكم الله وتقديره، وفيما بَيَّنه فى كتبه منذ بدء العالم. ومن هذه الاثنى عشر شهراً أربعة أشهر يحرم القتال فيها، وهى: رجب وذو القعدة وذو الحجة والمحرم. وهذا التحريم للأشهر الأربعة المذكورة هو دين الله المستقيم، الذى لا تبديل فيه ولا تغيير. فلا تظلموا فى هذه الأشهر أنفسكم باستحلال القتال أو امتناعكم عنه إذا أغار عليكم الأعداء فيها، وقاتلوا - أيها المؤمنون - جماعة المشركين دون استثناء أحد منهم، كما يقاتلونكم معادين لكم جميعاً، وكونوا على يقين من أن الله ناصر للذين يخافون، فيلتزمون أوامره ويجتنبون نواهيه.',NULL,'265',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1276,'<span data-type="title" id=toc-1285>٣٧ </span>- وما تأخير هذه الأشهر الحرم أو بعضها عما رتَّبها الله عليه - كما كان يفعله أهل الجاهلية - إلا إمعان فى الكفر، يزداد به الذين كفروا ضلالا فوق ضلالهم، وكان العرب فى الجاهلية يجعلون الشهر الحرام حلالا إذا احتاجوا القتال فيه، ويجعلون الشهر الحلال حراماً، ويقولون: شهر بشهر، ليوافقوا عدد الأشهر التى حرمها الله، وقد حسَّنت لهم أهواؤهم أعمالهم السيئة، والله لا يهدى القوم المصرين على كفرهم إلى طريق الخير.',NULL,'265',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1277,'<span data-type="title" id=toc-1286>٣٨ </span>- يا أيها المؤمنون ما لكم حينما قال لكم الرسول: اخرجوا للجهاد فى سبيل الله، تباطأ بعضكم عن الخروج للجهاد؟ لا ينبغى ذلك. عجباً لكم أآثرتم الحياة الدنيا الفانية على الحياة الآخرة ونعيمها الدائم؟ فما التمتع بالدنيا ولذائذها فى جنب متاع الآخرة إلا قليل تافه.',NULL,'265',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1278,'<span data-type="title" id=toc-1287>٣٩ </span>- إن لم تستجيبوا للرسول، فتخرجوا للجهاد فى سبيل الله، يعذبكم الله عذاباً موجعاً. ويستبدل ربكم بكم قوماً آخرين يستجيبون للرسول ولا يتخلفون عن الجهاد، ولا تضرون الله بهذا التخلف شيئاً، والله عظيم القدرة على كل شئ.',NULL,'265',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1279,'<span data-type="title" id=toc-1288>٤٠ </span>- يا أيها المؤمنون، إن لم تنصروا رسول الله فإن الله كفيل بنصره، كما أيَّدَه ونصره حينما اضطره الذين كفروا إلى الخروج من مكة. وليس معه إلا رفيقه أبو بكر، وكان ثانى اثنين، وبينما هما فى الغار مختفين من المشركين الذين يتعقبونهما خشى أبو بكر على حياة الرسول، فقال له الرسول مطمئناً: لا تحزن فإن الله معنا بالنصر والمعونة. عند ذلك أنزل الله الطمأنينة فى قلب صاحبه، وأيَّد الرسول بجنود من عنده، لا يعلمها إلا هو سبحانه. وانتهى الأمر بأن جعل شوكة الكافرين مفلولة ودين الله هو الغالب، والله متصف بالعزة فلا يقهر، وبالحكمة فلا يختل تدبيره.',NULL,'266',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1280,'<span data-type="title" id=toc-1289>٤١ </span>- أيها المؤمنون، إذا دعا داعى الجهاد فلبوا النداء أفراداً وجماعات - كل على قدر حاله - ناشطين بالقوة والسلامة والسلاح، وجاهدوا بالمال والنفس فى سبيل إعلاء كلمة الله. ففى ذلك العز والخير لكم. . إن كنتم من أهل العلم الصحيح والمعرفة الحقة.',NULL,'266',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1281,'<span data-type="title" id=toc-1290>٤٢ </span>- ندد القرآن بالمنافقين فى تخلفهم عن متابعة الرسول فى الجهاد، فقال: لو كان ما دعى إليه هؤلاء المنافقون عرضاً من أعراض الدنيا قريب المنال، أو لو كان كذلك سفراً سهلا، لاتبعوك - أيها الرسول - ولكن شق عليهم السفر وسيحلفون أنهم لو استطاعوا لخرجوا معك، وبهذا النفاق والكذب يهلكون أنفسهم، والله لا يخفى عليه حالهم، فهو يعلم كذبهم وسيجزيهم على ذلك.',NULL,'266',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1282,'<span data-type="title" id=toc-1291>٤٣ </span>- لقد عفا الله عنك - أيها الرسول - فى إذنك لهؤلاء المنافقين فى التخلف عن الجهاد، قبل أن تتبيَّن أمرهم، وتعلم الصادق من أعذارهم إن كان، كما تعرف الكاذبين منهم فى ادعائهم الإيمان وفى انتحال الأعذار غير الصادقة.',NULL,'266',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1283,'<span data-type="title" id=toc-1292>٤٤ </span>- ليس من شأن المؤمنين حقاً بالله، وحسابه فى اليوم الآخر، أن يستأذنوك فى الجهاد بالمال والنفس، أو فى التخلف عنك، لأن صدق إيمانهم يحبب إليهم الجهاد فى سبيل الله. والله يعلم صدق نِيَّات المؤمنين.',NULL,'267',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1284,'<span data-type="title" id=toc-1293>٤٥ </span>- إنما يستأذنك الذين لا يؤمنون إيماناً صادقاً بالله وحسابه فى اليوم الآخر، فإن قلوبهم دائماً فى شك وريبة، وهم يعيشون فى حيرة، وسينالون جزاء ذلك.',NULL,'267',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1285,'<span data-type="title" id=toc-1294>٤٦ </span>- ولو صدقت نيَّة هؤلاء المنافقين فى الخروج مع الرسول للجهاد، لأخذوا أهبة الحرب واستعدوا لها، ولكن الله كره خروجهم لعلمه أنهم لو خرجوا معكم لكانوا عليكم لا لكم، فعوّقهم عن الخروج بما امتلأت به قلوبهم من النفاق، وقال قائلهم: اقعدوا مع القاعدين من أصحاب المعاذير.',NULL,'267',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1286,'<span data-type="title" id=toc-1295>٤٧ </span>- ولو خرجوا معكم إلى الجهاد ما زادوكم بخروجهم قوة، ولكن يُشيعون الاضطراب أو يُسرعون إلى الفتنة، ويشيعونها فيما بينكم، وفيكم مَنْ يجهل خُبث نيَّاتهم، ويمكن أن يُخْدَع بكلامهم، أو لضعفه يسمع دعوتهم إلى الفتنة، والله عليم بهؤلاء المنافقين الذين يظلمون أنفسهم بما أضمروه من الفساد.',NULL,'267',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1287,'<span data-type="title" id=toc-1296>٤٨ </span>- وقد سبق أن سعى هؤلاء المنافقون بالفتنة فيما بينكم، ودبروا لك - أيها الرسول - المكايد، فأحبط الله تدبيرهم، وحقق نصرك، وأظهر دينه على الرغم منهم.',NULL,'267',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1288,'<span data-type="title" id=toc-1297>٤٩ </span>- وبعض المنافقين كان يقول للرسول: ائذن لى فى القعود عن الجهاد، ولا توقعنى فى شدة وضيق. إنهم بهذا الموقف قد أوقعوا أنفسهم فى معصية الله، وإن نار جهنم لمحيطة بهم فى اليوم الآخر.',NULL,'267',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1289,'<span data-type="title" id=toc-1298>٥٠ </span>- هؤلاء المنافقون لا يريدون بك - أيها الرسول - وبأصحابك إلا المكاره، فيتألمون إذا نالكم خير من نصر أو غنيمة، ويفرحون إذا أصابكم شر من جراح أو قتل، ويقولون حينئذ شامتين: قد أخذنا حذرنا بالقعود عن الخروج للجهاد، وينصرفون مسرورين.',NULL,'268',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1290,'<span data-type="title" id=toc-1299>٥١ </span>- قل لهم أيها الرسول: لن ينالنا فى دنيانا من الخير أو الشر إلا ما قدره الله علينا، فنحن راضون بقضاء الله، لا نغتر بالخير نناله، ولا نجزع بالشر يصيبنا، فإن الله وحده المتولى لجميع أمورنا، وعليه - وحده - يعتمد المؤمنون الصادقون.',NULL,'268',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1291,'<span data-type="title" id=toc-1300>٥٢ </span>- قل لهم - أيها الرسول - ليس لكم أن تتوقعوا شيئاً ينالنا إلا إحدى العاقبتين الحميدتين، إما النصر والغنيمة فى الدنيا، وإما الاستشهاد فى سبيل الله والجنة فى الآخرة، ونحن ننتظر لكم أن يوقع الله بكم عذاباً من عنده يهلككم به، أو يعذبكم بالذلة على أيدينا، فانتظروا أمر الله، ونحن معكم منتظرون أمره.',NULL,'268',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1292,'<span data-type="title" id=toc-1301>٥٣ </span>- قل - أيها الرسول - للمنافقين، الذين يريدون أن يستروا نفاقهم بإنفاق المال فى الجهاد وغيره: أنفقوا ما شئتم طائعين أو مكرهين، فلن يتقبل الله عملكم الذى أحبطه نفاقكم، إنكم دائماً متمردون على دين الله، خارجون على أمره.',NULL,'268',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1293,'<span data-type="title" id=toc-1302>٥٤ </span>- وما منع الله من قبول نفقاتهم إلا أنهم كفروا بالله ورسوله، والكفر يحبط الأعمال، وإلا أنهم لا يؤدون الصلاة على الوجه الذى أمروا أن يؤدوها عليه، فهم يؤدونها غير مقبلين عليها ستراً لنفاقهم، ولا ينفقون شيئاً إلا وهم كارهون لهذا الإنفاق فى سرائرهم.',NULL,'268',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1294,'<span data-type="title" id=toc-1303>٥٥ </span>- ولا يروقك - أيها السامع - ويأخذ بقلبك، ما ترى من المنافقين فيه من مال وبنين، فإن الله ما أعطاهم هذا إلا ليكابدوا فى سبيله المتاعب والمشقات، لحفظه فى الحياة الدنيا، دون أن يؤجروا على ذلك، ويدركهم الموت وهم كافرون، فيعذبون بسببها فى الآخرة.',NULL,'268',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1295,'<span data-type="title" id=toc-1304>٥٦ </span>- ويقسم هؤلاء المنافقون كذباً لكم - يا جماعة المؤمنين - أنهم مؤمنون مثلكم، والحقيقة أنهم ليسوا مؤمنين بالله، ولكنهم قوم من شأنهم الضعف والخوف، وإن ذلك يدفعهم إلى النفاق والخوف الدائم، فهم يؤكدونه بالأيْمان الفاجرة.',NULL,'268',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1296,'<span data-type="title" id=toc-1305>٥٧ </span>- وهم يضيقون بكم، ويكرهون معاشرتكم، ولو يجدون حصناً أو سراديب فى الجبال أو جحوراً فى الأرض يدخلون فيها، لانصرفوا إليها مسرعين.',NULL,'269',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1297,'<span data-type="title" id=toc-1306>٥٨ </span>- وبعض هؤلاء المنافقين يعيبك - أيها الرسول - ويطعن عليك فى قسمة الصدقات والغنائم، إذ لا هم لهم إلا حطام الدنيا، فإن أعطيتهم ما يرغبون منها رضوا عن عملك، وإن لم تعطهم تعجلوا بالسخط عليك.',NULL,'269',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1298,'<span data-type="title" id=toc-1307>٥٩ </span>- ولو أن هؤلاء المنافقين، الذين عابوك فى قسم الصدقات والغنائم، رضوا بما قسم الله لهم، وهو ما أعطاهم رسوله، وطابت نفوسهم به - وإن قل - وقالوا: كفانا حكم الله، وسيرزقنا الله من فضله، ويعطينا رسوله أكثر مما أعطانا فى هذه المرة، وإنا إلى طاعة الله وأفضاله وإحسانه لراغبون، لو أنهم فعلوا ذلك لكان خيراً لهم.',NULL,'269',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1299,'<span data-type="title" id=toc-1308>٦٠ </span>- لا تُصرف الزكاة المفروضة إلا للذين لا يجدون ما يكفيهم، والمرضى الذين لا يستطيعون كسباً ولا مال لهم، والذين يجمعونها ويعملون فيها، والذين تؤلف قلوبهم، لأنهم يرجى منهم الإسلام والانتفاع بهم فى خدمته ونصرته، والذين يدعون إلى الإسلام ويبشرون به، وفى عتق رقاب الأرقاء والأسرى من ربقة العبودية وذل الأسر، وفى قضاء الديون عن المدينين العاجزين عن الأداء، إذا لم تكن ناشئة عن إثم أو ظلم أو سفه، وفى إمداد الغزاة بما يعينهم على الجهاد فى سبيل الله، وما يتصل بذلك من طريق الخير ووجوه البر، وفى عون المسافرين إذا انقطعت أسباب اتصالهم بأموالهم وأهليهم. شرع الله ذلك فريضة منه لمصلحة عباده، والله سبحانه عليم بمصالح خلقه، حكيم فيما يشرع.',NULL,'269',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1300,'<span data-type="title" id=toc-1309>٦١ </span>- ومن الناس منافقون يتعمَّدون إيذاء النبى، وتناوله بما يكره، فيتهمونه بأنه محب لسماع كل ما يقال له من صدق وكذب، وأنه يخدع بما يسمع، فقل لهم - أيها الرسول -: إن من تتناولونه فى غيبته بهذه التهمة، ليس كما زعمتم، بل هو أذن خير لا يسمع إلا الصدق، ولا يخدع بالباطل، يصدق بالله ووحيه، ويصدق المؤمنين، لأن إيمانهم يمنعهم عن الكذب، وهو رحمة لكل من يؤمن منكم. وإن الله أعد لمن يؤذيه عذاباً مؤلماً دائماً شديداً.',NULL,'270',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1301,'<span data-type="title" id=toc-1310>٦٢ </span>- يتخلفون عنكم فى قتال أعدائكم دون تردد، ثم يعتذرون عن تخلفهم كذباً، ويحلفون لكم لترضوا عنهم وتقبلوا معاذيرهم، والله والرسول أحق بحرصهم على رضائه إن كانوا مؤمنين حقاً.',NULL,'270',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1302,'<span data-type="title" id=toc-1311>٦٣ </span>- ألم يعلم هؤلاء المنافقون أن مَن يكفر، أو يُحاد الله ورسوله جزاؤه العذاب الدائم فى نار جهنم، وذلك هو العار الفاضح، والذل الشديد.',NULL,'270',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1303,'<span data-type="title" id=toc-1312>٦٤ </span>- المنافقون يستهزئون فيما بينهم بالرسول، ويخشون أن يفتضح أمرهم، فتنزل فيهم على النبى آيات من القرآن تظهر ما يخفون فى قلوبهم ويسرونه فيما بينهم، فقل لهم - أيها الرسول - استهزئوا ما شئتم، فإن الله مظهر ما تخشون ظهوره.',NULL,'270',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1304,'<span data-type="title" id=toc-1313>٦٥ </span>- تأكد - أيها الرسول - أنك إن سألت المنافقين، بعد افتضاح أمرهم، عن سبب طعنهم فى الدين واستهزائهم بالله وآياته، اعتذروا بقولهم: كنا نخوض فى الحديث ونلهو، فقل لهم: كيف ساغ لكم أن تخوضوا أو تلهوا مستهزئين بالله وآياته ورسوله؟!',NULL,'270',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1305,'<span data-type="title" id=toc-1314>٦٦ </span>- لا تعتذروا بهذه المعاذير الباطلة، قد ظهر كفركم بعد ادعائكم الإيمان، فإن نعف عن طائفة منكم تابت وآمنت بسبب إيمانها وصدق توبتها، فإنا نعذب طائفة أخرى منكم بسبب إصرارها على الكفر والنفاق، وإجرامها فى حق الرسول والمؤمنين.',NULL,'270',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1306,'<span data-type="title" id=toc-1315>٦٧ </span>- المنافقون والمنافقات يتشابهون فى أنهم يفعلون القبيح ويأمرون به، ويتركون الحق وينهون عنه، ويبخلون ببذل المال فى وجوه الخير، فهم كأجزاء من شئ واحد، أعرضوا عن الله فأعرض عنهم ولم يهدهم، لأنهم هم الخارجون عن طاعة الله.',NULL,'271',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1307,'<span data-type="title" id=toc-1316>٦٨ </span>- كتب الله للمنافقين وللكافرين نار جهنم يعذبون فيها ولا يخرجون منها، وهى حَسْبهم عقاباً، وعليهم مع هذا العقاب غضب الله والعذاب الدائم يوم القيامة.',NULL,'271',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1308,'<span data-type="title" id=toc-1317>٦٩ </span>- إن حالكم - أيها المنافقون - كحال أمثالكم ممن سبقوكم إلى النفاق والكفر، فإنهم وقد كانوا أقوى منكم وأكثر أموالاً وأولاداً، استمتعوا بما قُدِّر لهم من حظوظ الدنيا، وأعرضوا عن ذكر الله وتقواه، وقابلوا أنبياءهم بالاستخفاف، وسخروا منهم فيما بينهم وبين أنفسهم، وقد استمتعتم بما قُدِّر لكم من ملاذ الدنيا كما استمتعوا، وخضتم فيما خاضوا فيه من المنكر والباطل، إنهم قد بطلت أعمالهم، فلم تَنْفعهُم فى الدنيا ولا فى الآخرة، وكانوا هم الخاسرين، وأنتم مثلهم فى سوء الحال والمآل.',NULL,'271',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1309,'<span data-type="title" id=toc-1318>٧٠ </span>- أفلا يعتبر المنافقون والكافرون بحال الذين سبقوهم، من قوم نوح وعاد وثمود وقوم إبراهيم وقوم شعيب وقوم لوط، جاءتهم رسل الله بالحُجج البينات من عند الله، فكذبوا وكفروا، فأخذ الله كُلاً بذنبه، وأهلكهم جميعاً، وما ظلمهم الله بهذا، ولكنهم ظلموا أنفسهم بكفرهم وتمردهم على الله واستحقاقهم العذاب - وحدهم - فهم الذين يظلمون أنفسهم.',NULL,'271',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1310,'<span data-type="title" id=toc-1319>٧١ </span>- والمؤمنون والمؤمنات بعضهم أحباء ونصراء بعض بمقتضى الإيمان، يأمرون بما يأمر به دينهم الحق، وينهون عما ينكره الدين، يؤدون الصلاة فى أوقاتها، ويؤتون الزكاة لمستحقيها فى إبَّانها، ويمتثلون ما يأمر به اللَّه ورسوله، ويجتنبون ما ينهى عنه اللَّه ورسوله، وهؤلاء هم الذين سيظلون فى رحمة اللَّه، فإن اللَّه قادر على رعايتهم بالرحمة، حكيم فى عطائه.',NULL,'272',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1311,'<span data-type="title" id=toc-1320>٧٢ </span>- وقد وعدهم اللَّه الجنة خالدين فى نعيمها، وأعد لهم مساكن تطيب بها نفوسهم فى دار الإقامة والخلود، ولهم مع ذلك رضاء اللَّه عنهم يستشعرون به، وهو النعيم الأكبر، وذلك هو الفوز العظيم.',NULL,'272',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1312,'<span data-type="title" id=toc-1321>٧٣ </span>- يا أيها النبى، ثابر على جهادك فى ردع الكفار عن كفرهم، والمنافقين عن نفاقهم، واشتد عليهم فى جهادك، وإن مآلهم الذى أعدَه اللَّه لهم فى الآخرة هو جهنم، وما أَسوأ هذا المصير.',NULL,'272',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1313,'<span data-type="title" id=toc-1322>٧٤ </span>- إن المنافقين يحلفون أمامك - أيها الرسول - باللَّه أنهم ما قالوا منكرا مما بلغك عنهم، وهم كاذبون فى الإنكار، حانثون فى اليمين، وإنهم قد قالوا كلمة الكفر، وظهر كفرهم بعد أن كان باطنا، وما كان سبب نقمتهم عليك إلا بطرا بالنعمة، بعد أن أغناهم اللَّه ورسوله بما حصلوا عليه من الغنائم التى شاركوا فيها المسلمين، فإن يرجعوا إلى اللَّه بترك النفاق والندم على ما كان منهم يقبل اللَّه توبتهم ويكون ذلك خيراً لهم، وإن يعرضوا عن الإيمان يعذبهم اللَّه فى الدنيا بمختلف ألوان البلاء، وفى الآخرة بنار جهنم، وليس لهم فى الأرض من يُدافع عنهم أو يشفع لهم، أو ينصرهم.',NULL,'272',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1314,'<span data-type="title" id=toc-1323>٧٥ </span>- ومن المنافقين منْ أقسم باللَّه وعاهده: لئن آتاهم اللَّه مالا وأغناهم من فضله، ليتصدقن وليكونن من الصالحين فى أعمالهم.',NULL,'272',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1315,'<span data-type="title" id=toc-1324>٧٦ </span>- فلما استجاب اللَّه لهم، وأعطاهم من فضله، بخلوا بما أوتوا فلم ينفقوا، ولم يوفوا بالعهد، وانصرفوا عن الخير، وهم معرضون عنه وعن اللَّه.',NULL,'273',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1316,'<span data-type="title" id=toc-1325>٧٧ </span>- فكانت عاقبة بخلهم أن تمكن النفاق فى قلوبهم إلى أن يموتوا ويلقوا اللَّه، بسبب نقضهم لعهدهم، وكذبهم فى يمينهم.',NULL,'273',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1317,'<span data-type="title" id=toc-1326>٧٨ </span>- كيف يتجاهلون أن اللَّه مُطلع عليهم؛ لا يخفى عليه ما يضمرونه فى السر من نقض العهد، وما يتناجون به فى الخفاء من الطعن فى الدين وتدبير المكايد للمسلمين، وهو - جل شأنه - العليم الذى لا يغيب عنه شئ.',NULL,'273',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1318,'<span data-type="title" id=toc-1327>٧٩ </span>- ومن نقائص هؤلاء المنافقين مع بخلهم أنهم يعيبون على الموسرين من المؤمنين تصدقهم على المحتاجين، ويسخرون بغير الموسرين من المؤمنين لتصدقهم مع قلة أموالهم، وقد جازاهم اللَّه على سخريتهم بما كشف من فضائحهم، وجعلهم سخرية للناس أجمعين، ولهم فى الآخرة عذاب شديد.',NULL,'273',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1319,'<span data-type="title" id=toc-1328>٨٠ </span>- لن ينفعهم أن تستجيب لدعاء بعضهم، وتطلب المغفرة من اللَّه لهم، فسواء أن تستغفر لهم - أيها النبى - أم لا تستغفر لهم، ومهما أكثرت من طلب المغفرة لهم، فلن يعفو اللَّه عنهم؛ لأنه لا أمل فى العفو والمغفرة مع الكفر والإصرار عليه، قد كفر هؤلاء باللَّه ورسوله، واللَّه لا يهدى الخارجين عليه وعلى رسوله، لتمردهم على شرعه ودينه.',NULL,'273',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1320,'<span data-type="title" id=toc-1329>٨١ </span>- إن المنافقين تخلفوا عن الخروج مع رسول اللَّه والمسلمين، وفرحوا بقعودهم فى المدينة بعد خروج النبى منها، وبمخالفتهم أمره بالجهاد معه، وكرهوا أن يجاهدوا بأموالهم، ويضحوا بأرواحهم فى سبيل إعلاء كلمة اللَّه ونصر دينه، وأخذوا يثبطون غيرهم، ويُغرونهم بالقعود معهم، ويخوفونهم من النفور إلى الحرب فى الحر، فقل - أيها الرسول - لهؤلاء: لو كنتم تعقلون، لذكرتم أن نار جهنم أكثر حرارة وأشد قسوة مما تخافون.',NULL,'274',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1321,'<span data-type="title" id=toc-1330>٨٢ </span>- فليضحكوا فرحا بالقعود، وسخرية من المؤمنين، فإن ضحكهم زمنه قليل، لانتهائه بانتهاء حياتهم فى الدنيا، وسيعقبه بكاء كثير لا نهاية له فى الآخرة، جزاء لهم بسبب ما ارتكبوه من سيئات.',NULL,'274',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1322,'<span data-type="title" id=toc-1331>٨٣ </span>- فإن أعادك اللَّه من الغزو إلى طائفة من هؤلاء المنافقين الذين تخلفوا عن الغزو، فاستأذنوك فى أن يخرجوا معك للجهاد فى غزوة أخرى، فلا تأذن لهم، وقل لهم: لن تخرجوا معى فى أية غزوة، ولن تشتركوا معى فى قتال أى عدو، لأن قعودكم عن الخروج فى أول مرة لم يسبق بعذر يبرره، ولم يلحق بتوبة تغفره، فاقعدوا كما ارتضيتم أن تقعدوا مع المتخلفين من العجزة والكهول والنساء والأطفال.',NULL,'274',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1323,'<span data-type="title" id=toc-1332>٨٤ </span>- وإذا مات أحد منهم، فلا تصل عليه، ولا تقف على قبرة عند دفنه، لأنهم عاشوا حياتهم كافرين باللَّه ورسوله، وماتوا وهم خارجون عن دين اللَّه.',NULL,'274',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1324,'<span data-type="title" id=toc-1333>٨٥ </span>- ولا يُثير عجبك - أيها الرسول - ما أعطيناهم من الأموال والأولاد مع سخطنا عليهم، فلم يكن ذلك عن إيثارهم بالخير، بل لتنفيذ ما أراد اللَّه من شقائهم فى الدنيا بالانهماك فى جمع المال، وما يلحقهم فى ذلك من الهموم والمصائب، ولتنفيذ ما أراد اللَّه من مفارقتهم للدنيا كافرين، وقد خسروا الأولى والآخرة.',NULL,'274',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1325,'<span data-type="title" id=toc-1334>٨٦ </span>- وهؤلاء المنافقون إذا سمعوا شيئا مما أنزل عليك فى القرآن يدعوهم إلى إخلاص الإيمان باللَّه، وإلى الجهاد مع رسول اللَّه، طلب الأغنياء والأقوياء منهم أن تأذن لهم فى التخلف عن الجهاد معك، وقالوا لك: اتركنا مع المعذورين القاعدين فى المدينة.',NULL,'275',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1326,'<span data-type="title" id=toc-1335>٨٧ </span>- إنهم قد رضوا لأنفسهم أن يكونوا فى عداد المتخلفين من النساء والعجزة والأطفال الذين لا ينهضون لقتال، وختم اللَّه على قلوبهم بالخوف والنفاق، فهم لا يفهمون فهما حقيقيا ما فى الجهاد ومتابعة الرسول فيه من عز فى الدنيا ورضوان فى الآخرة.',NULL,'275',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1327,'<span data-type="title" id=toc-1336>٨٨ </span>- ذلك شأن المنافقين، لكن الرسول والذين صدقوا معه باللَّه، قد بذلوا أموالهم وأرواحهم إرضاء للَّه وإعلاء لكلمته، وأولئك لهم - وحدهم - كل خير فى الدنيا من العز والنصرة والعمل الصالح، وهم - وحدهم - الفائزون،',NULL,'275',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1328,'<span data-type="title" id=toc-1337>٨٩ </span>- وقد هيَّأ اللَّه لهم فى الآخرة النعيم المقيم فى جنات تتخللها الأنهار، وذلك هو الفوز العظيم والنجاح الكبير.',NULL,'275',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1329,'<span data-type="title" id=toc-1338>٩٠ </span>- وكما تخلف بعض المنافقين فى المدينة عن الخروج للجهاد، جاء فريق من الأعراب، وهم أهل البادية، ينتحلون الأعذار ليؤذن لهم فى التخلف، وبذلك قَعَدَ الذين كذبوا اللَّه ورسوله فيما يظهرونه من الإيمان، فلم يحضروا، ولم يعتذروا للَّه ورسوله، وذلك دليل كفرهم، وسينزل العذاب المؤلم على الكافرين منهم.',NULL,'275',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1330,'<span data-type="title" id=toc-1339>٩١ </span>- إن الذين يقبل عذرهم فى التخلف هم الضعفاء، والمرضى، والفقراء الذين لا يجدون ما ينفقون، إذا أخلص هؤلاء للَّه ورسوله فى دينهم فإنهم بذلك محسنون، ولا حرج على المحسنين، واللَّه كثير الغفران واسع الرحمة.',NULL,'275',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1331,'<span data-type="title" id=toc-1340>٩٢ </span>- وكذلك لا حرج على من جاء من المؤمنين يلتمسون أن تحملهم إلى الجهاد فقلت لهم: لا أجد ما أحملكم عليه، فانصرفوا عنك وعيونهم تفيض الدمع حزنا أن فاتهم شرف الجهاد فى سبيل اللَّه لأنهم لا يجدون ما ينفقون.',NULL,'276',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1332,'<span data-type="title" id=toc-1341>٩٣ </span>- إنما اللوم والعقاب على هؤلاء الذين يستأذنوك - أيها النبى - فى تخلفهم عن الجهاد، وهم واجدون المال والعتاد، قادرون على الخروج معك، لأنهم - مع قدرتهم واستطاعتهم - رضوا بأن يقعدوا مع النساء الضعيفات، والشيوخ العاجزين، والمرضى غير القادرين، ولأن قلوبهم أغلقت عن الحق، فهم لا يعلمون العاقبة الوخيمة التى تترتب على تخلفهم فى الدنيا وفى الآخرة.',NULL,'276',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1333,'<span data-type="title" id=toc-1342>٩٤ </span>- سيعتذر هؤلاء المتخلفون المقصرون إليكم - أيها المؤمنون المجاهدون - إذا رجعتم من ميدان الجهاد والتقيتم بهم، فقل لهم - أيها الرسول -: لا تعتذروا فإنا لن نصدقكم، لأن اللَّه قد كشف حقيقة نفوسكم، وأوحى إلى نبيه بشئ من أكاذيبكم، وسيعلم اللَّه ورسوله ما يكون منكم بعد ذلك من عمل، ثم يكون مصيركم بعد الحياة الدنيا إلى اللَّه الذى يعلم السر والعلانية، فيخبركم بما كنتم تعملون، ويجازيكم بما تستحقون.',NULL,'276',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1334,'<span data-type="title" id=toc-1343>٩٥ </span>- سيحلفون لكم باللَّه، حينما ترجعون إليهم، أنهم صادقون فى معاذيرهم، لكى يرضوكم فتغفلوا عن عملهم، فلا تحققوا لهم هذا الغرض، بل اجتنبوهم وامقتوهم، لأنهم فى أشد درجات الخبث النفسى والكفر، ومصيرهم إلى جهنم، عقاباً على ما اقترفوه من ذنوب وأوزار. .',NULL,'276',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1335,'<span data-type="title" id=toc-1344>٩٦ </span>- يُقْسمون لكم طمعا فى رضائكم عنهم، فإن خُدِعْتم بأيمانهم ورضيتم عنهم، فإن رضاكم - وحدكم - لا ينفعهم، ذلك لأن الله ساخط عليهم لفسقهم وخروجهم على الدين.',NULL,'276',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1336,'<span data-type="title" id=toc-1345>٩٧ </span>- الأعراب من أهل البادية أشد جُحودا ونفاقا، وقد بلغوا فى ذلك غاية الشدة، وذلك لبعدهم عن أصل الحكمة ومنابع العلم، وهم حقيقون بأن يجهلوا حدود اللَّه، وما أنزل على رسوله من شرائع وأحكام، واللَّه عليم بأحوال الفريقين، حكيم فيما يقدره من جزاء.',NULL,'277',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1337,'<span data-type="title" id=toc-1346>٩٨ </span>- وبعض هؤلاء المنافقين من أهل البادية، يعتبرون الإنفاق فى سبيل اللَّه غرامة وخسراناً، لعدم اعتقادهم فى ثوابه تعالى، ويتوقعون وينتظرون أن تدور عليكم الحرب - أيها المؤمنون - ألا رَدَّ اللَّه تلك المصائب عليهم، وجعل الشر الذى ينتظرونه لكم محيطا بهم، واللَّه سميع بأقوالهم، عليم بأفعالهم ونياتهم، وبما يقترفون من آثام.',NULL,'277',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1338,'<span data-type="title" id=toc-1347>٩٩ </span>- وليس كل الأعراب كذلك، فمنهم مؤمنون باللَّه مصدقون بيوم القيامة، يتخذون الإنفاق فى سبيل اللَّه وسيلة يتقربون بها إلى اللَّه، وسببا لدعاء الرسول لهم، إذ كان يدعو للمتصدقين بالخير والبركة، وهى لا شك قربة عظيمة توصلهم إلى ما يبتغون، فإن اللَّه سيغمرهم برحمته، لأنه الغفور للذنوب، الرحيم بخلقه.',NULL,'277',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1339,'<span data-type="title" id=toc-1348>١٠٠ </span>- والمؤمنون - الذين سبقوا إلى الإسلام - من المهاجرين والأنصار، الذين ساروا على نهجهم فأحسنوا ولم يقصروا - يرضى اللَّه عنهم، فيقبل منهم ويجزيهم خيرا، وهم كذلك يرضون ويستبشرون بما أعد اللَّه لهم من جنات تجرى الأنهار تحت أشجارها، فينعمون فيها نعيما أبديا، وذلك هو الفوز العظيم.',NULL,'277',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1340,'<span data-type="title" id=toc-1349>١٠١ </span>- وممن يجاور المدينة من أهل البادية مَنْ يضمر الكفر ويُظهر الإيمان، ومن سكان المدينة قوم مرنوا على النفاق، حتى برعوا فيه، ستروه عن الناس حتى لقد خفى أمرهم عليك - أيها الرسول - ولكن اللَّه هو الذى يعلم حقيقتهم، وسيعذبهم فى الدنيا مرتين: مرة بنصركم على أعدائكم الذين يغيظهم، ومرة بفضيحتهم وكشف نفاقهم، ثم يردون فى الآخرة إلى عذاب النار وهولها الشديد.',NULL,'278',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1341,'<span data-type="title" id=toc-1350>١٠٢ </span>- وهناك ناس آخرون آذوكم، ثم من بعد ذلك اعترفوا بما أذنبوا، وسلكوا طريق الحق، فهؤلاء قد أتوا عملا صالحا وعملا سيئا، وإنهم لهذا يرجى لهم أن تقبل توبتهم، وإن اللَّه رحيم بعباده، يقبل توبتهم ويغفر لهم.',NULL,'278',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1342,'<span data-type="title" id=toc-1351>١٠٣ </span>- خذ - أيها الرسول - من أموال هؤلاء التائبين صدقات تطهرهم بها من الذنوب والشح، وترفع درجاتهم عند اللَّه، وادع لهم بالخير والهداية فإن دعاءك تسكن به نفوسهم، وتطمئن به قلوبهم، واللَّه سميع للدعاء، عليم بالمخلصين فى توبتهم.',NULL,'278',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1343,'<span data-type="title" id=toc-1352>١٠٤ </span>- ألا فليعلم هؤلاء التائبون أن اللَّه - وحده - هو الذى يقبل التوبة الخالصة، والصدقة الطيبة، وأنه سبحانه، هو الواسع الفضل فى قبول التوبة، العظيم الرحمة بعباده.',NULL,'278',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1344,'<span data-type="title" id=toc-1353>١٠٥ </span>- وقل - أيها الرسول - للناس: اعملوا، ولا تقصروا فى عمل الخير وأداء الواجب؛ فإن اللَّه يعلم كل أعمالكم، وسيراها الرسول والمؤمنون، فيزنونها بميزان الإيمان، ويشهدون بمقتضاها، ثم تردون بعد الموت إلى من يعلم سركم وجهركم، فيجازيكم بأعمالكم، بعد أن ينبئكم بها صغيرها وكبيرها.',NULL,'278',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1345,'<span data-type="title" id=toc-1354>١٠٦ </span>- وهناك ناس آخرون وقعوا فى الذنوب، منها التخلف عن الجهاد، وليس فيهم نفاق، وهؤلاء مُرْجوْن لأمر اللَّه: إما أن يُعذبهم، وإما أن يتوب عليهم ويغفر لهم، واللَّه عليم بأحوالهم وما تنطوى عليه قلوبهم، حكيم فيما يفعله بعباده من ثواب أو عقاب.',NULL,'278',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1346,'<span data-type="title" id=toc-1355>١٠٧ </span>- ومن المنافقين جماعة بنوا مسجدا لا يبتغون به وجه اللَّه، وإنما يبتغون به الضرار والكفر والتفريق بين جماعة المؤمنين، وأنهم سيحلفون على أنهم ما أرادوا ببناء هذا المسجد إلا الخير والعمل الأحسن، واللَّه يشهد عليهم أنهم كاذبون فى أيمانهم.',NULL,'279',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1347,'<span data-type="title" id=toc-1356>١٠٨ </span>- لا تصل - أيها الرسول - فى هذا المسجد أبدا، وإن مسجداً أقيم ابتغاء وجه اللَّه وطلبا لمرضاته من أول أمره كمسجد قُباء لجدير بأن تؤدى فيه شعائر اللَّه، وفى هذا المسجد رجال يحبون أن يُطهروا أجسادهم وقلوبهم بأداء العبادة الصحيحة فيه، واللَّه يحب ويثيب الذين يتقربون إليه بالطهارة الجسمية والمعنوية.',NULL,'279',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1348,'<span data-type="title" id=toc-1357>١٠٩ </span>- لا يستوى فى عقيدته ولا فى عمله من أقام بنيانه على الإخلاص فى تقوى اللَّه وابتغاء رضائه، ومن أقام بنيانه على النفاق والكفر، فإن عمل المتقى مستقيم ثابت على أصل متين، وعمل المنافق كالبناء على حافة هاوية، فهو واه ساقط، يقع بصاحبه فى نار جهنم، واللَّه لا يهدى إلى طريق الرشاد من أصر على ظلم نفسه بالكفر.',NULL,'279',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1349,'<span data-type="title" id=toc-1358>١١٠ </span>- وسيظل هذا البناء الذى بناه المنافقون مصدر اضطراب وخوف فى قلوبهم لا ينتهى حتى تتقطع قلوبهم بالندم والتوبة أو بالموت، واللَّه عليم بكل شئ، حكيم فى أفعاله وجزائه.',NULL,'279',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1350,'<span data-type="title" id=toc-1359>١١١ </span>- يؤكد اللَّه وعده للمؤمنين الذين يبذلون أنفسهم وأموالهم فى سبيله، فإنه اشترى منهم تلك الأنفس والأموال بالجنة ثمنا لما بذلوا، فإنهم يجاهدون فى سبيل اللَّه فيقتلون أعداء اللَّه أو يستشهدون فى سبيله، وقد أثبت اللَّه هذا الوعد الحق فى التوراة والإنجيل، كما أثبته فى القرآن، وليس أحد أبر ولا أوفى بعهده من اللَّه، فافرحوا - أيها المؤمنون المجاهدون - بهذه المبايعة التى بذلتم فيها أنفسكم وأموالكم الفانية، وعُوضتم عنها بالجنة الباقية، وهذا الشراء والبيع هو الظفر الكبير لكم.',NULL,'280',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1351,'<span data-type="title" id=toc-1360>١١٢ </span>- إن أوصاف أولئك الذين باعوا أنفسهم للَّه بالجنة أنهم يكثرون التوبة من هفواتهم إلى اللَّه، ويحمدونه على كل حال، ويسعون فى سبيل الخير لأنفسهم ولغيرهم، ويحافظون على صلواتهم. ويؤدونها كاملة فى خشوع، ويأمرون بكل خير يوافق ما جاء به الشرع، وينهون عن كل شر يأباه الدين ويلتزمون بشريعة اللَّه، وبشر - أيها الرسول - المؤمنين.',NULL,'280',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1352,'<span data-type="title" id=toc-1361>١١٣ </span>- ليس للنبى وللمؤمنين أن يطلبوا المغفرة للمشركين، ولو كانوا أقرب الناس إليهم، من بعد أن يعلم المؤمنون من أمر هؤلاء المشركين بموتهم على الكفر، أنهم مستحقون للخلود فى النار.',NULL,'280',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1353,'<span data-type="title" id=toc-1362>١١٤ </span>- لم يكن ما فعله إبراهيم - عليه السلام - من الاستغفار لأبيه، إلا تحقيقا لوعد من إبراهيم لأبيه، رجاء إيمانه، فلمَّا تبين لإبراهيم أن أباه عدو للَّه، بإصراره على الشرك حتى مات عليه، تبرأ منه وترك الاستغفار له، ولقد كان إبراهيم كثير الدعاء والتضرع للَّه صبورا على الأذى.',NULL,'280',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1354,'<span data-type="title" id=toc-1363>١١٥ </span>- وما كان من سنن اللَّه ولطفه بعباده أن يصف قوما بالضلال، ويجرى عليهم أحكامه بالذم والعقاب بعد أن أرشدهم إلى الإسلام، حتى يتبين لهم عن طريق الوحى إلى رسوله ما يجب عليهم اجتنابه، إن اللَّه محيط علمه بكل شئ.',NULL,'280',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1355,'<span data-type="title" id=toc-1364>١١٦ </span>- إن اللَّه - وحده - مالك السموات والأرض وما فيهما، وهو المتصرف فيهما بالإحياء والإماتة، وليس لكم سوى اللَّه من ولى يتولى أمركم، ولا نصير ينصركم ويدافع عنكم.',NULL,'281',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1356,'<span data-type="title" id=toc-1365>١١٧ </span>- لقد تفضل الله - سبحانه - على نبيه، وأصحابه المؤمنين الصادقين من المهاجرين والأنصار، الذين خرجوا معه إلى الجهاد فى وقت الشدة (فى غزوة تبوك) فثبتهم وصانهم عن التخلف، من بعد ما اشتد الضيق بفريق منهم، حتى كادت قلوبهم تميل إلى التخلف عن الجهاد، ثم غفر اللَّه لهم هذا الهم الذى خطر بنفوسهم، إنه - سبحانه - كثير الرأفة بهم، عظيم الرحمة.',NULL,'281',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1357,'<span data-type="title" id=toc-1366>١١٨ </span>- وتفضل - سبحانه - بالعفو عن الرجال الثلاثة الذين تخلفوا عن الخروج فى غزوة تبوك - لا عن نفاق منهم - وكان أمرهم مرجأ إلى أن يبين اللَّه حكمه فيهم، فلما كانت توبتهم خالصة، وندمهم شديدا؛ حتى شعروا بأن الأرض قد ضاقت عليهم على رحبها وسعتها، وضاقت عليهم نفوسهم هما وحزنا، وعلموا أنه لا ملجأ من غضب اللَّه إلا باستغفاره والرجوع إليه، حينئذ هداهم اللَّه إلى التوبة، وعفا عنهم، ليظلوا عليها، إن اللَّه كثير القبول لتوبة التائبين، عظيم الرحمة بعباده.',NULL,'281',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1358,'<span data-type="title" id=toc-1367>١١٩ </span>- يا أيها الذين آمنوا اثبتوا على التقوى والإيمان، وكونوا مع الذين صدقوا فى أقوالهم وأفعالهم.',NULL,'281',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1359,'<span data-type="title" id=toc-1368>١٢٠ </span>- ما كان يحل لأهل المدينة، ومن يجاورونهم من سكان البوادى، أن يتخلفوا عن الغزو مع رسول اللَّه صلى اللَّه عليه وسلم، ولا أن يضنوا بأنفسهم عما بذل الرسول فيه نفسه، إذ أنهم لا يصيبهم فى سبيل اللَّه ظمأ أو تعب أو جوع، ولا ينزلون مكاناً يثير وجودهم فيه غيظ الكفار، ولا ينالون من عدو غرضا كالهزيمة أو الغنيمة إلا حُسِب لهم بذلك عمل طيب يجزون عليه أحسن الجزاء، وإن اللَّه لا يضيع أجر الذين أحسنوا فى أعمالهم.',NULL,'282',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1360,'<span data-type="title" id=toc-1369>١٢١ </span>- وكذلك لا يبذل المجاهدون أى مال - صغيرا أو كبيرا - ولا يسافرون أى سفر فى سبيل اللَّه، إلا كتبه اللَّه لهم فى صحائف أعمالهم الصالحة، لينالوا به أحسن ما يستحقه العاملون من جزاء.',NULL,'282',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1361,'<span data-type="title" id=toc-1370>١٢٢ </span>- ليس للمؤمنين أن يخرجوا جميعا إلى النبى صلى اللَّه عليه وسلم إذا لم يقتض الأمر ذلك، فليكن الأمر أن تخرج إلى الرسول طائفة ليتفقهوا فى دينهم، وليدعوا قومهم بالإنذار والتبشير حينما يرجعون إليهم ليثبتوا دائما على الحق، وليحذروا الباطل والضلال.',NULL,'282',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1362,'<span data-type="title" id=toc-1371>١٢٣ </span>- يا أيها الذين آمنوا قاتلوا الكفار الذين يجاورونكم، حتى لا يكونوا مصدر خطر عليكم، وكونوا أشدَّاء عليهم فى القتال، ولا تأخذكم بهم رأفة، واعلموا أن اللَّه بعونه ونصره مع الذين يتقونه.',NULL,'282',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1363,'<span data-type="title" id=toc-1372>١٢٤ </span>- وإذا ما أنزلت سورة من سور القرآن، وسمعها المنافقون سخروا واستهزأوا، وقال بعضهم لبعض: أيكم زادته هذه السورة إيمانا؟ ولقد رد اللَّه عليهم بأن هناك فرقا بين المنافقين والمؤمنين: فأما المؤمنون الذين أبصروا النور، وعرفوا الحق، فقد زادتهم آيات اللَّه إيمانا، وهم عند نزولها يفرحون ويستبشرون.',NULL,'282',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1364,'<span data-type="title" id=toc-1373>١٢٥ </span>- وأما المنافقون الذين مرضت قلوبهم وعميت بصائرهم عن الحق فقد زادتهم كفرا إلى كفرهم، وماتوا وهم كافرون.',NULL,'282',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1365,'<span data-type="title" id=toc-1374>١٢٦ </span>- أو لا يعتبر المنافقون بما يبتليهم اللَّه به فى كل عام مرة أو مرات من ألوان البلاء بكشف أستارهم، وظهور أحوالهم، ونصر المؤمنين، وظهور باطلهم، ثم لا يتوبون عما هم فيه، ولا هم يذكرون ما وقع لهم؟',NULL,'283',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1366,'<span data-type="title" id=toc-1375>١٢٧ </span>- وكذلك إذا ما أنزلت سورة، وهم فى مجلس الرسول، تغامزوا، وقال بعضهم لبعض: هل يراكم أحد؟ ثم انصرفت قلوبهم عن متابعته والإيمان به، زادهم اللَّه ضلالا بسبب تماديهم فى الباطل وإعراضهم عن الحق، لأنهم قوم لا يفقهون.',NULL,'283',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1367,'<span data-type="title" id=toc-1376>١٢٨ </span>- لقد جاءكم - أيها الناس - رسول من البشر مثلكم فى تكوينه، يشق عليه ما يصيبكم من الضرر، وهو حريص على هدايتكم، وبالمؤمنين عظيم العطف والرحمة.',NULL,'283',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1368,'<span data-type="title" id=toc-1377>١٢٩ </span>- فإن أعرضوا عن الإيمان بك - أيها الرسول - فلا تحزن لإعراضهم، واعتز بربك، وقل: يكفينى اللَّه الذى لا إله غيره، عليه - وحده - توكلت، وهو مالك الملك، ورب الكون، وصاحب السلطان العظيم.',NULL,'283',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1369,'<span data-type="title" id=toc-1379>١ </span>- هذه حروف بدأ الله تعالى بها السورة، وهو أعلم بمراده منها، وهى مع ذلك تشير إلى أن القرآن مُكَوَّن من مثل هذه الحروف، ومع ذلك عجزتم عن أن تأتوا بمثله، وهذه الحروف الصوتية تثير انتباه المشركين فيستمعون إليه، وإن اتفقوا على عدم استماع هذه الآيات الكريمة ونحوها التى هى آيات القرآن المحكم فى أسلوبه ومعانيه، الذى اشتمل على الحكمة وما ينفع الناس فى أمور دينهم ودنياهم.',NULL,'284',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1370,'<span data-type="title" id=toc-1380>٢ </span>- ما كان للناس أن يَعجبوا ويُنكروا وحْينا إلى رجل منهم (محمد) ، ليُحذّر الناس من عذاب الله، ويُبَشّر الذين آمنوا منهم بأن لهم منزلة عالية عند ربهم، لا يتخلف وعد الله، وما كان لهؤلاء المنكرين أن يقولوا عن محمد - رسولنا -: إنه ساحر واضح أمره.',NULL,'284',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1371,'<span data-type="title" id=toc-1381>٣ </span>- إن ربكم - أيها الناس - هو الله الذى خلق السموات والأرض وما فيهما فى ستة أيام لا يعلم إلا الله مداها. ثم هيْمن - بعظيم سلطانه - وحده، ودبَّر أمور مخلوقاته، فليس لأحد سلطان مع الله فى شئ، ولا يستطيع أحد من خلقه أن يشفع لأحد إلا بإذنه. ذلكم الله الخالق، هو ربكم وولى نعمتكم فاعبدوه - وحده - وصدقوا رسوله، وآمنوا بكتابه. فعليكم أن تذكروا نعمة الله وتتدبروا آياته الدالة على وحدانيته.',NULL,'285',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1372,'<span data-type="title" id=toc-1382>٤ </span>- وكما بدأ الله الخلق فإليه - وحده - مرجعكم، ومرجع المخلوقات كلها، وقد وعد الله بذلك وعداً صادقاً لا يتخلف. وإنه سبحانه بدأ الخلق بقدرته، وبعد فنائه سيعيده بقدرته، ليثيب المؤمنين المطيعين بعدله التام، وأما الكافرون فلهم شراب فى جهنم شديد الغليان، ولهم عذاب موجع جزاء كفرهم.',NULL,'285',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1373,'<span data-type="title" id=toc-1383>٥ </span>- وربكم الذى خلق السموات والأرض، والذى جعل الشمس تشع الضياء، والقمر يرسل النور، وجعل للقمر منازل ينتقل فيها، فيختلف نوره تبعاً لهذه المنازل، لتستعينوا بهذا فى تقدير مواقيتكم، وتعلموا عدد السنين والحساب، وما خلق الله ذلك إلا بالحكمة، وهو سبحانه يبسط فى كتابه الآيات الدالة على ألوهيته وكمال قدرته، لكى تتدبروها بعقولكم وتستجيبوا لما يقتضيه العلم.',NULL,'285',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1374,'<span data-type="title" id=toc-1384>٦ </span>- إن فى تعاقب الليل والنهار واختلافهما بالزيادة والنقصان، وفى خلْق السموات والأرض وما فيهما من الكائنات، لأدلة واضحة وحُججاً بينة على ألوهية الخالق وقدرته لمن يتجنبون غضبه ويخافون عذابه.',NULL,'286',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1375,'<span data-type="title" id=toc-1385>٧ </span>- إن الذين لا يؤمنون بالبعث ولقاء الله فى اليوم الآخر، واعتقدوا - واهمين - أن الحياة الدنيا هى منتهاهم وليس بعدها حياة، فاطمأنوا بها، ولم يعملوا لما بعدها، وغفلوا عن آيات الله الدالة على البعث والحساب.',NULL,'286',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1376,'<span data-type="title" id=toc-1386>٨ </span>- هؤلاء مأواهم الذى يستقرون فيه هو النار، جزاء ما كسبوا من الكفر وقبيح الأعمال.',NULL,'286',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1377,'<span data-type="title" id=toc-1387>٩ </span>- إن الذين آمنوا إيماناً صحيحاً، وعملوا الأعمال الصالحة فى دنياهم يثبتهم ربهم على الهداية بسبب إيمانهم، ويدخلون يوم القيامة جنات تجرى الأنهار خلالها، وينعمون فيها نعيماً خالداً.',NULL,'286',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1378,'<span data-type="title" id=toc-1388>١٠ </span>- دعاء المؤمنين فى هذه الجنات تسبيح وتنزيه لله عما كان يقوله الكافرون فى الدنيا، وتحية الله لهم، وتحية بعضهم لبعض تقرير للأمن والاطمئنان، وخاتم دعائهم دائماً حمد الله على توفيقه إياهم بالإيمان، وظفرهم برضوانه عليهم.',NULL,'286',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1379,'<span data-type="title" id=toc-1389>١١ </span>- ولو أجاب الله ما يستعجل به الناس على أنفسهم من الشر مثل استعجالهم لطلب الخير، لأهلكهم وأبادهم جميعاً، ولكنه يتلطف بهم، فيرجئ هلاكهم، انتظاراً لما يظهر منهم حسب ما علمه فيهم، فتتضح عدالته فى جزائهم، إذ يتركون - والأدلة قائمة عليهم - يتعمدون الانحراف والاتجاه إلى طريق الضلال والظلم.',NULL,'286',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1380,'<span data-type="title" id=toc-1390>١٢ </span>- وإذا أصاب الإنسان ضر فى نفسه أو ماله أو نحو ذلك، أحس بضعفه ودعا ربه على أى حال من حالاته، مضطجعاً أو قاعداً أو قائماً، أن يكشف ما نزل به من محنته، فلما استجاب الله له، فكشف عنه ضره، انصرف عن جانب الله واستمر على عصيانه، ونسى فضل الله عليه، كأنه لم يصبه ضر ولم يدع الله إلى كشفه، وكمثل هذا المسلك زيَّن الشيطان للكافرين ما عملوا من سوء وما اقترفوا من باطل.',NULL,'287',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1381,'<span data-type="title" id=toc-1391>١٣ </span>- ولقد أهلكنا الأمم السابقة عليكم بسبب كفرهم حين جاءتهم رسلهم بالآيات الواضحة على صدق دعوتهم إلى الإيمان، وما كان فى علم الله أن يحصل منهم إيمان، بسبب تشبثهم بالكفر والعصيان، فاعتبروا يا كفار قريش، فكما أهلكنا مَنْ قبلكم، سنجزى المجرمين بإهلاكهم.',NULL,'287',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1382,'<span data-type="title" id=toc-1392>١٤ </span>- ثم جعلناكم - يا أمة محمد - خلفاء فى الأرض، تعمرونها من بعد هؤلاء السابقين، لنختبركم ونظهر ما تختارونه لأنفسكم من طاعة أو عصيان، بعد أن عرفتم ما جرى على أسلافكم.',NULL,'287',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1383,'<span data-type="title" id=toc-1393>١٥ </span>- وحينما تجلت آيات القرآن من رسولنا - محمد - على المشركين، قال له الكافرون الذين لا يخافون عذاب الله ولا يرجون ثوابه: آتنا كتاباً غير هذا القرآن، أو بَدِّل ما فيه مما لا يعجبنا. قل لهم - أيها الرسول: لا يمكننى ولا يجوز أن أغير أو أبدل فيه من عندى. ما أنا إلا متبع ومبلغ ما يوحى إلىَّ من ربى، إنى أخاف إن خالفت وحى ربى عذاب يوم عظيم خطره، شديد هوله.',NULL,'287',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1384,'<span data-type="title" id=toc-1394>١٦ </span>- قل لهم - يا أيها الرسول -: لو شاء الله ألا ينزل علىَّ قرآناً من عنده، وألا أبلغكم به ما أنزله، وما تلوته عليكم، ولا أعلمكم الله به. لكنه نزل، وأرسلنى به، وتلوته عليكم كما أمرنى، وقد مكثت بينكم زمناً طويلا قبل البعث لم أدّع فيه الرسالة، ولم أتل عليكم شيئاً، وأنتم تشهدون لى بالصدق والأمانة، ولكن جاء الوحى به فأمرت بتلاوته، ألا فاعقلوا الأمور وأدركوها، واربطوا بين الماضى والحاضر.',NULL,'288',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1385,'<span data-type="title" id=toc-1395>١٧ </span>- ليس هناك أشد ظلماً لنفسه ممن كفر وافترى الكذب على الله، أو كذب بآيات الله التى جاء بها رسوله. إنه لا ينجح الكافر فى عمله، وقد خسر خسراناً مبيناً بكفره، ومغاضبته لله تعالى.',NULL,'288',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1386,'<span data-type="title" id=toc-1396>١٨ </span>- ويعبد هؤلاء المشركون - المفترون على الله بالشرك - أصناماً باطلة، لا تضرهم ولا تنفعهم، ويقولون: هؤلاء الأصنام يشفعون لنا عند الله فى الآخرة، قل لهم - أيها الرسول -: هل تخبرون الله بشريك لا يعلم الله له وجوداً فى السموات ولا فى الأرض؟! تنزه الله عن الشريك وعما تزعمونه بعبادة هؤلاء الشركاء.',NULL,'288',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1387,'<span data-type="title" id=toc-1397>١٩ </span>- وما كان الناس فى تكوينهم إلا أمة واحدة بمقتضى الفطرة، ثم بعثنا إليهم الرسل لإرشادهم وهدايتهم بمقتضى وحى الله تعالى، فكانت تلك الطبيعة الإنسانية التى استعدت للخير والشر سبباً فى أن يغلب الشر على بعضهم، وتحكم الأهواء ونزغات الشيطان، فاختلفوا بسبب ذلك. ولولا حكم سابق من ربك بإمهال الكافرين بك - أيها النبى - وإرجاء هلاكهم إلى موعد محدد عنده، لعجل لهم الهلاك والعذاب، بسبب الخلاف الذى وقعوا فيه، كما وقع لأمم سابقة.',NULL,'288',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1388,'<span data-type="title" id=toc-1398>٢٠ </span>- ويقول هؤلاء المشركون: هلا أنزل على محمد معجزة من عند الله غير القرآن، تقنعنا بصدق رسالته؟ فقل لهم - أيها الرسول -: إن نزول الآيات غيب، ولا أحد يعلم الغيب إلا الله، وإن كان القرآن لا يقنعكم فانتظروا قضاء الله بينى وبينكم فيما تجحدونه، إنى معكم من المنتظرين.',NULL,'288',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1389,'<span data-type="title" id=toc-1399>٢١ </span>- ومن شأن الناس أننا إذا أنعمنا عليهم، من بعد شدة أصابتهم فى أنفسهم أو أهليهم أو أموالهم، لم يشكروا الله على ما أنعم به عليهم بعد صرف الضر عنهم، بل هم يقابلون ذلك بالإمعان فى التكذيب والكفر بالآيات. قل - أيها الرسول -: إن الله قادر على إهلاككم والإسراع بتعذيبكم، لولا حكم سابق منه بإمهالكم إلى موعد اختص - وحده - بعلمه. إن رسلنا من الملائكة الموكلين بكم يكتبون ما تمكرون، وسيحاسبكم ويجازيكم.',NULL,'289',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1390,'<span data-type="title" id=toc-1400>٢٢ </span>- الله الذى تكفرون بنعمه، وتُكَذِّبون بآياته، هو الذى يُمكِّنكم من السير والسعى فى البر مشاة وركباناً، وفى البحر بما سخَّر لكم من السفن التى تجرى على الماء، بما يهيئ الله لها من ريح طيبة تدفعها فى أمان إلى غايتها، حتى إذا اطمأننتم إليها وفرحتم بها هبت ريح عاصفة أثارت عليكم الموج من كل جانب، وأيقنتم أن الهلاك واقع لا محالة، فى هذه الشدة لا تجدون ملجأ غير الله فتدعونه مخلصين فى الدعاء، وموقنين أنه لا منقذ لكم سواه، متعهدين له لئن أنجاكم من هذه الكربة لتؤمنن به ولتكونن من الشاكرين.',NULL,'289',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1391,'<span data-type="title" id=toc-1401>٢٣ </span>- فلما أنجاهم مما تعرضوا له من الهلاك، نقضوا عهدهم، وعادوا مسرعين إلى الفساد الذى كانوا من قبل - يا أيها الناس - الناقضون للعهد إن عاقبة اعتدائكم وظلمكم سترجع عليكم - وحدكم - وإن ما تتمتعون به فى دنياكم متاع دنيوى زائل، ثم إلى الله مصيركم فى النهاية فيجزيكم بأعمالكم التى أسلفتموها فى دنياكم.',NULL,'289',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1392,'<span data-type="title" id=toc-1402>٢٤ </span>- ما حالة الحياة الدنيا فى روعتها وبهجتها، ثم فى فنائها بعد ذلك، إلا كحالة الماء ينزل من السماء، فيختلط به نبات الأرض، مما يأكله الناس والحيوان، فيزدهر ويثمر وتزدان به الأرض نضارة وبهجة، حتى إذا بلغت هذه الزينة تمامها، وأيقن أهلها أنهم مالكون زمامها ومنتفعون بثمارها وخيراتها، فاجأها أمرنا بزوالها فجعلناها شيئاً محصوداً، كأن لم تكن آهلة بسكانها وآخذة بهجتها من قبل، ففى كلتا الحالتين نضارة وازدهار يبتهج بهما الناس، ثم يعقبهما زوال ودمار، وكما بيَّن الله ذلك بالأمثال الواضحة، يبيِّن الآيات ويفصل ما فيها من أحكام وآيات لقوم يتفكرون ويعقلون.',NULL,'290',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1393,'<span data-type="title" id=toc-1403>٢٥ </span>- والله يدعو عباده بالإيمان والعمل الصالح إلى الجنة دار الأمن والاطمئنان، وهو سبحانه يهدى من يشاء هدايته - لحسن استعداده وميله إلى الخير - إلى الطريق الحق وهو السلام.',NULL,'290',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1394,'<span data-type="title" id=toc-1404>٢٦ </span>- للذين أحسنوا بالاستجابة لدعوة الله، فآمنوا وعملوا الخير لدينهم ودنياهم، لهم المنزلة الحسنى فى الآخرة وهى الجنة، ولهم زيادة على ذلك فضلا من الله وتكريماً، ولا يغشى وجوههم كآبة من همّ وهوان، وهؤلاء هم أهل الجنة الذين ينعمون فيها أبداً.',NULL,'290',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1395,'<span data-type="title" id=toc-1405>٢٧ </span>- والذين لم يستجيبوا لدعوة الله، فكفروا واقترفوا المعاصى فسيجزون بمثل ما عملوا من سوء، ويغشاهم الهوان، وليس لهم واقٍ يمنعهم من عذاب الله، ووجوهم مسودة من الغم والكآبة كأنما أسدل عليها سواد من ظلمة الليل، وهم أهل النار يشقون فيها أبداً.',NULL,'290',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1396,'<span data-type="title" id=toc-1406>٢٨ </span>- واذكر - أيها الرسول - هوْل الموقف، يوم نجمع الخلائق كافة، ثم نقول للذين أشركوا فى عبادتهم مع الله غيره: قفوا مكانكم أنتم ومن اتخذتموهم شركاء من دون الله، حتى تنظروا ما يفعل بكم، فوقعت الفرقة بين المشركين والشركاء، وتبرأ الشركاء من عابديهم، قائلين لهم: لم ندعكم إلى عبادتنا، وما كنتم تعبدوننا، وإنما كنتم تعبدون أهواءكم.',NULL,'290',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1397,'<span data-type="title" id=toc-1407>٢٩ </span>- ويكفينا الله بعلمه وحكمه شهيداً وفاصلاً بيننا وبينكم. إنا كنا بمعزل عنكم لا نشعر بعبادتكم لنا.',NULL,'291',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1398,'<span data-type="title" id=toc-1408>٣٠ </span>- فى ذلك الموقف تعلم كل نفس ما قدَّمت من خير أو شر، وتلقى جزاءها. وفى هذا الموقف أيقن المشركون بوحدانية الله الحق، وبطل كل ما كانوا يفترونه على الله.',NULL,'291',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1399,'<span data-type="title" id=toc-1409>٣١ </span>- ادع - أيها الرسول - إلى التوحيد الخالص، وقل: مَنْ الذى يأتيكم بالرزق من السماء بإنزال المطر، ومن الأرض بإخراج النبات والثمر؟ ومَن الذى يمنحكم السمع والأبصار؟ ومن يخرج الحى من الميت كالنبات وهو حى من الأرض وهى موات؟ ومن يخرج الميت من الحى كالإنسان يسلب عنه الحياة؟ ومن الذى يُدَبِّر ويصرف جميع أمور العالم كله بقدرته وحكمته؟ فسيعترفون - لا مناص - بأن الله - وحده - فاعل هذا كله. فقل لهم - أيها الرسول - عند اعترافهم بذلك: أليس الواجب المؤكد أن تذعنوا للحق وتخافوا الله مالك الملك.',NULL,'291',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1400,'<span data-type="title" id=toc-1410>٣٢ </span>- فذلكم الله الذى أقررتم به، هو - وحده - ربكم الذى تحققت ربوبيته، ووجبت عبادته دون سواه، وليس بعد الحق من توحيد الله وعبادته إلا الوقوع فى الضلال، وهو الإشراك بالله وعبادة غيره. فكيف تنصرفون عن الحق إلى الباطل؟ .',NULL,'291',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1401,'<span data-type="title" id=toc-1411>٣٣ </span>- كما تحققت ألوهية الله ووجبت عبادته، حق قضاؤه على الذين خرجوا عن أمر الله متمردين بأنهم لا يذعنون للحق، لأن الله تعالى لا يهدى إلى الحق إلا من سلك طريقه، لا من تمرد عليه.',NULL,'291',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1402,'<span data-type="title" id=toc-1412>٣٤ </span>- قل - أيها الرسول - لهؤلاء المشركين: هل من معبوداتكم - التى جعلتموها شركاء لله - مَنْ يستطيع أن ينشئ الخلق ابتداء، ثم يعيده بعد فنائه؟ إنهم سيعجزون عن الجواب، فقل لهم حينئذ: الله - وحده - هو الذى ينشئ الخلق من عدم، ثم يعيده بعد فنائه، فكيف تنصرفون عن الإيمان به؟',NULL,'291',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1403,'<span data-type="title" id=toc-1413>٣٥ </span>- قل - أيها الرسول - لهؤلاء المشركين: هل من معبوداتكم التى جعلتموها شركاء لله مَن يستطيع التمييز بين الهدى والضلال، فيرشد سواه إلى السبيل الحق؟ فسيعجزون! فهل القادر على الهداية إلى الحق أولى بالاتباع والعبادة؟ أم الذى لا يستطيع أن يهتدى فى نفسه، وهو بالأولى لا يهدى غيره، اللهم إلا إذا هداه غيره؟ كرؤوس الكفر والأحبار والرهبان الذين اتخذتموهم أرباباً من دون الله. فما الذى جعلكم تنحرفون حتى أشركتم هؤلاء بالله؟ وما هذه الحال العجيبة التى تجركم إلى تلك الأحكام الغريبة.',NULL,'292',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1404,'<span data-type="title" id=toc-1414>٣٦ </span>- وما يتبع أكثر المشركين فى معتقداتهم إلا ظنوناً باطلة لا دليل عليها، والظن - على وجه العموم - لا يفيد، ولا يغنى عن العلم الحق أى غناء، ولا سيما إذا كان ظناً وهمياً كظن هؤلاء المشركين. وإن الله عليم بما يفعله رؤساء الكفر وأتباعهم الذين يقلدونهم، وسيجازيهم على ذلك.',NULL,'292',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1405,'<span data-type="title" id=toc-1415>٣٧ </span>- وما كان يتأتى فى هذا القرآن أن يفتريه أحد، لأنه فى إعجازه وهدايته وإحكامه لا يمكن أن يكون من عند غير الله. وليس هو إلا مصدقاً لما سبقه من الكتب السماوية فيما جاءت به من الحق، وموضحاً لما كتب وأثبت من الحقائق والشرائع. لا شك فى أن هذا القرآن منزل من عند الله، وأنه معجز لا يقدر أحد على مثله.',NULL,'292',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1406,'<span data-type="title" id=toc-1416>٣٨ </span>- بل يقول هؤلاء المشركون: اختلق محمد هذا القرآن من عنده، فقل لهم - أيها الرسول -: إن كان هذا القرآن من عمل البشر، فأتوا أنتم بسورة واحدة مماثلة له، واستعينوا على ذلك بمن تشاءون من دون الله، إن كنتم صادقين فى زعمكم أن القرآن من عندى.',NULL,'292',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1407,'<span data-type="title" id=toc-1417>٣٩ </span>- بل سارع هؤلاء المشركون إلى تكذيب القرآن من غير أن يتدبروا، ويعلموا ما فيه، فلم ينظروا فيه بأنفسهم، ولم يقفوا على تفسيره وبيان أحكامه بالرجوع إلى غيرهم، وبمثل هذه الطريقة فى التكذيب من غير علم، كذب الكافرون من الأمم السابقة رسلهم وكتبهم، فانظر - أيها الإنسان - ما آل إليه أمر المكذبين السابقين من خذلانهم وهلاكهم بالعذاب، وهذه سنة الله فى أمثالهم.',NULL,'293',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1408,'<span data-type="title" id=toc-1418>٤٠ </span>- ومن هؤلاء المكذبين من سيؤمر بالقرآن بعد أن يفطن إلى ما فيه، ويتنبه لمعانيه، ومنهم فريق لا يؤمن به ولا يتحول عن ضلاله، والله سبحانه وتعالى أعلم بالمكذبين المفسدين، وسيجازيهم على ما فعلوه.',NULL,'293',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1409,'<span data-type="title" id=toc-1419>٤١ </span>- وإن أصروا على تكذيبك - أيها الرسول - بعد وضوح الأدلة على نبوتك - فقل لهم: إن لى جزاء عملى، ولكم جزاء عملكم كيفما كان، وإنى مستمر فى دعوتى، وأنتم لا تؤاخذون بعملى، وأنا لا أؤاخذ بعملكم، فافعلوا ما شئتم وسيجازى الله كلا بما كسب.',NULL,'293',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1410,'<span data-type="title" id=toc-1420>٤٢ </span>- ومن هؤلاء الكفار من يستمع إليك - أيها الرسول - حين تدعوهم إلى دين الله، وقد أغلقت قلوبهم دون قبول دعوتك، فأنت لا تقدر على إسماع هؤلاء الصم وهدايتهم، وخاصة إذا أضيف إلى صممهم عدم تفهمهم لما تقول.',NULL,'293',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1411,'<span data-type="title" id=toc-1421>٤٣ </span>- ومنهم من ينظر إليك ويفكر فى شأنك، فيرى دلائل نبوتك الواضحة، ولكن لا يهتدى بها، فمثله فى ذلك مثل الأعمى، ولست بقادر على هداية هؤلاء العمى، فعمى البصر كعمى البصيرة، كلاهما لا هداية له، فالأعمى لا يهتدى حساً، والضالُّ لا يهتدى معنى.',NULL,'293',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1412,'<span data-type="title" id=toc-1422>٤٤ </span>- إن الله سبحانه سيجازى الناس بأعمالهم بالعدل والقسطاس، ولا يظلم أحداً منهم شيئاً، ولكن الناس الذين يظلمون أنفسهم باختيارهم الكفر على الإيمان.',NULL,'293',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1413,'<span data-type="title" id=toc-1423>٤٥ </span>- وأنذرهم - أيها الرسول - يوم نجمعهم للحساب، فيتحققون مجئ اليوم الآخر بعد أن كانوا يكذبون به، ويتذكرون حياتهم فى الدنيا، كأنها ساعة من النهار لم تتسع لما كان ينبغى من عمل الخير، ويعرف بعضهم بعضا، يتلاومون على ما كانوا عليه من الكفر والضلال. قد خسر المكذبون باليوم الآخر، فلم يقدموا فى دنياهم عملا صالحاً، ولم يظفروا بنعيم الآخرة بكفرهم.',NULL,'294',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1414,'<span data-type="title" id=toc-1424>٤٦ </span>- وإن أريناك - أيها الرسول - بعض الذى نعدهم به، من نصرتك عليهم، وإلحاق العذاب بهم، أو نتوفينك قبل أن ترى كل ذلك، فلا مناص من عودتهم إلينا للحساب والجزاء. والله سبحانه رقيب وعالم بكل ما يفعلونه، ومجازيهم به.',NULL,'294',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1415,'<span data-type="title" id=toc-1425>٤٧ </span>- ولقد جاء رسول لكل أمة فبلَّغها دعوة الله، فآمن مَن آمن، وكذّب مَن كذب، فإذا كان يوم الحشر، وجاء رسولهم وشهد على مكذبيه بالكفر، وللمؤمنين بالإيمان، فيحكم الله بينهم بالعدل التام، فلا يظلم أحداً فيما يستحقه من جزاء.',NULL,'294',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1416,'<span data-type="title" id=toc-1426>٤٨ </span>- ويُمعن الكافرون فى التكذيب باليوم الآخر، فيستعجلونه متهكمين، ويقولون: متى يكون هذا الذى تعدنا به من العذاب، إن كنت - أيها الرسول - ومن معك، صادقين فيما تؤمنون به وتدعوننا إليه؟ .',NULL,'294',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1417,'<span data-type="title" id=toc-1427>٤٩ </span>- قل لهم - أيها الرسول - إننى لا أملك لنفسى خيراً ولا شراً، إلا ما أقدرنى الله عليه. فكيف أملك تقديم العقوبة؟ إن لكل أمة نهاية حددها الله أزلا، فإذا حانت هذه النهاية فلا يستطيعون التأخر عنها وقتاً ما، كما لا يستطيعون سبقها.',NULL,'294',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1418,'<span data-type="title" id=toc-1428>٥٠ </span>- قل لهؤلاء المكذبين المستعجلين وقوع العذاب: أخبرونى إن وقع بكم عذاب الله ليلا أو نهاراً، فأى فائدة يحصل عليها من استعجاله المجرمون الآثمون؟ والعذاب كله مكروه.',NULL,'294',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1419,'<span data-type="title" id=toc-1429>٥١ </span>- أتنكرون العذاب الآن، ثم إذا حل بكم يقال لكم توبيخاً: هل آمنتم به حين عاينتموه، وقد كنتم تستعجلونه فى الدنيا مستهينين جاحدين.',NULL,'294',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1420,'<span data-type="title" id=toc-1430>٥٢ </span>- ثم يقال يوم القيامة للذين ظلموا أنفسهم بالكفر والتكذيب: ذوقوا العذاب الدائم، لا تجزون الآن إلا على أعمالكم التى كسبتموها فى الدنيا.',NULL,'295',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1421,'<span data-type="title" id=toc-1431>٥٣ </span>- ويطلب الكفار منك - أيها الرسول - على سبيل الاستهزاء والإنكار - أن تخبرهم أحق ما جئت به من القرآن وما تعدهم به من البعث والعذاب؟ قل لهم: نعم وحق خالقى الذى أنشأنى إنه حاصل لا شك فيه، وما أنتم بغالبين ولا مانعين ما يريده الله بكم من العذاب.',NULL,'295',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1422,'<span data-type="title" id=toc-1432>٥٤ </span>- ولو أن كل ما فى الأرض مملوك لكل نفس ارتكبت ظلم الشرك والجحود، لارتضت أن تقدمه فداء لما تستقبل من عذاب تراه يوم القيامة وتعاين هوله، وحينئذ يتردد الندم والحسرة فى سرائرهم لعجزهم عن النطق به، ولشدة ما دهاهم من الفزع لرؤية العذاب، ونفذ فيهم قضاء الله بالعدل، وهم غير مظلومين فى هذا الجزاء. لأنه نتيجة ما قدَّموا فى الدنيا.',NULL,'295',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1423,'<span data-type="title" id=toc-1433>٥٥ </span>- ليعلم الناس أن الله مالك ومهيمن على جميع ما فى السموات والأرض، وليعلموا أن وعده حق، فلا يعجزه شئ، ولا يفلت من جزائه أحد، ولكنهم قد غرتهم الحياة الدنيا، لا يعلمون ذلك علم اليقين.',NULL,'295',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1424,'<span data-type="title" id=toc-1434>٥٦ </span>- والله سبحانه، يهب الحياة بعد عدم، ويسلبها بعد وجود، وإليه المرجع فى الآخرة، ومَن كان كذلك لا يعظم عليه شئ.',NULL,'295',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1425,'<span data-type="title" id=toc-1435>٥٧ </span>- يا أيها الناس: قد جاءكم على لسان الرسول محمد كتاب من الله، فيه تذكير بالإيمان والطاعة وعظة بالترغيب فى الخير، والترهيب من عمل السوء، وسوْق العبر بأخبار مَن سبقوكم، وتوجيه نظركم إلى عظمة الخلق لتدركوا عظمة الخالق، وفيه دواء لأمراض قلوبكم من الشرك والنفاق، وهداية إلى الطريق المستقيم. وذلك كله رحمة للمؤمنين الذين يستجيبون.',NULL,'295',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1426,'<span data-type="title" id=toc-1436>٥٨ </span>- قل لهم - أيها الرسول -: افرحوا بفضل الله عليكم ورحمته بكم، بإنزال القرآن، وبيان شريعة الإسلام، وهذا خير من كل ما يجمعه الناس من متاع الدنيا، لأنه غذاء القلوب وشفاء أسقامها.',NULL,'296',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1427,'<span data-type="title" id=toc-1437>٥٩ </span>- قل - أيها الرسول - للكفار الذين أوتوا بعض متاع الدنيا: أخبرونى عما منحكم الله من رزق حلال طيب، فأقمتم من أنفسكم مشرِّعين، تجعلون بعضه حلالا، وبعضه حراماً، دون أن تأخذوا بشرع الله؟ إن الله لم يأذن لكم فى هذا، بل أنتم تكذبون فى ذلك على الله.',NULL,'296',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1428,'<span data-type="title" id=toc-1438>٦٠ </span>- ما الذى يظنه يوم القيامة أولئك الذين كانوا يفترون الكذب على الله، فيدَّعون الحِلّ والتحريم من غير أن يكون عندهم دليل؟ إن الله أنعم عليهم نعماً كثيرة، وأحلها لهم بفضله، وشرع لهم ما فيه خيرهم، ولكن الأكثرين لا يشكرون الله عليها، بل يفترون على الله الكذب.',NULL,'296',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1429,'<span data-type="title" id=toc-1439>٦١ </span>- وإنك - أيها الرسول - قد بلغت وهو معلوم لله، وما تكون فى أمر من أمورك، وما تقرأ من قرآن ولا تعمل أنت وأمتك من عمل، إلا ونحن شهود رقباء عليه حين تدخلون فيه مجاهدين، ولا يغيب عن علم ربك شئ فى وزن الذرة فى الأرض ولا فى السماء، ولا أصغر من هذا ولا أكبر منه. إن ذلك كله يسجل فى كتاب عند الله بيِّن واضح.',NULL,'296',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1430,'<span data-type="title" id=toc-1440>٦٢ </span>- تنبهوا - أيها الناس - واعلموا أن الموالين لله بالإيمان والطاعة يحبهم ويحبونه، لا خوف عليهم من الخزى فى الدنيا، ولا من العذاب فى الآخرة، وهم لا يحزنون على ما فاتهم من عرض الدنيا؛ لأن لهم عند الله ما هو أعظم من ذلك وأكثر.',NULL,'296',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1431,'<span data-type="title" id=toc-1441>٦٣ </span>- وهم الذين صدَّقوا بكل ما جاء من عند الله، وأذعنوا للحق، واجتنبوا المعاصى، وخافوا الله فى كل أعمالهم.',NULL,'297',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1432,'<span data-type="title" id=toc-1442>٦٤ </span>- لهؤلاء الأولياء البشرى بالخير فى الدنيا، وما وعدهم الله به من نصر وعزة، وفى الآخرة يتحقق وعد الله، ولا خلف لما وعد الله به، وهذا الذى بشروا به فى الدنيا، وظفروا به فى الآخرة هو الفوز العظيم.',NULL,'297',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1433,'<span data-type="title" id=toc-1443>٦٥ </span>- ولا تحزن - أيها الرسول - لما يقوله المشركون من سخرية وطعن وتكذيب، ولا تظن أن حالهم ستدوم، بل إن النتيجة لك وسيعتز الإسلام، فإن العزة كلها لله تعالى، والنصر بيده، وسينصرك عليهم، وهو سبحانه السميع لما يفترون عليك، العليم بما يضمرونه، وسيجازيهم على ذلك.',NULL,'297',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1434,'<span data-type="title" id=toc-1444>٦٦ </span>- لتعلموا - أيها الناس - أن لله - وحده - كل مَن فى السموات والأرض خلقاً وملكاً وتدبيراً، وإن الذين أشركوا بالله لا يتبعون إلا أوهاماً باطلة لا حقيقة لها، وليسوا إلا واهمين يظنون القوة فيما لا يملك لنفسه نفعاً ولا ضراً.',NULL,'297',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1435,'<span data-type="title" id=toc-1445>٦٧ </span>- إن الذى يملك مَن فى السموات والأرض، هو الذى خلق لكم الليل لتستريحوا فيه من عناء السعى فى النهار، وخلق لكم النهار مضيئاً لتسعوا فيه وتجلبوا مصالحكم. إن فى خلق الليل والنهار لدلائل بينة لمن يسمعون ويتدبرون.',NULL,'297',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1436,'<span data-type="title" id=toc-1446>٦٨ </span>- وإذا كان عبدة الأوثان قد أشركوا فى العبادة حجارة، ولم ينزهوا الله حق التنزيه، وقالوا: إن لله ولداً. فالله منزه عن ذلك. إنه غنى عن أن يتخذ ولداً، لأن الولد مظهر الحاجة إلى البقاء، والله باق خالد، وكل ما فى السموات وما فى الأرض مخلوق ومملوك له، وليس عندكم - أيها المفترون - حُجة ولا دليل على ما زعمتم، فلا تختلقوا على الله أمراً لا أساس له من الحقيقة.',NULL,'298',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1437,'<span data-type="title" id=toc-1447>٦٩ </span>- قل لهم - أيها الرسول -: إن الذين يختلقون على الله الكذب ويزعمون أن له ولداً، لن يفلحوا أبداً.',NULL,'298',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1438,'<span data-type="title" id=toc-1448>٧٠ </span>- لهم متاع فى الدنيا يغترون به، وهو قليل، طال أو قصر، بجوار ما يستقبلهم. ثم إلينا مرجعهم، فنحاسبهم ونذيقهم العذاب المؤلم بسبب كفرهم.',NULL,'298',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1439,'<span data-type="title" id=toc-1449>٧١ </span>- وإن ما ينزل بك من قومك قد نزل بمن سبقك من الأنبياء، واقرأ - أيها الرسول - على الناس، فيما ينزله عليك ربك من القرآن قصة نوح رسول الله لمَّا أحس كراهية قومه وعداءهم لرسالته، فقال لهم: يا قوم إن كان وجودى فيكم لتبليغ الرسالة قد أصبح شديداً عليكم، فإنى مستمر مثابر على دعوتى متوكل على الله فى أمرى، فاحزموا أمركم ومعكم شركاؤكم فى التدبير، ولا يكن فى عدائكم لى أى خفاء، ولا تمهلونى بما تريدون لى من سوء، إن كنتم تقدرون على إيذائى، فإن ربى يرعانى.',NULL,'298',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1440,'<span data-type="title" id=toc-1450>٧٢ </span>- وإن بقيتم على الإعراض عن دعوتى، فإن ذلك لن يضيرنى، لأنى لم أقم بها لأتقاضاكم عليها أجراً أخشى عليه الضياع بسبب إعراضكم، إنما أطلب أجرى عليها من الله - وحده - وقد أمرنى أن أكون مُسَلِّماً إليه جميع أمرى.',NULL,'298',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1441,'<span data-type="title" id=toc-1451>٧٣ </span>- ومع هذا المجهود وتلك المثابرة التى بذلها من أجل هدايتهم، أصروا على أن يستمروا فى تكذيبه وعدائه، فنجَّاه الله ومَن معه من المؤمنين به، الراكبين معه فى الفلك، وجعلهم عُمَّاراً للأرض بعد هلاك الكافرين الذين أغرقهم الطوفان، فانظر - يا محمد - كيف لقى المستخفون بالنذر مصيرهم السيئ.',NULL,'299',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1442,'<span data-type="title" id=toc-1452>٧٤ </span>- ثم أرسلنا من بعد نوح رُسُلاً آخرين، داعين إلى التوحيد، ومبشرين ومنذرين، ومؤيدين بالمعجزات الدالة على صدقهم، فكذبت أقوامهم كما كذب قوم نوح، فما كان من شأن الجاحدين منهم أن يذعنوا، لأن التكذيب سبق التبصر والاعتبار، وبذلك طبع الله الباطل على قلوب الذين من شأنهم الاعتداء على الحقائق وعلى البينات.',NULL,'299',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1443,'<span data-type="title" id=toc-1453>٧٥ </span>- ثم أرسلنا من بعدهم موسى وأخاه هارون إلى فرعون ملك مصر وإلى خاصته، داعين إلى عبادة الله - وحده - ومؤيدين بالحُجج الباهرة، فاستكبر فرعون وقومه عن متابعة موسى وهارون فى دعوتهما، وكانوا بهذا الرفض مرتكبين جرماً عظيماً، آثمين به.',NULL,'299',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1444,'<span data-type="title" id=toc-1454>٧٦ </span>- فلما ظهر لهم الحق من عندنا على يد موسى، قالوا فى معجزة موسى وهى العصا التى انقلبت حية أمام أعينهم: إن هذا سحر مؤكد واضح.',NULL,'299',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1445,'<span data-type="title" id=toc-1455>٧٧ </span>- قال لهم موسى مستنكراً: أتصفون الحق الذى جئتكم به من عند الله بأنه سحر؟ أتكون هذه الحقيقة التى عاينتموها سحراً؟! وهأنذا أتحداكم أن تثبتوا أنها سحر، فأتوا بالساحرين ليثبتوا ما تدعون، ولن يفوز الساحرون فى هذا أبداً.',NULL,'299',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1446,'<span data-type="title" id=toc-1456>٧٨ </span>- قال فرعون وقومه لموسى: إنما جئت إلينا قاصداً أن تصْرِفَنا عن دين آبائنا، وتقاليد قومنا؛ لكى نصير لكما أتباعاً، ويكون لك ولأخيك الملك والعظمة والرياسة المسيطرة المتحكمة؟ وإذن فلن نؤمن بكما ولا برسالتكما.',NULL,'300',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1447,'<span data-type="title" id=toc-1457>٧٩ </span>- وزعم فرعون وقومه أن موسى وأخاه ساحران لا رسولان، فأمر رجاله بأن يحضروا له من مملكته كل من له مهارة فى فنون السحر.',NULL,'300',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1448,'<span data-type="title" id=toc-1458>٨٠ </span>- ولما حضر السحرة ووقفوا أمام موسى، لمنازلته بسحرهم على رؤوس الأشهاد، قال لهم موسى: هاتوا ما عندكم من فنون السحر.',NULL,'300',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1449,'<span data-type="title" id=toc-1459>٨١ </span>- فلما ألقوا حبالهم وَعصِيَّهم، قال لهم موسى: إن الذى فعلتموه هو السحر حقاً، والله سبحانه سيبطله على يدى، إن الله لا يهيئ أعمال المفسدين لأن تكون صالحة ونافعة.',NULL,'300',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1450,'<span data-type="title" id=toc-1460>٨٢ </span>- أما الحق فإن الله ناصره ومؤيده بقدرته وحكمته، مهما أظهر الكافرون من بغضهم له ومحاربتهم إياه.',NULL,'300',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1451,'<span data-type="title" id=toc-1461>٨٣ </span>- ومع ظهور الآيات الدالة على صدق الرسالة، فإن الذين آمنوا بموسى لم يكونوا إلا فئة قليلة من قوم فرعون، آمنوا على خوفٍ من فرعون ومن معه أن يردوهم عما آمنوا به، وما أعظم طغيان فرعون فى أرض مصر، وإنه لمن المغالين الذين أسرفوا فى استكبارهم واستعلائهم.',NULL,'300',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1452,'<span data-type="title" id=toc-1462>٨٤ </span>- أما موسى فقد قال للمؤمنين مواسياً لهم ومشجعاً: يا قوم، إن كان الإيمان قد دخل قلوبكم فى إخلاص لله فلا تخشوا سواه، وأسلموا أموركم إليه. وتوكلوا عليه، وثقوا فى النهاية إن كنتم ثابتين على الإسلام.',NULL,'300',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1453,'<span data-type="title" id=toc-1463>٨٥ </span>- فقال المؤمنون: على الله - وحده - توكلنا، ثم دعوا ربهم ألا يجعلهم أداة فتنة وتعذيب فى يد الكافرين.',NULL,'301',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1454,'<span data-type="title" id=toc-1464>٨٦ </span>- ودعوا ربهم قائلين: نجنا بما أسبغت علينا من نعمة ورحمة، وبفيض رحمتك التى اتصفت بها، من القوم الجاحدين الظالمين.',NULL,'301',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1455,'<span data-type="title" id=toc-1465>٨٧ </span>- وأوحينا إلى موسى وأخيه هارون أن يتخذا لقومهما بيوتاً يسكنونها بأرض مصر، وأن يجعلا هذه البيوت قبلة يتجه إليها أهل الإيمان الذين يتبعون دعوة الله، وأن يؤدوا الصلاة على وجهها الكامل. والبشرى بالخير للمؤمنين.',NULL,'301',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1456,'<span data-type="title" id=toc-1466>٨٨ </span>- ولما تمادى الكفار فى تعنتهم مع موسى، دعا الله عليهم، فقال: يا رب إنك أعطيت فرعون وخاصته بهجة الدنيا وزينتها من الأموال والبنين والسلطان، فكانت عاقبة هذه النعم إسرافهم فى الضلال والإضلال عن سبيل الحق، اللهم اسحق أموالهم. واتركهم فى ظلمة قلوبهم، فلا يوفقوا للإيمان حتى يروا رأى العين العذاب الأليم، الذى هو العاقبة التى تنتظرهم ليكونوا عبرة لغيرهم.',NULL,'301',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1457,'<span data-type="title" id=toc-1467>٨٩ </span>- قال الله تعالى: قد أجيب دعاؤكما، فاستمرا على السير فى الطريق المستقيم، واتركا سبيل أولئك الذين لا يعلمون الأمور على وجهها ولا يذعنون للحق الذى وضح.',NULL,'301',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1458,'<span data-type="title" id=toc-1468>٩٠ </span>- ولما جاوزنا ببنى إسرائيل البحر، تعقبهم فرعون وجنوده للاعتداء عليهم فأطبقنا عليهم البحر، فلما أدرك الغرق فرعون، قال: صدقت بالله الذى صدقت به بنو إسرائيل، وأذعنت له، وأنا من الطائعين الخاضعين.',NULL,'301',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1459,'<span data-type="title" id=toc-1469>٩١ </span>- لم يقبل الله من فرعون هذا الإيمان الذى اضطر إليه، وتلك التوبة التى كانت وقد حضره الموت، بعد أن عاش عاصياً لله مفسداً فى الأرض فمات كافراً مُهاناً.',NULL,'302',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1460,'<span data-type="title" id=toc-1470>٩٢ </span>- واليوم الذى هلكت فيه نُخرج جثتك من البحر، ونبعثها لتكون عظة وعبرة لمن كانوا يعبدونك، ولا ينتظرون لك مثل هذه النهاية المؤلمة المخزية، ولكن كثيراً من الناس يغفلون عن البينات الباهرة فى الكون التى تثبت قدرتنا.',NULL,'302',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1461,'<span data-type="title" id=toc-1471>٩٣ </span>- ولقد مكنا لبنى إسرائيل بعد ذلك فعاشوا فى أرض طيبة، محافظين على دينهم، بعيدين عن الظلم الذى كانوا فيه، موفورة لهم الأرزاق والنعم، ولكنهم ما إن ذاقوا نعمة العزة بعد الهوان، حتى أصابهم داء الفرقة، فاختلفوا، مع أنه قد تبيَّن لهم الحق والباطل، وسيقضى الله بينهم يوم القيامة، ويجزى كلا منهم بما عمل.',NULL,'302',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1462,'<span data-type="title" id=toc-1472>٩٤ </span>- فإن ساورك أو ساور أحداً غيرك شك فيما أنزلنا إليك من وحى، فاسأل أهل الكتب السابقة المنزلة على أنبيائهم، تجد عندهم الجواب القاطع الموافق لما أنزلنا عليك، وذلك تأكيد للصدق ببيان الدليل عند احتمال أى شك، فليس هناك مجال للشك، فقد أنزلنا عليك الحق الذى لا ريب فيه، فلا تجار غيرك فى الشك والتردد.',NULL,'302',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1463,'<span data-type="title" id=toc-1473>٩٥ </span>- ولا تكن - أنت ولا أحد من الذين اتبعوك - من الذين يكذِّبون بالحُجج والبينات، لئلا يحل عليك الخسران والغضب، كما هو شأن الكفار الذين لا يؤمنون، والخطاب للنبى خطاب لكل من اتبعه.',NULL,'302',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1464,'<span data-type="title" id=toc-1474>٩٦ </span>- إن الذين سبق عليهم قضاء الله بالكفر، لما عَلِمَ من عنادهم وتعصبهم، لن يؤمنوا مهما أجهدت نفسك فى إقناعهم.',NULL,'303',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1465,'<span data-type="title" id=toc-1475>٩٧ </span>- ولو جئتهم بكل حُجة - مهما يكن وضوحها - فلن يقتنعوا وسيستمرون على ضلالهم إلى أن ينتهى بهم الأمر إلى العذاب الأليم.',NULL,'303',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1466,'<span data-type="title" id=toc-1476>٩٨ </span>- لو أن كل قرية من القرى تؤمن؛ لنفعها إيمانها، لكنها لم تؤمن، فلم يكن النفع إلا لقوم يونس، فإنهم لما آمنوا وجدوا النفع لهم، فكشفنا عنهم الخزى وما يترتب عليه من آلام، وجعلناهم فى متعة الدنيا الفانية حتى كان يوم القيامة.',NULL,'303',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1467,'<span data-type="title" id=toc-1477>٩٩ </span>- ولو أراد الله إيمان من فى الأرض جميعاً لآمنوا، فلا تحزن على كفر المشركين، ولا إيمان مع الرغبة؛ فلا تستطيع أن تكره الناس حتى يذعنوا للحق ويستجيبوا له، فليس لك أن تحاول إكراههم على الإيمان، ولن تستطيع ذلك مهما حاولت.',NULL,'303',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1468,'<span data-type="title" id=toc-1478>١٠٠ </span>- لا يمكن لإنسان أن يؤمن إلا إذا اتجهت نفسه إلى ذلك، وهيَّأ الله لها الأسباب والوسائل، أما من لم يتجه إلى الإيمان فهو مستحق لسخط الله وعذابه، وسنة الله أن يجعل العذاب والغضب على الذين ينصرفون عن الحُجج الواضحة ولا يتدبرونها.',NULL,'303',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1469,'<span data-type="title" id=toc-1479>١٠١ </span>- قل - يا أيها النبى - لهؤلاء المعاندين: انظروا إلى ما فى السموات والأرض من بينات ترشد إلى ألوهيته ووحدانيته، ففيها ما يقنعكم بالإيمان. ولكن الآيات على كثرتها، والنذر على قوتها، لا تغنى عن قوم جاحدين لا يتعقلون، إذا لم يؤمن هؤلاء الجاحدون فلن ينظروا.',NULL,'303',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1470,'<span data-type="title" id=toc-1480>١٠٢ </span>- فهل ينتظر أولئك الجاحدون إلا أن ينالهم من الأيام الشِّداد مثل ما أصاب الذين مضوا من قوم نوح وقوم موسى وغيرهم؟! قل لهم - أيها النبى -: إذا كنتم تنتظرون غير ذلك، فانتظروا إنى منتظر معكم، وستصيبكم الهزيمة القريبة والعذاب يوم القيامة.',NULL,'304',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1471,'<span data-type="title" id=toc-1481>١٠٣ </span>- ثم ننجى رسلنا والمؤمنين من ذلك العذاب، لأنه وعد بنجاتهم، ووعده حق لا يتخلف.',NULL,'304',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1472,'<span data-type="title" id=toc-1482>١٠٤ </span>- قل لهم - أيها الرسول -: إن كنتم تشكون فى صحة الدين الذى بُعثت به، فاعلموا أنه مهما تشككتم فيه فلن أعبد الأصنام التى تعبدونها من دون الله، ولكنى أعبد الله الذى بيده مصيركم، وهو الذى يتوفاكم، وقد أمرنى أن أكون من المؤمنين به.',NULL,'304',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1473,'<span data-type="title" id=toc-1483>١٠٥ </span>- يا أيها - النبى - قم حق القيام بالاتجاه إلى الله منصرفاً إليه، ولا تدخل فى غمار الذين أشركوا بالله، فجانبهم وابتعد عنهم أنت ومن اتبعك من المؤمنين.',NULL,'304',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1474,'<span data-type="title" id=toc-1484>١٠٦ </span>- ولا تلجأ بالدعاء والعبادة إلى غير الله ما لا يجلب لك نفعاً، ولا ينزل بك ضرراً، فإنك إن فعلت ذلك كنت داخلاً فى غمار المشركين الظالمين. والنهى الموجه للنبى هو موجه لأمته، وهو تأكيد للنهى، لأن النهى حيث لا يمكن وقوع المنهى عنه مبالغة فى النهى.',NULL,'304',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1475,'<span data-type="title" id=toc-1485>١٠٧ </span>- وإنْ يصبك الله بضر - أيها النبى - فلن يكشفه عنك إلا هو، وإن يقدّر لك الخير فلن يمنعه عنك أحد؛ لأنه يهب الخير من فضله لمن يشاء من عباده، وهو - سبحانه - الواسع المغفرة، العظيم الرحمة.',NULL,'305',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1476,'<span data-type="title" id=toc-1486>١٠٨ </span>- بلّغ - أيها الرسول - دعوة الله إلى الناس كافة، وقل لهم: - أيها الناس - قد أنزل الله عليكم الشريعة الحقة من عنده، فمن شاء أن يهتدى بها فليسارع، فإن فائدة هداه ستكون لنفسه، ومن أصرَّ على ضلاله، فإن ضلاله سيقع عليه - وحده - وأنا لست موكلاً بإرغامكم على الإيمان، ولا مسيطراً عليكم.',NULL,'305',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1477,'<span data-type="title" id=toc-1487>١٠٩ </span>- واثْبت - أيها الرسول - على دين الحق، واتَّبع ما أُنْزل عليك من الوحى، صابراً على ما ينالك فى سبيل الدعوة من مكاره، حتى يقضى الله بينك وبينهم، بما وعدك به من نصر المؤمنين، وخذلان الكافرين، وهو خير الحاكمين.',NULL,'305',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1478,'<span data-type="title" id=toc-1489>١ </span>- الر ... حروف ابتدأت بها السورة للإشارة إلى أن القرآن معجز، مع أنه مكون من الحروف التى ينطقون بها، وللتنبيه إلى الإصغاء عند تلاوة القرآن الكريم إلى أنه كتاب ذو شأن عظيم، أُنزلت آياته محكمة لا باطل فيها ولا شبهة، ونظمت بأسلوب لا خلل فيه، واضحة بينة، ثم فصلت أحكامها. وللكتاب مع شرفه فى ذاته شرف أنه من عند الله الذى يعلم كل شئ، ويضع الأمور فى مواضعها سبحانه.',NULL,'306',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1479,'<span data-type="title" id=toc-1490>٢ </span>- أرشد به الناس - أيها النبى - وقلْ لهم: لا تعبدوا إلا الله، إننى مُرْسَلٌ منه لأنذركم بعذابه إن كفرتم، وأُبشّركم بثوابه إن آمنتم وأطعتم.',NULL,'306',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1480,'<span data-type="title" id=toc-1491>٣ </span>- وتضرّعوا إلى الله داعين أن يغفر لكم ذنوبكم، ثم ارجعوا إليه بإخلاص العبادة وعمل الصالحات، فَيُمتعكم متاعاً حسناً فى الدنيا إلى أن تنتهى آجالكم المقدّرة لكم فيها، ويُعطى فى الآخرة كل صاحب عمل صالح فاضل ثواب عمله وفضله. وإن تنصرفوا عمّا أدعوكم إليه، تعرضتم للعذاب، فإنى أخاف عليكم هذا العذاب فى يوم كبير يحشر فيه الناس جميعاً، ويكون فيه الهول الأكبر.',NULL,'307',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1481,'<span data-type="title" id=toc-1492>٤ </span>- إلى الله - وحده - مرجعكم فى الدنيا، ويوم القيامة حين يبعثكم من قبوركم ليجازيكم على أعمالكم، وهو قادر على كل شئ، لأنه كامل القدرة لا يعجز عن شئ من الأشياء.',NULL,'307',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1482,'<span data-type="title" id=toc-1493>٥ </span>- إن الناس يطوون صدورهم كاتمين لما يجول فيها، مجتهدين فى كتمانهم، زاعمين أن عاقبة ذلك أن تستخفى خلجات صدورهم عن الله! ألا فليعلم هؤلاء أنهم إن آووا إلى فراشهم لابسين لباس النوم، فاستتروا بظلام الليل والنوم وطى ما فى الصدور، فإن الله عليم بهم، فى سرهم وعلنهم، لأنه يعلم ما يصاحب الصدور ويطوى فيها.',NULL,'307',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1483,'<span data-type="title" id=toc-1494>٦ </span>- ولْيعْلم هؤلاء أن قدرة الله ونعمه وعلمه شاملة لكل شئ، فلا توجد دابة تتحرك فى الأرض إلا وقد تكَفَّل الله سبحانه برزقها المناسب لها فى مختلف البيئات تفضلاً منه، ويعلم مكان استقرارها فى حال حياتها، والمكان الذى تودع فيه بعد موتها. . كل شئ من ذلك مسجل عنده سبحانه فى كتاب موضح لأحوال ما فيه.',NULL,'307',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1484,'<span data-type="title" id=toc-1495>٧ </span>- والله خلق السموات والأرض وما فيهما فى ستة أيام، ومن قبل ذلك لم يكن الوجود أكثر من عالم الماء، ومن فوقه عرش الله. وقد خلق الله هذا الكون ليظهر بالاختبار أحوالكم - أيها الناس - ليظهر منكم من يقبل على الله بالطاعة والأعمال الحسنة، ومن يُعرض عن ذلك. . ومع هذه القدرة الخالقة إن قلت لهم مؤكداً: أنهم سيبعثون من قبورهم، وأنهم خلقوا ليموتوا ويُبعثوا، سارعوا إلى الرد عليك مؤكدين أن هذا الذى جئتهم به لا حقيقة له، وما هو إلا كالسحر الواضح الذى يلعب بالعقول.',NULL,'307',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1485,'<span data-type="title" id=toc-1496>٨ </span>- ولئن اقْتضت حكمتنا تأخير عذاب كفرهم فى الدنيا إلى وقت مُحدد عندنا هو يوم القيامة، ليقولون مستهزئين: ما الذى يمنعه عنا الآن؟ فليأت به إن كان صادقاً فى وعيده. ألا فليعلم هؤلاء أن العذاب آتٍ حتماً، وأنه لا خلاص لهم منه حين يأتيهم، وأنه سيحيط بهم بسبب استهزائهم واستهتارهم.',NULL,'308',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1486,'<span data-type="title" id=toc-1497>٩ </span>- وإن من طبيعة الإنسان أن تستغرق نفسه الحالُ التى يكون عليها، فإذا أعطيناه بعض النعم رحمة منا كالصحة والسعة فى الرزق، ثم نزعنا بعد ذلك هذه النعمة لحكمة منا، أسرفَ فى يأسه من عودة هذه النعمة إليه، وأسرف فى كفره بالنعم الأخرى التى لا يزال يتمتع بها.',NULL,'308',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1487,'<span data-type="title" id=toc-1498>١٠ </span>- وإننا لو أعطينا الإنسان نعمة بعد ضر لحق به، فإنه يقول: ذهب ما كان يسوؤنى ولن يعود ويحملُه ذلك على شدة الفرح بمتاع الدنيا، وعلى المبالغة فى التفاخر على الغير، فينشغل قلبه عن شكر ربه، هذا هو شأن غالب بنى الإنسان: مضطرب بين اليأس والتفاخر.',NULL,'308',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1488,'<span data-type="title" id=toc-1499>١١ </span>- ولا يخلو من هذا العيب إلا الذين صبروا عند الشدائد، وعملوا الصالحات فى السرَّاء والضرَّاء. هؤلاء لهم مغفرة من الذنوب وأجر كبير على أعمالهم الصالحة.',NULL,'308',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1489,'<span data-type="title" id=toc-1500>١٢ </span>- لا تحاول - أيها النبى - إرضاء المشركين لأنهم لا يؤمنون، وعساك إن حاولت إرضاءهم أن تترك تلاوة بعض ما يوحى إليك مما يشق سماعه عليهم، كاحتقار بعض آلهتهم، خوفاً من قبح ردهم واستهزائهم، وعسى أن تحس بالضيق وأنت تتلوه، لأنهم يطلبون أن ينزل الله عليك كنزاً تنعم به كالملوك، أو يجئ معك مَلَك يخبرنهم بصدقك، فلا تبال - أيها النبى - بعنادهم، فما أنت إلا منذر ومحذِّر من عقاب الله من يخالف أمره، وقد فعلت فأرِحْ نفسك منهم. واعلم أن الله على كل شئ رقيب ومهيمن، وسيفعل بهم ما يستحقون.',NULL,'308',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1490,'<span data-type="title" id=toc-1501>١٣ </span>- إن القرآن فيه الآية الدالة على صدقك، فإن قالوا: إنه ألَّفه من عنده أو افتراه على الله، فقل لهم: إن كان هذا القرآن من عند بشر، أمكن للبشر أن يأتوا بمثله، وأنتم فصحاء البشر. فأتوا بعشر سور مثله مُختَلَقَات، واستعينوا بما يمكنكم الاستعانة به من الإنس والجن، إن كنتم صادقين فى دعواكم أنه كلام بشر.',NULL,'309',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1491,'<span data-type="title" id=toc-1502>١٤ </span>- فإن عجزتم، وعجز من استعنتم بهم فأتوا بمثله ولو مُفْترىً، فاعلموا أن هذا القرآن ما أنزل إلا مقترناً بعلم الله، فلا يعلم علمه أحد، واعْلموا أنه لا إله إلا الله فلا يعمل عمله أحد. فأسلموا بعد قيام هذه الحُجة عليكم، إن كنتم طالبين للحق.',NULL,'309',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1492,'<span data-type="title" id=toc-1503>١٥ </span>- من كان يطلب الحياة الدنيا، والتَّمتع بلذاتها وزينتها، نعطهم ثمرات أعمالهم وافية لا ينقص منها شئ.',NULL,'309',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1493,'<span data-type="title" id=toc-1504>١٦ </span>- هؤلاء الذين قصروا هممهم على الدنيا، ليس لهم فى الآخرة إلا عذاب النار، وبَطَل نفع ما صنعوه فى الدنيا لأنه لم يكن للآخرة فيه نصيب، وهو فى نفسه باطل أيضاً، لأن العمل الذى لا يفيد السعادة الدائمة كأنه لم يكن.',NULL,'309',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1494,'<span data-type="title" id=toc-1505>١٧ </span>- أفَمَنْ كان يسير فى حياته على بصيرة وهداية من ربه، ويطلب الحق مخلصاً، معه شاهد بالصدق من الله وهو القرآن، وشاهد من قبله وهو كتاب موسى الذىأنزله الله قدوة يتبع ما جاء به، ورحمة لمتبعيه، كمن يسير فى حياته على ضلال وعماية، فلا يهتم إلا بمتاع الدنيا وزينتها؟! أولئك الأولون هم الذين أنار الله بصائرهم، يؤمنون بالنبى والكتاب الذى أنزل عليه. ومن يكفر به ممن تألبوا على الحق وتحزّبوا ضده، فالنار موعده يوم القيامة. فلا تكن - أيها النبى - فى شك من هذا القرآن، إنه الحق النازل من عند ربك، لا يأتيه الباطل، ولكن أكثر الناس تضلّهم الشهوات، فلا يؤمنون بما يجب الإيمان به.',NULL,'310',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1495,'<span data-type="title" id=toc-1506>١٨ </span>- وليس أحد أكثر ظلماً لنفسه وبُعداً عن الحق من الذين يختلقون الكذب وينسبونه إلى الله. إن هؤلاء سيعرضون يوم القيامة على ربهم ليحاسبهم على ما عملوا من سوء، فيقول الأشهاد من الملائكة والأنبياء وغيرهم: هؤلاء هم الذين ارتكبوا أفظع الجرم والظلم بالنسبة لخالقهم. إن لعنة الله ستقع عليهم لأنهم ظالمون.',NULL,'310',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1496,'<span data-type="title" id=toc-1507>١٩ </span>- هؤلاء الذين يصرفون الناس عن دين الله ويمنعونهم - وهو سبيله المستقيم - ويطلبون أن تكون هذه السبيل موافقة لشهواتهم وأهوائهم، فتكون معوجة، وهم بالآخرة - وما فيها من ثواب المؤمن وعقاب الكافر - كافرون.',NULL,'310',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1497,'<span data-type="title" id=toc-1508>٢٠ </span>- أولئك الكافرون، لم تكن لهم قوة تُعجز الله عن أخذهم بالعذاب فى الدنيا، ولم يكن لهم نُصراء يمنعون عنهم عذابه لو شاء أن يعجل لهم العذاب، وإن العذاب سيقع عليهم فى الآخرة أضعاف ما كان سيقع عليهم فى الدنيا، لو أراد الله أن يقع، لأنهم كرهوا أن يسمعوا القرآن، ويبصروا آيات الله فى الكون، كأنهم لم يكونوا يستطيعون أن يسمعوا أو يبصروا.',NULL,'310',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1498,'<span data-type="title" id=toc-1509>٢١ </span>- أولئك الكافرون لم يربحوا بعبادة غير الله شيئاً. بل خسروا أنفسهم وغاب عنهم فى الآخرة ما كانوا يفترون من أكاذيب ودعاوى باطلة، وما كانوا يختلقون من الآلهة الباطلة، ويزعمون أنهم ينفعونهم أو يشفعون لهم، فإن يوم القيامة هو يوم الحقائق التى لا زيف فيها ولا افتراء.',NULL,'310',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1499,'<span data-type="title" id=toc-1510>٢٢ </span>- حقاً، إنهم فى الآخرة أشد الناس خُسراناً.',NULL,'311',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1500,'<span data-type="title" id=toc-1511>٢٣ </span>- إن الذين آمنوا بالله ورسله، وعملوا الأعمال الصالحة، وخضعت قلوبهم واطمأنت إلى قضاء ربها، هؤلاء هم المستحقون لدخول الجنة والخُلْد فيها.',NULL,'311',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1501,'<span data-type="title" id=toc-1512>٢٤ </span>- مثل الفريقين: المؤمنين والكافرين، كالأعمى الذى يسير على غير هدى، والأصم الذى لا يسمع ما يرشده إلى النجاة، وكقوى البصر الذى يرى طريق الخير والنجاة، وقوى السمع الذى يسمع كل ما ينفعه، هذان الفريقان لا يستويان فى الحال والمآل. أفلا تتفكرون - أيها الناس - فيما بينكم من التباين والكفر، وفيما بين الباطل والحق من خلاف، فتبتعدوا عن طريق الضلال، وتسيروا فى الطريق المستقيم؟',NULL,'311',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1502,'<span data-type="title" id=toc-1513>٢٥ </span>- وكما أرسلناك إلى قومك لتنذرهم وتبشرهم، فقابلك فريق منهم بالعناد والجحود، أرسلنا نوحاً إلى قومه فقال لهم: إنى محذر لكم من عذاب الله، مبين لكم طريق النجاة.',NULL,'311',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1503,'<span data-type="title" id=toc-1514>٢٦ </span>- قائلاً لهم: إنى أطلب منكم ألا تعبدوا إلا الله، لأنى أخاف عليكم - إن عبدتم غيره أو أشركتم معه سواه فى العبادة - أن يحل عليكم يَوْمٌ عذابه ذو ألم شديد.',NULL,'311',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1504,'<span data-type="title" id=toc-1515>٢٧ </span>- قال الكبار من قومه: ما نرى إلا أنك بشر مثلنا، فليس فيك ما يجعل لك ميزة خاصة، وفضلاً يحملنا على الإيمان بأنك رسول من عند الله، وما نرى الذين اتبعوك من بيننا إلا الطبقة الدنيا منا، وما نرى لكم من فضل علينا. بل إنا نعتقد أنكم كاذبون فيما تزعمون.',NULL,'311',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1505,'<span data-type="title" id=toc-1516>٢٨ </span>- قال نوح: يا قوم، أخبرونى - إن كنت مؤيَّداً بحُجة واضحة من ربى، وأعطانى برحمته النبوة والرسالة، فحَجَب نورها عنكم، وعمَّاها عليكم اغتراركُم بالجاه والمال - فهل يصح أن نلزمكم بالحُجة والإيمان بها مضطرين كارهين؟ .',NULL,'311',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1506,'<span data-type="title" id=toc-1517>٢٩ </span>- ويا قوم، لا أطلب منكم على تبليغ رسالة ربى مالا، وإنما أطلب جزائى من الله، وما أنا بطارد الذين آمنوا بربهم عن مجلسى ومعاشرتى، لمجرد احتقاركم لهم. لأنهم سيلاقون ربهم يوم القيامة، فيشكوننى إليه إن طردتهم لفقرهم، ولكنى أراكم قوماً تجهلون ما يصح أن يتفاضل به الخلق عند الله. أهو الغنى والجاه، كما تزعمون؟ أم اتّباع الحق وعمل الخير؟ .',NULL,'312',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1507,'<span data-type="title" id=toc-1518>٣٠ </span>- ويا قوم، لا أحد يستطيع منع عقاب الله عنى إن طردتهم، وهم المؤمنون به، أهَلْ بعد هذا تصرون على جهلكم، فلا تتذكرون أن لهم رباً ينتقم لهم؟ .',NULL,'312',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1508,'<span data-type="title" id=toc-1519>٣١ </span>- ولا أقول لكم، لأنى رسول، إن عندى خزائن رزق الله أتصرف فيها كما أشاء، فأجعل من يتبعنى غنياً {ولا أقول: إنى أعلم الغيب، فأخبركم بما اختص به علم الله، بحيث لا يعلمه أحد من العباد} ، ولا أقول: إنى ملك حتى تردوا على بقولكم: ما ذاك إلا بشر، ولا أقول عن الذين تحتقرونهم إن الله لن يؤتيهم خيراً إرضاء لرغباتكم، لأن الله - وحده - هو الذى يعلم ما فى أنفسهم من إخلاص. إنى إذا قلت لهم ما تحبونه، أكون من زمرة الظالمين لأنفسهم ولغيرهم. د',NULL,'312',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1509,'<span data-type="title" id=toc-1520>٣٢ </span>- قالوا: يا نوح قد جادلتنا لنؤمن بك فأكثرت جدالنا، حتى مَلِلْنا، ولم نعد نتحمل منك كلاماً، فأتنا بهذا العذاب الذى تهددنا به، إن كنت صادقاً فى أن الله يعذبنا إذا لم نؤمن بك.',NULL,'312',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1510,'<span data-type="title" id=toc-1521>٣٣ </span>- قال نوح: هذا أمر بيد الله - وحده - فهو الذى يأتيكم بما يشاء حسب حكمته، ولستم بمفلتين من عذابه إذا جاء، لأنه سبحانه لا يعجزه شئ فى الأرض ولا فى السماء.',NULL,'312',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1511,'<span data-type="title" id=toc-1522>٣٤ </span>- ولا ينفعكم نصحى لمجرد إرادتى الخير لكم، إن كان الله يريد أن تضلوا لعلمه وتقديره فساد قلوبكم حتى صارت لا تقبل حقاً، وهو سبحانه ربكم، وسيرجعكم إليه يوم القيامة، ويجازيكم على ما كنتم تعملونه.',NULL,'313',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1512,'<span data-type="title" id=toc-1523>٣٥ </span>- إن هذا القَصصَ الصادق، ماذا يكون موقف المشركين منه؟ أيقولون افتراه؟ وإن قالوا ذلك، فقل - أيها الرسول - إن كنت افتريته على الله كما تزعمون، فهو جرم عظيم، علىَّ وحدى إثمه، وإذا كنت صادقاً، فأنتم المجرمون وأنا برئ من آثار جرمكم.',NULL,'313',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1513,'<span data-type="title" id=toc-1524>٣٦ </span>- وأوحى الله إلى نوح: أنه لن يصدّقك ويذعن للحق من قومك أحد بعد الآن - غير من سبق منه الإيمان قبل ذلك - فلا تحزن يا نوح بسبب ما كانوا يفعلونه معك من تكذيبك وإيذائك؛ لأننا سننتقم منهم قريباً.',NULL,'313',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1514,'<span data-type="title" id=toc-1525>٣٧ </span>- وقلنا له: اصنع الفلك لننجيك عليها بعنايتنا، وتحت رعايتنا، ولا تخاطبنى فى شأن هؤلاء الظالمين لأننى استجبت دعاءك، وأمرت بإهلاكهم غرقاً.',NULL,'313',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1515,'<span data-type="title" id=toc-1526>٣٨ </span>- وشرع نوح فى عمل الفلك، وكلما مرَّ عليه قادة الكفر من قومه استهزأوا به، لجهلهم ولعدم معرفة الغرض الذى يقصده، قال نوح: إن تسخروا منا لجهلكم بصدق وعد الله، فإنا أيضاً سنسخر منكم كما تسخرون منا.',NULL,'313',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1516,'<span data-type="title" id=toc-1527>٣٩ </span>- فسوف تعلمون من منا الذى سيأتيه عذاب يُذله فى الدنيا، ويحل عليه فى الآخرة عذاب دائم خالد.',NULL,'313',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1517,'<span data-type="title" id=toc-1528>٤٠ </span>- حتى إذا جاء وقت أمرنا بإهلاكهم، جاء الماء بقوة فائراً ذا رغوة، كالماء الذى يغلى فوق النار، قلنا لنوح: احْمل معك فى السفينة من كل نوع من أنواع الحيوانات ذكراً وأنثى، واحْمل فيها أيضاً أهل بيتك جميعاً، إلا من سبق عليه حكمنا بإهلاكه، واحْمل فيها أيضاً من آمن من قومك، ولم يكونوا إلا عدداً قليلاً.',NULL,'314',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1518,'<span data-type="title" id=toc-1529>٤١ </span>- وقال نوح للذين آمنوا من قومه - بعد أن أعدّ الفلك -: اركبوا فيها متيمنين بذكر اسم الله تعالى، وقت إجرائها، وفى وقت رسوها، وعند النزول فيها والخروج منها، وارجو مغفرة الله على ما فرط منكم، ورحمته بكم، فإن المغفرة والرحمة من شأنه سبحانه وتعالى.',NULL,'314',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1519,'<span data-type="title" id=toc-1530>٤٢ </span>- ونزلوا فى السفينة، فصارت تجرى بهم سائرة فى موج يعلو ويرتفع، حتى يصير كالجبال فى علوها، وفى ابتداء سيرها تذكر نوح ابنه بعاطفة الأبوة، وقد كان فى معزل عن دعوة أبيه فناداه: اركب معنا يا بُنى ولا تكن مع الجاحدين بدين الله تعالى.',NULL,'314',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1520,'<span data-type="title" id=toc-1531>٤٣ </span>- لم يطع الولد أباه الشفيق، وقال: سأتخذ مأوى لى مكاناً يمنعنى من الماء، فقال الأب العالم بقضاء الله فى شأن العصاة: يا بُنى لا يوجد ما يمنع من حكم الله تعالى بالإغراق للظالمين، وغاب الولد عن أبيه الناصح بالموج المرتفع؛ فكان مع المغرقين الهالكين الجاحدين.',NULL,'314',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1521,'<span data-type="title" id=toc-1532>٤٤ </span>- وبعد أن هلك الجاحدون بالإغراق، جاء أمر الله التكوينى، فقيل بحكم التكوين: ابْلَعى ماءك أيتها الأرض، وامتنعى عن إنزال الماء أيتها السماء، فذهب الماء من الأرض، ولم تمد بشئ من السماء، وانتهى حكم الله بالإهلاك واستوت الفلك، ووقفت عند الجبل المسمى بالجودى، وقضى الله بإبعاد الظالمين عن رحمته، فقيل: هلاكاً للقوم الظالمين بسبب ظلمهم.',NULL,'314',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1522,'<span data-type="title" id=toc-1533>٤٥ </span>- ثارت الشفقة فى قلب نوح على ابنه، فنادى رَبَّه ضارعاً مشفقاً فقال: يا خالقى ومنشئى، إن ابنى قطعة منى، وهو من أهلى. وقد وعدت أن تنجى أهلى، وإن وعدك حق ثابت واقع، وأنت أعدل الحاكمين، لأنك أعلمهم، ولأنك أكثر حكمة من كل ذوى الحكم.',NULL,'315',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1523,'<span data-type="title" id=toc-1534>٤٦ </span>- قال الله سبحانه: إن ابنك ليس من أهلك، إذ أنه بكفره وسيره مع الكافرين قد انقطعت الولاية بينك وبينه، وقد عمل أعمالا غير صالحة، فلم يصر منك، فلا تطلب ما لا تعلم: أهو صواب أم خطأ؟ ولا تَسِرْ وراء شفقتك وإنى أرشدك إلى الحق لكيلا تكون من الجاهلين الذين تنسيهم الشفقة الحقائق الثابتة.',NULL,'315',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1524,'<span data-type="title" id=toc-1535>٤٧ </span>- قال نوح: يا خالقى ومتولى أمرى ألجأ إليك فلا أسألك من بعد ما لا أعلم الحق فيه، واغفر لى ما قلته بدافع شفقتى، وإن لم تتفضل علىَّ بمغفرتك، وترحمنى برحمتك، كنت فى عداد الخاسرين.',NULL,'315',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1525,'<span data-type="title" id=toc-1536>٤٨ </span>- قيل بلسان الوحى: يا نوح، انزل على الأرض من سفينة النجاة سالماً آمناً، بسلام من الله تعالى وأَمْنٍ منه، وبركات من الله عليك وعلى الذين معك، الذين سيكونون أمماً مختلفة من بعدك، وسينال بركة الإيمان والإذعان بعضهم، وبعضهم سيكونون أمما يستمتعون بالدنيا وينالون متعها غير مذعنين للحق، ثم يصيبهم يوم القيامة عذاب مؤلم شديد.',NULL,'315',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1526,'<span data-type="title" id=toc-1537>٤٩ </span>- تلك القصة التى قصصناها عليك - أيها النبى - عن نوح وقومه، من أخبار الغيب التى لا يعلمها إلا الله، ما كنت تعلمها أنت ولا قومك على هذا الوجه من الدقة والتفصيل من قبل هذا الوحى، فاصبر على إيذاء قومك كما صبر الأنبياء قبلك، فإن عاقبتك الفوز مثل عاقبتهم، والعاقبة الطيبة دائماً للذين يتقون عذاب الله بالإيمان وعمل الصالحات.',NULL,'316',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1527,'<span data-type="title" id=toc-1538>٥٠ </span>- ولقد أرسلنا إلى قوم عَادٍ الأولى أخاً لهم من قبيلتهم هو (هود) فقال لهم: يا قوم اعبدوا الله - وحده - إذ ليس لكم مَنْ يستحق العبادة غيره - وما أنتم إلا كاذبون فى ادّعائكم أن لله شركاء فى استحقاقهم للعبادة ليكونوا شفعاء لكم عند الله.',NULL,'316',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1528,'<span data-type="title" id=toc-1539>٥١ </span>- يا قوم، لا أطلب منكم على النصح مكافأة من جاه أو سلطان أو مال، وإنما أجرى على الله الذى خلقنى، ولا يصح أن تستولى عليكم الغفلة فلا تعقلون ما ينفعكم وما يضركم.',NULL,'316',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1529,'<span data-type="title" id=toc-1540>٥٢ </span>- ويا قوم، اطلبوا من خالقكم أن يغفر لكم ما سلف من ذنوبكم، ثم ارجعوا إليه. إنكم إن فعلتم ذلك يُرْسِل المطر عليكم متتابعاً، فتكثر خيراتكم، ويزدكم قوة إلى قوتكم التى تغترون بها، ولا تعرضوا عما أدعوكم إليه، مصممين على الإجرام الذى يرديكم فى الهلاك.',NULL,'316',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1530,'<span data-type="title" id=toc-1541>٥٣ </span>- قالوا: يا هود ما جئتنا بحُجة واضحة على صحة ما تدعونا إليه، وما نحن بتاركى عبادة آلهتنا لمجرد قولك، أنتركها وما نحن لك بمصدقين.',NULL,'316',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1531,'<span data-type="title" id=toc-1542>٥٤ </span>- ما نقول فى موقفك منا: إلا أن بعض آلهتنا مَسَّتْكَ بِشَر، فصرت تهذى بهذا الكلام، قال مُصِراً على إيمانه متحدياً: أقول، وأشهد الله على ما أقول، وأشهدكم عليه: إنى برئ من داء الشرك الذى أنتم فيه، فأنتم المرضى.',NULL,'317',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1532,'<span data-type="title" id=toc-1543>٥٥ </span>- ولا أبالى بكم ولا بآلهتكم التى تدَّعون أنها مسَّتنى بسوء، فتعاونوا أنتم وآلهتكم على الكيد لى، ثم لا تؤخرون عقابى لحظة، إن استطعتم.',NULL,'317',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1533,'<span data-type="title" id=toc-1544>٥٦ </span>- إننى اعتمدت على الله، وهو مالك أمرى وأمركم، لا يعجزه شئ عن رد كيدكم، وهو القادر على كل شئ. فما من دابة إلا وهو مالك أمرها ومتصرف فيها، فلا يعجزه حفظى من أذاكم، ولا إهلاككم، إن أفعال ربى تجرى على طريق الحق والعدل فى ملكه، فينصر المؤمنين المصلحين، ويخذل الكافرين المفسدين.',NULL,'317',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1534,'<span data-type="title" id=toc-1545>٥٧ </span>- فإن تُعرضوا عن دعوتى لم يضرنى إعراضكم، والعاقبة السيئة عليكم، فقد أبلغتكم ما أرسلنى الله به إليكم، وليس علىّ إلا البلاغ، والله يهلككم ويجئ بقوم آخرين يخلفونكم فى دياركم وأموالكم، وأنتم لا تضرونه بإعراضكم عن عبادته، إن ربى مهيمن على كل شئ، مطلع عليه، فما تخفى عليه أعمالكم، ولا يغفل عن مؤاخذتكم.',NULL,'317',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1535,'<span data-type="title" id=toc-1546>٥٨ </span>- ولما جاء أمرنا بإهلاك عَادٍ نجّينا هُوداً، والذين آمنوا معه، من عذاب الريح العاتية التى أهلكتهم، ونجيناهم من عذاب شديد كبير فى الدنيا والآخرة، وذلك بسبب رحمتنا لهم بتوفيقهم للإيمان.',NULL,'317',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1536,'<span data-type="title" id=toc-1547>٥٩ </span>- تلك عاد أنْكروا الحُجج الواضحة، وعصوا رُسل الله جميعاً، بعصيانهم رسوله إليهم، وطاعتهم لأمر كل طاغية شديد العناد من رؤسائهم وكبرائهم.',NULL,'317',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1537,'<span data-type="title" id=toc-1548>٦٠ </span>- فاستحقوا من الله والملائكة والناس أجمعين لعنة تلحقهم فى الدنيا، ولعنة تتبعهم يوم القيامة، ألا فلينتبه كل من علم خبر عاد. أن عاداً جحدوا نعمة خالقهم عليهم، ولم يشكروها بالإيمان به وحده، فأصبحوا جديرين بطردهم من رحمة الله، وإنزال الهلاك الشديد بهم، ألا فهلاكاً لهم لتكذيبهم هودا.',NULL,'318',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1538,'<span data-type="title" id=toc-1549>٦١ </span>- وقد أرسلنا إلى ثمود واحداً منهم، تربطه بهم صلة النسب والمودة، وهو صالح، فقال لهم: يا قوم اعبدوا الله - وحده - ليس لكم من يستحق العبادة غيره، هو خلقكم من الأرض ومكَّنكم من عمارتها، واستثمار ما فيها والانتفاع بخيرها. . فادْعوه أن يغفر لكم ما سلف من ذنوبكم، ثم ارجعوا إليه بالندم على معصيته والإقبال على طاعته كلما وقعتم فى ذنب. إنّ ربى قريب الرحمة مجيب الدعاء لمن يستغفره ويدعوه.',NULL,'318',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1539,'<span data-type="title" id=toc-1550>٦٢ </span>- قالوا: يا صالح قد كنت بيننا موضع الرجاء والمحبة والتقدير من نفوسنا، قبل هذا الذى تدعونا إليه، أتطلب منا أن نترك عبادة ما كان يعبد آباؤنا وما ألفناه وألفوه؟ إنَّا لفى شك من دعوتك إلى عبادة الله - وحده فهذا مثير للرّيب، وسوء الظن فيك، وفيما تدعو إليه.',NULL,'318',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1540,'<span data-type="title" id=toc-1551>٦٣ </span>- قال: يا قوم: خبِّرونى إن كنت على بصيرة نيِّرة وبيِّنة مما أدعوكم إليه مُؤيداً بحجة من ربى، وأعطانى ربى رحمة لى ولكم، وهى النبوة والرسالة، فكيف أخالف أمره وأعصيه بعدم تبليغ رسالته، استجابة لكم؟ ومن ينصرنى ويعيننى على دفع عذابه إن عصيته؟ إنكم لا تستطيعون نصرتى ودفع عذابه عنى، فما تزيدوننى غير الضياع والوقوع فى الخسران إن أطعتكم وعصيت ربى وربكم.',NULL,'318',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1541,'<span data-type="title" id=toc-1552>٦٤ </span>- ويا قوم، هذه ناقة الله جعلها لكم علامة تشهد على صدقى فيما أبلغه لكم، لأنها على غير ما تألفون من أمثالها، فاتركوها تأكل فى أرض الله لأنها ناقته، والأرض أرضه، ولا تنالوها بسوء يؤذيها، فإنكم إن فعلتم ذلك يأخذكم من الله عذاب قريب.',NULL,'319',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1542,'<span data-type="title" id=toc-1553>٦٥ </span>- فلم يسمعوا نصحه، ولم يستجيبوا له، وبلغ بهم الكبرياء والاستهانة بتهديده أن قتلوا الناقة، فقال لهم: تمتعوا بحياتكم فى داركم ثلاثة أيام، ثم يأتيكم بعدها عذاب الله، ذلك وعده الحق الذى لا يتخلف، ولا يقع عليه تكذيب.',NULL,'319',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1543,'<span data-type="title" id=toc-1554>٦٦ </span>- فلما جاء عذابنا نجّينا صالحاً والذين آمنوا معه من الهلاك برحمة خاصة منا، ونجيناهم من مهانة وفضيحة يوم هلاك ثمود. إن ربك - أيها النبى - هو القوى الغالب، فاطمئن إلى قوته وعزته وعونه ونصره.',NULL,'319',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1544,'<span data-type="title" id=toc-1555>٦٧ </span>- وأخذت الصيحة ثمود بعنفها ورجفتها وصاعقتها، لأنهم ظلموا أنفسهم بالكفر والعدوان، فأصبحوا فى ديارهم هامدين، راقدين على وجوههم، ميتين لا حراك بهم.',NULL,'319',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1545,'<span data-type="title" id=toc-1556>٦٨ </span>- وانتهى أمرهم، وزالت آثارهم من ديارهم، كأنهم لم يقيموا فيها، ونطق حالهم بما يجب أن يتنبه له ويعتبر به كل عاقل، ويعلم أن ثَمودَ جحدوا بآيات من خلقهم، وبسبب ذلك كان الهلاك والبُعد عن رحمة الله.',NULL,'319',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1546,'<span data-type="title" id=toc-1557>٦٩ </span>- ولقد أرسلنا الملائكة إلى إبراهيم ببشارته هو وزوجته بمولود. قالوا يحيُّونه: سلاماً قال يرد تحيتهم: سلام. وأسرع فلم يمكث أن حضر إليهم بعجل مشوى سمين ليأكلوا منه.',NULL,'319',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1547,'<span data-type="title" id=toc-1558>٧٠ </span>- فلما رأى أيديهم لا تبلغه ولا تمتد إليه، كما هو معروف عن الضيوف أنكر أنهم ضيوف، وأحس أنهم ملائكة، وأضمر الخوف أن يكون مجيئهم لأمر أنكره الله عليه، أو لتعذيب قومه. قالوا - وقد عرفوا أثر الخوف فى نفسه -: لا تخف إنا أرسلنا لهلاك قوم لوط.',NULL,'320',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1548,'<span data-type="title" id=toc-1559>٧١ </span>- وكانت امرأته قائمة تسمع كلامهم فى مكان قريب منهم، فضحكت لسرورها لنجاة لوط ابن أخى زوجها، فبشرناها على ألْسِنَة الملائكة بأنها ستلد من إبراهم زوجها ولداً يسمى إسحاق، وسيعيش ولدها، وسيكون لها منه بعد إسحاق يعقوب.',NULL,'320',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1549,'<span data-type="title" id=toc-1560>٧٢ </span>- صاحت متعجبة وقالت: يا عجبا! أألدُ وأنا عجوز، وهذا زوجى ترونه شيخاً كبيراً ولا يولد لمثله؟ إن هذا الذى أسمعه والله شئ عجيب، إذ كيف يولد لهرِمَيْن مثلى ومثل زوجى؟ .',NULL,'320',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1550,'<span data-type="title" id=toc-1561>٧٣ </span>- قالت الملائكة لها: أتعجبين من أن يولد لكما على كبركما، وهو من أمر الله الذى لا يعجزه شئ؟ تلك رحمة الله ونعمه الكثيرة عليكم - أهل بيت النبوة - فليس بعجيب أن يهب لكم ما لا يهب لغيركم، إنه فاعل ما يستوجب الحمد، عظيم كثير الإحسان والكرم والعطاء.',NULL,'320',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1551,'<span data-type="title" id=toc-1562>٧٤ </span>- فلما ذهب عن إبراهيم الخوف، وسمع البشارة السارة بالولد، أخذه الإشفاق، وأخذ يجادل رسلنا فى هلاك قوم لوط.',NULL,'320',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1552,'<span data-type="title" id=toc-1563>٧٥ </span>- إن إبراهيم لكثير الحلم، لا يحب تعجيل العقاب، كثير التأوه والتوجع من السوء الذى يصيب غيره، تائب راجع إلى الله بما يحبه ويرضاه، فَرِقته ورحمته ورأفته حملته على المجادلة رجاء أن يرفع الله عذابه عن قوم لوط وأن يتوبوا وينيبوا إليه.',NULL,'320',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1553,'<span data-type="title" id=toc-1564>٧٦ </span>- قالت الملائكة: يا إبراهيم أعرض عن هذا الجدال والتماس الرحمة لهؤلاء القوم، إنه قد جاء أمر ربك بهلاكهم، وأنهم لا بد آتيهم عذاب نافذ غير مردود بجدل أو غير جدل.',NULL,'320',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1554,'<span data-type="title" id=toc-1565>٧٧ </span>- ولما جاءت الملائكة - رُسلنا - إلى لوط فى صورة شُبانٍ حِسَان، تألم واستاء، وأحس بضعفه عن حمايتهم، وضيقه بهم، لخوفه عليهم من فساد قومه، وقال: هذا يوم شديد المكاره والآلام.',NULL,'321',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1555,'<span data-type="title" id=toc-1566>٧٨ </span>- وعلم قومه بهم، فجاءوا مسرعين إليه، ومن قبل ذلك كانوا يرتكبون الفواحش، ويقترفون السيئات، قال لهم لوط: يا قوم هؤلاء بناتى، تزوّجوا بهن، فذلك أطهر لكم من ارتكاب الفواحش مع الذكور، فخافوا الله وصونوا أنفسكم من عقابه، ولا تفضحونى وتهينونى بالاعتداء على ضيفى، أليس فيكم رجل سديد الرأى، رشيد العقل، يردكم عن الغىِّ ويكفكم عن السوء؟ .',NULL,'321',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1556,'<span data-type="title" id=toc-1567>٧٩ </span>- قالوا: لقد علمتَ يا لوط إنه ليس لنا فى بناتك أَىُّ حق فى نكاحهن أو رغبة فيهن، وإنك دون شك تعلم ما نريد من مجيئنا وإسراعنا إليك.',NULL,'321',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1557,'<span data-type="title" id=toc-1568>٨٠ </span>- قال لوط: لو أن لى قوة أو ركناً قوياً اعتمدت عليه، لكان موقفى منكم غير هذا، ولدفعتكم عن ضيفى ومنعتكم من السيئات.',NULL,'321',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1558,'<span data-type="title" id=toc-1569>٨١ </span>- قالت الملائكة، وقد ظهرت على حقيقتها: يا لوط، لا تخف ولا تحزن إنا رسل ربك، لا بَشر كما بدا لك ولقومك، ولن يصل هؤلاء إليك بِشَرٍ يسوءك أو ضر يصيبك، فسر أنت وأهلك فى بعض أوقات الليل، إذا دخل جزء كبير منه، واخْرج بهم من هذه القرية، ولا يلتفت أحد منكم خلفه، لكيلا يرى هول العذاب فيصاب بشر منه، لكن امرأتك التى خانتك فلا تكن من الخارجين معك، إنه لا بد مصيبها ما قُدّر أن يصيب هؤلاء. . إن موعد هلاكهم الصبح، وهو موعد قريب، فلا تخف.',NULL,'321',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1559,'<span data-type="title" id=toc-1570>٨٢ </span>- فلما جاء وقت العذاب الذى قدرناه وقضينا به، جعلنا عالى القرية التى كان يعيش فيها قوم لوط سافلها، فقلبناها، وأمطرنا عليهم فى أثناء ذلك حجارة من طين حمى بالنار حتى تحجَّر.',NULL,'322',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1560,'<span data-type="title" id=toc-1571>٨٣ </span>- كانت تقع عليهم متتابعة منتظمة معلنة العذاب من عند ربك - أيها النبى - وليست بعيدة عن الظالمين من قومك.',NULL,'322',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1561,'<span data-type="title" id=toc-1572>٨٤ </span>- ولقد أرسلنا إلى قوم مدين أخاهم فى النسب والمودة والتراحم شُعيبا، قال لهم: يا قوم اعبدوا الله - وحده - ليس لكم من يستحق العبادة غيره، ولا تنقصوا المكيال والميزان حين تبيعون لغيركم ما يُكال ويُوزن، إنى أراكم يرجى منكم الخير، بالشكر والطاعة لله، وإعطاء الناس حقوقهم كاملة، وإنى أخاف عليكم إذا لم تشكروا خيره وتطيعوا أمره، أن يحل بكم عذاب يوم لا تستطيعون أن تفلتوا من أهواله، لأنها تحيط بالمعذبين فيها فلا يجدون سبيلا إلى الخلاص منها.',NULL,'322',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1562,'<span data-type="title" id=toc-1573>٨٥ </span>- ويا قوم أدُّوا المكِيل والموزون مما تبيعونه وافياً على وجْه العدل والتسوية، ولا تنقصوا الناس حقهم فى أشيائهم، ولا تجوروا وتفسدوا فى الأرض بسرقة أموالهم، أو الإغارة عليهم، أو قطع الطريق على العابرين منهم، تتخذون الفساد وسيلة للكسب الحرام.',NULL,'322',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1563,'<span data-type="title" id=toc-1574>٨٦ </span>- ما يبقى لكم من المال الحلال الذى تفضَّل به الله عليكم، خير لكم من المال الذى تجمعونه من حرام، إن كنتم تؤمنون بالله وتجتنبون ما حرمه عليكم فحاسبوا أنفسكم، وراقبوا ربكم، لست عليكم رقيباً أحصى أعمالكم وأحاسبكم عليها.',NULL,'322',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1564,'<span data-type="title" id=toc-1575>٨٧ </span>- قالوا ساخرين مستهزئين: يا شعيب، أصلاتك هى التى تأمرك أن تحملنا على ترك ما كان يعبد آباؤنا من الأصنام، وعلى أن نمتنع عن التصرف فى أموالنا كما نريد مما نرى فيه مصلحتنا؟ إن ذلك غاية السَّفه والطيش. ولا يتفق مع ما نعرفه عنك من العقل وسداد الرأى، فأنت المعروف بكثرة الحلم والرشد.',NULL,'323',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1565,'<span data-type="title" id=toc-1576>٨٨ </span>- قال: يا قوم: أخبرونى إن كنت على حُجة واضحة ويقين من ربى، ورزقنى رزقاً حسناً تفضلا منه، أيصح لى أن أكتم ما أمرنى بتبليغه لكم، من ترك عبادة الأصنام، وطلب إيفاء الكيل والميزان، وترك الفساد فى الأرض؟ وأنا لا أريد أن أتجه إلى فعل ما أنهاكم عنه من ذلك، ما أريد بموعظتى ونصيحتى وأمْرى ونهيى إلا الإصلاح قدر طاقتى وجهدى واستطاعتى، وما كنت موفّقاً لإصابة الحق إلا بمعونة الله وتأييده وتسديده، عليه - وحده - أعتمد، وإليه - وحده - أرجع.',NULL,'323',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1566,'<span data-type="title" id=toc-1577>٨٩ </span>- ويا قوم لا يحملنكم الخلاف بينى وبينكم على العناد والإصرار على الكفر، فيصيبكم ما أصاب قوم نوح أو قوم هود أو قوم صالح، وما عهد قوم لوط ومكانهم وهلاكهم ببعيد عنكم، فاعتبروا بهم حتى لا يصيبكم ما أصابهم.',NULL,'323',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1567,'<span data-type="title" id=toc-1578>٩٠ </span>- واطلبوا من الله أن يغفر لكم ذنوبكم، ثم ارجعوا إليه نادمين مستغفرين كلما وقع الذنب منكم، إن ربى كثير الرحمة محب ودود، يغفر للتائبين ويحب الأوَّابين.',NULL,'323',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1568,'<span data-type="title" id=toc-1579>٩١ </span>- قالوا: يا شعيب ما نعقل كثيراً مما تقوله لنا، ونؤكد لك أننا نراك بيننا ضعيفاً لا قدرة لك على الدفاع، وعلى الإقناع، إن أردنا بك ما تكره، ولولا مجاملتنا لعشيرتك، لأنها على ديننا، لقتلناك رجماً بالحجارة، وما أنت علينا بعزيز حتى نجلَّك ونحترمك ونكرمك ونصونك عن القتل بالرجم، وإنما هى المجاملة لعشيرتك تمنعنا عن قتلك.',NULL,'323',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1569,'<span data-type="title" id=toc-1580>٩٢ </span>- قال: يا قوم، أعشيرتى أحق بالمجاملة من الله، فذكرتموها ونسيتموه، وجاملتمونى واتخذتموه كالشئ المنبوذ وراء الظهر؟ إن ربى محيط علمه بكل ما تعملون، فلا يخفى عليه شئ من أعمالكم، وسيحاسبكم عليها إن نسيتموه.',NULL,'324',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1570,'<span data-type="title" id=toc-1581>٩٣ </span>- ويا قوم اعملوا على ما أنتم قادرون عليه، وما تستطيعون عمله، إن لم تسمعوا نصحى إنى مثابر على العمل بما يخالف عملكم، وسوف تعلمون مَنْ مِنا الذى يأتيه عذاب يفضحه ويذله، ومَن مِنا الذى هو كاذب: أأنا الذى أنذركم بالعذاب، أم أنتم الذين أنذرتمونى بالإخراج من القرية؟ وانتظروا ما سيحصل، إنى معكم منتظر.',NULL,'324',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1571,'<span data-type="title" id=toc-1582>٩٤ </span>- ولما وقع أمرنا بعذابهم وهلاكهم، نجينا شعيباً والذين آمنوا معه من العذاب والهلاك، وكانت نجاتهم بسبب رحمة منا لهم، وأخذت الظالمين من أهل مدين الصيحة، والرجفة المهلكة، فأصبحوا فى ديارهم هامدين، راقدين على وجوههم: لا حراك بهم.',NULL,'324',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1572,'<span data-type="title" id=toc-1583>٩٥ </span>- وانتهى أمرهم وزالت آثارهم، كأنهم لم يقيموا فى ديارهم، ونطق حالهم بما يجب أن يتنبه له ويعتبر به كل عاقل، ألا هلاكاً لمدْين، وبُعداً من رحمة الله كما بَعدت ثمود من قبلهم.',NULL,'324',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1573,'<span data-type="title" id=toc-1584>٩٦ </span>- ولقد أرسلنا موسى مؤيَّداً بمعجزاتنا الدالة على صدقه، وبالبرهان المبين ذى السلطان القاهر على النفوس.',NULL,'324',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1574,'<span data-type="title" id=toc-1585>٩٧ </span>- أرسلناه إلى فرعون وكبار رجاله، فكفر به فرعون وأمر قومه أن يتبعوه فى الكفر، فاتّبعوا أمر فرعون، وخالفوا أمر موسى، وما أمر فرعون بسديد حسن النتائج حتى يستحق أن يُتّبع.',NULL,'324',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1575,'<span data-type="title" id=toc-1586>٩٨ </span>- يتقدم قومه يوم القيامة ويقودهم كما قادهم فى الدنيا، فيوردهم النار حتماً، يصْلَونها ويتجرعون غُصص عذابها، وقُبح هذا المورد الذى يشربون منه ماء حميماً؛ ليطفئ ظمأهم، فيقطّع أمعاءهم.',NULL,'325',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1576,'<span data-type="title" id=toc-1587>٩٩ </span>- وهم فى هذه الدنيا قد تبعتهم لعنة من الله والملائكة والناس، ويوم القيامة تتبعهم كذلك اللعنة، لأنها عطاؤهم، وإنه لعطاء قبيح يثير الشعور بالذنب، ويقال فيه: بئس هذا العطاء المعطى لهؤلاء.',NULL,'325',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1577,'<span data-type="title" id=toc-1588>١٠٠ </span>- ذلك القَصَص - أيها النبى - هو بعض أخبار القرى التى أهلكناها، نقصُّها عليك لتعظ بها قومك، وتطمئن إلى نصر الله لك، بعض هذه القرى كالزرع القائم على ساقه، ليشهدوا بما حصل، وبعضها عَافى الأثر، كالزّرع الذى حصد.',NULL,'325',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1578,'<span data-type="title" id=toc-1589>١٠١ </span>- وما ظلمناهم بإهلاكهم، ولكنهم ظلموا أنفسهم بالكفر وعبادة غير الله والفساد فى الأرض، فما استطاعت أن ترد عنهم الهلاك آلهتهم التى كانوا يعبدونها من دون الله، ولا نفعتهم بشئ لما جاء أمر ربك - أيها النبى - وما زادهم إصرارهم على عبادة الأوثان إلا الهلاك والضياع.',NULL,'325',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1579,'<span data-type="title" id=toc-1590>١٠٢ </span>- ومثل هذا الأخْذ الشديد، الذى أخذ به ربك - أيها النبى - قومَ نوح وعاد وثمود وغيرهم، أخذُه الشديد إذا شاء أن يأخذ القرى وأهلها ظالمون بالكفر والفساد، إنّ أخذه قوى مؤلم شديد على الظالمين.',NULL,'325',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1580,'<span data-type="title" id=toc-1591>١٠٣ </span>- إن فى ذلك القَصَص لموعظة يعتبر بها من أيقن بالبعث وخاف عذاب يوم الآخرة، ذلك يوم مجموع للحساب فيه الناس، وذلك يوم مشهود يراه الملائكة والناس.',NULL,'325',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1581,'<span data-type="title" id=toc-1592>١٠٤ </span>- وما نؤخّره إلا لمدة قليلة حددناها، ومهما طالت فى نظر الناس فهى قليلة عند الله.',NULL,'325',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1582,'<span data-type="title" id=toc-1593>١٠٥ </span>- يوم يأتى هَوْلُه لا يستطيع إنسان أن يتكلم إلا بإذن الله، فمن الناس شقى بما يعانى من ألوان الشدة، وهو الكافر، ومنهم سعيد بما ينتظره من نعيم الآخرة، وهو المؤمن.',NULL,'326',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1583,'<span data-type="title" id=toc-1594>١٠٦ </span>- فأما الذين شقوا ففى النار مآلهم، لهم فيها تنفس مصحوب بآلام مزعجة، عند خروج الهواء من صدورهم، وعند دخوله فيها.',NULL,'326',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1584,'<span data-type="title" id=toc-1595>١٠٧ </span>- خالدين فى النار ما دامت السموات والأرض، لا يخرجون منها إلا فى الوقت الذى يشاء الله إخراجهم فيه، ليعذبهم بنوع آخر من العذاب، وإن ربك أيها - النبى - فعّال لما يريد فعله، لا يمنعه أحد عنه.',NULL,'326',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1585,'<span data-type="title" id=toc-1596>١٠٨ </span>- وأما الذين رَزقهم الله السعادة فيدخلون الجنة خالدين فيها من أول لحظة، بعد انتهاء موقف الحساب إلى ما لا نهاية، إلا الفريق الذى يشاء الله تأخيره عن دخول الجنة مع السابقين، وهم عصاة المؤمنين، الذين يتأخرون فى النار بمقدار توقيع الجزاء عليهم، ثم يخرجون منها إلى الجنة، ويعطى ربك هؤلاء السعداء فى الجنة عطاء عظيماً مستديماً، غير منقوص ولا مقطوع.',NULL,'326',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1586,'<span data-type="title" id=toc-1597>١٠٩ </span>- وإذا كان أمر الأمم المشركة الظالمة فى الدنيا ثم فى الآخرة، هو ما قصصنا عليك - أيها النبى - فلا يكن عندك أدنى شك فى مصير عبّاد الأوثان من قومك، إن استمروا على ضلالهم، لأنهم كالسابقين من آبائهم، الذين قصصنا عليك قصصهم من قبل، كلهم مشركون، وإنا لموفّون هؤلاء الكفرة استحقاقهم من العذاب كاملا على قدر جرائمهم، لا يُنقَصون منه شيئاً.',NULL,'326',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1587,'<span data-type="title" id=toc-1598>١١٠ </span>- ونؤكد لك - أيها النبى - أننا أعطينا موسى التوراة، فاختلف قومه من بعده فى تفسيرها ومعناها، حسب أهوائهم وشهواتهم، كل يريد إخضاعها لشهواته، فتقرقوا شيعا، وابتعد الكثير منهم عن الحق الذى جاءتهم به، ولولا وعد من الله سابق بتأخير عذابهم إلى يوم القيامة، لحل بهم فى دنياهم قضاء اللَّه وحكمه بإهلاك المبطلين ونجاة المحقين، كما حل بغيرهم من الأمم التى جاءتهم بها، بعد اختلاف أسلافهم فى فهمها وتحريفهم لها، مما جعل إدراك الحقائق منها أمرا عسيرا. وإن هؤلاء الذين ورثوا التوراة لفى حيرة وبعدٍ عن الحقيقة.',NULL,'327',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1588,'<span data-type="title" id=toc-1599>١١١ </span>- إن كل فريق من هؤلاء سيوفيهم ربك حتما جزاء أعمالهم، إنه سبحانه خبير بهم، محيط بدقائق ما يعملون من خير أو شر، ويجازى كلاً منهم حسب عمله.',NULL,'327',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1589,'<span data-type="title" id=toc-1600>١١٢ </span>- وإذا كان هذا هو حال الأمم التى جاءها كتاب من اللَّه فاختلفت فيه وخرجت عليه، فداوم أنت ومن معك من المؤمنين على التزام الطريق المستقيم كما أمرك الله، ولا تجاوزوا حدود الاعتدال بتقصير أو إهمال ومغالاة فى تكليف أنفسكم ما لا تطيقون. إنه سبحانه محيط علمه بكل ما تعملون فيجازيكم عليه.',NULL,'327',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1590,'<span data-type="title" id=toc-1601>١١٣ </span>- ولا تميلوا أدنى ميل إلى أعداء الله وأعدائكم الذين ظلموا أنفسهم وتجاوزوا حدود الله، ولا تُعَوِّلوا عليهم أو تستحسنوا طريقهم، فتستحقوا بسبب هذا الميل عذاب النار، ولا تجدوا أحداً يدفعه عنكم، ثم تكون عاقبتكم أنكم لا تُنصرون على أعدائكم بخذلان الله لكم، ولركونكم إلى عدوه.',NULL,'327',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1591,'<span data-type="title" id=toc-1602>١١٤ </span>- وأدِّ الصلاة - أيها النبى - على أتم وجه فى طرفى النهار، وفى أوقات متفرقة من الليل، فإنها تطهر النفوس فتتغلب على نزعة الشر، وتمحو آثار السيئات التى قَلّما يخلو منها البشر، ذلك الذى أُمِرت به - أيها النبى - من الإرشاد للخير عظة ينتفع بها المستعدون لقبولها، الذين يذكرون ربهم ولا ينسونه.',NULL,'327',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1592,'<span data-type="title" id=toc-1603>١١٥ </span>- واصبر - أيها النبى - على مشاق ما أمرناك به، وأحسِنْ تنفيذه، يعطك الله أجراً عظيماً، لأنه لا يضيع عنده أجر المحسنين لأعمالهم.',NULL,'328',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1593,'<span data-type="title" id=toc-1604>١١٦ </span>- كان يجب أن يكون من تلك الأمم السابقة - التى أهلكناها بسبب ظلمها - جماعة منهم لهم كلمة مسموعة، وفضل من دين وعقل، ينهون غيرهم عن الفساد فى الأرض، فيحفظوهم من العذاب الذى حل بهم، ولم يكن هذا، لكن الذى حدث أنه كان فيهم قليل من المؤمنين لم يُسمع لهم رأى ولا توجيه، فأنجاهم الله مع رسلهم، فى الوقت الذى أصرَّ فيه الظالمون المعاندون على ما تعوَّدوه من قبل من حياة الترف والفساد، فحال ذلك بينهم وبين الانتفاع بدعوة الحق والخير، وكانوا فى إيثارهم لهذا الطريق غارقين فى الذنوب والسيئات، فأهلكهم الله تنفيذاً لسنّته فى خلقه.',NULL,'328',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1594,'<span data-type="title" id=toc-1605>١١٧ </span>- وما كان من سنة الله، ولا من عدله فى خلقه، أن يظلم أمة من الأمم فيهلكها وهى متمسكة بالحق، ملتزمة للفضائل، عاملة على ما يصلح أمرها وأمر غيرها.',NULL,'328',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1595,'<span data-type="title" id=toc-1606>١١٨ </span>- ولو شاء ربك - أيها النبى - لجعل الناس على دين واحد، مطيعين الله بطبيعة خلقتهم، كالملائكة، ولكان العالم غير هذا العالم، ولكنه سبحانه لم يشأ ذلك، بل تركهم مختارين، فلا يزالون مختلفين فى كل شئ، حتى فى أصول العقائد، كالإيمان بالله وملائكته ورسله واليوم الآخر، مما لا يجوز الخلاف فيه، تبعاً لميولهم وشهواتهم وتفكيرهم، يتعصب كل فريق لرأيه، وما وجد عليه آباءه.',NULL,'328',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1596,'<span data-type="title" id=toc-1607>١١٩ </span>- لكن الذين رحمهم الله لسلامة فِطَرِهم، فإنهم اتفقوا على حُكم الله فيهم، فآمنوا بجميع رسله وكتبه واليوم الآخر. ولهذه المشيئة التى اقتضتها حكمته تعالى فى نظام هذا العالم، خلقهم الله سبحانه مستعدين لهذا الثواب والعقاب، وبهذا يتحقق وعد ربك بأنه لا بد من أن يملأ جهنم من أتباع إبليس من الجن والناس.',NULL,'328',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1597,'<span data-type="title" id=toc-1608>١٢٠ </span>- ونقص عليك - أيها النبى - من كل نوع من أخبار الرسل السابقة مع أممهم ما نُقَوِّى به قلبك على القيام بمشاق الرسالة، وقد جاءك فى هذه الأنباء بيان الحق الذى تدعو إليه، مثلما دعا إليه السابقون من الرسل، من توحيد الله والبُعْد عما يغضبه، كما جاءك فيها ما فيه عظة وعبرة ينتفع بها المؤمنون، فيزدادون إيماناً، والمستعدون للإيمان فيسارعون إليه.',NULL,'329',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1598,'<span data-type="title" id=toc-1609>١٢١ </span>- وقل - أيها النبى - للذين يصرون على العناد والكفر: ابْذلوا أقصى ما فى قدرتكم من محاربة الإسلام وإيذاء المؤمنين به، فإننا ماضون فى طريقنا ثابتون على عملنا.',NULL,'329',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1599,'<span data-type="title" id=toc-1610>١٢٢ </span>- وانتظروا ما تترقبونه لنا، إننا كذلك منتظرون وعد الله لنا بنجاح الدعوة والانتصار على أعدائها.',NULL,'329',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1600,'<span data-type="title" id=toc-1611>١٢٣ </span>- ولله - وحده - علم كل غيب فى السموات والأرض، فيعلم ما سيحل بكم، وما يكون لنا، وإليه وحده يرجع تصريف كل أمر من الأمور، وإذا كان الأمر كذلك، فاعبد - ربك وحده - وتوكل عليه، ولا تخش أحداً سواه، وما ربك بغافل عما تعملون جميعاً - أيها المؤمنون والكافرون - وسيجازى كلا بما يستحقه فى الدنيا والآخرة.',NULL,'329',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1601,'<span data-type="title" id=toc-1613>١ </span>- ألف. لام. راء. تلك الحروف وأمثالها يتكون منها كلامكم - أيها العرب - هى التى تتكون منها آيات الكتاب المعجز بكل ما فيه. الواضح الموضح لمن يسترشد به، ويستهديه. وفى هذه الحروف الصوتية تنبيه لهم، فيستمعوا ولو اتفقوا على عدم السماع.',NULL,'330',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1602,'<span data-type="title" id=toc-1614>٢ </span>- إنا أنزلنا على رسولنا بلغتكم - أيها العرب - كلاماً عربياً يُقرأ ويحفظ، لكى تفهموه وتبلغوا الناس ما فيه.',NULL,'330',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1603,'<span data-type="title" id=toc-1615>٣ </span>- نحن نلقى عليك - أيها النبى - أحسن القصص بإيحائنا إليك هذا الكتاب، وقد كنت قبل تلقيه من الذين غفلوا عما فيه، وعما اشتمل عليه من عظات وآيات بينات.',NULL,'331',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1604,'<span data-type="title" id=toc-1616>٤ </span>- من ذلك القصص - أيها النبى - قصة يوسف، إذ قال لأبيه: يا أبت، إنى رأيت فى منامى أحد عشر كوكباً، والشمس والقمر، رأيتهم جميعاً خاضعين لى ساجدين أمامى.',NULL,'331',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1605,'<span data-type="title" id=toc-1617>٥ </span>- قال أبوه: يا بُنى، لا تَحْك لإخوتك هذه الرؤيا، فإنها تثير فى نفوسهم الحسد، فيغريهم الشيطان بتدبير الحيل ضدك. يحتالون للكيد لك ويمكرون بك، إن الشيطان للإنسان عدو ظاهر العداوة.',NULL,'331',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1606,'<span data-type="title" id=toc-1618>٦ </span>- وكما رأيت نفسك فى المنام سيداً مطاعاً، ذا شرف وسلطان، يصطفيك ربك ويختارك ويعلمك تفسير الرؤى، وبيان ما تؤول إليه، فيعظم قدرك وذكرك، ويتم الله نعمته عليك، وعلى آل يعقوب، بالنبوة والرسالة كما أتمها على أبويك من قبل أبيك يعقوب، وهما إبراهيم وإسحاق، إن ربك كثير الحكمة فلا يخطئ، كثير العلم فيصطفى من عباده من يعلم أنه أهل للاصطفاء.',NULL,'331',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1607,'<span data-type="title" id=toc-1619>٧ </span>- لقد كان فى قصة يوسف وإخوته دلائل وعبر، للسائلين عنها والراغبين فى معرفتها.',NULL,'331',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1608,'<span data-type="title" id=toc-1620>٨ </span>- إذ قال إخوة يوسف لأبيه فيما بينهم: لَيوسف وأخوه الشقيق أحب إلى أبينا منا، ونحن جماعة قوية هى أنفع له منهما، إن أبانا بإيثاره يوسف وأخاه علينا لفى خطأ وبعد عن الحق، والصواب واضح، ظاهر الوضوح.',NULL,'331',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1609,'<span data-type="title" id=toc-1621>٩ </span>- اقتلوا يوسف أو ألقوا به فى أرض بعيدة عن أبيه، لا يصل إليها، يخلص لكم حب أبيكم وإقباله عليكم، وتكونوا من بعد إبعاد يوسف عنه بالقتل أو النفى قوماً صالحين إذ يقبل الله توبتكم، ويقبل أبوكم اعتذاركم.',NULL,'332',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1610,'<span data-type="title" id=toc-1622>١٠ </span>- قال أحد المتحدثين منهم: لا تقتلوا يوسف، فإن ذلك جرم عظيم، وألقوه فيما يغيب عن العيون من غور البئر، يلتقطه بعض السائرين فى الطريق، إذا ألقى دلوه فى البئر، فيذهب به بعيداً عنكم وعن أبيه، إن كنتم مصرين على إبعاده وتحقيق غرضكم بالفعل.',NULL,'332',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1611,'<span data-type="title" id=toc-1623>١١ </span>- قالوا بعد أن تم اتفاقهم على إبعاد يوسف: يا أبانا ما الذى رابك منا حتى تبعد يوسف عنا، ولا تشعر بالأمن إذا كان معنا؟ نحن نؤكد لك أننا نحبه، ونشفق عليه، ونريد له الخير، ونرشده إليه، وما وجد منا غير الحب وخالص النصح.',NULL,'332',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1612,'<span data-type="title" id=toc-1624>١٢ </span>- أرسله معنا إلى المراعى غداً، يتمتع بالأكل الطيب، ويلعب ويمرح وإنا لحريصون على المحافظة عليه، ودفع الأذى عنه.',NULL,'332',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1613,'<span data-type="title" id=toc-1625>١٣ </span>- قال: إننى لأشعر بالحزن إذا ذهبتم بعيداً عنى. . وأخاف إذا أمنتكم عليه أن يأكله الذئب وأنتم فى غفلة عنه.',NULL,'332',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1614,'<span data-type="title" id=toc-1626>١٤ </span>- قالوا: نقسم لك، لئن أكله الذئب، ونحن جماعة قوية، ليكونن ذلك العار والخسار، إنا إذا حدث هذا الذى تخشاه، لخاسرون لكل ما يجب الحرص عليه وعدم التفريط فيه. فاطمئن فلن نتهاون فى المحافظة عليه لأننا بذلك نعرّض أنفسنا للضياع والهوان.',NULL,'332',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1615,'<span data-type="title" id=toc-1627>١٥ </span>- فلما مضوا به بعيداً عن أبيه، وأجمعوا رأيهم فى إلقائه فى غور البئر، أنفذوا ما عزموا عليه، وألهمناه الاطمئنان والثقة بالله وأنه سيخبرهم بأمرهم هذا الذى دَبَّروه وقدموا عليه، وهم لا يشعرون حين تخبرهم أنك أخوهم يوسف الذى ائتمروا به، وظنوا أنهم قضوا عليه واستراحوا منه.',NULL,'332',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1616,'<span data-type="title" id=toc-1628>١٦ </span>- ورجعوا إلى أبيهم وقت العشاء، يظهرون الحزن ويرفعون أصواتهم بالبكاء.',NULL,'333',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1617,'<span data-type="title" id=toc-1629>١٧ </span>- قالوا: يا أبانا، إننا مضينا نتسابق فى الرمى والجرى، وتركنا يوسف عند متاعنا ليحرسه، فأكله الذئب ونحن بعيدون عنه، مشغولون بالتسابق دونه، وما أنت بمصدق لنا فيما نقوله لك، ولو كان ما نقوله الحق والصدق.',NULL,'333',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1618,'<span data-type="title" id=toc-1630>١٨ </span>- وأحضروا قميصه وعليه دم يشهد بادعائهم، إذ زعموا أنه دم يوسف ليصدقهم أبوهم، ولكنه قال: إن الذئب لم يأكله كما زعمتم، بل قد سولت لكم أنفسكم أمراً عظيماً فأقدمتم عليه، فشأنى صبر جميل لا يصحبه الجزع على ما أصابنى منكم، والله - وحده - الذى يُطْلَب منه العون على ما تزعمون وتدعون من الباطل.',NULL,'333',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1619,'<span data-type="title" id=toc-1631>١٩ </span>- وجاءت جهة البئر جماعة كانت تسرع فى السير إلى مصر، فأرسلوا مَن يرد الماء منهم ويعود إليهم من البئر بما يسقيهم، فألقى دلوه فيه ورفعه منه فإذا يوسف متعلق به. . قال واردهم يعلن ابتهاجه وفرحه: يا للخير ويا للخبر السار. . هذا غلام. . وأخفوه فى أمتعتهم، وجعلوه بضاعة تُباع، والله محيط علمه بما كانوا يعملون.',NULL,'333',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1620,'<span data-type="title" id=toc-1632>٢٠ </span>- وباعوه فى مصر بثمن دون قيمته، كان الثمن دراهم قليلة، وكانوا فى يوسف من الزاهدين الراغبين عنه، لخوفهم أن يدركهم أهله ويعرفوه بينهم وينتزعوه منهم.',NULL,'333',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1621,'<span data-type="title" id=toc-1633>٢١ </span>- وقال الذى اشتراه من مصر لزوجته: أحسنى معاملته وأكرميه حتى تطيب له الإقامة معنا، لعله ينفعنا أو نتبناه ونتخذه ولداً لنا، وكما كانت هذه المكانة عظيمة وهذه الإقامة كريمة جعلنا ليوسف فى أرض مصر مكانة أخرى كبرى، ليتصرف فيها بالعدل وحسن التدبير، لنعلمه تفسير الأحاديث والرؤى فيعرف منها ما سيقع قبل أن يقع ويستعد له، والله قوى قادر على تنفيذ كل أمر يريده، لا يُعجزه شئ عن شئ، ولكن أكثر الناس لا يعلمون خفايا حكمته ولطف تدبيره.',NULL,'334',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1622,'<span data-type="title" id=toc-1634>٢٢ </span>- ولما بلغ يوسف أقصى قوته أعطيناه حكماً صائباً، وعلماً نافعاً، ومثل هذا الجزاء الذى أعطيناه إياه على إحسانه، نجزى المحسنين على إحسانهم.',NULL,'334',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1623,'<span data-type="title" id=toc-1635>٢٣ </span>- وأرادت التى هى كان هو يعيش فى بيتها، ويشعر بسلطانها، أن تغريه بنفسها، لتصرفه عن نفسه الطاهرة إلى مواقعتها، فأخذت تذهب وتجئ أمامه، وتعرض عليه محاسنها ومفاتنها، وأوصدت الأبواب الكثيرة، وأحكمت إغلاقها، وقالت: أقبل علّى فقد هيأت لك نفسى، قال: إنى ألجأ إلى الله ليحمينى من الشر، وكيف أرتكبه معك وزوجك العزيز سيدى الذى أحسن مقامى؟ إنه لا يفوز الذين يظلمون الناس بالغدر والخيانة فيوقعون أنفسهم فى معصية الزنى.',NULL,'334',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1624,'<span data-type="title" id=toc-1636>٢٤ </span>- ولقد عزمت أن تخالطه ونازعته نفسه إليها، لولا أن رأى نور الله الحق نُصْبَ عينيه قد استضاء به، ولم يطاوع ميل النفس، وارتفع عن الهوى، فامتنع عن المعصية والخيانة وثبت على طهره وعفته. وهكذا ثبتنا يوسف على الطهر والعفاف لنصرف عنه سوء الخيانة ومعصية الزنى، إنه من عباد الله الذين أخلصوا دينهم لله.',NULL,'334',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1625,'<span data-type="title" id=toc-1637>٢٥ </span>- وأسرع يوسف إلى الباب يريد الخروج منه، فأسرعت تحاول أن تسبقه إليه، لتحول دون خروجه، وجذبت قميصه من خلفه تمنعه، وقطعته. . ووجدا عند الباب زوجها، قالت تثيره عليه: لا جزاء لمن أراد بزوجك ما يسوؤك إلا السجن يوضع فيه، أو عذاب مؤلم يقع عليه.',NULL,'335',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1626,'<span data-type="title" id=toc-1638>٢٦ </span>- قال يوسف يدافع عن نفسه: هى طلبتنى، وحاولت أن تخدعنى عن نفسى، وتخاصما فى الاتهام، فحكم حكَم من أهلها فقال: إن كان قميصه شق من أمام، فقد صدقت فى ادعائها، وهو من الكاذبين فيما أخبر به.',NULL,'335',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1627,'<span data-type="title" id=toc-1639>٢٧ </span>- وإن كان قميصه شق من خلف، فقد كذبت فى قولها، وهو من الصادقين فيما قال.',NULL,'335',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1628,'<span data-type="title" id=toc-1640>٢٨ </span>- فلما رأى الزوج قميص يوسف قُدَّ مِن خلف، قال لزوجته: إن اتهامك له بما وقعت أنت فيه مع براءته هو من كيدكن - معشر النسوة - إن مكركن عظيم.',NULL,'335',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1629,'<span data-type="title" id=toc-1641>٢٩ </span>- يا يوسف أعرض عن هذا الأمر، واكتمه ولا تذكره، واستغفرى أنت لذنبك، إنك كنتِ من الآثمين الذين تعمدوا الوقوع فى الخطأ وارتكاب الإثم، واتهموا غيرهم بما أثموا هم به.',NULL,'335',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1630,'<span data-type="title" id=toc-1642>٣٠ </span>- وانتهى الخبر إلى جماعة من النساء فى المدينة، فتحدثن وقلن: إن امرأة العزيز تغرى خادمها وتخدعه عن نفسه ليطيعها فيما تريده منه، قد خالط حبُّه شغاف قلبها حتى وصل إلى صميمه، إنا نعتقد أنها بمسلكها معه فى ضلال واضح وخطأ بيِّن.',NULL,'335',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1631,'<span data-type="title" id=toc-1643>٣١ </span>- فلما سمعت باغتيابهن وسوء كلامهن فيها، دعتهن إلى بيتها، وأعدت لهن ما يتكئن عليه من الوسائد والنمارق، وأعطت كل واحدة منهن سكيناً، بعد أن حضرن وجلسن متكئات، وقُدِّم لهن الطعام ليأكلن بالسكاكين ما تناله منه أيديهن. وقالت ليوسف: اخرج عليهن، فلما ظهر ورأينه أعظمنه وأخذهن حسنُه الرائع وجماله البارع، فجرحن أيديهن من فرط الدهشة والذهول، وهن يأكلنْ طعامهن، قلن متعجبات مندهشات: تنزيهاً لله، ما هذا الذى نراه بشراً؛ لأن البشر لا يكون على هذا الحسن والجمال والصفاء والنقاء، ما هذا إلا ملك كثير المحاسن طيب الشمائل، سخى الصفات.',NULL,'336',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1632,'<span data-type="title" id=toc-1644>٣٢ </span>- قالت امرأة العزيز تُعقّب على كلامهن: فذلك الفتى الذى بهركن حسنه، وأذهلكن عن أنفسكن حتى حصل ما حصل، هو الذى لُمْتُنَّنى فى شأنه، ولقد طلبته وحاولت إغراءه ليستجيب لى فامتنع وتأبى، كأنه فى عصمة كان يستزيد منها، وأٌقسم إن لم يفعل ما آمره به ليعاقبن بالسجن وليكونَنّ من الأذلاء المهينين.',NULL,'336',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1633,'<span data-type="title" id=toc-1645>٣٣ </span>- قال يوسف - وقد سمع منها التهديد والوعيد، وسمع منهن النصح بمطاوعتها - يا رب: السجن أحب إلى نفسى مما يطلبنه منى لأن فى هذا معصيتك، وإن لم تحوّل عنى شر مكرهن وكيدهن أمِل إليهن، وأكن من السفهاء الطائشين.',NULL,'336',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1634,'<span data-type="title" id=toc-1646>٣٤ </span>- فاستجاب الله له، فصرف عنه شر مكرهن، إنه هو - وحده - السميع لدعوات الملتجئين إليه، العليم بأحوالهم وبما يصلحهم.',NULL,'336',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1635,'<span data-type="title" id=toc-1647>٣٥ </span>- ثم ظهر رأى للعزيز وأهله، من بعد ما رأوا الدلائل الواضحة على براءة يوسف فأجمعوا على هذا الرأى، وأقسموا على تنفيذه، وهو أن يدخلوه السجن إلى زمن يقصر أو يطول، لكى يدفع مقالة السوء عن امرأته ويُبْعدها عن الغواية.',NULL,'336',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1636,'<span data-type="title" id=toc-1648>٣٦ </span>- ودخل السجن مع يوسف فتيان من خدام الملك، قال له أحدهما: لقد رأيت فى منامى أنى أعصر عنباً ليكون خمراً، وقال له الآخر: لقد رأيت أنى أحمل فوق رأسى خبزاً تأكل منه الطير، خبّرنا يا يوسف بتفسير هذا الذى رأيناه ومآل أمرنا على هداه. إنا نعتقد أنك من الذين يتصفون بالإحسان وإجادة تفسير الرؤى.',NULL,'337',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1637,'<span data-type="title" id=toc-1649>٣٧ </span>- قال لهما - يؤكد ما علماه عنه - لا يأتيكما طعام يُساق إليكما رزقاً مقدراً لكما إلا أخبرتكما بمآله إليكما قبل أن يأتيكما، وذكرت لكما صنعته وكيفيته، ذلكما التأويل للرؤيا والإخبار بالمغيبات مما علمنى ربى وأوحى به إلىَّ. لأنى أخلصت له عبادتى، ورفضت أن أشرك به شيئاً، وابتعدت عن دين قوم لا يصدقون بالله، ولا يؤمنون به على وجه صحيح، وهم بالآخرة وحسابها منكرون كافرون.',NULL,'337',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1638,'<span data-type="title" id=toc-1650>٣٨ </span>- إنى تركت ملة هؤلاء الكافرين، واتبعت دين آبائى إبراهيم وإسحاق ويعقوب، فعبدت الله - وحده - فما صح لنا أن نجعل لله أى شريك من أى شئ كان، من مَلَك أو جنى أو إنسى، فضلا عن الأصنام التى لا تنفع ولا تضر ولا تسمع ولا تبصر، ذلك التوحيد مما تفضل به الله علينا وعلى الناس، إذ أُمرنا بتبليغه إليهم، ولكن أكثر الناس لا يتلقون هذا الفضل بالشكر بل بالكفر.',NULL,'337',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1639,'<span data-type="title" id=toc-1651>٣٩ </span>- يا صاحبى فى السجن: أأرباب شتى كثيرة يخضع المرء لكل واحد منها خير، أم الله الواحد الذى لا يغالب؟ .',NULL,'337',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1640,'<span data-type="title" id=toc-1652>٤٠ </span>- ما تعبدون من غير الله إلا أسماء أطلقتموها أنتم وآباؤكم على أوهام لا وجود لها، ما أنزل الله بتسميتها آلهة من حُجة وبرهان، ما الحكم فى أمر العبادة وفيما يصح أن يعبد وما لا تصح عبادته، إلا لله أمر ألا تخضعوا لغيره وأن تعبدوه - وحده - ذلك الدين السليم القويم الذى تهدى إليه الأدلة والبراهين، ولكن أكثر الناس لا يسترشدون بهذه الأدلة، ولا يعلمون ما هم عليه من جهل وضلال.',NULL,'338',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1641,'<span data-type="title" id=toc-1653>٤١ </span>- يا صاحبى فى السجن، إليكما تفسير مناميكما: أمَّا أحدكما الذى عصر العنب فى رؤياه فيخرج من السجن ويكون ساقى الخمر للملك، وأما الثانى فيُصلَب ويُترك مصلوباً فتقع عليه الطير وتأكل من رأسه، تم الأمر على الوجه الذى بينته فيما تطلبان فيه تأويل الرؤيا.',NULL,'338',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1642,'<span data-type="title" id=toc-1654>٤٢ </span>- وقال للذى توقع النجاة منهما: اذكرنى عند الملك - بصفتى وقصتى - عساه ينصفنى وينقذنى مما أعانيه، فشغله الشيطان وأنساه أن يذكر للملك قصة يوسف، فمكث يوسف فى السجن سنين لا تقل عن ثلاث.',NULL,'338',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1643,'<span data-type="title" id=toc-1655>٤٣ </span>- وقال الملك: إنى رأيت فى منامى سبع بقرات سمان يأكلهن سبع عجاف ضعاف، ورأيت سبع سنبلات خضر، وسبع سنبلات أخر يابسات. . يا أيها الكبراء من العلماء والحكماء أفتونى فى رؤياى هذه إن كنتم تعرفون تفسير الرؤى وتفتون فيها.',NULL,'338',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1644,'<span data-type="title" id=toc-1656>٤٤ </span>- قالوا: هذه أخلاط أحلام باطلة، ووساوس تهجس فى النفس، وما نحن بتفسير الأحلام الباطلة بعالمين.',NULL,'338',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1645,'<span data-type="title" id=toc-1657>٤٥ </span>- وقال الذى نجا من صاحبى يوسف فى السجن، وتذكَّر بعد مضى مدة طويلة وصية يوسف، أنا أخبركم بتأويل الحديث الذى ذكره الملك، فأرسلونى إلى من عنده علم بتأويله آتكم بنبئه.',NULL,'338',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1646,'<span data-type="title" id=toc-1658>٤٦ </span>- مضى الساقى إلى يوسف حتى جاءه فناداه: يوسف - أيها الحريص على الصدق - أفتنا فى رؤيا سبع بقرات سمان يأكلهن سبع ضعاف، وفى رؤيا سبع سنبلات خضر وأخر يابسات. أرجو أن أرجع إلى الناس بفتواك عساهم يعلمون معناها، ويعرفون لك علمك وفضلك.',NULL,'339',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1647,'<span data-type="title" id=toc-1659>٤٧ </span>- قال يوسف: تفسير هذه الرؤيا أنكم تزرعون الأرض قمحاً وشعيراً سبع سنين متواليات دائبين على العمل فى الزراعة، فما تحصدونه احفظوه فاتركوه فى سنبله، إلا قليلا مما تأكلونه فى هذه السنين، مع الحرص على الاقتصاد.',NULL,'339',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1648,'<span data-type="title" id=toc-1660>٤٨ </span>- ثم يأتى بعد هذه السنين المخصبة سبع سنين مجدبة، تأكل ما ادخرتم لها، إلا قليلا مما تخبئونه وتحفظونه، ليكون بذراً لما تزرعونه بعد ذلك.',NULL,'339',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1649,'<span data-type="title" id=toc-1661>٤٩ </span>- ثم يأتى بعد هذه السنين المجدبة عام يغاث فيه الناس بالمطر، ويعصرون فيه العنب والزيتون وكل ما يعصر.',NULL,'339',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1650,'<span data-type="title" id=toc-1662>٥٠ </span>- تنبه الملك إلى يوسف بسبب تعبيره لرؤياه، وعزم على استدعائه فأمر أعوانه أن يحضروه، فلما أتاه من يبلغه رغبة الملك لم يستخِفَّه الخبر، رغم ما يحمل من بشرى الفرج ولم تزعزع حلمه لهفة السجين على الخلاص من ضيق السجن ووحشته، وآثر التمهل حتى تظهر براءته، على التعجل بالخروج وآثار التهمة عالقة بأردافه، فقال للرسول: عُدْ إلى سيدك واطلب منه أن يعود إلى تحقيق تهمتى، فيسأل النسوة اللواتى جمعتهن امرأة العزيز كيداً لى، فغلبهن الدهش وقطعن أيديهن: هل خرجن من التجربة معتقدات براءتى وطهرى، أو دنسى وعهرى؟ إنى أطلب ذلك كشفاً للحقيقة فى عيون الناس، أما ربى فإنه راسخ العلم باحتيالهن.',NULL,'339',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1651,'<span data-type="title" id=toc-1663>٥١ </span>- فاستحضر الملك النسوة وسألهن: ماذا كان حالكن حين حاولتن خداع يوسف ليغفل عن عصمته وطهارة نفسه؟ هل وجدتن منه ميلا إليكن؟ فأجبنه: تنزه الله عن أن يكون نسى عبده حتى تلوث طهره، فما لمسنا فيه شيئاً يشين. وحينئذٍ قويت نزعة الخير فى نفس امرأة العزيز، فاندفعت تقول: الآن وضح الحق وظهر. أنا التى خاتَلْته وحاولت فتنته عن نفسه بالإغراء فاستمسك بعصمته، وأؤكد أنه من أهل الصدق والحق حين رد التهمة علىّ ونسبها إلىّ.',NULL,'340',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1652,'<span data-type="title" id=toc-1664>٥٢ </span>- هذا اعتراف منى بالحق أُقَدِّمه، ليستيقن يوسف أنى لم أستغل غيبته فى السجن، وأتمادى فى الخيانة، وأعول على تثبيت اتهامه، ولأن الله لا ينجح تدبير الخائنين.',NULL,'340',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1653,'<span data-type="title" id=toc-1665>٥٣ </span>- وما أدَّعى عَصمةَ نفسى من الزلل، فإن النفس تميل بطبعها إلى الشهوات وتزيين السوء والشر، إلا نفس من حفظه الله وصرفه عن السوء. وإنى لأطمع فى رحمة الله وغفرانه، لأنه واسع الغفران لذنوب التائبين، قريب لا ينجح تدبير الخائنين.',NULL,'340',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1654,'<span data-type="title" id=toc-1666>٥٤ </span>- فلما ظهرت براءة يوسف عند الملك، صمم على استدعائه، وكلف رجاله أن يحضروه ليجعله من خاصته وخلصائه، فلما حضر إليه وجرى بينهما الحديث، تجلى له من يوسف ما تجلى من طهارة النفس وثقوب الرأى فقال له: إن لك فى نفسى لمقاماً كريماً ثابتاً وأنت الأمين الموثوق به.',NULL,'340',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1655,'<span data-type="title" id=toc-1667>٥٥ </span>- وعلم الملك منه حسن التدبير وكفاءته لما يقوم به، وأحسَّ يوسف بذلك، وحينئذ طلب منه أن يستوزره قائلا له: وَلّنى على خزائن ملكك ومستودعات غلات أرضك، لأنى كما تأكد لديك ضابط لأمور المملكة، حافظ لها، خبير بالتدبير وتصريف الأمور.',NULL,'340',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1656,'<span data-type="title" id=toc-1668>٥٦ </span>- وقبل الملك عرضه، فاستوزره، وبذلك أنعم الله على يوسف نعمة جليلة، فجعل له سلطاناً وقدرة فى أرض مصر، ينزل منها بأى مكان يريد. وهذا شأن الله فى عباده، يهب نعمته لمن يختاره منهم، ولا يهدر ثوابهم وإنما يؤتيهم أجورهم على الإحسان بالإحسان فى الدنيا.',NULL,'341',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1657,'<span data-type="title" id=toc-1669>٥٧ </span>- وأن ثوابه فى الآخرة لأفضل وأوفى لمن صدقوا به وبرسله، وكانوا يراقبونه ويخافون يوم الحساب.',NULL,'341',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1658,'<span data-type="title" id=toc-1670>٥٨ </span>- واشتد القحط بما حول مصر، ونزل بآل يعقوب ما نزل بغيرهم من الشدة، وقصد الناس مصر من كل مكان، بعد ما علموا من تدبير يوسف للمؤن، واستعداده لسنوات الجدب. فبعث يعقوب إليها أبناءه طلباً للطعام، واحتجز معه ابنه شقيق يوسف خوفاً عليه، فلما بلغ أبناؤه مصر توجهوا من فورهم إلى يوسف، فعرفهم دون أن يعرفوه.',NULL,'341',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1659,'<span data-type="title" id=toc-1671>٥٩ </span>- وأمر يوسف أن يُكرَّموا فى ضيافته، ويُدفع لهم من الميرة ما طلبوه فتم لهم ذلك، وأخذ يُحدثهم، ويسأل عن أحوالهم سؤال الجاهل بها، وهو بها عليم، فأخبروه أنهم تركوا أخا لهم حرص أبوهم ألا يفارقه، وهو بنيامين شقيق يوسف، فقال: ليحضر معكم أخوكم، ولا تخافوا شيئاً، فقد رأيتم إيفاء كيلكم وإكرامى لكم فى نزولكم.',NULL,'341',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1660,'<span data-type="title" id=toc-1672>٦٠ </span>- فإن لم تحضروا أخاكم هذا، فليس عندى لكم طعام، ولا تحاولوا أن تأتونى مرة أخرى.',NULL,'341',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1661,'<span data-type="title" id=toc-1673>٦١ </span>- قال إخواته: سنحتال على أبيه لينزل عن إرادته ولا يخاف عليه، ونؤكد لك أننا لن نقصر فى ذلك أو نتوانى فيه.',NULL,'341',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1662,'<span data-type="title" id=toc-1674>٦٢ </span>- ولما هموا بالرحيل، قال لأتباعه: ضعوا ما قدَّموه من ثمن بضاعتهم فى أمتعتهم، عساهم يرونها إذا عادوا إلى أهلهم، فيكون ذلك أرجى لعودتهم مؤملين فى إعطائهم الطعام، واثقين بالوفاء بالعهد، وآمنين على أخيهم وليبعثوا الطمأنينة فى نفس أبيهم.',NULL,'342',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1663,'<span data-type="title" id=toc-1675>٦٣ </span>- فلما عادوا إلى أبيهم قصوا عليه قصتهم مع عزيز مصر، وتلطفه بهم، وأنه أنذرهم بمنع الكيل لهم فى المستقبل إن لم يكن معهم بنيامين، وواعدهم بوفاء الكيل لهم، وإكرام منزلتهم إن عادوا إليه بأخيهم، وقالوا له: ابعث معنا أخانا فإنك إن بعثته اكْتلنا ما نحتاج إليه من الطعام وافياً، ونعدك وعداً مؤكداً أنا سنبذل الجهد فى المحافظة عليه.',NULL,'342',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1664,'<span data-type="title" id=toc-1676>٦٤ </span>- وثارت فى نفس يعقوب ذكريات الماضى، فربطها بالحاضر، وقال لبنيه: إن أمرى إذا استجبت لكم لعجيب فلن تكون حالى حين آمنكم على أخيكم إلا مثل حالى حين ائتمنتكم على يوسف فأخذتموه، ثم عدتم تقولون: أكله الذئب، فالله حسبى فى حماية ابنى، ولا أعتمد إلا عليه، فهو أقوى حافظ، ورحمته أوسع من أن يفجعنى بعد يوسف فى أخيه.',NULL,'342',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1665,'<span data-type="title" id=toc-1677>٦٥ </span>- وكان إخوة يوسف يجهلون أن يوسف وضع أموالهم فى حقائبهم، فلما فتحوها ووجدوا الأموال عرفوا جميل ما صنع بهم يوسف، وتذرعوا بذلك إلى بث الطمأنينة فى قلب يعقوب، وإقناعه بالاستجابة إلى ما طلب العزيز وبالغوا فى استمالته، فذكروه بما بينه وبينهم من رباط الأبوة، فقالوا: يا أبانا أى شئ تريده أجمل مما جرى وينتظر أن تجرى به الأحداث؟ هذه أموالنا أعيدت إلينا دون أن يحتجز منها شئ، فنسافر مع أخينا ونجلب الميرة لأهلنا، ونرعى أخانا، ويزيد ميرتنا حمل بعير لحق أخينا، فقد رسم العزيز أن يعطى الرجل حمل بعير.',NULL,'342',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1666,'<span data-type="title" id=toc-1678>٦٦ </span>- ونجحت محاولة أبناء يعقوب فى إقناعه، وأثَّر مقالهم فيه، فنزل عن التشدد فى احتجاز ابنه وحبسه عن الذهاب مع إخوته إلى مصر، ولكن قلبه لا يزال فى حاجة إلى ما يزيد اطمئنانه ولذلك قال لهم: لن أبعثه معكم إلا بعد أن تعطونى ضماناً قوياً، فتعاهدوا الله عهداً موثقاً أن تعيدوه إلىّ، وألا يمنعكم عن ردِّه إلا أن تُهلكوا أو يحيط بكم عدو يغلبكم عليه. فاستجابوا له، وقدَّموا ما طلب من المواثيق، وعندئذ أشهد الله على عهودهم وأيمانهم بقوله: الله على ما دار بيننا مطلع رقيب.',NULL,'342',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1667,'<span data-type="title" id=toc-1679>٦٧ </span>- اطمأن يعقوب إلى عهد أبنائه، ثم دفعته الشفقة عليهم إلا أن يوصيهم عند دخولهم مصر بأن يدخلوا من أبواب متفرقة، لكيلا يلفتوا الأنظار عند دخولهم، ولا تترقبهم الأعين، وقد يكون ما يسيئهم، وليس فى قدرتى أن أدفع عنكم أذى، فالدافع للأذى هو الله وله - وحده - الحكم، وقد توكلت عليه وفوضت إليه أمرى وأمركم، وعليه - وحده - يتوكل الذين يفوضون أمورهم إليه مؤمنين به.',NULL,'343',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1668,'<span data-type="title" id=toc-1680>٦٨ </span>- لقد استجابوا لوصية أبيهم، فدخلوا من أبواب متفرقة، وما كان ذلك ليدفع عنهم أذى كتبه الله لهم، وإن يعقوب ليعلم ذلك، فإنه ذو علم علَّمناه إيَّاه، ولكن وصيته كانت لحاجة فى نفسه، وهى شفقة الأب على أبنائه أعلنها فى هذه الوصية، وأن أكثر الناس لا يعلمون مثل علم يعقوب، فيفوضون لله ويحترسون.',NULL,'343',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1669,'<span data-type="title" id=toc-1681>٦٩ </span>- ولما دخلوا على يوسف أنزلهم منزلاً كريماً، واختص أخاه شقيقه بأن آواه إليه، وأسرَّ إليه قائلاً: إنى أخوك يوسف، فلا تحزن بما كانوا يصنعون معك وما صنعوه معى.',NULL,'343',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1670,'<span data-type="title" id=toc-1682>٧٠ </span>- فبعد أن أكرم وفادتهم، وكالهم الطعام، وزادهم حملا لأخيه، أعد رحالهم للسفر، ثم أمر أعوانه أن يدسوا إناء شرب الماء فى حمل بنيامين، ثم نادى أحد أعوان يوسف: - أيها الركب القافلون بأحمالكم - قفوا إنكم لسارقون.',NULL,'343',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1671,'<span data-type="title" id=toc-1683>٧١ </span>- فارتاع إخوة يوسف للنداء، واتجهوا إلى المنادين يسألونهم، ما الذى ضاع منكم وعم تبحثون؟',NULL,'343',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1672,'<span data-type="title" id=toc-1684>٧٢ </span>- فأجابهم الأعوان: نبحث عن الصواع، وهو إناء الملك الذى يشرب به، ومكافأة من يأتى به حمل جمل من الطعام، وأكد رئيسهم ذلك، فقال: وأنا بهذا الوعد ضامن وكفيل.',NULL,'344',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1673,'<span data-type="title" id=toc-1685>٧٣ </span>- قال إخوة يوسف: إن اتهامكم إيّانا بالسرقة لعجيب، ونؤكد بالقسم أن فيما ظهر لكم من أخلاقنا وتمسكنا بديننا فى مرتى مجيئنا ما يؤكد علمكم أننا لم نأت بغية الإفساد فى بلادكم، وما كان من أخلاقنا أن نكون من السارقين.',NULL,'344',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1674,'<span data-type="title" id=toc-1686>٧٤ </span>- وكان يوسف قد أوحى إلى أتباعه أن يكلوا إلى إخوته تقدير الجزاء الذى يستحقه من وجد الصواع عنده، تمهيداً لأخذ أخيه منهم بحكمهم، وليكون قضاؤهم مبرماً لا وجه للشفاعة فيه، فقالوا لهم: فماذا يكون جزاء السارقين عندكم إن ظهر أنه منكم؟',NULL,'344',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1675,'<span data-type="title" id=toc-1687>٧٥ </span>- ولوثوق أبناء يعقوب بأنهم لم يسرقوا الصواع، قالوا غير متلجلجين: جزاء من أخذ الصواع أن يؤخذ رقيقاً، فبمثل هذا الجزاء نجازى الظالمين الذين يأخذون أموال الناس.',NULL,'344',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1676,'<span data-type="title" id=toc-1688>٧٦ </span>- وانتهى الأمر إلى تفتيش الرحال، وكان لا بد من الأحكام حتى لا يظهر فى تنفيذ الخطة افتعال، وتولى يوسف التفتيش بنفسه، بعد أن مهّد الأمر، فبدأ بتفتيش أوعية العشرة الأشقاء، ثم انتهى إلى تفتيش وعاء أخيه، فأخرج السقاية منه، وبذلك نجحت حيلته، وحق له بقضاء إخوته أن يحتجز بنيامين، وهكذا دبَّر الله الأمر ليوسف فما كان فى استطاعته أخذ أخيه بمقتضى شريعة ملك مصر إلا بإرادة الله، وقد أرادها، فدبَّرنا الأمر ليوسف ووفقناه إلى ترتيب الأسباب وإحكام التدبير والتلطف فى الاحتيال، وهذا من فضل الله الذى يعلى فى العلم منازل من أراد، وفوق كل صاحب علم مَن هو أعظم، فهناك من يفوقه فى علمه.',NULL,'344',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1677,'<span data-type="title" id=toc-1689>٧٧ </span>- وكان إخراج الصواع من حقيبة أخيه مفاجأة أخجلت إخوته، فتنصلوا باعتذار يبرئ جماعتهم دونه، ويطعنه هو ويوسف، ويوحى بأن السرقة طبع ورثاه من قبل الأم، وقالوا: ليس بعجيب أن تقع منه سرقة إذ سبقه إلى ذلك أخوه الشقيق، وفطن يوسف إلى طعنهم الخفى، فساءه، ولكنه كتم ذلك، وأضمر فى نفسه جواباً لو صارحهم به لكان هذا الجواب: أنتم أسوأ منزلة وأحط قدرا، والله أعلم وأصدق علماً بكلامكم الذى تصفون به أخاه بوصمة السرقة.',NULL,'345',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1678,'<span data-type="title" id=toc-1690>٧٨ </span>- ولم يكن بد من محاولة لتخليص أخيهم أو افتدائه، رجاء أن تصدق مواثيقهم ليعقوب، فاتجهوا إلى ترقيق قلب يوسف بحديث الأبوة فى شيخوختها وقالوا له: - أيها العزيز - إن لأخينا أباً طاعناً فى السن، فإن رحمته قبلت واحداً منا ليلقى الجزاء بدل ابنه هذا الذى تعلق به قلبه، وأملنا أن تقبل الرجاء، فقد جربنا عادتك الكريمة، وتأكد لنا انطباعكم عن حب الإحسان وعمل المعروف.',NULL,'345',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1679,'<span data-type="title" id=toc-1691>٧٩ </span>- وما كان ليوسف أن ينقض تدبيرا وفَّقه الله إليه، ويفلت من يده أخاه، ولذلك لم يلنه استعطافهم، وردَّهم ردَّاً حاسماً، وقال لهم: إنى ألجأ إلى الله منزهاً نفسى عن الظلم فأحتجز غير من عثرنا على ما لنا معه، إذ لو أخذنا سواه بعقوبته لكنا من المعتدين الذين يأخذون البرئ بذنب المسئ.',NULL,'345',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1680,'<span data-type="title" id=toc-1692>٨٠ </span>- فلما انقطع منهم الأمل، ويئسوا من قبول الرجاء، اختلوا بأنفسهم يتشاورون فى موقفهم من أبيهم، فلما انتهى الرأى إلى كبيرهم المدبر لشئونهم قال لهم: ما كان ينبغى أن تنسوا عهدكم الموثق بيمين الله لأبيكم أن تحافظوا على أخيكم حتى تردوه إليه، ولأنكم عاقدتموه من قبل على صيانة يوسف ثم ضيعتموه، ولذلك سأبقى بمصر لا أفارقها، إلا إذا فهم أبى الوضع على حقيقته، وسمح لى بالرجوع إليه، أو قضى الله لى بالرجوع الكريم، ويسره لى بسبب من الأسباب، وهو أعدل الحاكمين.',NULL,'345',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1681,'<span data-type="title" id=toc-1693>٨١ </span>- عودوا - أنتم - إلى أبيكم وقصوا له القصة، وقولوا له: إن يد ابنك امتدت إلى صواع الملك فسرقها وقد ضبطت فى حقيبته، وعوقب على ذلك باسترقاقه، وما أخبرناك إلا بما عايناه، وما كنا مطلعين على المستور من قضاء الله حين طلبناه وأعطيناك على حفظه وردِّه إليك العهود والمواثيق وهو أعدل الحاكمين.',NULL,'346',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1682,'<span data-type="title" id=toc-1694>٨٢ </span>- وإن كنت فى شك مما بلغناك، فأرسل من يأتيك بشهادة أهل مصر واستشهد أنت بنفسك رفاقنا الذين عدنا معهم فى القافلة، لتظهر لك براءتنا، ونؤكد لك أننا صادقون فيما نقول.',NULL,'346',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1683,'<span data-type="title" id=toc-1695>٨٣ </span>- فرجع بقية الأبناء إلى يعقوب، وخبَّروه كما وصَّاهم أخوهم الكبير فَهَيَّج الخبر أحزانه، وضاعف منها فقد ابنه الثانى، ولم تطب نفسه ببراءتهم من التسبب فى ضياعه وهو المفجوع بما صنعوا من قبل فى يوسف، وصرح باتهامهم قائلا لهم: ما سلمت نيتكم فى المحافظة على ابنى، ولكن زينت لكم نفوسكم أن تخلصتم منه مثلما تخلصتم من أخيه، فلولا فتواكم وحكمكم أن يؤخذ السارق رقيقاً عقوبة له على السرقة، ما أخذ العزيز ابنى، ولا تخلف أخوكم الكبير بمصر، ولا حيلة لى إلا أن أتجمل فى مصيبتى بالعزاء الحميد، راجياً أن يرد الله على جميع أبنائى، فهو صاحب العلم المحيط بحالى وحالهم، وله الحكمة البالغة، فيما يصنع لى ويُدبِّر.',NULL,'346',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1684,'<span data-type="title" id=toc-1696>٨٤ </span>- وضاق بما قالوا فأعرض عنهم خالياً بنفسه، مشغولا بأساه وأسفه على فَقْد يوسف، فذهب سواد عينيه من شدة الحزن، وقد كظم غيظه وألمه أشد الكظم.',NULL,'346',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1685,'<span data-type="title" id=toc-1697>٨٥ </span>- وتوالت الأيام ويعقوب مسترسل فى لوعته، وخشى أبناؤه سوء العاقبة، فاتجهوا إلى مراجعته وحمله على التخفيف من شدة حزنه، وقالوا له - وهم بين الإشفاق عليه والغيظ من دوام ذكره ليوسف -: لئن لم تخفف عن نفسك لتزيدن ذكرى يوسف آلامك وأوجاعك، إلى أن يذيبك الغم فتشرف على الموت، أو تصبح فى عداد الميتين.',NULL,'346',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1686,'<span data-type="title" id=toc-1698>٨٦ </span>- ولم يؤثر قولهم فيه، فردهم قائلاً: ما شكوت لكم، ولا طلبت منكم تخفيف لوعتى، وليس لى إلا اللَّه أضرع إليه وأشكو له همومى صعبها وسهلها، وما أستطيع كتمانه منها وما لا أستطيع، لأنى أدرك من حسن صنعه وسعة رحمته ما لا تدركون.',NULL,'347',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1687,'<span data-type="title" id=toc-1699>٨٧ </span>- والثقة فى اللَّه تحيى الأمل ولذلك لم يذهب الغم برجاء يعقوب فى عودة ولديه إليه، وألقى فى روعه أنهما من الأحياء، وأن موعد التقائه بهما قد حان، فأمر بنيه أن ينقبوا عنهما، قائلا لهم: يا بنى ارجعوا إلى مصر فانضموا إلى أخيكم الكبير، وابحثوا عن يوسف وأخيه وتطلَّبوا أخبارهما فى رفق لا يشعر به الناس، ولا تقنطوا من أن يرحمنا اللَّه بردهما، لأنه لا يقنط من رحمة اللَّه غير الجاحدين.',NULL,'347',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1688,'<span data-type="title" id=toc-1700>٨٨ </span>- واستجاب إخوة يوسف لطلب أبيهم، فذهبوا إلى مصر، وتحايلوا لمقابلة حاكمها الذى ظهر لهم من بعد أنه يوسف، فلما دخلوا عليه، قالوا: - يا أيها العزيز - مسَّنا نحن وعشيرتنا الجوع وما يتبعه من ضر الأجسام والنفوس، وجئنا إليك بأموال قليلة هى بضاعتنا وهى ترد لقلتها ورداءتها، وليست كفاء ما نرجوه منك، لأننا نرجو منك وفاء الكيل فأوفه لنا، واجعل الزائد عن حقنا صدقة علينا، إن اللَّه تعالى يثيب المتصدقين بأحسن الثواب.',NULL,'347',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1689,'<span data-type="title" id=toc-1701>٨٩ </span>- أخذت يوسف الشفقة الأخوية الرحيمة التى تعفو عن الإساءة، وابتدأ يكشف أمره لهم قائلا فى عتب، هل أدركتم قبح ما فعلتموه بيوسف من إلقائه فى الجب، وبأخيه من أذى. مندفعين فى ذلك بجهل أنساكم الرحمة والأخوة؟',NULL,'347',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1690,'<span data-type="title" id=toc-1702>٩٠ </span>- نبهتهم تلك المفاجأة السارة إلى إدراك أن هذا يوسف، فتفحصوه، ثم قالوا مؤكدين: إنك لأنت يوسف حقا وصدقا، فقال يوسف الكريم مصدقا لهم: أنا يوسف، وهذا أخى، قد مَنَّ اللَّه علينا بالسلامة من المهالك، وبالكرامة والسلطان، وكان ذلك جزاء من اللَّه لإخلاصى وإحسانى، وإن اللَّه لا يضيع أجر من يُحسن ويستمر على الإحسان.',NULL,'348',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1691,'<span data-type="title" id=toc-1703>٩١ </span>- فقالوا: صدقت فيما قلت، ونؤكد لك بالقسم أن الله فضَّلك بالتقوى والصبر وحسن السيرة وأثابك بالملك وعلو المكانه، وإنما كنا آثمين فيما فعلنا بك وبأخيك، فأذلنا اللَّه لك، وجزانا جزاء الآثمين.',NULL,'348',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1692,'<span data-type="title" id=toc-1704>٩٢ </span>- فرد عليهم - النبى الكريم - قائلاً: لا لوم عليكم اليوم، ولا تأنيب، ولكم عندى الصفح الجميل لحرمة النسب وحق الأخوة، وأدعوا اللَّه لكم بالعفو والغفران، وهو صاحب الرحمة العظمى.',NULL,'348',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1693,'<span data-type="title" id=toc-1705>٩٣ </span>- ثم سألهم يوسف عن أبيه، فلما أخبروه عن سوء حاله وسوء بصره من كثرة غمه وبكائه؛ أعطاهم قميصه، وقال لهم: عودوا به إلى أبى فاطرحوه على وجهه، فسيؤكد له ذلك سلامتى، وتملأ قلبه الفرحة، ويجعله اللَّه سببا لعودة بصره، وحينئذ تعالوا إلىَّ به، وبأهلكم أجمعين.',NULL,'348',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1694,'<span data-type="title" id=toc-1706>٩٤ </span>- وارتحلوا بالقميص، وكان قلب يعقوب مستغرقا فى ترقب ما تأتى به رحلة بنيه، وكان اللَّه معه فى هذا الترقب فوصل روحه بأرواحهم، فحين تجاوزت قافلتهم أرض مصر فى طريقها إليه، شرح اللَّه صدره بالأمل، وأحاطه بجو من الطمأنينة إلى اقتراب البشرى بسلامة يوسف، وأخبر أهله بذلك إذ يقول: إنى أشعر برائحة يوسف المحبوبة تغمرنى، ولولا خشية أن تتهمونى فى قولى لأنبأتكم عن يوسف بأكثر من الشعور والوجدان.',NULL,'348',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1695,'<span data-type="title" id=toc-1707>٩٥ </span>- فرد عليه أهله ردا خشنا، حالفين باللَّه أنه لا يزال ذاهبا عن صوابه هائما فى خياله، فتهيأ له ما تهيأ من فرط محبته ليوسف، ولهجه بذكراه، ورجائه للقياه.',NULL,'348',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1696,'<span data-type="title" id=toc-1708>٩٦ </span>- واستمر على أمله منتظرا رحمة اللَّه، واستمر أهله على سوء الظن به إلى أن أتاه منْ يحمل القميص ويبشره بسلامة يوسف، فحين طرح القميص على وجه يعقوب نفحته رائحة يوسف وغمرت قلبه الفرحة، فعاد إليه بصره، ولما حدثه الرسول بحال يوسف، وأنه يطلب رحلته إليه بأهله، اتجه إلى من حوله يذكرهم بنبوءته، ويُعاتبهم على تكذيبه، ويوجه أذهانهم إلى ذكر ما أكده لهم آنفا من أنه يدرك من رحمة اللَّه وفضله ما لا يُدْرِكون.',NULL,'349',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1697,'<span data-type="title" id=toc-1709>٩٧ </span>- فقبلوا عليه معتذرين عمَّا كان منهم، راجين أن يصفح عنهم، وأن يطلب من اللَّه التجاوز عن آثامهم، لأنهم كما أكدوا فى اعتذارهم كانوا آثمين.',NULL,'349',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1698,'<span data-type="title" id=toc-1710>٩٨ </span>- فقال يعقوب: سأداوم طلب العفو من اللَّه عن سيئاتكم، إنه - وحده - صاحب المغفرة الثابتة والرحمة الدائمة.',NULL,'349',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1699,'<span data-type="title" id=toc-1711>٩٩ </span>- رحل يعقوب إلى مصر، وسار بأهله حتى بلغها، فحين دخلوا على يوسف - وكان قد استقبلهم فى مدخل مصر - عجَّل به الحنان والشوق إلى أبيه وأمه، فقربهما إليه، وطلب منهما ومن أهله أن يقيموا فى مصر آمنين سالمين بإذن اللَّه.',NULL,'349',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1700,'<span data-type="title" id=toc-1712>١٠٠ </span>- وسار الركب داخل مصر حتى بلغ دار يوسف، فدخلوها وصدَّر يوسف أبويه، فأجلسهما على سرير، وغمر يعقوب وأهله شعور بجليل ما هيأ اللَّه لهم على يدى يوسف، إذ جمع به شمل الأسرة بعد الشتات ونقلها إلى مكان عظيم من العزة والتكريم، فحيَّوه تحية مألوفة تعارف الناس عليها فى القديم للرؤساء والحاكمين، وأظهروا الخضوع لحكمه، فأثار ذلك فى نفس يوسف ذكرى حلمه وهو صغير، فقال لأبيه: هذا تفسير ما قصصت عليك من قبل من رؤيا، حين رأيت فى المنام أحد عشر كوكبا والشمس والقمر ساجدين لى، قد حققه ربى، وقد أكرمنى وأحسن إلىَّ، فأظهر براءتى، وخلصنى من السجن، وأتى بكم من البادية لنلتقى من بعد أن أفسد الشيطان بينى وبين إخوتى، وأغراهم بى، وما كان لهذا كله أن يتم بغير صنع اللَّه، فهو رفيق التدبير والتسخير لتنفيذ ما يريد، وهو المحيط علما بكل شئ، البالغ حكمه فى كل تصرف وقضاء.',NULL,'349',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1701,'<span data-type="title" id=toc-1713>١٠١ </span>- واتجه يوسف إلى اللَّه، يشكره بإحصاء نعمه عليه، ويرجوه المزيد من فضله، قائلا: يا رب ما أكثر نعمك علىَّ، وما أعظمها، لقد منحتنى من الملك ما أحمدك عليه، ووهبتنى من العلم بتفسير الأحلام ما وهبت، يا خالق السموات والأرض وبارئهما، أنت مالك أمرى ومتولى نعمتى فى محياى وبعد مماتى، اقبضنى إليك على ما ارتضيت لأنبيائك من دين الإسلام، وأدخلنى فى زمرة من هديتهم إلى الصلاح من آبائى وعبادك الصالحين المخلصين.',NULL,'350',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1702,'<span data-type="title" id=toc-1714>١٠٢ </span>- ذلك الذى قصصنا عليك - أيها النبى - من أخبار الماضى السحيق، لم يأتك إلا بإيحاء منا، وما كنت حاضرا إخوة يوسف وهم يدبرون له من المكائد وما علمت بكيدهم إلا عن طريقنا.',NULL,'350',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1703,'<span data-type="title" id=toc-1715>١٠٣ </span>- وفى أغلب الطباع مرض يجعلها غير قابلة لتصديق ما أوحى إليك مهما تعلق قلبك بأن يؤمنوا أو أجهدت نفسك أن يكونوا من المهتدين.',NULL,'350',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1704,'<span data-type="title" id=toc-1716>١٠٤ </span>- وما نقصد بما تحدثهم به من أحاديث الهدى نيل الجزاء أو منفعة، فإن لم يهتدوا فلا تحزن عليهم، وسيهدى اللَّه قوما غيرهم، فما أنزلناه إليهم خاصة، وما هو إلا موعظة وعبرة لكل من خلق اللَّه فى السموات والأرض.',NULL,'350',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1705,'<span data-type="title" id=toc-1717>١٠٥ </span>- وما أكثر الدلائل على وجود الخالق ووحدانيته وكماله، الثابتة فى السموات والأرض، يشاهدها قومك ويتولون عنها مكابرين غير معتبرين.',NULL,'350',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1706,'<span data-type="title" id=toc-1718>١٠٦ </span>- وفيهم مصدقون باللَّه معترفون بربوبيته وأنه خالق كل شئ، ولكن إيمان أكثرهم لا يقوم على أساس سليم من التوحيد، فلا يعترفون بوحدانية اللَّه اعترافاً خالصاً، ولكنه مقترن فى نفوسهم بشوائب تسلكهم فى مسلك المشركين.',NULL,'350',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1707,'<span data-type="title" id=toc-1719>١٠٧ </span>- أتَّخذوا عند اللَّه عهدا بعدم تعذيبهم، فضمنوا الأمن والسلامة من أن يصيبهم اللَّه بعذاب غامر، ويغشاهم بنقمته، كما فعل بأسلافهم من قبل؟ أو أن تفاجئهم القيامة وتبغتهم وهم مقيمون على الشرك والكفر ثم يكون مصيرهم إلى النار؟! .',NULL,'350',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1708,'<span data-type="title" id=toc-1720>١٠٨ </span>- نبِّههم - يا محمد - إلى سمو غايتك، وبصِّرهم بنبل مهمتك، فقل لهم: هذه سنتى وطريقتى، أدعو الناس إلى طريق اللَّه وأنا متثبت من أمرى، وكذلك يدعو إليها كل من تبعنى وآمن بشريعتى، وأنزِّه اللَّه عما لا يليق به، ولست مشركاً به أحداً سواه.',NULL,'351',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1709,'<span data-type="title" id=toc-1721>١٠٩ </span>- وما تحولنا عن سنتنا فى اختبار الرسل حين اخترناك - أيها النبى - ولا خرجت حال قومك عن أحوال الأمم السابقة فما بعثنا من قبلك ملائكة، وإنما اخترنا رجالا من أهل الأمصار ننزل عليهم الوحى، ونرسلهم مبشرين ومنذرين، فيستجيب لهم المهتدون، ويعاندهم الضالون {فهل غفل قومك عن هذه الحقيقة، وهل قعد بهم العجز عن السعى فأهلكناهم فى الدنيا ومصيرهم إلى النار، وآمن من آمن فنجيناهم ونصرناهم فى الدنيا، ولثواب الآخرة أفضل لمن خافوا اللَّه فلم يشركوا به ولم يعصوه، أسلبت عقولكم - أيها المعاندون - فلا تفكروا ولا تتدبروا؟} .',NULL,'351',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1710,'<span data-type="title" id=toc-1722>١١٠ </span>- ولا تستبطئ يا محمد نصرى، فإن نصرى قريب أكيد، وقد أرسلنا من قبلك رسلا فاقتضت حكمتنا أن يتراخى عنهم نصرنا، ويتطاول عليهم التكذيب من قومهم، حتى إذا زلزلت نفوس واستشعرت القنوط أدركهم نصرنا، فأنعمنا بالنجاة والسلامة على الذين يستأهلون منا إرادة النجاة وهم المؤمنون، وأدرنا دائرة السوء على الذين أجرموا بالعناد وأصروا على الشرك، ولا يدفع عذابنا وبطشنا دافع عن القوم المجرمين.',NULL,'351',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1711,'<span data-type="title" id=toc-1723>١١١ </span>- وقد أوحينا إليك ما أوحينا من قصص الأنبياء، تثبيتا لفؤادك، وهداية لقومك، وأودعناه من العبر والعظات ما يستنير به أصحاب العقول والفطن ويدركون أن القرآن حق وصدق، فما كان حديثاً مختلقاً ولا أساطير مفتراة، وإنما هو حق ووحى، ويؤكد صدق ما سبق من كتب السماء ومن جاء بها من الرسل، ويبين كل ما يحتاج إلى تفصيله من أمور الدين، ويهدى إلى الحق وإلى طريق مستقيم، ويفتح أبواب رحمة اللَّه لمن اهتدى بهديه وكان من المؤمنين الصادقين.',NULL,'351',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1712,'<span data-type="title" id=toc-1725>١ </span>- ألف. لام. ميم. راء. هذه حروف صوتية تبدأ بها بعض سور القرآن، وهى تشير إلى أنه معجز مع أنه مكون من الحروف التى تتكون منها كلمات العرب، وهذه الحروف الصوتية كانت تجذب العرب، لسماع القرآن. ذلك أن المشركين تواصوا فيما بينهم ألا يسمعوا هذا القرآن، فكان المؤمنون إذا ابتدأوا بهذه الحروف الصوتية استرعى ذلك أسماع المشركين فيسمعون.
INSERT INTO [b836] VALUES (1713,'<span data-type="title" id=toc-1726>٢ </span>- إن الذى أنزل هذا الكتاب هو الله الذى رفع ما ترون من سموات تجرى فيها النجوم بغير أعمدة تُرى ولا يعلمها إلا الله، وإن كان قد ربط بينها وبين الأرض بروابط لا تنقطع إلا أن يشاء الله، وذلل الشمس والقمر بسلطانه لمنفعتكم، وهما يدوران بانتظام لزمن قدّره الله سبحانه وتعالى، وهو سبحانه يُدَبِّر كل شئ فى السموات والأرض، ويُبَيِّن لكم آياته الكونية رجاء أن توقنوا بالوحدانية.',NULL,'353',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1714,'<span data-type="title" id=toc-1727>٣ </span>- وهو سبحانه الذى بسط لكم الأرض، وجعلها ذلولاً تسيرون فيها شرقاً وغرباً، وجعل فى هذه الأرض جبالا ثابتة وأنهاراً تجرى فيها المياه العذبة، وجعل من ماء هذه الأنهار الثمرات المختلفة التى تتوالد، والأصناف المتقابلة، منها الحلو والحامض، ومنها الأبيض والأسود، وأنه سبحانه يستر النهار بالليل، وأن فى هذا الكون وعجائبه لعلامات بينة تثبت قدرة الله ووحدانيته لمن يتفكر ويتدبر.',NULL,'353',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1715,'<span data-type="title" id=toc-1728>٤ </span>- وإن الأرض ذاتها فيها عجائب، فيها قطع من الأرض يجاور بعضها بعضا، وهى مختلفة التربة مع ذلك، بعضها قاحل، وبعضها خصب، وإن اتحدت التربة، ففيها حدائق مملوءة بكروم العنب، وفيها زرع يحصد، ونخيل مثمر، وهى مجتمعة ومتفرقة، ومع أنها تسقى بماء واحد يختلف طعمها، وإن فى هذه العجائب لدلائل واضحة على قدرة الله لمن له عقل يفكر به.',NULL,'353',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1716,'<span data-type="title" id=toc-1729>٥ </span>- وإن أمر المشركين مع هذه الدلائل لعجب، فإن كنت يا محمد تعجب، فالعجب هو قولهم: أبعد الموت وبعد أن نصير تراباً نكون أحياء من جديد؟ وهذا شأن الذين يكفرون بخالقهم، عقولهم قيدت بالضلال، ومآلهم النار التى يخلدون فيها، فهم جاحدون، مع أن مَنْ يقدر على الإنشاء يقدر على الإعادة.',NULL,'354',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1717,'<span data-type="title" id=toc-1730>٦ </span>- ويذهب بهم فرط ضلالهم أن يطلبوا إنزال العذاب عاجلا بدل أن يطلبوا الهداية التى تنقذهم، ويتوهمون أن الله لا ينزل بهم العقوبة فى الدنيا إن أراد، وقد مضت عقوبات أمثالهم على ذلك، فيمن أهلكهم الله قبلهم، وشأن الله أن يغفر الظلم لمن يتوب ويعود إلى الحق، وينزل العقاب الشديد بمن يستمر على ضلاله.',NULL,'354',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1718,'<span data-type="title" id=toc-1731>٧ </span>- ويقول هؤلاء الجاحدون غير معتدين بالمعجزة الكبرى، وهى القرآن: هلا أنزل عليه ربه علامة على نبوته من الحس كتحريك الجبال، فيبين الله لنبيه الحق فى القضية؟ ويقول له سبحانه: إنما أنت - أيها النبى - منذر لهم بسوء العاقبة، إن استمروا على ضلالهم، ولكل قوم رسول يهديهم إلى الحق، ومعجزة تبين رسالته، وليس لهم أن يختاروا، إنما عليهم أن يجيبوا التحدى وأن يأتوا بمثله.',NULL,'354',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1719,'<span data-type="title" id=toc-1732>٨ </span>- الذى أعطى الرسول تلك المعجزة الكبرى هو الذى يعلم كل شئ، ويعلم النفوس الإنسانية من وجودها نطفة فى الرحم إلى موتها، فيعلم ما تحمل كل أنثى من أجنة ليس فقط من ذكورة أو من أنوثة، وإنما يعلم حال الجنين ومستقبله فى حياته الدنيا شقى أم سعيد، مؤمن أم كافر، غنى أم فقير، ومقدار أجله فى الدنيا وكل ما يتصل بشئونه فى الحياة.',NULL,'354',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1720,'<span data-type="title" id=toc-1733>٩ </span>- هو الذى يعلم ما يغيب عن حسنا، ويعلم ما نشاهده علماً، أعظم مما نشاهد ونرى، وهو سبحانه العظيم الشأن الذى يعلم كل ما فى الوجود.',NULL,'354',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1721,'<span data-type="title" id=toc-1734>١٠ </span>- يعلم كل أحوالكم فى حياتكم، وكل أقوالكم وأعمالكم، فيعلم ما تسرون، وما تعلنون من أفعال وأقوال، ويعلم استخفاءكم بالليل وبروزكم بالنهار، والكل فى علمه سواء.',NULL,'355',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1722,'<span data-type="title" id=toc-1735>١١ </span>- وأن الله سبحانه هو الذى يحفظكم، فكل واحد من الناس له ملائكة تحفظه بأمر الله وتتناوب على حفظه من أمامه ومن خلفه، وأن الله سبحانه لا يغير حال قوم من شدة إلى رخاء، ومن قوة إلى ضعف، حتى يغيروا ما بأنفسهم بما يتناسب مع الحال التى يصيرون إليها، وإذا أراد الله أن ينزل بقوم ما يسوؤهم فليس لهم ناصر يحميهم من أمره، ولا من يتولى أمورهم فيدفع عنهم ما ينزل بهم.',NULL,'355',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1723,'<span data-type="title" id=toc-1736>١٢ </span>- وإن قدرة الله تعالى فى الكون بارزة آثارها ظاهرة، فهو الذى يريكم البرق فترهبون منظره، أو تخافون أن ينزل عليكم المطر من غير حاجة إليه فيفسد الزرع، أو تطمعون من وراء البرق فى مطر غزير تحتاجون إليه ليصلح الزرع. وهو الذى يكوِّن السحب المملوءة بالأمطار.',NULL,'355',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1724,'<span data-type="title" id=toc-1737>١٣ </span>- وإن الرعد خاضع لله سبحانه وتعالى خضوعاً مطلقاً، حتى أن صوته الذى تسمعون كأنه تسبيح له سبحانه بالحمد على تكوينه، دلالة على خضوعه، وكذلك الأرواح الطاهرة التى لا ترونها تسبِّح حامدة له، وهو الذى يُنْزِل الصواعق المحرقة فيصيب بها من يريد أن تنزل عليه، ومع هذه الدلائل الظاهرة الدالة على قدرته سبحانه يجادلون فى شأن الله سبحانه، وهو شديد القوة والتدبير فى رد كيد الأعداء.',NULL,'355',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1725,'<span data-type="title" id=toc-1738>١٤ </span>- وأن الذين يدعون فى خوفهم وأمنهم من الأصنام - دون أن يدعوا الله وحده - لا يجيبون لهم نداء ولا دعاء، وحالهم معهم كحال مَنْ يبْسط كفه ويضعها ليحمل بهذه اليد المبسوطة الماء ليبلغ فمه فيرتوى، وليس من شأن الكف المبسوطة أن توصل الماء إلى الفم، وإذا كانت تلك حالهم فما دعاؤهم الأصنام إلا ضياع وخسارة.',NULL,'355',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1726,'<span data-type="title" id=toc-1739>١٥ </span>- والله سبحانه يخضع لإرادته وعظمته كل من فى السموات والأرض من أكْوان وأناس وجن وملائكة طائعين، أو كارهين لما ينزل بهم، حتى ظلالهم من طول وقصر حسب أوقات النهار فى الظهيرة وفى الأصيل خاضعة لأمر الله ونهيه.',NULL,'356',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1727,'<span data-type="title" id=toc-1740>١٦ </span>- أمر الله نبيه أن يجادل المشركين هادياً مبيناً، فقال له: قل لهم - أيها النبى -: مَنْ الذى خلق السموات والأرض، وهو الحافظ لهما، والمسير لما فيهما؟ ثم بين لهم الجواب الصحيح الذى لا يحارون فيه، فقل لهم: هو الله المعبود بحق دون سواه، فكان حقا عليكم أن تعبدوه - وحده - ثم قل لهم: أفترون الأدلة المثبتة لإنشائه - وحده - كل شئ. وتتخذون مع ذلك أوثاناً تعتبرونها آلهة من غير أن تقروا بوحدانيته، وهذه الأوثان لا تملك لذاتها نفعاً ولا ضراً، فكيف تسوونها بالخالق المدبر، إنكم تسوون بين الخالق لكل شئ ومن لا يملك شيئاً! فكنتم كمن يسوى بين المتضادين، فهل يستوى من يبصر ومن لا يبصر؟ وهل تستوى الظلمة المتكاثفة الحالكة والنور المبين؟ أيسوغون تلك التسوية؟ أم ذهب بهم فرط ضلالهم إلى زعم أن أوثانهم شركاء له فى الخلق والتدبير، فتشابه عليهم أمر الخلق، كما ضلوا العبادة، قل لهم، أيها النبى: الله - وحده - هو الخالق لكل ما فى الوجود، وهو المتفرد بالخلق والعبادة، الغالب على كل شئ.',NULL,'356',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1728,'<span data-type="title" id=toc-1741>١٧ </span>- وأن نعمه تعالى مرئية لكم، وأصنامكم لا تأثير لها فى هذه النعم، فهو الذى أنزل عليكم الأمطار من السحاب، فتسيل بها الأنهار والوديان كل بالمقدار الذى قدره الله تعالى لإنبات الزرع، وإثمار الشجر. والأنهار فى جريانها تحمل ما لا نفع فيه ويعلو على سطحها، فيكون فيها ما فيه نفع فيبقى، وما لا نفع فيه يذهب. ومثل ذلك الحق والباطل، فالأول يبقى والثانى يذهب، ومن المعادن التى يصهرونها بالنار ما يتخذون منها حلية كالذهب والفضة، ومنافع ينتفعون بها كالحديد والنحاس، ومنها ما لا نفع فيه يعلو السطح، وأن ما لا نفع فيه يرمى وينبذ، وما فيه النفع يبقى، كذلك الأمر فى العقائد ما هو ضلال يذهب، وما هو صدق يبقى. وبمثل هذا يبين الله سبحانه الحقائق، ويمثل بعضها ببعض لتكون كلها واضحة بينة.',NULL,'356',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1729,'<span data-type="title" id=toc-1742>١٨ </span>- وإن الناس فى تلقيهم للهدى قسمان: قسم أجاب دعوة الله الخالق المدبر، فلهم العاقبة الحسنى فى الدنيا والآخرة، وقسم لم يُجب دعوة الذى أنشأه، وهؤلاء لهم العاقبة فى الآخرة، ولو ثبت لهم ملك كل ما فى الأرض جميعاً ومثله معه، ما استطاعوا أن يدفعوا عن أنفسهم العاقبة السيئة، ولكن أنى يكون لهم ذلك الملك؟ ولذلك كان لهم حساب يسوؤهم وينتهون به إلى جهنم وبئس القرار والمستقر.',NULL,'357',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1730,'<span data-type="title" id=toc-1743>١٩ </span>- إن المهتدين والضالين لا يستوون، فهل يكون الذى يعلم أن ما نزل عليك من الله الذى ربَّاك وكوَّنك واصطفاك لأداء رسالته، هو الحق الذى لا شك فيه. . هل يكون كمن ضل عن الحق، حتى صار كالأعمى الذى لا يبصر؟ إنه لا يدرك الحق وما يتذكر عظمة الله إلا أصحاب العقول التى تفكر.',NULL,'357',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1731,'<span data-type="title" id=toc-1744>٢٠ </span>- أولئك الذين يدركون الحق، هم الذين يوفون بعهد الله تعالى عليهم بمقتضى الفطرة والتكوين وبمقتضى توثيق عقودهم وعهودهم، ولا يقطعون المواثيق التى عقدوها باسم الله بينهم وبين العباد، ولا بالميثاق الأكبر الذى عقده بالفطرة والتكوين، وجعلهم يدركون الحق ويؤمنون، إلا أن يضلوا فى يقينهم.',NULL,'357',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1732,'<span data-type="title" id=toc-1745>٢١ </span>- وأولئك المؤمنون من دأبهم المحبة والطاعة، إنهم يعقدون المودة مع الناس ويخصون ذوى أرحامهم، ويؤيدون ولاتهم فى الحق، وهم يعرفون حق الله فيخشونه، ويخافون الحساب الذى يسوؤهم يوم القيامة فيتوقّون الذنوب ما استطاعوا.',NULL,'357',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1733,'<span data-type="title" id=toc-1746>٢٢ </span>- وهم يصبرون على الأذى يطلبون رضا الله بتحمله فى سبيل إعلاء الحق، ويؤدون الصلاة على وجهها تطهيراً لأرواحهم وتذكراً لربهم، وينفقون من المال الذى أعطاهم الله فى السر والعلن من غير رياء، ويدفعون السيئات بالحسنات يقومون بها، وهم بهذه الصفات لهم العاقبة الحسنة، بالإقامة يوم القيامة بأحسن دار وهى الجنة.',NULL,'357',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1734,'<span data-type="title" id=toc-1747>٢٣ </span>- تلك العاقبة الطيبة إقامة مستمرة فى الجنات والنعيم، يكونون فيها هم وآباؤهم الذين صلحت عقائدهم وأعمالهم، ومعهم أزواجهم وذرياتهم والملائكة تحييهم وتجئَ إليهم من كل ناحية.',NULL,'358',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1735,'<span data-type="title" id=toc-1748>٢٤ </span>- وتقول لهم: السلام الدائم لكم بسبب صبركم على الأذى وصبركم فى مكافحة أهوائكم، وما أحسن هذه العاقبة التى صرتم إليها، وهى الإقامة فى دار النعيم.',NULL,'358',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1736,'<span data-type="title" id=toc-1749>٢٥ </span>- وأن أوصاف المؤمنين الطيبة تقابلها أوصاف المشركين الذميمة. . فالمشركون ينقضون عهد الله الذى أخذه عليهم بمقتضى الفطرة ووثقه، فيخالفون فطرتهم وعقولهم بعبادتهم حجارة لا تنفع، ولا تضر، وينكثون فى عهودهم مع العباد، ثم يقطعون مودتهم مع الناس وصلتهم بالله، فلا يطيعون أوامره ولا يفردونه بالعبادة ويفسدون فى الأرض بالاعتداء فيها، وعدم إصلاحها والانتفاع بها، والله سبحانه لا يحب العبث والإفساد.',NULL,'358',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1737,'<span data-type="title" id=toc-1750>٢٦ </span>- وإذا كان أولئك المشركون يرون أنهم قد أُوتوا مالاً وفيراً، والمؤمنون فقراء ضعفاء، فليعلموا أن الله تعالى يعطى الرزق الوفير لمن يشاء إذا أخذ فى الأسباب، ويضيِّقه على من يشاء، فهو يعطيه للمؤمن وغير المؤمن، فلا تظنوا أن كثرة المال فى أيديهم دليل على أنهم على الحق، ولكنهم يفرحون بما أوتوا من مال، مع أن الله تعالى يعطى الدنيا لمن يحب ومن لا يحب، وما الحياة الدنيا إلا متع عارضة ضئيلة فانية.',NULL,'358',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1738,'<span data-type="title" id=toc-1751>٢٧ </span>- وأن أولئك المشركين تذهب بهم اللجاجة فيقولون: هلا أُنزل على النبى من الله معجزة أخرى؟ فقل - أيها النبى -: إن السبب فى عدم إيمانكم ليس نقص المعجزة، إنما هو الضلال، والله سبحانه وتعالى يضل من يريد ضلاله ما دام يسير فى طريق الضلال، ويهدى إلى الحق من يرجع إلى الله دائماً.',NULL,'358',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1739,'<span data-type="title" id=toc-1752>٢٨ </span>- وأن هؤلاء الذين يرجعون إلى الله، ويقبلون على الحق، هم الذين آمنوا وهم الذين تسكن قلوبهم عند ذكر الله تعالى بالقرآن وغيره، وإن القلوب لا تسكن وتطمئن إلا بتذكر عظمة الله وقدرته وطلب رضاه بطاعته.',NULL,'358',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1740,'<span data-type="title" id=toc-1753>٢٩ </span>- وإن الذين أذعنوا للحق، وقاموا بالأعمال الصالحة، لهم العاقبة الطيبة والمآل الحسن.',NULL,'359',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1741,'<span data-type="title" id=toc-1754>٣٠ </span>- كما أرسلنا إلى الماضين من الأمم رسلا بينوا لهم الحق، فضل من ضل واهتدى من اهتدى، وآتيناهم معجزات تدل على رسالتهم، أرسلناك فى أمة العرب وغيرهم، وقد مضت من قبلهم أمم، وكانت معجزتك القرآن لتقرأه لهم قراءة توضح معانيه وجلاله، وهم جاحدون برحمة الله عليهم بإنزال القرآن، فقل لهم - أيها النبى -: الله هو الذى خلقنى ويحمينى ويرحمنى، لا إله يُعبد - بحق - غيره، أعتمد عليه - وحده - وإليه مرجعى ومرجعكم.',NULL,'359',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1742,'<span data-type="title" id=toc-1755>٣١ </span>- إنهم يطلبون معجزة غير القرآن مع عظم تأثيره لو طلبوا الحق وأذعنوا له، فلو ثبت أن كتاباً يُقرأ فتتحرك به الجبال من أماكنها، أو تتصدع به الأرض، أو تخاطب به الموتى، لكان ذلك هو القرآن، ولكنهم معاندون، ولله - وحده - الأمر كله فى المعجزات وجزاء الجاحدين، وله فى ذلك القدرة الكاملة، وإذا كانوا فى هذه الحال من العناد، أفلا ييأس الذين أذعنوا للحق من أن يؤمن هؤلاء الجاحدين، وإن جحودهم بإرادة الله، ولو أراد أن يهتدى الناس جميعاً لاهتدوا، وأن قدرة الله ظاهرة بين أيديهم، فلا يزالون تصيبهم بسبب أعمالهم القوارع الشديدة التى تهلكهم، أو تنزل قريباً منهم، حتى يكون الموعد الذى وعد الله به، والله تعالى لا يخلف موعده.',NULL,'359',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1743,'<span data-type="title" id=toc-1756>٣٢ </span>- وإذا كان أولئك الجاحدون قد استهزأوا بما تدعو إليه وبالقرآن، فقد سخروا بالرسل الذين أرسلوا قبلك - أيها النبى - فلا تحزن لأنى أمهل الذين جحدوا ثم آخذهم فيكون العقاب الشديد الذى لا يقدر وصفه ولا تُعرف حاله.',NULL,'359',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1744,'<span data-type="title" id=toc-1757>٣٣ </span>- إن المشركين ضلوا فى جحودهم، فجعلوا لله شركاء فى العبادة، فهل من هو حافظ مراقب لكل نفس مُحْصٍ عليها ما تكسب من خير أو شر، تماثله هذه الأوثان؟ قل لهم - أيها النبى -: صفوهم بأوصافهم الحقيقية، أهم أحياء؟ أهم يدفعون الضر عن أنفسهم؟ فإن كانت حجارة لا تنفع ولا تضر، فهل تخدعون أنفسكم بأن يخبروا الله بما تتوهمون أنه لا يعلمه فى هذه الأرض، أم تضعونهم فى موضع العبادة بألفاظ تتلوى بها ألسنتكم، بل الحقيقة أنه زين لهم تدبيرهم وتمويههم الباطل، وبسبب ذلك صرفوا عن طريق الحق وتاهوا، ومن يكن ضلالهم مثلهم، فلن يهديه أحد، لأنه صرف نفسه عن سبيل الهداية.',NULL,'360',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1745,'<span data-type="title" id=toc-1758>٣٤ </span>- لهم العذاب فى الدنيا بالهزيمة والأسر والقتل، إن سار المؤمنون فى سبيل الحق، ولعذاب الآخرة النازل بهم لا محالة أشد وأدوْم، وما لهم أحد يقيهم من عذاب الله القاهر فوق كل شئ.',NULL,'360',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1746,'<span data-type="title" id=toc-1759>٣٥ </span>- وإذا كان لهؤلاء هذا العذاب، فللمؤمنين الجنة ونعيمها، وقد وعدوا بها. وحال هذه الجنة التى وعد بها أولئك الذين استقاموا على الحق، وجعلوا بينهم وبين الباطل وقاية من الإيمان أنها تجرى من تحت أشجارها المياه العذبة ثمراتها دائمة لا تنقطع، وظلها دائم. وهذه عاقبة الذين اتقوا الشر. أما الجاحدون فعاقبتهم دخول النار.',NULL,'360',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1747,'<span data-type="title" id=toc-1760>٣٦ </span>- والذين أعطوا علم الكتب المنزلة من شأنهم أن يفرحوا بالكتاب الذى أنزل عليك: لأنه امتداد للرسالة الإلهية، ومن يتخذون التدين تحزباً: ينكرون بعض ما أنزل إليك عداوة وعصبية، فقل - أيها النبى -: إنى ما أمرت إلا بأن أعبد الله لا أشرك فى عبادته شيئاً، وإلى عبادته - وحده - أدعو، وإليه - وحده - مرجعى.',NULL,'360',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1748,'<span data-type="title" id=toc-1761>٣٧ </span>- ومِثْل الإنزال للكتب السماوية، أنزلنا إليك القرآن حاكماً للناس فيما بينهم، وحاكماً على الكتب السابقة بالصدق. وقد أنزلناه بلغة عربية، فهو عربى، ولا تساير المشركين أو أهل الكتاب بعد الذى جاءك من الوحى والعلم، ولئن سايرتهم فما لك ناصر ينصرك من الله، أو يقيك منه. والخطاب للنبى، وهو أولى بالمؤمنين، والتحذير لهم حقيقى، وللنبى لبيان أنه مع اصطفائه وعلو منزلته قابل للتحذير.',NULL,'360',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1749,'<span data-type="title" id=toc-1762>٣٨ </span>- وإذا كان المشركون يثيرون العجب من أن لك أزواجاً وذرية، ويطلبون معجزة غير القرآن، فقد أرسلنا من قبلك رسلا لهم أزواج وأولاد، فالرسول من البشر له أوصاف البشر، ولكنه خير كله، وليس لنبى أن يأتى بمعجزة كما يحب أو يحب قومه، بل الذى يأتى بالمعجزة هو الله، وهو الذى يأذن له بها. لكل جيل من الأجيال أمر كتبه الله لهم يصلح به أمرهم، فلكل جيل معجزته التى تناسبه.',NULL,'361',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1750,'<span data-type="title" id=toc-1763>٣٩ </span>- يمحو الله ما يشاء من شرائع ومعجزات، ويحل محلها ما يشاء ويثبته وعنده أصل الشرائع الثابت الذى لا يتغير، وهو الوحدانية وأمهات الفضائل، وغير ذلك.',NULL,'361',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1751,'<span data-type="title" id=toc-1764>٤٠ </span>- ولئن أريناك بعض الذى نعدهم من ثواب أو عقاب، أو توفَّيناك قبل ذلك، لرأيت هول ما ينزل بالمشركين، ولرأيت نعيم المؤمنين، وليس عليك هذا، إنما الذى عليك أن تبلغ الرسالة والحساب علينا وحدنا.',NULL,'361',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1752,'<span data-type="title" id=toc-1765>٤١ </span>- وإن أمارات العذاب والهزيمة قائمة، ألم ينظروا إلى أنّا نأتى الأرض التى قد استولوا عليها، يأخذها منهم المؤمنون جزءا بعد جزء؟ وبذلك ننقص عليهم الأرض من حولهم، والله - وحده - هو الذى يحكم بالنصر أو الهزيمة، والثواب أو العقاب، ولا راد لحكمه، وحسابه سريع فى وقته، فلا يحتاج الفصل إلى وقت طويل، لأن عنده علم كل شئ، فالبينات قائمة.',NULL,'361',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1753,'<span data-type="title" id=toc-1766>٤٢ </span>- وقد دبَّر الذين من قبلهم التَّدبير السيئ لرسلهم، ولله سبحانه تدبير الأمر كله فى حاضر الكافرين وقابلهم، وسيكون الجزاء على ما يصنعون، وهو يعلم ما تعمله كل نفس. وإذا كانوا يجهلون أن العاقبة الحسنة للمؤمنين، فسيعلمون يوم القيامة - بالرؤية - لمن تكون العاقبة الحسنة بالإقامة فى دار النعيم.',NULL,'362',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1754,'<span data-type="title" id=toc-1767>٤٣ </span>- والغاية من المراء الذى يقوم به الذين جحدوا ولم يذعنوا للحق أن يقولوا لك - أيها النبى - لست مرسلا من عند الله، فقل لهم: حسبى أن الله هو الذى يحكم بينى وبينكم، والذى يعلم حقيقة القرآن، وما يدل عليه من إعجاز باهر تُدركه العقول السليمة.',NULL,'362',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1755,'<span data-type="title" id=toc-1769>١ </span>- ألف. لام. راء: فى الابتداء بهذه الحروف تنبيه إلى إعجاز القرآن، مع أنه مكون من حروف يتكلمون بها، وتنبيه للاستماع. هذا المذكور فى السورة كتاب منزل إليك يا محمد من عندنا، لتخرج الناس كافة من ظلمات الكفر والجهل إلى نور الإيمان والعلم بتيسير ربهم.',NULL,'363',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1756,'<span data-type="title" id=toc-1770>٢ </span>- طريق الله الذى له كل ما فى السموات وما فى الأرض - خلْقاً ومُلْكاً - إذا كان هذا هو حال الإله الحق، فالهلاك بعذاب شديد للكافرين.',NULL,'363',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1757,'<span data-type="title" id=toc-1771>٣ </span>- الذين يفضلون الحياة الدنيا على الآخرة، ويمنعون الناس عن شريعة الله، ويرغبون أن تصير الشريعة معوجة فى نظر الناس لينفروا منها؛ أولئك الموصوفون بما ذكر قد ضلوا ضلالا بعيداً عن الحق.',NULL,'363',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1758,'<span data-type="title" id=toc-1772>٤ </span>- وما أرسلنا رسولا قبلك - يا أيها النبى - إلا متكلماً بلغة قومه الذين بعثناه فيهم ليفهمهم ما أتى به، فيفقهوه ويدركوه بسهولة، وليس عليه هدايتهم، فالله يضل من يشاء لعدم استعداده لطلب الحق، ويهدى من يشاء لحسن استعداده، وهو القوى الذى لا يغلب على مشيئته، والذى يضع الأمور فى مواضعها، فلا يهدى ولا يضل إلا لحكمة.',NULL,'363',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1759,'<span data-type="title" id=toc-1773>٥ </span>- ولقد أرسلنا موسى مؤيداً بمعجزاتنا، وقلنا له: أخرج قومك بنى إسرائيل من ظلمات الكفر والجهل إلى نور الإيمان والعلم، وذكّرهم بالوقائع والنقم التى أوقعها الله بالأمم قبلهم. إن فى ذلك التذكير دلائل عظيمة على وحدانية الله، تدعو إلى الإيمان وإلى كل ما يتحقق به كمال الصبر على البلاء، والشكر على النعماء، وهذه صفة المؤمن.',NULL,'364',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1760,'<span data-type="title" id=toc-1774>٦ </span>- واذْكر - أيها النبى - لقومك، لعلهم يعتبرون، وقت قول موسى لقومه تنفيذاً لأمر ربك: اذكروا نعمة الله عليكم، حين أنجاكم من قوم فرعون وهم يذيقونكم العذاب الأليم، بتكليفكم الأعمال الشاقة، ويذبحون أبناءكم الذكور، ويستبقون نساءكم بلا قتل ذليلات مهانات، وفى كل ما ذكر من التعذيب والإنجاء اختبار من الله عظيم، ليظهر مقدار الصبر والشكر.',NULL,'364',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1761,'<span data-type="title" id=toc-1775>٧ </span>- واذكروا - يا بنى إسرائيل - حين أعلمكم ربكم وقال: والله إن شكرتم ما وهبتكم من نعمة الإنجاء وغيرها، وبالثبات على الإيمان والطاعة لأزيدنكم من نعمى، وإن جحدتم نعمى بالكفر والمعصية، لأعذبنكم عذابا مؤلما، لأن عذابى شديد للجاحدين.',NULL,'364',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1762,'<span data-type="title" id=toc-1776>٨ </span>- وقال موسى لقومه - حينما عاندوا وجحدوا -: إن تجحدوا نعم الله ولا تشكروها بالإيمان والطاعة، أنتم وجميع من فى الأرض، فإن ذلك لن يضر الله شيئا، لأن الله غنى عن شكر الشاكرين، مستوجب الحمد بذاته، وإن لم يحمده أحد.',NULL,'364',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1763,'<span data-type="title" id=toc-1777>٩ </span>- ألم يصل إليكم خبر الذين مَضوْا من قبلكم، قوم نوح وعاد وثمود، والأمم الذين جاءوا من بعدهم، وهم لا يعلمهم إلا الله لكثرتهم، وقد جاءتهم رسلهم بالحُجج الواضحة على صدقهم، فوضعوا أيديهم على أفواههم استغراباً واستنكاراً، وقالوا للرسل: إنا كفرنا بما جئتم به من المعجزات والأدلة، وإنا لفى شك مما تدعوننا إليه من الإيمان والتوحيد، لأننا لا نطمئن إليه ونشك فيه.',NULL,'365',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1764,'<span data-type="title" id=toc-1778>١٠ </span>- قالت الرسل لأقوامهم - منكرين عليهم شكهم فى وجود الله ووحدانيته، متعجبين من ذلك - أفى وجود الله وألوهيته - وحده - شك، وهو خالق السموات والأرض على غير مثال يحتذيه، وهو يدعوكم ليغفر لكم بعض ذنوبكم التى وقعت منكم قبل الإيمان، ويؤخركم إلى انتهاء آجالكم؟! قالت الأقوام لرسلهم تعنتاً: ما أنتم إلا بشر مثلنا، لا فضل لكم علينا يؤهلكم للرسالة. . تريدون أن تمنعونا بما تدعوننا إليه عمَّا كان عليه آباؤنا من العبادة، فأتونا بحُجة واضحة مما نقترحه عليكم.',NULL,'365',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1765,'<span data-type="title" id=toc-1779>١١ </span>- قالت لهم رسلهم: ما نحن إلا بشر مثلكم كما قلتم، ولكن الله يصطفى من يشاء من عباده فيخصهم بالنبوة والرسالة، وما كان فى قدرتنا أن نأتيكم بحُجة مما تقترحون إلا بتيسير منه، وعلى الله - وحده - فليتوكل المؤمنون ولنتوكل عليه بالصبر على معاندتكم.',NULL,'365',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1766,'<span data-type="title" id=toc-1780>١٢ </span>- وأى عذر لنا فى ترك التوكل على الله، وهو قد أرشدنا إلى سبيله ومنهاجه الذى شرع له، وأوجب عليه سلوكه فى الدين، وإنا لنؤكد توكلنا على الله، ولنصبرن على أذاكم لنا بالعناد واقتراح المعجزات، والله - وحده - هو الذى يتوكل عليه المتوكلون.',NULL,'365',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1767,'<span data-type="title" id=toc-1781>١٣ </span>- عمد الكفار المتجبرون إلى القوة، بعد أن عجزوا جميعاً عن مقاومة الدليل، وقالوا لرسلهم: ليكونن أحد أمرين: إما أن نخرجكم من أرضنا، وإما أن تدخلوا فى ديننا، فأوحى الله إلى الرسل قائلا: لنهلكن الكافرين لظلمهم.',NULL,'366',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1768,'<span data-type="title" id=toc-1782>١٤ </span>- ولنُسْكننّكم أرضهم من بعد هلاكهم. وذلك الإسكان للمؤمنين حق لمن خاف موقف حسابى، وخاف وعيدى بالعذاب، فإن من غلب عليه الخوف أطاع.',NULL,'366',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1769,'<span data-type="title" id=toc-1783>١٥ </span>- إن الرسل استنصروا على أقوامهم لما يئسوا من إيمانهم وطلبوا النصر من ربهم على الكافرين من أقوامهم، فنصرهم الله وربحوا، وخسر كل متكبر عن طاعة الله شديد العناد.',NULL,'366',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1770,'<span data-type="title" id=toc-1784>١٦ </span>- وقد استقبل الهزيمة فى الدنيا، ومن ورائه فى الآخرة عذاب جهنم، ويُسْقى فيها من ماء كريه، وهو كالصديد يسيل من أهل النار.',NULL,'366',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1771,'<span data-type="title" id=toc-1785>١٧ </span>- يتكلف شربه كأنه يبتلعه مرة أخرى، ولا يقرب من استساغته لأنه لا يمكن أن يستساغ لكراهته وقذارته ويحيط به أسباب الموت من الشدائد من كل جهة، وما هو فى جهنم بميت فيستريح مما هو فيه، بل يستقبل فى كل وقت عذاباً أشد.',NULL,'366',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1772,'<span data-type="title" id=toc-1786>١٨ </span>- إن حال أعمال الخيّرين الكافرين الدنيوية وكسبهم فيها - لبنائها على غير أساس من الإيمان - كحال رماد اشتدت لتفريقه الريح فى يوم شديد العواصف، لا يقدرون يوم القيامة على شئ مما كسبوا فى الدنيا من تلك الأعمال فلا يمكنهم الانتفاع بشئ منها إذ لا يرون لها أثراً من الثواب، كما لا يقدر صاحب الرماد المتطاير فى الريح على إمساك شئ منه، وهؤلاء الضالون يحسبون أنهم محسنون، مع أن أعمالهم بعيدة أشد البعد عن طريق الحق.',NULL,'366',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1773,'<span data-type="title" id=toc-1787>١٩ </span>- ألم تعلم - أيها المخاطب - أن الله تعالى خلق السموات والأرض لتقوما على الحق بمقتضى حكمته، ومن قدر على هذا كان قادراً على إهلاككم أيها الكافرون والإتيان بخلق جديد غيركم يعترفون بوجوده ووحدانيته إذا شاء.',NULL,'366',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1774,'<span data-type="title" id=toc-1788>٢٠ </span>- وما ذلك الإذهاب والإتيان على الله بمتعذر ولا بمتعسر.',NULL,'366',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1775,'<span data-type="title" id=toc-1789>٢١ </span>- وسيظهر الكفار جميعاً من قبورهم للرائين - لأجل حساب الله تعالى - ظهوراً لا شك فيه كأنه واقع الآن فعلا، فيقول ضعفاء الرأى من الأتباع للقادة المستكبرين: إنا كنا لكم تابعين فى تكذيب الرسل ومحاربتهم والإعراض عن نصائحهم، فهل أنتم اليوم دافعون عنا من عذاب الله بعض الشئ؟ قال المستكبرون: لو هدانا الله إلى طريق النجاة ووفقنا له لأرشدنا ودعوناكم إليه، ولكن ضللنا فأضللناكم، أى اخترنا لكم ما اخترناه لأنفسنا، ونحن وأنتم الآن سواء علينا الجزع والصبر، ليس لنا مهرب من العذاب.',NULL,'367',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1776,'<span data-type="title" id=toc-1790>٢٢ </span>- ويقول إبليس - حين يقضى الله الأمر بتنعيم الطائعين وتعذيب العاصين - لمن اتبعه: إن الله تعالى وعدكم وعداً حقاً بالبعث والجزاء فأنجزه، ووعدتكم وعداً باطلا بأن لا بعث ولا جزاء فأخلفتكم وعدى، وما كان لى عليكم قوة أقهركم بها على اتباعى، لكن دعوتكم بوسوستى إلى الضلالة فأسرعتم إلى طاعتى، فلا تلومونى بوسوستى، ولوموا أنفسكم على إجابتى وما أنا اليوم بمغيثكم من العذاب، وما أنتم بمغيثىَّ. إنى جحدت اليوم إشراككم إياى مع الله فى الدنيا حيث أطعتمونى كما يطيع العبد ربه. إن الكافرين لهم عذاب مؤلم.',NULL,'367',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1777,'<span data-type="title" id=toc-1791>٢٣ </span>- وأُدخل فى الآخرة الذين صدقوا وعملوا الأعمال الصالحة جنات تجرى من تحت قصورها الأنهار خالدين فيها بإذن الله تعالى وأمره، تحيتهم فيها من الملائكة تفيد الأمن والاطمئنان.',NULL,'367',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1778,'<span data-type="title" id=toc-1792>٢٤ </span>- ألم تعلم - ايها الإنسان - كيف ضرب الله مثلا لكلمة الحق الطيبة، وكلمة الباطل الخبيثة، فجعل الكلمة الحسنة الفائدة مثل شجرة حسنة المنفعة، أصلها ضارب بجذورها فى الأرض، وأفنانها مرتفعة إلى جهة السماء.',NULL,'367',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1779,'<span data-type="title" id=toc-1793>٢٥ </span>- تعطى ثمرها كل وقت عيَّنه الله؛ لإثمارها بإرادة خالقها، كذلك كلمة التوحيد ثابتة فى قلب المؤمن، وعمله يصعد إلى الله، وينال بركته وثوابه كل وقت، ويبيِّن الله الأمثال للناس، فيشبه المعانى بالمحسوسات ليتعظوا فيؤمنوا.',NULL,'368',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1780,'<span data-type="title" id=toc-1794>٢٦ </span>- الكلمة الباطلة الخبيثة شبيهة بشجرة خبيثة، كأنها اقتلعت، وكأنها ملقاة على الأرض لأنها ليس لها ثبات فيها، كذلك كلمة الباطل داحضة لا ثبات لها. لأنها لم تعاضد بحُجة.',NULL,'368',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1781,'<span data-type="title" id=toc-1795>٢٧ </span>- يثبت الله الذين آمنوا على القول الحق فى الحياة الدنيا وفى يوم القيامة، ويُبعد الله الكافرين عنه لسوء استعدادهم، ويفعل الله ما يشاء من تثبيت بعض وإضلال آخرين، لا معقب لحكمه ولا راد لقضائه.',NULL,'368',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1782,'<span data-type="title" id=toc-1796>٢٨ </span>- ألم تنظر - أيها السامع - إلى المشركين الذين وضعوا مكان شكر نعمة الله بمحمد ودينه كفراً بالله تعالى وأنزلوا أتباعهم - بإضلالهم إياهم - دار الهلاك.',NULL,'368',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1783,'<span data-type="title" id=toc-1797>٢٩ </span>- وهى جهنم يقاسون حرَّها وقبح المقر جهنم.',NULL,'368',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1784,'<span data-type="title" id=toc-1798>٣٠ </span>- وجعلوا لله - الواحد الأحد - أمثالاً من الأصنام فى العبادة، لتكون عاقبة عملهم إضلال الناس عن سبيل الله، وقل - أيها النبى - لأولئك الضالين: تمتعوا بشهواتكم فإن مرجعكم إلى النار! .',NULL,'368',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1785,'<span data-type="title" id=toc-1799>٣١ </span>- قل - يا محمد - لعبادى الصادقين الذين آمنوا وأحسنوا: أقيموا الصلاة، وأنفقوا بعض ما رزقناكم فى وجوه البر، مسرين ومعلنين، وفى كل خير، من قبل أن يأتى يوم لا انتفاع فيه بمبايعة ولا صداقة.',NULL,'368',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1786,'<span data-type="title" id=toc-1800>٣٢ </span>- الله - وحده - هو الذى أنشأ السموات وما فيها، والأرض وما فيها، وأنزل من السحاب ماء مدراراً، فأخرج بسببه رزقاً لكم. هو ثمرات الزرع أو الشجر، وسخَّر لكم السفن لتجرى فى البحر تحمل أرزاقكم وتجارتكم بإذنه ومشيئته، وسخر لكم الأنهار العذبة لتنتفعوا بها فى رى الأنفس والزروع.',NULL,'369',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1787,'<span data-type="title" id=toc-1801>٣٣ </span>- وسخر لكم الشمس والقمر دائبين، للإضاءة وإصلاح النبات والحيوان، وسخر لكم الليل للراحة، والنهار للسعى.',NULL,'369',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1788,'<span data-type="title" id=toc-1802>٣٤ </span>- وهيَّأ لكم كل ما تحتاجون إليه فى حياتكم مما شأنه أن يطلب سواء أطلبتموه أم لا، وإن تعدوا ما أنعم الله به عليكم لا يمكنكم حصر أنواعه، فضلا عن أفراده. إن الجاحد الذى قابل النعم بالجحود لشديد الظلم والجحود.',NULL,'369',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1789,'<span data-type="title" id=toc-1803>٣٥ </span>- واذكر - أيها النبى - لقومك، ليعتبروا فيرجعوا عن إشراكهم، قوْل أبيهم إبراهيم بعد بناء الكعبة: يا رب اجعل هذا البلد الذى فيه الكعبة ذا أمن من الظالمين وأبعدنى وأبنائى عن عبادة الأصنام.',NULL,'369',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1790,'<span data-type="title" id=toc-1804>٣٦ </span>- لأن الأصنام تسببت فى إضلال كثير من الناس بعبادتهم لها. فَمِن تبعنى من ذريتى، وأخلص لك العبادة، فإنه من أهل دينى، ومن عصانى - بإقامته على الشرك - فأنت قادر على هدايته لأنك كثير المغفرة والرحمة.',NULL,'369',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1791,'<span data-type="title" id=toc-1805>٣٧ </span>- يا ربنا إنى أسكنت بعض ذريتى فى وادى مكة الذى لا ينبت زرعاً، عند بيتك الذى حرَّمت التعرض له والتعاون بشأنه، وجعلت ما حوله آمناً. ربنا فأكرمهم ليقيموا الصلاة بجوار هذا البيت، فاجعل قلوباً خيرة من الناس تميل إليهم لزيارة بيتك، وارزقهم من الثمرات بإرسالها إليهم مع الوافدين، ليشكروا نعمتك بالصلاة والدعاء.',NULL,'369',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1792,'<span data-type="title" id=toc-1806>٣٨ </span>- ربنا، إنه يستوى عند علمك سرنا وعلانيتنا، فأنت أعلم بمصالحنا، وأرحم بنا منا، وما يخفى عليك شئ ولو كان صغيراً فى الأرض ولا فى السماء، فلا حاجة بنا إلى الدعاء، ولكننا ندعوك إظهاراً للعبودية، ونخشع لعظمتك، ونفتقر إلى ما عندك.',NULL,'370',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1793,'<span data-type="title" id=toc-1807>٣٩ </span>- الحمد لله الذى أعطانى - مع كبر سنى، واليأس من الولد - إسماعيل ثم إسحاق. إن ربى لسميع دعائى، مجيب له.',NULL,'370',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1794,'<span data-type="title" id=toc-1808>٤٠ </span>- رب وفقنى لأداء الصلاة على وجهها، ووفق لأدائها كذلك الأخيار من ذريتى، ربنا تقبل دعائى قبول المستجيب.',NULL,'370',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1795,'<span data-type="title" id=toc-1809>٤١ </span>- ربنا اغفر لى ما فرط منى من الذنوب، واغفر لوالدىّ وللمؤمنين. يوم يتحقق الحساب، ويكون من بعده الجزاء.',NULL,'370',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1796,'<span data-type="title" id=toc-1810>٤٢ </span>- ولا تظنن - أيها الرسول - ربك غافلاً عما يعمل الظالمون من محاربة الإسلام وأهله؛ بل هو عالم بمخالفتهم، وقدَّر تأخير عقوبتهم ليوم عسير، تبقى فيه أبصارهم مفتوحة، لا يسيطرون عليها، فلا ترتد إليهم من هول ما ترى.',NULL,'370',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1797,'<span data-type="title" id=toc-1811>٤٣ </span>- وهم مسرعون نحو الداعى، رافعو رؤوسهم إلى السماء، لا ترجع أعينهم إلى إرادتهم، وقلوبهم خالية ليس فيها تفكير من شدة الخوف.',NULL,'370',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1798,'<span data-type="title" id=toc-1812>٤٤ </span>- وبين - أيها النبى - للناس أهوال يوم القيامة الذى يأتيهم فيه العذاب فيقول الذين ظلموا أنفسهم بالكفر والمعاصى: ربنا أخِّر العذاب عنا، وردنا إلى الدنيا، وأمهلنا إلى أجل من الزمان قريب، نتدارك ما فرطنا بإجابة دعوتك إلى التوحيد واتباع الرسل. فيقال لهم: أتقولون اليوم هذا ونسيتم أنكم حلفتم من قبل فى الدنيا أنكم إذا متم لا تزول عنكم هذه النعمة، إن كان بعث يوم القيامة؟ .',NULL,'370',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1799,'<span data-type="title" id=toc-1813>٤٥ </span>- وسكنتم فى الدنيا فى مساكن الذين ظلموا أنفسهم بالكفر والمعاصى من الأمم قبلكم، وظهر لكم بمشاهدة آثارهم كيف عاقبناهم فلم تنزجروا، وبيَّنا لكم صفات ما فعلوا وما حل بهم، فلم تعتبروا.',NULL,'371',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1800,'<span data-type="title" id=toc-1814>٤٦ </span>- وقد دبَّر هؤلاء المشركون تدبيرهم لإبطال الدعوة، وعند الله علْم مكرهم وما كان مكرهم لتزول منه الشريعة الثابتة ثبات الجبال.',NULL,'371',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1801,'<span data-type="title" id=toc-1815>٤٧ </span>- فلا تظن - أيها الرسول - أن الله تعالى مُخْلف رسله ما وعدهم به من النصر، لأنه غالب لا يمنعه أحد عما يريد، شديد الانتقام ممن كفر به وعصى رسله.',NULL,'371',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1802,'<span data-type="title" id=toc-1816>٤٨ </span>- فينتقم منهم يوم القيامة حين نجعل الأرض غير الأرض الموجودة الآن، ونجعل السموات غير السموات كذلك، ويخرج الخلائق من قبورهم لحكم الله الذى لا شريك له ولا غالب له.',NULL,'371',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1803,'<span data-type="title" id=toc-1817>٤٩ </span>- وترى الكافرين يوم القيامة مشدودين بالقيود مع شياطينهم.',NULL,'371',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1804,'<span data-type="title" id=toc-1818>٥٠ </span>- مطلية جلودهم بسائل من القطران، كالملابس على أجسادهم، وتعلو النار وجوههم وتجللها.',NULL,'371',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1805,'<span data-type="title" id=toc-1819>٥١ </span>- يفعل بهم ذلك، ليجزى الله كل نفس منهم بما كسبته فى الدنيا، والله سريع الحساب يوم القيامة ولا يشغله عنه شئ.',NULL,'371',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1806,'<span data-type="title" id=toc-1820>٥٢ </span>- هذا القرآن هو البلاغ لنصحهم ولإنذارهم وتخويفهم من عذاب الله، وليعلموا إذا خافوا وتأملوا أنه لا إله إلا إله واحد، وليتذكر أصحاب العقول عظمة ربهم، فيبتعدوا عما فيه هلاكهم.',NULL,'371',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1807,'<span data-type="title" id=toc-1822>١ </span>- تلك آيات الكتاب المنزل المقروء المبين الواضح.',NULL,'372',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1808,'<span data-type="title" id=toc-1823>٢ </span>- يود ويتمنى الذين جحدوا بأيات اللَّه - سبحانه وتعالى - كثيرا عندما يرون عذاب يوم القيامة، أن لو كانوا قد أسلموا فى الدنيا وأخلصوا دينهم لله.',NULL,'372',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1809,'<span data-type="title" id=toc-1824>٣ </span>- ولكنهم الآن غافلون عما يستقبلهم فى الآخرة من عذاب، فدعهم بعد تبليغهم وإنذارهم، ليس لهم همٌّ إلا أن يأكلوا ويستمتعوا بملاذ الدنيا، ويصرفهم أملهم الكاذب، فمن المؤكد أنهم سيعلمون ما يستقبلهم عندما يرونه رأى العين يوم القيامة.',NULL,'372',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1810,'<span data-type="title" id=toc-1825>٤ </span>- وإذا كانوا يطلبون إنزال العذاب الدنيوى كما أهلك اللَّه الذين من قبلهم، فليعلموا أن اللَّه لا يهلك مدينة أو أمة إلا لأجل معلوم عنده.',NULL,'372',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1811,'<span data-type="title" id=toc-1826>٥ </span>- لا يتقدمون عليه ولا يتأخرون عنه.',NULL,'373',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1812,'<span data-type="title" id=toc-1827>٦ </span>- وإن من قُبح حالهم وشدة غفلتهم أن ينادوا النبى متهكمين قائلين: - أيها الذى نُزِّل عليه الكتاب للذِّكر - إن بك جنونا مستمرا، فليس النداء بنزول الذكر عليه إلا للتهكم.',NULL,'373',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1813,'<span data-type="title" id=toc-1828>٧ </span>- ولفرط جحودهم يقولون بعد ذلك الشتم والتهكم: هلا أتيتنا بدل الكتاب المنزل بملائكة تكون لك حجة إن كنت صادقاً معدوداً فى الصادقين.',NULL,'373',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1814,'<span data-type="title" id=toc-1829>٨ </span>- وقد أجابهم اللَّه تعالت كلماته: ما نُنزل الملائكة إلا ومعهم الحق المؤكد الثابت الذى لا مجال لإنكاره، فإن كفروا به فإنهم لا يمهلون، بل ينزل بهم العذاب الدنيوى فوراً.',NULL,'373',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1815,'<span data-type="title" id=toc-1830>٩ </span>- وإنه لأجل أن تكون دعوة النبى بالحق قائمة إلى يوم القيامة، لم ننزل الملائكة، بل أنزلنا القرآن المستمر تذكيره للناس، وإنا لحافظون له من كل تغيير وتبديل أو زيادة أو نقصان حتى تقوم القيامة.',NULL,'373',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1816,'<span data-type="title" id=toc-1831>١٠ </span>- ولا تحزن - أيها الرسول الأمين - فقد أرسلنا قبلك رسلاً فى طوائف تتعصب للباطل مثل تعصبهم، ولقد مضوا مع الأولين الذين هلكوا لجحودهم.',NULL,'373',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1817,'<span data-type="title" id=toc-1832>١١ </span>- وما كان شأن الذين سبقوهم فى تعصبهم للباطل إلا أن يستهزئوا برسلهم رسولا رسولا، كما يستهزئون بك، فتلك سنة المبطلين.',NULL,'373',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1818,'<span data-type="title" id=toc-1833>١٢ </span>- كما أدخلنا القرآن فى قلوب المؤمنين فأضاءها، أدخلنا الباطل فى قلوب الذين اتسموا بالإجرام، فانقلبت الأوضاع فى قلوبهم، إذ تأصل الباطل فى نفوسهم.',NULL,'373',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1819,'<span data-type="title" id=toc-1834>١٣ </span>- لا يؤمن المجرمون به وقد مضت طريقة اللَّه تعالى فى إمهالهم حتى يروا عذاب يوم القيامة المؤلم.',NULL,'373',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1820,'<span data-type="title" id=toc-1835>١٤ </span>- إن هؤلاء يطلبون أن تنزل عليهم الملائكة، ولا تظن - أيها النبى - أنهم يؤمنون لو نزلت، بل لو فتحنا عليهم بابا من السماء فظلوا فيه يصعدون، يرون العجائب ويرون الملائكة.',NULL,'374',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1821,'<span data-type="title" id=toc-1836>١٥ </span>- ما آمنوا، ولقالوا: إنما حبست أبصارنا عن النظر، وغطيت، بل إن ما كان هو السحر، وقد سحرنا، فلا جدوى فى أى آية مع الجحود فى قلوبهم.',NULL,'374',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1822,'<span data-type="title" id=toc-1837>١٦ </span>- وإننا قد جعلنا فى السماء نجوماً لتكون مجموعات متعددة مختلفة الأشكال والهيئات، وزيناها بذلك للذين ينظرون متأملين معتبرين مستدلين بها على قدرة مبدعها.',NULL,'374',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1823,'<span data-type="title" id=toc-1838>١٧ </span>- ولكن حفظناها من كل شيطان جدير بالرجم والطرد من رحمة اللَّه تعالى.',NULL,'374',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1824,'<span data-type="title" id=toc-1839>١٨ </span>- من يحاول من هؤلاء الشياطين أن يسترق الاستماع إلى الكلام الذى يجرى بين سكان هذه النجوم، فإنا نلحقه بجرم سماوى واضح بيِّن.',NULL,'374',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1825,'<span data-type="title" id=toc-1840>١٩ </span>- وخلقنا لكم الأرض ومهدناها حتى صارت كالبساط الممدود، ووضعنا فيها جبالاً ثابتة، وأنبتنا لكم فيها من كل أنواع النبات ما يحفظ حياتكم، وجعلناه مُقدرا بأزمان معينة فى نموه وغذائه، ومُقدَّراً بمقدار حاجتكم ومقدار كميته، وفى أشكاله فى الخلق والطبيعة.',NULL,'374',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1826,'<span data-type="title" id=toc-1841>٢٠ </span>- وجعلنا فى الأرض أسباب المعيشة الطيبة لكم، ففيها الحجارة التى تبنون منها المساكن، والحيوان الذى تنتفعون بلحمه أو جلده أو ريشه، والمعادن التى تخرج من بطنها، وغير ذلك، وكما أن فيها أسباب المعيشة الطيبة، ففيها المعيشة أيضا لمن يكونون فى ولايتكم من عيال وأتباع، فالله - وحده - هو يرزقهم وإياكم.',NULL,'374',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1827,'<span data-type="title" id=toc-1842>٢١ </span>- وما من شئ من الخير إلا عندنا كالخزائن المملوءة، من حيث تهيئته وتقديمه فى وقته، وما ننزله إلى العباد إلا بقدر معلوم حددته حكمتنا فى الكون.',NULL,'375',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1828,'<span data-type="title" id=toc-1843>٢٢ </span>- وقد أرسلنا الرياح حاملة بالأمطار وحاملة بذور الإنبات، وأنزلنا منها الماء وجعلناه سقيا لكم، وأن ذلك خاضع لإرادتنا، ولا يتمكن أحد من التحكم فيه حتى يصير عنده كالخزائن.',NULL,'375',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1829,'<span data-type="title" id=toc-1844>٢٣ </span>- وإنا - وحدنا - نُمد الأشياء بالحياة، ثم ننقلها إلى الموت إذ الوجود كله لنا.',NULL,'375',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1830,'<span data-type="title" id=toc-1845>٢٤ </span>- وكل منكم له أجل محدود، نعلمه نحن، فنعلم الذين يتقدمون فى الموت والحياة، والذين يتأخرون.',NULL,'375',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1831,'<span data-type="title" id=toc-1846>٢٥ </span>- وأن المتقدمين والمتأخرين سيجمعون فى وقت واحد، وسيحاسبهم ويجازيهم اللَّه، وإن ذلك مقتضى حكمته وعلمه، وهو الذى يسمى الحكيم العليم.',NULL,'375',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1832,'<span data-type="title" id=toc-1847>٢٦ </span>- وإننا فى خلقنا للعاملين فى هذه الأرض خلقنا طبيعتين: خلقنا الإنسان من طين يابس يصوت إذا نقر عليه.',NULL,'375',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1833,'<span data-type="title" id=toc-1848>٢٧ </span>- وعالم الجن خلقناه من قبل حين خلق أصله إبليس من النار ذات الحرارة الشديدة النافذة فى مسام الجسم الإنسانى.',NULL,'375',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1834,'<span data-type="title" id=toc-1849>٢٨ </span>- واذكر - أيها النبى - أصل الخلق، إذ قال خالقك رب العالمين للملائكة: إنى مبدع بشراً خلقته من طين يابس، له صوت إذا نقر عليه، هو متغير اللون له صورة.',NULL,'375',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1835,'<span data-type="title" id=toc-1850>٢٩ </span>- فإذا أكملته خلقاً ونفخت فيه الروح التى هى ملكى، فانزلوا بوجوهكم ساجدين له تحية وإكراما.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1836,'<span data-type="title" id=toc-1851>٣٠ </span>- فسجدوا جميعاً خاضعين لأمر الله.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1837,'<span data-type="title" id=toc-1852>٣١ </span>- لكن إبليس أبى واستكبر أن يكون مع الملائكة الذين خضعوا لأمر الله.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1838,'<span data-type="title" id=toc-1853>٣٢ </span>- عند إذ قال اللَّه تعالى: يا إبليس، ما الذى سوَّغ لك أن تعصى ولا تكون مع الخاضعين الساجدين.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1839,'<span data-type="title" id=toc-1854>٣٣ </span>- قال إبليس: ما كان من شأنى أن أسجد لإنسان خلقته من طين يابس له صوت إذا نقر عليه، وهو متغير اللون مصور.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1840,'<span data-type="title" id=toc-1855>٣٤ </span>- قال الله تعالى: إذا كنت متمردا خارجا على طاعتى، فاخرج من الجنة فإنك مطرود من رحمتى ومن مكان الكرامة.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1841,'<span data-type="title" id=toc-1856>٣٥ </span>- وإنى قد كتبت عليك الطرد من الرحمة والكرامة إلى يوم القيامة، يوم الحساب والجزاء، وفيه يكون لك ولمن اتبعك العقاب.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1842,'<span data-type="title" id=toc-1857>٣٦ </span>- قال إبليس - وهو المتمرد على طاعة الله: يا خالقى، أمهلنى ولا تقبضنى إلى يوم القيامة، يوم يبعث الناس أحياء بعد موتهم.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1843,'<span data-type="title" id=toc-1858>٣٧ </span>- قال الله تعالى: إنك من المؤجلين الممهلين.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1844,'<span data-type="title" id=toc-1859>٣٨ </span>- إلى وقت قدرته وهو معلوم لى، ومهما يطل فهو محدود.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1845,'<span data-type="title" id=toc-1860>٣٩ </span>- قال إبليس المتمرد العاصى: يا خالقى الذى يبقينى، لقد أردت لى الضلال فوقعت فيه، وبسبب ذلك لأزينن لبنى آدم السوء، ولأعملن على ضلالهم أجمعين.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1846,'<span data-type="title" id=toc-1861>٤٠ </span>- ولن ينجو من إضلالى إلا الذين أخلصوا لك من العباد، ولم أتمكن من الاستيلاء على نفوسهم لعمرانها بذكرك.',NULL,'376',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1847,'<span data-type="title" id=toc-1862>٤١ </span>- إن خلوص العباد الذين أخلصوا دينهم هو طريق مستقيم بحق علىَّ لا أتعداه، لأنى لا أستطيع إضلالهم.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1848,'<span data-type="title" id=toc-1863>٤٢ </span>- قال اللَّه تعالى: إن عبادى الذين أخلصوا لى دينهم ليس لك قدرة على إضلالهم، لكن من اتبعك من الضالين الموغلين فى الضلال لك سلطان على نفوسهم.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1849,'<span data-type="title" id=toc-1864>٤٣ </span>- وإن النار الشديدة العميقة هى ما يوعدون به أجمعين من عذاب أليم.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1850,'<span data-type="title" id=toc-1865>٤٤ </span>- وليس للنار الشديدة باب واحد، بل لها أبواب سبعة لكثرة المستحقين لها، لكل باب طائفة مختصة به، ولكل طائفة مرتبة معلومة تتكافأ مع شرهم.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1851,'<span data-type="title" id=toc-1866>٤٥ </span>- هذا جزاء الذين يتبعون الشيطان، أما الذين عجز الشيطان عن إغوائهم لأنهم يجعلون بينه وبين نفوسهم حجابا، فلهم حدائق عظيمة وعيون جارية.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1852,'<span data-type="title" id=toc-1867>٤٦ </span>- يقول لهم ربهم: ادخلوا هذه الجنات باطمئنان آمنين فلا خوف عليكم، ولا تحزنون على أوقاتكم.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1853,'<span data-type="title" id=toc-1868>٤٧ </span>- وإن أهل الإيمان يعيشون فى هذا النعيم طيبة نفوسهم، فقد أخرجنا ما فيها من حقد، فهم جميعاً يكونون إخوانا يجلسون على أسِرَّةٍ تتقابل وجوههم بالبشر والمحبة، ولا يتدابرون كل ينقب عما وراء الآخر.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1854,'<span data-type="title" id=toc-1869>٤٨ </span>- لا يمسهم فيها تعب، وهى نعيم دائم لا يخرجون منها أبدا.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1855,'<span data-type="title" id=toc-1870>٤٩ </span>- أخبر - أيها النبى الأمين - عبادى جميعاً: أنى كثير الغفران والعفو لمن تاب وآمن وعمل صالحاً، وأنى كثير الرحمة بهم.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1856,'<span data-type="title" id=toc-1871>٥٠ </span>- وأخبرهم أن العذاب الذى أنزله بالعصاة الجاحدين هو العذاب المؤلم حقا، وكل عذاب غيره لا يعد إلى جواره.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1857,'<span data-type="title" id=toc-1872>٥١ </span>- ونبئهم - أيها النبى - فى بيان رحمتى الخاصة فى الدنيا، وعذابى للعصاة فيها، عن الضيف من الملائكة الذين نزلوا على إبراهيم.',NULL,'378',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1858,'<span data-type="title" id=toc-1873>٥٢ </span>- اذكر - أيها الأمين - إذ دخلوا عليه فخاف منهم، فقالوا له: أمنا واطمئنانا. فقال لهم: إنا خائفون منكم إذ فاجأتمونا وجئتم فى غير وقت للضيف عادة، ولا نعلم ما وراءكم.',NULL,'378',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1859,'<span data-type="title" id=toc-1874>٥٣ </span>- قالوا: لا تخف واطمئن، فإنا نبشرك بمولود لك يؤتيه الله - تعالى - فى مستقبل حياته علما عظيما.',NULL,'378',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1860,'<span data-type="title" id=toc-1875>٥٤ </span>- قال: كيف تبشرونى بمولود يولد لى مع أنه قد أصابتنى الشيخوخة بضعفها، فعلى أى وجه تبشروننى بهذا الأمر الغريب؟! .',NULL,'378',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1861,'<span data-type="title" id=toc-1876>٥٥ </span>- قالوا: بشرناك بالأمر الثابت الذى لا شك فيه، فلا تكن ممن ييأسون من رحمة الله.',NULL,'378',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1862,'<span data-type="title" id=toc-1877>٥٦ </span>- قال إبراهيم: إنى لا أيأس من رحمة الله، فإنه لا ييأس من رحمة الله إلا الضالون الذين لا يدركون عظمته وقدرته.',NULL,'378',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1863,'<span data-type="title" id=toc-1878>٥٧ </span>- قال، وقد استأنس بهم: إذا كنتم قد بشرتمونى بهذه البشرى، فماذا يكون من شأنكم بعدها، أيها الذين أرسلكم الله.',NULL,'378',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1864,'<span data-type="title" id=toc-1879>٥٨ </span>- قالوا: إنا أرْسَلَنَا الله - تعالى - إلى قوم أجرموا فى حق الله وحق نبيهم وحق أنفسهم، من شأنهم الإجرام - هم قوم لوط - فسنهلكهم.',NULL,'378',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1865,'<span data-type="title" id=toc-1880>٥٩ </span>- ولم يسلم من الإجرام وعذابه إلا أهل لوط، فإن الله - تعالى - قد أمرنا بأن ننجيهم أجمعين.',NULL,'378',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1866,'<span data-type="title" id=toc-1881>٦٠ </span>- ولا يستثنى من أهله إلا امرأته، فإنها لم تتبع زوجها، بل كانت مع المجرمين الذين استحقوا العذاب.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1867,'<span data-type="title" id=toc-1882>٦١ </span>- ولما نزل أولئك الملائكة الذين أرسلهم الله - تعالى - لإنزال ما توعد به، بأرض لوط وآله.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1868,'<span data-type="title" id=toc-1883>٦٢ </span>- قال لهم لوط: إنكم قوم تنكركم نفسى وتنفر منكم، مخافة أن تمسونا بشر.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1869,'<span data-type="title" id=toc-1884>٦٣ </span>- قالوا: لا تخف منا، فما جئناك بما تخاف، بل جئناك بما يسرك، وهو إنزال العذاب بقومك الذين كذبوك، وكانوا يشكون فى صدقه أو ينكرونه.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1870,'<span data-type="title" id=toc-1885>٦٤ </span>- وجئناك بالأمر الثابت الذى لا شك فيه وهو إنزال العذاب، وإن صدق الوعد من صفاتنا بأمر الله.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1871,'<span data-type="title" id=toc-1886>٦٥ </span>- وما دام العذاب نازلاً بهم، فسر ليلاً مع أهلك الذين كتبت نجاتهم، بعد مرور قطع من الليل.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1872,'<span data-type="title" id=toc-1887>٦٦ </span>- وقد أوحى الله - سبحانه وتعالى - إلى لوط: أنا حكمنا وقدرنا أن هؤلاء المجرمين هالكون، يستأصلون عند دخول الصباح، ولا يبقى منهم أحد.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1873,'<span data-type="title" id=toc-1888>٦٧ </span>- ولما أصبح رأوا الملائكة فى صورة جميلة من صور البشر، ففرحوا بهم رجاء أن يفعلوا معهم جريمتهم الشنيعة، وهى إتيان الرجال.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1874,'<span data-type="title" id=toc-1889>٦٨ </span>- خشى لوط أن يفعلوا فعلتهم الشنيعة فقال: إن هؤلاء ضيوفى فلا تفضحونى بفعلتكم القبيحة.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1875,'<span data-type="title" id=toc-1890>٦٩ </span>- وخافوا الله تعالى، فلا ترتكبوا فاحشتكم، ولا توقعونى فى الخزى والذل أمامهم.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1876,'<span data-type="title" id=toc-1891>٧٠ </span>- قال أولئك المجرمون: أو لم ننهك أن تستضيف أحدا من الناس ثم تمنعنا من أن نفعل معهم ما نشتهى؟! .',NULL,'380',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1877,'<span data-type="title" id=toc-1892>٧١ </span>- قال نبى الله لوط - ينبههم إلى الطريق الطبيعى الشرعى: هؤلاء بنات القرية وهم بناتى، تزوجوهن إن كنتم راغبين فى قضاء الشهوة.',NULL,'380',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1878,'<span data-type="title" id=toc-1893>٧٢ </span>- بحق حياتك - أيها النبى - الأمين، إنهم لفى غفلة عما سينزل بهم، جعلتهم كالسكارى، إنهم لضالون متحيرون لا يعرفون ما يسلكون.',NULL,'380',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1879,'<span data-type="title" id=toc-1894>٧٣ </span>- وبينما هم فى هذه السكرة الغافلة، استولى على ألبابهم صوت شديد الإزعاج وقد أشرقت الشمس.',NULL,'380',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1880,'<span data-type="title" id=toc-1895>٧٤ </span>- ولقد نفذ اللَّه - سبحانه - حكمه فقال: جعلنا عالى مدائنهم سافلها بانقضاضها، وأنزلنا عليه طينا متحجرا كان ينزل كالمطر، فدورهم تهدمت، وإن خرجوا إلى العراء استقبلتهم تلك الأمطار من الحجارة، وبذلك أحيط بهم.',NULL,'380',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1881,'<span data-type="title" id=toc-1896>٧٥ </span>- إن فى هذا الذى نزل بقوم لوط لعلامة بينة تدل على تنفيذ اللَّه وعيده، يعرفها الذين يتعرفون الأمور ويدركون نتائجها من سماتها. فكل عمل موصوف بالإجرام متسم به، له مثل هذه النتيجة فى الدنيا وفى الآخرة.',NULL,'380',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1882,'<span data-type="title" id=toc-1897>٧٦ </span>- وأن هذه المدينة آثارها قائمة ثابتة، وهى واقعة على طريق ثابت يسلكه الناس ويعرفونه ويعتبر بها من أراد الاعتبار.',NULL,'380',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1883,'<span data-type="title" id=toc-1898>٧٧ </span>- وأن فى بقائها قائمة على طريق واضح لدليلا على تنفيذ اللَّه - تعالى - وعيده، يدركه المؤمنون المذعنون للحق.',NULL,'380',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1884,'<span data-type="title" id=toc-1899>٧٨ </span>- ومثل تكذيب قوم لوط، كذَّب أصحاب الغيضة العظيمة ذات الثمرات رسولهم، وكانوا ظالمين شديدى الظلم فى عقائدهم ومعاملاتهم.',NULL,'380',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1885,'<span data-type="title" id=toc-1900>٧٩ </span>- فأنزلنا نقمتنا عليهم، وإن أثارهم بطريق واضح بَين يعتبر بهم من يمر بديارهم، إن كان من أهل الإيمان.',NULL,'381',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1886,'<span data-type="title" id=toc-1901>٨٠ </span>- ولقد كذَّب - مثل السابقين - أصحاب الحجر رسولهم الذى أرسل إليهم، وكانوا لهذا مكذبين كل المرسلين، لأن رسالة الله واحدة.',NULL,'381',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1887,'<span data-type="title" id=toc-1902>٨١ </span>- بينا لهم الحجج الدالة على قدرتنا ورسالة رسولنا، فكانوا معرضين عنها لا يفكرون فيها.',NULL,'381',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1888,'<span data-type="title" id=toc-1903>٨٢ </span>- وكانوا قوما ذوى منعة وعمران، فكانوا يصنعون بيوتهم فى الجبال ومن الجبال، كانوا بها مطمئنين على أنفسهم وأموالهم.',NULL,'381',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1889,'<span data-type="title" id=toc-1904>٨٣ </span>- فلما كفروا وجحدوا أتتهم أصوات مزعجة منذرة بالهلاك، فأهلكوا فى وقت الصباح.',NULL,'381',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1890,'<span data-type="title" id=toc-1905>٨٤ </span>- وما دفع عنهم الهلاك الذى نزل بهم ما كانوا يكسبون من أموال، ويتحصنون به من حصون.',NULL,'381',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1891,'<span data-type="title" id=toc-1906>٨٥ </span>- ما أنشأنا السموات والأرض وما بينهما - من فضاء، وما فيهما من أناس وحيوان ونبات وجماد، وغيرها مما لا يعلمه البشر - إلا بالعدل والحكمة والصلاح والذى لا يتفق معه استمرار الفساد وعدم نهايته، ولذا كان اليوم الذى يكون فيه انتهاء الشر آتيا لا محالة، واصفح - أيها النبى - الكريم عن المشركين بالنسبة للعقاب الدنيوى، وعاملهم بالصبر على أذاهم، والدعوة بالحكمة معاملة الصفوح الحليم.',NULL,'381',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1892,'<span data-type="title" id=toc-1907>٨٦ </span>- إن اللَّه الذى خلقك - أيها النبى - ورباك هو الكثير الخلق، العليم بحالك وحالهم، فهو حقيق بأن تكل إليه أمرك وأمرهم، وهو الذى يعلم الأصلح لك ولهم.',NULL,'381',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1893,'<span data-type="title" id=toc-1908>٨٧ </span>- ولقد آتيناك - أيها النبى الأمين - سبع آيات من القرآن، هى الفاتحة التى تكررها فى كل صلاة، وفيها الضراعة لنا، وكمال طلب الهداية، وأعطيناك القرآن العظيم كله، وفيه الحجة والإعجاز، فأنت بهذا القوى الذى يجدر منه الصفح.',NULL,'381',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1894,'<span data-type="title" id=toc-1909>٨٨ </span>- لا تنظر - أيها الرسول - نظرة تمن ورغبة إلى ما أعطيناه من مُتَع الدنيا أصنافاً من الكفار المشركين واليهود والنصارى والمجوس، فإنه مستصغر بالنسبة لما أوتيته من كمال الاتصال بنا ومن القرآن العظيم، ولا تحزن عليهم بسبب استمرارهم على غيهم، وَأَلِنْ جانبك وتواضع وارفق بالذين معك من المؤمنين، فإنهم قوة الحق وأهل اللَّه.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1895,'<span data-type="title" id=toc-1910>٨٩ </span>- وقل - أيها النبى - للجاحدين جميعاً: إنى أنا المنذر لكم بعذابى الشديد، والمبين إنذارى بالأدلة القاطعة المعجزة.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1896,'<span data-type="title" id=toc-1911>٩٠ </span>- وإن هذا مثل إنذار أولئك الذين قسموا القرآن إلى شعر وكهانة وأساطير وغيرها، ولم يؤمنوا به مع قيام الحجة عليهم.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1897,'<span data-type="title" id=toc-1912>٩١ </span>- الذين جعلوا القرآن بهذا التقسيم قطعاً متفرقة، وهو كل لا يقبل التجزئة فى إعجازه وصدقه.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1898,'<span data-type="title" id=toc-1913>٩٢ </span>- وإذا كانت تلك حالهم، فوالذى خلقك وحفظك ورباك لنحاسبنهم أجمعين يوم القيامة.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1899,'<span data-type="title" id=toc-1914>٩٣ </span>- على أعمالهم من إيذاء وجحود واستهزاء.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1900,'<span data-type="title" id=toc-1915>٩٤ </span>- فاجهر بدعوة الحق ولا تلتفت إلى ما يفعله المشركون ويقولونه.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1901,'<span data-type="title" id=toc-1916>٩٥ </span>- وإن أولئك المشركين - الذين يسخرون من دعوتك - لن يتمكنوا منك ولن يستطيعوا أن يحولوا بينك وبين دعوتك.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1902,'<span data-type="title" id=toc-1917>٩٦ </span>- أولئك المشركون قد ضعفت مداركهم فجعلوا مع اللَّه آلهة أخرى من الأوثان، وسوف يعلمون نتائج شركهم حين ينزل بهم العذاب الأليم.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1903,'<span data-type="title" id=toc-1918>٩٧ </span>- وإنا لنعلم ما يصيبك من ضيق وألم نفسى بما يقولونه من ألفاظ الشرك والاستهزاء والاستهانة.',NULL,'383',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1904,'<span data-type="title" id=toc-1919>٩٨ </span>- فإذا أصابك ذلك الضيق فافزع إلى الله - تعالى - واتجه إليه، وكن من الخاضعين الضارعين إليه، واستعن بالصلاة فإن فيها الشفاء.',NULL,'383',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1905,'<span data-type="title" id=toc-1920>٩٩ </span>- والتزم عبادة الله الذى خلقك وهو حافظك حتى ينتهى أجلك وتلحق بالرفيق الأعلى.',NULL,'383',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1906,'<span data-type="title" id=toc-1922>١ </span>- تأكدوا - أيها المشركون - أن ما توعَّدكم الله به يوم القيامة واقع قريب الوقوع لا شك فيه، فلا تستهزئوا باستعجال وقوعه، تنزه الله عن أن يكون له شريك يُعبد من دونه، وعما تشركون به من آلهة لا تقدر على شئ.',NULL,'384',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1907,'<span data-type="title" id=toc-1923>٢ </span>- ينزل الملائكة بما يحيى القلوب من وحيه على من يختاره للرسالة من عباده، ليعلموا الناس أنه لا إله يُعبد بحق إلا أنا. فابتعدوا عما يغضبنى ويعرضكم للعذاب، والتزموا الطاعات لتكون وقاية لكم من العذاب.',NULL,'385',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1908,'<span data-type="title" id=toc-1924>٣ </span>- خلق السموات والأرض بمقتضى الحكمة، تنزه الله عن أن يكون له شريك يتصرف فى شئ من ملكه، أو يستحق أن يعبد مثله.',NULL,'385',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1909,'<span data-type="title" id=toc-1925>٤ </span>- خلق كل فرد من أفراد الإنسان من مادة سائلة لا تماسك فيها وهى النطفة، فإذا به إنسان قوى مجادل عن نفسه، مكافح لخصومه، مبين لحجته.',NULL,'385',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1910,'<span data-type="title" id=toc-1926>٥ </span>- وقد تفضل اللَّه عليكم - أيها العباد - فخلق لكم الإبل والبقر والضأن والمعز لتتخذوا من أصوافها وأوبارها وأشعارها ما تستدفئون به، ومن لحومها تأكلون ما يحفظ حياتكم.',NULL,'385',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1911,'<span data-type="title" id=toc-1927>٦ </span>- ولكم فيها بهجة وسرور، حين ترونها راجعة من مراعيها ملأى البطون والضروع، وحين تذهب إلى الحقول والمراعى تُسرع الخطا إلى غذائها.',NULL,'385',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1912,'<span data-type="title" id=toc-1928>٧ </span>- وتحمل أمتعتكم الثقيلة إلى بلد لم تكونوا مستطيعين الوصول إليه بدونها إلا بتحميل أنفسكم أقصى جهدها ومشقتها. إن ربكم الذى هيأ ذلك لراحتكم لشديد الرأفة بكم، واسع الرحمة لكم.',NULL,'385',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1913,'<span data-type="title" id=toc-1929>٨ </span>- وخلق لكم الخيل والبغال والحمير لتركبوها، فتتخذوا منها زينة تُدخل السرور على قلوبكم، وسيخلق ما لا تعلمون الآن من وسائل الركوب وقطع المسافات، مما سخره اللَّه لبنى الإنسان، إذا استخدم عقله وفكر به واهتدى إلى استخدام كل القوى.',NULL,'385',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1914,'<span data-type="title" id=toc-1930>٩ </span>- وعلى اللَّه بمقتضى فضله ورحمته أن يُبيِّن لكم الطريق المستقيم الذى يوصلكم إلى الخير ومن الطريق ما هو مائل منحرف لا يوصل إلى الحق، ولو شاء هدايتكم جميعا لهداكم وحملكم على الطريق المستقيم، ولكنه خلق لكم عقولا تدرك، وإرادة توجه. وترككم لاختياركم.',NULL,'385',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1915,'<span data-type="title" id=toc-1931>١٠ </span>- هو الذى أنزل من جهة السماء ماء لكم منه شراب، وبعضه ينبت منه الشجر، وفى هذا الشجر ترسلون أنعامكم لتأكل منه، وتمدكم باللبن واللحوم، والأصواف والأوبار والأشعار.',NULL,'386',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1916,'<span data-type="title" id=toc-1932>١١ </span>- ينبت لكم بالماء الذى ينزل من السماء الزرع الذى نخرج منه الحبوب والزيتون والنخيل والأعناب، وغيرها من كل أنواع الثمرات التى تأكلونها غير ما ذكر، إن فى إيجاد هذه الأشياء لعلامة هادية لقوم ينتفعون بعقولهم ويفكرون فى القدرة التى أوجدتها.',NULL,'386',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1917,'<span data-type="title" id=toc-1933>١٢ </span>- وسخر لكم الليل إذ جعله مهيئا لراحتكم، والنهار إذ جعله مناسبا لسعيكم وحركتكم وأعمالكم، والشمس إذ تمدكم بالدفء والضوء، والقمر لتعرفوا به عدد السنين والحساب، والنجوم مسخرات بأمر اللَّه تهتدوا بها فى الظلمات، إن فى ذلك لعلامات وأدلة لقوم ينتفعون بما وهبهم اللَّه من عقل يدرك.',NULL,'386',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1918,'<span data-type="title" id=toc-1934>١٣ </span>- وبجوار ما خلقه لكم فى السماء وهيأه لمنافعكم، خلق لكم على سطح الأرض كثيرا من أنواع الحيوان والنبات والجماد، وجعل فى جوفها كثيرا من المعادن المختلفة الألوان والأشكال والخواص، وجعل كل ذلك لمنافعكم. إن فى ذلك كله لأدلة واضحة كثيرة لقوم يتدبرون فيها فيتعظون، ويعرفون من خلالها قدرة خالقهم ورحمته بهم.',NULL,'386',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1919,'<span data-type="title" id=toc-1935>١٤ </span>- وهو الذى ذلل البحر وجعله فى خدمتكم لتصطادوا ولتأكلوا منه لحم الأسماك طريا طازجا، وتستخرجوا منه ما تتحلون به كالمرجان واللؤلؤ. وترى - أيها الناظر المتأمل - السفن تجرى فيه شاقة مياهه تحمل الأمتعة والأقوات. سخره اللَّه لذلك لتنتفعوا بما فيه وتطلبوا من فضل اللَّه الرزق عن طريق التجارة وغيرها. ولتشكروه على ما هيَّأه لكم، وذلك لخدمتكم.',NULL,'386',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1920,'<span data-type="title" id=toc-1936>١٥ </span>- وجعل اللَّه فى الأرض جبالا ثابتة تحفظها أن تضطرب، وجعل فيها أنهارا تجرى فيها المياه الصالحة للشرب والزرع، وطرقا ممهدة لتهتدوا بها فى السير إلى مقاصدكم.',NULL,'387',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1921,'<span data-type="title" id=toc-1937>١٦ </span>- وجعل علامات ترشد الناس فى أثناء سيرهم فى الأرض، وهم فى ذلك يسترشدون فى أثناء سيرهم بالنجوم التى أودعها السماء إذا عميت عليهم السبل والتبست معالم الطرق.',NULL,'387',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1922,'<span data-type="title" id=toc-1938>١٧ </span>- هل يستوى فى نظر العقل السليم التسوية بين القادر والعاجز فيجعل من يخلق هذه الأشياء كمن لا يستطيع خلق أى شئ؟ أتعمون - أيها المشركون - عن آثار قدرة اللَّه. فلا تعتبروا وتشكروا عليها اللَّه؟',NULL,'387',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1923,'<span data-type="title" id=toc-1939>١٨ </span>- وإن تحاولوا عد أنعم اللَّه عليكم فلن يمكنكم إحصاؤها، إن اللَّه كثير المغفرة واسع الرحمة، فتوبوا إليه وأخلصوا العبادة له، يغفر لكم ويرحمكم.',NULL,'387',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1924,'<span data-type="title" id=toc-1940>١٩ </span>- واللَّه يدرك بعلمه الشامل ما تخفون وما تظهرون، لا يخفى عليه شئ من سركم وجهركم.',NULL,'387',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1925,'<span data-type="title" id=toc-1941>٢٠ </span>- هذا الخالق المنعم العالم بكل شئ، هو - وحده - المستحق للعبادة، أما الأصنام التى تعبدونها، فهى عاجزة لا تستطيع أن تخلق شيئا، ولو كان ذبابا. . بل هى نفسها مخلوقة ربما صنعتموها بأيديكم.',NULL,'387',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1926,'<span data-type="title" id=toc-1942>٢١ </span>- وهى جمادات ميتة لا حس لها ولا حركة، ولا تدرى متى تكون القيامة والبعث لعابديها، فلا يليق بكم - أيها العقلاء - بعد هذا أن تظنوا أنها تنفعكم فتشركوها مع اللَّه فى العبادة.',NULL,'387',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1927,'<span data-type="title" id=toc-1943>٢٢ </span>- وقد وضح بكل هذه الدلائل أن إلهكم الذى يجب أن تعبدوه وحده إله واحد لا شريك له، ومع ذلك فالذين لا يؤمنون بالبعث والحساب قلوبهم منكرة لوحدانيته، منعهم الاستكبار عن اتباع الحق والخضوع له.',NULL,'387',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1928,'<span data-type="title" id=toc-1944>٢٣ </span>- لا شك أن اللَّه يعلم ما يسرون وما يعلنون من عقائد وأقوال وأفعال، وسيحاسبهم على كل ذلك ويعاقبهم على استكبارهم، لأنه - سبحانه - لا يحب المستكبرين عن سماع الحق والخضوع له.',NULL,'388',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1929,'<span data-type="title" id=toc-1945>٢٤ </span>- وإذا سئل هؤلاء الكفار المستكبرون: أى شئ أنزله ربكم على محمد؟ قالوا فى عناد: هذا الذى يزعم أن اللَّه أنزله عليه ما هو إلا أباطيل وخرافات سطرها السابقون فنقلها وصار يرددها.',NULL,'388',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1930,'<span data-type="title" id=toc-1946>٢٥ </span>- قالوا ذلك، ليصدوا الناس عن اتباع رسول اللَّه، لتكون عاقبة أمرهم أنهم يعذبون يوم القيامة عذاب ضلالهم كاملا، وعذاب بعض الناس الذين خدعوهم وغرورا بهم حتى ضلوا دون علم أو بحث. تنبه - أيها السامع - لقبح ما ارتكب هؤلاء من ذنوب ما أشد عقابهم عليها.',NULL,'388',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1931,'<span data-type="title" id=toc-1947>٢٦ </span>- وقد سبق هؤلاء الكافرين المتكبرين أمثال لهم، دبَّروا المكائد لأنبيائهم، واحتالوا فى إضلال الناس فأبطل اللَّه كيدهم، ودمر بلادهم ونزل بهم عذاب النار فى الدنيا من حيث لا يتوقعون.',NULL,'388',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1932,'<span data-type="title" id=toc-1948>٢٧ </span>- ثم فى الآخرة حيث يبعث الناس ويحاسبون على أعمالهم، ويوقفهم اللَّه موقف الخزى والعار، حين يفضحهم ويظهر ما كانت تخفيه صدورهم، ويقول لهم: أين هؤلاء الذين اتخذتموهم شركاء لى فى العبادة؟ وكنتم تحاربوننى ورسلى فى سبيلهم. أين هم حتى يمدوا لكم العون كما كنتم تزعمون، فلا يستطيعون جوابا، وحينئذ يقول الذين يعلمون الحق من الأنبياء والمؤمنين والملائكة: إن الخزى اليوم والعذاب المسئ واقعان على الجاحدين.',NULL,'388',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1933,'<span data-type="title" id=toc-1949>٢٨ </span>- الخزى على الكافرين الذين استمروا على كفرهم حتى قبضت الملائكة أرواحهم، وهم ظالمون لأنفسهم بالشرك وبارتكاب السوء، واستسلموا بعد طوال العناد إذ علموا حقيقة جرمهم، وقالوا كذبا من شدة دهشتهم: ما كنا فى الدنيا نعمل شيئا من المعاصى، فتقول لهم الملائكة والأنبياء: كلا، إنكم كاذبون، وقد ارتكبتم أفظع المعاصى. واللَّه - سبحانه - محيط بكل صغيرة وكبيرة مما كنتم تعملونه فى دنياكم، فلا يفيدكم إنكاركم.',NULL,'389',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1934,'<span data-type="title" id=toc-1950>٢٩ </span>- ويقال لهم بعد ذلك: مآلكم دخول النار والعذاب فيها عذابا مؤبدا لا ينقطع، وقبحت جهنم دارا ومقاما لكل متكبر على الانقياد إلى الحق والإيمان باللَّه ورسله.',NULL,'389',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1935,'<span data-type="title" id=toc-1951>٣٠ </span>- وقيل للذين آمنوا باللَّه وابتعدوا عما يغضبه من قول أو فعل أو عقيدة: ما الذى أنزله ربكم على رسوله؟ قالوا: أنزل عليه القرآن، فيه خير الدنيا والآخرة للناس جميعا، فكانوا بذلك من المحسنين. واللَّه - سبحانه - يكافئ المحسنين بحياة طيبة فى هذه الحياة الدنيا، ويكافئهم فى الآخرة بما هو خير وأحسن مما نالوه فى الدنيا. ولنعم الدار التى يقيم فيها المتقون فى الآخرة.',NULL,'389',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1936,'<span data-type="title" id=toc-1952>٣١ </span>- وهى جنات ثابتة للإقامة، تجرى من تحت قصورها وأشجارها الأنهار، لهم فيها ما يشاءون من النعيم، ومثل هذا الجزاء الحسن، يجزى اللَّه كل المتقين الذين آمنوا به، واتقوا ما يغضبه، وأحسنوا عملهم.',NULL,'389',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1937,'<span data-type="title" id=toc-1953>٣٢ </span>- وهم الذين تقبض أرواحهم الملائكة، وهم طاهرون من دنس الشرك والمعاصى، وتقول الملائكة تطمينا لهم: أمان من اللَّه لكم، فلا يصيبكم بعد اليوم مكروه، وأبشروا بالجنة تدخلونها بسبب ما قدمتم من أعمال صالحة فى دنياكم.',NULL,'389',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1938,'<span data-type="title" id=toc-1954>٣٣ </span>- هؤلاء هم المتقون الذين استعدوا لآخرتهم، وذلك جزاؤهم. أما المشركون، فإنهم بعنادهم وبقائهم على شركهم، لا ينتظرون إلا الملائكة تقبض أرواحهم، وهم ظالمون لأنفسهم بالشرك وعمل الشر، ويأتيهم عذاب ربك بإهلاكهم جميعاً. ومثل ما فعل هؤلاء الكفار المعاندون، فعل الذين سبقوهم فى ذلك مع أنبيائهم فعاقبهم الله على فعلهم، ولم يكن ظالماً لهم حين عاقبهم، ولكنهم هم الذين ظلموا أنفسهم حين عرضوها لعذاب الله بكفرهم.',NULL,'390',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1939,'<span data-type="title" id=toc-1955>٣٤ </span>- فأصابهم جزاء ما عملوا من سيئات، وأحاط بهم العذاب الذى كانوا ينكرونه ويستهزئون به.',NULL,'390',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1940,'<span data-type="title" id=toc-1956>٣٥ </span>- وقال الذين أشركوا عناداً ومغالطة: لو شاء الله أن نعبده - وحده - ونطيعه فيما يأمر به لما عبدنا غيره، ولما حرَّمنا من عندنا ما لم يحرمه، كالبحيرة والسائبة وهى حجة باطلة يستندون عليها فى كفرهم. وقد احتج بها من سبقوهم من الكفار، بعد ما أرسلنا إليهم رسلنا، فأمروهم بالتوحيد وطاعة الله، ونهوهم عن الشرك وعن تحريم ما حَرَّمه الله، فقامت عليهم الحُجة، وأدى رسلنا ما أمرناهم بتبليغه، وعلينا نحن حسابهم، وليس على الرسل شئ بعد ذلك.',NULL,'390',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1941,'<span data-type="title" id=toc-1957>٣٦ </span>- ولقد بعثنا فى كل أمة رسولا ليقول لهم: اعبدوا الله - وحده - واجتنبوا كل طاغية مفسد، فبلغهم وأرشدهم، ففريق استمع إلى الإرشاد وتقبله، فهداه الله بحسن استعداده إلى الطريق المستقيم، وفريق أعرض عن سماع الحق فانحرف عن سواء السبيل، فأنزل الله به العذاب. وإذا كنتم فى شك من هذا - يا مشركى مكة - فسيروا فى الأرض، قريباً منكم، فانظروا وتأملوا كيف حل بالمكذبين - من عاد وثمود وقوم لوط - عذاب الله، وكيف كانت عاقبة أمرهم خسراناً وهلاكاً؟! .',NULL,'390',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1942,'<span data-type="title" id=toc-1958>٣٧ </span>- إن تكن حريصاً - أيها النبى - على هداية المشركين من قومك، باذلاً معهم أقصى ما فى جهدك، فلا تهلك نفسك حزناً إذا لم يتحقق ما تريد، فقد تحكمت فيهم الشهوات، والله لا يجبر على الهداية من اختاروا الضلال وتمسكوا به، لأنه يتركهم لما اختاروا لأنفسهم، وسيلقون جزاءهم عذاباً عظيماً، ولا يجدون لهم يوم القيامة من ينصرهم ويحميهم من عذاب الله.',NULL,'390',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1943,'<span data-type="title" id=toc-1959>٣٨ </span>- وقد أضاف المشركون إلى شركهم بالله إنكارهم ليوم القيامة، فأقسموا بالله غاية طاقتهم فى القسم، وأكدوا أن الله لا يبعث من يموت وهم كاذبون فى قَسَمِهِمْ، وسيبعثهم الله جميعاً، لأنه أخذ العهد على نفسه بذلك، ولن يخلف الله عهده، ولكن أكثر الناس من الكفار لا يعلمون حكمة الله فى خلْق هذا العالم وأنه لم يخلقه عبثاً، ولا عن حسابه فى الآخرة ومجازاته.',NULL,'391',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1944,'<span data-type="title" id=toc-1960>٣٩ </span>- وأن من عدل الله فى خلقه أن يبعثهم جميعاً بعد موتهم، فيظهر لهم حقائق الأمور التى اختلفوا فيها، ليعلم المؤمنون أنهم على حق، ويعلم الكافرون أنهم كانوا مخطئين فى اتخاذهم شركاء.',NULL,'391',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1945,'<span data-type="title" id=toc-1961>٤٠ </span>- وليس بعث الناس يوم القيامة بعسير علينا حتى يستبعده هؤلاء الكفار، لأننا إذا أردنا شيئاً لا يحتاج إيجاده إلا أن نقول له: كن. فيكون كما نريد.',NULL,'391',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1946,'<span data-type="title" id=toc-1962>٤١ </span>- والمؤمنون الذين هاجروا من ديارهم لوجه الله تعالى، وإخلاصاً لعقيدتهم، من بعد ما وقع عليهم الظلم والعذاب من المشركين، لنُعوّضهم فى الدنيا على إخلاصهم واحتمالهم للعذاب، حياة طيبة حسنة لا تأتى إلا بالجهاد، وسيكون أجرهم يوم القيامة أكبر، ونعيمهم فى الجنة أعظم، لو كان المخالفون لهم يعلمون ذلك لما ظلموهم وظلموا أنفسهم.',NULL,'391',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1947,'<span data-type="title" id=toc-1963>٤٢ </span>- وهؤلاء المهاجرون هم الذين صبروا على ما تحملوه من عذاب فى سبيل عقيدتهم، وفوضوا أمرهم إلى الله - وحده - غير مبالين بما سواه، ومن أجل هذا أحسنا لهم الجزاء.',NULL,'391',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1948,'<span data-type="title" id=toc-1964>٤٣ </span>- وما أرسلنا إلى الأمم السابقة قبل إرسالك إلى أمتك - أيها النبى - إلا رجالاً نوحى إليهم بما نريد تبليغه لهم، ولم نرسل ملائكة كما يريد كفار قومك، فاسألوا - أيها الكافرون - أهل العلم بالكتب السماوية، إن كنتم لا تعلمون ذلك، فستعرفون أن رسل الله جميعاً ما كانوا إلا رجالاً لا ملائكة.',NULL,'391',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1949,'<span data-type="title" id=toc-1965>٤٤ </span>- وقد أيدنا هؤلاء الرسل بالمعجزات والدلائل البينة لصدقهم، وأنزلنا عليهم الكتب تبين لهم شرعهم الذى فيه مصلحتهم، وأنزلنا إليك - أيها النبى - القرآن لتبين للناس ما اشتمل عليه من العقائد والأحكام، وتدعوهم إلى التَّدبر فيه، رجاء أن يتدبروا فيتعظوا ويستقيم أمرهم.',NULL,'392',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1950,'<span data-type="title" id=toc-1966>٤٥ </span>- فكيف يصح بعد كل هذا أن يتمادى المشركون فى عنادهم، ويدبروا المكائد للرسول؟ هل أغراهم حلم الله بهم فاعتقدوا أنهم فى مأمن من عذاب الله، فلا يخسف بهم الأرض كما فعل بقارون؟ أو يأتيهم العذاب فجأة بصاعقة كما فعل بثمود وهم لا يدرون أين نزل.',NULL,'392',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1951,'<span data-type="title" id=toc-1967>٤٦ </span>- أو يهلكهم فى أثناء تنقلهم فى الأرض للتجارة بعيدين عن مساكنهم فلا يستطيعون الإفلات من عقاب الله، لأنه لا يعجزه شئ يريده.',NULL,'392',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1952,'<span data-type="title" id=toc-1968>٤٧ </span>- أو ينزل بهم العذاب فى أنفسهم وأموالهم رويداً رويدا، وهم فى كل لحظة فى عذاب من الخوف منه والترقب لوقوعه، فلا تتمادوا - أيها المشركون - وتغتروا بتأخير عقوبتكم، فقد اقتضت رأفة الله الشاملة ورحمته الواسعة ألا يعاجلكم بالعقوبة فى الدنيا، كى تتفكروا وتتدبروا لأنه - سبحانه - رؤوف رحيم.',NULL,'392',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1953,'<span data-type="title" id=toc-1969>٤٨ </span>- أغفل هؤلاء الكفار عن آيات الله حولهم، ولم ينظروا ويتدبروا فيما خلقه الله من الأشياء القائمة، تنتقل ظلالها وتمتد تارة يميناً وتارة شمالاً، تابعة فى ذلك لحركة الشمس نهاراً والقمر ليلاً، وكل ذلك خاضع لأمر الله، منقاد لأحكام تدبيره. لو تدبر المشركون هذا لعلموا أن خالقه ومدبره هو - وحده - المستحق للعبادة والخضوع، القادر على إهلاكهم لو أراد.',NULL,'392',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1954,'<span data-type="title" id=toc-1970>٤٩ </span>- ولله - وحده - لا لغيره - يخضع وينقاد جميع ما خلقه فى السموات وما دب على الأرض ومشى على ظهرها من مخلوقات، وفى مقدمتهم الملائكة يخضعون له ولا يستكبرون عن طاعته.',NULL,'392',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1955,'<span data-type="title" id=toc-1971>٥٠ </span>- وحالهم أنهم دائماً على خوف من ربهم القادر القاهر، ويفعلون ما يأمرهم به.',NULL,'393',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1956,'<span data-type="title" id=toc-1972>٥١ </span>- وقال الله: لا تعبدوا اثنين، وتجعلوهما إلهين، لأن الإشراك فى العبادة تنافى وحدانية الخلق والتّكون، إنما المعبود بحق إله واحد، فخافونى ولا تخافوا غيرى.',NULL,'393',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1957,'<span data-type="title" id=toc-1973>٥٢ </span>- وله - وحده - ما فى السموات والأرض خلقاً، وملكاً، وعبيداً، فحقه - دون غيره - أن يُعبد ويُحمد، ويُخضع له، وتُرْجى رحمته، ويخاف عذابه.',NULL,'393',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1958,'<span data-type="title" id=toc-1974>٥٣ </span>- وأى شئ جاءكم من النعم فهو من الله - وحده - ثم إذا لحقكم ما يضركم فلا تتضرعوا بأعلى أصواتكم إلا إليه.',NULL,'393',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1959,'<span data-type="title" id=toc-1975>٥٤ </span>- ثم إذا استجاب لدعائكم ورفع ذلك الضر عنكم، نسى بعضكم حق الله عليه من التوحيد وإخلاص العبادة له، فيشركون بخالقهم ومربيهم ويعبدون معه غيره.',NULL,'393',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1960,'<span data-type="title" id=toc-1976>٥٥ </span>- ذلك يحدث منهم لتكون عاقبة أمرهم إنكار فضلنا على ما أعطيناهم، فتمتعوا - أيها الكافرون - بما لا تؤدون حق شكره، فسوف تعلمون عاقبة الكفر.',NULL,'393',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1961,'<span data-type="title" id=toc-1977>٥٦ </span>- ويجعل المشركون لأوثانهم التى يسمونها بغير علم آلهة نصيبا يتقربون بها إليها، من الرزق الذى أعطيناهم إياه من الحرث والأنعام وغيرهما، لأسألكم وعزتى - أيها المشركون - عما كنتم تختلقونه من الكذب وتفترونه من الباطل، وأجازيكم عليه.',NULL,'393',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1962,'<span data-type="title" id=toc-1978>٥٧ </span>- ويجعلون لله ما يكرهون، فيزعمون أن الملائكة بنات، ويعبدونها، تنزه الله عن ذلك، ويجعلون لأنفسهم ما يحبون، وهم الذكور من الأولاد.',NULL,'393',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1963,'<span data-type="title" id=toc-1979>٥٨ </span>- وهم إذا خُبِّر أحدهم بأنه ولدت له أنثى، صار وجهه مسودا من الحزن وهو مملوء غيظاً يكظمه.',NULL,'394',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1964,'<span data-type="title" id=toc-1980>٥٩ </span>- يحاول الاختفاء عن أعين الناس، لئلا يروا كآبته من الألم الذى أصابه من وجود المولود الذى أخبروه به، وتستولى عليه حيرة. أيبقيه حياً مع ما يلحقه من الهوان على ذلك فى زعمه؟! أم يدفنه فى التراب وهو حى حتى يموت تحته؟ تنبه - أيها السامع - لفظاعة عمل هؤلاء. وقبح حكمهم الذى ينسبون فيه لله ما يكرهون أن ينسب إلى أنفسهم.',NULL,'394',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1965,'<span data-type="title" id=toc-1981>٦٠ </span>- الذين لا يؤمنون بالآخرة وما فيها من ثواب وعقاب الحال التى تسوء، وهى الحاجة إلى الأولاد الذكور وكراهة الإناث، ولله الصفة العليا، وهو الغنى عن كل شئ، فلا يحتاج إلى الولد، وهو الغالب القوى الذى لا يحتاج إلى معين.',NULL,'394',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1966,'<span data-type="title" id=toc-1982>٦١ </span>- ولو يعجل الله عقاب الناس بما ارتكبوا من ظلم، ما ترك على ظهر الأرض دابة، ولكنه بحلمه وحكمته يؤخر الظالمين إلى وقت عيَّنه، وهو وقت انتهاء آجالهم، فإذا جاء هذا الوقت لا يتأخرون عنه لحظة كما لا يتقدمون عليه لحظة.',NULL,'394',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1967,'<span data-type="title" id=toc-1983>٦٢ </span>- وينسب المشركون إلى الله ما يكرهون أن ينسب إليهم من البنات والشركة، وتنطق ألسنتهم الكذب، إذ يزعمون مع ذلك أن لهم فى الدنيا الغنى والسلطان الذى يقيهم العذاب، وأن لهم الجنة كذلك. والحق أن لهم النار، وأنهم مسوقون إليها قبل غيرهم.',NULL,'394',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1968,'<span data-type="title" id=toc-1984>٦٣ </span>- تأكد - أيها النبى - أننا أرسلنا رسلا إلى أمم من قبل بمثل ما أرسلناك به إلى الناس جميعاً، فحسَّن لهم الشيطان الكفر والشرك والمعاصى فكذبوا رسلهم، وعصوهم، وصدقوا الشيطان وأطاعوه، فهو متولى أمورهم فى الدنيا يزين لهم ما يضرهم، ولهم فى الآخرة عذاب شديد الألم.',NULL,'395',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1969,'<span data-type="title" id=toc-1985>٦٤ </span>- وما أنزلنا عليك القرآن إلا لتبين به للناس الحق فيما كان موضع خلافهم من الدين، وليكون هداية تامة، ورحمة عامة لقوم يؤمنون بالله وبالكتاب الذى أنزله.',NULL,'395',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1970,'<span data-type="title" id=toc-1986>٦٥ </span>- والله أنزل من السماء ماء يحمله السحاب، فجعل الأرض منبتة فيها حياة، بعد أن كانت قاحلة لا حياة فيها. إن فى ذلك لدليلاً واضحاً على وجود مدبر حكيم.',NULL,'395',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1971,'<span data-type="title" id=toc-1987>٦٦ </span>- وإن لكم - أيها الناس - فى الإبل والبقر والغنم لموعظة تعتبرون بها، وتنتقلون بتدبر عطائها إلى العلم بالصانع المبدع الحكيم، ونسقيكم من بعض ما فى بطونها من بين فضلات الطعام والدم لبناً صافياً سهل التناول للشاربين.',NULL,'395',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1972,'<span data-type="title" id=toc-1988>٦٧ </span>- ومن ثمرات النخيل والأعناب التى أنعمنا بها عليكم ومكناكم منها تتخذون عصيراً مسكراً غير حسن، وطعاماً طيباً حسناً، إن فى ذلك لعلامة دالة على القدرة والرحمة لقوم ينتفعون بعقولهم.',NULL,'395',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1973,'<span data-type="title" id=toc-1989>٦٨ </span>- وألهم ربك - أيها النبى - النحل أسباب حياتها، ووسائل معيشتها، بأن تتخذ من الجبال بيوتاً فى كهوفها، ومن فجوات الشجر، ومن عرائش المنازل والكروم بيوتاً كذلك.',NULL,'395',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1974,'<span data-type="title" id=toc-1990>٦٩ </span>- ثم هداها - سبحانه - للأكل من كل ثمرات الشجر والنبات، وسهَّل لها أن تسلك لذلك طرقاً هيأها لها ربها مذللة سهلة، فيخرج من بطونها شراب مختلف ألوانه فيه شفاء للناس، إن فى ذلك الصنع العجيب لأدلة قوية على وجود صانع قادر حكيم، ينتفع بها قوم يستعملون عقولهم بالتأمل فيفوزون بالسعادة الدائمة.',NULL,'396',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1975,'<span data-type="title" id=toc-1991>٧٠ </span>- والله خلقكم، وقدر لكم آجالا مختلفة، منكم من يتوفاه مبكراً، ومنكم من يبلغ أرذل العمر، فيرجع بذلك إلى حال الضعف، إذ تأخذ حيويته فى الضعف التدريجى، فيقل نشاط الخلايا وتهن العظام والعضلات والأعصاب فتكون عاقبته أن يفقد كل ما عليه. إن الله عليم بأسرار خلقه، قادر على تنفيذ ما يريده.',NULL,'396',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1976,'<span data-type="title" id=toc-1992>٧١ </span>- والله فضَّل بعضكم على بعض فى الرزق. فجعل رزق السيد المالك أفضل من رزق مملوكه، فما الذين كثر رزقهم من السادة بمعطين نصف رزقهم لعبيدهم المملوكين لهم، حتى يصيروا مشاركين لهم فى الرزق على حد المساواة، وإذا كان هؤلاء الكفار لا يرضون أن يشاركهم عبيدهم فى الرزق الذى جاء من عند الله، مع أنهم بشر مثلهم، فكيف يرضون أن يشركوا مع الله بعض مخلوقاته فيما لا يليق إلا به تعالى، وهو استحقاق العبادة؟ فهل تستمر بعد كل هذا بصائر هؤلاء المشركين مطموسة، فيجحدوا نعمة الله عليهم بإشراكهم معه غيره؟ .',NULL,'396',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1977,'<span data-type="title" id=toc-1993>٧٢ </span>- والله جعل لكم من جنس أنفسكم أزواجاً لتسكنوا إليها، وجعل لكم من أزواجكم بنين وأبناء بنين، ورزقكم ما أباحه لكم مما تطيب به نفوسكم. أبعد ذلك يشرك به بعض الناس؟ فيؤمنون بالباطل، ويجحدون نعمة الله المشاهدة، وهى التى تستحق منهم الشكر، وإخلاص العبادة لله.',NULL,'396',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1978,'<span data-type="title" id=toc-1994>٧٣ </span>- ويعبدون غير الله من الأوثان وهى لا تملك أن ترزقهم رزقا - أَىَّ رزقٍ - ولو قليلا سواء كان هذا الرزق آتيا من جهة السماء كالماء، أم خارجاً من الأرض كثمر الأشجار والنبات، ولا تستطيع هذه الآلهة أن تفعل شيئاً من ذلك ولا أقل منه.',NULL,'397',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1979,'<span data-type="title" id=toc-1995>٧٤ </span>- وحيث ثبت لكم عدم نفع غير الله لكم، فلا تذكروا لله تعالى أشباهاً، وتبرروا عبادتها بأقيسة فاسدة، وتشبيهات غير صحيحة تعبدونها معه. إن الله يعلم فساد ما تعملون، وسيجازيكم عليه، وأنتم فى غفلة لا تعلمون سوء مصيركم.',NULL,'397',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1980,'<span data-type="title" id=toc-1996>٧٥ </span>- جعل الله مثلا يوضح فساد ما عليه المشركون، بعبد مملوك لا يقدر على فعل شئ، وبحرّ رزقه الله رزقاً طيباً حلالا، فهو يتصرف فيه، وينفق منه فى السر والجهر. هل يستوى العبيد الذين لا يقدرون على شئ، والأحرار الذين يملكون ويتصرفون فيما يملكون؟ إن الله ملك كل شئ، فهو يتصرف فى ملكوته كما يريد، وغيره لا يملك أىَّ شئ فلا يستحق أن يُعبد ويُحمد، الثناء كله حق لله وحده، والتنزيه له وحده، وله العلو وحده، لأن كل خير صدر عنه، وكل جميل مرده إليه، ولا يفعل هؤلاء ما يفعلون عن علم، وإنما يفعلون ما يفعلون تقليداً لرؤسائهم، بل أكثرهم لا يعلمون، فيضيفون نعمه إلى غيره، ويعبدونه من دونه.',NULL,'397',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1981,'<span data-type="title" id=toc-1997>٧٦ </span>- وجعل الله مثلا آخر هو رجلان: أحدهما أخرس أصم لا يُفْهم غيره: كَلٌّ على من يلى أمره، إذا وجهه سيده إلى جهة ما لا يرجع بفائدة. هل يستوى هذا الرجل مع رجل فصيح قوى السمع. يأمر بالحق والعدل، وهو فى نفسه على طريق قويم لا عوج فيه؟ إن ذلك الأخرس الذى لا يسمع ولا يتكلم ولا يَفْهم ولا يُفهم، هو مثل الأصنام التى عبدوها من دون الله، فإنها لا تسمع ولا تنطق ولا تنفع، فلا يمكن أن تستوى مع السميع العليم الداعى إلى الخير والحق، وإلى الطريق المستقيم.',NULL,'397',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1982,'<span data-type="title" id=toc-1998>٧٧ </span>- ولله - وحده - علم ما غاب عن العباد فى السموات والأرض، وما أمر مجئ يوم القيامة، وبعث الناس فيه، عند الله فى السرعة والسهولة، إلا كرد طرف العين بعد فتحها. بل هو أقرب سرعة من ذلك. إن الله عظيم القدرة لا يعجزه أى شئ.',NULL,'398',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1983,'<span data-type="title" id=toc-1999>٧٨ </span>- والله أخرجكم من بطون أمهاتكم لا تدركون شيئاً مما يحيط بكم، وجعل لكم السمع والأبصار والأفئدة، وسائل للعلم والإدراك، لتؤمنوا به عن طريق العلم، وتشكروه على ما تفضل به عليكم.',NULL,'398',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1984,'<span data-type="title" id=toc-2000>٧٩ </span>- ألم ينظر المشركون إلى الطير مذللات للطيران فى الهواء إلى السماء بما زودها الله به من أجنحة أوسع من جسمها تبسطها وتقبضها، وسخر الهواء لها، فما يمسكهن فى الجو إلا الله بالنظام الذى خلقها عليه؟ إن فى النظر إليها والاعتبار بحكمة الله فى خلقها، لدلالة عظيمة ينتفع بها المستعدون للإيمان.',NULL,'398',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1985,'<span data-type="title" id=toc-2001>٨٠ </span>- والله سبحانه وتعالى هو الذى جعلكم قادرين على إنشاء بيوت لكم تتخذون منها مساكن، وجعل لكم من جلود الإبل والبقر والغنم وغيرها أخبية تسكنون فيها وتنقلونها فى حلكم وترحالكم، وجعلكم تتخذون من صوفها وشعرها ووبرها فرشاً تتمتعون بها فى هذه الدنيا إلى حين آجالكم.',NULL,'398',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1986,'<span data-type="title" id=toc-2002>٨١ </span>- والله جعل لكم من الأشجار التى خلقها وغيرها ظلالا تقيكم شر الحر، وجعل لكم من الجبال كهوفاً ومغارات تسكنون فيها كالبيوت، وجعل لكم ثياباً من الصوف والقطن والكتان وغيرها تصونكم من حرارة الشمس، ودروعاً من الحديد تصونكم من قسوة حروب أعدائكم، كما جعل لكم هذه الأشياء، يتم عليكم نعمته بالدين القيم، لتنقادوا لأمره وتخلصوا عبادتكم له دون غيره.',NULL,'399',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1987,'<span data-type="title" id=toc-2003>٨٢ </span>- فإن أعرض عنك - أيها النبى - الذين تدعوهم إلى الإسلام، فلا تبعة عليك فى إعراضهم، فليس عليك إلا التبليغ الواضح، وقد فعلت.',NULL,'399',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1988,'<span data-type="title" id=toc-2004>٨٣ </span>- إن إعراض هؤلاء الكفار ليس لأنهم يجهلون أن الله - سبحانه - هو مصدر كل النعم عليهم، ولكنهم يعملون عمل من ينكرها حيث لم يشكروه عليها، وأكثرهم جمد على تقليد الآباء فى الكفر بالله، حتى كان أكثرهم هم الجاحدون.',NULL,'399',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1989,'<span data-type="title" id=toc-2005>٨٤ </span>- وحذر - أيها النبى - كل كافر بربه مما سيحصل، يوم نبعث من كل أمة نبيا ليشهد لها أو عليها بما قابلت به رسول ربها، وإذا أراد الكافر منهم أن يعتذر لا يؤذن له فى الاعتذار، ولا يوجد لهم شفيع يمهد لشفاعته، بأن يطلب منهم الرجوع عن سبب غضب الله عليهم، لأن الآخرة ليست دار توبة.',NULL,'399',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1990,'<span data-type="title" id=toc-2006>٨٥ </span>- وإذا رأى الذين ظلموا أنفسهم بالكفر عذاب جهنم، وطلبوا أن يخففه اللَّه عنهم، لا يُجاب لهم طلب، ولا يؤخرون عن دخول جهنم لحظة.',NULL,'399',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1991,'<span data-type="title" id=toc-2007>٨٦ </span>- وإذا رأى الذين أشركوا آلهتهم التى عبدوها وزعموا أنها شركاء للَّه قالوا: يا ربنا هؤلاء شركاؤنا الذين كنا نعبدهم مخطئين، فخفف عنا العذاب بإلقاء بعضه عليهم، فيجيبهم شركاؤهم قائلين: إنكم - أيها المشركون - لكاذبون فى دعواكم أننا شركاء، وأنكم عبدتمونا، إنما عبدتم أهواءكم ولسنا كما زعمتم شركاء.',NULL,'399',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1992,'<span data-type="title" id=toc-2008>٨٧ </span>- حينئذٍ استسلم المشركون للَّه، وخضعوا لقضائه، وغاب عنهم ما كانوا يختلقونه من أن معبوداتهم تشفع لهم، وتدفع العذاب عنهم.',NULL,'400',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1993,'<span data-type="title" id=toc-2009>٨٨ </span>- الذين كفروا ومنعوا غيرهم عن طريق اللَّه، وهو طريق الخير والحق، زدناهم عذابا فوق العذاب الذى استحقوه بالكفر، بسبب ما كانوا يتعمدونه من الإفساد وإضلال العباد.',NULL,'400',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1994,'<span data-type="title" id=toc-2010>٨٩ </span>- وحذر - أيها النبى - كفار قومك مما سيحصل يوم نُحضر من كل أمة شهيداً عليها، هو نبيها الذى يكون بين أبنائها، ليكون ذلك أقطع لعذرها، ونجئ بك - أيها النبى - شهيداً على هؤلاء الذين كذبوك، وعليهم أن يعتبروا من الآن، قد نزلنا القرآن فيه بيان كل شئ من الحق، وفيه الهداية، وفيه الرحمة والبشرى بالنعيم، للذين يذعنون له ويؤمنون به.',NULL,'400',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1995,'<span data-type="title" id=toc-2011>٩٠ </span>- إن اللَّه يأمر عباده بأن يعدلوا فى أقوالهم وأفعالهم، ويقصدوا إلى الأحسن من كل الأمور، فيفضلوه على غيره، كما يأمر بإعطاء الأقارب ما يحتاجون إليه لدعم روابط المحبة بين الأسر، وينهى عن فعل كل خطيئة، خصوصاً الذنوب المفرطة فى القبح، وكل ما تنكره الشرائع والعقول السليمة، كما ينهى عن الاعتداء على الغير، واللَّه - سبحانه - بهذا يذكركم ويوجهكم إلى الصالح من أموركم، لعلكم تتذكرون فضله فى حسن توجيهكم، فتمتثلوا كلامه.',NULL,'400',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1996,'<span data-type="title" id=toc-2012>٩١ </span>- وأوفوا بالعهود التى تقطعونها على أنفسكم، مشهدين اللَّه على الوفاء بها، ما دام الوفاء متسقا مع ما شرعه اللَّه، ولا تنقضوا الأيمان بالحِنث فيها، بعد تأكيدها بذكر اللَّه، وبالعزم وبالتصميم عليها وقد راعيتم فى عهودكم وحلفكم أن اللَّه يكفل وفاءكم، وأن اللَّه رقيب ومطلع عليكم، فكونوا عند عهودكم وأيمانكم، لأن اللَّه - سبحانه - يعلم ما يكون منكم من وفاء وخُلف وبرّ وحِنث، فيجازيكم على ما تفعلون.',NULL,'400',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1997,'<span data-type="title" id=toc-2013>٩٢ </span>- ولا تكونوا فى الحنث فى أيمانكم بعد توكيدها مثل المرأة المجنونة التى تغزل الصوف وتحكم غزله، ثم تعود فتنقضه وتتركه محلولاً، متخذين أيمانكم وسيلة للتغرير والخداع لغيركم، مع أنكم مصرون على الغدر بهم، لأنكم أكثر وأقوى منهم، أو تنوون الانضمام لأعدائهم الأقوى منهم، أو لترجون زيادة القوة بالغدر، وإنما يختبركم اللَّه فإن آثرتم الوفاء كان لكم الغنم فى الدنيا والآخرة، وإن اتجهتم إلى الغدر كان الخسران. وليبين لكم يوم القيامة حقيقة ما كنتم عليه تختلفون عليه فى الدنيا، ويجازيكم حسب أعمالكم.',NULL,'401',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1998,'<span data-type="title" id=toc-2014>٩٣ </span>- ولو شاء اللَّه لجعلكم أمة واحدة فى الجنس واللون والإيمان ليس بينها تخالف، وذلك بخلقكم خلقا آخر. كالملائكة لا اختيار لها، ولكن شاء اللَّه أن تختلفوا فى الأجناس والألوان، وأن يجعل لكم اختيارا، فمن اختار شهوات الدنيا وآثرها على رضا اللَّه تركه وما يريد، ومن أراد رضا اللَّه بالعمل الصالح سهّل له ما أراد. وتأكدوا بعد ذلك أنكم ستسألون جميعا يوم القيامة عما كنتم تعملون فى الدنيا، وتجازون حسب أعمالكم.',NULL,'401',NULL,NULL,'0');
INSERT INTO [b836] VALUES (1999,'<span data-type="title" id=toc-2015>٩٤ </span>- ولا تسلكوا سبيل الغدر، فتتخذوا الأيمان سبيلاً للتغرير والخديعة، فإنه بسبب ذلك تزل الأقدام فتبتعدوا عن المحجة المستقيمة، ويكون فى ذلك إعراض عن سبيل اللَّه فى الوفاء، وتكونون قدوة سيئة فى الغدر، ويرى الناس فيكم صورة مشوهة للإسلام، فيعرضون عنه، وينزل السوء بكم فى الدنيا لعدم الثقة فيكم بسبب صدّكم عن طريق الحق وينزل بكم عذاب مؤلم شديد الإيلام.',NULL,'402',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2000,'<span data-type="title" id=toc-2016>٩٥ </span>- ولا تستبدلوا بالوفاء بالعهود المؤكدة متاع الدنيا، فهو قليل مهما كان كثيرا، لأن ما عند اللَّه من جزاء المحافظين على العهد فى الدنيا، ومن نعيم الآخرة الدائم، خير لكم من كل ما يغريكم بنقض العهود، فتدبروا ذلك وافهموه إن كنتم من أهل العلم والتمييز بين الصالح وغير الصالح، ولا تفعلوا إلا ما فيه صلاح لكم فى دنياكم وأخراكم.',NULL,'402',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2001,'<span data-type="title" id=toc-2017>٩٦ </span>- فإن ما عندكم - أيها الناس - من نعيم ينفد وينتهى مهما طال زمنه، وما عند اللَّه من نعيم الآخرة خالد لا ينقطع، ولنكافئن الذين صبروا على مشاق التكاليف بما وعدناهم به، من حسن الثواب المضاعف على أعمالهم، ينعمون به نعيما دائما فى الآخرة.',NULL,'402',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2002,'<span data-type="title" id=toc-2018>٩٧ </span>- أن من عمل عملاً صالحا فى هذه الدنيا، سواء كان ذكراً أو أنثى مندفعا إلى هذا العمل الصالح بقوة الإيمان بكل ما يجب الإيمان به، فإننا لا بد أن نحييه فى هذه الحياة الدنيا حياة طيبة لا تنغيص فيها، تغمرها القناعة والرضا والصبر على مصائب الدنيا، والشكر على نعم اللَّه فيها، وفى الآخرة لا بد أن نجزى هذا الفريق من الناس حسن الثواب المضاعف على أعمالهم فى الدنيا.',NULL,'402',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2003,'<span data-type="title" id=toc-2019>٩٨ </span>- وإن الذى يحمى النفس من نزعات الهوى هو القرآن، فإذا تدبرت هذا - أيها المؤمن - وأردت أن تحيا بعيداً عن تلاعب الشيطان، وتفوز بطيب الحياة فى الدارين، فإنى أرشدك إلى أمر يعينك على هذا، وهو قراءة القرآن، وإذا أردت قراءة القرآن فاستفتح قراءته بالدعاء الخالص إلى اللَّه أن يمنع عنك وساوس الشيطان المطرود من رحمة اللَّه، أخذ العهد على نفسه أن يغوى الناس ويوقعهم فى عصيان اللَّه.',NULL,'402',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2004,'<span data-type="title" id=toc-2020>٩٩ </span>- فإنك إن فعلت هذا مخلصا للَّه، حماك اللَّه منه، وَبعُدَت عنك وساوسه، إنه ليس له تأثير على الذين عمرت قلوبهم بالإيمان باللَّه، واستمداد العون منه - وحده - والاعتماد عليه.',NULL,'402',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2005,'<span data-type="title" id=toc-2021>١٠٠ </span>- إنما تأثيره وخطره على الذين خلت قلوبهم من التعلق باللَّه وحبه فلم يكن لهم عاصم من تأثيره، فانقادوا له كما ينقاد الصديق لصديقه، حتى أوقعتهم فى أن يُشركوا باللَّه فى العبادة آلهة لا تضر ولا تنفع.',NULL,'403',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2006,'<span data-type="title" id=toc-2022>١٠١ </span>- وإذا جعلنا معجزة لك بدل معجزة مساوية لنبى سابق، فجئناك بالقرآن معجزة، رموك بالافتراء والكذب على اللَّه، واللَّه - وحده - هو العليم علما ليس فوقه علم بما ينزل على الأنبياء من معجزات، ولكن أكثرهم ليسوا من أهل العلم والمعرفة الصادقة.',NULL,'403',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2007,'<span data-type="title" id=toc-2023>١٠٢ </span>- قل لهم مبينا منزلة معجزتك - أيها النبى -: إن القرآن قد نزل علىّ من ربى مع جبريل الروح الطاهر، مقترنا بالحق، مشتملا عليه، ليثبت به قلوب المؤمنين، وليكون هاديا للناس إلى الصواب ومبشرا بالنعيم لكل المسلمين.',NULL,'403',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2008,'<span data-type="title" id=toc-2024>١٠٣ </span>- إننا لنعلم ما يقوله كفار مكة: إنه لا يُعلم محمدا هذا القرآن إلا رجل من البشر نعرفه، هو شاب رومى، وما ينزله عليه ملك من عند اللَّه كما يقول قولهم، وهذا باطل، لأن الشاب الذى يقولونه عنه أنه يعلمك هذا التعليم أعجمى لا يحسن العربية، والقرآن لغة عربية واضحة الفصاحة، إلى حد أنكم عجزتم أيها المكابرون عن محاكاتها، كيف يصح بعد ذلك اتهامكم؟ .',NULL,'403',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2009,'<span data-type="title" id=toc-2025>١٠٤ </span>- إن الذين لا يذعنون لآيات اللَّه التى عجزوا عن محاكاتها، وأصروا مع عجزهم، على كفرهم بها، لا يهديهم اللَّه، ولهم فى الآخرة عذاب شديد بسبب كفرهم وعنادهم.',NULL,'403',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2010,'<span data-type="title" id=toc-2026>١٠٥ </span>- إنما يجرؤ على افتراء الكذب على اللَّه من لا يؤمنون بآيات اللَّه، وأولئك هم - وحدهم - البالغون فى الكذب نهايته، ولست - أيها النبى - من هؤلاء حتى يتهموك بما اتهموك به.',NULL,'403',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2011,'<span data-type="title" id=toc-2027>١٠٦ </span>- إن الذين ينطقون بالكفر بعد الإيمان عليهم غضب من اللَّه إلا من أكره على النطق بكلمة الكفر وهو عامر القلب بالإيمان، فإنه ناج من غضب اللَّه. أما الذين تنشرح قلوبهم للكفر، وتتجاوب مع قلوبهم ألسنتهم، فأولئك عليهم غضب شديد من اللَّه الذى أعد لهم عذابا عظيما فى الآخرة.',NULL,'404',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2012,'<span data-type="title" id=toc-2028>١٠٧ </span>- وذلك الذى استحقوه من غضب اللَّه وعذابه إنما كان بسبب حبهم الشديد لنعيم الدنيا ومتاعها الزائل حتى صرفهم هذا الحب عن الحق، وأعماهم عن الخير، فتركهم اللَّه وما يحبون من الكفر، لأنه قد جرت سنته فى خلقه بترك أمثال هؤلاء، وعدم هدايتهم لفسادهم، وتماديهم فى الباطل.',NULL,'404',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2013,'<span data-type="title" id=toc-2029>١٠٨ </span>- هؤلاء هم الذين طبع اللَّه على قلوبهم، فصارت لا تقبل الحق، وعلى أسماعهم فلم يعودوا يسمعون سماع فهم وتدبر، كأنهم صم، وعلى أبصارهم فلا ترى ما فى الكون أمامهم من عبر ودلالات، وأولئك هم الغارقون فى الغفلة عن الحق، فلا خير فيهم إلا إذا أزالوا الغفلة من عقولهم.',NULL,'404',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2014,'<span data-type="title" id=toc-2030>١٠٩ </span>- وهؤلاء لا شك أنهم - وحدهم - هم الخاسرون لكل خير فى الآخرة.',NULL,'404',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2015,'<span data-type="title" id=toc-2031>١١٠ </span>- ثم اعلم - أيها النبى - أن ربك مُعين وناصر للذين هاجروا من مكة فرارا بدينهم من الضغط، وبأنفسهم من عذاب المشركين، ثم جاهدوا بما يملكون الجهاد به من قول أو فعل، وصبروا على مشاق التكاليف، وعلى ما يلاقونه فى سبيل دينهم، إن ربك من بعد ما تحملوا ذلك لغفور لما حصل منهم إن تابوا، رحيم بهم فلا يؤاخذهم على ما أكرهوا عليه.',NULL,'404',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2016,'<span data-type="title" id=toc-2032>١١١ </span>- اذكر لقومك - أيها النبى - محذراً إياهم يوم، يأتى فيه كل إنسان لا يهمه إلا الدفاع عن نفسه، لا يشغله عنها والد ولا ولد، وهو يوم القيامة، ويوفى اللَّه فيه كل نفس جزاء ما كسبت من أعمال، خيراً كانت أو شراً، ولا يظلم ربك أحدا.',NULL,'405',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2017,'<span data-type="title" id=toc-2033>١١٢ </span>- وجعل اللَّه - سبحانه - لأهل مكة مثلاً يعتبرون به هو قصة قرية من القرى كان أهلها فى أمن من العدو، وطمأنينة من ضيق العيش، يأتيهم رزقهم واسعاً من كل مكان، فجحدوا نعم اللَّه عليهم، ولم يشكروه بطاعته وامتثال أمره، فعاقبهم اللَّه بالمصائب التى أحاطت بهم من كل جانب، وذاقوا مرارة الجوع والخوف بعد الغنى والأمن، وذلك بسبب تماديهم فى الكفر والمعاصى.',NULL,'405',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2018,'<span data-type="title" id=toc-2034>١١٣ </span>- ولقد جاءهم رسول منهم فكان يجب عليهم شكر اللَّه على ذلك، ولكنهم كذبوه عناداً وحسداً، فأخذهم العذاب حال تلبسهم بالظلم، وبسبب هذا الظلم.',NULL,'405',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2019,'<span data-type="title" id=toc-2035>١١٤ </span>- إذا كان المشركون يكفرون بنعم الله فيبدلها بؤسا، فاتجهوا - أيها المؤمنون - إلى الشكر، وكلوا مما رزقكم اللَّه، وجعله حلالا طيبا لكم، ولا تحرموه على أنفسكم، واشكروا نعمة اللَّه عليكم بطاعته - وحده - إن كنتم تخصونه حقا بالعبادة.',NULL,'405',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2020,'<span data-type="title" id=toc-2036>١١٥ </span>- فإن اللَّه لم يحرم عليكم إلا أكل الميتة، والدم الذى ينزل من الحيوان عند ذبحه، ولحم الخنزير، وما ذبح لغير اللَّه، فمن ألجأته ضرورة الجوع إلى تناول شئ مما حرَّمه اللَّه عليكم غير طالب له، ولا يتجاوز فى أكله حد إزالة الضرورة، فإن اللَّه لا يؤاخذه على ذلك، لأنه - سبحانه - غفور لعباده يغفر لهم ما يقعون فيه من أخطاء لا يصرون عليها، رحيم بهم حين منعهم مما يضرهم، وأباح لهم ما يحفظ حياتهم.',NULL,'405',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2021,'<span data-type="title" id=toc-2037>١١٦ </span>- وإذا كان اللَّه قد بيَّن حكم الحلال والحرام، فالتزموا ما بيَّن لكم، ولا تجرءوا على التحليل والتحريم انطلاقاً وراء ألسنتكم، فتقولوا: هذا حلال وهذا حرام، فتكون عاقبة قولكم هذا: أنكم تفترون على اللَّه الكذب، وتنسبون إليه ما لم يقله، إن الذين يفترون على اللَّه الكذب لا يفوزون بخير ولا فلاح.',NULL,'406',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2022,'<span data-type="title" id=toc-2038>١١٧ </span>- وإذا كانوا يجرون بذلك وراء شهواتهم ومنافعهم الدنيوية فإن تمتعهم بها قليل زائل، ولهم فى الآخرة عذاب شديد.',NULL,'406',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2023,'<span data-type="title" id=toc-2039>١١٨ </span>- ولم تحرم إلا على اليهود - وحدهم - ما قصصناه عليك - أيها النبى - من قبل نزول هذه الآيات، وهو كل ذى ظفر، وشحوم البقر والغنم، إلا ما حملت ظهورها، أو الحوايا أو ما اختلط بالعظام. وما ظلمناهم بهذا التحريم، ولكنهم الذين ظلموا أنفسهم، لتسببهم فيه بسبب تماديهم وشراهتهم وعدم وقوفهم عند الحلال.',NULL,'406',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2024,'<span data-type="title" id=toc-2040>١١٩ </span>- ثم إن الذين عملوا السوء تحت تأثير طيش وغفلة عن تدبر العواقب، ثم تابوا من ذلك الذنب، وأصلحوا نفوسهم وأعمالهم، فإن ربك - أيها النبى - يغفر لهم ذنوبهم، لأنه - سبحانه - بعد هذه التوبة كثير التجاوز عن السيئات، واسع الرحمة بالعباد.',NULL,'406',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2025,'<span data-type="title" id=toc-2041>١٢٠ </span>- إن إبراهيم الذى تفخرون به - أيها المشركون أنتم واليهود - كان جامعا لكل الفضائل، بعيدا عما أنتم عليه من باطل، خاضعاً لأمر ربه ولم يكن مثلكم مشركا به.',NULL,'406',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2026,'<span data-type="title" id=toc-2042>١٢١ </span>- وكان شاكرا لنعم ربه عليه، ولهذا كله اختاره اللَّه لحمل رسالته، ووفقه لسلوك طريق الحق المستقيم الموصل للنعيم الدائم.',NULL,'406',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2027,'<span data-type="title" id=toc-2043>١٢٢ </span>- وجعلنا له فى الدنيا ذكرا حسنا على كل لسان، وسيكون قطعا فى الآخرة فى زمرة الصالحين المنعَّمين بجنات اللَّه ورضوانه.',NULL,'406',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2028,'<span data-type="title" id=toc-2044>١٢٣ </span>- ثم أوحينا إليك - أيها النبى - بعد إبراهيم بقرون عديدة، وأمرناك باتباع إبراهيم فيما دعا إليه من التوحيد والفضائل والبعد عن الأديان الباطلة، فإنه لم يكن من الذين يشركون مع اللَّه آلهةً أخرى كما يزعم هؤلاء المشركون.',NULL,'407',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2029,'<span data-type="title" id=toc-2045>١٢٤ </span>- وليس تعظيم يوم الجمعة، وترك تعظيم يوم السبت فى الإسلام مخالفاً لما كان عليه إبراهيم كما يدعى اليهود، فإن تحريم الصيد يوم السبت احتراماً له لم يكن من شريعة إبراهيم، وإنما فرض على اليهود فقط، ومع ذلك لم يحترموه بل خرج بعضهم على هذا التعظيم، وخالفوا أمر ربهم فكيف يعيبون على غيرهم ممن لم يكلف بتعظيمه عدم تعظيمه، مع أنهم - وهم المكلفون بذلك - خرجوا عليه؟ وتأكد - أيها النبى - أن ربك سيقضى بينهم يوم القيامة فى الأمور التى اختلفوا فيها، ويجازى كلا منهم بعمله.',NULL,'407',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2030,'<span data-type="title" id=toc-2046>١٢٥ </span>- أيها النبى: ادع إلى طريق الحق الذى شرعه ربك مع قومك، واسلك فى دعوتهم الطريق الذى يناسب كل واحد منهم، فادع خواصهم ذوى المدارك العالية بالقول الحكيم المناسب لقولهم، وادع عوامهم بما يناسبهم من إيراد المواعظ، وضرب الأمثال التى توجههم إلى الحق، وترشدهم من أقرب طريق مناسب لهم، وجادل أصحاب الملل السابقة من أهل الكتب بالمنطق والقول اللين، والمجادلة الحسنة التى لا يشوبها عنف ولا سِبَاب حتى تتمكن من إقناعهم واستمالتهم. هذا هو الطريق لدعوة الناس إلى اللَّه على اختلاف ميولهم، فاسلك هذا الطريق معهم، واترك أمرهم بعد ذلك إلى ربك الذى يعلم من غرق فى الضلال منهم وابتعد عن طريق النجاة، من سلم طبعه فاهتدى وآمن بما جئت به.',NULL,'407',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2031,'<span data-type="title" id=toc-2047>١٢٦ </span>- وإن أردتم عقاب من يعتدى عليكم - أيها المسلمون - فعاقبوه بمثل ما فعل بكم، ولا تتجاوزوا هذا المثل، وتأكدوا لو صبرتم، ولم تقتصوا لأنفسكم، لكان خيرا لكم فى الدنيا والآخرة، فعاقبوا لأجل الحق، ولا تعاقبوا لأجل أنفسكم.',NULL,'408',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2032,'<span data-type="title" id=toc-2048>١٢٧ </span>- واصبر أنت - أيها النبى - فإن ذلك يسهل عليك كثيرا من مشقات الحياة، ويعالج مشاكلها، ولا تحزن على عدم استجابة قومك لدعوتك، وإيمانهم بك، ولا يضق صدرك من مكرهم وتدابيرهم لخنق دعوتك، فإنك لن يضرك شئ من فعلهم، وقد أديت ما عليك واتقيت ربك.',NULL,'408',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2033,'<span data-type="title" id=toc-2049>١٢٨ </span>- فإن ربك مع الذين اتقوا غضب الله باجتناب نواهيه، وأحسنوا لله أعمالهم بالإقبال على طاعته، يعينهم وينصرهم فى الدنيا ويجزيهم خير الجزاء فى الآخرة.',NULL,'408',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2034,'<span data-type="title" id=toc-2051>١ </span>- تنزيهاً لله عما لا يليق به، وهو الذى سار بعبده محمداً فى جزء من الليل من المسجد الحرام بمكة إلى المسجد الأقصى ببيت المقدس، الذى بارك الله حوله لسكانه فى أقواتهم، لِنُريَه من أدلتنا ما فيه البرهان الكافى على وحدانيتنا وعظم قدرتنا، إن الله - وحده - هو السميع البصير.',NULL,'409',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2035,'<span data-type="title" id=toc-2052>٢ </span>- وأن بيت المقدس كان يسكنه بنو إسرائيل من بعد موسى، حتى أفسدوا فيه، فشُرِّدُوا منه من قبل، مع أننا أعطينا موسى التوراة، وجعلنا فيها هداية لهم، وقلنا لهم: لا تتخذوا غير الله معبوداً تفوضون إليه أموركم.',NULL,'410',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2036,'<span data-type="title" id=toc-2053>٣ </span>- أنتم - أيها الإسرائيليون - ذرية المخلصين الذين كانوا مع نوح فى الفلك بعد إيمانهم، ونجيناهم من الغرق. اجعلوا نوحاً قدوتكم كما جعله أسلافكم، فإنه كان عبداً كثير الشكر لله على نعمته.',NULL,'410',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2037,'<span data-type="title" id=toc-2054>٤ </span>- وأنفذنا بقضائنا إلى بنى إسرائيل فيما كتبناه فى اللوح المكنون أنهم يُفْسدون فى بيت المقدس لا محالة مرتين، فى كل مرة منهما كان الظلم والطغيان، وترك أحكام التوراة، وقتل النبيين، والتعاون على الإثم. وأنه ليبسط سلطانكم وتعلون مستكبرين ظالمين.',NULL,'410',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2038,'<span data-type="title" id=toc-2055>٥ </span>- فإذا جاء وقت عقاب أولاهما سلَّطنا عليكم بسبب إفسادكم عباداً لنا أصحاب بطش شديد، فأخذوا يسيرون فى داخل الديار، لم يتركوا جزءا منها ليقتلوكم، وكان وعد العقاب وعداً لا بد أن يكون.',NULL,'410',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2039,'<span data-type="title" id=toc-2056>٦ </span>- ثم لما استقام أمركم، واهتديتم، وجمعتم شملكم، ورجعتم عن الفساد، رددنا لكم الغلبة على الذين بعثوا عليكم، ورزقناكم أموالاً وبنين، وجعلناكم أكثر مما كنتم عددا.',NULL,'410',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2040,'<span data-type="title" id=toc-2057>٧ </span>- وقلنا لهم: إن أحسنتم فأطعتم الله كان إحسانكم لأنفسكم فى الدنيا والآخرة، وإن أسأتم بالعصيان فإلى أنفسكم تسيئون. فإذا جاء وقت عقاب المرة الآخرة من مَرَّتى إفسادكم فى الأرض، بعثنا عليكم أعداءكم، ليجعلوا آثار المساءة والذلة والكآبة بادية على وجوهكم، وتكون العاقبة أن يدخلوا مسجد بيت المقدس، فيخربوه كما دخلوه وخربوه أول مرة، وليهلكوا ما غلبوا عليه إهلاكاً شديداً.',NULL,'410',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2041,'<span data-type="title" id=toc-2058>٨ </span>- عسى ربكم أن يرحمكم بعد المرة الثانية إن تبتم، وإن عدتم إلى الفساد عدنا إلى العقوبة، وجعلنا جهنم للكافرين سجناً ومحبساً.',NULL,'411',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2042,'<span data-type="title" id=toc-2059>٩ </span>- إن هذا القرآن يرشد الناس للسبيل التى هى أقوم السبل وأسلمها فى الوصول إلى السعادة الحقيقية فى الدنيا، ويبشر المؤمنين بالله ورسوله الذين يُذعنون للحق ويعملون الأعمال الصالحات بالأجر العظيم يوم القيامة.',NULL,'411',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2043,'<span data-type="title" id=toc-2060>١٠ </span>- وأن الذين لا يؤمنون بالآخرة أعددنا لهم فى جهنم عذاباً شديد الألم.',NULL,'411',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2044,'<span data-type="title" id=toc-2061>١١ </span>- وأن فى طبع الإنسان تعجلاً فى الحكم على ما يقع من الناس، وفى أقواله وأفعاله، فهو يسارع بالدعوة إلى الشر مسارعته فى الدعوة إلى الخير، ويسارع فى دعاء الله - تعالى - بأن ينزل الشر على من يبادر بالغضب عليه مسارعته بالدعاء له بالخير.',NULL,'411',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2045,'<span data-type="title" id=toc-2062>١٢ </span>- وجعلنا الليل والنهار بهيئاتهما وتعاقبهما علامتين دالتين على وحدانيتنا وقدرتنا فأزلنا من الليل الضوء فلا يستبان فيه شئ، وكانت علامته ظلاماً لا تسرى فيه الشمس، تلك العلامة الكبرى، وجعلنا النهار مبصراً، وترى فيه الشمس الآية الكبرى لتتجهوا فى ضوء النهار إلى التصرف فى معاشكم، ولتعلموا باختلاف الليل والنهار عدد السنين وحساب الأشهر والأيام، وكل شئ لكم فيه مصلحة بيَّناه لكم بياناً واضحاً، لتقوم عليكم الحُجة بعد تمام النعمة.',NULL,'411',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2046,'<span data-type="title" id=toc-2063>١٣ </span>- وألزمنا كل إنسان عمله لزوم القلادة للعنق، ونخرج له يوم القيامة كتاباً فيه أعماله، يلقاه مفتوحاً، ليسرع فى قراءته.',NULL,'411',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2047,'<span data-type="title" id=toc-2064>١٤ </span>- ويقال له: اقرأ بقدرة الله - ولو لم يكن فى الدنيا قارئاً - كتاب أعمالك تكفيك نفسك اليوم حاسبة ومحصية عليك عملك.',NULL,'411',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2048,'<span data-type="title" id=toc-2065>١٥ </span>- من اتبع طريق الحق فإنما ينفع نفسه، ومن حاد عنه فإنما إثم ضلاله على نفسه، ولا تتحمل نفس مذنبة فوق ذنبها ذنب نفس أخرى، وما صح لنا أن نعذب أحداً على فعل شئ قبل أن نبعث إليه رسولاً من لدنا يهدى إلى الحق ويردع عن الباطل.',NULL,'412',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2049,'<span data-type="title" id=toc-2066>١٦ </span>- وإذا قدَّرنا فى اللوح المحفوظ إهلاك أهل قرية حسب اقتضاء حكمتنا سلَّطنا المترفين فيها فأفسدوا فيها، وخرجوا عن جادة الحق، وأتبعهم غيرهم من غير أن يتبينوا، وبذلك يحق عليها كلها العقاب، فندمرها تدميراً شديداً.',NULL,'412',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2050,'<span data-type="title" id=toc-2067>١٧ </span>- وكثيراً من أهل القرون من بعد نوح أهلكناهم بتمردهم على أنبيائهم، ويكفيك بيان ربك وإعلامه، لأنه العالم بكل شئ علماً دقيقاً كعلم من يبصر، وهو الخبير بذنوب عباده البصير بها، فلا يخفى عليه أفعال أحد من العباد وسيجازيهم بما يستحقون.',NULL,'412',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2051,'<span data-type="title" id=toc-2068>١٨ </span>- مَنْ كان يطلب متاع الدنيا العاجلة ويعمل له متخذا الأسباب، ولا يوقن بميعاد، ولا ينتظر جزاء الدار الآخرة، عجَّلنا له فى الدنيا ما نشاء تعجيله من البسط والسعة، وكان هذا لمن نريد التعجيل له، ثم أعددنا له فى الآخرة جهنم يقاسى حرها، وهو مذموم بما قدم، هالك مطرود من رحمة الله.',NULL,'412',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2052,'<span data-type="title" id=toc-2069>١٩ </span>- ومن أراد بعمله الآخرة، ولها عمل، وهو مصدق بالله وجزائه، فأولئك كان عملهم مقبولاً عند الله ينالون الثواب عليه.',NULL,'412',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2053,'<span data-type="title" id=toc-2070>٢٠ </span>- وإنا نمد كلا الفريقين إذا اتخذوا الأسباب من عطاء ربك فى الدنيا، وما كان عطاء ربك فيها ممنوعاً من أحد، مؤمناً كان أو كافراً، ما داموا قد اتخذوا الأسباب.',NULL,'412',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2054,'<span data-type="title" id=toc-2071>٢١ </span>- انظر بعين الاعتبار كيف فضَّلنا بعض عبادنا على بعض فى المال والحياة والسعة، إذا اتخذوا أسباب ذلك فى الدنيا لحكمة نعلمها، وأن تفاوتهم فى الدار الآخرة أكبر درجات من تفاوتهم فى الدنيا، فينبغى الاعتناء بها، فالآخرة هى التى تكون فيها الرفعة الحقيقية والتفاضل الحقيقى.',NULL,'413',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2055,'<span data-type="title" id=toc-2072>٢٢ </span>- لا تجعل - أيها المكلف - مع الله شريكاً فتصير موصوماً بالإهانة، ويكون الخذلان مكتوباً عليك.',NULL,'413',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2056,'<span data-type="title" id=toc-2073>٢٣ </span>- وحكم ربك بألا تعبدوا إلا إياه، وبأن تبروا الوالدين براً تاماً، وإذا بلغ الوالدان أو أحدهما عندك - أيها المخاطب - حال الضعف وصارا فى آخر العمر فلا تتأفف لما يصدر منهما بصوت يدل على الضجر، ولا تزجرهما، وقل لهما قولا جميلا ليِّنا فيه إحسان وتكريم لهما.',NULL,'413',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2057,'<span data-type="title" id=toc-2074>٢٤ </span>- وَأَلِنْ لهما جانبك وتواضع لهما وكن شفيقاً عليهما، وقل فى شأنهما: رب ارحمهما كما رحمانى حين ربيانى صغيرا.',NULL,'413',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2058,'<span data-type="title" id=toc-2075>٢٥ </span>- ربكم - أيها الناس - أعلم منكم بما فى ضمائركم، ويحاسبكم عليه بالثواب أو العقاب، فإن تكونوا قاصدين الصلاح فاعلين له ثم كانت منكم هفوة ثم أنبتم إلى الله فإن الله - سبحانه - يغفر لكم، لأنه دائم المغفرة للراجعين إليه.',NULL,'413',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2059,'<span data-type="title" id=toc-2076>٢٦ </span>- وأعط ذا القربى حقه من البر والصلة، وذا الحاجة المسكين، والمسافر الذى انقطع عن ماله، حقهما من الزكاة والصدقة، ولا تبعثر مالك فى غير المصلحة تبذيراً كثيراً.',NULL,'413',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2060,'<span data-type="title" id=toc-2077>٢٧ </span>- لأن المبذرين كانوا قرناء الشياطين، يقبلون وسوستهم حين يسخِّرونهم للفساد والإنفاق فى الباطل، ودأب الشيطان أنه يكفر بنعمة ربه دائماً، وصاحبه مثله.',NULL,'413',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2061,'<span data-type="title" id=toc-2078>٢٨ </span>- وإن أرغمتك أحوالك المالية على الإعراض عن هؤلاء المذكورين، فلم تعطهم لعدم وجود ما تعطيهم فى الحال مع رجاء أن يفتح الله عليك به، فقل لهم قولا حسناً يؤملهم فيك.',NULL,'413',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2062,'<span data-type="title" id=toc-2079>٢٩ </span>- ولا تمسك يدك عن الإنفاق فى الخير، وتجعلها كأنها مربوطة فى عنقك بِغِلٍّ من الحديد لا تقدر على مدها، ولا تبسطها كل البسط بالإسراف فى الإنفاق، فتصير مذموماً على الإمساك نادماً أو منقطعاً لا شئ عندك بسبب التبذير والإسراف.',NULL,'414',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2063,'<span data-type="title" id=toc-2080>٣٠ </span>- إن ربك يوسع الرزق لمن يشاء من عباده ويضيقه على من يشاء منهم، لأنه خبير بطبائعهم بصير بحوائجهم، فهو يعطى كلا منهم ما يتفق مع الحكمة إن اتخذ الأسباب.',NULL,'414',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2064,'<span data-type="title" id=toc-2081>٣١ </span>- وإذا كان أمر الأرزاق بيد الله فلا يجوز أن تقتلوا أولادكم خوف فقر متوقع، لأنَّا نحن ضامنون رزقهم ورزقكم، إن قتلهم كان إثماً عظيماً.',NULL,'414',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2065,'<span data-type="title" id=toc-2082>٣٢ </span>- ولا تقربوا الزنى بمباشرة أسبابه ودواعيه، لأنه رذيلة واضحة القبح، وبئس طريقاً طريقه.',NULL,'414',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2066,'<span data-type="title" id=toc-2083>٣٣ </span>- ولا تقتلوا النفس التى حرَّم الله قتلها إلا قتلا يكون للحق، بأن تكون النفس مستحقة للقتل قصاصاً أو عقوبة، ومن قتل مظلوماً فقد جعلنا لأقرب قرابته سلطاناً على القاتل بطلب القصاص من القاضى، فلا يجاوز الحد فى القتل، بأن يقتل غير القاتل، أو يقتل اثنين بواحد، فإن الله نصره وأوجب له القصاص والدية، فلا يصح أن يتجاوز الحد.',NULL,'414',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2067,'<span data-type="title" id=toc-2084>٣٤ </span>- ولا تتصرفوا فى مال اليتيم إلا بالطريقة التى هى أحسن الطرق لتنميته وتثميره، واستمروا على ذلك حتى يبلغ رشده، وإذا بلغ فسلموه له، وحافظوا على كل عهد التزمتموه، فإن الله سيسأل ناقض العهد عن نقضه ويحاسبه عليه.',NULL,'414',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2068,'<span data-type="title" id=toc-2085>٣٥ </span>- وأوفوا الكيل إذا كلتم للمشترى، وزنوا له بالميزان العدل، فإن إيفاء الكيل والوزن خير لكم فى الدنيا، لأنه يرغب الناس فى معاملتكم، وأجمل عاقبة فى الآخرة.',NULL,'414',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2069,'<span data-type="title" id=toc-2086>٣٦ </span>- ولا تتبع - أيها المرء - ما لا علم لك به من قول أو فعل، فلا تقل: سمعت، وأنت لم تسمع، أو علمت، وأنت لم تعلم، فإن نِعَمَ السمع والبصر والقلب يسأل صاحبها عما يفعل بكل منها يوم القيامة.',NULL,'415',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2070,'<span data-type="title" id=toc-2087>٣٧ </span>- ولا تمش فى الأرض متكبراً مختالاً، فإنك مهما فعلت فلن تخرق الأرض بشدة وطأتك، ولن تبلغ مهما تطاولت أن تحاذى بطولك قمم الجبال.',NULL,'415',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2071,'<span data-type="title" id=toc-2088>٣٨ </span>- كل ذلك المذكور من الوصايا، كان القبيح منه من المنهيات مكروهاً مبغوضاً عند ربك.',NULL,'415',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2072,'<span data-type="title" id=toc-2089>٣٩ </span>- وهو مما أوحاه إليك ربك من معرفة الحق بذاته، والخير للعمل به، ولا تجعل مع الله إلهاً غيره فتلقى فى جهنم ملوماً عند نفسك، وعند غيرك هالكاً مطروداً من رحمة ربك.',NULL,'415',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2073,'<span data-type="title" id=toc-2090>٤٠ </span>- أنكر - سبحانه - على من قالوا: الملائكة بنات الله، فقال: أفضَّلكم ربكم على نفسه، فخصكم بأقوى الأولاد، وهم البنون، واتخذ هو لنفسه من الملائكة بنات بزعمكم؟ إنكم فى قولكم هذا تفترون بهتاناً عظيماً.',NULL,'415',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2074,'<span data-type="title" id=toc-2091>٤١ </span>- لقد بيَّنا فى هذا القرآن أحسن بيان ضروباً من الأمثال والمواعظ والأحكام، ليتعظ هؤلاء المشركون، ولكنهم لتحجر قلوبهم لا يزيدهم ذلك التَّبيين إلا شروداً عن الحق.',NULL,'415',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2075,'<span data-type="title" id=toc-2092>٤٢ </span>- قل - أيها النبى - إظهاراً لإبطال زعمهم الشركاء لله: لو كان مع الله آلهة فى الوجود كما يقولون لطلب هؤلاء الآلهة طريقاً يصلون منه إلى صاحب الملك المطلق لينازعوه عليه.',NULL,'415',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2076,'<span data-type="title" id=toc-2093>٤٣ </span>- تنزه الله تنزهاً لائقاً به، وتعالى جل شأنه عما يزعمون من أنه معه آلهة.',NULL,'415',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2077,'<span data-type="title" id=toc-2094>٤٤ </span>- إن السموات السبع والأرض ومن فيهن من المخلوقات تنزهه وتقدسه، وتدل بإتقان صنعها على تنزه الله - سبحانه - عن كل نقص وكمال ملكه، وأنه لا شريك له، وما من شئ من المخلوقات فى ملكه الواسع إلا ينزهه كذلك مع الثناء عليه، ولكن الكافرين لا يفهمون هذه الأدلة لاستيلاء الغفلة على قلوبهم، وكان الله حليماً عليهم غفوراً لمن تاب فلم يعاجلهم بالعقوبة.',NULL,'416',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2078,'<span data-type="title" id=toc-2095>٤٥ </span>- وإذا قرأت - أيها النبى - القرآن الناطق بدلائل الحق جعلنا بينك وبين الذين لا يؤمنون بالبعث والجزاء حين إرادة الفتك بك حجاباً ساتراً لك عنهم، فلا يرونك.',NULL,'416',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2079,'<span data-type="title" id=toc-2096>٤٦ </span>- وجعلنا بمقتضى حكمتك فى الإضلال والهداية على قلوبهم أغطية، كراهة أن يفهموا القرآن على حقيقته، وفى آذانهم صمماً فلا يسمعونه سماع انتفاع، لأنهم أسرفوا فى العناد والمكابرة، وإذا ذكرت ربك فى القرآن منفرداً عن ذكر آلهتهم رجعوا على أعقابهم نافرين عن استماعه.',NULL,'416',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2080,'<span data-type="title" id=toc-2097>٤٧ </span>- نحن أعلم بما يستمعون القرآن متلبسين به من الاستهزاء والسخرية حين استماعهم إليك، وهم ذوو مسارة بما ذكر، وذلك قول الظالمين لغيرهم فى مسارتهم: إن اتبعتم فأنتم لا تتبعون إلا رجلا مغلوباً على عقله.',NULL,'416',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2081,'<span data-type="title" id=toc-2098>٤٨ </span>- انظر كيف ذكر لك الأشباه فشبهوك بالمسحور، والكاهن، والشاعر، فضَلُّوا بذلك عن منهاج الحجة فلا يستطيعون طريقاً إلى الطعن يمكن قبوله، أو فضلوا بذلك عن الهدى فلا يجدون طريقاً إليه.',NULL,'416',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2082,'<span data-type="title" id=toc-2099>٤٩ </span>- قال المنكرون للبعث: أنبعث إذا صرنا عظاماً نخرة، وقطعاً متفرقة، فنكون خلْقاً جديداً فيه حياة؟ إن هذا ما لا يدخل العقول.',NULL,'416',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2083,'<span data-type="title" id=toc-2100>٥٠ </span>- فقل لهم - أيها النبى -: لو كنتم حجارة لا تقبل الحياة بحال، أو حديداً وهو أصلب من الحجارة.',NULL,'416',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2084,'<span data-type="title" id=toc-2101>٥١ </span>- أو خلقاً آخر غيرهما مما تنكر قلوبكم قبوله الحياة لبعثتم، فسيقولون - مستبعدين -: من يعيدنا؟ فقل لهم: يعيدكم الله الذى أوجدكم أول مرة، فسيحركون إليك رؤوسهم تعجباً ويقولون استهزاء: متى البعث الذى تعدُنا به؟ فقل لهم: أرجو أن يكون قريباً.',NULL,'417',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2085,'<span data-type="title" id=toc-2102>٥٢ </span>- وسيكون يوم يبعثكم الله فيه من قبوركم فتبعثون حامدين ربكم على كمال قدرته وتظنون أنكم ما لبثتم فى قبوركم إلا زمناً قليلا، تستقصرون المدة الطويلة فى جنب ما أنتم قادمون عليه.',NULL,'417',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2086,'<span data-type="title" id=toc-2103>٥٣ </span>- وقل - يا أيها النبى - لعبادى المؤمنين: أن يقولوا عند محاربتهم المشركين العبارات التى هى أحسن للإقناع، ويتركوا الكلام الخشن الذى يتسبب عنه الشر والفساد، فإن الشيطان يفسد بين المؤمنين والكافرين، لأنه دائماً عدو للإنسان بيِّن العداوة.',NULL,'417',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2087,'<span data-type="title" id=toc-2104>٥٤ </span>- ربكم أعلم بعاقبة أمركم إن يشأ يرحمكم بالتوفيق للإيمان، أو إن يشأ يعذبكم بعدمه، وما أرسلناك موكولا إليك أمرهم فتجبرهم على الإيمان، وإنما أرسلناك بشيراً للمصدقين ونذيراً للمكذبين، فدارهم، ومُرْ أصحابك بالاحتمال منهم.',NULL,'417',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2088,'<span data-type="title" id=toc-2105>٥٥ </span>- وربك أعلم بكل من فى السموات والأرض وبأحوالهم فيختار منهم لنبوته من يشاء، وقد اختارك لرسالته فلا يصح أن يستكثروا عليك النبوة، وهؤلاء الأنبياء ليسوا سواء فى الفضل عنده - جل شأنه - بل بعضهم أفضل من بعض، ولقد فضل بعض النبيين على بعض بالمعجزات وكثرة التابعين، لا بالملك، فَفَضَّل داود أنه أوتى الزبور، لا لأنه أوتى الملك. فلا عجب أن تنال الفضل العظيم بما أوتيت من القرآن.',NULL,'417',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2089,'<span data-type="title" id=toc-2106>٥٦ </span>- قل لهؤلاء الذين يعبدون المخلوقين، ويزعمونهم آلهة من دون الله: ادعوا من تعبدونه إذا نزلت بكم شدة، أو خفتم نزولها، وسلوهم فى شأنها، فلن تجدوا منهم كشفاً لضركم، ولا تحويلا له عنكم.',NULL,'418',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2090,'<span data-type="title" id=toc-2107>٥٧ </span>- وإن هؤلاء المخلوقين الذين يدعوهم من يعبدهم يعبدون الله، ويطلبون الدرجة والمنزلة عنده بالطاعة، ويحرص كل منهم أن يكون أقرب إلى الله، ويطمعون فى رحمته، ويرهبون عذابه، إن عذاب الله ينبغى أن يحذر ويخاف!! .',NULL,'418',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2091,'<span data-type="title" id=toc-2108>٥٨ </span>- وقد جرت سنتنا أن نهلك كل قرية ظالمة بمن فيها، أو نعذب أهلها عذاباً شديداً بالقتل وغيره، فليحذر ذلك قومك، فقد جرى بذلك قضاؤنا، وسُطِّر فى كتابنا.',NULL,'418',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2092,'<span data-type="title" id=toc-2109>٥٩ </span>- لقد اقترح عليك قومك أن تأتيهم بالآيات والمعجزات، ولم يقنعوا بما آتاهم مما يقنع ذوى الألباب، وقد جرت سنتنا مع من يقترح الآيات ثم يجاب إليها ولا يؤمن بها أن نستأصله بالعذاب كما فعلنا بالأولين. ومنهم ثمود، إذ اقترحوا آيات، فكانت الناقة معجزة مضيئة نيرة واضحة مجلية للشك والريب فكفروا بها، فكان ما كان من أمرهم، وكان من حكمة الله ألا يجيب قومك إلى ما طلبوا خشية أن يكفروا بها، ويرجى منهم مَنْ يؤمن أو يلد مَنْ يؤمن. والآيات إنما نرسل بها إلى الناس تخويفاً وإرهاباً.',NULL,'418',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2093,'<span data-type="title" id=toc-2110>٦٠ </span>- واذكر - أيها النبى - حين قلنا لك: إن ربك أحاط بالناس، فهم فى قبضة قدرته، فبلِّغهم ولا تخف أحداً فهو يعصمك منهم، وما جعلنا ما عاينته ليلة الإسراء من العجائب إلا امتحاناً واختباراً للناس، يزداد به إيمان المؤمن وكفر الكافر، وما جعلنا الشجرة المذمومة فى القرآن - وهى شجرة الزَّقوم التى تنبت فى أصل الجحيم - إلا اختباراً لهم أيضاً، إذ قالوا: النار تحرق الشجر، فكيف تنبته؟ ونخوفهم بها، فما يزيدهم تخويفنا إلا تجاوزا للحد الكبير.',NULL,'418',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2094,'<span data-type="title" id=toc-2111>٦١ </span>- وأن الله ليذكر بأصل الخلق والعداوة بين آدم وإبليس، إذ قال للملائكة: اسجدوا لآدم سجود تحية وتكريم بالانحناء، فسجدوا على الفور، إلا إبليس امتنع وقال منكراً: كيف أسجد لمن خلقته من طين، وأنا من نار، فأنا خير منه.',NULL,'419',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2095,'<span data-type="title" id=toc-2112>٦٢ </span>- قال إبليس: أخبرنى يا رب عن هذا الذى كرمته علىَّ، بأن أمرتنى بالسجود له. لِمَ كرَّمته علىَّ وأنا خير منه؟ وعزتك لئن أخرتنى حياً إلى يوم القيامة لأهلكن ذريته بالإغواء، إلا قليلا منهم ممن عصمته وحفظته.',NULL,'419',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2096,'<span data-type="title" id=toc-2113>٦٣ </span>- قال له المولى - تهديداً واستدراجاً -: امض لشأنك الذى اخترته لنفسك، فمن أطاعك من ذرية آدم فإن جهنم جزاؤك وجزاؤهم جزاء وافراً كاملا.',NULL,'419',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2097,'<span data-type="title" id=toc-2114>٦٤ </span>- واستخفَّ واستنزل بدعائك إلى معصية الله من استعطت منهم، وأفرغ جهدك فى جميع أنواع الإغراء، وشاركهم فى كسب الأموال من الحرام وصرفها فى الحرام، وتكفير الأولاد وإغرائهم على الإفساد، وعِدْهم المواعيد الباطلة كشفاعة آلهتهم، والكرامة عند الله بأنسابهم، وما يعد الشيطان أتباعه إلا بالتغرير والتمويه.',NULL,'419',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2098,'<span data-type="title" id=toc-2115>٦٥ </span>- أما عبادى المخلصون لى فليس لك على إغوائهم قدرة، لتوكلهم على ربهم، وكفى به ناصراً يستمدون منه العون فى الخلاص منك.',NULL,'419',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2099,'<span data-type="title" id=toc-2116>٦٦ </span>- ربكم هو - وحده - الذى يجرى لكم السفن فى البحر، لتطلبوا من فضله الأرباح بالتجارة وغيرها. إنه دائم الرحمة بكم.',NULL,'419',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2100,'<span data-type="title" id=toc-2117>٦٧ </span>- وإذا أصابكم الأذى وتعرَّضتم للمخاطر فى البحر، غاب عنكم كل من تدعونه فى حوائجكم من الأصنام، إلا الله - وحده - فإنكم لا تذكرون سواه، فلما نجَّاكم من الغرق، وأخرجكم إلى البر، أعرضتم عن توحيده وكفرتم النعمة، وشأن الإنسان دائماً جحد النعمة.',NULL,'419',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2101,'<span data-type="title" id=toc-2118>٦٨ </span>- وإذا نجَّوتكم بخروجكم إلى البر أفأمنتم من عذاب الله؟ كلا إن شاء قلب بكم جانباً من البر فهلكتم تحته، وإن شاء أرسل عليكم ريحاً شديدة ترميكم بالحصى والحجر، فلا تجدون حافظاً مما يصيبكم.',NULL,'420',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2102,'<span data-type="title" id=toc-2119>٦٩ </span>- أم أمنتم أن يعيدكم ربكم فى البحر مرة أخرى، فيرسل عليكم قاصفاً من الريح يكسر فلككم؟ فيغرقكم بسبب جحودكم نعمته حين أنجاكم أولا، ثم لا تجدوا لكم علينا من يطالبنا بما فعلنا انتصاراً لكم.',NULL,'420',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2103,'<span data-type="title" id=toc-2120>٧٠ </span>- ولقد كرمنا أولاد آدم بحسن القوام والنطق وتخير الأشياء، وأعطيناهم الكرامة والعزة إن أطاعوا، وحملناهم فى البر على الدواب، وفى البحر على السفن، ورزقناهم من المستلذات، وفضلناهم على كثير من المخلوقات بالعقل والتفكير تفضيلا عظيماً.',NULL,'420',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2104,'<span data-type="title" id=toc-2121>٧١ </span>- واذكر - أيها النبى - لقومك يوم ندعو كل جماعة بشعارهم الذى يعرفون به، أو زعيمهم من رئيس اتبعوه، أو نبى، أو كتاب، فيقال: يا أهل موسى، يا أهل القرآن، وهكذا ليتسلموا كتب أعمالهم، فمن أعطى كتاب أعماله بيمينه - وهم السعداء - فأولئك يقرأون كتابهم مبتهجين ولا ينقصون من أجورهم أدنى شئ.',NULL,'420',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2105,'<span data-type="title" id=toc-2122>٧٢ </span>- وأما الفريق الآخر فيغمه ما يرى، وتسد عليه مسالك النجاة، ويعمى عن كشف ضره، كما كان أعمى فى الدنيا عن طريق الحق والرشاد، ومن كان فى الدنيا أعمى فهو أشد فى الآخرة وأبعد عن سبيل الخير.',NULL,'420',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2106,'<span data-type="title" id=toc-2123>٧٣ </span>- وإن المشركين يتفننون فى محاولة صرفك عن القرآن لتطلب غيره من المعجزات، وتكون كالمفترى علينا، وحينئذ يتخذونك صاحباً لهم، وإن هذه المحاولات قد تكررت وكثرت، وكان من شأنها أن تقربك مما يريدون ولكنك رسولنا الأمين.',NULL,'420',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2107,'<span data-type="title" id=toc-2124>٧٤ </span>- وقد شملك لطفنا فصرفناك عن الاستجابة لهم، وثبتناك على الحق، ولولا ذلك لأوشكت أن تميل إلى استجابتهم طمعاً فى أن يكمل إيمانهم يوماً إذا دخلوا فى أوائل الإسلام.',NULL,'420',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2108,'<span data-type="title" id=toc-2125>٧٥ </span>- ولو قاربت الركون إليهم لجمعنا عليك عذاب الدنيا وضاعفناه، وعذاب الآخرة وضاعفناه، ثم لا تجد لك نصيراً علينا يمنع عنك العذاب، ولكن لا يكون ذلك أبدا لأنه ممتنع على رسولنا الأمين.',NULL,'421',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2109,'<span data-type="title" id=toc-2126>٧٦ </span>- ولقد حاول كفار مكة - وكادوا أن يزعجوك من أرض مكة بعداوتهم ومكرهم - ليخرجوك منها، ولو تحقق منهم ذلك لا يبقون بعد خروجك منها إلا زمناً قليلا، ثم يغلبون على أمرهم وتكون الكلمة لله.',NULL,'421',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2110,'<span data-type="title" id=toc-2127>٧٧ </span>- وذلك كطريقنا فى الرسل قبلك من إهلاك من أخرجوا نبيهم، ولن تجد لطريقنا تبديلا.',NULL,'421',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2111,'<span data-type="title" id=toc-2128>٧٨ </span>- أقم الصلاة المفروضة من أول زوال الشمس من وسط السماء نحو الغرب إلى ظلمة الليل، وهى صلوات الظهر والعصر والمغرب والعشاء، وأقم صلاة الفجر التى تشهدها الملائكة.',NULL,'421',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2112,'<span data-type="title" id=toc-2129>٧٩ </span>- وتيقظ من نومك فى بعض الليل فتهجد بالصلاة عبادة زائدة على الصلوات الخمس خاصة بك، رجاء أن يقيمك ربك يوم القيامة مقاماً يحمدك فيه الخلائق.',NULL,'421',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2113,'<span data-type="title" id=toc-2130>٨٠ </span>- وقل: يا رب أدخلنى إدخالا مرضياً كريماً فى كل ما أدخل فيه من أمر أو مكان، وأخرجنى منه إخراجاً مرضياً كريماً، واجعل من فضلك قوة تنصرنى بها على أعدائى.',NULL,'421',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2114,'<span data-type="title" id=toc-2131>٨١ </span>- وقل منذراً قومك - المشركين -: جاء الحق من التوحيد والدين الصحيح والعدل، وذهب الباطل والشرك والدين الفاسد، إن الباطل مضمحل زائل دائماً.',NULL,'421',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2115,'<span data-type="title" id=toc-2132>٨٢ </span>- وكيف لا يقوى الحق ونحن ننزل من القرآن ما هو شفاء لما فى الصدور من الشك والريب، وسبب رحمة لمن آمن به، ولا يزيد الظالمين إلا خسارا لكفرهم به.',NULL,'421',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2116,'<span data-type="title" id=toc-2133>٨٣ </span>- وإن فى طبع الإنسان الغرور والقنوط، فإذا أنعمنا عليه بالصحة والسعة، أعرض عن ذكرنا ودعائنا، وبَعُدَ عنا بنفسه تكبراً وتعاظماً، وإذا مسه الشر كالمرض والفقر، كان شديد القنوط من رحمة الله.',NULL,'422',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2117,'<span data-type="title" id=toc-2134>٨٤ </span>- قل - أيها النبى - لكفار قريش - رغبة عن إثارة الشر والجدال -: كل منا ومنكم يعمل ويسير على طريقته، فربكم عليم علماً ليس فوقه علم بمن هو أوضح طريقاً واتباعاً للحق فيؤتيه أجره موفوراً، ومن هو اضل سبيلا فيعاقبه بما يستحق.',NULL,'422',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2118,'<span data-type="title" id=toc-2135>٨٥ </span>- ويسألك - يا محمد - قومك - بإيعاز من اليهود - عن حقيقة الروح، قل: الروح من علم ربى الذى استأثر به، وما أوتيتم من العلم إلا شيئاً قليلاً فى جانب علم الله تعالى.',NULL,'422',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2119,'<span data-type="title" id=toc-2136>٨٦ </span>- ولئن أردنا أن نمحو من صدرك القرآن الذى أوحينا إليك لفعلنا ثم لا تجد من يقوم لك وينصرك.',NULL,'422',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2120,'<span data-type="title" id=toc-2137>٨٧ </span>- ولكن أبقيناه رحمة من ربك لأن فضله فى هذه المعجزة كان عليك عظيماً.',NULL,'422',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2121,'<span data-type="title" id=toc-2138>٨٨ </span>- قل لهم متحدياً: أن يأتوا بمثله وإنهم ليعجزون، ولئن اجتمعت الإنس والجن وتعاونوا على أن يأتوا بمثل هذا القرآن فى نظمه ومعانيه، لا يستطيعون، ولو كانوا متعاونين بعضهم يظاهر بعضا.',NULL,'422',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2122,'<span data-type="title" id=toc-2139>٨٩ </span>- ولقد نوعنا مناهج البيان بوجوه مختلفة للناس فى هذا القرآن من كل معنى هو كالمثل فى غرابته فأبى أكثر الناس إلا الجحود والإنكار.',NULL,'422',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2123,'<span data-type="title" id=toc-2140>٩٠ </span>- ولما ظهر إعجاز القرآن ولزمتهم الحُجة، اقترحوا الآيات والمعجزات، فعل المحجوج المبهوت المتحير، فقالوا: لن نؤمن حتى تفجر لنا من أرض مكة عيْناً لا ينقطع ماؤها.',NULL,'422',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2124,'<span data-type="title" id=toc-2141>٩١ </span>- أو يكون لك بمكة بستان من نخيل وعنب فتفجر الأنهار وسطه تفجيراً كثيراً.',NULL,'423',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2125,'<span data-type="title" id=toc-2142>٩٢ </span>- أو تسقط السماء فوق رؤوسنا قطعاً كما زعمت أن الله توعدنا بذلك، أو تأتى بالله والملائكة نقابلهم معاينة ومواجهة.',NULL,'423',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2126,'<span data-type="title" id=toc-2143>٩٣ </span>- أو يكون لك بيت من زخرف من ذهب، أو تصعد فى السماء ولن نصدقك فى هذه الحال إلا إذا جئتنا بكتاب من الله يقرر فيه صدقك نقرؤه، قل لهم: أنزه ربى عن أن يتحكم فيه أحد، أو يشاركه فى قدرته، ما كنت إلا بشراً كسائر الرسل، ولم يأتوا قومهم بآية إلا بإذن الله.',NULL,'423',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2127,'<span data-type="title" id=toc-2144>٩٤ </span>- وما منع مشركى مكة أن يذعنوا للحق حين جاءهم الوحى مقروناً بالمعجزات إلا زعمهم جهلا منهم أن الله تعالى لا يبعث رسله من البشر بل من الملائكة. ٩٥ - قل - يا محمد - رداً عليهم: لو كان فى الأرض بدل البشر ملائكة يمشون فيها كالآدميين مستقرين فيها، لنزلنا عليهم من السماء ملكا رسولا من جنسهم، ولكن الملائكة لا يكونون كالبشر، ولو كانوا لجاءوا فى صورة البشر.',NULL,'423',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2128,'<span data-type="title" id=toc-2145>٩٦ </span>- وقل: إن أنكرتم رسالتى فكفى بالله حاكماً بينى وبينكم مقرراً صدق رسالتى إليكم، إنه كان بعباده عالماً بأحوالهم بصيراً بأفعالهم وهو مجازيهم عليها.',NULL,'423',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2129,'<span data-type="title" id=toc-2146>٩٧ </span>- وقل لهم: من يهده الله لحسن استعداده فهو المهتدى، ومن يضلله لفساد طبعه فلن تجد له أنصاراً غيره يهدونهم فى الدنيا، ونحشرهم فى الآخرة مسحوبين على وجوههم لا ينظرون ولا ينطقون ولا يسمعون، ومكانهم الذى يأوون إليه جهنم كلما ضعف لهيبها زادها الله تَلَهُّباً واشتعالاً.',NULL,'423',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2130,'<span data-type="title" id=toc-2147>٩٨ </span>- ذلك العذاب جزاؤهم بسبب كفرهم بالأدلة التى أقمناها لهم على الحق، وقولهم: أنبعث خَلْقاً جديداً بعد أن نصير عظاماً ورفاتاً؟ .',NULL,'423',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2131,'<span data-type="title" id=toc-2148>٩٩ </span>- أغفلوا ولم يعلموا أن الله الذى خلق السموات والأرض - مع عظمهما - قادر على أن يخلق مثلهم من الإنس والجن، ومن هو قادر على ذلك كيف لا يقدر على إعادتهم، وهى أهون عليه، وقد جعل - سبحانه - لإعادتهم بعد الموت أجلا محددا لا شك فى حصوله وهو يوم القيامة، ومع ذلك أبى الذين ظلموا أنفسهم بالكفر، بعد إقامة هذه الحُجة إلا جحودا.',NULL,'424',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2132,'<span data-type="title" id=toc-2149>١٠٠ </span>- قل لهؤلاء المشركين: لو كنتم تملكون خزائن رزق ربى لبخلتم خشية الفقر، لأن الإنسان مطبوع على شدة الحرص والبخل، والله هو الغنى الجواد، يمنح ما شاء لمن يشاء، وينزل من المعجزات ما شاء لا ما شاء الناس، وهو فى ذلك كله حكيم عليم.',NULL,'424',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2133,'<span data-type="title" id=toc-2150>١٠١ </span>- ولو أوتى هؤلاء من الآيات ما اقترحوا لصرفوها عن وجهها، ولم يؤمنوا بها، ولقد آتينا موسى تسع آيات واضحات ومع ذلك كفروا، وقال فرعون: إنى لأظنك مسحورا يا موسى.',NULL,'424',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2134,'<span data-type="title" id=toc-2151>١٠٢ </span>- قال موسى: لقد علمت يا فرعون أن الذى أنزل هذه الآيات هو رب السموات والأرض، لأنه هو الذى يقدر عليها وهى واضحات تبصرك بصدقى، ولكنك تكابر وتعاند، وإنى لأظنك يا فرعون هالكاً إذا لم ترجع عن عنادك.',NULL,'424',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2135,'<span data-type="title" id=toc-2152>١٠٣ </span>- فتمادى فرعون فى طغيانه، فأراد أن يخرج موسى وبنى إسرائيل من أرض مصر، فأغرقناه مع جنوده جميعاً.',NULL,'424',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2136,'<span data-type="title" id=toc-2153>١٠٤ </span>- ونجَّينا موسى وقومه، وقلنا من بعد إغراق فرعون لبنى إسرائيل: اسكنوا الأرض المقدسة بالشام، فإذا جاء وقت الحياة الأخرى جئنا بكم من قبوركم مختلطين ثم نحكم بينكم بالعدل.',NULL,'424',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2137,'<span data-type="title" id=toc-2154>١٠٥ </span>- وما أنزلنا القرآن إلا مؤيداً بالحكمة الإلهية التى اقتضت إنزاله، وهو فى ذاته وما نزل إلا مشتملا على الحق كله، فعقائده هى الصحيحة، وأحكامه هى المستقيمة، وما أرسلناك - أيها النبى - إلا مبشراً لمن آمن بالجنة، ونذيراً لمن كفر بالنار. فليس عليك شئ إذا لم يؤمنوا.',NULL,'425',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2138,'<span data-type="title" id=toc-2155>١٠٦ </span>- وقد فرَّقنا هذا القرآن ونزَّلناه منجما على مدة طويلة لتقرأه على الناس على مهل ليفهموه، نزلناه شيئاً بعد شئ تنزيلا مؤكداً لا شبهة فيه.',NULL,'425',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2139,'<span data-type="title" id=toc-2156>١٠٧ </span>- قل لكفار مكة تهديداً لهم: اختاروا لأنفسكم ما تحبون من الإيمان بالقرآن أو عدمه، فإن الذين أوتوا العلم الصحيح والإدراك السليم من قبل نزوله، إذا يتلى عليهم يقعون على الوجوه سجداً، شكراً لله على نعمته.',NULL,'425',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2140,'<span data-type="title" id=toc-2157>١٠٨ </span>- ويقولون: تنزه ربنا عن خلف الوعد الذى وعد به من نعيم وعذاب، إن وعده كان حاصلا لا محالة.',NULL,'425',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2141,'<span data-type="title" id=toc-2158>١٠٩ </span>- ويقعون ثانيا على الوجوه سجداً باكين من خوف الله، ويزيدهم القرآن تواضعاً لله.',NULL,'425',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2142,'<span data-type="title" id=toc-2159>١١٠ </span>- قل لهؤلاء المشركين: سموا الله باسم الله أو اسم الرحمن فأى اسم تسمونه فهو حسن، وهو تعالى له الأسماء الحسنى، ولا شبهة لكم فى أن تعدد الأسماء يستوجب تعدد المسمى. وإذا قرأت القرآن فى صلاتك فلا ترفع صوتك به، لئلا يسمع المشركون فيسبوك ويؤذوك، ولا تسر به فلا يسمع المؤمنون، وكن وسطا فى قراءتك.',NULL,'425',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2143,'<span data-type="title" id=toc-2160>١١١ </span>- وقل الحمد لله الذى لم يتخذ ولدا لعدم حاجته إليه، ولم يكن له شريك فى الملك، لأنه - وحده - منشئه، ولم يكن له ناصر يعطيه عزة من ذُلٍّ لحقه، وعظِّم ربك تعظيماً يليق به.',NULL,'425',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2144,'<span data-type="title" id=toc-2162>١ </span>- الثناء الجميل مستحق لله تعالى الذى أنزل على عبده محمد صلى الله تعالى عليه وسلم القرآن، ولم يجعل فيه شيئاً من الانحراف عن الصواب، بل كان فيه الحق الذى لا ريب فيه.',NULL,'426',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2145,'<span data-type="title" id=toc-2163>٢ </span>- وجعله قيماً مستقيماً فى تعاليمه لينذر الجاحدين بعذاب شديد صادر من عنده، ويبشر المصدقين الذين يعملون الأعمال الصالحات بأن لهم ثواباً جزيلاً.',NULL,'426',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2146,'<span data-type="title" id=toc-2164>٣ </span>- هو الجنة خالدين فيها أبدا.',NULL,'426',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2147,'<span data-type="title" id=toc-2165>٤ </span>- ويُنذر - على وجه الخصوص - الذين قالوا عن الله: إنه اتخذ ولدا، وهو المنزه عن أن يكون كالحوادث يَلِدُ أو يُوَلَدُ له.',NULL,'427',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2148,'<span data-type="title" id=toc-2166>٥ </span>- وليس عندهم علم بذلك ولا عند آبائهم من قبل، فما أعظم الافتراء فى هذه الكلمة التى تجرءوا على إخراجها من أفواههم! ما يقولون: إلا افتراء ليس بعده افتراء.',NULL,'427',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2149,'<span data-type="title" id=toc-2167>٦ </span>- لا تهلك نفسك - أيها النبى - أسفاً وحزناً على إعراضهم عن دعوتك غير مصدقين بهذا القرآن.',NULL,'427',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2150,'<span data-type="title" id=toc-2168>٧ </span>- إنا قد خلقناهم للخير والشر، وصيَّرنا ما فوق الأرض زينة لها ومنفعة لأهلها، لنعاملهم معاملة المختبر ليُظهر منهم الأصلح عملا، فمن استهوته الدنيا ولم يلتفت إلى الآخرة ضلَّ، ومن آمن بالآخرة اهتدى.',NULL,'427',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2151,'<span data-type="title" id=toc-2169>٨ </span>- وإنا لمصيِّرون عند انقضاء الدنيا ما فوقها مثل أرض مستوية لا نبات فيها، بعد أن كانت خضراء عامرة بمظاهر الحياة.',NULL,'427',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2152,'<span data-type="title" id=toc-2170>٩ </span>- لقد أنكر الذين استهوتهم الدنيا بزينتها البعث، مع أن الوقائع تثبت الحياة بعد الرقود الطويل، وهذه قصة أهل الكهف فى الجبل واللوح الذى رقمت فيه أسماؤهم بعد موتهم لم تكن عجباً وحدها دون سائر الآيات، وإن كان شأنها خارقاً للعادة، فليس أعجب من آياتنا الدالة على قدرتنا.',NULL,'427',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2153,'<span data-type="title" id=toc-2171>١٠ </span>- اذكر حين صار هؤلاء الفتيان إلى هذه المغارة وجعلوها مأوى لهم، فراراً بدينهم من الشرك والمشركين، فقالوا: يا ربنا آتنا من عندك مغفرة وأمناً من عدونا، ويسِّر لنا من شأننا هداية وتوفيقا.',NULL,'427',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2154,'<span data-type="title" id=toc-2172>١١ </span>- فاستجبنا دعاءهم فَأنَمْنَاهُمْ آمنين فى الكهف سنين عديدة.',NULL,'427',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2155,'<span data-type="title" id=toc-2173>١٢ </span>- ثم أيقظهم الله بعد أن ظلوا نياماً أمداً طويلاً، لتكون عاقبة ذلك إظهار عِلْمنا بمن أصاب من الفريقين فى تقدير مدة مكثهم.',NULL,'427',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2156,'<span data-type="title" id=toc-2174>١٣ </span>- نحن نقص عليك - أيها الرسول - خبرهم بالصدق: إنهم فتيان كانوا قبل العهود السابقة على دين الحق، صدَّقوا بوحدانية ربهم وسط قوم مشركين وزدناهم يقينا.',NULL,'427',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2157,'<span data-type="title" id=toc-2175>١٤ </span>- وثبتنا قلوبهم على الإيمان والصبر على الشدائد حين قاموا فى قومهم فقالوا متعاهدين: ربنا أنت الحق رب السموات والأرض لن نعبد من غيره إلها، ولن نتحول عن هذه العقيدة. والله إذا قلنا غير هذا لكان قولنا بعيداً عن الصواب.',NULL,'428',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2158,'<span data-type="title" id=toc-2176>١٥ </span>- ثم قال بعضهم لبعض: هؤلاء قومنا أشركوا بالله غيره، هلاَّ يأتون على ألوهية من يعبدونهم من دون الله بحُجة ظاهرة؟ إنهم لظالمون فيما فعلوا، ولا أحد أشد ظلماً ممن افترى على الله كذباً بنسبة الشريك إليه.',NULL,'428',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2159,'<span data-type="title" id=toc-2177>١٦ </span>- وقال بعضهم لبعض: ما دمنا قد اعتزلنا القوم فى كفرهم وشركهم فالجأوا إلى الكهف فراراً بدينكم، يبسط لكم ربكم من مغفرته، ويسهل لكم من أمركم ما تنتفعون به من مرافق الحياة.',NULL,'428',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2160,'<span data-type="title" id=toc-2178>١٧ </span>- وقد كان فى الكهف فتحة متسعة فى الجبل، وهى متجهة إلى الشمال يجيئهم منها النسيم العليل، وإذا طلعت الشمس من الشرق عن يمينهم مالت أشعتها عنهم، وإذا غربت عن يسارهم تجاوزتهم ولم تدخل أشعتها فى كهفهم، فحرارة الشمس لا تؤذيهم. ونسيم الهواء يأتيهم، وذلك كله من دلائل قدرة الله، ومن يوفقه الله لإدراكها يهتدى، ومن لا يوفقه فلا مرشد له من بعد.',NULL,'429',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2161,'<span data-type="title" id=toc-2179>١٨ </span>- وتظنهم - أيها الناظر - منتبهين، وفى الحقيقة هم نيام، ونقلبهم فى نومهم يمينا مرة ويسارا مرة لنحفظ أجسامهم من تأثير الأرض، وكلبهم - الذى صاحبهم - مادا ذراعيه بالفناء وهو نائم أيضاً فى شكل اليقظان، لو اطلعت - أيها المخاطب - عليهم وهم على تلك الحال لفررت منهم هاربا، ولملئ قلبك منهم فزعا لهيبتهم فى منامهم، فلا يقع نظر أحد عليهم إلا هابهم، كيلا يدنو منهم أحد، ولا تمسهم يد حتى تنتهى المدة.',NULL,'429',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2162,'<span data-type="title" id=toc-2180>١٩ </span>- وكما أنَمْنَاهم أيقظناهم ليسأل بعضهم بعضاً عن مدة مكثهم نائمين، فقال واحد منهم: ما الزمن الذى مكثتموه فى نومكم؟ فقالوا: مكثنا يوماً أو بعض يوم، ولما لم يكونوا مُسْتَيقنين من ذلك قالوا: اتركوا الأمر لله، فهو الأعلم به، وليذهب واحد منكم بهذه العملة الفضية إلى المدينة وليتخير أطيب الأطعمة فيأتيكم بطعام منه، وليكن حسن التفاهم، ولا يظهرن أمركم لأحد من الناس.',NULL,'429',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2163,'<span data-type="title" id=toc-2181>٢٠ </span>- إنهم إن رأوكم يقتلوكم رجماً بالحجارة أو يعيدوكم إلى الشرك بالقوة، وإذا عدتم إليه فلن تفلحوا فى الدنيا والآخرة.',NULL,'429',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2164,'<span data-type="title" id=toc-2182>٢١ </span>- وكما أنمناهم وبعثناهم أطلعنا أهل المدينة عليهم ليعلم المطلعون أن وعد الله بالبعث حق، وأن القيامة لا شك فى إتيانها. فآمن أهل المدينة بالله واليوم الآخر، ثم أمات الله الفتية فتنازعوا فى شأنهم، فقال بعضهم: ابنوا على باب الكهف بنياناً ونتركهم وشأنهم فربهم أعلم بحالهم، وقال أصحاب الكلمة فى القوم: لنتخذن على مكانهم مسجداً للعبادة.',NULL,'430',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2165,'<span data-type="title" id=toc-2183>٢٢ </span>- سيقول فريق من الخائضين فى قصتهم من أهل الكتاب: هم ثلاثة رابعهم كلبهم، ويقول آخرون: هم خمسة سادسهم كلبهم. ظنا خالياً من الدليل، ويقول آخرون: هم سبعة وثامنهم كلبهم. قل لهؤلاء المختلفين: ربى عليم علماً ليس فوقه علم بعددهم. ولا يعلم حقيقته إلا قليل من الناس أطلعهم الله على عددهم، فلا تجادل هؤلاء المختلفين فى شأن الفتية إلا جدالا ظاهراً ليناً دون محاولة إقناعهم، فإنهم لا يقتنعون. ولا تسأل أحداً منهم عن نبئهم، فقد جاءك الحق الذى لا مِرْيَة فيه.',NULL,'430',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2166,'<span data-type="title" id=toc-2184>٢٣ </span>- ولا تقولن لشئ تُقْدِم عليه وتهتم به: إنى فاعل ذلك فيما يستقبل من الزمان.',NULL,'430',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2167,'<span data-type="title" id=toc-2185>٢٤ </span>- إلا قولا مقترناً بمشيئة الله بأن تقول: إن شاء الله! وإذا نسيت أمراً فتدارك نفسك بذكر الله، وقل عند اعتزامك أمراً وتعليقه على مشيئة الله: عسى أن يوفقنى ربى إلى أمر خير مما عزمت عليه وأرشد منه.',NULL,'430',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2168,'<span data-type="title" id=toc-2186>٢٥ </span>- وإن الفتية مكثوا فى كهفهم نياماً ثلاثمائة سنين زادت تسعا.',NULL,'430',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2169,'<span data-type="title" id=toc-2187>٢٦ </span>- وقل - أيها الرسول - للناس: إن الله - وحده - هو العالم بزمنهم كله، إنه - سبحانه - هو المختص بعلم الغيب فى السموات والأرض، فما أعظم بصره فى كل موجود، وما أعظم سمعه لكل مسموع، وما لأهل السموات والأرض من يتولى أمورهم غيره، ولا يشرك فى قضائه أحداً من خلقه.',NULL,'430',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2170,'<span data-type="title" id=toc-2188>٢٧ </span>- واقرأ - أيها الرسول - ما أُوحى إليك من القرآن، ومنه ما أُوحى إليك من نبأ الفتية، ولا تستمع لما يهزأون به من طلب تبديل معجزة القرآن بمعجزة أخرى، فإنه لا مغيِّر لما ينبئه الله بكلمة الحق فى معجزاته، فإنه لا يقدر أحد على تبديله، ولا تخالف أمر ربك، فإنك حينئذ لن تجد غيره ملجأ يحفظك منه.',NULL,'431',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2171,'<span data-type="title" id=toc-2189>٢٨ </span>- واحتفظ - أيها الرسول - بصحبة صحابتك من المؤمنين الذين يعبدون الله - وحده - فى الصباح وفى العشى دائماً، يريدون رضوانه، ولا تنصرف عيناك عنهم إلى الجاحدين من الكفار لإرادة التمتع معهم بزينة الحياة الدنيا، ولا تطع فى طرد فقراء المؤمنين من مجلسك من جعلنا قلبه غافلا عن ذكرنا، لسوء استعداده، وصار عبداً لهواه، وصار أمره فى جميع أعماله بعيداً عن الصواب، والنهى للنبى نهى لغيره، وأن النبى - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - لا يريد الحياة الدنيا وزينتها، ولكن كان اتجاه النهى إليه لكى يحترس غيره من استهواء الدنيا، فإنه إذا فرض فيه إرادة الزينة للأبدان؛ لفرض كل إنسان فى نفسه ذلك ليحترس.',NULL,'431',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2172,'<span data-type="title" id=toc-2190>٢٩ </span>- وقل - أيها الرسول -: إن ما جئت به هو الحق من عند ربكم، فمن شاء أن يؤمن به فليؤمن، فذلك خير له، ومن شاء أن يكفر فليكفر فإنه لم يظلم إلا نفسه. إننا أعددنا لمن ظلم نفسه بالكفر ناراً تحيط بهم كالسرادق. وإن يستغث الظالمون بطلب الماء وهم فى جهنم؛ يؤت لهم بماء كالزيت العكر الشديد الحرارة يحرق الوجوه بلهيبه. قَبُحَ هذا الشراب لهم، وقبحت جهنم مكاناً لراحتهم.',NULL,'431',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2173,'<span data-type="title" id=toc-2191>٣٠ </span>- أما الذين آمنوا بالله وبدينه الحق الذى يُوحى إليك، وعملوا ما أمرهم به ربهم من الأعمال الصالحة، فإنا لا نضيع أجرهم على ما أحسنوا من الأعمال.',NULL,'431',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2174,'<span data-type="title" id=toc-2192>٣١ </span>- هؤلاء لهم جنات يقيمون فيها منعَّمين أبدا، تنساب الأنهار من بين أشجارها وقصورها، يتحلون فيها بمظاهر السعادة فى الدنيا، كالأساور الذهبية، وملابسهم فيها الثياب الخضر من الحرير على اختلاف أنواعه، متكئين فيها على السرر بين الوسائد والستائر، نعم الثواب لهم، وحَسُنت الجنة دار مقام وراحة، يجدون فيها كل ما يطلبون.',NULL,'432',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2175,'<span data-type="title" id=toc-2193>٣٢ </span>- بيِّن - أيها الرسول - فى شأن الكفار الأغنياء مع المؤمنين الفقراء مثلا وقع فيما سلف بين رجلين: كافر ومؤمن، وللكافر حديقتان من أعناب، وأحطناهما بالنخيل زينة وفائدة، وجعلنا بين الجنتين زرعا نضِراً مثمراً.',NULL,'432',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2176,'<span data-type="title" id=toc-2194>٣٣ </span>- وقد أثمرت كل واحدة من الجنتين ثمرها ناضجاً موفوراً، ولم تنقص منه شيئاً، وفجَّرنا نهراً ينساب خلالهما.',NULL,'432',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2177,'<span data-type="title" id=toc-2195>٣٤ </span>- وكان لصاحب الجنتين أموال أخرى مثمرة، فداخله الزهو بتلك النعم، فقال لصاحبه المؤمن فى غرور وهما يتناقشان: أنا أكثر منك مالا وأقوى عشيرة ونصيرا.',NULL,'432',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2178,'<span data-type="title" id=toc-2196>٣٥ </span>- ثم دخل إحدى جنتيه مع صاحبه المؤمن، وهو مأخوذ بغروره فقال: ما أظن أن تفنى هذه الجنة أبدا! .',NULL,'432',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2179,'<span data-type="title" id=toc-2197>٣٦ </span>- وما أظن القيامة حاصلة، ولو فرض ورجعت إلى ربى بالبعث كما تزعم، والله لأجدن خيراً من هذه الجنة عاقبة لى؛ لأننى أهل للنعيم فى كل حال، فهو يقيس الغائب على الحاضر، ولا يعلم أن الغائب فيه الجزاء على الإيمان وفعل الخير.',NULL,'432',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2180,'<span data-type="title" id=toc-2198>٣٧ </span>- قال صاحبه المؤمن مجيباً له: أتسوغ لنفسك أن تكفر بربك الذى خلق أصلك آدم من تراب، ثم من نطفة مائية، ثم صوّرك رجلا كاملا، فإن اعتززت بمالك وعشيرتك، فاذكر ربك وأصلك الذى هو من الطين.',NULL,'432',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2181,'<span data-type="title" id=toc-2199>٣٨ </span>- لكن أقول: إن الذى خلقنى وخلق هذا العالم كله هو الله ربى، وأنا أعبده - وحده - ولا أشرك معه أحدا.',NULL,'433',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2182,'<span data-type="title" id=toc-2200>٣٩ </span>- ولولا قلت عند دخولك جنتك والنظر إلى ما فيها: هذا ما شاء الله ولا قوة لى على تحصيله إلا بمعونة الله، فيكون ذلك شكراً كفيلا بدوام نعمتك. ثم قال له: إن كنت ترانى أقل منك مالا وأقل ولدا ونصيرا.',NULL,'433',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2183,'<span data-type="title" id=toc-2201>٤٠ </span>- فلعل ربى يعطينى خيراً من جنتك فى الدنيا أو الآخرة، ويرسل على جنتك قدَراً قدَّره لها كصواعق من السماء، فتصير أرضاً ملساء لا ينبت فيها شئ، ولا يثبت عليها قدم.',NULL,'433',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2184,'<span data-type="title" id=toc-2202>٤١ </span>- أو يصير ماؤها غائراً فى الأرض لا يمكن الوصول إليه، فلا تقدر على إخراجه لسقيها.',NULL,'433',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2185,'<span data-type="title" id=toc-2203>٤٢ </span>- قد عاجل الله الكافر، وأحاطت المهلكات بثمار جنته، وأهلكتها، وأبادت أصولها، فأصبح يقلب كفيه ندماً وتحسراً على ما أنفق فى عمارتها، ثم عاجلها الخراب، فتمنى أن لم يكن أشرك بربه أحدا.',NULL,'433',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2186,'<span data-type="title" id=toc-2204>٤٣ </span>- عند هذه المحنة لم تكن له عشيرة تنصره من دون الله كما كان يعتز، وما كان هو بقادر على نصرة نفسه.',NULL,'433',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2187,'<span data-type="title" id=toc-2205>٤٤ </span>- فإن النصرة فى كل حال ثابتة لله الحق - وحده - وهو - سبحانه - خير لعبده المؤمن يجزل له الثواب ويحسن له العاقبة.',NULL,'433',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2188,'<span data-type="title" id=toc-2206>٤٥ </span>- واذكر - أيها الرسول - للناس مثلا للحياة الدنيا فى نضرتها وبهجتها ثم سرعة فنائها، بأنها كماء أُنزل من السماء فارتوى به نبات الأرض فاخضر وأينع، ثم لم يلبث طويلا حتى جف وصار يابسا متكسراً تفرقه الرياح، والله قادر على كل شئ إنشاءً وإفناءً.',NULL,'434',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2189,'<span data-type="title" id=toc-2207>٤٦ </span>- المال والبنون جمال ومتعة لكم فى الحياة الدنيا وهما قوتها، ولكن لا دوام لها، بل هى فانية غير باقية، والأعمال الصالحة الباقية خير لكم عند الله، يجزل ثوابها، وخير أمل يتعلق به الإنسان.',NULL,'434',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2190,'<span data-type="title" id=toc-2208>٤٧ </span>- وأنذر الناس - أيها الرسول - بيوم يفنى هذا الوجود، فيزيل فيه الجبال، وتبصر فيه الأرض ظاهرة مستوية لا يسترها شئ مما كان عليها، ونحشر فيه الناس للحساب فلا نترك منهم أحدا.',NULL,'434',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2191,'<span data-type="title" id=toc-2209>٤٨ </span>- ويعرض الناس فى هذا اليوم على الله فى جموع مصفوفة للحساب، ويقول الله تعالى: لقد بعثناكم بعد الموت كما أحييناكم أول مرة، وجئتمونا فرادى بلا مال ولا بنين، وكنتم فى الدنيا تكذبون بالبعث والحساب.',NULL,'434',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2192,'<span data-type="title" id=toc-2210>٤٩ </span>- ووضع فى يد كل واحد كتاب أعماله، فَيبْصره المؤمنون فرحين مما فيه، ويبصره الجاحدون خائفين مما فيه من الأعمال السيئة، ويقولون إذا رأوها: يا هلاكنا، إنا نعجب لهذا الكتاب الذى لم يترك من أعمالنا صغيرة ولا كبيرة إلا سجَّلها علينا، ووجدوا جزاء ما عملوا حقاً، ولا يظلم ربك أحداً من عباده.',NULL,'434',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2193,'<span data-type="title" id=toc-2211>٥٠ </span>- واذكر - أيها الرسول - لهم بدء خلقهم ليعلموا أنهم من الطين، وليس لهم أن يغتروا بما هم فيه، ويخضعوا لعدوّ أبيهم إبليس، لأنه كان من الجن، فاستكبر وتمرد على الله، فكيف بعد ما عرفتم من شأنه تتخذونه وذريته أنصاراً لكم من دون الله، وهم لكم أعداء؟! قبُح هذا البدل لمن ظلم نفسه فأطاع الشيطان.',NULL,'434',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2194,'<span data-type="title" id=toc-2212>٥١ </span>- ما أحضرت إبليس ولا ذريته خلْق السموات والأرض، ولا أشهدت بعضهم خلْق بعض لأستعين بهم، وما كنت فى حاجة إلى معين. فضلا عن أن أتخذ المفسدين أعواناً، فكيف تطيعون الشيطان وتعصوننى؟ .',NULL,'435',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2195,'<span data-type="title" id=toc-2213>٥٢ </span>- واذكر لهم يوم يقول الله للمشركين: نادوا الذين ادعيتم فى الدنيا أنهم شركائى فى العبادة ليشفعوا لكم بزعمكم، فاستغاثوا بهم فلم يجيبوهم، وجعلنا الآن ما كان بينهم هلاكاً للكفار بعد أن كان فى الدنيا تواصل عبادة ومحبة.',NULL,'435',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2196,'<span data-type="title" id=toc-2214>٥٣ </span>- وعاين المجرمون النار فأيقنوا أنهم واقعون فيها، ولم يجدوا بديلا عنها مكاناً يحلُّون فيه.',NULL,'435',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2197,'<span data-type="title" id=toc-2215>٥٤ </span>- ولقد ذكر الله للناس فى هذا القرآن الذين كفروا به، وطلبوا معجزة أخرى غيره، أمثلة متنوعة ليعظهم بما فيها، ولكن الإنسان فى طبيعته حب الجدل، فإذا كان جاحدا جادل بالباطل.',NULL,'435',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2198,'<span data-type="title" id=toc-2216>٥٥ </span>- وما منع المشركين من الإيمان حين جاءهم سبب الهدى - وهو الرسول والقرآن ليؤمنوا ويستغفروا الله - إلا تعنتهم وطلبهم من الرسول أن تأتيهم سنة الله فى الأولين، وهى الهلاك المستأصل الذى أتى الأولين، أو يأتيهم العذاب عياناً.',NULL,'435',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2199,'<span data-type="title" id=toc-2217>٥٦ </span>- ولكن الله لا يرسل رسله إلا للتبشير والإنذار، ولم يرسلهم ليقترح عليهم المعاندون معجزات معينة، ولكن الذين كفروا يعرضون عن الحُجَّة، ويجادلون المرسلين بالباطل ليبطلوا الحق، وقد وقفوا من القرآن والنُّذر موقف المستهزئ الساخر الذى لا يُعْنى بطلب الحقائق.',NULL,'435',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2200,'<span data-type="title" id=toc-2218>٥٧ </span>- وليس أحد أظلم ممن وُعِظ بآيات ربه فلم يتدبرها، ونسى عاقبة ما عمل من المعاصى. إنا بسبب ميلهم إلى الكفر جعلنا على قلوبهم أغطية، فلا تعقل ولا يصل إليها النور، وفى آذانهم صمما فلا تسمع سماع فهم، وإن تدعهم - أيها الرسول - إلى الدين الحق فلن يهتدوا ما دامت هذه طبيعتهم البتة.',NULL,'436',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2201,'<span data-type="title" id=toc-2219>٥٨ </span>- وربك العظيم المغفرة لذنوب عباده، صاحب الرحمة الواسعة لمن أناب إليه منهم، ولو شاء أن يؤاخذهم بما اجترحوا من السيئات لعجَّل لهم العذاب كما سلف لغيرهم، ولكنه - لحكمة قدَّرها - أخرهم لموعد يذوقون فيه أشد العقاب، ولن يجدوا ملجأ يحفظهم منه.',NULL,'436',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2202,'<span data-type="title" id=toc-2220>٥٩ </span>- وها هى ذى القرى الماضية التى دمرناها لما ظلم أهلها بتكذيب رسلهم، وجعلنا لهلاكهم موْعدا لا يتخلف، فكذلك حال المكذبين من قومك إذا لم يؤمنوا.',NULL,'436',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2203,'<span data-type="title" id=toc-2221>٦٠ </span>- وإن علم الله لا يحيط به أحد، إلا أن يعطيه نبياً أو صالحاً، واذكر - أيها الرسول - أن موسى ابن عمران قال لفتاه - خادمه وتلميذه -: لا أزال أسير حتى أبلغ ملتقى البحرين أو أسير زمناً طويلاً.',NULL,'436',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2204,'<span data-type="title" id=toc-2222>٦١ </span>- فلما بلغ موسى وفتاه المكان الجامع بين البحرين، نسيا فيه حوتهما الذى حملاه بأمر الله، فانحدر فى البحر واتخذ طريقه فى الماء.',NULL,'436',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2205,'<span data-type="title" id=toc-2223>٦٢ </span>- فلما ابتعد موسى وفتاه عن المكان، وأحسا بالجوع والتعب، قال موسى لفتاه: آتنا ما نتغذى به، لقد لقينا فى سفرنا هذا تعبا ومشقة.',NULL,'436',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2206,'<span data-type="title" id=toc-2224>٦٣ </span>- قال له فتاه: أتذكر حين التجأنا إلى الصخرة، فإنى نسيت الحوت، وما أنسانى ذلك إلا الشيطان، ولا بد أن يكون الحوت قد اتخذ سبيله فى البحر، وإنى لأعجب من نسيانى هذا.',NULL,'436',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2207,'<span data-type="title" id=toc-2225>٦٤ </span>- قال له موسى: إن هذا الذى حدث هو ما كنا نطلبه لحكمة أرادها الله، فرجعا فى الطريق الذى جاءا منه يتتبعان أثر سيرهما.',NULL,'436',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2208,'<span data-type="title" id=toc-2226>٦٥ </span>- حتى وصلا الصخرة، فوجدا عبداً من عبادنا الصالحين أعطيناه الحكمة، وعلمناه من عندنا علماً غزيراً.',NULL,'437',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2209,'<span data-type="title" id=toc-2227>٦٦ </span>- قال موسى للعبد الصالح: هل أسير معك على أن تعلمن مما علمك الله؟ .',NULL,'437',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2210,'<span data-type="title" id=toc-2228>٦٧ </span>- قال له: إنك لن تستطيع الصبر على مصاحبتى.',NULL,'437',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2211,'<span data-type="title" id=toc-2229>٦٨ </span>- وكيف يمكنك الصبر على شئ لا خبرة لك بمثله؟',NULL,'437',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2212,'<span data-type="title" id=toc-2230>٦٩ </span>- قال موسى: سترانى إن شاء الله صابراً مطيعاً لك فيما تأمر به.',NULL,'437',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2213,'<span data-type="title" id=toc-2231>٧٠ </span>- قال العبد الصالح: فإن اتبعتنى ورأيت ما تنكره، فلا تفاتحنى بالسؤال عنه حتى أحدثك عنه.',NULL,'437',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2214,'<span data-type="title" id=toc-2232>٧١ </span>- فانطلقا يمشيان على ساحل البحر حتى وجدا سفينة، فركباها، فخرقها العبد الصالح فى أثناء سيرها، فاعترض موسى قائلا: أخرقتها قاصداً إغراق أهلها؟ لقد ارتكبت أمراً منكراً {.',NULL,'437',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2215,'<span data-type="title" id=toc-2233>٧٢ </span>- قال العبد الصالح: إننى قلت لك: إنك لن تستطيع الصبر على مصاحبتى.',NULL,'437',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2216,'<span data-type="title" id=toc-2234>٧٣ </span>- قال له موسى: لا تؤاخذنى على نسيان وصيتك، ولا تكلفنى مشقة فى تحصيل العلم منك وتجعله عسيراً.',NULL,'437',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2217,'<span data-type="title" id=toc-2235>٧٤ </span>- وبعد أن خرجا من السفينة ذهبا منطلقين، فلقيا فى طريقهما صَبِياً فقتله العبد الصالح، فقال موسى مستنكراً: أتقتل نفساً طاهرة بريئة من الذنوب بغير أن يقتل صاحبها أحدا؟} لقد أتيت فعلا مستنكراً! .',NULL,'437',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2218,'<span data-type="title" id=toc-2236>٧٥ </span>- قال العبد الصالح لموسى: لقد قلت لك: إنك لن تستطيع صبرا على السكوت عن سؤالى.',NULL,'438',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2219,'<span data-type="title" id=toc-2237>٧٦ </span>- قال موسى: إن سألتك عن شئ بعد هذه المرة فلا تصاحبنى، لأنك قد بلغت الغاية التى تعذر بها فى فراقى.',NULL,'438',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2220,'<span data-type="title" id=toc-2238>٧٧ </span>- فسارا حتى أتيا قرية، فطلبا من أهلها طعاماً، فأبوا ضيافتهما، فوجدا فيها جداراً مائلاً يكاد يسقط، فنقضه العبد الصالح وبناه حتى أقامه، قال موسى: لو شئت طلب أجر على النقض والبناء لفعلت.',NULL,'438',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2221,'<span data-type="title" id=toc-2239>٧٨ </span>- قال العبد الصالح: هذا التعرض منك مراراً لما أفعل سبب الفراق بينى وبينك. وسأخبرك بحكمة هذه التصرفات التى خفى عليك أمرها، ولم تستطع صبراً على ما خفى حتى تعرف حقيقته وسره.',NULL,'438',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2222,'<span data-type="title" id=toc-2240>٧٩ </span>- أما السفينة التى خرقتها، فهى لضعفاء محتاجين يعملون بها فى البحر لتحصيل رزقهم، فأردت أن أحدث بها عيباً يُزهد فيها، لأن خلفهم ملكاً يغتصب كل سفينة صالحة.',NULL,'438',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2223,'<span data-type="title" id=toc-2241>٨٠ </span>- وأما الغلام الذى قتلته فكان أبواه مؤمنين، فعلمنا - إن عاش - أنه سيصير سبباً لكفرهما.',NULL,'438',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2224,'<span data-type="title" id=toc-2242>٨١ </span>- فأردنا بقتله أن يعوِّضهما الله عنه ولداً خيراً منه ديناً وأعظم براً وعطفاً.',NULL,'438',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2225,'<span data-type="title" id=toc-2243>٨٢ </span>- وأما الجدار الذى أقمته - دون أجر - فكان لغلامين يتيمين من أهل المدينة، وكان تحته كنز تركه أبوهما لهما، وكان رجلا صالحاً، فأراد الله أن يحفظ لهما الكنز حتى يبلغا رشدهما، ويستخرجاه، رحمة بهما، وتكريماً لأبيهما فى ذريته. وما فعلت ما فعلت باجتهادى، إنما فعلته بتوجيه من الله، هذا تفسير ما خفى عليك يا موسى ولم تستطع الصبر عليه.',NULL,'439',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2226,'<span data-type="title" id=toc-2244>٨٣ </span>- يسألك - أيها الرسول - بعض الكفار عن نبإ ذى القرنين، فقل لهم: سأقص عليكم بعض أخباره.',NULL,'439',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2227,'<span data-type="title" id=toc-2245>٨٤ </span>- لقد مكنَّا لأمره فى الأرض، يتصرف فيها بتدبيره وسلطانه، وآتيناه الكثير من العلم بالأسباب ما يستطيع به توجيه الأمور.',NULL,'439',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2228,'<span data-type="title" id=toc-2246>٨٥ </span>- فاستعان بهذه الأسباب على بسط سلطانه فى الأرض، واتخذ سبباً يوصّله إلى بلوغ مغرب الشمس.',NULL,'439',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2229,'<span data-type="title" id=toc-2247>٨٦ </span>- وسار حتى وصل إلى مكان سحيق جهة الغرب، فوجد الشمس - فى رأى العين - تغرب فى مكان به عين ذات ماء حار وطين أسود، وبالقرب من هذه العين وجد ذو القرنين قوماً كافرين، فألهمه الله أن يتخذ فيهم أحد أمرين: إما أن يدعُوهم إلى الإيمان، وهذا أمر حسن فى ذاته، وإما أن يقاتلهم إن لم يجيبوا داعى الإيمان.',NULL,'439',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2230,'<span data-type="title" id=toc-2248>٨٧ </span>- فأعلن ذو القرنين فيهم: أن من ظلم منهم نفسه بالبقاء على الشرك، استحق العذاب الدنيوى على يديه، ثم يرجع إلى ربه فيعذبه عذاباً شديداً ليس معروفاً لهم.',NULL,'439',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2231,'<span data-type="title" id=toc-2249>٨٨ </span>- وأن من استجاب له وآمن بربّه وعمل صالحاً، فله العاقبة الحسنى فى الآخرة، وسنعامله فى الدنيا برفق ويسر.',NULL,'439',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2232,'<span data-type="title" id=toc-2250>٨٩ </span>- ثم سار ذو القرنين كذلك، مستعيناً بتوفيق الله، واتبع سبباً للوصول إلى مطلع الشمس مشرقا.',NULL,'439',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2233,'<span data-type="title" id=toc-2251>٩٠ </span>- حتى بلغ مشرق الشمس - فى رأى العين - فى نهاية ما وصل إليه من العمران، فوجدها تطلع على قوم يعيشون على الفطرة الأولى لا يسترهم من حرها ساتر.',NULL,'440',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2234,'<span data-type="title" id=toc-2252>٩١ </span>- وكما دعا ذو القرنين السابقين من أهل المغرب إلى الإيمان دعا هؤلاء وسار فيهم سيرته الأولى.',NULL,'440',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2235,'<span data-type="title" id=toc-2253>٩٢ </span>- ثم سار كذلك مستعيناً بما هيَّأ الله له من أسباب التوفيق، سالكاً طريقاً بين الشرق والغرب.',NULL,'440',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2236,'<span data-type="title" id=toc-2254>٩٣ </span>- حتى وصل - فى رحلته الثالثة - إلى مكان سحيق بين جبلين مرتفعين، وهناك وجد قوماً لا يفقهون ما يُقال لهم إلا فى عسر ومشقة.',NULL,'440',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2237,'<span data-type="title" id=toc-2255>٩٤ </span>- فلما آنسوا فيه القوة، طلبوا منه أن يُقيم لهم سداً فى وجه يأجوج ومأجوج، وهم قوم كانوا يَغِيرون عليهم، فيفسدون فى أرضهم ويخربون، على أن يجعلوا له ضريبة فى نظير هذا العمل.',NULL,'440',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2238,'<span data-type="title" id=toc-2256>٩٥ </span>- فرد عليهم قائلا: إن ما منحنيه الله من الثروة والسلطان خير مما تعرضون علىّ. وشرع يُقيم السد طالباً منهم أن يعينوه بكل ما يقدرون عليه من رجال وأدوات، ليحقق لهم ما أرادوا.',NULL,'440',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2239,'<span data-type="title" id=toc-2257>٩٦ </span>- وطلب منهم أن يجمعوا له قطع الحديد. فجمعوا له منها ما أراد، فأقام به سداً عالياً ساوى به بين حافتى الجبلين، ثم أمرهم أن يوقدوا عليه النار، فأوقدها حتى انصهر الحديد، فصب عليه النحاس المذاب فأصبح سداً صلباً منيعاً.',NULL,'440',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2240,'<span data-type="title" id=toc-2258>٩٧ </span>- فما استطاع هؤلاء المغيرون أن يتسلقوا السد لارتفاعه، ولا أن يثقبوه لصلابته.',NULL,'441',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2241,'<span data-type="title" id=toc-2259>٩٨ </span>- وبعد أن أتم ذو القرنين بناء السد قال شاكراً لله: هذا السد رحمة من ربى بعباده، وسيظل قائماً حتى يجئ أمر الله بهدمه، فيصير أرضاً مستوياً، وأمر الله نافذ لا محالة.',NULL,'441',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2242,'<span data-type="title" id=toc-2260>٩٩ </span>- ومنذ إتمام السد ظل يأجوج ومأجوج من ورائه يضطربون فيما بينهم، وحبس شرهم عن الآخرين، فإذا كان يوم القيامة ونفخ فى الصور جمع الله الخلائق جميعاً للحساب والجزاء.',NULL,'441',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2243,'<span data-type="title" id=toc-2261>١٠٠ </span>- وعند ذلك يبرز الله جهنم للكافرين إبرازاً يروعهم ويحشرهم فيها.',NULL,'441',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2244,'<span data-type="title" id=toc-2262>١٠١ </span>- وذلك لأن أعينهم فى الدنيا كانت فى غفلة عن التبصر فى آيات الله كأن عليها غطاء، وكانوا لضلالهم لا يستطيعون سماع دعوة الحق كفاقدى حاسة السمع.',NULL,'441',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2245,'<span data-type="title" id=toc-2263>١٠٢ </span>- هل عميت بصائر الذين كفروا، فظنوا أن اتخاذهم آلهة من عبادى - كالملائكة وعيسى - يعبدونها من دونى نافع لهم وصارف عنهم العذاب؟ إنا أعتدنا لهم جهنم مقراً ينالون فيه ما يستحقون من جزاء.',NULL,'441',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2246,'<span data-type="title" id=toc-2264>١٠٣ </span>- قل - أيها الرسول - لهؤلاء الكافرين: هل أخبركم بأشد الناس خسراناً لأعمالهم، وحرماناً من ثوابها؟',NULL,'441',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2247,'<span data-type="title" id=toc-2265>١٠٤ </span>- هم الذين بطل عملهم فى الحياة الدنيا لفساد اعتقادهم، وهم يعتقدون أنهم يحسنون بعملهم صنيعاً.',NULL,'441',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2248,'<span data-type="title" id=toc-2266>١٠٥ </span>- هؤلاء هم الذين كفروا بدلائل قدرة الله، وأنكروا يوم البعث والحساب، فضاعت أعمالهم، واستحقوا يوم القيامة التحقير والإهمال، إذ ليس لهم عمل يُعْتدّ به.',NULL,'442',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2249,'<span data-type="title" id=toc-2267>١٠٦ </span>- ذلك الذى بيَّناه وفصَّلناه شأن هؤلاء، وجزاؤهم عليه جهنم، بسبب كفرهم وسخريتهم بما أنزل الله من آيات، وما أرسل من رسل.',NULL,'442',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2250,'<span data-type="title" id=toc-2268>١٠٧ </span>- إن الذين صدقوا فى الإيمان وعملوا الأعمال الصالحة؛ جزاؤهم جنات الفردوس ينزلون فيها.',NULL,'442',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2251,'<span data-type="title" id=toc-2269>١٠٨ </span>- وينعمون فيها أبدا لا يبغون عنها بديلا.',NULL,'442',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2252,'<span data-type="title" id=toc-2270>١٠٩ </span>- قل - أيها الرسول - للناس: إن علم الله محيط بكل شئ، ولو كان ماء البحر مداداً يُسطَّر به كلمات الله الدالة على علمه وحكمته، لنفد هذا المداد، ولو مُدّ بمثله قبل أن تنفد كلمات الله.',NULL,'442',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2253,'<span data-type="title" id=toc-2271>١١٠ </span>- قل - أيها الرسول - للناس: إنما أنا إنسان مثلكم، مرسل إليكم، أعلمكم ما علمنى الله إياه، يوحى إلىّ أنما إلهكم إله واحد لا شريك له، فمن كان يطمع فى لقاء الله وثوابه؛ فليعمل الأعمال الصالحة مخلصاً، وليتجنب الإشراك بالله فى العبادة.',NULL,'442',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2254,'<span data-type="title" id=toc-2273>١ </span>- حروف صوتية لبيان أن القرآن المعجز من هذه الحروف، ولتنبيههم فيسمعون.',NULL,'443',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2255,'<span data-type="title" id=toc-2274>٢ </span>- هذا - أيها الرسول - قصص ربك عن رحمته لعبده ونبيه زكريا.',NULL,'443',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2256,'<span data-type="title" id=toc-2275>٣ </span>- حين التجأ إلى الله ودعاه فى خفية عن الناس.',NULL,'443',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2257,'<span data-type="title" id=toc-2276>٤ </span>- فقال: رب إنى قد ضعفت، وشاب رأسى، وكنت بدعائك غير شقى يا رب، بل كنت سعيداً مستجاب الدعوة.',NULL,'443',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2258,'<span data-type="title" id=toc-2277>٥ </span>- وإنى خِفْتُ أقاربى ألا يحسنوا القيام على أمر الدين بعد موتى، وكانت ولا تزال امرأتى عقيماً، فارزقنى من فضلك غلاماً يخلفنى فى قومى.',NULL,'444',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2259,'<span data-type="title" id=toc-2278>٦ </span>- يرثنى فى العلم والدين، ويرث من آل يعقوب الملك، واجعله يا رب مرضياً عندك وعند الناس.',NULL,'444',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2260,'<span data-type="title" id=toc-2279>٧ </span>- فنودى: يا زكريا إنا نبشرك بغلام سميناه يحيى، ولم نُسم به أحداً قبل.',NULL,'444',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2261,'<span data-type="title" id=toc-2280>٨ </span>- قال زكريا متعجباً: يا رب كيف يكون لى ولد وزوجى عقيم وأنا فى سن الشيخوخة؟ .',NULL,'444',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2262,'<span data-type="title" id=toc-2281>٩ </span>- فأوحى الله لعبده زكريا أن الأمر كما بشرت به، وأن مَنْحَك الولد مع كبر السن وعُقْم الزوج هَيِّن علىّ، ولا تستبعد ذلك فقد خلقتك من قبل ولم تك شيئاً موجوداً.',NULL,'444',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2263,'<span data-type="title" id=toc-2282>١٠ </span>- قال زكريا: رب اجعل لى علامة تدل على حصول ما بشرت به. قال الله تعالى: علامتك أن تُحبس عن الكلام ثلاث ليال، وأنت سليم الحواس واللسان.',NULL,'444',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2264,'<span data-type="title" id=toc-2283>١١ </span>- فخرج زكريا على قومه من مصلاه، فأشار إليهم أن سبحوا الله صباحاً ومساء.',NULL,'444',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2265,'<span data-type="title" id=toc-2284>١٢ </span>- ولد يحيى وشب ثم نودى، وأمر بأن يعمل بما فى التوراة فى جد وعزم، وقد آتاه الله فى طور الصبا فقْه الدين وفهْم الأحكام.',NULL,'444',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2266,'<span data-type="title" id=toc-2285>١٣ </span>- وطبَّعه الله على الحنان، وسمو النفس، ونشَّأه على التقوى.',NULL,'444',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2267,'<span data-type="title" id=toc-2286>١٤ </span>- وجعله الله كثير البر بوالديه والإحسان إليهما، ولم يجعله مُتَجبّراً على الناس، ولا عاصياً لله.',NULL,'444',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2268,'<span data-type="title" id=toc-2287>١٥ </span>- وسلامة له وأمان، أن لا يمسه ضر أو أذى يوم ولادته، ويوم موته، ويوم بعثه حياً.',NULL,'445',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2269,'<span data-type="title" id=toc-2288>١٦ </span>- واذكر - أيها الرسول - ما فى القرآن من قصة مريم، حينما انفردت عن أهلها وعن الناس، وذهبت إلى مكان جهة الشرق من مقامها.',NULL,'445',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2270,'<span data-type="title" id=toc-2289>١٧ </span>- وضربت بينها وبينهم حجاباً، فأرسل الله إليها جبريل فى صورة إنسان تام الخلق، حتى لا تفزع من رؤيته فى هيئته الملكية التى لا تألفها.',NULL,'445',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2271,'<span data-type="title" id=toc-2290>١٨ </span>- قالت مريم: إنى ألتجئ إلى الرحمن منك إن يُرجى منك أن تتقى الله وتخشاه.',NULL,'445',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2272,'<span data-type="title" id=toc-2291>١٩ </span>- قال الملك: ما أنا إلا رسول من ربك لأكون سبباً فى أن يوهب لك غلام طاهر خيّر.',NULL,'445',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2273,'<span data-type="title" id=toc-2292>٢٠ </span>- قالت مريم: كيف يكون لى غلام ولم يقربنى إنسان، ولست فاجرة؟ .',NULL,'445',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2274,'<span data-type="title" id=toc-2293>٢١ </span>- قال الملك: الأمر كما قلت: لم يمسك رجل. قال ربك: إعطاء الغلام بلا أب علىَّ سهل، وليكون ذلك آية للناس تدل على عظيم قدرتنا، كما يكون رحمة لمن يهتدى به. وكان خلق عيسى أمراً مقدراً لا بد منه.',NULL,'445',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2275,'<span data-type="title" id=toc-2294>٢٢ </span>- وتحققت إرادة الله، وحملت مريم بعيسى على الوجه الذى أراده الله، وذهبت بحملها إلى المكان البعيد عن الناس.',NULL,'445',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2276,'<span data-type="title" id=toc-2295>٢٣ </span>- فألجأها ألم الولادة إلى أن تركن إلى جذع نخلة لتستند إليه وتستتر به، وتخيَّلت ما سيكون من إنكار أهلها هذا الأمر، وتمنت لو أدركها الموت، وكانت شيئاً منسيا لا يذكر.',NULL,'445',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2277,'<span data-type="title" id=toc-2296>٢٤ </span>- فناداها الملك من مكان منخفض عنها: لا تحزنى بالوحدة وعدم الطعام والشراب ومقالة الناس، فقد جعل ربك بالقرب منك نهراً صغيراً.',NULL,'445',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2278,'<span data-type="title" id=toc-2297>٢٥ </span>- وهزى النخلة نحوك يتساقط عليك الرطب الطيب.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2279,'<span data-type="title" id=toc-2298>٢٦ </span>- فكلى منه واشربى، وطيبى نفساً. فإن رأيت أحداً من البشر ينكر عليك أمرك، فأشيرى إليه أنك صائمة عن الكلام، ولن تتحدثى اليوم إلى أحد.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2280,'<span data-type="title" id=toc-2299>٢٧ </span>- فأقبلت مريم على أهلها تحمل عيسى، فقالوا لها فى دهشة واستنكار: لقد أتيت أمراً فظيعاً منكراً.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2281,'<span data-type="title" id=toc-2300>٢٨ </span>- يا سلالة هارون النبى التقى الورع، كيف تأتين ما أتيت وما كان أبوك فاسد الأخلاق وما كانت أمك فاجرة.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2282,'<span data-type="title" id=toc-2301>٢٩ </span>- فأشارت إلى ولدها عيسى ليكلموه، فقالوا: كيف نتحدث مع طفل لا يزال فى المهد.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2283,'<span data-type="title" id=toc-2302>٣٠ </span>- فلما سمع عيسى كلامهم أنطقه الله فقال: إنى عبد الله سيؤتينى الإنجيل، ويختارنى نبياً.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2284,'<span data-type="title" id=toc-2303>٣١ </span>- ويجعلنى مُباركاً مُعلماً للخير نفَّاعاً للناس، ويأمرنى بإقامة الصلاة وأداء الزكاة مدة حياتى.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2285,'<span data-type="title" id=toc-2304>٣٢ </span>- كما يأمرنى أن أكون بارّاً بوالدتى، ولم يجعلنى متجبراً فى الناس، ولا شقياً بمعصيته.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2286,'<span data-type="title" id=toc-2305>٣٣ </span>- والأمان من الله علىَّ يوم ولادتى، ويوم موتى، ويوم بعثى حياً.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2287,'<span data-type="title" id=toc-2306>٣٤ </span>- ذلك الموصوف بهذه الصفات، هو عيسى ابن مريم، وهذا هو القول الحق فى شأنه، الذى يجادل فيه المبطلون، ويشكك فى أمر نبوته الشاكُّون.',NULL,'446',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2288,'<span data-type="title" id=toc-2307>٣٥ </span>- وما صح ولا استقام فى العقل أن يتخذ الله ولدا - تنزه الله عن ذلك - وشأنه - سبحانه - أنه إذا قضى أمراً من الأمور نفذت إرادته لا محالة، بكلمة - كن - فيتحقق فى الوجود كائناً.',NULL,'447',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2289,'<span data-type="title" id=toc-2308>٣٦ </span>- وإن الله سيدى وسيدكم فاعبدوه، ولا تشركوا به أحداً، هذا الذى دعوتكم إليه طريق يوصلكم إلى السعادة.',NULL,'447',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2290,'<span data-type="title" id=toc-2309>٣٧ </span>- ومع ما تقدم من قول الحق فى عيسى، قد اختلف أهل الكتاب فيه، وذهبوا مذاهب شتى. والعذاب الشديد للكافرين يوم يحضرون موقف الحساب، ويشهدون موقف القيامة، ويلقون سوء الجزاء.',NULL,'447',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2291,'<span data-type="title" id=toc-2310>٣٨ </span>- ما أشد سمعهم وأقوى بصرهم يوم يلقون الله! {لكنهم اليوم فى الدنيا بظلمهم أنفسهم، وتركهم الانتفاع بالسمع والبصر فى ضلال عن الحق، ظاهر لا يخفى.',NULL,'447',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2292,'<span data-type="title" id=toc-2311>٣٩ </span>- وحذِّر - أيها الرسول - هؤلاء الظالمين يوماً يتحسرون فيه على تفريطهم فى حق الله وحق أنفسهم - وقد فرغوا من حسابهم، ونالوا جزاءهم - وقد كانوا فى الدنيا غافلين عن ذلك اليوم، لا يصدقون بالبعث ولا بالجزاء.',NULL,'447',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2293,'<span data-type="title" id=toc-2312>٤٠ </span>- ألا فليعلم الناس أن الله هو الوارث لهذا الكون وما فيه، وحسابهم على الله.',NULL,'447',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2294,'<span data-type="title" id=toc-2313>٤١ </span>- واذكر - أيها الرسول - للناس ما فى القرآن من قصة إبراهيم، إنه كان عظيم الصدق، قولا وعملا، مخبراً عن الله تعالى.',NULL,'447',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2295,'<span data-type="title" id=toc-2314>٤٢ </span>- واذكر حين وجه إبراهيم الخطاب إلى أبيه فى رفق قائلا له: يا أبى كيف تعبد أصناماً لا تسمع ولا تبصر ولا تجلب لك خيراً، ولا تدفع عنك شراً؟} .',NULL,'447',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2296,'<span data-type="title" id=toc-2315>٤٣ </span>- يا أبى، لقد جاءنى من طريق الوحى الإلهى ما لم يأتك من العلم بالله، والمعرفة بما يلزم الإنسان نحو ربه، فاتبعنى فيما أدعوك إليه من الإيمان، أَدُلَّك على الطريق المستقيم، الذى يوصلك إلى الحق والسعادة.',NULL,'448',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2297,'<span data-type="title" id=toc-2316>٤٤ </span>- يا أبت: لا تطع الشيطان فيما يُزين لك من عبادة الأصنام، فإن الشيطان دائب على معصية الرحمن ومخالفة أمره.',NULL,'448',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2298,'<span data-type="title" id=toc-2317>٤٥ </span>- يا أبت: إنى أخشى - إن أصْرَرتَ على الكفر - أن يُصيبك عذاب شديد من الرحمن، فتكون قريناً للشيطان فى النار تليه ويليك.',NULL,'448',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2299,'<span data-type="title" id=toc-2318>٤٦ </span>- قال الأب لإبراهيم منكراً عليه، مهدداً له: كيف تنصرف عن آلهتى يا إبراهيم وتدعونى إلى عبادة إلهك؟ لئن لم تكف عن شتم الأصنام لأضربنك بالحجارة، فاحذرنى واتركنى زماناً طويلا، حتى تهدأ ثائرتى عنك.',NULL,'448',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2300,'<span data-type="title" id=toc-2319>٤٧ </span>- تلطف إبراهيم مع أبيه وودعه قائلا: سلام عليك منى، وسأدعو لك ربى بالهداية والمغفرة، وقد عوَّدنى ربى أن يكون رحيماً بى قريباً منى.',NULL,'448',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2301,'<span data-type="title" id=toc-2320>٤٨ </span>- وهاأنذا أهجركم وأبتعد عما تعبدون من دون الله، وأعبد ربى - وحده - راجياً أن يقبل طاعتى ولا يخيب رجائى.',NULL,'448',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2302,'<span data-type="title" id=toc-2321>٤٩ </span>- فلما فارق إبراهيم أباه وقومه وآلهتهم، أكرمه الله بالذرية الصالحة على يأس منه، إذ بلغ هو وزوجه حد الكبر الذى لا ينجب، فوهب له إسحاق، ورزقه من إسحاق يعقوب، وجعلناهما نبيين.',NULL,'448',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2303,'<span data-type="title" id=toc-2322>٥٠ </span>- وأعطيناهم فوق منزلة النبوة كثيراً من خيْرى الدين والدنيا برحمتنا، وأورثناهم فى الدنيا ذكرى طيبة خالدة، بلسان صدق علىٍّ يتحدث بذكرهم.',NULL,'449',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2304,'<span data-type="title" id=toc-2323>٥١ </span>- واتل - أيها الرسول - على الناس ما فى القرآن من قصة موسى، إنه كان خالصاً بنفسه وقلبه وجسمه لله، وقد اصطفاه الله للنبوة والرسالة.',NULL,'449',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2305,'<span data-type="title" id=toc-2324>٥٢ </span>- وكرمناه فناديناه عند جبل الطور، وسمع موسى النداء الإلهى من الجهة اليمنى، وقرَّبناه تقْريب تشريف واصطفيناه لمناجاتنا.',NULL,'449',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2306,'<span data-type="title" id=toc-2325>٥٣ </span>- ومنحناه من رحمتنا ونعمنا، واخترنا معه أخاه هارون نبيا، يعاونه فى تبليغ الرسالة.',NULL,'449',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2307,'<span data-type="title" id=toc-2326>٥٤ </span>- واتل - أيها الرسول - على الناس ما فى القرآن من قصة إسماعيل، إنه كان يصدق فى وعده، وقد وعد أباه بالصبر على ذبحه له، وَوَفَّى بوعده، ففداه الله وشرفه بالرسالة والنبوة.',NULL,'449',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2308,'<span data-type="title" id=toc-2327>٥٥ </span>- وكان يأمر أهله بإقامة الصلاة وإيتاء الزكاة، وكان فى المقام الكريم من رضا ربه.',NULL,'449',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2309,'<span data-type="title" id=toc-2328>٥٦ </span>- واتل - أيها الرسول - على الناس ما فى القرآن من قصة إدريس، إنه كان شأنه الصدق قولا وفعلا وعملا. وقد منحه الله شرف النبوة.',NULL,'449',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2310,'<span data-type="title" id=toc-2329>٥٧ </span>- وقد رفعه الله بذلك مكاناً سامياً.',NULL,'449',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2311,'<span data-type="title" id=toc-2330>٥٨ </span>- أولئك الذين سلف ذكرهم، ممن أنعم الله عليهم من النبيين بنعم الدنيا والآخرة من ذرية آدم ومن ذرية من نجاه الله مع نوح فى السفينة، ومن ذرية إبراهيم كإسماعيل، ومن ذرية يعقوب كأنبياء بنى إسرائيل، وممن هديناهم إلى الحق، واخترناهم لإعلاء كلمة الله. هؤلاء إذا سمعوا آيات الله تُتْلى عليهم خشعوا وخروا ساجدين لله متضرعين له.',NULL,'449',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2312,'<span data-type="title" id=toc-2331>٥٩ </span>- ثم جاء بعد هؤلاء الأخيار أجيال على غير هدْيهم تركوا الصلاة، وأهملوا الانتفاع بِهدْيها، وانهمكوا فى المعاصى، وسيلقى هؤلاء جزاء غيهم وضلالهم فى الدنيا والآخرة.',NULL,'450',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2313,'<span data-type="title" id=toc-2332>٦٠ </span>- لكن من تداركوا أنفسهم بالتوبة، وصدق الإيمان، والعمل الصالح، فإن الله يقبل توبتهم، ويدخلهم الجنة، ويوفيهم أجورهم.',NULL,'450',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2314,'<span data-type="title" id=toc-2333>٦١ </span>- هذه الجنات دار خلود، وعد الرحمن بها عباده التائبين، فآمنوا بها بالغيب، فهم داخلوها لا محالة، فإن وعد الله لا يتخلف.',NULL,'450',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2315,'<span data-type="title" id=toc-2334>٦٢ </span>- وهم فى تلك الجنات لا يجرى بينهم لغو الحديث، ولا يسمعون إلا خيراً وأمناً، ورزقهم فيها رغد مكفول دائماً.',NULL,'450',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2316,'<span data-type="title" id=toc-2335>٦٣ </span>- وإنما يؤتى الله تلك الجنة ويملكها لمن كان تقياً فى الدنيا بترك المعاصى وفعل الطاعات.',NULL,'450',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2317,'<span data-type="title" id=toc-2336>٦٤ </span>- وكان الوحى قد تأخر وقلق الرسول - عَلَيْهِ الصَّلَاة وَالسَّلَام ُ - فجاءه جبريل - عليه السلام - ليطمئنه وقال له: إن الملائكة لا تنزل إلا بإذن ربها، فاطمئن أيها الرسول الكريم فإن ربك لا ينسى.',NULL,'450',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2318,'<span data-type="title" id=toc-2337>٦٥ </span>- فهو سُبحانه الخالق المالك للسموات والأرض وما بينهما، والمدبر لشئونهما، والمستحق - وحده - للعبادة، فاعبده - أيها المخاطب - وثابر على عبادته صابراً مطمئناً، فهو سبحانه المستحق - وحده - للعبادة، وليس له نظير يستحق العبادة، أو يسمى باسم من أسمائه.',NULL,'450',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2319,'<span data-type="title" id=toc-2338>٦٦ </span>- ويقول الإنسان مستغرباً البعث: كيف أبعث حياً بعد الموت والفناء؟!! .',NULL,'450',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2320,'<span data-type="title" id=toc-2339>٦٧ </span>- كيف يستغرب قدرة الله على البعث فى الآخرة، ولا يذكر أنه تعالى خلقه فى الدنيا من عدم؟ ، مع أن إعادة الخلق أهْوَن من بدئه فى حكم العقل.',NULL,'450',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2321,'<span data-type="title" id=toc-2340>٦٨ </span>- وإذا كان أمر البعث غريباً ينكره الكافرون، فوالذى خلقك وربَّاك ونمَّاك لنجمعن الكافرين يوم القيامة مع شياطينهم - الذين زينوا لهم الكفر - وسنحضرهم جميعاً حول جهنم جاثين على ركبهم فى ذلة لشدة الهول والفزع.',NULL,'451',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2322,'<span data-type="title" id=toc-2341>٦٩ </span>- ثم لننزعن من كل جماعة أشدهم كفراً بالله، وتمرداً عليه، فيدفع بهم قبل سواهم إلى أشد العذاب.',NULL,'451',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2323,'<span data-type="title" id=toc-2342>٧٠ </span>- ونحن أعلم بالذين هم أحق بسبقهم إلى دخول جهنم والاصطلاء بلهيبها.',NULL,'451',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2324,'<span data-type="title" id=toc-2343>٧١ </span>- وإن منكم - معشر الخلق - إلا حاضر لها، يراها المؤمن ويمر بها، والكافر يدخلها، وتنفيذ هذا أمر واقع حتماً، جرى به قضاء الله.',NULL,'451',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2325,'<span data-type="title" id=toc-2344>٧٢ </span>- ثم إننا نشمل المتقين برحمتنا فننجيهم من جهنم، ونترك بها الذين ظلموا أنفسهم جاثين على ركبهم، تعذيباً لهم.',NULL,'451',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2326,'<span data-type="title" id=toc-2345>٧٣ </span>- وكان الكافرون فى الدنيا إذا تليت عليهم آيات الله واضحة الدلالة أعرضوا عنها، وقالوا للمؤمنين - معتزين بمالهم وجمعهم - لستم مثلنا حظاً فى الدنيا، فنحن خير منكم منزلا ومجلساً، فكذلك سيكون حظنا فى الآخرة التى تؤمنون بها.',NULL,'451',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2327,'<span data-type="title" id=toc-2346>٧٤ </span>- وكان على هؤلاء الكافرين أن يتعظوا بمَنْ سبقهم من أمم كثيرة كفرت بالله وكانوا أحسن منهم حظاً فى الدنيا، وأكثر متاعاً وأبهى منظراً، فأهلكهم الله بكفرهم - وهم كثيرون - وفى آثارهم عبر لكل معتبر.',NULL,'451',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2328,'<span data-type="title" id=toc-2347>٧٥ </span>- قل - أيها الرسول - لهؤلاء: من كان فى الضلالة والكفر أمهله الرحمن، وأملى له فى العمر ليزداد طغياناً وضلالا، وسيردد الكفار قولهم للمؤمنين: أى الفريقين خير مقاماً وأحسن نديا؟ إلى أن يشاهدوا ما يوعدون: إما تعذيب المسلمين إياهم فى الدنيا بالقتل والأسر، وإما خزى القيامة لهم، فحينئذ يعلمون أنهم شر منزلا وأضعف أنصارا.',NULL,'452',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2329,'<span data-type="title" id=toc-2348>٧٦ </span>- أما المؤمنون بآيات الله، فحينما يسمعونها يقبلون عليها، ويزيدهم الله بها توفيقاً لحسن العمل، والأعمال الصالحة خير وأبقى عند الله ثواباً وعاقبة.',NULL,'452',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2330,'<span data-type="title" id=toc-2349>٧٧ </span>- تعجب - أيها الرسول - من أمر الكافر بآيات الله، الذى فتنته دنياه، فأنكر البعث وقال - مستهزئاً -: إن الله سيعطينى فى الآخرة التى تزعمونها مالاً وولداً أعتز بهما هناك، وظن أن الآخرة كالدنيا، تقاس عليها، ونسى أنها جزاء الخير والشر، وأن الفضل فيها بالعمل الصالح.',NULL,'452',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2331,'<span data-type="title" id=toc-2350>٧٨ </span>- فهل اطلع ذلك الكافر على الغيب حتى يخبر عن صدق؟ ، وهل أخذ من الله عهداً بذلك حتى يتعلق بأمل؟ .',NULL,'452',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2332,'<span data-type="title" id=toc-2351>٧٩ </span>- فليرتدع عما يفتريه، فإننا نحصى عليه افتراءه، وسيصل عذابه ممدوداً مدَّا طويلاً لا يتصوره.',NULL,'452',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2333,'<span data-type="title" id=toc-2352>٨٠ </span>- سيسلبه الله ما يعتز به فى الدنيا من مال وولد، ويهلكه، ويأتى فى الآخرة وحيداً منفرداً، دون مال أو ولد أو نصير.',NULL,'452',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2334,'<span data-type="title" id=toc-2353>٨١ </span>- أولئك الكافرون اتخذوا غير الله آلهة مختلفة عبدوها، لتكون لهم شفعاء فى الآخرة.',NULL,'452',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2335,'<span data-type="title" id=toc-2354>٨٢ </span>- عليهم أن يرتدعوا عما يظنون، سيجحد الآلهة عبادتهم وينكرونها، ويكون هؤلاء المعبودون خصماً للمشركين يطالبون بتعذيبهم.',NULL,'452',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2336,'<span data-type="title" id=toc-2355>٨٣ </span>- ألم تعلم - أيها الرسول - أننا مكنا الشياطين من الكافرين - وقد استحوذت على هؤلاء الكافرين - تُغريهم وتدفعهم إلى التمرد على الحق فانقادوا لها.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2337,'<span data-type="title" id=toc-2356>٨٤ </span>- فلا يضق صدرك - أيها الرسول - بكفرهم، ولا تستعجل لهم العذاب، فإنما نتركهم فى الدنيا أمداً محدوداً ونحصى عليهم أعمالهم وذنوبهم، لنحاسبهم عليها فى الآخرة.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2338,'<span data-type="title" id=toc-2357>٨٥ </span>- اذكر - أيها الرسول - اليوم الذى نجمع فيه المتقين إلى جنة الرحمن وفوداً وجماعات مكرمين.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2339,'<span data-type="title" id=toc-2358>٨٦ </span>- وندفع فيه المجرمين إلى جهنم عطاشا، كاندفاع الدواب العطاش إلى الماء.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2340,'<span data-type="title" id=toc-2359>٨٧ </span>- ولا يملك الشفاعة فى هذا اليوم أحد إلا من يأذن الله تعالى له، لعهد كان له.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2341,'<span data-type="title" id=toc-2360>٨٨ </span>- لقد قال المشركون واليهود والنصارى: إن الله اتخذ ولدا من الملائكة أو من الناس.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2342,'<span data-type="title" id=toc-2361>٨٩ </span>- لقد أتيتم - أيها القائلون - بذلك القول أمراً منكراً، تنكره العقول المستقيمة.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2343,'<span data-type="title" id=toc-2362>٩٠ </span>- تكاد السموات يتشققن منه، وتنخسف الأرض، وتسقط الجبال قطعاً مفتتة.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2344,'<span data-type="title" id=toc-2363>٩١ </span>- وإنما تقرب حوادث السموات والأرض والجبال أن تقع، لأنهم سموا لله ولدا.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2345,'<span data-type="title" id=toc-2364>٩٢ </span>- وما يستقيم فى العقل أن يكون لله ولد، لأن إثبات الولد له يقتضى حدوثه وحاجته.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2346,'<span data-type="title" id=toc-2365>٩٣ </span>- ما كل من فى السموات والأرض إلا سيأتى الله سبحانه يوم القيامة عبداً خاضعاً لألوهيته.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2347,'<span data-type="title" id=toc-2366>٩٤ </span>- لقد أحاط علمه بهم جميعاً وبأعمالهم، فلا يخفى عليه أحد منهم ولا شئ من أعمالهم.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2348,'<span data-type="title" id=toc-2367>٩٥ </span>- وهم جميعاً يجيئون إليه يوم القيامة منفردين عن النصراء وعن الولد والمال.',NULL,'453',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2349,'<span data-type="title" id=toc-2368>٩٦ </span>- إن المؤمنين العاملين الصالحات يُحبهم الله، ويُحببهم إلى الناس.',NULL,'454',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2350,'<span data-type="title" id=toc-2369>٩٧ </span>- فإنما يسرنا القرآن بلغتك لتُبشر برضا الله ونعيمه من اتبع أوامره واجتنب نواهيه، وتُنذر بسخط الله وعذابه من كفر به واشتد فى خصومته.',NULL,'454',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2351,'<span data-type="title" id=toc-2370>٩٨ </span>- فلا يحزنك - أيها الرسول - عنادهم لك، فقد أهلك الله قبلهم كثيراً من الأمم والأجيال، لعنادهم ولكفرهم، ولقد اندثروا، فلا ترى منهم أحداً، ولا تسمع لهم صوتاً.',NULL,'454',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2352,'<span data-type="title" id=toc-2372>١ </span>- بدأ الله تعالى السورة بهذه الحروف لتحدى المنكرين، والإشارة إلى أن القرآن مُكَوَّن من هذه الحروف التى تتكلمون بها، ومع ذلك عجزتم عن الإتيان بسورة قصيرة أو آيات من مثله.',NULL,'455',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2353,'<span data-type="title" id=toc-2373>٢ </span>- إنا ما أوحينا إليك - أيها الرسول - هذا القرآن ليكون سبباً فى إرهاق نفسك أسفاً على إعراض المعرضين عنك.',NULL,'455',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2354,'<span data-type="title" id=toc-2374>٣ </span>- لكن أنزلناه تذكرة لمن يخاف الله فيطيعه.',NULL,'455',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2355,'<span data-type="title" id=toc-2375>٤ </span>- قد نزل عليك هذا القرآن من عند الله القادر خالق الأرض والسموات الرفيعة العالية.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2356,'<span data-type="title" id=toc-2376>٥ </span>- عظيم الرحمة على ملكه استوى.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2357,'<span data-type="title" id=toc-2377>٦ </span>- له - وحده - سبحانه ملك السموات وما فيها والأرض وما عليها، وملك ما بينهما، وما اختبأ فى الأرض من معادن وخيرات.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2358,'<span data-type="title" id=toc-2378>٧ </span>- وكما شملت قدرة الله - عَزَّ وَجَلَّ - كل شئ قد أحاط علمه بكل شئ، وإن ترفع صوتك - أيها الإنسان - بالقول، فإن الله يعلمه، لأنه يعلم حديثك مع غيرك ويعلم حديث نفسك.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2359,'<span data-type="title" id=toc-2379>٨ </span>- هو الله الإله الواحد المستحق للعبادة دون سواه؛ إذ هو المتصف بصفات الكمال، وله الصفات الحسنى.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2360,'<span data-type="title" id=toc-2380>٩ </span>- هل علمت - أيها النبى - خبر موسى مع فرعون؟',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2361,'<span data-type="title" id=toc-2381>١٠ </span>- حين أبصر ناراً فى مسيره ليلا من مدين إلى مصر، فقال عند ذلك لزوجه ومن معها: انتظروا فى مكانكم، إنى أبصرت ناراً، أرجو أن أحمل لكم منها جمرة تدفئكم، أو أجد حول النار من يهدينى إلى الطريق.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2362,'<span data-type="title" id=toc-2382>١١ </span>- فلما بلغ مكانها، سمع صوتاً عُلْوِيا يناديه: يا موسى.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2363,'<span data-type="title" id=toc-2383>١٢ </span>- إنى أنا الله ربك، فاخلع نعليك تكريماً للموقف، فإنك بالوادى المطهر المبارك وهو «طوى» .',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2364,'<span data-type="title" id=toc-2384>١٣ </span>- وأنا الله أصطفيك بالرسالة، فاصغ لما أوحيه إليك لتعلمه وتبلغه قومك.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2365,'<span data-type="title" id=toc-2385>١٤ </span>- إننى أنا الله الإله الواحد، لا معبود بحق سواى، فآمن بى واعبدنى، وداوم على إقامة الصلاة لتظل فى ذكر دائم بى.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2366,'<span data-type="title" id=toc-2386>١٥ </span>- إن الساعة - التى هى موْعد لقائى، وقد أخفيت موعدها عن عبادى، وأظهرت لهم دلالتها - آتية لا محالة، لتحاسب كل نفس على ما عملت وتُجزى به.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2367,'<span data-type="title" id=toc-2387>١٦ </span>- فلا يصرفنك يا موسى عن الإيمان بالساعة والاستعداد لها من لا يصدق بها، ومال مع هواه فتهلك.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2368,'<span data-type="title" id=toc-2388>١٧ </span>- وما تلك التى تمسكها بيدك اليمنى؟',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2369,'<span data-type="title" id=toc-2389>١٨ </span>- وأجاب موسى: إنها عصاى أعتمد عليها فى مسيرى، وأسوق بها غنمى، ولى فيها منافع أخرى، كدفع أذى الحيوان.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2370,'<span data-type="title" id=toc-2390>١٩ </span>- قال الله سبحانه لموسى: ارم بها على الأرض.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2371,'<span data-type="title" id=toc-2391>٢٠ </span>- فرمى بها موسى، ففوجئ بها تنقلب حية تمشى.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2372,'<span data-type="title" id=toc-2392>٢١ </span>- فارتاع منها، فطمأنه الله قائلا: تَناوْلها دون خوف، فإننا سنعيدها عصا كما كانت.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2373,'<span data-type="title" id=toc-2393>٢٢ </span>- وأَدْخِل يدك فى جيب ثوبك مضمومة إلى جنبك تخرج بيضاء ناصعة من غير داء، وقد جعلناها لك معجزة ثانية على رسالتك.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2374,'<span data-type="title" id=toc-2394>٢٣ </span>- لِنُرِيك بعض معجزاتنا الكبرى لتكون دليلا على صدقك فى الرسالة.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2375,'<span data-type="title" id=toc-2395>٢٤ </span>- اذهب إلى فرعون وادعه إلى الإيمان بالله الواحد الأحد، فإنه قد تجاوز الحد فى كفره وطغيانه.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2376,'<span data-type="title" id=toc-2396>٢٥ </span>- فتضرَّع موسى إلى ربه أن يشرح له صدره، ليذهب عنه الغضب، وليؤدى رسالة ربه.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2377,'<span data-type="title" id=toc-2397>٢٦ </span>- وسهِّل لى أمر الرسالة لأؤدى حقها.',NULL,'457',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2378,'<span data-type="title" id=toc-2398>٢٧ </span>- وفك عُقْدة لسانى لأبين.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2379,'<span data-type="title" id=toc-2399>٢٨ </span>- ليفهم الناس فهماً دقيقاً ما أقول لهم.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2380,'<span data-type="title" id=toc-2400>٢٩ </span>- واجعل لى مؤازراً من أهلى.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2381,'<span data-type="title" id=toc-2401>٣٠ </span>- هو أخى هارون.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2382,'<span data-type="title" id=toc-2402>٣١ </span>- اشدد به قوتى.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2383,'<span data-type="title" id=toc-2403>٣٢ </span>- وأشركه معى فى تحمل أعباء الرسالة وتبليغها.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2384,'<span data-type="title" id=toc-2404>٣٣ </span>- كى نُنَزِّهك كثيراً عما لا يليق بك.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2385,'<span data-type="title" id=toc-2405>٣٤ </span>- ونردد أسماءك الحسنى كثيراً.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2386,'<span data-type="title" id=toc-2406>٣٥ </span>- يا ربنا: إنك دائماً بصير بنا، ومتكفل بأمرنا.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2387,'<span data-type="title" id=toc-2407>٣٦ </span>- نادى الله رسوله موسى قائلا: قد أعطيتك ما سألت، وهذه منة عليك.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2388,'<span data-type="title" id=toc-2408>٣٧ </span>- ولقد سبق أن تفضلنا عليك بمنة أخرى دون سؤال منك.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2389,'<span data-type="title" id=toc-2409>٣٨ </span>- حين ألهمنا أمك إلهاماً كريماً كانت فيه حياتك.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2390,'<span data-type="title" id=toc-2410>٣٩ </span>- ألْهمناها أن تضعك - طفلا رضيعاً - فى الصندوق، وأن تلقى به فى النيل، لننجيك من قتل فرعون، إذ كان يقتل من يولد فى بنى إسرائيل من الذكور، وسخرنا الماء لِيُلْقى الصندوق بالشاطئ، وشاءت إرادتنا أن يأخذ الصندوق فرعون عدوى وعدوك، وأحببتك حب رحمة وولاية، ليحبك كل من يراك، ولتربى تربية كريمة ملحوظاً برعايتى.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2391,'<span data-type="title" id=toc-2411>٤٠ </span>- واعلم يا موسى سابق عنايتنا بك حين مشت أختك ترقب أمرك، فلما صرت فى قصر فرعون، ورأتهم يبحثون لك عن مُرْضِع دلَّتهم على أمك، فرددناك إليها لتفرح بحياتك وعودتك، ولتكف عن الحزن والبكاء، ولما كبرت وقتلت خطأ رجلا من قوم فرعون نجيناك من الغم الذى لحق بك، وخلَّصناك من شرهم، فذهبت إلى مدين ومكثت فيها سنين عدة، ثم عدت من مدين فى الموعد الذى قدرناه لإرسالك.',NULL,'459',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2392,'<span data-type="title" id=toc-2412>٤١ </span>- واصطفيتك لوحيى وحمل رسالتى.',NULL,'459',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2393,'<span data-type="title" id=toc-2413>٤٢ </span>- اذهب مع أخيك مُؤَيَّدين بمعجزاتى الدالة على النبوة والرسالة، ولا تضْعُفا فى تبليغ رسالتى، ولا تغفلا عن ذكرى والاستعانة بى.',NULL,'459',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2394,'<span data-type="title" id=toc-2414>٤٣ </span>- اذهب مع أخيك هارون إلى فرعون، إنه كافر تجاوز الحد فى كفره وطغيانه.',NULL,'459',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2395,'<span data-type="title" id=toc-2415>٤٤ </span>- فادعواه إلى الإيمان بى فى رفق ولين، راجين أن يتذكر ما غفل عنه من الإيمان، ويخشى عاقبة كفره وطغيانه.',NULL,'459',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2396,'<span data-type="title" id=toc-2416>٤٥ </span>- فتضرع موسى وهارون إلى الله قائلين: يا ربنا إننا نخشى أن يُبادرنا فرعون بالأذى، ويتجاوز الحد فى الإساءة.',NULL,'459',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2397,'<span data-type="title" id=toc-2417>٤٦ </span>- فطمأنهما الله بقوله: لا تخافا فرعون، إننى معكما بالرعاية والحفظ، سميع لما يقول، مبصر لما يفعل، فلا أمكِّنه من إيذائكما.',NULL,'459',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2398,'<span data-type="title" id=toc-2418>٤٧ </span>- فاذهبا إلى فرعون فقولا له: إننا رسولان إليك من ربك، جئنا ندعوك إلى الإيمان به، وأن تطلق بنى إسرائيل من الأسر والعذاب، قد أتيناك بمعجزة من الله تشهد لنا بصدق ما دعوناك إليه، وبالأمان من عذاب الله وغضبه لمن اتبع هداه.',NULL,'459',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2399,'<span data-type="title" id=toc-2419>٤٨ </span>- وإن الله قد أوحى إلينا أن عذابه الشديد واقع على من كذبنا وأعرض عن دعوتنا.',NULL,'459',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2400,'<span data-type="title" id=toc-2420>٤٩ </span>- قال فرعون فى طغيانه وجبروته: فَمَنْ ربكما يا موسى؟ .',NULL,'459',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2401,'<span data-type="title" id=toc-2421>٥٠ </span>- فأجابه موسى: ربنا الذى منح نعمة الوجود لكل موجود، وخلقه على الصورة التى اختارها سبحانه له، ووجَّهه لما خلق.',NULL,'460',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2402,'<span data-type="title" id=toc-2422>٥١ </span>- قال لفرعون: فما شأن القرون الماضية وما جرى لها؟ .',NULL,'460',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2403,'<span data-type="title" id=toc-2423>٥٢ </span>- قال موسى: عِلْمُ هذه القرون عند ربى - وحده - وهى مسجلة فى صحائف أعمالهم، لا يغيب عن علمه شئ منها ولا ينساه.',NULL,'460',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2404,'<span data-type="title" id=toc-2424>٥٣ </span>- هو الإله المتفضل على عباده بالوجود والحفظ، مهَّد لكم الأرض فبسطها بقدرته، وشق لكم فيها طرقاً تسلكونها، وأنزل المطر عليها تجرى به الأنهار فيها، فأخرج سبحانه أنواع النبات المختلفة المتقابلة فى ألوانها وطعومها ومنافعها، فمنها الأبيض، ومنها الأسود، ومنها الحلو، ومنها المر.',NULL,'460',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2405,'<span data-type="title" id=toc-2425>٥٤ </span>- ووجَّه - سبحانه - عباده إلى الانتفاع بما أخرج من النبات بالأكل ورعى الأنعام، ونحو ذلك. فذكر أن فى هذا الخلق وإبداعه والإنْعام به دلائل واضحة، يهتدى بها ذوو العقول إلى الإيمان بالله ورسالاته.',NULL,'460',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2406,'<span data-type="title" id=toc-2426>٥٥ </span>- ومن تراب هذه الأرض خلق الله آدم وذريته، وإليها يردّهم بعد الموت لمواراة أجسامهم، ومنها يخرجهم أحياء مرة أخرى للبعث والجزاء.',NULL,'460',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2407,'<span data-type="title" id=toc-2427>٥٦ </span>- ولقد أرينا فرعون على يد موسى المعجزات البيِّنة المؤيدة لرسالته وصدقه فى كل ما أخبره به عن الله وعن آثار قدرته، ومع هذا فقد تمادى فرعون فى كفره، فكذّب بكل ذلك، وأَبَى أن يؤمن به.',NULL,'460',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2408,'<span data-type="title" id=toc-2428>٥٧ </span>- قال فرعون لموسى: أجئتنا لتخرجنا من أرضنا، وتجعلها فى يد قومك بسحرك الذى فَتَنْت الناس به؟ .',NULL,'460',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2409,'<span data-type="title" id=toc-2429>٥٨ </span>- وإنا سنبطل سحرك من عندنا، فاجعل بيننا وبينك موعداً نلتقى فيه، ولا يتخلف منا أحد.',NULL,'461',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2410,'<span data-type="title" id=toc-2430>٥٩ </span>- فأجابه موسى: موعدنا يوم عيدكم الذى تتزينون فيه مبتهجين به، فيجتمع الناس فى ضحى ذلك اليوم، ليشهدوا ما يكون بيننا وبينكم.',NULL,'461',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2411,'<span data-type="title" id=toc-2431>٦٠ </span>- فانصرف فرعون وتولى الأمر بنفسه، فجمع وسائل تدبيره، وعِلْيَة من السحرة، وأدوات السحر، ثم حضر فى الموعد بكل ذلك.',NULL,'461',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2412,'<span data-type="title" id=toc-2432>٦١ </span>- قال لهم موسى يحذرهم هلاك الله وعذابه، وينهاهم عن اختلاق الكذب، بزعمهم ألوهية فرْعون وتكذيبهم رسل الله، وإنكارهم المعجزات، وهددهم بأن الله يستأصلهم بالعذاب إن استمروا على هذا، ويؤكد خسران من افترى الكذب على الله.',NULL,'461',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2413,'<span data-type="title" id=toc-2433>٦٢ </span>- فذعروا من تحذير موسى، وتفاوضوا سرا فيما بينهم متجاذبين وكلٌّ يشير برأى فيما يلْقَون به موسى.',NULL,'461',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2414,'<span data-type="title" id=toc-2434>٦٣ </span>- وأجمعوا فيما بينهم على أن موسى وهارون ساحران، يعملان على إخراجهم من بلادهم، بإخراج السلطان من أيديهم، وذلك بالسحر ليتمكن بنو إسرائيل فيها، وليبطلا عقيدتهم الطيبة فى زعمهم.',NULL,'461',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2415,'<span data-type="title" id=toc-2435>٦٤ </span>- فاجعلوا ما تكيدون به موسى أمراً متفقاً عليه، ثم احضروا مُصْطَفِّين، لتكون لكم فى نفوس الرائين الهيبة والغلبة، وقد فاز اليوم من غلب.',NULL,'461',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2416,'<span data-type="title" id=toc-2436>٦٥ </span>- واجه السحرة موسى برأى واحد، وخيَّروه فى شموخ واعتزاز، بَيْن أن يبدأ فيلقى عصاه، أو أن يكونوا هم البادئين.',NULL,'461',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2417,'<span data-type="title" id=toc-2437>٦٦ </span>- قال موسى: بل ابتدئوا، فألقوا حبالهم وعصيهم، فتخيَّل موسى من السحر أنها انقلبت ثعابين تتحرك وتسير.',NULL,'462',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2418,'<span data-type="title" id=toc-2438>٦٧ </span>- فأحس موسى بالخوف لِما رآه من أثر السحر، ومن احتمال أن يلتبس السحر على الناس بالمعجزة.',NULL,'462',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2419,'<span data-type="title" id=toc-2439>٦٨ </span>- فأدركه الله بلطفه قائلا: لا تخش شيئاً، إنك الغالب المنتصر على باطلهم.',NULL,'462',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2420,'<span data-type="title" id=toc-2440>٦٩ </span>- وأَلْق العصا التى بيمينك لتبتلع ما زوَّروا من السحر، إن صنيعهم لا يجاوز تمويه السحرة، وإن الساحر لا يفوز أينما كان.',NULL,'462',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2421,'<span data-type="title" id=toc-2441>٧٠ </span>- فألقى موسى عصاه، فإذا بها تنقلب حقاً بقدرة الله حية كبيرة مخيفة، وابتلعت كل ما أعدّوه، فلما رأى السحرة تلك المعجزة بادروا إلى السجود موقنين بصدق موسى قائلين: آمنا بالله - وحده - رب هارون وموسى، ورب كل شئ.',NULL,'462',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2422,'<span data-type="title" id=toc-2442>٧١ </span>- قال فرعون: كيف تؤمنون به دون إذن منى؟ إنه لرئيسكم الذى علَّمكم السحر، وليس عمله معجزة كما توهمتم، وهددهم: لأُقَطعَنّ أيديكم وأرجلكم مختلفات بِقَطْع اليمنى من واحدة واليسرى من الأخرى، ولأصلبنكم فى جذوع النخل، وستعلمون أى الإلهين أشد عذاباً وأدوم زمنا: أنا أم إله موسى.',NULL,'462',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2423,'<span data-type="title" id=toc-2443>٧٢ </span>- ثبت السحرة على إيمانهم، ودفعوا تهديد فرعون بقولهم: لن نبقى على الكفر معك بعدما تبيَّن لنا الحق فى معجزة موسى، ولن نختارك على إله موسى الذى خلقنا، فافعل ما تريد أن تفعله، إن سلطانك لا يتجاوز هذه الحياة القصيرة.',NULL,'462',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2424,'<span data-type="title" id=toc-2444>٧٣ </span>- فإننا مقيمون على الإيمان بربنا الحق، ليتجاوز لنا عمَّا سلف من السيئات، وليغفر لنا ممارسة السحر الذى أكرهتنا على تعلمه والعمل به، وربنا خير منك ثواباً، إذا أُطيع، وأبقى منك سلطاناً وقدرة على الجزاء.',NULL,'463',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2425,'<span data-type="title" id=toc-2445>٧٤ </span>- إن من يموت على الكفر ويلقى الله مجرماً فجزاؤه جهنم لا يموت فيها فيستريح من العذاب، ولا يحيا حياة يتمتع فيها بنعيم.',NULL,'463',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2426,'<span data-type="title" id=toc-2446>٧٥ </span>- ومن يلاقى ربه على الإيمان وصالح العمل فله المنازل السامية.',NULL,'463',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2427,'<span data-type="title" id=toc-2447>٧٦ </span>- تلك المنازل هى الإقامة فى جنات النعيم تجرى بين أشجارها الأنهار خالدين فيها، وذلك جزاء لمن طهر نفسه بالإيمان والطاعة بعد الكفر والمعصية.',NULL,'463',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2428,'<span data-type="title" id=toc-2448>٧٧ </span>- ثم تتابعت الأحداث بين موسى وفرعون، وأوحى الله إلى رسوله موسى أن يخرج ببنى إسرائيل من مصر ليلا، وأن يضرب البحر بعصاه فتحدث معجزة أخرى، إذ يفتح له الطريق يبساً فى الماء، وطمأنه ألا يخاف من إدراك فرعون لهم، ولا أن يغرقهم الماء.',NULL,'463',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2429,'<span data-type="title" id=toc-2449>٧٨ </span>- فنفَّذ موسى ما أمر الله به، فخرج فرعون بجنوده وراءه، فأدركهم عند البحر، وسار وراءهم فى الطريق التى تفتحت فى البحر لموسى وقومه، وهنا تحققت المعجزة الأخرى، وهى انطباق مياه البحر على فرعون وقومه، فأغرقتهم جميعاً.',NULL,'463',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2430,'<span data-type="title" id=toc-2450>٧٩ </span>- وهكذا انحرف بقومه عن طريق الحق، وغرر بهم، فهلكوا جميعاً.',NULL,'463',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2431,'<span data-type="title" id=toc-2451>٨٠ </span>- يا بنى إسرائيل، قد أنجيناكم من عدوكم فرعون، وواعدناكم بالنجاة من عدوكم على لسان موسى أن تصلوا آمنين إلى جانب الطور، ونزّلنا عليكم المن والسلوى رزقاً طيباً من الحلو ولحم الطير الشهى.',NULL,'463',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2432,'<span data-type="title" id=toc-2452>٨١ </span>- كلوا من هذه الطيبات التى رزقتم بها دون مجهود، ولا تظلموا، ولا ترتكبوا معصية الله فى هذا العيش الرغيد، حتى لا ينزل بكم غضبى، فإن من ينزل عليه غضبى ينحدر إلى أسفل الطبقات من عذاب الله.',NULL,'464',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2433,'<span data-type="title" id=toc-2453>٨٢ </span>- وإنى عظيم الغفران لمن رجع عن كفره، وأحسن الإيمان، وأصلح العمل، واستمر على ذلك حتى يلقى الله.',NULL,'464',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2434,'<span data-type="title" id=toc-2454>٨٣ </span>- سبق موسى قومه إلى الطور، ليظفر بمناجاة ربه، فسأله الله عن السبب الذى أعجله بالحضور دون قومه.',NULL,'464',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2435,'<span data-type="title" id=toc-2455>٨٤ </span>- قال موسى: إن قومى قريبون منى، لاحقون بى، وإنما سبقتهم إليك يا رب رغبة فى رضاك.',NULL,'464',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2436,'<span data-type="title" id=toc-2456>٨٥ </span>- قال الله له: إنَّا قد امتحنا قومك من بعد مغادرتك لهم، فوقعوا فى فتنة، إذْ أضلّهم السامرى.',NULL,'464',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2437,'<span data-type="title" id=toc-2457>٨٦ </span>- فعاد موسى إلى قومه فى غضب شديد وحزن مؤلم، وخاطب قومه - منكراً عليهم - بقوله: لقد وعدكم ربكم النجاة والهداية بنزول التوراة، والنصر بدخول الأرض المقدسة، ولم يطل عليكم العهد حتى تنسوا وعد الله لكم، أردتم بسوء صنيعكم أن ينزل بكم غضب الله بطغيانكم الذى حذركم منه، فأخلفتم عهدكم لى بالسير على سنتى والمجئ على أثرى.',NULL,'464',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2438,'<span data-type="title" id=toc-2458>٨٧ </span>- قال قوم موسى معتذرين: لم نتخلف عن موعدك باختيارنا، ولكننا حُمِّلنا حين خرجنا من مصر أثقالا من حِلْى القوم، ثم رأينا - لشؤمها علينا - أن نتخلص منها، فأشعل السامرى النار فى حفرة ورمينا فيها هذه الأثقال، فكذلك رمى السامرى ما معه من الحلى.',NULL,'465',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2439,'<span data-type="title" id=toc-2459>٨٨ </span>- فصنع السامرى لهم عجلا مجسماً من الذهب، يمر الريح فى جوفه فيكون له صوت يسمع كخوار البقر، لتتم الخديعة به، ودعاهم إلى عبادته فاستجابوا، وقال هو وأتباعه: هذا معبودكم ومعبود موسى. فنسى أنه يسهل بالتأمل والاستدلال على أن العجل لا يكون إلهاً.',NULL,'465',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2440,'<span data-type="title" id=toc-2460>٨٩ </span>- لقد عميت بصائرهم حين يعتبرون هذا العجل إلهاً {أفلا يرون أنه لا يرد على أقوالهم، ولا يستطيع أن يدفع عنهم ضراً، ولا أن يجلب لهم نفعاً} ؟',NULL,'465',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2441,'<span data-type="title" id=toc-2461>٩٠ </span>- وكان هارون مقيماً فيهم - حين قيام هذه الفتنة - ولقد قال لهم قبل رجوع موسى - عليه السلام -: يا قوم، لقد وقعتم فى فتنة السامرى بهذا الباطل، وإن إلهكم الحق هو الله الرحمن دون سواه، فاتبعونى فيما أنصحكم به، وامتثلوا رأيى بالامتناع عن هذه الضلالة.',NULL,'465',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2442,'<span data-type="title" id=toc-2462>٩١ </span>- قالوا: سنظل مستمرين على عبادة هذا العجل إلى أن يعود موسى إلينا! .',NULL,'465',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2443,'<span data-type="title" id=toc-2463>٩٢ </span>- قال موسى متأثراً بما علمه ورآه من قومه: يا هارون، أى سبب منعك أن تكفهم عن الضلالة إذ رأيتهم وقعوا فيها؟',NULL,'465',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2444,'<span data-type="title" id=toc-2464>٩٣ </span>- ولم تقم مقامى بنصحهم كما عهدت إليك، أفلا تتبعنى فيما عهدت به إليك أم هل عصيت أمرى؟ .',NULL,'465',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2445,'<span data-type="title" id=toc-2465>٩٤ </span>- قال هارون لموسى: يا ابن أمى: لا تعاجلنى بغضبك، ولا تمسك بلحيتى ولا برأسى. لقد خفت إن شددت عليهم فتفرقوا شيعاً وأحزاباً أن تقول لى: فرقت بين بنى إسرائيل، ولم تخلفنى فيهم كما عهدت إليك.',NULL,'465',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2446,'<span data-type="title" id=toc-2466>٩٥ </span>- قال موسى - عليه السلام - للسامرى: ما هذا الأمر الخطير الذى يُعد خَطْباً ووقعت فيه؟! .',NULL,'466',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2447,'<span data-type="title" id=toc-2467>٩٦ </span>- قال السامرى لموسى: عرفت من حذق الصناعة وحِيَلِها ما لم يعلمه بنو إسرائيل، وصنعت لهم صورة عجل له هذا الصوت، وقبضت قبضة من أثر الرسول فألقيتها فى جوف العجل، تمويهاً على الناس، وكذلك زَيَّنت لى نفسى أن أفعل ما فعلت.',NULL,'466',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2448,'<span data-type="title" id=toc-2468>٩٧ </span>- قال موسى للسامرى: اخرج من جماعتنا، وابعد عنا، وإن جزاءك فى الدنيا أن تهيم على وجهك، وينفر الناس منك، حتى لا تكون بينك وبينهم صلة، فلا يقربك أحد، ولا تقترب أنت من أحد، وإن لعذابك فى الآخرة موعداً محدداً لا تستطيع الفرار منه، وندد موسى به وبإلهه قائلا: انظر الآن ماذا نصنع بإلهك الذى عكفت على عبادته، وفتنت الناس به، لنحرقنه ثم لنذروه فى البحر ذروا.',NULL,'466',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2449,'<span data-type="title" id=toc-2469>٩٨ </span>- وقام موسى بإنجاز ما قال، ثم اتجه إلى بنى إسرائيل بعد هذه العبرة قائلا لهم: إن إلهكم الواحد، هو الذى لا يُعبد بحق سواه، وقد أحاط علمه بكل شئ مما كان ومما سيكون.',NULL,'466',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2450,'<span data-type="title" id=toc-2470>٩٩ </span>- كما قصصنا عليك - أيها الرسول - نبأ موسى، نخبرك بالحق عن الأمم السابقة، وقد أنزلنا عليك من عندنا كتاباً فيه تذكير لك ولأمتك، بما فيه صلاح دينكم ودنياكم.',NULL,'466',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2451,'<span data-type="title" id=toc-2471>١٠٠ </span>- من انصرف عن تصديقه والاهتداء به فإنه يضل فى حياته، ويأتى يوم القيامة حاملا إثم ما صنع، ويجازى بالعذاب الشديد.',NULL,'466',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2452,'<span data-type="title" id=toc-2472>١٠١ </span>- ويخْلد فى هذا العذاب، وبئس هذا الحمل السيئ يوم القيامة.',NULL,'466',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2453,'<span data-type="title" id=toc-2473>١٠٢ </span>- اذكر - أيها الرسول - لأمتك اليوم الذى نأمر فيه المَلَك أن ينفخ فى الصور (البوق) نفخة الإحياء والبعث من القبور، وندعوهم إلى المحشر، ونسوق المجرمين إلى الموقف زرق الوجوه رعباً وفزعاً.',NULL,'467',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2454,'<span data-type="title" id=toc-2474>١٠٣ </span>- يتهامسون فيما بينهم فى ذلة واضطراب عن قِصَر الحياة الدنيا، حتى كأنهم لم ينعموا بها، ولم يلبثوا فيها إلا عشرة أيام.',NULL,'467',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2455,'<span data-type="title" id=toc-2475>١٠٤ </span>- وليس تهامسهم خافياً، فنحن أعلم بما يتهامسون به، وبما يقول أقربهم إلى تصوير شعورهم نحو الدنيا بأنها لم تكن إلا كيوم واحد.',NULL,'467',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2456,'<span data-type="title" id=toc-2476>١٠٥ </span>- ويسألك المنكرون للبعث - أيها الرسول - عن مصير الجبال يوم القيامة الذى تتحدث عنه، فأجِبْهم بأن الله يفتِّتها كالرمل، ثم يطيِّرها بالرياح فتكون هباء.',NULL,'467',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2457,'<span data-type="title" id=toc-2477>١٠٦ </span>- فيدع أماكنها من الأرض بعد نسفها ملساء مستوية.',NULL,'467',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2458,'<span data-type="title" id=toc-2478>١٠٧ </span>- لا تبصر فى الأرض انخفاضاً ولا ارتفاعاً، كأنها لم تكن معمورة من قبل.',NULL,'467',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2459,'<span data-type="title" id=toc-2479>١٠٨ </span>- يوم القيامة يتبع الناس بعد قيامهم من قبورهم دعوة الداعى إلى المحشر مستسلمين، لا يستطيع أحد منهم أن يعدل عنه يميناً ولا شمالاً، وتخشع الأصوات بالسكون والرهبة لعظمة الرحمن، فلا يُسمَع إلا صوت خفى.',NULL,'467',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2460,'<span data-type="title" id=toc-2480>١٠٩ </span>- يومئذ لا تنفع الشفاعة من أحد إلا من أكرمه الله فأذن له بالشفاعة ورضى قوله فيها، ولا تنفع الشفاعة فى أحد إلا من أذن الرحمن فى أن يُشفع له وكان مؤمناً، ورضى الله قوله بالتوحيد والإيمان.',NULL,'467',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2461,'<span data-type="title" id=toc-2481>١١٠ </span>- والله - جل شأنه - يعلم ما تقدم من أمورهم فى دنياهم، وما يستقبلونه منها فى أُخراهم، فهو سبحانه يُدبِّر الأمر فيهم بمقتضى علمه، وهم لا يحيطون علماً بتدبيره وحكمته.',NULL,'467',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2462,'<span data-type="title" id=toc-2482>١١١ </span>- وذَلّت وجوه فى هذا اليوم، وخضعت للحى الذى لا يموت، القائم بتدبير أمور خلقه، وقد خسر النجاة والثواب فى اليوم الآخر من ظلم نفسه فى الدنيا فأشرك بربه.',NULL,'468',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2463,'<span data-type="title" id=toc-2483>١١٢ </span>- ومن يعمل من الطاعات وهو مصدق بما جاء به - محمد صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - فهو لا يخاف أن يزاد فى سيئاته، أو ينقص من حسناته.',NULL,'468',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2464,'<span data-type="title" id=toc-2484>١١٣ </span>- ومثل هذا البيان الحق الذى سلف فى هذه السورة - فى تمجيد الله وقصة موسى، وأخبار القيامة - أنزل الله هذا الكتاب قرآناً عربى البيان، وصرف القول فى أساليب الوعيد ووجوهه، لينتهوا عما هم فيه من العصيان، وليجدد القرآن لهم عظة واعتبارا.',NULL,'468',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2465,'<span data-type="title" id=toc-2485>١١٤ </span>- فارتفع عن الظنون، وتنزَّه عن مشابهة الخلق، المَلِكُ الذى يحتاج إليه الحاكمون والمحكومون، المحق فى ألوهيته وعظمته، ولا تعجل يا محمد بقراءة القرآن من قبل أن يفرغ المَلَك من إلقائه إليك، وقل: رب زدنى علما بالقرآن ومعانيه.',NULL,'468',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2466,'<span data-type="title" id=toc-2486>١١٥ </span>- ولقد وصينا آدم - أيها الرسول - من أول أمره، ألا يخالف لنا أمراً، فنسى العهد وخالف، ولم نجد له أول أمره عزماً وثيقاً، وتصميماً قوياً يمنع من أن يتسلل الشيطان إلى نفسه بوسوسته.',NULL,'468',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2467,'<span data-type="title" id=toc-2487>١١٦ </span>- واذكر - أيها الرسول - حين أمر الله الملائكة بتعظيم آدم على وجه أراده سبحانه، فامتثلوا، لكن إبليس - وهو معهم وكان من الجن - خالف وامتنع، فأخرج وطُرِد!',NULL,'468',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2468,'<span data-type="title" id=toc-2488>١١٧ </span>- فخاطب الله آدم قائلا: إن هذا الشيطان الذى خالف أمرنا فى تعظيمك عدو لك ولحواء - زوجتك - فاحذروا وسوسته بالمعصية، فيكون سبباً فى خروجكما من الجنة، فتشقى يا آدم فى الحياة بعد الخروج من الجنة.',NULL,'468',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2469,'<span data-type="title" id=toc-2489>١١٨ </span>- إن علينا أن نكفل لك مطالب حياتك فى الجنة، فلن يُصيبك فيها جوع ولا عِرْى.',NULL,'469',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2470,'<span data-type="title" id=toc-2490>١١٩ </span>- وأنه لن يصيبك فيها عطش، ولن تتعرض فيها لحر الشمس، كما هو شأن الكادحين فى خارج الجنة.',NULL,'469',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2471,'<span data-type="title" id=toc-2491>١٢٠ </span>- فاحتال عليه الشيطان يهمس فى نفسه، مُرَغِّباً له ولزوجه فى الأكل من الشجرة المنهى عنها، قائلا: أنا أدلك يا آدم على شجرة، من أكل منها رزق الخلود، ورزق مُلكاً لا يَفنى.',NULL,'469',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2472,'<span data-type="title" id=toc-2492>١٢١ </span>- ودلَّه على الشجرة المحرَّمة، فخُدع آدم وزوجه بإغراء إبليس، ونسيا نهى الله، وأكلا منها، فظهرت لهما عوراتهما، جزاء طمعهما، حتى نسيا ووقعا فى مخالفته، وصارا يقطعان من ورق شجر الجنة ويستران ما بدا منهما، وخالف آدم ربه، وكان ذلك قبل النبوة، فَحُرِم الخلود الذى تمناه وفسد عيشه.',NULL,'469',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2473,'<span data-type="title" id=toc-2493>١٢٢ </span>- ثم اصطفاه الله للرسالة، فقبل توبته، وهداه إلى الاعتذار والاستغفار.',NULL,'469',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2474,'<span data-type="title" id=toc-2494>١٢٣ </span>- أمر الله آدم وزوجه أن يخرجا من الجنة ويهبطا إلى الأرض، وأخبرهما سبحانه بأن العداوة ستكون فى الأرض بين ذريتهما، وأنه سبحانه سيمدهم بالهدى والرشاد، فمن اتَّبع منهم هدى الله فلا يقع فى المآثم فى الدنيا، ولا يشقى بالعذاب.',NULL,'469',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2475,'<span data-type="title" id=toc-2495>١٢٤ </span>- ومن أعرض عن هدى الله وطاعته فإنه يحيا حياة لا سعادة فيها، فلا يقنع بما قسم الله، ولا يستسلم إلى قضاء الله وقدره، حتى إذا كان يوم القيامة جاء إلى موقف الحساب مأخوذاً بذنبه، عاجزاً عن الحجة التى يعتذر بها، كما كان فى دنياه أعمى البصيرة عن النظر فى آيات الله.',NULL,'469',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2476,'<span data-type="title" id=toc-2496>١٢٥ </span>- وفى هذا الموقف يسأل ربه فى فزع: يا رب كيف أنسيتنى الحُجة، وأعجزتنى عن المعذرة، ووقفتنى كالأعمى؟ {وقد كنت فى الدنيا أُبصر ما حولى وأجادل وأدافع.',NULL,'470',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2477,'<span data-type="title" id=toc-2497>١٢٦ </span>- الأمر فى شأنك كما وقع: جاءتك دلائلنا ورسلنا فى الدنيا فنسيتها، وتعاميت عنها، ولم تؤمن بها، وكذلك اليوم تترك منسيا فى العذاب والهوان.',NULL,'470',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2478,'<span data-type="title" id=toc-2498>١٢٧ </span>- ومثل هذا الجزاء السيئ نجزى فى الدنيا من أقبل على المعصية، وكذب بالله وآياته، وإن عذاب الآخرة لأشد ألماً، وأدْوَم مما كان فى الدنيا.',NULL,'470',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2479,'<span data-type="title" id=toc-2499>١٢٨ </span>- كيف يتعامون عن آيات الله، وقد تبين لهم إهلاكنا لكثير من الأمم السالفة بسبب كفرهم، ولم يتعظوا بهم مع أنهم يمشون فى ديارهم ومساكنهم، ويشهدون آثار ما حل بهم من العذاب؟} وإن فى تلك المشاهد لعظات لأصحاب العقول الراجحة.',NULL,'470',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2480,'<span data-type="title" id=toc-2500>١٢٩ </span>- ولولا حكم سبق من ربك بتأخير العذاب عنهم إلى أجل مسمى - هو القيامة - لكان العذاب لازماً لهم فى الدنيا كما لزم كفار القرون الماضية.',NULL,'470',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2481,'<span data-type="title" id=toc-2501>١٣٠ </span>- فاصبر - أيها الرسول - على ما يقولونه فى رسالتك من تكذيب واستهزاء، ونَزِّه ربك عما لا يليق به بالثناء عليه، وعبادته - وحده - دائماً، وخاصة قبل أن تشرق الشمس وقبل أن تغرب، ونزِّهه واعبده فى ساعات الليل، وفى أطراف النهار بالصلاة، حتى تدوم صلتك بالله، فلتطمئن إلى ما أنت عليه، وترضى بما قدر لك.',NULL,'470',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2482,'<span data-type="title" id=toc-2502>١٣١ </span>- ولا تتعَدَّ بنظرك إلى ما متَّعنا به أصنافاً من الكافرين، لأن هذا المتاع زينة الحياة الدنيا وزخرفها، يمتحن الله به عباده فى الدنيا، ويدِّخر الله لك فى الآخرة ما هو خير وأبقى من هذا المتاع.',NULL,'470',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2483,'<span data-type="title" id=toc-2503>١٣٢ </span>- ووجِّه أهلك إلى أن يؤدُّوا الصلاة فى أوقاتها، فالصلاة أقوى ما يصلهم بالله، وداوم على إقامتها كاملة، لا نكلفك رزق نفسك فنحن متكفلون برزقك، وإن العاقبة الحميدة فى الدنيا والآخرة مكفولة لأهل الصلاح والتقوى.',NULL,'471',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2484,'<span data-type="title" id=toc-2504>١٣٣ </span>- وقال الكافرون فى عنادهم: لماذا لا يأتينا محمد بدليل من ربه يَلزمُنا الإيمان به؟ {فكيف يجحدون القرآن - وقد جاءهم به مشتملا على ما فى الكتب السابقة من أنباء الأمم الماضية، وإهلاكهم بسبب تكذيب الرسل - وليس محمد بِدْعاً فى ذلك}',NULL,'471',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2485,'<span data-type="title" id=toc-2505>١٣٤ </span>- ولو عاجل الله هؤلاء الكافرين بالإهلاك قبل أن يرسل إليهم محمداً لاعتذروا يوم القيامة قائلين: يا ربنا لم ترسل إلينا رسولا فى الدنيا مؤيداً بالآيات لنتبعه قبل أن ينزل بنا العذاب والخزى فى الآخرة. ولكن لا عذر لهم الآن بعد إرسال الرسول.',NULL,'471',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2486,'<span data-type="title" id=toc-2506>١٣٥ </span>- قل - أيها الرسول - لهؤلاء المعاندين: إننا جميعاً منتظرون لما يؤول إليه أمرنا وأمركم، وستعلمون حقاً أى الفريقين صاحب الدين الحق والمهتدى بهدى الله؟ .',NULL,'471',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2487,'<span data-type="title" id=toc-2508>١ </span>- دنا للمشركين وقت حسابهم يوم القيامة، وهم غافلون عن هوله، معرضون عن الإيمان به.',NULL,'472',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2488,'<span data-type="title" id=toc-2509>٢ </span>- ما يأتيهم قرآن من ربهم مُجدَّد نزوله، مذكر لهم بما ينفعهم، إلا استمعوه وهم مشغولون عنه بما لا نفع فيه، يلعبون كما يلعب الأطفال.',NULL,'472',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2489,'<span data-type="title" id=toc-2510>٣ </span>- لاهية قلوبهم عن التأمل فيه، وبالغوا فى إخفاء تآمرهم على النبى وعلى القرآن، قائلين فيما بينهم: ما محمد إلا بشر مثلكم، والرسول لا يكون إلا مَلَكا. أتصدقون محمداً فتحضرون مجلس السحر وأنتم تشاهدون أنه سحر؟ {',NULL,'473',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2490,'<span data-type="title" id=toc-2511>٤ </span>- قال الرسول لهم - وقد أطلعه الله على حديثهم الذى أسروه -: ربى يعلم كل ما يقال فى السماء والأرض، وهو الذى يَسمع كل ما يُسمع، ويعلم كل ما يقع.',NULL,'473',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2491,'<span data-type="title" id=toc-2512>٥ </span>- بل قالوا: إنه أخلاط أحلام رآها فى المنام، بل اختلقه ونسبه كذباً إلى الله. ثم أعرضوا عن ذلك، وقالوا: بل هو شاعر يستولى على نفوس سامعيه، فليأتنا بمعجزة مادية دالة على صدقه، كما أُرسل الأنبياء الأولون مؤيدون بالمعجزات.',NULL,'473',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2492,'<span data-type="title" id=toc-2513>٦ </span>- لم تؤمن قبلهم أمة من الأمم التى أهلكناها بعد أن كذبت بالمعجزات المادية، فهل يؤمن هؤلاء إذا جاءهم ما يطلبون؟} .',NULL,'473',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2493,'<span data-type="title" id=toc-2514>٧ </span>- وما أرسلنا إلى الناس قبلك - أيها النبى - إلا رجالا من البشر، نوحى إليهم الدين ليبلغوه الناس، فاسألوا - أيها المنكرون - أهلَ العلم بالكتب المنزلة إن كنتم لا تعلمون ذلك.',NULL,'473',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2494,'<span data-type="title" id=toc-2515>٨ </span>- وما جعلنا الرسل أجساداً تخالف أجساد البشر يعيشون دون طعام، وما كانوا باقين مخلدين.',NULL,'473',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2495,'<span data-type="title" id=toc-2516>٩ </span>- ثم صدقناهم، وحققنا لهم الوعد، فأنجيناهم وأنجينا معهم من أردنا نجاتهم من المؤمنين، وأهلكنا الكافرين المسرفين فى تكذيبهم وكفرهم برسالة أنبيائهم.',NULL,'473',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2496,'<span data-type="title" id=toc-2517>١٠ </span>- لقد أنزلنا إليكم كتاباً فيه تذكير لكم إذا علمتموه وعملتم بما فيه، فكيف تعرضون وتكفرون به؟! أيبلغ بكم العناد والحمق إلى ما أنتم عليه فلا تعقلون ما ينفعكم فتسارعون إليه؟',NULL,'473',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2497,'<span data-type="title" id=toc-2518>١١ </span>- وكثير من أهل القرى أهلكناهم بسبب كفرهم وتكذيبهم لأنبيائهم، وأنشأنا بعد كل قوم منهم قوماً غيرهم أحسن منهم حالا ومآلا.',NULL,'474',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2498,'<span data-type="title" id=toc-2519>١٢ </span>- فلما أردنا إهلاكهم، وأحسوا بما يقع عليهم من شدة عذابنا وقدرتنا على إنزاله؛ سارعوا إلى الهرب والتماس النجاة بما يشبه عمل الدواب.',NULL,'474',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2499,'<span data-type="title" id=toc-2520>١٣ </span>- لا تسرعوا - أيها المنكرون - فلن يعصمكم من عذاب الله شئ، وارجعوا إلى ما كنتم فيه من نعيمكم ومساكنكم، لعل خدمكم وأشياعكم يسألونكم المعونة والرأى كما كان شأنكم، وأنَّى تستطيعون؟ .',NULL,'474',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2500,'<span data-type="title" id=toc-2521>١٤ </span>- قالوا - وقد سمعوا الاستهزاء بهم منادين هلاكهم موقنين به -: إنا كنا ظالمين حين أعرضنا عما ينفعنا، ولم نؤمن بآيات ربنا.',NULL,'474',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2501,'<span data-type="title" id=toc-2522>١٥ </span>- فما زالت هذه الكلمات يرددونها ويصيحون بها، حتى جعلناهم - بالعذاب - كالزرع المحصود خامدين لا حياة فيهم.',NULL,'474',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2502,'<span data-type="title" id=toc-2523>١٦ </span>- وما خلقنا السماء والأرض وما بينهما - بهذا النظام المحكم والصنع البديع - نلعب بها، بل جعلناها لحِكمٍ عالية يدركها المتأملون.',NULL,'474',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2503,'<span data-type="title" id=toc-2524>١٧ </span>- لو أردنا أن نتخذ ما نلْهُو به لما أمكن أن نتخذه إلا من مُلكنا الذى ليس فى الوجود مُلك غيره، إن كنا ممن يفعل ذلك، ولسنا ممن يفعله لاستحالته فى حقنا.',NULL,'474',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2504,'<span data-type="title" id=toc-2525>١٨ </span>- بل أَمْرُنا الذى يليق بنا هو أن نقذف الحق فى وجه الباطل فَيُذْهِبه، ولكم - أيها الكافرون - الهلاك بسبب افترائكم على الله ورسوله.',NULL,'474',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2505,'<span data-type="title" id=toc-2526>١٩ </span>- ولله - وحده - كل من فى السموات والأرض خَلقاً ومُلكاً، فمن حقه - وحده - أن يُعبد، والمقرَّبون إليه من الملائكة لا يستكبرون عن عبادته والخضوع له، ولا يشعرون بالإعياء والملل من طول عبادته بالليل والنهار.',NULL,'474',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2506,'<span data-type="title" id=toc-2527>٢٠ </span>- يُنَزّهونه جل شأنه عما لا يليق به، لا يتخلل تنزيههم هذا فُتُور، بل هو تنزيه دائم لا يشغلهم عنه شاغل.',NULL,'475',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2507,'<span data-type="title" id=toc-2528>٢١ </span>- لم يفعل المشركون ما يفعله المقربون من إخلاص العبادة لله، بل عبدوا غيره، واتخذوا من الأرض آلهة لا تستحق أن تعبد، وكيف يُعْبَد من دون الله من لا يستطيع إعادة الحياة؟! .',NULL,'475',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2508,'<span data-type="title" id=toc-2529>٢٢ </span>- لو كان فى السماء والأرض آلهة غير الله تُدبِّر أمرهما لاختلَّ النظام الذى قام عليه خلقهما، ولما بلغ غاية الدقة والإحكام، فتنزيهاً لله صاحبِ الملك عما ينسبه إليه المشركون.',NULL,'475',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2509,'<span data-type="title" id=toc-2530>٢٣ </span>- لا يُحاسب - سبحانه - ولا يُسأل عما يفعل، لأنه الواحد المتفرد بالعزة والسلطان، الحكيم العليم، فلا يخطئ فى فعل أى شئ، وهم يُحاسبون ويُسألون عما يفعلون؛ لأنهم يخطئون لضعفهم وجهلهم وغلبة الشهوة عليهم.',NULL,'475',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2510,'<span data-type="title" id=toc-2531>٢٤ </span>- لم يعرفوا حق الله عليهم، بل اتخذوا من غيره آلهة يعبدونها دون دليل معقول أو برهان صادق. قل - أيها النبى - هاتوا برهانكم على أن لله شريكاً فى الملك يبرر إشراكه فى العبادة. هذا القرآن الذى جاء مذكراً لأمتى بما يجب عليها، وهذه كتب الأنبياء التى جاءت لتذكِّر الأمم قبلى تقوم كلها على توحيد الله. بل أكثرهم لا يعلمون ما جاء فى هذه الكتب، لأنهم لم يهتموا بالتأمل فيها، فهم معرضون عن الإيمان بالله.',NULL,'475',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2511,'<span data-type="title" id=toc-2532>٢٥ </span>- وما أرسلنا إلى الناس قبلك - أيها النبى - رسولا ما، إلا أوحينا إليه أن يبلِّغ أمته أنه لا يستحق العبادة غيرى، فأَخْلِصُوا لى العبادة.',NULL,'475',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2512,'<span data-type="title" id=toc-2533>٢٦ </span>- وقال بعض كفار العرب: اتخذ الرحمن ولدا بزعمهم أن الملائكة بناته. تنزَّه عن أن يكون له ولد. بل الملائكة عباد مُكْرمون عنده بالقرب منه، والعبادة له.',NULL,'475',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2513,'<span data-type="title" id=toc-2534>٢٧ </span>- لا يسبقون الله بكلمة يقولونها، قبل أن يأذن لهم بها، وهم بأمره - دون غيره - يعملون، ولا يتعدون حدود ما يأمرهم به.',NULL,'475',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2514,'<span data-type="title" id=toc-2535>٢٨ </span>- يعلم الله كل أحوالهم وأعمالهم - ما قدَّموه وما أخَّروه - ولا يشفعون عنده إلا لمن رضى الله عنه، وهم من شدة خوفهم من الله تعالى وتعظيمهم له فى حذرٍ دائم.',NULL,'476',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2515,'<span data-type="title" id=toc-2536>٢٩ </span>- ومن يقل من الملائكة: إنى إله يعبد من دون الله فذلك نجزيه جهنم. مثل هذا الجزاء نجزى كل الذين يتجاوزون حدود الحق، ويظلمون أنفسهم بالشرك وادعاء الربوبية.',NULL,'476',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2516,'<span data-type="title" id=toc-2537>٣٠ </span>- أَعَمِىَ الذين كفروا ولم يبصروا أن السموات والأرض كانتا فى بدء خلقهما ملتصقتين، فبقدرتنا فَصَلَنا كلا منهما عن الأخرى، وجعلنا من الماء الذى لا حياة فيه كل شئ حى؟! فهل بعد كل هذا يُعرضون، فلا يؤمنون بأنه لا إله غيرنا؟',NULL,'476',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2517,'<span data-type="title" id=toc-2538>٣١ </span>- ومن دلائل قدرتنا أنا جعلنا فى الأرض جبالا ثوابت، لئلا تضطرب بهم، وجعلنا فيها طرقاً فسيحة، ومسالك واسعة، لكى يهتدوا بها فى سيرهم إلى أغراضهم.',NULL,'477',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2518,'<span data-type="title" id=toc-2539>٣٢ </span>- وجعلنا السماء فوقهم كالسقف المرفوع، وحفظناها من أن تقع أو يقع ما فيها عليهم. وهم مع ذلك منصرفون عن النظر والاعتبار بآياتنا الدالة على قدرتنا، وحكمتنا، ورحمتنا.',NULL,'477',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2519,'<span data-type="title" id=toc-2540>٣٣ </span>- والله هو الذى خلق الليل والنهار، والشمس والقمر، كلٌ يجرى فى مجاله الذى قدَّره الله له، ويسبح فى فلكه لا يحيد عنه.',NULL,'477',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2520,'<span data-type="title" id=toc-2541>٣٤ </span>- وما جعلنا لأحد من البشر قبلك - أيها النبى - الخلود فى هذه الحياة حتى يتربص بك الكفار الموت. فكيف ينتظرون موتك ليشمتوا بك وهم سيموتون كما تموت؟! أفإن مت يبقى هؤلاء أحياء دون غيرهم من سائر البشر؟ .',NULL,'477',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2521,'<span data-type="title" id=toc-2542>٣٥ </span>- كل نفس لا بد أن تذوق الموت، ونعاملكم فى هذه الحياة معاملة المختَبر بما يصيبكم من نفع وضر، ليتميز الشاكر للخير والصابر على البلاء من الجاحد للنعم والجازع عند المصيبة. وإلينا ترجعون فنحاسبكم على أعمالكم.',NULL,'478',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2522,'<span data-type="title" id=toc-2543>٣٦ </span>- وإذا رآك - أيها النبى - الذين كفروا بالله وبما جئت به لا يضعونك إلا فى موضع السخرية والاستهزاء. يقول بعضهم لبعض: أهذا الذى يذكر آلهتكم بالعيب؟ وهم بذكر الله الذى يَعُمُّهم برحمته لا يصدقون.',NULL,'478',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2523,'<span data-type="title" id=toc-2544>٣٧ </span>- وإذا كانوا يطلبون التعجيل بالعذاب فإن طبيعة الإنسان التعجل، سأريكم - أيها المستعجلون - نعمتى فى الدنيا، وعذابى فى الآخرة فلا تشغلوا أنفسكم باستعجال ما لابد منه.',NULL,'478',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2524,'<span data-type="title" id=toc-2545>٣٨ </span>- ويقول الكافرون مستعجلين العذاب مستبعدين وقوعه: متى يقع هذا الذى تَتوعدُوننا به - أيها المؤمنون - إن كنتم صادقين فيما تقولون؟ .',NULL,'478',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2525,'<span data-type="title" id=toc-2546>٣٩ </span>- لو يعلم الذين كفروا بالله حالهم - حين لا يستطيعون أن يدفعوا عن وجوههم النار ولا عن ظهورهم، ولا يجدون من ينصرهم بدفعها؛ ما قالوا هذا الذى يقولونه.',NULL,'478',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2526,'<span data-type="title" id=toc-2547>٤٠ </span>- لا تأتيهم القيامة على انتظارٍ وتوقع، بل تأتيهم فجأة فتحيّرهم فلا يستطيعون ردَّها، ولا هم يُمْهَلون ليتوبوا ويعتذروا عما قدَّموا.',NULL,'478',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2527,'<span data-type="title" id=toc-2548>٤١ </span>- ولقد حدث للرسل قبلك أن استهزأ بهم الكفار من أممهم، فحَلَّ بالذين كفروا وسخروا من رسلهم العذاب الذى جعلوه مثال السخرية والاستهزاء.',NULL,'478',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2528,'<span data-type="title" id=toc-2549>٤٢ </span>- قل - أيها النبى - لهم: مَنْ يحفظكم فى الليل والنهار من نقمته ويرحمكم وينعم عليكم؟ لا أحد يستطيع ذلك، بل هم منصرفون عن القرآن - الذى يذكّرهم بما ينفعهم ويدفع العذاب عنهم.',NULL,'478',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2529,'<span data-type="title" id=toc-2550>٤٣ </span>- أَلَهُم آلهة تمنع العذاب من دوننا؟ كلا: إنهم لا يستطيعون أن يعينوا أنفسهم حتى يعينوا غيرهم. ولا أحد يستطيع أن يحفظ واحداً منهم فى جواره وصحبته إذا أردنا بهم العذاب والهلاك.',NULL,'478',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2530,'<span data-type="title" id=toc-2551>٤٤ </span>- لم نُعجل عقاب هؤلاء بكفرهم، بل استدرجناهم ومتّعناهم فى الحياة الدنيا كما متعنا آباءهم قبلهم حتى طال عليهم العمر. أيتعامون عما حولهم فلا يرون أنا نقصد الأرض فننقصها من أطرافها بالفتح ونصر المؤمنين؟ {أفهم الغالبون، أم المؤمنون الذين وعدهم الله بالنصر والتأييد؟ .',NULL,'479',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2531,'<span data-type="title" id=toc-2552>٤٥ </span>- قل - أيها النبى -: لا أحذركم بكلام من عندى، وإنما أحذركم بالوحى الصادر عن الله لى - وهو حق وصدق - وهم لطول إعراضهم عن صوت الحق ختم الله على سمعهم حتى صاروا كالصم، ولا يسمع الصم الدعاءَ حين يخوِّفون بالعذاب.',NULL,'479',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2532,'<span data-type="title" id=toc-2553>٤٦ </span>- وتأكد أنهم إن أصابتهم إصابة خفيفة من العذاب الذى يسخرون منه يصيحون من الهول قائلين: يا ويلنا إنا كنا ظالمين لأنفسنا وغيرنا، إذ كفرنا بما أخبرنا به.',NULL,'479',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2533,'<span data-type="title" id=toc-2554>٤٧ </span>- ونضع الموازين التى تقيم العدل يوم القيامة، فلا تُظَلَم نفس بنقص شئ من حسناتها أو زيادة شئ فى سيئاتها، ولو كان وزن حبة صغيرة أتينا بها وحاسبنا عليها، وكفى أن نكون الحاسبين فلا تظلم نفس شيئاً.',NULL,'479',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2534,'<span data-type="title" id=toc-2555>٤٨ </span>- ولقد أعطينا موسى وهارون التوراة التى تفرق بين الحق والباطل، والحلال والحرام، وهى إلى ذلك نور يهدى إلى طرق الخير والرشاد، وتذكير ينتفع به المتقون.',NULL,'479',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2535,'<span data-type="title" id=toc-2556>٤٩ </span>- الذين يخافون خالقهم ومالك أمرهم - حال بُعْد الناس عنهم - لا يراءون أحداً، وهم من أهوال يوم القيامة فى خوف دائم.',NULL,'479',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2536,'<span data-type="title" id=toc-2557>٥٠ </span>- وهذا القرآن تذكير كثير للخير، أنزلناه لكم كما أنزلنا الذكر على موسى، فكيف يكون منكم إنكاره وأنتم أولى الناس بالإيمان به؟}',NULL,'479',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2537,'<span data-type="title" id=toc-2558>٥١ </span>- ولقد أعطينا إبراهيم الرشد والتفكير فى طلب الحق مخلصاً من قبل موسى وهارون، وكنا بأحواله وفضائله التى تؤهله لحمل الرسالة عالمين.',NULL,'479',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2538,'<span data-type="title" id=toc-2559>٥٢ </span>- واذكر - أيها النبى - حين قال إبراهيم لأبيه وقومه مستخِفّاً بالأصنام التى كانوا يعظمونها ويعكفون على عبادتها: ما هذه التماثيل التى أنتم مقيمون على عبادتها؟',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2539,'<span data-type="title" id=toc-2560>٥٣ </span>- قالوا: وجدنا آباءنا يعظمونها ويخصونها بعبادتهم، فاتَّبعناهم.',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2540,'<span data-type="title" id=toc-2561>٥٤ </span>- قال: لقد كنتم فى هذه العبادة وكان آباؤكم من قبلكم فى بُعْدٍ واضحٍ عن الحق.',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2541,'<span data-type="title" id=toc-2562>٥٥ </span>- قالوا: أجئتنا فى هذا الذى تقوله بما تعتقد أنه الحق، أم أنت بهذا الكلام من الذين يلهون ويلعبون غير متحملين أى تبعة؟',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2542,'<span data-type="title" id=toc-2563>٥٦ </span>- قال: لا هزل فيما قلته، بل ربكم الذى يستحق - دون غيره - التعظيم والخشوع والعبادة هو الذى خلق السموات والأرض، وأوجدهن على غير مثال سابق. فحقه - وحده - أن يعبد، وأنا على ذلك الذى أقوله من المتحققين الذين يقولون ما يشاهدونه ويعلمونه.',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2543,'<span data-type="title" id=toc-2564>٥٧ </span>- وقال فى نفسه: أقسم بالله لأدبّرن تدبيراً أكسر به أصنامكم بعد أن تبتعدوا عنها، ليظهر لكم ضلال ما أنتم عليه.',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2544,'<span data-type="title" id=toc-2565>٥٨ </span>- ذهب إبراهيم بعد انصرافهم إلى الأصنام فحطمها وجعلها قطعاً، إلا صنماً كبيرا تركه ليرجعوا إليه ويسألوه عما وقع لآلهتهم فلا يجيبهم فيظهر لهم بطلان عبادتهم.',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2545,'<span data-type="title" id=toc-2566>٥٩ </span>- قالوا بعد أن رأوا ما حصل لأصنامهم: مَنْ فعل هذا بآلهتنا؟ إنه دون شك من الذين ظلموا أنفسهم بتعريضها للعقاب.',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2546,'<span data-type="title" id=toc-2567>٦٠ </span>- قال بعضهم: سمعنا شاباً يذكرهم بالسب يُدعى إبراهيم.',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2547,'<span data-type="title" id=toc-2568>٦١ </span>- قال كبارهم: اذهبوا إليه فأحضروه ليُحاسب على مرأى من الناس، لعلهم يشهدون بما فعل ويشاهدون العقوبة التى سننزلها به.',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2548,'<span data-type="title" id=toc-2569>٦٢ </span>- قالوا بعد أن أحضروه: أأنت الذى فعلت هذا بآلهتنا يا إبراهيم؟ .',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2549,'<span data-type="title" id=toc-2570>٦٣ </span>- قال مُنَبِّهاً لهم على ضلالهم مُتَهكِّماً بهم: بل فعله كبيرهم هذا، فاسألوا الآلهة عمَّن فعل بها هذا إن كانوا يستطيعون أن يردوا جواب سؤالكم؟ .',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2550,'<span data-type="title" id=toc-2571>٦٤ </span>- فعادوا إلى أنفسهم يفكرون فيما هم عليه من عبادة ما لا ينفع غيره، ولا يدفع عن نفسه الشر، فاستبان لهم خطؤهم، وقال بعضهم: ليس إبراهيم من الظالمين، بل أنتم - بعبادة ما لا يستحق العبادة - الظالمون.',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2551,'<span data-type="title" id=toc-2572>٦٥ </span>- ثم انقلبوا من الرشاد الطارئ إلى الضلال، وقالوا لإبراهيم: إنك قد علمت أن هؤلاء الذين نعبدهم لا ينطقون، فكيف تطلب منا أن نسألهم؟',NULL,'480',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2552,'<span data-type="title" id=toc-2573>٦٦ </span>- قال: أيكون هذا حالهم من العجز، ويكون هذا حالكم معهم، فتعبدون من غير الله ما لا ينفعكم أقل نفع إن عبدتموه، ولا يضركم إن أهملتموه؟! .',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2553,'<span data-type="title" id=toc-2574>٦٧ </span>- قُبحاً لكم ولآلهتكم، أتعطلون تفكيركم وتُهملون الاعتبار بما تدركون؟ إن هذه الأصنام لا تستحق العبادة.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2554,'<span data-type="title" id=toc-2575>٦٨ </span>- قال بعضهم لبعض: أحرقوه بالنار وانصروا آلهتكم عليه بهذا العقاب، إن كنتم تريدون أن تفعلوا ما تنصرون به آلهتكم.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2555,'<span data-type="title" id=toc-2576>٦٩ </span>- فجعلنا النار باردة وسلاماً لا ضرر فيها على إبراهيم.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2556,'<span data-type="title" id=toc-2577>٧٠ </span>- وأرادوا أن يبطشوا به فأنجيناه وجعلناهم أشد الناس خسراناً.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2557,'<span data-type="title" id=toc-2578>٧١ </span>- ونجَّيناه ولوطاً من كيد الكائدين، فاتجها إلى الأرض التى أكثرنا فيها الخير للناس جميعاً، وأرسلنا فيها كثيراً من الأنبياء.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2558,'<span data-type="title" id=toc-2579>٧٢ </span>- ووهبنا له إسحاق، ومن إسحاق يعقوب هبة زائدة على ما طلب، وكلا من إسحاق ويعقوب جعلناه أهل صلاح.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2559,'<span data-type="title" id=toc-2580>٧٣ </span>- وجعلناهم أنبياء يدعون الناس ويهدونهم إلى الخير بأمرنا لهم أن يكونوا مرشدين، وألهمناهم فعل الخيرات وإدامة القيام بالصلاة على وجهها، وإعطاء الزكاة، وكانوا لنا - دون غيرنا - خاضعين مخلصين.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2560,'<span data-type="title" id=toc-2581>٧٤ </span>- وآتينا لوطاً القول الفصل والسداد فى الحكم والعلم النافع، ونجِّيناه من القرية التى كان أهلها يعملون الأعمال الشاذة الخبيثة، إنهم كانوا قوماً يأتون الذكران - وهى فاحشة - ما سبقهم بها أحد من العالمين.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2561,'<span data-type="title" id=toc-2582>٧٥ </span>- وسلكناه فى أهل رحمتنا، إنه من الصالحين الذين يشملهم الله برحمته ويمدهم بنصره.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2562,'<span data-type="title" id=toc-2583>٧٦ </span>- ولنذكر هنا نوحاً من قبل إبراهيم ولوط، حين دعا ربه أن يُطهّر الأرض من الفاسقين. فاستجبنا دعاءه ونجيناه هو ومَنْ آمن مِن أهله من كرب الطوفان العظيم.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2563,'<span data-type="title" id=toc-2584>٧٧ </span>- ومنعناه بنصرنا من كيد قومه الذين كذبوا بآياتنا الدالة على رسالته؛ إنهم كانوا أصحاب شر فأغرقناهم أجمعين.',NULL,'482',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2564,'<span data-type="title" id=toc-2585>٧٨ </span>- واذكر - أيها النبى - داود وسليمان حين كانا يحكمان فى الزرع، إذ انتشرت فيه غنم القوم من غير أصحابه وأكلته ليلا، وكنا لحكمهما فى القضية المتعلقة به عالمين.',NULL,'482',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2565,'<span data-type="title" id=toc-2586>٧٩ </span>- ففهمنا الفتوى سليمان، وكلا منهما أعطيناه حكمة وعلماً بالحياة وشئونها، وسخرنا مع داود الجبال ينزهن الله كما ينزِّهه داود عن كل ما لا يليق به، وسخرنا الطير كذلك يسبحن الله معه، وكنا فاعلين ذلك بقدرتنا التى لا تعجز.',NULL,'482',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2566,'<span data-type="title" id=toc-2587>٨٠ </span>- وعلَّمنا داود صنعة نسج الدروع، لتكون لباساً يمنعكم من شدة بأس بعضكم لبعض، فاشكروا الله على هذه النعم التى أنعم بها عليكم.',NULL,'482',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2567,'<span data-type="title" id=toc-2588>٨١ </span>- وسخَّرنا لسليمان الريح قوية شديدة الهبوب، تجرى بحسب رغبته وأمره إلى الأرض التى زدنا فيها الخير، وكنا بكل شئ عالمين، لا تغيب عنا كبيرة ولا صغيرة.',NULL,'482',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2568,'<span data-type="title" id=toc-2589>٨٢ </span>- وسخرنا له من الشياطين من يغوصون فى الماء إلى أعماق البحار، ليستخرجوا اللؤلؤ والمرجان، ويعملوا عملا غير ذلك، كبناء الحصون والقصور، وكنا لهم مراقبين لأعمالهم، فلا ينالون أحداً بسوء، ولا يتمرَّدون على أمر سليمان.',NULL,'482',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2569,'<span data-type="title" id=toc-2590>٨٣ </span>- واذكر - أيها النبى - أيوب حين دعا ربه - وقد أضْنَاه المرض - وقال: يا رب، إنى قد أصابنى الضُّر وآلمنى، وأنت أرحم الراحمين.',NULL,'482',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2570,'<span data-type="title" id=toc-2591>٨٤ </span>- فأجبناه إلى ما كان يرجوه، فرفعنا عنه الضُّر، وأعطيناه أولاداً بقدر مَنْ مات من أولاده، وزدناه مثلهم رحمة به من فضلنا، وتذكيراً لغيره ممن يعبدوننا ليصبروا كما صبر، ويطمعوا فى رحمة الله كما طمع.',NULL,'483',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2571,'<span data-type="title" id=toc-2592>٨٥ </span>- واذكر - أيها النبى - لقومك إسماعيل وإدريس وذا الكفل، كل منهم من الصابرين على احتمال التكاليف والشدائد.',NULL,'483',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2572,'<span data-type="title" id=toc-2593>٨٦ </span>- وجعلناهم من أهل رحمتنا، إنهم من عبادنا الصالحين.',NULL,'483',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2573,'<span data-type="title" id=toc-2594>٨٧ </span>- واذكر - أيها النبى - قصة يونس صاحب الحوت إذ ضاق بإعراض قومه عن دعوته، فهجرهم ورحل عنهم بعيداً غاضباً عليهم، ظاناً أن الله أباح له أن يهجرهم، فظن أن الله لن يقدر عليه، فابتلعه الحوت، وعاش وهو فى ظلمات البحر، ونادى ربه ضارعاً إليه معترفاً بما كان منه قائلا: يا رب، لا معبود بحق إلا أنت، أنزهك عن كل ما لا يليق بك، أعترف أنى كنت من الظالمين لنفسى بعملِ ما لا يرضيك.',NULL,'483',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2574,'<span data-type="title" id=toc-2595>٨٨ </span>- فأجبناه إلى ما كان يرجوه، ونجيناه من الغم الذى كان فيه، ومثل هذا الإنجاء من البلاء ننجى المؤمنين الذين يعترفون بأخطائهم ويدعوننا مخلصين.',NULL,'483',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2575,'<span data-type="title" id=toc-2596>٨٩ </span>- واذكر قصة زكريا، حين نادى ربه بعد أن رأى من قدرته سبحانه ما بعث فى نفسه الأمل فى رحمته، فقال: يا رب، لا تتركنى وحيداً دون وارث، وأنت خير الذين يرثون غيرهم، فإنك الباقى بعد فناء الخلق.',NULL,'483',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2576,'<span data-type="title" id=toc-2597>٩٠ </span>- فحققنا رجاءه، وأجبنا دعاءه، ووهبنا له على الكبر ابنه يحيى، وجعلنا زوجه العقيم صالحة للولد إن هؤلاء الأصفياء الأنبياء كانوا يسارعون فى عمل كل خير ندعوهم إليه، ويدعوننا طمعاً فى رحمتنا وخوفاً من عذابنا، وكانوا لا يعظِّمون ولا يهابون أحداً غيرنا.',NULL,'483',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2577,'<span data-type="title" id=toc-2598>٩١ </span>- واذكر مع هؤلاء قصة مريم التى صانت فرجها، فألقينا فيها سِرَّا من أسرارنا، وجعلناها تحمل دون زوج، وجعلنا ابنها دون أب، فكانت هى وابنها دليلا ظاهراً على قدرتنا فى تغيير الأسباب والمسببات، وإننا قادرون على كل شئ.',NULL,'484',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2578,'<span data-type="title" id=toc-2599>٩٢ </span>- إن هذه الملّة - التى هى الإسلام - هى ملّتكم الصحيحة التى يجب أن تحافظوا عليها، حال كونها ملة واحدة متجانسة لا تنافر بين أحكامها، فلا تتفرقوا فيها شيعاً وأحزاباً، وأنا خالقكم ومالك أمركم، فأخلصوا لى العبادة ولا تشركوا معى غيرى.',NULL,'484',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2579,'<span data-type="title" id=toc-2600>٩٣ </span>- ومع هذا الإرشاد، تفرّق أكثر الناس بحسب شهواتهم، جاعلين أمر دينهم قِطَعاً، فصاروا به فِرقاً مختلفة، وكل فريق منهم راجع إلينا يحاسب على أعماله.',NULL,'484',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2580,'<span data-type="title" id=toc-2601>٩٤ </span>- فمن يعمل عمله من الأعمال الصالحة وهو يؤمن بالله وبدينه الذى ارتضاه فلا نقص لشئ من سعيه، بل سيوفَّى جزاءه كاملا، وإنا لهذا السعى كاتبون، فلا يضيع شئ منه.',NULL,'484',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2581,'<span data-type="title" id=toc-2602>٩٥ </span>- وممتنع على أهل كل قرية أهلكناهم بسبب ظلمهم أنهم لا يرجعون إلينا يوم القيامة، بل لابد من رجوعهم وحسابهم على سوء أعمالهم.',NULL,'484',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2582,'<span data-type="title" id=toc-2603>٩٦ </span>- حتى إذا فتحت أبواب الشر والفساد، وأخذ أبناء يأجوج ومأجوج يسرعون خفافاً من كل مرتفع فى الجبال والطرق بعوامل الفوضى والقلق.',NULL,'484',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2583,'<span data-type="title" id=toc-2604>٩٧ </span>- واقترب الموعود به الذى لابد من تحققه وهو يوم القيامة، فيفاجأ الذين كفروا بأبصارهم لا تغمض أبداً من شدة الهول، فيصيحون قائلين: يا خوفنا من هلاكنا، ويا حسرتنا على ما قدمنا، قد كنا فى غفلة من هذا اليوم، بل كنا ظالمين لأنفسنا بالكفر والعناد.',NULL,'484',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2584,'<span data-type="title" id=toc-2605>٩٨ </span>- ويقال لهؤلاء الكفار: إنكم والآلهة التى عبدتموها من غير الله وقود نار جهنم، أنتم داخلون فيها معذَّبون بها.',NULL,'484',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2585,'<span data-type="title" id=toc-2606>٩٩ </span>- لو كان هؤلاء - الذين عبدتموهم من دون الله - آلهة تستحق أن تُعْبد ما دخلوها معكم، وكل من العابدين والمعبودين باقون فى النار.',NULL,'485',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2586,'<span data-type="title" id=toc-2607>١٠٠ </span>- لهم فيها نفَسٌ يخرج من الصدور بصوت مخنوق، لما يلاقونه من الضيق، وهم فيها لا يسمعون شيئاً يسرهم.',NULL,'485',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2587,'<span data-type="title" id=toc-2608>١٠١ </span>- إن الذين وفَّقناهم لاتباع الحق وعمل الخير، ووعدناهم بالعاقبة الحسنة، أولئك من جهنم وعذابها مبعدون.',NULL,'485',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2588,'<span data-type="title" id=toc-2609>١٠٢ </span>- لا يسمعون صوت فَوَران نارها، وهم فيما تشتهيه أنفسهم خالدون.',NULL,'485',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2589,'<span data-type="title" id=toc-2610>١٠٣ </span>- لا يحزنهم الهول الأكبر الذى يفزع منه الكفار، وتستقبلهم الملائكة بالتهنئة، يقولون: هذا يومكم الذى وعدكم ربكم النعيم فيه.',NULL,'485',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2590,'<span data-type="title" id=toc-2611>١٠٤ </span>- يوم نطوى السماء كما تُطْوى الورقة فى الكتاب، ونُعيد الخلق إلى الحساب والجزاء، لا تعجزنا إعادتهم، فقد بدأنا خلقهم، وكما بدأناهم نعيدهم، وَعَدْنا بذلك وعْداً حقاً، إنا كنا فاعلين دائماً ما نَعِدُ به.',NULL,'485',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2591,'<span data-type="title" id=toc-2612>١٠٥ </span>- ولقد كتبنا فى الزبور - وهو كتاب داود - من بعد التوراة أن الأرض يرثها عبادى الصالحون لعمارتها، وتيسير أسباب الحياة الطيبة فيها.',NULL,'485',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2592,'<span data-type="title" id=toc-2613>١٠٦ </span>- إن فى هذا الذى ذكرناه من أخبار الأنبياء مع أقوامهم، وأخبار الجنة والنار لكفاية فى التذكير والاعتبار، لقوم مهيئين لعبادة الله - وحده - لا تفتنهم زخارف الدنيا.',NULL,'485',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2593,'<span data-type="title" id=toc-2614>١٠٧ </span>- وما أرسلناك - أيها النبى - إلا لتكون رحمة عامة للعالمين.',NULL,'485',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2594,'<span data-type="title" id=toc-2615>١٠٨ </span>- قل - أيها النبى - للناس كافة: إن لُبَّ الذى أَوحَى الله به إلىَّ هو: أنه لا إله لكم إلا هو، وأن بقية ما يوحى به بعد ذلك تابع لهذا الأصل، وإذا كان الأمر كذلك فيجب أن تستسلموا وتخضعوا لله وحده.',NULL,'486',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2595,'<span data-type="title" id=toc-2616>١٠٩ </span>- فإن أعرضوا عن دعوتك، فقل لهم: لقد أعلمتكم جميعاً بما أمرنى به ربى، وبذلك استوينا فى العلم، ولا أدرى ما توعدون به من البعث والحساب، أهو قريب أم بعيد؟',NULL,'486',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2596,'<span data-type="title" id=toc-2617>١١٠ </span>- إن الله يعلم كل ما يقال مما تجهرون به، وما تكتمون فى أنفسكم.',NULL,'486',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2597,'<span data-type="title" id=toc-2618>١١١ </span>- وما أدرى لعل إمهالكم وتأخير العذاب عنكم اختبار يمتحنكم الله به، ويمتِّعكم فيه بلذائذ الحياة إلى حينٍ قدَّره الله بحسب حكمته.',NULL,'486',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2598,'<span data-type="title" id=toc-2619>١١٢ </span>- قل - أيها النبى -: يا رب احكم بينى وبين مَنْ بلَّغتُهم الوحى بالعدل حتى لا يستوى المؤمنون والكافرون، وربنا المنعم بجلائل النعم، المستحق للحمد والشكر، وهو المستعان به على إبطال ما تفترونه أيها الكافرون.',NULL,'486',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2599,'<span data-type="title" id=toc-2621>١ </span>- يا أيها الناس: احذروا عقاب ربكم، وتذكروا دائماً يوم القيامة، لأن الاضطراب الذى يحدث فيه شديد مزعج ترتجف منه الخلائق.',NULL,'487',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2600,'<span data-type="title" id=toc-2622>٢ </span>- يوم تشاهدون القيامة ترون هوْلاً يبلغ من شدته أنه لو كانت هناك مرضعة ثديها فى فم رضيعها لذهلت عنه وتركته. ولو كانت هناك امرأة ذات حمل أسقط جنينها فى غير أوانه فزعاً ورعباً، وتشاهد - أيها الناظر - حال الناس فى ذلك اليوم من نظراتهم الذاهلة، وخطواتهم المترنّحة فتظنهم سكارى وما بهم من سكر، ولكن الهول الذى شاهدوه، والخوف من عذاب الله الشديد هو الذى أفقدهم توازنهم.',NULL,'487',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2601,'<span data-type="title" id=toc-2623>٣ </span>- ومع هذا التحذير الشديد الصادق، فإن بعض الناس دفعه العناد - أو التقليد - إلى الجدل فى الله وصفاته فأثبت له الشركاء، أو أنكر قدرته على البعث ومجازاة الناس على أعمالهم، غير مستند فى جدله وإنكاره إلى علم صحيح أو حجة صادقة، ولكنه يقلد ويتبع خطوات كل شيطان متمرد على ربه بعيد عن هديه.',NULL,'488',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2602,'<span data-type="title" id=toc-2624>٤ </span>- قضى الله أن كل من اتبعه واتخذه ولياً وهادياً أضله عن طريق الحق، ووجَّهه إلى الباطل المفضى به إلى عذاب النار المسعَّرة المتأججة.',NULL,'488',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2603,'<span data-type="title" id=toc-2625>٥ </span>- يا أيها الناس إن كنتم فى شك من بَعْثِنا لكم بعْد الموت ففى خلقكم الدليل على قدرتنا على البعث، فقد خلقنا أصلكم من تراب، ثم جعلنا منه نطفة حوَّلْناها بعد مدة إلى قطعة دم متجمدة، ثم جعلناها قطعة من اللحم مُصَوَّرة فيها معالم الإنسان، أو غير مصورة لِنُبيِّن لكم قدرتنا على الإبداع والتدرج فى التكوين، والتغيير من حال إلى حال، ونسقِط من الأرحام ما نشاء، ونقر فيها ما نشاء، حتى تكمل مدة الحمل، ثم نُخْرِجكم من بطون أمهاتكم أطفالاً، ثم نرعاكم لتبلغوا تمام العقل والقوة، ومنكم بعد ذلك من يتوفاه الله، ومنكم من يمد له عمره حتى يصير إلى الهرم والخوف فيتوقف علمه وإدراكه للأشياء، ومن بَدَأَ خلقكم بهذه الصورة لا تعجزه إعادتكم. وأمر آخر يدلك على قدرة الله على البعث: أنك ترى الأرض قاحلة يابسة، فإذا أنزلنا عليها الماء دبَّت فيها الحياة وتحركت وزادت وارتفع سطحها بما تخلله من الماء والهواء، وأظهرت من أصناف النباتات ما يروق منظره، ويُبهر حسنه، ويُبْتَهجُ لمرآه.',NULL,'488',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2604,'<span data-type="title" id=toc-2626>٦ </span>-، ٧ - ذلك الذى تقدم من خلْق الإنسان وإنبْات الزرع شاهد بأن الله هو الإله الحق، وأنه الذى يحيى الموتى عند بعثهم كما بدأهم، وأنه القادر على كل شئ، وأنَّ القيامة آتية لا شك فيها تحقيقاً لوعده، وأن الله يحيى من فى القبور ببعثهم للحساب والجزاء.',NULL,'489',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2605,'<span data-type="title" id=toc-2627>٨ </span>- ومع ما تقدم، فبعض الناس يُجادل فى الله وقدرته، وينكر البعث على غير أساس علمى أو إلهام صادق، أو كتاب مُنَزَّل من الله يستبصر به. فجداله لمجرد الهوى والعناد.',NULL,'489',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2606,'<span data-type="title" id=toc-2628>٩ </span>- وهو مع ذلك يلوى جانبه تكبّراً وإعراضاً عن قبول الحق. وهذا الصنف من الناس سيصيبه خزى وهوان فى الدنيا بنصر كلمة الحق، ويوم القيامة يعذبه الله بالنار المحرقة.',NULL,'489',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2607,'<span data-type="title" id=toc-2629>١٠ </span>- ويقال له: ذلك الذى تَلْقاه من خزى وعذاب إنما كان بسبب افترائك وتكبرك، لأن الله عادل لا يظلم، ولا يُسَوِّى بين المؤمن والكافر، والصالح والفاجر، بل يجازى كلاً منهم بعمله.',NULL,'489',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2608,'<span data-type="title" id=toc-2630>١١ </span>- ومن الناس صنف ثالث لم يتمكن الإيمان من قلبه، بل هو مزعزع العقيدة، تتحكم مصالحه فى إيمانه، إن أصابه خير فرح به واطمأن، وإن أصابته شدة فى نفسه أو ماله أو ولده ارتد إلى الكفر، فخسر فى الدنيا راحة الاطمئنان إلى قضاء الله ونصره، كما خسر فى الآخرة النعيم الذى وعده الله للمؤمنين الثابتين الصابرين، ذلك الخسران المزدوج هو الخسران الحقيقى الواضح.',NULL,'489',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2609,'<span data-type="title" id=toc-2631>١٢ </span>- يعبد هذا الخاسر من دون الله أصناماً لا تضره إن لم يعبدها، ولا تنفعه إن عبدها، ذلك الفعل منه هو الضلال البعيد عن الحق والصواب.',NULL,'489',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2610,'<span data-type="title" id=toc-2632>١٣ </span>- يدعو من دون الله مَنْ ضرُّه بإفساد العقول وسيطرة الأوهام أقرب للنفس من اعتقاد مناصرته، فلبئس ذلك المعبود نصيراً، ولبئس ذلك المعبود عشيراً.',NULL,'489',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2611,'<span data-type="title" id=toc-2633>١٤ </span>- إن المؤمنين بالله ورسله إيماناً اقترن بالعمل الصالح يدخلهم ربهم يوم القيامة جنات تجرى من تحت أشجارها الأنهار، إن الله يفعل ما يريد من معاقبة المفسد وإثابة المصلح.',NULL,'489',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2612,'<span data-type="title" id=toc-2634>١٥ </span>- من كان من الكفار يظن أن الله لا ينصر نبيه فليمدد بحبل إلى سقف بيته، ثم ليختنق به وليقدر فى نفسه وينظر، هل يذهب فعله ذلك ما يغيظه من نصر الله لرسوله؟ .',NULL,'490',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2613,'<span data-type="title" id=toc-2635>١٦ </span>- ومثل ما بيَّنا حجتنا واضحة فيما سبق أن أنزلنا على الرسل، أنزلنا القرآن كله على محمد آيات واضحات لتقوم الحُجة على الناس، وأن الله يهدى من أراد هدايته لسلامة فطرته وبعده عن العناد وأسبابه.',NULL,'490',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2614,'<span data-type="title" id=toc-2636>١٧ </span>- إن الذين آمنوا بالله وبرسله جميعاً، واليهود المنتسبين إلى موسى، وعُبَّادَ النجوم، والملائكة، والنصارى المنتسبين إلى عيسى، والمجوسَ عُبَّادَ النار، والمشركين عًبَّاد الأوثان. إن هؤلاء جميعاً سيفصل الله بينهم يوم القيامة بإظهار المحق من المبطل منهم، لأنه مطلع على كل شئ، عالم بأعمال خلقه، وسيجازيهم على أعمالهم.',NULL,'490',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2615,'<span data-type="title" id=toc-2637>١٨ </span>- ألم تعلم - أيها العاقل - أن الله يخضع لتصريفه مَنْ فى السموات ومن فى الأرض والشمس والقمر والنجوم والجبال والشجر والدواب، وكثير من الناس يؤمن بالله ويخضع لتعاليمه فاسْتَحقُّوا بذلك الجنة، وكثير منهم لم يؤمن به ولم ينفذ تعاليمه فاستحقوا بذلك العذاب والإهانة، ومن يطرده الله من رحمته ويهنه لا يقدر أحد على إكرامه، إن الله قادر على كل شئ، فهو يفعل ما يريد.',NULL,'490',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2616,'<span data-type="title" id=toc-2638>١٩ </span>- هذان فريقان من الناس تنازعوا فى أمر ربهم، وما يليق به، وما لا يليق، فآمن به فريق، وكفر فريق، فالذين كفروا أعد الله لهم يوم القيامة ناراً تحيط بهم من كل جانب، كما يحيط الثوب بالجسد، ولزيادة تعذيبهم تصب الملائكة على رءوسهم الماء الشديد الحرارة.',NULL,'490',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2617,'<span data-type="title" id=toc-2639>٢٠ </span>- فينفذ إلى ما فى بطونهم فيذيبها كما يذيب جلودهم.',NULL,'490',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2618,'<span data-type="title" id=toc-2640>٢١ </span>- وأُعدت لهم أعمدة من حديد.',NULL,'490',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2619,'<span data-type="title" id=toc-2641>٢٢ </span>- كلما حاولوا الخروج من النار من شدة الغم والكرب ضربتهم الملائكة بها وردتهم حيث كانوا، وقالت لهم: ذوقوا عذاب النار المحرقة جزاء كفركم.',NULL,'491',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2620,'<span data-type="title" id=toc-2642>٢٣ </span>- أما الذين آمنوا بالله وعملوا الأعمال الصالحة فإن الله يدخلهم جنات تجرى من تحت قصورها وأشجارها الأنهار، ينعمون فيها صنوف النعيم، وتزينهم الملائكة بأساور الذهب وباللؤلؤ، أما لباسهم المعتاد فمن حرير.',NULL,'491',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2621,'<span data-type="title" id=toc-2643>٢٤ </span>- وزيادة فى تنعيمهم بالجنة ألهمهم الله فيها الطيِّب من القول، والحميد من الفعل، فيسبحون الله ويقدسونه ويشكرونه، ويعاشر بعضهم بعضاً بمحبة وسلام.',NULL,'491',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2622,'<span data-type="title" id=toc-2644>٢٥ </span>- إن الذين كفروا بالله ورسله واعتادوا مع ذلك منْع الناس من الدخول فى الإسلام، ومنْع المؤمنين من دخول المسجد الحرام فى مكة - وقد جعله الله حرماً آمناً للناس جميعاً المقيم والزائر - يجازيهم على ذلك بالعذاب الشديد، وكذلك كل من ينحرف عن الحق، ويرتكب أى ظلم فى الحرم عذَّبه عذاباً أليماً.',NULL,'491',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2623,'<span data-type="title" id=toc-2645>٢٦ </span>- واذكر - أيها النبى - لهؤلاء المشركين الذين يدَّعون اتِّباع إبراهيم - عليه السلام - ويتخذون من البيت الحرام مكاناً لأصنامهم، اذكر لهم قصة إبراهيم والبيت الحرام حين أرشدناه إلى مكانه، وأمرناه ببنائه وقلْنا له: لا تشرك بى شيئاً ما فى العبادة، وطهر بيتى من الأصنام والأقذار، ليكون مُعدَّا لمن يطوف به، ويقيم بجواره، ويتعبد عنده.',NULL,'491',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2624,'<span data-type="title" id=toc-2646>٢٧ </span>- وأَعْلِم الناس - أيها النبى - أن الله فرض على المستطيعين منهم أن يقصدوا هذا البيت فيلبوا نداءَك، ويأتون إليه مشاة وركباناً على إبل يُضَمِّرها السفر من كل مكان بعيد.',NULL,'491',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2625,'<span data-type="title" id=toc-2647>٢٨ </span>- ليحصلوا على منافع دينية لهم بأداء فريضة الحج، ومنافع دنيوية بالتعارف مع إخوانهم المسلمين، والتشاور معهم فيما ينفعهم فى دينهم ودنياهم، وليذكروا اسم الله فى يوم عيد النحر والأيام الثلاثة بعده على ذَبْح ما رزقهم ويسر لهم من الإبل والبقر والغنم، فكلوا منها ما شئتم وأطعموا الذى أصابه.',NULL,'492',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2626,'<span data-type="title" id=toc-2648>٢٩ </span>- ثم عليهم بعد ذلك أن يُزيلوا من أجسامهم ما علق بها أثناء الإحرام، من آثار العرق وطول السفر، ويصرفوا ما نذروه لله إن كانوا قد نذروا شيئاً، ويطوفوا بأقدم بيت بُنى لعبادة الله فى الأرض.',NULL,'492',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2627,'<span data-type="title" id=toc-2649>٣٠ </span>- ومَنْ يلتزم أوامر الله ونواهيه فى حجه تعظيماً لها فى نفسه كان ذلك خيراً له فى دنياه وآخرته، وقد أحل الله لكم أكل لحوم الإبل والبقر والغنم إلا فى حالات تعرفونها مما يُتْلى عليكم فى القرآن كالميتة وغيرها، فاجتنبوا عبادة الأوثان لأن عبادتها قذارة عقلية ونفسية لا تليق بالإنسان، واجتنبوا قول الزور على الله وعلى الناس.',NULL,'492',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2628,'<span data-type="title" id=toc-2650>٣١ </span>- وكونوا مخلصين لله حريصين على اتِّباع الحق غير متخذين أى شريك لله فى العبادة، فإن من يُشْرِك بالله فقد سقط من حصن الإيمان، وتنازعته الضلالات، وعرَّضَ نفسه لأبشع صورة من صور الهلاك، وكان حاله حينئذ كحال الذى سقط من السماء فتمزق قِطَعاً تخاطفتها الطيور فلم يَبْقَ له أثر، أو عصفت به الريح العاتية فشتَّتت أجزاءه، وَهَوتْ بكل جزء منه فى مكان بعيد.',NULL,'492',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2629,'<span data-type="title" id=toc-2651>٣٢ </span>- إن مَنْ يُعظِّم دين الله وفرائض الحج وأعماله والهدايا التى يسوقها إلى فقراء الحرم، فيختارها عظيمة سِماناً صِحاحاً لا عيب فيها فقد اتقى الله، لأن تعظيمها أثر من آثار تقوى القلوب المؤمنة، وعلامة من علامات الإخلاص.',NULL,'492',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2630,'<span data-type="title" id=toc-2652>٣٣ </span>- لكم فى هذه الهدايا منافع دنيوية، فتركبونها وتشربون لبنها إلى وقت ذبحها، ثم لكم منافعها الدينية كذلك حينما تذبحونها عند البيت الحرام تَقَرُّباً إلى الله.',NULL,'492',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2631,'<span data-type="title" id=toc-2653>٣٤ </span>- ليست هذه الفرائض التى تتعلق بالحج خاصة بكم، فقد جعلنا لكل جماعة مؤمنة قرابين يتقرَّبون بها إلى الله، ويذكرون اسمه ويعظِّمونه عند ذبحها شكراً له على ما أنعم عليهم، ويسره لهم من بهائم الإبل والبقر والغنم، والله الذى شرع لكم ولهم إله واحد، فأسْلِمُوا له - وحده - أمركم وأخلصوا له عملكم، ولا تشركوا معه أحداً، وَبَشِّر - أيها النبى - بالجنة والثواب الجزيل المخلصين لله من عباده.',NULL,'493',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2632,'<span data-type="title" id=toc-2654>٣٥ </span>- الذين إذا ذكر الله اضطربت قلوبهم من خشيته وخشعت لذكره، والذين صبروا على ما أصابهم من المكاره والمتاعب استسلاماً لأمره وقضائه، وأقاموا الصلاة على أكمل وجوهها، وأنفقوا بعض أموالهم التى رزقهم الله إياها فى سبيل الخير.',NULL,'493',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2633,'<span data-type="title" id=toc-2655>٣٦ </span>- وقد جعلنا ذبح الإبل والبقر فى الحج من أعلام الدين ومظاهره، وإنكم تتقربون بها إلى الناس، ولكم فيها خير كثير فى الدنيا بركوبها وشُرْب لبنها، وفى الآخرة بالأجر والثواب على ذبحها وإطعام الفقراء منها، فاذكروا اسم الله عليها حال كونها مصطفة مُعَدَّة للذبح خالية من العيب. فإذا تم لكم ذبحها فكلوا بعضها إن أردتم، وأطعموا الفقير القانع المتعفف عن السؤال، والذى دفعته حاجته إلى ذل السؤال، وكما سخَّرنا كل شئ لما نريده منه سخرناها لنفعكم، وذللناها لإرادتكم لتشكرونا على نعمنا الكثيرة عليكم.',NULL,'493',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2634,'<span data-type="title" id=toc-2656>٣٧ </span>- واعلموا أن الله لا ينظر إلى صوركم وأعمالكم، ولكن ينظر إلى قلوبكم، ولا يريد منكم مجرد التظاهر بالذبح وإراقة الدماء، ولكنه يريد منكم القلب الخاشع، فلن ينال رضاه من وزَّع تلك اللحوم ولا الدماء، ولكن الذى ينال رضاه هو تقواكم وإخلاص نواياكم. مثل هذا التسخير سخرناها لتنفعكم فَتُعظموا الله على ما هداكم إليه من إتمام مناسك الحج. وبشر - أيها النبى - المحسنين الذين أحسنوا أعمالهم ونواياهم بثواب عظيم.',NULL,'493',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2635,'<span data-type="title" id=toc-2657>٣٨ </span>- إن الله يدافع عن المؤمنين ويحميهم وينصرهم بإيمانهم، لأنه لا يحب الخائنين لأمانتهم، المبالغين فى كفرهم بربهم، ومن لا يحبه الله لا ينصره.',NULL,'493',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2636,'<span data-type="title" id=toc-2658>٣٩ </span>- أَذِنَ الله للمؤمنين الذين قاتلهم المشركون أن يردوا اعتداءهم عليهم بسبب ما نالهم من ظُلْم صبروا عليه طويلا، وإن الله لقدير على نصر أوليائه المؤمنين.',NULL,'494',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2637,'<span data-type="title" id=toc-2659>٤٠ </span>- الذين ظلمهم الكفار وأرغموهم على ترك وطنهم مكة والهجرة منها وما كان لهم من ذنب عندهم إلا أنهم عرفوا الله فعبدوه - وحده - ولولا أن الله سخر للحق أعواناً ينصرونه ويدفعون عنه طغيان الظالمين لساد الباطل، وتمادى الطغاة فى طغيانهم، وأخمدوا صوت الحق، ولم يتركوا للنصارى كنائس، ولا لرهبانهم صوامع، ولا لليهود معابد، ولا للمسلمين مساجد يذكر فيها اسم الله ذكراً كثيراً، وقد أخذ الله العهد الأكيد على نفسه أن ينصر كل من نصر دينه، وأن يعز كل من أعز كلمة الحق فى الأرض. ووعد الله لا يتخلف، لأنه قوى على تنفيذ ما يريد عزيز لا يغلبه غالب.',NULL,'494',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2638,'<span data-type="title" id=toc-2660>٤١ </span>- هؤلاء المؤمنون الذين وعدنا بنصرهم، هم الذين إن مكَّنا سلطانهم فى الأرض حافظوا على حسن صلتهم بالله وبالناس، فيؤدون الصلاة على أتم وجوهها، ويعطون زكاة أموالهم لمستَحقيها، ويأمرون بكل ما فيه خير، وينهون عن كل ما فيه شر. ولله - وحده - مصير الأمور كلها، فيعز من يشاء، ويذل من يشاء حسب حكمته.',NULL,'494',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2639,'<span data-type="title" id=toc-2661>٤٢ </span>- وإذا كنت تلاقى - أيها النبى - تكذيباً وإيذاء من قومك فلا تحزن، وتأمَّل فى تاريخ المرسلين قبلك تجد أنك لست أول رسول كذَّبه قومه وآذوه، فمن قبل هؤلاء الذين كذبوك كذبت قوم نوح رسولهم نوحاً وكذبت قوم عاد رسولهم هودا، وكذبت ثمود رسولهم صالحا.',NULL,'494',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2640,'<span data-type="title" id=toc-2662>٤٣ </span>- وكذَّب قوم إبراهيم رسولهم إبراهيم، وقوم لوط رسولهم لوطا.',NULL,'494',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2641,'<span data-type="title" id=toc-2663>٤٤ </span>- وكذب أهل مدين رسولهم شعيبا، وكذب فرعون وقومه رسول الله - موسى -. لقى هؤلاء المرسلون الكثير من الإنكار والتكذيب، وقد أمهلت المكذبين لعلهم يثوبون إلى رُشدهم ويستجيبون لدعوة الحق، ولكنهم افتروا وتمادوا فى تكذيب رسلهم وإيذائهم، وازدادوا إثماً على آثامهم فعاقبتهم بأشد أنواع العقاب، فانظر فى تاريخهم تجد كيف كان عقابى لهم شديداً، حيث أبدلتهم بالنعمة نقمة، وبالعافية هلاكاً، وبالعمران خراباً.',NULL,'495',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2642,'<span data-type="title" id=toc-2664>٤٥ </span>- فأهلكنا كثيراً من أهل القرى الذين يعمرونها بسبب ظلمهم وتكذيبهم لرسلهم فأصبحت ساقطة سقوفها على جدرانها، خالية من سكانها، كأن لم تكن موجودة بالأمس، فكم من بئر تعطلت من روادها واختفى ماؤها، وقَصْر عظيم مشيد مطلى بالجص خلا من سكانه.',NULL,'495',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2643,'<span data-type="title" id=toc-2665>٤٦ </span>- أيقولون ما يقولون ويستعجلون العذاب ولم يسيروا فى الأرض ليشاهدوا بأعينهم مصرع هؤلاء الظالمين المكذبين؟ فربما تستيقظ قلوبهم من غفلتها، وتعقل ما يجب عليهم نحو دعوة الحق التى تدعوهم إليها، وتسمع آذانهم أخبار مصارع هؤلاء الكفار فيعتبرون بها، ولكن من البعيد أن يعتبروا بما شاهدوا أو سمعوا ما دامت قلوبهم متحجرة، إذ ليس العمى الحقيقى عمى الأبصار، ولكنه فى القلوب والبصائر.',NULL,'495',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2644,'<span data-type="title" id=toc-2666>٤٧ </span>- ويأخذ الغرور كفار مكة فلا يبالون مع قيام هذه العبر، فيستعجلونك - أيها النبى - بوقوع ما توعدتهم به من العذاب تحدياً واستهزاء، وهو لا محالة واقع بهم، ولكن فى موعد قدَّره الله فى الدنيا أو فى الآخرة، ولن يخلف وعده بحال ولو طالت السنون، فإن يوماً واحداً عنده يماثل ألف سنة مما تقدرون وتحسبون.',NULL,'495',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2645,'<span data-type="title" id=toc-2667>٤٨ </span>- وكثير من أهل القرى كانوا مثلهم ظالمين، فأمْهلْتهم ولم أعاجلهم بالعقاب، ثم أنزلته بهم، وإلىَّ - وحدى - مرجع الجميع يوم القيامة فأجازيهم بما يستحقون، فلا تغتروا - أيها الكفار - بتأخير العذاب عنكم.',NULL,'496',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2646,'<span data-type="title" id=toc-2668>٤٩ </span>- قل - أيها النبى - لهؤلاء المكذبين الذين يطلبون منك التَّعجيل بعذابهم: ليس من مهمتى أن أجازيكم على أعمالكم، وإنما أنا مُحذِّر من عقاب الله تحذيراً واضحاً، والله هو الذى يتولى حسابكم ومجازاتكم.',NULL,'496',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2647,'<span data-type="title" id=toc-2669>٥٠ </span>- فالذين آمنوا بالله وبرسوله وعملوا الأعمال الصالحة لهم مغفرة من الله لذنوبهم التى وقعوا فيها، كما أن لهم رزقاً كريماً فى الدنيا والآخرة.',NULL,'496',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2648,'<span data-type="title" id=toc-2670>٥١ </span>- والذين أجهدوا أنفسهم فى محاربة القرآن مسابقين المؤمنين معارضين لهم، شاقين زاعمين - خطأ - أنهم بذلك يبلغون ما يريدون، أولئك يخلدون فى عذاب الجحيم.',NULL,'496',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2649,'<span data-type="title" id=toc-2671>٥٢ </span>- لا تحزن - أيها النبى - من محاولات هؤلاء الكفار، فقد جرت الحوادث من قبلك مع كل رسول من رسلنا ونبى من أنبيائنا أنه كلما قرأ عليهم شيئاً يدعوهم به إلى الحق تصدى له شياطين الإنس المتمردون لإبطال دعوته وتشكيك الناس فيما يتلوه عليهم لكى يحولوا بين النبى وبين أمنيته فى إجابة دعوته، فيزيل الله ما يدبِّرون، ثم تكون الغلبة فى النهاية للحق؛ حيث يثبت الله شريعته، وينصر رسوله، وهو عليم بأحوال الناس ومكائدهم، حكيم فى أفعاله يضع كل شئ فى موضعه.',NULL,'496',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2650,'<span data-type="title" id=toc-2672>٥٣ </span>- وإنما مكَّن الله المتمردين على الحق من إلقاء الشُّبه والعراقيل فى سبيل الدعوة ليكون فى ذلك امتحان واختبار للناس، فالكفار الذين تحجرت قلوبهم، والمنافقون ومرضى القلوب يزدادون ضلالا بترويج هذه الشُّبه ومناصرتها، ولا عجب فى أن يقف هؤلاء الظالمون هذا الموقف فإنهم لجُّوا فى الضلال، وأوغلوا فى العناد والشقاق.',NULL,'496',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2651,'<span data-type="title" id=toc-2673>٥٤ </span>- وليزداد الذين أوتوا علم الشرع والإيمان به إيماناً وعلماً، بأن ما يقوله الرسل والأنبياء إنما هو الحق المنزَّل من عند الله، وإن الله ليتولى المؤمنين دائماً بعنايته فى المشاكل التى تمر بهم، فيهديهم إلى معرفة الطريق المستقيم فيتبعونه.',NULL,'497',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2652,'<span data-type="title" id=toc-2674>٥٥ </span>- والذين كفروا لا يوفَّقون فيستمرون على شكهم فى القرآن حتى يأتيهم الموت، أو يأتيهم عذاب يوم لا خير لهم فيه ولا رحمة، وهو يوم القيامة.',NULL,'497',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2653,'<span data-type="title" id=toc-2675>٥٦ </span>- حيث يكون السلطان القاهر والتصرف المطلق للَّه - وحده - فى هذا اليوم الذى يحكم فيه بين عباده، فالذين آمنوا وعملوا الأعمال الصالحة يخلدون فى جنات تتوافر لهم فيها كل صنوف النعيم.',NULL,'497',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2654,'<span data-type="title" id=toc-2676>٥٧ </span>- والذين كفروا وكذبوا بآيات القرآن التى أنزلناها على محمد، أولئك لهم عذاب يلقون فيه الذل والهوان.',NULL,'497',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2655,'<span data-type="title" id=toc-2677>٥٨ </span>- والذين تركوا أوطانهم لإعلاء شأن دينهم يبتغون رضا اللَّه، ثم قُتِلوا فى ميدان الجهاد، أو ماتوا على فراشهم، يجزيهم اللَّه أحسن الجزاء، وإن اللَّه لهو خير من يعطى الثواب الجزيل.',NULL,'497',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2656,'<span data-type="title" id=toc-2678>٥٩ </span>- ولينزلنهم فى الجنة درجات يرضونها ويسعدون بها، وإن اللَّه لعليم بأحوالهم فيجزيهم الجزاء الحسن، حليم يتجاوز عن هفواتهم.',NULL,'497',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2657,'<span data-type="title" id=toc-2679>٦٠ </span>- ذلك شأننا فى مجازاة الناس: لا نظلمهم، والمؤمن الذى يقتص ممن جنى عليه، ويجازيه بمثل اعتدائه دون زيادة، ثم يتمادى الجانى فى الاعتداء عليه بعد ذلك، فإن اللَّه يعطى عهدا مؤكدا بنصره على من تعدى عليه، وإن اللَّه لكثير العفو عمن جازى بمثل ما وقع عليه، فلا يؤاخذه به، كثير المغفرة فيستر هفوات عبده الطائع ولا يفضحه يوم القيامة.',NULL,'497',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2658,'<span data-type="title" id=toc-2680>٦١ </span>- ذلك النصر هيَّن على اللَّه لأنه قادر على كل شئ، من آيات قدرته البارزة أمامكم هيمنته على العالم فيداول بين الليل والنهار بأن يزيد فى أحدهما ما ينقصه من الآخر، فتسير بعض ظلمة الليل مكان بعض ضوء النهار وينعكس ذلك، وهو سبحانه مع تمام قدرته سميع لقول المظلوم، بصير بفعل الظالم، فينتقم منه.',NULL,'498',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2659,'<span data-type="title" id=toc-2681>٦٢ </span>- ذلك النصر من الله للمظلومين، وتصرفه المطلق فى الكون كما تلمسون مرجعه أنه هو الإله الحق الذى لا إله معه غيره، وأن ما يعبده المشركون من الأصنام هو الباطل الذى لا حقيقة له، وأن اللَّه - وحده - هو العلى على ما عداه شأنا، الكبير سلطانا.',NULL,'498',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2660,'<span data-type="title" id=toc-2682>٦٣ </span>- ألا تعتبر - أيها العاقل - بما ترى حولك من مظاهر قدرة اللَّه فتعبده وحده؟ فهو الذى أنزل ماء الأمطار من السحاب فأصبحت الأرض به مخضرة بما ينبت فيها من النبات، بعد أن كانت مجدبة، إن اللَّه كثير اللطف بعباده، خبير بما ينفعهم فيهيئه لهم بقدرته.',NULL,'498',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2661,'<span data-type="title" id=toc-2683>٦٤ </span>- كل ما فى السموات وما فى الأرض ملك له، وعبيد له وحده، ويتصرف فيه كما يشاء، وهو الغنى عن عباده، وهم المفتقرون إليه، وهو الحقيق وحده بالحمد والثناء عليه من جميع خلقه.',NULL,'498',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2662,'<span data-type="title" id=toc-2684>٦٥ </span>- ألا تنظر - أيها العاقل - إلى مظاهر قدرة اللَّه فتراه ييسر للناس جميعاً الانتفاع بالأرض وما فيها، وهيَّأ لهم البحر تسير فيه السفن بمشيئته، وأمسك الكواكب فى الفضاء بقدرته حتى لا يختل نظامها، أو تقع على الأرض إلا إذا اقتضت إرادته ذلك، إن اللَّه سبحانه شديد الرأفة والرحمة بعباده فيهيئ كل سبل الحياة الطيبة لهم، كيف بعد ذلك كله لا يخلصون فى شكره وعبادته؟ .',NULL,'498',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2663,'<span data-type="title" id=toc-2685>٦٦ </span>- وهو الذى أوجد فيكم الحياة، ثم يميتكم حين تنقضى آجالكم، ثم يحييكم يوم القيامة للحساب والجزاء، إن الإنسان مع كل هذه النعم والدلائل لشديد الجحود باللَّه وبنعمه عليه.',NULL,'498',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2664,'<span data-type="title" id=toc-2686>٦٧ </span>- وقد جعلنا لكل أمة من أصحاب الشرائع السابقة شريعة خاصة بهم لائقة بعصرهم، يعبدون اللَّه عليها إلى أن ينسخها ما يأتى بعدها. ومن أجل هذا جعلنا لأمتك - أيها النبى - شريعة يُعْبَدُ اللَّه عليها إلى يوم القيامة، وإذا كان هذا هو أمرنا ووضعنا، فلا يجوز أن يشتد فى منازعتك فيه هؤلاء المتعبدون بأديانهم السابقة عليك، فقد نَسختْ شريعتك شرائعهم، فلا تلتفت لمجادلتهم، واستمر فى الدعوة إلى ربك حسبما يوحى إليك، إنك لتسير على هدى ربك المستقيم.',NULL,'499',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2665,'<span data-type="title" id=toc-2687>٦٨ </span>- وإن أصروا على الاستمرار فى مجادلتك فأعرض عنهم وقل لهم: اللَّه أعلم بأعمالكم، وبما تستحقون عليها من الجزاء.',NULL,'499',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2666,'<span data-type="title" id=toc-2688>٦٩ </span>- اللَّه يحكم بينى وبينكم يوم القيامة فيما كنتم تختلفون فيه معى، فَيُثيب المهتدى ويعاقب الضال.',NULL,'499',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2667,'<span data-type="title" id=toc-2689>٧٠ </span>- واعلم - أيها العاقل - أن علْم اللَّه محيط بكل ما فى السماء وما فى الأرض، فلا يخفى عليه شئ من أعمال هؤلاء المجادلين، فكل ذلك ثابت عند اللَّه فى لوح محفوظ، لأن إحاطته بذلك وإثباته وحفظه يسيرٌ عليه كل اليسر.',NULL,'499',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2668,'<span data-type="title" id=toc-2690>٧١ </span>- ويعبد المشركون من دون اللَّه أوثانا وأشخاصا لم ينزل بعبادتها حجة فى كتاب سماوى، وليس لديهم عليها دليل عقلى، ولكن لمجرد الهوى والتقليد، وليس لهؤلاء المشركين الذين ظلموا وامتهنوا عقولهم نصيرٌ ينصرهم ويدفع عنهم عذاب النار يوم القيامة كما يزعمون.',NULL,'499',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2669,'<span data-type="title" id=toc-2691>٧٢ </span>- هؤلاء المشركون إذا تلا أحد عليهم آياتنا الواضحات، وفيها الدليل على صحة ما تدعو إليه - أيها النبى - وفساد عبادتهم، تلحظ فى وجوههم الحنق والغيظ الذى يستبد بهم، حتى ليكاد يدفعهم إلى الفتنة بالذين يتلون عليهم هذه الآيات. قل لهم - أيها النبى - تبكيتا وإنذاراً: هل تستمعو إلىَّ فأخبركم بشئ هو أشد عليكم شراً من الغيظ الذى يحرق نفوسكم؟ إنه هو النار التى توعَّد اللَّه بها الذين كفروا أمثالكم يوم القيامة، وما أسوأها مصيراً ومقاماً.',NULL,'499',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2670,'<span data-type="title" id=toc-2692>٧٣ </span>- يا أيها الناس: إنا نبرز أمامكم حقيقة عجيبة فى شأنها، فاستمعوا إليها وتدبروها: إن هذه الأصنام لن تستطيع أبدا خلق شئ مهما يكن تافها حقيراً كالذباب، وإن تضافروا جميعاً على خلقه، بل إن هذا المخلوق التافه، لو سَلَبَ من الأصنام شيئا من القرابين التى تقدم إليها، فإنها لا تستطيع بحال من الأحوال أن تمنعه عنه أو تسترده منه، وما أضعف الذى يُهَزْم أمام الذباب عن استرداد ما سلبه منه، وما أضعف نفس الذباب، كلاهما شديد الضعف، بل الأصنام كما ترون أشد ضعفا، فكيف يليق بإنسان عاقل أن يعبدها ويلتمس النفع منها؟ .',NULL,'500',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2671,'<span data-type="title" id=toc-2693>٧٤ </span>- هؤلاء المشركون ما عرفوا اللَّه حق معرفته، ولا عظموه حق تعظيمه حين أشركوا به العبادة أعْجَزَ الأشياء، مع أن اللَّه هو القادر على كل شئ، العزيز الذى لا يغلبه غالب.',NULL,'500',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2672,'<span data-type="title" id=toc-2694>٧٥ </span>- وقد اقتضت إرادة اللَّه وحكمته أن يختار من الملائكة رسلا، ويختار من البشر كذلك رسلا، ليُبلِّغوا شرعه إلى خلقه، فكيف تعترضون على من اختاره رسولا إليكم؟ إن اللَّه سميع لأقوال عباده، بصير بما يفعلون ومجازيهم عليه.',NULL,'500',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2673,'<span data-type="title" id=toc-2695>٧٦ </span>- وهو سبحانه يعلم أحوالهم الظاهرة والباطنة، لا تخفى عليه منهم خافية، وإليه - وحده - مرجع الأمور كلها.',NULL,'500',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2674,'<span data-type="title" id=toc-2696>٧٧ </span>- يا أيها الذين آمنوا لا تلتفتوا إلى تضليل الكفار، واستمروا على أداء صلاتكم تامة وافية راكعين ساجدين، واعبدوا ربكم الذى خلقكم ورزقكم، ولا تشركوا به أحدا، واعملوا كل ما فيه خير ونفع، كى تكونوا من المصلحين السعداء فى أخراكم ودنياكم.',NULL,'500',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2675,'<span data-type="title" id=toc-2697>٧٨ </span>- وجاهدوا فى سبيل إعلاء كلمة اللَّه وابتغاء مرضاته حتى تنتصروا على أعدائكم وشهواتكم، لأنه سبحانه قربكم إليه، واختاركم لنصرة دينه، وجعلكم أمة وسطا، ولم يكلفكم فيما شرعه لكم ما فيه مشقة عليكم لا تحتملونها، ويسر عليكم ما يعترضكم من مشقة لا تطيقونها. بما فرضه لكم من أنواع الرُّخَص، فالزموا دين أبيكم إبراهيم فى مبادئه وأسسه، وهو سبحانه الذى سمَّاكم المسلمين فى الكتب المنزلة السابقة، وبإذعانكم لما شرعه الله لكم، تكونون كما سمَّاكم اللَّه، فتكون عاقبتكم أن يشهد رسولكم بأنه بلغكم، وعلمتم بما بلغكم به، فتسعدوا، وتكونوا شهداء على الأمم السابقة بما جاء فى القرآن من أن رسلها بلّغتها، وإذا كان اللَّه قد خصكم بهذه الميزات كلها، فمن الواجب عليكم أن تقابلوها بالشكر والطاعة له، فتقيموا الصلاة على أتم وجوهها، وتعطوا الزكاة لمستحقيها، وتتوكلوا على اللَّه فى كل أموركم، وتستمدوا منه العون. فهو معينكم وناصركم. فنعم المولى ونعم النصير.',NULL,'501',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2676,'<span data-type="title" id=toc-2699>١ </span>- تَحَقَّق الفلاح للمؤمنين بالله وبما جاءت به الرسل، وفازوا بأمانيهم.',NULL,'502',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2677,'<span data-type="title" id=toc-2700>٢ </span>- الذين ضموا إلى إيمانهم العمل الصالح، هم فى صلاتهم متوجهون إلى الله بقلوبهم، خائفون منه، متذللون له، يَحُسُّون بالخضوع المطلق له.',NULL,'502',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2678,'<span data-type="title" id=toc-2701>٣ </span>- هم مؤثرون للجد، معرضون عمَّا لا خير فيه من قول وعمل.',NULL,'502',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2679,'<span data-type="title" id=toc-2702>٤ </span>- وهم محافظون على أداء الزكاة إلى مستحقيها، وبذلك يجمعون بين العبادات البدنية والعبادات المالية، وبين تطهير النفس وتطهير المال.',NULL,'502',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2680,'<span data-type="title" id=toc-2703>٥ </span>- وهم يحافظون على أنفسهم من أن تكون لها علاقة بالنساء.',NULL,'502',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2681,'<span data-type="title" id=toc-2704>٦ </span>- إلا بطريق الزواج الشرعى أو بملكية الجوارى فلا مؤاخذة عليهم فيه.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2682,'<span data-type="title" id=toc-2705>٧ </span>- فمن أراد الاتصال بالمرأة عن غير هذين الطريقين فهو متعدٍ للحدود المشروعة غاية التعدى.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2683,'<span data-type="title" id=toc-2706>٨ </span>- وهم محافظون على كل ما ائتمنوا عليه من مال، أو قول، أو عمل، أو غير ذلك، وعلى كل عهد بينهم وبين الله أو بينهم وبين الناس، فلا يخونون الأمانات ولا ينقضون العهود.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2684,'<span data-type="title" id=toc-2707>٩ </span>- وهم مداومون على أداء الصلاة فى أوقاتها، محققون لأركانها وخشوعها، حتى تؤدى إلى المقصود منها، وهو الانتهاء عن الفحشاء والمنكر.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2685,'<span data-type="title" id=toc-2708>١٠ </span>- هؤلاء الموصوفون هم الذين يرثون الخير كله، وينالونه يوم القيامة.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2686,'<span data-type="title" id=toc-2709>١١ </span>- هم الذين يتفضل الله عليهم بالفردوس، أعلى مكان فى الجنة، يتمتعون فيه دون غيرهم.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2687,'<span data-type="title" id=toc-2710>١٢ </span>- وأن على الناس أن ينظروا إلى أصل تكوينهم، فإنه من دلائل قدرتنا الموجبة للإيمان بالله وبالبعث، فإننا خلقنا الإنسان من خلاصة الطين.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2688,'<span data-type="title" id=toc-2711>١٣ </span>- ثم خلقنا نسله فجعلناه نطفة - أى ماء فيه كل عناصر الحياة الأولى - تستقر فى الرحم، وهو مكان مستقر حصين.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2689,'<span data-type="title" id=toc-2712>١٤ </span>- ثم صيَّرنا هذه النطفة بعد تلقيح البويضة والإخصاب دما، ثم صيَّرنا الدم بعد ذلك قطعة لحم، ثم صيرناها هيكلا عظميا، ثم كسونا العظام باللحم، ثم أتممنا خلقه فصار فى النهاية بعد نفخ الروح فيه خلقاً مغايراً لمبدأ تكوينه، فتعالى شأن الله فى عظمته وقدرته، فهو لا يشبه أحد فى خلقته وتصويره وإبداعه.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2690,'<span data-type="title" id=toc-2713>١٥ </span>- ثم إنكم - يا بنى آدم - بعد ذلك الذى ذكرناه من أمركم صائرون إلى الموت لا محالة.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2691,'<span data-type="title" id=toc-2714>١٦ </span>- ثم إنكم تبعثون يوم القيامة للحساب والجزاء.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2692,'<span data-type="title" id=toc-2715>١٧ </span>- وإننا قد خلقنا سبع سموات مرتفعة فوقكم، فيها مخلوقات لم نغفل عنها فحفظناها ودبرناها، ونحن لا نغفل عن جميع المخلوقات، بل نحفظها كلها من الزوال والاختلال، وندبر كل أمورها بالحكمة.',NULL,'503',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2693,'<span data-type="title" id=toc-2716>١٨ </span>- وأنزلنا من السماء مطراً بحكمة وتقدير فى تكوينه وإنزاله، وتيسيراً للانتفاع به جعلناه مستقراً فى الأرض على ظهرها وفى جوفها، وإنا لقادرون على إزالته وعدم تمكينكم من الانتفاع به، ولكنا لم نفعل رحمة بكم، فآمنوا بخالقه واشكروه.',NULL,'504',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2694,'<span data-type="title" id=toc-2717>١٩ </span>- فخلقنا لكم بهذا الماء حدائق من نخيل وأعناب لكم فيها فواكه كثيرة، ومنها تأكلون.',NULL,'504',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2695,'<span data-type="title" id=toc-2718>٢٠ </span>- وخلقنا لكم شجرة الزيتون التى تنبت فى منطقة طور سيناء، وفى ثمارها زيت تنتفعون به، وهو إدام للآكلين.',NULL,'504',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2696,'<span data-type="title" id=toc-2719>٢١ </span>- وإن لكم فى الأنعام - وهى الإبل والبقر والغنم - ما يدل على قدرتنا وتفضلنا عليكم بالنعم، نسقيكم لبناً مستخرجاً مما فى بطونها خالصاً سائغاً سهلا للشاربين، ولكم فيها سوى اللبن منافع كثيرة كاللحوم والأصواف والأوبار، ومنها تعيشون وترزقون.',NULL,'504',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2697,'<span data-type="title" id=toc-2720>٢٢ </span>- وعلى هذه الأنعام وعلى السفن تركبون وتحملون الأثقال، فخلقنا لكم وسائل الانتقال والحمل فى البر والبحر، وبها يكون الاتصال بينكم.',NULL,'504',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2698,'<span data-type="title" id=toc-2721>٢٣ </span>- وفى قصص الأولين عبرة لكم لتؤمنوا، فقد أرسلنا نوحاً إلى قومه، فقال لهم: يا قوم اعبدوا الله وحده، فليس لكم إله يستحق العبادة غيره، ألا تخافون عقابه، وزوال نعمه إن عصيتم.',NULL,'504',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2699,'<span data-type="title" id=toc-2722>٢٤ </span>- فقال الكبراء من قومه الذين كفروا منكرين لدعوته صادِّين العامة عن اتباعه: لا فرق بين نوح وبينكم، فهو مثلكم فى البشرية، ولكنه يريد أن يتميز عليكم بهذه الدعوة، ولو كان هناك رسل من الله - كما يزعم - لأرسلهم ملائكة، ما سمعنا فى تاريخ آبائنا السابقين بهذه الدعوة، ولا بإرسال بشر رسولا.',NULL,'505',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2700,'<span data-type="title" id=toc-2723>٢٥ </span>- ما هو إلا رجل به جنون، ولذلك قالوا: فانتظروا واصبروا عليه حتى ينكشف جنونه، أو يحين هلاكه.',NULL,'505',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2701,'<span data-type="title" id=toc-2724>٢٦ </span>- دعا نوح ربه بعد ما يئس من إيمانهم، فقال: يا رب انصرنى عليهم، وانتقم منهم بسبب تكذيبهم لدعوتى.',NULL,'505',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2702,'<span data-type="title" id=toc-2725>٢٧ </span>- فقلنا له عن طريق الوحى: اصنع السفينة وعنايتنا ترعاك، فتدفع عنك شرهم ونرشدك فى عملك، فإذا حل ميعاد عذابهم، ورأيت التنور يفور ماء بأمرنا، فأدخل فى السفينة من كل نوع من الكائنات الحية ذكراً وأنثى، وأدخل أهلك أيضاً إلا من تقرر تعذيبهم لعدم إيمانهم، ولا تسألنى نجاة الذين ظلموا أنفسهم وغيرهم بالكفر والطغيان، فإنى حكمت بإغراقهم لظلمهم بالإشراك والعصيان.',NULL,'505',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2703,'<span data-type="title" id=toc-2726>٢٨ </span>- فإذا ركبت واستقررت أنت ومن معك فى السفينة فقل شاكراً ربك: الحمد لله الذى نجانا من شر القوم الكافرين الطاغين.',NULL,'505',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2704,'<span data-type="title" id=toc-2727>٢٩ </span>- وقل: يا رب مكّنِّى من النزول فى منزل مبارك تطيب الإقامة فيه عند النزول إلى الأرض، وهبْ لى الأمن فيه، فأنت - وحدك - الذى تُنزل فى مكان الخير والأمن والسلام.',NULL,'505',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2705,'<span data-type="title" id=toc-2728>٣٠ </span>- إن فى هذه القصة عبراً ومواعظ، وإنا نختبر العباد بالخير وبالشر، وفى أنفسهم الاستعداد لكل منها.',NULL,'505',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2706,'<span data-type="title" id=toc-2729>٣١ </span>- ثم خلقنا من بعد نوح طبقة من الناس غيرهم وهم عاد.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2707,'<span data-type="title" id=toc-2730>٣٢ </span>- فأرسلنا إليهم هودا وهو منهم، وقلنا لهم على لسانه: اعبدوا الله - وحده - فليس لكم إله يستحق العبادة غيره، وهو - وحده - الجدير بأن تخافوه، فهلا خفتم عقابه إن عصيتموه؟ .',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2708,'<span data-type="title" id=toc-2731>٣٣ </span>- وقال الكبراء من قومه الذين كفروا وكذبوا بلقاء الله وما فى الآخرة من حساب وجزاء، وأعطيناهم أكبر حظ من الترف والنعيم، قالوا منكرين عليه دعوته، صادين العامة عن اتباعه: لا فرق بين هود وبينكم، فما هو إلا بشر مماثل لكم فى البشرية، يأكل من جنس ما تأكلون منه، ويشرب من جنس ما تشربون، ومثل هذا لا يكون رسولا لعدم تميزه عنكم.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2709,'<span data-type="title" id=toc-2732>٣٤ </span>- وحذروهم فى قوة وتأكيد، فقالوا: إن أطعتم رجلا يماثلكم فى البشرية، فأنتم حقاً خاسرون لعدم انتفاعكم بطاعته.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2710,'<span data-type="title" id=toc-2733>٣٥ </span>- وقالوا لهم أيضاً منكرين للبعث: أيعدكم - هود - أنكم تبعثون من قبوركم بعد أن تموتوا وتصيروا تراباً وعظاماً مجردة من اللحوم والأعصاب؟',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2711,'<span data-type="title" id=toc-2734>٣٦ </span>- إن ما وعدكم به بعيد جداً، ولن يكون أبداً.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2712,'<span data-type="title" id=toc-2735>٣٧ </span>- ليس هناك إلا حياة واحدة هى هذه الحياة الدنيا التى نجد فيها الموت والحياة يتواردان علينا، فمولود يولد وحى يموت، ولن نبعث بعد الموت أبداً.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2713,'<span data-type="title" id=toc-2736>٣٨ </span>- ما هو إلا رجل كذب على الله، وادعى أن الله أرسله، وكذب فيما يدعو إليه، ولن نصدقه أبداً.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2714,'<span data-type="title" id=toc-2737>٣٩ </span>- قال هود بعد ما يئس من إيمانهم: يا رب انصرنى عليهم وانتقم منهم، بسبب تكذيبهم لدعوتى.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2715,'<span data-type="title" id=toc-2738>٤٠ </span>- قال الله له مؤكداً وعده: سيندمون بعد قليل من الزمن على ما فعلوا عندما يحل بهم العذاب.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2716,'<span data-type="title" id=toc-2739>٤١ </span>- فأخذتهم صيحة شديدة أهلكتهم لاستحقاقهم ذلك الهلاك، وجعلناهم فى الحقارة والضعف كالشئ الذى يجرفه السيل أمامه من أعواد الشجر وأوراقه. هلاكاً وبُعداً عن الرحمة للظالمين بكفرهم وطغيانهم.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2717,'<span data-type="title" id=toc-2740>٤٢ </span>- ثم خلقنا من بعدهم أقواماً غيرهم، كقوم صالح ولوط وشعيب.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2718,'<span data-type="title" id=toc-2741>٤٣ </span>- لكل أمة زمنها المعيَّن لها، لا تتقدم عنه ولا تتأخر.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2719,'<span data-type="title" id=toc-2742>٤٤ </span>- ثم أرسلنا رسلنا متتابعين كلا إلى قومه، وكلما جاء رسول إلى قومه كذَّبوه فى دعوته، فأهلكناهم متتابعين، وجعلنا أخبارهم أحاديث يرددها الناس ويعجبون منها، فَبُعْداً عن الرحمة وهلاكاً لقوم لا يصدقون الحق ولا يذعنون له.',NULL,'507',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2720,'<span data-type="title" id=toc-2743>٤٥ </span>- ثم أرسلنا موسى وأخاه هارون بالدلائل القاطعة الدالة على صدقهما، وبحجة واضحة تبيِّن أنهما قد أرسلا من عندنا.',NULL,'507',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2721,'<span data-type="title" id=toc-2744>٤٦ </span>- أرسلناهما إلى فرعون وقومه فامتنعوا فى تكبر عن الإيمان، وهم قوم موصوفون بالكبر والتعالى والقهر.',NULL,'507',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2722,'<span data-type="title" id=toc-2745>٤٧ </span>- وقالوا فى تعجب وإنكار: أنؤمن بدعوة رجلين مماثلين لنا فى البشرية، وقومها - بنو إسرائيل - خاضعون لنا ومطيعون كالعبيد؟ .',NULL,'507',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2723,'<span data-type="title" id=toc-2746>٤٨ </span>- فكذبوهما فى دعوتهما فكانوا من المهلكين بالغرق.',NULL,'507',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2724,'<span data-type="title" id=toc-2747>٤٩ </span>- ولقد أوحينا إلى موسى بالتوراة، ليهتدى قومه بما فيها من إرشادات إلى الأحكام وأسباب السعادة.',NULL,'507',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2725,'<span data-type="title" id=toc-2748>٥٠ </span>- وجعلنا عيسى ابن مريم وأمه - فى حملها به من غير أن يمسها بشر وولادته من غير أب - دلالة قاطعة على قدرتنا البالغة، وأنزلناها فى أرض مرتفعة منبسطة تستقر فيها الإقامة ويتوافر الماء الذى هو دعامة العيش الرغيد.',NULL,'507',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2726,'<span data-type="title" id=toc-2749>٥١ </span>- وقلنا للرسل ليبلغوا أقوامهم: كلوا من أنواع الحلال الطيب، وتمتعوا واشكروا نعمتى بعمل الصالحات، إنى عليم بما تعملون ومجاز لكم عليه.',NULL,'507',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2727,'<span data-type="title" id=toc-2750>٥٢ </span>- وقلنا لهم ليبلغوا أقوامهم: إن هذا الدين الذى أرسلتكم به دين واحد فى العقائد وأصول الشرائع، وإنكم أمة واحدة فى كل الأجيال، منهم المهتدى ومنهم الضال، وأنا ربكم الذى أمرتكم باتباعه فخافوا عقابى إن عصيتم.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2728,'<span data-type="title" id=toc-2751>٥٣ </span>- فقطع الناس أمر دينهم، فمنهم المهتدون ومنهم الضالون الذين اتبعوا أهواءهم، فتفرقوا بسبب ذلك جماعات مختلفة متعادية، كل جماعة فرحة بما هى عليه، ظانة أنه - وحده - الصواب.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2729,'<span data-type="title" id=toc-2752>٥٤ </span>- فاترك الكافرين - يا محمد - فى جهالتهم وغفلتهم ما دمت قد نصحتهم حتى يقضى الله فيهم بالعذاب بعد حين.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2730,'<span data-type="title" id=toc-2753>٥٥ </span>- أيظن هؤلاء العاصون أنا إذ نتركهم يتمتعون بما أعطيناهم من المال والبنين.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2731,'<span data-type="title" id=toc-2754>٥٦ </span>- نكون قد رضينا عنهم، فتفيض عليهم الخيرات بسرعة وكثرة، إنهم كالبهائم لا يشعرون لعدم استخدامهم عقولهم، إننى غير راض عنهم، وإن هذه النعم استدراج منا لهم.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2732,'<span data-type="title" id=toc-2755>٥٧ </span>- إن الذين هم يخشون الله ويهابونه وقد تربت فيهم المخافة منه سبحانه.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2733,'<span data-type="title" id=toc-2756>٥٨ </span>- والذين هم يؤمنون بآيات ربهم الموجودة فى الكون، والمتلوة فى الكتب المنزلة.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2734,'<span data-type="title" id=toc-2757>٥٩ </span>- والذين هم لا يشركون بالله أحداً.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2735,'<span data-type="title" id=toc-2758>٦٠ </span>- والذين يعطون مما رزقهم الله، ويؤدون عملهم وهم خائفون من التقصير، لأنهم راجعون إلى الله بالبعث ومحاسبون.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2736,'<span data-type="title" id=toc-2759>٦١ </span>- أولئك يسارعون بأعمالهم إلى نيل الخيرات، وهم سابقون غيرهم فى نيلها.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2737,'<span data-type="title" id=toc-2760>٦٢ </span>- ونحن لا نكلف أحداً إلا بما يستطيع أن يؤديه، لأنه داخل فى طاقته، وكل عمل من أعمال العباد مسجل عندنا فى كتاب، وسنخبرهم به كما هو، وهم لا يظلمون بزيادة عقاب أو نقص ثواب.',NULL,'508',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2738,'<span data-type="title" id=toc-2761>٦٣ </span>- لكن الكافرين بسبب عنادهم وتعصبهم غافلون عن عمل الخير والتكليف بالمستطاع ودقة الحساب، وإلى جانب ذلك لهم أعمال أخرى خبيثة مداومون عليها.',NULL,'509',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2739,'<span data-type="title" id=toc-2762>٦٤ </span>- فإذا أوقعنا العذاب بالأغنياء المترفين ضجوا وصرخوا مستغيثين.',NULL,'509',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2740,'<span data-type="title" id=toc-2763>٦٥ </span>- فنقول لهم: لا تصرخوا ولا تستغيثوا الآن، فلن تفلتوا من عذابنا، ولن ينفعكم صراخكم شيئاً.',NULL,'509',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2741,'<span data-type="title" id=toc-2764>٦٦ </span>- لا عذر لكم، فقد كانت آياتى الموحَى بها تُقرأ عليكم، فكنتم تُعرضون عنها إعراضاً يقلب أحوالكم، ولا تصدقونها ولا تعملون بها.',NULL,'509',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2742,'<span data-type="title" id=toc-2765>٦٧ </span>- وكنتم فى إعراضكم متكبرين مستهزئين، تصفون الوحى بالأوصاف القبيحة عندما تجتمعون للسمر.',NULL,'509',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2743,'<span data-type="title" id=toc-2766>٦٨ </span>- أَجَهِلَ هؤلاء المعرضون فلم يتدبروا القرآن ليعلموا أنه حق؟ أم كانت دعوة محمد لهم غريبة عن الدعوات التى جاء بها الرسل إلى الأقوام السابقين الذين أدركهم آباؤهم؟ .',NULL,'509',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2744,'<span data-type="title" id=toc-2767>٦٩ </span>- أم لم يعرفوا رسولهم - محمداً - الذى نشأ بينهم فى أخلاقه العالية التى لا يعهد معها الكذب، فهم ينكرون دعوته الآن بغياً وحسداً؟ .',NULL,'509',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2745,'<span data-type="title" id=toc-2768>٧٠ </span>- أم يقولون: إنه مجنون؟ كلا: إنه جاءهم بالدين الحق، وأكثرهم كارهون للحق، لأنه يخالف شهواتهم وأهواءهم فلا يؤمنون به.',NULL,'509',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2746,'<span data-type="title" id=toc-2769>٧١ </span>- ولو كان الحق تابعاً لأهوائهم لشاع الفساد فى الأرض ولتنازعت الأهواء، ولكنا أرسلنا إليهم القرآن الذى يُذَكِّرهم بالحق الذى يجب أن يجتمع عليه الجميع، ومع ذلك هم معرضون عنه.',NULL,'509',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2747,'<span data-type="title" id=toc-2770>٧٢ </span>- بل أَتطلب منهم - أيها النبى - أجراً على أداء الرسالة؟ لم يكن ذلك، فإن أجر ربك خير مما عندهم، وهو خير المعطين.',NULL,'509',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2748,'<span data-type="title" id=toc-2771>٧٣ </span>- وإنك - يا محمد - لتدعوهم إلى الدين الذى هو الطريق المستقيم الموصل إلى السعادة.',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2749,'<span data-type="title" id=toc-2772>٧٤ </span>- وإن الذين لا يؤمنون بالآخرة وما فيها من نعيم أو جحيم يعدلون عن الطريق المستقيم الذى يأمن السائر فيه من السَّير إلى طريق الحيرة والاضطراب والفساد.',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2750,'<span data-type="title" id=toc-2773>٧٥ </span>- ولو رحمناهم وأزلنا عنهم ما نزل بهم من ضرر فى أبدانهم وقحط فى أموالهم ونحو ذلك، لزادوا كفراً، وتمادوا فى الطغيان.',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2751,'<span data-type="title" id=toc-2774>٧٦ </span>- ولقد عذَّبناهم بعذاب أصابهم كالقتل أو الجوع فما خضعوا بعده لربهم، بل أقاموا على عتوهم واستكبارهم بمجرد زواله.',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2752,'<span data-type="title" id=toc-2775>٧٧ </span>- سيستمرون على إعراضهم حتى إذا عذَّبناهم عذاباً شديداً بالجوع أو القتل فى الدنيا صاروا حيارى يائسين من كل خير، لا يجدون مخلصاً.',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2753,'<span data-type="title" id=toc-2776>٧٨ </span>- وكيف تكفرون بالله وهو الذى أنشأ لكم السمع لتسمعوا الحق، والأبصار لتروا الكون وما فيه، والعقول لتدركوا عظمته فتؤمنوا؟ . إنكم لم تشكروا خالقها بالإيمان والطاعة إلا قليلا أىَّ قلة.',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2754,'<span data-type="title" id=toc-2777>٧٩ </span>- وهو الذى خلقكم فى الأرض، وإليه - وحده - تُجمعون للجزاء يوم القيامة.',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2755,'<span data-type="title" id=toc-2778>٨٠ </span>- وهو الذى يحيى ويميت، وبأمره وقوانينه تعاقب الليل والنهار واختلافهما طولا وقصراً، ألا تعقلون دلالة ذلك على قدرته ووجوب الإيمان به، وبالبعث؟ .',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2756,'<span data-type="title" id=toc-2779>٨١ </span>- لم يفعلوا ذلك، بل قلدوا السابقين المكذبين، فقالوا: مثل قولهم.',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2757,'<span data-type="title" id=toc-2780>٨٢ </span>- قالوا منكرين للبعث: أنبعث بعد الموت وبعد أن نصير تراباً وعظاماً؟ .',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2758,'<span data-type="title" id=toc-2781>٨٣ </span>- لقد وُعدنا ووعد آباؤنا من قبلنا بذلك، وما هذا الوعد إلا أكاذيب السابقين التى سَطروها.',NULL,'510',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2759,'<span data-type="title" id=toc-2782>٨٤ </span>- قل لهم يا محمد: من الذى ملك الأرض ومن فيها من الناس وسائر المخلوقات؟ إن كان لكم علم فأجيبونى.',NULL,'512',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2760,'<span data-type="title" id=toc-2783>٨٥ </span>- سيقرون بأن الأرض لله، قل لهم إذن: فلم تشركون به؟ ألا تذكرون أن من يملك ذلك جدير بأن يُعبد وحده؟',NULL,'512',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2761,'<span data-type="title" id=toc-2784>٨٦ </span>- قل لهم أيضاً: من رب السموات السبع ورب العرش العظيم؟',NULL,'512',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2762,'<span data-type="title" id=toc-2785>٨٧ </span>- سيقرون بأنه هو الله. قل لهم إذن: ألا تخافون عاقبة الشرك والكفر والعصيان لصاحب هذا الخلق العظيم؟',NULL,'512',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2763,'<span data-type="title" id=toc-2786>٨٨ </span>- قل لهم أيضاً: من بيده مُلْك كل شئ، ومن له الحكم المطلق فى كل شئ، وهو يحمى بقدرته من يشاء، ولا يمكن لأحد أن يحمى أحداً من عذابه؟ إن كنتم تعلمون جواباً فأجيبوا.',NULL,'512',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2764,'<span data-type="title" id=toc-2787>٨٩ </span>- سيقرون بأنه هو الله، قل لهم: إذن كيف تُخْدعون بالهوى ووحى الشياطين، وتنصرفون عن طاعة الله.',NULL,'512',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2765,'<span data-type="title" id=toc-2788>٩٠ </span>- لقد بيَّنا لهم الحق على لسان الرسل، وإنهم لكاذبون فى كل ما يخالف هذا الحق.',NULL,'512',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2766,'<span data-type="title" id=toc-2789>٩١ </span>- ما اتخذ الله له ولدا، وقد تنزَّه عن ذلك، وما كان له شريك. إذ لو كان له شريك لاستبد كل بما خلق، وصار له ملكه، ولتناحر بعضهم مع بعض كما يُرى بين الملوك، ولفسد الكون بهذا التنازع، فتنزه الله عما يقوله المشركون مما يخالف الحق.',NULL,'512',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2767,'<span data-type="title" id=toc-2790>٩٢ </span>- هو محيط بكل شئ علما، يعلم ما يغيب عنا وما يظهر لنا، فتنزه الله عما ينسبه الكافرون إليه من وجود الشريك له.',NULL,'512',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2768,'<span data-type="title" id=toc-2791>٩٣ </span>- قل - يا أيها النبى -: يا رب، إن أنزلت بهم ما أوعدتهم من العذاب فى الدنيا، وأنا موجود بينهم.',NULL,'512',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2769,'<span data-type="title" id=toc-2792>٩٤ </span>- فأتوسل إليك ألا تجعلنى معذباً مع القوم الكافرين الطاغين.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2770,'<span data-type="title" id=toc-2793>٩٥ </span>- ونحن قادرون تماماً على أن نريك ما أوعدناهم به من العذاب نازلا بهم، فاطمئن لنصرنا.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2771,'<span data-type="title" id=toc-2794>٩٦ </span>- استمر فى دعوتك وقابل إساءتهم بالعمل الذى هو أحسن من العفو أو غيره، ونحن عالمون تماماً بما يصفونك به، ويصفون دعوتك من سوء وافتراء، وسنجازيهم عليه.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2772,'<span data-type="title" id=toc-2795>٩٧ </span>- وقل: يا رب أستعيذ بك من أثر وساوس الشياطين على نفسى بعملى ما لا يرضيك.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2773,'<span data-type="title" id=toc-2796>٩٨ </span>- وأستعيذ بك يا رب، أن يكونوا معى فى أى عمل من الأعمال، ليكون سليماً خالصاً لوجهك الكريم.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2774,'<span data-type="title" id=toc-2797>٩٩ </span>- سيستمرون على تكذيبهم، حتى إذا حَلَّ موعد موت أحدهم ندم، وقال: يا رب رُدَّنى إلى الدنيا.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2775,'<span data-type="title" id=toc-2798>١٠٠ </span>- لأعمل عملا صالحاً فيما تركته من مالى أو حياتى وعمرى، ولن يجاب إلى طلبه، فهذا كلام يقوله دون فائدة لا يقبل منه، ولو استُجيب له لم يعمل به، ومع ذلك فلن يعود أبداً، فالموت حاجز بينهم وبين ما يتمنون إلى أن يبعثهم الله.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2776,'<span data-type="title" id=toc-2799>١٠١ </span>- فإذا جاء موعد البعث بعثناهم بدعوتهم إلى الخروج من مقابرهم، وذلك بما يشبه النفخ فى البوق فيجيئون متفرقين، لا تنفع أحداً قرابة أحد، ولا يسأل بعضهم بعضا شيئاً ينفعه، فلكل منهم يومئذ ما يشغله.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2777,'<span data-type="title" id=toc-2800>١٠٢ </span>- فالعمل هو ميزان التقدير، فمن كانت لهم عقائد سليمة وأعمال صالحة لها وزن فى ميزان الله، فأولئك هم الفائزون.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2778,'<span data-type="title" id=toc-2801>١٠٣ </span>- ومن لم يكن لهم حسنات أو أعمال لها وزن عند الله، فأولئك هم الذين خسروا أنفسهم ببيعها للشيطان، وهم معذَّبون فى النار، خالدون فيها.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2779,'<span data-type="title" id=toc-2802>١٠٤ </span>- تحرق النار وجوههم، وهم فيها عابسون من سوء مصيرهم.',NULL,'513',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2780,'<span data-type="title" id=toc-2803>١٠٥ </span>- يؤنبهم الله ويقول لهم: قد كانت آياتى المنزلة تُقْرأ عليكم فى الدنيا، فكنتم تكذبون بما فيها.',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2781,'<span data-type="title" id=toc-2804>١٠٦ </span>- قالوا مقرين بخطئهم: ربنا كثرت معاصينا التى أورثتنا الشقاء، وكنا بذلك ضالين عن طريق الصواب.',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2782,'<span data-type="title" id=toc-2805>١٠٧ </span>- وقالوا: ربنا، أخرجنا من النار وأعدنا إلى الدنيا، فإن عدنا إلى الكفر والعصيان كنا ظالمين لأنفسنا.',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2783,'<span data-type="title" id=toc-2806>١٠٨ </span>- قال الله لهم تحقيراً: اسكتوا أذلاء مهانين، ولا تكلمونى مطلقاً.',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2784,'<span data-type="title" id=toc-2807>١٠٩ </span>- ما ظلمتكم بل ظلمتم أنفسكم، إذ كان المؤمنون الصالحون من عبادى يقولون فى الدنيا: ربنا آمنا فاغفر لنا وارحمنا وأنت خير الراحمين.',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2785,'<span data-type="title" id=toc-2808>١١٠ </span>- فكنتم تسخرون منهم دائماً، حتى أنساكم الاشتغال بالسخرية منهم ذكرى وعبادتى فلم تؤمنوا وتطيعوا، وكنتم منهم تضحكون استهزاء.',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2786,'<span data-type="title" id=toc-2809>١١١ </span>- إنى جزيتهم اليوم بالفلاح، لأنهم صبروا على سخريتكم وإيذائكم.',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2787,'<span data-type="title" id=toc-2810>١١٢ </span>- قال الله للكافرين: كم سنة عشتموها فى الدنيا؟ .',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2788,'<span data-type="title" id=toc-2811>١١٣ </span>- قالوا - استقصاراً لمدة معيشتهم بالنسبة لطول مكثهم فى العذاب -: عشنا يوماً أو بعض يوم، فاسأل من يتمكنون من العدِّ، لأنا مشغولون بالعذاب.',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2789,'<span data-type="title" id=toc-2812>١١٤ </span>- فيقول الله لهم: ما عشتم فى الدنيا إلا زمناً قليلا. ولو أنكم كنتم تعلمون عاقبة الكفر والعصيان وأن متاع الدنيا قليل، لآمنتم وأطعتم.',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2790,'<span data-type="title" id=toc-2813>١١٥ </span>- أظننتم أننا خلقناكم بغير حكمة فأفسدتم فى الأرض، وظننتم أنكم لا تبعثون لمجازاتكم؟ كلا.',NULL,'514',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2791,'<span data-type="title" id=toc-2814>١١٦ </span>- العظمة لله - وحده - هو مالك الملك كله، لا معبود بحق سواه، هو صاحب العرش العظيم.',NULL,'515',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2792,'<span data-type="title" id=toc-2815>١١٧ </span>- ومن يعبد مع الله إلهاً آخر لا دليل له على استحقاقه العبودية. فإن الله يعاقبه على شركه لا محالة، إن الكافرين لا يفلحون، وإنما الذى يفلح هم المؤمنون.',NULL,'515',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2793,'<span data-type="title" id=toc-2816>١١٨ </span>- وقل - يا أيها النبى - داعياً الله ضارعاً إليه -: يا رب اغفر لى ذنبى، وارحمنى فأنت خير الراحمين، لأن رحمتك واسعة وقريبة من المحسنين.',NULL,'515',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2794,'<span data-type="title" id=toc-2818>١ </span>- هذه سورة أوحينا بها وأوجبنا أحكامها. ونزلنا فيها دلائل واضحة على قدرة الله ووحدانيته. وعلى أن هذا الكتاب من عند الله، لتتعظوا بها.',NULL,'516',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2795,'<span data-type="title" id=toc-2819>٢ </span>- ومن تلك الأحكام حكم الزانية والزانى فاضربوا كل واحد منهما مائة جلدة، ولا يمنعكم شئ من الرأفة بهما عن تنفيذ الحكم، إن كنتم تؤمنون بالله واليوم الآخر. لأن مقتضى الإيمان إيثار رضا الله على رضا الناس، ولْيحضر تنفيذ الحكم فيهما جماعة من المؤمنين. ليكون العقاب فيه ردع لغيرهما.',NULL,'516',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2796,'<span data-type="title" id=toc-2820>٣ </span>- الخبيث الذى من دأبه الزنا، لا يرغب إلا فى نكاح خبيثة عرفت الزنا أو الشرك، والخبيثة التى من دأبها الزنا لا يرغب فى نكاحها إلا خبيث عرف بالزنا أو الشرك. ولا يليق هذا النكاح بالمؤمنين لما فيه من التشبه بالفسق. والتعرض للتهم.',NULL,'518',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2797,'<span data-type="title" id=toc-2821>٤ </span>- والذين يتهمون العفيفات النزيهات بالزنا، ثم لم يأتوا بأربعة شهود يثبتون صدق الاتهام، فعاقبوهم بالضرب ثمانين جلدة وبعدم قبول شهادتهم على أى شئ كان مدى الحياة، فهؤلاء هم الجديرون باسم الخارجين خروجاً شنيعاً على حدود الدين.',NULL,'518',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2798,'<span data-type="title" id=toc-2822>٥ </span>- لكن من تاب منهم فندم على هذه المعصية، وعزم على الطاعة وظهر صدق توبته بصدق سلوكه، فإن الله يتجاوز عن عقابه.',NULL,'518',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2799,'<span data-type="title" id=toc-2823>٦ </span>- والذين يتهمون زوجاتهم بالزنا، ولم يكن هناك عدد يشهد بصدق اتهامهم، فيطالب الواحد منهم ليدفع عن نفسه الحد والعقوبة بأن يشهد بالله أربع مرات أنه صادق فى هذا الاتهام.',NULL,'518',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2800,'<span data-type="title" id=toc-2824>٧ </span>- ويذكر فى المرة الخامسة أنه يستحق الطرد من رحمة الله إن كان من الكاذبين فى ذلك.',NULL,'518',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2801,'<span data-type="title" id=toc-2825>٨ </span>- ولو سكتت الزوجة بعد ذلك أقيم عليها عقوبة الزنا، ولكى تدفع عنها العقوبة يجب عليها أن تشهد بالله أربع مرات أن الزوج كاذب فى اتهامه إياها بالزنا.',NULL,'518',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2802,'<span data-type="title" id=toc-2826>٩ </span>- وتذكر فى المرة الخامسة أنها تستحق أن ينزل بها غضب الله، إن كان من الصادقين فى هذا الاتهام.',NULL,'518',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2803,'<span data-type="title" id=toc-2827>١٠ </span>- ولولا تفضل الله عليكم ورحمته بكم - وإنه كثير قبول التوبة من عباده، وحكيم فى كل أفعاله - لما شرع لكم هذه الأحكام، ولعجل عقوبتكم فى الدنيا على المعصية.',NULL,'518',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2804,'<span data-type="title" id=toc-2828>١١ </span>- إن الذين اخترعوا الكذب الصارف عن كل هداية بالنسبة لعائشة زوج النبى - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - إذ أشاعوا حولها الإفك والكذب - هم جماعة ممن يعيشون معكم، لا تظنوا هذه الحادثة شراً لكم بل هى خير لكم، لأنها ميَّزت المنافقين من المؤمنين الخالصين، وأظهرت كرامة المبرئين منها، والمتألمين، ولكل شخص من هذه الجماعة المتهمة جزاؤه على مقدار اشتراكه فى هذا الاتهام، ورأس هذه الجماعة له عذاب عظيم لعظم جرمه.',NULL,'519',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2805,'<span data-type="title" id=toc-2829>١٢ </span>- كان مقتضى الإيمان أنكم عند سماع خبر التهمة؛ أن يظن المؤمنون والمؤمنات بأنفسهم خيراً من العفاف والطهر، وأن يقولوا فى إنكار: هذا كذب واضح البطلان، لتعلقه بأكرم المرسلين وأكرم الصديقات.',NULL,'519',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2806,'<span data-type="title" id=toc-2830>١٣ </span>- هلاَّ أحضر القائمون بالاتهام أربعة شهود يشهدون على ما قالوا؟ إنهم لم يفعلوا ذلك. وإذ لم يفعلوا فأولئك فى حكم الله هم الكاذبون.',NULL,'519',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2807,'<span data-type="title" id=toc-2831>١٤ </span>- ولولا تفضل الله عليكم ببيان الأحكام، ورحمته لكم فى الدنيا بعدم التعجيل بالعقوبة وفى الآخرة بالمغفرة لنزل بكم عذاب عظيم بسبب الخوض فى هذه التهمة.',NULL,'519',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2808,'<span data-type="title" id=toc-2832>١٥ </span>- فقد تناقلتم الخبر بألسنتكم وأشعتموه بينكم، ولم يكن عندكم علم بصحته، وتظنون أن هذا العمل هين، لا يعاقب الله عليه، أو يكون عقابه يسيراً مع أنه خطير يعاقب الله عليه أشد العقاب.',NULL,'519',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2809,'<span data-type="title" id=toc-2833>١٦ </span>- وكان ينبغى عند سماع هذا القول الباطل أن تنصحوا بعدم الخوض فيه، لأنه غير لائق بكم، وأن تتعجبوا من اختراع هذا النوع القبيح الخطير من الكذب.',NULL,'519',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2810,'<span data-type="title" id=toc-2834>١٧ </span>- وأن الله ينهاكم أن تعودوا لمثل هذه المعصية البتَّة إن كنتم مؤمنين حقاً، لأن وصف الإيمان يتنافى معها.',NULL,'519',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2811,'<span data-type="title" id=toc-2835>١٨ </span>- وينزل الله لكم الآيات الدالة على الأحكام واضحة جلية. والله واسع العلم لا يغيب عنه شئ من أعمالكم، وهو الحكيم فى كل ما يشرع ويخلق، فكل شرعه وخلقه على مقتضى الحكمة.',NULL,'519',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2812,'<span data-type="title" id=toc-2836>١٩ </span>- إن الذين يحبون أن يُفْشوا ذكر القبائح، فيفشوا معه القبائح نفسها بين المؤمنين، لهم عذاب مؤلم فى الدنيا بالعقوبة المقررة، وفى الآخرة بالنار إن لم يتوبوا. والله عليم بجميع أحوالكم الظاهرة والباطنة، وأنتم لا تعلمون ما يعلمه.',NULL,'520',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2813,'<span data-type="title" id=toc-2837>٢٠ </span>- ولولا فضل الله عليكم ورحمته بكم، وأنه شديد الرأفة واسع الرحمة، لَما بيّن لكم الأحكام، ولعجَّل عقوبتكم فى الدنيا بالمعصية.',NULL,'520',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2814,'<span data-type="title" id=toc-2838>٢١ </span>- يا أيها الذين آمنوا حصِّنوا أنفسكم بالإيمان، ولا تسيروا وراء الشيطان الذى يجركم إلى إشاعة الفاحشة والمعاصى بينكم. ومن يتبع الشيطان فقد عصى، لأنه يأمر بكبائر الذنوب وقبائح المعاصى، ولولا فضل الله عليكم ورحمته بكم ببيان الأحكام وقبول توبة العصاة ما طهر أحد منكم من دنس العصيان. ولكن الله يطهر من يتجه إلى ذلك بتوفيقه للبعد عن المعصية، أو مغفرتها له بالتوبة، والله سميع لكل قول، عليم بكل شئ، ومجازيكم عليه.',NULL,'520',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2815,'<span data-type="title" id=toc-2839>٢٢ </span>- ولا يَحّلِف الصالحون وذوو اليسار منكم على أن يمنعوا إحسانهم ممن يستحقونه من الأقارب والمساكين والمهاجرين فى سبيل الله وغيرهم لسبب من الأسباب الشخصية، كإساءتهم إليهم، ولكن ينبغى أن يسامحوهم ويعرضوا عن مجازاتهم، وإذا كنتم تحبون أن يعفو الله عن سيئاتكم فافعلوا مع المسئ إليكم مثل ما تحبون أن يفعل بكم ربكم، وتأدبوا بأدبه فهو واسع المغفرة والرحمة.',NULL,'520',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2816,'<span data-type="title" id=toc-2840>٢٣ </span>- إن الذين يتهمون بالزنا المؤمنات العفيفات الطاهرات، اللاتى لا يظن فيهن ذلك، بل هن لفرط انصرافهن إلى الله غافلات عما يُقال عنهن، يُبعدهم الله عن رحمته فى الدنيا والآخرة، ولهم عذاب عظيم إن لم يتوبوا.',NULL,'520',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2817,'<span data-type="title" id=toc-2841>٢٤ </span>- ذلك العذاب يكون يوم القيامة حيث لا سبيل للإنكار، بل يثبت عليهم ما ارتكبوا إذ تشهد عليهم ألسنتهم وأيديهم وأرجلهم بجميع ما ارتكبوا من آثام، وذلك بظهور آثار مما عملوه عليها، أو بأن يُنطقها الله الذى أنطق كل شئ.',NULL,'521',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2818,'<span data-type="title" id=toc-2842>٢٥ </span>- فى ذلك اليوم يعاقبهم الله العقاب المقرر لهم كاملاً غير منقوص، وهنا يعلمون علم اليقين أُلوهية الله وأحكام شريعته، وصدق وعده ووعيده، لأن كل ذلك واضح دون خفاء.',NULL,'521',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2819,'<span data-type="title" id=toc-2843>٢٦ </span>- الخبيثات من النساء يَكُنَّ للخبيثين من الرجال، والخبيثون من الرجال يكونون للخبيثات من النساء، وكذلك الطيبات من النساء يكن للطيبين من الرجال، والطيبون من الرجال يكونون للطيبات من النساء، فكيف يُتصور السوء فى الطيبة المصونة زوج الأمين، والرسول الكريم - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - وهؤلاء الطيبون مبرَّأون من التهم التى يصفهم بها الخبيثون، ولهم مغفرة من الله على مما لا يخلو منه البشر من صغار الذنوب، وإكرام عظيم بنعيم الجنة، وطيباتها.',NULL,'521',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2820,'<span data-type="title" id=toc-2844>٢٧ </span>- يا أيها الذين آمنوا لا تدخلوا بيوتاً ليست لكم إلا بعد أن تطلبوا الإذن من ساكنيها ويسمح لكم بالدخول، وبعد أن تلقوا تحية السلام على ساكنيها. ذلك الاستئذان والسلام خير لكم من الدخول بدونهما، وشرعه الله لكم لتتعظوا وتعملوا به.',NULL,'521',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2821,'<span data-type="title" id=toc-2845>٢٨ </span>- فإن لم تجدوا فى هذه البيوت أحداً يأذن لكم، فلا تدخلوا حتى يجئ من يسمح لكم به. وإن لم يُسمح لكم وطُلب منكم الرجوع فارجعوا، ولا تلحوا فى طلب السماح بالدخول، فإن الرجوع أكرم بكم وأطهر لنفوسكم، والله مطلع على كل أحوالكم ومجازيكم عليها فلا تخالفوا إرشاداته.',NULL,'521',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2822,'<span data-type="title" id=toc-2846>٢٩ </span>- وإذا أردتم دخول بيوت عامة غير مسكونة بقوم مخصوصين، ولكم فيها حاجة كالحوانيت والفنادق ودور العبادة فلا حرج عليكم إن دخلتم بدون استئذان، والله عالم أتم العلم بجميع أعمالكم الظاهرة والباطنة فاتقوا مخالفته.',NULL,'521',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2823,'<span data-type="title" id=toc-2847>٣٠ </span>- قل - يا أيها النبى - للمؤمنين - محذراً لهم مما يوصل إلى الزنا ويعرض للتهم -: إنهم مأمورون ألا ينظروا إلى ما يحرم النظر إليه من عورات النساء ومواطن الزينة منهن، وأن يصونوا فروجهم بسترها وبعدم الاتصال غير المشروع، ذلك الأدب أكرم بهم وأطهر لهم وأبعد عن الوقوع فى المعصية والتهم. إن الله عالم أتم العلم بجميع ما يعملون ومجازيهم على ذلك.',NULL,'522',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2824,'<span data-type="title" id=toc-2848>٣١ </span>- قل أيضاً - يا أيها النبى - للمؤمنات: إنهن مأمورات بكف نظرهن عما يحرم النظر إليه، وأن يَصُنَّ فروجهن بالستر وعدم الاتصال غير المشروع، وألا يُظهرن للرجال ما يغريهم من المحاسن الخلقية والزينة كالصدر والعضد والقلادة، إلا ما يظهر من غير إظهار كالوجه واليد، واطلب منهن - يا أيها النبى - أن يسترن المواضع التى تبدو من فتحات الملابس، كالعنق والصدر، وذلك بأن يسترن عليها أغطية رؤوسهن، وألا يسمحن بظهور محاسنهن، إلا لأزواجهن والأقارب الذين يحرم عليهم التزوج منهن تحريماً مؤبداً كآبائهن أو آباء أزواجهن، أو أبنائهن أو أبناء أزواجهن من غيرهن، أو إخوانهن أو أبناء إخوانهن، ومثل هؤلاء صواحبهن، وسواء منهن الحرائر والمملوكات، والرجال الذين يعيشون معهن، ولا يوجد عندهم الحاجة والميل للنساء كالطاعنين فى السن، وكذلك الأطفال الذين لم يبلْغوا حد الشهوة، واطلب منهن أيضاً ألا يفعلن شيئاً يلفت أنظار الرجال إلى ما خفى من الزينة، وذلك كالضرب فى الأرض بأرجلهن، ليسمع صوت خلاخيلهن المستترة بالثياب، وتوبوا إلى الله جميعاً - أيها المؤمنون - فيما خالفتم فيه أمر الله، والتزموا آداب الدين لتسعدوا فى دنياكم وأخراكم.',NULL,'522',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2825,'<span data-type="title" id=toc-2849>٣٢ </span>- وأعينوا على الابتعاد عن الزنا وما يوصل إليه بتزويج من لم يتزوج منْ رجالكم ونسائكم، ومَنْ كان صالحاً من مماليككم كذلك، ولا تكن رقة الحال مانعة من الزواج فإن الله سيهيئ وسائل العيش الكريم لمن أراد إعفاف نفسه، وفضل الله واسع لا يثقله إغناء الناس، هو عالم أتم العلم بالنيات وبكل ما يجرى فى الكون.',NULL,'523',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2826,'<span data-type="title" id=toc-2850>٣٣ </span>- والذين لا يجدون القدرة على مؤونات الزواج، فعليهم أن يسلكوا وسيلة أخرى كالصوم والرياضة. والأعمال العقلية، يعفون بها أنفسهم، حتى يهيئ الله لهم من فضله ما يستطيعون به الزواج، والأرقاء الذين يطلبون منكم تعاقداً على دفع عوض مقابل عتقهم، عليكم أن تجيبوهم إلى ما طلبوا، إنْ علمتم أنهم سيصدقون فى الوفاء ويستطيعون الأداء، وعليكم أن تساعدوهم على الوفاء بما تعاقدوا عليه، وذلك مثلا بتخفيض ما اتفقتم عليه أو إعطائهم بعض المال الذى أنعم الله به عليكم بالزكاة أو الصدقة. ويحرم عليكم أن تجعلوا جواريكم وسيلة للكسب الدنيوى الرخيص باحتراف البغاء وتكرهوهن عليه. كيف تُكرِهُوهُنَّ وهن يردن العفاف؟ ومن يكرههن عليه فإن الله يغفر لمن يكرهونهن بالتوبة عن الإكراه. لأن الله واسع المغفرة والرحمة.',NULL,'523',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2827,'<span data-type="title" id=toc-2851>٣٤ </span>- ولقد أنزلنا إليكم فى هذه السورة وغيرها آيات واضحة مبينة للأحكام، وأنزلنا إليكم أمثلة من أحوال السابقين. وإرشادات ومواعظ يفيد منها الخائفون من الله.',NULL,'523',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2828,'<span data-type="title" id=toc-2852>٣٥ </span>- الله مصدر النور فى السموات والأرض، فهو منورهما بكل نور حسى نراه ونسير فيه، وبكل نور معنوى، كنور الحق والعدل، والعلم والفضيلة، والهدى والإيمان، وبالشواهد والآثار التى أودعها مخلوقاته، وبكل ما يدل على وجود الله ويدعو إلى الإيمان به سبحانه، وَمَثلُ نوره العظيم وأدلته الباهرة فى الوضوح، كمثل نور مصباح شديد التوهج، وضع فى فجوة من حائط تساعد على تجميع نوره ووفرة إضاءته، وقد وضع المصباح فى قارورة صافية لامعة لمعان كوكب مشرق، يتلألأ كالدر ويستمد المصباح وقوده من زيت شجرة كثيرة البركات، طيبة التربة والموقع، هى شجرة الزيتون المغروسة فى مكان معتدل متوسط، فلا هى شرقية فتحرم حرارة الشمس آخر النهار، ولا هى غربية فتحرمها أول النهار، بل هى على قمة الجبل، أو فى فضاء الأرض تفيد من الشمس فى جميع أجزاء النهار، يكاد زيت هذه الشجرة لشدة صفائه يضئ، ولو لم تمسسه نار المصباح، فهذه العوامل كلها تزيد المصباح إضاءة فوق إضاءة، ونوراً على نور.
INSERT INTO [b836] VALUES (2829,'<span data-type="title" id=toc-2853>٣٦ </span>- إن هناك قوماً يُسبحون الله ويعبدونه فى المساجد التى أمر الله أن تبنى وتعظم وتُعمر بذكر الله، وهم يترددون عليها صباحا ومساء.',NULL,'524',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2830,'<span data-type="title" id=toc-2854>٣٧ </span>- لا تشغلهم الدنيا بما فيها من بيع وشراء عن تذكر الله ومراقبته، فهم يقيمون الصلاة ويؤدون الزكاة خائفين من يوم القيامة الذى لا تستقر فيه القلوب من القلق والهم، وترقب المصير فيه وتلتفت فيه الأنظار فى حيرة ودهشة من غرابة المنظر وشدة الهول.',NULL,'524',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2831,'<span data-type="title" id=toc-2855>٣٨ </span>- وستكون عاقبة عملهم مكافأة الله لهم أحسن مكافأة على أعمالهم الطيبة، وأن يتفضل عليهم بأكثر مما يستحقون، فهو سبحانه واسع الفضل يعطى من يشاء من عباده الصالحين عطاء كبيرا، لا يحاسبه عليه أحد ولا يستطيع العادُّون إحصاءه.',NULL,'524',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2832,'<span data-type="title" id=toc-2856>٣٩ </span>- والذين جحدوا وأنكروا يحسبون أنهم يحسنون صنعاً، وأن أعمالهم الحسنة ستفيدهم يوم القيامة، ولكنهم مخطئون فى ظنهم هذا، فمثل أعمالهم فى بطلانها وعدم جدواها كمثل اللمعان الذى يحدث من سقوط أشعة الشمس وقت الظهيرة على أرض مستوية فى بيداء، فيظنه العطشان ماء، حتى إذا جاءه لم يجده شيئاً نافعاً كما كان يظنه، كذلك أعمال الكفار يوم الجزاء ستكون هباء منثوراً، وسيجد الكافر عقاب الله ينتظره واقعاً تاماً لا نقص فيه، إن حساب الله آت لا ريب فيه، وهو سبحانه سريع فى حسابه لا يبطئ ولا يخطئ.',NULL,'525',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2833,'<span data-type="title" id=toc-2857>٤٠ </span>- وهذا مثل آخر لأعمال الكفار، فمثلها كمثل ظلمات البحر الواسع العميق، الذى تتلاطم أمواجه عند هياجه، ويعلو بعضها فوق بعض، ويغطيها سحاب كثيف قاتم يحجب النور عنها، فهذه ظلمات متراكمة، لا يستطيع راكب البحر معها أن يرى يده ولو أدناها إلى بصره، فوقف حائراً مبهوتاً، وكيف يرى شيئاً ويخلص من هذه الحيرة بدون نور يهديه فى مسيره ويقيه الارتطام والهلاك؟ وكذلك الكافرون لا يفيدون من أعمالهم، ولا يخرجون من عمايتهم وضلالهم، ولا ينجون بأنفسهم إلا بنور الإيمان، ومن لم يوفقه الله لنور الإيمان، فليس له نور يهديه إلى الخير ويدله على الطريق المستقيم، فيكون من الهالكين.',NULL,'525',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2834,'<span data-type="title" id=toc-2858>٤١ </span>- ألم تعلم - يا أيها النبى - علماً يقينياً أن الله يخضع له كل من يسكن السموات والأرض، ويخضع له الطير كذلك، وهى باسطة أجنحتها. فهذه المخلوقات كلها خاضعة لأمر الله وتدبيره تنزهه عن الشريك وعن كل ما لا يليق، وكل منها قد علم بإلهام الله ما وجب عليه من خضوع وتنزيه وأداء لوظيفته فى الحياة، والله من ورائهم عالم أتم العلم بصلاة كل مصل وتسبيح كل مسبح، وجميع ما يفعله العباد، فكيف لا يؤمن به الكافرون؟',NULL,'525',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2835,'<span data-type="title" id=toc-2859>٤٢ </span>- والله - وحده - هو مالك السموات والأرض وما فيهن، وصاحب السلطان عليها وكلهم راجع إليه يوم القيامة للحساب والجزاء.',NULL,'525',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2836,'<span data-type="title" id=toc-2860>٤٣ </span>- ألم تر - أيها النبى - أن الله يسوق بالريح سحاباً، ثم يضم بعضه إلى بعض ويجعله متراكماً، فترى المطر يخرج من خلال السحاب، والله ينزل من مجموعات السحب المتكاثفة التى تشبه الجبال فى عظمتها برداً، كالحصى ينزل على قوم فينفعهم أو يضرهم تبعاً لقوانينه وإرادته ولا ينزل على آخرين كما يريد الله فهو سبحانه الفاعل المختار، ويكاد ضوء البرق الحادث من اصطكاك السحب يذهب بالأبصار لشدته، وهذه الظواهر دلائل قدرة الله الموجبة للإيمان به.',NULL,'526',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2837,'<span data-type="title" id=toc-2861>٤٤ </span>- يغير الله أحوال الليل والنهار بالطول والقصر، والبدء والانتهاء بدوران الفلك، إن فى ذلك كله لعبرة لذوى العقول السليمة المتبصرة، يؤمنون عن طريقها بالله.',NULL,'526',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2838,'<span data-type="title" id=toc-2862>٤٥ </span>- الله خالق كل شئ، وأبدع الأشياء بإرادته، وخلق كل حى يدب من أصل مشترك هو الماء، لذلك لا يخلو الحى منه، ثم خالف بينها فى الأنواع والاستعدادات ووجوه الاختلاف الأخرى، فمن الدواب نوع يزحف على بطنه كالأسماك والزواحف، ومنها نوع يمشى على رجليه كالإنسان والطير، ومنها نوع يمشى على أربع كالبهائم، يخلق الله ما يشاء من خلقه على أية كيفية تكون للدلالة على قدرته وعلمه، فهو المريد المختار، وهو القادر على كل شئ.',NULL,'526',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2839,'<span data-type="title" id=toc-2863>٤٦ </span>- لقد أنزلنا بالوحى آيات واضحات تبين الأحكام والعظات، وتضرب الأمثال، والله يوفق إلى الخير من يشاء من عباده الذين استعدوا للنظر فيها والإفادة منها.',NULL,'527',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2840,'<span data-type="title" id=toc-2864>٤٧ </span>- والمنافقون يقولون بألسنتهم: آمنا بالله وبالرسول وأطعنا أوامرهما. وعند اختبارهم يعرض فريق منهم عن مشاركة المسلمين فى أعمال الخير كالجهاد وغيره، بعد قولهم هذا، وهؤلاء ليسوا بمؤمنين مخلصين، ولا جديرين بإطلاق اسم المؤمنين عليهم.',NULL,'527',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2841,'<span data-type="title" id=toc-2865>٤٨ </span>- ومن أحوالهم أنهم إذا طلبوا إلى التحاكم أمام الرسول بمقتضى ما أنزل الله، ظهر نفاق بعضهم فرفضوا التحاكم إذا عرفوا أن الحق فى جانب خصومهم.',NULL,'527',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2842,'<span data-type="title" id=toc-2866>٤٩ </span>- أما إذا عرفوا أن الحق فى جانبهم، فهم يأتون إلى الرسول مسرعين ليحكم بينهم وبين خصومهم.',NULL,'527',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2843,'<span data-type="title" id=toc-2867>٥٠ </span>- ولماذا يقفون هذا الموقف من التحاكم أمام الرسول؟ ألأنَّ نفوسهم مريضة بالعمى فلا تخضع لحكمك الحق، أم لأنهم شكوا فى عدالة محمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - فى الحكم؟ لا شئ من ذلك أصلاً، ولكنهم هم الظالمون لأنفسهم ولغيرهم بسبب كفرهم ونفاقهم وعدولهم عن الحق.',NULL,'527',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2844,'<span data-type="title" id=toc-2868>٥١ </span>- إنما كان القول الحق للمؤمنين الصادقين إذا دعوا إلى التحاكم بمقتضى ما جاء عن الله ورسوله أن يقولوا قابلين مذعنين: سمعنا دعوتك يا محمد ورضينا حكمك، وهؤلاء يكونون أهل فلاح فى دنياهم وأُخراهم.',NULL,'527',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2845,'<span data-type="title" id=toc-2869>٥٢ </span>- ومن يطع الله، ويرض بما يأمر به الرسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ -، ويخش ذات الله العلية، ويستحضر جلاله ويتق غضبه، فأولئك هم الفائزون برضا الله ومحبته، ونعيم الجنة، والفائزون بالخير المطلق.',NULL,'527',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2846,'<span data-type="title" id=toc-2870>٥٣ </span>- وأقسم المنافقون بالله أقصى ما يكون من إيمان مغلظة، إنك يا محمد إن أمرتهم بالخروج معك للغزو أطاعوا، قل لهم: لا تحلفوا فالأمور المطلوبة منكم معروفة لكم لا ينكرها أحد منكم، ولا ينفى العلم بها إيمان تكذبون فيها، وإن الله لمطلع تمام الاطلاع على كل ما يقع منكم ومجازيكم عليه.',NULL,'527',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2847,'<span data-type="title" id=toc-2871>٥٤ </span>- قل لهم: أطيعوا الله وأطيعوا الرسول طاعة صادقة تدل عليها أعمالكم، فإن أعرض المنافقون ولم يمتثلوا، فإنما على محمد ما حمله الله من أمر التبليغ وليس مكلفاً بهدايتهم، وعليكم ما حملكم الله من التكليف والطاعة، وستعاقبون إذا استمررتم على العصيان، وإن تطيعوا الرسول تهتدوا إلى الخير، وما عليه سوى التبليغ الواضح - أطعتم أم عصيتم - وقد بلغ.',NULL,'528',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2848,'<span data-type="title" id=toc-2872>٥٥ </span>- وعد الله الذين صدَّقوا بالحق وأذعنوا له منكم، وعملوا الأعمال الصالحة وعداً مؤكداً أن يجعلهم خلفاء لمن سبقوهم وارثين لهم فى الحكم والولاية فى الأرض، كما كان الشأن فيمن سبقوهم. وأن يمكن لهم الإسلام الذى ارتضاه ديناً لهم، فتكون لهم المهابة والسلطان، وأن يبدل حالهم من خوف إلى أمن بحيث يعبدوننى مطمئنين، لا يشركون معى أحداً فى العبادة. ومن اختاروا الكفر بعد هذا الوعد الصادق، أو ارتدوا عن الإسلام فأولئك هم الخارجون المتمردون الجاحدون.',NULL,'528',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2849,'<span data-type="title" id=toc-2873>٥٦ </span>- وأقيموا الصلاة كاملة الأركان فى خشوع وخضوع بحيث تكون مانعة من الفحشاء والمنكر، وأعطوا الزكاة لمستحقيها. وأطيعوا الرسول فى سائر ما يأمركم به ليكون لكم رجاء فى رحمة الله ورضوانه.',NULL,'528',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2850,'<span data-type="title" id=toc-2874>٥٧ </span>- لا تظن - أيها النبى - أن الكافرين سيعجزون الله عن أخذهم بذنوبهم، أو تمكين أهل الحق من رقابهم فى أى مكان من الأرض، بل إنه القادر، فمصيرهم يوم القيامة هو النار وبئس المصير مصيرهم.',NULL,'528',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2851,'<span data-type="title" id=toc-2875>٥٨ </span>- يا أيها الذين آمنوا، يجب أن تأمروا عبيدكم وصبيانكم الذين لم يصلوا إلى حد البلوغ ألا يدخلوا عليكم إلا بعد الاستئذان فى ثلاثة أوقات، وهى: قبل صلاة الفجر، وحين تتخففون من ثيابكم وقت القيلولة، ومن بعد صلاة العشاء عند الاستعداد للنوم. فهذه الأوقات يتغير فيها نظام اللبس باستبدال ثياب النوم بثياب اليقظة، ويبدو من عورات الجسم ما لا ينبغى رؤيته، ولا حرج عليكم ولا عليهم فى الدخول بغير استئذان فى غير هذه الأوقات، لأن العادة جرت بأن يتردد فيها بعضكم على بعض لقضاء المصالح. وبمثل هذا التوضيح يوضح الله لكم آيات القرآن لبيان الأحكام، والله سبحانه واسع العلم عظيم الحكمة، يعلم ما يصلح لعباده ويشرع لهم ما يناسبهم ويحاسبهم عليه.',NULL,'528',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2852,'<span data-type="title" id=toc-2876>٥٩ </span>- وإذا وصل صبيانكم حد البلوغ وجب عليهم أن يستأذنوا للدخول فى كل بيت، وفى جميع الأوقات، كما وجب ذلك على الذين بلغوا من قبلهم، وبمثل هذا التوضيح يوضح الله لكم آياته التى أنزلها، والله سبحانه واسع العلم، عظيم الحكمة، يعلم ما يصلح لعباده ويشرِّع لهم ما يناسبهم ويحاسبهم عليه.',NULL,'529',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2853,'<span data-type="title" id=toc-2877>٦٠ </span>- والنساء الطاعنات فى السن اللاتى لا يطمعن فى الزواج، لا مؤاخذة عليهن إذا تخففن من بعض الملابس، بحيث تكون غير مظهرات زينة أمر الله بإخفائها من أجسامهن، ولكن استعفافهن بالاستتار الكامل خير لهن من التخفف، والله سميع لقولهن عليم بفعلهن وقصدهن ومجازيهن على ذلك.',NULL,'529',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2854,'<span data-type="title" id=toc-2878>٦١ </span>- ليس على أصحاب الأعذار كالأعمى والأعرج والمريض حرج، بل ولا عليكم أيها الأصحاء حرج فى أن تأكلوا من بيوت أولادكم فهى بيوتكم، ولا أن تأكلوا من بيوت آبائكم أو أمهاتكم أو إخوانكم أو أخواتكم أو بيوت أعمامكم أو عماتكم أو أخوالكم أو خالاتكم، أو البيوت التى وكل إليكم التصرف فيها، أو بيوت أصدقائكم المخالطين إذا لم يكن فيها حرمات، وذلك كله إذا عُلِم سماح رب البيت بإذن أو قرينة، وليس عليكم جناح فى أن تأكلوا مجتمعين أو منفردين، فإذا دخلتم بيوتاً فحيوا بالسلام أهلها الذين هم قطعة منكم بسبب اتحاد الدين أو القرابة فهم كأنفسكم، وهذه التحية تحية مشروعة مباركة بالثواب وفيها تطييب للنفوس وعلى هذا النحو يوضح الله لكم الآيات لتعقلوا ما فيها من العظات والأحكام وتفهموها وتعملوا بها.',NULL,'529',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2855,'<span data-type="title" id=toc-2879>٦٢ </span>- إن المؤمنين الصادقين هم الذين آمنوا بالله ورسوله، ولم يتركوا الرسول وحده فى أمر مهم يتطلب اجتماعهم كالجهاد، إلا بعد أن يستأذنوه فى الانصراف ويسمح لهم به، إن الذين يقدرونك - أيها النبى - حق قدرك، ويدركون خطر الاجتماع فلا ينصرفون إلا بعد موافقتك، وهم الصادقون فى إيمانهم بالله ورسوله، فإذا استأذنك هؤلاء لقضاء بعض مصالحهم فأذن بالانصراف لمن تشاء منهم، إذا رأيت من الدلائل أنهم فى حاجة ماسة إلى الانصراف، ولا يحتم الاجتماع وجودهم، ومع ذلك اطلب المغفرة لهم من الله على انصرافهم الذى ما كان يليق أبداً، إن الله واسع المغفرة والرحمة.',NULL,'530',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2856,'<span data-type="title" id=toc-2880>٦٣ </span>- احرصوا على احترام دعوة الرسول لكم إلى الاجتماع للأمور الهامة، واستجيبوا لها، ولا تجعلوها كدعوة بعضكم لبعض فى جواز التهاون فيها، والانصراف عنها، ولا تنصرفوا إلا بعد الاستئذان والموافقة، وفى أضيق الحدود وأشد الضرورات. فالله سبحانه يعلم من ينصرفون بدون إذن مختفين بين الجموع حتى لا يراهم الرسول، فليحذر المخالفون عن أمر الله أن يعاقبهم سبحانه على عصيانهم بمحنة شديدة فى الدنيا كالقحط والزلزال، أو بعذاب شديد الإيلام قد أعد لهم فى الآخرة وهو النار.',NULL,'530',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2857,'<span data-type="title" id=toc-2881>٦٤ </span>- تنبهوا - أيها الناس - إلى أن الله - وحده - هو مالك السموات والأرض وما فيها، يعلم ما أنتم عليه من الكفر والإسلام والعصيان والطاعة، فلا تخالفوا عن أمره، وسيخبر الناس عند رجوعهم إليه يوم القيامة بكل ما عملوا فى الدنيا وسيجازيهم عليه، لأنه محيط بكل شئ علماً.',NULL,'530',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2858,'<span data-type="title" id=toc-2883>١ </span>- تعالى أمر الله وتزايد خيره، هو الذى نزل القرآن فارقاً بين الحق والباطل على عبده محمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - ليكون نذيراً به مبلغاً إياه إلى العالمين.',NULL,'531',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2859,'<span data-type="title" id=toc-2884>٢ </span>- هو سبحانه الذى يملك - وحده - السموات والأرض، والمنزه عن اتخاذ الولد، ولم يكن له أى شريك فى ملكه، وقد خلق كل شئ وقدَّره تقديراً دقيقاً بنواميس تكفل له أداء مهمته بنظام.',NULL,'531',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2860,'<span data-type="title" id=toc-2885>٣ </span>- ومع ذلك ترك الكافرون عبادته، واتخذوا آلهة يعبدونها من دون الله من أصنام وكواكب وأشخاص وهم لا يستطيعون أن يخلقوا شيئاً ما، وهم مخلوقون لله، ولا يملكون دفع الضر عن أنفسهم ولا جلب خير لها، ولا يملكون إماتة أحد ولا إحياءه، ولا بعث الأموات من قبورهم، وكل من لا يملك شيئاً من ذلك لا يستحق أن يعبد، وما أجهل من يعبده، والمستحق للعبادة وحده هو مالك كل هذا.',NULL,'531',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2861,'<span data-type="title" id=toc-2886>٤ </span>- وطعن الكفار فى القرآن وقالوا: إنه كذب اخترعه محمد من عند نفسه ونسبه إلى الله، وساعده فى اختراعه جماعة آخرون من أهل الكتاب، فارتكب الكفار بقولهم هذا ظلماً فى الحكم واعتداء على الحق، وجاءوا بزور لا دليل عليه، لأن من أشاروا إليهم من أهل الكتاب لسانهم أعجمى، والقرآن لسان عربى مبين.',NULL,'532',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2862,'<span data-type="title" id=toc-2887>٥ </span>- وقالوا عن القرآن أيضاً: إنه أكاذيب السابقين سطَّروها فى كتبهم، ثم طلب منهم أن تُكتب له وتُقرأ عليه على الدوام صباحاً ومساء حتى يحفظها ويقولها.',NULL,'532',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2863,'<span data-type="title" id=toc-2888>٦ </span>- قل لهم - أيها النبى -: إن القرآن أنزله الله الذى يعلم الأسرار الخفية فى السموات والأرض، وقد أودعها فى القرآن المعجز دليلا على أنه وحيه سبحانه، إن الله واسع المغفرة والرحمة، يتجاوز عن العاصين إذا تابوا ولا يعجل بعقوبتهم.',NULL,'532',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2864,'<span data-type="title" id=toc-2889>٧ </span>- وسَخِروا من محمد فقالوا: أى شئ يمتاز به هذا الذى يزعم أنه رسول حتى إنه يأكل الطعام كما نأكل، ويتردد فى الأسواق لكسب عيشه كما يفعل سائر البشر؟ لو كان رسولا لكفاه الله ذلك، ولسأل ربه أن ينزل له ملكاً من السماء يساعده على الإنذار والتبليغ ويصدقه فى دعواه فنؤمن به.',NULL,'532',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2865,'<span data-type="title" id=toc-2890>٨ </span>- وهلا سأل أن يكفيه مؤونة التردد على الأسواق فيلقى إليه كنزاً من السماء ينفق منه، أو يجعل له حديقة يقتات من ثمارها؟ وقال كبار الكافرين الذين ظلموا أنفسهم بالكفر صادين الناس عن الإيمان بمحمد، ومحاولين تشكيك المؤمنين: ما تتبعون إلا رجلاً مسحوراً عقلُه، فهو يهذى بما لا حقيقة له.',NULL,'532',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2866,'<span data-type="title" id=toc-2891>٩ </span>- انظر - أيها النبى - كيف ضربوا لك الأمثال، فمثلوك مرة بمسحور، وأخرى بمجنون، وثالثة بكذاب، ورابعة بتلقى القرآن عن أعاجم، إنهم بذلك قد ضلوا طريق الحق والمحاجة الصحيحة فلا يجدون إليهما سبيلا.',NULL,'532',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2867,'<span data-type="title" id=toc-2892>١٠ </span>- تعالى الله وتزايد خيره، هو الذى إن شاء جعل لك فى الدنيا أحسن مما اقترحوا، فيجعل لك فيها مثل ما وعدك فى الآخرة من جنات كثيرة تجرى الأنهار فى جنباتها وخلال أشجارها، ومن قصور مشيدة.',NULL,'533',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2868,'<span data-type="title" id=toc-2893>١١ </span>- والحقيقة أنهم جاحدون بكل آية، لأنهم كذَّبوا بالبعث ويوم القيامة، فهم لهذا يتعللون بهذه المطالب ليصرفوا الناس إلى باطلهم، وقد أعددنا لمن كذب بيوم القيامة ناراً مستعرة شديدة الالتهاب.',NULL,'533',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2869,'<span data-type="title" id=toc-2894>١٢ </span>- إذا رأوها ورأتهم من بعيد سمعوا لها صوتاً متغيظاً متحفزاً لإهلاكهم، وفيه مثل الزفرات التى تخرج من صدر متغيظ علامة على ما هى عليه من شدة.',NULL,'533',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2870,'<span data-type="title" id=toc-2895>١٣ </span>- وإذا ألقوا فى مكان ضيق منها يتناسب مع جرمهم وهم مقرونة أيديهم إلى أعناقهم بالأغلال، نادوا هناك طالبين تعجيل هلاكهم ليستريحوا من هول العذاب.',NULL,'533',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2871,'<span data-type="title" id=toc-2896>١٤ </span>- فيقال لهم توبيخاً وسخرية: لا تطلبوا هلاكاً واحداً بل اطلبوه مراراً، فلن تجدوا خلاصاً مما أنتم فيه، وإن أنواع عذابهم كثيرة.',NULL,'533',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2872,'<span data-type="title" id=toc-2897>١٥ </span>- قل - يا أيها النبى - للكافرين: أهذا المصير الذى أوعد به الكافرون خير، أم الجنة الدائم نعيمها والتى وعد المؤمنون الأتقياء بأن تكون لهم ثواباً ومصيراً يصيرون إليه بعد البعث والحساب؟ .',NULL,'533',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2873,'<span data-type="title" id=toc-2898>١٦ </span>- لهم فيها ما يرغبون وينعمون به نعيماً دون انقطاع، وكان هذا النعيم وعداً من الله لهم، سألوا ربهم تحقيقه فأجابهم إلى ما سألوه، لأن وعده لا يتخلف.',NULL,'533',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2874,'<span data-type="title" id=toc-2899>١٧ </span>- واذكر - للعظة - يوم يحشر الله المشركين للحساب فى يوم القيامة مع مَن عبدوهم فى الدنيا من دون الله، كعيسى وعزير والملائكة، فيسأل الله المعبودين: أأنتم الذين أضللتم عبادى فأمرتموهم بأن يعبدوكم، أم هم الذين ضلوا السبيل باختيارهم فعبدوكم؟ .',NULL,'533',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2875,'<span data-type="title" id=toc-2900>١٨ </span>- فيكون جوابهم: تنزَّهت وتقَّدست، ما كان يحق لنا أبداً أن نطلب من دونك ولياً ينصرنا ويتولى أمرنا، فكيف مع هذا ندعوا أحداً أن يعبدنا دونك؟ ولكن السبب فى كفرهم هو إنعامك عليهم بأن متّعتهم طويلا بالدنيا هم وآباؤهم، فأطغاهم ذلك ونسوا شكرك والتوجه إليك - وحدك - بالعبادة، وكانوا بذلك الطغيان والكفر قوماً مستحقين للهلاك.',NULL,'534',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2876,'<span data-type="title" id=toc-2901>١٩ </span>- فيقال للعابدين المشركين: لقد كذَّبكم مَن عبدتموه فيما زعمتم من إضلالهم إياكم. فأنتم اليوم إلى العذاب صائرون، لا تملكون حيلة لصرفه عنكم، ولا تجدون نصراً من أحد يخلصكم منه، وليعلم العباد جميعاً أن من يظلم نفسه بالكفر والطغيان كما فعل أولئك فإننا نعذَّبه عذاباً شديداً.',NULL,'534',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2877,'<span data-type="title" id=toc-2902>٢٠ </span>- وإذا كان المشركون يعيبونك - أيها النبى - بأكلك الطعام ومشيك فى الأسواق للعمل والكسب فتلك سنة الله فى المرسلين من قبلك، ما أرسلنا أحداً منهم إلا كان يأكل الطعام ويتردد فى الأسواق. وجعلنا بعضكم - أيها الناس - ابتلاء لبعض، والمفسدون يحاولون سد الطريق إلى الهداية والحق بشتى الأساليب، فهل تصبرون على حقكم - أيها المؤمنون - وتتمسكون بدينكم حتى يأتى أمر الله بالنصر؟ اصبروا فالله مطلع على كل شئ ويجازى كلا بما عمل.',NULL,'534',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2878,'<span data-type="title" id=toc-2903>٢١ </span>- وقال الذين ينكرون البعث ولا يتوقعون الجزاء على أعمالهم: لماذا لا تنزل علينا الملائكة بتأييدك، أو يتراءى لنا الله فيخبرنا بأنه أرسلك؟ . لقد تمكن الكبر من نفوسهم وجاوزوا الحد فى الظلم والطغيان.',NULL,'534',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2879,'<span data-type="title" id=toc-2904>٢٢ </span>- يوم القيامة يرون الملائكة كما تمنوا، وسيكون ذلك مصدر تنفير لهم لا بشارة. يستعيذون منهم كما كانوا يستعيذون مما يفزعهم فى الدنيا.',NULL,'534',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2880,'<span data-type="title" id=toc-2905>٢٣ </span>- ويوم القيامة نأتى إلى ما عملوه من مظاهر البر والإحسان فى الدنيا فنحبطه ونحرمهم ثوابه، لعدم إيمانهم الذى به تعتبر الأعمال.',NULL,'535',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2881,'<span data-type="title" id=toc-2906>٢٤ </span>- أصحاب الجنة يوم القيامة خير مستقراً وأحسن منزلا ومأوى للاسترواح، لأنه الجنة المعدة للمؤمنين لا النار المعدة للكافرين.',NULL,'535',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2882,'<span data-type="title" id=toc-2907>٢٥ </span>- واذكر - أيها النبى - يوم تنفرج السماء وتنفتح، ويظهر من فُرَجها الغمام، وتنزل الملائكة نزولاً مؤكداً.',NULL,'535',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2883,'<span data-type="title" id=toc-2908>٢٦ </span>- فى هذا اليوم تبطل أملاك المالكين من الناس وتنقطع دعاواهم، ويخلص الملك للرحمن - وحده - ويكون يوماً شديداً عصيباً على الكافرين.',NULL,'535',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2884,'<span data-type="title" id=toc-2909>٢٧ </span>- يوم القيامة يعض الظالم لنفسه - بالكفر ومخالفة الرسل - على يديه أسفاً وندماً يقول متمنياً: يا ليتنى اتبعت الرسل فسلكت طريق الجنة وتجنبت طريق النار.',NULL,'535',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2885,'<span data-type="title" id=toc-2910>٢٨ </span>- يقول نادماً على اتِّباع مَنْ أضلوه: يا ليتنى لم أصادق فلاناً الذى ملَّكته قيادى.',NULL,'535',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2886,'<span data-type="title" id=toc-2911>٢٩ </span>- لقد أبعدنى هذا الصديق عن ذكر الله وذكر القرآن بعد أن يُسِّر لى، وهكذا يخذل الشيطان الإنسان ويسلمه إلى ما فيه هلكته.',NULL,'535',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2887,'<span data-type="title" id=toc-2912>٣٠ </span>- وقال الرسول يشكو إلى الله ما يلاقيه من تعنت قومه: إنهم تركوا القرآن وهجروه، وتمادوا فى إعراضهم وعنادهم وعدائهم.',NULL,'535',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2888,'<span data-type="title" id=toc-2913>٣١ </span>- كما جعلنا قومك - يا محمد - يعادونك ويُكذِّبوك، جعلنا لكل نبى عدواً من المجرمين يعادونه ويقاومون دعوته، وسينصرك الله ويهديك إلى قهرهم، وحسبُك به هادياً ونصيراً.',NULL,'535',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2889,'<span data-type="title" id=toc-2914>٣٢ </span>- وقال الذين كفروا طعناً فى القرآن: لِمَ لَمْ ينزل دفعة واحدة، لقد أنزلناه كذلك مفرقاً ليثبت به فؤادك بأُنسك به وحفظك له، ورتَّلناه. فرقنا آيه، أو قرأناه على لسان جبريل شيئاً فشيئاً على تؤدة وتمهل.',NULL,'535',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2890,'<span data-type="title" id=toc-2915>٣٣ </span>- ولا يأتونك بحال من الاعتراضات الواهية إلا جئناك بالحق نبيِّنه ونُفسِّره أحسن تفسير.',NULL,'536',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2891,'<span data-type="title" id=toc-2916>٣٤ </span>- والذين كفروا برسالتك سيُسحبون إلى النار على وجوههم أذلاء، وهم شر الناس منزلة وأوغلُهم فى الضلال.',NULL,'536',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2892,'<span data-type="title" id=toc-2917>٣٥ </span>- ويُسَلّى الرسول مما وقع للرسل قبله، ولقد نزلنا على موسى التوراة وكلفناه أن يقوم بتبليغ رسالتنا، وأيدناه بأخيه هارون وزيراً له ومعيناً فى أمره.',NULL,'536',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2893,'<span data-type="title" id=toc-2918>٣٦ </span>- فقلنا: اذهب أنت وأخوك إلى فرعون وقومه. وأيدناه بالمعجزات التى تدل على صدقه، فلم يؤمنوا بها وكذبوه، فكان عاقبتهم أن أهلكناهم ومحقناهم محقاً.',NULL,'536',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2894,'<span data-type="title" id=toc-2919>٣٧ </span>- وكذلك فعلنا من قبل موسى مع قوم نوح لما كذَّبوه - ومن كذَّب رسولا فقد كذّب الرسل أجمعين - فقد أغرقناهم بالطوفان وجعلناهم عبرة للناس، وجعلنا لهم ولكل مشرك فى الآخرة عذاباً أليماً.',NULL,'536',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2895,'<span data-type="title" id=toc-2920>٣٨ </span>- وكذلك أهلكنا عادا وثمود وأصحاب الرَّسَ لما كذبوا رسلهم، وأهلكنا أمماً كثيرة كانوا بين أمة نوح وبين عاد فأصابهم جزاء الظالمين.',NULL,'536',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2896,'<span data-type="title" id=toc-2921>٣٩ </span>- ولقد أنذرنا هؤلاء الأقوام كلهم، وذكرنا لهم العظات والأمثال الصحيحة النافعة، ولكنهم لم يتعظوا فأخذناهم كلهم بالعذاب وأهلكناهم ودمرنا ديارهم تدميراً.',NULL,'536',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2897,'<span data-type="title" id=toc-2922>٤٠ </span>- وهؤلاء - قريش - يمرون فى أسفارهم إلى الشام على قرية قوم لوط التى أمطرنا عليها شر مطر وأسوأه - حجارة من سجيل - أفلم يروا هذه القرية فيتعظوا بما حل لأهلها؟ إنهم يرونها ولكن لا بأعين الاتعاظ والاعتبار، إذ كانوا لا يؤمنون بمعاد ولا بعث، ولا يتوقعون يوماً ينشرون فيه إلى الحساب.',NULL,'536',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2898,'<span data-type="title" id=toc-2923>٤١ </span>- وإذا أبصرك هؤلاء لا يتخذونك إلا موضع هزؤ وسخرية، ويقول بعضهم لبعض: أهذا هو الذى بعثه الله رسولاً إلينا نتبعه ونسير وراءه؟!',NULL,'536',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2899,'<span data-type="title" id=toc-2924>٤٢ </span>- لقد أوتى هذا الرجل من حُسْن البيان وقوة الحُجة ما يجذب السامعين، ولقد نال من عقائدنا حتى لقد كاد يُزحزحنا عن آلهتنا ويميلنا إلى إلهه، ولكننا ثبتنا على آلهتنا وديننا. سنبين لهم جلية الأمر حين يرون العذاب يوم القيامة ويعلمون من هو أثبت فى الضلال والغواية.',NULL,'537',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2900,'<span data-type="title" id=toc-2925>٤٣ </span>- أرأيت - أيها الرسول - ضلال من اتَّبع هواه وشهواته حتى إنه ليعبد حجارة لا تضر ولا تنفع؟ وأنت قد بعثت نذيراً وبشيراً ولست موكلا بإيمانهم وهدايتهم.',NULL,'537',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2901,'<span data-type="title" id=toc-2926>٤٤ </span>- وهل تظن أن أكثرهم يسمعون سماع الفهم أو يهتدون بعقولهم؟! لقد نفذوا ما تأمرهم به أحلامهم، وصاروا كالبهائم لا همّ لهم إلا الأكل والشرب ومتاع الحياة الدنيا، ولا تفكير لهم فيما وراء ذلك، بل هم شر مكاناً من البهائم، فالبهائم تنقاد لأصحابها إلى ما فيه خيرها، وتنأى عما يضرها، وهؤلاء يلقون بأنفسهم فيما يهلكهم.',NULL,'537',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2902,'<span data-type="title" id=toc-2927>٤٥ </span>- لقد نصبنا من الدلائل على التوحيد ما يهدى ذوى الألباب، انظر إلى الظل فقد بسطه الله وجعله ساكناً أول النهار، ثم سلطنا الشمس تزيل منه بما يحل محله من أشعتها، فكانت الشمس دالة عليه ولولاها ما عرف الظل، ولو شاء الله لجعل الظل ساكناً مطبقاً على الناس فتفوت مصالحهم ومرافقهم.',NULL,'537',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2903,'<span data-type="title" id=toc-2928>٤٦ </span>- ولقد كان نسخنا للظل بالشمس تدريجياً بمقدار ولم يكن دفعة واحدة، وفى ذلك منافع للناس.',NULL,'537',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2904,'<span data-type="title" id=toc-2929>٤٧ </span>- ومن آيات التوحيد أن جعل الليل ستراً بظلامه، يدخل فيه الخلق فيحيطهم إحاطة الثوب بلابسه. وهيَّأ الناس للنوم فكان راحة لهم يستجمون به من التعب، ثم يأتى النهار بضيائه ناشراً للناس باحثين عن معايشهم طالبين لرزقهم.',NULL,'537',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2905,'<span data-type="title" id=toc-2930>٤٨ </span>- وهو الذى سخر الرياح فتسوق السحب وتبشر الناس بالمطر الذى هو رحمة منه لهم، ولقد أنزلنا من السماء ماء طاهراً مُطهراً مزيلا للأنجاس والأوساخ.',NULL,'537',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2906,'<span data-type="title" id=toc-2931>٤٩ </span>- أنزلنا المطر لينبت به الزرع، فتحيا به الأرض الجدبة بعد موتها، وينتفع به السقيا مما خلق أنعاماً وأناسى كثيراً.',NULL,'538',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2907,'<span data-type="title" id=toc-2932>٥٠ </span>- وهذا القرآن قد بيَّنا آياته وصرَّفناها، ليتذكر الناس ربهم وليتعظوا ويعملوا بموجبه، ولكن أكثر الناس أبوا إلا الكفر والعناد.',NULL,'538',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2908,'<span data-type="title" id=toc-2933>٥١ </span>- ولو شئنا لبعثنا فى كل بلدة نذيراً، فاجتهد فى دعوتك، ودع كلام الكافرين، وانبذ ما يأتون به.',NULL,'538',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2909,'<span data-type="title" id=toc-2934>٥٢ </span>- واستمر فى دعوتك إلى الحق وتبليغ رسالة ربك، وإن قاوموا دعوتك واعتدوا على المؤمنين فحاربهم وجاهد فى ذلك جهاداً عظيماً.',NULL,'538',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2910,'<span data-type="title" id=toc-2935>٥٣ </span>- والله هو الذى أجرى البحرين: البحر العذب والبحر الملح، وجعل المجرى لكل واحد يجاور المجرى الآخر، ومع ذلك لا يختلطان، نعمة ورحمة بالناس.',NULL,'538',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2911,'<span data-type="title" id=toc-2936>٥٤ </span>- والله هو الذى خلق من النطفة هؤلاء الناس، وجعلهم ذكوراً وإناثاً ذوى قرابات بالنسب أو المصاهرة، وكان الله قديراً على ما يريد إذ خلق من النطفة الواحدة نوعين متمايزين.',NULL,'538',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2912,'<span data-type="title" id=toc-2937>٥٥ </span>- وبعد هذه الآيات الدالة على استحقاق الله - وحده - العبادة، وأن لا إله سواه، يعبد فريق من الناس ما لا ينفع ولا يضر من الأوثان، وهؤلاء بعملهم هذا يعاونون الشيطان وهو يضلهم، فهم متظاهرون على الحق الذى دعاهم إليه الله.',NULL,'538',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2913,'<span data-type="title" id=toc-2938>٥٦ </span>- وليس عليك - أيها النبى - إلا تبليغ ما أرسلت به، وتبشير المؤمنين بالجنة، وتخويف الكافرين ما سيلقونه، وليس عليك بعد ذلك شئ تطالب به.',NULL,'538',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2914,'<span data-type="title" id=toc-2939>٥٧ </span>- وقل لهم: إنى لا أبتغى على دعوتكم إلى الإسلام أجراً وجزاء، إلا أن يهتدى أحدكم ويسلك سبيل الحق ويرجع إلى ربه.',NULL,'538',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2915,'<span data-type="title" id=toc-2940>٥٨ </span>- وتوكل فى أمورك على الله الحى الذى لا يمكن أن يموت، ونزّهه وقدسه حامداً أنعمه، ودع من خرج عن الجادة، فالله خبير بهم مكافئ لهم على ذنوبهم.',NULL,'539',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2916,'<span data-type="title" id=toc-2941>٥٩ </span>- والله هو الذى خلق السموات والأرض وما بينهما فى ستة أيام، وقد استولى على العرش والملكوت وعم سلطانه كل شئ، وهو الرحمن، وإن ابتغيت أن تعرف شيئاً من صفاته فاسأل الخبير عنه يجبك وهو الله العليم الحكيم.',NULL,'539',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2917,'<span data-type="title" id=toc-2942>٦٠ </span>- وإذا قيل لهؤلاء الكفار: اخضعوا للرحمن واعبدوه. كان جوابهم بالإنكار وتجاهل الرحمن وقالوا: من هو الرحمن؟! نحن لا نعلمه حتى نسجد له، فهل نخضع لأمرك وحسب؟ ، وازدادوا عن الإيمان بُعْداً ونفوراً.',NULL,'539',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2918,'<span data-type="title" id=toc-2943>٦١ </span>- تعالى الرحمن وتزايد فضله، أنشأ الكواكب فى السموات وجعل لها منازل تسير فيها، وجعل من الكواكب الشمس سراجاً مضيئاً والقمر منيراً.',NULL,'539',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2919,'<span data-type="title" id=toc-2944>٦٢ </span>- والرحمن هو الذى جعل الليل والنهار متعاقبين: يخلف أحدهما الآخر، وقد دبرنا هذا ليتذكر من شاء هذا التدبير، فيعرف حكمة الله وقدرته، أو يشكره على هذه النعمة الجليلة.',NULL,'539',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2920,'<span data-type="title" id=toc-2945>٦٣ </span>- فعباد الرحمن هم الذين يتواضعون فى الدنيا، إذا مَشوْا على الأرض مشوا فى سكينة ووقار، وكذلك فى سائر أعمالهم، وإذا سابَّهم السفهاء من المشركين تركوهم وشأنهم وقالوا لهم: لا شأن لنا بكم بل أمرنا سلام عليكم.',NULL,'539',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2921,'<span data-type="title" id=toc-2946>٦٤ </span>- والذين يبيتون على التعبد والصلاة ويذكرون الله كثيراً.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2922,'<span data-type="title" id=toc-2947>٦٥ </span>- والذين يغلِّبون الخوف على الرجاء - شأن الأتقياء - فيخافون عذاب الآخرة، يكون دأبهم أن يدعوا الله أن ينجيهم من عذاب جهنم، فإن عذابها إذا نزل بمجرم يلزمه ولا يفارقه.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2923,'<span data-type="title" id=toc-2948>٦٦ </span>- وأن جهنم شر مستقر لمن يستقر فيها، وشر مقام لمن يقيم.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2924,'<span data-type="title" id=toc-2949>٦٧ </span>- ومن سمات عباد الرحمن: الاعتدال فى إنفاقهم المال على أنفسهم وأسرهم، فهم لا يبذرون ولا يضيقون فى النفقة، بل نفقتهم وسط بين الأمرين.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2925,'<span data-type="title" id=toc-2950>٦٨ </span>- ومن شأنهم أنهم أخلصوا التوحيد، ونبذوا كل أثر للشرك فى عبادة ربهم، وتنزَّهوا عن قتل النفوس التى نهى الله عن قتلها. لكن إن اعتدت قُتِلَتْ بالحق. وقد تجنبوا الزنى، وقصروا أنفسهم على الحلال من أوجه المتاع، لينجوا من عقاب هذه الملهكات، فإن من يفعل هذه الأمور يلق منها شرا وعذاباً.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2926,'<span data-type="title" id=toc-2951>٦٩ </span>- فإنه سيلقى يوم القيامة عذاباً مضاعفاً، ويخلد فيه ذليلا مهاناً.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2927,'<span data-type="title" id=toc-2952>٧٠ </span>- ولكن مَن تاب من هذه الذنوب، وصدق فى إيمانه، وأَتبعَ ذلك بالطاعات والأعمال الصالحة، فهؤلاء يغفر لهم رحمة منه، ويجعل لهم مكان السيئات السالفة حسنات يثيبهم عليها أجزل الثواب، وأن الله من شأنه الرحمة والغفران.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2928,'<span data-type="title" id=toc-2953>٧١ </span>- وهكذا مضى أمرنا: أن من تاب من إثمه وظهر أثر ذلك فى إقباله على الطاعة واجتنابه المعصية، فهو الذى يقبل الله توبته. وبها يرجع إلى ربه بعد نِفاره.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2929,'<span data-type="title" id=toc-2954>٧٢ </span>- ومن أخلاق عباد الرحمن: أنهم يتنزَّهون عن شهادة الزور، وأنهم إذا صادفوا من إنسان ما لا يُحمد من قول أو فعل لم يشتركوا فيه، ورفعوا أنفسهم عن مقارنته.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2930,'<span data-type="title" id=toc-2955>٧٣ </span>- ومن صفاتهم: أنهم إذا وعظهم واعظ وتلا عليهم آيات الله ألقوا بمسامعهم إليها، فوعتها قلوبهم، وتفتحت لها بصائرهم، ولم يكونوا كأولئك الذين يضطربون عند سماعها معرضين عنها، لا تخرق آذانهم وتنسد عنها أبصارهم.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2931,'<span data-type="title" id=toc-2956>٧٤ </span>- وهم يسألون ربهم أن يجعل نساءهم وأولادهم موضع أنس أنفسهم بما يعملون من خير، وأن يجعلهم أئمة فى الخير يقتدى بهم الصالحون.',NULL,'540',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2932,'<span data-type="title" id=toc-2957>٧٥ </span>- هؤلاء الموصوفون بما وصفناهم عباد الله حقاً، وجزاؤهم غرف الجنة العالية كفاء صبرهم على الطاعات، وسيلقون فى الجنة التحية والتسليم.',NULL,'541',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2933,'<span data-type="title" id=toc-2958>٧٦ </span>- ونعيمهم فى الجنة خالد لا انقطاع له، فنعم الجنة مستقراً ومقاماً.',NULL,'541',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2934,'<span data-type="title" id=toc-2959>٧٧ </span>- قل - أيها الرسول - للناس: إن الله لا يعنيه منكم إلا أن تعبدوه وتدعوه فى شئونكم ولا تدعوا غيره، ولذلك خلقكم، ولكن الكافرين منكم كذبوا ما جاء به الرسل، فسيكون عذابهم لازماً لا منجى لهم منه.',NULL,'541',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2935,'<span data-type="title" id=toc-2961>١ </span>- هذه الحروف لبيان أن القرآن المعجز للبشر ركبت كلماته منها ومن أخواتها، وهى فى طوقهم، فمن ارتاب فى أنه من عند الله فليأت بمثله، ولن يستطيع.',NULL,'542',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2936,'<span data-type="title" id=toc-2962>٢ </span>- هذا الكلام الذى أوحيت به إليك آيات الكتاب الموضح لما اشتمل عليه من أحكام.',NULL,'542',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2937,'<span data-type="title" id=toc-2963>٣ </span>- أشفق على نفسك - أيها النبى - أن تقتلها حزناً على عناد قومك، وعدم إيمانهم.',NULL,'542',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2938,'<span data-type="title" id=toc-2964>٤ </span>- إن فى قدرتنا أن نأتيهم بمعجزة تلجئهم إلى الإيمان، فيخضعوا لأمره، ويتم ما ترجوه، ولم نأتهم بذلك لأن سنتنا تكليف الناس بالإيمان دون إلجاء، كى لا تفوت الحكمة فى الابتلاء، وما وراءه من ثواب وعقاب.',NULL,'542',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2939,'<span data-type="title" id=toc-2965>٥ </span>- وما يجدد الله لقومك بوحيه ما يذكرهم بالدين الحق، رحمة بهم، إلا جددوا إعراضاً عنه، وكفراً به، حيث أغلقت أمامهم طرق الهداية.',NULL,'542',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2940,'<span data-type="title" id=toc-2966>٦ </span>- فقد كذّب هؤلاء بالحق الذى جئتهم به، وسخروا منه، فاصبر عليهم، فسيرون عاقبة استهزائهم القاصمة.',NULL,'542',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2941,'<span data-type="title" id=toc-2967>٧ </span>- فَعلوا ما فَعلوا من الكفر والتكذيب ولم ينظروا إلى بعض خلق الله فى الأرض، ولو نظروا متأملين لاهتدوا، فهذه الكثرة من أصناف النباتات النافعة أخرجناها من الأرض، ولا يستطيع ذلك غير إله واحد قدير.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2942,'<span data-type="title" id=toc-2968>٨ </span>- إن فى إخراج النبات من الأرض لدلالة عظيمة على وجود الخالق القدير، وما كان أكثر القوم مؤمنين.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2943,'<span data-type="title" id=toc-2969>٩ </span>- وإن مالك أمرك وحافظك لهو المنتقم من المكذبين المتفضل بالرحمة على المؤمنين.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2944,'<span data-type="title" id=toc-2970>١٠ </span>- واذكر - يا محمد - لقومك قصة موسى حين ناداه ربك: يا موسى، اذهب رسولا إلى القوم الذين ظلموا أنفسهم بالكفر، وبنى إسرائيل بالاستعباد وذبح الأولاد.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2945,'<span data-type="title" id=toc-2971>١١ </span>- ائت قوم فرعون، فإنهم ماضون فى ظلمهم. عجباً لهم! أما يخافون عاقبة ذلك ويحذرونها؟',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2946,'<span data-type="title" id=toc-2972>١٢ </span>- قال موسى: يا رب إننى أخشى ألا يقبلوا رسالتى كبْراً وعناداً.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2947,'<span data-type="title" id=toc-2973>١٣ </span>- ويحيط بى الغم إذا كذبونى، ولا ينطلق لسانى حينئذٍ فى محاجتهم كما أحب، فأرسل جبريل إلى أخى هارون ليؤازرنى فى أمرى.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2948,'<span data-type="title" id=toc-2974>١٤ </span>- ولهؤلاء ذنب علىَّ، فقد قتلت منهم رجلا فأخاف أن يقتلونى قصاصاً قبل أداء مهمتى، ويزيدنى ذلك خوفاً.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2949,'<span data-type="title" id=toc-2975>١٥ </span>- قال الله له: لن يقتلوك، وقد أجبت سؤالك فى هارون، فاذهبا مزودين بمعجزاتنا، إنى معكما بالحفظ أسمع ما يجرى بينكما وبين فرعون، فلكما النصر والتأييد.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2950,'<span data-type="title" id=toc-2976>١٦ </span>- فتوجها إلى فرعون فقولا له: إنَّا مرسلان إليك من رب العالمين.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2951,'<span data-type="title" id=toc-2977>١٧ </span>- يقول لك رب العالمين: أطلقْ سراح بنى إسرائيل ليذهبوا معنا.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2952,'<span data-type="title" id=toc-2978>١٨ </span>- قال فرعون لموسى مُمْتَناً - وقد عرفه حينما دخلا عليه وأديا الرسالة حيث تربى فى قصره - ألم نربك فينا وليداً، ومكثت فى رعايتنا سنين من عمرك؟ .',NULL,'543',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2953,'<span data-type="title" id=toc-2979>١٩ </span>- وجنيت جنايتك النكراء بقتلك رجلا من قومى، وجحدت نعمتى التى سلفت منا عليك، فلم تحفظ رعيتى، واعتديت على ألوهيتنا بادعاء أنك رسول رب العالمين.',NULL,'544',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2954,'<span data-type="title" id=toc-2980>٢٠ </span>- قال موسى: لقد فعلت ما ذكرت جهلا بما يفضى إليه العقل من القتل، فلا تثريب على.',NULL,'544',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2955,'<span data-type="title" id=toc-2981>٢١ </span>- ففررت منكم لما خفت أن تقتلونى بهذه الجناية التى لم تكن عن عمد، فوهب لى ربى فهْماً وعلماً، تفضلا وإنعاماً، وجعلنى من المرسلين.',NULL,'544',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2956,'<span data-type="title" id=toc-2982>٢٢ </span>- أشار موسى إلى خصلة ذميمة من خصال فرعون، وبيَّن أنها تعبيد بنى إسرائيل وذبح أبنائهم، وأبى أن تسمى تربيته فى بيته نعمة، فسببها اتصافه بما تقدم، فألقى فى اليَم لينجو من قتله، فآل إلى بيته، ولولا ذلك لرباه أبواه.',NULL,'544',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2957,'<span data-type="title" id=toc-2983>٢٣ </span>- قال فرعون: وما صفة رب العالمين الذى تذكره كثيراً، وتدعى أنك رسوله حيث لا نعلم عنه شيئاً؟',NULL,'544',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2958,'<span data-type="title" id=toc-2984>٢٤ </span>- قال موسى هو مالك السموات والأرض وما بينهما، إن كنتم موقنين بصدق هذا الجواب لانتفعتم واهتديتم، وعرفتم أن مُلْك فرعون المُدَّعى لا يذكر فى جانب ملكه، فهو لا يعدو إقليماً واحداً فى الأرض.',NULL,'544',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2959,'<span data-type="title" id=toc-2985>٢٥ </span>- قال فرعون - يعجب لمن حوله من جواب موسى، أذْ ذكر ربَّا غيره لا يذكر فى جانب ملكه ملك فرعون: كيف تسمعون كلام موسى؟',NULL,'544',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2960,'<span data-type="title" id=toc-2986>٢٦ </span>- قال موسى ماضياً فى أمره غير مبال بغيظ فرعون وسوء مقالته: رب العالمين خالقكم وخالق آبائكم السابقين، ومنهم مَن كان يدَّعى الأُلوهية كما تدَّعى، وقد لحقهم الفناء، وستفنى مثلهم فيبطل ما تدعيه، إذ الإله الحق لا يموت.',NULL,'544',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2961,'<span data-type="title" id=toc-2987>٢٧ </span>- قال فرعون محرضاً قومه على تكذيبه: إن رسولكم لمجنون، حيث سألته عن حقيقة ربه فذكر لى أشياء وصفات غريبة.',NULL,'544',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2962,'<span data-type="title" id=toc-2988>٢٨ </span>- قال موسى: إن كنتم تعقلون فآمنوا برسالتى، لأن شروق الشمس وغروبها بتقدير مُحكم دليل ظاهر على الخالق، إذن فأنتم الأحِقَّاء بصفة الجنون.',NULL,'545',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2963,'<span data-type="title" id=toc-2989>٢٩ </span>- قال فرعون لموسى: لئن اتخذت إلهاً غيرى لأجعلنك واحداً ممن عرفت سوء حالهم فى سجونى، وقد لجأ إلى تهديده بهذا بعد أن يئس من رفع آثار صنع الخالق.',NULL,'545',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2964,'<span data-type="title" id=toc-2990>٣٠ </span>- قال موسى متلطفاً طمعاً فى إيمانه: أتجعلنى من المسجونين ولو جئتك ببرهان عظيم يصدقنى فيما أقول؟',NULL,'545',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2965,'<span data-type="title" id=toc-2991>٣١ </span>- قال فرعون: فأت بالذى يشهد بنبوتك إن كنت صادقاً فى دعواك، قال ذلك طمعاً فى أن يجد موطن ضعف فى حجته.',NULL,'545',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2966,'<span data-type="title" id=toc-2992>٣٢ </span>- فألقى موسى عصاه فى الأرض أمامهم، فانقلبت ثعباناً حقيقياً، لا شيئاً مُزَوراً بالسِّحر يُشْبه الثعبان.',NULL,'545',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2967,'<span data-type="title" id=toc-2993>٣٣ </span>- وأخرج موسى يده من جيبه آية ثانية، فإذا هى بيضاء، اشتد بياضها من غير سوء، حتى بهر الناظرين.',NULL,'545',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2968,'<span data-type="title" id=toc-2994>٣٤ </span>- قال فرعون لقومه: إن موسى لساحر فائق فى سحره. قال ذلك خشية أن يخضعوا للحق الذى رأوْه من موسى.',NULL,'545',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2969,'<span data-type="title" id=toc-2995>٣٥ </span>- وقال فرعون أيضاً: يريد هذا الساحر أن يقهرنى فيخرجكم من أرضكم، وذلك تحريض على موسى. إذ من أَشَقّ الأشياء مفارقة الوطن لا سيما إذا كانت قهراً. وطلب الرأى ممن يعبدونه ناسياً ألوهيته لقوة آيات موسى.',NULL,'545',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2970,'<span data-type="title" id=toc-2996>٣٦ </span>- قال له قومه: أجِّل الفصل فى أمرهما، وأرسل الجند فى المدائن يجمعون لك السحرة من رعيتك، فالسحر يعارض بالسحر.',NULL,'545',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2971,'<span data-type="title" id=toc-2997>٣٧ </span>- يأتوك بالعدد الكثير، وكلهم قد أجاد فن السحر ويفوق موسى عملا به ومراناً عليه. وقصدوا بهذا التخفيف من قلق فرعون.',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2972,'<span data-type="title" id=toc-2998>٣٨ </span>- فجمع السحرة من كل أرجاء البلاد، وحدد لهم وقت الضحى من يوم الزينة للاجتماع بموسى.',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2973,'<span data-type="title" id=toc-2999>٣٩ </span>- وقال الناس - يحث بعضهم بعضاً على الاجتماع فى اليوم المعلوم لحضور الحفل المشهود -: «هل أنتم مجتمعون» ؟ أى اجتمعوا.',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2974,'<span data-type="title" id=toc-3000>٤٠ </span>- وأعلنوا توقعهم انتصار السحرة، فيثبتون على دينهم، حمْلا على الاهتمام والجد فى مغالبة موسى.',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2975,'<span data-type="title" id=toc-3001>٤١ </span>- فلما جاء السحرة فرعون قالوا له: أيكون لنا قِبَلك أجر عظيم إن كنا نحن الغالبين؟ .',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2976,'<span data-type="title" id=toc-3002>٤٢ </span>- قال فرعون: نعم لكم ما ذكرتم، ومع هذا الأجر العظيم تكونون من المقربين لدىَّ، ومن أصحاب الجاه والسلطان.',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2977,'<span data-type="title" id=toc-3003>٤٣ </span>- قال موسى للسحرة - حينما جاء الوقت المحدد فى اليوم الموعود - ألقوا ما تريدون إلقاءه من السحر.',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2978,'<span data-type="title" id=toc-3004>٤٤ </span>- فألقوا حبالهم وعصيّهم، وخُيّل للناس أنها حيات تسعى، وأقسموا بعزة فرعون وقوته إنهم الغالبون.',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2979,'<span data-type="title" id=toc-3005>٤٥ </span>- فألقى موسى عصاه، فإذا هى حية عظيمة تبتلع ما كانوا يزوِّرونه بالسحر من حبالهم وعصيِّهم، متوهمين أنها حيات تسعى.',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2980,'<span data-type="title" id=toc-3006>٤٦ </span>- فبادر السحرة بالسجود لله حينما أيقنوا أن أمر موسى ليس بالسحر.',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2981,'<span data-type="title" id=toc-3007>٤٧ </span>- قالوا مؤكدين فعل السجود بالقول: {آمنا برب العالمين} .',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2982,'<span data-type="title" id=toc-3008>٤٨ </span>- وبيَّنوا أن رب العالمين الذى آمنوا به {رب موسى وهارون} .',NULL,'546',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2983,'<span data-type="title" id=toc-3009>٤٩ </span>- قال فرعون - منكراً على قومه إيمانهم بموسى قبل إذنه لهم، مهدداً إياهم على ذلك بأنه أستاذهم الذى عليه تلقوا فنون السحر، وسيعلمون ما سينزل بهم من العقاب -: لأقطعنَّ أيديكم وأرجلكم من خلاف. أقطع اليمنى مع اليسرى أو العكس. ولأُصلبنكم أجمعين.',NULL,'547',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2984,'<span data-type="title" id=toc-3010>٥٠ </span>- قال السحرة: لا ضرر علينا مما يلحقنا من عذابك الذى توعدتنا به. لأنا راجعون إلى ثواب ربنا، وهو خير ثواب وخير عاقبة.',NULL,'547',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2985,'<span data-type="title" id=toc-3011>٥١ </span>- إنا نرجو أن يغفر لنا ربنا خطايانا التى أسلفْناها، إذ كنا أول المؤمنين فى قومك.',NULL,'547',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2986,'<span data-type="title" id=toc-3012>٥٢ </span>- وأوحى الله إلى موسى - عليه السلام - أن يسير ليلا بالمؤمنين من بنى إسرائيل حينما لم تُجْدِ مصابرة موسى، وقد نظم أمر الفريقين على أن يتقدم موسى بقومه، ويتبعهم فرعون بقومه حتى يدخلوا مدخلهم من طريق البحر، فيهلكهم الله.',NULL,'547',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2987,'<span data-type="title" id=toc-3013>٥٣ </span>- فأرسل فرعون جنده فى مدائن مملكته يجمعون الأشداء من قومه حينما علم بسير موسى ببنى إسرائيل، ليحول بينهم وبين ما يقصدون.',NULL,'547',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2988,'<span data-type="title" id=toc-3014>٥٤ </span>- قال فرعون: إن بنى إسرائيل الذين فروا مع موسى طائفة خسيسة فى شأنها قليل عددها. يثير بذلك الحمية فى نفوس جنده.',NULL,'547',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2989,'<span data-type="title" id=toc-3015>٥٥ </span>- وإنهم مع هذا فاعلون ما يثير غيظنا بمخالفة أمرنا والخروج بغير إذننا.',NULL,'547',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2990,'<span data-type="title" id=toc-3016>٥٦ </span>- وإنا لجمع من عادتنا الحذر واليقظة، والحزم فى الأمور.',NULL,'547',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2991,'<span data-type="title" id=toc-3017>٥٧ </span>- فأخرجنا فرعون وجنوده من أرضهم الشبيهة بجنات تجرى من تحتها الأنهار، فأهلكوا بصرفهم عن الحق، وإثارتهم إلى الخروج وراء موسى بما جاء فى الآيات الثلاث السابقة.',NULL,'547',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2992,'<span data-type="title" id=toc-3018>٥٨ </span>- وأخرجناهم كذلك من كنوز الذهب والفضة والأماكن التى كانوا يقيمون فيها، مُنَعَّمين بجمالها وحسن مرافقها.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2993,'<span data-type="title" id=toc-3019>٥٩ </span>- مثل هذا الإخراج العجيب الذى وصفناه لك أخرجناهم، وجعلنا هذا الملك وما فيه من ألوان النعيم لبنى إسرائيل بعد أن كانوا مُعْدمين.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2994,'<span data-type="title" id=toc-3020>٦٠ </span>- جدَّ فرعون وقومه فى السير ليلحقوا ببنى إسرائيل، فلحقوا بهم وقت شروق الشمس.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2995,'<span data-type="title" id=toc-3021>٦١ </span>- فلما رأى كل من الجمعين الآخر قال أصحاب موسى: إن فرعون وقومه سيدركوننا، فينزل بنا الهلاك.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2996,'<span data-type="title" id=toc-3022>٦٢ </span>- قال موسى: إن معى عناية الله تلاحقنى بالحفظ، وسيرشدنى إلى طريق النجاة. ليطمئنوا على سلامتهم، ولتبتعد عن أذهانهم فكرة الإدراك المفزعة.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2997,'<span data-type="title" id=toc-3023>٦٣ </span>- فأوحينا إلى موسى: أن يضرب البحر بعصاه، فانفلق البحر إلى اثنى عشر طريقاً بعدد طوائف بنى إسرائيل، وكان كل طريق من هذه الطرق حاجزاً من الماء كالجبل العظيم الثابت.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2998,'<span data-type="title" id=toc-3024>٦٤ </span>- وقرَّبنا فرعون وقومه حتى دخلوا هذه الطرق وراء موسى وقومه.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (2999,'<span data-type="title" id=toc-3025>٦٥ </span>- وأنجينا موسى ومن معه بحفظ البحر متماسكاً حتى تم عبورهم.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3000,'<span data-type="title" id=toc-3026>٦٦ </span>- ثم أغرقنا فرعون ومن معه بإطباق الماء عليهم عندما تبعوهم.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3001,'<span data-type="title" id=toc-3027>٦٧ </span>- إن فى ذلك التصرف الإلهى العجيب لعبرة لمن أراد أن ينتفع، وما كان أكثر القوم مصدقين.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3002,'<span data-type="title" id=toc-3028>٦٨ </span>- وإن خالقك ومربيك لهو القوى فى الانتقام من المكذبين، المنعم بالرحمات على المؤمنين.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3003,'<span data-type="title" id=toc-3029>٦٩ </span>- واتْلُ على الكافرين - أيها الرسول - قصة إبراهيم - عليه السلام.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3004,'<span data-type="title" id=toc-3030>٧٠ </span>- إذ قال لأبيه وقومه: أى شئ هذا الذى تعبدونه مما لا يستحق العبادة؛ يقصد تقبيح عبادة الأصنام.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3005,'<span data-type="title" id=toc-3031>٧١ </span>- قالوا مجيبين بطريق المباهاة: نعبد أصناماً فنقيم على عبادتها دائماً تعظيماً لها وتمجيداً.',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3006,'<span data-type="title" id=toc-3032>٧٢ </span>- قال إبراهيم: هل يسمعون دعاءكم، أو يستجيبون لكم إذ تدعونهم؟ يقصد بذلك التنبيه على فساد مسلكهم.',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3007,'<span data-type="title" id=toc-3033>٧٣ </span>- أو يقدمون لكم نفعاً إذا أطعتموهم، أو يصيبونكم بضر إذا عصيتموهم؟ .',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3008,'<span data-type="title" id=toc-3034>٧٤ </span>- قالوا: لا يفعلون شيئاً من ذلك، ولكن وجدنا آباءنا يعبدونها مثل عبادتنا، فقلدناهم فيما كانوا يفعلون.',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3009,'<span data-type="title" id=toc-3035>٧٥ </span>- قال إبراهيم - تبكيتاً لهم -: أفكَّرتم فعلمتم أى شئ تستمرون على عبادته؟',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3010,'<span data-type="title" id=toc-3036>٧٦ </span>- أنتم وآباؤكم الأقدمون. أهو أهل لأن يعبد أم لا؟ . لو تأملتم لعلمتم أنكم فى الضلال المبين.',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3011,'<span data-type="title" id=toc-3037>٧٧ </span>- فإن ما تعبدونهم من دون الله أعداء لى ولكم، فلا أعبدهم. لكن خالق العالمين ومالك أمرهم وحافظهم هو الذى أعبده، وأتقرب إليه.',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3012,'<span data-type="title" id=toc-3038>٧٨ </span>- الذى أوجدنى من العدم فى أحسن تقويم، ووهبنى الهداية لما يوصلنى إلى سعادتى فى الدنيا والآخرة.',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3013,'<span data-type="title" id=toc-3039>٧٩ </span>- وهو الذى أنعم علىَّ بالطعام والشراب، وأقدرنى على تناولهما والانتفاع بهما، حفظاً لحياتى.',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3014,'<span data-type="title" id=toc-3040>٨٠ </span>- وإذا نزل بى مرض فهو الذى يشفينى بتيسير أسباب الشفاء، وتفويض الأمر إليه.',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3015,'<span data-type="title" id=toc-3041>٨١ </span>- والذى يُميتنى إذا حلَّ أجلى، والذى يُحيينى مرة أخرى للحساب والجزاء.',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3016,'<span data-type="title" id=toc-3042>٨٢ </span>- والذى أطمع فى غفرانه وتجاوزه عما فرط منى من الهفوات فى الدنيا، إذا جاء وقت الحساب.',NULL,'549',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3017,'<span data-type="title" id=toc-3043>٨٣ </span>- قال إبراهيم - عليه السلام - داعياً: رب امنحنى كمالا فى العلم والعمل، حتى أكون أهلا لحمل رسالتك والحكم بين عبادك، ووفقنى لانتظم فى عداد الصالحين.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3018,'<span data-type="title" id=toc-3044>٨٤ </span>- واجعل لى ثناء حسناً، وذكراً جميلا فى الأمم التى تجئ بعدى، يبقى أثره بين الناس إلى يوم القيامة.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3019,'<span data-type="title" id=toc-3045>٨٥ </span>- واجعلنى من عبادك الذين منحتهم نعيم الجنة، ثواباً على إيمانهم بك وعبادتهم لك.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3020,'<span data-type="title" id=toc-3046>٨٦ </span>- واجعل أبى أهلاً للمغفرة بتوفيقه للإسلام - وكان قد وعده بالإسلام يوم فارقه - لأنه كان من المنحرفين عن طريق الهدى والرشاد.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3021,'<span data-type="title" id=toc-3047>٨٧ </span>- ولا تُلْحق بى هواناً أو خجلا بين الناس يوم يخرجون من القبور للحساب والجزاء.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3022,'<span data-type="title" id=toc-3048>٨٨ </span>- يوم لا ينفع أحداً مال يُبذل، ولا بنون ينصرون.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3023,'<span data-type="title" id=toc-3049>٨٩ </span>- إلا من كان مؤمناً، وأقبل على الله بقلب برئ من مرض الكفر والنفاق والرياء.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3024,'<span data-type="title" id=toc-3050>٩٠ </span>- وأُدنِيت الجنة وقُرِّبت من مكان السعداء، فيسير إليها الذين اتقوا الكفر والمعاصى، وأقبلوا على الإيمان والطاعة فى الدنيا.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3025,'<span data-type="title" id=toc-3051>٩١ </span>- وأُظْهِرت الجحيم للمنصرفين عن دين الحق، حتى يكاد يأخذهم لهبها فيتحسرون.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3026,'<span data-type="title" id=toc-3052>٩٢ </span>- وقيل لهم توبيخاً: أين آلهتكم التى كنتم تعبدونها؟!',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3027,'<span data-type="title" id=toc-3053>٩٣ </span>- من دون الله وتزعمون أنها تشفع لكم اليوم، هل ينفعونكم بنصرتهم لكم، أو ينفعون أنفسهم بانتصارهم؟ لا شئ من ذلك، لأنهم وآلهتهم وقود النار.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3028,'<span data-type="title" id=toc-3054>٩٤ </span>- فألقوا فى الجحيم على وجوههم، ينقلبون مرة بعد أخرى إلى أن يستقروا فى قاعها هم والذين أضَلُّوهم وأوقعوهم فى الغى والضلال.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3029,'<span data-type="title" id=toc-3055>٩٥ </span>- ومعهم أعوان إبليس الذين كانوا يزينون للناس الشرور والآثام، أو الذين اتبعوه من عصاة الإنس والجن.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3030,'<span data-type="title" id=toc-3056>٩٦ </span>- قالوا - معترفين بخطئهم - وهم يتخاصمون مع مَن أضلُّوهم من معبوداتهم:',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3031,'<span data-type="title" id=toc-3057>٩٧ </span>- والله إن كنا فى دنيانا لفى تخبط واضح، وجهل مطبق، وزيغ عن الحق الذى لا خفاء فيه.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3032,'<span data-type="title" id=toc-3058>٩٨ </span>- إذ نسوِّيكم أيها المعبودون من دون الله برب العالمين فى استحقاق العبادة، مع عجزكم وقدرته.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3033,'<span data-type="title" id=toc-3059>٩٩ </span>- وما أوقعنا فى هذا الهلاك إلا المجرمون الذين أضلُّونا عن سواء السبيل.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3034,'<span data-type="title" id=toc-3060>١٠٠ </span>- فلا يوجد لنا شافعون يخلَّصوننا من العذاب كما توهمنا من قبل.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3035,'<span data-type="title" id=toc-3061>١٠١ </span>- ولا صديق يتوجع لحالهم، وإن لم يخلصهم.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3036,'<span data-type="title" id=toc-3062>١٠٢ </span>- فيتمنون لأنفسهم حينئذ رجعة إلى الدنيا ليكونوا من المؤمنين حتى ينجوا.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3037,'<span data-type="title" id=toc-3063>١٠٣ </span>- إن فيما ذكر الله من نبأ إبراهيم لعظة وعبرة لمن أراد أن يتعظ ويعتبر، وما كان أكثر قومك الذين تتلو عليهم هذا النبأ مذعنين لدعوتك.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3038,'<span data-type="title" id=toc-3064>١٠٤ </span>- وإن ربك لهو القادر على الانتقام من المكذبين، المتفضل بالإنعام على المحسنين.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3039,'<span data-type="title" id=toc-3065>١٠٥ </span>- وذكر الله نبأ نوح فى قوله: كذبت قوم نوح رسالته، وردّوها عليه، وبهذا كانوا مكذبين لجميع رسل الله، لاتحاد دعوتهم فى أصولها وغايتها.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3040,'<span data-type="title" id=toc-3066>١٠٦ </span>- كذبوا هذه الرسالة حين قال لهم أخوهم نوح - نسباً لا ديناً - محذراً: ألا تتقون الله فتتركوا عبادة غيره.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3041,'<span data-type="title" id=toc-3067>١٠٧ </span>- إنى رسول الله إليكم لأهديكم إلى طريق الرشاد، أمين على تبليغ هذه الرسالة.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3042,'<span data-type="title" id=toc-3068>١٠٨ </span>- فخافوا الله وامتثلوا أمرى فيما أدعوكم إليه من توحيد الله وطاعته.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3043,'<span data-type="title" id=toc-3069>١٠٩ </span>- وما أطلب منكم أى أجر على ما أبذله لكم من النصح والدعاء، ما جزائى إلا على خالق العالمين ومالك أمرهم.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3044,'<span data-type="title" id=toc-3070>١١٠ </span>- فاحذروا عقاب الله، وامتثلوا ما آمركم به.',NULL,'551',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3045,'<span data-type="title" id=toc-3071>١١١ </span>- قال قوم نوح - يردُّون دعوته -: لن يكون منا إيمان لك فى حال اتباع سفْلة الناس وأقلهم جاهاً ومالا لك.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3046,'<span data-type="title" id=toc-3072>١١٢ </span>- قال نوح: أى شئ أعلمنى ما هم عليه من قلة الجاه والمال؟ إنما أطلب منهم الإيمان دون تعرض لمعرفة صناعاتهم وأعمالهم.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3047,'<span data-type="title" id=toc-3073>١١٣ </span>- ما جزاؤهم على أعمالهم إلا على ربى، فهو المطلع على بواطنهم، لو كنتم من أهل الشعور لعلمتم ذلك.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3048,'<span data-type="title" id=toc-3074>١١٤ </span>- وما أنا بطارد الذين يؤمنون بدعوتى مهما كان حالهم من فقر أو غنى، تلبية لرغبتكم كى تؤمنوا بى.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3049,'<span data-type="title" id=toc-3075>١١٥ </span>- ما أنا إلا رسول من الله لإنذار المكلفين إنذاراً واضحاً بالبرهان الذى يتميز به الحق من الباطل، لا فرق بين شريف وضعيف، فكيف يليق بى طرد المؤمنين لفقرهم؟!',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3050,'<span data-type="title" id=toc-3076>١١٦ </span>- قالوا: لئن لم ترجع يا نوح عن دعوتك لَنرجُمنَّك بالحجارة. يقصدون بهذا القول تهديده بالقتل.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3051,'<span data-type="title" id=toc-3077>١١٧ </span>- قال نوح مظهراً استمرار قومه على التكذيب بندائه: {رب إن قومى كذبون} . ليبرر دعاءه عليهم.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3052,'<span data-type="title" id=toc-3078>١١٨ </span>- فاحكم بينى وبينهم حكماً تهلك به من جحد توحيدك، وكذَّب رسولك، ونجِّنى ومن معى من المؤمنين من عذاب بغيهم.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3053,'<span data-type="title" id=toc-3079>١١٩ </span>- فأنجيناه ومن آمن معه فى السفينة المملوءة بهم، وبما يحتاجون إليه، استجابة لدعوته.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3054,'<span data-type="title" id=toc-3080>١٢٠ </span>- ثم أغرق الله - بعد إنجاء نوح ومن آمن به - الباقين الذين لم يؤمنوا من قومه.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3055,'<span data-type="title" id=toc-3081>١٢١ </span>- إن فيما ذكره القرآن من نبأ نوح لحجة على صدق الرسل وقدرة الله، وما كان أكثر الذين تتلو عليهم هذا القصص مؤمنين.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3056,'<span data-type="title" id=toc-3082>١٢٢ </span>- وإن ربك لهو القوى فى الانتقام من كل جبار عنيد. المُنْعم بأنواع الفضل على المتقين.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3057,'<span data-type="title" id=toc-3083>١٢٣ </span>- كذبت قبيلة عاد رسولهم هوداً - عليه السلام - وبهذا كانوا مكذبين لجميع الرسل لاتحاد دعوتهم فى أصولها وغايتها.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3058,'<span data-type="title" id=toc-3084>١٢٤ </span>- إذ قال لهم أخوهم هود: ألا تخشون الله فتخلصوا له العبادة؟! .',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3059,'<span data-type="title" id=toc-3085>١٢٥ </span>- إنى مرسل من الله لهدايتكم إلى الرشاد، حفيظ على رسالة الله، أبلغها إليكم كما أمرنى ربى.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3060,'<span data-type="title" id=toc-3086>١٢٦ </span>- فامتثلوا أمر الله، وخافوا عقوبته، وأطيعوا ما آمركم به من عند الله.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3061,'<span data-type="title" id=toc-3087>١٢٧ </span>- وما أطلب منكم على نصحى وإرشادى أى نوع من أنواع الأجر. ما جزائى إلا على خالق العالمين.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3062,'<span data-type="title" id=toc-3088>١٢٨ </span>- أتُشَيِّدون بكل مكان مرتفع من الأرض بناء شامخاً تتفاخرون به، وتجتمعون فيه لتعيثوا وتفسدوا؟ يريد سبحانه تنبيههم إلى ما ينفعهم، وتوبيخهم على ترك الإيمان وعمل الصالحات.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3063,'<span data-type="title" id=toc-3089>١٢٩ </span>- وتتخذون قصوراً مشيدة منيعة، وحياضاً للماء مؤملين الخلود فى هذه الدنيا كأنكم لا تموتون.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3064,'<span data-type="title" id=toc-3090>١٣٠ </span>- وإذا أخذتم أخذ العقوبة أسرفتم فى البغى جبارين، تقتلون وتضربون غاضبين بلا رأفة.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3065,'<span data-type="title" id=toc-3091>١٣١ </span>- فخافوا الله فى البطش، وامتثلوا أمرى فيما أدعوكم إليه، فإنه أنفع لكم وأبقى.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3066,'<span data-type="title" id=toc-3092>١٣٢ </span>- واحذروا غضب الله الذى بسط إليكم يد إنعامه بالذى تعلمونه بين أيديكم من ألوان عطائه.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3067,'<span data-type="title" id=toc-3093>١٣٣ </span>- عدَّد ما أمدهم به من إبل وبقر وغنم، وبنين أقوياء، ليحفظوا لهم الأنعام، ويعينوهم على تكاليف الحياة.',NULL,'553',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3068,'<span data-type="title" id=toc-3094>١٣٤ </span>- وبساتين مثمرات، وعيون تجرى بالماء الذى تحتاجون إليه.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3069,'<span data-type="title" id=toc-3095>١٣٥ </span>- إنى أخاف أن يُنزل الله بكم عذاباً شديداً فى الدنيا، ويُدخلكم فى الآخرة نار جهنم، بسبب طغيانكم وإنعام الله عليكم.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3070,'<span data-type="title" id=toc-3096>١٣٦ </span>- قالوا - استخفافاً به -: سواء لدينا بالغت فى وعظنا وإنذارنا أم لم تكن من الواعظين.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3071,'<span data-type="title" id=toc-3097>١٣٧ </span>- ما هذا الذى جئتنا به إلا كذب الأولين وأباطيلهم، اعتادوا تلفيق مثله، فلا نرجع عما نحن فيه.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3072,'<span data-type="title" id=toc-3098>١٣٨ </span>- وما نحن بمعذبين على ما يصدر منا من عمل.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3073,'<span data-type="title" id=toc-3099>١٣٩ </span>- فاستمروا على تكذيبه، فعاجلهم الله بالهلاك، إن فى ذلك الذى أنزله الله بعاد جزاء تكذيبهم لحجة تدل على كمال قدرة الله، وما كان أكثر الذين تتلوا عليهم نبأ عاد مؤمنين.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3074,'<span data-type="title" id=toc-3100>١٤٠ </span>- وإن ربك لهو القاهر للجبارين، الرحيم بالمؤمنين.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3075,'<span data-type="title" id=toc-3101>١٤١ </span>- كذبت قبيلة ثمود صالحاً فى رسالته ودعوته لهم إلى توحيد الله، وبهذا كذبوا جميع المرسلين، لاتحاد رسالاتهم فى أصولها.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3076,'<span data-type="title" id=toc-3102>١٤٢ </span>- اذكر لقومك - أيها الرسول - وقت أن قال لثمود أخوهم صالح فى النسب والوطن: ألا تخشون الله فتفردوه بالعبادة؟!',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3077,'<span data-type="title" id=toc-3103>١٤٣ </span>- إنى مرسل من الله إليكم بما فيه خيركم وسعادتكم، حفيظ على هذه الرسالة كما تلقيتها عن الله.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3078,'<span data-type="title" id=toc-3104>١٤٤ </span>- فاحذروا عقوبة الله، وامتثلوا ما أدعوكم إليه من أوامره.',NULL,'554',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3079,'<span data-type="title" id=toc-3105>١٤٥ </span>- وما أطلب منكم أى أجر على نصحى لكم وإرشادى، ما أجرى إلا على مالك العالمين.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3080,'<span data-type="title" id=toc-3106>١٤٦ </span>- أنكر عليهم اعتقادهم البقاء فيما هم فيه من النعيم، آمنين من العذاب والزوال والموت.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3081,'<span data-type="title" id=toc-3107>١٤٧ </span>- فى حدائق مثمرات، وعيون تجرى بالماء الفرات.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3082,'<span data-type="title" id=toc-3108>١٤٨ </span>- وزروع يانعات، ونخل ثمرها الذى يظهر منها لين نضيج.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3083,'<span data-type="title" id=toc-3109>١٤٩ </span>- وتتخذون من الجبال بيوتاً عاليات. حاذقين نشطين فيما تصنعون.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3084,'<span data-type="title" id=toc-3110>١٥٠ </span>- فخافوا عقوبة الله لعدم شكركم له على نعمه، واقبلوا نصحى واعملوا به.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3085,'<span data-type="title" id=toc-3111>١٥١ </span>- ولا تطيعوا أمر الذين أسرفوا على أنفسهم بالشرك واتباع الهوى والشهوات.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3086,'<span data-type="title" id=toc-3112>١٥٢ </span>- الذين يعيثون فى أرض الله فساداً، ولا يقومون فيها بإصلاح به تسعد البلاد.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3087,'<span data-type="title" id=toc-3113>١٥٣ </span>- قالوا ما أنت إلا من الذين سُحِروا سحراً شديداً حتى غلب على عقولهم. وفى هذا الرد عنف وسفاهة.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3088,'<span data-type="title" id=toc-3114>١٥٤ </span>- ما أنت إلا فرد مماثل لنا فى البشرية، فكيف تتميز علينا بالنبوة والرسالة؟! فإن كنت صادقاً فى دعواك فأت بمعجزة تدل على ثبوت رسالتك.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3089,'<span data-type="title" id=toc-3115>١٥٥ </span>- قال لهم صالح - حينما أعطاه الله الناقة معجزة له -: هذه ناقة الله أخرجَها لكم آية، لها نصيب من الماء فى يوم فلا تشربوا فيه، ولكم نصيب منه فى يوم آخر فلا تشرب فيه.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3090,'<span data-type="title" id=toc-3116>١٥٦ </span>- ولا تلحقوا بها أذى، فيهلككم عذاب عظيم.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3091,'<span data-type="title" id=toc-3117>١٥٧ </span>- فذبحوا الناقة مخالفين ما اتفقوا عليه مع صالح، فحق عليهم العذاب، فأصبحوا على ما فعلوا نادمين.',NULL,'555',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3092,'<span data-type="title" id=toc-3118>١٥٨ </span>- فأهلكهم عذاب الله الذى توعدهم به صالح، ولم يدفع الندم عنهم عقابَ جُرمهم. إن فى ذكر قصتهم لدلالة على قدرة الله على إهلاك الكافرين وإنجاء المؤمنين، وما كان أكثر قومك مؤمنين.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3093,'<span data-type="title" id=toc-3119>١٥٩ </span>- وإن خالقك لهو القادر على إهلاك الجاحدين المتفضل بإنجاء المتقين.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3094,'<span data-type="title" id=toc-3120>١٦٠ </span>- كذبت قوم لوط - حين دعاهم إلى توحيد الله وترك الشرك - جميع المرسلين.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3095,'<span data-type="title" id=toc-3121>١٦١ </span>- اذكر لقومك - أيها الرسول - إذ قال لوط لقومه - وهو أخوهم وصهرهم -: ألا تخافون عذاب الله؟!',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3096,'<span data-type="title" id=toc-3122>١٦٢ </span>- إنى مُرسَل لكم من الله بالدين الحق، أمين على تبليغ هذا الدين.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3097,'<span data-type="title" id=toc-3123>١٦٣ </span>- فاحذروا عذاب الله، وامتثلوا أمرى فيما أدعوكم إليه.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3098,'<span data-type="title" id=toc-3124>١٦٤ </span>- وما أطلب منكم أجراً على ما أدعوكم إليه من الهدى والرشاد، ما جزائى إلا على مالك العالمين ومربيهم.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3099,'<span data-type="title" id=toc-3125>١٦٥ </span>- قال لوط: أتستمتعون بوطء الذكور دون الإناث؟ يريد بذلك أن ينكر ما دأبوا عليه من ارتكاب هذه الفاحشة النكراء.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3100,'<span data-type="title" id=toc-3126>١٦٦ </span>- وتتركون ما خلقه الله لمتاعكم من أزواجكم الحلائل، بل أنتم قوم متجاوزون الحد فى الظلم بارتكاب جميع المعاصى.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3101,'<span data-type="title" id=toc-3127>١٦٧ </span>- قالوا - غاضبين لإنكاره وتشنيعه عليهم بسبب تلك الرذيلة -: لئن لم تترك توبيخنا لتكونن من المنفيين من بلادنا على أسوإ حال.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3102,'<span data-type="title" id=toc-3128>١٦٨ </span>- قال لوط: إنى لعملكم هذا من المبغضين، فلا أترك إنكاره والتشنيع عليه.',NULL,'556',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3103,'<span data-type="title" id=toc-3129>١٦٩ </span>- ونادى ربه: أن ينقذه وأهله مما يعمل هؤلاء الجاهلون حينما يئس من استجابتهم له.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3104,'<span data-type="title" id=toc-3130>١٧٠ </span>- فاستجاب الله دعاءه، ونجَّاه ومن اتبع دعوته بإخراجهم جميعاً من بيوتهم وقت نزول العذاب بالمكذبين.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3105,'<span data-type="title" id=toc-3131>١٧١ </span>- إلا امرأته العجوز بقيت ولم تخرج معه فهلكت لكفرها وخيانتها بموالاتها للفاسقين.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3106,'<span data-type="title" id=toc-3132>١٧٢ </span>- ثم أهلك الله الكفرة الفجرة أشد إهلاك وأفظعه.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3107,'<span data-type="title" id=toc-3133>١٧٣ </span>- وأنزل الله على شُذَّاذ القوم حجارة من السماء فأهلكتهم، وكان مطراً هائلا فى كثرته ونوعه، فساء مطر المنذرين مطرهم. إذ نزل بأشد أنواع الهلاك.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3108,'<span data-type="title" id=toc-3134>١٧٤ </span>- إن فى ذلك العقاب الذى نزل بالقوم لحجة تدل على تمام قدرة الله، وما كان أكثر قومك مصدقين بدعوتك.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3109,'<span data-type="title" id=toc-3135>١٧٥ </span>- وإن ربك لهو الغالب على كل شئ. المتصف بالرحمة الكاملة فيعاقب المذنبين، ويثيب المؤمنين.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3110,'<span data-type="title" id=toc-3136>١٧٦ </span>- هذه قصة شعيب مع أصحاب الأيكة - وهى غَيْضة تنبت ناعم الشجر بقرب مَدْيَن - نزل بها جماعة من الناس وأقاموا بها، فبعث الله إليهم شعيباً كما بعث إلى مدين، فكذبوه فى دعوته، وبهذا كانوا منكرين لجميع الرسالات.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3111,'<span data-type="title" id=toc-3137>١٧٧ </span>- اذكر - يا محمد - لقومك وقت قول شعيب لأصحاب الأيكة: ألا تخافون الله فتؤمنوا به؟! فبادروا بتكذيبه.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3112,'<span data-type="title" id=toc-3138>١٧٨ </span>- إنى لهدايتكم وإرشادكم مرسل من رب العالمين، أمين على توصيل رسالته إليكم.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3113,'<span data-type="title" id=toc-3139>١٧٩ </span>- فاحذروا عقوبة الله، وأطيعونى باتباع أوامر الله وتخليص أنفسكم من الآثام.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3114,'<span data-type="title" id=toc-3140>١٨٠ </span>- وما أطلب منكم على إرشادى وتعليمى أى أجر، ما جزائى الكامل فى مقابل عملى إلا على رب العالمين.',NULL,'557',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3115,'<span data-type="title" id=toc-3141>١٨١ </span>- أمرهم شعيب بإعطاء الكيل وافياً حيث كان يشيع بينهم بخس الكيل والميزان، ونقص حقوق الناس بالتطفيف والخسران.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3116,'<span data-type="title" id=toc-3142>١٨٢ </span>- وزِنُوا بين الناس بالميزان السوى حتى يأخذوا حقهم بالعدل المستقيم.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3117,'<span data-type="title" id=toc-3143>١٨٣ </span>- ولا تنقصوا الناس شيئاً من حقوقهم، ولا تعثوا فى الأرض مفسدين، بالقتل وقطع الطريق وارتكاب الموبقات وإطاعة الهوى.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3118,'<span data-type="title" id=toc-3144>١٨٤ </span>- واحذروا عقوبة الله الذى خلقكم، وخلق الأمم القوية العاتية المتقدمة.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3119,'<span data-type="title" id=toc-3145>١٨٥ </span>- قالوا: ما أنت إلا واحد من الذين أصابهم السحر إصابة شديدة، فذهب بعقولهم.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3120,'<span data-type="title" id=toc-3146>١٨٦ </span>- وما أنت إلا واحد منا مُساوٍ لنا فى البشرية، فكيف تتميز علينا بالرسالة؟! ونحن نعتقد أنك من الراسخين فى الكذب.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3121,'<span data-type="title" id=toc-3147>١٨٧ </span>- فأسقط علينا قطع عذاب من السماء إن كنت من الصادقين فى الرسالة. وهذا اقتراح تحته كل ألوان الإنكار.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3122,'<span data-type="title" id=toc-3148>١٨٨ </span>- قال شعيب: ربى بالغ العلم بما تعملونه من المعاصى، وبما تستحقونه من العذاب ينزله عليكم فى وقته المقدر له. وهذا منه منتهى التفويض لله وغايته التهديد لهم.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3123,'<span data-type="title" id=toc-3149>١٨٩ </span>- فاستَمرُّوا على تكذيبه، فسلَّط الله عليهم الحر الشديد، فكانوا يفرون منه إلى غير حمى، إلى أن أظلتهم سحابة من الشمس فاجتمعوا تحتها، فأسقطها الله عليهم ناراً فأهلكتهم جميعاً فى يوم شديد الهول.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3124,'<span data-type="title" id=toc-3150>١٩٠ </span>- إن فيما نزل بأصحاب الأيكة من العقوبة - جزاء تمردهم - لدليل على كمال قدرة الله، وما كان أكثر قومك مصدقين.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3125,'<span data-type="title" id=toc-3151>١٩١ </span>- وإن ربك لهو المتفرد بالقوة والغلبة المنعم بالرحمات على المؤمنين.',NULL,'558',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3126,'<span data-type="title" id=toc-3152>١٩٢ </span>- وإن هذا القرآن - الذى ذكرت فيه هذه القصص الصادقة - مُنَزَّلٌ من خالق العالمين ومالك أمرهم ومربيهم، فخبره صادق، وحكمه نافذ إلى يوم القيامة.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3127,'<span data-type="title" id=toc-3153>١٩٣ </span>- نزل به الروح الأمين، جبريل - عليه السلام -.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3128,'<span data-type="title" id=toc-3154>١٩٤ </span>- على قلبك متمكناً من حفظه وفهمه، مستقراً فى قلبك استقراراً لا ينسى، لتنذرهم بما تضمنه من العقوبات للمخالفين.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3129,'<span data-type="title" id=toc-3155>١٩٥ </span>- نزل به جبريل - عليه السلام - عليك بلغة عربية، واضحة المعنى، ظاهرة الدلالة فيما يحتاجون إليه فى إصلاح شئون دينهم ودنياهم.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3130,'<span data-type="title" id=toc-3156>١٩٦ </span>- وإن ذكر القرآن والإخبار عنه بأنه من عند الله نزل على محمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - لثابت فى كتب الأنبياء السابقين.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3131,'<span data-type="title" id=toc-3157>١٩٧ </span>- أكفر هؤلاء المعاندون بالقرآن وعندهم حجة تدل على صدق محمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - وهى عِلْم علماء بنى إسرائيل بالقرآن كما جاء فى كتبهم؟!',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3132,'<span data-type="title" id=toc-3158>١٩٨ </span>- ولو نزلنا القرآن على بعض من الأعجمين يقدر على التكلم بالعربية ولا يفصح بها، فلا يتوهم اتهامه باختراعه.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3133,'<span data-type="title" id=toc-3159>١٩٩ </span>- فقرأه عليهم قراءة صحيحة خارقة للعادة لكفروا به، وانتحلوا لجحودهم عذراً.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3134,'<span data-type="title" id=toc-3160>٢٠٠ </span>- أدخلنا التكذيب فى قلوب المجرمين، وقرَّرناه فيها مثل تقريره فى قلوب من هم على صفتهم.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3135,'<span data-type="title" id=toc-3161>٢٠١ </span>- فلا سبيل إلى أن يتغيروا عما هم عليه من جحوده، حتى يعاينوا العذاب الشديد الذى وعدوا به.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3136,'<span data-type="title" id=toc-3162>٢٠٢ </span>- فينزل بهم العذاب فجأة من غير توقع وهم لا يشعرون بقدومه.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3137,'<span data-type="title" id=toc-3163>٢٠٣ </span>- فيقولون عند نزول العذاب: {هل نحن مُنْظَرون} تحسراً على ما فاتهم من الإيمان وطلباً للإمهال، ولكن لا يجابون.',NULL,'559',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3138,'<span data-type="title" id=toc-3164>٢٠٤ </span>- قال تعالى: أَغَرَّ كفار مكة إمْهالى فيستعجلون نزول العذاب؟! يريد سبحانه تسفيه عقولهم بسبب استعجالهم العذاب إثر تكرار إنذارهم وتخويفهم.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3139,'<span data-type="title" id=toc-3165>٢٠٥ </span>- أفكَّرتَ فعلمتَ أننا متعناهم بالحياة سنين طويلة مع طيب العيش؟',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3140,'<span data-type="title" id=toc-3166>٢٠٦ </span>- ثم نزل بهم العذاب الموعود.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3141,'<span data-type="title" id=toc-3167>٢٠٧ </span>- ما يدفع عنهم تمتعهم بطول العمر وطيب العيش من عذاب الله شيئاً، فعذاب الله واقع عاجلاً أو آجلاً، ولا خير فى نعيم يعقبه عذاب.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3142,'<span data-type="title" id=toc-3168>٢٠٨ </span>- وسُنّتنا فى الأمم جميعاً أننا لم ننزل هلاكاً بأمة إلا بعد أن نرسل إليها رسلا ينذرونها إلزاماً للحجة.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3143,'<span data-type="title" id=toc-3169>٢٠٩ </span>- تذكرة وعبرة، وما كان شأننا الظلم فنعذب أمة قبل أن نبعث إليها رسولا.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3144,'<span data-type="title" id=toc-3170>٢١٠ </span>- نفى القرآن ما قاله كفار مكة من أن لمحمد تابعاً من الجن، يلقى القرآن إليه فقال: وما تنزلت الشياطين بهذا القرآن.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3145,'<span data-type="title" id=toc-3171>٢١١ </span>- وما يجوز لهم أن ينزلوا به، وما يستطيعون ذلك.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3146,'<span data-type="title" id=toc-3172>٢١٢ </span>- إنهم عن سماع القرآن الذى ينزل به الوحى على محمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - لمحجوبون.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3147,'<span data-type="title" id=toc-3173>٢١٣ </span>- فتوجه إلى الله مستمراً على إخلاصك له فى العبادة، ولا تهتم بفساد زعم المشركين وسوء مسلكهم. ودعوة الرسول إلى هذا اللون من الإخلاص دعوةٌ لأفراد أمته جميعاً.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3148,'<span data-type="title" id=toc-3174>٢١٤ </span>- وخوِّف بالعذاب على الشرك والمعاصى الأقرب فالأقرب من عشيرتك.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3149,'<span data-type="title" id=toc-3175>٢١٥ </span>- وأَلِنْ جانبك لمن أجاب دعوتك بالإيمان.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3150,'<span data-type="title" id=toc-3176>٢١٦ </span>- فإن عصوك ولم يتبعوك، فتبرأ منهم ومن أعمالهم، من الشرك وسائر المعاصى.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3151,'<span data-type="title" id=toc-3177>٢١٧ </span>- وفوض أمرك إلى القوى القادر على قهر أعدائك بعزته، وعلى نصرتك ونصرة كل مخلص فى عمله برحمته.',NULL,'560',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3152,'<span data-type="title" id=toc-3178>٢١٨ </span>- الذى يراك حين تقوم إلى التهجد وأعمال الخير.',NULL,'561',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3153,'<span data-type="title" id=toc-3179>٢١٩ </span>- ويرى تصرفك فيما بين المصلين بالقيام والقعود والركوع والسجود حين تؤمهم فى الصلاة.',NULL,'561',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3154,'<span data-type="title" id=toc-3180>٢٢٠ </span>- إنه سبحانه هو السميع لدعائك وذكرك، العليم بنيتك وعملك، وكأنه سبحانه يقول له: هَوِّن على نفسك مشاق العبادة، فأنت تعمل بمرأى ومسمع منا.',NULL,'561',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3155,'<span data-type="title" id=toc-3181>٢٢١ </span>- قال المشركون: إن الشياطين تلقى السمع على محمد. فرد القرآن عليهم: هل أخبركم على من تتنزل الشياطين وتلقى الوساوس؟!',NULL,'561',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3156,'<span data-type="title" id=toc-3182>٢٢٢ </span>- تتنزل على كل مرتكب لأقبح أنواع الكذب وأشنع الآثام، وهم الكهنة الفجرة الذين بين طباعهم وطباع الشياطين تجانس ووفاق.',NULL,'561',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3157,'<span data-type="title" id=toc-3183>٢٢٣ </span>- يلقون أسماعهم إلى الشياطين، فيتلقون منهم ظنوناً، وأكثرهم كاذبون، حيث يزيدون فى القول على ما تلقيه الشياطين.',NULL,'561',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3158,'<span data-type="title" id=toc-3184>٢٢٤ </span>- قال الكفار: إن القرآن شعر، ومحمد شاعر. فأبطل الله هذا بإثبات أن القرآن ملئ بالحكم والأحكام، فأسلوبه ينافى أسلوب الشعر الذى يقوم على الباطل والكذب، وبين أن حال محمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - ينافى حال الشعراء، فهو ينطق بالحكمة، وهم ينطقون بالزور، وهذا حال أغلب الشعراء.',NULL,'561',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3159,'<span data-type="title" id=toc-3185>٢٢٥ </span>- ألم تر أنهم فى كل واد من أودية القول يهيمون على وجوههم، فلا يهتدون إلى الحق؟',NULL,'561',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3160,'<span data-type="title" id=toc-3186>٢٢٦ </span>- وأنهم يقولون بألسنتهم ما لا يلتزمونه فى عملهم.',NULL,'561',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3161,'<span data-type="title" id=toc-3187>٢٢٧ </span>- لكن الذين اهتدوا بهدى الله وعملوا الصالحات حتى تمكنت فيهم ملكات فاضلة، وذكروا الله كثيراً حتى تمكنت خشيته من قلوبهم، هؤلاء يجعلون الشعر كالدواء يصيب الداء، وينتصرون لدينهم وإقامة الحق إذا جير على الحق، وسيعلم الذين ظلموا أنفسهم بالشرك وهجاء الرسول أى مرجع من مراجع الشر والهلاك يرجعون إليه.',NULL,'561',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3162,'<span data-type="title" id=toc-3189>١ </span>- طس - حرفان صوتيان ابتدأت بهما السورة الكريمة تنبيها إلى سر الإعجاز فى القرآن مع الإشارة إلى أنه من جنس ما يتكلمون، ولتنبيه الأذهان للاستماع إليه.
INSERT INTO [b836] VALUES (3163,'<span data-type="title" id=toc-3190>٢ </span>- وهو هادٍ للمؤمنين إلى طريق الخير والفوز فى الدنيا والآخرة، ومبشر لهم بحسن المآل.',NULL,'562',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3164,'<span data-type="title" id=toc-3191>٣ </span>- الذين يؤدون الصلاة فى خشوع مستوفية الأركان، ويعطون الزكاة فى أوقاتها، وهم يوقنون بالحياة الآخرة، وما يكون فيها من ثواب وعقاب.',NULL,'563',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3165,'<span data-type="title" id=toc-3192>٤ </span>- إن الذين لا يؤمنون باليوم الآخر زيَّنا لهم أعمالهم بخلق الشهوة فيهم، فهم يتردون فى ضلالهم.',NULL,'563',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3166,'<span data-type="title" id=toc-3193>٥ </span>- أولئك الذين لهم العذاب السيئ، وهم فى الآخرة أشد الناس خسرانا.',NULL,'563',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3167,'<span data-type="title" id=toc-3194>٦ </span>- وإنك - أيها النبى - لتتلقى القرآن الذى ينزل عليك من لدن من لا يدانى فى حكمته، وقد أحاط بكل شئ علما.',NULL,'563',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3168,'<span data-type="title" id=toc-3195>٧ </span>- اذكر حين قال موسى لزوجته ومن معه وهو عائد إلى مصر: إنِّى أبصرت نارا، سآتيكم منها بخبر عن الطريق، أو آتيكم بشعلة مضيئة نارا مقبوسة، لعلكم تستدفئون بها من البرد.',NULL,'563',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3169,'<span data-type="title" id=toc-3196>٨ </span>- فلما وصل إليها نودى: أن بُورك من فى مكان النار ومن حولها. وهم الملائكة وموسى. ونزه الله رب العالمين عن كل ما لا يليق به.',NULL,'563',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3170,'<span data-type="title" id=toc-3197>٩ </span>- يا موسى إنى أنا الله المستحق للعبادة - وحده - الغالب على كل شئ، الذى يضع كل أمر فى موضعه.',NULL,'563',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3171,'<span data-type="title" id=toc-3198>١٠ </span>- وفى سبيل أن تؤدى دعوتك ألْق عصاك. فلما ألقاها ورآها تهتز كأنها حية خفيفة سريعة أعرض عنها راجعا إلى الوراء، ولم يعد إليها بعد أن أدبر عنها، فطمْأنه الله تعالى بقوله: لا تخف إنى لا يخاف عندى المرسلون حين أخاطبهم.',NULL,'563',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3172,'<span data-type="title" id=toc-3199>١١ </span>- لكن من عمل شيئا غير مأذون له فيه، ثم بدَّل حسنا بعد هفوة فإنى كثير المغفرة عظيم الرحمة.',NULL,'563',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3173,'<span data-type="title" id=toc-3200>١٢ </span>- وأدخل يدك فى فتحة ثوبك تخرج بيضاء من غير برص، فى جملة تسع معجزات، مرسلا إلى فرعون وقومه، إنهم كانوا قوما خارجين عن أمر اللَّه كافرين.',NULL,'564',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3174,'<span data-type="title" id=toc-3201>١٣ </span>- فلما جاءت هذه المعجزات واضحة ظاهرة قالوا: هذا سحر واضح بيِّن.',NULL,'564',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3175,'<span data-type="title" id=toc-3202>١٤ </span>- وكذبوا بها منكرين لدلالتها على صدق الرسالة، وقد وقع اليقين فى قلوبهم، ولكنهم لم يذعنوا لاستعلائهم بالباطل وطغيانهم، فانظر - أيها النبى - كيف كانت عاقبة الذين دأبوا على الفساد، فكفروا بالمعجزات وهى واضحة؟',NULL,'564',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3176,'<span data-type="title" id=toc-3203>١٥ </span>- هذا طغيان فرعون بسبب ملكه، فانظر إلى السلطان العادل، سلطان الحكم وسلطان النبوة فى داود وابنه سليمان - عليهما السلام - لقد آتيناهما علماً كثيراً بالشريعة ودراية بالأحكام، فأقاما العدل وحمدا الله الذى منحهما فضلاً على كثير من عباده الصادقين المذعنين للحق.',NULL,'564',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3177,'<span data-type="title" id=toc-3204>١٦ </span>- وقد آل الملك والحكم من داود إلى سليمان ابنه، وقال: يا أيها الناس عُلِّمنا لغة الطير، وأوتينا كثيرا مما نحتاج إليه فى سلطاننا: إن هذه النعم لهى الفضل الواضح الذى خصنا الله به.',NULL,'564',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3178,'<span data-type="title" id=toc-3205>١٧ </span>- وجمع لسليمان جنوده من الجن والإنس والطير فى صعيد واحد، فهم بحبس أولهم على آخرهم حتى يكونوا جيشاً منظماً خاضعاً.',NULL,'564',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3179,'<span data-type="title" id=toc-3206>١٨ </span>- حتى إذا بلغوا وادى النمل قالت نملة: يا أيها النمل ادخلوا مخابئكم، لكيلا تميتكم جنود سليمان وهم لا يحسون بوجودكم.',NULL,'564',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3180,'<span data-type="title" id=toc-3207>١٩ </span>- فتبسَّم سليمان ضاحكاً من قول هذه النملة الحريصة على مصالحها، وأحس بنعمة الله تعالى عليه وقال: يا خالقى ألهمنى أن أشكر نعمتك التى أنعمت بها علىَّ وعلى والدىَّ، ووفقنى لأن أعمل الأعمال الصالحة التى ترضاها، وأدخلنى برحمتك السابغة فى عبادك الذين ترتضى أعمالهم.',NULL,'565',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3181,'<span data-type="title" id=toc-3208>٢٠ </span>- وتَعَرَّفَ جنوده من الطير فلم يجدوا الهدهد، فتعجب وقال: مالى لا أرى الهدهد؟ أهو بيننا ولم يقع عليه نظرى، أم هو غائب عنا ليس بيننا؟!',NULL,'565',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3182,'<span data-type="title" id=toc-3209>٢١ </span>- والله لأنزلن به عذاباً شديداً يردعه، أو لأذبحنه إن كان الذنب عظيماً، إلا أن يأتينى بحجة بيِّنة تُبرر غيابه عنى.',NULL,'565',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3183,'<span data-type="title" id=toc-3210>٢٢ </span>- وكان الهدهد قد مكث فى مكان غير بعيد زماناً غير مديد، ثم جاء إلى سليمان يقول له: قد أحطت علماً بما لم يكن عندك علم به، وجئتك من سبأ بخبر ذى شأن عظيم وهو مستيقن به.',NULL,'565',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3184,'<span data-type="title" id=toc-3211>٢٣ </span>- إنى وجدت فى أهل سبأ امرأة تحكمهم، وأوتيت من كل شئ من أسباب الدنيا، ولها سرير كبير يدل على عظمة ملكها وقوة سلطانها.',NULL,'565',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3185,'<span data-type="title" id=toc-3212>٢٤ </span>- وجدتها هى وقومها يعبدون الشمس ولا يعبدون الله، وحسَّن لهم الشيطان أعمالهم فظنوها حسنة وهى السوء، فصرفهم بذلك عن سبيل الحق، فهم لا يهتدون.',NULL,'565',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3186,'<span data-type="title" id=toc-3213>٢٥ </span>- ألا يسجدوا لله تعالى، وهو الذى يخرج المخبوء فى السموات والأرض، ويعلم ما تسرون وما تظهرون؟ {',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3187,'<span data-type="title" id=toc-3214>٢٦ </span>- الله لا معبود بحق سواه، صاحب السلطان المطلق العظيم على كل ما فى الوجود.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3188,'<span data-type="title" id=toc-3215>٢٧ </span>- قال سليمان مخاطباً الهدهد: سنتحرى خبرك هذا، أصدقت فيه أم كنت من الكاذبين؟',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3189,'<span data-type="title" id=toc-3216>٢٨ </span>- اذهب بكتابى هذا فأوصله إليها وإلى قومها ثم تنح عنهم متوارياً فى مكان قريب، لتنظر فيما يرجع به بعضهم إلى بعض ويرددونه من قول.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3190,'<span data-type="title" id=toc-3217>٢٩ </span>- وصل الكتاب إليها فجمعت أشراف قومها، وذوى شوراها، وقالت: يا أيها الملأ إنى قد وصل إلىَّ كتاب عظيم الشأن.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3191,'<span data-type="title" id=toc-3218>٣٠ </span>- ثم تلت الكتاب عليهم قائلة: إنه من سليمان وإنه مفتتح باسم الله ذى الجلال والإنعام الذى يفيض برحمته دائماً على خلقه.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3192,'<span data-type="title" id=toc-3219>٣١ </span>- لا تتكبروا علىَّ وأتونى منقادين خاضعين.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3193,'<span data-type="title" id=toc-3220>٣٢ </span>- قالت لمجلس شوراها: بينوا لى الصواب فى هذا الأمر الخطير الذى عرض لى، فإنى لا أبت فى أمر حتى يكون بمحضركم.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3194,'<span data-type="title" id=toc-3221>٣٣ </span>- قالوا مطمئنين لها: نحن أصحاب قوة بدنية وأهل نجدة وشجاعة، لا نخاف الحرب، فانظرى فى الأمر الذى تأمريننا به، فإنا مطيعون.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3195,'<span data-type="title" id=toc-3222>٣٤ </span>- قالت متريثة مسالمة: إن الملوك إذا دخلوا مدينة عظيمة بجيوشهم أفسدوها، فأذهبوا عمرانها، وأبادوا الحرث والنسل، وأفعالهم كذلك دائماً.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3196,'<span data-type="title" id=toc-3223>٣٥ </span>- وإنى - إيثاراً للسلم والعافية - مرسلة إلى سليمان وقومه بهدية، ومنتظرة ما يرجع به الرسل، بقبول الهدية أم بردها.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3197,'<span data-type="title" id=toc-3224>٣٦ </span>- وصل الرسل إلى سيدنا سليمان بالهدية، فقال لهم شاعراً بأنعم الله تعالى عليه، مخاطباً لها ولقومها فى مواجهة رسلها: أتعطوننى مالا؟} فما أعطانى الله من النبوة والملك والنعمة أعظم مما آتاكم، بل أنتم بهديتكم وكثرة أموالكم تفرحون لا مثلى، لأنكم لا تعلمون إلا ما يتعلق بالدنيا.',NULL,'566',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3198,'<span data-type="title" id=toc-3225>٣٧ </span>- وقال يخاطب المتكلم باسمهم: ارجع - أيها الرسول - إليهم، فوالله لنأتينّهم بجنود لا طاقة لهم بمقاومتها ومقابلتها، ولنخرجنهم من سبأ فاقدى العز، وهم مستعبدون.',NULL,'567',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3199,'<span data-type="title" id=toc-3226>٣٨ </span>- اتجه سليمان إلى الاستعانة بمن سخرهم الله له من الإنس والجن، ليفاجئها بأمر غريب، فقال: أيكم يأتينى بعرشها العظيم قبل أن يأتونى خاضعين منقادين؟',NULL,'567',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3200,'<span data-type="title" id=toc-3227>٣٩ </span>- قال مارد من الجن: أنا آتيك به وأنت فى مجلسك هذا قبل أن تقوم منه، وإنى لقادر أمين فى قولى وفعلى.',NULL,'567',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3201,'<span data-type="title" id=toc-3228>٤٠ </span>- قال الذى آتاه الله قوة روحية وعلماً من الكتاب: أنا آتيك بهذا العرش قبل أن تحرك أجفانك. وقد نفذ ما قال. فلما رأى سليمان العرش ثابتاً عنده غير مضطرب قال: هذا من فضل الله الذى خلقنى وأمدنى بخيره ليختبرنى أأشكر هذه النعمة أم لا أؤدى حقها؟ ومن شكر الله فإنما يحط عن نفسه عبء الواجب، ومن يترك الشكر على النعمة فإن ربى غنى عن الشكر، كريم بالإنعام.',NULL,'567',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3202,'<span data-type="title" id=toc-3229>٤١ </span>- قال سليمان لحاشيته: اخفوا عنها العرش ببعض التغيير فى مظاهره لنرى أتعرفه مهتدية إليه أم لا تعرفه فلا تهتدى إليه؟',NULL,'567',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3203,'<span data-type="title" id=toc-3230>٤٢ </span>- فلما أقبلت وجهت نظرها إلى عرشها، فقيل لها: أهذا مثل عرشك؟ فقالت: - لكمال التشابه - كأنه هو. وقال سليمان ومن معه: أوتينا العلم بالله وبقدرته وبصحة ما جاء من عنده مثل علمها وكنا قوماً منقادين لله مخلصين العبادة له.',NULL,'567',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3204,'<span data-type="title" id=toc-3231>٤٣ </span>- وصرفها عن عبادة الله ما كانت تعبده من آلهة غير الله تعالى من شمس ونحوها، إنها كانت من قوم كافرين.',NULL,'567',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3205,'<span data-type="title" id=toc-3232>٤٤ </span>- قيل لها من بعد ذلك: ادخلى قصر سليمان، وكان صحنه من زجاج تحته ماء يسبح فيه السمك، فكشفت عن ساقيها تحسب ما تمر فيه ماء، فنبهها سليمان إلى أن الصحن أملس مكون من زجاج، فراعها ذلك المنظر المادى، وعلمت أن ملكها لا يساوى شيئاً بجوار ملك سليمان - النبى - فقالت: رب إنى ظلمت نفسى باغترارى بملكى وكفرى، وأذعنت فى صحبة سليمان مؤمنة بالله تعالى خالق العالمين ومربيهم والقائم عليهم.',NULL,'568',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3206,'<span data-type="title" id=toc-3233>٤٥ </span>- ولقد بعثنا إلى ثمود أخاهم صالحاً بأن وحِّدوا الله، فسارعوا إلى الاختصام والاختلاف، وصاروا فريقين: أحدهما مؤمن والآخر كافر.',NULL,'568',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3207,'<span data-type="title" id=toc-3234>٤٦ </span>- قال صالح ناصحاً لهم: يا قوم لم تستعجلون بالعذاب الذى توعدون قبل التوبة، هلا تطلبون المغفرة من ربكم وتؤمنون به رجاء أن ترحموا؟!',NULL,'568',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3208,'<span data-type="title" id=toc-3235>٤٧ </span>- وقالوا: تشاءمنا بك أنت ومن معك وأصابنا القحط، قال: أسباب الخير والشر الذى نزل بكم إنما كان من عند الله. بل أنتم قوم تختبرون بالسراء والضراء، لعلكم تؤمنون.',NULL,'568',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3209,'<span data-type="title" id=toc-3236>٤٨ </span>- وكان زعماء الشر فيهم تسعة، يفسدون بآرائهم ودعايتهم فى الأرض، وليس من شأنهم عمل الصالح.',NULL,'568',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3210,'<span data-type="title" id=toc-3237>٤٩ </span>- قال أولئك المشركون بعضهم لبعض: تبادلوا القسم بالله لنغيرن عليه هو وأهله ونقتلهم، ثم نقول لولى دمه: ما شهدنا هلاكه ولاهلاك أهله، وإنا لصادقون فيما ذكرنا.',NULL,'568',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3211,'<span data-type="title" id=toc-3238>٥٠ </span>- دبَّروا الفتك بصالح وأهله، والله من ورائهم قد دبَّر النجاة لنبيه وأهله والهلاك لهم وهم لا يشعرون بتدبير الله.',NULL,'568',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3212,'<span data-type="title" id=toc-3239>٥١ </span>- فانظر - أيها النبى - إلى عاقبة تدبيرهم وتدبيرنا لنبينا أنا أهلكناهم وقومهم أجمعين.',NULL,'568',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3213,'<span data-type="title" id=toc-3240>٥٢ </span>- فانظر إلى آثارهم تجد بيوتهم ساقطة متهدمة بسبب ظلمهم وكفرهم وإرادتهم الشر لنبيهم. إن فيما فُعِل بثمود لآية لقوم يعلمون قدرتنا فيتعظون.',NULL,'569',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3214,'<span data-type="title" id=toc-3241>٥٣ </span>- وأنجينا الفريق المؤمن بصالح من هذا الهلاك وكانوا يتقون ترك أوامره.',NULL,'569',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3215,'<span data-type="title" id=toc-3242>٥٤ </span>- واذكر - أيها النبى - لوطا وخبره مع قومه الفاسقين الشاذين إذ قال لهم: أتأتون هذا الذنب البالغ أقصى درجات الفحش والشذوذ، وأنتم تبصرون وتنظرون الشر الذى استمرأتموه؟',NULL,'569',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3216,'<span data-type="title" id=toc-3243>٥٥ </span>- أيسوغ فى نظر العقل والفطرة أن تأتوا الرجال بشهواتكم وتتركوا النساء؟ بل أنتم قوم قد أصابكم الحمق والجهل المطبق حتى صرتم لا تميزون بين الخبيث والطيب.',NULL,'569',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3217,'<span data-type="title" id=toc-3244>٥٦ </span>- فما كان رد قومه عليه حين نهاهم إلا قولهم: أخرجوا لوطا وأتباعه من هذه القرية لأنهم يتنزهون عن مشاركتنا فيما نفعل.',NULL,'569',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3218,'<span data-type="title" id=toc-3245>٥٧ </span>- فخلصناه هو وأهله من العذاب الذى سيقع بالقوم إلا امرأته، قدر الله أن تكون من الباقين حتى تهلك بالعذاب مع الكافرين.',NULL,'569',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3219,'<span data-type="title" id=toc-3246>٥٨ </span>- وأمطرنا على هؤلاء المفسدين مطر عذاب ونقمة، فكان مطراً سيئاً مُهلكاً لمن أُنذروا بالعذاب الأليم ولم يذعنوا.',NULL,'569',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3220,'<span data-type="title" id=toc-3247>٥٩ </span>- قل - أيها الرسول -: إنى أحمد الله وأثنى عليه - وحده - وأسأل الله سلاماً وتحية لعباده الذين اختارهم لأداء رسالته، وقل - أيها الرسول - للمشركين: هل توحيد الله خير لمن آمن، أم عبادة الأصنام التى أشركتم بها وهى لا تملك ضراً ولا نفعاً؟!',NULL,'569',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3221,'<span data-type="title" id=toc-3248>٦٠ </span>- بل اسألهم - أيها الرسول - عمَّن خلق السموات والأرض وما فيهما، وأنزل لأجلكم من السماء غيثاً نافعاً، فأنبت به بساتين ذات حُسن وبهاء ما أمكن لكم أن تنبتوا شجرها المختلف الأنواع والألوان والثمار. هذا التناسق فى الخلق يثبت أن ليس مع الله إله، ولكن الكفار قوم يعدلون عن الحق والإيمان ويميلون للباطل والشرك.',NULL,'570',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3222,'<span data-type="title" id=toc-3249>٦١ </span>- بل اسألهم - أيها الرسول - عمَّن مهَّد الأرض للإقامة فيها والاستقرار عليها، وخلق وسطها أنهاراً، وخلق عليها جبالا تمنعها من الميل، وجعل بين الماء العذب والماء الملح فاصلا يمنع امتزاج أحدهما بالآخر! {ليس هناك إله مع الله فهو الخالق - وحده - لكن أكثر الناس لا ينتفعون بالعلم الحق على وجهه وكأنهم لا يعلمون.',NULL,'570',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3223,'<span data-type="title" id=toc-3250>٦٢ </span>- بل اسألهم - أيها الرسول - عمَّن يجيب المضطر - فى دعائه - إذا أحوجته الشدة فلجأ إلى الله فى ضراعة وخشوع، ويدفع عن الإنسان ما يعتريه من مكروه، ويجعلكم خلفاء لمن سبقكم فى الأرض؟ . ليس هناك إله مع الله المانح لهذه النعم، ولكنكم أيها الكافرون قلّما تتعظون.',NULL,'570',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3224,'<span data-type="title" id=toc-3251>٦٣ </span>- بل اسألهم - أيها الرسول - عمَّن يرشدهم إلى السير فى ظلام الليل برا وبحراً، وعمن يبعث الرياح مبشرة بمطر هو رحمة من الله} ؟ أهناك إله مع الله تعالى يصنع ذلك؟! تنزه الله سبحانه عن أن يكون له شريك.',NULL,'570',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3225,'<span data-type="title" id=toc-3252>٦٤ </span>- بل اسألهم - أيها الرسول - عمن ينشئ الخلق ابتداء، ثم يوجده بعد فنائه كما كان؟ ومن الذى ينزل لكم الرزق من السماء ويخرجه من الأرض؟ . ليس هناك إله مع الله يفعل ذلك. قل - أيها الرسول - موبخاً لهم ومنكراً عليهم: إن كان لكم إله سوى الله فأقيموا لنا حجة على ذلك إن كنتم تزعمون أنكم صادقون، ولن يتأتى لكم ذلك.',NULL,'570',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3226,'<span data-type="title" id=toc-3253>٦٥ </span>- قل - أيها الرسول -: إن مَن تَفرَّد بفعل هذا كله قد تفرد - سبحانه - بعلم ما فى السموات والأرض من أمور الغيب، وهو الله - وحده - وما يعلم الناس أى وقت يبعثون فيه من قبورهم للحساب والجزاء.',NULL,'571',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3227,'<span data-type="title" id=toc-3254>٦٦ </span>- تلاحق علمهم فى الآخرة من جهل بها إلى شك فيها، وهم فى عماية عن إدراك الحق فى أى شئ من أمرها لأن الغواية أفسدت إدراكهم.',NULL,'571',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3228,'<span data-type="title" id=toc-3255>٦٧ </span>- وقال الكافرون منكرين للبعث: أئذا صرنا تراباً وبليت أجسامنا وأجسام آبائنا السابقين هل نعاد ونخرج إلى الحياة من جديد؟ {',NULL,'571',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3229,'<span data-type="title" id=toc-3256>٦٨ </span>- لقد وعدنا محمد بهذا البعث كما وعد الرسل السابقون آباءنا، ولو كان حقاً لحصل، وليس هذا إلا من أكاذيب السابقين.',NULL,'571',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3230,'<span data-type="title" id=toc-3257>٦٩ </span>- قل لهم - أيها الرسول -: تجولوا فى الدنيا وانظروا آثار ما حل بالمكذبين من عذاب الله لعلكم تعتبرون بهذا، وتخشون ما وراءه من عذاب الآخرة.',NULL,'571',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3231,'<span data-type="title" id=toc-3258>٧٠ </span>- لا تحزن - أيها الرسول - على الكافرين الذين لم يتبعوك، فإنما عليك البلاغ، ولا يكن فى صدرك حرج من مكرهم وكيدهم، فإن الله ناصرك عليهم.',NULL,'571',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3232,'<span data-type="title" id=toc-3259>٧١ </span>- ويبالغ الكافرون فى التكذيب، فيستعجلون العذاب قائلين: متى يحين موعد العذاب الذى هددتمونا به إن كنتم صادقين فى أن العذاب نازل بالمكذبين؟}',NULL,'571',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3233,'<span data-type="title" id=toc-3260>٧٢ </span>- قل - أيها الرسول -: لعله أن يكون قد لحق بكم وقرب منكم بعض ما تستعجلونه من العذاب.',NULL,'571',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3234,'<span data-type="title" id=toc-3261>٧٣ </span>- وإن الله ربك - أيها الرسول - لصاحب إنعام وإحسان على الناس كافة، ومن رحمته تأخير العقوبة على المكذبين، ولكن أكثر الناس لا يدركون فضل الله ولا يشكرونه.',NULL,'571',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3235,'<span data-type="title" id=toc-3262>٧٤ </span>- وإن الله ربك - أيها الرسول - لعليم بكل ما يسرون وما يعلنون من الأقوال والأفعال المنكرة، ومجازيهم عليها.',NULL,'571',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3236,'<span data-type="title" id=toc-3263>٧٥ </span>- وما من خافية غائبة مهما صغرت وضَؤُلَتْ فى السموات أو فى الأرض إلا علمها الله وأحصاها فى كتاب حق عنده.',NULL,'572',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3237,'<span data-type="title" id=toc-3264>٧٦ </span>- إن هذا الكتاب - الذى أنزل على محمد - يبين لبنى إسرائيل حقيقة ما جاء فى التوراة من عقائد وأحكام وقصص، ويردهم إلى الصواب فيما اختلفوا فيه.',NULL,'572',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3238,'<span data-type="title" id=toc-3265>٧٧ </span>- وإن هذا الكتاب لهداية من الضلال ورحمة من العذاب لجميع من آمن به.',NULL,'572',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3239,'<span data-type="title" id=toc-3266>٧٨ </span>- إن ربك - أيها الرسول - يفصل بين الناس جميعاً يوم القيامة بعدله، وهو الغالب فلا يرد قضاؤه، العليم فلا يلتبس لديه حق بباطل.',NULL,'572',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3240,'<span data-type="title" id=toc-3267>٧٩ </span>- ففوض أمرك - أيها الرسول - إلى الله، وثابر بدعوتك واثقاً بنصره، لأنك على الحق الواضح، ولا يضرك إعراض الكافرين عنك.',NULL,'572',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3241,'<span data-type="title" id=toc-3268>٨٠ </span>- إنك - أيها الرسول - لا تستطيع هدايتهم فإنهم كالموتى فى عدم الوعى، وكالصم فى فقدان أداة السمع، فليسوا مستعدين لسماع دعوتك لتماديهم فى الإعراض عنك.',NULL,'572',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3242,'<span data-type="title" id=toc-3269>٨١ </span>- ولست بمستطيع أن تهدى إلى الحق من عميت أبصارهم وبصائرهم، ولا يمكنك أن تُسمع إلا من يقبل على الإيمان بآياتنا، فهم مطيعون مستجيبون.',NULL,'572',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3243,'<span data-type="title" id=toc-3270>٨٢ </span>- وإذا قرب أن يتحقق وعد الله بقيام الساعة، وأن يقع العذاب على الكافرين أخرج الله للناس دابة من الأرض تقول لهم من جملة ما تقول: إن الكفار كانوا بمعجزاتنا كلها وباليوم الآخر لا يؤمنون، وقد تحقق الآن ما كانوا به يكذبون. وها هو ذا هول الساعة وما وراءها.',NULL,'572',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3244,'<span data-type="title" id=toc-3271>٨٣ </span>- واذكر - أيها الرسول - يوم نجمع من كل أمة طائفة من المكذبين بآياتنا، وهم الزعماء المتَّبَعون فهم يساقون فى مقدمة أممهم إلى الحساب والجزاء.',NULL,'573',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3245,'<span data-type="title" id=toc-3272>٨٤ </span>- وحينما يقفون بين يدى الله للحساب يقول - سبحانه - لهم تبكيتاً وتعنيفاً: قد كذبتم بكل آياتى وأنكرتموها دون تدبر ولا فهم. بل ماذا كنتم تعملون وأنتم لم تخلقوا عبثاً؟',NULL,'573',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3246,'<span data-type="title" id=toc-3273>٨٥ </span>- وحل بهم العذاب بسبب ظلمهم أنفسهم بالكفر، فهم عاجزون عن الدفاع والاعتذار.',NULL,'573',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3247,'<span data-type="title" id=toc-3274>٨٦ </span>- لقد شاهدوا أن الله جعل الليل ليستريحوا فيه، وجعل النهار مضيئاً ليتصرفوا فيه ويسعوا على معايشهم؛ إن فى ذلك لدلالات واضحة على ألوهية الله ووحدانيته لقوم يتدبرونها فيؤمنون.',NULL,'573',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3248,'<span data-type="title" id=toc-3275>٨٧ </span>- واذكر - أيها الرسول - يوم ينفخ إسرافيل فى البوق بإذن الله، فيرتعب من فى السموات ومن فى الأرض من هول النفخة إلا من طمأنه الله وأعفاه من الفزع، وكل المخلوقات يأتون إلى ربهم صاغرين.',NULL,'573',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3249,'<span data-type="title" id=toc-3276>٨٨ </span>- وترى - أيها الرسول - الجبال تظنها ثابتة لا تتحرك، ولكنها فى واقع الأمر تتحرك بسرعة كالسحاب، وهذا من صنع الله الذى خلق كل شئ وأبدعه. إنه سبحانه كامل العلم بما يفعل الناس من طاعة ومعصية، ومجازيهم عليه.',NULL,'573',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3250,'<span data-type="title" id=toc-3277>٨٩ </span>- كل من أتى بالحسنة فى الدنيا وهى الإيمان والإخلاص فى الطاعة فله فى الآخرة الثواب الأعظم من أجل ما تقدم. وأصحاب هذه الحسنات آمنون من الخوف والفزع يوم القيامة.',NULL,'573',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3251,'<span data-type="title" id=toc-3278>٩٠ </span>- وكل من أتى فى الدنيا بالسيئة - وهى الشرك والمعصية - ومات على ذلك فجزاء هذا الفريق أن يكبهم الله على وجوههم فى النار يوم القيامة ويقال لهم حينئذ - توبيخاً - إنكم لا تجزون اليوم إلا بسبب شرككم ومعصيتكم.',NULL,'573',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3252,'<span data-type="title" id=toc-3279>٩١ </span>- قل - أيها الرسول - للناس: ما أمرت أن أعبد أحداً إلا الله رب مكة الذى كرمها، فجعلها حرماً آمناً، لا يسفك فيها دم، ولا يصاد صيدها، ولا يقطع شجرها. وله سبحانه كل ما فى الكون خلقاً وملكاً وأمرت أن أكون من الخاضعين لله.',NULL,'574',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3253,'<span data-type="title" id=toc-3280>٩٢ </span>- وأمرت أن أواظب على تلاوة القرآن عبادة وتدبراً ودعوة إلى ما فيه، فمن اهتدى وآمن به واتبعك فإنما خير ذلك وجزاؤه لنفسه لا لك، ومن ضل عن الحق ولم يتبعك فقل: إنما أنا رسول أنذر وأبلغ.',NULL,'574',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3254,'<span data-type="title" id=toc-3281>٩٣ </span>- وقل - أيها الرسول -: الحمد لله على نعمة النبوة والهداية، سيكشف الله لكم فى الدنيا عن آثار قدرته، وفى الآخرة عن صدق ما أخبركم به فتعرفونها معرفة حق، وليس الله بعاجز عن حسابكم ولا بغافل عن أعمالكم.',NULL,'574',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3255,'<span data-type="title" id=toc-3283>١ </span>- طسم: حروف صوتية سيقت لبيان أن القرآن المعجز من هذه الحروف التى يتألف منها حديثكم، ولتنبيه السامعين.',NULL,'575',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3256,'<span data-type="title" id=toc-3284>٢ </span>- هذه الآيات التى نوحيها إليك - أيها الرسول - آيات القرآن المبين الواضح، المظهر للحق من الباطل، وللحلال من الحرام، والوعد بالثواب، والوعيد بالعقاب.',NULL,'575',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3257,'<span data-type="title" id=toc-3285>٣ </span>- نقُص عليك بعض أخبار موسى وفرعون بالصدق، ليعتبر بما فيه المؤمنون.',NULL,'575',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3258,'<span data-type="title" id=toc-3286>٤ </span>- إن فرعون تعاظم فى نفسه، وجاوز الحد فى ظلمه، واستكبر فى أرض مصر، وصيَّر أهلها فرقا، يصطفى بعضها ويسخِّر بعضها، ويستضعف منهم بنى إسرائيل، فيذبح الذكور من أولادهم، ويستبقى الإناث. إنه كان من المسرفين فى الطغيان والإفساد.',NULL,'575',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3259,'<span data-type="title" id=toc-3287>٥ </span>- وأراد الله أن يتفضَّل على الذين استضعفهم فرعون فى الأرض، وأن يجعلهم هداة إلى الخير، ويورثهم ملك الأرض والسلطان.',NULL,'575',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3260,'<span data-type="title" id=toc-3288>٦ </span>- ونُثَّبتهم فى الأرض ويتخذون فيها مكاناً، ونُثَبت لفرعون ووزيره هامان وجندهما ما كانوا يخشونه من ذهاب ملكهم على يد مولود من بنى إسرائيل.',NULL,'576',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3261,'<span data-type="title" id=toc-3289>٧ </span>- وألهم الله أمَّ موسى - حينما خشيت عليه أن يذبحه فرعون كما يذبح أبناء بنى إسرائيل - أن ترضعه مطمئنة عليه من قتل فرعون، فإذا خشيت أن يعرف أمره وضعته فى صندوق وألقته فى النيل غير خائفة ولا محزونه، فقد تكفل الله لها بحفظه وردِّه إليها، وأن يرسله إلى بنى إسرائيل.',NULL,'576',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3262,'<span data-type="title" id=toc-3290>٨ </span>- فأخذه آل فرعون ليتحقق ما قدَّره الله بأن يكون موسى رسولا معاديا لهم، ومثيرا لحزنهم بنقد دينهم والطعن على ظلمهم. إن فرعون وهامان وأعوانهما كانوا آثمين مسرفين فى الطغيان والفساد.',NULL,'576',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3263,'<span data-type="title" id=toc-3291>٩ </span>- وقالت امرأة فرعون - حين رأته - لزوجها: هذا الطفل مبعث السرور لى ولك. نستبقيه ولا نقتله رجاء أن ننتفع به فى تدبير شأننا أو نتبناه، وهم لا يشعرون بما قدر الله فى شأنه.',NULL,'576',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3264,'<span data-type="title" id=toc-3292>١٠ </span>- وصار قلب أم موسى خاليا من العقل لما دهمها من الجزع لوقوع ولدها فى يد فرعون. إنها كادت تظهر أمره بأنه ولدها لولا أن ثَبَّت الله قلبها بالصبر لأعلنت أنه ولدها شفقة عليه، ولتكون فى ضمن المؤمنين المطمئنين.',NULL,'576',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3265,'<span data-type="title" id=toc-3293>١١ </span>- وقالت أمه لأخته: تتبعى أثره لتعرفى خبره، فرأته عن بُعد وهى تتجنب ظهور أمرها وفرعون وآله لا يدرون أنها أخته.',NULL,'576',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3266,'<span data-type="title" id=toc-3294>١٢ </span>- ومنع الله الطفل - موسى - أن يرضع ثديا لمرضع قبل أن يرشدوا إلى أمه، فاغتم آل فرعون، وأهمهم ذلك، فقالت لهم أخته: ألا أرشدكم إلى أسرة تكفله وتتعهده بالرضاع والتربية وهم له حافظون؟',NULL,'577',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3267,'<span data-type="title" id=toc-3295>١٣ </span>- فقبلوا إرشادها، وردَّه الله إلى أمه كى تطيب نفسها، وتفرح بعودته إليها، ولا تحزن بفراقه، ولتزداد علما بأن وعد الله بردِّه لها حاصل لا يتخلف، ولكن أكثر الناس لا يعلمون عودة موسى إلى أمه، لخفائه عليهم.',NULL,'577',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3268,'<span data-type="title" id=toc-3296>١٤ </span>- ولما بلغ موسى رشده واكتمل نضجه أعطاه الله الحكمة والعلم، ومثل ذلك الإحسان الذى أحسنا به إلى موسى وأمه نكافئ المحسنين على إحسانهم.',NULL,'577',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3269,'<span data-type="title" id=toc-3297>١٥ </span>- ودخل موسى المدينة فى وقت غفل فيه أهلها، فوجد فيها رجلين يقتتلان: أحدهما من بنى إسرائيل، والآخر من قوم فرعون، فاستعان به الإسرائيلى على خصمه فأعانه موسى، وضرب الخصم بقبضة يده فقتله من غير قصد. ثم أسف موسى، وقال: إنَّ إقدامى على هذا من عمل الشيطان. إن الشيطان لعدو ظاهر العداوة واضح الضلال.',NULL,'577',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3270,'<span data-type="title" id=toc-3298>١٦ </span>- قال موسى متضرعاً إلى الله فى ندم: يا رب إنى أسأت إلى نفسى بما فعلت، فاغفر لى فعلتى. فأجاب الله دعوته وغفر له. إن الله هو العظيم المغفرة الواسع الرحمة.',NULL,'577',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3271,'<span data-type="title" id=toc-3299>١٧ </span>- قال موسى متضرعاً: يا رب بحق إنعامك علىَّ بالحكمة والعلم وفقنى للخير والصواب، فإذا وفقتنى فلن أكون عوناً للكافرين.',NULL,'577',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3272,'<span data-type="title" id=toc-3300>١٨ </span>- فأصبح موسى فى المدينة - مصر - فزعا، يتوقع أن يصيبه الأذى من القوم بسبب قتله المصرى، فوجد الإسرائيلي الذى طلب منه النصرة بالأمس يستغيث به ثانية على مصرى آخر، فنهره موسى قائلا له: إنك لشديد الغواية ظاهر الضلال، حيث عدت لمثل ما فعلت بالأمس ودعوتنى مرة ثانية لنصرتك.',NULL,'578',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3273,'<span data-type="title" id=toc-3301>١٩ </span>- فلما هم موسى بالبطش بالمصرى الذى هو عدو لهما، بسبب هذه العداوة، قال - وقد ظن أن موسى سيقتله -: أتريد أن تقتلنى كما قتلت شخصاً آخر بالأمس. ما تريد إلا أن تكون طاغية فى الأرض، وما تريد أن تكون من دعاة الإصلاح والخير.',NULL,'578',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3274,'<span data-type="title" id=toc-3302>٢٠ </span>- وجاء رجل مؤمن من آل فرعون - يخفى إيمانه - من أقصى المدينة حينما انتشر نبأ قتل موسى للمصرى، يخبر موسى أن قوم فرعون يتشاورون لقتلك، ويقول له: اخرج من المدينة فرارا من القتل، إنى لك من الناصحين.',NULL,'578',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3275,'<span data-type="title" id=toc-3303>٢١ </span>- فخرج موسى من المدينة خائفاً يتوقع أن يتعرض له أعداؤه بالأذى، ضارعا إلى الله أن يُنجيه من ظلم الكافرين.',NULL,'578',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3276,'<span data-type="title" id=toc-3304>٢٢ </span>- ولما توجه ناحية مدين - قرية شعيب - لما فيها من الأمن - تضرع إلى الله أن يهديه طريق الخير والنجاة.',NULL,'578',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3277,'<span data-type="title" id=toc-3305>٢٣ </span>- ولما وصل ماء آل مدين الذى يسقون منه، وجد على جانب البئر جماعة كثيرة من أناس مختلفين يسقون مواشيهم، ووجد فى مكان أسفل من مكانهم امرأتين تدفعان غنمهما بعيدا عن الماء، فقال لهما موسى: لم تبتعدان عن الماء؟ فأجابتا: لا نستطيع الزحام، ولا نسقى حتى يسقى الرعاة، وأبونا شيخ طاعن لا يستطيع الرعى ولا السقى.',NULL,'578',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3278,'<span data-type="title" id=toc-3306>٢٤ </span>- فتطوع موسى وسقى لهما، ثم ركن إلى ظل شجرة يستريح من الجهد، وهو يقول فى ضراعة: يا رب إنى فقير لما تسوقه إلىَّ من خير ورزق.',NULL,'579',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3279,'<span data-type="title" id=toc-3307>٢٥ </span>- فجاءت إحدى الفتاتين - مُرْسَلة من قبل أبيها بعد أن علم بأمر موسى معهما - تسير إلى موسى حياء، قالت: إن أبى يدعوك ليجزيك أجر سقيك لنا. فلما ذهب إليه وقصَّ عليه قصة خروجه من مصر قال والد الفتاتين: لا تخف، نجوت من القوم الظالمين، إذ لا سلطان لفرعون علينا.',NULL,'579',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3280,'<span data-type="title" id=toc-3308>٢٦ </span>- قالت أحدى الفتاتين: يا أبت اتخذه أجيرا لرعى الغنم والقيام على شأنها، إنه خير من تستأجره لقوَّته وأمانته.',NULL,'579',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3281,'<span data-type="title" id=toc-3309>٢٧ </span>- قال له شعيب - عليه السلام - إنى أريد أن أزوجك واحدة من ابنتى هاتين، على أن يكون مهرها أن تعمل عندنا ثمانى سنوات، فإن أتممت عشرا فمن عندك تطوعا، وما أريد أن ألزمك بأطول الأجلين، وستجدنى إن شاء الله من الصالحين المحسنين للمعاملة الموفين بالعهد.',NULL,'579',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3282,'<span data-type="title" id=toc-3310>٢٨ </span>- قال موسى: ذلك الذى عاهدتنى عليه قائم بينى وبينك، أى مدة من المدتين أقضيها فى العمل أكون وفيتك عهدك فلا أطالب بزيادة عليها، والله شاهد على ما نقول.',NULL,'579',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3283,'<span data-type="title" id=toc-3311>٢٩ </span>- فلما أتم موسى المدة المشروطة، وأصبح زوجاً لبنت الذى آواه، وعاد بها إلى مصر أبصر فى طريقه من ناحية جبل الطور نارا، فقال لمن معه: امكثوا هنا، إنى رأيت نارا استأنست بها فى هذه الظلمة، سأذهب إليها لآتيكم من عندها بخبر عن الطريق أو بجذوة منها لعلكم تستدفئون بها.',NULL,'579',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3284,'<span data-type="title" id=toc-3312>٣٠ </span>- فلما جاء موسى إلى النار التى أبصرها، سمع من ناحية الجانب الأيمن له من الشجرة النابتة فى البقعة المباركة بجانب الجبل نداء علويا يقول له: يا موسى، إنى أنا الله الذى لا يستحق العبادة سواه، خالق العالمين وحاميهم وحافظهم ومربيهم.',NULL,'580',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3285,'<span data-type="title" id=toc-3313>٣١ </span>- ونودى: أن ألق عصاك. فألقاها فقلبها الله ثعبانا، فلما أبصرها موسى تتحرك كأنها حية فى سعيها خاف وفر فزعا ولم يرجع، فقيل له: يا موسى أقبل على النداء وعُد إلى مكانك ولا تخف، إنك فى عداد الآمنين من كل مكروه.',NULL,'580',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3286,'<span data-type="title" id=toc-3314>٣٢ </span>- وأدخل يدك فى طوق ثوبك تخرج شديدة البياض من غير عيب ولا مرض، واضمم يدك إلى جانبك فى ثبات من الخوف، ولا تفزع من رؤية العصا حية ومن رؤية اليد بيضاء، فهاتان المعجزتان من الله تواجه بهما فرعون وقومه حينما يقابلون رسالتك بالتكذيب خارجين عن طاعة الله.',NULL,'580',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3287,'<span data-type="title" id=toc-3315>٣٣ </span>- قال موسى - متخوفا وطالبا العون - يا رب، إنى قتلت منهم نفسا فأخاف أن يقتلونى به قصاصا.',NULL,'580',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3288,'<span data-type="title" id=toc-3316>٣٤ </span>- وأخى هارون أفصح منى لسانا، فأرسله معى عونا فى التبليغ، لأنى أخاف أن يكذبون.',NULL,'580',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3289,'<span data-type="title" id=toc-3317>٣٥ </span>- قال الله - استجابة لدعائه -: سنقويك بهارون، ونجعل لكما سلطانا وتأييدا بالمعجزات فلا يستطيعون الاعتداء عليكما، وأنكما ومن اتبعكما واهتدى بكما الغالبون المنتصرون على هؤلاء الكافرين.',NULL,'580',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3290,'<span data-type="title" id=toc-3318>٣٦ </span>- فلما واجههم موسى بدعوته مؤيدة بالمعجزات الواضحة أنكروا ما شاهدوا، قالوا: ما هذا إلا سحر تفتريه على الله، ولم نسمع بهذا الذى تدَّعيه فيمن سبقنا من آبائنا الأولين.',NULL,'581',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3291,'<span data-type="title" id=toc-3319>٣٧ </span>- وقال موسى - ردا على فرعون وقومه -: ربى يعلم أنى جئت بهذه الآيات الدالة على الحق والهدى من عنده، فهو شاهد لى على ذلك إن كذبتمونى، ويعلم أن العاقبة الحميدة لنا ولأهل الحق، إنه لا يفوز بالخير الكافرون.',NULL,'581',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3292,'<span data-type="title" id=toc-3320>٣٨ </span>- وقال فرعون - عندما عجز عن محاجة موسى، تماديا فى طغيانه - يا أيها الملأ، ليس لى علم بوجود إله لكم غيرى. وأمر وزيره هامان أن يصنع له الآجُرّ ويُشيد له صرحاً شامخاً عالياً ليصعد عليه، وينظر إلى الإله الذى يدعو إليه موسى، ويؤكد فرعون مع ذلك أن موسى من الكاذبين فى ظنه.',NULL,'581',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3293,'<span data-type="title" id=toc-3321>٣٩ </span>- وظل فرعون وجنوده مستكبرين فى أرض مصر بالباطل، وظنوا أنهم لن يُبعثوا فى الآخرة للحساب والجزاء.',NULL,'581',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3294,'<span data-type="title" id=toc-3322>٤٠ </span>- فانتزعنا فرعون من سلطانه، واستدرجناه هو وجنوده إلى اليَم، وأغرقناهم فيه نابذين لهم بسبب ظلمهم. فتدبر يامحمد، وحذر قومك كيف كانت نهاية الظالمين فى دنياهم؟ وإنك لمنصور عليهم.',NULL,'581',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3295,'<span data-type="title" id=toc-3323>٤١ </span>- قال تعالى: وجعلناهم دعاة يدعون إلى الكفر الذى يؤدى إلى النار، ويوم القيامة لا يجدون من ينصرهم ويخرجهم من هذا العذاب.',NULL,'581',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3296,'<span data-type="title" id=toc-3324>٤٢ </span>- وجعلناهم فى هذه الدنيا مطرودين من رحمتنا، ويوم القيامة هم من المهلكين. وما حكى فى الآيتين بشأنهم دليل على غضب الله.',NULL,'582',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3297,'<span data-type="title" id=toc-3325>٤٣ </span>- ولقد أنزل الله التوراة على موسى بعد أن أهلك المكذبين من الأمم السابقة لتكون نورا للقلوب، لأنها كانت مظلمة لا تعرف حقا وإرشادا، لأنهم كانوا يتخبطون فى الضلال، وطريقاً لنيل الرحمة لمن عمل بها، ليتعظوا بما فيها فيسارعوا إلى امتثال الأوامر واجتناب النواهى.',NULL,'582',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3298,'<span data-type="title" id=toc-3326>٤٤ </span>- وما كنت - يامحمد - حاضرا مع موسى فى المكان الغربى من الجبل حين عهد الله إليه بأمر الرسالة، ولم تكن معاصرا لموسى ولا شاهدا تبليغه للرسالة، فكيف يكذب قومك برسالتك وأنت تتلوا عليهم أنباء السابقين؟ .',NULL,'582',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3299,'<span data-type="title" id=toc-3327>٤٥ </span>- ولكنا خلقنا أمماً كثيرة فى أجيال طال عليها الزمن فنسوا ما أخذه عليهم من العهود، ولم تكن - أيها الرسول - مقيما فى مدين حتى تخبر أهل مكة بأنبائهم، ولكنا أرسلناك وأخبرناك بها من طريق الوحى.',NULL,'582',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3300,'<span data-type="title" id=toc-3328>٤٦ </span>- وما كنت - أيها الرسول - حاضرا فى جانب الطور حين نادى الله موسى واصطفاه لرسالته، ولكن الله أعلمك بهذا من طريق الوحى رحمة بك وبأمتك، لتبلغه قوما لم يأتهم رسول من قبلك لعلهم يتذكرون.',NULL,'582',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3301,'<span data-type="title" id=toc-3329>٤٧ </span>- ولولا أن الكفار حين تصيبهم عقوبة بسبب كفرهم يعتذرون ويحتجون قائلين: ربنا لم ترسل إلينا رسولا نؤمن ونُذعن لمعجزاته ونكون من المؤمنين، ما كانت رسالات الرسل.',NULL,'582',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3302,'<span data-type="title" id=toc-3330>٤٨ </span>- فلما جاء رسول الله - محمد - بالقرآن من عند الله قال الكفار: ليته أُعْطى مثل ما أُعْطى موسى من معجزات حسية، وكتاب نزل جملة واحدة كالتوراة، وقد كفروا من قبل بموسى وآياته كما كفروا اليوم بمحمد وكتابه، وقالوا: نحن بكل منهما كافرون. فالجحود هو الذى أدى إلى الكفر بالمعجزات.',NULL,'583',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3303,'<span data-type="title" id=toc-3331>٤٩ </span>- قل لهم - أيها الرسول - إذا لم تؤمنوا بالتوراة والقرآن؛ فهاتوا كتاباً من عند الله أحسن منهما هداية أو مثلهما أتبعه معكم إن كنتم صادقين فى زعمكم أن ما جئنا به سحر.',NULL,'583',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3304,'<span data-type="title" id=toc-3332>٥٠ </span>- فإن لم يستجيبوا دعاءك إلى الإتيان بالكتاب الأهدى، فاعلم أنهم قد ألزموا الحجة ولم يبق لهم حجة، وأنهم بذلك يتبعون أهواءهم، ولا أحد أكثر ضلالا ممن اتبع هواه فى الدين بغير هدى من الله، إن الله لا يوفق من ظلم نفسه باتباع الباطل دون أن ينشد حقاً.',NULL,'583',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3305,'<span data-type="title" id=toc-3333>٥١ </span>- ولقد أنزل الله القرآن عليهم متواصلا بعضه إثر بعض حسبما تقتضيه الحكمة، ومتتابعا وعدا ووعيدا وقصصا وعبرا، ليتدبروا ويؤمنوا بما فيها.',NULL,'583',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3306,'<span data-type="title" id=toc-3334>٥٢ </span>- الذين أنزلنا لهم التوراة والإنجيل من قبل نزول القرآن وآمنوا بهما وصدقوا بما فيهما عن محمد وكتابه، هم بمحمد وكتابه يؤمنون.',NULL,'583',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3307,'<span data-type="title" id=toc-3335>٥٣ </span>- وإذا يُقرأ القرآن على هؤلاء قالوا - مسارعين إلى إعلان الإيمان -: آمنا به لأنه الحق من ربنا، ونحن عرفنا محمدا وكتابه قبل نزوله، فإسلامنا سابق على تلاوته.',NULL,'583',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3308,'<span data-type="title" id=toc-3336>٥٤ </span>- أولئك الذين آمنوا بالقرآن وبما أنزل من قبله من كتب يعطوْن ثوابهم مضاعفا، بصبرهم على ما يلحقهم من الأذى فى سبيل الإيمان، ويؤثرون العمل الصالح، ويقابلون بالعفو والإحسان، وينفقون فى سبيل الخير مما منحهم الله من مال.',NULL,'583',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3309,'<span data-type="title" id=toc-3337>٥٥ </span>- وإذا سمعوا الباطل من الجاهلين انصرفوا عنه تنزها وترفعا، وقالوا: لنا أعمالنا الحقة لا نحيد عنها، ولكم أعمالكم الباطلة ووزرها عليكم، ونحن نترككم وشأنكم لأننا لا نريد صحبة الجاهلين.',NULL,'584',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3310,'<span data-type="title" id=toc-3338>٥٦ </span>- إنك - أيها الرسول - شديد الحرص على هداية قومك، ولكنك لا تستطيع أن تُدْخل فى الإسلام كل من تحب، ولكن الله يهدى للإيمان من علم فيهم قبول الهداية واختيارها، وهو الذى يعلم علما ليس فوقه علم من سيدخل فى صفوف المهتدين.',NULL,'584',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3311,'<span data-type="title" id=toc-3339>٥٧ </span>- وقال مشركوا مكة للرسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - معتذرين عن بقائهم على دينهم: إن اتبعناك على دينك أخرجَنَا العرب من بلدنا وغلبونا على سلطاننا. وهم كاذبون فيما يعتذرون به، فقد ثَبَّتَ الله أقدامهم ببلدهم، وجعله حرما يأمنون فيه - وهم كفرة - من الإغارة والقتل، وتُحمل إليه الثمرات والخيرات المتنوعة الكثيرة رزقاً يسوقه الله إليهم من كل جهة، فكيف يستقيم أن يسلبهم الأمن ويعرضهم للتخطف إذا ضموا إلى حرمة البيت الإيمان بمحمد؟ ولكن أكثرهم لا يعلمون الحق، ولو علموا لما خافوا التَّخطف.',NULL,'584',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3312,'<span data-type="title" id=toc-3340>٥٨ </span>- لم يعتبر هؤلاء بمصاير الأمم السابقة، فقد أهلك الله قرى الذين اغتروا بنعمة الله ثم كفروا بها وبالله، وهذه ديارهم خاوية لا تصلح للسكن بعدهم إلا فترات عابرة للمارين بها، ولم يبق لها مالك بعدهم إلا الله ذو الجلال والإكرام.',NULL,'584',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3313,'<span data-type="title" id=toc-3341>٥٩ </span>- وما كان من حكمة الله تعالى - وهو ربك الذى خلقك واصطفاك - أن يهلك المدن العظيمة إلا بعد أن يُرسل إلى أهلها رسولا بالمعجزات الباهرة يتلو عليهم الكتاب المنزل، ويبين لهم شرائعه، ثم لم يؤمنوا، وما كنا مُهلكى المدن العظيمة إلا وأهلها مستمرون على الظلم والاعتداء.',NULL,'584',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3314,'<span data-type="title" id=toc-3342>٦٠ </span>- وكل شئ رُزقْتموه من أعراض الدنيا وزينتها متاع محدود إلى أمد قريب، فلا يصرفَنَّكم عن الإيمان والعمل الصالح، فإن ما عند الله فى الآخرة من الثواب والنعيم الخالد أنفع وأدوم من ذلك كله، فلماذا لا تُعمِلون عقولكم بدل أهوائكم؟ .',NULL,'585',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3315,'<span data-type="title" id=toc-3343>٦١ </span>- لا يستوى من آمن وعمل صالحا فاستحق وعد الله - الوعد الحسن بالثواب والجنة - فهو مُدْركه كما وعده الله، ومن كفر وعمل سيئا وفتنه متاع الحياة وزخرفها، ثم هو يوم القيامة من المُحضرين للحساب، الهالكين فى العذاب.',NULL,'585',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3316,'<span data-type="title" id=toc-3344>٦٢ </span>- واذكر - أيها الرسول - يوم يقف هؤلاء بين يدى الله للحساب فيناديهم سبحانه نداء توبيخ: أين الآلهة الذين زعمتموهم شركاء ليدافعوا عنكم أو ليشفعوا فيكم؟!',NULL,'585',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3317,'<span data-type="title" id=toc-3345>٦٣ </span>- قال قادة الكفر من الذين حق عليهم غضب الله ووعيده: يا ربنا، هؤلاء الذين دعوناهم إلى الشرك وزيَّنا لهم الضلال أغْويناهم لأنهم اختاروا الكفر وتقبَّلوه كما اخترناه نحن وتقبَّلناه. تبرأنا إليك منهم اليوم ومما اختاروه فى الدنيا من الكفر، لم يعبدونا نحن، بل عبدوا أهواءهم وأطاعوا شهواتهم.',NULL,'585',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3318,'<span data-type="title" id=toc-3346>٦٤ </span>- وأمر المشركين من جانب الله أمر توبيخ بدعوة الآلهة التى أشركوها مع الله لتخلصهم من عذابه كما زعموا، فخضعوا فى ذلة ودعوهم فى حيرة، فلم يظفروا منهم بجواب، وشاهدوا العذاب المعد لهم حاضرا، وتمنوا لو أنهم كانوا فى دنياهم مؤمنين مهتدين لما حاق بهم ذلك العذاب.',NULL,'585',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3319,'<span data-type="title" id=toc-3347>٦٥ </span>- واذكر - أيها الرسول - كذلك يوم ينادى المشركون من جانب الله تعالى نداء توبيخ، فقال لهم: بأى شئ أجبتم رسلى الذين أرسلتهم لدعوتكم إلى الإيمان فبلغوكم الرسالة؟',NULL,'585',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3320,'<span data-type="title" id=toc-3348>٦٦ </span>- فصارت الأخبار غائبة عنهم لا يهتدون إليها، كأنهم فى عَمى، ولم يرجع بعضهم إلى بعض فى ذلك لتساويهم فى العجز عن الإجابة.',NULL,'586',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3321,'<span data-type="title" id=toc-3349>٦٧ </span>- هذا شأن المشركين، فأمَّا من تاب من الشرك، وآمن إيماناً صادقاً وعمل الصالحات، فهو يرجو أن يكون عند الله من الفائزين برضوان الله وبالنعيم الدائم المستمر.',NULL,'586',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3322,'<span data-type="title" id=toc-3350>٦٨ </span>- وربك يخلق ما يشاء بقدرته، ويختار بحكمته من يشاء للرسالة والطاعة على مقتضى علمه باستعدادهم لذلك، ولم يكن فى مقدور الخلق ولا من حقهم أن يختاروا على الله ما يشاءون من أديان باطلة وآلهة زائفة، تنزَّه الله - تعالى شأنه - عن الشركاء.',NULL,'586',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3323,'<span data-type="title" id=toc-3351>٦٩ </span>- وربك - أيها الرسول - محيط علمه بما تخفيه صدور المشركين من عداوتهم لك، وما يعلنون بألسنتهم من المطاعن فيك والاعتراض على اختيارك للرسالة.',NULL,'586',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3324,'<span data-type="title" id=toc-3352>٧٠ </span>- وربك - أيها الرسول - هو الله الحق المختص بالألوهية، المستحق - وحده - للحمد من عباده فى الدنيا على إنعامه وهدايته، وفى الآخرة على عدله ومثوبته. وهو - وحده - صاحب الحكم والفصل بين عباده، وإليه المرجع والمصير.',NULL,'586',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3325,'<span data-type="title" id=toc-3353>٧١ </span>- قل - أيها الرسول -: أخبرونى أيها الناس، إن جعل الله عليكم الليل متتابعاً دون نهار إلى يوم القيامة، فهل لكم إله سوى الله يأتيكم بنهار مضئ تقومون فيه بمعاشكم وشئون دنياكم؟',NULL,'586',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3326,'<span data-type="title" id=toc-3354>٧٢ </span>- قل - أيها الرسول -: للناس: إن جعل الله عليكم النهار متتابعاً دون ليل إلى يوم القيامة، فهل لكم إله سوى الله يأتيكم بليل تستريحون فيه من عمل النهار؟ ليس لكم ذلك، فلماذا لا تبصرون آيات الله فتؤمنوا وتهتدوا؟',NULL,'586',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3327,'<span data-type="title" id=toc-3355>٧٣ </span>- ومن رحمة الله بخلقه أن خلق لهم الليل والنهار وجعلهما متعاقبين، ليستريحوا فى الليل، وليسعوا على رزقهم ومنافعهم فى النهار، وليُدركوا فضل الله عليهم فيشكروه.',NULL,'586',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3328,'<span data-type="title" id=toc-3356>٧٤ </span>- واذكر كذلك - أيها الرسول - يوم ينادَى المشركون من جانب الله تعالى نداء توبيخ، فيقال لهم: أين الشركاء الذين زعمتموهم آلهة ينصرونكم أو شفعاء يشفعون لكم؟!',NULL,'587',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3329,'<span data-type="title" id=toc-3357>٧٥ </span>- وأخرجنا يوم القيامة من كل أمة شهيداً هو نبيُّها. يشهد عليها بما كان منها فى الدنيا فنقول حينئذ للمخالفين منهم: ما هى حجتكم فيما كنتم عليه من الشرك والمعصية؟ فيعجزون عن الجواب، ويعلمون حينئذ أن الحق لله بداية ونهاية، وغاب عنهم غيبة الشئ الضائع ما كانوا يفترون على الله.',NULL,'587',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3330,'<span data-type="title" id=toc-3358>٧٦ </span>- ذكرت السورة قصة قارون، وأنه كان من قوم موسى، فتكبر عليهم غروراً بنفسه وماله، وقد أعطاه الله كنوزاً زاخرة بالأموال، بلغت مفاتيحها من الكثرة بحيث يثقل حملها على الجماعة الأقوياء من الرجال، وحين اغتر بنعمة الله عليه وكفر بما نصحه قومه قائلين له: لا تغتر بمالك، ولا يفتنك الفرح به عن شكر الله، إن الله لا يرضى عن المغرورين المفتونين، والعبرة فى هذه القصة أن الكافرين بمحمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - قد اغتروا بأموالهم، فبين القرآن أن أموالهم بجانب مال قارون ليست شيئاً مذكوراً.',NULL,'587',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3331,'<span data-type="title" id=toc-3359>٧٧ </span>- واجعل نصيباً مما أعطى لك الله من الغنى والخير فى سبيل الله والعمل للدار الآخرة، ولا تمنع نفسك نصيبها من التمتع بالحلال فى الدنيا، وأحسن إلى عباد الله مثلما أحسن الله إليك بنعمته، ولا تُفسد فى الأرض متجاوزاً حدود الله، إن الله سبحانه لا يرضى عن المفسدين لسوء أعمالهم.',NULL,'587',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3332,'<span data-type="title" id=toc-3360>٧٨ </span>- فلم يستجب قارون لنصح قومه، ونسى فضل الله عليه، وتجاهل أن الله قد أهلك قبله كثيرين كانوا أكثر منه قدرة على كسب المال وخبرة بوجوه استثماره، والمجرمون لا يُسألون عن ذنوبهم لعلمه تعالى بها، فيدخلون النار بغير حساب، وإنما يُسألون سؤال توبيخ.',NULL,'587',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3333,'<span data-type="title" id=toc-3361>٧٩ </span>- لم يعبأ قارون بنصح قومه، وخرج عليهم فى زينته، فاغتر به الذين يحبون متاع الحياة الدنيا، وتمنوا أن يكون لهم مثل ما أُعطى قارون من المال والحظ العظيم فى الحياة.',NULL,'588',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3334,'<span data-type="title" id=toc-3362>٨٠ </span>- أما الذين رزقهم الله العلم النافع فلم يفتنهم ذلك، وتوجهوا بالنصح للمفتونين قائلين لهم: لا تتمنوا هذا ولا تنصرفوا عن الدين، فإن ما عند الله من ثواب ونعيم أزكى لمن آمن به وعمل صالحاً، وتلك نصيحة حقة لا يتقبلها إلا من يجاهدون أنفسهم ويصبرون على الطاعة.',NULL,'588',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3335,'<span data-type="title" id=toc-3363>٨١ </span>- فخسف الله به الأرض فابتلعته هو وداره بما فيها من أموال وزينة، فلم يكن له أنصار يمنعونه من عذاب الله، ولم يكن يستطيع أن ينتصر لنفسه.',NULL,'588',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3336,'<span data-type="title" id=toc-3364>٨٢ </span>- وصار الذين تمنوا منذ وقت قريب منزلته من الدنيا يرددون عبارات التحسّر والندم بعد أن فكروا فيما أصابه! ويقولون: إن الله يوسِّع الرزق على من يشاء من عباده المؤمنين وغير المؤمنين، ويضيِّق على من يشاء منهم، ويقولون شاكرين: لولا أن الله أحسن إلينا بالهداية إلى الإيمان والعصمة من الزلل لامتحننا بإجابة ما تمنيناه، ولفعل بنا مثل ما فعل بقارون. إن الكافرين بنعمة الله لا يفلحون بالنجاة من عذابه.',NULL,'588',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3337,'<span data-type="title" id=toc-3365>٨٣ </span>- تلك الدار التى سمعت خبرها - أيها الرسول - وبلغك وصفها - وهى الجنة - نخص بها المؤمنين الطائعين الذين لا يطلبون الغلبة والتسلط فى الدنيا، ولا ينحرفون إلى الفساد بالمعاصى، والعاقبة الحميدة إنما هى للذين تمتلئ قلوبهم خشية من الله فيعملون ما يرضيه.',NULL,'588',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3338,'<span data-type="title" id=toc-3366>٨٤ </span>- الذى يأتى بالحسنة - وهى الإيمان والعمل الصالح - له ثواب مضاعف بسببها، والذى يأتى بالسيئة - وهى الكفر والمعصية - فلا يجزى إلا بمثل ما عمل من سوء.',NULL,'588',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3339,'<span data-type="title" id=toc-3367>٨٥ </span>- إن الله الذى أنزل القرآن، وفرض عليك تبليغه والتمسك به لرادك إلى موعد - لا محالة منه - وهو يوم القيامة ليفصل بينك وبين مكذبيك، قل - أيها الرسول - للكافرين: ربى هو الذى يعلم علماً ليس فوقه علم بمن منحه الهداية والرشاد، وبمن هو واقع فى الضلال الذى يدركه كل عاقل سليم الإدراك.',NULL,'589',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3340,'<span data-type="title" id=toc-3368>٨٦ </span>- وما كنت - أيها الرسول - تأمل وتنتظر أن ينزل عليك القرآن، ولكن الله أنزله عليك من عنده رحمة بك وبأمتك، فاذكر هذه النعمة، وثابر على تبليغها، ولا تكن أنت ولا من اتبعك عوناً للكافرين على ما يريدون.',NULL,'589',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3341,'<span data-type="title" id=toc-3369>٨٧ </span>- ولا يصرفك الكافرون عن تبليغ آيات الله والعمل بها، بعد أن نزل بها الوحى عليك من الله وأصبحت رسالتك، وثابر على الدعوة إلى دين الله، ولا تكن أنت ولا من اتبعك من أنصار المشركين بإعانتهم على ما يريدون.',NULL,'589',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3342,'<span data-type="title" id=toc-3370>٨٨ </span>- ولا تعبد من دون الله إلهاً سواه، إذ ليس هناك إله يعبد بحق غيره، كل ما عدا الله هالك وفان، والخالد إنما هو الله الذى له القضاء النافذ فى الدنيا والآخرة، وإليه - لا محالة - مصير الخلق أجمعين.',NULL,'589',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3343,'<span data-type="title" id=toc-3372>١ </span>- ا. ل. م: حروف صوتية سيقت لبيان أن القرآن المعجز مؤلف من هذه الحروف التى يحسنون نطقها، ولتنبيه السامعين ولفت أنظارهم إلى الحق.',NULL,'590',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3344,'<span data-type="title" id=toc-3373>٢ </span>- أظن الناس أنهم يُتركون وشأنهم لنطقهم بالشهادتين دون أن يختبروا بما يبين به حقيقة إيمانهم من المحن والتكاليف؟ لا. بل لا بد من امتحانهم بذلك.',NULL,'590',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3345,'<span data-type="title" id=toc-3374>٣ </span>- ولقد اختبر الله الأمم السابقة بالتكاليف وألوان النعم والمحن، ليظهر ما سبق فى علمه القديم، ويتميَّز الصادقون فى إيمانهم من الكاذبين.',NULL,'591',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3346,'<span data-type="title" id=toc-3375>٤ </span>- أظن الذين يشركون بالله ويعصونه أن يسبقونا فى فرارهم من عذاب الله وعقابه؟! بئس حكمهم هذا.',NULL,'591',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3347,'<span data-type="title" id=toc-3376>٥ </span>- من كان يؤمن بالبعث ويرجو ثواب الله ويخاف عقابه فإيمانه حق، وليبادر إلى العمل الصالح، فإن اليوم الموعود آت لا محالة، والله سميع لأقوال العباد عليم بأفعالهم، وسيجزى كلا بما يستحق.',NULL,'591',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3348,'<span data-type="title" id=toc-3377>٦ </span>- ومن جاهد فى سبيل إعلاء كلمة الله، وجاهد نفسه بالصبر على الطاعة فإن ثواب جهاده لنفسه، وإن الله - سبحانه - لغنى عن طاعة العالمين.',NULL,'591',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3349,'<span data-type="title" id=toc-3378>٧ </span>- والذين اتصفوا بالإيمان وعملوا الصالحات لنُذهبن عنهم سيئاتهم ونغفر لهم، ونجزيهم أوفى جزاء على أعمالهم الصالحة.',NULL,'591',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3350,'<span data-type="title" id=toc-3379>٨ </span>- وأمر الله الإنسان أن يبالغ فى الإحسان إلى والديه وطاعتهما. وإن حملاك على الشرك بالله - وهو ما لا يقره علم ولا عقل - فلا تطعهما، وإلى الله مرجع الخلق كافة فينبئهم بما عملوا فى الدنيا ويجزيهم به.',NULL,'591',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3351,'<span data-type="title" id=toc-3380>٩ </span>- والذين صدَّقوا بالله ورسالاته، وعملوا الصالحات ليُدخلنهم الله فى الصالحين، ينالون جزاءهم ويأنسون بهم.',NULL,'591',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3352,'<span data-type="title" id=toc-3381>١٠ </span>- ومن الناس من يقول بلسانه: آمنا، فإذا أصابه أذى فى سبيل الله جزع وفُتن عن دينه، ولم يفكر فى عذاب الله يوم القيامة، فكأنه جعل إيذاء الناس كعذاب الله فى الآخرة. إذا نصر الله المؤمنين على عدوهم فغنموا منهم جاء هؤلاء المتظاهرون بالإيمان، وقالوا للمسلمين: إنا كنا معكم فى الإيمان، فأعطونا نصيباً من الغنيمة. لا ينبغى أن يظن هؤلاء أن أمرهم خافٍ على الله، فالله أعلم بما فى صدور الناس من نفاق وإيمان.',NULL,'592',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3353,'<span data-type="title" id=toc-3382>١١ </span>- وليظهرنَّ الله للناس سابق علمه، فيُمَيِّزُ بين المؤمنين والمنافقين، ويجازى كلا بما عمل.',NULL,'592',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3354,'<span data-type="title" id=toc-3383>١٢ </span>- وكان زعماء الشرك يقولون للذين دخلوا فى الإسلام مخلصين: كونوا كما كنتم على ديننا، واتبعوا ما نحن عليه، وإذا كان هناك بعث وحساب تخشونه فنحن نحمل عنكم آثامكم. لن تحمل نفس وزر نفس أخرى، إن الكافرين لكاذبون فى وعدهم.',NULL,'592',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3355,'<span data-type="title" id=toc-3384>١٣ </span>- وسوف يحمل الكفار أوزار أنفسهم الثقيلة، ويحملون معها مثل أوزار من أضلوهم وصرفوهم عن الحق، وسيحاسبون حتماً يوم القيامة على ما كانوا يختلقون فى الدنيا من الأكاذيب، ويعذبون بها.',NULL,'592',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3356,'<span data-type="title" id=toc-3385>١٤ </span>- ولقد بعث الله نوحاً إلى قومه يدعوهم إلى التوحيد، فمكث يدعوهم تسعمائة وخمسين سنة وهم لا يستجيبون له، فأغرقهم الله بالطوفان وهم ظالمون لأنفسهم بالكفر.',NULL,'592',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3357,'<span data-type="title" id=toc-3386>١٥ </span>- وحقق الله وعده لنوح، فأنجاه والمؤمنين الذين ركبوا معه السفينة، وجعل قصتهم عبرة لمن بعدهم.',NULL,'592',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3358,'<span data-type="title" id=toc-3387>١٦ </span>- واذكر - أيها الرسول - قصة إبراهيم حين دعا قومه إلى توحيد الله وطاعته، وَنَبَّهَهُم إلى أن الإيمان خير لهم من الكفر إن كانوا من ذوى العلم والعقل.',NULL,'593',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3359,'<span data-type="title" id=toc-3388>١٧ </span>- وقال لهم: أنتم لا تعبدون من دون الله إلا تماثيل وأصناماً تصنعونها بأيديكم، وتختلقون الكذب فتسمونها آلهة. وأن هذه الأوثان التى تعبدونها من دون الله لا تنفع ولا تضر ولا تستطيع لكم رزقاً، فالتمسوا الرزق من الله - وحده - وخُصُّوه بالعبادة والشكر له على أنعمه، فإليه مصيركم أجمعين فيجازيكم على أعمالكم.',NULL,'593',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3360,'<span data-type="title" id=toc-3389>١٨ </span>- وإن تستمروا على تكذيبى فلن تضرونى، فقد أبلغتكم أن الرسل قبلى كذبتهم أممهم وما ضروهم، وإنما ضروا أنفسهم إذ أهلكهم الله بسبب تكذيبهم، فليس على الرسول إلا أن يبلغ فى وضوح رسالته إلى قومه.',NULL,'593',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3361,'<span data-type="title" id=toc-3390>١٩ </span>- قد رأوا وعلموا أن الله يُبدئ الخلق ثم يُعيده، فكيف ينكرون البعث فى اليوم الآخر للحساب والجزاء؟ إن الإعادة على الله أسهل.',NULL,'593',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3362,'<span data-type="title" id=toc-3391>٢٠ </span>- قل - أيها الرسول - لهؤلاء المكذبين: امشوا فى الأرض، وتأملوا فيما أنشأ الله فيها من مختلف الكائنات، وانظروا إلى آثار من كان فيها قبلكم بعد أن ماتوا وخلت منهم ديارهم، واعلموا أن الله بقدرته سيعيد كل ذلك فى الآخرة بالبعث وهو الإنشاء الآخر، وكذلك شأنكم، إن الله - سبحانه - تام القدرة على كل شئ.',NULL,'593',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3363,'<span data-type="title" id=toc-3392>٢١ </span>- يعذب الله من يشاء بعد النشأة الآخرة وهم المنكرون لها، ويرحم من يشاء وهم المؤمنون المقرون بها، وإليه - وحده - مرجع الخلق جميعاً للحساب والجزاء.',NULL,'593',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3364,'<span data-type="title" id=toc-3393>٢٢ </span>- ولستم - أيها المكذبون - بغالبين لقدرة الله، سواء أكنتم فى الأرض أم فى السماء، بل هى محيطة بكم، وليس لكم ولىٌّ يمنعكم من الله ولا نصير يدفع عنكم عذابه.',NULL,'593',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3365,'<span data-type="title" id=toc-3394>٢٣ </span>- والذين كفروا بدلائل الله على وحدانيته، وكذَّبوا برسله وكتبه، وأنكروا البعث والحساب. هؤلاء ليس لهم مطمع فى رحمة الله وهؤلاء لهم عذاب شديد مؤلم.',NULL,'594',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3366,'<span data-type="title" id=toc-3395>٢٤ </span>- لم يكن جواب قوم إبراهيم له - حين أمرهم بعبادة الله وترك ما هم عليه من عبادة الأوثان - إلا الإمعان فى الكفر، وقوْل بعضهم لبعض: اقتلوه أو حَرِّقوه، فألقوه فى النار، فجعلها الله برداً وسلاماً عليه، وأنجاه منها، إن فى إحباط كيدهم وإنجائه منها، وعدم تأثيرها فيه لدلائل واضحة لقوم يصدقون بتوحيد الله وقدرته.',NULL,'594',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3367,'<span data-type="title" id=toc-3396>٢٥ </span>- وقال إبراهيم لقومه: لم تعبدوا إلا آلهة باطلة عبادتها. ثم يتبدل الحال يوم القيامة، فيتبرأ القادة من الأتباع، ويلعن الأتباع القادة، ومصيركم جميعاً النار، وليس لكم ناصر يمنعكم من دخولها.',NULL,'594',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3368,'<span data-type="title" id=toc-3397>٢٦ </span>- وكان أول من أجاب دعوة إبراهيم إلى الحق «لوط» فصدق وكان موحداً من قبل، وقال إبراهيم - مطيعاً لأمر الله -: إنى مهاجر إلى الجهة التى أمرنى ربى بالهجرة إليها والقيام بالدعوة إلى الله فيها. وهو العزيز الذى يمنعنى من أعدائى، الحكيم الذى لا يأمرنى إلا بما هو خير.',NULL,'594',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3369,'<span data-type="title" id=toc-3398>٢٧ </span>- ومَنَّ الله على إبراهيم بإسحاق ولده وبيعقوب حفيده، وكرَّمه بأن جعل النبوات فى ذريته، وأنزل عليهم الكتب السماوية، وجزاه الله أحسن الجزاء فى الدنيا، وهو فى الآخرة من خيار الصالحين.',NULL,'594',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3370,'<span data-type="title" id=toc-3399>٢٨ </span>- واذكر - أيها الرسول - إذ أرسلنا لوطاً إلى قومه، فدعاهم إلى توحيد الله وطاعته، وأنكر عليهم العمل الفاحش الذى كانوا يفعلونه ولم يسبقهم إلى فعله أحد من خلق الله.',NULL,'595',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3371,'<span data-type="title" id=toc-3400>٢٩ </span>- إن ما تفعلونه منكر مُهلك. فإنكم تفعلون الفاحشة بالرجال، وتقطعون سبيل النسل، فيكون المآل الفناء. وترتكبون فى مجتمعاتكم المنكرات دون خوف من الله ولا حياء فيما بينكم. فلم يستمع له قومه، ولم يكن لهم جواب غير السخرية به، وطلبوا منه أن يُعجِّل بعذاب الله يُهددهم به إن كان صادقاً فيما يقول.',NULL,'595',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3372,'<span data-type="title" id=toc-3401>٣٠ </span>- فاستعان لوط عليهم بالله، وطلب أن ينصره على قومه المفسدين فى الأرض.',NULL,'595',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3373,'<span data-type="title" id=toc-3402>٣١ </span>- وحين جاءت ملائكة الله إلى إبراهيم - عليه السلام - مبشرين، قالوا: إن أمرهم بإهلاك أهل هذه القرية بسبب إفسادهم وظلمهم أنفسهم بالشرك وارتكاب الفاحشة.',NULL,'595',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3374,'<span data-type="title" id=toc-3403>٣٢ </span>- قال إبراهيم - عليه السلام - للملائكة: إن فى القرية لوطاً، وكيف تهلكونهم وهو فيهم؟ فأجابته الملائكة: بأنهم يعلمون من فيها، وأنهم ينجون لوطاً وأهله من العذاب، إلا امرأته فإنها فى الهالكين لكفرها وإساءتها.',NULL,'595',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3375,'<span data-type="title" id=toc-3404>٣٣ </span>- ولما ذهب الملائكة المرسلون إلى لوط ورآهم حزن، لخوفه عليهم من عدوان قومه، وعجزت حيلته فيما يتعلق بحمايتهم، فطمأنوه وقالوا له: لا تخْشَيَنَّ عدوان قومك علينا، ولا تحزن من أجلنا، فقد أتينا لإهلاك أهل هذه القرية، وسننجيك وأهلك، ولكن امرأتك لكفرها ستكون مع الهالكين.',NULL,'595',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3376,'<span data-type="title" id=toc-3405>٣٤ </span>- وقالت الملائكة: إننا مرسَلُون لتنفيذ أمر الله بإنزال العذاب من السماء على سكان هذه القرية بسبب فسقهم وكفرهم.',NULL,'596',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3377,'<span data-type="title" id=toc-3406>٣٥ </span>- ولقد أهلك الله هذه القرية وترك منها آثاراً ظاهرة، لتكون دليلا على ما فعله الله بهم، وعبرة لمن يتدبَّر.',NULL,'596',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3378,'<span data-type="title" id=toc-3407>٣٦ </span>- وأرسل الله إلى أهل مدين رسولا منهم هو شعيب، دعاهم إلى توحيد الله وعبادته والخوف من اليوم الآخر، وفِعْل ما يرجون به ثواب الله فيه. ونهاهم عن السعى فى الأرض بالفساد.',NULL,'596',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3379,'<span data-type="title" id=toc-3408>٣٧ </span>- فكذبوه وعصوه، فأهلكهم الله بزلزال شديد دمَّر عليهم مساكنهم، فغدوا فيها صرعى ميتين.',NULL,'596',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3380,'<span data-type="title" id=toc-3409>٣٨ </span>- واذكر - أيها الرسول - مصارع عاد وثمود إذ أهلكناهم، وقد بقيت من مساكنهم آثار ظاهرة ترونها، وكان هذا الهلاك بسبب ما زين لهم الشيطان من أعمالهم الباطلة فاتبعوه، فصرفهم عن طريق الحق الذى كانوا يعرفونه بواسطة الرسل.',NULL,'596',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3381,'<span data-type="title" id=toc-3410>٣٩ </span>- واذكر - أيها الرسول - لهؤلاء المغترين بأموالهم وسلطانهم مصرع قارون وفرعون وهامان وما جرى عليهم من سنة الله بإهلاك المكذبين، وقد بعث الله إليهم موسى بالمعجزات الظاهرة الدالة على صدقه، فكذبوه وأبوا أن يستجيبوا له استكباراً، وما كانوا غالبين لقدرة الله بالإفلات من عذابه.',NULL,'596',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3382,'<span data-type="title" id=toc-3411>٤٠ </span>- فكل أمة من هذه الأمم المكذبة برسلها أهلكها الله بسبب كفرها وما ارتكبت من المعصية، فبعض هذه الأمم أهلكه الله بالريح العاصفة التى حصبتهم بالحجارة، وبعضهم هلك بالصيحة المدوية المهلكة، وبعضهم خسف الله به الأرض، وبعضهم أغرقه الله فى اليم. ولم يكن هذا العذاب ظلماً من الله لهم، بل كان بسبب كفرهم وارتكابهم الذنوب.',NULL,'597',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3383,'<span data-type="title" id=toc-3412>٤١ </span>- شأن المبطلين الموالين لغير الله فى الضعف والوهن والاعتماد على غير معتمد؛ كشأن العنكبوت فى اتخاذها بيتاً تحتمى به، وبيتها أوهى البيوت وأبعد عن الصلاحية للاحتماء، ولو كان هؤلاء المبطلون أهل علم وفطنة لما فعلوا ذلك.',NULL,'597',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3384,'<span data-type="title" id=toc-3413>٤٢ </span>- إن الله - سبحانه - محيط علماً ببطلان عبادة الآلهة، وهو - سبحانه - الغالب على كل شئ الحكيم فى تدبيره وتشريعه.',NULL,'597',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3385,'<span data-type="title" id=toc-3414>٤٣ </span>- وهذه العبر والأمثال يذكرها الله للناس للعظة والاعتبار، وما يعتبر بها إلا العقلاء الذين يتدبرون.',NULL,'597',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3386,'<span data-type="title" id=toc-3415>٤٤ </span>- وبجانب ما ذكر الله من القصص والأمثال والآيات آية أوضح، هى خلق السموات والأرض بالقدرة والحكمة والتدبير الكامل لصالح الناس، وفى هذا دلائل صادقة لمن يؤمنون بالحق.',NULL,'597',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3387,'<span data-type="title" id=toc-3416>٤٥ </span>- اقرأ - أيها النبى - كتاب الله، ولا تلتفت إليهم، وأد الصلاة على وجهها، لأن الصلاة مع الإخلاص من شأنها أن تصرف من يقيمها عن الذنوب وكل ما ينكره الشرع. ولتقوى الله ومراقبته فى الصلاة وغيرها أكبر أثراً وأعظم ثواباً. والله يعلم ما تفعلون من الخير والشر فيجازيكم عليه.',NULL,'597',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3388,'<span data-type="title" id=toc-3417>٤٦ </span>- ولا تجادلوا مخالفيكم من اليهود والنصارى إلا بالطريقة التى هى أهدأ وألين وأدعى إلى القبول. إلا الذين جاوزوا حد الاعتدال فى الجدال فلا حرج فى مقابلتهم بالشدة، وقولوا لمن تجادلونهم: صدَّقنا بما أنزل الله إلينا من القرآن وما أنزل إليكم من التوراة والإنجيل، ومعبودنا ومعبودكم واحد، ونحن له - وحده - منقَادون.',NULL,'598',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3389,'<span data-type="title" id=toc-3418>٤٧ </span>- وكما أنزلنا الكتب على - من قبلك من الرسل - أنزلنا إليك القرآن، فالذين آتيناهم الكتاب قبل القرآن فتدبروه واهتدوا به يؤمنون بهذا القرآن. ومن هؤلاء العرب من يؤمن به، وما يُنكر آياتنا - بعد ظهورها وزوال الشبهة عنها - إلا المصِرُّون على الكفر.',NULL,'598',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3390,'<span data-type="title" id=toc-3419>٤٨ </span>- وما كنت تقرأ كتاباً من الكتب قبل القرآن، ولا كنت تكتب بيمينك، ولو كنت ممن يقرأ ويكتب لشك أهل الباطل فى أنه من عند الله.',NULL,'598',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3391,'<span data-type="title" id=toc-3420>٤٩ </span>- ليس هذا الكتاب موْضع ارتياب، بل هو آيات واضحات محفوظة فى صدور الذين آتاهم الله العلم، وما يُنكر آياتنا - بعد العلم بها - إلا الظالمون للحق ولأنفسهم.',NULL,'598',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3392,'<span data-type="title" id=toc-3421>٥٠ </span>- وقال الكفار فى جدالهم ولجاجهم: هلا أُنزل عليه معجزات حسية كالتى نزلت على الرسل من قبل؟ . قل لهم: إنما المعجزات كلها من عند الله، ينزلها حين يشاء، وإنما أنا مكلف بالإنذار الواضح، لا الإتيان بما تقترحون.',NULL,'598',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3393,'<span data-type="title" id=toc-3422>٥١ </span>- أيقترحون هذه الآيات ولا يكفيهم أنَّا أنزلنا عليك الكتاب يُقرأ عليهم - وهو الآية الخالدة على مر الزمن - إنَّ فى إنزال هذا الكتاب عليكم لرحمة بهم وبالأجيال من بعدهم، وتذكرة دائمة نافعة لقوم شأنهم أن يؤمنوا إذا وضحت لهم سبل الهداية.',NULL,'598',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3394,'<span data-type="title" id=toc-3423>٥٢ </span>- قل: حسبى وحسبكم أن يكون الله شاهداً على أنَّى قد بلَّغتكم ما أُرسلت به إليكم، فهو مطلع على أمرى وأمركم، لا يخفى عليه شئ فى السموات والأرض. والذين عبدوا غير الله وكفروا بالله فلم يخصوه بالعبادة؛ أولئك هم الذين اشتروا الكفر بالإيمان فأصابهم الخسران المبين.',NULL,'599',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3395,'<span data-type="title" id=toc-3424>٥٣ </span>- ويتحداك الكافرون أن تعجِّل لهم العذاب الذى حذرتهم منه، ولولا أجل معلوم قضت به حكمتنا لعجَّلنا لهم العذاب الذى استعجلوه، وأقسم ليأتينهم فجأة وهم لا يشعرون.',NULL,'599',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3396,'<span data-type="title" id=toc-3425>٥٤ </span>- يطلبون إليك تعجيل العذاب وهو واقع بهم لا محالة. وإن جهنم لتحيط - يقيناً - بالكافرين.',NULL,'599',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3397,'<span data-type="title" id=toc-3426>٥٥ </span>- يوم يغمرهم العذاب من أعلاهم ومن أسفلهم، ويقول الملك الموكل بعذابهم: ذوقوا جزاء ما كنتم تعملون من السيئات.',NULL,'599',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3398,'<span data-type="title" id=toc-3427>٥٦ </span>- يا عبادى الذين صدَّقوا بى وبرسولى: إنّ أرضى واسعة لمن أراد أن يفر عن مواطن الشرك. ففروا إلى مخلصين لى العبادة.',NULL,'599',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3399,'<span data-type="title" id=toc-3428>٥٧ </span>- كل نفس ستذوق طعم الموت - لا محالة - ثم إلينا تعودون فَتُجْزَوْن بما قدمتم من خير وشر.',NULL,'599',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3400,'<span data-type="title" id=toc-3429>٥٨ </span>-، ٥٩ - والذين صدَّقوا بالله وكتبه ورسله، وعملوا الأعمال الصالحة، نقسم: لننزلنَّهم من دار النعيم غرفات تجرى من تحتها الأنهار، لا ينقطع عنهم نعيمها، نعم هذا الجزاء أجراً للعاملين الصابرين على كل ما يصيبهم فى سبيل الله من فراق الأوطان والأهل والأموال، المعتمدين على الله - وحده - فى جميع أحوالهم.',NULL,'599',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3401,'<span data-type="title" id=toc-3430>٦٠ </span>- وكثير من الدواب التى تعيش معكم فى الأرض لا تستطيع - لضعفها - أن تحمل رزقها وتنقله، لتأكله أو تدخره. الله يهيئ لها أسباب رزقها وحياتها، ويهيئ لكم أسباب رزقكم وحياتكم. وهو المحيط بكل ما خلق سمعاً وعلماً.',NULL,'599',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3402,'<span data-type="title" id=toc-3431>٦١ </span>- وأقسم إن سألت المشركين: من أوجد السموات والأرض، وذلل الشمس والقمر وأخضعهما لمنافع الناس؟ ليقولون: خلقهن الله، ولا يذكرون أحدا سواه، فكيف إذن ينصرفون عن توحيد الله - تعالى - مع إقرارهم بهذا كله؟! .',NULL,'600',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3403,'<span data-type="title" id=toc-3432>٦٢ </span>- الله يُوسِّع على مَن يشاء فى الرزق، ويُضيِّقُ على مَن يشاء حسبما يقتضيه علمه بالمصالح، فإن الله قد أحاط بكل شئ علما.',NULL,'600',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3404,'<span data-type="title" id=toc-3433>٦٣ </span>- وأقسم إن سألتهم: من نزَّل من السماء ماء فجعل منه حياة الأرض بالنبات بعد جدبها؟ ليقولن: الله. قل: الحمد لله على اعترافهم بالحق، بل أكثرهم لا يفهمون ما يقعون فيه من تناقض.',NULL,'600',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3405,'<span data-type="title" id=toc-3434>٦٤ </span>- وليست هذه الحياة الدنيا إلا متاعاً محدود الوقت، يلهو به الغافلون كما يلهو الصبيان ويلعبون وقتاً ما ثم ينفضون. وإن الدار الآخرة لهى دار الحياة الحقيقية الكاملة الدائمة، وهذه حقائق ثابتة يدركها هؤلاء لو كان من شأنهم الإدراك الصحيح.',NULL,'600',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3406,'<span data-type="title" id=toc-3435>٦٥ </span>- هم على ما وُصفوا به من شرك، فإذا ركبوا السفن فى البحر وأدركهم شئ من أهواله توجهوا إلى الله مخلصين له الدعاء أن يكشف عنهم الضر، فلما نجاهم إلى البر سارعوا بالعودة إلى الإشراك.',NULL,'600',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3407,'<span data-type="title" id=toc-3436>٦٦ </span>- لينكروا ما أعطيناهم من النعم، ولينتفعوا بما يرضى هواهم فى هذه الحياة، فسوف يعلمون عاقبة الكفر حين يشاهدون العذاب الأليم.',NULL,'600',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3408,'<span data-type="title" id=toc-3437>٦٧ </span>- أَعَمِى كفار مكة عن نعم الله التى أسبغها عليهم، أَوَ لَمْ يروا أنا جعلنا بلدهم مصوناً لا يُنهب ولا يُسْلَب، مقدسا لا يُسْبَى أهله ولا يقع فيه قتل، ويُسْلَب الناس ويُسبون من حولهم؟ {أعموا عن هذه النعم. فَبمَ لا أصل له يصدقون، وبمحمد وبكل ما جاء به يكذبون؟} .',NULL,'601',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3409,'<span data-type="title" id=toc-3438>٦٨ </span>- وليس هناك أحد أشد ظلماً ممن نسب إلى الله ما لم يشرعه، أو كذَّب بالدين الحق حين بلغه، إن فى جهنم لمأوى لهؤلاء الظالمين الكافرين.',NULL,'601',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3410,'<span data-type="title" id=toc-3439>٦٩ </span>- والذين بذلوا جهدهم، واحتملوا المشقة فى نصرة ديننا، لنزيدنَّهم هداية إلى الخير والحق. وإن الله لمع الذين يحسنون أعمالهم، يعينهم وينصرهم. والله أعلم.',NULL,'601',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3411,'<span data-type="title" id=toc-3441>١ </span>- بدأت السورة بهذه الآية لبيان أن القرآن مؤلف من هذه الحروف التى ينطق بها العرب فى سهولة ووضوح، ولكن المنكرين له عجزوا عن الإتيان بمثله. وهى - كذلك - تُنَبِّه الناس إلى الاستماع والإنصات. وتحملهم على التصديق برسالة محمد صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ.',NULL,'602',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3412,'<span data-type="title" id=toc-3442>٢ </span>-، ٣ - غَلَبت فارس الروم فى أقرب الأرض من العرب، وهى أطراف الشام، وهم بعد انهزامهم سيغلبون فارس.',NULL,'602',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3413,'<span data-type="title" id=toc-3443>٤ </span>-، ٥ - قبل أن تمضى تسع سنوات - وكان المشركون قد فرحوا بانتصار فارس، وقالوا للمسلمين: سنغلبكم كما غلبت فارس الروم التى هى من أهل الكتاب - قد حقَّق الله وعده، فانتصر الروم على فارس فى الأجل الذى سمَّاه، فكان ذلك آية بينة على صدق محمد (فى دعواه وصحة ما جاء به، لله الأمر والقضاء من قبل كل شئ ومن بعد كل شئ، ويوم ينتصر الروم على فارس يفرح المؤمنون بنصر الله الذى يؤيد من يشاء، وهو الغالب على أعدائه، الرحيم بأوليائه.',NULL,'603',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3414,'<span data-type="title" id=toc-3444>٦ </span>- وعد الله المؤمنين وعداً صادقاً - لا يخلف الله وعده - ولكن الجاحدين ليس من شأنهم العلم بالأمور على وجهها.',NULL,'603',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3415,'<span data-type="title" id=toc-3445>٧ </span>- يعلمون شئون ووسائل عمرانها والتمتع بزخارفها، وهم عن التزود للآخرة مسرفون فى الجهل والغفلة.',NULL,'603',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3416,'<span data-type="title" id=toc-3446>٨ </span>- أَطُمِسَ على أعينهم وقلوبهم ولم يتفكروا فى أمر أنفسهم ليعرفوا مصيرهم؟ ما خلق الله السموات والأرض وما بينهما من كواكب وغيرها إلا مقرونة بالجد، مصحوبة ومحدودة بوقت تنتهى عنده، وإن كثيراً من الناس بلقاء الله وقيام الساعة لجاحدون.',NULL,'603',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3417,'<span data-type="title" id=toc-3447>٩ </span>- أَلَزِمُوا وطنهم ولم يسيروا فى أرجاء الأرض ليشاهدوا كيف كانت نهاية الذين كفروا من قبلهم؟ كانوا أشد من هؤلاء الكافرين الحاضرين قوة، وقلبوا وجه الأرض، ليستخرجوا ما فيها من مياه ومعادن وزروع، وعمروا الأرض أكثر مما عمرها هؤلاء، وجاءتهم رسل الله بالمعجزات الواضحات فكفروا، فأخذهم الله - لأنه ما كان ليجزيهم من غير ذنب، ولا ليأخذهم قبل تذكيرهم وإمهالهم - ولكن كان هؤلاء لا يظلمون إلا أنفسهم.',NULL,'604',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3418,'<span data-type="title" id=toc-3448>١٠ </span>- ثم كانت نهاية الذين ارتكبوا أشد ألوان الإساءة أن جحدوا آيات الله، وكانوا يُحقِّرون من شأنها.',NULL,'604',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3419,'<span data-type="title" id=toc-3449>١١ </span>- الله - سبحانه وتعالى - ينشئ خلق الناس ابتداء، ثم يُعيد خلقهم بعد موتهم، ثم إليه - وحده - يعودون للحساب والجزاء.',NULL,'604',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3420,'<span data-type="title" id=toc-3450>١٢ </span>- ويوم تأتى القيامة ييأس الكافرون من الدفاع عن أنفسهم.',NULL,'604',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3421,'<span data-type="title" id=toc-3451>١٣ </span>- ولم يوجد لهم من الذين عبدوهم مع الله شفعاء، وكانوا فى الدنيا بسببهم كافرين.',NULL,'604',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3422,'<span data-type="title" id=toc-3452>١٤ </span>- ويوم تقوم الساعة - يوم إذ تقوم - يذهب كل فريق إلى مصيره الأبدى.',NULL,'604',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3423,'<span data-type="title" id=toc-3453>١٥ </span>- فأما الذين آمنوا وقرنوا إيمانهم بالأعمال الصالحة؛ فهم فى جنة ذات أشجار وأزهار يسرون وينعمون.',NULL,'604',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3424,'<span data-type="title" id=toc-3454>١٦ </span>- وأمَّا الذين كفروا وأنكروا آياتنا ولقاء البعث والحساب؛ فأولئك فى العذاب مقيمون لا يغيبون عنه.',NULL,'604',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3425,'<span data-type="title" id=toc-3455>١٧ </span>- فنزِّهوا الله عما لا يليق بجلاله وكماله واعبدوه حين تدخلون فى المساء وحين تدخلون فى الصباح.',NULL,'604',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3426,'<span data-type="title" id=toc-3456>١٨ </span>- والله - وحده - هو الحقيق بالحمد والثناء والشكر من أهل السموات والأرض، فاحمدوه واعبدوه فى العشى، وحين تدخلون فى الظهيرة.',NULL,'604',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3427,'<span data-type="title" id=toc-3457>١٩ </span>- يُخرج الكائن الحى من شئ لا حياة فيه، ويُخرج الشئ الذى لا حياة فيه من الكائن الحى، ويُحيى الأرض بالنبات بعد يبسها، ومثل هذا الإخراج يُخرجكم الله من قبوركم.',NULL,'605',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3428,'<span data-type="title" id=toc-3458>٢٠ </span>- ومن الدلائل على كمال قدرته أن خلق أصلكم من تراب لا حياة فيه، ثم أنتم بشر تتفرقون فى الأرض للسعى فى تحصيل ما به بقاؤكم.',NULL,'605',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3429,'<span data-type="title" id=toc-3459>٢١ </span>- ومن دلائل رحمته أن خلق لكم - أيها الرجال - زوجات من جنسكم لتألفوهن، وجعل بينكم وبينهن مودة وتراحماً. إن فى ذلك لدلائل لقوم يفكرون فى صنع الله تعالى.',NULL,'605',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3430,'<span data-type="title" id=toc-3460>٢٢ </span>- ومن الدلائل على كمال قدرته وحكمته خلق السموات والأرض على هذا النظام البديع، واختلاف ألسنتكم فى اللغات واللهجات، وتباين ألوانكم فى السواد والبياض وغيرهما. إن فى ذلك لدلائل ينتفع بها أهل العلم والفهم.',NULL,'605',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3431,'<span data-type="title" id=toc-3461>٢٣ </span>- ومن آياته الدالة على كمال قدرته أن هيأ لكم أسباب الراحة بمنامكم، ويسَّر لكم طلب الرزق ليلا ونهارا من فضله الواسع. إن فى ذلك لدلائل لقوم ينتفعون بما يسمعون.',NULL,'605',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3432,'<span data-type="title" id=toc-3462>٢٤ </span>- ومن آياته أنه يريكم البرق من خلال السحب لتشعروا بالخوف من الصواعق، وتطمعوا فى المطر أن ينزل من السماء لتحيا به الأرض بعد أن يبست. إن فى ذلك لدلالات لقوم يتدبَّرون الأمور فيفهمونها على وجهها.',NULL,'605',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3433,'<span data-type="title" id=toc-3463>٢٥ </span>- ومن الدلائل على كمال قدرته وحكمته وسعة رحمته أن تقوم السماء والأرض بأمر الله على ما ترون من إحكام صنع ودقة تدبير، ثم إذا دعاكم للبعث تخرجون من القبور مسرعين مستجيبين لدعائه.',NULL,'605',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3434,'<span data-type="title" id=toc-3464>٢٦ </span>- ولله - سبحانه وتعالى - كل من فى السموات والأرض خلْقاً وملكاً وخضوعاً، كلهم لله منقادون.',NULL,'605',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3435,'<span data-type="title" id=toc-3465>٢٧ </span>- والله - سبحانه - الذى يبدأ الخلق على غير مثال، ثم يعيده بعد الموت، وإعادته أهون عليه من ابتدائه بالنظر إلى مقاييسكم واعتقادكم أن إعادة الشئ أسهل من ابتدائه. ولله الوصف السابق العجيب الشأن فى القدرة الكاملة والحكمة التامة فى السموات والأرض، وهو الغالب فى ملكه الحكيم فى فعله وتقديره.',NULL,'606',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3436,'<span data-type="title" id=toc-3466>٢٨ </span>- بيَّن الله لكم مثلا مُنْتزعاً من أنفسكم وقد ضربه الله - عَزَّ وَجَلَّ - لمن جعل له شريكاً من خلقه: هل لكم من عبيدكم شركاء فيما ملّكناكم من الأموال وغيرها؟ فأنتم وهم مستوون فيها، تخافون هؤلاء العبيد فلا تتصرفون فى شئ مما تملكون دون إذنهم كما يخاف الأحرار بعضهم بعضاً، فإذا كنتم لا تعقلون هذا ولا تفعلونه، فكيف تجعلون بعض مملوكات الله شركاء له؟ مثل هذا التفصيل نبين الآيات لقوم يتدبرون فى ضرب الأمثال.',NULL,'606',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3437,'<span data-type="title" id=toc-3467>٢٩ </span>- بل اتبع الذين كفروا أهواءهم دون علم بعاقبة كفرهم، فلا أحد يهدى من أضل الله، وليس لهم من يشفع أو يدفع عنهم عذابه.',NULL,'606',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3438,'<span data-type="title" id=toc-3468>٣٠ </span>- فسدد وجهك واتجه إلى الدين بعيداً عن ضلالتهم، والزم خلقة الله التى خلق الناس عليها، وهى أنهم قابلون للتوحيد غير منكرين له، وما ينبغى أن تغير هذه الخلقة. ذلك الخلق على التوحيد هو الدين المستقيم، ولكن المشركين لا يعلمون حقيقة ذلك.',NULL,'606',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3439,'<span data-type="title" id=toc-3469>٣١ </span>- كونوا راجعين إليه، وافعلوا ما أمركم به، واتركوا ما نهاكم عنه، وحافظوا على الصلاة، ولا تكونوا من الذين عبدوا مع الله غيره.',NULL,'606',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3440,'<span data-type="title" id=toc-3470>٣٢ </span>- من الذين فرَّقوا دينهم فاختلفوا فيه، وصاروا فرقاً كل فرقة تشايع من تتبعه، كل فريق منهم بما عندهم مسرورون، يظنون أنهم على الحق.',NULL,'606',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3441,'<span data-type="title" id=toc-3471>٣٣ </span>- وإذا أصاب الناس ضر - من مرض أو شدة - التجأوا إلى الله ودعوه راجعين إليه، طالبين كشف الشدة عنهم، ثم إذا أذاقهم الله خلاصاً من الشدة ومنحهم من فضله سارع فريق منهم بربهم يشركون.',NULL,'606',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3442,'<span data-type="title" id=toc-3472>٣٤ </span>- لتكون عاقبة أمرهم أن يكفروا بما آتاهم الله من النعم، فتمتعوا - أيها الجاحدون - كما تشاءون، فسوف تعرفون عاقبتكم.',NULL,'607',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3443,'<span data-type="title" id=toc-3473>٣٥ </span>- أتركناهم فى ضلالهم ولم نسفه أحلامهم؟ بل أنزلنا عليهم بُرهانا فهو يشهد بالذى كانوا يشركونه مع الله.',NULL,'607',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3444,'<span data-type="title" id=toc-3474>٣٦ </span>- وإذا أذقنا الناس نعمة فرحوا بها فرحاً يُبطرهم، وإن تصبهم شدة بسبب ما اقترفوا من ذنوب يسارع إليهم اليأس من الرحمة.',NULL,'607',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3445,'<span data-type="title" id=toc-3475>٣٧ </span>- أجهلوا ما يوصل إلى الإيمان، ولم يعلموا أن الله يوسع الرزق لمن يشاء ويُضيق على من يشاء، بحسب ما تقتضيه حكمته؟ إن فى ذلك لدلائل واضحة لقوم يصدقون بالحق.',NULL,'607',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3446,'<span data-type="title" id=toc-3476>٣٨ </span>- وإذا كان الله - تعالى - هو الذى يبسط الرزق ويقدره؛ فأعط القريب حقه من البر والصلة والمحتاج والمنقطع به الطريق حقهما من الزكاة والصدقة، ذلك خير للذين يريدون رضا الله ويطلبون ثوابه، وأولئك هم الفائزون بالنعيم المقيم.',NULL,'607',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3447,'<span data-type="title" id=toc-3477>٣٩ </span>- وما أعطيتم أكلة الربا من مال ليزيد لكم فى أموالهم فلا يَزْكو عند الله ولا يبارك فيه، وما أعطيتم من صدقة تبتغون بها وجه الله - بدون رياء ولا طمع فى مكافأة - فأولئك هم أصحاب الأضعاف من الحسنات.',NULL,'607',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3448,'<span data-type="title" id=toc-3478>٤٠ </span>- الله - سبحانه - الذى أوجدكم، ثم أعطاكم ما تعيشون به، ثم يميتكم ثم يبعثكم من قبوركم. هل هناك من الشركاء - الذين تزعمونهم فتعبدونهم من دون الله - من يفعل من الخلق والرزق والإماتة والإحياء شيئاً من تلك الأفعال؟ تنزه الله وتعالى عما يشركون به.',NULL,'607',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3449,'<span data-type="title" id=toc-3479>٤١ </span>- ظهر الحرق والقحط والآفات وكساد التجارة والغرق بسبب ما فعله الناس من جرائم وآثام، ليعاقب الله الناس فى الدنيا ببعض أعمالهم لعلهم يرجعون عن المعاصى.',NULL,'608',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3450,'<span data-type="title" id=toc-3480>٤٢ </span>- قل - يا أيها النبى - للمشركين: سيروا فى نواحى الأرض، فانظروا كيف كانت نهاية الذين مضوا قبلكم، فسترون أن الله أهلكهم وخرَّب ديارهم، لأن أكثرهم كانوا مشركين مثلكم.',NULL,'608',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3451,'<span data-type="title" id=toc-3481>٤٣ </span>- فأخلص عبادتك للإله الواحد ليصح إسلامك ولا تكونن من المشركين، من قبل أن يأتى يوم لا يستطيع أحد أن يرده من الله، يومئذٍ يتفرق الناس وتختلف حالهم.',NULL,'608',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3452,'<span data-type="title" id=toc-3482>٤٤ </span>- من كفر بالله فعليه وبال كفره، ومن آمن وعمل صالحاً فلأنفسهم - وحدها - يسوون طريق النعيم المقيم.',NULL,'608',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3453,'<span data-type="title" id=toc-3483>٤٥ </span>- لأن الله يجزى الذين آمنوا وعملوا الصالحات على ما قدَّموا ويزيد جزاءهم تفضلا منه، لأنه يحبهم، ويُبغض الذين كفروا به وأنكروا نعمه.',NULL,'608',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3454,'<span data-type="title" id=toc-3484>٤٦ </span>- ومن الدلائل على قدرة الله ورحمته أنه يبعث الرياح مبشرات بالمطر الذى يكون لكم رِياً وسقيا، وليهبكم من فيض إحسانه المنافع التى نشأت من المطر، ولتجرى السفن فى الماء بأمر الله وقدرته، ولتطلبوا الرزق من فضله بالتجارة واستغلال ما فى البر والبحر، ولتشكروا لله نعمه بطاعتكم له وعبادتكم إيَّاه.',NULL,'608',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3455,'<span data-type="title" id=toc-3485>٤٧ </span>- ولقد أرسلنا من قبلك رسلا إلى قومهم، فجاء كل رسول قومه بالحُجج الواضحة الدالة على صدقه فكذَّبه قومه، فأهلكنا الذين أذنبوا وعصوا. وقد أوجب الله على نفسه أن ينصر عباده المؤمنين.',NULL,'609',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3456,'<span data-type="title" id=toc-3486>٤٨ </span>- الله - سبحانه وتعالى - الذى يُرسل الرياح فتحرك بقوة دفعها السحاب، فيبسطه الله فى السماء كيف يشاء هنا وهناك فى قلة أو كثرة، ويجعله قطعاً، فترى المطر يخرج من بين السحاب، فإذا أنزل الله المطر على من يشاء من عباده يسارعون إلى البِشْر والفرح.',NULL,'609',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3457,'<span data-type="title" id=toc-3487>٤٩ </span>- وإنهم كانوا قبل أن ينزل بهم المطر لفى يأس وحيرة.',NULL,'609',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3458,'<span data-type="title" id=toc-3488>٥٠ </span>- فانظر نظر تفكر وتدبر إلى آثار المطر، كيف يحيى الله الأرض بالنبات بعد أن كانت هامدة كالميت، إن الذى قَدِر على إحياء الأرض بعد موتها لقادر على إحياء الموتى من الناس، وهو تام القدرة لا يعجزه شئ.',NULL,'609',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3459,'<span data-type="title" id=toc-3489>٥١ </span>- وأقسم: لئن أرسلنا ريحاً مضرة بالنبات فرأوه مصفراً بسببها، لصاروا من بعد اصفراره يجحدون النعمة ويكفرون بالله.',NULL,'609',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3460,'<span data-type="title" id=toc-3490>٥٢ </span>- فلا تحزن من عنادهم وعدم استجابتهم لك، فأنت لا تستطيع أن تُسمع الموتى دعاءك، ولا أن تُسمع الصم نداءك، إذا زادوا على صممهم بأن فروا عنك معرضين.',NULL,'609',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3461,'<span data-type="title" id=toc-3491>٥٣ </span>- وهؤلاء كالعمى لإغلاقهم قلوبهم عن الاستجابة للهدى، وأنت لا تستطيع هداية من فقدوا البصر والبصيرة وتحول بينهم وبين كفرهم، وإنما تسمع سماع فهم وقبول من تهيأت قلوبهم لتلقى الإيمان. فهؤلاء ينقادون للحق متى ظهر.',NULL,'609',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3462,'<span data-type="title" id=toc-3492>٥٤ </span>- الله الذى خلقكم من نطفة فنشأتم ضعافا، ثم جعل لكم من بعد هذا الضعف قوة بنموكم وبلوغكم حد الرشد، ثم جعل لكم من بعد هذه القوة ضعف الشيخوخة والشيب، يخلق ما يشاء وهو العليم بتدبير خلقه القدير على إيجاد ما يشاء.',NULL,'610',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3463,'<span data-type="title" id=toc-3493>٥٥ </span>- ويوم تقوم الساعة يحلف الكافرون أنهم ما لبثوا فى الدنيا أو فى قبورهم غير ساعة، ومثل ذلك التصرف كانت تصرفهم الشياطين فى الدنيا عن الحق إلى الباطل.',NULL,'610',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3464,'<span data-type="title" id=toc-3494>٥٦ </span>- وقال الذين آتاهم الله العلم من الأنبياء والملائكة والمؤمنين: لقد لبثتم فى حكم الله وقضائه إلى يوم البعث. فهذا يوم البعث الذى أنكرتموه، ولكنكم كنتم فى الدنيا لا تعلمون أنه حق، لجهالتكم وإعراضكم.',NULL,'610',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3465,'<span data-type="title" id=toc-3495>٥٧ </span>- فيومئذ يبعث الناس لا ينفع الذين كفروا اعتذارهم عن إنكارهم وتكذيبهم لرسلهم، ولا يطلب منهم أحد أن يفعلوا ما يرضى الله لهوانهم عنده وطردهم من رحمته.',NULL,'610',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3466,'<span data-type="title" id=toc-3496>٥٨ </span>- ولقد بيَّنا لهداية الناس فى هذا القرآن كل مثل يرشدهم إلى طريق الهدى، ولئن أتيتهم بآية معجزة ليقولن الذين كفروا - من فرط عنادهم وقسوة قلوبهم -: ما أنت وأتباعك إلا مبطلون فى دعواكم.',NULL,'610',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3467,'<span data-type="title" id=toc-3497>٥٩ </span>- ومثل ذلك يكون الطبع على قلوب هؤلاء الذين لا يعلمون التوحيد من الجاهلين.',NULL,'610',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3468,'<span data-type="title" id=toc-3498>٦٠ </span>- فاصبر - أيها النبى - على أذاهم، إن وعد الله بنصرك على أعدائك وإظهار الإسلام على كل دين حق لا يتخلف أبداً، ولا يحملنك على القلق وعدم الصبر الذين لا يؤمنون بالله ورسوله.',NULL,'610',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3469,'<span data-type="title" id=toc-3500>١ </span>- الم: هذه حروف ابتدأ الله بها بعض السور، ليشير بها إلى إعجاز القرآن المؤلف من حروف كالحروف التى يؤلف منها العرب كلامهم، ومع ذلك عجزوا عن الإتيان بمثله، ولينبِّه إلى الاستماع والإنصات، وكان المشركون قد اتفقوا على أن يَلْغوا فيه ولا يسمعوا.',NULL,'611',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3470,'<span data-type="title" id=toc-3501>٢ </span>- هذه الآيات العظيمة آيات القرآن المشتمل على الحكمة والصواب.',NULL,'611',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3471,'<span data-type="title" id=toc-3502>٣ </span>- هذه الآيات هداية كاملة ورحمة شاملة لمن يحسنون العمل.',NULL,'611',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3472,'<span data-type="title" id=toc-3503>٤ </span>- هم الذين يؤدون الصلاة على أكمل وجه، ويعطون الزكاة لمستحقيها، وهم بالحياة الآخرة يؤمنون أقوى الإيمان.',NULL,'612',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3473,'<span data-type="title" id=toc-3504>٥ </span>- أولئك المؤمنون المحسنون فى أعمالهم متمكنون من الهدى الذى جاءهم من ربهم، وأولئك هم - دون غيرهم - الفائزون حقاً.',NULL,'612',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3474,'<span data-type="title" id=toc-3505>٦ </span>- ومن الناس من يشترى باطل الحديث ويقصُّه على الناس، ليصدَّهم عن الإسلام والقرآن جهلا منه بما عليه من إثم، ويتخذ دين الله ووحيه سخرية. الذين يفعلون ذلك لهم عذاب يهينهم ويذلهم.',NULL,'612',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3475,'<span data-type="title" id=toc-3506>٧ </span>- وإذا تتلى على هذا الضال آيات الله البينات أعرض عنها متكبراً، وحاله فى ذلك حال من لم يسمع، كأن فى أذنيه صمما، فأنذره بأن الله أعد له عذاباً شديدا الإيلام.',NULL,'612',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3476,'<span data-type="title" id=toc-3507>٨ </span>- إن الذين آمنوا بالله وعملوا الأعمال الطيبة الصالحة لهم جنات النعيم.',NULL,'612',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3477,'<span data-type="title" id=toc-3508>٩ </span>- يبقون فيها على وجه الخلود: وعدهم الله وعْداً لا يتخلف، والله الغالب على كل شئ. الحكيم فى أقواله وأفعاله.',NULL,'612',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3478,'<span data-type="title" id=toc-3509>١٠ </span>- خلق الله السموات من غير عُمُد مرئية لكم، وجعل فى الأرض جبالا ثوابت، لئلا تضطرب بكم، ونشر فيها من كل الحيوانات التى تدب وتتحرك، وأنزلنا من السماء ماء، فأنبتنا به فى الأرض من كل صنف حسن كثير المنافع.',NULL,'612',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3479,'<span data-type="title" id=toc-3510>١١ </span>- هذا مخلوق الله أمامكم، فأرونى ماذا خلق الذين تجعلونهم آلهة من دونه حتى يكونوا شركاء له؟ بل الظالمون - بإشراكهم - فى ضلال واضح.',NULL,'612',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3480,'<span data-type="title" id=toc-3511>١٢ </span>- ولقد أعطينا لقمان الحكم والعلم والإصابة فى القول، وقلنا له: اشكر الله على ما أعطاك من النعم. ومن يشكر فإنما يبتغى الخير لنفسه، ومن كفر النعم ولم يشكرها فإن الله غير محتاج إلى شكره، وهو مستحق للحمد وإن لم يحمده أحد.',NULL,'613',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3481,'<span data-type="title" id=toc-3512>١٣ </span>- واذكر إذ قال لقمان لابنه وهو يعظه: يا بنى، لا تشرك بالله أحداً، إن الشرك بالله لظلم عظيم يسوى بين الله المستحق للعبادة وحده، وبين من لا يستحقونها من الأوثان وغيرها من المبعودات.',NULL,'613',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3482,'<span data-type="title" id=toc-3513>١٤ </span>- وأمرنا الإنسان أن يبرّ والديه ويجعل أمه أوفر نصيباً، حملته فيتزايد ضعفها ويعظم شيئاً فشيئاً، وفطامه فى عامين، ووصيناه أن اشكر لله ولوالديك، إليه المرجع للحساب والجزاء.',NULL,'613',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3483,'<span data-type="title" id=toc-3514>١٥ </span>- وإِنْ حملك والداك - بجهد - على أن تشرك بالله ما لا تعلم أنه يستحق فلا تطعهما، وصاحبهما فى الدنيا بالبر والصلة والإحسان، واتبع طريق من رجع إلىَّ بالتوحيد والإخلاص، ثم إلىَّ مرجعكم جميعاً، فأخبركم بما كنتم تعملون من خير وشر لأجازيكم عليه.',NULL,'613',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3484,'<span data-type="title" id=toc-3515>١٦ </span>- يا بنى: إن الحسنة أو السيئة للإنسان إن تكن - مثلا - فى الصغر كحبة الخردل، فتكن فى أخفى مكان كقلب صخرة أو فى السموات أو فى الأرض يظهرها الله ويحاسب عليها، إن الله لطيف لا تخفى عليه دقائق الأشياء، خبير يعلم حقائق الأشياء كلها.',NULL,'614',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3485,'<span data-type="title" id=toc-3516>١٧ </span>- يا بنى: حافظ على الصلاة، وأمر بكل حسن، وانْه عن كل قبيح، واحتمل ما أصابك من الشدائد، إن ما أوصى الله به هو من الأمور التى ينبغى الحرص عليها والتمسك بها.',NULL,'614',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3486,'<span data-type="title" id=toc-3517>١٨ </span>- ولا تُمِلْ خدك للناس تكبراً، ولا تمش فى الأرض مُعجباً بنفسك، إن الله لا يحب كل مختال يعدد مناقبه.',NULL,'614',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3487,'<span data-type="title" id=toc-3518>١٩ </span>- وتوسط فى مشيك بين السرعة والبطء، واخفض من صوتك، لأن أقبح ما يستنكر من الأصوات هو صوت الحمير، أوله زفير مما يكره، وآخره شهيق مما يستقبح.',NULL,'614',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3488,'<span data-type="title" id=toc-3519>٢٠ </span>- قد رأيتم أن الله ذلَّل لكم ما فى السموات من الشمس والقمر والنجوم وغيرها، وما فى الأرض من الأنهار والثمار والدواب، وأتم عليكم نعمه ظاهرة لكم ومستورة عنكم، ومن الناس من يُجادل فى ذات الله وصفاته بلا دليل ولا رشاد مأثور عن نبى ولا وحى يضئ طريق الحق.',NULL,'614',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3489,'<span data-type="title" id=toc-3520>٢١ </span>- وإذا قيل لهم: اتبعوا ما أنزل الله من الحق والهدى، قالوا: بل نتبع ما وجدنا عليه آباءنا، أيتبعونهم ولو كان الشيطان يدعوهم إلى ضلال يدخلهم عذاب السعير؟ .',NULL,'614',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3490,'<span data-type="title" id=toc-3521>٢٢ </span>- ومن يتجه إلى الله بقلبه ووجهه ويفوض إليه جميع أمره - وهو محسن فى عمله - فقد تعلق بأقوى الأسباب التى توصله إلى رضا الله، وإليه - سبحانه - مصير الأمور كلها.',NULL,'615',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3491,'<span data-type="title" id=toc-3522>٢٣ </span>- ومن لم يجعل ذاته ونفسه خالصة لله فلا يحزنك جحوده وإعراضه، إلينا - وحدنا - مرجع هؤلاء يوم القيامة، فنعرض عليهم أعمالهم. لأننا نحيط علماً بدخائل النفوس فكيف بظواهر الأعمال؟ .',NULL,'615',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3492,'<span data-type="title" id=toc-3523>٢٤ </span>- نمتعهم زمناً قليلا فى دنياهم، ثم نلجئهم إلى عذاب شديد لا يحتمل.',NULL,'615',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3493,'<span data-type="title" id=toc-3524>٢٥ </span>- وأقسم لك - أيها النبى - إن سألتهم من خلق السموات والأرض ليقولن: هو الله، قل: الحمد لله الذى أوجد من دلائل وحدانيته ما يهدم ما هم عليه من إشراك غيره معه فى العبادة. ولكن أكثرهم لا يعلمون أنهم بإقرارهم هذا قد أقاموا الحُجة على أنفسهم بفساد عقيدتهم.',NULL,'615',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3494,'<span data-type="title" id=toc-3525>٢٦ </span>- لله ما فى السموات والأرض خلقاً واقتداراً وتدبيراً، فكيف يتركون عبادته؟ وإن الله - سبحانه - هو الغنى عن خلقه وعن عبادتهم له. المحمود بذاته. الجدير بالثناء عليه من عباده.',NULL,'615',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3495,'<span data-type="title" id=toc-3526>٢٧ </span>- ولو تحولت كل أشجار الأرض أقلاماً وصارت مياه البحر الكثيرة مِداداً تكتب به كلمات الله لفنيت الأقلام ونفد المداد قبل أن تنفد كلمات الله. لأن الله عزيز لا يعجزه شئ. حكيم لا يخرج من علمه وحكمته شئ، فلا تنفد كلماته وحكمته.',NULL,'615',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3496,'<span data-type="title" id=toc-3527>٢٨ </span>- ما خلقكم ابتداء ولا بعثكم بعد الموت أمام قدرة الله إلا كخلق نفس واحدة أو بعثها. إن الله سميع لقول المشركين: لا بعث. بصير بأعمالهم فيجازيهم عليها.',NULL,'615',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3497,'<span data-type="title" id=toc-3528>٢٩ </span>- ألم تنظر - أيها المكلف - نظر اعتبار أن الله ينقص من زمن الليل بقدر ما يزيد من النهار، وينقص من زمن النهار بقدر ما يزيد فى زمن الليل، وذَلَّل الشمس والقمر لمصالحكم، وأخضعهما لنظام بديع، فيجرى كل منهما فى فلك معين لا يحيد عنه، ويستمر كذلك إلى يوم القيامة، وأنه - سبحانه - خبير بكل ما تعملون ومجازيكم عليه.',NULL,'616',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3498,'<span data-type="title" id=toc-3529>٣٠ </span>- ذلك المذكور من عجائب صنع الله وقدرته بسبب أن صانعه هو الإله الثابت الألوهية، الجدير - وحده - بالعبادة، وإن الآلهة التى تعبدونها من دونه باطلة الألوهية، وإن الله - وحده - هو العلى الشأن، الكبير السلطان.',NULL,'616',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3499,'<span data-type="title" id=toc-3530>٣١ </span>- ألم تنظر - أيها الإنسان - إلى الفلك تجرى فى البحر برحمة الله حاملة على ظهرها ما ينفعكم، ليظهر لكم بذلك بعض عجائب صنعه، ودلائل قدرته. إن فى ذلك لآيات لكل صبَّار على بلائه. شكور لنعمائه.',NULL,'616',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3500,'<span data-type="title" id=toc-3531>٣٢ </span>- هؤلاء الجاحدون بالله إذا ركبوا فى السفن واضطرب بهم البحر وارتفعت أمواجه حتى بدت كأنها تظللهم، وظنوا أنهم غارقون - لا محالة - لجأوا إلى الله يدعونه فى إخلاص وخضوع أن ينجيهم، فلما نجَّاهم إلى البر كان منهم قليل تذكَّر عهده، واعتدل فى عمله، ومنهم كثير نسى فضل ربه، وظل على جحوده به، ولا ينكر فضل ربه عليه وإحسانه إليه إلا كل إنسان شديد الغدر، مسرف فى الكفر بربه.',NULL,'616',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3501,'<span data-type="title" id=toc-3532>٣٣ </span>- يا أيها الناس: افعلوا ما أمركم ربكم به، واتركوا ما نهاكم عنه، واحذروا عذابه يوم القيامة، يوم لا يغنى والد فيه عن ولده شيئاً، ولا مولود هو مغن عن والده شيئاً، إن هذا اليوم وعد الله به، ووعده حق لا يتخلف، فلا تلهينكم زخارف الدنيا وزينتها عن الاستعداد له، ولا تخْدَعنَّكُم وساوس الشيطان، فتصرفكم عن الله وطاعته.',NULL,'616',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3502,'<span data-type="title" id=toc-3533>٣٤ </span>- إن الله يثبت عنده علم الساعة فلا يعلمها أحد سواه، وينزل المطر فى موعده الذى ضربه له، ويعلم ما فى الأرحام، أى يعلم مصير هذا الخارج من الأرحام إلى الدنيا بين الشقاء والسعادة، وبين التوفيق والخذلان، وبين مقدار إقامته فى الدنيا وخروجه منها. وما تعلم نفس بارة أو فاجرة ما تكسبه فى غدها من خير أو شر، وما تعلم نفس ببقعة الأرض التى فيها ينقضى أجلها، لأن الله تام العلم والخبرة لكل شئ، ولا يظهر على غيبه أحداً.',NULL,'617',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3503,'<span data-type="title" id=toc-3535>١ </span>- ا. ل. م: حروف صيغ منها القرآن، كما صيغ منها كلامكم، فإذا عجزتم عن الإتيان بمثله كان عجزكم دليلا على أنه من عند الله، ولم يقله بشر.',NULL,'618',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3504,'<span data-type="title" id=toc-3536>٢ </span>- تنزيل القرآن من الله رب العالمين ومدبر أمورهم، لا شك فى كونه منزلاً منه.',NULL,'618',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3505,'<span data-type="title" id=toc-3537>٣ </span>- بل يقولون: اختلقه محمد، ونسبه لله. ما كان لهم أن يقولوا هذا، بل هو الحق المنزل عليك من ربك لتخوف به قوماً لم يأتهم من رسول من قبلك، ترجو بذلك الإنذار هدايتهم وإذعانهم للحق.',NULL,'618',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3506,'<span data-type="title" id=toc-3538>٤ </span>- الله الذى خلق السموات والأرض وما بينهما فى ستة أيام، ثم استوى على العرش استواء يليق به، ما لكم من دون الله ناصر ينصركم، ولا شفيع لكم، أتتمادون فى الكفر والعناد فلا تتعظون بمواعظ الله؟ .',NULL,'618',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3507,'<span data-type="title" id=toc-3539>٥ </span>- يُدبِّر شئون الخلق من السماء إلى الأرض، ثم يصعد إليه أمرها فى يوم مُقدر بألف سنة من سنى الدنيا التى تعدونها.',NULL,'619',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3508,'<span data-type="title" id=toc-3540>٦ </span>- ذلك الموصوف بالخلق والاستواء والتدبير عالِمٌ ما غاب عن الخلق وما شاهدوه، الغالب أمره، الواسع الرحمة.',NULL,'619',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3509,'<span data-type="title" id=toc-3541>٧ </span>- الذى أتقن كل شئ خلقه بحسب ما تقتضيه حكمته، وبدأ خلق الإنسان الأول من طين.',NULL,'619',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3510,'<span data-type="title" id=toc-3542>٨ </span>- ثم جعل ذريته - بعد ذلك - متخلقة من ماء قليل ضعيف لا يُؤْبَه له فى العادة.',NULL,'619',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3511,'<span data-type="title" id=toc-3543>٩ </span>- ثم قوَّمه ووضع فيه من سره الذى اختص به، وجعل لكم السمع والأبصار والعقول لتسمعوا وتبصروا وتعقلوا، ما تشكرون إلا شكراً قليلا.',NULL,'619',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3512,'<span data-type="title" id=toc-3544>١٠ </span>- وقال المنكرون للبعث: أئذا صرنا تراباً مختلطاً بتراب الأرض لا يتميز عنه، أئنا لنعود فى خلق جديد؟! ، إنهم لا ينكرون البعث - وحده - بل هم بجميع ما يكون فى الآخرة مكذبون.',NULL,'619',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3513,'<span data-type="title" id=toc-3545>١١ </span>- قل: يتوفاكم ملك الموت الموكل بقبض أرواحكم عند انتهاء آجالكم، ثم إلى الله - وحده - تعودون.',NULL,'619',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3514,'<span data-type="title" id=toc-3546>١٢ </span>- ولو أتيح لك أن ترى المجرمين فى موقف الحساب، لرأيت عجباً، إذ المجرمون المستكبرون منكسو الرءوس خزياً من ربهم، يقولون فى ذلة: ربنا أبصرنا ما كنا نتعامى عنه، وسمعنا ما كنا نتصامم عنه، فارجعنا إلى الدنيا نعمل صالحاً غير الذى كنا نعمله، إنا موقنون - الآن بالحق الذى جاء به رسلك.',NULL,'619',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3515,'<span data-type="title" id=toc-3547>١٣ </span>- ولو شئنا لأعطينا كل نفس هُداها، ولكن سبق القول منى لأملأن جهنم من الجِنة والناس أجمعين، لعلمنا أن أكثرهم سيختارون الضلالة دون الهدى.',NULL,'620',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3516,'<span data-type="title" id=toc-3548>١٤ </span>- فذوقوا العذاب بما غفلتم عن لقاء يومكم هذا، إنا تركناكم فى العذاب كالمنسيين، وذوقوا العذاب الدائم الذى لا انقطاع له بسبب كفركم ومعاصيكم.',NULL,'620',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3517,'<span data-type="title" id=toc-3549>١٥ </span>- إنما يُصَدِّق بآياتنا الذين إذا وعظوا بها خروا لله ساجدين، ونزَّهوا ربهم عن كل نقص، مثنين عليه بكل كمال، وهم لا يستكبرون عن الانقياد لهذه الآيات.',NULL,'620',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3518,'<span data-type="title" id=toc-3550>١٦ </span>- تتنحى جنوبهم عن مضاجعها. يدعون ربهم خوفاً من سخطه، وطمعاً فى رحمته، ومن المال الذى رزقناهم به ينفقون فى وجوه الخير.',NULL,'620',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3519,'<span data-type="title" id=toc-3551>١٧ </span>- فلا تعلم نفس مقدار ما أعده الله وأخفاه لهؤلاء من النعيم العظيم، الذى تقر به عيونهم، جزاء بما كانوا يكسبون من الطاعة والأعمال.',NULL,'620',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3520,'<span data-type="title" id=toc-3552>١٨ </span>- أيستوى الناس فى جزائهم وقد اختلفوا فى أعمالهم؟ أفمن كان مؤمناً بالله كمن كان كافراً به عاصياً له؟ لا يستوون!',NULL,'620',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3521,'<span data-type="title" id=toc-3553>١٩ </span>- أما الذين آمنوا وعملوا الصالحات فلهم جنات المأوى التى فيها مساكنهم، كرامة لهم بما كانوا يعملون.',NULL,'620',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3522,'<span data-type="title" id=toc-3554>٢٠ </span>- وأما الذين خرجوا عن طاعة الله بكفرهم فمقامهم الذى أُعِدَّ لهم النار، كلما حاولوا الخروج منها أعيدوا فيها، وقيل لهم: ذوقوا عذاب النار الذى كنتم فى الدنيا تصرون على التكذيب به.',NULL,'620',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3523,'<span data-type="title" id=toc-3555>٢١ </span>- ونُقْسم: لنذيقنهم فى الدنيا عذاب الخذلان قبل أن يصلوا إلى العذاب الأكبر، وهو الخلود فى النار، لعل المعذبين بالعذاب الأدنى يتوبون عن الكفر.',NULL,'620',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3524,'<span data-type="title" id=toc-3556>٢٢ </span>- ولا أحد أشد ظلماً للحق ولنفسه من إنسان ذُكِّرَ بآيات الله وحججه البينات ثم انصرف عن الإيمان بها مع وضوحها، إننا من كل مجرم سننتقم.',NULL,'621',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3525,'<span data-type="title" id=toc-3557>٢٣ </span>- ولقد آتينا موسى التوراة فلا تكن فى شك من لقاء موسى للكتاب، وجعلنا الكتاب المنزل على موسى هادياً لبنى إسرائيل.',NULL,'621',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3526,'<span data-type="title" id=toc-3558>٢٤ </span>- وجعلنا من بنى إسرائيل أئمة فى الدين يقومون بهداية الناس، استجابة لأمرنا حين صبروا على العمل بما فى التوراة، وكانوا بآياتنا يصدقون أقوى التصديق.',NULL,'621',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3527,'<span data-type="title" id=toc-3559>٢٥ </span>- إن ربك هو - وحده - يقضى بين الأنبياء وأممهم يوم القيامة فيما كانوا فيه يختلفون.',NULL,'621',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3528,'<span data-type="title" id=toc-3560>٢٦ </span>- أتركَ الله المكذبين لرسلهم ولم يبين لهم أنه أهلك كثيراً من الأمم التى سبقتهم، وهم يمرون بديارهم، ويمشون فى مساكنهم؟ إن فى ذلك لعظات تبصرهم بالحق، أصمُّوا فلا يسمعون هذه العظات؟',NULL,'621',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3529,'<span data-type="title" id=toc-3561>٢٧ </span>- أعَموا ولم يروا أنا نجرى المطر والأنهار إلى الأرض التى قطع نباتها فنخرج به زرعاً تأكل منه أنعامهم، ويأكلون حبه وثمره؟ أعموا فلا يبصرون دلائل قدرة الله على إحياء الموتى؟ .',NULL,'621',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3530,'<span data-type="title" id=toc-3562>٢٨ </span>- ويقول المشركون لك وللمؤمنين: فى أى وقت يفتح الله عليكم بالنصر، أخبرونا بموعده إن كنتم صادقين.',NULL,'621',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3531,'<span data-type="title" id=toc-3563>٢٩ </span>- قل لهم: يوم القضاء والفصل إذا حل بكم لا ينفع الذين كفروا إيمانهم، ولا هم يمهلون لحظة عن العذاب الذى يستحقونه.',NULL,'621',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3532,'<span data-type="title" id=toc-3564>٣٠ </span>- وإذا كان هذا الاستهزاء دأبهم فأعرض عنهم، وانتظر صدق ما وعدك ربك فيهم إنهم ينتظرون الغلبة عليكم.',NULL,'621',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3533,'<span data-type="title" id=toc-3566>١ </span>- يا أيها النبى: استمر على ما أنت عليه من تقوى الله، ولا تقبل رأيا من الكافرين والمنافقين، إن الله محيط علما بكل شئ، حكيم فى أقواله وأفعاله.',NULL,'622',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3534,'<span data-type="title" id=toc-3567>٢ </span>- واتبع الوحى الذى ينزل عليك من ربك، إن الله الذى يوحى إليك خبير بدقائق ما تعمل - أنت - ويعمل الكافرون والمنافقون.',NULL,'622',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3535,'<span data-type="title" id=toc-3568>٣ </span>- وفوض جميع أمورك إلى الله، وكفى بالله حافظاً موكولاً إليه كل أمر.',NULL,'622',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3536,'<span data-type="title" id=toc-3569>٤ </span>- ما جعل الله لرجل من قلبين فى جوفه، وما جعل زوجة أحدكم حين يقول لها: أنت علىَّ كظهر أمِّى أُمَّا له، وما جعل الأولاد الذين تتبنوهم أبناء لكم يأخذون حكم الأبناء من النسب. ذلكم - أى جعْلِكُم الأدعياء أبناء - قول يصدر من أفواهكم لا حقيقة له، فلا حكم يترتب عليه، والله يقول الأمر الثابت المحقق، ويرشدكم إليه، وهو - وحده سبحانه - يهدى الناس إلى طريق الصواب.',NULL,'623',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3537,'<span data-type="title" id=toc-3570>٥ </span>- انْسِبُوا هؤلاء الأولاد لآبائهم الحقيقيين هو أعدل عند الله، فإن لم تعلموا آباءهم المنتسبين بحق إليهم فهم إخوانكم فى الدين ونصراؤكم، ولا إثم عليكم حين تنسبونهم إلى غير آبائهم خطأ، ولكن الإثم فيما تقصده قلوبكم بعد أن تبيَّن لكم الأمر. والله يغفر لكم خطأكم، ويقبل توبة متعمدكم.',NULL,'623',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3538,'<span data-type="title" id=toc-3571>٦ </span>- النبى - محمد - أحق ولاية بالمؤمنين، وأرحم بهم من نفوسهم، فعليهم أن يحبوه ويطيعوه، وأزواجه أمهاتهم فى التوقير وحرمة التزوج بهن بعده، وذوو القرابات أولى من المؤمنين والمهاجرين بأن يتوارثوا فيما بينهم فرضا فى القرآن. لكن يجوز أن تقدموا إلى مَنْ وَاليتم فى الدين من غير الأقارب معروفاً، فتعطوه - براً وعطفاً عليه - أو توصوا له بجزء من مالكم. كان ذلك التوارث بالأرحام فى الكتاب مقررا لا يعتريه تبديل.',NULL,'623',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3539,'<span data-type="title" id=toc-3572>٧ </span>- واذكر حين أخذنا من النبيين السابقين ميثاقهم - بتبليغ الرسالة والدعاء إلى الدين القيم - ومنك ومن نوح وإبراهيم وموسى وعيسى ابن مريم وأخذنا منهم عهدا عظيم الشأن.',NULL,'623',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3540,'<span data-type="title" id=toc-3573>٨ </span>- ليسأل الله يوم القيامة الأنبياء عمَّا قالوه لقومهم، وأعد للكافرين بالرسل عذاباً أليما.',NULL,'623',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3541,'<span data-type="title" id=toc-3574>٩ </span>- يا أيها الذين آمنوا اذكروا نعمة الله وفضله عليكم حين جاءتكم الأحزاب يوم الخندق فأرسلنا عليهم ريحا عاصفة باردة وملائكة لم تروها نشرت الرعب فى قلوبهم، وكان الله بصيرا بأعمالكم وصِدْق نياتكم، فتولى الدفاع عنكم.',NULL,'623',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3542,'<span data-type="title" id=toc-3575>١٠ </span>- حين جاءوكم من أعلى الوادى ومن أسفله، حين مالت الأبصار عن مستوى نظرها، وارتفعت القلوب إلى منتهى الحلقوم فزعاً واضطراباً، وأنتم فى ذلك الوقت العصيب تذهب بكم الظنون فى وعد الله كل مذهب.',NULL,'624',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3543,'<span data-type="title" id=toc-3576>١١ </span>- فى ذلك الوقت امتحن المؤمنون بالصبر على الإيمان، واضطربوا بالخوف اضطراباً شديداً.',NULL,'624',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3544,'<span data-type="title" id=toc-3577>١٢ </span>- واذكر ما حدث من المنافقين ومرضى القلوب بالريب حين يقولون: ما وعدنا الله ورسوله إلا وعداً باطلاً قصد به التغرير بنا.',NULL,'624',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3545,'<span data-type="title" id=toc-3578>١٣ </span>- واذكر حين قالت طائفة من المنافقين وضعاف العزائم: يا أهل المدينة، لا وجه لبقائكم هنا فى معركة خاسرة، فارجعوا إلى منازلكم. ويستأذن فريق منهم الرسول فى الرجوع إلى المدينة، ويقولون إن بيوتنا غير محصنة، ولا بد لنا من الرجوع لحراستها، وما كانت بيوتهم معرضة كما يقولون، وما يريدون إلا الفرار من المعركة بهذا العذر الكاذب.',NULL,'624',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3546,'<span data-type="title" id=toc-3579>١٤ </span>- ولو دخلت الأحزاب عليهم المدينة من كل جوانبها، ثم طلب منهم أن يعلنوا رجوعهم عن الإسلام ويقاتلوا المسلمين لاستجابوا لما طلب منهم، وما انتظروا فى ذلك إلا وقتاً قصيراً.',NULL,'624',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3547,'<span data-type="title" id=toc-3580>١٥ </span>- ولقد كان هؤلاء الفارون من ميدان القتال عاهدوا الله - من قبل هذه الغزوة - أن يثبتوا فى القتال مع الرسول ولا يفروا. وكان عهد الله مسئولا عن صاحبه، يجب عليه الوفاء به.',NULL,'624',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3548,'<span data-type="title" id=toc-3581>١٦ </span>- قل لهم: لن ينفعكم الهرب إن هربتم من الموت أو القتل وقد حضر أجلكم، وإذا لم يحضر وبقيتم لا تُمَتَّعُون فى الدنيا إلا مدة أعماركم، وهى قليلة.',NULL,'624',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3549,'<span data-type="title" id=toc-3582>١٧ </span>- قل لهؤلاء المترددين: من ذا الذى يجيركم من الله إن أراد بكم شرا، أو يمنع الخير عنكم إن أراد بكم رحمة؟ ولا يجدون لهم من دون الله مجيراً ولا مغيثا.',NULL,'625',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3550,'<span data-type="title" id=toc-3583>١٨ </span>- إن الله يعلم المثبطين منكم والذين يقولون لإخوانهم: انضموا إلينا، ولا يأتون شدة الحرب إلا إتياناً قليلاً.',NULL,'625',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3551,'<span data-type="title" id=toc-3584>١٩ </span>- حُرَصاء عليكم فى الظاهر حيث لا خوف، فإذا جاء الخوف من قبل العدو أو من قبل الرسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - رأيتهم ينظرون إليك تدور أعينهم حائرة، كحال المغشى عليه من سكرات الموت، فإذا ذهب الخوف بالغوا فى ذمكم وشتمكم بألسنة قاطعة، بخلاء بكل خير. أولئك لم يؤمنوا بقلوبهم وإن أعلنوا إسلامهم فأبطل الله أعمالهم بإضمارهم الكفر، وكان ذلك الإحباط أمرا هينا على الله.',NULL,'625',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3552,'<span data-type="title" id=toc-3585>٢٠ </span>- يظن هؤلاء المنافقون أن جيوش الكفار المتحزبة لا تزال مكانها تحاصر المدينة، وإن يأت الأحزاب كرة أخرى يَتَمَنَّ الجبناء أن لو كانوا يعيشون مع الأعراب فى البوادى يتسقطون أخباركم، ولو ظل هؤلاء فى معسكرهم ولم يفروا والتحم الجيشان ما قاتلوا معكم إلا قليلا للرياء والسمعة.',NULL,'625',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3553,'<span data-type="title" id=toc-3586>٢١ </span>- لقد كان لكم فى رسول الله قدوة حسنة لمن كان يرجو رحمة الله ونعيم اليوم الآخر، وذكر الله كثيراً فى الخوف والرجاء والشدة والرخاء.',NULL,'625',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3554,'<span data-type="title" id=toc-3587>٢٢ </span>- ولمَّا رأى المؤمنون الأحزاب المشركين قالوا: هذا ما وعدنا الله ورسوله من قبل، فقد وعدنا بالشدائد ثم النصر، وصدق الله ورسوله، وما زادتهم هذه الشدائد إلا قوة إيمان بالله وحسن تسليم لقضائه.',NULL,'625',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3555,'<span data-type="title" id=toc-3588>٢٣ </span>- من هؤلاء المؤمنين رجال عاهدوا الله على الثبات فى القتال مع الرسول فوفوا بما عاهدوا، فمنهم من نال شرف الاستشهاد، ومنهم من بقى حيا ينتظر أن ينال هذا الشرف، وما بدلوا عهد الله الذى قطعوه على أنفسهم، ولا غيروا شيئاً منه.',NULL,'626',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3556,'<span data-type="title" id=toc-3589>٢٤ </span>- ليجزى الله المؤمنين الصادقين بصدقهم فى إيمانهم ووفائهم بعهدهم، ويعذب المنافقين - إن شاء - أو يوفق المستعد منهم إلى التوبة، إن الله كان غفوراً بقبول التوبة. رحيما بالعفو عن المعصية.',NULL,'626',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3557,'<span data-type="title" id=toc-3590>٢٥ </span>- وردَّ الله الكفار المتحزبين على الرسول ممتلئة قلوبهم بالغيظ لم ينالوا خيرا من نصر أو غنيمة، وكفى الله المؤمنين مشقة قتالهم بما سلطه عليهم من الريح والملائكة، وكان الله قويا على تنفيذ ما يريد، عزيزا لا يغلبه غالب.',NULL,'626',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3558,'<span data-type="title" id=toc-3591>٢٦ </span>- وأنزل الله الذين عاونوا الأحزاب من أهل الكتاب - وهم يهود بنى قريظة - من قلاعهم التى يتحصنون بها، وألقى فى قلوبهم الرعب. فريقاً تقتلون - وهم الرجال - وتأسرون فريقا آخر وهو النساء والذرارى.',NULL,'626',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3559,'<span data-type="title" id=toc-3592>٢٧ </span>- وأورثكم أرضهم وديارهم وأموالهم وأرضاً لم تطأها أقدامكم من قبل، وكان الله - سبحانه - قديراً على تنفيذ كل شئ يريد.',NULL,'626',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3560,'<span data-type="title" id=toc-3593>٢٨ </span>- يا أيها النبى: قل لأزواجك - ناصحاً لهن -: إن كنتن تردن الحياة الدنيا ومتعتها فأقبلن أدفع إليكن ما يُخفف وحشة الطلاق، فيكون متعة لَكُنَّ، وأطلقكن طلاقاً لا إساءة معه.',NULL,'626',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3561,'<span data-type="title" id=toc-3594>٢٩ </span>- وإن كنتن تؤثرن حب الله ورسوله ونعيم الدار الآخرة، وترضين بما أنتُنَّ فيه من خشونة عيش، فإن الله أعد لأمثالكن من المحسنات فى أعمالهن أجرا لا يقدر قدره.',NULL,'626',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3562,'<span data-type="title" id=toc-3595>٣٠ </span>- يا نساء النبى: من يفعل منكن خطيئة ظاهرة فى قبحها يضم إلى عذابها عذابان، حتى تكون ثلاثة بالقياس إلى عذاب غيرها، وكان ذلك التضعيف على الله هينا.',NULL,'627',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3563,'<span data-type="title" id=toc-3596>٣١ </span>- ومن يدم منكن على الخضوع لله ورسوله، وتعمل صالحاً يعطها الله أجرها مرتين، وأعددنا لها فى الآخرة رزقاً جليل القدر.',NULL,'627',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3564,'<span data-type="title" id=toc-3597>٣٢ </span>- يا نساء النبى: لستن فى الفضل والشرف كأحد من النساء، إن أردتن التقوى فلا تتحدثن بكلام فيه طراوة وتكسر، فيطمع فيكن من فى قلبه فساد، وليكن قولكن قولا متعارفاً غير متكلف.',NULL,'627',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3565,'<span data-type="title" id=toc-3598>٣٣ </span>- والْزَمْنَ بيوتكن لا تخرجن إلا لحاجة شرع الله الخروج لقضائها، ولا تُظْهرن محاسنكن وزينتكن للرجال إذا خرجتن. كما كانت تفعل أهل الجهالة الأولى، وأدين الصلاة كاملة، وأعطين الزكاة، وامتثلن أمر الله ورسوله. إنما يريد الله - بكل ما يأمركن به وينهاكن عنه - الشرف والكرامة. ليذهب عنكم الإثم والمعصية - يا أهل بيت النبى - ويطهركم تطهيرا لا يخالطه شبهة.',NULL,'627',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3566,'<span data-type="title" id=toc-3599>٣٤ </span>- واحفظن ما يقرأ فى بيوتكن من آيات القرآن التى أنزلها الله، وما ينطق به رسول الله من الحكم السديد. إن الله كان عالما بغوامض الأشياء وحقائقها، فاحذرن مخالفته ومعصية رسوله.',NULL,'627',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3567,'<span data-type="title" id=toc-3600>٣٥ </span>- إن المنقادين من الرجال والنساء، والمصدقين بالله ورسوله والمصدقات، والقائمين بالطاعة والقائمات، والصادقين فى أقوالهم وأعمالهم ونياتهم والصادقات، والصابرين على تحمل المشاق فى سبيل الله والصابرات، والمتواضعين لله والمتواضعات، والمتصدقين من مالهم على المحتاجين والمتصدقات، والصائمين الفرض والنفل والصائمات، والحافظين فروجهم عما لا يحل والحافظات، والذاكرين الله كثيرا بقلوبهم وألسنتهم والذاكرات. أعد الله لهم غفراناً لذنوبهم وثوابا عظيما على أعمالهم.',NULL,'628',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3568,'<span data-type="title" id=toc-3601>٣٦ </span>- وما ساغ لمؤمن ولا لمؤمنة إذا حكم الله ورسوله فى أمر من الأمور أن يكون له خيار فيه بعد أن حكم الله ورسوله، ومن يخالف ما حكم به الله ورسوله فقد بَعُد عن طريق الصواب بُعْداً ظاهراً.',NULL,'628',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3569,'<span data-type="title" id=toc-3602>٣٧ </span>- واذكر إذ تقول لزيد بن حارثة الذى أنعم الله عليه بهداية الإسلام، وأنعمت عليه بالتربية والعتق، أمسك عليك زوجك - زينب بنت جحش - واتق الله فيها، واصبر على معاشرتها، وتخفى فى نفسك ما الله مظهره من أنه سيطلقها وأنك ستتزوجها، وتخاف أن يُعيِّرك الناس، والله هو الجدير بأن تخافه، ولو كان فى ذلك مشقة عليك. فلما قضى زيد منها حاجته وطلقها تخلصاً من ضيق الحياة معها زوجناكها. لتكون قدوة فى إبطال هذه العادة المرذولة، ولا يتحرج المسلمين بعد ذلك من التزوج بزوجات من كانوا يتبنونهم بعد طلاقهن. وكان أمر الله الذى يريده واقعاً لا محالة.',NULL,'628',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3570,'<span data-type="title" id=toc-3603>٣٨ </span>- ما كان على النبى من إثم فى عمل أمره الله به، سن الله سنته مع الأنبياء من قبل الا يحظر عليهم ما أباح لهم ووسع عليهم، وكان أمر الله قضاء مقضيا وحكماً مثبوتاً.',NULL,'628',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3571,'<span data-type="title" id=toc-3604>٣٩ </span>- الذين يُبلِّغون إلى الناس رسالات الله كما أنزلها، ويخافونه ولا يخافون أحدا سواه، وكفى أن يكون الله هو الرقيب المحاسب.',NULL,'628',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3572,'<span data-type="title" id=toc-3605>٤٠ </span>- ما كان محمد أبا أحد من رجالكم حتى يحرم عليه التزوج من مطلقته، ولكن رسول الله وخاتم النبيين، عليه أن يؤدى رسالته كما أمره ربه من غير خشية أحد، وكان الله بكل شئ محيطا علمه.',NULL,'629',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3573,'<span data-type="title" id=toc-3606>٤١ </span>-، ٤٢ - يا أيها الذين آمنوا: اثنوا على الله بضروب الثناء وأكثروا من ذلك، ونزّهوه عن كل ما لا يليق به أول النهار وآخره.',NULL,'629',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3574,'<span data-type="title" id=toc-3607>٤٣ </span>- وهو الذى يتعهدكم برحمته ولطفه، وملائكته تطلب المغفرة والهداية لكم، ليخرجكم الله بذلك من ظلمات الكفر والضلال إلى نور الإيمان والطاعة، وكان الله بالمؤمنين عظيم الرحمة.',NULL,'629',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3575,'<span data-type="title" id=toc-3608>٤٤ </span>- تحيتهم من الله يوم يلقونه أمن وسلام لهم، وهيَّأ لهم على أعمالهم أجراً سخيا يشعرهم بفضله.',NULL,'629',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3576,'<span data-type="title" id=toc-3609>٤٥ </span>- يا أيها النبى: إنا بعثناك إلى الناس برسالة الإسلام تشهد بالحق، وتبشر المؤمنين بما يكون لهم من خير وثواب، وتنذر الكافرين بسوء المصير.',NULL,'629',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3577,'<span data-type="title" id=toc-3610>٤٦ </span>- وداعيا الخلق إلى الله بأمره، وسراجا يهدى بنوره الحاضرين فى ظلمات الشك.',NULL,'629',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3578,'<span data-type="title" id=toc-3611>٤٧ </span>- وبشر المؤمنين بأن لهم مزيدا كبيرا من الخير فى الدنيا والآخرة.',NULL,'629',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3579,'<span data-type="title" id=toc-3612>٤٨ </span>- ولا تطع الكافرين والمنافقين ولا تعبأ بأذاهم، واجعل الله وكيلك يدفع عنك ضرهم وشرهم وحسبك الله وكيلا يكفيك ويغنيك.',NULL,'629',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3580,'<span data-type="title" id=toc-3613>٤٩ </span>- يا أيها الذين آمنوا إذا عقدتم على المؤمنات ثم طلقتموهن من قبل أن تدخلوا بهن، فليس لكم عليهن عدة تستوفون عددها، فأعطوهن شيئا من المال جبرا لخاطرهن، وأخرجوهن من بيوتكم من غير إضرار بهن.',NULL,'630',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3581,'<span data-type="title" id=toc-3614>٥٠ </span>- يا أيها النبى: إنا أبحنا لك أزواجك اللاتى أعطيتهن مهورهن، وأبحنا لك ما ملكت يمينك من الإماء مما أنعم الله به عليك، وأحللنا لك التزوج من بنات عمك وبنات عماتك، وبنات خالك وبنات خالاتك اللاتى هاجرن معك، وأحللنا لك امرأة مؤمنة إن وهبت نفسها لك بلا مهر، وأنت تريد نكاحها وترغب فيها. خلصت لك هذه الهبة من دون المؤمنين فلا تحل لهم. قد علمنا ما فرضناه على المؤمنين فى أزواجهم وإمائهم من أحكام. وما رخصنا لك فيه دونهم. لئلا يكون عليك ضيق فيما شرعناه لك. وكان الله غفوراً لذنوب عباده رحيماً بالتوسعة عليهم.',NULL,'630',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3582,'<span data-type="title" id=toc-3615>٥١ </span>- تؤخر مَن تشاء منهن فى القسم، وتدنى إليك مَن تشاء، ومن طلبت ممن أخرت قسمها فلا مؤاخذة عليك، ذلك التفويض إلى مشيئتك أقرب إلى سرورهن وبُعْد الحزن عنهن، ويرضين كلهن بما آتيتهن، والله يعلم ما فى قلوبكم من السخط أو الرضا بما شرع، وكان الله عليما بما فى الصدور. حليما لا يعاجل بالعقوبة.',NULL,'630',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3583,'<span data-type="title" id=toc-3616>٥٢ </span>- لا يحل لك النساء من بعد، ولا أن تطلقهن لتستبدل بهن من النساء من تشاء، ولو أعجبك حسنهن، ولكن الله أحل لك ما تملكه يدك من الإماء، وكان الله مطلعا على كل شئ، حافظا له.',NULL,'630',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3584,'<span data-type="title" id=toc-3617>٥٣ </span>- يا أيها الذين آمنوا لا تدخلوا بيوت النبى إلا فى حال إذنه لكم لتناول الطعام غير منتظرين وقت إدراكه، ولكن إذا دعاكم الرسول فادخلوا، فإذا طعمتم فانصرفوا، ولا تمكثوا بعد ذلك مستأنسين لحديث بعضكم بعضا. لأن الدخول بدون إذنه وإطالة المكث بعد الطعام كان يؤذى النبى فيستحى أن يطلب إليكم الخروج، ولكن الله - تعالى - لا يمنعه من الجهر بالحق ما يمنع المخلوقين، وإذا سألتم إحدى زوجات النبى - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - حاجة فاسألوهن من وراء حجاب، ذلك أعظم طهارة لقلوبكم وقلوبهن من وساوس الشيطان، وما صح لكم أن تؤذوا رسول الله ولا أن تتزوجوا نساءه من بعده أبدا. احتراما له ولهن. إن ذلكم كان عند الله ذنباً عظيما.',NULL,'631',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3585,'<span data-type="title" id=toc-3618>٥٤ </span>- إن تظهروا شيئاً مما يؤذيه أو تخفوه فى صدوركم فإن الله كان بكل شئ عليما.',NULL,'631',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3586,'<span data-type="title" id=toc-3619>٥٥ </span>- لا إثم على نساء النبى ألا يحتجبن من آبائهن ولا أبنائهن ولا إخوانهن ولا أبناء إخوانهن ولا أبناء أخواتهن ولا النساء المؤمنات ولا العبيد المملوكين لهن لشدة الحاجة إليهم فى الخدمة، واتقين الله فيما أمركن به، فلا تتجاوزن حدوده. لأنه كان بكل شئ عالما لا تخفى عليه خافية.',NULL,'631',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3587,'<span data-type="title" id=toc-3620>٥٦ </span>- إن الله يرحم نبيه ويرضى عنه، والملائكة يدعون له، يا أيها الذين آمنوا صلوا عليه وسلموا تسليما.',NULL,'631',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3588,'<span data-type="title" id=toc-3621>٥٧ </span>- إن الذين يؤذون الله ورسوله بتحديهما والتحريض على الكفر بهما طردهم الله فى الدنيا والآخرة من رحمته، وأعد لهم عذاباً يذل كبرياءهم.',NULL,'632',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3589,'<span data-type="title" id=toc-3622>٥٨ </span>- والذين يؤذون المؤمنين والمؤمنات بقول أو فعل من غير ذنب فعلوه فقد تحملوا وزر كذبهم عليهم، وأتوا ذنباً ظاهر القبح.',NULL,'632',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3590,'<span data-type="title" id=toc-3623>٥٩ </span>- يا أيها النبى قل لأزواجك وبناتك ونساء المؤمنين: يسدلن على أجسامهن من جلابيبهن، وذلك اللباس على هذا الحال أولى وأحق بأن يعرفن فلا يُتعرض لهن بأذى، وكان الله غفوراً رحيما لمن أقلع عن ذنوبه.',NULL,'632',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3591,'<span data-type="title" id=toc-3624>٦٠ </span>- أقسم: إن لم يكف المنافقون والذين فى قلوبهم مرض والمشيعون للأخبار الكاذبة فى المدينة لنسلطنك عليهم، ثم لا يكون لهم بقاء بجوارك فيها إلا زمناً قليلاً.',NULL,'632',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3592,'<span data-type="title" id=toc-3625>٦١ </span>- مستحقين للعنة والطرد أينما وجدوا أُخذوا وقُتلوا تقتيلا.',NULL,'632',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3593,'<span data-type="title" id=toc-3626>٦٢ </span>- سن الله - تعالى - من قبل فيمن نافقوا الأنبياء والمرسلين وتمردوا أن يُقتلوا أينما وجدوا، ولن تجد لسنة الله تغييرا.',NULL,'632',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3594,'<span data-type="title" id=toc-3627>٦٣ </span>- يسألك الناس عن وقت قيام الساعة قل لهؤلاء: إن علم وقتها عند الله - وحده - وما يدريك لعل وقت قيامها يكون قريبا.',NULL,'632',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3595,'<span data-type="title" id=toc-3628>٦٤ </span>- إن الله طرد الكافرين من رحمته، وأعد لهم نارا شديدة الاتقاد.',NULL,'632',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3596,'<span data-type="title" id=toc-3629>٦٥ </span>- لا يخرجون منها أبدا، ولا يجدون لهم من يتكفل بحمايتهم، ولا مَن يدفعها عنهم.',NULL,'632',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3597,'<span data-type="title" id=toc-3630>٦٦ </span>- يوم تتقلب وجوههم فى النار من حال إلى حال يقولون - نادمين -: يا ليتنا أطعنا الله وأطعنا الرسول.',NULL,'633',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3598,'<span data-type="title" id=toc-3631>٦٧ </span>- وقالوا: ربنا إنا اتبعنا رؤساءنا وكبراءنا فى الكفر بك وبرسولك فأبْعدونا عن الطريق المستقيم.',NULL,'633',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3599,'<span data-type="title" id=toc-3632>٦٨ </span>- ربنا اجعل عذابهم مضاعفا، واطردهم من رحمتك طرداً كبيراً بمقادر إثمهم وجُرمهم.',NULL,'633',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3600,'<span data-type="title" id=toc-3633>٦٩ </span>- يا أيها الذين آمنوا لا تؤذوا النبى بأى نوع من الأذى، كالذين آذوا موسى من قومه فَبَرَّأه الله مما نسبوه إليه، وكان موسى عند الله سيدا ذا جاه.',NULL,'633',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3601,'<span data-type="title" id=toc-3634>٧٠ </span>- يا أيها الذين آمنوا خافوا عقاب الله إذا عصيتموه، وقولوا قولا مستقيما لا اعوجاج فيه.',NULL,'633',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3602,'<span data-type="title" id=toc-3635>٧١ </span>- يوفقكم للعمل الصالح ويمحُ ذنوبكم، ومَن يطع الله ورسوله فقد نال الفوز العظيم بالنجاة من العقاب والحصول على الثواب.',NULL,'633',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3603,'<span data-type="title" id=toc-3636>٧٢ </span>- إنا عرضنا التكاليف على السموات والأرض والجبال فأبين حملها وخفْن منها، وحملها الإنسان إنه كان شديد الظلم لنفسه، جهولا بما يطيق حمله.',NULL,'633',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3604,'<span data-type="title" id=toc-3637>٧٣ </span>- ليُعذب الله المنافقين والمنافقات والمشركين والمشركات، ويقبل الله توبة المؤمنين والمؤمنات، والله كثير المغفرة واسع الرحمة.',NULL,'633',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3605,'<span data-type="title" id=toc-3639>١ </span>- الثناء كله حق لله - وحده - الذى له ما فى السموات وما فى الأرض خلقا ومُلْكا وتدبيرا، وله - وحده - الثناء فى الآخرة لملكه الشامل، وهو الحكيم الذى لا يخطئ، الخبير الذى لا يغيب عنه سر.',NULL,'634',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3606,'<span data-type="title" id=toc-3640>٢ </span>- يعلم كل ما يدخل فى أجزاء الأرض كالماء والكنوز والدفائن وأجزاء الموتى، وكل ما يخرج منها كالحيوان والنبات والمعادن ومياه الآبار والعيون، ويعلم ما ينزل من السماء كالملائكة والكتب التى يَتَلَقَّاها الأنبياء والمطر والصواعق، وما يصعد فيها ويرقى إليها كالملائكة وأعمال العباد والأرواح، وهو الكثير الرحمة العظيم المغفرة.',NULL,'635',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3607,'<span data-type="title" id=toc-3641>٣ </span>- وقال الذين كفروا: لا تأتينا الساعة الموعودة للبعث والنشور. قل لهم - أيها الرسول -: ستأتيكم، وربى لتأتينكم عالم الغيب لا يغيب عن علمه قدر ذرة فى السموات ولا فى الأرض، ولا أصغر من الذرة ولا أكبر منها إلا مسطور فى كتاب تام البيان.',NULL,'635',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3608,'<span data-type="title" id=toc-3642>٤ </span>- ليثيب الله الذين آمنوا وعملوا الخير لأنفسهم وللناس، أولئك المؤمنون العاملون لهم من الله مغفرة تمحو ذنوبهم ورزق واسع لا مَنَّ فيه.',NULL,'635',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3609,'<span data-type="title" id=toc-3643>٥ </span>- والذين أجهدوا أنفسهم فى محاربة القرآن مغالبين أمر الله فى نصر رسوله، أولئك لهم عذاب من أسوأ العذاب المؤلم.',NULL,'635',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3610,'<span data-type="title" id=toc-3644>٦ </span>- ويعلم الذين مَنَّ الله عليهم بالعلم أن القرآن الذى أنزل إليك من ربك - بما فيه من عقائد وهداية - هو الحق الذى لا مرية فيه، وهو الذى يهدى إلى طريق الله الغالب على كل شئ، المستحق لكل ثناء.',NULL,'635',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3611,'<span data-type="title" id=toc-3645>٧ </span>- وقال الكفار بعضهم لبعض - استهزاء بخبر البعث -: هل ندلكم على رجل يُحدثكم أنكم إذا متم وفُرِّقت أجسامكم كل تفريق أنكم لتبعثون فى خلق جديد؟',NULL,'635',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3612,'<span data-type="title" id=toc-3646>٨ </span>- أختلق هذا الرجل على الله كذباً فيما نسبه إليه من إحياء الموتى، أم به جنون فهو يتكلم بما لا يدرى؟ ليس الأمر كما زعموا، بل الحقيقة أن الذين لا يؤمنون بالآخرة واقعون فى العذاب والضلال البعيد عن الحق.',NULL,'636',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3613,'<span data-type="title" id=toc-3647>٩ </span>- أعموا فلم ينظروا إلى ما بين أيديهم وما وراءهم من السماء والأرض، ليعلموا قدرتنا على فعل ما نشاء؟! إن نشأ نخسف بهم الأرض خسفناها بهم، أو إن نشأ نُسقط عليهم قطعاً من السماء نسحقهم بها أسقطناها. إن فيما ذكرنا لدليلا لكل عبد راجع إلى ربه فى كل أمره.',NULL,'636',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3614,'<span data-type="title" id=toc-3648>١٠ </span>- والله: لقد أعطينا داود منا فضلا بإعطائه الحكمة والكتاب، وقلنا: يا جبال رددى معه التسبيح إذا سبَّح، وسخرنا له الطير ترجع تقديس الله، وصيرنا له الحديد ليِّنا يشكله كما يشاء.',NULL,'636',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3615,'<span data-type="title" id=toc-3649>١١ </span>- أوحينا إليه أن اعمل دروعاً واسعة تحمى من بأس الأعداء، وأحكم نسْجها بتداخل حلقاتها، وقلنا له ولآله: اعملوا ما يعود عليكم وعلى غيركم بالخير والصلاح، إنى بكل ما تعملون بصير لا يغيب عنى شئ منه.',NULL,'636',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3616,'<span data-type="title" id=toc-3650>١٢ </span>- وسخرنا لسليمان الريح، جريها فى أول النهار يعدل السير العادى شهرا، وجريها فى آخر النهار يعدل السير شهراً وأسلْنا له معدن النحاس يجرى غزيرا مستمرا، وسخرنا له من الجن من يعمل أمامه بتسخير ربه، ومن ينحرف من الجن عن أمرنا لهم بطاعة سليمان نُذقه من عذاب النار المستعرة.',NULL,'636',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3617,'<span data-type="title" id=toc-3651>١٣ </span>- يعملون له ما يريد من مساجد للعبادة، وصور مجسمة، وقصاع كبيرة كالأحواض، وأوان للطبخ ثابتات على قواعدها لعظمها، وقلنا لآل داود: اعملوا عملا تشكرون به الله شكراً، وقليل من عبادى من يذكر نعمى فيكثر شكرى.',NULL,'636',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3618,'<span data-type="title" id=toc-3652>١٤ </span>- فلما حكمنا على سليمان بالموت ما دل الجن على موته إلا دابة الأرض تأكل عصاه وهو متكئ عليها، فلما سقط علمت الجن أنهم لو كانوا يعلمون الغيب ما مكثوا فى العذاب الشاق المهين لهم.',NULL,'637',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3619,'<span data-type="title" id=toc-3653>١٥ </span>- أقسم: قد كان لأهل سبأ فى مسكنهم باليمن آية دالة على قدرتنا: حديقتان تحفَّان ببلدهم عن يمين وشمال، قيل لهم: كلوا من رزق ربكم واشكروا نعمه بصرفها فى وجوهها. بلدتكم بلدة طيبة ذات ظل وثمار، وربكم كثير المغفرة لمن شكره.',NULL,'637',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3620,'<span data-type="title" id=toc-3654>١٦ </span>- فأعرضوا عن شكر النعمة وبطروا معيشتهم، فأطلقنا عليهم السيل الجارف الذى أعقب تصدع السدود فأهلكت البساتين، وبدَّلناهم بجنتيهم المثمرتين جنتين ذواتى ثمر مر، وشجر لا يثمر، وشئ من نبق قليل لا غناء فيه.',NULL,'637',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3621,'<span data-type="title" id=toc-3655>١٧ </span>- ذلك الجزاء جزيناهم بكفرهم النعمة وعدم شكرها، وهل نعاقب هذا العقاب إلا شديد الكفر بالله وبأفضاله؟!',NULL,'637',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3622,'<span data-type="title" id=toc-3656>١٨ </span>- وجعلنا بين مسكنهم باليمن وبين القرى المباركة قرى متقاربة يظهر بعضها لبعض، وجعلنا نسبة بعضها إلى بعض على مقدار مُعَيَّن من السير لا مشقة معه، وقلنا لهم: سيروا فيها ليالى وأياماً متمتعين بالأمن.',NULL,'637',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3623,'<span data-type="title" id=toc-3657>١٩ </span>- فقالوا - بطراً بنعمة الراحة والأمن -: ربنا باعد بين أسفارنا، فلا نصادف قرى عامرة فى طريقنا إلى مقاصدنا، فباعد الله بين أسفارهم، وظلموا أنفسهم بطغيانهم، فصيَّرناهم أحاديث للناس، وفرقناهم كل تفريق، إن فيما وقع لهم لعظات لكل صابر على البلاء، شكور على العطاء.',NULL,'637',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3624,'<span data-type="title" id=toc-3658>٢٠ </span>- ولقد حقَّق إبليس ظنه عليهم، فاتبعوه إلا فريقاً قليلا من المؤمنين.',NULL,'637',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3625,'<span data-type="title" id=toc-3659>٢١ </span>- وما كان لإبليس عليهم من قوة يخضعهم بها، ولكن الله امتحنهم ليُظهر من يُصدق بالآخرة ممن هو منها فى شك. وربك - أيها النبى - على كل شئ رقيب قائم على كل أمر.',NULL,'638',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3626,'<span data-type="title" id=toc-3660>٢٢ </span>- قل - أيها النبى - للمشركين: ادعو الذين ادَّعيتم باطلا أنهم شركاء من دون الله يجلبوا لكم نفعا أو يدفعوا عنكم ضراً. هم لا يجيبونكم لأنهم لا يملكون مقدار ذرة فى السموات ولا فى الأرض، وليس لهم فيهما شركة مع الله فى خلق أو ملك، وليس لله من هؤلاء الشركاء المزعومين من يُعينه على تدبير شئون خلقه.',NULL,'638',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3627,'<span data-type="title" id=toc-3661>٢٣ </span>- ولا تنفع الشفاعة عند الله إلا للمستأهلين لمقام الشفاعة، حتى إذا كشف الفزع عن قلوبهم بالإذن لهم فى الشفاعة قال بعضهم لبعض - مستبشرين -: ماذا قال ربكم؟ {فيجابون بأنه قال القول الحق بإذنه فى الشفاعة لمن ارتضى، وهو - وحده - صاحب العلو والكبرياء، ويأذن ويمنع من يشاء كما يشاء.',NULL,'638',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3628,'<span data-type="title" id=toc-3662>٢٤ </span>- قل - أيها النبى - للمشركين: من يأتيكم برزقكم من السموات والأرض؟} قل لهم - حين لا يجيبون عناداً -: الله - وحده - هو الذى يرزقكم منهما، وإننا معشر المؤمنين أو إياكم معشر المشركين لعلى أحد الأمرين من الهدى أو الضلال الواضح.',NULL,'638',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3629,'<span data-type="title" id=toc-3663>٢٥ </span>- قل لهم - أيها النبى -: لا تسألون عما أذنبنا ولا نسأل عن أعمالكم.',NULL,'638',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3630,'<span data-type="title" id=toc-3664>٢٦ </span>- قل لهم: يجمع بيننا ربنا يوم القيامة ثم يقضى بيننا بالحق، وهو - سبحانه - الحاكم فى كل أمر، العليم بحقيقة ما كان منا ومنكم.',NULL,'638',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3631,'<span data-type="title" id=toc-3665>٢٧ </span>- قل لهم: أرونى الذين ألحقتم بالله فى استحقاق العبادة تزعمون شركتهم له، ليس له شريك، بل هو الله الغالب على كل شئ. الحكيم فى تدبيره وتصريفه.',NULL,'638',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3632,'<span data-type="title" id=toc-3666>٢٨ </span>- وما أرسلناك - يا محمد - إلا للناس جميعاً بشيراً للمؤمنين بالخير، ونذيراً للكافرين بالشر، ولكن أكثر الناس لا يعلمون صدقك وعموم رسالتك.',NULL,'639',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3633,'<span data-type="title" id=toc-3667>٢٩ </span>- ويقول الكافرون - استبعاداً لليوم الموعود للجزاء -: متى هذا الوعد فندخل النار وتدخلون الجنة إن كنتم صادقين فى وعدكم به؟ {',NULL,'639',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3634,'<span data-type="title" id=toc-3668>٣٠ </span>- قل لهم - أيها النبى -: لكم ميعاد يوم عظيم لا تستأخرون عنه ساعة ولا تستقدمون.',NULL,'639',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3635,'<span data-type="title" id=toc-3669>٣١ </span>- وقال الذين كفروا: لن نصدق بهذا القرآن ولا بالكتب التى تقدمت عليه فيما تأمر به وتدعو إليه، ولو ترى - يا من تمكنك الرؤية - وقت وقف الظالمين عند خالقهم ومالك أمرهم لرأيت العجيب فى موقفهم حين يرد بعضهم إلى بعض القول، يقول المستضعفون للمستعلين عليهم: لولا أنتم - بتسلطكم علينا - لكنا مؤمنين.',NULL,'639',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3636,'<span data-type="title" id=toc-3670>٣٢ </span>- قال المستكبرون للمستضعفين - منكرين قولهم -: أنحن صددناكم عن الهدى بعد مجيئه لكم نصدكم عنه؟ . بل كنتم مؤثرين الضلالة على الهدى.',NULL,'639',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3637,'<span data-type="title" id=toc-3671>٣٣ </span>- وقال المستضعفون للمستكبرين: بل تدبيركم ووسوستكم لنا فى الليل والنهار أوقعنا فى التهلكة حين كنتم تطلبون منا أن نكفر بالله، ونجعل له شركاء، وأسر الفريقان الحسرة لما رأوا العذاب واقعاً بهم، فعلموا أن لا فائدة من إظهار هذه الحسرة، وجعلنا الأغلال فى أعناق الذين لم يؤمنوا. هل يستحق هؤلاء إلا جزاء ما كانوا يعملون؟}',NULL,'639',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3638,'<span data-type="title" id=toc-3672>٣٤ </span>- وما أرسلنا فى قرية من رسول يدعوهم إلى الحق إلا قال المترفون من أهلها: إنا بما جئتم به مكذبون.',NULL,'639',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3639,'<span data-type="title" id=toc-3673>٣٥ </span>- وقالوا - متباهين -: نحن أكثر أموالاً وأولاداً، وما نحن بمعذبين فى الآخرة.',NULL,'639',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3640,'<span data-type="title" id=toc-3674>٣٦ </span>- قل لهم - أيها النبى -: إن خالقى يُوسِّع الرزق لمن يشاء من العاصين والمطيعين ويُضيِّق على من يشاء، وليس ذلك دليل رضاه أو سخطه، ولكن أكثر الناس لا يعلمون.',NULL,'640',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3641,'<span data-type="title" id=toc-3675>٣٧ </span>- وليست أموالكم وأولادكم بالمزية التى تقربكم عندنا قربة، لكن من ثبت له الإيمان وعمل صالحاً فأولئك لهم الثواب المضاعف بما عملوا، وهم فى أعالى الجنات آمنون.',NULL,'640',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3642,'<span data-type="title" id=toc-3676>٣٨ </span>- والذين يسْعَون فى معارضة آياتنا - محاولين إبطالها وتعجيز أنبيائنا عن تبليغها - أولئك فى العذاب محضرون لا يفلتون.',NULL,'640',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3643,'<span data-type="title" id=toc-3677>٣٩ </span>- قل - أيها النبى -: إن ربى يُوسِّع الرزق لمن يشاء من عباده ويُضيِّق عليه، وما أنفقتم من شئ فهو يعوضه، وهو - سبحانه - خير الرازقين.',NULL,'640',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3644,'<span data-type="title" id=toc-3678>٤٠ </span>- واذكر - أيها النبى - يوم يحشرهم الله جميعاً. ثم يقول - سبحانه - للملائكة أمام من كانوا يعبدونهم: أهؤلاء خصُّوكم بالعبادة دونى؟!',NULL,'640',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3645,'<span data-type="title" id=toc-3679>٤١ </span>- قالت الملائكة: نُنزهك - تنزيها - عن أن يكون لك شريك، أنت الذى نواليه من دونهم، وهم واهمون فى زعمهم أنهم كانوا يعبدوننا، بل كانوا خاضعين لتأثير الشياطين الذين زينوا لهم الشرك أكثرهم بهم مصدقون.',NULL,'640',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3646,'<span data-type="title" id=toc-3680>٤٢ </span>- فيوم الحشر لا يملك بعضكم لبعض جلب نفع ولا دفع ضر، ونقول للظالمين أنفسهم: ذوقوا عذاب النار التى كنتم بها فى الدنيا تكذبون.',NULL,'640',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3647,'<span data-type="title" id=toc-3681>٤٣ </span>- وإذا تتلى على الكفار آياتنا واضحات الدلالة على الحق، قال الكافرون: ما هذا إلا رجل يُريد أن يمنعكم عمَّا كان يعبد آباؤكم، وقالوا: ما هذا القرآن إلا كذب مختلق، وقال الذين كفروا للقرآن لمَّا جاءهم: ما هذا إلا سحر واضح.',NULL,'641',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3648,'<span data-type="title" id=toc-3682>٤٤ </span>- وما أنزل الله على العرب من كتب سماوية يدرسونها، وما أرسلنا إليهم قبلك من نذير يخوفهم عاقبة جحودهم.',NULL,'641',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3649,'<span data-type="title" id=toc-3683>٤٥ </span>- وكَذَّب الذين سبقوا من الأمم أنبياءهم، وما بلغ مشركو قومك عُشْر ما آتينا هؤلاء السابقين من قوة وتمكين، فكذَّبوا رسلى، فكيف كان إنكارى عليهم بعقابى لهم؟ .',NULL,'641',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3650,'<span data-type="title" id=toc-3684>٤٦ </span>- قل لهم: إنما آمركم بخصلة واحدة هى: أن تقوموا - مخلصين لله بعيدين عن التقليد - فى البحث بإخلاص لله، ومتفرقين اثنين اثنين ليتعاونا فى التأمل، وواحداً واحداً ينظر بعدل وإنصاف، ثم تتفكروا فى أمر صاحبكم - محمد - الذى عاشرتموه وعرفتم سلامة عقله. ما به من جنون حين تصدى لهذا الأمر. إن هو إلا نذير لكم بعذاب شديد مقبل أمامكم.',NULL,'641',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3651,'<span data-type="title" id=toc-3685>٤٧ </span>- قل للكفار: أى شئ من أجر طلبته منكم على تبليغ الرسالة فهو لكم، ما أجرى الذى انتظره إلا على الله، وهو على كل شئ رقيب مطلع.',NULL,'641',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3652,'<span data-type="title" id=toc-3686>٤٨ </span>- قل لهم: إن ربى يرمى بالحق فى وجه الباطل فيمحقه، وهو علام الغيوب لا يخفى عليه سر.',NULL,'641',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3653,'<span data-type="title" id=toc-3687>٤٩ </span>- قل لهم: ظهر الإسلام، وما يصلح الباطل أن يكون وسيلة لدفع الحق، ولا أن يفيد وسائله السابقة.',NULL,'641',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3654,'<span data-type="title" id=toc-3688>٥٠ </span>- قل لهم: إن انحرفتُ عن الحق فإنما ضرر ذلك عائد على نفسى، وإن اهتديت فبإرشاد ربى، إنه سميع لقولى وقولكم، قريب منى ومنكم.',NULL,'642',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3655,'<span data-type="title" id=toc-3689>٥١ </span>- ولو ترى - أيها المبصر - حين فزع الكفار عند ظهور الحق فلا مهرب لهم، وأخذوا إلى النار من مكان قريب.',NULL,'642',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3656,'<span data-type="title" id=toc-3690>٥٢ </span>- وقالوا - عندما شاهدوا العذاب: - آمنا بالحق، وكيف يكون لهم تناول الإيمان بسهولة من مكان بعيد هو الدنيا التى انقضى وقتها؟',NULL,'642',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3657,'<span data-type="title" id=toc-3691>٥٣ </span>- وقد كفروا بالحق من قبل هذا اليوم، ويرجمون بالظن الباطل من مكان بعيد عن الصواب.',NULL,'642',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3658,'<span data-type="title" id=toc-3692>٥٤ </span>- وحيل بينهم وبين ما يشتهون من إيمان ينفعهم، كما فعلَ بأشياعهم من قبل عندما آمنوا بعد فوات الوقت، لأنهم - جميعاً - كانوا فى شك من الحق.',NULL,'642',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3659,'<span data-type="title" id=toc-3694>١ </span>- الثناء الجميل حق لله - وحده - موجد السموات والأرض على غير مثال سبق، جاعل الملائكة رسلاً إلى خلقه ذوى أجنحة مختلفة العدد، اثنين اثنين وثلاثا ثلاثا وأربعا أربعا يزيد فى الخلق ما يشاء أن يزيد، لا يعجزه شئ، إن الله على كل شئ عظيم القدرة.',NULL,'644',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3660,'<span data-type="title" id=toc-3695>٢ </span>- ما يرسل الله للناس رحمة - أى رحمة كانت مطرا أو نعمة أو أمنا أو حكمة - فلا أحد يحبسها عنهم، وما يحبس من ذلك فلا أحد يستطيع أن يطلقه من بعده، وهو الغالب الذى لا يغلب، الحكيم الذى لا يخطئ.',NULL,'644',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3661,'<span data-type="title" id=toc-3696>٣ </span>- يا أيها الناس اذكروا نعمة الله عليكم بشكرها وتأدية حقها، وأقروا بما يقع فى نفوسكم إنه لا خالق غير الله، يرزقكم من السماء بما ترسله، والأرض بما تخرجه مما به حياتكم. لا إله إلا هو يرزق عباده، فكيف تصرفون عن توحيد خالقكم ورازقكم إلى الشرك فى عبادته؟',NULL,'644',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3662,'<span data-type="title" id=toc-3697>٤ </span>- وإن يكذبك كفار قومك فيما جئتهم به من الهدى فاصبر عليهم، فقد كذبت رسل من قبلك فصبروا على ما كُذِّبوا حتى انتصروا، وإلى الله - وحده - ترجع الأمور كلها.',NULL,'644',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3663,'<span data-type="title" id=toc-3698>٥ </span>- يا أيها الناس: إن وعد الله - بالبعث والجزاء والنصر - حق فلا تخدعنكم الدنيا عن الآخرة، ولا يخدعنكم الشيطان عن اتباع الرسل، فيمنيكم بالمغفرة مع الإصرار على المعصية.',NULL,'644',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3664,'<span data-type="title" id=toc-3699>٦ </span>- إن الشيطان لكم عدو قديم فلا تنخدعوا بوعوده فاتخذوه عدوا، إنما يدعو متابعيه ليكونوا من أصحاب النار المشتعلة لا يدعوهم لغيرها.',NULL,'644',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3665,'<span data-type="title" id=toc-3700>٧ </span>- الذين كفروا بالله ورسله لهم عذاب شديد، والذين آمنوا بالله ورسله وعملوا الصالحات لهم عند الله مغفرة لذنوبهم وأجر كبير على أعمالهم.',NULL,'644',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3666,'<span data-type="title" id=toc-3701>٨ </span>- أفقدوا التمييز؟ ، فمن زيَّن له الشيطان عمله السيئ فرآه حسنا كمن اهتدى بهدى الله فرأى الحسن حسنا والسيئ سيئا!؟ فإن الله يضل من يشاء ممن ارتضوا سبيل الضلال سبيلا، ويهدى من يشاء ممن اختاروا سبيل الهداية سبيلا. فلا تهلك نفسك حزنا على الضالين وحسرة عليهم. إن الله محيط علمه بما يصنعون من شر، فيجزيهم به.',NULL,'644',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3667,'<span data-type="title" id=toc-3702>٩ </span>- والله - وحده - هو الذى أرسل الرياح فتحرك سحابا تراكم من أبخرة الماء، فسقنا السحاب إلى بلد مجدب، فأحيينا أرضه بالنبات بعد موتها، مثل إخراجنا النبات من الأرض نُخرج الموتى من القبور يوم القيامة.',NULL,'645',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3668,'<span data-type="title" id=toc-3703>١٠ </span>- من كان يريد الشرف والقوة فليطلبها بطاعة الله، فإن له القوة كلها، إليه يعلو الكلم الطيب، ويرفع الله العمل الصالح فيقبله، والذين يدبرون للمؤمنين المكيدات التى تسوؤهم لهم عذاب شديد، وتدبيرهم فاسد، لا يحقق غرضا ولا ينتج شيئا.',NULL,'645',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3669,'<span data-type="title" id=toc-3704>١١ </span>- والله أوجدكم من تراب، إذ خلق أباكم آدم منه، ثم خلقكم من نطفة هى الماء الذى يصب فى الأرحام، وهى أيضاً من أغذية تخرج من التراب، ثم جعلكم ذكراناً وإناثاً، وما تحمل من أنثى ولا تضع حملها إلا بعلمه تعالى، وما يمد فى عمر أحد ولا ينقص من عمره إلا مسجل فى كتاب. إن ذلك على الله سهل هين.',NULL,'645',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3670,'<span data-type="title" id=toc-3705>١٢ </span>- وما يستوى البحران فى علمنا وتقديرنا وإن اشتركا فى بعض منافعهما، هذا ماؤه عذب يقطع العطش لشدة عذوبته وحلاوته وسهولة تناوله، وهذا ملح شديد الملوحة. ومن كل منهما تأكلون لحماً طريا مما تصيدون من الأسماك وتستخرجون ما تتخذونه زينة كاللؤلؤ والمرجان. وترى - أيها المشاهد - السفن تجرى فيه شاقة الماء بسرعتها، لتطلبوا شيئاً من فضل الله بالتجارة، ولعلكم تشكرون لربكم هذه النعم.',NULL,'645',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3671,'<span data-type="title" id=toc-3706>١٣ </span>- يُدخل الليل فى النهار ويُدخل النهار فى الليل بطول ساعات أحدهما وقصرها فى الآخر. حسب أوضاع محكمة مدى الأعوام والدهور، وسخر الشمس والقمر لمنفعتكم، كل منهما يجرى إلى أجل معين ينتهى إليه. ذلك العظيم الشأن هو الله مدبِّر أموركم، له الملك - وحده - والذين تدعون من غيره آلهة تعبدونها ما يملكون من لفافة نواة، فكيف يستأهلون العبادة؟!',NULL,'646',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3672,'<span data-type="title" id=toc-3707>١٤ </span>- إن تدعوا الذين تعبدونهم من دون الله لا يسمعوا دعاءكم، ولو سمعوا دعاءكم ما أجابوا شيئاً مما تطلبون، ويوم القيامة ينكرون إشراككم لهم مع الله، ولا يخبركم بهذا الخبر من أحوال الآخرة مثل عليم به علماً دقيقاً.',NULL,'646',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3673,'<span data-type="title" id=toc-3708>١٥ </span>- يا أيها الناس: أنتم المحتاجون إلى الله فى كل شئ، والله هو الغنى - وحده - عن كل خلقه، المستحق للحمد على كل حال.',NULL,'646',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3674,'<span data-type="title" id=toc-3709>١٦ </span>- إن يشأ الله إهلاككم أهلككم لتمام قدرته، ويأت بخلق جديد ترضاه حكمته.',NULL,'646',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3675,'<span data-type="title" id=toc-3710>١٧ </span>- وما هلاككم والإتيان بغيركم بممتنع على الله.',NULL,'646',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3676,'<span data-type="title" id=toc-3711>١٨ </span>- ولا تحمل نفس مذنبة إثم نفس أخرى، وإن تدع نفس مثقلة بالذنوب شخصاً ليحمل عنها لا يحمل هذا الشخص من ذنوبها شيئاً، ولو كان ذا قرابة بها، لاشتغال كل بنفسه، ولا يحزنك - أيها النبى - عناد قومك، إنما ينفع تحذيرك الذين يخافون ربهم فى خلواتهم، وأقاموا الصلاة على وجهها، ومن تطهر من دنس الذنوب فإنما يتطهر لنفسه، وإلى الله المرجع فى النهاية، فيعامل كلا بما يستحق.',NULL,'646',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3677,'<span data-type="title" id=toc-3712>١٩ </span>-، ٢٠، ٢١ - وما يستوى الذى لا يهتدى إلى الحق لجهله، والذى يسلك طريق الهداية لعلمه، ولا الباطل ولا الحق، ولا الظل ولا الريح الحارة.',NULL,'647',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3678,'<span data-type="title" id=toc-3713>٢٢ </span>- ولا يستوى الأحياء بقبول الإيمان، ولا الأموات الذين عطلت حواسهم وأغلقت قلوبهم عن سماع الحق، إن الله يهدى من يشاء إلى سماع الحجة سماع قبول، وما أنت - أيها النبى - بمسمع أموات القلوب بالعناد والكفر، كما أنك لا تسمع الموتى فى القبور.',NULL,'647',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3679,'<span data-type="title" id=toc-3714>٢٣ </span>- ما عليك إلا أن تبلغ وتنذر.',NULL,'647',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3680,'<span data-type="title" id=toc-3715>٢٤ </span>- إنا أرسلناك - أيها النبى - للناس جميعاً بالدين الحق، مبشراً من آمن به بالجنة، ومُنْذرا من كفر به بعذاب النار، وما من أمة من الأمم الماضية إلا جاءها من قبل الله من يحذرها عقابه.',NULL,'647',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3681,'<span data-type="title" id=toc-3716>٢٥ </span>- وإن يكذبك قومك فى ذلك فقد كذب الذين من قبلهم رسلهم، وقد جاءوهم بالمعجزات الواضحات وبالصحف الربانية وبالكتاب المنير لطريق النجاة فى الدنيا والآخرة.',NULL,'647',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3682,'<span data-type="title" id=toc-3717>٢٦ </span>- ثم أخذت الذين كفروا أخذاً شديداً، فانظر كيف كان إنكارى لعملهم وغضبى عليهم؟ .',NULL,'647',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3683,'<span data-type="title" id=toc-3718>٢٧ </span>- ألم تر - أيها العاقل - أنَّ الله أنزل من السماء ماء فأخرج به ثمرات مختلفاً ألوانها، منها الأحمر والأصفر والحلو والمر والطيب والخبيث، ومن الجبال جبال ذوو طرائق وخطوط بيض وحمر مختلفة بالشدة والضعف.',NULL,'647',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3684,'<span data-type="title" id=toc-3719>٢٨ </span>- ومن الناس والدواب والإبل والبقر والغنم مختلف ألوانه كذلك فى الشكل والحجم واللون. وما يتدبر هذا الصنع العجيب ويخشى صانعه إلا العلماء الذين يدركون أسرار صنعه، إن الله غالب يخشاه المؤمنون، غفور كثير المحو لذنوب من يرجع إليه.',NULL,'648',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3685,'<span data-type="title" id=toc-3720>٢٩ </span>- إن الذين يتلون كتاب الله، متدبرين فيه عاملين به، وأقاموا الصلاة على وجهها الصحيح، وأنفقوا بعض ما رزقهم الله سراً وجهراً، يرجون بذلك تجارة مع الله لن تكسد.',NULL,'648',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3686,'<span data-type="title" id=toc-3721>٣٠ </span>- ليوفيهم ربهم أجورهم ويزيدهم من فضله، بما يربى من حسناتهم ويمحو من سيئاتهم، إنه غفور كثير المحو للهفوات، شكور كثير الشكر للطاعات.',NULL,'648',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3687,'<span data-type="title" id=toc-3722>٣١ </span>- والذى أوحينا إليك من القرآن هو الحق الذى لا شبهة فيه، أنزلناه مصدقاً لما تقدم من الكتب المنزلة على الرسل قبلك، لاتفاق أصولها، إن الله بعباده واسع الخبرة والبصر.',NULL,'648',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3688,'<span data-type="title" id=toc-3723>٣٢ </span>- ثم جعلنا هذا الكتاب ميراثاً للذين اخترناهم من عبادنا، فمنهم ظالم لنفسه بغلبة سيئاته على حسناته، ومنهم مقتصد لم يسرف فى السيئات ولم يكثر من الحسنات، ومنهم سابق غيره بفعل الخيرات بتيسير الله، ذلك السبق بالخيرات هو الفوز الكبير من الله.',NULL,'648',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3689,'<span data-type="title" id=toc-3724>٣٣ </span>- جزاؤهم فى الآخرة جنات إقامة يدخلونها، يتزيَّنون فيها بأساور من ذهب ولؤلؤا، وثيابهم فى الجنة حرير.',NULL,'648',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3690,'<span data-type="title" id=toc-3725>٣٤ </span>- وقالوا وقد دخلوها: الثناء الجميل لله الذى أذهب عنا ما يحزننا. إن ربنا لكثير المغفرة كثير الشكر.',NULL,'648',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3691,'<span data-type="title" id=toc-3726>٣٥ </span>- الذى أنزلنا دار النعيم المقيم من فضله لا يصيبنا فيها تعب، ولا يمسنا فيها إعياء.',NULL,'649',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3692,'<span data-type="title" id=toc-3727>٣٦ </span>- والذين كفروا جزاؤهم المعد لهم نار جهنم يدخلونها، لا يقضى عليهم الله بالموت فيموتوا، ولا يخفف عنهم شئ من عذابها فيستريحوا. كذلك نجزى به كل متمادٍ فى الكفر مصرٍّ عليه.',NULL,'649',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3693,'<span data-type="title" id=toc-3728>٣٧ </span>- وهم يستغيثون فيها قائلين: ربنا أخرجنا من النار نعمل صالحاً غير العمل الذى كنا نعمله فى الدنيا، فيقول لهم: ألم نمكنكم من العمل ونطل أعماركم زمناً يتمكن فيه من التدبر من يتدبر، وجاءكم الرسول يحذركم من هذا العذاب؟ فذوقوا فى جهنم جزاء ظلمكم، فليس للظالمين من ناصر أو معين.',NULL,'649',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3694,'<span data-type="title" id=toc-3729>٣٨ </span>- إن الله مطلع على كل غائب فى السموات والأرض، لا يغيب عن علمه شئ، ولو أجابكم وأعادكم إلى الدنيا لعدتم إلى ما نهاكم عنه. إنه - تعالى - عليم بخفايا الصدور من النزعات والميول.',NULL,'649',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3695,'<span data-type="title" id=toc-3730>٣٩ </span>- الله هو الذى جعل بعضكم يخلف بعضاً فى تعمير الأرض وتثميرها، وهو حقيق بالشكر لا بالكفر، فمن كفر بالله فعليه وزر كفره، ولا يزيد الكافرين كفرهم عند ربهم إلا بغضاً وغضباً، ولا يزيد الكافرين إلا خسراناً.',NULL,'649',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3696,'<span data-type="title" id=toc-3731>٤٠ </span>- قل - أيها النبى - للمشركين: أخبرونى: أأبصرتم حال شركائكم الذين تعبدونهم من دون الله؟ {أخبرونى: أى جزء خلقوا من الأرض؟} بل ألهم شركة مع الله فى خلق السموات؟! لم نعطهم كتاباً بالشركة فهم على حجة منه، بل ما يعد الظالمون بعضهم بعضاً بشفاعة الآلهة التى يشركونها مع الله إلا باطلا وزخرفاً لا يخدع إلا ضعاف العقول.',NULL,'649',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3697,'<span data-type="title" id=toc-3732>٤١ </span>- إن الله هو الذى يمنع اختلال نظام السموات والأرض، ويحفظهما بقدرته من الزوال، ولئن قدِّر لهما الزوال ما استطاع أحد أن يحفظهما بعد الله. إنه كان حليماً لا يُعجل بعقوبة المخالفين غفوراً لذنوب الراجعين إليه.',NULL,'650',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3698,'<span data-type="title" id=toc-3733>٤٢ </span>- وأقسم الكافرون بالله غاية اجتهادهم فى تأكيد يمينهم: لئن جاءهم رسول ينذرهم ليكونن أكثر هداية من إحدى الأمم التى كذبت رسلها، فلما جاءهم رسول منهم ينذرهم ما زادهم بإنذاره ونصحه إلا نفوراً عن الحق.',NULL,'650',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3699,'<span data-type="title" id=toc-3734>٤٣ </span>- نفروا استكباراً فى الأرض وأنفة من الخضوع للرسول والدين الذى جاء به، ومكروا مكر السيئ - وهو الشيطان الذى قادهم إلى الانصراف عن الدين ومحاربة الرسول - ولا يحيط ضرر المكر السيئ إلا بمن دبروه، فهل ينتظرون إلا ما جرت به سنة الله فى الذين سبقوهم؟ فلن تجد لطريقة الله فى معاملة الأمم تغييراً يُطمِّع هؤلاء الماكرين فى وضع لم يكن لمن سبقوهم، ولن تجد لسنة الله تحويلا عن اتجاهها.',NULL,'650',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3700,'<span data-type="title" id=toc-3735>٤٤ </span>- اقعدوا وأنكروا وعيد الله للمشركين، ولم يسيروا فى الأرض فينظروا بأعينهم آثار الهلاك الذى أنزل على من قبلهم عقاباً لتكذيبهم الرسل؟! وكان من قبلهم من الأمم أشد منهم قوة، فلم تمنعهم قوتهم من عذاب الله، وما كان ليعجزه من شئ فى السموات ولا فى الأرض. إنه واسع العلم عظيم القدر.',NULL,'650',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3701,'<span data-type="title" id=toc-3736>٤٥ </span>- ولو يعاقب الله الناس فى الدنيا لعم العقاب، وما ترك على ظهر الأرض دابة، لصدور الذنوب منهم جميعاً، ولكن يؤخر عقابهم إلى زمن معين هو يوم القيامة، فإذا جاء أجلهم المضروب لهم فسيجازيهم بكل دقة، لأنه كان بأعمال عباده بصيراً، لا يخفى عليه شئ منها.',NULL,'650',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3702,'<span data-type="title" id=toc-3738>١ </span>-<span data-type="title" id=toc-3737> يس: </span>حرفان بُدئت بهما السورة على طريقة القرآن فى بدء بعض السور بالحروف المقطعة.',NULL,'651',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3703,'<span data-type="title" id=toc-3739>٢ </span>- أقسم بالقرآن المشتمل على الحكمة والعلم النافع.',NULL,'651',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3704,'<span data-type="title" id=toc-3740>٣ </span>- إنك يا محمد لمن الذين بعثهم الله إلى الناس بالهدى ودين الحق.',NULL,'651',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3705,'<span data-type="title" id=toc-3741>٤ </span>- على طريق معتدل، هو دين الإسلام.',NULL,'651',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3706,'<span data-type="title" id=toc-3742>٥ </span>- تنزيل القوى الغالب على كل شئ الذى لا يستطيع أحد أن يمنعه عما يريد، الرحيم بعباده، إذ أرسل إليهم من يرشدهم إلى طريق النجاة.',NULL,'652',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3707,'<span data-type="title" id=toc-3743>٦ </span>- لتُنذر قوماً لم ينذر آباؤهم الأقربون من قبل، فهم ساهون عمَّا يجب عليهم نحو الله ونحو أنفسهم ونحو الناس.',NULL,'652',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3708,'<span data-type="title" id=toc-3744>٧ </span>- لقد سبق فى علمنا أن أكثرهم لا يختارون الإيمان، فطابق واقعهم ما علمناه عنهم، فلن يكون منهم الإيمان.',NULL,'652',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3709,'<span data-type="title" id=toc-3745>٨ </span>- إنا جعلنا المصرين على الكفر كمن وضعت فى أعناقهم السلاسل، فهى تصل إلى أذقانهم، وتشد أيديهم برؤوسهم وترفعها مع غض أبصارهم، فلا يستطيعون أن يحركوا الرءوس ليروا.',NULL,'652',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3710,'<span data-type="title" id=toc-3746>٩ </span>- وجعلنا من حُرموا النظر فى الآيات والدلائل كمن حبسوا بين سدَّين فغطّيت أعينهم فهم لا يرون ما أمامهم وما خلفهم.',NULL,'652',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3711,'<span data-type="title" id=toc-3747>١٠ </span>- وسواء عليهم تحذيرك لهم وعدم تحذيرك، فهم لا يؤمنون.',NULL,'652',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3712,'<span data-type="title" id=toc-3748>١١ </span>- إنما يفيد تحذيرك من يتبع القرآن ويخاف الرحمن - وإن كان لا يراه - فبشر هؤلاء بعفو من الله عن سيئاتهم، وجزاء حسن على أعمالهم.',NULL,'652',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3713,'<span data-type="title" id=toc-3749>١٢ </span>- إنا نحن نحيى الموتى، ونُسجِّل ما قدموا فى الدنيا من أعمال وما أبقوا فيها من آثار بعد موتهم، وكل شئ أثبتناه فى كتاب واضح.',NULL,'652',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3714,'<span data-type="title" id=toc-3750>١٣ </span>- واذكر - أيها النبى - لقومك: قصة أهل القرية فإنها كقصتهم، إذ ذهب إليهم المرسلون لهدايتهم.',NULL,'652',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3715,'<span data-type="title" id=toc-3751>١٤ </span>- أرسلنا إليهم اثنين فكذبوهما، فقويناهما بثالث، فقال هؤلاء الثلاثة: إنا إليكم مرسلون.',NULL,'652',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3716,'<span data-type="title" id=toc-3752>١٥ </span>- قال أهل القرية - ردا عليهم -: ما أنتم إلا بشر مثلنا، وما أوْحى الرحمن إلى بشر من شئ، ما أنتم إلا قوم تقولون غير الواقع.',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3717,'<span data-type="title" id=toc-3753>١٦ </span>- قال المرسلون: ربنا الذى بعثنا إليكم يعلم إنا إليكم لمرسلون.',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3718,'<span data-type="title" id=toc-3754>١٧ </span>- وليس علينا إلا أن نُبَلِّغ رسالة الله بلاغاً واضحاً.',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3719,'<span data-type="title" id=toc-3755>١٨ </span>- قال أهل القرية: إننا تشاءمنا بكم. ونُقسم: إن لم تكفوا عن دعوتكم لنرمينكم بالحجارة، وليصيبنكم منا عذاب شديد الألم.',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3720,'<span data-type="title" id=toc-3756>١٩ </span>- قال المرسلون: شؤمكم معكم بكفركم، أئن وُعظْتم بما فيه سعادتكم تتشاءموا منا وتهددونا بالعذاب الأليم؟! لكن أنتم قوم متجاوزون الحق والعدل.',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3721,'<span data-type="title" id=toc-3757>٢٠ </span>- وأقبل من أبعد مكان بالمدينة رجل يُسرع نحو أهل المدينة، قال: يا قوم، اتبعوا المرسلين من الله إليكم.',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3722,'<span data-type="title" id=toc-3758>٢١ </span>- اتبعوا الذين لا يطلبون منكم أجراً على نصحكم وإرشادكم - وهم مهتدون - تنتفعون بهديهم فى سلوك طريق الخير والفلاح.',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3723,'<span data-type="title" id=toc-3759>٢٢ </span>- وأى شئ يمنعنى أن أعبد الذى خلقنى وإليه - لا إلى غيره - مرجعكم ومصيركم للحساب والجزاء.؟',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3724,'<span data-type="title" id=toc-3760>٢٣ </span>- أأتخذ من دون الله آلهة لا تفيدنى شفاعتهم شيئاً إن أرادنى الله بسوء، ولا يخلصوننى منه إن نزل بى؟',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3725,'<span data-type="title" id=toc-3761>٢٤ </span>- إنى - إذْ أتخذ من دونه آلهة - لفى ضلال مبين.',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3726,'<span data-type="title" id=toc-3762>٢٥ </span>- إنى صدقت بربكم الذى خلقكم وتولى أمركم، فاسمعوا لى وأطيعون.',NULL,'653',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3727,'<span data-type="title" id=toc-3763>٢٦ </span>-، ٢٧ - قيل له - جزاء على إيمانه ودعوته إلى الله -: ادخل الجنة قال - وهو فى ظل النعيم والكرامة -: يا ليت قومى يعلمون بغفران ربى وإكرامه لى، ليؤمنوا كما آمنت.',NULL,'654',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3728,'<span data-type="title" id=toc-3764>٢٨ </span>- وما أهلكناهم بجنود أنزلناها من السماء، وما كان من سنتنا فى إهلاك الأمم أن ننزل جنوداً.',NULL,'654',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3729,'<span data-type="title" id=toc-3765>٢٩ </span>- ما كان هلاكهم إلا بصيحة واحدة أرسلناها عليهم، فإذا هم ميِّتون كالنار الخامدة.',NULL,'654',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3730,'<span data-type="title" id=toc-3766>٣٠ </span>- يا خسارتهم - التى تستحق التحسر عليهم - ما نبعث إليهم برسول إلا كانوا منه يسخرون.',NULL,'654',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3731,'<span data-type="title" id=toc-3767>٣١ </span>- ألم يعتبروا بالأمم الكثيرة الخالية التى أهلكناها، أنهم لا يعودون كرة أخرى إلى حياتهم الدنيا؟',NULL,'654',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3732,'<span data-type="title" id=toc-3768>٣٢ </span>- وما كل من الأمم السابقة واللاحقة إلا مجموعون لدينا يوم الحساب والجزاء.',NULL,'654',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3733,'<span data-type="title" id=toc-3769>٣٣ </span>- ودليل لهم على قدرتنا على البعث والنشور: الأرض الجدبة أحييناها بالماء، وأخرجنا منها حباً، فمنه يأكلون.',NULL,'654',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3734,'<span data-type="title" id=toc-3770>٣٤ </span>-، ٣٥ - وأنشأنا فيها حدائق وبساتين من نخيل وأعناب، وشققنا فيها من عيون الماء ما يروى شجرها ويخرج ثمرها ليأكلوا منه، وما هو من صنع أيديهم، أفلا يؤدون حق الله عليهم فى ذلك بالإيمان والثناء عليه؟ .',NULL,'654',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3735,'<span data-type="title" id=toc-3771>٣٦ </span>- تنزيهاً لله الذى خلق الأشياء كلها على سنة الذكورة والأنوثة من النبات ومن الأنفس ومما لا يعلم الناس.',NULL,'654',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3736,'<span data-type="title" id=toc-3772>٣٧ </span>- وآية لهم على وجود الله وقدرته الليل ننزع عنه النهار الساتر له، فإذا الناس داخلون فى الظلام المشتمل عليهم من كل جانب.',NULL,'655',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3737,'<span data-type="title" id=toc-3773>٣٨ </span>- والشمس تسير لمستقر لها، قدَّره الله زماناً ومكاناً، ذلك تدبير الغالب بقدرته، المحيط علماً بكل شئ.',NULL,'655',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3738,'<span data-type="title" id=toc-3774>٣٩ </span>- والقمر جعلناه بتدبير منا منازل، إذ يبدو أول الشهر ضئيلا، ثم يزداد ليلة بعد ليلة إلى أن يكتمل بدراً، ثم يأخذ فى النقصان كذلك حتى يعود فى مرآه كأصل العنقود من الرطب إذا قدم فدق وانحنى واصفر.',NULL,'655',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3739,'<span data-type="title" id=toc-3775>٤٠ </span>- لا الشمس يتأتى لها أن تخرج على نواميسها فتلحق القمر وتدخل فى مداره، ولا الليل يتأتى له أن يغلب النهار ويحول دون مجيئه، بل هما متعاقبان. وكل من الشمس والقمر وغيرهما يسبح فى فلك لا يخرج عنه.',NULL,'655',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3740,'<span data-type="title" id=toc-3776>٤١ </span>- وآية أخرى لهم أنا حملنا بنى الإنسان فى السفن المملوءة بهم وبأمتعتهم وأرزاقهم.',NULL,'655',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3741,'<span data-type="title" id=toc-3777>٤٢ </span>- وخلقنا لهم من مثل الفلك ما يركبونه كذلك.',NULL,'655',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3742,'<span data-type="title" id=toc-3778>٤٣ </span>- وإن نُرد إغراقهم بما كسبوا نُغرقهم، - فليس لهم مغيث، ولا هم ينجون من الهلاك.',NULL,'655',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3743,'<span data-type="title" id=toc-3779>٤٤ </span>- لكنا لا نغرقهم رحمة منا بهم، ولنمتعهم إلى أجل مقدر.',NULL,'655',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3744,'<span data-type="title" id=toc-3780>٤٥ </span>- وإذا قيل لهم: خافوا مثل ما جرى للأمم الماضية بتكذيبهم، وخافوا عذاب الآخرة الذى تتعرضون له بإصراركم على الكفر - رجاء أن يرحمكم ربكم إذا اتقيتموه - أعرضوا.',NULL,'656',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3745,'<span data-type="title" id=toc-3781>٤٦ </span>- وما تجيئهم من حجة من حجج ربهم دالةً على وحدانية الله وقدرته إلا كانوا عنها منصرفين.',NULL,'656',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3746,'<span data-type="title" id=toc-3782>٤٧ </span>- وإذا قيل لهم أنفقوا على الفقراء مما رزقكم الله قال الكافرون للمؤمنين: أنطعم من لو أراد الله إطعامه، فنعاند بهذا مشيئة الله؟ ما أنتم - أيها الداعون إلى الإنفاق - إلا فى عمى واضح عن الحق.',NULL,'656',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3747,'<span data-type="title" id=toc-3783>٤٨ </span>- ويقولون للمؤمنين - استهزاء بهم -: متى يقع هذا الذى وعدتمونا به إن كنتم صادقين فيما وعدتم؟!',NULL,'656',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3748,'<span data-type="title" id=toc-3784>٤٩ </span>- ما ينتظرون إلا صوتاً واحداً يقضى عليهم بغتة، وهم يتنازعون فى شئون الدنيا، غافلين عن الآخرة.',NULL,'656',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3749,'<span data-type="title" id=toc-3785>٥٠ </span>- فلا يستطيعون - لسرعة ما نزل بهم - أن يُوصلوا بشئ، ولا أن يرجعوا إلى أهلهم.',NULL,'656',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3750,'<span data-type="title" id=toc-3786>٥١ </span>- ونفخ فى الصور نفخة البعث، فإذا الأموات يخرجون من قبورهم مسرعين للقاء الله. والصور والنفخ فيه مما استأثر الله بعلمه.',NULL,'656',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3751,'<span data-type="title" id=toc-3787>٥٢ </span>- قال المبعوثون من القبور: يا هول ما ينتظرنا، من أيقظنا من نومنا؟ ويحضرهم جواب سؤالهم: هذا يوم البعث الذى وعد الرحمن به عباده، وصدق المرسلون فيما أخبروا عنه.',NULL,'656',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3752,'<span data-type="title" id=toc-3788>٥٣ </span>- ما كانت دعوتهم إلى الخروج إلا نداءً واحداً، فإذا هم مجتمعون لدينا، محضرون لحسابنا.',NULL,'656',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3753,'<span data-type="title" id=toc-3789>٥٤ </span>- ففى هذا اليوم لا تنقص نفس أجر شئ مما عملته، ولا تلقون إلا جزاء ما كنتم تعملون من خير أو شر.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3754,'<span data-type="title" id=toc-3790>٥٥ </span>- إن أصحاب الجنة فى هذا اليوم مشغولون بما هم فيه من نعيم، معجبون به فرحون.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3755,'<span data-type="title" id=toc-3791>٥٦ </span>- هم وأزواجهم فى ظلال سابغة على السرر المزينة متكئون.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3756,'<span data-type="title" id=toc-3792>٥٧ </span>- لهم فى الجنة فاكهة من كل أنواعها، ولهم فيها كل ما يطلبون.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3757,'<span data-type="title" id=toc-3793>٥٨ </span>- يقال لهم: سلام قولاً صادراً من رب رحيم.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3758,'<span data-type="title" id=toc-3794>٥٩ </span>- ويقال للمجرمين فى هذا اليوم: اعتزلوا عن المؤمنين.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3759,'<span data-type="title" id=toc-3795>٦٠ </span>- ألم أوصكم - يا بنى آدم - ألا تطيعوا الشيطان طاعة المعبود؟ إنه لكم عدو بَيِّن العداوة.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3760,'<span data-type="title" id=toc-3796>٦١ </span>- وأن افردونى بالعبادة، فإفرادى بها طريق عظيم فى استقامته.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3761,'<span data-type="title" id=toc-3797>٦٢ </span>- ولقد أغوى الشيطان منكم خلْقاً كثيراً. أغفلتم عن ذلك، فلم تكونوا تعقلون حين أطعتموه؟!',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3762,'<span data-type="title" id=toc-3798>٦٣ </span>- يقال لهم: هذه جهنم التى كنتم توعدون بها فى الدنيا، جزاء كفركم.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3763,'<span data-type="title" id=toc-3799>٦٤ </span>- ادخلوها، وقاسوا حرها فى هذا اليوم بكفركم.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3764,'<span data-type="title" id=toc-3800>٦٥ </span>- اليوم نُغطى على أفواههم فلا تنطق، وتكلمنا أيديهم، وتنطق أرجلهم شاهدة عليهم بما كانوا يعملون.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3765,'<span data-type="title" id=toc-3801>٦٦ </span>- فمضوا يتخبطون لا يعرفون فيها - فى الدنيا أو على الصراط فى الآخرة - طريق الهدى بعدما أعميناهم.',NULL,'657',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3766,'<span data-type="title" id=toc-3802>٦٧ </span>- ولو نشاء تغيير صورهم لغيرناها إلى صور قبيحة يتسمرون عندها فى أماكنهم، لا يمضون إلى الأمام، ولا يرجعون إلى الخلف لما جرى عليهم من أمرنا فى إفقادهم قواهم.',NULL,'658',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3767,'<span data-type="title" id=toc-3803>٦٨ </span>- ومن نُطل عمره نرده من القوة إلى الضعف، أفلا يعقلون قدرتنا على ذلك ليعلموا أن الدنيا دار فناء، وأن الآخرة هى دار البقاء!',NULL,'658',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3768,'<span data-type="title" id=toc-3804>٦٩ </span>- وما علمنا رسولنا الشعر، وما يصح - لمكانته ومنزلته - أن يكون شاعراً. وما القرآن المنزل عليه إلا عظة وكتاب سماوى واضح، فلا مناسبة بينه وبين الشعر.',NULL,'658',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3769,'<span data-type="title" id=toc-3805>٧٠ </span>- ليخوف من كان حى القلب مُستنير العقل، وتَجِبُ كلمة العذاب على الجاحدين به، المنكرين لهديه.',NULL,'658',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3770,'<span data-type="title" id=toc-3806>٧١ </span>- أعمى الكافرون ولم يروا أنا خلقنا لهم مما صنعت قدرتنا أنعاماً فهم مالكون لها، يتصرفون فيها كما يشاءون؟',NULL,'658',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3771,'<span data-type="title" id=toc-3807>٧٢ </span>- وأخضعناها لهم، فمنها ما يركبون، ومنها ما يأكلون.',NULL,'658',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3772,'<span data-type="title" id=toc-3808>٧٣ </span>- ولهم فيها ما ينتفعون به من أصوافها وأوبارها وأشعارها وجلودها وعظامها، ومشارب من ألبانها، أينسون هذه النعم فلا يشكرون المنعم بها؟ .',NULL,'658',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3773,'<span data-type="title" id=toc-3809>٧٤ </span>- واتخذ المشركون من دون الله آلهة يعبدونها، رجاء أن تنصرهم.',NULL,'658',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3774,'<span data-type="title" id=toc-3810>٧٥ </span>- لا تستطيع الآلهة نصرهم إن أراد الله بهم سوءاً، لأنها لا تنفع ولا تضر، وهم لآلهتهم العاجزة جند معدون لخدمتهم ودفع السوء عنهم.',NULL,'658',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3775,'<span data-type="title" id=toc-3811>٧٦ </span>- فلا يحزنك قولهم فى الله بالإلحاد وفيك بالتكذيب، إنا نعلم ما يُخفون وما يُعلنون، فنجازيهم عليه.',NULL,'659',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3776,'<span data-type="title" id=toc-3812>٧٧ </span>- أجحد الإنسان وجود الله وقدرته. ولم ير أنا خلقناه - بعد العدم - من نطفة مهينة؟ فإذا هو شديد الخصومة، مبين لها، معلن عنها.',NULL,'659',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3777,'<span data-type="title" id=toc-3813>٧٨ </span>- وساق لنا هذا الخصيم المبين مثلاً ينكر به قدرتنا على إحياء العظام بعد أن تبلى، ونسى خلقنا إياه بعد أن لم يكن، قال - منكراً مستبعداً قدرتنا على ذلك -: من يُحيى العظام وهى رميم؟',NULL,'659',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3778,'<span data-type="title" id=toc-3814>٧٩ </span>- قل - يا محمد -: يُحييها الذى أنشأها أول مرة، ففى استطاعة من بدأ أن يُعيد، وهو عظيم العلم بكل ما خلق، فلا يعجزه جمع الأجزاء بعد تفرقها.',NULL,'659',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3779,'<span data-type="title" id=toc-3815>٨٠ </span>- الذى خلق لكم من الشجر الأخضر - بعد جفافه ويبسه - ناراً.',NULL,'659',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3780,'<span data-type="title" id=toc-3816>٨١ </span>- أفقدوا عقولهم ولم يعلموا أن الذى خلق السموات والأرض - مع عظم حجمهما - قادر على إعادة خلق الناس مع صغرهم وضعف شأنهم؟ بلى - أى هو القادر - وهو الكثير الخلق، المحيط علمه بكل شئ.',NULL,'659',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3781,'<span data-type="title" id=toc-3817>٨٢ </span>- إنما شأنه فى الخلق إذا أراد إيجاد شئ أن يقول له: كن، فيكون فى الحال وكما يقول بعض العلماء: إن أمره سبحانه بين الكاف والنون.',NULL,'659',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3782,'<span data-type="title" id=toc-3818>٨٣ </span>- فتنزيهاً للذى بقدرته ملك كل شئ - خلقاً وتدبيراً وتصرفاً - عما لا يليق بذاته - تعالى - وإليه - وحده - تعودون، فيحاسبكم على أعمالكم.',NULL,'659',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3783,'<span data-type="title" id=toc-3820>١ </span>- أُقسم بطوائف من خلقى، تصطف بنفسها صفا مُحكماً فى مقام العبودية والانقياد.',NULL,'660',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3784,'<span data-type="title" id=toc-3821>٢ </span>- فالمانعات للمتجاوز حدوده منعاً شديداً، يبقى النظام ويحفظ الأكوان.',NULL,'660',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3785,'<span data-type="title" id=toc-3822>٣ </span>- فالتاليات للآيات يذكرون الله ذكراً بالتسبيح والتمجيد.',NULL,'660',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3786,'<span data-type="title" id=toc-3823>٤ </span>- إن إلهكم المستوجب للعبادة لواحد لا شريك له فى ذات أو فعل أو صفة.',NULL,'660',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3787,'<span data-type="title" id=toc-3824>٥ </span>- هو - وحده - خالق السموات والأرض وما بينهما، ومدبِّر الأمر، ومالك المشارق لكل ما له مشرق.',NULL,'661',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3788,'<span data-type="title" id=toc-3825>٦ </span>- إنَّا جمَّلْنا السماء القريبة من أهل الأرض بزينة هى الكواكب المشرقة المختلفة الأحجام والأوضاع فى محيط الكون التى نراها كل مساء بالعين المجردة.',NULL,'661',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3789,'<span data-type="title" id=toc-3826>٧ </span>- وحفظناها حفظاً محكماً من كل شيطان عات متمرد.',NULL,'661',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3790,'<span data-type="title" id=toc-3827>٨ </span>- لا يمكن عتاة الشياطين من التسمع إلى ما يجرى فى عالم الملائكة، ويُرْمَوْن من كل بما يدفعهم.',NULL,'661',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3791,'<span data-type="title" id=toc-3828>٩ </span>- يُطردون طرداً عنيفاً عن الوصول إلى تسمع أخبار السماء، ولهم عذاب شديد دائم فى الآخرة.',NULL,'661',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3792,'<span data-type="title" id=toc-3829>١٠ </span>- إلا من اختلس الكلمة من أخبار السماء، فإننا نتبعه بشعلة من النار تثقب الجو بضوئها فتحرقه.',NULL,'661',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3793,'<span data-type="title" id=toc-3830>١١ </span>- فاستخبر - أيها النبى - المنكرين للبعث والمستبعدين لحصوله: أهُم أصعب خلقاً أم من خلقنا من السموات والأرض والكواكب وغير ذلك؟ . إنا خلقناهم من طين لاصق بعضه ببعض، فَلِمَ يستبعدون إعادتهم؟ {.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3794,'<span data-type="title" id=toc-3831>١٢ </span>- بل عجبت - أيها النبى - من إنكارهم للبعث - مع قيام الأدلة على قدرة الله - وهم يسخرون من تعجبك وتقريرك له.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3795,'<span data-type="title" id=toc-3832>١٣ </span>- وإذا ووجهوا بأدلة قدرة الله على البعث لا يلتفتون ولا ينتفعون بدلالتها.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3796,'<span data-type="title" id=toc-3833>١٤ </span>- وإذا رأوا برهاناً على قدرة الله دعا بعضهم بعضاً إلى المبالغة فى الاستهزاء به.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3797,'<span data-type="title" id=toc-3834>١٥ </span>- وقال الكافرون فى الآيات الدالة على القدرة: ما هذا الذى نراه إلا سحر واضح.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3798,'<span data-type="title" id=toc-3835>١٦ </span>- أئذا متنا وصرنا تراباً وعظاماً أئنا لمُخرجون من قبورنا أحياء؟ .',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3799,'<span data-type="title" id=toc-3836>١٧ </span>- أنحيا ويبعث آباؤنا الأولون الذين ماتوا قبلنا فبادوا وهلكوا؟}',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3800,'<span data-type="title" id=toc-3837>١٨ </span>- قل - أيها النبى - لهم: نعم ستبعثون جميعاً وأنتم أذلاء صاغرون.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3801,'<span data-type="title" id=toc-3838>١٩ </span>- فإنما البعثة صيحة واحدة فإذا هم أحياء ينظرون ما كانوا يوعدون.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3802,'<span data-type="title" id=toc-3839>٢٠ </span>- وقال المشركون: يا هلاكنا. . هذا يوم الحساب والجزاء على الأعمال.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3803,'<span data-type="title" id=toc-3840>٢١ </span>- فيجابون: هذا يوم القضاء والفصل فى الأعمال الذى كنتم به فى الدنيا تكذبون.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3804,'<span data-type="title" id=toc-3841>٢٢ </span>-، ٢٣ - اجمعوا - يا ملائكتى - الظالمين أنفسهم بالكفر وأزواجهم الكافرات وآلهتهم التى كانوا يعبدونها من دون الله من الأوثان والأنداد، فعرفوهم طريق النار ليسلكوها.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3805,'<span data-type="title" id=toc-3842>٢٤ </span>- واحبسوهم فى هذا الموقف، إنهم مسئولون عن عقائدهم وأعمالهم.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3806,'<span data-type="title" id=toc-3843>٢٥ </span>- ما لكم - أيها المشركون - لا ينصر بعضكم بعضا كما كنتم تتناصرون فى الدنيا؟!',NULL,'662',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3807,'<span data-type="title" id=toc-3844>٢٦ </span>- لا يتناصرون فى هذا اليوم، بل هم منقادون مستسلمون لأمر الله.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3808,'<span data-type="title" id=toc-3845>٢٧ </span>- وأقبل بعضهم على بعض يتلاومون ويتخاصمون، ويسأل بعضهم بعضاً عن مصيرهم السيئ.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3809,'<span data-type="title" id=toc-3846>٢٨ </span>- قال الضعفاء للذين استكبروا: إنكم كنتم تأتوننا من الناحية التى نظن فيها الخير واليُمن، لتصرفونا عن الحق إلى الضلال.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3810,'<span data-type="title" id=toc-3847>٢٩ </span>- قال المستكبرون: لم نصرفكم، بل أنتم أبيتم الإيمان وأعرضتم عنه باختياركم.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3811,'<span data-type="title" id=toc-3848>٣٠ </span>- وما كان لنا من تسلط عليكم نسلبكم به اختياركم، بل كنتم قوماً خارجين على الحق.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3812,'<span data-type="title" id=toc-3849>٣١ </span>- فحق علينا كلمة ربنا: إنا لذائقون العذاب يوم القيامة.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3813,'<span data-type="title" id=toc-3850>٣٢ </span>- فدعوناكم إلى الغى والضلال فاستجبتم لدعوتنا، إن شأننا التحايل لدعوة الناس إلى ما نحن عليه من الضلال، فلا لوم علينا.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3814,'<span data-type="title" id=toc-3851>٣٣ </span>- فإن الأتباع والمتبوعين يوم القيامة فى العذاب مشتركون.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3815,'<span data-type="title" id=toc-3852>٣٤ </span>- إن مثل ذلك العذاب نفعل بالذين أجرموا فى حق الله بالشرك وفعل المعاصى.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3816,'<span data-type="title" id=toc-3853>٣٥ </span>- إن هؤلاء كانوا إذا قيل لهم: لا إله إلا الله يأبون الإقرار بذلك تكبراً واستعظاماً.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3817,'<span data-type="title" id=toc-3854>٣٦ </span>- ويقولون: أنحن نترك عبادة آلهتنا لقول شاعر متخبل مستور العقل؟ .',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3818,'<span data-type="title" id=toc-3855>٣٧ </span>- بل جاءهم رسولهم بالتوحيد الذى دعا إليه جميع الرسل، وصدق بذلك دعوة المرسلين.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3819,'<span data-type="title" id=toc-3856>٣٨ </span>- إنكم - يا أيها المشركون - لذائقوا العذاب الشديد فى الآخرة.',NULL,'663',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3820,'<span data-type="title" id=toc-3857>٣٩ </span>- وما تلقون من جزاء فى الآخرة إلا جزاء عملكم فى الدنيا.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3821,'<span data-type="title" id=toc-3858>٤٠ </span>- إلا عباد الله المخلصين، فإنهم لا يذوقون العذاب، لأنهم أهل إيمان وطاعة.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3822,'<span data-type="title" id=toc-3859>٤١ </span>- هؤلاء المخلصون لهم فى الآخرة رزق معلوم عند الله.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3823,'<span data-type="title" id=toc-3860>٤٢ </span>- فواكه متنوعة، وهم مرفهون معظمون.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3824,'<span data-type="title" id=toc-3861>٤٣ </span>- فى جنات النعيم.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3825,'<span data-type="title" id=toc-3862>٤٤ </span>- يجلسون فيها على سرر يقابل بعضهم بعضاً.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3826,'<span data-type="title" id=toc-3863>٤٥ </span>- يطوف عليهم ولدان بإناء فيه شراب من منابع جارية لا تنقطع.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3827,'<span data-type="title" id=toc-3864>٤٦ </span>- بيضاء عند مزجها، شهية للشاربين.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3828,'<span data-type="title" id=toc-3865>٤٧ </span>- ليس فيها غائلة الصداع تأخذهم على غرة، ولا هم بشربها يذهب وعيهم شيئاً فشيئاً.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3829,'<span data-type="title" id=toc-3866>٤٨ </span>- وعند هؤلاء المخلصين فى الجنة حوريات طبعن على العفاف، قد قصرن أبصارهن على أزواجهن، فلا يتطلعن لشهوة ضالة، نُجْلُ العيون حسانها.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3830,'<span data-type="title" id=toc-3867>٤٩ </span>- كأن قاصرات الطرف بيض النعام، المصون بأجنحته، فلم تمسه الأيدى، ولم يصبه الغبار.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3831,'<span data-type="title" id=toc-3868>٥٠ </span>- فأقبل بعض هؤلاء المخلصين على بعض يتساءلون عن أحوالهم. وكيف كانوا فى الدنيا؟',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3832,'<span data-type="title" id=toc-3869>٥١ </span>- قال قائل منهم عند ذلك: إنى كان لى صاحب من المشركين، يجادلنى فى الدين وما جاء به القرآن الكريم.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3833,'<span data-type="title" id=toc-3870>٥٢ </span>- يقول: أئِنك لمن الذين يصدقون بالبعث بعد الموت والحساب والجزاء؟ .',NULL,'664',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3834,'<span data-type="title" id=toc-3871>٥٣ </span>- أبعد أن نفنى ونصير تراباً وعظاماً نحيا مرة أخرى، لنحاسب ونجازى على ما قدمنا من عمل؟! .',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3835,'<span data-type="title" id=toc-3872>٥٤ </span>- قال المؤمن لجلسائه: هل أنتم يا أهل الجنة مُطَّلعون على أهل النار، فأرى قَرِينى؟ .',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3836,'<span data-type="title" id=toc-3873>٥٥ </span>- ودار ببصره نحو النار، فرأى صاحبه القديم فى وسطها، يُعذب بنارها.',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3837,'<span data-type="title" id=toc-3874>٥٦ </span>- قال حينما رآه: تالله إن كدت فى الدنيا لتهلكنى لو أطعتك فى كفرك وعصيانك.',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3838,'<span data-type="title" id=toc-3875>٥٧ </span>- ولولا نعمة ربى بهدايته وتوفيقه لى إلى الإيمان بالله وبالبعث لكنت مثلك من المحضرين فى العذاب.',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3839,'<span data-type="title" id=toc-3876>٥٨ </span>-، ٥٩ - أنحن مُخلَّدون منعَّمون فى الجنة، فلا نموت أبداً غير موتتنا الأولى فى الدنيا، وما نحن بمعذبين بعد دخولنا الجنة؟',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3840,'<span data-type="title" id=toc-3877>٦٠ </span>- إن هذا الذى أعطانا الله من الكرامة فى الجنة لهو الفوز العظيم، والنجاة الكبرى مما كنا نحذره فى الدنيا من عقاب الله.',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3841,'<span data-type="title" id=toc-3878>٦١ </span>- لنيل مثل ما حظى به المؤمنون من الكرامة فى الآخرة فليعمل فى الدنيا العاملون، ليدركوا ما أدركوه.',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3842,'<span data-type="title" id=toc-3879>٦٢ </span>- أذلك الرزق المعلوم المعد لأهل الجنة خير أم شجرة الزقوم المعدة لأهل النار؟',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3843,'<span data-type="title" id=toc-3880>٦٣ </span>- إنا جعلنا هذه الشجرة محنة وعذاباً فى الآخرة للمشركين.',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3844,'<span data-type="title" id=toc-3881>٦٤ </span>- إنها شجرة فى وسط الجحيم، غذيت من النار ومنها خلقت.',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3845,'<span data-type="title" id=toc-3882>٦٥ </span>- ثمرها قبيح المنظر، كريه الصورة، تنفر منه العيون كأنه رؤوس الشياطين التى لم يرها الناس، ولكن وقع فى وهمهم شناعتها وقبح منظرها.',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3846,'<span data-type="title" id=toc-3883>٦٦ </span>- فإنهم لآكلون من هذه الشجرة فمالئون من طلعها بطونهم، إذ لا يجدون غيرها ما يأكلون.',NULL,'665',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3847,'<span data-type="title" id=toc-3884>٦٧ </span>- ثم إن لهؤلاء المشركين على ما يأكلون من الزَّقوم لخلطاً ومزاجاً من ماء حار يشوى وجوههم، وتنقطع منه أمعاؤهم.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3848,'<span data-type="title" id=toc-3885>٦٨ </span>- ثم إن مصيرهم إلى النار، فهم فى عذاب دائم، إذ يؤتى بهم من النار إلى شجرة الزَّقوم، فيأكلون ثم يسقون، ثم يرجع بهم إلى محلهم من الجحيم.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3849,'<span data-type="title" id=toc-3886>٦٩ </span>-، ٧٠ - إنهم وجدوا آباءهم ضالين، فهم يُسرعون الخطا على آثارهم، ويستعجلون السير فى طريقهم، مقلدين لا متبصرين، كأنهم يزعجون ويحثون على الإسراع إلى متابعة الآباء من غير تدبر ولا تعقل.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3850,'<span data-type="title" id=toc-3887>٧١ </span>- ولقد ضل عن قصد السبيل وطريق الإيمان قبل مشركى مكة أكثر الأمم الخالية من قبلهم.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3851,'<span data-type="title" id=toc-3888>٧٢ </span>- ولقد أرسلنا فى هذه الأمم الخالية رسلاً ينذرونهم ويخوفونهم عذاب الله فكذبوهم.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3852,'<span data-type="title" id=toc-3889>٧٣ </span>- فانظر - يا من يتأتى منك النظر - كيف كان مآل الذين أنذرتْهم رسلهم؟! لقد أُهْلِكوا، فصاروا عبرة للناس.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3853,'<span data-type="title" id=toc-3890>٧٤ </span>- لكن هناك مؤمنون استخلصهم الله لعبادته، لينالوا فضل كرامته، ففازوا بثوابه، ونجوا من عذابه.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3854,'<span data-type="title" id=toc-3891>٧٥ </span>- ولقد نادانا نوح حين يئس من قومه فلنعم المجيبون كنا له إذ استجبنا دعاءه، فأهلكنا قومه بالطوفان.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3855,'<span data-type="title" id=toc-3892>٧٦ </span>- ونجينا نوحاً ومن آمن معه من الغرق والطوفان.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3856,'<span data-type="title" id=toc-3893>٧٧ </span>- وجعلنا ذرية نوح هم الباقين فى الأرض بعد هلاك قومه.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3857,'<span data-type="title" id=toc-3894>٧٨ </span>- وتركنا ذكراً جميلاً على نوح فى الآخرين من الأمم إلى يوم القيامة.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3858,'<span data-type="title" id=toc-3895>٧٩ </span>- تحية سلام وأمن لنوح فى الملائكة والثقلين جميعاً.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3859,'<span data-type="title" id=toc-3896>٨٠ </span>- إنا مثل هذا الجزاء نجزى من أحسن، فجاهد لإعلاء كلمتنا، وتحمل الأذى فى سبيلنا.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3860,'<span data-type="title" id=toc-3897>٨١ </span>- إنه من عبادنا الذين آمنوا بنا، وَوَفُوا بعهدنا، وأدوا رسالتنا.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3861,'<span data-type="title" id=toc-3898>٨٢ </span>- ثم أغرقنا الآخرين من كفار قومه.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3862,'<span data-type="title" id=toc-3899>٨٣ </span>- وإن ممن على طريقته وسنته فى الدعوة إلى التوحيد والإيمان بالله لإبراهيم.',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3863,'<span data-type="title" id=toc-3900>٨٤ </span>- إذ أقبل على ربه بقلب نقى من الشرك، مخلصاً له العبادة.',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3864,'<span data-type="title" id=toc-3901>٨٥ </span>- إذ أنكر على أبيه وقومه ما هم عليه من عبادة الأصنام بقوله: ما هذه الأوثان التى تعبدونها؟ .',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3865,'<span data-type="title" id=toc-3902>٨٦ </span>- أترتكبون كذباً فاضحاً بما تصنعون، إذ تعبدون غير الله، وتريدون هذا الإفك بلا مسوغ إلا اختياركم له؟ .',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3866,'<span data-type="title" id=toc-3903>٨٧ </span>- ما ظنكم بمن هو الجدير والمستحق بالعبادة لكونه خالقاً للعالمين، إذا لاقيتموه وقد أشركتم معه فى العبادة غيره؟ .',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3867,'<span data-type="title" id=toc-3904>٨٨ </span>- فنظر نظرة فى النجوم، ليستدل بها على خالق الكون، فوجدها متغيرة متحولة.',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3868,'<span data-type="title" id=toc-3905>٨٩ </span>- أخاف على نفسى الضلال وسقم الاعتقاد.',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3869,'<span data-type="title" id=toc-3906>٩٠ </span>- فانصرف عنه قومه معرضين عن قوله.',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3870,'<span data-type="title" id=toc-3907>٩١ </span>- فمال إلى أصنامهم مسرعاً متخفياً، وعرض عليها من الطعام الذى وضعوه أمامها. ليصيبوا من بركتها فى زعمهم، فقال فى سخرية واستهزاء: ألا تأكلون.؟ .',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3871,'<span data-type="title" id=toc-3908>٩٢ </span>- ما لكم عجزتم عن الكلام بالإيجاب أو السلب؟ .',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3872,'<span data-type="title" id=toc-3909>٩٣ </span>- فمال عليهم ضرباً باليد اليمنى - لأنها أقوى الباطشتين - فحطمها.',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3873,'<span data-type="title" id=toc-3910>٩٤ </span>- فأسرعوا إلى إبراهيم - وبعد أن تبيَّن لهم أن ما حدث لآلهتهم من التكسير كان بفعله - يعاقبونه على ارتكب فى شأن آلهتهم.',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3874,'<span data-type="title" id=toc-3911>٩٥ </span>- قال إبراهيم موبخاً لهم: أتعبدون ما سويتم بأيديكم من أحجار؟ . فأين ذهبت عقولكم؟ .',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3875,'<span data-type="title" id=toc-3912>٩٦ </span>- والله خلقكم، وخلق ما تصنعون بأيديكم من الأوثان، فهو المستحق - وحده - للعبادة.',NULL,'667',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3876,'<span data-type="title" id=toc-3913>٩٧ </span>- قال عُبَّاد الأصنام لبعض - لما قرعتهم الحُجَّة، ولجأوا إلى القوة، فعزموا على إحراقه -: ابنوا له بنياناً، واملأوه ناراً متأججة، وألقوه فى وسطها.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3877,'<span data-type="title" id=toc-3914>٩٨ </span>- فأرادوا بهذا أن يُنزلوا به الأذى، فأنجاه الله من النار بعد أن أُلقى فيها، وعلا شأنه بما كان له من كرامة، وجعلهم الله هم الأسفلين.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3878,'<span data-type="title" id=toc-3915>٩٩ </span>- وقال إبراهيم - لما يئس من إيمانهم -: إنى مهاجر إلى المكان الذى أمرنى ربى بالمسير إليه، سيهدينى ربى إلى المقر الأمين والبلد الطيب.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3879,'<span data-type="title" id=toc-3916>١٠٠ </span>- رب هب لى ذرية من الصالحين، تقوم على الدعوة إليك من بعدى.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3880,'<span data-type="title" id=toc-3917>١٠١ </span>- فبشرته الملائكة بابن يتحلى بالعقل والحلم.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3881,'<span data-type="title" id=toc-3918>١٠٢ </span>- وَوُلِدَ إسماعيل وشَبّ، فلما بلغ معه مبلغ السعى فى مطالب الحياة اختُبر إبراهيم فيه برؤية رآها. قال إبراهيم: يا بنى إنى أرى فى المنام وحياً من الله يطلب منى ذبحك، فانظر ماذا ترى؟ قال الابن الصالح: يا أبت أنجز أمر ربك، ستجدنى من الصابرين إن شاء الله.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3882,'<span data-type="title" id=toc-3919>١٠٣ </span>- فلما استسلم الوالد والمولود لقضاء الله، ودفعه إبراهيم على الرمل المتجمع، وأسقطه على شقه، فوقع جبينه على الأرض، وتهيأ لذبحه.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3883,'<span data-type="title" id=toc-3920>١٠٤ </span>-، ١٠٥ - وعلم الله صِدْق إبراهيم وابنه فى الاختبار، وناداه الله - نداء الخليل -: يا إبراهيم، قد استجبت مطمئنا لوحى الرؤيا، ولم تتردد فى الامتثال، فحسبك هذا، إنا نخفف عنك اختبارنا جزاء إحسانك، كما نجزى المحسنين على إحسانهم.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3884,'<span data-type="title" id=toc-3921>١٠٦ </span>- إن هذا الابتلاء الذى ابتلينا به إبراهيم وابنه لهو الابتلاء الذى أبان جوهر إيمانهما ويقينهما فى رب العالمين.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3885,'<span data-type="title" id=toc-3922>١٠٧ </span>- وفديناه بمذبوح عظيم القدر لكونه بأمر الله تعالى.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3886,'<span data-type="title" id=toc-3923>١٠٨ </span>- وتركنا له الثناء على ألسنة مَنْ جاء بعده.',NULL,'668',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3887,'<span data-type="title" id=toc-3924>١٠٩ </span>- تحية أمن وسلام على إبراهيم.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3888,'<span data-type="title" id=toc-3925>١١٠ </span>- مثل ذلك الجزاء الدافع للبلاء نجزى المحسنين فى امتثال أوامر الله.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3889,'<span data-type="title" id=toc-3926>١١١ </span>- إن إبراهيم من عبادنا المذعنين للحق.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3890,'<span data-type="title" id=toc-3927>١١٢ </span>- وبشرته الملائكة - بأمرنا - بأنه سيرزق ابنه إسحاق على يأس وعقم من امرأته، وأنه سيكون نبياً من الصالحين.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3891,'<span data-type="title" id=toc-3928>١١٣ </span>- ومنحناه وابنه البركة والخير فى الدنيا والآخرة، ومن ذريتهما محسن لنفسه بالإيمان والطاعة، وظالم لها بيِّن الضلال بكفره ومعصيته.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3892,'<span data-type="title" id=toc-3929>١١٤ </span>- ولقد أنعمنا على موسى وهارون بالنبوة والنعم الجسام.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3893,'<span data-type="title" id=toc-3930>١١٥ </span>- ونجيناهما وقومهما من الكرب الشديد الذى كان ينزله بهم فرعون وقومه.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3894,'<span data-type="title" id=toc-3931>١١٦ </span>- ونصرناهم على أعدائهم، فكانوا هم الغالبين.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3895,'<span data-type="title" id=toc-3932>١١٧ </span>- وآتينا موسى وهارون الكتاب الواضح المبين لأحكام الدين، وهى التوراة.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3896,'<span data-type="title" id=toc-3933>١١٨ </span>- وأرشدناهما إلى الطريق المعتدل.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3897,'<span data-type="title" id=toc-3934>١١٩ </span>- وأبقينا ثناءً حسناً عليهما فى الآخرين الذين جاءوا من بعدهم.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3898,'<span data-type="title" id=toc-3935>١٢٠ </span>- تحية أمن وسلام على موسى وهارون.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3899,'<span data-type="title" id=toc-3936>١٢١ </span>- إن مثل الجزاء الذى جازينا به موسى وهارون نجزى كل المحسنين.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3900,'<span data-type="title" id=toc-3937>١٢٢ </span>- إنهما من عبادنا المذعنين للحق.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3901,'<span data-type="title" id=toc-3938>١٢٣ </span>- وإن إلياس لَمنَ الذين أرسلناهم لهداية أقوامهم.',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3902,'<span data-type="title" id=toc-3939>١٢٤ </span>- إذ قال إلياس لقومه - وكانوا يعبدون صنماً لهم -: أتستمرون على غيِّكم، فلا تخافون الله باتقاء عذابه؟ .',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3903,'<span data-type="title" id=toc-3940>١٢٥ </span>- أتعبدون الصنم المسمى بَعْلاً، وتتركون عبادة الله الذى خلق العالم فأحسن خلقه؟ .',NULL,'669',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3904,'<span data-type="title" id=toc-3941>١٢٦ </span>- الله خلقكم وحفظكم أنتم وآباءكم الأولين، فهو الحقيق بالعبادة.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3905,'<span data-type="title" id=toc-3942>١٢٧ </span>- فكذَّبوه، فجزاؤهم أن يُحضروا إلى النار يوم القيامة.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3906,'<span data-type="title" id=toc-3943>١٢٨ </span>- إلا عباد الله الذين كمل إخلاصهم فى إيمانهم، فهؤلاء هم الفائزون.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3907,'<span data-type="title" id=toc-3944>١٢٩ </span>- وجعلنا له ذكراً حسناً على ألسنة من جاءوا من بعده.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3908,'<span data-type="title" id=toc-3945>١٣٠ </span>- سلام على إل ياسين، أو عليه وعلى آله بتغليبه عليهم.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3909,'<span data-type="title" id=toc-3946>١٣١ </span>- إن مثل الجزاء الذى جازينا به آل ياسين نجزى كل محسن على إحسانه.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3910,'<span data-type="title" id=toc-3947>١٣٢ </span>- إن إلياس من عبادنا المؤمنين.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3911,'<span data-type="title" id=toc-3948>١٣٣ </span>- وإن لوطاً لمن المرسلين الذين أرسلناهم لتبليغ رسالتنا إلى الناس.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3912,'<span data-type="title" id=toc-3949>١٣٤ </span>- لقد نجيناه وأهله جميعاً، مما حل بقومه من العذاب.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3913,'<span data-type="title" id=toc-3950>١٣٥ </span>- إلا امرأته العجوز، فقد هلكت مع الهالكين.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3914,'<span data-type="title" id=toc-3951>١٣٦ </span>- ثم أهلكنا من سِوى لوط ومن آمن به.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3915,'<span data-type="title" id=toc-3952>١٣٧ </span>-، ١٣٨ - وإنكم يا أهل مكة لتمرون على ديار قوم لوط فى سفركم إلى الشام صباحاً ومساء، أفقدتم عقولكم فلا تعقلون ما حل بهم جزاء تكذيبهم؟ .',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3916,'<span data-type="title" id=toc-3953>١٣٩ </span>- وإن يونس لمن الذين أرسلناهم لتبليغ رسالتنا إلى الناس.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3917,'<span data-type="title" id=toc-3954>١٤٠ </span>-، ١٤١ - إذ هجر قومه من غير أمر ربه، وذهب إلى سفينة مملوءة فركب فيها، فتعرضت السفينة للغرق فاقترعوا لإخراج أحد ركابها عن حمولتها، فخرجت القرعة على يونس، فكان من المغلوبين بالقرعة، فأُلْقِىَ فى البحر على حسب عُرْفهم فى ذلك الحين.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3918,'<span data-type="title" id=toc-3955>١٤٢ </span>- فابتلعه حوت وهو مستحق للملامة، جزاء هروبه من الدعوة إلى الحق وعدم الصبر على المخالفين.',NULL,'670',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3919,'<span data-type="title" id=toc-3956>١٤٣ </span>-، ١٤٤ - فلولا أن يونس كان من المنزّهين لله، المواظبين على ذكره، لمات فى بطن الحوت، وما خرج منه إلى يوم البعث.',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3920,'<span data-type="title" id=toc-3957>١٤٥ </span>- فطرحناه فى الفضاء الواسع من الأرض، لا يواريه شئ من شجر أو بناء، وهو عليل مما كان فيه.',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3921,'<span data-type="title" id=toc-3958>١٤٦ </span>- وأنبتنا عليه شجرة لا تقوم على ساق فغطته ووقته غوائل الجو.',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3922,'<span data-type="title" id=toc-3959>١٤٧ </span>- حتى إذا صح مما أصابه، أرسلناه إلى عدد كبير يقول من رآه: إنهم مائة ألف أو يزيدون.',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3923,'<span data-type="title" id=toc-3960>١٤٨ </span>- فاستجابوا لدعوته، فبسطنا عليهم نعمتنا إلى وقت معلوم.',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3924,'<span data-type="title" id=toc-3961>١٤٩ </span>- فاستفت قومك - أيها النبى -: ألخالقك البنات دونهم، ولهم البنون دونه؟ .',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3925,'<span data-type="title" id=toc-3962>١٥٠ </span>- بل أخلقنا الملائكة إناثاً وهم معاينون خلقهم، فتعلقوا بما شاهدوه؟ .',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3926,'<span data-type="title" id=toc-3963>١٥١ </span>-، ١٥٢ - تنبه - أيها السامع - لحديثهم، إنهم من كذبهم ليقولون: ولد الله، وهو المُنَزَّهُ عن الوالدية والولدية، وإنهم لكاذبون فىهذا القول بشهادة الأدلة على وحدانيته.',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3927,'<span data-type="title" id=toc-3964>١٥٣ </span>- أختار لنفسه البنات المكروهة فى زعمكم على البنين المحبوبين منكم، وهو الخالق للبنات والبنين؟ .',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3928,'<span data-type="title" id=toc-3965>١٥٤ </span>- ماذا أصابكم حين حكمتم بلا دليل؟ ، كيف تحكمون بذلك مع وضوح بطلانه؟ .',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3929,'<span data-type="title" id=toc-3966>١٥٥ </span>- أنسيتم دلائل القدرة والتنزيه فلا تتذكرون حتى وقعتم فى الضلال؟ .',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3930,'<span data-type="title" id=toc-3967>١٥٦ </span>- بل ألكم قوة دليل بَيّن تستدلون به على ما تدعون؟ .',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3931,'<span data-type="title" id=toc-3968>١٥٧ </span>- فأتوا بحجتكم - إن كان لكم حجة فى كتاب سماوى - إن كنتم صادقين فيما تقولون وتحكمون.',NULL,'671',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3932,'<span data-type="title" id=toc-3969>١٥٨ </span>- تمادوا فى اعتقادهم، وجعلوا بين الله وبين الجِنة المستورين عنهم قرابة، ولقد علمت الجِنَّة إن الكفار لمحضرون إلى الله، لينالوا جزاءهم المحتوم.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3933,'<span data-type="title" id=toc-3970>١٥٩ </span>- تنزيهاً لله - تعالى - عما يذكره المفترون من صفات العجز والنقص.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3934,'<span data-type="title" id=toc-3971>١٦٠ </span>- لكن عباد الله المخلصين برآء مما يصفه الكافرون.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3935,'<span data-type="title" id=toc-3972>١٦١ </span>-، ١٦٢، ١٦٣ - فإنكم - أيها الكفار - وما تعبدون من دون الله، ما أنتم على ما تعبدون من دونه بمضلين أحداً بإغوائكم، إلا من سبق فى علمه - تعالى - أنه من أهل الجحيم وسيصلى نارها.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3936,'<span data-type="title" id=toc-3973>١٦٤ </span>- وقالت الملائكة - متحيزين لموقف العبودية -: ما أحد منا إلا له مقام فى المعرفة والعبادة معلوم لا يتعداه.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3937,'<span data-type="title" id=toc-3974>١٦٥ </span>- وإنا لنحن الصافون أنفسنا فى مواقف العبودية دائماً.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3938,'<span data-type="title" id=toc-3975>١٦٦ </span>- وإنا لنحن المنزهون لله - تعالى - عما لا يليق به فى كل حال.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3939,'<span data-type="title" id=toc-3976>١٦٧ </span>-، ١٦٨، ١٦٩ - وإن كان كفار مكة قبل بعثة الرسول ليقولون: لو أن عندنا كتاباً من جنس كتب الأولين - كالتوراة والإنجيل - لكنَّا عباد الله المخلصين له العبادة.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3940,'<span data-type="title" id=toc-3977>١٧٠ </span>- وجاءهم الكتاب فكفروا به، فسوف يعلمون عاقبة كفرهم.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3941,'<span data-type="title" id=toc-3978>١٧١ </span>-، ١٧٢ - أقسم: لقد سبق قضاؤنا لعبادنا المرسلين أن النصر والعاقبة لهم على الكافرين.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3942,'<span data-type="title" id=toc-3979>١٧٣ </span>- وإن أتباعنا وأنصارنا لهم الغلبة - وحدهم - على المخالفين.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3943,'<span data-type="title" id=toc-3980>١٧٤ </span>- فأعرض عنهم وانتظر إلى وقت مؤجل، فإننا سنعجل لك العاقبة والنصر والظفر.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3944,'<span data-type="title" id=toc-3981>١٧٥ </span>- وأنظرهم وارتقب ماذا يحل بهم من العذاب والنكال بمخالفتك وتكذيبك، فسوف يعاينون الهزيمة بصفوفهم، ويرون نصر الله للمؤمنين.',NULL,'672',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3945,'<span data-type="title" id=toc-3982>١٧٦ </span>- أسلبوا عقولهم فبعذابنا يستعجلون؟',NULL,'673',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3946,'<span data-type="title" id=toc-3983>١٧٧ </span>- فإذا نزل العذاب بفنائهم الواسع فساء صباح المنذرين بالعذاب.',NULL,'673',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3947,'<span data-type="title" id=toc-3984>١٧٨ </span>- وأعرض عنهم إلى حين ينتهى إليه أمرهم.',NULL,'673',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3948,'<span data-type="title" id=toc-3985>١٧٩ </span>- وأبصر ما يستقبلهم ويستقبلك، فسوف يرون ما به يستعجلون.',NULL,'673',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3949,'<span data-type="title" id=toc-3986>١٨٠ </span>- تنزيهاً لله خالقك وخالق القوة والغلبة عما ينعتونه به من المفتريات.',NULL,'673',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3950,'<span data-type="title" id=toc-3987>١٨١ </span>- وسلام على الأصفياء المرسلين.',NULL,'673',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3951,'<span data-type="title" id=toc-3988>١٨٢ </span>- والثناء لله - وحده - خالق العالمين، والقائم على الخلق أجمعين.',NULL,'673',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3952,'<span data-type="title" id=toc-3990>١ </span>-<span data-type="title" id=toc-3989> ص: </span>حرف بدئت به السورة على طريقة القرآن فى بدء بعض السور بالحروف المقطعة، أقسم بالقرآن ذى الشرف والشأن العظيم إنه لحق لا ريب فيه.',NULL,'674',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3953,'<span data-type="title" id=toc-3991>٢ </span>- بل الذين كفروا فى استكبار عن اتباع الحق ومعاندة لأهله.',NULL,'674',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3954,'<span data-type="title" id=toc-3992>٣ </span>- كثيراً ما أهلكنا قبلهم من أمة مكذبة، فاستغاثوا حين جاءهم العذاب، وليس الوقت وقت خلاص منه.',NULL,'674',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3955,'<span data-type="title" id=toc-3993>٤ </span>- وعجب هؤلاء أن جاءهم رسول بشر مثلهم، وقال الجاحدون لرسالته: هذا مموه شديد الكذب.',NULL,'674',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3956,'<span data-type="title" id=toc-3994>٥ </span>- أجعل الآلهة المتعددة إلهاً واحداً؟ إن هذا الأمر بالغ نهاية العجب.',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3957,'<span data-type="title" id=toc-3995>٦ </span>- واندفع الكُبراء منهم يوصى بعضهم بعضاً: أن سيروا على طريقتكم، واثبتوا على عبادة آلهتكم. إن هذا لأمر جسيم يراد بنا.',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3958,'<span data-type="title" id=toc-3996>٧ </span>- ما سمعنا بهذا التوحيد فى دين آبائنا الذين أدركناهم. ما هذا إلا كذب مصنوع.',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3959,'<span data-type="title" id=toc-3997>٨ </span>- أخص محمد من بيننا بشرف نزول القرآن عليه؟ ليس الحق فى شئ مما زعموا بل هم من القرآن فى حيرة وتخبط. بل إنهم لم يتحيروا ويتخبطوا إلا لأنهم لم يذوقوا عذابى بعد وإنهم لذائقوه.',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3960,'<span data-type="title" id=toc-3998>٩ </span>- بل نسأل هؤلاء الحاسدين لك: أعندهم خزائن رحمة ربك العزيز الوهاب، حتى يتخيروا للنبوة من تهوى أنفسهم؟ .',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3961,'<span data-type="title" id=toc-3999>١٠ </span>- بل نسألهم: ألهم مُلْك السموات والأرض وما بينهما؟! إذن فليتدرجوا فى المراقى إلى المنزلة التى يتحكمون فيها بما يشاءون، إن استطاعوا.',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3962,'<span data-type="title" id=toc-4000>١١ </span>- جند حقير هنالك مهزوم - لا محالة - كما هزم أمثالهم من المتحزبين على الأنبياء؟',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3963,'<span data-type="title" id=toc-4001>١٢ </span>-، ١٣ - كذبت قبل هؤلاء قوم نوح وعاد وفرعون ذو الأبنية العظيمة الراسخة كالجبال، وثمود، وقوم لوط، وقوم شعيب - أصحاب الشجر الكثيف الملتف - أولئك الذين تحزبوا على رسلهم كما تحزب قومك.',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3964,'<span data-type="title" id=toc-4002>١٤ </span>- ما أحد من كل هؤلاء إلا كذَّب رسوله، فحل بهم عقابى.',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3965,'<span data-type="title" id=toc-4003>١٥ </span>- وما ينتظر هؤلاء المتحزبون على الرسل إلا صيحة واحدة لا تحتاج إلى تكرار.',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3966,'<span data-type="title" id=toc-4004>١٦ </span>- وقال الكافرون مستهزئين: ربنا عجِّل لنا نصيبنا من العذاب قبل يوم الجزاء.',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3967,'<span data-type="title" id=toc-4005>١٧ </span>- اصبر - يا محمد - على ما يقوله فيك المشركون، واذكر عبدنا داود ذا القوة فى الدين والدنيا، إنه كان رجَّاعاً إلى الله فى جميع أحواله.',NULL,'675',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3968,'<span data-type="title" id=toc-4006>١٨ </span>- إنا ذللنا الجبال معه، يستغل ما فيها من منافع، وهُنَّ ينزِّهن الله - تعالى - عن كل نقص فى آخر النهار وأوله.',NULL,'676',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3969,'<span data-type="title" id=toc-4007>١٩ </span>- وذللنا له الطير مجموعة من كل صنف وكل مكان، كلٌّ من الجبال والطير رجَّاعة لمشيئة داود، يصرفها كيف شاء للخير العام.',NULL,'676',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3970,'<span data-type="title" id=toc-4008>٢٠ </span>- وقوَّينا ملكه، وآتيناه النبوة، وتمييز الحق من الباطل.',NULL,'676',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3971,'<span data-type="title" id=toc-4009>٢١ </span>- وهل جاءك - يا محمد - خبر الخصوم الذين جاءوا داود من سور المحراب وهو محل العبادة، لا من بابه؟! .',NULL,'676',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3972,'<span data-type="title" id=toc-4010>٢٢ </span>- إذ دخلوا على داود فخاف منهم واضطرب. قالوا: لا تخف نحن متخاصمان، ظلم بعضنا بعضاً، فاحكم بيننا بالعدل ولا تتجاوزه، وأرشدنا إلى الطريقة المثلى.',NULL,'676',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3973,'<span data-type="title" id=toc-4011>٢٣ </span>- قال أحد الخصمين: إن هذا أخى له تسع وتسعون نعجة، ولى نعجة واحدة فقال: اجعلنى كافلها كما أكفل ما تحت يدى، وغلبنى فى المخاطبة.',NULL,'676',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3974,'<span data-type="title" id=toc-4012>٢٤ </span>- قال داود - قبل أن يسمع كلام الخصم الآخر -: لقد ظلمك بطلب ضم نعجتك إلى نعاجه، وإن كثيراً من المتخالطين ليجور بعضهم على بعض، إلا من استقر الإيمان فى قلوبهم، وكان عمل الصالحات من دأبهم، وهم قلة نادرة، وعرف داود أن الأمر ما هو إلا امتحان منا له فطلب من الله المغفرة، وانحنى راكعاً لله، ورجع إليه خاشعاً.',NULL,'676',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3975,'<span data-type="title" id=toc-4013>٢٥ </span>- فغفرنا له تعجّله فى الحكم، وإن له عندنا لقربى وحسن مرجع.',NULL,'676',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3976,'<span data-type="title" id=toc-4014>٢٦ </span>- وأوحى الله إليه: يا داود إنا صيَّرناك خليفة عنا فى الأرض، فاحكم بين الناس بما شرعت لك، ولا تَسِر فى الحكم وراء الهوى، فيحيد بك عن سبيل الله، إن الذين يحيدون عن سبيل الله باتباع أهوائهم لهم عذاب شديد بغفلتهم عن يوم الجزاء.',NULL,'677',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3977,'<span data-type="title" id=toc-4015>٢٧ </span>- وما خلقنا السموات والأرض وما بينهما عبثاً، ذلك ما يظنه الكافرون، فأجروا الأحكام على أهوائهم، فعذاب شديد للذين كفروا من النار.',NULL,'677',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3978,'<span data-type="title" id=toc-4016>٢٨ </span>- أيليق بحكمتنا وعدلنا أن نسوِّى بين المؤمنين الصالحين وبين المفسدين فى الأرض؟ ، أم يليق أن نسوِّى بين من خاف عذابنا واتقى عقابنا وبين المتمردين على أحكامنا؟ .',NULL,'677',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3979,'<span data-type="title" id=toc-4017>٢٩ </span>- هذا المُنَزَّل عليك - يا محمد - كتاب أنزلناه كثير النفع، ليتعمقوا فى فهم آياته، وليتعظ به أصحاب العقول الصحيحة والبصائرة النَّيِّرة.',NULL,'677',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3980,'<span data-type="title" id=toc-4018>٣٠ </span>- ووهبنا لداود سليمان المستحق للثناء، الخليق أن يُقال فيه: نعم العبد، لأنه رجَّاع إلى الله فى كل أحواله.',NULL,'677',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3981,'<span data-type="title" id=toc-4019>٣١ </span>- واذكر من أخبار سليمان أنه عرض عليه بعد الظهر الخيل الأصيلة التى تسكن حين وقوفها وتسرع حين سيرها.',NULL,'677',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3982,'<span data-type="title" id=toc-4020>٣٢ </span>- فقال سليمان: إنى أشربت حب الخيل - لأنها عدة الخير وهو الجهاد فى سبيل الله - حبا ناشئاً عن ذكر ربى، وما زال مشغولا بعرضها حتى غابت الشمس عن ناظريه.',NULL,'677',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3983,'<span data-type="title" id=toc-4021>٣٣ </span>- أمر بردها عليه ليتعرف أحوالها، فأخذ يمسح سوقها وأعناقها ترفقاً بها وحباً لها.',NULL,'677',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3984,'<span data-type="title" id=toc-4022>٣٤ </span>- ولقد امتحنا سليمان حتى لا يغتر بأبَّهة الملك، فألقينا جسداً على كرسيه لا يستطيع تدبير الأمور، فتنبه إلى هذا الامتحان فرجع إلى الله - تعالى - وأناب.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3985,'<span data-type="title" id=toc-4023>٣٥ </span>- دعا سليمان ربه - منيباً إليه -: رب اغفر لى ما بدر منى، وهب لى ملكاً لا يليق لأحد من بعدى، إنك أنت الوهَّاب الكثير العطاء.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3986,'<span data-type="title" id=toc-4024>٣٦ </span>- فذللنا له الريح، تجرى حسب مشيئته رخية هينة، حيث قصد وأراد.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3987,'<span data-type="title" id=toc-4025>٣٧ </span>- وذللنا له كل بنَّاء وغواص فى أعماق البحار من الشياطين المتمردين.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3988,'<span data-type="title" id=toc-4026>٣٨ </span>- وآخرين من هؤلاء الشياطين قرن بعضهم ببعض فى الأغلال والسلاسل، ليكف فسادهم عن الآخرين.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3989,'<span data-type="title" id=toc-4027>٣٩ </span>- وأوحى إليه أن هذا الذى أنعمنا به عليك عطاؤنا، فأعط من شئت واحرم من شئت، فلا حساب عليك فى الإعطاء أو المنع.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3990,'<span data-type="title" id=toc-4028>٤٠ </span>- إن لسليمان عندنا لقربة عظيمة وحُسْن مرجع ومآل.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3991,'<span data-type="title" id=toc-4029>٤١ </span>- واذكر - يا محمد - عبدنا أيوب إذ دعا ربه أنى أصابنى الشيطان بالتعب والألم.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3992,'<span data-type="title" id=toc-4030>٤٢ </span>- فاستجبنا له وناديناه: أن اضرب برجليك الأرض، فثمة ماء بارد تغتسل منه وتشرب، فيزول ما بك من نصب وعذاب.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3993,'<span data-type="title" id=toc-4031>٤٣ </span>- وجمعنا شمله بأهله الذين تفرقوا عنه أيام محنته، وزدنا عليهم مثلهم، وفعلنا ذلك رحمة منا له، وعظة لأولى العقول، ليعرفوا أن عاقبة الصبر الفرج.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3994,'<span data-type="title" id=toc-4032>٤٤ </span>- كان أيوب قد حلف أن يضرب أحداً من أهله عدداً من العصى، فحلل الله له يمينه بأن يأخذ حزمة فيها العدد الذى حلف أن يضربه به، فيضرب بالحزمة من حلف على ضَرْبه فيبر بيمينه بأقل ألم وقد مَن الله عليه بهذه النعم، لأن الله وجده صابراً على بلائه، فاستحق بذلك الثناء، فنعم الموصوف بالعبادة هو، لأنه رجَّاع إلى الله فى كل الأمور.',NULL,'678',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3995,'<span data-type="title" id=toc-4033>٤٥ </span>- واذكر عبادنا إبراهيم وإسحاق ويعقوب أولى القوة فى الدين والدنيا والبصائر النَّيِّرة.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3996,'<span data-type="title" id=toc-4034>٤٦ </span>- إنا خصصناهم بصفة هى: ذكرهم الدار الآخرة، يذكرونها ويُذَكِّرون بها.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3997,'<span data-type="title" id=toc-4035>٤٧ </span>- وإنهم عندنا لمن المختارين الأخيار.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3998,'<span data-type="title" id=toc-4036>٤٨ </span>- واذكر إسماعيل واليسع وذا الكفل وكلهم من الأخيار.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (3999,'<span data-type="title" id=toc-4037>٤٩ </span>- هذا الذى قصصناه عليك نبأ بعض المرسلين تذكير لك ولقومك، وإن للمتقين المتحرزين من عصيان الله - تعالى - حسن مرجع ومآل.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4000,'<span data-type="title" id=toc-4038>٥٠ </span>- أعد لهم جنات عدن مفتحة لهم أبوابها، لا يصدهم عنها صاد.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4001,'<span data-type="title" id=toc-4039>٥١ </span>- يجلسون فيها متكئين على الأرائك والسُّرر شأن المترفين، ويتمتعون فيها بطلب فاكهة كثيرة وشراب كثير.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4002,'<span data-type="title" id=toc-4040>٥٢ </span>- وعندهم فى الجنة من نسوة قصرن أبصارهن على أزواجهن، فلا ينظرن إلى غيرهم، وهن مستويات السن معهم، ليكون ذلك أدعى إلى الوفاق.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4003,'<span data-type="title" id=toc-4041>٥٣ </span>- هذا النعيم هو الذى توعدونه ليوم القيامة.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4004,'<span data-type="title" id=toc-4042>٥٤ </span>- إن هذا لعطاؤنا ما له من نهاية.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4005,'<span data-type="title" id=toc-4043>٥٥ </span>- هذا النعيم جزاء المتقين. وإن للطاغين المتمردين على أنبيائهم لشر مآل ومنقلب.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4006,'<span data-type="title" id=toc-4044>٥٦ </span>- وهو جهنم يدخلونها ويقاسون حرها، وبئس الفراش هى.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4007,'<span data-type="title" id=toc-4045>٥٧ </span>- هذا ماء بلغ الغاية فى الحرارة وصديد أهل جهنم، يؤمرون أن يذوقوه.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4008,'<span data-type="title" id=toc-4046>٥٨ </span>- وعذاب آخر مثل هذا العذاب أنواع مزدوجة.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4009,'<span data-type="title" id=toc-4047>٥٩ </span>- ويقال للطاغين - وهم رؤساء المشركين -: هذا جمع كثير داخلون النار معكم فى زحمة وشدة، وهمَ أتباعكم، فيقول هؤلاء الرؤساء: لا مرحباً بهم، إنهم داخلون النار يقاسون مرها، ويكتوون بعذابها.',NULL,'679',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4010,'<span data-type="title" id=toc-4048>٦٠ </span>- قال الأتباع: بل أنتم أحق بهذا الدعاء الذى دعوتم به علينا، لأنكم الذين قدمتم لنا هذا العذاب بإغرائكم لنا ودعوتنا إلى الكفر، فكفرنا بسببكم، فبئس الدار والمستقر جهنم.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4011,'<span data-type="title" id=toc-4049>٦١ </span>- قال الأتباع: ربنا مَن تسبَّب لنا فى هذا العذاب فزده عذاباً مضاعفاً فى النار.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4012,'<span data-type="title" id=toc-4050>٦٢ </span>- وقال أهل النار: ما لنا لا نرى رجالا كنا نعدهم فى الدنيا من الأشرار الأراذل الذين لا خير فيهم؟ وهم فقراء المسلمين.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4013,'<span data-type="title" id=toc-4051>٦٣ </span>- كيف اتخذناهم فى الدنيا هزؤا ولم يدخلوا النار معنا. أم أنهم دخلوها وزاغت عنهم أبصارنا فلم نرهم؟',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4014,'<span data-type="title" id=toc-4052>٦٤ </span>- إن ذلك الذى ذكرناه من حديث أهل النار حق لا بد أن يقع، وهو تخاصم ونزاع أهل النار بعضهم مع بعض.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4015,'<span data-type="title" id=toc-4053>٦٥ </span>- قل للمشركين - يا محمد -: إنما أنا مخوِّف من عذاب الله، وما من معبود بحق إلا الله الواحد الذى لا شريك له، القهار الذى يغلب كل ذى سلطان.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4016,'<span data-type="title" id=toc-4054>٦٦ </span>- رب السموات والأرض وما بينهما العزيز الذى لا يغلب، الغفار المتجاوز عن ذنوب من آمن به.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4017,'<span data-type="title" id=toc-4055>٦٧ </span>-، ٦٨ - قل لهم - يا محمد -: هذا الذى أنذرتكم به خبر عظيم أنتم عنه معرضون لا تفكرون فيه.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4018,'<span data-type="title" id=toc-4056>٦٩ </span>- ما كان لى من علم بأخبار الملأ الأعلى وقت اختصامهم فى شأن آدم، لأنى لم أسلك للعلم الطريق المتعارف بين الناس من قراءة الكتب، أو التلقى عن المعلمين، وطريق علمى هو: الوحى.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4019,'<span data-type="title" id=toc-4057>٧٠ </span>- ما يوحى إلى إلا لأننى رسول أبلِّغكم رسالة ربى بِأَبْيَن عبارة.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4020,'<span data-type="title" id=toc-4058>٧١ </span>- اذكر لهم حين قال ربك للملائكة: إنى خالق بشراً - وهو آدم عليه السلام - من طين.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4021,'<span data-type="title" id=toc-4059>٧٢ </span>- فإذا أتمت خلقه ونفخت فيه سر الحياة - وهو الروح - فخِروا له ساجدين سجود تعظيم وتحية، لا سجود عبادة.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4022,'<span data-type="title" id=toc-4060>٧٣ </span>-، ٧٤ - فامتثل الملائكة كلهم أجمعون، وخروا له ساجدين، إلا إبليس لم يسجد، وتعاظم وتكبر، وكان بهذا التكبر من الكافرين.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4023,'<span data-type="title" id=toc-4061>٧٥ </span>- قال الله تعالى: يا إبليس، ما منعك من السجود لما خلقته بنفسى بلا واسطة؟ أتكبرت مع أنك غير كبير، أم أنت فى حقيقة نفسك من المتفوقين؟ .',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4024,'<span data-type="title" id=toc-4062>٧٦ </span>- قال إبليس: أنا خير من آدم لأنك خلقتنى من نار وخلقته من طين، فكيف أسجد له.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4025,'<span data-type="title" id=toc-4063>٧٧ </span>- قال الله تعالى لإبليس - جزاء له على تكبره عن أمر ربه -: فاخرج من جماعة الملأ الأعلى، فإنك مطرود من رحمتى.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4026,'<span data-type="title" id=toc-4064>٧٨ </span>- وإن عليك إبعادى لك عن كل خير إلى يوم الجزاء، فتجزى على كفرك بى وتكبرك على.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4027,'<span data-type="title" id=toc-4065>٧٩ </span>- قال إبليس: رب أمهلنى ولا تمتنى إلى يوم البعث.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4028,'<span data-type="title" id=toc-4066>٨٠ </span>-، ٨١ - قال الله تعالى: فإنك من المؤجلين الممهلين إلى يوم الوقت المعلوم لنا، وهو نهاية الدنيا.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4029,'<span data-type="title" id=toc-4067>٨٢ </span>-، ٨٣ - قال إبليس: فبعظمتك وجلالك لأغوين البشر أجمعين، إلا عبادك الذين أخلصتهم لطاعتك، فلا سلطان لى عليهم.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4030,'<span data-type="title" id=toc-4068>٨٤ </span>-، ٨٥ - قال الله تعالى: الحق يمينى وقسمى، ولا أقول إلا الحق، لأملأن جهنم من جنسك من الشياطين وممن تبعك من ذرية آدم أجمعين، لا فرق عندى بين تابع ومتبوع.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4031,'<span data-type="title" id=toc-4069>٨٦ </span>- قل لأمتك - يا محمد -: ما أسألكم على ما أمرت بتبليغه إليكم من القرآن والوحى أجراً، وما أنا من الذين يتحلون بما ليس فيهم حتى أدعى النبوة.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4032,'<span data-type="title" id=toc-4070>٨٧ </span>- ما القرآن إلا تذكير وعظة للعالمين جميعاً.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4033,'<span data-type="title" id=toc-4071>٨٨ </span>- ولتعلمن - أيها المكذبون به - صدق ما اشتمل عليه من وعد ووعيد وأخبار عن أمور مستقبلة وآيات كونية بعد وقت قريب.',NULL,'681',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4034,'<span data-type="title" id=toc-4073>١ </span>- تنزيل القرآن من الله الذى لا يغلبه أحد على مراده، الحكيم فى فعله وتشريعه.',NULL,'683',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4035,'<span data-type="title" id=toc-4074>٢ </span>- إنا أنزلنا إليك - يا محمد - القرآن آمراً بالحق، فاعبد الله مخلصاً له - وحده - العبادة.',NULL,'683',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4036,'<span data-type="title" id=toc-4075>٣ </span>- ألا لله - وحده - الدين البرئ من كل شائبة، والمشركون الذين اتخذوا من دونه نصراء يقولون: ما نعبد هؤلاء لأنهم خالقون، إنما نعبدهم ليقربونا إلى الله - تقريباً - بشفاعتهم لنا عنده. إن الله يحكم بين هؤلاء المشركين وبين المؤمنين الموحِّدين فيما كانوا فيه يختلفون من أمر الشرك والتوحيد، إن الله لا يوفق لإدراك الحق من شأنه الكذب والإمعان فيه.',NULL,'683',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4037,'<span data-type="title" id=toc-4076>٤ </span>- لو أراد الله أن يتخذ ولداً - كما قالت النصارى فى المسيح، والمشركون فى الملائكة - لاختار الولد من خلقه كما يشاء هو، لا كما تشاءون أنتم، تنزه الله عن أن يكون له ولد، هو الله الذى لا مثيل له، القهَّار الذى بلغ الغاية فى القهر.',NULL,'683',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4038,'<span data-type="title" id=toc-4077>٥ </span>- خلق السموات والأرض متصفا دائماً بالحق والصواب على ناموس ثابت، يلف الليل على النهار ويلف النهار على الليل على صورة الكرة، وذلل الشمس والقمر لإرادته ومصلحة عباده، كل منهما يسير فى فلكه إلى وقت محدد عنده، وهو يوم القيامة، ألا هو - دون غيره - الغالب على كل شئ، فلا يخرج شئ عن إرادته، الذى بلغ الغاية فى الصفح عن المذنبين من عباده.',NULL,'683',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4039,'<span data-type="title" id=toc-4078>٦ </span>- خلقكم - أيها الناس - من نفس واحدة - هو آدم أبو البشر - وخلق من هذه النفس زوجها حواء، وأنزل لصالحكم ثمانية أنواع من الأنعام ذكراً وأنثى: وهى الإبل والبقر والضأن والماعز، يخلقكم فى بطون أمهاتكم طوراً من بعد طور فى ظلمات ثلاث: هى ظلمة البطن والرحم والمشيمة، ذلكم المنعم بهذه النعم هو الله مربيكم ومالك أمركم، له - لا لغيره - الملك الخالص، لا معبود بحق إلا هو، فكيف يعدلون عن عبادته إلى عبادة غيره؟ .',NULL,'684',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4040,'<span data-type="title" id=toc-4079>٧ </span>- إن تكفروا بنعمه - أيها الناس - فإن الله غنى عن إيمانكم وشكركم، ولا يحب لعباده الكفر، لما فيه من ضرهم، وإن تشكروه على نعمه يرض هذا الشكر لكم، ولا تحمل نفس آثمة إثم نفس أخرى، ثم إلى ربكم مآلكم فيخبركم بما كنتم تعملون فى الدنيا، إنه عليم بما تكتمه قلوبكم التى فى الصدور.',NULL,'684',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4041,'<span data-type="title" id=toc-4080>٨ </span>- وإذا أصاب الإنسان مكروه - من مكاره الدنيا - دعا ربه راجعاً إليه بعد أن كان معرضاً عنه، ثم إذا أعطاه ربه نعمة عظيمة نسى الضر الذى كان يدعو ربه إلى إزالته وكشفه من قبل أن يمن عليه بهذه النعمة، وجعل لله شركاء متساوين معه فى العبادة، فعل هذا الإنسان ذلك ليضل نفسه وغيره عن طريق الله. قل - يا محمد - لمن هذه صفته متوعداً: تمتع بكفرك بنعم الله عليك زمناً قليلاً، إنك من أهل النار.',NULL,'684',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4042,'<span data-type="title" id=toc-4081>٩ </span>- أَمَّن هو خاشع لله أثناء الليل يقضيه ساجداً وقائماً، يخشى الآخرة ويرجو رحمة ربه. كمن يدعو ربه فى الضراء وينساه فى السراء؟! قل لهم - يا محمد -: هل يستوى الذين يعلمون حقوق الله فيوحدونه، والذين لا يعلمون، لإهمالهم النظر فى الأدلة؟ إنما يتعظ أصحاب العقول السليمة.',NULL,'685',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4043,'<span data-type="title" id=toc-4082>١٠ </span>- قل - أيها النبى - مُبلغاً عن ربك: يا عبادى الذين آمنوا بى، اتخذوا وقاية من غضب ربكم، فإن لِمن أحسن العمل عاقبة حسنة فى الدنيا بالتأييد، وفى الآخرة بالجنة. ولا تقيموا فى ذل، فأرض الله واسعة، واصبروا على مفارقة الأوطان والأحباب، إنما يوفى الله الصابرين أجرهم مضاعفاً، لا يدخل تحت حساب الحاسبين.',NULL,'685',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4044,'<span data-type="title" id=toc-4083>١١ </span>- قل: إنى أمرت أن أعبد الله مخلصاً له عبادتى من كل شرك ورياء.',NULL,'685',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4045,'<span data-type="title" id=toc-4084>١٢ </span>- وأمرت منه تعالى - أمراً مؤكداً - أن أكون أول المنقادين لأوامره.',NULL,'685',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4046,'<span data-type="title" id=toc-4085>١٣ </span>- قل: إنى أخشى إن عصيت ربى عذاب يوم عظيم الهول.',NULL,'685',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4047,'<span data-type="title" id=toc-4086>١٤ </span>-، ١٥ - قل لهم يا محمد: الله - وحده - أعبد، مبرئاً عبادتى من الشرك والرياء، فإذا عرفتم طريقتى ولم تطيعونى فاعبدوا ما شئتم من دونه. قل لهم: إن الخاسرين - كل الخسران - هم الذين أضاعوا أنفسهم بضلالهم، وأهليهم بإضلالهم يوم القيامة. ألا ذلك الضياع هو الخسران الكامل الواضح.',NULL,'685',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4048,'<span data-type="title" id=toc-4087>١٦ </span>- لهؤلاء الخاسرين من فوقهم طبقات متراكمة من النار، ومن تحتهم مثلها، ذلك التصوير للعذاب يخوِّف الله به عباده، يا عباد: فاخشوا بأسى.',NULL,'685',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4049,'<span data-type="title" id=toc-4088>١٧ </span>-، ١٨ - والذين اجتنبوا الأصنام والشياطين، ولم يتقربوا إليها، ورجعوا إلى الله فى كل أمورهم، لهم البشارة العظيمة فى جميع المواطن، فبشر - يا محمد - عبادى الذين يستمعون القول فيتبعون الأحسن والأهدى إلى الحق، أولئك - دون غيرهم - الذين يوفقهم الله إلى الهدى، وأولئك هم - دون غيرهم - أصحاب العقول النَّيِّرة.',NULL,'686',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4050,'<span data-type="title" id=toc-4089>١٩ </span>- أتملك التصرف فى ملكى، فمن وجبت عليه كلمة العذاب تستطيع أن تمنعه؟ ألك هذه القوة، أفأنت تنقذ من فى النار بعد أن وجبت لهم؟ .',NULL,'686',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4051,'<span data-type="title" id=toc-4090>٢٠ </span>- لكن الذين خافوا ربهم لهم أعالى الجنة وقصورها، مبنية بعضها فوق بعض، تجرى من تحتها الأنهار، وعداً من الله، والله لا يخلف وعده.',NULL,'686',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4052,'<span data-type="title" id=toc-4091>٢١ </span>- ألم تر - أيها المخاطب - أن الله أنزل من السماء ماء فأجراه فى ينابيع وعيون فى الأرض، ثم يُخرج به زرعاً مختلفاً أشكاله، ثم ييبس بعد نضارته فتراه مصفرا، ثم يجعله فتاتاً متكسراً؟ إن فى ذلك التنقل - من حال إلى حال - لتذكير لأولى العقول النَّيرة.',NULL,'686',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4053,'<span data-type="title" id=toc-4092>٢٢ </span>- أكُلّ الناس سواء؟ ، فمن شرح الله صدره للإسلام بقبول تعاليمه، فهو على بصيرة من ربه، كمن أعرض عن النظر فى آياته؟ . فعذاب شديد للذين قست قلوبهم عن ذكر الله، أولئك القاسية قلوبهم فى انحراف عن الحق واضح.',NULL,'686',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4054,'<span data-type="title" id=toc-4093>٢٣ </span>- الله نزَّل أحسن الحديث كتاباً تشابهت معانيه وألفاظه فى بلوغ الغاية فى الإعجاز والإحكام، تتردد فيه المواعظ والأحكام، كما يكرر فى التلاوة، تنقبض عند تلاوته وسماع وعيده جلود الذين يخافون ربهم، ثم تلين جلودهم وقلوبهم إلى ذكر الله، ذلك الكتاب الذى اشتمل على هذه الصفات نور الله يهدى به من يشاء، فيوفقه إلى الإيمان به، ومن يضله الله - لعلمه أنه سيُعرِض عن الحق - فليس له من مرشد ينقذه من الضلال.',NULL,'687',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4055,'<span data-type="title" id=toc-4094>٢٤ </span>- لم يكون الناس متساوين يوم القيامة، فالذى يتقى بوجهه العذاب بعد أن تغل يداه، ليس كمن يأتى آمنا يوم القيامة؟ حيث يقال للظالمين: ذوقوا وبال عملكم.',NULL,'687',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4056,'<span data-type="title" id=toc-4095>٢٥ </span>- كذَّب الذين من قبل هؤلاء المشركين فجاءهم العذاب من حيث لا يتوقعون.',NULL,'687',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4057,'<span data-type="title" id=toc-4096>٢٦ </span>- فأذاقهم الله الصغار فى الحياة الدنيا، أقسم: لعذاب الآخرة أكبر من عذاب الدنيا، لو كانوا من أهل العلم والنظر.',NULL,'687',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4058,'<span data-type="title" id=toc-4097>٢٧ </span>- ولقد بيَّنا للناس فى هذا القرآن من كل مثل يذكرهم بالحق، رجاء أن يتذكروا ويتعظوا.',NULL,'687',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4059,'<span data-type="title" id=toc-4098>٢٨ </span>- ولقد أنزلنا قرآناً عربياً بلسانهم لا اختلال فيه، رجاء أن يتقوا ويخشوا ربهم.',NULL,'687',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4060,'<span data-type="title" id=toc-4099>٢٩ </span>- ضرب الله مثلاً للمشرك: رجلاً مملوكاً لشركاء متنازعين فيه، وضرب مثلا للموحد: رجلاً خالص الملكية لواحد، هل يستويان مثلاً؟ لا يستويان. الحمد لله على إقامة الحُجة على الناس، لكن أكثر الناس لا يعلمون الحق.',NULL,'687',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4061,'<span data-type="title" id=toc-4100>٣٠ </span>-، ٣١ - إنك - يا محمد - وإنهم جميعاً ميِّتون. ثم إنكم بعد الموت والبعث عند الله يخاصم بعضكم بعضاً.',NULL,'687',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4062,'<span data-type="title" id=toc-4101>٣٢ </span>- فليس أحد أشد ظلماً ممن نسب إلى الله ما ليس له، وأنكر الحق حين جاءه على لسان الرسل من غير تفكير ولا تدبر، أليس فى جهنم مستقر للكافرين المغترين حتى يجترئوا على الله؟!',NULL,'688',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4063,'<span data-type="title" id=toc-4102>٣٣ </span>- والذى جاء بالحق وصدَّق به إذ جاءه، أولئك هم المتقون لا غيرهم.',NULL,'688',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4064,'<span data-type="title" id=toc-4103>٣٤ </span>- لهؤلاء المتقين عند ربهم ما يحبون، ذلك الفضل جزاء كل محسن فى عقيدته وعمله.',NULL,'688',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4065,'<span data-type="title" id=toc-4104>٣٥ </span>- أكرم الله المتقين بما أكرمهم به ليغفر لهم أسوأ عملهم، ويوفيهم أجرهم بأحسن ما عملوا فى الدنيا.',NULL,'688',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4066,'<span data-type="title" id=toc-4105>٣٦ </span>- الله - وحده - كاف عباده كل ما يهمهم، ويخوفك - يا محمد - كفار قريش بآلهتهم التى يدعونها من دون الله - وذلك من ضلالهم - ومن يضلل الله - لعلمه أنه يختار الضلالة على الهدى - فما له من مرشد يرشده.',NULL,'688',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4067,'<span data-type="title" id=toc-4106>٣٧ </span>- ومن يرشده الله إلى الحق ويوفقه إليه - لعلمه أنه يختار الهدى على الضلالة - فما له من مضل ينحرف به عن سبيل الرشاد، أليس الله بمنيع الجناب، ذى انتقام شديد، فيحفظ أولياءه من أعدائه؟ .',NULL,'688',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4068,'<span data-type="title" id=toc-4107>٣٨ </span>- وأقسم: لئن سألت - يا محمد هؤلاء المشركين - من خلق السموات والأرض؟ ليقولن: الله هو الذى خلقهن. قل لهم - يا محمد -: أعقلتم فرأيتم الشركاء الذين تدعونهم من دون الله، إن شاء الله ضرى هل هن مزيلات عنى ضره، أو شاء لى رحمة هل هن مانعات عنى رحمته؟ قل لهم - يا محمد -: الذى يكفينى فى كل شئ وحده، عليه - لا على غيره - يعتمد المتوكلون المفوضون كل شئ إليه.',NULL,'688',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4069,'<span data-type="title" id=toc-4108>٣٩ </span>-، ٤٠ - قل لهم - متوعداً -: يا قوم اثبتوا على طريقتكم من الكفر والتكذيب إنى ثابت على عمل ما أمرنى به ربى، فسوف تدركون من منا الذى يأتيه عذاب يذله، وينزل عليه عذاب دائم لا ينكشف عنه.',NULL,'688',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4070,'<span data-type="title" id=toc-4109>٤١ </span>- إنا أنزلنا عليك - أيها النبى - القرآن الكريم لجميع الناس مشتملا على الحق الثابت. فمن استرشد به فنفْع ذلك لنفسه، ومن ضل عن طريقه فإنما يرجع وبال ضلاله على نفسه. وما أنت - يا محمد - بموكل بهدايتهم، فما عليك إلا البلاغ، وقد بلغت.',NULL,'689',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4071,'<span data-type="title" id=toc-4110>٤٢ </span>- الله يقبض الأرواح حين موتها، ويقبض الأرواح التى لم تمت حين نومها، فيُمسك التى قضى عليها الموت لا يردها إلى بدنها، ويُرسل الأخرى التى لم يحن أجلها عند اليقظة إلى أجل محدد عنده. إن فى ذلك لأدلة واضحة لقوم يتدبرون.',NULL,'689',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4072,'<span data-type="title" id=toc-4111>٤٣ </span>- بل اتخذ المشركون من دون الله شفعاء يتقربون بهم إليه. قل لهم - يا محمد -: أفعلتم هذا ولو كان هؤلاء الشفعاء لا يملكون شيئاً ولا يعقلون؟',NULL,'689',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4073,'<span data-type="title" id=toc-4112>٤٤ </span>- قل لهم - يا محمد -: لله - وحده - الشفاعة كلها، فلا ينالها أحد إلا برضاه، له - وحده - ملك السموات والأرض، ثم إليه - وحده - ترجعون فيحاسبكم على أعمالكم.',NULL,'689',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4074,'<span data-type="title" id=toc-4113>٤٥ </span>- وإذا ذكر الله - وحده - دون أن تذكر آلهتهم انقبضت ونفرت قلوب الذين لا يؤمنون بالحياة الآخرة، وإذا ذكرت آلهتهم التى يعبدونها من دون الله سارعوا إلى الفرح والاستبشار.',NULL,'689',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4075,'<span data-type="title" id=toc-4114>٤٦ </span>- قل - يا محمد - متوجهاً إلى مولاك: يا الله، يا خالق السموات والأرض على غير مثال، يا عالم السر والعلن، أنت - وحدك - تفصل بين عبادك فيما كانوا فيه يختلفون من أمور الدنيا والآخرة، فاحكم بينى وبين هؤلاء المشركين.',NULL,'689',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4076,'<span data-type="title" id=toc-4115>٤٧ </span>- ولو أن للذين ظلموا أنفسهم بالشرك كل ما فى الأرض جميعاً وضعفه معه لقدَّموه افتداء لأنفسهم من سوء العذاب الذى أُعد لهم يوم القيامة، وظهر لهم من الله ما لم يخطر على بالهم من العذاب.',NULL,'689',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4077,'<span data-type="title" id=toc-4116>٤٨ </span>- وظهر لهم فى هذا اليوم سوء عملهم، وأحاط بهم من العذاب ما كانوا يستهزئون به فى الدنيا.',NULL,'690',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4078,'<span data-type="title" id=toc-4117>٤٩ </span>- فإذا أصاب الإنسان ضر نادانا متضرعاً، ثم إذا أعطيناه - تفضلا منا - نعمة قال هذا الإنسان: ما أوتيت هذه النعم إلا لعلم منى بوجوه كسبه، وفات هذا الإنسان أن الأمر ليس كما قال، بل هذه النعمة التى أنعم الله بها عليه اختبار له ليبين له الطائع من العاصى، ولكن أكثر الناس لا يعلمون أنها اختبار وفتنة.',NULL,'690',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4079,'<span data-type="title" id=toc-4118>٥٠ </span>- قد قال هذه المقالة الذين من قبل هؤلاء المشركين، فما دفع عنهم العذاب ما اكتسبوه من مال ومتاع.',NULL,'690',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4080,'<span data-type="title" id=toc-4119>٥١ </span>- فأصاب الكفار السابقين جزاء سيئات عملهم، والظالمون من هؤلاء المخاطبين سيصيبهم جزاء سيئات عملهم، وما هؤلاء بمفلتين من العقاب.',NULL,'690',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4081,'<span data-type="title" id=toc-4120>٥٢ </span>- أيقول هؤلاء ما قالوا، ولم يعلموا أن الله يوسع الرزق لمن يشاء من عباده، ويعطيه بقدر لمن يشاء على مقتضى حكمته؟ إن فى هذا لعبراً لقوم يؤمنون.',NULL,'690',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4082,'<span data-type="title" id=toc-4121>٥٣ </span>- قل يا محمد - مبلغاً عن ربك: يا عبادى الذين أكثروا على أنفسهم من المعاصى، لا تيأسوا من رحمة الله، إن الله يتجاوز عن الذنوب جميعاً، إنه هو - وحده - العظيم فى مغفرته ورحمته.',NULL,'690',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4083,'<span data-type="title" id=toc-4122>٥٤ </span>- وارجعوا - أيها المسرفون على أنفسهم - إلى مالك أمركم ومربيكم، وانقادوا له من قبل أن يجيئكم العذاب ثم لا ينصركم أحد من الله ويدفع عنكم عذابه.',NULL,'690',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4084,'<span data-type="title" id=toc-4123>٥٥ </span>- واتبعوا أحسن ما أنزل إليكم من ربكم - وهو القرآن الكريم - من قبل أن يجيئكم العذاب فجأة وعلى غير استعداد، وأنتم لا تعلمون بمجيئه.',NULL,'690',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4085,'<span data-type="title" id=toc-4124>٥٦ </span>- ارجعوا إلى ربكم، وأسلموا له، واتبعوا تعاليمه، لئلا تقول نفس مذنبة حينما ترى العذاب: يا أسفى على ما فرَّطت فى جنب الله وحقه، وإنى كنت فى الدنيا لمن المستهزئين بدينه.',NULL,'691',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4086,'<span data-type="title" id=toc-4125>٥٧ </span>- أو تقول تلك النفس المذنبة - متحملة للعذر -: لو أن الله وفقنى للهدى لكنت فى الدنيا من الذين وقوا أنفسهم من عذاب الله بالإيمان والعمل الصالح.',NULL,'691',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4087,'<span data-type="title" id=toc-4126>٥٨ </span>- أو تقول تلك النفس المذنبة - حين تشاهد العذاب -: ليت لى رجعة إلى الدنيا، فأكون فيها ممن يحسنون العقيدة والعمل.',NULL,'691',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4088,'<span data-type="title" id=toc-4127>٥٩ </span>- بلى - أيها النادم - قد جاءتك تعاليمى على لسان الرسل، فكذبت بها وتعاليت عن اتباعها، وكنت فى دنياك من الثابتين على الكفر.',NULL,'691',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4089,'<span data-type="title" id=toc-4128>٦٠ </span>- ويوم القيامة ترى الذين كذبوا على الله - فنسبوا إليه ما ليس له - وجوههم مسودة من الحزن والكآبة، إن فى جهنم مقراً للمتكبرين المتعالين عن الحق.',NULL,'691',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4090,'<span data-type="title" id=toc-4129>٦١ </span>- ويُنجِّى الله الذين جعلوا لهم وقاية من عذاب الله - بما سبق فى علمه من فوزهم - لاختيارهم الهدى على الضلال، لا يصيبهم فى هذا اليوم السوء، ولا هم يحزنون على فوت نعيم كانوا يؤملونه.',NULL,'691',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4091,'<span data-type="title" id=toc-4130>٦٢ </span>- الله خالق كل شئ، وهو - وحده - على كل شئ وكيل، يتولى أمره بمقتضى حكمته.',NULL,'691',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4092,'<span data-type="title" id=toc-4131>٦٣ </span>- لله - وحده - تصاريف أمور السموات والأرض، فلا يتصرف فيهن سواه، والكافرون بحجج الله وبراهينه هم وحدهم - الخاسرون أتم خسران.',NULL,'691',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4093,'<span data-type="title" id=toc-4132>٦٤ </span>- قل - يا محمد - أفبعد وضوح الآيات على وجوب توحيد الله بالعبادة تأمرونى أن أخص غيره بالعبادة أيها الجاهلون؟!',NULL,'691',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4094,'<span data-type="title" id=toc-4133>٦٥ </span>- وأقسم: لقد أوحى إليك - يا محمد - وإلى الرسل من قبلك: لئن أشركت بالله شيئاً ما، ليبطلن الله عملك، ولتكونن من القوم الخاسرين أتم خسران.',NULL,'692',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4095,'<span data-type="title" id=toc-4134>٦٦ </span>- لا تجبهم - أيها الرسول - إلى ما طلبوه منك، بل اعبد الله - وحده - وكن من القوم الشاكرين له على نعمه.',NULL,'692',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4096,'<span data-type="title" id=toc-4135>٦٧ </span>- وما عظَّم المشركون الله حق عظمته، وما عرفوه حق معرفته إذ أشركوا معه غيره، ودعوا الرسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - إلى الشرك به، والأرض جميعها مملوكة له يوم القيامة، والسموات قد طويت - كما تطوى الثياب - بيمينه، تنزه الله عن كل نقص، وتعالى علواً كبيراً عما يشركونه من دونه.',NULL,'692',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4097,'<span data-type="title" id=toc-4136>٦٨ </span>- وسينفخ - حتماً - فى الصور، فيموت من فى السموات ومن فى الأرض إلا من شاء الله أن يؤخرهم إلى وقت آخر، ثم نفخ فيه أخرى فإذا الجميع قائمون من قبورهم ينتظرون ما يُفعل بهم.',NULL,'692',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4098,'<span data-type="title" id=toc-4137>٦٩ </span>- وأضاءت الأرض - يومئذ - بنور خالقها ومالكها، وأعد الكتاب الذى سجلت فيه أعمالهم، وأحضر الأنبياء والعدول ليشهدوا على الخلق، وفصل بين الخلق بالعدل، وهم لا يظلمون بنقص ثواب أو زيادة عقاب.',NULL,'692',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4099,'<span data-type="title" id=toc-4138>٧٠ </span>- وأعطيت كل نفس جزاء عملها، والله أعلم بفعلهم.',NULL,'692',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4100,'<span data-type="title" id=toc-4139>٧١ </span>- وحُثَّ الكافرون على السير - بعنف - إلى جهنم جماعات جماعات، حتى إذا بلغوها فتحت أبوابها، وقال لهم حراسها - موبخين -: ألم يأتكم سفراء عن الله من نوعكم، يقرأون عليكم آيات ربكم، ويُخوِّفونكم لقاء يومكم هذا؟ قال الكافرون مقرين: بلى جاءتنا الرسل، ولكن وجبت كلمة العذاب على الكافرين، لاختيارهم الكفر على الإيمان.',NULL,'692',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4101,'<span data-type="title" id=toc-4140>٧٢ </span>- قيل لهم: ادخلوا أبواب جهنم مقدرا لكم فيها الخلود، فبئست جهنم مستقرا للمتعالين عن قبول الحق.',NULL,'693',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4102,'<span data-type="title" id=toc-4141>٧٣ </span>- وحُثَّ المتقون على السير - مكرمين - إلى الجنة جماعات جماعات، حتى إذا بلغوها، وقد فتحت أبوابها، وقال لهم حفظتها: أمان عظيم عليكم، طبتم فى الدنيا من دنس المعاصى، وطبتم فى الآخرة - نفساً - بما نلتم من النعيم، فادخلوها مُقَدَّراً لكم الخلود، فإن لكم من النعيم ما لا يخطر على بال.',NULL,'693',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4103,'<span data-type="title" id=toc-4142>٧٤ </span>- وقال المتقون: الثناء لله - وحده - الذى حقق لنا ما وعدنا به على لسان رسله، وملكنا أرض الجنة ننزل منها حيث نشاء، فنعم أجر العاملين المحسنين الجنة.',NULL,'693',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4104,'<span data-type="title" id=toc-4143>٧٥ </span>- وترى - أيها الرائى - الملائكة محيطين بالعرش، يزهون الله عن كل نقص، تنزيهاً مقترناً بحمد خالقهم ومربيهم، وفصل بين جميع الخلائق بالعدل، ونطق الكون كله قائلا: الحمد لله رب الخلائق كلها.',NULL,'693',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4105,'<span data-type="title" id=toc-4145>١ </span>- ح. م: حرفان من حروف الهجاء بدئت بهما السورة - على طريقة القرآن فى بعض السور - للإشارة إلى أن القرآن من جنس كلامهم، ومع ذلك عجزوا عن الإتيان بمثله.',NULL,'694',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4106,'<span data-type="title" id=toc-4146>٢ </span>-، ٣ - تنزيل القرآن من الله القوى الغالب، المحيط علمه بكل شئ، وقابل التوبة من التائبين، شديد العذاب، صاحب الإنعام، لا معبود بحق إلا هو، إليه - وحده - المرجع والمآل.',NULL,'694',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4107,'<span data-type="title" id=toc-4147>٤ </span>- ما يمارى فى آيات الله الدالة عليه إلا الذين كفروا، فلا يخدعك تنقلهم فى البلاد بتيسير الله شئونهم مع كفرهم.',NULL,'694',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4108,'<span data-type="title" id=toc-4148>٥ </span>- كذبت قبل هؤلاء المشركين قوم نوح والمجتمعون على معاداة الرسل من بعد قومه، وحرصت على إيقاع الشر برسولهم ليأخذوه بالبطش، وتماروا فى الباطل الذى لا حقيقة له، ليزيلوا بجدلهم الحق الثابت، فأخذتهم بالعذاب المستأصل، فانظر كيف كان عقابى لهم؟ .',NULL,'695',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4109,'<span data-type="title" id=toc-4149>٦ </span>- وكما حقَّت كلمة العذاب على الأمم التى كذَّبت أنبياءها، حقت كلمة ربك على الكافرين بك - يا محمد - لأنهم أصحاب النار، لاختيارهم الكفر على الإيمان.',NULL,'695',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4110,'<span data-type="title" id=toc-4150>٧ </span>- الذين يحملون العرش من الملائكة والمحيطون به، ينزِّهون مالك أمرهم ومربيهم عن كل نقص تنزيهاً مقترناً بالثناء عليه، ويؤمنون به ويطلبون المغفرة للمؤمنين قائلين: ربنا وسعت رحمتك كل شئ، وأحاط علمك بكل شئ، فاصفح عن سيئات الذين رجعوا إليك واتبعوا طريقك، وجَنِّبْهُم عذاب الجحيم.',NULL,'695',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4111,'<span data-type="title" id=toc-4151>٨ </span>- ويقول هؤلاء الملائكة: ربنا وأدخل المؤمنين جنات الإقامة التى وعدتهم بها على لسان رسلك، وأدخل معهم الصالحين من الآباء والأزواج والذرية. إنك أنت - وحدك - الغالب الذى لا يغلب، الحكيم الذى لا يخطئ.',NULL,'695',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4112,'<span data-type="title" id=toc-4152>٩ </span>- ويقولون فى دعائهم: جنِّب المؤمنين جزاء سيئاتهم، ومن جنبته جزاء سيئاته يوم الجزاء فقد رحمته بفضلك، والوقاية من جزاء السيئات هو الظفر البالغ العظم.',NULL,'695',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4113,'<span data-type="title" id=toc-4153>١٠ </span>- إن الذين كفروا يُنادون: لكراهة الله وبُغضه لكم أكبر من كراهتكم أنفسكم التى أوردتكم موارد العذاب، حين كنتم تدعون إلى الإيمان مرة بعد مرة فتسارعون إلى الكفر.',NULL,'695',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4114,'<span data-type="title" id=toc-4154>١١ </span>- قال الكافرون: ربنا أمتنا موتتين: موتة من حياتنا الدنيا، وموتة من حياتنا فى البرزخ وأحييتنا مرتين: مرة هى حياتنا الدنيا، ومرة أخرى بالبعث من القبور، فهل إلى خروجنا من العذاب من طريق.',NULL,'696',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4115,'<span data-type="title" id=toc-4155>١٢ </span>- ذلكم العذاب الذى أنتم فيه لأن شأنكم فى الدنيا إذا دُعِىَ الله - وحده - كفرتم وإن يُشرك به غيره تُؤمنوا، وإذا كان هذا شأنكم فقد استحققتم جزاء شكرككم، فالحكم لله العلى الكبير الذى يجازى من كفر بما يستحقه.',NULL,'696',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4116,'<span data-type="title" id=toc-4156>١٣ </span>- الله الذى يريكم دلائل قدرته، فينزل لمصالحكم من السماء ماء يكون سبب رزقكم. وما يتعظ بهذا إلا من يرجع إلى التفكير فى آيات الله.',NULL,'696',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4117,'<span data-type="title" id=toc-4157>١٤ </span>- فاعبدوا الله مخلصين له العبادة، ولو أبغض الكافرون عبادتكم وإخلاصكم.',NULL,'696',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4118,'<span data-type="title" id=toc-4158>١٥ </span>-، ١٦ - الله عالى المقامات، صاحب العرش، يُنزل الوحى من قضائه وأمره على من اصطفاه من عباده، ليخوِّف الناس عاقبة مخالفة المرسلين يوم التقاء الخلق أجمعين، يوم الحساب الذى يظهر فيه الناس واضحين، لا يخفى على الله من أمرهم شئ، يتسامعون نداءً رهيباً: لمن الملك اليوم؟ وجواباً حاسماً: لله الواحد المتفرد بالحكم بين عباده، البالغ القهر لهم.',NULL,'696',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4119,'<span data-type="title" id=toc-4159>١٧ </span>- اليوم تُجزى كل نفس بما فعلت، لا ظلم اليوم بنقص أجر أو زيادة عقاب، إن الله سريع حسابه فلا يتأخر عن وقته.',NULL,'696',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4120,'<span data-type="title" id=toc-4160>١٨ </span>- وخوِّفهم - يا محمد - يوم القيامة القريبة، حين تكون القلوب عند الحناجر من شدة الخوف، ممتلئين غيظاً لا يستطيعون التعبير عنه، ليس للظالمين أنفسهم بالكفر قريب ولا شفيع يطاع فى أمرهم.',NULL,'696',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4121,'<span data-type="title" id=toc-4161>١٩ </span>- وهو - سبحانه - يعلم النظرة الخائنة للعين، وما تخفيه الصدور من المكنونات.',NULL,'696',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4122,'<span data-type="title" id=toc-4162>٢٠ </span>- والله يحكم بالعدل، والشركاء الذين يدعونهم من دون الله لا يحكمون بشئ لعجزهم، إن الله - وحده - هو المحيط بكل ما يسمع ويبصر.',NULL,'697',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4123,'<span data-type="title" id=toc-4163>٢١ </span>- أقعد المشركون ولم يسيروا فى الأرض، فيروا كيف كان حال الأمم الذين كانوا من قبلهم؟ كانوا - هم - أشد منهم قدرة وآثاراً فى الأرض، فاستأصلهم الله بذنوبهم، وليس لهم من الله حافظ يحفظهم من عذابه.',NULL,'697',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4124,'<span data-type="title" id=toc-4164>٢٢ </span>- ذلك العذاب الذى نزل بهم، لأنهم كانت تأتيهم رسلهم بالأدلة الواضحات فجحدوها، فعجَّل الله عذابهم المستأصل، إنه ذو قوة عظيمة، بالغ الشدة فى العذاب.',NULL,'697',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4125,'<span data-type="title" id=toc-4165>٢٣ </span>-، ٢٤ - لقد أرسلنا موسى بمعجزاتنا وبرهان ذى سلطان واضح إلى فرعون وهامان وقارون فقالوا: هو ساحر بما جاء من المعجزات، مبالغ فى الكذب لدعواه أنه رسول من ربه.',NULL,'697',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4126,'<span data-type="title" id=toc-4166>٢٥ </span>- فلما أتاهم موسى بالحق من عندنا، قال فرعون ومن معه لأتباعهم: اقتلوا أبناء الذين آمنوا معه واتركوا نساءهم أحياء. وليس مكر الكافرين إلا ذاهباً فى متاهة وضياع.',NULL,'697',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4127,'<span data-type="title" id=toc-4167>٢٦ </span>- وقال فرعون: دعونى أقتل موسى وليدعُ ربه لينقذه منى، إنى أخشى أن يغيِّر دينكم - يا قوم - أو أن يشيع فى الأرض الفتن.',NULL,'697',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4128,'<span data-type="title" id=toc-4168>٢٧ </span>- وقال موسى لفرعون وقومه: إنى تحصنت بمالك أمرى الذى ربانى، ومالك أمركم ومربيكم بنعمه وإحسانه، من كل متغطرس متعال لا يؤمن بيوم الحساب.',NULL,'697',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4129,'<span data-type="title" id=toc-4169>٢٨ </span>- وقال رجل مؤمن من أهل فرعون يخفى إيمانه - مخاطباً قومه -: أتقصدون رجلاً بالقتل لأنه يقول: معبودىَ الله، وقد جاءكم بالأدلة الواضحات من مالك أمركم ومربيكم، وإن يكن كاذباً فى دعواه فعليه - وحده - وبال كذبه، وإن يكن صادقاً يُنزل بكم بعض الذى يخوفكم به من العذاب، إن الله لا يوفق إلى طريق النجاة من هو مجاوز الحد مبالغ فى الكذب.',NULL,'698',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4130,'<span data-type="title" id=toc-4170>٢٩ </span>- قال فرعون: ما دعوتكم إليه هو الحق، وما أدعوكم إليه هو طريق الخير والرشاد.',NULL,'698',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4131,'<span data-type="title" id=toc-4171>٣٠ </span>-، ٣١ - وقال الرجل الذى آمن من آل فرعون: يا قوم إنى أخشى عليكم يوماً مثل يوم الأقوام المتحزبين على رسلهم، مثل عادة قوم نوح وعاد وثمود والأقوام الذين من بعدهم، وما الله يشاء ظلماً لعباده.',NULL,'698',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4132,'<span data-type="title" id=toc-4172>٣٢ </span>-، ٣٣ - ويا قوم: إنى أخاف عليكم يوم تفرون مدبرين ليس لكم من الله من مانع، ومن يضلله الله - لعلمه أنه يختار الضلالة على الهدى - فما له من مرشد يهديه.',NULL,'698',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4133,'<span data-type="title" id=toc-4173>٣٤ </span>- لقد أتاكم يوسف من قبل موسى بالآيات الواضحات، فما زلتم فى شك مما أتاكم به، حتى إذا مات قلتم: لن يرسل الله من بعد يوسف رسولا، مثل هذا الإضلال الشنيع يُضل الله من هو مجاوز الحد، كثير الشك والارتياب.',NULL,'699',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4134,'<span data-type="title" id=toc-4174>٣٥ </span>- الذين يجادلون فى آيات الله بغير برهان جاءهم، كَبُرَ كرهاً وسخطاً عند الله وعند المؤمنين ما انطبعوا عليه من الجدال، مثل هذا الختم يختم الله على كل قلب متعال على الخلق، متسلط على الناس.',NULL,'699',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4135,'<span data-type="title" id=toc-4175>٣٦ </span>-، ٣٧ - وقال فرعون: يا هامان ابن لى بناءً عالياً رجاء أن أبلغ المسالك، مسالك السموات فأرى إله موسى، وإنى لأظنه كاذباً فى دعوى الرسالة، ومثل هذا التزين الباطل زين لفرعون سوء عمله حتى رآه حسناً، ومنع عن سبيل الحق لاختياره سبيل الضلالة، وليس مكر فرعون إلا فى خسار عظيم.',NULL,'699',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4136,'<span data-type="title" id=toc-4176>٣٨ </span>- وقال الذى آمن من قوم فرعون: يا قوم اقتدوا بى أرشدكم طريق الصلاح.',NULL,'699',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4137,'<span data-type="title" id=toc-4177>٣٩ </span>- يا قوم: ما هذه الحياة الدنيا إلا كمتاع الراكب يفنى بسرعة، وإن الدار الآخرة هى - وحدها - دار الاستقرار.',NULL,'699',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4138,'<span data-type="title" id=toc-4178>٤٠ </span>- من عمل سيئة فى الدنيا فلا يُجازى عليها فى الآخرة إلا مثلها، ومن عمل صالحاً من ذكر أو أنثى وهو مؤمن فأولئك يدخلون الجنة يرزقون فيها رزقاً غير مقدر بحساب الحاسبين.',NULL,'699',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4139,'<span data-type="title" id=toc-4179>٤١ </span>-، ٤٢ - ويا قوم: أى شئ لى، أدعوكم إلى أسباب النجاة وتدعوننى إلى النار؟ . تدعوننى إلى الكفر بالله وإشراك من لا علم لى به، وأنا أدعوكم إلى القوى الذى لا يغلب، الكثير المغفرة للذنوب.',NULL,'699',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4140,'<span data-type="title" id=toc-4180>٤٣ </span>- لا محالة أن الإله الذى تدعوننى إلى عبادته ليس له دعوة يستجيبها فى الدنيا ولا فى الآخرة، وأن مرجعنا إلى الله، وأن المجاوزين الحدود هم أهل النار لا المؤمنين المعتدلين.',NULL,'700',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4141,'<span data-type="title" id=toc-4181>٤٤ </span>- فستعلمون صدق ما قلته لكم، وأكل أمرى إلى الله، إن الله محيط بصره بالعباد فيجازيهم على أعمالهم.',NULL,'700',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4142,'<span data-type="title" id=toc-4182>٤٥ </span>-، ٤٦ - فَوَقَى الله مؤمن آل فرعون شدائد مكرهم وأحاط بآل فرعون العذاب السيئ. النار يدخلونها صباحاً ومساءً، هذا فى الدنيا وهم فى عالم البرزخ، ويوم القيامة يقول الله تعالى: أدخلوا قوم فرعون أشد أنوع العذاب.',NULL,'700',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4143,'<span data-type="title" id=toc-4183>٤٧ </span>- واذكر لهم - يا محمد - حين يتخاصم أهل النار فيها، فيقول الضعفاء - وهم الأتباع - للمستكبرين - وهم الرؤساء -: إنا كنا لكم فى الدنيا تَبعاً، فهل أنتم حاملون عنا جزءاً من عذاب النار؟ .',NULL,'700',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4144,'<span data-type="title" id=toc-4184>٤٨ </span>- قال المستكبرون: إننا كلنا فيها - نحن وأنتم - إن الله فصل بالحق بين العباد، فلكل منا ما قضاه عليه من العذاب.',NULL,'700',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4145,'<span data-type="title" id=toc-4185>٤٩ </span>- وقال الذين فى النار من الضعفاء والكبراء لحفظة جهنم - متوسلين إليهم - ادعوا إلهكم يُخفف عنا يوماً من العذاب نستروح فيه.',NULL,'700',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4146,'<span data-type="title" id=toc-4186>٥٠ </span>- قال خزنة جهنم لهم - موبخين -: ألم تتنبهوا إلى ما نزل بكم وكانت تجيئكم الرسل بالبراهين الواضحات؟ قال أهل جهنم: بلى جاءتنا الرسل فكذبناها. قال الخزنة: فإذا كان الأمر كذلك فادعوا - أنتم - وما دعاء الجاحدين إلا فى ضياع.',NULL,'700',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4147,'<span data-type="title" id=toc-4187>٥١ </span>- إنا لننصر رسلنا والمؤمنين فى الحياة الدنيا بالانتقام من أعدائهم، وإقامة الحُجة عليهم، وفى يوم القيامة يوم يقوم الشهود يشهدون للرسل بالتبليغ، ويشهدون على الكفرة بالتكذيب.',NULL,'700',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4148,'<span data-type="title" id=toc-4188>٥٢ </span>- يوم لا ينفع الظالمين اعتذارهم عمَّا فرط منهم فى الدنيا، ولهم الطرد من الرحمة، ولهم سوء الدار.',NULL,'701',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4149,'<span data-type="title" id=toc-4189>٥٣ </span>-، ٥٤ - لقد آتينا موسى ما يهتدى به إلى الحق، وأورثنا بنى إسرائيل التوراة هادية ومذكرة لأصحاب العقول السليمة.',NULL,'701',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4150,'<span data-type="title" id=toc-4190>٥٥ </span>- إذا عرفت ما قصصناه عليك فاصبر - يا محمد - على ما ينالك من أذى، إن وعد الله بنصرك ونصر المؤمنين حق لا يتخلف، واطلب المغفرة من ربك لما قد يُعد ذنباً بالنسبة إليك، ونزِّه ربك عن النقائص تنزيها مقترناً بالثناء عليه أواخر النهار وأوائله.',NULL,'701',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4151,'<span data-type="title" id=toc-4191>٥٦ </span>- إن الذين يمارون فى دلائل الله بغير حُجة منه - تعالى - ليس فى صدورهم إلا تعال عن اتباع الحق، وليس تعاليهم بموصلهم إلى غايتهم، فاطلب الحفظ من الله، إنه هو المحيط سمعه وبصره بكل شئ.',NULL,'701',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4152,'<span data-type="title" id=toc-4192>٥٧ </span>- أقسم: لخلق السموات والأرض أعظم من خلق الناس، لكن أكثر الناس سلبوا العلم، فلم يؤمنوا بالبعث مع إقرارهم بأنه خالق السموات والأرض.',NULL,'701',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4153,'<span data-type="title" id=toc-4193>٥٨ </span>- وما يستوى الأعمى عن الحق والبصير العارف به، ولا يستوى المحسنون الذين آمنوا وعملوا الصالحات والمسئ فى عقيدته وعمله، قليلا - أى قليل - تتذكرون - أيها الناس.',NULL,'701',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4154,'<span data-type="title" id=toc-4194>٥٩ </span>- إن القيامة لآتية لا شك فيها، ولكن أكثر الناس لا يصدقون.',NULL,'701',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4155,'<span data-type="title" id=toc-4195>٦٠ </span>- وقال خالقكم ومالك أمركم: اسألونى أعطكم، إن الذين يتعاظمون عن دعائى سيدخلون جهنم أذلاء صاغرين.',NULL,'701',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4156,'<span data-type="title" id=toc-4196>٦١ </span>- الله - وحده - الذى جعل لكم الليل لتهدأوا فيه وتستريحوا من العمل، والنهار مضيئاً لتعملوا فيه، إن الله لصاحب فضل عظيم على الناس، ولكن أكثرهم لا يشكرونه على نعمه.',NULL,'702',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4157,'<span data-type="title" id=toc-4197>٦٢ </span>- ذلكم المنعم بهذه النعم الجليلة الله مالك أمركم، خالق كل شئ، لا معبود بحق إلا هو، فإلى أى جهة تصرفون عن عبادته إلى عبادة غيره؟ .',NULL,'702',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4158,'<span data-type="title" id=toc-4198>٦٣ </span>- مثل هذا الانصراف عن الحق إلى الباطل انصرف الذين كانوا من قبلكم، ينكرون آيات الله ويجحدونها.',NULL,'702',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4159,'<span data-type="title" id=toc-4199>٦٤ </span>- الله - وحده - الذى جعل لكم الأرض مستقرة صالحة لحياتكم عليها، والسماء بناء محكم الترابط، وقدَّر خلقكم فأبدع صوركم، وجعلكم فى أحسن تقويم، ورزقكم من المباحات ما يلذ لكم، ذلك المنعم بهذه النعم الله ربكم، فتعالى الله مالك العوالم كلها ومربيهم.',NULL,'702',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4160,'<span data-type="title" id=toc-4200>٦٥ </span>- هو المنفرد بالحياة الدائمة، لا معبود بحق إلا هو، فتوجهوا بالدعاء إليه مخلصين له العبادة، الثناء كله حق ثابت لله رب الخلائق جميعاً.',NULL,'702',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4161,'<span data-type="title" id=toc-4201>٦٦ </span>- قل - أيها الرسول -: إنى نُهيت عن عبادة الآلهة التى تعبدونها من دون الله حين جاءنى الحُجج من ربى، وأمرت أن أنقاد فى كل أمورى لله رب العوالم كلها.',NULL,'702',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4162,'<span data-type="title" id=toc-4202>٦٧ </span>- الله - وحده - الذى خلقكم - يا بنى آدم - من تراب، ثم حول هذا التراب نطفة، ثم حول هذه النطفة إلى قطعة دم جامدة، ثم يُخرجكم من بطون أمهاتكم أطفالا، ثم يمد فى آجالكم لتبلغوا سن الكمال فى القوة والعقل، ثم يطيل أعماركم لتكونوا شيوخاً، ومنكم من يُتوفى قبل سن الشباب أو الشيخوخة، وخلقكم الله على هذا النمط لتبلغوا وقتاً مسمى عنده وهو يوم البعث، ولكى تعقلوا ما فى هذا التنقل فى الأطوار من حكم وعبر.',NULL,'703',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4163,'<span data-type="title" id=toc-4203>٦٨ </span>- الله الذى يحيى ويميت، فإذا أراد إبراز أمر إلى الوجود فإنما يقول له: كن. فيكون دون تخلف.',NULL,'703',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4164,'<span data-type="title" id=toc-4204>٦٩ </span>- ألم تنظر إلى الذين يجادلون فى آيات الله الواضحة كيف يُصرفون عن النظر فيها ويصرون على ما هم فيه من ضلال؟ .',NULL,'703',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4165,'<span data-type="title" id=toc-4205>٧٠ </span>-، ٧١، ٧٢، ٧٣، ٧٤ - الذين كذَّبوا بالقرآن وبما أرسلنا به رسلنا - جميعاً - من الوحى، فسوف يعلمون عاقبة تكذيبهم حين تكون الأغلال والسلاسل فى أعناقهم، يجرون بها فى الماء الذى بلغ الغاية فى الحرارة، ثم بعد ذلك يلقون فى النار يصطلون حرها، ثم يقال لهم - توبيخاً وتبكيتاً -: أين معبوداتكم التى كنتم تعبدونها من دون الله؟ قال الكافرون: غابوا عنا، بل الحق أننا لم نكن نعبد من قبل فى الدنيا شيئاً يعتد به. مثل هذا الإضلال الشنيع يُضل الله الكافرين عن سبيل الحق لعلمه أنهم يؤثرون الضلالة على الهدى.',NULL,'703',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4166,'<span data-type="title" id=toc-4206>٧٥ </span>-، ٧٦ - يُقال للكافرين: ذلكم العذاب بسبب ما كنتم فى الدنيا تفرحون فى الأرض بغير ما يستحق الفرح، وبسبب توسعكم فى الفرح بما يصيب أنبياء الله وأولياءه من أذى، ادخلوا أبواب جهنم مقدراً لكم فيها الخلود، فبئس مستقر المتكبرين جهنم.',NULL,'703',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4167,'<span data-type="title" id=toc-4207>٧٧ </span>- فاصبر - يا محمد - إن وعد الله لك - بعذاب أعدائك - حق لا ريب فيه، وسيأتيهم هذا العذاب إمَّا فى حياتك أو حين يرجعون إلينا، فإن نُرِكَ بعض ما خوَّفناهم من العذاب فى حياتك فذاك، وإن نُمِتْك قبل ذلك فإلينا يرجعون، فنحاسبهم على ما كانوا يفعلون.',NULL,'704',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4168,'<span data-type="title" id=toc-4208>٧٨ </span>- لقد أرسلنا رسلا كثيرين من قبلك، منهم من أوردنا أخبارهم عليك، ومنهم من لم نرد عليك أخبارهم، وما كان لرسول منهم أن يأتى بمعجزة إلا بمشيئة الله وإرادته، لا من تلقاء نفسه ولا باقتراح قومه، فإذا جاء أمر الله بالعذاب فى الدنيا أو الآخرة قضى بينهم بالعدل، وخسر فى ذلك الوقت أهل الباطل.',NULL,'704',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4169,'<span data-type="title" id=toc-4209>٧٩ </span>- الله الذى ذلل لكم الإبل، لتركبوا بعضها وتأكلوا بعضها.',NULL,'704',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4170,'<span data-type="title" id=toc-4210>٨٠ </span>- ولكم فيها منافع كثيرة غير الركوب والأكل، ولتبلغوا عليها حاجة تهتمون بها فى أنفسكم، كجر الأثقال وحملها ونحو ذلك. وعلى الإبل التى هى نوع من الأنعام، وعلى الفلك تحملون أنتم وأمتعتكم.',NULL,'704',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4171,'<span data-type="title" id=toc-4211>٨١ </span>- ويريكم الله دلائل قدرته، فأخبرونى أى دليل منها تنكرون، وهى من الوضوح بحيث لا ينكرها من له أدنى عقل.',NULL,'704',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4172,'<span data-type="title" id=toc-4212>٨٢ </span>- أقعدوا فلم يسيروا فى الأرض فيروا كيف كان عاقبة الذين من قبلهم من الهلاك والتدمير!؟ كان من قبلهم أكثر منهم عدداً وأشد منهم قوة وآثاراً فى الأرض، فما دفع عنهم عذاب الله ما كسبوه من مال أو قوة أو سلطان.',NULL,'704',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4173,'<span data-type="title" id=toc-4213>٨٣ </span>- فحين جاءت هذه الأمم رسلهم بالشرائع والمعجزات الواضحات فرحت هذه الأمم بما عندهم من علوم الدنيا، واستهزأوا بعلم المرسلين، فنزل بهم العذاب الذى أخبرهم به المرسلون وكانوا به يستهزئون.',NULL,'705',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4174,'<span data-type="title" id=toc-4214>٨٤ </span>- فَلمَّا رأت هذه الأمم شدة عذابنا قالوا: صدَّقنا بالله - وحده - وأنكرنا الآلهة التى كنا بسببها مشركين.',NULL,'705',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4175,'<span data-type="title" id=toc-4215>٨٥ </span>- فلم يكن ينفعهم إيمانهم حين رأوا شدة عذابنا، سَنَّ الله سنة قد سبقت فى عباده: ألا يقبل الإيمان حين نزول العذاب، وخسر وقت نزول العذاب الكافرون.',NULL,'705',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4176,'<span data-type="title" id=toc-4217>١ </span>- حم حرفان من حروف المعجم افتتحت بهما السورة - كعادة القرآن فى افتتاح كثير من السور - لإثارة الانتباه والتدليل على إعجاز القرآن.',NULL,'706',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4177,'<span data-type="title" id=toc-4218>٢ </span>- هذا الكتاب تنزيل بديع من المنعم بجلائل النعم ودقائقها.',NULL,'706',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4178,'<span data-type="title" id=toc-4219>٣ </span>- كتاب ميزت آياته لفظاً ومقاطع، ومعنى بتمييزه بين الحق والباطل، والبشارة والإنذار، وتهذيب النفوس، وضرب الأمثال، وبيان الأحكام، وهو مقروء باللسان العربى ميسراً فهمه لقوم يعلمون.',NULL,'706',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4179,'<span data-type="title" id=toc-4220>٤ </span>- مبشراً المؤمنين العاملين بما أعد لهم من نعيم، ومخوفاً المكذبين بما أعد لهم من عذاب أليم، فانصرف عنه أكثرهم، فلم ينتفعوا به، كأنهم لم يسمعوا.',NULL,'707',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4180,'<span data-type="title" id=toc-4221>٥ </span>- وقال الكافرون للرسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ -: قلوبنا فى أغطية متكاثقة مما تدعونا إليه من توحيد الله، وفى آذاننا صمم فلا نسمع ما تدعونا إليه، ومن بيننا وبينك حجاب منيع يمنعنا من قبول ما جئت به، فاعمل ما شئت إننا عاملون ما شئنا.',NULL,'707',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4181,'<span data-type="title" id=toc-4222>٦ </span>-، ٧ - قل لهم - أيها الرسول -: ما أنا إلا بشر مثلكم يوحى إلى من الله إنما معبودكم الحق إله واحد، فاسلكوا إليه الطريق القويم، واطلبوا منه المغفرة لذنوبكم، وعذاب شديد للمشركين الذين لا يؤدون الزكاة إلى مستحقيها، وهم بالحياة الآخرة - دون غيرهم - جاحدون.',NULL,'707',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4182,'<span data-type="title" id=toc-4223>٨ </span>- إن المؤمنين الذين عملوا الصالحات لهم جزاء حسن غير مقطوع.',NULL,'707',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4183,'<span data-type="title" id=toc-4224>٩ </span>- قل - أيها الرسول - لهؤلاء المشركين: عجباً لكم، تكفرون بالله الذى خلق الأرض فى يومين، وأنتم - مع هذا - تجعلون له شركاء متساوين معه، ذلك الخالق للأرض مالك العوالم كلها ومربيهم.',NULL,'707',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4184,'<span data-type="title" id=toc-4225>١٠ </span>- وجعل فى الأرض جبالا ثابتة من فوقها لئلا تميد بكم، وأكثر فيها الخير وقدر فيها أرزاق أهلها، حسبما تقتضيه حكمته، فى أربعة أيام، وأنتم - مع هذا - تجعلون له شركاء، وقدر كل شئ لا نقص فيه ولا زيادة، هذا التفصيل فى خلق الأرض وما عليها بيان للسائلين.',NULL,'707',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4185,'<span data-type="title" id=toc-4226>١١ </span>- ثم تعلقت قدرته بخلق السماء وهى على هيئة دخان فوجدت، وخلقه للسموات والأرض - على وفق إرادته - هيِّن عليه بمنزلة ما يقال للشئ: احضر - راضياً أو كارهاً - فيطيع.',NULL,'708',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4186,'<span data-type="title" id=toc-4227>١٢ </span>- وأتم خلق السموات سبعاً فى يومين آخرين، وأوجد فى كل سماء ما أعدت له واقتضته حكمته، وزين السماء القريبة من الأرض بالنجوم المنيرة كالمصابيح، للهداية وحفظا من استماع الشياطين لأخبار الملأ الأعلى، ذلك الخلق المتقن تدبير العزيز الذى لا يغلب، المحيط علمه بكل شئ.',NULL,'708',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4187,'<span data-type="title" id=toc-4228>١٣ </span>- فإن أعرض المشركون عن الإيمان بعد وضوح دلائله فقل لهم - أيها الرسول -: خوفتكم عذاباً شديد الوقع كالصاعقة مثل صاعقة عاد وثمود.',NULL,'708',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4188,'<span data-type="title" id=toc-4229>١٤ </span>- أتت عاداً وثمود الصاعقة حين أتتهم رسلهم من جميع الجهات، فلم يدعوا طريقاً لإرشادهم إلا سلكوه، وقالوا لهم: لا تعبدوا إلا الله. قالوا: لو أراد الله إرسال رسول لأنزل إلينا ملائكة، فإنا بما أرسلتم به من التوحيد وغيره جاحدون.',NULL,'708',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4189,'<span data-type="title" id=toc-4230>١٥ </span>- فأما عاد فتعالوا فى الأرض بغير حق لهم فى هذا التعالى، وقالوا - مغترين بأنفسهم -: من أشد منا قوة؟ {عجباً لهم. أيقولون ذلك ولم يروا أن الله الذى خلقهم هو أشد منهم قوة؟} وكانوا بآياتنا ينكرون.',NULL,'708',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4190,'<span data-type="title" id=toc-4231>١٦ </span>- فأرسلنا عليهم ريحاً ذات صوت شديد فى أيام مشئومات لنذيقهم عذاب الهون فى الحياة الدنيا، وأقسم: لعذاب الآخرة أشد خزياً، وهم لا ينصرهم ناصر يومئذ.',NULL,'708',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4191,'<span data-type="title" id=toc-4232>١٧ </span>- وأما ثمود فبينا لهم طريق الخير وطريق الشر، فاختاروا الضلالة على الهدى فأصابتهم صاعقة أحرقتهم فى مذلة وهوان، بسبب ما كسبوا من ذنوب.',NULL,'709',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4192,'<span data-type="title" id=toc-4233>١٨ </span>- ونجينا من هذا العذاب الذين آمنوا وكانوا يتقون الله ويخشون عذابه.',NULL,'709',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4193,'<span data-type="title" id=toc-4234>١٩ </span>- واذكر لهم - أيها النبى - يوم يحشر أعداء الله إلى النار، فيجئ أولهم على آخرهم، ليتم إلزام الحُجة عليهم بين جميعهم.',NULL,'709',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4194,'<span data-type="title" id=toc-4235>٢٠ </span>- حتى إذا ما جاءوا النار وسئلوا عما ارتكبوا من الآثام فى الدنيا، فأنكروا، شهد عليهم سمعهم وأبصارهم وجلودهم بما كانوا يعملون فى الدنيا.',NULL,'709',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4195,'<span data-type="title" id=toc-4236>٢١ </span>- وقال أعداء الله لجلودهم: لم شهدتم علينا؟ قالوا: أنطقنا الله الذى أنطق كل شئ، وهو خلقكم أول مرة من العدم، وإليه - وحده - ترجعون بعد البعث فيحاسبكم على ما قدمتم من عمل.',NULL,'709',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4196,'<span data-type="title" id=toc-4237>٢٢ </span>- وما كان باستطاعتكم أن تخفوا أعمالكم القبيحة عن جوارحكم مخافة أن يشهد عليكم سمعكم وأبصاركم وجلودكم، ولكن كنتم تظنون أن الله لا يعلم كثيراً من أعمالكم، بسبب إتيانها فى الخفاء.',NULL,'709',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4197,'<span data-type="title" id=toc-4238>٢٣ </span>- وذلك الظن الفاسد الذى ظننتموه بربكم أهلككم، فأصبحتم - يوم القيامة - من الخاسرين أتم خسران.',NULL,'709',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4198,'<span data-type="title" id=toc-4239>٢٤ </span>- فإن يكظموا آلامهم فالنار مصيرهم ومستقرهم الدائم، وإن يطلبوا رضاء الله عليهم فما هم بمجابين إلى طلبهم.',NULL,'709',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4199,'<span data-type="title" id=toc-4240>٢٥ </span>- وهيأنا لهم قرناء فاسدين - فى الدنيا - فحسنوا لهم ما بين أيديهم من أمور الآخرة - فأغروهم بأنه لا بعث ولا حساب - وما خلفهم من أمور الدنيا ليستمتعوا بها، وثبتت عليهم كلمة العذاب مع أمم قد مضت من قبلهم من الجن والإنس ممن كانوا على شاكلتهم، لاختيارهم الضلالة على الهدى، إن هؤلاء - جميعاً - كانوا من الخاسرين أتم خسران.',NULL,'709',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4200,'<span data-type="title" id=toc-4241>٢٦ </span>- وقال الكفار بعضهم لبعض: لا تصغوا لهذا القرآن، وأتوا باللغو الباطل عند تلاوته فلا يستمع لتلاوته أحد ولا ينتفع به، رجاء أن تغلبوا محمداً بذلك.',NULL,'710',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4201,'<span data-type="title" id=toc-4242>٢٧ </span>- فنقسم: لنذيقن الذين كفروا عذاباً شديداً على فعلهم - ولا سيما محاربتهم القرآن - ولنجزينهم أسوأ جزاء على أعمالهم.',NULL,'710',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4202,'<span data-type="title" id=toc-4243>٢٨ </span>- ذلك الذى ذكر من العذاب جزاء حق لأعداء الله، النار مُعَدٌّ لهم فيها دار الخلود، جزاء جحودهم المستمر بآيات الله وحججه.',NULL,'710',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4203,'<span data-type="title" id=toc-4244>٢٩ </span>- وقال الكافرون - وهم فى النار -: ربنا أرنا الفريقين اللذين أوقعانا فى الضلال من الجن والإنس نجعلهما تحت أقدامنا، ليكونا من الأسفلين مكانة ومكاناً.',NULL,'710',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4204,'<span data-type="title" id=toc-4245>٣٠ </span>- إن الذين قالوا: ربنا الله إقراراً بوحدانيته، ثم استقاموا على شريعته، تنزل عليهم الملائكة مرة بعد مرة، قائلين: لا تخافوا من شر ينزل بكم، ولا تحزنوا على خير يفوتكم، وأبشروا بالجنة التى كنتم توعدون بها على لسان الأنبياء والمرسلين.',NULL,'710',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4205,'<span data-type="title" id=toc-4246>٣١ </span>-، ٣٢ - وتقول لهم الملائكة: نحن نصراؤكم فى الحياة الدنيا بالتأييد وفى الآخرة بالشفاعة والتكريم، ولكم فى الآخرة ما تشتهيه أنفسكم من الملاذ والطيبات، ولكم فيها ما تتمنون إكراماً وتحية من رب واسع المغفرة والرحمة.',NULL,'710',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4206,'<span data-type="title" id=toc-4247>٣٣ </span>- لا أحد أحسن قولاً ممن دعا إلى توحيد الله وطاعته، وعمل - مع ذلك - عملا صالحاً، وقال - اعترافاً بعقيدته -: إنى من المنقادين لأوامر الله.',NULL,'710',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4207,'<span data-type="title" id=toc-4248>٣٤ </span>- ولا تستوى الخصلة الحسنة ولا الخصلة القبيحة، ادفع الإساءة - إن جاءتك من عدو - بالخصلة التى هى أحسن منها، فتكون العاقبة العاجلة. إن الذى بينك وبينه عداوة كأنه ناصر مخلص.',NULL,'710',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4208,'<span data-type="title" id=toc-4249>٣٥ </span>- وما يُرزَق هذه الخصلة - وهى دفع السيئة بالحسنة - إلا الذين عندهم خُلق الصبر، وما يُرزقها إلا ذو نصيب عظيم من خصال الخير وكمال النفس.',NULL,'711',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4209,'<span data-type="title" id=toc-4250>٣٦ </span>- وإن يوسوس لك الشيطان ليصرفك عمَّا أمرت به - أيها المخاطب - فتحصن بالله منه، إن الله هو المحيط سمعه وعلمه بكل شئ فيُعيذك منه.',NULL,'711',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4210,'<span data-type="title" id=toc-4251>٣٧ </span>- ومن دلائل قدرته تعالى - الليل والنهار والشمس والقمر، لا تسجدوا للشمس ولا للقمر، لأنهما من آياته، واسجدوا لله - وحده - الذى خلق الشمس والقمر والليل والنهار إن كنتم حقا تعبدونه وحده.',NULL,'711',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4211,'<span data-type="title" id=toc-4252>٣٨ </span>- فإن تعاظم المشركون عن امتثال أمرك فلا تأسف، فالذين عند ربك فى حضرة قدسه - وهم الملائكة - يُنزِّهونه عن كل نقص فى كل وقت بالليل والنهار، مخلصين له، وهم لا يَمِلُّون من تسبيحه.',NULL,'711',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4212,'<span data-type="title" id=toc-4253>٣٩ </span>- ومن دلائل قدرته - تعالى - أنك ترى - يا من يستطيع أن يرى - الأرض يابسة، فإذا أنزلنا عليها الماء تحرَّكت للإنبات، إن الذى أحيا الأرض بعد موتها لخليق أن يحيى الموتى من الحيوان، إنه على كل شئ تام القدرة.',NULL,'711',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4213,'<span data-type="title" id=toc-4254>٤٠ </span>- إن الذين يميلون عن الصراط السوى فى شأن آياتنا، ويزيغون عنها تكذيباً لها، لا يغيب عنا أمرهم وما يقصدون، وسنجازيهم بما يستحقون، أفمَن يرمى فى النار خير أم من يأتى مطمئنا يوم القيامة إلى نجاته من كل سوء؟ قل لهم متوعداً: اعملوا ما أردتم، إن الله محيط بصره بكل شئ، فيجازى كلا بعمله.',NULL,'711',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4214,'<span data-type="title" id=toc-4255>٤١ </span>-، ٤٢ - إن الذين جحدوا بالقرآن ذى الشأن حين جاءهم - من غير تدبر - سيكون لهم من العذاب ما لا يدخل تحت تصور أحد. جحدوه وإنه لكتاب عز نظيره، يغلب كل من عارضه، لا يأتيه الباطل الذى لا أصل له من أية ناحية من نواحيه، نزل متتابعاً من إله منزه عن العبث، محمود كثير الحمد بما أسدى من نعم.',NULL,'711',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4215,'<span data-type="title" id=toc-4256>٤٣ </span>- لا يقال لك - يا محمد - من أعدائك إلا كما قيل للرسل من قبلك من أعدائهم من شتْم وتكذيب، إن خالقك ومربيك لذو مغفرة عظيمة وذو عقاب بالغ الألم، فيغفر لمن تاب منهم وينتقم لك ممن أصر على عناده.',NULL,'712',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4216,'<span data-type="title" id=toc-4257>٤٤ </span>- ولو جعلنا القرآن أعجمياً - كما اقترح بعض المتعنتين - لقالوا - منكرين -: هلا بيَّنت آياته بلسان نفقهه، أكتاب أعجمى ومخاطب به عربى؟ قل لهم - أيها الرسول - هو كما نزل للمؤمنين - دون غيرهم - هدى وشفاء للمؤمنين، ينقذهم من الحيرة، ويشفيهم من الشكوك. والذين لا يؤمنون به كأن فى آذانهم - من الإعراض - صمماً، وهو عليهم عمى، لأنهم لا يرون منه إلا ما يبتغون به الفتنة، أولئك الكافرون كمن يدعون إلى الإيمان به من مكان بعيد لا يسمعون فيه دعاء.',NULL,'712',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4217,'<span data-type="title" id=toc-4258>٤٥ </span>- أقسم: لقد آتينا موسى التوراة فاختلف فيها قومه، ولولا قضاء سبق من ربك - يا محمد - أن يُؤخر عذاب المكذبين بك إلى أجل محدد عنده، لفصل بينك وبينهم باستئصال المكذبين، وإن كفار قومك لفى شك من القرآن موجب للقلق والاضطراب.',NULL,'712',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4218,'<span data-type="title" id=toc-4259>٤٦ </span>- من عمل عملاً صالحاً فأجره لنفسه، ومن أساء فى عمله فإثمه على نفسه، وليس ربك بظلام لعبيده، فيعاقب أحداً بذنب غيره.',NULL,'712',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4219,'<span data-type="title" id=toc-4260>٤٧ </span>- إلى الله - وحده - يرجع علم قيام الساعة، وما تخرج من ثمرات من أوعيتها، وما تحمل من أنثى ولا تضع حملها إلا كان هذا مقترناً بعلمه، واذكر يوم ينادى الله المشركين - توبيخاً لهم -: أين شركائى الذين كنتم تدعونهم من دونى؟ قالوا - معتذرين -: نُعْلمك - يا الله - ليس منا من يشهد أن لك شريكاً.',NULL,'712',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4220,'<span data-type="title" id=toc-4261>٤٨ </span>- وغاب عنهم ما كانوا يعبدونه من قبل من الشركاء، وأيقنوا أنه لا مهرب لهم.',NULL,'712',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4221,'<span data-type="title" id=toc-4262>٤٩ </span>- لا يمل الإنسان من دعاء ربه بالخير الدنيوى، فإذا أصابه الشر فهو ذو يأس شديد من الخير، ذو قنوط بالغ من أن يستجيب الله دعاءه.',NULL,'713',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4222,'<span data-type="title" id=toc-4263>٥٠ </span>- ونقسم: إن أذقنا الإنسان نعمة - تفضلا منا - من بعد ضر شديد أصابه ليقولن: هذا الذى نلته من النعم حق ثابت لى، وما أظن القيامة آتية، وأقسم: إن فُرِضَ ورجعت إلى ربى إن لى عنده للعاقبة البالغة الحسن. ونقسم نحن لنجزين الذين كفروا - يوم القيامة - بعملهم، ولنذيقنهم من عذاب شديد متراكماً بعضه فوق بعض.',NULL,'713',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4223,'<span data-type="title" id=toc-4264>٥١ </span>- وإذا أنعمنا على الإنسان تولى عن شكرنا، وبعد بجانبه عن ديننا، وإذا مسه الشر فهو ذو دعاء كثير.',NULL,'713',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4224,'<span data-type="title" id=toc-4265>٥٢ </span>- قل لهم - يا محمد -: أخبرونى إن كان هذا القرآن من عند الله ثم جحدتم به، فمن أبعد عن الصواب ممن هو فى خلاف بعيد عن الحق؟!',NULL,'713',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4225,'<span data-type="title" id=toc-4266>٥٣ </span>- قريباً نُرِى هؤلاء المنكرين دلائلنا على صدقك فى أقطار السموات والأرض وفى أنفسهم حتى يظهر لهم أن ما جئت به هو الحق دون غيره، أأنكروا إظهارنا لهم الآيات، أو لم يكف بربك أنه مطلع على كل شئ؟',NULL,'713',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4226,'<span data-type="title" id=toc-4267>٥٤ </span>- ألا إن هؤلاء الكفار فى شك عظيم من لقاء ربهم لاستبعادهم البعث، ألا إن الله بكل شئ محيط بعلمه وقدرته.',NULL,'713',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4227,'<span data-type="title" id=toc-4269>١ </span>-، ٢ - حم. عسق: افتتحت هذه السورة بهذه الحروف الصوتية على طريقة القرآن الكريم فى افتتاح كثير من السور بمثل هذه الحروف.',NULL,'714',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4228,'<span data-type="title" id=toc-4270>٣ </span>- مثل ما فى هذه السورة من المعانى يوحى إليك وإلى المرسلين من قبلك الله الغالب بقهره، الذى يضع كل شئ موضعه، على وفق الحكمة فى أفعاله وتدبيره.',NULL,'714',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4229,'<span data-type="title" id=toc-4271>٤ </span>- لله - وحده - ما فى السموات وما فى الأرض خلقاً وملكاً وتدبيراً، وهو المتفرد بعلو الشأن وعظم السلطان.',NULL,'715',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4230,'<span data-type="title" id=toc-4272>٥ </span>- تكاد السموات - مع عِظَمِهِن وتماسكهن - أن يتشققن من فوقهن، خشية من الله، وتأثراً بعظمته وجلاله، والملائكة ينزهون الله عما لا يليق به، مثنين عليه بما هو أهله، ويسألون الله المغفرة لأهل الأرض، وينبه - سبحانه - إلى أن الله هو - وحده - صاحب المغفرة الشاملة والرحمة الواسعة.',NULL,'715',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4231,'<span data-type="title" id=toc-4273>٦ </span>- والذين اتخذوا من دون الله نصراء، الله رقيب عليهم فيما يفعلون، ولست أنت - يا محمد - موكلاً بمراقبهم.',NULL,'715',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4232,'<span data-type="title" id=toc-4274>٧ </span>- ومثل ذلك الإيحاء البيِّن أوحينا إليك قرآناً عربياً لا لبس فيه، لتنذر أهل مكة ومن حولها من العرب، وتنذر الناس عذاب يوم يجمع الله فيه الخلائق للحساب، لا ريب فى مجيئه، الناس فيه فريقان: فريق فى الجنة، وفريق فى السعير.',NULL,'715',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4233,'<span data-type="title" id=toc-4275>٨ </span>- ولو شاء الله أن يجمع الناس فى الدنيا على طريقة واحدة لجمعهم، ولكن يدخل من يشاء فى رحمته، لعلمه أنهم سيختارون الهدى على الضلالة، والظالمون أنفسهم بالكفر ليس لهم من دون الله ولى يتكفل بحمايتهم، ولا نصير يُنقذهم من عذاب الله.',NULL,'715',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4234,'<span data-type="title" id=toc-4276>٩ </span>- هؤلاء الظالمون لم يتخذوا الله ولياً، بل اتخذوا غيره أولياء، وليس لهم ذلك، فالله - وحده - الولى بحق إن أرادوا ولياً، وهو يحيى الموتى للحساب، وهو المسيطر بقدرته على كل شئ.',NULL,'715',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4235,'<span data-type="title" id=toc-4277>١٠ </span>- والذى اختلفتم فيه من الإيمان والكفر فالحكم الفصل فيه مفوض إلى الله، وقد بينه، وهو - سبحانه - معتمدى ومرجعى فى كل أمورى.',NULL,'715',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4236,'<span data-type="title" id=toc-4278>١١ </span>- مُبدع السموات والأرض، خلق لكم من جنسكم أزواجاً ذكوراً وإناثاً، وخلق من الأنعام من جنسها أزواجاً كذلك، يكثِّركم بهذا التدبير المحكم، ليس كذاته شئ، فليس له شئ يزاوجه، وهو المدرك - إدراكاً كاملاً - لجميع المسموعات والمرئيات بلا تأثر حاسة.',NULL,'716',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4237,'<span data-type="title" id=toc-4279>١٢ </span>- له مقاليد السموات والأرض حفظاً وتدبيراً، يوسع الرزق لمن يشاء، ويضيقه على من يشاء، إنه - تعالى - محيط علمه بكل شئ.',NULL,'716',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4238,'<span data-type="title" id=toc-4280>١٣ </span>- شرع لكم من العقائد ما عهد به إلى نوح، والذى أوحيناه إليك، وما عهدنا به إلى إبراهيم وموسى وعيسى، أن ثبِّتوا دعائم الدين - بامتثال ما جاء به - ولا تختلفوا فى شأنه، شق على المشركين ما تدعوهم إليه من إقامة دعائم الدين، الله يصطفى لرسالته من يشاء، ويوفق للإيمان وإقامة الدين من يترك العناد ويقبل عليه.',NULL,'716',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4239,'<span data-type="title" id=toc-4281>١٤ </span>- وما اختلف أتباع الرسل السابقين فى الدين عداوة وحسداً فيما بينهم إلا من بعد ما جاءهم العلم بحقيقته فى رسالتك، ولولا وعْد سابق من الله بتأجيل العذاب إلى يوم القيامة لأهلكوا، وإن الذين ورثوا الكتاب من أسلافهم وأدركوا عهدك لفى شك من كتابهم موقع فى الريب، حيث لم يستجيبوا لدعوتك.',NULL,'716',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4240,'<span data-type="title" id=toc-4282>١٥ </span>- فلأجل وحدة الدين، وعدم التفرق فيه، فادعهم إلى إقامة الدين، وثابر على تلك الدعوة كما أمرك الله، ولا تساير أهواء المشركين، وقل: آمنت بجميع الكتب التى أنزلها الله على رسله، وأمرنى الله بإقامة العدل بينكم، وقل لهم: الله خالقنا وخالقكم، لنا أعمالنا لا لكم، ولكم أعمالكم لا لنا، لا احتجاج بيننا وبينكم لوضوح الحق. الله يجمع بيننا للفصل بالعدل، وإليه - وحده - المرجع والمآل.',NULL,'716',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4241,'<span data-type="title" id=toc-4283>١٦ </span>- والذين يُجادلون فى دين الله من بعد ما استجاب الناس لدعوته الواضحة، حُجَّة هؤلاء المرتابين باطلة عند ربهم، وعليهم غضب شديد بكفرهم، ولهم عذاب أليم ينتظرهم.',NULL,'717',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4242,'<span data-type="title" id=toc-4284>١٧ </span>- الله الذى أنزل كتاب محمد وما قبله من كتب المرسلين مشتملة على الحق والعدل، وما يُعلمك لعل وقت الساعة قريب وأنت لا تدرى.',NULL,'717',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4243,'<span data-type="title" id=toc-4285>١٨ </span>- يستعجل بمجئ الساعة - استهزاء - الذين لا يصدقون بها، والذين صدقوا بها خائفون من وقوعها فلا يستعجلونها، ويعلمون أنها الحق الثابت الذى لا ريب فيه، ويُنبِّه - سبحانه - إلى أن الذين يجادلون فى وقوعها لفى ضلال بعيد عن الحق.',NULL,'717',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4244,'<span data-type="title" id=toc-4286>١٩ </span>- الله عظيم البر بجميع عباده، يرزق من يشاء كما يشاء، وهو الغالب على كل شئ، المنيع الذى لا يغلب.',NULL,'717',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4245,'<span data-type="title" id=toc-4287>٢٠ </span>- من كان يريد بعمله ثواب الآخرة نضاعف له أجره، ومن كان يريد بأعماله متاع الدنيا - فحسب - غير راغب فى متاع الآخرة نعطه ما قسم له فيها، وليس له فى الآخرة نصيب من الثواب.',NULL,'717',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4246,'<span data-type="title" id=toc-4288>٢١ </span>- بل ألهُم آلهة شرعوا لهم من الدين ما لم يأمر به الله؟ لم يكن ذلك، ولولا وعد سابق بتأخر الفصل إلى يوم القيامة لقضى بين الكافرين والمؤمنين فى الدنيا، وإن الظالمين أنفسهم بالكفر لهم عذاب شديد الإيلام.',NULL,'717',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4247,'<span data-type="title" id=toc-4289>٢٢ </span>- ترى فى القيامة - أيها المخاطب - الذين ظلموا أنفسهم بالشرك خائفين عقاب شركهم، وهو نازل بهم - لا محالة - وترى الذين آمنوا وعملوا الصالحات مُتَمَتِّعين فى أطيب بقاع الجنة، لهم ما يتمنون من النعيم عند ربهم، ذلك الجزاء العظيم هو الفضل الكبير الذى تتعلق به الآمال.',NULL,'717',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4248,'<span data-type="title" id=toc-4290>٢٣ </span>- ذلك الفضل الكبير هو الذى يُبشِّر الله به عباده المؤمنين الطائعين، قل - أيها الرسول -: لا أطلب منكم على تبليغ الرسالة أجراً إلا أن تحبوا الله ورسوله فى تقربكم إليه - سبحانه - بعمل الصالحات، ومن يكتسب طاعة يُضاعف الله له جزاءها، إن الله واسع المغفرة للمذنبين، شكور لعباده طيبات أعمالهم.',NULL,'718',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4249,'<span data-type="title" id=toc-4291>٢٤ </span>- أيقول الكفار: اختلق محمد الكذب على الله؟! فإن يشأ الله يربط على قلبك بالصبر على أذاهم، واتهامك بالافتراء على الله، ويزيل الله الشرك ويخذله، ويثبت الإسلام ويظهره بالوحى الذى أنزله على رسوله صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ إنه - سبحانه - محيط بخفايا قلوبكم جميعاً.',NULL,'718',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4250,'<span data-type="title" id=toc-4292>٢٥ </span>- والله - وحده - الذى يقبل التوبة من أهل طاعته بالتجاوز عما تابوا منه، ويصفح - تفضلاً ورحمة - عن السيئات دون الشرك، ويعلم ما تفعلون من خير أو شر.',NULL,'718',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4251,'<span data-type="title" id=toc-4293>٢٦ </span>- ويُجيب الله المؤمنين إلى ما طلبوا، ويزيدهم خيراً على مطلوبهم، والكافرون لهم عذاب بالغ غاية الشدة والإيلام.',NULL,'718',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4252,'<span data-type="title" id=toc-4294>٢٧ </span>- ولو وسَّع الله الرزق لجميع عباده - كما يبتغون - لطغوا فى الأرض وظلموا، ولكن الله يوسع الرزق لمن يشاء، ويُضيقه على من يشاء، حسبما اقتضته حكمته، إن الله محيط علماً بما خفى وظهر من أمور عباده، فيقدر بحكمته لكلٍّ ما يصلح شأنه.',NULL,'718',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4253,'<span data-type="title" id=toc-4295>٢٨ </span>- والله - وحده - هو الذى ينزل المطر الذى يغيثهم من الجدب من بعد اليأس واشتداد القحط، رحمة بعباده، وينشر بركات المطر فى النبات والثمار والحيوان والسهل والجبل، وهو - وحده - الذى يتولى تدبير أمور عباده، المحمود على إنعامه وجميع أفعاله.',NULL,'718',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4254,'<span data-type="title" id=toc-4296>٢٩ </span>- ومن دلائل قدرة الله على خلق ما يشاء: خلق السموات والأرض على هذا النظام المحكم، وخلق ما فرق ونشر فيهما من الدواب المرئية وغيرها، والله الذى ثبتت قدرته بإبداع ما تقدم قدير على جمع المكلفين فى الوقت الذى يشاء بعثهم فيه للجزاء.',NULL,'718',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4255,'<span data-type="title" id=toc-4297>٣٠ </span>- وأى مصيبة أصابتكم مما تكرهونه فبسبب معاصيكم، وما عفا عنه فى الدنيا أو آخذ عليه فيها، فالله أكرم من أن يعاقب به فى الآخرة، وبهذا تنزَّه عن الظلم واتصف بالرحمة الواسعة.',NULL,'719',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4256,'<span data-type="title" id=toc-4298>٣١ </span>- ولستم بقادرين على أن تعجزوا الله عن إنزال المصائب فى الدنيا عقاباً على معاصيكم، وإن هربتم فى الأرض كل مهرب، وليس لكم من دون الله من يتولاكم بالرحمة عند نزول البلاء، ولا من ينصركم بدفعه عنكم.',NULL,'719',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4257,'<span data-type="title" id=toc-4299>٣٢ </span>- ومن دلائل قدرة الله السفن الجارية فى البحر كالجبال الشاهقة فى عظمتها.',NULL,'719',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4258,'<span data-type="title" id=toc-4300>٣٣ </span>- إن يشأ الله يُسْكِن الريح فتظل السفن ثوابت على ظهر الماء لا تجرى بهم إلى مقاصدهم، إن فى سيرها ووقوفها بأمر الله لدلائل واضحة على قدرة الله، يعتبر بها المؤمنون الصابرون فى الضراء، الشاكرون فى السراء.',NULL,'719',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4259,'<span data-type="title" id=toc-4301>٣٤ </span>- أو يهلكن بذنوب ركابها بإرسال الرياح العاصفة، وإن يشأ يعف عن كثير، فلا يعاقبهم بإسكان الريح، أو بإرسالها عاصفة مغرقة.',NULL,'719',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4260,'<span data-type="title" id=toc-4302>٣٥ </span>- الله - سبحانه - فعل ذلك ليعتبر المؤمنون، ويعلم الذين يردون آياته بالباطل أنهم فى قبضته، ما لهم مهرب من عذاب الله.',NULL,'719',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4261,'<span data-type="title" id=toc-4303>٣٦ </span>- لا تغتروا بمتاع الدنيا، فكل ما أعطيتموه - أيها الناس - من المال والبنين وسواهما فهو متاع لكم فى الحياة الدنيا، وما أعده الله من نعيم الجنة خير وأدْوم للذين آمنوا، وعلى خالقهم ومربيهم - وحده - يعتمدون.',NULL,'719',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4262,'<span data-type="title" id=toc-4304>٣٧ </span>- والذين يبتعدون عن ارتكاب كبائر ما نهى الله عنه، وكل ما زاد قبحه من الذنوب، وإذا ما استفزوا بالإساءة إليهم فى دنياهم، هم - وحدهم - يبادرون بالصفح حتى كان ذلك علاجاً نافعاً.',NULL,'719',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4263,'<span data-type="title" id=toc-4305>٣٨ </span>- والذين أجابوا دعوة خالقهم ومربيهم، فآمنوا به، وحافظوا على صلواتهم، وكان شأنهم التشاور فى أمورهم لإقامة العدل فى مجتمعهم، دون أن يستبد بهم فرد أو قلة من الناس، ومما أنعم الله به عليهم ينفقون فى وجوه الخير.',NULL,'720',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4264,'<span data-type="title" id=toc-4306>٣٩ </span>- والذين إذا اعتدى عليهم ظالم هم ينتصرون لأنفسهم بمقاومة عدوانه.',NULL,'720',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4265,'<span data-type="title" id=toc-4307>٤٠ </span>- وجزاء المسئ إساءة مماثلة تقريراً للعدل، فمن عفا عمن أساءه عند القدرة، وأصلح ما بينه وبين خصمه تقريراً للود، فثوابه على الله الذى لا يعلم بقدره سواه، إن الله لا يرحم المعتدين على حقوق الناس بمجاوزة شريعة الله.',NULL,'720',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4266,'<span data-type="title" id=toc-4308>٤١ </span>- وإن الذين يعاقبون المعتدين بمثل ما اعتدوا به فلا مؤاخذة عليهم ولا لوم.',NULL,'720',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4267,'<span data-type="title" id=toc-4309>٤٢ </span>- إنما اللوم والمؤاخذة على المعتدين الذين يظلمون الناس ويتكبرون فى الأرض، ويفسدون فيها بغير الحق، أولئك لهم عذاب شديد الإيلام.',NULL,'720',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4268,'<span data-type="title" id=toc-4310>٤٣ </span>- أقسم: لمن صبر على الظلم وتجاوز عن ظالمه، ولم ينتصر لنفسه حينما لا يكون العفو تمكيناً للفساد فى الأرض، إن ذلك لمن الأمور التى ينبغى أن يوجبها العاقل على نفسه.',NULL,'720',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4269,'<span data-type="title" id=toc-4311>٤٤ </span>- ومن ضل طريق الهدى - لسوء اختياره - فليس له ناصر سوى الله يهديه أو يمنعه من العذاب، وترى فى القيامة - أيها المخاطب - الظالمين حين يشاهدون عذاب الآخرة يسألون ربهم أى وسيلة يرجعون بها إلى الدنيا، كى يعملوا صالحاً غير الذى كانوا يعملون.',NULL,'720',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4270,'<span data-type="title" id=toc-4312>٤٥ </span>- وترى الظالمين - كذلك - يُعرضون على النار متضائلين بسبب ما رأوه من الهول وما نزل بهم من الهوان، يسارقون النظر إلى النار خوفاً من مكارهها، ويقول المؤمنون - حينئذ -: إن الخاسرين حقا هم الذين ظلموا أنفسهم بالكفر، وخسروا أزواجهم وأولادهم وأقاربهم بما حيل بينهم، ويُنبِّه الله إلى أن الظالمين فى عذاب دائم.',NULL,'720',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4271,'<span data-type="title" id=toc-4313>٤٦ </span>- وما كان لهم نصراء مما عبدوهم من دون الله، وممن أطاعوا فى معصيته، ينقذونهم من عذاب الله، ومن ضل طريق الحق - لسوء اختياره - فليس له أى طريق ينجِّيه من سوء المصير.',NULL,'721',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4272,'<span data-type="title" id=toc-4314>٤٧ </span>- سارعوا - أيها الناس - إلى إجابة ما دعاكم إليه رسول خالقكم ومُربيكم من الإيمان والطاعة، من قبل أن تنتهى الحياة التى هى فرصة للعمل، ويأتى يوم الحساب الذى لا يردُّه الله بعد أن قضى به، ليس لكم - يومئذ - أى ملاذ يحميكم من العذاب ولا تجدوا من يدفع عنكم أو يقوى على حمايتكم.',NULL,'721',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4273,'<span data-type="title" id=toc-4315>٤٨ </span>- فإن أعرض المشركون عن إجابتك - أيها الرسول - فلا تحزن، فلست رقيباً عليهم فيما يفعلون، إنما كُلِّفْت البلاغ، وقد بيَّنت، وإن شأن الناس إذا منحناهم من لدنا سعة بطروا لأجلها، وإن تصبهم مصيبة بسبب معاصيهم فإنهم ينسون النعمة، ويجزعون لنزول البلاء كفراً وجحوداً.',NULL,'721',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4274,'<span data-type="title" id=toc-4316>٤٩ </span>- لله - وحده - ملك السموات والأرض خلقاً وتدبيراً وتصرفاً، يخلق ما يشاء خلقه، يهب لمن يشاء الإناث من الذرية، ويمنح من يشاء الذكور دون الإناث.',NULL,'721',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4275,'<span data-type="title" id=toc-4317>٥٠ </span>- ويتفضل - سبحانه - على من يشاء بالجمع بين الذكور والإناث، ويجعل من يشاء لا ولد له، إن الله محيط علمه بكل شئ، قدير على فعل كل ما يريد.',NULL,'721',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4276,'<span data-type="title" id=toc-4318>٥١ </span>- وما صح لأحد من البشر أن يُكلمه الله إلا وحياً بالإلقاء فى القلب إلهاماً، أو مناماً، أو بإسماع الكلام الإلهى دون أن يرى السامع من يكلمه، أو بإرسال ملك يرى صورته، ويسمع صوته، ليوحى بإذن الله ما يشاء، إن الله قاهر فلا يمانع، بالغ الحكمة فى تصرفاته وتدبيره.',NULL,'721',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4277,'<span data-type="title" id=toc-4319>٥٢ </span>- ومثل ما أوحينا إلى الرسل قبلك أوحينا إليك - أيها الرسول - هذا القرآن حياة للقلوب بأمرنا، ما كنت تعرف قبل الإيحاء إليك ما هو القرآن، ولا تعرف ما شرائع الإيمان، ولكن جعلنا القرآن نوراً عظيماً يرشد به من اختار الهدى، وإنك لتدعو بهذا القرآن إلى طريق مستقيم.',NULL,'722',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4278,'<span data-type="title" id=toc-4320>٥٣ </span>- صراط الله طريقه الذى له - وحده - ما فى السموات وما فى الأرض وهذا ما تدعو إليه - يا محمد - وما نزلت به رسالتك، ليعلم الناس أن إلى الله وحده - تصير كل الأمور.',NULL,'722',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4279,'<span data-type="title" id=toc-4322>١ </span>- حم: افتتحت هذه السورة ببعض الحروف الصوتية على طريقة القرآن الكريم فى افتتاح كثير من السور بمثل هذه الحروف.',NULL,'723',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4280,'<span data-type="title" id=toc-4323>٢ </span>- أقسم سبحانه - بالقرآن الموضح لما اشتمل عليه من العقائد والأحكام.',NULL,'723',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4281,'<span data-type="title" id=toc-4324>٣ </span>- إنا صيَّرنا الكتاب قرآناً عربياً، لكى تستطيعوا إدراك إعجازه وتدبر معانيه.',NULL,'723',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4282,'<span data-type="title" id=toc-4325>٤ </span>- وإن هذا القرآن الثابت فى اللوح المحفوظ عندنا، لرفيع القدر، ومُحكم النَّظْم، فى أعلى طبقات البلاغة.',NULL,'723',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4283,'<span data-type="title" id=toc-4326>٥ </span>- أَنُهْملكم فنمنع إنزال القرآن إليكم إعراضاً عنكم، لإسرافكم على أنفسكم فى الكفر، لا يكون ذلك، لاقتضاء الحكمة إلزامكم الحجة.',NULL,'723',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4284,'<span data-type="title" id=toc-4327>٦ </span>- وأرسلنا كثيراً من الأنبياء فى الأمم السابقة، فليس عجيباً إرسال رسول إليكم.',NULL,'723',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4285,'<span data-type="title" id=toc-4328>٧ </span>- وما يجيئهم من رسول يُذكِّرهم بالحق إلا استمروا على استهزائهم به.',NULL,'724',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4286,'<span data-type="title" id=toc-4329>٨ </span>- فأهلكنا المكذبين السابقين، وقد كانوا أشد من كفار مكة قوة ومنعة، فلا يغتر هؤلاء بسطوتهم، وسلف فى القرآن من قصص الأولين العجيب ما جعلهم عبرة لغيرهم، فاعتبروا - أيها المكذبون.',NULL,'724',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4287,'<span data-type="title" id=toc-4330>٩ </span>- وأقسم: إن سألت الكافرين - أيها الرسول - عمن خلق السموات والأرض؟ ليقولن - جواباً لذلك -: خلقهن الله، المتصف فى واقع الأمر بالعزة والعلم المحيط.',NULL,'724',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4288,'<span data-type="title" id=toc-4331>١٠ </span>- الذى جعل لكم الأرض مكاناً ممهداً، لتستطيعوا الإقامة فيها واستغلالها، وجعل لأجلكم فيها طرقات تسلكونها فى أسفاركم كى تصلوا إلى غاياتكم.',NULL,'724',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4289,'<span data-type="title" id=toc-4332>١١ </span>- والذى نزَّل من السماء ماء بقدر الحاجة، فأحيا به بلدة مجدبة لا نبات فيها، كمثل ذلك الإحياء للأرض وإخراج الزروع منها تبعثون من قبوركم للجزاء، فكيف تنكرونه؟ .',NULL,'724',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4290,'<span data-type="title" id=toc-4333>١٢ </span>- والذى خلق أصناف المخلوقات كلها، وسخَّر لكم من السفن والإبل ما تركبونه فى أسفاركم لقضاء حوائجكم.',NULL,'724',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4291,'<span data-type="title" id=toc-4334>١٣ </span>- كى تستقروا فوق ظهورها، ثم تذكروا نعمة خالقكم ومُربِّيكم فى تسخيرها لكم عند الاستقرار عليها، ولتقولوا - استعظاماً لتذليلها العجيب، واعترافاً بالعجز عن ضبطها، والتسلط عليها -: سبحان الذى ذلل لنا هذا، وما كنا لتذليلها مطيقين.',NULL,'724',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4292,'<span data-type="title" id=toc-4335>١٤ </span>- وإنا إلى خالقنا لراجعون بعد هذه الحياة، ليحاسب كُلٌّ على ما قدَّمت يداه.',NULL,'724',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4293,'<span data-type="title" id=toc-4336>١٥ </span>- وجعل المشركون لله - سبحانه - بعض خلقه ولداً ظنوه جزءاً منه، إن الإنسان بعمله هذا لمبالغ فى كفره، واضح فى جحوده.',NULL,'724',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4294,'<span data-type="title" id=toc-4337>١٦ </span>- بل أتزعمون أنه اتخذ لنفسه من خلقه البنات وآثركم بالذكور؟ {إن هذا لأمر عجيب حقاً.',NULL,'725',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4295,'<span data-type="title" id=toc-4338>١٧ </span>- نسبوا إليه ذلك، والحال أنه إذا بشر أحدهم بولادة أنثى له صار وجهه مسوداً غيظاً، وهو مملوء كآبة وحزناً لسوء ما بُشِّر به.',NULL,'725',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4296,'<span data-type="title" id=toc-4339>١٨ </span>- أيجترئون ويجعلون ولداً لله من شأنه النشأة فى الزينة، وهو فى الجدال وإقامة الحُجة عاجز لقصور بيانه؟} إن هذا لعجيب.',NULL,'725',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4297,'<span data-type="title" id=toc-4340>١٩ </span>- وسموا الملائكة المخلوقين للرحمن إناثاً، أرأوا خلقهم رؤية مشاهدة حتى يحكموا بذلك؟ لم يروه، سنسجل عليهم هذا الافتراء، ويُحاسبون عليه يوم القيامة.',NULL,'725',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4298,'<span data-type="title" id=toc-4341>٢٠ </span>- وقال المشركون: لو شاء الرحمن عدم عبادتنا لهؤلاء الشركاء ما عبدناهم، زاعمين أنه راض عن عبادتهم لهؤلاء الشركاء، ليس لديهم بما قالوا أى علم يستندون إليه، وما هم إلا واهمون، يقولون قولاً غير مستند إلى دليل.',NULL,'725',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4299,'<span data-type="title" id=toc-4342>٢١ </span>- هل أعطيناهم كتاباً من قبل القرآن يؤيد افتراءهم، فهم به متعلقون أشد التعلق؟! لم ننزل عليهم ذلك، فلا حُجة لهم من النقل.',NULL,'725',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4300,'<span data-type="title" id=toc-4343>٢٢ </span>- بل قال المشركون - حين فقدوا كل حُجة -: إنا وجدنا آباءنا على دين، وإننا على آثارهم سائرون.',NULL,'725',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4301,'<span data-type="title" id=toc-4344>٢٣ </span>- ومثل الحال الذى عليه هؤلاء حال الأمم السابقة، ما أرسلنا من قبلك فى قرية رسولا إلا قال المتنعمون فيها - وهم الذين أبطرتهم النعمة -: إننا وجدنا آباءنا على دين، وإنا على آثارهم سائرون، فالتقليد ضلال قديم.',NULL,'725',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4302,'<span data-type="title" id=toc-4345>٢٤ </span>- قال النذير: أتتبعون آباءكم ولو جئتكم بما هو أدخل فى الهداية مما وجدتم عليه آباءكم؟ قالوا - مجيبين لرسلهم يكذبون بالدين -: إننا بما أرسلتم به جاحدون.',NULL,'726',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4303,'<span data-type="title" id=toc-4346>٢٥ </span>- فعاقبنا المكذبين لرسلهم عقاباً شديداً فى الدنيا، فانظر - أيها المتأمل - كيف صار مآل المكذبين لكم مثلا عجيباً وعظة بالغة؟! .',NULL,'726',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4304,'<span data-type="title" id=toc-4347>٢٦ </span>- واذكر - يا محمد - للمكذبين قصة إبراهيم، إذ قال لأبيه وقومه: إننى برئ من عبادة آلهتكم الباطلة.',NULL,'726',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4305,'<span data-type="title" id=toc-4348>٢٧ </span>- لكنى أعبد الله الذى خلقنى، لأنه سبحانه - الذى سيرشدنى إلى طريق الحق.',NULL,'726',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4306,'<span data-type="title" id=toc-4349>٢٨ </span>- وصيَّرها - بإعلانها لهم - كلمة باقية فى ذريته - هى كلمة التوحيد - لعلهم يرجعون إليها، فيؤمنون بها.',NULL,'726',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4307,'<span data-type="title" id=toc-4350>٢٩ </span>- لم يحقق المشركون رجاء إبراهيم، ولم أعجل لهم العقوبة، بل متعت الحاضرين - لك يا محمد - ومتعت آباءهم من قبل بأنواع النعم، حتى نزل القرآن داعياً إلى الحق وجاءهم رسول مبين يدعوهم إليه.',NULL,'726',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4308,'<span data-type="title" id=toc-4351>٣٠ </span>- وحين نزل القرآن يرشدهم إلى التوحيد ضموا إلى شركهم تسميته سحراً وتمويهاً - استهزاء به - وأصروا على كفرهم.',NULL,'726',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4309,'<span data-type="title" id=toc-4352>٣١ </span>- وقال المشركون، استخفافاً بمحمد، واستعظاماً أن ينزل عليه القرآن: هلا نزل القرآن - الذى يزعم أنه وحى الله - على رجل عظيم من مكة أو الطائف؟ .',NULL,'726',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4310,'<span data-type="title" id=toc-4353>٣٢ </span>- ليس بأيدى المشركين مفاتيح الرسالة، حتى يجعلوها فى أصحاب الجاه، نحن تولينا تدبير معيشتهم لعجزهم عن ذلك، وفضلنا بعضهم على بعض فى الرزق والجاه، ليتخذ بعضهم من بعض أعواناً يسخرونهم فى قضاء حوائجهم، حتى يتساندوا فى طلب العيش وتنظيم الحياة والنبوة وما يتبعها من سعادة الدارين خير من أكبر مقامات الدنيا.',NULL,'726',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4311,'<span data-type="title" id=toc-4354>٣٣ </span>- ولولا كراهة أن يكفر الناس جميعاً إذا رأوا الكفار فى سعة من الرزق، لجعلنا لبيوت من يكفر بالرحمن سقفاً ومصاعد يرتقون عليها من الفضة، لهوان الدنيا علينا.',NULL,'727',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4312,'<span data-type="title" id=toc-4355>٣٤ </span>-، ٣٥ - ولجعلنا لبيوتهم أبواباً وسرراً من فضة ينعمون بها ويتكئون عليها، ولجعلنا لهم زينة من كل شئ، وما كل ذلك المتاع الذى وصفناه لك إلا متاعاً فانياً مقصوراً على الحياة الدنيا، وثواب الآخرة عند خالقك ومربيك مُعَد للذين اتقوا الشرك، واجتنبوا الموبقات.',NULL,'727',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4313,'<span data-type="title" id=toc-4356>٣٦ </span>- ومن يتعامى عن القرآن الذى أنزله الرحمن ذكرى للعاملين نجعل له شيطاناً يتسلط عليه، فهو معه - دائماً - يضله ويغويه.',NULL,'727',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4314,'<span data-type="title" id=toc-4357>٣٧ </span>- وإن شياطين المتعامين عن القرآن ليمنعونهم عن الطريق الذى يدعو إليه الرحمن، ويحسب المتعامون أنهم - باتباع قرنائهم - على الهدى.',NULL,'727',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4315,'<span data-type="title" id=toc-4358>٣٨ </span>- حتى إذا جاء من تعامى عن القرآن إلى الله يوم القيامة، ورأى عاقبة تعاميه، قال لقرينه - نادماً -: يا ليت بينى وبينك فى الدنيا بُعد المشرق عن المغرب، فبئس الصاحب كنت لى، حتى أوقعتنى فى الهاوية.',NULL,'727',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4316,'<span data-type="title" id=toc-4359>٣٩ </span>- ويقال لهم حينئذ - توبيخاً -: لن يخفف العذاب عنكم اليوم - إذ ظلمتم أنفسكم بالكفر - اشتراك شياطينكم معكم فيه، لأن كلا يعانى من العذاب ما يثقله.',NULL,'727',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4317,'<span data-type="title" id=toc-4360>٤٠ </span>- أتقدر - يا محمد - على هداية من استولى عليهم الضلال؟ أفأنت تسمع الصم عن الحق، والعمى عن الاعتبار، ومن كان فى علم الله أنه يموت على الضلال؟ لا تستطيع ذلك، لأنهم استقروا فى الكفر، فلم ينتفعوا بما يسمعونه ويرونه.',NULL,'727',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4318,'<span data-type="title" id=toc-4361>٤١ </span>- فإن قبضناك قبل أن نريك عذابهم، ونشفى بذلك صدرك وصدور قوم مؤمنين فإنا سننتقم منهم - لا محالة - فى الدنيا والآخرة.',NULL,'727',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4319,'<span data-type="title" id=toc-4362>٤٢ </span>- أو إذا أردت أن نريك العذاب الذى وعدناهم قبل وفاتك أريناك، لأننا مسيطرون عليهم بقدرتنا وقهرنا.',NULL,'727',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4320,'<span data-type="title" id=toc-4363>٤٣ </span>- إذا كان أحد هذين الأمرين واقعاً - لا محالة - فكن مستمسكاً بالقرآن الذى أوحيناه إليك، واثبت على العمل به، لأنك على طريق الحق القويم.',NULL,'728',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4321,'<span data-type="title" id=toc-4364>٤٤ </span>- وإن هذا القرآن لشرف عظيم لك - يا محمد - ولأمتك، لنزوله عليك بلغة العرب، وسوف تسألون يوم القيامة عن القيام بحقه وشكر نعمته.',NULL,'728',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4322,'<span data-type="title" id=toc-4365>٤٥ </span>- وانظر فى شرائع من أرسلنا من قبلك من رسلنا، أجاءت فيها دعوة الناس إلى عبادة غير الله؟ لم يجئ ذلك، فالعابدون لغير الله متوغلون فى الضلال بعبادتهم.',NULL,'728',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4323,'<span data-type="title" id=toc-4366>٤٦ </span>- ولقد أرسلنا موسى بالمعجزات الدالة على صدقه إلى فرعون وقومه، فقال: إنى رسول خالق العالمين ومُربِّيهم إليكم، فطالبوه بالمعجزات.',NULL,'728',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4324,'<span data-type="title" id=toc-4367>٤٧ </span>- فلما جاءهم بالمعجزات المؤيدة لرسالته قابلوه فور مجيئها بالضحك منها - سخرية واستهزاء - دون تأمل فيها.',NULL,'728',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4325,'<span data-type="title" id=toc-4368>٤٨ </span>- وكل معجزة من المعجزات التى توالت عليهم إذا نظر إليها قيل: هى أكبر من قرينتها وصاحبتها. وحينما أصروا على الطغيان أصبناهم بأنواع البلايا، ليرجعوا عن غيهم.',NULL,'728',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4326,'<span data-type="title" id=toc-4369>٤٩ </span>- وقالوا - مستغيثين بموسى حينما عمَّهم البلاء -: يا أيها الساحر - وهو العالم - ادع لنا ربك متوسلا بعهده عندك أن يكشف عنا العذاب، إنا - إذا كُشِف - لمهتدون.',NULL,'728',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4327,'<span data-type="title" id=toc-4370>٥٠ </span>- فلما كشف الله عنهم المصائب بدعاء موسى فاجأوه بنقض عهدهم بالإيمان.',NULL,'728',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4328,'<span data-type="title" id=toc-4371>٥١ </span>- ونادى فرعون فى قومه - معلناً قوته وتسلطه -: أليس لى - لا لغيرى - ملك مصر، وهذه الأنهار التى تشاهدونها تجرى من تحت قصرى؟ أعميتم عن مشاهدة ذلك، فلا تعقلون ما تمليه المشاهدة من قوتى وضعف موسى؟ وأراد بندائه تثبيتهم على طاعته.',NULL,'728',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4329,'<span data-type="title" id=toc-4372>٥٢ </span>- قال فرعون - مبالغة فى الطغيان -: بل أنا خير من هذا الذى هو ضعيف ذليل، ولا يُكاد يُبين دعواه بلسان فصيح.',NULL,'729',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4330,'<span data-type="title" id=toc-4373>٥٣ </span>- وقال أيضاً - محرضاً على تكذيب موسى -: فهلا ألقى عليه ربه أسْورة من ذهب ليلقى إليه بمقاليد الأمور، أو أعانه بملائكة يؤيدونه إن كان صادقاً فى دعواه الرسالة؟',NULL,'729',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4331,'<span data-type="title" id=toc-4374>٥٤ </span>- فاستفز فرعون قومه بالقول، وأثر فيهم هذا التمويه، فأطاعوه فى ضلاله، إنهم كانوا قوماً خارجين عن دين الله القويم.',NULL,'729',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4332,'<span data-type="title" id=toc-4375>٥٥ </span>- فلما أغضبونا أشد الغضب - بإفراطهم فى الفساد - انتقمنا منهم بإغراقهم أجمعين.',NULL,'729',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4333,'<span data-type="title" id=toc-4376>٥٦ </span>- فجعلنا فرعون وقومه قدوة للكافرين بعدهم فى استحقاق مثل عقابهم. وحديثاً عجيب الشأن يعتبر به جميع الناس.',NULL,'729',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4334,'<span data-type="title" id=toc-4377>٥٧ </span>- ولما ضرب الله عيسى ابن مريم مثلاً، فى كونه كآدم، خلقه من تراب، ثم قال له: كن فيكون، فهو عبد مخلوق، مُنعم عليه بالنبوة، لا تصح عبادته من دون الله. إذا قومك يعرضون ولا يعون.',NULL,'729',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4335,'<span data-type="title" id=toc-4378>٥٨ </span>- وقال الكافرون: أآلهتنا خير أم عيسى؟ فإذا كان هو فى النار فلنكن نحن وآلهتنا معه. ما ضرب الكفار هذا المثل لك إلا للجدل والغلبة فى القول لا لطلب الحق، بل هم قوم شداد فى الخصومة ممعنون فيها.',NULL,'729',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4336,'<span data-type="title" id=toc-4379>٥٩ </span>- ما عيسى إلا عبد أنعمنا عليه بالنبوة، وصيَّرناه عبرة عجيبة كالمثل - لخلقه بدون أب - لبنى إسرائيل يستدلون به على كمال قدرتنا.',NULL,'729',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4337,'<span data-type="title" id=toc-4380>٦٠ </span>- لو نشاء لحوَّلنا بعضكم - أيها الرجال - ملائكة يخلفونكم فى الأرض كما يخلفكم أولادكم، لتعرفوا أن الملائكة خاضعون لتصريف قدرة الله، فمن أين لهم استحقاق الألوهية؟',NULL,'729',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4338,'<span data-type="title" id=toc-4381>٦١ </span>- وأن عيسى بحدوثه بدون أب، وإبرائه الأكمه والأبرص لدليل على قيام الساعة، فلا تشكن فيها، واتبعوا هداى ورسولى. هذا الذى أدعوكم إليه، طريق مستقيم موصل إلى النجاة.',NULL,'730',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4339,'<span data-type="title" id=toc-4382>٦٢ </span>- ولا يمنعكم الشيطان عن اتباع طريقى المستقيم، إنه لكم عدو ظاهر العداوة.',NULL,'730',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4340,'<span data-type="title" id=toc-4383>٦٣ </span>- وحينما أرسل عيسى إلى بنى إسرائيل بالمعجزات الواضحات والآيات البينات قال لهم: قد جئتكم بشريعة حكيمة تدعوكم إلى التوحيد، وجئتكم لأبين لكم بعض الذى تختلفون فيه من أمر الدين لتجتمعوا على الحق، فاخشوا عذاب الله وأطيعون فيما أدعوكم إليه.',NULL,'730',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4341,'<span data-type="title" id=toc-4384>٦٤ </span>- إن الله - وحده - هو خالقى وخالقكم، فاعبدوه دون سواه، وحافظوا على شريعته، هذا الذى أدعوكم إليه طريق مستقيم موصل إلى النجاة.',NULL,'730',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4342,'<span data-type="title" id=toc-4385>٦٥ </span>- فاختلف الأحزاب من بين النصارى بعد عيسى فرقاً فى أمره، فهلاك للذين ظلموا بما قالوه فى عيسى مما كفروا به من عذاب شديد الإيلام يوم القيامة.',NULL,'730',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4343,'<span data-type="title" id=toc-4386>٦٦ </span>- ما ينتظر الكافرون شيئاً بعد إعراضهم عن الإيمان إلا إتيان الساعة بغتة، وهم غافلون عنها.',NULL,'730',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4344,'<span data-type="title" id=toc-4387>٦٧ </span>- الأصدقاء الذين جمعهم الباطل فى الدنيا يكون بعضهم لبعض عدواً يوم إتيان الساعة بغتة، وتنقطع كل محبة إلا محبة الذين خافوا - وهم فى الدنيا - عذاب الله، واجتمعوا فيها على طاعته.',NULL,'730',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4345,'<span data-type="title" id=toc-4388>٦٨ </span>- ينادى الله المتقين - تكريماً لهم - يا عبادى، لا تخافوا اليوم عذاباً، ولا أنتم تحزنون، فقد أمنتم العذاب، وضمن الله لكم الثواب.',NULL,'730',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4346,'<span data-type="title" id=toc-4389>٦٩ </span>- الذين صدَّقوا بآيات الله وأطاعوه، وكانوا له منقادين.',NULL,'730',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4347,'<span data-type="title" id=toc-4390>٧٠ </span>- يقال لهم يوم القيامة تشريفاً: ادخلوا الجنة أنتم مع أزواجكم، تُسرُّون فيها سروراً عظيماً، يظهر أثره على وجوهكم.',NULL,'730',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4348,'<span data-type="title" id=toc-4391>٧١ </span>- وبعد دخولهم الجنة يُطاف عليهم بأوان من ذهب وأكواب كذلك، وفيها ألوان الأطعمة وأنواع الأشربة، ولهم فى الجنة كل ما تشتهيه الأنفس وتقر به الأعين، ويقال لهم - إكمالا للسرور -: أنتم فى هذا النعيم مخلدون.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4349,'<span data-type="title" id=toc-4392>٧٢ </span>- ويقال - إتماماً للنعمة -: تلك هى الجنة التى ظفرتم بها بسبب ما قدَّمتم فى الدنيا من عمل الصالحات.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4350,'<span data-type="title" id=toc-4393>٧٣ </span>- لكم فيها فاكهة كثيرة الأنواع والألوان والطعوم، تتمتعون بالأكل منها.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4351,'<span data-type="title" id=toc-4394>٧٤ </span>- إن الذين أجرموا بالكفر فى عذاب جهنم خالدون.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4352,'<span data-type="title" id=toc-4395>٧٥ </span>- لا يُخفف العذاب عن هؤلاء المجرمين ولا ينقطع، وهم فيه يائسون من النجاة.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4353,'<span data-type="title" id=toc-4396>٧٦ </span>- وما ظلمنا هؤلاء المجرمين بهذا العذاب، ولكن كانوا هم الذين ظلموا أنفسهم باختيارهم الضلالة على الهدى.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4354,'<span data-type="title" id=toc-4397>٧٧ </span>- ونادى المجرمون - حين يئسوا من تخفيف العذاب الشديد - مالكاً خازن النار قائلين له: سل ربك أن يُميتنا لنستريح من أهوال جهنم. فقال لهم مالك: إنكم مقيمون فى العذاب دائماً.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4355,'<span data-type="title" id=toc-4398>٧٨ </span>- قال تعالى - رداً عليهم -: لقد جاءكم رسولنا - يا أهل مكة - بالدين الحق. فآمن به قليل، وأعرض عنه أكثركم. وهم لهذا الحق كارهون.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4356,'<span data-type="title" id=toc-4399>٧٩ </span>- بل أأحكم مشركو مكة أمرهم على تكذيب الرسول والتآمر على قتله؟ فإنا محكمون أمراً فى مجازاتهم وإظهارك عليهم.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4357,'<span data-type="title" id=toc-4400>٨٠ </span>- بل أيحسب هؤلاء المشركون أنا لا نسمع حديث أنفسهم بتدبير الكيد، وما يتكلمون به فيما بينهم من تكذيب الحق؟ بلى نسمعها، والحفظة من الملائكة عندهم يكتبون ذلك.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4358,'<span data-type="title" id=toc-4401>٨١ </span>- قل للمشركين: إن صح بالبرهان أنَّ للرحمن ولداً فأنا أول العابدين لهذا الولد، لكنه لم يصح بالحُجة أن ولداً للرحمن، لما يترتب عليه من مشابهة الخالق للمخلوقين، وهو - سبحانه - منزه عن مشابهة الحوادث من خلقه.',NULL,'731',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4359,'<span data-type="title" id=toc-4402>٨٢ </span>- تنزيهاً لخالق السموات والأرض خالق العرش، العظيم عمَّا يصفه به المشركون، مما لا يليق بألوهيته.',NULL,'732',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4360,'<span data-type="title" id=toc-4403>٨٣ </span>- فدعهم ينغمسوا فى أباطيلهم ويلعبوا فى دنياهم - بترك الجادة - غير ملتفت إليهم، حتى يجئ يوم القيامة الذى وعدوا به، لتجزى كل نفس بما كسبت.',NULL,'732',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4361,'<span data-type="title" id=toc-4404>٨٤ </span>- وهو الذى يُعبد فى السماء بحق، ويُعبد فى الأرض بحق، وهو - وحده - ذو الإحكام البالغ فى أفعاله وتدبيره، المحيط علمه بما كان وما يكون.',NULL,'732',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4362,'<span data-type="title" id=toc-4405>٨٥ </span>- وتعالى وتعظم الذى له - وحده - كمال التصرف فى السموات والأرض وفيما بينهما من مخلوقات الجو المشاهدة وغيرها، وله تدبير الأمر فى ذلك، وعنده - وحده - علم وقت القيامة، وإليه - وحده - ترجعون فى الآخرة للحساب.',NULL,'732',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4363,'<span data-type="title" id=toc-4406>٨٦ </span>- ولا يملك آلهتهم الذين يعبدونهم من غير الله الشفاعة لمن عبدوهم، لكن من شهدوا بالتوحيد - وهم يعتقدون أن الله ربهم حقاً - هم الذين يشفعون فيمن يشاء الله من المؤمنين.',NULL,'732',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4364,'<span data-type="title" id=toc-4407>٨٧ </span>- ولئن سألت - أيها الرسول - هؤلاء المشركين عمن خلقهم، فيقولن: خلقهن الله، فكيف يصرفون عن عبادته تعالى إلى عبادة غيره مع إقرارهم بأنه خالقهم؟! إن هذا لعجيب.',NULL,'732',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4365,'<span data-type="title" id=toc-4408>٨٨ </span>- أقسم بقول محمد صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ مستغيثاً داعياً: «يا رب» إن هؤلاء المعاندين قوم لا ينتظر منهم إيمان.',NULL,'732',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4366,'<span data-type="title" id=toc-4409>٨٩ </span>- فأعرض - أيها الرسول - عنهم - لشدة عنادهم - ودعهم، وقل لهم: سلام.',NULL,'732',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4367,'<span data-type="title" id=toc-4411>١ </span>- حم: ابتدأت هذه السورة ببعض الحروف الصوتية على طريقة القرآن الكريم فى افتتاح كثير من السور بمثل هذه الحروف.',NULL,'733',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4368,'<span data-type="title" id=toc-4412>٢ </span>- أقسم الله بالقرآن الكاشف عن الدين الحق، الموضح للناس ما يُصلح دنياهم وآخرتهم، إعلاماً برفعة قدره.',NULL,'733',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4369,'<span data-type="title" id=toc-4413>٣ </span>- إننا ابتدأنا إنزال القرآن فى ليلة وفيرة الخير، كثيرة البركات، لأن من شأننا الإنذار بإرسال الرسل وإنزال الكتب.',NULL,'733',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4370,'<span data-type="title" id=toc-4414>٤ </span>- فى هذه الليلة المباركة يُفَصَّل ويُبيَّن كل أمر محكم، والقرآن رأس الحكمة، والفيصل بين الحق والباطل، ولذا كان إنزاله فيها.',NULL,'733',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4371,'<span data-type="title" id=toc-4415>٥ </span>- أعنى بهذا الأمر أمراً عظيماً صادراً من عندنا كما اقتضاه تدبيرنا، لأن من شأننا إرسال الرسل بالكتب لتبليغ العباد.',NULL,'733',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4372,'<span data-type="title" id=toc-4416>٦ </span>- لأجل رحمة ربك بعباده أرسل رسله للناس يبلغونهم هَدْيه، لأنه - وحده - السميع لكل مسموع، المحيط علماً بكل معلوم.',NULL,'733',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4373,'<span data-type="title" id=toc-4417>٧ </span>- هو خالق السموات والأرض وما بينهما، إن كنتم موقنين بالحق، مذعنين له، مؤمنين بأنه المنزل للقرآن رحمة وهداية.',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4374,'<span data-type="title" id=toc-4418>٨ </span>- لا إله يستحق العبادة سواه، هو - وحده يحيى ويميت، وهو - وحده - خالقكم وخالق آبائكم الأولين.',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4375,'<span data-type="title" id=toc-4419>٩ </span>- بل الكفار فى شك من هذا الحق، يتبعون أهواءهم، وذلك شأن اللاهين اللاعبين، لا شأن أهل العلم واليقين.',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4376,'<span data-type="title" id=toc-4420>١٠ </span>- فانتظر - أيها الرسول - حينما ينزل بهم القحط، فيصابون بالهزال وضعف البصر، فيرى الرجل بين السماء والأرض دخاناً واضحاً! .',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4377,'<span data-type="title" id=toc-4421>١١ </span>- يحيط هذا الدخان بالمكذبين الذين أصابهم الجدب، فيقولون لشدة الهول: هذا عذاب شديد الإيلام.',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4378,'<span data-type="title" id=toc-4422>١٢ </span>- كما يقولون استغاثة بالله: إننا سنؤمن بعد أن تكشف عنا عذاب الجوع والحرمان.',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4379,'<span data-type="title" id=toc-4423>١٣ </span>- كيف يتعظ هؤلاء، ويوفون بما وعدوا من الإيمان عند كشف العذاب، وقد جاءهم رسول واضح الرسالة بالمعجزات الدالة على صدقه، وذلك أعظم موجبات الاتعاظ؟ .',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4380,'<span data-type="title" id=toc-4424>١٤ </span>- ثم أعرضوا عن التصديق بالرسول المؤيد بالمعجزات الواضحة، وقالوا - كذباً وافتراءً -: تارة يعلمه البشر، وقالوا تارة أخرى: اختلط عقله.',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4381,'<span data-type="title" id=toc-4425>١٥ </span>- فرد الله عليهم: إنا سنرفع عنكم العذاب زمن الدنيا، وهو قليل، وإنكم عائدون - لا محالة - إلى ما كنتم عليه.',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4382,'<span data-type="title" id=toc-4426>١٦ </span>- اذكر - أيها الرسول - يوم نأخذهم الأخذة الكبرى بعنف وقوة، إننا - بهذا الأخذ - منتقمون منهم.',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4383,'<span data-type="title" id=toc-4427>١٧ </span>- ولقد امتحنا قبل كفار مكة قوم فرعون بالدعوة إلى الإيمان، وجاءهم موسى رسول كريم على الله، فكفروا به عناداً، وكذلك شأن هؤلاء المشركين.',NULL,'734',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4384,'<span data-type="title" id=toc-4428>١٨ </span>- قال لهم الرسول الكريم: أدَّوا إلىَّ يا عباد الله ما هو واجب عليكم من قبول دعوتى، لأنى لكم رسول إليكم خاصة، أمين على رسالتى.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4385,'<span data-type="title" id=toc-4429>١٩ </span>- ولا تتكبروا على الله بتكذيب رسوله، لأنى آتيكم بمعجزة واضحة تبين صدق نبوتى ورسالتى.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4386,'<span data-type="title" id=toc-4430>٢٠ </span>- وإنى اعتصمت بخالقى وخالقكم من أن تتمكنوا من قتلى رجماً.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4387,'<span data-type="title" id=toc-4431>٢١ </span>- وإن لم تصدقوا بى فكونوا بمعزل منى، ولا تُؤْذُونى.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4388,'<span data-type="title" id=toc-4432>٢٢ </span>- فدعا موسى ربه - شاكياً قومه حين يئس من إيمانهم - بأن هؤلاء قوم تناهى أمرهم فى الكفر، فافعل بهم ما يستحقون.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4389,'<span data-type="title" id=toc-4433>٢٣ </span>- فسِر بالمؤمنين ليلا فى خفية، حتى لا يدركوكم، لأن فرعون وجنوده سيتبعونكم، إذا علموا، للإيقاع بكم.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4390,'<span data-type="title" id=toc-4434>٢٤ </span>- واترك البحر ساكناً على هيئته بعد ضربه بالعصا، ليدخله المنكرون، فإنهم مغرقون لا محالة.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4391,'<span data-type="title" id=toc-4435>٢٥ </span>- تركوا بعد إغراقهم كثيراً من الجنات الناضرة والعيون الجارية.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4392,'<span data-type="title" id=toc-4436>٢٦ </span>- والزورع المتنوعة والمنازل الحسنة.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4393,'<span data-type="title" id=toc-4437>٢٧ </span>- وعيشة مترفة نضرة كانوا فيها متنعمين.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4394,'<span data-type="title" id=toc-4438>٢٨ </span>- مثل ذلك العقاب يعاقب الله من خالف أمره، وخرج على طاعته، ويحول ما كان فيه من النعم إلى قوم آخرين ليسوا منهم فى شئ من قرابة ولا دين.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4395,'<span data-type="title" id=toc-4439>٢٩ </span>- فما حزنت عليهم السماء والأرض عندما أخذهم العذاب لهوان شأنهم، ولم يُنظَروا لتوبة، ولم يمهلوا لتدارك تقصيرهم، احتقاراً لهم.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4396,'<span data-type="title" id=toc-4440>٣٠ </span>- ولقد نجَّى الله بنى إسرائيل من العذاب المذل لهم.',NULL,'735',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4397,'<span data-type="title" id=toc-4441>٣١ </span>- نجاهم من فرعون، إن فرعون كان مستعلياً على قومه، مسرفاً فى الشر والطغيان.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4398,'<span data-type="title" id=toc-4442>٣٢ </span>- أقسم: لقد اخترنا بنى إسرائيل على علم منا بأحقيتهم بالاختيار على عالمى زمانهم، فبعثنا فيهم أنبياء كثيرين مع علمنا بحالهم.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4399,'<span data-type="title" id=toc-4443>٣٣ </span>- وآتاهم الله على يد موسى من الدلائل ما فيه اختبار ظاهر لهم.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4400,'<span data-type="title" id=toc-4444>٣٤ </span>-، ٣٥ - إن هؤلاء المكذبين بالبعث ليقولون: ما الموتة إلا موتتنا الأولى فى الدنيا وما نحن بعدها بمبعوثين.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4401,'<span data-type="title" id=toc-4445>٣٦ </span>- ويقولون لرسول الله والمؤمنين: إن كنتم صادقين فى دعواكم أن ربكم يحيى الموتى للحساب فى الآخرة، فعجلوا لنا إحياء من مات من آبائنا بسؤالكم ربكم ذلك.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4402,'<span data-type="title" id=toc-4446>٣٧ </span>- أكفار مكة خير فى القوة والمنعة والسلطان وسائر أمور الدنيا أم قوم تبع ومن سبقهم؟ ليس مشركو قومك - يا محمد - أقوى منهم، وقد أهلكناهم فى الدنيا بكفرهم وأجرامهم، فليعتبروا بهم.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4403,'<span data-type="title" id=toc-4447>٣٨ </span>- وما خلق الله السموات والأرض وما بينهما بحكمة ولحكمة.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4404,'<span data-type="title" id=toc-4448>٣٩ </span>- ما خلقناهما إلا خلقاً منوطاً بالحكمة على نظام ثابت يدل على وجود الله ووحدانيته وقدرته، ولكن أكثر هؤلاء فى غفلة عمياء، لا يعلمون هذه الدلالة.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4405,'<span data-type="title" id=toc-4449>٤٠ </span>- إن يوم الحكم بين المحق والمبطل وقت موعدهم أجمعين.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4406,'<span data-type="title" id=toc-4450>٤١ </span>- يوم لا يدفع أى قريب عن أى قريب، ولا أى حليف عن أى حليف شيئاً قليلاً من العذاب، ولا هم ينصرون عند الله بأنفسهم.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4407,'<span data-type="title" id=toc-4451>٤٢ </span>- لكن الذين رحمهم الله من المؤمنين يعفو الله عنهم، ويأذن لهم بالشفاعة، إنه الغالب على كل شئ، الرحيم بعباده المؤمنين.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4408,'<span data-type="title" id=toc-4452>٤٣ </span>-، ٤٤ - إن شجرة الزقوم - المعروفة بقبح منظرها وخبث طعمها وريحها - طعام الفاجر كثير الآثام.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4409,'<span data-type="title" id=toc-4453>٤٥ </span>-، ٤٦ - طعامها كسائل المعدن الذى صهرته الحرارة، يغلى فى البطون كغلى الماء الذى بلغ النهاية فى غليانه.',NULL,'736',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4410,'<span data-type="title" id=toc-4454>٤٧ </span>- خذوا - يا زبانية جهنم - هذا الفاجر الأثيم فقودوه بعنف وغلظة إلى وسط جهنم.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4411,'<span data-type="title" id=toc-4455>٤٨ </span>- ثم صبوا فوق رأسه الماء الشديد الحرارة، زيادة فى تعذيبه وإيلامه.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4412,'<span data-type="title" id=toc-4456>٤٩ </span>- يقال له - استهزاء وتهكماً به - ذق العذاب الشديد، إنك أنت العزيز فى قومك، الكريم فى حسبك.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4413,'<span data-type="title" id=toc-4457>٥٠ </span>- إن هذا العذاب الذى لمستموه حقيقة واقعة هو ما كنتم تخاصمون بشأنه فى الدنيا، وتشكُّون فى وقوعه.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4414,'<span data-type="title" id=toc-4458>٥١ </span>- إن الذين وقوا أنفسهم من المعاصى بالتزام طاعة الله فى مكان عظيم يأمنون فيه على أنفسهم.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4415,'<span data-type="title" id=toc-4459>٥٢ </span>- فى جنات ينعمون فيها، وعيون من الماء تجرى من تحتها، إكراماً لهم بإعظام نعيمهم.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4416,'<span data-type="title" id=toc-4460>٥٣ </span>- يلبسون ما رقَّ وما غلظ من الحرير زيادة فى زينتهم، متقابلين فى مجالسهم، ليتم لهم الأنس.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4417,'<span data-type="title" id=toc-4461>٥٤ </span>- ومع هذا الجزاء زوَّجناهم فى الجنة بحور عين، يحار فيهن الطرف لفرط حسنهن وجمالهن وسعة عيونهن.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4418,'<span data-type="title" id=toc-4462>٥٥ </span>- يطلبون فى الجنة كل فاكهة يشتهونها، آمنين من الغصص والزوال والحرمان.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4419,'<span data-type="title" id=toc-4463>٥٦ </span>- لا يذوقون فى الجنة الموت بعد الموتة الأولى التى ذاقوها فى الدنيا عند انقضاء آجالهم، وحفظهم ربهم من عذاب النار.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4420,'<span data-type="title" id=toc-4464>٥٧ </span>- حفظوا من العذاب - فضلا وإحساناً من خالقك - ذلك الحفظ من العذاب ودخول الجنة هو غاية الفوز العظيم.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4421,'<span data-type="title" id=toc-4465>٥٨ </span>- فإنما سهَّلنا عليك تلاوة القرآن وتبليغه مُنَزَّلا بلغتك ولغتهم كى يتعظوا فيؤمنوا به ويعملوا بما فيه.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4422,'<span data-type="title" id=toc-4466>٥٩ </span>- فانتظر ما يحل بهم، إنهم منتظرون ما يحل بك وبدعوتك من الدوائر.',NULL,'737',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4423,'<span data-type="title" id=toc-4468>١ </span>- حم: حرفان من الحروف الصوتية ابتدأت بهما هذه السورة على طريقة القرآن فى افتتاح بعض سوره بمثل هذه الحروف للإشارة إلى عجز المشركين عن الإتيان بمثله مع أنه مؤلف من الحروف التى يستعملونها فى كلامهم.',NULL,'738',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4424,'<span data-type="title" id=toc-4469>٢ </span>- تنزيل القرآن من الله القوى المنيع، الحكيم فى تدبيره وصنعه.',NULL,'738',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4425,'<span data-type="title" id=toc-4470>٣ </span>- إن فى خلق السموات والأرض من بديع صنع الله لدلالات قوية على ألوهيته ووحدانيته، يؤمن بها المصدقون بالله بفطرهم السليمة.',NULL,'738',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4426,'<span data-type="title" id=toc-4471>٤ </span>- وفى خلق الله لكم - أيها الناس - على ما أنتم عليه من حسن الصورة وبديع الصنع، وما يفرق وينشر من الدواب على اختلاف الصور والمنافع لدلالات قوية واضحة لقوم يَسْتَيْقِنونَ بأمورهم بالتدبر والتفكر.',NULL,'738',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4427,'<span data-type="title" id=toc-4472>٥ </span>- وفى اختلاف الليل والنهار فى الطول والقصر والنور والظلام مع تعاقبهما على نظام ثابت، وفيما أنزل الله من السماء من مطر فأحيا به الأرض بالإنبات بعد موتها بالجدب، وتصريف الرياح إلى جهات متعددة مع اختلافها برودة وحرارة وقوة وضعفاً، علامات واضحة على كمال قدرة الله لقوم فكروا بعقولهم فخلص يقينهم.',NULL,'739',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4428,'<span data-type="title" id=toc-4473>٦ </span>- تلك آيات الله الكونية التى أقامها الله للناس، نقرؤها عليك فى القرآن على لسان جبريل مشتملة على الحق، فإذا لم يؤمنوا فبأى حديث بعد حديث الله - وهو القرآن - وآياته يصدقون؟ .',NULL,'739',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4429,'<span data-type="title" id=toc-4474>٧ </span>- هلاك شديد لكل من افترى على الله أقبح الأكاذيب ولمن كثرت آثامه بذلك.',NULL,'739',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4430,'<span data-type="title" id=toc-4475>٨ </span>- يسمع هذا المفترى آيات الله تتلى ناطقة بالحق، ثم يُصر على الكفر متكبراً عن الإيمان، شأنه شأن من لم يسمع الآيات، فبشره أيها النبى - تهكماً - بعذاب أليم لإصراره على عمل ما يوصل إليه.',NULL,'739',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4431,'<span data-type="title" id=toc-4476>٩ </span>- وإذا علم هذا العنيد أى شئ من آيات الله، جعل آيات الله كلها مادة لسخريته واستهزائه، أولئك الأفاكون الآثمون لهم عذاب يذل كبرياءهم.',NULL,'739',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4432,'<span data-type="title" id=toc-4477>١٠ </span>- من ورائهم جهنم تنتظرهم، ولا يدفع عنهم ما كسبوا فى الدنيا شيئاً من عذابها، ولا الآلهة التى اتخذوها من دون الله نصراء تدفع شيئاً من عذابها، ولهم عذاب عظيم فى هوله وشدته.',NULL,'739',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4433,'<span data-type="title" id=toc-4478>١١ </span>- هذا القرآن دليل كامل على أن الحق من عند الله، والذين جحدوا ما اشتمل عليه من حُجج خالقهم ومربيهم، لهم عذاب من أشد أنواع العذاب.',NULL,'739',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4434,'<span data-type="title" id=toc-4479>١٢ </span>- الله - وحده - هو الذى ذلل لكم البحر لتسير السفن فيه بإذنه وقدرته حاملة لكم ولحاجاتكم، ولتطلبوا من فضل الله من خيرات البحر باستفادة علم وتجارة وجهاد وهداية وصيد، واستخراج آنية، ولعلكم تشكرون نعمه بإخلاص الدين لله.',NULL,'739',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4435,'<span data-type="title" id=toc-4480>١٣ </span>- وذلل لكم جميع ما فى السموات من نجوم مضيئة وكواكب، وكل ما فى الأرض من زرع وضرع وخصب وماء ونار وهواء وصحراء جميعاً منه - تعالى - ليوفر لكم منافع الحياة. إن فيما ذكر من نعم لآيات دالة على قدرته لقوم يتدبرون الآيات.',NULL,'740',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4436,'<span data-type="title" id=toc-4481>١٤ </span>- قل - أيها الرسول - للذين صدَّقوا بالله واتبعوك يصفحوا عن الإيذاء الذى يصيبهم من الذين لا يتوقعون أيام الله التى يجزى فيها أقواماً بالخير وأقواماً بالشر حسبما كانوا.',NULL,'740',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4437,'<span data-type="title" id=toc-4482>١٥ </span>- من عمل صالحاً فلنفسه الأجر والثواب، ومن أساء عمله فعلى نفسه وزر عمله، ثم إلى خالقكم ترجعون للجزاء.',NULL,'740',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4438,'<span data-type="title" id=toc-4483>١٦ </span>- أقسم لقد أعطينا بنى إسرائيل التوراة والحكم بما فيها، والنبوة الملهمة من قبل الله، ورزقناهم من الخيرات المتنوعة، وفضلناهم بكثير من النعم على الخلق أجمعين.',NULL,'740',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4439,'<span data-type="title" id=toc-4484>١٧ </span>- وأعطيناهم دلائل واضحة من أمر دينهم فما وقع بينهم اختلاف إلا من بعد ما جاءهم العلم بحقيقة الدين وأحكامه عداوة وحسداً فيما بينهم، إن ربك يفصل بين المختلفين يوم القيامة فى الأمر الذى كانوا فيه يختلفون.',NULL,'740',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4440,'<span data-type="title" id=toc-4485>١٨ </span>- ثم جعلناك - يا محمد - بعد اختلاف أهل الكتاب مبعوثاً على منهاج واضح من أمر الدين الذى شرعناه لك ولمن قبلك من رسلنا، فاتبع شريعتك الحقة الثابتة بالحُجج والدلائل، ولا تتبع أهواء الذين لا يعلمون طريق الحق.',NULL,'740',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4441,'<span data-type="title" id=toc-4486>١٩ </span>- إن المبطلين الطامعين فى اتباعك لهم لن يدفعوا عنك من عذاب الله شيئاً أن اتبعتهم، وإن المتجاوزين لحدود الله بعضهم أنصار بعض على الباطل، والله ناصر الذين يخشونه فلا ينالهم ظلم الظالمين.',NULL,'740',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4442,'<span data-type="title" id=toc-4487>٢٠ </span>- هذا القرآن - المنزل عليك - دلائل للناس تبصرهم بالدين الحق، وهدى يرشدهم إلى مسالك الخير، ونعمة لقوم يستيقنون بثواب الله وعقابه.',NULL,'740',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4443,'<span data-type="title" id=toc-4488>٢١ </span>- بل حسب الذين اكتسبوا ما يسوء من الكفر والمعاصى أن نجعلهم كالذين آمنوا بالله وعملوا الصالحات من الأعمال، فنسوِّى بين الفريقين فى الحياة ونسِّوى بين الفريقين فى الممات؟ ، بئس ما يقضون إذا أحسوا أنهم كالمؤمنين.',NULL,'741',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4444,'<span data-type="title" id=toc-4489>٢٢ </span>- وخلق الله السموات والأرض متلبساً بالحكمة والنظام، لتظهر دلائل أُلوهيته وقدرته، ولتجزى كل نفس بما كسبت من خير أو شر، وهم لا ينقصون شيئاً من جزائهم.',NULL,'741',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4445,'<span data-type="title" id=toc-4490>٢٣ </span>- أنظرت فرأيت - أيها الرسول - مَنْ اتخذ هواه معبوداً له فخضع له وأطاعه، وضل عن سبيل الحق على علم منه بهذا السبيل، وأغلق سمعه فلا يقبل وعظاً، وقلبه فلا يعتقد حقاً، وجعل على بصره غطاء فلا يبصر عبرة، فمن يستطيع هدايته بعد الله؟ أفلا تتعظون بمثل هذه الحالات؟ .',NULL,'741',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4446,'<span data-type="title" id=toc-4491>٢٤ </span>- وقال المنكرون للبعث: ما الحياة إلا حياتنا الدنيا التى نحن فيها نحيا ونموت، وليس وراء ذلك حياة بعد الموت، وما يُهلكنا إلا مرور الزمان. وما يقولون ذلك عن علم ويقين ولكن عن ظن وتخمين.',NULL,'741',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4447,'<span data-type="title" id=toc-4492>٢٥ </span>- وإذا قُرِئت عليهم آيات الله واضحات الدلالة على قدرته على البعث ما كان حجتهم إلا مقالتهم - فراراً من الحق -: أحيوا آباءنا إن كنتم صادقين فى دعوى وقوع البعث.',NULL,'741',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4448,'<span data-type="title" id=toc-4493>٢٦ </span>- قل لهم - يا محمد - الله يحييكم فى الدنيا من العدم ثم يميتكم فيها عند انقضاء آجالكم، ثم يجمعكم فى يوم القيامة لا شك فى هذا الجمع، ولكن أكثر الناس لا يعلمون قدرة الله على البعث، لإعراضهم عن التأمل فى الدلائل، والقادر على ذلك قادر على الإتيان بآبائكم.',NULL,'741',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4449,'<span data-type="title" id=toc-4494>٢٧ </span>- ولله - وحده - ملك السموات والأرض خلقاً وملكاً وتدبيراً، وحين تقوم الساعة - يوم قيامها - يخسر الذين اتبعوا الباطل.',NULL,'741',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4450,'<span data-type="title" id=toc-4495>٢٨ </span>- وترى يوم تقوم الساعة - أيها المخاطب - أهل كل دين جالسين على الركب من هول الموقف متحفزين لإجابة النداء، كل أمة تُدعى إلى سجل أعمالها، ويُقال لهم: اليوم تستوفون جزاء ما كنتم تعملون فى الدنيا.',NULL,'742',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4451,'<span data-type="title" id=toc-4496>٢٩ </span>- ويقال لهم: هذا كتابنا الذى سجلنا فيه أعمالكم وأخذتموه بأيديكم، ينطق عليكم بما عملتم شهادة صدق، إنا كنا نستكتب الملائكة أعمالكم لنحاسبكم على ما فرط منكم.',NULL,'742',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4452,'<span data-type="title" id=toc-4497>٣٠ </span>- فأما الذين آمنوا وعملوا الأعمال الصالحة فيدخلهم ربهم فى جنته، ذلك الجزاء هو الفوز البين الواضح.',NULL,'742',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4453,'<span data-type="title" id=toc-4498>٣١ </span>- وأما الذين كفروا بالله ورسله فيقال لهم - توبيخاً -: ألم تأتكم رسلى، أفلم تكن آياتى تتلى عليكم فتعاليتم عن قول الحق وكنتم قوماً كافرين؟ .',NULL,'742',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4454,'<span data-type="title" id=toc-4499>٣٢ </span>- وإذا قال لكم رسول الله: - أيها المنكرون للبعث - إن وعد الله بالجزاء حق ثابت، والساعة لا شك فى مجيئها، قلتم: ما نعلم أى شئ عن الساعة ما حقيقتها؟ ما نحن إلا نظن مجئ الساعة ظنا، وما نحن بموقنين أنها آتية.',NULL,'742',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4455,'<span data-type="title" id=toc-4500>٣٣ </span>- وظهر لهؤلاء الكفار قبائح أعمالهم، ونزل بهم جزاء استهزائهم بآيات الله.',NULL,'742',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4456,'<span data-type="title" id=toc-4501>٣٤ </span>- وقيل لهؤلاء المشركين - توبيخاً -: اليوم نترككم فى العذاب كما تركتم الاستعداد للقاء ربكم فى هذا اليوم بالطاعة والعمل الصالح، ومقركم النار، وليس لكم من ناصرين ينقذونكم من عذابها.',NULL,'742',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4457,'<span data-type="title" id=toc-4502>٣٥ </span>- ذلكم العذاب الذى نزل بكم بسبب كفركم واستهزائكم بآيات الله، وخدعتكم الحياة الدنيا بزخرفها، فاليوم لا يستطيع أحد إخراج هؤلاء من النار، ولا يؤذن لهم بالاعتذار.',NULL,'743',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4458,'<span data-type="title" id=toc-4503>٣٦ </span>- فلله - وحده - الثناء، خالق السموات والأرض وخالق جميع الخلق، فإن هذه الربوبية العامة توجب الحمد على كل نعمة.',NULL,'743',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4459,'<span data-type="title" id=toc-4504>٣٧ </span>- وله - وحده سبحانه - العظمة والسلطان فى السموات والأرض، وهو العزيز الذى لا يغلب، ذو الحكمة الذى لا يخطئ فى أحكامه.',NULL,'743',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4460,'<span data-type="title" id=toc-4506>١ </span>- افتتحت هذه السورة ببعض الحروف على طريقة القرآن الكريم فى افتتاح طائفة من سوره بالحروف.',NULL,'744',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4461,'<span data-type="title" id=toc-4507>٢ </span>- تنزيل القرآن من عند الله الغالب على كل شئ، ذى الحكمة فى كل ما يفعل.',NULL,'744',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4462,'<span data-type="title" id=toc-4508>٣ </span>- ما خلقنا السموات والأرض وما بينهما إلا على نواميس ثابتة، لغايات تقتضيها الحكمة، وإلى أمد معين تفنى بعده، والذين جحدوا بهذه الحقيقة معرضون عما أنذروا به من خلق جديد يوم يبعث الناس للجزاء.',NULL,'744',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4463,'<span data-type="title" id=toc-4509>٤ </span>- قل للذين يدعون غير الله: أخبرونى عن حال ما تدعون من دون الله؟ هل خلقوا شيئا من الأرض أم كانوا شركاء لله فى خلق السموات؟ ائتونى بكتاب من عند الله أو أثر من علم الأولين تستندون إليه فى دعواكم إن كنتم صادقين.',NULL,'744',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4464,'<span data-type="title" id=toc-4510>٥ </span>- ومن أكثر ضلالاً ممن يدعو من دون الله معبودات لا تستجيب له ما بقيت الدنيا! وهم مع ذلك غافلون عن دعائهم، غير شاعرين به.',NULL,'744',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4465,'<span data-type="title" id=toc-4511>٦ </span>- وإذا جمع الناس للحساب يوم القيامة كان هؤلاء المعبودون أعداء لمن عبدوهم، يَتَبَرَّأُونَ منهم، ويكذبونهم فيما زعموا من استحقاقهم لعبادتهم.',NULL,'745',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4466,'<span data-type="title" id=toc-4512>٧ </span>- وإذا تُتلى على المشركين آياتنا واضحات لكفرهم وعنادهم - عن تلك الآيات دون تأمل قالوا: هذا سحر ظاهر.',NULL,'745',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4467,'<span data-type="title" id=toc-4513>٨ </span>- بل أيقول هؤلاء الكافرون: اختلق محمد القرآن وأضافه إلى الله؟ ، قل رداً عليهم: إن افتريته عاجلنى الله بعقوبته، فلا تستطيعون أن تدفعوا عنى من عذابه شيئاً هو - وحده - أعلم بما تخوضون فيه من الطعن فى آياته، كفى به شهيداً لى بالصدق وشهيداً عليكم بالتكذيب، وهو - وحده - واسع المغفرة لمن تاب، عظيم الرحمة، يمهل العصاة ليتداركوا.',NULL,'745',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4468,'<span data-type="title" id=toc-4514>٩ </span>- قل لهم: ما كنت أول رسول من عند الله فتنكروا رسالتى، ولست أعلم ما يفعل الله بى ولا بكم، ما أتبع فيما أقول أو أفعل إلا الذى يوحيه الله إلى، وما أنا إلا منذر بيِّن الإنذار.',NULL,'745',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4469,'<span data-type="title" id=toc-4515>١٠ </span>- قل: أخبرونى إن كان القرآن من عند الله وكفرتم به، وشهد شاهد من بنى إسرائيل على نزول مثله من عند الله، فآمن به واستكبرتم. ألا تكونون حينئذ أضل الناس وأظلمهم لأنفسهم! إن الله لا يوفق إلى الهدى من ظلم نفس واستكبر عن الحق.',NULL,'745',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4470,'<span data-type="title" id=toc-4516>١١ </span>- وقال الذين كفروا فى شأن الذين آمنوا استهزاء بهم واستعلاء عليهم: لو كان ما جاء به محمد خيراً ما سبقنا هؤلاء إلى الإيمان به، فإنا نحن أصحاب السيادة والعقول الراجحة، ولما لم يهتدوا به يطعنون فيه، وقالوا: هذا كذب قديم من أساطير الأولين.',NULL,'745',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4471,'<span data-type="title" id=toc-4517>١٢ </span>- ومن قبل القرآن أنزل الله التوراة قدوة ورحمة للعاملين بها، وهذا القرآن الذى يكذبونه مصدق لما قبله من الكتب، أنزله الله بلسان عربى ليكون إنذاراً متجدداً للذين ظلموا، وبُشرى للذين استقاموا على الطريقة.',NULL,'746',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4472,'<span data-type="title" id=toc-4518>١٣ </span>- إن الذين قالوا: ربنا الله - وحده - ثم أحسنوا العمل، فلا خوف عليهم من نزول مكروه، ولا هم يحزنون لفوات مطلوب.',NULL,'746',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4473,'<span data-type="title" id=toc-4519>١٤ </span>- أولئك الموصوفون بالتوحيد والاستقامة هم المختصون بدخول الجنة خالدين فيها أعطاهم الله ذلك جزاء بما كانوا يعملون من الصالحات.',NULL,'746',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4474,'<span data-type="title" id=toc-4520>١٥ </span>- ووصينا الإنسان بوالديه أن يحسن إليهما إحساناً عظيماً، حملته أمه حملاً ذا مشقة، ووضعته وضعاً ذا مشقة، ومدة حمله وفصاله ثلاثون شهراً قاست فيها صنوف الآلام، حتى إذا بلغ كمال قوته وعقله، وبلغ أربعين سنة، قال: رب ألهمنى شكر نعمتك التى أنعمت علىَّ وعلى والدى، وألهمنى أن أعمل عملاً صالحاً ترضاه، واجعل الصلاح سارياً فى ذريتى، إنى تبت إليك من كل ذنب، وإنى من الذين أسلموا أنفسهم إليك.',NULL,'746',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4475,'<span data-type="title" id=toc-4521>١٦ </span>- أولئك الموصوفون بتلك المحامد هم الذين نتقبل عنهم أعمالهم الحسنة، ونعفو عن سيئاتهم فى عداد أصحاب الجنة، محققين لهم وعد الصدق الذى كانوا يوعدون به فى الدنيا.',NULL,'746',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4476,'<span data-type="title" id=toc-4522>١٧ </span>- والذى قال لوالديه حين دعواه إلى الإيمان بالبعث متضجراً منهما ومنكراً عليهما: أف لكما، أتعداننى بالخروج من القبر وقد مضت الأمم من قبلى ولم يبعث من القبور أحد؟ وأبواه يستغيثان الله استعظاماً لجرمه، ويقولان له حثَّا على الإيمان: هلكت إن لم تؤمن، إن وعد الله بالبعث حق، فيقول - إمعاناً فى التكذيب -: ما هذا الذى تقولانه إلا خرافات سطرها الأولون.',NULL,'746',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4477,'<span data-type="title" id=toc-4523>١٨ </span>- أولئك القائلون ذلك هم الذين حق عليهم وقوع العذاب فى عداد أمم قد خلت من قبلهم من الجن والإنس، لأنهم كانوا خاسرين.',NULL,'746',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4478,'<span data-type="title" id=toc-4524>١٩ </span>- ولكل من المسلمين والكفار منازل ملائمة لما عملوا ليظهر عدل الله فيهم، وليوفيهم جزاء أعمالهم وهم لا يظلمون، لاستحقاقهم ما يجزون به.',NULL,'747',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4479,'<span data-type="title" id=toc-4525>٢٠ </span>- ويوم يوقف الذين كفروا على النار يقال لهم: أذهبتم نصيبكم من الطيبات فى حياتكم الدنيا، واستمتعتم بها فاليوم تجزون عذاب الهون بما كنتم عليه فى الدنيا من الاستكبار فى الأرض بغير الحق، والخروج عن طاعة الله.',NULL,'747',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4480,'<span data-type="title" id=toc-4526>٢١ </span>- واذكر هوداً أخا عاد إذ حذَّر قومه المقيمين بالأحقاف - وقد مضت الرسل قبله وبعده بمثل إنذاره - قائلاً لهم: لا تعبدوا إلا الله إنى أخاف عليكم عذاب يوم عظيم الهول.',NULL,'747',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4481,'<span data-type="title" id=toc-4527>٢٢ </span>- قال قوم هود إنكاراً عليه: أجئتنا لتصرفنا عن عبادة آلهتنا؟! فأتنا بما تعدنا من العذاب إن كنت من الصادقين فى هذا الوعيد.',NULL,'747',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4482,'<span data-type="title" id=toc-4528>٢٣ </span>- قال هود: إنما العلم بوقت عذابكم عند الله - وحده - وأنا أبلغكم الذى أرسلت به، ولكنى أراكم قوماً تجهلون ما تبعث به الرسل.',NULL,'747',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4483,'<span data-type="title" id=toc-4529>٢٤ </span>-، ٢٥ - فأتاهم العذاب فى صورة سحاب، فلما رأوه ممتداً فى الأفق متوجهاً نحو أوديتهم، قالوا فرحين: هذا سحاب يأتينا بالمطر والخير. فقيل لهم: بل هو ما استعجلتم به ريح فيها عذاب شديد الألم، تهلك كل شئ بأمر خالقها، فدمرتهم فأصبحوا لا يرى من آثارها إلا مساكنهم. كذلك الجزاء نجزى كل من ارتكب مثل جرمهم.',NULL,'747',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4484,'<span data-type="title" id=toc-4530>٢٦ </span>- ولقد مكنا عاداً فيما لم نمكنكم فيه من السعة والقوة يا أهل مكة، وجعلنا لهم سمعاً وأبصاراً وأفئدة لو شاءوا الانتفاع بها، فما نفعهم سمعهم ولا أبصارهم ولا أفئدتهم شيئاً قليلاً، لأنهم كانوا يكذبون بآيات الله، فحال ذلك بينهم وبين انتفاعهم بما أوتوا، وأحاط بهم العذاب الذى كانوا به يستهزئون.',NULL,'748',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4485,'<span data-type="title" id=toc-4531>٢٧ </span>- ولقد أهلكنا القرى التى كانت حولكم يا أهل مكة، وبيَّنا لهم الدلائل بأساليب متنوعة، لعلهم يرجعون عن الكفر، فلم يرجعوا.',NULL,'748',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4486,'<span data-type="title" id=toc-4532>٢٨ </span>- فهلا منعهم من الهلاك الذين اتخذوهم من دون الله آلهة متقربين بهم إليه تعالى؟! بل غابت هذه الآلهة عنهم وهم أحوج ما كانوا إلى النصرة، وذلك الذى حل بهم من خذلان آلهتهم لهم وضلالهم عنهم هو عاقبة كذبهم وافترائهم.',NULL,'748',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4487,'<span data-type="title" id=toc-4533>٢٩ </span>- واذكر - يا محمد - إذ وجهنا إليك جماعة من الجن يستمعون القرآن، فلما حضروا تلاوته قال بعضهم لبعض: أنصتوا - فلما تمت تلاوته رجعوا مسرعين إلى قومهم، محذرين من الكفر داعين إلى الإيمان.',NULL,'748',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4488,'<span data-type="title" id=toc-4534>٣٠ </span>- قالوا: يا قومنا إنا سمعنا كتاباً عظيم الشأن، أنزل من بعد موسى، مصدقاً لما تقدمه من الكتب الإلهية، يرشد إلى الحق فى الاعتقاد، وإلى شريعة قويمة فى العمل.',NULL,'748',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4489,'<span data-type="title" id=toc-4535>٣١ </span>- يا قومنا: أجيبوا داعى الله الذى يهدى إلى الحق وإلى طريق مستقيم، وصدقوا بالله يغفر لكم ما سلف من ذنوبكم، ويمنعكم من عذاب شديد الألم.',NULL,'748',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4490,'<span data-type="title" id=toc-4536>٣٢ </span>- ومن لا يجب داعى الله فليس بمستطيع أن يعجز الله عن أخذه وإن هرب فى الأرض كل مهرب. وليس له من دون الله نصراء يمنعونه من عذابه. أولئك الذين يعرضون عن إجابة الداعى إلى الله فى حيرة وبعد واضح عن الحق.',NULL,'749',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4491,'<span data-type="title" id=toc-4537>٣٣ </span>- أَغَفلوا ولم يعلموا أن الله الذى خلق السموات والأرض ولم يعجز عن خلقهن قادر على إحياء الموتى؟ بل هو قادر على ذلك، لأنه - تعالى - على كل شئ تام القدرة.',NULL,'749',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4492,'<span data-type="title" id=toc-4538>٣٤ </span>- ويوم يوقف الذين كفروا على النار يقال لهم تقريعاً: أليس هذا العذاب بالأمر الحق المطابق لما أنذرناكم فى الدنيا؟ قالوا: بلى وربنا هو الحق، قال: فذوقوا ألوان العذاب الشديد بإصراركم على الكفر والتكذيب.',NULL,'749',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4493,'<span data-type="title" id=toc-4539>٣٥ </span>- فاصبر - يا محمد - على الكافرين كما صبر أصحاب القوة والثبات من الرسل فى الشدائد، ولا تستعجل لهم العذاب، فهو واقع بهم - لا محالة - وإن طال الأمد. كأنهم يوم يشاهدون هوله يحسبون مدة لبثهم قبله ساعة من نهار. هذا الذى وعظتم به كاف فى الموعظة، فلن يهلك بعذاب الله إلا الخارجون عن طاعته.',NULL,'749',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4494,'<span data-type="title" id=toc-4541>١ </span>- الذين كفروا بالله ورسوله وصدُّوا غيرهم عن الدخول فى الإسلام أبطل الله كل ما عملوه.',NULL,'750',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4495,'<span data-type="title" id=toc-4542>٢ </span>- والذين آمنوا وعملوا الصالحات وصدَّقوا بما أنزل على محمد وهو الحق من ربهم، محا عنهم سيئاتهم، وأصلح حالهم فى الدين والدنيا.',NULL,'750',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4496,'<span data-type="title" id=toc-4543>٣ </span>- ذلك بأن الذين كفروا سلكوا طريق الباطل، وأن الذين آمنوا اتبعوا طريق الحق من ربهم، مثل ذلك البيان الواضح يُبَيِّن الله للناس أحوالهم ليعتبروا.',NULL,'750',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4497,'<span data-type="title" id=toc-4544>٤ </span>-، ٥، ٦ - فإذا لقيتم الذين كفروا فى الحرب فاضربوا رقابهم، حتى إذا أضعفتموهم بكثرة القتل فيهم فاحكموا قيد الأسارى، فإما أن تمنوا بعد انتهاء المعركة مناً بإطلاقهم دون عوض، وإمَّا أن تقبلوا أن يفتدوا بالمال أو بالأسرى من المسلمين. وليكن هذا شأنكم مع الكافرين، حتى تضع الحرب أثقالها وينتهى، فهذا حكم الله فيهم، ولو شاء الله لانتصر منهم بغير قتال، وليختبر المؤمنين بالكافرين شرع الجهاد، والذين قتلوا فى سبيل الله فلن يُبطل أعمالهم، سيهديهم ويصلح قلوبهم، ويدخلهم الجنة عرَّفها لهم.',NULL,'751',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4498,'<span data-type="title" id=toc-4545>٧ </span>- يا أيها الذين آمنوا: إن تنصروا دين الله ينصركم على عدوكم، ويوطد أمركم.',NULL,'751',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4499,'<span data-type="title" id=toc-4546>٨ </span>- والذين كفروا فأشقاهم الله وأبطل أعمالهم.',NULL,'751',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4500,'<span data-type="title" id=toc-4547>٩ </span>- أَمْرُهم ذلك بسبب أنهم كرهوا ما أنزل الله من القرآن والتكاليف، فأبطل أعمالهم.',NULL,'751',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4501,'<span data-type="title" id=toc-4548>١٠ </span>- أَقَعدوا عن طلب ما يعظهم، فلم يسيروا فى الأرض فينظروا فى أى حال كان عاقبة الذين كذَّبوا الرسل من قبلهم، أوقع الله عليهم الهلاك فى كل ما يختص بهم من نفس ومال وولد، وللكافرين بالله وبرسوله أمثال هذه العاقبة.',NULL,'751',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4502,'<span data-type="title" id=toc-4549>١١ </span>- ذلك الجزاء من نصر المؤمنين وقهر الكافرين بأن الله مولى الذين آمنوا وناصرهم، وأن الكافرين لا مولى لهم ينصرهم ويمنع هلاكهم.',NULL,'751',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4503,'<span data-type="title" id=toc-4550>١٢ </span>- إن الله يدخل الذين آمنوا وعملوا الصالحات جنات عظيمة تجرى من تحتها الأنهار، والذين كفروا يتمتعون فى الدنيا قليلا، ويأكلون كما تأكل الأنعام، غافلين عن التفكير فى العاقبة، لا همَّ لهم سوى شهواتهم، والنار فى الآخرة مأوى لهم.',NULL,'751',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4504,'<span data-type="title" id=toc-4551>١٣ </span>- وكثير من أهل القرى السابقين هم أشد قوة من أهل قريتك - مكة - التى أخرجك أهلها - يا محمد - أهلكناهم بأنواع العذاب، فلا ناصر لهم يمنعهم منا.',NULL,'752',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4505,'<span data-type="title" id=toc-4552>١٤ </span>- أيستوى الفريقان فى الجزاء؟ {أفمن كان منهما على معرفة بينة بخالقه ومربيه فأطاعه، كمن زُيِّن له سوء عمله، واتبعوا فيما يأتون ويذرون أهواءهم الباطلة؟ .',NULL,'752',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4506,'<span data-type="title" id=toc-4553>١٥ </span>- تعرض الآية لبيان الفارق بين نعيم الجنة وعذاب النار فنتحدث عن الجنة التى وعد الله بها المتقين: فيها أنهار من ماء غير متغير، وأنهار من لبن لم يفسد طعمه، وأنهار من خمر لذة للشاربين، وأنهار من عسل مصفى مما يخالطه. ولهم فيها أنواع من كل الثمرات، ومغفرة عظيمة من ربهم. فهل حال من يستمعون بهذا النعيم من المؤمنين كحال من يخلدون فى النار من الكفار الذين يسقون من ماء شديد الحرارة، فقطع أمعاءهم.',NULL,'752',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4507,'<span data-type="title" id=toc-4554>١٦ </span>- ومن الكفار فريق يستمعون إليك - يا محمد - غير مؤمنين بك، ولا منتفعين بقولك، حتى إذا انصرفوا من مجلسك، قالوا استهزاء للذين أوتوا العلم: أَىَّ قول قال محمد الآن؟ أولئك الذين طبع الله على قلوبهم بالكفر، فانصرفوا عن الخير منقادين لشهواتهم.',NULL,'752',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4508,'<span data-type="title" id=toc-4555>١٧ </span>- والذين اهتدوا إلى طريق الحق زادهم الله هدى، وأعطاهم تقواهم التى يتقون بها النار.',NULL,'752',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4509,'<span data-type="title" id=toc-4556>١٨ </span>- لم يتعظ المكذبون بأحوال السابقين. فهل ينظرون إلا الساعة أن تأتيهم فجأة؟ فقد ظهرت علاماتها ولم يعتبروا بمجيئها، فمن أين لهم التذكر إذا جاءتهم الساعة بغتة؟}',NULL,'752',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4510,'<span data-type="title" id=toc-4557>١٩ </span>- فاثبت على العلم بأنه لا معبود بحق إلا الله، واستغفر الله لذنبك ولذنوب المؤمنين والمؤمنات، والله يعلم كل منصرف لكم وكل إقامة.',NULL,'753',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4511,'<span data-type="title" id=toc-4558>٢٠ </span>-، ٢١، ٢٢ - ويقول الذين آمنوا: هلا نزلت سورة تدعونا إلى القتال؟ فإذا نزلت سورة لا تحتمل غير وجوبه، وذكر فيها القتال مأموراً به رأيت الذين فى قلوبهم نفاق ينظرون إليك نظر المغشى عليه من الموت خوفاً منه وكراهية له، فأحق بهم طاعة لله وقول يقره الشرع، فإذا جد الأمر ولزمهم القتال، فلو صدقوا الله فى الإيمان والطاعة لكان خيراً لهم من النفاق، فهل يتوقع منكم - أيها المنافقون - إن توليتم أن تفسدوا فى الأرض وتقطعوا صلاتكم بأقاربكم؟',NULL,'753',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4512,'<span data-type="title" id=toc-4559>٢٣ </span>- أولئك الذين أبعدهم الله عن رحمته، فأصمَّهم عن سماع الحق، وأعمى أبصارهم عن رؤية طريق الهدى.',NULL,'753',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4513,'<span data-type="title" id=toc-4560>٢٤ </span>- أعموا فلا يتفهمون هدى القرآن؟ بل على قلوبهم ما يحجبها عن تدبره.',NULL,'753',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4514,'<span data-type="title" id=toc-4561>٢٥ </span>- إن الذين ارتدوا إلى ما كانوا عليه من الكفر والضلال من بعد ما ظهر لهم طريق الهداية. الشيطان زيَّن لهم ذلك، ومد لهم فى الآمال الكاذبة.',NULL,'753',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4515,'<span data-type="title" id=toc-4562>٢٦ </span>-، ٢٧ - ذلك الارتداد بأنهم قالوا للذين كرهوا ما نزَّل الله: سنطيعكم فى بعض الأمر، والله يعلم أسرار هؤلاء المنافقين. فهذا حالهم فى حياتهم، أم حين تتوفاهم الملائكة يضربون وجوههم وأدبارهم إذلالاً لهم فهذا ما لا يتصورنه ولن يقدروا على احتماله.',NULL,'754',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4516,'<span data-type="title" id=toc-4563>٢٨ </span>- ذلك التوفى الرهيب على تلك الحالة بأنهم اتبعوا الباطل الذى أغضب الله، وكرهوا الحق الذى يرضاه، فأبطل كل ما عملوه.',NULL,'754',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4517,'<span data-type="title" id=toc-4564>٢٩ </span>- بل أَظَنَّ هؤلاء الذين فى قلوبهم نفاق أن لن يظهر الله أحقادهم لرسوله وللمؤمنين؟ .',NULL,'754',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4518,'<span data-type="title" id=toc-4565>٣٠ </span>- ولو نشاء لدللناك عليهم، فلعرفتهم بعلامات نسمهم بها، وأقسم: لتعرفنهم من أسلوب قولهم، والله يعلم حقيقة أعمالكم جميعاً.',NULL,'754',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4519,'<span data-type="title" id=toc-4566>٣١ </span>- وأقسم: لنعاملكم معاملة المختبر، حتى نعلم المجاهدين منكم والصابرين فى البأساء والضراء، ونبلوا أخباركم من طاعتكم وعصيانكم فى الجهاد وغيره.',NULL,'754',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4520,'<span data-type="title" id=toc-4567>٣٢ </span>- إن الذين كفروا وصدوا عن طريق الله، وخالفوا الرسول فى عناد وإصرار، من بعد ما ظهر لهم الهدى، لن يضروا الله شيئاً، وسيبطل كل ما عملوه.',NULL,'754',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4521,'<span data-type="title" id=toc-4568>٣٣ </span>- يا أيها الذين آمنوا: أطيعوا الله فيما أمركم به، وأطيعوا الرسول فيما دعاكم إليه، ولا تضيعوا أعمالكم.',NULL,'754',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4522,'<span data-type="title" id=toc-4569>٣٤ </span>- إن الذين كفروا وصدوا عن الدخول فى الإسلام، ثم ماتوا وهم كفار فلن يغفر الله لهم.',NULL,'754',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4523,'<span data-type="title" id=toc-4570>٣٥ </span>- فلا تضعفوا لأعدائكم إذا لقيتموهم، ولا تدعوهم إلى المسالمة خوفاً منهم، وأنتم الأَعْلَون الغالبون بقوة الإيمان، والله معكم بنصره، ولن ينقصكم ثواب أعمالكم.',NULL,'755',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4524,'<span data-type="title" id=toc-4571>٣٦ </span>-، ٣٧ - إنما الحياة الدنيا باطل وغرور، وإن تؤمنوا وتتركوا المعاصى وتفعلوا الخير يُعْطكم الله ثواب ذلك، ولا يسألكم أموالكم، إن يسألكم إيَّاها فيبالغ فى طلبها تبخلوا بها، ويظهر أحقادكم لحبكم لها.',NULL,'755',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4525,'<span data-type="title" id=toc-4572>٣٨ </span>- ها أنتم هؤلاء تدعون لتنفقوا فى سبيل الله الذى شرعه، فمنكم من يبخل بهذا الإنفاق ومن يبخل فما يضر إلا نفسه. والله - وحده - الغنى، وأنتم الفقراء المحتاجون إليه.
INSERT INTO [b836] VALUES (4526,'<span data-type="title" id=toc-4574>١ </span>-، ٢، ٣ - إنا فتحنا لك - يا محمد - فتحا عظيما مبينا بانتصار الحق على الباطل، ليغفر لك الله ما تقدَّم مما يُعَدُّ لمثل مقامك ذنبا، وما تأخر منه، ويكمل نعمته عليك بانتشار دعوتك، ويُثَبِّتَكَ على طريق الله المستقيم، وينصرك الله على أعداء رسالتك نصراً قويا غاليا.',NULL,'756',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4527,'<span data-type="title" id=toc-4575>٤ </span>- هو الله الذى أنزل الطمأنينة فى قلوب المؤمنين ليزدادوا بها يقينا مع يقينهم، ولله - وحده - جنود السموات والأرض، يدبِّر أمرها كما يشاء، وكان الله محيطا علمه بكل شئ، ذا حكمة بالغة فى تدبير كل شأن.',NULL,'757',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4528,'<span data-type="title" id=toc-4576>٥ </span>-، ٦ - ليُدخل الله المؤمنين والمؤمنات بالله ورسوله جنات تجرى من تحتها الأنهار، دائمين فيها، يمحو عنهم سيئاتهم، وكان ذلك الجزاء عند الله فوزاً بالغاً غاية العِظم. وليعذب المنافقين والمنافقات، والمشركين مع الله غيره والمشركات، الظانين بالله ظنا فاسدا. وهو أنه لا ينصر رسوله، عليهم - وحدهم - دائرة السوء، لا يفلتون منها، وغضب الله عليهم وطردهم من رحمته وَهَيَّأ لعذابهم جهنم وساءت نهاية لهم.',NULL,'757',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4529,'<span data-type="title" id=toc-4577>٧ </span>- ولله جنود السموات والأرض، يدبِّر أمرها بحكمته كما يشاء، وكان الله غالباً على كل شئ، ذا حكمة بالغة فى تدبير كل شأن.',NULL,'757',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4530,'<span data-type="title" id=toc-4578>٨ </span>- إنا أرسلناك - يا محمد - شاهداً على أمتك وعلى من قبلها من الأمم، ومُبشّراً المتقين بحسن الثواب، ونذيراً للعصاة بسوء العذاب.',NULL,'757',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4531,'<span data-type="title" id=toc-4579>٩ </span>- لتؤمنوا - أيها المرسل إليكم - بالله ورسوله، وتنصروا الله بنصر دينه، وتعظِّموه مع الإجلال والإكبار، وتنزِّهوه عما لا يليق به غدوة وعشيا.',NULL,'758',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4532,'<span data-type="title" id=toc-4580>١٠ </span>- إن الذين يعاهدونك - على بذل الطاقة لنصرتك - إنما يعاهدون الله، قوة الله معك فوق قوتهم، فمن نقض عهدك بعد ميثاقه، فلا يعود ضرر ذلك إلا على نفسه، ومَن وفَّى بالعهد الذى عاهد الله عليه - بإتمام بيعتك - فسيعطيه الله ثوابا بالغا غاية العظم.',NULL,'758',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4533,'<span data-type="title" id=toc-4581>١١ </span>- سيقول لك من خلَّفهم النفاق من سكان البادية - إذا رجعت من سفرك -: شغلتنا عن الخروج معك أموالنا وأهلونا فاستغفر لنا. يقولون بألسنتهم غير ما فى قلوبهم، قل ردا عليهم: فمن يملك لكم من الله شيئا يدفع عنكم قضاءه، إن أراد بكم ما يضركم، أو أراد بكم ما ينفعكم؟ بل كان الله بكل ما تعملون محيطاً.',NULL,'758',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4534,'<span data-type="title" id=toc-4582>١٢ </span>- بل ظننتم أن لن يرجع الرسول والمؤمنون من غزوهم إلى أهليهم أبداً، فتخلفتم، وزُيِّن ذلك الظن فى قلوبكم، وظننتم الظن الفاسد فى كل شئونكم. وكنتم فى علم الله قوما هالكين، مستحقين لسخطه وعقابه.',NULL,'758',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4535,'<span data-type="title" id=toc-4583>١٣ </span>- ومن لم يؤمن بالله ورسوله، فإنا هيَّأنا للكافرين ناراً موقدة ذات لهب.',NULL,'758',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4536,'<span data-type="title" id=toc-4584>١٤ </span>- ولله - وحده - ملك السموات والأرض يدبره تدبير قادر حكيم، يغفر الذنوب لمن يشاء، ويعذب بحكمته من يشاء، وكان الله عظيم المغفرة واسع الرحمة.',NULL,'758',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4537,'<span data-type="title" id=toc-4585>١٥ </span>- سيقول هؤلاء الذين أقعدهم النفاق عن الخروج معك من سكان البادية: إذا انطلقتم إلى مغانم وعدكم الله بها لتأخذوها، دعونا نتَّبعكم إليها. يريدون بذلك تغيير وعد الله بأن تلك الغنائم لا تكون للمخلَّفين الذين قعدوا عن الجهاد وإنما تكون للذين خرجوا مع رسول الله يوم الحديبية، قل لهم - يا محمد -: لن تتبعونا. فحكم الله أن هذه الغنائم لمن خرج إلى الغزو مع رسوله، أما المخلَّفون فسيقولون: لم يأمركم الله بذلك، بل تحسدوننا أن نشارككم، وهم بمقولتهم عاجزون عن إدراك أن أمر الله لا بد أن ينفذ.',NULL,'759',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4538,'<span data-type="title" id=toc-4586>١٦ </span>- قل للمتخلفين عن الخروج من أهل البادية: ستدعوْن إلى قتال قوم ذوى شدة قوية فى الحرب، فإن تَسْتَجيبوا لهذه الدعوة يعطكم الله الغنيمة فى الدنيا، والثواب فى الآخرة، وإن تعرضوا عنها كما أعرضتم من قبل يُعذبكم الله عذابا بالغ الألم.',NULL,'759',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4539,'<span data-type="title" id=toc-4587>١٧ </span>- ليس على الأعمى إثم فى التَّخلف عن قتال الكفار، ولا على الأعرج إثم، ولا على المريض إثم كذلك، حيث لا يستطيعون، ومن يطع الله ورسوله فى كل أمر ونهى يُدخله جنات فسيحات تجرى من تحتها الأنهار، ومن يعرض عن طاعة الله ورسوله يعذبه عذابا بالغ الألم.',NULL,'759',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4540,'<span data-type="title" id=toc-4588>١٨ </span>-، ١٩ - لقد رضى الله عن المؤمنين حين يعاهدونك مختارين تحت الشجرة، فعلم ما فى قلوبهم من الإخلاص والوفاء لرسالتك، فأنزل الطمأنينة عليهم وأعطاهم بصدقهم فى البيعة وإتمام الصلح عِزا عاجلا. ومغانم كثيرة وعدهم الله بها يأخذونها، وكان الله غالبا على كل شئ، ذا حكمة بالغة فى كل ما قضاه.',NULL,'759',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4541,'<span data-type="title" id=toc-4589>٢٠ </span>-، ٢١ - وعدكم الله مغانم كثيرة تأخذونها فى الوقت المقدَّر لها، فعجَّل لكم هذه - وهو ما وعدكم به من الغنائم - ومنع أذى الناس عنكم، ولتكون آية للمؤمنين على صدق وعد الله لهم.
INSERT INTO [b836] VALUES (4542,'<span data-type="title" id=toc-4590>٢٢ </span>- ولو قاتلكم الذين كفروا من أهل مكة، ولم يعقدوا معكم صلحا، لفروا منهزمين رُعْبا منكم، ثم لا يجدون أى ولىٍّ يلى أمرهم، ولا أى نصير ينصرهم.',NULL,'760',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4543,'<span data-type="title" id=toc-4591>٢٣ </span>- سَنَّ الله سُنَّة قد مضت من قبل فى خلقه أن تكون العاقبة لرسله وللمؤمنين، ولن تجد لسنة الله تغييراً.',NULL,'760',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4544,'<span data-type="title" id=toc-4592>٢٤ </span>- وهو الله - وحده - الذى منع أيدى الكفار من إيذائكم وأيديكم من قتالهم بوسط مكة من بعد أن أقدركم عليهم، وكان الله بكل ما تعملون بصيرا.',NULL,'760',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4545,'<span data-type="title" id=toc-4593>٢٥ </span>-، ٢٦ - أهل مكة هم الذين كفروا ومنعوكم من دخول المسجد الحرام، ومنعوا الهدى الذى سقتموه محبوسا معكم على التقرب به من بلوغ مكانه الذى ينحر فيه، ولولا كراهة أن تُصيبوا رجالاً مؤمنين ونساء مؤمنات بين الكفار بمكة أخفوا إيمانهم فلم تعلموهم فتقتلوهم بغير علم بهم، فيلحقكم بقتلهم عار وخزى، ولهذا كان منع القتال فى هذا اليوم حتى يحفظ الله من كانوا مستخفين بإسلامهم بين كفار مكة. لو تميز المؤمنون لعاقبنا الذين أصروا على الكفر منهم عقابا بالغ الألم، حين جعل الذين كفروا فى قلوبهم الأنفة أنفة الجاهلية، فأنزل الله طمأنينته على رسوله وعلى المؤمنين، وألزمهم كلمة الوقاية من الشرك والعذاب، وكانوا أحق بها وأهلاً لها. وكان علم الله محيطا بكل شئ.',NULL,'760',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4546,'<span data-type="title" id=toc-4594>٢٧ </span>- لقد صدق الله رسوله رؤياه دخول المسجد الحرام بتحققها. أقسم: لتدخلن المسجد الحرام - إن شاء الله - آمنين عدوكم، بين محلق رأسه ومقصر، وغير خائفين، فعلم سبحانه الخير الذى لم تعلموه فى تأخير دخول المسجد الحرام، فجعل من قبل دخولكم فتحا قريبا.',NULL,'761',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4547,'<span data-type="title" id=toc-4595>٢٨ </span>- هو الله الذى أرسل رسوله بالإرشاد الواضح ودين الإسلام ليعليه على الأديان كلها، وكفى بالله شهيدا على ذلك.',NULL,'761',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4548,'<span data-type="title" id=toc-4596>٢٩ </span>- محمد رسول الله وأصحابه الذين معه أشداء أقوياء على الكفار، متراحمون، متعاطفون فيما بينهم، تُبصرهم راكعين ساجدين كثيرا، يرجون بذلك ثوابا عظيما من الله ورضوانا عميما، علامتهم خشوع ظاهر فى وجوههم من أثر الصلاة كثيرا، ذلك هو وصفهم العظيم فى التوراة، وصفتهم فى الإنجيل كصفة زرع أخرج أول ما ينشق عنه، فآزره، فتحول من الدقة إلى الغلظ، فاستقام على أصوله، يُعجب الزراع بقوته، وكان المؤمنون كذلك، ليغيظ الله بقوتهم الكفار، وعد الله الذين آمنوا وعملوا الصالحات منهم مغفرة تمحو جميع ذنوبهم، وثوابا بالغا غاية العظم.',NULL,'761',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4549,'<span data-type="title" id=toc-4598>١ </span>- يا أيها الذين آمنوا: لا تقدِّموا أى أمر فى الدين والدنيا دون أن يأمر به الله ورسوله، واجعلوا لأنفسكم وقاية من عذاب الله بامتثال شريعته. إن الله عظيم السمع لكل ما تقولون، محيط علمه بكل شئ.',NULL,'762',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4550,'<span data-type="title" id=toc-4599>٢ </span>- يا أيها الذين آمنوا: لا ترفعوا أصواتكم فوق صوت النبى إذا تكلم وتكلمتم، ولا تساووا أصواتكم بصوته - كما يخاطب بعضكم بعضاً - كراهة أن تبطل أعمالكم وأنتم لا تشعرون ببطلانها.',NULL,'762',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4551,'<span data-type="title" id=toc-4600>٣ </span>- إن الذين يخفضون أصواتهم فى مجلس رسول الله - إجلالاً له - أولئك - وحدهم - هم الذين أخلص الله قلوبهم للتقوى، فليس لغيرها مكان فيها، لهم مغفرة واسعة لذنوبهم وثواب بالغ غاية العظم.',NULL,'762',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4552,'<span data-type="title" id=toc-4601>٤ </span>- إن الذين ينادونك من وراء حجراتك أكثرهم لا يعقلون ما ينبغى لمقامك من التوقير والإجلال.',NULL,'762',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4553,'<span data-type="title" id=toc-4602>٥ </span>- ولو أنَّ هؤلاء صبروا - تأدباً معك - حتى تقصد الخروج إليهم لكان ذلك خيراً لهم فى دينهم، والله عظيم المغفرة ذو رحمة واسعة.',NULL,'763',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4554,'<span data-type="title" id=toc-4603>٦ </span>- يا أيها الذين آمنوا إن جاءكم أى خارج عن حدود شريعة الله بأى خبر فتثبتوا من صدقه، كراهة أن تصيبوا أى قوم بأذى - جاهلين حالهم - فتصيروا على ما فعلتم معهم - بعد ظهور براءتهم - مغتمِّين دائماً على وقوعه، متمنين أنه لم يقع منكم.',NULL,'763',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4555,'<span data-type="title" id=toc-4604>٧ </span>-، ٨ - واعلموا - أيها المؤمنون - أن فيكم رسول الله فاقْدروه حق قدْره واصْدقوه، لو يطيع ضعاف الإيمان منكم فى كثير من الأمور، لوقعتم فى المشقة والهلاك، ولكن الله حبَّب فى الكاملين منكم الإيمان، وزيَّنه فى قلوبكم، فتصَونوّا عن تزيين ما لا ينبغى، وبغَّض إليكم جحود نعم الله، والخروج عن حدود شريعته ومخالفة أوامره، أولئك هم - وحدهم - الذين عرفوا طريق الهدى وثبتوا عليه تفضلا كريماً، وإنعاماً عظيماً من الله عليهم، والله محيط علمه بكل شئ، ذو حكمة بالغة فى تدبير كل شأن.',NULL,'763',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4556,'<span data-type="title" id=toc-4605>٩ </span>- وإن طائفتان من المؤمنين تقاتلوا فأصلحوا - أيها المؤمنون - بينهما، فإن تعدت إحداهما على الأخرى ورفضت الصلح معها فقاتلوا التى تتعدى إلى أن ترجع إلى حكم الله، فإن رجعت فأصلحوا بينهما بالإنصاف، واعدلوا بين الناس جميعاً فى كل الشئون، إن الله يحب العادلين.',NULL,'763',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4557,'<span data-type="title" id=toc-4606>١٠ </span>- إنما المؤمنون بالله ورسوله إخوة جمع الإيمان بين قلوبهم، فأصلحوا بين أخويكم رعاية لأخُوّة الإيمان، واجعلوا لأنفسكم وقاية من عذاب الله بامتثال أمره واجتناب نهيه راجين أن يرحمكم الله بتقواكم.',NULL,'763',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4558,'<span data-type="title" id=toc-4607>١١ </span>- يا أيها الذّين آمنوا: لا يسخر رجال منكم من رجال آخرين، عسى أنْ يكونوا عند الله خيراً من الساخرين. ولا يسخر نساء مؤمنات من نساء مؤمنات عسى أن يكنَّ عند الله خيراً من الساخرات ولا يعب بعضكم بعضاً، ولا يدْعُ الواحد أخاه بما يستكره من الألقاب. بئس الذكر للمؤمنين أن يُذكروا بالفسوق بعد اتصافهم بالإيمان، ومن لم يرجع عمَّا نهى عنه فأولئك هم - وحدهم - الظالمون أنفسهم وغيرهم.',NULL,'763',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4559,'<span data-type="title" id=toc-4608>١٢ </span>- يا أيها الذين آمنوا: ابتعدوا عن كثير من ظن السوء بأهل الخير. إن بعض الظن إثم يستوجب العقوبة، ولا تتبعوا عورات المسلمين، ولا يذكر بعضكم بعضاً بما يكره فى غيبته. أيحب أحدكم أن يأكل لحم أخيه ميتاً، فقد كرهتموه؟! فاكرهوا الغيبة فإنها مماثلة له، وقوا أنفسكم عذاب الله بامتثال ما أمر، واجتناب ما نهى. إن الله عظيم فى قبول توبة التائبين ذو رحمة واسعة بالعالمين.',NULL,'764',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4560,'<span data-type="title" id=toc-4609>١٣ </span>- يا أيها الناس: إنا خلقناكم متساوين من أصل واحد هو آدم وحواء، وصيَّرناكم بالتكاثر جموعاً عظيمة وقبائل متعددة، ليتم التعارف والتعاون بينكم، إن أرفعكم منزلة عند الله فى الدنيا والآخرة أتقاكم له. إن الله محيط علمه بكل شئ، خبير لا تخفى عليه دقائق كل شأن.',NULL,'764',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4561,'<span data-type="title" id=toc-4610>١٤ </span>- قالت الأعراب بألسنتهم: آمنا، قل لهم - يا محمد -: لم تؤمنوا، لأن قلوبكم لم تصدق ما نطقتم به، ولكن قولوا: انقدنا ظاهراً لرسالتك ولمَّا يدخل الإيمان فى قلوبكم بعد، وإن تطيعوا الله ورسوله صادقين لا ينقصكم من ثواب أعمالكم أى شئ. إن الله عظيم المغفرة للعباد، ذو رحمة واسعة بكل شئ.',NULL,'764',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4562,'<span data-type="title" id=toc-4611>١٥ </span>- إنما المؤمنون - حقاً - هم الذين آمنوا بالله ورسوله، ثم لم يقع فى قلوبهم شك فيما آمنوا به، وجاهدوا بأموالهم وأنفسهم فى طريق طاعة الله، أولئك هم - وحدهم - الذين صدقوا فى إيمانهم.',NULL,'765',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4563,'<span data-type="title" id=toc-4612>١٦ </span>- قل لهم - يا محمد - تكذيباً لقولهم آمنا: أتخبرون الله بتصديق قلوبكم، والله - وحده - يعلم كل ما فى السموات، وكل ما فى الأرض، والله محيط علمه بكل شئ.',NULL,'765',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4564,'<span data-type="title" id=toc-4613>١٧ </span>- يعدون إسلامهم يداً لهم عليك - يا محمد - تستوجب شكرك لهم، قل: لا تمنُّوا علىَّ إسلامكم فخيره لكم، بل الله - وحده - يَمُنُّ عليكم بهدايته إياكم إلى الإيمان، إن كنتم صادقين فى دعواكم.',NULL,'765',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4565,'<span data-type="title" id=toc-4614>١٨ </span>- إن الله يعلم كل ما استتر فى السموات والأرض، والله محيط الرؤية بكل ما تعملون.',NULL,'765',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4566,'<span data-type="title" id=toc-4616>١ </span>-، ٢ -<span data-type="title" id=toc-4615> ق: </span>حرف من حروف الهجاء افتتحت السورة به على طريقة القرآن الكريم فى افتتاح بعض السور ببعض هذه الحروف للتحدى وتنبيه الأذهان، أُقسم بالقرآن ذى الكرامة والمجد والشرف: إنا أرسلناك - يا محمد - لتنذر الناس به، فلم يؤمن أهل مكة، بل عجبوا أن جاءهم رسول من جنسهم يُنذرهم بالبعث، فقال الكافرون: هذا شئ منكر عجيب.',NULL,'766',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4567,'<span data-type="title" id=toc-4617>٣ </span>- أبعد أن نموت ونصير تراباً نرجع؟ ذلك البعث بعد الموت رجْع بعيد الوقوع.',NULL,'766',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4568,'<span data-type="title" id=toc-4618>٤ </span>- قد علمنا ما تأخذه الأرض من أجسامهم بعد الموت، وعندنا كتاب دقيق الإحصاء والحفظ.',NULL,'766',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4569,'<span data-type="title" id=toc-4619>٥ </span>- لم يتدبروا ما جاءهم به الرسول، بل كذَّبوا به من فورهم دون تدبّر وتفكر، فهم فى شأن مضطرب لا يستقرون على حال.',NULL,'766',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4570,'<span data-type="title" id=toc-4620>٦ </span>- أغفلوا فلم ينظروا إلى السماء مرفوعة فوقهم بغير عمد؟ كيف أحكمنا بناءها وزيَّناها بالكواكب، وليس فيها أى شقوق تعاب بها؟',NULL,'767',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4571,'<span data-type="title" id=toc-4621>٧ </span>- والأرض بسطناها وأرسينا فيها جبالاً ثوابت ضاربة فى أعماقها، وأنبتنا فيها من كل صنف يبتهج به من النبات، يسر الناظرين.',NULL,'767',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4572,'<span data-type="title" id=toc-4622>٨ </span>- جعلنا ذلك تبصيرا وتذكيرا لكل عبد راجع إلى ربه، يُفكر فى دلائل قدرته.',NULL,'767',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4573,'<span data-type="title" id=toc-4623>٩ </span>- ونزَّلنا من السماء ماء كثير الخير والبركات، فأنبتنا به جنات ذات أشجار وأزهار وثمار، وأخرجنا به حب الزرع الذى يحصد.',NULL,'767',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4574,'<span data-type="title" id=toc-4624>١٠ </span>- والنخل ذاهبات إلى السماء طولاً، لها طلع متراكم بعضه فوق بعض لكثرة ما فيه من مادة الثمر.',NULL,'767',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4575,'<span data-type="title" id=toc-4625>١١ </span>- أنبتناها رزقاً للعباد، وأحيينا بالماء أرضاً جف نباتها، كذلك خروج الموتى من القبور حين يبعثون.',NULL,'767',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4576,'<span data-type="title" id=toc-4626>١٢ </span>-، ١٣، ١٤ - كذبت بالرسل قبل هؤلاء أمم كثيرة: قوم نوح، والقوم المعروفون بأصحاب الرس، وثمود، وعاد، وفرعون، وقوم لوط، والقوم المعروفون بأصحاب الأيكة، وقوم تُبَّع، كل من هؤلاء كذب رسوله فحق عليهم ما وعدتهم به من الهلاك.',NULL,'767',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4577,'<span data-type="title" id=toc-4627>١٥ </span>- أعطلت إرادتنا أو عوَّقت قدرتنا فعجزنا عن الخلق الأول فلا نستطيع إعادتهم؟! لم نعجز باعترافهم، بل هم فى ريب وشبهة من خلق جديد بعد الموت.',NULL,'767',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4578,'<span data-type="title" id=toc-4628>١٦ </span>- أقسم: لقد خلقنا الإنسان ونعلم ما تحدثه به نفسه، ونحن - بعلمنا بأحواله كلها - أقرب إليه من عرق الوريد، الذى هو أقرب شئ منه.',NULL,'767',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4579,'<span data-type="title" id=toc-4629>١٧ </span>- إذ يتلقى الملكان الحافظان أحدهما عن اليمين قعيد والآخر عن الشمال قعيد، لتسجيل أعماله.',NULL,'767',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4580,'<span data-type="title" id=toc-4630>١٨ </span>- ما يتكلم به من قول إلا لديه ملك حافظ مهيأ لكتابة قوله.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4581,'<span data-type="title" id=toc-4631>١٩ </span>- وجاءت غشية الموت بالحق الذى لا مرية فيه. ذلك الأمر الحق ما كنت تهرب منه.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4582,'<span data-type="title" id=toc-4632>٢٠ </span>- ونفخ فى الصور نفخة البعث، ذلك النفخ يوم وقوع العذاب الذى توعدهم به.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4583,'<span data-type="title" id=toc-4633>٢١ </span>- وجاءت كل نفس برة أو فاجرة معها من يسوقها إلى المحشر، ومن يشهد بعملها.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4584,'<span data-type="title" id=toc-4634>٢٢ </span>- ثم يقال - تقريعاً - للمكذب: لقد كنت فى الدنيا فى غفلة تامة من هذا الذى تقاسيه، فأزلنا عنك الحجاب الذى يُغطى عنك أمور الآخرة. فبصرك اليوم نافذ قوى.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4585,'<span data-type="title" id=toc-4635>٢٣ </span>- وقال شيطانه الذى كان مقيضاً له فى الدنيا: هذا هو الكافر الذى عندى مُهيأ لجهنم بإضلالى.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4586,'<span data-type="title" id=toc-4636>٢٤ </span>-، ٢٥ - يقال للملكين: ألقيا فى جهنم كل مبالغ فى الكفر، مبالغ فى العناد، وترك الانقياد للحق، مبالغ فى المنع لكل خير، ظالم متجاوز للحق، شاك فى الله تعالى وفيما أنزله.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4587,'<span data-type="title" id=toc-4637>٢٦ </span>- الذى اتخذ مع الله إلهاً آخر يعبده فألقياه فى العذاب البالغ غاية الشدة.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4588,'<span data-type="title" id=toc-4638>٢٧ </span>- قال الشيطان رداً لقول الكافر: ربنا ما أطغيته، ولكن كان فى ضلال بعيد عن الحق، فأعنته عليه بإغوائى.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4589,'<span data-type="title" id=toc-4639>٢٨ </span>- قال تعالى للكافرين وقرنائهم: لا تختصموا عندى فى موقف الحساب والجزاء، وقد قدَّمت إليكم فى الدنيا وعيداً على الكفر فى رسالاتى إليكم، فلم تؤمنوا.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4590,'<span data-type="title" id=toc-4640>٢٩ </span>- ما يُغَيَّر القول الذى عندى ووعيدى بإدخال الكافرين النار، ولست بظلام للعبيد فلا أعاقب عبداً بغير ذنب.',NULL,'768',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4591,'<span data-type="title" id=toc-4641>٣٠ </span>- يوم يقول الحق لجهنم تقريعاً للكافرين: هل امتلأت، وتقول جهنم غضباً عليهم: هل من زيادة أستزيد بها من هؤلاء الظالمين؟ .',NULL,'769',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4592,'<span data-type="title" id=toc-4642>٣١ </span>- وأدنيت الجنة مزينة للذين اتقوا ربهم - بامتثال أمره واجتناب نهيه - مكاناً غير بعيد منهم.',NULL,'769',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4593,'<span data-type="title" id=toc-4643>٣٢ </span>- هذا الثواب الذى توعدون به لكل رجَّاع إلى الله، شديد الحفظ لشريعته.',NULL,'769',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4594,'<span data-type="title" id=toc-4644>٣٣ </span>- من خاف عقاب من وسعت رحمته كل شئ - وهو غائب عنه لم يره - وجاء فى الآخرة بقلب راجع إليه تعالى.',NULL,'769',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4595,'<span data-type="title" id=toc-4645>٣٤ </span>- يُقال تكريماً للمؤمنين: ادخلوا الجنة آمنين ذلك اليوم الذى دخلتم فيه الجنة هو يوم البقاء الذى لا انتهاء له.',NULL,'769',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4596,'<span data-type="title" id=toc-4646>٣٥ </span>- لهؤلاء المتقين كل ما يشاءون فى الجنة، وعندنا مزيد من النعيم مما لا يخطر على قلب بشر.',NULL,'769',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4597,'<span data-type="title" id=toc-4647>٣٦ </span>- وكثيراً أهلكنا من قبل هؤلاء المكذبين من أهل القرون الماضية، هم أشد من هؤلاء قوة وتسلّطاً، فطوَّفوا فى البلاد وأمعنوا فى البحث والطلب، هل كان لهم مهرب من الهلاك؟ .',NULL,'769',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4598,'<span data-type="title" id=toc-4648>٣٧ </span>- إن فيما فعل بالأمم الماضية لعظة لمن كان له قلب يدرك الحقائق، أو أصغى إلى الهداية وهو حاضر بفطنته.',NULL,'769',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4599,'<span data-type="title" id=toc-4649>٣٨ </span>- أقسم: لقد خلقنا السموات والأرض وما بينهما من الخلائق فى ستة أيام، وما أصابنا أى إعياء.',NULL,'769',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4600,'<span data-type="title" id=toc-4650>٣٩ </span>-، ٤٠ - إذا تبين ذلك، فاصبر - أيها الرسول - على ما يقول هؤلاء المكذبون من الزور والبهتان فى شأن رسالتك، ونزه خالقك ومربيك عن كل نقص، حامداً له وقت الفجر، ووقت العصر، لعظم العبادة فيهما، ونزهه فى بعض الليل وأعقاب الصلاة.',NULL,'769',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4601,'<span data-type="title" id=toc-4651>٤١ </span>-، ٤٢ - واستمع لما أُخبرك به من حديث القيامة لعظم شأنه، يوم يُنادى الملك المنادى من مكان قريب ممن يُناديهم، يوم يسمعون النفخة الثانية بالحق الذى هو البعث. ذلك اليوم هو يوم الخروج من القبور.',NULL,'770',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4602,'<span data-type="title" id=toc-4652>٤٣ </span>- إنا نحن - وحدنا - نُحيى الخلائق ونُميتهم فى الدنيا، وإلينا - وحدنا - الرجوع فى الآخرة.',NULL,'770',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4603,'<span data-type="title" id=toc-4653>٤٤ </span>- يوم تنشق الأرض عنهم فيخرجون منها مسرعين إلى المحشر ... ذلك الأمر العظيم حشر هَيِّن ويسير علينا - وحدنا -.',NULL,'770',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4604,'<span data-type="title" id=toc-4654>٤٥ </span>- نحن أعلم بكل ما يقولون من الأكاذيب فى شأن رسالتك، وما أنت عليهم بمسلط تجبرهم على ما تريد، وإنما أنت منذر، فذكر بالقرآن المؤمن الذى يخاف عقابى، فتنفعه الذكرى.',NULL,'770',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4605,'<span data-type="title" id=toc-4656>١ </span>-، ٢، ٣، ٤ - أقسم بالرياح المثيرات للسحاب، تدفعها دفعاً، فالحاملات منها ثقلاً عظيماً من الماء، فالجاريات به مُيَسرة بتسخير الله، فالمقسمات رزقاً يسوقه الله إلى من يشاء.',NULL,'771',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4606,'<span data-type="title" id=toc-4657>٥ </span>-، ٦ - إن الذى تُوعدونه من البعث وغيره لمحقق الوقوع، وإن الجزاء على أعمالكم لحاصل لا محالة.',NULL,'771',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4607,'<span data-type="title" id=toc-4658>٧ </span>-، ٨ - أُقسم بالسماء ذات الطرائق المحكمة: إنكم إذ تقولون - ما تقولون - لفى قول مضطرب.',NULL,'771',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4608,'<span data-type="title" id=toc-4659>٩ </span>- ينصرف عن الإيمان بذلك الوعد الصادق، والجزاء الواقع من صرف عنه، لإيثاره هواه على عقله.',NULL,'771',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4609,'<span data-type="title" id=toc-4660>١٠ </span>-، ١١ - هلك الكذَّابون القائلون فى شأن القيامة بالظن والتخمين، الذين هم مغمورون فى الجهل، غافلون عن أدلة اليقين.',NULL,'771',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4610,'<span data-type="title" id=toc-4661>١٢ </span>- يسألون - مستهزئين مستبعدين - متى يوم الجزاء؟ .',NULL,'771',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4611,'<span data-type="title" id=toc-4662>١٣ </span>- يوم هم موْقوفون على النار، يُصهرون بها.',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4612,'<span data-type="title" id=toc-4663>١٤ </span>- يُقال لهم: ذوقوا عذابكم هذا الذى كنتم فى الدنيا تستعجلون وقوعه.',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4613,'<span data-type="title" id=toc-4664>١٥ </span>- إن الذين أطاعوا الله وخافوه ينعمون فى جنات وعيون لا يحيط بها الوصف.',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4614,'<span data-type="title" id=toc-4665>١٦ </span>- متقبلين ما أعطاهم ربهم من الثواب والتكريم، إنهم كانوا قبل ذلك - فى الدنيا - محسنين فى أداء ما طُلب منهم.',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4615,'<span data-type="title" id=toc-4666>١٧ </span>-، ١٨ - كانوا ينامون قليلاً من الليل، ويستيقظون أكثره للعبادة، وبأواخر الليل هم يستغفرون.',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4616,'<span data-type="title" id=toc-4667>١٩ </span>- وفى أموالهم نصيب ثابت للمحتاجين، السائلين منهم والمحرومين المتعففين.',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4617,'<span data-type="title" id=toc-4668>٢٠ </span>- وفى الأرض دلائل واضحات موصلة إلى اليقين لمن سلك طريقه.',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4618,'<span data-type="title" id=toc-4669>٢١ </span>- وفى أنفسكم كذلك آيات واضحات، أغفِلتم عنها فلا تبصرون دلالتها؟ .',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4619,'<span data-type="title" id=toc-4670>٢٢ </span>- وفى السماء أمْرُ رزقكم وتقدير ما توعدون.',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4620,'<span data-type="title" id=toc-4671>٢٣ </span>- فأقسم برب السماء والأرض: إن كل ما تنكرون من وقوع البعث والجزاء وتعذيب المكذبين وإثابة المتقين لثابت مثل نطقكم الذى لا تشكون فى وقوعه منكم.',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4621,'<span data-type="title" id=toc-4672>٢٤ </span>-، ٢٥ - هل علمت قصة الملائكة أضياف إبراهيم المكرمين إذ دخلوا عليه فقالوا: سلاماً، قال: سلام قوم غير معروفين.',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4622,'<span data-type="title" id=toc-4673>٢٦ </span>-، ٢٧ - فذهب إلى أهله فى خفية، فجاء بعجل سمين، فقرَّبه إليهم، فلم يأكلوا منه، قال متعجبا من حالهم: ألا تأكلون؟ .',NULL,'772',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4623,'<span data-type="title" id=toc-4674>٢٨ </span>- فأحس فى نفسه خوْفاً منهم، قالوا: لا تخف، وبشروه بغلام له حظ وافر من العلم.',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4624,'<span data-type="title" id=toc-4675>٢٩ </span>- فأقبلت امرأته فى صيحة حين سمعت البشارة، فضربت وجهها بيدها - استبعاداً وتعجباً - وقالت: أنا عجوز عاقر، فكيف ألد؟',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4625,'<span data-type="title" id=toc-4676>٣٠ </span>- قالوا: كذلك قضى ربك إنه هو الحكيم فى كل ما يقضى، العليم الذى لا يخفى عليه شئ.',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4626,'<span data-type="title" id=toc-4677>٣١ </span>- قال إبراهيم: فما شأنكم - بعد هذه البشارة - أيها المرسلون؟! .',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4627,'<span data-type="title" id=toc-4678>٣٢ </span>-، ٣٣، ٣٤ - قالوا: إنا أُرسلنا إلى قوم مفرطين فى العصيان، لنلقى عليهم حجارة من طين لا يعلم كنهه إلا الله، مُعلَّمة مخصصة عند ربك للمجاوزين الحد فى الفجور.',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4628,'<span data-type="title" id=toc-4679>٣٥ </span>-، ٣٦ - فقضينا بإخراج من كان فى تلك القرية من المؤمنين، فما وجدنا فيها غير أهل بيت واحد من المسلمين.',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4629,'<span data-type="title" id=toc-4680>٣٧ </span>- وتركنا فيها علامة تدل على هلاك أهلها، ليعتبر بها الذين يخافون العذاب الأليم.',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4630,'<span data-type="title" id=toc-4681>٣٨ </span>- وفى قصة موسى عظة، إذ أرسلناه إلى فرعون مؤيداً ببرهان بيِّن.',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4631,'<span data-type="title" id=toc-4682>٣٩ </span>- فأعرض فرعون عن الإيمان بموسى معتدا بقوَّته، وقال: هو ساحر أو مجنون.',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4632,'<span data-type="title" id=toc-4683>٤٠ </span>- فأخذناه ومن اعتز بهم فرميناهم فى البحر، وهو مقترف ما يُلام عليه من الكفر والعناد.',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4633,'<span data-type="title" id=toc-4684>٤١ </span>- وفى قصة عاد عظة، إذ أرسلنا عليهم الريح التى لا خير فيها.',NULL,'773',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4634,'<span data-type="title" id=toc-4685>٤٢ </span>- ما تترك من شئ مرّت عليه إلا جعلته كالعظم البالى.',NULL,'774',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4635,'<span data-type="title" id=toc-4686>٤٣ </span>-، ٤٤ - وفى قصة ثمود آية، إذ قيل لهم: تمتعوا فى داركم إلى وقت معلوم، فتَجبَّروا وتعالوا عن الاستجابة لأمر ربهم، فأهلكتهم الصاعقة وهم يعاينون وقوعها بهم.',NULL,'774',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4636,'<span data-type="title" id=toc-4687>٤٥ </span>- فما تمكنوا من نهوض، وما كانوا قادرين على الانتصار بدفع العذاب.',NULL,'774',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4637,'<span data-type="title" id=toc-4688>٤٦ </span>- وقوم نوح أهلكناهم من قبل هؤلاء، إنهم كانوا قوماً خارجين عن طاعة الله.',NULL,'774',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4638,'<span data-type="title" id=toc-4689>٤٧ </span>-، ٤٨ - والسماء أحكمناها بقوة، وإنا لقادرون على أكثر من ذلك. والأرض بسطناها، فنعم المهاد الذى ينتفع به الإنسان.',NULL,'774',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4639,'<span data-type="title" id=toc-4690>٤٩ </span>- ومن كل شئ خلقنا صنفين مزدوجين لعلكم تتذكرون فتؤمنوا بقدرتنا.',NULL,'774',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4640,'<span data-type="title" id=toc-4691>٥٠ </span>-، ٥١ - فسارعوا إلى طاعة الله، إنى لكم من الله نذير مُبَيِّن عاقبة الإشراك.',NULL,'774',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4641,'<span data-type="title" id=toc-4692>٥٢ </span>- كذلك كان شأن الأمم مع رسلهم، ما أتى الذين من قبل قومك من رسول إلا قالوا: ساحر أو مجنون.',NULL,'774',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4642,'<span data-type="title" id=toc-4693>٥٣ </span>- أأوصى بعضهم بعضاً بهذا القول حتى تواردوا عليه؟ بل هم قوم متجاوزون الحدود فتلاقوا فى الطعن على الرسل.',NULL,'774',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4643,'<span data-type="title" id=toc-4694>٥٤ </span>- فأعرضْ عن هؤلاء المعاندين، فما أنت بملوم على عدم استجابتهم.',NULL,'774',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4644,'<span data-type="title" id=toc-4695>٥٥ </span>- ودم على التذكير، فإن الذكرى تزيد المؤمنين بصيرة وقوة يقين.',NULL,'775',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4645,'<span data-type="title" id=toc-4696>٥٦ </span>- وما خلقت الجن والإنس لشئ يعود علىّ بالنفع، وإنما خلقتهم ليعبدونى، والعبادة نفع لهم.',NULL,'775',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4646,'<span data-type="title" id=toc-4697>٥٧ </span>- ما أريد منهم من رزق - لأنى غنى عن العالمين - وما أريد أن يطعمونى لأنى أُطْعِم ولا أُطْعَم.',NULL,'775',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4647,'<span data-type="title" id=toc-4698>٥٨ </span>- إن الله - وحده - هو المتكفل برزق عباده، وهو ذو القوة الشديد الذى لا يعجز.',NULL,'775',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4648,'<span data-type="title" id=toc-4699>٥٩ </span>- فإن للذين ظلموا أنفسهم بالكفر والتكذيب نصيباً من العذاب مثل نصيب أصحابهم من الأمم الماضية، فلا يستعجلونى بإنزال العذاب قبل أوانه.',NULL,'775',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4649,'<span data-type="title" id=toc-4700>٦٠ </span>- فهلاك للذين كفروا من يومهم الذى يوعدونه، لما فيه من الشدائد والأهوال.',NULL,'775',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4650,'<span data-type="title" id=toc-4702>١ </span>-، ٢، ٣، ٤، ٥، ٦ - أقسم بجبل طور سيناء الذى كلَّم الله عليه موسى، وبكتاب منزل من عند الله مكتوب فى صحف ميسرة للقراءة، وبالبيت المعمور بالطائقين والقائمين والركع السجود، وبالسماء المرفوعة بغير عمد، وبالبحر المملوء.',NULL,'776',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4651,'<span data-type="title" id=toc-4703>٧ </span>-، ٨ - إن عذاب ربك الذى توعد به الكافرين لنازل بهم لا محالة، ليس له من دافع يدفعه عنهم.',NULL,'776',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4652,'<span data-type="title" id=toc-4704>٩ </span>-، ١٠ - يوم تضطرب السماء اضطراباً شديداً، وتنتقل الجبال من مقارها انتقالاً ظاهراً.',NULL,'776',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4653,'<span data-type="title" id=toc-4705>١١ </span>-، ١٢ - فهلاك شديد فى هذا اليوم للمكذبين بالحق، والذين هم فى باطل يلهون.',NULL,'776',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4654,'<span data-type="title" id=toc-4706>١٣ </span>- يوم يُدْفعون إلى نار جهنم دفعاً عنيفاً.',NULL,'776',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4655,'<span data-type="title" id=toc-4707>١٤ </span>- يقال لهم: هذه النار التى كنتم بها تكذبون فى الدنيا.',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4656,'<span data-type="title" id=toc-4708>١٥ </span>- أبقيتم على إنكاركم، فهذا الذى تشاهدونه من النار سحراً؟ أم أنتم لا تبصرون! .',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4657,'<span data-type="title" id=toc-4709>١٦ </span>- ادخلوها وذوقوا حرَّها، فاصبروا على شدائدها أو لا تصبروا، فصبركم وعدمه سواء عليكم، إنما تلاقون اليوم جزاء ما كنتم تعملون فى الدنيا.',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4658,'<span data-type="title" id=toc-4710>١٧ </span>- إن المتقين فى جنات فسيحات، لا يحاط بوصفها، ونعيم عظيم كذلك.',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4659,'<span data-type="title" id=toc-4711>١٨ </span>- متنعمين بما أعطاهم ربهم، ووقاهم ربهم عذاب النار.',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4660,'<span data-type="title" id=toc-4712>١٩ </span>- يقال لهم: كلوا طعاماً هنيئاً، واشربوا شراباً سائغاً، جزاء بما كنتم تعملون فى الدنيا.',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4661,'<span data-type="title" id=toc-4713>٢٠ </span>- جالسين متكئين على أرائك مصفوفة، وزوجناهم بنساء بيض واسعات العيون حسانها.',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4662,'<span data-type="title" id=toc-4714>٢١ </span>- والذين أمنوا واستحقوا درجات عالية، واتبعتهم ذريتهم بإيمان، ولم يبلغوا درجات الآباء، ألحقنا بهم ذريتهم، لتقر أعينهم بهم، وما نقصناهم شيئاً من ثواب أعمالهم. ولا يحمل الآباء شيئاً من أخطاء ذرياتهم، لأن كل إنسان مرهون بعمله، لا يؤخذ به غيره.',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4663,'<span data-type="title" id=toc-4715>٢٢ </span>- وزدناهم بفاكهة كثيرة، ولحم مما يشتهون.',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4664,'<span data-type="title" id=toc-4716>٢٣ </span>- يتجاذبون فى الجنة - متوادين - كأساً مليئة بالشراب، لا يكون منهم بشربها كلام باطل، ولا عمل يستوجب الإثم.',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4665,'<span data-type="title" id=toc-4717>٢٤ </span>- ويطوف عليهم غلمان مُعَدُّون لخدمتهم، كأنهم فى الصفاء والبياض لؤلؤ مصون.',NULL,'777',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4666,'<span data-type="title" id=toc-4718>٢٥ </span>- وأقبل بعض أهل الجنة على بعض، يسأل كل صاحبه عن عظم ما هم فيه وسببه.',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4667,'<span data-type="title" id=toc-4719>٢٦ </span>-، ٢٧ - قالوا: إنا كنا قبل هذا النعيم بين أهلينا خائفين من عذاب الله، فَمَنَّ الله علينا برحمته ووقانا عذاب النار.',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4668,'<span data-type="title" id=toc-4720>٢٨ </span>- إنا كنا من قبل فى الدنيا نعبده. إنه - وحده - هو المحسن الواسع الرحمة.',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4669,'<span data-type="title" id=toc-4721>٢٩ </span>- فَدُمْ على ما أنت عليه من التذكير، فما أنت - بما أنعم الله عليك من النبوة ورجاحة العقل - بكاهن، تخبر بالغيب دون علم، ولا مجنون تقول ما لا تقصد.',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4670,'<span data-type="title" id=toc-4722>٣٠ </span>-، ٣١ - بل أيقولون هو شاعر، ننتظر به نزول الموت؟ ، قل تهديداً لهم، انتظروا فإنى معكم من المنتظرين عاقبة أمرى وأمركم.',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4671,'<span data-type="title" id=toc-4723>٣٢ </span>- أتأمرهم عقولهم بهذا القول المتناقض؟ ، فالكاهن والشاعر ذو فطنة وعقل، والمجنون لا عقل له، بل هم قوم مجاوزون الحد فى العناد.',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4672,'<span data-type="title" id=toc-4724>٣٣ </span>- أيقولون: اختلق محمد القرآن؟ بل هم لمكابرتهم لا يؤمنون.',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4673,'<span data-type="title" id=toc-4725>٣٤ </span>- فليأتوا بحديث مثل القرآن، إن كانوا صادقين فى قولهم: أن محمدا اختلقه.',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4674,'<span data-type="title" id=toc-4726>٣٥ </span>- أَخُلِقُوا من غير خالق. أم هم الذين خَلَقُوا أنفسهم، فلا يعترفون بخالق يعبدونه؟ .',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4675,'<span data-type="title" id=toc-4727>٣٦ </span>- أخلقوا السموات والأرض على هذا الصنع البديع؟ بل هم لا يوقنون بما يجب للخالق، فلهذا يشركون به.',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4676,'<span data-type="title" id=toc-4728>٣٧ </span>- أعندهم خزائن ربك يتصرفون فيها؟ ، بل أهم القاهرون المدبرون للأمور كما يشاءون؟ .',NULL,'778',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4677,'<span data-type="title" id=toc-4729>٣٨ </span>- بل ألهم مرقى يصعدون فيه إلى السماء، فيستمعون ما يقضي به الله؟ فليأت مستمعهم بحجة واضحة تصدق دعواه.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4678,'<span data-type="title" id=toc-4730>٣٩ </span>- بل ألله البنات كما تزعمون، ولكم البنين كما تحبون.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4679,'<span data-type="title" id=toc-4731>٤٠ </span>- بل أتسألهم شيئا من الأجر على تبليغ الرسالة، فهم لما يلحقهم من الغرامة مثقلون متبرمون.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4680,'<span data-type="title" id=toc-4732>٤١ </span>- بل أعندهم علم الغيب، فهم يكتبون منه ما شاءوا؟.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4681,'<span data-type="title" id=toc-4733>٤٢ </span>- بل أيريدون مكرا بك وإبطالا لرسالتك؟ ، فالذين كفروا هم الذين يحيق بهم مكرهم.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4682,'<span data-type="title" id=toc-4734>٤٣ </span>- أم لهم معبود غير الله يمنعهم من عذاب الله، تنزيها لله عما يشركون.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4683,'<span data-type="title" id=toc-4735>٤٤ </span>- وإن يشاهدوا جزءا من السماء ساقطا عليهم لعذابهم، يقولوا عنادا: هو سحاب مجتمع.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4684,'<span data-type="title" id=toc-4736>٤٥ </span>- فدعهم غير مكترث بهم، حتى يلاقوا يومهم الذي فيه يهلكون.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4685,'<span data-type="title" id=toc-4737>٤٦ </span>- يوم لا يدفع عنهم مكرهم شيئا من العذاب، ولا هم يجدون ناصرا.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4686,'<span data-type="title" id=toc-4738>٤٧ </span>- وإن للذين ظلموا عذابا غير العذاب الذي يهلكون به في الدنيا، ولكن أكثرهم لا يعلمون ذلك.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4687,'<span data-type="title" id=toc-4739>٤٨ </span>- واصبر لحكم ربك بإمهالهم، وعلى ما يلحقك من أذاهم، فإنك في حفظنا ورعايتنا، فلن يضرك كيدهم، وسبح بحمد ربك حين تقوم.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4688,'<span data-type="title" id=toc-4740>٤٩ </span>- وتخير جزءا من الليل فسبحه فيه، وسبحه وقت إدبار النجوم.',NULL,'779',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4689,'<span data-type="title" id=toc-4742>١ </span>-، ٢ - أقسم ب<span data-type="title" id=toc-4741>النجم </span>إذا هوى للغروب: ما عدل محمد عن طريق الحق وما اعتقد باطلا.',NULL,'780',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4690,'<span data-type="title" id=toc-4743>٣ </span>- وما يصدر نطقه فيما يتكلم به من القرآن عن هوى نفسه',NULL,'780',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4691,'<span data-type="title" id=toc-4744>٤ </span>- ما القرآن الذي ينطق به إلا وحي من الله يوحيه إليه.',NULL,'780',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4692,'<span data-type="title" id=toc-4745>٥ </span>- علَّمه هذا الوحى ملك شديد القوى.',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4693,'<span data-type="title" id=toc-4746>٦ </span>-، ٧ - ذو حصافة فى عقله ورأيه، فاستقام على صورته، وهو بالجهة العليا من السماء المقابلة للناظر.',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4694,'<span data-type="title" id=toc-4747>٨ </span>-، ٩ - ثم قرب جبريل منه، فزاد فى القرب، فكان دُنُوُّه قدر قوسين، بل أدنى من ذلك.',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4695,'<span data-type="title" id=toc-4748>١٠ </span>- فأوحى جبريل إلى عبد الله ورسوله ما أوحاه، وأنه أمر عظيم الشأن بعيد الأثر.',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4696,'<span data-type="title" id=toc-4749>١١ </span>- ما أنكر فؤاد محمد ما رآه بصره.',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4697,'<span data-type="title" id=toc-4750>١٢ </span>- أتكذِّبون رسول الله، فتجادلونه على ما يراه معاينة؟ .',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4698,'<span data-type="title" id=toc-4751>١٣ </span>-، ١٤، ١٥، ١٦، ١٧ - ولقد رأى محمد جبريل على صورته مرة أخرى، فى مكان لا يعلم علمه إلا الله، سماه «سدرة المنتهى» ، وأنبأ أن عنده جنة المأوى، إذ يغشاها ويغطيها من فضل الله ما لا يحيط به وصف، ما مال بصر محمد عما رآه، وما تجاوز ما أُمر برؤيته.',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4699,'<span data-type="title" id=toc-4752>١٨ </span>- لقد رأى كثيراً من آيات الله وعجائبه العظمى.',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4700,'<span data-type="title" id=toc-4753>١٩ </span>-، ٢٠ - أعلمتم ذلك ففكرتم فى شأن اللات والعزى، ومناة الثالثة الأخرى، التى اتخذتموها آلهة تعبدونها؟ .',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4701,'<span data-type="title" id=toc-4754>٢١ </span>- أقسَّمتم الأمر فجعلتم لأنفسكم الذكور، وجعلتم لله الإناث؟ .',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4702,'<span data-type="title" id=toc-4755>٢٢ </span>- تلك - إذن - قسمة جائرة، إذ تجعلون لله ما تكرهون.',NULL,'781',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4703,'<span data-type="title" id=toc-4756>٢٣ </span>- ما الأصنام إلا مجرد أسماء ليس فيها شئ من معنى الألوهية، سميتموها أنتم وآباؤكم بمقتضى أهوائكم الباطلة، ما أنزل الله بها من حُجَّة تُصدق دعواكم فيها، ما يتبعون إلا الظن وما تهواه النفوس المنحرفة عن الفطرة السليمة، ولقد جاءهم من ربهم ما فيه هدايتهم لو اتبعوه.',NULL,'782',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4704,'<span data-type="title" id=toc-4757>٢٤ </span>-، ٢٥ - بل ليس للإنسان ما تمناه من شفاعة هذه الأصنام أو غير ذلك مما تشتهيه نفسه، فلله - وحده - أمر الآخرة والدنيا جميعاً.',NULL,'782',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4705,'<span data-type="title" id=toc-4758>٢٦ </span>- وكثير من الملائكة فى السموات مع علو منزلتهم لا تغنى شفاعتهم شيئاً - ما - إلا بعد إذنه تعالى للشفيع ورضاه عن المشفوع له.',NULL,'782',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4706,'<span data-type="title" id=toc-4759>٢٧ </span>- إن الذين لا يؤمنون بالدار الآخرة ليصفون الملائكة بالأنوثة، فيقولون: الملائكة بنات الله.',NULL,'782',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4707,'<span data-type="title" id=toc-4760>٢٨ </span>- وما لهم بهذا القول من علم، ما يتبعون فيه إلا ظنهم الباطل، وإن الظن لا يغنى من الحق شيئاً.',NULL,'782',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4708,'<span data-type="title" id=toc-4761>٢٩ </span>- فانصرف عن هؤلاء الكافرين الذين أعرضوا عن القرآن ولم يكن همهم إلا الحياة الدنيا جاهدين فيما يصلحها.',NULL,'782',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4709,'<span data-type="title" id=toc-4762>٣٠ </span>- ذلك الذي يتبعونه فى عقائدهم وأعمالهم منتهى ما وصلوا إليه من العلم، إن ربك هو أعلم بمن أصر على الضلال، وهو أعلم بمن شأنه قبول الاهتداء.',NULL,'782',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4710,'<span data-type="title" id=toc-4763>٣١ </span>- ولله - وحده - ما فى السموات وما فى الأرض خلقاً وتدبيراً، ليجزى الضالين المسيئين بعملهم، ويجزى المهتدين المحسنين بالمثوبة الحسنى.',NULL,'783',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4711,'<span data-type="title" id=toc-4764>٣٢ </span>- الذين يجتنبون ما يكبر عقابه من الذنوب وما يعظم قبحه منها، لكن الصغائر من الذنوب يعفو الله عنها، إن ربك عظيم المغفرة، هو أعلم بأحوالكم، إذ خلقكم من الأرض، وإذ أنتم أجنة فى بطون أمهاتكم فى أطواركم المختلفة، فلا تصفوا أنفسكم بالتزكى مدحاً وتفاخراً، هو أعلم بمن اتقى، فزكت نفسه حقيقة بتقواه.',NULL,'783',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4712,'<span data-type="title" id=toc-4765>٣٣ </span>-، ٣٤، ٣٥ - أتأملت فرأيت الذى أعرض عن اتباع الحق، وأعطى شيئاً قليلاً من المال، وقطع العطاء؟ {أعنده علم الغيب فهو منكشف له عما يدفعه إلى التولى عن الحق والبخل بالمال؟',NULL,'783',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4713,'<span data-type="title" id=toc-4766>٣٦ </span>-، ٣٧، ٣٨ - بل ألم يخبَر بما فى صحف موسى وإبراهيم الذى بلغ الغاية فى الوفاء بما عاهد الله عليه: أنه لا تحمل نفس إثم نفس أخرى؟}',NULL,'783',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4714,'<span data-type="title" id=toc-4767>٣٩ </span>- وأنه ليس للإنسان إلا جزاء عمله.',NULL,'783',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4715,'<span data-type="title" id=toc-4768>٤٠ </span>- وأن عمله سوف يعلن، فيُرى يوم القيامة تشريفاً للمحسن وتوبيخاً للمسئ.',NULL,'783',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4716,'<span data-type="title" id=toc-4769>٤١ </span>- ثم يجزى الإنسان على عمله الجزاء الأوفر.',NULL,'783',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4717,'<span data-type="title" id=toc-4770>٤٢ </span>- وأن إلى ربك - لا إلى غيره - المعاد.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4718,'<span data-type="title" id=toc-4771>٤٣ </span>- وأنه هو - وحده - بسط أسارير الوجوه وقبضها، وخلق أسباب البسط والقبض.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4719,'<span data-type="title" id=toc-4772>٤٤ </span>- وأنه هو - وحده - سلب الحياة ووهبها.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4720,'<span data-type="title" id=toc-4773>٤٥ </span>-، ٤٦ - وأنه خلق الزوجين الذكر والأنثى - من الإنسان والحيوان - من نطفة دافقة.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4721,'<span data-type="title" id=toc-4774>٤٧ </span>- وأن عليه الإحياء بعد الإماتة.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4722,'<span data-type="title" id=toc-4775>٤٨ </span>- وأنه - هو - أعطى ما يكفى، وأرضى بما يقتنى ويدخر.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4723,'<span data-type="title" id=toc-4776>٤٩ </span>- وأنه - هو - رب هذا الكوكب العظيم المسمى بالشعرى.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4724,'<span data-type="title" id=toc-4777>٥٠ </span>-، ٥١ - وأنه أهلك عاداً الأولى قوم هود، وأهلك ثمود قوم صالح، فما أبقى عليهم.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4725,'<span data-type="title" id=toc-4778>٥٢ </span>- وأهلك قوم نوح من قبل هلاك عاد وثمود، إنهم كانوا - هم - أكثر ظلماً وأشد طغياناً من عاد وثمود.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4726,'<span data-type="title" id=toc-4779>٥٣ </span>- والقرى المنقلبة بقوم لوط هو الذى قلبها.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4727,'<span data-type="title" id=toc-4780>٥٤ </span>-، ٥٥ - فأحاط بها من العذاب ما أحاط، فبأى نعمة من نعم ربك ترتاب!',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4728,'<span data-type="title" id=toc-4781>٥٦ </span>- هذا القرآن نذير من جنس النذر الأولى التى أنذرت بها الأمم السابقة.',NULL,'784',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4729,'<span data-type="title" id=toc-4782>٥٧ </span>-، ٥٨ - قربت القيامة، ليس لها من دون الله من يكشف عن وقت وقوعها.',NULL,'785',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4730,'<span data-type="title" id=toc-4783>٥٩ </span>-، ٦٠، ٦١ - أتجحدون كل حق؟ ، فمن هذا القرآن تعجبون إنكاراً، وتضحكون استهزاءً وسخريةً، ولا تبكون كما يفعل الموقنون، وأنتم لاهون متكبرون؟!',NULL,'785',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4731,'<span data-type="title" id=toc-4784>٦٢ </span>- فاسجدوا لله الذى أنزل القرآن هدى للناس، وأفْرِدوه بالعبادة جل جلاله.',NULL,'785',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4732,'<span data-type="title" id=toc-4786>١ </span>- دنت القيامة وسينشق<span data-type="title" id=toc-4785> القمر </span>لا محالة.',NULL,'786',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4733,'<span data-type="title" id=toc-4787>٢ </span>- وإن ير الكفار معجزة عظيمة يُعرضوا عن الإيمان بها، ويقولون: هى سحر دائم متتابع.',NULL,'786',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4734,'<span data-type="title" id=toc-4788>٣ </span>- وكذَّبوا الرسل واتبعوا ما تزيِّنُهُ لهم أهواؤهم، وكل أمر منته إلى غاية يستقر عليها.',NULL,'786',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4735,'<span data-type="title" id=toc-4789>٤ </span>- وأقسم لقد جاء الكفار من أخبار الأمم السابقة والحقائق الكونية ما فيه كفاية لزجرهم.',NULL,'786',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4736,'<span data-type="title" id=toc-4790>٥ </span>- هذا الذى جاءهم حكمة عظيمة بالغة غايتها. فأى نفع تُفيد النُّذر من انصرف عنها؟ .',NULL,'786',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4737,'<span data-type="title" id=toc-4791>٦ </span>- فأعرض - يا محمد - عن هؤلاء الكفار، وانتظر يوم يدعو داعى الله إلى أمر شديد تنكره النفوس.',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4738,'<span data-type="title" id=toc-4792>٧ </span>- خاضعة أبصارهم من شدة الهول، يخرجون من القبور كأنهم - فى الكثرة والسرعة - جراد منتشر! {',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4739,'<span data-type="title" id=toc-4793>٨ </span>- مسرعين إلى الداع، ينظرون إليه فى ذل وخضوع، لا يتحول بصرهم عنه، يقول الكافرون يوم القيامة: هذا يوم صعب شديد.',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4740,'<span data-type="title" id=toc-4794>٩ </span>- كذَّبت قبل كفار مكة قوم نوح، فكذَّبوا نوحاً - عبدنا ورسولنا - ورموه بالجنون، وحالوا - بأنواع الأذى والتخويف - بينه وبين تبليغ الرسالة.',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4741,'<span data-type="title" id=toc-4795>١٠ </span>- فدعا نوح ربه إنى مغلوب من قومى، فانتقم لى منهم.',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4742,'<span data-type="title" id=toc-4796>١١ </span>-، ١٢ - ففتحنا أبواب السماء بماء منصب كثير متتابع، وشققنا الأرض عيوناً متفجرة بالماء. فالتقى ماء السماء وماء الأرض على إهلاكهم الذى قدَّره الله تعالى.',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4743,'<span data-type="title" id=toc-4797>١٣ </span>-، ١٤ - وحملنا نوحاً على سفينة من خشب، وخيوط من ليف تشد ألواحها، تجرى على الماء بحفظنا، جزاء لنوح الذى استمر قومه على تكذيب دعوته.',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4744,'<span data-type="title" id=toc-4798>١٥ </span>- ولقد تركنا حادثة إغراق الكافرين وإنجاء المؤمنين عظة، فهل من مُتَّعظ.',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4745,'<span data-type="title" id=toc-4799>١٦ </span>- فعلى أى حال كان عذابى وإنذارى للمخالفين؟} {',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4746,'<span data-type="title" id=toc-4800>١٧ </span>- وأقسم لقد سهَّلنا القرآن للتذكرة والاتعاظ، فهل من متعظ؟ .',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4747,'<span data-type="title" id=toc-4801>١٨ </span>- كذبت عاد رسولهم - هوداً - فعلى أى حال كان عذابى وإنذارى للمخالفين؟} !',NULL,'787',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4748,'<span data-type="title" id=toc-4802>١٩ </span>-، ٢٠ - إنا سلّطنا عليهم ريحاً باردة مدوية فى يوم شؤم دائم، تقلع الناس من أماكنهم، وترمى بهم على الأرض صرعى، كأنهم أصول نخل منقلع من مغارسه.',NULL,'788',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4749,'<span data-type="title" id=toc-4803>٢١ </span>- فعلى أى حال كان عذابى وإنذارى للمخالفين! {',NULL,'788',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4750,'<span data-type="title" id=toc-4804>٢٢ </span>- ولقد سهلنا القرآن للعظة والاعتبار، فهل من متعظ؟',NULL,'788',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4751,'<span data-type="title" id=toc-4805>٢٣ </span>- كذَّبت ثمود بإنذارات نبيِّهم صالح.',NULL,'788',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4752,'<span data-type="title" id=toc-4806>٢٤ </span>- فقالوا: أبشراً من عامتنا لا عصبية له نتبعه؟ ، إنا إذا اتبعناه لفى بُعْد عن الحق وجنون.',NULL,'788',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4753,'<span data-type="title" id=toc-4807>٢٥ </span>- أَاُنْزِلَ الوحى عليه من بيننا وفينا من هو أحق منه؟ بل هو كثير الكذب، منكر للنعمة.',NULL,'788',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4754,'<span data-type="title" id=toc-4808>٢٦ </span>- سيعلمون قريباً يوم ينزل بهم العذاب: مَنْ الكذَّاب المنكر للنعمة، أهم أم صالح رسولهم؟',NULL,'788',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4755,'<span data-type="title" id=toc-4809>٢٧ </span>- إنا مرسلو الناقة آية لرسولنا - صالح - امتحاناً لهم، فانتظرهم وتبصَّر ما هم فاعلون، واصبر على أذاهم حتى يأتى أمر الله.',NULL,'788',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4756,'<span data-type="title" id=toc-4810>٢٨ </span>- ونبئهم أن الماء مقسوم بينهم وبين الناقة، كل نصيب يحضره صاحبه فى يومه.',NULL,'788',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4757,'<span data-type="title" id=toc-4811>٢٩ </span>-، ٣٠ - فنادوا صاحبهم، فتهيأ لعقر الناقة فعقروها. فعلى أى حال كان عذابى وإنذارى للمخالفين؟}',NULL,'788',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4758,'<span data-type="title" id=toc-4812>٣١ </span>- إنا سلّطنا عليهم صيحة واحدةً فكانوا بها كشجر يابس يجمعه من يريد اتخاذ حظيرة!!',NULL,'789',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4759,'<span data-type="title" id=toc-4813>٣٢ </span>- ولقد سهّلنا القرآن للعظة والاعتبار، فهل من متعظ؟ .',NULL,'789',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4760,'<span data-type="title" id=toc-4814>٣٣ </span>- كذبت قوم لوط بإنذارات رسولهم.',NULL,'789',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4761,'<span data-type="title" id=toc-4815>٣٤ </span>-، ٣٥ - إنا أرسلنا عليهم ريحاً شديدة ترميهم بالحصى، إلا آل لوط المؤمنين نجَّيناهم من هذا العذاب آخر الليل، إنعاماً عليهم من عندنا، كذلك الإنعام العظيم نجزى به من شكر نعمتنا بالإيمان والطاعة.',NULL,'789',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4762,'<span data-type="title" id=toc-4816>٣٦ </span>- ولقد خوَّف لوط قومه أخذتنا الشديدة، فشكوا فى إنذاراته تكذيباً له.',NULL,'789',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4763,'<span data-type="title" id=toc-4817>٣٧ </span>- ولقد أرادوا منه تمكينهم من ضيفه، فمحونا أبصارهم جزاء ما أرادوا، فقل لهم - تهكماً -: تجرعوا عذابى وإنذاراتى.',NULL,'789',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4764,'<span data-type="title" id=toc-4818>٣٨ </span>-، ٣٩ - ولقد فاجأهم فى الصباح الباكر عذاب ثابت دائم، فقيل لهم: تجرعوا عذابى وإنذاراتى.',NULL,'789',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4765,'<span data-type="title" id=toc-4819>٤٠ </span>- ولقد سهَّلنا القرآن للعظة والاعتبار، فهل من متعظ؟',NULL,'789',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4766,'<span data-type="title" id=toc-4820>٤١ </span>- ولقد جاء آل فرعون الإنذارات المتتابعة.',NULL,'789',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4767,'<span data-type="title" id=toc-4821>٤٢ </span>- كذبوا بآياتنا ومعجزاتنا التى جاءت على يد رسلنا، فأهلكناهم إهلاك قوى لا يُغْلَب. عظيم القدرة.',NULL,'789',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4768,'<span data-type="title" id=toc-4822>٤٣ </span>- أأنتم - أيها الكفار - أقوى من أولئكم الأقوام السابقين الذين أهلكوا؟ . بل ألكم براءة من العذاب فيما نزل من الكتب السماوية؟',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4769,'<span data-type="title" id=toc-4823>٤٤ </span>- أيقول هؤلاء الكفار: نحن جمع مؤتلف ممتنع على أعدائه لا يغلب؟ .',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4770,'<span data-type="title" id=toc-4824>٤٥ </span>- سيغلب هذا الجمع، ويفرون مولين الأدبار.',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4771,'<span data-type="title" id=toc-4825>٤٦ </span>- بل القيامة موْعد عذابهم، والقيامة أعظم داهية وأقسى مرارة.',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4772,'<span data-type="title" id=toc-4826>٤٧ </span>- إن المجرمين من هؤلاء وأولئك فى هلاك وجحيم مستعر.',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4773,'<span data-type="title" id=toc-4827>٤٨ </span>- يوم يُجَرُّون فى النار على وجوههم يقال لهم: قاسوا آلام جهنم وحرارتها.',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4774,'<span data-type="title" id=toc-4828>٤٩ </span>- إنا خلقنا كل شئ، خلقناه بتقدير على ما تقتضيه الحكمة.',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4775,'<span data-type="title" id=toc-4829>٥٠ </span>- وما أَمْرُنا لشئ أردناه إلا كلمة واحدة هى أن نقول له: «كن» فيكون فى سرعة الاستجابة كلمح البصر.',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4776,'<span data-type="title" id=toc-4830>٥١ </span>- ولقد أهلكنا أشباهكم فى الكفر، فهل من متعظ؟ .',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4777,'<span data-type="title" id=toc-4831>٥٢ </span>- وكل شئ فعلوه فى الدنيا مكتوب فى الصحف. مقيد عليهم.',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4778,'<span data-type="title" id=toc-4832>٥٣ </span>- وكل صغير وكبير من الأعمال مكتوب لا يغيب منه شئ.',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4779,'<span data-type="title" id=toc-4833>٥٤ </span>- إن المتقين فى جنات عظيمة الشأن، وأنها متعددة الأنواع.',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4780,'<span data-type="title" id=toc-4834>٥٥ </span>- فى مجلس حق لا لغو فيه ولا تأثيم عند مليك عظيم القدرة.',NULL,'790',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4781,'<span data-type="title" id=toc-4836>١ </span>-، ٢ -<span data-type="title" id=toc-4835> الرحمن</span>. علَّم الإنسان القرآن ويسَّره له.',NULL,'791',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4782,'<span data-type="title" id=toc-4837>٣ </span>-، ٤ - أوجد الإنسان. علمه الإبانة عما فى نفسه تمييزاً له عن غيره.',NULL,'791',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4783,'<span data-type="title" id=toc-4838>٥ </span>- الشمس والقمر يجريان فى بروجهما بحساب وتقدير لا إخلال فيه.',NULL,'791',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4784,'<span data-type="title" id=toc-4839>٦ </span>- والنبات الذى لا ساق له، والشجر الذى يقوم على ساق يخضعان لله تعالى فى كل ما يريد بهما.',NULL,'791',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4785,'<span data-type="title" id=toc-4840>٧ </span>-، ٨ - والسماء خلقها مرفوعة، وشرع العدل لئلا تتجاوزوا الحد.',NULL,'791',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4786,'<span data-type="title" id=toc-4841>٩ </span>- وأقيموا الوزن بالعدل فى كل معاملاتكم، ولا تنقصوا الميزان.',NULL,'791',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4787,'<span data-type="title" id=toc-4842>١٠ </span>- والأرض بسطها ومهَّدها للخلائق ينتفعون بها.',NULL,'791',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4788,'<span data-type="title" id=toc-4843>١١ </span>- فى الأرض أنواع كثيرة من الفاكهة، وفيها النخل ذات الأوعية التى فيها الثمر.',NULL,'791',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4789,'<span data-type="title" id=toc-4844>١٢ </span>- وفيها الحب ذو القشر رزقاً لكم ولأنعامكم، وفيها كل نبْت طيب الرائحة.',NULL,'792',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4790,'<span data-type="title" id=toc-4845>١٣ </span>- فبأى نعمة من نعم ربكما تجحدان أيها الثقلان؟ {.',NULL,'792',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4791,'<span data-type="title" id=toc-4846>١٤ </span>-، ١٥ - خلق جنس الإنسان من طين يابس غير مطبوخ كالخزف، وخلق جنس الجان من لهيب خالص من نار} .',NULL,'792',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4792,'<span data-type="title" id=toc-4847>١٦ </span>- فبأى نعمة من نعم ربكما تجحدان؟ {.',NULL,'792',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4793,'<span data-type="title" id=toc-4848>١٧ </span>- رب مشرقى الشمس فى الصيف والشتاء، ورب مغربيها فيهما.',NULL,'792',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4794,'<span data-type="title" id=toc-4849>١٨ </span>- فبأى نعمة من نعم ربكما تجحدان؟}',NULL,'792',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4795,'<span data-type="title" id=toc-4850>١٩ </span>-، ٢٠ - أرسل الله البحرين العذب والملح يتجاوران ويتماسّ سطوحهما، بينهما حاجز من قدرة الله لا يطغى أحدهما على الآخر فيمتزجان.',NULL,'792',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4796,'<span data-type="title" id=toc-4851>٢١ </span>- فبأى نعمة من نعم ربكما تجحدان؟ {',NULL,'792',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4797,'<span data-type="title" id=toc-4852>٢٢ </span>- يخرج منهما اللؤلؤ والمرجان، تتخذون منهما حلية تلبسونها.',NULL,'792',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4798,'<span data-type="title" id=toc-4853>٢٣ </span>- فبأى نعمة من نعم ربكما تجحدان؟}',NULL,'792',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4799,'<span data-type="title" id=toc-4854>٢٤ </span>- وله السفن المصنوعات بأيديكم الجاريات فى البحر، العظيمة كالجبال الشاهقة.',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4800,'<span data-type="title" id=toc-4855>٢٥ </span>- فبأى نعمة من نعم ربكما تجحدان؟ {',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4801,'<span data-type="title" id=toc-4856>٢٦ </span>-، ٢٧ - كل من على الأرض زائل ويبقى الله صاحب العظمة والإنعام.',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4802,'<span data-type="title" id=toc-4857>٢٨ </span>- فبأى نعمة من نعم ربكما تجحدان؟} .',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4803,'<span data-type="title" id=toc-4858>٢٩ </span>- يسأل الله جميع من فى السموات والأرض حاجاتهم، كل وقت هو فى شأن، يعز ويذل، ويعطى ويمنع.',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4804,'<span data-type="title" id=toc-4859>٣٠ </span>- فبأى نعمة من نعم ربكما تجحدان؟ {.',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4805,'<span data-type="title" id=toc-4860>٣١ </span>- فسنتفرغ لحسابكم يوم القيامة أيها الجن والإنس.',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4806,'<span data-type="title" id=toc-4861>٣٢ </span>- فبأى نعمة من نعم ربكما تجحدان؟} .',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4807,'<span data-type="title" id=toc-4862>٣٣ </span>- يا معشر الجن والإنس إن استطعتم أن تخرجوا من جوانب السموات والأرض هاربين فاخرجوا، لا تستطيعون الخروج إلا بقوة وقهر، ولن يكون لكم ذلك.',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4808,'<span data-type="title" id=toc-4863>٣٤ </span>- فبأى نعمة من نعم ربكما تجحدان؟!',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4809,'<span data-type="title" id=toc-4864>٣٥ </span>- يصب عليكما لهب من نار ونحاس مذاب، فلا تقدران على دفع هذا العذاب.',NULL,'793',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4810,'<span data-type="title" id=toc-4865>٣٦ </span>- فبأى نعمة من نعم ربكما تجحدان؟ {.',NULL,'794',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4811,'<span data-type="title" id=toc-4866>٣٧ </span>- فإذا انشقت السماء فكانت حمراء كدرة كالزيت المحترق.',NULL,'794',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4812,'<span data-type="title" id=toc-4867>٣٨ </span>- فبأى نعمة من نعم ربكما تجحدان؟}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4813,'<span data-type="title" id=toc-4868>٣٩ </span>- فيومئذ تنشق السماء فلا يسأل عن ذنبه إنس ولا جن.',NULL,'794',NULL,NULL,'0');
