INSERT INTO [b836] VALUES (4816,'<span data-type="title" id=toc-4871>٤٢ </span>- فبأى نعمة من نعم ربكما تجحدان؟}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4817,'<span data-type="title" id=toc-4872>٤٣ </span>-، ٤٤ - يقال - تقريعا -: هذه جهنم التى يكذب بها المجرمون منكم، يترددون بين نارها وبين ماء متناه فى الحرارة.',NULL,'794',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4818,'<span data-type="title" id=toc-4873>٤٥ </span>- فبأى نعمة من نعم ربكما تجحدان؟ {',NULL,'794',NULL,NULL,'0');
