INSERT INTO [b836] VALUES (4848,'<span data-type="title" id=toc-4903>٧٥ </span>- فبأى نعمة من نعم ربكما تجحدان؟ {',NULL,'796',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4849,'<span data-type="title" id=toc-4904>٧٦ </span>- متكئين على فُرش ذوات أغطية خضر وطنافس حسان عجيبة الصنع.',NULL,'796',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4850,'<span data-type="title" id=toc-4905>٧٧ </span>- فبأى نعمة من نعم ربكما تجحدان؟}',NULL,'796',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4851,'<span data-type="title" id=toc-4906>٧٨ </span>- تعالى وتنزَّه اسم ربك صاحب العظمة والإنعام.',NULL,'796',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4852,'<span data-type="title" id=toc-4908>١ </span>-، ٢، ٣ - إذا وقعت القيامة، لا تكون نفس مكذبة بوقوعها، هى خافضة للأشقياء رافعة للسعداء.',NULL,'797',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4853,'<span data-type="title" id=toc-4909>٤ </span>-، ٥، ٦ - إذا زُلزلت الأرض واهتزت اهتزازاً شديداً، وفتتت الجبال تفتيتاً دقيقاً، فصارت غباراً متطايراً.',NULL,'797',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4854,'<span data-type="title" id=toc-4910>٧ </span>- وصرتم جميعاً فى هذا اليوم بأعمالكم أصنافاً ثلاثة.',NULL,'797',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4855,'<span data-type="title" id=toc-4911>٨ </span>-، ٩ - فأصحاب اليمين أهل المنزلة السنية ما أعظم مكانتهم، وأصحاب الشمال أهل المنزلة الدنية ما أسوأ حالهم.',NULL,'798',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4856,'<span data-type="title" id=toc-4912>١٠ </span>-، ١١، ١٢ - والسابقون إلى الخيرات فى الدنيا هم السابقون إلى الدرجات فى الآخرة، أولئك هم المقربون عند الله، يدخلهم ربهم فى جنات النعيم.',NULL,'798',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4857,'<span data-type="title" id=toc-4913>١٣ </span>-، ١٤ - هؤلاء المقربون جماعة كثيرة من الأمم السابقة وأنبيائهم، وقليل من أمة محمد بالنسبة إليهم.',NULL,'798',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4858,'<span data-type="title" id=toc-4914>١٥ </span>-، ١٦ - على سرر منسوجة بالجواهر النفيسة. مضطجعين عليها فى راحة واستقرار. متقابلة وجوههم زيادة فى المحبة.',NULL,'798',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4859,'<span data-type="title" id=toc-4915>١٧ </span>-، ١٨ - يدور عليهم للخدمة ولدان باقون أبداً على هذا الوصف بأقداح وأباريق مملوءة من شراب الجنة، وبكأس مملوءة خمراً من عيون جارية.',NULL,'798',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4860,'<span data-type="title" id=toc-4916>١٩ </span>- لا يُصيبهم بشربها صداع يصرفهم عنها ولا تذهب عقولهم.',NULL,'798',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4861,'<span data-type="title" id=toc-4917>٢٠ </span>-، ٢١ - وفاكهة من أى نوع يختارونه ويرونه، ولحم طير مما ترغب فيه نفوسهم.',NULL,'798',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4862,'<span data-type="title" id=toc-4918>٢٢ </span>-، ٢٣، ٢٤ - ونساء ذوات عيون واسعة. كأمثال اللؤلؤ المصون فى صدفه صفاء ورونقاً. يعطون هذا الجزاء بما كانوا يعملون من الصالحات فى الدنيا.',NULL,'798',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4863,'<span data-type="title" id=toc-4919>٢٥ </span>-، ٢٦ - لا يسمعون فى الجنة كلاماً لا ينفع، ولا حديثاً يأثم سامعه إلا قول بعضهم لبعض: سلاماً سلاماً.',NULL,'798',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4864,'<span data-type="title" id=toc-4920>٢٧ </span>- وأصحاب اليمين لا يعلم أحد ما جزاء أصحاب اليمين.',NULL,'799',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4865,'<span data-type="title" id=toc-4921>٢٨ </span>-، ٢٩، ٣٠، ٣١، ٣٢، ٣٣، ٣٤ - فى شجر من النبق مقطوع شوكه، وشجر من الموز متراكب ثمره بعضه فوق بعض، وظل منبسط لا يذهب، وماء منصب فى آنيتهم حيث شاءوه، وفاكهة كثيرة الأنواع والأصناف لا مقطوعة فى وقت من الأوقاف، ولا ممنوعة عمن يُريدها، وفرش عالية ناعمة.',NULL,'799',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4866,'<span data-type="title" id=toc-4922>٣٥ </span>-، ٣٦، ٣٧، ٣٨ - إنا ابتدأنا خلق الحور العين ابتداءً، فخلقناهن أبكاراً محببات إلى أزواجهن متقاربات فى السن، مهيئات لنعيم أصحاب اليمين.',NULL,'799',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4867,'<span data-type="title" id=toc-4923>٣٩ </span>-، ٤٠ - أصحاب اليمين جماعة كثيرة من الأمم السابقة، وجماعة كثيرة من أمة محمد.',NULL,'799',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4868,'<span data-type="title" id=toc-4924>٤١ </span>- وأصحاب الشمال لا يدرى أحد ما فيه أصحاب الشمال من العذاب.',NULL,'799',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4869,'<span data-type="title" id=toc-4925>٤٢ </span>-، ٤٣، ٤٤ - فى ريح حارة تنفذ فى المسام وتحيط بهم، وماء مُتَناه فى الحرارة يشربونه ويصب على رءوسهم، وفى ظل من دخان حار شديد السواد. لا بارد يخفف حرارة الجو، ولا كريم يعود عليهم بالنفع إذا استنشقوه.',NULL,'799',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4870,'<span data-type="title" id=toc-4926>٤٥ </span>- إنهم كانوا قبل هذا العذاب مسرفين فى الاستمتاع بنعيم الدنيا. لاهين عن طاعة الله تعالى.',NULL,'799',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4871,'<span data-type="title" id=toc-4927>٤٦ </span>- وكانوا يصمِّمون دائماً على الذنب العظيم الجرم. حيث أقسموا بالله جهد أيمانهم لا يبعث الله من يموت.',NULL,'799',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4872,'<span data-type="title" id=toc-4928>٤٧ </span>- وكانوا يقولون - إنكاراً للإعادة -: أنُبعث إذا متنا وصار بعض أجسامنا تراباً وبعضها عظاماً بالية أئنا لعائدون إلى الحياة ثانياً؟ .',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4873,'<span data-type="title" id=toc-4929>٤٨ </span>- أنبعث - نحن - وآباؤنا الأقدمون الذين صاروا تراباً متفرقاً ضالا فى الأرض.',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4874,'<span data-type="title" id=toc-4930>٤٩ </span>-، ٥٠ - قل - لهم - رداً لإنكارهم: إن الأولين من الأمم والآخرين الذين أنتم من جملتهم لمجموعون إلى وقت يوم مُعَيَّن لا يتجاوزونه.',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4875,'<span data-type="title" id=toc-4931>٥١ </span>-، ٥٢، ٥٣ - ثم إنكم أيها الخارجون المنحرفون عن سبيل الهدى - المكذبون بالبعث - لآكلون فى جهنم من شجر هو الزَّقوم، فمالئون من هذا الشجر بطونكم من شدة الجوع.',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4876,'<span data-type="title" id=toc-4932>٥٤ </span>-، ٥٥ - فشاربون على ما تأكلون من هذا الشجر ماء مُتَنَاهياً فى الحرارة لا يروى ظمأ، فشاربون بكثرة كشرب الإبل العطاش التى لا تروى بشرب الماء.',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4877,'<span data-type="title" id=toc-4933>٥٦ </span>- هذا الذى ذكر من ألوان العذاب هو ما أعد قِرى لهم يوم الجزاء.',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4878,'<span data-type="title" id=toc-4934>٥٧ </span>- نحن ابتدأنا خلقكم من عدم، فهلا تقرون بقدرتنا على إعادتكم حين بعثكم؟ .',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4879,'<span data-type="title" id=toc-4935>٥٨ </span>-، ٥٩ - أفرأيتم ما تقذفونه فى الأرحام من النطف؟ أأنتم تقدرونه وتتعهدونه فى أطواره حتى يصير بشراً، أم نحن المقدرون له؟',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4880,'<span data-type="title" id=toc-4936>٦٠ </span>-، ٦١ - نحن قضينا بينكم بالموت، وجعلنا لموتكم وقتاً معيَّناً، وما نحن بمغلوبين على أن نبدل صوركم بغيرها، وننشئكم فى خلق وصور لا تعهدونها.',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4881,'<span data-type="title" id=toc-4937>٦٢ </span>- ولقد أيقنتم أن الله أنشأكم النشأة الأولى، فهلا تتذكرون أن مَن قدر عليها فهو على النشأة الأخرى أقدر؟',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4882,'<span data-type="title" id=toc-4938>٦٣ </span>-، ٦٤ - أفرأيتم ما تبذرونه من الحب فى الأرض؟ أأنتم تنبتونه أم نحن المنبتون له وحدنا؟ .',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4883,'<span data-type="title" id=toc-4939>٦٥ </span>-، ٦٦، ٦٧ - لو نشاء لصيَّرنا هذا النبات هشيماً متكسراً قبل أن يبلغ نضجه فلا تزالون تتعجبون من سوء ما أصابه قائلين: إنا لملزمون الغرم بعد جهدنا فيه. بل نحن سيئو الحظ، محرومون من الرزق.',NULL,'800',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4884,'<span data-type="title" id=toc-4940>٦٨ </span>-، ٦٩ - أفرأيتم الماء العذب الذى تشربون منه، أأنتم أنزلتموه من السحاب أم نحن المنزلون له رحمة بكم؟',NULL,'801',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4885,'<span data-type="title" id=toc-4941>٧٠ </span>- لو نشاء صيَّرناه مالحاً لا يساغ، فهلا تشكرون الله أن جعله عذباً سائغاً؟ .',NULL,'801',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4886,'<span data-type="title" id=toc-4942>٧١ </span>-، ٧٢ - أفرأيتم النار التى توقدون؟ أأنتم أنبتم شجرتها وأودعتم فيها النار أم نحن المنشئون لها كذلك؟ .',NULL,'801',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4887,'<span data-type="title" id=toc-4943>٧٣ </span>- نحن جعلنا هذه النار تذكيراً لنار جهنم عند رؤيتها، ومنفعة للنازلين بالقفر ينتفعون بها فى طهو طعامهم وتدفئتهم.',NULL,'801',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4888,'<span data-type="title" id=toc-4944>٧٤ </span>- فدم على التسبيح بذكر اسم ربك العظيم، تنزيهاً وشكراً له على هذه النعم الجليلة.',NULL,'801',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4889,'<span data-type="title" id=toc-4945>٧٥ </span>-، ٧٦ - فأقسم - حقا - بمساقط النجوم عند غروبها آخر الليل أوقات التهجد والاستغفار، وإنه لقسم - لو تفكرون فى مدلوله - عظيم الخطر بعيد الأثر.',NULL,'801',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4890,'<span data-type="title" id=toc-4946>٧٧ </span>-، ٧٨ - إنه لقرآن كثير المنافع فى اللوح المحفوظ مصون لا يطلع عليه غير المقربين من الملائكة.',NULL,'801',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4891,'<span data-type="title" id=toc-4947>٧٩ </span>-، ٨٠ - لا يمس القرآن الكريم إلا المطهرون من الأدناس والأحداث. وهو منزَّل من عند الله رب الخلق أجمعين.',NULL,'801',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4892,'<span data-type="title" id=toc-4948>٨١ </span>- أأنتم متهاونون بقدر هذا القرآن العظيم؟!',NULL,'802',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4893,'<span data-type="title" id=toc-4949>٨٢ </span>- وتجعلون بدل شكر رزقكم أنكم تكذبونه.',NULL,'802',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4894,'<span data-type="title" id=toc-4950>٨٣ </span>-، ٨٤، ٨٥ - فهلا إذا بلغت روح أحدكم عند الموت مجرى النفس، وأنتم حين بلوغ الروح الحلقوم حول المحتَضِر تنظرون إليه، ونحن أقرب إلى المحتَضَر وأعلم بحاله منكم، ولكن لا تدركون ذلك ولا تحسونه.',NULL,'802',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4895,'<span data-type="title" id=toc-4951>٨٦ </span>-، ٨٧ - فهلا - إن كنتم غير خاضعين لربوبيتنا - تردون روح المحتضر إليه إن كنتم صادقين فى أنكم ذوو قوة لا تقهر.',NULL,'802',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4896,'<span data-type="title" id=toc-4952>٨٨ </span>-، ٨٩ - فأما إن كان المحتضر من السابقين المقربين فمآله راحة ورحمة ورزق طيب وجنة ذات نعيم.',NULL,'802',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4897,'<span data-type="title" id=toc-4953>٩٠ </span>-، ٩١ - وأما إن كان من أصحاب اليمين فيقال له تحية وتكريماً: سلام لك من إخوانك أصحاب اليمين.',NULL,'802',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4898,'<span data-type="title" id=toc-4954>٩٢ </span>-، ٩٣، ٩٤ - وأما إن كان من أصحاب الشمال المكذبين الضالين، فله نزل وقرى أعد له من ماء حار تناهت حرارته، وإحراق بنار شديدة الاتقاد.',NULL,'802',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4899,'<span data-type="title" id=toc-4955>٩٥ </span>- إن هذا الذى ذكر فى هذه السورة الكريمة لهو عين اليقين الثابت الذى لا يداخله شك.',NULL,'802',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4900,'<span data-type="title" id=toc-4956>٩٦ </span>- فدم على التسبيح بذكر اسم ربك العظيم، تنزيهاً له وشكراً على آلائه.',NULL,'802',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4901,'<span data-type="title" id=toc-4958>١ </span>- نزه الله تعالى ما فى السموات والأرض من الإنسان والحيوان والجماد، وهو الغالب الذى يصرِّف الأمور بما تقتضيه الحكمة.',NULL,'803',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4902,'<span data-type="title" id=toc-4959>٢ </span>- لله ملك السموات والأرض - لا لغيره - يتصرف فى كل ما فيهما. يفعل الإحياء والإماتة، وهو على كل شئ تام القدرة.',NULL,'803',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4903,'<span data-type="title" id=toc-4960>٣ </span>- هو الموجود قبل كل شئ، والباقى بعد فناء كل شئ، والظاهر فى كل شئ، فكل شئ له آية، والباطن فلا تُدركه الأبصار، وهو بكل شئ ظاهر أو باطن تام العلم.',NULL,'803',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4904,'<span data-type="title" id=toc-4961>٤ </span>- هو الذى خلق السموات والأرض وما بينهما فى ستة أيام ثم استولى على العرش بتدبير ملكه، يعلم كل ما يغيب فى الأرض وما يخرج منها، وكل ما ينزل من السماء وما يصعد إليها، وهو عليم بكم محيط بشئونكم فى أى مكان كنتم، والله بما تعملون بصير، مطَّلع لا يخفى عليه شئ من ذلك.',NULL,'804',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4905,'<span data-type="title" id=toc-4962>٥ </span>- لله - وحده - ملك السموات والأرض، وإليه تعالى ترجع أمور خلقه، وتنتهى مصائرهم.',NULL,'804',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4906,'<span data-type="title" id=toc-4963>٦ </span>- يُدخل من ساعات الليل فى النهار، ويُدخل من ساعات النهار فى الليل، فتختلف أطوالها، وهو العليم بمكنونات الصدور وما تُضمره القلوب.',NULL,'804',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4907,'<span data-type="title" id=toc-4964>٧ </span>- صدقوا بالله ورسوله، وأنفقوا فى سبيل الله من المال الذى جعلكم الله خُلفاء فى التصرف فيه، فالذين آمنوا منكم بالله ورسوله - وأنفقوا مما استخلفهم فيه، لهم بذلك عند الله ثواب كبير.',NULL,'804',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4908,'<span data-type="title" id=toc-4965>٨ </span>- وما لكم لا تؤمنون بالله والرسول يدعوكم للإيمان بربكم ويحثكم عليه، وقد أخذ الله ميثاقكم بالإيمان من قبل إن كنتم تريدون الإيمان فقد تحقق دليله.',NULL,'804',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4909,'<span data-type="title" id=toc-4966>٩ </span>- هو الذى ينِّزل على رسوله آيات واضحات من القرآن ليُخرجكم بها من الضلال إلى الهدى، وإن الله بكم لكثير الرأفة، واسع الرحمة.',NULL,'804',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4910,'<span data-type="title" id=toc-4967>١٠ </span>- وأى شئ حصل لكم فى ألا تنفقوا فى سبيل الله من أموالكم؟ ، ولله ميراث السموات والأرض يرث كل ما فيهما، ولا يبقى أحد مالكاً لشئ منهما. لا يستوى فى الدرجة والمثوبة منكم من أنفق من قبل فتح مكة وقاتل - والإسلام فى حاجة إلى من يسنده ويقويه - أولئك المنفقون المقاتلون قبل الفتح أعلى درجة من الذين أنفقوا بعد الفتح وقاتلوا، وكلا من الفريقين وعد الله المثوبة الحسنى مع تفاوت درجاتهم، والله بما تعملون خبير، فيجازى كلا بما يستحق.',NULL,'805',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4911,'<span data-type="title" id=toc-4968>١١ </span>- مَنِ المُؤمِنُ الذى يُنفق فى سبيل الله مخلصاً، فيضاعف الله له ثوابه، وله فوق المضاعفة ثواب كريم يوم القيامة؟ .',NULL,'805',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4912,'<span data-type="title" id=toc-4969>١٢ </span>- يوم ترى المؤمنين والمؤمنات يسبق نور إيمانهم وأعمالهم الطيبة أمامهم وعن أيمانهم، تقول لهم الملائكة: بُشراكم فى هذا اليوم جنات تجرى من تحت أشجارها الأنهار لا تخرجون منها أبداً، ذلك الجزاء هو الفوز العظيم لكم لقاء أعمالكم.',NULL,'805',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4913,'<span data-type="title" id=toc-4970>١٣ </span>- يوم يقول المنافقون والمنافقات للمؤمنين والمؤمنات: انتظرونا نُصِبْ بعض نوركم، قيل - توبيخاً لهم ارجعوا إلى حيث أعطينا هذا النور فاطلبوه، فضرب بين المؤمنين والمنافقين بحاجز له باب، باطن الحاجز الذى يلى الجنة فيه الرحمة والنعيم، وظاهر الحاجز الذى يلى النار من جهته النقمة والعذاب.',NULL,'805',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4914,'<span data-type="title" id=toc-4971>١٤ </span>- ينادى المنافقون المؤمنين: ألم نكن فى الدنيا معكم وفى رفقتكم؟ فيرد المؤمنون: بلى، كنتم معنا كما تقولون، ولكنكم أهلكتم أنفسكم بالنفاق، وتمنيتم للمؤمنين الحوادث المهلكة، وشككتم فى أمور الدين، وخدعتكم الآمال. ووهمتم أنكم على خير حتى جاء الموت وخدعكم بعفو الله ومغفرته الشيطان.',NULL,'805',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4915,'<span data-type="title" id=toc-4972>١٥ </span>- فاليوم لا يقبل منكم ما تفتدون به أنفسكم من العذاب مهما أغليتم فى ذلك. ولا يقبل من الكافرين المعلنين كفرهم فدية كذلك، مرجعكم جميعاً النار. هى منزلكم الأوْلى بكم، وبئس المصير النار.',NULL,'806',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4916,'<span data-type="title" id=toc-4973>١٦ </span>- ألم يحن الوقت للذين آمنوا أن ترق قلوبهم لذكر الله والقرآن الكريم، ولا يكونوا كالذين أوتوا الكتاب من قبلهم من اليهود والنصارى، عملوا به مدة فطال عليهم الزمن، فجمدت قلوبهم وكثير منهم خارجون عن حدود دينهم؟ .',NULL,'806',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4917,'<span data-type="title" id=toc-4974>١٧ </span>- اعلموا - أيها المؤمنون - أن الله يصلح الأرض ويهيئها للإنبات بنزول المطر بعد يبسها، قد وضَّحنا لكم الآيات، وضربنا لكم الأمثال لعلكم تعقلون ما فيها، فتخشع قلوبكم لذكر الله.',NULL,'806',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4918,'<span data-type="title" id=toc-4975>١٨ </span>- إن المتصدقين والمتصدقات وأنفقوا فى سبيل الله نفقات طيبة بها نفوسهم يُضاعف الله لهم ثواب ذلك، ولهم فوق المضاعفة أجر كريم يوم القيامة.',NULL,'806',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4919,'<span data-type="title" id=toc-4976>١٩ </span>- والذين آمنوا بالله ورسله ولم يُفَرِّقُوا بين أحد منهم أولئك هم فى منزلة الصديقين والشهداء، لهم ثواب ونور يوم القيامة. مثل ثواب الصديقين والشهداء ونورهم، والذين كفروا وكذبوا بآيات الله أولئك هم أصحاب النار لا يفارقونها أبداً.',NULL,'806',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4920,'<span data-type="title" id=toc-4977>٢٠ </span>- اعلموا - أيها المغرورون بالدنيا - أنَّما الحياة الدنيا لعب لا ثمرة له، ولهو يشغل الإنسان عما ينفعه، وزينة لا تحصل شرفاً ذاتياً، وتفاخر بينكم بأنساب زائلة وعظام بالية، وتكاثر بالعدد فى الأموال والأولاد. مثلها فى ذلك مثل مطر أعجب الزراع نباته، ثم يكمل نضجه ويبلغ تمامه، فتراه عقب ذلك مصفراً آخذاً فى الجفاف، ثم يصير بعد فترة هشيماً جامداً متكسراً، لا يبقى منه ما ينفع، وفى الآخرة عذاب شديد لمن آثر الدنيا وأخذها بغير حقها، ومغفرة من الله لمن آثر آخرته على دُنياه، وليست الحياة الدنيا إلا متاع هو غرور لا حقيقة له لمن اطمأن بها ولم يجعلها ذريعة للآخرة.',NULL,'807',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4921,'<span data-type="title" id=toc-4978>٢١ </span>- سارعوا فى السبق إلى مغفرة من ربكم إلى جنة فسيحة الأرجاء، عرضها مثل عرض السموات والأرض هُيِّئت للذين صدقوا بالله ورسله. ذلك الجزاء العظيم فضل الله يؤتيه من يشاء من عباده، والله - وحده - صاحب الفضل الذى جل أن تحيط بوصفه العقول.',NULL,'807',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4922,'<span data-type="title" id=toc-4979>٢٢ </span>- ما نزل من مصيبة فى الأرض من قحط أو نقص فى الثمرات أو غير ذلك، ولا فى أنفسكم من مرض أو فقر أو موت أو غير ذلك إلا مكتوبة فى اللوح مثبتة فى علم الله من قبل أن نوجدها فى الأرض أو فى الأنفس. إن ذلك الإثبات للمصيبة والعلم بها على الله سهل لإحاطة علمه بكل شئ.',NULL,'807',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4923,'<span data-type="title" id=toc-4980>٢٣ </span>- أعْلمناكم بذلك لكيلا تحزنوا على ما لم تحصلوا عليه حزناً مفرطاً يجركم إلى السخط، ولا تفرحوا فرحاً مُبطراً بما أعطاكم. والله لا يحب كل متكبر فخور على الناس بما عنده.',NULL,'807',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4924,'<span data-type="title" id=toc-4981>٢٤ </span>- الذين يضنون بأموالهم عن الإنفاق فى سبيل الله، ويأمرون الناس بالبخل بتحسينه لهم، ومَن يعرض عن طاعة الله فإن الله - وحده - الغنى عنه، المستحق بذاته للحمد والثناء.',NULL,'807',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4925,'<span data-type="title" id=toc-4982>٢٥ </span>- لقد أرسلنا رسلنا الذين اصطفيناهم بالمعجزات القاطعة، وأنزلنا معهم الكتب المتضمنة للأحكام وشرائع الدين، والميزان الذى يحقق الإنصاف فى التعامل، ليتعامل الناس فيما بينهم بالعدل، وخلقنا الحديد فيه عذاب شديد فى الحرب، ومنافع للناس فى السلم، يستغلونه في التصنيع، لينتفعوا به فى مصالحهم ومعايشهم، وليعلم الله من ينصر دينه، وينصر رسله غائباً عنهم. إن الله قادر بذاته. لا يفتقر إلى عون أحد.',NULL,'808',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4926,'<span data-type="title" id=toc-4983>٢٦ </span>- ولقد أرسلنا نوحاً وإبراهيم وجعلنا فى ذريتهما النبوة والكتب الهادية، فبعض هذه الذرية سالكون طريق الهداية، وكثير منهم خارجون عن الطريق المستقيم.',NULL,'808',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4927,'<span data-type="title" id=toc-4984>٢٧ </span>- ثم تابعنا على آثار نوح وإبراهيم ومن سبقهما أو عاصرهما من الرسل برسلنا رسولاً بعد رسول، واتبعناهم بإرسال عيسى ابن مريم، وأوحينا إليه الإنجيل، وأودعنا فى قلوب المتبعين له شفقة شديدة ورقة وعطفاً، فابتدعوا زيادة فى العبادة وغلوا فى التدين رهبانية ما فرضناها عليهم ابتداء، ولكن التزموها ابتغاء رضوان الله تعالى، فما حافظوا عليها حق المحافظة، فأعطينا الذين آمنوا بمحمد نصيبهم من الأجر والثواب، وكثير منهم مكذبون بمحمد خارجون عن الطاعة والطريق المستقيم.',NULL,'808',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4928,'<span data-type="title" id=toc-4985>٢٨ </span>- يا أيها الذين آمنوا خافوا عقاب الله، واثبتوا على إيمانكم برسوله يعطكم نصيبين من رحمته، ويجعل لكم نوراً تهتدون به، ويغفر لكم ما فرط من ذنوبكم والله واسع المغفرة وافر الرحمة.',NULL,'809',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4929,'<span data-type="title" id=toc-4986>٢٩ </span>- يمنحكم الله تعالى كل ذلك ليعلم أهل الكتاب الذين لم يؤمنوا بمحمد أنهم لا يقدرون على شئ من إنعام الله يكسبونه لأنفسهم أو يمنحونه لغيرهم، وأن الفضل - كله - بيد الله - وحده - يؤتيه من يشاء من عباده، والله صاحب الفضل العظيم.',NULL,'809',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4930,'<span data-type="title" id=toc-4988>١ </span>- قد سمع الله قول المرأة التى تراجعك فى شأن زوجها الذى ظاهَر منها، وتضرع إلى الله، والله يسمع ما تتراجعان به من الكلام. إن الله محيط سمعه بكل ما يسمع، محيط بصره بكل ما يبصر.',NULL,'810',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4931,'<span data-type="title" id=toc-4989>٢ </span>- والذين يظاهرون منكم - أيها المؤمنون - من نسائهم بتشبيههن فى التحريم بأمهاتهم مخطئون، فليست الزوجات أمهاتهم. ما أمهاتهم - حقاَّ - إلا اللآئى ولدنهم، وإن المظاهرين ليقولون منكراً من القول تنفر منه الأذواق السليمة، وكذباً منحرفاً عن الحق، وإن الله لعظيم العفو والمغفرة عما سلف منكم.',NULL,'810',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4932,'<span data-type="title" id=toc-4990>٣ </span>- والذين يظاهرون من نسائهم ثم يراجعون ما قالوه فيظهر لهم خطؤهم، ويودون بقاء الزوجية، فعليهم عتق رقبة قبل أن يتماسا. ذلكم الذى أوجبه الله - من عتق الرقبة - عظة لكم توعظون به كيلا تعودوا والله بما تعملون خبير.',NULL,'810',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4933,'<span data-type="title" id=toc-4991>٤ </span>- فمَن لم يجد رقبة فعليه صيام شهرين متتابعين من قبل أن يتماسا، فمَن لم يستطع ذلك الصوم فعليه إطعام ستين مسْكيناً. شرع الله ذلك لتؤمنوا بالله ورسوله، وتعملوا بمقتضى هذا الإيمان، وتلك حدود الله فلا تتجاوزوها، وللكافرين عذاب شديد الألم.',NULL,'811',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4934,'<span data-type="title" id=toc-4992>٥ </span>- إن الذين يعاندون الله ورسوله خُذلوا كما خُذل الذين من قبلهم، وقد أنزلنا دلائل واضحات على الحق، وللجاحدين بها عذاب شديد الإهانة.',NULL,'811',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4935,'<span data-type="title" id=toc-4993>٦ </span>- يوم يحييهم الله جميعاً بعد موتهم فيخبرهم بما عملوا، أحصاه الله عليهم ونسوه، والله على كل شئ شاهد مطلع.',NULL,'811',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4936,'<span data-type="title" id=toc-4994>٧ </span>- ألم تعلم أن الله يعلم ما فى السموات وما فى الأرض ما يكون من مُسارّة بين ثلاثة إلا هو رابعهم بعلمه بما يتسارون به، ولا خمسة إلا هو سادسهم، ولا أقل من ذلك ولا أكثر إلا وهو معهم. يعلم ما يتناجون به - أينما كانوا - ثم يُخبرهم يوم القيامة بكل ما عملوا. إن الله بكل شئ عليم.',NULL,'811',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4937,'<span data-type="title" id=toc-4995>٨ </span>- ألم تر - أيها الرسول - هؤلاء الذين نُهوا عن المسارة فيما بينهم بما يثير الشك فى نفوس المؤمنين، ثم يرجعون إلى ما نهوا عنه، ويتسارون فيما بينهم بالذنب يقترفونه، والعدوان يعتزمونه، ومعصيتهم لرسول الله، وإذا جاءوك حيوك بقول محرف لم يحيِّك به الله، ويقولون فى أنفسهم: هلا يعذبنا الله بما نقول إن كان رسولا حقاً؟ حسبهم جهنم يدخلونها ويحترقون بنارها، فبئس المآل مآلهم.',NULL,'811',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4938,'<span data-type="title" id=toc-4996>٩ </span>- يا أيها الذين صدَّقوا بالله ورسوله: إذا تناجيتم فلا تتناجوا بالذنب والاعتداء ومخالفة الرسول، وتناجوا متواصين بالخير والتحرز عن الآثام، وخافوا الله الذى إليه - لا إلى غيره - تساقون بعد بعثكم.',NULL,'812',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4939,'<span data-type="title" id=toc-4997>١٠ </span>- إنما التناجى المثير للشك من تزْيين الشيطان ليُدخل الحزن على قلوب المؤمنين، وليس ذلك بضارهم شيئاً إلا بمشيئة الله، وعلى الله - وحده - فليعتمد المؤمنون.',NULL,'812',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4940,'<span data-type="title" id=toc-4998>١١ </span>- يا أيها الذين صدَّقوا بالله ورسوله: إذا طلب منكم أن يوسع بعضكم فى المجالس لبعض فأوسعوا يوسع الله لكم، وإذا طلب منكم أن تنهضوا من مجالسكم فانهضوا. يُعْلِ الله مكانة المؤمنين المخلصين والذين أوتوا العلم درجات والله بما تعملون خبير.',NULL,'812',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4941,'<span data-type="title" id=toc-4999>١٢ </span>- يا أيها الذين صدَّقوا بالله ورسوله: إذا أردتم مناجاة رسول الله فقدِّموا قبل مناجاتكم صدقة، ذلك خير لكم وأطهر لقلوبكم، فإن لم تجدوا ما تتصدقون به فإن الله واسع المغفرة شامل الرحمة.',NULL,'812',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4942,'<span data-type="title" id=toc-5000>١٣ </span>- أخشيتم أن تلتزموا تقديم صدقات أمام مناجاتكم رسول الله؟ فإذا لم تقدموا وعفا الله عنكم فحافظوا على إقامة الصلاة وإيتاء الزكاة، وأطيعوا الله ورسوله، والله خبير بعملكم فيجازيكم عليه.',NULL,'812',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4943,'<span data-type="title" id=toc-5001>١٤ </span>- ألم تر - أيها الرسول - إلى المنافقين الذين وَالَوْا قوماً غضب الله عليهم. ما هؤلاء الموالون منكم ولا ممن وَالوُهم، ويجترئون على الحلف كذباً مع علمهم بكذبهم.',NULL,'812',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4944,'<span data-type="title" id=toc-5002>١٥ </span>- أعد الله لهؤلاء المنافقين عذاباً بالغ الشدة. إنهم ساء ما كانوا يعملون من النفاق والحلف على الكذب.',NULL,'813',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4945,'<span data-type="title" id=toc-5003>١٦ </span>- اتخذوا أيمانهم وقاية لأنفسهم من القتل ولأولادهم من السبى، ولأموالهم من الغنيمة، فَصَدّوا بذلك عن سبيل الله، فلهم عذاب شديد الإهانة.',NULL,'813',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4946,'<span data-type="title" id=toc-5004>١٧ </span>- لن تدفع عنهم أموالهم ولا أولادهم من عذاب الله شيئاً. أولئك أهل النار هم فيها مخلدون.',NULL,'813',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4947,'<span data-type="title" id=toc-5005>١٨ </span>- يوم يبعثهم الله جميعاً فيقسمون له إنهم كانوا مؤمنين كما يقسمون لكم الآن، ويظنون أنهم بقسَمهم هذا على شئ من الدهاء ينفعهم. ألا إنهم هم البالغون الغاية فى الكذب.',NULL,'813',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4948,'<span data-type="title" id=toc-5006>١٩ </span>- استولى عليهم الشيطان فأنساهم تذكر الله واستحضار عظمته، أولئك حزب الشيطان ألا إن حزب الشيطان هم الذين بلغوا الغاية فى الخسران.',NULL,'813',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4949,'<span data-type="title" id=toc-5007>٢٠ </span>- إن الذين يعاندون الله ورسوله أولئك فى عداد الذين بلغوا الغاية فى الذلة.',NULL,'813',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4950,'<span data-type="title" id=toc-5008>٢١ </span>- قضى الله لأنتصرن أنا ورسلى. إن الله تام القوة لا يغلبه غالب.',NULL,'813',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4951,'<span data-type="title" id=toc-5009>٢٢ </span>- لا تجد قوماً يصدِّقون بالله واليوم الآخر يتبادلون المودة مع من عادى الله ورسوله، ولو كانوا آباءهم أو أبناءهم أو إخوانهم أو أقرباءهم، أولئك الذين لا يوالون من تطاول على الله ثَبَّت الله فى قلوبهم الإيمان، وأيدهم بقوة منه، ويدخلهم جنات تجرى من تحتها الأنهار خالدين فيها، لا ينقطع عنهم نعيمها. أحبهم الله وأحبوه أولئك حزب الله ألا إنّ حزب الله هم الفائزون.',NULL,'813',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4952,'<span data-type="title" id=toc-5011>١ </span>- نزه الله عما لا يليق به كل ما فى السموات وما فى الأرض، وهو الغالب الذى لا يعجزه شئ، الحكيم فى تدبيره وأفعاله.',NULL,'814',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4953,'<span data-type="title" id=toc-5012>٢ </span>- هو الذى أخرج الذين كفروا من أهل الكتاب - وهم يهود بنى النضير - من ديارهم عند أول إخراج لهم من جزيرة العرب. ما ظننتم - أيها المسلمون - أن يخرجوا من ديارهم لقوتهم، وظنوا - هم - أنهم مانعتهم حصونهم من بأس الله، فأخذهم الله من حيث لم يظنوا أن يؤخذوا من جهته، وألقى فى قلوبهم الفزع الشديد، يخربون بيوتهم بأيديهم ليتركوها خاوية، وأيدى المؤمنين ليقضوا على تحصنهم، فاتعظوا بما نزل بهم يا أصحاب العقول.',NULL,'815',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4954,'<span data-type="title" id=toc-5013>٣ </span>- ولولا أن كتب الله عليهم الإخراج من ديارهم على هذه الصورة الكريمة لعذَّبهم فى الدنيا بما هو أشد من الإخراج، ولهم فى الآخرة - مع هذا الإخراج - عذاب النار.',NULL,'815',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4955,'<span data-type="title" id=toc-5014>٤ </span>- ذلك الذى أصابهم فى الدنيا وما ينتظرهم فى الآخرة لأنهم عادوا الله ورسوله أشد العداء، ومن يُعَادِ الله هذا العداء فلن يفلت من عقابه، فإن الله شديد العقاب.',NULL,'815',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4956,'<span data-type="title" id=toc-5015>٥ </span>- ما قطعتم - أيها المسلمون - من نخلة أو تركتموها باقية على ما كان عليه فبأمر الله. لا حرج عليكم فيه، ليعز المؤمنين، وليهين الفاسقين المنحرفين عن شرائعه.',NULL,'815',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4957,'<span data-type="title" id=toc-5016>٦ </span>- وما أفاء الله وردَّه على رسوله من أموال بنى النضير فما أسرعتم فى السير إليه بخيل ولا إبل، ولكن الله يُسلط على مَن يشاء من عباده بلا قتال، والله على كل شئ تام القدرة.',NULL,'815',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4958,'<span data-type="title" id=toc-5017>٧ </span>- ما رده الله على رسوله من أموال أهل القرى بغير إيجاف خيل أو ركوب، فهو لله وللرسول ولذى القربى واليتامى والمساكين وابن السبيل؛ كيلا تكون الأموال متداولة بين الأغنياء منكم خاصة، وما جاءكم به الرسول من الأحكام فتمسكوا به، وما نهاكم عنه فاتركوه، واجعلوا لكم وقاية من غضب الله. إن الله شديد العقاب.',NULL,'816',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4959,'<span data-type="title" id=toc-5018>٨ </span>- وكذلك يُعطى ما ردَّه الله على رسوله من أموال أهل القرى الفقراء المهاجرين الذين أُخرجوا من ديارهم وأموالهم. يرجون زيادة من الله فى أرزاقهم ورضواناً، وينصرون الله ورسوله بنفوسهم وأموالهم، أولئك هم المؤمنون.',NULL,'816',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4960,'<span data-type="title" id=toc-5019>٩ </span>- والأنصار الذين نزلوا المدينة وأقاموا بها، وأخلصوا الإيمان من قبل نزول المهاجرين بها، يحبُّون مَن هاجر إليهم من المسلمين، ولا يحسون فى نفوسهم شيئاً مما أوتى المهاجرون من الفئ، ويقدمون المهاجرين على أنفسهم ولو كان بهم حاجة، ومن يُحْفَظ - بتوفيق الله - من بخل نفسه الشديد فأولئك هم الفائزون بكل ما يحبون.',NULL,'816',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4961,'<span data-type="title" id=toc-5020>١٠ </span>- والمؤمنون الذين جاءوا بعد المهاجرين والأنصار يقولون: ربنا اغفر لنا ذنوبنا ولإخواننا الذين سبقونا بالإيمان، ولا تجعل فى قلوبنا حقداً للذين آمنوا. ربنا إنك بالغ الرأفة والرحمة.',NULL,'816',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4962,'<span data-type="title" id=toc-5021>١١ </span>- ألم تنظر - متعجباً - إلى المنافقين، يتكرر منهم القول لإخوانهم الذين كفروا من أهل الكتاب - وهم بنو النضير -: والله إن أُجبرتم على الخروج من المدينة لنخرجن معكم، ولا نطيع فى شأنكم أحداً مهما طال الزمان، وإن قاتلكم المسلمون لننصركم، والله يشهد إن المنافقين لكاذبون فيما وعدوا به.',NULL,'817',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4963,'<span data-type="title" id=toc-5022>١٢ </span>- لئن أُخرج اليهود لا يخرج المنافقون معهم، ولئن قوتلوا لا ينصرونهم، ولئن نصروهم ليفرون مدبرين ثم لا ينصرون.',NULL,'817',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4964,'<span data-type="title" id=toc-5023>١٣ </span>- لأنتم - أيها المسلمون - أشد مهابة فى صدور المنافقين واليهود من الله؛ ذلك لأنهم قوم لا يعلمون حقيقة الإيمان.',NULL,'817',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4965,'<span data-type="title" id=toc-5024>١٤ </span>- لا يقاتلكم اليهود مجتمعين إلا فى قرى محصنة أو من وراء جدران يستترون بها، بأسهم بينهم شديد، تظنهم مجتمعين متحدين مع أن قلوبهم متفرقة، اتصافهم بهذه الصفات لأنهم قوم لا يعقلون عواقب الأمور.',NULL,'817',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4966,'<span data-type="title" id=toc-5025>١٥ </span>- مثل بنى النضير كمثل الذين كفروا من قبلهم قريباً ذاقوا فى الدنيا عاقبة كفرهم ونقضهم العهود، ولهم فى الآخرة عذاب شديد الألم.',NULL,'817',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4967,'<span data-type="title" id=toc-5026>١٦ </span>- مثل المنافقين فى إغرائهم بنى النضير - بالتمرد على رسول الله - كمثل الشيطان حين أغرى الإنسان بترك الإيمان، فقال له: أكفر، فلما كفر قال، إنى برئ منك. إنى أخاف الله رب العالمين.',NULL,'817',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4968,'<span data-type="title" id=toc-5027>١٧ </span>- فكان مآل الشيطان ومن أغواه أنهما فى النار خالدين فيها، وذلك الخلود جزاء المعتدين المتجاوزين سبيل الحق.',NULL,'817',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4969,'<span data-type="title" id=toc-5028>١٨ </span>- يا أيها الذين آمنوا اجعلوا لكم وقاية من عذاب الله بالتزام طاعته، ولتتدبر كل نفس أى شئ قَدَّمَتْ من العمل لغد، والتزموا تقوى الله. إن الله خبير بما تعملون، فيجازيكم عليه.',NULL,'818',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4970,'<span data-type="title" id=toc-5029>١٩ </span>- ولا تكونوا - أيها المؤمنون - كالذين نسوا حقوق الله، فأنساهم أنفسهم - بما ابتلاهم من البلايا - فصاروا لا يعرفون ما ينفعها مما يضرها. أولئك هم الخارجون عن طاعة الله.',NULL,'818',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4971,'<span data-type="title" id=toc-5030>٢٠ </span>- لا يستوى أصحاب النار المعذبون وأصحاب الجنة المنعمون. أصحاب الجنة هم - دون غيرهم - الفائزون بكل ما يحبون.',NULL,'818',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4972,'<span data-type="title" id=toc-5031>٢١ </span>- لو أنزلنا هذا القرآن على جبل شديد لرأيت هذا الجبل - على قوته - خاضعاً متشققاً من خشية الله، وتلك الأمثال نعرضها للناس لعلهم يتدبرون عواقب أمورهم.',NULL,'818',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4973,'<span data-type="title" id=toc-5032>٢٢ </span>- هو الله الذى لا معبود بحق إلا هو - وحده - عالم ما غاب وما حضر، هو الرحمن الرحيم.',NULL,'818',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4974,'<span data-type="title" id=toc-5033>٢٣ </span>- هو الله الذى لا إله إلا هو، المالك لكل شئ على الحقيقة، الكامل عن كل نقص، المبرَّأ عما لا يليق، ذو السلامة من النقائص، المصدق رسله بما أيدهم به من معجزات، الرقيب على كل شئ، الغالب فلا يعجزه شئ، العظيم الشأن فى القوة والسلطان. المتعظم عما لا يليق بجماله وجلاله، تنزَّه الله وتعالى عما يشركون.',NULL,'818',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4975,'<span data-type="title" id=toc-5034>٢٤ </span>- هو الله المبدع للأشياء من غير مثال سابق. الموجد لها بريئة من التفاوت، المصور لها على هيئاتها كما أراد. له الأسماء الحسنى، ينزهه عما لا يليق كل ما فى السموات والأرض، وهو الغالب الذى لا يعجزه شئ، الحكيم فى تدبيره وتشريعه.',NULL,'818',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4976,'<span data-type="title" id=toc-5036>١ </span>- يا أيها الذين صدّقوا بالله ورسوله: لا تتخذوا أعدائى وأعداءكم أنصاراً تُفْضُون إليهم بالمحبة الخالصة، مع أنهم جحدوا بما جاءكم من الإيمان بالله ورسوله وكتابه، يخرجون الرسول ويخرجونكم من دياركم، لإيمانكم بالله ربكم، إن كنتم خرجتم من دياركم للجهاد فى سبيلى وطلب رضائى فلا تتولوا أعدائى، تُلْقُون إليهم بالمودة سراً، وأنا أعلم بما أسررتم وما أعلنتم، ومن يتخذ عدو الله ولياً له فقد ضل الطريق المستقيم.',NULL,'819',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4977,'<span data-type="title" id=toc-5037>٢ </span>- إن يلقوكم ويتمكنوا منكم تظهر لكم عداوتهم، ويمدوا إليكم أيديهم وألسنتهم بما يسوؤكم، وتمنوا كفركم مثلهم.',NULL,'820',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4978,'<span data-type="title" id=toc-5038>٣ </span>- لن تنفعكم قراباتكم ولا أولادكم الذين تتخذونهم أولياء وهم عدو لله ولكم، يوم القيامة يفصل الله بينكم، فيجعل أعداءه فى النار وأولياءه فى الجنة، والله بكل ما تعملون بصير.',NULL,'820',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4979,'<span data-type="title" id=toc-5039>٤ </span>- قد كانت لكم قدوة حسنة تقتدون بها فى إبراهيم والذين آمنوا معه، حين قالوا لقومهم: إنا بريئون منكم ومن الآلهة التى تعبدونها من دون الله، جحدنا بكم وظهر بيننا وبينكم العداوة والبغضاء، لا تزول أبداً حتى تؤمنوا بالله - وحده - لكن قول إبراهيم لأبيه: لأطلبن لك المغفرة، وما أملك من الله من شئ - ليس مما يقتدى به - لأن ذلك كان قبل أن يعلم أنه مصمم على عداوته لله، فلما تبين له أنه عدو لله تبرأ منه، قولوا: - أيها المؤمنون - ربنا عليك اعتمدنا، وإليك رجعنا، وإليك المصير فى الآخرة.',NULL,'820',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4980,'<span data-type="title" id=toc-5040>٥ </span>- ربنا لا تجعلنا بحال نكون بها فتنة للذين كفروا، واغفر لنا ذنوبنا يا ربنا. إنك أنت العزيز الذى لا يغلب ذو الحكمة فيما قضى وقَدَّر.',NULL,'820',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4981,'<span data-type="title" id=toc-5041>٦ </span>- لقد كان لكم - أيها المؤمنون - فى إبراهيم والذين معه قدوة حسنة فى معاداتهم أعداء الله، هذه القدوة لمَن كان يرجو لقاء الله واليوم الآخر، ومَن يُعْرِض عن هذا الاقتداء فقد ظلم نفسه، فإن الله هو الغنى عما سواه، المستحق للحمد من كل ما عداه.',NULL,'820',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4982,'<span data-type="title" id=toc-5042>٧ </span>- عسى الله أن يجعل بينكم وبين الذين عاديتم من الكافرين مودة بتوفيقهم للإيمان، والله تام القدرة والله واسع المغفرة لمن تاب، رحيم بعباده.',NULL,'820',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4983,'<span data-type="title" id=toc-5043>٨ </span>- لا ينهاكم الله عن الكافرين الذين لم يقاتلوكم ولم يخرجوكم من دياركم، أن تكرموهم وتمنحوهم صلتكم. إن الله يحب أهل البر والتواصل.',NULL,'821',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4984,'<span data-type="title" id=toc-5044>٩ </span>- إنما ينهاكم الله عن الذين حاربوكم فى الدين ليصدوكم عنه، وأجبروكم على الخروج من دياركم، وعاونوا على إخراجكم منها أن تتخذوهم أنصاراً، ومن يتخذ هؤلاء أنصاراً فأولئك هم الظالمون لأنفسهم.',NULL,'821',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4985,'<span data-type="title" id=toc-5045>١٠ </span>- يا أيها الذين آمنوا: إذا جاءكم المؤمنات مهاجرات من دار الشرك فاختبروهن لتعلموا صدق إيمانهن، الله أعلم بحقيقة إيمانهن، فإن اطمأننتم إلى إيمانهن فلا تردوهن إلى أزواجهن الكفار، فلا المؤمنات بعد هذا حلال للكافرين، ولا الكافرون حلال للمؤمنات، وآتوا الأزواج الكافرين ما أنفقوا من الصداق على زوجاتهم المهاجرات إليكم، ولا حرج عليكم أن تتزوجوا هؤلاء المهاجرات إذا آتيتموهن صداقهن، ولا تتمسكوا بعقد زوجية الكافرات الباقيات فى دار الشرك أو اللاحقات بها، واطلبوا من الكفار ما أنفقتم من صداق على اللاحقات بدار الشرك وليطلبوا - هم - ما أنفقوا على زوجاتهم المهاجرات. ذلكم - التشريع - حكم الله، يفصل به بينكم، والله عليم بمصالح عباده، حكيم فى تشريعه.',NULL,'821',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4986,'<span data-type="title" id=toc-5046>١١ </span>- وإن أفلت منكم بعض زوجاتكم إلى الكفار، ثم حاربتموهم، فآتوا الذين ذهبت زوجاتهم مثل ما أنفقوا عليهن من صداق، واتقوا الله الذى أنتم به مؤمنون.',NULL,'821',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4987,'<span data-type="title" id=toc-5047>١٢ </span>- يا أيها النبى: إذا جاءك المؤمنات يعاهدنك على أن لا يشركن بالله شيئاً، ولا يسرقن، ولا يزنين، ولا يقتلن أولادهن، ولا يُلْحقْنَ بأزواجهن مَن ليس من أولادهن بهتاناً وكذباً يختلقنه بين أيديهن وأرجلهن، ولا يخالفنك فى معروف تدعوهن إليه، فعاهدهن على ذلك، واطلب لهن المغفرة من الله، إن الله عظيم المغفرة والرحمة.',NULL,'822',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4988,'<span data-type="title" id=toc-5048>١٣ </span>- يا أيها الذين صدقوا بالله ورسوله لا توالوا قوماً غضب الله عليهم قد يئسوا من الآخرة وما فيها من ثواب وحساب كما يئس الكفار من إحياء أصحاب القبور.',NULL,'822',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4989,'<span data-type="title" id=toc-5050>١ </span>- نزَّه الله عما لا يليق به كل ما فى السموت وما فى الأرض، وهو - وحده - الغالب على كل شئ، ذو الحكمة البالغة.',NULL,'823',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4990,'<span data-type="title" id=toc-5051>٢ </span>- يا أيها الذين آمنوا: لأى غرض تقولون بألسنتكم ما لا تصدقه أفعالكم؟ .',NULL,'823',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4991,'<span data-type="title" id=toc-5052>٣ </span>- كره الله كُرهاً شديداً أن تقولوا ما لا تفعلون.',NULL,'823',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4992,'<span data-type="title" id=toc-5053>٤ </span>- إن الله يحب الذين يقاتلون فى سبيل إعلاء كلمته متماسكين، كأنهم بُنيان مُحكم.',NULL,'823',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4993,'<span data-type="title" id=toc-5054>٥ </span>- واذكر - يا محمد - حين قال موسى لقومه، لِمَ تؤذوننى وأنتم تعلمون أنى رسول الله إليكم؟ . فلما أصروا على الانحراف عن الحق أمال الله قلوبهم عن قبول الهداية، والله لا يهدى القوم الخارجين عن طاعته.',NULL,'823',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4994,'<span data-type="title" id=toc-5055>٦ </span>- واذكر حين قال عيسى ابن مريم: يا بنى إسرائيل إنى رسول الله إليكم مُصدقاً لما تقدم من التوراة، ومُبشراً برسول يأتى من بعدى اسمه أحمد، فلما جاءهم الرسول المبشَّر به بالآيات الواضحات قالوا: هذا الذى جئتنا به سحر بيِّن.',NULL,'824',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4995,'<span data-type="title" id=toc-5056>٧ </span>- ومَنْ أشَدُّ ظلماً ممن اختلق على الله الكذب وهو يُدعى إلى الإسلام دين الحق والخير، والله لا يهدى القوم المصرين على الظلم.',NULL,'824',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4996,'<span data-type="title" id=toc-5057>٨ </span>- يفترى بنو إسرائيل الكذب على الله، لكى يُطفئوا نور دينه بأفواههم، كمن يريد إطفاء نور الشمس بنفخة من فيه، والله مكمل نوره بإتمام دينه ولو كره الجاحدون.',NULL,'824',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4997,'<span data-type="title" id=toc-5058>٩ </span>- الله الذى أرسل رسوله - محمداً - بالقرآن هدى للناس وبالإسلام دين الحق، ليعليه على كل الأديان ولو كره المشركون.',NULL,'824',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4998,'<span data-type="title" id=toc-5059>١٠ </span>- يا أيها الذين آمنوا: هل أرشدكم إلى تجارة عظيمة تُنجيكم من عذاب شديد الألم؟ .',NULL,'824',NULL,NULL,'0');
INSERT INTO [b836] VALUES (4999,'<span data-type="title" id=toc-5060>١١ </span>- هذه التجارة هى أن تثبتوا على الإيمان بالله ورسوله، وتُجاهدوا فى سبيل الله بأموالكم وأنفسكم ذلك الذى أرشدكم إليه خير لكم إن كنتم تعلمون.',NULL,'824',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5000,'<span data-type="title" id=toc-5061>١٢ </span>- إن تؤمنوا وتجاهدوا فى سبيل الله يغفر لكم ذنوبكم، ويُدخلكم جنات تجرى من تحتها الأنهار، ومساكن طيبة فى جنات عدن. ذلك الجزاء هو الفوز العظيم.',NULL,'824',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5001,'<span data-type="title" id=toc-5062>١٣ </span>- ونعمة أخرى لكم - أيها المؤمنون - المجاهدون تُحبونها، هى نصر من الله وفتح قريب تغنمون خيره، وبشر المؤمنين - يا محمد - بهذا الجزاء وهذه النعمة.',NULL,'825',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5002,'<span data-type="title" id=toc-5063>١٤ </span>- يا أيها الذين آمنوا كونوا أنصار الله إذا دعاكم رسول الله أن تكونوا أنصاره. كما كان أصفياء عيسى أنصاراً لله حين قال: من أنصارى إلى الله؟ فآمنت طائفة من بنى إسرائيل بعيسى، وكفرت طائفة، فَقَوَّيْنَا الذين آمنوا به على عدوهم الذين كفروا، فأصبحوا بتأييدنا منتصرين غالبين.',NULL,'825',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5003,'<span data-type="title" id=toc-5065>١ </span>- يُسبح لله وينزِّهه عما لا يليق به كل ما فى السموات وما فى الأرض. المالك لكل شئ. المتصرف فيه بلا منازع، المنزه تنزيهاً كاملاً عن كل نقص، الغالب على كل شئ، ذى الحكمة البالغة.',NULL,'826',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5004,'<span data-type="title" id=toc-5066>٢ </span>- الله هو الذى أرسل فى العرب الذين لا يعرفون الكتابة رسولاً منهم. يقرأ عليهم آياته ويُطهرهم من خبائث العقائد والأخلاق، ويُعلمهم القرآن والتَّفقه فى الدين، وأنهم كانوا قبل بعثته لفى انحراف عن الحق شديد الوضوح.',NULL,'826',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5005,'<span data-type="title" id=toc-5067>٣ </span>- وبعثه فى آخرين منهم. لم يجيئوا بعد وسيجيئون، وهو - وحده - الغالب على كل شئ. ذو الحكمة البالغة فى كل أفعاله.',NULL,'826',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5006,'<span data-type="title" id=toc-5068>٤ </span>- ذلك البعث فضل من الله يكرم به من يختار من عباده، والله - وحده - صاحب الفضل العظيم.',NULL,'826',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5007,'<span data-type="title" id=toc-5069>٥ </span>- مثل اليهود الذين عُلِّموا التوراة، وكلِّفوا العمل بها، ثم لم يعملوا. كمثل الحمار يحمل كتباً نافعة ولا يعرف ما فيها. ساء مثل القوم الذين كذَّبوا بآيات الله، والله لا يوفق إلى الهدى القوم الذين شأنهم الظلم.',NULL,'827',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5008,'<span data-type="title" id=toc-5070>٦ </span>- قل - يا محمد -: يا أيها الذين صاروا يهوداً، إن ادعيتم - باطلاً - أنكم أحباء الله من دون الناس جميعاً، فتمنوا من الله الموت إن كنتم صادقين فى دعوى حب الله لكم.',NULL,'827',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5009,'<span data-type="title" id=toc-5071>٧ </span>- قال الله: ولا يتمنى الموت يهودى أبداً بسبب ما قدَّموه من الكفر وسوء الفعال، والله محيط علمه بالظالمين.',NULL,'827',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5010,'<span data-type="title" id=toc-5072>٨ </span>- قل: إن الموت الذى تهربون منه لا مهرب منه، فإنه ملاقيكم، ثم تردُّون إلى عالم السر والعلانية، فيخبركم بما كنتم تعملون.',NULL,'827',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5011,'<span data-type="title" id=toc-5073>٩ </span>- يا أيها الذين آمنوا إذا أُذِّنَ للصلاة من يوم الجمعة فامضوا إلى ذكر الله. حريصين عليه، واتركوا البيع، ذلكم الذى أُمرتم به أنفع لكم إن كنتم تعلمون.',NULL,'827',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5012,'<span data-type="title" id=toc-5074>١٠ </span>- فإذا أديتم الصلاة فتفرقوا فى الأرض لمصالحكم، واطلبوا من فضل الله، واذكروا الله بقلوبكم وألسنتكم كثيراً، لعلكم تفوزون بخيرى الدنيا والآخرة.',NULL,'827',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5013,'<span data-type="title" id=toc-5075>١١ </span>- وإذا أبصروا متاعاً للتجارة أو لهواً تفرقوا إليها وتركوك قائماً تخطب، قل: إن ما عند الله من الفضل والثواب أنفع لكم من اللهو ومن التجارة، والله خير الرازقين، فاطلبوا رزقه بطاعته.',NULL,'827',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5014,'<span data-type="title" id=toc-5077>١ </span>- إذا جاءك<span data-type="title" id=toc-5076> المنافقون </span>- يا محمد - قالوا بألسنتهم: نشهد إنك لرسول الله، والله يعلم إنك لرسوله، والله يشهد إن المنافقين لكاذبون فى دعواهم الإيمان بك لعدم تصديقهم بقلوبهم.',NULL,'828',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5015,'<span data-type="title" id=toc-5078>٢ </span>- جعلوا أيمانهم الكاذبة وقاية لهم من المؤاخذة، فمنعوا أنفسهم عن طريق الله المستقيم. إنهم قَبُحَ ما كانوا يعملون فى النفاق والأيمان الكاذبة.',NULL,'828',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5016,'<span data-type="title" id=toc-5079>٣ </span>- ذلك - الذى دأبوا عليه من الظهور بغير حقيقته والحلف بالأيمان الكاذبة - بسبب أنهم آمنوا بألسنتهم، ثم كفروا بقلوبهم، فختم على قلوبهم بهذا الكفر، فهم لا يفهمون ما ينجيهم من عذاب الله.',NULL,'828',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5017,'<span data-type="title" id=toc-5080>٤ </span>- وإذا أبصرتهم تُعجبك أجسامهم لوجاهتهم، وإن يتحدثوا تسمع لقولهم لحلاوته، وهم مع ذلك فارغة قلوبهم من الإيمان كأنهم خُشب مسندة لا حياة فيها. يحسبون كل نازلة عليهم - لشعورهم بحقيقة حالهم - هم العدو فاحذرهم - طردهم الله من رحمته - كيف يُصرفون عن الحق إلى ما هم عليه من النفاق.',NULL,'829',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5018,'<span data-type="title" id=toc-5081>٥ </span>- وإذا قيل لهم: أقبلوا يستغفر لكم رسول الله حركوا رؤوسهم استهزاء، ورأيتهم يُعرضون وهم مستكبرون عن الامتثال.',NULL,'829',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5019,'<span data-type="title" id=toc-5082>٦ </span>- سواء على هؤلاء المنافقين استغفارك لهم أو عدم استغفارك لأنهم لن يرجعوا عن نفاقهم، فلن يغفر الله لهم، إن الله لا يهدى إلى الحق الخارجين على أمره، وغير المؤمنون به.',NULL,'829',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5020,'<span data-type="title" id=toc-5083>٧ </span>- هم الذين يقولون لأهل المدينة: لا تُنفقوا على مَن عند رسول الله من المؤمنين حتى يتفرقوا عنه، ولله خزائن السموات والأرض وما فيها من أرزاق يعطيها من يشاء ولكن المنافقين لا يفهمون ذلك.',NULL,'829',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5021,'<span data-type="title" id=toc-5084>٨ </span>- يقول المنافقون متوعدين: والله لئن رجعنا إلى المدينة ليخرجن فريقنا الأعز منها فريق المؤمنين الأذل، ولله العزة ولرسوله وللمؤمنين لا لهؤلاء المتوعدين، ولكن المنافقين لا يعلمون.',NULL,'829',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5022,'<span data-type="title" id=toc-5085>٩ </span>- يا أيها الذين صدَّقوا بالله ورسوله، لا تشغلكم العناية بأموالكم ولا أولادكم عن ذكر الله وأداء ما فرضه عليكم، ومن تشغله أمواله وأولاده عن ذلك فأولئك هم الخاسرون يوم القيامة.',NULL,'829',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5023,'<span data-type="title" id=toc-5086>١٠ </span>- وأنفقوا - أيها المؤمنون - من الأموال التى رزقناكم مبادرين بذلك من قبل أن يأتى أحدكم الموت، فيقول نادماً: رب هلا أمهلتنى إلى وقت قصير، فَأَصَّدَّق وأكن من الصالحين فى عمل الصالحات.',NULL,'830',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5024,'<span data-type="title" id=toc-5087>١١ </span>- ولن يمهل الله نفساً إذا حان وقت موتها، والله تام العلم بما تعملون، يجازيكم عليه.',NULL,'830',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5025,'<span data-type="title" id=toc-5089>١ </span>- ينزِّه الله عما لا يليق بجلاله كل ما فى السموات وما فى الأرض. له الملك التام - وحده - وله الثناء الجميل، وهو على كل شئ تام القدرة.',NULL,'831',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5026,'<span data-type="title" id=toc-5090>٢ </span>- هو الذى تفرد بخلقكم من عدم، فمنكم منكر لألوهيته، ومنكم مصدق بها، والله بما تعملون بصير فيجازيكم على أعمالكم.',NULL,'831',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5027,'<span data-type="title" id=toc-5091>٣ </span>- خلق الله السموات والأرض بالحكمة البالغة، وصوَّركم فأحسن صوركم حيث جعلكم فى أحسن تقويم وإليه المرجع يوم القيامة.',NULL,'831',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5028,'<span data-type="title" id=toc-5092>٤ </span>- يعلم كلَّ ما فى السموات والأرض، ويعلم ما تُخفون وما تُعلنون من أقوال وأفعال، والله تام العلم بِمُضمرات الصدور.',NULL,'832',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5029,'<span data-type="title" id=toc-5093>٥ </span>- قد أتاكم خبر الذين كفروا من قبلكم، فتجرَّعوا سوء عاقبة أمرهم فى الدنيا، ولهم فى الآخرة عذاب شديد الألم.',NULL,'832',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5030,'<span data-type="title" id=toc-5094>٦ </span>- ذلك الذى أصابهم ويصيبهم من العذاب بسبب أنهم أتتهم رسلهم بالمعجزات الظاهرة، فقالوا منكرين: أبشرٌ مثلنا يرشدوننا، فأنكروا بعثتهم، وانصرفوا عن الحق، وأظهر الله غناه عن إيمانهم بإهلاكهم، والله تام الغنى عن خلقه، مستحق للثناء والحمد على جميل نعمه.',NULL,'832',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5031,'<span data-type="title" id=toc-5095>٧ </span>- ادَّعى الذين كفروا - باطلاً - أنهم لن يُبعثوا بعد الموت، قل لهم - يا محمد -: ليس الأمر كما زعمتم. أقسم بربى لتُبعثن بعد الموت، ولتخبرن بما عملتم فى الدنيا ثم تجازون عليه، وذلك البعث والحساب والجزاء على الله سهل يسير.',NULL,'832',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5032,'<span data-type="title" id=toc-5096>٨ </span>- فصدِّقوا بالله ورسوله، واهتدوا بالنور الذى أنزلناه إذ وضح لكم أن البعث آت لا ريب فيه، والله بما يصدر منكم من عمل تام العلم.',NULL,'832',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5033,'<span data-type="title" id=toc-5097>٩ </span>- يوم يجمعكم فى يوم الجمع للأولين والآخرين يجازيكم على أعمالكم، ذلك يوم التغابن الذى يظهر فيه غُبْنَ الكافرين لانصرافهم عن الإيمان، وغبن المؤمنين المقصرين لتهاونهم فى تحصيل الطاعات، ومَن يؤمن بالله ويعمل صالحاً يذهب عنه سيئاته، ويدخله جنات تجرى من تحتها الأنهار ماكثين فيه أبداً. ذلك الجزاء هو الفوز العظيم',NULL,'832',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5034,'<span data-type="title" id=toc-5098>١٠ </span>- والذين جحدوا بالإيمان وكذَّبوا بمعجزاتنا التى أيدنا بها رسلنا، أولئك أصحاب النار ماكثين فيها، وساء المصير الذى صاروا إليه.',NULL,'833',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5035,'<span data-type="title" id=toc-5099>١١ </span>- ما أصاب العبد من بلاء إلا بتقدير الله، ومن يُصدِّق بالله يَهْدِ قلبه إلى الرضا بما كان، والله بكل شئ تام العلم.',NULL,'833',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5036,'<span data-type="title" id=toc-5100>١٢ </span>- وأطيعوا الله فيما كلَّفكم به، وأطيعو الرسول فيما بلَّغ عن ربه، فإن أعرضتم عن هذه الطاعة فلن يضره إعراضكم، فإنما على رسولنا إبلاغكم الرسالة بلاغاً بيناً.',NULL,'833',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5037,'<span data-type="title" id=toc-5101>١٣ </span>- الله لا معبود - بحق - إلا هو، وعلى الله - وحده - فليعتمد المؤمنون فى كل أمورهم.',NULL,'833',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5038,'<span data-type="title" id=toc-5102>١٤ </span>- يا أيها الذين آمنوا إن من أزواجكم وأولادكم عدواً لكم بما يصرفونكم عن طاعة الله لتحقيق رغباتهم، فكونوا منهم على حذر، وإن تتجاوزوا عن سيئاتهم التى تقبل العفو، وتُعرضوا عنها وتستروها عليهم يغفر الله لكم، فإن الله واسع المغفرة والرحمة.',NULL,'833',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5039,'<span data-type="title" id=toc-5103>١٥ </span>- إنما أموالكم وأولادكم ابتلاء وامتحان، إن اغتررتم بهما فُتنتم، وإن شكرتم عليها أُجرتم، والله عنده أجر عظيم للشاكرين.',NULL,'833',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5040,'<span data-type="title" id=toc-5104>١٦ </span>- فابذلوا فى تقوى الله جُهدكم وطاقتكم، واسمعوا مواعظه وأطيعوا أوامره، وأنفقوا مما رزقكم فيما أمر بالإنفاق فيه، وافعلوا خيراً لأنفسكم، ومَن يكفِهِ الله بخل نفسه وحرصها على المال فأولئك هم الفائزون بكل خير.',NULL,'833',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5041,'<span data-type="title" id=toc-5105>١٧ </span>- إن تنفقوا فى وجوه البر إنفاقاً مخلصين فيه يُضاعف الله لكم ثواب ما أنفقتم، ويغفر لكم ما فرط من ذنوبكم، والله عظيم الشكر والمكافأة للمحسنين، حليم فلا يُعَجِّل بالعقوبة على مَن عصاه.',NULL,'833',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5042,'<span data-type="title" id=toc-5106>١٨ </span>- هو عالم كل ما غاب وما حضر، القوى القاهر، الحكيم فى تدبير خلقه الذى يضع كل شئ موضعه.',NULL,'833',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5043,'<span data-type="title" id=toc-5108>١ </span>- يا أيها النبى إذا أردتم أن تُطلِّقوا النساء فطلّقوهن مستقبلاً لعدتهن، واضبطوا العدة، واتقوا الله ربكم. لا تخرجوا المطلقات من مساكنهن التى طلقن فيها، ولا يخرجن منها إلا أن يفعلن فعلة منكرة واضحة، تلك الأحكام المتقدمة معالم الله، شرعها لعباده، ومن يُجاوز حدود الله فقد ظلم نفسه. لا تدرى لعل الله يوجد بعد ذلك<span data-type="title" id=toc-5107> الطلاق </span>أمراً لا تتوقعه، فيتحابان.',NULL,'834',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5044,'<span data-type="title" id=toc-5109>٢ </span>- فإذا قاربت المطلقات نهاية عدتهن، فراجعوهن مع حُسن مُعاشرة، أو فارقوهن من غير مضارة، وأشهدوا على الرجعة والمفارقة صاحبى عدالة منكم. وأدّوا الشهادة على وجهها خالصة لله. ذلكم الذى أمرتم به يوعظ به مَن كان يؤمن بالله واليوم الآخر، ومَن يخف الله فيقف عند أوامره ونواهيه يجعل له مخرجاً من كل ضيق.',NULL,'834',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5045,'<span data-type="title" id=toc-5110>٣ </span>- ويُهيِّئ له أسباب الرزق من حيث لا يخطر على باله، ومَن يُفوض إلى الله كل أموره فهو كافيه، إن الله بالغ مراده، منفذ مشيئته. قد جعل الله لكل شئ وقتاً لا يعدوه، وتقديراً لا يجاوزه.',NULL,'835',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5046,'<span data-type="title" id=toc-5111>٤ </span>- والمعتدات من المطلقات اللائى يئسن من لمحيض لكبرهن، إن لم تعلموا كيف يعتددن، فعدتهن ثلاثة أشهر، واللائى لم يحضن عدتهن كذلك، وصواحب الحمل عدتهن أن يضعن حملهن، ومن يتق الله فينفذ أحكامه يُيسر الله له أموره.',NULL,'835',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5047,'<span data-type="title" id=toc-5112>٥ </span>- ذلك التشريع أمر الله - لا غير - أنزله إليكم، ومن يتق الله بالمحافظة على أحكامه يمح عنه خطاياه، ويُعظم له الجزاء.',NULL,'835',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5048,'<span data-type="title" id=toc-5113>٦ </span>- أسكنوا المعتدات بعض أماكن سكناكم، على قدر طاقتكم، ولا تلحقوا بهن ضرراً، لتضيقوا عليهن فى السكنى. وإن كن ذوات حمل فأنفقوا عليهن حتى يضعن حملهن، فإن أرضعت المطلقات لكم أولادكم فوفوهن أجورهن، وليأمر بعضكم بعضاً بما تُعورف عليه من سماحة وعدم تعنّت، وإن اختلف الرجل والمرأة فطلبت المرأة فى أجرة الرضاع كثيرا ولم يجبها الرجل إلى ذلك أو بذل الرجل قليلاً ولم توافقه عليه فليسترضع غيرها.',NULL,'835',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5049,'<span data-type="title" id=toc-5114>٧ </span>- لينفق صاحب بسطة فى الرزق مما بسطه الله له، ومن ضُيِّق عليه رزقه فلينفق مما أعطاه الله، لا يكلف الله نفساً إلا ما أعطاها، سيجعل الله بعد ضيق فرجا.',NULL,'835',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5050,'<span data-type="title" id=toc-5115>٨ </span>-، ٩ - وكثير من القرى التى تجبَّر أهلها وأعرضوا عن أمر ربهم ورسله فحاسبناها حساباً شديداً. بتقصى كل ما فعلوه ومناقشتهم، وعذَّبناها عذاباً فظيعاً، فتجرَّعوا سوء مآل أمرهم، وكان عاقبة أمرهم خسراناً شديداً.',NULL,'835',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5051,'<span data-type="title" id=toc-5116>١٠ </span>-، ١١ - هيأ الله لأهل القرى المتجبرين عذاباً بالغ الشدة، فاحذروا غضب الله - يا أصحاب العقول الراجحة - الذين اتصفوا بالإيمان. قد أنزل الله إليكم - ذا شرف ومكانة - رسولاً يقرأ عليكم آيات الله مُبَيِّنات لكم الحق من الباطل. ليُخرج الذين آمنوا وعملوا الصالحات من ظلمات الضلال إلى نور الهداية، ومَن يصدِّق بالله ويعمل عملاً صالحاً يدخله جنات تجرى من خلالها الأنهار، مخلدا فيها أبداً، قد أحسن الله للمؤمن الصالح رزقاً طيباً.',NULL,'836',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5052,'<span data-type="title" id=toc-5117>١٢ </span>- الله - وحده - الذى خلق سبع سموات ومن الأرض مثلهن، يجرى أمره بينهن، لتعلموا أن الله على كل شئ تام القدرة، وأن الله قد أحاط بكل شئ علما.',NULL,'836',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5053,'<span data-type="title" id=toc-5119>١ </span>- يا أيها النبى لِمَ تمنع نفسك عمَّا أحل الله لك؟! تريد إرضاء زوجاتك، والله بالغ المغفرة واسع الرحمة.',NULL,'837',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5054,'<span data-type="title" id=toc-5120>٢ </span>- قد شرع الله لكم تحليل أيمانكم بالتكفير عنها، والله سيَّدكم ومُتولى أموركم، وهو التام العلم، فيشرع لكم ما فيه خيركم، الحكيم فيما يشرعه لكم.',NULL,'837',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5055,'<span data-type="title" id=toc-5121>٣ </span>- واذكر حين أسرَّ النبى إلى بعض أزواجه حديثاً، فلما أخبرت به، وأطلع الله نبيه على إفشائه، أعلم بها بعضاً، وأعرض - تكرماً - عن بعض، فلما أعلمها به، قالت: من أعلمك هذا؟ قال: أنبأنى العليم بكل شئ. الذى لا تخفى عليه خافية.',NULL,'837',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5056,'<span data-type="title" id=toc-5122>٤ </span>- إن ترجعا إلى الله نادمتين فقد فعلتما ما يوجب التوبة. لأنه قد مالت قلوبكما عما يحبه رسول الله من حفظ سره، وإن تتعاونا عليه بما يسوؤه، فإن الله هو ناصره وجبريل والمتصفون بالصلاح من المؤمنين والملائكة - بعد نصرة الله - مظاهرون له ومعينون.',NULL,'838',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5057,'<span data-type="title" id=toc-5123>٥ </span>- عسى ربُّه إن طلقكن - أيتها الزوجات - أن يزوجه بدلا منكن زوجات خاضعات لله بالطاعة، مصدقات بقلوبهن. خاشعات لله. رجّاعات إلى الله. متعبدات متذللات له. ذاهبات فى طاعة الله كل مذهب، ثيبات وأبكاراً.',NULL,'838',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5058,'<span data-type="title" id=toc-5124>٦ </span>- يا أيها الذين آمنوا: احفظوا أنفسكم وأهليكم من نار وقودها الناس والحجارة. يقوم على أمرها وتعذيب أهلها ملائكة قساة فى معاملتهم أقوياء. يتقبلون أوامر الله، وينفذون ما يؤمرون به، غير متوانين.',NULL,'838',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5059,'<span data-type="title" id=toc-5125>٧ </span>- يُقال للكافرين يوم القيامة: لا تلتمسوا المعاذير اليوم، إنما تجزون ما كنتم تعملون فى الدنيا.',NULL,'838',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5060,'<span data-type="title" id=toc-5126>٨ </span>- يا أيها الذين آمنوا: ارجعوا إلى الله من ذنوبكم رجعة بالغة فى الإخلاص، عسى ربكم أن يمحو عنكم سيئاتكم، ويُدخلكم جنات تجرى من تحت قصورها وأشجارها الأنهار. يوم يرفع الله شأن النبى والذين آمنوا معه، نور هؤلاء يسير أمامهم وهم بأيمانهم، يقولون - تقرباً إلى الله -: يا سيدنا ومالك أمرنا، أتمم لنا نورنا، حتى نهتدى إلى الجنة، وتجاوز عن ذنوبنا إنك على كل شئ تام القدرة.',NULL,'838',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5061,'<span data-type="title" id=toc-5127>٩ </span>- يا أيها النبى: جاهد الكفار الذين أعلنوا كفرهم، والمنافقين الذين أبطنوه بما تملكه من قوة وحُجة. واشتد على الفريقين فى جهادك، ومستقرهم جهنم، وبئس المآل مآلهم.',NULL,'838',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5062,'<span data-type="title" id=toc-5128>١٠ </span>- ذكر الله حالة عجيبة تعرف بها أحوال مماثلة للذين كفروا؛ هى امرأة نوح وامرأة لوط، كانتا تحت عصمة عبدين من خالص عبادنا صالحين، فخانتاهما بالتآمر عليهما وإفشاء سرهما إلى قومهما، فلم يدفع هذان العبدان الصالحان عن زوجتيهما من عذاب الله شيئاً، وقيل للزوجين عند هلاكهما: ادخلا النار مع الداخلين.',NULL,'839',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5063,'<span data-type="title" id=toc-5129>١١ </span>- وضرب الله مثلاً للذين آمنوا امرأة فرعون إذ قالت: رب ابن لى عندك - قريباً من رحمتك - بيتاً فى الجنة، وأنقذنى من سلطان فرعون وعمله، المسرف فى الظلم، وأنقذنى من القوم المعتدين.',NULL,'839',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5064,'<span data-type="title" id=toc-5130>١٢ </span>- وضرب الله كذلك مثلاً للذين آمنوا مريم ابنة عمران التى حفظت فرجها فنفخنا فيه من روحنا، فحملت بعيسى، وصدقت بكلمات الله، وهى أوامره ونواهيه وكتبه المنزلة على رسله، وكانت من عداد المواظبين على طاعة الله.',NULL,'839',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5065,'<span data-type="title" id=toc-5132>١ </span>- تعالى وازدادت بركات مَن يملك - وحده - التصرف فى جميع المخلوقات، وهو على كل شئ تام القدرة.',NULL,'840',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5066,'<span data-type="title" id=toc-5133>٢ </span>- الذى خلق الموت والحياة لغاية أرادها، هى أن يختبركم أيكم أصلح عملا وأخلص نية، وهو الغالب الذى لا يعجزه شئ. العفوُّ عن المقصرين.',NULL,'840',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5067,'<span data-type="title" id=toc-5134>٣ </span>- الذى أبدع سبع سموات متوافقة على سنَّة واحدة من الإتقان. ما ترى فى صنع الله - الذى عمَّت رحمته خلقه - أى تفاوت. فأعد بصرك. هل تجد أى خلل؟ .',NULL,'840',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5068,'<span data-type="title" id=toc-5135>٤ </span>- ثم أعد البصر مرة بعد مرة. يرجع إليك البصر مردوداً عن إصابة ما التمس من عيب، وهو متعب كليل.',NULL,'840',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5069,'<span data-type="title" id=toc-5136>٥ </span>- ولقد زيَّنا السماء القريبة التى تراها العيون بكواكب مضيئة، وجعلناها مصادر شهب، يُرْجَم بها الشياطين، وأعددنا لهم فى الآخرة عذاب النار الموقدة.',NULL,'841',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5070,'<span data-type="title" id=toc-5137>٦ </span>- وللذين لم يؤمنوا بربهم عذاب جهنم، وساءت عاقبة لهم هذه العاقبة.',NULL,'841',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5071,'<span data-type="title" id=toc-5138>٧ </span>-، ٨ - إذا طُرحوا فيها سمعوا لها صوتاً منكراً، وهى تغلى غلياناً شديداً. تكاد تتقطع وتتفرق من شدة الغضب عليهم. كلما أُلْقِىَ فيها جماعة منهم سألهم الموكلون بها - موبخين لهم -: ألم يأتكم رسول يحذركم لقاء يومكم هذا؟ .',NULL,'841',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5072,'<span data-type="title" id=toc-5139>٩ </span>- قالوا مُجيبين: قد جاءنا نذير فكذبناه، وقلنا: ما نزَّل الله من شئ عليك ولا على غيرك من الرسل، ما أنتم - أيها المدعون للرسالة - إلا فى انحراف بعيد عن الحق.',NULL,'841',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5073,'<span data-type="title" id=toc-5140>١٠ </span>- وقالوا: لو كنا نسمع سماع مَن يطلب الحق، أو نفكر فيما نُدعى إليه؛ ما كنا فى عداد أصحاب السعير.',NULL,'841',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5074,'<span data-type="title" id=toc-5141>١١ </span>- فاعترفوا بتكذيبهم وكفرهم، فبعداً لأصحاب السعير عن رحمة الله.',NULL,'841',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5075,'<span data-type="title" id=toc-5142>١٢ </span>- إن الذين يخافون ربهم - وهم لا يرونه - لهم مغفرة لذنوبهم، وثواب عظيم على حسناتهم.',NULL,'841',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5076,'<span data-type="title" id=toc-5143>١٣ </span>- واخفوا قولكم أو اعلنوه فهما عند الله سواء؛ لأنه عظيم الإحاطة، عليم بخفايا الصدور.',NULL,'842',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5077,'<span data-type="title" id=toc-5144>١٤ </span>- أليس يعلم الخالق لجميع الأشياء خلقه، وهو العالم بدقائق الأشياء وحقائقها؟ {',NULL,'842',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5078,'<span data-type="title" id=toc-5145>١٥ </span>- هو الذى جعل لكم الأرض طيِّعة مُيَسرة، فامشوا فى جوانبها، وكلوا من رزقه الذى يخرجه لكم منها، وإليه - وحده - البعث للجزاء.',NULL,'842',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5079,'<span data-type="title" id=toc-5146>١٦ </span>- أأمنتم من فى السماء - سلطانه - أن يقطع بكم الأرض، فيفاجئكم أنها تضطرب اضطراباً شديداً؟}',NULL,'842',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5080,'<span data-type="title" id=toc-5147>١٧ </span>- بل أأمنتم من فى السماء - سلطانه - أن يرسل عليكم ريحاً ترجمكم بالحصباء؟ {فستعلمون حينئذ هول وعيدى لكم.',NULL,'842',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5081,'<span data-type="title" id=toc-5148>١٨ </span>- ولقد كذَّب الذين من قبل قومك رسلهم، فعلى أى حال من الشدة كان إنكارى عليهم بإهلاكهم وأخذهم؟}',NULL,'842',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5082,'<span data-type="title" id=toc-5149>١٩ </span>- هل أصابهم العمى ولم ينظروا إلى الطير فوقهم باسطات أجنحتهن، ويقْبِضْنَهُن - حيناً بعد حين ما يمسكهن أن يقعن إلا الرحمن؟! إنه بكل شئ عليم خبير. يعطيه ما يصلح عليه أمره.',NULL,'842',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5083,'<span data-type="title" id=toc-5150>٢٠ </span>- بل من هذا الذى هو قوة لكم يدفع عنكم العذاب سوى الرحمن؟ {ما الكافرون إلا فى غرور بما يتوهمون.',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5084,'<span data-type="title" id=toc-5151>٢١ </span>- بل من هذا الذى يرزقكم - بما تكون به حياتكم وسعادتكم - إن حبس الله رزقه عنكم؟} بل تمادى الكافرون فى استكبارهم وشرودهم عن الحق.',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5085,'<span data-type="title" id=toc-5152>٢٢ </span>- فهل تنعكس الحال، فيكون مَنْ يمشى منكبا على وجهه أهدى فى سيره وقصده. أم من يمشى مستوى القامة على طريق لا اعوجاج فيه؟ {',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5086,'<span data-type="title" id=toc-5153>٢٣ </span>- قل: هو الذى أوجدكم من العدم، وجعل لكم السمع والأبصار والأفئدة التى هى أسباب عملكم وسعادتكم. قليلاً ما تؤدون شكر هذه النعم لواهبها.',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5087,'<span data-type="title" id=toc-5154>٢٤ </span>- قل: هو الذى بثكم فى الأرض، وإليه - وحده - تجمعون لحسابكم وجزائكم.',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5088,'<span data-type="title" id=toc-5155>٢٥ </span>- ويقول المنكرون للبعث: متى يتحقق هذا الوعد بالنشور؟} نبئونا بزمانه إن كنتم صادقين؟ {',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5089,'<span data-type="title" id=toc-5156>٢٦ </span>- قل - يا محمد -: هذا علم اختص الله به، وإنما أنا نذير بيِّن الإنذار.',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5090,'<span data-type="title" id=toc-5157>٢٧ </span>- فلمَّا عاينوا الموعود به قريباً منهم، علت وجوه الكافرين الكآبة والذلة، وقيل - توبيخاً وإيلاماً لهم -: هذا الذى كنتم تطلبون تعجيله.',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5091,'<span data-type="title" id=toc-5158>٢٨ </span>- قل: أخبرونى إن أماتنى الله ومَن معى من المؤمنين كما تتمنون، أو رحمنا فأخر آجالنا وعافانا من عذابه - فقد أنجانا فى الحالين - فمَن يمنع الكافرين من عذاب أليم استحقوه بكفرهم وغرورهم بآلهتهم؟}',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5092,'<span data-type="title" id=toc-5159>٢٩ </span>- هو الرحمن صدَّقنا به ولم تصدِّقوا، وعليه - وحده - اعتمدنا، واعتمدتم على غيره، فستعلمون إذا نزل العذاب أى الفريقين هو فى انحراف بعيد عن الحق.',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5093,'<span data-type="title" id=toc-5160>٣٠ </span>- قل: أخبرونى إن أصبح ماؤكم ذاهباً فى الأرض لا تصلون إليه بأى سبب، فمَن غير الله يأتيكم بماء طاهر متدفق يصل إليه كل من أراده؟! .',NULL,'843',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5094,'<span data-type="title" id=toc-5162>١ </span>- ن: حرف من حروف المعجم التى بدئت بعض السور بها تحدياً للمكذبين وتنبيهاً للمصدقين.',NULL,'844',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5095,'<span data-type="title" id=toc-5163>٢ </span>- أقسم بالقلم الذى يكتب به الملائكة وغيرهم، وبما يكتبونه من الخير والمنافع، ما أنت - وقد أنعم الله عليك بالنبوة - بضعيف العقل، ولا سفيه الرأى.',NULL,'844',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5096,'<span data-type="title" id=toc-5164>٣ </span>- وإن لك على ما تلقاه فى تبليغ الرسالة لثواباً عظيماً غير مقطوع.',NULL,'844',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5097,'<span data-type="title" id=toc-5165>٤ </span>- وإنك لمستمسك بمحاسن الصفات ومحاسن الأفعال التى فطرك الله عليها.',NULL,'844',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5098,'<span data-type="title" id=toc-5166>٥ </span>-، ٦ - فعن قريب تبصر - يا محمد - ويبصر الكافرون بأيكم الجنون.',NULL,'844',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5099,'<span data-type="title" id=toc-5167>٧ </span>- إن ربك هو أعلم بمن حاد عن سبيله، وهو أعلم بالعقلاء المهتدين إليه.',NULL,'844',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5100,'<span data-type="title" id=toc-5168>٨ </span>-، ٩ - فلا تترك ما أنت عليه من مخالفة للمكذبين. تمنوا لو تلين لهم بعض الشئ، فهم يلينون لك طمعاً فى تجاوبك معهم.',NULL,'844',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5101,'<span data-type="title" id=toc-5169>١٠ </span>-، ١١، ١٢، ١٣ - ولا تترك ما أنت عليه من مخالفتك كُلّ كَثِيرِ الحلف. حقير. عيّاب. مغتاب. نقّال للحديث بين الناس على وجه الإفساد بينهم. شديد الصد عن الخير. معتد. كثير الآثام. غليظ القلب. جاف الطبع. لئيم معروف الشر. فوق ما له من تلك الصفات الذميمة.',NULL,'845',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5102,'<span data-type="title" id=toc-5170>١٤ </span>-، ١٥ - لأنه كان صاحب مال وبنين. كذَّبَ بآياتنا وأعرض عنها. إذا يتلى عليه القرآن قال: هذا قصص الأولين وخرافاتهم.',NULL,'845',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5103,'<span data-type="title" id=toc-5171>١٦ </span>- سنجعل على أنفه علامة لازمة. ليكون مفتضحاً بها أمام الناس.',NULL,'845',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5104,'<span data-type="title" id=toc-5172>١٧ </span>-، ١٨ - إنا اختبرنا أهل مكة بالإنعام عليهم فكفروا. كما اختبرنا أصحاب الجنة حين حلفوا ليقطعن ثمار جنتهم مبكرين، ولا يذكرون الله فيعلقون الأمر بمشيئته.',NULL,'845',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5105,'<span data-type="title" id=toc-5173>١٩ </span>-، ٢٠ - فنزل بها بلاء شديد من ربك ليلاً وهم نائمون، فأصبحت كالليل المظلم مما أصابها.',NULL,'845',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5106,'<span data-type="title" id=toc-5174>٢١ </span>-، ٢٢ - فنادى بعضهم بعضاً عند الصباح. أن بكِّروا مقبلين على حرثكم إن كنتم مصرين على قطع الثمار.',NULL,'845',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5107,'<span data-type="title" id=toc-5175>٢٣ </span>-، ٢٤ - فاندفعوا وهم يتهامسون متواصين: ألا يمكنن أحد منكم اليوم مسكيناً من دخولها عليكم.',NULL,'845',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5108,'<span data-type="title" id=toc-5176>٢٥ </span>- وساروا أول النهار إلى جنتهم على قصدهم السيئ الذين توهموا أنهم قادرين على تنفيذه.',NULL,'845',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5109,'<span data-type="title" id=toc-5177>٢٦ </span>-، ٢٧ - فلما رأوها سواداً محترقة قالوا مضطربين: إنا لضالون فما هذه بجنتنا ثم بعد ذلك قالوا: بل هى جنتنا، ونحن محرومون.',NULL,'845',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5110,'<span data-type="title" id=toc-5178>٢٨ </span>- قال أعدلهم وأخيرهم - لائماً لهم -: ألم أقل لكم حين تواصيتم بحرمان المساكين: هلا تذكرون الله فتعدلوا عن نيتكم؟ {',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5111,'<span data-type="title" id=toc-5179>٢٩ </span>- قالوا - بعد أن ثابوا إلى رشدهم -: ننزه الله أن يكون قد ظلمنا بما أصابنا. إنا كنا ظالمين أنفسنا لسوء قصدنا.',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5112,'<span data-type="title" id=toc-5180>٣٠ </span>-، ٣١ - فأقبل بعضهم على بعض يلوم كل منهم الآخر، قالوا: يا هلاكنا، إنا كنا مسرفين فى ظلمنا.',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5113,'<span data-type="title" id=toc-5181>٣٢ </span>- عسى ربنا أن يعوِّضنا خيراً من جنتنا. إنا إلى ربنا - وحده - راغبون فى عفوه وتعويضه.',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5114,'<span data-type="title" id=toc-5182>٣٣ </span>- مثل ذلك الذى أصاب الجنة، يكون عذابى الذى أُنزلَه فى الدنيا بمَن يستحقه، ولعذاب الآخرة أكبر لو كان الناس يعلمون ذلك.',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5115,'<span data-type="title" id=toc-5183>٣٤ </span>- إن للمتقين عند ربهم جنات النعيم الخالص.',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5116,'<span data-type="title" id=toc-5184>٣٥ </span>-، ٣٦ - أنظلم فى حكمنا فنجعل المسلمين كالكافرين؟} ماذا أصابكم؟ {كيف تحكمون مثل هذا الحكم الجائر؟} .',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5117,'<span data-type="title" id=toc-5185>٣٧ </span>-، ٣٨ - بل ألكم كتاب من الله فيه تقرأون؟ {إن لكم فيه للذى تتخيرونه}',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5118,'<span data-type="title" id=toc-5186>٣٩ </span>- بل ألكم عهود علينا مؤكدة بالأَيْمان باقية إلى يوم القيامة، إن لكم للذى تحكمون به؟ {',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5119,'<span data-type="title" id=toc-5187>٤٠ </span>- سل المشركين - يا محمد -: أيهم بذلك الحكم كفيل؟}',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5120,'<span data-type="title" id=toc-5188>٤١ </span>- بل ألهم مَن يشاركهم ويذهب مذهبهم فى هذا القول؟! فليأتوا بشركائهم إن كانوا صادقين فى دعواهم.',NULL,'846',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5121,'<span data-type="title" id=toc-5189>٤٢ </span>-، ٤٣ - يوم يشتد الأمر ويصعب، ويُدْعَى الكفار إلى السجود - تعجيزاً وتوبيخاً - فلا يستطيعون، منكسرة أبصارهم تغشاهم ذلة مرهقة، وقد كانوا يُدعوْن فى الدنيا إلى السجود وهم قادرون فلا يسجدون.',NULL,'847',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5122,'<span data-type="title" id=toc-5190>٤٤ </span>- فدعنى - يا محمد - ومن يكذِّب بهذا القرآن سندنيهم من العذاب درجة درجة من الجهة التى لا يعلمون أن العذاب يأتى منها.',NULL,'847',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5123,'<span data-type="title" id=toc-5191>٤٥ </span>- وأمهلهم بتأخير العذاب. إن تدبيرى قوى لا يفلت منه أحد.',NULL,'847',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5124,'<span data-type="title" id=toc-5192>٤٦ </span>- بل أتسألهم أجراً على تبليغ الرسالة، فهم من غرامة كلفتهم إيَّاها مثقلون؟!',NULL,'847',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5125,'<span data-type="title" id=toc-5193>٤٧ </span>- بل أعندهم علم الغيب فهم يكتبون عنه ما يحكمون به؟',NULL,'847',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5126,'<span data-type="title" id=toc-5194>٤٨ </span>- فاصبر لإمهالهم وتأخير نصرك عليهم، ولا تكن كيونس صاحب الحوت - فى العجلة والغضب على قومه - حين نادى ربه وهو مملوء غيظاً وغضباً طالباً تعجيل عذابهم.',NULL,'847',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5127,'<span data-type="title" id=toc-5195>٤٩ </span>- لولا أن تداركته نعمة ربه بقبول توبته، لطرح من بطن الحوت بالفضاء، وهو معاقب بزلته.',NULL,'847',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5128,'<span data-type="title" id=toc-5196>٥٠ </span>- فاصطفاه ربه بقبول توبته، فجعله من الصالحين.',NULL,'847',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5129,'<span data-type="title" id=toc-5197>٥١ </span>- وإن يكاد الكافرون ليزيلونك عن مكانك، بنظرهم إليك - عداوةً وبُغْضاً - حين سمعوا القرآن، ويقولون: إنك لمجنون.',NULL,'847',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5130,'<span data-type="title" id=toc-5198>٥٢ </span>- وما القرآن إلا عظة وحكمة وتذكير للعالمين.',NULL,'847',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5131,'<span data-type="title" id=toc-5200>١ </span>-، ٢ - القيامة الواقعة حقاً. ما القيامة الواقعة حقاً؟ .',NULL,'848',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5132,'<span data-type="title" id=toc-5201>٣ </span>- وأى شئ أدراك حقيقتها، وصور لك هولها وشدتها؟ .',NULL,'848',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5133,'<span data-type="title" id=toc-5202>٤ </span>- كذبت ثمود وعاد بالقيامة التى تقرع العالمين بأهوالها وشدائدها.',NULL,'848',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5134,'<span data-type="title" id=toc-5203>٥ </span>- فأما ثمود فأهلكوا بالواقعة التى جاوزت الحد فى الشدة.',NULL,'848',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5135,'<span data-type="title" id=toc-5204>٦ </span>- وأما عاد فأهلكوا بريح باردة عنيفة متمردة.',NULL,'848',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5136,'<span data-type="title" id=toc-5205>٧ </span>- سلَّطَهَا الله عليهم سبع ليال وثمانية أيام متتابعة لا تنقطع، فترى القوم فى مهاب الريح موتى كأنهم أصول نخل خاوية أجوافها.',NULL,'848',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5137,'<span data-type="title" id=toc-5206>٨ </span>- فهل ترى لهم من نفس باقية دون هلاك.',NULL,'848',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5138,'<span data-type="title" id=toc-5207>٩ </span>- وجاء فرعون ومَن قبله من الأمم التى كفرت، والجماعة المنصرفة عن الحق والفطرة السليمة بالأفعال ذات الخطأ العظيم الفاحش.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5139,'<span data-type="title" id=toc-5208>١٠ </span>- فعصت كل أمة من هؤلاء رسول ربهم، فأخذهم بعقابه أخذة زائدة فى الشدة.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5140,'<span data-type="title" id=toc-5209>١١ </span>- إنا لما جاوز الماء حدَّه، وعلا فوق الجبال فى حادث الطوفان. حملناكم - بحمل أصولكم - فى السفينة الجارية.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5141,'<span data-type="title" id=toc-5210>١٢ </span>- لنجعل الواقعة التى كان فيها نجاة المؤمنين وإغراق الكافرين عبرة لكم وعظة، وتحفظها كلُّ أذُنٍ حافظة لما تسمع.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5142,'<span data-type="title" id=toc-5211>١٣ </span>-، ١٤ - فإذا نفخ فى الصور نفخة واحدة، ورفعت الأرض والجبال عن موضعهما، فَدُكَّتا مرة واحدة.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5143,'<span data-type="title" id=toc-5212>١٥ </span>-، ١٦ - فيومئذٍ نزلت النازلة، وانشقت السماء بزوال أحكامها، فهى يومئذٍ ضعيفة بعد أن كانت محكمة قوية.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5144,'<span data-type="title" id=toc-5213>١٧ </span>- والملائكة على جوانبها، ويحمل عرش ربك فوق هؤلاء الملائكة يومئذ ثمانية.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5145,'<span data-type="title" id=toc-5214>١٨ </span>- يومئذ تعرضون للحساب لا يخفى منكم أى سر كنتم تكتمونه.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5146,'<span data-type="title" id=toc-5215>١٩ </span>- فأما مَن أُعطى كتابه بيمينه فيقول - معلناً سروره لمن حوله -: خذوا اقرأوا كتابى.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5147,'<span data-type="title" id=toc-5216>٢٠ </span>- إنى أيقنت فى الدنيا أنى ملاق حسابى، فأعددت نفسى لهذا اللقاء.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5148,'<span data-type="title" id=toc-5217>٢١ </span>- فهو فى عيشة يعمّها الرضى.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5149,'<span data-type="title" id=toc-5218>٢٢ </span>- فى جنة رفيعة المكان والدرجات.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5150,'<span data-type="title" id=toc-5219>٢٣ </span>- ثمارها قريبة التناول.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5151,'<span data-type="title" id=toc-5220>٢٤ </span>- كلوا واشربوا أكلاً وشرباً - لا مكروه فيهما، ولا أذى منهما - بما قدمتم من الأعمال الصالحة فى أيام الدنيا الماضية.',NULL,'849',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5152,'<span data-type="title" id=toc-5221>٢٥ </span>-، ٢٦ - وأما من أُعطى كتابه بشماله فيقول ندماً وحسرةً: يا ليتنى لم أُعْطَ كتابى، ولم أعلم ما حسابى.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5153,'<span data-type="title" id=toc-5222>٢٧ </span>- يا ليت الموتة التى متها كانت الفاصلة فى أمرى، فلم أُبعث بعدها.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5154,'<span data-type="title" id=toc-5223>٢٨ </span>-، ٢٩ - ما نفعنى شئ ملكته فى الدنيا. ذهبت عنى صحتى، وزالت قوتى.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5155,'<span data-type="title" id=toc-5224>٣٠ </span>-، ٣١، ٣٢ - يُقال لخزنة جهنم: خذوه فاجمعوا يديه إلى عنقه، ثم لا تدخلوه إلا نار الجحيم، ثم فى سلسلة بالغة الطول فاسلكوه.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5156,'<span data-type="title" id=toc-5225>٣٣ </span>-، ٣٤ - إنه كان لا يصدق بالله العظيم، ولا يحث أحداً على إطعام المسكين.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5157,'<span data-type="title" id=toc-5226>٣٥ </span>-، ٣٦، ٣٧ - فليس لهذا الكافر اليوم فى الجحيم قريب يدفع عنه، وليس له طعام إلا من غسالة أهل النار التى هى دم وقيح وصديد. لا يأكله إلا الخاطئون الذين تعمدوا الخطيئة وأصروا عليها.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5158,'<span data-type="title" id=toc-5227>٣٨ </span>-، ٣٩، ٤٠ - فلا أقسم بما تبصرون من المرئيات، وما لا تبصرون من عالم الغيب. إن القرآن لمن الله على لسان رسول رفيع المكانة.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5159,'<span data-type="title" id=toc-5228>٤١ </span>- وما القرآن بقول شاعر كما تزعمون، قليلاً ما يكون منكم إيمان بأن القرآن من عند الله.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5160,'<span data-type="title" id=toc-5229>٤٢ </span>- وما القرآن بسجع كسجع الكُهَّان الذى تعهدون. قليلاً ما يكون منكم تذكر وتأمل للفرق بينهما.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5161,'<span data-type="title" id=toc-5230>٤٣ </span>- هو تنزيل ممن تعهد العالمين بالخلق والتربية.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5162,'<span data-type="title" id=toc-5231>٤٤ </span>-، ٤٥، ٤٦ - ولو ادعى علينا شيئاً لم نقله لأخذنا منه كما يأخذ الآخذ بيمين من يُجهز عليه للحال، ثم لقطعنا منه نياط قلبه، فيموت لساعته.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5163,'<span data-type="title" id=toc-5232>٤٧ </span>- فليس منكم أحد - مهما بلغت قوته - يحجز عقابنا عنه.',NULL,'850',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5164,'<span data-type="title" id=toc-5233>٤٨ </span>- وإنَّ القرآن لعظة للذين يمتثلون أوامر الله، ويجتنبون نواهيه.',NULL,'851',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5165,'<span data-type="title" id=toc-5234>٤٩ </span>- وإنَّا لنعلم أن منكم مكذبين بالقرآن.',NULL,'851',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5166,'<span data-type="title" id=toc-5235>٥٠ </span>- وإنه لسبب فى ندامة شديدة على الجاحدين به، حين يرون عذابهم ونعيم المصدقين.',NULL,'851',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5167,'<span data-type="title" id=toc-5236>٥١ </span>- وإن القرآن لحق ثابت لا ريب فيه.',NULL,'851',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5168,'<span data-type="title" id=toc-5237>٥٢ </span>- فنزِّه ربك العظيم، ودُمْ على ذكر اسمه.',NULL,'851',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5169,'<span data-type="title" id=toc-5239>١ </span>-، ٢، ٣ - دعا داع - استعجالا على سبيل الاستهزاء - بعذاب واقع من الله للكافرين لا محالة. ليس لذلك العذاب راد يصرفه عنهم، فوقوعه لا شك فيه، لأنه من الله صاحب الأمر والحكم النافذ.',NULL,'852',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5170,'<span data-type="title" id=toc-5240>٤ </span>- تصعد الملائكة وجبريل إلى مهبط أمره فى يوم كان طوله خمسين ألف سنة من سِنى الدنيا.',NULL,'852',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5171,'<span data-type="title" id=toc-5241>٥ </span>-، ٦، ٧ - فاصبر - يا محمد - على استهزائهم واستعجالهم بالعذاب صبراً لا جزع فيه ولا شكوى منه. إن الكفار يرون يوم القيامة مستحيلاً لا يقع، ونراه هيناً فى قدرتنا غير متعذر علينا.',NULL,'852',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5172,'<span data-type="title" id=toc-5242>٨ </span>-، ٩، ١٠ - يوم تكون السماء كالفضة المذابة، وتكون الجبال كالصوف المنفوش، ولا يسأل قريب قريبه كيف حالك، لأن كل واحد منهما مشغول بنفسه.',NULL,'852',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5173,'<span data-type="title" id=toc-5243>١١ </span>-، ١٢، ١٣، ١٤ - يتعارفون بينهم حتى يعرف بعضهم بعضاً - يقيناً - وهو مع ذلك لا يسأله. يود الكافر لو يفدى نفسه من عذاب يوم القيامة ببنيه، وزوجته وأخيه، وعشيرته التى تضمه وينتمى إليها، ومن فى الأرض جميعاً، ثم يُنجيه هذا الفداء.',NULL,'853',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5174,'<span data-type="title" id=toc-5244>١٥ </span>-، ١٦، ١٧، ١٨ - ارتدع - أيها المجرم - عما تتمناه من الافتداء، إنّ النار لهب خالص، شديدة النزع ليديك ورجليك وسائر أطرافك، تنادى بالاسم مَن أعرض عن الحق، وترك الطاعة، وجمع المال فوضعه فى خزائنه، ولم يؤد حق الله فيه.',NULL,'853',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5175,'<span data-type="title" id=toc-5245>١٩ </span>-، ٢٠، ٢١، ٢٢، ٢٣ - إن الإنسان طُبع على الهلع، شديد الجزع والسخط إذا مسه المكروه والعسر، شديد المنع والحرمان إذا أصابه الخير واليسر، إلا المصلين، الذين هم دائمون على صلاتهم فلا يتركونها فى وقت من الأوقات، فإن الله يعصمهم ويوفقهم إلى الخير.',NULL,'853',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5176,'<span data-type="title" id=toc-5246>٢٤ </span>-، ٢٥ - والذين فى أموالهم حق مُعَيَّن مشروع. لمن يسأل المعونة منهم، ولمن يتعفف عن سؤالها.',NULL,'853',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5177,'<span data-type="title" id=toc-5247>٢٦ </span>-، ٢٧، ٢٨ - والذين يُصدِّقون بيوم الجزاء فيتزودون له، والذين هم من عذاب ربهم خائفون فيتقونه ولا يقعون فى أسبابه. إن عذاب ربهم غير مأمون لأحد أن يقع فيه.',NULL,'853',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5178,'<span data-type="title" id=toc-5248>٢٩ </span>-، ٣٠، ٣١ - والذين هم حافظون لفروجهم فلا تغلبهم شهواتها، لكن على أزواجهم وإمائهم لا يحفظونها، لأنهم غير ملومين فى تركها على طبيعتها، فمن طلب متاعاً وراء الزوجات والإماء فأولئك هم المتجاوزون الحلال إلى الحرام.',NULL,'853',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5179,'<span data-type="title" id=toc-5249>٣٢ </span>-، ٣٣، ٣٤ - والذين هم لأمانات الشرع وأمانات العباد وما التزموه لله وللناس حافظون غير خائنين ولا ناقضين، والذين هم بشهاداتهم قائمون بالحق غير كاتمين لما يعلمون، والذين هم على صلاتهم يحافظون، فيؤدونها على أكمل الوجه وأفضله.',NULL,'854',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5180,'<span data-type="title" id=toc-5250>٣٥ </span>- أصحاب هذه الصفات المحمودة فى جنات مكرمون من الله تعالى.',NULL,'854',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5181,'<span data-type="title" id=toc-5251>٣٦ </span>-، ٣٧، ٣٨ - أى شئ ثبت للذين كفروا إلى جهتك مسرعين ملتفين عن يمينك وشمالك جماعات؟! أيطمع كل امرئ منهم - وقد سمع وعد الله ورسوله للمؤمنين بالجنة - أن يدخل جنة نعيم؟ .',NULL,'854',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5182,'<span data-type="title" id=toc-5252>٣٩ </span>- فليرتدعوا عن طمعهم فى دخولهم الجنة، إنا خلقناهم من ماء مهين.',NULL,'854',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5183,'<span data-type="title" id=toc-5253>٤٠ </span>-، ٤١ - فلا أقسم برب المشارق والمغارب من الأيام والكواكب، إنا لقادرون على أن نهلكهم ونأتى بمن هم أطوع منهم لله، وما نحن بعاجزين عن هذا التبديل.',NULL,'854',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5184,'<span data-type="title" id=toc-5254>٤٢ </span>- فاتركهم يخوضوا فى باطلهم، ويلعبوا بدنياهم، حتى يلاقوا يومهم الذى يوعدون فيه العذاب.',NULL,'854',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5185,'<span data-type="title" id=toc-5255>٤٣ </span>-، ٤٤ - يوم يخرجون من القبور سراعاً إلى الداعى، كأنهم إلى ما كانوا قد نصبوه وعبدوه فى الدنيا من دون الله يسرعون، ذليلة أبصارهم، لا يستطيعون رفعها، تغشاهم المذلة والمهانة، ذلك اليوم كانوا يوعدون به فى الدنيا وهم يكذبون.',NULL,'854',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5186,'<span data-type="title" id=toc-5257>١ </span>- إنا أرسلنا<span data-type="title" id=toc-5256> نوح</span>اً إلى قومه، وقلنا له: أنذر قومك من قبل أن يأتيهم عذاب شديد الإيلام.',NULL,'855',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5187,'<span data-type="title" id=toc-5258>٢ </span>-، ٣، ٤ - قال نوح: يا قوم إنى لكم نذير مُبَيِّن رسالة ربكم بلغة تعرفونها، أن أطيعوا الله واخضعوا له فى أداء الواجبات، وخافوه بترك المحظورات، وأطيعونى فيما أنصح لكم به، يغفر الله لكم ذنوبكم ويُمد فى أعماركم إلى أجل مسمى جعله غاية الطول فى العمر، إن الموت إذا جاء لا يؤخر أبداً، لو كنتم تعلمون ما يحل بكم من الندامة عند انقضاء أجلكم لآمنتم.',NULL,'855',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5188,'<span data-type="title" id=toc-5259>٥ </span>-، ٦ - قال نوح: رب إنى دعوت قومى إلى الإيمان ليلاً ونهاراً بلا فتور، فلم يزدهم دعائى لهم إلا هروباً من طاعتك.',NULL,'855',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5189,'<span data-type="title" id=toc-5260>٧ </span>- وإنى كلما دعوتهم إلى الإيمان بك لتغفر لهم وضعوا أصابعهم فى آذانهم حتى لا يسمعوا دعوتى، وتغطوا بثيابهم حتى لا يروا وجهى، وأقاموا على كفرهم، وتعظموا عن إجابتى تعظماً بالغاً.',NULL,'856',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5190,'<span data-type="title" id=toc-5261>٨ </span>-، ٩ - ثم إنى دعوتهم إليك بصوت مرفوع، ثم إنى جهرت بالدعوة فى حال، وأخفيتها إخفاء فى حال أخرى، حتى أجرب كل خطة.',NULL,'856',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5191,'<span data-type="title" id=toc-5262>١٠ </span>-، ١١، ١٢ - فقلت لقومى: اطلبوا مغفرة الكفار والعصيان من ربكم، إنه لم يزل غفاراً لذنوب مَن يرجع إليه، يرسل السماء عليكم غزيرة الدر بالمطر، ويمدكم بأموال وبنين هما زينة الحياة الدنيا، ويجعل لكم بساتين تنعمون بجمالها وثمارها، ويجعل لكم أنهاراً تسقون منها زرعكم ومواشيكم.',NULL,'856',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5192,'<span data-type="title" id=toc-5263>١٣ </span>-، ١٤ - ما لكم لا تعظِّمون الله حق عظمته حتى ترجو تكريمكم بإنجائكم من العذاب، وقد خلقكم كَرَّاتٍ متدرجة، نطفاً ثم علقاً ثم مضغاً ثم عظاماً ولحما؟ .',NULL,'856',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5193,'<span data-type="title" id=toc-5264>١٥ </span>-، ١٦ - ألم تنظروا كيف خلق الله سبع سموات بعضها فوق بعض، وجعل القمر فى هذه السموات نوراً ينبعث منها، وجعل الشمس مصباحاً يبصر أهل الدنيا فى ضوئه ما يحتاجون إلى رؤيته.',NULL,'856',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5194,'<span data-type="title" id=toc-5265>١٧ </span>-، ١٨ - والله أنشأكم من الأرض فنبتم نباتاً عجيباً، ثم يعيدكم فى الأرض بعد الموت، ويخرجكم منها إخراجاً محققاً لا محالة.',NULL,'856',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5195,'<span data-type="title" id=toc-5266>١٩ </span>-، ٢٠ - والله جعل لكم الأرض مبسوطة لتكون لكم طرقاً واسعة.',NULL,'856',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5196,'<span data-type="title" id=toc-5267>٢١ </span>-، ٢٢ - قال نوح: رب إن قومى عصَونى فيما أمرتهم به من الإيمان والاستغفار، واتبع الضعفاء منهم مَن لم يزده مالُه وولدُه إلا خسراناً فى الآخرة، ومكر أصحاب الأموال والأولاد بتابعيهم من الضعفاء مكراً بالغ النهاية فى العِظَم.',NULL,'856',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5197,'<span data-type="title" id=toc-5268>٢٣ </span>-، ٢٤ - وقالوا لهم: لا تترُكنَّ عبادة آلهتكم، ولا تترُكُنَّ ودَّا ولا سواعاً ولا يغُوثَ ويعُوقَ ونسرا - وكانت أصناماً منحوتة على صور مختلفة من الحيوان - وقد أضل هؤلاء المتبوعون كثيراً من الناس، ولا تزد الظالمين لأنفسهم بالكفر والعناد إلا بُعداً عن الحق.',NULL,'857',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5198,'<span data-type="title" id=toc-5269>٢٥ </span>- بسبب ذنوبهم أُغرقوا بالطوفان، فأُدخلوا عقب هلاكهم ناراً عظيمة اللهب والإحراق، فلم يجدوا لهم من دون الله أنصاراً يدفعون عنهم العذاب.',NULL,'857',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5199,'<span data-type="title" id=toc-5270>٢٦ </span>- وقال نوح - بعد يأسه من قومه -: رب لا تترك على الأرض من الكافرين بك أحداً يدور فى الأرض ويدب عليها.',NULL,'857',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5200,'<span data-type="title" id=toc-5271>٢٧ </span>- إنك - يا رب - إن تتركهم دون إهلاك واستئصال يوقعوا عبادك فى الضلال ولا يلدوا إلا معانداً للحق شديد الكفر بك والعصيان لك.',NULL,'857',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5201,'<span data-type="title" id=toc-5272>٢٨ </span>- رب اعفُ عنى وعن والدىّ اللذين كانا سبباً فى وجودى، وعمن دخل بيتى مؤمناً بك، وعن المؤمنين والمؤمنات جميعاً، ولا تزد الكافرين إلا هلاكاً.',NULL,'857',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5202,'<span data-type="title" id=toc-5274>١ </span>-، ٢ - قل يا - محمد - لأمتك: أوحى الله إلىَّ أن جماعة من<span data-type="title" id=toc-5273> الجن </span>قد استمعوا إلى قراءتى للقرآن، فقالوا لقومهم: إنا سمعنا قرآناً بديعاً لم نسمع مثله من قبل، يدعو إلى الهدى والصواب، فآمنا - بالقرآن الذى سمعناه - ولن نشرك مع ربنا - الذى خلقنا وربانا - أحداً فى عبادته.',NULL,'858',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5203,'<span data-type="title" id=toc-5275>٣ </span>- وأنه تعالى قدر ربنا وعظمته، ما اتخذ زوجة ولا ولداً.',NULL,'858',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5204,'<span data-type="title" id=toc-5276>٤ </span>- وأنه كان يقول - جاهلنا على الله -: قولاً بعيداً عن الحق والصواب.',NULL,'858',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5205,'<span data-type="title" id=toc-5277>٥ </span>- وأنا ظننا أن لن تنسب الإنس والجن إلى الله ما لم يكن، ويصفوه بما لا يليق به.',NULL,'858',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5206,'<span data-type="title" id=toc-5278>٦ </span>- وأنه كان رجال من الإنس يستجيرون برجال من الجن، فزاد رجال الإنس رجال الإنس الجن طُغياناً وسفهاً.',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5207,'<span data-type="title" id=toc-5279>٧ </span>- وأن الجن ظنوا كما ظننتم - معشر الإنس - أن لن يبعث الله أحداً بعد الموت، ولا رسولاً من البشر إليهم.',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5208,'<span data-type="title" id=toc-5280>٨ </span>- وأنا طلبنا بلوغ السماء فوجدناها مُلئت حرساً قوياً من الملائكة وشهباً محرقة من جهتها.',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5209,'<span data-type="title" id=toc-5281>٩ </span>- وأنا كنا - قبل اليوم - نقعد من السماء مقاعد لاستراق أخبار السماء، فمَن يرد الاستماع الآن يجد له شهاباً مترصداً ينقَضُّ عليه فيهلكه.',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5210,'<span data-type="title" id=toc-5282>١٠ </span>- وأنا لا نعلم أعذاب أريد بمَن فى الأرض - من حراسة السماء لمنع الاستماع - أم أراد بهم ربهم خيراً وهدى؟ .',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5211,'<span data-type="title" id=toc-5283>١١ </span>- وأنا منا الأبرار المتقون ومنَّا دون ذلك، وهم قوم مقتصدون فى الصلاح، كنا ذوى مذاهب متفرقة.',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5212,'<span data-type="title" id=toc-5284>١٢ </span>- وأنا أيقنا أن لن نعجز الله - أينما كنا فى الأرض - ولن نعجزه هاربين من قضائه نحو السماء.',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5213,'<span data-type="title" id=toc-5285>١٣ </span>- وأنا لما سمعنا القرآن آمنا به، فمن يؤمن بربه فلا يخاف نقصاً من حسنة، ولا ظلماً يلحقه بزيادة فى سيئاته.',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5214,'<span data-type="title" id=toc-5286>١٤ </span>- وأنا منا المسلمون المِقرّون بالحق ومنا الحائدون عن طريق الهدى، فمن أسلم فأولئك قصدوا سبيل الحق مجتهدين فى اختياره.',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5215,'<span data-type="title" id=toc-5287>١٥ </span>- وأما الحائدون عن طريق الإسلام فكانوا لجهنم وقوداً.',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5216,'<span data-type="title" id=toc-5288>١٦ </span>- وأنه لو أطاع الإنس والجن ما يدعوهم إليه الإسلام ولم يحيدوا عنه لأعطاهم الله الماء الكثير الذى يحتاجون إليه.',NULL,'859',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5217,'<span data-type="title" id=toc-5289>١٧ </span>- لنختبرهم فيه كيف يشكرون لله نعمه عليهم، ومَن يُعرض عن عبادة ربه يدخله عذاباً شاقاً لا يطيقه.',NULL,'860',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5218,'<span data-type="title" id=toc-5290>١٨ </span>- وأُوحى إلىَّ أن المساجد لله - وحده - فلا تدعوا فيها غيره وأخلصوا لعبادته وحده.',NULL,'860',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5219,'<span data-type="title" id=toc-5291>١٩ </span>- وأُوحى إلىَّ أنه لما قام عبد الله - محمد - فى صلاته يعبد الله كاد الجن يكونون عليه جماعات ملتفة، تعجباً مما رأوه وسمعوه.',NULL,'860',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5220,'<span data-type="title" id=toc-5292>٢٠ </span>- قل: إنما أعبد ربى - وحده - ولا أشرك به فى العبادة أحداً.',NULL,'860',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5221,'<span data-type="title" id=toc-5293>٢١ </span>- قل: إنى لا أملك لكم دفع ضر ولا تحصيل هداية ونفع.',NULL,'860',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5222,'<span data-type="title" id=toc-5294>٢٢ </span>- قل: إنى لن يدفع عنى عذاب الله أحد إن عصيته، ولن أجد من دونه ملجأ أفر إليه من عذابه.',NULL,'860',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5223,'<span data-type="title" id=toc-5295>٢٣ </span>- لكن أملك تبليغاً عن الله ورسالاته التى بعثنى بها، ومن يعص الله ورسوله فأعرَضَ عن دين الله فإن له نار جهنم باقياً فيها أبداً.',NULL,'860',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5224,'<span data-type="title" id=toc-5296>٢٤ </span>- حتى إذا أبصروا ما يوعدونه من العذاب، فسيعلمون - عند حلوله بهم - من أضعف ناصراً وأقل عدداً أهم أم المؤمنون؟ .',NULL,'860',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5225,'<span data-type="title" id=toc-5297>٢٥ </span>- قل: ما أدرى - أيها الكافرون - أقريب ما توعدون من العذاب، أم يجعل له ربى غاية بعيدة؟!',NULL,'861',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5226,'<span data-type="title" id=toc-5298>٢٦ </span>-، ٢٧ - هو عالم الغيب، فلا يطلع على غيبه أحداً من خلقه، إلا رسولاً ارتضاه لعلم بعض الغيب، فإنه يدخل من بين يدى الرسول ومن خلفه حفظة من الملائكة تحول بينه وبين الوساوس.',NULL,'861',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5227,'<span data-type="title" id=toc-5299>٢٨ </span>- ليعلم الله - وعلمه كائن ومحيط - أن الأنبياء قد أبلغوا رسالات ربهم، وقد علم تفصيلا بما عندهم، وعلم عدد الموجودات كلها، لا يغيب عنه شئ منها.',NULL,'861',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5228,'<span data-type="title" id=toc-5301>١ </span>-، ٢، ٣، ٤ - يا أيها المتلفف بثيابه، قُمْ الليل مصلياً إلا قليلاً، قُمْ نصفْ الليل أو انقص من النصف قليلاً حتى تصل إلى الثلث، أو زد على النصف حتى تصل إلى الثلثين، واقرأ القرآن متمهلاً مبيناً للحروف والوقوف قراءة سالمة من أى نقصان.',NULL,'862',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5229,'<span data-type="title" id=toc-5302>٥ </span>- إنا سنلقى عليك - أيها الرسول - قرآناً مشتملاً على الأوامر والنواهى والتكاليف الشاقة.',NULL,'862',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5230,'<span data-type="title" id=toc-5303>٦ </span>- إن العبادة التى تكون بالليل، هى أشد رسوخاً فى القلب، وأبْيَن قولاً، لما يكون بالليل من هدوء وصفاء.',NULL,'862',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5231,'<span data-type="title" id=toc-5304>٧ </span>- إن لك فى النهار تقلباً فى مصالحك، واشتغالاً بأمور الرسالة، ففرِّغ نفسك ليلاً لعبادة ربك.',NULL,'862',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5232,'<span data-type="title" id=toc-5305>٨ </span>- وأجر على لسانك ذكر اسم مَن تعهدك بالخلق والتربية، وانقطع لعبادته من كل شئ انقطاعاً تاماً.',NULL,'862',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5233,'<span data-type="title" id=toc-5306>٩ </span>- هو مالك المشرق والمغرب لا معبود بحق إلا هو، فاتخذه كافياً لأمورك، كفيلاً بما وعدك.',NULL,'863',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5234,'<span data-type="title" id=toc-5307>١٠ </span>- واصبر على ما يقولون من الأباطيل، وجانبهم بقلبك، وخالفهم فى أفعالهم، مع الإغضاء عنهم. وترك الانتقام منهم.',NULL,'863',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5235,'<span data-type="title" id=toc-5308>١١ </span>- واتركنى والمكذبين - أصحاب النعيم - وأمهلهم إمهالاً قصير الأمد.',NULL,'863',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5236,'<span data-type="title" id=toc-5309>١٢ </span>-، ١٣ - إن لدينا للمكذبين فى الآخرة قيوداً ثقالاً، وناراً محرقة، وطعاماً ينشب فى الحلق لا يستساغ، وعذاباً شديد الإيلام لا يطاق.',NULL,'863',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5237,'<span data-type="title" id=toc-5310>١٤ </span>- يوم تتحرك الأرض والجبال حركة شديدة، وصارت الجبال رملاً مجتمعاً متناثراً بعد أن كانت حجارة صلبة متماسكة.',NULL,'863',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5238,'<span data-type="title" id=toc-5311>١٥ </span>-، ١٦ - إنا أرسلنا إليكم يا أهل مكة - محمداً - رسولاً يشهد عليكم يوم القيامة بالإجابة والامتناع، كما أرسلنا موسى إلى فرعون رسولاً، فعصى فرعون الرسول فأخذناه أخذاً ثقيلاً شديداً.',NULL,'863',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5239,'<span data-type="title" id=toc-5312>١٧ </span>- فكيف تدفعون عنكم إن كفرتم عذاب يوم يجعل الشُّبان لهوله شيوخاً ضعافاً.',NULL,'863',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5240,'<span data-type="title" id=toc-5313>١٨ </span>- السماء فى قوتها وعظمتها، شئ منشق فى ذلك اليوم لشدته وهوله، كان وعد الله واقعاً لا محالة.',NULL,'863',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5241,'<span data-type="title" id=toc-5314>١٩ </span>- إن هذه الآيات الناطقة بالوعد موعظة، فمَن شاء الانتفاع بها اتخذ إلى ربه سبيلا بالتقوى والخشية.',NULL,'863',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5242,'<span data-type="title" id=toc-5315>٢٠ </span>- إن ربك يعلم أنك تقوم - يا محمد - أقل من ثلثى الليل أحياناً، وتقوم نصفه وثلثه أحياناً أخرى، ويقوم طائفة من أصحابك كما تقوم، ولا يقدر على تقدير الليل والنهار وضبط ساعاتهما إلا الله. علم أنه لا يمكنكم إحصاء كل جزء من أجزاء الليل والنهار. فخفف عليكم، فاقرءوا فى الصلاة ما تيسر من القرآن. علم أنه سيكون منكم مرضى يشق عليهم قيام الليل، وآخرون يتنقلون فى الأرض للتجارة والعمل يطلبون رزق الله، وآخرون يجاهدون فى سبيل الله لإعلاء كلمته، فاقرءوا ما تيسر من القرآن وواظبوا على فرائض الصلاة، وأعطوا الزكاة الواجبة عليكم، وأقرضوا الله قرضاً حسناً بإعطاء الفقراء نافلة فوق ما وجب لهم، وما تقدموا لأنفسكم من خير تجدوا ثوابه عند الله هو خيراً مما خلفتم وتركتم، وأجزل ثواباً، واستغفروا الله من فعل السيئات والتقصير فى الحسنات. إن الله غفور لذنوب المؤمنين، رحيم بهم.',NULL,'864',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5243,'<span data-type="title" id=toc-5317>١ </span>-، ٢، ٣، ٤ - يا أيها المتدثر بثيابه قم من مضجعك فحذر الناس من عذاب الله إن لم يؤمنوا، وخُصَّ ربك - وحده - بالتعظيم، وثيابك فطهرها بالماء من النجاسة.',NULL,'865',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5244,'<span data-type="title" id=toc-5318>٥ </span>-، ٦، ٧ - والعذاب فاترك، أى دُمْ على هَجْر ما يوصل إلى العذاب، ولا تعط أحداً مستكثراً لما تعطيه إيَّاه، ولمرضاة ربك فاصبر على الأوامر والنواهى وكل ما فيه جهد ومشقة.',NULL,'865',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5245,'<span data-type="title" id=toc-5319>٨ </span>-، ٩، ١٠ - فإذا نفخ فى الصور، فذلك الوقت يومئذ شديد على الكافرين، غير سهل أن يخلصوا مما هم فيه من مناقشة الحساب، وغيره من الأهوال.',NULL,'865',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5246,'<span data-type="title" id=toc-5320>١١ </span>-، ١٢، ١٣، ١٤، ١٥ - اتركنى وحدى مع مَن خلقته، فإنى أكفيك أمره، جعلت له مالاً مبسوطاً واسعاً غير منقطع، وبنين حضوراً معه، وبسطت له الجاه والرياسة بسطة تامة، ثم يطمع أن أزيده فى ماله وبنيه وجاهه بدون شكر؟ .',NULL,'865',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5247,'<span data-type="title" id=toc-5321>١٦ </span>-، ١٧ - ردعاً له عن طمعه إنه كان للقرآن معانداً مكذباً، سأغشيه عقبة شاقة، لا يستطيع اقتحامها.',NULL,'866',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5248,'<span data-type="title" id=toc-5322>١٨ </span>-، ١٩، ٢٠ - إنه فكَّر فى نفسه وهيَّأ ما يقوله من الطعن فى القرآن، فاستحق بذلك الهلاك، كيف هيَّأ هذا الطعن؟ ثم استحق الهلاك لأنه أعد هذا الطعن.',NULL,'866',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5249,'<span data-type="title" id=toc-5323>٢١ </span>-، ٢٢، ٢٣، ٢٤ - ثم نظر فى وجوه الناس، ثم قطَّب وجهه وزاد فى كلوحه، ثم أعرض عن الحق وتعاظم أن يعترف به، فقال: ما هذا إلا سحر ينقل عن الأولين.',NULL,'866',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5250,'<span data-type="title" id=toc-5324>٢٥ </span>- ما هذا إلا قول الخلق تعلمه - محمد - وادَّعى أنه من عند الله.',NULL,'866',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5251,'<span data-type="title" id=toc-5325>٢٦ </span>-، ٢٧، ٢٨، ٢٩، ٣٠ - سأُدخله جهنم ليحترق بها، وما أدراك ما جهنم؟ ، لا تبقى لحماً ولا تترك عظماً إلا أحرقته. مُسوِّدة لأعالى الجلد، عليها تسعة عشر يَلُون أمرها وتعذيب أهلها.',NULL,'866',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5252,'<span data-type="title" id=toc-5326>٣١ </span>- وما جعلنا خزنة النار إلا ملائكة، وما جعلنا عدتهم تسعة عشر إلا اختباراً للذين كفروا، ليحصل اليقين للذين أوتوا الكتاب بأن ما يقوله القرآن عن خزنة جهنم إنما هو حق من الله تعالى - حيث وافق ذلك كتبهم - ويزداد الذين آمنوا بمحمد إيماناً، ولا يشك فى ذلك الذين أعطوا الكتاب والمؤمنون، وليقول الذين فى قلوبهم نفاق والكافرون: ما الذى أراده الله بهذا العدد المستغرب استغراب المثل؟ . . بمثل ذلك المذكور من الإضلال والهدى يضل الله الكافرين ويهدى المؤمنين، وما يعلم جنود ربك لفرط كثرتهم إلا هو - سبحانه وتعالى - وما سقر إلا تذكرة للبشر وتخويف لهم.',NULL,'866',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5253,'<span data-type="title" id=toc-5327>٣٢ </span>-، ٣٣، ٣٤، ٣٥، ٣٦ - ردعاً لمن ينذر بها ولم يخف، أقسم بالقمر، وبالليل إذا ذهب، وبالصبح إذا أضاء وانكشف إن سقر لأعظم الدواهى الكبرى إنذاراً وتخويفاً.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5254,'<span data-type="title" id=toc-5328>٣٧ </span>- إنذار للبشر لمَن شاء منكم أن يتقدم إلى الخير أو يتأخر عنه.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5255,'<span data-type="title" id=toc-5329>٣٨ </span>-، ٣٩ - كل نفس بما عملت مأخوذة إلا المسلمين الذين فكُّوا رقابهم بالطاعة.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5256,'<span data-type="title" id=toc-5330>٤٠ </span>-، ٤١، ٤٢ - هم فى جنات لا يُدرك وصفها، يسأل بعضهم بعضاً عن المجرمين، وقد سألوهم عن حالهم، ما أدخلكم فى سقر؟ .',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5257,'<span data-type="title" id=toc-5331>٤٣ </span>-، ٤٤، ٤٥، ٤٦، ٤٧ - قالوا لم نك من المصلين كما كان يصلى المسلمون، ولم نك نطعم المسكين كما كان يطعم المسلمون، وكنا نندفع وننغمس فى الباطل والزور مع الخائضين فيه، وكنا نكذِّب بيوم الحساب والجزاء حتى أتانا الموت.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5258,'<span data-type="title" id=toc-5332>٤٨ </span>- فما تغيثهم شفاعة الشافعين من الملائكة والنبيين والصالحين.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5259,'<span data-type="title" id=toc-5333>٤٩ </span>- فما لهم عن العظة بالقرآن منصرفين.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5260,'<span data-type="title" id=toc-5334>٥٠ </span>-، ٥١ - كأنهم حمر شديدة النفار فرَّت من مطارديها.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5261,'<span data-type="title" id=toc-5335>٥٢ </span>- بل يريد كل امرئ منهم أن يُؤتى صُحفاً من السماء واضحة مكشوفة تثبت صدْق الرسول - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ -.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5262,'<span data-type="title" id=toc-5336>٥٣ </span>- ردْعاً لهم عما أرادوا. بل هم لا يخافون الآخرة، فأعرضوا عن التذكرة، وتفننوا فى طلب الآيات.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5263,'<span data-type="title" id=toc-5337>٥٤ </span>-، ٥٥ - حقاً إن القرآن تذكرة بليغة كافية، فمن شاء أن يذكره ولا ينساه فعل.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5264,'<span data-type="title" id=toc-5338>٥٦ </span>- وما يذكرون إلا بمشيئة الله، هو أهل لأن يُتقى وأهل لأن يَغْفر لمَن اتقاه.',NULL,'867',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5265,'<span data-type="title" id=toc-5340>١ </span>-، ٢، ٣ - أقسم وأؤكد القسم بيوم<span data-type="title" id=toc-5339> القيامة </span>- وهو الحق الثابت - وأقسم وأؤكد القسم بالنفس التى تلوم صاحبها على الذنب والتقصير، لتبعثن بعد جمع ما تفرق من عظامكم، أيحسب الإنسان - بعد أن خلقناه من عدم - أن لن نجمع ما بلى وتفرق من عظامه؟ .',NULL,'868',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5266,'<span data-type="title" id=toc-5341>٤ </span>- بلى نجمعها قادرين على أن نُسوى ما دق من أطراف أصابعه، فكيف بما كبر من عظام جسمه؟ .',NULL,'868',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5267,'<span data-type="title" id=toc-5342>٥ </span>- بل أينكر الإنسان البعث، يريد أن يبقى على الفجور فيما يستقبل من أيام عمره كلها؟ {.',NULL,'868',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5268,'<span data-type="title" id=toc-5343>٦ </span>- يسأل مستبعداً قيام الساعة: متى يكون يوم القيامة؟} .',NULL,'868',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5269,'<span data-type="title" id=toc-5344>٧ </span>-، ٨، ٩، ١٠ - فإذا تحيَّر البصر فزعاً ودهشاً، وذهب ضوء القمر، وقرن بين الشمس والقمر فى الطلوع من المغرب، يقول الإنسان يومئذٍ: أين الفرار من العذاب؟! .',NULL,'869',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5270,'<span data-type="title" id=toc-5345>١١ </span>-، ١٢ - ردعاً لك - أيها الإنسان - عن طلب المفر، لا ملجأ لك إلا إلى ربك - وحده - مستقر العباد من جنة أو نار.',NULL,'869',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5271,'<span data-type="title" id=toc-5346>١٣ </span>- يُخْبَرُ الإنسان يومئذ بما قدمه من عمل وما أخره.',NULL,'869',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5272,'<span data-type="title" id=toc-5347>١٤ </span>-، ١٥ - بل الإنسان على نفسه حجة واضحة تلزمه بما فعل أو ترك، ولو طرح معاذيره وبسطها لا يمكنه أن يتخلص منها.',NULL,'869',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5273,'<span data-type="title" id=toc-5348>١٦ </span>-، ١٧ - لا تُحَرِّكْ بالقرآن لسانك حين الوحى لتعجل بقراءته وحفظه، إن علينا جمعه فى صدرك، وإثبات قراءته فى لسانك.',NULL,'869',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5274,'<span data-type="title" id=toc-5349>١٨ </span>-، ١٩ - فإذا قرأه عليك رسولنا فاتبع قراءته منصتاً لها، ثم إن علينا بعد ذلك بيانه لك إذا أشكل عليك شئ منه.',NULL,'869',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5275,'<span data-type="title" id=toc-5350>٢٠ </span>-، ٢١ - ردعاً لكم عن إنكار البعث وهو حق، بل أنتم تحبون الدنيا ومتاعها، وتتركون الآخرة ونعيمها.',NULL,'869',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5276,'<span data-type="title" id=toc-5351>٢٢ </span>-، ٢٣ - وجوه يومئذٍ حسنة ناعمة إلى ربها ناظرة بدون تحديد بصفة أو جهة أو مسافة.',NULL,'869',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5277,'<span data-type="title" id=toc-5352>٢٤ </span>-، ٢٥ - ووجوه يومئذٍ كالحة شديدة العبوس، تتوقع أن يفعل بها ما هو فى شدته داهية تقصم فقرات الظهر.',NULL,'869',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5278,'<span data-type="title" id=toc-5353>٢٦ </span>-، ٢٧، ٢٨، ٢٩، ٣٠ - ردعاً لكم عن حب الدنيا التى تفارقونها إذا بلغت الروح عظام النحر، وقال الحاضرون بعضهم لبعض: هل من راق يرقيه مما به؟ وظن المحتضر أن الذى نزل به هو فراق الدنيا المحبوبة، وبلغت به الشدة أقصاها، والتوت إحدى الساقين على الأخرى عند نزع الروح، إلى ربك يومئذٍ مساق العباد، إما إلى الجنة وإما إلى النار.',NULL,'870',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5279,'<span data-type="title" id=toc-5354>٣١ </span>-، ٣٢، ٣٣ - أنكر الإنسان البعث فلا صدَّق بالرسول والقرآن، ولا أدَّى لله فرائض الصلوات، ولكن كذَّب القرآن، فأعرض عن الإيمان، ثم ذهب إلى أهله يمد ظهره متبختراً.',NULL,'870',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5280,'<span data-type="title" id=toc-5355>٣٤ </span>-، ٣٥ - هلاك لك - أيها المكذب - فهلاك، ثم هلاك دائم لك، فهلاك.',NULL,'870',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5281,'<span data-type="title" id=toc-5356>٣٦ </span>- أيحسب هذا الإنسان المنكر للبعث أن يُترك مهملاً يرتع فى حياته، ثم يموت ولا يبعث فيحاسب على عمله؟! {.',NULL,'870',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5282,'<span data-type="title" id=toc-5357>٣٧ </span>-، ٣٨ - ألم يك الإنسان نطفة من مَنِىٍّ يقدر تكوينه فى الرحم، ثم صار قطعة دم جامد، فخلقه الله، فسواه فى أحسن تقويم؟} .',NULL,'870',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5283,'<span data-type="title" id=toc-5358>٣٩ </span>- فجعل منه الصنفين الذكر والأنثى.',NULL,'870',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5284,'<span data-type="title" id=toc-5359>٤٠ </span>- أليس ذلك المبدع الفعَّال لهذه الأشياء بقادر على أن يُحْيى الموتى بعد جمع عظامهم؟ .',NULL,'870',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5285,'<span data-type="title" id=toc-5361>١ </span>- قد مضى على<span data-type="title" id=toc-5360> الإنسان </span>حين من الزمان قبل أن ينفخ فيه الروح، لم يكن شيئاً يذكر باسمه، ولا يعرف ما يراد منه.',NULL,'871',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5286,'<span data-type="title" id=toc-5362>٢ </span>- إنا خلقنا الإنسان من نطفة ذات عناصر شتى، مختبرين له بالتكاليف فيما بعد، فجعلناه ذا سمع وذا بصر، ليسمع الآيات ويرى الدلائل.',NULL,'871',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5287,'<span data-type="title" id=toc-5363>٣ </span>- إنا بينا له طريق الهدى: إما مؤمنا وإما كافراً.',NULL,'871',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5288,'<span data-type="title" id=toc-5364>٤ </span>- إنا أعددنا للكافرين سلاسل لأرجلهم، وأغلالا لأيديهم وأعناقهم، ونارا موقدة.',NULL,'871',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5289,'<span data-type="title" id=toc-5365>٥ </span>-، ٦ - إن الصادقين فى إيمانهم يشربون من خمر كان ما تمزج به ماء كافور، عيناً يشرب منها عباد الله يجرونها حيث شاءوا إجراء سهلاً.',NULL,'871',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5290,'<span data-type="title" id=toc-5366>٧ </span>- يوفون بما أوجبوا على أنفسهم، ويخافون يوماً عظيماً كان ضرره البالغ فاشياً منتشراً كل الانتشار.',NULL,'871',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5291,'<span data-type="title" id=toc-5367>٨ </span>- ويُطعمون الطعام - مع حبهم له، وحاجاتهم إليه - فقيراً عاجزاً عن الكسب، وصغيراً فقد أباه، ومأسورا لا يملك شيئاً.',NULL,'872',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5292,'<span data-type="title" id=toc-5368>٩ </span>- ويقولون فى أنفسهم: إنما نطعمكم لطلب ثواب الله، لا نريد منكم عوضاً أو هدية، ولا نريد منكم ثناء.',NULL,'872',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5293,'<span data-type="title" id=toc-5369>١٠ </span>- إنا نخاف من ربنا يوماً اشتد عبوس مَن فيه، وقطَّبوا وجوههم وجباههم.',NULL,'872',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5294,'<span data-type="title" id=toc-5370>١١ </span>-، ١٢ - فصانهم الله من شدائد ذلك اليوم، وأعطاهم بدل عبوس الفجَّار حسناً فى وجوههم، وبهجة وفرحاً فى قلوبهم، وجزاهم بصبرهم جنة ملكها هنئ، وملبسها حرير ناعم الملمس.',NULL,'872',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5295,'<span data-type="title" id=toc-5371>١٣ </span>- متكئين فى الجنة على السرر، لا يجدون فيها حر شمس، ولا شدة برد.',NULL,'872',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5296,'<span data-type="title" id=toc-5372>١٤ </span>- وجنة وارفة عليهم ظلال أشجارها، وسَهَّلَ لهم أخذ ثمارها تسهيلاً.',NULL,'872',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5297,'<span data-type="title" id=toc-5373>١٥ </span>-، ١٦ - ويطوف عليهم خدمهم بأوعية شراب من فضة، وبأكواب كوِّنت قوارير شفافة، قوارير مصنوعة من فضة، قدَّرها الساقون تقديرا على وفاق ما يشتهى الشاربون.',NULL,'872',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5298,'<span data-type="title" id=toc-5374>١٧ </span>-، ١٨ - ويُسقى الأبرار فى الجنة خمرا كان ما تمزج به ما يشبه الزنجبيل فى الطعم، عينا فى الجنة تسمى - لسلامة شرابها وسهولة مساغه وطيبه - سلسبيلاً.',NULL,'872',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5299,'<span data-type="title" id=toc-5375>١٩ </span>- ويطوف عليهم - للبهجة والسرور - غلمان دائمون على حالهم، إذا أبصرتهم عند طوافهم بخفة ونشاط - لحسنهم وصفاء ألوانهم - لؤلؤا منثورا حولك مضيئا.',NULL,'872',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5300,'<span data-type="title" id=toc-5376>٢٠ </span>- وإذا أبصرت أى مكان فى الجنة رأيت فيه نعيما عظيما، وملكا واسعا.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5301,'<span data-type="title" id=toc-5377>٢١ </span>- يعلوهم ثياب من حرير رقيق خضر، وثياب من حرير غليظ، وجُعِلَتْ حُليهم التى فى أيديهم أساور من فضة، وسقاهم ربهم شرابا آخر طهورا لا رجس فيه ولا دنس.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5302,'<span data-type="title" id=toc-5378>٢٢ </span>- إن هذه النعيم أُعِدَّ لكم جزاء لأعمالكم، وكان سعيكم فى الدنيا محموداً عند الله مرضيا ومقبولاً.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5303,'<span data-type="title" id=toc-5379>٢٣ </span>- إنا - برحمتنا وحكمتنا - نزَّلنا عليك القرآن على وجه يسكن به فؤادك، ويدوم به حفظك، فلا تنساه أبداً.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5304,'<span data-type="title" id=toc-5380>٢٤ </span>- فاصبر لحكم ربك بتأخير نصرتك على أعدائك، وابتلائك بأذاهم، ولا تُطع من المشركين من هو ذا إثم، أو مستغرقاً فى الكفر.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5305,'<span data-type="title" id=toc-5381>٢٥ </span>-، ٢٦ - ودم على ذكر ربك، فصل الفجر بكرة، والظهر والعصر أصيلاً، ومن الليل فصل له المغرب والعشاء، وتهجد زمناً طويلاً من الليل.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5306,'<span data-type="title" id=toc-5382>٢٧ </span>- إن هؤلاء الكفرة يُحبون الدنيا ويؤثرونها على الآخرة، ويتركون خلف ظهورهم يوماً ثقيلاً كَرْبه شديدا هوله، فلم يعملوا ما ينجيهم من ذلك.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5307,'<span data-type="title" id=toc-5383>٢٨ </span>- نحن خلقناهم وأحكمنا خلقهم، وإذا شئنا أهلكناهم وَبَدَّلْنا أمثالهم ممن يطيع الله تبديلاً.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5308,'<span data-type="title" id=toc-5384>٢٩ </span>- إن هذه السورة عظة للعالمين، فمن شاء اتخذ بالإيمان والتقوى إلى ربه طريقاً يوصله إلى مغفرته وجنته.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5309,'<span data-type="title" id=toc-5385>٣٠ </span>- وما تشاءون شيئاً من الأشياء إلا إذا شاءه الله إن الله كان عليما بأحوالكم حكيما فيما يشاء ويختار.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5310,'<span data-type="title" id=toc-5386>٣١ </span>- يُدخل من يشاء فى جنته - فدخولها بفضله ورحمته - وأعد للظالمين عذاباً أليما.',NULL,'873',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5311,'<span data-type="title" id=toc-5388>١ </span>-، ٢، ٣، ٤، ٥، ٦، ٧ - أقسم بالآيات المرسلة على لسان جبريل إلى محمد للعرف والخير، فالآيات القاهرات لسائر الأديان الباطلة تنسفها نسفاً، وبالآيات الناشرات للحكمة والهداية فى قلوب العالمين نشراً عظيماً، فالفارقات بين الحق والباطل فرقاً واضحاً، فالملقيات على الناس تذكرة تنفعهم - إعذاراً لهم وإنذاراً - فلا تكون لهم حُجة: إن الذى توعدونه من مجئ يوم القيامة لنازل لا ريب فيه.',NULL,'874',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5312,'<span data-type="title" id=toc-5389>٨ </span>-، ٩، ١٠، ١١ - فإذا النجوم مُحقت ذواتها، وإذا السماء شُقَّت، وإذا الجبال فُتِّتت ونسفتها الرياح نسفاً، وإذا الرسل عُيِّن لهم الوقت الذى يحضرون فيه للشهادة على الأمم.',NULL,'874',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5313,'<span data-type="title" id=toc-5390>١٢ </span>-، ١٣، ١٤، ١٥ - لأى يوم أُخِّرت هذه الأمور العظيمة؟ ليوم يكون فيه الفصل بين الخلائق، وما أعلمك ما شأن يوم الفصل؟ هلاك دائم يومئذٍ للمكذبين بما أوعدهم به الرسل.',NULL,'874',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5314,'<span data-type="title" id=toc-5391>١٦ </span>-، ١٧، ١٨ - ألم نُهلك الأولين من الأمم المكذبة، ثم نُتبع الأولين الآخرين فى الهلاك مثل ذلك الفعل نفعل بكل من أجرم وكفر بالله.',NULL,'874',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5315,'<span data-type="title" id=toc-5392>١٩ </span>- هلاك يومئذٍ للمكذبين بما أوعدناهم.',NULL,'875',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5316,'<span data-type="title" id=toc-5393>٢٠ </span>-، ٢١، ٢٢، ٢٣، ٢٤ - ألم نخلقكم من ماء حقير وهو النطفة، فجعلنا هذا الماء فى مقر يتمكن فيه، فيتم خلقه وتصويره مؤخراً إلى وقت قد علمه الله، فقدرنا على خلقه وتصويره وإخراجه، فنعم المقدرون الخالقون له نحن؟ ويل يومئذٍ للمكذبين بنعمة الخلق والتقدير.',NULL,'875',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5317,'<span data-type="title" id=toc-5394>٢٥ </span>-، ٢٦، ٢٧، ٢٨ - ألم نجعل الأرض ضامَّة على ظهرها أحياء لا يعدون، وفى بطنها أمواتٌ لا يحصرون، وجعلنا فيها جبالاً ثوابت عاليات، وأسقيناكم ماء عذباً سائغاً؟ هلاك يومئذٍ للمكذبين بهذه النعمة.',NULL,'875',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5318,'<span data-type="title" id=toc-5395>٢٩ </span>-، ٣٠، ٣١ - يُقال للكافرين يوم الفصل: سيروا إلى النار التى كنتم بها تكذِّبون. سيروا إلى حرارة دخان من جهنم يتشعب لعِظمه ثلاث شعب. لا مظل من حر ذلك اليوم، ولا يغنى ذلك الظل من حر اللهب شيئاً.',NULL,'875',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5319,'<span data-type="title" id=toc-5396>٣٢ </span>-، ٣٣، ٣٤ - إن النار ترمى بما تطاير منها كالقَصْرِ فى العِظم. كأن الشرر جِمَالٌ سودٌ تضرب إلى الصفرة. هلاك يومئذ للمكذبين بأن هذه صفتها.',NULL,'875',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5320,'<span data-type="title" id=toc-5397>٣٥ </span>-، ٣٦، ٣٧ - هذا الذى قص عليكم إنه واقع يوم لا ينطقون بشئ ينفعهم، ولا يكون لهم إذْن فى النطق، ولا يصدر منهم اعتذار لأنه لا عذر لهم. هلاك يومئذٍ للمكذبين بهذا اليوم.',NULL,'875',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5321,'<span data-type="title" id=toc-5398>٣٨ </span>-، ٣٩، ٤٠ - هذا يوم الفصل بين المحق والمبطل بجزاء كل بما يستحقه، جعلناكم - يا مكذبى محمد - والأولين المكذبين مثلكم، فإن كان لكم حيلة فى دفع هذا العذاب عنكم فاحتالوا، فاحضروا وتخلصوا من عذابى. هلاك يومئذٍ للمكذبين بوعيد الله.',NULL,'875',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5322,'<span data-type="title" id=toc-5399>٤١ </span>-، ٤٢، ٤٣، ٤٤، ٤٥ - إن المتقين من عذاب الله فى ظلال وارفة، وعيون جارية، وفواكه مما يستطيبون. مقولاً لهم تحية وتكريماً: كلوا واشربوا أكلاً وشرباً هنيئاً بما كنتم تعملون فى الدنيا من الصالحات. إنا نجزى المحسنين بهذا الجزاء العظيم. هلاك يومئذٍ للمكذبين بالجنة.',NULL,'875',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5323,'<span data-type="title" id=toc-5400>٤٦ </span>-، ٤٧ - ويُقال للكافرين: كلوا وتمتعوا متاعاً ليس له بقاء. إنكم مجرمون بإشراككم بالله. هلاك يومئذٍ للمكذبين بالنعم.',NULL,'876',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5324,'<span data-type="title" id=toc-5401>٤٨ </span>-، ٤٩ - وإذا قيل لهم: صلوا لله، واخشعوا إليه. لا يخشعون ولا يصلُّون. بل يُصرُّون على استكبارهم. هلاك يومئذٍ للمكذبين بأوامر الله ونواهيه.',NULL,'876',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5325,'<span data-type="title" id=toc-5402>٥٠ </span>- فبأى حديث بعد القرآن يؤمنون إن لم يؤمنوا بالقرآن. مع أنه معجزة من السماء؟ .',NULL,'876',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5326,'<span data-type="title" id=toc-5404>١ </span>- عن أى شئ يسأل هؤلاء الجاحدون بعضهم بعضا؟ {',NULL,'877',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5327,'<span data-type="title" id=toc-5405>٢ </span>-، ٣ - عن الخبر العظيم، خبر البعث الذى هم موغلون فى الاختلاف فيه بين منكر له وشاك فيه.',NULL,'877',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5328,'<span data-type="title" id=toc-5406>٤ </span>- زجراً لهم عن هذا التساؤل، سيعلمون حقيقة الحال حين يرون البعث أمراً واقعاً.',NULL,'877',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5329,'<span data-type="title" id=toc-5407>٥ </span>- ثم زجراً لهم، سيعلمون ذلك عندما يحل بهم النكال.',NULL,'877',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5330,'<span data-type="title" id=toc-5408>٦ </span>- ألم يروا من آيات قدرتنا أنا جعلنا الأرض ممهدة للاستقرار عليها والتقلب فى أنحائها؟}',NULL,'877',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5331,'<span data-type="title" id=toc-5409>٧ </span>- وجعلنا الجبال أوتاداً للأرض تُثَبِّتها.',NULL,'877',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5332,'<span data-type="title" id=toc-5410>٨ </span>- وخلقناكم مزدوجين ذكوراً وإناثاً.',NULL,'877',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5333,'<span data-type="title" id=toc-5411>٩ </span>- وجعلنا نومكم راحة لكم من عناء العمل.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5334,'<span data-type="title" id=toc-5412>١٠ </span>- وجعلنا الليل ساتراً لكم بما يُغطِّيكم من ظلمته.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5335,'<span data-type="title" id=toc-5413>١١ </span>- وجعلنا النهار وقت سعى لكم لتحصيل ما به تعيشون.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5336,'<span data-type="title" id=toc-5414>١٢ </span>- وأقمنا فوقكم سبع سموات قويات محكمات.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5337,'<span data-type="title" id=toc-5415>١٣ </span>- وأنشأنا شمساً مضيئة متوقدة.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5338,'<span data-type="title" id=toc-5416>١٤ </span>- وأنزلنا من السحب الممطرة ماء قوى الانصباب.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5339,'<span data-type="title" id=toc-5417>١٥ </span>- لنخرج بهذا لماء حبّاً ونباتاً غذاء للناس والحيوان.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5340,'<span data-type="title" id=toc-5418>١٦ </span>- وبساتين ذات أشجار ملتفة متشابكة الأغصان.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5341,'<span data-type="title" id=toc-5419>١٧ </span>- إن يوم الفصل بين الخلائق كان ميعاداً مقدراً للبعث.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5342,'<span data-type="title" id=toc-5420>١٨ </span>- يوم ينفخ فى الصور للبعث فتأتون إلى المحشر جماعات جماعات.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5343,'<span data-type="title" id=toc-5421>١٩ </span>- وشققت السماء من كل جانب فصارت أبواباً.',NULL,'878',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5344,'<span data-type="title" id=toc-5422>٢٠ </span>- وسُيِّرت الجبال بعد قلعها من مقارها وتفتتها، فصارت تريك صورة الجبال وهى غبار متكاثف كالسراب يريك صورة الماء وليس بماء.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5345,'<span data-type="title" id=toc-5423>٢١ </span>- إن جهنم كانت موضع رصد يترقب منه الخزنة أهلها.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5346,'<span data-type="title" id=toc-5424>٢٢ </span>- للطغاة المتجبرين مصيراً سيئاً يجازون فيه ما انتهكوا من حدود الله وحرمات العباد.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5347,'<span data-type="title" id=toc-5425>٢٣ </span>- ماكثين فيها دهوراً متتابعة.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5348,'<span data-type="title" id=toc-5426>٢٤ </span>- لا يذوقون فيها نسيماً ينفس عنهم حرها، ولا شراباً يسكن عطشهم فيها.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5349,'<span data-type="title" id=toc-5427>٢٥ </span>- لكن يذوقون ماء بالغاً الغاية فى الحرارة، وصديداً يسيل من جلود أهلها.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5350,'<span data-type="title" id=toc-5428>٢٦ </span>- جزاء موافقاً لأعمالهم السيئة.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5351,'<span data-type="title" id=toc-5429>٢٧ </span>- إنهم كانوا لا يتوقعون الحساب، فيعملوا للنجاة منه.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5352,'<span data-type="title" id=toc-5430>٢٨ </span>- وكذَّبوا بآيات الله الدالة على البعث تكذيباً شديداً.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5353,'<span data-type="title" id=toc-5431>٢٩ </span>- وكل شئ ضبطناه كتابة.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5354,'<span data-type="title" id=toc-5432>٣٠ </span>- فذوقوا، فلن يكون لكم إلا المزيد من العذاب.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5355,'<span data-type="title" id=toc-5433>٣١ </span>- إن للذين يتقون ربهم نجاة من العذاب وظفراً بالجنة.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5356,'<span data-type="title" id=toc-5434>٣٢ </span>- حدائق مثمرة وأعنابا طيبة.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5357,'<span data-type="title" id=toc-5435>٣٣ </span>- وعذارى نواهد مُتَماثلات فى السن.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5358,'<span data-type="title" id=toc-5436>٣٤ </span>- وكأساً ممتلئة صافية.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5359,'<span data-type="title" id=toc-5437>٣٥ </span>- لا يسمعون فى الجنة لغواً من القول ولا كذاباً.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5360,'<span data-type="title" id=toc-5438>٣٦ </span>- جزاء عظيماً من ربك، تفضلاً منه وإحساناً كافياً.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5361,'<span data-type="title" id=toc-5439>٣٧ </span>- رب السموات والأرض وما بينهما. الذى وسعت رحمته كل شئ لا يملك أحد حق من مخاطبته.',NULL,'879',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5362,'<span data-type="title" id=toc-5440>٣٨ </span>- يوم يقوم جبريل والملائكة مصطفِّين خاشعين. لا يتكلم أحد منهم إلا من أذِن له الرحمن بالكلام، ونطق بالصواب.',NULL,'880',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5363,'<span data-type="title" id=toc-5441>٣٩ </span>- ذلك اليوم الذى لا شك فيه، فمن شاء اتخذ إلى ربه مرجعاً كريماً والعمل الصالح.',NULL,'880',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5364,'<span data-type="title" id=toc-5442>٤٠ </span>- إنا حذَّرناكم عذاباً قريباً وقوعه. يوم ينظر المرء ما قدَّمت يداه من عمل، ويقول الكافر متمنياً الخلاص: يا ليتنى بقيت تراباً بعد الموت، فلم أبعث ولم أحاسب.',NULL,'880',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5365,'<span data-type="title" id=toc-5444>١ </span>- أقسم بكل ما أودعت فيه قوة نزع الأشياء من مقارها بشدة.',NULL,'881',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5366,'<span data-type="title" id=toc-5445>٢ </span>- وبكل ما أودعت فيه قوة إخراج الأشياء فى خفة ولين.',NULL,'881',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5367,'<span data-type="title" id=toc-5446>٣ </span>- وبكل ما أودعت فيه السرعة فى تأدية وظائفه بسهولة ويسر.',NULL,'881',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5368,'<span data-type="title" id=toc-5447>٤ </span>- فالسابقات التى تسبق فى أداء ما وكِّل إليها سبقاً عظيماً.',NULL,'881',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5369,'<span data-type="title" id=toc-5448>٥ </span>-، ٦، ٧ - فالمدبرات التى تدبر الأمور وتصرفها بما أودع فيها من خصائص: لتقومن الساعة يوم تزلزل النفخة الأولى جميع الكائنات، تتبعها النفخة الثانية التى يكون معها البعث.',NULL,'881',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5370,'<span data-type="title" id=toc-5449>٨ </span>- قلوب فى ذلك اليوم فزعة شديدة الاضطراب من الخوف.',NULL,'881',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5371,'<span data-type="title" id=toc-5450>٩ </span>- أبصار أصحابها حزينة ذليلة منكسرة.',NULL,'881',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5372,'<span data-type="title" id=toc-5451>١٠ </span>- يقول هؤلاء المنكرون للبعث -: أنرد بعد الموت أحياء إلى الخلقة الأولى كما كنا؟!',NULL,'881',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5373,'<span data-type="title" id=toc-5452>١١ </span>- أئذا صرنا عظاماً بالية نرد ونبعث من جديد؟ .',NULL,'881',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5374,'<span data-type="title" id=toc-5453>١٢ </span>- قالوا - منكرين مستهزئين -: تلك الرجعة إن وقعت: رجعة خاسرة، ولسنا أهل خسران.',NULL,'881',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5375,'<span data-type="title" id=toc-5454>١٣ </span>-، ١٤ - لا تحسبوا الرجعة عسيرة، فإنما هى صحيحة واحدة، فإذا الموتى حضور بأرض المحشر.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5376,'<span data-type="title" id=toc-5455>١٥ </span>-، ١٦ - هل أتاك - يا محمد - حديث موسى حين ناداه ربُه بالوادى المطهر المسمى «طوى» .',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5377,'<span data-type="title" id=toc-5456>١٧ </span>- اذهب إلى فرعون الذى جاوز الحد فى الظلم.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5378,'<span data-type="title" id=toc-5457>١٨ </span>- فقل: هل لك فى أن تتطهر؟',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5379,'<span data-type="title" id=toc-5458>١٩ </span>- وأرشدك إلى معرفة ربك، فتخشاه.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5380,'<span data-type="title" id=toc-5459>٢٠ </span>- فأرى موسى فرعونَ المعجزة الكبرى.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5381,'<span data-type="title" id=toc-5460>٢١ </span>- فكذب فرعونُ موسى فيما جاء به، وعصاه فيما دعاه إليه.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5382,'<span data-type="title" id=toc-5461>٢٢ </span>- ثم تولى عنه يجتهد فى معارضته.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5383,'<span data-type="title" id=toc-5462>٢٣ </span>-، ٢٤ - فجمع السحرة، ودعا الناس فقال: أنا ربكم الأعلى.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5384,'<span data-type="title" id=toc-5463>٢٥ </span>- فعذَّبه الله عذاب المقالة الآخرة: وهى أنا ربكم الأعلى، وعذاب المقالة الأولى، وهى تكذيبه لموسى عليه السلام.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5385,'<span data-type="title" id=toc-5464>٢٦ </span>- إن فى ذلك الحديث لعظة لمَن يخاف الله.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5386,'<span data-type="title" id=toc-5465>٢٧ </span>-، ٢٨ - أخَلْقُكُم - أيها المنكرون - للبعث أشقُ أم خلق السماء؟ ضم أجزاءها المتفرقة بعضها إلى بعض. رفع جرمها فوقكم، فجعلها مستوية لا تفاوت فيها ولا خلل.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5387,'<span data-type="title" id=toc-5466>٢٩ </span>- وأظلم ليلها وأظهر نهارها.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5388,'<span data-type="title" id=toc-5467>٣٠ </span>- والأرض بعد ذلك بسَطَها ومهدها لسكنى أهلها.',NULL,'882',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5389,'<span data-type="title" id=toc-5468>٣١ </span>- أخرج منها ماءها بتفجير عيونها، وإجراء أنهارها ونباتها ليقتات به الناس والدواب.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5390,'<span data-type="title" id=toc-5469>٣٢ </span>- والجبال ثبَّتها.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5391,'<span data-type="title" id=toc-5470>٣٣ </span>- متاعاً لكم ولأنعامكم.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5392,'<span data-type="title" id=toc-5471>٣٤ </span>- فإذا جاءت القيامة التى تعم أهوالها.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5393,'<span data-type="title" id=toc-5472>٣٥ </span>- يوم يتذكر الإنسان ما عمله من خير أو شر.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5394,'<span data-type="title" id=toc-5473>٣٦ </span>- وأظهرت الجحيم إظهاراً بينا، ماثلة أمامكم.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5395,'<span data-type="title" id=toc-5474>٣٧ </span>-، ٣٨، ٣٩ - فأما مَن تجاوز الحد بعصيانه، واختار لنفسه الحياة الفانية، فإن النار المتأججة هى مأواه.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5396,'<span data-type="title" id=toc-5475>٤٠ </span>-، ٤١ - وأما مَن خاف عظمة ربه وجلاله، وكف نفسه عن الشهوات، فإن دار النعيم هى المنزل لا غيرها.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5397,'<span data-type="title" id=toc-5476>٤٢ </span>- يسألونك - يا محمد - عن الساعة متى وقوعها؟ .',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5398,'<span data-type="title" id=toc-5477>٤٣ </span>- ليس عِلْمها إليك حتى تذكرها لهم.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5399,'<span data-type="title" id=toc-5478>٤٤ </span>- إلى ربك منتهى علمها لا إلى غيره.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5400,'<span data-type="title" id=toc-5479>٤٥ </span>- إنما واجبك إنذار مَن يخاف لا الإعلام بوقتها.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5401,'<span data-type="title" id=toc-5480>٤٦ </span>- كأنهم يوم يشاهدونها لم يلبثوا فى الدنيا إلا مقدار عشية أو ضحاها.',NULL,'883',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5402,'<span data-type="title" id=toc-5482>١ </span>- تغيَّر وجه النبى صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ ضائقا معرضا.',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5403,'<span data-type="title" id=toc-5483>٢ </span>- لأِنْ جاءه الأعمى يسأل عن أمر دينه.',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5404,'<span data-type="title" id=toc-5484>٣ </span>- ومَا يُدْريك لعل هذا الأعمى يتطهر بما يتلقاه عنك.',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5405,'<span data-type="title" id=toc-5485>٤ </span>- أو يتعظ فتنفعه العظة.',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5406,'<span data-type="title" id=toc-5486>٥ </span>-، ٦ - أما مَن استغنى بثروته وقوته فأنت تُقْبل عليه، وتهتم بتبليغه دعوتك.',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5407,'<span data-type="title" id=toc-5487>٧ </span>- وأى شئ عليك إذا لم يتطهر بالإيمان؟!',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5408,'<span data-type="title" id=toc-5488>٨ </span>-، ٩، ١٠ - وأما مَن جاءك يسرع لطلب العلم والهداية، وهو يخاف الله فأنت عنه تتشاغل.',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5409,'<span data-type="title" id=toc-5489>١١ </span>- حقاً إن هذه الآيات عظة.',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5410,'<span data-type="title" id=toc-5490>١٢ </span>- فمن شاء اتعظ بالقرآن.',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5411,'<span data-type="title" id=toc-5491>١٣ </span>- هو فى صحف مكرمة عند الله.',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5412,'<span data-type="title" id=toc-5492>١٤ </span>- عالية القدر والمكانة. منزهة عن كل نقص.',NULL,'884',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5413,'<span data-type="title" id=toc-5493>١٥ </span>- بأيدى ملائكة جعلهم الله سفراء بينه وبين رسله.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5414,'<span data-type="title" id=toc-5494>١٦ </span>- أخيار محسنين.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5415,'<span data-type="title" id=toc-5495>١٧ </span>- هلاكاً للإنسان. ما أشد كفره! {',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5416,'<span data-type="title" id=toc-5496>١٨ </span>- أمَا يتذكر حقيقة أصله، ومن أى شئ خُلق؟}',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5417,'<span data-type="title" id=toc-5497>١٩ </span>- من ماء مهين. بدأ خلقه فقدَّره أطواراً.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5418,'<span data-type="title" id=toc-5498>٢٠ </span>- ثم يسر له الطريق إلى الإيمان، وأعلمه به.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5419,'<span data-type="title" id=toc-5499>٢١ </span>- ثم أماته، فكرمه بأن يقبر.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5420,'<span data-type="title" id=toc-5500>٢٢ </span>- ثم إذا شاء أحياه بعد الموت.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5421,'<span data-type="title" id=toc-5501>٢٣ </span>- حقاً لمَّا يقض الإنسان - مع امتداد حياته فى الدنيا - ما أمره الله به من الإيمان والطاعة.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5422,'<span data-type="title" id=toc-5502>٢٤ </span>- فليتأمل الإنسان شأن طعامه، كيف دبرناه ويسرناه! .',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5423,'<span data-type="title" id=toc-5503>٢٥ </span>- إنا أنزلنا الغيث من السماء إنزالاً.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5424,'<span data-type="title" id=toc-5504>٢٦ </span>- ثم شققنا الأرض بالنبات شقاً.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5425,'<span data-type="title" id=toc-5505>٢٧ </span>- فأنبتنا فيها حبا يقتات به الناس.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5426,'<span data-type="title" id=toc-5506>٢٨ </span>- وعنباً ونباتاً يؤكل رطباً.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5427,'<span data-type="title" id=toc-5507>٢٩ </span>- وزيتوناً طيباً، ونخلاً مثمراً.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5428,'<span data-type="title" id=toc-5508>٣٠ </span>- وحدائق ملتفة الأغصان.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5429,'<span data-type="title" id=toc-5509>٣١ </span>- وثماراً يتفكه بها، وعشباً تأكله البهائم.',NULL,'885',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5430,'<span data-type="title" id=toc-5510>٣٢ </span>- أنبتنا ذلك متاعاً لكم ولأنعامكم.',NULL,'886',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5431,'<span data-type="title" id=toc-5511>٣٣ </span>- فإذا جاءت صيحة القيامة التى تَصُم الآذان.',NULL,'886',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5432,'<span data-type="title" id=toc-5512>٣٤ </span>-، ٣٥، ٣٦ - يوم يهرب المرء من أخيه، وأمه وأبيه، وزوجته وبنيه.',NULL,'886',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5433,'<span data-type="title" id=toc-5513>٣٧ </span>- لكل امرئ من هؤلاء فى هذا اليوم شأن يشغله.',NULL,'886',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5434,'<span data-type="title" id=toc-5514>٣٨ </span>-، ٣٩ - وجوه فى هذا اليوم مضيئة مشرقة مسرورة بنعيم الله.',NULL,'886',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5435,'<span data-type="title" id=toc-5515>٤٠ </span>- ووجوه فى هذا اليوم عليها غبار وكدورة.',NULL,'886',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5436,'<span data-type="title" id=toc-5516>٤١ </span>- تغشاها ظلمة وسواد.',NULL,'886',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5437,'<span data-type="title" id=toc-5517>٤٢ </span>- أولئك أصحاب هذه الوجوه الكفرة الفجرة.',NULL,'886',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5438,'<span data-type="title" id=toc-5519>١ </span>- إذا الشمس لُفَّتْ ومُحِىَ ضوؤها.',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5439,'<span data-type="title" id=toc-5520>٢ </span>- وإذا النجوم انطمس نورها.',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5440,'<span data-type="title" id=toc-5521>٣ </span>- وإذا الجبال حُرِّكت من أماكنها.',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5441,'<span data-type="title" id=toc-5522>٤ </span>- والذى مَنْ شَأْنُه أن يحمل ويلد فقد خاصته.',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5442,'<span data-type="title" id=toc-5523>٥ </span>- وإذا الوحوش جُمِعت من أوكارها وجحورها ذاهلة من شدة الفزع.',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5443,'<span data-type="title" id=toc-5524>٦ </span>- وإذا البحار تأججت ناراً.',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5444,'<span data-type="title" id=toc-5525>٧ </span>- وإذا الأرواح قرنت بأجسادها.',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5445,'<span data-type="title" id=toc-5526>٨ </span>-، ٩ - وإذا المدفونة حية سئلت - ترضية لها، وسخطاً على من وَأدَها -: بأى جريرة قتلت، ولا ذنب لها؟ .',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5446,'<span data-type="title" id=toc-5527>١٠ </span>- وإذا الصحف التى كتبت فيها أعمال أصحابها بسطت عند الحساب.',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5447,'<span data-type="title" id=toc-5528>١١ </span>- وإذا السماء أزيلت من مكانها.',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5448,'<span data-type="title" id=toc-5529>١٢ </span>- وإذا النار أوقدت إيقاداً شديداً.',NULL,'887',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5449,'<span data-type="title" id=toc-5530>١٣ </span>- وإذا الجنة أُدْنيت وقربت.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5450,'<span data-type="title" id=toc-5531>١٤ </span>- إذا حدثت تلك الظواهر علمت كل نفس ما قدَّمته من خير أو شر.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5451,'<span data-type="title" id=toc-5532>١٥ </span>- فأقسم قسماً مؤكداً بالنجوم التى تنقبض عند طلوعها، فيكون ضوؤها خافتاً.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5452,'<span data-type="title" id=toc-5533>١٦ </span>- الجارية التى تستتر وقت غروبها.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5453,'<span data-type="title" id=toc-5534>١٧ </span>- وبالليل إذا خف ظلامه عند إدباره.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5454,'<span data-type="title" id=toc-5535>١٨ </span>- وبالصبح إذا بدأ ضوؤه وهب نسيمه.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5455,'<span data-type="title" id=toc-5536>١٩ </span>- إن القرآن لقول رسول من الله كريم عليه.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5456,'<span data-type="title" id=toc-5537>٢٠ </span>- صاحب قوة فى أداء مهمته، صاحب مكانة ومنزلة عند الله ذى العرش.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5457,'<span data-type="title" id=toc-5538>٢١ </span>- مطاع أمين على الوحى هناك فى الملأ الأعلى.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5458,'<span data-type="title" id=toc-5539>٢٢ </span>- وما رسولكم - الذى صاحبتموه وعرفتم رجاحة عقله - بمجنون.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5459,'<span data-type="title" id=toc-5540>٢٣ </span>- وأقسم: لقد رأى محمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - جبريل بالأفق المظهر لما يرى فيه.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5460,'<span data-type="title" id=toc-5541>٢٤ </span>- وما محمد على الوحى ببخيل يقصر فى تبليغه وتعليمه.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5461,'<span data-type="title" id=toc-5542>٢٥ </span>- وما الوحى المنزل عليه بقول شيطان مطرود من رحمة الله.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5462,'<span data-type="title" id=toc-5543>٢٦ </span>- فأى طريق أهدى من هذا الطريق تسلكون؟!',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5463,'<span data-type="title" id=toc-5544>٢٧ </span>- ما القرآن إلا تذكير وموعظة للعالمين.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5464,'<span data-type="title" id=toc-5545>٢٨ </span>- لمن أراد منكم الاستقامة وتحرى الحق والصواب.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5465,'<span data-type="title" id=toc-5546>٢٩ </span>- وما تشاءون شيئاً إلا أن يشاء الله رب العالمين ذلك.',NULL,'888',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5466,'<span data-type="title" id=toc-5548>١ </span>- إذا السماء انشقت.',NULL,'889',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5467,'<span data-type="title" id=toc-5549>٢ </span>- وإذا الكواكب تساقطت مُتبعثرة.',NULL,'889',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5468,'<span data-type="title" id=toc-5550>٣ </span>- وإذا البحار فُتِحَ بعضها فى بعض بزوال الحواجز بينها.',NULL,'889',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5469,'<span data-type="title" id=toc-5551>٤ </span>- وإذا القبور بُعثرت، فخرج من فيها من الموتى.',NULL,'889',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5470,'<span data-type="title" id=toc-5552>٥ </span>- علمت كل نفس ما أسلفت من خير أو شر، وما أخرت من ذلك.',NULL,'889',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5471,'<span data-type="title" id=toc-5553>٦ </span>- يا أيها الإنسان: أى شئ خدعك بربك الكريم حتى تجرأت على معصيته؟ .',NULL,'889',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5472,'<span data-type="title" id=toc-5554>٧ </span>- الذى أوجدك من العدم، فخلق لك أعضاء تنتفع بها. وجعلك معتدلاً متناسب الخلق.',NULL,'889',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5473,'<span data-type="title" id=toc-5555>٨ </span>- فى أى صورة من الصور شاءها ربّك وأوجدك عليها.',NULL,'889',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5474,'<span data-type="title" id=toc-5556>٩ </span>- ردعا لكم. بل تكذبون بالجزاء يوم القيامة.',NULL,'889',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5475,'<span data-type="title" id=toc-5557>١٠ </span>-، ١١، ١٢ - وإن عليكم لملائكة حافظين، كراماً لدينا، مسجلين عليكم أعمالكم، يعلمون الذى تفعلونه من خير وشر.',NULL,'890',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5476,'<span data-type="title" id=toc-5558>١٣ </span>- إن الصادقين فى إيمانهم لفى نعيم عظيم.',NULL,'890',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5477,'<span data-type="title" id=toc-5559>١٤ </span>-، ١٥ - وإن الذين انشقوا عن أمر الله لفى نيران محرقة يدخلونها يوم الجزاء.',NULL,'890',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5478,'<span data-type="title" id=toc-5560>١٦ </span>- وإنهم لن يفلتوا من جهنم وسيكونون بعض وقودها.',NULL,'890',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5479,'<span data-type="title" id=toc-5561>١٧ </span>- وأى شئ أعلمك ما يوم الجزاء، وأمره خارج عن درايتك وتصورك؟ .',NULL,'890',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5480,'<span data-type="title" id=toc-5562>١٨ </span>- ثم أى شئ أعلمك ما يوم الجزاء فى الهول والشدة؟ .',NULL,'890',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5481,'<span data-type="title" id=toc-5563>١٩ </span>- يوم لا تملك نفس لنفس شيئا من النفع أو الضرر والأمر يومئذٍ لله - وحده -.',NULL,'890',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5482,'<span data-type="title" id=toc-5565>١ </span>-، ٢، ٣ - هلاك للمطففين. الذين إذا أخذوا لأنفسهم الكيل من الناس يأخذونه وافيا زائدا، وإذا كالوا للناس أو وزنوا لهم ينقصونهم حقهم الواجب لهم اعتداء عليهم.',NULL,'891',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5483,'<span data-type="title" id=toc-5566>٤ </span>-، ٥ - ألا يخطر ببال هؤلاء المطففين أنهم سيبعثون ليوم عظيم الهول؟ .',NULL,'891',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5484,'<span data-type="title" id=toc-5567>٦ </span>- يوم يقوم الناس لأمر رب العالمين وقضائه.',NULL,'891',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5485,'<span data-type="title" id=toc-5568>٧ </span>- ارتدعوا عن التطفيف والغفلة عن البعث. وإن ما كُتب على الفجَّار من عملهم السيئ لفى سجين.',NULL,'891',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5486,'<span data-type="title" id=toc-5569>٨ </span>- وما أعلمك ما سجين؟ .',NULL,'891',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5487,'<span data-type="title" id=toc-5570>٩ </span>- هو كتاب مسطور بيِّن الكتابة.',NULL,'891',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5488,'<span data-type="title" id=toc-5571>١٠ </span>- هلاك للمكذبين يومَ إذْ يكون البعث والجزاء.',NULL,'891',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5489,'<span data-type="title" id=toc-5572>١١ </span>- الذين يُكذبون بيوم الجزاء.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5490,'<span data-type="title" id=toc-5573>١٢ </span>- وما يكذب بيوم الجزاء إلا كل متجاوز الحد مصرٍّ على الذنب.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5491,'<span data-type="title" id=toc-5574>١٣ </span>- إذا تُتلى عليه آيات الله الناطقة بحصول الجزاء قال: أباطيل السابقين.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5492,'<span data-type="title" id=toc-5575>١٤ </span>- ارتدع - أيها المعتدى - عن هذا القول الباطل، بل غطى على قلوب المعتدين ما اكتسبوه من الكفر والمعاصى.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5493,'<span data-type="title" id=toc-5576>١٥ </span>- حقا إن المكذبين لمحجوبون عن رحمة ربهم يومئذٍ بسبب ما اكتسبوه من المعاصى.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5494,'<span data-type="title" id=toc-5577>١٦ </span>- ثم إنهم لداخلون الجحيم.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5495,'<span data-type="title" id=toc-5578>١٧ </span>- ثم يقال - تبكيتا لهم -: هذا العذاب النازل بكم الذى كنتم به تكذبون فى الدنيا.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5496,'<span data-type="title" id=toc-5579>١٨ </span>- حقا إن ما كتب من أعمال المحسنين لفى عليين.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5497,'<span data-type="title" id=toc-5580>١٩ </span>- وما أعلمك ما عِليّون؟! والمعنى: تعظيم وتفخيم لهذا الكتاب وموضعه.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5498,'<span data-type="title" id=toc-5581>٢٠ </span>-، ٢١ - هو كتاب مسطور بيّن الكتابة، يحضره ويحفظه المقربون من الملائكة.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5499,'<span data-type="title" id=toc-5582>٢٢ </span>-، ٢٣ - إن الإبرار لفى نعيم الجنة. على الأرائك ينظرون إلى ما أولاهم الله من النعمة والكرامة.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5500,'<span data-type="title" id=toc-5583>٢٤ </span>- تعرف فى وجوههم بهجة النعيم ونضارته.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5501,'<span data-type="title" id=toc-5584>٢٥ </span>-، ٢٦ - يُسْقَون من شراب خالص مصون لا تزيده الصيانة إلا طيبا، وفى نيل ذلك النعيم فليتسابق المتسابقون.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5502,'<span data-type="title" id=toc-5585>٢٧ </span>-، ٢٨ - ومِزاج الرحيق من ماء تسنيم فى الجنة: عينا يشرب منها المقربون دون غيرهم من أهل الجنة.',NULL,'892',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5503,'<span data-type="title" id=toc-5586>٢٩ </span>- إن الذين ارتكبوا الجُرْم فى حق الدين كانوا يضحكون استهزاء فى الدنيا من الذين آمنوا.',NULL,'893',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5504,'<span data-type="title" id=toc-5587>٣٠ </span>- وإذا مر المؤمنون بهم يغمز بعضهم بعضا استهزاء.',NULL,'893',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5505,'<span data-type="title" id=toc-5588>٣١ </span>- وإذا رجع المجرمون إلى أهلهم رجعوا فرحين باستخفافهم بالمؤمنين.',NULL,'893',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5506,'<span data-type="title" id=toc-5589>٣٢ </span>-، ٣٣ - وإذا رأوا المؤمنين، قالوا: إن هؤلاء لضالون لإيمانهم بمحمد، وما أرسل هؤلاء المجرمون حاكمين عليهم بالرشد أو الضلال حافظين لأعمالهم.',NULL,'893',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5507,'<span data-type="title" id=toc-5590>٣٤ </span>- فيوم الجزاء تجد الذين آمنوا من الكفار يضحكون جزاء ما ضحكوا سخرية بهم فى الدنيا.',NULL,'893',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5508,'<span data-type="title" id=toc-5591>٣٥ </span>- على الأسرَّة والمتكآت ينظر المؤمنون ما أولاهم الله من النعيم.',NULL,'893',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5509,'<span data-type="title" id=toc-5592>٣٦ </span>- هل جوزى الكفار فى الآخرة ما كانوا يفعلون فى الدنيا؟ والاستفهام هنا للتقرير.',NULL,'893',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5510,'<span data-type="title" id=toc-5594>١ </span>- إذا السماء تصدعت بما يؤذن بزوالها.',NULL,'894',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5511,'<span data-type="title" id=toc-5595>٢ </span>- وسمعت لربها وأطاعت، وجدير بها أن تسمع وتطيع.',NULL,'894',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5512,'<span data-type="title" id=toc-5596>٣ </span>- وإذا الأرض زيدت سعة بدك جبالها وإزالة آكامها، وتغيرت طبيعتها.',NULL,'894',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5513,'<span data-type="title" id=toc-5597>٤ </span>- ورمت ما بجوفها من الموتى والكنوز، وتخلت عنه.',NULL,'894',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5514,'<span data-type="title" id=toc-5598>٥ </span>- وانقادت لربها فى زيادة سعتها، وإلقاء ما فى جوفها وتخليها عنه، وحقيق بها ذلك. إذا حدث كل ما تقدم لَقِىَ كل إنسان جزاء عمله.',NULL,'894',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5515,'<span data-type="title" id=toc-5599>٦ </span>- يا أيها الإنسان: إنك مجدّ فى عملك جدا يوصلك إلى غايتك، فملاق ربك بعملك، فيجازيك عليه.',NULL,'894',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5516,'<span data-type="title" id=toc-5600>٧ </span>-، ٨، ٩ - فأما مَن أعطى كتاب عمله بيمينه فسوف يحاسب حسابا يسيرا، ويرجع إلى عشيرته من المؤمنين مبتهجا.',NULL,'894',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5517,'<span data-type="title" id=toc-5601>١٠ </span>-، ١١، ١٢ - وأما من أوتى كتابه بشماله من وراء ظهره تحقيرا لأمره، فسوف يصبح متمنيا هلاك نفسه ويدخل جهنم يحترق بنارها.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5518,'<span data-type="title" id=toc-5602>١٣ </span>- إنه كان بين أهله فى الدنيا مسرورا بما أوتيه، لاهيا عن العمل لعاقبته.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5519,'<span data-type="title" id=toc-5603>١٤ </span>- إنه ظن أنه لن يرجع إلى الله فيحاسبه.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5520,'<span data-type="title" id=toc-5604>١٥ </span>- بل سيرجع ويُحاسب، إن ربه كان به وبأعماله بصيرا.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5521,'<span data-type="title" id=toc-5605>١٦ </span>- فأقسم قسما مؤكدا بحمرة الأفق بعد الغروب.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5522,'<span data-type="title" id=toc-5606>١٧ </span>- والليل وما جَمَعَ ولَف فى ظلمته من الناس والدواب وغيرها.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5523,'<span data-type="title" id=toc-5607>١٨ </span>-، ١٩ - والقمر إذا تكامل وتم نوره، لَتلاقُنَّ حالاً بعد حال، بعضها أشد من بعض، من الموت والبعث وأهوال القيامة.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5524,'<span data-type="title" id=toc-5608>٢٠ </span>- فأى شئ لهؤلاء الجاحدين يمنعهم من الإيمان بالله والبعث بعد وضوح الدلائل على وجوبه.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5525,'<span data-type="title" id=toc-5609>٢١ </span>- وإذا سمعوا آيات القرآن لا يسجدون ولا يخضعون.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5526,'<span data-type="title" id=toc-5610>٢٢ </span>- بل هؤلاء - لكفرهم - يُكذبون عنادا وتعالياً عن الحق.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5527,'<span data-type="title" id=toc-5611>٢٣ </span>- والله أعلم بما يضمرون فى قلوبهم.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5528,'<span data-type="title" id=toc-5612>٢٤ </span>- فبشِّرهم بعذاب أليم مستهزئا بهم.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5529,'<span data-type="title" id=toc-5613>٢٥ </span>- لكن الذين آمنوا وعملوا الصالحات لهم عند الله أجر غير مقطوع عنهم ولا محسوب عليهم.',NULL,'895',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5530,'<span data-type="title" id=toc-5615>١ </span>- أقسم بالسماء ذات المنازل التى تنزلها الكواكب أثناء سيرها.',NULL,'896',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5531,'<span data-type="title" id=toc-5616>٢ </span>- وباليوم الموعود للحساب والجزاء.',NULL,'896',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5532,'<span data-type="title" id=toc-5617>٣ </span>- وبحاضر من الخلائق فى هذا اليوم، وما يحضر فيه من الأهوال والعجائب.',NULL,'896',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5533,'<span data-type="title" id=toc-5618>٤ </span>- لقد لعن الله أصحاب الشق المستطيل فى الأرض.',NULL,'896',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5534,'<span data-type="title" id=toc-5619>٥ </span>- أصحاب النار ذات الوقود التى أضرموها لعذاب المؤمنين.',NULL,'896',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5535,'<span data-type="title" id=toc-5620>٦ </span>- إذ هم على حافتها قعود يشهدون عذاب المؤمنين.',NULL,'896',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5536,'<span data-type="title" id=toc-5621>٧ </span>- وهم على الذى يفعلون بالمؤمنين - من تعذيبهم - حضور.',NULL,'896',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5537,'<span data-type="title" id=toc-5622>٨ </span>- وما أنكروا من المؤمنين إلا إيمانهم بالله القوى الذى يُخشى عقابه، الحميد الذى يُرجى ثوابه.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5538,'<span data-type="title" id=toc-5623>٩ </span>- الذى له - وحده - ملك السموات والأرض، والله على كل شئ مما يفعله المؤمنون والكافرون شهيد يشهد ذلك ويجزى عليه.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5539,'<span data-type="title" id=toc-5624>١٠ </span>- إن الذين امتحنوا المؤمنين والمؤمنات فى دينهم بالأذى والتعذيب بالنار. ثم لم يرجعوا عن ذلك، فلهم فى الآخرة عذاب جهنم بكفرهم، ولهم عذاب الحريق بإحراقهم المؤمنين.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5540,'<span data-type="title" id=toc-5625>١١ </span>- إن الذين جمعوا إلى الإيمان بالله العمل الصالح. لهم جنات تجرى من تحتها الأنهار. ذلك النعيم الذى جوزوا به هو الفوز الكبير.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5541,'<span data-type="title" id=toc-5626>١٢ </span>- إن أخذ ربك للجبابرة والظلمة بالغ الغاية فى الشدة.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5542,'<span data-type="title" id=toc-5627>١٣ </span>- إنه - وحده - يبدأ الخلق ويعيدهم.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5543,'<span data-type="title" id=toc-5628>١٤ </span>- وهو كثير المغفرة لمن تاب وأناب. كثير المحبة لمن أحبَّه وأطاعه.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5544,'<span data-type="title" id=toc-5629>١٥ </span>- صاحب العرش ومالكه، عظيم فى ذاته وصفاته.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5545,'<span data-type="title" id=toc-5630>١٦ </span>- فعَّال لما يريد لا يتخلف عن قدرته مراد.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5546,'<span data-type="title" id=toc-5631>١٧ </span>- هل أتاك - يا محمد - حديث الجموع الطاغية من الأمم الخالية؟ .',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5547,'<span data-type="title" id=toc-5632>١٨ </span>- قوم فرعون، وثمود، وما حل بهم من جزاء تماديهم فى الباطل.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5548,'<span data-type="title" id=toc-5633>١٩ </span>- بل الكافرون من قومك أشد فى تكذيبهم لك من تكذيب هؤلاء لرسلهم.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5549,'<span data-type="title" id=toc-5634>٢٠ </span>- والله متمكن منهم عالم بهم.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5550,'<span data-type="title" id=toc-5635>٢١ </span>- بل ما جئتهم به قرآن عظيم بيِّن الدلالة على صدقك.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5551,'<span data-type="title" id=toc-5636>٢٢ </span>- فى لوح محفوظ لا ترقى إليه قوة بتحريف أو تبديل.',NULL,'897',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5552,'<span data-type="title" id=toc-5638>١ </span>- أقسم بالسماء وبالنجم الذى يظهر ليلاً.',NULL,'898',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5553,'<span data-type="title" id=toc-5639>٢ </span>-، ٣ - وأى شئ أعلمك ما حقيقة هذا النجم؟ هو الذى ينفذ ضوؤه فى الظلام.',NULL,'898',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5554,'<span data-type="title" id=toc-5640>٤ </span>- ما كل نفس إلا عليها حافظ يرقبها ويحصى عليها أعمالها.',NULL,'898',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5555,'<span data-type="title" id=toc-5641>٥ </span>- فليفكر الإنسان من أى شئ خلق؟ .',NULL,'898',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5556,'<span data-type="title" id=toc-5642>٦ </span>- خلق الإنسان من ماء متدفق.',NULL,'898',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5557,'<span data-type="title" id=toc-5643>٧ </span>- يخرج هذا الماء من بين الصلب وعظام الصدر من الرجل والمرأة.',NULL,'898',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5558,'<span data-type="title" id=toc-5644>٨ </span>- إن الله الذى خلقه هكذا ابتداء لقادر على إعادة خلقه بعد موته.',NULL,'899',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5559,'<span data-type="title" id=toc-5645>٩ </span>- يوم تُمَتَحن الضمائر، ويُميز بين ما طاب منها وما خبث.',NULL,'899',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5560,'<span data-type="title" id=toc-5646>١٠ </span>- فما للإنسان فى ذلك الوقت من قوة ذاتية ولا خارجية يمتنع بها، ولا ناصر ينتصر به.',NULL,'899',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5561,'<span data-type="title" id=toc-5647>١١ </span>- أقسم بالسماء ذات المطر الذى يعود ويتكرر.',NULL,'899',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5562,'<span data-type="title" id=toc-5648>١٢ </span>- وبالأرض ذات الإنشقاقات التى تتكون منها البحار والأنهار.',NULL,'899',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5563,'<span data-type="title" id=toc-5649>١٣ </span>-، ١٤ - إن القرآن فاصل بين الحق والباطل، وليس فيه شائبة اللعب والباطل.',NULL,'899',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5564,'<span data-type="title" id=toc-5650>١٥ </span>- إن المكذبين بالقرآن يمكرون فى إبطال أمره مكرا بالغ الغاية.',NULL,'899',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5565,'<span data-type="title" id=toc-5651>١٦ </span>- وأجازيهم وأقابل كيدهم بكيد متين لا يدفعونه.',NULL,'899',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5566,'<span data-type="title" id=toc-5652>١٧ </span>- فأنظر الكافرين: أمهلهم إمهالا قريبا حتى آمرك فيهم بأمر حاسم.',NULL,'899',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5567,'<span data-type="title" id=toc-5654>١ </span>- نزِّه اسم ربك الأعظم عما لا يليق به.',NULL,'900',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5568,'<span data-type="title" id=toc-5655>٢ </span>- الذى خلق كل شئ فجعله مستوى الخلق فى إحكام واتساق.',NULL,'900',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5569,'<span data-type="title" id=toc-5656>٣ </span>- والذى قدَّر لكل شئ ما يصلحه فهداه إليه.',NULL,'900',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5570,'<span data-type="title" id=toc-5657>٤ </span>- والذى أخرج من الأرض ما ترعاه الدواب من صنوف النباتات.',NULL,'900',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5571,'<span data-type="title" id=toc-5658>٥ </span>- فصيره بعد الخضرة يابسا مسودا.',NULL,'900',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5572,'<span data-type="title" id=toc-5659>٦ </span>- سنجعلك - يا محمد - قارئا بإلهام منا، فلا تَنْس ما تحفظ.',NULL,'900',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5573,'<span data-type="title" id=toc-5660>٧ </span>- إلا ما شاء الله أن تنساه، إنه تعالى يعلم ما يجهر به عباده وما يخفونه من الأقوال والأفعال.',NULL,'900',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5574,'<span data-type="title" id=toc-5661>٨ </span>- ونوفقك للطريقة البالغة اليسر فى كل أحوالك.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5575,'<span data-type="title" id=toc-5662>٩ </span>- فذكر الناس إن نفعت الذكرى، فشأنها أن تنفع.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5576,'<span data-type="title" id=toc-5663>١٠ </span>- سينتفع بتذكيرك من يخاف الله.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5577,'<span data-type="title" id=toc-5664>١١ </span>- ويتجنب الذكرى الأشقى المصر على العناد والكفر.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5578,'<span data-type="title" id=toc-5665>١٢ </span>- الذى يدخل النار الكبرى المعدة للجزاء.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5579,'<span data-type="title" id=toc-5666>١٣ </span>- ثم لا يموت فى النار فيستريح بالموت، ولا يحيا حياة يهنأ بها.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5580,'<span data-type="title" id=toc-5667>١٤ </span>- قد فاز مَن تطهر من الكفر والمعاصى.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5581,'<span data-type="title" id=toc-5668>١٥ </span>- وذكر اسم خالقه بقلبه ولسانه فصلى خاشعا ممثلا.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5582,'<span data-type="title" id=toc-5669>١٦ </span>- لم تفعلوا ما يؤدى إلى الفلاح. بل تقدمون فى اهتمامكم الحياة الدنيا على الآخرة.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5583,'<span data-type="title" id=toc-5670>١٧ </span>- والآخرة خير من الدنيا بصفاء نعيمها، وأبقى بدوامه.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5584,'<span data-type="title" id=toc-5671>١٨ </span>-، ١٩ - إن هذا المذكور فى هذه السورة لثابت فى الصحف الأولى - صحف إبراهيم وموسى - فهو مما توافقت فيه الأديان وسجلته الكتب السماوية.',NULL,'901',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5585,'<span data-type="title" id=toc-5673>١ </span>- هل أتاك - يا محمد - حديث القيامة التى تغشى الناس بأهوالها؟ .',NULL,'902',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5586,'<span data-type="title" id=toc-5674>٢ </span>-، ٣ - وجوه يوم القيامة ذليلة، دائبة العمل فيما يتعبها ويشقيها فى النار.',NULL,'902',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5587,'<span data-type="title" id=toc-5675>٤ </span>- تدخل ناراً شديدة الحرارة.',NULL,'902',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5588,'<span data-type="title" id=toc-5676>٥ </span>- تشرب من عين تناهى حرها.',NULL,'902',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5589,'<span data-type="title" id=toc-5677>٦ </span>- ليس لهم طعام إلا من نوع خبيث يُعذَّب به آكله.',NULL,'902',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5590,'<span data-type="title" id=toc-5678>٧ </span>- لا يؤثر سمنا فى الأجسام، ولا ينفع شيئاً من جوع.',NULL,'902',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5591,'<span data-type="title" id=toc-5679>٨ </span>-، ٩، ١٠ - وجوه يوم القيامة ذات نضارة جزاء عملها الذى عملته فى الدنيا. راضية فى جنة مرتفعة مكاناً وقدراً.',NULL,'902',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5592,'<span data-type="title" id=toc-5680>١١ </span>-، ١٢ - لا تسمع فيها كلمات لا معنى لها ولا فائدة منها، فيها عين جارية بالماء لا تنقطع.',NULL,'902',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5593,'<span data-type="title" id=toc-5681>١٣ </span>- فيها سرر مرتفعة مكاناً وقدراً زيادة لهم فى النعيم.',NULL,'902',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5594,'<span data-type="title" id=toc-5682>١٤ </span>- وأكواب حاضرة بين أيديهم.',NULL,'903',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5595,'<span data-type="title" id=toc-5683>١٥ </span>- ووسائد صف بعضها إلى جانب بعض.',NULL,'903',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5596,'<span data-type="title" id=toc-5684>١٦ </span>- وبُسط كثيرة متفرقة فى المجالس.',NULL,'903',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5597,'<span data-type="title" id=toc-5685>١٧ </span>- أيهملون التدبر فى الآيات، فلا ينظرون إلى الإبل كيف خلقت خلقاً بديعاً يدل على قدرة الله؟ .',NULL,'903',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5598,'<span data-type="title" id=toc-5686>١٨ </span>- وإلى السماء التى يشاهدونها دائماً، كيف رفعت رفعاً بعيد المدى بلا عمد؟ .',NULL,'903',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5599,'<span data-type="title" id=toc-5687>١٩ </span>- وإلى الجبال التى يصعدون إلى قممها، كيف أقيمت شامخة، تمسك الأرض فلا تميل ولا تميد؟ .',NULL,'903',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5600,'<span data-type="title" id=toc-5688>٢٠ </span>- وإلى الأرض التى يتقلبون عليها كيف بسطت ومهدت؟ .',NULL,'903',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5601,'<span data-type="title" id=toc-5689>٢١ </span>-، ٢٢ - فذكر بدعوتك، إنما مهمتك التبليغ، لست عليهم بمتسلط.',NULL,'904',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5602,'<span data-type="title" id=toc-5690>٢٣ </span>-، ٢٤ - لكن من أعرض منهم وكفر، فيعذبه الله العذاب الأكبر الذى لا عذاب فوقه.',NULL,'904',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5603,'<span data-type="title" id=toc-5691>٢٥ </span>- إن إلينا رجوعهم بالموت والبعث، لا إلى غيرنا.',NULL,'904',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5604,'<span data-type="title" id=toc-5692>٢٦ </span>- ثم إن علينا وحدنا حسابهم وجزاءهم.',NULL,'904',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5605,'<span data-type="title" id=toc-5694>١ </span>- أقسم بضوء الصبح عند مطاردته الليل.',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5606,'<span data-type="title" id=toc-5695>٢ </span>- وبليال عشر مفضلة عند الله.',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5607,'<span data-type="title" id=toc-5696>٣ </span>- وبالزوج والفرد من كل شئ.',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5608,'<span data-type="title" id=toc-5697>٤ </span>- وبالليل إذ يَنْقَضى بحركة الكون العجيبة.',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5609,'<span data-type="title" id=toc-5698>٥ </span>- هل فيما ذكر من الأشياء ما يراه العاقل قسماً مقنعاً؟',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5610,'<span data-type="title" id=toc-5699>٦ </span>-، ٧ - ألم تعلم كيف أنزل ربك عقابه بعاد - قوم هود - أهل إرم ذات البناء الرفيع؟ {',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5611,'<span data-type="title" id=toc-5700>٨ </span>- التى لم يُخلق مثلها فى البلاد متانة وضخامة بناء.',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5612,'<span data-type="title" id=toc-5701>٩ </span>- وألم تعلم كيف أنزل ربك عقابه بثمود - قوم صالح - الذين قطعوا الصخر من الجبال يبنون به القصور بالوادى؟}',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5613,'<span data-type="title" id=toc-5702>١٠ </span>- وألم تعلم كيف أنزل ربك عقابه بفرعون وجنوده الكثيرون الذين يشدون ملكه كما تشد الأوتاد الخيام؟!',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5614,'<span data-type="title" id=toc-5703>١١ </span>- الذين تجاوزوا الحدود فى البلاد.',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5615,'<span data-type="title" id=toc-5704>١٢ </span>- فأكثروا فيها الفساد بالكفر والظلم.',NULL,'905',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5616,'<span data-type="title" id=toc-5705>١٣ </span>- فأنزل عليهم ربك ألواناً ملهبة من العذاب.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5617,'<span data-type="title" id=toc-5706>١٤ </span>- إن ربك ليرقب عمل الناس، ويحصيه عليهم، ويجازيهم به.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5618,'<span data-type="title" id=toc-5707>١٥ </span>- فأما الإنسان إذا ما اختبره ربه فأكرمه، ونعَّمه بالمال والجاه والقوة، فيقول مغترا بذلك: ربى فَضَّلَنى لاستحقاقى لهذا.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5619,'<span data-type="title" id=toc-5708>١٦ </span>- وأما إذا ما اختبره ربه بضيق الرزق فيقول - غافلا عن الحكمة فى ذلك -: ربى أهاننى.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5620,'<span data-type="title" id=toc-5709>١٧ </span>- ارتدعوا، فليس الأمر كما تقولون. بل أنتم لا تكرمون اليتيم.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5621,'<span data-type="title" id=toc-5710>١٨ </span>- ولا يحث بعضكم بعضاً على إطعام المساكين.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5622,'<span data-type="title" id=toc-5711>١٩ </span>- وتأكلون المال الموروث أكلاً لمَّاً، لا تميزون فيه بين ما يحمد وما يذم.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5623,'<span data-type="title" id=toc-5712>٢٠ </span>- وتحبون المال حباً كثيراً يدفعكم إلى الحرص على جمعه والبخل بإنفاقه.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5624,'<span data-type="title" id=toc-5713>٢١ </span>- ارتدعوا عن تلك الأفعال لما ينتظركم من الوعيد إذا سويت الأرض تسوية بعد تسوية.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5625,'<span data-type="title" id=toc-5714>٢٢ </span>- وجاء ربك مجيئاً يليق به سبحانه، وجاءت الملائكة صفا صفا.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5626,'<span data-type="title" id=toc-5715>٢٣ </span>- وجئ يومئذٍ بجهنم دار العذاب. ويوم يحدث ذلك: يتذكر الإنسان ما فرَّط فيه، ومن أين له الذكرى النافعة، وقد فات أوانها؟',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5627,'<span data-type="title" id=toc-5716>٢٤ </span>- يقول نادماً: يا ليتنى قدَّمت فى الدنيا أعمالاً صالحةً تنفعنى لحياتى الآخرة.',NULL,'906',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5628,'<span data-type="title" id=toc-5717>٢٥ </span>-، ٢٦ - فيومئذٍ تكون هذه الأحوال، لا يعذب أحد كعذاب الله، ولا يقيد أحد كقيده.',NULL,'907',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5629,'<span data-type="title" id=toc-5718>٢٧ </span>- يا أيتها النفس المطمئنة بالحق.',NULL,'907',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5630,'<span data-type="title" id=toc-5719>٢٨ </span>- ارجعى إلى رضوان ربك راضية بما أوتيت من النعم. مرضية بما قدَّمت من عمل.',NULL,'907',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5631,'<span data-type="title" id=toc-5720>٢٩ </span>- فادخلى فى زمرة عبادى الصالحين.',NULL,'907',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5632,'<span data-type="title" id=toc-5721>٣٠ </span>- وادخلى جنتى دار النعيم المقيم.',NULL,'907',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5633,'<span data-type="title" id=toc-5723>١ </span>- أقسم قسماً مؤكداً بمكة<span data-type="title" id=toc-5722> البلد </span>الحرام.',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5634,'<span data-type="title" id=toc-5724>٢ </span>- وأنت مقيم بهذا البلد تزيده شرفاً وقدراً.',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5635,'<span data-type="title" id=toc-5725>٣ </span>- وبوالد وما ولد وبهما حفظ النوع وبقاء العمران.',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5636,'<span data-type="title" id=toc-5726>٤ </span>- لقد خلقنا الإنسان فى مشقة وتعب منذ نشأته إلى منتهى أمره.',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5637,'<span data-type="title" id=toc-5727>٥ </span>- أيظن الإنسان المخلوق فى هذه المشقة أن لن يقدر على إخضاعه أحد؟',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5638,'<span data-type="title" id=toc-5728>٦ </span>- يقول: أنفقت فى عداوة محمد - صَلَّى اللَّهُ عَلَيْهِ وَسَلَّم َ - والصَّد عن دعوته مالاً كثيراً.',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5639,'<span data-type="title" id=toc-5729>٧ </span>- أيظن أن أمره قد خفى فلم يطَّلع عليه أحد حتى من خلقه؟',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5640,'<span data-type="title" id=toc-5730>٨ </span>-، ٩ - ألم نخلق له عينين ينظر بهما؟ ولساناً وشفتين ليتمكن من النطق والإبانة؟',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5641,'<span data-type="title" id=toc-5731>١٠ </span>- وبيَّنا له طريقى الخير والشر وهيَّأناه للاختيار؟',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5642,'<span data-type="title" id=toc-5732>١١ </span>- فلا انتفع بما هيَّأناه له، ولا تخطى العقبة التى تحول بينه وبين النجاة، وهى شح نفسه.',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5643,'<span data-type="title" id=toc-5733>١٢ </span>- وأى شئ أعلمك ما اقتحام العقبة؟!',NULL,'908',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5644,'<span data-type="title" id=toc-5734>١٣ </span>- عتق لنفس وتحريرها من العبودية.',NULL,'909',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5645,'<span data-type="title" id=toc-5735>١٤ </span>- أو إطعام فى حالات المجاعة.',NULL,'909',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5646,'<span data-type="title" id=toc-5736>١٥ </span>- يتيماً ذا قرابة يُواسى لرحمه وفقره.',NULL,'909',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5647,'<span data-type="title" id=toc-5737>١٦ </span>- أو مسكيناً ذا حاجة وافتقار.',NULL,'909',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5648,'<span data-type="title" id=toc-5738>١٧ </span>- ثم كان مع ذلك من أهل الإيمان الذين يتواصون فيما بينهم بالصبر وبالرحمة.',NULL,'909',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5649,'<span data-type="title" id=toc-5739>١٨ </span>- أولئك الموصوفون بهذه الصفات هم السعداء أصحاب اليمين.',NULL,'909',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5650,'<span data-type="title" id=toc-5740>١٩ </span>- والذين كفروا بما نصبناه دليلاً على الحق من كتاب وحجة هم الأشقياء أهل الشؤم والعذاب.',NULL,'909',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5651,'<span data-type="title" id=toc-5741>٢٠ </span>- عليهم نار مطبقة مُغلَّقة أبوابها.',NULL,'909',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5652,'<span data-type="title" id=toc-5743>١ </span>- أقسم ب<span data-type="title" id=toc-5742>الشمس </span>وبضوئها وإشراقها وحرارتها.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5653,'<span data-type="title" id=toc-5744>٢ </span>- وبالقمر إذا تبعها وخلَفها فى الإضاءة بعد غروبها.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5654,'<span data-type="title" id=toc-5745>٣ </span>- وبالنهار إذا أظهر الشمس واضحة غير محجوبة.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5655,'<span data-type="title" id=toc-5746>٤ </span>- وبالليل إذا يغشى الشمس، فيغطى ضوءها.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5656,'<span data-type="title" id=toc-5747>٥ </span>- وبالسماء وبالقادر العظيم الذى رفعها وأحكم بناءها.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5657,'<span data-type="title" id=toc-5748>٦ </span>- وبالأرض وبالقادر العظيم الذى بسطها من كل جانب، وهيَّأها للاستقرار، وجعلها مهادا للإنسان.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5658,'<span data-type="title" id=toc-5749>٧ </span>- وبالنفس ومَن أنشأها وعدَّلها بما أودع فيها من القوى.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5659,'<span data-type="title" id=toc-5750>٨ </span>- فعرفها الحسن والقبيح، ومنحها القدرة على فعل ما تريد منهما.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5660,'<span data-type="title" id=toc-5751>٩ </span>- قد فاز من طَهَّرَ نفسه بالطاعات وعمل الخير.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5661,'<span data-type="title" id=toc-5752>١٠ </span>- وقد خسر من أخفى فضائلها، وأمات استعدادها للخير.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5662,'<span data-type="title" id=toc-5753>١١ </span>-، ١٢ - كذَّبت ثمود نبيها بطغيانها وبغيها، حين نهض أشقاها مريداً عقر الناقة.',NULL,'910',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5663,'<span data-type="title" id=toc-5754>١٣ </span>- فقال لهم صالح - رسول الله -: اتركوا ناقة الله تأكل فى أرض الله، واحذروا منعها الشرب فى يومها.',NULL,'911',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5664,'<span data-type="title" id=toc-5755>١٤ </span>- فكذَّبوا رسولهم فى وعيده فعقروها، فدمر عليهم ربهم ديارهم بذنبهم، فسواها بالأرض.',NULL,'911',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5665,'<span data-type="title" id=toc-5756>١٥ </span>- ولا يخاف تبعة هذه العقوبة؛ لأنها الجزاء العادل لما صنعوا.',NULL,'911',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5666,'<span data-type="title" id=toc-5758>١ </span>- أقسم ب<span data-type="title" id=toc-5757>الليل </span>حين يعم ظلامه.',NULL,'912',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5667,'<span data-type="title" id=toc-5759>٢ </span>- وبالنهار إذا سطع ضوؤه.',NULL,'912',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5668,'<span data-type="title" id=toc-5760>٣ </span>- وبالله الذى خلق الصنفين الذكر والأنثى من كل ما يتوالد.',NULL,'912',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5669,'<span data-type="title" id=toc-5761>٤ </span>- إن سعيكم لمختلِف، فمنه ما يسعد به الساعى، ومنه ما يشقى به.',NULL,'912',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5670,'<span data-type="title" id=toc-5762>٥ </span>-، ٦، ٧ - فأما مَن أنفق فى سبيل الله، وخاف ربه فاجتنب محارمه، وأيقن بالفضيلة الحسنى، وهى الإيمان بالله عن علم، فسنهيئه للخصلة التى تؤدى إلى يسر وراحة بتوجيهه إلى طريق الخير.',NULL,'912',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5671,'<span data-type="title" id=toc-5763>٨ </span>-، ٩، ١٠ - وأما مَن بخل بماله فلم يؤد حق الله فيه، واستغنى به عما عند الله، وكذب بالخصلة الحسنى، فسنهيئه للخصلة التى تؤدى إلى العسر والشقاء الأبدى.',NULL,'912',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5672,'<span data-type="title" id=toc-5764>١١ </span>- وأى شئ من العذاب يدفعه عنه ماله الذى بخل به إذا هلك؟',NULL,'912',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5673,'<span data-type="title" id=toc-5765>١٢ </span>- إن علينا بمقتضى حكمتنا أن نبين للخلق طريق الهدى.',NULL,'912',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5674,'<span data-type="title" id=toc-5766>١٣ </span>- وإن لنا - وحدنا - لأمر التصرف فى الدارين.',NULL,'913',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5675,'<span data-type="title" id=toc-5767>١٤ </span>- فَخَوَّفْتُكُم ناراً تتوقد وتتلهب.',NULL,'913',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5676,'<span data-type="title" id=toc-5768>١٥ </span>-، ١٦ - لا يدخلها مخلَّدا فيها إلا الكافر الذى كذَّب بالحق وأعرض عن آيات ربه.',NULL,'913',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5677,'<span data-type="title" id=toc-5769>١٧ </span>-، ١٨ - وسيُبعد عنها المبالغ الأكثر خشية لله وإعراضا عن معاصيه. الذى يعطى ماله فى وجوه اليسر يتطهر من رجس البخل ودنس الإمساك.',NULL,'913',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5678,'<span data-type="title" id=toc-5770>١٩ </span>- وليس لأحد عند هذا المنفق من نعمة أو يد يكافأ بهما.',NULL,'913',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5679,'<span data-type="title" id=toc-5771>٢٠ </span>- لكن يعطيه ابتغاء وجه ربه الأعلى.',NULL,'913',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5680,'<span data-type="title" id=toc-5772>٢١ </span>- ولسوف ينال من ربه ما يبتغيه على أكمل الوجوه حتى يتحقق له الرضا.',NULL,'913',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5681,'<span data-type="title" id=toc-5774>١ </span>- أقسم بوقت ارتفاع الشمس. والنشاط فى العمل.',NULL,'914',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5682,'<span data-type="title" id=toc-5775>٢ </span>- وبالليل إذا سكن وامتد ظلامه.',NULL,'914',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5683,'<span data-type="title" id=toc-5776>٣ </span>- ما تركك ربك - يا محمد - وما كرهك.',NULL,'914',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5684,'<span data-type="title" id=toc-5777>٤ </span>- ولعاقبة أمرك ونهايته خير من بدايته.',NULL,'914',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5685,'<span data-type="title" id=toc-5778>٥ </span>- وأقسم لسوف يعطيك ربك من خيرى الدنيا والآخرة حتى ترضى.',NULL,'914',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5686,'<span data-type="title" id=toc-5779>٦ </span>- ألم يجدك يتيماً تحتاج إلى مَن يرعاك، فآواك إلى مَن يحسن القيام بأمرك؟',NULL,'914',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5687,'<span data-type="title" id=toc-5780>٧ </span>- ووجدك حائراً لا تقنعك المعتقدات حولك، فهداك إلى منهج الحق؟',NULL,'914',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5688,'<span data-type="title" id=toc-5781>٨ </span>- ووجدك فقيراً من المال فأغناك بما أعطاك من رزق؟',NULL,'914',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5689,'<span data-type="title" id=toc-5782>٩ </span>-، ١٠، ١١ - إذا كان هذا حالنا معك، فأما اليتيم فلا تذله، وأما السائل فلا ترده بقسوة، وأما بنعمة ربك فحدِّث شكراً لله وإظهاراً للنعمة.',NULL,'914',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5690,'<span data-type="title" id=toc-5784>١ </span>- قد شرحنا لك صدرك بما أودعنا فيه من الهدى والإيمان.',NULL,'915',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5691,'<span data-type="title" id=toc-5785>٢ </span>- وخففنا عنك ما أثقل ظهرك من أعباء الدعوة بمساندتك وتيسير أمرك.',NULL,'915',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5692,'<span data-type="title" id=toc-5786>٣ </span>- الذى أثقل ظهرك.',NULL,'915',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5693,'<span data-type="title" id=toc-5787>٤ </span>- ونوهنا باسمك، فجعلناه مذكوراً على لسان كل مؤمن مقروناً باسمنا.',NULL,'915',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5694,'<span data-type="title" id=toc-5788>٥ </span>- تلك بعض نعمتنا عليك، فكن على ثقة من ألطافه تعالى، فإن من العسر يسراً كثيراً يحيط به.',NULL,'915',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5695,'<span data-type="title" id=toc-5789>٦ </span>- إن مع العسر يسراً كثيراً كذلك.',NULL,'915',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5696,'<span data-type="title" id=toc-5790>٧ </span>- فإذا فرغت من أمر الدعوة ومقتضيات الجهاد، فاجتهد فى العبادة وأتعب نفسك فيها.',NULL,'915',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5697,'<span data-type="title" id=toc-5791>٨ </span>- وإلى ربك - وحده - فاتجه بمسألتك وحاجتك.',NULL,'915',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5698,'<span data-type="title" id=toc-5793>١ </span>- أقسم ب<span data-type="title" id=toc-5792>التين </span>والزيتون لبركتهما وعظيم منفعتهما.',NULL,'916',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5699,'<span data-type="title" id=toc-5794>٢ </span>- وبالجبل الذى كلَّم الله عليه موسى.',NULL,'916',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5700,'<span data-type="title" id=toc-5795>٣ </span>- وهذا البلد - مكة - المعظمة، يشهد بعظمتها مَن زارها. الآمن مَنْ دخلها.',NULL,'916',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5701,'<span data-type="title" id=toc-5796>٤ </span>- لقد خلقنا جنس الإنسان مقوماً فى أحسن ما يكون من التعديل. متصفاً بأجمل ما يكون من الصفات.',NULL,'916',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5702,'<span data-type="title" id=toc-5797>٥ </span>- ثم أنزلنا درجته إلى أسفل سافلين لعدم قيامه بموجب ما خلقناه عليه.',NULL,'916',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5703,'<span data-type="title" id=toc-5798>٦ </span>- لكن الذين آمنوا وعملوا الأعمال الصالحة، فلهم أجر غير مقطوع عنهم ولا ممنون به عليهم.',NULL,'916',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5704,'<span data-type="title" id=toc-5799>٧ </span>- فأى شئ يحملك على التكذيب بالبعث والجزاء. بعد أن وضحت قدرتنا على ذلك؟ {',NULL,'916',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5705,'<span data-type="title" id=toc-5800>٨ </span>- أليس الله الذى فعل ما أنبأناك به بأحكم الحاكمين صنعاً وتدبيراً؟}',NULL,'916',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5706,'<span data-type="title" id=toc-5802>١ </span>- اقرأ - يا محمد - ما يوحى إليك مفتتحاً باسم ربك الذى له - وحده - القدرة على الخلق.',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5707,'<span data-type="title" id=toc-5803>٢ </span>- أوجد الإنسان الكامل الجسم والعلم من علق.',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5708,'<span data-type="title" id=toc-5804>٣ </span>- امض فى القراءة وربك الأكرم يقدرك ولا يخذلك.',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5709,'<span data-type="title" id=toc-5805>٤ </span>- الذى علَّم الإنسان الكتابة بالقلم ولم يكن يعلمها.',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5710,'<span data-type="title" id=toc-5806>٥ </span>- علم الإنسان ما لم يكن يخطر بباله.',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5711,'<span data-type="title" id=toc-5807>٦ </span>-، ٧ - حقاً إن الإنسان ليجاوز الحدود يستكبر على ربه، من أجل أن رأى نفسه ذا غنى وثراء.',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5712,'<span data-type="title" id=toc-5808>٨ </span>- إن إلى ربك - وحده - يا محمد رجوع الكل بالبعث والجزاء.',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5713,'<span data-type="title" id=toc-5809>٩ </span>-، ١٠ - أأبصرت هذا الطاغى الذى ينهى عبداً عن الصلاة إذا صلى؟ {',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5714,'<span data-type="title" id=toc-5810>١١ </span>-، ١٢ - أخبرنى عن حال هذا الطاغى إن كان على الهدى فى نهيه، أو أمر بالتقوى فيما أمر.',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5715,'<span data-type="title" id=toc-5811>١٣ </span>- أخبرنى عن حال هذا الناهى إن كذَّب بما جاء به الرسول، وأعرض عن الإيمان والعمل الطيب.',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5716,'<span data-type="title" id=toc-5812>١٤ </span>- أجهل أن الله يطلع على أحواله فيجازيه بها؟}',NULL,'917',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5717,'<span data-type="title" id=toc-5813>١٥ </span>- ردْعاً لهذا الناهى، لئن لم ينزجر عما هو عليه لنأخذن بناصيته إلى النار بشدة.',NULL,'918',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5718,'<span data-type="title" id=toc-5814>١٦ </span>- ناصية يعلو وجه صاحبها الكذب وآثار الخطيئة.',NULL,'918',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5719,'<span data-type="title" id=toc-5815>١٧ </span>- فليطلب عشيرته وأهل مجلسه ليكونوا نصراء فى الدنيا أو فى الآخرة.',NULL,'918',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5720,'<span data-type="title" id=toc-5816>١٨ </span>- سندعو جنودنا لينصروا محمداً ومَن معه، وليدفعوا هذا الناهى وأعوانه إلى جهنم.',NULL,'918',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5721,'<span data-type="title" id=toc-5817>١٩ </span>- ردْعاً لهذا الناهى، لا تطعه فيما نهاك عنه، ودم على صلاتك وواظب على سجودك، وتقرَّب بذلك إلى ربك.',NULL,'918',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5722,'<span data-type="title" id=toc-5819>١ </span>- إنا أنزلنا القرآن فى ليلة<span data-type="title" id=toc-5818> القدر </span>والشرف.',NULL,'919',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5723,'<span data-type="title" id=toc-5820>٢ </span>- وأى شئ أعلمك ما ليلة القدر والشرف؟!',NULL,'919',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5724,'<span data-type="title" id=toc-5821>٣ </span>- ليلة القدر والشرف خير من ألف شهر بما اختصت به من تنزيل القرآن الكريم.',NULL,'919',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5725,'<span data-type="title" id=toc-5822>٤ </span>- تنزل الملائكة وجبريل فيها إلى الأرض بإذن ربهم من أجل كل أمر.',NULL,'919',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5726,'<span data-type="title" id=toc-5823>٥ </span>- أمان من الأذى والسوء، هى كذلك حتى مطلع الفجر.',NULL,'919',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5727,'<span data-type="title" id=toc-5825>١ </span>- لم يكن الذين كفروا بالله وبرسوله من اليهود والنصارى، ومن المشركين منصرفين عن غفلتهم وجهلهم بالحق حتى تأتيهم الحُجة القاطعة.',NULL,'920',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5728,'<span data-type="title" id=toc-5826>٢ </span>-، ٣ - رسول مبعوث من عند الله يقرأ عليهم صحفاً مُنزَّهة عن الباطل، فيها أحكام مستقيمة ناطقة بالحق والصواب.',NULL,'920',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5729,'<span data-type="title" id=toc-5827>٤ </span>- وما تفرق الذين أوتوا الكتاب من اليهود والنصارى. إلا من بعد ما جاءتهم الحُجة الواضحة الدالة على أن محمداً هو رسول الله الموعود به فى كتبهم.',NULL,'920',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5730,'<span data-type="title" id=toc-5828>٥ </span>- وما كُلِّفوا بما كُلِّفوا به إلا لتكون عبادتهم لله مخلصين له الدين، مائلين عن الباطل مستقيمين على الحق، وأن يحافظوا على الصلاة ويؤدوا الزكاة، وذلك دين الملة المستقيمة.',NULL,'920',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5731,'<span data-type="title" id=toc-5829>٦ </span>- إن الذين كفروا من أهل الكتاب والمشركين فى نار جهنم يصلونها ولا يخرجون منها. أولئك هم شر الخليقة عقيدة وعملاً.',NULL,'920',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5732,'<span data-type="title" id=toc-5830>٧ </span>- إن الذين آمنوا بالله ورسوله وعملوا الأعمال الصالحة، أولئك هم خير الخليقة عقيدة وعملاً.',NULL,'920',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5733,'<span data-type="title" id=toc-5831>٨ </span>- جزاؤهم فى الآخرة على ما قدموا من الإيمان والأعمال الصالحة، جنات إقامة تجرى من تحتها الأنهار ماكثين فيها أبداً، قَبِلَ الله أعمالهم، وشكروا إحسانه إليهم. ذلك الجزاء لمَن خاف عقاب ربه، فآمن وعمل صالحاً.',NULL,'921',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5734,'<span data-type="title" id=toc-5833>١ </span>- إذا حُرِّكت الأرض حركة شديدة، واضطربت أقوى ما يكون من التحريك والاضطراب الذى تطيقه وتحتمله.',NULL,'922',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5735,'<span data-type="title" id=toc-5834>٢ </span>- وأخرجت الأرض ما فى بطنها من الكنوز والموتى.',NULL,'922',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5736,'<span data-type="title" id=toc-5835>٣ </span>- وقال الإنسان فى دهشة وخوف: ما للأرض تتزلزل، وتخرج ما فى بطنها. جاءت الساعة؟!',NULL,'922',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5737,'<span data-type="title" id=toc-5836>٤ </span>-، ٥ - يومئذٍ تُحَدِّث الأرض الإنسان أخبارها التى أفزعته بأن ربه وخالقه أوحى لها: أن تتزلزل وتضطرب، فسارعت إلى امتثال أمره.',NULL,'922',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5738,'<span data-type="title" id=toc-5837>٦ </span>- يومئذٍ يخرج الناس من قبورهم سراعاً متفرقين، ليتبيَّنوا حسابهم وجزاءهم الذى وعدهم الله به.',NULL,'922',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5739,'<span data-type="title" id=toc-5838>٧ </span>- فمن يعمل زنة ذرة من التراب خيراً. يره فى صحيفته ويلق جزاءه عليه.',NULL,'922',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5740,'<span data-type="title" id=toc-5839>٨ </span>- ومن يعمل زنة ذرة من التراب شراً. يره كذلك، ويلق جزاءه ولا يظلم ربك أحداً.',NULL,'922',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5741,'<span data-type="title" id=toc-5841>١ </span>- أقسم بخيل الجهاد المسرعات، يسمع لأنفاسها صوت هو: الضبح.',NULL,'923',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5742,'<span data-type="title" id=toc-5842>٢ </span>- بالخيل التى تخرج شرر النار من الأرض بوقْع حوافرها واندفاعها فى سيرها.',NULL,'923',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5743,'<span data-type="title" id=toc-5843>٣ </span>- بالخيل التى تغير على العدو قبل طلوع الشمس.',NULL,'923',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5744,'<span data-type="title" id=toc-5844>٤ </span>- فأثارت هذه الخيل فى مواقع العدو غباراً كثيفاً لا يشق.',NULL,'923',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5745,'<span data-type="title" id=toc-5845>٥ </span>- فجعلن الغبار يتوسط جمع العدو حتى يصيبه الرعب والفزع.',NULL,'923',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5746,'<span data-type="title" id=toc-5846>٦ </span>- إن الإنسان لنعم ربه التى لا تحصى لشديد الكفران والجحود.',NULL,'923',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5747,'<span data-type="title" id=toc-5847>٧ </span>- وإنه على ذلك فى الآخرة لشهيد على نفسه معترف بذنوبه.',NULL,'923',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5748,'<span data-type="title" id=toc-5848>٨ </span>- وإنه لحبه المال وحرصه عليه لبخيل به لا يؤدى ما وجب فيه.',NULL,'923',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5749,'<span data-type="title" id=toc-5849>٩ </span>-، ١٠ - أجهل عاقبة أمره فلا يعلم إذا نُشِر ما فى القبور من أجساد، وجُمِع ما فى الصدور - وقد سُجل فى صحفهم - من خير اكتسبوه وشر اقترفوه.',NULL,'923',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5750,'<span data-type="title" id=toc-5850>١١ </span>- إن ربهم وخالقهم - بأعمالهم وجزائهم يوم البعث والحساب - لخبير.',NULL,'923',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5751,'<span data-type="title" id=toc-5852>١ </span>- هى القيامة التى تبدأ بالنفخة الأولى، وتنتهى بفصل القضاء بين الناس.',NULL,'924',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5752,'<span data-type="title" id=toc-5853>٢ </span>- أى شئ عجيب - هى - فى ضخامتها وخطرها وفظاعتها؟ {',NULL,'924',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5753,'<span data-type="title" id=toc-5854>٣ </span>- أى شئ أعلمك ما شأن القارعة فى هولها على النفوس؟}',NULL,'924',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5754,'<span data-type="title" id=toc-5855>٤ </span>- هى كائنة يوم يكون الناس كالفراش المبثوث كثرة وتدافعاً يميناً وشمالاً ضعيفاً ذليلاً.',NULL,'924',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5755,'<span data-type="title" id=toc-5856>٥ </span>- وتكون الجبال كالصوف الملون المنفوش فى تفرق الأجزاء والتطاير فى الجو هنا وهناك.',NULL,'924',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5756,'<span data-type="title" id=toc-5857>٦ </span>-، ٧ - فأما مَن ثقلت موازينه فرجحت حسناته على سيئاته، فهو فى عيشة يرضاها صاحبها تطيب نفسه بها.',NULL,'924',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5757,'<span data-type="title" id=toc-5858>٨ </span>-، ٩ - وأما مَن خفت موازينه فرجحت سيئاته على حسناته، فمأواه جهنم.',NULL,'924',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5758,'<span data-type="title" id=toc-5859>١٠ </span>- وما أعلمك ما الهاوية؟!',NULL,'924',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5759,'<span data-type="title" id=toc-5860>١١ </span>- نار حامية لا تبلغ حرارتها أية نار مهما سُعِّرت وألقى فيها من وقود.',NULL,'924',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5760,'<span data-type="title" id=toc-5862>١ </span>-، ٢ - شغلكم عن الواجبات والطاعات تباهيكم بالأولاد والأنصار، وتفاخركم بالأموال والأحساب والأنساب حتى أصابكم الموت.',NULL,'925',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5761,'<span data-type="title" id=toc-5863>٣ </span>- حقا سوف تعلمون عاقبة سفهكم وتفريطكم.',NULL,'925',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5762,'<span data-type="title" id=toc-5864>٤ </span>- ثم حقا سوف تعلمون حتماً تلك العاقبة.',NULL,'925',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5763,'<span data-type="title" id=toc-5865>٥ </span>- حقا لو تعلمون يقيناً سوء مصيركم لفزعتم من تكاثركم وتزودتم لآخرتكم.',NULL,'925',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5764,'<span data-type="title" id=toc-5866>٦ </span>- أقسم لكم وأؤكد - أيها الناس - أنكم ستشاهدون النار الموقدة.',NULL,'925',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5765,'<span data-type="title" id=toc-5867>٧ </span>- ثم أقسم وأؤكد أنكم ستشاهدونها عياناً ويقيناً.',NULL,'925',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5766,'<span data-type="title" id=toc-5868>٨ </span>- ثم أقسم وأؤكد أنكم ستحاسبون على ألوان النعيم الذى أُترفتم فيه واستمتعتم به، ولم تؤدوا فيه حق لله.',NULL,'925',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5767,'<span data-type="title" id=toc-5870>١ </span>- أقسم بالزمان لكثرة ما انطوى عليه من عجائب وعبر.',NULL,'926',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5768,'<span data-type="title" id=toc-5871>٢ </span>- إن كل إنسان لفى نوع من الخسران. لما يغلب عليه من الأهواء والشهوات.',NULL,'926',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5769,'<span data-type="title" id=toc-5872>٣ </span>- إلا الذين آمنوا بالله وعملوا الصالحات، وأقاموا على الطاعات، وأوصى بعضهم بعضا بالتمسك بالحق اعتقادا وقولاً وعملاً، وأوصى بعضهم بعضا بالصبر على المشاق التى تعترض مَن يعتصم بالدين، فهؤلاء ناجون من الخسران. مُفلحون فى الدنيا والآخرة.',NULL,'926',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5770,'<span data-type="title" id=toc-5874>١ </span>- عذاب شديد وهلاك لمن دأبه أن يعيب الناس بالقول أو بالإشارة أو يتكلم فى أعراضهم.',NULL,'927',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5771,'<span data-type="title" id=toc-5875>٢ </span>- الذى جمع مالاً كثيرا وأحصى عدَّه مرة بعد أخرى حبا له، وتلذذا بإحصائه. دون أن يؤدى فيه حق الله تعالى.',NULL,'927',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5772,'<span data-type="title" id=toc-5876>٣ </span>- يظن أن ماله يخلده فى الدنيا ويدفع عنه ما يكره.',NULL,'927',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5773,'<span data-type="title" id=toc-5877>٤ </span>- ليرتدع عن هذا الظن، والله ليطرحن - لسوء عمله - فى النار التى تحطم كل ما يلقى فيها.',NULL,'927',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5774,'<span data-type="title" id=toc-5878>٥ </span>- وأى شئ أعلمك ما حقيقة هذه النار الحطمة؟',NULL,'927',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5775,'<span data-type="title" id=toc-5879>٦ </span>- نار الله المسعَّرة بأمره الموقدة دائما.',NULL,'927',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5776,'<span data-type="title" id=toc-5880>٧ </span>- التى تصل القلوب وتُحيط بها.',NULL,'927',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5777,'<span data-type="title" id=toc-5881>٨ </span>-، ٩ - إنها عليهم مغلَّقة الأبواب، وهم موثقون فيها مشدودون إلى عُمُدٍ ممدودة. فلا حركة لهم فيها ولا خلاص لهم منها.',NULL,'927',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5778,'<span data-type="title" id=toc-5883>١ </span>- قد علمت - يا محمد - علماً لا يخالطه شك فِعْل ربك بأصحاب<span data-type="title" id=toc-5882> الفيل</span>؛ الذين قصدوا الاعتداء على البيت الحرام.',NULL,'928',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5779,'<span data-type="title" id=toc-5884>٢ </span>- قد علمت أن الله قد جعل سعيهم لتخريب الكعبة فى تضييع وإبطال، فخيب مسعاهم، ولم ينالوا قصدهم.',NULL,'928',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5780,'<span data-type="title" id=toc-5885>٣ </span>- وسلَّط الله عليهم من جنوده طيرا أتتهم جماعات متتابعة وأحاطت بهم من كل ناحية.',NULL,'928',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5781,'<span data-type="title" id=toc-5886>٤ </span>- تقذفهم بحجارة من جهنم.',NULL,'928',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5782,'<span data-type="title" id=toc-5887>٥ </span>- فجعلهم كورق زرع أصابته آفة فأتلفته.',NULL,'928',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5783,'<span data-type="title" id=toc-5889>١ </span>-، ٢ - أعجبوا لما يَسَّرْتُ لهم رحلة الشتاء إلى اليمن، ورحلة الصيف إلى الشام فى اطمئنان وأمن للاتجار وابتغاء الرزق.',NULL,'929',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5784,'<span data-type="title" id=toc-5890>٣ </span>- فليخلصوا العبادة لرب هذا البيت الذى مكَّنهم من هاتين الرحلتين.',NULL,'929',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5785,'<span data-type="title" id=toc-5891>٤ </span>- الذى أطعمهم من جوع وهم بواد غير ذى زرع، وآمنهم من خوف والناس يُتَخطَّفون من حولهم.',NULL,'929',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5786,'<span data-type="title" id=toc-5893>١ </span>- أَعَرَفْت الذى يكذب بالجزاء والحساب فى الآخرة؟',NULL,'930',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5787,'<span data-type="title" id=toc-5894>٢ </span>-، ٣ - إن أردت أن تعرفه فهو الذى يدفع اليتيم دفعا عنيفا، ويقهره ويظلمه، ولا يحث على إطعام المسكين.',NULL,'930',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5788,'<span data-type="title" id=toc-5895>٤ </span>-، ٥ - فهلاك للمصلين المتصفين بهذه الصفات الذين هم عن صلاتهم غافلون غير منتفعين بها.',NULL,'930',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5789,'<span data-type="title" id=toc-5896>٦ </span>- الذين هم يظهرون للناس أعمالهم لينالوا المنزلة فى قلوبهم والثناء عليهم.',NULL,'930',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5790,'<span data-type="title" id=toc-5897>٧ </span>- ويمنعون معروفهم ومعونتهم عن الناس.',NULL,'930',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5791,'<span data-type="title" id=toc-5899>١ </span>- إنا أوليناك الخير الكثير الدائم فى الدنيا والآخرة.',NULL,'931',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5792,'<span data-type="title" id=toc-5900>٢ </span>- وإذا أعطيت ذلك فَدُمْ على الصلاة لربك خالصة له، وانحر ذبائحك شكرا لله على ما أولاك من كرامة، وخصَّك من خير.',NULL,'931',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5793,'<span data-type="title" id=toc-5901>٣ </span>- إن مَن يكرهك هو المنقطع عن كل خير.',NULL,'931',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5794,'<span data-type="title" id=toc-5903>١ </span>- قل - يا محمد -: يا أيها<span data-type="title" id=toc-5902> الكافرون </span>المصرون على كفرهم.',NULL,'932',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5795,'<span data-type="title" id=toc-5904>٢ </span>- لا أعبد الذي تعبدون من دون الله.',NULL,'932',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5796,'<span data-type="title" id=toc-5905>٣ </span>- ولا أنتم عابدون الذى أعبد، وهو الله وحده.',NULL,'932',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5797,'<span data-type="title" id=toc-5906>٤ </span>- ولا أنا عابد مثل عبادتكم لأنكم مشركون.',NULL,'932',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5798,'<span data-type="title" id=toc-5907>٥ </span>- ولا أنتم عابدون مثل عبادتى لأنها التوحيد.',NULL,'932',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5799,'<span data-type="title" id=toc-5908>٦ </span>- لكم دينكم الذى اعتقدتموه، ولى دينى الذى ارتضاه الله لى.',NULL,'932',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5800,'<span data-type="title" id=toc-5910>١ </span>- إذا تحقق نصر الله والفتح لك وللمؤمنين.',NULL,'933',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5801,'<span data-type="title" id=toc-5911>٢ </span>- ورأيت الناس يدخلون فى دين الله جماعات جماعات.',NULL,'933',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5802,'<span data-type="title" id=toc-5912>٣ </span>- فاشكر ربك، وسبح بحمده. واطلب مغفرته لك ولأمتك. إنه كان تَوَّابا كثير القبول لتوبة عباده.',NULL,'933',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5803,'<span data-type="title" id=toc-5914>١ </span>- هلكت يدا أبى لهب اللتان كان يؤذى بهما المسلمين، وهلك معهما.',NULL,'934',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5804,'<span data-type="title" id=toc-5915>٢ </span>- ما دفع عنه عذاب الله ماله الذى كان له، ولا جاهه الذى كسبه.',NULL,'934',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5805,'<span data-type="title" id=toc-5916>٣ </span>- سيدخل نارا مشتعلة يحترق فيها.',NULL,'934',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5806,'<span data-type="title" id=toc-5917>٤ </span>- وستدخل امرأته حَمَّالة النميمة بين الناس النار كما دخلها.',NULL,'934',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5807,'<span data-type="title" id=toc-5918>٥ </span>- فى عنقها حبل من ليف للتنكيل بها.',NULL,'934',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5808,'<span data-type="title" id=toc-5920>١ </span>- قل - يا محمد - لمن قالوا مستهزئين: صف لنا ربك: هو الله أحد لا سواه، ولا شريك له.',NULL,'935',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5809,'<span data-type="title" id=toc-5921>٢ </span>- الله المقصود - وحده - فى الحوائج والمطالب.',NULL,'935',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5810,'<span data-type="title" id=toc-5922>٣ </span>-، ٤ - لم يتخذ ولدا، ولم يولد من أب أو أم، ولم يكن له أحد شبيها أو نظيرا، وليس كمثله شئ.',NULL,'935',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5811,'<span data-type="title" id=toc-5924>١ </span>- قل أعتصم برب الصبح الذى ينجلى الليل عنه.',NULL,'936',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5812,'<span data-type="title" id=toc-5925>٢ </span>- من شر كل ذى شر من المخلوقات التى لا يدفع شرها إلا مالك أمرها.',NULL,'936',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5813,'<span data-type="title" id=toc-5926>٣ </span>- ومن شر الليل إذا اشتد ظلامه.',NULL,'936',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5814,'<span data-type="title" id=toc-5927>٤ </span>- ومن شر من يسعى بين الناس بالإفساد باستخدام السحر.',NULL,'936',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5815,'<span data-type="title" id=toc-5928>٥ </span>- ومن شر حاسد يتمنى زوال النعمة عن غيره.',NULL,'936',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5816,'<span data-type="title" id=toc-5930>١ </span>- قل: أعتصم برب<span data-type="title" id=toc-5929> الناس </span>ومدبِّر شئونهم.',NULL,'937',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5817,'<span data-type="title" id=toc-5931>٢ </span>- مالك الناس ملكاً تاماً حاكمين أو محكومين.',NULL,'937',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5818,'<span data-type="title" id=toc-5932>٣ </span>- إله الناس القادر على التصرف الكامل فيهم.',NULL,'937',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5819,'<span data-type="title" id=toc-5933>٤ </span>- من شر الموسوس للناس الذى يمتنع إذا استعنت عليه بالله.',NULL,'937',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5820,'<span data-type="title" id=toc-5934>٥ </span>- الذى يُلقى - فى خفية - فى صدور الناس ما يصرفها عن سبيل الرشاد.',NULL,'937',NULL,NULL,'0');
INSERT INTO [b836] VALUES (5821,'<span data-type="title" id=toc-5935>٦ </span>- من الجن والإنس.',NULL,'937',NULL,NULL,'0');
