-- Insert data for b8601
INSERT INTO [b8601] VALUES (1,'شرح السنة
للإمام أبي محمد الحسن بن علي بن خلف البربهاري
المتوفى سنة ٣٢٩ هـ',NULL,'33',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (2,'الحمد لله الذي هدانا للإسلام ومنَّ علينا به، وأخرجنا في خير أمة، فنسأله التوفيق لما يحب ويرضى، والحفظ مما يكره ويسخط.
[١] اعلموا أن الإسلام هو السنة، والسنة هي الإسلام، ولا يقوم أحدهما إلا بالآخر.
[٢] فمن السنة لزوم الجماعة، فمن رغب عن الجماعة وفارقها فقد خلع ربقة الإسلام من عنقه، وكان ضالا مضلا.
[٣] والأساس الذي تبنى عليه الجماعة، وهم أصحاب محمد صلى الله عليه وسلم ورحمهم الله أجمعين، وهم أهل السنة والجماعة، فمن لم يأخذ',NULL,'35',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (3,'عنهم فقد ضل وابتدع، وكل بدعة ضلالة، والضلالة وأهلها في النار.
وقال عمر بن الخطاب رضي الله عنه: " لا عذر لأحد في ضلالة ركبها حسبها هدى، ولا في هدى تركه حسبه ضلالة، فقد بينت الأمور، وثبتت الحجة، وانقطع العذر ".
وذلك أن السنة والجماعة قد أحكما أمر الدين كله، وتبين للناس، فعلى الناس الاتباع.
[٤] واعلم - رحمك الله - أن الدين إنما جاء من قبل الله تبارك وتعالى، لم يوضع على عقول الرجال وآرائهم، وعلمه عند الله وعند رسوله، فلا تتبع شيئا بهواك، فتمرق من الدين، فتخرج من',NULL,'36',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (4,'الإسلام، فإنه لا حجة لك، فقد بين رسول الله صلى الله عليه وسلم لأمته السنة، وأوضحها لأصحابه وهم الجماعة، وهم السواد الأعظم، والسواد الأعظم: الحق وأهله، فمن خالف أصحاب رسول الله صلى الله عليه وسلم في شيء من أمر الدين فقد كفر.
[٥] واعلم أن الناس لم يبتدعوا بدعة قط حتى تركوا من السنة مثلها، فاحذر المحدثات من الأمور، فإن كل محدثة بدعة، وكل بدعة ضلالة، والضلالة وأهلها في النار.
[٦] واحذر صغار المحدثات من الأمور؛ فإن صغير البدع',NULL,'37',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (5,'يعود حتى يصير كبيرا، وكذلك كل بدعة أحدثت في هذه الأمة، كان أولها صغيرا يشبه الحق، فاغتر بذلك من دخل فيها، ثم لم يستطع الخروج منها، فعظمت وصارت دينا يدان [به] فخالف الصراط المستقيم، فخرج من الإسلام، فانظر رحمك الله كل من سمعت كلامه من أهل زمانك [خاصة] فلا تعجلن، ولا تدخلن في شيء [منه] حتى تسأل وتنظر هل تكلم به أصحاب رسول الله صلى الله عليه وسلم [أو أحد من العلماء؟] فإن وجدت فيه أثرا عنهم فتمسك به، ولا تجاوزه لشيء، ولا [تختر] عليه شيئا فتسقط في النار.
[٧] واعلم أن الخروج من الطريق على وجهين؛ أما أحدهما: فرجل زل عن الطريق، وهو لا يريد إلا الخير، فلا يُقتدى بزلته،',NULL,'38',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (6,'فإنه هالك، وآخر عاند الحق وخالف من كان قبله من المتقين، فهو ضال مضل، شيطان مريد في هذه الأمة، حقيق على من يعرفه أن يحذر الناس منه، ويبين لهم قصته؛ لئلا يقع أحد في بدعته فيهلك.
[٨] واعلم رحمك الله أنه لا يتم إسلام عبد حتى يكون متبعا مصدقا مسلما، فمن زعم أنه بقي شيء من أمر الإسلام لم يكفوناه أصحاب محمد صلى الله عليه وسلم فقد كذَّبهم، وكفى به فرقة وطعنا عليهم، وهو مبتدع ضال مضل، محدث في الإسلام ما ليس منه.
[٩] واعلم رحمك الله أنه ليس في السنة قياس، ولا يُضرب لها الأمثال، ولا تتبع فيها الأهواء، وهو التصديق بآثار رسول الله صلى الله عليه وسلم بلا كيف ولا شرح، ولا يقال: لم ولا كيف؟ .
[١٠] والكلام والخصومة والجدال والمراء محدث يقدح الشك في القلب، وإن أصاب صاحبه الحق والسنة.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (7,'[١١] واعلم - رحمك الله - أن الكلام في الرب محدث، وهو بدعة وضلالة، ولا يتكلم في الرب إلا بما وصف به نفسه في القرآن، وما بين رسول الله صلى الله عليه وسلم لأصحابه، وهو - جل ثناؤه- واحد {لَيْسَ كَمِثْلِهِ شَيْءٌ وَهُوَ السَّمِيعُ الْبَصِيرُ} ربنا أول بلا متى، وآخر بلا منتهى، يعلم السر وأخفى، وهو على عرشه استوى، وعلمه بكل',NULL,'40',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (8,'مكان، ولا يخلو من علمه مكان.
[١٢] ولا يقول في صفات الرب: كيف؟ ولم؟ إلا شاك في الله.
[١٣] والقرآن كلام الله وتنزيله ونوره، ليس بمخلوق؛ لأن القرآن من الله، وما كان من الله فليس بمخلوق، وهكذا قال مالك بن أنس وأحمد بن حنبل والفقهاء قبلهما',NULL,'41',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (9,'وبعدهما، والمراء فيه كفر.
[١٤] والإيمان بالرؤية يوم القيامة، يرون الله بأبصار رؤوسهم، وهو يحاسبهم بلا حجاب ولا ترجمان.
[١٥] والإيمان بالميزان يوم القيامة، يوزن فيه الخير والشر، له كفتان ولسان.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (10,'[١٦] والإيمان بعذاب القبر، ومنكر ونكير.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (11,'[١٧] والإيمان بحوض رسول الله صلى الله عليه وسلم، ولكل نبي حوض، إلا صالح النبي عليه السلام؛ فإن حوضه ضرع ناقته.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (12,'[١٨] والإيمان بشفاعة رسول الله صلى الله عليه وسلم للمذنبين الخاطئين؛ في يوم القيامة، وعلى الصراط، ويخرجهم من جوف',NULL,'45',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (13,'جهنم، وما من نبي إلا له شفاعة، وكذلك [الصديقون] والشهداء [والصالحون] ، ولله بعد ذلك تفضل كثير فيمن يشاء، والخروج من النار بعدما احترقوا وصاروا فحما.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (14,'[١٩] والإيمان بالصراط على جهنم، يأخذ الصراط من شاء الله، ويجوز من شاء الله، ويسقط في جهنم من شاء الله، ولهم أنوار على قدر إيمانهم.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (15,'[٢٠] والإيمان بالأنبياء والملائكة.
[٢١] والإيمان بأن الجنة حق والنار حق، والجنة والنار مخلوقتان، الجنة في السماء السابعة، وسقفها العرش، والنار تحت [الأرض] السابعة السفلى، وهما مخلوقتان، قد علم الله',NULL,'48',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (16,'عدد أهل الجنة ومن يدخلها، وعدد أهل النار ومن يدخلها، لا تفنيان أبدا، هما مع بقاء الله تبارك وتعالى [أبد] الآبدين، في دهر الداهرين، وآدم كان في الجنة الباقية المخلوقة، فأخرج منها بعدما عصى الله.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (17,'[٢٢] والإيمان بالمسيح الدجال.
[٢٣] وبنزول عيسى ابن مريم، ينزل فيقتل الدجال',NULL,'50',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (18,'ويتزوج، ويصلي خلف القائم من آل محمد صلى الله عليه وسلم، ويموت، ويدفنه',NULL,'51',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (19,'المسلمون.
[٢٤] والإيمان بأن الإيمان قول وعمل، وعمل وقول، ونية وإصابة، يزيد وينقص، يزيد ما شاء الله وينقص حتى لا يبقى منه شيء.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (20,'[٢٥] و<span data-type="title" id=toc-4>خير هذه الأمة بعد وفاة نبيها: </span>أبو بكر وعمر وعثمان، هكذا روي لنا عن ابن عمر؛ قال: كنا نقول ورسول الله صلى الله عليه وسلم بين أظهرنا: إن خير الناس بعد رسول الله صلى الله عليه وسلم: أبو بكر وعمر وعثمان ويسمع النبي صلى الله عليه وسلم بذلك فلا ينكره.
ثم أفضل الناس بعد هؤلاء: علي وطلحة والزبير وسعد وسعيد وعبد الرحمن بن عوف [وأبو عبيدة بن الجراح] ، وكلهم',NULL,'53',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (21,'يصلح للخلافة.
ثم أفضل الناس بعد هؤلاء: أصحاب رسول الله صلى الله عليه وسلم، القرن الأول الذي بعث فيهم: المهاجرون الأولون والأنصار، وهم من صلى القبلتين.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (22,'ثم أفضل الناس بعد هؤلاء: من صحب رسول الله صلى الله عليه وسلم يوما أو شهرا أو سنة أقل أو كثر، ترحم عليهم وتذكر فضله وتكف عن زلته، ولا تذكر أحدا منهم إلا بخير، لقول رسول الله صلى الله عليه وسلم: «إذا ذكر أصحابي فأمسكوا» .
وقال ابن عيينة: " من نطق في أصحاب رسول الله صلى الله عليه وسلم بكلمة فهو صاحب هوى ".',NULL,'55',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (23,'[وقال النبي صلى الله عليه وسلم: «أصحابي كالنجوم، بأيهم اقتديتم اهتديتم» ] .
[٢٦] والسمع والطاعة للأئمة فيما يحب الله ويرضى.
ومن ولي الخلافة بإجماع الناس عليه ورضاهم به فهو أمير المؤمنين، لا يحل لأحد أن يبيت ليلة ولا يرى أن عليه إماما، برا كان أو فاجرا.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (24,'[٢٧] والحج والغزو مع الإمام ماض، وصلاة الجمعة خلفهم جائزة، ويصلي بعدها ست ركعات، يفصل بين كل ركعتين، هكذا قال أحمد بن حنبل.
[٢٨] والخلافة في قريش إلى أن ينزل عيسى ابن مريم.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (25,'[٢٩] ومن خرج على إمام من أئمة المسلمين فهو خارجي، وقد شق عصا المسلمين، وخالف الآثار، وميتته ميتة جاهلية.
[٣٠] ولا يحل قتال السلطان والخروج عليه وإن جاروا، وذلك قول رسول الله صلى الله عليه وسلم لأبي ذر: «اصبر، وإن كان عبدا حبشيا» .
وقوله للأنصار: «اصبروا حتى تلقوني على [الحوض] » .
وليس من السنة قتال السلطان؛ فإن فيه فساد الدين والدنيا.
[٣١] ويحل قتال الخوارج إذا عرضوا للمسلمين في أنفسهم',NULL,'58',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (26,'وأموالهم وأهاليهم، وليس له إذا فارقوه أن يطلبهم، ولا [يجهز] على جريحهم ولا يأخذ فيئهم، ولا يقتل أسيرهم، ولا يتبع مدبرهم.
[٣٢] واعلم – رحمك الله - أنه لا طاعة لبشر في معصية الله عز وجل. من كان من أهل الإسلام، ولا يشهد على أحد ولا يشهد له بعمل خير ولا شر، فإنك لا تدري بما يختم له، ترجو',NULL,'59',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (27,'له، وتخاف عليه ولا تدري ما يسبق له عند الموت إلى الله من الندم، وما أحدث الله في ذلك الوقت إذا مات على الإسلام ترجو له رحمة الله، وتخاف عليه ذنوبه، وما من ذنب إلا وللعبد منه توبة.
[٣٣] والرجم حق.
[٣٤] والمسح على الخفين سنة.
[٣٥] وتقصير الصلاة في السفر سنة.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (28,'[٣٦] والصوم في السفر؛ من شاء صام ومن شاء أفطر.
[٣٧] ولا بأس بالصلاة في السراويل.
[٣٨] والنفاق أن تظهر الإسلام باللسان وتخفي الكفر.
[٣٩] واعلم أن الدنيا دار إيمان وإسلام، فأمة محمد صلى الله عليه وسلم فيها مؤمنون مسلمون في أحكامهم ومواريثهم [وذبائحهم] والصلاة عليهم، لا تشهد لأحد بحقيقة الإيمان حتى يأتي بجميع شرائع',NULL,'61',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (29,'الإسلام، فإن قصر في شيء من ذلك كان ناقص الإيمان حتى يموت، وعلم إيمانه إلى الله تعالى: تام الإيمان أو ناقص الإيمان، إلا ما ظهر لك من تضييع شرائع الإسلام.
[٤٠] والصلاة على من مات من أهل القبلة سنة: المرجوم، والزاني، والزانية، والذي يقتل',NULL,'62',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (30,'نفسه، وغيرهم من أهل القبلة، والسكران وغيره، الصلاة عليهم سنة.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (31,'[٤١] ولا نخرج أحدا من أهل القبلة من الإسلام حتى يرد آية من كتاب الله، أو يرد شيئا من آثار رسول الله صلى الله عليه وسلم، أو يذبح لغير الله، أو يصلي لغير الله، فإذا فعل شيئا من ذلك فقد وجب عليك أن تخرجه من الإسلام، وإذا لم يفعل شيئا من ذلك فهو مؤمن مسلم بالاسم لا بالحقيقة.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (32,'[٤٢] وكل ما سمعت من الآثار شيئا مما لم يبلغه عقلك، نحو قول رسول الله صلى الله عليه وسلم: «قلوب العباد بين إصبعين من أصابع الرحمن» .
وقوله: «إن الله تبارك وتعالى ينزل إلى سماء الدنيا» .
وينزل يوم عرفة',NULL,'65',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (33,'ويوم القيامة.
وأن جهنم لا تزال يُطرح فيها حتى يضع عليها قدمه جل ثناؤه.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (34,'وقول الله تعالى للعبد: «إن مشيت إليّ هرولت إليك» .
وقوله: «إن الله تبارك وتعالى ينزل يوم القيامة» .
وقوله: «إن الله خلق آدم على صورته» .
وقول النبي صلى الله عليه وسلم: «إني رأيت ربي في أحسن صورة» . وأشباه',NULL,'67',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (35,'هذه الأحاديث، فعليك بالتسليم والتصديق والتفويض والرضى، ولا تفسر شيئا [من هذه] بهواك، فإن الإيمان بهذا واجب، فمن فسر شيئا من هذا بهواه أو رده فهو جهمي.
[٤٣] ومن زعم أنه يرى ربه في دار الدنيا فهو كافر بالله.
[٤٤] والفكرة في الله تبارك وتعالى بدعة؛ لقول رسول الله صلى الله عليه وسلم: «تفكروا في الخلق ولا تفكروا في الله» . فإن الفكرة في الرب تقدح الشك في القلب.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (36,'[٤٥] واعلم أن الهوام والسباع والدواب كلها، نحو الذر [والذباب] والنمل كلها مأمورة، لا يعملون شيئا إلا بإذن الله تبارك وتعالى.
[٤٦] والإيمان بأن الله تبارك وتعالى قد علم ما كان من أول الدهر، وما لم يكن مما هو كائن، أحصاه وعده عدا، ومن قال: إنه لا يعلم ما كان وما هو كائن فقد كفر بالله العظيم.
[٤٧] ( «ولا نكاح إلا بولي وشاهدي عدل» ) وصداق',NULL,'69',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (37,'قل أو كثر، ومن لم يكن لها ولي فالسلطان ولي من لا ولي له.
[٤٨] وإذا طلق الرجل امرأته ثلاثا فقد حرمت عليه، ولا تحل له حتى تنكح زوجا غيره.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (38,'[٤٩] ولا يحل دم امرئ مسلم يشهد أن لا إله إلا الله ويشهد أن محمدا رسول الله عبده ورسوله إلا بإحدى ثلاث: زان بعد إحصان، أو مرتد بعد إيمان، أو قتل نفسا مؤمنة [بغير حق] فيقتل به، وما سوى ذلك فدم المسلم على المسلم حرام [أبدا] حتى تقوم الساعة.
[٥٠] وكل شيء مما أوجب الله عليه الفناء يفنى، إلا الجنة والنار والعرش والكرسي واللوح والقلم والصور، ليس يفنى شيء من هذا أبدا،',NULL,'71',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (39,'ثم يبعث الله الخلق على ما ماتوا عليه يوم القيامة، فيحاسبهم بما شاء، فريق في الجنة وفريق في السعير، ويقول لسائر الخلق [ممن لم يخلق للبقاء] كونوا ترابا.
[٥١] والإيمان بالقصاص يوم القيامة بين الخلق كلهم، بني آدم والسباع والهوام حتى للذرة من الذرة، حتى يأخذ الله لبعضهم من بعض؛ لأهل الجنة من أهل النار، وأهل النار من أهل الجنة وأهل الجنة بعضهم من بعض وأهل النار بعضهم من بعض.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (40,'[٥٢] وإخلاص العمل لله.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (41,'[٥٣] والرضا بقضاء الله.
[٥٤] والصبر على حكم الله.
[٥٥] والإيمان بما قال الله عز وجل.
[٥٦] والإيمان بأقدار الله كلها، خيرها وشرها، وحلوها ومرها، قد علم الله ما العباد عاملون، وإلى ما هم صائرون، لا يخرجون من علم الله، ولا يكون في الأرضين ولا في السماوات إلا ما علم الله عز وجل.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (42,'[٥٧] وتعلم أن ما أصابك لم يكن ليخطئك، وما أخطأك لم يكن ليصيبك.
[٥٨] ولا خالق مع الله.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (43,'[٥٩] والتكبير على الجنائز أربع، وهو قول مالك بن أنس وسفيان الثوري والحسن بن صالح وأحمد بن',NULL,'76',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (44,'حنبل والفقهاء، وهكذا قال رسول الله صلى الله عليه وسلم.
[٦٠] والإيمان بأن مع كل قطرة ملك ينزل من السماء حتى يضعها حيث أمره الله عز وجل.
[٦١] والإيمان بأن النبي صلى الله عليه وسلم حين كلم أهل القليب يوم بدر، أن المشركين [كانوا] يسمعون كلامه.
[٦٢] والإيمان بأن الرجل إذا مرض يأجره الله على مرضه.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (45,'[٦٣] والشهيد يأجره الله على القتل.
[٦٤] والإيمان بأن الأطفال إذا أصابهم شيء في دار الدنيا يألمون، وذلك أن بكر ابن أخت عبد الواحد قال: لا يألمون، وكذب.
[٦٥] واعلم أنه لا يدخل الجنة أحد إلا برحمة الله، ولا',NULL,'78',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (46,'يعذب الله أحدا إلا بذنوبه، بقدر ذنوبه، ولو عذب الله أهل السماوات وأهل الأرضين برهم وفاجرهم، عذبهم غير ظالم لهم، لا يجوز أن يقال لله تبارك وتعالى: إنه يظلم، وإنما يظلم من يأخذ ما ليس له، والله جل ثناؤه له الخلق والأمر، الخلق خلقه، والدار داره، لا يسأل عما يفعل بخلقه، ولا يقال: لم وكيف؟ لا يدخل أحد بين الله وبين خلقه.
[٦٦] وإذا سمعت الرجل يطعن على الآثار [ولا يقبلها أو ينكر شيئا من أخبار رسول الله صلى الله عليه وسلم] فاتهمه على الإسلام؛ فإنه رجل رديء القول والمذهب، وإنما طعن على رسول الله صلى الله عليه وسلم وأصحابه؛ لأنه إنما عرفنا الله وعرفنا رسول الله صلى الله عليه وسلم وعرفنا القرآن وعرفنا الخير والشر والدنيا',NULL,'79',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (47,'والآخرة بالآثار، فإن القرآن إلى السنة أحوج من السنة إلى القرآن.
[٦٧] والكلام والجدل والخصومة في القدر خاصة منهي عنه [عند] جميع الفرق؛ لأن القدر سر الله ونهى الرب تبارك',NULL,'80',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (48,'وتعالى الأنبياء عن الكلام في القدر، ونهى رسول الله صلى الله عليه وسلم عن الخصومة في القدر، وكرهه العلماء وأهل الورع ونهوا عن الجدال في القدر، فعليك بالتسليم والإقرار والإيمان، واعتقاد ما قال رسول الله صلى الله عليه وسلم في جملة الأشياء وتسكت عما سوى ذلك.
[٦٨] والإيمان بأن رسول الله صلى الله عليه وسلم أسري به إلى السماء وصار إلى العرش وكلمه الله تبارك وتعالى، ودخل الجنة واطلع إلى النار ورأى الملائكة ونشرت له الأنبياء، ورأى سرادقات العرش والكرسي وجميع ما في السماوات وما في الأرضين في اليقظة، حمله جبريل على البراق حتى أداره في السماوات، وفرضت عليه الصلاة في تلك الليلة، ورجع إلى مكة في تلك الليلة، وذلك قبل الهجرة.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (49,'[٦٩] واعلم أن أرواح الشهداء في قناديل تحت العرش تسرح في الجنة، وأرواح المؤمنين تحت العرش، وأرواح الكفار والفجار في برهوت، [وهي في سجين] .',NULL,'82',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (50,'[٧٠] والإيمان بأن الميت يقعد في قبره، وترسل فيه الروح حتى يسأله منكر ونكير عن الإيمان وشرائعه، ثم يسل روحه بلا ألم.
[٧١] ويعرف الميت الزائر إذا أتاه، ويتنعم في القبر المؤمن',NULL,'83',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (51,'ويعذب الفاجر كيف شاء الله.
[٧٢] واعلم أن [الشر والخير] بقصاء الله وقدره.
[٧٣] والإيمان بأن الله تبارك وتعالى هو الذي كلم موسى بن عمران يوم الطور وموسى يسمع من الله الكلام بصوت وقع في مسامعه منه لا من غيره، فمن قال غير هذا فقد كفر.
[٧٤] والعقل مولود، أعطي كل إنسان من العقل ما أراد الله',NULL,'84',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (52,'يتفاوتون في العقول مثل الذرة في السماوات، ويطلب من كل إنسان من العمل على قدر ما أعطاه من العقل، وليس العقل باكتساب، إنما هو فضل من الله تبارك وتعالى.
[٧٥] واعلم أن الله فضل العباد بعضهم على بعض في الدين والدنيا عدل منه، لا يقال: جار ولا حابى، فمن قال: إن فضل الله على المؤمن والكافر سواء فهو صاحب بدعة، بل فضل الله المؤمنين على الكافرين. والطائع على العاصي، والمعصوم على المخذول، عدل منه، هو فضله يعطي من يشاء ويمنع من يشاء.
[٧٦] ولا يحل أن تكتم النصيحة للمسلمين، برهم وفاجرهم في أمر الدين، فمن كتم فقد غش المسلمين، ومن غش المسلمين فقد غش الدين، ومن غش الدين فقد خان الله ورسوله والمؤمنين.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (53,'[٧٧] والله تبارك وتعالى سميع بصير، سميع عليم، يداه مبسوطتان، قد علم الله أن الخلق يعصونه قبل أن يخلقهم، علمه نافذ فيهم، فلم يمنعه علمه فيهم أن هداهم للإسلام، ومن به عليهم كرما وجودا وتفضلا فله الحمد.
[٧٨] واعلم أن البشارة عند الموت ثلاث بشارات؛ يقال: أبشر يا حبيب الله برضى الله والجنة، ويقال: أبشر يا عدو الله بغضب الله والنار، ويقال: أبشر يا عبد الله بالجنة بعد [الانتقام] . هذا قول ابن عباس.
[٧٩] واعلم أن أول من ينظر إلى الله في الجنة الأضراء، ثم الرجال، ثم النساء، بأعين رؤوسهم، كما قال رسول الله صلى الله عليه وسلم: «سترون»',NULL,'86',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (54,'«ربكم كما ترون القمر ليلة البدر، لا تضامون في رؤيته» ، والإيمان بهذا واجب وإنكاره كفر.
[٨٠] واعلم - رحمك الله- أنها ما كانت زندقة قط، ولا كفر ولا شك ولا بدعة ولا ضلالة ولا حيرة في الدين إلا من الكلام وأصحاب الكلام والجدل والمراء والخصومة، والعجب وكيف يجترئ الرجل على المراء والخصومة والجدال، والله تعالى يقول: {مَا يُجَادِلُ فِي آيَاتِ اللَّهِ إِلَّا الَّذِينَ كَفَرُوا} فعليك بالتسليم والرضى بالآثار وأهل الآثار، والكف والسكوت.
[٨١] والإيمان بأن الله – تبارك وتعالى - يعذب الخلق في النار في الأغلال والأنكال والسلاسل، والنار في أجوافهم وفوقهم وتحتهم،',NULL,'87',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (55,'وذلك أن الجهمية - منهم هشام الفوطي - قال: [إنما] يعذب عند النار، رد على الله وعلى رسوله.
[٨٢] واعلم أن الصلاة الفريضة خمس، لا يزاد فيهن ولا ينقص في مواقيتها، وفي السفر [ركعتان] إلا المغرب، فمن قال: أكثر من خمس، فقد ابتدع، ومن قال: أقل من خمس فقد ابتدع، لا يقبل الله شيئا منها إلا لوقتها، إلا أن يكون نسيان فإنه معذور، يأتي بها إذا ذكرها،',NULL,'88',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (56,'أو يكون مسافرا فيجمع بين الصلاتين إن شاء.
[٨٣] والزكاة من الذهب والفضة والتمر والحبوب والدواب، على ما قال رسول الله صلى الله عليه وسلم فإن قسمها فجائز، وإن أعطاها الإمام فجائز.
[٨٤] واعلم أن أول الإسلام شهادة أن لا إله إلا الله وأن محمدا عبده ورسوله.
[٨٥] وأن ما قال الله كما قال، ولا خلف لما قال، وهو عند ما قال.
[٨٦] والإيمان بالشرائع كلها.
[٨٧] واعلم أن الشراء والبيع ما بيع في أسواق المسلمين حلال ما بيع على حكم الكتاب والإسلام والسنة، من غير أن يدخله تغيير أو ظلم أو جور أو خلاف للقرآن أو خلاف للعلم.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (57,'[٨٨] واعلم – رحمك الله - أنه ينبغي للعبد أن تصحبه الشفقة أبدا ما صحب الدنيا؛ لأنه لا يدري على ما يموت، وبما يختم له، وعلى ما يلقى الله، وإن عمل كل عمل من الخير، وينبغي للرجل المسرف على نفسه أن لا يقطع رجاءه من الله تعالى عند الموت، ويحسن ظنه بالله تبارك وتعالى ويخاف ذنوبه، فإن رحمه الله فبفضل، وإن عذبه فبذنب.
[٨٩] والإيمان بأن الله تبارك وتعالى أطلع نبيه على ما يكون في أمته إلى يوم القيامة.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (58,'[٩٠] واعلم أن رسول الله صلى الله عليه وسلم قال: «ستفترق أمتي على ثلاث وسبعين فرقة كلها في النار إلا واحدة، وهي الجماعة، قيل: يا رسول الله من هم؟ قال: ما أنا عليه اليوم وأصحابي» .
وهكذا كان الدين إلى خلافة عمر وهكذا كان في زمن عثمان، فلما قتل عثمان جاء الاختلاف والبدع، وصار الناس أحزابا وصاروا فرقا، فمن الناس من ثبت على الحق عند أول التغيير، وقال به ودعا الناس إليه، فكان الأمر مستقيما حتى كانت الطبقة الرابعة في خلافة بني فلان انقلب الزمان وتغير الناس جدا، وفشت البدع، وكثرت الدعاة إلى غير سبيل الحق والجماعة، ووقعت المحن في شيء لم يتكلم به رسول الله صلى الله عليه وسلم، ولا أصحابه، ودعوا إلى الفرقة [ونهى]',NULL,'91',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (59,'رسول الله عن الفرقة، وكفر بعضهم بعضا، وكل [داع] إلى رأيه، وإلى تكفير من خالفه فضل [الجهال] والرعاع ومن لا علم له، وأطمعوا الناس في شيء من أمر الدنيا وخوفوهم عقاب الدنيا، فاتبعهم الخلق على خوف [في] دنياهم ورغبة في دنياهم، فصارت السنة وأهلها مكتومين، وظهرت البدعة وفشت، وكفروا من حيث لا يعلمون من وجوه شتى، ووضعوا القياس، وحملوا قدرة الرب في آياته وأحكامه وأمره ونهيه على عقولهم [وآرائهم] ، فما وافق عقولهم قبلوه وما لم يوافق عقولهم ردوه، فصار الإسلام غريبا، والسنة غريبة، وأهل السنة غرباء في [جوف ديارهم] .
[٩١] واعلم أن المتعة - متعة النساء - والاستحلال حرام إلى يوم القيامة.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (60,'[٩٢] واعرف لبني هاشم فضلهم؛ لقرابتهم من رسول الله صلى الله عليه وسلم وتعرف فضل قريش والعرب وجميع الأفخاذ، فاعرف قدرهم [وحقوقهم] في الإسلام، ومولى القوم منهم، وتعرف لسائر الناس',NULL,'93',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (61,'حقهم في الإسلام و [تعرف فضل] الأنصار، ووصية رسول الله صلى الله عليه وسلم فيهم، وآل الرسول فلا تنساهم، تعرف فضلهم، وجيرانه من أهل المدينة، فاعرف فضلهم.
[٩٣] واعلم – رحمك الله - أن أهل العلم لم يزالوا يردون قول الجهمية حتى كان في خلافة بني فلان تكلم الرويبضة في أمر العامة،',NULL,'94',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (62,'وطعنوا على آثار رسول الله صلى الله عليه وسلم، وأخذوا بالقياس والرأي، وكفروا من خالفهم، فدخل في قولهم الجاهل والمغفل والذي لا علم له، حتى كفروا من حيث لا يعلمون، فهلكت الأمة من وجوه، وكفرت من وجوه، وتزندقت من وجوه، وضلت من وجوه، [وتفرقت] وابتدعت من وجوه، إلا من ثبت على قول رسول الله صلى الله عليه وسلم وأمره وأمر أصحابه، ولم يخطئ أحدا منهم، ولم [يجاوز] أمرهم، ووسعه ما وسعهم، ولم يرغب عن طريقتهم ومذهبهم، وعلم أنهم كانوا على الإسلام الصحيح والإيمان الصحيح، فقلدهم دينه [واستراح] ، وعلم أن الدين إنما هو بالتقليد، والتقليد لأصحاب محمد صلى الله عليه وسلم.
[٩٤] واعلم أن من قال: لفظي بالقرآن مخلوق، فهو مبتدع، ومن سكت فلم يقل: مخلوق ولا غير مخلوق، فهو جهمي.
هكذا قال أحمد بن حنبل وقال رسول الله صلى الله عليه وسلم: «من يعش منكم بعدي فسيرى اختلافا كثيرا، فإياكم ومحدثات الأمور، فإنها ضلالة، وعليكم بسنتي»',NULL,'95',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (63,'«وسنة الخلفاء الراشدين المهديين، وعضوا عليها بالنواجذ» .
[٩٥] واعلم أنه إنما جاء هلاك الجهمية أنهم [فكروا] في الرب، فأدخلوا لم وكيف، وتركوا الأثر، ووضعوا القياس، وقاسوا الدين على رأيهم فجاءوا بالكفر عيانا لا يخفي أنه كفر، وأكفروا الخلق واضطرهم الأمر حتى قالوا بالتعطيل.
وقال بعض العلماء - منهم أحمد بن حنبل رضي الله عنه -: الجهمي كافر، ليس من أهل القبلة، حلال الدم، لا يرث ولا يورث؛ لأنه قال: لا جمعة ولا جماعة، [ولا عيدين] ولا صدقة، وقالوا: إن من لم يقل: القرآن مخلوق فهو كافر، واستحلوا السيف على أمة محمد صلى الله عليه وسلم، وخالفوا من كان قبلهم، وامتحنوا الناس بشيء لم يتكلم فيه رسول الله صلى الله عليه وسلم، ولا أحد من أصحابه، وأرادوا تعطيل المساجد والجوامع، وأوهنوا الإسلام، وعطلوا الجهاد، وعملوا في',NULL,'96',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (64,'الفرقة، وخالفوا الآثار، وتكلموا بالمنسوخ، واحتجوا بالمتشابه، فشككوا الناس في آرائهم وأديانهم، واختصموا في ربهم، وقالوا: ليس عذاب قبر، ولا حوض ولا شفاعة، والجنة والنار لم يخلقا، وأنكروا كثيرا مما قال رسول الله صلى الله عليه وسلم، فاستحل من استحل تكفيرهم ودماءهم من هذا الوجه؛ لأن من رد آية من كتاب الله فقد رد الكتاب كله، ومن رد أثرا عن رسول الله صلى الله عليه وسلم فقد رد الأثر كله، وهو كافر بالله العظيم، فدامت لهم المدة، ووجدوا من السلطان معونة على ذلك، ووضعوا السيف والسوط دون ذلك، فدرس علم السنة والجماعة [وأوهنوهما] وصارتا مكتومين؛ لإظهار البدع والكلام فيها ولكثرتهم، واتخذوا المجالس، وأظهروا رأيهم، ووضعوا فيها الكتب، وأطمعوا الناس، وطلبوا لهم الرياسة، فكانت فتنة عظيمة لم ينج منها إلا من عصم الله، فأدنى ما كان يصيب الرجل من مجالستهم أن يشك في دينه، أو يتابعهم أو يزعم أنهم على الحق، ولا يدري أنه على الحق أو على الباطل، فصار شاكا، فهلك الخلق، حتى كان أيام جعفر - الذي يقال له المتوكل - فأطفأ الله به البدع، وأظهر به',NULL,'97',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (65,'الحق، وأظهر به أهل السنة، وطالت ألسنتهم، مع قلتهم وكثرة أهل البدع إلى يومنا [هذا] والرسم وأعلام الضلالة قد بقي قوم يعملون بها، ويدعون إليها، لا مانع يمنعهم، ولا أحد يحجزهم عما يقولون ويعملون.
[٩٦] واعلم أنه لم تجئ بدعة قط إلا من الهمج الرعاع أتباع كل ناعق يميلون مع كل ريح، فمن كان هكذا فلا دين له، قال الله تبارك وتعالى: {فَمَا اخْتَلَفُوا إِلَّا مِنْ بَعْدِ مَا جَاءَهُمُ الْعِلْمُ بَغْيًا بَيْنَهُمْ} ، وقال: {وَمَا تَفَرَّقُوا إِلَّا مِنْ بَعْدِ مَا جَاءَهُمُ الْعِلْمُ بَغْيًا بَيْنَهُمْ} وقال: {وَمَا اخْتَلَفَ فِيهِ إِلَّا الَّذِينَ أُوتُوهُ مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ بَغْيًا بَيْنَهُمْ} وهم علماء السوء، أصحاب الطمع والبدع.
[٩٧] واعلم أنه لا يزال الناس في عصابة من أهل الحق والسنة، يهديهم الله ويهدي بهم غيرهم، ويحيي بهم السنن، فهم الذين وصفهم الله مع قلتهم عند الاختلاف، وقال: {الَّذِينَ أُوتُوهُ مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ بَغْيًا بَيْنَهُمْ} فاستثناهم فقال: {فَهَدَى اللَّهُ الَّذِينَ آمَنُوا لِمَا اخْتَلَفُوا فِيهِ مِنَ الْحَقِّ بِإِذْنِهِ وَاللَّهُ يَهْدِي مَنْ يَشَاءُ إِلَى صِرَاطٍ مُسْتَقِيمٍ} .',NULL,'98',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (66,'وقال رسول الله صلى الله عليه وسلم: «لا [تزال عصابة] من أمتي ظاهرين على الحق لا يضرهم من خذلهم حتى يأتي أمر الله» .
[٩٨] واعلم – رحمك الله - أن العلم ليس بكثرة الرواية [والكتب] ، إنما العالم من اتبع العلم والسنن، وإن كان قليل العلم [والكتب] ومن خالف الكتاب والسنة فهو صاحب بدعة، وإن كان كثير العلم [والكتب] .
[٩٩] واعلم – رحمك الله - أن من قال في دين الله برأيه وقياسه وتأويله من غير حجة من السنة والجماعة فقد قال على الله ما لا يعلم، ومن قال على الله ما لا يعلم، فهو من المتكلفين، والحق ما جاء من عند الله، والسنة سنة رسول الله صلى الله عليه وسلم، والجماعة ما اجتمع عليه',NULL,'99',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (67,'أصحاب رسول الله صلى الله عليه وسلم في خلافة أبي بكر وعمر [وعثمان] ومن اقتصر على سنة رسول الله صلى الله عليه وسلم، وما كان عليه [أصحابه و] الجماعة فلج على أهل البدع كلها، واستراح بدنه وسلم له دينه إن شاء الله؛ لأن رسول الله صلى الله عليه وسلم قال: «ستفترق أمتي» وبين لنا رسول الله صلى الله عليه وسلم الناجي منها فقال: «ما كنت أنا عليه اليوم وأصحابي» .
فهذا هو الشفاء والبيان والأمر الواضح والمنار المستنير.
وقال رسول الله صلى الله عليه وسلم: «إياكم والتعمق، وإياكم والتنطع، وعليكم بدينكم العتيق» .
[١٠٠] واعلم أن العتيق ما كان من وفاة رسول الله صلى الله عليه وسلم إلى',NULL,'100',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (68,'قتل عثمان بن عفان وكان قتله أول الفُرقة، وأول الاختلاف، فتحاربت الأمة وتفرقت واتبعت الطمع والأهواء والميل إلى الدنيا، فليس لأحد رخصة في شيء أحدثه مما لم يكن عليه أصحاب محمد رسول الله صلى الله عليه وسلم، أو يكون [رجل] يدعو إلى شيء أحدثه من قبله [أو من قبل رجل] من أهل البدع، فهو كمن أحدثه، فمن زعم ذلك أو قال به، فقد رد السنة وخالف [الحق و] الجماعة، وأباح البدع، وهو أضر على هذه الأمة من إبليس.
ومن عرف ما ترك أصحاب البدع من السنة، وما فارقوا فيه فتمسك به فهو صاحب سنة وصاحب جماعة، وحقيق أن يتبع وأن يعان، وأن يحفظ، وهو ممن أوصى به رسول الله صلى الله عليه وسلم.
[١٠١] واعلموا – رحمكم الله - أن أصول البدع أربعة أبواب،',NULL,'101',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (69,'انشعب من هذه الأربعة اثنان [وسبعون] هوًى، ثم يصير كل واحد من البدع [يتشعب] حتى تصير كلها [إلى] ألفين وثمان مائة [مقالة] ، وكلها ضلالة، وكلها في النار إلا واحدة، وهو من آمن بما في هذا الكتاب، واعتقده من غير ريبة في قلبه، ولا شكوك، فهو صاحب سنة، وهو الناجي إن شاء الله.
[١٠٢] واعلم – رحمك الله – لو أن الناس وقفوا عند محدثات الأمور ولم يتجاوزوها بشيء [ولم] يولدوا كلاما مما لم يجئ فيه أثر عن رسول الله صلى الله عليه وسلم ولا عن أصحابه لم تكن بدعة.
[١٠٣] واعلم – رحمك الله – أنه ليس بين العبد وبين أن يكون مؤمنا حتى يصير كافرا إلا أن يجحد شيئا مما أنزله الله تعالى، أو يزيد في كلام الله، أو ينقص، أو ينكر شيئا مما قال الله، أو شيئا مما تكلم به رسول الله صلى الله عليه وسلم، فاتق الله – رحمك الله – وانظر لنفسك، وإياك والغلو في الدين، فإنه ليس من طريق الحق في شيء.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (70,'[١٠٤] وجميع ما وصفت لك في هذا الكتاب، فهو عن الله، وعن رسول الله صلى الله عليه وسلم، وعن أصحابه وعن التابعين، والقرن الثالث إلى القرن الرابع، فاتق الله يا عبد الله، وعليك بالتصديق والتسليم والتفويض [والرضى] لما في هذا الكتاب، ولا تكتم هذا الكتاب أحدا من أهل القبلة، فعسى يرد الله به [حيرانا] عن حيرته، أو صاحب بدعة من بدعته، أو ضالا عن ضلالته، فينجو به.
فاتق الله، وعليك بالأمر الأول العتيق، وهو ما وصفت لك في هذا الكتاب، فرحم الله عبدا، ورحم والديه قرأ هذا الكتاب، وبثه وعمل به ودعا إليه، واحتج به، فإنه دين الله ودين رسول الله صلى الله عليه وسلم، فإنه من انتحل شيئا خلاف ما في هذا الكتاب، فإنه ليس يدين لله بدين، وقد رده كله، كما لو أن عبدا آمن بجميع ما قال الله تبارك وتعالى، إلا أنه شك في حرف فقد رد جميع ما قال الله تعالى، وهو كافر، كما أن شهادة أن لا إله إلا الله لا تقبل من صاحبها إلا بصدق النية وخالص اليقين، كذلك لا يقبل الله شيئا',NULL,'103',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (71,'من السنة في ترك بعض، ومن ترك من السنة شيئا فقد ترك السنة كلها.
فعليك بالقبول، ودع عنك المحك واللجاجة، فإنه ليس من دين الله في شيء، وزمانك خاصة زمان سوء، فاتق الله.
[١٠٥] وإذا وقعت الفتنة فالزم جوف بيتك، وفر من جوار الفتنة، وإياك والعصبية، وكل ما كان من قتال بين المسلمين على الدنيا فهو فتنة، فاتق الله وحده لا شريك له، ولا تخرج',NULL,'104',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (72,'فيها، ولا تقاتل فيها، ولا تهو، ولا تشايع، ولا تمايل، ولا تحب شيئا من أمورهم، فإنه يقال: من أحب فعال قوم - خيرا كان أو شرا - كان كمن عمله. وفقنا الله وإياكم لمرضاته، وجنبنا وإياكم معصيته.
[١٠٦] وأقل النظر في النجوم إلا ما تستعين به على مواقيت الصلاة، واله عما سوى ذلك، فإنه يدعو إلى الزندقة.
[١٠٧] وإياك والنظر في الكلام والجلوس إلى أصحاب الكلام، وعليك بالآثار، وأهل الآثار، وإياهم فاسأل، ومعهم فاجلس، ومنهم فاقتبس.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (73,'[١٠٨] واعلم أنه ما عبد الله بمثل<span data-type="title" id=toc-16> الخوف من الله</span>، وطريق الخوف والحزن والشفقات والحياء من الله تبارك وتعالى.
[١٠٩] واحذر أن تجلس مع من يدعو إلى الشوق والمحبة، ومن يخلو مع النساء وطريق المذهب، فإن هؤلاء كلهم على ضلالة.
[١١٠] واعلم – رحمك الله - أن الله – تبارك وتعالى - دعا الخلق كلهم إلى عبادته، ومن بعد ذلك على من شاء بالإسلام تفضلا منه.
[١١١] والكف عن حرب علي ومعاوية وعائشة وطلحة والزبير، ومن كان معهم، ولا تخاصم [فيهم] ، وكل أمرهم إلى الله تبارك وتعالى، فإن رسول الله صلى الله عليه وسلم قال: «إياكم وذكر أصحابي وأصهاري وأختاني» .',NULL,'106',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (74,'وقوله: «إن الله تبارك وتعالى نظر إلى أهل بدر فقال: اعملوا ما شئتم فإني قد غفرت لكم» .
[١١٢] واعلم – رحمك الله - أنه لا يحل مال امرئ مسلم إلا بطيبة من نفسه، وإن كان مع رجل [مال] حرام فقد ضمنه، لا يحل لأحد أن يأخذ منه شيئا إلا بإذنه، فإنه عسى [أن] يتوب هذا فيريد أن يرده على أربابه فأخذت حراما.
[١١٣] والمكاسب [مطلقة] ما بان لك صحته فهو مطلق إلا ما ظهر فساده، وإن كان فاسدا، يأخذ من الفساد مسيكة نفسه، لا تقول: أترك [المكاسب] وآخذ ما أعطوني، لم يفعل هذا الصحابة',NULL,'107',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (75,'ولا العلماء إلى زماننا هذا، وقال عمر رضي الله عنه: كسب فيه بعض الدنية خير من الحاجة إلى الناس.
[١١٤] والصلوات الخمس جائزة خلف [من] صليت خلفه، إلا أن يكون [جهميا] ، فإنه معطل، وإن صليت خلفه فأعد صلاتك، وإن كان إمامك يوم الجمعة جهميا، وهو سلطان فصل خلفه، وأعد صلاتك، وإن كان إمامك من السلطان وغيره صاحب سنة، فصل خلفه ولا تعد صلاتك.
[١١٥] والإيمان بأن أبا بكر وعمر في حجرة عائشة مع رسول الله صلى الله عليه وسلم [قد] دفنا هنالك معه، فإذا أتيت القبر فالتسليم عليهما واجب بعد رسول الله صلى الله عليه وسلم.
[١١٦] والأمر بالمعروف والنهي عن المنكر واجب، إلا من خفت سيفه أو عصاه.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (76,'[١١٧] والتسليم على عباد الله أجمعين.
[١١٨] ومن ترك [صلاة الجمعة] والجماعة في المسجد من غير عذر فهو مبتدع، والعذر',NULL,'109',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (77,'كمرض لا طاقة له بالخروج إلى المسجد، أو خوف من سلطان ظالم، وما سوى ذلك فلا عذر له.
[١١٩] ومن صلى خلف إمام فلم يقتد به فلا صلاة له.
[١٢٠] والأمر بالمعروف والنهي عن المنكر باليد واللسان والقلب، بلا سيف.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (78,'[١٢١] والمستور من المسلمين من لم تظهر له ريبة.
[١٢٢] وكل علم ادعاه العباد من علم الباطن لم يوجد في الكتاب والسنة فهو بدعة وضلالة، ولا ينبغي لأحد [أن] يعمل به، ولا يدعو إليه.
[١٢٣] وأيما امرأة وهبت نفسها لرجل، فإنها لا تحل له، يعاقبان إن نال منها شيئا، إلا بولي وشاهدي [عدل] وصداق.
[١٢٤] وإذا رأيت الرجل يطعن على أحد من أصحاب رسول الله صلى الله عليه وسلم فاعلم أنه صاحب قول سوء وهوى، لقول',NULL,'111',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (79,'رسول صلى الله عليه وسلم: «إذا ذكر أصحابي فأمسكوا» . فقد علم النبي صلى الله عليه وسلم ما يكون منهم من الزلل بعد موته، فلم يقل فيهم إلا خيرا.
وقوله: «ذروا أصحابي، لا تقولوا فيهم إلا خيرا» .
ولا تحدث بشيء من زللهم، ولا حربهم، ولا ما غاب عنك علمه، ولا [تسمعه] من أحد يحدث به، فإنه لا يسلم لك قلبك إن سمعت.
[١٢٥] وإذا سمعت الرجل يطعن على الآثار، [أو يرد الآثار] ، أو يريد غير الآثار، فاتهمه على الإسلام، ولا [تشك] أنه صاحب هوى مبتدع.
[١٢٦] واعلم أن جور السلطان لا ينقص فريضة من فرائض الله عز وجل',NULL,'112',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (80,'التي افترضها على لسان نبيه صلى الله عليه وسلم؛ جوره على نفسه، وتطوعك وبرك معه تام لك إن شاء الله تعالى، يعني: [الجماعة و] الجمعة معهم، والجهاد معهم، وكل شيء من الطاعات فشارك فيه، فلك نيتك.
[١٢٧] وإذا رأيت الرجل يدعو على السلطان فاعلم أنه صاحب هوى، وإذا رأيت الرجل يدعو للسلطان بالصلاح فاعلم أنه صاحب سنة إن شاء الله.
لقول فضيل: لو كانت لي دعوة ما جعلتها الا في السلطان.
أنا أحمد بن كامل قال: نا الحسين بن محمد الطبري، نا مردويه الصائغ، قال:: سمعت فضيلا يقول: لو أن لي دعوة مستجابة ما جعلتها إلا في السلطان.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (81,'قيل له: يا أبا علي فسر لنا هذا.
قال: إذا جعلتها في نفسي لم تعدني، وإذا جعلتها في السلطان صلح، فصلح بصلاحه العباد والبلاد.
فأمرنا أن ندعو لهم [بالصلاح] ، ولم نؤمر أن ندعو عليهم وإن ظلموا، وإن جاروا؛ لأن ظلمهم وجورهم على أنفسهم، وصلاحهم لأنفسهم وللمسلمين.
[١٢٨] ولا تذكر أحدا من أمهات المؤمنين إلا بخير.
[١٢٩] وإذا رأيت الرجل يتعاهد الفرائض في جماعة مع السلطان وغيره، فاعلم أنه صاحب سنة إن شاء الله.
وإذا رأيت الرجل يتهاون بالفرائض في جماعة، وإن كان مع السلطان، فاعلم أنه صاحب هوى.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (82,'[١٣٠] والحلال ما شهدت عليه وحلفت عليه أنه حلال، وكذلك الحرام، وما حاك في صدرك فهو شبهة.
[١٣١] والمستور من بان ستره، والمهتوك من بان هتكه.
[١٣٢] وإن سمعت الرجل يقول: [فلان] مشبه، فاعلم أنه رافضي.
وإذا سمعت الرجل يقول: تكلم بالتوحيد، واشرح لي التوحيد، فاعلم أنه خارجي معتزلي.
أو يقول: فلان [مجبر] ، أو يتكلم بالإجبار، أو يتكلم بالعدل،',NULL,'115',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (83,'فاعلم أنه قدري؛ لأن هذه الأسماء محدثة أحدثها أهل الأهواء.
قال عبد الله بن المبارك: لا تأخذوا عن أهل الكوفة في الرفض [شيئا] ، ولا عن أهل الشام في السيف [شيئا] ولا عن أهل البصرة في القدر [شيئا] ، ولا عن أهل خراسان في الإرجاء [شيئا] ، ولا عن أهل مكة في الصرف، ولا عن أهل المدينة في الغناء، لا تأخذوا عنهم في هذه الأشياء شيئا.
[١٣٣] وإذا رأيت الرجل يحب أبا هريرة وأنس بن مالك وأسيد بن حضير فاعلم أنه صاحب سنة إن شاء الله.',NULL,'116',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (84,'وإذا رأيت الرجل يحب أيوب، وابن عون، ويونس بن عبيد، وعبد الله بن إدريس الأودي، والشعبي، ومالك بن مغول، ويزيد بن زريغ،',NULL,'117',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (85,'ومعاذ بن معاذ، ووهب بن جرير، وحماد بن سلمة، وحماد بن زيد، [ومالك بن أنس، والأوزاعي، وزائدة بن قدامة، فاعلم',NULL,'118',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (86,'أنه صاحب سنة، وإذا رأيت الرجل يحب] الحجاج بن المنهال، وأحمد بن حنبل، وأحمد بن نصر، فاعلم أنه صاحب سنة إن شاء الله إذا ذكرهم بخير، وقال بقولهم.
[١٣٤] وإذا رأيت الرجل جالس مع رجل من أهل الأهواء، فحذره وعرفه، فإن جلس معه بعدما علم فاتقه، فإنه صاحب هوى.
[١٣٥] وإذا سمعت الرجل تأتيه بالأثر فلا يريده، ويريد القرآن، فلا [تشك] أنه رجل قد احتوى على الزندقة، فقم من عنده',NULL,'119',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (87,'[ودعه] .
[١٣٦] واعلم أن الأهواء كلها ردية تدعو كلها إلى السيف، وأرداها وأكفرها: الروافض، والمعتزلة، والجهمية، فإنهم [يريدون الناس] على التعطيل والزندقة.
[١٣٧] واعلم أنه من تناول أحدا من أصحاب محمد صلى الله عليه وسلم، فاعلم أنه إنما أراد محمدا صلى الله عليه وسلم، وقد آذاه في قبره.
[١٣٨] وإذا ظهر لك من إنسان شيء من البدع، فاحذره؛ فإن الذي أخفى [عنك] أكثر مما أظهر.
[١٣٩] وإذا رأيت الرجل رديء الطريق والمذهب، فاسقا فاجرا، صاحب معاص، ضالا، وهو أهل السنة فاصحبه، واجلس معه فإنه ليس [تضرك] معصيته، وإذا رأيت',NULL,'120',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (88,'[الرجل] مجتهدا – وإن بدا متقشفا محترقا بالعبادة - صاحب هوى، فلا تجالسه، ولا تقعد معه، ولا تسمع كلامه ولا [تمش] معه في طريق، فإني لا آمن أن تستحلي طريقته [فتهلك] معه.
ورأى يونس بن عبيد ابنه [وقد] خرج من عند صاحب هوى، فقال: يا بني! من أين جئت؟ قال: من عند فلان. قال: يا بني لأن أراك تخرج من بيت خنثى أحب إلي من أن أراك تخرج من بيت فلان، ولأن تلقى الله يا بني زانيا سارقا فاسقا',NULL,'121',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (89,'خائنا أحب إلي من أن تلقاه بقول فلان وفلان.
ألا ترى أن يونس بن عبيد علم أن الخنثى لا يضل ابنه عن دينه، وأن صاحب البدعة يضله حتى يكفره.
[١٤٠] واحذر ثم احذر [أهل] زمانك خاصة، وانظر من تجالس، وممن تسمع، ومن تصحب، فإن الخلق كأنهم في ردة، إلا من عصمه الله منهم.
[١٤١] وانظر إذا سمعت الرجل يذكر ابن أبي دؤاد، وبشرا المريسي،. . . . . . . . . . . . . . . . .',NULL,'122',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (90,'وثمامة، أو أبا الهذيل أو [هشاما] الفوطي أو واحدا من [أتباعهم و] أشياعهم فاحذره، فإنه صاحب بدعة، فإن هؤلاء كانوا على الردة، واترك هذا الرجل الذي ذكرهم بخير، ومن ذكر منهم بمنزلتهم.
[١٤٢] والمحنة في الإسلام بدعة، وأما اليوم فيمتحن بالسنة، لقوله: إن هذا العلم دين فانظروا عمن تأخذون دينكم، ولا تقبلوا الحديث إلا ممن تقبلون شهادته.',NULL,'123',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (91,'فتنظر، إن كان صاحب سنة له معرفة صدوق كتبت عنه وإلا تركته.
[١٤٣] وإذا أردت الاستقامة على الحق وطريق أهل السنة قبلك فاحذر الكلام، وأصحاب الكلام، والجدال والمراء، والقياس، والمناظرة في الدين، فإن [استماعك] منهم - وإن لم تقبل منهم - يقدح الشك في القلب، وكفى به قبولا [فتهلك] ، وما كانت زندقة قط، ولا بدعة، ولا هوى، ولا ضلالة، إلا من الكلام، والجدال، والمراء، والقياس، [وهي] أبواب البدعة، والشكوك والزندقة.
[١٤٤] فالله الله في نفسك، وعليك بالأثر، وأصحاب الأثر، والتقليد؛ فإن الدين إنما هو بالتقليد [يعني للنبي صلى الله عليه وسلم وأصحابه رضوان الله عليهم] ، ومن قبلنا لم يدعونا في لبس، فقلدهم واسترح، ولا تجاوز الأثر، وأهل الأثر، وقف عند المتشابه، ولا تقس شيئا، ولا تطلب من عندك حيلة ترد [بها] على أهل البدع، فإنك أمرت بالسكوت عنهم، ولا تمكنهم من نفسك.
أما علمت أن محمد بن سيرين',NULL,'124',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (92,'في فضله لم يجب رجلا من أهل البدع في مسألة واحدة، ولا سمع منه آية من كتاب الله، فقيل له، فقال: أخاف أن يحرفها فيقع في قلبي شيء.
[١٤٥] وإذا سمعت الرجل يقول: إنا نحن نعظم الله - إذا سمع آثار رسول الله صلى الله عليه وسلم - فاعلم أنه جهمي، يريد أن يرد أثر رسول الله صلى الله عليه وسلم، ويدفع بهذه الكلمة آثار رسول الله صلى الله عليه وسلم، وهو يزعم أنه يعظم الله وينزهه إذا سمع حديث الرؤية، وحديث النزول وغيره، أفليس قد رد أثر رسول الله صلى الله عليه وسلم؟ وإذا قال: إنا نحن نعظم الله أن يزول من موضع إلى موضع، فقد زعم أنه أعلم بالله من غيره، فاحذر هؤلاء؛ فإن جمهور الناس من السوقة وغيرهم على هذا [الحال، وحذر الناس منهم] .
[١٤٦] وإذا سألك أحد عن مسألة في هذا الكتاب، وهو [مسترشد] فكلمه، وأرشده، وإذا جاءك يناظرك، فاحذره، فإن في',NULL,'125',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (93,'المناظرة: [المراء] ، والجدال، والمغالبة، والخصومة، والغضب، وقد نهيت عن هذا جدا، بخرجان جميعا من طريق الحق، ولم يبلغنا عن أحد من فقهائنا، وعلمائنا أنه ناظر أو جادل أو خاصم.
[١٤٧] قال الحسن: الحكيم لا يماري ولا يداري، حكمته ينشرها، إن قبلت حمد الله، وإن ردت حمد الله.
وجاء رجل إلى الحسن فقال له: أناظرك في الدين؟ فقال',NULL,'126',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (94,'الحسن: أنا عرفت ديني، فإن ضل دينك فاذهب فاطلبه.
وسمع رسول الله صلى الله عليه وسلم قوما على باب حجرته، يقول أحدهم: ألم يقل الله كذا؟ ويقول الآخر: ألم يقل [الله] كذا؟ فخرج مغضبا، فقال: «أبهذا أمرتم؟ أم بهذا بعثت إليكم؟ أن تضربوا كتاب الله بعضه ببعض؟» فنهى عن الجدال.
وكان ابن عمر يكره المناظرة، ومالك بن أنس، ومن فوقه، ومن دونه إلى يومنا هذا، وقول الله أكبر من قول الخلق، قال الله تبارك وتعالى: {مَا يُجَادِلُ فِي آيَاتِ اللَّهِ إِلَّا الَّذِينَ كَفَرُوا} .',NULL,'127',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (95,'وسأل رجل عمر فقال: ما الناشطات نشطا؟ فقال: لو كنت محلوقا لضربت عنقك.
وقال النبي صلى الله عليه وسلم: «المؤمن لا يماري، ولا أشفع للمماري يوم القيامة، فدعوا المراء [لقلة خيره] » .
[١٤٨] ولا يحل لرجل أن يقول: فلان صاحب سنة حتى يعلم منه أنه قد اجتمعت فيه خصال السنة، لا يقال له: صاحب سنة حتى تجتمع فيه السنة كلها.
وقال عبد الله بن المبارك: أصل اثنين وسبعين هوى: أربعة',NULL,'128',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (96,'أهواء، فمن هذه الأربعة الأهواء انشعبت [الاثنان] وسبعون هوى: القدرية، والمرجئة، والشيعة، والخوارج.
فمن قدم أبا بكر وعمر وعثمان [وعليا] على أصحاب رسول الله صلى الله عليه وسلم، ولم يتكلم في الباقين إلا بخير، ودعا لهم، فقد خرج من التشيع أوله وآخره.
ومن قال: الإيمان قول وعمل، يزيد وينقص، فقد خرج من الإرجاء كله أوله وآخره.
ومن قال: الصلاة خلف كل بر وفاجر، والجهاد مع كل خليفة، ولم ير الخروج على السلطان بالسيف، ودعا لهم بالصلاح، فقد خرج من قول الخوارج أوله وآخره.
ومن قال: المقادير كلها [من] الله خيرها وشرها، يضل من يشاء، ويهدي من يشاء، فقد خرج من قول القدرية، أوله وآخره، وهو صاحب سنة.
[١٤٩] وبدعة ظهرت، هي كفر بالله العظيم، ومن قال بها فهو كافر، لا شك فيه: من يؤمن بالرجعة، ويقول: علي بن أبي طالب',NULL,'129',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (97,'حي، وسيرجع قبل يوم القيامة، ومحمد بن علي وجعفر بن محمد، وموسى بن جعفر، وتكلموا في الإمامة، وأنهم يعلمون الغيب، فاحذرهم؛ فإنهم كفار بالله العظيم، ومن قال بهذا.
قال طعمة بن [عمرو] ، وسفيان بن عيينة: من وقف عند عثمان وعلي فهو شيعي لا يعدل، ولا يكلم، ولا يجالس.
ومن قدم عليا على عثمان فهو رافضي قد رفض أمر أصحاب رسول الله صلى الله عليه وسلم.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (98,'ومن قدم [الأربعة] على جماعتهم، وترحم على الباقين، وكف عن زللهم فهو على طريق [الاستقامة و] الهدى في هذا [الباب] .
[١٥٠] والسنة أن تشهد العشرة الذين شهد لهم رسول الله صلى الله عليه وسلم بالجنة أنهم في الجنة لا شك.
[١٥١] ولا تفرد بالصلاة على أحد، إلا لرسول الله صلى الله عليه وسلم وعلى آله فقط.
[١٥٢] وتعلم أن عثمان بن عفان قتل مظلوما، ومن قتله كان ظالما.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (99,'[١٥٣] فمن أقر بما في هذا الكتاب وآمن به واتخذه إماما، ولم يشك في حرف منه، ولم يجحد حرفا واحدا، فهو صاحب سنة وجماعة، كامل، قد كملت فيه السنة، ومن جحد حرفا مما في هذا الكتاب، أو شك [في حرف منه أو شك فيه] أو وقف فهو صاحب هوى. ومن جحد أو شك في حرف من القرآن، أو في شيء جاء عن رسول الله صلى الله عليه وسلم، لقي الله تعالى مكذبا، فاتق الله واحذر وتعاهد إيمانك.
[١٥٤] ومن السنة أن لا تطيع أحدا على معصية الله، ولا أولي الخير ولا الخلق أجمعين، لا طاعة لبشر في معصية الله، ولا تحب عليه [أحدا] ، واكره ذلك كله لله تبارك وتعالى.
[١٥٥] والإيمان بأن التوبة فريضة على العباد أن يتوبوا [إلى الله عز وجل] من كبير المعاصي وصغيرها.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (100,'[١٥٦] ومن لم يشهد لمن شهد له رسول الله صلى الله عليه وسلم بالجنة، فهو صاحب بدعة وضلالة، شاك فيما قال رسول الله صلى الله عليه وسلم.
وقال مالك بن أنس: من لزم السنة، وسلم منه أصحاب رسول الله صلى الله عليه وسلم ثم مات، كان مع النبيين والصديقين والشهداء والصالحين، وإن كان له تقصير في العمل.
وقال [بشر بن الحارث] : الإسلام هو السنة، والسنة هي الإسلام.
وقال فضيل بن عياض: إذا رأيت رجلا من أهل السنة، فكأنما أرى رجلا من أصحاب رسول الله صلى الله عليه وسلم، وإذا رأيت رجلا من أهل البدع، فكأنما أرى رجلا من المنافقين.
وقال يونس بن عبيد العجب ممن يدعو اليوم إلى السنة،',NULL,'133',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (101,'وأعجب منه من يجيب إلى السنة فيقبل.
وكان ابن عون يقول عند الموت: السنة السنة، وإياكم والبدع، حتى مات.
وقال [أحمد بن حنبل] : ومات [رجل] من أصحابي فرئي في المنام، فقال: قولوا لأبي عبد الله: عليك بالسنة؛ فإن أول ما سألني الله سألني عن السنة.
وقال أبو العالية: من مات على السنة مستورا، فهو صديق.
ويقال: الاعتصام بالسنة نجاة.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (102,'[وقال سفيان الثوري: من أصغى بأذنه إلى صاحب بدعة، خرج من عصمة الله، ووكل إليها - يعني إلى البدع -.
وقال داود بن أبي هند: أوحى الله تبارك وتعالى إلى موسى بن عمران: لا تجالس أهل البدع، فإن جالستهم، فحاك في صدرك شيء مما يقولون، أكببتك في نار جهنم.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (103,'وقال الفضيل بن عياض: من جالس صاحب بدعة لم يعط الحكمة.
وقال الفضيل بن عياض: لا تجلس مع صاحب بدعة، فإني أخاف أن تنزل عليك اللعنة.
وقال الفضيل بن عياض: من أحب صاحب بدعة، أحبط الله عمله، وأخرج نور الإسلام من قلبه.
وقال الفضيل بن عياض: من جلس مع صاحب بدعة، ورثه العمى.
وقال الفضيل بن عياض: إذا رأيت صاحب بدعة في طريق فجز في طريق غيره.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (104,'وقال الفضيل بن عياض: من عظم صاحب بدعة، فقد أعان على هدم الإسلام، ومن تبسم في وجه مبتدع فقد استخف بما أنزل الله عز وجل على محمد صلى الله عليه وسلم، ومن زوج كريمته مبتدع فقد قطع رحمها، ومن تبع جنازة مبتدع لم يزل في سخط الله حتى يرجع.
وقال الفضيل بن عياض: آكل مع يهودي ونصراني، ولا آكل مع مبتدع، وأحب أن يكون بيني وبين صاحب بدعة حصن من حديد.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b8601] VALUES (105,'وقال الفضيل بن عياض: إذا علم الله عز وجل من الرجل أنه مبغض لصاحب بدعة غفر له، وإن قل عمله.
ولا يكن صاحب سنة يمالئ صاحب بدعة إلا نفاقا.
ومن أعرض بوجهه عن صاحب بدعة ملأ الله قلبه إيمانا، ومن انتهر صاحب بدعة آمنه الله يوم الفزع الأكبر، ومن أهان صاحب بدعة رفعه الله في الجنة مائة درجة.
فلا تكن تحب صاحب بدعة في الله أبدا] .',NULL,'138',NULL,NULL,'0');
