INSERT INTO [b8661] VALUES (204,'<span data-type="title" id=toc-7>الباب السادس المناقشة والنتيجة</span>
...
ملحق رقم "٢":
تحقيق المخطوطات:
كثير من رسائل الماجستير والدكتوراه في الدراسات العربية والإسلامية تكون تحقيقا لمخطوط لم ينشر بعد، وقد كتب لي بعض قراء هذا الكتاب يطلبون أن أضع فيه فكرة عن تحقيق المخطوطات، وهو طلب أراه عادلًا، ولهذا فإنني أستجيب له؛ لأساعد مجموعة من الطلاب يعملون في هذا الاتجاه، وأقرر هنا أن إضافة هذا الملحق لهذا الموضوع حدثت ابتداء من الطبعة السادسة عشرة، وهذا يؤكد للطالب أن عمله يمكن دائما أن يطور وأن تضاف له تحسينات من حين إلى آخر، وأن النجاح يحتاج إلى مزيد من الجهد ليبقى، وليزيد ازدهار فإن إقبال الناس على هذا الكتاب لم يحل دون الإضافة له والتحسين فيه.
متى تحقق مخطوطا؟
أول ما أذكره للباحثين الذين يجعلون تحقيق مخطوط أساسا لرسائلهم العلمية، هو أن يتأكد الباحث من قيمة',NULL,'15',NULL,NULL,'0');
