INSERT INTO [b8884] VALUES (85,'سمع على أم الكرام كريمة بنت أحمد المروزية صحيح البخاري وعلى القاضي أبي عبد الله محمد بن سلامة بن جعفر بن علي الضا مسند احاديث كتابه الشهاب.
