INSERT INTO [b88] VALUES (1,'<span data-type="title" id=toc-1><span data-type="title" id=toc-2>١ </span></span>- عن زيد بن أرقم قال: قالت امرأة أبى لهب لما مكث النبى صلى الله عليه وسلم لم ينزل عليه الوحى: يامحمد ما أرى شيطانك إلا وقد قلاك، فنزلت والضحى.
** ك
(فتح الباري ٩/٣)
** رجاله ثقات','1','1',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2,'<span data-type="title" id=toc-3>٢ </span>- قوله صلى الله عليه وسلم: " كل خطبة ليس فيها شهادة فهى كاليد الجذماء ".
** د
(فتح الباري ٨/١)
** فيه مقال','1','2',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3,'<span data-type="title" id=toc-4>٣ </span>- عن عبد الله بن مسعود: كان فينا رجل خطب امرأة يقال لها: أم قيس فأبت أن تتزوجه حتى يهاجر فهاجر فتزوجها فكنا نسميه مهاجر أم قيس.
** طب
(فتح الباري ١٠/١)
** إسناده صحيح على شرط الشيخين
** قال الحافظ فى " الفتح " ١ / ١٠: قصة مهاجر أم قيس رواها سعيد بن منصور قال: أخبرنا أبو معاوية عن الأعمش عن شقيق عن عبد الله _ هو ابن مسعود _ قال: من هاجر يبتغى شيئا فإنما له ذلك، هاجر رجل ليتزوج امرأة يقال لها أم قيس فكان يقال له مهاجر أم قيس. ورواه الطبرانى من طريق أخرى عن الأعمش.','1','3',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4,'<span data-type="title" id=toc-5>٤ </span>- حديث ابن مسعود: " رب قتيل بين الصفين الله أعلم بنيته ".
** حم
(فتح الباري ١١/١)
** صحيح
** قال الحافظ فى " الفتح " ١ / ١١: ورد عدة أحاديث صحت فى مطلق النية كحديث ابن مسعود.','1','4',NULL,NULL,'0');
INSERT INTO [b88] VALUES (5,'<span data-type="title" id=toc-6>٥ </span>- حديث عبادة: " من غزا وهو لا ينوى إلا عقالا فله ما نوى ".
** ن
(فتح الباري ١١/١)
** صحيح
** قال الحافظ فى " الفتح " ١ / ١١: وردت عدة أحاديث صحت فى مطلق النية كحديث عبادة.','1','5',NULL,NULL,'0');
INSERT INTO [b88] VALUES (6,'<span data-type="title" id=toc-7>٦ </span>- عن أبى سلمة الماجشون أنه بلغه أن النبى صلى الله عليه وسلم كان يقول: " كان الوحى يأتينى على نحوين: يأتينى به جبريل فيلقيه على كما يلقى الرجل على الرجل، فذاك ينفلت منى، ويأتينى فى بيتى مثل صوت الجرس حتى يخالط قلبى فذاك الذى لا ينفلت منى ".
** سعد
(فتح الباري ١٩/١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١ / ١٩: هذا مرسل مع ثقة رجاله.','1','6',NULL,NULL,'0');
INSERT INTO [b88] VALUES (7,'<span data-type="title" id=toc-8>٧ </span>- حديث: " إن روح القدس نفث فى روعى "، من طريق ابن مسعود.
** ابن أبى الدنيا فى القناعة
(فتح الباري ٢٠/١)
** قال الحافظ فى " الفتح " ١ / ٢٠: صححه الحاكم.','1','7',NULL,NULL,'0');
INSERT INTO [b88] VALUES (8,'<span data-type="title" id=toc-9>٨ </span>- قد وقع فى رواية أبى الأسود عن عروة عن عائشة قالت: إن النبى صلى الله عليه وسلم، كان أول شأنه يرى فى المنام، وكان أول ما رأى جبريل بأجياد، صرخ جبريل: يامحمد "، فنظر يمينا وشمالا فلم ير شيئا فرفع بصره فإذا هو على أفق السماء، فقال: يامحمد، جبريل جبريل، فهرب فدخل فى الناس، فلم ير شيئا، ثم خرج عنهم فناداه فهرب، ثم استعلن له جبريل من قبل حراء _ فذكر قصة (اقرأ بسم ربك) ورأى حينئذ جبريل له جناحان من ياقوت يختطفان البصر _.
** لم
(فتح الباري ٢٣/١)
** قال الحافظ فى " الفتح " ١ / ٢٣: هذا من رواية ابن لهيعة عن أبى الأسود، وابن لهيعة ضعيف. وقال الحافظ فى " الفتح " ١ / ٢٣: وللترمذى من طريق مسروق عن عائشة: لم ير محمد جبريل فى صورته إلا مرتين: مرة عند سدرة المنتهى، ومرة فى أجياد، وهذا يقوى رواية ابن لهيعة.','1','8',NULL,NULL,'0');
INSERT INTO [b88] VALUES (9,'<span data-type="title" id=toc-10>٩ </span>- فى مرسل الزهرى فى دلائل البيهق: " كيف أقرأ؟ ".
** هق فى الدلائل
(فتح الباري ٢٤/١)
** مرسل','1','9',NULL,NULL,'0');
INSERT INTO [b88] VALUES (10,'<span data-type="title" id=toc-11>١٠ </span>- حديث أنس رفعه: " أنا أجود ولد آدم، وأجودهم بعدى رجل علم علما فنشر علمه ورجل جاد بنفسه فى سبيل الله ".
** ت
(فتح الباري ٣٠/١)
** إسناده فيه مقال','1','10',NULL,NULL,'0');
INSERT INTO [b88] VALUES (11,'<span data-type="title" id=toc-12>١١ </span>- عن عروة مرسلا: خرج أبو سفيان إلى الشام _ فذكر الحديث إلى أن قال _ فقال أبو سفيان: هو ساحر كذاب، فقال هرقل: إنى لا أريد شتمه ولكن كيف نسبه _ إلى أن قال _ فهل يقدر إذا عاهد؟ ، قال: لا إلا أن يغدر فى هدنته هذه، فقال: وما يخاف من هذه؟ ، فقال: إن قومى أمدوا حلفاءهم على حلفائه، قال: إن كنتم بدأتم فأنتم أعذر ".
** لم
(فتح الباري ٣٦/١)
** مرسل','1','11',NULL,NULL,'0');
INSERT INTO [b88] VALUES (12,'<span data-type="title" id=toc-13>١٢ </span>- من مرسل بكر بن عبد الله المزنى نحوه، ولفظه فقال: " كذب عدو الله ليس بمسلم ".
** أبو عبيد فى كتاب الأموال
(فتح الباري ٣٧/١)
** إسناده صحيح مرسلا','1','12',NULL,NULL,'0');
INSERT INTO [b88] VALUES (13,'<span data-type="title" id=toc-14>١٣ </span>- عن عمير بن إسحاق، قال: كتب رسول الله صلى الله عليه وسلم إلى كسرى وقيصر فأما كسرى فلما قرأ الكتاب فرقه، وأما قيصر فلما قرأ الكتاب طواه ثم رفعه، فقال رسول الله صلى الله عليه وسلم: " أما هؤلاء فيمزقون وأما هؤلاء فستكون لهم بقية ".
** أبو عبيد فى كتاب الأموال
(فتح الباري ٤٤/١)
** مرسل','1','13',NULL,NULL,'0');
INSERT INTO [b88] VALUES (14,'<span data-type="title" id=toc-15>١٤ </span>- عن الأسود بن هلال قال: قال لى معاذ بن جبل: اجلس بنا نؤمن ساعة.
** حم شب
(فتح الباري ٤٨/١)
** إسناده صحيح','1','14',NULL,NULL,'0');
INSERT INTO [b88] VALUES (15,'<span data-type="title" id=toc-16>١٥ </span>- " اليقين الإيمان كله والصبر نصف الإيمان ".
** طب حل البيهقى فى الزهد
(فتح الباري ٤٨/١)
** إسناد صحيح
** قال الحافظ فى " الفتح " ١ / ٤٨: وأخرجه أبو نعيم فى الحلية والبيهقى فى الزهد من حديثه مرفوعا ولا يثبت رفعه.','1','15',NULL,NULL,'0');
INSERT INTO [b88] VALUES (16,'<span data-type="title" id=toc-17>١٦ </span>- من طريق عبد الله بن عكيم عن ابن مسعود أنه كان يقول: " اللهم زدنا إيمانا ويقينا وفقها ".
** حم فى الإيمان
(فتح الباري ٤٨/١)
** إسناده صحيح','1','16',NULL,NULL,'0');
INSERT INTO [b88] VALUES (17,'<span data-type="title" id=toc-18>١٧ </span>- عن عطية السعدى قال: قال رسول الله صلى الله عليه وسلم: " لا يكون الرجل من المتقين حتى يدع ما لابأس به حذرا لما به البأس ".
** ت
(فتح الباري ٤٨/١)
** قال الحافظ فى " الفتح " ١ / ٤٨: حسنه الترمذى.','1','17',NULL,NULL,'0');
INSERT INTO [b88] VALUES (18,'<span data-type="title" id=toc-19>١٨ </span>- قال ابن عباس: شرعة ومنهاجا: سبيلا وسنة.
** عب فى التفسير
(فتح الباري ٤٨/١)
** إسناده صحيح','1','18',NULL,NULL,'0');
INSERT INTO [b88] VALUES (19,'<span data-type="title" id=toc-20>١٩ </span>- حديث النعمان بن بشير: " إن الدعاء هو العبادة ".
** د ت ن جه
(فتح الباري ٤٩/١)
** إسناده جيد
** قال الحافظ فى " الفتح " ١ / ٤٩: أخرجه أصحاب السنن بسند جيد.','1','19',NULL,NULL,'0');
INSERT INTO [b88] VALUES (20,'<span data-type="title" id=toc-21>٢٠ </span>- عن مجاهد: أن أبا ذر سأل النبى صلى الله عليه وسلم عن الإيمان فتلا عليه (ليس البر إلى أخرها) .
** عب
(فتح الباري ٥١/١)
** رجاله ثقات','1','20',NULL,NULL,'0');
INSERT INTO [b88] VALUES (21,'<span data-type="title" id=toc-22>٢١ </span>- " والمؤمن من أمنه الناس ".
** حب ك
(فتح الباري ٥٤/١)
** صحيح
** تعقيب: هذه زيادة عند حب ك.','1','21',NULL,NULL,'0');
INSERT INTO [b88] VALUES (22,'<span data-type="title" id=toc-23>٢٢ </span>- عن أبى هريرة أن النبى صلى الله عليه وسلم قال: " لا أدرى الحدود كفارة لأهلها أم لا ".
** ك بز حم عب
(فتح الباري ٦٦/١)
** صحيح على شرط الشيخين
** قال الحافظ فى " الفتح " ١ / ٦٦: ذكر الدارقطنى أن عبد الرازق تفرد بوصله، وأن هشام بن يوسف رواه عن معمر فأرسله قلت: وقد وصله آدم بن أبى إياس عن ابن أبى ذئب، وأخرجه الحاكم أيضا فقويت رواية معمر. وقال الحافظ فى " الفتح " ١ / ٦٦: والحق عندى أن حديث أبى هريرة صحيح.','1','22',NULL,NULL,'0');
INSERT INTO [b88] VALUES (23,'<span data-type="title" id=toc-24>٢٣ </span>- عن أيوب، عن عمرو بن شعيب عن أبيه عن جده قال: قال رسول الله صلى الله عليه وسلم: " أبايعكم على أن لا تشركوا بالله شيئا "، فذكر نحو حديث عبادة.
** ابن أبى خثيمة فى تاريخه
(فتح الباري ٦٧/١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١ / ٦٧: قال إسحاق بن راهوية: إذا صح الإسناد إلى عمرو بن شعيب فهو كأيوب عن نافع عن ابن عمر اه.','1','23',NULL,NULL,'0');
INSERT INTO [b88] VALUES (24,'<span data-type="title" id=toc-25>٢٤ </span>- روى على بن أبى طالب وفيه: " من أصاب ذنبا فعوقب به فى الدنيا فالله أكرم من أن يثنى العقوبة على عبده فى الآخرة ".
** ت
(فتح الباري ٦٧/١)
** إسناده حسن
** قال الحافظ فى " الفتح " ١ / ٦٧: صححه الحاكم.','1','24',NULL,NULL,'0');
INSERT INTO [b88] VALUES (25,'<span data-type="title" id=toc-26>٢٥ </span>- " من أصاب ذنبا أقيم عليه ذلك الذنب فهو كفارة له ".
** حم
(فتح الباري ٦٨/١)
** إسناده حسن
** قال الحافظ فى " الفتح " ١ / ٦٧: هو عند الطبرانى بإسناد حسن من حديث أبى تميمة الهجيمى.','1','25',NULL,NULL,'0');
INSERT INTO [b88] VALUES (26,'<span data-type="title" id=toc-27>٢٦ </span>- " إن السيف محاء للخطايا ".
** حب
(فتح الباري ٦٨/١)
** قال الحافظ فى " الفتح " ١ / ٦٨: صححه ابن حبان وغيره.
** تعقيب: * راوى الحديث هو عتبة بن عبد السلمى. والحديث فى ابن ماجه رقم ٤٦٦٣ _ احسان.','1','26',NULL,NULL,'0');
INSERT INTO [b88] VALUES (27,'<span data-type="title" id=toc-28>٢٧ </span>- عن أبى سالم الجيشافى: عن أبى ذر أن رسول الله صلى الله عليه وسلم قال له: " كيف ترى جعيلا؟ "، قال: قلت: كشكله من الناس، يعنى المهاجرين، قال: " فكيف ترى فلانا؟ "، قال: قلت: سيد من سادات الناس قال: " فجعيل خير من ملء الأرض من فلان " قال: قلت: ففلان هكذا وأنت تصنع به ما تصنع، قال: " إنه رأس قومه فأنا أتألفهم به ".
** محمد بن هرون الرويانى فى مسنده
(فتح الباري ٨٠/١)
** إسناده صحيح','1','27',NULL,NULL,'0');
INSERT INTO [b88] VALUES (28,'<span data-type="title" id=toc-29>٢٨ </span>- قال عمار _ هو ابن ياسر _: " ثلاث من كن فيه فقد استكمل الإيمان ".
** حم فى الإيمان يعقوب بن شيبه فى مسنده عب بز البغوى فى شرح السنة جامع الأصول ابن أبى حاتم فى
(فتح الباري ٨٢/١)
** معلول
** قال الحافظ فى " الفتح " ١ / ٨٣: استغربه البزار، وقال أبو زرعة: خطأ اه. قلت: وهو معلول من حيث صناعة الإسناد لأن عبد الرازق تغير بآخرة وسماع هؤلاء منه فى حال تغيره إلا أن مثله لا يقال بالرأى فهو فى حكم المرفوع، وقد رويناه مرفوعا من وجه آخر عن عمار أخرجه الطبرانى فى الكبير وفى إسناده ضعف.','1','28',NULL,NULL,'0');
INSERT INTO [b88] VALUES (29,'<span data-type="title" id=toc-30>٢٩ </span>- فى الطبرانى _ فى حديث طويل _: من حديث سلمان: "إذا وعد وهو يحدث نفسه أنه يخلف "، وكذا قال فى باقى الخصال.
** طب
(فتح الباري ٩٠/١)
** إسناده لابأس به
** قال الحافظ فى " الفتح " ١ / ٩٠: إسناده لابأس به، ليس فيهم من أجمع على تركه، وهو عند أبى داود والترمذى من حديث زيد بن أرقم مختصر.','1','29',NULL,NULL,'0');
INSERT INTO [b88] VALUES (30,'<span data-type="title" id=toc-31>٣٠ </span>- أخرج أحمد بسند صحيح من حديث أعرابى لم يسمه، أنه سمع رسول الله صلى الله عليه وسلم يقول: " خير دينكم أيسره ".
** حم
(فتح الباري ٩٤/١)
** إسناده صحيح','1','30',NULL,NULL,'0');
INSERT INTO [b88] VALUES (31,'<span data-type="title" id=toc-32>٣١ </span>- حديث عروة الفقيمى عن النبى صلى الله عليه وسلم قال: " إن دين الله يسر ".
** حم
(فتح الباري ٩٤/١)
** إسناده حسن','1','31',NULL,NULL,'0');
INSERT INTO [b88] VALUES (32,'<span data-type="title" id=toc-33>٣٢ </span>- حديث بريدة قال: قال رسول الله صلى الله عليه وسلم: " عليكم هديا قاصدا، فإنه من يشاد هذا الدين يغلبه ".
** حم
(فتح الباري ٩٤/١)
** إسناده حسن','1','32',NULL,NULL,'0');
INSERT INTO [b88] VALUES (33,'<span data-type="title" id=toc-34>٣٣ </span>- قال ابن عباس وغيره: كان يصلى إلى بيت المقدس، لكنه لا يستدبر الكعبة، بل يجعلها بينه وبين بيت المقدس.
** لم
(فتح الباري ٩٦/١)
** قال الحافظ فى " الفتح " ١ / ٩٦: صححه الحاكم وغيره من حديث ابن عباس.','1','33',NULL,NULL,'0');
INSERT INTO [b88] VALUES (34,'<span data-type="title" id=toc-35>٣٤ </span>- فى بعض طرق حديث عائشة بلفظ: " اكلفوا من العمل ما تطيقون، فإن الله لا يمل من الثواب حتى تملوا من العمل ".
** لم
(فتح الباري ١٠٢/١)
** قال الحافظ فى " الفتح " ١ / ١٠٢: فى سنده موسى بن عبيدة وهو ضعيف.','1','34',NULL,NULL,'0');
INSERT INTO [b88] VALUES (35,'<span data-type="title" id=toc-36>٣٥ </span>- عن عبد الرحمن بن عبد الله بن مسعود عن أبيه ولفظه: " قتال المسلم أخاه كفر، وسبابه فسوق ".
** ت
(فتح الباري ١١٢/١)
** قال الحافظ فى " الفتح " ١ / ١١٢: أخرجه الترمذى مصححا، ورواه جماعة عن عبد الله بن مسعود موقوفا ومرفوعا ورواه النسائى من حديث سعد بن أبى وقاص أيضا مرفوعا.','1','35',NULL,NULL,'0');
INSERT INTO [b88] VALUES (36,'<span data-type="title" id=toc-37>٣٦ </span>- حديث عبد الله بن عمرو _ المخرج عند أحمد _ مرفوعا: " ويل للمصرين الذين يصرون على مافعلوا وهم يعلمون ".
** حم
(فتح الباري ١١٢/١)
** إسناده حسن','1','36',NULL,NULL,'0');
INSERT INTO [b88] VALUES (37,'<span data-type="title" id=toc-38>٣٧ </span>- عن أبى بكر الصديق مرفوعا: " ما أصر من استغفر وإن عاد فى اليوم سبعين مرة ".
** ت
(فتح الباري ١١٢/١)
** إسناده حسن','1','37',NULL,NULL,'0');
INSERT INTO [b88] VALUES (38,'<span data-type="title" id=toc-39>٣٨ </span>- عن أبى بكرة قال: أما الدباء فان أهل الطائف كانوا يأخذون القرع فيخرطون فيه العنب ثم يدفنونه حتى يهدر ثم يموت، وأما النقير فإن أهل اليمامة كانوا ينقرون أصل النخلة ثم ينبذون الرطب، والبسر ثم يدعونه حتى يهدر ويموت، وأما الحنتم فجرار كان تحمل إلينا فيها الخمر، وأما المزفت فهذه الأوعية التى كانت فيها الزفت.
** طيا
(فتح الباري ١٣٥/١)
** إسناده حسن','1','38',NULL,NULL,'0');
INSERT INTO [b88] VALUES (39,'<span data-type="title" id=toc-40>٣٩ </span>- من طريق سفيان بن حسين عن أبى بشر عن مجاهد عن ابن عمر قال: قال رسول الله صلى الله عليه وسلم: " مثل المؤمن مثل النخلة، ما أتاك منها نفعك ".
** بز
(فتح الباري ١٤٧/١)
** إسناده صحيح','1','39',NULL,NULL,'0');
INSERT INTO [b88] VALUES (40,'<span data-type="title" id=toc-41>٤٠ </span>- " إن العلماء هم ورثة الأنبياء، ورثوا العلم، من أخذه أخذ بحظ وافر ".
** خت د ت حب ك
(فتح الباري ١٦٠/١)
** قال الحافظ فى " الفتح " ١ / ١٦٠: رواه الحاكم مصححا من حديث أبى الدرداء وحسنه حمزة الكنانى وضعفه عندهم باضطراب فى سنده، لكن له شاهد يتقوى بها.','1','40',NULL,NULL,'0');
INSERT INTO [b88] VALUES (41,'<span data-type="title" id=toc-42>٤١ </span>- من طريق ابن عمر عن عمر مرفوعا: " من يرد الله به خيرا يفهمه ".
** خت عاصم فى العلم
(فتح الباري ١٦١/١)
** إسناده حسن','1','41',NULL,NULL,'0');
INSERT INTO [b88] VALUES (42,'<span data-type="title" id=toc-43>٤٢ </span>- عن معاوية مرفوعا: " يا أيهاالناس تعلموا، إنما العلم بالتعلم والفقه بالتفقه، ومن يرد الله به خيرا يفقهه فى الدين ".
** خت عاصم طب
(فتح الباري ١٦١/١)
** إسناده حسن
** قال الحافظ فى " الفتح " ١ / ١٦١: إسناده حسن إلا أن فيه مبهما اعتضد بمجيئه من وجه آخر، وروى البزار نحوه من حديث ابن مسعود موقوفا، ورواه أبو نعيم الأصبهانى مرفوعا وفى الباب عن أبى الدرداء وغيره.','1','42',NULL,NULL,'0');
INSERT INTO [b88] VALUES (43,'<span data-type="title" id=toc-44>٤٣ </span>- حديث معاوية من وجه آخر ضعيف وزاد فى آخره: " ومن لم يتفقه فى الدين لم يبال الله به ".
** يع
(فتح الباري ١٦٥/١)
** إسناده ضعيف','1','43',NULL,NULL,'0');
INSERT INTO [b88] VALUES (44,'<span data-type="title" id=toc-45>٤٤ </span>- قال عمر: تفقهوا قبل أن تسودوا.
** خت شب
(فتح الباري ١٦٦/١)
** إسناده صحيح','1','44',NULL,NULL,'0');
INSERT INTO [b88] VALUES (45,'<span data-type="title" id=toc-46>٤٥ </span>- عند الترمذى من حديث أبى كبشة الأنمارى أنه سمع رسول الله صلى الله عليه وسلم _ فذكر حديثا طويلا فيه استواء العامل فى المال بالحق والمتمنى فى الأجر ولفظه: " وعبد رزقه الله علما ولم يرزقه مالا فهو صادق النية يقول: لو أن لى مالا لعملت مثل ما يعمل فلان، فأجرهما سواء "، الحديث وذكر فى ضدهما " أنهما فى الوزر سواء ".
** ت
(فتح الباري ١٦٧/١)
** قال الحافظ فى " الفتح " ١ / ١٦٧: قال الترمذى: حديث حسن صحيح.','1','45',NULL,NULL,'0');
INSERT INTO [b88] VALUES (46,'<span data-type="title" id=toc-47>٤٦ </span>- " اللهم فقهه فى الدين وعلمه التأويل ".
** حم حب طب سعد
(فتح الباري ١٧٠/١)
** مرسل
** قال الحافظ فى " الفتح " ١ / ١٧٠: هذه الزيادة فى رواية سعيد بن جبير الت قدمناها عند أحمد وابن حبان والطبرانى ورواها ابن سعد من وجه آخر عن عكرمة مرسلا. وأخرج البغوى فى معجم الصحابة من طريق زيد بن أسلم عن ابن عمر كان عمر يدعو ابن عباس.','1','46',NULL,NULL,'0');
INSERT INTO [b88] VALUES (47,'<span data-type="title" id=toc-48>٤٧ </span>- طريق الحجاج بن دينار عن محمد بن المنكدر، عن جابر قال: كان يبلغنى عن النبى صلى الله عليه وسلم حديث فى القصاص وكان صاحب الحديث بمصر فاشتريت بعيرا فسرت حتى وردت مصر فقصدت إلى باب الرجل..... فذكر نحوه.
** طب فى مسند الشاميين فوائد تمام
(فتح الباري ١٧٤/١)
** إسناده صالح
** قال الحافظ فى " الفتح " ١ / ١٧٤: الحديث أخرجه البخارى فى الأدب المفرد وأحمد وأبو يعلى فى مسنديهما من طريق عبد الله بن محمد بن عقيل أنه سمع جابر بن عبد الله يقول: بلغنى عن رجل حديث سمعه من رسول الله صلى الله عليه وسلم فاشتريت بعيرا ثم شددت رحلى فسرت إليه شهرا حتى قدمت الشام، فإذا عبد الله بن أنيس، فقلت للبواب: قل له جابر على الباب، فقال: ابن عبد الله؟ ، قلت: نعم، فخرج واعتنقنى فقلت
حديث بلغنى عنك أنك سمعته من رسول الله صلى الله عليه وسلم فخشيت أن أموت قبل أن أسمعه فقال: سمعت رسول الله صلى الله عليه وسلم يقول: " يحشر الله الناس يوم القيامة عراة ... "، فذكر الحديث، وله طريق أخرى أخرجها الطبرانى فى مسند الشاميين.','1','47',NULL,NULL,'0');
INSERT INTO [b88] VALUES (48,'<span data-type="title" id=toc-49>٤٨ </span>- عن على بن رفاعة القرظى قال: خرج عشرة من أهل الكتاب _ منهم أبى رفاعة _ إلى النبى صلى الله عليه وسلم فأمنوا به فأوذوا فنزلت (الذين أتيناهم الكتاب من قبله هم به يؤمنون) الآيات.
** طب
(فتح الباري ١٩١/١)
** إسناده صحيح','1','48',NULL,NULL,'0');
INSERT INTO [b88] VALUES (49,'<span data-type="title" id=toc-50>٤٩ </span>- " من كذب على ليضل به الناس " الحديث.
** دى
(فتح الباري ٢٠٠/١)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ١ / ٢٠٠: ورد فى بعض طرق الحديث زيادة لم تثبت وهى ما أخرجه البزار من حديث ابن مسعود. وقال الحافظ فى " الفتح " ١ / ٢٠٠: قد اختلف فى وصله وإرساله، ورجح الدرقطنى والحاكم إرساله وأخرجه الدارمى من حديث يعلى بن مرة بسند ضعيف.','1','49',NULL,NULL,'0');
INSERT INTO [b88] VALUES (50,'<span data-type="title" id=toc-51>٥٠ </span>- عن ابن عمر بلفظ: " إن الذى يكذب على يبنى له بيت فى النار ".
** حم
(فتح الباري ٢٠١/١)
** إسناده صحيح','1','50',NULL,NULL,'0');
INSERT INTO [b88] VALUES (51,'<span data-type="title" id=toc-52>٥١ </span>- عن طارق بن شهاب قال: شهدت عليا على المنبر وهو يقول: والله ما عندنا كتاب نقرؤه عليكم إلا كتاب الله وهذه الصحيفة.
** حم
(فتح الباري ٢٠٤/١)
** إسناده حسن','1','51',NULL,NULL,'0');
INSERT INTO [b88] VALUES (52,'<span data-type="title" id=toc-53>٥٢ </span>- عن عمرو بن شعيب: عن مجاهد والمغيرة بن حكيم قالا: سمعنا أبا هريرة يقول: ما كان أحد أعلم بحديث رسول الله منى إلا ما كان من عبد الله بن عمرو فإنه كان يكتب بيده ويعى بقلبه، وكنت أعى ولا أكتب، استأذن رسول الله صلى الله عليه وسلم فى الكتاب عنه فاذن له.
** حم هق فى المدخل
(فتح الباري ٢٠٧/١)
** إسناده حسن','1','52',NULL,NULL,'0');
INSERT INTO [b88] VALUES (53,'<span data-type="title" id=toc-54>٥٣ </span>- حديث عمر: كان النبى صلى الله عليه وسلم يسمر مع أبى بكر فى الأمر من أمور المسلمين.
** ت ن
(فتح الباري ٢١٣/١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١ / ٢١٣: رجاله ثقات إلا أن فى إسناده اختلافا على علقمة.','1','53',NULL,NULL,'0');
INSERT INTO [b88] VALUES (54,'<span data-type="title" id=toc-55>٥٤ </span>- حديث عبد الله بن عمرو: كان النبى صلى الله عليه وسلم يحدثنا عن بنى إسرائيل حتى يصبح لا يقوم إلا إلى عظيم صلاة.
** د
(فتح الباري ٢١٣/١)
** قال الحافظ فى " الفتح " ١ / ٢١٣: صححه ابن خزيمة، وهو من رواية أبى حسان عن عبد الله بن عمرو وايس على شرط البخارى.','1','54',NULL,NULL,'0');
INSERT INTO [b88] VALUES (55,'<span data-type="title" id=toc-56>٥٥ </span>- حديث: " لاسمر إلا لمصل أو مسافر ".
** حم
(فتح الباري ٢١٣/١)
** إسناده فيه راو مجهول','1','55',NULL,NULL,'0');
INSERT INTO [b88] VALUES (56,'<span data-type="title" id=toc-57>٥٦ </span>- عن ابن عمر أنه قال لأبى هريرة: كنت ألزمنا لرسول الله صلى الله عليه وسلم وأعرفنا بحديثه.
** حم ت
(فتح الباري ٢١٤/١)
** قال الحافظ فى " الفتح " ١ / ٢١٤: قال الترمذى: حسن اه. واختلف فى إسناد هذا الحديث على الزهرى، فرواه مالك عنه هكذا، ووافقه إبراهيم بن سعد وسفيان بن عيينة ورواه شعيب عن الزهرى عن سعيد بن المسيب وأبى سلمة بن عبد الرحمن، كلاهما عن أبى هريرة وتابعه يونس بن يزيد، والإسنادان جميعا محفوظان صححهما الشيخان.','1','56',NULL,NULL,'0');
INSERT INTO [b88] VALUES (57,'<span data-type="title" id=toc-58>٥٧ </span>- عن الحسن بن عمرو بن أميه قال: تحدثت عند أبى هريرة بحديث فانكره فقلت إنى سمعت منك، فقال: إن كنت سمعته منى فهو مكتوب عندى.
** ابن وهب
(فتح الباري ٢١٥/١)
** إسناده ضعيف','1','57',NULL,NULL,'0');
INSERT INTO [b88] VALUES (58,'<span data-type="title" id=toc-59>٥٨ </span>- ووقع فى المسند عنه " حفظت ثلاثة أجربة، بثثت منها جرابين "، ووقع فى المحدث الفاصل للرامهرمزى من طريق منقطعة عن أبى هريرة: " خمسة أجربة ".
** الرامهرمزى فى المحدث الفاصل
(فتح الباري ٢١٦/١)
** إسناده منقطع','1','58',NULL,NULL,'0');
INSERT INTO [b88] VALUES (59,'<span data-type="title" id=toc-60>٥٩ </span>- حديث أبى سعيد الخدرى رضى الله عنه أن النبى صلى الله عليه وسلم أذن لمعاذ فى التبشير، فلقيه عمر فقال: لا تعجل، ثم دخل فقال: يانبى الله أنت أفضل رأيا، إن الناس إذا سمعوا ذلك اتكلوا عليها قال: " فرده ".
** بز
(فتح الباري ٢٢٧/١)
** إسناده حسن','1','59',NULL,NULL,'0');
INSERT INTO [b88] VALUES (60,'<span data-type="title" id=toc-61>٦٠ </span>- عن جابر بن عبد الله الأنصارى قال: أخبرنى من شهد معاذا حين حضرته الوفاة يقول: سمعت من رسول الله صلى الله عليه وسلم حديثا لم يمنعنى أن أحدثكموه إلا مخافة أن تتكلوا: فذكره " من شهد ألا إله إلا الله مخلصا من قلبه أو يقينا....... " الحديث.
** حم
(فتح الباري ٢٢٧/١)
** إسناده صحيح','1','60',NULL,NULL,'0');
INSERT INTO [b88] VALUES (61,'<span data-type="title" id=toc-62>٦١ </span>- عن معاذ أنه لما حضرته الوفاة قال: أدخلوا على الناس فأدخلوا عليه فقال: سمعت رسول الله صلى الله عليه وسلم يقول: " من مات لا يشرك بالله شيئا جعله الله فى الجنة "، وما كنت أحدثكموه لا عند الموت، وشاهده على ذلك أبو الدرداء، فقال: صدق أخى وما كان يحدثكم به إلا عند موته.
** حم
(فتح الباري ٢٢٨/١)
** منقطع
** قال الحافظ فى " الفتح " ١ / ٢٢٨: أخرجه أحمد من وجه فيه انقطاع.','1','61',NULL,NULL,'0');
INSERT INTO [b88] VALUES (62,'<span data-type="title" id=toc-63>٦٢ </span>- عن أبى الأسود _ يتيم عروة _: عن عروة: أن جبريل علم النبى صلى الله عليه وسلم الوضوء عند نزوله عليه بالوحى.
** ابن لهيعة فى المغازى
(فتح الباري ٢٣٣/١)
** مرسل
** قال الحافظ فى " الفتح " ١ / ٢٣٣: ووصله أحمد من طريق ابن لهيعة أيضا، لكن قال: عن الزهرى عن عروة عن أسامة بن زيد عن أبيه، وأخرجه ابن ماجه من رواية رشدين بن سعد عن عقيل عن الزهرى نحوه، لكن لم يذكر زيد بن حارثة فى السند، وأخرجه الطبرانى فى الأوسط من طريق الليث عن عقيل موصولا، ولو ثبت لكان على شرط الصحيح لكن المعروف رواية ابن لهيعة.','1','62',NULL,NULL,'0');
INSERT INTO [b88] VALUES (63,'<span data-type="title" id=toc-64>٦٣ </span>- حديث أبى بن كعب أن النبى صلى الله عليه وسلم دعا بماء فتوضأ مرة مرة وقال: " هذا وضوء لا يقبل الله الصلاة إلا به ".
** جه
(فتح الباري ٢٣٣/١)
** ضعيف
** قال الحافظ فى " الفتح " ١ / ٢٣٣: له طرق أخرى كلها ضعيفة.','1','63',NULL,NULL,'0');
INSERT INTO [b88] VALUES (64,'<span data-type="title" id=toc-65>٦٤ </span>- عن عمرو بن شعيب عن أبيه عن جده أن النبى صلى الله عليه وسلم توضأ ثلاثا ثلاثا ثم قال: " من زاد على هذا أو نقص فقد أساء وظلم ".
** د
(فتح الباري ٢٣٣/١)
** إسناده جيد
** قال الحافظ فى " الفتح " ١ / ٢٣٣: عده مسلم فى جميلة ما أنكر على عمرو بن شعيب.','1','64',NULL,NULL,'0');
INSERT INTO [b88] VALUES (65,'<span data-type="title" id=toc-66>٦٥ </span>- عن المطلب بن حنطب مرفوعا: " الوضوء مرة ومرتين وثلاثا، فإن نقص من واحدة أو زاد على ثلاث فقد أخطأ ".
** نعيم بن حماد
(فتح الباري ٢٣٣/١)
** رجاله ثقات مرسلا','1','65',NULL,NULL,'0');
INSERT INTO [b88] VALUES (66,'<span data-type="title" id=toc-67>٦٦ </span>- الحديث الوارد: " الوضوء على الوضوء نور ".
** لم
(فتح الباري ٢٣٤/١)
** ضعيف','1','66',NULL,NULL,'0');
INSERT INTO [b88] VALUES (67,'<span data-type="title" id=toc-68>٦٧ </span>- عن أبى ذر مرفوعا: " الصعيد الطيب وضوء المسلم ".
** ن
(فتح الباري ٢٣٥/١)
** إسناده قوى','1','67',NULL,NULL,'0');
INSERT INTO [b88] VALUES (68,'<span data-type="title" id=toc-69>٦٨ </span>- حديث: " هذا وضوئى ووضوء الأنبياء قبلى ".
** لم
(فتح الباري ٢٣٦/١)
** ضعيف
** قال الحافظ فى " الفتح " ١ / ٢٣٦: لا يصح الإحتجاج به لضعفه.','1','68',NULL,NULL,'0');
INSERT INTO [b88] VALUES (69,'<span data-type="title" id=toc-70>٦٩ </span>- من طريق عبد العزيز بن المختار، عن عبد العزيز بن صهيب: " إذ دخلتم الخلاء فقولوا: بسم الله أعوذ بالله من الخبث والخبائث ".
** العمرى
(فتح الباري ٢٤٤/١)
** إسناده على شرط مسلم','1','69',NULL,NULL,'0');
INSERT INTO [b88] VALUES (70,'<span data-type="title" id=toc-71>٧٠ </span>- حديث معقل الأسدى: " نهى رسول الله صلى الله عليه وسلم أن نستقبل القبلتين ببول أو بغائط ".
** د
(فتح الباري ٢٤٦/١)
** ضعيف
** قال الحافظ فى " الفتح " ١ / ٢٤٦: لأن فيه راويا مجهول الحال.','1','70',NULL,NULL,'0');
INSERT INTO [b88] VALUES (71,'<span data-type="title" id=toc-72>٧١ </span>- وللحكيم الترمذى بسند صحيح: " فرأيته فى كنيف ".
** الحكيم الترمذى
(فتح الباري ٢٤٧/١)
** إسناده صحيح','1','71',NULL,NULL,'0');
INSERT INTO [b88] VALUES (72,'<span data-type="title" id=toc-73>٧٢ </span>- عن حذيفة بن اليمان رضى الله عنه أنه سئل عن الإستنجاء بالماء فقال: إذا لايزال فى يدى نتن.
** حشب
(فتح الباري ٢٥١/١)
** إسناده صحيح','1','72',NULL,NULL,'0');
INSERT INTO [b88] VALUES (73,'<span data-type="title" id=toc-74>٧٣ </span>- قوله صلى الله عليه وسلم لطلق بن على، حين سأله عن مس ذكره: " إنما هو بضعة منك ".
** لم
(فتح الباري ٢٥٤/١)
** صحيح أو حسن','1','73',NULL,NULL,'0');
INSERT INTO [b88] VALUES (74,'<span data-type="title" id=toc-75>٧٤ </span>- حديث أبى هريرة: أن النبى صلى الله عليه وسلم نهى أن يستنجى بروث أو بعظم وقال: " إنهما لا يطهران ".
** قط
(فتح الباري ٢٥٦/١)
** قال الحافظ فى " الفتح " ١ / ٢٥٦: صححه الدارقطنى.','1','74',NULL,NULL,'0');
INSERT INTO [b88] VALUES (75,'<span data-type="title" id=toc-76>٧٥ </span>- طريق معمر عن أبى إسحاق عن علقمة، عن ابن مسعود فى هذا الحديث، فيه: فألقى الروثة وقال: " إنها ركس ائتنى بحجر ".
** حم
(فتح الباري ٢٥٧/١)
** رجاله ثقات أثبات
** قال الحافظ فى " الفتح " ١ / ٢٥٧: وقد تابع عليه معمر: أبو شعبة الواسطى وهو ضعيف، أخرجه الدارقطنى وتابعهما عمار بن زريق _ أحد الثقات _ عن أبى إسحاق، وقد قيل إن أبا إسحاق لم يسمع من علقمة، لكن أثبت سماعه لهذا الحديث منه الكرابيسى.','1','75',NULL,NULL,'0');
INSERT INTO [b88] VALUES (76,'<span data-type="title" id=toc-77>٧٦ </span>- عن أبى هريرة:أن النبى صلى الله عليه وسلم توضأ مرتين مرتين.
** د ت حب
(فتح الباري ٢٥٩/١)
** قال الحافظ فى " الفتح " ١ / ٢٥٩: صححه الترمذى، وهو شاهد قوى لرواية فليح.','1','76',NULL,NULL,'0');
INSERT INTO [b88] VALUES (77,'<span data-type="title" id=toc-78>٧٧ </span>- ولأبى داود الطيالسى: " إذا توضأ أحدكم واستنثر فليفعل ذلك مرتين أو ثلاثا ".
** طيا
(فتح الباري ٢٦٢/١)
** إسناده حسن
** قال الحافظ فى " الفتح " ١ / ٢٦٢: رواه أحمد وأبو داود والحاكم من حديث ابن عباس مرفوعا: " استنثروا مرتين بالغتين أو ثلاثا ".','1','77',NULL,NULL,'0');
INSERT INTO [b88] VALUES (78,'<span data-type="title" id=toc-79>٧٨ </span>- قوله صلى الله عليه وسلم للأعرابى: " توضأ كما أمرك الله ".
** ت ك
(فتح الباري ٢٦٢/١)
** قال الحافظ فى " الفتح " ١ / ٢٦٢: حسنه الترمذى وصححه الحاكم.','1','78',NULL,NULL,'0');
INSERT INTO [b88] VALUES (79,'<span data-type="title" id=toc-80>٧٩ </span>- " إذا استيقظ أحدكم من الليل فلا يدخل ".
** ت
(فتح الباري ٢٦٣/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٢٦٣: للترمذى من وجه صحيح.','1','79',NULL,NULL,'0');
INSERT INTO [b88] VALUES (80,'<span data-type="title" id=toc-81>٨٠ </span>- عن أبى هريرة: أنه كان يفعله ولا يرى بتركه بأسا.
** صى
(فتح الباري ٢٦٤/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٢٦٤: وعن ابن عمر والبراء نحو ذلك.','1','80',NULL,NULL,'0');
INSERT INTO [b88] VALUES (81,'<span data-type="title" id=toc-82>٨١ </span>- عن جابر بن عبد الله: " إذا ضحك فى الصلاة أعاد الصلاة ولم يعد الوضوء ".
** خت صى قط
(فتح الباري ٢٨٠/١)
** صحيح
** قال الحافظ فى " الفتح " ١ / ٢٨٠: أخرجه الدارقطنى من طريق أخرى مرفوعا لكن ضعفها.','1','81',NULL,NULL,'0');
INSERT INTO [b88] VALUES (82,'<span data-type="title" id=toc-83>٨٢ </span>- عن أبى هريرة موقوفا: " لاوضوء إلا من حدث ".
** خت اسماعيل القاضى فى الأحكام
(فتح الباري ٢٨١/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٢٨١: ورواه أحمد وأبو داود والترمذى _ من طريق شعبة عن سهيل بن أبى صالح عن أبيه عنه مرفوعا وزاد " أو ريح ".','1','82',NULL,NULL,'0');
INSERT INTO [b88] VALUES (83,'<span data-type="title" id=toc-84>٨٣ </span>- ويذكر عن جابر أن النبى صلى الله عليه وسلم كان فى غزوة ذات الرقاع فرمى رجل بسهم فنزفه الدم فركع وسجد ومضى فى صلاته ".
** حم د قط
(فتح الباري ٢٨١/١)
** قال الحافظ فى " الفتح " ١ / ٢٨١: وصله ابن اسحاق فى المغازى قال: حدثنى صدقة بن يسار عن عقيل بن جابر عن أبيه مطولا. وقال الحافظ فى " الفتح " ١ / ٢٨١: صححه ابن خزيمة وابن حبان والحاكم كلهم من طريق ابن إسحق وشيخه صدقة ثقة، وعقيل: لا أعرف راويا عنه غير صدقة، ولهذا لم يجزم به المصنف أو لكونه اختصره، أو للخلاف فى ابن اسحاق.','1','83',NULL,NULL,'0');
INSERT INTO [b88] VALUES (84,'<span data-type="title" id=toc-85>٨٤ </span>- قد صح أن عمر صلى وجرحه ينبع دما.
** لم
(فتح الباري ٢٨١/١)
** صحيح','1','84',NULL,NULL,'0');
INSERT INTO [b88] VALUES (85,'<span data-type="title" id=toc-86>٨٥ </span>- عصر ابن عمر بثرة فخرج منها لدم ولم يتوضأ.
** خت شب
(فتح الباري ٢٨٢/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٢٨٢: وصله ابن أبى شيبة بإسناد صحيح، وزاد قبل قوله _ ولم يتوضأ _.','1','85',NULL,NULL,'0');
INSERT INTO [b88] VALUES (86,'<span data-type="title" id=toc-87>٨٦ </span>- بزق ابن أبى أوفى دما فمضى فى صلاته.
** خت سفيان الثورى فى جامعه
(فتح الباري ٢٨٢/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٢٨٢: أثره هذا وصله سفيان الثورى فى جماعة عن عطاء بن السائب أنه رأه فعل ذلك، وسفيان سمع من عطاء قبل اختلاطه.','1','86',NULL,NULL,'0');
INSERT INTO [b88] VALUES (87,'<span data-type="title" id=toc-88>٨٧ </span>- من حديث عثمان فى صفته الوضوء: " فغسل يديه إلى المرفقين حتى مس أطراف العضدين ".
** قط
(فتح الباري ٢٩٢/١)
** إسناده حسن','1','87',NULL,NULL,'0');
INSERT INTO [b88] VALUES (88,'<span data-type="title" id=toc-89>٨٨ </span>- عن جابر قال: كان رسول الله صلى الله عليه وسلم إذا توضأ أدار الماء على مرفقيه.
** قط
(فتح الباري ٢٩٢/١)
** إسناده ضعيف','1','88',NULL,NULL,'0');
INSERT INTO [b88] VALUES (89,'<span data-type="title" id=toc-90>٨٩ </span>- حديث عطاء أن رسول الله صلى الله عليه وسلم توضأ فسر العمامة عن رأسه ومسح مقدم رأسه.
** شفع
(فتح الباري ٢٩٣/١)
** مرسل
** قال الحافظ فى " الفتح " ١ / ٢٩٣: هو مرسل لكنه اعتضد بمجيئه من وجه آخر موصولا، أخرجه أبو داود من حديث أنس، وفى إسناده أبو معقل لا يعرف حاله، فقد اعتضد كل من المرسل، والموصول بالآخر وحصلت القوة فى الصورة المجموعة.','1','89',NULL,NULL,'0');
INSERT INTO [b88] VALUES (90,'<span data-type="title" id=toc-91>٩٠ </span>- عن عثمان فى صفة الوضوء قال: " ومسح مقدم رأسه ".
** ص
(فتح الباري ٢٩٣/١)
** قال الحافظ فى " الفتح " ١ / ٢٩٣: فيه خالد بن يزيد بن أبى مالك مختلف فيه.','1','90',NULL,NULL,'0');
INSERT INTO [b88] VALUES (91,'<span data-type="title" id=toc-92>٩١ </span>- عن جرير بن عبد الله: " كان يقول لأهله: توضؤا من هذا الذى أدخل فيه سواكى.
** قط
(فتح الباري ٢٩٥/١)
** قال الحافظ فى " الفتح " ١ / ٢٩٥: صححه الدارقطنى، وقد روى مرفوعا أخرجه الدارقطنى من حديث أنس، وسنده ضعيف.','1','91',NULL,NULL,'0');
INSERT INTO [b88] VALUES (92,'<span data-type="title" id=toc-93>٩٢ </span>- عن أنس: أن النبى صلى الله عليه وسلم كان يتوضأ بفضل سواكه.
** قط
(فتح الباري ٢٩٥/١)
** إسناده ضعيف','1','92',NULL,NULL,'0');
INSERT INTO [b88] VALUES (93,'<span data-type="title" id=toc-94>٩٣ </span>- الحديث المشهور الذى صححه ابن خزيمة وغيره من طريق عبد الله بن عمرو بن العاص فى صفة الوضوء حيث قال النبى صلى الله عليه وسلم بعد أن فرغ " من زاد على هذا فقد أساء وظلم ".
** لم
(فتح الباري ٢٩٨/١)
** قال الحافظ فى " الفتح " ١ / ٢٩٨: صححه ابن خزيمة.','1','93',NULL,NULL,'0');
INSERT INTO [b88] VALUES (94,'<span data-type="title" id=toc-95>٩٤ </span>- هذا الأثر بلفظ: إن عمر كان يتوضأ بالحميم ويغتسل منه.
** خت ص عب
(فتح الباري ٢٩٩/١)
** إسناده صحيح','1','94',NULL,NULL,'0');
INSERT INTO [b88] VALUES (95,'<span data-type="title" id=toc-96>٩٥ </span>- وروى ابن أبى شيبة والدارقطنى بلفظ: كان يسخن له ماء فى قمقم ثم يغتسل به.
** شب قط
(فتح الباري ٢٩٩/١)
** قال الحافظ فى " الفتح " ١ / ٢٩٩: قال الدارقطنى: إسناده صحيح.','1','95',NULL,NULL,'0');
INSERT INTO [b88] VALUES (96,'<span data-type="title" id=toc-97>٩٦ </span>- عن حميد بن عبد الرحمن الحميدى قال: لقيت رجلا صحب النبى صلى الله عليه وسلم أربع سنين، فقال: نهى رسول الله صلى الله عليه وسلم أن تغتسل المرأة بفضل الرجل أو يغتسل الرجل بفضل المرأة وليغترفا جميعا.
** د ن
(فتح الباري ٣٠٠/١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١ / ٣٠٠: لم أقف عن أعله على حجة قوية ودعوى البيهقى أنه فى معنى المرسل مردودة لأن إبهام الصحابى لا يضر، وقد صرح التابعى بأنه لقيه ودعوى ابن حزم أن داود راويه عن حميد بن عبد الرحمن هو ابن يزيد الأودى وهو ضعيف مردودة فإنه ابن عبد الله الأودى وهو ثقة وقد صرح باسم أبيه أبو داود وغيره اه.','1','96',NULL,NULL,'0');
INSERT INTO [b88] VALUES (97,'<span data-type="title" id=toc-98>٩٧ </span>- عن ميمونة قالت: أجنبت فاغتسلت من جفنة، ففضلت فيها فضلة فجاء النبى صلى الله عليه وسلم يغتسل منه، فقلت له، فقال: " الماء ليس عليه جنابة "، واغتسل منه _ لفظ الدارقطنى _.
** د ت ن جه قط
(فتح الباري ٣٠٠/١)
** قال الحافظ فى " الفتح " ١ / ٣٠٠: أخرجه أصحاب السنن والدارقطنى، وصححه الترمذى وابن خزيمة وغيرهما من حديث ابن عباس عن ميمونه وقد أعله قوم بسماك بن حرب راويه عن عكرمة لأنه كان يقبل التلقين لكن قد رواه عنه شعبة، وهو لايحمل عن مشايخه إلا صحيح حديثهم.','1','97',NULL,NULL,'0');
INSERT INTO [b88] VALUES (98,'<span data-type="title" id=toc-99>٩٨ </span>- عن ابن عباس: أن المقوقس أهدى للنبى صلى الله عليه وسلم قدحا من زجاج.
** حم
(فتح الباري ٣٠٤/١)
** إسناده فيه مقال','1','98',NULL,NULL,'0');
INSERT INTO [b88] VALUES (99,'<span data-type="title" id=toc-100>٩٩ </span>- حديث صفوان بن عسال: أمرنا رسول الله صلى الله عليه وسلم أن نمسح على الخفين إذا نحن أدخلناهما على طهر ثلاثا إذا سافرنا، ويوما وليلة إذا أقمنا.
** خز
(فتح الباري ٣٠٩/١)
** صحيح
** قال الحافظ فى " الفتح " ١ / ٣٠٩: قال ابن خزيمة: ذكرته للمزنى فقال لى: حدث به أصحابنا فإنه أقوى حجة للشافعى اه. وحديث صفوان وإن كان صحيحا لكنه ليس على شرط البخارى.','1','99',NULL,NULL,'0');
INSERT INTO [b88] VALUES (100,'<span data-type="title" id=toc-101>١٠٠ </span>- من طريق سليم بن عامر قال: رأيت أبا بكر وعمر وعثمان أكلوا مما مست النار ولم يتوضؤا.
** خت طب فى مسند الشاميين
(فتح الباري ٣١١/١)
** إسناده حسن','1','100',NULL,NULL,'0');
INSERT INTO [b88] VALUES (101,'<span data-type="title" id=toc-102>١٠١ </span>- حديث جابر قال: كان أخر اللأمرين من رسول الله صلى الله عليه وسلم ترك الوضوء مما مست النار.
** د ن
(فتح الباري ٣١١/١)
** قال الحافظ فى " الفتح " ١ / ٣١١: صححه ابن خزيمة وابن حبان وغيرهما.','1','101',NULL,NULL,'0');
INSERT INTO [b88] VALUES (102,'<span data-type="title" id=toc-103>١٠٢ </span>- عن أنس: أن النبى صلى الله عليه وسلم شرب لبنا فلم يتمضمض ولم يتوضأ.
** د
(فتح الباري ٣١٣/١)
** إسناده حسن','1','102',NULL,NULL,'0');
INSERT INTO [b88] VALUES (103,'<span data-type="title" id=toc-104>١٠٣ </span>- حديث أنس: كان أصحاب رسول الله صلى الله عليه وسلم ينتظرون الصلاة فينعسون حتى تخفق رءوسهم، ثم يقومون إلى الصلاة.
** نصر فى قيام الليل
(فتح الباري ٣١٤/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٣١٤: وأصله عند مسلم.','1','103',NULL,NULL,'0');
INSERT INTO [b88] VALUES (104,'<span data-type="title" id=toc-105>١٠٤ </span>- حديث عبد الله بن حنظلة: أن النبى صلى الله عليه وسلم أمر بالوضوء لكل صلاة فلما شق عليه أمر بالسواك.
** د
(فتح الباري ٣١٦/١)
** قال الحافظ فى " الفتح " ١ / ٣١٦: صححه ابن خزيمة.','1','104',NULL,NULL,'0');
INSERT INTO [b88] VALUES (105,'<span data-type="title" id=toc-106>١٠٥ </span>- حديث أبى هريرة مرفوعا: " أكثر عذاب القبر من البول ".
** لم
(فتح الباري ٣١٨/١)
** قال الحافظ فى " الفتح " ١ / ٣١٨: صححه ابن خزيمة.','1','105',NULL,NULL,'0');
INSERT INTO [b88] VALUES (106,'<span data-type="title" id=toc-107>١٠٦ </span>- حديث أبى رافع: لفظه: كنا مع النبى صلى الله عليه وسلم فى جنازة إذ سمع شيئا فى قبر فقال لبلال: " ائتنى بجريدة خضراء ... "، الحديث.
** ن
(فتح الباري ٣١٩/١)
** إسناده ضعيف','1','106',NULL,NULL,'0');
INSERT INTO [b88] VALUES (107,'<span data-type="title" id=toc-108>١٠٧ </span>- حديث جابر: أن النبى صلى الله عليه وسلم مر على قبرين من بنى النجار هلكا فى الجاهلية، فسمعهما يعذبان فى البول والنميمة.
** أبو موسى المدينى
(فتح الباري ٣٢١/١)
** ضعيف
** قال الحافظ فى " الفتح " ١ / ٣٢١: حديث جابر بسند فيه ابن لهيعة. قال الحافظ فى " الفتح " ١ / ٣٢١: قال أبو موسى: هذا وإن كان ليس بقوى لكن معناه صحيح. وقال الحافظ فى " الفتح " ١ / ٣٢١: الحديث الذى احتج به أبو موسى ضعيف كما اعترف به، وقد رواه أحمد باسناد صحيح على شرط مسلم وليس فيه سبب التعذيب، فهو من تخليط ابن لهيعة.','1','107',NULL,NULL,'0');
INSERT INTO [b88] VALUES (108,'<span data-type="title" id=toc-109>١٠٨ </span>- رواية أبى بكرة: " يعذبان وما يعذبان فى كبير "، " وبلى وما يعذبان إلا فى الغيبة والبول "
** حم طب
(فتح الباري ٣٢١/١)
** إسناده صحيح','1','108',NULL,NULL,'0');
INSERT INTO [b88] VALUES (109,'<span data-type="title" id=toc-110>١٠٩ </span>- عن سليمان بن يسار قال: " اطلع ذو الخويصرة اليمانى وكان رجلا جافيا "، فذكره تاما بمعناه وزيادة.
** أبو موسى المدينى فى الصحابة
(فتح الباري ٣٢٣/١)
** مرسل
** قال الحافظ فى " الفتح " ١ / ٣٢٣: روى ابن ماجه وابن حبان الحديث تاما من طريق محمد بن عمرو عن أبى سلمة عن أبى هريرة وكذا رواه ابن ماجه أيضا من حديث واثلة بن الأسقع. قال الحافظ فى " الفتح " ١ / ٣٢٣: وفى إسناده أيضا مبهم بين محمد بن إسحاق وبين محمد بن عمرو بن عطاء.','1','109',NULL,NULL,'0');
INSERT INTO [b88] VALUES (110,'<span data-type="title" id=toc-111>١١٠ </span>- حديث على مرفوعا فى بول الرضيع: " ينضح بول الغلام ويغسل بول الجارية ".
** حم د ت جه
(فتح الباري ٣٢٥/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٣٢٦: ورواه سعيد عن قتادة فوقفه، وليس ذلك بعلة قادحة.','1','110',NULL,NULL,'0');
INSERT INTO [b88] VALUES (111,'<span data-type="title" id=toc-112>١١١ </span>- حديث لباية بنت الحارث مرفوعا: " إنما يغسل من بول الأنثى وينضح من بول الذكر ".
** حم جه
(فتح الباري ٣٢٦/١)
** قال الحافظ فى " الفتح " ١ / ٣٢٦: صححه ابن خزيمة وغيره.','1','111',NULL,NULL,'0');
INSERT INTO [b88] VALUES (112,'<span data-type="title" id=toc-113>١١٢ </span>- حديث أم سلمة قالت: بال الحسن _ أو الحسين _ على بطن رسول الله صلى الله عليه وسلم فتركه حتى قضى بوله ثم دعا بماء فصبه عليه.
** طس
(فتح الباري ٣٢٦/١)
** إسناده حسن
** قال الحافظ فى " الفتح " ١ / ٣٢٦: ولأحمد عن أبى ليلى نحوه، ورواه الطحاوى من طريقه قال: فجىء بالحسن ولم يتردد وكذا الطبرانى عن أبى أمامة ".','1','112',NULL,NULL,'0');
INSERT INTO [b88] VALUES (113,'<span data-type="title" id=toc-114>١١٣ </span>- حديث عبد الرحمن بن حسنة فيه: بال رسول الله صلى الله عليه وسلم جالسا فقلنا انظروا إليه يبول كما تبول المرأة.
** ن جه
(فتح الباري ٣٢٨/١)
** صحيح
** قال الحافظ فى " الفتح " ١ / ٣٢٨: وهو حديث صحيح صححه الدارقطنى وغيره.','1','113',NULL,NULL,'0');
INSERT INTO [b88] VALUES (114,'<span data-type="title" id=toc-115>١١٤ </span>- حديث أبى هريرة قال: إنما بال رسول الله صلى الله عليه وسلم قائما لجرح كان فى مأبضه.
** ك هق
(فتح الباري ٣٣٠/١)
** قال الحافظ فى " الفتح " ١ / ٣٣٠: ضعفه الدارقطنى والبيهقى.','1','114',NULL,NULL,'0');
INSERT INTO [b88] VALUES (115,'<span data-type="title" id=toc-116>١١٥ </span>- عن همام بن الحارث أن عائشة أنكرت على ضيفها غسله الثوب فقالت: لم أفسد علينا ثوبنا؟ ، إنما كان يكفيه أن يفركه بإصبعه فربما فركته من ثوب رسول الله صلى الله عليه وسلم بأصابعى.
** لم
(فتح الباري ٣٣٣/١)
** قال الحافظ فى " الفتح " ١ / ٣٣٣: صححه الترمذى.','1','115',NULL,NULL,'0');
INSERT INTO [b88] VALUES (116,'<span data-type="title" id=toc-117>١١٦ </span>- حديث أبى هريرة أن خولة بنت يسار قالت: يا رسول الله ليس لى إلا ثوب واحد، وأنا أحيض فكيف أصنع؟ ، قال: " إذا طهرت فاغسليه ثم صلى فيه " قالت: فإن لم يخرج الدم، قال: " يكفيك الماء ولا يضرك أثره ".
** د
(فتح الباري ٣٣٤/١)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ١ / ٣٣٤: وفى إسناده ضعف، وله شاهد مرسل ذكره البيهقى.','1','116',NULL,NULL,'0');
INSERT INTO [b88] VALUES (117,'<span data-type="title" id=toc-118>١١٧ </span>- حديث أم قيس: " مكيه واغسليه بماء وسدر ".
** د
(فتح الباري ٣٣٤/١)
** إسناده حسن','1','117',NULL,NULL,'0');
INSERT INTO [b88] VALUES (118,'<span data-type="title" id=toc-119>١١٨ </span>- حديث أبى هريرة مرفوعا بلفظ: " استنزهوا من البول فإن عامة عذاب القبر منه ".
** لم
(فتح الباري ٣٣٦/١)
** قال الحافظ فى " الفتح " ١ / ٣٣٦: صححه ابن خزيمة وغيره.','1','118',NULL,NULL,'0');
INSERT INTO [b88] VALUES (119,'<span data-type="title" id=toc-120>١١٩ </span>- حديث سلمة بن الأكوع قال: كان للنبى صلى الله عليه وسلم غلاما يقال له يسار ".
** طب
(فتح الباري ٣٣٩/١)
** إسناده صالح','1','119',NULL,NULL,'0');
INSERT INTO [b88] VALUES (120,'<span data-type="title" id=toc-121>١٢٠ </span>- عن عائشة أن النبى صلى الله عليه وسلم أمرهم ببناء المساجد فى الدور وأن تطيب وتنظف.
** حم د
(فتح الباري ٣٤٢/١)
** صحيح
** قال الحافظ فى " الفتح " ١ / ٣٤٢: صححه ابن خزيمة وغيره، ولأبى داود نحوه من حديث سمرة وزاد: " وأن نطهرها ".','1','120',NULL,NULL,'0');
INSERT INTO [b88] VALUES (121,'<span data-type="title" id=toc-122>١٢١ </span>- حديث ابن عباس مرفوعا: " الماء لاينجسه شىء ".
** د ت ن جه خز
(فتح الباري ٣٤٢/١)
** صحيح','1','121',NULL,NULL,'0');
INSERT INTO [b88] VALUES (122,'<span data-type="title" id=toc-123>١٢٢ </span>- عن أبى هريرة ولفظه: سئل رسول الله صلى الله عليه وسلم عن الفأرة تقع فى السمن، قال: " كان جامدا فألقوها وما حولها، وإن كان مائعا فلا تقربوه ".
** عب
(فتح الباري ٣٤٤/١)
** قال الحافظ فى " الفتح " ١ / ٣٤٤: حكى الترمذى عن البخارى أنه قال فى رواية معمر هذه: هى خطأ وقال ابن أبى حاتم عن أبيه: إنها وهم اه. وأشار الترمذى إلى أنها شاذة وقال الذهبى فى الزهريات: الطريقان عندنا محفوظان، لكن طريق ابن عباس عن ميمونة أشهر والله أعلم.','1','122',NULL,NULL,'0');
INSERT INTO [b88] VALUES (123,'<span data-type="title" id=toc-124>١٢٣ </span>- عن ابن عمر أنه كان إذا كان فى الصلاة فرأى فى ثوبه دما فاستطاع أن يضعه وضعه، وإن لم يستطع خرج فغسله ثم جاء، فيبنى على ماكان صلى.
** شب خت
(فتح الباري ٣٤٨/١)
** إسناده صحيح','1','123',NULL,NULL,'0');
INSERT INTO [b88] VALUES (124,'<span data-type="title" id=toc-125>١٢٤ </span>- حديث أبى سعيد: أنه صلى الله عليه وسلم خلع نعليه فى الصلاة ثم قال: "إن جبريل أخبرنى أن فيهما قذرا ".
** حم د
(فتح الباري ٣٤٨/١)
** قال الحافظ فى " الفتح " ١ / ٣٤٨: صححه ابن خزيمة. وله شاهد من حديث ابن مسعود، أخرجه الحاكم.','1','124',NULL,NULL,'0');
INSERT INTO [b88] VALUES (125,'<span data-type="title" id=toc-126>١٢٥ </span>- حديث ابن مسعود حيث قال له النبى صلى الله عليه وسلم ليلة الجن ما فى إداوتك؟ ، قال: نبيذ، قال: " ثمرة طيبة وماء طهور ". رواه أبو داود والترمذى وزاد: " فتوضأ به ".
** د ت
(فتح الباري ٣٥٤/١)
** قال الحافظ فى " الفتح " ١ / ٣٥٤: هذا الحديث أطبق علماء السلف على تضعيفه.','1','125',NULL,NULL,'0');
INSERT INTO [b88] VALUES (126,'<span data-type="title" id=toc-127>١٢٦ </span>- عن عائشة قالت: كان رسول الله صلى الله عليه وسلم يستن وعنده رجلان فأوحى إليه أن أعط السواك الأكبر.
** د
(فتح الباري ٣٥٧/١)
** إسناده حسن','1','126',NULL,NULL,'0');
INSERT INTO [b88] VALUES (127,'<span data-type="title" id=toc-128>١٢٧ </span>- عن عائشة أنها وصفت غسل رسول الله صلى الله عليه وسلم من الجنابة الحديث وفيه: " ثم يتمضمض ثلاثا ويستنشق ثلاثا، ويغسل وجهه ثلاثا ويديه ثلاثا ثم يفيض على رأسه ثلاثا ".
** ن هق
(فتح الباري ٣٦١/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٣٦١: ورد ذلك من طريق صحيحة أخرجها النسائى والبيهقى من رواية أبى سلمة عن عائشة.','1','127',NULL,NULL,'0');
INSERT INTO [b88] VALUES (128,'<span data-type="title" id=toc-129>١٢٨ </span>- حديث ضعيف أورده الرافعى وغيره بلفظ: " لا تنفضوا أيديكم فى الوضوء فإنها مراوح الشيطان ".
** حب فى الضعفاء ابن أبى حاتم فى العلل
(فتح الباري ٣٦٢/١)
** ضعيف
** قال الحافظ فى " الفتح " ١ / ٣٦٢ _ ٣٦٣: قال ابن الصلاح: لم أجده اه، وتبعه النووى. وقال الحافظ فى " الفتح " ١ / ٣٦٣: ولو لم يعارضه الحديث الصحيح لم يكن صالحا أن يحتج به.','1','128',NULL,NULL,'0');
INSERT INTO [b88] VALUES (129,'<span data-type="title" id=toc-130>١٢٩ </span>- روى عن ابن مسعود: أنه كان يغسل رأسه بخطمى ويكتفى بذلك فى غسل الجنابة. ورواه أبو داود مرفوعا عن عائشة.
** د
(فتح الباري ٣٧٠/١)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ١ / ٣٧٠: روى عن ابن مسعود كما أخرجه ابن أبى شيبة وغيره عنه ورواه أبو داود مرفوعا عن عائشة بإسناد ضعيف.','1','129',NULL,NULL,'0');
INSERT INTO [b88] VALUES (130,'<span data-type="title" id=toc-131>١٣٠ </span>- حديث زيد بن أرقم رفعه: " إن الرجل من أهل الجنة ليعطى قوة مائة فى الأكل ولاشرب والجماع والشهوة ".
** حم ن
(فتح الباري ٣٧٨/١)
** قال الحافظ فى " الفتح " ١ / ٣٧٨: صححه الحاكم.','1','130',NULL,NULL,'0');
INSERT INTO [b88] VALUES (131,'<span data-type="title" id=toc-132>١٣١ </span>- قال ابن أبى شيبة: حدثنا يزيد بن هارون حدثنا بهز بن حكيم عن أبيه عن جده قال: قلت: يانبى الله عوراتنا ما نأتى منها وما نذر؟ ، قال: " احفظ عورتك إلا من زوجتك أو ماملكت يمينك "، قلت: يا رسول الله أحدنا إذا كان خاليا؟ ، قال: " الله أحق أن يستحى منه من الناس ".
** شب د ت ن ى خت
(فتح الباري ٣٨٦/ا)
** قال الحافظ فى " الفتح " ١ / ٣٨٦: قد أخرجه أصحاب السنن وغيرهم من طرق عن بهز وحسنه الترمذى وصححه الحاكم فالإسناد إلى بهز صحيح، ولهذا جزم به البخارى.','1','131',NULL,NULL,'0');
INSERT INTO [b88] VALUES (132,'<span data-type="title" id=toc-133>١٣٢ </span>- ورد عن على مرفوعا: " إن الملائكة لا تدخل بيتا فيه كلب، ولا صورة ولا جنب ".
** د
(فتح الباري ٣٩٢/١)
** قال الحافظ فى " الفتح " ١ / ٣٩٢: قيل: أشار المصنف بهذه الترجمة إلى تضعيف ماورد عن على مرفوعا، وفيه نجى الحضرمى، ماروى عنه غير ابنه عبد الله فهو مجهول، لكن وثقه العجلى وصحح حديثه ابن حبان والحاكم.','1','132',NULL,NULL,'0');
INSERT INTO [b88] VALUES (133,'<span data-type="title" id=toc-134>١٣٣ </span>- عن شداد بن أوس الصحابى قال: إذا أجنب أحدكم من الليل ثم أراد أن ينام فليتوضأ فإنه نصف غسل الجنابة ".
** شب
(فتح الباري ٣٩٤/١)
** رجاله ثقات','1','133',NULL,NULL,'0');
INSERT INTO [b88] VALUES (134,'<span data-type="title" id=toc-135>١٣٤ </span>- عن عائشة: أنه صلى الله عليه وسلم كان إذا أجنب فاراد أن ينام توضأ أو تيمم.
** هق
(فتح الباري ٣٩٤/١)
** إسناده حسن','1','134',NULL,NULL,'0');
INSERT INTO [b88] VALUES (135,'<span data-type="title" id=toc-136>١٣٥ </span>- روى البيهقى من طريق ابن أبى عروبة عن قتادة مختصرا ولفظه: " إذا التقى الختانان فقد وجب الغسل ". وروى أيضا بهذا اللفظ من حديث عائشة أخرجه الشافعى.
** جه
(فتح الباري ٣٩٥/١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١ / ٣٩٥: أخرجه الشافعى من طريق سعيد بن المسيب عنها وفى إسناده على بن زيد وهو ضعيف، وابن ماجه من طريق القاسم بن محمد عنها ورجاله ثقات، ورواه مسلم من طريق أبى موسى الأشعرى عنها.','1','135',NULL,NULL,'0');
INSERT INTO [b88] VALUES (136,'<span data-type="title" id=toc-137>١٣٦ </span>- روى أحمد وغيره من طريق الزهرى عن سهل بن سعد قال: حدثنى أبى بن كعب أن الفتيا التى كانوا يقولون: الماء من الماء، رخصة كان رسول الله صلى الله عليه وسلم رخص بها فى أول الإسلام ثم أمر بالإغتسال بعد.
** حم د خز
(فتح الباري ٣٩٧/١)
** إسناده صالح للإحتجاج
** قال الحافظ فى " الفتح " ١ / ٣٩٧: صححه ابن خزيمة وابن حبان وقال الأسماعيلى: صحيح على شرط البخارى، كذا قال وكأنه لم يطلع على علته فقد اختلفوا فى كون الزهرى سمعه من سهل اه. نعم أخرجه أبو داود وابن خزيمة أيضا من طريق أبى حازم عن سهل ولهذا الإسناد أيضا علة أخرى ذكرها ابن أبى حاتم وفى الجملة هو إسناد صالح لأن يحتج به.','1','136',NULL,NULL,'0');
INSERT INTO [b88] VALUES (137,'<span data-type="title" id=toc-138>١٣٧ </span>- عن ابن مسعود، قال: " كان الرجال والنساء من بنى إسرائيل يصلون جميعا فكانت المرأة تتشرف للرجل، فألقى الله عليهن الحيض ومنعهن المساجد ".
** عب
(فتح الباري ٤٠٠/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٤٠٠: وعند عبد الرازق عن عائشة نحوه.','1','137',NULL,NULL,'0');
INSERT INTO [b88] VALUES (138,'<span data-type="title" id=toc-139>١٣٨ </span>- عن ابن عباس: " ان ابتداء الحيض كان على حواء بعد أن هبطت من الجنة ".
** ك ابن المنذر
(فتح الباري ٤٠٠/١)
** إسناده صحيح','1','138',NULL,NULL,'0');
INSERT INTO [b88] VALUES (139,'<span data-type="title" id=toc-140>١٣٩ </span>- عن عكرمة عن بعض أزواج النبى صلى الله عليه وسلم، أنه كان إذا أراد من الحائض شيئا ألقى على فرجها ثوبا.
** د
(فتح الباري ٤٠٤/١)
** إسناده قوى','1','139',NULL,NULL,'0');
INSERT INTO [b88] VALUES (140,'<span data-type="title" id=toc-141>١٤٠ </span>- عن أم سلمة أن النبى صلى الله عليه وسلم كان يتقى سورة الدم ثلاثا ثم يباشرها بعد ذلك.
** جه
(فتح الباري ٤٠٤/١)
** إسناده حسن','1','140',NULL,NULL,'0');
INSERT INTO [b88] VALUES (141,'<span data-type="title" id=toc-142>١٤١ </span>- حديث على: كان رسول الله صلى الله عليه وسلم لا يحجبه عن القرآن شىء ليس الجنابة.
** د ن ت جه
(فتح الباري ٤٠٨/١)
** حسن
** قال الحافظ فى " الفتح " ١ / ٤٠٨: رواه أصحاب السنن، وصححه الترمذى وابن حبان، وضعف بعض رواته والحق أنه من قبيل الحسن _ يصلح للحجة.','1','141',NULL,NULL,'0');
INSERT INTO [b88] VALUES (142,'<span data-type="title" id=toc-143>١٤٢ </span>- حديث ابن عمر مرفوعا: " لا تقرأ الحائض ولا الجنب شيئا من القرآن ".
** لم
(فتح الباري ٤٠٩/١)
** ضعيف
** قال الحافظ فى " الفتح " ١ / ٤٠٩: ضعيف من جميع طرقه.','1','142',NULL,NULL,'0');
INSERT INTO [b88] VALUES (143,'<span data-type="title" id=toc-144>١٤٣ </span>- عن ابن مسعود قال: " إذا وقعت النطفة فى الرحم، بعث الله ملكا فقال: يارب مخلقة أو غير مخلقة؟ ، فإن قال: غير مخلقة مجها الرحم دما، وإن قال: مخلقة، قال: يارب فما صفة هذه النطفة؟ "، فذكر الحديث.
** الطبرى
(فتح الباري ٤١٩/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٤١٩: وهو موقوف لفظا مرفوع حكما.','1','143',NULL,NULL,'0');
INSERT INTO [b88] VALUES (144,'<span data-type="title" id=toc-145>١٤٤ </span>- عن ابن عمر قال: " لا يحل لها إن كانت حائضا أن تكتم حيضها، ولا إن كانت حاملا أن تكتم حملها ".
** الطبرى
(فتح الباري ٤٢٥/١)
** إسناده حسن','1','144',NULL,NULL,'0');
INSERT INTO [b88] VALUES (145,'<span data-type="title" id=toc-146>١٤٥ </span>- عن عامر هو الشعبى، قال: جاءت امرأة إلى على تخاصم زوجها طلقها، فقالت: حضت فى شهر ثلاث حيض فقال على لشريح: اقض بينهما قال: يا أمير المؤمنين وأنت ههنا؟ ، قال: اقض بينهما، قال: إذا جاءت من بطانة أهلها ممن يرضى دينه وأمانته وتزعم أنها حاضت ثلاث حيض، تطهر عند كل قرء، وتصلى جاز لها
وإلا فلا، قال على: قالون، قال: وقالون _ بلسان الروم _: أحسنت.
** خت دى
(فتح الباري ٤٢٥/١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١ / ٤٢٥: قال البخارى: ويذكر عن على اه. وإنما لم يجزم البخارى به للتردد فى سماع الشعبى من على.','1','145',NULL,NULL,'0');
INSERT INTO [b88] VALUES (146,'<span data-type="title" id=toc-147>١٤٦ </span>- عن عكرمة قال: كانت أم حبيبة تستحاض وكان زوجها يغشاها.
** د
(فتح الباري ٤٢٩/١)
** قال الحافظ فى " الفتح " ١ / ٤٢٩: وهو حديث صحيح إن كان عكرمة سمعه منها.','1','146',NULL,NULL,'0');
INSERT INTO [b88] VALUES (147,'<span data-type="title" id=toc-148>١٤٧ </span>- عن عائشة قالت: لما كان من أمر عقدى ما كان، وقال أهل الإفك ما قالوا خرجت مع رسول الله صلى الله عليه وسلم فى غزوة أخرى فسقط أيضا عقدى حتى حبس الناس على التماسه، فقال لى أبو بكر: يا بنية فى كل سفرة تكونين عناء وبلاء على الناس، فانزل الله عز وجل الرخصة فى التيمم، فقالى أبو بكر: إنك لمباركة ثلاثا.
** الطبرانى
(فتح الباري ٤٣٥/١)
** إسناده فيه مقال
** قال الحافظ فى " الفتح " ١ / ٤٣٥: فى إسناده محمد بن حميد الرازى، وفيه مقال.','1','147',NULL,NULL,'0');
INSERT INTO [b88] VALUES (148,'<span data-type="title" id=toc-149>١٤٨ </span>- عن أنس مرفوعا: " جعلت لى كل أرض طيبة مسجدا وطهورا ".
** ابن المنذر جا
(فتح الباري ٤٣٨/١)
** إسناده صحيح','1','148',NULL,NULL,'0');
INSERT INTO [b88] VALUES (149,'<span data-type="title" id=toc-150>١٤٩ </span>- فى حديث على: " وجعل التراب لى طهورا ".
** حم هق
(فتح الباري ٤٣٨/١)
** إسناده حسن','1','149',NULL,NULL,'0');
INSERT INTO [b88] VALUES (150,'<span data-type="title" id=toc-151>١٥٠ </span>- حديث: " لا صلاة لجار المسجد إلا فى المسجد ".
** قط
(فتح الباري ٤٣٩/١)
** ضعيف','1','150',NULL,NULL,'0');
INSERT INTO [b88] VALUES (151,'<span data-type="title" id=toc-152>١٥١ </span>- حديث أبى هريرة مرفوعا: " الصعيد الطيب وضوء المسلم يكفيه من الماء ".
** بز
(فتح الباري ٤٤٦/١)
** قال الحافظ فى " الفتح " ١ / ٤٤٦: صححه ابن القطان، لكن قال الدارقطنى: إن الصواب إرساله.','1','151',NULL,NULL,'0');
INSERT INTO [b88] VALUES (152,'<span data-type="title" id=toc-153>١٥٢ </span>- عن أبى ذر ولفظه: " إن الصعيد الطيب طهور المسلم وإن لم يجد الماء عشر سنين ".
** د ت ن جه
(فتح الباري ٤٤٦/١)
** قال الحافظ فى " الفتح " ١ / ٤٤٦: صححه الترمذى وابن حبان والدارقطنى.','1','152',NULL,NULL,'0');
INSERT INTO [b88] VALUES (153,'<span data-type="title" id=toc-154>١٥٣ </span>- عن زيد بن أسلم مرسلا: عرس رسول الله صلى الله عليه وسلم ليلة بطريق مكة ووكل بلالا.
** ما
(فتح الباري ٤٤٨/١)
** مرسل','1','153',NULL,NULL,'0');
INSERT INTO [b88] VALUES (154,'<span data-type="title" id=toc-155>١٥٤ </span>- عن ابن عباس قال: الصابئون ليس لهم من كتاب.
** ابن مردويه
(فتح الباري ٤٥٤/١)
** إسناده حسن','1','154',NULL,NULL,'0');
INSERT INTO [b88] VALUES (155,'<span data-type="title" id=toc-156>١٥٥ </span>- عن عمرو بن العاص قال: احتلمت فى ليلة باردة فى غزوة ذات السلاسل فأشفقت أن أغتسل فأهلك، فتيممت ثم صليت بأصحابى الصبح فذكروا ذلك للنبى صلى الله عليه وسلم فقال: " يا عمرو صليت بأصحابك وأنت جنب؟ " فأخبرته بالذى منعنى من الإغتسال وقلت: إنى سمعت الله يقول: ولاتقتلوا أنفسكم إن الله كان بكم رحيما، فضحك رسول الله صلى الله عليه وسلم ولم يقل شيئا.
** د ك خت
(فتح الباري ٤٥٤/١)
** إسناده قوى','1','155',NULL,NULL,'0');
INSERT INTO [b88] VALUES (156,'<span data-type="title" id=toc-157>١٥٦ </span>- فى حديث أبى هريرة: " فإذا عن يمينه باب يخرج منه ريح طيبة، وعن شماله باب يخرج منه ريح خبيثة، إذا نظر عن يمينه استبشر، وإن نظر عن شماله حزن ".
** طب بز
(فتح الباري ٤٦٢/١)
** إسناده ضعيف','1','156',NULL,NULL,'0');
INSERT INTO [b88] VALUES (157,'<span data-type="title" id=toc-158>١٥٧ </span>- عن سلمة بن الأكوع قال: قلت: يارسول الله إنى رجل أتصيد، أفأصلى فى القميص الواحد؟ ، قال: " نعم، زره ولو بشوكة ".
** خت تخ د خز حب
(فتح الباري ٤٦٥/١)
** قال الحافظ فى " الفتح " ١ / ٤٦٥: قال البخارى: فى إسناده نظر اه. قال الحافظ فى " الفتح " ١ / ٤٦٥: وفى رواية عطاف صرح بالتحديث بين موسى وسلمة فاحتمل أن يكون رواية أبى أويس فى المزيد فى متصل الأسانيد أو يكون التصريح فى رواية عطاف وهما، فهز وجه النظر فى إسناده.','1','157',NULL,NULL,'0');
INSERT INTO [b88] VALUES (158,'<span data-type="title" id=toc-159>١٥٨ </span>- عن معاوية بن أبى سفيان أنه سأل أخته أم حبيبة: هل كان رسول الله صلى الله عليه وسلم يصلى فى الثوب الذى يجامع فيه؟ ، قالت: نعم إذا لم ير فيه أذى.
** د ن
(فتح الباري ٤٦٦/١)
** قال الحافظ فى " الفتح " ١ / ٤٦٦: صححه ابن خزيمة وابن حبان.','1','158',NULL,NULL,'0');
INSERT INTO [b88] VALUES (159,'<span data-type="title" id=toc-160>١٥٩ </span>- حديث أبى بكر الصديق أن النبى صلى الله عليه وسلم بعثه: " لا يحج بعد العام مشرك ولا يطوف بالبيت عريان "، الحديث.
** حم
(فتح الباري ٤٦٦/١)
** إسناده حسن','1','159',NULL,NULL,'0');
INSERT INTO [b88] VALUES (160,'<span data-type="title" id=toc-161>١٦٠ </span>- عن محمد بن جحش: مر النبى صلى الله عليه وسلم وأنا معه على معمر وفخذاه مكشوفتان، فقال: " يا معمر غط عليك فخذيك فإن الفخذين عورة ".
** حم تخ ك ابن قانع خت
(فتح الباري ٤٧٩/١)
** قال الحافظ فى " الفتح " ١ / ٤٧٩: رجاله رجال الصحيح، غير أبى كثير فقد روى عنه جماعة لكن لم أجد فيه تصريحا بتعديل.','1','160',NULL,NULL,'0');
INSERT INTO [b88] VALUES (161,'<span data-type="title" id=toc-162>١٦١ </span>- أن النبى صلى الله عليه وسلم أتى بخميصتين سوداوين فلبس أحداهما وبعث الأخرى إلى أبى جهم.
** الزبير بن بكار
(فتح الباري ٤٨٣/١)
** مرسل','1','161',NULL,NULL,'0');
INSERT INTO [b88] VALUES (162,'<span data-type="title" id=toc-163>١٦٢ </span>- حديث عبد الله بن عمرو، قال: مر بالنبى صلى الله عليه وسلم رجل وعليه ثوبان أحمران، فسلم عليه فلم يرد عليه.
** د
(فتح الباري ٤٨٥/١)
** إسناده ضعيف','1','162',NULL,NULL,'0');
INSERT INTO [b88] VALUES (163,'<span data-type="title" id=toc-164>١٦٣ </span>- عن صالح مولى التوأمة قال: صليت مع أبى هريرة فوق المسجد بصلاة الإمام.
** شب
(فتح الباري ٤٨٦/١)
** قال الحافظ فى " الفتح " ١ / ٤٨٦: صالح فيه ضعف، لكن رواه سعيد بن منصور من وجه آخر عن أبى هريرة فاعتضد.','1','163',NULL,NULL,'0');
INSERT INTO [b88] VALUES (164,'<span data-type="title" id=toc-165>١٦٤ </span>- حديث جابر: أن رسول الله صلى الله عليه وسلم كان يصلى إلى سارية فى المسجد ويخطب إليها ويعتمد عليها، فأمرت عائشة فصنعت له منبره هذا. فذكر الحديث.
** طس
(فتح الباري ٤٨٧/١)
** إسناده ضعيف','1','164',NULL,NULL,'0');
INSERT INTO [b88] VALUES (165,'<span data-type="title" id=toc-166>١٦٥ </span>- عن عبد الرحمن بن صفوان قال: فلما خرج سألت من كان معه فقالوا: صلى ركعتين عند السارية الوسطى.
** طب
(فتح الباري ٥٠١/١)
** إسناده صحيح','1','165',NULL,NULL,'0');
INSERT INTO [b88] VALUES (166,'<span data-type="title" id=toc-167>١٦٦ </span>- عن شيبة بن عثمان قال: لقد صلى ركعتين عند العمودين.
** طب
(فتح الباري ٥٠١/١)
** إسناده جيد','1','166',NULL,NULL,'0');
INSERT INTO [b88] VALUES (167,'<span data-type="title" id=toc-168>١٦٧ </span>- عن عمارة بن رويبة قال: كنا مع النبى صلى الله عليه وسلم فى إحدى صلاتى العشى حين حرفت القبلة فدار ودرنا معه فى ركعتين.
** ابن أبى داود
(فتح الباري ٥٠٣/١)
** إسناده ضعيف
** تعقيب: قال أبو نعيم حدثنا سليمان بن أحمد ___ الخ الحديث (٦٨٤٤) ، من جامع المسانيد لأبن كثير. قال أبو نعيم: كذا قال عمارة بن رؤيبة وإنما هو عمارة بن أوس اه. وانظر الهيثمى فى مجمع الزوائد (٢ / ١٣) ، وقال: رواه الطبرانى فى الكبير وفيه عبد الملك بن حسين أبو مالك لنخعى __ وهو ضعيف.','1','167',NULL,NULL,'0');
INSERT INTO [b88] VALUES (168,'<span data-type="title" id=toc-169>١٦٨ </span>- عن أنس: انصرف رسول الله صلى الله عليه وسلم عن بيت المقدس وهو يصلى الظهر بوجهه إلىالكعبة.
** بز طب
(فتح الباري ٥٠٣/١)
** ضعيف
** قال الحافظ فى " الفتح " ١ / ٥٠٣: أخرج البزار من حديث أنس، والطبرانى نحوه من وجه آخر عن أنس وفى كل منهما ضعف.','1','168',NULL,NULL,'0');
INSERT INTO [b88] VALUES (169,'<span data-type="title" id=toc-170>١٦٩ </span>- وصحح الترمذى من حديث ابن عمر أنه قال: " ما نزل بالناس أمر فقالوا فيه وقال فيه عمر إلا نزل القرآن فيه على نحو ما قال عمر ".
** ت
(فتح الباري ٥٠٥/١)
** قال الحافظ فى " الفتح " ١ / ٥٠٥: صححه الترمذى.','1','169',NULL,NULL,'0');
INSERT INTO [b88] VALUES (170,'<span data-type="title" id=toc-171>١٧٠ </span>- قال ابن عباس: " إن وطئت على قذر رطب فاغسله، وإن كان يابسا فلا ".
** خت شب
(فتح الباري ٥١٠/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٥١٠: هذا التعليق وصله ابن أبى شيبة بسند صحيح وقال فى آخره وإن كان ناسيا لم يضره.','1','170',NULL,NULL,'0');
INSERT INTO [b88] VALUES (171,'<span data-type="title" id=toc-172>١٧١ </span>- حديث سعد بن أبى وقاص مرفوعا: " من تنخم فى المسجد فليغيب نخامته أن تصيب جلد مؤمن أو ثوبه فتؤذيه ".
** حم
(فتح الباري ٥١٢/١)
** إسناده حسن','1','171',NULL,NULL,'0');
INSERT INTO [b88] VALUES (172,'<span data-type="title" id=toc-173>١٧٢ </span>- حديث أبى أمامة مرفوعا: " من تنخع فى المسجد فلم يدفنه فسيئة، وإن دفنه فحسنة ".
** حم طب
(فتح الباري ٥١٢/١)
** إسناده حسن','1','172',NULL,NULL,'0');
INSERT INTO [b88] VALUES (173,'<span data-type="title" id=toc-174>١٧٣ </span>- حديث عبد الله بن الشخير: أنه صلى مع النبى صلى الله عليه وسلم فبصق تحت قدمه اليسرى ثم دلكه بنعله.
** د
(فتح الباري ٥١٢/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٥١٢: وأصله فى مسلم.','1','173',NULL,NULL,'0');
INSERT INTO [b88] VALUES (174,'<span data-type="title" id=toc-175>١٧٤ </span>- حديث عوف بن مالك الأشجعى قال: خرج رسول الله صلى الله عليه وسلم وبيده عصا، وقد علق رجل قنا حشف فجعل يطعن ذلك القنو، ويقول: " لو شاء رب هذه الصدقة تصدق بأطيب من هذا ".
** ن
(فتح الباري ٥١٦/١)
** إسناده قوى
** قال الحافظ فى " الفتح " ١ / ٥١٦: وليس هو على شرط البخارى وإن كان إسناده قويا.','1','174',NULL,NULL,'0');
INSERT INTO [b88] VALUES (175,'<span data-type="title" id=toc-176>١٧٥ </span>- عن أبى هريرة أن النبى صلى الله عليه وسلم قال لمن تكلم فيه: " أليس قد شهد بدرا ".
** ابن عبد البر
(فتح الباري ٥٢١/١)
** إسناده حسن','1','175',NULL,NULL,'0');
INSERT INTO [b88] VALUES (176,'<span data-type="title" id=toc-177>١٧٦ </span>- حديث عبد الله بن عمر أن النبى صلى الله عليه وسلم كان يصلى فى مرابض الغنم ولا يصلى فى مرابض الإبل والبقر.
** حم
(فتح الباري ٥٢٧/١)
** إسناده ضعيف','1','176',NULL,NULL,'0');
INSERT INTO [b88] VALUES (177,'<span data-type="title" id=toc-178>١٧٧ </span>- عن أبى سعيد الخدرى مرفوعا: " الأرض كلها مسجد إلا المقبرة والحمام ".
** د ت
(فتح الباري ٥٢٩/١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١ / ٥٢٩: لكن اختلف فى وصله وإرساله وحكم مع ذلك بصحته الحاكم وابن حبان.','1','177',NULL,NULL,'0');
INSERT INTO [b88] VALUES (178,'<span data-type="title" id=toc-179>١٧٨ </span>- حديث ابن عباس: عن أبى بكر مرفوعا: " ما قبض نبى إلا دفن حيث قبض ".
** جه
(فتح الباري ٥٢٩/١)
** قال الحافظ فى " الفتح " ١ / ٥٢٩: وفى إسناده حسين بن عبد الله الهاشمى وهو ضعيف وله طريق آخرى مرسلة ذكرها البيهقى فى الدلائل.','1','178',NULL,NULL,'0');
INSERT INTO [b88] VALUES (179,'<span data-type="title" id=toc-180>١٧٩ </span>- عن سالم بن عبيد الأشجعى الصحابى عن أبى بكر الصديق أنه قيل له: فأين يدفن رسول الله صلى الله عليه وسلم؟ ، قال: فى المكان الذى قبض الله فيه روحه، فإنه لم يقبض روحه إلا فى مكان طيب.
** تم نب
(فتح الباري ٥٢٩/١)
** إسناده صحيح موقوفا','1','179',NULL,NULL,'0');
INSERT INTO [b88] VALUES (180,'<span data-type="title" id=toc-181>١٨٠ </span>- عن على مرفوعا ولفظه: نهانى حبيبى صلى الله عليه وسلم أن أصلى فى أرض بابل فإنها ملعونة.
** د
(فتح الباري ٥٣٠/١)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ١ / ٥٣٠: فى إسناده ضعف.','1','180',NULL,NULL,'0');
INSERT INTO [b88] VALUES (181,'<span data-type="title" id=toc-182>١٨١ </span>- عن أبى سعيد الخدرى قال: رأيت رجلا جاء بخاتم وجده بالحجر فى بيوت المعذبين فأعرض عنه النبى صلى الله عليه وسلم واستتر بيده أن ينظر إليه وقال: " ألقه "، فألقاه.
** ك فى الإكليل
(فتح الباري ٥٣١/١)
** إسناده ضعيف','1','181',NULL,NULL,'0');
INSERT INTO [b88] VALUES (182,'<span data-type="title" id=toc-183>١٨٢ </span>- عن عمر مرفوعا: " ما ساء عمل قوم قط إلا ذخرفوا مساجدهم ".
** جه
(فتح الباري ٥٣٩/١)
** قال الحافظ فى " الفتح " ١ / ٥٣٩: رجاله ثقات إلا شيخه جبارة بن المفلس ففيه مقال.','1','182',NULL,NULL,'0');
INSERT INTO [b88] VALUES (183,'<span data-type="title" id=toc-184>١٨٣ </span>- عن أبى سعيد فذكرالحديث فى بناء المسجد وحملهم لبنة لبنة وفيه فقال أبو سعيد: فحدثنى أصحابى ولم أسمعه من رسول الله صلى الله عليه وسلم أنه قال: " يا ابن سميه تقتلك الفئة الباغية ".
** بز
(فتح الباري ٥٤٢/١)
** إسناده على شرط مسلم','1','183',NULL,NULL,'0');
INSERT INTO [b88] VALUES (184,'<span data-type="title" id=toc-185>١٨٤ </span>- ويؤيده قوله فى رواية أم حبيبة " من بنى لله بيتا ".
** سموية فى فوائده
(فتح الباري ٥٤٥/١)
** إسناده حسن','1','184',NULL,NULL,'0');
INSERT INTO [b88] VALUES (185,'<span data-type="title" id=toc-186>١٨٥ </span>- لفظ رواية سعيد بن المسيب: مر عمر فى المسجد وحسان ينشد فقال: كنت أنشد فيه وفيه من هو خير منك، ثم التفت إلى أبى هريرة فقال: أنشدك الله.... الحديث.
** قط
(فتح الباري ٥٤٨/١)
** مرسل
** قال الحافظ فى " الفتح " ١ / ٥٤٨: رواية سعيد للقصة عندهم مرسلة.','1','185',NULL,NULL,'0');
INSERT INTO [b88] VALUES (186,'<span data-type="title" id=toc-187>١٨٦ </span>- عن عمرو بن شعيب عن أبيه عن جده قال: نهى رسول الله صلى الله عليه وسلم عن تناشد الأشعار فى المساجد.
** خز ت
(فتح الباري ٥٤٩/١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١ / ٥٤٩: حسنه الترمذى. قال الحافظ فى " الفتح " ١ / ٥٤٩: إسناده صحيح إلى عمرو فمن يصحح نسخته يصححه.','1','186',NULL,NULL,'0');
INSERT INTO [b88] VALUES (187,'<span data-type="title" id=toc-188>١٨٧ </span>- حديث: " جنبوا مساجدكم صبيانكم ومجانينكم "
** لم
(فتح الباري ٥٤٩/١)
** ضعيف','1','187',NULL,NULL,'0');
INSERT INTO [b88] VALUES (188,'<span data-type="title" id=toc-189>١٨٨ </span>- عن نافع قال: كان عمر يقول: لا تكثروا اللغط فدخل المسجد فإذا هو برجلين قد ارتفعت أصواتهما، فقال: إن مسجدنا هذا لا يرفع فيه الصوت. الحديث.
** عب
(فتح الباري ٥٦١/١)
** منقطع
** قال الحافظ فى " الفتح " ١ / ٥٦١: فيه انقطاع لان نافعا لم يدرك ذلك الزمان.','1','188',NULL,NULL,'0');
INSERT INTO [b88] VALUES (189,'<span data-type="title" id=toc-190>١٨٩ </span>- حديث كعب بن عجرة قال: قال رسول الله صلى الله عليه وسلم: " إذا توضأ أحدكم ثم خرج عامدا إلىالمسجد فلا يشبكن يديه فإنه فى صلاة ".
** د
(فتح الباري ٥٦٦/١)
** قال الحافظ فى " الفتح " ١ / ٥٦٦: صححه ابن خزيمة وابن حبان وفى إسناده اختلاف ضعفه بعضهم بسببه.','1','189',NULL,NULL,'0');
INSERT INTO [b88] VALUES (190,'<span data-type="title" id=toc-191>١٩٠ </span>- روى ابن أبى شيبة بلفظ: " إذا صلى أحدكم فلا يشبكن بين أصابعه فإن التشبيك من الشيطان وإن أحدكم لا يزال فى صلاة ما دام فى المسجد حتى يخرج منه ".
** شب
(فتح الباري ٥٦٦/١)
** إسناده فيه ضعيف ومجهول
** قال الحافظ فى " الفتح " ١ / ٥٦٦: وفى إسناده ضعيف ومجهول.
** تعقيب: * وكذلك أخرجه ابن سعد والإمام أحمد عن مولى لابى سعيد الخدرى.','1','190',NULL,NULL,'0');
INSERT INTO [b88] VALUES (191,'<span data-type="title" id=toc-192>١٩١ </span>- عن أنس مرفوعا: " سترة الإمام سترة لمن خلفه ".
** طس
(فتح الباري ٥٧٢/١)
** قال الحافظ فى " الفتح " ١ / ٥٧٢: قال الطبرانى: تفرد به سويد بن عاصم اه. وسويد ضعيف عندهم وورد أيضا فى حديث موقوف على ابن عمر أخرجه عبد الرزاق.','1','191',NULL,NULL,'0');
INSERT INTO [b88] VALUES (192,'<span data-type="title" id=toc-193>١٩٢ </span>- عن كثير بن كثير بن المطلب عن أبيه عن جده قال: رأيت النبى صلى الله
عليه
وسلم يصلى فى المسجد الحرام ليس بينه وبينهم _ أى الناس _ سترة.
** عب د ت ن جه
(فتح الباري ٥٧٦/١)
** رجاله موثقون
** قال الحافظ فى " الفتح " ١ / ٥٧٦: رجاله ثقات إلا أنه معلول.','1','192',NULL,NULL,'0');
INSERT INTO [b88] VALUES (193,'<span data-type="title" id=toc-194>١٩٣ </span>- حديث على قال: لقد رأيتنا يوم بدر وما فينا إنسان إلا نائم، إلا رسول الله صلى الله عليه وسلم فإنه كان يصلى إلى شجرة يدعو حتى أصبح.
** ن
(فتح الباري ٥٨٠/١)
** إسناده حسن','1','193',NULL,NULL,'0');
INSERT INTO [b88] VALUES (194,'<span data-type="title" id=toc-195>١٩٤ </span>- قال أبو مسعود: فرأيت رسول الله صلى الله عليه وسلم يصلى الظهر حتى تزول الشمس، فذكر الحديث.
** د طب
(فتح الباري ٥/٢)
** قال الحافظ فى " الفتح " ٢ / ٥: صححه ابن خزيمة وغيره من طريق ابن وهب.','1','194',NULL,NULL,'0');
INSERT INTO [b88] VALUES (195,'<span data-type="title" id=toc-196>١٩٥ </span>- عن طلق بن حبيب مرسلا: " إن الرجل ليصلى الصلاة ومافاتته، ولما فاته من وقتها خير له من أهله وماله ".
** ص
(فتح الباري ٦/٢)
** مرسل
** قال الحافظ فى " الفتح " ٢ / ٦: ورواه أيضا عن ابن عمر من قوله.','1','195',NULL,NULL,'0');
INSERT INTO [b88] VALUES (196,'<span data-type="title" id=toc-197>١٩٦ </span>- عن أبى سعيد الخدرى يحدث أنه سمع رسول الله صلى الله عليه وسلم يقول: " أرأيت لو أن رجلا كان معتمل، وبين منزله ومعتمله خمسة أنهار فإذا انطلق إلى معتمله عمل ما شاء الله، فأصابه وسخ أو عرق، فكلما مر بنهر اغتسل منه ... " الحديث.
** بز طب
(فتح الباري ١٢/٢)
** إسناده لابأس به','1','196',NULL,NULL,'0');
INSERT INTO [b88] VALUES (197,'<span data-type="title" id=toc-198>١٩٧ </span>- عن المغيرة بن شعبة، قال: كنا نصلى مع النبى صلى الله عليه وسلم الظهر بالهاجرة، ثم قال لنا: " أبردوا بالصلاة "، الحديث.
** حم جه
(فتح الباري ١٧/٢)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٢ / ١٧: صححه ابن حبان.','1','197',NULL,NULL,'0');
INSERT INTO [b88] VALUES (198,'<span data-type="title" id=toc-199>١٩٨ </span>- عن أبى الدرداء مرفوعا: " من ترك صلاة مكتوبة حتى تفوته ... "، الحديث.
** شب
(فتح الباري ٣٠/٢)
** منقطع
** قال الحافظ فى " الفتح " ٢ / ٣٠: وفى إسناده انقطاع لأن أبا قلابة لم يسمع من أبى الدرداء.','1','198',NULL,NULL,'0');
INSERT INTO [b88] VALUES (199,'<span data-type="title" id=toc-200>١٩٩ </span>- عن ابن عمر رفعه قال: " إن أدنى أهل الجنى منزلة ... "، فذكر الحديث وفيه: " وأكرمهم على الله من ينظر إلى وجهه غدوة وعشية ".
** ت
(فتح الباري ٣٤/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٣٤: فى سنده ضعف.','1','199',NULL,NULL,'0');
INSERT INTO [b88] VALUES (200,'<span data-type="title" id=toc-201>٢٠٠ </span>- عن أبى هريرة بلفظ: " إن الملائكة فيكم يعتقبون ".
** حل
(فتح الباري ٣٥/٢)
** إسناده صحيح','1','200',NULL,NULL,'0');
INSERT INTO [b88] VALUES (201,'<span data-type="title" id=toc-202>٢٠١ </span>- عن أبى هريرة فى قوله تعالى (إن قرآن الفجر كان مشهودا) ، قال: " تشهده ملائكة الليل والنهار ".
** ت ن
(فتح الباري ٣٦/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٣٦: روى ابن مردويه من حديث أبى الدرداء مرفوعا نحوه.','1','201',NULL,NULL,'0');
INSERT INTO [b88] VALUES (202,'<span data-type="title" id=toc-203>٢٠٢ </span>- عن على بن بلال: عن ناس من الأنصار، قالوا: كنا نصلى مع رسول الله صلى الله عليه وسلم المغرب، ثم نرجع فنترامى حتى نأتى ديارنا، فما يخفى علينا مواقع سهامنا.
** حم
(فتح الباري ٤١/٢)
** إسناده حسن','1','202',NULL,NULL,'0');
INSERT INTO [b88] VALUES (203,'<span data-type="title" id=toc-204>٢٠٣ </span>- حديث أبى هريرة: " لولا أن أشق على أمتى لأمرتهم أن يؤخروا العشاء إلى ثلث الليل أو نصفه ".
** ت
(فتح الباري ٤٨/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٨: صححه الترمذى.','1','203',NULL,NULL,'0');
INSERT INTO [b88] VALUES (204,'<span data-type="title" id=toc-205>٢٠٤ </span>- حديث رافع بن خديج قال: قال رسول الله صلى الله عليه وسلم: " أسفروا بالفجر فإنه أعظم للأجر ".
** د ت ن جه
(فتح الباري ٥٥/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٥: رواه أصحاب السنن، وصححه غير واحد.','1','204',NULL,NULL,'0');
INSERT INTO [b88] VALUES (205,'<span data-type="title" id=toc-206>٢٠٥ </span>- عن النبى صلى الله عليه وسلم قال: " لا تصلوا بعد الصبح ولا بعد العصر، إلا أن تكون الشمس نقية "، وفى رواية " مرتفعة ".
** د ن
(فتح الباري ٦١/٢)
** إسناده حسن','1','205',NULL,NULL,'0');
INSERT INTO [b88] VALUES (206,'<span data-type="title" id=toc-207>٢٠٦ </span>- حديث الصنابحى لفظه: " ثم إذا استوت قارنها،فإذا زالت فارقها......... " وفى آخره: ونهى رسول الله صلى الله عليه وسلم عن الصلاة فى تلك الساعات.
** ما
(فتح الباري ٦٣/٢)
** مرسل
** قال الحافظ فى " الفتح " ٢ / ٦٣: حديث مرسل مع قوة رجاله.','1','206',NULL,NULL,'0');
INSERT INTO [b88] VALUES (207,'<span data-type="title" id=toc-208>٢٠٧ </span>- عن أبى قتادة مرفوعا: إنه صلى الله عليه وسلم كره الصلاة نصف النهار إلا يوم الجمعة.
** لم
(فتح الباري ٦٣/٢)
** منقطع
** قال الحافظ فى " الفتح " ٢ / ٦٣: فى إسناده انقطاع، وقد ذكر له البيهقى شواهد ضعيفة إذا ضمت قوى الخبر والله أعلم.','1','207',NULL,NULL,'0');
INSERT INTO [b88] VALUES (208,'<span data-type="title" id=toc-209>٢٠٨ </span>- حديث على: أنه صلى الله عليه وسلم نهى عن الصلاة بعد العصر إلا والشمس مرتفعة.
** د
(فتح الباري ٦٣/٢)
** إسناده صحيح قوى','1','208',NULL,NULL,'0');
INSERT INTO [b88] VALUES (209,'<span data-type="title" id=toc-210>٢٠٩ </span>- عن ابن عباس، قال: إنما صلى النبى صلى الله عليه وسلم الركعتين بعد العصر لأنه اتاه مال فشغله عن الركعتين بعد الظهر، فصلاهما بعد العصر ثم لم يعد.
** ت
(فتح الباري ٦٥/٢)
** قال الحافظ فى " الفتح " ٢ / ٦٥: قال الترمذى: حديث حسن اه. قلت: وهو من رواية جرير عن عطاء، قد سمع منه بعد اختلاطه، وإن صح فهو شاهد لحديث أم سلمة.','1','209',NULL,NULL,'0');
INSERT INTO [b88] VALUES (210,'<span data-type="title" id=toc-211>٢١٠ </span>- عن عبد العزيز بن رفيع قال: بلغنا أن رسول الله صلى الله عليه وسلم قال: " عجلوا صلاة العصر فى يوم الغيم ".
** حل
(فتح الباري ٦٦/٢)
** إسناده قوى مرسلا
** قال الحافظ فى " الفتح " ٢ / ٦٦: إسناده قوى مع إرساله.','1','210',NULL,NULL,'0');
INSERT INTO [b88] VALUES (211,'<span data-type="title" id=toc-212>٢١١ </span>- حديث أبى جمعة: أن رسول الله صلى الله عليه وسلم صلى المغرب يوم الأحزاب فلما سلم قال: " هل علم رجل منكم أنى صليت العصر؟ "، قالوا: لا يارسول الله، فصلى العصر ثم صلى المغرب.
** حم
(فتح الباري ٦٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٦٩: فى صحة هذا الحديث نظر، لأنه مخالف لما فى الصحيحين.','1','211',NULL,NULL,'0');
INSERT INTO [b88] VALUES (212,'<span data-type="title" id=toc-213>٢١٢ </span>- عن عمر: أن النبى صلى الله عليه وسلم كان يسمر هو وأبو بكر فى الأمر من أمور المسلمين وأنا معهم.
** ت
(فتح الباري ٧٤/٢)
** قال الحافظ فى " الفتح " ٢ / ٧٤: رواه الترمذى محسنا.','1','212',NULL,NULL,'0');
INSERT INTO [b88] VALUES (213,'<span data-type="title" id=toc-214>٢١٣ </span>- صح عن عمر: لو أطيق الأذان مع الخلافة لأذنت.
** ص
(فتح الباري ٧٧/٢)
** صحيح','1','213',NULL,NULL,'0');
INSERT INTO [b88] VALUES (214,'<span data-type="title" id=toc-215>٢١٤ </span>- عن عمر: أول من أذن بالصلاة جبريل فى سماء الدنيا، فسمعه عمر وبلال، فسبق عمر بلالا فأخبر النبى صلى الله عليه وسلم ثم جاء بلال، فقال له: " سبقك بها عمر ".
** حارث
(فتح الباري ٧٨/٢)
** إسناده واه','1','214',NULL,NULL,'0');
INSERT INTO [b88] VALUES (215,'<span data-type="title" id=toc-216>٢١٥ </span>- طريق سالم بن عبد الله بن عمر عن أبيه، قال: لما أسرى بالنبى صلى الله عليه وسلم أوحى الله إليه الأذان فنزل به فعلمه بلالا.
** طب
(فتح الباري ٧٨/٢)
** قال الحافظ فى " الفتح " ٢ / ٧٨: وفى إسناده طلحة بن زيد وهو متروك.','1','215',NULL,NULL,'0');
INSERT INTO [b88] VALUES (216,'<span data-type="title" id=toc-217>٢١٦ </span>- عن أنس: أن جبريل أمر النبى صلى الله عليه وسلم بالأذان حين فرضت الصلاة.
** قط فى الأفراد
(فتح الباري ٧٨/٢)
** إسناده ضعيف','1','216',NULL,NULL,'0');
INSERT INTO [b88] VALUES (217,'<span data-type="title" id=toc-218>٢١٧ </span>- عن عائشة مرفوعا: " لما أسرى بى أذن جبريل فظنت الملائكة أنه يصلى بهم فقدمنى فصليت ".
** ابن مردوية
(فتح الباري ٧١/٢)
** قال الحافظ فى " الفتح " ٢ / ٧٨: فيه من لا يعرف.','1','217',NULL,NULL,'0');
INSERT INTO [b88] VALUES (218,'<span data-type="title" id=toc-219>٢١٨ </span>- عن على قال: لما أراد الله أن يعلم رسوله الأذان أتاه جبريل بدابة يقال لها: البراق فركبها.. فذكر الحديث وفيه: إذا خرج ملك من وراء الحجاب فقال: الله أكبر، الله أكبر، وفى آخره: ثم أخذ الملك بيده، فأم بأهل السماء.
** بز
(فتح الباري ٧٨/٢)
** قال الحافظ فى " الفتح " ٢ / ٧٨: فى إسناده زياد بن المنذر أبو الجارود وهو متروك.','1','218',NULL,NULL,'0');
INSERT INTO [b88] VALUES (219,'<span data-type="title" id=toc-220>٢١٩ </span>- عن عبد الله بن الزبير قال: أخذ الأذان من أذان إبراهيم (وأذن فى الناس بالحج) الآية، قال: فأذن رسول الله صلى الله عليه وسلم.
** شيخ
(فتح الباري ٧٩/٢)
** إسناده فيه مجهول','1','219',NULL,NULL,'0');
INSERT INTO [b88] VALUES (220,'<span data-type="title" id=toc-221>٢٢٠ </span>- " أن جبريل نادى بالأذان لآدم حين أهبط من الجنة ".
** حل
(فتح الباري ٧٩/٢)
** إسناده فيه مجاهيل','1','220',NULL,NULL,'0');
INSERT INTO [b88] VALUES (221,'<span data-type="title" id=toc-222>٢٢١ </span>- عن أبى عمير بن أنس عن عمومته من الأنصار، قالوا: اهتم النبى صلى الله عليه وسلم للصلاة كيف يجمع الناس لها، فقال: " انصب راية عند حضور وقت الصلاة فإذا رأوها آذن بعضهم بعضا فلم يعجبه.. " الحديث، وفيه: ذكروا القنع، بضم القاف وسكون النون يعنى البوق، وذكروا الناقوس فانصرف عبد الله بن زيد وهو مهتم فأرى الأذان فغدا على رسول الله صلى الله عليه وسلم، قال: وكان عمر رآه قبل ذلك، فكتمه عشرين يوما ثم أخبر به النبى صلى الله عليه وسلم، فقال: " ما منعك أن تخبرنا؟ "، قال: سبقنى عبد الله بن زيد فاستحييت، فقال رسول الله صلى الله عليه وسلم: " يابلال قم فانظر ما يأمرك به عبد الله بن زيد فافعله ".
** د
(فتح الباري ٨١/٢)
** إسناده صحيح','1','221',NULL,NULL,'0');
INSERT INTO [b88] VALUES (222,'<span data-type="title" id=toc-223>٢٢٢ </span>- عن سعيد بن المسيب: كان اللفظ الذى ينادى به بلال للصلاة: الصلاة جامعة.
** سعد
(فتح الباري ٨٢/٢)
** مرسل','1','222',NULL,NULL,'0');
INSERT INTO [b88] VALUES (223,'<span data-type="title" id=toc-224>٢٢٣ </span>- حديث أبى محذورة: وأمره أن يقيم واحدة واحدة.
** قط
(فتح الباري ٨٤/٢)
** قال الحافظ فى " الفتح " ٢ / ٨٤: حسنه الدارقطنى.','1','223',NULL,NULL,'0');
INSERT INTO [b88] VALUES (224,'<span data-type="title" id=toc-225>٢٢٤ </span>- عن عبد الله بن شبرمة قال: شاح الناس فى الأذان بالقادسية فاختصموا إلى سعد بن أبى وقاص، فأقرع بينهم.
** حل هق
(فتح الباري ٩٦/٢)
** منقطع
** قال الحافظ فى " الفتح " ٢ / ٩٦: وقد وصله سيف بن عمر فى الفتوح والطبرى من طريقه.','1','224',NULL,NULL,'0');
INSERT INTO [b88] VALUES (225,'<span data-type="title" id=toc-226>٢٢٥ </span>- عن سليمان بن صرد: إنه كان يؤذن فى العسكر فيأمر غلامه بالحاجة فى أذانه.
** تخ
(فتح الباري ٩٨/٢)
** إسناده صحيح','1','225',NULL,NULL,'0');
INSERT INTO [b88] VALUES (226,'<span data-type="title" id=toc-227>٢٢٦ </span>- عن نعيم بن النحام، قال: أذن مؤذن النبى صلى الله عليه وسلم للصبح فى ليلة باردة، فتمنيت لو، قال: ومن قعد فلا حرج، فلما، قال: الصلاة خير من النوم قالها.
** عب
(فتح الباري ٩٩/٢)
** إسناده صحيح','1','226',NULL,NULL,'0');
INSERT INTO [b88] VALUES (227,'<span data-type="title" id=toc-228>٢٢٧ </span>- عن أنس: أن سائلا سأل عن وقت الصلاة، فأمر رسول الله صلى الله عليه وسلم بلالا فأذن حين طلع الفجر.......... الحديث.
** ن
(فتح الباري ١٠٣/٢)
** إسناده صحيح','1','227',NULL,NULL,'0');
INSERT INTO [b88] VALUES (228,'<span data-type="title" id=toc-229>٢٢٨ </span>- عن عروة عن امرأة من بنى النجار قالت: كان بلالا يجلس على بيتى وهو أعلى بيت فى المدينة فإذا رأى الفجر تمطأ ثم أذن.
** د
(فتح الباري ١٠٣/٢)
** إسناده حسن','1','228',NULL,NULL,'0');
INSERT INTO [b88] VALUES (229,'<span data-type="title" id=toc-230>٢٢٩ </span>- عن ابن عمر موصولا مرفوعا: أنه ربما كان أخطأ الفجر فأذن قبل طلوعه، وأنه أخطأ مرة فأمره النبى صلى الله عليه وسلم أن يرجع فيقول: " ألا إن العبد نام ".
** د
(فتح الباري ١٠٣/٢)
** رجاله ثقات حفاظ','1','229',NULL,NULL,'0');
INSERT INTO [b88] VALUES (230,'<span data-type="title" id=toc-231>٢٣٠ </span>- عن زياد بن الحارث: إنه أذن قبل الفجر بأمر النبى صلى الله عليه وسلم وأنه استأذنه فى الإقامة فمنعه، إلى أن طلع الفجر فأمره فأقام.
** د
(فتح الباري ١٠٤/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ١٠٤: فى إسناده ضعف.','1','230',NULL,NULL,'0');
INSERT INTO [b88] VALUES (231,'<span data-type="title" id=toc-232>٢٣١ </span>- عن جابر أن النبى صلى الله عليه وسلم قال لبلال: " اجعل بين أذانك وإقامتك قدر ما يفرغ الآكل من أكله والشارب من شربه والمعتصر إذا دخل لقضاء حاجته ".
** ت ك
(فتح الباري ١٠٦/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ١٠٦: وله شاهد من حديث أبى هريرة، ومن حديث سلمان، أخرجهما أبو الشيخ ومن حديث أبى بن كعب، أخرجه عبد الله بن أحمد فى زيادات المسند وكلها واهية.','1','231',NULL,NULL,'0');
INSERT INTO [b88] VALUES (232,'<span data-type="title" id=toc-233>٢٣٢ </span>- عن سالم أبى النضر: أن النبى صلى الله عليه وسلم كان يخرج بعد النداء إلى المسجد، فإن رأى أهل المسجد قليلا جلس حتى يجتمعوا ثم يصلى.
** هق
(فتح الباري ١١٠/٢)
** إسناده قوى مرسلا','1','232',NULL,NULL,'0');
INSERT INTO [b88] VALUES (233,'<span data-type="title" id=toc-234>٢٣٣ </span>- أن ابن عمر كان يؤذن للصبح فى السفر أذانين.
** عب
(فتح الباري ١١٠/٢)
** إسناده صحيح','1','233',NULL,NULL,'0');
INSERT INTO [b88] VALUES (234,'<span data-type="title" id=toc-235>٢٣٤ </span>- حديث أبى المليح عن أبيه: أنهم مطروا يوما فرخص لهم.
** د
(فتح الباري ١١٣/٢)
** إسناده صحيح','1','234',NULL,NULL,'0');
INSERT INTO [b88] VALUES (235,'<span data-type="title" id=toc-236>٢٣٥ </span>- عن سعد القرظ: أن النبى صلى الله عليه وسلم أمر بلالا أن يجعل إصبعيه فى أذنيه.
** جه ك
(فتح الباري ١١٥/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ١١٥: فى إسناده ضعف.','1','235',NULL,NULL,'0');
INSERT INTO [b88] VALUES (236,'<span data-type="title" id=toc-237>٢٣٦ </span>- عن عطاء بن يسار: أنه صلى الله عليه وسلم كبر فى صلاة من الصلوات ثم أشار بيده أن امكثوا.
** ما
(فتح الباري ١٢٢/٢)
** مرسل','1','236',NULL,NULL,'0');
INSERT INTO [b88] VALUES (237,'<span data-type="title" id=toc-238>٢٣٧ </span>- عن الحسن فى رجل يصوم _ يعنى تطوعا _ فتأمره أمه أن يفطر، قال: فليفطر ولا قضاء عليه وله أجر الصوم وأجر البر، قيل: فتنهاه أن يصلى العشاء فى جماعة، قال: ليس ذلك لها، هذه فريضة.
** كتاب الصيام للحسين بن الحسن المروزى
(فتح الباري ١٢٥/٢)
** إسناده صحيح','1','237',NULL,NULL,'0');
INSERT INTO [b88] VALUES (238,'<span data-type="title" id=toc-239>٢٣٨ </span>- عن أبى عمير بن أنس حدثنى عمومتى من الأنصار قالوا: قال رسول الله صلى الله عليه وسلم: " ما يشهدهما منافق "، يعنى العشاء والفجر.
** شب ص
(فتح الباري ١٢٧/٢)
** إسناده صحيح','1','238',NULL,NULL,'0');
INSERT INTO [b88] VALUES (239,'<span data-type="title" id=toc-240>٢٣٩ </span>- عن ابن عباس موقوفا عليه، قال: فضل صلاة الجماعة على صلاة المنفرد خمس وعشرون درجة، قال: فإن كانوا أكثر من ذلك فعلى عدد من فى المسجد، فقال رجل: وإن كانوا عشرة آلاف؟ ، قال: نعم.
** شب
(فتح الباري ١٣٥/٢)
** غير ثابت
** قال الحافظ فى " الفتح " ٢ / ١٣٥: وهذا له حكم الرفع لأنه لا يقال بالرأى، لكنه غير ثابت.','1','239',NULL,NULL,'0');
INSERT INTO [b88] VALUES (240,'<span data-type="title" id=toc-241>٢٤٠ </span>- عن أوس المعافرى، أنه قال لعبد الله بن عمرو بن العاص: أرأيت من توضأ فأحسن الوضوء ثم صلى فى بيته؟ ، قال: حسن جميل، قال: فإن صلى فى مسجد عشيرته؟ ، قال: خمس عشرة صلاة، قال: فإن مشى إلى مسجد جماعة فصلى فيه، قال: خمس وعشرون.
** ص
(فتح الباري ١٣٥/٢)
** إسناده حسن
** قال الحافظ فى " الفتح " ٢ / ١٣٥: وأخرج حميد بن زنجوية فى كتاب الترغيب نحوه من حديث واثلة، وسند ضعيف.','1','240',NULL,NULL,'0');
INSERT INTO [b88] VALUES (241,'<span data-type="title" id=toc-242>٢٤١ </span>- حديث أبى بن كعب مرفوعا: " صلاة الرجل مع الرجل أزكى من صلاته وحده وصلاته مع الرجلين أزكى من صلاته مع الرجل، وماأكثر فهو أحب إلى الله ".
** حم د ت ن جه
(فتح الباري ١٣٦/٢)
** قال الحافظ فى " الفتح " ٢ / ١٣٦: صححه ابن خزيمة وغيره، وله شاهد قوى فى الطبرانى من حديث قباث بن أشيم.','1','241',NULL,NULL,'0');
INSERT INTO [b88] VALUES (242,'<span data-type="title" id=toc-243>٢٤٢ </span>- حديث أبى أمامة: أنه صلى الله عليه وسلم رأى رجلا يصلى وحده، فقال: " ألا رجل يتصدق على هذا فيصلى معه؟ "، فقام رجل فصلى معه، فقال: " هذان جماعة ".
** طس حم
(فتح الباري ١٤٢/٢)
** ضعيف
** قال الحافظ فى " الفتح " ٢ / ١٤٢: (اثنان فما فوقهما جماعة) حديث ورد من طرق ضعيفة، منها فى ابن ماجه من حديث أبى موسى الأشعرى، وفى معجم البغوى من حديث الحكم بن عمير، وفى أفراد الدارقطنى من حديث عبد الله بن عمرو، وفى البيهقى من حديث أنس. القصة المذكورة دون قوله " هذان جماعة "، أخرجهما أبو داود والترمذى من وجه آخر صحيح.','1','242',NULL,NULL,'0');
INSERT INTO [b88] VALUES (243,'<span data-type="title" id=toc-244>٢٤٣ </span>- حديث سلمان: " سبعة يظلهم الله فى ظل عرشه.... " فذكر الحديث.
** ص
(فتح الباري ١٤٤/٢)
** إسناده حسن','1','243',NULL,NULL,'0');
INSERT INTO [b88] VALUES (244,'<span data-type="title" id=toc-245>٢٤٤ </span>- حديث أبى سعيد مرفوعا: " أحب الناس إلى الله يوم القيامة وأقربهم منى مجلساأمام عادل ".
** ت
(فتح الباري ١٤٤/٢)
** قال الحافظ فى " الفتح " ٢ / ١٤٤: حسنه الترمذى.','1','244',NULL,NULL,'0');
INSERT INTO [b88] VALUES (245,'<span data-type="title" id=toc-246>٢٤٥ </span>- عن أنس مرفوعا: " إن الملائكة، قالت: يارب هل من خلقك شىء أشد من الجبال قال: نعم، الحديد، قالت: فهل أشد من الحديث؟ ، قال: نعم، النار قالت: فهل أشد من النار، قال: نعم، الماء، قالت: فهل أشد من الماء، قال: نعم الريح قالت: فهل أشد من الريح؟ ، قال: نعم، ابن آدم يتصدق بيمينه فيخفيها بشماله ".
** حم
(فتح الباري ١٤٧/٢)
** إسناده حسن','1','245',NULL,NULL,'0');
INSERT INTO [b88] VALUES (246,'<span data-type="title" id=toc-247>٢٤٦ </span>- زاد مسلم بن خالد عن عمرو بن دينار فى هذا الحديث: قيل يارسول الله ولا ركعتى الفجر؟ ، قال: " ولا ركعتى الفجر ".
** عد
(فتح الباري ١٤٩/٢)
** إسناده حسن','1','246',NULL,NULL,'0');
INSERT INTO [b88] VALUES (247,'<span data-type="title" id=toc-248>٢٤٧ </span>- " إذا وضع العشاء وأحدكم صائم ... " الحديث.
** حب طس
(فتح الباري ١٦٠/٢)
** صحيح
** قال الحافظ فى " الفتح " ٢ / ١٦٠: زاد ابن حبان والطبرانى فى الأوسط من رواية موسى بن أعين عن عمرو بن الحارث عن ابن شهاب " وأحدكم صائم "، وذكر الطبرانى أن موسى بن أعين تفرد بها اه. وموسى ثقة متفق عليه.','1','247',NULL,NULL,'0');
INSERT INTO [b88] VALUES (248,'<span data-type="title" id=toc-249>٢٤٨ </span>- عن أبى هريرة وابن عباس أنهما كانا يأكلان طعاما وفى التنور شواء فأراد المؤذن أن يقيم، فقال له ابن عباس: لا تعجل لئلا نقوم وفى أنفسنا منه شىء "، وفى رواية ابن أبى شيبة: " لئلا يعرض لنا فى صلاتنا ".
** ص ش
(فتح الباري ١٦١/٢)
** إسناده حسن','1','248',NULL,NULL,'0');
INSERT INTO [b88] VALUES (249,'<span data-type="title" id=toc-250>٢٤٩ </span>- " الصلاة فى أول وقتها ".
** ك قط هق
(فتح الباري ١٠/٢)
** قال الحافظ فى " الفتح " ٢ / ١٠: على بن حفص: شيخ صدوق من رجال مسلم. قال الدارقطنى: ما أحسبه حفظه، لأنه كبر وتغير حفظه اه. قلت: ورواه الحسن بن على المعمرى فى اليوم والليلة عن أبى موسى محمد بن المثنى عن غندر عن شعبة كذلك، قال الدارقطنى: تفرد به المعمرى، فقد رواه أصحاب أبى موسى عنه بلفظ " على وقتها " اه. والظاهر أن المعمرى وهم فيه لأنه كان يحدث من حفظه، وقد أطلق النووى فى شرح المهذب أن رواية " فى أول وقتها "
ضعيفة اه.','1','249',NULL,NULL,'0');
INSERT INTO [b88] VALUES (250,'<span data-type="title" id=toc-251>٢٥٠ </span>- عن ابن عمر أنه كان يقول: إنما التأذين لجيش أو ركب عليهم أمير فينادى بالصلاة ليجتمعوا لها، فأما غيرهم فإنما هى الإقامة.
** عب
(فتح الباري ١١١/٢)
** إسناده صحيح موقوفا','1','250',NULL,NULL,'0');
INSERT INTO [b88] VALUES (251,'<span data-type="title" id=toc-252>٢٥١ </span>- عن مالك بن الحويرث مرفوعا: " من زار قوما فلا يؤمهم، ولؤمهم رجل منهم ".
** د ت
(فتح الباري ١٧٢/٢)
** قال الحافظ فى " الفتح " ٢ / ١٧٢: حسنه الترمذى.','1','251',NULL,NULL,'0');
INSERT INTO [b88] VALUES (252,'<span data-type="title" id=toc-253>٢٥٢ </span>- لفظه: " لا تبادروا أئمتكم بالركوع ولا بالسجود إذا رفع أحدكم رأسه والإمام ساجد فليسجد، ثم ليمكث قدر ما سبقه به الإمام ".
** شب خت
(فتح الباري ١٧٤/٢)
** إسناده صحيح','1','252',NULL,NULL,'0');
INSERT INTO [b88] VALUES (253,'<span data-type="title" id=toc-254>٢٥٣ </span>- عن عمر، ولفظه: أيما رجل رفع رأسه قبل الإمام فى ركوع أو سجود فليضع رأسه بقدر رفعه إياه.
** عب
(فتح الباري ١٧٤/٢)
** إسناده صحيح','1','253',NULL,NULL,'0');
INSERT INTO [b88] VALUES (254,'<span data-type="title" id=toc-255>٢٥٤ </span>- رواية أرقم بن شرحبيل عن ابن عباس: وأخذ رسول الله صلى الله عليه وسلم القراءة من حيث بلغ أبو بكر. هذا لفظ ابن ماجه.
** جه
(فتح الباري ١٧٤/٢)
** إسناده حسن','1','254',NULL,NULL,'0');
INSERT INTO [b88] VALUES (255,'<span data-type="title" id=toc-256>٢٥٥ </span>- حديث جابر الجعفى عن الشعبى مرفوعا: " لا يؤمن أحد بعدى جالسا ".
** لم
(فتح الباري ١٧٥/٢)
** قال الحافظ فى " الفتح " ٢ / ١٧٥: قال الشافعى: لا حجة فيه لأنه مرسل، ومن رواية رجل يرغب أهل العلم عن الرواية عنه، يعنى جابرا الجعفى.','1','255',NULL,NULL,'0');
INSERT INTO [b88] VALUES (256,'<span data-type="title" id=toc-257>٢٥٦ </span>- عن قيس بن قهد الأنصارى: أن إماما لهم اشتكى لهم على عهد رسول الله صلى الله عليه وسلم، قال: فكان يؤمنا وهو جالس ونحن جلوس.
** عب
(فتح الباري ١٧٦/٢)
** إسناده صحيح','1','256',NULL,NULL,'0');
INSERT INTO [b88] VALUES (257,'<span data-type="title" id=toc-258>٢٥٧ </span>- عن أسيد بن حضير أنه كان يؤم قومه فاشتكى فخرج إليهم بعد شكواه فأمروه أن يصلى بهم، فقال: إنى لا أستطيع أن أصلى قائما فأقعدوا، فصلى بهم قاعدا وهو قعود.
** ابن المنذر
(فتح الباري ١٧٦/٢)
** إسناده صحيح','1','257',NULL,NULL,'0');
INSERT INTO [b88] VALUES (258,'<span data-type="title" id=toc-259>٢٥٨ </span>- عن أسيد بن حضير أنه قال: يارسول الله إن إمامنا مريض، قال: " إذا صلى قاعدا فصلوا قعودا ".
** د
(فتح الباري ١٧٦/٢)
** إسناده منقطع
** قال الحافظ فى " الفتح " ٢ / ١٧٦: فى إسناده انقطاع.','1','258',NULL,NULL,'0');
INSERT INTO [b88] VALUES (259,'<span data-type="title" id=toc-260>٢٥٩ </span>- عن جابر: أنه اشتكى، فحضرت الصلاة فصلى بهم جالسا وصلوا معه جلوسا.
** شب
(فتح الباري ١٧٦/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ١٧٦: وعن أبى هريرة أنه أفتى بذلك وإسناده صحيح.','1','259',NULL,NULL,'0');
INSERT INTO [b88] VALUES (260,'<span data-type="title" id=toc-261>٢٦٠ </span>- عن ابن جريج أخبرنى عطاء _ فذكر الحديث ولفظه _: فصلى النبى صلى الله عليه وسلم قاعدا وجعل أبو بكر وراءه بينه وبين الناس وصلى الناس وراءه قياما.
** عب
(فتح الباري ١٧٧/٢)
** مرسل
** قال الحافظ فى " الفتح " ٢ / ١٧٧: وهذا مرسل يعتضد بالرواية التى علقها الشافعى عن النخعى. وقال الحافظ فى " الفتح " ٢ / ١٧٧: وقع فى مرسل عطاء المذكور متصلا به بعد قوله (وصلى الناس وراءه قياما) ، فقال النبى صلى الله عليه وسلم: " لو استقبلت من أمرى ما استدبرت ما صليتم إلا قعودا، فصلوا صلاة إمامكم ما كان، إن صلى قائما فصلوا قياما وإن صلى قاعدا فصلوا قعودا ".','1','260',NULL,NULL,'0');
INSERT INTO [b88] VALUES (261,'<span data-type="title" id=toc-262>٢٦١ </span>- رواية أبى سفيان عن جابر قال: ركب رسول الله صلى الله عليه وسلم فرسا بالمدينة فصرعه على جذع نخلة فانفكت قدمه.. الحديث.
** د خز
(فتح الباري ١٧٧/٢)
** إسناده صحيح','1','261',NULL,NULL,'0');
INSERT INTO [b88] VALUES (262,'<span data-type="title" id=toc-263>٢٦٢ </span>- عن أبى هريرة مرفوعا: " الذى يخفض ويرفع قبل الإمام إنما ناصيته بيد شيطان ".
** بز
(فتح الباري ١٨٣/٢)
** قال الحافظ فى " الفتح " ٢ / ١٨٣: وأخرجه عبد الرزاق من هذا الوجه موقوفا وهو المحفوظ.','1','262',NULL,NULL,'0');
INSERT INTO [b88] VALUES (263,'<span data-type="title" id=toc-264>٢٦٣ </span>- عن اين عباس مرفوعا: " لايؤم الغلام حتى يحتلم ".
** عب
(فتح الباري ١٨٥/٢)
** إسناده ضعيف','1','263',NULL,NULL,'0');
INSERT INTO [b88] VALUES (264,'<span data-type="title" id=toc-265>٢٦٤ </span>- عن ابن مسعود مرفوعا: " لعلكم تدركون أقواما يصلون الصلاة لغير وقتها فإذا ادركتموهم فصلوا فى بيوتكم فى الوقت، ثم صلوا واجعلوها سبحة ".
** ن
(فتح الباري ١٨٧/٢)
** حسن','1','264',NULL,NULL,'0');
INSERT INTO [b88] VALUES (265,'<span data-type="title" id=toc-266>٢٦٥ </span>- عن مكحول، قال: قالوا لعثمان: إنا نتحرج أن نصلى خلف هؤلاء الذين حصروك ... فذكر نحو حديث الزهرى.
** ص
(فتح الباري ١٩٠/٢)
** منقطع
** قال الحافظ فى " الفتح " ٢ / ١٩٠: هذا منقطع إلا أنه اعتضد.','1','265',NULL,NULL,'0');
INSERT INTO [b88] VALUES (266,'<span data-type="title" id=toc-267>٢٦٦ </span>- حديث أبى سعيد: أن النبى صلى الله عليه وسلم رأى رجلا يصلى وحده فقال: " ألا رجل يتصدق على هذا فيصلى معه ".
** د
(فتح الباري ١٩٢/٢)
** قال الحافظ فى " الفتح " ٢ / ١٩٢: حسنه الترمذى وصححه ابن خزيمة، وابن حبان والحاكم.','1','266',NULL,NULL,'0');
INSERT INTO [b88] VALUES (267,'<span data-type="title" id=toc-268>٢٦٧ </span>- من حديث بريدة: فقرأ اقتربت الساعة.
** حم
(فتح الباري ١٩٣/٢)
** إسناده قوى
** قال الحافظ فى " الفتح " ٢ / ١٩٣: وهى شاذة إلا أن حمل على التعدد.','1','267',NULL,NULL,'0');
INSERT INTO [b88] VALUES (268,'<span data-type="title" id=toc-269>٢٦٨ </span>- عن أنس قال: كان معاذ يؤم قومه فدخل حزام وهو يريد أن يسقى نخله.. الحديث.
** حم ن يع سكن
(فتح الباري ١٩٤/٢)
** إسناده صحيح','1','268',NULL,NULL,'0');
INSERT INTO [b88] VALUES (269,'<span data-type="title" id=toc-270>٢٦٩ </span>- عن معاذ بن رفاعة، عن رجل من بنى سلمة يقال له: سليم أنه أتى النبى صلى الله عليه وسلم، فقال: يانبى الله، إنا نظل فى أعمالنا فنأتى حين نمسى فنصلى، فيأتى معاذ بن جبل فينادى بالصلاة فنأتيه فيطول علينا الحديث.
** حم طحا طب
(فتح الباري ١٩٤/٢)
** مرسل
** قال الحافظ فى " الفتح " ٢ / ١٩٤: هذا مرسل لأن معاذ بن رفاعة لم يدركه، وقد رواه الطحاوى والطبرانى من هذا الوجه عن معاذ بن رفاعة أن رجلا من بنى سليم فذكره مرسلا، ورواه البزار من وجه آخر عن جابر وسماه سليما أيضا.','1','269',NULL,NULL,'0');
INSERT INTO [b88] VALUES (270,'<span data-type="title" id=toc-271>٢٧٠ </span>- عن عمر قال: لا تبغضوا إلى الله عباده، يكون أحدكم إماما فيطول على القوم الصلاة حتى يبغض إليهم ماهم فيه.
** هب
(فتح الباري ١٩٥/٢)
** إسناده صحيح','1','270',NULL,NULL,'0');
INSERT INTO [b88] VALUES (271,'<span data-type="title" id=toc-272>٢٧١ </span>- " إن أهل العالية كانوا يصلون فى بيوتهم ثم يصلون مع النبى صلى الله عليه وسلم فبلغه ذلك فنهاهم ".
** طحا
(فتح الباري ١٩٦/٢)
** مرسل','1','271',NULL,NULL,'0');
INSERT INTO [b88] VALUES (272,'<span data-type="title" id=toc-273>٢٧٢ </span>- حديث يزيد بن الأسود العامرى، قال صلى الله عليه وسلم للذين لم يصليا معه: " إذا صليتما فى رحالكما ثم أتيتما مسجد جماعة فصليا معهم فإنها لكما نافلة ".
** د ت ن جه
(فتح الباري ١٩٦/٢)
** قال الحافظ فى " الفتح " ٢ / ١٩٦: وصححه ابن خزيمة وغيره.','1','272',NULL,NULL,'0');
INSERT INTO [b88] VALUES (273,'<span data-type="title" id=toc-274>٢٧٣ </span>- عن جابر، قال: كان أبى بن كعب يصلى بأهل قباء فاستفتح سورة طويلة فدخل معه غلام من الأنصار فى الصلاة فلما سمعه استفتحها انتفل من صلاه، فغضب أبى فأتى النبى صلى الله عليه وسلم يشكو الغلام، وأتى الغلام يشكو أبيا فغضب النبى صلى الله عليه وسلم حتى عرف الغضب فى وجهه ثم قال: " إن منكم منفرين فإذا صليتم فأوجزوا، فإن خلفكم الضعيف والكبير وذا الحاجة ".
** يع
(فتح الباري ١٩٨/٢)
** إسناده حسن','1','273',NULL,NULL,'0');
INSERT INTO [b88] VALUES (274,'<span data-type="title" id=toc-275>٢٧٤ </span>- عن عثمان بن أبى العاص، أن النبى صلى الله عليه وسلم، قال له: " أنت إمام قومك، واقدر القوم باضعفهم ".
** د ن
(فتح الباري ١٩٩/٢)
** إسناده حسن
** قال الحافظ فى " الفتح " ٢ / ١٩٩: إسناده حسن وأصله فى مسلم.','1','274',NULL,NULL,'0');
INSERT INTO [b88] VALUES (275,'<span data-type="title" id=toc-276>٢٧٥ </span>- طريق عبد الرحمن بن سابط لفظه: إنه صلى الله عليه وسلم قرأ فى الركعة الأولى بسورة طويلة فسمع بكاء صبى، فقرأ بالثانية بثلاث آيات.
** شب
(فتح الباري ٢٠٢/٢)
** مرسل','1','275',NULL,NULL,'0');
INSERT INTO [b88] VALUES (276,'<span data-type="title" id=toc-277>٢٧٦ </span>- حديث عبد الله بن الشخير: رأيت رسول الله صلى الله عليه وسلم يصلى بنا وفى صدره أزيز كأزيز المرجل من البكاء.
** د ن تم
(فتح الباري ٢٠٦/٢)
** إسناده قوى
** قال الحافظ فى " الفتح " ٢ / ٢٠٦: صححه ابن خزيمة وابن حبان والحاكم ووهم من زعم أن مسلما أخرجه.','1','276',NULL,NULL,'0');
INSERT INTO [b88] VALUES (277,'<span data-type="title" id=toc-278>٢٧٧ </span>- حديث أبى أمامة: " لتسون الصفوف أو لتطمس الوجوه ".
** حم
(فتح الباري ٢٠٧/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٢٠٧: فى إسناده ضعف، وقال ابن الجوزى: وفى إسناده ضعف.','1','277',NULL,NULL,'0');
INSERT INTO [b88] VALUES (278,'<span data-type="title" id=toc-279>٢٧٨ </span>- حديث ابن عمر ولفظه: أن رسول الله صلى الله عليه وسلم قال: " أقيموا الصفوف وحاذوا بين المناكب وسدوا الخلل ولا تذروا فرجات للشيطان ومن وصل صفا وصله الله، ومن قطع صفا قطعه الله ".
** د
(فتح الباري ٢١١/٢)
** قال الحافظ فى " الفتح " ٢ / ٢١١: صححه ابن خزيمة والحاكم.','1','278',NULL,NULL,'0');
INSERT INTO [b88] VALUES (279,'<span data-type="title" id=toc-280>٢٧٩ </span>- عن النعمان بن بشير قال: أقبل رسول الله صلى الله عليه وسلم على الناس بوجهه، فقال: " أقيموا صفوفكم ثلاثا، والله لتقيمن صفوفكم أو ليخالفن الله بين قلوبكم "، قال: فلقد رأيت الرجل منا يلزق منكبه بمنكب صاحبه، وكعبه بكعبه.
** خت د
(فتح الباري ٢١١/٢)
** صحيح
** قال الحافظ فى " الفتح " ٢ / ٢١١: صححه ابن خزيمة. وقال الحافظ فى " الفتح " ٢ / ٢٩٣: ومن أوضح الأدلة فيه حديث النعمان بن بشير الصحيح فى صف الصف فى الصلاة: فرأيت الرجل منا يلزق كعبه بكعب صاحبه.','1','279',NULL,NULL,'0');
INSERT INTO [b88] VALUES (280,'<span data-type="title" id=toc-281>٢٨٠ </span>- الحديث: " لا صلاة لمنفرد خلف الصف ".
** حب خز
(فتح الباري ٢١٣/٢)
** قال الحافظ فى " الفتح " ٢ / ٢١٣: فى صحته نظر. وقال الحافظ فى " الفتح " ٢ / ٢٦٨: ولابن خزيمة أيضا من حديث على بن شيبان نحو حديث وابصة بن معبد، وزاد " لاصلاة لمنفرد خلف الصف ".','1','280',NULL,NULL,'0');
INSERT INTO [b88] VALUES (281,'<span data-type="title" id=toc-282>٢٨١ </span>- عن البراء قال: كنا إذا صلينا خلف النبى صلى الله عليه وسلم أحببنا أن نكون عن يمينه.
** ن
(فتح الباري ٢١٣/٢)
** إسناده صحيح','1','281',NULL,NULL,'0');
INSERT INTO [b88] VALUES (282,'<span data-type="title" id=toc-283>٢٨٢ </span>- عن عائشة مرفوعا: " إن الله وملائكته يصلون على ميامن الصفوف ".
** د
(فتح الباري ٢١٣/٢)
** إسناده حسن','1','282',NULL,NULL,'0');
INSERT INTO [b88] VALUES (283,'<span data-type="title" id=toc-284>٢٨٣ </span>- عن ابن عمر، قال: قيل للنبى صلى الله عليه وسلم: إن ميسرة المسجد فقال: " من عمر ميسرة المسجد كتب له كفلان من الأجر ".
** جه
(فتح الباري ٢١٣/٢)
** إسناده فيه مقال','1','283',NULL,NULL,'0');
INSERT INTO [b88] VALUES (284,'<span data-type="title" id=toc-285>٢٨٤ </span>- حديث أبى حميد: كان رسول الله صلى الله عليه وسلم إذا قام إلى الصلاة أعتدل قائما ورفع يديه ثم، قال: " الله أكبر ".
** جه
(فتح الباري ٢١٧/٢)
** قال الحافظ فى " الفتح " ٢ / ٢١٧: صححه ابن خزيمة وابن حبان.','1','284',NULL,NULL,'0');
INSERT INTO [b88] VALUES (285,'<span data-type="title" id=toc-286>٢٨٥ </span>- عن على: أن النبى صلى الله عليه وسلم كان إذا قام إلى الصلاة قال: " الله أكبر ".
** بز
(فتح الباري ٢١٧/٢)
** إسناده صحيح على شرط مسلم','1','285',NULL,NULL,'0');
INSERT INTO [b88] VALUES (286,'<span data-type="title" id=toc-287>٢٨٦ </span>- حديث ابن مسعود: أنه رأى النبى صلى الله عليه وسلم يرفع يديه عند الافتتاح ثم لايعود.
** د
(فتح الباري ٢٢٠/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٢٠: رده الشافعى بأنه لم يثبت، وقد صححه بعض أهل الحديث.','1','286',NULL,NULL,'0');
INSERT INTO [b88] VALUES (287,'<span data-type="title" id=toc-288>٢٨٧ </span>- عن ابن عمر قال: كان النبى صلى الله عليه وسلم إذا قام فى الركعتين كبر ورفع يديه.
** د البخارى فى جزء رفع اليدين
(فتح الباري ٢٢٢/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٢٢: صححه البخارى فى جزء رفع اليدين، وله شواهد منها حديث أبى حميد الساعدى وحديث على بن أبى طالب أخرجهما أبو داود وصححهما ابن خزيمة وابن حبان وقال البخارى فى الجزء المذكور: ما زاده ابن عمر وعلى وأبو حميد _ فى عشرة الصحابة _ من الرفع عند القيام من الركعتين: صحيح.','1','287',NULL,NULL,'0');
INSERT INTO [b88] VALUES (288,'<span data-type="title" id=toc-289>٢٨٨ </span>- عن عبد الأعلى: كان يرفع يديه فى كل خفض ورفع وركوع وسجود وقيام وقعود بين السجدتين، ويذكر أن النبى صلى الله عليه وسلم كان يفعل ذلك.
** طحا فى المشكل
(فتح الباري ٢٢٣/٢)
** شاذ
** قال الحافظ فى " الفتح " ٢ / ٢٢٣: وهذه رواية شاذة.','1','288',NULL,NULL,'0');
INSERT INTO [b88] VALUES (289,'<span data-type="title" id=toc-290>٢٨٩ </span>- حديث وائل: ثم وضع يديه على ظهر كفه اليسرى والرسغ والساعد.
** د ن
(فتح الباري ٢٢٤/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٢٤: صححه ابن خزيمة وغيره وأصله فى صحيح مسلم بدون الزيادة.','1','289',NULL,NULL,'0');
INSERT INTO [b88] VALUES (290,'<span data-type="title" id=toc-291>٢٩٠ </span>- عن ابن مسعود: رآنى النبى صلى الله عليه وسلم، واضعا يدى اليسرى على يدى اليمنى فنزعها ووضع اليمنى على اليسرى.
** د ن سكن
(فتح الباري ٢٢٤/٢)
** إسناده حسن','1','290',NULL,NULL,'0');
INSERT INTO [b88] VALUES (291,'<span data-type="title" id=toc-292>٢٩١ </span>- عن مجاهد قال: كان ابن الزبير إذا قام فى الصلاة كأنه عود.
** هق
(فتح الباري ٢٢٦/٢)
** إسناده صحيح','1','291',NULL,NULL,'0');
INSERT INTO [b88] VALUES (292,'<span data-type="title" id=toc-293>٢٩٢ </span>- لفظه: صلى رسول الله صلى الله عليه وسلم يوما بأصحابه، فلما قضى الصلاة أقبل عليهم بوجهه ... فذكره.
** جه السراج
(فتح الباري ٢٣٣/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٣٣: قد رواه عبد الرزاق عن معمر عن قتادة مرسلا ولم يذكر أنسا وهى علة غير قادحة لأن سعيدا أعلم بحديث قتادة من معمر وقد تابعه همام على وصله عن قتادة أخرجه السراج.','1','292',NULL,NULL,'0');
INSERT INTO [b88] VALUES (293,'<span data-type="title" id=toc-294>٢٩٣ </span>- حديث: " من صلى خلف إمام فقراءة الإمام له قراءة ".
** قط
(فتح الباري ٢٤٢/٢)
** ضعيف
** قال الحافظ فى " الفتح " ٢ / ٢٤٢: حديث ضعيف عند الحفاظ، قد استوعب طرقه وعلله الدارقطنى وغيره.','1','293',NULL,NULL,'0');
INSERT INTO [b88] VALUES (294,'<span data-type="title" id=toc-295>٢٩٤ </span>- عن أبى سعيد: أمرنا رسول الله صلى الله عليه وسلم أن نقرأ بفاتحة الكتاب وما تيسر.
** د
(فتح الباري ٢٤٣/٢)
** إسناده قوى','1','294',NULL,NULL,'0');
INSERT INTO [b88] VALUES (295,'<span data-type="title" id=toc-296>٢٩٥ </span>- حديث سليمان بن يسار: عن أبى هريرة أنه، قال: ما رأيت أحدا أشبه صلاة برسول الله صلى الله عليه وسلم من فلان، قال سليمان: فكان يقرأ فى الصبح الصبح بطوال المفصل، وفى المغرب بقصار المفصل.
** ن
(فتح الباري ٢٤٨/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٤٨: صححه ابن خزيمة وغيره.','1','295',NULL,NULL,'0');
INSERT INTO [b88] VALUES (296,'<span data-type="title" id=toc-297>٢٩٦ </span>- عن أبى بكر الصديق أنه أم الصحابة فى صلاة الصبح بسورة البقرة فقرأها فى الركعتين.
** عب
(فتح الباري ٢٥٦/٢)
** إسناده صحيح','1','296',NULL,NULL,'0');
INSERT INTO [b88] VALUES (297,'<span data-type="title" id=toc-298>٢٩٧ </span>- عن ابن عباس أنه قرأ الفاتحة وآية من البقرة فى كل ركعة.
** قط
(فتح الباري ٢٥٦/٢)
** إسناده قوى','1','297',NULL,NULL,'0');
INSERT INTO [b88] VALUES (298,'<span data-type="title" id=toc-299>٢٩٨ </span>- عن عبد الله بن شقيق قال: سألت عائشة أكان رسول الله صلى الله عليه وسلم يجمع بين السور؟ ، قالت: نعم من المفصل.
** د
(فتح الباري ٢٦٠/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٦٠: صححه ابن خزيمة.','1','298',NULL,NULL,'0');
INSERT INTO [b88] VALUES (299,'<span data-type="title" id=toc-300>٢٩٩ </span>- عن بلال أنه قال: يا رسول الله لا تستبقنى بآمين.
** د
(فتح الباري ٢٦٣/٢)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٢ / ٢٦٣: لكن قيل أن أبا عثمان لم يلق بلالا، وقد روى عنه بلفظ " إن بلالا قال "
وهو ظاهر الإرسال، ورجحه الدارقطنى وغيره على الموصول.','1','299',NULL,NULL,'0');
INSERT INTO [b88] VALUES (300,'<span data-type="title" id=toc-301>٣٠٠ </span>- حديث وابصة بن معبد: إن النبى صلى الله عليه وسلم رأى رجلا يصلى خلف الصف وحده فأمره أن يعيد الصلاة.
** د ت ن جه
(فتح الباري ٢٦٨/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٦٨: أخرجه أصحاب السنن، وصححه أحمد وابن خزيمة وغيرهما، ولابن خزيمة أيضا من حديث على بن شيبان نحوه، وزاد " لا صلاة لمنفرد خلف الصف.','1','300',NULL,NULL,'0');
INSERT INTO [b88] VALUES (301,'<span data-type="title" id=toc-302>٣٠١ </span>- عن أبى هريرة مرفوعا: " إذا أتى أحدكم الصلاة فلا يركع دون الصف حتى يأخذ مكانه من الصف ".
** طحا
(فتح الباري ٢٦٩/٢)
** إسناده حسن','1','301',NULL,NULL,'0');
INSERT INTO [b88] VALUES (302,'<span data-type="title" id=toc-303>٣٠٢ </span>- عن عبد العزيز بن رفيع، عن أناس من أهل المدينة، أن النبى صلى الله عليه وسلم، قال: " من وجدنى قائما أو راكعا أو صاجدا فليكن معى على الحال التى أنا عليها ".
** ص
(فتح الباري ٢٦٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٦٩: وفى الترمذى نحوه عن معاذ بن جبل مرفوعا وفى إسناده ضعف، لكنه ينجبر بطريق سعيد بن منصور المذكورة.','1','302',NULL,NULL,'0');
INSERT INTO [b88] VALUES (303,'<span data-type="title" id=toc-304>٣٠٣ </span>- عن عبد الرحمن بن أبزى قال: صليت خلف النبى صلى الله عليه وسلم فلم يتم التكبير.
** د
(فتح الباري ٢٦٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٦٩: قول البخارى (باب إتمام التكبير فى الركوع) اه، لعله أراد بلفظ الإتمام الإشارة إلى تضعيف ما رواه أبو داود، وقد نقل البخارى فى التاريخ عن أبى داود الطيالسى أنه قال: هذا عندنا باطل، وقال الطبرى والبزار: تفرد به الحسن بن عمران وهو مجهول.','1','303',NULL,NULL,'0');
INSERT INTO [b88] VALUES (304,'<span data-type="title" id=toc-305>٣٠٤ </span>- عن أبى موسى الأشعرى، قال: ذكرنا على صلاة كنا نصليها مع رسول الله صلى الله عليه وسلم إما نسيناها وإما تركناها عمدا.
** حم طحا
(فتح الباري ٢٧٠/٢)
** إسناده صحيح','1','304',NULL,NULL,'0');
INSERT INTO [b88] VALUES (305,'<span data-type="title" id=toc-306>٣٠٥ </span>- عن ابن عمر قال: إنما فعله النبى صلى الله عليه وسلم مرة يعنى التطبيق.
** ابن المنذر
(فتح الباري ٢٧٤/٢)
** إسناده قوى','1','305',NULL,NULL,'0');
INSERT INTO [b88] VALUES (306,'<span data-type="title" id=toc-307>٣٠٦ </span>- عن على قال: إذا ركعت فإن شئت قلت هكذا _ يعنى وضعت يديك على ركبتيك _، وإن شئت طبقت.
** شب
(فتح الباري ٢٧٤/٢)
** إسناده حسن','1','306',NULL,NULL,'0');
INSERT INTO [b88] VALUES (307,'<span data-type="title" id=toc-308>٣٠٧ </span>- حديث أبى هريرة: " إذا سجد أحدكم فليبدأ بركبتيه قبل يديه ولا يبرك بروك الفحل ".
** الأثرم
(فتح الباري ٢٩١/٢)
** إسناده ضعيف','1','307',NULL,NULL,'0');
INSERT INTO [b88] VALUES (308,'<span data-type="title" id=toc-309>٣٠٨ </span>- حديث سعد قال: كنا نضع اليدين قبل الركبتين، فأمرنا بالركبتين قبل اليدين.
** لم
(فتح الباري ٢٩١/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٩١: هذا لو صح لكان قاطعا للنزاع، لكنه من أفراد إبراهيم بن إسماعيل بن يحيى بم سلمة بن كهيل عن أبيه وهما ضعيفان.','1','308',NULL,NULL,'0');
INSERT INTO [b88] VALUES (309,'<span data-type="title" id=toc-310>٣٠٩ </span>- حديث ابن عمر أنه قال: " لا تفترش افتراش السبع، وادعم على راحتيك وأبد ضبعيك، فإذا فعلت ذلك سجد كل عضو ".
** طب
(فتح الباري ٢٩٤/٢)
** إسناده صحيح','1','309',NULL,NULL,'0');
INSERT INTO [b88] VALUES (310,'<span data-type="title" id=toc-311>٣١٠ </span>- عن عبد الله بن أرقم: صليت مع النبى صلى الله عليه وسلم فكنت أنظر إلى عفرتى إبطيه إذا سجد.
** ت
(فتح الباري ٢٩٤/٢)
** قال الحافظ فى " الفتح " ٢ / ٢٩٤: حسنه الترمذى.','1','310',NULL,NULL,'0');
INSERT INTO [b88] VALUES (311,'<span data-type="title" id=toc-312>٣١١ </span>- أن أبا رافع رأى الحسن بن على يصلى قد عرز ضفيرته فى قفاه فحلها وقال: سمعت رسول الله صلى الله عليه وسلم يقول: " ذلك مقعد الشيطان ".
** د
(فتح الباري ٢٩٩/٢)
** إسناده جيد','1','311',NULL,NULL,'0');
INSERT INTO [b88] VALUES (312,'<span data-type="title" id=toc-313>٣١٢ </span>- عن أبى هريرة أنه صلى الله عليه وسلم كان ينهض على صدور قدميه.
** ص
(فتح الباري ٣٠٣/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٣٠٣: وعن ابن مسعود مثله بإسناد صحيح.','1','312',NULL,NULL,'0');
INSERT INTO [b88] VALUES (313,'<span data-type="title" id=toc-314>٣١٣ </span>- عن ابن مسعود: كنا لا ندرى ما نقول قبل أن يفرض علينا التشهد.
** قط
(فتح الباري ٣١٢/٢)
** إسناده صحيح','1','313',NULL,NULL,'0');
INSERT INTO [b88] VALUES (314,'<span data-type="title" id=toc-315>٣١٤ </span>- عن عطاء أن الصحابة كانوا يقولون والنبى صلى الله عليه وسلم حى: السلام عليك أيها النبى، فلما مات، قالوا: السلام على النبى.
** عب
(فتح الباري ٣١٤/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٣١٤: متابع قوى.','1','314',NULL,NULL,'0');
INSERT INTO [b88] VALUES (315,'<span data-type="title" id=toc-316>٣١٥ </span>- عن عبيدة بن عبد الله بن مسعود عن أبيه أن النبى صلى الله عليه وسلم علمهم التشهد فذكره قال: فقال ابن عباس: إنما كنا نقول السلام عليك أيها النبى إذ كان حيا، فقال ابن مسعود: هكذا علمنا وهكذا نعلم.
** صى
(فتح الباري ٣١٤/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٣١٤: رواية أبى معمر أصح لأن أبا عبيدة لم يسمع من أبيه والإسناد إليه مع ذلك ضعيف.','1','315',NULL,NULL,'0');
INSERT INTO [b88] VALUES (316,'<span data-type="title" id=toc-317>٣١٦ </span>- من حديث أبى بن كعب: أن رسول الله صلى الله عليه وسلم كان إذا ذكر أحدا فدعا له بدأ بنفسه.
** ت
(فتح الباري ٣١٤/٢)
** قال الحافظ فى " الفتح " ٢ / ٣١٤: فى الترمذى مصححا من حديث أبى بن كعب، وأصله فى مسلم.','1','316',NULL,NULL,'0');
INSERT INTO [b88] VALUES (317,'<span data-type="title" id=toc-318>٣١٧ </span>- عن ابن عمر فى التشهد " أشهد أن لا إله إلا الله "، قال ابن عمر: زدت فيها: " وحده لاشريك له ".
** د
(فتح الباري ٣١٥/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٣١٥: حديث ابن عمر عند الدارقطنى إلا أن سنده ضعيف وقد روى أبو داود من وجه آخر صحيح عن ابن عمر. وقال الحافظ فى " الفتح " ٢ / ٣١٥: وهذا ظاهره الوقف.','1','317',NULL,NULL,'0');
INSERT INTO [b88] VALUES (318,'<span data-type="title" id=toc-319>٣١٨ </span>- عن عطاء قال: بينا النبى صلى الله عليه وسلم يعلم التشهد إذ قال رجل: وأشهد أن محمدا رسوله وعبده، فقال عليه الصلاة والسلام: " لقد كنت عبدا قبل أن أكون رسولا، قل عبده ورسوله ".
** عب
(فتح الباري ٣١٥/٢)
** مرسل رجاله ثقات','1','318',NULL,NULL,'0');
INSERT INTO [b88] VALUES (319,'<span data-type="title" id=toc-320>٣١٩ </span>- حديث عمرو بن مرة: كانوا يستحبون إذا وضع الميت فى قبره أن يقولوا: اللهم أعذه من الشيطان.
** الحكيم الترمذى فى نوادر الأصول
(فتح الباري ٣١٩/٢)
** إسناده جيد','1','319',NULL,NULL,'0');
INSERT INTO [b88] VALUES (320,'<span data-type="title" id=toc-321>٣٢٠ </span>- قال عبد الله: يتشهد الرجل فى الصلاة ثم يصلى على النبى صلى الله عليه وسلم ثم يدعو لنفسه بعد.
** ص شب
(فتح الباري ٣٢١/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٣٢١: بإسناد صحيح إلى أبى الأحوص.','1','320',NULL,NULL,'0');
INSERT INTO [b88] VALUES (321,'<span data-type="title" id=toc-322>٣٢١ </span>- حديث: " تحليلها التسليم ".
** د ت ن جه
(فتح الباري ٣٢٢/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٣٢٢: أخرجه أصحاب السنن بسند صحيح.','1','321',NULL,NULL,'0');
INSERT INTO [b88] VALUES (322,'<span data-type="title" id=toc-323>٣٢٢ </span>- حديث: " إذا أحدث وقد جلس فى آخر صلاته قبل أن يسلم فقد جازت صلاته ".
** لم
(فتح الباري ٣٢٢/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٢٣: ضعفه الحفاظ.
** تعقيب: * والحديث المذكور أخرجه عبد الرزاق فى مصنفه (٣٦٧٣) بإسناده عن عبد الله بن عمرو _ مرفوعا _ بلفظ: " إذا أحدث الإمام فى آخر صلاته حين يستوى قاعدا، فقد تمت صلاته، وصلاة من ورائه على مثل صلاته ". وكذلك أخرجه البيهقى فى سننه (٢ / ١٧٦) من طريق ابن داشة عن أبى داود.','1','322',NULL,NULL,'0');
INSERT INTO [b88] VALUES (323,'<span data-type="title" id=toc-324>٣٢٣ </span>- عن حدام بن حكيم عن أبى ذر، وقال فيه: فقال أبو ذر: يارسول الله إنهم قد قالوا مثل ما نقول، فقال: " ذلك فضل الله يؤتيه من يشاء ".
** جعفر الفريابى
(فتح الباري ٣٣٠/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٣٠: نقل الخطيب أن حرام بن حكيم يرسل الرواية عن أبى ذر فعلى هذا لم يصح بهذه الزيادة إسناد، إلا أن هذين الطريقين يقوى بهما مرسل أبى صالح.','1','323',NULL,NULL,'0');
INSERT INTO [b88] VALUES (324,'<span data-type="title" id=toc-325>٣٢٤ </span>- زاد الطبرانى من طريق أخرى: عن المغيرة: " يحيى ويميت وهو حى لا يموت بيده الخير ... إلى قدير ".
** طب
(فتح الباري ٣٣٢/٢)
** رجاله موثقون
** قال الحافظ فى " الفتح " ٢ / ٣٣٢: رواته موثقون وثبت مثله عند البزار من حديث عبد الرحمن بن عوف بسند ضعيف لكن فى القول إذا أصبح وإذا أمسى.','1','324',NULL,NULL,'0');
INSERT INTO [b88] VALUES (325,'<span data-type="title" id=toc-326>٣٢٥ </span>- يذكر عن أبى هريرة رفعه: " لا يتطوع الإمام فى مكانه ".
** خت
(فتح الباري ٣٣٥/٢)
** إسناده ضعيف مضطرب
** قال الحافظ فى " الفتح " ٢ / ٣٣٥: قوله لم يصح هو كلام البخارى وذلك بضعف إسناده واضطرابه، تفرد به ليث بن أبى سليم وهو ضعيف واختلف عليه فيه، وقد ذكر البخارى الأختلاف فى تاريخه وقال: لم يثبت هذا الحديث اه. وفى الباب عن المغيرة بن شعبة مرفوعا.','1','325',NULL,NULL,'0');
INSERT INTO [b88] VALUES (326,'<span data-type="title" id=toc-327>٣٢٦ </span>- عن المغيرة بن شعبة مرفوعا بلفظ: " لا يصلى الإمام فى الموضع الذى صلى فيه حتى يتحول ".
** د
(فتح الباري ٣٣٥/٢)
** إسناده منقطع
** قال الحافظ فى " الفتح " ٢ / ٣٣٥: حكى ابن قدامة فى المغنى عن أحمد أنه قال: لا أعرفه عن غير على اه. فكأنه لم يثبت عنده حديث أبى هريرة ولا المغيرة.','1','326',NULL,NULL,'0');
INSERT INTO [b88] VALUES (327,'<span data-type="title" id=toc-328>٣٢٧ </span>- عن على قال: من السنة أن لا يتطوع الإمام حتى يتحول من مكانه.
** شب
(فتح الباري ٣٣٥/٢)
** إسناده حسن','1','327',NULL,NULL,'0');
INSERT INTO [b88] VALUES (328,'<span data-type="title" id=toc-329>٣٢٨ </span>- عن عبد الملك بن الربيع بن سبرة عن أبيه عن جده مرفوعا: " علموا الصبى الصلاة ابن سبع، واضربوه عليها ابن عشر ".
** د ت خز ك
(فتح الباري ٣٤٥/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٤٥: رواه الترمذى وصححه وكذا ابن خزيمة والحاكم.','1','328',NULL,NULL,'0');
INSERT INTO [b88] VALUES (329,'<span data-type="title" id=toc-330>٣٢٩ </span>- عن ابن عمر بلفظ: " لا تمنعوا نساءكم المساجد، وبيوتهن خير لهن ".
** د
(فتح الباري ٣٥٠/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٥٠: صححه ابن خزيمة.','1','329',NULL,NULL,'0');
INSERT INTO [b88] VALUES (330,'<span data-type="title" id=toc-331>٣٣٠ </span>- حديث أم حميد الساعدية: إنها جاءت إلى رسول الله صلى الله عليه وسلم فقالت: يارسول الله إنى أحب الصلاة معك، قال: " قد علمت وصلاتك فى بيتك خير لك من صلاتك فى حجرتك وصلاتك فى حجرتك خير من صلاتك فى دارك وصلاتك فى دارك خير من صلاتك فى مسجد قومك، وصلاتك فى مسجد قومك خير لك من صلاتك فى مسجد الجماعة ".
** حم طب
(فتح الباري ٣٥٠/٢)
** إسناده حسن
** قال الحافظ فى " الفتح " ٢ / ٣٥٠: وإسناد أحمد حسن، وله شاهد من حديث ابن مسعود عند أبى داود،','1','330',NULL,NULL,'0');
INSERT INTO [b88] VALUES (331,'<span data-type="title" id=toc-332>٣٣١ </span>- عن عائشة موقوفا ولفظه: قالت: كن نساء بنى إسرائيل يتخذن أرجلا من خشب يتشرفن للرجال فى المسجد، فحرم الله عليهن المساجد وسلطت عليهن الحيضة.
** عب
(فتح الباري ٣٥٠/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٣٥٠: هذا وإن كان موقوفا فحكمه حكم الرفع لأنه لا يقال بالرأى، وروى عبد الرزاق أيضا نحوه بإسناد صحيح عن ابن مسعود.','1','331',NULL,NULL,'0');
INSERT INTO [b88] VALUES (332,'<span data-type="title" id=toc-333>٣٣٢ </span>- عن محمد بن سيرين، قال: جمع أهل المدينة قبل أن يقدمها رسول الله صلى الله عليه وسلم وقبل أن تنزل الجمعة فقالت الأنصار: إن لليهود يوما يجتمعون فيه كل سبعة أيام وللنصارى كذلك، فهلم فلنجعل يوما نجتمع فيه فنذكر الله تعالى ونصلى ونشكره، فجعلوه يوم العروبة، واجتمعوا إلى أسعد بن زرارى فصلى بهم يومئذ، وأنزل الله تعالى بعد ذلك (إذا نودى للصلاة من يوم الجمعة) الآية.
** عب
(فتح الباري ٣٥٥/٢)
** إسناده صحيح مرسلا
** قال الحافظ فى " الفتح " ٢ / ٣٥٥: وهذا وإن كان مرسلا فله شاهد بإسناد حسن أخرجه أحمد وأبو داود وابن ماجه وصححه ابن خزيمة وغير واحد من حديث كعب بن مالك.','1','332',NULL,NULL,'0');
INSERT INTO [b88] VALUES (333,'<span data-type="title" id=toc-334>٣٣٣ </span>- حديث كعب بن مالك قال: كان أول من صلى بنا الجمعة قبل مقدم رسول الله صلى الله عليه وسلم المدينة أسعد بن زراره........ الحديث.
** حم د جه
(فتح الباري ٣٥٥/٢)
** إسناده حسن
** قال الحافظ فى " الفتح " ٢ / ٣٥٥: صححه ابن خزيمة وغير واحد.','1','333',NULL,NULL,'0');
INSERT INTO [b88] VALUES (334,'<span data-type="title" id=toc-335>٣٣٤ </span>- رواية عثمان بن واقد عن نافع بلفظ: " من أتى الجمعة من الرجال والنساء فليغتسل ومن لم يأتها فليس عليه غسل ".
** عو خز حب
(فتح الباري ٣٥٨/٢)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٢ / ٣٥٨: رجاله ثقات لكن قال البزار: أخشى أن يكون عثمان ين واقد وهم فيه.','1','334',NULL,NULL,'0');
INSERT INTO [b88] VALUES (335,'<span data-type="title" id=toc-336>٣٣٥ </span>- عن حفصة قالت: قال رسول الله صلى الله عليه وسلم: " الجمعة واجبة على كل محتلم وعلى من راح إلى الجمعة الغسل ".
** د ن خز حب
(فتح الباري ٣٥٨/٢)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٢ / ٣٥٨: قال الطبرانى فى الأوسط: لم يروه عن نافع بزيادة حفصة إلا بكير ولا عنه إلا عياش تفرد به مفضل اه. قلت: رواته ثقات فإن كان محفوظا فهو حديث آخر، ولا مانع أن يسمعه ابن عمر من النبى صلى الله عليه وسلم ومن غيره من الصحابة.','1','335',NULL,NULL,'0');
INSERT INTO [b88] VALUES (336,'<span data-type="title" id=toc-337>٣٣٦ </span>- عن سعيد بن عبد الرحمن بن أبزى عن أبيه وله صحبة: أنه كان يغتسل يوم الجمعة ثم يحدث فيتوضأ ولا يعيد الغسل.
** شب
(فتح الباري ٣٥٨/٢)
** إسناده صحيح','1','336',NULL,NULL,'0');
INSERT INTO [b88] VALUES (337,'<span data-type="title" id=toc-338>٣٣٧ </span>- حديث: " من توضأ يوم الجمعة فبها ونعمت، ومن اغتسل فالغسل أفضل ".
** د ت ن خز حب
(فتح الباري ٣٦٢/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٦٢: لهذا الحديث طرق أشهرها وأقواها رواية الحسن عن سمرة أخرجها أصحاب السنن الثلاثة وابن خزيمة وابن حبان وله علتان: أحدهما أنه من عنعنة الحسن، والأخرى أنه اختلف عليه فيه، وأخرجه ابن ماجه من حديث أنس والطبرانى من حديث عبد الرحمن بن سمرة والبزار من حديث أبى سعيد وابن عدى من حديث جابر وكلها ضعيفة.','1','337',NULL,NULL,'0');
INSERT INTO [b88] VALUES (338,'<span data-type="title" id=toc-339>٣٣٨ </span>- عن أبى هريرة أنه كان يوجب الطيب يوم الجمعة.
** سفيان بن عيينة فى جامعه
(فتح الباري ٣٦٢/٢)
** إسناده صحيح','1','338',NULL,NULL,'0');
INSERT INTO [b88] VALUES (339,'<span data-type="title" id=toc-340>٣٣٩ </span>- حديث ابن عباس، أنه سئل عن غسل يوم الجمعة أواجب هو؟ ، فقال: لا، ولكنه أظهر لمن اغتسل ومن لم يغتسل فليس بواجب عليه، وسأخبركم عن بدء الغسل كان الناس مجهودين يلبسون الصوف ويعملون وكان مسجدهم ضيقا فلما آذى بعضهم بعضا قال النبى صلى الله عليه وسلم: " أيها الناس إذا كان هذا اليوم فاغتسلوا "، قال ابن عباس: ثم جاء الله بالخير ولبسوا غير الصوف وكفوا العمل، ووسع المسجد.
** د طحا
(فتح الباري ٣٦٢/٢)
** إسناده حسن
** قال الحافظ فى " الفتح " ٢ / ٣٦٢: إسناده حسن، لكن الثابت عن ابن عباس خلافه.','1','339',NULL,NULL,'0');
INSERT INTO [b88] VALUES (340,'<span data-type="title" id=toc-341>٣٤٠ </span>- حديث جابر مرفوعا: " يوم الجمعة اثنتا عشرة ساعة ".
** د ن
(فتح الباري ٣٦٨/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٦٨: صححه الحاكم.','1','340',NULL,NULL,'0');
INSERT INTO [b88] VALUES (341,'<span data-type="title" id=toc-342>٣٤١ </span>- عن أبى هريرة بلفظ: " المتعجل إلى الجمعة كالمهدى بدنة " الحديث.
** لم
(فتح الباري ٣٦٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٦٩: صححه ابن خزيمة.','1','341',NULL,NULL,'0');
INSERT INTO [b88] VALUES (342,'<span data-type="title" id=toc-343>٣٤٢ </span>- عن ابن عباس مرفوعا: " من جاء إلى الجمعة فليغتسل وإن كان له طيب فليمس منه ".
** جه
(فتح الباري ٣٧٣/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٧٣: أخرجه ابن مجه من رواية صالح بن أبى الأخضر عن الزهرى عن عبيد وصالح ضعيف وقد خالفه مالك فرواه عن الزهرى عن عبيد بن السباق بمعناه مرسلا، فإن كان صالح حفظ فيه ابن عباس، احتمل أن يكون ذكره بعد ما نسيه أو عكس ذلك.','1','342',NULL,NULL,'0');
INSERT INTO [b88] VALUES (343,'<span data-type="title" id=toc-344>٣٤٣ </span>- عن يحيى بن سعيد الأنصارى أنه بلغه أن رسول الله صلى الله عليه وسلم قال: " ما على أحدكم لو اتخذ ثوبين لجمعته سوى ثوبى مهنته ".
** ما
(فتح الباري ٣٧٤/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٧٤: وصله ابن عبد البر فى التمهيد من طريق يحيى بن سعيد الأموى عن يحيى بن سعيد الأنصارى عن عمرة عن عائشة وفى إسناده نظر فقد رواه أبو داود من طريق عمرو بن الحارث وسعيد بن منصور عن ابن عيينة وعبد الرزاق عن الثورى ثلاثتهم عن يحيى بن سعيد عن محمد بن يحيى بن حبان مرسلا، ووصله أبو داود وابن ماجه من وجه آخر عن محمد بن يحيى عن عبد الله بن سلام، ولحديث عائشة طريق عند ابن خزيمة وابن ماجه.','1','343',NULL,NULL,'0');
INSERT INTO [b88] VALUES (344,'<span data-type="title" id=toc-345>٣٤٤ </span>- عن ابن عباس قال: كان رسول الله صلى الله عليه وسلم يصلى ركعتين ثم ينصرف فيستاك ".
** جه
(فتح الباري ٣٧٦/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٣٧٦: لكنه مختصر من حديث طويل أورده أبو داود، وأصل الحديث فى مسلم مبيناأيضا.','1','344',NULL,NULL,'0');
INSERT INTO [b88] VALUES (345,'<span data-type="title" id=toc-346>٣٤٥ </span>- عن الزهرى عن عبيد بن السباق مرسلا بلفظ: " عليكم بالسواك ".
** ما
(فتح الباري ٣٧٦/٢)
** مرسل
** قال الحافظ فى " الفتح " ٢ / ٣٧٦: وهو فى أثناء حديث وصله ابن ماجه من طريق صالح بن أبى الأخضر عن الزهرى _ يذكر ابن عباس فيه _.','1','345',NULL,NULL,'0');
INSERT INTO [b88] VALUES (346,'<span data-type="title" id=toc-347>٣٤٦ </span>- عن ابن عمر أنه صلى الله عليه وسلم قرأ سورة فيها سجدة فىصلاة الظهر فسجد بهم فيها.
** د ك
(فتح الباري ٣٧٨/٢)
** صحيح
** قال الحافظ فى " الفتح " ٢ / ٣٧٩: صح من حديث ابن عمر.
** تعقيب: قال الشيخ عبد العزيز بن باز ٢ / ٣٧٨: فى تصحيحه نظر والصواب أنه ضعيف لأن فى إسناده عند أبى داود رجلا مجهولا يدعى أمية كما نص على ذلك أبو داود فى رواية الرملى عنه، ونبه عليه الشوكانى فى نيل الأوطار والله أعلم.','1','346',NULL,NULL,'0');
INSERT INTO [b88] VALUES (347,'<span data-type="title" id=toc-348>٣٤٧ </span>- عن ابن عباس قال: غدوت على النبى صلى الله عليه وسلم يوم الجمعة فى صلاة الفجر فقرأ سورة فيها سجدة فسجد ... الحديث.
** الشريعة لابن أبى داود
(فتح الباري ٣٧٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٧٩: فى إسناده من ينظر فى حاله.','1','347',NULL,NULL,'0');
INSERT INTO [b88] VALUES (348,'<span data-type="title" id=toc-349>٣٤٨ </span>- حديث على: أن النبى صلى الله عليه وسلم سجد فى صلاة الصبح فى تنزيل السجدة.
** طص
(فتح الباري ٣٧٩/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٣٧٩: فى إسناده ضعف.','1','348',NULL,NULL,'0');
INSERT INTO [b88] VALUES (349,'<span data-type="title" id=toc-350>٣٤٩ </span>- عن عمر: أنه كتب إلى أهل البحرين أن جمعوا خيثما كنتم.
** شب
(فتح الباري ٣٨٠/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٨٠: صححه ابن خزيمة.','1','349',NULL,NULL,'0');
INSERT INTO [b88] VALUES (350,'<span data-type="title" id=toc-351>٣٥٠ </span>- عن ابن عمر أنه كان يرى أهل المياه بين مكة والمدينة يجمعون فلا يعيب عليهم.
** عب
(فتح الباري ٣٨٠/٢)
** إسناده صحيح','1','350',NULL,NULL,'0');
INSERT INTO [b88] VALUES (351,'<span data-type="title" id=toc-352>٣٥١ </span>- قال ابن عمر: إنما الغسل على من تجب عليه الجمعة. وزاد البيهقى: والجمعة على من يأتى أهله.
** خت هق
(فتح الباري ٣٨٢/٢)
** إسناده صحيح','1','351',NULL,NULL,'0');
INSERT INTO [b88] VALUES (352,'<span data-type="title" id=toc-353>٣٥٢ </span>- حديث جابر بلفظ: " الغسل واجب على كل مسلم فى كل أسبوع يوما وهو يوم الجمعة ".
** ن
(فتح الباري ٣٨٣/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٨٣: صححه ابن خزيمة، ولسعيد بن منصور وأبى بكر بن أبى شيبة من حديث البراء بن عازب مرفوعا نحوه، ونحوه للطحاوى من طريق محمود بن عبد الرحمن بن ثوبان عن رجل من الصحابة أنصارى مرفوعا.','1','352',NULL,NULL,'0');
INSERT INTO [b88] VALUES (353,'<span data-type="title" id=toc-354>٣٥٣ </span>- عن ابن عمر قال: كانت عائكة بنة زيد بن عمرو بن نفيل عند عمر بن الخطاب وكانت تشهد الصلاة فى المسجد، وكان عمر يقول لها: والله إنك لتعلمين أنى ما أحب هذا، قالت: والله لا أنتهى حتى تنهانى، قال: فلقد طعن عمر وإنها لفى المسجد.
** عب
(فتح الباري ٣٨٣/٢)
** مرسل
** قال الحافظ فى " الفتح " ٢ / ٣٨٣: وصله عبد الأعلى عن معمر بذكر سالم بن عبد الله عن أبيه لكن أبهم المرأة أخرجه أحمد عنه وسماها أحمد من وجه آخر عن سالم قال: كان عمر رجلا غيورا وكان إذا خرج إلى الصلاة اتبعته عائكة بنت زيد.. " الحديث وهو مرسل أيضا.','1','353',NULL,NULL,'0');
INSERT INTO [b88] VALUES (354,'<span data-type="title" id=toc-355>٣٥٤ </span>- حديث: " الجمعة على من آواه الليل إلى أهله ".
** ت
(فتح الباري ٣٨٥/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٨٥: نقل أحمد أنه لم يره شيئا وقال لمن ذكره له: استغفر ربك.','1','354',NULL,NULL,'0');
INSERT INTO [b88] VALUES (355,'<span data-type="title" id=toc-356>٣٥٥ </span>- رواية عبد الله بن سيدان قال: شهدت الجمعة مع أبى بكر فكانت صلاته وخطبته قبل نصف النهار، وشهدتها مع عمر رضى الله عنه فكانت صلاته وخطبته إلى أن أقول قد انتصف النهار.
** أبو نعيم فى كتاب الصلاة شب
(فتح الباري ٣٨٧/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٨٧: رجاله ثقات إلا عبد الله بن سيدان تابعى كبير إلا أنه غير معروف العدالة، قال ابن عدى: شبه مجهول، قال البخارى: لا يتابع على حديثه، بل عارضه ما هو أقوى منه اه.','1','355',NULL,NULL,'0');
INSERT INTO [b88] VALUES (356,'<span data-type="title" id=toc-357>٣٥٦ </span>- من طريق سويد بن غفلة: أنه صلى مع أبى بكر وعمر حين زالت الشمس.
** شب
(فتح الباري ٣٨٧/٢)
** إسناده قوى','1','356',NULL,NULL,'0');
INSERT INTO [b88] VALUES (357,'<span data-type="title" id=toc-358>٣٥٧ </span>- عن مالك بن أبى عامر قال: كنت أرى طنفسة لعقيل بن أبى طالب تطرح يوم الجمعة إلى جدار المسجد الغربى، فإذا غشيتها ظل الجدار فرج عمر.
** ما
(فتح الباري ٣٨٧/٢)
** إسناده صحيح','1','357',NULL,NULL,'0');
INSERT INTO [b88] VALUES (358,'<span data-type="title" id=toc-359>٣٥٨ </span>- من طريق أبى إسحق أنه صلى خلف على الجمعة بعد ما زالت الشمس.
** شب
(فتح الباري ٣٨٧/٢)
** إسناده صحيح','1','358',NULL,NULL,'0');
INSERT INTO [b88] VALUES (359,'<span data-type="title" id=toc-360>٣٥٩ </span>- عن سماك بن حرب قال: كان النعمان بن بشير يصلى بناالجمعة بعد ما تزول الشمس.
** شب
(فتح الباري ٣٨٧/٢)
** إسناده صحيح','1','359',NULL,NULL,'0');
INSERT INTO [b88] VALUES (360,'<span data-type="title" id=toc-361>٣٦٠ </span>- من طريق الوليد بن العيزار قال: ما رأيت إماما كان أحسن صلاة للجمعة من عمرو بن حريث فكان يصليها إذا زالت الشمس.
** شب
(فتح الباري ٣٨٧/٢)
** إسناده صحيح','1','360',NULL,NULL,'0');
INSERT INTO [b88] VALUES (361,'<span data-type="title" id=toc-362>٣٦١ </span>- من طريق عبد الله بن سلمة قال: صلى بنا عبد الله _ يعنى ابن مسعود _ الجمعة ضحى وقال: خشيت عليكم الحر.
** شب
(فتح الباري ٣٨٧/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٨٧: عبد الله صدوق إلا أنه تغير لما كبر، قاله شعبة وغيره.','1','361',NULL,NULL,'0');
INSERT INTO [b88] VALUES (362,'<span data-type="title" id=toc-363>٣٦٢ </span>- من طريق سعيد بن سويد قال: صلى بنا معاوية الجمعة ضحى.
** شب
(فتح الباري ٣٨٧/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٨٧: سعيد ذكره ابن عدى فى الضعفاء.','1','362',NULL,NULL,'0');
INSERT INTO [b88] VALUES (363,'<span data-type="title" id=toc-364>٣٦٣ </span>- من طريق مكحول: أن النداء كان على عهد رسول الله صلى الله عليه وسلم يؤذن يوم الجمعة مؤذن واحد حين يخرج الإمام، وذلك النداء الذى يحرم عنده البيع.
** عمر بن شبة فى أخبار المدينة
(فتح الباري ٣٩١/٢)
** مرسل
** قال الحافظ فى " الفتح " ٢ / ٣٩١: هو مرسل يعتضد بشواهد.','1','363',NULL,NULL,'0');
INSERT INTO [b88] VALUES (364,'<span data-type="title" id=toc-365>٣٦٤ </span>- عن مكحول، عن معاذ: أن عمر أمر مؤذنين أن يؤذنا للناس الجمعة خارجا من المسجد حتى يسمع الناس، وأمر أن يؤذن بين يديه كما كان فى عهد النبى صلى الله عليه وسلم وأبى بكر، ثم قال عمر: نحن ابتدعناه لكثرة المسلمين.
** تفسير جورير
(فتح الباري ٣٩٥/٢)
** منقطع
** قال الحافظ فى " الفتح " ٢ / ٣٩٥: هذا منقطع بين مكحول ومعاذ، ولا يثبت لأن معاذ كان خرج من المدينة إلى الشام فى أول ما غزوا الشام واستمر إلى أن مات بالشام فى طاعون علواس.','1','364',NULL,NULL,'0');
INSERT INTO [b88] VALUES (365,'<span data-type="title" id=toc-366>٣٦٥ </span>- قال سليمان بن موسى: أول من زاد الأذان بالمدينة عثمان، فقال عطاء: كلا إنما كان يدعو الناس دعاء ولا يؤذن غير أذان واحد.
** عب
(فتح الباري ٣٩٥/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٩٥: عطاء لم يدرك عثمان فرواية من أثبت ذلك عنه مقدمة على إنكاره.','1','365',NULL,NULL,'0');
INSERT INTO [b88] VALUES (366,'<span data-type="title" id=toc-367>٣٦٦ </span>- عن ابن عمر أن تميما الدارى قال لرسول الله صلى الله عليه وسلم لما كثر لحمه: ألا نتخذ لك منبرا يحمل عظامك؟ ، قال: " بلى "، فاتخذ له منبرا.
** د الحسن بن سفيان هق
(فتح الباري ٣٩٨/٢)
** إسناده جيد
** قال الحافظ فى " الفتح " ٢ / ٣٩٨: ليس فى جميع الروايات التى سمى فيها النجار شىء قوى السند إلا حديث ابن عمر.','1','366',NULL,NULL,'0');
INSERT INTO [b88] VALUES (367,'<span data-type="title" id=toc-368>٣٦٧ </span>- من حديث أبى هريرة: أن النبى صلى الله عليه وسلم كان يخطب، وهو مستندا إلى جذع فقال: " إن القيام قد شق على "، فقال له تميم الدارى: ألا أعمل لك منبرا كما رأيت يصنع بالشام؟ ، فشاور النبى صلى الله عليه وسلم المسلمين فى ذلك فرأوا أن يتخذه، فقال العباس بن عبد المطلب: أن لى غلاما يقال له: كلاب أعمل الناس، فقال مره أن يعمل.
** سعد
(فتح الباري ٣٩٨/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٩٨: رجاله ثقات إلا الواقدى.','1','367',NULL,NULL,'0');
INSERT INTO [b88] VALUES (368,'<span data-type="title" id=toc-369>٣٦٨ </span>- عن أنس: كان النبى صلى الله عليه وسلم يقوم يوم الجمعة فيسند ظهره إلى جذع منصوب فى المسجد يخطب، فجاء إليه رومى، فقال: ألا أصنع لك منبرا.
** ت خز
(فتح الباري ٣٩٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٣٩٩: وقع عند الترمذى وابن خزيمة وصححاه.','1','368',NULL,NULL,'0');
INSERT INTO [b88] VALUES (369,'<span data-type="title" id=toc-370>٣٦٩ </span>- عن طاوس قال: أول من خطب قاعدا معاوية حين كثر شحم بطنه.
** شب
(فتح الباري ٤٠١/٢)
** مرسل
** قال الحافظ فى " الفتح " ٢ / ٤٠١: يعضده ما روى سعيد بن منصور عن الحسن قال: أول من استراح فى الخطبة يوم الجمعة عثمان، وكان إذا أعيى جلس ولم يتكلم حتى يقوم وأول من خطب جالسا معاوية.','1','369',NULL,NULL,'0');
INSERT INTO [b88] VALUES (370,'<span data-type="title" id=toc-371>٣٧٠ </span>- عن أنس: أنه كان إذا أخذ الإمام فى الخطبة يوم الجمعة يستقبله بوجهه حتى يفرغ من الخطبة.
** نسخة نعيم بن حماد
(فتح الباري ٤٠٢/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٤٠٢: ورواه ابن المنذر من وجه آخر.','1','370',NULL,NULL,'0');
INSERT INTO [b88] VALUES (371,'<span data-type="title" id=toc-372>٣٧١ </span>- عن المسور بن مخرمة: كان النبى صلى الله عليه وسلم إذا خطب خطبة قال: " أما بعد ".
** الحافظ عبد القادر الرهاوى فى خطبة الأربعين المتباينة
(فتح الباري ٤٠٦/٢)
** رجاله ثقات','1','371',NULL,NULL,'0');
INSERT INTO [b88] VALUES (372,'<span data-type="title" id=toc-373>٣٧٢ </span>- من طريق أبى صالح: عن أبى ذر: أنه أتى النبى صلى الله عليه وسلم وهو يخطب فقال لأبى ذر: " صليت ركعتين؟ "، قال: لا....، الحديث.
** طب
(فتح الباري ٤٠٨/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٠٨: فى إسناده ابن لهيعة، وشذ بقوله ": وهو يخطب "، فإن الحديث المشهور عن أبى ذر أنه جاء إلى النبى صلى الله عليه وسلم وهو جالس فى المسجد أخرجه ابن حبان وغيره.','1','372',NULL,NULL,'0');
INSERT INTO [b88] VALUES (373,'<span data-type="title" id=toc-374>٣٧٣ </span>- قوله صلى الله عليه وسلم للذى دخل يتخطى رقاب الناس: " اجلس فقد آذيت ".
** د ن
(فتح الباري ٤٠٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٠٩: صححه ابن خزيمة وغيره من حديث عبد الله بن بشر.','1','373',NULL,NULL,'0');
INSERT INTO [b88] VALUES (374,'<span data-type="title" id=toc-375>٣٧٤ </span>- عن ابن عمر رفعه: " إذا دخل أحدكم والإمام على المنبر فلا صلاة ولا كلام حتى يفرغ الإمام ".
** طب
(فتح الباري ٤٠٩/٢)
** ضعيف
** قال الحافظ فى " الفتح " ٢ / ٤٠٩: حديث ابن عمر ضعيف فيه أيوب بن نهيك وهو منكر الحديث، قاله أبو زرعة وأبو حاتم، والأحاديث الصحيحة لا تعارض بمثله.','1','374',NULL,NULL,'0');
INSERT INTO [b88] VALUES (375,'<span data-type="title" id=toc-376>٣٧٥ </span>- حديث أنس قال: دخل رجل من قيس المسجد.... فذكر نحو قصة سليك.
** قط
(فتح الباري ٤٠٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٠٩: الدارقطنى الذى أخرجه من حديث أنس قد ضعفه، وقال: إن الصواب أنه من رواية سليمان التيمى مرسلا أو معضلا.','1','375',NULL,NULL,'0');
INSERT INTO [b88] VALUES (376,'<span data-type="title" id=toc-377>٣٧٦ </span>- عن عياض بن أبى سرح أن أبا سعيد الخدرى دخل ومروان يخطب فصلى الركعتين فأراد حرس مروان أن يمنعوه فأبى حتى صلاهما ثم قال: ما كنت لأدعهما بعد أن سمعت رسول الله صلى الله عليه وسلم يأمر بهما.
** ت خز
(فتح الباري ٤١١/٢)
** قال الحافظ فى " الفتح " ٢ / ٤١١: روى الترمذى وابن خزيمة وصححاه.','1','376',NULL,NULL,'0');
INSERT INTO [b88] VALUES (377,'<span data-type="title" id=toc-378>٣٧٧ </span>- حديث ابن عباس مرفوعا: " من تكلم يوم الجمعة والإمام يخطب فهو كالحمار يحمل أسفارا والذى يقول له أنصت ليست له جمعة.
** حم بز
(فتح الباري ٤١٤/٢)
** قال الحافظ فى " الفتح " ٢ / ٤١٤: وله شاهد قوى فى جامع حماد بن سلمة عن ابن عمر موقوفا.','1','377',NULL,NULL,'0');
INSERT INTO [b88] VALUES (378,'<span data-type="title" id=toc-379>٣٧٨ </span>- عن عبد الله بن عبس مولى معاوية قال: قلت لأبى هريرة: إنهم زعموا أن الساعة التى فى يوم الجمعة يستجاب فيها الدعاء رفعت، فقال: كذب من قال ذلك، قلت: فهى فى كل جمعة؟ ، قال: نعم.
** عب
(فتح الباري ٤١٧/٢)
** إسناده قوى','1','378',NULL,NULL,'0');
INSERT INTO [b88] VALUES (379,'<span data-type="title" id=toc-380>٣٧٩ </span>- عن أبى هريرة مرفوعا: " يوم الجمعة فيه طبعت طينة آدم، وفى آخر ثلاث ساعات منه ساعة من دعا الله فيها استجيب له ".
** حم
(فتح الباري ٤١٨/٢)
** قال الحافظ فى " الفتح " ٢ / ٤١٨: وفى إسناده فرج بن فضالة وهو ضعيف، وعلى لم يسمع من أبى هريرة.','1','379',NULL,NULL,'0');
INSERT INTO [b88] VALUES (380,'<span data-type="title" id=toc-381>٣٨٠ </span>- من طريق كثير بن عبد الله بن عمر بن عوف عن أبيه عن جده مرفوعا وفيه، قالوا: أية ساعة يارسول الله؟ ، قال: " حين تقام الصلاة إلى الإنصراف منها ".
** ت جه
(فتح الباري ٤١٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٤١٩: وقد ضعف كثير رواية كثير.','1','380',NULL,NULL,'0');
INSERT INTO [b88] VALUES (381,'<span data-type="title" id=toc-382>٣٨١ </span>- عن أنس مرفوعا بلفظ:: بعد العصر إلى غيبوبة الشمس ".
** ت
(فتح الباري ٤٢٠/٢)
** إسناده ضعيف','1','381',NULL,NULL,'0');
INSERT INTO [b88] VALUES (382,'<span data-type="title" id=toc-383>٣٨٢ </span>- عن مرجانة، مولاة فاطمة بنت رسول الله صلى الله عليه وسلم، قالت: حدثتنى فاطمة عليها السلام عن أبيها.... فذكر الحديث، وفيه: قلت للنبى صلى الله عليه وسلم أى ساعة هى؟ ، قال: " إذا تدلى نصف الشمس للغروب " فكانت فاطمة إذا كان يوم الجمعة، أرسلت غلاما لها، يقال له: زيد ينظر لها الشمس فإذا أخبرها أنها تدلت للغروب أقبلت على الدعاء إلى أن تغيب.
** طس الدارقطنى فى العلل هب البيهقى فى فضائل الأوقات
(فتح الباري ٤٢١/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٢١: فى إسناده اختلاف على زيد بن على، وفى بعض رواته من لا يعرف حاله.','1','382',NULL,NULL,'0');
INSERT INTO [b88] VALUES (383,'<span data-type="title" id=toc-384>٣٨٣ </span>- عن أبى سلمة بن عبد الرحمن أن ناسا من الصحابة اجتمعوا فتذاكروا ساعة الجمعة ثم افترقوا فلم يختلفوا أنها آخر ساعة من يوم الجمعة.
** ص
(فتح الباري ٤٢١/٢)
** إسناده صحيح','1','383',NULL,NULL,'0');
INSERT INTO [b88] VALUES (384,'<span data-type="title" id=toc-385>٣٨٤ </span>- " ما بين أن يجلس الإمام على المنبر إلى أن تقضى الصلاة ".
** م د
(فتح الباري ٤٢٢/٢)
** مضطرب منقطع
** قال الحافظ فى " الفتح " ٢ / ٤١٩: رواه مسلم وأبو داود من طريق مخرمة بن بكير، عن أبيه عن أبى بردة بن أبى موسى أن ابن عمر سأله عما سمع من أبيه فى ساعة الجمعة فقال: سمعت أبى يقول: سمعت رسول الله صلى الله عليه وسلم فذكره. وقال الحافظ فى " الفتح " ٢ / ٤٢٢: حديث أبى موسى هذا أعل بالإنقطاع والإضطراب، فأما الإنقطاع فلان مخرمة بن بكير لم يسمع من أبيه قاله أحمد عن حماد بن خالد عن مخرمة نفسه، وكذا قال سعيد بن أبى مريم عن موسى بن سلمة عن مخرمة وزاد إنما هى كتب كانت عندنا، وقال على بن المدينى: لم أسمع أحدا من أهل المدينة يقول عن مخرمة إنه قال فى
شىء من حديثه _ سمعت أبى _، ولا يقال مسلم يكتفى فى المعنعن بإمكان اللقاء مع المعاصرة وهو كذلك هنا لأنا نقول: وجود التصريح عن مخرمة بإنه لم يسمع من
أبيه كان فى دعوى الإنقطاع، وأما الإضطراب فقد رواه أبو إسحق وواصل الأحدب ومعاوية بن قرة وغيرهم عن أبى بردة من قوله، وهؤلاء من أهل الكوفة وأبو بردة كوفى، فهم أعلم بحديثه من بكير المدنى، وهم عدد وهو واحد، وأيضا فلوكان عند أبى بردة مرفوعا لم يفت فيه برأيه بخلاف المرفوع ولهذا جزم الدارقطنى بأن الموقوف هو الصواب.','1','384',NULL,NULL,'0');
INSERT INTO [b88] VALUES (385,'<span data-type="title" id=toc-386>٣٨٥ </span>- عن أبى قتادة قال: قال لهم رسول الله صلى الله عليه وسلم: " كم أنتم "، فعدوا أنفسهم، فإذا هم اثنا عشر رجلا وامرأة.
** تفسير الطبرى ابن أبى حاتم فى التفسير
(فتح الباري ٤٢٤/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٤٢٤: وفى تفسير اسماعيل بن أبى زياد الشامى: " وامرأتان "، ولابن مردوية من حديث ابن عباس وسبع نسوة، لكن إسناده ضعيف واتفقت هذه الروايات كلها على اثنى عشر رجلا، إلا ما رواه على بن عاصم عن حصين بالإسناد المذكور فقال " إلا أربعين رجلا ". أخرجه الدارقطنى وقال: تفرد به على بن عاصم وهو ضعيف الحفظ وخالفه أصحاب حصين كلهم.','1','385',NULL,NULL,'0');
INSERT INTO [b88] VALUES (386,'<span data-type="title" id=toc-387>٣٨٦ </span>- من طريق جعفر بن محمد عن أبيه مرسلا: كان النبى صلى الله عليه وسلم يخطب يوم الجمعة، وكانت لهم سوق كانت بنو سليم يجلبون إليها الخيل، والإبل والسمن فقدموا فخرج إليهم الناس وتركوه، وكان لهم لهوا يضربونه فنزلت.
** شفع
(فتح الباري ٤٢٤/٢)
** مرسل
** قال الحافظ فى " الفتح " ٢ / ٤٢٤: ووصله أبو عوانة فى صحيحه والطبرى بذكر جابر فيه، أنهم كانوا إذا نكحوا تضرب الجوارى بالمزامير فيشتد الناس إليهم ويدعون رسول الله صلى الله عليه وسلم قائما فنزلت هذه الآية.','1','386',NULL,NULL,'0');
INSERT INTO [b88] VALUES (387,'<span data-type="title" id=toc-388>٣٨٧ </span>- كان رجال يقومون إلى نواضحهم، وإلى السفر يقدمون يبتغون التجارة، واللهو فنزلت.
** عبد
(فتح الباري ٤٢٤/٢)
** مرسل','1','387',NULL,NULL,'0');
INSERT INTO [b88] VALUES (388,'<span data-type="title" id=toc-389>٣٨٨ </span>- قال صلى الله عليه وسلم: " لو تتابعتم حتى لم يبق منكم أحد لسال بكم الوادى نارا ".
** إسماعيل بن أبى زياد الشامى فى التفسير
(فتح الباري ٤٢٥/٢)
** إسناده ساقط
** قال الحافظ فى " الفتح " ٢ / ٤٢٥: وقعت فى مرسلى الحسن وقتادة وكذا فى حديث ابن عباس عند ابن مردوية وفى حديث أنس عند إسماعيل بن أبى زياد وسنده ساقط.','1','388',NULL,NULL,'0');
INSERT INTO [b88] VALUES (389,'<span data-type="title" id=toc-390>٣٨٩ </span>- عن أبى هريرة بلفظ: كان يصلى قبل الجمعة ركعتين وبعدها أربعا.
** بز
(فتح الباري ٤٢٦/٢)
** ضعيف
** قال الحافظ فى " الفتح " ٢ / ٤٢٦: ورد فى سنة الجمعة التى قبلها أحاديث ضعيفة منها عن أبى هريرة رواه البزار وفى إسناده ضعف. وقال الحافظ فى " الفتح " ٢ / ٤٢٦: وروى ابن سعد عن صفية زوج النبى صلى الله عليه وسلم موقوفا نحو حديث أبى هريرة.','1','389',NULL,NULL,'0');
INSERT INTO [b88] VALUES (390,'<span data-type="title" id=toc-391>٣٩٠ </span>- عن على بلفظ: كان يصلى قبل الجمعة أربعا وبعدها أربعا.
** الأثرم طس
(فتح الباري ٤٢٦/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٢٦: فيه محمد بن عبد الرحمن السهمى وهو ضعيف عند البخارى وغيره، وقال الأثرم إنه حديث واه، وعن ابن عباس مثله وزاد " لا يفصل فى شىء منهن "، أخرجه ابن ماجه بسند واه، قال النووى فى الخلاصة: إنه حديث باطل، وعن ابن مسعود عند الطبرانى أيضا مثله وفى إسناده ضعف وانقطاع ورواه عبد الرزاق عن ابن مسعود موقوفا وهو الصواب.','1','390',NULL,NULL,'0');
INSERT INTO [b88] VALUES (391,'<span data-type="title" id=toc-392>٣٩١ </span>- حديث عبد الله بن الزبير مرفوعا: " ما من صلاة مفروضة إلا وبين يديها ركعتان ".
** لم
(فتح الباري ٤٢٦/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٢٦: صححه ابن حبان.','1','391',NULL,NULL,'0');
INSERT INTO [b88] VALUES (392,'<span data-type="title" id=toc-393>٣٩٢ </span>- عن ابن عمر مرفوعا: قال رسول الله فى صلاة الخوف: أن يكون الإمام يصلى بطائفة...... فذكر نحو سياق سالم عن أبيه وقال فى آخره: " فإن كان خوف أشد من ذلك ".
** جه
(فتح الباري ٤٣٢/٢)
** إسناده جيد','1','392',NULL,NULL,'0');
INSERT INTO [b88] VALUES (393,'<span data-type="title" id=toc-394>٣٩٣ </span>- حديث عبيد الله فى صلاة الطالب بن أنيس إذ بعثه النبى صلى الله عليه وسلم إلى سفيان الهذلى، قال: فرأيته وحضرت العصر فخشيت فوتها فانطلقت أمشى وأنا أصلى أو مىء إيماء.
** د
(فتح الباري ٤٣٧/٢)
** إسناده حسن','1','393',NULL,NULL,'0');
INSERT INTO [b88] VALUES (394,'<span data-type="title" id=toc-395>٣٩٤ </span>- عن ابن عمر أنه كان يلبس أحسن ثيابه فى العيدين.
** ابن أبى الدنيا هق
(فتح الباري ٤٣٩/٢)
** إسناده صحيح','1','394',NULL,NULL,'0');
INSERT INTO [b88] VALUES (395,'<span data-type="title" id=toc-396>٣٩٥ </span>- من طريق فليح، عن هشام بن عروة: وحمامة وصاحبتها تغنيان.
** ابن أبى الدنيا فى العيدين
(فتح الباري ٤٤٠/٢)
** إسناده صحيح','1','395',NULL,NULL,'0');
INSERT INTO [b88] VALUES (396,'<span data-type="title" id=toc-397>٣٩٦ </span>- عن أنس: قدم النبى صلى الله عليه وسلم المدينة، ولهم يومان يلعبون فيهما فقال: " قد أبدلكم الله تعالى بهما خيرا منهما يوم الفطر والأضحى ".
** ن حب
(فتح الباري ٤٤٢/٢)
** إسناده صحيح','1','396',NULL,NULL,'0');
INSERT INTO [b88] VALUES (397,'<span data-type="title" id=toc-398>٣٩٧ </span>- من طريق أبى سلمة عن عائشة: دخل الحبشة يلعبون، فقال لى النبى صلى الله عليه وسلم: " يا حميراء أتحبين أن تنظرى إليهم؟ "، فقلت: نعم.
** ن
(فتح الباري ٤٤٤/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٤٤٤: لم أر فى حديث صحيح ذكر الحميراء إلا فى هذا.','1','397',NULL,NULL,'0');
INSERT INTO [b88] VALUES (398,'<span data-type="title" id=toc-399>٣٩٨ </span>- من حديث واثلة أنه لقى رسول الله صلى الله عليه وسلم يوم عيد، فقال: تقبل الله منا ومنك، فقال: " نعم تقبل الله منا ومنك ".
** عد
(فتح الباري ٤٤٦/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٤٦: فى إسناده محمد بن إبراهيم الشامى وهو ضعيف وقد تفرد به مرفوعا وخولف فيه.','1','398',NULL,NULL,'0');
INSERT INTO [b88] VALUES (399,'<span data-type="title" id=toc-400>٣٩٩ </span>- من حديث عبادة بن الصامت أنه سأل رسول الله صلى الله عليه وسلم عن ذلك، فقال: " ذلك فعل أهل الكتابين ".
** هق
(فتح الباري ٤٤٦/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٤٤٥: كأنه أراد أنه لم يصح فيه شىء.','1','399',NULL,NULL,'0');
INSERT INTO [b88] VALUES (400,'<span data-type="title" id=toc-401>٤٠٠ </span>- عن جبير بن نفير قال: كان أصحاب رسول الله صلى الله عليه وسلم إذا التقوا يوم العيد يقول بعضهم لبعض: تقبل الله منا ومنك.
** المحامليات
(فتح الباري ٤٤٦/٢)
** إسناده حسن','1','400',NULL,NULL,'0');
INSERT INTO [b88] VALUES (401,'<span data-type="title" id=toc-402>٤٠١ </span>- من حديث بريدة قال: كان النبى صلى الله عليه وسلم لا يخرج يوم الفطر حتى يطعم، ولا يطعم يوم الأضحى حتى يصلى.
** ت ك
(فتح الباري ٤٤٨/٢)
** إسناده فيه مقال
** قال الحافظ فى " الفتح " ٢ / ٤٤٨: ونحوه عند البزار عن جابر بن سمرة. وقال الحافظ فى " الفتح " ٢ / ٤٤٨: وفى كل من الأسانيد الثلاثة مقال.','1','401',NULL,NULL,'0');
INSERT INTO [b88] VALUES (402,'<span data-type="title" id=toc-403>٤٠٢ </span>- من حديث ابن عباس: من السنة أن لا يخرج يوم الفطر حتى يخرج الصدقة ويطعم شيئا قبل أن يخرج.
** طب قط
(فتح الباري ٤٤٨/٢)
** إسناده فيه مقال
** قال الحافظ فى " الفتح " ٢ / ٤٤٨: وفى كل من الأسانيد الثلاثة مقال.','1','402',NULL,NULL,'0');
INSERT INTO [b88] VALUES (403,'<span data-type="title" id=toc-404>٤٠٣ </span>- عن أبى سعيد: أول من خطب الناس فى المصلى على المنبر عثمان بن عفان كلهم على منبر من طين بناه كثير بن الصلت.
** المدونة لمالك عمر بن شبة
(فتح الباري ٤٤٩/٢)
** معضل
** قال الحافظ فى " الفتح " ٢ / ٤٤٩: مافى الصحيحين أصح.','1','403',NULL,NULL,'0');
INSERT INTO [b88] VALUES (404,'<span data-type="title" id=toc-405>٤٠٤ </span>- عن نافع قال: كان اسم كثير بن الصلت قليلا فسماه عمر كثيرا.
** سعد
(فتح الباري ٤٤٩/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٤٤٩: ورواه أبو عوانة فوصله بذكر ابن عمر ورفعه بذكر النبى صلى الله عليه وسلم والأول أصح.','1','404',NULL,NULL,'0');
INSERT INTO [b88] VALUES (405,'<span data-type="title" id=toc-406>٤٠٥ </span>- عن على قال: من السنة أن يخرج إلى العيد ماشيا.
** ت
(فتح الباري ٤٥١/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٤٥١: وأسانيد الثلاثة ضعاف.','1','405',NULL,NULL,'0');
INSERT INTO [b88] VALUES (406,'<span data-type="title" id=toc-407>٤٠٦ </span>- عن سعد القرظ: أن النبى صلى الله عليه وسلم كان يأتى العيد ماشيا.
** جه
(فتح الباري ٤٥١/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٤٥١: وفيه عن أبى رافع نحوه، وأسانيد الثلاثة ضعاف.','1','406',NULL,NULL,'0');
INSERT INTO [b88] VALUES (407,'<span data-type="title" id=toc-408>٤٠٧ </span>- عن الحسن البصرى قال: أول من خطب قبل الصلاة عثمان، صلى بالناس ثم خطبهم _ يعنى على العادة _ فرأى ناسا لم يدركوا الصلاة ففعل ذلك.
** ابن المنذر
(فتح الباري ٤٥١/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٤٥٢: وقد روى عن عمر مثل فعل عثمان، قال عياض ومن تبعه: لا يصح عنه وفيما قالوا نظر، لأن عبد الرزاق وابن أبى شيبة روياه جميعا عن ابن عيينة عن يحيى بن سعيد الأنصارى عن يوسف بن عبد الله بن سلام، وهذا إسناد صحيح لكن يعارضه حديث ابن عباس، وكذا حديث ابن عمر.','1','407',NULL,NULL,'0');
INSERT INTO [b88] VALUES (408,'<span data-type="title" id=toc-409>٤٠٨ </span>- عن ابن عباس: أن رسول الله صلى الله عليه وسلم صلى العيد بلا أذان ولا إقامة.
** د
(فتح الباري ٤٥٢/٢)
** إسناده صحيح','1','408',NULL,NULL,'0');
INSERT INTO [b88] VALUES (409,'<span data-type="title" id=toc-410>٤٠٩ </span>- عن الثقة عن الزهرى قال: كان رسول الله صلى الله عليه وسلم يأمر المؤذن فى العيدين أن يقول: الصلاة جامعة.
** شفع
(فتح الباري ٤٥٢/٢)
** مرسل
** قال الحافظ فى " الفتح " ٢ / ٤٥٢: هذا مرسل يعضده القياس على صلاة الكسوف لثبوت ذلك.
** تعقيب: قال الشيخ ابن باز ٢ / ٤٥٢: مراسيل الزهرى ضعيفة عند أهل العلم، والقياس لا يصح اعتباره مع وجود النص الثابت الدال اه.','1','409',NULL,NULL,'0');
INSERT INTO [b88] VALUES (410,'<span data-type="title" id=toc-411>٤١٠ </span>- قال: نهى رسول الله صلى الله عليه وسلم أن يخرج بالسلاح يوم العيد.
** عب
(فتح الباري ٤٥٥/٢)
** إسناده مرسل','1','410',NULL,NULL,'0');
INSERT INTO [b88] VALUES (411,'<span data-type="title" id=toc-412>٤١١ </span>- عن عبد الله بن عباس: أن النبى صلى الله عليه وسلم نهى أن يلبس السلاح فى بلاد الإسلام فى العيدين، إلا أن يكونوا بحضرة العدو.
** جه
(فتح الباري ٤٥٥/٢)
** إسناده ضعيف','1','411',NULL,NULL,'0');
INSERT INTO [b88] VALUES (412,'<span data-type="title" id=toc-413>٤١٢ </span>- أن الحجاج دخل على ابن عمر يعوده لما أصيبت رجله فقال له: يا أبا عبد الرحمن هل تدرى من أصاب رجلك؟ ، قال: لا، قال: أما والله لو علمت من أصابك لقتلته، قال: فأطرق ابن عمر فجعل لا يكلمه ولا يلتفت إليه فوثب كالمغضب.
** سعد
(فتح الباري ٤٥٦/٢)
** رجاله لا بأس بهم','1','412',NULL,NULL,'0');
INSERT INTO [b88] VALUES (413,'<span data-type="title" id=toc-414>٤١٣ </span>- من طريق يزيد بن خمير قال: خرج عبد الله بن بسر صاحب النبى صلى الله عليه وسلم مع الناس يوم عيد فطر أو أضحى فأنكر إبطاء الإمام وقال: إن كنا مع النبى صلى الله عليه وسلم وقد فرغنا ساعتنا هذه.
** حم د ك
(فتح الباري ٤٥٧/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٥٧: صححه الحاكم.','1','413',NULL,NULL,'0');
INSERT INTO [b88] VALUES (414,'<span data-type="title" id=toc-415>٤١٤ </span>- حديث على: " لا جمعة ولا تشريق إلا فى مصر جامع ".
** أبو عبيد
(فتح الباري ٤٥٧/٢)
** إسناده صحيح موقوفا','1','414',NULL,NULL,'0');
INSERT INTO [b88] VALUES (415,'<span data-type="title" id=toc-416>٤١٥ </span>- من مرسل الشعبى: " من ذبح قبل التشريق _ أى قبل صلاة العيد _ فليعد ".
** أبو عبيد
(فتح الباري ٤٥٧/٢)
** رجاله ثقات مرسلا','1','415',NULL,NULL,'0');
INSERT INTO [b88] VALUES (416,'<span data-type="title" id=toc-417>٤١٦ </span>- عن ابن عباس قال: الأيام المعلومات التى قبل يوم التروية ويوم التروية ويوم عرفة، والمعدودات أيام التشريق.
** ابن مردوية
(فتح الباري ٤٥٨/٢)
** إسناده صحيح','1','416',NULL,NULL,'0');
INSERT INTO [b88] VALUES (417,'<span data-type="title" id=toc-418>٤١٧ </span>- عن أبى هريرة: يعدل صيام كل يوم منها بصيام سنة وقيام كل ليلة بقيام ليلة القدر.
** ت
(فتح الباري ٤٦١/٢)
** إسناده ضعيف','1','417',NULL,NULL,'0');
INSERT INTO [b88] VALUES (418,'<span data-type="title" id=toc-419>٤١٨ </span>- من طريق عدى بن ثابت فى حديث ابن عباس: " فأكثروا فيهن من التهليل والتكبير ". وفى رواية عدى من الزيادة: " وإن صيام يوم منها يعدل صيام سنة والعمل بسبعمائة ضعف ".
** هب
(فتح الباري ٤٦١/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٤٦١: طريق سعيد بن المسيب عن أبى هريرة إسناده ضعيف، وكذا الإسناد إلى عدى بن ثابت.','1','418',NULL,NULL,'0');
INSERT INTO [b88] VALUES (419,'<span data-type="title" id=toc-420>٤١٩ </span>- عن سلمان قال: كبروا الله، الله أكبر الله أكبر، الله أكبر كبيرا.
** عب
(فتح الباري ٤٦٢/٢)
** إسناده صحيح','1','419',NULL,NULL,'0');
INSERT INTO [b88] VALUES (420,'<span data-type="title" id=toc-421>٤٢٠ </span>- من طريق المطلب بن عبد الله بن خنطب مرسلا: أنه صلى الله عليه وسلم كان يغدو يوم العيد إلى المصلى من الطريق الأعظم ويرجع من الطريق الأخرى.
** شفع
(فتح الباري ٤٧٣/٢)
** مرسل','1','420',NULL,NULL,'0');
INSERT INTO [b88] VALUES (421,'<span data-type="title" id=toc-422>٤٢١ </span>- قال ابن مسعود: " من فاته العيد مع الإمام فليصل أربعا ".
** ص
(فتح الباري ٤٧٥/٢)
** إسناده صحيح','1','421',NULL,NULL,'0');
INSERT INTO [b88] VALUES (422,'<span data-type="title" id=toc-423>٤٢٢ </span>- حديث عقبة بن عامر مرفوعا: " أيام منى عيدنا أهل الإسلام ".
** د ت ن
(فتح الباري ٤٧٥/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٢٢: هو فى السنن وصححه ابن خزيمة.','1','422',NULL,NULL,'0');
INSERT INTO [b88] VALUES (423,'<span data-type="title" id=toc-424>٤٢٣ </span>- حديث أبى سعيد: أن النبى صلى الله عليه وسلم كان لا يصلى قبل العيد شيئا فإذا رجع إلى منزله صلى ركعتين.
** جه
(فتح الباري ٤٧٦/٢)
** إسناده حسن
** قال الحافظ فى " الفتح " ٢ / ٤٧٦: صححه الحاكم.','1','423',NULL,NULL,'0');
INSERT INTO [b88] VALUES (424,'<span data-type="title" id=toc-425>٤٢٤ </span>- عن ابن عمر مرفوعا: " صلاة الليل والنهار مثنى مثنى ".
** د ت ن جه
(فتح الباري ٤٧٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٧٩: فى السنن وصححه ابن خزيمة وغيره. وتعقب بأن أكثر أئمة الحديث أعلوا هذه الزيادة، وهى قوله " والنهار " بأن الحفاظ من أصحاب ابن عمر لم يذكروها عنه، وحكم النسائى على راويها بأنه أخطأ فيها وقال يحيى بن معين: من على الأزدى حتى أقبل منه، وادعى يحيى بن سعيد الأنصارى عن نافع أن ابن عمر كان يتطوع بالنهار أربعا لا يفصل بينهن ولو كان حديث الأزدى صحيحا لا خالفه ابن عمر يعنى مع شدة اتباعه رواه عنه محمد نصر فى سؤالاته، لكن روى ابن وهب بإسناد قوى عن ابن عمر موقوفا، أخرجه ابن عبد البر
من طريقه، فلعل الأزدى اختلط عليه الموقوف بالمرفوع، فلا تكون هذه الزيادة صحيحة فى طريقة من يشترط فى الصحيح أن لا يكون شاذا.','1','424',NULL,NULL,'0');
INSERT INTO [b88] VALUES (425,'<span data-type="title" id=toc-426>٤٢٥ </span>- عن ابن عمر قال: صلاة الليل والنهار مثنى مثنى.
** ابن وهب ابن عبد البر
(فتح الباري ٤٧٩/٢)
** إسناده قوى موقوفا','1','425',NULL,NULL,'0');
INSERT INTO [b88] VALUES (426,'<span data-type="title" id=toc-427>٤٢٦ </span>- عن عائشة: أن النبى صلى الله عليه وسلم كان يصلى ما بين أن يفرغ من العشاء إلى الفجر إحدى عشرة ركعة يسلم من كل ركعتين.
** د محمد بن نصر
(فتح الباري ٤٧٩/٢)
** إسناده على شرط الشيخين','1','426',NULL,NULL,'0');
INSERT INTO [b88] VALUES (427,'<span data-type="title" id=toc-428>٤٢٧ </span>- قوله صلى الله عليه وسلم: " الصلاة خير موضوع، فمن شاء استكثر ومن شاء استقل ".
** لم
(فتح الباري ٤٧٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٧٩: صححه ابن حبان.
** تعقيب: * الحديث أخرجه الإمام أحمد فى مسنده وابن حبان والحاكم وصححاه عن أبى ذر، وأخرجه أحمد وغيره عن أبى أمامة، وأخرجه الطبرانى فى الأوسط عن أبى هريرة وحسنه الألبانى.','1','427',NULL,NULL,'0');
INSERT INTO [b88] VALUES (428,'<span data-type="title" id=toc-429>٤٢٨ </span>- وعن نافع أن ابن عمر كان يقول: من صلى من الليل فليجعل آخر صلاته وترا فإن رسول الله صلى الله عليه وسلم كان يأمر بذلك فإذا كان الفجر فقد ذهب كل صلاة الليل والوتر.
** د ن
(فتح الباري ٤٨٠/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٨٠: صححه أبو عوانة وغيره.','1','428',NULL,NULL,'0');
INSERT INTO [b88] VALUES (429,'<span data-type="title" id=toc-430>٤٢٩ </span>- قوله صلى الله عليه وسلم: " لا وتران فى ليلة ".
** ن خز
(فتح الباري ٤٨٨/٢)
** حسن','1','429',NULL,NULL,'0');
INSERT INTO [b88] VALUES (430,'<span data-type="title" id=toc-431>٤٣٠ </span>- عن أبى هريرة مرفوعا وموقوفا: " لا توتروا بثلاث تشبهوا بصلاة المغرب ".
** محمد بن نصر المروزى
(فتح الباري ٤٨١/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٨١: من طريق عبد الله بن الفضل عن أبى سلمة والأعرج عن أبى هريرة، صححه الحاكم مرفوعا نحوه وإسناده على شرط الشيخين، وقد صححه ابن حبان والحاكم.','1','430',NULL,NULL,'0');
INSERT INTO [b88] VALUES (431,'<span data-type="title" id=toc-432>٤٣١ </span>- حديث أبى أيوب مرفوعا: " الوتر حق فمن شاء أوتر بخمس ومن شاء بثلاث ومن شاء بواحدة ".
** د ن
(فتح الباري ٤٨١/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٨٢: صححه ابن حبان والحاكم.','1','431',NULL,NULL,'0');
INSERT INTO [b88] VALUES (432,'<span data-type="title" id=toc-433>٤٣٢ </span>- عن السائب بن يزيد أن عثمان قرأ القرآن ليلة فى ركعة لم يصل غيرها.
** محمد بن نصر
(فتح الباري ٤٨٢/٢)
** إسناده صحيح','1','432',NULL,NULL,'0');
INSERT INTO [b88] VALUES (433,'<span data-type="title" id=toc-434>٤٣٣ </span>- عن بكر بن عبد الله المزنى قال: صلى ابن عمر ركعتين ثم قال: ياغلام أرحل لنا، ثم قام فأوتر بركعة.
** ص
(فتح الباري ٤٨٢/٢)
** إسناده صحيح موقوفا','1','433',NULL,NULL,'0');
INSERT INTO [b88] VALUES (434,'<span data-type="title" id=toc-435>٤٣٤ </span>- عن عبد الله بن عمر أنه كان يفصل بين شفعه ووتره بتسليمه وأخبر أن النبى صلى الله عليه وسلم كان يفعله.
** طحا
(فتح الباري ٤٨٢/٢)
** إسناده قوى','1','434',NULL,NULL,'0');
INSERT INTO [b88] VALUES (435,'<span data-type="title" id=toc-436>٤٣٥ </span>- من حديث معاذ مرفوعا: " زادنى ربى صلاة وهى الوتر، وقتها من العشاء إلى طلوع الفجر ".
** حم
(فتح الباري ٤٨٧/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٤٨٧: فى إسناده ضعف.','1','435',NULL,NULL,'0');
INSERT INTO [b88] VALUES (436,'<span data-type="title" id=toc-437>٤٣٦ </span>- حديث بريدة رفعه: " الوتر حق فمن لم يوتر فليس منا وأعاد ذلك ثلاثا ".
** لم
(فتح الباري ٤٨٧/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٨٧: فى سنده أبو المنيب وفيه ضعف.','1','436',NULL,NULL,'0');
INSERT INTO [b88] VALUES (437,'<span data-type="title" id=toc-438>٤٣٧ </span>- حديث الحسن بن على قال: علمنى رسول الله صلى الله عليه وسلم كلمات أقولهن فى قنوت الوتر: " اللهم اهدنى فيمن هديت.... " الحديث.
** د ت ن جه
(فتح الباري ٤٩٠/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٩٠: رواه أصحاب السنن وصححه الترمذى وغيره لكن ليس على شرط البخارى.','1','437',NULL,NULL,'0');
INSERT INTO [b88] VALUES (438,'<span data-type="title" id=toc-439>٤٣٨ </span>- عن أنس أنه سئل عن القنوت فقال: قبل الركوع وبعده.
** جه
(فتح الباري ٤٩١/٢)
** إسناده قوى','1','438',NULL,NULL,'0');
INSERT INTO [b88] VALUES (439,'<span data-type="title" id=toc-440>٤٣٩ </span>- عن أنس قال: جاء رجل أعرابى إلى النبى صلى الله عليه وسلم فقال: يارسول الله، أتيناك ومالنا بعير يئط، ولا صبى يغط، ثم أنشده شعرا يقول فيه: وليس لنا إلا إليك فرارنا وأين فرار الناس إلا إلى الرسل. فقام يجر ردائه حتى صعد المنبر فقال: "اللهم اسقنا "، الحديث وفيه ثم قال صلى الله عليه وسلم: "لو كان أبو طالب حيا لقرت عيناه من ينشدنا قوله؟ "، فقام على فقال: يارسول الله كأنك أردت قوله: وأبيض يستسقى الغمام بوجهه.. الأبيات.
** هق فى الدلائل
(فتح الباري ٤٩٥/٢)
** قال الحافظ فى " الفتح " ٢ / ٤٩٥: إسناد حديث أنس وإن كان فيه ضعف، لكنه يصلح للمتابعة وقد ذكره ابن هشام فى زوائده فى السيرة تعليقا عمن يثق به.','1','439',NULL,NULL,'0');
INSERT INTO [b88] VALUES (440,'<span data-type="title" id=toc-441>٤٤٠ </span>- عن مالك الدارى _ وكان خازن عمر _ قال: أصاب الناس قحط فى زمن عمر فجاء رجل إلى قبر النبى صلى الله عليه وسلم فقال: يارسول الله استسق لأمتك فإنهم قد هلكوا، فأتى الرجل فى المنام فقيل له: أأئت عمر.
** شب
(فتح الباري ٤٩٤/٢)
** إسناده صحيح','1','440',NULL,NULL,'0');
INSERT INTO [b88] VALUES (441,'<span data-type="title" id=toc-442>٤٤١ </span>- من حديث ابن عباس: أنه كان يكبر فيهما سبعا وخمسا كالعيد، وأنه يقرأ فيهما بسبح وهل أتاك.
** قط
(فتح الباري ٥٠٠/٢)
** إسناده فيه مقال
** قال الحافظ فى " الفتح " ٢ / ٥٠٠: أصله فىالسنن بلفظ: ثم صلى ركعتين كما يصلى فى العيد.','1','441',NULL,NULL,'0');
INSERT INTO [b88] VALUES (442,'<span data-type="title" id=toc-443>٤٤٢ </span>- عن أنس أن النبى صلى الله عليه وسلم كان إذا هاجت ريح شديدة قال: " اللهم إنى أسألك من خير ماأمرت به، وأعوذ بك من شر ما أمرت به ".
** يع
(فتح الباري ٥٢٠/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٥٢٠: هذه زيادة على رواية حميد يجب قبولها لثقة رواتها، وفى الباب عن عائشة عند الترمذى، وعن أبى هريرة عند أبى داود والنسائى وعن ابن عباس عند الطبرانى وعن غيرهم.','1','442',NULL,NULL,'0');
INSERT INTO [b88] VALUES (443,'<span data-type="title" id=toc-444>٤٤٣ </span>- عن سعيد بن جبير، عن ابن عباس أنه كان يقرأ: " وتجعلون شكركم أنكم تكذبون ".
** ص ابن مردوية فى التفسير
(فتح الباري ٥٢٢/٢)
** إسناده صحيح','1','443',NULL,NULL,'0');
INSERT INTO [b88] VALUES (444,'<span data-type="title" id=toc-445>٤٤٤ </span>- عن أبى قلابة أنه صلى الله عليه وسلم كان كلما ركع ركعة أرسل رجلا ينظر هل انجلث.
** عب
(فتح الباري ٥٢٧/٢)
** إسناده صحيح','1','444',NULL,NULL,'0');
INSERT INTO [b88] VALUES (445,'<span data-type="title" id=toc-446>٤٤٥ </span>- عن النعمان بن بشير: انكسفت الشمس على عهد رسول الله صلى الله عليه وسلم فخرج فزعا يجر ثوبه حتى أتى المسجد، فلم يزل يصلى حتى انجلت، فلما انجلت قال: " إن الناس يزعمون أن الشمس والقمر لا ينكسفان، إلا لموت عظيم من العظماء، وليس كذلك........ " الحديث.
** حم ن جه
(فتح الباري ٥٢٨/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٢٨: صححه ابن خزيمة وابن حبان.','1','445',NULL,NULL,'0');
INSERT INTO [b88] VALUES (446,'<span data-type="title" id=toc-447>٤٤٦ </span>- عن عروة قال: " لا تقولوا كسفت الشمس ولكن قولوا خسفت ".
** ص
(فتح الباري ٥٣٥/٢)
** صحيح موقوفا
** قال الحافظ فى " الفتح " ٢ / ٥٣٥: أخرجه مسلم عن يحيى بن يحيى عنه لكن الأحاديث الصحيحة تخالفه لثبوتها بلفظ الكسوف فى الشمس من طرق كثيرة.','1','446',NULL,NULL,'0');
INSERT INTO [b88] VALUES (447,'<span data-type="title" id=toc-448>٤٤٧ </span>- حديث النعمان بن بشير وغيره: " إن الشمس والقمر لا ينكسفان لموت أحد ولا لحياته ولكنهما آيتان من آيات الله، وإن الله إذا تجلى لشىء من خلقه خشع له ".
** حم ن جه
(فتح الباري ٥٣٧/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٣٧: صححه ابن خزيمة والحاكم.','1','447',NULL,NULL,'0');
INSERT INTO [b88] VALUES (448,'<span data-type="title" id=toc-449>٤٤٨ </span>- حديث عبد الله بن عمرو فيه: ثم ركع فأطال حتى قيل لا يرفع، ثم رفع فأطال حتى قيل لا يسجد ثم سجد فأطال حتى قيل لا يرفع، ثم رفع فجلس فأطال الجلوس حتى قيل لا يسجد، ثم سجد.
** ن خز
(فتح الباري ٥٣٩/٢)
** صحيح
** قال الحافظ فى " الفتح " ٢ / ٥٣٩: لفظ ابن خزيمة من طريق الثورى عن عطاء بن السائب عن أبيه عنه، والثورى سمع من عطاء قبل الاختلاط، فالحديث صحيح.','1','448',NULL,NULL,'0');
INSERT INTO [b88] VALUES (449,'<span data-type="title" id=toc-450>٤٤٩ </span>- قال طاوس: كسفت الشمس فصلى بنا ابن عباس فى صفة زمزم ست ركعات فى أربع سجدت.
** شفع ص
(فتح الباري ٥٤٠/٢)
** صحيح موقوفا
** قال الحافظ فى " الفتح " ٢ / ٥٤٠: هذا موقوف صحيح إلا أن ابن عيينة خولف فيه.','1','449',NULL,NULL,'0');
INSERT INTO [b88] VALUES (450,'<span data-type="title" id=toc-451>٤٥٠ </span>- حديث جابر: فلما قضى الصلاة قال له أبى بن كعب شيئا صنعته فى الصلاة لم تكن تصنعه ... فذكر نحو حديث ابن عباس.
** حم
(فتح الباري ٥٤٠/٢)
** إسناده حسن','1','450',NULL,NULL,'0');
INSERT INTO [b88] VALUES (451,'<span data-type="title" id=toc-452>٤٥١ </span>- من طريق مرسلة: " أردت أن آخذ منها قطفا لأريكموه فلم يقدر ".
** عب
(فتح الباري ٥٤١/٢)
** مرسل','1','451',NULL,NULL,'0');
INSERT INTO [b88] VALUES (452,'<span data-type="title" id=toc-453>٤٥٢ </span>- من حديث مخرمة بن نوفل قال:" لما أظهر النبى صلى الله عليه وسلم الإسلام أسلم أهل مكة حتى أنه كان ليقرأ السجدة فيسجدون فلا يقدر بعضهم أن يسجد من الزحام حتى قدم رؤساء قريش الوليد بن المغيرة وأبو جهل وغيرهما وكانوا بالطائف فرجعوا وقالوا: تدعون دين أبائكم.
** طب
(فتح الباري ٥٥١/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٥١: فى ثبوت هذا نظر.','1','452',NULL,NULL,'0');
INSERT INTO [b88] VALUES (453,'<span data-type="title" id=toc-454>٤٥٣ </span>- عن على بن أبى طالب: أن العزائم: حم والنجم واقرأ وألم تنزيل.
** ابن المنذر
(فتح الباري ٥٥٢/٢)
** إسناده حسن','1','453',NULL,NULL,'0');
INSERT INTO [b88] VALUES (454,'<span data-type="title" id=toc-455>٤٥٤ </span>- عن ابن عمر قال: ولا يسجد الرجل إلا وهو طاهر.
** هق
(فتح الباري ٥٥٤/٢)
** إسناده صحيح','1','454',NULL,NULL,'0');
INSERT INTO [b88] VALUES (455,'<span data-type="title" id=toc-456>٤٥٥ </span>- عن ابن عباس: أن النبى صلى الله عليه وسلم لم يسجد فى شىء من المفصل منذ تحول إلى المدينة.
** د
(فتح الباري ٥٥٥/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٥٥: ضعفه أهل العلم بالحديث لضعف فى رواته واختلاف فى إسناده.','1','455',NULL,NULL,'0');
INSERT INTO [b88] VALUES (456,'<span data-type="title" id=toc-457>٤٥٦ </span>- عن أبى هريرة: أن النبى صلى الله عليه وسلم سجد فى سورة النجم وسجدنا معه.. الحديث.
** بز قط
(فتح الباري ٥٥٥/٢)
** رجاله ثقات','1','456',NULL,NULL,'0');
INSERT INTO [b88] VALUES (457,'<span data-type="title" id=toc-458>٤٥٧ </span>- عن أبى سلمة بن عبد الرحمن أنه رأى أبا هريرة سجد فى خاتمة النجم فسأله فقال: إنه رأى رسول الله صلى الله عليه وسلم يسجد فيها.
** ابن مردوية فى التفسير
(فتح الباري ٥٥٥/٢)
** إسناده حسن','1','457',NULL,NULL,'0');
INSERT INTO [b88] VALUES (458,'<span data-type="title" id=toc-459>٤٥٨ </span>- عن الأسود بن يزيد عن عمر أنه سجد فى إذا السماء انشقت.
** عب
(فتح الباري ٥٥٥/٢)
** إسناده صحيح','1','458',NULL,NULL,'0');
INSERT INTO [b88] VALUES (459,'<span data-type="title" id=toc-460>٤٥٩ </span>- عن عبد الرحمن بن أبزى: عن عمر أنه قرأ النجم فى الصلاة فسجد فيها ثم قام فقرأ: إذا زلزلت.
** الطبرى
(فتح الباري ٥٥٥/٢)
** إسناده صحيح','1','459',NULL,NULL,'0');
INSERT INTO [b88] VALUES (460,'<span data-type="title" id=toc-461>٤٦٠ </span>- عن زيد بن أسلم أن غلاما قرأ عند النبى صلى الله عليه وسلم السجدة، فانتظر الغلام النبى صلى الله عليه وسلم أن يسجد، فلما لم يسجد، قال: يارسول الله أليس فى هذه السجدة سجود؟ ، قال: " بلى ولكنك كنت إمامنا فيها ولو سجدت لسجدنا ".
** شب
(فتح الباري ٥٥٦/٢)
** رجاله ثقات','1','460',NULL,NULL,'0');
INSERT INTO [b88] VALUES (461,'<span data-type="title" id=toc-462>٤٦١ </span>- من طريق مطرف قال: سألت عمران بن حصين عن الرجل لا يدرى أسمع السجدة أو لا؟ ، فقال: وسمعها أو لا فماذا؟ .
** شب
(فتح الباري ٥٥٨/٢)
** إسناده صحيح','1','461',NULL,NULL,'0');
INSERT INTO [b88] VALUES (462,'<span data-type="title" id=toc-463>٤٦٢ </span>- عن مطرف أن عمران مر بقاضى فقرأ القاضى السجدة فمضى عمران ولم يسجد معه.
** عب
(فتح الباري ٥٥٨/٢)
** إسناده صحيح','1','462',NULL,NULL,'0');
INSERT INTO [b88] VALUES (463,'<span data-type="title" id=toc-464>٤٦٣ </span>- من طريق أبى عبد الرحمن السلمى قال: مر سلمان على قوم قعود، فقرؤا السجدة فسجدوا، فقيل له، فقال: ليس لهذا غدونا.
** عب خت
(فتح الباري ٥٥٨/٢)
** إسناده صحيح','1','463',NULL,NULL,'0');
INSERT INTO [b88] VALUES (464,'<span data-type="title" id=toc-465>٤٦٤ </span>- عن ابن المسيب: أن عثمان مر بقاصى فقرأ سجدة ليسجد معه عثمان، فقال عثمان: إنما السجود على من استمع، ثم مضى ولم يسجد.
** عب ابن وهب
(فتح الباري ٥٥٨/٢)
** إسناده صحيح','1','464',NULL,NULL,'0');
INSERT INTO [b88] VALUES (465,'<span data-type="title" id=toc-466>٤٦٥ </span>- عن عثمان: إنما السجدة على من جلس لها واستمع.
** شب ص
(فتح الباري ٥٥٨/٢)
** إسناده صحيح','1','465',NULL,NULL,'0');
INSERT INTO [b88] VALUES (466,'<span data-type="title" id=toc-467>٤٦٦ </span>- من حديث عمران بن حصين أنه صلى الله عليه وسلم كان يصلى بمكة ركعتين ويقول: " ياأهل مكة أتموا فإنا قوم سفر ".
** ت
(فتح الباري ٥٦٣/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٦٣: الحديث من رواية على بن زيد بن جدعان وهو ضعيف.','1','466',NULL,NULL,'0');
INSERT INTO [b88] VALUES (467,'<span data-type="title" id=toc-468>٤٦٧ </span>- من حديث ابن عباس: خرج من المدينة إلى مكة لا يخاف إلا الله يصلى ركعتين.
** ت
(فتح الباري ٥٦٤/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٦٤: صححه النسائى.','1','467',NULL,NULL,'0');
INSERT INTO [b88] VALUES (468,'<span data-type="title" id=toc-469>٤٦٨ </span>- عن ابن عباس أن رسول الله صلى الله عليه وسلم قال: " يا أهل مكة لا تقصروا الصلاة فى أدنى من أربعة برد من مكة إلى عسفان ".
** قط شب
(فتح الباري ٥٦٦/٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٢ / ٥٦٦: هذا إسناد ضعيف من أجل عبد الوهاب بن مجاهد.','1','468',NULL,NULL,'0');
INSERT INTO [b88] VALUES (469,'<span data-type="title" id=toc-470>٤٦٩ </span>- عن ابن عباس قال: تقصر الصلاة فى مسيرة يوم وليلة ".
** شب
(فتح الباري ٥٦٦/٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٢ / ٥٦٦: ولابن أبى شيبة من وجه آخر صحيح.','1','469',NULL,NULL,'0');
INSERT INTO [b88] VALUES (470,'<span data-type="title" id=toc-471>٤٧٠ </span>- عن محارب سمعت ابن عمر يقول: إنى لأسافر الساعة من النهار فأقصر.
** شب
(فتح الباري ٥٦٧/٢)
** إسناده صحيح','1','470',NULL,NULL,'0');
INSERT INTO [b88] VALUES (471,'<span data-type="title" id=toc-472>٤٧١ </span>- عن جبلة ابن سحيم سمعت ابن عمر يقول: لو خرجت ميلا قصرت الصلاة.
** لم
(فتح الباري ٥٦٧/٢)
** إسناده صحيح','1','471',NULL,NULL,'0');
INSERT INTO [b88] VALUES (472,'<span data-type="title" id=toc-473>٤٧٢ </span>- من حديث عثمان أنه لما صلى بمنى أربع ركعات أنكر الناس عليه فقال: إنى تأهلت بمكة لما قدمت وإنى سمعت رسول الله صلى الله عليه وسلم يقول: " من تأهل ببلده فإنه يصلى صلاة مقيم ".
** حم هق
(فتح الباري ٥٧٠/٢)
** منقطع
** قال الحافظ فى " الفتح " ٢ / ٥٧٠: هذا الحديث لا يصح لأنه منقطع، وفى رواته من لا يحتج به.','1','472',NULL,NULL,'0');
INSERT INTO [b88] VALUES (473,'<span data-type="title" id=toc-474>٤٧٣ </span>- عن عباد بن عبد الله بن الزبير، قال: لما قدم علينا معاوية حاجا صلى بنا الظهر ركعتين بمكة، ثم انصرف إلى دار الندوة، فدخل عليه مروان وعمرو بن عثمان، فقالا: لقد عبت أمر ابن عمك لأنه كان قد أتم الصلاة، قال: وكان عثمان حيث أتم الصلاة إذا قدم مكة، صلى الظهر والعصر والعشاء أربعا أربعا ثم إذا خرج إلى منى وعرفة قصر الصلاة، فإذا فرغ من الحج بمنى أتم الصلاة.
** حم
(فتح الباري ٥٧١/٢)
** إسناده حسن','1','473',NULL,NULL,'0');
INSERT INTO [b88] VALUES (474,'<span data-type="title" id=toc-475>٤٧٤ </span>- عن عثمان أنه كان لا يودع النساء إلا على ظهر راحلته ويسرع الخروج خشية أن يرجع فى هجرته.
** لم
(فتح الباري ٥٧١/٢)
** صحيح','1','474',NULL,NULL,'0');
INSERT INTO [b88] VALUES (475,'<span data-type="title" id=toc-476>٤٧٥ </span>- ثبت عن عثمان أنه قال لما حاصروه _ وقال له المغيرة اركب رواحلك إلى مكة _، قال: لن أفارق دار هجرتى.
** لم
(فتح الباري ٥٧١/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٧١: ثبت عن عثمان.','1','475',NULL,NULL,'0');
INSERT INTO [b88] VALUES (476,'<span data-type="title" id=toc-477>٤٧٦ </span>- من طريق هشام بن عروة عن أبيه: إنها كانت تصلى فى السفر أربعا، فقلت لها لو صليت ركعتين، فقالت: ياابن أختى إنه لا يشق على.
** هق
(فتح الباري ٥٧١/٢)
** إسناده صحيح','1','476',NULL,NULL,'0');
INSERT INTO [b88] VALUES (477,'<span data-type="title" id=toc-478>٤٧٧ </span>- عن أبى هريرة أنه سافر مع النبى صلى الله عليه وسلم، ومع أبى بكر وعمر، فكلهم كان يصلى ركعتين من حين يخرج من المدينة إلى مكة حتى يرجع إلى المدينة فى السير وفى المقام بمكة.
** يع طب
(فتح الباري ٥٧١/٢)
** إسناده جيد','1','477',NULL,NULL,'0');
INSERT INTO [b88] VALUES (478,'<span data-type="title" id=toc-479>٤٧٨ </span>- عن عمران بن حصين قال: ما سافر رسول الله صلى الله عليه وسلم إلا صلى ركعتين إلا المغرب.
** لم
(فتح الباري ٥٧٣/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٧٣: صححه الترمذى.','1','478',NULL,NULL,'0');
INSERT INTO [b88] VALUES (479,'<span data-type="title" id=toc-480>٤٧٩ </span>- عن سعيد بن جبير أن ابن عمر كان يصلى على الراحلة تطوعا فإذا أراد أن يوتر نزل فأوتر على الأرض.
** حم
(فتح الباري ٥٧٤/٢)
** إسناده صحيح','1','479',NULL,NULL,'0');
INSERT INTO [b88] VALUES (480,'<span data-type="title" id=toc-481>٤٨٠ </span>- عن أنس أنه رأى النبى صلى الله عليه وسلم يصلى على حمار وهو ذاهب إلى خيبر.
** السراج
(فتح الباري ٥٧٦/٢)
** إسناده حسن
** قال الحافظ فى " الفتح " ٢ / ٥٧٦: له شاهد عند مسلم.','1','480',NULL,NULL,'0');
INSERT INTO [b88] VALUES (481,'<span data-type="title" id=toc-482>٤٨١ </span>- عن مجاهد قال: صحبت ابن عمر من المدينة إلى مكة وكان يصلى تطوعا على دابته حيثما توجهت فإذا كانت الفريضة نزل فصلى.
** شب
(فتح الباري ٥٧٨/٢)
** إسناده صحيح','1','481',NULL,NULL,'0');
INSERT INTO [b88] VALUES (482,'<span data-type="title" id=toc-483>٤٨٢ </span>- حديث البراء بن عازب قال: سافرت مع النبى صلى الله عليه وسلم ثمانية عشر سفرا فلم أره ترك ركعتين إذا زاغت الشمس قبل الظهر.
** د ت
(فتح الباري ٥٧٩/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٧٩: استغربه الترمذى ونقل عن البخارى أنه رأه حسنا.','1','482',NULL,NULL,'0');
INSERT INTO [b88] VALUES (483,'<span data-type="title" id=toc-484>٤٨٣ </span>- عن شبابة قال: كان إذا كان فى سفر فزالت الشمس صلى الظهر والعصر جميعا ثم ارتحل.
** الأسماعيلى
(فتح الباري ٥٨٣/٢)
** قال الحافظ فى " الفتح " ٢ / ٥٨٣: أعل بتفرد اسحق بذل عن شبابة ثم تفرد به جعفر الفريابى به عن إسحق وليس ذلك بقادح فإنهما إمامان حافظان، وقد وقع نظيره فى الأربعين للحاكم. وقال الحافظ صلاح الدين العلائى: وسند هذه الزيادة جيد اه. قلت: وهى متابعة قوية رواية إسحق بن راهوية إن كانت ثابته لكن فى ثبوتها نظر.','1','483',NULL,NULL,'0');
INSERT INTO [b88] VALUES (484,'<span data-type="title" id=toc-485>٤٨٤ </span>- عن ابن عباس لا أعلمه إلا مرفوعا: أنه كان إذا نزل منزلا فى السفر أعجبه أقام فيه حتى يجمع بين الظهر والعصر ثم يرتحل فإذا لم يتهيأ له المنزل مد السير فسار حتى ينزل فيجمع بين الظهر والعصر.
** هق
(فتح الباري ٥٨٣/٢)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٢ / ٥٨٣: أخرجه البيهقى ورجاله ثقات، إلا أنه مشكوك فى رفعه، والمحفوظ أنه موقوف، وقد أخرجه البيقهى من وجه آخر مجزوما بوقفه على ابن عباس.','1','484',NULL,NULL,'0');
INSERT INTO [b88] VALUES (485,'<span data-type="title" id=toc-486>٤٨٥ </span>- عن أنس قال: قدم النبى صلى الله عليه وسلم المدينة وهى محمة، فحمى الناس فدخل النبى صلى الله عليه وسلم المسجد والناس يصلون من قعود، فقال: " صلاة القاعد نصف صلاة القائم ".
** حم
(فتح الباري ٥٨٥/٢)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٢ / ٥٨٥: وعند النسائى متابع له من وجه آخر.','1','485',NULL,NULL,'0');
INSERT INTO [b88] VALUES (486,'<span data-type="title" id=toc-487>٤٨٦ </span>- عن ابن عباس: أن النافلة للنبى صلى الله عليه وسلم خاصة لأنه أمر بقيام الليل وكتب عليه دون أمته.
** الطبرى
(فتح الباري ٣/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ /','1','486',NULL,NULL,'0');
INSERT INTO [b88] VALUES (487,'<span data-type="title" id=toc-488>٤٨٧ </span>- عن عائشة قالت: كان رسول الله صلى الله عليه وسلم يقول فى صلاة الليل فى سجوده: " سبحانك لا إله إلا أنت ".
** حم
(فتح الباري ٨/٣)
** رجاله ثقات','1','487',NULL,NULL,'0');
INSERT INTO [b88] VALUES (488,'<span data-type="title" id=toc-489>٤٨٨ </span>- عن تفسير بقى بن مخلد قال: قالت خديجة للنبى صلى الله عليه وسلم حين أبطأ عنه الوحى: إن ربك قد قلاك، فنزلت الضحى.
** إسماعيل القاضى فى أحكامه والطبرى فى تفسيره أبى داود فى أعلام النبوة
(فتح الباري ٩/٣)
** إسناده قوى
** قال الحافظ فى " الفتح " ٣ / ٩: عبد الله بن شداد بن الهاد _ وهو من صغار الصحابة _ والإسناد إليه صحيح.','1','488',NULL,NULL,'0');
INSERT INTO [b88] VALUES (489,'<span data-type="title" id=toc-490>٤٨٩ </span>- عن زيد بن أرقم قال: قالت امرأة أبى لهب لما مكث النبى صلى الله عليه وسلم لم ينزل عليه الوحى: يامحمد ما أرى شيطانك إلا وقد قلاك، فنزلت والضحى.
** ك
(فتح الباري ٩/٣)
** رجاله ثقات','1','489',NULL,NULL,'0');
INSERT INTO [b88] VALUES (490,'<span data-type="title" id=toc-491>٤٩٠ </span>- عن ابن عباس قال: إن ناشئة الليل هو كلام الحبشة، نشأ قام.
** عبد خت
(فتح الباري ٢٣/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ٢٣: وأخرج عبد بن حميد عن أبى ميسرة وأبى مالك نحوه، ووصله ابن أبى حاتم من طريق أبى ميسرة عن ابن مسعود أيضا.','1','490',NULL,NULL,'0');
INSERT INTO [b88] VALUES (491,'<span data-type="title" id=toc-492>٤٩١ </span>- عن ابن عمر: ما أصبح رجل على غير وتر إلا أصبح على رأسه جرير قدر سبعين ذراعا.
** ص
(فتح الباري ٢٥/٣)
** إسناده جيد','1','491',NULL,NULL,'0');
INSERT INTO [b88] VALUES (492,'<span data-type="title" id=toc-493>٤٩٢ </span>- عن ابن مسعود: " حسب الرجل من الخيبة والشر أن ينام حتى يصبح وقد بال الشيطان فى أذنه ".
** محمد بن نصر
(فتح الباري ٢٩/٣)
** إسناده صحيح موقوفا','1','492',NULL,NULL,'0');
INSERT INTO [b88] VALUES (493,'<span data-type="title" id=toc-494>٤٩٣ </span>- والأخبار الجياد فيها: كان إذا أراد أن ينام وهو جنب توضأ.
** لم
(فتح الباري ٣٢/٣)
** جيد','1','493',NULL,NULL,'0');
INSERT INTO [b88] VALUES (494,'<span data-type="title" id=toc-495>٤٩٤ </span>- كان رسول الله صلى الله عليه وسلم ينام وهو جنب من غير أن يمس ماء.
** لم
(فتح الباري ٣٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٢: أنكره الحفاظ على أبى إسحق. وقال الحافظ فى " الفتح " ٣ / ٣٢: قال الترمذى: يرون هذا غلطا من أبى إسحق، وكذا قال مسلم فى التمييز، وقال أبو داود: من رواية أبى الحسن بن العبد عنه: ليس بصحيح، ثم روى عن يزيد بن هارون أنه قال: هو وهم اه.','1','494',NULL,NULL,'0');
INSERT INTO [b88] VALUES (495,'<span data-type="title" id=toc-496>٤٩٥ </span>- عن عائشة كانت تقول: إن النبى صلى الله عليه وسلم لم يضطجع لسنة ولكنه كان يدأب ليلته فيستريح.
** عب
(فتح الباري ٤٤/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٤: فى إسناده راو لم يسم.','1','495',NULL,NULL,'0');
INSERT INTO [b88] VALUES (496,'<span data-type="title" id=toc-497>٤٩٦ </span>- عن عائشة قالت: كان رسول الله صلى الله عليه وسلم يصلى ركعتين قبل الفجر وكان يقول: " نعم السورتان "، يقرأ بهما فى ركعتى الفجر: قل ياأيها الكافرون وقل هو الله أحد.
** جه
(فتح الباري ٤٧/٣)
** إسناده قوى','1','496',NULL,NULL,'0');
INSERT INTO [b88] VALUES (497,'<span data-type="title" id=toc-498>٤٩٧ </span>- عن عمار بن ياسر: أنه دخل المسجد فصلى ركعتين خفيفتين.
** شب
(فتح الباري ٤٩/٣)
** إسناده حسن','1','497',NULL,NULL,'0');
INSERT INTO [b88] VALUES (498,'<span data-type="title" id=toc-499>٤٩٨ </span>- عن أنس بن مالك قال: رأيت رسول الله صلى الله عليه وسلم صلى فى السفر سبحة الضحى ثمان ركعات.
** حم
(فتح الباري ٥٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٢: صححه ابن خزيمة والحاكم.','1','498',NULL,NULL,'0');
INSERT INTO [b88] VALUES (499,'<span data-type="title" id=toc-500>٤٩٩ </span>- عن ابن عمر أنه قال: إنهما لمن أحسن ما أحدثوا.
** ص
(فتح الباري ٥٢/٣)
** إسناده صحيح','1','499',NULL,NULL,'0');
INSERT INTO [b88] VALUES (500,'<span data-type="title" id=toc-501><span data-type="title" id=toc-502>٥٠٠ </span></span>- عن الأعرج قال: سألت ابن عمر عن صلاة الضحى، فقال: بدعة ونعمت البدعة.
** شب
(فتح الباري ٥٢/٣)
** إسناده صحيح','1','500',NULL,NULL,'0');
INSERT INTO [b88] VALUES (501,'<span data-type="title" id=toc-503>٥٠١ </span>- عن سالم عن أبيه قال: لقد قتل عثمان وما أحد يسبحها، وماأحدث الناس شيئاأحب إلى منها.
** عب
(فتح الباري ٥٢/٣)
** إسناده صحيح','2','1',NULL,NULL,'0');
INSERT INTO [b88] VALUES (502,'<span data-type="title" id=toc-504>٥٠٢ </span>- عن ابن عمر قال: ما صليت الضحى منذ أسلمت، إلا أن أطوف بالبيت.
** شب
(فتح الباري ٥٢/٣)
** إسناده صحيح','2','2',NULL,NULL,'0');
INSERT INTO [b88] VALUES (503,'<span data-type="title" id=toc-505>٥٠٣ </span>- حديث أنس مرفوعا: " من صلى الضحى ثنتى عشرة ركعة بنى الله له قصرا فى الجنة ".
** ت
(فتح الباري ٥٤/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٤: أخرجه الترمذى واستغربه، وليس فى إسناده من أطلق عليه الضعف. قال النووى فى شرح المهذب: فيه حديث ضعيف اه. كأنه يشير إلى حديث أنس لكن إذا ضم إليه حديث أبى ذر وأبى الدرداء قوى وصلح للإحتجاج به.','2','3',NULL,NULL,'0');
INSERT INTO [b88] VALUES (504,'<span data-type="title" id=toc-506>٥٠٤ </span>- حديث أبى الدرداء مرفوعا: " من صلى الضحى ركعتين لم يكتب من الغافلين ومن صلى أربعا كتب من التائبين، ومن صلى ستا كفى ذلك اليوم، ومن صلى ثمانيا كتب من العابدين، ومن صلى ثنتى عشرة بنى الله له بيتا فى الجنة ".
** طب
(فتح الباري ٥٤/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٥٤: فى إسناده ضعف، وله شاهد من حديث أبى ذر رواه البزار وفى إسناده ضعف أيضا.','2','4',NULL,NULL,'0');
INSERT INTO [b88] VALUES (505,'<span data-type="title" id=toc-507>٥٠٥ </span>- حديث لأبى هريرة مرفوع لفظه: " رحم الله امراءا صلى قبل العصر أربعا ".
** حم د ت
(فتح الباري ٥٩/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٩: صححه ابن حبان، وورد من فعله أيضا من حديث على بن أبى طالب أخرجه الترمذى والنسائى، وليسا على شرط البخارى.
** تعقيب: قال عبد العزيز بن باز تعليقا على قوله لأبى هريرة ٣ / ٥٩: هذا وهم والصواب لأبن عمر: كما يعلم ذلك من الأصول التى عزاه إليها الشارح وقد نسبه فى بلوغ المرام لأبن عمر فأصاب والله أعلم.','2','5',NULL,NULL,'0');
INSERT INTO [b88] VALUES (506,'<span data-type="title" id=toc-508>٥٠٦ </span>- من طريق شهر بن حوشب قال: سمعت أبا سعيد وذكرت عنده الصلاة فى الطور فقال: قال رسول الله صلى الله عليه وسلم: " لا ينبغى للمصلى أن يشد رحاله إلى مسجد تبتغى فيه الصلاة غير المسجد الحرام والمسجد الأقصى ومسجدى ".
** حم
(فتح الباري ٦٥/٣)
** قال الحافظ فى " الفتح " ٣ / ٦٥: شهر حسن وإن كان فيه بعض الضعف.','2','6',NULL,NULL,'0');
INSERT INTO [b88] VALUES (507,'<span data-type="title" id=toc-509>٥٠٧ </span>- عن عبد الله بن الزبير قال: قال رسول الله صلى الله عليه وسلم: " صلاة فى مسجدى هذا أفضل من ألف صلاة فيما سواه من المساجد إلا المسجد الحرام، وصلاة فى المسجد الحرام أفضل من مائة صلاة فى هذا ".
** حم
(فتح الباري ٦٧/٣)
** قال الحافظ فى " الفتح " ٣ / ٦٧: صححه ابن حبان، وفى رواية ابن حبان " وصلاة فى ذلك أف١ل من مائة صلاة فى مسجد المدينة ". قال ابن عبد البر: اختلف على ابن الزبير فى رفعه ووقفه، ومن رفعه أحفظ وأثبت ومثله لا يقال بالرأى.','2','7',NULL,NULL,'0');
INSERT INTO [b88] VALUES (508,'<span data-type="title" id=toc-510>٥٠٨ </span>- حديث جابر مرفوعا: " صلاة فى مسجدى أفضل من ألف صلاة فيما سواه إلا المسجد الحرام وصلاة فى المسجد الحرام أفضل من مائة ألف صلاة فيما سواه ". وفى بعض النسخ: " من مائة صلاة فيما سواه ".
** جه
(فتح الباري ٦٧/٣)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٣ / ٦٧: رجال إسناده ثقات لكنه من رواية عطاء فى ذلك عنه، قال ابن عبد البر: جائز أن يكون عند عطاء فى ذلك عنهما، وعلى ذلك يحمله أهل العلم بالحديث، ويؤيده أن عطاء إمام واسع الرواية، معروف بالرواية عن جابر وابن الزبير.','2','8',NULL,NULL,'0');
INSERT INTO [b88] VALUES (509,'<span data-type="title" id=toc-511>٥٠٩ </span>- حديث أبى الدرداء رفعه: " الصلاة فى المسجد الحرام بمائة ألف صلاة، والصلاة فى مسجدى بألف صلاة، والصلاة فى بيت المقدس بخمسمائة صلاة ".
** بز طب
(فتح الباري ٦٧/٣)
** قال الحافظ فى " الفتح " ٣ / ٦٧: قال البزار: إسناده حسن.','2','9',NULL,NULL,'0');
INSERT INTO [b88] VALUES (510,'<span data-type="title" id=toc-512>٥١٠ </span>- عن عبد الله بن عدى بن الحمراء قال: رأيت رسول الله صلى الله عليه وسلم واقفا على الخرورة، فقال: " والله إنك لخير أرض الله وأحب أرض الله إلى الله، ولولا أنى أخرجت منك ما خرجت ".
** د ت ن جه
(فتح الباري ٦٧/٣)
** صحيح
** قال الحافظ فى " الفتح " ٣ / ٦٧: أرجه أصحاب السنن وصححه الترمذى وابن حبان وغيرهم.','2','10',NULL,NULL,'0');
INSERT INTO [b88] VALUES (511,'<span data-type="title" id=toc-513>٥١١ </span>- عن سعد بن أبى وقاص قال: لأن أصلى فى مسجد قباء ركعتين أحب إلى من أن آتى بيت المقدس مرتين، ولو يعلمون ما فى قباء لضربوا إليه أكباد الإبل.
** عمر بن شبة فى أخبار المدينة
(فتح الباري ٦٩/٣)
** إسناده صحيح','2','11',NULL,NULL,'0');
INSERT INTO [b88] VALUES (512,'<span data-type="title" id=toc-514>٥١٢ </span>- عن يزيد بن حوشب عن أبيه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " لو كان جريج عالما لعلم أن إجابته أمه أول من عبادة ربه ".
** الحسن بن سفيان
(فتح الباري ٧٨/٣)
** قال الحافظ فى " الفتح " ٣ / ٧٨: يزيد هذا مجهول.','2','12',NULL,NULL,'0');
INSERT INTO [b88] VALUES (513,'<span data-type="title" id=toc-515>٥١٣ </span>- عن عبد الله بن عمرو، قال: كسفت الشمس على عهد رسول الله صلى الله عليه وسلم فقام وقمنا معه.. الحديث بطوله وفيه: وجعل ينفخ فى الأرض ويبكى وهو ساجد.
** حم
(فتح الباري ٨٤/٣)
** قال الحافظ فى " الفتح " ٣ / ٨٤: صححه ابن خزيمة والطبرى وابن حبان. وقال الحافظ فى " الفتح " ٣ / ٨٤: تعليقا على قول البخارى " ويذكر عن عبد الله بن عمرو " وإنما ذكره البخارى بصيغة التمريض لأن عطاء بن السائب مختلف فى الإحتجاج به، وقد اختلط فى آخر عمره. لكن أخرجه ابن خزيمة من رواية سفيان الثورى عنه، وهو ممن سمع منه قبل اختلاطه وأبوه وثقه العجلى وابن حبان وليس هو فى شرط البخارى.','2','13',NULL,NULL,'0');
INSERT INTO [b88] VALUES (514,'<span data-type="title" id=toc-516>٥١٤ </span>- حديث عبد الله بن عمرو فإن فيه: ثم نفخ فى آخر سجوده فقال: أف أف.
** د
(فتح الباري ٨٥/٣)
** قال الحافظ فى " الفتح " ٣ / ٨٥: ثبت فى أبى داود من حديث عبد الله بن عمرو. وقال الحافظ فى " الفتح " ٣ / ٨٥: والزيادة المذكورة من رواية حماد بن سلمة عن عطاء وقد سمع منه قبل الإختلاط فى قول يحيى بن معين وأبى داود والطحاوى وغيرهم.','2','14',NULL,NULL,'0');
INSERT INTO [b88] VALUES (515,'<span data-type="title" id=toc-517>٥١٥ </span>- حديث أم سلمة قالت: رأى النبى صلى الله عليه وسلم غلاما لنا يقال له: أفلح إذا سجد نفخ، فقال: " يا أفلح ترب وجهك ".
** ت
(فتح الباري ٨٥/٣)
** قال الحافظ فى " الفتح " ٣ / ٨٥: قال الترمذى: ضعيف الإسناد.','2','15',NULL,NULL,'0');
INSERT INTO [b88] VALUES (516,'<span data-type="title" id=toc-518>٥١٦ </span>- قال عمر: إنى لأجهز جيشى وأنا فى الصلاة.
** خت شب
(فتح الباري ٩٠/٣)
** إسناده صحيح','2','16',NULL,NULL,'0');
INSERT INTO [b88] VALUES (517,'<span data-type="title" id=toc-519>٥١٧ </span>- قال عمر: إنى لأحسب جزية البحرين وأنا فى الصلاة.
** شب
(فتح الباري ٩٠/٣)
** رجالثه ثقات
** قال الحافظ فى " الفتح " ٣ / ٩٠: رجال هذه الآثار ثقات.','2','17',NULL,NULL,'0');
INSERT INTO [b88] VALUES (518,'<span data-type="title" id=toc-520>٥١٨ </span>- من طريق عياض الأشعرى قال: صلى عمر المغرب فلم يقرأ، فقال له أبو موسى: إنك لم تقرأ فأقبل على عبد الرحمن بن عوف فقال: صدق، فأعاد فلما فرغ قال: لا صلاة ليست فيها قراءة، إنما شغلنى عير جهزتها إلى الشام فجعلت أتفكر فيها.
** مسائل الإمام أحمد رواية صالح بن أحمد بن حنبل
(فتح الباري ٩٠/٣)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٣ / ٩٠: رجال هذه الآثار ثقات.','2','18',NULL,NULL,'0');
INSERT INTO [b88] VALUES (519,'<span data-type="title" id=toc-521>٥١٩ </span>- من طريق همام بن الحارث: أن عمر صلى المغرب فلم يقرأ فلما انصرف قالوا: يا أمير المؤمنين إنك لم تقرأ، فقال: إنى حدثت نفسى وأنا فى الصلاة بعير جهزتها من المدينة حتى دخلت الشام، ثم أعاد وأعاد القراءة.
** مسائل الإمام أحمد رواية صالح بن أحمد بن حنبل
(فتح الباري ٩٠/٣)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٣ / ٩٠: رجال هذه الآثار ثقات.','2','19',NULL,NULL,'0');
INSERT INTO [b88] VALUES (520,'<span data-type="title" id=toc-522>٥٢٠ </span>- عن عبد الرحمن بن حنظله بن الراهب: أن عمر صلى المغرب فلم يقرأ فى الركعة الأولى فلما كانت الثانية قرأ بفاتحة الكتاب مرتين فلما فرغ وسلم سجد سجدتى السهو.
** طحا
(فتح الباري ٩٠/٣)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٣ / ٩٠: رجال هذه الآثار ثقات.
** تعقيب: قال عبد العزيز بن باز ٣ / ٩٠: _ تعليقا على قوله عن عبد الرحمن بن حنظلة _ كذا فى الأصول التى فى أيدينا ولعل الصواب عن أبى عبد الرحمن لأن ضمضم المذكور إنما روى عن عبد الله بن حنظلة وهو يكنى أبا عبد الرحمن وليس له رواية عن عبد الرحمن بن حنظلة كما يعلم ذلك من الإستيعاب والأصابة وتهذيب التهذيب.','2','20',NULL,NULL,'0');
INSERT INTO [b88] VALUES (521,'<span data-type="title" id=toc-523>٥٢١ </span>- عن الأعرج: حتى إذا فرغ من الصلاة إلا أن يسلم.
** جه
(فتح الباري ٩٣/٣)
** قال الحافظ فى " الفتح " ٣ / ٩٣: من طريق جماعة من الثقات عن يحيى بن سعيد عن الأعرج.','2','21',NULL,NULL,'0');
INSERT INTO [b88] VALUES (522,'<span data-type="title" id=toc-524>٥٢٢ </span>- عن عمران بن حصين أن النبى صلى الله عليه وسلم صلى بهم فسها فسجد سجدتين ثم تشهد ثم سلم.
** د ت حب ك
(فتح الباري ٩٨/٣)
** قال الحافظ فى " الفتح " ٣ / ٩٨: قال الترمذى: حسن غريب، وقال الحاكم: صحيح على شرط الشيخين وقال ابن حبان: ما روى ابن سيرين عن خالد غير هذا الحديث انتهى. وهو من رواية الأكابر عن الأصاغر وضعفه البيهقى وابن عبد البر وغيرهما، ووهموا رواية أشعث لمخالفته غيره من الحفاظ عن ابن سيرين، فإن المحفوظ عن ابن سيرين فى حديث عمران ليس فيه ذكر التشهد.','2','22',NULL,NULL,'0');
INSERT INTO [b88] VALUES (523,'<span data-type="title" id=toc-525>٥٢٣ </span>- عن يحيى بن أبى كثير بهذا الإسناد مرفوعا: " إذا سها أحدكم فلم يدر أزاد أو نقص فليسجد سجدتين وهو جالس ثم يسلم ".
** قط
(فتح الباري ١٠٤/٣)
** إسناده قوى
** قال الحافظ فى " الفتح " ٣ / ١٠٤: ولأبى داود من طريق ابن أخى الزهرى عن عمه نحوه بلفظ: " وهو جالس قبل التسليم ".','2','23',NULL,NULL,'0');
INSERT INTO [b88] VALUES (524,'<span data-type="title" id=toc-526>٥٢٤ </span>- " فليسجد سجدتين قبل أن يسلم ثم يسلم ".
** د
(فتح الباري ١٠٤/٣)
** حسن
** قال الحافظ فى " الفتح " ٣ / ١٠٤: هذه الزيادة فى هذا الحديث بمجموع هذه الطرق لا تنزل من درجة الحسن المحتج به.','2','24',NULL,NULL,'0');
INSERT INTO [b88] VALUES (525,'<span data-type="title" id=toc-527>٥٢٥ </span>- عن أبى العالية قال: رأيت ابن عباس يسجد بعد وتره سجدتين.
** شب
(فتح الباري ١٠٥/٣)
** إسناده صحيح','2','25',NULL,NULL,'0');
INSERT INTO [b88] VALUES (526,'<span data-type="title" id=toc-528>٥٢٦ </span>- عن أبى سلمة: أن معاوية قال وهو على المنبر لكثير بن الصلت: اذهب إلى عائشة فاسألها، فقال أبو سلمة: فقمت معه، وقال ابن عباس لعبد الله بن الحارث اذهب معه، فجئناها فسألناها ... فذكره.
** طحا
(فتح الباري ١٠٥/٣)
** إسناده صحيح','2','26',NULL,NULL,'0');
INSERT INTO [b88] VALUES (527,'<span data-type="title" id=toc-529>٥٢٧ </span>- كان حذيفة إذا مات له الميت يقول: لا تؤذنوا به أحدا، إنى أخاف أن يكون نعيا، إنى سمعة رسول الله صلى الله عليه وسلم بأذنى هاتين ينهى عن النعى.
** ت جه
(فتح الباري ١١٧/٣)
** إسناده حسن','2','27',NULL,NULL,'0');
INSERT INTO [b88] VALUES (528,'<span data-type="title" id=toc-530>٥٢٨ </span>- حديث ابن مسعود مرفوعا: " من قدم ثلاثة من الولد لم يبلغوا الحنث كانوا له حصنا حصينا من النار، قال أبو ذر: قدمت اثنين، قال: واثنين، قال أبى بن كعب: قدمت واحدا، قال: وواحدا ".
** ت
(فتح الباري ١١٩/٣)
** قال الحافظ فى " الفتح " ٣ / ١١٩: قال الترمذى: غريب. وقال الحافظ فى " الفتح " ٣ / ١١٩: وليس فى شىء من هذه الطرق ما يصلح للإحتجاج.','2','28',NULL,NULL,'0');
INSERT INTO [b88] VALUES (529,'<span data-type="title" id=toc-531>٥٢٩ </span>- فى حديث عتبة بن عبد الله السلمى: " ما من مسلم يموت له ثلاثة من الولد لم يبلغوا الحنث إلا تلقوه من أبواب الجنة الثمانية من أيها شاء دخل ".
** جه
(فتح الباري ١٢١/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ١٢١: ويشهد له مارواه النسائى بإسناد صحيح من حديث معاوية بن قرة عن أبيه مرفوعا.','2','29',NULL,NULL,'0');
INSERT INTO [b88] VALUES (530,'<span data-type="title" id=toc-532>٥٣٠ </span>- من حديث معاوية بن قرة عن أبيه مرفوعا: " ما يسرك أن لا تأتى بابا من أبواب الجنة إلا وجدته عنده يسعى يفتح لك ".
** ن
(فتح الباري ١٢١/٣)
** إسناده صحيح','2','30',NULL,NULL,'0');
INSERT INTO [b88] VALUES (531,'<span data-type="title" id=toc-533>٥٣١ </span>- عن أم سليم الأنصارية، والدة أنس بن مالك قالت: قال رسول الله صلى الله عليه وسلم ذات يوم وأنا عنده: " ما من مسلمين يموت لهما ثلاثة لم يبلغوا الحلم إلا أدخله الجنة بفضل رحمته إياهم "، فقلت: وإثنان، قال: " وإثنان ".
** طب
(فتح الباري ١٢١/٣)
** إسناده جيد
** قال الحافظ فى " الفتح " ٣ / ١٢١: أخرجه أحمد لكن الحديث دون القصة.','2','31',NULL,NULL,'0');
INSERT INTO [b88] VALUES (532,'<span data-type="title" id=toc-534>٥٣٢ </span>- حديث بريدة أن عمر سأل عن ذلك أيضا ولفظه: " ما من امرىء ولا امرأة يموت له ثلاثة أولاد إلا أدخله الله الجنة "، فقال عمر: يارسول الله وإثنان؟ ، قال: " وإثنان ".
** ك بز
(فتح الباري ١٢٢/٣)
** قال الحافظ فى " الفتح " ٣ / ١٢٢: قال الحاكم: صحيح الإسناد.','2','32',NULL,NULL,'0');
INSERT INTO [b88] VALUES (533,'<span data-type="title" id=toc-535>٥٣٣ </span>- طريق عمرو بن عمير عن أبى هريرة مرفوعا: " من غسل الميت فليغتسل ومن حمله فليتوضأ ".
** د
(فتح الباري ١٢٧/٣)
** قال الحافظ فى " الفتح " ٣ / ١٢٧: رواته ثقات إلا عمرو بن عمير فليس بمعروف. وروى الترمذى وابن حبان من طريق سهيل بن أبى صالح عن أبيه عن أبى هريرة نحوه وهو معلول لأن أبا صالح لم يسمعه من أبى هريرة رضى الله عنه. وقال ابن أبى حاتم عن أبيه: الصواب عن أبى هريرة موقوف اه. وقال الذهلى فيما رواه الحاكم فى تاريخه: ليس فيمن غسل ميتا فليغتسل حديث ثابت','2','33',NULL,NULL,'0');
INSERT INTO [b88] VALUES (534,'<span data-type="title" id=toc-536>٥٣٤ </span>- عن ابن عباس رضى الله عنهما قال: " لا تنجسوا موتاكم فإن المؤمن ليس ينجس حيا ولا ميتا ".
** ص خت ش
(فتح الباري ١٢٧/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ١٢٧: وقد روى مرفوعا أخرجه الدارقطنى من رواية عبد الرحمن بن يحيى المخزومى عن سفيان، وكذلك أخرجه الحاكم من طريق أبى بكر وعثمان ابن أبى شيبة عن سفيان والذى فى مصنف ابن أبى شيبة عن سفيان موقوف كما رواه سعيد بن منصور، وروى الحاكم نحوه مرفوعا أيضا من طريق عمرو بن أبى عمرو عن عكرمة عن ابن عباس.','2','34',NULL,NULL,'0');
INSERT INTO [b88] VALUES (535,'<span data-type="title" id=toc-537>٥٣٥ </span>- عن أيوب ولفظه: دخل علينا ونحن نغسل ابنته أم كلثوم.
** جه
(فتح الباري ١٢٨/٣)
** إسناده على شرط الشيخين
** قال الحافظ فى " الفتح " ٣ / ١٢٨: هذا الإسناد على شرط الشيخين وفيه نظر.','2','35',NULL,NULL,'0');
INSERT INTO [b88] VALUES (536,'<span data-type="title" id=toc-538>٥٣٦ </span>- عن أم عطية قالت: فكفناها فى خمسة أثواب وخمرناها كما يخمر الحى.
** الجوزقى
(فتح الباري ١٣٣/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ١٣٣: هذه الزيادة صحيحة الإسناد.','2','36',NULL,NULL,'0');
INSERT INTO [b88] VALUES (537,'<span data-type="title" id=toc-539>٥٣٧ </span>- حديث ابن عباس بلفظ: " البسوا ثياب البياض فإنها أطهر وأطيب وكفنوا فيها موتاكم ".
** د ت ن جه
(فتح الباري ١٣٥/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ١٣٥: رواه أصحاب السنن، وصححه الترمذى والحاكم، وله شاهد من حديث سمرة بن جندب أخرجوه وإسناده صحيح أيضا.','2','37',NULL,NULL,'0');
INSERT INTO [b88] VALUES (538,'<span data-type="title" id=toc-540>٥٣٨ </span>- روى أنه صلى الله عليه وسلم كفن فى ثوبين وبرد حبرة.
** د
(فتح الباري ١٣٥/٣)
** إسناده حسن','2','38',NULL,NULL,'0');
INSERT INTO [b88] VALUES (539,'<span data-type="title" id=toc-541>٥٣٩ </span>- عن أبى هريرة أن رسول الله صلى الله عليه وسلم كان فى جنازة فرأى عمر امرأة فصاح بها فقال: " دعها ياعمر " الحديث.
** جه ن
(فتح الباري ١٤٥/٣)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٣ / ١٤٥: رواه ابن أبى شيبة من طريق محمد بن عمرو بن عطاء عن أبى هريرة، وأخرجه ابن ماجه والنسائى من هذا الوجه، ومن طريق أخرى عن محمد بن عمرو عن سلمة بن الأزرق عن أبى هريرة ورجاله ثقات.','2','39',NULL,NULL,'0');
INSERT INTO [b88] VALUES (540,'<span data-type="title" id=toc-542>٥٤٠ </span>- حديث: " لعن الله زوارات القبور ".
** ت
(فتح الباري ١٤٩/٣)
** قال الحافظ فى " الفتح " ٣ / ١٤٩: أخرجه الترمذى وصححه. وله شاهد من حديث ابن عباس ومن حديث حسان بن ثابت.','2','40',NULL,NULL,'0');
INSERT INTO [b88] VALUES (541,'<span data-type="title" id=toc-543>٥٤١ </span>- فى مرسل يحيى بن أبى كثير: فقال: " اذهبى إليك فإن الصبر عند الصدمة الأولى ".
** عب
(فتح الباري ١٥٠/٣)
** مرسل
** قال الحافظ فى " الفتح " ٣ / ١٥٠: وزاد عبد الرزاق فيه، من مرسل الحسن: " والعبرة لا يملكها ابن آدم ".','2','41',NULL,NULL,'0');
INSERT INTO [b88] VALUES (542,'<span data-type="title" id=toc-544>٥٤٢ </span>- فى مرسل الحسن: " والعبرة لا يملكهاابن آدم ".
** عب
(فتح الباري ١٥٠/٣)
** مرسل','2','42',NULL,NULL,'0');
INSERT INTO [b88] VALUES (543,'<span data-type="title" id=toc-545>٥٤٣ </span>- عن أبى مسعود الأنصارى وقرظة بن كعب قالا: رخص لنا فى البكاء عند المصيبة فى غير نوح.
** شب طب
(فتح الباري ١٥٣/٣)
** قال الحافظ فى " الفتح " ٣ / ١٥٣: صححه الحاكم ولكن ليس إسناده على شرط البخارى.','2','43',NULL,NULL,'0');
INSERT INTO [b88] VALUES (544,'<span data-type="title" id=toc-546>٥٤٤ </span>- حديث قيلة بنت مخرمة، قلت: يارسول الله قد ولدته فقاتل معك يوم الربذة ثم أصبابته الحمى فمات ونزل على البكاء، فقال رسول الله صلى الله عليه وسلم: " أيغلب أحدكم أن يصاحب صويحبه فى الدنيا معروفا، وإذا مات استرجع فوالذى نفس محمد بيده إن أحدكم يبكى فيستعير إليه صويحبه، فيا عباد الله لا تعذبوا موتاكم ".
** ابن أبى خيثمة شب طب
(فتح الباري ١٥٥/٣)
** إسناده حسن
** قال الحافظ فى " الفتح " ٣ / ١٥٥: هذا طرف من حديث طويل، وأخرج أبو داود والترمذى أطرافا منه.','2','44',NULL,NULL,'0');
INSERT INTO [b88] VALUES (545,'<span data-type="title" id=toc-547>٥٤٥ </span>- قال أبو هريرة: إن أعمال العباد تعرض على أقربائهم من موتاهم.
** الطبرى
(فتح الباري ١٥٥/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ١٥٥: وشاهده حديث النعمان بن بشير مرفوعا، أخرجه البخارى فى تاريخه وصححه الحاكم.','2','45',NULL,NULL,'0');
INSERT INTO [b88] VALUES (546,'<span data-type="title" id=toc-548>٥٤٦ </span>- حديث عبد الله بن عمرو: " الراحمون يرحمهم الرحمن ".
** د
(فتح الباري ١٥٨/٣)
** قال الحافظ فى " الفتح " ٣ / ١٥٨: ثبت فى حديث عبد الله بن عمرو عند أبى داود وغيره.','2','46',NULL,NULL,'0');
INSERT INTO [b88] VALUES (547,'<span data-type="title" id=toc-549>٥٤٧ </span>- عن ابن عمر أن رسول الله صلى الله عليه وسلم مر بنساء بنى عبد الأشهل يبكين هلكاهن يوم أحد، فقال: " لكن حمزة لا بواكى له "، فجاء نساء الأنصار يبكين حمزة، فاستيقظ رسول الله صلى الله عليه وسلم فقال: " ويحهن، ما انقلبن بعد مروهو فلينقلبن ولا يبكين على هالك بعد اليوم ".
** حم جه
(فتح الباري ١٦١/٣)
** قال الحافظ فى " الفتح " ٣ / ١٦١: صححه الحاكم. وله شاهد أخرجه عبد الرزاق من طريق عكرمة مرسلا ورجاله ثقات.','2','47',NULL,NULL,'0');
INSERT INTO [b88] VALUES (548,'<span data-type="title" id=toc-550>٥٤٨ </span>- حديث عبد الله بن أبى أوفى قال: نهى رسول الله صلى الله عليه وسلم عن المراثى.
** حم جه
(فتح الباري ١٦٤/٣)
** قال الحافظ فى " الفتح " ٣ / ١٦٥: صححه الحاكم وهو عند ابن أبى شيبة بلفظ: نهانا أن نتراثى.','2','48',NULL,NULL,'0');
INSERT INTO [b88] VALUES (549,'<span data-type="title" id=toc-551>٥٤٩ </span>- حديث أبى أمامة: أن رسول الله صلى الله عليه وسلم لعن الخامشة وجهها والشاقة جيبها والداعية بالويل والبثور.
** جه
(فتح الباري ١٦٦/٣)
** قال الحافظ فى " الفتح " ٣ / ١٦٦: صححه ابن حبان.','2','49',NULL,NULL,'0');
INSERT INTO [b88] VALUES (550,'<span data-type="title" id=toc-552>٥٥٠ </span>- عن ابن عباس أنه نعى إليه أخوه قثم وهو فى سفر فاسترجع ثم تنحى عن الطريق فأناخ فصلى ركعتين أطال فيهما الجلوس ثم قام وهو يقول: واستعينوا بالصبر والصلاة.. الآية.
** الطبرى فى تفسيره
(فتح الباري ١٧٢/٣)
** إسناده حسن','2','50',NULL,NULL,'0');
INSERT INTO [b88] VALUES (551,'<span data-type="title" id=toc-553>٥٥١ </span>- عن حذيفة قال: كان رسول الله صلى الله عليه وسلم إذا خربه أمر صلى.
** د
(فتح الباري ١٧٢/٣)
** إسناده حسن','2','51',NULL,NULL,'0');
INSERT INTO [b88] VALUES (552,'<span data-type="title" id=toc-554>٥٥٢ </span>- عن مكحول: " إنما أنهى الناس عن النياحة أن يندب الرجل بما ليس فيه ".
** عب
(فتح الباري ١٧٤/٣)
** مرسل','2','52',NULL,NULL,'0');
INSERT INTO [b88] VALUES (553,'<span data-type="title" id=toc-555>٥٥٣ </span>- من حديث عبادة قال: كان النبى صلى الله عليه وسلم يقوم للجنازة، فمر حبر من اليهود فقال: هكذا نفعل، فقال: " اجلسوا وخالفوهم ".
** حم د ت جه
(فتح الباري ١٨١/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ١٨١: أخرجه أحمد وأصحاب السنن إلا النسائى. وقال الحافظ: فلو لم يكن إسناده ضعيفا لكان حجة فى النسخ.','2','53',NULL,NULL,'0');
INSERT INTO [b88] VALUES (554,'<span data-type="title" id=toc-556>٥٥٤ </span>- من حديث المغيرة بن شعبة مرفوعا: " الراكب خلف الجنازة والماشى حيث شاء منها ".
** د ت ن جه
(فتح الباري ١٨٣/٣)
** قال الحافظ فى " الفتح " ٣ / ١٨٣: أخرجه أصحاب السنن وصححه ابن حبان والحاكم.','2','54',NULL,NULL,'0');
INSERT INTO [b88] VALUES (555,'<span data-type="title" id=toc-557>٥٥٥ </span>- من طريق عبد الرحمن بن أبزى عن على قال: المشى خلفها أفضل من المشى أمامها كفضل صلاة الجماعة على صلاة الفذ.
** ص
(فتح الباري ١٨٣/٣)
** إسناده حسن
** قال الحافظ فى " الفتح " ٣ / ١٨٣: هو موقوف له حكم المرفوع، لكن حكى الأثرم عن أحمد أنه تكلم فى إسناده.','2','55',NULL,NULL,'0');
INSERT INTO [b88] VALUES (556,'<span data-type="title" id=toc-558>٥٥٦ </span>- حديث ابن عمر: سمعت رسول الله صلى الله عليه وسلم يقول: " إذا مات أحدكم فلا تحبسوه وأسرعوا به إلى قبره ".
** طب
(فتح الباري ١٨٤/٣)
** إسناده حسن','2','56',NULL,NULL,'0');
INSERT INTO [b88] VALUES (557,'<span data-type="title" id=toc-559>٥٥٧ </span>- حديث مالك بن هبيرة مرفوعا: " من صلى عليه ثلاثة صفوف فقد أوجب ". وفى رواية " إلا غفر له ".
** د
(فتح الباري ١٨٦/٣)
** قال الحافظ فى " الفتح " ٣ / ١٨٧: حسنه الترمذى وصححه الحاكم.
** تعقيب: قال عبد العزيز بن باز ٣ / ١٨٧: لكن فى إسناده محمد بن إسحاق، وهو مدلس وقد رواه بالعنعنة وهى علة مؤثرة فى حق المدلس، وعليه لا تقوم بهذا الحديث حجة حتى يوجد ما يشهد له بالصحة والله أعلم.','2','57',NULL,NULL,'0');
INSERT INTO [b88] VALUES (558,'<span data-type="title" id=toc-560>٥٥٨ </span>- عن ابن عمر: أنه كان يرفع يديه فى كل تكبيرة على الجنازة.
** خ فى كتاب رفع اليدين خد
(فتح الباري ١٩٠/٣)
** قال الحافظ فى " الفتح " ٣ / ١٩٠: وولى مرفوعا أخرجه الطبرانى فى الأوسط من وجه آخر عن نافع عن ابن عمر بإسناد ضعيف.
** تعقيب: قال عبد العزيز بن باز ٣ / ١٩٠: وأخرجه الدارقطنى فى العلل بإسناد جيد عن ابن عمر مرفوعا وصوب وقفه لأنه لم يرفعه سوى عمر بن شبة، والأظهر عدم الإلتفات إلى هذه العلة لأن عمر المذكور ثقة فيقبل رفعه، لأن ذلك زيادة من ثقة وهى مقبولة على الراجح عند أئمة الحديث.','2','58',NULL,NULL,'0');
INSERT INTO [b88] VALUES (559,'<span data-type="title" id=toc-561>٥٥٩ </span>- عن أبى هريرة قال: أميران ليسا بأميرين: الرجل يكون مع الجنازة يصلى عليها فليس له أن يرجع حتى يستأذن وليها ... الحديث.
** عب
(فتح الباري ١٩٣/٣)
** منقطع موقوف
** قال الحافظ فى " الفتح " ٣ / ١٩٣: وروى عبد الرزاق مثله من قول إبراهيم، أخرجه ابن أبى شيبة عن المسور من فعله أيضا، وقد ورد مثيله مرفوعا من حديث جابر أخرجه البزار بإسناد فيه مقال، وأخرجه العقيلى فى الضعفاء من حديث أبى هريرة مرفوعا بإسناد ضعيف.','2','59',NULL,NULL,'0');
INSERT INTO [b88] VALUES (560,'<span data-type="title" id=toc-562>٥٦٠ </span>- عن أبى هريرة مرفوعا: " من تبع جنازة فحمل من علوها وحثا فى قبرها وقعد حتى يؤذن له رجع بقيراطين ".
** حم
(فتح الباري ١٩٣/٣)
** إسناده ضعيف','2','60',NULL,NULL,'0');
INSERT INTO [b88] VALUES (561,'<span data-type="title" id=toc-563>٥٦١ </span>- فقال ابن عمر: ياأبا هريرة أنظر ما تحدث عن رسول الله صلى الله عليه وسلم.
** ص مسدد خم
(فتح الباري ١٩٥/٣)
** إسناده صحيح','2','61',NULL,NULL,'0');
INSERT INTO [b88] VALUES (562,'<span data-type="title" id=toc-564>٥٦٢ </span>- عن أنس بن مالك، أنه صلى على رجل فقام عند رأسه، وصلى على امرأة فقام عند عجيزتها فقال له العلاء بن زياد: أهكذا كان رسول الله صلى الله عليه وسلم يفعل؟ ، قال: نعم.
** د ت
(فتح الباري ٢٠١/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٠١: أورد المصنف ترجمة مورد السؤال وأشار إلى تضعيف مارواه أبو داود والترمذى.
** تعقيب: قال عبد العزيز بن باز ٣ / ١٩٥: وأخرجه أحمد وابن ماجه ولفظهما لفظ الترمذى: عند رأس الرجل ووسط المرأة، وإسناده جيد وهو حجة قائمة على التفرقة بين الرجل والمرأة فى الموقف.','2','62',NULL,NULL,'0');
INSERT INTO [b88] VALUES (563,'<span data-type="title" id=toc-565>٥٦٣ </span>- عن أبى معبد: صليت خلف ابن عباس على جنازة فكبر ثلاثا.
** ابن المنذر
(فتح الباري ٢٠٢/٣)
** إسناده صحيح','2','63',NULL,NULL,'0');
INSERT INTO [b88] VALUES (564,'<span data-type="title" id=toc-566>٥٦٤ </span>- عن سعيد بن المسيب قال: كان التكبير أربعا أو خمسا، فجمع عمر الناس على أربع.
** ابن المنذر
(فتح الباري ٢٠٢/٣)
** إسناده صحيح','2','64',NULL,NULL,'0');
INSERT INTO [b88] VALUES (565,'<span data-type="title" id=toc-567>٥٦٥ </span>- عن أبى وائل قال: كانوا يكبرون على عهد رسول الله صلى الله عليه وسلم سبعا وستا وخمسا وأربعا فجمع عمر الناس على أربع كأطول الصلاة.
** هق
(فتح الباري ٢٠٢/٣)
** إسناده حسن','2','65',NULL,NULL,'0');
INSERT INTO [b88] VALUES (566,'<span data-type="title" id=toc-568>٥٦٦ </span>- عن أبى أمامة بن سهل بن حنيف قال: السنة فى الصلاة على الجنازة أن يكبر ثم يقرأ بأم القرأن ثم يصلى على النبى صلى الله عليه وسلم ثم يخلص الدعاء للميت ولا يقرأ إلا فى الأولى.
** عب ك
(فتح الباري ٢٠٣/٣)
** إسناده صحيح','2','66',NULL,NULL,'0');
INSERT INTO [b88] VALUES (567,'<span data-type="title" id=toc-569>٥٦٧ </span>- حديث جابر بلفظ: وقرأ بأم القرآن بعد التكبيرة الأولى.
** شفع
(فتح الباري ٢٠٤/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٠٤: أفاده شيخنا فى شرح الترمذى وقال إن سنده ضعيف.','2','67',NULL,NULL,'0');
INSERT INTO [b88] VALUES (568,'<span data-type="title" id=toc-570>٥٦٨ </span>- عن ابن عباس أنه صلى على جنازة بالأبواء فكبر ثم قرأ الفاتحة رافعا صوته ثم صلى على النبى صلى الله عليه وسلم ثم قال: اللهم عبدك وابن عبدك أصبح فقيرا إلى رحمتك، وأنت غنى عن عذابه، إن كان زاكيا فزكه، وإن كان مخطئا فاغفر له، اللهم لا تحرمناأجره، ولا تضلنا بعده، ثم كبر ثلاث تكبيرات ثم انصرف فقال: ياأيها الناس إنى لم أقرأ عليها _ أى جهرا _ إلا لتعلموا أنها سنة.
** ك
(فتح الباري ٢٠٤/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٠٤: قال الحاكم: شرحبيل لم يحتج به الشيخان وإنما أخرجته لأنه مفسر للطرق المتقدمة اه. وشرحبيل مختلف فى توثيقه.','2','68',NULL,NULL,'0');
INSERT INTO [b88] VALUES (569,'<span data-type="title" id=toc-571>٥٦٩ </span>- حديث بشير بن الخصاصة: أن النبى صلى الله عليه وسلم رأى رجلا يمشى بين القبور وعليه نعلان سبتيتان، فقال: " ياصاحب السبتيتين ألق نعليك ".
** د ن
(فتح الباري ٢٠٦/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٠٦: صححه الحاكم.','2','69',NULL,NULL,'0');
INSERT INTO [b88] VALUES (570,'<span data-type="title" id=toc-572>٥٧٠ </span>- عن هشام بن عامر الأنصارى قال: جاءت الأنصار إلى رسول الله صلى الله عليه وسلم يوم أحد فقالوا: أصابنا قرح وجهد، قال: " احفروا وأوسعوا واجعلوا الرجلين والثلاثة فى القبر ".
** د ت ن جه
(فتح الباري ٢١١/٣)
** قال الحافظ فى " الفتح " ٣ / ٢١١: رواه أصحاب السنن وصححه الترمذى.','2','70',NULL,NULL,'0');
INSERT INTO [b88] VALUES (571,'<span data-type="title" id=toc-573>٥٧١ </span>- عن واثلة بن الأسقع: أنه كان يدفن الرجل والمرأة فى القبر الواحد فيقدم الرجل ويجلع المرأة وراءه ".
** عب
(فتح الباري ٢١١/٣)
** إسناده حسن','2','71',NULL,NULL,'0');
INSERT INTO [b88] VALUES (572,'<span data-type="title" id=toc-574>٥٧٢ </span>- حديث ابن عباس: أصيب حمزة بن عبد المطلب وحنظلة بن الراهب وهما جنب فقال رسول الله صلى الله عليه وسلم: " رأيت الملائكة تغسلهما ".
** طب
(فتح الباري ٢١٢/٣)
** إسناده لابأس به
** قال الحافظ فى " الفتح " ٣ / ٢١٢: غريب فى ذكر حمزة.','2','72',NULL,NULL,'0');
INSERT INTO [b88] VALUES (573,'<span data-type="title" id=toc-575>٥٧٣ </span>- عن جابر: أن أباه قتل يوم أحد ثم مثلوا به فجدعوا أنفه وأذنيه.
** طب
(فتح الباري ٢١٧/٣)
** إسناده صحيح','2','73',NULL,NULL,'0');
INSERT INTO [b88] VALUES (574,'<span data-type="title" id=toc-576>٥٧٤ </span>- أن عائذ بن عمرو جاء يوم الفتح مع أبى سفيان بن حرب، فقال الصحابة: هذا أبو سفيان وعائذ بن عمرو، فقال رسول الله صلى الله عليه وسلم: " هذا عائذ بن عمرو وأبو سفيان الإسلام أعز من ذلك الإسلام يعلو ولا يعلى ".
** قط محمد بن هرون الرويانى فى مسنده فوائد أبى يعلى الخليلى
(فتح الباري ٢٢٠/٣)
** إسناده حسن
** قال الحافظ فى " الفتح " ٣ / ٢٢٠: زاد أبو يعلى الخليلى فى أوله قصة: أن عائذ بن عمرو جاء يوم الفتح مع أبى سفيان بن حرب، فقال الصحابة: هذا أبو سفيان وعائذ بن عمرو، فقال رسول الله صلى الله عليه وسلم: " هذا عائذ بن عمرو وأبو سفيان، الإسلام أعز من ذلك الإسلام يعلو ولا يعلى.','2','74',NULL,NULL,'0');
INSERT INTO [b88] VALUES (575,'<span data-type="title" id=toc-577>٥٧٥ </span>- عن أبى هريرة قال: لأن أجلس على جمرة فتحرق ما دون لحم حتى تفضى إلى، أحب إلى من أن أجلس على قبر، قال عثمان: فرأيت خارجة بن زيد فى المقابر فذكرت له ذلك فأخذ بيدى ... الحديث.
** مسدد
(فتح الباري ٢٢٤/٣)
** إسناده صحيح','2','75',NULL,NULL,'0');
INSERT INTO [b88] VALUES (576,'<span data-type="title" id=toc-578>٥٧٦ </span>- طريق محمد بن كعب قال: إنما قال أبو هريرة: من جلس على قبر يبول عليه ويتغوط فكأنما جلس على جمرة.
** طحا
(فتح الباري ٢٢٤/٣)
** إسناده ضعيف','2','76',NULL,NULL,'0');
INSERT INTO [b88] VALUES (577,'<span data-type="title" id=toc-579>٥٧٧ </span>- عن ابن عمر: لأن أطأ على رضف أحب إلى من أن أطأ على قبر.
** شب
(فتح الباري ٢٢٤/٣)
** إسناده صحيح','2','77',NULL,NULL,'0');
INSERT INTO [b88] VALUES (578,'<span data-type="title" id=toc-580>٥٧٨ </span>- عن زيد بن ثابت مرفوعا: إنما نهى النبى صلى الله عليه وسلم عن الجلوس على القبور لحدث غائطا أو بول.
** طحا
(فتح الباري ٢٢٤/٣)
** رجاله ثقات','2','78',NULL,NULL,'0');
INSERT INTO [b88] VALUES (579,'<span data-type="title" id=toc-581>٥٧٩ </span>- عن عمرو بن حزم الأنصارى: رأنى رسول الله صلى الله عليه وسلم وأنا متكىء على قبر فقال: "لا تؤذ صاحب القبر ".
** حم
(فتح الباري ٢٢٥/٣)
** إسناده صحيح','2','79',NULL,NULL,'0');
INSERT INTO [b88] VALUES (580,'<span data-type="title" id=toc-582>٥٨٠ </span>- عن البراء مطولا مبينا وفيه من الزيادة فى أوله: " استعيذوا بالله من عذاب القبر "، وفيه: " فترد روحه فى جسده "، وفيه: " فيأتيه ملكان فيجلسانه فيقولان له: من ربك؟ ، فيقول: ربى الله فيقولان له: ما دينك فيقول: دينى الإسلام، فيقولان له: ماهذا الرجل الذى بعث فيكم؟ فيقول: هو رسول الله فيقولان له: ما يدريك، فيقول: قرأت القرآن كتاب الله فأمنت به وصدقت، فذلك قوله تعالى (يثبت الله الذين أمنوا بالقول الثابت) "، وفيه: " وأن الكافر تعاد روحه فى جسده فيأتيه ملكان فيجلسانه فيقولان له: من ربك؟ فيقول: هاه هاه لا أدرى..... " الحديث.
** د ت ن جه
(فتح الباري ٢٣٤/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٣٤: أخرجه أصحاب السنن وصححه أبو عوانة وغيره.','2','80',NULL,NULL,'0');
INSERT INTO [b88] VALUES (581,'<span data-type="title" id=toc-583>٥٨١ </span>- عن عائشة أن يهودية كانت تخدمها، فلا تصنع عائشة إليها شيئا من المعروف إلا قالت لها اليهودية: وقاك الله عذاب القبر، قالت: فقالت يارسول الله هل للقبر عذاب، قال: " كذبت يهود لا عذاب دون يوم القيامة "، ثم مكث بعد ذلك ماشاء أن يمكث فخرج ذات يوم نصف النها وهو ينادى بأعلى صوته: " أيها الناس استعيذوا بالله من عذاب القبر، فإن عذاب القبر حق ".
** حم
(فتح الباري ٢٣٦/٣)
** إسناده على شرط البخارى','2','81',NULL,NULL,'0');
INSERT INTO [b88] VALUES (582,'<span data-type="title" id=toc-584>٥٨٢ </span>- من مرسل عمرو بن دينار زاد: " يحفران بأنيابهما ويطآن فى أشعارها، معهما مرزبة لو اجتمع عليها أهل منى لم يقلوها ".
** عب
(فتح الباري ٢٣٧/٣)
** مرسل','2','82',NULL,NULL,'0');
INSERT INTO [b88] VALUES (583,'<span data-type="title" id=toc-585>٥٨٣ </span>- حديث أبى هريرة: " فيقال له: هل رأيت الله؟ ، فيقول: ما ينبغى لأحد أن يرى الله، فتفرج له فرجة قبل النار فينظر إليها يحطم بعضها بعضا فيقال له: أنظر إلى ماوقاك الله ".
** جه
(فتح الباري ٢٣٨/٣)
** إسناده صحيح','2','83',NULL,NULL,'0');
INSERT INTO [b88] VALUES (584,'<span data-type="title" id=toc-586>٥٨٤ </span>- عن عبيد بن عمير: " لا دريت ولا أفلحت ".
** عب
(فتح الباري ٢٣٩/٣)
** مرسل','2','84',NULL,NULL,'0');
INSERT INTO [b88] VALUES (585,'<span data-type="title" id=toc-587>٥٨٥ </span>- حديث أنس مرفوعا: " سألت ربى اللاهين من ذرية البشر أن لا يعذبهم فأعطانيهم ".
** يع
(فتح الباري ٢٤٦/٣)
** إسناده حسن','2','85',NULL,NULL,'0');
INSERT INTO [b88] VALUES (586,'<span data-type="title" id=toc-588>٥٨٦ </span>- من طريق خنساء بنت معاوية بن صريم عن عمتها قالت: قلت يارسول الله من فى الجنة؟ ، قال: النبى فى الجنة والشهيد فى الجنة والمولود فى الجنة ".
** حم
(فتح الباري ٢٤٦/٣)
** إسناده حسن','2','86',NULL,NULL,'0');
INSERT INTO [b88] VALUES (587,'<span data-type="title" id=toc-589>٥٨٧ </span>- حديث عائشة: سألت رسول الله عن ولدان من المسلمين، قال: " فى الجنة "، وعن أولاد المشركين، قال: " فى النار "، فقلت: يارسول الله لم يدركوا الأعمال قال: " ربك أعلم بما كانوا عاملين لو شئت أسمعتك تضاغيهم فى النار ".
** حم
(فتح الباري ٢٤٦/٣)
** ضعيف جدا
** قال الحافظ فى " الفتح " ٣ / ٢٤٦: وهو حديث ضعيف جدا، لأن فى إسناده أبا عقيل مولى بهية وهو متروك.','2','87',NULL,NULL,'0');
INSERT INTO [b88] VALUES (588,'<span data-type="title" id=toc-590>٥٨٨ </span>- حديث سمرة مرفوعا: " أولاد المشركين خدم أهل الجنة ".
** طب بز
(فتح الباري ٢٤٦/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٢٤٦: وفيه حديث عن أنس ضعيف أخرجه أبو داود الطيالسى وأبو يعلى.','2','88',NULL,NULL,'0');
INSERT INTO [b88] VALUES (589,'<span data-type="title" id=toc-591>٥٨٩ </span>- من طريق أبى معاذ عن الزهرى عن عروة عن عائشة قالت: سألت خديجة النبى صلى الله عليه وسلم عن أولاد المشركين، فقال: " هم مع آبائهم "، ثم سألته بعد ذلك فقال: " الله أعلم بما كانوا عاملين "، ثم سألته بعد ما استحكم الإسلام فنزل (ولا تزر وازرة وزر أخرى) ، قال: " هم على الفطرة أو قال: فى الجنة ".
** عب
(فتح الباري ٢٤٧/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٤٧: أبو معاذ هو سليمان بن أرقم وهو ضعيف.','2','89',NULL,NULL,'0');
INSERT INTO [b88] VALUES (590,'<span data-type="title" id=toc-592>٥٩٠ </span>- حديث عبد الله بن عمرو مرفوعا: " ما من مسلم يموت يوم الجمعة أو ليلة الجمعة إلا وقاه الله فتنة القبر ".
** ت
(فتح الباري ٢٥٣/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٢٥٣: وفى إسناده ضعف، أخرجه أبو يعلى من حديث أنس نحوه وإسناده أضعف.','2','90',NULL,NULL,'0');
INSERT INTO [b88] VALUES (591,'<span data-type="title" id=toc-593>٥٩١ </span>- من حديث عبيد بن خالد السلمى: موت الفجأة أخذة أسف.
** د
(فتح الباري ٢٥٤/٣)
** إسناده فيه مقال
** قال الحافظ فى " الفتح " ٣ / ٢٥٤: الحديث أخرجه أبو داود من حديث عبيد بن خالد السلمة ورجاله ثقات إلا أن راويه رفعه مرة ووقفه أخرى، وقد روى ابن أبى الدنيا فى كتاب الموت من حديث أنس نحو حديث عبيد بن خالد، وزاد فيه: " المحروم من حرم وصيته ".','2','91',NULL,NULL,'0');
INSERT INTO [b88] VALUES (592,'<span data-type="title" id=toc-594>٥٩٢ </span>- عن عائشة: أبو بكر عن يمينه وعمر عن يساره.
** يع
(فتح الباري ٢٥٧/٣)
** إسناده ضعيف','2','92',NULL,NULL,'0');
INSERT INTO [b88] VALUES (593,'<span data-type="title" id=toc-595>٥٩٣ </span>- من طريق مسروق أن عليا بعث يزيد بن قيس الأرجى فى أيام الحمل برسالة فلم ترد عليه جوابا، فبلغها أنه عاب عليها ذلك فكانت تلعنه ثم لما بلغها موته نهت عن لعنه، وقالت: إن رسول الله نهانا عن سب الأموات.
** عمر بن شبة
(فتح الباري ٢٥٩/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٥٩: صححه ابن حبان من وجه آخر عن الأعمش عن مجاهد بالقصة.','2','93',NULL,NULL,'0');
INSERT INTO [b88] VALUES (594,'<span data-type="title" id=toc-596>٥٩٤ </span>- عن صخر بن القعقاع قال: لقيت النبى صلى الله عليه وسلم بين عرفه ومزدلفة فأخذت بخطام ناقته فقلت: يارسول الله ما يقربنى من الجنة ويباعدنى من النار فذكر الحديث.
** طب
(فتح الباري ٢٦٤/٣)
** إسناده حسن','2','94',NULL,NULL,'0');
INSERT INTO [b88] VALUES (595,'<span data-type="title" id=toc-597>٥٩٥ </span>- قصة ثعلبة بن حاطب المطوله فيها: لما أنزلت آية الصدقة بعث النبى صلى الله عليه وسلم عاملا فقال: ماهذه إلا جزية أو أخت الجزية.
** لم
(فتح الباري ٢٦٦/٣)
** ضعيف
** قال الحافظ فى " الفتح " ٣ / ٢٦٦: حديث ضعيف لا يحتج به.','2','95',NULL,NULL,'0');
INSERT INTO [b88] VALUES (596,'<span data-type="title" id=toc-598>٥٩٦ </span>- حديث قيس بن سعد بن عبادة قال: أمرنا رسول الله صلى الله عليه وسلم بصدقة الفطر قبل أن تنزل الزكاة ثم نزلت فريضة الزكاة فلم يأمرنا ولم ينهنا ونحن نفعله.
** حم خز ن جه ك
(فتح الباري ٢٦٦/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ٢٦٧: رجاله رجال الصحيح إلا أبا عمار الراوى له عن قيس بن سعد وهو كوفى اسمه غريب بالمهمله والمفتوحة ابن حميد وقد وثقه أحمد وابن معين.','2','96',NULL,NULL,'0');
INSERT INTO [b88] VALUES (597,'<span data-type="title" id=toc-599>٥٩٧ </span>- عن ابن عمر موقوفا: " ما أدى زكاته فليس بكنز ".
** ما شفع
(فتح الباري ٢٧٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٧٢: ووصله البيهقى والطبرانى من طريقالثورى عن عبد الله بن دينار وقال: إنه ليس بمحفوظ.','2','97',NULL,NULL,'0');
INSERT INTO [b88] VALUES (598,'<span data-type="title" id=toc-600>٥٩٨ </span>- عن ابن عمر مرفوعا بلفظ: " كل ما أديت زكاته وإن كان تحت سبع أرضين فليس بكنز وكل مالا تؤدى زكاته فهو كنز وإن كان ظاهرا على وجه الأرض ".
** هق
(فتح الباري ٢٧٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٧٢: أورده البيهقى مرفوعا ثم قال: ليس بمحفوظ والمشهور وقفه.','2','98',NULL,NULL,'0');
INSERT INTO [b88] VALUES (599,'<span data-type="title" id=toc-601>٥٩٩ </span>- عن جابر بلفظ: " إذا أديت زكاة مالك فقد أذهبت عنك شره ".
** ك
(فتح الباري ٢٧٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٧٢: رجح أبو زرعة والبيهقى وغيرهما وقفه كما عند البزار.','2','99',NULL,NULL,'0');
INSERT INTO [b88] VALUES (600,'<span data-type="title" id=toc-602>٦٠٠ </span>- عن أبى هريرة بلفظ: " إذا أديت زكاة مالك فقد قضيت ماعليك ".
** ت
(فتح الباري ٢٧٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٧٢: قال الترمذى: حسن غريب، وصححه الحاكم وهو على شرط ابن حبان، وعن أم سلمة عند الحاكم وصححه ابن القطان أيضا وأخرجه أبو داود، وقال ابن عبد البر: فى سنده مقال، وذكر شيخنا فى شرح الترمذى: أن سنده جيد.
** تعقيب: قال عبد العزيز بن باز ٣ / ٢٧٢: عليقا على قول شيخنا: هو الحافظ العراقى، ولفظه عند أبى داود عن أم سلمة أنها كانت تلبس أوضاحا من ذهب فقالت: يارسول الله أكنز هو، فقال: ما بلغ أن تؤدى زكاته فزكى فليس بكنز. وسنده جيد كما قال العراقى.','2','100',NULL,NULL,'0');
INSERT INTO [b88] VALUES (601,'<span data-type="title" id=toc-603>٦٠١ </span>- عن ابن عباس قال: أستأذن أبو ذر على عثمان، فقال: إنه يؤذينا، فلما دخل قال له عثمان: أنت الذى تزعم إنك خير من أبى بكر وعمر؟ ، قال: لا ولكن سمعت رسول الله صلى الله عليه وسلم يقول: " إن أحبكم إلى وأقربكم منى من بقى على العهد الذى عاهدته عليه "، وأنا باق على عهده، فأمره أن يلحق بالشام، وكان يحدثهم ويقول: لا يبيتن عند أحدكم دينار ولا درهم إلا ما ينفقه فى سبيل الله أو يعده لغريم، فكتب معاوية إلى عثمان إن كان لك بالشام حاجة فابعث إلى أبى ذر، فكتب إليه عثمان أن أقدم على فقدم.
** يع
(فتح الباري ٢٧٤/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٣٧٤: باسناد فيه ضعف.','2','101',NULL,NULL,'0');
INSERT INTO [b88] VALUES (602,'<span data-type="title" id=toc-604>٦٠٢ </span>- حديث أبى المليح عن أبيه مرفوعا: " لا يقبل الله صدقة من غلول، ولا صلاة بغير طهور ".
** د
(فتح الباري ٢٧٨/٣)
** إسناده صحيح','2','102',NULL,NULL,'0');
INSERT INTO [b88] VALUES (603,'<span data-type="title" id=toc-605>٦٠٣ </span>- حديث ابن مسعود مرفوعا: " ليتق أحدكم وجهه النار ولو بشق تمرة ".
** حم
(فتح الباري ٢٨٤/٣)
** إسناده صحيح','2','103',NULL,NULL,'0');
INSERT INTO [b88] VALUES (604,'<span data-type="title" id=toc-606>٦٠٤ </span>- حديث عائشة: " ياعائشة استترى من النار ولو بشق تمرة فإنها تسد من الجائع موقعها من الشبعان ".
** حم
(فتح الباري ٢٨٤/٣)
** إسناده حسن
** قال الحافظ فى " الفتح " ٣ / ٢٨٤: ولأبى يعلى من حديث أبى بكر الصديق نحوه، وأتم منه بلفظ: " تقع من الجائع موقعها من الشبعان ".','2','104',NULL,NULL,'0');
INSERT INTO [b88] VALUES (605,'<span data-type="title" id=toc-607>٦٠٥ </span>- عن عائشة قالت: قال رسول الله صلى الله عليه وسلم لأزواجه: " أسرعكن لحوقا بى أطولكن يدا "، قالت عائشة: فكلنا إذا اجتمعنا فى بيت إحدانا بعد وفاة رسول الله صلى الله عليه وسلم، نمد إيدينا فى الجدار نتطاول، فلم نزل نفعل ذلك حتى توفيت زينب بنت جحش، وكانت امرأة قصيرة فلم تكن أطولنا فعرفنا حينئذ أن النبى صلى الله عليه وسلم إنما أراد بطول اليد الصدقة، وكانت زينب امرأة صناعة باليد، وكانت تدبغ وتخرز وتصدق فى سبيل الله.
** ك
(فتح الباري ٢٨٧/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٨٧: قال الحاكم: على شرط مسلم اه.','2','105',NULL,NULL,'0');
INSERT INTO [b88] VALUES (606,'<span data-type="title" id=toc-608>٦٠٦ </span>- عن ميمونة أن النبى صلى الله عليه وسلم قال لهن: " ليس ذلك أعنى إنما أعنى أصنعكن يدا ".
** طس
(فتح الباري ٢٨٨/٣)
** ضعيف جدا','2','106',NULL,NULL,'0');
INSERT INTO [b88] VALUES (607,'<span data-type="title" id=toc-609>٦٠٧ </span>- قوله تعالى (الذين ينفقون أموالهم بالليل والنهار سرا وعلانية ... إلى قوله. ولا هم يحزنون) ، عن ابن عباس إنها نزلت فى على بن أبى طالب كان عنده أربعة دراهم فانفق بالليل واحدا وبالنهار واحدا وفى السر واحدا وفى العلانية واحدا.
** عب
(فتح الباري ٢٨٩/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٢٨٩: باسناد فيه ضعف إلى ابن عباس، وذكره الكلبى فى تفسيره عن أبى صالح عن ابن عباس أيضا، وزاد أن النبى صلى الله عليه وسلم قاله له: أما إن ذلك لك.','2','107',NULL,NULL,'0');
INSERT INTO [b88] VALUES (608,'<span data-type="title" id=toc-610>٦٠٨ </span>- أن النبى صلى الله عليه وسلم كان إذا رأى مالا يعجبه قال: " اللهم لك الحمد على كل حال ".
** لم
(فتح الباري ٢٩٠/٣)
** ثابت
** قال الحافظ فى " الفتح " ٣ / ٢٩٠: وقد ثبت أن النبى صلى الله عليه وسلم كان إذا رأى......... " الحديث.','2','108',NULL,NULL,'0');
INSERT INTO [b88] VALUES (609,'<span data-type="title" id=toc-611>٦٠٩ </span>- من طريق زيد بن أسلم عن أبيه سمعت عمر يقول: أمرنا رسول الله صلى الله عليه وسلم أن نتصدق فوافق ذلك مالا عندى، فقلت: اليوم أسبق أبا بكر إن سبقته يوما فجئت بنصف مالى، وأتى أبو بكر بكل ما عنده فقال له النبى صلى الله عليه وسلم " ياأبا بكر ما أبقيت لأهلك "؟ ، قال: أبقيت لهم الله ورسوله الحديث.
** د
(فتح الباري ٢٩٥/٣)
** قال الحافظ فى " الفتح " ٣ / ٢٩٥: صححه الترمذى والحاكم، والحديث تفرد به هشام بن سعد عن زيد وهشام صدوق فيه مقال من جهة حفظه.','2','109',NULL,NULL,'0');
INSERT INTO [b88] VALUES (610,'<span data-type="title" id=toc-612>٦١٠ </span>- عن ابن عمر أنه كتب إلى مروان إنى سمعت النبى صلى الله عليه وسلم يقول: " اليد العليا خير من اليد السفلى، ولا أحسب اليد السفلى إلا السائلة ولا العليا إلا المعطية ".
** كتاب العسكرى فى الصحابة
(فتح الباري ٢٩٧/٣)
** منقطع
** قال الحافظ فى " الفتح " ٣ / ٢٩٧: بإسناد له فيه انقطاع.','2','110',NULL,NULL,'0');
INSERT INTO [b88] VALUES (611,'<span data-type="title" id=toc-613>٦١١ </span>- عن حطيم بن حزام مرفوعا: " يد الله فوق يد المعطى، ويد المعطى فوق يد المعطى، ويد المعطى أسفل الأيدى ".
** طب
(فتح الباري ٢٩٧/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ٢٩٧: وللطبرانى من حديث عدى الجذامى مرفوعا مثله.','2','111',NULL,NULL,'0');
INSERT INTO [b88] VALUES (612,'<span data-type="title" id=toc-614>٦١٢ </span>- من حديث ابن عمر مرفوعا: " ما المعطى من سعى بأفضل من الآخذ إذا كان محتاجا ".
** طب
(فتح الباري ٢٩٨/٣)
** إسناده فيه مقال','2','112',NULL,NULL,'0');
INSERT INTO [b88] VALUES (613,'<span data-type="title" id=toc-615>٦١٣ </span>- قال طاوس، قال معاذ رضى الله عنه لأهل اليمن: ائتونى بعرض ثياب خميص أو لبيس فى الصدقة مكان الشعيرة والذرة، أهون عليكم وخير لآصحاب النبى صلى الله عليه وسلم بالمدينة.
** خت الخراج ليحيى بن آدم
(فتح الباري ٣١١/٣)
** منقطع
** قال الحافظ فى " الفتح " ٣ / ٣١٢: هذا التعليق صحيح الإسناد إلى طاوس، لكن طاوس لم يسمع من معاذ ٠فهو منقطع فلا يغتر بقول من قال: ذكره البخارى بالتعليق الجازم فهو صحيح عنده، لأن ذلك لا يفيد إلا الصحة إلى من علق عنه، وأما باقى الإسناد فلا.','2','113',NULL,NULL,'0');
INSERT INTO [b88] VALUES (614,'<span data-type="title" id=toc-616>٦١٤ </span>- عن على مرفوعا: " أن فى خمس وعشرين خمس شياه فإذا صارت ستا وعشرين كان فيها بنت مخاض ".
** شب
(فتح الباري ٣١٩/٣)
** ضعيف
** قال الحافظ فى " الفتح " ٣ / ٣١٩: أخرجه ابن أبى شيبة وغيره عنه موقوفا ومرفوعا وإسناد المرفوع ضعيف.','2','114',NULL,NULL,'0');
INSERT INTO [b88] VALUES (615,'<span data-type="title" id=toc-617>٦١٥ </span>- حديث معاذ المرفوع: " إن فى كل ثلاثين بقرة تبيعا وفى كل أربعين مسنة ".
** د ت ن جه ك
(فتح الباري ٣٢٤/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٢٤: زعم ابن بطال أن حديث معاذ المرفوع متصل صحيح وإن مثله فى كتاب الصدقات لأبى بكر وعمر وفى كلامه نظر، أما حديث معاذ فأخرجه أصحاب السنن وقال الترمذى: حسن، وأخرجه الحاكم فى المستدرك، وفى الحكم بصحته نظر لأن مسروقا لم يلق معاذ وإنما حسنه الترمذى لشواهده، ففى الموطأ من طريق طاوس عن معاذ نحوه وطاووس عن معاذ منقطع أيضا وفى الباب عن على عند أبى داود.','2','115',NULL,NULL,'0');
INSERT INTO [b88] VALUES (616,'<span data-type="title" id=toc-618>٦١٦ </span>- حديث على مرفوعا: " قد عفوت عن الخيل والرقيق فهاتوا صدقة الرقة ".
** د
(فتح الباري ٣٢٧/٣)
** إسناده حسن','2','116',NULL,NULL,'0');
INSERT INTO [b88] VALUES (617,'<span data-type="title" id=toc-619>٦١٧ </span>- قال ابن عمر: أما إن الحج فى سبيل الله.
** أبو عبيد
(فتح الباري ٣٣٢/٣)
** إسناده صحيح موقوفا','2','117',NULL,NULL,'0');
INSERT INTO [b88] VALUES (618,'<span data-type="title" id=toc-620>٦١٨ </span>- ويذكر عن أبى لاس: حملنا النبى صلى الله عليه وسلم على إبل الصدقة للحج. لفظ أحمد. " على إبل من إبل الصدقة ضعاف للحج، فقلنا يارسول الله ما نرى أن تحمل هذه، فقال: " إنما يحمل الله ... " الحديث.
** خت حم
(فتح الباري ٣٣٢/٣)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٣ / ٣٣٢: وقد وصله أحمد وابن خزيمة والحاكم وغيرهم. وقال الحافظ فى " الفتح " ٣ / ٣٣٢: رجاله ثقات إلا أن فيه عنعنة ابن اسحق، ولهذا توقف ابن المنذر فى ثبوته.','2','118',NULL,NULL,'0');
INSERT INTO [b88] VALUES (619,'<span data-type="title" id=toc-621>٦١٩ </span>- من طريق موسى بن طلحة أن النبى صلى الله عليه وسلم قال: " إنا كنا احتجنا فتعجلنا من العباس صدقة ماله سنتين ".
** قط
(فتح الباري ٣٣٣/٣)
** مرسل
** قال الحافظ فى " الفتح " ٣ / ٣٣٣: أخرجه الترمذى وغيره من حديث على وفى إسناده مقال، وروى الدارقطنى أيضا موصولا بذكر طلحة فيه وإسناد المرسل أصح.','2','119',NULL,NULL,'0');
INSERT INTO [b88] VALUES (620,'<span data-type="title" id=toc-622>٦٢٠ </span>- حديث ابن عباس أن النبى صلى الله عليه وسلم بعث عمر ساعيا فأتى العباس فأغلظ له، فأخبر النبى صلى الله عليه وسلم، فقال: "إن العباس قد أسلفنا زكاة ماله العام والعام المقبل ".
** قط
(فتح الباري ٣٣٤/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٣٣٤: وفى إسناده ضعف. وأخرجه أيضا هو والطبرانى من حديث أبى رافع نحو هذا وإسناده ضعيف.','2','120',NULL,NULL,'0');
INSERT INTO [b88] VALUES (621,'<span data-type="title" id=toc-623>٦٢١ </span>- حديث ابن مسعود: أن النبى صلى الله عليه وسلم تعجل من العباس صدقته سنتين.
** طب
(فتح الباري ٣٣٤/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٣٤: فى إسناده محمد بن ذكوان وهو ضعيف. وقال الحافظ فى " الفتح " ٣ / ٣٣٤: وليس ثبوت هذه القصة فى تعجيل صدقة العباس ببعيد فى النظر بمجموع هذه الطرق والله أعلم.','2','121',NULL,NULL,'0');
INSERT INTO [b88] VALUES (622,'<span data-type="title" id=toc-624>٦٢٢ </span>- حديث ابن مسعود مرفوعا: " من سأل الناس وله ما يغنيه جاء يوم القيامة ومسألته فى وجهه خموش "، قيل: يارسول الله وما يغنيه؟ ، قال: " خمسون درهما أو قيمتها من الذهب ".
** ت
(فتح الباري ٣٤١/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٤١: فى إسناده حكيم بن جبير وهو ضعيف، وقد تكلم فيه شبة من أجل هذا الحديث. وحدث به سفيان الثورى عن حكيم فقيل له: إن شعبة لا يحدث عنه، قال: لقد حدثنى به زبيد أبو عبد الرحمن عن محمد بن عبد الر حمن بن يزيد _ يعنى شيخ حكيم _ أخرجه الترمذى أيضا ونص أحمد فى " علل الخلال " وغيرها على أن رواية زبيد موقوفة.','2','122',NULL,NULL,'0');
INSERT INTO [b88] VALUES (623,'<span data-type="title" id=toc-625>٦٢٣ </span>- عن سهل بن الحنظلية قال: قال رسول الله صلى الله عليه وسلم: " من سأل وعنده ما يغنيه فإنما يستكثر من النار "، فقالوا: يارسول الله وما يغنيه قال: " قدر ما يغديه ويعشيه ".
** د
(فتح الباري ٣٤٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٤٢: صححه ابن حبان.','2','123',NULL,NULL,'0');
INSERT INTO [b88] VALUES (624,'<span data-type="title" id=toc-626>٦٢٤ </span>- عن أبى هريرة قال: كتب رسول الله صلى الله عليه وسلم إلى أهل اليمن أن يؤخذ من العسل العشر.
** عب
(فتح الباري ٣٤٨/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٤٨: فى إسناده عبد الله بن مجرر، قال البخارى فى تاريخه: عبد الله متروك، ولا يصح فى زكاة العسل شىء، قال الترمذى: لا يصح فى هذا الباب شىء، قال الشافعى فى القديم: حديث إن فى العسل العشر ضعيف إلا عن عمر بن عبد العزيزاه.','2','124',NULL,NULL,'0');
INSERT INTO [b88] VALUES (625,'<span data-type="title" id=toc-627>٦٢٥ </span>- من طريق طاوس: أن معاذا لما أتى اليمن، قال: لم أؤمر فيهما بشىء، يعنى العسل وأوقاص البقر.
** عب شب
(فتح الباري ٣٤٨/٣)
** منقطع','2','125',NULL,NULL,'0');
INSERT INTO [b88] VALUES (626,'<span data-type="title" id=toc-628>٦٢٦ </span>- من طريق عمرو بن شعيب عن أبيه عن جده قال: جاء هلال أحد بنى متعان إلى رسول الله صلى الله عليه وسلم بعشور نحل له وكان سأله أن يحمة له واديا فحماه له، فلما ولى عمر كتب إلى عامله: إن أدى إليك عشور نحله فاحم له سلبه وإلا فلا.
** د ن
(فتح الباري ٣٤٨/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٤٨: إسناد صحيح إلى عمرو. وترجمة عمرو قوية على المختار لكن حيث لا تعارض.
** تعقيب: قال عبد العزيز بن باز ٣ / ٣٤٨: مراده أن إسناد هذا الحديث إلى عمرو بن شعيب صحيح، أما رواية عمرو عن أبيه عن جده فمختلف فيها بين أهل الحديث، والصواب أنها حجة ما لم يخالفها ما هو أقوى منهما.','2','126',NULL,NULL,'0');
INSERT INTO [b88] VALUES (627,'<span data-type="title" id=toc-629>٦٢٧ </span>- روى مرفوعا من طريق على وطلحة ومعاذ: " لا زكاة فى الخضراوات ".
** قط
(فتح الباري ٣٥٠/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٥٠: قال الترمذى: لا يصح فيه شىء إلا مرسل موسى بن طلحة عن النبى صلى الله عليه وسلم','2','127',NULL,NULL,'0');
INSERT INTO [b88] VALUES (628,'<span data-type="title" id=toc-630>٦٢٨ </span>- حديث الحسن بن على قال: كنت مع النبى صلى الله عليه وسلم فمر على جرين من تمر الصدقة فأخذت منه تمرة فألقيتها فى فى فأخذها بلعابها فقال: " إنا آل محمد لا تحل لنا الصدقة ".
** حم طحا
(فتح الباري ٣٥٥/٣)
** إسناده قوى','2','128',NULL,NULL,'0');
INSERT INTO [b88] VALUES (629,'<span data-type="title" id=toc-631>٦٢٩ </span>- عن عائشة قالت: إنا آل محمد لا تحل لنا الصدقة.
** الخلال شب
(فتح الباري ٣٥٦/٣)
** إسناده حسن','2','129',NULL,NULL,'0');
INSERT INTO [b88] VALUES (630,'<span data-type="title" id=toc-632>٦٣٠ </span>- عن أبى رافع مرفوعا: " إنا لا تحل لنا الصدقة، وإن موالى القوم من أنفسهم ".
** د ت ن جه
(فتح الباري ٣٥٦/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٥٦: رواه أصحاب السنن وصححه الترمذى وابن حبان وغيره.','2','130',NULL,NULL,'0');
INSERT INTO [b88] VALUES (631,'<span data-type="title" id=toc-633>٦٣١ </span>- حديث أبى هريرة مرفوعا: " دعوة المظلوم مستجابة، وإن كان فاجرا ففجوره على نفسه ".
** حم
(فتح الباري ٣٦٠/٣)
** إسناده حسن','2','131',NULL,NULL,'0');
INSERT INTO [b88] VALUES (632,'<span data-type="title" id=toc-634>٦٣٢ </span>- عن قيس بن سعد بن عبادة قال: أمرنا رسول الله صلى الله عليه وسلم بصدقة الفطر قبل أن تنزل الزكاة، فلما نزلت الزكاة لم يأمرنا ولم ينهنا ونحن نفعله.
** ن
(فتح الباري ٣٦٨/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٦٨: تعقب بأن فى إسناده راويا مجهولا.','2','132',NULL,NULL,'0');
INSERT INTO [b88] VALUES (633,'<span data-type="title" id=toc-635>٦٣٣ </span>- من طريق عبد العزيز بن أبى داود عن نافع قال فيه: فلما كان عمر كثرت الحنطة، فجعل عمر نصف صاع حنطة مكان صاع من تلك الأشياء.
** د
(فتح الباري ٣٧٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٧٢: حكم مسلم فى كتاب التمييز على عبد العزيز فيه بالوهم وأوضح الرد عليه.','2','133',NULL,NULL,'0');
INSERT INTO [b88] VALUES (634,'<span data-type="title" id=toc-636>٦٣٤ </span>- قال أبو سعيد _ وذكروا عنده قصة رمضان _ فقال: لا أخرج إلا ماكنت أخرج فى عهد رسول الله صلى الله عليه وسلم: صاع تمر أو صاع حنطة أو صاع شعير أو صاع أقط، فقال له رجل من القوم: أو مدين من قمح، فقال: لا تلك قيمة معاوية مطوية لا أقبلها ولا أعمل بها.
** خز ك
(فتح الباري ٣٧٣/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٧٣: قال ابن خزيمة ذكر قصة الحنطة فى خبر أبى سعيد غير محفوظ ولا أدرى ممن الوهم اه. وقد أشار أبو داود إلى رواية ابن اسحق هذه وقال: إن ذكر الحنطة فيه غير محفوظ وذكر رواية نصف صاع من بد وهو وهم.','2','134',NULL,NULL,'0');
INSERT INTO [b88] VALUES (635,'<span data-type="title" id=toc-637>٦٣٥ </span>- روى بو معشر عن نافع: عن ابن عمر بلفظ: كان يأمرنا أن نخرجها قبل أن نصلى فإذا انصرف قسمه بينهم وقال: " أغنوهم عن الطلب ".
** ص
(فتح الباري ٣٧٥/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٧٥: أبو معشر ضعيف.','2','135',NULL,NULL,'0');
INSERT INTO [b88] VALUES (636,'<span data-type="title" id=toc-638>٦٣٦ </span>- روى الحديث بلفظ آخر: على رجل رث وقطيفة تساوى أربعة دراهم ثم قال: " اللهم حجة لا رياء فيها ولا سمعة ".
** جه
(فتح الباري ٣٨١/٣)
** إسناده ضعيف','2','136',NULL,NULL,'0');
INSERT INTO [b88] VALUES (637,'<span data-type="title" id=toc-639>٦٣٧ </span>- حديث جابر: قالوا: يارسول الله ما بر الحج؟ ، قال: " إطعام الطعام وإفشاء السلام ".
** حم ك
(فتح الباري ٣٨٢/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٣٨٢: فى إسناده ضعف.','2','137',NULL,NULL,'0');
INSERT INTO [b88] VALUES (638,'<span data-type="title" id=toc-640>٦٣٨ </span>- عن أبى حازم بلفظ: " من حج أو أعتمر ".
** قط
(فتح الباري ٣٨٢/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٣٨٢: فى الإسناد إلى الأعمش ضعيف.','2','138',NULL,NULL,'0');
INSERT INTO [b88] VALUES (639,'<span data-type="title" id=toc-641>٦٣٩ </span>- فى مرسل عطاء عند الشافعى: " ولا هل نجد قرن ولمن سلك نجدا من أهل اليمن وغيرهم قرن المنازل ".
** شفع
(فتح الباري ٣٨٥/٣)
** مرسل','2','139',NULL,NULL,'0');
INSERT INTO [b88] VALUES (640,'<span data-type="title" id=toc-642>٦٤٠ </span>- عن ابن عمر قال: وقت رسول الله صلى الله عليه وسلم لأهل العراق قرنا.
** غرائب مالك للدارقطنى سحق
(فتح الباري ٣٨٩/٣)
** غريب جدا
** قال الحافظ فى " الفتح " ٣ / ٣٨٩: قال عبد الرزاق: قال لى بعضهم إن مالكا محاه من كتابه اه. قال الدارقطنى: تفرد به عبد الرزاق اه، قلت: والإسناد إليه ثقات أثبات. قال الحافظ فى " الفتح " ٣ / ٣٨٩: قال الشافعى فى الأم: لم يثبت عن النبى صلى الله عليه وسلم أنه حد ذات عرق وإنما أجمع عليه الناس اه. وقال الحافظ فى " الفتح " ٣ / ٣٩٠: قال ابن خزيمة: رويت فى ذات عرق أخبار لا يثبت شىء منها عند أهل الحديث اه. وقال ابن المنذر: لم نجد فى ذات عرق حديثا ثابتا اه. لكن الحديث بمجموع الطرق يقوى.','2','140',NULL,NULL,'0');
INSERT INTO [b88] VALUES (641,'<span data-type="title" id=toc-643>٦٤١ </span>- عن ابن عباس: أن النبى صلى الله عليه وسلم وقت لأهل المشرق العقيق.
** د ت
(فتح الباري ٣٩٠/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٩٠: تفرد به يزيد بن أبى زياد وهو ضعيف.','2','141',NULL,NULL,'0');
INSERT INTO [b88] VALUES (642,'<span data-type="title" id=toc-644>٦٤٢ </span>- حديث عمر: تختموا بالعقيق فإن جبريل أتانى به من الجنة.. الحديث.
** ابن الجوزى فى الموضوعات
(فتح الباري ٣٩٢/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٣٩٢: أسانيده ضعيفة.','2','142',NULL,NULL,'0');
INSERT INTO [b88] VALUES (643,'<span data-type="title" id=toc-645>٦٤٣ </span>- عن ابن عباس مرفوعا قال: " لا بأس بالهميان والخاتم للمحرم ".
** طب عد
(فتح الباري ٣٩٧/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٣٩٧: أخرجه الطبرانى وابن عدى فى الكامل من وجه آخر عن ابن عباس مرفوعا وإسناده ضعيف.','2','143',NULL,NULL,'0');
INSERT INTO [b88] VALUES (644,'<span data-type="title" id=toc-646>٦٤٤ </span>- قال صلى الله عليه وسلم: " حبب إلى النساء والطيب ".
** ن
(فتح الباري ٣٩٩/٣)
** قال الحافظ فى " الفتح " ٣ / ٣٩٩: قد ثبت عنه أنه قال حبب إلى..... ".','2','144',NULL,NULL,'0');
INSERT INTO [b88] VALUES (645,'<span data-type="title" id=toc-647>٦٤٥ </span>- عن عائشة قالت: طيب أبى بالمسك لإحرامه حين أحرم.
** ص
(فتح الباري ٣٩٩/٣)
** إسناده صحيح','2','145',NULL,NULL,'0');
INSERT INTO [b88] VALUES (646,'<span data-type="title" id=toc-648>٦٤٦ </span>- من طريق القاسم بن محمد قال: كانت عائشة تلبس الثياب المعصفرة وهى محرمة.
** ص
(فتح الباري ٤٠٥/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ٤٠٥: وأخرجه البيهقى من طريق ابن أبى مليكه.','2','146',NULL,NULL,'0');
INSERT INTO [b88] VALUES (647,'<span data-type="title" id=toc-649>٦٤٧ </span>- عن عائشة قالت: كنا مع رسول الله صلى الله عليه وسلم إذا مر بنا ركب سدلناالثوب على وجوهنا ونحن محرمات فإذا جاوزنا رفعناه ".
** ابن المنذر
(فتح الباري ٤٠٦/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٤٠٦: فى إسناده ضعف.','2','147',NULL,NULL,'0');
INSERT INTO [b88] VALUES (648,'<span data-type="title" id=toc-650>٦٤٨ </span>- من طريق خلاد بن السائب عن أبيه مرفوعا: " جاءنى جبريل فأمرنى أن آمر أصحابى يرفعون أصواتهم بالإهلال ".
** ما د ت ن جه
(فتح الباري ٤٠٨/٣)
** رجالق ثقات
** قال الحافظ فى " الفتح " ٣ / ٤٠٨: ورواه أصحاب السنن وصححه الترمذى وابن خزيمة والحاكم ورجاله ثقات إلا أنه اختلف على التابعى فى صحابيه.','2','148',NULL,NULL,'0');
INSERT INTO [b88] VALUES (649,'<span data-type="title" id=toc-651>٦٤٩ </span>- عن بكر بن عبد الله المزنى قال: كنت مع ابن عمر فلبى حتى أسمع ما بين الجبلين.
** شب
(فتح الباري ٤٠٨/٣)
** إسناده صحيح','2','149',NULL,NULL,'0');
INSERT INTO [b88] VALUES (650,'<span data-type="title" id=toc-652>٦٥٠ </span>- من طريق المطلب بن عبد الله قال: كان أصحاب رسول الله صلى الله عليه وسلم يرفعون أصواتهم بالتلبية حتى تبح أصواتهم.
** شب
(فتح الباري ٤٠٨/٣)
** إسناده صحيح','2','150',NULL,NULL,'0');
INSERT INTO [b88] VALUES (651,'<span data-type="title" id=toc-653>٦٥١ </span>- قال: " لما فرغ إبراهيم عليه السلام من بناء البيت قيل له: أذن فى الناس بالحج، قال: رب وما يبلغ صوتى؟ ، قال: أذن وعلى البلاغ، قال: فنادى إبراهيم: ياأيهاالناس كتب عليكم الحج إلى البيت العتيق، فسمعه من بين السماء والأرض، أفلا ترون أن الناس يجيئون من أقصى الأرض يلبون ".
** منيع ابن أبى حاتم
(فتح الباري ٤٠٩/٣)
** إسناده قوى','2','151',NULL,NULL,'0');
INSERT INTO [b88] VALUES (652,'<span data-type="title" id=toc-654>٦٥٢ </span>- حديث أبى هريرة: قال: كان من تلبية رسول الله صلى الله عليه وسلم لبيك إله الحق لبيك.
** ن جه
(فتح الباري ٤١٠/٣)
** قال الحافظ فى " الفتح " ٣ / ٤١٠: صححه ابن حبان والحاكم.','2','152',NULL,NULL,'0');
INSERT INTO [b88] VALUES (653,'<span data-type="title" id=toc-655>٦٥٣ </span>- عن عبد الله بن دينار عن ابن عمر قال: الحج أشهر معلومات شوال وذو القعدة وعشر من ذى الحجة.
** الطبرى قط هق خت
(فتح الباري ٤٢٠/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ٤٢٠: وصله الطبرى والدارقطنى من طريق ورقاء عن على بن دينار عنه، وروى البيهقى من طريق عبد الله بن نمير عن عبيد الله بن عمر عن نافع عن ابن عمر مثله، والإسنادان صحيحان.','2','153',NULL,NULL,'0');
INSERT INTO [b88] VALUES (654,'<span data-type="title" id=toc-656>٦٥٤ </span>- فى رواية عطاء عنها: وأرجع أنا بحجة ليس معها عمرة.
** حم
(فتح الباري ٤٢٤/٣)
** ضعيف
** قال الحافظ فى " الفتح " ٣ / ٤٢٤: فى رواية عطاء عنها ضعف.','2','154',NULL,NULL,'0');
INSERT INTO [b88] VALUES (655,'<span data-type="title" id=toc-657>٦٥٥ </span>- من طريق ابن لهيعة: عن أبى الزبير قال: سألت جابرا هل يقوم الرجل عريانا فقال: أخبرنى النبى صلى الله عليه وسلم أنه لما انهدمت الكعبة نقل كل بطن من قريش وأن النبى صلى الله عليه وسلم نقل مع العباس وكانوا يضعون ثيابهم على العواتق يتقون بها _ أى على حمل الحجارة _ فقال النبى صلى الله عليه وسلم " فاعتقلت رجلى فخررت وسقط ثوبى فقلت للعباس: هلم ثوبى، فلست أتعرى بعدها إلا إلى الغسل ".
** طب أبو نعيم فى الدلائل
(فتح الباري ٤٤١/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٤١: ابن لهيعة ضعيف، وقد تابعه عبد العزيز بن سليمان عن أبى الزبير، ذكره أبو نعيم.','2','155',NULL,NULL,'0');
INSERT INTO [b88] VALUES (656,'<span data-type="title" id=toc-658>٦٥٦ </span>- عن ابن عباس وقال فى آخره: فكان أول شىء رأى من النبوة.
** أبو نعيم فى الدلائل
(فتح الباري ٤٤١/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٤١: من طريق النضر أبى عمر عن عكرمة عن ابن عباس ليس فيه العباس، والنضر ضعيف، وقد خبط فى إسناده ومتنه.','2','156',NULL,NULL,'0');
INSERT INTO [b88] VALUES (657,'<span data-type="title" id=toc-659>٦٥٧ </span>- عن عياش بن أبى ربيعة المحزومى عن النبى صلى الله عليه وسلم قال: " إن هذه الأمة لا تزال بخير ما عظموا هذه الحرمة _ يعنى الكعبة _ حق تعظيمها فإذا ضيعوا ذلك هلكوا ".
** حم جه عمر بن شبة فى كتاب مكة
(فتح الباري ٤٤٩/٣)
** إسناده حسن
** قال الحافظ فى " الفتح " ٣ / ٤٤٩: سنده حسن.','2','157',NULL,NULL,'0');
INSERT INTO [b88] VALUES (658,'<span data-type="title" id=toc-660>٦٥٨ </span>- حديث علقمة بن نضلة قال: توفى رسول الله صلى الله عليه وسلم وأبو بكر وعمر وما تدعى رباع مكة إلا السوائب من احتاج سكن.
** جه
(فتح الباري ٤٥٠/٣)
** إسناده منقطع
** قال الحافظ فى " الفتح " ٣ / ٤٥٠: أشار البخارى بهذه الترجمة إلى تضعيف حديث علقمة بن نضلة وفى إسناده انقطاع وإرسال.','2','158',NULL,NULL,'0');
INSERT INTO [b88] VALUES (659,'<span data-type="title" id=toc-661>٦٥٩ </span>- عن عائشة رضى الله عنها قالت: دخل على شيبة الحجبى فقال: ياأم المؤمنين إن ثياب الكعبة تجتمع عندنا فتكثر فننزعها، ونحفر ئبارا فنعمقها وندفنها لكى لا تلبسها الحائض والجنب، قالت: بئسما صنعت ولكن بعها فاجعل عنها فى سبيل الله وفى المساكين فإنها إذا نزعت عنها لم يضر من لبسها من حائض أو جنب، فكان شيبة يبعث بها إلى اليمن فتباع له فيضعها حيث أمرته.
** هق
(فتح الباري ٤٥٨/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٥٨: رواه الفاكهى فى كتاب مكة من طريق علقمة بن أبى علقمة عن أمه عن عائشة، وأخرجه البيهقى من هذا الوجه، لكن فى إسناده راو ضعيف وإسناد الفاكهى سالم منه.','2','159',NULL,NULL,'0');
INSERT INTO [b88] VALUES (660,'<span data-type="title" id=toc-662>٦٦٠ </span>- عن ابن عمر: أنه كان يكسو بدنه القباطى والحبرات يوم يقلدها، فإذا كان يوم النحر نزعها ثم أرسل بها إلى شيبة بن عثمان فناطها على الكعبة.
** الفاكهى
(فتح الباري ٤٥٩/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ٤٥٩: زاد فى رواية صحيحة أيضا: فلما كست الأمراء الكعبة جللها القباطى ثم تصدق بها.','2','160',NULL,NULL,'0');
INSERT INTO [b88] VALUES (661,'<span data-type="title" id=toc-663>٦٦١ </span>- عن ليث ابن أبى سليم: كانت كسوة الكعبة على عهد النبى صلى الله عليه وسلم المسوح والأنطاع.
** شب
(فتح الباري ٤٥٩/٣)
** معضل
** قال الحافظ فى " الفتح " ٣ / ٤٥٩: ليث ضعيف.','2','161',NULL,NULL,'0');
INSERT INTO [b88] VALUES (662,'<span data-type="title" id=toc-664>٦٦٢ </span>- فى رواية صحيحة: فلما كست الأمراء الكعبة جللها القباطى ثم تصدق بها.
** الفاكهى
(فتح الباري ٤٥٩/٣)
** صحيح','2','162',NULL,NULL,'0');
INSERT INTO [b88] VALUES (663,'<span data-type="title" id=toc-665>٦٦٣ </span>- حديث عبد الله بن عمرو بن العاص مرفوعا: " إن الحجر والمقام ياقوتتان من ياقوت الجنة طمس الله نورهما ولولا ذلك لأضاآ مابين المشرق والمغرب ".
** حم ت
(فتح الباري ٤٦٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٦٢: صححه ابن حبان وفى إسناده رجاء بو يحيى وهو ضعيف، قال الترمذى: حديث غريب، ويروى عن عبد الله بن عمرو موقوفا، قال ابن أبى حاتم عن أبيه: وقفه أشبه والذى رفعه ليس بقوى.','2','163',NULL,NULL,'0');
INSERT INTO [b88] VALUES (664,'<span data-type="title" id=toc-666>٦٦٤ </span>- حديث ابن عباس مرفوعا: " نزل الحجر الأسود من الجنة وهو أشد بياضا من اللبن، فسودته خطايا بنى آدم ".
** ت
(فتح الباري ٤٦٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٦٢: صححه الترمذى، وفيه عطاء بن السائب وهو صدوق ولكنه اختلط، وجرير ممن سمع منه بعد اختلاطه، لكن له طريق أخرى فى صحيح ابن خزيمة فيقوى بها.','2','164',NULL,NULL,'0');
INSERT INTO [b88] VALUES (665,'<span data-type="title" id=toc-667>٦٦٥ </span>- لفظه: " الحجر الأسود من الجنة ".
** ن
(فتح الباري ٤٦٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٦٢: من طريق حماد بن سلمة عن عطاء، وحماد ممن سمع من عطاء قبل الإختلاط.','2','165',NULL,NULL,'0');
INSERT INTO [b88] VALUES (666,'<span data-type="title" id=toc-668>٦٦٦ </span>- عن ابن عباس مرفوعا: " إن لهذا الحجر لسانا وشفتين يشهدان لمن استلمه يوم القيامة بحق ".
** خز
(فتح الباري ٤٦٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٦٢: وصححه أيضا ابن حبان والحاكم وله شاهد من حديث أنس عند الحاكم أيضا.','2','166',NULL,NULL,'0');
INSERT INTO [b88] VALUES (667,'<span data-type="title" id=toc-669>٦٦٧ </span>- عن أبى سعيد أن عمر لما قال هذا قال له على بن أبى طالب: إنه يضر وينفع وذكر أن الله لما أخذ المواثيق على ولد آدم كتب ذلك فى رق وألقمه الحجر قال: وقد سمعت رسول الله صلى الله عليه وسلم يقول: " يؤتى يوم القيامة بالحجر الأسود وله لسان ذلق يشهد لمن استلمه بالتوحيد ".
** ك
(فتح الباري ٤٦٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٦٢: فى إسناده أبو هارون العبدى وهو ضعيف جدا.','2','167',NULL,NULL,'0');
INSERT INTO [b88] VALUES (668,'<span data-type="title" id=toc-670>٦٦٨ </span>- عن ابن عمر قال: كان بنو أبى طلحة يزعمون أنه لا يستطيع أحد فتح الكعبة غيرهم فأخذ رسول الله صلى الله عليه وسلم المفتاح ففتحها بيده.
** الفاكهى
(فتح الباري ٤٦٤/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٤٦٤: من طريق ضعيفة عن ابن عمر.','2','168',NULL,NULL,'0');
INSERT INTO [b88] VALUES (669,'<span data-type="title" id=toc-671>٦٦٩ </span>- حديث ابن عباس مرفوعا: " من دخل البيت دخل فى حسنة وخرج مغفورا له ".
** خز هق
(فتح الباري ٤٤٦/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٦٦: قال البيهقى: تفرد به عبد الله بن المؤمل وهو ضعيف.','2','169',NULL,NULL,'0');
INSERT INTO [b88] VALUES (670,'<span data-type="title" id=toc-672>٦٧٠ </span>- عن عائشة أنه صلى الله عليه وسلم خرج من عندها وهو قرير العين ثم رجع وهو كئيب فقال: " دخلت الكعبة فأخاف أن أكون قد شققت على أمتى ".
** د ت
(فتح الباري ٤٦٦/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٦٦: صححه الترمذى وابن خزيمة والحاكم.','2','170',NULL,NULL,'0');
INSERT INTO [b88] VALUES (671,'<span data-type="title" id=toc-673>٦٧١ </span>- عن أسامة قال: دخلت على رسول الله صلى الله عليه وسلم فى الكعبة فرأى صورا فدعا بدلو من ماء فأتيته به فضرب به الصور.
** طيا
(فتح الباري ٤٦٨/٣)
** إسناده جيد','2','171',NULL,NULL,'0');
INSERT INTO [b88] VALUES (672,'<span data-type="title" id=toc-674>٦٧٢ </span>- عن ابن عباس قال: قلت له كيف أصلى فى الكعبة قال: كما تصلى فى الجنازة تسبح وتكبر ولا تركع ولا تسجد ثم عند أركان البيت سبح وكبر وتضرع واستغفر ولا تركع ولا تسجد.
** عمر بن شبة فى كتاب مكة
(فتح الباري ٤٦٩/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ٤٦٩: سنده صحيح.','2','172',NULL,NULL,'0');
INSERT INTO [b88] VALUES (673,'<span data-type="title" id=toc-675>٦٧٣ </span>- قال عطاء: وبلغنى أن النبى صلى الله عليه وسلم أمر أم سلمة أن تطوف راكبة فى خدرها من وراء المصلين من جوف المسجد.
** الفاكهى
(فتح الباري ٤٨١/٣)
** مرسل
** قال الحافظ فى " الفتح " ٣ / ٤٨١: كأن البخارى حذفه لكونه مرسلا.','2','173',NULL,NULL,'0');
INSERT INTO [b88] VALUES (674,'<span data-type="title" id=toc-676>٦٧٤ </span>- عن ابن عباس موقوفا ومرفوعا: " الطواف بالبيت صلاة، إلا أن الله أباح فيه الكلام، فمن نطق فلا ينطق إلا بخير.
** د ت ن جه
(فتح الباري ٤٨٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٨٢: أخرجه أصحاب السنن وصححه ابن خزيمة وابن حبان.','2','174',NULL,NULL,'0');
INSERT INTO [b88] VALUES (675,'<span data-type="title" id=toc-677>٦٧٥ </span>- طريق عمرو بن شعيب عن أبيه عن جده: أن النبى صلى الله عليه وسلم أدرك رجلين وهما مقترنان، فقال: " مابال القران؟ "، قالا: إنا نذرنا لنقترن حتى نأتى الكعبة، فقال: " أطلقا أنفسكما ليس هذا نذرا إنما النذر ما يبتغى به وجه الله ".
** حم الفاكهى
(فتح الباري ٤٨٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٨٢: إسناده إلى عمرو حسن.','2','175',NULL,NULL,'0');
INSERT INTO [b88] VALUES (676,'<span data-type="title" id=toc-678>٦٧٦ </span>- عن المسور بن مخرمة أنه كان يقرن بين الأسابيع إذا طاف بعد الصبح والعصر، فإذا طلعت الشمس أو غربت صلى لكل أسبوع ركعتين.
** شب
(فتح الباري ٤٨٥/٣)
** إسناده جيد','2','176',NULL,NULL,'0');
INSERT INTO [b88] VALUES (677,'<span data-type="title" id=toc-679>٦٧٧ </span>- حديث جبير بن مطعم: أن رسول الله صلى الله عليه وسلم قال: " يابنى عبد مناف، من ولى منكم من أمر الناس شيئا، فلا يمنعن أحدا طاف بهذا البيت وصلى أى ساعة شاء من ليل أو نهار ".
** شفع د ت ن جه
(فتح الباري ٤٨٨/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٨٨: رواه الشافعى وأصحاب السنن وصححه الترمذى وابن خزيمة وغيرهما.','2','177',NULL,NULL,'0');
INSERT INTO [b88] VALUES (678,'<span data-type="title" id=toc-680>٦٧٨ </span>- عن أبى الزبير: عن جابر قال: كنا نطوف فنمسح الركن الفاتحة والخاتمة ولم نكن نطوف بعد الصبح حتى تطلع الشمس ولا بعد العصر حتى تغرب الشمس. قال: وسمعت رسول الله صلى الله عليه وسلم يقول: " تطلع الشمس بين قرنى شيطان ".
** حم
(فتح الباري ٤٨٩/٣)
** إسناده حسن','2','178',NULL,NULL,'0');
INSERT INTO [b88] VALUES (679,'<span data-type="title" id=toc-681>٦٧٩ </span>- عن عمرو بن دينار: رأيت ابن عمر طاف سبعا بعد الفجر وصلى ركعتين وراء المقام.
** ص
(فتح الباري ٤٨٩/٣)
** إسناده صحيح','2','179',NULL,NULL,'0');
INSERT INTO [b88] VALUES (680,'<span data-type="title" id=toc-682>٦٨٠ </span>- عن عائشة أنها قالت: إذا أردت الطواف بالبيت بعد صلاة الفجر أو العصر فطف، وأخر الصلاة حتى تغيب الشمس أو حتى تطلع فصل لكل أسبوع ركعتين.
** شب
(فتح الباري ٤٨٩/٣)
** إسناده حسن','2','180',NULL,NULL,'0');
INSERT INTO [b88] VALUES (681,'<span data-type="title" id=toc-683>٦٨١ </span>- حديث ابن عباس مرفوعا: " ماء زمزم لما شرب له ".
** ك
(فتح الباري ٤٩٣/٣)
** رجاله موثقون
** قال الحافظ فى " الفتح " ٣ / ٤٩٣: رجالث موثقون إلا أنه اختلف فى إرساله ووصله، وأرساله أصح، وله شاهد من حديث جابر، وهو أشهر منه أخرجه الشافعى وابن ماجه ورجاله ثقات إلا عبد الله بن المؤمل المكى، فذكر العقيلى أنه تفرد به، لكن ورد من رواية غيره عند البيهقى من طريق إبراهيم بن طهمان، ومن طريق حمزة الزيات كلاهما عن أبى الزبير بن سعيد عن جابر، ووقع فى " فوائد ابن المقرى " من طريق سويد بن سعيد عن ابن المبارك عن ابن أبى الموالى عن ابن المنكدر عن جابر، وزعم الدمياطى أنه رسم الصحيح وهو كما قال فى حديث الرجال إلا أن سويدا وإن أخرج له مسلم فإنه خلط وطعنوا فيه، وقد شذ بإسناده والمحفوظ عن ابن المبارك عن ابن المؤمل.','2','181',NULL,NULL,'0');
INSERT INTO [b88] VALUES (682,'<span data-type="title" id=toc-684>٦٨٢ </span>- عن ابن عمر مرفوعا عن النبى صلى الله عليه وسلم قال: " من جمع بين الحج والعمرة كفاه لهما طواف واحد وسعى واحد ".
** ص
(فتح الباري ٤٩٥/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٩٤: أعله الطحاوى بأن الدراوردى أخطأ فيه وأن الصواب أنه موقوف، وتمسك فى تخطئته بما رواه أيوب والليث وموسى بن عقبة وغير واحد عن نافع أن ذلك وقع لابن عمر وأنه قال: إن النبى صلى الله عليه وسلم فعل ذلك لاأنه روى هذا اللفظ عن النبى صلى الله عليه وسلم. قال الحافظ فى " الفتح " ٣ / ٤٩٥: وهو تعليل مردود فالدراوردى صدوق وليس ما رواه مخالفا لما رواه غيره فلا مانع من أن يكون الحديث عند نافع على الوجهين.','2','182',NULL,NULL,'0');
INSERT INTO [b88] VALUES (683,'<span data-type="title" id=toc-685>٦٨٣ </span>- عن على أنه: جمع بين الحج والعمرة فطاف لهما طوافين وسعى لهما سعيين ثم قال: هكذا رأيت رسول الله صلى الله عليه وسلم فعل.
** عب قط
(فتح الباري ٤٩٥/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٤٩٥: طرقه عن على عند عبد الرزاق والدارقطنى وغيرهما ضعيفة وكذا أخرج من حديث ابن مسعود بإسناد ضعيف نحوه وأخرج من حديث ابن عمر نحو ذلك وفيه الحسن بن عمارة وهو متروك.','2','183',NULL,NULL,'0');
INSERT INTO [b88] VALUES (684,'<span data-type="title" id=toc-686>٦٨٤ </span>- عن سلمة بن كهيل قال: طاف طاوس ماطاف أحد من أصحاب رسول الله صلى الله عليه وسلم لحجة وعمرته إلا طوافا واحدا.
** عب
(فتح الباري ٤٩٥/٣)
** إسناده صحيح','2','184',NULL,NULL,'0');
INSERT INTO [b88] VALUES (685,'<span data-type="title" id=toc-687>٦٨٥ </span>- حديث صفية بنت شيبة عن حبيبة بنت أبى تجرأه وهى إحدى نساء بنى عبد الدار قالت: دخلت مع نسوى قريش دار أل أبى حسين فرأيت رسول الله صلى الله عليه وسلم يسعى وإن مئزره ليدور من شدة السعى وسمعته يقول: " اسعوا فإن الله كتب عليكم السعى ".
** شفع حم
(فتح الباري ٤٩٨/٣)
** قال الحافظ فى " الفتح " ٣ / ٤٩٨: فى إسناد هذا الحديث عبد الله بن المؤمل وفيه ضعف، قلت: له طريق أخرى فى صحيح ابن خزيمة مختصرة، وعند الطبرانى عن ابن عباس كالأولى وإذا انضمت إلى الأولى قويت، واختلف على صفية بنت شيبة فى إسم الصحابية التى أخبرتها به ويجوز أن تكون أخذته عن جماعة، فقد وقع عند الدارقطنى عنها: " أخبرتنى نسوة من بنى عبد الدار فلا يضره الأختلاف.','2','185',NULL,NULL,'0');
INSERT INTO [b88] VALUES (686,'<span data-type="title" id=toc-688>٦٨٦ </span>- عن زيد بن حارثة قال: كان الصفا والمروة صنمان من نحاس يقال لهما أساف ونائلة كان المشركون إذا طافوا تمسحوا بهما.
** ن
(فتح الباري ٥٠٠/٣)
** إسناده قوى','2','186',NULL,NULL,'0');
INSERT INTO [b88] VALUES (687,'<span data-type="title" id=toc-689>٦٨٧ </span>- حديث ابن عباس قال: قالت الأنصار: إن السعى بين الصفا والمروة من أمر الجاهلية فأنزل الله عز وجل (إن الصفا والمروة من شعائر الله..) الآية.
** طب ابن أبى حاتم فى التفسير
(فتح الباري ٥٠٠/٣)
** إسناده حسن','2','187',NULL,NULL,'0');
INSERT INTO [b88] VALUES (688,'<span data-type="title" id=toc-690>٦٨٨ </span>- عن ابن عباس قال: " هذا ما أورثتكموه أم إسماعيل ".
** الفاكهى
(فتح الباري ٥٠٣/٣)
** إسناده حسن','2','188',NULL,NULL,'0');
INSERT INTO [b88] VALUES (689,'<span data-type="title" id=toc-691>٦٨٩ </span>- عن ابن عمر: إذا طافت ثم حاضت قبل أن تسعى بين الصفا والمروة فلتسع.
** شب
(فتح الباري ٥٠٥/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ٥٠٥: وعن عبد الأعلى عن هشام عن الحسن مثله، وهذا إسناد صحيح عن الحسن.','2','189',NULL,NULL,'0');
INSERT INTO [b88] VALUES (690,'<span data-type="title" id=toc-692>٦٩٠ </span>- عن عمر أنه قال لأهل مكة: مالكم يقدم الناس عليكم شعثا وأنتم تنضحون طيبا مدهنين، إذا رأيتم الهلال فأهلوا بالحج.
** ما
(فتح الباري ٥٠٦/٣)
** إسناده منقطع
** قال الحافظ فى " الفتح " ٣ / ٥٠٦: رواه مالك وغيره بإسناد منقطع وابن المنذر بإسناد متصل.','2','190',NULL,NULL,'0');
INSERT INTO [b88] VALUES (691,'<span data-type="title" id=toc-693>٦٩١ </span>- عن القاسم بن محمد: سمعت ابن الزبير يقول: إن من سنة الحج أن الإمام يروح إذا زالت الشمس يخطب فيخطب الناس فإذا فرغ من خطبته فصلى الظهر والعصر جميعا
** ابن المنذر
(فتح الباري ٥١٣/٣)
** إسناده صحيح','2','191',NULL,NULL,'0');
INSERT INTO [b88] VALUES (692,'<span data-type="title" id=toc-694>٦٩٢ </span>- كان جابر يقول: لا صلاة إلا بجمع.
** ابن المنذر
(فتح الباري ٥٢٠/٣)
** إسناده صحيح','2','192',NULL,NULL,'0');
INSERT INTO [b88] VALUES (693,'<span data-type="title" id=toc-695>٦٩٣ </span>- من طريق جابرالجعفى عن عدى بهذا الإسناد: صلى بجمع المغرب ثلاثا والعشاء ركعتين بإقامة واحدة.
** طب
(فتح الباري ٥٢٤/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٢٤: جابر وإن كان ضعيفا فقد تابعه محمد بن أبى ليلى عن عدى على ذكر الإقامة فيه عند الطبرانى أيضا، فيقوى كل واحد منهما بالآخر.','2','193',NULL,NULL,'0');
INSERT INTO [b88] VALUES (694,'<span data-type="title" id=toc-696>٦٩٤ </span>- حديث ابن عباس: أن النبى صلى الله عليه وسلم قال لغلمان بنى عبد المطلب: " لا ترموا الجمرة حتى تطلع الشمس ".
** د ن طحا حب
(فتح الباري ٥٢٨/٣)
** حسن
** قال الحافظ فى " الفتح " ٣ / ٥٢٨: وأخرجه الترمذى والطحاوى من طرق عن الحكم عن مقسم عنه، وأخرجه أبو داود من طريق حبيب عن عطاء وهذه الطرق يقوى بعضها بعضا، ومن ثم صححه الترمذى وابن حبان.','2','194',NULL,NULL,'0');
INSERT INTO [b88] VALUES (695,'<span data-type="title" id=toc-697>٦٩٥ </span>- عن عبد الله بن عمرو مرفوعا: " إنما جمع منزل لدلج المسلمين ".
** الطبرى
(فتح الباري ٥٢٩/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٥٢٩: سنده فيه ضعف.','2','195',NULL,NULL,'0');
INSERT INTO [b88] VALUES (696,'<span data-type="title" id=toc-698>٦٩٦ </span>- حديث عروة بن مضرس رفعه قال: " من شهد معنا صلاة الفجر بالمزدلفة وكان قد وقف قبل ذلك بعرفة ليلاأو نهارا فقد تم حجه.
** د ت ن جه
(فتح الباري ٥٢٩/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٢٩: رواه أصحاب السنن وصححه ابن حبان والدارقطنى والحاكم.','2','196',NULL,NULL,'0');
INSERT INTO [b88] VALUES (697,'<span data-type="title" id=toc-699>٦٩٧ </span>- عن ابن عباس أنه كان يقول: التلبية شعار الحج، فإن كنت حاجا فلب حتى بدء حلك، وبدء حلك أن ترمى جمرة العقبة.
** ابن المنذر
(فتح الباري ٥٣٣/٣)
** إسناد صحيح','2','197',NULL,NULL,'0');
INSERT INTO [b88] VALUES (698,'<span data-type="title" id=toc-700>٦٩٨ </span>- عن عبد الرحمن بن يزيد قال: حججت مع عبد الله فلما أفاض إلى جمع جعل يلبى فقال رجل: أعرابى هذا؟ ، فقال عبد الله: أنسى الناس أم ضلوا.
** طحا
(فتح الباري ٥٣٣/٣)
** إسناده صحيح','2','198',NULL,NULL,'0');
INSERT INTO [b88] VALUES (699,'<span data-type="title" id=toc-701>٦٩٩ </span>- عن ابن عباس عن الفضل قال: أفضت مع النبى صلى الله عليه وسلم من عرفات فلم يزل يلبى حتى رمى جمرة العقبة يكبر مع كل حصاة، ثم قطع التلبية مع آخر حصاة.
** خز
(فتح الباري ٥٣٣/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٣٣: قال ابن خزيمة: هذا حديث صحيح.','2','199',NULL,NULL,'0');
INSERT INTO [b88] VALUES (700,'<span data-type="title" id=toc-702>٧٠٠ </span>- عن محمد بن سيرين عن ابن عباس قال: ما كنت أرى أن دما واحدا يقضى على أكثر من واحد.
** إسماعيل القاضى
(فتح الباري ٥٣٤/٣)
** منقطع
** قال الحافظ فى " الفتح " ٣ / ٥٣٤: رواية محمد عن ابن عباس منقطعة.','2','200',NULL,NULL,'0');
INSERT INTO [b88] VALUES (701,'<span data-type="title" id=toc-703>٧٠١ </span>- قال ابن عباس: الهدى شاة، فقيل له فى ذلك، فقال: أنا أقرأ عليكم من كتاب الله ما تقوون به، مافى الظبى، قالوا: شاة، قال فإن الله تعالى يقول: (هديا بالغ الكعبة) .
** الطبرى
(فتح الباري ٥٣٥/٣)
** إسناده صحيح','2','201',NULL,NULL,'0');
INSERT INTO [b88] VALUES (702,'<span data-type="title" id=toc-704>٧٠٢ </span>- حديث على: أنه سئل هل يركب الرجل هديه؟ ، فقال: لا بأس قد كان النبى صلى الله عليه وسلم يمر بالرجال يمشون فيأمرهم يركبون هديه.
** حم
(فتح الباري ٥٣٧/٣)
** إسناده صالح','2','202',NULL,NULL,'0');
INSERT INTO [b88] VALUES (703,'<span data-type="title" id=toc-705>٧٠٣ </span>- عن عطاء كان النبى صلى الله عليه وسلم يأمر بالبدنة إذا أحتاج إليها سيدها أن يحمل عليها ويركبها غير منهكها، قلت: ماذا؟ ، قال: الراجل والمتيع اليسير فإن نتجت حمل عليها ولدها.
** ص مد
(فتح الباري ٥٣٨/٣)
** إسناده صحيح','2','203',NULL,NULL,'0');
INSERT INTO [b88] VALUES (704,'<span data-type="title" id=toc-706>٧٠٤ </span>- عن عمر وعلى أنهما قالا فى الرجل يرسل ببدنته: إنه يمسك عما يمسك عنه المحرم.
** شب
(فتح الباري ٥٤٦/٣)
** منقطع','2','204',NULL,NULL,'0');
INSERT INTO [b88] VALUES (705,'<span data-type="title" id=toc-707>٧٠٥ </span>- طريق عبد الملك بن جابر عن أبيه قال: كنت جالسا عند النبى صلى الله عليه وسلم فقد قميصه من جيبه حتى أخرجه من رجليه وقال: " إنى أمرت ببدنى التى بعثت بها أن تقلد اليوم وتشعر على مكان كذا، فلبست قميصى ونسيت فلم أكن لأخرج قميصى من رأسى ... " الحديث.
** طحا
(فتح الباري ٥٤٧/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٥٤٦: هذا لا حجة فيه لضعف إسناده.','2','205',NULL,NULL,'0');
INSERT INTO [b88] VALUES (706,'<span data-type="title" id=toc-708>٧٠٦ </span>- عن عائشة: أن رسول الله صلى الله عليه وسلم نحر عن أزواجه بقرة واحدة.
** ن د
(فتح الباري ٥٥١/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٥١: قال إسماعيل القاضى: تفرد يونس بذلك، وقد خالفه غيره اه. قال الحافظ: ورواية يونس أخرجها النسائى وأبو داود وغيرهما ويونس ثقة حافظ.','2','206',NULL,NULL,'0');
INSERT INTO [b88] VALUES (707,'<span data-type="title" id=toc-709>٧٠٧ </span>- عن أبى هريرة قال: ذبح رسول الله صلى الله عليه وسلم عمن اعتمر من نسائه فى حجة الوداع بقرة بينهن.
** ن
(فتح الباري ٥٥١/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٥١: صححه الحاكم وهو شاهد قوى لرواية الزهرى.','2','207',NULL,NULL,'0');
INSERT INTO [b88] VALUES (708,'<span data-type="title" id=toc-710>٧٠٨ </span>- عن عائشة قالت: ذبح عنا رسول الله صلى الله عليه وسلم يوم حججنا بقرة بقرة.
** ن
(فتح الباري ٥٥١/٣)
** شاذ','2','208',NULL,NULL,'0');
INSERT INTO [b88] VALUES (709,'<span data-type="title" id=toc-711>٧٠٩ </span>- عن ابن عباس: من قدم شيئا من نسكه أو أخره فليهرق لذلك دما.
** شب
(فتح الباري ٥٧٢/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٧٢: احتج الطحاوى بقول ابن عباس، وأجيب بأن الطريق بذلك إلى ابن عباس فيها ضعف فإن ابن أبى شيبة أخرجها وفيها إبراهيم بن مهاجر وفيه مقال.','2','209',NULL,NULL,'0');
INSERT INTO [b88] VALUES (710,'<span data-type="title" id=toc-712>٧١٠ </span>- حديث ابن عمر لفظه: أنزلت (إذا جاء نصر الله والفتح) على رسول الله صلى الله عليه وسلم فى وسط أيام التشريق، وعرف أنه الوداع فأمر براحلته القصواء فرحلت له فركب، فوقف بالعقبة واجتمع الناس إليه فقال: ياأيها الناس ... الحديث.
** هق
(فتح الباري ٥٧٧/٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٣ / ٥٧٧: وقع فى طريق ضعيفة عند البيهقى من حديث ابن عمر.','2','210',NULL,NULL,'0');
INSERT INTO [b88] VALUES (711,'<span data-type="title" id=toc-713>٧١١ </span>- عن الزهرى قال: كان النبى صلى الله عليه وسلم يخطب يوم النحر وفشغل الأمراء فأخرجوه إلى الغد.
** شب
(فتح الباري ٥٧٧/٣)
** مرسل
** قال الحافظ فى " الفتح " ٣ / ٥٧٧: هذا وإن كان مرسلا لكنه يعتضد.','2','211',NULL,NULL,'0');
INSERT INTO [b88] VALUES (712,'<span data-type="title" id=toc-714>٧١٢ </span>- عن مسروق: أن النبى صلى الله عليه وسلم خطب يوم النحر.
** لم
(فتح الباري ٥٧٨/٣)
** مرسل','2','212',NULL,NULL,'0');
INSERT INTO [b88] VALUES (713,'<span data-type="title" id=toc-715>٧١٣ </span>- عن عمرو بن ميمون: عن عمر: أنه رمى جمرة العقبة فى السنة التى أصيب فيها وفى غيرها من بطن الوادى.
** شب
(فتح الباري ٥٨٠/٣)
** إسناده صحيح','2','213',NULL,NULL,'0');
INSERT INTO [b88] VALUES (714,'<span data-type="title" id=toc-716>٧١٤ </span>- من طريق الأسود: رأيت عمر رمى جمرة العقبة من فوقها.
** شب
(فتح الباري ٥٨٠/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٨٠: فى إسناده حجاج بن أرطاة وفيه ضعف.','2','214',NULL,NULL,'0');
INSERT INTO [b88] VALUES (715,'<span data-type="title" id=toc-717>٧١٥ </span>- عن عبد الرحمن بن يزيد: لما أتى عبد الله جمرة العقبة استبطن الوادى واستقبل القبلة.
** ت
(فتح الباري ٥٨٢/٣)
** شاذ
** قال الحافظ فى " الفتح " ٣ / ٥٨٢: فى إسناده المسعودى وقد أختلط.','2','215',NULL,NULL,'0');
INSERT INTO [b88] VALUES (716,'<span data-type="title" id=toc-718>٧١٦ </span>- عن عطاء: كان ابن عمر يقوم عندالجمرتين مقدار ما يقرأ سورة البقرة.
** شب
(فتح الباري ٥٨٤/٣)
** إسناده صحيح','2','216',NULL,NULL,'0');
INSERT INTO [b88] VALUES (717,'<span data-type="title" id=toc-719>٧١٧ </span>- ان ابن عمر كان يمشى إلى الجمار مقبلا ومدبرا.
** شب
(فتح الباري ٥٨٤/٣)
** إسناده صحيح','2','217',NULL,NULL,'0');
INSERT INTO [b88] VALUES (718,'<span data-type="title" id=toc-720>٧١٨ </span>- عن ابن عمر قال: طافت امرأة بالبيت يوم النحر ثم حاضت، فأمر عمر بحبسها بمكة بعد أن ينفر الناس حتى تطهر وتطوف بالبيت.
** لم
(فتح الباري ٥٨٧/٣)
** إسناده صحيح','2','218',NULL,NULL,'0');
INSERT INTO [b88] VALUES (719,'<span data-type="title" id=toc-721>٧١٩ </span>- عن ابن عمر قال: من حج فليكن آخر عهده بالبيت إلا الحيض رخص لهن رسول الله صلى الله عليه وسلم.
** ن ت ك
(فتح الباري ٥٨٩/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٨٩: أخرجه النسائى والترمذى وصححه الحاكم.','2','219',NULL,NULL,'0');
INSERT INTO [b88] VALUES (720,'<span data-type="title" id=toc-722>٧٢٠ </span>- من طريق أبى هريرة مرفوعا: " أميران وليسا بأميرين: من تبع جنازة فليس له أن ينصرف حتى تدفن أو يأذن أهلها، والمرأة تحج أو تعتمر مع قوم فتحيض قبل طواف الركن فليس لهم أن ينصرفوا حتى تطهر أو تأذن لهم ".
** البيهقى فى الفوائد
(فتح الباري ٥٩٠/٣)
** فى إسناده ضعف شديد
** قال الحافظ فى " الفتح " ٣ / ٥٩٠: وأخرجه البزار من حديث جابر، وفى إسناد كل منهما ضعف شديد.','2','220',NULL,NULL,'0');
INSERT INTO [b88] VALUES (721,'<span data-type="title" id=toc-723>٧٢١ </span>- روى الحجاج بن أرطأة عن محمد بن المنكدر: عن جابر: أتى أعرابى النبى صلى الله عليه وسلم فقال: يارسول الله أخبرنى عن العمرة أواجبة هى؟ ، فقال: " لا، وإن تعتمر خير لك ".
** ت
(فتح الباري ٥٩٧/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٧٩: الحجاج ضعيف.','2','221',NULL,NULL,'0');
INSERT INTO [b88] VALUES (722,'<span data-type="title" id=toc-724>٧٢٢ </span>- روى ابن لهيعة عن عطاء: عن جابر مرفوعا: " الحج والعمرة فريضتان ".
** عد
(فتح الباري ٥٩٧/٣)
** قال الحافظ فى " الفتح " ٣ / ٥٩٧: ابن لهيعة ضعيف ولا يثبت فى هذا الباب عن جابر شىء.','2','222',NULL,NULL,'0');
INSERT INTO [b88] VALUES (723,'<span data-type="title" id=toc-725>٧٢٣ </span>- روى ابن الجهم المالكى عن جابر موقوفا: " ليس مسلم إلا عليه عمرة ".
** ابن الجهم المالكى
(فتح الباري ٥٩٧/٣)
** إسناده حسن','2','223',NULL,NULL,'0');
INSERT INTO [b88] VALUES (724,'<span data-type="title" id=toc-726>٧٢٤ </span>- من طريق عطاء: عن ابن عباس: الحج والعمرة فريضتان.
** ك
(فتح الباري ٥٩٨/٣)
** إسناده ضعيف','2','224',NULL,NULL,'0');
INSERT INTO [b88] VALUES (725,'<span data-type="title" id=toc-727>٧٢٥ </span>- عن عائشة أن النبى صلى الله عليه وسلم اعتمر ثلاث عمر: عمرتين فى ذى القعدة وعمرة فى شوال.
** ص
(فتح الباري ٦٠٠/٣)
** إسناده قوى
** قال الحافظ فى " الفتح " ٣ / ٦٠٠: وقد رواه ابن مالك عن هشام عن أبيه مرسلا.','2','225',NULL,NULL,'0');
INSERT INTO [b88] VALUES (726,'<span data-type="title" id=toc-728>٧٢٦ </span>- عن عائشة: لم يعتمر رسول الله صلى الله عليه وسلم إلا فى ذى القعدة.
** جه
(فتح الباري ٦٠٠/٣)
** إسناده صحيح','2','226',NULL,NULL,'0');
INSERT INTO [b88] VALUES (727,'<span data-type="title" id=toc-729>٧٢٧ </span>- روى عن عائشة قالت: خرجت مع رسول الله صلى الله عليه وسلم فى عمرة رمضان فأفطر وصمت، وقصر وأتممت. الحديث.
** قط
(فتح الباري ٦٠٣/٣)
** قال الحافظ فى " الفتح " ٣ / ٦٠٣: قال الدارقطنى: إن إسناده حسن، قال صاحب الهدى: إنه غلط لأن النبى صلى الله عليه وسلم لم يعتمر فى رمضان اه. ورواه الدارقطنى بإسناد آخر إلى العلاء بن زهير فلم يقل فى الإسناد عن أبيه، ولا قال فيه فى رمضان.','2','227',NULL,NULL,'0');
INSERT INTO [b88] VALUES (728,'<span data-type="title" id=toc-730>٧٢٨ </span>- عن سعيد بن جبير عن امرأة من الأنصار يقال لها أم سنان أنها أرادت الحج.... فذكر الحديث.
** منيع
(فتح الباري ٦٠٣/٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٣ / ٦٠٣: ذكر الحديث نحوه دون ذكر قصة زوجها، وقد اختلف فى صحابيه على عطاء.','2','228',NULL,NULL,'0');
INSERT INTO [b88] VALUES (729,'<span data-type="title" id=toc-731>٧٢٩ </span>- من طريق ابن أبى مليكة عنها فى هذا الحديث قال: ثم أرسل إلى عبد الرحمن بن أبى بكر فقال: احملها خلفك حتى تخرج من الحرم فوالله ما قال فتخرجها إلىالجعرانة ولا إلى التنعيم.
** حم
(فتح الباري ٦٠٧/٣)
** ضعيف
** قال الحافظ فى " الفتح " ٣ / ٦٠٧: وهى راوية ضعيفة لضعف أبى عامر الخراز الراوى له عن ابن أبى مليكة.','2','229',NULL,NULL,'0');
INSERT INTO [b88] VALUES (730,'<span data-type="title" id=toc-732>٧٣٠ </span>- عن قيس بن جبير النهشلى قال: كانوا إذا أحرموا لم يأتوا بيتا من قبل بابه ولكن من قبل ظهره، وكانت الحمس تفعله فدخل رسول الله صلى الله عليه وسلم حائطا فاتبعه رجل يقال له: رفاعة بن تابوت ولم يكن من الحمس. فذكر القصة.
** عبد الطبرى
(فتح الباري ٦٢١/٣)
** مرسل','2','230',NULL,NULL,'0');
INSERT INTO [b88] VALUES (731,'<span data-type="title" id=toc-733>٧٣١ </span>- عن الحسن قال كان الرجل فى الجاهلية يهم بالشىء يصنعه فيحبس عن ذلك فلا يأتى بيتا من قبل بابه حتى يأتى الذى كان هم به.
** عبد
(فتح الباري ٦٢٢/٣)
** إسناده صحيح','2','231',NULL,NULL,'0');
INSERT INTO [b88] VALUES (732,'<span data-type="title" id=toc-734>٧٣٢ </span>- قال محمد بن كعب القرظى: كان الرجل إذا اعتكف لم يدخل منزله من باب البيت، فنزلت.
** ابن أبى حاتم
(فتح الباري ٦٢٢/٣)
** إسناده ضعيف
** تعقيب: قال الشيخ ابن باز (٣ / ٦٢٢) فى نسخة صحيح.','2','232',NULL,NULL,'0');
INSERT INTO [b88] VALUES (733,'<span data-type="title" id=toc-735>٧٣٣ </span>- " وليتخذ لأهله هدية وإن لم يجد إلا حجرا ".
** لم
(فتح الباري ٦٢٣/٣)
** قال الحافظ فى " الفتح " ٣ / ٦٢٣: قال ابن عبد البر: زاد فيه بعض الضعفاء عن مالك " وليتخذ ... " الحديث. وقال: وهى زيادة منكرة.','2','233',NULL,NULL,'0');
INSERT INTO [b88] VALUES (734,'<span data-type="title" id=toc-736>٧٣٤ </span>- أفتى ابن مسعود رجلا لدغ بإنه محصر.
** ابن جرير
(فتح الباري ٣/٤)
** إسناده صحيح','2','234',NULL,NULL,'0');
INSERT INTO [b88] VALUES (735,'<span data-type="title" id=toc-737>٧٣٥ </span>- عن ابن عباس قال: لا حصر إلا من حبسه عدو فيحل بعمرة، وليس عليه حج ولا عمرة.
** عب شفع
(فتح الباري ٣/٤)
** صحيح','2','235',NULL,NULL,'0');
INSERT INTO [b88] VALUES (736,'<span data-type="title" id=toc-738>٧٣٦ </span>- عن عائشة قالت: لا أعلم المحرم يحل بشىء دون البيت.
** ابن جرير الطبرى
(فتح الباري ٣/٤)
** إسناده صحيح','2','236',NULL,NULL,'0');
INSERT INTO [b88] VALUES (737,'<span data-type="title" id=toc-739>٧٣٧ </span>- عن ابن عباس: لا احصار اليوم.
** ابن جرير
(فتح الباري ٣/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ٣: وروى ذلك عن عبد الله بن الزبير.','2','237',NULL,NULL,'0');
INSERT INTO [b88] VALUES (738,'<span data-type="title" id=toc-740>٧٣٨ </span>- عن أبى قلابة قال: خرجت معتمرا فوقعت عن راحلتى فانكسرت، فأرسلت إلى ابن عباس وابن عمر فقالا: ليس لها وقت كالحج يكون على إحرامه حتى يصل إلى البيت.
** إسماعيل القاضى
(فتح الباري ٥/٤)
** إسناده صحيح','2','238',NULL,NULL,'0');
INSERT INTO [b88] VALUES (739,'<span data-type="title" id=toc-741>٧٣٩ </span>- عن هشام بن عروة عن أبيه: أن رسول الله صلى الله عليه وسلم مر بضباعة بنت الزبير فقال: " أما تريدين الحج؟ "، فقالت: إنى شاكية، فقال لها: " حجى واشترطى أن محلى حيث حبستنى ".
** شفع
(فتح الباري ٨/٤)
** قال الحافظ فى " الفتح " ٤ / ٨: قال البيهقى: قد ثبت هذا الحديث من أوجه عن النبى صلى الله عليه وسلم اه. ثم ساقه من طريق عبد الجبار بن العلاء عن ابن عيينة موصولا بذكر عائشة فيه، وقال: وقد وصله عبد الجبار وهو ثقة اه. قال الحافظ فى " الفتح " ٤ / ٩: لقصة ضباعة شواهد منها حديث ابن عباس أخرجه مسلم وأصحاب السنن والبيهقى من طرق عن ابن عباس، قال الترمذى: وفى الباب عن جابر وأسماء بنت أبى بكر.اه. قلت: وعن ضباعة نفسها وعن سعدى بنت عوف وأسانيدها كلها قوية.','2','239',NULL,NULL,'0');
INSERT INTO [b88] VALUES (740,'<span data-type="title" id=toc-742>٧٤٠ </span>- عن كعب فى آخر هذا الحديث: فقلت يارسول الله: خر لى، قال: " أطعم ستة مساكين ".
** طب
(فتح الباري ١٥/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ١٥: رواه الطبرانى من طريق ضعيفة عن عطاء عن كعب.','2','240',NULL,NULL,'0');
INSERT INTO [b88] VALUES (741,'<span data-type="title" id=toc-743>٧٤١ </span>- من طريق عمرو بن أميه أن الصعب أهدى للنبى صلى الله عليه وسلم عجز حمار وحسن وهو بالجحفة فأكل منه، وأكل القوم.
** ابن وهب هق
(فتح الباري ٣٢/٤)
** إسناده حسن','2','241',NULL,NULL,'0');
INSERT INTO [b88] VALUES (742,'<span data-type="title" id=toc-744>٧٤٢ </span>- حديث عمير بن سلمة أن البهزى أهدى للنبى صلى الله عليه وسلم ظبيا وهو محرم، فأمر أبابكر أن يقسمه بين الرفاق.
** ما د ت ن جه
(فتح الباري ٣٣/٤)
** قال الحافظ فى " الفتح " ٤ / ٣٣: أخرجه مالك وأصحاب السنن وصححه ابن خزيمة وغيره.','2','242',NULL,NULL,'0');
INSERT INTO [b88] VALUES (743,'<span data-type="title" id=toc-745>٧٤٣ </span>- طريق سعيد بن المسيب عن النبى صلى الله عليه وسلم قال: " يقتل المحرم الحية والذئب ".
** شب ص د
(فتح الباري ٣٦/٤)
** رجاله ثقات','2','243',NULL,NULL,'0');
INSERT INTO [b88] VALUES (744,'<span data-type="title" id=toc-746>٧٤٤ </span>- طريق حجاج بن أرطأة عن وبرة عن ابن عمر قال: أمر رسول الله صلى الله عليه وسلم بقتل الذئب للمحرم.
** حم
(فتح الباري ٣٦/٤)
** قال الحافظ فى " الفتح " ٤ / ٣٦: حجاج ضعيف، وخالفه مسعر عن وبرة، فرواه موقوفا، أخرجه ابن أبى شيبة.','2','244',NULL,NULL,'0');
INSERT INTO [b88] VALUES (745,'<span data-type="title" id=toc-747>٧٤٥ </span>- عن أبى هريرة قال: الكلب العقور الأسد.
** ص
(فتح الباري ٣٩/٤)
** إسناده حسن','2','245',NULL,NULL,'0');
INSERT INTO [b88] VALUES (746,'<span data-type="title" id=toc-748>٧٤٦ </span>- قوله صلى الله عليه وسلم: " اللهم سلط عليه كلبا من كلابك "، فقتله الأسد.
** ك
(فتح الباري ٣٩/٤)
** حسن','2','246',NULL,NULL,'0');
INSERT INTO [b88] VALUES (747,'<span data-type="title" id=toc-749>٧٤٧ </span>- من مرسل عطاء بن يزيد: " فلا يستن بى أحد فيقول: قتل فيها رسول الله صلى الله عليه وسلم ".
** ص
(فتح الباري ٤٤/٤)
** مرسل','2','247',NULL,NULL,'0');
INSERT INTO [b88] VALUES (748,'<span data-type="title" id=toc-750>٧٤٨ </span>- من طريق أبى عثمان النهدى أن أبا برزة الأسلمى قتل ابن خطل وهو متعلق بأستار الكعبة.
** شب
(فتح الباري ٦١/٤)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٤ / ٦١: وله شاهد عند ابن المبارك فى البر والصلة من حديث أبى برزة نفسه ورواه أحمد من وجه آخر.','2','248',NULL,NULL,'0');
INSERT INTO [b88] VALUES (749,'<span data-type="title" id=toc-751>٧٤٩ </span>- قال مالك: قال ابن شهاب ولم يكن رسول الله صلى الله عليه وسلم يومئذ محرما.
** ما
(فتح الباري ٦١/٤)
** مرسل
** قال الحافظ فى " الفتح " ٤ / ٦١: وقع فى الموطأ من رواية أبى مصعب وغيره، ويشهد له ما رواه مسلم من حديث جابر.','2','249',NULL,NULL,'0');
INSERT INTO [b88] VALUES (750,'<span data-type="title" id=toc-752>٧٥٠ </span>- قال طاوس: لم يدخل النبى صلى الله عليه وسلم مكة إلا محرما إلا يوم فتح مكة.
** شب
(فتح الباري ٦١/٤)
** إسناده صحيح','2','250',NULL,NULL,'0');
INSERT INTO [b88] VALUES (751,'<span data-type="title" id=toc-753>٧٥١ </span>- عن ابن عمر لا يحج أحد عن أحد.
** ص
(فتح الباري ٦٦/٤)
** إسناده صحيح','2','251',NULL,NULL,'0');
INSERT INTO [b88] VALUES (752,'<span data-type="title" id=toc-754>٧٥٢ </span>- من طريق عوف عن الحسن قال: بلغنى أن رسول الله صلى الله عليه وسلم أتاه رجل فقال: إن أبى شيخ كبير أدرك الإسلام لم يحج ... الحديث.
** خز
(فتح الباري ٦٨/٤)
** مرسل','2','252',NULL,NULL,'0');
INSERT INTO [b88] VALUES (753,'<span data-type="title" id=toc-755>٧٥٣ </span>- من طريق سعيد بن جبير عن ابن عباس عن الفضل بن عباس قال: كنت ردف النبى صلى الله عليه وسلم وأعرابى معه بنت له حسناء فجعل الأعرابى يعرضها لرسول الله صلى الله عليه وسلم رجاء أن يتزوجها، وجعلت التفت إليها، ويأخذ النبى صلى الله عليه وسلم برأسى فيلويه، فكان يلبى حتى رمى جمرة العقبة.
** يع
(فتح الباري ٦٨/٤)
** إسناده قوى','2','253',NULL,NULL,'0');
INSERT INTO [b88] VALUES (754,'<span data-type="title" id=toc-756>٧٥٤ </span>- عن أبى العوث بن حصين الخثعمى أنه استفتى النبى صلى الله عليه وسلم عن حجة كانت على أبيه.
** جه
(فتح الباري ٦٨/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ٦٨: إسنادها ضعيف، ولعله كان فيه عن أبى الغوث حصين، فزيد فى الرواية ابن، أو أن أباالغوث أيضا كان مع أبيه حصين، فسأل كما سأل أبوه وأخته والله أعلم.','2','254',NULL,NULL,'0');
INSERT INTO [b88] VALUES (755,'<span data-type="title" id=toc-757>٧٥٥ </span>- " عنه، وليس لأحد بعده ".
** عبد الملك بن حبيب صاحب الواضحة
(فتح الباري ٦٩/٤)
** ضعيف مرسلا
** قال الحافظ فى " الفتح " ٤ / ٦٩: رواه عبد الملك بن حبيب صاحب الواضحة بإسنادين مرسلين. ولا حجة فيه لضعف الإسنادين مع إرسالهما.','2','255',NULL,NULL,'0');
INSERT INTO [b88] VALUES (756,'<span data-type="title" id=toc-758>٧٥٦ </span>- من حديث ابن عباس فزاد فى الحديث: حج عن أبيك فإن لم يزده خيرا لم يزده شرا ".
** عب
(فتح الباري ٧٠/٤)
** قال الحافظ فى " الفتح " ٤ / ٧٠: جزم الحفاظ بأنها رواية شاذة.','2','256',NULL,NULL,'0');
INSERT INTO [b88] VALUES (757,'<span data-type="title" id=toc-759>٧٥٧ </span>- عن ابن عباس: أيما غلام حج به أهله ثم بلغ فعليه حجة أخرى.
** طحا
(فتح الباري ٧١/٤)
** إسناده صحيح','2','257',NULL,NULL,'0');
INSERT INTO [b88] VALUES (758,'<span data-type="title" id=toc-760>٧٥٨ </span>- " وعلى هوادجهم الطيالسة الخضر ".
** سعد
(فتح الباري ٧٣/٤)
** قال الحافظ فى " الفتح " ٤ / ٧٣: فى إسناده الواقدى.','2','258',NULL,NULL,'0');
INSERT INTO [b88] VALUES (759,'<span data-type="title" id=toc-761>٧٥٩ </span>- من طريق أبى إسحق السبيعى قال: رأيت نساء النبى صلى الله عليه وسلم حججن فى هوادج عليها الطيالسة زمن المغيرة.
** سعد
(فتح الباري ٧٣/٤)
** إسناده صحيح','2','259',NULL,NULL,'0');
INSERT INTO [b88] VALUES (760,'<span data-type="title" id=toc-762>٧٦٠ </span>- من طريق واقد بن أبى واقد الليثى عن أبيه: أن النبى صلى الله عليه وسلم قال لنسائه فى حجة الوداع: " هذه ثم ظهور الحصر ".
** د حم
(فتح الباري ٧٤/٤)
** إسناده صحيح','2','260',NULL,NULL,'0');
INSERT INTO [b88] VALUES (761,'<span data-type="title" id=toc-763>٧٦١ </span>- من مرسل أبى جعفر الباقر قال: منع عمر أزواج النبى صلى الله عليه وسلم الحج والعمرة.
** سعد
(فتح الباري ٧٤/٤)
** مرسل','2','261',NULL,NULL,'0');
INSERT INTO [b88] VALUES (762,'<span data-type="title" id=toc-764>٧٦٢ </span>- من طريق ابن جريج عن عمرو بن دينار بلفظ: لا تحجن امرأة إلا ومعها ذو محرم.
** قط
(فتح الباري ٧٦/٤)
** قال الحافظ فى " الفتح " ٤ / ٧٦: صححه أبو عوانة.','2','262',NULL,NULL,'0');
INSERT INTO [b88] VALUES (763,'<span data-type="title" id=toc-765>٧٦٣ </span>- حديث ابن عمر مرفوعا: سفر المرأة مع عبدها ضيعة.
** ص
(فتح الباري ٧٧/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ٧٧: فى إسناده ضعف.','2','263',NULL,NULL,'0');
INSERT INTO [b88] VALUES (764,'<span data-type="title" id=toc-766>٧٦٤ </span>- عن عوف بن مالك قال: دخل رسول الله صلى الله عليه وسلم المسجد ثم نظر إلينا، فقال: " أما والله ليدعنها أهلها مذللة أربعين عاما للعوافى، أتدرون ما العوافى؟ ، الطير والسباع ".
** عمر بن شبة
(فتح الباري ٩٠/٤)
** إسناده صحيح','2','264',NULL,NULL,'0');
INSERT INTO [b88] VALUES (765,'<span data-type="title" id=toc-767>٧٦٥ </span>- حديث جابر أنه سمع رسول الله صلى الله عليه وسلم يقول: " ليأتين على أهل المدينة زمان ينطلق الناس منها إلى الأرياف يلتمسون الرخاء فيجدون رخاء ثم يأتون فيتحملون بأهليهم إلى الرخاء والمدينة خير لهم لو كانوا يعلمون ".
** حم
(فتح الباري ٩٢/٤)
** قال الحافظ فى " الفتح " ٤ / ٩٢: فى إسناده ابن لهيعة ولا بأس به فى المتابعات.','2','265',NULL,NULL,'0');
INSERT INTO [b88] VALUES (766,'<span data-type="title" id=toc-768>٧٦٦ </span>- عن عوف بن مالك أنه رأى رؤيا فيها أن عمر شهيد مستشهد، فقال: لما قصها عليه أنى لى بالشهادة وأنا بين ظهرانى جزيرة العرب لست أغزو والناس حولى ثم قال: بلى يأتى بها الله إن شاء.
** سعد
(فتح الباري ١٠١/٤)
** إسناده صحيح','2','266',NULL,NULL,'0');
INSERT INTO [b88] VALUES (767,'<span data-type="title" id=toc-769>٧٦٧ </span>- من وجه منقطع وزاد: فكان الناس يتعجبون من ذلك ولا يدرون ما وجهه حتى طعن أبو لؤلؤة عمر رضى الله عنه.
** عمر بن شبة
(فتح الباري ١٠١/٤)
** إسناده منقطع
** قال الحافظ فى " الفتح " ٤ / ١٠١: من وجه منقطع.','2','267',NULL,NULL,'0');
INSERT INTO [b88] VALUES (768,'<span data-type="title" id=toc-770>٧٦٨ </span>- عن أبى أمامة قال: قلت يارسول الله: مرنى بأمر آخذه عنك، قال: " عليك بالصوم فإنه لا مثل له ". وفى رواية: " لا عدل له ".
** ن
(فتح الباري ١٠٤/٤)
** إسناده صحيح','2','268',NULL,NULL,'0');
INSERT INTO [b88] VALUES (769,'<span data-type="title" id=toc-771>٧٦٩ </span>- حديث جابر فى اثناء حديث مرفوع فى فضل هذه الأمة فى رمضان وأما الثانية: فإن خلوف أفواههم حين يمسون أطيب عند الله من ريح المسك.
** مسندالحسن بن سفيان هب
(فتح الباري ١٠٦/٤)
** قال الحافظ فى " الفتح " ٤ / ١٠٦: قال المنذرى: إسناده مقارب.','2','269',NULL,NULL,'0');
INSERT INTO [b88] VALUES (770,'<span data-type="title" id=toc-772>٧٧٠ </span>- قوله صلى الله عليه وسلم: " ليس فى الصيام رياء ".
** هب
(فتح الباري ١٠٧/٤)
** مرسل
** قال الحافظ فى " الفتح " ٤ / ١٠٧: وأورده البيهقى فى الشعب من وجه آخر عن الزهرى موصولا عن أبى سلمة عن أبى هريرة وإسناده ضعيف.','2','270',NULL,NULL,'0');
INSERT INTO [b88] VALUES (771,'<span data-type="title" id=toc-773>٧٧١ </span>- عن أبى هريرة: الصيام لا رياء فيه، قال الله عز وجل: هو لى وأنا أجزى به.
** هب
(فتح الباري ١٠٧/٤)
** إسناده ضعيف','2','271',NULL,NULL,'0');
INSERT INTO [b88] VALUES (772,'<span data-type="title" id=toc-774>٧٧٢ </span>- " وأعلموا أن خير أعمالكم الصلاة ".
** لم
(فتح الباري ١٠٨/٤)
** صحيح','2','272',NULL,NULL,'0');
INSERT INTO [b88] VALUES (773,'<span data-type="title" id=toc-775>٧٧٣ </span>- عن أبى هريرة مرفوعا: " لا تقولوا رمضان، فإن رمضان اسم من أسماء الله ولكن قولوا: شهر رمضان ".
** عد
(فتح الباري ١١٣/٤)
** ضعيف
** قال الحافظ فى " الفتح " ٤ / ١١٣: أخرجه ابن عدى فى الكامل وضعفه بأبى معشر، قال البيهقى: قد روى عن أبى معشر عن محمد بن كعب وهو أشبه، وروى عن مجاهد والحسن من طريقين ضعيفين.','2','273',NULL,NULL,'0');
INSERT INTO [b88] VALUES (774,'<span data-type="title" id=toc-776>٧٧٤ </span>- حديث أنس: " من لم يدع الخنا والكذب..... ".
** طس
(فتح الباري ١١٧/٤)
** رجاله ثقات','2','274',NULL,NULL,'0');
INSERT INTO [b88] VALUES (775,'<span data-type="title" id=toc-777>٧٧٥ </span>- من طريق منصور عن ربعى: أن عمارا وناسا معه أتوهم يسألونهم فى اليوم الذى يشك فيه، فاعتزلهم رجل، فقال له عمار: تعال فكل، فقال: إنى صائم، فقال له عمار: إن كنت تؤمن بالله واليوم الآخر فتعال وكل.
** شب
(فتح الباري ١٢٠/٤)
** إسناده حسن
** قال الحافظ فى " الفتح " ٤ / ١٢٠: ورواه عبد الرزاق من وجه آخر عن منصور عن ربعى عن رجل عن عمار، وله شاهد من وجه آخر، أخرجه اسحق بن راهوية من رواية سماك عن عكرمة ومنهم من وصله بذكر ابن عباس فيه.','2','275',NULL,NULL,'0');
INSERT INTO [b88] VALUES (776,'<span data-type="title" id=toc-778>٧٧٦ </span>- حديث عائشة: كان رسول الله صلى الله عليه وسلم يتحفظ من شعبان مالا يتحفظ من غيره ثم يصوم لرؤية رمضان، فإن غم عليه عد ثلاثين يوما ثم صام.
** قط خز د
(فتح الباري ١٢١/٤)
** قال الحافظ فى " الفتح " ٤ / ١٢١: صححه الدارقطنى.','2','276',NULL,NULL,'0');
INSERT INTO [b88] VALUES (777,'<span data-type="title" id=toc-779>٧٧٧ </span>- من طريق هشيم عن خالد الحذاء بسنده، بلفظ: كل شهر حرام لا ينقص ثلاثون يوما وثلاثون ليلة.
** طب
(فتح الباري ١٢٦/٤)
** شاذ
** قال الحافظ فى " الفتح " ٤ / ١٢٦: هذا بهذا اللفظ شاذ.','2','277',NULL,NULL,'0');
INSERT INTO [b88] VALUES (778,'<span data-type="title" id=toc-780>٧٧٨ </span>- عن أبى هريرة مرفوعا: " إذا انتصف شعبان فلا تصوموا ".
** د ت ن جه
(فتح الباري ١٢٩/٤)
** قال الحافظ فى " الفتح " ٤ / ١٢٦: أخرجه أصحاب السنن وصححه ابن حبان وغيره، وقال أحمد وابن معين: منكر.','2','278',NULL,NULL,'0');
INSERT INTO [b88] VALUES (779,'<span data-type="title" id=toc-781>٧٧٩ </span>- عن أنس مرفوعا: " أفضل الصيام بعد رمضان شعبان ".
** لم
(فتح الباري ١٢٩/٤)
** إسناده ضعيف','2','279',NULL,NULL,'0');
INSERT INTO [b88] VALUES (780,'<span data-type="title" id=toc-782>٧٨٠ </span>- من طريق عبد ربه بن سعيد، عن أبى عياض عن عبد الرحمن بن الحارث قال: أرسلنى مروان إلى عائشة، فأتيتها فلقيت غلامها ذكوان فأرسلته إليها، فسألها عن ذلك، فقالت: فذكر الحديث مرفوعا، قال: فأتيت مروان فحدثته بذلك فأرسلنى إلى أم سلمة فأتيتها فلقيت غلامها نافعا فأرسله إليها فسألها عن ذلك.
** ن
(فتح الباري ١٤٤/٤)
** إسناده فيه نظر
** قال الحافظ فى " الفتح " ٤ / ١٤٤: فى إسناده نظر لأن أبا عياض مجهول.','2','280',NULL,NULL,'0');
INSERT INTO [b88] VALUES (781,'<span data-type="title" id=toc-783>٧٨١ </span>- عن أبى هريرة أنه قال: كنت حدثتكم من أصبح جنبا فقد أفطر، وإن ذلك من كيس أبى هريرة.
** ابن عبد البر
(فتح الباري ١٤٦/٤)
** قال الحافظ فى " الفتح " ٤ / ١٤٦: لا يصح ذلك عن أبى هريرة لأنه من رواية عمر بن قيس وهو متروك.','2','281',NULL,NULL,'0');
INSERT INTO [b88] VALUES (782,'<span data-type="title" id=toc-784>٧٨٢ </span>- عن عبيد الله بن عبد الله بن عمر أنه احتلم ليلا فى رمضان فاستيقظ قبل أن يطلع الفجر ثم نام قبل أن يغتسل فلم يستيقظ حتى أصبح، قال: فاستفتيت أبا هريرة فقال: أفطر.
** ن
(فتح الباري ١٤٧/٤)
** إسناده صحيح','2','282',NULL,NULL,'0');
INSERT INTO [b88] VALUES (783,'<span data-type="title" id=toc-785>٧٨٣ </span>- عن حكيم بن عقال قال: سألت عائشة ما يحرم على من أمرأتى وأنا صائم، قالت: فرجها.
** خت طحا
(فتح الباري ١٤٩/٤)
** قال الحافظ فى " الفتح " ٤ / ١٤٩: إسناده إلى حكيم صحيح.','2','283',NULL,NULL,'0');
INSERT INTO [b88] VALUES (784,'<span data-type="title" id=toc-786>٧٨٤ </span>- عن ابن عمر: أنه كان يكره القبلة والمباشرة.
** شب
(فتح الباري ١٥٠/٤)
** إسناده صحيح','2','284',NULL,NULL,'0');
INSERT INTO [b88] VALUES (785,'<span data-type="title" id=toc-787>٧٨٥ </span>- عن عطاء بن يسار عن رجل من الأنصار: أنه قبل امرأته وهو صائم فأمر امرأته أن تسأل النبى صلى الله عليه وسلم عن ذلك، فسألته، فقال: " إنى أفعل ذلك "، فقال زوجها: يرخص الله لنبيه فيما يشاء، فرجعت فقال: " أنا أعلمكم بحدود الله وأتقاكم ".
** عب
(فتح الباري ١٥١/٤)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٤ / ١٥١: أخرجه مالك، لكنه أرسله قال: عن عطاء أن رجلا.. فذكر نحوه مطولا.','2','285',NULL,NULL,'0');
INSERT INTO [b88] VALUES (786,'<span data-type="title" id=toc-788>٧٨٦ </span>- عن حذيفة: من تأمل خلق امرأته وهو صائم بطل صومه.
** عب
(فتح الباري ١٥١/٤)
** إسناده ضعيف','2','286',NULL,NULL,'0');
INSERT INTO [b88] VALUES (787,'<span data-type="title" id=toc-789>٧٨٧ </span>- قوله صلى الله عليه وسلم للسائل عنها: " أرأيت لو تمضمضت ".
** د ن
(فتح الباري ١٥٢/٤)
** قال الحافظ فى " الفتح " ٤ / ١٥٢: قال النسائى: منكر، وصححه ابن خزيمة وابن حبان والحاكم.','2','287',NULL,NULL,'0');
INSERT INTO [b88] VALUES (788,'<span data-type="title" id=toc-790>٧٨٨ </span>- عن عائشة أن النبى صلى الله عليه وسلم كان يقبلها ويمص لسانها.
** د
(فتح الباري ١٥٣/٤)
** إسناده ضعيف','2','288',NULL,NULL,'0');
INSERT INTO [b88] VALUES (789,'<span data-type="title" id=toc-791>٧٨٩ </span>- عن أبى هريرة: جاء رجل فقال: يارسول الله إنى أكلت وشربت ناسيا وأنا صائم.
** قط
(فتح الباري ١٥٦/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ١٥٦: ولأبى داود من طريق حبيب بن الشهيد وأيوب عن ابن سيرين عن أبى هريرة.','2','289',NULL,NULL,'0');
INSERT INTO [b88] VALUES (790,'<span data-type="title" id=toc-792>٧٩٠ </span>- عن أبى هريرة: فإنما هو رزق ساقة الله إليه ولا قضاء عليه.
** قط
(فتح الباري ١٥٧/٤)
** قال الحافظ فى " الفتح " ٤ / ١٥٧: قال الدارقطنى بعد تخريجه: هذا إسناد صحيح وكلهم ثقات.','2','290',NULL,NULL,'0');
INSERT INTO [b88] VALUES (791,'<span data-type="title" id=toc-793>٧٩١ </span>- حديث أبى سعيد رفعه: " من أكل فى شهر رمضان ناسيا فلا قضاء عليه ".
** قط
(فتح الباري ١٥٧/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ١٥٧: إسناده وإن كان ضعيفا لكنه صالح للمتابعة، فأقل درجات الحديث بهذه الزيادة أن يكون حسنا، فيلح الإحتجاج به.','2','291',NULL,NULL,'0');
INSERT INTO [b88] VALUES (792,'<span data-type="title" id=toc-794>٧٩٢ </span>- من طريق عبد الله بن محمد بن عقيل، عن جابر بلفظ: " مع كل صلاة سواك ".
** أبو نعيم فى السواك
(فتح الباري ١٥٩/٤)
** قال الحافظ فى " الفتح " ٤ / ١٥٩: عبد الله مختلف فيه.','2','292',NULL,NULL,'0');
INSERT INTO [b88] VALUES (793,'<span data-type="title" id=toc-795>٧٩٣ </span>- عن جابر بلفظ: " لجعلت السواك عليهم عزيمة ".
** عد
(فتح الباري ١٥٩/٤)
** إسناده ضعيف','2','293',NULL,NULL,'0');
INSERT INTO [b88] VALUES (794,'<span data-type="title" id=toc-796>٧٩٤ </span>- ويذكر عن أبى هريرة رفعه: " من أفطر يوما فى رمضان من غير علة ولا مرض لم يقضه الدهر وإن صامه ".
** خت د ت ن جه
(فتح الباري ١٦١/٤)
** قال الحافظ فى " الفتح " ٤ / ١٦١: صححه ابن خزيمة من طريق سفيان الثورى وشعبة كلاهما عن حبيب بن أبى ثابت عن عمارة بن عمير عن أبى المكوس عن أبيه عن أبى هريرة نحوه. وقال البخارى فى التاريخ: تفرد به أبو المطوس بهذا الحديث، ولا أدرى سمع أبوه من أبى هريرة أم لا.اه. قلت: واختلف فيه على حبيب بن أبى ثابت اختلافا كثيرا، فحصلت فيه ثلاث علل الإضطراب والجهل بحال أبى المطوس والشك فى سماع أبيه من أبى هريرة، وهذه الثلاثة تختص بطريقة البخارى فى اشتراط اللقاء.','2','294',NULL,NULL,'0');
INSERT INTO [b88] VALUES (795,'<span data-type="title" id=toc-797>٧٩٥ </span>- أن أبا بكر الصديق قال لعمر بن الخطاب فيما أوصاه به: من صام شهر رمضان فى غيره لم يقبل منه ولو صام الدهر أجمع.
** ابن حزم
(فتح الباري ١٦٢/٤)
** منقطع
** قال الحافظ فى " الفتح " ٤ / ١٦٢: إسناد له فيه انقطاع.','2','295',NULL,NULL,'0');
INSERT INTO [b88] VALUES (796,'<span data-type="title" id=toc-798>٧٩٦ </span>- عن سعيد بن المسيب فى هذه القصة مرسلا أنه قال فى جواب قوله: " هل تستطيع أن تصوم، إنى لأدع الطعام ساعة فما أطيق ذلك ".
** قط
(فتح الباري ١٦٦/٤)
** إسناده فيه مقال','2','296',NULL,NULL,'0');
INSERT INTO [b88] VALUES (797,'<span data-type="title" id=toc-799>٧٩٧ </span>- رواية عطاء ومجاهد عن أبى هريرة: أنه أتى بمكتل فيه عشرون صاعا فقال: تصدق بهذا. وقال قبل ذلك: تصدق بعشرين صاعا أو بتسع عشرة أو بإحدى وعشرين.
** طس
(فتح الباري ١٦٩/٤)
** قال الحافظ فى " الفتح " ٤ / ١٦٩: لا حجة فيه لما فيه من الشك، ولأنه من رواية ليث بن أبى سليم وهو ضعيف وقد اضطرب فيه وفى الإسناد إليه مع ذلك من لا يحتج به.','2','297',NULL,NULL,'0');
INSERT INTO [b88] VALUES (798,'<span data-type="title" id=toc-800>٧٩٨ </span>- فى حديث على: وكله أنت وعيالك فقد كفر الله عنك.
** لم
(فتح الباري ١٧٢/٤)
** ضعيف
** قال الحافظ فى " الفتح " ٤ / ١٧٢: لكنه حديث ضعيف لا يحتج بما انفرد به.','2','298',NULL,NULL,'0');
INSERT INTO [b88] VALUES (799,'<span data-type="title" id=toc-801>٧٩٩ </span>- فى مرسل السدى: كتب على النصارى الصيام، وكتب علهيم أن لا يأكلوا ولا يشربوا ولا ينكحوا بعد النوم، وكتب علي المسلمين أولا مثل ذلك حتى أقبل رجل من الأنصار فذكرالقصة. وفي حديث السدى المذكور: حتي أقبل رجل من الأنصار يقال له أبو قيس بن صرمة، وفي مرسل السدى أنه أتاها بتمر فقال: استبدلى به طحينا واجعليه سخينا فإن التمر أحرق جوفى، وفيه لعلي آكله سخينا وأنها استبدلته له وصنعته، وفى مرسل السدى: كان يعمل في حيطان المدينة بالأجرة، وفى مرسل السدى، فأيقظته فكره أن يعصى لله وأبى أن يأكل.
** ابن جرير الطبرى
(فتح الباري ١٣٠/٤)
** مرسل','2','299',NULL,NULL,'0');
INSERT INTO [b88] VALUES (800,'<span data-type="title" id=toc-802>٨٠٠ </span>- في مرسل ابن أبى ليلى: فقال لأهله أطعمونى، فقالت: حتى أجعل لك شيئا سخينا.
** ابن جرير الطبرى
(فتح الباري ١٣١/٤)
** مرسل
** قال الحافظ فى " الفتح " ٤ / ١٣١: ووصله أبو داود من طريق ابن أبى ليلى فقال: حدثنا أصحاب محمد ... فذكره مختصرا، وفى رواية أبى داود: فلم ينتصف النهار حتى غشى عليه.','2','300',NULL,NULL,'0');
INSERT INTO [b88] VALUES (801,'<span data-type="title" id=toc-803>٨٠١ </span>- فى مرسل محمد بن يحيى: فقالت له: كل، فقال: إنى قد نمت، فقالت: لم تنم فأبى فأصبح جائعا مجهودا
** ابن جرير الطبرى
(فتح الباري ١٣١/٤)
** مرسل
** قال الحافظ فى " الفتح " ٤ / ١٣١: ولا بن جرير من طريق ابن إسحق عن محمد بن يحيى بن حبان مرسلا صرمة بن أبى أنس','2','301',NULL,NULL,'0');
INSERT INTO [b88] VALUES (802,'<span data-type="title" id=toc-804>٨٠٢ </span>- عن أبى هريرة _ رفعه _ قال: " من ذرعه القيء وهو صائم فليس عليه القضاء وإن استقاء فليقض ".
** تخ دى د ت ن جه ك
(فتح الباري ١٧٥/٤)
** قال الحافظ فى " الفتح " ٤ / ١٧٥: قال البخارى: لم يصح، وإنما يروى عن عبد الله بن سعيد المقبرى عن أبيه عن أبى هريرة، وعبد الله ضعيف جدا. ورواه الدارمى من طريق عيسى بن يونس، ونقل عن عيسى أنه قال: زعم أهل البصرة أن هشاما وهم فيه. وقال أبو داود: سمعت أحمد يقول: ليس من ذا شىء. ورواه أصحاب السنن الأربعة والحاكم من طريق عيسى بن يونس به، وقال الترمذى: غريب لا نعرفه إلا من رواية عيسى بن يونس عن هشام. وسألت محمدا عنه، فقال: لا أراه محفوظاانتهى. وقد أخرجه ابن ماجه والحاكم من طريق حفص بن غياث أيضا عن خشام قال: وقد روى من غير وجه عن أبى هريرة، ولا يصح إسناده، ولكن العمل عليه عند أهل العلم.','2','302',NULL,NULL,'0');
INSERT INTO [b88] VALUES (803,'<span data-type="title" id=toc-805>٨٠٣ </span>- أن بلالا أتى النبى صلى الله عليه وسلم وهو يتسحر فقال: الصلاة يارسول الله، قد والله أصبحت، فقال: يرحم الله بلالا لولا بلال لرجونا أن يرخص لنا حتى تطلع الشمس.
** عب
(فتح الباري ١٣٥/٤)
** رجاله ثقات','2','303',NULL,NULL,'0');
INSERT INTO [b88] VALUES (804,'<span data-type="title" id=toc-806>٨٠٤ </span>- عن ابن عباس قال: أحل الله لك الأكل والشرب ما شككت.
** عب
(فتح الباري ١٣٥/٤)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٤ / ١٣٥: ولا بن أبى شيبة عن أبى بكر وعمر نحوه.','2','304',NULL,NULL,'0');
INSERT INTO [b88] VALUES (805,'<span data-type="title" id=toc-807>٨٠٥ </span>- عن على: أنه صلى الصبح ثم قال: الآن حين تبين الخيط الأبيض من الخيط الأسود.
** ابن المنذر
(فتح الباري ١٣٦/٤)
** إسناده صحيح','2','305',NULL,NULL,'0');
INSERT INTO [b88] VALUES (806,'<span data-type="title" id=toc-808>٨٠٦ </span>- حديث أبى الدرداء أن النبى صلى الله عليه وسلم قاء فأفطر.
** د ت ن جه
(فتح الباري ١٧٥/٤)
** قال الحافظ فى " الفتح " ٤ / ١٧٥: أخرجه أصحاب السنن مصححا.','2','306',NULL,NULL,'0');
INSERT INTO [b88] VALUES (807,'<span data-type="title" id=toc-809>٨٠٧ </span>- عن سالم بن عبيد الأشجعى _ وله صحبة _ أن أبا بكر قال له: أخرج فانظر هل طلع الفجر، قال: فنظرت فأتيته فقلت: قد أبيض وسطع ثم قال: اخرج فانظر هل طلع، فنظرت فقلت: قد أعترض، فقال: الآن أبلغني شرابى.
** ابن المنذر
(فتح الباري ١٣٧/٤)
** إسناده صحيح','2','307',NULL,NULL,'0');
INSERT INTO [b88] VALUES (808,'<span data-type="title" id=toc-810>٨٠٨ </span>- عن الزهرى: كان ابن عمر يحتجم وهو صائم فى رمضان وغيره ثم تركه لأجل الضعف.
** عب
(فتح الباري ١٧٥/٤)
** منقطع
** قال الحافظ فى " الفتح " ٤ / ١٧٥: هكذا وجدته منقطعا، ووصله عبد الرزاق عن معمر عن الزهرى عن سالم عن أبيه.','2','308',NULL,NULL,'0');
INSERT INTO [b88] VALUES (809,'<span data-type="title" id=toc-811>٨٠٩ </span>- " تسحروا ولو بلقمة ".
** ص
(فتح الباري ١٤٠/٤)
** مرسل','2','309',NULL,NULL,'0');
INSERT INTO [b88] VALUES (810,'<span data-type="title" id=toc-812>٨١٠ </span>- حديث عبد الله بن عمر عن أخته حفصة: أن النبى صلى الله عليه وسلم قال: " من لم يبيت الصيام من الليل فلا صيام له "، _ لفظ النسائى _ ولأبى داود والترمذى " من لم يجمع الصيام قبل الفجر فلا صيام له ".
** د ت ن جه
(فتح الباري ١٤٢/٤)
** قال الحافظ فى " الفتح " ٤ / ١٤٢: اختلف فى رفعه ووقفه، ورجح الترمذى والنسائى الموقوف، بعد أن أطنب النسائى فى تخريج طرقه، وحكى الترمذى في العلل عن البخارى ترجيح وقفه، وعمل بظاهر الإسناد جماعة من الأئمة، فصصحوا الحديث المذكور، منهم ابن خزيمة وابن حبان والحاكم وابن حزم، وروى الدارقطنى طريقا آخر، وقال: رجالها ثقات.','2','310',NULL,NULL,'0');
INSERT INTO [b88] VALUES (811,'<span data-type="title" id=toc-813>٨١١ </span>- من طريق مطر الوراق عن بكر أن أبا رافع قال: دخلت علي أبى موسى وهو يحتجم ليلا، فقلت: ألا كان هذا نهارا؟ ، فقال: أتأمرنى أن أهرق دمى وأنا صائم وقد سمعت رسول الله صلى الله عليه وسلم يقول: " أفطر الحاجم والمحجوم ".
** ن ك
(فتح الباري ١٧٥/٤)
** قال الحافظ فى " الفتح " ٤ / ١٧٦: قال الحاكم: سمعت أبا علىالنيسابورى يقول: قلت لعبدان الأهوازى يصح في " أفطر الحاجم والمحجوم " شىء قال: سمعت عباسا العنبرى يقول: سمعت علي بن المدينى يقول: قد صح حديث أبى رافع عن أبى موسى. قلت: إلا أن مطرا خولف في رفعه فالله أعلم.','2','311',NULL,NULL,'0');
INSERT INTO [b88] VALUES (812,'<span data-type="title" id=toc-814>٨١٢ </span>- عن مسروق سألت عائشة ما يحل للرجل من امرأته صائما؟ قالت: كل شىء إلا الجماع.
** عب
(فتح الباري ١٤٩/٤)
** إسناده صحيح','2','312',NULL,NULL,'0');
INSERT INTO [b88] VALUES (813,'<span data-type="title" id=toc-815>٨١٣ </span>- عن ابن شهاب: أن سعد بن أبى وقاص وعبد الله بن عمر كانا يحتجمان وهما صائمان.
** ما
(فتح الباري ١٧٦/٤)
** قال الحافظ فى " الفتح " ٤ / ١٧٦: هذا منقطع عن سعد لكن ذكره ابن عبد البر من وجه آخر عن عامر بن سعد عن أبيه.','2','313',NULL,NULL,'0');
INSERT INTO [b88] VALUES (814,'<span data-type="title" id=toc-816>٨١٤ </span>- عن دينار قال: حجمت زيد بن أرقم وهو صائم.
** عب
(فتح الباري ١٧٦/٤)
** قال الحافظ فى " الفتح " ٤ / ١٧٦: دينار هو الحجام مولى جرم _ بفتح الجيم _ لا يعرف إلا فى هذا الأثر وقال أبو الفتح الأزدى: لا يصح حديثه.','2','314',NULL,NULL,'0');
INSERT INTO [b88] VALUES (815,'<span data-type="title" id=toc-817>٨١٥ </span>- عن فرات عن مولى أم سلمة أنه رأى أم سلمة تحتجم وهى صائمة.
** شب
(فتح الباري ١٧٦/٤)
** قال الحافظ فى " الفتح " ٤ / ١٧٦: فرات هو ابن عبد الرحمن ثقة لكن مولى أم سلمة مجهول الحال.','2','315',NULL,NULL,'0');
INSERT INTO [b88] VALUES (816,'<span data-type="title" id=toc-818>٨١٦ </span>- حديث شداد لفظه: كنا مع رسول الله صلى الله عليه وسلم فى زمان الفتح، فرأى رجلا يحتجم لثمان عشرة خلت من رمضان، فقال _ وهو آخذ بيدى _: " أفطر الحاجم
والمحجوم ".
** شفع فى اختلاف الحديث
(فتح الباري ١٧٧/٤)
** قال الحافظ فى " الفتح " ٤ / ١٧٧: نقل الترمذى عن البخارى أنه قال: ليس فى هذا الباب أصح من حديث شداد وثوبان، قلت: فكيف بما فيهما من الإختلاف، يعنى عن أبى قلابة قال: كلاهما عندى صحيح لأن يحيى بن أبى كثير روى عن أبى قلابة عن أبى أسماء عن ثوبان وعن أبى قلابة عن أبى الأشعث عن شداد روى الحديثين جميعا، يعنى فانتفى الإضطراب وتعين الجمع بذلك، وكذا قال عثمان الدارمى: يصح حديث أفطر الحاجم والمحجوم من طريق ثوبان وشداد، قال: وسمعت أحمد يذكر ذلك،
وقد المروزى: قلت لأحمد إن يحيى بن معين قال: ليس فيه شىء يثبت، فقال: هذا مجازفة وقال ابن خزيمة: صح الحديثان جميعا وكذا قال ابن حبان والحاكم وأطنب النسائى في تخريج طرق هذا المتن وبيان الإختلاف فيه فأجاد وأفاد.','2','316',NULL,NULL,'0');
INSERT INTO [b88] VALUES (817,'<span data-type="title" id=toc-819>٨١٧ </span>- حديث أبى سعيد: أرخص النبى صلى الله عليه وسلم فى الحجامة للصائم.
** ن خز قط
(فتح الباري ١٧٨/٤)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٤ / ١٧٨: قال ابن حزم: وإسناده صحيح اه. رجاله ثقات، ولكن اختلف فى رفعه ووقفه، وله شاهد من حديث أنس أخرجه أخرجه الدارقطنى.','2','317',NULL,NULL,'0');
INSERT INTO [b88] VALUES (818,'<span data-type="title" id=toc-820>٨١٨ </span>- حديث أنس لفظه: أول ماكرهت الحجامة للصائم أن جعفر بن أبى طالب احتجم وهو صائم، فمر به رسول الله صلى الله عليه وسلم فقال: " أفطر هذا "، ثم رخص النبى صلى الله عليه وسلم بعد فى الحجامة للصائم وكان أنس يحتجم وهو صائم.
** قط
(فتح الباري ١٧٨/٤)
** قال الحافظ فى " الفتح " ٤ / ١٧٨: رواته كلهم من رجال البخارى، إلا أن فى المتن ما ينكر لأن فيه أن ذلك كان فى الفتح، جعفر كان قتل قبل ذلك.','2','318',NULL,NULL,'0');
INSERT INTO [b88] VALUES (819,'<span data-type="title" id=toc-821>٨١٩ </span>- عن عبد الرحمن بن أبى ليلى عن رجل من أصحاب رسول الله صلى الله عليه وسلم قال: نهى النبى عن الحجامة للصائم وعن المواصلة ولم يحرمهما إبقاء على أصحابه.
** عب د
(فتح الباري ١٧٨/٤)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٤ / ١٧٨: والجهالة بالصحابى لا تضر.','2','319',NULL,NULL,'0');
INSERT INTO [b88] VALUES (820,'<span data-type="title" id=toc-822>٨٢٠ </span>- عن ابن عمر قال: قوله تعالي (فمن شهد منكم الشهر فليصمه) نسخها قوله تعالى (ومن كان منكم مريضا أو على سفر) الآية.
** ابن المنذر
(فتح الباري ١٨٠/٤)
** إسناده صحيح','2','320',NULL,NULL,'0');
INSERT INTO [b88] VALUES (821,'<span data-type="title" id=toc-823>٨٢١ </span>- الحديث المشهور: " الصائم فى السفر كالمفطر فى الحضر ".
** جه
(فتح الباري ١٨٤/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ١٨٤: أخرجه ابن ماجه مرفوعا من حديث ابن عمر بسند ضعيف.','2','321',NULL,NULL,'0');
INSERT INTO [b88] VALUES (822,'<span data-type="title" id=toc-824>٨٢٢ </span>- الحديث المشهور: " الصائم فى السفر كالمفطر فى الحضر ".
** طبرى
(فتح الباري ١٨٤/٤)
** قال الحافظ فى " الفتح " ٤ / ١٨٤: أخرجه الطبرى من طريث أبى سلمة عن عائشة مرفوعا، وفيه ابن لهيعة وهو ضعيف.','2','322',NULL,NULL,'0');
INSERT INTO [b88] VALUES (823,'<span data-type="title" id=toc-825>٨٢٣ </span>- الحديث المشهور: " الصائم فى السفر كالمفطر فى الحضر ".
** ن ابن المنذر
(فتح الباري ١٨٤/٤)
** منقطع
** قال الحافظ فى " الفتح " ٤ / ١٨٤: رواه الأثرم من طريث أبى سلمة عن أبيه مرفوعا، والمحفوظ عن أبى سلمة موقوفا كذلك أخرجه النسائى وابن المنذر، ومع وقفه فهو منقطع لأن أبا سلمة لم يسمع من أبيه.','2','323',NULL,NULL,'0');
INSERT INTO [b88] VALUES (824,'<span data-type="title" id=toc-826>٨٢٤ </span>- عن الزهرى: أن ابن عباس وأبا هريرة اختلفا فى قضاء رمضان، فقال أحدهما: يفرق، وقال الآخر: لا يفرق.
** ما
(فتح الباري ١٨٩/٤)
** منقطع
** قال الحافظ فى " الفتح " ٤ / ١٨٩: هكذا أخرجه منقطعا مبهما، ووصله عبد الرزاق معينا عن معمر عن الزهرى عن عبيد الله بن عبد الله بن عباس فيمن عليه قضاء فى رمضان قال: يقضيه مفرقا، قال الله تعالى: (فعدة من أيام أخر) ، وأخرجه الدارقطنى من وجه آخر عن معمر بسنده قال: صمه كيف شئت، ورويناه فى: فوائد أحمد بن شبيب، من روايته عن يونس عن الزهرى بلفظ: لا يضرك كيف قضيتها، إنما هى عدة من أيام أخر فأحصه.','2','324',NULL,NULL,'0');
INSERT INTO [b88] VALUES (825,'<span data-type="title" id=toc-827>٨٢٥ </span>- عن على: أنه نهى عن قضاء رمضان فى عشر ذى الحجة.
** ابن المنذر
(فتح الباري ١٨٩/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ١٨٩: قال ابن المنذر: وروى بإسناد صحيح نحوه عن الحسن والزهرى وليس مع أحد منهم حجة على ذلك.اه. وروى ابن أبى شيبة بإسناد صحيح عن عمر أنه كان يستحب ذلك.','2','325',NULL,NULL,'0');
INSERT INTO [b88] VALUES (826,'<span data-type="title" id=toc-828>٨٢٦ </span>- قال على: لو كان الدين بالرأى لكان باطن الخف أحق بالمسح من أعلاه.
** حم د قط
(فتح الباري ١٩٤/٤)
** رجال إسناده ثقات','2','326',NULL,NULL,'0');
INSERT INTO [b88] VALUES (827,'<span data-type="title" id=toc-829>٨٢٧ </span>- عن عائشة سألت عن امرأة ماتت وعليها صوم، قالت: يطعم عنها.
** لم
(فتح الباري ١٩٤/٤)
** قال الحافظ فى " الفتح " ٤ / ١٩٤: الآثار المذكورة عن عائشة وعن ابن عباس فيها مقال.','2','327',NULL,NULL,'0');
INSERT INTO [b88] VALUES (828,'<span data-type="title" id=toc-830>٨٢٨ </span>- عن عائشة قالت: لا تصوموا عن موتاكم وأطعموا عنهم.
** هق
(فتح الباري ١٩٤/٤)
** ضعيف جدا
** قال الحافظ فى " الفتح " ٤ / ١٩٤: الآثار المذكورة عن عائشة وعن ابن عباس فيها مقال.','2','328',NULL,NULL,'0');
INSERT INTO [b88] VALUES (829,'<span data-type="title" id=toc-831>٨٢٩ </span>- عن ابن عباس قال فى رجل مات وعليه رمضان قال: يطعم عنه ثلاثون مسكينا.
** عب
(فتح الباري ١٩٤/٤)
** قال الحافظ فى " الفتح " ٤ / ١٩٤: الآثار المذكورة عن عائشة وعن ابن عباس فيها مقال.','2','329',NULL,NULL,'0');
INSERT INTO [b88] VALUES (830,'<span data-type="title" id=toc-832>٨٣٠ </span>- عن ابن عباس قال: لا يصوم أحد عن أحد.
** ن
(فتح الباري ١٩٤/٤)
** قال الحافظ فى " الفتح " ٤ / ١٩٤: الآثار المذكورة عن عائشة وعن ابن عباس فيها مقال.','2','330',NULL,NULL,'0');
INSERT INTO [b88] VALUES (831,'<span data-type="title" id=toc-833>٨٣١ </span>- عن أنس مرفوعا: " من وجد تمرا فليفطر عليه ومن لا فليفطر على الماء ".
** ك
(فتح الباري ١٩٨/٤)
** قال الحافظ فى " الفتح " ٤ / ١٩٨: صححه الترمذى وابن حبان من حديث سلمان بن عامر.','2','331',NULL,NULL,'0');
INSERT INTO [b88] VALUES (832,'<span data-type="title" id=toc-834>٨٣٢ </span>- عن عمرو بن ميمون الأودى قال: كان أصحاب محمد صلى الله عليه وسلم أسرع الناس إفطارا وأبطأهم سحورا.
** عب
(فتح الباري ١٩٩/٤)
** إسناده صحيح','2','332',NULL,NULL,'0');
INSERT INTO [b88] VALUES (833,'<span data-type="title" id=toc-835>٨٣٣ </span>- حديث رزينة: أن النبى صلى الله عليه وسلم كان يأمر مرضعاته فى عاشوراء ورضعاء فاطمة فيتفل فى أفواههم ويأمر أمهاتهم أن لا يرضعن إلى الليل.
** خز
(فتح الباري ٢٠١/٤)
** إسناده لابأس به
** قال الحافظ فى " الفتح " ٤ / ٢٠١: أخرجه ابن خزيمة وتوقف فى صحته.','2','333',NULL,NULL,'0');
INSERT INTO [b88] VALUES (834,'<span data-type="title" id=toc-836>٨٣٤ </span>- حديث أبى سعيد الخير مرفوعا: " إن الله لم يكتب الصيام بالليل، فمن صام فقد تعنى ولا أجر له ".
** ت فى العلل سكن الدولابى فى الكنى
(فتح الباري ٢٠٢/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٠٢: قال ابن منده: غريب لا نعرفه إلا من هذا الوجه، وقال الترمذى: سألت البخارى عنه فقال: ما أرى عبادة سمع من أبى سعيد الخير','2','334',NULL,NULL,'0');
INSERT INTO [b88] VALUES (835,'<span data-type="title" id=toc-837>٨٣٥ </span>- عن ليلى امرأة بشير بن الخصاصية قالت: أردت أن أصوم يومين مواصلة فمنعنى بشير وقال: إن النبى صلى الله عليه وسلم نهى عن هذا وقال: " يفعل ذلك النصارى، ولكن صوموا كما أمركم الله تعالى، أتموا الصيام إلى الليل فإذا كان الليل فأفطروا ".
** حم طب ص عبد ابن أبى حاتم
(فتح الباري ٢٠٢/٤)
** إسناده صحيح','2','335',NULL,NULL,'0');
INSERT INTO [b88] VALUES (836,'<span data-type="title" id=toc-838>٨٣٦ </span>- من طريق على بن أبى طلحة عن عبد الملك عن أبى ذر _ رفعه _ قال: " لا صيام بعد الليل ".
** طس
(فتح الباري ٢٠٣/٤)
** لا يصح
** قال الحافظ فى " الفتح " ٤ / ٢٠٣: عبد الملك: ما عرفته، فلا يصح وإن كان بقية رجاله ثقات.','2','336',NULL,NULL,'0');
INSERT INTO [b88] VALUES (837,'<span data-type="title" id=toc-839>٨٣٧ </span>- من طريق عبد الرحمن بن أبى ليلى عن رجل من الصحابة قال: نهى النبى صلى الله عليه وسلم عن الحجامة والمواصلة ولم يحرمهما إبقاء على أصحابه.
** د
(فتح الباري ٢٠٣/٤)
** إسناده صحيح','2','337',NULL,NULL,'0');
INSERT INTO [b88] VALUES (838,'<span data-type="title" id=toc-840>٨٣٨ </span>- أن النبى صلى الله عليه وسلم كان يواصل من سحر إلى سحر.
** حم عب طب ص
(فتح الباري ٢٠٤/٤)
** مرسل','2','338',NULL,NULL,'0');
INSERT INTO [b88] VALUES (839,'<span data-type="title" id=toc-841>٨٣٩ </span>- عن أبى ذر رضى الله عنه، أن جبريل قال للنبى صلى الله عليه وسلم: إن الله قد
قبل وصالك ولا يحل لأحد بعدك.
** طس
(فتح الباري ٢٠٥/٤)
** إسناده غير صحيح','2','339',NULL,NULL,'0');
INSERT INTO [b88] VALUES (840,'<span data-type="title" id=toc-842>٨٤٠ </span>- عن أبى هريرة بلفظ: إياكم والوصال ثلاث مرات.
** شب
(فتح الباري ٢٠٦/٤)
** إسناده صحيح','2','340',NULL,NULL,'0');
INSERT INTO [b88] VALUES (841,'<span data-type="title" id=toc-843>٨٤١ </span>- من مرسل الحسن بلفظ: إنى أبيت عند ربى.
** ص شب
(فتح الباري ٢٠٧/٤)
** مرسل','2','341',NULL,NULL,'0');
INSERT INTO [b88] VALUES (842,'<span data-type="title" id=toc-844>٨٤٢ </span>- حديث أبى سعيد قال: صنعت للنبى صلى الله عليه وسلم طعاما فلما وضع قال رجل:
أنا صائم، فقال رسول الله: دعاك أخوك وتكلف لك، أفطر وصم مكانه إن شئت.
** هق
(فتح الباري ٢١٠/٤)
** إسناده حسن','2','342',NULL,NULL,'0');
INSERT INTO [b88] VALUES (843,'<span data-type="title" id=toc-845>٨٤٣ </span>- من طريق حميد بن هلال: آخى بين سلمان وأبى الدرداء فنزل سلمان الكوفة ونزل
أبو الدرداء الشام.
** سعد
(فتح الباري ٢١٠/٤)
** رجاله ثقات','2','343',NULL,NULL,'0');
INSERT INTO [b88] VALUES (844,'<span data-type="title" id=toc-846>٨٤٤ </span>- عند ابن سعد من وجه مرسل: فقال له أبو الدرداء أتمنعنى أن أصوم لربى وأصلى
لربى.
** سعد
(فتح الباري ٢١١/٤)
** مرسل','2','344',NULL,NULL,'0');
INSERT INTO [b88] VALUES (845,'<span data-type="title" id=toc-847>٨٤٥ </span>- قال: كان أبو الدرداء يحيى ليلة الجمعة ويصوم يومها فأتاه سلمان.
** طب
(فتح الباري ٢١١/٤)
** مرسل','2','345',NULL,NULL,'0');
INSERT INTO [b88] VALUES (846,'<span data-type="title" id=toc-848>٨٤٦ </span>- عن عائشة قالت: كنت أنا وحفصة صائمتين فعرض لنا طعام إشتهيناه فأكلنا منه، فجاء رسول الله صلى الله عليه وسلم فبدرتنى إليه حفصة وكانت ببيت أبيها فقالت
يارسول الله فذكرت ذلك فقال: " أقضيا يوما آخر مكانه ".
** ت ن ما
(فتح الباري ٢١٢/٤)
** مرسل
** قال الحافظ فى " الفتح " ٤ / ٢١٢ توارد الحفاظ على الحكم بضعف حديث عائشة هذا.','2','346',NULL,NULL,'0');
INSERT INTO [b88] VALUES (847,'<span data-type="title" id=toc-849>٨٤٧ </span>- عن عائشة: أنه صلى الله عليه وسلم كان يفطر من صوم التطوع.
** لم
(فتح الباري ٢١٢/٤)
** صحيح','2','347',NULL,NULL,'0');
INSERT INTO [b88] VALUES (848,'<span data-type="title" id=toc-850>٨٤٨ </span>- زاد بعضهم: فأكل ثم قال: لكن أصوم يوما مكانه.
** ن
(فتح الباري ٢١٢/٤)
** قال الحافظ فى " الفتح " ٤ / ٢١٢ ضعفه النسائى.','2','348',NULL,NULL,'0');
INSERT INTO [b88] VALUES (849,'<span data-type="title" id=toc-851>٨٤٩ </span>- عن عائشة: كان رسول الله صلى الله عليه وسلم يصوم ثلاثة أيام من كل شهر، فربما آخر ذلك حتي يجتمع عليه صوم السنة فيصوم شعبان.
** طس
(فتح الباري ٤١٤/٤)
** ضعيف
** قال الحافظ فى " الفتح " ٤ / ٢١٤ ابن أبى ليلى ضعيف.','2','349',NULL,NULL,'0');
INSERT INTO [b88] VALUES (850,'<span data-type="title" id=toc-852>٨٥٠ </span>- عن أنس قال: سئل النبى صلى الله عليه وسلم أى الصوم أفضل بعد رمضان، قال: " شعبان لتعظيم رمضان ".
** ت
(فتح الباري ٢١٤/٤)
** قال الحافظ فى " الفتح " ٤ / ٢١٤: قال الترمذى: حديث غريب، وصدفة عندهم ليس بذاك القوى.','2','350',NULL,NULL,'0');
INSERT INTO [b88] VALUES (851,'<span data-type="title" id=toc-853>٨٥١ </span>- عن أسامة بن زيد قال: قلت يارسول الله لم أرك تصوم من شهر من الشهور ماتصوم من شعبان قال: " ذلك شهر يغفل الناس عنه بين رجب ورمضان وهو شهر ترفع فيه
الأعمال إلى رب العالمين فأحب أن يرفع عملى وأنا صائم ".
** ن د
(فتح الباري ٢١٥/٤)
** قال الحافظ فى " الفتح " ٤ / ٢١٥: صححه ابن خزيمة.','2','351',NULL,NULL,'0');
INSERT INTO [b88] VALUES (852,'<span data-type="title" id=toc-854>٨٥٢ </span>- حديث: نية المؤمن خير من عمله.
** مسند الشهاب
(فتح الباري ٢١٩/٤)
** ضعيف','2','352',NULL,NULL,'0');
INSERT INTO [b88] VALUES (853,'<span data-type="title" id=toc-855>٨٥٣ </span>- عن ابن عمر والشيبانى قال: بلغ عمر أن رجلا يصوم الدهر، فأتاه فعلاه بالدرة وجعل يقول: كل يادهرى.
** شب
(فتح الباري ٢٢٢/٤)
** إسناده صحيح','2','353',NULL,NULL,'0');
INSERT INTO [b88] VALUES (854,'<span data-type="title" id=toc-856>٨٥٤ </span>- عن ابن مسعود: قيل له إنك لتقل الصيام، فقال: إنى أخاف أن يضعفنى عن القراءة والقراءة أحب إلى من الصيام.
** ص
(فتح الباري ٢٢٣/٤)
** إسناده صحيح','2','354',NULL,NULL,'0');
INSERT INTO [b88] VALUES (855,'<span data-type="title" id=toc-857>٨٥٥ </span>- عن أبى هريرة قال: جاء أعرابى إلى النبى صلى الله عليه وسلم بأرنب قد شواها، فأمرهم أن يأكلوا وأمسك الأعرابى، فقال: " مامنعك أن تأكل "، فقال: إنى أصوم ثلاثة أيام من كل شهر، قال: " إن كنت صائما فصم الغر أى البيض ".
** حم ن
(فتح الباري ٢٢٦/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٢٦: صححه ابن حبان، وهذا الحديث اختلف فيه على موسى بن طلحة اختلافا كثيرا.','2','355',NULL,NULL,'0');
INSERT INTO [b88] VALUES (856,'<span data-type="title" id=toc-858>٨٥٦ </span>- حديث جرير مرفوعا: " صيام ثلاثة أيام من كل شهر: صايم الدهر أيام البيض صبيحة ثلاث عشرة ".
** ن
(فتح الباري ٢٢٦/٤)
** إسناده صحيح','2','356',NULL,NULL,'0');
INSERT INTO [b88] VALUES (857,'<span data-type="title" id=toc-859>٨٥٧ </span>- حديث ابن مسعود: أن النبى صلى الله عليه وسلم كان يصوم ثلاثة أيام من غرة كل شهر.
** د ت ن جه
(فتح الباري ٢٢٦/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٢٦: رواه أصحاب السنن وصححه ابن خزيمة.','2','357',NULL,NULL,'0');
INSERT INTO [b88] VALUES (858,'<span data-type="title" id=toc-860>٨٥٨ </span>- عن أنس قال: اللهم أكثر ماله وولده وأطل عمره واغفر ذنبه.
** سعد
(فتح الباري ٢٢٩/٤)
** إسناده صحيح','2','358',NULL,NULL,'0');
INSERT INTO [b88] VALUES (859,'<span data-type="title" id=toc-861>٨٥٩ </span>- عن عبد الله بن عمرو بن العاص أن النبى صلى الله عليه وسلم دخل على جويريه.... فذكره.
** ن
(فتح الباري ٢٣٤/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٣٤: صححه ابن حبان.','2','359',NULL,NULL,'0');
INSERT INTO [b88] VALUES (860,'<span data-type="title" id=toc-862>٨٦٠ </span>- حديث ابن مسعود: كان رسول الله صلى الله عليه وسلم يصوم من كل شهر ثلاثة أيام، وقلما كان يفطر يوم الجمعة.
** لم
(فتح الباري ٢٣٤/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٣٤: حسنه الترمذى.','2','360',NULL,NULL,'0');
INSERT INTO [b88] VALUES (861,'<span data-type="title" id=toc-863>٨٦١ </span>- حديث أم سلمة: أن النبى صلى الله عليه وسلم كان يصوم من الأيام السبت والأحد وكان يقول: " إنهما يوما عيد المشركين فأحب أن أخالفهم ".
** د ن
(فتح الباري ٢٣٥/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٣٥: صححه ابن حبان.','2','361',NULL,NULL,'0');
INSERT INTO [b88] VALUES (862,'<span data-type="title" id=toc-864>٨٦٢ </span>- عن على قال: من كان منكم متطوعا من الشهر فليصم يوم الخميس، ولا يصم يوم الجمعة فإنه يوم طعام وشراب وذكر.
** شب
(فتح الباري ٢٣٥/٤)
** إسناده حسن','2','362',NULL,NULL,'0');
INSERT INTO [b88] VALUES (863,'<span data-type="title" id=toc-865>٨٦٣ </span>- حديث عائشة: أن النبى صلى الله عليه وسلم كان يتحرى صيام الإثنين والخميس.
** د ت ن
(فتح الباري ٢٣٦/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٣٦: صححه ابن حبان.','2','363',NULL,NULL,'0');
INSERT INTO [b88] VALUES (864,'<span data-type="title" id=toc-866>٨٦٤ </span>- حديث أسامة: رأيت رسول الله صلى الله عليه وسلم يصوم يوم الإثنين والخميس فسألته فقال: " إن الأعمال تعرض يوم الإثنين والخميس فأحب أن يرفع عملى وأنا صائم ".
** ن د
(فتح الباري ٢٣٦/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٣٦: صححه ابن خزيمة.','2','364',NULL,NULL,'0');
INSERT INTO [b88] VALUES (865,'<span data-type="title" id=toc-867>٨٦٥ </span>- من طريق عكرمة أن أبا هريرة حدثهم: أن رسول الله صلى الله عليه وسلم نهى عن صوم يوم عرفة بعرفة.
** د ن
(فتح الباري ٢٣٨/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٣٨: صححه ابن خزيمة والحاكم.','2','365',NULL,NULL,'0');
INSERT INTO [b88] VALUES (866,'<span data-type="title" id=toc-868>٨٦٦ </span>- حديث عمرو بن العاص أنه قال لابنه عبد الله في أيام التشريق: إنها الأيام التى نهى رسول الله صلى الله عليه وسلم عن صومهن وأمر بفطرهن.
** د ابن المنذر
(فتح الباري ٢٤٢/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٤٢: صححه ابن خزيمة والحاكم.','2','366',NULL,NULL,'0');
INSERT INTO [b88] VALUES (867,'<span data-type="title" id=toc-869>٨٦٧ </span>- رخص رسول الله صلى الله عليه وسلم للمتمتع إذا لم يجد الهدى أن يصوم أيام التشريق.
** طحا
(فتح الباري ٢٤٣/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٤٣: قال: إن يحيى بن سلام ليس بالقوى.','2','367',NULL,NULL,'0');
INSERT INTO [b88] VALUES (868,'<span data-type="title" id=toc-870>٨٦٨ </span>- عن أبى هريرة: خرج رسول الله صلى الله عليه وسلم وإذا الناس فى رمضان يصلون فى ناحية المسجد فقال: " ماهذا؟ "، فقيل: ناس يصلى بهم أبى بن كعب، فقال: " أصابوا ونعم ماصنعوا ".
** ابن وهب
(فتح الباري ٢٥٢/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٥٢: فيه مسلم بن خالد وهو ضعيف، والمحفوظ أن عمر هو الذى جمع الناس على أبى بن كعب.','2','368',NULL,NULL,'0');
INSERT INTO [b88] VALUES (869,'<span data-type="title" id=toc-871>٨٦٩ </span>- عن الزهرى: حتى جمعهم عمر على أبى بن كعب فقام بهم فى رمضان، فكان ذلك أول إجتماع الناس على قارىء واحد فى رمضان.
** سحق
(فتح الباري ٢٥٢/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٥٢: جزم الذهلى فى (علل حديث الزهرى) بأنه وهم عن عبد الله بن الحارث والمحفوظ عن رواية مالك ومن تابعه.','2','369',NULL,NULL,'0');
INSERT INTO [b88] VALUES (870,'<span data-type="title" id=toc-872>٨٧٠ </span>- حديث ابن عباس: كان رسول الله صلى الله عليه وسلم يصلى فى رمضان عشرين ركعة والوتر.
** شب
(فتح الباري ٢٥٤/٤)
** إسناده ضعيف','2','370',NULL,NULL,'0');
INSERT INTO [b88] VALUES (871,'<span data-type="title" id=toc-873>٨٧١ </span>- عن معاوية قال: ليلة القدر ليلة ثلاث وعشرين.
** شب
(فتح الباري ٢٦٤/٤)
** إسناده صحيح','2','371',NULL,NULL,'0');
INSERT INTO [b88] VALUES (872,'<span data-type="title" id=toc-874>٨٧٢ </span>- حديث أبى سعيد أن جبريل قال للنبى صلى الله عليه وسلم لما أعتكف العشر الأوسط: إن الذى تطلب أمامك.
** لم
(فتح الباري ٢٦٥/٤)
** صحيح','2','372',NULL,NULL,'0');
INSERT INTO [b88] VALUES (873,'<span data-type="title" id=toc-875>٨٧٣ </span>- عن أبى العالية: أن أعرابيا أتى النبى صلى الله عليه وسلم وهو يصلى فقال له: متى ليلة القدر؟ ، فقال: " أطلبوها فى أول ليلة وآخر ليلة والوتر من الليل ".
** مد
(فتح الباري ٢٦٦/٤)
** مرسل','2','373',NULL,NULL,'0');
INSERT INTO [b88] VALUES (874,'<span data-type="title" id=toc-876>٨٧٤ </span>- من مرسل سعيد بن المسيب أنه صلى الله عليه وسلم قال: " ألا أخبركم بليلة القدر؟ "، قالوا: بلى، فسكت ساعة ثم قال: " لقد قلت لكم وأنا أعلمها ثم أنسيتها ".
** عب
(فتح الباري ٢٦٨/٤)
** مرسل','2','374',NULL,NULL,'0');
INSERT INTO [b88] VALUES (875,'<span data-type="title" id=toc-877>٨٧٥ </span>- عن على أن النبى صلى الله عليه وسلم كان يوقظ أهله فى العشر الأخير من
رمضان.
** حم شب يع
(فتح الباري ٢٧٠/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٧٠: قال الترمذى: حسن صحيح.','2','375',NULL,NULL,'0');
INSERT INTO [b88] VALUES (876,'<span data-type="title" id=toc-878>٨٧٦ </span>- عن ابن عمر أن النبى صلى الله عليه وسلم قال له: " اعتكف وصم ".
** د ن
(فتح الباري ٢٧٤/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ٢٧٤: من طريق عبد الله بن بديل وهو ضعيف.','2','376',NULL,NULL,'0');
INSERT INTO [b88] VALUES (877,'<span data-type="title" id=toc-879>٨٧٧ </span>- حديث أبى بن كعب أن النبى صلى الله عليه وسلم كان يعتكف العشر الأواخر من رمضان فسافر عاما فلم يعتكف، فلما كان العام المقبل اعتكف عشرين.
** ن د
(فتح الباري ٢٨٥/٤)
** قال الحافظ فى " الفتح " ٤ / ٢٨٥: صححه ابن حبان وغيره.','2','377',NULL,NULL,'0');
INSERT INTO [b88] VALUES (878,'<span data-type="title" id=toc-880>٨٧٨ </span>- من مرسل أبى قلابة أن النبى صلى الله عليه وسلم قال: " لا يتفرق بيعان إلا عن رضا ".
** الطبرى
(فتح الباري ٢٨٩/٤)
** رجاله ثقات','2','378',NULL,NULL,'0');
INSERT INTO [b88] VALUES (879,'<span data-type="title" id=toc-881>٨٧٩ </span>- عن عائشة قالت: لما مرض أبو بكر مرضه الذى مات فيه قال: انظروا مازاد فى مالى منذ دخلت الإمارة فابعثوا به إلى الخليفة بعدى، قالت: فلما مات
نظرنا فإذا عبد نوبى كان يحمل صبيانه وناضح كان يسقى بستانا له، فبعثنا بهما إلى عمر فقال: رحمة الله على أبى بكر لقد أتعب من بعده.
** سعد ابن المنذر
(فتح الباري ٣٠٤/٤)
** إسناده صحيح','2','379',NULL,NULL,'0');
INSERT INTO [b88] VALUES (880,'<span data-type="title" id=toc-882>٨٨٠ </span>- لما استخلف أبو بكر أصبح غاديا إلى السوق على رأسه أثواب يتجر بها فلقيه عمر
بن الخطاب وأبو عبيدة بن الجراح فقال: كيف تصنع هذا وقد وليت أمر المسلمين قال: فمن أين أطعم عيالى قالوا: نفرض لك، ففرضوا له كل يوم شطر شاة.
** سعد
(فتح الباري ٣٠٥/٤)
** رجاله ثقات مرسلا','2','380',NULL,NULL,'0');
INSERT INTO [b88] VALUES (881,'<span data-type="title" id=toc-883>٨٨١ </span>- عن ابن مس٤عود: لعن رسول الله صلى الله عليه وسلم آكل الربا وموكله وشاهده وكاتبه.
** د ت ن جه
(فتح الباري ٣١٤/٤)
** قال الحافظ فى " الفتح " ٤ / ٣١٤: لأصحاب السنن وصححه ابن خزيمة.','2','381',NULL,NULL,'0');
INSERT INTO [b88] VALUES (882,'<span data-type="title" id=toc-884>٨٨٢ </span>- حديث ابن مسعود مرفوعا: " إن الربا وإن أكثر عاقبته إلى قل ".
** جه حم
(فتح الباري ٣١٥/٤)
** إسناده حسن','2','382',NULL,NULL,'0');
INSERT INTO [b88] VALUES (883,'<span data-type="title" id=toc-885>٨٨٣ </span>- حديث: أكذب الناس الصباغون والصواغون.
** حم
(فتح الباري ٣١٧/٤)
** مضطرب الإسناد','2','383',NULL,NULL,'0');
INSERT INTO [b88] VALUES (884,'<span data-type="title" id=toc-886>٨٨٤ </span>- حديث عن عمر أنه كلم فى البيع فقال: ما أجد لكم شيئا أوسع مما جعل رسول الله صلى الله عليه وسلم لحبان بن منقذ ثلاثة أيام.
** لم
(فتح الباري ٣٣٨/٤)
** قال الحافظ فى " الفتح " ٤ / مداره على ابن لهيعة وهو ضعيف.','2','384',NULL,NULL,'0');
INSERT INTO [b88] VALUES (885,'<span data-type="title" id=toc-887>٨٨٥ </span>- حديث جبير بن مطعم أن النبى صلى الله عليه وسلم قال: " أحب البقاع إلى الله المساجد، وأبغض البقاع إلى الله الأسواق ".
** حم بز حب ك
(فتح الباري ٣٣٩/٤)
** إسناده حسن
** قال الحافظ فى " الفتح " ٤ / ٣٣٩: صححه الحاكم.','2','385',NULL,NULL,'0');
INSERT INTO [b88] VALUES (886,'<span data-type="title" id=toc-888>٨٨٦ </span>- يذكر عن عثمان أن النبى صلى الله عليه وسلم قال له: " إذا بعت فكل وإذا ابتعت فاكتل ".
** قط حم جه بز
(فتح الباري ٣٤٥/٤)
** قال الحافظ فى " الفتح " ٤ / ٣٤٥: فيه ابن لهيعة ولكنه من قديم حديثه، وصله الدارقطنى وفيه منقذ مولى ابن سراقة عن عثمان ومنقذ مجهول الحال.','2','386',NULL,NULL,'0');
INSERT INTO [b88] VALUES (887,'<span data-type="title" id=toc-889>٨٨٧ </span>- حديث عمر مرفوعا: " من أحتكر على المسلمين طعامهم ضربه الله بالجذام والإفلاس.
** جه
(فتح الباري ٣٤٨/٤)
** إسناده حسن','2','387',NULL,NULL,'0');
INSERT INTO [b88] VALUES (888,'<span data-type="title" id=toc-890>٨٨٨ </span>- عن عمر بن الخطاب مرفوعا قال: " الجالب مرزوق والمحتكر ملعون ".
** جه ك
(فتح الباري ٣٤٨/٤)
** إسناده ضعيف','2','388',NULL,NULL,'0');
INSERT INTO [b88] VALUES (889,'<span data-type="title" id=toc-891>٨٨٩ </span>- عن عمر مرفوعا: " من أحتكر طعاما أربعين ليلة فقد برىء من الله وبرىء منه ".
** حم ك
(فتح الباري ٣٤٨/٤)
** فى إسناده مقال','2','389',NULL,NULL,'0');
INSERT INTO [b88] VALUES (890,'<span data-type="title" id=toc-892>٨٩٠ </span>- حديث زيد بن ثابت: نهى رسول الله صلى الله عليه وسلم أن تباع السلع حيث تبتاع حتى يحوزها التجار إلى رحالهم.
** د
(فتح الباري ٣٥٠/٤)
** قال الحافظ فى " الفتح " ٤ / ٣٥٠: صححه ابن حبان.','2','390',NULL,NULL,'0');
INSERT INTO [b88] VALUES (891,'<span data-type="title" id=toc-893>٨٩١ </span>- حديث أنس: أنه صلى الله عليه وسلم باع حلسا وقدحا وقال: " من يشترى هذا
الحلس والقدح؟ " فقال رجل: أخذتهما بدرهم، فقال: من يزيد على درهم، فأعطاه رجل درهمين فباعهما منه.
** حم د ن ت جه
(فتح الباري ٣٥٤/٤)
** قال الحافظ فى " الفتح " ٤ / ٣٥٤: قال الترمذى: حسن.','2','391',NULL,NULL,'0');
INSERT INTO [b88] VALUES (892,'<span data-type="title" id=toc-894>٨٩٢ </span>- عن سفيان بن وهب: سمعت النبى صلى الله عليه وسلم ينهى عن بيع المزايدة.
** بز
(فتح الباري ٣٥٤/٤)
** قال الحافظ فى " الفتح " ٤ / ٣٥٤: فى إسناده ابن لهيعة وهو ضعيف.','2','392',NULL,NULL,'0');
INSERT INTO [b88] VALUES (893,'<span data-type="title" id=toc-895>٨٩٣ </span>- حديث قيس بن عبادة قال: لولا أنى سمعت رسول الله صلى الله عليه وسلم يقول: " المكر والخديعة فى النار لكنت من أمكر الناس ".
** عد
(فتح الباري ٣٥٦/٤)
** إسناده لا بأس به
** قال الحافظ فى " الفتح " ٤ / ٣٥٦: أخرجه الطبرانى فى الصغير، والحاكم فى المستدرك وإسحق بن راهوية وفى إسناد كل منهما مقال.','2','393',NULL,NULL,'0');
INSERT INTO [b88] VALUES (894,'<span data-type="title" id=toc-896>٨٩٤ </span>- فى أول رواية ليث: " لا تبيعوا المصراة من الإبل والغنم " الحديث.
** قط
(فتح الباري ٣٦٣/٤)
** قال الحافظ فى " الفتح " ٤ / ٣٦٣: ليث ضعيف وفى محمد بن مسلم أيضا لين.','2','394',NULL,NULL,'0');
INSERT INTO [b88] VALUES (895,'<span data-type="title" id=toc-897>٨٩٥ </span>- " فإن ردها رد معها صاعا من طعام أو صاعا من تمر ".
** حم
(فتح الباري ٣٦٤/٤)
** إسناده صحيح','2','395',NULL,NULL,'0');
INSERT INTO [b88] VALUES (896,'<span data-type="title" id=toc-898>٨٩٦ </span>- حديث ابن عمر بلفظ: " إن ردها رد معها مثل أو مثلى لبنها قمحا ".
** د
(فتح الباري ٣٦٤/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ٣٦٤: قل ابن قدامة: إنه متروك الظاهر بالأتفاق.','2','396',NULL,NULL,'0');
INSERT INTO [b88] VALUES (897,'<span data-type="title" id=toc-899>٨٩٧ </span>- عن ابن مسعود مرفوعا: " بيع المحفلات خلابة ولا تحل الخلابة لمسلم ".
** حم جه
(فتح الباري ٣٦٧/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ٣٦٧: رواه ابن أبى شيبة وعبد الرزاق موقوفا بإسناد صحيح.','2','397',NULL,NULL,'0');
INSERT INTO [b88] VALUES (898,'<span data-type="title" id=toc-900>٨٩٨ </span>- حديث أبى هريرة: أن صاحب السلعة إذا باعها لمن تلقاه يصير بالخيار إذا دخل السوق.
** لم
(فتح الباري ٣٧٤/٤)
** إسناده صحيح','2','398',NULL,NULL,'0');
INSERT INTO [b88] VALUES (899,'<span data-type="title" id=toc-901>٨٩٩ </span>- عن أبى هريرة: أن النبى صلى الله عليه وسلم نهى عن تلقى الجلب، فإن تلقاه فاشتراه وصاحبه بالخيار إذا أتى السوق.
** د ت
(فتح الباري ٣٧٤/٤)
** قال الحافظ فى " الفتح " ٤ / ٣٧٤: صححه ابن خزيمة من طريق أيوب.','2','399',NULL,NULL,'0');
INSERT INTO [b88] VALUES (900,'<span data-type="title" id=toc-902>٩٠٠ </span>- حديث سعد بن أبى وقاص: أن النبى صلى الله عليه وسلم سئل عن بيع الرطب بالتمر، فقال: " أينقص الرطب إذا جف؟ "، قالوا: نعم، قال: " فلا إذا ".
** ما د ت ن جه
(فتح الباري ٣٨٥/٤)
** قال الحافظ فى " الفتح " ٤ / ٣٨٥: صححه الترمذى وابن خزيمة وابن حبان والحاكم.','2','400',NULL,NULL,'0');
INSERT INTO [b88] VALUES (901,'<span data-type="title" id=toc-903>٩٠١ </span>- حديث عبد الله بن عمرو: أن النبى صلى الله عليه وسلم أخمره أن يجهز جيشا _ وفيه _ فابتاع البعير بالبعيرين بأمر رسول الله صلى الله عليه وسلم.
** قط
(فتح الباري ٤١٩/٤)
** إسناده قوى','2','401',NULL,NULL,'0');
INSERT INTO [b88] VALUES (902,'<span data-type="title" id=toc-904>٩٠٢ </span>- عن جابر أنه صلى الله عليه وسلم قال: " لا بأس ببيع خدمة المدبر ".
** قط
(فتح الباري ٤٢٣/٤)
** رجال إسناده ثقات
** قال الحافظ فى " الفتح " ٤ / ٤٢٣: اختلف فى وصله وإرساله.','2','402',NULL,NULL,'0');
INSERT INTO [b88] VALUES (903,'<span data-type="title" id=toc-905>٩٠٣ </span>- عن أنس فى النهى عن أكل الحمر الأهلية: إن الله ورسوله ينهيانكم.
** لم
(فتح الباري ٤٢٥/٤)
** صحيح
** قال الحافظ فى " الفتح " ٤ / ٤٢٥: وقع فى رواية النسائى فى هذا الحديث " ينهاكم ".','2','403',NULL,NULL,'0');
INSERT INTO [b88] VALUES (904,'<span data-type="title" id=toc-906>٩٠٤ </span>- حديث ابن عباس مرفوعا: نهى رسول الله صلى الله عليه وسلم عن ثمن الكلب وقال: " إن جاء يطلب ثمن الكلب فاملأ كفه ترابا ".
** د
(فتح الباري ٤٢٦/٤)
** إسناده صحيح','2','404',NULL,NULL,'0');
INSERT INTO [b88] VALUES (905,'<span data-type="title" id=toc-907>٩٠٥ </span>- عن أبى هريرة مرفوعا: " لا يحل ثمن الكلب ولا حلوان الكاهن ولا مهر البغى ".
** د
(فتح الباري ٤٢٦/٤)
** إسناده حسن','2','405',NULL,NULL,'0');
INSERT INTO [b88] VALUES (906,'<span data-type="title" id=toc-908>٩٠٦ </span>- حديث جابر قال: نهى رسول الله صلى الله عليه وسلم عن ثمن الكلب إلا كلب صيد.
** ن
(فتح الباري ٤٢٧/٤)
** إسناده رجاله ثقات
** قال الحافظ فى " الفتح " ٤ / ٤٢٧: أخرجه النسائى بإسناد رجاله ثقات إلا أنه طعن فى صحته.','2','406',NULL,NULL,'0');
INSERT INTO [b88] VALUES (907,'<span data-type="title" id=toc-909>٩٠٧ </span>- حديث ابن عمر: نهى عن ثمن الكلب وإن كان ضاريا.
** ابن أبى حاتم
(فتح الباري ٤٢٧/٤)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٤ / ٤٢٧: قال أبو حاتم: هو منكر.','2','407',NULL,NULL,'0');
INSERT INTO [b88] VALUES (908,'<span data-type="title" id=toc-910>٩٠٨ </span>- حديث ابن عمر رفعه: " من أسلف فى شىء فلا يشترط على صاحبه غير قضائه ".
** قط
(فتح الباري ٤٣٤/٤)
** إسناده ضعيف','2','408',NULL,NULL,'0');
INSERT INTO [b88] VALUES (909,'<span data-type="title" id=toc-911>٩٠٩ </span>- عن ابن عباس قال: أشهد أن السلف المضمون إلى أجل مسمى قد أحله الله فى كتابه وأذن فيه ثم قرأ " يا أيها الذين آمنوا إذا تداينتم بدين إلى أجل مسمى فاكتبوه ".
** ك
(فتح الباري ٤٣٧/٤)
** قال الحافظ فى " الفتح " ٤ / ٤٣٥: صححه الحاكم.','2','409',NULL,NULL,'0');
INSERT INTO [b88] VALUES (910,'<span data-type="title" id=toc-912>٩١٠ </span>- حديث عائشة: أن النبى صلى الله عليه وسلم بعث إلى يهودى ابعث لى ثوبين إلى الميسرة.
** ن
(فتح الباري ٤٣٥/٤)
** قال الحافظ فى " الفتح " ٤ / ٤٣٥: طعن ابن المنذر فى صحته بما وهم فيه.','2','410',NULL,NULL,'0');
INSERT INTO [b88] VALUES (911,'<span data-type="title" id=toc-913>٩١١ </span>- حديث ابن عباس مرفوعا: " الشفعة فى كل شىء ".
** هق
(فتح الباري ٤٣٦/٤)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٤ / ٤٣٦: رجاله ثقات إلا أنه أعل بالإرسال.','2','411',NULL,NULL,'0');
INSERT INTO [b88] VALUES (912,'<span data-type="title" id=toc-914>٩١٢ </span>- عن عمر بن الخطاب وزاد فيه: " فزوجه وأقام موسى معه يكفيه ويعمل له فى رعاية عمله ".
** هق
(فتح الباري ٤٤٠/٤)
** إسناده صحيح','2','412',NULL,NULL,'0');
INSERT INTO [b88] VALUES (913,'<span data-type="title" id=toc-915>٩١٣ </span>- من طريق كثير بن عبد الله بن عمرو بن عوف عن أبيه عن جده مرفوعا بلفظه وزاد: " إلا شرطا حرم حلالا أو أحل حراما ".
** سحق
(فتح الباري ٤٥١/٤)
** قال الحافظ فى " الفتح " ٤ / ٤٥١: وكثير بن عبد الله ضعيف عند الأكثر لكن البخارى ومن تبعه كالترمذى وابن خزيمة يقوون أمره.','2','413',NULL,NULL,'0');
INSERT INTO [b88] VALUES (914,'<span data-type="title" id=toc-916>٩١٤ </span>- " كل لحم أنبته السحت فالنار أولى به " قيل يارسول الله وما السحت؟ ، قال: " الرشوة فى الحكم ".
** ابن جرير
(فتح الباري ٤٥٤/٤)
** رجاله ثقات مرسلا','2','414',NULL,NULL,'0');
INSERT INTO [b88] VALUES (915,'<span data-type="title" id=toc-917>٩١٥ </span>- حديثق محيصة أنه سأل النبى صلى الله عليه وسلم عن كسب الحجام فنهاه فذكر له الحاجة فقال: " أعلفه نواضحك ".
** ما حم د ت ن جه
(فتح الباري ٤٥٩/٤)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٤ / ٤٥٩: أخرجه مالك وأحمد وأصحاب السنن.','2','415',NULL,NULL,'0');
INSERT INTO [b88] VALUES (916,'<span data-type="title" id=toc-918>٩١٦ </span>- حديث عثمان أنه قال فى الحوالة أو الكفالة: يرجع صاحبها لاتوى أى لا هلاك على مسلم.
** لم
(فتح الباري ٤٦٤/٤)
** قال الحافظ فى " الفتح " ٤ / ٤٦٤: قال: فسأله عن إسناده فذكره عن رجل مجهول عن آخر معروف لكنه منقطع بينه وبين عثمان فبطل الإحتجاج به من أوجه.','2','416',NULL,NULL,'0');
INSERT INTO [b88] VALUES (917,'<span data-type="title" id=toc-919>٩١٧ </span>- عن عبد الله بن عمرو بن العاص يرفعه أن رجلا جاء إلى النجاشى، فقال له: أسلفنى ألف دينار إلى أجل فقال: من الحميل بك؟ قال: الله، فأعطاه الألف فضرب بها الرجل _ أى سافر بها _ فى تجارة فلما بلغ الأجل أراد الخروج إليه فحبسته الريح فعمل تابوتا.... فذكر الحديث.
** مسند الصحابة الذين نزلوا مصر محمد بن الربيع الجيزى
(فتح الباري ٤٧١/٤)
** قال الحافظ فى " الفتح " ٤ / ٤٧١: إسناد فيه مجهول.','2','417',NULL,NULL,'0');
INSERT INTO [b88] VALUES (918,'<span data-type="title" id=toc-920>٩١٨ </span>- عن ابن عباس رفعه: " ما كان من حلف فى الجاهلية لم يزده الإسلام إلا شدة وحدة ".
** عمر بن شبة حم
(فتح الباري ٤٧٣/٤)
** قال الحافظ فى " الفتح " ٤ / ٤٧٣: صححه ابن حبان.','2','418',NULL,NULL,'0');
INSERT INTO [b88] VALUES (919,'<span data-type="title" id=toc-921>٩١٩ </span>- عن عدى بن ثابت قال: أرادت الأوس أن تحالف سلمان فقال رسول الله مثل حديث قيس بن عاصم.
** عمر بن شبة
(فتح الباري ٤٧٣/٤)
** مرسل','2','419',NULL,NULL,'0');
INSERT INTO [b88] VALUES (920,'<span data-type="title" id=toc-922>٩٢٠ </span>- الشعبى رفعه: " لا حلف فى الإسلام وحلف الجاهلية مشدود ".
** لم
(فتح الباري ٤٧٣/٤)
** مرسل','2','420',NULL,NULL,'0');
INSERT INTO [b88] VALUES (921,'<span data-type="title" id=toc-923>٩٢١ </span>- من مرسل طلحة بن عوف: " ولو دعيت به اليوم فى الإسلام لأجبت ".
** لم
(فتح الباري ٤٧٣/٤)
** مرسل','2','421',NULL,NULL,'0');
INSERT INTO [b88] VALUES (922,'<span data-type="title" id=toc-924>٩٢٢ </span>- حديث ابن عباس: أن النبى صلى الله عليه وسلم لما امتنع عن الصلاة على من عليه دين جاءه جبريل فقال " إنما الظالم فى الديون التى حملت فى البغى والإسراف، فإما المتعفف ذو العيال فأنا ضامن له أؤدى عنه فصلى عليه النبى صلى الله عليه وسلم وقال بعد ذلك من ترك ضياعا ... الحديث.
** الحازمى
(فتح الباري ٤٧٨/٤)
** ضعيف','2','422',NULL,NULL,'0');
INSERT INTO [b88] VALUES (923,'<span data-type="title" id=toc-925>٩٢٣ </span>- من طريق موسى بن أنس عن أبيه: أن عمر أعطاه آنية مموهة بالذهب فقال له: أذهب فبعها، فباعها من يهودى بضعف وزنه، فقال له عمر: أردده، فقال له اليهودى: أزيدك، فقال له عمر: لا إلا بوزنه.
** ص
(فتح الباري ٤٨١/٤)
** إسناده صحيح','2','423',NULL,NULL,'0');
INSERT INTO [b88] VALUES (924,'<span data-type="title" id=toc-926>٩٢٤ </span>- من طريق الحسن بن سعد قال: كانت لى عند ابن عمر دراهم فأصبت عنده دنانير فأرسل معى رسولا إلى السوق فقال: إذا قامت على سعر فأعرضها عليه فإن أخذها وإلا فاشتر له حقه، ثم أقضه إياه.
** ص
(فتح الباري ٤٨١/٤)
** إسناده صحيح','2','424',NULL,NULL,'0');
INSERT INTO [b88] VALUES (925,'<span data-type="title" id=toc-927>٩٢٥ </span>- عن عبد الله بن الزبير: أنه كان يواصل خمسة عشر يوما.
** شب
(فتح الباري ٢٠٤/٤)
** إسناده صحيح','2','425',NULL,NULL,'0');
INSERT INTO [b88] VALUES (926,'<span data-type="title" id=toc-928>٩٢٦ </span>- **
(فتح الباري /)
**','2','426',NULL,NULL,'0');
INSERT INTO [b88] VALUES (927,'<span data-type="title" id=toc-929>٩٢٧ </span>- حديث أبى هريرة مرفوعا: " لا يقل أحدكم زرعت ولكن ليقل حرثت، ألم تسمع لقول الله تعالى: {أأنتم تزرعونه أم نحن الزارعون} .
** ابن أبى حاتم
(فتح الباري ٤/٥)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٥ / ٤: رجاله ثقات إلا أن مسلم ابن أبى مسلم الجرمى قال فيه ابن حبان ربما أخطأ.','2','427',NULL,NULL,'0');
INSERT INTO [b88] VALUES (928,'<span data-type="title" id=toc-930>٩٢٨ </span>- عن يحيى بن سعيد أن عمر أجلى أهل نجران واليهود والنصارى ولا شترى بياض أرضهم وكرويهم فعامل عمر الناس إن هم جاءوا بالبقر والحديد من عندهم فلهم الثلثان ولعمر الثلث، وإن جاء عمر بالبذر من عنده فله الشطر، وعاملهم فى النخل على أن لهم الخمس وله الباقى وعاملهم فى الكرم على أن لهم الثلث وله الثلثان.
** شب
(فتح الباري ١٢/٥)
** مرسل','2','428',NULL,NULL,'0');
INSERT INTO [b88] VALUES (929,'<span data-type="title" id=toc-931>٩٢٩ </span>- عن عمر بن عبد العزيز: لما استخلف عمر أجلى أهل نجران وأهل فدك وتيماء وأهل خيبر، واشترى عقارهم وأموالهم، واستعمل يعلى بن منية إلى اليمن فأعطى البياض _ يعنى بياض الأرض _ على أن كان البذر والبقر والحديد من عمر فلهم الثلث ولعمر الثلثان، وإن كان منهم فلهم الشطر وله الشطر وأعطى النخل والعنب على أن لعمر الثلثين ولهم الثلث.
** هق
(فتح الباري ١٢/٥)
** مرسل','2','429',NULL,NULL,'0');
INSERT INTO [b88] VALUES (930,'<span data-type="title" id=toc-932>٩٣٠ </span>- عن كثير بن عبد الله بن عمرو بن عوف حدثنى أبى عن أباه حدثه أنه سمع النبى صلى الله عليه وسلم يقول: " من أحياأرضا مواتا من غير أن يكون فيها حق مسلم فهى له، وليس لعرق ظالم حق ".
** سحق
(فتح الباري ١٩/٥)
** قال الحافظ فى " الفتح " ٥ / ١٩: كثير هذا ضعيف.','2','430',NULL,NULL,'0');
INSERT INTO [b88] VALUES (931,'<span data-type="title" id=toc-933>٩٣١ </span>- قال عروة: لقد خبرنى الذى حدثنى بهذا الحديث، أن رجلين اختصما إلى النبى صلى الله عليه وسلم غرس أحدهما نخلا فى أرض الآخر فقضى لصاحب الأرض بأرضه وأمر صاحب النخل أن يخرج نخله منها.
** د
(فتح الباري ١٩/٥)
** مرسل','2','431',NULL,NULL,'0');
INSERT INTO [b88] VALUES (932,'<span data-type="title" id=toc-934>٩٣٢ </span>- عن هشام بلفظ: " من أحيا أرضا ميتة فهى له ".
** ت
(فتح الباري ١٩/٥)
** قال الحافظ فى " الفتح " ٥ / ١٩: صححه الترمذى.','2','432',NULL,NULL,'0');
INSERT INTO [b88] VALUES (933,'<span data-type="title" id=toc-935>٩٣٣ </span>- عن سعد بن أبى وقاص قال: كان أصحاب المزارع يكرونها بما يكون على المساقى من الزرع، فاختصموا فى ذلك، فنهاهم رسول الله صلى الله عليه وسلم أن يكروا بذلك وقال: " أكروا بالذهب والفضة ".
** د
(فتح الباري ٢٥/٥)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٥ / ٢٥: رجاله ثقات إلا أن محمد بن عكرمة المخزومى لم يرو عنه إلا إبراهيم بن سعد.','2','433',NULL,NULL,'0');
INSERT INTO [b88] VALUES (934,'<span data-type="title" id=toc-936>٩٣٤ </span>- عن سعيد بن جبير عنه ولفظه: أن أمثل ما أنتم صانعون أن تستأجروا الأرض البيضاء فيها شجر.
** هق الثورى فى جامعه
(فتح الباري ٢٦/٥)
** إسناده صحيح','2','434',NULL,NULL,'0');
INSERT INTO [b88] VALUES (935,'<span data-type="title" id=toc-937>٩٣٥ </span>- عن رافع بن خديج: نهى رسول الله صلى الله عليه وسلم عن المحافلة والمزابنة وقال: " إنما يزرع ثلاثة: رجل له أرض، ورجل منح أرضا، ورجل أكترى أرضا بذهب أو فضة ".
** د ن
(فتح الباري ٢٦/٥)
** إسناده صحيح','2','435',NULL,NULL,'0');
INSERT INTO [b88] VALUES (936,'<span data-type="title" id=toc-938>٩٣٦ </span>- عن أبى هريرة: قلت: يارسول الله أخبرنى عن كل شىء قال: " كل شىء خلق من الماء ".
** ابن أبى حاتم
(فتح الباري ٢٩/٥)
** إسناده صحيح','2','436',NULL,NULL,'0');
INSERT INTO [b88] VALUES (937,'<span data-type="title" id=toc-939>٩٣٧ </span>- عن أبى هريرة بلفظ: لا تمنعوا فضل الماء ولا تمنعوا فضل الكلأ فينهزك المال وتجوع العيال.
** لم
(فتح الباري ٣٢/٥)
** قال الحافظ فى " الفتح " ٥ / ٣٢: صححه ابن حبان.','2','437',NULL,NULL,'0');
INSERT INTO [b88] VALUES (938,'<span data-type="title" id=toc-940>٩٣٨ </span>- عن أبى هريرة مرفوعا: " ثلاثة لا يمنعن: الماء والكلأ والنار ".
** جه
(فتح الباري ٣٢/٥)
** إسناده صحيح','2','438',NULL,NULL,'0');
INSERT INTO [b88] VALUES (939,'<span data-type="title" id=toc-941>٩٣٩ </span>- عن سعيد بن المسيب فى قوله تعالى: {فلا وربك لا يؤمنون حتى يحكموك فيما شجر بينهم} الآية، قال: نزلت فى الزبير بن العوام وحاطب بن أبى بلتعة أختصما فى ماء.... الحديث.
** ابن أبى حاتم
(فتح الباري ٣٥/٥)
** إسناده قوى','2','439',NULL,NULL,'0');
INSERT INTO [b88] VALUES (940,'<span data-type="title" id=toc-942>٩٤٠ </span>- أن الزبير وحاطبا لما خرجا مرا بالمقداد قال: لمن كان القضاء؟ فقال حاطب: قضى لابن عمته، ولوى شدقه فقص له يهودى فقال: قاتل الله هؤلاء يشهدون أنه رسول الله ويتهمونه.
** الثعلبى
(فتح الباري ٣٦/٥)
** قال الحافظ فى " الفتح " ٥ / ٣٦: ذكره الثعلبى بغير سند، وفى صحة هذا نظر.','3','165',NULL,NULL,'0');
INSERT INTO [b88] VALUES (941,'<span data-type="title" id=toc-943>٩٤١ </span>- عن الشعبى قال: كان بين رجل من اليهود ورجل من المنافقين خصومة، فدعا اليهودى المنافق إلى النبى صلى الله عليه وسلم لأنه علم أنه لا يقبل الرشوة ودعا المنافق اليهودى إلى حكامهم لأنه علم أنهم يأخذونها فانزل الله هذه الآيات إلى قوله: (ويسلموا تسليما) .
** سحق
(فتح الباري ٣٧/٥)
** إسناده صحيح','3','166',NULL,NULL,'0');
INSERT INTO [b88] VALUES (942,'<span data-type="title" id=toc-944>٩٤٢ </span>- عن ابن عباس: إن حاكم اليهود يومئذ كان أبا برزة الأسلمى قبل أن يسلم ويصحب.
** الطبرى
(فتح الباري ٣٧/٥)
** إسناده صحيح','3','167',NULL,NULL,'0');
INSERT INTO [b88] VALUES (943,'<span data-type="title" id=toc-945>٩٤٣ </span>- عن سعيد بن المسيب فى هذه القصة: فقضى رسول الله صلى الله عليه وسلم أن يسقى الأعلى ثم الأسفل.
** لم
(فتح الباري ٣٨/٥)
** مرسل','3','168',NULL,NULL,'0');
INSERT INTO [b88] VALUES (944,'<span data-type="title" id=toc-946>٩٤٤ </span>- عن عبد الله بن أبى بكر: أن رسول الله صلى الله عليه وسلم قضى فى مسيل مهزور ومذينب أن يمسك حتى يبلغ الكعبين، ثم يرسل الأعلى على الأسفل.
** ما
(فتح الباري ٤٠/٥)
** مرسل
** قال الحافظ فى " الفتح " ٥ / ٤٠: له إسناد موصول من حديث عائشة وصححه الحاكم، وأخرجه أبو داود وابن ماجه والطبرى من حديث عمرو بن شعيب عن أبيه عن جده وإسناد كل منهما حسن وأخرج عبد الرزاق هذا الحديث المرسل بإسناد آخر موصول.','3','169',NULL,NULL,'0');
INSERT INTO [b88] VALUES (945,'<span data-type="title" id=toc-947>٩٤٥ </span>- حديث ابن عمر أن النبى صلى الله عليه وسلم حمى النقيع لخيل المسلمين ترعى فيه.
** هق
(فتح الباري ٤٥/٥)
** قال الحافظ فى " الفتح " ٥ / ٤٥: فى إسناده العمرى وهو ضعيف.','3','170',NULL,NULL,'0');
INSERT INTO [b88] VALUES (946,'<span data-type="title" id=toc-948>٩٤٦ </span>- عن ابن عمر أن عمر حمى الربذة لنعم الصدقة.
** شب
(فتح الباري ٤٥/٥)
** إسناده صحيح','3','171',NULL,NULL,'0');
INSERT INTO [b88] VALUES (947,'<span data-type="title" id=toc-949>٩٤٧ </span>- أن النبى صلى الله عليه وسلم لما قدم المدينة أقطع الدور.
** شفع طب
(فتح الباري ٤٨/٥)
** مرسل','3','172',NULL,NULL,'0');
INSERT INTO [b88] VALUES (948,'<span data-type="title" id=toc-950>٩٤٨ </span>- عن ابن عباس مرفوعا: " لا أشترى ما ليس عندى ثمنه ".
** د ك
(فتح الباري ٥٣/٥)
** قال الحافظ فى " الفتح " ٥ / ٥٣: تفرد به شريك عن سماك واختلف فى وصله وإرساله.','3','173',NULL,NULL,'0');
INSERT INTO [b88] VALUES (949,'<span data-type="title" id=toc-951>٩٤٩ </span>- عن عبد الله بن جعفر أنه كان يستدين، فسئل فقال: سمعت رسول الله صلى الله
عليه وسلم يقول: " إن الله مع الدائن حتى يقضى دينه ".
** جه ك
(فتح الباري ٥٤/٥)
** إسناده حسن','3','174',NULL,NULL,'0');
INSERT INTO [b88] VALUES (950,'<span data-type="title" id=toc-952>٩٥٠ </span>- ويذكر عن النبى صلى الله عليه وسلم: لى الواجد يحل عقوبته وعرضه.
** حم سحق د ن
(فتح الباري ٦٢/٥)
** إسناده حسن','3','175',NULL,NULL,'0');
INSERT INTO [b88] VALUES (951,'<span data-type="title" id=toc-953>٩٥١ </span>- عن سعيد: أفلس مولى لأم حبيبة فاختصم فيه إلى عثمان فقضى فذكره.
** هق
(فتح الباري ٦٣/٥)
** إسناده صحيح','3','176',NULL,NULL,'0');
INSERT INTO [b88] VALUES (952,'<span data-type="title" id=toc-954>٩٥٢ </span>- عن أبى بكر بن عبد الرحمن بن الحارث مرسلا: " أيما رجل باع متاعا فأفلس الذى ابتاعه ولم يقبض البائع من ثمنه شيئا فوجده بعينه فهو أحق به ".
** ما
(فتح الباري ٦٣/٥)
** مرسل','3','177',NULL,NULL,'0');
INSERT INTO [b88] VALUES (953,'<span data-type="title" id=toc-955>٩٥٣ </span>- فى مرسل ابن أبى مليكة: من باع سلعة من رجل لم ينقده ثم أفلس الرجل فوجدها بعينها فليأخذها من بين الغرماء.
** عب
(فتح الباري ٦٤/٥)
** مرسل','3','178',NULL,NULL,'0');
INSERT INTO [b88] VALUES (954,'<span data-type="title" id=toc-956>٩٥٤ </span>- فى مرسل مالك: وإن مات الذى ابتاعه فصاحب المتاع فيه أسوة الغرماء.
** مالك
(فتح الباري ٦٤/٥)
** مرسل','3','179',NULL,NULL,'0');
INSERT INTO [b88] VALUES (955,'<span data-type="title" id=toc-957>٩٥٥ </span>- عن أبى هريرة: قضى رسول الله صلى الله عليه وسلم أيما رجل مات أو أفلس فصاحب المتاع أحق بمتاعه إذا وجده بعينه.
** حم د جه
(فتح الباري ٦٤/٥)
** حسن
** قال الحافظ فى " الفتح " ٥ / ٦٤: صححه الحاكم.','3','180',NULL,NULL,'0');
INSERT INTO [b88] VALUES (956,'<span data-type="title" id=toc-958>٩٥٦ </span>- أن ابن عمر استسلف من رجل دراهم فقضاه خيرا منها.
** ما
(فتح الباري ٦٦/٥)
** إسناده صحيح','3','181',NULL,NULL,'0');
INSERT INTO [b88] VALUES (957,'<span data-type="title" id=toc-959>٩٥٧ </span>- حديث جابر فى قصة الرجل الذى جاء ببيضة من ذهب أصابها فى معدن فقال يارسول الله خذها منى صدقة فوالله مالى مال غيرها فأعرض عنه، فأعاد فحذفه بها، ثم قال: " يأتى أحدكم بماله لا يملك غيره فيتصدق به ثم يقعد بعد ذلك يتكفف الناس إنما الصدقة عن ظهر غنى ".
** د
(فتح الباري ٧٢/٥)
** قال الحافظ فى " الفتح " ٥ / ٧٢: صححه ابن خزيمة.','3','182',NULL,NULL,'0');
INSERT INTO [b88] VALUES (958,'<span data-type="title" id=toc-960>٩٥٨ </span>- عن سعيد بن المسيب قال: لما توفى أبو بكر أقامت عائشة عليه النوح فبلغ عمر
فنهاهن فابين، فقال لهشام بن الوليد: اخرج إلى بيت أبى قحافة _ يعنى أم مروة _ فعلاها بالدرة ضربات فتفرق النوائح حين سمعن بذلك.
** سعد
(فتح الباري ٧٤/٥)
** إسناده صحيح','3','183',NULL,NULL,'0');
INSERT INTO [b88] VALUES (959,'<span data-type="title" id=toc-961>٩٥٩ </span>- من طريق مالك بن عمير عن أبيه: أنه سأل رسول الله صلى الله عليه وسلم عن اللقطة فقال: " إن وجدت من يعرفها فادفعها إليه ... " الحديث.
** الأسماعيلى فى الصحابة
(فتح الباري ٨١/٥)
** إسناده واه جدا','3','184',NULL,NULL,'0');
INSERT INTO [b88] VALUES (960,'<span data-type="title" id=toc-962>٩٦٠ </span>- حديث جابر قال: رخص لنا رسول الله صلى الله عليه وسلم فى العصا والسوط والحبل وأشباهه يلتقطه الرجل ينتفع به.
** د
(فتح الباري ٨٥/٥)
** إسناده ضعيف','3','185',NULL,NULL,'0');
INSERT INTO [b88] VALUES (961,'<span data-type="title" id=toc-963>٩٦١ </span>- عن سمرة مرفوعا: " إذا أتى أحدكم على ماشية فإن لم يكن صاحبها فيها فليصوت ثلاثا فإن أجاب فليستأذنه فإن أذن له وإلا فليحلب وليشرب ولا يحمل.
** د ت
(فتح الباري ٨٩/٥)
** قال الحافظ فى " الفتح " ٥ / ٨٩: صححه الترمذى وإسناده صحيح إلى الحسن فمن صحح سماعه عن سمرة صححه ومن لا أعله بالإنقطاع.','3','186',NULL,NULL,'0');
INSERT INTO [b88] VALUES (962,'<span data-type="title" id=toc-964>٩٦٢ </span>- حديث أبى سعيد مرفوعا: " إذا أتيت على راع فناده ثلاثا فإن أجابك وإلا فاشرب من غير أن تفسد، وإذا أتيت على حائط بستان.... " فذكر مثله.
** جه طحا
(فتح الباري ٨٩/٥)
** قال الحافظ فى " الفتح " ٥ / ٨٩: صححه ابن حبان والحاكم.','3','187',NULL,NULL,'0');
INSERT INTO [b88] VALUES (963,'<span data-type="title" id=toc-965>٩٦٣ </span>- حديث ابن عمر مرفوعا: إذا مر أحدكم بحائط فليأكل ولا يتخذ خبيئة.
** ت
(فتح الباري ٩٠/٥)
** قال الحافظ فى " الفتح " ٥ / ٩٠: أخرجه الترمذى واستغربه، قال البيهقى: لم يصح، وجاء من أوجه غير قوية. قلت: والحق أن مجموعها لا يقصر عن درجة الصحيح.','3','188',NULL,NULL,'0');
INSERT INTO [b88] VALUES (964,'<span data-type="title" id=toc-966>٩٦٤ </span>- حديث الجارود مرفوعا: " ضالة المسلم حرق النار ".
** ن
(فتح الباري ٩٢/٥)
** إسناده صحيح','3','189',NULL,NULL,'0');
INSERT INTO [b88] VALUES (965,'<span data-type="title" id=toc-967>٩٦٥ </span>- عن عائشة قالت: دخلت على زينب بنت جحش فسبتنى، فردعها النبى صلى الله عليه
وسلم فأبت، فقال لى: " سبيها "، فسببتها حتى جف ريقها من فمها فرأيت وجهه يتهلل.
** ن جه
(فتح الباري ٩٩/٥)
** إسناده حسن','3','190',NULL,NULL,'0');
INSERT INTO [b88] VALUES (966,'<span data-type="title" id=toc-968>٩٦٦ </span>- عن الحكم بن الحارث السلمى مرفوعا: " من أخذ من طريق المسلمين شبرا جاء يوم القيامة يحمله من سبع أرضين ".
** يع
(فتح الباري ١٠٤/٥)
** إسناده حسن','3','191',NULL,NULL,'0');
INSERT INTO [b88] VALUES (967,'<span data-type="title" id=toc-969>٩٦٧ </span>- حديث أبى مالك الأشعرى: ما أعظم الغلول عند الله يوم القيامة ذراع أرض يسرقه رجل فيطوقه من سبع أرضين.
** شب
(فتح الباري ١٠٥/٥)
** إسناده حسن','3','192',NULL,NULL,'0');
INSERT INTO [b88] VALUES (968,'<span data-type="title" id=toc-970>٩٦٨ </span>- أن الضحاك بن خليفة سأل محمد بن مسلمة أن يسوق خليجا له فيمر به فى أرض محمد بن مسلمة، فامتنع فكلمه عمر فى ذلك فأبى، فقال: والله ليمرن به ولو على بطنك.
** شفع ما
(فتح الباري ١١١/٥)
** إسناده صحيح','3','193',NULL,NULL,'0');
INSERT INTO [b88] VALUES (969,'<span data-type="title" id=toc-971>٩٦٩ </span>- عن ابن عباس عن النبى صلى الله عليه وسلم: " إذا اختلفتم فى الطريق الميتاء فاجعلوها سبعة أذرع ".
** عب
(فتح الباري ١١٩/٥)
** فى إسناده مقال','3','194',NULL,NULL,'0');
INSERT INTO [b88] VALUES (970,'<span data-type="title" id=toc-972>٩٧٠ </span>- حديث عبادة بن الصامت قضى رسول الله صلى الله عليه وسلم فى الطريق الميتاء.... فذكر الحديث.
** عم
(فتح الباري ١١٩/٥)
** فى إسناده مقال','3','195',NULL,NULL,'0');
INSERT INTO [b88] VALUES (971,'<span data-type="title" id=toc-973>٩٧١ </span>- حديث أنس: قضى رسول الله صلى الله عليه وسلم فى الطريق الميتاء التى تؤتى من كل مكان ... فذكره.
** عد
(فتح الباري ١١٩/٥)
** فى إسناده مقال','3','196',NULL,NULL,'0');
INSERT INTO [b88] VALUES (972,'<span data-type="title" id=toc-974>٩٧٢ </span>- عن عائشة قالت: كان رسول الله صلى الله عليه وسلم مع أصحابه فصنعت له طعاما وصنعت له حفصة طعاما فسبقتنى، فقلت للجارية انطلقى فأكفئى قصعتها فأكفأتها فانكسرت وانتشر الطعام، فجمعه على القطع فأكلوا ثم بعث بقصعتى إلى حفصة فقال " خذوا ظرفا مكان ظرفكم ".
** شب جه
(فتح الباري ١٢٥/٥)
** قال الحافظ فى " الفتح " ٥ / ١٢٥: من طريق رجل من بنى سوادة غير مسمى وبقية رجاله ثقات.','3','197',NULL,NULL,'0');
INSERT INTO [b88] VALUES (973,'<span data-type="title" id=toc-975>٩٧٣ </span>- عن عائشة قالت: ما رأيت صانعة طعاما مثل صفية أهدت إلى النبى صلى الله عليه
وسلم إناء فيه طعام فما ملكت نفسى أن كسرته، فقلت: يارسول الله ماكفارته؟ قال: " إناء كإناء وطعام كطعام ".
** د ن
(فتح الباري ١٢٥/٥)
** إسناده حسن','3','198',NULL,NULL,'0');
INSERT INTO [b88] VALUES (974,'<span data-type="title" id=toc-976>٩٧٤ </span>- حديث أبى جويرة: نفس المؤمن معلقة بدينه حتى يقضى.
** لم
(فتح الباري ١٤٢/٥)
** قال الحافظ فى " الفتح " ٥ / ١٤٢: صححه ابن حبان وغيره.','3','199',NULL,NULL,'0');
INSERT INTO [b88] VALUES (975,'<span data-type="title" id=toc-977>٩٧٥ </span>- عن الشعبى مرسلا: أن أبا بكر أفتك الدرع وسلمها لعلى بن أبى طالب.
** سحق
(فتح الباري ١٤٢/٥)
** مرسل','3','200',NULL,NULL,'0');
INSERT INTO [b88] VALUES (976,'<span data-type="title" id=toc-978>٩٧٦ </span>- عن أبى هريرة مرفوعا: " الرهن مركوب ومحلوب ".
** ك
(فتح الباري ١٤٣/٥)
** قال الحافظ فى " الفتح " ٥ / ١٤٣: صححه الحاكم.','3','201',NULL,NULL,'0');
INSERT INTO [b88] VALUES (977,'<span data-type="title" id=toc-979>٩٧٧ </span>- حديث البراء بن عازب قال: قال رسول الله صلى الله عليه وسلم: " أعتق النسمة وفك الرقبة "، قيل: يارسول الله أليستا واحدة؟ قال: " لا إن عتق النسمة أن تفرد يعتقها، وفك الرقبة أن تعين فى عتقها ".
** حم حب ك
(فتح الباري ١٤٦/٥)
** صحيح','3','202',NULL,NULL,'0');
INSERT INTO [b88] VALUES (978,'<span data-type="title" id=toc-980>٩٧٨ </span>- حديث كعب بن مرة: وإيما امرىء مسلم أعتق أمرأتين مسلمتين كانتا فكاكه من النار عظيمين منهما بعظم وأيما امرأة مسلمة أعتقت امرأة مسلمة كانت فكاكها من النار.
** ن
(فتح الباري ١٤٧/٥)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٥ / ١٤٧: ومثله للترمذى من حديث أبى أمامة وللطبرانى من حديث عبد الرحمن بن عوف ورجاله ثقات.','3','203',NULL,NULL,'0');
INSERT INTO [b88] VALUES (979,'<span data-type="title" id=toc-981>٩٧٩ </span>- حديث أبى المليح عن أبيه: أن رجلا أعتق شقصا من غلام فذكر ذلك للنبى صلى الله عليه وسلم ليس لله شريك، وفى رواية: فأجاز عتقه.
** د ن
(فتح الباري ١٥٩/٥)
** إسناده قوى','3','204',NULL,NULL,'0');
INSERT INTO [b88] VALUES (980,'<span data-type="title" id=toc-982>٩٨٠ </span>- حديث سمرة: أن رجلا أعتق شقصا له فى مملوك، فقال النبى صلى الله عليه وسلم: " هو كله فليس لله شريك ".
** حم
(فتح الباري ١٥٩/٥)
** إسناده حسن','3','205',NULL,NULL,'0');
INSERT INTO [b88] VALUES (981,'<span data-type="title" id=toc-983>٩٨١ </span>- من طريق ملقام بن التلب عن أبيه: أن رجلا أعتق نصيبه من مملوك فلم يضمنه النبى صلى الله عليه وسلم.
** د
(فتح الباري ١٥٩/٥)
** إسناده حسن','3','206',NULL,NULL,'0');
INSERT INTO [b88] VALUES (982,'<span data-type="title" id=toc-984>٩٨٢ </span>- عن أبى قلابة عن رجل من بنى عذرة: أن رجلا منهم أعتق مملوكا له عنر موته وليس له مال غيره فأعتق رسول الله صلى الله عليه وسلم ثلثه وأمره أن يسعى فى الثلثين.
** عب
(فتح الباري ١٥٩/٥)
** رجاله ثقات','3','207',NULL,NULL,'0');
INSERT INTO [b88] VALUES (983,'<span data-type="title" id=toc-985>٩٨٣ </span>- قوله صلى الله عليه وسلم " دخلت الجنة فسمعت فيها نحمة من نعيم ".
** لم
(فتح الباري ١٦٦/٥)
** قال الحافظ فى " الفتح " ٥ / ١٦٦: من رواية الواقدى وهو ضعيف.','3','208',NULL,NULL,'0');
INSERT INTO [b88] VALUES (984,'<span data-type="title" id=toc-986>٩٨٤ </span>- من طريق ابن عيينه عنه بلفظ: أن رجلا من الأنصار دبر غلاما له فمات ولم يترك مالا غيره.
** ت
(فتح الباري ١٦٦/٥)
** قال الحافظ فى " الفتح " ٥ / ١٦٦: قد أعله الشافعى بأنه سمعه عن ابن عيينه مرارا لم يذكر قوله فمات وكذلك رواه
الأئمة أحمد وإسحق وابن المدينى والحميدى وابن أبى شيبة عن ابن عيينه.','3','209',NULL,NULL,'0');
INSERT INTO [b88] VALUES (985,'<span data-type="title" id=toc-987>٩٨٥ </span>- عن ابن عمر رفعه: " من أعتق عبدا فمال العبد له، إلا أن يستثنيه سيده.
** د ت ن جه
(فتح الباري ١٧١/٥)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٥ / ١٧١: أخرجه أصحاب السنن.','3','210',NULL,NULL,'0');
INSERT INTO [b88] VALUES (986,'<span data-type="title" id=toc-988>٩٨٦ </span>- عن أبى قتادة أن عمرو بن الجموح أتى رسول الله صلى الله عليه وسلم فقال: أرأيت أن قاتلت حتى أقتل فى سبيل الله ترانى أمشى برجلى هذه صحيحة فى الجنة فقال: " نعم "، وكانت عرجاء، زاد عمر: فقتل يوم أحد رحمه الله.
** حم عمر بن شبة فى أخبار المدينة
(فتح الباري ١٧٨/٥)
** إسناده حسن','3','211',NULL,NULL,'0');
INSERT INTO [b88] VALUES (987,'<span data-type="title" id=toc-989>٩٨٧ </span>- حديث كعب بن مالك أن النبى صلى الله عليه وسلم قال: " من سيدكم يابنى سلمة؟ "، قالوا: جد بن قيس ... فذكر الحديث، فقال: " سيدكم بشر بن البراء بن بعرور ".
** ابن منده شيخ فى الأمثال الوليد بن أبان فى كتاب الجود
(فتح الباري ١٧٩/٥)
** قال الحافظ فى " الفتح " ٥ / ١٧٩: رجال هذا الإسناد ثقات إلا أنه اختلف فى وصله وإرساله عن الزهرى.','3','212',NULL,NULL,'0');
INSERT INTO [b88] VALUES (988,'<span data-type="title" id=toc-990>٩٨٨ </span>- عن أبى هريرة قال: من قاتل فليجتنب الوجه فإن صورة وجه الإنسان على صورة وجه الرحمن.
** ابن أبى عاصم فى السنة طب
(فتح الباري ١٨٣/٥)
** رجاله ثقات','3','213',NULL,NULL,'0');
INSERT INTO [b88] VALUES (989,'<span data-type="title" id=toc-991>٩٨٩ </span>- عن عمرو بن شعيب عن أبيه عن جده مرفوعا: المكاتب عبد مابقى عليه درهم.
** د ن
(فتح الباري ١٩٥/٥)
** قال الحافظ فى " الفتح " ٥ / ١٩٥: صححه الحاكم.','3','214',NULL,NULL,'0');
INSERT INTO [b88] VALUES (990,'<span data-type="title" id=toc-992>٩٩٠ </span>- عن ابن عباس مرفوعا: " المكاتب يعتق منه بقدر ماأدى ".
** ن
(فتح الباري ١٩٥/٥)
** رجاله إسناده ثقات
** قال الحافظ فى " الفتح " ٥ / ١٩٥: اختلف فى وصله وإرساله.','3','215',NULL,NULL,'0');
INSERT INTO [b88] VALUES (991,'<span data-type="title" id=toc-993>٩٩١ </span>- من مرسل على بن الحسين: فذهبت زينب حتى أستأذنت فقال: " أئذنوا لها "، فقالت: حسبك إذا برقت لك بنت أبى قحافة ذراعيها.
** لم
(فتح الباري ٢٠٧/٥)
** مرسل','3','216',NULL,NULL,'0');
INSERT INTO [b88] VALUES (992,'<span data-type="title" id=toc-994>٩٩٢ </span>- حديث ابن عمر مرفوعا: " ثلاث لا ترد: الوسائد والدهن واللبن ".
** ت
(فتح الباري ٢٠٩/٥)
** إسناده حسن
** قال الحافظ فى " الفتح " ٥ / ٢٠٩: ليس على شرط البخارى.','3','217',NULL,NULL,'0');
INSERT INTO [b88] VALUES (993,'<span data-type="title" id=toc-995>٩٩٣ </span>- عن أبى هريرة مرفوعا: " من عرض عليه طيب فلا يرده فإنه خفيف الحمل طيب الرائحة ".
** د ن عو
(فتح الباري ٢٠٩/٥)
** صحيح','3','218',NULL,NULL,'0');
INSERT INTO [b88] VALUES (994,'<span data-type="title" id=toc-996>٩٩٤ </span>- أنت ومالك لأبيك.
** جه
(فتح الباري ٢١١/٥)
** قال الحافظ فى " الفتح " ٥ / ٢١١: قال الدارقطنى: غريب تفرد به عيسى بن يونس ابن أبى إسحق، ويوسف بن إسحق بن أبى إسحق عن ابن المنكدر قال ابن القطان: إسناده صحيح، وقال المنذرى: رجاله ثقات.','3','219',NULL,NULL,'0');
INSERT INTO [b88] VALUES (995,'<span data-type="title" id=toc-997>٩٩٥ </span>- حديث: لا يحل لرجل يعطى عطية أو يهب هبة فيرجع فيها إلا الوالد فيما يعطى ولده.
** د جه
(فتح الباري ٢١١/٥)
** رجاله ثقات','3','220',NULL,NULL,'0');
INSERT INTO [b88] VALUES (996,'<span data-type="title" id=toc-998>٩٩٦ </span>- من طريق طاوس مرسلا: " لا أشهد إلا على الحق لا أشهد بهذه ".
** عب
(فتح الباري ٢١٣/٥)
** مرسل','3','221',NULL,NULL,'0');
INSERT INTO [b88] VALUES (997,'<span data-type="title" id=toc-999>٩٩٧ </span>- حديث ابن عباس رفعه: " سووا بين أولادكم فى العطية فلو كنت مفضلا أحدا لفضلت النساء ".
** ص هق
(فتح الباري ٢١٤/٥)
** إسناده حسن','3','222',NULL,NULL,'0');
INSERT INTO [b88] VALUES (998,'<span data-type="title" id=toc-1000>٩٩٨ </span>- عن عائشة أن أبا بكر قال لها فى مرض موته: إنى كنت نحلتك نحلا فلو كنت أخترتيه لكان لك، وإنما هو اليوم للوارث.
** ما
(فتح الباري ٢١٥/٥)
** إسناده صحيح','3','223',NULL,NULL,'0');
INSERT INTO [b88] VALUES (999,'<span data-type="title" id=toc-1001>٩٩٩ </span>- عن عمر أنه كتب: إن النساء يعطين رغبة ورهبة فأيما امرأة أعطت زوجها فشاءت أن ترجع رجعت.
** عب
(فتح الباري ٢١٧/٥)
** إسناده منقطع','3','224',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1000,'<span data-type="title" id=toc-1002><span data-type="title" id=toc-1003>١٠٠٠ </span></span>- حديث أبى حميد مرفوعا: " هدايا العمال غلول ".
** حم طب
(فتح الباري ٢٢١/٥)
** قال الحافظ فى " الفتح " ٥ / ٢٢١: فى إسناده إسماعيل بن عياش، وروايته عن غير أهل المدينة.','3','225',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1001,'<span data-type="title" id=toc-1004>١٠٠١ </span>- عن أم كلثوم بنت أبى مسلمة وهى بنت أم سلمة قالت: لما تزوج النبى صلى الله
عليه وسلم أم سلمة قال لها: إنى قد أهديت إلى النجاشى حلة وأواقى من مسك، ولا أرى النجاشى إلا قد مات، ولا أرى هديتى إلا مردودة على، فإن ردت على فهى لك، قال: كان كما قال ... الحديث.
** حم طب
(فتح الباري ٢٢٢/٥)
** إسناده حسن','3','226',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1002,'<span data-type="title" id=toc-1005>١٠٠٢ </span>- عن ابن عباس مرفوعا: " من أهديت له هدية وعنده قوم فهم شركاؤه فيها ".
** عبد
(فتح الباري ٢٢٧/٥)
** قال الحافظ فى " الفتح " ٥ / ٢٢٧: فى إسناده مندل بن على وهو ضعيف.','3','227',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1003,'<span data-type="title" id=toc-1006>١٠٠٣ </span>- عن عبد الرحمن بن كعب بن مالك ورجال من أهل العلم: أن عامر بن مالك الذى يدعى ملاعب الأسنة قدم على رسول الله صلى الله عليه وسلم وهو مشرك فأهدى له فقال: " إنى لا أقبل هدية مشرك ".
** موسى بن عقبة فى المغازى
(فتح الباري ٢٣٠/٥)
** رجاله ثقات','3','228',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1004,'<span data-type="title" id=toc-1007>١٠٠٤ </span>- حديث قيس بن النعمان أنه لما قدم أخرج قباء من ديباج منسوجا بالذهب فرده النبى صلى الله عليه وسلم عليه، ثم إنه وجد فى نفسه من رد هديته فرجع له فقال له النبى صلى الله عليه وسلم: " ادفعه إلى عمر ... " الحديث.
** يع
(فتح الباري ٢٣١/٥)
** إسناده قوى','3','229',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1005,'<span data-type="title" id=toc-1008>١٠٠٥ </span>- عن عياض قال: أهديت للنبى صلى الله عليه وسلم ناقة فقال: " أسلمت " قلت: لا، قال: " إنى نهيت عن زبد المشركين ".
** د ت
(فتح الباري ٢٣٠/٥)
** قال الحافظ فى " الفتح " ٥ / ٢٣١: صححه الترمذى.
** تعقيب: قال الحافظ فى " الفتح " ٥ / ٢٣٠: وفى الباب حديث عياض بن حماد أ. هـ. بالدال، والتصحيح عياض بن حمار بالراء، إرجع إلى الإصابة ٧ / ١٨٥.','3','230',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1006,'<span data-type="title" id=toc-1009>١٠٠٦ </span>- عن ابن عباس موقوفا: " العمرى والرقبى سواء ".
** ن
(فتح الباري ٢٤٠/٥)
** إسناده صحيح','3','231',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1007,'<span data-type="title" id=toc-1010>١٠٠٧ </span>- عن عطاء قال: نهى رسول الله صلى الله عليه وسلم عن العمرى والرقبى، قلت: وما الرقبى؟ قال: يقول الرجل للرجل هى لك حياتك فإن فعلتم فهو جائز.
** ن
(فتح الباري ٢٤٠/٥)
** مرسل','3','232',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1008,'<span data-type="title" id=toc-1011>١٠٠٨ </span>- عن ابن عمر مرفوعا: " لا عمرى ولا رقبى، فمن أعمر شيئا أو أرقبة فهو له حياته ومماته ".
** ن
(فتح الباري ٢٤٠/٥)
** رجاله ثقات','3','233',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1009,'<span data-type="title" id=toc-1012>١٠٠٩ </span>- حديث أبى أمامة أنه سمع النبى صلى الله عليه وسلم فى حجة الوداع يقول: " العارية مؤداة والزعيم غارم ".
** د
(فتح الباري ٢٤١/٥)
** قال الحافظ فى " الفتح " ٥ / ٢٤١: حسنه الترمذى وصححه ابن حبان.','3','234',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1010,'<span data-type="title" id=toc-1013>١٠١٠ </span>- عن سمرة رفعه: " على اليد ما أخذت حتى تؤديه ".
** د ت ن جه
(فتح الباري ٢٤١/٥)
** قال الحافظ فى " الفتح " ٥ / ٢٤١: صححه الحاكم.','3','235',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1011,'<span data-type="title" id=toc-1014>١٠١١ </span>- حديث عمرو بن شعيب عن أبيه عن جده مرفوعا: " لا تجوز شهادة خائن ولا خائنة ولا محدود فى الإسلام ".
** د جه
(فتح الباري ٢٥٧/٥)
** قال الحافظ فى " الفتح " ٥ / ٢٥٧: احتجوا فى رد شهادة المحدود بأحاديث قال الحفاظ: لا يصح منها شىء، رواه الترمذى من حديث عائشة نحوه وقال: لا يصح، وقال أبو زرعة: منكر.','3','236',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1012,'<span data-type="title" id=toc-1015>١٠١٢ </span>- حديث أبى هريرة: أن النبى صلى الله عليه وسلم قضى باليمين مع الشاهد.
** د ت ن جه
(فتح الباري ٢٨٢/٥)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٥ / ٢٨٢: هو عند أصحاب السنن.','3','237',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1013,'<span data-type="title" id=toc-1016>١٠١٣ </span>- عن أبى مليكة قال: كنت قاضيا لا بن الزبير على الطائف فذكر قصة المرأتين فكتبت إلى ابن عباس فكتب إلى أن رسول الله صلى الله عليه وسلم قال: " لو يعطى الناس بدعواهم لادعى رجال أموال قم ودمائهم ولكن البينة على من أدعى واليمين على من أنكر ".
** هق
(فتح الباري ٢٨٣/٥)
** قال الحافظ فى " الفتح " ٥ / ٢٨٣: هذه الزيادة ليست فى الصحيحين وإسنادها حسن.','3','238',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1014,'<span data-type="title" id=toc-1017>١٠١٤ </span>- عن نافع أن ابن عمر كان وصى رجل، فأتاه رجل بصك قد درست أسماء شهوده فقال ابن عمر: يانافع أذهب به إلى المنبر فاستحلفه، فقال الرجل: يا ابن عمر أتريد أن تسمع بى الذى يسمعنى هنا فقال ابن عمر: صدق فاستحلفه مكانه.
** أبو عبيد فى كتاب القضاء
(فتح الباري ٢٨٥/٥)
** إسناده صحيح','3','239',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1015,'<span data-type="title" id=toc-1018>١٠١٥ </span>- عن سعيد بن المسيب قال: أدعى مدع على آخر أنه أغتصب له بعيرا فخاصمه إلى عثمان، فأمره عثمان أن يحلف عند المنبر فأبى أن يحلف وقال: أحلف له حيث شاء غير المنبر، فأبى عثمان عليه أن لا يحلف إلا عند المنبر فغرم له بعيرا مثل بعيره ولم يحلف.
** الكرابيس فى أدب القضاء
(فتح الباري ٢٨٥/٥)
** إسناده قوى','3','240',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1016,'<span data-type="title" id=toc-1019>١٠١٦ </span>- حديث جابر مرفوعا: " لا يحلف أحد عند منبرى هذا على يمين آثمة ولو على سواك أخضر إلا تبوأ مقعده من النار ".
** ما د ن جه
(فتح الباري ٢٨٥/٥)
** قال الحافظ فى " الفتح " ٥ / ٢٨٥: صححه ابن خزيمة وابن حبان والحاكم وغيرهم.','3','241',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1017,'<span data-type="title" id=toc-1020>١٠١٧ </span>- حديث أبى أمامة بن ثعلبة مرفوعا: " من حلف عند منبرى هذا بيمين كاذبة يستحل بها مال أمرىء مسلم فعليه لعنة الله والملائكة والناس أجمعين لا يقبل الله منه صرفا ولا عدلا ".
** ن
(فتح الباري ٢٨٥/٥)
** رجاله ثقات','3','242',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1018,'<span data-type="title" id=toc-1021>١٠١٨ </span>- فتبسم رسول الله صلى الله عليه وسلم فقال له عروة: من هذا يامحمد؟ قال: " هذا ابن أخيك المغيرة بن شعبة ".
** شب
(فتح الباري ٣٤١/٥)
** إسناده صحيح','3','243',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1019,'<span data-type="title" id=toc-1022>١٠١٩ </span>- من مرسل على بن زيد: فقال له عروة: أى قوم إنى رأيت الملوك ما رأيت مثل محمد وما هو بملك ولكن رأيت الهدى معكوفا وما أراكم إلا ستصيبكم قارعة فانصرف هو من اتبعه إلى الطائف.
** شب
(فتح الباري ٣٤١/٥)
** مرسل','3','244',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1020,'<span data-type="title" id=toc-1023>١٠٢٠ </span>- حديث ابن عمر: إن مدة الصلح كانت أربع سنين.
** عد ك طس
(فتح الباري ٣٤٣/٥)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٥ / ٣٤٣: هو مع ضعف إسناده منكر مخالف للصحيح.','3','245',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1021,'<span data-type="title" id=toc-1024>١٠٢١ </span>- قوله تعالى (وهو الذى كف أيديهم عنكم) حديث عبد الله بن مغفل: أنها نزلت بسبب القوم الذين أرادوا من قريش أن يأخذوا عن المسلمين عزة فظفروا بهم فعفا عنهم النبى صلى الله عليه وسلم فنزلت الآية.
** حم ن
(فتح الباري ٣٥١/٥)
** إسناده صحيح','3','246',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1022,'<span data-type="title" id=toc-1025>١٠٢٢ </span>- عن عمر أنه أجاز وصية غلام لم يحتلم.
** ما
(فتح الباري ٣٥٦/٥)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٥ / ٣٥٦: وله شاهد.','3','247',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1023,'<span data-type="title" id=toc-1026>١٠٢٣ </span>- عن ابن عباس " الإضرار فى الوصية من الكبائر ".
** ص ن
(فتح الباري ٣٥٩/٥)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٥ / ٣٥٩: رواه النسائى ورجاله ثقات.','3','248',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1024,'<span data-type="title" id=toc-1027>١٠٢٤ </span>- عن نافع قال: قيل لابن عمر فى مرض موته: ألا توصى؟ قال: أما مالى فالله يعلم ماكنت أضع فيه وأما رباعى فلا أحب أن يشارك ولدى فيها أحد.
** ابن المنذر
(فتح الباري ٣٥٩/٥)
** إسناده صحيح','3','249',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1025,'<span data-type="title" id=toc-1028>١٠٢٥ </span>- عن ابن عباس: أنه صلى الله عليه وسلم لم يوص.
** شب
(فتح الباري ٣٦١/٥)
** صحيح
** قال الحافظ فى " الفتح " ٥ / ٣٦١: صح عن ابن عباس.','3','250',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1026,'<span data-type="title" id=toc-1029>١٠٢٦ </span>- عن ابن عباس: فى أثناء حديث فيه أمر النبى صلى الله عليه وسلم فى مرضه أبا بكر أن يصلى بالناس، قال فى آخر الحديث: مات رسول الله صلى الله عليه وسلم ولم يوص.
** حم جه
(فتح الباري ٣٦٢/٥)
** إسناده قوى
** قال الحافظ فى " الفتح " ٥ / ٣٦٢: أخرج أحمد وابن ماجه بسند قوى وصححه.','3','251',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1027,'<span data-type="title" id=toc-1030>١٠٢٧ </span>- من مرسل العلاء بن عبد الرحمن أنه صلى الله عليه وسلم أوصى فاطمة فقال: " قولى إذا مت إنا لله وإنا إليه راجعون ".
** الواقدى
(فتح الباري ٣٦٢/٥)
** مرسل','3','252',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1028,'<span data-type="title" id=toc-1031>١٠٢٨ </span>- حديث عبد الرحمن بن عوف: قالوا: يارسول الله أوصنا _ يعنى فى مرض موته _ فقال: " أوصيكم بالسابقين الأولين من المهاجرين وأبنائهم من بعدهم.
** طس
(فتح الباري ٣٦٢/٥)
** قال الحافظ فى " الفتح " ٥ / ٣٦٢: وقال _ أى الطبرى فى الأوسط _: لا يروى عن عبد الرحمن إلا بهذا الإسناد، تفرد به عتيق بن يعقوب.أ. هـ. قال الحافظ فى " الفتح " ٥ / ٣٦٢: وفيه من لا يعرف حاله.','3','253',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1029,'<span data-type="title" id=toc-1032>١٠٢٩ </span>- أنه صلى الله عليه وسلم أوصى أن يصلوا عليه أرسالا بغير إمام.
** بز ك
(فتح الباري ٣٦٣/٥)
** إسناده ضعيف','3','254',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1030,'<span data-type="title" id=toc-1033>١٠٣٠ </span>- عن زيد بن على بن الحسن قال: لما كان اليوم الذى توفى فيه رسول الله صلى
الله عليه وسلم _ فذكر قصة طويلة فيها_: فدخل على فقامت عائشة فاكب عليه فأخبره بألف باب مما يكون قبل يوم القيامة يفتح كل باب منها ألف باب.
** لم
(فتح الباري ٣٦٣/٥)
** مرسل أو معضل
** قال الحافظ فى " الفتح " ٥ / ٣٦٣: من أكاذيب الرافضة ما رواه كثير بن يحيى وهو من كبارهم وله طريق أخرى موصولة عند ابن عدى فى كتاب الضعفاء من حديث عبد الله بن عمر بسند واه.','3','255',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1031,'<span data-type="title" id=toc-1034>١٠٣١ </span>- حديث أبى أمامة: سمعت رسول الله صلى الله عليه وسلم يقول فى خطبته فى حجة الوداع: " إن الله قد أعطى كل ذى حق حقه فلا وصية لوارث ".
** د ت
(فتح الباري ٣٧٢/٥)
** قال الحافظ فى " الفتح " ٥ / ٣٧٢: فى إسناده إسماعيل بن عياش، وقد قوى حديثه عن الشاميين جماعة من الأئمة منهم أحمد والبخارى، وهذا من روايته عن شرحبيل بن مسلم وهو شامى ثقة، وصرح فى روايته بالتحديث عند الترمذى، وقال الترمذى: حديث حسن.','3','256',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1032,'<span data-type="title" id=toc-1035>١٠٣٢ </span>- عن ابن عباس مرفوعا: " لا تجوز وصية لوارث إلا أن يشاء الورثة ".
** قط
(فتح الباري ٣٧٢/٥)
** رجاله ثقات
** قال الحافظ فى " الفتح " ٥ / ٣٧٢: رجاله ثقات إلا أنه معلول، فقد قيل إن عطاء هو الخراسانى والله أعلم.','3','257',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1033,'<span data-type="title" id=toc-1036>١٠٣٣ </span>- حديث بسر بن جحاش قال: بزق النبى صلى الله عليه وسلم فى كفه ثم وضع إصبعه السبابه وقال: " يقول الله أنى يعجزنى ابن آدم قد خلقتك من قبل من مثل هذا، فإذا بلغت نفسك إلى هذه _ وأشار إلى حلقه _ قلت: أتصدق، وأنى أوان الصدقة.
** حم جه
(فتح الباري ٣٧٤/٥)
** قال الحافظ فى " الفتح " ٥ / ٣٧٤: صححه ابن ماجه.','3','258',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1034,'<span data-type="title" id=toc-1037>١٠٣٤ </span>- عن أبى الدرداء مرفوعا قال: مثل الذى يعتق ويتصدق عند موته مثل الذى يهدى إذا شبع.
** ت
(فتح الباري ٣٧٤/٥)
** إسناده حسن
** قال الحافظ فى " الفتح " ٥ / ٣٧٤: صححه ابن حبان.','3','259',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1035,'<span data-type="title" id=toc-1038>١٠٣٥ </span>- حديث أبى سعيد الخدرى مرفوعا: " لأن يتصدق الرجل فى حياته وصحته بدرهم خير له من أن يتصدق عند موته بمائة.
** د
(فتح الباري ٣٧٤/٥)
** قال الحافظ فى " الفتح " ٥ / ٣٧٤: صححه ابن حبان.','3','260',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1036,'<span data-type="title" id=toc-1039>١٠٣٦ </span>- عن على بن أبى طالب قال: قضى محمد صلى الله عليه وسلم أن الدين قبل الوصية وأنتم تقرؤن الوصية قبل الدين.
** حم ت
(فتح الباري ٣٧٧/٥)
** إسناده ضعيف','3','261',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1037,'<span data-type="title" id=toc-1040>١٠٣٧ </span>- من مرسل أبى بكر بن حزم: أن أبا طلحة تصدق بماله وكان موضعه قصر بنى حديلة فدفعه إلى رسول الله فرده على أقاربه، أبى بن كعب وحسان بن ثابت وثبيط بن جابر، وشداد بن أوس أو ابنه أوس بن ثابت، فتقاوموه فصار لحسان فباعه من معاوية بمائة ألف فابتنى قصر بنى حديلة فى موضعها.
** محمد بن الحسن بن زبالة فى كتاب المدينة
(فتح الباري ٣٨١/٥)
** مرسل
** قال الحافظ فى " الفتح " ٥ / ٣٨١: ابن زبالة ضعيف فلا يحتج بما ينفرد به فكيف إذا خالف.','3','262',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1038,'<span data-type="title" id=toc-1041>١٠٣٨ </span>- عن سعيد بن جبير أن سبب نزول الآية المذكورة لما نزلت (إن الذين يأكلون أموال اليتامى ظلما) عزلوا أموالهم عن أموالهم فنزلت (قل إصلاح لهم خير وإن تحالطوهم فإخوانكم) قال: فحلطوا أموالهم بأموالهم.
** الثورى فى تفسيره
(فتح الباري ٣٩٥/٥)
** مرسل
** قال الحافظ فى " الفتح " ٥ / ٣٩٥: هذا هو المحفوظ مع إرساله.','3','263',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1039,'<span data-type="title" id=toc-1042>١٠٣٩ </span>- عن ابن عباس قال: لما نزلت هذه الآية (ولا تقربوا مال اليتيم إلا بالتى هى أحسن، وإن الذين يأكلون أموال اليتامى ظلما) اجتنب الناس مال اليتيم وطعامه، فشق ذلك عليهم فشكوا إلى النبى صلى الله عليه وسلم ذلك، فنزلت (ويسئلونك عن اليتامى) الآية.
** د ن
(فتح الباري ٣٩٥/٥)
** قال الحافظ فى " الفتح " ٥ / ٣٩٥: صححه الحاكم.','3','264',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1040,'<span data-type="title" id=toc-1043>١٠٤٠ </span>- عن أبى بكر بن محمد بن عمرو بن حزم: أن عمر رأى فى المنام ثلاث ليال أن يتصدق بثمغ.
** عمر بن شبة
(فتح الباري ٤٠٠/٥)
** إسناده صحيح','3','265',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1041,'<span data-type="title" id=toc-1044>١٠٤١ </span>- أن عمر قال: يارسول الله إنى نذرت أن أتصدق بمالى.
** قط
(فتح الباري ٤٠٠/٥)
** إسناده ضعيف','3','266',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1042,'<span data-type="title" id=toc-1045>١٠٤٢ </span>- من مرسل قتادة: " حمل عثمان على ألف بعير وسبعين فرسا فى العسرة.
** أسد بن موسى فى فضائل الصحابة
(فتح الباري ٤٠٨/٥)
** مرسل','3','267',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1043,'<span data-type="title" id=toc-1046>١٠٤٣ </span>- فجاء عثمان بسبعمائة أوقية ذهب.
** يع
(فتح الباري ٤٠٨/٥)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٥ / ٤٠٨: عند أبى يعلى من وجه آخر ضعيف.','3','268',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1044,'<span data-type="title" id=toc-1047>١٠٤٤ </span>- عن حذيفة: أن النبى صلى الله عليه وسلم استعان عثمان فى جيش العسرة فجاء بعشرة آلاف دينار ولعلها عشرة آلاف درهم.
** عد
(فتح الباري ٤٠٨/٥)
** إسناده ضعيف جدا','3','269',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1045,'<span data-type="title" id=toc-1048>١٠٤٥ </span>- عن ابن عباس: أن الآية نزلت فيمن مات مسافرا وليس عنده أحد من المسلمين فإن اتهما استحلفا.
** الطبرى
(فتح الباري ٤١٢/٥)
** إسناده رجاله ثقات','3','270',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1046,'<span data-type="title" id=toc-1049>١٠٤٦ </span>- عن الشعبى قال: حضرت رجلا من المسلمين الوفاة بدقوقا ولم يجد أحدا من المسلمين فأشهد رجلين من أهل الكتاب فقدما الكوفة بتركته ووصيته فأخبرا الأشعرى فقال: هذا لم يكن يعد الذى كان فى عهد رسول الله صلى الله عليه وسلم وأحلفهما بعد العصر ماخانا ولا كذبا ولا كتما ولا بدلا وأمضى شهادتهما.
** د
(فتح الباري ٤١٢/٥)
** إسناد رجاله ثقات','3','271',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1047,'<span data-type="title" id=toc-1050>١٠٤٧ </span>- عن محمد بن كعب قال عبد الله بن رواحة: يارسول الله أشترط لربك ولنفسك ما شئت فقال: أشترط لربى أن تعبدوه ولا تشركوا به شيئا، وأشترط لنفسى أن تمنعونى مما تمنعون منه أنفسكم قالوا: فمالنا إذا فعلنا ذلك؟ قال: الجنة قالوا: ربح البيع لا يقبل ولا تستقيل.
** لم
(فتح الباري ٤/٦)
** مرسل','3','272',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1048,'<span data-type="title" id=toc-1051>١٠٤٨ </span>- حديث أبى الدرداء مرفوعا: ألا أنبئكم بخير أعمالكم وأزكاها عند مليككم وأرفعها فى درجاتكم وخير لكم م انفاق الذهب والورق وخير لكم من أن تلقوا عدوكم فتضربوا أعناقهم ويضربوا أعناقكم قالوا: بلى قال: ذكر الله.
** ت جه حم
(فتح الباري ٥/٦)
** قال الحافظ فى " الفتح " ٦ / ٥: صححه الحاكم.','3','273',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1049,'<span data-type="title" id=toc-1052>١٠٤٩ </span>- من طريق سعيد بن جبير إن هذه الآيات لما نزلت قال المسلمون: لو علمنا هذه التجارة لأعطينا فيها الأموال، والأهلين فنزلت: تؤمنون بالله ورسوله وتجاهدون ... الآية.
** ابن أبى حاتم
(فتح الباري ٦/٦)
** مرسل','3','274',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1050,'<span data-type="title" id=toc-1053>١٠٥٠ </span>- عن أبى هريرة أن رجلا مر بشعب فيه عين عذبة فأعجبه فقال: لو اعتزلت ثم ثم استأذن النبى صلى الله عليه وسلم فقال: " لا تفعل فإن مقام أحدكم فى سبيل الله أفضل من صلاته فى بيته سبعين عاما ".
** ت ك
(فتح الباري ٧/٦)
** قال الحافظ فى " الفتح " ٦ / ٧: حسنه الترمذى وصححه الحاكم.','3','275',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1051,'<span data-type="title" id=toc-1054>١٠٥١ </span>- عن رسول الله صلى الله عليه وسلم فيما يحكى عن ربه قال: " أيما عبد من عبادى خرج مجاهدا فى سبيلى ابتغاء مرضاتى ضمنت له إن رجعته أن أرجعه بما أصاب من أجر أو غنيمة ... " الحديث.
** حم ن
(فتح الباري ٧/٦)
** رجاله ثقات','3','276',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1052,'<span data-type="title" id=toc-1055>١٠٥٢ </span>- حديث عبادة يقول الله عز وجل: المجاهد فى سبيلى هو على ضامن إن رجعته رجعته بأجر أو غنيمة. الحديث.
** ت
(فتح الباري ٧/٦)
** قال الحافظ فى " الفتح " ٦ / ٧: صححه الترمذى.','3','277',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1053,'<span data-type="title" id=toc-1056>١٠٥٣ </span>- عن الحسن قال: بعث رسول الله صلى الله عليه وسلم جيشا فيهم عبد الله بن رواحة فتأخر يشهد الصلاة مع النبى صلى الله عليه وسلم فقال له النبى صلى الله عليه وسلم: " والذى نفسى بيده لو أنفقت ما فى الأرض ما أدركت فضل غدوتهم ".
** ابن المبارك فى كتاب الجهاد
(فتح الباري ١٤/٦)
** مرسل','3','278',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1054,'<span data-type="title" id=toc-1057>١٠٥٤ </span>- حديث عقبة بن عامر مرفوعا: من صرع عن دابته فى سبيل الله فمات فهو شهيد.
** طب
(فتح الباري ١٨/٦)
** إسناده حسن
** قال الحافظ فى " الفتح " ٦ / ١٨: كأنه لم يكن على شرط البخارى أشاى إليه فى الترجمة.','3','279',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1055,'<span data-type="title" id=toc-1058>١٠٥٥ </span>- حديث معاذ بن جبل: منجرح فى سبيل الله أو نكب نكبة فإنها تجىء يوم القيامة كأغزر ما كانت، لونها الزعفران وريحها المسك.
** د ت ن جه
(فتح الباري ٢٠/٦)
