INSERT INTO [b88] VALUES (1082,'<span data-type="title" id=toc-1085>١٠٨٢ </span>- حديث عمر أنه خطب فقال: تقولون فى مغازيكم فلان شهيد ومات فلان شهيدا ولعله قد أوقر راحلته، ألا لا تقولوا ذلكم ولكن قولوا كما قال رسول الله صلى الله عليه وسلم: " من مات فى سبيل الله أو قتل فهو شهيد ".
