(فتح الباري ١٨٤/٦)
** إسناده واه','3','325',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1101,'<span data-type="title" id=toc-1104>١١٠١ </span>- من طريق صالح بن محمد بن زائدة الليثى المدنى أحد الضعفاء قال: دخلت مع مسلمة بن عبد الملك أرض الروم فأتى برجل قد غل، فسأل سالما _ أى ابن عبد الله بن عمر _ عنه فقال: سمعت أبى يحدث عن عمر عن النبى صلى الله عليه وسلم قال: " إذا وجدتم الرجل قد غل فأحرقوا متاعه ".
** د
(فتح الباري ١٨٧/٦)
** قال الحافظ فى " الفتح " ٦ / ١٨٧: ساقه _ يعنى أبو داود _ من وجه آخر عن سالم موقوفا، قال أبو داود: هذا أصح، وقال البخارى فى التاريخ: يحتجون بهذا الحديث فى إحراق رحل الغال وهو باطل ليس له أصل وراويه لا يعتمد عليه، وروى الترمذى عنه أيضا أنه قال: صالح منكر الحديث.','3','326',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1102,'<span data-type="title" id=toc-1105>١١٠٢ </span>- عن عبادة بن الصامت قال: فلما اختلفتا فى الغنيمة وساءت أخلاقنا انتزعها الله منا فجعلها لرسوله فقسمها على الناس عن سواء.
** ابن إسحق حم ك
(فتح الباري ١٩٩/٦)
** إسناده حسن
** قال الحافظ فى " الفتح " ٦ / ١٩٩: إسناده حسن يحتج بمثله وصححه ابن حبان من وجه آخر ليس فيه ابن إسحق.','3','327',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1103,'<span data-type="title" id=toc-1106>١١٠٣ </span>- من طريق الشعبى: أن أبا بكر عاد فاطمة فقال لها على: هذا أبو بكر يستأذن عليك قالت: أتحب أن آذن له؟ قال: نعم فأذنت له، فدخل عليها فترضاها حتى رضيت.
** هق
(فتح الباري ٢٠٢/٦)
** مرسل','3','328',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1104,'<span data-type="title" id=toc-1107>١١٠٤ </span>- إن النبى لا يورث.
** لم
(فتح الباري ٢٠٢/٦)
** صحيح','3','329',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1105,'<span data-type="title" id=toc-1108>١١٠٥ </span>- عن سهل بن أبى خثيمة قال: قسم رسول الله صلى الله عليه وسلم خيبر نصفين نصفها لنوائبه وحاجته، ونصفها بين المسلمين قسمها بينهم على ثمانية عشر
سهما.
** د
(فتح الباري ٢٠٣/٦)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٦ / ٢٠٣: رواه بمعناه من طرق أخرى عن بشير بن يسار مرسلا ليس فيه سهل.','3','330',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1106,'<span data-type="title" id=toc-1109>١١٠٦ </span>- عن ابن عمر قال: بعث على إلى عثمان بصحيفة فيها: لا تأخذوا الصدقة من الرخة ولا من النخة.
** الخطابى فى غريب الحديث
(فتح الباري ٢١٥/٦)
** إسناده ضعيف','3','331',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1107,'<span data-type="title" id=toc-1110>١١٠٧ </span>- خولة بنت قيس وكانت تحت حمزة بن عبد المطلب سمعت رسول الله صلى الله عليه وسلم يقول: " إن هذا المال خضرة حلوة من أصابه بحقه بورك له فيه ورب متخوص فيما شاءت نفسه من مال الله ورسوله ليس له يوم القيامة إلا النار ".
** ت
(فتح الباري ٢١٩/٦)
** قال الحافظ فى " الفتح " ٦ / ٢١٩: قال الترمذى: حسن صحيح.','3','332',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1108,'<span data-type="title" id=toc-1111>١١٠٨ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: ": إن الشمس لم تحبس لبشر إلا ليوشع بن نون ليالى سار إلى بيت المقدس.
** حم
(فتح الباري ٢٢١/٦)
** إسناده صحيح
** قال الحافظ فى " الفتح " ٦ / ٢٢١: من طريق مرفوعة صحيحة.','3','333',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1109,'<span data-type="title" id=toc-1112>١١٠٩ </span>- عن على قال: سأل قوم يوشع منه أن يطلعهم على بدء الخلق وآجالهم فأراهم ذلك فى ماء من غمامة أمطرها الله عليهم، فكان أحدهم يعلم متى يموت فبقوا على ذلك إلى أن قاتلهم داود على الكفر فاخرجوا إلى داود من لم يحضر أجله فكان يقتل من أصحاب داود ولا يقتل منهم، فشكى إلى الله ودعاه فحبست عليهم الشمس فزيد فى
النهار فاختلطت الزيادة بالليل والنهار فاختلط عليهم الحساب.
** خ فى المبتدأ
(فتح الباري ٢٢١/٦)
** إسناده ضعيف جدا
** قال الحافظ فى " الفتح " ٦ / ٢٢١: أخرجه الخطيب فى ذم النجوم له من طريق أبى حذيفة.','3','334',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1110,'<span data-type="title" id=toc-1113>١١١٠ </span>- أن النبى صلى الله عليه وسلم لما أخبر قريشا صبيحة الإسراء أنه رأى العير، التى لهم وأنها تقدم مع شروق الشمس فدعا الله فحبست الشمس حتى دخلت العير.
** ابن إسحق فى المغازى
(فتح الباري ٢٢١/٦)
** منقطع','3','335',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1111,'<span data-type="title" id=toc-1114>١١١١ </span>- حديث جابر أن النبى صلى الله عليه وسلم أمر الشمس فتأخرت ساعة من نهار.
** طس
(فتح الباري ٢٢١/٦)
** إسناده حسن','3','336',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1112,'<span data-type="title" id=toc-1115>١١١٢ </span>- عن طارق بن شهاب: أن عمر كتب إلى عمار أن الغنيمة لمن شهد الواقعة.
** عب
(فتح الباري ٢٢٤/٦)
** إسناده صحيح','3','337',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1113,'<span data-type="title" id=toc-1116>١١١٣ </span>- بشر قاتل ابن صفية بالنار.
** حم
(فتح الباري ٢٢٩/٦)
** إسناده صحيح','3','338',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1114,'<span data-type="title" id=toc-1117>١١١٤ </span>- حديث عبادة بن الصامت: أن النبى صلى الله عليه وسلم قال: " مالى مما أفاء الله عليكم إلا الخمس وهو مردود عليكم ".
** ن
(فتح الباري ٢٤١/٦)
** إسناده حسن','3','339',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1115,'<span data-type="title" id=toc-1118>١١١٥ </span>- عن سعد بن أبى وقاص أن عبد الله بن جحش قال يوم أحد: تعال بنا ندعو فدعا سعد فقال: اللهم ارزقنى رجلا شديدا بأسه فأقاتله ويقاتلنى ثم ارزقنى عليه الظفر حتى أقتله وآخذ سلبه.... الحديث.
** ك هق
(فتح الباري ٢٤٨/٦)
** إسناده صحيح','3','340',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1116,'<span data-type="title" id=toc-1119>١١١٦ </span>- عن عبد الله بن الزبير قال: كانت صفية فى حصن حسان بن ثابت يوم الخندق.
** حم
(فتح الباري ٢٤٨/٦)
** إسناده قوى','3','341',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1117,'<span data-type="title" id=toc-1120>١١١٧ </span>- حديث رويفع بن ثابت مرفوعا: " من كان يؤمن بالله واليوم الآخر فلا يأخذ دابة من المغنم فيركبها حتى إذا أعجفها ردها إلى المغانم ".
** د طحا
(فتح الباري ٢٥٦/٦)
** حسن','3','342',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1118,'<span data-type="title" id=toc-1121>١١١٨ </span>- أن صاحب المغانم كعب بن عمرو بن زيد الأنصارى أخذ منه الجراب، فقال النبى صلى الله عليه وسلم: " خل بينه وبين جرابه ".
** ابن وهب
(فتح الباري ٢٥٦/٦)
** إسناده معضل','3','343',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1119,'<span data-type="title" id=toc-1122>١١١٩ </span>- عن معاذ: أن النبى صلى الله عليه وسلم حين بعثه إلى اليمن قال: " خذ من كل حالم دينارا ".
** د ت ن جه
(فتح الباري ٢٦٠/٦)
** قال الحافظ فى " الفتح " ٦ / ٢٦٠: أخرجه أصحاب السنن وصححه الترمذى والحاكم.','3','344',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1120,'<span data-type="title" id=toc-1123>١١٢٠ </span>- عن حذيفة: لولا أنى رأيت أصحابى أخذوا الجزية من المجوس ماأخذتها.
** أبو عبيد
(فتح الباري ٢٦١/٦)
** إسناده صحيح','3','345',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1121,'<span data-type="title" id=toc-1124>١١٢١ </span>- عن جعفر بن محمد عن أبيه أن عمر قال: لا أدرى ما أصنع بالمجوس؟ فقال عبد الرحمن بن عوف: أشهد لسمعت رسول الله صلى الله عليه وسلم يقول: " سنوا بهم سنة أهل الكتاب ".
** ما
(فتح الباري ٢٦١/٦)
** منقطع
** قال الحافظ فى " الفتح " ٦ / ٢٦١: منقطع مع ثقة رجاله، ورواه ابن المنذر والدارقطنى فى الغرائب من طريق أبى على الحنفى عن مالك فزاد فيه (عن جده) وهو منقطع أيضا لأن جده على بن الحسين لم يلحق عبد الرحمن بن عوف ولا عمر.','3','346',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1122,'<span data-type="title" id=toc-1125>١١٢٢ </span>- عن على كان المجوس أهل كتاب يقرؤنه وعلم يدرسونه فشرب أميرهم الخمر فوقع على أخته فلما أصبح دعا أهل الطمع فأعطاهم وقال: إن آدم كان ينكح أولاده
وبناته فأطاعوه وقتل من خالفه فأسرى على كتابهم وعلى مافى قلوبهم منه فلم يبق عندهم منه شىء.
** شفع عب
(فتح الباري ٢٦١/٦)
** إسناده حسن','3','347',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1123,'<span data-type="title" id=toc-1126>١١٢٣ </span>- عن ابن أبذى: لما هزم المسلمون أهل فارس قال عمر: اجتمعوا. فقال: إن المجوس ليسوا أهل كتاب فنضع عليهم، ولا من عبدة الأوثان فنجرى عليهم أحكامهم فقال على: بل هم أهل كتاب.. فذكر نحوه لكن قال: وقع على ابنته وقال فى آخره: فوضع الأخدود لمن خالفه.
** عبد
(فتح الباري ٢٦١/٦)
** إسناده صحيح','3','348',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1124,'<span data-type="title" id=toc-1127>١١٢٤ </span>- عن أنس بن مالك قال: حاصرنا تستر فنزل الهرمزان على حكم عمر فلما قدم به عليه استعجم فقال له عمر: تكلم لا بأس عليك وكان ذلك تأمينا من عمر.
** شب ويعقوب بن سفيان فى تاريخه
(فتح الباري ٢٧٥/٦)
** إسناده صحيح','3','349',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1125,'<span data-type="title" id=toc-1128>١١٢٥ </span>- حديث أبى رزين العقيلى: إن الماء خلق قبل العرش.
** حم ت
(فتح الباري ٢٨٩/٦)
** قال الحافظ فى " الفتح " ٦ / ٢٨٩: صححه الترمذى.','3','350',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1126,'<span data-type="title" id=toc-1129>١١٢٦ </span>- حديث عبادة بن الصامت مرفوعا: " أول ماخلق الله القلم، ثم قال: أكتب فجرى بما هو كائن إلى يوم القيامة ".
** حم ت
(فتح الباري ٢٨٩/٦)
** قال الحافظ فى " الفتح " ٦ / ٢٨٩: صححه الترمذى.','3','351',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1127,'<span data-type="title" id=toc-1130>١١٢٧ </span>- حديث: " أول ما خلق الله العقل ".
** لم
(فتح الباري ٢٨٩/٦)
** قال الحافظ فى " الفتح " ٦ / ٢٨٩: ليس له طريق ثبت.','3','352',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1128,'<span data-type="title" id=toc-1131>١١٢٨ </span>- حديث عبد الله بن عمرو بن العاص قال: خرج علينا رسول الله صلى الله عليه وسلم وفى يده كتابان فقال للذى فى يده اليمنى: " هذا كتاب من رب العالمين فيه أسماء أهل الجنة وأسماء أبائهم وقبائلهم، ثم أجمل على آخرهم فلا يزاد فيهم ولا ينقص منهم أبدا "، ثم قال للذى فى شماله مثله فى أهل النار وقال فى آخر الحديث، فقال بيديه فنبذهما ثم قال: " فرغ ربكم من العباد فريق فى الجنة وفريق فى السعير ".
** ت
(فتح الباري ٢٩١/٦)
** إسناده حسن','3','353',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1129,'<span data-type="title" id=toc-1132>١١٢٩ </span>- حديث أبى سعيد: صلى بنا رسول الله صلى الله عليه وسلم يوما صلاة العصر ثم قام
يحدثنا فلم يدع شيئا يكون إلى قيام الساعة إلى أخبرنا به حفظه من حفظه ونسيه من نسيه.
** ت
(فتح الباري ٢٩١/٦)
** قال الحافظ فى " الفتح " ٦ / ٢٩١: قال الترمذى: حسن.','3','354',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1130,'<span data-type="title" id=toc-1133>١١٣٠ </span>- عن شعبة عن عمرو بن مرة عن أبى الضحى عن ابن عباس فى هذه الآية (ومن الأرض مثلهن) قال: فى كل أرض مثل إبراهيم ونحو ما على الأرض من الخلق.
** ابن جرير
(فتح الباري ٢٩٣/٦)
** إسناده صحيح','3','355',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1131,'<span data-type="title" id=toc-1134>١١٣١ </span>- عن عطاء بن السائب عن أبى الضحى عن ابن عباس مطولا وأوله: أى سبع أرضيين _ يعنى فى قوله تعالى (ومن الأرض مثلهن) _ قال: فى كل أرض آدم كآدمكم ونوح كنوحكم وإبراهيم كإبراهيمكم وعيسى كعيسى ونبى كنبيكم.
** ك هق
(فتح الباري ٢٩٣/٦)
** قال الحافظ فى " الفتح " ٦ / ٢٩٣: قال البيهقى: إسناده صحيح إلا أنه شاذ بمرة.','3','356',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1132,'<span data-type="title" id=toc-1135>١١٣٢ </span>- من طريق إبراهيم التيمى: أن أبا بكر الصديق سأل عن الأب فقال: أى سماء تظلنى وأى أرض تقلنى إذا قلت فى كتاب الله بغير علم.
** ابن جرير
(فتح الباري ٢٩٦/٦)
** منقطع','3','357',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1133,'<span data-type="title" id=toc-1136>١١٣٣ </span>- عن عمر أنه قال: عرفنا الفاكهة فما الأب ثم قال: إن هذا لهو التكلف.
** عبد
(فتح الباري ٢٩٦/٦)
** صحيح','3','358',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1134,'<span data-type="title" id=toc-1137>١١٣٤ </span>- من طريق ابن جريج قال: قال ابن عباس: القاصف التى تفرق.
** الطبرى
(فتح الباري ٣٠٠/٦)
** منقطع','3','359',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1135,'<span data-type="title" id=toc-1138>١١٣٥ </span>- أن النبى صلى الله عليه وسلم قال: " نصرت بالصبا وكانت عذابا على من كان قبلنا ".
** شفع
(فتح الباري ٣٠١/٦)
** إسناده منقطع
** قال الحافظ فى " الفتح " ٦ / ٣٠١: بإسناد فيه انقطاع.','3','360',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1136,'<span data-type="title" id=toc-1139>١١٣٦ </span>- حديث ابن عباس قال: قال رسول الله صلى الله عليه وسلم لجبريل: " على أى شىء أنت؟ " قال: على الريح والجنود قال: " وعلى أى شىء ميكائيل " قال: على النبات والقطر، قال: " وعلى أى شىء ملك الموت؟ " قال: على قبض الأرواح.. الحديث.
** طب
(فتح الباري ٣٠٧/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٠٧: فى إسناده محمد بن عبد الرحمن بن أبى ليلى وقد ضعف لسوء حفظه ولم يترك.','3','361',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1137,'<span data-type="title" id=toc-1140>١١٣٧ </span>- حديث أبو هريرة: وفى السماء نهر يقال له نهر الحيوان يدخله جبريل كل يوم فينغمس ثم يخرج فينتفض فيخر عنه سبعون ألف قطرة يخلق الله من كل قطرة ملكا فهم الذين يصلون فيه ثم لا يعودون إليه.
** ابن مردوية ابن أبى حاتم
(فتح الباري ٣٠٩/٦)
** إسناده ضعيف','3','362',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1138,'<span data-type="title" id=toc-1141>١١٣٨ </span>- عن أبى هريرة عن النبى صلى الله عليه وسلم قال: " لما خلق الله الجنة قال لجبريل: اذهب فانظر إليها ... " الحديث.
** حم د
(فتح الباري ٣٢٠/٦)
** إسناده قوى','3','363',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1139,'<span data-type="title" id=toc-1142>١١٣٩ </span>- عن ابن عباس قال: الأرائك السرر فى الحجال.
** عبد
(فتح الباري ٣٢١/٦)
** إسناده صحيح','3','364',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1140,'<span data-type="title" id=toc-1143>١١٤٠ </span>- عن ابن عباس قال: التسنيم يعلو شراب أهل الجنة وهو صرف للمقربين، ويمزج لأصحاب اليمين.
** عب
(فتح الباري ٣٢١/٦)
** إسناده صحيح','3','365',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1141,'<span data-type="title" id=toc-1144>١١٤١ </span>- من طريق جعفر بن محمد عن أبيه عن جده مرفوعا: " العرب كلا منهن عربى ".
**
(فتح الباري ٣٢٢/٦)
** ضعيف منقطع','3','366',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1142,'<span data-type="title" id=toc-1145>١١٤٢ </span>- عن أنس مرفوعا: " إن أدنى أهل الجنة درجة لمن يقوم على رأسه عشرة آلاف خادم بيد كل واحد صحفتان واحدة من ذهب والأخرى من فضة.... " الحديث.
** طب
(فتح الباري ٣٢٤/٦)
** إسناده قوى','3','367',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1143,'<span data-type="title" id=toc-1146>١١٤٣ </span>- عن أبى هريرة مرفوعا فى صفة أدنى أهل الجنة منزلة: " وإن له من الحور العين لاثنتين وسبعين زوجة سوى أزواجه من الدنيا ".
** حم
(فتح الباري ٣٢٥/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٢٥: فى سنده شهر بن حوشب وفيه مقال.','3','368',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1144,'<span data-type="title" id=toc-1147>١١٤٤ </span>- حديث أبى سعيد رفعه: " إن أدنى أهل الجنة الذى له ثمانون ألف خادم وثنتان وسبعون زوجة.
** ت
(فتح الباري ٣٢٥/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٢٥: قال الترمذى: غريب.','3','369',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1145,'<span data-type="title" id=toc-1148>١١٤٥ </span>- حديث أبى أمامة رفعه: " ماأحد يدخل الجنة إلازوجه الله ثنتين وسبعين من الحور العين وسبعين وثنتين من أهل الدنيا ".
** ده دى
(فتح الباري ٣٢٥/٦)
** إسناده ضعيف جدا','3','370',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1146,'<span data-type="title" id=toc-1149>١١٤٦ </span>- حديث عبد الله بن أبى أوفى رفعه: " إن الرجل من أهل الجنة ليزوج خمسمائة حوراء أو أنه ليفضى إلى أربعة آلاف بكر وثمانية آلاف ثيب ".
** شيخ فى العظمة هق فى البعث
(فتح الباري ٣٢٥/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٢٥: فيه راو لم يسم.','3','371',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1147,'<span data-type="title" id=toc-1150>١١٤٧ </span>- إن تحت العرش ستارة معلقة فيه ثم تطوى فإذا نشرت كانت علامة البكور وإذا طويت كانت علامة العشى.
** لم
(فتح الباري ٣٢٦/٦)
** ضعيف','3','372',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1148,'<span data-type="title" id=toc-1151>١١٤٨ </span>- حديث زيد بن خالد رفعه: " لا تسبوا الديك فإنه يدعو إلى الصلاة ".
** د حم
(فتح الباري ٣٥٣/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٥٣: صححه ابن حبان.','3','373',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1149,'<span data-type="title" id=toc-1152>١١٤٩ </span>- حديث ابن مسعود قال: ذكر عند النبى صلى الله عليه وسلم القردة والخنازير فقال: " إن الله لم يجعل للمسخ نسلا ولا عقبا، وقد كانت القردة والخنازير قبل ذلك ".
** لم
(فتح الباري ٣٥٣/٦)
** صحيح','3','374',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1150,'<span data-type="title" id=toc-1153>١١٥٠ </span>- حديث أبى ذر مرفوعا: " إنهم مائة ألف وأربعة وعشرون ألفا، الرسل منهم ثلاثمائة وثلاثة عشر ".
** لم
(فتح الباري ٣٦١/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٦١: صححه ابن حبان.','3','375',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1151,'<span data-type="title" id=toc-1154>١١٥١ </span>- عن أبى هريرة مرفوعا: " إن الله خلق آدم من تراب فجعله طينا ثم تركه حتى إذا كان حمأ مسنونا خلقه وصوره ثم تركه حتى إذا كان صلصالا كالفخار كان إبليس
يمر به فيقول: لقد خلقت لأمر عظيم ثم نفخ فيه من روحه وكان أول ماجرى فيه الروح بصره وخياشيمه فعطس فقال: الحمد لله فقال الله: يرحمك الله ".
** ن بز
(فتح الباري ٣٦٤/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٦٤: صححه ابن حبان.','3','376',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1152,'<span data-type="title" id=toc-1155>١١٥٢ </span>- حديث أبى موسى مرفوعا: " إن الله خلق آدم من قبضة قبضها من جميع الأرض فجاء بنو آدم على قدر الأرض.... " الحديث.
** د ت
(فتح الباري ٣٦٤/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٦٤: صححه ابن حبان.','3','377',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1153,'<span data-type="title" id=toc-1156>١١٥٣ </span>- عن أبى بن كعب مرفوعا: " إن الله خلق آدم رجلا طوالا كثير لشعر الرأس كأنه نخلة سحوق ".
** ابن أبى حاتم
(فتح الباري ٣٦٧/٦)
** إسناده حسن','3','378',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1154,'<span data-type="title" id=toc-1157>١١٥٤ </span>- حديث أبى أمامة: إن رجلا قال: يارسول الله أنبى كان آدم؟ قال: " نعم " قال: فكم كان بينه وبين نوح؟ قال: " عشرة قرون ".
** لم
(فتح الباري ٣٧٢/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٧٢: صححه ابن حبان.','3','379',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1155,'<span data-type="title" id=toc-1158>١١٥٥ </span>- إن نوحا كان إذا ذهب إلى الغائط قال: الحمد لله الذى رزقنى لذته، وأبقى فى قوته وأذهب عنى أذاه.
** عب
(فتح الباري ٣٧٣/٦)
** إسناده مقطوع','3','380',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1156,'<span data-type="title" id=toc-1159>١١٥٦ </span>- ويذكر عن ابن مسعود وابن عباس أن إلياس هو أدريس.
** خت
(فتح الباري ٣٧٣/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٧٣: أما قول ابن مسعود فوصله عبد بن حميد وابن أبى حاتم بإسناد حسن عنه قال: إلياس هو أدريس وأما قول ابن عباس فوصله جويبير فى تفسيره عن الضحاك عنه وإسناده ضعيف.','3','381',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1157,'<span data-type="title" id=toc-1160>١١٥٧ </span>- حديث جابر رفعه: " إن الناقة كانت ترد يوما فتشرب جميع الماء ويحتلبون منها مثل الذى كانت تشرب ".
** حم ابن أبى حاتم
(فتح الباري ٣٨٠/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٨٠: فى سنده إسماعيل بن عياش وفى روايته عن غير الشاميين ضعف وهذا منها.','3','382',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1158,'<span data-type="title" id=toc-1161>١١٥٨ </span>- عن جابر قال: لما مر رسول الله صلى الله عليه وسلم بالحجر قال: " لا تسألوا الآيات فقد سألها قوم صالح، وكانت الناقة ترد من هذا الفج، وتصدر من هذا الفج فعتوا عن أمر ربهم وكانت تشرب يوما ويشربون لبنها يوما فعقروها فأخذتهم صيحة أهدم الله من تحت أديم السماء منهم إلا رجلا واحدا كان فى حرم الله وهو أبو رغال فلما خرج من الحرم أصابه ما أصاب قومه ".
** حم ك
(فتح الباري ٣٨٠/٦)
** إسناده حسن','3','383',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1159,'<span data-type="title" id=toc-1162>١١٥٩ </span>- أن رجلا سأل النبى صلى الله عليه وسلم عن ذى القرنين فقال: " كان من الروم فأعطى ملكا فصار إلى مصر وبنى الإسكندرية فلما فرغ أتاه ملك فعرج به فقال: أنظر ما تحتك، قال: أرى مدينة واحدة، قال: تلك الأرض كلها وإنما أراد الله أن يريك وقد جعل لك فى الأرض سلطانا فسر فيها وعلم الجاهل وثبت العالم ".
** طبرى محمد بن ربيع الجيزى فى كتاب الصحابة الذين نزلوا مصر
(فتح الباري ٣٨٣/٦)
** ضعيف','3','384',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1160,'<span data-type="title" id=toc-1163>١١٦٠ </span>- عن أبى الطفيل سمعت ابن الكوا يقول لعلى بن أبى طالب: أخبرنى ما كان ذو القرنين؟ قال: كان رجلا أحب الله فأحبه، بعثه الله إلى قومه فضربوه على قرنه ضربة مات منها ثم بعثه الله إليهم فضربوه على قرنه ضربة مات منها ثم بعثه الله فسمى ذو القرنين.
** الزبير فى أوائل كتاب النسب
(فتح الباري ٣٨٣/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٨٣: عبد العزيز ضعيف.','3','385',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1161,'<span data-type="title" id=toc-1164>١١٦١ </span>- عن ابن عباس: ذو القرنين عبد الله بن الضحاك بن معد بن عدنان.
** الزبير فى كتاب النسب
(فتح الباري ٣٨٤/٦)
** إسناده ضعيف جدا','3','386',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1162,'<span data-type="title" id=toc-1165>١١٦٢ </span>- فى زيادة منكرة وهى: " والذى نفسى بيده لقد رأيته ليلة أسرى بى لبنة من ذهب ولبنة من فضة ".
** لم
(فتح الباري ٣٨٦/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٨٦: زيادة منكرة.','3','387',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1163,'<span data-type="title" id=toc-1166>١١٦٣ </span>- من طريق ابن مسعود: الأواه الرحيم.
** ابن أبى حاتم
(فتح الباري ٣٨٩/٦)
** إسناده حسن','3','388',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1164,'<span data-type="title" id=toc-1167>١١٦٤ </span>- من طريق أبى ذر قال: كان رجل يطوف بالبيت ويقول فى دعائه أوه أوه فقال النبى صلى الله عليه وسلم: " إنه لأواه ".
** لم
(فتح الباري ٣٨٩/٦)
** قال الحافظ فى " الفتح " ٦ / ٣٨٩: رجاله ثقات إلا أن فيه رجلا مبهما.','3','389',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1165,'<span data-type="title" id=toc-1168>١١٦٥ </span>- حديث على قال: أول من فتق الله لسانه بالعربية المبينة إسماعيل.
** الزبير بن بكار فى النسب
(فتح الباري ٤٠٣/٦)
** إسناده حسن','3','390',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1166,'<span data-type="title" id=toc-1169>١١٦٦ </span>- عن ابن عباس قال: قام إبراهيم على الحجر، فقال: ياأيها الناس كتب عليكم الحج، فأسمع من فى أصلاب الرجال وأرحام النساء فأجابه من آمن ومن كان سبق فى علم الله أنه يحج إلى يوم القيامة: لبيك اللهم لبيك.
** الفاكهى
(فتح الباري ٤٠٦/٦)
** إسناده صحيح','3','391',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1167,'<span data-type="title" id=toc-1170>١١٦٧ </span>- عن على قال: كانت البيوت قبلة ولكنه كان أول بيت وضع لعبادة الله.
** ابن أبى حاتم
(فتح الباري ٤٠٨/٦)
** إسناده صحيح','3','392',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1168,'<span data-type="title" id=toc-1171>١١٦٨ </span>- حديث عبد الله بن عمرو بن العاص مرفوعا: " إن سليمان لما بنى بيت المقدس سأل الله خلالا ثلاثا ... " الحديث.
** ن
(فتح الباري ٤٠٨/٦)
** إسناده صحيح','3','393',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1169,'<span data-type="title" id=toc-1172>١١٦٩ </span>- عن أنس: إن أيوب عليه السلام ابتلى فلبث فى بلائه ثلاث عشرة سنة فرفضه القريب والبعيد إلا رجلين من إخوانه فكانا يغدوان إليه ويروحان، فقال أحدهما للآخر: لقد أذنب أيوب ذنبا عظيما وإلا لكشف عنه هذا البلاء فذكره الآخر لأيوب، يعنى فحزن ودعا الله حينئذ فخرج لحاجته وأمسكت امرأته بيده
فلما فرغ أبطأت عليه فأوحى الله إليه أن أركض برجلك فضرب برجله الأرض فنبعت عين فاغتسل منها فرجع صحيحا، فجاءت امرأته فلم تعرفه، فسألته عن أيوب فقال: إنى أنا هو وكان له أندران: أحدهما للقمح، والآخر للشعير، فبعث الله له سحابة فأفرغت فى أندر القمح الذهب حتى فاض وفى أندر الشعير الفضة حتى فاض.
** ابن أبى حاتم ابن جريج
(فتح الباري ٤٢١/٦)
** قال الحافظ فى " الفتح " ٦ / ٤٢١: صححه ابن حبان والحاكم.','3','394',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1170,'<span data-type="title" id=toc-1173>١١٧٠ </span>- عن ابن عباس فى قوله: (اشدد به أزرى) قال: ظهرى.
** أبو عبيدة
(فتح الباري ٤٢٥/٦)
** إسناده لين','3','395',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1171,'<span data-type="title" id=toc-1174>١١٧١ </span>- عن ابن عباس قال: هو ابن آدم لصلبه.
** قط فى الأفراد
(فتح الباري ٤٣٣/٦)
** ضعيف منقطع
** قال الحافظ فى " الفتح " ٦ / ٤٣٣: والخضر قد اختلف فى اسمه قبل ذلك وفى إسم أبيه ونسبه وفى نبوته وفى تعميره.','3','396',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1172,'<span data-type="title" id=toc-1175>١١٧٢ </span>- من طريق كثير بن عبد الله بن عمرو بن عوف عن أبيه عن جده: أن النبى صلى الله عليه وسلم سمع وهو فى المسجد كلاما فقال: " يا أنس أذهب إلى هذا القائل فقل له يستغفر لى "، فذهب إليه، فقال: قل له أن الله فضلك على الأنبياء بما فضل به رمضان على الشهور، قال فذهبوا ينظرون فإذا هو الخضر.
** عد
(فتح الباري ٤٣٤/٦)
** إسناده ضعيف','3','397',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1173,'<span data-type="title" id=toc-1176>١١٧٣ </span>- عن ابن عباس مرفوعا: " يجتمع الخضر وإلياس كل عام فى الموسم فيحلق كل واحد منهما رأس صاحبه ويفترقان عن هؤلاء الكلمات: بسم الله ماشاء الله.... " الحديث.
** قط فى الأفراد
(فتح الباري ٤٣٥/٦)
** قال الحافظ فى " الفتح " ٦ / ٤٣٥: فى إسناده محمد بن أحمد بن زيد وهو ضعيف.','3','398',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1174,'<span data-type="title" id=toc-1177>١١٧٤ </span>- ويشربان من ماء زمزم شربة تكفيهما إلى قابل.
** كر
(فتح الباري ٤٣٥/٦)
** معضل','3','399',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1175,'<span data-type="title" id=toc-1178>١١٧٥ </span>- عن ابن أبى رواد: يصومان رمضان ببيت المقدس.
** حمز
(فتح الباري ٤٣٥/٦)
** إسناده حسن','3','400',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1176,'<span data-type="title" id=toc-1179>١١٧٦ </span>- عن على أنه دخل الطواف فسمع رجلا يقول: يا من لا يشغله سمع عن سمع ... الحديث، فإذا هو الخضر.
** كر
(فتح الباري ٤٣٥/٦)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ٦ / ٤٣٥: أخرجه ابن عساكر من وجهين فى كل منهما ضعف.','3','401',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1177,'<span data-type="title" id=toc-1180>١١٧٧ </span>- عن أنس لما قبض النبى صلى الله عليه وسلم دخل رجل فتخطاهم _ فذكر الحديث فى التعزية _ فقال أبو بكر وعلى: هذا الخضر.
** ابن أبى الدنيا هق
(فتح الباري ٤٣٥/٦)
** إسناده واه
** قال الحافظ فى " الفتح " ٦ / ٤٣٥: فى إسناده عباد بن عبد الصمد وهو واه.','3','402',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1178,'<span data-type="title" id=toc-1181>١١٧٨ </span>- من طريق ابن المنكدر: أن عمر صلى على جنازة، فسمع قائلا يقول: لا تسبقنا _ فذكر القصة _ وفيها: أنه دعا للميت، فقال عمر: خذوا الرجل فتوارى عنهم فإذا أثر قدمه ذراع، فقال عمر: هذا والله الخضر.
** ابن وهب
(فتح الباري ٤٣٥/٦)
** منقطع
** قال الحافظ فى " الفتح " ٦ / ٤٣٥: فى إسناده مجهول مع انقطاعه.','3','403',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1179,'<span data-type="title" id=toc-1182>١١٧٩ </span>- عن نصر بن حزن قال: افتخر أهل الأبل والشاء فقال النبى صلى الله عليه وسلم: " بعث موسى وهو راعى غنم.... " الحديث.
