(فتح الباري ٤٨٨/٦)
** إسناده صحيح','3','417',NULL,NULL,'0');
INSERT INTO [b88] VALUES (1193,'<span data-type="title" id=toc-1196>١١٩٣ </span>- " من صلى على عند قبرى سمعته ومن صلى على نائيا بلغته ".
