INSERT INTO [b88] VALUES (1194,'<span data-type="title" id=toc-1197>١١٩٤ </span>- عن أوس بن أوس رفعه فى فضل يوم الجمعة: " فأكثروا على من الصلاة فيه فإن صلاتكم معروضة على " قالوا: يارسول الله وكيف تعرض صلاتنا عليك وقد أرحت قال: " إن الله حرم على الأرض أن تأكل أجساد الأنبياء ".
