INSERT INTO [b88] VALUES (1197,'<span data-type="title" id=toc-1200>١١٩٧ </span>- عن ابن عباس: لا يموت يهودى ولا نصرانى حتى يؤمن بعيسى فقا له عكرمة: أرأيت أن خر من بيت أو أحترق أو أكله السبع؟ قال: لا يموت حتى يحرك شفتيه بالإيمان بعيسى.
