INSERT INTO [b88] VALUES (1198,'<span data-type="title" id=toc-1201>١١٩٨ </span>- ينزل عيسى عليه ثوبان ممصران فيدق الصليب ويقتل الخنزير ويضع الجزية ويدعو الناس إلى الإسلام ويهلك الله فى زمانه الملل كلها إلا الإسلام وتقع الأمنة فى الأرض حتى ترتع الأسود مع الإبل وتلعب الصبيان بالحيات _ وقال فى آخره _ ثم يتوف ويصلى عليه المسلمون.
