INSERT INTO [b88] VALUES (1569,'<span data-type="title" id=toc-1573>١٥٦٩ </span>- حديث الحسين بن على رفعه: " إنى رأيت كأن بنى أمية يتعاورون منبرى هذا فقيل هى دنيا تنالهم ونزلت هذه الآية ".
