INSERT INTO [b88] VALUES (1571,'<span data-type="title" id=toc-1575>١٥٧١ </span>- حديث حذيفة قال: يجتمع الناس فى صعيد واحد فأول مدعو محمد فيقول: " لبيك وسعديك والخير فى يديك والشر ليس إليك المهتدى من هديت عبدك وابن عبديك وبك وإليك ولاملجأ ولا منجا منك إلا إليك تباركت وتعاليت "، فهذا قوله (عسى أن يبعثك ربك مقاما محمودا) .
