INSERT INTO [b88] VALUES (2007,'<span data-type="title" id=toc-2012>٢٠٠٧ </span>- حديث عبد الله بن عكيم قال: أتانا كتاب رسول الله صلى الله عليه وسلم قبل موته: أن لا تنتفعوا من الميتة بإهاب ولا عصب، وفى رواية للشافعى ولأحمد ولأبى داود: قبل موته بشهر.
