INSERT INTO [b88] VALUES (2009,'<span data-type="title" id=toc-2014>٢٠٠٩ </span>- عن أبى هريرة جاء أعرابى إلى النبى صلى الله عليه وسلم بأرنب قد شواها فوضعها بين يديه فأمسك وأمر أصحابه أن يأكلوا.
