** طحا
(فتح الباري ٤١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤١: تعقب بأنه ضعيف لأنه تفرد به حجاج بن أرطأة عن حماد بن أبى سليمان عن النخعى وحجاج هو ضعيف ومدلس أيضا، قال البيهقى: ذكر هذا لعبد الله بن المبارك فقال: هذا باطل.','5','271',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2047,'<span data-type="title" id=toc-2052>٢٠٤٧ </span>- عن أبى مسعود قال: عطش النبى صلى الله عليه وسلم وهو يطوف فأتى بنبيذ من السقاية فقطب فقيل: أحرام هو؟ قال: " لا على بذنوب من ماء زمزم من " فصب عليه وشرب.
** ن
(فتح الباري ٤١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤١: قد ضعف حديث أبى مسعود النسائى وأحمد وعبد الرحمن بن مهدى وغيرهم لتفرد يحيى بن يمان برفعه وهو ضعيف.','5','272',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2048,'<span data-type="title" id=toc-2053>٢٠٤٨ </span>- حديث جابر قال: قال رسول الله صلى الله عليه وسلم: " ما أسكر كثيره فقليله حرام ".
** د ن
(فتح الباري ٤٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٣: صححه ابن حبان، وللنسائى من حديث عمرو بن شعيب عن أبيه عن جده مثله، وسنده
إلى عمرو صحيح.','5','273',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2049,'<span data-type="title" id=toc-2054>٢٠٤٩ </span>- حديث ابن عباس رفعه: " حرمت الخمر قليلها وكثيرها والسكر من كل شراب ".
** ن
(فتح الباري ٤٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٣: رجاله ثقات إلا أنه أختلف من وصله وانقطاعه وفى رفعه ووقفه.','5','274',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2050,'<span data-type="title" id=toc-2055>٢٠٥٠ </span>- حديث عمر: كل مسكر حرام.
** يع
(فتح الباري ٤٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٤: فيه الأفريقى وحديث ابن مسعود عند ابن ماجه من طريق لين بلفظ عمر.','5','275',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2051,'<span data-type="title" id=toc-2056>٢٠٥١ </span>- حديث على: اجتنبو ما أسكر.
** حم
(فتح الباري ٤٤/١٠)
** حسن
** قال الحافظ فى " الفتح " ١٠ / ٤٤: أخرجه أحمد من وجه آخر لين أيضا بلفظ على.','5','276',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2052,'<span data-type="title" id=toc-2057>٢٠٥٢ </span>- حديث أنس: ما أسكر فهو حرام.
** حم
(فتح الباري ٤٤/١٠)
** إسناده صحيح','5','277',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2053,'<span data-type="title" id=toc-2058>٢٠٥٣ </span>- حديث ديلم الحميرى فيه: قال هل يسكر؟ قال: نعم، قال: فاجتنبوه.
** د
(فتح الباري ٤٤/١٠)
** إسناده حسن','5','278',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2054,'<span data-type="title" id=toc-2059>٢٠٥٤ </span>- حديث ميمونة: وكل شراب أسكر فهو حرام.
** حم
(فتح الباري ٤٤/١٠)
** إسناده حسن','5','279',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2055,'<span data-type="title" id=toc-2060>٢٠٥٥ </span>- حديث النعمان بن بشير: وإنى أنهاكم عن كل مسكر.
** د
(فتح الباري ٤٤/١٠)
** إسناده حسن','5','280',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2056,'<span data-type="title" id=toc-2061>٢٠٥٦ </span>- حديث أم سلمة: نهى عن كل مسكر ومفتر.
** د
(فتح الباري ٤٤/١٠)
** إسناده حسن','5','281',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2057,'<span data-type="title" id=toc-2062>٢٠٥٧ </span>- عن المختار بن فلفل يقول: سألت أنس فقال: نهى رسول الله صلى الله عليه وسلم عن المزفت وقال: " كل مسكر حرام "، قال: فقلت له: صدقت المسكر حرام، فالشربة والشربتان على الطعام؟ فقال: ما أسكر كثيره فقليله حرام.
** حم
(فتح الباري ٤٥/١٠)
** إسناده على شرط مسلم','5','282',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2058,'<span data-type="title" id=toc-2063>٢٠٥٨ </span>- عن الشعبى أن النعمان بن بشير قال: سمعت رسول الله صلى الله عليه وسلم يقول: " إن الخمر من العصير والزبيب والتمر والحنطة والشعير والذرة وإنى
أنهاكم عن كل مسكر ".
** د ت ن جه
(فتح الباري ٤٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٦: أخرجه أصحاب السنن الأربعة، صححه ابن حبان، اللفظ لأبى داود وكذا ابن حبان.','5','283',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2059,'<span data-type="title" id=toc-2064>٢٠٥٩ </span>- من حديث أنس قال: الخمر من العنب والتمر والعسل.
** حم
(فتح الباري ٤٦/١٠)
** إسناده صحيح','5','284',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2060,'<span data-type="title" id=toc-2065>٢٠٦٠ </span>- من حديث أنس قال: الخمر من العنب والتمر والعسل والحنطة والشعير والذرة
** حم
(فتح الباري ٤٦/١٠)
** إسناده صحيح','5','285',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2061,'<span data-type="title" id=toc-2066>٢٠٦١ </span>- حديث المختار بن فلفل: قلت لأنس: الخمر من العنب أو من غيرها؟ قال: ماخمرت عن ذلك فهو الخمر.
** شب
(فتح الباري ٤٨/١٠)
** إسناده صحيح','5','286',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2062,'<span data-type="title" id=toc-2067>٢٠٦٢ </span>- عن ابن عمر قال: أما الخمر فحرام لا سبيل إليها وأما ماعداها من الأشربة فكل مسكر حرام.
** عب
(فتح الباري ٥٠/١٠)
** إسناده جيد','5','287',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2063,'<span data-type="title" id=toc-2068>٢٠٦٣ </span>- عن أبى مالك الأشعرى عن النبى صلى الله عليه وسلم: ليشربن ناس الخمر يسمونها بغير أسمها.
** د
(فتح الباري ٥١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥١: صححه ابن حبان وله شواهد كثيرة.','5','288',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2064,'<span data-type="title" id=toc-2069>٢٠٦٤ </span>- ليستحلن طائفة من أمتى الخمر.
** حم
(فتح الباري ٥١/١٠)
** إسناده جيد','5','289',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2065,'<span data-type="title" id=toc-2070>٢٠٦٥ </span>- عن عائشة: سمعت رسول الله صلى الله عليه وسلم يقول: " إن أول ما يكفأ الإسلام كما يكفأ الإناء كفأ الخمر "، قيل: وكيف ذالك يارسول الله؟ قال: " يسمونها بغير إسمها فيستحلونها ".
** دى
(فتح الباري ٥٢/١٠)
** إسناده لين
** قال الحافظ فى " الفتح " ١٠ / ٥٢: أخرجه ابن أبى عاصم من وجه آخر عن عائشة.','5','290',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2066,'<span data-type="title" id=toc-2071>٢٠٦٦ </span>- من حديث على: يوشك أن تستحل أمتى فروج النساء والحرير.
** مب
(فتح الباري ٥٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٥: وقع عند الداودى بالمعجمتين ثم تعقبه بأنه ليس بمحفوظ لأن كثيرا من الصحابة لبسوه.','5','291',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2067,'<span data-type="title" id=toc-2072>٢٠٦٧ </span>- من حديث الأشجع العصرى أن النبى صلى الله عليه وسلم قال لهم: " مالى أرى وجوهكم قد تغيرت؟ "، قالوا: نحن بأرض وخمة وكنت نتخذ من هذه الأنبذة مايقطع اللحمان فى بطوننا فلما نهيتنا عن الظروف فذلك الذى ترى فى وجوهنا، فقال النبى صلى الله عليه وسلم: " إن الظروف لا تحل ولا تحرم، ولكن كل مسكر حرام ".
** يع
(فتح الباري ٥٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٩: صححه ابن حبان.','5','292',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2068,'<span data-type="title" id=toc-2073>٢٠٦٨ </span>- عن ابن مسعود: أنه كان ينبذ له فى الجر الأخضر.
** شب
(فتح الباري ٦١/١٠)
** إسناده صحيح','5','293',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2069,'<span data-type="title" id=toc-2074>٢٠٦٩ </span>- من طريق محمود بن لبيد الأنصارى: أن عمر بن الخطاب حين قدم الشام شكا إليه أهل الشام وباء الأرض وثقلها، وقالوا: لا يصلحنا إلا هذا الشراب فقال عمر: اشربوا العسل، قالوا: ما يصلحنا العسل، فقال: رجال من أهل الأرض هل لك أن نجعل لك من هذا الشراب شيئا لا يسكر؟ ، فقال: نعم، فطبخوه حتى ذهب منه ثلثان وبقى الثلث، فأتوا به عمر فأدخل فيه إصبعه ثم رفع يده فتبعها يتمطط، فقال: هذا الطلاء مثل طلاء الإبل فأمرهم عمر أن يشربوه وقال عمر: اللهم إنى لا أحل لهم شيئا حرمته عليهم.
** ما
(فتح الباري ٦٣/١٠)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٠ / ٦٣: هذه الأسانيد صحيحة.','5','294',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2070,'<span data-type="title" id=toc-2075>٢٠٧٠ </span>- عن عامر بن عبد الله قال: كتب عمر إلى عمار: أما بعد فإنه جاءنى عير تحمل شرابا أسود كأنه طلاء الإبل فذكروا أنهم يطبخونه حتى يذهب ثلثاه الأخبثان: ثلث بريحه وثلث ببغيه، فمر من قبلك أن يشربوه.
** ص
(فتح الباري ٦٣/١٠)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٠ / ٦٣: هذه الأسانيد صحيحة.','5','295',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2071,'<span data-type="title" id=toc-2076>٢٠٧١ </span>- من طريق سعيد بن المسيب: أن عمر أحل من الشراب ما طبخ فذهب ثلثاه وبقى ثلثه.
** ص
(فتح الباري ٦٣/١٠)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٠ / ٦٣: هذه الأسانيد صحيحة.','5','296',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2072,'<span data-type="title" id=toc-2077>٢٠٧٢ </span>- من طريق عبد الله بن يزيد الخطمى قال: كتب عمر اطبخوا شرابكم حتى يذهب نصيب الشيطان منه، فإن للشيطان اثنين ولكن واحد.
** ن
(فتح الباري ٦٣/١٠)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٠ / ٦٣: هذه الأسانيد صحيحة.','5','297',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2073,'<span data-type="title" id=toc-2078>٢٠٧٣ </span>- عن ابن عباس: أن النار لا تحل شيئا ولا تحرمه.
** ن
(فتح الباري ٦٤/١٠)
** إسناده صحيح','5','298',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2074,'<span data-type="title" id=toc-2079>٢٠٧٤ </span>- عن الزهرى عن السائب بن يزيد أنه أخبره أن عمر بن الخطاب خرج عليهم فقال: إنى
وجدت من فلان ريح شراب، فزعم أنه شراب الطلاء، وإنى سائل عما شرب، فإن كان يسكر جلدته، فجلده عمر الحد تاما.
** ما
(فتح الباري ٦٥/١٠)
** إسناده صحيح','5','299',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2075,'<span data-type="title" id=toc-2080>٢٠٧٥ </span>- عن ابن عباس: حرمت الخمر بعينها ... الحديث.
** لم
(فتح الباري ٦٦/١٠)
** ضعيف','5','300',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2076,'<span data-type="title" id=toc-2081>٢٠٧٦ </span>- عن ابن عباس قال: إن النار لا تحل شيئا ولا تحرمه.
** هق
(فتح الباري ٦٦/١٠)
** إسناده صحيح','5','301',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2077,'<span data-type="title" id=toc-2082>٢٠٧٧ </span>- حديث عائشة رضى الله عنها: كان رسول الله صلى الله عليه وسلم يستعذب له الماء من بيوت السقيا.
** د
(فتح الباري ٧٤/١٠)
** إسناده جيد
** قال الحافظ فى " الفتح " ١٠ / ٧٤: صححه الحاكم.','5','302',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2078,'<span data-type="title" id=toc-2083>٢٠٧٨ </span>- عن ابن عمر قال: مررنا على بركة فجعلنا نكرع فيها، فقال رسول الله صلى الله
عليه وسلم: " لا تكرعوا ولكن اغسلوا أيديكم ثم اشربوا بها ".
** جه
(فتح الباري ٧٧/١٠)
** إسناده ضعيف','5','303',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2079,'<span data-type="title" id=toc-2084>٢٠٧٩ </span>- عن ابن عمر فقال: نهانا رسول الله صلى الله عليه وسلم أن نشرب على بطوننا.
** جه
(فتح الباري ٧٧/١٠)
** إسناده ضعيف','5','304',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2080,'<span data-type="title" id=toc-2085>٢٠٨٠ </span>- قال عبد الله هو ابن مسعود: لا تسقوا أولادكم الخمر فإنهم ولدوا على الفطرة وإن الله لم يجعل شفاءكم فيما حرم عليكم.
** داود بن نصير الطائى
(فتح الباري ٧٩/١٠)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٠ / ٧٩: أخرجه ابن أبى شيبة من وجه آخر عن ابن مسعود.','5','305',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2081,'<span data-type="title" id=toc-2086>٢٠٨١ </span>- من حديث أم سلمة قالت: اشتكت بنت لى فنبذت لها فى كوز، فدخل النبى صلى الله عليه وسلم وهو يغلى فقال: " ماهذا "، فأخبرته، فقال: " إن الله لم يجعل شفاءكم فيما حرم عليكم ".
** يع
(فتح الباري ٧٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٧٩: صححه ابن حبان.','5','306',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2082,'<span data-type="title" id=toc-2087>٢٠٨٢ </span>- من طريق أبى صالح عنه: لو يعلم الذى يشرب وهو قائم لاستقاء.
** حم
(فتح الباري ٨٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٨٢: صححه ابن حبان.','5','307',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2083,'<span data-type="title" id=toc-2088>٢٠٨٣ </span>- عن أبى هريرة: أنه صلى الله عليه وسلم رأى رجلا يشرب قائما فقال: " قه " قال: لمه، قال: " أيسرك أن يشرب معك الهد "، قال: لا، قال: " قد شرب معك من هو شر منه الشيطان ".
** حم
(فتح الباري ٨٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٨٢: هو من رواية شعبة عن أبى زياد الطحان مولى الحسن بن على عنه، وأبو زياد لا
يعرف أسمه وقد وثقه يحيى بن معين.','5','308',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2084,'<span data-type="title" id=toc-2089>٢٠٨٤ </span>- حديث ابن عمر: كنا نأكل على عهد رسول الله صلى الله عليه وسلم ونحن نمشى، ونشرب ونحن قيام.
** لم
(فتح الباري ٨٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٨٤: صححه الترمذى.','5','309',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2085,'<span data-type="title" id=toc-2090>٢٠٨٥ </span>- عن كبشة قالت: دخلت على النبى صلى الله عليه وسلم فشرب من قربة معلقة.
** ت
(فتح الباري ٨٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٨٤: صححه الترمذى، وعن كلثم نحوه أخرجه أبو موسى بسند حسن.','5','310',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2086,'<span data-type="title" id=toc-2091>٢٠٨٦ </span>- حديث ابن عباس قال: كان رسول الله صلى الله عليه وسلم إذا سقى قال: " ابدءوا بالكبير ".
** يع
(فتح الباري ٨٧/١٠)
** إسناده قوى','5','311',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2087,'<span data-type="title" id=toc-2092>٢٠٨٧ </span>- من حديث عائشة: نهى أن يشرب من فى السقاء لأن ذلك ينتنه.
** ك
(فتح الباري ٩١/١٠)
** إسناده قوى','5','312',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2088,'<span data-type="title" id=toc-2093>٢٠٨٨ </span>- من حديث عبد الرحمن بن أبى عمرة عن جدته كبشة قالت: دخلت على رسول الله صلى
الله عليه وسلم فشرب من فى قربة معلقة.
** ت
(فتح الباري ٩٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٩٢: صححه الترمذى.','5','313',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2089,'<span data-type="title" id=toc-2094>٢٠٨٩ </span>- حديث أبى سعيد أن النبى صلى الله عليه وسلم نهى عن النفخ فى الشراب فقال رجل: القناة أراها فى الإناء، قال: " أهرقها "، قال: فإنى لا أروى من نفسى واحد، قال: " فأين القدح إذا عنا فيك ".
** ت
(فتح الباري ٩٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٩٣: صححه الحاكم.','5','314',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2090,'<span data-type="title" id=toc-2095>٢٠٩٠ </span>- عن ابن عباس رفعه: " لا تشربوا واحدة كما يشرب البعير، ولكن اشربوا مثنى وثلاث ".
** ت
(فتح الباري ٩٣/١٠)
** إسناده ضعيف','5','315',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2091,'<span data-type="title" id=toc-2096>٢٠٩١ </span>- عن ابن عباس: أن النبى صلى الله عليه وسلم كان إذا شرب تنفس مرتين.
** ت
(فتح الباري ٩٣/١٠)
** إسناده ضعيف','5','316',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2092,'<span data-type="title" id=toc-2097>٢٠٩٢ </span>- عن أبى هريرة: أن النبى صلى الله عليه وسلم كان يشرب فى ثلاثة أنفاس إذا أدنى الإناء إلى فيه يسمى الله، فإذا أخره حمد الله، يفعل ذلك ثلاثا.
** طس
(فتح الباري ٩٤/١٠)
** إسناده حسن
** قال الحافظ فى " الفتح " ١٠ / ٩٤: أصله فى ابن ماجه وله شاهد من حديث ابن مسعود عند البزار والطبرانى.','5','317',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2093,'<span data-type="title" id=toc-2098>٢٠٩٣ </span>- عن أبى هريرة رفعه: " من شرب من أنية الفضة والذهب فىالدنيا لم يشرب فيهما فى الآخرة وآنية أهل الجنة الذهب والفضة ".
** ن
(فتح الباري ٩٧/١٠)
** إسناده قوى','5','318',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2094,'<span data-type="title" id=toc-2099>٢٠٩٤ </span>- حديث أم عطية أن النبى صلى الله عليه وسلم نهى عن لبس الذهب وتفضيض الأقداح ثم رخص فى تفضيض الأقداح.
** طس
(فتح الباري ١٠١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٠١: هذا لو ثبت لكان حجة فى الجواز، لكن فى سنده من لايعرف.','5','319',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2095,'<span data-type="title" id=toc-2100>٢٠٩٥ </span>- عن عائشة أن رجلا تلا هذه الآية (من يعمل سوءا يجز به) فقال: إنا لنجزى بكل ماعملناه؟ هلكنا إذا، فبلغ ذلك النبى صلى الله عليه وسلم فقال: " نعم يجزى به فى الدنيا من مصيبة فى جسده مما يؤذيه ".
** حم
(فتح الباري ١٠٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٠٤: صححه ابن حبان.','5','320',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2096,'<span data-type="title" id=toc-2101>٢٠٩٦ </span>- من حديث أبى بكر الصديق أنه قال: يارسول الله كيف الصلاح بعد هذه الآية (ليس بأمانيكم ولا أمانى أهل الكتاب من يعمل سوء يجز به) ؟ فقال: " غفر الله لك ياأبا بكر ألست تمرض، ألست تحزن؟ "، قال: قلت: بلى، قال: " هو ماتجزون به ".
** حم
(فتح الباري ١٠٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٠٤: صححه ابن حبان.','5','321',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2097,'<span data-type="title" id=toc-2102>٢٠٩٧ </span>- عن عائشة: ما ضرب على مؤمن عرق قط إلا وحط الله به عنه خطيئة، وكتب له حسنة ورفع له درجة.
** طس
(فتح الباري ١٠٥/١٠)
** إسناده جيد','5','322',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2098,'<span data-type="title" id=toc-2103>٢٠٩٨ </span>- من طريق عبد الرحمن بن شيبة العبدى، أن عائشة أخبرته أن رسول الله صلى الله عليه وسلم طرقه وجع فجعل يتقلب على فراشه ويشكى فقالت له عائشة: لو صنع هذا بعضنا لوجدت عليه، فقال: " إن الصالحين يشدد عليهم وإنه لا
يصيبا المؤمن نكبة شوكة.... " الحديث.
** حم
(فتح الباري ١٠٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٠٥: صححه أبو عوانة والحاكم.','5','323',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2099,'<span data-type="title" id=toc-2104>٢٠٩٩ </span>- حديث محمود بن لبيد رفعه: " إذا أحب الله قوما ابتلاهم فمن صبر منه الصبر ومن جذع منه الجزع ".
** حم
(فتح الباري ١٠٨/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٠٨: رواته ثقات إلا أن محمود بن لبيد اختلف فى سماعه من النبى صلى الله عليه وسلم وقد رآه وهو صغير.','5','324',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2100,'<span data-type="title" id=toc-2105>٢١٠٠ </span>- حديث محمد بن خالد عن أبيه عن جده وكانت له صحبة: سمعت رسول الله صلى الله عليه وسلم يقول: " إن العبد إذا سبقت له من الله منزلة فلم يبلغها بعمل ابتلاه فى جسده أو ولده أو ماله ثم صبر على ذلك حتى يبلغ تلك المنزلة ".
** حم د
(فتح الباري ١٠٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٠٩: رواته ثقات إلا أن خالد لم يرو عنه غير ابنه محمد، وأبوه اختلف فى اسمه لكن
إيهام الصحابى لا يضر.','5','325',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2101,'<span data-type="title" id=toc-2106>٢١٠١ </span>- حديث سنجره _ بمهملة ثم معجمة ثم موحدة وزن مسلمة _ رفعه: " من أعطى فشكر وابتلى فصبر وظلم فاستغفر وظلم فغفر، أولئك لهم الأمن وهم مهتدون ".
** طب
(فتح الباري ١٠٩/١٠)
** إسناده حسن','5','326',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2102,'<span data-type="title" id=toc-2107>٢١٠٢ </span>- من طريق عياض بن غطيف قال: دخلنا على أبى عبيدة نعوده من شكوى أصابته فقلنا: كيف بت أبو عبيدة؟ ، فقالت امرأته نحيفة: لقد بات بأجر، فقال أبو عبيدة: مابت بأجر، سمعت رسول الله صلى الله عليه وسلم يقول: " من ابتلاه الله ببلاء فى جسده فهو له حطة ".
** ن
(فتح الباري ١٠٩/١٠)
** إسناده جيد
** قال الحافظ فى " الفتح " ١٠ / ١٠٩: صححه الحاكم.','5','327',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2103,'<span data-type="title" id=toc-2108>٢١٠٣ </span>- عن أبى هريرة قال: وما من مرض يصيبنى أحب إلى من الحمى، لأنها تدخل فى كل عضو منى، وإن الله يعطى كل عضو قسطه من الأجر.
** خد
(فتح الباري ١١٠/١٠)
** إسناده صحيح','5','328',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2104,'<span data-type="title" id=toc-2109>٢١٠٤ </span>- عن جابر قال: استأذنت الحمى على رسول الله صلى الله عليه وسلم فأمر بها إلى أهل قباء فشكوا إليه ذلك فقال: " ماشئتم إن شئتم دعوت الله لكم فكشفها عنكم وإن شئتم أن تكون لكم طهورا "، قالوا: فدعها.
** حم
(فتح الباري ١١٠/١٠)
** إسناده جيد','5','329',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2105,'<span data-type="title" id=toc-2110>٢١٠٥ </span>- عن مصعب بن سعد بن أبى وقاص عن أبيه قال: قلت: يارسول الله أى الناس أشد بلاء؟ ، قال: " الأنبياء، ثم الأمثل فالأمثل يبتلى الرجل على حسب دينه.. " الحديث وفيه: " حتى يمشى على الأرض وماعليه خطيئة ".
** دى نب ده
(فتح الباري ١١١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١١١: صححه الترمذى وابن حبان والحاكم وأخرجه الحاكم من رواية العلاء بن المسيب عن مصعب أيضا.','5','330',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2106,'<span data-type="title" id=toc-2111>٢١٠٦ </span>- من حديث فاطمة بنت اليمان أخت حذيفة قالت: أتيت النبى صلى الله عليه وسلم فى نساء نعوده فإذا بسقاء يقطر عليه من شدة الحمى، فقال: " إن من أشد الناس
بلاء الأنبياء، ثم الذين يلونهم ثم الذين يلونهم ".
** ن
(فتح الباري ١١١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١١١: صححه الحاكم.','5','331',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2107,'<span data-type="title" id=toc-2112>٢١٠٧ </span>- حديث زيد بن أرقم قال: عادنى رسول الله صلى الله عليه وسلم فى وجع كان بعينى
** د
(فتح الباري ١١٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١١٣: صححه الحاكم وهو عند البخارى فى الأدب المفرد وسياقه أتم.','5','332',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2108,'<span data-type="title" id=toc-2113>٢١٠٨ </span>- عن أنس: كان النبى صلى الله عليه وسلم لا يعود مريضا إلا بعد ثلاث.
** جه
(فتح الباري ١١٣/١٠)
** ضعيف جدا
** قال الحافظ فى " الفتح " ١٠ / ١١٣: تفرد به سلمة بن على وهو متروك، وقد سئل عنه أبو حاتم فقال: هو حديث باطل ووجدت له شاهدا من حديث أبى هريرة عندالطبرانى فى الأوسط وفيه راو متروك أيضا.','5','333',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2109,'<span data-type="title" id=toc-2114>٢١٠٩ </span>- عن عائشة قالت: كان رسول الله صلى الله عليه وسلم إذا عاد مريضا يضع يده على
المكان الذى يألم ثم يقول: "بسم الله ".
** يع
(فتح الباري ١٢١/١٠)
** إسناده حسن','5','334',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2110,'<span data-type="title" id=toc-2115>٢١١٠ </span>- حديث أبى أمامة رفعه: " تمام عيادة المريض أن يضع أحدكم يده على جبهته فيسأله كيف هو ".
** ت
(فتح الباري ١٢١/١٠)
** إسناده لين','5','335',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2111,'<span data-type="title" id=toc-2116>٢١١١ </span>- عن عمر رفعه: " إذا دخلت على مريض فمره يدعو لك فإن دعاءه كدعاء الملاءكة "
** جه
(فتح الباري ١٢٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٢٢: بسند حسن لكن فيه انقطاع.','5','336',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2112,'<span data-type="title" id=toc-2117>٢١١٢ </span>- عن أنس رفعه: " أن أيوب لما طال بلاؤه رفضه القريب والبعيد غير رجلين من إخوانه فقال أحدهما لصاحبه: لقد أذنب أيوب ذنبا ما أذنبه أحد من العالمين، فبلغ ذلك أيوب _ يعنى فجزع من قوله _ ودعا ربه فكشف مابه ".
** فوائد ميمونة
(فتح الباري ١٢٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٢٤: صححه ابن حبان والحاكم.','5','337',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2113,'<span data-type="title" id=toc-2118>٢١١٣ </span>- حديث معاذ فى القول فى دبر كل صلاة وفيه: وإذا أردت بقوم فتنة فتوفنى إليك غير مفتون.
** د
(فتح الباري ١٢٨/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٢٨: صححه الحاكم.','5','338',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2114,'<span data-type="title" id=toc-2119>٢١١٤ </span>- من طريق عمر بن إسماعيل بن مجالد، حدثنا أبى عن بيان بن بشر وإسماعيل ابن أبى خالد جميعا عن قيس عن أبى حازم قال: دخلنا على خباب نعوده...... فذكر الحديث وفيه: وهو يعالج حائطا له فقال: إن رسول الله صلى الله عليه وسلم قال: " إن المسلم يؤجر فى نفقته كلها إلا ما يجعله فى التراب ".
** طب
(فتح الباري ١٢٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٢٩: عمر كذبه يحيى بن معين.','5','339',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2115,'<span data-type="title" id=toc-2120>٢١١٥ </span>- حديث أبى أمامة: أن النبى صلى الله عليه وسلم قال لسعد: " يا سعد إن كنت
خلقت للجنة فما طال من عمرك أو حسن من عملك فهو خير لك ".
** لم
(فتح الباري ١٣٠/١٠)
** إسناده لين','5','340',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2116,'<span data-type="title" id=toc-2121>٢١١٦ </span>- عن زيد بن أسلم مرسلا: أن النبى صلى الله عليه وسلم قال لرجلين: " أيكما أطب؟ "، قالا: يارسول الله وفى الطب خير؟ ، قال: " أنزل الدواء الذى أنزل الدواء ".
** ما
(فتح الباري ١٣٤/١٠)
** مرسل','5','341',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2117,'<span data-type="title" id=toc-2122>٢١١٧ </span>- عن ابن مسعود رفعه: " إن الله لم ينزل داء إلا أنزل له شفاء فتداوو ".
** ن
(فتح الباري ١٣٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٣٥: صححه ابن حبان والحاكم.','5','342',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2118,'<span data-type="title" id=toc-2123>٢١١٨ </span>- حديث أسامة بن شريك: تداووا يا عباد الله، فإن الله لم يصنع داء إلا وضع له شفاء، إلا داء واحد الهرم.
** حم خد د ت ن جه
(فتح الباري ١٣٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٣٥: صححه الترمذى وابن خزيمة والحاكم.','5','343',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2119,'<span data-type="title" id=toc-2124>٢١١٩ </span>- حديث المغيرة رفعه: " من اكتوى أو استرقى فقد برىء من التوكل ".
** ت ن
(فتح الباري ١٣٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٣٩: صححه ابن حبان والحاكم.','5','344',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2120,'<span data-type="title" id=toc-2125>٢١٢٠ </span>- حديث ابن عمر رفعه فى أثناء حديث وفيه: " فاحتجموا على بركة الله يوم الخميس، واحتجموا يوم الإثنين والثلاثاء، واحتجموا الحجامة يوم الأربعاء والجمعة والسبت والأحد ".
** جه
(فتح الباري ١٤٩/١٠)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ١٠ / ١٤٩: أخرجه من طريقين ضعيفين، وله طريق ثالثة ضعيفة أيضا عند الدارقطنى فى
الأفراد، وأخرجه بسند جيد عن ابن عمر موقوفا.','5','345',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2121,'<span data-type="title" id=toc-2126>٢١٢١ </span>- حديث أبى هريرة رفعه: " من احتجم بسبع عشرة وتسع عشرة وإحدى وعشرين كان شفاء من كل داء ".
** د
(فتح الباري ١٥٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٥٠: هو من رواية سعيد بن عبد الرحمن الجمحى عن سهيل بن صالح، وسعيد وثقه الأكثر ولينه بعضهم من قبل حفظه، وله شاهد من حديث ابن عباس عند أحمد والترمذى ورجاله ثقات لكنه معلول، وله شاهد آخر من حديث أنس عند ابن ماجه، وسنده ضعيف.','5','346',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2122,'<span data-type="title" id=toc-2127>٢١٢٢ </span>- عنابن عباس رفعه: " الحجامة فى الرأس تنفع من سبع: من الجنون والجذام والبرص والنعاس والصداع ووجع الضرس والعين ".
** عد
(فتح الباري ١٥٢/١٠)
** ضعيف','5','347',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2123,'<span data-type="title" id=toc-2128>٢١٢٣ </span>- عن قتادة عنه قال: احتجم النبى صلى الله عليه وسلم وهو محرم على ظهر القدم من وجع كان به.
** د تم ن
(فتح الباري ١٥٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٥٤: صححه ابن خزيمة وابن حبان ورجاله رجال الصحيح إلا أن أبا داود حكى عن أحمد أن سعيد بن أبى عروبة رواه عن قتادة فأرسله، وسعيد أحفظ من معمر، وليست هذه بعلة قادحة.','5','348',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2124,'<span data-type="title" id=toc-2129>٢١٢٤ </span>- عن أنس قال: كوانى أبو طلحة فى زمن النبى صلى الله عليه وسلم.
** طحا
(فتح الباري ١٥٥/١٠)
