INSERT INTO [b88] VALUES (2136,'<span data-type="title" id=toc-2141>٢١٣٦ </span>- عن على: إذا اشتكى أحدكم فليستوهب من امرأته من صداقها فليشتر به عسلا، ثم يأخذ ماء السماء فيجمع هنيئا مريئا شفاء مباركا.
