INSERT INTO [b88] VALUES (2137,'<span data-type="title" id=toc-2142>٢١٣٧ </span>- قال سمرة: كان رسول الله صلى الله عليه وسلم إذا حم دعا بقربة ماء فأفرغها على قرنه فاغتسل.
** بز
(فتح الباري ١٧٧/١٠)
** إسناده فيه راو ضعيف
** قال الحافظ فى " الفتح " ١٠ / ١٧٧: صححه الحاكم.','5','362',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2138,'<span data-type="title" id=toc-2143>٢١٣٨ </span>- حديث ثوبان مرفوعا: " إذا أصاب أحدكم الحمى _ وهى قطعة من النار _ فليطفئها عنه بالماء، يستنقع فى نهر جار ويستقبل جريته وليقل: بسم الله، اللهم اشف عبدك وصدق رسولك بعد صلاة الصبح قبل طلوع الشمس، ولينغمس فيه ثلاث غمسات ثلاثة أيام، فإن لم يبرأ فخمس، وإلا فسبع وإلا فتسع، فإنها لا تكاد تجاوز تسعا بإذن الله ".
** ت
(فتح الباري ١٧٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٧٦: قال الترمذى: غريب، قلت: وفى سنده سعيد بن زرعة مختلف فيه.','5','363',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2139,'<span data-type="title" id=toc-2144>٢١٣٩ </span>- قال أنس: إذا حم أحدكم فليشن عليه من الماء البارد من السحر ثلاث ليال.
** طحا أبو نعيم فى الطب طس
(فتح الباري ١٧٧/١٠)
** إسناده قوى
** قال الحافظ فى " الفتح " ١٠ / ١٧٧: صححه الحاكم.','5','364',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2140,'<span data-type="title" id=toc-2145>٢١٤٠ </span>- عن أبى موسى رفعه: " فناء أمتى بالطعن والطاعون "، قيل: يارسول الله هذا الطاعن قد عرفناه، فما الطاعون؟ ، قال: " وخز أعدائكم من الجن، وفى كل شهادة ".
** حم
(فتح الباري ١٨١/١٠)
** رجاله رجال الصحيح إلا المبهم','5','365',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2141,'<span data-type="title" id=toc-2146>٢١٤١ </span>- عن أبى بكر بن أبى موسى الأشعرى قال: سألت عنه رسول الله صلى الله عليه وسلم فقال: " هو وخز أعدائكم من الجن وهو لكم شهادة ".
** حم طب
(فتح الباري ١٨٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٨٢: رجاله رجال الصحيح إلا أبا بلج _ بفتح الموحدة وسكون اللام بعدها جيم _ واسمه يحيى، وثقه ابن معين والنسائى وجماعة وضعفه جماعة بسبب التشيع، وذلك لايقدح فى قبول روايته عند الجمهور.','5','366',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2142,'<span data-type="title" id=toc-2147>٢١٤٢ </span>- عن أبى بردة بن قيس أخر أبى موسى الأشعرى رفعه: " اللهم اجعل فناء أمتى فى سبيلك بالطعن والطاعون ".
** حم
(فتح الباري ١٨٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٨٢: صححه الحاكم.','5','367',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2143,'<span data-type="title" id=toc-2148>٢١٤٣ </span>- عن سيار: أن رجلا كان يقال له بلعام كان مجاب الدعوة وأن موسى أقبل فى بنى إسرائيل يريد الأرض التى فيها بلعام فأتاه قومه فقالوا: ادع الله عليهم، فقال: حتى أؤامر ربى، فمنع فأتوه بهدية فقبلها وسألوه ثانيا فقال: حتى أؤامر ربى فلم يرجع إليه بشىء، فقالوا: لو كره لنهاك فدعا عليهم فصار يجرى على لسانه ما يدعو به على بنى إسرائيل فينقلب على قومه فلاموه على ذلك فقال:
سأدلكم على مافيه هلاكهم أرسلوا النساء فى عسكرهم ومرونهن أن لا يمتنعن من
أحد، فعسى أن يزنوا فيهلكوا، فكان فيمن خرج بنت الملك فأرادها رأس بعض الأسباط وأخبرها بمكانه فمكنته من نفسها، فوقع فى بنى إسرائيل الطاعون، فمات منهم نحو سبعون ألف فى يوم، وجاء رجل من بنى هارون ومعه الرمح فطعنهما
وأيده الله فأنتظمهما جميعا.
** الطبرى
(فتح الباري ١٨٣/١٠)
** مرسل جيد','5','368',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2144,'<span data-type="title" id=toc-2149>٢١٤٤ </span>- من طريق سعيد بن جبير: أمر موسى بنى إسرائيل أن يذبح كل رجل منهم كبشا ثم ليخضب كفه فى دمه، ثم ليضرب به على بابه ففعلوا، فسألهم القبط عن ذلك فقالوا: إن الله سيبعث عليكم عذابا وإنما ننجو منه لهذه العلامة، فأصبحوا وقد مات من قوم فرعون سبعون ألفا، فقال فرعون عند ذلك لموسى: (ادع لن ربك بما عهد عندك للئن كشفت عنا الرجز) الآية، فدعا فكشفه عنهم.
** الطبرى ابن أبى حاتم
(فتح الباري ١٨٣/١٠)
** إسناده جيد مرسلا','5','369',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2145,'<span data-type="title" id=toc-2150>٢١٤٥ </span>- عن أنس: أن عمر أتى الشام فاستقبله أبو طلحة وأبو عبيدة فقالا: يا أمير المؤمنين إن معك وجوه الصحابة وخيارهم، وإنا تركنا من بعدنا مثل حريق النار فارجع العام فرجع.
** طحا
(فتح الباري ١٨٦/١٠)
** إسناده صحيح','5','370',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2146,'<span data-type="title" id=toc-2151>٢١٤٦ </span>- عن ابن عمر قال: جئت عمر حين قدم فوجدته قائلا فى خبائه، فانتظرته فى ظل الخباء فسمعته يقول حين تضور: اللهم اغفر لى رجوعى من سرغ.
** شب
(فتح الباري ١٨٧/١٠)
** إسناده جيد','5','371',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2147,'<span data-type="title" id=toc-2152>٢١٤٧ </span>- قال عمر: اللهم إن الناس قد نحلونى ثلاثا أنا أبرأ إليك منهن زعموا أنى فررت من الطاعون وأنا أبرأ إليك من ذلك.
** طحا
(فتح الباري ١٨٧/١٠)
** إسناده صحيح','5','372',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2148,'<span data-type="title" id=toc-2153>٢١٤٨ </span>- عن هشام بن عروة عن أبيه أن الزبير بن العوام خرج غازيا نحو مصر، فكتب إليه
أمراء مصر أن الطاعون قد وقع فقال: إنما خرجنا للطعن والطاعون، فدخلها فلقى
طعنا فى جبهته ثم سلم.
** خز
(فتح الباري ١٨٧/١٠)
** إسناده صحيح','5','373',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2149,'<span data-type="title" id=toc-2154>٢١٤٩ </span>- عن أبى منيب: أن عمرو بن العاص قال فى الطاعون: إن هذا رجز مثل السيل من
تنكبه أخطأه، ومثل النار من أقام أحرقته، فقال شرحبيل بن حسنة: إن هذا رحمة ربكم ودعوة نبيكم وقبض الصالحين قبلكم.
** حم
(فتح الباري ١٨٧/١٠)
** إسناده صحيح','5','374',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2150,'<span data-type="title" id=toc-2155>٢١٥٠ </span>- من حديث عائشة مرفوعا: قلت: يارسول الله فما الطاعون؟ ، قال: " غدة كفدة الإبل المقيم فيها كالشهيد، والفار منها كالفار من الزحف ".
** حم خز
(فتح الباري ١٨٨/١٠)
** إسناده حسن','5','375',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2151,'<span data-type="title" id=toc-2156>٢١٥١ </span>- حديث جابر مرفوعا: " الفار من الطاعون كالفار من الزحف ".
** حم خز
(فتح الباري ١٨٨/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٨٨: سنده صالح للمتابعات.','5','376',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2152,'<span data-type="title" id=toc-2157>٢١٥٢ </span>- عن أبى موسى أنه قال: إن هذا الطاعون قد وقع فمن أراد أن يتنزه عنه فليفعل واحذروا اثنتين: أن يقول قائل خرج خارج فسلم، وجلس جالس فأصيب فلو كنت خرجت لسلمت كما سلم فلان، أو لو كنت جلست أصبت كما أصيب فلان.
** الهيثم بن كليب طحا هق
(فتح الباري ١٨٨/١٠)
** إسناده حسن','5','377',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2153,'<span data-type="title" id=toc-2158>٢١٥٣ </span>- حديث أبى هريرة: المدينة ومكة محفوفتان بالملائكة على كل نقب منهما ملك لا يدخلها الدجال ولا الطاعون.
** عمر بن شبة فى كتاب مكة
(فتح الباري ١٩١/١٠)
** رجاله رجال الصحيح','5','378',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2154,'<span data-type="title" id=toc-2159>٢١٥٤ </span>- حديث ابن عمر: لم تظهر الفاحشة فى قوم قط حتى يعلنوا بها إلا فشا فيهم الطاعون والأوجاع التى لم تكن مضت فى أسلافهم.. الحديث.
** جه هق
(فتح الباري ١٩٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٩٣: فى إسناده خالد بن يزيد ابن مالك، وكان من فقهاء الشام لكنه ضعيف عند أحمد وابن معين وغيرهما، ووثقه أحمد بن صالح المصرى وأبو زرعة الدمشقى وقال ابن حبان: كان يخطىء كثيرا.','5','379',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2155,'<span data-type="title" id=toc-2160>٢١٥٥ </span>- عن ابن عباس: ولا فشا الزنا فى قوم قط إلا وكثر فيهم الموت ... الحديث.
** ما
(فتح الباري ١٩٣/١٠)
** منقطع
** قال الحافظ فى " الفتح " ١٠ / ١٩٣: فيه انقطاع.','5','380',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2156,'<span data-type="title" id=toc-2161>٢١٥٦ </span>- حديث عمرو بن العاص: ما من قوم يظهر فيهم الزنا إلا أخذوا بالفناء. الحديث
** طب
(فتح الباري ١٩٣/١٠)
** إسناده ضعيف','5','381',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2157,'<span data-type="title" id=toc-2162>٢١٥٧ </span>- حديث بريدة: ولا ظهرت الفاحشة فى قوم إلا سلط الله عليهم الموت.
** ك
(فتح الباري ١٩٣/١٠)
** إسناده جيد','5','382',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2158,'<span data-type="title" id=toc-2163>٢١٥٨ </span>- حديث عائشة مرفوعا: " لا تزال أمتى بخير مالم يفش فيهم ولد الزنا، فإذا فشا فيهم ولد الزنا أوشك أن يعمهم الله بعقاب.
** حم
(فتح الباري ١٩٣/١٠)
** إسناده حسن','5','383',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2159,'<span data-type="title" id=toc-2164>٢١٥٩ </span>- حديث عتبة بن عبيد رفعه: " القتل ثلاثة: رجل جاهد بنفسه وماله فى سبيل الله حتى إذا لقى العدو قاتلهم حتى يقتل فذاك الشهيد المفتخر فى خيمة الله تحت عرشه لا يفضله النبيون إلا بدرجة النبوة، ورجل مؤمن قرف على نفسه فى الذنوب والخطايا جاهد بنفسه وماله فى سبيل الله حتى إذا لقى العدو قاتلهم حتى يقتل فانمحت خطاياه، إن السيف محاء للخطايا، ورجل منافق جاهد بنفسه وماله حتى يقتل فهو فى النار إن السيف لا يمحو النفاق ".
** حم
(فتح الباري ١٩٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٩٣: صححه الحاكم.','5','384',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2160,'<span data-type="title" id=toc-2165>٢١٦٠ </span>- " إن الشهيد يغفر له كل شىء إلا الدين ".
** لم
(فتح الباري ١٩٣/١٠)
** صحيح','5','385',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2161,'<span data-type="title" id=toc-2166>٢١٦١ </span>- من طريق إبراهيم بن عبيد بن رفاعة أن أبا محمد أخبره وكان من أصحاب ابن مسعود عن رسول الله صلى الله عليه وسلم قال: " إن أكثر شهداء أمتى لأصحاب الفرس، ورب قتيل بن الصفين الله أعلم بنيته ".
** حم
(فتح الباري ١٩٤/١٠)
** إسناده رجاله موثقون','5','386',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2162,'<span data-type="title" id=toc-2167>٢١٦٢ </span>- عن عتبة بن عبد السلمى رفعه: " يأتى الشهداء والمتوفون بالطاعون، فيقول أصحاب الطاعون: نحن شهداء، فيقال: انظروا فإن كان جراحهم كجراح الشهداء تسيل دما وريحها كريح المسك فهم شهداء، فيجدونهم كذلك ".
** حم
(فتح الباري ١٩٤/١٠)
** إسناده حسن','5','387',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2163,'<span data-type="title" id=toc-2168>٢١٦٣ </span>- حديث العرباض بن سارية: يختصم الشهداء والمتوفون على فرشهم إلى ربنا عز وجل فى الذين ماتوا بالطاعون، فيقول الشهداء: إخواننا قتلوا كما قتلنا، ويقول الذين ماتوا على فرشهم: إخواننا ماتوا على فرشهم كما متنا، فيقول الله عز وجل: انظروا إلى جراحهم فإن شبهت جراح المقتولين فإنهم منهم فإذا جراحهم أشبهم جراحهم.
** حم ن
(فتح الباري ١٩٤/١٠)
** إسناده حسن','5','388',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2164,'<span data-type="title" id=toc-2169>٢١٦٤ </span>- فى رواية عبد الرحمن بن حرملة، عن ابن مسعود: أن النبى صلى الله عليه وسلم كان يكره عشر خصال فذكر فيها: الرقى إلا بالمعوذات.
** حم د ك
(فتح الباري ١٩٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٩٥: صححه ابن حبان والحاكم، وعبد الرحمن بن حرملة قال البخارى: لا يصح حديثه وقال الطبرى: لا يحتج بهذا الخبر لجهالة راويه.','5','389',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2165,'<span data-type="title" id=toc-2170>٢١٦٥ </span>- حديث أبى سعيد: كان رسول الله صلى الله عليه وسلم يتعوذ من الجان وعين الإنسان حتى نزلت المعوذات فأخذ بها وترك ماسواها.
** ت ن
(فتح الباري ١٩٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٩٥: حسنه الترمذى.','5','390',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2166,'<span data-type="title" id=toc-2171>٢١٦٦ </span>- عن ابن مسعود رفعه: " إن الرقى والتمائم والتولة شرك ".
** د جه
(فتح الباري ١٩٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٩٦: صححه الحاكم.','5','391',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2167,'<span data-type="title" id=toc-2172>٢١٦٧ </span>- قال الحافظ فى " الفتح " ١٠ / من نزل منزلا فقال: أعوذ بكلمات الله التامات من شر ماخلق، لم يضره شىء حتى يتحول ".
** لم
(فتح الباري ١٩٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ١٩٦: صححه الترمذى.','5','392',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2168,'<span data-type="title" id=toc-2173>٢١٦٨ </span>- عن سهيل بن أبى صالح عن أبيه عن رجل من أسلم، جاء رجل فقال: لدغت الليلة فلم أنم، فقال النبى صلى الله عليه وسلم: " لو قلت حيت أمسيت أعوذ بكلمات الله التامات من شر ماخلق لم يضرك ".
** د ن
(فتح الباري ١٩٦/١٠)
** إسناده صحيح','5','393',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2169,'<span data-type="title" id=toc-2174>٢١٦٩ </span>- عن جابر رفعه: " أكثر من يموت بعد قضاء الله وقدره بالنفس ".
** بز
(فتح الباري ٢٠٠/١٠)
** إسناده حسن','5','394',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2170,'<span data-type="title" id=toc-2175>٢١٧٠ </span>- من طريق عبيد بن رفاعة عن أسماء بنت عميس أنها قالت: يارسول الله إن ولد جعفر تسرع إليهم العين أفأسترقى لهم؟ ، قال: " نعم ... " الحديث.
** ت ن
(فتح الباري ٢٠١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٠١: صححه الترمذى.','5','395',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2171,'<span data-type="title" id=toc-2176>٢١٧١ </span>- حديث جابر عهن النبى صلى الله عليه وسلم قال: " أكثر من يموت من أمتى بعد قضاء الله وقدره بالأنفس ".
** بز
(فتح الباري ٢٠٤/١٠)
** إسناده حسن','5','396',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2172,'<span data-type="title" id=toc-2177>٢١٧٢ </span>- عن أبى أمامة بن سهل بن حنيف: أن أباه حدثه أن النبى صلى الله عليه وسلم خرج وساروا معه نحو هاء، حتى إذا كانوا بشعب الحزار من الجحفة اغتسل سهل بن حنيف _ وكان أبيض حسن الجسم والجلد _ فنظر إليه عامر بن ربيعة فقال: مارأيت كاليوم ولا جلد فخبأه، فلبط _ أى صرع وزنا ومعنى _ سهيل، فأتى رسول الله صلى الله عليه وسلم فقال: " هل تتهمون به من أحب "، قالوا: عامر بن ربيعة، فدعا عامر فتغيظ عليه، فقال: " علام يقتل أحدكم أخاه؟ ، هلا إذا رأيت مايعجبك بركت "، ثم قال: " اغتسل له "، فغسل وجهه ويديه ومرفقيه وركبتيه وأطراف رجليه وداخلة إزاره فى قدح، ثم يصب ذلك الماء عليه رجل من خلفه على رأسه وظهره ثم يكفأ القدح ففعل به ذلك، فراح سهل مع الناس ليس به بأس.
** حم ن
(فتح الباري ٢٠٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٠٤: صححه ابن حبان.','5','397',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2173,'<span data-type="title" id=toc-2178>٢١٧٣ </span>- عن إسماعيل بن أمية عن النبى صلى الله عليه وسلم: " ثلاثة لا يسلم منهن أحد الطيرة والظن والحسد، فإذا تطيرت فلا ترجع، وإذا حسدت فلا تبغ، وإذا ظننت لا تحقق ".
** عب
(فتح الباري ٢١٣/١٠)
** مرسل أو معضل','5','398',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2174,'<span data-type="title" id=toc-2179>٢١٧٤ </span>- عن أبى هريرة رفعه: " إذا تطيرتم فامضوا، وعلى الله فتوكلوا ".
** عب
(فتح الباري ٢١٣/١٠)
** إسناده لين','5','399',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2175,'<span data-type="title" id=toc-2180>٢١٧٥ </span>- عن أبى الدرداء رفعه: " لن ينال الدرجات العلا من تكهن، أو استقسم أو رجع من سفر تطيرا ".
** طب
(فتح الباري ٢١٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢١٣: رجاله ثقات إلا أننى أظن أن فيه انقطاعا وله شاهد عن عمران بن حصين وأخرجه البزار فى أثناء حديث جيد.','5','400',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2176,'<span data-type="title" id=toc-2181>٢١٧٦ </span>- عن ابن مسعود رفعه: " الطيرة شرك ومامنا إلا تطير ولكن الله يذهبه بالتوكل ".
** د ت
(فتح الباري ٢١٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢١٣: صححه الترمذى وابن حبان، وقوله: ومامنا إلا، من كلام ابن مسعود إذا أدرج فى الخبر وقد بينه سليمان بن حرب شيخ البخارى فيما حكاه الترمذى عن البخارى عنه.','5','401',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2177,'<span data-type="title" id=toc-2182>٢١٧٧ </span>- عن أبى هريرة رفعه: " كان يعجبه الفأل ويكره الطيرة ".
** جه
(فتح الباري ٢١٤/١٠)
** إسناده حسن','5','402',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2178,'<span data-type="title" id=toc-2183>٢١٧٨ </span>- حديث أنس: أن النبى صلى الله عليه وسلم كان إذا خرج لحاجته يعجبه أن يسمع يانجيح ياراشد.
** ت
(فتح الباري ٢١٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢١٥: صححه الترمذى.','5','403',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2179,'<span data-type="title" id=toc-2184>٢١٧٩ </span>- عن بريدة: أن النبى صلى الله عليه وسلم كان لا يتطير من شىء وكان إذا بعث عاملا يسأل عن اسمه فإذا أعجبه فرح به وإن كره اسمه رؤى كراهة ذلك فى وجهه.
** د
(فتح الباري ٢١٥/١٠)
** إسناده حسن','5','404',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2180,'<span data-type="title" id=toc-2185>٢١٨٠ </span>- حديث أبى هريرة رفعه: " من أتى كاهنا أو عرافا فصدقه بما يقول فقد كفر بما أنزل على محمد ".
** د ت ن جه
(فتح الباري ٢١٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢١٧: أخرجه أصحاب السنن وصححه الحاكم.','5','405',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2181,'<span data-type="title" id=toc-2186>٢١٨١ </span>- حديث أنس مرفوعا: " من أتى كاهنا فصدقه بما يقول فقد برىء مما أنزل على محمد ومن أتاه غير مصدق له لم تقبل صلاته أربعين يوما ".
** طب
(فتح الباري ٢١٧/١٠)
** إسناده لين','5','406',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2182,'<span data-type="title" id=toc-2187>٢١٨٢ </span>- عن ابن عباس قال: انطلقت الشياطين فى الأيام التى ابتلى فيها سليمان فكتبت كتب فيها سحر وكفر ثم دفنتها تحت كرسيه ثم أخرجوها بعده فقرؤها على الناس.
** الطبرى
(فتح الباري ٢٢٤/١٠)
** إسناده صحيح','5','407',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2183,'<span data-type="title" id=toc-2188>٢١٨٣ </span>- عن ابن عباس: أن عليا وعمارا لما بعثهما النبى صلى الله عليه وسلم لاستخراج السحر وجدا طلعة فيها إحدى عشرة عقدة.
** سعد
(فتح الباري ٢٢٥/١٠)
** إسناده منقطع','5','408',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2184,'<span data-type="title" id=toc-2189>٢١٨٤ </span>- عن عمر بن الحكم قال: لما رجع رسول الله صلى الله عليه وسلم من الحديبية فى ذى الحجة ودخل المحرم من سنة سبع جاءت رؤساء اليهود إلى لبيد بن الأعصم _ وكان حليفا فى بنى رزيق وكان ساحرا _ فقالوا له: يا أبا الأعصم، أنت أسحرنا وقد سحرنا محمد فلم نصنع شيئا ونحن نجعل لك جعلا على أن تسحره لنا ينكؤه، فجعلوا له ثلاثة دنانية. وفى مرسل عمر بن الحكم: فعمد إلى مشط وما مشط من الرأس من شعر فعقد بذلك عقدا. فى مرسل عمر بن الحكم: فدعا جبير بن إياس الزرقى وهو ممن شهد بدرا فدله على موضعه فى بئر ذروان فاستخرجه. وفى مرسل عمر بن الحكم: فقال له: ماحملك على هذا؟ ، قال: حب الدنانير.
** سعد
(فتح الباري ٢٢٦/١٠)
** مرسل','5','409',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2185,'<span data-type="title" id=toc-2190>٢١٨٥ </span>- فى مرسل يحيى بن يعمر: سحر النبى صلى الله عليه وسلم عن عائشة حتى أنكر بصره.
** عب
(فتح الباري ٢٢٧/١٠)
** مرسل','5','410',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2186,'<span data-type="title" id=toc-2191>٢١٨٦ </span>- فى مرسل عبد الرحمن بن كعب: فقالت أخت لبيد بن الأعصم: أن يكن نبيا فسيخبر وإلا فيذهله هذا السحر حتى يذهب عقله.
** سعد
(فتح الباري ٢٢٧/١٠)
** مرسل','5','411',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2187,'<span data-type="title" id=toc-2192>٢١٨٧ </span>- حديث زيد بن أرقم: سحر النبى صلى الله عليه وسلم رجل من اليهود فاشتكى لذلك أياما فأتاه جبريل فقال: إن رجلا من اليهود سحرك، عقد لك عقدا فى بئر كذا.
** ن سعد
(فتح الباري ٢٢٨/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٢٨: صححه الحاكم وعبد بن حميد.','5','412',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2188,'<span data-type="title" id=toc-2193>٢١٨٨ </span>- حديث ابن عباس: فهبط عليه ملكان وهو بين النائم واليقظان.
** سعد
(فتح الباري ٢٢٨/١٠)
** إسناده ضعيف جدا','5','413',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2189,'<span data-type="title" id=toc-2194>٢١٨٩ </span>- فى مرسل عبد الرحمن بن أبى ليلى قال: احتجم النبى صلى الله عليه وسلم على رأسه بقرن حين طب.
** أبو عبيد
(فتح الباري ٢٢٨/١٠)
** مرسل','5','414',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2190,'<span data-type="title" id=toc-2195>٢١٩٠ </span>- من طريق عمر مولى غفرة: فاستخرج السحر من الجف من تحت البئر ثم نزعه فحله فكشف عن رسول الله صلى الله عليه وسلم.
** سعد
(فتح الباري ٢٣٠/١٠)
** معضل','5','415',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2191,'<span data-type="title" id=toc-2196>٢١٩١ </span>- عن الحسن رفعه: " النشرة من عمل الشيطان ".
** مد
(فتح الباري ٢٣٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٣٣: ووصله أحمد وأبو داود بسند حسن عن جابر.','5','416',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2192,'<span data-type="title" id=toc-2197>٢١٩٢ </span>- حديث جندب رفعه قال: " حد الساحر ضربة سيف ".
** ت
(فتح الباري ٢٣٦/١٠)
** إسناده ضعيف','5','417',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2193,'<span data-type="title" id=toc-2198>٢١٩٣ </span>- فى مرسل الزهرى أنها أكثرت السم فى الكتف والذراع لأنه بلغها أن ذلك كان أحب أعضاء الشاة إليه وفيه: فتناول رسول الله صلى الله عليه وسلم الكتف فنهش منها، وفيه: فلما ازدرد لقمته قال: " إن الشاة تخبرنى ".
** لم
(فتح الباري ٢٤٥/١٠)
** مرسل','5','418',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2194,'<span data-type="title" id=toc-2199>٢١٩٤ </span>- عن ابن عباس: إن اليهود كانوا يقولون: هذه الدنيا سبعة آلاف سنة وإنما نعذب بكل ألف سنة يوما فى النار، وإنما هى سبعة أيام.. فنزلت.
** ابن إسحق
(فتح الباري ٢٤٦/١٠)
** إسناده حسن','5','419',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2195,'<span data-type="title" id=toc-2200>٢١٩٥ </span>- عن عكرمة قال: اجتمعت يهود تخاصم النبى صلى الله عليه وسلم فقالوا: لن تصيبنا النا، وزاد: فقال النبى صلى الله عليه وسلم: " كذبتم بل أنتم خالدون مخلدون لا نخلفكم فيها أبدا إن شاء الله تعالى "، فنزل القرآن تصديقا للنبى صلى الله عليه وسلم.
** ابن أسحق
(فتح الباري ٢٤٦/١٠)
** مرسل
** قال الحافظ فى " الفتح " ١٠ / ٢٤٦: هذان خبران مرسلا يقوى أحدهما الآخر.','5','420',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2196,'<span data-type="title" id=toc-2201>٢١٩٦ </span>- عن زيد بن أسلم أن رسول الله صلى الله عليه وسلم قال ليهود: " أنشدكم الله من أهل النار الذين ذكرهم الله فى التوراة "، قالوا: إن الله غضب علينا غضبة فنمكث فى النار أربعين يوما ثم نخرج فتخلفوننا فيها، فقال: " كذبتم، والله لا نخلفكم فيها أبدا "، فنزل القرآن تصديقا له.
** الطبرى
(فتح الباري ٢٤٦/١٠)
** مرسل
** قال الحافظ فى " الفتح " ١٠ / ٢٤٦: هذان خبران مرسلان يقوى أحدهما الآخر.','5','421',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2197,'<span data-type="title" id=toc-2202>٢١٩٧ </span>- عن الزهرى مرسلا: مانزلت أجد من الأكلة التى أكلت بخيبر عدادا حتى كان هذا أوان انقطاع أبهرى.
** مغازى موسى بن عقبة
(فتح الباري ٢٤٧/١٠)
** مرسل','5','422',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2198,'<span data-type="title" id=toc-2203>٢١٩٨ </span>- عن ابن عمر مرفوعا: " عمر الذباب أربعون ليلة والذباب كله فى النار إلا النحل ".
** يع
(فتح الباري ٢٥٠/١٠)
** إسناده لا بأس به
** قال الحافظ فى " الفتح " ١٠ / ٢٥٠: أخرجه ابن عدى دون أوله من وجه آخر ضعيف.','5','423',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2199,'<span data-type="title" id=toc-2204>٢١٩٩ </span>- فى رواية عبد الله بن المثنى عن عمه ثمامة أنه حدثه قال: كنا عند أنس فوقع ذباب فى إناء، فقال أنس: بإصبعه فغمسه فى ذلك الإناء ثلاثا، ثم قال: بسم الله، وقال: إن رسول الله صلى الله عليه وسلم أمرهم أن يفعلوا ذلك.
** بز
(فتح الباري ٢٥٠/١٠)
** رجاله ثقات','5','424',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2200,'<span data-type="title" id=toc-2205>٢٢٠٠ </span>- عن ابن عباس قال: كانت قريش تطوف بالبيت عراة يصفرون ويصفقون فأنزل الله تعالى (قل من حرم زينة الله) الآية.
** الطبرى
(فتح الباري ٢٥٣/١٠)
** إسناده صحيح','5','425',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2201,'<span data-type="title" id=toc-2206>٢٢٠١ </span>- حديث أبى جرى _ بالجيم والراء مصفر واسمه جابر بن سليم _ رفعه قال فى أثناء حديث مرفوع: " وارفع إزارك إلى نصف الساق فإن أبيت فالى الكعبين، وإياك وإسبال الإزار فإنه من المخيلة، وإن الله لا يحب المخيلة ".
** د ن
(فتح الباري ٢٥٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٥٦: صححه الحاكم.','5','426',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2202,'<span data-type="title" id=toc-2207>٢٢٠٢ </span>- حديث حذيفة: الإزار إلى أنصاف الساقين فإن أبيت فأسفل فإن أبيت فمن وراء الساقين ولا حق للكعبين فى الإزار.
** ن
(فتح الباري ٢٥٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٥٦: صححه الحاكم.','5','427',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2203,'<span data-type="title" id=toc-2208>٢٢٠٣ </span>- عن ابن مسعود أنه رأى أعرابيا يصلى قد أسبل فقال: المسبل فى الصلاة ليس من الله فى حل ولا حرام.
** طب
(فتح الباري ٢٥٧/١٠)
** إسناده حسن','5','428',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2204,'<span data-type="title" id=toc-2209>٢٢٠٤ </span>- عن ابن عمر: فقالت أم سلمة: فكيف تصنع النساء بذيولهن؟ فقال: " يرخين شبرا "، فقالت: إذا تنكشف أقدامهن، قال: " فيرخينه ذراعا لا يزدن عليه ".
** ن ت
(فتح الباري ٢٥٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٥٩: صححه الترمذى.','5','429',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2205,'<span data-type="title" id=toc-2210>٢٢٠٥ </span>- عن عمرو بن شعيب عن أبيه عن جده رفعه: " إن الله يحب أن يرى أثر نعمته على عبده ".
** ت
(فتح الباري ٢٦٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٦٠: حسنه الترمذى.','5','430',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2206,'<span data-type="title" id=toc-2211>٢٢٠٦ </span>- حديث أبى الأحوص عوف بن مالك الجشمى عن أبيه: أن النبى صلى الله عليه وسلم قال له ورآه رث الثياب: " إذا آتاك الله مالا فلير أثره عليك ".
** ن د
(فتح الباري ٢٦٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٦٠: صححه ابن حبان والحاكم.','5','431',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2207,'<span data-type="title" id=toc-2212>٢٢٠٧ </span>- من طريق كريب قال: كنت أقود ابن عباس فقال: حدثنى العباس قال: بينا أنا مع رسول الله صلى الله عليه وسلم إذ أقبل رجل يتبختر بين ثوبين.
** يع
(فتح الباري ٢٦٠/١٠)
** إسناده ضعيف','5','432',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2208,'<span data-type="title" id=toc-2213>٢٢٠٨ </span>- من حديث أبى هريرة وابن عباس قالا: خطبنا رسول الله صلى الله عليه وسلم فذكر الحديث الطويل وفيه: ومن لبس ثوبا فأختال فيه خسف به من شفير جهنم فيتجلجل فيها.
** حارث
(فتح الباري ٢٦٠/١٠)
** إسناده ضعيف جدا','5','433',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2209,'<span data-type="title" id=toc-2214>٢٢٠٩ </span>- من طريق عبد العزيز بن أبى داود عن سالم بن عبد الله بن عمر عن أبيه عن النبى صلى الله عليه وسلم قال: " الإسبال فى الإزار والقميص والعمامة من جر منها شيئا خيلاء ... " الحديث.
** د ن جه
(فتح الباري ٢٦٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٦٢: أخرجه أصحاب السنن إلا الترمذى واستغربه ابن أبى شيبة وعبد العزيز فيه مقال','5','434',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2210,'<span data-type="title" id=toc-2215>٢٢١٠ </span>- حديث أبى هريرة: أن رسول الله صلى الله عليه وسلم لعن الرجل يلبس لبسة المرأة.
** لم
(فتح الباري ٢٦٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٦٣: صححه الحاكم.','5','435',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2211,'<span data-type="title" id=toc-2216>٢٢١١ </span>- عن عبيد بن خالد قال: كنت أمشى وعلى برد أجره، فقال لى رجل: " أرفع ثوبك فإنه أنقى وأبقى "، فنظرت فإذا هو النبى صلى الله عليه وسلم فقلت: إنما هى بردة ملحاء، فقال: " أما لك فى أسوة؟ "، قال: فنظرت فإذا إزاره إلى أنصاف ساقيه.
** ن
(فتح الباري ٢٦٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٦٣: سنده قبلها جيد (يعنى رحم بنت الأسود) .
** تعقيب: رهم بنت الأسود عمة أشعث لا تعرف كما قال الحافظ فى التقريب.','5','436',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2212,'<span data-type="title" id=toc-2217>٢٢١٢ </span>- عن عمرو بن زرارة وفيه: وضرب رسول الله صلى الله عليه وسلم بأربع أصابع تحت ركبة عمرو، فقال: " ياعمرو "، فقال: " ياعمرو هذا موضع الإزار، ثم ضرب بأربع أصابع تحت الأربع "، فقال: " ياعمرو هذا موضع الإزار ".
** طب
(فتح الباري ٢٦٤/١٠)
** رجاله ثقات','5','437',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2213,'<span data-type="title" id=toc-2218>٢٢١٣ </span>- عن ابن مسعود: أنه كان يسبل إزاره، فقيل له فى ذلك فقال: أنى حمش الساقين
** شب
(فتح الباري ٢٦٤/١٠)
** إسناده جيد','5','438',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2214,'<span data-type="title" id=toc-2219>٢٢١٤ </span>- حديث المغيرة بن شعبة: رأيت رسول الله صلى الله عليه وسلم أخذ برداء سفيان بن سهيل وهو يقول: " سفيان لا تسبل فإن الله لا يحب المسبلين ".
** ن جه
(فتح الباري ٢٦٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٦٤: صححه الحاكم.','5','439',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2215,'<span data-type="title" id=toc-2220>٢٢١٥ </span>- حديث قرة بن إياس لما بايع النبى صلى الله عليه وسلم قال: فأدخلت يدى فى جيب قميصه فمسست الخاتم.
** د ت
(فتح الباري ٢٦٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٦٧: صححه الترمذى وابن حبان.','5','440',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2216,'<span data-type="title" id=toc-2221>٢٢١٦ </span>- حديث ابن عمر رفعه: " من تشبه بقوم فهو منهم ".
** د
(فتح الباري ٢٧١/١٠)
** إسناده حسن','5','441',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2217,'<span data-type="title" id=toc-2222>٢٢١٧ </span>- حديث أبى قرصافة قال: كسانى رسول الله صلى الله عليه وسلم برنسا فقال: " البسه ".
** طب
(فتح الباري ٢٧٢/١٠)
** إسناده فيه من لا يعرف','5','442',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2218,'<span data-type="title" id=toc-2223>٢٢١٨ </span>- حديث جابر رفعه: " من لعق ثلاث غدوات فى كل شهر لم يصبه عظيم بلاء ".
** جه
(فتح الباري ١٤٠/١٠)
** إسناده ضعيف','5','443',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2219,'<span data-type="title" id=toc-2224>٢٢١٩ </span>- حديث على رفعه: " إياكم ولبوس الرهبان، فإنه من تزيا بهم أو تشبه فليس منى ".
** طس
(فتح الباري ٢٧٢/١٠)
** إسناده لابأس به','5','444',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2220,'<span data-type="title" id=toc-2225>٢٢٢٠ </span>- حديث أبى هريرة: دخلت يوما السوق مع رسول الله صلى الله عليه وسلم فجلس إلى البزاز فاشترى سراويل بأربعة دراهم.... الحديث وفيه: قلت: يارسول الله وإنك لتلبس السراويل؟ ، قال: " أجل فى السفر، والحضر، والليل والنهار فإنى أمرت بالتستر ".
** يع طس
(فتح الباري ٢٧٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٧٣: فيه يونس بن زياد البصرى وهو ضعيف.','5','445',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2221,'<span data-type="title" id=toc-2226>٢٢٢١ </span>- عن أبى المليح بن أسامة عن أبيه رفعه: " اعتموا تزدادوا حلما ".
** طب الترمذى فى العلل
(فتح الباري ٢٧٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٧٣: وضعفه البخارى، وقد صححه الحاكم فلي يصب، وله شاهد عند البزار عن ابن عباس ضعيف أيضا.','5','446',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2222,'<span data-type="title" id=toc-2227>٢٢٢٢ </span>- وصف لرسول الله صلى الله عليه وسلم الطيلسان فقال: " هذا ثوب لا يؤدى شكره ".
** سعد
(فتح الباري ٢٧٤/١٠)
** مرسل','5','447',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2223,'<span data-type="title" id=toc-2228>٢٢٢٣ </span>- من طريق الحسن البصرى: أن عمر بن الخطاب أراد أن ينهى عن حلل الحبرة لأنها تصبخ بالبول فقال له أبى: ليس ذلك لك، فقد لبسهن النبى صلى الله عليه وسلم ولسناهن فى عهده.
** حم
(فتح الباري ٢٧٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٧٧: والحسن لم يسمع من عمر.','5','448',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2224,'<span data-type="title" id=toc-2229>٢٢٢٤ </span>- عن أبى نضرة قال: كان أصحاب رسول الله صلى الله عليه وسلم إذا لبس أحدهم ثوبا جديدا قيل له: تبلى ويخلف الله.
** د
(فتح الباري ٢٨٠/١٠)
** إسناده صحيح','5','449',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2225,'<span data-type="title" id=toc-2230>٢٢٢٥ </span>- حديث سمرة رفعه: " عليكم بالثياب البيض فالبسوها فإنها أطيب وأطهر وكفنوا فيها موتاكم ".
** حم د ت ن جه
(فتح الباري ٢٨٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٨٣: أخرجه أصحاب السنن وصححه الحاكم.','5','450',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2226,'<span data-type="title" id=toc-2231>٢٢٢٦ </span>- عن على قال: نهانى النبى صلى الله عليه وسلم عن القسى والحرير.
** د ن حم
(فتح الباري ٢٩٤/١٠)
** إسناده على شرط الشيخين','5','451',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2227,'<span data-type="title" id=toc-2232>٢٢٢٧ </span>- حديث ابن عباس: إنما نهانى رسول الله صلى الله عليه وسلم عن الثوب المصمت من الحرير فأما العلم من الحرير وسدى الثوب فلا بأس به.
** طب
(فتح الباري ٢٩٤/١٠)
** إسناده حسن','5','452',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2228,'<span data-type="title" id=toc-2233>٢٢٢٨ </span>- إنما نهى عن المصمت إذا كان حريرا.
** ك
(فتح الباري ٢٩٤/١٠)
** إسناده صحيح','5','453',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2229,'<span data-type="title" id=toc-2234>٢٢٢٩ </span>- حديث على أن النبى صلى الله عليه وسلم أخذ حريرا وذهبا فقال: " هذان حرامان على ذكور أمتى حلا لإناثهم ".
** حم د ت ن جه
(فتح الباري ٢٩٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٩٦: صححه الطحاوى.','5','454',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2230,'<span data-type="title" id=toc-2235>٢٢٣٠ </span>- حديث مسلمة بن مخلد أنه قال لعقبة بن عامر: قم فحدث بما سمعت من رسول الله صلى الله عليه وسلم، فقال: سمعته يقول: " الذهب والحرير حرام على ذكور أمتى حل لإناثهم ".
** حم طحا
(فتح الباري ٢٩٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٢٩٦: صححه الطحاوى.','5','455',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2231,'<span data-type="title" id=toc-2236>٢٢٣١ </span>- أهدى أمير أذربيجان إلى النبى صلى الله عليه وسلم حلة مسيرة بحرير.
** طحا
(فتح الباري ٢٩٧/١٠)
** إسناده ضعيف','5','456',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2232,'<span data-type="title" id=toc-2237>٢٢٣٢ </span>- فأعطاه لعمر فقال: " لم أعطكه لتلبسه بل لتبيعه "، فباعه عمر.
** ن
(فتح الباري ٢٩٩/١٠)
** إسناده قوى
** قال الحافظ فى " الفتح " ١٠ / ٢٩٩: أصله فى مسلم.','5','457',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2233,'<span data-type="title" id=toc-2238>٢٢٣٣ </span>- حديث عقبة: أن النبى صلى الله عليه وسلم كان يمنع أهله الحرير والحلة.
** ن
(فتح الباري ٣٠٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٠٠: صححه ابن حبان.','5','458',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2234,'<span data-type="title" id=toc-2239>٢٢٣٤ </span>- حديث ابن عمر قال: رأى النبى صلى الله عليه وسلم على عمر ثوبا فقال: " ألبس جديدا وعش حميدا، ومت شهيدا ".
** ن جه
(فتح الباري ٣٠٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٠٣: صححه ابن حبان وأعله النسائى.','5','459',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2235,'<span data-type="title" id=toc-2240>٢٢٣٥ </span>- حديث أبى سعيد: كان رسول الله صلى الله عليه وسلم إذا استجد ثوبا سماه باسمه عمامة أو قميصا أو رداء ثم يقول: " اللهم لك الحمد أنت كسوتنيه، أسألك خيره وخير ما صنع له وأعوذ بك من شره وشر ماصنع له ".
** د ن ت
(فتح الباري ٣٠٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٠٣: صححه الترمذى.','5','460',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2236,'<span data-type="title" id=toc-2241>٢٢٣٦ </span>- حديث عمر رفعه: " من لبس ثوبا جديدا فقال: الحمد لله الذى كسانى ما أوارى به عورتى، وأتجمل به فى حياتى ثم عمد إلى الثوب الذى أخلق فتصدق به كان فى حفظ الله وفى كنف الله حيا وميتا ".
** ت جه
(فتح الباري ٣٠٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٠٣: صححه الحاكم.','5','461',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2237,'<span data-type="title" id=toc-2242>٢٢٣٧ </span>- حديث معاذ بن أنس رفعه: " من لبس ثوبا فقال: الحمد لله الذى كسانى هذا ورزقنيه من غير حول منى ولا قوة، غفر الله له ما تقدم من ذنبه ".
** حم ت
(فتح الباري ٣٠٣/١٠)','5','462',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2238,'<span data-type="title" id=toc-2243>٢٢٣٨ </span>- حديث عبد الله بن جعفر قال: رأيت رسول الله صلى الله عليه وسلم وعليه ثوبان مصبوغان بالزعفران.
** ك
(فتح الباري ٣٠٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٠٥: فى سنده عبد الله بن مصعب الزبيرى وفيه ضعف.','5','463',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2239,'<span data-type="title" id=toc-2244>٢٢٣٩ </span>- حديث أم سلمة: أن رسول الله صلى الله عليه وسلم صبغ إزاره ورداءه بزعفران.
** طب
(فتح الباري ٣٠٥/١٠)
** إسناده فيه راو مجهول
** قال الحافظ فى " الفتح " ١٠ / ٣٠٥: فيه راو مجهول.','5','464',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2240,'<span data-type="title" id=toc-2245>٢٢٤٠ </span>- حديث هلال بن عامر عن أبيه: رأيت النبى صلى الله عليه وسلم يخطب بمنى على
بعير وعليه برد أحمر.
** د
(فتح الباري ٣٠٥/١٠)
** إسناده حسن','5','465',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2241,'<span data-type="title" id=toc-2246>٢٢٤١ </span>- من مرسل الحسن: الحمرة من زينة الشيطان والشيطان يحب الحمرة.
** شب
(فتح الباري ٣٠٦/١٠)
** مرسل','5','466',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2242,'<span data-type="title" id=toc-2247>٢٢٤٢ </span>- عن رافع بن يزيد الثقفى رفعه: " إن الشيطان يحب الحمرة وإياكم والحمرة وكل ثوب ذى شهرة ".
** هب
(فتح الباري ٣٠٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٠٦: من رواية أبى بكر الهذلى وهو ضعيف.','5','467',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2243,'<span data-type="title" id=toc-2248>٢٢٤٣ </span>- عن عبد الله بن عمرو: مر على النبى صلى الله عليه وسلم رجل وعليه ثوبان أحمران فسلم عليه فلم يرد عليه النبى صلى الله عليه وسلم.
** د ت بز
(فتح الباري ٣٠٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٠٦: حسنه الترمذى، وقال البزار: لا نعلمه إلا بهذا الإسناد، وفيه أبو يحيى القتات مختلف فيه.','5','468',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2244,'<span data-type="title" id=toc-2249>٢٢٤٤ </span>- عن رافع بن خديج قال: خرجنا مع رسول الله صلى الله عليه وسلم فى سفر، فرأى على رواحلنا أكسية فيها خطوط عهن حمر فقال: " ألا أرى هذه الحمرة قد غلبتكم "
قال: فقمنا سراعا فنزعناها حتى نفر بعض إبلنا.
** د
(فتح الباري ٣٠٦/١٠)
** إسناده فيه راو لم يسم','5','469',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2245,'<span data-type="title" id=toc-2250>٢٢٤٥ </span>- عن امرأة من بنى أسد قالت: كنت عند زينب أم المؤمنين ونحن نصبغ ثيابا لها بمغرة إذ طلع النبى صلى الله عليه وسلم فلما رأى المغرة رجع، فلما رأت ذلك زينب غسلت ثيابها ووارت كل حمرة فجاء فدخل.
** د
(فتح الباري ٣٠٦/١٠)
** إسناده ضعيف','5','470',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2246,'<span data-type="title" id=toc-2251>٢٢٤٦ </span>- عن على قال: نهى عن المياثر الأرجوان.
** د
(فتح الباري ٣٠٧/١٠)
** إسناده صحيح','5','471',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2247,'<span data-type="title" id=toc-2252>٢٢٤٧ </span>- عن على قال: نهى رسول الله صلى الله عليه وسلم عن خاتم الذهب وعن لبس القسى الميثرة الحمراء.
** حم د ت ن جه
(فتح الباري ٣٠٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٠٧: أخرجه أحمد وأصحاب السنن وصححه ابن حبان.','5','472',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2248,'<span data-type="title" id=toc-2253>٢٢٤٨ </span>- حديث بشير بن الخصاصية: بينما أنا أمشى فى المقابر على نعلان إذا رجل ينادى من خلفى: ياصاحب السبتيتين إذا كنت فى هذا الموضع فاخلع نعليك.
** حم د
(فتح الباري ٣٠٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٠٩: صححه الحاكم.','5','473',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2249,'<span data-type="title" id=toc-2254>٢٢٤٩ </span>- عن عائشة قالت: ربما انقطع شسع نعل رسول الله صلى الله عليه وسلم فمشى فى
النعل الواحدة حتى يصلحها.
** ت
(فتح الباري ٣١٠/١٠)
** ضعيف
** قال الحافظ فى " الفتح " ١٠ / ٣١٠: قد رجح البخارى وغير واحد وقفه على عائشة.','5','474',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2250,'<span data-type="title" id=toc-2255>٢٢٥٠ </span>- عن عائشة: أنها كانت تقول: لأضيفن أبا هريرة فيمشى فى نعل واحدة.
** ت
(فتح الباري ٣١٠/١٠)
** إسناده صحيح','5','475',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2251,'<span data-type="title" id=toc-2256>٢٢٥١ </span>- حديث ابن عباس: كانت لنعل رسول الله صلى الله عليه وسلم قبالان مثنى
شراكهما.
** تم جه
(فتح الباري ٣١٢/١٠)
** إسناده قوى','5','476',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2252,'<span data-type="title" id=toc-2257>٢٢٥٢ </span>- من طريق شريح بن هانىء أنه سأل عائشة: أكان رسول الله صلى الله عليه وسلم يصلى على الحصير والله يقول: (وجعلنا جهنم للكافرين حصيرا) فقالت: لم يكن يصلى على الحصير.
** شب
(فتح الباري ٣١٤/١٠)
** ضعيف','5','477',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2253,'<span data-type="title" id=toc-2258>٢٢٥٣ </span>- عن أبى السفر قال: رأيت على البراء خاتما من ذهب.
** شب
(فتح الباري ٣١٧/١٠)
** إسناده صحيح','5','478',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2254,'<span data-type="title" id=toc-2259>٢٢٥٤ </span>- من طريق محمد بن مالك قال: رأيت على البراء خاتما من ذهب، فقال: قسم رسول الله صلى الله عليه وسلم قسما فألبسنيه فقال: " البس ماكساك الله ورسوله ".
** حم
(فتح الباري ٣١٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣١٧: قال الحازمى: إسناده ليس بذاك.','5','479',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2255,'<span data-type="title" id=toc-2260>٢٢٥٥ </span>- عن مكحول: أن خاتم رسول الله صلى الله عليه وسلم كان من حديد ملويا عليه فضة، غير أن فصه باد.
** سعد
(فتح الباري ٣٢٢/١٠)
** مرسل','5','480',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2256,'<span data-type="title" id=toc-2261>٢٢٥٦ </span>- عن عبد الله بن بريدة عن أبيه: أن رجلا جاء إلى النبى صلى الله عليه وسلم وعليه خاتم من شبه فقال: " مالى أجد منك ريح الأصنام "، فطرحه ثم جاء وعليه خاتم من حديث فقال: " مالى أرى عليك حلية أهل النار "، فطرحه فقال يارسول الله من أى شىء أتخذه؟ ، قال: " اتخذه من ورق ولا تتمه مثقالا ".
** د ت ن جه
(فتح الباري ٣٢٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٢٣: أخرجه أصحاب السنن وصححه ابن حبان، وفى سنده أبو طيبة _ بفتح المهملة وسكون التحتانية بعدها _ اسمه عبد الله بن مسلم المروزى، قال أبو حاتم الرازى: يكتب حديثه ولا يحتج به، وقال ابن حبان فى الثقات: يخطىء ويخالف','5','481',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2257,'<span data-type="title" id=toc-2262>٢٢٥٧ </span>- عن عبد الله بن محمد بن عقيل، أنه أخرج لهم خاتما فزعم أن رسول الله صلى الله عليه وسلم كان يلبسه فيه تمثال أسد.
** عب
(فتح الباري ٣٢٤/١٠)
** مرسل ضعيف','5','482',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2258,'<span data-type="title" id=toc-2263>٢٢٥٨ </span>- عن عبد الله بن دينار عن ابن عمر قال: كان النبى صلى الله عليه وسلم يتختم فى يمينه.
** طس
(فتح الباري ٣٢٦/١٠)
** إسناده حسن','5','483',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2259,'<span data-type="title" id=toc-2264>٢٢٥٩ </span>- عن ابن عباس: كان النبى صلى الله عليه وسلم يتختم فى يمينه.
** طب
(فتح الباري ٣٢٦/١٠)
** إسناده لين','5','484',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2260,'<span data-type="title" id=toc-2265>٢٢٦٠ </span>- عن على: أن النبى صلى الله عليه وسلم كان يتختم فى يمينه.
** د ن تم
(فتح الباري ٣٢٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٢٦: صححه ابن حبان.','5','485',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2261,'<span data-type="title" id=toc-2266>٢٢٦١ </span>- حديث أبى سعيد: كان يلبس خاتمه فى يساره.
** أبو الشيخ سعد
(فتح الباري ٣٢٧/١٠)
** إسناده لين','5','486',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2262,'<span data-type="title" id=toc-2267>٢٢٦٢ </span>- عن ابن عمر: أن النبى صلى الله عليه وسلم تختم فى يمينه ثم إنه حوله فى يساره.
** شيخ عد
(فتح الباري ٣٢٧/١٠)
** إسناده ضعيف','5','487',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2263,'<span data-type="title" id=toc-2268>٢٢٦٣ </span>- من طريق جعفر بن بن محمد عن أبيه قال: طرح رسول الله صلى الله عليه وسلم خاتمه الذهب ثم تختم خاتما من ورق فجعله فى يساره.
** سعد
(فتح الباري ٣٢٧/١٠)
** مرسل أو معضل','5','488',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2264,'<span data-type="title" id=toc-2269>٢٢٦٤ </span>- من رواية عرعرة بن البرند عن عزرة بن ثابت عن عثمان عن أنس قال: كان فص خاتم النبى صلى الله عليه وسلم مكتوبا عليه لا إله إلا الله محمد رسول الله.
** شيخ أخلاق النبى صلى الله عليه وسلم
(فتح الباري ٣٢٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٢٩: عرعرة ضعفه ابن المدينى وزيادته هذه شاذة.','5','489',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2265,'<span data-type="title" id=toc-2270>٢٢٦٥ </span>- حديث زيد بن أرقم مرفوعا: " من لم يؤخذ شاربه فليس منا ".
** ت ن
(فتح الباري ٣٣٧/١٠)
** إسناده قوى','5','490',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2266,'<span data-type="title" id=toc-2271>٢٢٦٦ </span>- عن ابن عباس فى قوله تعالى (وإذا ابتلى إبراهيم ربه بكلمات فأتمهن) قال: اتبلاه الله بالطهارة خمس فى الرأس، وخمس فى الجسد.
** عب الطبرى
(فتح الباري ٣٣٧/١٠)
** إسناده صحيح','5','491',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2267,'<span data-type="title" id=toc-2272>٢٢٦٧ </span>- حديث عبد الله بن بشر رفعه: " قصوا أظفاركم وادفنوا قلاماتكم ونقوا براجمكم ".
** الترمذى الحكيم
(فتح الباري ٣٣٨/١٠)
** إسناده فيه راو مجهول','5','492',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2268,'<span data-type="title" id=toc-2273>٢٢٦٨ </span>- حديث أم عطية أن امرأة كانت تختن بالمدينة فقال لها النبى صلى الله عليه وسلم: " لا تنهكى فإن ذلك أحظى للمرأة ".
** د
(فتح الباري ٣٤٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٤٠: قال _ أبو داود _: إنه ليس بالقوى.','5','493',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2269,'<span data-type="title" id=toc-2274>٢٢٦٩ </span>- حديث كليب جد عثيم بن كثير أن النبى صلى الله عليه وسلم قال له: " الق عنك شعر الكفر واختتن ".
** د
(فتح الباري ٣٤١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٤٨: تعقب بإن سند الحديث ضعيف.','5','494',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2270,'<span data-type="title" id=toc-2275>٢٢٧٠ </span>- عن ابن عباس قال: سبع من السنة فى الصبى يسمى فى السابع ويختن ... الحديث.
** طس
(فتح الباري ٣٤٣/١٠)
** ضعيف','5','495',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2271,'<span data-type="title" id=toc-2276>٢٢٧١ </span>- حديث أم سلمة: أن النبى صلى الله عليه وسلم إذا طلى ولى عانته بيده.
** جه هق
(فتح الباري ٣٤٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٤٤: رجاله ثقات ولكنه أعله بالإرسال وأنكر أحمد صحته.','5','496',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2272,'<span data-type="title" id=toc-2277>٢٢٧٢ </span>- حديث أنس أن النبى صلى الله عليه وسلم كان لا يتنور وكان إذا كثر شعره حلقه.
** لم
(فتح الباري ٣٤٤/١٠)
** إسناده ضعيف جدا','5','497',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2273,'<span data-type="title" id=toc-2278>٢٢٧٣ </span>- من طريق قيس بن حازم قال: صلى النبى صلى الله عليه وسلم صلاة فأوهم فيها فسئل فقال: " مالى لا أوهم ورفغ أحدكم بين ظفره وأنملته ".
** هب
(فتح الباري ٣٤٥/١٠)
** رجاله ثقات مرسلا
** قال الحافظ فى " الفتح " ١٠ / ٣٤٥: رجاله ثقات مع إرساله، وقد وصله الطبرانى من وجه آخر.','5','498',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2274,'<span data-type="title" id=toc-2279>٢٢٧٤ </span>- من مرسل أبى جعفر الباقر قال: كان رسول الله صلى الله عليه وسلم يستحب أن يأخذ من أظفاره وشاربه يوم الجمعة.
** هق
(فتح الباري ٣٤٦/١٠)
** مرسل
** قال الحافظ فى " الفتح " ١٠ / ٣٤٦: له شاهد موصول عن أبى هريرة، لكن سنده ضعيف أخرجه البيهقى أيضا فى الشعب.','5','499',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2275,'<span data-type="title" id=toc-2280>٢٢٧٥ </span>- من جهة عبد الله بن عمران شيخ مصرى عن ثابت عن أنس قال: أن يحلق الرجل عانته كل أربعين يوما، وأن ينتف أبطه كلما طلع، ولا يدع شاربيه يطولان وأن يقلم أظفاره من الجمعة إلى الجمعة.
** عد
(فتح الباري ٣٤٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٤٦: أتى فيه بألفاظ مستغربة، وعبد الله والراوى عنه مجهولان.','5','500',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2276,'<span data-type="title" id=toc-2281>٢٢٧٦ </span>- حديث ابن عباس: كان النبى صلى الله عليه وسلم يقص شاربه.
** ت
(فتح الباري ٣٤٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٤٧: حسنه الترمذى.','6','1',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2277,'<span data-type="title" id=toc-2282>٢٢٧٧ </span>- حديث جابر قال: كنا نعفر السبال إلا فى حج أو عمرة.
** د
(فتح الباري ٣٥٠/١٠)
** إسناده حسن','6','2',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2278,'<span data-type="title" id=toc-2283>٢٢٧٨ </span>- حديث عمرو بن شعيب عن أبيه عن جده أن النبى صلى الله عليه وسلم كان يأخذ من لحيته من عرضها وطولها.
** ت
(فتح الباري ٣٥٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٥٠: نقل عن البخارى أنه قال فى رواية عمر بن هارون: لا أعلم له حديثا منكرا إلا هذا أ. هـ. وقد ضعف عمر بن هارون مطلقا جماعة.','6','3',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2279,'<span data-type="title" id=toc-2284>٢٢٧٩ </span>- عن أبى أمامة قال: خرج رسول الله صلى الله عليه وسلم على مشيخة من الأنصار بيض لحاهم فقال: " يامعشر الأنصار حمروا وصفروا وخالفوا أهل الكتاب ".
** حم
(فتح الباري ٣٥٤/١٠)
** إسناده حسن','6','4',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2280,'<span data-type="title" id=toc-2285>٢٢٨٠ </span>- حديث البراء بن عازب رفعه: " من خضب بالسواد سود الله وجهه يوم القيامة ".
** طب ابن أبى عاصم
(فتح الباري ٣٥٥/١٠)
** إسناده لين','6','5',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2281,'<span data-type="title" id=toc-2286>٢٢٨١ </span>- حديث أبى ذر رفعه: " إن أحسن ما غيرتم به الشيب الحناء والكتم ".
** د ت ن جه
(فتح الباري ٣٥٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٥٥: لأصحاب السنن وصححه الترمذى.','6','6',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2282,'<span data-type="title" id=toc-2287>٢٢٨٢ </span>- عن عائشة: كان شعرالنبى صلى الله عليه وسلم فوق الوفرة ودون الجمة.
** د جه
(فتح الباري ٣٥٨/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٥٨: لفظ أبى داود، ولفظ ابن ماجه بنحوه، وصححه الترمذى.','6','7',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2283,'<span data-type="title" id=toc-2288>٢٢٨٣ </span>- حديث أم هانىء قالت: قدم رسول الله صلى الله عليه وسلم مكة وله أربع غدائر وفى لفظ: أربع ضفائر.
** د ت
(فتح الباري ٣٦٠/١٠)
** إسناده حسن','6','8',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2284,'<span data-type="title" id=toc-2289>٢٢٨٤ </span>- عن وائل بن حجر قال: أتيت النبى صلى الله عليه وسلم ولى شعر طويل فقال: " ذناب ذباب "، فرجعت فجززته، ثم أتيت من الغد، فقال: " إنى لم أعنك ".
** د ن جه
(فتح الباري ٣٦٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٦٠: صححه ابن ماجه.','6','9',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2285,'<span data-type="title" id=toc-2290>٢٢٨٥ </span>- عن زياد بن حصين عن أبيه أنى: أتى النبى صلى الله عليه وسلم فوضع يده على ذؤابته وسمت عليه ودعا له.
** ن
(فتح الباري ٣٦٥/١٠)
** إسناده صحيح','6','10',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2286,'<span data-type="title" id=toc-2291>٢٢٨٦ </span>- عن حميد بن عبد الرحمن لقيت رجلا صحب النبى صلى الله عليه وسلم كما صحبه أبو هريرة أربع سنين قال: نهانا رسول الله صلى الله عليه وسلم أن يمشط أحدنا كل يوم.
** ن
(فتح الباري ٣٦٧/١٠)
** إسناده صحيح','6','11',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2287,'<span data-type="title" id=toc-2292>٢٢٨٧ </span>- حديث عبد الله بن مغفل: أن النبى صلى الله عليه وسلم كان ينهى عن الترجل إلا غبا.
** د ت ن جه
(فتح الباري ٣٦٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٧٦: لأصحاب السنن وصححه ابن حبان.','6','12',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2288,'<span data-type="title" id=toc-2293>٢٢٨٨ </span>- عن عطاء بن يسار أن رسول الله صلى الله عليه وسلم رأى رجلا ثائر الرأس واللحية فأشار إليه بإصلاح رأسه ولحيته.
** ما
(فتح الباري ٣٦٧/١٠)
** إسناده صحيح مرسلا
** قال الحافظ فى " الفتح " ١٠ / ٣٦٧: له شاهد من حديث جابر أخرجه أبو داود والنسائى بسند حسن.','6','13',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2289,'<span data-type="title" id=toc-2294>٢٢٨٩ </span>- حديث عائشة قالت: خمس لم يكن النبى صلى الله عليه وسلم يدعهن فى سفر ولا حضر: المرآة والمكحلة والمشط والمدرى والسواك.
** الخطيب فى الكفاية
(فتح الباري ٣٦٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٦٧: فى إسناده أبو أمية بن يعلى وهو ضعيف، وأخرجه ابن عدى من وجه آخر ضعيف أيضا.','6','14',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2290,'<span data-type="title" id=toc-2295>٢٢٩٠ </span>- عن عائشة: كان لا يفارق رسول الله صلى الله عليه وسلم سواكه ومشطه وكان ينظر فى المرآة إذا سرح لحيته.
** طس
(فتح الباري ٣٦٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٦٧: فيه سليمان بن أرقم وهو ضعيف ب، وله شاهد من مرسل خالد بن معدان أخرجه ابن سعد.','6','15',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2291,'<span data-type="title" id=toc-2296>٢٢٩١ </span>- روى أبو أمامة بن ثعلبة رفعه: " البذاذة من الإيمان ".
** د
(فتح الباري ٣٦٨/١٠)
** صحيح','6','16',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2292,'<span data-type="title" id=toc-2297>٢٢٩٢ </span>- عن أبى هريرة رفعه: " من كان له شعر فليكرمه ".
** د
(فتح الباري ٣٦٨/١٠)
** إسناده حسن
** قال الحافظ فى " الفتح " ١٠ / ٣٦٨: له شاهد من حديث عائشة فى الغيلانيات وسنده حسن أيضا.','6','17',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2293,'<span data-type="title" id=toc-2298>٢٢٩٣ </span>- عن أنس: ماعرض على النبى صلى الله عليه وسلم طيب قط فرده.
** بز
(فتح الباري ٣٧١/١٠)
** إسناده حسن','6','18',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2294,'<span data-type="title" id=toc-2299>٢٢٩٤ </span>- عن أبى هريرة رفعه: " من عرض عليه طيب فلا يرده، فإنه طيب الريح خطيف المحمل ".
** د ن
(فتح الباري ٣٧١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٧١: صححه ابن حبان.','6','19',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2295,'<span data-type="title" id=toc-2300>٢٢٩٥ </span>- مرسل أبى عثمان النهدى: إذا أعطى أحدكم الريحان فلا يرده فإنه خرج من الجنة.
** ت
(فتح الباري ٣٧١/١٠)
** مرسل','6','20',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2296,'<span data-type="title" id=toc-2301>٢٢٩٦ </span>- عن قيس بن حازم قال: دخلت مع أبى على أبى بكر الصديق فرأيت يد أسماء موشومة
** الطبرى
(فتح الباري ٣٧٦/١٠)
** إسناده صحيح','6','21',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2297,'<span data-type="title" id=toc-2302>٢٢٩٧ </span>- حديث أبى هريرة: " أتانى جبريل فقال: أتيتك البارحة فلم يمنعنى أن أكون دخلت إلا أنه كان على الباب تماثيل، وكان فى البيت قرام ستر فيه تماثيل وكان فى البيت كلب فمر برأس التمثال الذى على باب البيت يقطع فيصير كهيئة
الشجرة، ومر بالستر فليقطع فليجعل منه وسادتان منبوذتان توطآن ومر بالكلب
فليخرج ففعل رسول الله صلى الله عليه وسلم.
** د ت ن جه
(فتح الباري ٣٩٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٩٢: حديث أبى هريرة فى السنن وصححه الترمذى وابن حبان، وفى رواية النسائى: إما أن تقطع رءوسها أو تجعل بسطا توطأ.','6','22',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2298,'<span data-type="title" id=toc-2303>٢٢٩٨ </span>- عن جابر: نهى رسول الله صلى الله عليه وسلم أن يركب ثلاثة على دابة.
** طس
(فتح الباري ٣٩٥/١٠)
** إسناده ضعيف','6','23',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2299,'<span data-type="title" id=toc-2304>٢٢٩٩ </span>- عن أبى سعيد رفعه: " لا يركب الدابة فوق اثنين ".
** الطبرى
(فتح الباري ٣٩٦/١٠)
** إسناده لين','6','24',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2300,'<span data-type="title" id=toc-2305>٢٣٠٠ </span>- من مرسل راذان أنه رأى ثلاثة على بغل فقال: لينزل أحدكم فإن رسول الله صلى
الله عليه وسلم لعن الثالث.
** شب
(فتح الباري ٣٩٦/١٠)
** مرسل','6','25',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2301,'<span data-type="title" id=toc-2306>٢٣٠١ </span>- حديث المهاجر بن قنفذ أنه لعن فاعل ذلك وقال: " إنا قد نهينا أن يركب الثلاثة على الدابة ".
** لم
(فتح الباري ٣٩٦/١٠)
** إسناده ضعيف','6','26',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2302,'<span data-type="title" id=toc-2307>٢٣٠٢ </span>- عن ابن مسعود قال: كان يوم بدر ثلاثة على بعير.
** الطبرى
(فتح الباري ٣٩٦/١٠)
** إسناده جيد','6','27',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2303,'<span data-type="title" id=toc-2308>٢٣٠٣ </span>- عن عبد الله بن بريدة عن أبيه قال: بينما رسول الله صلى الله عليه وسلم يمشى إذ جاءه رجل ومعه حمار فقال: يارسول الله أركب وتأخر الرجل، فقال: " لأنت أحق بصدر دابتك إلا أن تجعله لى "، قال: قد جعلته لك فركب.
** د ت حم
(فتح الباري ٣٩٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٣٩٧: صححه ابن حبان والحاكم.','6','28',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2304,'<span data-type="title" id=toc-2309>٢٣٠٤ </span>- حديث المقدام بن معدى كرب: إن الله يوصيكم بأمهاتكم ثم يوصيكم بأمهاتكم ثم يوصيكم بأمهاتكم ثم يوصيكم بآبائكم، ثم يوصيكم بالأقرب فالأقرب.
** خد حم جه
(فتح الباري ٤٠٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٠٢: صححه الحاكم.','6','29',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2305,'<span data-type="title" id=toc-2310>٢٣٠٥ </span>- حديث عائشة: سألت النبى صلى الله عليه وسلم أى الناس أعظم حقا على المرأة قال: " زوجها "، قلت: فعلى الرجل؟ ، قال: " أمه ".
** حم ن
(فتح الباري ٤٠٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٠٢: صححه الحاكم.','6','30',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2306,'<span data-type="title" id=toc-2311>٢٣٠٦ </span>- لابن عمر حديث بلفظ: ثلاثة لا ينظر الله إليهم يوم القيامة: العاق لوالديه ومدمن الخمر والمنان.
** ن بز
(فتح الباري ٤٠٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٠٥: صححه ابن حبان والحاكم.','6','31',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2307,'<span data-type="title" id=toc-2312>٢٣٠٧ </span>- عن ابن عباس قال: كل ماتوعد الله عليه بالنار كبيرة.
** ابن أبى حاتم
(فتح الباري ٤١٠/١٠)
** رجاله لا بأس بهم','6','32',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2308,'<span data-type="title" id=toc-2313>٢٣٠٨ </span>- حديث عبد الله بن أنيس الجهنى مرفوعا قال: " من أكبر الكبائر _ فذكر منها _ اليمين الغموس ".
** ت
(فتح الباري ٤١١/١٠)
** إسناده حسن','6','33',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2309,'<span data-type="title" id=toc-2314>٢٣٠٩ </span>- حديث أبى هريرة رفعه: " أن من أكبر الكبائر استطالة المرء فى عرض رجل مسلم ".
** ابن أبى حاتم
(فتح الباري ٤١١/١٠)
** إسناده حسن','6','34',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2310,'<span data-type="title" id=toc-2315>٢٣١٠ </span>- حديث بريدة رفعه: " من أكبر الكبائر _ فذكرمنها _ منع فضل الماء ومنع
الفحل ".
** بز
(فتح الباري ٤١١/١٠)
** إسناده ضعيف','6','35',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2311,'<span data-type="title" id=toc-2316>٢٣١١ </span>- حديث ابن عمر رفعه: " أكبر الكبائر سوء الظن بالله ".
** ابن مردويه
(فتح الباري ٤١١/١٠)
** إسناده ضعيف','6','36',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2312,'<span data-type="title" id=toc-2317>٢٣١٢ </span>- عن أبى هريرة: إن صلة الرحم محبة فى الأهل مثراة فى المال فسأة فى الأثر.
** ت
(فتح الباري ٤١٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤١٥: حسنه الترمذى.','6','37',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2313,'<span data-type="title" id=toc-2318>٢٣١٣ </span>- عن عائشة مرفوعا: " صلة الرحم وحسن الجوار وحسن الخلق يعمران الديار ويزيدان فى الأعمار ".
** حم
(فتح الباري ٤١٥/١٠)
** إسناده رجاله ثقات','6','38',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2314,'<span data-type="title" id=toc-2319>٢٣١٤ </span>- حديث أنس رفعه: " إن الصدقة وصلة الرحم يزيد الله بهما فى العمر، ويدفعها
بهما ميته السوء ".
** يع
(فتح الباري ٤١٦/١٠)
** إسناده ضعيف','6','39',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2315,'<span data-type="title" id=toc-2320>٢٣١٥ </span>- عن أبى الدرداء قال: ذكر عند رسول الله صلى الله عليه وسلم من وصل رحمه أنشىء له فى أجله، فقال: " إنه ليس زيادة فى عمره، قال الله تعالى (فإذا جاء أجلهم..) الآية ولكن الرجل تكون له الذرية الصالحة يدعون له من بعده ".
** طص
(فتح الباري ٤١٦/١٠)
** إسناده ضعيف','6','40',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2316,'<span data-type="title" id=toc-2321>٢٣١٦ </span>- حديث أسماء بنت عميس مرفوعا، سمعت رسول الله صلى الله عليه وسلم يقول: " صالح المؤمنين على بن أبى طالب ".
** ابن مردويه
(فتح الباري ٤٢٢/١٠)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ١٠ / ٤٢٢: أخرجه ابن مردويه بسندين ضعيفين.','6','41',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2317,'<span data-type="title" id=toc-2322>٢٣١٧ </span>- حديث ابن مسعود رفعه: " من كانت له ابنة فأدبها وأحسن تعليمها وأوسع عليها
من نعمة الله التى أوسع عليه ".
** طب
(فتح الباري ٤٢٨/١٠)
** إسناده واه','6','42',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2318,'<span data-type="title" id=toc-2323>٢٣١٨ </span>- أبو هريرة قال: دخل عيينة بن حصن على رسول الله صلى الله عليه وسلم فرآه يقبل الحسن والحسين فقال: أتقبلهما يارسول الله؟ ، إن لى عشرة فما قبلت أحدا منهم.
** يع
(فتح الباري ٤٣٠/١٠)
** إسناده رجاله ثقات','6','43',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2319,'<span data-type="title" id=toc-2324>٢٣١٩ </span>- حديث ابن مسعود: لن تؤمنوا حتى ترحموا، قالوا: كلنا رحيم يارسول الله، قال: " إنه ليس برحمة أحدكم صاحبه ولكنها رحمة الناس رحمة العامة ".
** طب
(فتح الباري ٤٣٨/١٠)
** رجاله ثقات','6','44',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2320,'<span data-type="title" id=toc-2325>٢٣٢٠ </span>- عن أبى هريرة قال: دخل الأعرابى المسجد فقال: اللهم اغفر لى ولمحمد ولا تغفر لأحد معنا، فقال النبى صلى الله عليه وسلم: " لقد احتظرت واسعا " ثم تنحى الأعرابى فبال فى ناحية المسجد.
** جه
(فتح الباري ٤٣٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٣٩: وصححه ابن حبان من وجه آخر.','6','45',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2321,'<span data-type="title" id=toc-2326>٢٣٢١ </span>- حديث ابن مسعود رفعه: " أرحم من فى الأرض يرحمك من فى السماء ".
** طب
(فتح الباري ٤٤٠/١٠)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١٠ / ٤٤٠: رواته ثقات.','6','46',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2322,'<span data-type="title" id=toc-2327>٢٣٢٢ </span>- عن معاذ بن جبل، فقالوا: يارسول الله ماحق الجار على الجار؟ ، قال: " إن استقرضك أقرضته وإن استعانك أعنه، وإن مرض عدته، وإن احتاج أعطيته وإن افتقر عدت عليه وإن أصابه خير هنيته، وإن أصابته مصيبة عزيته، وإذا مات اتبعت جنازته، ولا تستطيل عليه بالبناء فتحجب عنه الريح إلا بإذنه، ولا
تؤذيه بريح قدرك إلا أن تغرف له، وإن اشتريت فاكهة فأهد له، وإن لم تفعل فأدخلها سرا ولا تخرج بها ولدك ليغيظ بها ولده ".
** شيخ من كتاب التوبيخ
(فتح الباري ٤٤٦/١٠)
** إسناده واه
** قال الحافظ فى " الفتح " ١٠ / ٤٤٦: أخرجه الطبرانى من حديث بهز بن حكيم عن أبيه عن جده والخرائطى فى مكارم الأخلاق من حديث عمرو بن شعيب عن أبيه عن جده وأسانيدهم واهية، لكن اختلاف مخارجها يشعر بأن للحديث أصلا.','6','47',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2323,'<span data-type="title" id=toc-2328>٢٣٢٣ </span>- حديث البراء رفعه: قال: " فإن لم تطق ذلك فكف لسانك إلا من خير ".
** حم
(فتح الباري ٤٤٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٤٦: صححه ابن حبان.','6','48',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2324,'<span data-type="title" id=toc-2329>٢٣٢٤ </span>- عن كعب بن مالك مرفوعا: " ألا إن أربعين دارا جارا ".
** طب
(فتح الباري ٤٤٧/١٠)
** إسناده ضعيف','6','49',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2325,'<span data-type="title" id=toc-2330>٢٣٢٥ </span>- حديث أبى الدرداء: من أعطى حظه من الرفق فقد أعطى حظه من الخير ... الحديث.
** ت
(فتح الباري ٤٤٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٤٩: صححه الترمذى وابن خزيمة.','6','50',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2326,'<span data-type="title" id=toc-2331>٢٣٢٦ </span>- حديث عن ابن عباس رفعه: " من سعى لأخيه المسلم فى حاجة قضيت له أو لم تقض غفر له ".
** لم
(فتح الباري ٤٥١/١٠)
** إسناده ضعيف','6','51',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2327,'<span data-type="title" id=toc-2332>٢٣٢٧ </span>- حديث أسامة رفعه: " إن الله لا يحب كل فحاش متفحش ".
** حم طب
(فتح الباري ٤٥٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٥٣: صححه ابن حبان.','6','52',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2328,'<span data-type="title" id=toc-2333>٢٣٢٨ </span>- كان النبى صلى الله عليه وسلم يقول: " اللهم كما حسنت خلقى فحسن خلقى ".
** حم
(فتح الباري ٤٥٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٥٦: صححه ابن حبان.','6','53',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2329,'<span data-type="title" id=toc-2334>٢٣٢٩ </span>- حديث ابن الحنفية: إذا سئل فأراد أن يفعل قال: نعم وإذا لم يرد أن يفعل سكت.
** سعد
(فتح الباري ٤٥٧/١٠)
** مرسل','6','54',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2330,'<span data-type="title" id=toc-2335>٢٣٣٠ </span>- حديث أبى هريرة رفعه: " إن من أكمل المؤمنين أحسنهم خلقا ".
** ت ك
(فتح الباري ٤٥٨/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٥٨: حسنه الترمذى وصححه الحاكم.','6','55',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2331,'<span data-type="title" id=toc-2336>٢٣٣١ </span>- حديث أبى الدرداء رفعه: " ماشىء أثقل فى الميزان من حسن الخلق ".
** خد د ت
(فتح الباري ٤٥٨/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٥٨: صححه الترمذى وزاد: وإن صاحب حسن الخلق يبلغ درجة صاحب الصوم والصلاة.','6','56',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2332,'<span data-type="title" id=toc-2337>٢٣٣٢ </span>- حديث أبى هريرة: سئل النبى صلى الله عليه وسلم عن أكثر مايدخل الناس الجنة، فقال: " تقوى الله وحسن الخلق ".
** ت حب خد
(فتح الباري ٤٥٨/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٥٨: صححه الترمذى وابن حبان.','6','57',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2333,'<span data-type="title" id=toc-2338>٢٣٣٣ </span>- حديث أبى هريرة رفعه: " إنكم لن تسعوا الناس بأموالكم ولكن يسعهم منكم بسط الوجه وحسن الخلق ".
** بز
(فتح الباري ٤٥٩/١٠)
** إسناده حسن','6','58',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2334,'<span data-type="title" id=toc-2339>٢٣٣٤ </span>- حديث الأشجع العصرى: أن النبى صلى الله عليه وسلم قال: " إن فيك لخصلتين يحبهما الله: الحلم والأناة "، قال: يارسول الله قديما كانا فى أو حديثا قال: " قديما "، قال: الحمد لله الذى جبلنى على خلقين يحبهما.
** حم ن خد
(فتح الباري ٤٥٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٥٩: صححه ابن حبان.','6','59',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2335,'<span data-type="title" id=toc-2340>٢٣٣٥ </span>- فى رواية هشام بن عروة عن أبيه، قلت لعائشة: ماكان رسول الله صلى الله عليه
وسلم يصنع فى بيته؟ ، قالت: يخيط ثوبه ويخصف نعله ويعمل مايعمل الرجال فى
بيوتهم.
** حم سعد
(فتح الباري ٤٦١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٦١: صححه ابن حبان.','6','60',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2336,'<span data-type="title" id=toc-2341>٢٣٣٦ </span>- عن أبى الدرداء رفعه: " إن العبد إذا لعن شيئا صعدت اللعنة إلى السماء فتغلق أبواب السماء دونها ثم تهبط إلى الأرض فتأخذ يمنة ويسرة فإن لم تجد مساغا رجعت إلى الذى لعن، فإن كان أهلا وإلا رجعت إلى قائلها ".
** د
(فتح الباري ٤٦٧/١٠)
** إسناده جيد','6','61',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2337,'<span data-type="title" id=toc-2342>٢٣٣٧ </span>- حديث أبى هريرة رفعه: " من أكل لحم أخيه فى الدنيا قرب له يوم القيامة فيقال له: كله ميتا كما أكلته حيا، فيأكله ويكلح ويصيح ".
** يع
(فتح الباري ٤٧٠/١٠)
** إسناده حسن','6','62',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2338,'<span data-type="title" id=toc-2343>٢٣٣٨ </span>- حديث أبى هريرة فى قصة ماعز ورجمه فى الزنا، وإن رجلا قال لصاحبه: انظر إلى هذا الذى ستى الله عليه فلم يدع نفسه حتى رجم رجم الكلب، فقال لهماالنبى صلى الله عليه وسلم: " كلا من جيفة هذا الحمار _ لحمار ميت _ فما نلتما من
عرض هذا الرجل أشد من هذه الجيفة ".
** خد
(فتح الباري ٤٧٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٧٠: صححه ابن حبان.','6','63',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2339,'<span data-type="title" id=toc-2344>٢٣٣٩ </span>- عن جابر قال: كنا مع النبى صلى الله عليه وسلم فهاجت ريح فتنة فقال النبى صلى الله عليه وسلم: " هذه ريح الذين يغتابون المؤمنين ".
** حم خد
(فتح الباري ٤٧٠/١٠)
** إسناده حسن','6','64',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2340,'<span data-type="title" id=toc-2345>٢٣٤٠ </span>- حديث يعلى بن شبابة: إن النبى صلى الله عليه وسلم مر على قبر يعذب صاحبه فقال: " إن هذا كان يأكل لحوم الناس " ثم دعا بجريدة رطبة.... الحديث.
** حم طب
(فتح الباري ٤٧١/١٠)
** رجاله موثقون
** قال الحافظ فى " الفتح " ١٠ / ٤٧١: رواته موثقون.','6','65',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2341,'<span data-type="title" id=toc-2346>٢٣٤١ </span>- حديث أنس رفعه: " إذا مدح الفاسق غضب الرب ".
** يع ابن أبى الدنيا فى الصمت
(فتح الباري ٤٧٨/١٠)
** إسناده ضعيف','6','66',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2342,'<span data-type="title" id=toc-2347>٢٣٤٢ </span>- من طريق أبى الضحى قال: قال شيتر بن شكل لمسروق: حدث ياأباعائشة وأصدقك قال: هل سمعت عبد الله بن مسعود ليقول: ما فى القرآن أجمع لحلال وحرام وأمر، ونهى من هذه الآية (إن الله يأمر بالعدل والإحسان وإيتاء ذى
القربى) قال: نعم.
** خد
(فتح الباري ٤٧٩/١٠)
** إسناده صحيح','6','67',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2343,'<span data-type="title" id=toc-2348>٢٣٤٣ </span>- عن أنس قال: التدابر التصارم.
** الحسين بن الحسن المروزى فى زيادات كتاب البر والصلة لابن المبارك
(فتح الباري ٤٨٣/١٠)
** إسناده صحيح','6','68',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2344,'<span data-type="title" id=toc-2349>٢٣٤٤ </span>- عن أبى أمامة مرفوعا: "لا أقول إلا ما أقول ".
** حم
(فتح الباري ٤٨٣/١٠)
** إسناده حسن','6','69',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2345,'<span data-type="title" id=toc-2350>٢٣٤٥ </span>- حديث ابن عمر رفعه: " اجتنبوا هذه القاذورات التى نهى الله عنها، فمن ألم بشىء منها فليستتر بستر الله.... " الحديث.
** ك
(فتح الباري ٤٨٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٨٧: ليس على شرط البخارى، وهو فى الموطأ من مرسل زيد بن أسلم.','6','70',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2346,'<span data-type="title" id=toc-2351>٢٣٤٦ </span>- حديث ثوبان عن النبى صلى الله عليه وسلم: " من مات وهو برىء من الكبر والغلول والدين دخل الجنة ".
** ت ن جه
(فتح الباري ٤٩١/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٩١: صححه ابن حبان والحاكم.','6','71',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2347,'<span data-type="title" id=toc-2352>٢٣٤٧ </span>- عن ابن عمر رفعه: " إياكم والكبر فإن الكبر يكون فى الرجل وإن عليه
العباءة ".
** طس
(فتح الباري ٤٩١/١٠)
** رجاله موثقون
** قال الحافظ فى " الفتح " ١٠ / ٤٩١: رواته موثقون.','6','72',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2348,'<span data-type="title" id=toc-2353>٢٣٤٨ </span>- حديث أبى هريرة: فإن مرت به ثلاث فلقيه فليسلم عليه، فإن رد عليه فقد اشتركا فى الأجر، وإن لم يرد عليه فقد باء بالإثم وخرج المسلم من الهجرة.
