(فتح الباري ٤٩٥/١٠)
** إسناده صحيح','6','73',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2349,'<span data-type="title" id=toc-2354>٢٣٤٩ </span>- حديث هشام بن عامر: " فإنهما ناكثان عن الحق ماداما على صرامهما، وأولهما فيئا يكون سبقة كفارة "، وزاد فى آخره: " فإن ماتا على صرامهما لم يدخلا الجنة جميعا ".
** حم خد
(فتح الباري ٤٩٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٩٥: صححه ابن حبان.','6','74',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2350,'<span data-type="title" id=toc-2355>٢٣٥٠ </span>- " زر غبا تزدد حبا ".
**
(فتح الباري ٤٩٨/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٤٩٨: قد ورد من طريق أكثرها غرائب لا يخلو واحد منها من مقال.','6','75',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2351,'<span data-type="title" id=toc-2356>٢٣٥١ </span>- حديث أبى هريرة رفعه: " من عاد مريضا أو زاد أخا له فى الله ناداه مناد طبت وطاب ممشاك وتبوأت من الجنة منزلا ".
** ت
(فتح الباري ٥٠٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٠٠: حسنه الترمذى وصححه ابن حبان، وله شاهد عند البزار من حديث أنس بسند جيد.','6','76',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2352,'<span data-type="title" id=toc-2357>٢٣٥٢ </span>- حديث معاذ بن جبل مرفوعا: " حقت محبتى للمتزاورين فى..... " الحديث.
** ما
(فتح الباري ٥٠٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٠٠: صححه ابن حبان، وأخرجه أحمد بسند صحيح من حديث عتبان بن مالك.','6','77',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2353,'<span data-type="title" id=toc-2358>٢٣٥٣ </span>- عن أنس قال: آخى النبى صلى الله عليه وسلم بين ابن مسعود والزبير.
** حم خد
(فتح الباري ٥٠١/١٠)
** إسناده صحيح','6','78',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2354,'<span data-type="title" id=toc-2359>٢٣٥٤ </span>- حديث عبد الرحمن بن عوف مرفوعا: " شهدت مع عمومتى حلف المطيبين فما أحب أن أنكثه ".
** حم يع
(فتح الباري ٥٠٢/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٠٢: صححه ابن حبان والحاكم.','6','79',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2355,'<span data-type="title" id=toc-2360>٢٣٥٥ </span>- عن أبى بكر الصديق قال: الكذب يجانب الإيمان.
** هب
(فتح الباري ٥٠٨/١٠)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٠ / ٥٠٨: وأخرجه عنه مرفوعا وقال: الصحيح موقوف.','6','80',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2356,'<span data-type="title" id=toc-2361>٢٣٥٦ </span>- حديث سعد بن أبى وقاص رفعه قال: " يطبع المؤمن على كل شىء إلا الخيانة والكذب ".
** بز
(فتح الباري ٥٠٨/١٠)
** إسناده قوى','6','81',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2357,'<span data-type="title" id=toc-2362>٢٣٥٧ </span>- عن ابن عباس رفعه: " الهدى الصالح والسمت الصالح والأقتصاد جزء من خمسة وعشرين جزءا من النبوة ".
** خد د حم
(فتح الباري ٥٠٩/١٠)
** إسناده حسن','6','82',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2358,'<span data-type="title" id=toc-2363>٢٣٥٨ </span>- من طريق زيد بن وهب: سمعت ابن مسعود قال: اعلموا أن حسن الهدى فى آخر الزمان خير من بعض العمل.
** خد
(فتح الباري ٥١٠/١٠)
** إسناده صحيح','6','83',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2359,'<span data-type="title" id=toc-2364>٢٣٥٩ </span>- عن ابن عمر رفعه: " المؤمن الذى يخالط الناس ويصبر على أذاهم خير من الذى لا يخالط الناس ولا يصبر على أذاهم ".
** جه
(فتح الباري ٥١٢/١٠)
** إسناده حسن
** قال الحافظ فى " الفتح " ١٠ / ٥١٢: ليس على شرط البخارى، وأخرجه الترمذى من حديث صحابى لم يسم.','6','84',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2360,'<span data-type="title" id=toc-2365>٢٣٦٠ </span>- عن أنس: أن النبى مر بقوم يصطرعون فقال: " ماهذا؟ "، قالوا: فلان ما يصارع أحدا إلا صرعه، قال: " أفلا أدلكم على من هو أشد منه، رجل كلمه رجل فكظم غيظه فغلبه وغلب شيطانه وغلب شيطان صاحبه ".
** بز
(فتح الباري ٥١٩/١٠)
** إسناده حسن','6','85',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2361,'<span data-type="title" id=toc-2366>٢٣٦١ </span>- حديث أبى هريرة قال: قالوا يارسول الله إنك تداعبنا، قال: " إنى لا أقول إلا حقا ".
** ت
(فتح الباري ٥٢٦/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٢٦: حسنه الترمذى.','6','86',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2362,'<span data-type="title" id=toc-2367>٢٣٦٢ </span>- حديث جابر عن النبى صلى الله عليه وسلم قال: " مداراة الناس صدقة ".
** عد طس
(فتح الباري ٥٢٨/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٢٨: فى سنده يوسف بن محمد بن المنكدر ضعفوه، وقال ابن عدى: أرجو أنه لا بأس
به، وأخرجه ابن أبى عاصم فى آداب الحكماء بسند أحسن منه.','6','87',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2363,'<span data-type="title" id=toc-2368>٢٣٦٣ </span>- حديث أبى هريرة: رأس العقل بعد الإيمان بالله مداراة الناس.
** بز
(فتح الباري ٥٢٨/١٠)
** إسناده ضعيف','6','88',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2364,'<span data-type="title" id=toc-2369>٢٣٦٤ </span>- حديث أبى سعيد مرفوعا: " لا حليم إلا ذو عثرة، ولا حكيم إلا ذو تجربة ".
** حم
(فتح الباري ٥٢٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٢٩: صححه ابن حبان.','6','89',NULL,NULL,'0');
