INSERT INTO [b88] VALUES (2369,'<span data-type="title" id=toc-2374>٢٣٦٩ </span>- " أعظم الناس فرية رجل هاجى رجلا فهجا القبيلة بأسرها ".
** جه
(فتح الباري ٥٣٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٣٩: صححه ابن حبان.','6','94',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2370,'<span data-type="title" id=toc-2375>٢٣٧٠ </span>- حديث عبد الله بن عمرو مرفوعا: " الشعر بمنزلة الكلام، فحسنه كحسن الكلام وقبيحه كقبيح الكلام ".
** خد
(فتح الباري ٥٣٩/١٠)
** إسناده ضعيف','6','95',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2371,'<span data-type="title" id=toc-2376>٢٣٧١ </span>- حديث ابن عباس: إن من الشعر حكما.
** خد د ت جه
(فتح الباري ٥٤٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٤٠: حسن الترمذى.','6','96',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2372,'<span data-type="title" id=toc-2377>٢٣٧٢ </span>- عن أبى سلمة بن عبد الرحمن قال: لم يكن أصحاب رسول الله صلى الله عليه وسلم منحرفين ولا متماوتين، وكانوا يتناشدون الأشعار فى مجالسهم ويذكرون أمر جاهليتهم، فإذا أريد أحدهم على شىء من دينه دارت حماليق عينيه.
** شب
(فتح الباري ٥٤٠/١٠)
** إسناده حسن','6','97',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2373,'<span data-type="title" id=toc-2378>٢٣٧٣ </span>- حديث جابر بن سمرة قال: كان أصحاب رسول الله صلى الله عليه وسلم يتذاكرون الشعر وحديث الجاهلية عند رسول الله صلى الله عليه وسلم فلا ينهاهم وربما يتبسم.
** حم شب ت
(فتح الباري ٥٤٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٤٠: صححه الترمذى.','6','98',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2374,'<span data-type="title" id=toc-2379>٢٣٧٤ </span>- حديث أنس رفعه: " جاهدوا المشركين بألسنتكم ".
