INSERT INTO [b88] VALUES (2387,'<span data-type="title" id=toc-2392>٢٣٨٧ </span>- عن أنس رفعه: " يسمونهم محمدا ويلعنونهم ".
** بز يع
(فتح الباري ٥٧٢/١٠)
** إسناده لين','6','112',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2388,'<span data-type="title" id=toc-2393>٢٣٨٨ </span>- عن جابر رفعه: " من تسمى باسمى فلا يكتنى بكنيتى، ومن اكتنى بكنيتى فلا يتسمى باسمى ".
** حم د
(فتح الباري ٥٧٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٧٣: حسن الترمذى وصححه ابن حبان.','6','113',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2389,'<span data-type="title" id=toc-2394>٢٣٨٩ </span>- حديث على قال: قلت: يارسول الله إن ولد لى من بعدك ولد أسميه باسمك وأكنيه بكنيتك؟ ، قال: " نعم ".
** خد د جه
(فتح الباري ٥٧٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٧٣: صححه الحاكم.','6','114',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2390,'<span data-type="title" id=toc-2395>٢٣٩٠ </span>- إن زينت بنت جحش قالت: يارسول الله اسمى برة فلو غيرته فإن البرة صغيرة فقال: " لو كان مسلما لسميته باسم من أسمائهم ولكن هو جحش فالجحش أكبر من البرة ".
** قط فى المؤتلف
(فتح الباري ٥٧٦/١٠)
** إسناده ضعيف','6','115',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2391,'<span data-type="title" id=toc-2396>٢٣٩١ </span>- حديث أبى الدرداء رفعه: " إنكم تدعون يوم القيامة بأسمائكم وأسماء آبائكم فأحسنوا أسماءكم ".
** د
(فتح الباري ٥٧٧/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٧٧: صححه ابن حبان، ورجاله ثقات إلا أن فى سنده انقطاعا بين عبد الله بن أبى زكريا راويه عن أبى الدرداء وأبى الدرداء فإنه لم يدركه.','6','116',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2392,'<span data-type="title" id=toc-2397>٢٣٩٢ </span>- حديث يوسف بن عبد الله بن سلام قال: سمانى النبى صلى الله عليه وسلم يوسف ... الحديث.
** خد تم
(فتح الباري ٥٧٨/١٠)
** إسناده صحيح','6','117',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2393,'<span data-type="title" id=toc-2398>٢٣٩٣ </span>- حديث ابن عباس قال: لما مات إبراهيم ابن النبى صلى الله عليه وسلم وقال: " إن له مرضعا فى الجنة، لو عاش لكان صديقا نبيا ولإن عتقت
أخواله القبط ".
** جه
(فتح الباري ٥٧٨/١٠)
** صحيح
** قال الحافظ فى " الفتح " ١٠ / ٥٧٩: هذه عدة أحاديث صحيحة عن هؤلاء الصحابة.','6','118',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2394,'<span data-type="title" id=toc-2399>٢٣٩٤ </span>- من طريق السدى: سألت أنسا كم بلغ إبراهيم؟ ، قال: كان قد ملأ المهد ولو بقى لكان نبيا، ولكن لم يكن ليبقى، لأن نبيكم آخر الأنبياء. ولفظ أحمد: لو عاش إبراهيم ابن النبى صلى الله عليه وسلم لكان صديقا نبيا.
** حم ابن منده
(فتح الباري ٥٧٩/١٠)
** صحيح
** قال الحافظ فى " الفتح " ١٠ / ٥٧٩: هذه عدة أحاديث صحيحة عن هؤلاء الصحابة.','6','119',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2395,'<span data-type="title" id=toc-2400>٢٣٩٥ </span>- حديث ابن مسعود:نهى رسول الله صلى الله عليه وسلم أن يسمى الرجل عبده أو ولده حربا أو مرة أو وليدا.... الحديث.
** طب
(فتح الباري ٥٨٠/١٠)
** إسناده ضعيف جدا','6','120',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2396,'<span data-type="title" id=toc-2401>٢٣٩٦ </span>- حديث معاذ بن جبل قال: خرج علينا رسول الله صلى الله عليه وسلم _ فذكر حديثا فيه _ قال: الوليد اسم فرعون هادم شرائع الإسلام يبوء بدمه رجل من أهل بيته.
** طب
(فتح الباري ٥٨١/١٠)
** إسناده ضعيف جدا','6','121',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2397,'<span data-type="title" id=toc-2402>٢٣٩٧ </span>- حديث صهيب: إن عمر قال له: مالك تكنى أبا يحيى وليس لكط ولد؟ ، قال: أن النبى صلى الله عليه وسلم كنانى.
** جه حم طحا
(فتح الباري ٥٨٢/١٠)
** مقال الحافظ فى " الفتح " ١٠ / ٥٨٢: صححه الحاكم.','6','122',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2398,'<span data-type="title" id=toc-2403>٢٣٩٨ </span>- عن ابن مسعود: أن النبى صلى الله عليه وسلم كناه أبا عبد الرحمن قبل أن يولد له.
** طب
(فتح الباري ٥٨٢/١٠)
** إسناده صحيح','6','123',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2399,'<span data-type="title" id=toc-2404>٢٣٩٩ </span>- " أبغض الأسماء إلى الله مالك وخالد ".
** لم
(فتح الباري ٥٨٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٨٩: قال الداودى: ماأراه محفوظا لأن فى الصحابة من تسمى بهما أ. هـ. الحديث الذى أشار إليه ماوقفت عليه بعد البحث.','6','124',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2400,'<span data-type="title" id=toc-2405>٢٤٠٠ </span>- عن أبى هريرة رفعه: " أحب الأسماء إلى الله ماسمى به، وأصدقها الحارث وهمام، وأكذب الأسماء خالد ومالك، وأبغضهما إلى الله ماسمى لغيره ".
** لم
(فتح الباري ٥٨٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٥٨٩: فى ترجمة إبراهيم بن الفضل المدنى أحد الضعفاء من مناكيره عن سعيد المقبرى عن أبى هريرة رفعه.','6','125',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2401,'<span data-type="title" id=toc-2406>٢٤٠١ </span>- عن مطرف بن عبد الله قال: صحبت عمران بن حصين من الكوفة إلى البصرة فما أتى عليه يوم إلا أنشدنا فيه شعرا وقال: إن فى معاريض الكلام مندوحة عن الكذب.
** الطبرى فى التهذيب طب
(فتح الباري ٥٩٤/١٠)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١٠ / ٥٩٤: أخرجه ابن عدى من وجه آخر عن قتادة مرفوعا ووهاه، وأخرجه أبو بكر بن كامل فى فوائده والبيهقى فى الشعب من طريقه كذلك، وأخرجه ابن عدى.','6','126',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2402,'<span data-type="title" id=toc-2407>٢٤٠٢ </span>- عن على قال: من قال عند عطسة سمعها: الحمد لله رب العالمين على كل حال ماكان لم يجد وجع الضرس ولا الأذن أبدا.
** خد طب
(فتح الباري ٦٠٠/١٠)
** رجاله ثقات
** تعقيب: قال الحافظ فى " الفتح " ١٠ / ٦٠٠: موقوف رجاله ثقات ومثله لا يقال من قبل الرأى فله حكم الرفع.','6','127',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2403,'<span data-type="title" id=toc-2408>٢٤٠٣ </span>- عن على مرفوعا: " من بادر العاطس بالحمد عوفى من وجع الخاصرة ولم يشتك ضرسه أبدا ".
** طب
(فتح الباري ٦٠٠/١٠)
** إسناده ضعيف','6','128',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2404,'<span data-type="title" id=toc-2409>٢٤٠٤ </span>- عن ابن عباس قال: إذا عطس الرجل فقال: الحمد لله، قال الملك: رب
العالمين فإن قال: رب العالمين، قال الملك: يرحمك الله.
** خد طب
(فتح الباري ٦٠٠/١٠)
** إسناده لا بأس به','6','129',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2405,'<span data-type="title" id=toc-2410>٢٤٠٥ </span>- عن أم سلمة قالت: عطس رجل عند النبى صلى الله عليه وسلم فقال: الحمد لله فقال النبى صلى الله عليه وسلم: " يرحمك الله "، وعطس آخر فقال: الحمد لله رب العالمين حمدا طيبا كثيرا مباركا فيه، فقال: " ارتفع هذا على هذا تسعة عشرة درجة ".
** الطبرى فى التهذيب
(فتح الباري ٦٠٠/١٠)
** إسناده لا بأس به','6','130',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2406,'<span data-type="title" id=toc-2411>٢٤٠٦ </span>- حديث رفاعة بن رافع قال: صليت مع النبى صلى الله عليه وسلم فعطست فقلت: الحمد لله حمدا طيبا مباركا فيه مباركا عليه كما يحب ربنا ويرضى، فلما انصرف قال: " من المتكلم "، ثلاث فقلت: أنا فقال: " والذى نفسى بيده لقد ابتدرها بضعة وثلاثون ملكا أيهم يصعد بها ".
** ت طب
(فتح الباري ٦٠٠/١٠)
** إسناده لا بأس به','6','131',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2407,'<span data-type="title" id=toc-2412>٢٤٠٧ </span>- عطس رجل فقال: الحمد لله والصلاة على رسول الله صلى الله عليه وسلم فقال ابن عمر: الحمد لله والصلاة على رسول، ولكن ليس هكذا علمنا رسول الله صلى الله عليه وسلم.
** ت
(فتح الباري ٦٠٠/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٦٠١: قال الترمذى: غريب لا نعرفه إلا فى رواية زياد بن الربيع أ. هـ. قلت: وهو صدوق، قال البخارى: وفيه نظر، وقال ابن عدى: لا أرى به بأسا ورجح البيهقى ماتقدم على رواية زياد والله أعلم أ. هـ.','6','132',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2408,'<span data-type="title" id=toc-2413>٢٤٠٨ </span>- عن مجاهد أن ابن عمر سمع ابنه عطس فقال: أب، فقال: ما أب إن الشيطان جعلها بين العطسة والحمد.
** خد
(فتح الباري ٦٠١/١٠)
** إسناده صحيح','6','133',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2409,'<span data-type="title" id=toc-2414>٢٤٠٩ </span>- عن أبى هريرة قال: كان النبى صلى الله عليه وسلم إذا عطس وضع يده على فيه وخفض صوته.
** د ت
(فتح الباري ٦٠٢/١٠)
** إسناده جيد','6','134',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2410,'<span data-type="title" id=toc-2415>٢٤١٠ </span>- حديث أبى موسى الأشعرى قال: كانت اليهود يتعاطسون عند النبى صلى الله عليه
وسلم رجاء أن يقول: يرحمكم الله فكان يقول: " يهديكم الله ويصلح بالكم ".
** د
(فتح الباري ٦٠٤/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٦٠٤: صححه الحاكم.','6','135',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2411,'<span data-type="title" id=toc-2416>٢٤١١ </span>- عن عبد الله بن أبى بكر عن أبيه رفعه: " إن عطش فشمته ثم إن عطس فشمته ثم إن عطس فقل إنك مضنوك "، قال ابن أبى بكر: لا أدرى بعد الثالثة أو الرابعة.
** ما
(فتح الباري ٦٠٤/١٠)
** مرسل جيد','6','136',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2412,'<span data-type="title" id=toc-2417>٢٤١٢ </span>- عن أبى هريرة: إذا عطس أحدكم فليشمته جليسه فإن زاد على ثلاث فهو مزكوم ولا يشمته بعد ثلاث.
** يع سنى
(فتح الباري ٦٠٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٦٠٥: قال النووى: فيه رجل لم أتحقق حاله وباقى إسناده صحيح أ. هـ. قلت: الرجل المذكور هو سليمان بن أبى داود الحرانى والحديث عندهما من رواية محمد بن سليمان عن أبيه، ومحمد موثق وأبوه يقال له الحرانى ضعيف،
قال النسائى: ليس بثقة ولا مأمون.','6','137',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2413,'<span data-type="title" id=toc-2418>٢٤١٣ </span>- عن عبيد بن رفاعة الصحابى قال: قال رسول الله صلى الله عليه وسلم: " يشمت العاطس ثلاثا فإن زاد شئت فشمته وإن شئت فلا ".
** د ت
(فتح الباري ٦٠٥/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٦٠٥: قال النووى: هو حديث ضعيف، قال فيه الترمذى: هذا حديث غريب، وإسناده
مجهول أ. هـ.، قلت: إطلاقه عليه الضعف ليس بجيد، إذ لا يلزم من الغرابة
الضعف، وأما وصف الترمذى إسناده بكونه مجهولا فلم يرد جميع رجال الإسناد فإن معظمهم موثقون، وإنما وقع فى روايته تغيير اسم بعض رواته وإبهام اثنين منهم.','6','138',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2414,'<span data-type="title" id=toc-2419>٢٤١٤ </span>- عن عدى بن ثابت عن أبيه عن جده رفعه قال: " العطاس والنعاس والتثاؤب فى الصلاة من الشيطان ".
** ت
(فتح الباري ٦٠٧/١٠)
** إسناده ضعيف','6','139',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2415,'<span data-type="title" id=toc-2420>٢٤١٥ </span>- عن أبى هريرة: أن الله يكره التثاؤب ويحب العطاس فى الصلاة.
** شب
(فتح الباري ٦٠٧/١٠)
** إسناده ضعيف','6','140',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2416,'<span data-type="title" id=toc-2421>٢٤١٦ </span>- عن أبى هريرة رفعه: " لما خلق الله آدم عطس، فألهمه ربه أن قال: الحمد لله، فقال له ربه: يرحمك الله ".
** هب
(فتح الباري ٦٠٨/١٠)','6','141',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2417,'<span data-type="title" id=toc-2422>٢٤١٧ </span>- عن أبى جمرة _ بالجيم _ سمعت ابن عباس إذا شمت يقول: عافنا الله وإياكم من النار يرحمكم الله.
** خد
(فتح الباري ٦٠٩/١٠)
** إسناده صحيح','6','142',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2418,'<span data-type="title" id=toc-2423>٢٤١٨ </span>- عن ابن عمر قال: اجتمع اليهود والمسلمون فعطس النبى صلى الله عليه وسلم فشمته الفريقان جميعا فقال للمسلمين: " يغفر الله لكم ويرحمنا وإياكم " وقال لليهود: " يهديكم الله ويصلح بالكم ".
** هب
(فتح الباري ٦٠٩/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٦٠٩: قال البيهقى: تفرد به عبد الله بن عبد العزيز بن أبى رواد عن أبيه عن نافع وعبد الله ضعيف.','6','143',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2419,'<span data-type="title" id=toc-2424>٢٤١٩ </span>- من مرسل يزيد بن الأصم قال: ماتثاءب النبى صلى الله عليه وسلم قط.
** شب تخ
(فتح الباري ٦١٣/١٠)
** مرسل','6','144',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2420,'<span data-type="title" id=toc-2425>٢٤٢٠ </span>- من طريق مسلمة بن عبد الملك بن مروان قال: ما تثاءب نبى قط.
** الخطابى
(فتح الباري ٦١٣/١٠)
** قال الحافظ فى " الفتح " ١٠ / ٦١٣: مسلمة أدرك بعض الصحابة وهو صدوق.','6','145',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2421,'<span data-type="title" id=toc-2426>٢٤٢١ </span>- عن ربعى بن حراش: حدثنى رجل أنه استأذن على النبى صلى الله عليه وسلم وهو فى بيته فقال: أألج؟ ، فقال لخادمه: " أخرج لهذا فعلمه "، فقال قل: السلام عليكم أأدخل.
** د شب
(فتح الباري ٣/١١)
** إسناده جيد','6','146',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2422,'<span data-type="title" id=toc-2427>٢٤٢٢ </span>- عن عائشة مرفوعا: " ما حسدتكم اليهود على شىء ماحسدوكم على السلام والتأمين ".
** خد جه
(فتح الباري ٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٣: صححه ابن خزيمة.','6','147',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2423,'<span data-type="title" id=toc-2428>٢٤٢٣ </span>- حديث عمران بن حصين: كنا نقول فى الجاهلية: نعم بك عينا، وأنعم صباحا فلما جاء الإسلام نهينا عن ذلك.
** د
(فتح الباري ٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٤: رجاله ثقات لكنه منقطع.','6','148',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2424,'<span data-type="title" id=toc-2429>٢٤٢٤ </span>- عن أبى جرى الهجيمى قال: أتيت رسول الله صلى الله عليه وسلم فقلت: عليك
السلام يارسول الله، قال: " لا تقل عليك السلام فإن عليك السلام تحية الموتى ".
** د ت حم ن
(فتح الباري ٥/١١)
** قال الحافظ فى " الفتح " ١١ / ٥: قال النووى: رويناه فى سنن أبى داود والترمذى وصححه وغيرهما بالأسانيد الصحيحة.أ. هـ. قلت: قوله بالأسانيد الصحيحة يوهم أن له طرقا إلى الصحابى المذكور وليس كذلك فإنه لم يروه عن النبى صلى الله عليه وسلم غير أبى جرى ومع ذلك فمداره على أبى تميمة الهجيمى راويه عن أبى جرى، وقد أخرجه أحمد أيضا والنسائى وصححه الحاكم.','6','149',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2425,'<span data-type="title" id=toc-2430>٢٤٢٥ </span>- من طريق زهرة بن معبد قال: قال عمر: أنتهى السلام إلى وبركاته.
** هب
(فتح الباري ٦/١١)
** رجاله ثقات','6','150',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2426,'<span data-type="title" id=toc-2431>٢٤٢٦ </span>- عن عمران بن حصين قال: جاء رجل إلى النبى صلى الله عليه وسلم فقال: السلام عليكم فرد عليه وقال: " عشر "، ثم جاء آخر فقال: السلام عليكم ورحمة الله، فرد عليه وقال: " عشرون "، ثم جاء آخر فزاد وبركاته، فرد وقال: " ثلاثون ".
** د ت ن
(فتح الباري ٦/١١)
** إسناده قوى','6','151',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2427,'<span data-type="title" id=toc-2432>٢٤٢٧ </span>- حديث سهل بن حنيف رفعه: " من قال السلام عليكم كتب له عشر حسنات، ومن زاد ورحمة الله كتب له عشرون حسنة، ومن زاد وبركاته كتبت له ثلاثون حسنة "
** طب
(فتح الباري ٦/١١)
** إسناده ضعيف','6','152',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2428,'<span data-type="title" id=toc-2433>٢٤٢٨ </span>- حديث أنس قال: كان الرجل يمر فيقول: السلام عليك يارسول الله فيقول له: " وعليك السلام ورحمة الله وبركاته ومغفرته ورضوانه ".
** سنى
(فتح الباري ٦/١١)
** إسناده واه','6','153',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2429,'<span data-type="title" id=toc-2434>٢٤٢٩ </span>- حديث زيد بن أرقم: كنا إذا سلم علينا النبى صلى الله عليه وسلم قلنا: وعليك السلام ورحمة الله وبركاته ومغفرته.
** هب
(فتح الباري ٦/١١)
** إسناده ضعيف','6','154',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2430,'<span data-type="title" id=toc-2435>٢٤٣٠ </span>- حديث على رفعه: " يجزىء عن الجماعة إذا مروا أن يسلم أحدهم، ويجزىء عن الجلوس أن يرد أحدهم ".
** د بز
(فتح الباري ٧/١١)
** إسناده ضعيف','6','155',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2431,'<span data-type="title" id=toc-2436>٢٤٣١ </span>- حديث أبى أيوب قال: قلت: يارسول الله هذا السلام فما الإستئناس؟ ، قال: " يتكلم الرجل بتسبيحة أو تكبيرة أو يتنحنح فيؤذن أهل البيت ".
** ابن أبى حاتم
(فتح الباري ٣/١١)
** إسناده ضعيف','6','156',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2432,'<span data-type="title" id=toc-2437>٢٤٣٢ </span>- أن ابن عباس كان يقرأ حتى تستأنسوا ويقول: أخطأ الكاتب، وكان يقرأ على قراءة أبى بن كعب.
** ص الطبرى هب
(فتح الباري ٨/١١)
** إسناده صحيح','6','157',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2433,'<span data-type="title" id=toc-2438>٢٤٣٣ </span>- من مرسل سعيد بن المسيب: وكان رجل من الأنصار نذر أن رأى ابن أبى سرح أن
يقتله.... فذكر بقية الحديث.
** سعد
(فتح الباري ٩/١١)
** مرسل','6','158',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2434,'<span data-type="title" id=toc-2439>٢٤٣٤ </span>- حديث أنس رفعه: " والله يحب إغاثة اللهفان ".
** المرهبى فى العلم
(فتح الباري ١٢/١١)
** إسناده ضعيف جدا','6','159',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2435,'<span data-type="title" id=toc-2440>٢٤٣٥ </span>- حديث أبى ذر مرفوعا: " وإرشادك الرجل فى أرض الضلال صدقة ".
** ت
(فتح الباري ١٢/١١)
** قال الحافظ فى " الفتح " ١١ / ١٢: صححه ابن حبان.','6','160',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2436,'<span data-type="title" id=toc-2441>٢٤٣٦ </span>- حديث البراء رفعه: " من منح منيحة أو هدى زقاقا كان له عدل عتق نسمة ".
** خد ت
(فتح الباري ١٢/١١)
** قال الحافظ فى " الفتح " ١١ / ١٢: صححه الترمذى.','6','161',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2437,'<span data-type="title" id=toc-2442>٢٤٣٧ </span>- حديث المهاجر بن قنفذ أنه سلم على النبى صلى الله عليه وسلم فلم يرد عليه حتى توضأ وقال: " إنى كرهت أن أذكر الله إلا على طهر ".
** د ن
(فتح الباري ١٣/١١)
** قال الحافظ فى " الفتح " ١١ / ١٣: صححه ابن خزيمة وغيره.','6','162',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2438,'<span data-type="title" id=toc-2443>٢٤٣٨ </span>- من طريق عمرو بن شعيب عن أبيه عن جده رفعه: " لا تشبهوا باليهود والنصارى فإن تسليم اليهود الإشارة بالإصبع وتسلم النصارى بالأكف ".
** ت
(فتح الباري ١٤/١١)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ١١ / ١٤: قال الترمذى: غريب، قلت: وفى سنده ضعف.','6','163',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2439,'<span data-type="title" id=toc-2444>٢٤٣٩ </span>- عن جابر رفعه: " لا تسلموا تسليم اليهود فإن تسليمهم بالرؤس والأكف والإشارة ".
** ن
(فتح الباري ١٤/١١)
** إسناده جيد','6','164',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2440,'<span data-type="title" id=toc-2445>٢٤٤٠ </span>- حديث فضالة بن عبيد: يسلم الفارس على الماشى والماشى على القائم.
** خد ت ن حب
(فتح الباري ١٦/١١)
** قال الحافظ فى " الفتح " ١١ / ١٦: صححه الترمذى.','6','165',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2441,'<span data-type="title" id=toc-2446>٢٤٤١ </span>- حديث جابر قال: الماشيان إذا اجتمعا فأيهما بدأ بالسلام فهو أفضل.
** خد
(فتح الباري ١٦/١١)
** إسناده صحيح','6','166',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2442,'<span data-type="title" id=toc-2447>٢٤٤٢ </span>- عن الأغر المزنى، قال لى أبو بكر: لا يسبقك أحد إلى السلام.
** شب
(فتح الباري ١٦/١١)
** إسناده صحيح','6','167',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2443,'<span data-type="title" id=toc-2448>٢٤٤٣ </span>- عن ابن عمر: إذا سلمت فأسمع فإنها تحية من عند الله.
** خد
(فتح الباري ١٨/١١)
** إسناده صحيح','6','168',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2444,'<span data-type="title" id=toc-2449>٢٤٤٤ </span>- من طريق عبد الرحمن بن عوسجة عنه رفعه: " أفشوا السلام تسلموا ".
** خد
(فتح الباري ١٨/١١)
** قال الحافظ فى " الفتح " ١١ / ١٨: صححه ابن حبان.','6','169',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2445,'<span data-type="title" id=toc-2450>٢٤٤٥ </span>- عن عبد الله بن سلام رفعه: " أطعموا الطعام وأفشوا السلام ... " الحديث وفيه: " تدخلوا الجنة بسلام ".
** خد
(فتح الباري ١٩/١١)
** قال الحافظ فى " الفتح " ١١ / ١٩: صححه الترمذى والحاكم.','6','170',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2446,'<span data-type="title" id=toc-2451>٢٤٤٦ </span>- حديث عبد الله بن عمرو رفعه: " اعبدوا الرحمن وأفشوا السلام.... " الحديث وفيه: " تدخلوا الجنان ".
** خد ت
(فتح الباري ١٩/١١)
** قال الحافظ فى " الفتح " ١١ / ١٩: صححه ابن حبان.','6','171',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2447,'<span data-type="title" id=toc-2452>٢٤٤٧ </span>- عن جابر رفعه: " لا تسلموا تسليم اليهود فإن تسليمهم بالرؤس والأكف ".
** ن
(فتح الباري ١٩/١١)
** إسناده جيد','6','172',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2448,'<span data-type="title" id=toc-2453>٢٤٤٨ </span>- حديث أبى سعيد: أن رجلا سلم على النبى صلى الله عليه وسلم وهو يصلى فرد عليه إشارة.
** لم
(فتح الباري ١٩/١١)
** جيد
** قال الحافظ فى " الفتح " ١١ / ١٩: وردت أحاديث جيدة أنه صلى الله عليه وسلم رد السلام وهو يصلى إشارة منها حديث أبى سعيد.','6','173',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2449,'<span data-type="title" id=toc-2454>٢٤٤٩ </span>- عن ابن عمر: فيستحب إذا لم يكن أحد فى البيت أن يقول: السلام علينا وعلى عباد الله الصالحين.
** خد شب
(فتح الباري ٢٠/١١)
** إسناده حسن','6','174',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2450,'<span data-type="title" id=toc-2455>٢٤٥٠ </span>- عن ابن مسعود أنه مر برجل فقال: السلام عليك ياأبا عبد الرحمن، فرد عليه ثم قال: سيأتى على الناس زمان يكون السلام فيه للمعرفة.
** خد
(فتح الباري ٢١/١١)
** إسناده صحيح','6','175',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2451,'<span data-type="title" id=toc-2456>٢٤٥١ </span>- حديث ثوبان رفعه: " لا يحل لامرىء مسلم أن ينظر إلى جوف بيت حتى يستأذن فإن فعل فقد دخل ".
** خد د ت
(فتح الباري ٢٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٤: حسنه الترمذى.','6','176',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2452,'<span data-type="title" id=toc-2457>٢٤٥٢ </span>- حديث أبى هريرة رفعه: " إذا دخل البصر فلا إذن ".
** خد د
(فتح الباري ٢٤/١١)
** إسناده حسن','6','177',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2453,'<span data-type="title" id=toc-2458>٢٤٥٣ </span>- حديث ابن عباس: كان الناس ليس لبيوتهم ستور فأمرهم الله بالإستئذان ثم جاء الله بالخير فلم أر أحدا يعمل بذلك.
** د
(فتح الباري ٢٥/١١)
** إسناده قوى','6','178',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2454,'<span data-type="title" id=toc-2459>٢٤٥٤ </span>- حديث ابن عباس أنه سئل عن الإستئذان فى العورات الثلاث فقال: إن الله ستير يحب الستر وكان الناس ليس لهم ستور على أبوابهم فربما فاجأ الرجل خادمه أو ولده وهو على أهله فأمروا أن يستأذنوا فى العورات الثلاث ثم بسط الله الرزق فاتخذوا الستور والحجال فرأى الناس أن ذلك قد كفاهم الله به مما أمروا به.
** د ابن أبى حاتم
(فتح الباري ٣١/١١)
** إسناده قوى','6','179',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2455,'<span data-type="title" id=toc-2460>٢٤٥٥ </span>- عن ابن عباس: لم يعمل بها أكثر الناس وإنى لآمر جاريتى أن تستأذن على.
** لم
(فتح الباري ٣١/١١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١١ / ٣١: من وجه صحيح عن ابن عباس.','6','180',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2456,'<span data-type="title" id=toc-2461>٢٤٥٦ </span>- عن يحيى بن أبى كثير: بلغنى أنه يكره أن يسلم الرجال على النساء.
** عب
(فتح الباري ٣٣/١١)
** مقطوع أو معضل','6','181',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2457,'<span data-type="title" id=toc-2462>٢٤٥٧ </span>- حديث أسماء بنت يزيد: مر علينا النبى صلى الله عليه وسلم فى نسوة فسلم علينا
** عب
(فتح الباري ٣٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٢: حسنه الترمذى وليس على شرط البخارى.','6','182',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2458,'<span data-type="title" id=toc-2463>٢٤٥٨ </span>- حديث واثلة مرفوعا: " يسلم الرجال على النساء ولا يسلم النساء على الرجال ".
** أبو نعيم فى عمل يوم وليلة
(فتح الباري ٣٤/١١)
** إسناده واه','6','183',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2459,'<span data-type="title" id=toc-2464>٢٤٥٩ </span>- حديث بريدة أن النبى صلى الله عليه وسلم أتى المسجد وأبو موسى يقرأ، قال: فجئت فقال: " من هذا؟ "، قلت: أنا بريدة.
** خد
(فتح الباري ٣٥/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٥: صححه الحاكم.','6','184',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2460,'<span data-type="title" id=toc-2465>٢٤٦٠ </span>- من طريق معاوية بن قرة قال: قال لى أبى قرة بن إياس المزنى الصحابى: إذا مر بك الرجل فقال: السلام عليكم فلا تقل وعليك السلام فتخصه وحده فإنه ليس وحده.
** خد
(فتح الباري ٣٧/١١)
** إسناده صحيح','6','185',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2461,'<span data-type="title" id=toc-2466>٢٤٦١ </span>- عن ابن عباس أنه كان إذا سلم عليه يقول: وعليك ورحمة الله.
** خد
(فتح الباري ٣٧/١١)
** إسناده صحيح','6','186',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2462,'<span data-type="title" id=toc-2467>٢٤٦٢ </span>- عن ابن عمر: لا تسلموا على فى شرب الخمر ولا تعودوهم إذا مرضوا ولا تصلوا عليهم إذا ماتوا.
** ص
(فتح الباري ٤١/١١)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ١١ / ٤١: أخرجه ابن عدى بسند أضعف منه عن ابن عمر مرفوعا.','6','187',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2463,'<span data-type="title" id=toc-2468>٢٤٦٣ </span>- أنه كان لا يمر بمسلم ولا نصرانى ولا صغير ولا كبير إلا سلم عليه فقيل له، فقال: إنا أمرنا بإفشاء السلام.
** الطبرى
(فتح الباري ٤١/١١)
** إسناده جيد','6','188',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2464,'<span data-type="title" id=toc-2469>٢٤٦٤ </span>- عن علقمة قال: كنت ردفا لابن مسعود فصحبنا دهقان فلما انشعبت له الطريق أخذ فيها فأتبعه عبد الله بصره فقال: السلام عليكم فقلت: ألست تكره أن يبدؤا بالسلام؟ ، قال: نعم ولكن حق الصحبة.
** الطبرى
(فتح الباري ٤١/١١)
** إسناده صحيح','6','189',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2465,'<span data-type="title" id=toc-2470>٢٤٦٥ </span>- عن زيد بن أرقم قال: بينما أنا عند النبى صلى الله عليه وسلم إذ أقبل رجل من اليهود يقال له: ثعلبة بن الحارث، فقال: السام عليك يامحمد، فقال: " وعليكم ".
** طب
(فتح الباري ٤٢/١١)
** إسناده ضعيف','6','190',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2466,'<span data-type="title" id=toc-2471>٢٤٦٦ </span>- عن أنس: أمرنا أن لا نزيد على أهل الكتاب على: وعليكم.
** حم
(فتح الباري ٤٥/١١)
** إسناده جيد','6','191',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2467,'<span data-type="title" id=toc-2472>٢٤٦٧ </span>- حديث ابن عباس: من نظر فى كتاب أخيه بغير إذنه فكأنما ينظر فى النار.
** د
(فتح الباري ٤٧/١١)
** إسناده ضعيف','6','192',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2468,'<span data-type="title" id=toc-2473>٢٤٦٨ </span>- عن نافع: كانت لابن عمر حاجة إلى معاوية فأراد أن يبدأ بنفسه فلم يزالوا به حتى كتب بسم الله الرحمن الرحيم إلى معاوية.
** خد
(فتح الباري ٤٨/١١)
** إسناده صحيح','6','193',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2469,'<span data-type="title" id=toc-2474>٢٤٦٩ </span>- حديث أبى أمامة قال: خرج علينا النبى صلى الله عليه وسلم متوكئا على عصا فقمنا له فقال: " لا تقوموا كما تقوم الأعاجم بعضهم لبعض ".
** لم
(فتح الباري ٤٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٠: أجاب الطبرى بأنه حديث ضعيف مضطرب السند فيه من لا يعرف.','6','194',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2470,'<span data-type="title" id=toc-2475>٢٤٧٠ </span>- عن عائشة: كان رسول الله صلى الله عليه وسلم إذا رأى فاطمة بنته قد أقبلت رحب بها ثم قام فقبلها ثم أخذ بيدها حتى يجلسها مكانه.
** د ت
(فتح الباري ٥٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٠: حسنه الترمذى وصححه ابن حبان والحاكم.','6','195',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2471,'<span data-type="title" id=toc-2476>٢٤٧١ </span>- من طريق أبى مجلز قال: خرج معاوية على ابن الزبير وابن عامر، فقام ابن عامر وجلس ابن الزبير، فقال معاوية لابن عامر: اجلس فإنى سمعت رسول الله صلى الله عليه وسلم يقول: " من أحب أن يتمثل له الرجال قياما فليتبوأ مقعده من النار ".
** د ت
(فتح الباري ٥٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٠: حسنه الترمذى واللفظ لأبى داود.','6','196',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2472,'<span data-type="title" id=toc-2477>٢٤٧٢ </span>- قال أبو سعيد: فلما طلع قال النبى صلى الله عليه وسلم س: " قوموا إلى سيدكم فانزلوه ".
** حم
(فتح الباري ٥١/١١)
** إسناده حسن','6','197',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2473,'<span data-type="title" id=toc-2478>٢٤٧٣ </span>- عن أنس قال: لم يكن شخص أحب إليهم من رسول الله صلى الله عليه وسلم وكانوا إذا رأوه لم يقوموا لما يعلمون من كراهيته لذلك.
** ت
(فتح الباري ٥٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٣: قال الترمذى: حسن صحيح غريب.؟','6','198',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2474,'<span data-type="title" id=toc-2479>٢٤٧٤ </span>- حديث أبى أمامة رفعه: " تمام تحيتكم بينكم المصافحة ".
** ت
(فتح الباري ٥٤/١١)
** إسناده ضعيف','6','199',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2475,'<span data-type="title" id=toc-2480>٢٤٧٥ </span>- عن أنس رفعه: " قد أقبل أهل اليمن وهم أول من حيانا بالمصافحة ".
** د
(فتح الباري ٥٤/١١)
** إسناده صحيح','6','200',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2476,'<span data-type="title" id=toc-2481>٢٤٧٦ </span>- عن أنس: قيل: يارسول الله الرجل يلقى أخاه أينتحنى له، قال: " لا " قال: فيأخذ بيده ويصافحه؟ ، قال: " نعم ".
** ت
(فتح الباري ٥٥/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٥: قال الترمذى: حسن.','6','201',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2477,'<span data-type="title" id=toc-2482>٢٤٧٧ </span>- مرسل عطاء الخراسانى: " تصافحوا يذهب الغل ".
** ما
(فتح الباري ٥٥/١١)
** مرسل','6','202',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2478,'<span data-type="title" id=toc-2483>٢٤٧٨ </span>- حديث ابن مسعود: من تمام التحية الأخذ باليد.
** ت
(فتح الباري ٥٦/١١)
** إسناده ضعيف','6','203',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2479,'<span data-type="title" id=toc-2484>٢٤٧٩ </span>- حديث صفوان بن عسال: أن يهوديين أتيا النبى صلى الله عليه وسلم فسألاه عن تسع آيات.... الحديث وفى آخره: فقبلا يده ورجله.
** ت ن جه
(فتح الباري ٥٧/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٧: قال الترمذى: حسن صحيح.أ. هـ. وصححه الحاكم.','6','204',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2480,'<span data-type="title" id=toc-2485>٢٤٨٠ </span>- حديث الزارع العبدى وكان فى وفد عبد القيس قال: فجعلنا نتبادر من رواحلنا فنقبل يد النبى صلى الله عليه وسلم ورجله.
** د
(فتح الباري ٥٧/١١)
** جيد
** قال الحافظ فى " الفتح " ١١ / ٥٧: جمع الحافظ أبو بكر المقرى جزءا فى تقبيل اليد سمعناه، وأورد فيه أحاديث كثيرة وآثارا فمن جيدها حديث الزارع العبدى.','6','205',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2481,'<span data-type="title" id=toc-2486>٢٤٨١ </span>- حديث أسامة بن شريك قال: قمنا إلى النبى صلى الله عليه وسلم فقبلنا يده.
** لم
(فتح الباري ٥٧/١١)
** إسناده قوى','6','206',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2482,'<span data-type="title" id=toc-2487>٢٤٨٢ </span>- من طريق رجل من عنزة لم يسم قال: قلت لأبى ذر هل كان رسول الله صلى الله عليه وسلم يصافحكم إذا لقيتموه قال: مالقيته قط إلا صافحنى، وبعث إلى ذات يوم فلم أكن فى أهلى، فلما جئت أخبرت أنه أرسل إلى فأتيته وهو على سريره فالتزمنى فكانت أجود وأجور.
** حم د
(فتح الباري ٥٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٩: رجاله ثقات إلا هذا الرجل المبهم.','6','207',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2483,'<span data-type="title" id=toc-2488>٢٤٨٣ </span>- عن ابن عباس قال: لما قدم جعفر فى الحبشة اعتنقه النبى صلى الله عليه وسلم.
** لم
(فتح الباري ٥٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٩: قال الذهبى فى الميزان: هذه الحكاية باطلة وإسنادها مظلم أ. هـ. قلت: والمحفوظ عن ابن عيينة بغير هذا الإسناد.','6','208',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2484,'<span data-type="title" id=toc-2489>٢٤٨٤ </span>- حديث عائشة: لما قدم جعفر استقبله رسول الله صلى الله عليه وسلم فقبل مابين عينيه.
** البغوى فى معجم الصحابة
(فتح الباري ٦٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٦٠: سنده موصول لكن فى سنده محمد بن عبد الله بن عبيد بن عمير وهو ضعيف.','6','209',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2485,'<span data-type="title" id=toc-2490>٢٤٨٥ </span>- عن عائشة قالت: قدم زيد بن حارثة المدينة ورسول الله صلى الله عليه وسلم فى بيتى فقرع الباب فقام إليه النبى صلى الله عليه وسلم عريانا يجر ثوبه فاعتنقه وقبله.
** ت
(فتح الباري ٦٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٦٠: قال الترمذى: حديث حسن.','6','210',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2486,'<span data-type="title" id=toc-2491>٢٤٨٦ </span>- عن أبى الهيثم بن التيهان أن النبى صلى الله عليه وسلم لقيه فاعتنقه وقبله.
** قاسم بن ضبع
(فتح الباري ٦٠/١١)
** إسناده ضعيف','6','211',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2487,'<span data-type="title" id=toc-2492>٢٤٨٧ </span>- حديث محمد بن حاطب قال: انطلقت بى أمى إلى رجل جالس فقالت له: يارسول الله قال: " لبيك وسعديك ".
** ن
(فتح الباري ٦١/١١)
** قال الحافظ فى " الفتح " ١١ / ٦١: صححه ابن حبان.','6','212',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2488,'<span data-type="title" id=toc-2493>٢٤٨٨ </span>- من طريق سعيد بن أبى الحسن: جاءنا أبو بكرة فقام له رجل من مجلسه فأبى أن يجلس فيه وقال: إن النبى صلى الله عليه وسلم نهى عن هذا.
** د
(فتح الباري ٦٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٦٣: أخرجه الحاكم وصححه من هذا الوجه لكن لفظه مثل لفظ ابن عمر الذى فى الصحيح، وقد قال البزار إنه لا يعرف له طريق إلا هذه، وفى سنده أبو عبد الله مولى
أبى بردة بن أبى موسى وقيل مولى قريس وهو بصرى لا يعرف.','6','213',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2489,'<span data-type="title" id=toc-2494>٢٤٨٩ </span>- حديث قيلة قالت: فجاء رجل فقال: السلام عليك يارسول الله فقال: " وعليك السلام ورحمة الله "، وعليه أسمال مليتين قد كانتا بزعفران فنفضتا وبيده عسيب نخلة مقشرا قاعدا القرفصاء، قالت: فلما رأيت رسول الله صلى الله عليه
وسلم المتخشع فى الجلسة أرعدت من الفرق قال له جليسه: يارسول الله أرعدت المسكينة فقال ولم ينظر إلى: " يامسكينة عليك السكينة "، فذهب عنى ما أجد من الرعب.... الحديث.
** د تم طب
(فتح الباري ٦٥/١١)
** إسناده لا بأس به','6','214',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2490,'<span data-type="title" id=toc-2495>٢٤٩٠ </span>- عن جابر بن سمرة: رأيت النبى صلى الله عليه وسلم متكئا على وسادة.
** دى ت
(فتح الباري ٦٦/١١)
** قال الحافظ فى " الفتح " ١١ / ٦٦: صححه الترمذى وأبو عوانة وابن حبان.','6','215',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2491,'<span data-type="title" id=toc-2496>٢٤٩١ </span>- من طريق الحسن البصرى قال: كان عمار يقول: قاتلت مع رسول الله صلى الله عليه
وسلم الجن والإنس، أرسلنى إلى بئر بدر فلقيت الشيطان فى صورة إنسى فصارعنى فصرعته.... الحديث.
** طب
(فتح الباري ٦٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٦٩: فى سنده الحكم بن عطية مختلف فيه والحسن لم يسمع من عمار.','6','216',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2492,'<span data-type="title" id=toc-2497>٢٤٩٢ </span>- حديث ابن عباس: استعينوا على صيام النهار بالسحور وعلى قيام الليل
بالقيلولة.
** جه خز
(فتح الباري ٧٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٧٠: فى سنده زمعة بن صالح وفيه ضعف.','6','217',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2493,'<span data-type="title" id=toc-2498>٢٤٩٣ </span>- حديث أنس رفعه: " قيلوا فإن الشياطين لا تقيل ".
** طس
(فتح الباري ٧٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٧٠: فى سنده كثير بن مروان وهو متروك.','6','218',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2494,'<span data-type="title" id=toc-2499>٢٤٩٤ </span>- حديث خوات بن جبير رضى الله عنه موقوفا قال: " نوم أول النهار حرق، وأوسطه خلق وآخره حمق ".
** سفيان بن عيينة فى جامعه
(فتح الباري ٧٠/١١)
** إسناده صحيح','6','219',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2495,'<span data-type="title" id=toc-2500>٢٤٩٥ </span>- عن عطاء بن يسار أن امرأة حدثته قالت: نام رسول الله صلى الله عليه وسلم ثم استيقظ وهو يضحك، فقلت: تضحك منى يارسول الله؟ ، قال: " لا ولكن من قوم من أمتى يخرجون غزاة فى البحر مثلهم كمثل الملوك على الأسرة "، ثم نام ثم استيقظ فقال: " مثل ذلك سواء "، لكن قال: " فيرجعون قليلة غنائمهم مغفورا لهم "، قالت: فادع الله أن يجعلنى منهم، فدعا لها، قال عطاء: فرأيتها فى غزاة غزاها المنذر بن الزبير إلى أرض الروم فماتت بأرض الروم.
** عب
(فتح الباري ٧٦/١١)
** إسناده على شرط الصحيح','6','220',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2496,'<span data-type="title" id=toc-2501>٢٤٩٦ </span>- عن عاصم الأحول قال: لما نزلت كان لا يناجى النبى صلى الله عليه وسلم أحد إلا تصدقا فكان أول من ناجاه على بن أبى طالب فتصدق بدينار، ونزلت الرخصة (فإذا لم تفعلوا وتاب الله عليكم) الآية.
** سفيان بن عيينة فى جامعه
(فتح الباري ٨١/١١)
** رجاله ثقات مرسلا','6','221',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2497,'<span data-type="title" id=toc-2502>٢٤٩٧ </span>- من طريق على بن علقمة عنه قال: لما نزلت هذه الآية قال لى رسول الله صلى الله
عليه وسلم: " ماتقول دينار "، قلت: لا يطيقونه، قال: " فى نصف دينار "، قلت: لا يطيقونه، قال: " فكم؟ "، قلت: شعيرة، قال: " إنك لزهيد "، قال: فنزلت أأشفقتم ... الآية، قال على: فبى خفف عن هذه الأمة.
** ت حب ابن مردويه
(فتح الباري ٨١/١١)
** قال الحافظ فى " الفتح " ١١ / ٨١: صححه ابن حبان.','6','222',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2498,'<span data-type="title" id=toc-2503>٢٤٩٨ </span>- حديث أنس: أحفظ سرى تكن مؤمنا.
** يع الخرائطى
(فتح الباري ٨٢/١١)
** قال الحافظ فى " الفتح " ١١ / ٨٢: فيه على بن زيد وهو صدوق كثير الأوهام، وقد أخرج أصله الترمذى وحسنه.','6','223',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2499,'<span data-type="title" id=toc-2504>٢٤٩٩ </span>- إنما يتجالس المتجالسان بالأمانة فلا يحل لأحد أن يفشى على صاحبه مايكره.
** عب
(فتح الباري ٨٢/١١)
** مرسل','6','224',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2500,'<span data-type="title" id=toc-2505><span data-type="title" id=toc-2506>٢٥٠٠ </span></span>- حديث على مرفوعا: " المجالس بالأمانة ".
** القضاعى فى مسند الشهاب
(فتح الباري ٨٢/١١)
** إسناده ضعيف','6','225',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2501,'<span data-type="title" id=toc-2507>٢٥٠١ </span>- عن عبد الله بن عمرو: أن النبى صلى الله عليه وسلم قال: " ولا يحل لثلاثة نفر يكونون بأرض فلاة أن يتناجى اثنان دون صاحبهما.... " الحديث.
** حم
(فتح الباري ٨٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٨٤: فى سنده ابن لهيعة.','6','226',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2502,'<span data-type="title" id=toc-2508>٢٥٠٢ </span>- عن ابن عباس قال: جاءت فأرة فجرت الفتيلة فألقتها بين يدى رسول الله صلى
الله عليه وسلم على الخمرة التى كان قاعدا عليها فأحرقت منها مثل موضع الدرهم فقال النبى صلى الله عليه وسلم: " إذا نمتم فأطفئوا سراجكم فإن الشيطان يدل مثل هذه على هذا فيحرقكم ".
** د
(فتح الباري ٨٦/١١)
** قال الحافظ فى " الفتح " ١١ / ٨٦: صححه ابن حبان والحاكم.','6','227',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2503,'<span data-type="title" id=toc-2509>٢٥٠٣ </span>- عن ابن عباس: أتيت النبى صلى الله عليه وسلم بمنى وأنا قد ناهزت الإحتلام.
** لم
(فتح الباري ٩٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٩٠: قال الإسماعيلى: والأحاديث عن ابن عباس فى هذا مضطربة أ. هـ. قلت: فى كلامه نظر.','6','228',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2504,'<span data-type="title" id=toc-2510>٢٥٠٤ </span>- عن ابن عباس أنه قال: ولدت وبنو هاشم فى الشعب.
** ابن عبد البر
(فتح الباري ٩٠/١١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١١ / ٩٠: صححه ابن عبد البر.','6','229',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2505,'<span data-type="title" id=toc-2511>٢٥٠٥ </span>- حديث عقبة بن عامر رفعه: " كل مايلهو به المرء المسلم باطل إلا رميه بقوسه وتأديبه فرسه وملاعبته أهله.... " الحديث.
** حم د ت ن جه
(فتح الباري ٩١/١١)
** قال الحافظ فى " الفتح " ١١ / ٩١: أخرجه أحمد والأربعة وصححه ابن خزيمة والحاكم.','6','230',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2506,'<span data-type="title" id=toc-2512>٢٥٠٦ </span>- حديث أبى أمامة رفعه: " لا يحل بيع المغنيات ولا شراؤهن.... " الحديث وفيه
" وفيهن أنزل الله (ومن الناس من يشترى لهو الحديث) " الآية.
** ت
(فتح الباري ٩١/١١)
** إسناده ضعيف','6','231',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2507,'<span data-type="title" id=toc-2513>٢٥٠٧ </span>- حديث أبى هريرة قال: كنا حديثى عهد بجاهلية فحلفت باللات والعزى فذكرت ذلك لرسول الله صلى الله عليه وسلم فقال: " قل لا إله إلا الله وحده لا شريك له له الملك وله الحمد وهو على كل شىء قدير وانفث عن شمالك وتعوذ بالله ثم لا تعد ".
** ن
(فتح الباري ٩٢/١١)
** إسناده قوى','6','232',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2508,'<span data-type="title" id=toc-2514>٢٥٠٨ </span>- من رواية عمارة بن عامر: إذا رفع الرجل بناء فوق سبعة أذرع نودى يافاسق إلى أين.
** ابن أبى الدنيا
(فتح الباري ٩٢/١١)
** إسناده ضعيف','6','233',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2509,'<span data-type="title" id=toc-2515>٢٥٠٩ </span>- حديث خباب رفعه قال: " يؤجر الرجل فى نفقته كلها إلا التراب " أو قال: " البناء ".
** ت
(فتح الباري ٩٢/١١)
** قال الحافظ فى " الفتح " ١١ / ٩٢: صححه الترمذى.','6','234',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2510,'<span data-type="title" id=toc-2516>٢٥١٠ </span>- حديث عبد الله بن عمرو بن العاص قال: مر بى النبى صلى الله عليه وسلم وأنا أطين حائطا فقال: " الأمر أعجل من ذلك ".
** د
(فتح الباري ٩٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٩٣: صححه الترمذى وابن حبان.','6','235',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2511,'<span data-type="title" id=toc-2517>٢٥١١ </span>- حديث أنس رفعه: " أما أن كل بناء وبال على صاحبه إلا مالا إلا مالا ".
** د
(فتح الباري ٩٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٩٣: رواته موثقون إلا الراوى عن أنس وهو أبو طلحة الأسدى فليس بمعروف.','6','236',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2512,'<span data-type="title" id=toc-2518>٢٥١٢ </span>- حديث النعمان بن بشير عن النبى صلى الله عليه وسلم قال: " الدعاء هو العبادة ثم قرأ (وقال ربكم ادعونى أستجب لكم إن الذين يستكبرون عن عبادتى) " الآية
** د ت ن جه
(فتح الباري ٩٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٩٤: أخرجه الأربعة وصححه الترمذى والحاكم.','6','237',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2513,'<span data-type="title" id=toc-2519>٢٥١٣ </span>- حديث أبى هريرة رفعه: " ليس شىء أكرم على الله من الدعاء ".
** ت جه
(فتح الباري ٩٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٩٤: صححه ابن حبان والحاكم.','6','238',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2514,'<span data-type="title" id=toc-2520>٢٥١٤ </span>- حديث رفعه: " من لم يسأل الله يغضب عليه ".
** حم خد ت جه بز ك
(فتح الباري ٩٥/١١)
** قال الحافظ فى " الفتح " ١١ / ٩٥: من رواية أبى صالح الخوزى وهذا الخوزى مختلف فيه ضعفه ابن معين وقواه أبو زرعة وظن الحافظ ابن كثير أنه أبو صالح السمان فجزم بإن أحمد تفرد بتخريجه وليس كما قال فقد جزم شيخه المزى فى الأطراف بما قلته.','6','239',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2515,'<span data-type="title" id=toc-2521>٢٥١٥ </span>- حديث ابن عمر رفعه: " إن الدعاء ينفع مما نزل ومما لم ينزل فعليكم عباد الله بالدعاء ".
** ت
(فتح الباري ٩٥/١١)
** إسناده لين
** قال الحافظ فى " الفتح " ١١ / ٩٥: فى سنده لين وقد صححه مع ذلك الحاكم.','6','240',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2516,'<span data-type="title" id=toc-2522>٢٥١٦ </span>- عن عائشة مرفوعا: " إن الله يحب الملحين فى الدعاء ".
** طب فى الدعاء
(فتح الباري ٩٥/١١)
** قال الحافظ فى " الفتح " ١١ / ٩٥: بسند رجاله ثقات إلا أن فيه عنعنة بقية عن عائشة مرفوعا.','6','241',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2517,'<span data-type="title" id=toc-2523>٢٥١٧ </span>- حديث عبادة بن الصامت رفعه: " ماعلى الأرض مسلم يدعو بدعوة إلا آتاه الله إياها أو صرف عنه من السوء مثلها ".
** ت ك
(فتح الباري ٩٦/١١)
** صحيح','6','242',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2518,'<span data-type="title" id=toc-2524>٢٥١٨ </span>- حديث أبى سعيد رفعه: " مامن مسلم يدعو بدعوة ليس فيها إثم ولا قطيعة رحم إلا أعطاه الله بها إحدى ثلاث: أما أن يعجل له دعوته، وإما أن يدخرها له فى الآخرة، وإما أن يصرف عنه من السوء مثلها ".
** حم
(فتح الباري ٩٦/١١)
** قال الحافظ فى " الفتح " ١١ / ٩٦: صححه الحاكم.','6','243',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2519,'<span data-type="title" id=toc-2525>٢٥١٩ </span>- حديث على بن أبى طالب قال: حدثنى أبو بكر الصديق رضى الله عنهما وصدق أبو بكر: سمعت النبى صلى الله عليه وسلم يقول: " مامن رجل يذنب ذنبا ثم يقول فيتطهر فيحسن الطهور ثم يستغفر الله عز وجل إلا غفر له " ثم تلا (والذين إذا فعلوا فاحشة) الآية.
** حم د ت ن جه
(فتح الباري ٩٨/١١)
** قال الحافظ فى " الفتح " ١١ / ٩٨: أخرجه أحمد والأربعة وصححه ابن حبان.','6','244',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2520,'<span data-type="title" id=toc-2526>٢٥٢٠ </span>- عن ابن عمر أنه سمع النبى صلى الله عليه وسلم يقول: " استغفر الله الذى لا
إله إلا هو الحى القيوم وأتوب إليه " فى المجلس قبل أن يقوم مائة مرة.
** ن
(فتح الباري ١٠١/١١)
** إسناده جيد','6','245',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2521,'<span data-type="title" id=toc-2527>٢٥٢١ </span>- عن أبى بن كعب أنه سأل النبى صلى الله عليه وسلم فقال: " أن يذنب الذنب فيستغفر ثم لا يعود إليه ".
** ابن أبى حاتم
(فتح الباري ١٠٤/١١)
** إسناده ضعيف جدا','6','246',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2522,'<span data-type="title" id=toc-2528>٢٥٢٢ </span>- عن سعد بن عبيدة: إذا أويت إلى فراشك وأنت طاهر فتوسد يمينك.
** د ن
(فتح الباري ١١٠/١١)
** إسناده جيد','6','247',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2523,'<span data-type="title" id=toc-2529>٢٥٢٣ </span>- من طريق مجاهد قال: قال لى ابن عباس: لا تبيتن إلا على وضوء فإن الأرواح تبغث ماقبضت عليه.
** عب
(فتح الباري ١١٠/١١)
** قال الحافظ فى " الفتح " ١١ / ١١٠: رجاله ثقات إلا أبا يحيى القتات وهو صدوق فيه كلام.','6','248',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2524,'<span data-type="title" id=toc-2530>٢٥٢٤ </span>- حديث رافع بن خديج أن النبى صلى الله عليه وسلم قال: " إذا أضطجع أحدكم على يمينه " ثم قال:.... فذكر الحديث وفى أخره: " أؤمن بكتابك الذى أنزلت ويرسلك الذى أرسلت ".
** ت
(فتح الباري ١١٣/١١)
** قال الحافظ فى " الفتح " ١١ / ١١٣: قال الترمذى: حسن غريب.','6','249',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2525,'<span data-type="title" id=toc-2531>٢٥٢٥ </span>- عن أبى إسحق عنه أن النبى صلى الله عليه وسلم كان إذا أوى إلى فراشه وضع يده اليمنى تحت خده الأيمن وقال: " اللهم قنى عذابك يوم تبعث عبادك ".
** ن
(فتح الباري ١١٥/١١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١١ / ١١٥: أخرجه أيضا بسند صحيح عن حفصة وزاد: يقول ذلك ثلاثا.','6','250',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2526,'<span data-type="title" id=toc-2532>٢٥٢٦ </span>- عن على: أشتكت فاطمة مجل يدها.
** عم
(فتح الباري ١١٩/١١)
** قال الحافظ فى " الفتح " ١١ / ١١٩: صححه ابن حبان.','6','251',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2527,'<span data-type="title" id=toc-2533>٢٥٢٧ </span>- مرسل على بن الحسين: أن فاطمة أتت النبى صلى الله عليه وسلم تسأله خادما وبيدها الطحن من قطب الرحى، فقال: " إذا أويت إلى فراشك ... " الحديث.
** جعفر فى الذكر
(فتح الباري ١٢١/١١)
** مرسل','6','252',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2528,'<span data-type="title" id=toc-2534>٢٥٢٨ </span>- عن عبد الله بن عمرو بن العاص: خصلتان لا يحصيهما عبد إلا دخل الجنة.
** د ت ن جه
(فتح الباري ١٢١/١١)
** قال الحافظ فى " الفتح " ١١ / ١٢١: صححه الترمذى وابن حبان.','6','253',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2529,'<span data-type="title" id=toc-2535>٢٥٢٩ </span>- حديث أبى هريرة: كان النبى صلى الله عليه وسلم يأمرنا إذا أخذ أحدنا مضجعه أن يقول: " اللهم رب السماوات ورب الأرض.... " الحديث.
** د
(فتح الباري ١٢٥/١١)
** قال الحافظ فى " الفتح " ١١ / ١٢٥: صححه الحاكم.','6','254',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2530,'<span data-type="title" id=toc-2536>٢٥٣٠ </span>- عن ابن عمر رضى الله عنهما: أن النبى صلى الله عليه وسلم أمر رجلا إذا أخذ مضجعه أن يقول: اللهم أنت خلقت نفسى وأنت تتوفاها لك مماتها ومحياها إن أحييتها فاحفظها وإن أمتها فاغفر لها.
** ن
(فتح الباري ١٢٧/١١)
** قال الحافظ فى " الفتح " ١١ / ١٢٧: صححه ابن حبان.','6','255',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2531,'<span data-type="title" id=toc-2537>٢٥٣١ </span>- حديث أبى الأزهر الأنمارى أن النبى صلى الله عليه وسلم كان يقول إذا أخذ مضجعه من الليل: " بسم الله وضعت جنبى اللهم اغفر لى ذنوبى وأخسىء شيطانى وفك رهانى واجعلنى فى النداء الأعلى ".
** د
(فتح الباري ١٢٧/١١)
** قال الحافظ فى " الفتح " ١١ / ١٢٧: صححه الحاكم والترمذى.','6','256',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2532,'<span data-type="title" id=toc-2538>٢٥٣٢ </span>- حديث أبى سعيد رفعه: " من قال حين يأوى إلى فراشه: أستغفر الله الذى لا إله إلا هو الحى القيوم وأتوب، ثلاث مرات غفرت له ذنوبه وإن كانت مثل زبد البحر وإن كانت عدد أيام الدنيا ".
** لم
(فتح الباري ١٢٧/١١)
** قال الحافظ فى " الفتح " ١١ / ١٢٧: حسنه الترمذى.','6','257',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2533,'<span data-type="title" id=toc-2539>٢٥٣٣ </span>- حديث حفصة أن النبى صلى الله عليه وسلم كان إذا أراد أن يرقد وضع يده اليمنى تحت خده ثم يقول: " اللهم قنى عذابك يوم تبعث عبادك ثلاثا ".
** د ن
(فتح الباري ١٢٧/١١)
** قال الحافظ فى " الفتح " ١١ / ١٢٧ _ ١٢٨: وأخرجه الترمذى من حديث البراء وحسنه ومن حديث حذيفة وصححه.','6','258',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2534,'<span data-type="title" id=toc-2540>٢٥٣٤ </span>- حديث أنس رفعه: " من قال حين يصبح: اللهم إنى أصبحت أشهدك وأشهد حملة عرشك وملائكتك وجميع خلقك أنك أنت الله لا إله إلا أنت وأن محمدا عبدك ورسولك أعتق الله ربعه من النار، ومن قالها مرتين أعتق الله نصفه من النار.... " الحديث.
** د ت ن
(فتح الباري ١٣٠/١١)
** قال الحافظ فى " الفتح " ١١ / ١٣٠: رواه الثلاثة وحسنه الترمذى.','6','259',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2535,'<span data-type="title" id=toc-2541>٢٥٣٥ </span>- حديث أبى سلام عمن خدم رسول الله صلى الله عليه وسلم رفعه: " من قال إذا أصبح وإذا أمسى: رضيت بالله ربا وبالإسلام دينا وبمحمد رسولا إلا كان حقا على الله أن يرضيه ".
** د
(فتح الباري ١٣٠/١١)
** إسناده قوى
** قال الحافظ فى " الفتح " ١١ / ١٣٠: وهو عند الترمذى بنحوه من حديث ثوبان بسند ضعيف.','6','260',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2536,'<span data-type="title" id=toc-2542>٢٥٣٦ </span>- حديث عبد الله بن غنام البياضى رفعه: " من قال حين يصبح: اللهم ما أصبح بى من نعمة أو بأحد من خلقك فمنك وحدك لا شريك لك، فلك الحمد ولك الشكر، فقد أدى شكر يومه ".
** ن
(فتح الباري ١٣١/١١)
** قال الحافظ فى " الفتح " ١١ / ١٣١: صححه ابن حبان.','6','261',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2537,'<span data-type="title" id=toc-2543>٢٥٣٧ </span>- عن معاذ بن جبل أن النبى صلى الله عليه وسلم قال له: " يامعاذ إنى والله
لأحبك، فلا تدع دبر كل صلاة أن تقول: اللهم أعنى على ذكرك وشكرك وحسن عبادتك ".
** د ن
(فتح الباري ١٣٣/١١)
** قال الحافظ فى " الفتح " ١١ / ١٣٣: صححه ابن حبان والحاكم.','6','262',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2538,'<span data-type="title" id=toc-2544>٢٥٣٨ </span>- حديث أبى بكرة: اللهم إنى أعوذ بك من الكفر والفقر وعذاب القبر، كان النبى صلى الله عليه وسلم يدعو بهن دبر كل صلاة.
** حم ت ن
(فتح الباري ١٣٣/١١)
** قال الحافظ فى " الفتح " ١١ / ١٣٣: صححه الحاكم.','6','263',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2539,'<span data-type="title" id=toc-2545>٢٥٣٩ </span>- حديث صهيب رفعه: كان يقول إذا انصرف من الصلاة: " اللهم أصلح لى دينى ".
** ن
(فتح الباري ١٣٣/١١)
** قال الحافظ فى " الفتح " ١١ / ١٣٣: صححه ابن حبان.','6','264',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2540,'<span data-type="title" id=toc-2546>٢٥٤٠ </span>- حديث أبى أمامة قيل يارسول الله أى الدعاء أسمع؟ ، قال: " جوف الليل الأخير ودبر الصلوات المكتوبات ".
** ت
(فتح الباري ١٣٤/١١)
** قال الحافظ فى " الفتح " ١١ / ١٣٤: قال الترمذى: حسن.','6','265',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2541,'<span data-type="title" id=toc-2547>٢٥٤١ </span>- حديث ابن عمر رفعه: " يقول الله تعالى: من شغله ذكرى عن مسألتى أعطيته أفضل ما أعطى السائلين ".
** طب
(فتح الباري ١٣٤/١١)
** إسناده لين','6','266',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2542,'<span data-type="title" id=toc-2548>٢٥٤٢ </span>- حديث أبى سعيد: من شغله القرآن وذكرى عن مسألتى ... الحديث.
** ت
(فتح الباري ١٣٣/١١)
** قال الحافظ فى " الفتح " ١١ / ١٣٤: أخرجه الترمذى وحسنه.','6','267',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2543,'<span data-type="title" id=toc-2549>٢٥٤٣ </span>- حديث ابن عمر رفعه: " من فتح له منكم باب الدعاء فتحت له أبواب الرحمة ".
** ت
(فتح الباري ١٤١/١١)
** إسناده لين
** قال الحافظ فى " الفتح " ١١ / ١٤١: صححه الحاكم فوهم.','6','268',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2544,'<span data-type="title" id=toc-2550>٢٥٤٤ </span>- حديث أنس: لم يكن النبى صلى الله عليه وسلم يرفع يديه فى شىء من دعائه إلا فى الإستسقاء.
** لم
(فتح الباري ١٤٢/١١)
** صحيح','6','269',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2545,'<span data-type="title" id=toc-2551>٢٥٤٥ </span>- حديث عائشة أنها رأت النبى صلى الله عليه وسلم يدعو رافعا يديه يقول: " اللهم إنما أنا بشر ".
** لم
(فتح الباري ١٤٢/١١)
** إسناده صحيح','6','270',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2546,'<span data-type="title" id=toc-2552>٢٥٤٦ </span>- حديث أسامة كنت ردف النبى صلى الله عليه وسلم بعرفات فرفع يديه يدعو فمالت به ناقته فسقط خطامها فتناوله بيده وهو رافع يده الأخرى.
** ن
(فتح الباري ١٤٢/١١)
** إسناده جيد','6','271',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2547,'<span data-type="title" id=toc-2553>٢٥٤٧ </span>- حديث قيس بن سعد: ثم رفع رسول الله صلى الله عليه وسلم يديه وهو يقول: " اللهم صلواتك ورحمتك على آل سعد بن عبادة.... " الحديث.
** د
(فتح الباري ١٤٢/١١)
** إسناده جيد','6','272',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2548,'<span data-type="title" id=toc-2554>٢٥٤٨ </span>- حديث سلمان رفعه: " إن ربكم حيى كريم يستحى من عبده إذا رفعه يديه إليه أن يردهما صفرا ".
** د ت
(فتح الباري ١٤٣/١١)
** إسناده جيد
** قال الحافظ فى " الفتح " ١١ / ١٤٣: حسنه الترمذى.','6','273',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2549,'<span data-type="title" id=toc-2555>٢٥٤٩ </span>- عن أبى العالية فى ذكر أنس: وكان له بستان يأتى فى كل سنة الفاكهة مرتين وكان فيه ريحان يجىء منه ريح المسك.
** ت
(فتح الباري ١٤٥/١١)
** رجاله ثقات','6','274',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2550,'<span data-type="title" id=toc-2556>٢٥٥٠ </span>- حديث على: لقننى رسول الله صلى الله عليه وسلم هؤلاء الكلمات وأمرنى إن نزل بى كرب أو شدة أن أقولها.
** ن
(فتح الباري ١٤٦/١١)
** قال الحافظ فى " الفتح " ١١ / ١٤٦: صححه الحاكم.','6','275',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2551,'<span data-type="title" id=toc-2557>٢٥٥١ </span>- عن أبى بكرة رفعه: " دعوات المكروب: اللهم رحمتك أرجوا فلا تكلنى إلى نفسى طرفة عين وأصلح لى شأنى كله لا إله إلا أنت ".
** د
(فتح الباري ١٤٨/١١)
** قال الحافظ فى " الفتح " ١١ / ١٤٨: صححه ابن حبان.','6','276',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2552,'<span data-type="title" id=toc-2558>٢٥٥٢ </span>- عن أبى أمامة: من مسح رأس يتيم لا يمسحه إلا لله كان له بكل شعرة تمر يده عليها حسنة ".
** حم طب
(فتح الباري ١٥١/١١)
** إسناده ضعيف','6','277',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2553,'<span data-type="title" id=toc-2559>٢٥٥٣ </span>- حديث أبى هريرة: أن رجلا شكى إلى النبى صلى الله عليه وسلم قسوة قلبه فقال: " أطعم المسكين وامسح رأس اليتيم ".
** حم
(فتح الباري ١٥١/١١)
** إسناده حسن','6','278',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2554,'<span data-type="title" id=toc-2560>٢٥٥٤ </span>- حديث ابن مسعود: وارحم محمدا وآل محمد كما صليت وباركت وترحمت على إبراهيم.
** ك
(فتح الباري ١٥٩/١١)
** قال الحافظ فى " الفتح " ١١ / ١٥٩: أخرجه الحاكم فى صحيحه من حديث ابن مسعود فاغتر بتصحيحه قوم فوهموا، فإنه فى رواية يحيى بن السباق وهو مجهول عن رجل مبهم.','6','279',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2555,'<span data-type="title" id=toc-2561>٢٥٥٥ </span>- عن أبى هريرة رفعه: " من قال: اللهم صل على محمد وعلى آل محمد كما صليت على إبراهيم وعلى آل إبراهيم، وبارك على محمد وعلى آل محمد، كما باركت
على إبراهيم وعلى آل إبراهيم وترحم على محمد وعلى آل محمد كما ترحمت على
إبراهيم وعلى آل إبراهيم شهدت له يوم القيامة وشفعت له ".
** الطبرى فى تهذيبه
(فتح الباري ١٥٩/١١)
** قال الحافظ فى " الفتح " ١١ / ١٥٩: رجال سنده رجال الصحيح إلا سعيد بن سليمان مولى سعيد بن العاص الراوى له عن حنظلة بن على فإنه مجهول.','6','280',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2556,'<span data-type="title" id=toc-2562>٢٥٥٦ </span>- حديث أنس: آل محمد كل تقى.
** طب
(فتح الباري ١٦١/١١)
** إسناده واه جدا
** قال الحافظ فى " الفتح " ١١ / ١٦١: أخرج البيهقى عن جابر نحوه فى قوله بسند ضعيف.','6','281',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2557,'<span data-type="title" id=toc-2563>٢٥٥٧ </span>- عن ابن مسعود: فكيف نصلى عليك إذا نحن صلينا عليك فى صلاتنا.
** د ت ن جه
(فتح الباري ١٦٣/١١)
** قال الحافظ فى " الفتح " ١١ / ١٦٣: أخرجه أصحاب السنن وصححه الترمذى وابن خزيمة والحاكم، قال الدارقطنى: إسناده حسن متصل، وقال البيهقى: إسناده حسن صحيح.','6','282',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2558,'<span data-type="title" id=toc-2564>٢٥٥٨ </span>- عن ابن مسعود قال: يتشهد الرجل ثم يصلى على النبى ثم يدعو لنفسه.
** ك
(فتح الباري ١٦٤/١١)
** إسناده قوى','6','283',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2559,'<span data-type="title" id=toc-2565>٢٥٥٩ </span>- عن ابن عمر قال: لا تكون صلاة إلا بقراءة وتشهد وصلاة على.
** العمرى فى عمل يوم وليلة
(فتح الباري ١٦٤/١١)
** إسناده جيد','6','284',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2560,'<span data-type="title" id=toc-2566>٢٥٦٠ </span>- حديث فضالة بن عبيد قال: سمع النبى صلى الله عليه وسلم رجلا يدعو فى صلاته لم يحمد الله ولم يصل على النبى فقال: " عجل هذا "، ثم دعاه فقال: " إذا صلى أحدكم فليبدأ بتحميد ربه والثناء عليه ثم يصلى على النبى صلى الله عليه
وسلم ثم يدعو بما شاء ".
** د ن ت
(فتح الباري ١٦٥/١١)
** قال الحافظ فى " الفتح " ١١ / ١٦٥: الترمذى صححه، وكذا ابن خزيمة وابن حبان والحاكم.','6','285',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2561,'<span data-type="title" id=toc-2567>٢٥٦١ </span>- عن أبى بردة بن ينار: من صلى على من أمتى صلاة مخلصا فى قلبه صلى الله عليه بها عشر صلوات ورفعه بها عشر درجات وكتب له بها عشر حسنات ومحا عنه عشر سيئات.
** ن
(فتح الباري ١٦٧/١١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١١ / ١٦٧: عن أبى برده بن دينار وأبى طلحة كلاهما عند النسائى ورواتهما ثقات ولفظ أبى طلحة عنده نحوه وصححه ابن حبان.','6','286',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2562,'<span data-type="title" id=toc-2568>٢٥٦٢ </span>- حديث ابن مسعود رفعه: " إن أولى الناس بى يوم القيامة أكثرهم على صلاة ".
** لم
(فتح الباري ١٦٧/١١)
** قال الحافظ فى " الفتح " ١١ / ١٦٧: حسنه الترمذى وصححه ابن حبان.','6','287',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2563,'<span data-type="title" id=toc-2569>٢٥٦٣ </span>- عن أبى أمامة: صلاة أمتى تعرض على فى كل يوم جمعة فمن كان أكثرهم على صلاة كان أقربهم منى منزلة.
** هق
(فتح الباري ١٦٧/١١)
** إسناده لا بأس به','6','288',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2564,'<span data-type="title" id=toc-2570>٢٥٦٤ </span>- حديث " البخيل من ذكرت عنده فلم يصل على ".
** ت ن حب ك إسماعيل القاضى
(فتح الباري ١٦٧/١١)
** قال الحافظ فى " الفتح " ١١ / ١٦٨: لا يقصر عن درجة الحسن.','6','289',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2565,'<span data-type="title" id=toc-2571>٢٥٦٥ </span>- حديث أبى هريرة: من ذكرت عنده ولم يصل على فمات فدخل النار فأبعده الله.
** ت
(فتح الباري ١٦٨/١١)
** قال الحافظ فى " الفتح " ١١ / ١٦٨: صححه الحاكم.','6','290',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2566,'<span data-type="title" id=toc-2572>٢٥٦٦ </span>- من مرسل قتادة: من الجفاء أن أذكر عند رجل فلا يصل على.
** عب
(فتح الباري ١٦٨/١١)
** مرسل','6','291',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2567,'<span data-type="title" id=toc-2573>٢٥٦٧ </span>- حديث أبى بن كعب أن رجلا قال: يارسول الله إنى أكثر الصلاة فما أجعل لك من صلاتى، قال: " ماشئت "، قال: الثلث؟ ، قال: " ماشئت وإن زدت فهو خير ... " إلى أن قال: أجعل لك كل صلاتى؟ ، قال: " إذا تكفى همك ... " الحديث.
** حم
(فتح الباري ١٦٨/١١)
** إسناده حسن','6','292',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2568,'<span data-type="title" id=toc-2574>٢٥٦٨ </span>- حديث بريدة رفعه: " لا تتركن فى التشهد الصلة على وعلى أنبياء الله..
الحديث.
** هق
(فتح الباري ١٦٩/١١)
** إسناده واه','6','293',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2569,'<span data-type="title" id=toc-2575>٢٥٦٩ </span>- حديث أبى هريرة رفعه: " صلوا على أنبياء الله.... " الحديث
** إسماعيل القاضى
(فتح الباري ١٦٩/١١)
** إسناده ضعيف','6','294',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2570,'<span data-type="title" id=toc-2576>٢٥٧٠ </span>- حديث ابن عباس رفعه: " إذا صليتم على فصلوا على أنبياء الله فإن الله بعثهم كما بعثنى ".
** طب فوائد العيسوى
(فتح الباري ١٦٩/١١)
** إسناده ضعيف','6','295',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2571,'<span data-type="title" id=toc-2577>٢٥٧١ </span>- عن عكرمة عنه قال: ما أعلم الصلاة تنبغى على أحد من أحد إلا على النبى صلى
الله عليه وسلم.
** شب
(فتح الباري ١٦٩/١١)
** إسناده صحيح','6','296',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2572,'<span data-type="title" id=toc-2578>٢٥٧٢ </span>- عن قيس بن سعد بن عبادة: أن النبى صلى الله عليه وسلم رفع يديه وهو يقول: " اللهم اجعل صلواتك ورحمتك على آل سعد بن عبادة ".
** د ن
(فتح الباري ١٧٠/١١)
** إسناده جيد','6','297',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2573,'<span data-type="title" id=toc-2579>٢٥٧٣ </span>- حديث جابر: أن امرأته قالت للنبى صلى الله عليه وسلم صل على وعلى زوجى ففعل.
** حم
(فتح الباري ١٧٠/١١)
** قال الحافظ فى " الفتح " ١١ / ١٧٠: صححه ابن حبان.','6','298',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2574,'<span data-type="title" id=toc-2580>٢٥٧٤ </span>- حديث سعد رفعه: " من سعادة ابن آدم استخارته الله ".
** حم
(فتح الباري ١٨٤/١١)
** إسناده حسن','6','299',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2575,'<span data-type="title" id=toc-2581>٢٥٧٥ </span>- حديث أبى بكر الصديق رضى الله عنه أن النبى صلى الله عليه وسلم كان إذا أراد أمرا قال: " اللهم خر لى واختر لى ".
** ت
(فتح الباري ١٨٤/١١)
** إسناده ضعيف','6','300',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2576,'<span data-type="title" id=toc-2582>٢٥٧٦ </span>- حديث أنس رفعه: " ماخاب من استخار ".
** طص
(فتح الباري ١٨٤/١١)
** إسناده واه جدا','6','301',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2577,'<span data-type="title" id=toc-2583>٢٥٧٧ </span>- حديث أنس: إذا هممت بأمر فاستخر ربك سبعا ثم انظر الذى يسبق فى قلبك فإن الخير منه.
** سنى
(فتح الباري ١٨٧/١١)
** إسناده واه جدا','6','302',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2578,'<span data-type="title" id=toc-2584>٢٥٧٨ </span>- حديث ابن مسعود: أن النبى صلى الله عليه وسلم كان يعجبه أن يدعو ثلاثا ويستغفر ثلاثا.
** د ن
(فتح الباري ١٩٣/١١)
** قال الحافظ فى " الفتح " ١١ / ١٩٣: صححه ابن حبان.','6','303',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2579,'<span data-type="title" id=toc-2585>٢٥٧٩ </span>- عن أبى سلمة قال: قلت: ياأبا سعيد أن أبا هريرة حدثنا عن الساعة التى فى الجمعة فقال: سألت عنها النبى صلى الله عليه وسلم فقال: " إنى كنت أعلمها ثم نسيتها كما أنسيت ليلة القدر ".
** حم
(فتح الباري ١٩٩/١١)
** قال الحافظ فى " الفتح " ١١ / ١٩٩: صححه ابن خزيمة.','6','304',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2580,'<span data-type="title" id=toc-2586>٢٥٨٠ </span>- حديث عائشة مرفوعا: " ماحسدتكم اليهود على شىء ماحسدتكم على السلام والتأمين ".
** جه
(فتح الباري ٢٠٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٠٠: صححه ابن خزيمة.','6','305',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2581,'<span data-type="title" id=toc-2587>٢٥٨١ </span>- حديث البراء: من قال: لا إله إلا الله ... وفى أخره عشر مرات كن له عدل رقبة.
** ن
(فتح الباري ٢٠٢/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٠: صححه ابن حبان والحاكم.','6','306',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2582,'<span data-type="title" id=toc-2588>٢٥٨٢ </span>- حديث عمرو بن شعيب عن أبيه عن جده: لم يجىء أحد بأفضل من عمله إلا من قال أفضل من ذلك.
** ن
(فتح الباري ٢٠٢/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٠٢: أخرجه النسائى بسند صحيح إلى عمرو.','6','307',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2583,'<span data-type="title" id=toc-2589>٢٥٨٣ </span>- عن أبى أيوب رفعه: " من قال إذا صلى الصبح لا إله إلا الله.... " فذكره بلفظ عشر مرات: " كن كعدل أربع رقاب وكتب له بهن عشر حسنات ومحى عنه بهن عشر سيئات ورفع له بهن عشر درجات وكن له حرسا من الشيطان حتى يمسى وإذا قالها بعد المغرب فمثل ذلك ".
** حم
(فتح الباري ٢٠٥/١١)
** إسناده حسن','6','308',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2584,'<span data-type="title" id=toc-2590>٢٥٨٤ </span>- عن عمر رفعه: " من قال حين يدخل السوق لا إله إلا الله وحده لا شريك له له الملك وله الحمد يحيى ويميت وهو حى لا يموت بيده الخير وهو على كل شىء قدير ... " الحديث.
** ت
(فتح الباري ٢٠٦/١١)
** إسناده لين
** قال الحافظ فى " الفتح " ١١ / ٢٠٦: هذا لفظ جعفر فى الذكر وفى سنده لين.','6','309',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2585,'<span data-type="title" id=toc-2591>٢٥٨٥ </span>- حديث: أفضل الذكر لا إله إلا الله.
** ت ن
(فتح الباري ٢٠٧/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٠٧: صححه ابن حبان والحاكم.','6','310',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2586,'<span data-type="title" id=toc-2592>٢٥٨٦ </span>- عن أبى سعيد عن النبى صلى الله عليه وسلم: " قال موسى يارب علمنى شيئا أذكرك به، قال: قل لا إله إلا الله.... "، الحديث وفيه: " لو أن السموات السبع
وعامرهن والأرضين السبع جعلن فى كفة ولا إله إلا الله فى كفة لمالت بهن لا إله إلا الله ".
** ن
(فتح الباري ٢٠٨/١١)
** إسناده صحيح','6','311',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2587,'<span data-type="title" id=toc-2593>٢٥٨٧ </span>- حديث أبى هريرة رفعه: " لأن أقول سبحان الله والحمد لله ولا إله إلا الله والله أكبر أحب إلى مما طلعت عليه الشمس ".
** ت ن
(فتح الباري ٢١٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٢١٠: صححه الحاكم عن الحارث بن الحارث الأشعرى فى حديث طويل.','6','312',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2588,'<span data-type="title" id=toc-2594>٢٥٨٨ </span>- عن عبد الله بن بسر أن رجلا قال: يارسول الله إن شرائع الإسلام قد كثرت على فأخبرنى بشىء أتشبث به، قال: " لا يزال لسانك رطبا فى ذكر الله ".
** ت جه
(فتح الباري ٢١٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٢١٠: صححه ابن حبان والحاكم.','6','313',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2589,'<span data-type="title" id=toc-2595>٢٥٨٩ </span>- حديث أبى الدرداء مرفوعا: " ألا أخبركم بخير أعمالك وأزكاها عند مليككم وأرفعها فى درجاتكم وخيركم فى أنفاق الذهب والورق وخير لكم فى أن تلقوا عدوكم فتضربوا أعناقهم ويضربوا أعناقكم "، قالوا: بلى، قال: " ذكر الله عز وجل ".
** ت جه
(فتح الباري ٢١٠/١١)','6','314',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2590,'<span data-type="title" id=toc-2596>٢٥٩٠ </span>- حديث ابن مسعود: " أسألك بكل اسم هو لك سميت به نفسك أو أنزلته فى كتابك أو علمته أحدا من خلقك أو استأثرت به فى علم الغيب عندك ".
** حم
(فتح الباري ٢٢٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٢٠: صححه ابن حبان.','6','315',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2591,'<span data-type="title" id=toc-2597>٢٥٩١ </span>- عن عائشة أنها سألت النبى صلى الله عليه وسلم أن يعلمها الإسم الأعظم فلم يفعل، فصلت ودعت: اللهم إنى أدعوك الله وأدعوك الرحمن وأدعوك الرحيم وأدعوك بأسمائك الحسنى كلها ماعملت منها ومالم أعلم ... الحديث وفيه أنه صلى الله عليه وسلم قال لها: " إنه لفى الأسماء التى دعوت بها ".
** جه
(فتح الباري ٢٢٤/١١)
** إسناده ضعيف','6','316',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2592,'<span data-type="title" id=toc-2598>٢٥٩٢ </span>- حديث أسماء بنت يزيد أن النبى صلى الله عليه وسلم قال: " أسم الله الأعظم فى هاتين الآيتين (وإلهكم إله واحد لا إله إلا هو الرحمن الرحيم) وفاتحة آل عمران (الله لا إله إلا هو الحى القيوم) ".
** د ت جه
(فتح الباري ٢٢٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٢٤: أخرجه أصحاب السنن إلا النسائى وحسنه الترمذى وفى نسخة صحيحة، وفيه نظر لإنه من رواية شهر بن حوشب.','6','317',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2593,'<span data-type="title" id=toc-2599>٢٥٩٣ </span>- حديث على: أن الوتر ليس بحتم كالمكتوبة ولكن رسول الله صلى الله عليه وسلم أوتر ثم قال: " أوتروا يا أهل القرآن فإن الله وتر يحب الوتر ".
** د ت ن جه
(فتح الباري ٢٢٧/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٢٧: أخرجوه فى السنن الأربعة وصححه ابن خزيمة واللفظ له.','6','318',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2594,'<span data-type="title" id=toc-2600>٢٥٩٤ </span>- عن ابن عباس قال: أو لم نعمركم ما يتذاكر فيه من تذاكر وجاكم النذير فقال: نزلت تعييرا لأبناء السبعين.
** ابن مردويه
(فتح الباري ٢٣٩/١١)
** تقال الحافظ فى " الفتح " ١١ / ٢٣٩: فى إسناده يحيى بن ميمون وهو ضعيف.','6','319',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2595,'<span data-type="title" id=toc-2601>٢٥٩٥ </span>- عن أبى هريرة: معترك المنايا مابين ستين وسبعين.
** يع
(فتح الباري ٢٣٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٣٩: من طريق إبراهيم بن الفضل عن سعيد عن أبى هريرة وإبراهيم ضعيف.','6','320',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2596,'<span data-type="title" id=toc-2602>٢٥٩٦ </span>- عن أبى هريرة رفعه: " أعمار أمتى مابين الستين إلى السبعين وأقلهم ".
** ت
(فتح الباري ٢٤٠/١١)
** إسناده حسن','6','321',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2597,'<span data-type="title" id=toc-2603>٢٥٩٧ </span>- عن جابر قلنا: يارسول الله واثنان؟ ، قال: " وإثنان "، قال محمود: فقلت لجابر: أراكم لو قلتم واحدا لقال واحد، قال: وأنا أظن ذلك.
** حم
(فتح الباري ٢٤٣/١١)
** رجاله موثقون','6','322',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2598,'<span data-type="title" id=toc-2604>٢٥٩٨ </span>- حديث جابر بن سمرة رفعه: " من دفن له ثلاثة فصبر ... " الحديث وفيه فقالت أم أيمن: وواحد، فسكت ثم قال: " يا أم أيمن من دفن واحدا فصبر عليه واحتسبه وجبت له الجنة ".
** طب طس
(فتح الباري ٢٤٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٤٣: فى سندهما ناصح بن عبد الله وهو ضعيف جدا.','6','323',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2599,'<span data-type="title" id=toc-2605>٢٥٩٩ </span>- حديث قرة بن إياس: أن رجلا كان يأتى النبى صلى الله عليه وسلم ومعه ابن له فقال: " أتحبه "، قال: نعم ففقده فقال: " مافعل فلان "، قالوا: يارسول الله مات ابنه، فقال: " ألا تحب أن لا تأتى بابا من أبواب الجنة إلا وجدته ينتظرك "، فقال رجل: يارسول الله أله خاصة أم لكلنا؟ ، قال: " بل لكلكم ".
** حم ن
(فتح الباري ٢٤٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٤٣: سنده على شرط الصحيح وقد صححه ابن حبان والحاكم.','6','324',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2600,'<span data-type="title" id=toc-2606>٢٦٠٠ </span>- حديث كعب بن عياض: سمعت رسول الله صلى الله عليه وسلم يقول: " إن لكل أمة فتنة وفتنة أمتى المال ".
** ت حب ك
(فتح الباري ٢٥٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٥٣: صححه الترمذى وابن حبان والحاكم.','6','325',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2601,'<span data-type="title" id=toc-2607>٢٦٠١ </span>- عن جبير بن نفير: ولو سيل لابن آدم واديان من مال لتمنى إليه ثالثا.
** ص
(فتح الباري ٢٥٣/١١)
** مرسل','6','326',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2602,'<span data-type="title" id=toc-2608>٢٦٠٢ </span>- حديث بريدة قال: كان رسول الله صلى الله عليه وسلم يخطب فجاء الحسن والحسين عليهما قميصان أحران يعثران فنزل عن المنبر فحملهما فوضعهما بين يديه ثم قال: " صدق الله ورسوله إنما أموالكم وأولادكم فتنة ... " الحديث.
** حم د ت ن جه
(فتح الباري ٢٥٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٥٤: أخرجه أحمد وأصحاب السنن وصححه ابن خزيمة وابن حبان.','6','327',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2603,'<span data-type="title" id=toc-2609>٢٦٠٣ </span>- عن أبى بن كعب أن رسول الله صلى الله عليه وسلم قال له: " إن الله أمرنى أن أقرأ عليك القرآن "، فقرأ القرآن (لم يكن الذين كفروا من أهل الكتاب) قال: وقرأ فيها: " إن الدين عند الله الحنيفية السمحة ... " الحديث وفيه وقرأ عليه: لو أن لابن أدم واديا من مال ... الحديث وفيه: " ويتوب الله على من تاب ".
** ت
(فتح الباري ٢٥٧/١١)
** إسناده جيد','6','328',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2604,'<span data-type="title" id=toc-2610>٢٦٠٤ </span>- اللهم احينى مسكينا وأمتنى مسكينا.
** ت
(فتح الباري ٢٧٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٧٤: قال أحمد بن نصر الداودى: الحديث ضعيف.','6','329',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2605,'<span data-type="title" id=toc-2611>٢٦٠٥ </span>- معن ابن عباس أنه سئل عن رجل قليل العمل قليل الذنوب أفضل، أو رجل كثير العمل كثير الذنوب؟ ، فقال: لا أعدل بالسلامة شيئا.
** مب
(فتح الباري ٢٥٧/١١)
** إسناده صحيح','6','330',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2606,'<span data-type="title" id=toc-2612>٢٦٠٦ </span>- عن أنس رفعه: " مامن غنى ولا فقير إلا ود يوم القيامة أنه أوتى من الدنيا قوتا ".
** جه
(فتح الباري ٢٧٥/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٧٥: من طريق نقيع وهو ضعيف.','6','331',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2607,'<span data-type="title" id=toc-2613>٢٦٠٧ </span>- عن محمد بن إبراهيم التيمى قال: قيل يارسول الله أعطيت عيينة والأقرع مائة مائة وتركت جعيلا؟ ، قال: " والذى نفسى بيده لجعيل بن سراقة خير من طلاع
الأرض مثل عيينة والأقرع ولكنى أتألفهما وأكل جعيلا إلى إيمانه ".
** ابن إسحق فى المغازى
(فتح الباري ٢٧٧/١١)
** مرسل أو معضل','6','332',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2608,'<span data-type="title" id=toc-2614>٢٦٠٨ </span>- حديث ابن عمر لا يصيب عبد من الدنيا شيئا إلا نقص من درجاته وإن كان عند الله كريما.
** ابن أبى الدنيا
(فتح الباري ٢٨٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٨٠: قال المنذرى: وسنده جيد.','6','333',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2609,'<span data-type="title" id=toc-2615>٢٦٠٩ </span>- عن أبى هريرة أتيت رسول الله صلى الله عليه وسلم بتمرات فقلت: ادع لى فيهن بالبركة قال: فقبض ثم دعا ثم قال: " خذهن فاجعلهم فى مزود فإذا أردت أن تأخذ منهن فأدخل يدك فخذ ولا تنثر بهن نثرا " فحملت من ذلك كذا وكذا وسقا فى سبيل الله وكنا نأكل ونطعم وكان المزود معلقا يحتوى لا يفارقه فلما قتل عثمان انقطع.
** ت هق فى الدلائل
(فتح الباري ٢٨٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٢٨٠: حسنه الترمذى.','6','334',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2610,'<span data-type="title" id=toc-2616>٢٦١٠ </span>- مرسل يزيد بن عبد الله بن قسيط: كان أهل الصفة ناسا فقراء لا منازل لهم
فكانوا ينامون فى المسجد لا مأوى لهم غيره.
** سعد
(فتح الباري ٢٨٦/١١)
** مرسل','6','335',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2611,'<span data-type="title" id=toc-2617>٢٦١١ </span>- مرسل محمد بن سيرين: كان رسول الله صلى الله عليه وسلم إذا صلى قسم ناسا من أصحاب الصفة بين الناس من أصحابه فيذهب الرجل بالرجل والرجل بالرجلين حتى ذكر عشرة ... الحديث.
