INSERT INTO [b88] VALUES (2618,'<span data-type="title" id=toc-2624>٢٦١٨ </span>- عن أنس سمعت رسول الله صلى الله عليه وسلم يقول مرارا: " والذى نفس محمد بيده ما أصبح عند آل محمد صاع حب ولا صاع تمر وإن له يومئذ لتسع نسوة ".
