INSERT INTO [b88] VALUES (2640,'<span data-type="title" id=toc-2646>٢٦٤٠ </span>- حديث ابن عباس رفعه: " ألا أخبركم بخير الناس؟ ، رجل ممسك بعنان فرسه.. الحديث وفيه: " ألا أخبركم بالذى يتله؟ ، رجل معتزل فى غنيمة يؤدى حق الله فيها ".
